var abasa=[{"id":"1","ar":"عَبَسَ وَتَوَلّٰىٓۙ","bc":"'abasa wa tawallā","at":"Dia (Muhammad) berwajah masam dan berpaling,"},{"id":"2","ar":"اَنْ جَاۤءَهُ الْاَعْمٰىۗ","bc":"an jā`ahul-a'mā","at":"karena seorang buta telah datang kepadanya (Abdullah bin Ummi Maktum)."},{"id":"3","ar":"وَمَا يُدْرِيْكَ لَعَلَّهٗ يَزَّكّٰىٓۙ","bc":"wa mā yudrīka la'allahụ yazzakkā","at":"Dan tahukah engkau (Muhammad) barangkali dia ingin menyucikan dirinya (dari dosa),"},{"id":"4","ar":"اَوْ يَذَّكَّرُ فَتَنْفَعَهُ الذِّكْرٰىۗ","bc":"au yażżakkaru fa tanfa'ahuż-żikrā","at":"atau dia (ingin) mendapatkan pengajaran, yang memberi manfaat kepadanya?"},{"id":"5","ar":"اَمَّا مَنِ اسْتَغْنٰىۙ","bc":"ammā manistagnā","at":"Adapun orang yang merasa dirinya serba cukup (pembesar-pembesar Quraisy),"},{"id":"6","ar":"فَاَنْتَ لَهٗ تَصَدّٰىۗ","bc":"fa anta lahụ taṣaddā","at":"maka engkau (Muhammad) memberi perhatian kepadanya,"},{"id":"7","ar":"وَمَا عَلَيْكَ اَلَّا يَزَّكّٰىۗ","bc":"wa mā 'alaika allā yazzakkā","at":"padahal tidak ada (cela) atasmu kalau dia tidak menyucikan diri (beriman)."},{"id":"8","ar":"وَاَمَّا مَنْ جَاۤءَكَ يَسْعٰىۙ","bc":"wa ammā man jā`aka yas'ā","at":"Dan adapun orang yang datang kepadamu dengan bersegera (untuk mendapatkan pengajaran),"},{"id":"9","ar":"وَهُوَ يَخْشٰىۙ","bc":"wa huwa yakhsyā","at":"sedang dia takut (kepada Allah),"},{"id":"10","ar":"فَاَنْتَ عَنْهُ تَلَهّٰىۚ","bc":"fa anta 'an-hu talahhā","at":"engkau (Muhammad) malah mengabaikannya."},{"id":"11","ar":"كَلَّآ اِنَّهَا تَذْكِرَةٌ ۚ","bc":"kallā innahā tażkirah","at":"Sekali-kali jangan (begitu)! Sungguh, (ajaran-ajaran Allah) itu suatu peringatan,"},{"id":"12","ar":"فَمَنْ شَاۤءَ ذَكَرَهٗ ۘ","bc":"fa man syā`a żakarah","at":"maka barangsiapa menghendaki, tentulah dia akan memperhatikannya,"},{"id":"13","ar":"فِيْ صُحُفٍ مُّكَرَّمَةٍۙ","bc":"fī ṣuḥufim mukarramah","at":"di dalam kitab-kitab yang dimuliakan (di sisi Allah),"},{"id":"14","ar":"مَّرْفُوْعَةٍ مُّطَهَّرَةٍ ۢ ۙ","bc":"marfụ'atim muṭahharah","at":"yang ditinggikan (dan) disucikan,"},{"id":"15","ar":"بِاَيْدِيْ سَفَرَةٍۙ","bc":"bi`aidī safarah","at":"di tangan para utusan (malaikat),"},{"id":"16","ar":"كِرَامٍۢ بَرَرَةٍۗ","bc":"kirāmim bararah","at":"yang mulia lagi berbakti."},{"id":"17","ar":"قُتِلَ الْاِنْسَانُ مَآ اَكْفَرَهٗۗ","bc":"qutilal-insānu mā akfarah","at":"Celakalah manusia! Alangkah kufurnya dia!"},{"id":"18","ar":"مِنْ اَيِّ شَيْءٍ خَلَقَهٗۗ","bc":"min ayyi syai`in khalaqah","at":"Dari apakah Dia (Allah) menciptakannya?"},{"id":"19","ar":"مِنْ نُّطْفَةٍۗ خَلَقَهٗ فَقَدَّرَهٗۗ","bc":"min nuṭfah, khalaqahụ fa qaddarah","at":"Dari setetes mani, Dia menciptakannya lalu menentukannya."},{"id":"20","ar":"ثُمَّ السَّبِيْلَ يَسَّرَهٗۙ","bc":"ṡummas-sabīla yassarah","at":"Kemudian jalannya Dia mudahkan,"},{"id":"21","ar":"ثُمَّ اَمَاتَهٗ فَاَقْبَرَهٗۙ","bc":"ṡumma amātahụ fa aqbarah","at":"kemudian Dia mematikannya lalu menguburkannya,"},{"id":"22","ar":"ثُمَّ اِذَا شَاۤءَ اَنْشَرَهٗۗ","bc":"ṡumma iżā syā`a ansyarah","at":"kemudian jika Dia menghendaki, Dia membangkitkannya kembali."},{"id":"23","ar":"كَلَّا لَمَّا يَقْضِ مَآ اَمَرَهٗۗ","bc":"kallā lammā yaqḍi mā amarah","at":"Sekali-kali jangan (begitu)! Dia (manusia) itu belum melaksanakan apa yang Dia (Allah) perintahkan kepadanya."},{"id":"24","ar":"فَلْيَنْظُرِ الْاِنْسَانُ اِلٰى طَعَامِهٖٓ ۙ ","bc":"falyanẓuril-insānu ilā ṭa'āmih","at":"Maka hendaklah manusia itu memperhatikan makanannya."},{"id":"25","ar":"اَنَّا صَبَبْنَا الْمَاۤءَ صَبًّاۙ","bc":"annā ṣababnal-mā`a ṣabbā","at":"Kamilah yang telah mencurahkan air melimpah (dari langit),"},{"id":"26","ar":"ثُمَّ شَقَقْنَا الْاَرْضَ شَقًّاۙ","bc":"ṡumma syaqaqnal-arḍa syaqqā","at":"kemudian Kami belah bumi dengan sebaik-baiknya,"},{"id":"27","ar":"فَاَنْۢبَتْنَا فِيْهَا حَبًّاۙ","bc":"fa ambatnā fīhā ḥabbā","at":"lalu di sana Kami tumbuhkan biji-bijian,"},{"id":"28","ar":"وَّعِنَبًا وَّقَضْبًاۙ","bc":"wa 'inabaw wa qaḍbā","at":"dan anggur dan sayur-sayuran,"},{"id":"29","ar":"وَّزَيْتُوْنًا وَّنَخْلًاۙ","bc":"wa zaitụnaw wa nakhlā","at":"dan zaitun dan pohon kurma,"},{"id":"30","ar":"وَّحَدَاۤئِقَ غُلْبًا","bc":"wa ḥadā`iqa gulbā","at":"dan kebun-kebun (yang) rindang,"},{"id":"31","ar":"وَفَاكِهَةً وَّاَبًّا","bc":"wa fākihataw wa abbā","at":"dan buah-buahan serta rerumputan."},{"id":"32","ar":"مَتَاعًا لَّكُمْ وَلِاَنْعَامِكُمْۗ","bc":"matā'al lakum wa li`an'āmikum","at":"(Semua itu) untuk kesenanganmu dan untuk hewan-hewan ternakmu."},{"id":"33","ar":"فَاِذَا جَاۤءَتِ الصَّاۤخَّةُ ۖ","bc":"fa iżā jā`atiṣ-ṣākhkhah","at":"Maka apabila datang suara yang memekakkan (tiupan sangkakala yang kedua),"},{"id":"34","ar":"يَوْمَ يَفِرُّ الْمَرْءُ مِنْ اَخِيْهِۙ","bc":"yauma yafirrul-mar`u min akhīh","at":"pada hari itu manusia lari dari saudaranya,"},{"id":"35","ar":"وَاُمِّهٖ وَاَبِيْهِۙ","bc":"wa ummihī wa abīh","at":"dan dari ibu dan bapaknya,"},{"id":"36","ar":"وَصَاحِبَتِهٖ وَبَنِيْهِۗ","bc":"wa ṣāḥibatihī wa banīh","at":"dan dari istri dan anak-anaknya."},{"id":"37","ar":"لِكُلِّ امْرِئٍ مِّنْهُمْ يَوْمَىِٕذٍ شَأْنٌ يُّغْنِيْهِۗ","bc":"likullimri`im min-hum yauma`iżin sya`nuy yugnīh","at":"Setiap orang dari mereka pada hari itu mempunyai urusan yang menyibukkannya."},{"id":"38","ar":"وُجُوْهٌ يَّوْمَىِٕذٍ مُّسْفِرَةٌۙ","bc":"wujụhuy yauma`iżim musfirah","at":"Pada hari itu ada wajah-wajah yang berseri-seri,"},{"id":"39","ar":"ضَاحِكَةٌ مُّسْتَبْشِرَةٌ ۚ","bc":"ḍāḥikatum mustabsyirah","at":"tertawa dan gembira ria,"},{"id":"40","ar":"وَوُجُوْهٌ يَّوْمَىِٕذٍ عَلَيْهَا غَبَرَةٌۙ","bc":"wa wujụhuy yauma`iżin 'alaihā gabarah","at":"dan pada hari itu ada (pula) wajah-wajah yang tertutup debu (suram),"},{"id":"41","ar":"تَرْهَقُهَا قَتَرَةٌ ۗ","bc":"tarhaquhā qatarah","at":"tertutup oleh kegelapan (ditimpa kehinaan dan kesusahan)."},{"id":"42","ar":"اُولٰۤىِٕكَ هُمُ الْكَفَرَةُ الْفَجَرَةُ","bc":"ulā`ika humul-kafaratul-fajarah","at":"Mereka itulah orang-orang kafir yang durhaka."}],ad_duha=[{"id":"1","ar":"وَالضُّحٰىۙ","bc":"waḍ-ḍuḥā","at":"Demi waktu duha (ketika matahari naik sepenggalah),"},{"id":"2","ar":"وَالَّيْلِ اِذَا سَجٰىۙ","bc":"wal-laili iżā sajā","at":"dan demi malam apabila telah sunyi,"},{"id":"3","ar":"مَا وَدَّعَكَ رَبُّكَ وَمَا قَلٰىۗ","bc":"mā wadda'aka rabbuka wa mā qalā","at":"Tuhanmu tidak meninggalkan engkau (Muhammad) dan tidak (pula) membencimu,"},{"id":"4","ar":"وَلَلْاٰخِرَةُ خَيْرٌ لَّكَ مِنَ الْاُوْلٰىۗ","bc":"wa lal-ākhiratu khairul laka minal-ụlā","at":"dan sungguh, yang kemudian itu lebih baik bagimu dari yang permulaan."},{"id":"5","ar":"وَلَسَوْفَ يُعْطِيْكَ رَبُّكَ فَتَرْضٰىۗ","bc":"wa lasaufa yu'ṭīka rabbuka fa tarḍā","at":"Dan sungguh, kelak Tuhanmu pasti memberikan karunia-Nya kepadamu, sehingga engkau menjadi puas."},{"id":"6","ar":"اَلَمْ يَجِدْكَ يَتِيْمًا فَاٰوٰىۖ","bc":"a lam yajidka yatīman fa āwā","at":"Bukankah Dia mendapatimu sebagai seorang yatim, lalu Dia melindungi(mu),"},{"id":"7","ar":"وَوَجَدَكَ ضَاۤلًّا فَهَدٰىۖ","bc":"wa wajadaka ḍāllan fa hadā","at":"dan Dia mendapatimu sebagai seorang yang bingung, lalu Dia memberikan petunjuk,"},{"id":"8","ar":"وَوَجَدَكَ عَاۤىِٕلًا فَاَغْنٰىۗ","bc":"wa wajadaka 'ā`ilan fa agnā","at":"dan Dia mendapatimu sebagai seorang yang kekurangan, lalu Dia memberikan kecukupan."},{"id":"9","ar":"فَاَمَّا الْيَتِيْمَ فَلَا تَقْهَرْۗ","bc":"fa ammal-yatīma fa lā taq-har","at":"Maka terhadap anak yatim janganlah engkau berlaku sewenang-wenang."},{"id":"10","ar":"وَاَمَّا السَّاۤىِٕلَ فَلَا تَنْهَرْ","bc":"wa ammas-sā`ila fa lā tan-har","at":"Dan terhadap orang yang meminta-minta janganlah engkau menghardik(nya)."},{"id":"11","ar":"وَاَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ","bc":"wa ammā bini'mati rabbika fa ḥaddiṡ","at":"Dan terhadap nikmat Tuhanmu hendaklah engkau nyatakan (dengan bersyukur)."}],ad_dukhan=[{"id":"1","ar":"حٰمۤ ۚ ","bc":"ḥā mīm","at":"Ha Mim"},{"id":"2","ar":"وَالْكِتٰبِ الْمُبِيْنِۙ ","bc":"wal-kitābil-mubīn","at":"Demi Kitab (Al-Qur'an) yang jelas,"},{"id":"3","ar":"اِنَّآ اَنْزَلْنٰهُ فِيْ لَيْلَةٍ مُّبٰرَكَةٍ اِنَّا كُنَّا مُنْذِرِيْنَ","bc":"innā anzalnāhu fī lailatim mubārakatin innā kunnā munżirīn","at":"sesungguhnya Kami menurunkannya pada malam yang diberkahi. ) Sungguh, Kamilah yang memberi peringatan."},{"id":"4","ar":"فِيْهَا يُفْرَقُ كُلُّ اَمْرٍ حَكِيْمٍۙ ","bc":"fīhā yufraqu kullu amrin ḥakīm","at":"Pada (malam itu) dijelaskan segala urusan yang penuh hikmah,"},{"id":"5","ar":"اَمْرًا مِّنْ عِنْدِنَاۗ اِنَّا كُنَّا مُرْسِلِيْنَۖ ","bc":"amram min 'indinā, innā kunnā mursilīn","at":"(yaitu) urusan dari sisi Kami. Sungguh, Kamilah yang mengutus rasul-rasul,"},{"id":"6","ar":"رَحْمَةً مِّنْ رَّبِّكَ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُۗ ","bc":"raḥmatam mir rabbik, innahụ huwas-samī'ul-'alīm","at":"sebagai rahmat dari Tuhanmu. Sungguh, Dia Maha Mendengar, Maha Mengetahui,"},{"id":"7","ar":"رَبِّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۘ اِنْ كُنْتُمْ مُّوْقِنِيْنَ","bc":"rabbis-samāwāti wal-arḍi wa mā bainahumā, ing kuntum mụqinīn","at":"Tuhan (yang memelihara) langit dan bumi dan apa yang ada di antara keduanya; jika kamu orang-orang yang meyakini."},{"id":"8","ar":"لَآ اِلٰهَ اِلَّا هُوَ يُحْيٖ وَيُمِيْتُ ۗرَبُّكُمْ وَرَبُّ اٰبَاۤىِٕكُمُ الْاَوَّلِيْنَ","bc":"lā ilāha illā huwa yuḥyī wa yumīt, rabbukum wa rabbu ābā`ikumul-awwalīn","at":"Tidak ada tuhan selain Dia, Dia yang menghidupkan dan mematikan. (Dialah) Tuhanmu dan Tuhan nenek moyangmu dahulu."},{"id":"9","ar":"بَلْ هُمْ فِيْ شَكٍّ يَّلْعَبُوْنَ","bc":"bal hum fī syakkiy yal'abụn","at":"Tetapi mereka dalam keraguan, mereka bermain-main."},{"id":"10","ar":"فَارْتَقِبْ يَوْمَ تَأْتِى السَّمَاۤءُ بِدُخَانٍ مُّبِيْنٍ ","bc":"fartaqib yauma ta`tis-samā`u bidukhānim mubīn","at":"Maka tunggulah pada hari ketika langit membawa kabut yang tampak jelas,"},{"id":"11","ar":"يَغْشَى النَّاسَۗ هٰذَا عَذَابٌ اَلِيْمٌ ","bc":"yagsyan-nās, hāżā 'ażābun alīm","at":"yang meliputi manusia. Inilah azab yang pedih."},{"id":"12","ar":"رَبَّنَا اكْشِفْ عَنَّا الْعَذَابَ اِنَّا مُؤْمِنُوْنَ ","bc":"rabbanaksyif 'annal-'ażāba innā mu`minụn","at":"(Mereka berdoa), “Ya Tuhan kami, lenyapkanlah azab itu dari kami. Sungguh, kami akan beriman.”"},{"id":"13","ar":"اَنّٰى لَهُمُ الذِّكْرٰى وَقَدْ جَاۤءَهُمْ رَسُوْلٌ مُّبِيْنٌۙ ","bc":"annā lahumuż-żikrā wa qad jā`ahum rasụlum mubīn","at":"Bagaimana mereka dapat menerima peringatan, padahal (sebelumnya pun) seorang Rasul telah datang memberi penjelasan kepada mereka,"},{"id":"14","ar":"ثُمَّ تَوَلَّوْا عَنْهُ وَقَالُوْا مُعَلَّمٌ مَّجْنُوْنٌۘ ","bc":"ṡumma tawallau 'an-hu wa qālụ mu'allamum majnụn","at":"kemudian mereka berpaling darinya dan berkata, “Dia itu orang yang menerima ajaran (dari orang lain) dan orang gila.”"},{"id":"15","ar":"اِنَّا كَاشِفُوا الْعَذَابِ قَلِيْلًا اِنَّكُمْ عَاۤىِٕدُوْنَۘ ","bc":"innā kāsyiful-'ażābi qalīlan innakum 'ā`idụn","at":"Sungguh (kalau) Kami melenyapkan azab itu sedikit saja, tentu kamu akan kembali (ingkar)."},{"id":"16","ar":"يَوْمَ نَبْطِشُ الْبَطْشَةَ الْكُبْرٰىۚ اِنَّا مُنْتَقِمُوْنَ","bc":"yauma nabṭisyul-baṭsyatal-kubrā, innā muntaqimụn","at":"(Ingatlah) pada hari (ketika) Kami menghantam mereka dengan keras. Kami pasti memberi balasan."},{"id":"17","ar":"۞ وَلَقَدْ فَتَنَّا قَبْلَهُمْ قَوْمَ فِرْعَوْنَ وَجَاۤءَهُمْ رَسُوْلٌ كَرِيْمٌۙ ","bc":"wa laqad fatannā qablahum qauma fir'auna wa jā`ahum rasụlung karīm","at":"Dan sungguh, sebelum mereka Kami benar-benar telah menguji kaum Fir’aun dan telah datang kepada mereka seorang Rasul yang mulia,"},{"id":"18","ar":"اَنْ اَدُّوْٓا اِلَيَّ عِبَادَ اللّٰهِ ۗاِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌۙ ","bc":"an addū ilayya 'ibādallāh, innī lakum rasụlun amīn","at":"(dengan berkata), “Serahkanlah kepadaku hamba-hamba Allah (Bani Israil). Sesungguhnya aku adalah utusan (Allah) yang dapat kamu percaya,"},{"id":"19","ar":"وَّاَنْ لَّا تَعْلُوْا عَلَى اللّٰهِ ۚاِنِّيْٓ اٰتِيْكُمْ بِسُلْطٰنٍ مُّبِيْنٍۚ ","bc":"wa al lā ta'lụ 'alallāh, innī ātīkum bisulṭānim mubīn","at":"dan janganlah kamu menyombongkan diri terhadap Allah. Sungguh, aku datang kepadamu dengan membawa bukti yang nyata."},{"id":"20","ar":"وَاِنِّيْ عُذْتُ بِرَبِّيْ وَرَبِّكُمْ اَنْ تَرْجُمُوْنِۚ ","bc":"wa innī 'użtu birabbī wa rabbikum an tarjumụn","at":"Dan sesungguhnya aku berlindung kepada Tuhanku dan Tuhanmu, dari ancamanmu untuk merajamku,"},{"id":"21","ar":"وَاِنْ لَّمْ تُؤْمِنُوْا لِيْ فَاعْتَزِلُوْنِ ","bc":"wa il lam tu`minụ lī fa'tazilụn","at":"dan jika kamu tidak beriman kepadaku maka biarkanlah aku (memimpin Bani Israil).”"},{"id":"22","ar":"فَدَعَا رَبَّهٗٓ اَنَّ هٰٓؤُلَاۤءِ قَوْمٌ مُّجْرِمُوْنَ ","bc":"fa da'ā rabbahū anna hā`ulā`i qaumum mujrimụn","at":"Kemudian dia (Musa) berdoa kepada Tuhannya, “Sungguh, mereka ini adalah kaum yang berdosa (segerakanlah azab kepada mereka).”"},{"id":"23","ar":"فَاَسْرِ بِعِبَادِيْ لَيْلًا اِنَّكُمْ مُّتَّبَعُوْنَۙ ","bc":"fa asri bi'ibādī lailan innakum muttaba'ụn","at":"(Allah berfirman), “Karena itu berjalanlah dengan hamba-hamba-Ku pada malam hari, sesungguhnya kamu akan dikejar,"},{"id":"24","ar":"وَاتْرُكِ الْبَحْرَ رَهْوًاۗ اِنَّهُمْ جُنْدٌ مُّغْرَقُوْنَ ","bc":"watrukil-baḥra rahwā, innahum jundum mugraqụn","at":"dan biarkanlah laut itu terbelah. Sesungguhnya mereka, bala tentara yang akan ditenggelamkan.”"},{"id":"25","ar":"كَمْ تَرَكُوْا مِنْ جَنّٰتٍ وَّعُيُوْنٍۙ ","bc":"kam tarakụ min jannātiw wa 'uyụn","at":"Betapa banyak taman-taman dan mata air-mata air yang mereka tinggalkan,"},{"id":"26","ar":"وَّزُرُوْعٍ وَّمَقَامٍ كَرِيْمٍۙ ","bc":"wa zurụ'iw wa maqāming karīm","at":"juga kebun-kebun serta tempat-tempat kediaman yang indah,"},{"id":"27","ar":"وَّنَعْمَةٍ كَانُوْا فِيْهَا فٰكِهِيْنَۙ ","bc":"wa na'mating kānụ fīhā fākihīn","at":"dan kesenangan-kesenangan yang dapat mereka nikmati di sana,"},{"id":"28","ar":"كَذٰلِكَ ۗوَاَوْرَثْنٰهَا قَوْمًا اٰخَرِيْنَۚ","bc":"każālik, wa auraṡnāhā qauman ākharīn","at":"demikianlah, dan Kami wariskan (semua) itu kepada kaum yang lain."},{"id":"29","ar":"فَمَا بَكَتْ عَلَيْهِمُ السَّمَاۤءُ وَالْاَرْضُۗ وَمَا كَانُوْا مُنْظَرِيْنَ","bc":"fa mā bakat 'alaihimus-samā`u wal-arḍ, wa mā kānụ munẓarīn","at":"Maka langit dan bumi tidak menangisi mereka dan mereka pun tidak diberi penangguhan waktu."},{"id":"30","ar":"وَلَقَدْ نَجَّيْنَا بَنِيْٓ اِسْرَاۤءِيْلَ مِنَ الْعَذَابِ الْمُهِيْنِۙ ","bc":"wa laqad najjainā banī isrā`īla minal-'ażābil-muhīn","at":"Dan sungguh, telah Kami selamatkan Bani Israil dari siksaan yang menghinakan,"},{"id":"31","ar":"مِنْ فِرْعَوْنَ ۗاِنَّهٗ كَانَ عَالِيًا مِّنَ الْمُسْرِفِيْنَ ","bc":"min fir'aụn, innahụ kāna 'āliyam minal-musrifīn","at":"dari (siksaan) Fir‘aun, sungguh, dia itu orang yang sombong, termasuk orang-orang yang melampaui batas."},{"id":"32","ar":"وَلَقَدِ اخْتَرْنٰهُمْ عَلٰى عِلْمٍ عَلَى الْعٰلَمِيْنَ ۚ ","bc":"wa laqadikhtarnāhum 'alā 'ilmin 'alal-'ālamīn","at":"Dan sungguh, Kami pilih mereka (Bani Israil) dengan ilmu (Kami) di atas semua bangsa (pada masa itu)."},{"id":"33","ar":"وَاٰتَيْنٰهُمْ مِّنَ الْاٰيٰتِ مَا فِيْهِ بَلٰۤـؤٌا مُّبِيْنٌ ","bc":"wa ātaināhum minal-āyāti mā fīhi balā`um mubīn","at":"Dan telah Kami berikan kepada mereka di antara tanda-tanda (kebesaran Kami) sesuatu yang di dalamnya terdapat nikmat yang nyata."},{"id":"34","ar":"اِنَّ هٰٓؤُلَاۤءِ لَيَقُوْلُوْنَۙ ","bc":"inna hā`ulā`i layaqụlụn","at":"Sesungguhnya mereka (kaum musyrik) itu pasti akan berkata,"},{"id":"35","ar":"اِنْ هِيَ اِلَّا مَوْتَتُنَا الْاُوْلٰى وَمَا نَحْنُ بِمُنْشَرِيْنَ ","bc":"in hiya illā mautatunal-ụlā wa mā naḥnu bimunsyarīn","at":"”Tidak ada kematian selain kematian di dunia ini. Dan kami tidak akan dibangkitkan,"},{"id":"36","ar":"فَأْتُوْا بِاٰبَاۤىِٕنَآ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"fa`tụ bi`ābā`inā ing kuntum ṣādiqīn","at":"maka hadirkanlah (kembali) nenek moyang kami jika kamu orang yang benar.”"},{"id":"37","ar":"اَهُمْ خَيْرٌ اَمْ قَوْمُ تُبَّعٍۙ وَّالَّذِيْنَ مِنْ قَبْلِهِمْۗ اَهْلَكْنٰهُمْ اِنَّهُمْ كَانُوْا مُجْرِمِيْنَ","bc":"a hum khairun am qaumu tubba'iw wallażīna ming qablihim, ahlaknāhum innahum kānụ mujrimīn","at":"Apakah mereka (kaum musyrikin) yang lebih baik atau kaum Tubba‘, dan orang-orang yang sebelum mereka yang telah Kami binasakan karena mereka itu adalah orang-orang yang sungguh berdosa."},{"id":"38","ar":"وَمَا خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا لٰعِبِيْنَ","bc":"wa mā khalaqnas-samāwāti wal-arḍa wa mā bainahumā lā'ibīn","at":"Dan tidaklah Kami bermain-main menciptakan langit dan bumi dan apa yang ada di antara keduanya. "},{"id":"39","ar":"مَا خَلَقْنٰهُمَآ اِلَّا بِالْحَقِّ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ","bc":"mā khalaqnāhumā illā bil-ḥaqqi wa lākinna akṡarahum lā ya'lamụn","at":"Tidaklah Kami ciptakan keduanya melainkan dengan haq (benar), tetapi kebanyakan mereka tidak mengetahui."},{"id":"40","ar":"اِنَّ يَوْمَ الْفَصْلِ مِيْقَاتُهُمْ اَجْمَعِيْنَ ۙ ","bc":"inna yaumal-faṣli mīqātuhum ajma'īn","at":"Sungguh, pada hari keputusan (hari Kiamat) itu adalah waktu yang dijanjikan bagi mereka semuanya,"},{"id":"41","ar":"يَوْمَ لَا يُغْنِيْ مَوْلًى عَنْ مَّوْلًى شَيْـًٔا وَّلَا هُمْ يُنْصَرُوْنَۙ ","bc":"yauma lā yugnī maulan 'am maulan syai`aw wa lā hum yunṣarụn","at":"(yaitu) pada hari (ketika) seorang teman sama sekali tidak dapat memberi manfaat kepada teman lainnya dan mereka tidak akan mendapat pertolongan,"},{"id":"42","ar":"اِلَّا مَنْ رَّحِمَ اللّٰهُ ۗاِنَّهٗ هُوَ الْعَزِيْزُ الرَّحِيْمُ ","bc":"illā mar raḥimallāh, innahụ huwal-'azīzur-raḥīm","at":"Kecuali orang yang diberi rahmat oleh Allah. Sungguh, Dia Mahaperkasa, Maha Penyayang."},{"id":"43","ar":"اِنَّ شَجَرَتَ الزَّقُّوْمِۙ","bc":"inna syajarataz-zaqqụm","at":"Sungguh pohon zaqqum itu,"},{"id":"44","ar":"طَعَامُ الْاَثِيْمِ ۛ ","bc":"ṭa'āmul-aṡīm","at":"makanan bagi orang yang banyak dosa."},{"id":"45","ar":"كَالْمُهْلِ ۛ يَغْلِيْ فِى الْبُطُوْنِۙ ","bc":"kal-muhli yaglī fil-buṭụn","at":"Seperti cairan tembaga yang mendidih di dalam perut,"},{"id":"46","ar":"كَغَلْيِ الْحَمِيْمِ ۗ","bc":"kagalyil-ḥamīm","at":"seperti mendidihnya air yang sangat panas."},{"id":"47","ar":"خُذُوْهُ فَاعْتِلُوْهُ اِلٰى سَوَاۤءِ الْجَحِيْمِۙ ","bc":"khużụhu fa'tilụhu ilā sawā`il-jaḥīm","at":"”Peganglah dia kemudian seretlah dia sampai ke tengah-tengah neraka,"},{"id":"48","ar":"ثُمَّ صُبُّوْا فَوْقَ رَأْسِهٖ مِنْ عَذَابِ الْحَمِيْمِۗ","bc":"ṡumma ṣubbụ fauqa ra`sihī min 'ażābil-ḥamīm","at":"kemudian tuangkanlah di atas kepalanya azab (dari) air yang sangat panas.”"},{"id":"49","ar":"ذُقْۚ اِنَّكَ اَنْتَ الْعَزِيْزُ الْكَرِيْمُ ","bc":"żuq, innaka antal-'azīzul-karīm","at":"”Rasakanlah, sesungguhnya kamu benar-benar orang yang perkasa lagi mulia.”"},{"id":"50","ar":"اِنَّ هٰذَا مَا كُنْتُمْ بِهٖ تَمْتَرُوْنَ ","bc":"inna hāżā mā kuntum bihī tamtarụn","at":"Sungguh, inilah azab yang dahulu kamu ragukan."},{"id":"51","ar":"اِنَّ الْمُتَّقِيْنَ فِيْ مَقَامٍ اَمِيْنٍۙ","bc":"innal-muttaqīna fī maqāmin amīn","at":"Sungguh, orang-orang yang bertakwa berada dalam tempat yang aman,"},{"id":"52","ar":"فِيْ جَنّٰتٍ وَّعُيُوْنٍ ۙ","bc":"fī jannātiw wa 'uyụn","at":"(yaitu) di dalam taman-taman dan mata air-mata air,"},{"id":"53","ar":"يَّلْبَسُوْنَ مِنْ سُنْدُسٍ وَّاِسْتَبْرَقٍ مُّتَقٰبِلِيْنَۚ","bc":"yalbasụna min sundusiw wa istabraqim mutaqābilīn","at":"mereka memakai sutra yang halus dan sutra yang tebal, (duduk) berhadapan,"},{"id":"54","ar":"كَذٰلِكَۗ وَزَوَّجْنٰهُمْ بِحُوْرٍ عِيْنٍۗ ","bc":"każālik, wa zawwajnāhum biḥụrin 'īn","at":"demikianlah, kemudian Kami berikan kepada mereka pasangan bidadari yang bermata indah."},{"id":"55","ar":"يَدْعُوْنَ فِيْهَا بِكُلِّ فَاكِهَةٍ اٰمِنِيْنَۙ ","bc":"yad'ụna fīhā bikulli fākihatin āminīn","at":"Di dalamnya mereka dapat meminta segala macam buah-buahan dengan aman dan tenteram,"},{"id":"56","ar":"لَا يَذُوْقُوْنَ فِيْهَا الْمَوْتَ اِلَّا الْمَوْتَةَ الْاُوْلٰىۚ وَوَقٰىهُمْ عَذَابَ الْجَحِيْمِۙ ","bc":"lā yażụqụna fīhal-mauta illal-mautatal-ụlā, wa waqāhum 'ażābal-jaḥīm","at":"mereka tidak akan merasakan mati di dalamnya selain kematian pertama (di dunia). Allah melindungi mereka dari azab neraka,"},{"id":"57","ar":"فَضْلًا مِّنْ رَّبِّكَۚ ذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ","bc":"faḍlam mir rabbik, żālika huwal-fauzul-'aẓīm","at":"itu merupakan karunia dari Tuhanmu. Demikian itulah kemenangan yang agung."},{"id":"58","ar":"فَاِنَّمَا يَسَّرْنٰهُ بِلِسَانِكَ لَعَلَّهُمْ يَتَذَكَّرُوْنَ","bc":"fa innamā yassarnāhu bilisānika la'allahum yatażakkarụn","at":"Sungguh, Kami mudahkan Al-Qur'an itu dengan bahasamu agar mereka mendapat pelajaran."},{"id":"59","ar":"فَارْتَقِبْ اِنَّهُمْ مُّرْتَقِبُوْنَࣖ","bc":"fartaqib innahum murtaqibụn","at":"Maka tunggulah; sungguh, mereka itu (juga sedang) menunggu."}],al_adiyat=[{"id":"1","ar":"وَالْعٰدِيٰتِ ضَبْحًاۙ ","bc":"wal-'ādiyāti ḍab-ḥā","at":"Demi kuda perang yang berlari kencang terengah-engah,"},{"id":"2","ar":"فَالْمُوْرِيٰتِ قَدْحًاۙ","bc":"fal-mụriyāti qad-ḥā","at":"dan kuda yang memercikkan bunga api (dengan pukulan kuku kakinya),"},{"id":"3","ar":"فَالْمُغِيْرٰتِ صُبْحًاۙ","bc":"fal-mugīrāti ṣub-ḥā","at":"dan kuda yang menyerang (dengan tiba-tiba) pada waktu pagi,"},{"id":"4","ar":"فَاَثَرْنَ بِهٖ نَقْعًاۙ","bc":"fa aṡarna bihī naq'ā","at":"sehingga menerbangkan debu,"},{"id":"5","ar":"فَوَسَطْنَ بِهٖ جَمْعًاۙ","bc":"fa wasaṭna bihī jam'ā","at":"lalu menyerbu ke tengah-tengah kumpulan musuh,"},{"id":"6","ar":"اِنَّ الْاِنْسَانَ لِرَبِّهٖ لَكَنُوْدٌ ۚ","bc":"innal-insāna lirabbihī lakanụd","at":"sungguh, manusia itu sangat ingkar, (tidak bersyukur) kepada Tuhannya,"},{"id":"7","ar":"وَاِنَّهٗ عَلٰى ذٰلِكَ لَشَهِيْدٌۚ","bc":"wa innahụ 'alā żālika lasyahīd","at":"dan sesungguhnya dia (manusia) menyaksikan (mengakui) keingkarannya,"},{"id":"8","ar":"وَاِنَّهٗ لِحُبِّ الْخَيْرِ لَشَدِيْدٌ ۗ","bc":"wa innahụ liḥubbil-khairi lasyadīd","at":"dan sesungguhnya cintanya kepada harta benar-benar berlebihan."},{"id":"9","ar":"۞ اَفَلَا يَعْلَمُ اِذَا بُعْثِرَ مَا فِى الْقُبُوْرِۙ","bc":"a fa lā ya'lamu iżā bu'ṡira mā fil-qubụr","at":"Maka tidakkah dia mengetahui apabila apa yang di dalam kubur dikeluarkan,"},{"id":"10","ar":"وَحُصِّلَ مَا فِى الصُّدُوْرِۙ","bc":"wa huṣṣila mā fiṣ-ṣudụr","at":"dan apa yang tersimpan di dalam dada dilahirkan?"},{"id":"11","ar":"اِنَّ رَبَّهُمْ بِهِمْ يَوْمَىِٕذٍ لَّخَبِيْرٌ","bc":"inna rabbahum bihim yauma`iżil lakhabīr","at":"sungguh, Tuhan mereka pada hari itu Mahateliti terhadap keadaan mereka."}],al_ahqaf=[{"id":"1","ar":"حٰمۤ ۚ","bc":"ḥā mīm","at":"Ha Mim"},{"id":"2","ar":"تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِ ","bc":"tanzīlul-kitābi minallāhil-'azīzil-ḥakīm","at":"Kitab ini diturunkan dari Allah Yang Mahaperkasa, Mahabijaksana. "},{"id":"3","ar":"مَا خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَآ اِلَّا بِالْحَقِّ وَاَجَلٍ مُّسَمًّىۗ وَالَّذِيْنَ كَفَرُوْا عَمَّآ اُنْذِرُوْا مُعْرِضُوْنَ","bc":"mā khalaqnas-samāwāti wal-arḍa wa mā bainahumā illā bil-ḥaqqi wa ajalim musamman, wallażīna kafarụ 'ammā unżirụ mu'riḍụn","at":"Kami tidak menciptakan langit dan bumi dan apa yang ada di antara keduanya melainkan dengan (tujuan) yang benar dan dalam waktu yang ditentukan. Namun orang-orang yang kafir, berpaling dari peringatan yang diberikan kepada mereka. "},{"id":"4","ar":"قُلْ اَرَءَيْتُمْ مَّا تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ اَرُوْنِيْ مَاذَا خَلَقُوْا مِنَ الْاَرْضِ اَمْ لَهُمْ شِرْكٌ فِى السَّمٰوٰتِ ۖائْتُوْنِيْ بِكِتٰبٍ مِّنْ قَبْلِ هٰذَآ اَوْ اَثٰرَةٍ مِّنْ عِلْمٍ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"qul a ra`aitum mā tad'ụna min dụnillāhi arụnī māżā khalaqụ minal-arḍi am lahum syirkun fis-samāwāti`tụnī bikitābim ming qabli hāżā au aṡāratim min 'ilmin ing kuntum ṣādiqīn","at":"Katakanlah (Muhammad), “Terangkanlah (kepadaku) tentang apa yang kamu sembah selain Allah; perlihatkan kepadaku apa yang telah mereka ciptakan dari bumi atau adakah peran serta mereka dalam (penciptaan) langit? Bawalah kepadaku kitab yang sebelum (Al-Qur'an) ini atau peninggalan dari pengetahuan (orang-orang dahulu), jika kamu orang yang benar.” "},{"id":"5","ar":"وَمَنْ اَضَلُّ مِمَّنْ يَّدْعُوْا مِنْ دُوْنِ اللّٰهِ مَنْ لَّا يَسْتَجِيْبُ لَهٗٓ اِلٰى يَوْمِ الْقِيٰمَةِ وَهُمْ عَنْ دُعَاۤىِٕهِمْ غٰفِلُوْنَ ","bc":"wa man aḍallu mim may yad'ụ min dụnillāhi mal lā yastajību lahū ilā yaumil-qiyāmati wa hum 'an du'ā`ihim gāfilụn","at":"Dan siapakah yang lebih sesat daripada orang-orang yang menyembah selain Allah (sembahan) yang tidak dapat memperkenankan (doa)nya sampai hari Kiamat dan mereka lalai dari (memperhatikan) doa mereka? "},{"id":"6","ar":"وَاِذَا حُشِرَ النَّاسُ كَانُوْا لَهُمْ اَعْدَاۤءً وَّكَانُوْا بِعِبَادَتِهِمْ كٰفِرِيْنَ ","bc":"wa iżā ḥusyiran-nāsu kānụ lahum a'dā`aw wa kānụ bi'ibādatihim kāfirīn","at":"Dan apabila manusia dikumpulkan (pada hari Kiamat), sesembahan itu menjadi musuh mereka dan mengingkari pemujaan-pemujaan yang mereka lakukan kepadanya."},{"id":"7","ar":"وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ قَالَ الَّذِيْنَ كَفَرُوْا لِلْحَقِّ لَمَّا جَاۤءَهُمْۙ هٰذَا سِحْرٌ مُّبِيْنٌۗ","bc":"wa iżā tutlā 'alaihim āyātunā bayyināting qālallażīna kafarụ lil-ḥaqqi lammā jā`ahum hāżā siḥrum mubīn","at":"Dan apabila mereka dibacakan ayat-ayat Kami yang jelas, orang-orang yang kafir berkata ketika kebenaran itu datang kepada mereka, “Ini adalah sihir yang nyata.”"},{"id":"8","ar":"اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۗ قُلْ اِنِ افْتَرَيْتُهٗ فَلَا تَمْلِكُوْنَ لِيْ مِنَ اللّٰهِ شَيْـًٔا ۗهُوَ اَعْلَمُ بِمَا تُفِيْضُوْنَ فِيْهِۗ كَفٰى بِهٖ شَهِيْدًا ۢ بَيْنِيْ وَبَيْنَكُمْ ۗ وَهُوَ الْغَفُوْرُ الرَّحِيْمُ","bc":"am yaqụlụnaftarāh, qul iniftaraituhụ fa lā tamlikụna lī minallāhi syai`ā, huwa a'lamu bimā tufīḍụna fīh, kafā bihī syahīdam bainī wa bainakum, wa huwal-gafụrur-raḥīm","at":"Bahkan mereka berkata, “Dia (Muhammad) telah mengada-adakannya (Al-Qur'an).” Katakanlah, “Jika aku mengada-adakannya, maka kamu tidak kuasa sedikit pun menghindarkan aku dari (azab) Allah. Dia lebih tahu apa yang kamu percakapkan tentang Al-Qur'an itu. Cukuplah Dia menjadi saksi antara aku dengan kamu. Dia Maha Pengampun, Maha Penyayang.”"},{"id":"9","ar":"قُلْ مَا كُنْتُ بِدْعًا مِّنَ الرُّسُلِ وَمَآ اَدْرِيْ مَا يُفْعَلُ بِيْ وَلَا بِكُمْۗ اِنْ اَتَّبِعُ اِلَّا مَا يُوْحٰٓى اِلَيَّ وَمَآ اَنَا۠ اِلَّا نَذِيْرٌ مُّبِيْنٌ ","bc":"qul mā kuntu bid'am minar-rusuli wa mā adrī mā yuf'alu bī wa lā bikum, in attabi'u illā mā yụḥā ilayya wa mā ana illā nażīrum mubīn","at":"Katakanlah (Muhammad), “Aku bukanlah Rasul yang pertama di antara rasul-rasul dan aku tidak tahu apa yang akan diperbuat terhadapku dan terhadapmu. Aku hanyalah mengikuti apa yang diwahyukan kepadaku dan aku hanyalah pemberi peringatan yang menjelaskan.”"},{"id":"10","ar":"قُلْ اَرَءَيْتُمْ اِنْ كَانَ مِنْ عِنْدِ اللّٰهِ وَكَفَرْتُمْ بِهٖ وَشَهِدَ شَاهِدٌ مِّنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ عَلٰى مِثْلِهٖ فَاٰمَنَ وَاسْتَكْبَرْتُمْۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ","bc":"qul ara`aitum ing kāna min 'indillāhi wa kafartum bihī wa syahida syāhidum mim banī isrā`īla 'alā miṡlihī fa āmana wastakbartum, innallāha lā yahdil-qaumaẓ-ẓālimīn","at":"Katakanlah, “Terangkanlah kepadaku, bagaimana pendapatmu jika sebenarnya (Al-Qur'an) ini datang dari Allah, dan kamu mengingkarinya, padahal ada seorang saksi dari Bani Israil yang mengakui (kebenaran) yang serupa dengan (yang disebut dalam) Al-Qur'an lalu dia beriman, kamu menyombongkan diri. Sungguh, Allah tidak memberi petunjuk kepada orang-orang yang zalim.”"},{"id":"11","ar":"وَقَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوْا لَوْ كَانَ خَيْرًا مَّا سَبَقُوْنَآ اِلَيْهِۗ وَاِذْ لَمْ يَهْتَدُوْا بِهٖ فَسَيَقُوْلُوْنَ هٰذَآ اِفْكٌ قَدِيْمٌ ","bc":"wa qālallażīna kafarụ lillażīna āmanụ lau kāna khairam mā sabaqụnā ilaīh, wa iż lam yahtadụ bihī fa sayaqụlụna hāżā ifkung qadīm","at":"Dan orang-orang yang kafir berkata kepada orang-orang yang beriman, “Sekiranya Al-Qur'an itu sesuatu yang baik, tentu mereka tidak pantas mendahului kami (beriman) kepadanya.” Tetapi karena mereka tidak mendapat petunjuk dengannya maka mereka akan berkata, “Ini adalah dusta yang lama.”"},{"id":"12","ar":"وَمِنْ قَبْلِهٖ كِتٰبُ مُوْسٰٓى اِمَامًا وَّرَحْمَةً ۗوَهٰذَا كِتٰبٌ مُّصَدِّقٌ لِّسَانًا عَرَبِيًّا لِّيُنْذِرَ الَّذِيْنَ ظَلَمُوْا ۖوَبُشْرٰى لِلْمُحْسِنِيْنَ","bc":"wa ming qablihī kitābu mụsā imāmaw wa raḥmah, wa hāżā kitābum muṣaddiqul lisānan 'arabiyyal liyunżirallażīna ẓalamụ wa busyrā lil-muḥsinīn","at":"Dan sebelum (Al-Qur'an) itu telah ada Kitab Musa sebagai petunjuk dan rahmat. Dan (Al-Qur'an) ini adalah Kitab yang membenarkannya dalam bahasa Arab untuk memberi peringatan kepada orang-orang yang zalim dan memberi kabar gembira kepada orang-orang yang berbuat baik."},{"id":"13","ar":"اِنَّ الَّذِيْنَ قَالُوْا رَبُّنَا اللّٰهُ ثُمَّ اسْتَقَامُوْا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَۚ","bc":"innallażīna qālụ rabbunallāhu ṡummastaqāmụ fa lā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Sesungguhnya orang-orang yang berkata, “Tuhan kami adalah Allah,” kemudian mereka tetap istiqamah tidak ada rasa khawatir pada mereka, dan mereka tidak (pula) bersedih hati."},{"id":"14","ar":"اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِ خٰلِدِيْنَ فِيْهَاۚ جَزَاۤءً ۢبِمَا كَانُوْا يَعْمَلُوْنَ ","bc":"ulā`ika aṣ-ḥābul-jannati khālidīna fīhā, jazā`am bimā kānụ ya'malụn","at":"Mereka itulah para penghuni surga, kekal di dalamnya; sebagai balasan atas apa yang telah mereka kerjakan. "},{"id":"15","ar":"وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِ اِحْسَانًا ۗحَمَلَتْهُ اُمُّهٗ كُرْهًا وَّوَضَعَتْهُ كُرْهًا ۗوَحَمْلُهٗ وَفِصٰلُهٗ ثَلٰثُوْنَ شَهْرًا ۗحَتّٰىٓ اِذَا بَلَغَ اَشُدَّهٗ وَبَلَغَ اَرْبَعِيْنَ سَنَةًۙ قَالَ رَبِّ اَوْزِعْنِيْٓ اَنْ اَشْكُرَ نِعْمَتَكَ الَّتِيْٓ اَنْعَمْتَ عَلَيَّ وَعَلٰى وَالِدَيَّ وَاَنْ اَعْمَلَ صَالِحًا تَرْضٰىهُ وَاَصْلِحْ لِيْ فِيْ ذُرِّيَّتِيْۗ اِنِّيْ تُبْتُ اِلَيْكَ وَاِنِّيْ مِنَ الْمُسْلِمِيْنَ ","bc":"wa waṣṣainal-insāna biwālidaihi iḥsānā, ḥamalat-hu ummuhụ kurhaw wa waḍa'at-hu kurhā, wa ḥamluhụ wa fiṣāluhụ ṡalāṡụna syahrā, ḥattā iżā balaga asyuddahụ wa balaga arba'īna sanatang qāla rabbi auzi'nī an asykura ni'matakallatī an'amta 'alayya wa 'alā wālidayya wa an a'mala ṣāliḥan tarḍāhu wa aṣliḥ lī fī żurriyyatī, innī tubtu ilaika wa innī minal-muslimīn","at":"Dan Kami perintahkan kepada manusia agar berbuat baik kepada kedua orang tuanya. Ibunya telah mengandungnya dengan susah payah, dan melahirkannya dengan susah payah (pula). Masa mengandung sampai menyapihnya selama tiga puluh bulan, sehingga apabila dia (anak itu) telah dewasa dan umurnya mencapai empat puluh tahun dia berdoa, “Ya Tuhanku, berilah aku petunjuk agar aku dapat mensyukuri nikmat-Mu yang telah Engkau limpahkan kepadaku dan kepada kedua orang tuaku dan agar aku dapat berbuat kebajikan yang Engkau ridai; dan berilah aku kebaikan yang akan mengalir sampai kepada anak cucuku. Sesungguhnya aku bertobat kepada Engkau dan sungguh, aku termasuk orang muslim.”"},{"id":"16","ar":"اُولٰۤىِٕكَ الَّذِيْنَ نَتَقَبَّلُ عَنْهُمْ اَحْسَنَ مَا عَمِلُوْا وَنَتَجَاوَزُ عَنْ سَيِّاٰتِهِمْ فِيْٓ اَصْحٰبِ الْجَنَّةِۗ وَعْدَ الصِّدْقِ الَّذِيْ كَانُوْا يُوْعَدُوْنَ ","bc":"ulā`ikallażīna nataqabbalu 'an-hum aḥsana mā 'amilụ wa natajāwazu 'an sayyi`ātihim fī aṣ-ḥābil-jannah, wa'daṣ-ṣidqillażī kānụ yụ'adụn","at":"Mereka itulah orang-orang yang Kami terima amal baiknya yang telah mereka kerjakan dan (orang-orang) yang Kami maafkan kesalahan-kesalahannya, (mereka akan menjadi) penghuni-penghuni surga. Itu janji yang benar yang telah dijanjikan kepada mereka."},{"id":"17","ar":"وَالَّذِيْ قَالَ لِوَالِدَيْهِ اُفٍّ لَّكُمَآ اَتَعِدَانِنِيْٓ اَنْ اُخْرَجَ وَقَدْ خَلَتِ الْقُرُوْنُ مِنْ قَبْلِيْۚ وَهُمَا يَسْتَغِيْثٰنِ اللّٰهَ وَيْلَكَ اٰمِنْ ۖاِنَّ وَعْدَ اللّٰهِ حَقٌّۚ فَيَقُوْلُ مَا هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ ","bc":"wallażī qāla liwālidaihi uffil lakumā ata'idāninī an ukhraja wa qad khalatil-qurụnu ming qablī, wa humā yastagīṡānillāha wailaka āmin inna wa'dallāhi ḥaqq, fa yaqụlu mā hāżā illā asāṭīrul-awwalīn","at":"Dan orang yang berkata kepada kedua orang tuanya, “Ah.” Apakah kamu berdua memperingatkan kepadaku bahwa aku akan dibangkitkan (dari kubur), padahal beberapa umat sebelumku telah berlalu? Lalu kedua orang tuanya itu memohon pertolongan kepada Allah (seraya berkata), “Celaka kamu, berimanlah! Sesungguhnya janji Allah itu benar.” Lalu dia (anak itu) berkata, “Ini hanyalah dongeng orang-orang dahulu.”"},{"id":"18","ar":"اُولٰۤىِٕكَ الَّذِيْنَ حَقَّ عَلَيْهِمُ الْقَوْلُ فِيْٓ اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِّنَ الْجِنِّ وَالْاِنْسِ ۗاِنَّهُمْ كَانُوْا خٰسِرِيْنَ","bc":"ulā`ikallażīna ḥaqqa 'alaihimul-qaulu fī umaming qad khalat ming qablihim minal-jinni wal-ins, innahum kānụ khāsirīn","at":"Mereka itu orang-orang yang telah pasti terkena ketetapan (azab) bersama umat-umat dahulu sebelum mereka, dari (golongan) jin dan manusia. Mereka adalah orang-orang yang rugi."},{"id":"19","ar":"وَلِكُلٍّ دَرَجٰتٌ مِّمَّا عَمِلُوْاۚ وَلِيُوَفِّيَهُمْ اَعْمَالَهُمْ وَهُمْ لَا يُظْلَمُوْنَ ","bc":"wa likullin darajātum mimmā 'amilụ, wa liyuwaffiyahum a'mālahum wa hum lā yuẓlamụn","at":"Dan setiap orang memperoleh tingkatan sesuai dengan apa yang telah mereka kerjakan dan agar Allah mencukupkan balasan amal perbuatan mereka dan mereka tidak dirugikan."},{"id":"20","ar":"وَيَوْمَ يُعْرَضُ الَّذِيْنَ كَفَرُوْا عَلَى النَّارِۗ اَذْهَبْتُمْ طَيِّبٰتِكُمْ فِيْ حَيَاتِكُمُ الدُّنْيَا وَاسْتَمْتَعْتُمْ بِهَاۚ فَالْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُوْنِ بِمَا كُنْتُمْ تَسْتَكْبِرُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنْتُمْ تَفْسُقُوْنَ ","bc":"wa yauma yu'raḍullażīna kafarụ 'alan-nār, aż-habtum ṭayyibātikum fī ḥayātikumud-dun-yā wastamta'tum bihā, fal-yauma tujzauna 'ażābal-hụni bimā kuntum tastakbirụna fil-arḍi bigairil-ḥaqqi wa bimā kuntum tafsuqụn","at":"Dan (ingatlah) pada hari (ketika) orang-orang kafir dihadapkan ke neraka (seraya dikatakan kepada mereka), “Kamu telah menghabiskan (rezeki) yang baik untuk kehidupan duniamu dan kamu telah bersenang-senang (menikmati)nya; maka pada hari ini kamu dibalas dengan azab yang menghinakan karena kamu sombong di bumi tanpa mengindahkan kebenaran dan karena kamu berbuat durhaka (tidak taat kepada Allah).”"},{"id":"21","ar":"۞ وَاذْكُرْ اَخَا عَادٍۗ اِذْ اَنْذَرَ قَوْمَهٗ بِالْاَحْقَافِ وَقَدْ خَلَتِ النُّذُرُ مِنْۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهٖٓ اَلَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۗاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيْمٍ ","bc":"ważkur akhā 'ād, iż anżara qaumahụ bil-aḥqāfi wa qad khalatin-nużuru mim baini yadaihi wa min khalfihī allā ta'budū illallāh, innī akhāfu 'alaikum 'ażāba yaumin 'aẓīm","at":"Dan ingatlah (Hud) saudara kaum ‘Ad yaitu ketika dia mengingatkan kaumnya tentang bukit-bukit pasir dan sesungguhnya telah berlalu beberapa orang pemberi peringatan sebelumnya dan setelahnya (dengan berkata), “Janganlah kamu menyembah selain Allah, aku sungguh khawatir nanti kamu ditimpa azab pada hari yang besar.”"},{"id":"22","ar":"قَالُوْٓا اَجِئْتَنَا لِتَأْفِكَنَا عَنْ اٰلِهَتِنَاۚ فَأْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ","bc":"qālū a ji`tanā lita`fikanā 'an ālihatinā, fa`tinā bimā ta'idunā ing kunta minaṣ-ṣādiqīn","at":"Mereka menjawab, “Apakah engkau datang kepada kami untuk memalingkan kami dari (menyembah) tuhan-tuhan kami? Maka datangkanlah kepada kami azab yang telah engkau ancamkan kepada kami jika engkau termasuk orang yang benar.”"},{"id":"23","ar":"قَالَ اِنَّمَا الْعِلْمُ عِنْدَ اللّٰهِ ۖوَاُبَلِّغُكُمْ مَّآ اُرْسِلْتُ بِهٖ وَلٰكِنِّيْٓ اَرٰىكُمْ قَوْمًا تَجْهَلُوْنَ ","bc":"qāla innamal-'ilmu 'indallāhi wa uballigukum mā ursiltu bihī wa lākinnī arākum qauman taj-halụn","at":"Dia (Hud) berkata, “Sesungguhnya ilmu (tentang itu) hanya pada Allah dan aku (hanya) menyampaikan kepadamu apa yang diwahyukan kepadaku, tetapi aku melihat kamu adalah kaum yang berlaku bodoh.”"},{"id":"24","ar":"فَلَمَّا رَاَوْهُ عَارِضًا مُّسْتَقْبِلَ اَوْدِيَتِهِمْ قَالُوْا هٰذَا عَارِضٌ مُّمْطِرُنَا ۗبَلْ هُوَ مَا اسْتَعْجَلْتُمْ بِهٖ ۗرِيْحٌ فِيْهَا عَذَابٌ اَلِيْمٌۙ","bc":"fa lammā ra`auhu 'āriḍam mustaqbila audiyatihim qālụ hāżā 'āriḍum mumṭirunā, bal huwa masta'jaltum bih, rīḥun fīhā 'ażābun alīm","at":"Maka ketika mereka melihat azab itu berupa awan yang menuju ke lembah-lembah mereka, mereka berkata, “Inilah awan yang akan menurunkan hujan kepada kita.” (Bukan!) Tetapi itulah azab yang kamu minta agar disegerakan datangnya (yaitu) angin yang mengandung azab yang pedih,"},{"id":"25","ar":"تُدَمِّرُ كُلَّ شَيْءٍۢ بِاَمْرِ رَبِّهَا فَاَصْبَحُوْا لَا يُرٰىٓ اِلَّا مَسٰكِنُهُمْۗ كَذٰلِكَ نَجْزِى الْقَوْمَ الْمُجْرِمِيْنَ ","bc":"tudammiru kulla syai`im bi`amri rabbihā fa`aṣbaḥụ lā yurā illā masākinuhum, każālika najzil-qaumal-mujrimīn","at":"yang menghancurkan segala sesuatu dengan perintah Tuhannya, sehingga mereka (kaum ‘Ad) menjadi tidak tampak lagi (di bumi) kecuali hanya (bekas-bekas) tempat tinggal mereka. Demikianlah Kami memberi balasan kepada kaum yang berdosa."},{"id":"26","ar":"وَلَقَدْ مَكَّنّٰهُمْ فِيْمَآ اِنْ مَّكَّنّٰكُمْ فِيْهِ وَجَعَلْنَا لَهُمْ سَمْعًا وَّاَبْصَارًا وَّاَفْـِٕدَةًۖ فَمَآ اَغْنٰى عَنْهُمْ سَمْعُهُمْ وَلَآ اَبْصَارُهُمْ وَلَآ اَفْـِٕدَتُهُمْ مِّنْ شَيْءٍ اِذْ كَانُوْا يَجْحَدُوْنَ بِاٰيٰتِ اللّٰهِ وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ","bc":"wa laqad makkannāhum fīmā im makkannākum fīhi wa ja'alnā lahum sam'aw wa abṣāraw wa af`idatan fa mā agnā 'an-hum sam'uhum wa lā abṣāruhum wa lā af`idatuhum min syai`in iż kānụ yaj-ḥadụna bi`āyātillāhi wa ḥāqa bihim mā kānụ bihī yastahzi`ụn","at":"Dan sungguh, Kami telah meneguhkan kedudukan mereka (dengan kemakmuran dan kekuatan) yang belum pernah Kami berikan kepada kamu dan Kami telah memberikan kepada mereka pendengaran, penglihatan, dan hati; tetapi pendengaran, penglihatan, dan hati mereka itu tidak berguna sedikit pun bagi mereka, karena mereka (selalu) mengingkari ayat-ayat Allah dan (ancaman) azab yang dahulu mereka perolok-olokkan telah mengepung mereka."},{"id":"27","ar":"وَلَقَدْ اَهْلَكْنَا مَا حَوْلَكُمْ مِّنَ الْقُرٰى وَصَرَّفْنَا الْاٰيٰتِ لَعَلَّهُمْ يَرْجِعُوْنَ","bc":"wa laqad ahlaknā mā ḥaulakum minal-qurā wa ṣarrafnal-āyāti la'allahum yarji'ụn","at":"Dan sungguh, telah Kami binasakan negeri-negeri di sekitarmu dan juga telah Kami menjelaskan berulang-ulang tanda-tanda (kebesaran Kami), agar mereka kembali (bertobat)."},{"id":"28","ar":"فَلَوْلَا نَصَرَهُمُ الَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ قُرْبَانًا اٰلِهَةً ۗبَلْ ضَلُّوْا عَنْهُمْۚ وَذٰلِكَ اِفْكُهُمْ وَمَا كَانُوْا يَفْتَرُوْنَ ","bc":"falau lā naṣarahumullażīnattakhażụ min dụnillāhi qurbānan ālihah, bal ḍallụ 'an-hum, wa żālika ifkuhum wa mā kānụ yaftarụn","at":"Maka mengapa (berhala-berhala dan tuhan-tuhan) yang mereka sembah selain Allah untuk mendekatkan diri (kepada-Nya) tidak dapat menolong mereka? Bahkan tuhan-tuhan itu telah lenyap dari mereka? Dan itulah akibat kebohongan mereka dan apa yang dahulu mereka ada-adakan."},{"id":"29","ar":"وَاِذْ صَرَفْنَآ اِلَيْكَ نَفَرًا مِّنَ الْجِنِّ يَسْتَمِعُوْنَ الْقُرْاٰنَۚ فَلَمَّا حَضَرُوْهُ قَالُوْٓا اَنْصِتُوْاۚ فَلَمَّا قُضِيَ وَلَّوْا اِلٰى قَوْمِهِمْ مُّنْذِرِيْنَ ","bc":"wa iż ṣarafnā ilaika nafaram minal-jinni yastami'ụnal-qur`ān, fa lammā ḥaḍarụhu qālū anṣitụ, fa lammā quḍiya wallau ilā qaumihim munżirīn","at":"Dan (ingatlah) ketika Kami hadapkan kepadamu (Muhammad) serombongan jin yang mendengarkan (bacaan) Al-Qur'an, maka ketika mereka menghadiri (pembacaan)nya mereka berkata, “Diamlah kamu (untuk mendengarkannya)!” Maka ketika telah selesai mereka kembali kepada kaumnya (untuk) memberi peringatan."},{"id":"30","ar":"قَالُوْا يٰقَوْمَنَآ اِنَّا سَمِعْنَا كِتٰبًا اُنْزِلَ مِنْۢ بَعْدِ مُوْسٰى مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ يَهْدِيْٓ اِلَى الْحَقِّ وَاِلٰى طَرِيْقٍ مُّسْتَقِيْمٍ ","bc":"qālụ yā qaumanā innā sami'nā kitāban unzila mim ba'di mụsā muṣaddiqal limā baina yadaihi yahdī ilal-ḥaqqi wa ilā ṭarīqim mustaqīm","at":"Mereka berkata, “Wahai kaum kami! Sungguh, kami telah mendengarkan Kitab (Al-Qur'an) yang diturunkan setelah Musa, membenarkan (kitab-kitab) yang datang sebelumnya, membimbing kepada kebenaran dan kepada jalan yang lurus."},{"id":"31","ar":"يٰقَوْمَنَآ اَجِيْبُوْا دَاعِيَ اللّٰهِ وَاٰمِنُوْا بِهٖ يَغْفِرْ لَكُمْ مِّنْ ذُنُوْبِكُمْ وَيُجِرْكُمْ مِّنْ عَذَابٍ اَلِيْمٍ ","bc":"yā qaumanā ajībụ dā'iyallāhi wa āminụ bihī yagfir lakum min żunụbikum wa yujirkum min 'ażābin alīm","at":"Wahai kaum kami! Terimalah (seruan) orang (Muhammad) yang menyeru kepada Allah. Dan berimanlah kepada-Nya, niscaya Dia akan mengampuni dosa-dosamu dan melepaskan kamu dari azab yang pedih."},{"id":"32","ar":"وَمَنْ لَّا يُجِبْ دَاعِيَ اللّٰهِ فَلَيْسَ بِمُعْجِزٍ فِى الْاَرْضِ وَلَيْسَ لَهٗ مِنْ دُوْنِهٖٓ اَوْلِيَاۤءُ ۗ اُولٰۤىِٕكَ فِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"wa mal lā yujib dā'iyallāhi fa laisa bimu'jizin fil-arḍi wa laisa lahụ min dụnihī auliyā`, ulā`ika fī ḍalālim mubīn","at":"Dan barang siapa tidak menerima (seruan) orang yang menyeru kepada Allah (Muhammad) maka dia tidak akan dapat melepaskan diri dari siksa Allah di bumi padahal tidak ada pelindung baginya selain Allah. Mereka berada dalam kesesatan yang nyata.”"},{"id":"33","ar":"اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَلَمْ يَعْيَ بِخَلْقِهِنَّ بِقٰدِرٍ عَلٰٓى اَنْ يُّحْيِ َۧ الْمَوْتٰى ۗبَلٰٓى اِنَّهٗ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"a wa lam yarau annallāhallażī khalaqas-samāwāti wal-arḍa wa lam ya'ya bikhalqihinna biqādirin 'alā ay yuḥyiyal-mautā, balā innahụ 'alā kulli syai`ing qadīr","at":"Dan tidakkah mereka memperhatikan bahwa sesungguhnya Allah yang menciptakan langit dan bumi dan Dia tidak merasa payah karena menciptakannya, dan Dia kuasa menghidupkan yang mati? Begitulah; sungguh, Dia Mahakuasa atas segala sesuatu."},{"id":"34","ar":"وَيَوْمَ يُعْرَضُ الَّذِيْنَ كَفَرُوْا عَلَى النَّارِۗ اَلَيْسَ هٰذَا بِالْحَقِّ ۗ قَالُوْا بَلٰى وَرَبِّنَا ۗقَالَ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ ","bc":"wa yauma yu'raḍullażīna kafarụ 'alan-nār, a laisa hāżā bil-ḥaqq, qālụ balā wa rabbinā, qāla fa żụqul-'ażāba bimā kuntum takfurụn","at":"Dan (ingatlah) pada hari (ketika) orang-orang yang kafir dihadapkan kepada neraka, (mereka akan ditanya), “Bukankah (azab) ini benar?” Mereka menjawab, “Ya benar, demi Tuhan kami.” Allah berfirman, “Maka rasakanlah azab ini disebabkan dahulu kamu mengingkarinya.”"},{"id":"35","ar":"فَاصْبِرْ كَمَا صَبَرَ اُولُوا الْعَزْمِ مِنَ الرُّسُلِ وَلَا تَسْتَعْجِلْ لَّهُمْ ۗ كَاَنَّهُمْ يَوْمَ يَرَوْنَ مَا يُوْعَدُوْنَۙ لَمْ يَلْبَثُوْٓا اِلَّا سَاعَةً مِّنْ نَّهَارٍ ۗ بَلٰغٌ ۚفَهَلْ يُهْلَكُ اِلَّا الْقَوْمُ الْفٰسِقُوْنَ","bc":"faṣbir kamā ṣabara ulul-'azmi minar-rusuli wa lā tasta'jil lahum, ka`annahum yauma yarauna mā yụ'adụna lam yalbaṡū illā sā'atam min nahār, balāg, fa hal yuhlaku illal-qaumul-fāsiqụn","at":"Maka bersabarlah engkau (Muhammad) sebagaimana kesabaran rasul-rasul yang memiliki keteguhan hati dan janganlah engkau meminta agar azab disegerakan untuk mereka. Pada hari mereka melihat azab yang dijanjikan, mereka merasa seolah-olah mereka tinggal (di dunia) hanya sesaat saja pada siang hari. Tugasmu hanya menyampaikan. Maka tidak ada yang dibinasakan kecuali kaum yang fasik (tidak taat kepada Allah)."}],al_ahzab=[{"id":"1","ar":"يٰٓاَيُّهَا النَّبِيُّ اتَّقِ اللّٰهَ وَلَا تُطِعِ الْكٰفِرِيْنَ وَالْمُنٰفِقِيْنَ ۗاِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًاۙ ","bc":"yā ayyuhan-nabiyyuttaqillāha wa lā tuṭi'il-kāfirīna wal-munāfiqīn, innallāha kāna 'alīman ḥakīmā","at":"Wahai Nabi! Bertakwalah kepada Allah dan janganlah engkau menuruti (keinginan) orang-orang kafir dan orang-orang munafik. Sesungguhnya Allah Maha Mengetahui, Mahabijaksana,"},{"id":"2","ar":"وَّاتَّبِعْ مَا يُوْحٰىٓ اِلَيْكَ مِنْ رَّبِّكَ ۗاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًاۙ ","bc":"wattabi' mā yụḥā ilaika mir rabbik, innallāha kāna bimā ta'malụna khabīrā","at":"dan ikutilah apa yang diwahyukan Tuhanmu kepadamu. Sungguh, Allah Mahateliti terhadap apa yang kamu kerjakan,"},{"id":"3","ar":"وَّتَوَكَّلْ عَلَى اللّٰهِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا","bc":"wa tawakkal 'alallāh, wa kafā billāhi wakīlā","at":"dan bertawakallah kepada Allah. Dan cukuplah Allah sebagai pemelihara."},{"id":"4","ar":"مَا جَعَلَ اللّٰهُ لِرَجُلٍ مِّنْ قَلْبَيْنِ فِيْ جَوْفِهٖ ۚوَمَا جَعَلَ اَزْوَاجَكُمُ الّٰـِٕۤيْ تُظٰهِرُوْنَ مِنْهُنَّ اُمَّهٰتِكُمْ ۚوَمَا جَعَلَ اَدْعِيَاۤءَكُمْ اَبْنَاۤءَكُمْۗ ذٰلِكُمْ قَوْلُكُمْ بِاَفْوَاهِكُمْ ۗوَاللّٰهُ يَقُوْلُ الْحَقَّ وَهُوَ يَهْدِى السَّبِيْلَ","bc":"mā ja'alallāhu lirajulim ming qalbaini fī jaufih, wa mā ja'ala azwājakumul-lā`ī tuẓāhirụna min-hunna ummahātikum, wa mā ja'ala ad'iyā`akum abnā`akum, żālikum qaulukum bi`afwāhikum, wallāhu yaqụlul-ḥaqqa wa huwa yahdis-sabīl","at":"Allah tidak menjadikan bagi seseorang dua hati dalam rongganya; dan Dia tidak menjadikan istri-istrimu yang kamu zihar itu sebagai ibumu, dan Dia tidak menjadikan anak angkatmu sebagai anak kandungmu (sendiri). Yang demikian itu hanyalah perkataan di mulutmu saja. Allah mengatakan yang sebenarnya dan Dia menunjukkan jalan (yang benar)."},{"id":"5","ar":"اُدْعُوْهُمْ لِاٰبَاۤىِٕهِمْ هُوَ اَقْسَطُ عِنْدَ اللّٰهِ ۚ فَاِنْ لَّمْ تَعْلَمُوْٓا اٰبَاۤءَهُمْ فَاِخْوَانُكُمْ فِى الدِّيْنِ وَمَوَالِيْكُمْ ۗوَلَيْسَ عَلَيْكُمْ جُنَاحٌ فِيْمَآ اَخْطَأْتُمْ بِهٖ وَلٰكِنْ مَّا تَعَمَّدَتْ قُلُوْبُكُمْ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا","bc":"ud'ụhum li`ābā`ihim huwa aqsaṭu 'indallāh, fa il lam ta'lamū ābā`ahum fa ikhwānukum fid-dīni wa mawālīkum, wa laisa 'alaikum junāḥun fīmā akhṭa`tum bihī wa lākim mā ta'ammadat qulụbukum, wa kānallāhu gafụrar raḥīmā","at":"Panggillah mereka (anak angkat itu) dengan (memakai) nama bapak-bapak mereka; itulah yang adil di sisi Allah, dan jika kamu tidak mengetahui bapak mereka, maka (panggillah mereka sebagai) saudara-saudaramu seagama dan maula-maulamu. Dan tidak ada dosa atasmu jika kamu khilaf tentang itu, tetapi (yang ada dosanya) apa yang disengaja oleh hatimu. Allah Maha Pengampun, Maha Penyayang."},{"id":"6","ar":"اَلنَّبِيُّ اَوْلٰى بِالْمُؤْمِنِيْنَ مِنْ اَنْفُسِهِمْ وَاَزْوَاجُهٗٓ اُمَّهٰتُهُمْ ۗوَاُولُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ فِيْ كِتٰبِ اللّٰهِ مِنَ الْمُؤْمِنِيْنَ وَالْمُهٰجِرِيْنَ اِلَّآ اَنْ تَفْعَلُوْٓا اِلٰٓى اَوْلِيَاۤىِٕكُمْ مَّعْرُوْفًا ۗ كَانَ ذٰلِكَ فِى الْكِتٰبِ مَسْطُوْرًا","bc":"an-nabiyyu aulā bil-mu`minīna min anfusihim wa azwājuhū ummahātuhum, wa ulul-ar-ḥāmi ba'ḍuhum aulā biba'ḍin fī kitābillāhi minal-mu`minīna wal-muhājirīna illā an taf'alū ilā auliyā`ikum ma'rụfā, kāna żālika fil-kitābi masṭụrā","at":"Nabi itu lebih utama bagi orang-orang mukmin dibandingkan diri mereka sendiri dan istri-istrinya adalah ibu-ibu mereka. Orang-orang yang mempunyai hubungan darah satu sama lain lebih berhak (waris-mewarisi) di dalam Kitab Allah daripada orang-orang mukmin dan orang-orang Muhajirin, kecuali kalau kamu hendak berbuat baikkepada saudara-saudaramu (seagama). Demikianlah telah tertulis dalam Kitab (Allah)."},{"id":"7","ar":"وَاِذْ اَخَذْنَا مِنَ النَّبِيّٖنَ مِيْثَاقَهُمْ وَمِنْكَ وَمِنْ نُّوْحٍ وَّاِبْرٰهِيْمَ وَمُوْسٰى وَعِيْسَى ابْنِ مَرْيَمَ ۖوَاَخَذْنَا مِنْهُمْ مِّيْثَاقًا غَلِيْظًاۙ","bc":"wa iż akhażnā minan-nabiyyīna mīṡāqahum wa mingka wa min nụḥiw wa ibrāhīma wa mụsā wa 'īsabni maryama wa akhażnā min-hum mīṡāqan galīẓā","at":"Dan (ingatlah) ketika Kami mengambil perjanjian dari para nabi dan dari engkau (sendiri), dari Nuh, Ibrahim, Musa dan Isa putra Maryam, dan Kami telah mengambil dari mereka perjanjian yang teguh,"},{"id":"8","ar":"لِّيَسْـَٔلَ الصّٰدِقِيْنَ عَنْ صِدْقِهِمْ ۚوَاَعَدَّ لِلْكٰفِرِيْنَ عَذَابًا اَلِيْمًا","bc":"liyas`alaṣ-ṣādiqīna 'an ṣidqihim, wa a'adda lil-kāfirīna 'ażāban alīmā","at":"agar Dia menanyakan kepada orang-orang yang benar tentang kebenaran mereka. Dia menyediakan azab yang pedih bagi orang-orang kafir."},{"id":"9","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَاۤءَتْكُمْ جُنُوْدٌ فَاَرْسَلْنَا عَلَيْهِمْ رِيْحًا وَّجُنُوْدًا لَّمْ تَرَوْهَا ۗوَكَانَ اللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرًاۚ ","bc":"yā ayyuhallażīna āmanużkurụ ni'matallāhi 'alaikum iż jā`atkum junụdun fa arsalnā 'alaihim rīḥaw wa junụdal lam tarauhā, wa kānallāhu bimā ta'malụna baṣīrā","at":"Wahai orang-orang yang beriman! Ingatlah akan nikmat Allah (yang telah dikaruniakan) kepadamu ketika bala tentara datang kepadamu, lalu Kami kirimkan kepada mereka angin topan dan bala tentara yang tidak dapat terlihat olehmu. Allah Maha Melihat apa yang kamu kerjakan."},{"id":"10","ar":"اِذْ جَاۤءُوْكُمْ مِّنْ فَوْقِكُمْ وَمِنْ اَسْفَلَ مِنْكُمْ وَاِذْ زَاغَتِ الْاَبْصَارُ وَبَلَغَتِ الْقُلُوْبُ الْحَنَاجِرَ وَتَظُنُّوْنَ بِاللّٰهِ الظُّنُوْنَا۠ ۗ","bc":"iż jā`ụkum min fauqikum wa min asfala mingkum wa iż zāgatil-abṣāru wa balagatil-qulụbul-ḥanājira wa taẓunnụna billāhiẓ-ẓunụnā","at":"(Yaitu) ketika mereka datang kepadamu dari atas dan dari bawahmu, dan ketika penglihatan(mu) terpana dan hatimu menyesak sampai ke tenggorokan dan kamu berprasangka yang bukan-bukan terhadap Allah."},{"id":"11","ar":"هُنَالِكَ ابْتُلِيَ الْمُؤْمِنُوْنَ وَزُلْزِلُوْا زِلْزَالًا شَدِيْدًا","bc":"hunālikabtuliyal-mu`minụna wa zulzilụ zilzālan syadīdā","at":"Di situlah diuji orang-orang mukmin dan digoncangkan (hatinya) dengan goncangan yang dahsyat."},{"id":"12","ar":"وَاِذْ يَقُوْلُ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ مَّا وَعَدَنَا اللّٰهُ وَرَسُوْلُهٗٓ اِلَّا غُرُوْرًا","bc":"wa iż yaqụlul-munāfiqụna wallażīna fī qulụbihim maraḍum mā wa'adanallāhu wa rasụluhū illā gurụrā","at":"Dan (ingatlah) ketika orang-orang munafik dan orang-orang yang hatinya berpenyakit berkata, “Yang dijanjikan Allah dan Rasul-Nya kepada kami hanya tipu daya belaka.”"},{"id":"13","ar":"وَاِذْ قَالَتْ طَّاۤىِٕفَةٌ مِّنْهُمْ يٰٓاَهْلَ يَثْرِبَ لَا مُقَامَ لَكُمْ فَارْجِعُوْا ۚوَيَسْتَأْذِنُ فَرِيْقٌ مِّنْهُمُ النَّبِيَّ يَقُوْلُوْنَ اِنَّ بُيُوْتَنَا عَوْرَةٌ ۗوَمَا هِيَ بِعَوْرَةٍ ۗاِنْ يُّرِيْدُوْنَ اِلَّا فِرَارًا","bc":"wa iż qālat ṭā`ifatum min-hum yā ahla yaṡriba lā muqāma lakum farji'ụ, wa yasta`żinu farīqum min-humun-nabiyya yaqụlụna inna buyụtanā 'aurah, wa mā hiya bi'aurah, iy yurīdụna illā firārā","at":"Dan (ingatlah) ketika segolongan di antara mereka berkata, “Wahai penduduk Yasrib (Madinah)! Tidak ada tempat bagimu, maka kembalilah kamu.” Dan sebagian dari mereka meminta izin kepada Nabi (untuk kembali pulang) dengan berkata, “Sesungguhnya rumah-rumah kami terbuka (tidak ada penjaga).” Padahal rumah-rumah itu tidak terbuka, mereka hanyalah hendak lari."},{"id":"14","ar":"وَلَوْ دُخِلَتْ عَلَيْهِمْ مِّنْ اَقْطَارِهَا ثُمَّ سُـِٕلُوا الْفِتْنَةَ لَاٰتَوْهَا وَمَا تَلَبَّثُوْا بِهَآ اِلَّا يَسِيْرًا","bc":"walau dukhilat 'alaihim min aqṭārihā ṡumma su`ilul-fitnata la`ātauhā wa mā talabbaṡụ bihā illā yasīrā","at":"Dan kalau (Yasrib) diserang dari segala penjuru, dan mereka diminta agar membuat kekacauan, niscaya mereka mengerjakannya; dan hanya sebentar saja mereka menunggu."},{"id":"15","ar":"وَلَقَدْ كَانُوْا عَاهَدُوا اللّٰهَ مِنْ قَبْلُ لَا يُوَلُّوْنَ الْاَدْبَارَ ۗوَكَانَ عَهْدُ اللّٰهِ مَسْـُٔوْلًا","bc":"wa laqad kānụ 'āhadullāha ming qablu lā yuwallụnal-adbār, wa kāna 'ahdullāhi mas`ụlā","at":"Dan sungguh, mereka sebelum itu telah berjanji kepada Allah, tidak akan berbalik ke belakang (mundur). Dan perjanjian dengan Allah akan diminta pertanggungjawabannya."},{"id":"16","ar":"قُلْ لَّنْ يَّنْفَعَكُمُ الْفِرَارُ اِنْ فَرَرْتُمْ مِّنَ الْمَوْتِ اَوِ الْقَتْلِ وَاِذًا لَّا تُمَتَّعُوْنَ اِلَّا قَلِيْلًا ","bc":"qul lay yanfa'akumul-firāru in farartum minal-mauti awil-qatli wa iżal lā tumatta'ụna illā qalīlā","at":"Katakanlah (Muhammad), “Lari tidaklah berguna bagimu, jika kamu melarikan diri dari kematian atau pembunuhan, dan jika demikian (kamu terhindar dari kematian) kamu hanya akan mengecap kesenangan sebentar saja.”"},{"id":"17","ar":"قُلْ مَنْ ذَا الَّذِيْ يَعْصِمُكُمْ مِّنَ اللّٰهِ اِنْ اَرَادَ بِكُمْ سُوْۤءًا اَوْ اَرَادَ بِكُمْ رَحْمَةً ۗوَلَا يَجِدُوْنَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ وَلِيًّا وَّلَا نَصِيْرًا","bc":"qul man żallażī ya'ṣimukum minallāhi in arāda bikum sū`an au arāda bikum raḥmah, wa lā yajidụna lahum min dụnillāhi waliyyaw wa lā naṣīrā","at":"Katakanlah, “Siapakah yang dapat melindungi kamu dari (ketentuan) Allah jika Dia menghendaki bencana atasmu atau menghendaki rahmat untuk dirimu?” Mereka itu tidak akan mendapatkan pelindung dan penolong selain Allah."},{"id":"18","ar":"۞ قَدْ يَعْلَمُ اللّٰهُ الْمُعَوِّقِيْنَ مِنْكُمْ وَالْقَاۤىِٕلِيْنَ لِاِخْوَانِهِمْ هَلُمَّ اِلَيْنَا ۚوَلَا يَأْتُوْنَ الْبَأْسَ اِلَّا قَلِيْلًاۙ","bc":"qad ya'lamullāhul-mu'awwiqīna mingkum wal-qā`ilīna li`ikhwānihim halumma ilainā, wa lā ya`tụnal-ba`sa illā qalīlā","at":"Sungguh, Allah mengetahui orang-orang yang menghalang-halangi di antara kamu dan orang yang berkata kepada saudara-saudaranya, “Marilah bersama kami.” Tetapi mereka datang berperang hanya sebentar,"},{"id":"19","ar":"اَشِحَّةً عَلَيْكُمْ ۖ فَاِذَا جَاۤءَ الْخَوْفُ رَاَيْتَهُمْ يَنْظُرُوْنَ اِلَيْكَ تَدُوْرُ اَعْيُنُهُمْ كَالَّذِيْ يُغْشٰى عَلَيْهِ مِنَ الْمَوْتِۚ فَاِذَا ذَهَبَ الْخَوْفُ سَلَقُوْكُمْ بِاَلْسِنَةٍ حِدَادٍ اَشِحَّةً عَلَى الْخَيْرِۗ اُولٰۤىِٕكَ لَمْ يُؤْمِنُوْا فَاَحْبَطَ اللّٰهُ اَعْمَالَهُمْۗ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا","bc":"asyiḥḥatan 'alaikum fa iżā jā`al-khaufu ra`aitahum yanẓurụna ilaika tadụru a'yunuhum kallażī yugsyā 'alaihi minal-maụt, fa iżā żahabal-khaufu salaqụkum bi`alsinatin ḥidādin asyiḥḥatan 'alal-khaīr, ulā`ika lam yu`minụ fa aḥbaṭallāhu a'mālahum, wa kāna żālika 'alallāhi yasīrā","at":"mereka kikir terhadapmu. Apabila datang ketakutan (bahaya), kamu lihat mereka itu memandang kepadamu dengan mata yang terbalik-balik seperti orang yang pingsan karena akan mati, dan apabila ketakutan telah hilang, mereka mencaci kamu dengan lidah yang tajam, sedang mereka kikir untuk berbuat kebaikan. Mereka itu tidak beriman, maka Allah menghapus amalnya. Dan yang demikian itu mudah bagi Allah."},{"id":"20","ar":"يَحْسَبُوْنَ الْاَحْزَابَ لَمْ يَذْهَبُوْا ۚوَاِنْ يَّأْتِ الْاَحْزَابُ يَوَدُّوْا لَوْ اَنَّهُمْ بَادُوْنَ فِى الْاَعْرَابِ يَسْاَلُوْنَ عَنْ اَنْۢبَاۤىِٕكُمْ ۖوَلَوْ كَانُوْا فِيْكُمْ مَّا قٰتَلُوْٓا اِلَّا قَلِيْلًا","bc":"yaḥsabụnal-aḥzāba lam yaż-habụ, wa iy ya`til-aḥzābu yawaddụ lau annahum bādụna fil-a'rābi yas`alụna 'an ambā`ikum, walau kānụ fīkum mā qātalū illā qalīlā","at":"Mereka mengira (bahwa) golongan-golongan (yang bersekutu) itu belum pergi, dan jika golongan-golongan (yang bersekutu) itu datang kembali, niscaya mereka ingin berada di dusun-dusun bersama-sama orang Arab Badui, sambil menanyakan berita tentang kamu. Dan sekiranya mereka berada bersamamu, mereka tidak akan berperang, melainkan sebentar saja."},{"id":"21","ar":"لَقَدْ كَانَ لَكُمْ فِيْ رَسُوْلِ اللّٰهِ اُسْوَةٌ حَسَنَةٌ لِّمَنْ كَانَ يَرْجُوا اللّٰهَ وَالْيَوْمَ الْاٰخِرَ وَذَكَرَ اللّٰهَ كَثِيْرًاۗ","bc":"laqad kāna lakum fī rasụlillāhi uswatun ḥasanatul limang kāna yarjullāha wal-yaumal-ākhira wa żakarallāha kaṡīrā","at":"Sungguh, telah ada pada (diri) Rasulullah itu suri teladan yang baik bagimu (yaitu) bagi orang yang mengharap (rahmat) Allah dan (kedatangan) hari Kiamat dan yang banyak mengingat Allah."},{"id":"22","ar":"وَلَمَّا رَاَ الْمُؤْمِنُوْنَ الْاَحْزَابَۙ قَالُوْا هٰذَا مَا وَعَدَنَا اللّٰهُ وَرَسُوْلُهٗ وَصَدَقَ اللّٰهُ وَرَسُوْلُهٗ ۖوَمَا زَادَهُمْ اِلَّآ اِيْمَانًا وَّتَسْلِيْمًاۗ ","bc":"wa lammā ra`al-mu`minụnal-aḥzāba qālụ hāżā mā wa'adanallāhu wa rasụluhụ wa ṣadaqallāhu wa rasụluhụ wa mā zādahum illā īmānaw wa taslīmā","at":"Dan ketika orang-orang mukmin melihat golongan-golongan (yang bersekutu) itu, mereka berkata, “Inilah yang dijanjikan Allah dan Rasul-Nya kepada kita.” Dan benarlah Allah dan Rasul-Nya. Dan yang demikian itu menambah keimanan dan keislaman mereka."},{"id":"23","ar":"مِنَ الْمُؤْمِنِيْنَ رِجَالٌ صَدَقُوْا مَا عَاهَدُوا اللّٰهَ عَلَيْهِ ۚ فَمِنْهُمْ مَّنْ قَضٰى نَحْبَهٗۙ وَمِنْهُمْ مَّنْ يَّنْتَظِرُ ۖوَمَا بَدَّلُوْا تَبْدِيْلًاۙ ","bc":"minal-mu`minīna rijālun ṣadaqụ mā 'āhadullāha 'alaīh, fa min-hum mang qaḍā naḥbahụ wa min-hum may yantaẓiru wa mā baddalụ tabdīlā","at":"Di antara orang-orang mukmin itu ada orang-orang yang menepati apa yang telah mereka janjikan kepada Allah. Dan di antara mereka ada yang gugur, dan di antara mereka ada (pula) yang menunggu-nunggu dan mereka sedikit pun tidak mengubah (janjinya),"},{"id":"24","ar":"لِيَجْزِيَ اللّٰهُ الصّٰدِقِيْنَ بِصِدْقِهِمْ وَيُعَذِّبَ الْمُنٰفِقِيْنَ اِنْ شَاۤءَ اَوْ يَتُوْبَ عَلَيْهِمْ ۗاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًاۚ ","bc":"liyajziyallāhuṣ-ṣādiqīna biṣidqihim wa yu'ażżibal-munāfiqīna in syā`a au yatụba 'alaihim, innallāha kāna gafụrar raḥīmā","at":"agar Allah memberikan balasan kepada orang-orang yang benar itu karena kebenarannya, dan mengazab orang munafik jika Dia kehendaki, atau menerima tobat mereka. Sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"25","ar":"وَرَدَّ اللّٰهُ الَّذِيْنَ كَفَرُوْا بِغَيْظِهِمْ لَمْ يَنَالُوْا خَيْرًا ۗوَكَفَى اللّٰهُ الْمُؤْمِنِيْنَ الْقِتَالَ ۗوَكَانَ اللّٰهُ قَوِيًّا عَزِيْزًاۚ ","bc":"wa raddallāhullażīna kafarụ bigaiẓihim lam yanālụ khairā, wa kafallāhul-mu`minīnal-qitāl, wa kānallāhu qawiyyan 'azīzā","at":"Dan Allah menghalau orang-orang kafir itu yang keadaan mereka penuh kejengkelan, karena mereka (juga) tidak memperoleh keuntungan apa pun. Cukuplah Allah (yang menolong) menghindarkan orang-orang mukmin dalam peperangan. Dan Allah Mahakuat, Mahaperkasa."},{"id":"26","ar":"وَاَنْزَلَ الَّذِيْنَ ظَاهَرُوْهُمْ مِّنْ اَهْلِ الْكِتٰبِ مِنْ صَيَاصِيْهِمْ وَقَذَفَ فِيْ قُلُوْبِهِمُ الرُّعْبَ فَرِيْقًا تَقْتُلُوْنَ وَتَأْسِرُوْنَ فَرِيْقًاۚ ","bc":"wa anzalallażīna ẓāharụhum min ahlil-kitābi min ṣayāṣīhim wa qażafa fī qulụbihimur-ru'ba farīqan taqtulụna wa ta`sirụna farīqā","at":"Dan Dia menurunkan orang-orang Ahli Kitab (Bani Quraizah) yang membantu mereka (golongan-golongan yang bersekutu) dari benteng-benteng mereka, dan Dia memasukkan rasa takut ke dalam hati mereka. Sebagian mereka kamu bunuh dan sebagian yang lain kamu tawan."},{"id":"27","ar":"وَاَوْرَثَكُمْ اَرْضَهُمْ وَدِيَارَهُمْ وَاَمْوَالَهُمْ وَاَرْضًا لَّمْ تَطَـُٔوْهَا ۗوَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرًا ","bc":"wa auraṡakum arḍahum wa diyārahum wa amwālahum wa arḍal lam taṭa'ụhā, wa kānallāhu 'alā kulli syai`ing qadīrā","at":"Dan Dia mewariskan kepadamu tanah-tanah, rumah-rumah dan harta benda mereka, dan (begitu pula) tanah yang belum kamu injak. Dan Allah Mahakuasa terhadap segala sesuatu."},{"id":"28","ar":"يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّاَزْوَاجِكَ اِنْ كُنْتُنَّ تُرِدْنَ الْحَيٰوةَ الدُّنْيَا وَزِيْنَتَهَا فَتَعَالَيْنَ اُمَتِّعْكُنَّ وَاُسَرِّحْكُنَّ سَرَاحًا جَمِيْلًا ","bc":"yā ayyuhan-nabiyyu qul li`azwājika ing kuntunna turidnal-ḥayātad-dun-yā wa zīnatahā fa ta'ālaina umatti'kunna wa usarriḥkunna sarāḥan jamīlā","at":"Wahai Nabi! Katakanlah kepada istri-istrimu, “Jika kamu menginginkan kehidupan di dunia dan perhiasannya, maka kemarilah agar kuberikan kepadamu mut‘ah dan aku ceraikan kamu dengan cara yang baik.”"},{"id":"29","ar":"وَاِنْ كُنْتُنَّ تُرِدْنَ اللّٰهَ وَرَسُوْلَهٗ وَالدَّارَ الْاٰخِرَةَ فَاِنَّ اللّٰهَ اَعَدَّ لِلْمُحْسِنٰتِ مِنْكُنَّ اَجْرًا عَظِيْمًا","bc":"wa ing kuntunna turidnallāha wa rasụlahụ wad-dāral-ākhirata fa innallāha a'adda lil-muḥsināti mingkunna ajran 'aẓīmā","at":"Dan jika kamu menginginkan Allah dan Rasul-Nya dan negeri akhirat, maka sesungguhnya Allah menyediakan pahala yang besar bagi siapa yang berbuat baik di antara kamu."},{"id":"30","ar":"يٰنِسَاۤءَ النَّبِيِّ مَنْ يَّأْتِ مِنْكُنَّ بِفَاحِشَةٍ مُّبَيِّنَةٍ يُّضٰعَفْ لَهَا الْعَذَابُ ضِعْفَيْنِۗ وَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا ۔","bc":"yā nisā`an-nabiyyi may ya`ti mingkunna bifāḥisyatim mubayyinatiy yuḍā'af lahal-'ażābu ḍi'faīn, wa kāna żālika 'alallāhi yasīrā","at":"Wahai istri-istri Nabi! Barangsiapa di antara kamu yang mengerjakan perbuatan keji yang nyata, niscaya azabnya akan dilipatgandakan dua kali lipat kepadanya. Dan yang demikian itu, mudah bagi Allah."},{"id":"31","ar":"۞ وَمَنْ يَّقْنُتْ مِنْكُنَّ لِلّٰهِ وَرَسُوْلِهٖ وَتَعْمَلْ صَالِحًا نُّؤْتِهَآ اَجْرَهَا مَرَّتَيْنِۙ وَاَعْتَدْنَا لَهَا رِزْقًا كَرِيْمًا ","bc":"wa may yaqnut mingkunna lillāhi wa rasụlihī wa ta'mal ṣāliḥan nu`tihā ajrahā marrataini wa a'tadnā lahā rizqang karīmā","at":"Dan barangsiapa di antara kamu (istri-istri Nabi) tetap taat kepada Allah dan Rasul-Nya dan mengerjakan kebajikan, niscaya Kami berikan pahala kepadanya dua kali lipat dan Kami sediakan rezeki yang mulia baginya."},{"id":"32","ar":"يٰنِسَاۤءَ النَّبِيِّ لَسْتُنَّ كَاَحَدٍ مِّنَ النِّسَاۤءِ اِنِ اتَّقَيْتُنَّ فَلَا تَخْضَعْنَ بِالْقَوْلِ فَيَطْمَعَ الَّذِيْ فِيْ قَلْبِهٖ مَرَضٌ وَّقُلْنَ قَوْلًا مَّعْرُوْفًاۚ ","bc":"yā nisā`an-nabiyyi lastunna ka`aḥadim minan-nisā`i inittaqaitunna fa lā takhḍa'na bil-qauli fa yaṭma'allażī fī qalbihī maraḍuw wa qulna qaulam ma'rụfā","at":"Wahai istri-istri Nabi! Kamu tidak seperti perempuan-perempuan yang lain, jika kamu bertakwa. Maka janganlah kamu tunduk (melemah lembutkan suara) dalam berbicara sehingga bangkit nafsu orang yang ada penyakit dalam hatinya, dan ucapkanlah perkataan yang baik."},{"id":"33","ar":"وَقَرْنَ فِيْ بُيُوْتِكُنَّ وَلَا تَبَرَّجْنَ تَبَرُّجَ الْجَاهِلِيَّةِ الْاُوْلٰى وَاَقِمْنَ الصَّلٰوةَ وَاٰتِيْنَ الزَّكٰوةَ وَاَطِعْنَ اللّٰهَ وَرَسُوْلَهٗ ۗاِنَّمَا يُرِيْدُ اللّٰهُ لِيُذْهِبَ عَنْكُمُ الرِّجْسَ اَهْلَ الْبَيْتِ وَيُطَهِّرَكُمْ تَطْهِيْرًاۚ ","bc":"wa qarna fī buyụtikunna wa lā tabarrajna tabarrujal-jāhiliyyatil-ụlā wa aqimnaṣ-ṣalāta wa ātīnaz-zakāta wa aṭi'nallāha wa rasụlah, innamā yurīdullāhu liyuż-hiba 'angkumur-rijsa ahlal-baiti wa yuṭahhirakum taṭ-hīrā","at":"Dan hendaklah kamu tetap di rumahmu dan janganlah kamu berhias dan (bertingkah laku) seperti orang-orang jahiliah dahulu, dan laksanakanlah salat, tunaikanlah zakat dan taatilah Allah dan Rasul-Nya. Sesungguhnya Allah bermaksud hendak menghilangkan dosa dari kamu, wahai ahlulbait dan membersihkan kamu sebersih-bersihnya."},{"id":"34","ar":"وَاذْكُرْنَ مَا يُتْلٰى فِيْ بُيُوْتِكُنَّ مِنْ اٰيٰتِ اللّٰهِ وَالْحِكْمَةِۗ اِنَّ اللّٰهَ كَانَ لَطِيْفًا خَبِيْرًا ","bc":"ważkurna mā yutlā fī buyụtikunna min āyātillāhi wal-ḥikmah, innallāha kāna laṭīfan khabīrā","at":"Dan ingatlah apa yang dibacakan di rumahmu dari ayat-ayat Allah dan hikmah (sunnah Nabimu). Sungguh, Allah Mahalembut, Maha Mengetahui."},{"id":"35","ar":"اِنَّ الْمُسْلِمِيْنَ وَالْمُسْلِمٰتِ وَالْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ وَالْقٰنِتِيْنَ وَالْقٰنِتٰتِ وَالصّٰدِقِيْنَ وَالصّٰدِقٰتِ وَالصّٰبِرِيْنَ وَالصّٰبِرٰتِ وَالْخٰشِعِيْنَ وَالْخٰشِعٰتِ وَالْمُتَصَدِّقِيْنَ وَالْمُتَصَدِّقٰتِ وَالصَّاۤىِٕمِيْنَ وَالصّٰۤىِٕمٰتِ وَالْحٰفِظِيْنَ فُرُوْجَهُمْ وَالْحٰفِظٰتِ وَالذَّاكِرِيْنَ اللّٰهَ كَثِيْرًا وَّالذَّاكِرٰتِ اَعَدَّ اللّٰهُ لَهُمْ مَّغْفِرَةً وَّاَجْرًا عَظِيْمًا ","bc":"innal-muslimīna wal-muslimāti wal-mu`minīna wal-mu`mināti wal-qānitīna wal-qānitāti waṣ-ṣādiqīna waṣ-ṣādiqāti waṣ-ṣābirīna waṣ-ṣābirāti wal-khāsyi'īna wal-khāsyi'āti wal-mutaṣaddiqīna wal-mutaṣaddiqāti waṣ-ṣā`imīna waṣ-ṣā`imāti wal-ḥāfiẓīna furụjahum wal-ḥāfiẓāti waż-żākirīnallāha kaṡīraw waż-żākirāti a'addallāhu lahum magfirataw wa ajran 'aẓīmā","at":"Sungguh, laki-laki dan perempuan muslim, laki-laki dan perempuan mukmin, laki-laki dan perempuan yang tetap dalam ketaatannya, laki-laki dan perempuan yang benar, laki-laki dan perempuan yang sabar, laki-laki dan perempuan yang khusyuk, laki-laki dan perempuan yang bersedekah, laki-laki dan perempuan yang berpuasa, laki-laki dan perempuan yang memelihara kehormatannya, laki-laki dan perempuan yang banyak menyebut (nama) Allah, Allah telah menyediakan untuk mereka ampunan dan pahala yang besar."},{"id":"36","ar":"وَمَا كَانَ لِمُؤْمِنٍ وَّلَا مُؤْمِنَةٍ اِذَا قَضَى اللّٰهُ وَرَسُوْلُهٗٓ اَمْرًا اَنْ يَّكُوْنَ لَهُمُ الْخِيَرَةُ مِنْ اَمْرِهِمْ ۗوَمَنْ يَّعْصِ اللّٰهَ وَرَسُوْلَهٗ فَقَدْ ضَلَّ ضَلٰلًا مُّبِيْنًاۗ ","bc":"wa mā kāna limu`miniw wa lā mu`minatin iżā qaḍallāhu wa rasụluhū amran ay yakụna lahumul-khiyaratu min amrihim, wa may ya'ṣillāha wa rasụlahụ fa qad ḍalla ḍalālam mubīnā","at":"Dan tidaklah pantas bagi laki-laki yang mukmin dan perempuan yang mukmin, apabila Allah dan Rasul-Nya telah menetapkan suatu ketetapan, akan ada pilihan (yang lain) bagi mereka tentang urusan mereka. Dan barangsiapa mendurhakai Allah dan Rasul-Nya, maka sungguh, dia telah tersesat, dengan kesesatan yang nyata."},{"id":"37","ar":"وَاِذْ تَقُوْلُ لِلَّذِيْٓ اَنْعَمَ اللّٰهُ عَلَيْهِ وَاَنْعَمْتَ عَلَيْهِ اَمْسِكْ عَلَيْكَ زَوْجَكَ وَاتَّقِ اللّٰهَ وَتُخْفِيْ فِيْ نَفْسِكَ مَا اللّٰهُ مُبْدِيْهِ وَتَخْشَى النَّاسَۚ وَاللّٰهُ اَحَقُّ اَنْ تَخْشٰىهُ ۗ فَلَمَّا قَضٰى زَيْدٌ مِّنْهَا وَطَرًاۗ زَوَّجْنٰكَهَا لِكَيْ لَا يَكُوْنَ عَلَى الْمُؤْمِنِيْنَ حَرَجٌ فِيْٓ اَزْوَاجِ اَدْعِيَاۤىِٕهِمْ اِذَا قَضَوْا مِنْهُنَّ وَطَرًاۗ وَكَانَ اَمْرُ اللّٰهِ مَفْعُوْلًا ","bc":"wa iż taqụlu lillażī an'amallāhu 'alaihi wa an'amta 'alaihi amsik 'alaika zaujaka wattaqillāha wa tukhfī fī nafsika mallāhu mubdīhi wa takhsyan-nās, wallāhu aḥaqqu an takhsyāh, fa lammā qaḍā zaidum min-hā waṭarā, zawwajnākahā likai lā yakụna 'alal-mu`minīna ḥarajun fī azwāji ad'iyā`ihim iżā qaḍau min-hunna waṭarā, wa kāna amrullāhi maf'ụlā","at":"Dan (ingatlah), ketika engkau (Muhammad) berkata kepada orang yang telah diberi nikmat oleh Allah dan engkau (juga) telah memberi nikmat kepadanya, “Pertahankanlah terus istrimu dan bertakwalah kepada Allah,” sedang engkau menyembunyikan di dalam hatimu apa yang akan dinyatakan oleh Allah, dan engkau takut kepada manusia, padahal Allah lebih berhak engkau takuti. Maka ketika Zaid telah mengakhiri keperluan terhadap istrinya (menceraikannya), Kami nikahkan engkau dengan dia (Zainab) agar tidak ada keberatan bagi orang mukmin untuk (menikahi) istri-istri anak-anak angkat mereka, apabila anak-anak angkat itu telah menyelesaikan keperluannya terhadap istrinya. Dan ketetapan Allah itu pasti terjadi."},{"id":"38","ar":"مَا كَانَ عَلَى النَّبِيِّ مِنْ حَرَجٍ فِيْمَا فَرَضَ اللّٰهُ لَهٗ ۗسُنَّةَ اللّٰهِ فِى الَّذِيْنَ خَلَوْا مِنْ قَبْلُ ۗوَكَانَ اَمْرُ اللّٰهِ قَدَرًا مَّقْدُوْرًاۙ ","bc":"mā kāna 'alan-nabiyyi min ḥarajin fīmā faraḍallāhu lah, sunnatallāhi fillażīna khalau ming qabl, wa kāna amrullāhi qadaram maqdụrā","at":"Tidak ada keberatan apa pun pada Nabi tentang apa yang telah ditetapkan Allah baginya. (Allah telah menetapkan yang demikian) sebagai sunnah Allah pada nabi-nabi yang telah terdahulu. Dan ketetapan Allah itu suatu ketetapan yang pasti berlaku,"},{"id":"39","ar":" ۨالَّذِيْنَ يُبَلِّغُوْنَ رِسٰلٰتِ اللّٰهِ وَيَخْشَوْنَهٗ وَلَا يَخْشَوْنَ اَحَدًا اِلَّا اللّٰهَ ۗوَكَفٰى بِاللّٰهِ حَسِيْبًا ","bc":"allażīna yuballigụna risālātillāhi wa yakhsyaunahụ wa lā yakhsyauna aḥadan illallāh, wa kafā billāhi ḥasībā","at":"(yaitu) orang-orang yang menyampaikan risalah-risalah Allah, mereka takut kepada-Nya dan tidak merasa takut kepada siapa pun selain kepada Allah. Dan cukuplah Allah sebagai pembuat perhitungan."},{"id":"40","ar":"مَا كَانَ مُحَمَّدٌ اَبَآ اَحَدٍ مِّنْ رِّجَالِكُمْ وَلٰكِنْ رَّسُوْلَ اللّٰهِ وَخَاتَمَ النَّبِيّٖنَۗ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمًا","bc":"mā kāna muḥammadun aba aḥadim mir rijālikum wa lākir rasụlallāhi wa khātaman-nabiyyīn, wa kānallāhu bikulli syai`in 'alīmā","at":"Muhammad itu bukanlah bapak dari seseorang di antara kamu, tetapi dia adalah utusan Allah dan penutup para nabi. Dan Allah Maha Mengetahui segala sesuatu."},{"id":"41","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوا اللّٰهَ ذِكْرًا كَثِيْرًاۙ ","bc":"yā ayyuhallażīna āmanużkurullāha żikrang kaṡīrā","at":"Wahai orang-orang yang beriman! Ingatlah kepada Allah, dengan mengingat (nama-Nya) sebanyak-banyaknya,"},{"id":"42","ar":"وَّسَبِّحُوْهُ بُكْرَةً وَّاَصِيْلًا ","bc":"wa sabbiḥụhu bukrataw wa aṣīlā","at":"dan bertasbihlah kepada-Nya pada waktu pagi dan petang."},{"id":"43","ar":"هُوَ الَّذِيْ يُصَلِّيْ عَلَيْكُمْ وَمَلٰۤىِٕكَتُهٗ لِيُخْرِجَكُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَكَانَ بِالْمُؤْمِنِيْنَ رَحِيْمًا ","bc":"huwallażī yuṣallī 'alaikum wa malā`ikatuhụ liyukhrijakum minaẓ-ẓulumāti ilan-nụr, wa kāna bil-mu`minīna raḥīmā","at":"Dialah yang memberi rahmat kepadamu dan para malaikat-Nya (memohonkan ampunan untukmu), agar Dia mengeluarkan kamu dari kegelapan kepada cahaya (yang terang). Dan Dia Maha Penyayang kepada orang-orang yang beriman."},{"id":"44","ar":"تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهٗ سَلٰمٌ ۚوَاَعَدَّ لَهُمْ اَجْرًا كَرِيْمًا ","bc":"taḥiyyatuhum yauma yalqaunahụ salām, wa a'adda lahum ajrang karīmā","at":"Penghormatan mereka (orang-orang mukmin itu) ketika mereka menemui-Nya ialah, “Salam,” dan Dia menyediakan pahala yang mulia bagi mereka."},{"id":"45","ar":"يٰٓاَيُّهَا النَّبِيُّ اِنَّآ اَرْسَلْنٰكَ شَاهِدًا وَّمُبَشِّرًا وَّنَذِيْرًاۙ ","bc":"yā ayyuhan-nabiyyu innā arsalnāka syāhidaw wa mubasysyiraw wa nażīrā","at":"Wahai Nabi! Sesungguhnya Kami mengutusmu untuk menjadi saksi, pembawa kabar gembira dan pemberi peringatan,"},{"id":"46","ar":"وَّدَاعِيًا اِلَى اللّٰهِ بِاِذْنِهٖ وَسِرَاجًا مُّنِيْرًا ","bc":"wa dā'iyan ilallāhi bi`iżnihī wa sirājam munīrā","at":"dan untuk menjadi penyeru kepada (agama) Allah dengan izin-Nya dan sebagai cahaya yang menerangi."},{"id":"47","ar":"وَبَشِّرِ الْمُؤْمِنِيْنَ بِاَنَّ لَهُمْ مِّنَ اللّٰهِ فَضْلًا كَبِيْرًا ","bc":"wa basysyiril-mu`minīna bi`anna lahum minallāhi faḍlang kabīrā","at":"Dan sampaikanlah kabar gembira kepada orang-orang mukmin bahwa sesungguhnya bagi mereka karunia yang besar dari Allah."},{"id":"48","ar":"وَلَا تُطِعِ الْكٰفِرِيْنَ وَالْمُنٰفِقِيْنَ وَدَعْ اَذٰىهُمْ وَتَوَكَّلْ عَلَى اللّٰهِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا ","bc":"wa lā tuṭi'il-kāfirīna wal-munāfiqīna wa da' ażāhum wa tawakkal 'alallāh, wa kafā billāhi wakīlā","at":"Dan janganlah engkau (Muhammad) menuruti orang-orang kafir dan orang-orang munafik itu, janganlah engkau hiraukan gangguan mereka dan bertawakallah kepada Allah. Dan cukuplah Allah sebagai pelindung."},{"id":"49","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا نَكَحْتُمُ الْمُؤْمِنٰتِ ثُمَّ طَلَّقْتُمُوْهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوْهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍ تَعْتَدُّوْنَهَاۚ فَمَتِّعُوْهُنَّ وَسَرِّحُوْهُنَّ سَرَاحًا جَمِيْلًا ","bc":"yā ayyuhallażīna āmanū iżā nakaḥtumul-mu`mināti ṡumma ṭallaqtumụhunna ming qabli an tamassụhunna fa mā lakum 'alaihinna min 'iddatin ta'taddụnahā, fa matti'ụhunna wa sarriḥụhunna sarāḥan jamīlā","at":"Wahai orang-orang yang beriman! Apabila kamu menikahi perempuan-perempuan mukmin, kemudian kamu ceraikan mereka sebelum kamu mencampurinya maka tidak ada masa idah atas mereka yang perlu kamu perhitungkan. Namun berilah mereka mut’ah dan lepaskanlah mereka itu dengan cara yang sebaik-baiknya."},{"id":"50","ar":"يٰٓاَيُّهَا النَّبِيُّ اِنَّآ اَحْلَلْنَا لَكَ اَزْوَاجَكَ الّٰتِيْٓ اٰتَيْتَ اُجُوْرَهُنَّ وَمَا مَلَكَتْ يَمِيْنُكَ مِمَّآ اَفَاۤءَ اللّٰهُ عَلَيْكَ وَبَنٰتِ عَمِّكَ وَبَنٰتِ عَمّٰتِكَ وَبَنٰتِ خَالِكَ وَبَنٰتِ خٰلٰتِكَ الّٰتِيْ هَاجَرْنَ مَعَكَۗ وَامْرَاَةً مُّؤْمِنَةً اِنْ وَّهَبَتْ نَفْسَهَا لِلنَّبِيِّ اِنْ اَرَادَ النَّبِيُّ اَنْ يَّسْتَنْكِحَهَا خَالِصَةً لَّكَ مِنْ دُوْنِ الْمُؤْمِنِيْنَۗ قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ فِيْٓ اَزْوَاجِهِمْ وَمَا مَلَكَتْ اَيْمَانُهُمْ لِكَيْلَا يَكُوْنَ عَلَيْكَ حَرَجٌۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ","bc":"yā ayyuhan-nabiyyu innā aḥlalnā laka azwājakallātī ātaita ujụrahunna wa mā malakat yamīnuka mimmā afā`allāhu 'alaika wa banāti 'ammika wa banāti 'ammātika wa banāti khālika wa banāti khālātikallātī hājarna ma'ak, wamra`atam mu`minatan iw wahabat nafsahā lin-nabiyyi in arādan-nabiyyu ay yastangkiḥahā khāliṣatal laka min dụnil-mu`minīn, qad 'alimnā mā faraḍnā 'alaihim fī azwājihim wa mā malakat aimānuhum likai lā yakụna 'alaika ḥaraj, wa kānallāhu gafụrar raḥīmā","at":"Wahai Nabi! Sesungguhnya Kami telah menghalalkan bagimu istri-istrimu yang telah engkau berikan maskawinnya dan hamba sahaya yang engkau miliki, termasuk apa yang engkau peroleh dalam peperangan yang dikaruniakan Allah untukmu, dan (demikian pula) anak-anak perempuan dari saudara laki-laki bapakmu, anak-anak perempuan dari saudara perempuan bapakmu, anak-anak perempuan dari saudara laki-laki ibumu dan anak-anak perempuan dari saudara perempuan ibumu yang turut hijrah bersamamu, dan perempuan mukmin yang menyerahkan dirinya kepada Nabi kalau Nabi ingin menikahinya, sebagai kekhususan bagimu, bukan untuk semua orang mukmin. Kami telah mengetahui apa yang Kami wajibkan kepada mereka tentang istri-istri mereka dan hamba sahaya yang mereka miliki agar tidak menjadi kesempitan bagimu. Dan Allah Maha Pengampun, Maha Penyayang."},{"id":"51","ar":"۞ تُرْجِيْ مَنْ تَشَاۤءُ مِنْهُنَّ وَتُـْٔوِيْٓ اِلَيْكَ مَنْ تَشَاۤءُۗ وَمَنِ ابْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلَا جُنَاحَ عَلَيْكَۗ ذٰلِكَ اَدْنٰٓى اَنْ تَقَرَّ اَعْيُنُهُنَّ وَلَا يَحْزَنَّ وَيَرْضَيْنَ بِمَآ اٰتَيْتَهُنَّ كُلُّهُنَّۗ وَاللّٰهُ يَعْلَمُ مَا فِيْ قُلُوْبِكُمْ ۗوَكَانَ اللّٰهُ عَلِيْمًا حَلِيْمًا ","bc":"turjī man tasyā`u min-hunna wa tu`wī ilaika man tasyā`, wa manibtagaita mim man 'azalta fa lā junāḥa 'alaīk, żālika adnā an taqarra a'yunuhunna wa lā yaḥzanna wa yarḍaina bimā ātaitahunna kulluhunn, wallāhu ya'lamu mā fī qulụbikum, wa kānallāhu 'alīman ḥalīmā","at":"Engkau boleh menangguhkan (menggauli) siapa yang engkau kehendaki di antara mereka (para istrimu) dan (boleh pula) menggauli siapa (di antara mereka) yang engkau kehendaki. Dan siapa yang engkau ingini untuk menggaulinya kembali dari istri-istrimu yang telah engkau sisihkan, maka tidak ada dosa bagimu. Yang demikian itu lebih dekat untuk ketenangan hati mereka, dan mereka tidak merasa sedih, dan mereka rela dengan apa yang telah engkau berikan kepada mereka semuanya. Dan Allah mengetahui apa yang (tersimpan) dalam hatimu. Dan Allah Maha Mengetahui, Maha Penyantun."},{"id":"52","ar":"لَا يَحِلُّ لَكَ النِّسَاۤءُ مِنْۢ بَعْدُ وَلَآ اَنْ تَبَدَّلَ بِهِنَّ مِنْ اَزْوَاجٍ وَّلَوْ اَعْجَبَكَ حُسْنُهُنَّ اِلَّا مَا مَلَكَتْ يَمِيْنُكَۗ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ رَّقِيْبًا ","bc":"lā yaḥillu lakan-nisā`u mim ba'du wa lā an tabaddala bihinna min azwājiw walau a'jabaka ḥusnuhunna illā mā malakat yamīnuk, wa kānallāhu 'alā kulli syai`ir raqībā","at":"Tidak halal bagimu (Muhammad) menikahi perempuan-perempuan (lain) setelah itu, dan tidak boleh (pula) mengganti mereka dengan istri-istri (yang lain), meskipun kecantikannya menarik hatimu kecuali perempuan-perempuan (hamba sahaya) yang engkau miliki. Dan Allah Maha Mengawasi segala sesuatu."},{"id":"53","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَدْخُلُوْا بُيُوْتَ النَّبِيِّ اِلَّآ اَنْ يُّؤْذَنَ لَكُمْ اِلٰى طَعَامٍ غَيْرَ نٰظِرِيْنَ اِنٰىهُ وَلٰكِنْ اِذَا دُعِيْتُمْ فَادْخُلُوْا فَاِذَا طَعِمْتُمْ فَانْتَشِرُوْا وَلَا مُسْتَأْنِسِيْنَ لِحَدِيْثٍۗ اِنَّ ذٰلِكُمْ كَانَ يُؤْذِى النَّبِيَّ فَيَسْتَحْيٖ مِنْكُمْ ۖوَاللّٰهُ لَا يَسْتَحْيٖ مِنَ الْحَقِّۗ وَاِذَا سَاَلْتُمُوْهُنَّ مَتَاعًا فَاسْـَٔلُوْهُنَّ مِنْ وَّرَاۤءِ حِجَابٍۗ ذٰلِكُمْ اَطْهَرُ لِقُلُوْبِكُمْ وَقُلُوْبِهِنَّۗ وَمَا كَانَ لَكُمْ اَنْ تُؤْذُوْا رَسُوْلَ اللّٰهِ وَلَآ اَنْ تَنْكِحُوْٓا اَزْوَاجَهٗ مِنْۢ بَعْدِهٖٓ اَبَدًاۗ اِنَّ ذٰلِكُمْ كَانَ عِنْدَ اللّٰهِ عَظِيْمًا ","bc":"yā ayyuhallażīna āmanụ lā tadkhulụ buyụtan-nabiyyi illā ay yu`żana lakum ilā ṭa'āmin gaira nāẓirīna ināhu wa lākin iżā du'ītum fadkhulụ fa iżā ṭa'imtum fantasyirụ wa lā musta`nisīna liḥadīṡ, inna żālikum kāna yu`żin-nabiyya fa yastaḥyī mingkum wallāhu lā yastaḥyī minal-ḥaqq, wa iżā sa`altumụhunna matā'an fas`alụhunna miw warā`i ḥijāb, żālikum aṭ-haru liqulụbikum wa qulụbihinn, wa mā kāna lakum an tu`żụ rasụlallāhi wa lā an tangkiḥū azwājahụ mim ba'dihī abadā, inna żālikum kāna 'indallāhi 'aẓīmā","at":"Wahai orang-orang yang beriman! Janganlah kamu memasuki rumah-rumah Nabi kecuali jika kamu diizinkan untuk makan tanpa menunggu waktu masak (makanannya), tetapi jika kamu dipanggil maka masuklah dan apabila kamu selesai makan, keluarlah kamu tanpa memperpanjang percakapan. Sesungguhnya yang demikian itu adalah mengganggu Nabi sehingga dia (Nabi) malu kepadamu (untuk menyuruhmu keluar), dan Allah tidak malu (menerangkan) yang benar. Apabila kamu meminta sesuatu (keperluan) kepada mereka (istri-istri Nabi), maka mintalah dari belakang tabir. (Cara) yang demikian itu lebih suci bagi hatimu dan hati mereka. Dan tidak boleh kamu menyakiti (hati) Rasulullah dan tidak boleh (pula) menikahi istri-istrinya selama-lamanya setelah (Nabi wafat). Sungguh, yang demikian itu sangat besar (dosanya) di sisi Allah."},{"id":"54","ar":"اِنْ تُبْدُوْا شَيْـًٔا اَوْ تُخْفُوْهُ فَاِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَلِيْمًا ","bc":"in tubdụ syai`an au tukhfụhu fa innallāha kāna bikulli syai`in 'alīmā","at":"Jika kamu menyatakan sesuatu atau menyembunyikannya, maka sesungguhnya Allah Maha Mengetahui segala sesuatu."},{"id":"55","ar":"لَا جُنَاحَ عَلَيْهِنَّ فِيْٓ اٰبَاۤىِٕهِنَّ وَلَآ اَبْنَاۤىِٕهِنَّ وَلَآ اِخْوَانِهِنَّ وَلَآ اَبْنَاۤءِ اِخْوَانِهِنَّ وَلَآ اَبْنَاۤءِ اَخَوٰتِهِنَّ وَلَا نِسَاۤىِٕهِنَّ وَلَا مَا مَلَكَتْ اَيْمَانُهُنَّۚ وَاتَّقِيْنَ اللّٰهَ ۗاِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ شَهِيْدًا ","bc":"lā junāḥa 'alaihinna fī ābā`ihinna wa lā abnā`ihinna wa lā ikhwānihinna wa lā abnā`i ikhwānihinna wa lā abnā`i akhawātihinna wa lā nisā`ihinna wa lā mā malakat aimānuhunn, wattaqīnallāh, innallāha kāna 'alā kulli syai`in syahīdā","at":"Tidak ada dosa atas istri-istri Nabi (untuk berjumpa tanpa tabir) dengan bapak-bapak mereka, anak laki-laki mereka, saudara laki-laki mereka, anak laki-laki dari saudara laki-laki mereka, anak laki-laki dari saudara perempuan mereka, perempuan-perempuan mereka (yang beriman) dan hamba sahaya yang mereka miliki, dan bertakwalah kamu (istri-istri Nabi) kepada Allah. Sungguh, Allah Maha Menyaksikan segala sesuatu."},{"id":"56","ar":"اِنَّ اللّٰهَ وَمَلٰۤىِٕكَتَهٗ يُصَلُّوْنَ عَلَى النَّبِيِّۗ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا صَلُّوْا عَلَيْهِ وَسَلِّمُوْا تَسْلِيْمًا ","bc":"innallāha wa malā`ikatahụ yuṣallụna 'alan-nabiyy, yā ayyuhallażīna āmanụ ṣallụ 'alaihi wa sallimụ taslīmā","at":"Sesungguhnya Allah dan para malaikat-Nya bersalawat untuk Nabi. Wahai orang-orang yang beriman! Bersalawatlah kamu untuk Nabi dan ucapkanlah salam dengan penuh penghormatan kepadanya."},{"id":"57","ar":"اِنَّ الَّذِيْنَ يُؤْذُوْنَ اللّٰهَ وَرَسُوْلَهٗ لَعَنَهُمُ اللّٰهُ فِى الدُّنْيَا وَالْاٰخِرَةِ وَاَعَدَّ لَهُمْ عَذَابًا مُّهِيْنًا ","bc":"innallażīna yu`żụnallāha wa rasụlahụ la'anahumullāhu fid-dun-yā wal-ākhirati wa a'adda lahum 'ażābam muhīnā","at":"Sesungguhnya (terhadap) orang-orang yang menyakiti Allah dan Rasul-Nya, Allah akan melaknatnya di dunia dan di akhirat, dan menyediakan azab yang menghinakan bagi mereka."},{"id":"58","ar":"وَالَّذِيْنَ يُؤْذُوْنَ الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ بِغَيْرِ مَا اكْتَسَبُوْا فَقَدِ احْتَمَلُوْا بُهْتَانًا وَّاِثْمًا مُّبِيْنًا ","bc":"wallażīna yu`żụnal-mu`minīna wal-mu`mināti bigairi maktasabụ faqadiḥtamalụ buhtānaw wa iṡmam mubīnā","at":"Dan orang-orang yang menyakiti orang-orang mukmin laki-laki dan perempuan, tanpa ada kesalahan yang mereka perbuat, maka sungguh, mereka telah memikul kebohongan dan dosa yang nyata."},{"id":"59","ar":"يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّاَزْوَاجِكَ وَبَنٰتِكَ وَنِسَاۤءِ الْمُؤْمِنِيْنَ يُدْنِيْنَ عَلَيْهِنَّ مِنْ جَلَابِيْبِهِنَّۗ ذٰلِكَ اَدْنٰىٓ اَنْ يُّعْرَفْنَ فَلَا يُؤْذَيْنَۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ","bc":"yā ayyuhan-nabiyyu qul li`azwājika wa banātika wa nisā`il-mu`minīna yudnīna 'alaihinna min jalābībihinn, żālika adnā ay yu'rafna fa lā yu`żaīn, wa kānallāhu gafụrar raḥīmā","at":"Wahai Nabi! Katakanlah kepada istri-istrimu, anak-anak perempuanmu dan istri-istri orang mukmin, “Hendaklah mereka menutupkan jilbabnya ke seluruh tubuh mereka.” Yang demikian itu agar mereka lebih mudah untuk dikenali, sehingga mereka tidak diganggu. Dan Allah Maha Pengampun, Maha Penyayang."},{"id":"60","ar":"۞ لَىِٕنْ لَّمْ يَنْتَهِ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ وَّالْمُرْجِفُوْنَ فِى الْمَدِيْنَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لَا يُجَاوِرُوْنَكَ فِيْهَآ اِلَّا قَلِيْلًا ","bc":"la`il lam yantahil-munāfiqụna wallażīna fī qulụbihim maraḍuw wal-murjifụna fil-madīnati lanugriyannaka bihim ṡumma lā yujāwirụnaka fīhā illā qalīlā","at":"Sungguh, jika orang-orang munafik, orang-orang yang berpenyakit dalam hatinya dan orang-orang yang menyebarkan kabar bohong di Madinah tidak berhenti (dari menyakitimu), niscaya Kami perintahkan engkau (untuk memerangi) mereka, kemudian mereka tidak lagi menjadi tetanggamu (di Madinah) kecuali sebentar,"},{"id":"61","ar":"مَلْعُوْنِيْنَۖ اَيْنَمَا ثُقِفُوْٓا اُخِذُوْا وَقُتِّلُوْا تَقْتِيْلًا ","bc":"mal'ụnīna ainamā ṡuqifū ukhiżụ wa quttilụ taqtīlā","at":"dalam keadaan terlaknat. Di mana saja mereka dijumpai, mereka akan ditangkap dan dibunuh tanpa ampun."},{"id":"62","ar":"سُنَّةَ اللّٰهِ فِى الَّذِيْنَ خَلَوْا مِنْ قَبْلُ ۚوَلَنْ تَجِدَ لِسُنَّةِ اللّٰهِ تَبْدِيْلًا ","bc":"sunnatallāhi fillażīna khalau ming qabl, wa lan tajida lisunnatillāhi tabdīlā","at":"Sebagai sunnah Allah yang (berlaku juga) bagi orang-orang yang telah terdahulu sebelum(mu), dan engkau tidak akan mendapati perubahan pada sunnah Allah."},{"id":"63","ar":"يَسْـَٔلُكَ النَّاسُ عَنِ السَّاعَةِۗ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ اللّٰهِ ۗوَمَا يُدْرِيْكَ لَعَلَّ السَّاعَةَ تَكُوْنُ قَرِيْبًا ","bc":"yas`alukan-nāsu 'anis-sā'ah, qul innamā 'ilmuhā 'indallāh, wa mā yudrīka la'allas-sā'ata takụnu qarībā","at":"Manusia bertanya kepadamu (Muhammad) tentang hari Kiamat. Katakanlah, “Ilmu tentang hari Kiamat itu hanya di sisi Allah.” Dan tahukah engkau, boleh jadi hari Kiamat itu sudah dekat waktunya."},{"id":"64","ar":"اِنَّ اللّٰهَ لَعَنَ الْكٰفِرِيْنَ وَاَعَدَّ لَهُمْ سَعِيْرًاۙ ","bc":"innallāha la'anal-kāfirīna wa a'adda lahum sa'īrā","at":"Sungguh, Allah melaknat orang-orang kafir dan menyediakan bagi mereka api yang menyala-nyala (neraka),"},{"id":"65","ar":"خٰلِدِيْنَ فِيْهَآ اَبَدًاۚ لَا يَجِدُوْنَ وَلِيًّا وَّلَا نَصِيْرًا ۚ","bc":"khālidīna fīhā abadā, lā yajidụna waliyyaw wa lā naṣīrā","at":"mereka kekal di dalamnya selama-lamanya; mereka tidak akan mendapatkan pelindung dan penolong."},{"id":"66","ar":"يَوْمَ تُقَلَّبُ وُجُوْهُهُمْ فِى النَّارِ يَقُوْلُوْنَ يٰلَيْتَنَآ اَطَعْنَا اللّٰهَ وَاَطَعْنَا الرَّسُوْلَا۠ ","bc":"yauma tuqallabu wujụhuhum fin-nāri yaqụlụna yā laitanā aṭa'nallāha wa aṭa'nar-rasụlā","at":"Pada hari (ketika) wajah mereka dibolak-balikkan dalam neraka, mereka berkata, “Wahai, kiranya dahulu kami taat kepada Allah dan taat (pula) kepada Rasul.”"},{"id":"67","ar":"وَقَالُوْا رَبَّنَآ اِنَّآ اَطَعْنَا سَادَتَنَا وَكُبَرَاۤءَنَا فَاَضَلُّوْنَا السَّبِيْلَا۠ ","bc":"wa qālụ rabbanā innā aṭa'nā sādatanā wa kubarā`anā fa aḍallụnas-sabīlā","at":"Dan mereka berkata, “Ya Tuhan kami, sesungguhnya kami telah menaati para pemimpin dan para pembesar kami, lalu mereka menyesatkan kami dari jalan (yang benar)."},{"id":"68","ar":"رَبَّنَآ اٰتِهِمْ ضِعْفَيْنِ مِنَ الْعَذَابِ وَالْعَنْهُمْ لَعْنًا كَبِيْرًا ","bc":"rabbanā ātihim ḍi'faini minal-'ażābi wal'an-hum la'nang kabīrā","at":"Ya Tuhan kami, timpakanlah kepada mereka azab dua kali lipat dan laknatlah mereka dengan laknat yang besar.”"},{"id":"69","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَكُوْنُوْا كَالَّذِيْنَ اٰذَوْا مُوْسٰى فَبَرَّاَهُ اللّٰهُ مِمَّا قَالُوْا ۗوَكَانَ عِنْدَ اللّٰهِ وَجِيْهًا ۗ ","bc":"yā ayyuhallażīna āmanụ lā takụnụ kallażīna āżau mụsā fa barra`ahullāhu mimmā qālụ, wa kāna 'indallāhi wajīhā","at":"Wahai orang-orang yang beriman! Janganlah kamu seperti orang-orang yang menyakiti Musa, maka Allah membersihkannya dari tuduhan-tuduhan yang mereka lontarkan. Dan dia seorang yang mempunyai kedudukan terhormat di sisi Allah."},{"id":"70","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَقُوْلُوْا قَوْلًا سَدِيْدًاۙ ","bc":"yā ayyuhallażīna āmanuttaqullāha wa qụlụ qaulan sadīdā","at":"Wahai orang-orang yang beriman! Bertakwalah kamu kepada Allah dan ucapkanlah perkataan yang benar,"},{"id":"71","ar":"يُّصْلِحْ لَكُمْ اَعْمَالَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوْبَكُمْۗ وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ فَقَدْ فَازَ فَوْزًا عَظِيْمًا ","bc":"yuṣliḥ lakum a'mālakum wa yagfir lakum żunụbakum, wa may yuṭi'illāha wa rasụlahụ fa qad fāza fauzan 'aẓīmā","at":"niscaya Allah akan memperbaiki amal-amalmu dan mengampuni dosa-dosamu. Dan barangsiapa menaati Allah dan Rasul-Nya, maka sungguh, dia menang dengan kemenangan yang agung."},{"id":"72","ar":"اِنَّا عَرَضْنَا الْاَمَانَةَ عَلَى السَّمٰوٰتِ وَالْاَرْضِ وَالْجِبَالِ فَاَبَيْنَ اَنْ يَّحْمِلْنَهَا وَاَشْفَقْنَ مِنْهَا وَحَمَلَهَا الْاِنْسَانُۗ اِنَّهٗ كَانَ ظَلُوْمًا جَهُوْلًاۙ ","bc":"innā 'araḍnal-amānata 'alas-samāwāti wal-arḍi wal-jibāli fa abaina ay yaḥmilnahā wa asyfaqna min-hā wa ḥamalahal-insān, innahụ kāna ẓalụman jahụlā","at":"Sesungguhnya Kami telah menawarkan amanat kepada langit, bumi dan gunung-gunung; tetapi semuanya enggan untuk memikul amanat itu dan mereka khawatir tidak akan melaksanakannya (berat), lalu dipikullah amanat itu oleh manusia. Sungguh, manusia itu sangat zalim dan sangat bodoh,"},{"id":"73","ar":"لِّيُعَذِّبَ اللّٰهُ الْمُنٰفِقِيْنَ وَالْمُنٰفِقَتِ وَالْمُشْرِكِيْنَ وَالْمُشْرِكٰتِ وَيَتُوْبَ اللّٰهُ عَلَى الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ","bc":"liyu'ażżiballāhul-munāfiqīna wal-munāfiqāti wal-musyrikīna wal-musyrikāti wa yatụballāhu 'alal-mu`minīna wal-mu`mināt, wa kānallāhu gafụrar raḥīmā","at":"sehingga Allah akan mengazab orang-orang munafik laki-laki dan perempuan, orang-orang musyrik, laki-laki dan perempuan; dan Allah akan menerima tobat orang-orang mukmin laki-laki dan perempuan. Dan Allah Maha Pengampun, Maha Penyayang."}],al_ala=[{"id":"1","ar":"سَبِّحِ اسْمَ رَبِّكَ الْاَعْلَىۙ ","bc":"sabbiḥisma rabbikal-a'lā","at":"Sucikanlah nama Tuhanmu Yang Mahatinggi,"},{"id":"2","ar":"الَّذِيْ خَلَقَ فَسَوّٰىۖ ","bc":"allażī khalaqa fa sawwā","at":"Yang menciptakan, lalu menyempurnakan (ciptaan-Nya)."},{"id":"3","ar":"وَالَّذِيْ قَدَّرَ فَهَدٰىۖ ","bc":"wallażī qaddara fa hadā","at":"Yang menentukan kadar (masing-masing) dan memberi petunjuk,"},{"id":"4","ar":"وَالَّذِيْٓ اَخْرَجَ الْمَرْعٰىۖ ","bc":"wallażī akhrajal-mar'ā","at":"dan Yang menumbuhkan rerumputan,"},{"id":"5","ar":"فَجَعَلَهٗ غُثَاۤءً اَحْوٰىۖ ","bc":"fa ja'alahụ guṡā`an aḥwā","at":"lalu dijadikan-Nya (rumput-rumput) itu kering kehitam-hitaman."},{"id":"6","ar":"سَنُقْرِئُكَ فَلَا تَنْسٰىٓ ۖ ","bc":"sanuqri`uka fa lā tansā","at":"Kami akan membacakan (Al-Qur'an) kepadamu (Muhammad) sehingga engkau tidak akan lupa,"},{"id":"7","ar":"اِلَّا مَا شَاۤءَ اللّٰهُ ۗاِنَّهٗ يَعْلَمُ الْجَهْرَ وَمَا يَخْفٰىۗ ","bc":"illā mā syā`allāh, innahụ ya'lamul-jahra wa mā yakhfā","at":"kecuali jika Allah menghendaki. Sungguh, Dia mengetahui yang terang dan yang tersembunyi."},{"id":"8","ar":"وَنُيَسِّرُكَ لِلْيُسْرٰىۖ ","bc":"wa nuyassiruka lil-yusrā","at":"Dan Kami akan memudahkan bagimu ke jalan kemudahan (mencapai kebahagiaan dunia dan akhirat),"},{"id":"9","ar":"فَذَكِّرْ اِنْ نَّفَعَتِ الذِّكْرٰىۗ ","bc":"fa żakkir in nafa'atiż-żikrā","at":"oleh sebab itu berikanlah peringatan, karena peringatan itu bermanfaat,"},{"id":"10","ar":"سَيَذَّكَّرُ مَنْ يَّخْشٰىۙ ","bc":"sayażżakkaru may yakhsyā","at":"orang yang takut (kepada Allah) akan mendapat pelajaran,"},{"id":"11","ar":"وَيَتَجَنَّبُهَا الْاَشْقَىۙ ","bc":"wa yatajannabuhal-asyqā","at":"dan orang-orang yang celaka (kafir) akan menjauhinya,"},{"id":"12","ar":"الَّذِيْ يَصْلَى النَّارَ الْكُبْرٰىۚ ","bc":"allażī yaṣlan-nāral-kubrā","at":"(yaitu) orang yang akan memasuki api yang besar (neraka),"},{"id":"13","ar":"ثُمَّ لَا يَمُوْتُ فِيْهَا وَلَا يَحْيٰىۗ ","bc":"ṡumma lā yamụtu fīhā wa lā yaḥyā","at":"selanjutnya dia di sana tidak mati dan tidak (pula) hidup."},{"id":"14","ar":"قَدْ اَفْلَحَ مَنْ تَزَكّٰىۙ ","bc":"qad aflaḥa man tazakkā","at":"Sungguh beruntung orang yang menyucikan diri (dengan beriman),"},{"id":"15","ar":"وَذَكَرَ اسْمَ رَبِّهٖ فَصَلّٰىۗ ","bc":"wa żakarasma rabbihī fa ṣallā","at":"dan mengingat nama Tuhannya, lalu dia salat."},{"id":"16","ar":"بَلْ تُؤْثِرُوْنَ الْحَيٰوةَ الدُّنْيَاۖ ","bc":"bal tu`ṡirụnal-ḥayātad-dun-yā","at":"Sedangkan kamu (orang-orang kafir) memilih kehidupan dunia,"},{"id":"17","ar":"وَالْاٰخِرَةُ خَيْرٌ وَّاَبْقٰىۗ ","bc":"wal-ākhiratu khairuw wa abqā","at":"padahal kehidupan akhirat itu lebih baik dan lebih kekal."},{"id":"18","ar":"اِنَّ هٰذَا لَفِى الصُّحُفِ الْاُوْلٰىۙ ","bc":"inna hāżā lafiṣ-ṣuḥufil-ụlā","at":"Sesungguhnya ini terdapat dalam kitab-kitab yang dahulu,"},{"id":"19","ar":"صُحُفِ اِبْرٰهِيْمَ وَمُوْسٰى ","bc":"ṣuḥufi ibrāhīma wa mụsā","at":"(yaitu) kitab-kitab Ibrahim dan Musa. "}],al_alaq=[{"id":"1","ar":"اِقْرَأْ بِاسْمِ رَبِّكَ الَّذِيْ خَلَقَۚ","bc":"iqra` bismi rabbikallażī khalaq","at":"Bacalah dengan (menyebut) nama Tuhanmu yang menciptakan,"},{"id":"2","ar":"خَلَقَ الْاِنْسَانَ مِنْ عَلَقٍۚ","bc":"khalaqal-insāna min 'alaq","at":"Dia telah menciptakan manusia dari segumpal darah."},{"id":"3","ar":"اِقْرَأْ وَرَبُّكَ الْاَكْرَمُۙ","bc":"iqra` wa rabbukal-akram","at":"Bacalah, dan Tuhanmulah Yang Mahamulia,"},{"id":"4","ar":"الَّذِيْ عَلَّمَ بِالْقَلَمِۙ","bc":"allażī 'allama bil-qalam","at":"Yang mengajar (manusia) dengan pena."},{"id":"5","ar":"عَلَّمَ الْاِنْسَانَ مَا لَمْ يَعْلَمْۗ","bc":"'allamal-insāna mā lam ya'lam","at":"Dia mengajarkan manusia apa yang tidak diketahuinya."},{"id":"6","ar":"كَلَّآ اِنَّ الْاِنْسَانَ لَيَطْغٰىٓ ۙ","bc":"kallā innal-insāna layaṭgā","at":"Sekali-kali tidak! Sungguh, manusia itu benar-benar melampaui batas,"},{"id":"7","ar":"اَنْ رَّاٰهُ اسْتَغْنٰىۗ","bc":"ar ra`āhustagnā","at":"apabila melihat dirinya serba cukup."},{"id":"8","ar":"اِنَّ اِلٰى رَبِّكَ الرُّجْعٰىۗ","bc":"inna ilā rabbikar-ruj'ā","at":"Sungguh, hanya kepada Tuhanmulah tempat kembali(mu)."},{"id":"9","ar":"اَرَاَيْتَ الَّذِيْ يَنْهٰىۙ","bc":"a ra`aitallażī yan-hā","at":"Bagaimana pendapatmu tentang orang yang melarang?"},{"id":"10","ar":"عَبْدًا اِذَا صَلّٰىۗ","bc":"'abdan iżā ṣallā","at":"seorang hamba ketika dia melaksanakan salat,"},{"id":"11","ar":"اَرَاَيْتَ اِنْ كَانَ عَلَى الْهُدٰىٓۙ","bc":"a ra`aita ing kāna 'alal-hudā","at":"bagaimana pendapatmu jika dia (yang dilarang salat itu) berada di atas kebenaran (petunjuk),"},{"id":"12","ar":"اَوْ اَمَرَ بِالتَّقْوٰىۗ","bc":"au amara bit-taqwā","at":"atau dia menyuruh bertakwa (kepada Allah)?"},{"id":"13","ar":"اَرَاَيْتَ اِنْ كَذَّبَ وَتَوَلّٰىۗ","bc":"a ra`aita ing każżaba wa tawallā","at":"Bagaimana pendapatmu jika dia (yang melarang) itu mendustakan dan berpaling?"},{"id":"14","ar":"اَلَمْ يَعْلَمْ بِاَنَّ اللّٰهَ يَرٰىۗ","bc":"a lam ya'lam bi`annallāha yarā","at":"Tidakkah dia mengetahui bahwa sesungguhnya Allah melihat (segala perbuatannya)?"},{"id":"15","ar":"كَلَّا لَىِٕنْ لَّمْ يَنْتَهِ ەۙ لَنَسْفَعًاۢ بِالنَّاصِيَةِۙ","bc":"kallā la`il lam yantahi lanasfa'am bin-nāṣiyah","at":"Sekali-kali tidak! Sungguh, jika dia tidak berhenti (berbuat demikian) niscaya Kami tarik ubun-ubunnya, (ke dalam neraka),"},{"id":"16","ar":"نَاصِيَةٍ كَاذِبَةٍ خَاطِئَةٍۚ","bc":"nāṣiyating kāżibatin khāṭi`ah","at":"yaitu) ubun-ubun orang yang mendustakan dan durhaka."},{"id":"17","ar":"فَلْيَدْعُ نَادِيَهٗۙ","bc":"falyad'u nādiyah","at":"Maka biarlah dia memanggil golongannya (untuk menolongnya),"},{"id":"18","ar":"سَنَدْعُ الزَّبَانِيَةَۙ","bc":"sanad'uz-zabāniyah","at":"Kelak Kami akan memanggil Malaikat Zabaniyah, (penyiksa orang-orang yang berdosa), "},{"id":"19","ar":"كَلَّاۗ لَا تُطِعْهُ وَاسْجُدْ وَاقْتَرِبْ ۩","bc":"kallā, lā tuṭi'hu wasjud waqtarib","at":"sekali-kali tidak! Janganlah kamu patuh kepadanya; dan sujudlah serta dekatkanlah (dirimu kepada Allah)."}],al_anam=[{"id":"1","ar":"اَلْحَمْدُ لِلّٰهِ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَجَعَلَ الظُّلُمٰتِ وَالنُّوْرَ ەۗ ثُمَّ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْ يَعْدِلُوْنَ","bc":"al-ḥamdu lillāhillażī khalaqas-samāwāti wal-arḍa wa ja'alaẓ-ẓulumāti wan-nụr, ṡummallażīna kafarụ birabbihim ya'dilụn","at":"Segala puji bagi Allah yang telah menciptakan langit dan bumi, dan menjadikan gelap dan terang, namun demikian orang-orang kafir masih mempersekutukan Tuhan mereka dengan sesuatu."},{"id":"2","ar":"هُوَ الَّذِيْ خَلَقَكُمْ مِّنْ طِيْنٍ ثُمَّ قَضٰٓى اَجَلًا ۗوَاَجَلٌ مُّسَمًّى عِنْدَهٗ ثُمَّ اَنْتُمْ تَمْتَرُوْنَ ","bc":"huwallażī khalaqakum min ṭīnin ṡumma qaḍā ajalā, wa ajalum musamman 'indahụ ṡumma antum tamtarụn","at":"Dialah yang menciptakan kamu dari tanah, kemudian Dia menetapkan ajal (kematianmu), dan batas waktu tertentu yang hanya diketahui oleh-Nya. Namun demikian kamu masih meragukannya."},{"id":"3","ar":"وَهُوَ اللّٰهُ فِى السَّمٰوٰتِ وَفِى الْاَرْضِۗ يَعْلَمُ سِرَّكُمْ وَجَهْرَكُمْ وَيَعْلَمُ مَا تَكْسِبُوْنَ ","bc":"wa huwallāhu fis-samāwāti wa fil-arḍ, ya'lamu sirrakum wa jahrakum wa ya'lamu mā taksibụn","at":"Dan Dialah Allah (yang disembah), di langit maupun di bumi; Dia mengetahui apa yang kamu rahasiakan dan apa yang kamu nyatakan dan mengetahui (pula) apa yang kamu kerjakan."},{"id":"4","ar":"وَمَا تَأْتِيْهِمْ مِّنْ اٰيَةٍ مِّنْ اٰيٰتِ رَبِّهِمْ اِلَّا كَانُوْا عَنْهَا مُعْرِضِيْنَ ","bc":"wa mā ta`tīhim min āyatim min āyāti rabbihim illā kānụ 'an-hā mu'riḍīn","at":"Dan setiap ayat dari ayat-ayat Tuhan yang sampai kepada mereka (orang kafir), semuanya selalu diingkarinya."},{"id":"5","ar":"فَقَدْ كَذَّبُوْا بِالْحَقِّ لَمَّا جَاۤءَهُمْۗ فَسَوْفَ يَأْتِيْهِمْ اَنْۢبـٰۤؤُا مَا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ","bc":"fa qad każżabụ bil-ḥaqqi lammā jā`ahum, fa saufa ya`tīhim ambā`u mā kānụ bihī yastahzi`ụn","at":"Sungguh, mereka telah mendustakan kebenaran (Al-Qur'an) ketika sampai kepada mereka, maka kelak akan sampai kepada mereka (kenyataan dari) berita-berita yang selalu mereka perolok-olokkan."},{"id":"6","ar":"اَلَمْ يَرَوْا كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِّنْ قَرْنٍ مَّكَّنّٰهُمْ فِى الْاَرْضِ مَا لَمْ نُمَكِّنْ لَّكُمْ وَاَرْسَلْنَا السَّمَاۤءَ عَلَيْهِمْ مِّدْرَارًا ۖوَّجَعَلْنَا الْاَنْهٰرَ تَجْرِيْ مِنْ تَحْتِهِمْ فَاَهْلَكْنٰهُمْ بِذُنُوْبِهِمْ وَاَنْشَأْنَا مِنْۢ بَعْدِهِمْ قَرْنًا اٰخَرِيْنَ","bc":"a lam yarau kam ahlaknā ming qablihim ming qarnim makkannāhum fil-arḍi mā lam numakkil lakum wa arsalnas-samā`a 'alaihim midrāraw wa ja'alnal-an-hāra tajrī min taḥtihim fa ahlaknāhum biżunụbihim wa ansya`nā mim ba'dihim qarnan ākharīn","at":"Tidakkah mereka memperhatikan berapa banyak generasi sebelum mereka yang telah Kami binasakan, padahal (generasi itu) telah Kami teguhkan kedudukannya di bumi, yaitu keteguhan yang belum pernah Kami berikan kepadamu. Kami curahkan hujan yang lebat untuk mereka dan Kami jadikan sungai-sungai mengalir di bawah mereka, kemudian Kami binasakan mereka karena dosa-dosa mereka sendiri, dan Kami ciptakan generasi yang lain setelah generasi mereka."},{"id":"7","ar":"وَلَوْ نَزَّلْنَا عَلَيْكَ كِتٰبًا فِيْ قِرْطَاسٍ فَلَمَسُوْهُ بِاَيْدِيْهِمْ لَقَالَ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ ","bc":"walau nazzalnā 'alaika kitāban fī qirṭāsin fa lamasụhu bi`aidīhim laqālallażīna kafarū in hāżā illā siḥrum mubīn","at":"Dan sekiranya Kami turunkan kepadamu (Muhammad) tulisan di atas kertas, sehingga mereka dapat memegangnya dengan tangan mereka sendiri, niscaya orang-orang kafir itu akan berkata, “Ini tidak lain hanyalah sihir yang nyata.”"},{"id":"8","ar":"وَقَالُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ مَلَكٌ ۗوَلَوْ اَنْزَلْنَا مَلَكًا لَّقُضِيَ الْاَمْرُ ثُمَّ لَا يُنْظَرُوْنَ ","bc":"wa qālụ lau lā unzila 'alaihi malak, walau anzalnā malakal laquḍiyal-amru ṡumma lā yunẓarụn","at":"Dan mereka berkata, “Mengapa tidak diturunkan malaikat kepadanya (Muhammad)?” Jika Kami turunkan malaikat (kepadanya), tentu selesailah urusan itu, tetapi mereka tidak diberi penangguhan (sedikit pun)."},{"id":"9","ar":"وَلَوْ جَعَلْنٰهُ مَلَكًا لَّجَعَلْنٰهُ رَجُلًا وَّلَلَبَسْنَا عَلَيْهِمْ مَّا يَلْبِسُوْنَ ","bc":"walau ja'alnāhu malakal laja'alnāhu rajulaw wa lalabasnā 'alaihim mā yalbisụn","at":"Dan sekiranya rasul itu Kami jadikan (dari) malaikat, pastilah Kami jadikan dia (berwujud) laki-laki, dan (dengan demikian) pasti Kami akan menjadikan mereka tetap ragu sebagaimana kini mereka ragu."},{"id":"10","ar":"وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَحَاقَ بِالَّذِيْنَ سَخِرُوْا مِنْهُمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ","bc":"wa laqadistuhzi`a birusulim ming qablika fa ḥāqa billażīna sakhirụ min-hum mā kānụ bihī yastahzi`ụn","at":"Dan sungguh, beberapa rasul sebelum engkau (Muhammad) telah diperolok-olokkan, sehingga turunlah azab kepada orang-orang yang mencemoohkan itu sebagai balasan olok-olokan mereka."},{"id":"11","ar":"قُلْ سِيْرُوْا فِى الْاَرْضِ ثُمَّ انْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ ","bc":"qul sīrụ fil-arḍi ṡummanẓurụ kaifa kāna 'āqibatul-mukażżibīn","at":"Katakanlah (Muhammad), “Jelajahilah bumi, kemudian perhatikanlah bagai-mana kesudahan orang-orang yang mendustakan itu.”"},{"id":"12","ar":"قُلْ لِّمَنْ مَّا فِى السَّمٰوٰتِ وَالْاَرْضِۗ قُلْ لِّلّٰهِ ۗ كَتَبَ عَلٰى نَفْسِهِ الرَّحْمَةَ ۗ لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ فِيْهِۗ اَلَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُوْنَ ","bc":"qul limam mā fis-samāwāti wal-arḍ, qul lillāh, kataba 'alā nafsihir-raḥmah, layajma'annakum ilā yaumil-qiyāmati lā raiba fīh, allażīna khasirū anfusahum fa hum lā yu`minụn","at":"Katakanlah (Muhammad), “Milik siapakah apa yang di langit dan di bumi?” Katakanlah, “Milik Allah.” Dia telah menetapkan (sifat) kasih sayang pada diri-Nya. Dia sungguh akan mengumpulkan kamu pada hari Kiamat yang tidak diragukan lagi. Orang-orang yang merugikan dirinya, mereka itu tidak beriman."},{"id":"13","ar":"۞ وَلَهٗ مَا سَكَنَ فِى الَّيْلِ وَالنَّهَارِ ۗوَهُوَ السَّمِيْعُ الْعَلِيْمُ ","bc":"wa lahụ mā sakana fil-laili wan-nahār, wa huwas-samī'ul-'alīm","at":"Dan milik-Nyalah segala apa yang ada pada malam dan siang hari. Dan Dialah Yang Maha Mendengar, Maha Mengetahui."},{"id":"14","ar":"قُلْ اَغَيْرَ اللّٰهِ اَتَّخِذُ وَلِيًّا فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِ وَهُوَ يُطْعِمُ وَلَا يُطْعَمُ ۗ قُلْ اِنِّيْٓ اُمِرْتُ اَنْ اَكُوْنَ اَوَّلَ مَنْ اَسْلَمَ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ ","bc":"qul agairallāhi attakhiżu waliyyan fāṭiris-samāwāti wal-arḍi wa huwa yuṭ'imu wa lā yuṭ'am, qul innī umirtu an akụna awwala man aslama wa lā takụnanna minal-musyrikīn","at":"Katakanlah (Muhammad), “Apakah aku akan menjadikan pelindung selain Allah yang menjadikan langit dan bumi, padahal Dia memberi makan dan tidak diberi makan?” Katakanlah, “Sesungguhnya aku diperintahkan agar aku menjadi orang yang pertama berserah diri (kepada Allah), dan jangan sekali-kali kamu masuk golongan orang-orang musyrik.”"},{"id":"15","ar":"قُلْ اِنِّيْٓ اَخَافُ اِنْ عَصَيْتُ رَبِّيْ عَذَابَ يَوْمٍ عَظِيْمٍ ","bc":"qul innī akhāfu in 'aṣaitu rabbī 'ażāba yaumin 'aẓīm","at":"Katakanlah (Muhammad), “Aku benar-benar takut akan azab hari yang besar (hari Kiamat), jika aku mendurhakai Tuhanku.”"},{"id":"16","ar":"مَنْ يُّصْرَفْ عَنْهُ يَوْمَىِٕذٍ فَقَدْ رَحِمَهٗ ۗوَذٰلِكَ الْفَوْزُ الْمُبِيْنُ","bc":"may yuṣraf 'an-hu yauma`iżin fa qad raḥimah, wa żālikal-fauzul-mubīn","at":"Barangsiapa dijauhkan dari azab atas dirinya pada hari itu, maka sungguh, Allah telah memberikan rahmat kepadanya. Dan itulah kemenangan yang nyata."},{"id":"17","ar":"وَاِنْ يَّمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهٗٓ اِلَّا هُوَ ۗوَاِنْ يَّمْسَسْكَ بِخَيْرٍ فَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ","bc":"wa iy yamsaskallāhu biḍurrin fa lā kāsyifa lahū illā huw, wa iy yamsaska bikhairin fa huwa 'alā kulli syai`ing qadīr","at":"Dan jika Allah menimpakan suatu bencana kepadamu, tidak ada yang dapat menghilangkannya selain Dia. Dan jika Dia mendatangkan kebaikan kepadamu, maka Dia Mahakuasa atas segala sesuatu."},{"id":"18","ar":"وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهٖۗ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ ","bc":"wa huwal-qāhiru fauqa 'ibādih, wa huwal-ḥakīmul-khabīr","at":"Dan Dialah yang berkuasa atas hamba-hamba-Nya. Dan Dia Mahabijaksana, Maha Mengetahui."},{"id":"19","ar":"قُلْ اَيُّ شَيْءٍ اَكْبَرُ شَهَادَةً ۗ قُلِ اللّٰهُ ۗشَهِيْدٌۢ بَيْنِيْ وَبَيْنَكُمْ ۗوَاُوْحِيَ اِلَيَّ هٰذَا الْقُرْاٰنُ لِاُنْذِرَكُمْ بِهٖ وَمَنْۢ بَلَغَ ۗ اَىِٕنَّكُمْ لَتَشْهَدُوْنَ اَنَّ مَعَ اللّٰهِ اٰلِهَةً اُخْرٰىۗ قُلْ لَّآ اَشْهَدُ ۚ قُلْ اِنَّمَا هُوَ اِلٰهٌ وَّاحِدٌ وَّاِنَّنِيْ بَرِيْۤءٌ مِّمَّا تُشْرِكُوْنَ","bc":"qul ayyu syai`in akbaru syahādah, qulillāh, syahīdum bainī wa bainakum, wa ụḥiya ilayya hāżal-qur`ānu li`unżirakum bihī wa mam balag, a innakum latasy-hadụna anna ma'allāhi ālihatan ukhrā, qul lā asy-had, qul innamā huwa ilāhuw wāḥiduw wa innanī barī`um mimmā tusyrikụn","at":"Katakanlah (Muhammad), “Siapakah yang lebih kuat kesaksiannya?” Katakanlah, “Allah, Dia menjadi saksi antara aku dan kamu. Al-Qur'an ini diwahyukan kepadaku agar dengan itu aku memberi peringatan kepadamu dan kepada orang yang sampai (Al-Qur'an kepadanya). Dapatkah kamu benar-benar bersaksi bahwa ada tuhan-tuhan lain bersama Allah?” Katakanlah, “Aku tidak dapat bersaksi.” Katakanlah, “Sesungguhnya hanya Dialah Tuhan Yang Maha Esa dan aku berlepas diri dari apa yang kamu persekutukan (dengan Allah).”"},{"id":"20","ar":"اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْرِفُوْنَهٗ كَمَا يَعْرِفُوْنَ اَبْنَاۤءَهُمْۘ اَلَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فَهُمْ لَا يُؤْمِنُوْنَ","bc":"allażīna ātaināhumul-kitāba ya'rifụnahụ kamā ya'rifụna abnā`ahum, allażīna khasirū anfusahum fa hum lā yu`minụn","at":"Orang-orang yang telah Kami berikan Kitab kepadanya, mereka mengenalnya (Muhammad) seperti mereka mengenal anak-anaknya sendiri. Orang-orang yang merugikan dirinya, mereka itu tidak beriman (kepada Allah)."},{"id":"21","ar":"وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ","bc":"wa man aẓlamu mim maniftarā 'alallāhi każiban au każżaba bi`āyātih, innahụ lā yufliḥuẓ-ẓālimụn","at":"Dan siapakah yang lebih zalim daripada orang yang mengada-adakan suatu kebohongan terhadap Allah, atau yang mendustakan ayat-ayat-Nya? Sesungguhnya orang-orang yang zalim itu tidak beruntung."},{"id":"22","ar":"وَيَوْمَ نَحْشُرُهُمْ جَمِيْعًا ثُمَّ نَقُوْلُ لِلَّذِيْنَ اَشْرَكُوْٓا اَيْنَ شُرَكَاۤؤُكُمُ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ ","bc":"wa yauma naḥsyuruhum jamī'an ṡumma naqụlu lillażīna asyrakū aina syurakā`ukumullażīna kuntum taz'umụn","at":"Dan (ingatlah), pada hari ketika Kami mengumpulkan mereka semua kemudian Kami berfirman kepada orang-orang yang menyekutukan Allah, “Di manakah sembahan-sembahanmu yang dahulu kamu sangka (sekutu-sekutu Kami)?”"},{"id":"23","ar":"ثُمَّ لَمْ تَكُنْ فِتْنَتُهُمْ اِلَّآ اَنْ قَالُوْا وَاللّٰهِ رَبِّنَا مَا كُنَّا مُشْرِكِيْنَ ","bc":"ṡumma lam takun fitnatuhum illā ang qālụ wallāhi rabbinā mā kunnā musyrikīn","at":"Kemudian tidaklah ada jawaban bohong mereka, kecuali mengatakan, “Demi Allah, ya Tuhan kami, tidaklah kami mempersekutukan Allah.”"},{"id":"24","ar":"اُنْظُرْ كَيْفَ كَذَبُوْا عَلٰٓى اَنْفُسِهِمْ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ ","bc":"unẓur kaifa każabụ 'alā anfusihim wa ḍalla 'an-hum mā kānụ yaftarụn","at":"Lihatlah, bagaimana mereka berbohong terhadap diri mereka sendiri. Dan sesembahan yang mereka ada-adakan dahulu akan hilang dari mereka."},{"id":"25","ar":"وَمِنْهُمْ مَّنْ يَّسْتَمِعُ اِلَيْكَ ۚوَجَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًا ۗوَاِنْ يَّرَوْا كُلَّ اٰيَةٍ لَّا يُؤْمِنُوْا بِهَا ۗحَتّٰٓى اِذَا جَاۤءُوْكَ يُجَادِلُوْنَكَ يَقُوْلُ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ","bc":"wa min-hum may yastami'u ilaīk, wa ja'alnā 'alā qulụbihim akinnatan ay yafqahụhu wa fī āżānihim waqrā, wa iy yarau kulla āyatil lā yu`minụ bihā, ḥattā iżā jā`ụka yujādilụnaka yaqụlullażīna kafarū in hāżā illā asāṭīrul-awwalīn","at":"Dan di antara mereka ada yang mendengarkan bacaanmu (Muhammad), dan Kami telah menjadikan hati mereka tertutup (sehingga mereka tidak) memahaminya, dan telinganya tersumbat. Dan kalaupun mereka melihat segala tanda (kebenaran), mereka tetap tidak mau beriman kepadanya. Sehingga apabila mereka datang kepadamu untuk membantahmu, orang-orang kafir itu berkata, “Ini (Al-Qur'an) tidak lain hanyalah dongengan orang-orang terdahulu.”"},{"id":"26","ar":"وَهُمْ يَنْهَوْنَ عَنْهُ وَيَنْـَٔوْنَ عَنْهُ ۚوَاِنْ يُّهْلِكُوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَ ","bc":"wa hum yan-hauna 'an-hu wa yan`auna 'an-h, wa iy yuhlikụna illā anfusahum wa mā yasy'urụn","at":"Dan mereka melarang (orang lain) mendengarkan (Al-Qur'an) dan mereka sendiri menjauhkan diri daripadanya, dan mereka hanyalah membinasakan diri mereka sendiri, sedang mereka tidak menyadari."},{"id":"27","ar":"وَلَوْ تَرٰٓى اِذْ وُقِفُوْا عَلَى النَّارِ فَقَالُوْا يٰلَيْتَنَا نُرَدُّ وَلَا نُكَذِّبَ بِاٰيٰتِ رَبِّنَا وَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ ","bc":"walau tarā iż wuqifụ 'alan-nāri fa qālụ yā laitanā nuraddu wa lā nukażżiba bi`āyāti rabbinā wa nakụna minal-mu`minīn","at":"Dan seandainya engkau (Muhammad) melihat ketika mereka dihadapkan ke neraka, mereka berkata, “Seandainya kami dikembalikan (ke dunia), tentu kami tidak akan mendustakan ayat-ayat Tuhan kami, serta menjadi orang-orang yang beriman.”"},{"id":"28","ar":"بَلْ بَدَا لَهُمْ مَّا كَانُوْا يُخْفُوْنَ مِنْ قَبْلُ ۗوَلَوْ رُدُّوْا لَعَادُوْا لِمَا نُهُوْا عَنْهُ وَاِنَّهُمْ لَكٰذِبُوْنَ ","bc":"bal badā lahum mā kānụ yukhfụna ming qabl, walau ruddụ la'ādụ limā nuhụ 'an-hu wa innahum lakāżibụn","at":"Tetapi (sebenarnya) bagi mereka telah nyata kejahatan yang mereka sembunyikan dahulu. Seandainya mereka dikembalikan ke dunia, tentu mereka akan mengulang kembali apa yang telah dilarang mengerjakannya. Mereka itu sungguh pendusta."},{"id":"29","ar":"وَقَالُوْٓا اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا وَمَا نَحْنُ بِمَبْعُوْثِيْنَ ","bc":"wa qālū in hiya illā ḥayātunad-dun-yā wa mā naḥnu bimab'ụṡīn","at":"Dan tentu mereka akan mengatakan (pula), “Hidup hanyalah di dunia ini, dan kita tidak akan dibangkitkan.”"},{"id":"30","ar":"وَلَوْ تَرٰٓى اِذْ وُقِفُوْا عَلٰى رَبِّهِمْ ۗ قَالَ اَلَيْسَ هٰذَا بِالْحَقِّ ۗقَالُوْا بَلٰى وَرَبِّنَا ۗقَالَ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ","bc":"walau tarā iż wuqifụ 'alā rabbihim, qāla a laisa hāżā bil-ḥaqq, qālụ balā wa rabbinā, qāla fa żụqul-'ażāba bimā kuntum takfurụn","at":"Dan seandainya engkau (Muhammad) melihat ketika mereka dihadapkan kepada Tuhannya (tentulah engkau melihat peristiwa yang mengharukan). Dia berfirman, “Bukankah (kebangkitan) ini benar?” Mereka menjawab, “Sungguh benar, demi Tuhan kami.” Dia berfirman, “Rasakanlah azab ini, karena dahulu kamu mengingkarinya.”"},{"id":"31","ar":"قَدْ خَسِرَ الَّذِيْنَ كَذَّبُوْا بِلِقَاۤءِ اللّٰهِ ۗحَتّٰٓى اِذَا جَاۤءَتْهُمُ السَّاعَةُ بَغْتَةً قَالُوْا يٰحَسْرَتَنَا عَلٰى مَا فَرَّطْنَا فِيْهَاۙ وَهُمْ يَحْمِلُوْنَ اَوْزَارَهُمْ عَلٰى ظُهُوْرِهِمْۗ اَلَا سَاۤءَ مَا يَزِرُوْنَ ","bc":"qad khasirallażīna każżabụ biliqā`illāh, ḥattā iżā jā`at-humus-sā'atu bagtatang qālụ yā ḥasratanā 'alā mā farraṭnā fīhā wa hum yaḥmilụna auzārahum 'alā ẓuhụrihim, alā sā`a mā yazirụn","at":"Sungguh rugi orang-orang yang mendustakan pertemuan dengan Allah; sehingga apabila Kiamat datang kepada mereka secara tiba-tiba, mereka berkata, “Alangkah besarnya penyesalan kami terhadap kelalaian kami tentang Kiamat itu,” sambil mereka memikul dosa-dosa di atas punggungnya. Alangkah buruknya apa yang mereka pikul itu."},{"id":"32","ar":"وَمَا الْحَيٰوةُ الدُّنْيَآ اِلَّا لَعِبٌ وَّلَهْوٌ ۗوَلَلدَّارُ الْاٰخِرَةُ خَيْرٌ لِّلَّذِيْنَ يَتَّقُوْنَۗ اَفَلَا تَعْقِلُوْنَ ","bc":"wa mal-ḥayātud-dun-yā illā la'ibuw wa lahw, wa lad-dārul-ākhiratu khairul lillażīna yattaqụn, a fa lā ta'qilụn","at":"Dan kehidupan dunia ini hanyalah permainan dan senda gurau. Sedangkan negeri akhirat itu, sungguh lebih baik bagi orang-orang yang bertakwa. Tidakkah kamu mengerti?"},{"id":"33","ar":"قَدْ نَعْلَمُ اِنَّهٗ لَيَحْزُنُكَ الَّذِيْ يَقُوْلُوْنَ فَاِنَّهُمْ لَا يُكَذِّبُوْنَكَ وَلٰكِنَّ الظّٰلِمِيْنَ بِاٰيٰتِ اللّٰهِ يَجْحَدُوْنَ ","bc":"qad na'lamu innahụ layaḥzunukallażī yaqụlụna fa innahum lā yukażżibụnaka wa lākinnaẓ-ẓālimīna bi`āyātillāhi yaj-ḥadụn","at":"Sungguh, Kami mengetahui bahwa apa yang mereka katakan itu menyedihkan hatimu (Muhammad), (janganlah bersedih hati) karena sebenarnya mereka bukan mendustakan engkau, tetapi orang yang zalim itu mengingkari ayat-ayat Allah."},{"id":"34","ar":"وَلَقَدْ كُذِّبَتْ رُسُلٌ مِّنْ قَبْلِكَ فَصَبَرُوْا عَلٰى مَا كُذِّبُوْا وَاُوْذُوْا حَتّٰٓى اَتٰىهُمْ نَصْرُنَا ۚوَلَا مُبَدِّلَ لِكَلِمٰتِ اللّٰهِ ۚوَلَقَدْ جَاۤءَكَ مِنْ نَّبَإِ۟ى الْمُرْسَلِيْنَ ","bc":"wa laqad kużżibat rusulum ming qablika fa ṣabarụ 'alā mā kużżibụ wa ụżụ ḥattā atāhum naṣrunā, wa lā mubaddila likalimātillāh, wa laqad jā`aka min naba`il-mursalīn","at":"Dan sesungguhnya rasul-rasul sebelum engkau pun telah didustakan, tetapi mereka sabar terhadap pendustaan dan penganiayaan (yang dilakukan) terhadap mereka, sampai datang pertolongan Kami kepada mereka. Dan tidak ada yang dapat mengubah kalimat-kalimat (ketetapan) Allah. Dan sungguh, telah datang kepadamu sebagian dari berita rasul-rasul itu."},{"id":"35","ar":"وَاِنْ كَانَ كَبُرَ عَلَيْكَ اِعْرَاضُهُمْ فَاِنِ اسْتَطَعْتَ اَنْ تَبْتَغِيَ نَفَقًا فِى الْاَرْضِ اَوْ سُلَّمًا فِى السَّمَاۤءِ فَتَأْتِيَهُمْ بِاٰيَةٍ ۗوَلَوْ شَاۤءَ اللّٰهُ لَجَمَعَهُمْ عَلَى الْهُدٰى فَلَا تَكُوْنَنَّ مِنَ الْجٰهِلِيْنَ ","bc":"wa ing kāna kabura 'alaika i'rāḍuhum fa inistaṭa'ta an tabtagiya nafaqan fil-arḍi au sullaman fis-samā`i fa ta`tiyahum bi`āyah, walau syā`allāhu lajama'ahum 'alal-hudā fa lā takụnanna minal-jāhilīn","at":"Dan jika keberpalingan mereka terasa berat bagimu (Muhammad), maka sekiranya engkau dapat membuat lubang di bumi atau tangga ke langit lalu engkau dapat mendatangkan mukjizat kepada mereka, (maka buatlah). Dan sekiranya Allah menghendaki, tentu Dia jadikan mereka semua mengikuti petunjuk, sebab itu janganlah sekali-kali engkau termasuk orang-orang yang bodoh."},{"id":"36","ar":"۞ اِنَّمَا يَسْتَجِيْبُ الَّذِيْنَ يَسْمَعُوْنَ ۗوَالْمَوْتٰى يَبْعَثُهُمُ اللّٰهُ ثُمَّ اِلَيْهِ يُرْجَعُوْنَ ","bc":"innamā yastajībullażīna yasma'ụn, wal-mautā yab'aṡuhumullāhu ṡumma ilaihi yurja'ụn","at":"Hanya orang-orang yang mendengar sajalah yang mematuhi (seruan Allah), dan orang-orang yang mati, kelak akan dibangkitkan oleh Allah, kemudian kepada-Nya mereka dikembalikan."},{"id":"37","ar":"وَقَالُوْا لَوْلَا نُزِّلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ قُلْ اِنَّ اللّٰهَ قَادِرٌ عَلٰٓى اَنْ يُّنَزِّلَ اٰيَةً وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ","bc":"wa qālụ lau lā nuzzila 'alaihi āyatum mir rabbih, qul innallāha qādirun 'alā ay yunazzila āyataw wa lākinna akṡarahum lā ya'lamụn","at":"Dan mereka (orang-orang musyrik) berkata, “Mengapa tidak diturunkan kepadanya (Muhammad) suatu mukjizat dari Tuhannya?” Katakanlah, “Sesungguhnya Allah berkuasa menurunkan suatu mukjizat, tetapi kebanyakan mereka tidak mengetahui.”"},{"id":"38","ar":"وَمَا مِنْ دَاۤبَّةٍ فِى الْاَرْضِ وَلَا طٰۤىِٕرٍ يَّطِيْرُ بِجَنَاحَيْهِ اِلَّآ اُمَمٌ اَمْثَالُكُمْ ۗمَا فَرَّطْنَا فِى الْكِتٰبِ مِنْ شَيْءٍ ثُمَّ اِلٰى رَبِّهِمْ يُحْشَرُوْنَ ","bc":"wa mā min dābbatin fil-arḍi wa lā ṭā`iriy yaṭīru bijanāḥaihi illā umamun amṡālukum, mā farraṭnā fil-kitābi min syai`in ṡumma ilā rabbihim yuḥsyarụn","at":"Dan tidak ada seekor binatang pun yang ada di bumi dan burung-burung yang terbang dengan kedua sayapnya, melainkan semuanya merupakan umat-umat (juga) seperti kamu. Tidak ada sesuatu pun yang Kami luputkan di dalam Kitab, kemudian kepada Tuhan mereka dikumpulkan."},{"id":"39","ar":"وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا صُمٌّ وَّبُكْمٌ فِى الظُّلُمٰتِۗ مَنْ يَّشَاِ اللّٰهُ يُضْلِلْهُ وَمَنْ يَّشَأْ يَجْعَلْهُ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"wallażīna każżabụ bi`āyātinā ṣummuw wa bukmun fiẓ-ẓulumāt, may yasya`illāhu yuḍlil-hu wa may yasya` yaj'al-hu 'alā ṣirāṭim mustaqīm","at":"Dan orang-orang yang mendustakan ayat-ayat Kami adalah tuli, bisu dan berada dalam gelap gulita. Barangsiapa dikehendaki Allah (dalam kesesatan), niscaya disesatkan-Nya. Dan barangsiapa dikehendaki Allah (untuk diberi petunjuk), niscaya Dia menjadikannya berada di atas jalan yang lurus."},{"id":"40","ar":"قُلْ اَرَءَيْتَكُمْ اِنْ اَتٰىكُمْ عَذَابُ اللّٰهِ اَوْ اَتَتْكُمُ السَّاعَةُ اَغَيْرَ اللّٰهِ تَدْعُوْنَۚ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"qul a ra`aitakum in atākum 'ażābullāhi au atatkumus-sā'atu a gairallāhi tad'ụn, ing kuntum ṣādiqīn","at":"Katakanlah (Muhammad), “Terangkanlah kepadaku jika siksaan Allah sampai kepadamu, atau hari Kiamat sampai kepadamu, apakah kamu akan menyeru (tuhan) selain Allah, jika kamu orang yang benar!”"},{"id":"41","ar":"بَلْ اِيَّاهُ تَدْعُوْنَ فَيَكْشِفُ مَا تَدْعُوْنَ اِلَيْهِ اِنْ شَاۤءَ وَتَنْسَوْنَ مَا تُشْرِكُوْنَ","bc":"bal iyyāhu tad'ụna fa yaksyifu mā tad'ụna ilaihi in syā`a wa tansauna mā tusyrikụn","at":"(Tidak), hanya kepada-Nya kamu minta tolong. Jika Dia menghendaki, Dia hilangkan apa (bahaya) yang kamu mohonkan kepada-Nya, dan kamu tinggalkan apa yang kamu persekutukan (dengan Allah)."},{"id":"42","ar":"وَلَقَدْ اَرْسَلْنَآ اِلٰٓى اُمَمٍ مِّنْ قَبْلِكَ فَاَخَذْنٰهُمْ بِالْبَأْسَاۤءِ وَالضَّرَّاۤءِ لَعَلَّهُمْ يَتَضَرَّعُوْنَ ","bc":"wa laqad arsalnā ilā umamim ming qablika fa akhażnāhum bil-ba`sā`i waḍ-ḍarrā`i la'allahum yataḍarra'ụn","at":"Dan sungguh, Kami telah mengutus (para rasul) kepada umat-umat sebelum engkau, kemudian Kami siksa mereka dengan (menimpakan) kemelaratan dan kesengsaraan, agar mereka memohon (kepada Allah) dengan kerendahan hati."},{"id":"43","ar":"فَلَوْلَآ اِذْ جَاۤءَهُمْ بَأْسُنَا تَضَرَّعُوْا وَلٰكِنْ قَسَتْ قُلُوْبُهُمْ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ مَا كَانُوْا يَعْمَلُوْنَ ","bc":"falau lā iż jā`ahum ba`sunā taḍarra'ụ wa lāking qasat qulụbuhum wa zayyana lahumusy-syaiṭānu mā kānụ ya'malụn","at":"Tetapi mengapa mereka tidak memohon (kepada Allah) dengan kerendahan hati ketika siksaan Kami datang menimpa mereka? Bahkan hati mereka telah menjadi keras dan setan pun menjadikan terasa indah bagi mereka apa yang selalu mereka kerjakan."},{"id":"44","ar":"فَلَمَّا نَسُوْا مَا ذُكِّرُوْا بِهٖ فَتَحْنَا عَلَيْهِمْ اَبْوَابَ كُلِّ شَيْءٍۗ حَتّٰٓى اِذَا فَرِحُوْا بِمَآ اُوْتُوْٓا اَخَذْنٰهُمْ بَغْتَةً فَاِذَا هُمْ مُّبْلِسُوْنَ","bc":"fa lammā nasụ mā żukkirụ bihī fataḥnā 'alaihim abwāba kulli syaī`, ḥattā iżā fariḥụ bimā ụtū akhażnāhum bagtatan fa iżā hum mublisụn","at":"Maka ketika mereka melupakan peringatan yang telah diberikan kepada mereka, Kami pun membukakan semua pintu (kesenangan) untuk mereka. Sehingga ketika mereka bergembira dengan apa yang telah diberikan kepada mereka, Kami siksa mereka secara tiba-tiba, maka ketika itu mereka terdiam putus asa."},{"id":"45","ar":"فَقُطِعَ دَابِرُ الْقَوْمِ الَّذِيْنَ ظَلَمُوْاۗ وَالْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ ","bc":"fa quṭi'a dābirul-qaumillażīna ẓalamụ, wal-ḥamdu lillāhi rabbil-'ālamīn","at":"Maka orang-orang yang zalim itu dimusnahkan sampai ke akar-akarnya. Dan segala puji bagi Allah, Tuhan seluruh alam."},{"id":"46","ar":"قُلْ اَرَاَيْتُمْ اِنْ اَخَذَ اللّٰهُ سَمْعَكُمْ وَاَبْصَارَكُمْ وَخَتَمَ عَلٰى قُلُوْبِكُمْ مَّنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِهٖۗ اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيٰتِ ثُمَّ هُمْ يَصْدِفُوْنَ ","bc":"qul a ra`aitum in akhażallāhu sam'akum wa abṣārakum wa khatama 'alā qulụbikum man ilāhun gairullāhi ya`tīkum bih, unẓur kaifa nuṣarriful-āyāti ṡumma hum yaṣdifụn","at":"Katakanlah (Muhammad), “Terangkanlah kepadaku jika Allah mencabut pendengaran dan penglihatan serta menutup hatimu, siapakah tuhan selain Allah yang kuasa mengembalikannya kepadamu?” Perhatikanlah, bagaimana Kami menjelaskan berulang-ulang (kepada mereka) tanda-tanda kekuasaan (Kami), tetapi mereka tetap berpaling."},{"id":"47","ar":"قُلْ اَرَاَيْتَكُمْ اِنْ اَتٰىكُمْ عَذَابُ اللّٰهِ بَغْتَةً اَوْ جَهْرَةً هَلْ يُهْلَكُ اِلَّا الْقَوْمُ الظّٰلِمُوْنَ ","bc":"qul a ra`aitakum in atākum 'ażābullāhi bagtatan au jahratan hal yuhlaku illal-qaumuẓ-ẓālimụn","at":"Katakanlah (Muhammad), “Terangkanlah kepadaku jika siksaan Allah sampai kepadamu secara tiba-tiba atau terang-terangan, maka adakah yang dibinasakan (Allah) selain orang-orang yang zalim?”"},{"id":"48","ar":"وَمَا نُرْسِلُ الْمُرْسَلِيْنَ اِلَّا مُبَشِّرِيْنَ وَمُنْذِرِيْنَۚ فَمَنْ اٰمَنَ وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ ","bc":"wa mā nursilul-mursalīna illā mubasysyirīna wa munżirīn, fa man āmana wa aṣlaḥa fa lā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Para rasul yang Kami utus itu adalah untuk memberi kabar gembira dan memberi peringatan. Barangsiapa beriman dan mengadakan perbaikan, maka tidak ada rasa takut pada mereka dan mereka tidak bersedih hati."},{"id":"49","ar":"وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا يَمَسُّهُمُ الْعَذَابُ بِمَا كَانُوْا يَفْسُقُوْنَ ","bc":"wallażīna każżabụ bi`āyātinā yamassuhumul-'ażābu bimā kānụ yafsuqụn","at":"Dan orang-orang yang mendustakan ayat-ayat Kami akan ditimpa azab karena mereka selalu berbuat fasik (berbuat dosa)."},{"id":"50","ar":"قُلْ لَّآ اَقُوْلُ لَكُمْ عِنْدِيْ خَزَاۤىِٕنُ اللّٰهِ وَلَآ اَعْلَمُ الْغَيْبَ وَلَآ اَقُوْلُ لَكُمْ اِنِّيْ مَلَكٌۚ اِنْ اَتَّبِعُ اِلَّا مَا يُوْحٰٓى اِلَيَّۗ قُلْ هَلْ يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُۗ اَفَلَا تَتَفَكَّرُوْنَ","bc":"qul lā aqụlu lakum 'indī khazā`inullāhi wa lā a'lamul-gaiba wa lā aqụlu lakum innī malak, in attabi'u illā mā yụḥā ilayy, qul hal yastawil-a'mā wal-baṣīr, a fa lā tatafakkarụn","at":"Katakanlah (Muhammad), “Aku tidak mengatakan kepadamu, bahwa perbendaharaan Allah ada padaku, dan aku tidak mengetahui yang gaib dan aku tidak (pula) mengatakan kepadamu bahwa aku malaikat. Aku hanya mengikuti apa yang diwahyukan kepadaku.” Katakanlah, “Apakah sama orang yang buta dengan orang yang melihat? Apakah kamu tidak memikirkan(nya)?”"},{"id":"51","ar":"وَاَنْذِرْ بِهِ الَّذِيْنَ يَخَافُوْنَ اَنْ يُّحْشَرُوْٓا اِلٰى رَبِّهِمْ لَيْسَ لَهُمْ مِّنْ دُوْنِهٖ وَلِيٌّ وَّلَا شَفِيْعٌ لَّعَلَّهُمْ يَتَّقُوْنَ","bc":"wa anżir bihillażīna yakhāfụna ay yuḥsyarū ilā rabbihim laisa lahum min dụnihī waliyyuw wa lā syafī'ul la'allahum yattaqụn","at":"Peringatkanlah dengannya (Al-Qur'an) itu kepada orang yang takut akan dikumpulkan menghadap Tuhannya (pada hari Kiamat), tidak ada bagi mereka pelindung dan pemberi syafaat (pertolongan) selain Allah, agar mereka bertakwa."},{"id":"52","ar":"وَلَا تَطْرُدِ الَّذِيْنَ يَدْعُوْنَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُرِيْدُوْنَ وَجْهَهٗ ۗمَا عَلَيْكَ مِنْ حِسَابِهِمْ مِّنْ شَيْءٍ وَّمَا مِنْ حِسَابِكَ عَلَيْهِمْ مِّنْ شَيْءٍ فَتَطْرُدَهُمْ فَتَكُوْنَ مِنَ الظّٰلِمِيْنَ ","bc":"wa lā taṭrudillażīna yad'ụna rabbahum bil-gadāti wal-'asyiyyi yurīdụna waj-hah, mā 'alaika min ḥisābihim min syai`iw wa mā min ḥisābika 'alaihim min syai`in fa taṭrudahum fa takụna minaẓ-ẓālimīn","at":"Janganlah engkau mengusir orang-orang yang menyeru Tuhannya pada pagi dan petang hari, mereka mengharapkan keridaan-Nya. Engkau tidak memikul tanggung jawab sedikit pun terhadap perbuatan mereka dan mereka tidak memikul tanggung jawab sedikit pun terhadap perbuatanmu, yang menyebabkan engkau (berhak) mengusir mereka, sehingga engkau termasuk orang-orang yang zalim."},{"id":"53","ar":"وَكَذٰلِكَ فَتَنَّا بَعْضَهُمْ بِبَعْضٍ لِّيَقُوْلُوْٓا اَهٰٓؤُلَاۤءِ مَنَّ اللّٰهُ عَلَيْهِمْ مِّنْۢ بَيْنِنَاۗ اَلَيْسَ اللّٰهُ بِاَعْلَمَ بِالشّٰكِرِيْنَ ","bc":"wa każālika fatannā ba'ḍahum biba'ḍil liyaqụlū a hā`ulā`i mannallāhu 'alaihim mim baininā, a laisallāhu bi`a'lama bisy-syākirīn","at":"Demikianlah Kami telah menguji sebagian mereka (orang yang kaya) dengan sebagian yang lain (orang yang miskin), agar mereka (orang yang kaya itu) berkata, “Orang-orang semacam inikah di antara kita yang diberi anugerah oleh Allah?” (Allah berfirman), “Tidakkah Allah lebih mengetahui tentang mereka yang bersyukur (kepada-Nya)?”"},{"id":"54","ar":"وَاِذَا جَاۤءَكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاٰيٰتِنَا فَقُلْ سَلٰمٌ عَلَيْكُمْ كَتَبَ رَبُّكُمْ عَلٰى نَفْسِهِ الرَّحْمَةَۙ اَنَّهٗ مَنْ عَمِلَ مِنْكُمْ سُوْۤءًاۢ بِجَهَالَةٍ ثُمَّ تَابَ مِنْۢ بَعْدِهٖ وَاَصْلَحَ فَاَنَّهٗ غَفُوْرٌ رَّحِيْمٌ ","bc":"wa iżā jā`akallażīna yu`minụna bi`āyātinā fa qul salāmun 'alaikum kataba rabbukum 'alā nafsihir-raḥmata annahụ man 'amila mingkum sū`am bijahālatin ṡumma tāba mim ba'dihī wa aṣlaḥa fa annahụ gafụrur raḥīm","at":"Dan apabila orang-orang yang beriman kepada ayat-ayat Kami datang kepadamu, maka katakanlah, “Salamun ‘alaikum (selamat sejahtera untuk kamu).” Tuhanmu telah menetapkan sifat kasih sayang pada diri-Nya, (yaitu) barang-siapa berbuat kejahatan di antara kamu karena kebodohan, kemudian dia bertobat setelah itu dan memperbaiki diri, maka Dia Maha Pengampun, Maha Penyayang."},{"id":"55","ar":"وَكَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ وَلِتَسْتَبِيْنَ سَبِيْلُ الْمُجْرِمِيْنَ","bc":"wa każālika nufaṣṣilul-āyāti wa litastabīna sabīlul-mujrimīn","at":"Dan demikianlah Kami terangkan ayat-ayat Al-Qur'an, (agar terlihat jelas jalan orang-orang yang saleh) dan agar terlihat jelas (pula) jalan orang-orang yang berdosa."},{"id":"56","ar":"قُلْ اِنِّيْ نُهِيْتُ اَنْ اَعْبُدَ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗ قُلْ لَّآ اَتَّبِعُ اَهْوَاۤءَكُمْۙ قَدْ ضَلَلْتُ اِذًا وَّمَآ اَنَا۠ مِنَ الْمُهْتَدِيْنَ ","bc":"qul innī nuhītu an a'budallażīna tad'ụna min dụnillāh, qul lā attabi'u ahwā`akum qad ḍalaltu iżaw wa mā ana minal-muhtadīn","at":"Katakanlah (Muhammad), “Aku dilarang menyembah tuhan-tuhan yang kamu sembah selain Allah.” Katakanlah, “Aku tidak akan mengikuti keinginanmu. Jika berbuat demikian, sungguh tersesatlah aku, dan aku tidak termasuk orang yang mendapat petunjuk.”"},{"id":"57","ar":"قُلْ اِنِّيْ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَكَذَّبْتُمْ بِهٖۗ مَا عِنْدِيْ مَا تَسْتَعْجِلُوْنَ بِهٖۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗيَقُصُّ الْحَقَّ وَهُوَ خَيْرُ الْفَاصِلِيْنَ","bc":"qul innī 'alā bayyinatim mir rabbī wa każżabtum bih, mā 'indī mā tasta'jilụna bih, inil-ḥukmu illā lillāh, yaquṣṣul-ḥaqqa wa huwa khairul-fāṣilīn","at":"Katakanlah (Muhammad), “Aku (berada) di atas keterangan yang nyata (Al-Qur'an) dari Tuhanku sedang kamu mendustakannya. Bukanlah kewenanganku (untuk menurunkan azab) yang kamu tuntut untuk disegerakan kedatangannya. Menetapkan (hukum itu) hanyalah hak Allah. Dia menerangkan kebenaran dan Dia pemberi keputusan yang terbaik.”"},{"id":"58","ar":"قُلْ لَّوْ اَنَّ عِنْدِيْ مَا تَسْتَعْجِلُوْنَ بِهٖ لَقُضِيَ الْاَمْرُ بَيْنِيْ وَبَيْنَكُمْ ۗوَاللّٰهُ اَعْلَمُ بِالظّٰلِمِيْنَ ","bc":"qul lau anna 'indī mā tasta'jilụna bihī laquḍiyal-amru bainī wa bainakum, wallāhu a'lamu biẓ-ẓālimīn","at":"Katakanlah (Muhammad), “Seandainya ada padaku apa (azab) yang kamu minta agar disegerakan kedatangannya, tentu selesailah segala perkara antara aku dan kamu.” Dan Allah lebih mengetahui tentang orang-orang yang zalim."},{"id":"59","ar":"۞ وَعِنْدَهٗ مَفَاتِحُ الْغَيْبِ لَا يَعْلَمُهَآ اِلَّا هُوَۗ وَيَعْلَمُ مَا فِى الْبَرِّ وَالْبَحْرِۗ وَمَا تَسْقُطُ مِنْ وَّرَقَةٍ اِلَّا يَعْلَمُهَا وَلَا حَبَّةٍ فِيْ ظُلُمٰتِ الْاَرْضِ وَلَا رَطْبٍ وَّلَا يَابِسٍ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍ ","bc":"wa 'indahụ mafātiḥul-gaibi lā ya'lamuhā illā huw, wa ya'lamu mā fil-barri wal-baḥr, wa mā tasquṭu miw waraqatin illā ya'lamuhā wa lā ḥabbatin fī ẓulumātil-arḍi wa lā raṭbiw wa lā yābisin illā fī kitābim mubīn","at":"Dan kunci-kunci semua yang gaib ada pada-Nya; tidak ada yang mengetahui selain Dia. Dia mengetahui apa yang ada di darat dan di laut. Tidak ada sehelai daun pun yang gugur yang tidak diketahui-Nya. Tidak ada sebutir biji pun dalam kegelapan bumi dan tidak pula sesuatu yang basah atau yang kering, yang tidak tertulis dalam Kitab yang nyata (Lauh Mahfuzh)."},{"id":"60","ar":"وَهُوَ الَّذِيْ يَتَوَفّٰىكُمْ بِالَّيْلِ وَيَعْلَمُ مَا جَرَحْتُمْ بِالنَّهَارِ ثُمَّ يَبْعَثُكُمْ فِيْهِ لِيُقْضٰٓى اَجَلٌ مُّسَمًّىۚ ثُمَّ اِلَيْهِ مَرْجِعُكُمْ ثُمَّ يُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ","bc":"wa huwallażī yatawaffākum bil-laili wa ya'lamu mā jaraḥtum bin-nahāri ṡumma yab'aṡukum fīhi liyuqḍā ajalum musammā, ṡumma ilaihi marji'ukum ṡumma yunabbi`ukum bimā kuntum ta'malụn","at":"Dan Dialah yang menidurkan kamu pada malam hari dan Dia mengetahui apa yang kamu kerjakan pada siang hari. Kemudian Dia membangunkan kamu pada siang hari untuk disempurnakan umurmu yang telah ditetapkan. Kemudian kepada-Nya tempat kamu kembali, lalu Dia memberitahukan kepadamu apa yang telah kamu kerjakan."},{"id":"61","ar":"وَهُوَ الْقَاهِرُ فَوْقَ عِبَادِهٖ وَيُرْسِلُ عَلَيْكُمْ حَفَظَةً ۗحَتّٰٓى اِذَا جَاۤءَ اَحَدَكُمُ الْمَوْتُ تَوَفَّتْهُ رُسُلُنَا وَهُمْ لَا يُفَرِّطُوْنَ ","bc":"wa huwal-qāhiru fauqa 'ibādihī wa yursilu 'alaikum ḥafaẓah, ḥattā iżā jā`a aḥadakumul-mautu tawaffat-hu rusulunā wa hum lā yufarriṭụn","at":"Dan Dialah Penguasa mutlak atas semua hamba-Nya, dan diutus-Nya kepadamu malaikat-malaikat penjaga, sehingga apabila kematian datang kepada salah seorang di antara kamu, malaikat-malaikat Kami mencabut nyawanya, dan mereka tidak melalaikan tugasnya."},{"id":"62","ar":"ثُمَّ رُدُّوْٓا اِلَى اللّٰهِ مَوْلٰىهُمُ الْحَقِّۗ اَلَا لَهُ الْحُكْمُ وَهُوَ اَسْرَعُ الْحَاسِبِيْنَ ","bc":"ṡumma ruddū ilallāhi maulāhumul-ḥaqq, alā lahul-ḥukmu wa huwa asra'ul-ḥāsibīn","at":"Kemudian mereka (hamba-hamba Allah) dikembalikan kepada Allah, penguasa mereka yang sebenarnya. Ketahuilah bahwa segala hukum (pada hari itu) ada pada-Nya. Dan Dialah pembuat perhitungan yang paling cepat."},{"id":"63","ar":"قُلْ مَنْ يُّنَجِّيْكُمْ مِّنْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِ تَدْعُوْنَهٗ تَضَرُّعًا وَّخُفْيَةً ۚ لَىِٕنْ اَنْجٰىنَا مِنْ هٰذِهٖ لَنَكُوْنَنَّ مِنَ الشّٰكِرِيْنَ ","bc":"qul may yunajjīkum min ẓulumātil-barri wal-baḥri tad'ụnahụ taḍarru'aw wa khufyah, la`in anjānā min hāżihī lanakụnanna minasy-syākirīn","at":"Katakanlah (Muhammad), “Siapakah yang dapat menyelamatkan kamu dari bencana di darat dan di laut, ketika kamu berdoa kepada-Nya dengan rendah hati dan dengan suara yang lembut?” (Dengan mengatakan), “Sekiranya Dia menyelamatkan kami dari (bencana) ini, tentulah kami menjadi orang-orang yang bersyukur.”"},{"id":"64","ar":"قُلِ اللّٰهُ يُنَجِّيْكُمْ مِّنْهَا وَمِنْ كُلِّ كَرْبٍ ثُمَّ اَنْتُمْ تُشْرِكُوْنَ ","bc":"qulillāhu yunajjīkum min-hā wa ming kulli karbin ṡumma antum tusyrikụn","at":"Katakanlah (Muhammad), “Allah yang menyelamatkan kamu dari bencana itu dan dari segala macam kesusahan, namun kemudian kamu (kembali) mempersekutukan-Nya.”"},{"id":"65","ar":"قُلْ هُوَ الْقَادِرُ عَلٰٓى اَنْ يَّبْعَثَ عَلَيْكُمْ عَذَابًا مِّنْ فَوْقِكُمْ اَوْ مِنْ تَحْتِ اَرْجُلِكُمْ اَوْ يَلْبِسَكُمْ شِيَعًا وَّيُذِيْقَ بَعْضَكُمْ بَأْسَ بَعْضٍۗ اُنْظُرْ كَيْفَ نُصَرِّفُ الْاٰيٰتِ لَعَلَّهُمْ يَفْقَهُوْنَ ","bc":"qul huwal-qādiru 'alā ay yab'aṡa 'alaikum 'ażābam min fauqikum au min taḥti arjulikum au yalbisakum syiya'aw wa yużīqa ba'ḍakum ba`sa ba'ḍ, unẓur kaifa nuṣarriful-āyāti la'allahum yafqahụn","at":"Katakanlah (Muhammad), “Dialah yang berkuasa mengirimkan azab kepadamu, dari atas atau dari bawah kakimu atau Dia mencampurkan kamu dalam golongan-golongan (yang saling bertentangan) dan merasakan kepada sebagian kamu keganasan sebagian yang lain.” Perhatikanlah, bagaimana Kami menjelaskan berulang-ulang tanda-tanda (kekuasaan Kami) agar mereka memahami(nya)."},{"id":"66","ar":"وَكَذَّبَ بِهٖ قَوْمُكَ وَهُوَ الْحَقُّۗ قُلْ لَّسْتُ عَلَيْكُمْ بِوَكِيْلٍ ۗ","bc":"wa każżaba bihī qaumuka wa huwal-ḥaqq, qul lastu 'alaikum biwakīl","at":"Dan kaummu mendustakannya (azab) padahal (azab) itu benar adanya. Katakanlah (Muhammad), “Aku ini bukanlah penanggung jawab kamu.”"},{"id":"67","ar":"لِكُلِّ نَبَاٍ مُّسْتَقَرٌّ وَّسَوْفَ تَعْلَمُوْنَ ","bc":"likulli naba`im mustaqarruw wa saufa ta'lamụn","at":"Setiap berita (yang dibawa oleh rasul) ada (waktu) terjadinya dan kelak kamu akan mengetahui."},{"id":"68","ar":"وَاِذَا رَاَيْتَ الَّذِيْنَ يَخُوْضُوْنَ فِيْٓ اٰيٰتِنَا فَاَعْرِضْ عَنْهُمْ حَتّٰى يَخُوْضُوْا فِيْ حَدِيْثٍ غَيْرِهٖۗ وَاِمَّا يُنْسِيَنَّكَ الشَّيْطٰنُ فَلَا تَقْعُدْ بَعْدَ الذِّكْرٰى مَعَ الْقَوْمِ الظّٰلِمِيْنَ ","bc":"wa iżā ra`aitallażīna yakhụḍụna fī āyātinā fa a'riḍ 'an-hum ḥattā yakhụḍụ fī ḥadīṡin gairih, wa immā yunsiyannakasy-syaiṭānu fa lā taq'ud ba'daż-żikrā ma'al-qaumiẓ-ẓālimīn","at":"Apabila engkau (Muhammad) melihat orang-orang memperolok-olokkan ayat-ayat Kami, maka tinggalkanlah mereka hingga mereka beralih ke pembicaraan lain. Dan jika setan benar-benar menjadikan engkau lupa (akan larangan ini), setelah ingat kembali janganlah engkau duduk bersama orang-orang yang zalim. "},{"id":"69","ar":"وَمَا عَلَى الَّذِيْنَ يَتَّقُوْنَ مِنْ حِسَابِهِمْ مِّنْ شَيْءٍ وَّلٰكِنْ ذِكْرٰى لَعَلَّهُمْ يَتَّقُوْنَ","bc":"wa mā 'alallażīna yattaqụna min ḥisābihim min syai`iw wa lākin żikrā la'allahum yattaqụn","at":"Orang-orang yang bertakwa tidak ada tanggung jawab sedikit pun atas (dosa-dosa) mereka; tetapi (berkewajiban) mengingatkan agar mereka (juga) bertakwa."},{"id":"70","ar":"وَذَرِ الَّذِيْنَ اتَّخَذُوْا دِيْنَهُمْ لَعِبًا وَّلَهْوًا وَّغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَذَكِّرْ بِهٖٓ اَنْ تُبْسَلَ نَفْسٌۢ بِمَا كَسَبَتْۖ لَيْسَ لَهَا مِنْ دُوْنِ اللّٰهِ وَلِيٌّ وَّلَا شَفِيْعٌ ۚوَاِنْ تَعْدِلْ كُلَّ عَدْلٍ لَّا يُؤْخَذْ مِنْهَاۗ اُولٰۤىِٕكَ الَّذِيْنَ اُبْسِلُوْا بِمَا كَسَبُوْا لَهُمْ شَرَابٌ مِّنْ حَمِيْمٍ وَّعَذَابٌ اَلِيْمٌ ۢبِمَا كَانُوْا يَكْفُرُوْنَ","bc":"wa żarillażīnattakhażụ dīnahum la'ibaw wa lahwaw wa garrat-humul-ḥayātud-dun-yā wa żakkir bihī an tubsala nafsum bimā kasabat laisa lahā min dụnillāhi waliyyuw wa lā syafī', wa in ta'dil kulla 'adlil lā yu`khaż min-hā, ulā`ikallażīna ubsilụ bimā kasabụ lahum syarābum min ḥamīmiw wa 'ażābun alīmum bimā kānụ yakfurụn","at":"Tinggalkanlah orang-orang yang menjadikan agamanya sebagai permainan dan senda gurau, dan mereka telah tertipu oleh kehidupan dunia. Peringatkanlah (mereka) dengan Al-Qur'an agar setiap orang tidak terjerumus (ke dalam neraka), karena perbuatannya sendiri. Tidak ada baginya pelindung dan pemberi syafaat (pertolongan) selain Allah. Dan jika dia hendak menebus dengan segala macam tebusan apa pun, niscaya tidak akan diterima. Mereka itulah orang-orang yang dijerumuskan (ke dalam neraka), karena perbuatan mereka sendiri. Mereka mendapat minuman dari air yang mendidih dan azab yang pedih karena kekafiran mereka dahulu."},{"id":"71","ar":"قُلْ اَنَدْعُوْا مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُنَا وَلَا يَضُرُّنَا وَنُرَدُّ عَلٰٓى اَعْقَابِنَا بَعْدَ اِذْ هَدٰىنَا اللّٰهُ كَالَّذِى اسْتَهْوَتْهُ الشَّيٰطِيْنُ فِى الْاَرْضِ حَيْرَانَ لَهٗٓ اَصْحٰبٌ يَّدْعُوْنَهٗٓ اِلَى الْهُدَى ائْتِنَا ۗ قُلْ اِنَّ هُدَى اللّٰهِ هُوَ الْهُدٰىۗ وَاُمِرْنَا لِنُسْلِمَ لِرَبِّ الْعٰلَمِيْنَۙ ","bc":"qul a nad'ụ min dụnillāhi mā lā yanfa'unā wa lā yaḍurrunā wa nuraddu 'alā a'qābinā ba'da iż hadānallāhu kallażistahwat-husy-syayāṭīnu fil-arḍi ḥairāna lahū aṣ-ḥābuy yad'ụnahū ilal-huda`tinā, qul inna hudallāhi huwal-hudā, wa umirnā linuslima lirabbil-'ālamīn","at":"Katakanlah (Muhammad), “Apakah kita akan memohon kepada sesuatu selain Allah, yang tidak dapat memberi manfaat dan tidak (pula) mendatangkan mudarat kepada kita, dan (apakah) kita akan dikembalikan ke belakang, setelah Allah memberi petunjuk kepada kita, seperti orang yang telah disesatkan oleh setan di bumi, dalam keadaan kebingungan.” Kawan-kawannya mengajaknya ke jalan yang lurus (dengan mengatakan), “Ikutilah kami.” Katakanlah, “Sesungguhnya petunjuk Allah itulah petunjuk (yang sebenarnya); dan kita diperintahkan agar berserah diri kepada Tuhan seluruh alam,"},{"id":"72","ar":"وَاَنْ اَقِيْمُوا الصَّلٰوةَ وَاتَّقُوْهُۗ وَهُوَ الَّذِيْٓ اِلَيْهِ تُحْشَرُوْنَ ","bc":"wa an aqīmuṣ-ṣalāta wattaqụh, wa huwallażī ilaihi tuḥsyarụn","at":"dan agar melaksanakan salat serta bertakwa kepada-Nya.” Dan Dialah Tuhan yang kepada-Nya kamu semua akan dihimpun."},{"id":"73","ar":"وَهُوَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ وَيَوْمَ يَقُوْلُ كُنْ فَيَكُوْنُۚ قَوْلُهُ الْحَقُّۗ وَلَهُ الْمُلْكُ يَوْمَ يُنْفَخُ فِى الصُّوْرِۗ عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ ","bc":"wa huwallażī khalaqas-samāwāti wal-arḍa bil-ḥaqq, wa yauma yaqụlu kun fa yakụn, qauluhul-ḥaqq, wa lahul-mulku yauma yunfakhu fiṣ-ṣụr, 'ālimul-gaibi wasy-syahādati wa huwal-ḥakīmul-khabīr","at":"Dialah yang menciptakan langit dan bumi dengan hak (benar), ketika Dia berkata, “Jadilah!” Maka jadilah sesuatu itu. Firman-Nya adalah benar, dan milik-Nyalah segala kekuasaan pada waktu sangkakala ditiup. Dia mengetahui yang gaib dan yang nyata. Dialah Yang Mahabijaksana, Mahateliti."},{"id":"74","ar":"۞ وَاِذْ قَالَ اِبْرٰهِيْمُ لِاَبِيْهِ اٰزَرَ اَتَتَّخِذُ اَصْنَامًا اٰلِهَةً ۚاِنِّيْٓ اَرٰىكَ وَقَوْمَكَ فِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"wa iż qāla ibrāhīmu li`abīhi āzara a tattakhiżu aṣnāman ālihah, innī arāka wa qaumaka fī ḍalālim mubīn","at":"Dan (ingatlah) ketika Ibrahim berkata kepada ayahnya Azar, ”Pantaskah engkau menjadikan berhala-berhala itu sebagai tuhan? Sesungguhnya aku melihat engkau dan kaummu dalam kesesatan yang nyata.”"},{"id":"75","ar":"وَكَذٰلِكَ نُرِيْٓ اِبْرٰهِيْمَ مَلَكُوْتَ السَّمٰوٰتِ وَالْاَرْضِ وَلِيَكُوْنَ مِنَ الْمُوْقِنِيْنَ ","bc":"wa każālika nurī ibrāhīma malakụtas-samāwāti wal-arḍi wa liyakụna minal-mụqinīn","at":"Dan demikianlah Kami memperlihatkan kepada Ibrahim kekuasaan (Kami yang terdapat) di langit dan di bumi, dan agar dia termasuk orang-orang yang yakin."},{"id":"76","ar":"فَلَمَّا جَنَّ عَلَيْهِ الَّيْلُ رَاٰ كَوْكَبًا ۗقَالَ هٰذَا رَبِّيْۚ فَلَمَّآ اَفَلَ قَالَ لَآ اُحِبُّ الْاٰفِلِيْنَ","bc":"fa lammā janna 'alaihil-lailu ra`ā kaukabā, qāla hāżā rabbī, fa lammā afala qāla lā uḥibbul-āfilīn","at":"Ketika malam telah menjadi gelap, dia (Ibrahim) melihat sebuah bintang (lalu) dia berkata, “Inilah Tuhanku.” Maka ketika bintang itu terbenam dia berkata, “Aku tidak suka kepada yang terbenam.”"},{"id":"77","ar":"فَلَمَّا رَاَ الْقَمَرَ بَازِغًا قَالَ هٰذَا رَبِّيْ ۚفَلَمَّآ اَفَلَ قَالَ لَىِٕنْ لَّمْ يَهْدِنِيْ رَبِّيْ لَاَكُوْنَنَّ مِنَ الْقَوْمِ الضَّاۤلِّيْنَ ","bc":"fa lammā ra`al-qamara bāzigang qāla hāżā rabbī, fa lammā afala qāla la`il lam yahdinī rabbī la`akụnanna minal-qaumiḍ-ḍāllīn","at":"Lalu ketika dia melihat bulan terbit dia berkata, “Inilah Tuhanku.” Tetapi ketika bulan itu terbenam dia berkata, “Sungguh, jika Tuhanku tidak memberi petunjuk kepadaku, pastilah aku termasuk orang-orang yang sesat.”"},{"id":"78","ar":"فَلَمَّا رَاَ الشَّمْسَ بَازِغَةً قَالَ هٰذَا رَبِّيْ هٰذَآ اَكْبَرُۚ فَلَمَّآ اَفَلَتْ قَالَ يٰقَوْمِ اِنِّيْ بَرِيْۤءٌ مِّمَّا تُشْرِكُوْنَ ","bc":"fa lammā ra`asy-syamsa bāzigatang qāla hāżā rabbī hāżā akbar, fa lammā afalat qāla yā qaumi innī barī`um mimmā tusyrikụn","at":"Kemudian ketika dia melihat matahari terbit, dia berkata, “Inilah Tuhanku, ini lebih besar.” Tetapi ketika matahari terbenam, dia berkata, “Wahai kaumku! Sungguh, aku berlepas diri dari apa yang kamu persekutukan.”"},{"id":"79","ar":"اِنِّيْ وَجَّهْتُ وَجْهِيَ لِلَّذِيْ فَطَرَ السَّمٰوٰتِ وَالْاَرْضَ حَنِيْفًا وَّمَآ اَنَا۠ مِنَ الْمُشْرِكِيْنَۚ ","bc":"innī wajjahtu waj-hiya lillażī faṭaras-samāwāti wal-arḍa ḥanīfaw wa mā ana minal-musyrikīn","at":"Aku hadapkan wajahku kepada (Allah) yang menciptakan langit dan bumi dengan penuh kepasrahan (mengikuti) agama yang benar, dan aku bukanlah termasuk orang-orang musyrik."},{"id":"80","ar":"وَحَاۤجَّهٗ قَوْمُهٗ ۗقَالَ اَتُحَاۤجُّوْۤنِّيْ فِى اللّٰهِ وَقَدْ هَدٰىنِۗ وَلَآ اَخَافُ مَا تُشْرِكُوْنَ بِهٖٓ اِلَّآ اَنْ يَّشَاۤءَ رَبِّيْ شَيْـًٔا ۗوَسِعَ رَبِّيْ كُلَّ شَيْءٍ عِلْمًا ۗ اَفَلَا تَتَذَكَّرُوْنَ ","bc":"wa ḥājjahụ qaumuh, qāla a tuḥājjūnnī fillāhi wa qad hadān, wa lā akhāfu mā tusyrikụna bihī illā ay yasyā`a rabbī syai`ā, wasi'a rabbī kulla syai`in 'ilmā, a fa lā tatażakkarụn","at":"Dan kaumnya membantahnya. Dia (Ibrahim) berkata, “Apakah kamu hendak membantahku tentang Allah, padahal Dia benar-benar telah memberi petunjuk kepadaku? Aku tidak takut kepada (malapetaka dari) apa yang kamu persekutukan dengan Allah, kecuali Tuhanku menghendaki sesuatu. Ilmu Tuhanku meliputi segala sesuatu. Tidakkah kamu dapat mengambil pelajaran?"},{"id":"81","ar":"وَكَيْفَ اَخَافُ مَآ اَشْرَكْتُمْ وَلَا تَخَافُوْنَ اَنَّكُمْ اَشْرَكْتُمْ بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ عَلَيْكُمْ سُلْطٰنًا ۗفَاَيُّ الْفَرِيْقَيْنِ اَحَقُّ بِالْاَمْنِۚ اِنْ كُنْتُمْ تَعْلَمُوْنَۘ ","bc":"wa kaifa akhāfu mā asyraktum wa lā takhāfụna annakum asyraktum billāhi mā lam yunazzil bihī 'alaikum sulṭānā, fa ayyul-farīqaini aḥaqqu bil-amn, ing kuntum ta'lamụn","at":"Bagaimana aku takut kepada apa yang kamu persekutukan (dengan Allah), padahal kamu tidak takut dengan apa yang Allah sendiri tidak menurunkan keterangan kepadamu untuk mempersekutukan-Nya. Manakah dari kedua golongan itu yang lebih berhak mendapat keamanan (dari malapetaka), jika kamu mengetahui?”"},{"id":"82","ar":"اَلَّذِيْنَ اٰمَنُوْا وَلَمْ يَلْبِسُوْٓا اِيْمَانَهُمْ بِظُلْمٍ اُولٰۤىِٕكَ لَهُمُ الْاَمْنُ وَهُمْ مُّهْتَدُوْنَ","bc":"allażīna āmanụ wa lam yalbisū īmānahum biẓulmin ulā`ika lahumul-amnu wa hum muhtadụn","at":"Orang-orang yang beriman dan tidak mencampuradukkan iman mereka dengan syirik, mereka itulah orang-orang yang mendapat rasa aman dan mereka mendapat petunjuk."},{"id":"83","ar":"وَتِلْكَ حُجَّتُنَآ اٰتَيْنٰهَآ اِبْرٰهِيْمَ عَلٰى قَوْمِهٖۗ نَرْفَعُ دَرَجٰتٍ مَّنْ نَّشَاۤءُۗ اِنَّ رَبَّكَ حَكِيْمٌ عَلِيْمٌ ","bc":"wa tilka ḥujjatunā ātaināhā ibrāhīma 'alā qaumih, narfa'u darajātim man nasyā`, inna rabbaka ḥakīmun 'alīm","at":"Dan itulah keterangan Kami yang Kami berikan kepada Ibrahim untuk menghadapi kaumnya. Kami tinggikan derajat siapa yang Kami kehendaki. Sesungguhnya Tuhanmu Mahabijaksana, Maha Mengetahui."},{"id":"84","ar":"وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَۗ كُلًّا هَدَيْنَا وَنُوْحًا هَدَيْنَا مِنْ قَبْلُ وَمِنْ ذُرِّيَّتِهٖ دَاوٗدَ وَسُلَيْمٰنَ وَاَيُّوْبَ وَيُوْسُفَ وَمُوْسٰى وَهٰرُوْنَ ۗوَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَۙ ","bc":"wa wahabnā lahū is-ḥāqa wa ya'qụb, kullan hadainā wa nụḥan hadainā ming qablu wa min żurriyyatihī dāwụda wa sulaimāna wa ayyụba wa yụsufa wa mụsā wa hārụn, wa każālika najzil-muḥsinīn","at":"Dan Kami telah menganugerahkan Ishak dan Yakub kepadanya. Kepada masing-masing telah Kami beri petunjuk; dan sebelum itu Kami telah memberi petunjuk kepada Nuh, dan kepada sebagian dari keturunannya (Ibrahim) yaitu Dawud, Sulaiman, Ayyub, Yusuf, Musa, dan Harun. Dan demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik,"},{"id":"85","ar":"وَزَكَرِيَّا وَيَحْيٰى وَعِيْسٰى وَاِلْيَاسَۗ كُلٌّ مِّنَ الصّٰلِحِيْنَۙ ","bc":"wa zakariyyā wa yaḥyā wa 'īsā wa ilyās, kullum minaṣ-ṣāliḥīn","at":"dan Zakaria, Yahya, Isa, dan Ilyas. Semuanya termasuk orang-orang yang saleh,"},{"id":"86","ar":"وَاِسْمٰعِيْلَ وَالْيَسَعَ وَيُوْنُسَ وَلُوْطًاۗ وَكُلًّا فَضَّلْنَا عَلَى الْعٰلَمِيْنَۙ ","bc":"wa ismā'īla walyasa'a wa yụnusa wa lụṭā, wa kullan faḍḍalnā 'alal-'ālamīn","at":"dan Ismail, Alyasa‘, Yunus, dan Lut. Masing-masing Kami lebihkan (derajatnya) di atas umat lain (pada masanya),"},{"id":"87","ar":"وَمِنْ اٰبَاۤىِٕهِمْ وَذُرِّيّٰتِهِمْ وَاِخْوَانِهِمْ ۚوَاجْتَبَيْنٰهُمْ وَهَدَيْنٰهُمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"wa min ābā`ihim wa żurriyyātihim wa ikhwānihim, wajtabaināhum wa hadaināhum ilā ṣirāṭim mustaqīm","at":"(dan Kami lebihkan pula derajat) sebagian dari nenek moyang mereka, keturunan mereka dan saudara-saudara mereka. Kami telah memilih mereka (menjadi nabi dan rasul) dan mereka Kami beri petunjuk ke jalan yang lurus."},{"id":"88","ar":"ذٰلِكَ هُدَى اللّٰهِ يَهْدِيْ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۗوَلَوْ اَشْرَكُوْا لَحَبِطَ عَنْهُمْ مَّا كَانُوْا يَعْمَلُوْنَ ","bc":"żālika hudallāhi yahdī bihī may yasyā`u min 'ibādih, walau asyrakụ laḥabiṭa 'an-hum mā kānụ ya'malụn","at":"Itulah petunjuk Allah, dengan itu Dia memberi petunjuk kepada siapa saja di antara hamba-hamba-Nya yang Dia kehendaki. Sekiranya mereka mempersekutukan Allah, pasti lenyaplah amalan yang telah mereka kerjakan."},{"id":"89","ar":"اُولٰۤىِٕكَ الَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ وَالْحُكْمَ وَالنُّبُوَّةَ ۚفَاِنْ يَّكْفُرْ بِهَا هٰٓؤُلَاۤءِ فَقَدْ وَكَّلْنَا بِهَا قَوْمًا لَّيْسُوْا بِهَا بِكٰفِرِيْنَ ","bc":"ulā`ikallażīna ātaināhumul-kitāba wal-ḥukma wan-nubuwwah, fa iy yakfur bihā hā`ulā`i fa qad wakkalnā bihā qaumal laisụ bihā bikāfirīn","at":"Mereka itulah orang-orang yang telah Kami berikan kitab, hikmah dan kenabian. Jika orang-orang (Quraisy) itu mengingkarinya, maka Kami akan menyerahkannya kepada kaum yang tidak mengingkarinya."},{"id":"90","ar":"اُولٰۤىِٕكَ الَّذِيْنَ هَدَى اللّٰهُ فَبِهُدٰىهُمُ اقْتَدِهْۗ قُلْ لَّآ اَسْـَٔلُكُمْ عَلَيْهِ اَجْرًاۗ اِنْ هُوَ اِلَّا ذِكْرٰى لِلْعٰلَمِيْنَ ","bc":"ulā`ikallażīna hadallāhu fa bihudāhumuqtadih, qul lā as`alukum 'alaihi ajrā, in huwa illā żikrā lil-'ālamīn","at":"Mereka itulah (para nabi) yang telah diberi petunjuk oleh Allah, maka ikutilah petunjuk mereka. Katakanlah (Muhammad), “Aku tidak meminta imbalan kepadamu dalam menyampaikan (Al-Qur'an).” Al-Qur'an itu tidak lain hanyalah peringatan untuk (segala umat) seluruh alam. "},{"id":"91","ar":"وَمَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهٖٓ اِذْ قَالُوْا مَآ اَنْزَلَ اللّٰهُ عَلٰى بَشَرٍ مِّنْ شَيْءٍۗ قُلْ مَنْ اَنْزَلَ الْكِتٰبَ الَّذِيْ جَاۤءَ بِهٖ مُوْسٰى نُوْرًا وَّهُدًى لِّلنَّاسِ تَجْعَلُوْنَهٗ قَرَاطِيْسَ تُبْدُوْنَهَا وَتُخْفُوْنَ كَثِيْرًاۚ وَعُلِّمْتُمْ مَّا لَمْ تَعْلَمُوْٓا اَنْتُمْ وَلَآ اٰبَاۤؤُكُمْ ۗقُلِ اللّٰهُ ۙثُمَّ ذَرْهُمْ فِيْ خَوْضِهِمْ يَلْعَبُوْنَ ","bc":"wa mā qadarullāha ḥaqqa qadrihī iż qālụ mā anzalallāhu 'alā basyarim min syaī`, qul man anzalal-kitāballażī jā`a bihī mụsā nụraw wa hudal lin-nāsi taj'alụnahụ qarāṭīsa tubdụnahā wa tukhfụna kaṡīrā, wa 'ullimtum mā lam ta'lamū antum wa lā ābā`ukum, qulillāhu ṡumma żar-hum fī khauḍihim yal'abụn","at":"Mereka tidak mengagungkan Allah sebagaimana mestinya ketika mereka berkata, “Allah tidak menurunkan sesuatu pun kepada manusia.” Katakanlah (Muhammad), “Siapakah yang menurunkan Kitab (Taurat) yang dibawa Musa sebagai cahaya dan petunjuk bagi manusia, kamu jadikan Kitab itu lembaran-lembaran kertas yang bercerai-berai, kamu memperlihatkan (sebagiannya) dan banyak yang kamu sembunyikan, padahal telah diajarkan kepadamu apa yang tidak diketahui, baik olehmu maupun oleh nenek moyangmu.” Katakanlah, “Allah-lah (yang menurunkannya),” kemudian (setelah itu), biarkanlah mereka bermain-main dalam kesesatannya."},{"id":"92","ar":"وَهٰذَا كِتٰبٌ اَنْزَلْنٰهُ مُبٰرَكٌ مُّصَدِّقُ الَّذِيْ بَيْنَ يَدَيْهِ وَلِتُنْذِرَ اُمَّ الْقُرٰى وَمَنْ حَوْلَهَاۗ وَالَّذِيْنَ يُؤْمِنُوْنَ بِالْاٰخِرَةِ يُؤْمِنُوْنَ بِهٖ وَهُمْ عَلٰى صَلَاتِهِمْ يُحٰفِظُوْنَ ","bc":"wa hāżā kitābun anzalnāhu mubārakum muṣaddiqullażī baina yadaihi wa litunżira ummal-qurā wa man ḥaulahā, wallażīna yu`minụna bil-ākhirati yu`minụna bihī wa hum 'alā ṣalātihim yuḥāfiẓụn","at":"Dan ini (Al-Qur'an), Kitab yang telah Kami turunkan dengan penuh berkah; membenarkan kitab-kitab yang (diturunkan) sebelumnya dan agar engkau memberi peringatan kepada (penduduk) Ummul Qura (Mekah) dan orang-orang yang ada di sekitarnya. Orang-orang yang beriman kepada (kehidupan) akhirat tentu beriman kepadanya (Al-Qur'an), dan mereka selalu memelihara salatnya."},{"id":"93","ar":"وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ قَالَ اُوْحِيَ اِلَيَّ وَلَمْ يُوْحَ اِلَيْهِ شَيْءٌ وَّمَنْ قَالَ سَاُنْزِلُ مِثْلَ مَآ اَنْزَلَ اللّٰهُ ۗوَلَوْ تَرٰٓى اِذِ الظّٰلِمُوْنَ فِيْ غَمَرٰتِ الْمَوْتِ وَالْمَلٰۤىِٕكَةُ بَاسِطُوْٓا اَيْدِيْهِمْۚ اَخْرِجُوْٓا اَنْفُسَكُمْۗ اَلْيَوْمَ تُجْزَوْنَ عَذَابَ الْهُوْنِ بِمَا كُنْتُمْ تَقُوْلُوْنَ عَلَى اللّٰهِ غَيْرَ الْحَقِّ وَكُنْتُمْ عَنْ اٰيٰتِهٖ تَسْتَكْبِرُوْنَ","bc":"wa man aẓlamu mim maniftarā 'alallāhi każiban au qāla ụḥiya ilayya wa lam yụḥa ilaihi syai`uw wa mang qāla sa`unzilu miṡla mā anzalallāh, walau tarā iżiẓ-ẓālimụna fī gamarātil-mauti wal-malā`ikatu bāsiṭū aidīhim, akhrijū anfusakum, al-yauma tujzauna 'ażābal-hụni bimā kuntum taqụlụna 'alallāhi gairal-ḥaqqi wa kuntum 'an āyātihī tastakbirụn","at":"Siapakah yang lebih zalim daripada orang-orang yang mengada-adakan dusta terhadap Allah atau yang berkata, “Telah diwahyukan kepadaku,” padahal tidak diwahyukan sesuatu pun kepadanya, dan orang yang berkata, “Aku akan menurunkan seperti apa yang diturunkan Allah.” (Alangkah ngerinya) sekiranya engkau melihat pada waktu orang-orang zalim (berada) dalam kesakitan sakratul maut, sedang para malaikat memukul dengan tangannya, (sambil berkata), “Keluarkanlah nyawamu.” Pada hari ini kamu akan dibalas dengan azab yang sangat menghinakan, karena kamu mengatakan terhadap Allah (perkataan) yang tidak benar dan (karena) kamu menyombongkan diri terhadap ayat-ayat-Nya."},{"id":"94","ar":"وَلَقَدْ جِئْتُمُوْنَا فُرَادٰى كَمَا خَلَقْنٰكُمْ اَوَّلَ مَرَّةٍ وَّتَرَكْتُمْ مَّا خَوَّلْنٰكُمْ وَرَاۤءَ ظُهُوْرِكُمْۚ وَمَا نَرٰى مَعَكُمْ شُفَعَاۤءَكُمُ الَّذِيْنَ زَعَمْتُمْ اَنَّهُمْ فِيْكُمْ شُرَكٰۤؤُا ۗ لَقَدْ تَّقَطَّعَ بَيْنَكُمْ وَضَلَّ عَنْكُمْ مَّا كُنْتُمْ تَزْعُمُوْنَ","bc":"wa laqad ji`tumụnā furādā kamā khalaqnākum awwala marratiw wa taraktum mā khawwalnākum warā`a ẓuhụrikum, wa mā narā ma'akum syufa'ā`akumullażīna za'amtum annahum fīkum syurakā`, laqat taqaṭṭa'a bainakum wa ḍalla 'angkum mā kuntum taz'umụn","at":"Dan kamu benar-benar datang sendiri-sendiri kepada Kami sebagaimana Kami ciptakan kamu pada mulanya, dan apa yang telah Kami karuniakan kepadamu, kamu tinggalkan di belakangmu (di dunia). Kami tidak melihat pemberi syafaat (pertolongan) besertamu yang kamu anggap bahwa mereka itu sekutu-sekutu (bagi Allah). Sungguh, telah terputuslah (semua pertalian) antara kamu dan telah lenyap dari kamu apa yang dahulu kamu sangka (sebagai sekutu Allah)."},{"id":"95","ar":"۞ اِنَّ اللّٰهَ فَالِقُ الْحَبِّ وَالنَّوٰىۗ يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَمُخْرِجُ الْمَيِّتِ مِنَ الْحَيِّ ۗذٰلِكُمُ اللّٰهُ فَاَنّٰى تُؤْفَكُوْنَ ","bc":"innallāha fāliqul-ḥabbi wan-nawā, yukhrijul-ḥayya minal-mayyiti wa mukhrijul-mayyiti minal-ḥayy, żālikumullāhu fa annā tu`fakụn","at":"Sungguh, Allah yang menumbuhkan butir (padi-padian) dan biji (kurma). Dia mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup. Itulah (kekuasaan) Allah, maka mengapa kamu masih berpaling?"},{"id":"96","ar":"فَالِقُ الْاِصْبَاحِۚ وَجَعَلَ الَّيْلَ سَكَنًا وَّالشَّمْسَ وَالْقَمَرَ حُسْبَانًا ۗذٰلِكَ تَقْدِيْرُ الْعَزِيْزِ الْعَلِيْمِ ","bc":"fāliqul-iṣbāḥ, wa ja'alal-laila sakanaw wasy-syamsa wal-qamara ḥusbānā, żālika taqdīrul-'azīzil-'alīm","at":"Dia menyingsingkan pagi dan menjadikan malam untuk beristirahat, dan (menjadikan) matahari dan bulan untuk perhitungan. Itulah ketetapan Allah Yang Mahaperkasa, Maha Mengetahui."},{"id":"97","ar":"وَهُوَ الَّذِيْ جَعَلَ لَكُمُ النُّجُوْمَ لِتَهْتَدُوْا بِهَا فِيْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِۗ قَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ","bc":"wa huwallażī ja'ala lakumun-nujụma litahtadụ bihā fī ẓulumātil-barri wal-baḥr, qad faṣṣalnal-āyāti liqaumiy ya'lamụn","at":"Dan Dialah yang menjadikan bintang-bintang bagimu, agar kamu menjadikannya petunjuk dalam kegelapan di darat dan di laut. Kami telah menjelaskan tanda-tanda (kekuasaan Kami) kepada orang-orang yang mengetahui."},{"id":"98","ar":"وَهُوَ الَّذِيْٓ اَنْشَاَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ فَمُسْتَقَرٌّ وَّمُسْتَوْدَعٌ ۗقَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّفْقَهُوْنَ ","bc":"wa huwallażī ansya`akum min nafsiw wāḥidatin fa mustaqarruw wa mustauda', qad faṣṣalnal-āyāti liqaumiy yafqahụn","at":"Dan Dialah yang menciptakan kamu dari diri yang satu (Adam), maka (bagimu) ada tempat menetap dan tempat simpanan. Sesungguhnya telah Kami jelaskan tanda-tanda (kebesaran Kami) kepada orang-orang yang mengetahui."},{"id":"99","ar":"وَهُوَ الَّذِيْٓ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۚ فَاَخْرَجْنَا بِهٖ نَبَاتَ كُلِّ شَيْءٍ فَاَخْرَجْنَا مِنْهُ خَضِرًا نُّخْرِجُ مِنْهُ حَبًّا مُّتَرَاكِبًاۚ وَمِنَ النَّخْلِ مِنْ طَلْعِهَا قِنْوَانٌ دَانِيَةٌ وَّجَنّٰتٍ مِّنْ اَعْنَابٍ وَّالزَّيْتُوْنَ وَالرُّمَّانَ مُشْتَبِهًا وَّغَيْرَ مُتَشَابِهٍۗ اُنْظُرُوْٓا اِلٰى ثَمَرِهٖٓ اِذَٓا اَثْمَرَ وَيَنْعِهٖ ۗاِنَّ فِيْ ذٰلِكُمْ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ","bc":"wa huwallażī anzala minas-samā`i mā`ā, fa akhrajnā bihī nabāta kulli syai`in fa akhrajnā min-hu khaḍiran nukhriju min-hu ḥabbam mutarākibā, wa minan-nakhli min ṭal'ihā qinwānun dāniyatuw wa jannātim min a'nābiw waz-zaitụna war-rummāna musytabihaw wa gaira mutasyābih, unẓurū ilā ṡamarihī iżā aṡmara wa yan'ih, inna fī żālikum la`āyātil liqaumiy yu`minụn","at":"Dan Dialah yang menurunkan air dari langit, lalu Kami tumbuhkan dengan air itu segala macam tumbuh-tumbuhan, maka Kami keluarkan dari tumbuh-tumbuhan itu tanaman yang menghijau, Kami keluarkan dari tanaman yang menghijau itu butir yang banyak; dan dari mayang kurma, mengurai tangkai-tangkai yang menjulai, dan kebun-kebun anggur, dan (Kami keluarkan pula) zaitun dan delima yang serupa dan yang tidak serupa. Perhatikanlah buahnya pada waktu berbuah, dan menjadi masak. Sungguh, pada yang demikian itu ada tanda-tanda (kekuasaan Allah) bagi orang-orang yang beriman."},{"id":"100","ar":"وَجَعَلُوْا لِلّٰهِ شُرَكَاۤءَ الْجِنَّ وَخَلَقَهُمْ وَخَرَقُوْا لَهٗ بَنِيْنَ وَبَنٰتٍۢ بِغَيْرِ عِلْمٍۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يَصِفُوْنَ ","bc":"wa ja'alụ lillāhi syurakā`al-jinna wa khalaqahum wa kharaqụ lahụ banīna wa banātim bigairi 'ilm, sub-ḥānahụ wa ta'ālā 'ammā yaṣifụn","at":"Dan mereka (orang-orang musyrik) menjadikan jin sekutu-sekutu Allah, padahal Dia yang menciptakannya (jin-jin itu), dan mereka berbohong (dengan mengatakan), “Allah mempunyai anak laki-laki dan anak perempuan,” tanpa (dasar) pengetahuan. Mahasuci Allah dan Mahatinggi dari sifat-sifat yang mereka gambarkan."},{"id":"101","ar":"بَدِيْعُ السَّمٰوٰتِ وَالْاَرْضِۗ اَنّٰى يَكُوْنُ لَهٗ وَلَدٌ وَّلَمْ تَكُنْ لَّهٗ صَاحِبَةٌ ۗوَخَلَقَ كُلَّ شَيْءٍۚ وَهُوَ بِكُلِّ شَيْءٍ عَلِيْمٌ ","bc":"badī'us-samāwāti wal-arḍ, annā yakụnu lahụ waladuw wa lam takul lahụ ṣāḥibah, wa khalaqa kulla syaī`, wa huwa bikulli syai`in 'alīm","at":"Dia (Allah) pencipta langit dan bumi. Bagaimana (mungkin) Dia mempunyai anak padahal Dia tidak mempunyai istri. Dia menciptakan segala sesuatu; dan Dia mengetahui segala sesuatu."},{"id":"102","ar":"ذٰلِكُمُ اللّٰهُ رَبُّكُمْۚ لَآ اِلٰهَ اِلَّا هُوَۚ خَالِقُ كُلِّ شَيْءٍ فَاعْبُدُوْهُ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ وَّكِيْلٌ ","bc":"żālikumullāhu rabbukum, lā ilāha illā huw, khāliqu kulli syai`in fa'budụh, wa huwa 'alā kulli syai`iw wakīl","at":"Itulah Allah, Tuhan kamu; tidak ada tuhan selain Dia; pencipta segala sesuatu, maka sembahlah Dia; Dialah pemelihara segala sesuatu."},{"id":"103","ar":"لَا تُدْرِكُهُ الْاَبْصَارُ وَهُوَ يُدْرِكُ الْاَبْصَارَۚ وَهُوَ اللَّطِيْفُ الْخَبِيْرُ ","bc":"lā tudrikuhul-abṣāru wa huwa yudrikul-abṣār, wa huwal-laṭīful-khabīr","at":"Dia tidak dapat dicapai oleh penglihatan mata, sedang Dia dapat melihat segala penglihatan itu, dan Dialah Yang Mahahalus, Mahateliti."},{"id":"104","ar":"قَدْ جَاۤءَكُمْ بَصَاۤىِٕرُ مِنْ رَّبِّكُمْۚ فَمَنْ اَبْصَرَ فَلِنَفْسِهٖۚ وَمَنْ عَمِيَ فَعَلَيْهَاۗ وَمَآ اَنَا۠ عَلَيْكُمْ بِحَفِيْظٍ ","bc":"qad jā`akum baṣā`iru mir rabbikum, fa man abṣara fa linafsih, wa man 'amiya fa 'alaihā, wa mā ana 'alaikum biḥafīẓ","at":"Sungguh, bukti-bukti yang nyata telah datang dari Tuhanmu. Barangsiapa melihat (kebenaran itu), maka (manfaatnya) bagi dirinya sendiri; dan barangsiapa buta (tidak melihat kebenaran itu), maka dialah yang rugi. Dan aku (Muhammad) bukanlah penjaga-(mu)."},{"id":"105","ar":"وَكَذٰلِكَ نُصَرِّفُ الْاٰيٰتِ وَلِيَقُوْلُوْا دَرَسْتَ وَلِنُبَيِّنَهٗ لِقَوْمٍ يَّعْلَمُوْنَ ","bc":"wa każālika nuṣarriful-āyāti wa liyaqụlụ darasta wa linubayyinahụ liqaumiy ya'lamụn","at":"Dan demikianlah Kami menjelaskan berulang-ulang ayat-ayat Kami agar orang-orang musyrik mengatakan, “Engkau telah mempelajari ayat-ayat itu (dari Ahli Kitab),” dan agar Kami menjelaskan Al-Qur'an itu kepada orang-orang yang mengetahui."},{"id":"106","ar":"اِتَّبِعْ مَآ اُوْحِيَ اِلَيْكَ مِنْ رَّبِّكَۚ لَآ اِلٰهَ اِلَّا هُوَۚ وَاَعْرِضْ عَنِ الْمُشْرِكِيْنَ ","bc":"ittabi' mā ụḥiya ilaika mir rabbik, lā ilāha illā huw, wa a'riḍ 'anil-musyrikīn","at":"Ikutilah apa yang telah diwahyukan Tuhanmu kepadamu (Muhammad); tidak ada tuhan selain Dia; dan berpalinglah dari orang-orang musyrik."},{"id":"107","ar":"وَلَوْ شَاۤءَ اللّٰهُ مَآ اَشْرَكُوْاۗ وَمَا جَعَلْنٰكَ عَلَيْهِمْ حَفِيْظًاۚ وَمَآ اَنْتَ عَلَيْهِمْ بِوَكِيْلٍ ","bc":"walau syā`allāhu mā asyrakụ, wa mā ja'alnāka 'alaihim ḥafīẓā, wa mā anta 'alaihim biwakīl","at":"Dan sekiranya Allah menghendaki, niscaya mereka tidak mempersekutukan(-Nya). Dan Kami tidak menjadikan engkau penjaga mereka; dan engkau bukan pula pemelihara mereka."},{"id":"108","ar":"وَلَا تَسُبُّوا الَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ فَيَسُبُّوا اللّٰهَ عَدْوًاۢ بِغَيْرِ عِلْمٍۗ كَذٰلِكَ زَيَّنَّا لِكُلِّ اُمَّةٍ عَمَلَهُمْۖ ثُمَّ اِلٰى رَبِّهِمْ مَّرْجِعُهُمْ فَيُنَبِّئُهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ ","bc":"wa lā tasubbullażīna yad'ụna min dụnillāhi fa yasubbullāha 'adwam bigairi 'ilm, każālika zayyannā likulli ummatin 'amalahum ṡumma ilā rabbihim marji'uhum fa yunabbi`uhum bimā kānụ ya'malụn","at":"Dan janganlah kamu memaki sesembahan yang mereka sembah selain Allah, karena mereka nanti akan memaki Allah dengan melampaui batas tanpa dasar pengetahuan. Demikianlah, Kami jadikan setiap umat menganggap baik pekerjaan mereka. Kemudian kepada Tuhan tempat kembali mereka, lalu Dia akan memberitahukan kepada mereka apa yang telah mereka kerjakan."},{"id":"109","ar":"وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ جَاۤءَتْهُمْ اٰيَةٌ لَّيُؤْمِنُنَّ بِهَاۗ قُلْ اِنَّمَا الْاٰيٰتُ عِنْدَ اللّٰهِ وَمَا يُشْعِرُكُمْ اَنَّهَآ اِذَا جَاۤءَتْ لَا يُؤْمِنُوْنَ ","bc":"wa aqsamụ billāhi jahda aimānihim la`in jā`at-hum āyatul layu`minunna bihā, qul innamal-āyātu 'indallāhi wa mā yusy'irukum annahā iżā jā`at lā yu`minụn","at":"Dan mereka bersumpah dengan nama Allah dengan segala kesungguhan, bahwa jika datang suatu mukjizat kepada mereka, pastilah mereka akan beriman kepadanya. Katakanlah, “Mukjizat-mukjizat itu hanya ada pada sisi Allah.” Dan tahukah kamu, bahwa apabila mukjizat (ayat-ayat) datang, mereka tidak juga akan beriman."},{"id":"110","ar":"وَنُقَلِّبُ اَفْـِٕدَتَهُمْ وَاَبْصَارَهُمْ كَمَا لَمْ يُؤْمِنُوْا بِهٖٓ اَوَّلَ مَرَّةٍ وَّنَذَرُهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ ۔ ","bc":"wa nuqallibu af`idatahum wa abṣārahum kamā lam yu`minụ bihī awwala marratiw wa nażaruhum fī ṭugyānihim ya'mahụn","at":"Dan (begitu pula) Kami memalingkan hati dan penglihatan mereka seperti pertama kali mereka tidak beriman kepadanya (Al-Qur'an), dan Kami biarkan mereka bingung dalam kesesatan."},{"id":"111","ar":"۞ وَلَوْ اَنَّنَا نَزَّلْنَآ اِلَيْهِمُ الْمَلٰۤىِٕكَةَ وَكَلَّمَهُمُ الْمَوْتٰى وَحَشَرْنَا عَلَيْهِمْ كُلَّ شَيْءٍ قُبُلًا مَّا كَانُوْا لِيُؤْمِنُوْٓا اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ وَلٰكِنَّ اَكْثَرَهُمْ يَجْهَلُوْنَ ","bc":"walau annanā nazzalnā ilaihimul-malā`ikata wa kallamahumul-mautā wa ḥasyarnā 'alaihim kulla syai`ing qubulam mā kānụ liyu`minū illā ay yasyā`allāhu wa lākinna akṡarahum yaj-halụn","at":"Dan sekalipun Kami benar-benar menurunkan malaikat kepada mereka, dan orang yang telah mati berbicara dengan mereka dan Kami kumpulkan (pula) di hadapan mereka segala sesuatu (yang mereka inginkan), mereka tidak juga akan beriman, kecuali jika Allah menghendaki. Tapi kebanyakan mereka tidak mengetahui (arti kebenaran)."},{"id":"112","ar":"وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا شَيٰطِيْنَ الْاِنْسِ وَالْجِنِّ يُوْحِيْ بَعْضُهُمْ اِلٰى بَعْضٍ زُخْرُفَ الْقَوْلِ غُرُوْرًا ۗوَلَوْ شَاۤءَ رَبُّكَ مَا فَعَلُوْهُ فَذَرْهُمْ وَمَا يَفْتَرُوْنَ ","bc":"wa każālika ja'alnā likulli nabiyyin 'aduwwan syayāṭīnal-insi wal-jinni yụḥī ba'ḍuhum ilā ba'ḍin zukhrufal-qauli gurụrā, walau syā`a rabbuka mā fa'alụhu fa żar-hum wa mā yaftarụn","at":"Dan demikianlah untuk setiap nabi Kami menjadikan musuh yang terdiri dari setan-setan manusia dan jin, sebagian mereka membisikkan kepada sebagian yang lain perkataan yang indah sebagai tipuan. Dan kalau Tuhanmu menghendaki, niscaya mereka tidak akan melakukannya, maka biarkanlah mereka bersama apa (kebohongan) yang mereka ada-adakan."},{"id":"113","ar":"وَلِتَصْغٰٓى اِلَيْهِ اَفْـِٕدَةُ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ وَلِيَرْضَوْهُ وَلِيَقْتَرِفُوْا مَا هُمْ مُّقْتَرِفُوْنَ ","bc":"wa litaṣgā ilaihi af`idatullażīna lā yu`minụna bil-ākhirati wa liyarḍauhu wa liyaqtarifụ mā hum muqtarifụn","at":"Dan agar hati kecil orang-orang yang tidak beriman kepada akhirat, tertarik kepada bisikan itu, dan menyenanginya, dan agar mereka melakukan apa yang biasa mereka lakukan."},{"id":"114","ar":"اَفَغَيْرَ اللّٰهِ اَبْتَغِيْ حَكَمًا وَّهُوَ الَّذِيْٓ اَنْزَلَ اِلَيْكُمُ الْكِتٰبَ مُفَصَّلًا ۗوَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْلَمُوْنَ اَنَّهٗ مُنَزَّلٌ مِّنْ رَّبِّكَ بِالْحَقِّ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَ ","bc":"a fagairallāhi abtagī ḥakamaw wa huwallażī anzala ilaikumul-kitāba mufaṣṣalā, wallażīna ātaināhumul-kitāba ya'lamụna annahụ munazzalum mir rabbika bil-ḥaqqi fa lā takụnanna minal-mumtarīn","at":"Pantaskah aku mencari hakim selain Allah, padahal Dialah yang menurunkan Kitab (Al-Qur'an) kepadamu secara rinci? Orang-orang yang telah Kami beri kitab mengetahui benar bahwa (Al-Qur'an) itu diturunkan dari Tuhanmu dengan benar. Maka janganlah kamu termasuk orang-orang yang ragu."},{"id":"115","ar":"وَتَمَّتْ كَلِمَتُ رَبِّكَ صِدْقًا وَّعَدْلًاۗ لَا مُبَدِّلَ لِكَلِمٰتِهٖ ۚوَهُوَ السَّمِيْعُ الْعَلِيْمُ","bc":"wa tammat kalimatu rabbika ṣidqaw wa 'adlā, lā mubaddila likalimātih, wa huwas-samī'ul-'alīm","at":"Dan telah sempurna firman Tuhanmu (Al-Qur'an) dengan benar dan adil. Tidak ada yang dapat mengubah firman-Nya. Dan Dia Maha Mendengar, Maha Mengetahui."},{"id":"116","ar":"وَاِنْ تُطِعْ اَكْثَرَ مَنْ فِى الْاَرْضِ يُضِلُّوْكَ عَنْ سَبِيْلِ اللّٰهِ ۗاِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُوْنَ ","bc":"wa in tuṭi' akṡara man fil-arḍi yuḍillụka 'an sabīlillāh, iy yattabi'ụna illaẓ-ẓanna wa in hum illā yakhruṣụn","at":"Dan jika kamu mengikuti kebanyakan orang di bumi ini, niscaya mereka akan menyesatkanmu dari jalan Allah. Yang mereka ikuti hanya persangkaan belaka dan mereka hanyalah membuat kebohongan."},{"id":"117","ar":"اِنَّ رَبَّكَ هُوَ اَعْلَمُ مَنْ يَّضِلُّ عَنْ سَبِيْلِهٖۚ وَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ ","bc":"inna rabbaka huwa a'lamu may yaḍillu 'an sabīlih, wa huwa a'lamu bil-muhtadīn","at":"Sesungguhnya Tuhanmu, Dialah yang lebih mengetahui siapa yang tersesat dari jalan-Nya, dan Dia lebih mengetahui orang-orang yang mendapat petunjuk."},{"id":"118","ar":"فَكُلُوْا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ اِنْ كُنْتُمْ بِاٰيٰتِهٖ مُؤْمِنِيْنَ ","bc":"fa kulụ mimmā żukirasmullāhi 'alaihi ing kuntum bi`āyātihī mu`minīn","at":"Maka makanlah dari apa (daging hewan) yang (ketika disembelih) disebut nama Allah, jika kamu beriman kepada ayat-ayat-Nya."},{"id":"119","ar":"وَمَا لَكُمْ اَلَّا تَأْكُلُوْا مِمَّا ذُكِرَ اسْمُ اللّٰهِ عَلَيْهِ وَقَدْ فَصَّلَ لَكُمْ مَّا حَرَّمَ عَلَيْكُمْ اِلَّا مَا اضْطُرِرْتُمْ اِلَيْهِ ۗوَاِنَّ كَثِيرًا لَّيُضِلُّوْنَ بِاَهْوَاۤىِٕهِمْ بِغَيْرِ عِلْمٍ ۗاِنَّ رَبَّكَ هُوَ اَعْلَمُ بِالْمُعْتَدِيْنَ ","bc":"wa mā lakum allā ta`kulụ mimmā żukirasmullāhi 'alaihi wa qad faṣṣala lakum mā ḥarrama 'alaikum illā maḍṭurirtum ilaīh, wa inna kaṡīral layuḍillụna bi`ahwā`ihim bigairi 'ilm, inna rabbaka huwa a'lamu bil-mu'tadīn","at":"Dan mengapa kamu tidak mau memakan dari apa (daging hewan) yang (ketika disembelih) disebut nama Allah, padahal Allah telah menjelaskan kepadamu apa yang diharamkan-Nya kepadamu, kecuali jika kamu dalam keadaan terpaksa. Dan sungguh, banyak yang menyesatkan orang dengan keinginannya tanpa dasar pengetahuan. Tuhanmu lebih mengetahui orang-orang yang melampaui batas."},{"id":"120","ar":"وَذَرُوْا ظَاهِرَ الْاِثْمِ وَبَاطِنَهٗ ۗاِنَّ الَّذِيْنَ يَكْسِبُوْنَ الْاِثْمَ سَيُجْزَوْنَ بِمَا كَانُوْا يَقْتَرِفُوْنَ ","bc":"wa żarụ ẓāhiral-iṡmi wa bāṭinah, innallażīna yaksibụnal-iṡma sayujzauna bimā kānụ yaqtarifụn","at":"Dan tinggalkanlah dosa yang terlihat ataupun yang tersembunyi. Sungguh, orang-orang yang mengerjakan (perbuatan) dosa kelak akan diberi balasan sesuai dengan apa yang mereka kerjakan."},{"id":"121","ar":"وَلَا تَأْكُلُوْا مِمَّا لَمْ يُذْكَرِ اسْمُ اللّٰهِ عَلَيْهِ وَاِنَّهٗ لَفِسْقٌۗ وَاِنَّ الشَّيٰطِيْنَ لَيُوْحُوْنَ اِلٰٓى اَوْلِيَاۤىِٕهِمْ لِيُجَادِلُوْكُمْ ۚوَاِنْ اَطَعْتُمُوْهُمْ اِنَّكُمْ لَمُشْرِكُوْنَ ","bc":"wa lā ta`kulụ mimmā lam yużkarismullāhi 'alaihi wa innahụ lafisq, wa innasy-syayāṭīna layụḥụna ilā auliyā`ihim liyujādilụkum, wa in aṭa'tumụhum innakum lamusyrikụn","at":"Dan janganlah kamu memakan dari apa (daging hewan) yang (ketika disembelih) tidak disebut nama Allah, perbuatan itu benar-benar suatu kefasikan. Sesungguhnya setan-setan akan membisikkan kepada kawan-kawannya agar mereka membantah kamu. Dan jika kamu menuruti mereka, tentu kamu telah menjadi orang musyrik."},{"id":"122","ar":"اَوَمَنْ كَانَ مَيْتًا فَاَحْيَيْنٰهُ وَجَعَلْنَا لَهٗ نُوْرًا يَّمْشِيْ بِهٖ فِى النَّاسِ كَمَنْ مَّثَلُهٗ فِى الظُّلُمٰتِ لَيْسَ بِخَارِجٍ مِّنْهَاۗ كَذٰلِكَ زُيِّنَ لِلْكٰفِرِيْنَ مَا كَانُوْا يَعْمَلُوْنَ ","bc":"a wa mang kāna maitan fa aḥyaināhu wa ja'alnā lahụ nụray yamsyī bihī fin-nāsi kamam maṡaluhụ fiẓ-ẓulumāti laisa bikhārijim min-hā, każālika zuyyina lil-kāfirīna mā kānụ ya'malụn","at":"Dan apakah orang yang sudah mati lalu Kami hidupkan dan Kami beri dia cahaya yang membuatnya dapat berjalan di tengah-tengah orang banyak, sama dengan orang yang berada dalam kegelapan, sehingga dia tidak dapat keluar dari sana? Demikianlah dijadikan terasa indah bagi orang-orang kafir terhadap apa yang mereka kerjakan."},{"id":"123","ar":"وَكَذٰلِكَ جَعَلْنَا فِيْ كُلِّ قَرْيَةٍ اَكٰبِرَ مُجْرِمِيْهَا لِيَمْكُرُوْا فِيْهَاۗ وَمَا يَمْكُرُوْنَ اِلَّا بِاَنْفُسِهِمْ وَمَا يَشْعُرُوْنَ ","bc":"wa każālika ja'alnā fī kulli qaryatin akābira mujrimīhā liyamkurụ fīhā, wa mā yamkurụna illā bi`anfusihim wa mā yasy'urụn","at":"Dan demikianlah pada setiap negeri Kami jadikan pembesar-pembesar yang jahat agar melakukan tipu daya di negeri itu. Tapi mereka hanya menipu diri sendiri tanpa menyadarinya."},{"id":"124","ar":"وَاِذَا جَاۤءَتْهُمْ اٰيَةٌ قَالُوْا لَنْ نُّؤْمِنَ حَتّٰى نُؤْتٰى مِثْلَ مَآ اُوْتِيَ رُسُلُ اللّٰهِ ۘ اَللّٰهُ اَعْلَمُ حَيْثُ يَجْعَلُ رِسٰلَتَهٗۗ سَيُصِيْبُ الَّذِيْنَ اَجْرَمُوْا صَغَارٌ عِنْدَ اللّٰهِ وَعَذَابٌ شَدِيْدٌۢ بِمَا كَانُوْا يَمْكُرُوْنَ","bc":"wa iżā jā`at-hum āyatung qālụ lan nu`mina ḥattā nu`tā miṡla mā ụtiya rusulullāh, allāhu a'lamu ḥaiṡu yaj'alu risālatah, sayuṣībullażīna ajramụ ṣagārun 'indallāhi wa 'ażābun syadīdum bimā kānụ yamkurụn","at":"Dan apabila datang suatu ayat kepada mereka, mereka berkata, “Kami tidak akan percaya (beriman) sebelum diberikan kepada kami seperti apa yang diberikan kepada rasul-rasul Allah.” Allah lebih mengetahui di mana Dia menempatkan tugas kerasulan-Nya. Orang-orang yang berdosa, nanti akan ditimpa kehinaan di sisi Allah dan azab yang keras karena tipu daya yang mereka lakukan."},{"id":"125","ar":"فَمَنْ يُّرِدِ اللّٰهُ اَنْ يَّهْدِيَهٗ يَشْرَحْ صَدْرَهٗ لِلْاِسْلَامِۚ وَمَنْ يُّرِدْ اَنْ يُّضِلَّهٗ يَجْعَلْ صَدْرَهٗ ضَيِّقًا حَرَجًا كَاَنَّمَا يَصَّعَّدُ فِى السَّمَاۤءِۗ كَذٰلِكَ يَجْعَلُ اللّٰهُ الرِّجْسَ عَلَى الَّذِيْنَ لَا يُؤْمِنُوْنَ ","bc":"fa may yuridillāhu ay yahdiyahụ yasyraḥ ṣadrahụ lil-islām, wa may yurid ay yuḍillahụ yaj'al ṣadrahụ ḍayyiqan ḥarajang ka`annamā yaṣṣa''adu fis-samā`, każālika yaj'alullāhur-rijsa 'alallażīna lā yu`minụn","at":"Barangsiapa dikehendaki Allah akan mendapat hidayah (petunjuk), Dia akan membukakan dadanya untuk (menerima) Islam. Dan barangsiapa dikehendaki-Nya menjadi sesat, Dia jadikan dadanya sempit dan sesak, seakan-akan dia (sedang) mendaki ke langit. Demikianlah Allah menimpakan siksa kepada orang-orang yang tidak beriman."},{"id":"126","ar":"وَهٰذَا صِرَاطُ رَبِّكَ مُسْتَقِيْمًاۗ قَدْ فَصَّلْنَا الْاٰيٰتِ لِقَوْمٍ يَّذَّكَّرُوْنَ ","bc":"wa hāżā ṣirāṭu rabbika mustaqīmā, qad faṣṣalnal-āyāti liqaumiy yażżakkarụn","at":"Dan inilah jalan Tuhanmu yang lurus. Kami telah menjelaskan ayat-ayat (Kami) kepada orang-orang yang menerima peringatan."},{"id":"127","ar":"۞ لَهُمْ دَارُ السَّلٰمِ عِنْدَ رَبِّهِمْ وَهُوَ وَلِيُّهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ ","bc":"lahum dārus-salāmi 'inda rabbihim wa huwa waliyyuhum bimā kānụ ya'malụn","at":"Bagi mereka (disediakan) tempat yang damai (surga) di sisi Tuhannya. Dan Dialah pelindung mereka karena amal kebajikan yang mereka kerjakan."},{"id":"128","ar":"وَيَوْمَ يَحْشُرُهُمْ جَمِيْعًاۚ يٰمَعْشَرَ الْجِنِّ قَدِ اسْتَكْثَرْتُمْ مِّنَ الْاِنْسِ ۚوَقَالَ اَوْلِيَاۤؤُهُمْ مِّنَ الْاِنْسِ رَبَّنَا اسْتَمْتَعَ بَعْضُنَا بِبَعْضٍ وَّبَلَغْنَآ اَجَلَنَا الَّذِيْٓ اَجَّلْتَ لَنَا ۗقَالَ النَّارُ مَثْوٰىكُمْ خٰلِدِيْنَ فِيْهَآ اِلَّا مَا شَاۤءَ اللّٰهُ ۗاِنَّ رَبَّكَ حَكِيْمٌ عَلِيْمٌ ","bc":"wa yauma yaḥsyuruhum jamī'ā, yā ma'syaral-jinni qadistakṡartum minal-ins, wa qāla auliyā`uhum minal-insi rabbanastamta'a ba'ḍunā biba'ḍiw wa balagnā ajalanallażī ajjalta lanā, qālan-nāru maṡwākum khālidīna fīhā illā mā syā`allāh, inna rabbaka ḥakīmun 'alīm","at":"Dan (ingatlah) pada hari ketika Dia mengumpulkan mereka semua (dan Allah berfirman), “Wahai golongan jin! Kamu telah banyak (menyesatkan) manusia.” Dan kawan-kawan mereka dari golongan manusia berkata, “Ya Tuhan, kami telah saling mendapatkan kesenangan dan sekarang waktu yang telah Engkau tentukan buat kami telah datang.” Allah berfirman, “Nerakalah tempat kamu selama-lamanya, kecuali jika Allah menghendaki lain.” Sungguh, Tuhanmu Mahabijaksana, Maha Mengetahui."},{"id":"129","ar":"وَكَذٰلِكَ نُوَلِّيْ بَعْضَ الظّٰلِمِيْنَ بَعْضًاۢ بِمَا كَانُوْا يَكْسِبُوْنَ ","bc":"wa każālika nuwallī ba'ḍaẓ-ẓālimīna ba'ḍam bimā kānụ yaksibụn","at":"Dan demikianlah Kami jadikan sebagian orang-orang zalim berteman dengan sesamanya, sesuai dengan apa yang mereka kerjakan."},{"id":"130","ar":"يٰمَعْشَرَ الْجِنِّ وَالْاِنْسِ اَلَمْ يَأْتِكُمْ رُسُلٌ مِّنْكُمْ يَقُصُّوْنَ عَلَيْكُمْ اٰيٰتِيْ وَيُنْذِرُوْنَكُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَاۗ قَالُوْا شَهِدْنَا عَلٰٓى اَنْفُسِنَا وَغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَا وَشَهِدُوْا عَلٰٓى اَنْفُسِهِمْ اَنَّهُمْ كَانُوْا كٰفِرِيْنَ ","bc":"yā ma'syaral-jinni wal-insi a lam ya`tikum rusulum mingkum yaquṣṣụna 'alaikum āyātī wa yunżirụnakum liqā`a yaumikum hāżā, qālụ syahidnā 'alā anfusinā wa garrat-humul-ḥayātud-dun-yā wa syahidụ 'alā anfusihim annahum kānụ kāfirīn","at":"Wahai golongan jin dan manusia! Bukankah sudah datang kepadamu rasul-rasul dari kalanganmu sendiri, mereka menyampaikan ayat-ayat-Ku kepadamu dan memperingatkanmu tentang pertemuan pada hari ini? Mereka menjawab, “(Ya), kami menjadi saksi atas diri kami sendiri.” Tetapi mereka tertipu oleh kehidupan dunia dan mereka telah menjadi saksi atas diri mereka sendiri, bahwa mereka adalah orang-orang kafir."},{"id":"131","ar":"ذٰلِكَ اَنْ لَّمْ يَكُنْ رَّبُّكَ مُهْلِكَ الْقُرٰى بِظُلْمٍ وَّاَهْلُهَا غٰفِلُوْنَ ","bc":"żālika al lam yakur rabbuka muhlikal-qurā biẓulmiw wa ahluhā gāfilụn","at":"Demikianlah (para rasul diutus) karena Tuhanmu tidak akan membinasakan suatu negeri secara zalim, sedang penduduknya dalam keadaan lengah (belum tahu)."},{"id":"132","ar":"وَلِكُلٍّ دَرَجٰتٌ مِّمَّا عَمِلُوْاۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا يَعْمَلُوْنَ ","bc":"wa likullin darajātum mimmā 'amilụ, wa mā rabbuka bigāfilin 'ammā ya'malụn","at":"Dan masing-masing orang ada tingkatannya, (sesuai) dengan apa yang mereka kerjakan. Dan Tuhanmu tidak lengah terhadap apa yang mereka kerjakan."},{"id":"133","ar":"وَرَبُّكَ الْغَنِيُّ ذُو الرَّحْمَةِ ۗاِنْ يَّشَأْ يُذْهِبْكُمْ وَيَسْتَخْلِفْ مِنْۢ بَعْدِكُمْ مَّا يَشَاۤءُ كَمَآ اَنْشَاَكُمْ مِّنْ ذُرِّيَّةِ قَوْمٍ اٰخَرِيْنَ ","bc":"wa rabbukal-ganiyyu żur-raḥmah, iy yasya` yuż-hibkum wa yastakhlif mim ba'dikum mā yasyā`u kamā ansya`akum min żurriyyati qaumin ākharīn","at":"Dan Tuhanmu Mahakaya, penuh rahmat. Jika Dia menghendaki, Dia akan memusnahkan kamu dan setelah kamu (musnah) akan Dia ganti dengan yang Dia kehendaki, sebagaimana Dia menjadikan kamu dari keturunan golongan lain."},{"id":"134","ar":"اِنَّ مَا تُوْعَدُوْنَ لَاٰتٍۙ وَّمَآ اَنْتُمْ بِمُعْجِزِيْنَ ","bc":"inna mā tụ'adụna la`ātiw wa mā antum bimu'jizīn","at":"Sesungguhnya apa pun yang dijanjikan kepadamu pasti datang dan kamu tidak mampu menolaknya."},{"id":"135","ar":"قُلْ يٰقَوْمِ اعْمَلُوْا عَلٰى مَكَانَتِكُمْ اِنِّيْ عَامِلٌۚ فَسَوْفَ تَعْلَمُوْنَۙ مَنْ تَكُوْنُ لَهٗ عَاقِبَةُ الدَّارِۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ ","bc":"qul yā qaumi'malụ 'alā makānatikum innī 'āmil, fa saufa ta'lamụna man takụnu lahụ 'āqibatud-dār, innahụ lā yufliḥuẓ-ẓālimụn","at":"Katakanlah (Muhammad), “Wahai kaumku! Berbuatlah menurut kedudukanmu, aku pun berbuat (demikian). Kelak kamu akan mengetahui, siapa yang akan memperoleh tempat (terbaik) di akhirat (nanti). Sesungguhnya orang-orang yang zalim itu tidak akan beruntung."},{"id":"136","ar":"وَجَعَلُوْا لِلّٰهِ مِمَّا ذَرَاَ مِنَ الْحَرْثِ وَالْاَنْعَامِ نَصِيْبًا فَقَالُوْا هٰذَا لِلّٰهِ بِزَعْمِهِمْ وَهٰذَا لِشُرَكَاۤىِٕنَاۚ فَمَا كَانَ لِشُرَكَاۤىِٕهِمْ فَلَا يَصِلُ اِلَى اللّٰهِ ۚوَمَا كَانَ لِلّٰهِ فَهُوَ يَصِلُ اِلٰى شُرَكَاۤىِٕهِمْۗ سَاۤءَ مَا يَحْكُمُوْنَ ","bc":"wa ja'alụ lillāhi mimmā żara`a minal-ḥarṡi wal-an'āmi naṣīban fa qālụ hāżā lillāhi biza'mihim wa hāżā lisyurakā`inā, fa mā kāna lisyurakā`ihim fa lā yaṣilu ilallāh, wa mā kāna lillāhi fa huwa yaṣilu ilā syurakā`ihim, sā`a mā yaḥkumụn","at":"Dan mereka menyediakan sebagian hasil tanaman dan hewan (bagian) untuk Allah sambil berkata menurut persangkaan mereka, “Ini untuk Allah dan yang ini untuk berhala-berhala kami.” Bagian yang untuk berhala-berhala mereka tidak akan sampai kepada Allah, dan bagian yang untuk Allah akan sampai kepada berhala-berhala mereka. Sangat buruk ketetapan mereka itu."},{"id":"137","ar":"وَكَذٰلِكَ زَيَّنَ لِكَثِيْرٍ مِّنَ الْمُشْرِكِيْنَ قَتْلَ اَوْلَادِهِمْ شُرَكَاۤؤُهُمْ لِيُرْدُوْهُمْ وَلِيَلْبِسُوْا عَلَيْهِمْ دِيْنَهُمْۗ وَلَوْ شَاۤءَ اللّٰهُ مَا فَعَلُوْهُ فَذَرْهُمْ وَمَا يَفْتَرُوْنَ ","bc":"wa każālika zayyana likaṡīrim minal-musyrikīna qatla aulādihim syurakā`uhum liyurdụhum wa liyalbisụ 'alaihim dīnahum, walau syā`allāhu mā fa'alụhu fa żar-hum wa mā yaftarụn","at":"Dan demikianlah berhala-berhala mereka (setan) menjadikan terasa indah bagi banyak orang-orang musyrik membunuh anak-anak mereka, untuk membinasakan mereka dan mengacaukan agama mereka sendiri. Dan kalau Allah menghendaki, niscaya mereka tidak akan mengerjakannya. Biarkanlah mereka bersama apa (kebohongan) yang mereka ada-adakan."},{"id":"138","ar":"وَقَالُوْا هٰذِهٖٓ اَنْعَامٌ وَّحَرْثٌ حِجْرٌ لَّا يَطْعَمُهَآ اِلَّا مَنْ نَّشَاۤءُ بِزَعْمِهِمْ وَاَنْعَامٌ حُرِّمَتْ ظُهُوْرُهَا وَاَنْعَامٌ لَّا يَذْكُرُوْنَ اسْمَ اللّٰهِ عَلَيْهَا افْتِرَاۤءً عَلَيْهِۗ سَيَجْزِيْهِمْ بِمَا كَانُوْا يَفْتَرُوْنَ ","bc":"wa qālụ hāżihī an'āmuw wa ḥarṡun ḥijrul lā yaṭ'amuhā illā man nasyā`u biza'mihim wa an'āmun ḥurrimat ẓuhụruhā wa an'āmul lā yażkurụnasmallāhi 'alaihaftirā`an 'alaīh, sayajzīhim bimā kānụ yaftarụn","at":"Dan mereka berkata (menurut anggapan mereka), “Inilah hewan ternak dan hasil bumi yang dilarang, tidak boleh dimakan, kecuali oleh orang yang kami kehendaki.” Dan ada pula hewan yang diharamkan (tidak boleh) ditunggangi, dan ada hewan ternak yang (ketika disembelih) boleh tidak menyebut nama Allah, itu sebagai kebohongan terhadap Allah. Kelak Allah akan membalas semua yang mereka ada-adakan."},{"id":"139","ar":"وَقَالُوْا مَا فِيْ بُطُوْنِ هٰذِهِ الْاَنْعَامِ خَالِصَةٌ لِّذُكُوْرِنَا وَمُحَرَّمٌ عَلٰٓى اَزْوَاجِنَاۚ وَاِنْ يَّكُنْ مَّيْتَةً فَهُمْ فِيْهِ شُرَكَاۤءُ ۗسَيَجْزِيْهِمْ وَصْفَهُمْۗ اِنَّهٗ حَكِيْمٌ عَلِيْمٌ ","bc":"wa qālụ mā fī buṭụni hāżihil-an'āmi khāliṣatul liżukụrinā wa muḥarramun 'alā azwājinā, wa iy yakum maitatan fa hum fīhi syurakā`, sayajzīhim waṣfahum, innahụ ḥakīmun 'alīm","at":"Dan mereka berkata (pula), “Apa yang ada di dalam perut hewan ternak ini khusus untuk kaum laki-laki kami, haram bagi istri-istri kami.” Dan jika yang dalam perut itu (dilahirkan) mati, maka semua boleh (memakannya). Kelak Allah akan membalas atas ketetapan mereka. Sesungguhnya Allah Mahabijaksana, Maha Mengetahui."},{"id":"140","ar":"قَدْ خَسِرَ الَّذِيْنَ قَتَلُوْٓا اَوْلَادَهُمْ سَفَهًاۢ بِغَيْرِ عِلْمٍ وَّحَرَّمُوْا مَا رَزَقَهُمُ اللّٰهُ افْتِرَاۤءً عَلَى اللّٰهِ ۗقَدْ ضَلُّوْا وَمَا كَانُوْا مُهْتَدِيْنَ ","bc":"qad khasirallażīna qatalū aulādahum safaham bigairi 'ilmiw wa ḥarramụ mā razaqahumullāhuftirā`an 'alallāh, qad ḍallụ wa mā kānụ muhtadīn","at":"Sungguh rugi mereka yang membunuh anak-anaknya karena kebodohan tanpa pengetahuan, dan mengharamkan rezeki yang dikaruniakan Allah kepada mereka dengan semata-mata membuat-buat kebohongan terhadap Allah. Sungguh, mereka telah sesat dan tidak mendapat petunjuk."},{"id":"141","ar":"۞ وَهُوَ الَّذِيْٓ اَنْشَاَ جَنّٰتٍ مَّعْرُوْشٰتٍ وَّغَيْرَ مَعْرُوْشٰتٍ وَّالنَّخْلَ وَالزَّرْعَ مُخْتَلِفًا اُكُلُهٗ وَالزَّيْتُوْنَ وَالرُّمَّانَ مُتَشَابِهًا وَّغَيْرَ مُتَشَابِهٍۗ كُلُوْا مِنْ ثَمَرِهٖٓ اِذَآ اَثْمَرَ وَاٰتُوْا حَقَّهٗ يَوْمَ حَصَادِهٖۖ وَلَا تُسْرِفُوْا ۗاِنَّهٗ لَا يُحِبُّ الْمُسْرِفِيْنَۙ","bc":"wa huwallażī ansya`a jannātim ma'rụsyātiw wa gaira ma'rụsyātiw wan-nakhla waz-zar'a mukhtalifan ukuluhụ waz-zaitụna war-rummāna mutasyābihaw wa gaira mutasyābih, kulụ min ṡamarihī iżā aṡmara wa ātụ ḥaqqahụ yauma ḥaṣādihī wa lā tusrifụ, innahụ lā yuḥibbul-musrifīn","at":"Dan Dialah yang menjadikan tanaman-tanaman yang merambat dan yang tidak merambat, pohon kurma, tanaman yang beraneka ragam rasanya, zaitun dan delima yang serupa (bentuk dan warnanya) dan tidak serupa (rasanya). Makanlah buahnya apabila ia berbuah dan berikanlah haknya (zakatnya) pada waktu memetik hasilnya, tapi janganlah berlebih-lebihan. Sesungguhnya Allah tidak menyukai orang-orang yang berlebihan,"},{"id":"142","ar":"وَمِنَ الْاَنْعَامِ حَمُوْلَةً وَّفَرْشًا ۗ كُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ وَلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌۙ","bc":"wa minal-an'āmi ḥamụlataw wa farsyā, kulụ mimmā razaqakumullāhu wa lā tattabi'ụ khuṭuwātisy-syaiṭān, innahụ lakum 'aduwwum mubīn","at":"dan di antara hewan-hewan ternak itu ada yang dijadikan pengangkut beban dan ada (pula) yang untuk disembelih. Makanlah rezeki yang diberikan Allah kepadamu, dan janganlah kamu mengikuti langkah-langkah setan. Sesungguhnya setan itu musuh yang nyata bagimu,"},{"id":"143","ar":"ثَمٰنِيَةَ اَزْوَاجٍۚ مِنَ الضَّأْنِ اثْنَيْنِ وَمِنَ الْمَعْزِ اثْنَيْنِۗ قُلْ ءٰۤالذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۗ نَبِّئُوْنِيْ بِعِلْمٍ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"ṡamāniyata azwāj, minaḍ-ḍa`niṡnaini wa minal-ma'ziṡnaīn, qul āż-żakaraini ḥarrama amil-unṡayaini ammasytamalat 'alaihi ar-ḥāmul-unṡayaīn, nabbi`ụnī bi'ilmin ing kuntum ṣādiqīn","at":"ada delapan hewan ternak yang berpasangan (empat pasang); sepasang domba dan sepasang kambing. Katakanlah, “Apakah yang diharamkan Allah dua yang jantan atau dua yang betina atau yang ada dalam kandungan kedua betinanya? Terangkanlah kepadaku berdasar pengetahuan jika kamu orang yang benar.”"},{"id":"144","ar":"وَمِنَ الْاِبِلِ اثْنَيْنِ وَمِنَ الْبَقَرِ اثْنَيْنِۗ قُلْ ءٰۤالذَّكَرَيْنِ حَرَّمَ اَمِ الْاُنْثَيَيْنِ اَمَّا اشْتَمَلَتْ عَلَيْهِ اَرْحَامُ الْاُنْثَيَيْنِۗ اَمْ كُنْتُمْ شُهَدَاۤءَ اِذْ وَصّٰىكُمُ اللّٰهُ بِهٰذَاۚ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا لِّيُضِلَّ النَّاسَ بِغَيْرِ عِلْمٍۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ","bc":"wa minal-ibiliṡnaini wa minal-baqariṡnaīn, qul āż-żakaraini ḥarrama amil-unṡayaini ammasytamalat 'alaihi ar-ḥāmul-unṡayaīn, am kuntum syuhadā`a iż waṣṣākumullāhu bihāżā, fa man aẓlamu mim maniftarā 'alallāhi każibal liyuḍillan-nāsa bigairi 'ilm, innallāha lā yahdil-qaumaẓ-ẓālimīn","at":"Dan dari unta sepasang dan dari sapi sepasang. Katakanlah, “Apakah yang diharamkan dua yang jantan atau dua yang betina, atau yang ada dalam kandungan kedua betinanya? Apakah kamu menjadi saksi ketika Allah menetapkan ini bagimu? Siapakah yang lebih zalim daripada orang-orang yang mengada-adakan kebohongan terhadap Allah untuk menyesatkan orang-orang tanpa pengetahuan?” Sesungguhnya Allah tidak akan memberi petunjuk kepada orang-orang yang zalim."},{"id":"145","ar":"قُلْ لَّآ اَجِدُ فِيْ مَآ اُوْحِيَ اِلَيَّ مُحَرَّمًا عَلٰى طَاعِمٍ يَّطْعَمُهٗٓ اِلَّآ اَنْ يَّكُوْنَ مَيْتَةً اَوْ دَمًا مَّسْفُوْحًا اَوْ لَحْمَ خِنْزِيْرٍ فَاِنَّهٗ رِجْسٌ اَوْ فِسْقًا اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّلَا عَادٍ فَاِنَّ رَبَّكَ غَفُوْرٌ رَّحِيْمٌ ","bc":"qul lā ajidu fī mā ụḥiya ilayya muḥarraman 'alā ṭā'imiy yaṭ'amuhū illā ay yakụna maitatan au damam masfụḥan au laḥma khinzīrin fa innahụ rijsun au fisqan uhilla ligairillāhi bih, fa maniḍṭurra gaira bāgiw wa lā 'ādin fa inna rabbaka gafụrur raḥīm","at":"Katakanlah, “Tidak kudapati di dalam apa yang diwahyukan kepadaku, sesuatu yang diharamkan memakannya bagi yang ingin memakannya, kecuali daging hewan yang mati (bangkai), darah yang mengalir, daging babi – karena semua itu kotor – atau hewan yang disembelih bukan atas (nama) Allah. Tetapi barangsiapa terpaksa bukan karena menginginkan dan tidak melebihi (batas darurat) maka sungguh, Tuhanmu Maha Pengampun, Maha Penyayang."},{"id":"146","ar":"وَعَلَى الَّذِيْنَ هَادُوْا حَرَّمْنَا كُلَّ ذِيْ ظُفُرٍۚ وَمِنَ الْبَقَرِ وَالْغَنَمِ حَرَّمْنَا عَلَيْهِمْ شُحُوْمَهُمَآ اِلَّا مَا حَمَلَتْ ظُهُوْرُهُمَآ اَوِ الْحَوَايَآ اَوْ مَا اخْتَلَطَ بِعَظْمٍۗ ذٰلِكَ جَزَيْنٰهُمْ بِبَغْيِهِمْۚ وَاِنَّا لَصٰدِقُوْنَ ","bc":"wa 'alallażīna hādụ ḥarramnā kulla żī ẓufur, wa minal-baqari wal-ganami ḥarramnā 'alaihim syuḥụmahumā illā mā ḥamalat ẓuhụruhumā awil-ḥawāyā au makhtalaṭa bi'aẓm, żālika jazaināhum bibagyihim, wa innā laṣādiqụn","at":"Dan kepada orang-orang Yahudi, Kami haramkan semua (hewan) yang berkuku, dan Kami haramkan kepada mereka lemak sapi dan domba, kecuali yang melekat di punggungnya, atau yang dalam isi perutnya, atau yang bercampur dengan tulang. Demikianlah Kami menghukum mereka karena kedurhakaannya. Dan sungguh, Kami Mahabenar."},{"id":"147","ar":"فَاِنْ كَذَّبُوْكَ فَقُلْ رَّبُّكُمْ ذُوْ رَحْمَةٍ وَّاسِعَةٍۚ وَلَا يُرَدُّ بَأْسُهٗ عَنِ الْقَوْمِ الْمُجْرِمِيْنَ ","bc":"fa ing każżabụka fa qur rabbukum żụ raḥmatiw wāsi'ah, wa lā yuraddu ba`suhụ 'anil-qaumil-mujrimīn","at":"Maka jika mereka mendustakan kamu, katakanlah, “Tuhanmu mempunyai rahmat yang luas, dan siksa-Nya kepada orang-orang yang berdosa tidak dapat dielakkan.”"},{"id":"148","ar":"سَيَقُوْلُ الَّذِيْنَ اَشْرَكُوْا لَوْ شَاۤءَ اللّٰهُ مَآ اَشْرَكْنَا وَلَآ اٰبَاۤؤُنَا وَلَا حَرَّمْنَا مِنْ شَيْءٍۗ كَذٰلِكَ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ حَتّٰى ذَاقُوْا بَأْسَنَاۗ قُلْ هَلْ عِنْدَكُمْ مِّنْ عِلْمٍ فَتُخْرِجُوْهُ لَنَاۗ اِنْ تَتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنْ اَنْتُمْ اِلَّا تَخْرُصُوْنَ","bc":"sayaqụlullażīna asyrakụ lau syā`allāhu mā asyraknā wa lā ābā`unā wa lā ḥarramnā min syaī`, każālika każżaballażīna ming qablihim ḥattā żāqụ ba`sanā, qul hal 'indakum min 'ilmin fa tukhrijụhu lanā, in tattabi'ụna illaẓ-ẓanna wa in antum illā takhruṣụn","at":"Orang-orang musyrik akan berkata, “Jika Allah menghendaki, tentu kami tidak akan mempersekutukan-Nya, begitu pula nenek moyang kami, dan kami tidak akan mengharamkan apa pun.” Demikian pula orang-orang sebelum mereka yang telah mendustakan (para rasul) sampai mereka merasakan azab Kami. Katakanlah (Muhammad), “Apakah kamu mempunyai pengetahuan yang dapat kamu kemukakan kepada kami? Yang kamu ikuti hanya persangkaan belaka, dan kamu hanya mengira.”"},{"id":"149","ar":"قُلْ فَلِلّٰهِ الْحُجَّةُ الْبَالِغَةُۚ فَلَوْ شَاۤءَ لَهَدٰىكُمْ اَجْمَعِيْنَ ","bc":"qul falillāhil-ḥujjatul-bāligah, falau syā`a lahadākum ajma'īn","at":"Katakanlah (Muhammad), “Alasan yang kuat hanya pada Allah. Maka kalau Dia menghendaki, niscaya kamu semua mendapat petunjuk.”"},{"id":"150","ar":"قُلْ هَلُمَّ شُهَدَاۤءَكُمُ الَّذِيْنَ يَشْهَدُوْنَ اَنَّ اللّٰهَ حَرَّمَ هٰذَاۚ فَاِنْ شَهِدُوْا فَلَا تَشْهَدْ مَعَهُمْۚ وَلَا تَتَّبِعْ اَهْوَاۤءَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَالَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ وَهُمْ بِرَبِّهِمْ يَعْدِلُوْنَ ","bc":"qul halumma syuhadā`akumullażīna yasy-hadụna annallāha ḥarrama hāżā, fa in syahidụ fa lā tasy-had ma'ahum, wa lā tattabi' ahwā`allażīna każżabụ bi`āyātinā wallażīna lā yu`minụna bil-ākhirati wa hum birabbihim ya'dilụn","at":"Katakanlah (Muhammad), “Bawalah saksi-saksimu yang dapat membuktikan bahwa Allah mengharamkan ini.” Jika mereka memberikan kesaksian, engkau jangan (ikut pula) memberikan kesaksian bersama mereka. Jangan engkau ikuti keinginan orang-orang yang mendustakan ayat-ayat Kami dan orang-orang yang tidak beriman kepada akhirat, dan mereka mempersekutukan Tuhan."},{"id":"151","ar":"۞ قُلْ تَعَالَوْا اَتْلُ مَا حَرَّمَ رَبُّكُمْ عَلَيْكُمْ اَلَّا تُشْرِكُوْا بِهٖ شَيْـًٔا وَّبِالْوَالِدَيْنِ اِحْسَانًاۚ وَلَا تَقْتُلُوْٓا اَوْلَادَكُمْ مِّنْ اِمْلَاقٍۗ نَحْنُ نَرْزُقُكُمْ وَاِيَّاهُمْ ۚوَلَا تَقْرَبُوا الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَۚ وَلَا تَقْتُلُوا النَّفْسَ الَّتِيْ حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّۗ ذٰلِكُمْ وَصّٰىكُمْ بِهٖ لَعَلَّكُمْ تَعْقِلُوْنَ ","bc":"qul ta'ālau atlu mā ḥarrama rabbukum 'alaikum allā tusyrikụ bihī syai`aw wa bil-wālidaini iḥsānā, wa lā taqtulū aulādakum min imlāq, naḥnu narzuqukum wa iyyāhum, wa lā taqrabul-fawāḥisya mā ẓahara min-hā wa mā baṭan, wa lā taqtulun-nafsallatī ḥarramallāhu illā bil-ḥaqq, żālikum waṣṣākum bihī la'allakum ta'qilụn","at":"Katakanlah (Muhammad), “Marilah aku bacakan apa yang diharamkan Tuhan kepadamu. Jangan mempersekutukan-Nya dengan apa pun, berbuat baik kepada ibu bapak, janganlah membunuh anak-anakmu karena miskin. Kamilah yang memberi rezeki kepadamu dan kepada mereka; janganlah kamu mendekati perbuatan yang keji, baik yang terlihat ataupun yang tersembunyi, janganlah kamu membunuh orang yang diharamkan Allah kecuali dengan alasan yang benar. Demikianlah Dia memerintahkan kepadamu agar kamu mengerti."},{"id":"152","ar":"وَلَا تَقْرَبُوْا مَالَ الْيَتِيْمِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهٗ ۚوَاَوْفُوا الْكَيْلَ وَالْمِيْزَانَ بِالْقِسْطِۚ لَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَاۚ وَاِذَا قُلْتُمْ فَاعْدِلُوْا وَلَوْ كَانَ ذَا قُرْبٰىۚ وَبِعَهْدِ اللّٰهِ اَوْفُوْاۗ ذٰلِكُمْ وَصّٰىكُمْ بِهٖ لَعَلَّكُمْ تَذَكَّرُوْنَۙ","bc":"wa lā taqrabụ mālal-yatīmi illā billatī hiya aḥsanu ḥattā yabluga asyuddah, wa auful-kaila wal-mīzāna bil-qisṭ, lā nukallifu nafsan illā wus'ahā, wa iżā qultum fa'dilụ walau kāna żā qurbā, wa bi'ahdillāhi aufụ, żālikum waṣṣākum bihī la'allakum tażakkarụn","at":"Dan janganlah kamu mendekati harta anak yatim, kecuali dengan cara yang lebih bermanfaat, sampai dia mencapai (usia) dewasa. Dan sempurnakanlah takaran dan timbangan dengan adil. Kami tidak membebani seseorang melainkan menurut kesanggupannya. Apabila kamu berbicara, bicaralah sejujurnya, sekalipun dia kerabat(mu) dan penuhilah janji Allah. Demikianlah Dia memerintahkan kepadamu agar kamu ingat.”"},{"id":"153","ar":"وَاَنَّ هٰذَا صِرَاطِيْ مُسْتَقِيْمًا فَاتَّبِعُوْهُ ۚوَلَا تَتَّبِعُوا السُّبُلَ فَتَفَرَّقَ بِكُمْ عَنْ سَبِيْلِهٖ ۗذٰلِكُمْ وَصّٰىكُمْ بِهٖ لَعَلَّكُمْ تَتَّقُوْنَ ","bc":"wa anna hāżā ṣirāṭī mustaqīman fattabi'ụh, wa lā tattabi'us-subula fa tafarraqa bikum 'an sabīlih, żālikum waṣṣākum bihī la'allakum tattaqụn","at":"Dan sungguh, inilah jalan-Ku yang lurus. Maka ikutilah! Jangan kamu ikuti jalan-jalan (yang lain) yang akan mencerai-beraikan kamu dari jalan-Nya. Demikianlah Dia memerintahkan kepadamu agar kamu bertakwa."},{"id":"154","ar":"ثُمَّ اٰتَيْنَا مُوْسَى الْكِتٰبَ تَمَامًا عَلَى الَّذِيْٓ اَحْسَنَ وَتَفْصِيْلًا لِّكُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً لَّعَلَّهُمْ بِلِقَاۤءِ رَبِّهِمْ يُؤْمِنُوْنَ ","bc":"ṡumma ātainā mụsal-kitāba tamāman 'alallażī aḥsana wa tafṣīlal likulli syai`iw wa hudaw wa raḥmatal la'allahum biliqā`i rabbihim yu`minụn","at":"Kemudian Kami telah memberikan kepada Musa Kitab (Taurat) untuk menyempurnakan (nikmat Kami) kepada orang yang berbuat kebaikan, untuk menjelaskan segala sesuatu, dan sebagai petunjuk dan rahmat, agar mereka beriman akan adanya pertemuan dengan Tuhannya."},{"id":"155","ar":"وَهٰذَا كِتٰبٌ اَنْزَلْنٰهُ مُبٰرَكٌ فَاتَّبِعُوْهُ وَاتَّقُوْا لَعَلَّكُمْ تُرْحَمُوْنَۙ ","bc":"wa hāżā kitābun anzalnāhu mubārakun fattabi'ụhu wattaqụ la'allakum tur-ḥamụn","at":"Dan ini adalah Kitab (Al-Qur'an) yang Kami turunkan dengan penuh berkah. Ikutilah, dan bertakwalah agar kamu mendapat rahmat,"},{"id":"156","ar":"اَنْ تَقُوْلُوْٓا اِنَّمَآ اُنْزِلَ الْكِتٰبُ عَلٰى طَاۤىِٕفَتَيْنِ مِنْ قَبْلِنَاۖ وَاِنْ كُنَّا عَنْ دِرَاسَتِهِمْ لَغٰفِلِيْنَۙ ","bc":"an taqụlū innamā unzilal-kitābu 'alā ṭā`ifataini ming qablinā wa ing kunnā 'an dirāsatihim lagāfilīn","at":"(Kami turunkan Al-Qur'an itu) agar kamu (tidak) mengatakan, “Kitab itu hanya diturunkan kepada dua golongan sebelum kami (Yahudi dan Nasrani) dan sungguh, kami tidak memperhatikan apa yang mereka baca,”"},{"id":"157","ar":"اَوْ تَقُوْلُوْا لَوْ اَنَّآ اُنْزِلَ عَلَيْنَا الْكِتٰبُ لَكُنَّآ اَهْدٰى مِنْهُمْۚ فَقَدْ جَاۤءَكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْ وَهُدًى وَّرَحْمَةٌ ۚفَمَنْ اَظْلَمُ مِمَّنْ كَذَّبَ بِاٰيٰتِ اللّٰهِ وَصَدَفَ عَنْهَا ۗسَنَجْزِى الَّذِيْنَ يَصْدِفُوْنَ عَنْ اٰيٰتِنَا سُوْۤءَ الْعَذَابِ بِمَا كَانُوْا يَصْدِفُوْنَ ","bc":"au taqụlụ lau annā unzila 'alainal-kitābu lakunnā ahdā min-hum, fa qad jā`akum bayyinatum mir rabbikum wa hudaw wa raḥmah, fa man aẓlamu mim mang każżaba bi`āyātillāhi wa ṣadafa 'an-hā, sanajzillażīna yaṣdifụna 'an āyātinā sū`al-'ażābi bimā kānụ yaṣdifụn","at":"atau agar kamu (tidak) mengatakan, “Jikalau Kitab itu diturunkan kepada kami, tentulah kami lebih mendapat petunjuk daripada mereka.” Sungguh, telah datang kepadamu penjelasan yang nyata, petunjuk dan rahmat dari Tuhanmu. Siapakah yang lebih zalim daripada orang yang mendustakan ayat-ayat Allah dan berpaling daripadanya? Kelak, Kami akan memberi balasan kepada orang-orang yang berpaling dari ayat-ayat Kami dengan azab yang keras, karena mereka selalu berpaling."},{"id":"158","ar":"هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ تَأْتِيَهُمُ الْمَلٰۤىِٕكَةُ اَوْ يَأْتِيَ رَبُّكَ اَوْ يَأْتِيَ بَعْضُ اٰيٰتِ رَبِّكَ ۗيَوْمَ يَأْتِيْ بَعْضُ اٰيٰتِ رَبِّكَ لَا يَنْفَعُ نَفْسًا اِيْمَانُهَا لَمْ تَكُنْ اٰمَنَتْ مِنْ قَبْلُ اَوْ كَسَبَتْ فِيْٓ اِيْمَانِهَا خَيْرًاۗ قُلِ انْتَظِرُوْٓا اِنَّا مُنْتَظِرُوْنَ ","bc":"hal yanẓurụna illā an ta`tiyahumul-malā`ikatu au ya`tiya rabbuka au ya`tiya ba'ḍu āyāti rabbik, yauma ya`tī ba'ḍu āyāti rabbika lā yanfa'u nafsan īmānuhā lam takun āmanat ming qablu au kasabat fī īmānihā khairā, qulintaẓirū innā muntaẓirụn","at":"Yang mereka nanti-nantikan hanyalah kedatangan malaikat kepada mereka, atau kedatangan Tuhanmu, atau sebagian tanda-tanda dari Tuhanmu. Pada hari datangnya sebagian tanda-tanda Tuhanmu tidak berguna lagi iman seseorang yang belum beriman sebelum itu, atau (belum) berusaha berbuat kebajikan dengan imannya itu. Katakanlah, “Tunggulah! Kami pun menunggu.”"},{"id":"159","ar":"اِنَّ الَّذِيْنَ فَرَّقُوْا دِيْنَهُمْ وَكَانُوْا شِيَعًا لَّسْتَ مِنْهُمْ فِيْ شَيْءٍۗ اِنَّمَآ اَمْرُهُمْ اِلَى اللّٰهِ ثُمَّ يُنَبِّئُهُمْ بِمَا كَانُوْا يَفْعَلُوْنَ ","bc":"innallażīna farraqụ dīnahum wa kānụ syiya'al lasta min-hum fī syaī`, innamā amruhum ilallāhi ṡumma yunabbi`uhum bimā kānụ yaf'alụn","at":"Sesungguhnya orang-orang yang memecah belah agamanya dan mereka menjadi (terpecah) dalam golongan-golongan, sedikit pun bukan tanggung jawabmu (Muhammad) atas mereka. Sesungguhnya urusan mereka (terserah) kepada Allah. Kemudian Dia akan memberitahukan kepada mereka apa yang telah mereka perbuat."},{"id":"160","ar":"مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ عَشْرُ اَمْثَالِهَا ۚوَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَلَا يُجْزٰٓى اِلَّا مِثْلَهَا وَهُمْ لَا يُظْلَمُوْنَ","bc":"man jā`a bil-ḥasanati fa lahụ 'asyru amṡālihā, wa man jā`a bis-sayyi`ati fa lā yujzā illā miṡlahā wa hum lā yuẓlamụn","at":"Barangsiapa berbuat kebaikan mendapat balasan sepuluh kali lipat amalnya. Dan barangsiapa berbuat kejahatan dibalas seimbang dengan kejahatannya. Mereka sedikit pun tidak dirugikan (dizalimi)."},{"id":"161","ar":"قُلْ اِنَّنِيْ هَدٰىنِيْ رَبِّيْٓ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ەۚ دِيْنًا قِيَمًا مِّلَّةَ اِبْرٰهِيْمَ حَنِيْفًاۚ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ","bc":"qul innanī hadānī rabbī ilā ṣirāṭim mustaqīm, dīnang qiyamam millata ibrāhīma ḥanīfā, wa mā kāna minal-musyrikīn","at":"Katakanlah (Muhammad), “Sesungguhnya Tuhanku telah memberiku petunjuk ke jalan yang lurus, agama yang benar, agama Ibrahim yang lurus. Dia (Ibrahim) tidak termasuk orang-orang musyrik.”"},{"id":"162","ar":"قُلْ اِنَّ صَلَاتِيْ وَنُسُكِيْ وَمَحْيَايَ وَمَمَاتِيْ لِلّٰهِ رَبِّ الْعٰلَمِيْنَۙ","bc":"qul inna ṣalātī wa nusukī wa maḥyāya wa mamātī lillāhi rabbil-'ālamīn","at":"Katakanlah (Muhammad), “Sesungguhnya salatku, ibadahku, hidupku dan matiku hanyalah untuk Allah, Tuhan seluruh alam,"},{"id":"163","ar":"لَا شَرِيْكَ لَهٗ ۚوَبِذٰلِكَ اُمِرْتُ وَاَنَا۠ اَوَّلُ الْمُسْلِمِيْنَ ","bc":"lā syarīka lah, wa biżālika umirtu wa ana awwalul-muslimīn","at":"tidak ada sekutu bagi-Nya; dan demikianlah yang diperintahkan kepadaku dan aku adalah orang yang pertama-tama berserah diri (muslim).”"},{"id":"164","ar":"قُلْ اَغَيْرَ اللّٰهِ اَبْغِيْ رَبًّا وَّهُوَ رَبُّ كُلِّ شَيْءٍۗ وَلَا تَكْسِبُ كُلُّ نَفْسٍ اِلَّا عَلَيْهَاۚ وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۚ ثُمَّ اِلٰى رَبِّكُمْ مَّرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ ","bc":"qul a gairallāhi abgī rabbaw wa huwa rabbu kulli syaī`, wa lā taksibu kullu nafsin illā 'alaihā, wa lā taziru wāziratuw wizra ukhrā, ṡumma ilā rabbikum marji'ukum fa yunabbi`ukum bimā kuntum fīhi takhtalifụn","at":"Katakanlah (Muhammad), “Apakah (patut) aku mencari tuhan selain Allah, padahal Dialah Tuhan bagi segala sesuatu. Setiap perbuatan dosa seseorang, dirinya sendiri yang bertanggung jawab. Dan seseorang tidak akan memikul beban dosa orang lain. Kemudian kepada Tuhanmulah kamu kembali, dan akan diberitahukan-Nya kepadamu apa yang dahulu kamu perselisihkan.”"},{"id":"165","ar":"وَهُوَ الَّذِيْ جَعَلَكُمْ خَلٰۤىِٕفَ الْاَرْضِ وَرَفَعَ بَعْضَكُمْ فَوْقَ بَعْضٍ دَرَجٰتٍ لِّيَبْلُوَكُمْ فِيْ مَآ اٰتٰىكُمْۗ اِنَّ رَبَّكَ سَرِيْعُ الْعِقَابِۖ وَاِنَّهٗ لَغَفُوْرٌ رَّحِيْمٌ","bc":"wa huwallażī ja'alakum khalā`ifal-arḍi wa rafa'a ba'ḍakum fauqa ba'ḍin darajātil liyabluwakum fī mā ātākum, inna rabbaka sarī'ul-'iqābi wa innahụ lagafụrur raḥīm","at":"Dan Dialah yang menjadikan kamu sebagai khalifah-khalifah di bumi dan Dia mengangkat (derajat) sebagian kamu di atas yang lain, untuk mengujimu atas (karunia) yang diberikan-Nya kepadamu. Sesungguhnya Tuhanmu sangat cepat memberi hukuman dan sungguh, Dia Maha Pengampun, Maha Penyayang."}],al_anbiya=[{"id":"1","ar":"اِقْتَرَبَ لِلنَّاسِ حِسَابُهُمْ وَهُمْ فِيْ غَفْلَةٍ مُّعْرِضُوْنَ ۚ ","bc":"iqtaraba lin-nāsi ḥisābuhum wa hum fī gaflatim mu'riḍụn","at":"Telah semakin dekat kepada manusia perhitungan amal mereka, sedang mereka dalam keadaan lalai (dengan dunia), berpaling (dari akhirat)."},{"id":"2","ar":"مَا يَأْتِيْهِمْ مِّنْ ذِكْرٍ مِّنْ رَّبِّهِمْ مُّحْدَثٍ اِلَّا اسْتَمَعُوْهُ وَهُمْ يَلْعَبُوْنَ ۙ","bc":"mā ya`tīhim min żikrim mir rabbihim muḥdaṡin illastama'ụhu wa hum yal'abụn","at":"Setiap diturunkan kepada mereka ayat-ayat yang baru dari Tuhan, mereka mendengarkannya sambil bermain-main."},{"id":"3","ar":"لَاهِيَةً قُلُوْبُهُمْۗ وَاَسَرُّوا النَّجْوَىۖ الَّذِيْنَ ظَلَمُوْاۖ هَلْ هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۚ اَفَتَأْتُوْنَ السِّحْرَ وَاَنْتُمْ تُبْصِرُوْنَ ","bc":"lāhiyatang qulụbuhum, wa asarrun-najwallażīna ẓalamụ hal hāżā illā basyarum miṡlukum, a fa ta`tụnas-siḥra wa antum tubṣirụn","at":"Hati mereka dalam keadaan lalai. Dan orang-orang yang zalim itu merahasiakan pembicaraan mereka, “(Orang) ini (Muhammad) tidak lain hanyalah seorang manusia (juga) seperti kamu. Apakah kamu menerima sihir itu padahal kamu menyaksikannya?”"},{"id":"4","ar":"قٰلَ رَبِّيْ يَعْلَمُ الْقَوْلَ فِى السَّمَاۤءِ وَالْاَرْضِۖ وَهُوَ السَّمِيْعُ الْعَلِيْمُ ","bc":"qāla rabbī ya'lamul-qaula fis-samā`i wal-arḍi wa huwas-samī'ul-'alīm","at":"Dia (Muhammad) berkata, “Tuhanku mengetahui (semua) perkataan di langit dan di bumi, dan Dia Maha Mendengar, Maha Mengetahui!”"},{"id":"5","ar":"بَلْ قَالُوْٓا اَضْغَاثُ اَحْلَامٍۢ بَلِ افْتَرٰىهُ بَلْ هُوَ شَاعِرٌۚ فَلْيَأْتِنَا بِاٰيَةٍ كَمَآ اُرْسِلَ الْاَوَّلُوْنَ ","bc":"bal qālū aḍgāṡu aḥlām, baliftarāhu bal huwa syā'ir, falya`tinā bi`āyating kamā ursilal-awwalụn","at":" Bahkan mereka mengatakan, “(Al-Qur'an itu buah) mimpi-mimpi yang kacau, atau hasil rekayasanya (Muhammad), atau bahkan dia hanya seorang penyair, cobalah dia datangkan kepada kita suatu tanda (bukti), seperti halnya rasul-rasul yang diutus terdahulu.”"},{"id":"6","ar":"مَآ اٰمَنَتْ قَبْلَهُمْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَاۚ اَفَهُمْ يُؤْمِنُوْنَ ","bc":"mā āmanat qablahum ming qaryatin ahlaknāhā, a fa hum yu`minụn","at":" Penduduk suatu negeri sebelum mereka, yang telah Kami binasakan, mereka itu tidak beriman (padahal telah Kami kirimkan bukti). Apakah mereka akan beriman?"},{"id":"7","ar":"وَمَآ اَرْسَلْنَا قَبْلَكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ فَسْـَٔلُوْٓا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُوْنَ ","bc":"wa mā arsalnā qablaka illā rijālan nụḥī ilaihim fas`alū ahlaż-żikri ing kuntum lā ta'lamụn","at":"Dan Kami tidak mengutus (rasul-rasul) sebelum engkau (Muhammad), melainkan beberapa orang laki-laki yang Kami beri wahyu kepada mereka, maka tanyakanlah kepada orang yang berilmu, jika kamu tidak mengetahui."},{"id":"8","ar":"وَمَا جَعَلْنٰهُمْ جَسَدًا لَّا يَأْكُلُوْنَ الطَّعَامَ وَمَا كَانُوْا خٰلِدِيْنَ ","bc":"wa mā ja'alnāhum jasadal lā ya`kulụnaṭ-ṭa'āma wa mā kānụ khālidīn","at":"Dan Kami tidak menjadikan mereka (rasul-rasul) suatu tubuh yang tidak memakan makanan dan mereka tidak (pula) hidup kekal."},{"id":"9","ar":"ثُمَّ صَدَقْنٰهُمُ الْوَعْدَ فَاَنْجَيْنٰهُمْ وَمَنْ نَّشَاۤءُ وَاَهْلَكْنَا الْمُسْرِفِيْنَ ","bc":"ṡumma ṣadaqnāhumul-wa'da fa anjaināhum wa man nasyā`u wa ahlaknal-musrifīn","at":"Kemudian Kami tepati janji (yang telah Kami janjikan) kepada mereka. Maka Kami selamatkan mereka dan orang-orang yang Kami kehendaki, dan Kami binasakan orang-orang yang melampaui batas."},{"id":"10","ar":"لَقَدْ اَنْزَلْنَآ اِلَيْكُمْ كِتٰبًا فِيْهِ ذِكْرُكُمْۗ اَفَلَا تَعْقِلُوْنَ","bc":"laqad anzalnā ilaikum kitāban fīhi żikrukum, a fa lā ta'qilụn","at":" Sungguh, telah Kami turunkan kepadamu sebuah Kitab (Al-Qur'an) yang di dalamnya terdapat peringatan bagimu. Maka apakah kamu tidak mengerti?"},{"id":"11","ar":"وَكَمْ قَصَمْنَا مِنْ قَرْيَةٍ كَانَتْ ظَالِمَةً وَّاَنْشَأْنَا بَعْدَهَا قَوْمًا اٰخَرِيْنَ ","bc":"wa kam qaṣamnā ming qaryating kānat ẓālimataw wa ansya`nā ba'dahā qauman ākharīn","at":" Dan berapa banyak (penduduk) negeri yang zalim yang telah Kami binasakan, dan Kami jadikan generasi yang lain setelah mereka itu (sebagai penggantinya)."},{"id":"12","ar":"فَلَمَّآ اَحَسُّوْا بَأْسَنَآ اِذَا هُمْ مِّنْهَا يَرْكُضُوْنَ ۗ","bc":"fa lammā aḥassụ ba`sanā iżā hum min-hā yarkuḍụn","at":" Maka ketika mereka merasakan azab Kami, tiba-tiba mereka melarikan diri dari (negerinya) itu."},{"id":"13","ar":"لَا تَرْكُضُوْا وَارْجِعُوْٓا اِلٰى مَآ اُتْرِفْتُمْ فِيْهِ وَمَسٰكِنِكُمْ لَعَلَّكُمْ تُسْـَٔلُوْنَ ","bc":"lā tarkuḍụ warji'ū ilā mā utriftum fīhi wa masākinikum la'allakum tus`alụn","at":"Janganlah kamu lari tergesa-gesa; kembalilah kamu kepada kesenangan hidupmu dan tempat-tempat kediamanmu (yang baik), agar kamu dapat ditanya."},{"id":"14","ar":"قَالُوْا يٰوَيْلَنَآ اِنَّا كُنَّا ظٰلِمِيْنَ ","bc":"qālụ yā wailanā innā kunnā ẓālimīn","at":"Mereka berkata, “Betapa celaka kami, sungguh, kami orang-orang yang zalim.”"},{"id":"15","ar":"فَمَا زَالَتْ تِّلْكَ دَعْوٰىهُمْ حَتّٰى جَعَلْنٰهُمْ حَصِيْدًا خَامِدِيْنَ ","bc":"fa mā zālat tilka da'wāhum ḥattā ja'alnāhum ḥaṣīdan khāmidīn","at":" Maka demikianlah keluhan mereka berkepanjangan, sehingga mereka Kami jadikan sebagai tanaman yang telah dituai, yang tidak dapat hidup lagi."},{"id":"16","ar":"وَمَا خَلَقْنَا السَّمَاۤءَ وَالْاَرْضَ وَمَا بَيْنَهُمَا لٰعِبِيْنَ ","bc":"wa mā khalaqnas-samā`a wal-arḍa wa mā bainahumā lā'ibīn","at":"Dan Kami tidak menciptakan langit dan bumi dan segala apa yang ada di antara keduanya dengan main-main."},{"id":"17","ar":"لَوْ اَرَدْنَآ اَنْ نَّتَّخِذَ لَهْوًا لَّاتَّخَذْنٰهُ مِنْ لَّدُنَّآ ۖاِنْ كُنَّا فٰعِلِيْنَ ","bc":"lau aradnā an nattakhiża lahwal lattakhażnāhu mil ladunnā ing kunnā fā'ilīn","at":" Seandainya Kami hendak membuat suatu permainan (istri dan anak), tentulah Kami membuatnya dari sisi Kami, jika Kami benar-benar menghendaki berbuat demikian."},{"id":"18","ar":"بَلْ نَقْذِفُ بِالْحَقِّ عَلَى الْبَاطِلِ فَيَدْمَغُهٗ فَاِذَا هُوَ زَاهِقٌۗ وَلَكُمُ الْوَيْلُ مِمَّا تَصِفُوْنَ ","bc":"bal naqżifu bil-ḥaqqi 'alal-bāṭili fa yadmaguhụ fa iżā huwa zāhiq, wa lakumul-wailu mimmā taṣifụn","at":"Sebenarnya Kami melemparkan yang hak (kebenaran) kepada yang batil (tidak benar) lalu yang hak itu menghancurkannya, maka seketika itu (yang batil) lenyap. Dan celaka kamu karena kamu menyifati (Allah dengan sifat-sifat yang tidak pantas bagi-Nya)."},{"id":"19","ar":"وَلَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَمَنْ عِنْدَهٗ لَا يَسْتَكْبِرُوْنَ عَنْ عِبَادَتِهٖ وَلَا يَسْتَحْسِرُوْنَ ۚ","bc":"wa lahụ man fis-samāwāti wal-arḍ, wa man 'indahụ lā yastakbirụna 'an 'ibādatihī wa lā yastaḥsirụn","at":"Dan milik-Nya siapa yang di langit dan di bumi. Dan (malaikat-malaikat) yang di sisi-Nya, tidak mempunyai rasa angkuh untuk menyembah-Nya dan tidak (pula) merasa letih."},{"id":"20","ar":"يُسَبِّحُوْنَ الَّيْلَ وَالنَّهَارَ لَا يَفْتُرُوْنَ ","bc":"yusabbiḥụnal-laila wan-nahāra lā yafturụn","at":"Mereka (malaikat-malaikat) bertasbih tidak henti-hentinya malam dan siang."},{"id":"21","ar":"اَمِ اتَّخَذُوْٓا اٰلِهَةً مِّنَ الْاَرْضِ هُمْ يُنْشِرُوْنَ ","bc":"amittakhażū ālihatam minal-arḍi hum yunsyirụn","at":"Apakah mereka mengambil tuhan-tuhan dari bumi, yang dapat menghidupkan (orang-orang yang mati)?"},{"id":"22","ar":"لَوْ كَانَ فِيْهِمَآ اٰلِهَةٌ اِلَّا اللّٰهُ لَفَسَدَتَاۚ فَسُبْحٰنَ اللّٰهِ رَبِّ الْعَرْشِ عَمَّا يَصِفُوْنَ ","bc":"lau kāna fīhimā ālihatun illallāhu lafasadatā, fa sub-ḥānallāhi rabbil-'arsyi 'ammā yaṣifụn","at":"Seandainya pada keduanya (di langit dan di bumi) ada tuhan-tuhan selain Allah, tentu keduanya telah binasa. Mahasuci Allah yang memiliki ‘Arsy, dari apa yang mereka sifatkan."},{"id":"23","ar":"لَا يُسْـَٔلُ عَمَّا يَفْعَلُ وَهُمْ يُسْـَٔلُوْنَ ","bc":"lā yus`alu 'ammā yaf'alu wa hum yus`alụn","at":"Dia (Allah) tidak ditanya tentang apa yang dikerjakan, tetapi merekalah yang akan ditanya."},{"id":"24","ar":"اَمِ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةً ۗقُلْ هَاتُوْا بُرْهَانَكُمْۚ هٰذَا ذِكْرُ مَنْ مَّعِيَ وَذِكْرُ مَنْ قَبْلِيْۗ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَۙ الْحَقَّ فَهُمْ مُّعْرِضُوْنَ ","bc":"amittakhażụ min dụnihī ālihah, qul hātụ bur-hānakum, hāżā żikru mam ma'iya wa żikru mang qablī, bal akṡaruhum lā ya'lamụnal-ḥaqqa fa hum mu'riḍụn","at":"Atau apakah mereka mengambil tuhan-tuhan selain Dia? Katakanlah (Muhammad), “Kemukakanlah alasan-alasanmu! (Al-Qur'an) ini adalah peringatan bagi orang yang bersamaku, dan peringatan bagi orang sebelumku.” Tetapi kebanyakan mereka tidak mengetahui yang hak (kebenaran), karena itu mereka berpaling."},{"id":"25","ar":"وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَّسُوْلٍ اِلَّا نُوْحِيْٓ اِلَيْهِ اَنَّهٗ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاعْبُدُوْنِ ","bc":"wa mā arsalnā ming qablika mir rasụlin illā nụḥī ilaihi annahụ lā ilāha illā ana fa'budụn","at":"Dan Kami tidak mengutus seorang rasul pun sebelum engkau (Muhammad), melainkan Kami wahyukan kepadanya, bahwa tidak ada tuhan (yang berhak disembah) selain Aku, maka sembahlah Aku."},{"id":"26","ar":"وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَدًا سُبْحٰنَهٗ ۗبَلْ عِبَادٌ مُّكْرَمُوْنَ ۙ","bc":"wa qāluttakhażar-raḥmānu waladan sub-ḥānah, bal 'ibādum mukramụn","at":"Dan mereka berkata, “Tuhan Yang Maha Pengasih telah menjadikan (malaikat) sebagai anak.” Mahasuci Dia. Sebenarnya mereka (para malaikat itu) adalah hamba-hamba yang dimuliakan,"},{"id":"27","ar":"لَا يَسْبِقُوْنَهٗ بِالْقَوْلِ وَهُمْ بِاَمْرِهٖ يَعْمَلُوْنَ ","bc":"lā yasbiqụnahụ bil-qauli wa hum bi`amrihī ya'malụn","at":"mereka tidak berbicara mendahului-Nya dan mereka mengerjakan perintah-perintah-Nya."},{"id":"28","ar":"يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ وَلَا يَشْفَعُوْنَۙ اِلَّا لِمَنِ ارْتَضٰى وَهُمْ مِّنْ خَشْيَتِهٖ مُشْفِقُوْنَ ","bc":"ya'lamu mā baina aidīhim wa mā khalfahum wa lā yasyfa'ụna illā limanirtaḍā wa hum min khasy-yatihī musyfiqụn","at":"Dia (Allah) mengetahui segala sesuatu yang di hadapan mereka (malaikat) dan yang di belakang mereka, dan mereka tidak memberi syafaat melainkan kepada orang yang diridai (Allah), dan mereka selalu berhati-hati karena takut kepada-Nya."},{"id":"29","ar":"۞ وَمَنْ يَّقُلْ مِنْهُمْ اِنِّيْٓ اِلٰهٌ مِّنْ دُوْنِهٖ فَذٰلِكَ نَجْزِيْهِ جَهَنَّمَۗ كَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ","bc":"wa may yaqul min-hum innī ilāhum min dụnihī fa żālika najzīhi jahannam, każālika najziẓ-ẓālimīn","at":"Dan barangsiapa di antara mereka berkata, “Sungguh, aku adalah tuhan selain Allah,” maka orang itu Kami beri balasan dengan Jahanam. Demikianlah Kami memberikan balasan kepada orang-orang yang zalim."},{"id":"30","ar":"اَوَلَمْ يَرَ الَّذِيْنَ كَفَرُوْٓا اَنَّ السَّمٰوٰتِ وَالْاَرْضَ كَانَتَا رَتْقًا فَفَتَقْنٰهُمَاۗ وَجَعَلْنَا مِنَ الْمَاۤءِ كُلَّ شَيْءٍ حَيٍّۗ اَفَلَا يُؤْمِنُوْنَ ","bc":"a wa lam yarallażīna kafarū annas-samāwāti wal-arḍa kānatā ratqan fa fataqnāhumā, wa ja'alnā minal-mā`i kulla syai`in ḥayy, a fa lā yu`minụn","at":"Dan apakah orang-orang kafir tidak mengetahui bahwa langit dan bumi keduanya dahulunya menyatu, kemudian Kami pisahkan antara keduanya; dan Kami jadikan segala sesuatu yang hidup berasal dari air; maka mengapa mereka tidak beriman?"},{"id":"31","ar":"وَجَعَلْنَا فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِهِمْۖ وَجَعَلْنَا فِيْهَا فِجَاجًا سُبُلًا لَّعَلَّهُمْ يَهْتَدُوْنَ ","bc":"wa ja'alnā fil-arḍi rawāsiya an tamīda bihim wa ja'alnā fīhā fijājan subulal la'allahum yahtadụn","at":"Dan Kami telah menjadikan di bumi ini gunung-gunung yang kokoh agar ia (tidak) guncang bersama mereka, dan Kami jadikan (pula) di sana jalan-jalan yang luas, agar mereka mendapat petunjuk."},{"id":"32","ar":"وَجَعَلْنَا السَّمَاۤءَ سَقْفًا مَّحْفُوْظًاۚ وَهُمْ عَنْ اٰيٰتِهَا مُعْرِضُوْنَ ","bc":"wa ja'alnas-samā`a saqfam maḥfụẓā, wa hum 'an āyātihā mu'riḍụn","at":"Dan Kami menjadikan langit sebagai atap yang terpelihara, namun mereka tetap berpaling dari tanda-tanda (kebesaran Allah) itu (matahari, bulan, angin, awan, dan lain-lain)."},{"id":"33","ar":"وَهُوَ الَّذِيْ خَلَقَ الَّيْلَ وَالنَّهَارَ وَالشَّمْسَ وَالْقَمَرَۗ كُلٌّ فِيْ فَلَكٍ يَّسْبَحُوْنَ ","bc":"wa huwallażī khalaqal-laila wan-nahāra wasy-syamsa wal-qamar, kullun fī falakiy yasbaḥụn","at":"Dan Dialah yang telah menciptakan malam dan siang, matahari dan bulan. Masing-masing beredar pada garis edarnya."},{"id":"34","ar":"وَمَا جَعَلْنَا لِبَشَرٍ مِّنْ قَبْلِكَ الْخُلْدَۗ اَفَا۟ىِٕنْ مِّتَّ فَهُمُ الْخٰلِدُوْنَ ","bc":"wa mā ja'alnā libasyarim ming qablikal-khuld, a fā im mitta fa humul-khālidụn","at":"Dan Kami tidak menjadikan hidup abadi bagi seorang manusia sebelum engkau (Muhammad); maka jika engkau wafat, apakah mereka akan kekal?"},{"id":"35","ar":"كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ وَنَبْلُوْكُمْ بِالشَّرِّ وَالْخَيْرِ فِتْنَةً ۗوَاِلَيْنَا تُرْجَعُوْنَ ","bc":"kullu nafsin żā`iqatul-maụt, wa nablụkum bisy-syarri wal-khairi fitnah, wa ilainā turja'ụn","at":"Setiap yang bernyawa akan merasakan mati. Kami akan menguji kamu dengan keburukan dan kebaikan sebagai cobaan. Dan kamu akan dikembalikan hanya kepada Kami."},{"id":"36","ar":"وَاِذَا رَاٰكَ الَّذِيْنَ كَفَرُوْٓا اِنْ يَّتَّخِذُوْنَكَ اِلَّا هُزُوًاۗ اَهٰذَا الَّذِيْ يَذْكُرُ اٰلِهَتَكُمْۚ وَهُمْ بِذِكْرِ الرَّحْمٰنِ هُمْ كٰفِرُوْنَ ","bc":"wa iżā ra`ākallażīna kafarū iy yattakhiżụnaka illā huzuwā, a hāżallażī yażkuru ālihatakum, wa hum biżikrir-raḥmāni hum kāfirụn","at":"Dan apabila orang-orang kafir itu melihat engkau (Muhammad), mereka hanya memperlakukan engkau menjadi bahan ejekan. (Mereka mengatakan), “Apakah ini orang yang mencela tuhan-tuhanmu?” Padahal mereka orang yang ingkar mengingat Allah Yang Maha Pengasih."},{"id":"37","ar":"خُلِقَ الْاِنْسَانُ مِنْ عَجَلٍۗ سَاُورِيْكُمْ اٰيٰتِيْ فَلَا تَسْتَعْجِلُوْنِ ","bc":"khuliqal-insānu min 'ajal, sa`urīkum āyātī fa lā tasta'jilụn","at":"Manusia diciptakan (bersifat) tergesa-gesa. Kelak akan Aku perlihatkan kepadamu tanda-tanda (kekuasaan)-Ku. Maka janganlah kamu meminta Aku menyegerakannya."},{"id":"38","ar":"وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"wa yaqụlụna matā hāżal-wa'du ing kuntum ṣādiqīn","at":"Dan mereka berkata, “Kapankah janji itu (akan datang), jika kamu orang yang benar?”"},{"id":"39","ar":"لَوْ يَعْلَمُ الَّذِيْنَ كَفَرُوْا حِيْنَ لَا يَكُفُّوْنَ عَنْ وُّجُوْهِهِمُ النَّارَ وَلَا عَنْ ظُهُوْرِهِمْ وَلَا هُمْ يُنْصَرُوْنَ ","bc":"lau ya'lamullażīna kafarụ ḥīna lā yakuffụna 'aw wujụhihimun-nāra wa lā 'an ẓuhụrihim wa lā hum yunṣarụn","at":"Seandainya orang kafir itu mengetahui, ketika mereka itu tidak mampu mengelakkan api neraka dari wajah dan punggung mereka, sedang mereka tidak mendapat pertolongan (tentulah mereka tidak meminta disegerakan)."},{"id":"40","ar":"بَلْ تَأْتِيْهِمْ بَغْتَةً فَتَبْهَتُهُمْ فَلَا يَسْتَطِيْعُوْنَ رَدَّهَا وَلَا هُمْ يُنْظَرُوْنَ ","bc":"bal ta`tīhim bagtatan fa tab-hatuhum fa lā yastaṭī'ụna raddahā wa lā hum yunẓarụn","at":" Sebenarnya (hari Kiamat) itu akan datang kepada mereka secara tiba-tiba, lalu mereka menjadi panik; maka mereka tidak sanggup menolaknya dan tidak (pula) diberi penangguhan (waktu)."},{"id":"41","ar":"وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَحَاقَ بِالَّذِيْنَ سَخِرُوْا مِنْهُمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ","bc":"wa laqadistuhzi`a birusulim ming qablika fa ḥāqa billażīna sakhirụ min-hum mā kānụ bihī yastahzi`ụn","at":"Dan sungguh, rasul-rasul sebelum engkau (Muhammad) pun telah diperolok-olokkan, maka turunlah (siksaan) kepada orang-orang yang mencemoohkan apa (rasul-rasul) yang selalu mereka perolok-olokkan."},{"id":"42","ar":"قُلْ مَنْ يَّكْلَؤُكُمْ بِالَّيْلِ وَالنَّهَارِ مِنَ الرَّحْمٰنِۗ بَلْ هُمْ عَنْ ذِكْرِ رَبِّهِمْ مُّعْرِضُوْنَ ","bc":"qul may yakla`ukum bil-laili wan-nahāri minar-raḥmān, bal hum 'an żikri rabbihim mu'riḍụn","at":"Katakanlah, “Siapakah yang akan menjaga kamu pada waktu malam dan siang dari (siksaan) Allah Yang Maha Pengasih?” Tetapi mereka enggan mengingat Tuhan mereka."},{"id":"43","ar":"اَمْ لَهُمْ اٰلِهَةٌ تَمْنَعُهُمْ مِّنْ دُوْنِنَاۗ لَا يَسْتَطِيْعُوْنَ نَصْرَ اَنْفُسِهِمْ وَلَا هُمْ مِّنَّا يُصْحَبُوْنَ","bc":"am lahum ālihatun tamna'uhum min dụninā, lā yastaṭī'ụna naṣra anfusihim wa lā hum minnā yuṣ-ḥabụn","at":"Ataukah mereka mempunyai tuhan-tuhan yang dapat memelihara mereka dari (azab) Kami? Tuhan-tuhan mereka itu tidak sanggup menolong diri mereka sendiri dan tidak (pula) mereka dilindungi dari (azab) Kami."},{"id":"44","ar":"بَلْ مَتَّعْنَا هٰٓؤُلَاۤءِ وَاٰبَاۤءَهُمْ حَتّٰى طَالَ عَلَيْهِمُ الْعُمُرُۗ اَفَلَا يَرَوْنَ اَنَّا نَأْتِى الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۗ اَفَهُمُ الْغٰلِبُوْنَ ","bc":"bal matta'nā hā`ulā`i wa ābā`ahum ḥattā ṭāla 'alaihimul-'umur, a fa lā yarauna annā na`til-arḍa nangquṣuhā min aṭrāfihā, a fa humul-gālibụn","at":"Sebenarnya Kami telah memberi mereka dan nenek moyang mereka kenikmatan (hidup di dunia) hingga panjang usia mereka. Maka apakah mereka tidak melihat bahwa Kami mendatangi negeri (yang berada di bawah kekuasaan orang kafir), lalu Kami kurangi luasnya dari ujung-ujung negeri. Apakah mereka yang menang?"},{"id":"45","ar":"قُلْ اِنَّمَآ اُنْذِرُكُمْ بِالْوَحْيِۖ وَلَا يَسْمَعُ الصُّمُّ الدُّعَاۤءَ اِذَا مَا يُنْذَرُوْنَ ","bc":"qul innamā unżirukum bil-waḥyi wa lā yasma'uṣ-ṣummud-du'ā`a iżā mā yunżarụn","at":"Katakanlah (Muhammad), “Sesungguhnya aku hanya memberimu peringatan sesuai dengan wahyu.” Tetapi orang tuli tidak mendengar seruan apabila mereka diberi peringatan."},{"id":"46","ar":"وَلَىِٕنْ مَّسَّتْهُمْ نَفْحَةٌ مِّنْ عَذَابِ رَبِّكَ لَيَقُوْلُنَّ يٰوَيْلَنَآ اِنَّا كُنَّا ظٰلِمِيْنَ ","bc":"wa la`im massat-hum naf-ḥatum min 'ażābi rabbika layaqụlunna yā wailanā innā kunnā ẓālimīn","at":"Dan jika mereka ditimpa sedikit saja azab Tuhanmu, pastilah mereka berkata, “Celakalah kami! Sesungguhnya kami termasuk orang yang selalu menzalimi (diri sendiri).”"},{"id":"47","ar":"وَنَضَعُ الْمَوَازِيْنَ الْقِسْطَ لِيَوْمِ الْقِيٰمَةِ فَلَا تُظْلَمُ نَفْسٌ شَيْـًٔاۗ وَاِنْ كَانَ مِثْقَالَ حَبَّةٍ مِّنْ خَرْدَلٍ اَتَيْنَا بِهَاۗ وَكَفٰى بِنَا حَاسِبِيْنَ ","bc":"wa naḍa'ul-mawāzīnal-qisṭa liyaumil-qiyāmati fa lā tuẓlamu nafsun syai`ā, wa ing kāna miṡqāla ḥabbatim min khardalin atainā bihā, wa kafā binā ḥāsibīn","at":"Dan Kami akan memasang timbangan yang tepat pada hari Kiamat, maka tidak seorang pun dirugikan walau sedikit; sekalipun hanya seberat biji sawi, pasti Kami mendatangkannya (pahala). Dan cukuplah Kami yang membuat perhitungan."},{"id":"48","ar":"وَلَقَدْ اٰتَيْنَا مُوْسٰى وَهٰرُوْنَ الْفُرْقَانَ وَضِيَاۤءً وَّذِكْرًا لِّلْمُتَّقِيْنَ ۙ","bc":"wa laqad ātainā mụsā wa hārụnal-furqāna wa ḍiyā`aw wa żikral lil-muttaqīn","at":"Dan sungguh, Kami telah memberikan kepada Musa dan Harun, Furqan (Kitab Taurat) dan penerangan serta pelajaran bagi orang-orang yang bertakwa."},{"id":"49","ar":"الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَهُمْ مِّنَ السَّاعَةِ مُشْفِقُوْنَ ","bc":"allażīna yakhsyauna rabbahum bil-gaibi wa hum minas-sā'ati musyfiqụn","at":"(Yaitu) orang-orang yang takut (azab) Tuhannya, sekalipun mereka tidak melihat-Nya, dan mereka merasa takut akan (tibanya) hari Kiamat."},{"id":"50","ar":"وَهٰذَا ذِكْرٌ مُّبٰرَكٌ اَنْزَلْنٰهُۗ اَفَاَنْتُمْ لَهٗ مُنْكِرُوْنَ","bc":"wa hāżā żikrum mubārakun anzalnāh, a fa antum lahụ mungkirụn","at":"Dan ini (Al-Qur'an) adalah suatu peringatan yang mempunyai berkah yang telah Kami turunkan. Maka apakah kamu mengingkarinya?"},{"id":"51","ar":"۞ وَلَقَدْ اٰتَيْنَآ اِبْرٰهِيْمَ رُشْدَهٗ مِنْ قَبْلُ وَكُنَّا بِهٖ عٰلِمِيْنَ ","bc":"wa laqad ātainā ibrāhīma rusydahụ ming qablu wa kunnā bihī 'ālimīn","at":"Dan sungguh, sebelum dia (Musa dan Harun) telah Kami berikan kepada Ibrahim petunjuk, dan Kami telah mengetahui dia."},{"id":"52","ar":"اِذْ قَالَ لِاَبِيْهِ وَقَوْمِهٖ مَا هٰذِهِ التَّمَاثِيْلُ الَّتِيْٓ اَنْتُمْ لَهَا عَاكِفُوْنَ ","bc":"iż qāla li`abīhi wa qaumihī mā hāżihit-tamāṡīlullatī antum lahā 'ākifụn","at":"(Ingatlah), ketika dia (Ibrahim) berkata kepada ayahnya dan kaumnya, “Patung-patung apakah ini yang kamu tekun menyembahnya?”"},{"id":"53","ar":"قَالُوْا وَجَدْنَآ اٰبَاۤءَنَا لَهَا عٰبِدِيْنَ ","bc":"qālụ wajadnā ābā`anā lahā 'ābidīn","at":"Mereka menjawab, “Kami mendapati nenek moyang kami menyembahnya.”"},{"id":"54","ar":"قَالَ لَقَدْ كُنْتُمْ اَنْتُمْ وَاٰبَاۤؤُكُمْ فِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"qāla laqad kuntum antum wa ābā`ukum fī ḍalālim mubīn","at":"Dia (Ibrahim) berkata, “Sesungguhnya kamu dan nenek moyang kamu berada dalam kesesatan yang nyata.”"},{"id":"55","ar":"قَالُوْٓا اَجِئْتَنَا بِالْحَقِّ اَمْ اَنْتَ مِنَ اللّٰعِبِيْنَ ","bc":"qālū a ji`tanā bil-ḥaqqi am anta minal-lā'ibīn","at":"Mereka berkata, “Apakah engkau da-tang kepada kami membawa kebenaran atau engkau main-main?”"},{"id":"56","ar":"قَالَ بَلْ رَّبُّكُمْ رَبُّ السَّمٰوٰتِ وَالْاَرْضِ الَّذِيْ فَطَرَهُنَّۖ وَاَنَا۠ عَلٰى ذٰلِكُمْ مِّنَ الشّٰهِدِيْنَ ","bc":"qāla bar rabbukum rabbus-samāwāti wal-arḍillażī faṭarahunna wa ana 'alā żālikum minasy-syāhidīn","at":"Dia (Ibrahim) menjawab, “Sebenarnya Tuhan kamu ialah Tuhan (pemilik) langit dan bumi; (Dialah) yang telah menciptakannya; dan aku termasuk orang yang dapat bersaksi atas itu.”"},{"id":"57","ar":"وَتَاللّٰهِ لَاَكِيْدَنَّ اَصْنَامَكُمْ بَعْدَ اَنْ تُوَلُّوْا مُدْبِرِيْنَ ","bc":"wa tallāhi la`akīdanna aṣnāmakum ba'da an tuwallụ mudbirīn","at":"Dan demi Allah, sungguh, aku akan melakukan tipu daya terhadap berhala-berhalamu setelah kamu pergi meninggalkannya."},{"id":"58","ar":"فَجَعَلَهُمْ جُذَاذًا اِلَّا كَبِيْرًا لَّهُمْ لَعَلَّهُمْ اِلَيْهِ يَرْجِعُوْنَ ","bc":"fa ja'alahum jużāżan illā kabīral lahum la'allahum ilaihi yarji'ụn","at":"Maka dia (Ibrahim) menghancurkan (berhala-berhala itu) berkeping-keping, kecuali yang terbesar (induknya); agar mereka kembali (untuk bertanya) kepadanya."},{"id":"59","ar":"قَالُوْا مَنْ فَعَلَ هٰذَا بِاٰلِهَتِنَآ اِنَّهٗ لَمِنَ الظّٰلِمِيْنَ ","bc":"qālụ man fa'ala hāżā bi`ālihatinā innahụ laminaẓ-ẓālimīn","at":"Mereka berkata, “Siapakah yang melakukan (perbuatan) ini terhadap tuhan-tuhan kami? Sungguh, dia termasuk orang yang zalim.”"},{"id":"60","ar":"قَالُوْا سَمِعْنَا فَتًى يَّذْكُرُهُمْ يُقَالُ لَهٗٓ اِبْرٰهِيْمُ ۗ","bc":"qālụ sami'nā fatay yażkuruhum yuqālu lahū ibrāhīm","at":"Mereka (yang lain) berkata, “Kami mendengar ada seorang pemuda yang mencela (berhala-berhala ini), namanya Ibrahim.”"},{"id":"61","ar":"قَالُوْا فَأْتُوْا بِهٖ عَلٰٓى اَعْيُنِ النَّاسِ لَعَلَّهُمْ يَشْهَدُوْنَ ","bc":"qālụ fa`tụ bihī 'alā a'yunin-nāsi la'allahum yasy-hadụn","at":"Mereka berkata, “(Kalau demikian) bawalah dia dengan diperlihatkan kepada orang banyak, agar mereka menyaksikan.”"},{"id":"62","ar":"قَالُوْٓا ءَاَنْتَ فَعَلْتَ هٰذَا بِاٰلِهَتِنَا يٰٓاِبْرٰهِيْمُ ۗ","bc":"qālū a anta fa'alta hāżā bi`ālihatinā yā ibrāhīm","at":" Mereka bertanya, “Apakah engkau yang melakukan (perbuatan) ini terhadap tuhan-tuhan kami, wahai Ibrahim?”"},{"id":"63","ar":"قَالَ بَلْ فَعَلَهٗ كَبِيْرُهُمْ هٰذَا فَسْـَٔلُوْهُمْ اِنْ كَانُوْا يَنْطِقُوْنَ ","bc":"qāla bal fa'alahụ kabīruhum hāżā fas`alụhum ing kānụ yanṭiqụn","at":"Dia (Ibrahim) menjawab, “Sebenarnya (patung) besar itu yang melakukannya, maka tanyakanlah kepada mereka, jika mereka dapat berbicara.”"},{"id":"64","ar":"فَرَجَعُوْٓا اِلٰٓى اَنْفُسِهِمْ فَقَالُوْٓا اِنَّكُمْ اَنْتُمُ الظّٰلِمُوْنَ ۙ","bc":"fa raja'ū ilā anfusihim fa qālū innakum antumuẓ-ẓālimụn","at":"Maka mereka kembali kepada kesadaran mereka dan berkata, “Sesungguhnya kamulah yang menzalimi (diri sendiri).”"},{"id":"65","ar":"ثُمَّ نُكِسُوْا عَلٰى رُءُوْسِهِمْۚ لَقَدْ عَلِمْتَ مَا هٰٓؤُلَاۤءِ يَنْطِقُوْنَ ","bc":"ṡumma nukisụ 'alā ru`ụsihim, laqad 'alimta mā hā`ulā`i yanṭiqụn","at":"Kemudian mereka menundukkan kepala (lalu berkata), “Engkau (Ibrahim) pasti tahu bahwa (berhala-berhala) itu tidak dapat berbicara.”"},{"id":"66","ar":"قَالَ اَفَتَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُكُمْ شَيْـًٔا وَّلَا يَضُرُّكُمْ ۗ","bc":"qāla a fa ta'budụna min dụnillāhi mā lā yanfa'ukum syai`aw wa lā yaḍurrukum","at":"Dia (Ibrahim) berkata, “Mengapa kamu menyembah selain Allah, sesuatu yang tidak dapat memberi manfaat sedikit pun, dan tidak (pula) mendatangkan mudarat kepada kamu?"},{"id":"67","ar":"اُفٍّ لَّكُمْ وَلِمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ ۗاَفَلَا تَعْقِلُوْنَ ","bc":"uffil lakum wa limā ta'budụna min dụnillāh, a fa lā ta'qilụn","at":"Celakalah kamu dan apa yang kamu sembah selain Allah! Tidakkah kamu mengerti?”"},{"id":"68","ar":"قَالُوْا حَرِّقُوْهُ وَانْصُرُوْٓا اٰلِهَتَكُمْ اِنْ كُنْتُمْ فٰعِلِيْنَ ","bc":"qālụ ḥarriqụhu wanṣurū ālihatakum ing kuntum fā'ilīn","at":" Mereka berkata, “Bakarlah dia dan bantulah tuhan-tuhan kamu, jika kamu benar-benar hendak berbuat.”"},{"id":"69","ar":"قُلْنَا يَا نَارُ كُوْنِيْ بَرْدًا وَّسَلٰمًا عَلٰٓى اِبْرٰهِيْمَ ۙ","bc":"qulnā yā nāru kụnī bardaw wa salāman 'alā ibrāhīm","at":"Kami (Allah) berfirman, “Wahai api! Jadilah kamu dingin, dan penyelamat bagi Ibrahim!”"},{"id":"70","ar":"وَاَرَادُوْا بِهٖ كَيْدًا فَجَعَلْنٰهُمُ الْاَخْسَرِيْنَ ۚ","bc":"wa arādụ bihī kaidan fa ja'alnāhumul-akhsarīn","at":"Dan mereka hendak berbuat jahat terhadap Ibrahim, maka Kami menjadikan mereka itu orang-orang yang paling rugi."},{"id":"71","ar":"وَنَجَّيْنٰهُ وَلُوْطًا اِلَى الْاَرْضِ الَّتِيْ بٰرَكْناَ فِيْهَا لِلْعٰلَمِيْنَ ","bc":"wa najjaināhu wa lụṭan ilal-arḍillatī bāraknā fīhā lil-'ālamīn","at":"Dan Kami selamatkan dia (Ibrahim) dan Lut ke sebuah negeri yang telah Kami berkahi untuk seluruh alam."},{"id":"72","ar":"وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَ نَافِلَةً ۗوَكُلًّا جَعَلْنَا صٰلِحِيْنَ ","bc":"wa wahabnā lahū is-ḥāqa wa ya'qụba nāfilah, wa kullan ja'alnā ṣāliḥīn","at":"Dan Kami menganugerahkan kepadanya (Ibrahim) Ishak dan Yakub, sebagai suatu anugerah. Dan masing-masing Kami jadikan orang yang saleh."},{"id":"73","ar":"وَجَعَلْنٰهُمْ اَىِٕمَّةً يَّهْدُوْنَ بِاَمْرِنَا وَاَوْحَيْنَآ اِلَيْهِمْ فِعْلَ الْخَيْرٰتِ وَاِقَامَ الصَّلٰوةِ وَاِيْتَاۤءَ الزَّكٰوةِۚ وَكَانُوْا لَنَا عٰبِدِيْنَ ۙ","bc":"wa ja'alnāhum a`immatay yahdụna bi`amrinā wa auḥainā ilaihim fi'lal-khairāti wa iqāmaṣ-ṣalāti wa ītā`az-zakāh, wa kānụ lanā 'ābidīn","at":"Dan Kami menjadikan mereka itu sebagai pemimpin-pemimpin yang memberi petunjuk dengan perintah Kami dan Kami wahyukan kepada mereka agar berbuat kebaikan, melaksanakan salat dan menunaikan zakat, dan hanya kepada Kami mereka menyembah."},{"id":"74","ar":"وَلُوْطًا اٰتَيْنٰهُ حُكْمًا وَّعِلْمًا وَّنَجَّيْنٰهُ مِنَ الْقَرْيَةِ الَّتِيْ كَانَتْ تَّعْمَلُ الْخَبٰۤىِٕثَ ۗاِنَّهُمْ كَانُوْا قَوْمَ سَوْءٍ فٰسِقِيْنَۙ","bc":"wa lụṭan ātaināhu ḥukmaw wa 'ilmaw wa najjaināhu minal-qaryatillatī kānat ta'malul-khabā`iṡ, innahum kānụ qauma sau`in fāsiqīn","at":"Kepada Lut, Kami berikan hikmah dan ilmu, dan Kami selamatkan dia dari (azab yang telah menimpa penduduk) kota yang melakukan perbuatan keji. Sungguh, mereka orang-orang yang jahat lagi fasik. "},{"id":"75","ar":"وَاَدْخَلْنٰهُ فِيْ رَحْمَتِنَاۗ اِنَّهٗ مِنَ الصّٰلِحِيْنَ ","bc":"wa adkhalnāhu fī raḥmatinā, innahụ minaṣ-ṣāliḥīn","at":" Dan Kami masukkan dia ke dalam rahmat Kami; sesungguhnya dia termasuk golongan orang yang saleh."},{"id":"76","ar":"وَنُوْحًا اِذْ نَادٰى مِنْ قَبْلُ فَاسْتَجَبْنَا لَهٗ فَنَجَّيْنٰهُ وَاَهْلَهٗ مِنَ الْكَرْبِ الْعَظِيْمِ ۚ","bc":"wa nụḥan iż nādā ming qablu fastajabnā lahụ fa najjaināhu wa ahlahụ minal-karbil-'aẓīm","at":"Dan (ingatlah kisah) Nuh, sebelum itu, ketika dia berdoa. Kami perkenankan (doa)nya, lalu Kami selamatkan dia bersama pengikutnya dari bencana yang besar."},{"id":"77","ar":"وَنَصَرْنٰهُ مِنَ الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ اِنَّهُمْ كَانُوْا قَوْمَ سَوْءٍ فَاَغْرَقْنٰهُمْ اَجْمَعِيْنَ ","bc":"wa naṣarnāhu minal-qaumillażīna każżabụ bi`āyātinā, innahum kānụ qauma sau`in fa agraqnāhum ajma'īn","at":" Dan Kami menolongnya dari orang-orang yang telah mendustakan ayat-ayat Kami. Sesungguhnya mereka adalah orang-orang yang jahat, maka Kami tenggelamkan mereka semuanya."},{"id":"78","ar":"وَدَاوٗدَ وَسُلَيْمٰنَ اِذْ يَحْكُمٰنِ فِى الْحَرْثِ اِذْ نَفَشَتْ فِيْهِ غَنَمُ الْقَوْمِۚ وَكُنَّا لِحُكْمِهِمْ شٰهِدِيْنَ ۖ","bc":"wa dāwụda wa sulaimāna iż yaḥkumāni fil-ḥarṡi iż nafasyat fīhi ganamul-qaụm, wa kunnā liḥukmihim syāhidīn","at":" Dan (ingatlah kisah) Dawud dan Sulaiman, ketika keduanya memberikan keputusan mengenai ladang, karena (ladang itu) dirusak oleh kambing-kambing milik kaumnya. Dan Kami menyaksikan keputusan (yang diberikan) oleh mereka itu."},{"id":"79","ar":"فَفَهَّمْنٰهَا سُلَيْمٰنَۚ وَكُلًّا اٰتَيْنَا حُكْمًا وَّعِلْمًاۖ وَّسَخَّرْنَا مَعَ دَاوٗدَ الْجِبَالَ يُسَبِّحْنَ وَالطَّيْرَۗ وَكُنَّا فٰعِلِيْنَ","bc":"fa fahhamnāhā sulaimān, wa kullan ātainā ḥukmaw wa 'ilmaw wa sakhkharnā ma'a dāwụdal-jibāla yusabbiḥna waṭ-ṭaīr, wa kunnā fā'ilīn","at":" Dan Kami memberikan pengertian kepada Sulaiman (tentang hukum yang lebih tepat); dan kepada masing-masing Kami berikan hikmah dan ilmu, dan Kami tundukkan gunung-gunung dan burung-burung, semua bertasbih bersama Dawud. Dan Kamilah yang melakukannya."},{"id":"80","ar":"وَعَلَّمْنٰهُ صَنْعَةَ لَبُوْسٍ لَّكُمْ لِتُحْصِنَكُمْ مِّنْۢ بَأْسِكُمْۚ فَهَلْ اَنْتُمْ شَاكِرُوْنَ","bc":"wa 'allamnāhu ṣan'ata labụsil lakum lituhṣinakum mim ba`sikum, fa hal antum syākirụn","at":"Dan Kami ajarkan (pula) kepada Dawud cara membuat baju besi untukmu, guna melindungi kamu dalam peperangan. Apakah kamu bersyukur (kepada Allah)?"},{"id":"81","ar":"وَلِسُلَيْمٰنَ الرِّيْحَ عَاصِفَةً تَجْرِيْ بِاَمْرِهٖٓ اِلَى الْاَرْضِ الَّتِيْ بٰرَكْنَا فِيْهَاۗ وَكُنَّا بِكُلِّ شَيْءٍ عٰلِمِيْنَ","bc":"wa lisulaimānar-rīḥa 'āṣifatan tajrī bi`amrihī ilal-arḍillatī bāraknā fīhā, wa kunnā bikulli syai`in 'ālimīn","at":"Dan (Kami tundukkan) untuk Sulaiman angin yang sangat kencang tiupannya yang berhembus dengan perintahnya ke negeri yang Kami beri berkah padanya. Dan Kami Maha Mengetahui segala sesuatu."},{"id":"82","ar":"وَمِنَ الشَّيٰطِيْنِ مَنْ يَّغُوْصُوْنَ لَهٗ وَيَعْمَلُوْنَ عَمَلًا دُوْنَ ذٰلِكَۚ وَكُنَّا لَهُمْ حٰفِظِيْنَ ۙ","bc":"wa minasy-syayāṭīni may yagụṣụna lahụ wa ya'malụna 'amalan dụna żālik, wa kunnā lahum ḥāfiẓīn","at":"Dan (Kami tundukkan pula kepada Sulaiman) segolongan setan-setan yang menyelam (ke dalam laut) untuknya dan mereka mengerjakan pekerjaan selain itu; dan Kami yang memelihara mereka itu."},{"id":"83","ar":"۞ وَاَيُّوْبَ اِذْ نَادٰى رَبَّهٗٓ اَنِّيْ مَسَّنِيَ الضُّرُّ وَاَنْتَ اَرْحَمُ الرَّاحِمِيْنَ ۚ","bc":"wa ayyụba iż nādā rabbahū annī massaniyaḍ-ḍurru wa anta ar-ḥamur-rāḥimīn","at":"Dan (ingatlah kisah) Ayub, ketika dia berdoa kepada Tuhannya, “(Ya Tuhanku), sungguh, aku telah ditimpa penyakit, padahal Engkau Tuhan Yang Maha Penyayang dari semua yang penyayang.”"},{"id":"84","ar":"فَاسْتَجَبْنَا لَهٗ فَكَشَفْنَا مَا بِهٖ مِنْ ضُرٍّ وَّاٰتَيْنٰهُ اَهْلَهٗ وَمِثْلَهُمْ مَّعَهُمْ رَحْمَةً مِّنْ عِنْدِنَا وَذِكْرٰى لِلْعٰبِدِيْنَ ۚ","bc":"fastajabnā lahụ fa kasyafnā mā bihī min ḍurriw wa ātaināhu ahlahụ wa miṡlahum ma'ahum raḥmatam min 'indinā wa żikrā lil-'ābidīn","at":"Maka Kami kabulkan (doa)nya, lalu Kami lenyapkan penyakit yang ada padanya dan Kami kembalikan keluarganya kepadanya, dan (Kami lipat gandakan jumlah mereka) sebagai suatu rahmat dari Kami, dan untuk menjadi peringatan bagi semua yang menyembah Kami."},{"id":"85","ar":"وَاِسْمٰعِيْلَ وَاِدْرِيْسَ وَذَا الْكِفْلِۗ كُلٌّ مِّنَ الصّٰبِرِيْنَ ۙ","bc":"wa ismā'īla wa idrīsa wa żal-kifl, kullum minaṣ-ṣābirīn","at":" Dan (ingatlah kisah) Ismail, Idris dan Zulkifli. Mereka semua termasuk orang-orang yang sabar."},{"id":"86","ar":"وَاَدْخَلْنٰهُمْ فِيْ رَحْمَتِنَاۗ اِنَّهُمْ مِّنَ الصّٰلِحِيْنَ ","bc":"wa adkhalnāhum fī raḥmatinā, innahum minaṣ-ṣāliḥīn","at":"Dan Kami masukkan mereka ke dalam rahmat Kami. Sungguh, mereka termasuk orang-orang yang saleh."},{"id":"87","ar":"وَذَا النُّوْنِ اِذْ ذَّهَبَ مُغَاضِبًا فَظَنَّ اَنْ لَّنْ نَّقْدِرَ عَلَيْهِ فَنَادٰى فِى الظُّلُمٰتِ اَنْ لَّآ اِلٰهَ اِلَّآ اَنْتَ سُبْحٰنَكَ اِنِّيْ كُنْتُ مِنَ الظّٰلِمِيْنَ ۚ","bc":"wa żan-nụni iż żahaba mugāḍiban fa ẓanna al lan naqdira 'alaihi fa nādā fiẓ-ẓulumāti al lā ilāha illā anta sub-ḥānaka innī kuntu minaẓ-ẓālimīn","at":"Dan (ingatlah kisah) Zun Nun (Yunus), ketika dia pergi dalam keadaan marah, lalu dia menyangka bahwa Kami tidak akan menyulitkannya, maka dia berdoa dalam keadaan yang sangat gelap, ”Tidak ada tuhan selain Engkau, Mahasuci Engkau. Sungguh, aku termasuk orang-orang yang zalim.”"},{"id":"88","ar":"فَاسْتَجَبْنَا لَهٗۙ وَنَجَّيْنٰهُ مِنَ الْغَمِّۗ وَكَذٰلِكَ نُـْۨجِى الْمُؤْمِنِيْنَ ","bc":"fastajabnā lahụ wa najjaināhu minal-gamm, wa każālika nunjil-mu`minīn","at":"Maka Kami kabulkan (doa)nya dan Kami selamatkan dia dari kedukaan. Dan demikianlah Kami menyelamatkan orang-orang yang beriman."},{"id":"89","ar":"وَزَكَرِيَّآ اِذْ نَادٰى رَبَّهٗ رَبِّ لَا تَذَرْنِيْ فَرْدًا وَّاَنْتَ خَيْرُ الْوٰرِثِيْنَ ۚ","bc":"wa zakariyyā iż nādā rabbahụ rabbi lā tażarnī fardaw wa anta khairul-wāriṡīn","at":" Dan (ingatlah kisah) Zakaria, ketika dia berdoa kepada Tuhannya, “Ya Tuhanku, janganlah Engkau biarkan aku hidup seorang diri (tanpa keturunan) dan Engkaulah ahli waris yang terbaik."},{"id":"90","ar":"فَاسْتَجَبْنَا لَهٗ ۖوَوَهَبْنَا لَهٗ يَحْيٰى وَاَصْلَحْنَا لَهٗ زَوْجَهٗۗ اِنَّهُمْ كَانُوْا يُسٰرِعُوْنَ فِى الْخَيْرٰتِ وَيَدْعُوْنَنَا رَغَبًا وَّرَهَبًاۗ وَكَانُوْا لَنَا خٰشِعِيْنَ ","bc":"fastajabnā lahụ wa wahabnā lahụ yaḥyā wa aṣlaḥnā lahụ zaujah, innahum kānụ yusāri'ụna fil-khairāti wa yad'ụnanā ragabaw wa rahabā, wa kānụ lanā khāsyi'īn","at":"Maka Kami kabulkan (doa)nya, dan Kami anugerahkan kepadanya Yahya, dan Kami jadikan istrinya (dapat mengandung). Sungguh, mereka selalu bersegera dalam (mengerjakan) kebaikan, dan mereka berdoa kepada Kami dengan penuh harap dan cemas. Dan mereka orang-orang yang khusyuk kepada Kami."},{"id":"91","ar":"وَالَّتِيْٓ اَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيْهَا مِنْ رُّوْحِنَا وَجَعَلْنٰهَا وَابْنَهَآ اٰيَةً لِّلْعٰلَمِيْنَ ","bc":"wallatī aḥṣanat farjahā fa nafakhnā fīhā mir rụḥinā wa ja'alnāhā wabnahā āyatal lil-'ālamīn","at":"Dan (ingatlah kisah Maryam) yang memelihara kehormatannya, lalu Kami tiupkan (roh) dari Kami ke dalam (tubuh)nya; Kami jadikan dia dan anaknya sebagai tanda (kebesaran Allah) bagi seluruh alam."},{"id":"92","ar":"اِنَّ هٰذِهٖٓ اُمَّتُكُمْ اُمَّةً وَّاحِدَةًۖ وَّاَنَا۠ رَبُّكُمْ فَاعْبُدُوْنِ ","bc":"inna hāżihī ummatukum ummataw wāḥidataw wa ana rabbukum fa'budụn","at":"Sungguh, (agama tauhid) inilah agama kamu, agama yang satu, dan Aku adalah Tuhanmu, maka sembahlah Aku."},{"id":"93","ar":"وَتَقَطَّعُوْٓا اَمْرَهُمْ بَيْنَهُمْۗ كُلٌّ اِلَيْنَا رَاجِعُوْنَ","bc":"wa taqaṭṭa'ū amrahum bainahum, kullun ilainā rāji'ụn","at":"Tetapi mereka terpecah belah dalam urusan (agama) mereka di antara mereka. Masing-masing (golongan itu semua) akan kembali kepada Kami."},{"id":"94","ar":"فَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ وَهُوَ مُؤْمِنٌ فَلَا كُفْرَانَ لِسَعْيِهٖۚ وَاِنَّا لَهٗ كَاتِبُوْنَ ","bc":"fa may ya'mal minaṣ-ṣāliḥāti wa huwa mu`minun fa lā kufrāna lisa'yih, wa innā lahụ kātibụn","at":"Barangsiapa mengerjakan kebajikan, dan dia beriman, maka usahanya tidak akan diingkari (disia-siakan), dan sungguh, Kamilah yang mencatat untuknya."},{"id":"95","ar":"وَحَرَامٌ عَلٰى قَرْيَةٍ اَهْلَكْنٰهَآ اَنَّهُمْ لَا يَرْجِعُوْنَ ","bc":"wa ḥarāmun 'alā qaryatin ahlaknāhā annahum lā yarji'ụn","at":"Dan tidak mungkin bagi (penduduk) suatu negeri yang telah Kami binasakan, bahwa mereka tidak akan kembali (kepada Kami)."},{"id":"96","ar":"حَتّٰىٓ اِذَا فُتِحَتْ يَأْجُوْجُ وَمَأْجُوْجُ وَهُمْ مِّنْ كُلِّ حَدَبٍ يَّنْسِلُوْنَ ","bc":"ḥattā iżā futiḥat ya`jụju wa ma`jụju wa hum ming kulli ḥadabiy yansilụn","at":"Hingga apabila (tembok) Yakjuj dan Makjuj dibukakan dan mereka turun dengan cepat dari seluruh tempat yang tinggi."},{"id":"97","ar":"وَاقْتَرَبَ الْوَعْدُ الْحَقُّ فَاِذَا هِيَ شَاخِصَةٌ اَبْصَارُ الَّذِيْنَ كَفَرُوْاۗ يٰوَيْلَنَا قَدْ كُنَّا فِيْ غَفْلَةٍ مِّنْ هٰذَا بَلْ كُنَّا ظٰلِمِيْنَ ","bc":"waqtarabal-wa'dul-ḥaqqu fa iżā hiya syākhiṣatun abṣārullażīna kafarụ, yā wailanā qad kunnā fī gaflatim min hāżā bal kunnā ẓālimīn","at":"Dan (apabila) janji yang benar (hari berbangkit) telah dekat, maka tiba-tiba mata orang-orang yang kafir terbelalak. (Mereka berkata), ”Alangkah celakanya kami! Kami benar-benar lengah tentang ini, bahkan kami benar-benar orang yang zalim.”"},{"id":"98","ar":"اِنَّكُمْ وَمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ حَصَبُ جَهَنَّمَۗ اَنْتُمْ لَهَا وَارِدُوْنَ ","bc":"innakum wa mā ta'budụna min dụnillāhi ḥaṣabu jahannam, antum lahā wāridụn","at":"Sungguh, kamu (orang kafir) dan apa yang kamu sembah selain Allah, adalah bahan bakar Jahanam. Kamu (pasti) masuk ke dalamnya."},{"id":"99","ar":"لَوْ كَانَ هٰٓؤُلَاۤءِ اٰلِهَةً مَّا وَرَدُوْهَاۗ وَكُلٌّ فِيْهَا خٰلِدُوْنَ ","bc":"lau kāna hā`ulā`i ālihatam mā waradụhā, wa kullun fīhā khālidụn","at":"Seandainya (berhala-berhala) itu tuhan, tentu mereka tidak akan memasukinya (neraka). Tetapi semuanya akan kekal di dalamnya."},{"id":"100","ar":"لَهُمْ فِيْهَا زَفِيْرٌ وَّهُمْ فِيْهَا لَا يَسْمَعُوْنَ ","bc":"lahum fīhā zafīruw wa hum fīhā lā yasma'ụn","at":"Mereka merintih dan menjerit di dalamnya (neraka), dan mereka di dalamnya tidak dapat mendengar."},{"id":"101","ar":"اِنَّ الَّذِيْنَ سَبَقَتْ لَهُمْ مِّنَّا الْحُسْنٰىٓۙ اُولٰۤىِٕكَ عَنْهَا مُبْعَدُوْنَ ۙ","bc":"innallażīna sabaqat lahum minnal-ḥusnā ulā`ika 'an-hā mub'adụn","at":"Sungguh, sejak dahulu bagi orang-orang yang telah ada (ketetapan) yang baik dari Kami, mereka itu akan dijauhkan (dari neraka)."},{"id":"102","ar":"لَا يَسْمَعُوْنَ حَسِيْسَهَاۚ وَهُمْ فِيْ مَا اشْتَهَتْ اَنْفُسُهُمْ خٰلِدُوْنَ ۚ","bc":"lā yasma'ụna ḥasīsahā, wa hum fī masytahat anfusuhum khālidụn","at":"Mereka tidak mendengar bunyi desis (api neraka), dan mereka kekal dalam (menikmati) semua yang mereka ingini. "},{"id":"103","ar":"لَا يَحْزُنُهُمُ الْفَزَعُ الْاَكْبَرُ وَتَتَلَقّٰىهُمُ الْمَلٰۤىِٕكَةُۗ هٰذَا يَوْمُكُمُ الَّذِيْ كُنْتُمْ تُوْعَدُوْنَ ","bc":"lā yaḥzunuhumul-faza'ul-akbaru wa tatalaqqāhumul-malā`ikah, hāżā yaumukumullażī kuntum tụ'adụn","at":"Kejutan yang dahsyat tidak membuat mereka merasa sedih, dan para malaikat akan menyambut mereka (dengan ucapan), “Inilah harimu yang telah dijanjikan kepadamu.”"},{"id":"104","ar":"يَوْمَ نَطْوِى السَّمَاۤءَ كَطَيِّ السِّجِلِّ لِلْكُتُبِۗ كَمَا بَدَأْنَآ اَوَّلَ خَلْقٍ نُّعِيْدُهٗۗ وَعْدًا عَلَيْنَاۗ اِنَّا كُنَّا فٰعِلِيْنَ ","bc":"yauma naṭwis-samā`a kaṭayyis-sijilli lil-kutub, kamā bada`nā awwala khalqin nu'īduh, wa'dan 'alainā, innā kunnā fā'ilīn","at":"(Ingatlah) pada hari langit Kami gulung seperti menggulung lembaran-lembaran kertas. Sebagaimana Kami telah memulai penciptaan pertama, begitulah Kami akan mengulanginya lagi. (Suatu) janji yang pasti Kami tepati; sungguh, Kami akan melaksanakannya."},{"id":"105","ar":"وَلَقَدْ كَتَبْنَا فِى الزَّبُوْرِ مِنْۢ بَعْدِ الذِّكْرِ اَنَّ الْاَرْضَ يَرِثُهَا عِبَادِيَ الصّٰلِحُوْنَ ","bc":"wa laqad katabnā fiz-zabụri mim ba'diż-żikri annal-arḍa yariṡuhā 'ibādiyaṣ-ṣāliḥụn","at":"Dan sungguh, telah Kami tulis di dalam Zabur setelah (tertulis) di dalam Az-Zikr (Lauh Mahfuzh), bahwa bumi ini akan diwarisi oleh hamba-hamba-Ku yang saleh."},{"id":"106","ar":"اِنَّ فِيْ هٰذَا لَبَلٰغًا لِّقَوْمٍ عٰبِدِيْنَ ۗ","bc":"inna fī hāżā labalāgal liqaumin 'ābidīn","at":"Sungguh, (apa yang disebutkan) di dalam (Al-Qur'an) ini, benar-benar menjadi petunjuk (yang lengkap) bagi orang-orang yang menyembah (Allah)."},{"id":"107","ar":"وَمَآ اَرْسَلْنٰكَ اِلَّا رَحْمَةً لِّلْعٰلَمِيْنَ ","bc":"wa mā arsalnāka illā raḥmatal lil-'ālamīn","at":"Dan Kami tidak mengutus engkau (Muhammad) melainkan untuk (menjadi) rahmat bagi seluruh alam."},{"id":"108","ar":"قُلْ اِنَّمَا يُوْحٰٓى اِلَيَّ اَنَّمَآ اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ فَهَلْ اَنْتُمْ مُّسْلِمُوْنَ","bc":"qul innamā yụḥā ilayya annamā ilāhukum ilāhuw wāḥid, fa hal antum muslimụn","at":"Katakanlah (Muhammad), “Sungguh, apa yang diwahyukan kepadaku ialah bahwa Tuhanmu adalah Tuhan Yang Esa, maka apakah kamu telah berserah diri (kepada-Nya)?”"},{"id":"109","ar":"فَاِنْ تَوَلَّوْا فَقُلْ اٰذَنْتُكُمْ عَلٰى سَوَاۤءٍۗ وَاِنْ اَدْرِيْٓ اَقَرِيْبٌ اَمْ بَعِيْدٌ مَّا تُوْعَدُوْنَ ","bc":"fa in tawallau fa qul āżantukum 'alā sawā`, wa in adrī a qarībun am ba'īdum mā tụ'adụn","at":"Maka jika mereka berpaling, maka katakanlah (Muhammad), “Aku telah menyampaikan kepadamu (ajaran) yang sama (antara kita) dan aku tidak tahu apakah yang diancamkan kepadamu itu sudah dekat atau masih jauh.”"},{"id":"110","ar":"اِنَّهٗ يَعْلَمُ الْجَهْرَ مِنَ الْقَوْلِ وَيَعْلَمُ مَا تَكْتُمُوْنَ ","bc":"innahụ ya'lamul-jahra minal-qauli wa ya'lamu mā taktumụn","at":"Sungguh, Dia (Allah) mengetahui perkataan (yang kamu ucapkan) dengan terang-terangan, dan mengetahui (pula) apa yang kamu rahasiakan."},{"id":"111","ar":"وَاِنْ اَدْرِيْ لَعَلَّهٗ فِتْنَةٌ لَّكُمْ وَمَتَاعٌ اِلٰى حِيْنٍ ","bc":"wa in adrī la'allahụ fitnatul lakum wa matā'un ilā ḥīn","at":"Dan aku tidak tahu, boleh jadi hal itu cobaan bagi kamu dan kesenangan sampai waktu yang ditentukan."},{"id":"112","ar":"قَالَ رَبِّ احْكُمْ بِالْحَقِّۗ وَرَبُّنَا الرَّحْمٰنُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُوْنَ","bc":"qāla rabbiḥkum bil-ḥaqq, wa rabbunar-raḥmānul-musta'ānu 'alā mā taṣifụn","at":"Dia (Muhammad) berkata, “Ya Tuhanku, berilah keputusan dengan adil. Dan Tuhan kami Maha Pengasih, tempat memohon segala pertolongan atas semua yang kamu katakan.”"}],al_anfal=[{"id":"1","ar":"يَسْـَٔلُوْنَكَ عَنِ الْاَنْفَالِۗ قُلِ الْاَنْفَالُ لِلّٰهِ وَالرَّسُوْلِۚ فَاتَّقُوا اللّٰهَ وَاَصْلِحُوْا ذَاتَ بَيْنِكُمْ ۖوَاَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗٓ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ","bc":"yas`alụnaka 'anil-anfāl, qulil-anfālu lillāhi war-rasụl, fattaqullāha wa aṣliḥụ żāta bainikum wa aṭī'ullāha wa rasụlahū ing kuntum mu`minīn","at":"Mereka menanyakan kepadamu (Muhammad) tentang (pembagian) harta rampasan perang. Katakanlah, “Harta rampasan perang itu milik Allah dan Rasul (menurut ketentuan Allah dan Rasul-Nya), maka bertakwalah kepada Allah dan perbaikilah hubungan di antara sesamamu, dan taatlah kepada Allah dan Rasul-Nya jika kamu orang-orang yang beriman.”"},{"id":"2","ar":"اِنَّمَا الْمُؤْمِنُوْنَ الَّذِيْنَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوْبُهُمْ وَاِذَا تُلِيَتْ عَلَيْهِمْ اٰيٰتُهٗ زَادَتْهُمْ اِيْمَانًا وَّعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَۙ","bc":"innamal-mu`minụnallażīna iżā żukirallāhu wajilat qulụbuhum wa iżā tuliyat 'alaihim āyātuhụ zādat-hum īmānaw wa 'alā rabbihim yatawakkalụn","at":"Sesungguhnya orang-orang yang beriman adalah mereka yang apabila disebut nama Allah gemetar hatinya, dan apabila dibacakan ayat-ayat-Nya kepada mereka, bertambah (kuat) imannya dan hanya kepada Tuhan mereka bertawakal,"},{"id":"3","ar":"الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَۗ ","bc":"allażīna yuqīmụnaṣ-ṣalāta wa mimmā razaqnāhum yunfiqụn","at":"(Yaitu) orang-orang yang melaksanakan salat dan yang menginfakkan sebagian dari rezeki yang Kami berikan kepada mereka."},{"id":"4","ar":"اُولٰۤىِٕكَ هُمُ الْمُؤْمِنُوْنَ حَقًّاۗ لَهُمْ دَرَجٰتٌ عِنْدَ رَبِّهِمْ وَمَغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌۚ ","bc":"ulā`ika humul-mu`minụna ḥaqqā, lahum darajātun 'inda rabbihim wa magfiratuw wa rizqung karīm","at":"Mereka itulah orang-orang yang benar-benar beriman. Mereka akan memperoleh derajat (tinggi) di sisi Tuhannya dan ampunan serta rezeki (nikmat) yang mulia."},{"id":"5","ar":"كَمَآ اَخْرَجَكَ رَبُّكَ مِنْۢ بَيْتِكَ بِالْحَقِّۖ وَاِنَّ فَرِيْقًا مِّنَ الْمُؤْمِنِيْنَ لَكٰرِهُوْنَ","bc":"kamā akhrajaka rabbuka mim baitika bil-ḥaqqi wa inna farīqam minal-mu`minīna lakārihụn","at":"Sebagaimana Tuhanmu menyuruhmu pergi dari rumahmu dengan kebenaran, meskipun sesungguhnya sebagian dari orang-orang yang beriman itu tidak menyukainya,"},{"id":"6","ar":"يُجَادِلُوْنَكَ فِى الْحَقِّ بَعْدَمَا تَبَيَّنَ كَاَنَّمَا يُسَاقُوْنَ اِلَى الْمَوْتِ وَهُمْ يَنْظُرُوْنَ ۗ ","bc":"yujādilụnaka fil-ḥaqqi ba'da mā tabayyana ka`annamā yusāqụna ilal-mauti wa hum yanẓurụn","at":"mereka membantahmu (Muhammad) tentang kebenaran setelah nyata (bahwa mereka pasti menang), seakan-akan mereka dihalau kepada kematian, sedang mereka melihat (sebab kematian itu)."},{"id":"7","ar":"وَاِذْ يَعِدُكُمُ اللّٰهُ اِحْدَى الطَّاۤىِٕفَتَيْنِ اَنَّهَا لَكُمْ وَتَوَدُّوْنَ اَنَّ غَيْرَ ذَاتِ الشَّوْكَةِ تَكُوْنُ لَكُمْ وَيُرِيْدُ اللّٰهُ اَنْ يُّحِقَّ الْحَقَّ بِكَلِمٰتِهٖ وَيَقْطَعَ دَابِرَ الْكٰفِرِيْنَۙ ","bc":"wa iż ya'idukumullāhu iḥdaṭ-ṭā`ifataini annahā lakum wa tawaddụna anna gaira żātisy-syaukati takụnu lakum wa yurīdullāhu ay yuḥiqqal-ḥaqqa bikalimātihī wa yaqṭa'a dābiral-kāfirīn","at":"Dan (ingatlah) ketika Allah menjanjikan kepadamu bahwa salah satu dari dua golongan (yang kamu hadapi) adalah untukmu, sedang kamu menginginkan bahwa yang tidak mempunyai kekuatan senjatalah untukmu. Tetapi Allah hendak membenarkan yang benar dengan ayat-ayat-Nya dan memusnahkan orang-orang kafir sampai ke akar-akarnya,"},{"id":"8","ar":"لِيُحِقَّ الْحَقَّ وَيُبْطِلَ الْبَاطِلَ وَلَوْ كَرِهَ الْمُجْرِمُوْنَۚ ","bc":"liyuḥiqqal-ḥaqqa wa yubṭilal-bāṭila walau karihal-mujrimụn","at":"agar Allah memperkuat yang hak (Islam) dan menghilangkan yang batil (syirik) walaupun orang-orang yang berdosa (musyrik) itu tidak menyukainya."},{"id":"9","ar":"اِذْ تَسْتَغِيْثُوْنَ رَبَّكُمْ فَاسْتَجَابَ لَكُمْ اَنِّيْ مُمِدُّكُمْ بِاَلْفٍ مِّنَ الْمَلٰۤىِٕكَةِ مُرْدِفِيْنَ","bc":"iż tastagīṡụna rabbakum fastajāba lakum annī mumiddukum bi`alfim minal-malā`ikati murdifīn","at":"(Ingatlah), ketika kamu memohon pertolongan kepada Tuhanmu, lalu diperkenankan-Nya bagimu, “Sungguh, Aku akan mendatangkan bala bantuan kepadamu dengan seribu malaikat yang datang berturut-turut.”"},{"id":"10","ar":"وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى وَلِتَطْمَىِٕنَّ بِهٖ قُلُوْبُكُمْۗ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِ ۗاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ","bc":"wa mā ja'alahullāhu illā busyrā wa litaṭma`inna bihī qulụbukum, wa man-naṣru illā min 'indillāh, innallāha 'azīzun ḥakīm","at":"Dan tidaklah Allah menjadikannya melainkan sebagai kabar gembira agar hatimu menjadi tenteram karenanya. Dan kemenangan itu hanyalah dari sisi Allah. Sungguh, Allah Mahaperkasa, Mahabijaksana."},{"id":"11","ar":"اِذْ يُغَشِّيْكُمُ النُّعَاسَ اَمَنَةً مِّنْهُ وَيُنَزِّلُ عَلَيْكُمْ مِّنَ السَّمَاۤءِ مَاۤءً لِّيُطَهِّرَكُمْ بِهٖ وَيُذْهِبَ عَنْكُمْ رِجْزَ الشَّيْطٰنِ وَلِيَرْبِطَ عَلٰى قُلُوْبِكُمْ وَيُثَبِّتَ بِهِ الْاَقْدَامَۗ ","bc":"iż yugasysyīkumun-nu'āsa amanatam min-hu wa yunazzilu 'alaikum minas-samā`i mā`al liyuṭahhirakum bihī wa yuż-hiba 'angkum rijzasy-syaiṭāni wa liyarbiṭa 'alā qulụbikum wa yuṡabbita bihil-aqdām","at":"(Ingatlah), ketika Allah membuat kamu mengantuk untuk memberi ketenteraman dari-Nya, dan Allah menurunkan air (hujan) dari langit kepadamu untuk menyucikan kamu dengan (hujan) itu dan menghilangkan gangguan-gangguan setan dari dirimu dan untuk menguatkan hatimu serta memperteguh telapak kakimu (teguh pendirian)."},{"id":"12","ar":"اِذْ يُوْحِيْ رَبُّكَ اِلَى الْمَلٰۤىِٕكَةِ اَنِّيْ مَعَكُمْ فَثَبِّتُوا الَّذِيْنَ اٰمَنُوْاۗ سَاُلْقِيْ فِيْ قُلُوْبِ الَّذِيْنَ كَفَرُوا الرُّعْبَ فَاضْرِبُوْا فَوْقَ الْاَعْنَاقِ وَاضْرِبُوْا مِنْهُمْ كُلَّ بَنَانٍۗ ","bc":"iż yụḥī rabbuka ilal-malā`ikati annī ma'akum fa ṡabbitullażīna āmanụ, sa`ulqī fī qulụbillażīna kafarur-ru'ba faḍribụ fauqal-a'nāqi waḍribụ min-hum kulla banān","at":"(Ingatlah), ketika Tuhanmu mewahyukan kepada para malaikat, “Sesungguhnya Aku bersama kamu, maka teguhkanlah (pendirian) orang-orang yang telah beriman.” Kelak akan Aku berikan rasa ketakutan ke dalam hati orang-orang kafir, maka pukullah di atas leher mereka dan pukullah tiap-tiap ujung jari mereka."},{"id":"13","ar":"ذٰلِكَ بِاَنَّهُمْ شَاۤقُّوا اللّٰهَ وَرَسُوْلَهٗۚ وَمَنْ يُّشَاقِقِ اللّٰهَ وَرَسُوْلَهٗ فَاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ","bc":"żālika bi`annahum syāqqullāha wa rasụlah, wa may yusyāqiqillāha wa rasụlahụ fa innallāha syadīdul-'iqāb","at":"(Ketentuan) yang demikian itu adalah karena sesungguhnya mereka menentang Allah dan Rasul-Nya; dan barangsiapa menentang Allah dan Rasul-Nya, sungguh, Allah sangat keras siksa-Nya."},{"id":"14","ar":"ذٰلِكُمْ فَذُوْقُوْهُ وَاَنَّ لِلْكٰفِرِيْنَ عَذَابَ النَّارِ","bc":"żālikum fa żụqụhu wa anna lil-kāfirīna 'ażāban-nār","at":"Demikianlah (hukuman dunia yang ditimpakan atasmu), maka rasakanlah hukuman itu. Sesungguhnya bagi orang-orang kafir ada (lagi) azab neraka."},{"id":"15","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا لَقِيْتُمُ الَّذِيْنَ كَفَرُوْا زَحْفًا فَلَا تُوَلُّوْهُمُ الْاَدْبَارَۚ ","bc":"yā ayyuhallażīna āmanū iżā laqītumullażīna kafarụ zaḥfan fa lā tuwallụhumul-adbār","at":"Wahai orang yang beriman! Apabila kamu bertemu dengan orang-orang kafir yang akan menyerangmu, maka janganlah kamu berbalik membelakangi mereka (mundur)."},{"id":"16","ar":"وَمَنْ يُّوَلِّهِمْ يَوْمَىِٕذٍ دُبُرَهٗٓ اِلَّا مُتَحَرِّفًا لِّقِتَالٍ اَوْ مُتَحَيِّزًا اِلٰى فِئَةٍ فَقَدْ بَاۤءَ بِغَضَبٍ مِّنَ اللّٰهِ وَمَأْوٰىهُ جَهَنَّمُ ۗ وَبِئْسَ الْمَصِيْرُ","bc":"wa may yuwallihim yauma`iżin duburahū illā mutaḥarrifal liqitālin au mutaḥayyizan ilā fi`atin fa qad bā`a bigaḍabim minallāhi wa ma`wāhu jahannam, wa bi`sal-maṣīr","at":"Dan barangsiapa mundur pada waktu itu, kecuali berbelok untuk (siasat) perang atau hendak menggabungkan diri dengan pasukan yang lain, maka sungguh, orang itu kembali dengan membawa kemurkaan dari Allah, dan tempatnya ialah neraka Jahanam, seburuk-buruk tempat kembali."},{"id":"17","ar":"فَلَمْ تَقْتُلُوْهُمْ وَلٰكِنَّ اللّٰهَ قَتَلَهُمْۖ وَمَا رَمَيْتَ اِذْ رَمَيْتَ وَلٰكِنَّ اللّٰهَ رَمٰىۚ وَلِيُبْلِيَ الْمُؤْمِنِيْنَ مِنْهُ بَلَاۤءً حَسَنًاۗ اِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ","bc":"fa lam taqtulụhum wa lākinnallāha qatalahum wa mā ramaita iż ramaita wa lākinnallāha ramā, wa liyubliyal-mu`minīna min-hu balā`an ḥasanā, innallāha samī'un 'alīm","at":"Maka (sebenarnya) bukan kamu yang membunuh mereka, melainkan Allah yang membunuh mereka, dan bukan engkau yang melempar ketika engkau melempar, tetapi Allah yang melempar. (Allah berbuat demikian untuk membinasakan mereka) dan untuk memberi kemenangan kepada orang-orang mukmin, dengan kemenangan yang baik. Sungguh, Allah Maha Mendengar, Maha Mengetahui."},{"id":"18","ar":"ذٰلِكُمْ وَاَنَّ اللّٰهَ مُوْهِنُ كَيْدِ الْكٰفِرِيْنَ","bc":"żālikum wa annallāha mụhinu kaidil-kāfirīn","at":"Demikianlah (karunia Allah yang dilimpahkan kepadamu), dan sungguh, Allah melemahkan tipu daya orang-orang kafir."},{"id":"19","ar":"اِنْ تَسْتَفْتِحُوْا فَقَدْ جَاۤءَكُمُ الْفَتْحُۚ وَاِنْ تَنْتَهُوْا فَهُوَ خَيْرٌ لَّكُمْۚ وَاِنْ تَعُوْدُوْا نَعُدْۚ وَلَنْ تُغْنِيَ عَنْكُمْ فِئَتُكُمْ شَيْـًٔا وَّلَوْ كَثُرَتْۙ وَاَنَّ اللّٰهَ مَعَ الْمُؤْمِنِيْنَ ","bc":"in tastaftiḥụ fa qad jā`akumul-fat-ḥ, wa in tantahụ fa huwa khairul lakum, wa in ta'ụdụ na'ud, wa lan tugniya 'angkum fi`atukum syai`aw walau kaṡurat wa annallāha ma'al-mu`minīn","at":"Jika kamu meminta keputusan, maka sesungguhnya keputusan telah datang kepadamu; dan jika kamu berhenti (memusuhi Rasul), maka itulah yang lebih baik bagimu; dan jika kamu kembali, niscaya Kami kembali (memberi pertolongan); dan pasukanmu tidak akan dapat menolak sesuatu bahaya sedikit pun darimu, biarpun dia jumlahnya (pasukan) banyak. Sungguh, Allah beserta orang-orang beriman."},{"id":"20","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ وَلَا تَوَلَّوْا عَنْهُ وَاَنْتُمْ تَسْمَعُوْنَ","bc":"yā ayyuhallażīna āmanū aṭī'ullāha wa rasụlahụ wa lā tawallau 'an-hu wa antum tasma'ụn","at":"Wahai orang-orang yang beriman! Taatlah kepada Allah dan Rasul-Nya, dan janganlah kamu berpaling dari-Nya, padahal kamu mendengar (perintah-perintah-Nya),"},{"id":"21","ar":"وَلَا تَكُوْنُوْا كَالَّذِيْنَ قَالُوْا سَمِعْنَا وَهُمْ لَا يَسْمَعُوْنَۚ ","bc":"wa lā takụnụ kallażīna qālụ sami'nā wa hum lā yasma'ụn","at":"dan janganlah kamu menjadi seperti orang-orang (munafik) yang berkata, “Kami mendengarkan,” padahal mereka tidak mendengarkan (karena hati mereka mengingkarinya)."},{"id":"22","ar":"۞ اِنَّ شَرَّ الدَّوَاۤبِّ عِنْدَ اللّٰهِ الصُّمُّ الْبُكْمُ الَّذِيْنَ لَا يَعْقِلُوْنَ","bc":"inna syarrad-dawābbi 'indallāhiṣ-ṣummul-bukmullażīna lā ya'qilụn","at":"Sesungguhnya makhluk bergerak yang bernyawa yang paling buruk dalam pandangan Allah ialah mereka yang tuli dan bisu (tidak mendengar dan memahami kebenaran) yaitu orang-orang yang tidak mengerti."},{"id":"23","ar":"وَلَوْ عَلِمَ اللّٰهُ فِيْهِمْ خَيْرًا لَّاَسْمَعَهُمْۗ وَلَوْ اَسْمَعَهُمْ لَتَوَلَّوْا وَّهُمْ مُّعْرِضُوْنَ","bc":"walau 'alimallāhu fīhim khairal la`asma'ahum, walau asma'ahum latawallaw wa hum mu'riḍụn","at":"Dan sekiranya Allah mengetahui ada kebaikan pada mereka, tentu Dia jadikan mereka dapat mendengar. Dan jika Allah menjadikan mereka dapat mendengar, niscaya mereka berpaling, sedang mereka memalingkan diri."},{"id":"24","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اسْتَجِيْبُوْا لِلّٰهِ وَلِلرَّسُوْلِ اِذَا دَعَاكُمْ لِمَا يُحْيِيْكُمْۚ وَاعْلَمُوْٓا اَنَّ اللّٰهَ يَحُوْلُ بَيْنَ الْمَرْءِ وَقَلْبِهٖ وَاَنَّهٗٓ اِلَيْهِ تُحْشَرُوْنَ","bc":"yā ayyuhallażīna āmanustajībụ lillāhi wa lir-rasụli iżā da'ākum limā yuḥyīkum, wa'lamū annallāha yaḥụlu bainal-mar`i wa qalbihī wa annahū ilaihi tuḥsyarụn","at":"Wahai orang-orang yang beriman! Penuhilah seruan Allah dan Rasul, apabila dia menyerumu kepada sesuatu yang memberi kehidupan kepadamu, dan ketahuilah bahwa sesungguhnya Allah membatasi antara manusia dan hatinya dan sesungguhnya kepada-Nyalah kamu akan dikumpulkan."},{"id":"25","ar":"وَاتَّقُوْا فِتْنَةً لَّا تُصِيْبَنَّ الَّذِيْنَ ظَلَمُوْا مِنْكُمْ خَاۤصَّةً ۚوَاعْلَمُوْٓا اَنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ","bc":"wattaqụ fitnatal lā tuṣībannallażīna ẓalamụ mingkum khāṣṣah, wa'lamū annallāha syadīdul-'iqāb","at":"Dan peliharalah dirimu dari siksaan yang tidak hanya menimpa orang-orang yang zalim saja di antara kamu. Ketahuilah bahwa Allah sangat keras siksa-Nya."},{"id":"26","ar":"وَاذْكُرُوْٓا اِذْ اَنْتُمْ قَلِيْلٌ مُّسْتَضْعَفُوْنَ فِى الْاَرْضِ تَخَافُوْنَ اَنْ يَّتَخَطَّفَكُمُ النَّاسُ فَاٰوٰىكُمْ وَاَيَّدَكُمْ بِنَصْرِهٖ وَرَزَقَكُمْ مِّنَ الطَّيِّبٰتِ لَعَلَّكُمْ تَشْكُرُوْنَ","bc":"ważkurū iż antum qalīlum mustaḍ'afụna fil-arḍi takhāfụna ay yatakhaṭṭafakumun-nāsu fa āwākum wa ayyadakum binaṣrihī wa razaqakum minaṭ-ṭayyibāti la'allakum tasykurụn","at":"Dan ingatlah ketika kamu (para Muhajirin) masih (berjumlah) sedikit, lagi tertindas di bumi (Mekah), dan kamu takut orang-orang (Mekah) akan menculik kamu, maka Dia memberi kamu tempat menetap (Madinah) dan dijadikan-Nya kamu kuat dengan pertolongan-Nya dan diberi-Nya kamu rezeki yang baik agar kamu bersyukur."},{"id":"27","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَخُوْنُوا اللّٰهَ وَالرَّسُوْلَ وَتَخُوْنُوْٓا اَمٰنٰتِكُمْ وَاَنْتُمْ تَعْلَمُوْنَ","bc":"yā ayyuhallażīna āmanụ lā takhụnullāha war-rasụla wa takhụnū amānātikum wa antum ta'lamụn","at":"Wahai orang-orang yang beriman! Janganlah kamu mengkhianati Allah dan Rasul dan (juga) janganlah kamu mengkhianati amanat yang dipercayakan kepadamu, sedang kamu mengetahui."},{"id":"28","ar":"وَاعْلَمُوْٓا اَنَّمَآ اَمْوَالُكُمْ وَاَوْلَادُكُمْ فِتْنَةٌ ۙوَّاَنَّ اللّٰهَ عِنْدَهٗٓ اَجْرٌ عَظِيْمٌ","bc":"wa'lamū annamā amwālukum wa aulādukum fitnatuw wa annallāha 'indahū ajrun 'aẓīm","at":"Dan ketahuilah bahwa hartamu dan anak-anakmu itu hanyalah sebagai cobaan dan sesungguhnya di sisi Allah ada pahala yang besar."},{"id":"29","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تَتَّقُوا اللّٰهَ يَجْعَلْ لَّكُمْ فُرْقَانًا وَّيُكَفِّرْ عَنْكُمْ سَيِّاٰتِكُمْ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ","bc":"yā ayyuhallażīna āmanū in tattaqullāha yaj'al lakum furqānaw wa yukaffir 'angkum sayyi`ātikum wa yagfir lakum, wallāhu żul-faḍlil-'aẓīm","at":"Wahai orang-orang yang beriman! Jika kamu bertakwa kepada Allah, niscaya Dia akan memberikan furqan (kemampuan membedakan antara yang hak dan batil) kepadamu dan menghapus segala kesalahanmu dan mengampuni (dosa-dosa)mu. Allah memiliki karunia yang besar."},{"id":"30","ar":"وَاِذْ يَمْكُرُ بِكَ الَّذِيْنَ كَفَرُوْا لِيُثْبِتُوْكَ اَوْ يَقْتُلُوْكَ اَوْ يُخْرِجُوْكَۗ وَيَمْكُرُوْنَ وَيَمْكُرُ اللّٰهُ ۗوَاللّٰهُ خَيْرُ الْمَاكِرِيْنَ","bc":"wa iż yamkuru bikallażīna kafarụ liyuṡbitụka au yaqtulụka au yukhrijụk, wa yamkurụna wa yamkurullāh, wallāhu khairul-mākirīn","at":"Dan (ingatlah), ketika orang-orang kafir (Quraisy) memikirkan tipu daya terhadapmu (Muhammad) untuk menangkap dan memenjarakanmu atau membunuhmu, atau mengusirmu. Mereka membuat tipu daya dan Allah menggagalkan tipu daya itu. Allah adalah sebaik-baik pembalas tipu daya."},{"id":"31","ar":"وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا قَالُوْا قَدْ سَمِعْنَا لَوْ نَشَاۤءُ لَقُلْنَا مِثْلَ هٰذَآ ۙاِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ","bc":"wa iżā tutlā 'alaihim āyātunā qālụ qad sami'nā lau nasyā`u laqulnā miṡla hāżā in hāżā illā asāṭīrul-awwalīn","at":"Dan apabila ayat-ayat Kami dibacakan kepada mereka, mereka berkata, “Sesungguhnya kami telah mendengar (ayat-ayat seperti ini), jika kami menghendaki niscaya kami dapat membacakan yang seperti ini. (Al-Qur'an) ini tidak lain hanyalah dongeng orang-orang terdahulu.”"},{"id":"32","ar":"وَاِذْ قَالُوا اللهم اِنْ كَانَ هٰذَا هُوَ الْحَقَّ مِنْ عِنْدِكَ فَاَمْطِرْ عَلَيْنَا حِجَارَةً مِّنَ السَّمَاۤءِ اَوِ ائْتِنَا بِعَذَابٍ اَلِيْمٍ","bc":"wa iż qālullāhumma ing kāna hāżā huwal-ḥaqqa min 'indika fa amṭir 'alainā ḥijāratam minas-samā`i awi`tinā bi'ażābin alīm","at":"Dan (ingatlah), ketika mereka (orang-orang musyrik) berkata, “Ya Allah, jika (Al-Qur'an) ini benar (wahyu) dari Engkau, maka hujanilah kami dengan batu dari langit, atau datangkanlah kepada kami azab yang pedih.”"},{"id":"33","ar":"وَمَا كَانَ اللّٰهُ لِيُعَذِّبَهُمْ وَاَنْتَ فِيْهِمْۚ وَمَا كَانَ اللّٰهُ مُعَذِّبَهُمْ وَهُمْ يَسْتَغْفِرُوْنَ ","bc":"wa mā kānallāhu liyu'ażżibahum wa anta fīhim, wa mā kānallāhu mu'ażżibahum wa hum yastagfirụn","at":"Tetapi Allah tidak akan menghukum mereka, selama engkau (Muhammad) berada di antara mereka. Dan tidaklah (pula) Allah akan menghukum mereka, sedang mereka (masih) memohon ampunan."},{"id":"34","ar":"وَمَا لَهُمْ اَلَّا يُعَذِّبَهُمُ اللّٰهُ وَهُمْ يَصُدُّوْنَ عَنِ الْمَسْجِدِ الْحَرَامِ وَمَا كَانُوْٓا اَوْلِيَاۤءَهٗۗ اِنْ اَوْلِيَاۤؤُهٗٓ اِلَّا الْمُتَّقُوْنَ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ","bc":"wa mā lahum allā yu'ażżibahumullāhu wa hum yaṣuddụna 'anil-masjidil-ḥarāmi wa mā kānū auliyā`ah, in auliyā`uhū illal-muttaqụna wa lākinna akṡarahum lā ya'lamụn","at":"Dan mengapa Allah tidak menghukum mereka padahal mereka menghalang-halangi (orang) untuk (mendatangi) Masjidilharam dan mereka bukanlah orang-orang yang berhak menguasainya? Orang yang berhak menguasai(nya), hanyalah orang-orang yang bertakwa, tetapi kebanyakan mereka tidak mengetahui."},{"id":"35","ar":"وَمَا كَانَ صَلَاتُهُمْ عِنْدَ الْبَيْتِ اِلَّا مُكَاۤءً وَّتَصْدِيَةًۗ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ","bc":"wa mā kāna ṣalātuhum 'indal-baiti illā mukā`aw wa taṣdiyah, fa żụqul-'ażāba bimā kuntum takfurụn","at":"Dan salat mereka di sekitar Baitullah itu, tidak lain hanyalah siulan dan tepuk tangan. Maka rasakanlah azab disebabkan kekafiranmu itu."},{"id":"36","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا يُنْفِقُوْنَ اَمْوَالَهُمْ لِيَصُدُّوْا عَنْ سَبِيْلِ اللّٰهِ ۗفَسَيُنْفِقُوْنَهَا ثُمَّ تَكُوْنُ عَلَيْهِمْ حَسْرَةً ثُمَّ يُغْلَبُوْنَ ەۗ وَالَّذِيْنَ كَفَرُوْٓا اِلٰى جَهَنَّمَ يُحْشَرُوْنَۙ","bc":"innallażīna kafarụ yunfiqụna amwālahum liyaṣuddụ 'an sabīlillāh, fa sayunfiqụnahā ṡumma takụnu 'alaihim ḥasratan ṡumma yuglabụn, wallażīna kafarū ilā jahannama yuḥsyarụn","at":"Sesungguhnya orang-orang yang kafir itu, menginfakkan harta mereka untuk menghalang-halangi (orang) dari jalan Allah. Mereka akan (terus) menginfakkan harta itu, kemudian mereka akan menyesal sendiri, dan akhirnya mereka akan dikalahkan. Ke dalam neraka Jahanamlah orang-orang kafir itu akan dikumpulkan,"},{"id":"37","ar":"لِيَمِيْزَ اللّٰهُ الْخَبِيْثَ مِنَ الطَّيِّبِ وَيَجْعَلَ الْخَبِيْثَ بَعْضَهٗ عَلٰى بَعْضٍ فَيَرْكُمَهٗ جَمِيْعًا فَيَجْعَلَهٗ فِيْ جَهَنَّمَۗ اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ","bc":"liyamīzallāhul-khabīṡa minaṭ-ṭayyibi wa yaj'alal-khabīṡa ba'ḍahụ 'alā ba'ḍin fa yarkumahụ jamī'an fa yaj'alahụ fī jahannam, ulā`ika humul-khāsirụn","at":"agar Allah memisahkan (golongan) yang buruk dari yang baik dan menjadikan (golongan) yang buruk itu sebagiannya di atas yang lain, lalu kesemuanya ditumpukkan-Nya, dan dimasukkan-Nya ke dalam neraka Jahanam. Mereka itulah orang-orang yang rugi."},{"id":"38","ar":"قُلْ لِّلَّذِيْنَ كَفَرُوْٓا اِنْ يَّنْتَهُوْا يُغْفَرْ لَهُمْ مَّا قَدْ سَلَفَۚ وَاِنْ يَّعُوْدُوْا فَقَدْ مَضَتْ سُنَّتُ الْاَوَّلِيْنَ","bc":"qul lillażīna kafarū iy yantahụ yugfar lahum mā qad salaf, wa iy ya'ụdụ fa qad maḍat sunnatul-awwalīn","at":"Katakanlah kepada orang-orang yang kafir itu (Abu Sufyan dan kawan-kawannya), “Jika mereka berhenti (dari kekafirannya), niscaya Allah akan mengampuni dosa-dosa mereka yang telah lalu; dan jika mereka kembali lagi (memerangi Nabi) sungguh, berlaku (kepada mereka) sunnah (Allah terhadap) orang-orang dahulu (dibinasakan).”"},{"id":"39","ar":"وَقَاتِلُوْهُمْ حَتّٰى لَا تَكُوْنَ فِتْنَةٌ وَّيَكُوْنَ الدِّيْنُ كُلُّهٗ لِلّٰهِۚ فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ بِمَا يَعْمَلُوْنَ بَصِيْرٌ","bc":"wa qātilụhum ḥattā lā takụna fitnatuw wa yakụnad-dīnu kulluhụ lillāh, fa inintahau fa innallāha bimā ya'malụna baṣīr","at":"Dan perangilah mereka itu sampai tidak ada lagi fitnah, dan agama hanya bagi Allah semata. Jika mereka berhenti (dari kekafiran), maka sesungguhnya Allah Maha Melihat apa yang mereka kerjakan."},{"id":"40","ar":"وَاِنْ تَوَلَّوْا فَاعْلَمُوْٓا اَنَّ اللّٰهَ مَوْلٰىكُمْ ۗنِعْمَ الْمَوْلٰى وَنِعْمَ النَّصِيْرُ ۔","bc":"wa in tawallau fa'lamū annallāha maulākum, ni'mal-maulā wa ni'man-naṣīr","at":"Dan jika mereka berpaling, maka ketahuilah bahwa sesungguhnya Allah pelindungmu. Dia adalah sebaik-baik pelindung dan sebaik-baik penolong."},{"id":"41","ar":"۞ وَاعْلَمُوْٓا اَنَّمَا غَنِمْتُمْ مِّنْ شَيْءٍ فَاَنَّ لِلّٰهِ خُمُسَهٗ وَلِلرَّسُوْلِ وَلِذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَابْنِ السَّبِيْلِ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ وَمَآ اَنْزَلْنَا عَلٰى عَبْدِنَا يَوْمَ الْفُرْقَانِ يَوْمَ الْتَقَى الْجَمْعٰنِۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"wa'lamū annamā ganimtum min syai`in fa anna lillāhi khumusahụ wa lir-rasụli wa liżil-qurbā wal-yatāmā wal-masākīni wabnis-sabīli ing kuntum āmantum billāhi wa mā anzalnā 'alā 'abdinā yaumal-furqāni yaumaltaqal jam'ān, wallāhu 'alā kulli syai`ing qadīr","at":"Dan ketahuilah, sesungguhnya segala yang kamu peroleh sebagai rampasan perang, maka seperlima untuk Allah, Rasul, kerabat Rasul, anak yatim, orang miskin dan ibnu sabil, (demikian) jika kamu beriman kepada Allah dan kepada apa yang Kami turunkan kepada hamba Kami (Muhammad) di hari Furqan, yaitu pada hari bertemunya dua pasukan. Allah Mahakuasa atas segala sesuatu."},{"id":"42","ar":"اِذْ اَنْتُمْ بِالْعُدْوَةِ الدُّنْيَا وَهُمْ بِالْعُدْوَةِ الْقُصْوٰى وَالرَّكْبُ اَسْفَلَ مِنْكُمْۗ وَلَوْ تَوَاعَدْتُّمْ لَاخْتَلَفْتُمْ فِى الْمِيْعٰدِۙ وَلٰكِنْ لِّيَقْضِيَ اللّٰهُ اَمْرًا كَانَ مَفْعُوْلًا ەۙ لِّيَهْلِكَ مَنْ هَلَكَ عَنْۢ بَيِّنَةٍ وَّيَحْيٰى مَنْ حَيَّ عَنْۢ بَيِّنَةٍۗ وَاِنَّ اللّٰهَ لَسَمِيْعٌ عَلِيْمٌۙ","bc":"iż antum bil-'udwatid-dun-yā wa hum bil-'udwatil-quṣwā war-rakbu asfala mingkum, walau tawā'attum lakhtalaftum fil-mī'ādi wa lākil liyaqḍiyallāhu amrang kāna maf'ụlal liyahlika man halaka 'am bayyinatiw wa yaḥyā man ḥayya 'am bayyinah, wa innallāha lasamī'un 'alīm","at":"(Yaitu) ketika kamu berada di pinggir lembah yang dekat dan mereka berada di pinggir lembah yang jauh sedang kafilah itu berada lebih rendah dari kamu. Sekiranya kamu mengadakan persetujuan (untuk menentukan hari pertempuran), niscaya kamu berbeda pendapat dalam menentukan (hari pertempuran itu), tetapi Allah berkehendak melaksanakan suatu urusan yang harus dilaksanakan, yaitu agar orang yang binasa itu binasa dengan bukti yang nyata dan agar orang yang hidup itu hidup dengan bukti yang nyata. Sungguh, Allah Maha Mendengar, Maha Mengetahui."},{"id":"43","ar":"اِذْ يُرِيْكَهُمُ اللّٰهُ فِيْ مَنَامِكَ قَلِيْلًاۗ وَلَوْ اَرٰىكَهُمْ كَثِيْرًا لَّفَشِلْتُمْ وَلَتَنَازَعْتُمْ فِى الْاَمْرِ وَلٰكِنَّ اللّٰهَ سَلَّمَۗ اِنَّهٗ عَلِيْمٌۢ بِذَاتِ الصُّدُوْرِ ","bc":"iż yurīkahumullāhu fī manāmika qalīlā, walau arākahum kaṡīral lafasyiltum wa latanāza'tum fil-amri wa lākinnallāha sallam, innahụ 'alīmum biżātiṣ-ṣudụr","at":"(Ingatlah) ketika Allah memperlihatkan mereka di dalam mimpimu (berjumlah) sedikit. Dan sekiranya Allah memperlihatkan mereka (berjumlah) banyak tentu kamu menjadi gentar dan tentu kamu akan berbantah-bantahan dalam urusan itu, tetapi Allah telah menyelamatkan kamu. Sungguh, Allah Maha Mengetahui apa yang ada dalam hatimu."},{"id":"44","ar":"وَاِذْ يُرِيْكُمُوْهُمْ اِذِ الْتَقَيْتُمْ فِيْٓ اَعْيُنِكُمْ قَلِيْلًا وَّيُقَلِّلُكُمْ فِيْٓ اَعْيُنِهِمْ لِيَقْضِيَ اللّٰهُ اَمْرًا كَانَ مَفْعُوْلًا ۗوَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ","bc":"wa iż yurīkumụhum iżiltaqaitum fī a'yunikum qalīlaw wa yuqallilukum fī a'yunihim liyaqḍiyallāhu amrang kāna maf'ụlā, wa ilallāhi turja'ul-umụr","at":"Dan ketika Allah memperlihatkan mereka kepadamu, ketika kamu berjumpa dengan mereka berjumlah sedikit menurut penglihatan matamu dan kamu diperlihatkan-Nya berjumlah sedikit menurut penglihatan mereka, itu karena Allah berkehendak melaksanakan suatu urusan yang harus dilaksanakan. Hanya kepada Allah segala urusan dikembalikan."},{"id":"45","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا لَقِيْتُمْ فِئَةً فَاثْبُتُوْا وَاذْكُرُوا اللّٰهَ كَثِيْرًا لَّعَلَّكُمْ تُفْلِحُوْنَۚ ","bc":"yā ayyuhallażīna āmanū iżā laqītum fi`atan faṡbutụ ważkurullāha kaṡīral la'allakum tufliḥụn","at":"Wahai orang-orang yang beriman! Apabila kamu bertemu pasukan (musuh), maka berteguh hatilah dan sebutlah (nama) Allah banyak-banyak (berzikir dan berdoa) agar kamu beruntung."},{"id":"46","ar":"وَاَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ وَلَا تَنَازَعُوْا فَتَفْشَلُوْا وَتَذْهَبَ رِيْحُكُمْ وَاصْبِرُوْاۗ اِنَّ اللّٰهَ مَعَ الصّٰبِرِيْنَۚ ","bc":"wa aṭī'ullāha wa rasụlahụ wa lā tanāza'ụ fa tafsyalụ wa taż-haba rīḥukum waṣbirụ, innallāha ma'aṣ-ṣābirīn","at":"Dan taatilah Allah dan Rasul-Nya dan janganlah kamu berselisih, yang menyebabkan kamu menjadi gentar dan kekuatanmu hilang dan bersabarlah. Sungguh, Allah beserta orang-orang sabar."},{"id":"47","ar":"وَلَا تَكُوْنُوْا كَالَّذِيْنَ خَرَجُوْا مِنْ دِيَارِهِمْ بَطَرًا وَّرِئَاۤءَ النَّاسِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ ۗوَاللّٰهُ بِمَايَعْمَلُوْنَ مُحِيْطٌ ","bc":"wa lā takụnụ kallażīna kharajụ min diyārihim baṭaraw wa ri`ā`an-nāsi wa yaṣuddụna 'an sabīlillāh, wallāhu bimā ya'malụna muḥīṭ","at":"Dan janganlah kamu seperti orang-orang yang keluar dari kampung halamannya dengan rasa angkuh dan ingin dipuji orang (ria) serta menghalang-halangi (orang) dari jalan Allah. Allah meliputi segala yang mereka kerjakan."},{"id":"48","ar":"وَاِذْ زَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ وَقَالَ لَا غَالِبَ لَكُمُ الْيَوْمَ مِنَ النَّاسِ وَاِنِّيْ جَارٌ لَّكُمْۚ فَلَمَّا تَرَاۤءَتِ الْفِئَتٰنِ نَكَصَ عَلٰى عَقِبَيْهِ وَقَالَ اِنِّيْ بَرِيْۤءٌ مِّنْكُمْ اِنِّيْٓ اَرٰى مَا لَا تَرَوْنَ اِنِّيْٓ اَخَافُ اللّٰهَ ۗوَاللّٰهُ شَدِيْدُ الْعِقَابِ ","bc":"wa iż zayyana lahumusy-syaiṭānu a'mālahum wa qāla lā gāliba lakumul-yauma minan-nāsi wa innī jārul lakum, fa lammā tarā`atil-fi`atāni nakaṣa 'alā 'aqibaihi wa qāla innī barī`um mingkum innī arā mā lā tarauna innī akhāfullāh, wallāhu syadīdul-'iqāb","at":"Dan (ingatlah) ketika setan menjadikan terasa indah bagi mereka perbuatan (dosa) mereka dan mengatakan, “Tidak ada (orang) yang dapat mengalahkan kamu pada hari ini, dan sungguh, aku adalah penolongmu.” Maka ketika kedua pasukan itu telah saling melihat (berhadapan), setan balik ke belakang seraya berkata, “Sesungguhnya aku berlepas diri dari kamu; aku dapat melihat apa yang kamu tidak dapat melihat; sesungguhnya aku takut kepada Allah.” Allah sangat keras siksa-Nya."},{"id":"49","ar":"اِذْ يَقُوْلُ الْمُنٰفِقُوْنَ وَالَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ غَرَّ هٰٓؤُلَاۤءِ دِيْنُهُمْۗ وَمَنْ يَّتَوَكَّلْ عَلَى اللّٰهِ فَاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ","bc":"iż yaqụlul-munāfiqụna wallażīna fī qulụbihim maraḍun garra hā`ulā`i dīnuhum, wa may yatawakkal 'alallāhi fa innallāha 'azīzun ḥakīm","at":"(Ingatlah), ketika orang-orang munafik dan orang-orang yang ada penyakit di dalam hatinya berkata, “Mereka itu (orang mukmin) ditipu agamanya.” (Allah berfirman), “Barangsiapa bertawakal kepada Allah, ketahuilah bahwa Allah Mahaperkasa, Mahabijaksana.”"},{"id":"50","ar":"وَلَوْ تَرٰٓى اِذْ يَتَوَفَّى الَّذِيْنَ كَفَرُوا الْمَلٰۤىِٕكَةُ يَضْرِبُوْنَ وُجُوْهَهُمْ وَاَدْبَارَهُمْۚ وَذُوْقُوْا عَذَابَ الْحَرِيْقِ","bc":"walau tarā iż yatawaffallażīna kafarul-malā`ikatu yaḍribụna wujụhahum wa adbārahum, wa żụqụ 'ażābal-ḥarīq","at":"Dan sekiranya kamu melihat ketika para malaikat mencabut nyawa orang-orang yang kafir sambil memukul wajah dan punggung mereka (dan berkata), “Rasakanlah olehmu siksa neraka yang membakar.”"},{"id":"51","ar":"ذٰلِكَ بِمَا قَدَّمَتْ اَيْدِيْكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِۙ","bc":"żālika bimā qaddamat aidīkum wa annallāha laisa biẓallāmil lil-'abīd","at":"Demikian itu disebabkan oleh perbuatan tanganmu sendiri. Dan sesungguhnya Allah tidak menzalimi hamba-hamba-Nya,"},{"id":"52","ar":"كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۗ كَفَرُوْا بِاٰيٰتِ اللّٰهِ فَاَخَذَهُمُ اللّٰهُ بِذُنُوْبِهِمْۗ اِنَّ اللّٰهَ قَوِيٌّ شَدِيْدُ الْعِقَابِ ","bc":"kada`bi āli fir'auna wallażīna ming qablihim, kafarụ bi`āyātillāhi fa akhażahumullāhu biżunụbihim, innallāha qawiyyun syadīdul-'iqāb","at":"(Keadaan mereka) serupa dengan keadaan pengikut Fir‘aun dan orang-orang yang sebelum mereka. Mereka mengingkari ayat-ayat Allah, maka Allah menyiksa mereka disebabkan dosa-dosanya. Sungguh, Allah Mahakuat lagi sangat keras siksa-Nya."},{"id":"53","ar":"ذٰلِكَ بِاَنَّ اللّٰهَ لَمْ يَكُ مُغَيِّرًا نِّعْمَةً اَنْعَمَهَا عَلٰى قَوْمٍ حَتّٰى يُغَيِّرُوْا مَا بِاَنْفُسِهِمْۙ وَاَنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌۙ ","bc":"żālika bi`annallāha lam yaku mugayyiran ni'matan an'amahā 'alā qaumin ḥattā yugayyirụ mā bi`anfusihim wa annallāha samī'un 'alīm","at":"Yang demikian itu karena sesungguhnya Allah tidak akan mengubah suatu nikmat yang telah diberikan-Nya kepada suatu kaum, hingga kaum itu mengubah apa yang ada pada diri mereka sendiri. Sungguh, Allah Maha Mendengar, Maha Mengetahui,"},{"id":"54","ar":"كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۚ كَذَّبُوْا بِاٰيٰتِ رَبِّهِمْ فَاَهْلَكْنٰهُمْ بِذُنُوْبِهِمْ وَاَغْرَقْنَآ اٰلَ فِرْعَوْنَۚ وَكُلٌّ كَانُوْا ظٰلِمِيْنَ ","bc":"kada`bi āli fir'auna wallażīna ming qablihim, każżabụ bi`āyāti rabbihim fa ahlaknāhum biżunụbihim wa agraqnā āla fir'aụn, wa kullung kānụ ẓālimīn","at":"(Keadaan mereka) serupa dengan keadaan pengikut Fir‘aun dan orang-orang yang sebelum mereka. Mereka mendustakan ayat-ayat Tuhannya, maka Kami membinasakan mereka disebabkan oleh dosa-dosanya dan Kami tenggelamkan Fir‘aun dan pengikut-pengikutnya; karena mereka adalah orang-orang yang zalim."},{"id":"55","ar":"اِنَّ شَرَّ الدَّوَاۤبِّ عِنْدَ اللّٰهِ الَّذِيْنَ كَفَرُوْا فَهُمْ لَا يُؤْمِنُوْنَۖ ","bc":"inna syarrad-dawābbi 'indallāhillażīna kafarụ fa hum lā yu`minụn","at":"Sesungguhnya makhluk bergerak yang bernyawa yang paling buruk dalam pandangan Allah ialah orang-orang kafir, karena mereka tidak beriman."},{"id":"56","ar":"الَّذِيْنَ عَاهَدْتَّ مِنْهُمْ ثُمَّ يَنْقُضُوْنَ عَهْدَهُمْ فِيْ كُلِّ مَرَّةٍ وَّهُمْ لَا يَتَّقُوْنَ ","bc":"allażīna 'āhatta min-hum ṡumma yangquḍụna 'ahdahum fī kulli marratiw wa hum lā yattaqụn","at":"(Yaitu) orang-orang yang terikat perjanjian dengan kamu, kemudian setiap kali berjanji mereka mengkhianati janjinya, sedang mereka tidak takut (kepada Allah)."},{"id":"57","ar":"فَاِمَّا تَثْقَفَنَّهُمْ فِى الْحَرْبِ فَشَرِّدْ بِهِمْ مَّنْ خَلْفَهُمْ لَعَلَّهُمْ يَذَّكَّرُوْنَ ","bc":"fa immā taṡqafannahum fil-ḥarbi fa syarrid bihim man khalfahum la'allahum yażżakkarụn","at":"Maka jika engkau (Muhammad) mengungguli mereka dalam peperangan, maka cerai-beraikanlah orang-orang yang di belakang mereka dengan (menumpas) mereka, agar mereka mengambil pelajaran."},{"id":"58","ar":"وَاِمَّا تَخَافَنَّ مِنْ قَوْمٍ خِيَانَةً فَانْۢبِذْ اِلَيْهِمْ عَلٰى سَوَاۤءٍۗ اِنَّ اللّٰهَ لَا يُحِبُّ الْخَاۤىِٕنِيْنَ ","bc":"wa immā takhāfanna ming qaumin khiyānatan fambiż ilaihim 'alā sawā`, innallāha lā yuḥibbul-khā`inīn","at":"Dan jika engkau (Muhammad) khawatir akan (terjadinya) pengkhianatan dari suatu golongan, maka kembalikanlah perjanjian itu kepada mereka dengan cara yang jujur. Sungguh, Allah tidak menyukai orang yang berkhianat."},{"id":"59","ar":"وَلَا يَحْسَبَنَّ الَّذِيْنَ كَفَرُوْا سَبَقُوْاۗ اِنَّهُمْ لَا يُعْجِزُوْنَ ","bc":"wa lā yaḥsabannallażīna kafarụ sabaqụ, innahum lā yu'jizụn","at":"Dan janganlah orang-orang kafir mengira, bahwa mereka akan dapat lolos (dari kekuasaan Allah). Sungguh, mereka tidak dapat melemahkan (Allah)."},{"id":"60","ar":"وَاَعِدُّوْا لَهُمْ مَّا اسْتَطَعْتُمْ مِّنْ قُوَّةٍ وَّمِنْ رِّبَاطِ الْخَيْلِ تُرْهِبُوْنَ بِهٖ عَدُوَّ اللّٰهِ وَعَدُوَّكُمْ وَاٰخَرِيْنَ مِنْ دُوْنِهِمْۚ لَا تَعْلَمُوْنَهُمْۚ اَللّٰهُ يَعْلَمُهُمْۗ وَمَا تُنْفِقُوْا مِنْ شَيْءٍ فِيْ سَبِيْلِ اللّٰهِ يُوَفَّ اِلَيْكُمْ وَاَنْتُمْ لَا تُظْلَمُوْنَ ","bc":"wa a'iddụ lahum mastaṭa'tum ming quwwatiw wa mir ribāṭil-khaili tur-hibụna bihī 'aduwwallāhi wa 'aduwwakum wa ākharīna min dụnihim, lā ta'lamụnahum, allāhu ya'lamuhum, wa mā tunfiqụ min syai`in fī sabīlillāhi yuwaffa ilaikum wa antum lā tuẓlamụn","at":"Dan persiapkanlah dengan segala kemampuan untuk menghadapi mereka dengan kekuatan yang kamu miliki dan dari pasukan berkuda yang dapat menggentarkan musuh Allah, musuhmu dan orang-orang selain mereka yang kamu tidak mengetahuinya; tetapi Allah mengetahuinya. Apa saja yang kamu infakkan di jalan Allah niscaya akan dibalas dengan cukup kepadamu dan kamu tidak akan dizalimi (dirugikan)."},{"id":"61","ar":"۞ وَاِنْ جَنَحُوْا لِلسَّلْمِ فَاجْنَحْ لَهَا وَتَوَكَّلْ عَلَى اللّٰهِ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ ","bc":"wa in janaḥụ lis-salmi fajnaḥ lahā wa tawakkal 'alallāh, innahụ huwas-samī'ul-'alīm","at":"Tetapi jika mereka condong kepada perdamaian, maka terimalah dan bertawakallah kepada Allah. Sungguh, Dia Maha Mendengar, Maha Mengetahui."},{"id":"62","ar":"وَاِنْ يُّرِيْدُوْٓا اَنْ يَّخْدَعُوْكَ فَاِنَّ حَسْبَكَ اللّٰهُ ۗهُوَ الَّذِيْٓ اَيَّدَكَ بِنَصْرِهٖ وَبِالْمُؤْمِنِيْنَۙ ","bc":"wa iy yurīdū ay yakhda'ụka fa inna ḥasbakallāh, huwallażī ayyadaka binaṣrihī wa bil-mu`minīn","at":"Dan jika mereka hendak menipumu, maka sesungguhnya cukuplah Allah (menjadi pelindung) bagimu. Dialah yang memberikan kekuatan kepadamu dengan pertolongan-Nya dan dengan (dukungan) orang-orang mukmin,"},{"id":"63","ar":"وَاَلَّفَ بَيْنَ قُلُوْبِهِمْۗ لَوْاَنْفَقْتَ مَا فِى الْاَرْضِ جَمِيْعًا مَّآ اَلَّفْتَ بَيْنَ قُلُوْبِهِمْ وَلٰكِنَّ اللّٰهَ اَلَّفَ بَيْنَهُمْۗ اِنَّهٗ عَزِيْزٌ حَكِيْمٌ ","bc":"wa allafa baina qulụbihim, lau anfaqta mā fil-arḍi jamī'am mā allafta baina qulụbihim wa lākinnallāha allafa bainahum, innahụ 'azīzun ḥakīm","at":"dan Dia (Allah) yang mempersatukan hati mereka (orang yang beriman). Walaupun kamu menginfakkan semua (kekayaan) yang berada di bumi, niscaya kamu tidak dapat mempersatukan hati mereka, tetapi Allah telah mempersatukan hati mereka. Sungguh, Dia Mahaperkasa, Mahabijaksana."},{"id":"64","ar":"يٰٓاَيُّهَا النَّبِيُّ حَسْبُكَ اللّٰهُ وَمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِيْنَ ","bc":"yā ayyuhan-nabiyyu ḥasbukallāhu wa manittaba'aka minal-mu`minīn","at":"Wahai Nabi (Muhammad)! Cukuplah Allah (menjadi pelindung) bagimu dan bagi orang-orang mukmin yang mengikutimu."},{"id":"65","ar":"يٰٓاَيُّهَا النَّبِيُّ حَرِّضِ الْمُؤْمِنِيْنَ عَلَى الْقِتَالِۗ اِنْ يَّكُنْ مِّنْكُمْ عِشْرُوْنَ صَابِرُوْنَ يَغْلِبُوْا مِائَتَيْنِۚ وَاِنْ يَّكُنْ مِّنْكُمْ مِّائَةٌ يَّغْلِبُوْٓا اَلْفًا مِّنَ الَّذِيْنَ كَفَرُوْا بِاَنَّهُمْ قَوْمٌ لَّا يَفْقَهُوْنَ ","bc":"yā ayyuhan-nabiyyu ḥarriḍil-mu`minīna 'alal-qitāl, iy yakum mingkum 'isyrụna ṣābirụna yaglibụ mi`ataīn, wa iy yakum mingkum mi`atuy yaglibū alfam minallażīna kafarụ bi`annahum qaumul lā yafqahụn","at":"Wahai Nabi (Muhammad)! Kobarkanlah semangat para mukmin untuk berperang. Jika ada dua puluh orang yang sabar di antara kamu, niscaya mereka dapat mengalahkan dua ratus orang musuh. Dan jika ada seratus orang (yang sabar) di antara kamu, niscaya mereka dapat mengalahkan seribu orang kafir, karena orang-orang kafir itu adalah kaum yang tidak mengerti."},{"id":"66","ar":"اَلْـٰٔنَ خَفَّفَ اللّٰهُ عَنْكُمْ وَعَلِمَ اَنَّ فِيْكُمْ ضَعْفًاۗ فَاِنْ يَّكُنْ مِّنْكُمْ مِّائَةٌ صَابِرَةٌ يَّغْلِبُوْا مِائَتَيْنِۚ وَاِنْ يَّكُنْ مِّنْكُمْ اَلْفٌ يَّغْلِبُوْٓا اَلْفَيْنِ بِاِذْنِ اللّٰهِ ۗوَاللّٰهُ مَعَ الصّٰبِرِيْنَ ","bc":"al-āna khaffafallāhu 'angkum wa 'alima anna fīkum ḍa'fā, fa iy yakum mingkum mi`atun ṣābiratuy yaglibụ mi`ataīn, wa iy yakum mingkum alfuy yaglibū alfaini bi`iżnillāh, wallāhu ma'aṣ-ṣābirīn","at":"Sekarang Allah telah meringankan kamu karena Dia mengetahui bahwa ada kelemahan padamu. Maka jika di antara kamu ada seratus orang yang sabar, niscaya mereka dapat mengalahkan dua ratus (orang musuh); dan jika di antara kamu ada seribu orang (yang sabar), niscaya mereka dapat mengalahkan dua ribu orang dengan seizin Allah. Allah beserta orang-orang yang sabar."},{"id":"67","ar":"مَاكَانَ لِنَبِيٍّ اَنْ يَّكُوْنَ لَهٗٓ اَسْرٰى حَتّٰى يُثْخِنَ فِى الْاَرْضِۗ تُرِيْدُوْنَ عَرَضَ الدُّنْيَاۖ وَاللّٰهُ يُرِيْدُ الْاٰخِرَةَۗ وَاللّٰهُ عَزِيْزٌحَكِيْمٌ ","bc":"mā kāna linabiyyin ay yakụna lahū asrā ḥattā yuṡkhina fil-arḍ, turīdụna 'araḍad-dun-yā wallāhu yurīdul-ākhirah, wallāhu 'azīzun ḥakīm","at":"Tidaklah pantas, bagi seorang nabi mempunyai tawanan sebelum dia dapat melumpuhkan musuhnya di bumi. Kamu menghendaki harta benda duniawi sedangkan Allah menghendaki (pahala) akhirat (untukmu). Allah Mahaperkasa, Mahabijaksana."},{"id":"68","ar":"لَوْلَاكِتٰبٌ مِّنَ اللّٰهِ سَبَقَ لَمَسَّكُمْ فِيْمَآ اَخَذْتُمْ عَذَابٌ عَظِيْمٌ ","bc":"lau lā kitābum minallāhi sabaqa lamassakum fīmā akhażtum 'ażābun 'aẓīm","at":"Sekiranya tidak ada ketetapan terdahulu dari Allah, niscaya kamu ditimpa siksaan yang besar karena (tebusan) yang kamu ambil."},{"id":"69","ar":"فَكُلُوْا مِمَّاغَنِمْتُمْ حَلٰلًا طَيِّبًاۖ وَّاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"fa kulụ mimmā ganimtum ḥalālan ṭayyibaw wattaqullāh, innallāha gafụrur raḥīm","at":"Maka makanlah dari sebagian rampasan perang yang telah kamu peroleh itu, sebagai makanan yang halal lagi baik, dan bertakwalah kepada Allah. Sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"70","ar":"يٰٓاَيُّهَا النَّبِيُّ قُلْ لِّمَنْ فِيْٓ اَيْدِيْكُمْ مِّنَ الْاَسْرٰٓىۙ اِنْ يَّعْلَمِ اللّٰهُ فِيْ قُلُوْبِكُمْ خَيْرًا يُّؤْتِكُمْ خَيْرًا مِّمَّآ اُخِذَ مِنْكُمْ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ","bc":"yā ayyuhan-nabiyyu qul liman fī aidīkum minal-asrā iy ya'lamillāhu fī qulụbikum khairay yu`tikum khairam mimmā ukhiża mingkum wa yagfir lakum, wallāhu gafụrur raḥīm","at":"Wahai Nabi (Muhammad)! Katakanlah kepada para tawanan perang yang ada di tanganmu, “Jika Allah mengetahui ada kebaikan di dalam hatimu, niscaya Dia akan memberikan yang lebih baik dari apa yang telah diambil darimu dan Dia akan mengampuni kamu.” Allah Maha Pengampun, Maha Penyayang."},{"id":"71","ar":"وَاِنْ يُّرِيْدُوْا خِيَانَتَكَ فَقَدْ خَانُوا اللّٰهَ مِنْ قَبْلُ فَاَمْكَنَ مِنْهُمْ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ","bc":"wa iy yurīdụ khiyānataka fa qad khānullāha ming qablu fa amkana min-hum wallāhu 'alīmun ḥakīm","at":"Tetapi jika mereka (tawanan itu) hendak mengkhianatimu (Muhammad) maka sesungguhnya sebelum itu pun mereka telah berkhianat kepada Allah, maka Dia memberikan kekuasaan kepadamu atas mereka. Allah Maha Mengetahui, Mahabijaksana."},{"id":"72","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ فِيْ سَبِيْلِ اللّٰهِ وَالَّذِيْنَ اٰوَوْا وَّنَصَرُوْٓا اُولٰۤىِٕكَ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ وَالَّذِيْنَ اٰمَنُوْا وَلَمْ يُهَاجِرُوْا مَا لَكُمْ مِّنْ وَّلَايَتِهِمْ مِّنْ شَيْءٍ حَتّٰى يُهَاجِرُوْاۚ وَاِنِ اسْتَنْصَرُوْكُمْ فِى الدِّيْنِ فَعَلَيْكُمُ النَّصْرُ اِلَّا عَلٰى قَوْمٍۢ بَيْنَكُمْ وَبَيْنَهُمْ مِّيْثَاقٌۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ","bc":"innallażīna āmanụ wa hājarụ wa jāhadụ bi`amwālihim wa anfusihim fī sabīlillāhi wallażīna āwaw wa naṣarū ulā`ika ba'ḍuhum auliyā`u ba'ḍ, wallażīna āmanụ wa lam yuhājirụ mā lakum miw walāyatihim min syai`in ḥattā yuhājirụ, wa inistanṣarụkum fid-dīni fa 'alaikumun-naṣru illā 'alā qaumim bainakum wa bainahum mīṡāq, wallāhu bimā ta'malụna baṣīr","at":"Sesungguhnya orang-orang yang beriman dan berhijrah serta berjihad dengan harta dan jiwanya pada jalan Allah dan orang-orang yang memberikan tempat kediaman dan memberi pertolongan (kepada Muhajirin), mereka itu satu sama lain saling melindungi. Dan (terhadap) orang-orang yang beriman tetapi belum berhijrah, maka tidak ada kewajiban sedikit pun bagimu melindungi mereka, sampai mereka berhijrah. (Tetapi) jika mereka meminta pertolongan kepadamu dalam (urusan pembelaan) agama, maka kamu wajib memberikan pertolongan kecuali terhadap kaum yang telah terikat perjanjian antara kamu dengan mereka. Dan Allah Maha Melihat apa yang kamu kerjakan."},{"id":"73","ar":"وَالَّذِيْنَ كَفَرُوْا بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ اِلَّا تَفْعَلُوْهُ تَكُنْ فِتْنَةٌ فِى الْاَرْضِ وَفَسَادٌ كَبِيْرٌۗ","bc":"wallażīna kafarụ ba'ḍuhum auliyā`u ba'ḍ, illā taf'alụhu takun fitnatun fil-arḍi wa fasādung kabīr","at":"Dan orang-orang yang kafir, sebagian mereka melindungi sebagian yang lain. Jika kamu tidak melaksanakan apa yang telah diperintahkan Allah (saling melindungi), niscaya akan terjadi kekacauan di bumi dan kerusakan yang besar."},{"id":"74","ar":"وَالَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ وَالَّذِيْنَ اٰوَوْا وَّنَصَرُوْٓا اُولٰۤىِٕكَ هُمُ الْمُؤْمِنُوْنَ حَقًّاۗ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ ","bc":"wallażīna āmanụ wa hājarụ wa jāhadụ fī sabīlillāhi wallażīna āwaw wa naṣarū ulā`ika humul-mu`minụna ḥaqqā, lahum magfiratuw wa rizqung karīm","at":"Dan orang-orang yang beriman dan berhijrah serta berjihad di jalan Allah, dan orang-orang yang memberi tempat kediaman dan memberi pertolongan (kepada orang Muhajirin), mereka itulah orang yang benar-benar beriman. Mereka memperoleh ampunan dan rezeki (nikmat) yang mulia."},{"id":"75","ar":"وَالَّذِيْنَ اٰمَنُوْا مِنْۢ بَعْدُ وَهَاجَرُوْا وَجَاهَدُوْا مَعَكُمْ فَاُولٰۤىِٕكَ مِنْكُمْۗ وَاُولُوا الْاَرْحَامِ بَعْضُهُمْ اَوْلٰى بِبَعْضٍ فِيْ كِتٰبِ اللّٰهِ ۗاِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ","bc":"wallażīna āmanụ mim ba'du wa hājarụ wa jāhadụ ma'akum fa ulā`ika mingkum, wa ulul-ar-ḥāmi ba'ḍuhum aulā biba'ḍin fī kitābillāh, innallāha bikulli syai`in 'alīm","at":"Dan orang-orang yang beriman setelah itu, kemudian berhijrah dan berjihad bersamamu maka mereka termasuk golonganmu. Orang-orang yang mempunyai hubungan kerabat itu sebagiannya lebih berhak terhadap sesamanya (daripada yang bukan kerabat) menurut Kitab Allah. Sungguh, Allah Maha Mengetahui segala sesuatu."}],al_ankabut=[{"id":"1","ar":"الۤمّۤ ۗ ","bc":"alif lām mīm","at":"Alif Lam Mim."},{"id":"2","ar":"اَحَسِبَ النَّاسُ اَنْ يُّتْرَكُوْٓا اَنْ يَّقُوْلُوْٓا اٰمَنَّا وَهُمْ لَا يُفْتَنُوْنَ ","bc":"a ḥasiban-nāsu ay yutrakū ay yaqụlū āmannā wa hum lā yuftanụn","at":"Apakah manusia mengira bahwa mereka akan dibiarkan hanya dengan mengatakan, “Kami telah beriman,” dan mereka tidak diuji?"},{"id":"3","ar":"وَلَقَدْ فَتَنَّا الَّذِيْنَ مِنْ قَبْلِهِمْ فَلَيَعْلَمَنَّ اللّٰهُ الَّذِيْنَ صَدَقُوْا وَلَيَعْلَمَنَّ الْكٰذِبِيْنَ ","bc":"wa laqad fatannallażīna ming qablihim fa laya'lamannallāhullażīna ṣadaqụ walaya'lamannal-kāżibīn","at":"Dan sungguh, Kami telah menguji orang-orang sebelum mereka, maka Allah pasti mengetahui orang-orang yang benar dan pasti mengetahui orang-orang yang dusta."},{"id":"4","ar":"اَمْ حَسِبَ الَّذِيْنَ يَعْمَلُوْنَ السَّيِّاٰتِ اَنْ يَّسْبِقُوْنَا ۗسَاۤءَ مَا يَحْكُمُوْنَ ","bc":"am ḥasiballażīna ya'malụnas-sayyi`āti ay yasbiqụnā, sā`a mā yaḥkumụn","at":"Ataukah orang-orang yang mengerjakan kejahatan itu mengira bahwa mereka akan luput dari (azab) Kami? Sangatlah buruk apa yang mereka tetapkan itu! "},{"id":"5","ar":"مَنْ كَانَ يَرْجُوْا لِقَاۤءَ اللّٰهِ فَاِنَّ اَجَلَ اللّٰهِ لَاٰتٍ ۗوَهُوَ السَّمِيْعُ الْعَلِيْمُ ","bc":"mang kāna yarjụ liqā`allāhi fa inna ajalallāhi la`āt, wa huwas-samī'ul-'alīm","at":"Barangsiapa mengharap pertemuan dengan Allah, maka sesungguhnya waktu (yang dijanjikan) Allah pasti datang. Dan Dia Yang Maha Mendengar, Maha Mengetahui."},{"id":"6","ar":"وَمَنْ جَاهَدَ فَاِنَّمَا يُجَاهِدُ لِنَفْسِهٖ ۗاِنَّ اللّٰهَ لَغَنِيٌّ عَنِ الْعٰلَمِيْنَ ","bc":"wa man jāhada fa innamā yujāhidu linafsih, innallāha laganiyyun 'anil-'ālamīn","at":"Dan barangsiapa berjihad, maka sesungguhnya jihadnya itu untuk dirinya sendiri. Sungguh, Allah Mahakaya (tidak memerlukan sesuatu) dari seluruh alam."},{"id":"7","ar":"وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّاٰتِهِمْ وَلَنَجْزِيَنَّهُمْ اَحْسَنَ الَّذِيْ كَانُوْا يَعْمَلُوْنَ ","bc":"wallażīna āmanụ wa 'amiluṣ-ṣāliḥāti lanukaffiranna 'an-hum sayyi`ātihim wa lanajziyannahum aḥsanallażī kānụ ya'malụn","at":"Dan orang-orang yang beriman dan mengerjakan kebajikan, pasti akan Kami hapus kesalahan-kesalahannya dan mereka pasti akan Kami beri balasan yang lebih baik dari apa yang mereka kerjakan."},{"id":"8","ar":"وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِ حُسْنًا ۗوَاِنْ جَاهَدٰكَ لِتُشْرِكَ بِيْ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ فَلَا تُطِعْهُمَا ۗاِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ ","bc":"wa waṣṣainal-insāna biwālidaihi ḥusnā, wa in jāhadāka litusyrika bī mā laisa laka bihī 'ilmun fa lā tuṭi'humā, ilayya marji'ukum fa unabbi`ukum bimā kuntum ta'malụn","at":"Dan Kami wajibkan kepada manusia agar (berbuat) kebaikan kepada kedua orang tuanya. Dan jika keduanya memaksamu untuk mempersekutukan Aku dengan sesuatu yang engkau tidak mempunyai ilmu tentang itu, maka janganlah engkau patuhi keduanya. Hanya kepada-Ku tempat kembalimu, dan akan Aku beritakan kepadamu apa yang telah kamu kerjakan."},{"id":"9","ar":"وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُدْخِلَنَّهُمْ فِى الصّٰلِحِيْنَ ","bc":"wallażīna āmanụ wa 'amiluṣ-ṣāliḥāti lanudkhilannahum fiṣ-ṣāliḥīn","at":"Dan orang-orang yang beriman dan mengerjakan kebajikan mereka pasti akan Kami masukkan mereka ke dalam (golongan) orang yang saleh."},{"id":"10","ar":"وَمِنَ النَّاسِ مَنْ يَّقُوْلُ اٰمَنَّا بِاللّٰهِ فَاِذَآ اُوْذِيَ فِى اللّٰهِ جَعَلَ فِتْنَةَ النَّاسِ كَعَذَابِ اللّٰهِ ۗوَلَىِٕنْ جَاۤءَ نَصْرٌ مِّنْ رَّبِّكَ لَيَقُوْلُنَّ اِنَّا كُنَّا مَعَكُمْۗ اَوَلَيْسَ اللّٰهُ بِاَعْلَمَ بِمَا فِيْ صُدُوْرِ الْعٰلَمِيْنَ ","bc":"wa minan-nāsi may yaqụlu āmannā billāhi fa iżā ụżiya fillāhi ja'ala fitnatan-nāsi ka'ażābillāh, wa la`in jā`a naṣrum mir rabbika layaqụlunna innā kunnā ma'akum, a wa laisallāhu bi`a'lama bimā fī ṣudụril-'ālamīn","at":"Dan di antara manusia ada sebagian yang berkata, “Kami beriman kepada Allah,” tetapi apabila dia disakiti (karena dia beriman) kepada Allah, dia menganggap cobaan manusia itu sebagai siksaan Allah. Dan jika datang pertolongan dari Tuhanmu, niscaya mereka akan berkata, “Sesungguhnya kami bersama kamu.” Bukankah Allah lebih mengetahui apa yang ada di dalam dada semua manusia?"},{"id":"11","ar":"وَلَيَعْلَمَنَّ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَلَيَعْلَمَنَّ الْمُنٰفِقِيْنَ ","bc":"wa laya'lamannallāhullażīna āmanụ wa laya'lamannal-munāfiqīn","at":"Dan Allah pasti mengetahui orang-orang yang beriman dan Dia pasti mengetahui orang-orang yang munafik."},{"id":"12","ar":"وَقَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوا اتَّبِعُوْا سَبِيْلَنَا وَلْنَحْمِلْ خَطٰيٰكُمْۗ وَمَا هُمْ بِحَامِلِيْنَ مِنْ خَطٰيٰهُمْ مِّنْ شَيْءٍۗ اِنَّهُمْ لَكٰذِبُوْنَ ","bc":"wa qālallażīna kafarụ lillażīna āmanuttabi'ụ sabīlanā walnaḥmil khaṭāyākum, wa mā hum biḥāmilīna min khaṭāyāhum min syaī`, innahum lakāżibụn","at":"Dan orang-orang yang kafir berkata kepada orang-orang yang beriman, “Ikutilah jalan kami, dan kami akan memikul dosa-dosamu,” padahal mereka sedikit pun tidak (sanggup) memikul dosa-dosa mereka sendiri. Sesungguhnya mereka benar-benar pendusta."},{"id":"13","ar":"وَلَيَحْمِلُنَّ اَثْقَالَهُمْ وَاَثْقَالًا مَّعَ اَثْقَالِهِمْ وَلَيُسْـَٔلُنَّ يَوْمَ الْقِيٰمَةِ عَمَّا كَانُوْا يَفْتَرُوْنَ ","bc":"wa layaḥmilunna aṡqālahum wa aṡqālam ma'a aṡqālihim wa layus`alunna yaumal-qiyāmati 'ammā kānụ yaftarụn","at":"Dan mereka benar-benar akan memikul dosa-dosa mereka sendiri, dan dosa-dosa yang lain bersama dosa mereka, dan pada hari Kiamat mereka pasti akan ditanya tentang kebohongan yang selalu mereka ada-adakan."},{"id":"14","ar":"وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَلَبِثَ فِيْهِمْ اَلْفَ سَنَةٍ اِلَّا خَمْسِيْنَ عَامًا ۗفَاَخَذَهُمُ الطُّوْفَانُ وَهُمْ ظٰلِمُوْنَ ","bc":"wa laqad arsalnā nụḥan ilā qaumihī fa labiṡa fīhim alfa sanatin illā khamsīna 'āmā, fa akhażahumuṭ-ṭụfānu wa hum ẓālimụn","at":"Dan sungguh, Kami telah mengutus Nuh kepada kaumnya, maka dia tinggal bersama mereka selama seribu tahun kurang lima puluh tahun. Kemudian mereka dilanda banjir besar, sedangkan mereka adalah orang-orang yang zalim."},{"id":"15","ar":"فَاَنْجَيْنٰهُ وَاَصْحٰبَ السَّفِيْنَةِ وَجَعَلْنٰهَآ اٰيَةً لِّلْعٰلَمِيْنَ ","bc":"fa anjaināhu wa aṣ-hābas-safīnati wa ja'alnāhā āyatal lil-'ālamīn","at":"Maka Kami selamatkan Nuh dan orang-orang yang berada di kapal itu, dan Kami jadikan (peristiwa) itu sebagai pelajaran bagi semua manusia."},{"id":"16","ar":"وَاِبْرٰهِيْمَ اِذْ قَالَ لِقَوْمِهِ اعْبُدُوا اللّٰهَ وَاتَّقُوْهُ ۗذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ","bc":"wa ibrāhīma iż qāla liqaumihi'budullāha wattaqụh, żālikum khairul lakum ing kuntum ta'lamụn","at":"Dan (ingatlah) Ibrahim, ketika dia berkata kepada kaumnya, “Sembahlah Allah dan bertakwalah kepada-Nya. Yang demikian itu lebih baik bagimu, jika kamu mengetahui."},{"id":"17","ar":"اِنَّمَا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ اَوْثَانًا وَّتَخْلُقُوْنَ اِفْكًا ۗاِنَّ الَّذِيْنَ تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ لَا يَمْلِكُوْنَ لَكُمْ رِزْقًا فَابْتَغُوْا عِنْدَ اللّٰهِ الرِّزْقَ وَاعْبُدُوْهُ وَاشْكُرُوْا لَهٗ ۗاِلَيْهِ تُرْجَعُوْنَ ","bc":"innamā ta'budụna min dụnillāhi auṡānaw wa takhluqụna ifkā, innallażīna ta'budụna min dụnillāhi lā yamlikụna lakum rizqan fabtagụ 'indallāhir-rizqa wa'budụhu wasykurụ lah, ilaihi turja'ụn","at":"Sesungguhnya yang kamu sembah selain Allah hanyalah berhala-berhala, dan kamu membuat kebohongan. Sesungguhnya apa yang kamu sembah selain Allah itu tidak mampu memberikan rezeki kepadamu; maka mintalah rezeki dari Allah, dan sembahlah Dia dan bersyukurlah kepada-Nya. Hanya kepada-Nya kamu akan dikembalikan."},{"id":"18","ar":"وَاِنْ تُكَذِّبُوْا فَقَدْ كَذَّبَ اُمَمٌ مِّنْ قَبْلِكُمْ ۗوَمَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ الْمُبِيْنُ ","bc":"wa in tukażżibụ fa qad każżaba umamum ming qablikum, wa mā 'alar-rasụli illal-balāgul-mubīn","at":"Dan jika kamu (orang kafir) mendustakan, maka sungguh, umat sebelum kamu juga telah mendustakan (para rasul). Dan kewajiban rasul itu hanyalah menyampaikan (agama Allah) dengan jelas.”"},{"id":"19","ar":"اَوَلَمْ يَرَوْا كَيْفَ يُبْدِئُ اللّٰهُ الْخَلْقَ ثُمَّ يُعِيْدُهٗ ۗاِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ ","bc":"a wa lam yarau kaifa yubdi`ullāhul-khalqa ṡumma yu'īduh, inna żālika 'alallāhi yasīr","at":"Dan apakah mereka tidak memperhatikan bagaimana Allah memulai penciptaan (makhluk), kemudian Dia mengulanginya (kembali). Sungguh, yang demikian itu mudah bagi Allah."},{"id":"20","ar":"قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ بَدَاَ الْخَلْقَ ثُمَّ اللّٰهُ يُنْشِئُ النَّشْاَةَ الْاٰخِرَةَ ۗاِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ۚ","bc":"qul sīrụ fil-arḍi fanẓurụ kaifa bada`al-khalqa ṡummallāhu yunsyi`un-nasy`atal-ākhirah, innallāha 'alā kulli syai`ing qadīr","at":"Katakanlah, “Berjalanlah di bumi, maka perhatikanlah bagaimana (Allah) memulai penciptaan (makhluk), kemudian Allah menjadikan kejadian yang akhir. Sungguh, Allah Mahakuasa atas segala sesuatu."},{"id":"21","ar":"يُعَذِّبُ مَنْ يَّشَاۤءُ وَيَرْحَمُ مَنْ يَّشَاۤءُ ۚوَاِلَيْهِ تُقْلَبُوْنَ ","bc":"yu'ażżibu may yasyā`u wa yar-ḥamu may yasyā`, wa ilaihi tuqlabụn","at":"Dia (Allah) mengazab siapa yang Dia kehendaki dan memberi rahmat kepada siapa yang Dia kehendaki, dan hanya kepada-Nya kamu akan dikembalikan."},{"id":"22","ar":"وَمَآ اَنْتُمْ بِمُعْجِزِيْنَ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ ۖوَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ","bc":"wa mā antum bimu'jizīna fil-arḍi wa lā fis-samā`i wa mā lakum min dụnillāhi miw waliyyiw wa lā naṣīr","at":"Dan kamu sama sekali tidak dapat melepaskan diri (dari azab Allah) baik di bumi maupun di langit, dan tidak ada pelindung dan penolong bagimu selain Allah."},{"id":"23","ar":"وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِ اللّٰهِ وَلِقَاۤىِٕهٖٓ اُولٰۤىِٕكَ يَىِٕسُوْا مِنْ رَّحْمَتِيْ وَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"wallażīna kafarụ bi`āyātillāhi wa liqā`ihī ulā`ika ya`isụ mir raḥmatī wa ulā`ika lahum 'ażābun alīm","at":"Dan orang-orang yang mengingkari ayat-ayat Allah dan pertemuan dengan-Nya, mereka berputus asa dari rahmat-Ku, dan mereka itu akan mendapat azab yang pedih."},{"id":"24","ar":"فَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوا اقْتُلُوْهُ اَوْ حَرِّقُوْهُ فَاَنْجٰىهُ اللّٰهُ مِنَ النَّارِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ","bc":"fa mā kāna jawāba qaumihī illā ang qāluqtulụhu au ḥarriqụhu fa anjāhullāhu minan-nār, inna fī żālika la`āyātil liqaumiy yu`minụn","at":"Maka tidak ada jawaban kaumnya (Ibrahim), selain mengatakan, “Bunuhlah atau bakarlah dia,” lalu Allah menyelamatkannya dari api. Sungguh, pada yang demikian itu pasti terdapat tanda-tanda (kebesaran Allah) bagi orang yang beriman."},{"id":"25","ar":"وَقَالَ اِنَّمَا اتَّخَذْتُمْ مِّنْ دُوْنِ اللّٰهِ اَوْثَانًاۙ مَّوَدَّةَ بَيْنِكُمْ فِى الْحَيٰوةِ الدُّنْيَا ۚ ثُمَّ يَوْمَ الْقِيٰمَةِ يَكْفُرُ بَعْضُكُمْ بِبَعْضٍ وَّيَلْعَنُ بَعْضُكُمْ بَعْضًا ۖوَّمَأْوٰىكُمُ النَّارُ وَمَا لَكُمْ مِّنْ نّٰصِرِيْنَۖ ","bc":"wa qāla innamattakhażtum min dụnillāhi auṡānam mawaddata bainikum fil-ḥayātid-dun-yā, ṡumma yaumal-qiyāmati yakfuru ba'ḍukum biba'ḍiw wa yal'anu ba'ḍukum ba'ḍaw wa ma`wākumun-nāru wa mā lakum min nāṣirīn","at":"Dan dia (Ibrahim) berkata, “Sesungguhnya berhala-berhala yang kamu sembah selain Allah, hanya untuk menciptakan perasaan kasih sayang di antara kamu dalam kehidupan di dunia, kemudian pada hari Kiamat sebagian kamu akan saling mengingkari dan saling mengutuk; dan tempat kembalimu ialah neraka, dan sama sekali tidak ada penolong bagimu.”"},{"id":"26","ar":"۞ فَاٰمَنَ لَهٗ لُوْطٌۘ وَقَالَ اِنِّيْ مُهَاجِرٌ اِلٰى رَبِّيْ ۗاِنَّهٗ هُوَ الْعَزِيْزُ الْحَكِيْمُ ","bc":"fa āmana lahụ lụṭ, wa qāla innī muhājirun ilā rabbī, innahụ huwal-'azīzul-ḥakīm","at":"Maka Lut membenarkan (kenabian Ibrahim). Dan dia (Ibrahim) berkata, “Sesungguhnya aku harus berpindah ke (tempat yang diperintahkan) Tuhanku; sungguh, Dialah Yang Mahaperkasa, Mahabijaksana.”"},{"id":"27","ar":"وَوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَ وَجَعَلْنَا فِيْ ذُرِّيَّتِهِ النُّبُوَّةَ وَالْكِتٰبَ وَاٰتَيْنٰهُ اَجْرَهٗ فِى الدُّنْيَا ۚوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ ","bc":"wa wahabnā lahū is-ḥāqa wa ya'qụba wa ja'alnā fī żurriyyatihin-nubuwwata wal-kitāba wa ātaināhu ajrahụ fid-dun-yā, wa innahụ fil-ākhirati laminaṣ-ṣāliḥīn","at":"Dan Kami anugerahkan kepada Ibrahim, Ishak dan Yakub, dan Kami jadikan kenabian dan kitab kepada keturunannya, dan Kami berikan kepadanya balasannya di dunia; dan sesungguhnya dia di akhirat, termasuk orang yang saleh. "},{"id":"28","ar":"وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اِنَّكُمْ لَتَأْتُوْنَ الْفَاحِشَةَ ۖمَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِّنَ الْعٰلَمِيْنَ ","bc":"wa lụṭan iż qāla liqaumihī innakum lata`tụnal-fāḥisyata mā sabaqakum bihā min aḥadim minal-'ālamīn","at":"Dan (ingatlah) ketika Lut berkata kepada kaumnya, “Kamu benar-benar melakukan perbuatan yang sangat keji (homoseksual) yang belum pernah dilakukan oleh seorang pun dari umat-umat sebelum kamu. "},{"id":"29","ar":"اَىِٕنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ وَتَقْطَعُوْنَ السَّبِيْلَ ەۙ وَتَأْتُوْنَ فِيْ نَادِيْكُمُ الْمُنْكَرَ ۗفَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوا ائْتِنَا بِعَذَابِ اللّٰهِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ","bc":"a innakum lata`tụnar-rijāla wa taqṭa'ụnas-sabīla wa ta`tụna fī nādīkumul-mungkar, fa mā kāna jawāba qaumihī illā ang qālu`tinā bi'ażābillāhi ing kunta minaṣ-ṣādiqīn","at":"Apakah pantas kamu mendatangi laki-laki, menyamun dan mengerjakan kemungkaran di tempat-tempat pertemuanmu?” Maka jawaban kaumnya tidak lain hanya mengatakan, “Datangkanlah kepada kami azab Allah, jika engkau termasuk orang-orang yang benar.” "},{"id":"30","ar":"قَالَ رَبِّ انْصُرْنِيْ عَلَى الْقَوْمِ الْمُفْسِدِيْنَ","bc":"qāla rabbinṣurnī 'alal-qaumil-mufsidīn","at":"Dia (Lut) berdoa, “Ya Tuhanku, tolonglah aku (dengan menimpakan azab) atas golongan yang berbuat kerusakan itu.” "},{"id":"31","ar":"وَلَمَّا جَاۤءَتْ رُسُلُنَآ اِبْرٰهِيْمَ بِالْبُشْرٰىۙ قَالُوْٓا اِنَّا مُهْلِكُوْٓا اَهْلِ هٰذِهِ الْقَرْيَةِ ۚاِنَّ اَهْلَهَا كَانُوْا ظٰلِمِيْنَ ۚ","bc":"wa lammā jā`at rusulunā ibrāhīma bil-busyrā qālū innā muhlikū ahli hāżihil-qaryah, inna ahlahā kānụ ẓālimīn","at":"Dan ketika utusan Kami (para malaikat) datang kepada Ibrahim dengan membawa kabar gembira, mereka mengatakan, “Sungguh, kami akan membinasakan penduduk kota (Sodom) ini karena penduduknya sungguh orang-orang zalim.” "},{"id":"32","ar":"قَالَ اِنَّ فِيْهَا لُوْطًا ۗقَالُوْا نَحْنُ اَعْلَمُ بِمَنْ فِيْهَا ۖ لَنُنَجِّيَنَّهٗ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ كَانَتْ مِنَ الْغٰبِرِيْنَ ","bc":"qāla inna fīhā lụṭā, qālụ naḥnu a'lamu biman fīhā lanunajjiyannahụ wa ahlahū illamra`atahụ kānat minal-gābirīn","at":"Ibrahim berkata, “Sesungguhnya di kota itu ada Lut.” Mereka (para malaikat) berkata, “Kami lebih mengetahui siapa yang ada di kota itu. Kami pasti akan menyelamatkan dia dan pengikut-pengikutnya kecuali istrinya. Dia termasuk orang-orang yang tertinggal (dibinasakan).” "},{"id":"33","ar":"وَلَمَّآ اَنْ جَاۤءَتْ رُسُلُنَا لُوْطًا سِيْۤءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَّقَالُوْا لَا تَخَفْ وَلَا تَحْزَنْ ۗاِنَّا مُنَجُّوْكَ وَاَهْلَكَ اِلَّا امْرَاَتَكَ كَانَتْ مِنَ الْغٰبِرِيْنَ","bc":"wa lammā an jā`at rusulunā lụṭan sī`a bihim wa ḍāqa bihim żar'aw wa qālụ lā takhaf wa lā taḥzan, innā munajjụka wa ahlaka illamra`ataka kānat minal-gābirīn","at":"Dan ketika para utusan Kami (para malaikat) datang kepada Lut, dia merasa bersedih hati karena (kedatangan) mereka, dan (merasa) tidak mempunyai kekuatan untuk melindungi mereka, dan mereka (para utusan) berkata, “Janganlah engkau takut dan jangan (pula) bersedih hati. Sesungguhnya Kami akan menyelamatkanmu dan pengikut-pengikutmu, kecuali istrimu, dia termasuk orang-orang yang tinggal (dibinasakan).” "},{"id":"34","ar":"اِنَّا مُنْزِلُوْنَ عَلٰٓى اَهْلِ هٰذِهِ الْقَرْيَةِ رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَفْسُقُوْنَ ","bc":"innā munzilụna 'alā ahli hāżihil-qaryati rijzam minas-samā`i bimā kānụ yafsuqụn","at":"Sesungguhnya Kami akan menurunkan azab dari langit kepada penduduk kota ini karena mereka berbuat fasik. "},{"id":"35","ar":"وَلَقَدْ تَّرَكْنَا مِنْهَآ اٰيَةً ۢ بَيِّنَةً لِّقَوْمٍ يَّعْقِلُوْنَ ","bc":"wa laqat taraknā min-hā āyatam bayyinatal liqaumiy ya'qilụn","at":"Dan sungguh, tentang itu telah Kami tinggalkan suatu tanda yang nyata bagi orang-orang yang mengerti. "},{"id":"36","ar":"وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًاۙ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ وَارْجُوا الْيَوْمَ الْاٰخِرَ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ ۖ","bc":"wa ilā madyana akhāhum syu'aiban fa qāla yā qaumi'budullāha warjul-yaumal-ākhira wa lā ta'ṡau fil-arḍi mufsidīn","at":"Dan kepada penduduk Madyan, (Kami telah mengutus) saudara mereka Syuaib, dia berkata, “Wahai kaumku! Sembahlah Allah, harapkanlah (pahala) hari akhir, dan jangan kamu berkeliaran di bumi berbuat kerusakan.” "},{"id":"37","ar":"فَكَذَّبُوْهُ فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَ ۙ","bc":"fa każżabụhu fa akhażat-humur-rajfatu fa aṣbaḥụ fī dārihim jāṡimīn","at":"Mereka mendustakannya (Syuaib), maka mereka ditimpa gempa yang dahsyat, lalu jadilah mereka mayat-mayat yang bergelimpangan di tempat-tempat tinggal mereka."},{"id":"38","ar":"وَعَادًا وَّثَمُوْدَا۟ وَقَدْ تَّبَيَّنَ لَكُمْ مِّنْ مَّسٰكِنِهِمْۗ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيْلِ وَكَانُوْا مُسْتَبْصِرِيْنَ ۙ","bc":"wa 'ādaw wa ṡamụda wa qat tabayyana lakum mim masākinihim, wa zayyana lahumusy-syaiṭānu a'mālahum fa ṣaddahum 'anis-sabīli wa kānụ mustabṣirīn","at":"Juga (ingatlah) kaum ’Ad dan Samud, sungguh telah nyata bagi kamu (kehancuran mereka) dari (puing-puing) tempat tinggal mereka. Setan telah menjadikan terasa indah bagi mereka perbuatan (buruk) mereka, sehingga menghalangi mereka dari jalan (Allah), sedangkan mereka adalah orang-orang yang berpandangan tajam, "},{"id":"39","ar":"وَقَارُوْنَ وَفِرْعَوْنَ وَهَامٰنَۗ وَلَقَدْ جَاۤءَهُمْ مُّوْسٰى بِالْبَيِّنٰتِ فَاسْتَكْبَرُوْا فِى الْاَرْضِ وَمَا كَانُوْا سَابِقِيْنَ ۚ","bc":"wa qārụna wa fir'auna wa hāmān, wa laqad jā`ahum mụsā bil-bayyināti fastakbarụ fil-arḍi wa mā kānụ sābiqīn","at":"dan (juga) Karun, Fir‘aun dan Haman. Sungguh, telah datang kepada mereka Musa dengan (membawa) keterangan-keterangan yang nyata. Tetapi mereka berlaku sombong di bumi, dan mereka orang-orang yang tidak luput (dari azab Allah). "},{"id":"40","ar":"فَكُلًّا اَخَذْنَا بِذَنْۢبِهٖۙ فَمِنْهُمْ مَّنْ اَرْسَلْنَا عَلَيْهِ حَاصِبًا ۚوَمِنْهُمْ مَّنْ اَخَذَتْهُ الصَّيْحَةُ ۚوَمِنْهُمْ مَّنْ خَسَفْنَا بِهِ الْاَرْضَۚ وَمِنْهُمْ مَّنْ اَغْرَقْنَاۚ وَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ","bc":"fa kullan akhażnā biżambihī fa min-hum man arsalnā 'alaihi ḥāṣibā, wa min-hum man akhażat-huṣ-ṣaiḥah, wa min-hum man khasafnā bihil-arḍ, wa min-hum man agraqnā, wa mā kānallāhu liyaẓlimahum wa lāking kānū anfusahum yaẓlimụn","at":"Maka masing-masing (mereka itu) Kami azab karena dosa-dosanya, di antara mereka ada yang Kami timpakan kepadanya hujan batu kerikil, ada yang ditimpa suara keras yang mengguntur, ada yang Kami benamkan ke dalam bumi, dan ada pula yang Kami tenggelamkan. Allah sama sekali tidak hendak menzalimi mereka, akan tetapi merekalah yang menzalimi diri mereka sendiri. "},{"id":"41","ar":"مَثَلُ الَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اَوْلِيَاۤءَ كَمَثَلِ الْعَنْكَبُوْتِۚ اِتَّخَذَتْ بَيْتًاۗ وَاِنَّ اَوْهَنَ الْبُيُوْتِ لَبَيْتُ الْعَنْكَبُوْتِۘ لَوْ كَانُوْا يَعْلَمُوْنَ ","bc":"maṡalullażīnattakhażụ min dụnillāhi auliyā`a kamaṡalil-'angkabụt, ittakhażat baitā, wa inna auhanal-buyụti labaitul-'angkabụt, lau kānụ ya'lamụn","at":"Perumpamaan orang-orang yang mengambil pelindung selain Allah adalah seperti laba-laba yang membuat rumah. Dan sesungguhnya rumah yang paling lemah ialah rumah laba-laba, sekiranya mereka mengetahui. "},{"id":"42","ar":"اِنَّ اللّٰهَ يَعْلَمُ مَا يَدْعُوْنَ مِنْ دُوْنِهٖ مِنْ شَيْءٍۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ","bc":"innallāha ya'lamu mā yad'ụna min dụnihī min syaī`, wa huwal-'azīzul-ḥakīm","at":"Sungguh, Allah mengetahui apa saja yang mereka sembah selain Dia. Dan Dia Mahaperkasa, Mahabijaksana. "},{"id":"43","ar":"وَتِلْكَ الْاَمْثَالُ نَضْرِبُهَا لِلنَّاسِۚ وَمَا يَعْقِلُهَآ اِلَّا الْعَالِمُوْنَ ","bc":"wa tilkal-amṡālu naḍribuhā lin-nās, wa mā ya'qiluhā illal-'ālimụn","at":"Dan perumpamaan-perumpamaan ini Kami buat untuk manusia; dan tidak ada yang akan memahaminya kecuali mereka yang berilmu. "},{"id":"44","ar":"خَلَقَ اللّٰهُ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّلْمُؤْمِنِيْنَ ۔","bc":"khalaqallāhus-samāwāti wal-arḍa bil-ḥaqq, inna fī żālika la`āyatal lil-mu`minīn","at":"Allah menciptakan langit dan bumi dengan haq. Sungguh, pada yang demikian itu pasti terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang beriman. "},{"id":"45","ar":"اُتْلُ مَآ اُوْحِيَ اِلَيْكَ مِنَ الْكِتٰبِ وَاَقِمِ الصَّلٰوةَۗ اِنَّ الصَّلٰوةَ تَنْهٰى عَنِ الْفَحْشَاۤءِ وَالْمُنْكَرِ ۗوَلَذِكْرُ اللّٰهِ اَكْبَرُ ۗوَاللّٰهُ يَعْلَمُ مَا تَصْنَعُوْنَ","bc":"utlu mā ụḥiya ilaika minal-kitābi wa aqimiṣ-ṣalāh, innaṣ-ṣalāta tan-hā 'anil-faḥsyā`i wal-mungkar, walażikrullāhi akbar, wallāhu ya'lamu mā taṣna'ụn","at":"Bacalah Kitab (Al-Qur'an) yang telah diwahyukan kepadamu (Muhammad) dan laksanakanlah salat. Sesungguhnya salat itu mencegah dari (perbuatan) keji dan mungkar. Dan (ketahuilah) mengingat Allah (salat) itu lebih besar (keutamaannya dari ibadah yang lain). Allah mengetahui apa yang kamu kerjakan. "},{"id":"46","ar":"۞ وَلَا تُجَادِلُوْٓا اَهْلَ الْكِتٰبِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُۖ اِلَّا الَّذِيْنَ ظَلَمُوْا مِنْهُمْ وَقُوْلُوْٓا اٰمَنَّا بِالَّذِيْٓ اُنْزِلَ اِلَيْنَا وَاُنْزِلَ اِلَيْكُمْ وَاِلٰهُنَا وَاِلٰهُكُمْ وَاحِدٌ وَّنَحْنُ لَهٗ مُسْلِمُوْنَ","bc":"wa lā tujādilū ahlal-kitābi illā billatī hiya aḥsanu illallażīna ẓalamụ min-hum wa qụlū āmannā billażī unzila ilainā wa unzila ilaikum wa ilāhunā wa ilāhukum wāḥiduw wa naḥnu lahụ muslimụn","at":"Dan janganlah kamu berdebat dengan Ahli Kitab, melainkan dengan cara yang baik, kecuali dengan orang-orang yang zalim di antara mereka, dan katakanlah, ”Kami telah beriman kepada (kitab-kitab) yang diturunkan kepada kami dan yang diturunkan kepadamu; Tuhan kami dan Tuhan kamu satu; dan hanya kepada-Nya kami berserah diri.”"},{"id":"47","ar":"وَكَذٰلِكَ اَنْزَلْنَآ اِلَيْكَ الْكِتٰبَۗ فَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يُؤْمِنُوْنَ بِهٖۚ وَمِنْ هٰٓؤُلَاۤءِ مَنْ يُّؤْمِنُ بِهٖۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا الْكٰفِرُوْنَ","bc":"wa każālika anzalnā ilaikal-kitāb, fallażīna ātaināhumul-kitāba yu`minụna bih, wa min hā`ulā`i may yu`minu bih, wa mā yaj-ḥadu bi`āyātinā illal-kāfirụn","at":"Dan demikianlah Kami turunkan Kitab (Al-Qur'an) kepadamu. Adapun orang-orang yang telah Kami berikan Kitab (Taurat dan Injil) mereka beriman kepadanya (Al-Qur'an), dan di antara mereka (orang-orang kafir Mekah) ada yang beriman kepadanya. Dan hanya orang-orang kafir yang mengingkari ayat-ayat Kami. "},{"id":"48","ar":"وَمَا كُنْتَ تَتْلُوْا مِنْ قَبْلِهٖ مِنْ كِتٰبٍ وَّلَا تَخُطُّهٗ بِيَمِيْنِكَ اِذًا لَّارْتَابَ الْمُبْطِلُوْنَ","bc":"wa mā kunta tatlụ ming qablihī ming kitābiw wa lā takhuṭṭuhụ biyamīnika iżal lartābal-mubṭilụn","at":"Dan engkau (Muhammad) tidak pernah membaca sesuatu kitab sebelum (Al-Qur'an) dan engkau tidak (pernah) menulis suatu kitab dengan tangan kananmu; sekiranya (engkau pernah membaca dan menulis), niscaya ragu orang-orang yang mengingkarinya. "},{"id":"49","ar":"بَلْ هُوَ اٰيٰتٌۢ بَيِّنٰتٌ فِيْ صُدُوْرِ الَّذِيْنَ اُوْتُوا الْعِلْمَۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا الظّٰلِمُوْنَ","bc":"bal huwa āyātum bayyinātun fī ṣudụrillażīna ụtul-'ilm, wa mā yaj-ḥadu bi`āyātinā illaẓ-ẓālimụn","at":"Sebenarnya, (Al-Qur'an) itu adalah ayat-ayat yang jelas di dalam dada orang-orang yang berilmu. Hanya orang-orang yang zalim yang mengingkari ayat-ayat Kami. "},{"id":"50","ar":"وَقَالُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيٰتٌ مِّنْ رَّبِّهٖ ۗ قُلْ اِنَّمَا الْاٰيٰتُ عِنْدَ اللّٰهِ ۗوَاِنَّمَآ اَنَا۠ نَذِيْرٌ مُّبِيْنٌ","bc":"wa qālụ lau lā unzila 'alaihi āyātum mir rabbih, qul innamal-āyātu 'indallāh, wa innamā ana nażīrum mubīn","at":"Dan mereka (orang-orang kafir Mekah) berkata, ”Mengapa tidak diturunkan mukjizat-mukjizat dari Tuhannya?” Katakanlah (Muhammad), ”Mukjizat-mukjizat itu terserah kepada Allah. Aku hanya seorang pemberi peringatan yang jelas.” "},{"id":"51","ar":"اَوَلَمْ يَكْفِهِمْ اَنَّآ اَنْزَلْنَا عَلَيْكَ الْكِتٰبَ يُتْلٰى عَلَيْهِمْ ۗاِنَّ فِيْ ذٰلِكَ لَرَحْمَةً وَّذِكْرٰى لِقَوْمٍ يُّؤْمِنُوْنَ ","bc":"a wa lam yakfihim annā anzalnā 'alaikal-kitāba yutlā 'alaihim, inna fī żālika laraḥmataw wa żikrā liqaumiy yu`minụn","at":"Apakah tidak cukup bagi mereka bahwa Kami telah menurunkan kepadamu Kitab (Al-Qur'an) yang dibacakan kepada mereka? Sungguh, dalam (Al-Qur'an) itu terdapat rahmat yang besar dan pelajaran bagi orang-orang yang beriman. "},{"id":"52","ar":"قُلْ كَفٰى بِاللّٰهِ بَيْنِيْ وَبَيْنَكُمْ شَهِيْدًاۚ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَالَّذِيْنَ اٰمَنُوْا بِالْبَاطِلِ وَكَفَرُوْا بِاللّٰهِ اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ","bc":"qul kafā billāhi bainī wa bainakum syahīdā, ya'lamu mā fis-samāwāti wal-arḍ, wallażīna āmanụ bil-bāṭili wa kafarụ billāhi ulā`ika humul-khāsirụn","at":"Katakanlah (Muhammad), ”Cukuplah Allah menjadi saksi antara aku dan kamu. Dia mengetahui apa yang di langit dan di bumi. Dan orang yang percaya kepada yang batil dan ingkar kepada Allah, mereka itulah orang-orang yang rugi.” "},{"id":"53","ar":"وَيَسْتَعْجِلُوْنَكَ بِالْعَذَابِۗ وَلَوْلَآ اَجَلٌ مُّسَمًّى لَّجَاۤءَهُمُ الْعَذَابُۗ وَلَيَأْتِيَنَّهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ","bc":"wa yasta'jilụnaka bil-'ażāb, walau lā ajalum musammal lajā`ahumul-'ażāb, wa laya`tiyannahum bagtataw wa hum lā yasy'urụn","at":"Dan mereka meminta kepadamu agar segera diturunkan azab. Kalau bukan karena waktunya yang telah ditetapkan, niscaya datang azab kepada mereka, dan (azab itu) pasti akan datang kepada mereka dengan tiba-tiba, sedang mereka tidak menyadarinya. "},{"id":"54","ar":"يَسْتَعْجِلُوْنَكَ بِالْعَذَابِۗ وَاِنَّ جَهَنَّمَ لَمُحِيْطَةٌ ۢ بِالْكٰفِرِيْنَۙ","bc":"yasta'jilụnaka bil-'ażāb, wa inna jahannama lamuḥīṭatum bil-kāfirīn","at":"Mereka meminta kepadamu agar segera diturunkan azab. Dan sesungguhnya neraka Jahanam itu pasti meliputi orang-orang kafir, "},{"id":"55","ar":"يَوْمَ يَغْشٰىهُمُ الْعَذَابُ مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْ وَيَقُوْلُ ذُوْقُوْا مَا كُنْتُمْ تَعْمَلُوْنَ","bc":"yauma yagsyāhumul-'ażābu min fauqihim wa min taḥti arjulihim wa yaqụlu żụqụ mā kuntum ta'malụn","at":"pada hari (ketika) azab menutup mereka dari atas dan dari bawah kaki mereka dan (Allah) berkata (kepada mereka), ”Rasakanlah (balasan dari) apa yang telah kamu kerjakan!”"},{"id":"56","ar":"يٰعِبَادِيَ الَّذِيْنَ اٰمَنُوْٓا اِنَّ اَرْضِيْ وَاسِعَةٌ فَاِيَّايَ فَاعْبُدُوْنِ","bc":"yā 'ibādiyallażīna āmanū inna arḍī wāsi'atun fa iyyāya fa'budụn","at":"Wahai hamba-hamba-Ku yang beriman! Sungguh, bumi-Ku luas, maka sembahlah Aku (saja). "},{"id":"57","ar":"كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ ثُمَّ اِلَيْنَا تُرْجَعُوْنَ","bc":"kullu nafsin żā`iqatul-maụt, ṡumma ilainā turja'ụn","at":"Setiap yang bernyawa akan merasakan mati. Kemudian hanya kepada Kami kamu dikembalikan. "},{"id":"58","ar":"وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَنُبَوِّئَنَّهُمْ مِّنَ الْجَنَّةِ غُرَفًا تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ نِعْمَ اَجْرُ الْعٰمِلِيْنَۖ","bc":"wallażīna āmanụ wa 'amiluṣ-ṣāliḥāti lanubawwi`annahum minal-jannati gurafan tajrī min taḥtihal-an-hāru khālidīna fīhā, ni'ma ajrul-'āmilīn","at":"Dan orang-orang yang beriman dan mengerjakan kebajikan, sungguh, mereka akan Kami tempatkan pada tempat-tempat yang tinggi (di dalam surga), yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Itulah sebaik-baik balasan bagi orang yang berbuat kebajikan, "},{"id":"59","ar":"الَّذِيْنَ صَبَرُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ","bc":"allażīna ṣabarụ wa 'alā rabbihim yatawakkalụn","at":"(yaitu) orang-orang yang bersabar dan bertawakal kepada Tuhannya. "},{"id":"60","ar":"وَكَاَيِّنْ مِّنْ دَاۤبَّةٍ لَّا تَحْمِلُ رِزْقَهَاۖ اللّٰهُ يَرْزُقُهَا وَاِيَّاكُمْ وَهُوَ السَّمِيْعُ الْعَلِيْمُ","bc":"wa ka`ayyim min dābbatil lā taḥmilu rizqahallāhu yarzuquhā wa iyyākum wa huwas-samī'ul-'alīm","at":"Dan berapa banyak makhluk bergerak yang bernyawa yang tidak (dapat) membawa (mengurus) rezekinya sendiri. Allah-lah yang memberi rezeki kepadanya dan kepadamu. Dia Maha Mendengar, Maha Mengetahui. "},{"id":"61","ar":"وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ لَيَقُوْلُنَّ اللّٰهُ ۗفَاَنّٰى يُؤْفَكُوْنَ","bc":"wa la`in sa`altahum man khalaqas-samāwāti wal-arḍa wa sakhkharasy-syamsa wal-qamara layaqụlunnallāh, fa annā yu`fakụn","at":"Dan jika engkau bertanya kepada mereka, ”Siapakah yang menciptakan langit dan bumi dan menundukkan matahari dan bulan?” Pasti mereka akan menjawab, ”Allah.” Maka mengapa mereka bisa dipalingkan (dari kebenaran). "},{"id":"62","ar":"اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُ لَهٗ ۗاِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ","bc":"allāhu yabsuṭur-rizqa limay yasyā`u min 'ibādihī wa yaqdiru lah, innallāha bikulli syai`in 'alīm","at":"Allah melapangkan rezeki bagi orang yang Dia kehendaki di antara hamba-hamba-Nya dan Dia (pula) yang membatasi baginya. Sungguh, Allah Maha Mengetahui segala sesuatu. "},{"id":"63","ar":"وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ نَّزَّلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَحْيَا بِهِ الْاَرْضَ مِنْۢ بَعْدِ مَوْتِهَا لَيَقُوْلُنَّ اللّٰهُ ۙقُلِ الْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْقِلُوْنَ","bc":"wa la`in sa`altahum man nazzala minas-samā`i mā`an fa aḥyā bihil-arḍa mim ba'di mautihā layaqụlunnallāhu qulil-ḥamdu lillāh, bal akṡaruhum lā ya'qilụn","at":"Dan jika kamu bertanya kepada mereka, ”Siapakah yang menurunkan air dari langit lalu dengan (air) itu dihidupkannya bumi yang sudah mati?” Pasti mereka akan menjawab, ”Allah.” Katakanlah, ”Segala puji bagi Allah,” tetapi kebanyakan mereka tidak mengerti. "},{"id":"64","ar":"وَمَا هٰذِهِ الْحَيٰوةُ الدُّنْيَآ اِلَّا لَهْوٌ وَّلَعِبٌۗ وَاِنَّ الدَّارَ الْاٰخِرَةَ لَهِيَ الْحَيَوَانُۘ لَوْ كَانُوْا يَعْلَمُوْنَ","bc":"wa mā hāżihil-ḥayātud-dun-yā illā lahwuw wa la'ib, wa innad-dāral-ākhirata lahiyal-ḥayawān, lau kānụ ya'lamụn","at":"Dan kehidupan dunia ini hanya senda gurau dan permainan. Dan sesungguhnya negeri akhirat itulah kehidupan yang sebenarnya, sekiranya mereka mengetahui. "},{"id":"65","ar":"فَاِذَا رَكِبُوْا فِى الْفُلْكِ دَعَوُا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۚ فَلَمَّا نَجّٰىهُمْ اِلَى الْبَرِّ اِذَا هُمْ يُشْرِكُوْنَۙ ","bc":"fa iżā rakibụ fil-fulki da'awullāha mukhliṣīna lahud-dīn, fa lammā najjāhum ilal-barri iżā hum yusyrikụn","at":"Maka apabila mereka naik kapal, mereka berdoa kepada Allah dengan penuh rasa pengabdian (ikhlas) kepada-Nya, tetapi ketika Allah menyelamatkan mereka sampai ke darat, malah mereka (kembali) mempersekutukan (Allah), "},{"id":"66","ar":"لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۙ وَلِيَتَمَتَّعُوْاۗ فَسَوْفَ يَعْلَمُوْنَ","bc":"liyakfurụ bimā ātaināhum wa liyatamatta'ụ, fa saufa ya'lamụn","at":"biarlah mereka mengingkari nikmat yang telah Kami berikan kepada mereka dan silakan mereka (hidup) bersenang-senang (dalam kekafiran). Maka kelak mereka akan mengetahui (akibat perbuatannya). "},{"id":"67","ar":"اَوَلَمْ يَرَوْا اَنَّا جَعَلْنَا حَرَمًا اٰمِنًا وَّيُتَخَطَّفُ النَّاسُ مِنْ حَوْلِهِمْۗ اَفَبِالْبَاطِلِ يُؤْمِنُوْنَ وَبِنِعْمَةِ اللّٰهِ يَكْفُرُوْنَ","bc":"a wa lam yarau annā ja'alnā ḥaraman āminaw wa yutakhaṭṭafun-nāsu min ḥaulihim, a fa bil-bāṭili yu`minụna wa bini'matillāhi yakfurụn","at":"Tidakkah mereka memperhatikan, bahwa Kami telah menjadikan (negeri mereka) tanah suci yang aman, padahal manusia di sekitarnya saling merampok. Mengapa (setelah nyata kebenaran) mereka masih percaya kepada yang batil dan ingkar kepada nikmat Allah? "},{"id":"68","ar":"وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِالْحَقِّ لَمَّا جَاۤءَهٗ ۗ اَلَيْسَ فِيْ جَهَنَّمَ مَثْوًى لِّلْكٰفِرِيْنَ","bc":"wa man aẓlamu mim maniftarā 'alallāhi każiban au każżaba bil-ḥaqqi lammā jā`ah, a laisa fī jahannama maṡwal lil-kāfirīn","at":"Dan siapakah yang lebih zalim daripada orang yang mengada-adakan kebohongan kepada Allah atau orang yang mendustakan yang hak ketika (yang hak) itu datang kepadanya? Bukankah dalam neraka Jahanam ada tempat bagi orang-orang kafir? "},{"id":"69","ar":"وَالَّذِيْنَ جَاهَدُوْا فِيْنَا لَنَهْدِيَنَّهُمْ سُبُلَنَاۗ وَاِنَّ اللّٰهَ لَمَعَ الْمُحْسِنِيْنَ ","bc":"wallażīna jāhadụ fīnā lanahdiyannahum subulanā, wa innallāha lama'al-muḥsinīn","at":"Dan orang-orang yang berjihad untuk (mencari keridaan) Kami, Kami akan tunjukkan kepada mereka jalan-jalan Kami. Dan sungguh, Allah beserta orang-orang yang berbuat baik."}],al_araf=[{"id":"1","ar":"الۤمّۤصۤ ۚ ","bc":"alif lām mīm shād","at":"Alif Lam Mim shad."},{"id":"2","ar":"كِتٰبٌ اُنْزِلَ اِلَيْكَ فَلَا يَكُنْ فِيْ صَدْرِكَ حَرَجٌ مِّنْهُ لِتُنْذِرَ بِهٖ وَذِكْرٰى لِلْمُؤْمِنِيْنَ ","bc":"kitābun unzila ilaika fa lā yakun fī ṣadrika ḥarajum min-hu litunżira bihī wa żikrā lil-mu`minīn","at":"(Inilah) Kitab yang diturunkan kepadamu (Muhammad); maka janganlah engkau sesak dada karenanya, agar engkau memberi peringatan dengan (Kitab) itu dan menjadi pelajaran bagi orang yang beriman."},{"id":"3","ar":"اِتَّبِعُوْا مَآ اُنْزِلَ اِلَيْكُمْ مِّنْ رَّبِّكُمْ وَلَا تَتَّبِعُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَۗ قَلِيْلًا مَّا تَذَكَّرُوْنَ ","bc":"ittabi'ụ mā unzila ilaikum mir rabbikum wa lā tattabi'ụ min dụnihī auliyā`, qalīlam mā tażakkarụn","at":"Ikutilah apa yang diturunkan kepadamu dari Tuhanmu, dan janganlah kamu ikuti selain Dia sebagai pemimpin. Sedikit sekali kamu mengambil pelajaran."},{"id":"4","ar":"وَكَمْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَا فَجَاۤءَهَا بَأْسُنَا بَيَاتًا اَوْ هُمْ قَاۤىِٕلُوْنَ","bc":"wa kam ming qaryatin ahlaknāhā fa jā`ahā ba`sunā bayātan au hum qā`ilụn","at":"Betapa banyak negeri yang telah Kami binasakan, siksaan Kami datang (menimpa penduduk)nya pada malam hari, atau pada saat mereka beristirahat pada siang hari."},{"id":"5","ar":"فَمَا كَانَ دَعْوٰىهُمْ اِذْ جَاۤءَهُمْ بَأْسُنَآ اِلَّآ اَنْ قَالُوْٓا اِنَّا كُنَّا ظٰلِمِيْنَ ","bc":"fa mā kāna da'wāhum iż jā`ahum ba`sunā illā ang qālū innā kunnā ẓālimīn","at":"Maka ketika siksaan Kami datang menimpa mereka, keluhan mereka tidak lain, hanya mengucap, “Sesungguhnya kami adalah orang-orang yang zalim.”"},{"id":"6","ar":"فَلَنَسْـَٔلَنَّ الَّذِيْنَ اُرْسِلَ اِلَيْهِمْ وَلَنَسْـَٔلَنَّ الْمُرْسَلِيْنَۙ","bc":"fa lanas`alannallażīna ursila ilaihim wa lanas`alannal-mursalīn","at":"Maka pasti akan Kami tanyakan kepada umat yang telah mendapat seruan (dari rasul-rasul) dan Kami akan tanyai (pula) para rasul,"},{"id":"7","ar":"فَلَنَقُصَّنَّ عَلَيْهِمْ بِعِلْمٍ وَّمَا كُنَّا غَاۤىِٕبِيْنَ ","bc":"fa lanaquṣṣanna 'alaihim bi'ilmiw wa mā kunnā gā`ibīn","at":"dan pasti akan Kami beritakan kepada mereka dengan ilmu (Kami) dan Kami tidak jauh (dari mereka)."},{"id":"8","ar":"وَالْوَزْنُ يَوْمَىِٕذِ ِۨالْحَقُّۚ فَمَنْ ثَقُلَتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ","bc":"wal-waznu yauma`iżinil-ḥaqq, fa man ṡaqulat mawāzīnuhụ fa ulā`ika humul-mufliḥụn","at":"Timbangan pada hari itu (menjadi ukuran) kebenaran. Maka barangsiapa berat timbangan (kebaikan)nya, mereka itulah orang yang beruntung,"},{"id":"9","ar":"وَمَنْ خَفَّتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ بِمَا كَانُوْا بِاٰيٰتِنَا يَظْلِمُوْنَ ","bc":"wa man khaffat mawāzīnuhụ fa ulā`ikallażīna khasirū anfusahum bimā kānụ bi`āyātinā yaẓlimụn","at":"dan barangsiapa ringan timbangan (kebaikan)nya, maka mereka itulah orang yang telah merugikan dirinya sendiri, karena mereka mengingkari ayat-ayat Kami."},{"id":"10","ar":"وَلَقَدْ مَكَّنّٰكُمْ فِى الْاَرْضِ وَجَعَلْنَا لَكُمْ فِيْهَا مَعَايِشَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ ","bc":"wa laqad makkannākum fil-arḍi wa ja'alnā lakum fīhā ma'āyisy, qalīlam mā tasykurụn","at":"Dan sungguh, Kami telah menempatkan kamu di bumi dan di sana Kami sediakan (sumber) penghidupan untukmu. (Tetapi) sedikit sekali kamu bersyukur."},{"id":"11","ar":"وَلَقَدْ خَلَقْنٰكُمْ ثُمَّ صَوَّرْنٰكُمْ ثُمَّ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ لَمْ يَكُنْ مِّنَ السّٰجِدِيْنَ","bc":"wa laqad khalaqnākum ṡumma ṣawwarnākum ṡumma qulnā lil-malā`ikatisjudụ li`ādama fa sajadū illā iblīs, lam yakum minas-sājidīn","at":"Dan sungguh, Kami telah menciptakan kamu, kemudian membentuk (tubuh)mu, kemudian Kami berfirman kepada para malaikat, “Bersujudlah kamu kepada Adam,” maka mereka pun sujud kecuali Iblis. Ia (Iblis) tidak termasuk mereka yang bersujud."},{"id":"12","ar":"قَالَ مَا مَنَعَكَ اَلَّا تَسْجُدَ اِذْ اَمَرْتُكَ ۗقَالَ اَنَا۠ خَيْرٌ مِّنْهُۚ خَلَقْتَنِيْ مِنْ نَّارٍ وَّخَلَقْتَهٗ مِنْ طِيْنٍ ","bc":"qāla mā mana'aka allā tasjuda iż amartuk, qāla ana khairum min-h, khalaqtanī min nāriw wa khalaqtahụ min ṭīn","at":"(Allah) berfirman, “Apakah yang menghalangimu (sehingga) kamu tidak bersujud (kepada Adam) ketika Aku menyuruhmu?” (Iblis) menjawab, “Aku lebih baik daripada dia. Engkau ciptakan aku dari api, sedangkan dia Engkau ciptakan dari tanah.”"},{"id":"13","ar":"قَالَ فَاهْبِطْ مِنْهَا فَمَا يَكُوْنُ لَكَ اَنْ تَتَكَبَّرَ فِيْهَا فَاخْرُجْ اِنَّكَ مِنَ الصّٰغِرِيْنَ ","bc":"qāla fahbiṭ min-hā fa mā yakụnu laka an tatakabbara fīhā fakhruj innaka minaṣ-ṣāgirīn","at":"(Allah) berfirman, “Maka turunlah kamu darinya (surga); karena kamu tidak sepatutnya menyombongkan diri di dalamnya. Keluarlah! Sesungguhnya kamu termasuk makhluk yang hina.”"},{"id":"14","ar":"قَالَ اَنْظِرْنِيْٓ اِلٰى يَوْمِ يُبْعَثُوْنَ ","bc":"qāla anẓirnī ilā yaumi yub'aṡụn","at":"(Iblis) menjawab, “Berilah aku penangguhan waktu, sampai hari mereka dibangkitkan.”"},{"id":"15","ar":"قَالَ اِنَّكَ مِنَ الْمُنْظَرِيْنَ ","bc":"qāla innaka minal-munẓarīn","at":"(Allah) berfirman, “Benar, kamu termasuk yang diberi penangguhan waktu.”"},{"id":"16","ar":"قَالَ فَبِمَآ اَغْوَيْتَنِيْ لَاَقْعُدَنَّ لَهُمْ صِرَاطَكَ الْمُسْتَقِيْمَۙ","bc":"qāla fa bimā agwaitanī la`aq'udanna lahum ṣirāṭakal-mustaqīm","at":"(Iblis) menjawab, “Karena Engkau telah menyesatkan aku, pasti aku akan selalu menghalangi mereka dari jalan-Mu yang lurus,"},{"id":"17","ar":"ثُمَّ لَاٰتِيَنَّهُمْ مِّنْۢ بَيْنِ اَيْدِيْهِمْ وَمِنْ خَلْفِهِمْ وَعَنْ اَيْمَانِهِمْ وَعَنْ شَمَاۤىِٕلِهِمْۗ وَلَا تَجِدُ اَكْثَرَهُمْ شٰكِرِيْنَ","bc":"ṡumma la`ātiyannahum mim baini aidīhim wa min khalfihim wa 'an aimānihim wa 'an syamā`ilihim, wa lā tajidu akṡarahum syākirīn","at":"kemudian pasti aku akan mendatangi mereka dari depan, dari belakang, dari kanan dan dari kiri mereka. Dan Engkau tidak akan mendapati kebanyakan mereka bersyukur.”"},{"id":"18","ar":"قَالَ اخْرُجْ مِنْهَا مَذْءُوْمًا مَّدْحُوْرًا ۗ لَمَنْ تَبِعَكَ مِنْهُمْ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنْكُمْ اَجْمَعِيْنَ","bc":"qālakhruj min-hā maż`ụmam mad-ḥụrā, laman tabi'aka min-hum la`amla`anna jahannama mingkum ajma'īn","at":"(Allah) berfirman, “Keluarlah kamu dari sana (surga) dalam keadaan terhina dan terusir! Sesungguhnya barangsiapa di antara mereka ada yang mengikutimu, pasti akan Aku isi neraka Jahanam dengan kamu semua.”"},{"id":"19","ar":"وَيٰٓاٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ فَكُلَا مِنْ حَيْثُ شِئْتُمَا وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُوْنَا مِنَ الظّٰلِمِيْنَ ","bc":"wa yā ādamuskun anta wa zaujukal-jannata fa kulā min ḥaiṡu syi`tumā wa lā taqrabā hāżihisy-syajarata fa takụnā minaẓ-ẓālimīn","at":"Dan (Allah berfirman), “Wahai Adam! Tinggallah engkau bersama istrimu dalam surga dan makanlah apa saja yang kamu berdua sukai. Tetapi janganlah kamu berdua dekati pohon yang satu ini. (Apabila didekati) kamu berdua termasuk orang-orang yang zalim.”"},{"id":"20","ar":"فَوَسْوَسَ لَهُمَا الشَّيْطٰنُ لِيُبْدِيَ لَهُمَا مَا وٗرِيَ عَنْهُمَا مِنْ سَوْاٰتِهِمَا وَقَالَ مَا نَهٰىكُمَا رَبُّكُمَا عَنْ هٰذِهِ الشَّجَرَةِ ِالَّآ اَنْ تَكُوْنَا مَلَكَيْنِ اَوْ تَكُوْنَا مِنَ الْخٰلِدِيْنَ ","bc":"fa waswasa lahumasy-syaiṭānu liyubdiya lahumā mā wụriya 'an-humā min sau`ātihimā wa qāla mā nahākumā rabbukumā 'an hāżihisy-syajarati illā an takụnā malakaini au takụnā minal-khālidīn","at":"Kemudian setan membisikkan pikiran jahat kepada mereka agar menampakkan aurat mereka (yang selama ini) tertutup. Dan (setan) berkata, “Tuhanmu hanya melarang kamu berdua mendekati pohon ini, agar kamu berdua tidak menjadi malaikat atau tidak menjadi orang yang kekal (dalam surga).”"},{"id":"21","ar":"وَقَاسَمَهُمَآ اِنِّيْ لَكُمَا لَمِنَ النّٰصِحِيْنَۙ","bc":"wa qāsamahumā innī lakumā laminan-nāṣiḥīn","at":"Dan dia (setan) bersumpah kepada keduanya, “Sesungguhnya aku ini benar-benar termasuk para penasihatmu,”"},{"id":"22","ar":"فَدَلّٰىهُمَا بِغُرُورٍۚ فَلَمَّا ذَاقَا الشَّجَرَةَ بَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفٰنِ عَلَيْهِمَا مِنْ وَّرَقِ الْجَنَّةِۗ وَنَادٰىهُمَا رَبُّهُمَآ اَلَمْ اَنْهَكُمَا عَنْ تِلْكُمَا الشَّجَرَةِ وَاَقُلْ لَّكُمَآ اِنَّ الشَّيْطٰنَ لَكُمَا عَدُوٌّ مُّبِيْنٌ ","bc":"fa dallāhumā bigurụr, fa lammā żāqasy-syajarata badat lahumā sau`ātuhumā wa ṭafiqā yakhṣifāni 'alaihimā miw waraqil-jannah, wa nādāhumā rabbuhumā a lam an-hakumā 'an tilkumasy-syajarati wa aqul lakumā innasy-syaiṭāna lakumā 'aduwwum mubīn","at":"dia (setan) membujuk mereka dengan tipu daya. Ketika mereka mencicipi (buah) pohon itu, tampaklah oleh mereka auratnya, maka mulailah mereka menutupinya dengan daun-daun surga. Tuhan menyeru mereka, “Bukankah Aku telah melarang kamu dari pohon itu dan Aku telah mengatakan bahwa sesungguhnya setan adalah musuh yang nyata bagi kamu berdua?”"},{"id":"23","ar":"قَالَا رَبَّنَا ظَلَمْنَآ اَنْفُسَنَا وَاِنْ لَّمْ تَغْفِرْ لَنَا وَتَرْحَمْنَا لَنَكُوْنَنَّ مِنَ الْخٰسِرِيْنَ ","bc":"qālā rabbanā ẓalamnā anfusana wa il lam tagfir lanā wa tar-ḥamnā lanakụnanna minal-khāsirīn","at":"Keduanya berkata, “Ya Tuhan kami, kami telah menzalimi diri kami sendiri. Jika Engkau tidak mengampuni kami dan memberi rahmat kepada kami, niscaya kami termasuk orang-orang yang rugi.”"},{"id":"24","ar":"قَالَ اهْبِطُوْا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚوَلَكُمْ فِى الْاَرْضِ مُسْتَقَرٌّ وَّمَتَاعٌ اِلٰى حِيْنٍ ","bc":"qālahbiṭụ ba'ḍukum liba'ḍin 'aduww, wa lakum fil-arḍi mustaqarruw wa matā'un ilā ḥīn","at":"(Allah) berfirman, “Turunlah kamu! Kamu akan saling bermusuhan satu sama lain. Bumi adalah tempat kediaman dan kesenanganmu sampai waktu yang telah ditentukan.”"},{"id":"25","ar":"قَالَ فِيْهَا تَحْيَوْنَ وَفِيْهَا تَمُوْتُوْنَ وَمِنْهَا تُخْرَجُوْنَ ","bc":"qāla fīhā taḥyauna wa fīhā tamụtụna wa min-hā tukhrajụn","at":"(Allah) berfirman, “Di sana kamu hidup, di sana kamu mati, dan dari sana (pula) kamu akan dibangkitkan.”"},{"id":"26","ar":"يَا بَنِيْٓ اٰدَمَ قَدْ اَنْزَلْنَا عَلَيْكُمْ لِبَاسًا يُّوَارِيْ سَوْاٰتِكُمْ وَرِيْشًاۗ وَلِبَاسُ التَّقْوٰى ذٰلِكَ خَيْرٌۗ ذٰلِكَ مِنْ اٰيٰتِ اللّٰهِ لَعَلَّهُمْ يَذَّكَّرُوْنَ ","bc":"yā banī ādama qad anzalnā 'alaikum libāsay yuwārī sau`ātikum warīsyā, wa libāsut-taqwā żālika khaīr, żālika min āyātillāhi la'allahum yażżakkarụn","at":"Wahai anak cucu Adam! Sesungguhnya Kami telah menyediakan pakaian untuk menutupi auratmu dan untuk perhiasan bagimu. Tetapi pakaian takwa, itulah yang lebih baik. Demikianlah sebagian tanda-tanda kekuasaan Allah, mudah-mudahan mereka ingat."},{"id":"27","ar":"يٰبَنِيْٓ اٰدَمَ لَا يَفْتِنَنَّكُمُ الشَّيْطٰنُ كَمَآ اَخْرَجَ اَبَوَيْكُمْ مِّنَ الْجَنَّةِ يَنْزِعُ عَنْهُمَا لِبَاسَهُمَا لِيُرِيَهُمَا سَوْاٰتِهِمَا ۗاِنَّهٗ يَرٰىكُمْ هُوَ وَقَبِيْلُهٗ مِنْ حَيْثُ لَا تَرَوْنَهُمْۗ اِنَّا جَعَلْنَا الشَّيٰطِيْنَ اَوْلِيَاۤءَ لِلَّذِيْنَ لَا يُؤْمِنُوْنَ ","bc":"yā banī ādama lā yaftinannakumusy-syaiṭānu kamā akhraja abawaikum minal-jannati yanzi'u 'an-humā libāsahumā liyuriyahumā sau`ātihimā, innahụ yarākum huwa wa qabīluhụ min ḥaiṡu lā taraunahum, innā ja'alnasy-syayāṭīna auliyā`a lillażīna lā yu`minụn","at":"Wahai anak cucu Adam! Janganlah sampai kamu tertipu oleh setan sebagaimana halnya dia (setan) telah mengeluarkan ibu bapakmu dari surga, dengan menanggalkan pakaian keduanya untuk memperlihatkan aurat keduanya. Sesungguhnya dia dan pengikutnya dapat melihat kamu dari suatu tempat yang kamu tidak bisa melihat mereka. Sesungguhnya Kami telah menjadikan setan-setan itu pemimpin bagi orang-orang yang tidak beriman."},{"id":"28","ar":"وَاِذَا فَعَلُوْا فَاحِشَةً قَالُوْا وَجَدْنَا عَلَيْهَآ اٰبَاۤءَنَا وَاللّٰهُ اَمَرَنَا بِهَاۗ قُلْ اِنَّ اللّٰهَ لَا يَأْمُرُ بِالْفَحْشَاۤءِۗ اَتَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ","bc":"wa iżā fa'alụ fāḥisyatang qālụ wajadnā 'alaihā ābā`anā wallāhu amaranā bihā, qul innallāha lā ya`muru bil-faḥsyā`, a taqụlụna 'alallāhi mā lā ta'lamụn","at":"Dan apabila mereka melakukan perbuatan keji, mereka berkata, “Kami mendapati nenek moyang kami melakukan yang demikian, dan Allah menyuruh kami mengerjakannya.” Katakanlah, “Sesungguhnya Allah tidak pernah menyuruh berbuat keji. Mengapa kamu membicarakan tentang Allah apa yang tidak kamu ketahui?”"},{"id":"29","ar":"قُلْ اَمَرَ رَبِّيْ بِالْقِسْطِۗ وَاَقِيْمُوْا وُجُوْهَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَّادْعُوْهُ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۗ كَمَا بَدَاَكُمْ تَعُوْدُوْنَۗ","bc":"qul amara rabbī bil-qisṭ, wa aqīmụ wujụhakum 'inda kulli masjidiw wad'ụhu mukhliṣīna lahud-dīn, kamā bada`akum ta'ụdụn","at":"Katakanlah, “Tuhanku menyuruhku berlaku adil. Hadapkanlah wajahmu (kepada Allah) pada setiap salat, dan sembahlah Dia dengan mengikhlaskan ibadah semata-mata hanya kepada-Nya. Kamu akan dikembalikan kepada-Nya sebagaimana kamu diciptakan semula."},{"id":"30","ar":"فَرِيْقًا هَدٰى وَفَرِيْقًا حَقَّ عَلَيْهِمُ الضَّلٰلَةُ ۗاِنَّهُمُ اتَّخَذُوا الشَّيٰطِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ اللّٰهِ وَيَحْسَبُوْنَ اَنَّهُمْ مُّهْتَدُوْنَ ","bc":"farīqan hadā wa farīqan ḥaqqa 'alaihimuḍ-ḍalālah, innahumuttakhażusy-syayāṭīna auliyā`a min dụnillāhi wa yaḥsabụna annahum muhtadụn","at":"Sebagian diberi-Nya petunjuk dan sebagian lagi sepantasnya menjadi sesat. Mereka menjadikan setan-setan sebagai pelindung selain Allah. Mereka mengira bahwa mereka mendapat petunjuk."},{"id":"31","ar":"۞ يٰبَنِيْٓ اٰدَمَ خُذُوْا زِيْنَتَكُمْ عِنْدَ كُلِّ مَسْجِدٍ وَّكُلُوْا وَاشْرَبُوْا وَلَا تُسْرِفُوْاۚ اِنَّهٗ لَا يُحِبُّ الْمُسْرِفِيْنَ ","bc":"yā banī ādama khużụ zīnatakum 'inda kulli masjidiw wa kulụ wasyrabụ wa lā tusrifụ, innahụ lā yuḥibbul-musrifīn","at":"Wahai anak cucu Adam! Pakailah pakaianmu yang bagus pada setiap (memasuki) masjid, makan dan minumlah, tetapi jangan berlebihan. Sungguh, Allah tidak menyukai orang yang berlebih-lebihan."},{"id":"32","ar":"قُلْ مَنْ حَرَّمَ زِيْنَةَ اللّٰهِ الَّتِيْٓ اَخْرَجَ لِعِبَادِهٖ وَالطَّيِّبٰتِ مِنَ الرِّزْقِۗ قُلْ هِيَ لِلَّذِيْنَ اٰمَنُوْا فِى الْحَيٰوةِ الدُّنْيَا خَالِصَةً يَّوْمَ الْقِيٰمَةِۗ كَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ","bc":"qul man ḥarrama zīnatallāhillatī akhraja li'ibādihī waṭ-ṭayyibāti minar-rizq, qul hiya lillażīna āmanụ fil-ḥayātid-dun-yā khāliṣatay yaumal-qiyāmah, każālika nufaṣṣilul-āyāti liqaumiy ya'lamụn","at":"Katakanlah (Muhammad), “Siapakah yang mengharamkan perhiasan dari Allah yang telah disediakan untuk hamba-hamba-Nya dan rezeki yang baik-baik? Katakanlah, “Semua itu untuk orang-orang yang beriman dalam kehidupan dunia, dan khusus (untuk mereka saja) pada hari Kiamat.” Demikianlah Kami menjelaskan ayat-ayat itu untuk orang-orang yang mengetahui."},{"id":"33","ar":"قُلْ اِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ وَالْاِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ وَاَنْ تُشْرِكُوْا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا وَّاَنْ تَقُوْلُوْا عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ","bc":"qul innamā ḥarrama rabbiyal-fawāḥisya mā ẓahara min-hā wa mā baṭana wal-iṡma wal-bagya bigairil-ḥaqqi wa an tusyrikụ billāhi mā lam yunazzil bihī sulṭānaw wa an taqụlụ 'alallāhi mā lā ta'lamụn","at":"Katakanlah (Muhammad), “Tuhanku hanya mengharamkan segala perbuatan keji yang terlihat dan yang tersembunyi, perbuatan dosa, perbuatan zalim tanpa alasan yang benar, dan (mengharamkan) kamu mempersekutukan Allah dengan sesuatu, sedangkan Dia tidak menurunkan alasan untuk itu, dan (mengharamkan) kamu membicarakan tentang Allah apa yang tidak kamu ketahui.”"},{"id":"34","ar":"وَلِكُلِّ اُمَّةٍ اَجَلٌۚ فَاِذَا جَاۤءَ اَجَلُهُمْ لَا يَسْتَأْخِرُوْنَ سَاعَةً وَّلَا يَسْتَقْدِمُوْنَ ","bc":"wa likulli ummatin ajal, fa iżā jā`a ajaluhum lā yasta`khirụna sā'ataw wa lā yastaqdimụn","at":"Dan setiap umat mempunyai ajal (batas waktu). Apabila ajalnya tiba, mereka tidak dapat meminta penundaan atau percepatan sesaat pun."},{"id":"35","ar":"يٰبَنِيْٓ اٰدَمَ اِمَّا يَأْتِيَنَّكُمْ رُسُلٌ مِّنْكُمْ يَقُصُّوْنَ عَلَيْكُمْ اٰيٰتِيْۙ فَمَنِ اتَّقٰى وَاَصْلَحَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ ","bc":"yā banī ādama immā ya`tiyannakum rusulum mingkum yaquṣṣụna 'alaikum āyātī fa manittaqā wa aṣlaḥa fa lā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Wahai anak cucu Adam! Jika datang kepadamu rasul-rasul dari kalanganmu sendiri, yang menceritakan ayat-ayat-Ku kepadamu, maka barangsiapa bertakwa dan mengadakan perbaikan, maka tidak ada rasa takut pada mereka, dan mereka tidak bersedih hati."},{"id":"36","ar":"وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاسْتَكْبَرُوْا عَنْهَآ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ ","bc":"wallażīna każżabụ bi`āyātinā wastakbarụ 'an-hā ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn","at":"Tetapi orang-orang yang mendustakan ayat-ayat Kami dan menyombongkan diri terhadapnya, mereka itulah penghuni neraka; mereka kekal di dalamnya."},{"id":"37","ar":"فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اُولٰۤىِٕكَ يَنَالُهُمْ نَصِيْبُهُمْ مِّنَ الْكِتٰبِۗ حَتّٰٓى اِذَا جَاۤءَتْهُمْ رُسُلُنَا يَتَوَفَّوْنَهُمْۙ قَالُوْٓا اَيْنَ مَا كُنْتُمْ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗقَالُوْا ضَلُّوْا عَنَّا وَشَهِدُوْا عَلٰٓى اَنْفُسِهِمْ اَنَّهُمْ كَانُوْا كٰفِرِيْنَ ","bc":"fa man aẓlamu mim maniftarā 'alallāhi każiban au każżaba bi`āyātih, ulā`ika yanaluhum naṣībuhum minal-kitāb, ḥattā iżā jā`at-hum rusulunā yatawaffaunahum qālū aina mā kuntum tad'ụna min dụnillāh, qālụ ḍallụ 'annā wa syahidụ 'alā anfusihim annahum kānụ kāfirīn","at":"Siapakah yang lebih zalim daripada orang yang mengada-adakan kebohongan terhadap Allah atau yang mendustakan ayat-ayat-Nya? Mereka itu akan memperoleh bagian yang telah ditentukan dalam Kitab sampai datang para utusan (malaikat) Kami kepada mereka untuk mencabut nyawanya. Mereka (para malaikat) berkata, “Manakah sembahan yang biasa kamu sembah selain Allah?” Mereka (orang musyrik) menjawab, “Semuanya telah lenyap dari kami.” Dan mereka memberikan kesaksian terhadap diri mereka sendiri bahwa mereka adalah orang-orang kafir."},{"id":"38","ar":"قَالَ ادْخُلُوْا فِيْٓ اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِكُمْ مِّنَ الْجِنِّ وَالْاِنْسِ فِى النَّارِۙ كُلَّمَا دَخَلَتْ اُمَّةٌ لَّعَنَتْ اُخْتَهَا ۗحَتّٰٓى اِذَا ادَّارَكُوْا فِيْهَا جَمِيْعًا ۙقَالَتْ اُخْرٰىهُمْ لِاُوْلٰىهُمْ رَبَّنَا هٰٓؤُلَاۤءِ اَضَلُّوْنَا فَاٰتِهِمْ عَذَابًا ضِعْفًا مِّنَ النَّارِ ەۗ قَالَ لِكُلٍّ ضِعْفٌ وَّلٰكِنْ لَّا تَعْلَمُوْنَ","bc":"qāladkhulụ fī umaming qad khalat ming qablikum minal-jinni wal-insi fin-nāri kullamā dakhalat ummatul la'anat ukhtahā, ḥattā iżad dārakụ fīhā jamī'ang qālat ukhrāhum li`ụlāhum rabbanā hā`ulā`i aḍallụnā fa ātihim 'ażāban ḍi'fam minan-nār, qāla likullin ḍi'fuw wa lākil lā ta'lamụn","at":"Allah berfirman, “Masuklah kamu ke dalam api neraka bersama golongan jin dan manusia yang telah lebih dahulu dari kamu. Setiap kali suatu umat masuk, dia melaknat saudaranya, sehingga apabila mereka telah masuk semuanya, berkatalah orang yang (masuk) belakangan (kepada) orang yang (masuk) terlebih dahulu, “Ya Tuhan kami, mereka telah menyesatkan kami. Datangkanlah siksaan api neraka yang berlipat ganda kepada mereka” Allah berfirman, “Masing-masing mendapatkan (siksaan) yang berlipat ganda, tapi kamu tidak mengetahui.”"},{"id":"39","ar":"وَقَالَتْ اُوْلٰىهُمْ لِاُخْرٰىهُمْ فَمَا كَانَ لَكُمْ عَلَيْنَا مِنْ فَضْلٍ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْسِبُوْنَ","bc":"wa qālat ụlāhum li`ukhrāhum fa mā kāna lakum 'alainā min faḍlin fa żụqul-'ażāba bimā kuntum taksibụn","at":"Dan orang yang (masuk) terlebih dahulu berkata kepada yang (masuk) belakangan, “Kamu tidak mempunyai kelebihan sedikit pun atas kami. Maka rasakanlah azab itu karena perbuatan yang telah kamu lakukan.”"},{"id":"40","ar":"اِنَّ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاسْتَكْبَرُوْا عَنْهَا لَا تُفَتَّحُ لَهُمْ اَبْوَابُ السَّمَاۤءِ وَلَا يَدْخُلُوْنَ الْجَنَّةَ حَتّٰى يَلِجَ الْجَمَلُ فِيْ سَمِّ الْخِيَاطِ ۗ وَكَذٰلِكَ نَجْزِى الْمُجْرِمِيْنَ ","bc":"innallażīna każżabụ bi`āyātinā wastakbarụ 'an-hā lā tufattaḥu lahum abwābus-samā`i wa lā yadkhulụnal-jannata ḥattā yalijal-jamalu fī sammil-khiyāṭ, wa każālika najzil-mujrimīn","at":"Sesungguhnya orang-orang yang mendustakan ayat-ayat Kami dan menyombongkan diri terhadapnya, tidak akan dibukakan pintu-pintu langit bagi mereka, dan mereka tidak akan masuk surga, sebelum unta masuk ke dalam lubang jarum. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat jahat."},{"id":"41","ar":"لَهُمْ مِّنْ جَهَنَّمَ مِهَادٌ وَّمِنْ فَوْقِهِمْ غَوَاشٍۗ وَكَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ ","bc":"lahum min jahannama mihāduw wa min fauqihim gawāsy, wa każālika najziẓ-ẓālimīn","at":"Bagi mereka tikar tidur dari api neraka dan di atas mereka ada selimut (api neraka). Demikianlah Kami memberi balasan kepada orang-orang yang zalim."},{"id":"42","ar":"وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِۚ هُمْ فِيْهَا خٰلِدُوْنَ ","bc":"wallażīna āmanụ wa 'amiluṣ-ṣāliḥāti lā nukallifu nafsan illā wus'ahā ulā`ika aṣ-ḥābul-jannah, hum fīhā khālidụn","at":"Dan orang-orang yang beriman serta mengerjakan kebajikan, Kami tidak akan membebani seseorang melainkan menurut kesanggupannya. Mereka itulah penghuni surga; mereka kekal di dalamnya,"},{"id":"43","ar":"وَنَزَعْنَا مَا فِيْ صُدُوْرِهِمْ مِّنْ غِلٍّ تَجْرِيْ مِنْ تَحْتِهِمُ الْانْهٰرُۚ وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذِيْ هَدٰىنَا لِهٰذَاۗ وَمَا كُنَّا لِنَهْتَدِيَ لَوْلَآ اَنْ هَدٰىنَا اللّٰهُ ۚ لَقَدْ جَاۤءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۗ وَنُوْدُوْٓا اَنْ تِلْكُمُ الْجَنَّةُ اُوْرِثْتُمُوْهَا بِمَا كُنْتُمْ تَعْمَلُوْنَ","bc":"wa naza'nā mā fī ṣudụrihim min gillin tajrī min taḥtihimul-an-hār, wa qālul-ḥamdu lillāhillażī hadānā lihāżā, wa mā kunnā linahtadiya lau lā an hadānallāh, laqad jā`at rusulu rabbinā bil-ḥaqq, wa nụdū an tilkumul-jannatu ụriṡtumụhā bimā kuntum ta'malụn","at":"dan Kami mencabut rasa dendam dari dalam dada mereka, di bawahnya mengalir sungai-sungai. Mereka berkata, “Segala puji bagi Allah yang telah menunjukkan kami ke (surga) ini. Kami tidak akan mendapat petunjuk sekiranya Allah tidak menunjukkan kami. Sesungguhnya rasul-rasul Tuhan kami telah datang membawa kebenaran.” Diserukan kepada mereka, “Itulah surga yang telah diwariskan kepadamu, karena apa yang telah kamu kerjakan.”"},{"id":"44","ar":"وَنَادٰٓى اَصْحٰبُ الْجَنَّةِ اَصْحٰبَ النَّارِ اَنْ قَدْ وَجَدْنَا مَا وَعَدَنَا رَبُّنَا حَقًّا فَهَلْ وَجَدْتُّمْ مَّا وَعَدَ رَبُّكُمْ حَقًّا ۗقَالُوْا نَعَمْۚ فَاَذَّنَ مُؤَذِّنٌۢ بَيْنَهُمْ اَنْ لَّعْنَةُ اللّٰهِ عَلَى الظّٰلِمِيْنَ ","bc":"wa nādā aṣ-ḥābul-jannati aṣ-ḥāban-nāri ang qad wajadnā mā wa'adanā rabbunā ḥaqqan fa hal wajattum mā wa'ada rabbukum ḥaqqā, qālụ na'am, fa ażżana mu`ażżinum bainahum al la'natullāhi 'alaẓ-ẓālimīn","at":"Dan para penghuni surga menyeru penghuni-penghuni neraka, “Sungguh, kami telah memperoleh apa yang dijanjikan Tuhan kepada kami itu benar. Apakah kamu telah memperoleh apa yang dijanjikan Tuhan kepadamu itu benar?” Mereka menjawab, “Benar.” Kemudian penyeru (malaikat) mengumumkan di antara mereka, “Laknat Allah bagi orang-orang zalim,"},{"id":"45","ar":"اَلَّذِيْنَ يَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًاۚ وَهُمْ بِالْاٰخِرَةِ كٰفِرُوْنَۘ","bc":"allażīna yaṣuddụna 'an sabīlillāhi wa yabgụnahā 'iwajā, wa hum bil-ākhirati kāfirụn","at":"(yaitu) orang-orang yang menghalang-halangi (orang lain) dari jalan Allah dan ingin membelokkannya. Mereka itulah yang mengingkari kehidupan akhirat.”"},{"id":"46","ar":"وَبَيْنَهُمَا حِجَابٌۚ وَعَلَى الْاَعْرَافِ رِجَالٌ يَّعْرِفُوْنَ كُلًّا ۢ بِسِيْمٰىهُمْۚ وَنَادَوْا اَصْحٰبَ الْجَنَّةِ اَنْ سَلٰمٌ عَلَيْكُمْۗ لَمْ يَدْخُلُوْهَا وَهُمْ يَطْمَعُوْنَ ","bc":"wa bainahumā ḥijāb, wa 'alal-a'rāfi rijāluy ya'rifụna kullam bisīmāhum, wa nādau aṣ-ḥābal-jannati an salāmun 'alaikum, lam yadkhulụhā wa hum yaṭma'ụn","at":"Dan di antara keduanya (penghuni surga dan neraka) ada tabir dan di atas A‘raf (tempat yang tertinggi) ada orang-orang yang saling mengenal, masing-masing dengan tanda-tandanya. Mereka menyeru penghuni surga, “Salamun ‘alaikum” (salam sejahtera bagimu). Mereka belum dapat masuk, tetapi mereka ingin segera (masuk)."},{"id":"47","ar":"۞ وَاِذَا صُرِفَتْ اَبْصَارُهُمْ تِلْقَاۤءَ اَصْحٰبِ النَّارِۙ قَالُوْا رَبَّنَا لَا تَجْعَلْنَا مَعَ الْقَوْمِ الظّٰلِمِيْنَ","bc":"wa iżā ṣurifat abṣāruhum tilqā`a aṣ-ḥābin-nāri qālụ rabbanā lā taj'alnā ma'al-qaumiẓ-ẓālimīn","at":"Dan apabila pandangan mereka dialihkan ke arah penghuni neraka, mereka berkata, “Ya Tuhan kami, janganlah Engkau tempatkan kami bersama-sama orang-orang zalim itu.”"},{"id":"48","ar":"وَنَادٰٓى اَصْحٰبُ الْاَعْرَافِ رِجَالًا يَّعْرِفُوْنَهُمْ بِسِيْمٰىهُمْ قَالُوْا مَآ اَغْنٰى عَنْكُمْ جَمْعُكُمْ وَمَا كُنْتُمْ تَسْتَكْبِرُوْنَ ","bc":"wa nādā aṣ-ḥābul-a'rāfi rijālay ya'rifụnahum bisīmāhum qālụ mā agnā 'angkum jam'ukum wa mā kuntum tastakbirụn","at":"Dan orang-orang di atas A‘raf (tempat yang tertinggi) menyeru orang-orang yang mereka kenal dengan tanda-tandanya sambil berkata, “Harta yang kamu kumpulkan dan apa yang kamu sombongkan, (ternyata) tidak ada manfaatnya buat kamu."},{"id":"49","ar":"اَهٰٓؤُلَاۤءِ الَّذِيْنَ اَقْسَمْتُمْ لَا يَنَالُهُمُ اللّٰهُ بِرَحْمَةٍۗ اُدْخُلُوا الْجَنَّةَ لَا خَوْفٌ عَلَيْكُمْ وَلَآ اَنْتُمْ تَحْزَنُوْنَ ","bc":"a hā`ulā`illażīna aqsamtum lā yanaluhumullāhu biraḥmah, udkhulul-jannata lā khaufun 'alaikum wa lā antum taḥzanụn","at":"Itukah orang-orang yang kamu telah bersumpah, bahwa mereka tidak akan mendapat rahmat Allah?” (Allah berfirman), “Masuklah kamu ke dalam surga! Tidak ada rasa takut padamu dan kamu tidak pula akan bersedih hati.”"},{"id":"50","ar":"وَنَادٰٓى اَصْحٰبُ النَّارِ اَصْحٰبَ الْجَنَّةِ اَنْ اَفِيْضُوْا عَلَيْنَا مِنَ الْمَاۤءِ اَوْ مِمَّا رَزَقَكُمُ اللّٰهُ ۗقَالُوْٓا اِنَّ اللّٰهَ حَرَّمَهُمَا عَلَى الْكٰفِرِيْنَۙ ","bc":"wa nādā aṣ-ḥābun-nāri aṣ-ḥābal-jannati an afīḍụ 'alainā minal-mā`i au mimmā razaqakumullāh, qālū innallāha ḥarramahumā 'alal-kāfirīn","at":"Para penghuni neraka menyeru para penghuni surga, “Tuangkanlah (sedikit) air kepada kami atau rezeki apa saja yang telah dikaruniakan Allah kepadamu.” Mereka menjawab, “Sungguh, Allah telah mengharamkan keduanya bagi orang-orang kafir,”"},{"id":"51","ar":"الَّذِيْنَ اتَّخَذُوْا دِيْنَهُمْ لَهْوًا وَّلَعِبًا وَّغَرَّتْهُمُ الْحَيٰوةُ الدُّنْيَاۚ فَالْيَوْمَ نَنْسٰىهُمْ كَمَا نَسُوْا لِقَاۤءَ يَوْمِهِمْ هٰذَاۙ وَمَا كَانُوْا بِاٰيٰتِنَا يَجْحَدُوْنَ ","bc":"allażīnattakhażụ dīnahum lahwaw wa la'ibaw wa garrat-humul-ḥayātud-dun-yā, fal-yauma nansāhum kamā nasụ liqā`a yaumihim hāżā wa mā kānụ bi`āyātinā yaj-ḥadụn","at":"(yaitu) orang-orang yang menjadikan agamanya sebagai permainan dan senda gurau, dan mereka telah tertipu oleh kehidupan dunia. Maka pada hari ini (Kiamat), Kami melupakan mereka sebagaimana mereka dahulu melupakan pertemuan hari ini, dan karena mereka mengingkari ayat-ayat Kami."},{"id":"52","ar":"وَلَقَدْ جِئْنٰهُمْ بِكِتٰبٍ فَصَّلْنٰهُ عَلٰى عِلْمٍ هُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ ","bc":"wa laqad ji`nāhum bikitābin faṣṣalnāhu 'alā 'ilmin hudaw wa raḥmatal liqaumiy yu`minụn","at":"Sungguh, Kami telah mendatangkan Kitab (Al-Qur'an) kepada mereka, yang Kami jelaskan atas dasar pengetahuan, sebagai petunjuk dan rahmat bagi orang-orang yang beriman."},{"id":"53","ar":"هَلْ يَنْظُرُوْنَ اِلَّا تَأْوِيْلَهٗۗ يَوْمَ يَأْتِيْ تَأْوِيْلُهٗ يَقُوْلُ الَّذِيْنَ نَسُوْهُ مِنْ قَبْلُ قَدْ جَاۤءَتْ رُسُلُ رَبِّنَا بِالْحَقِّۚ فَهَلْ لَّنَا مِنْ شُفَعَاۤءَ فَيَشْفَعُوْا لَنَآ اَوْ نُرَدُّ فَنَعْمَلَ غَيْرَ الَّذِيْ كُنَّا نَعْمَلُۗ قَدْ خَسِرُوْٓا اَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ ","bc":"hal yanẓurụna illā ta`wīlah, yauma ya`tī ta`wīluhụ yaqụlullażīna nasụhu ming qablu qad jā`at rusulu rabbinā bil-ḥaqq, fa hal lanā min syufa'ā`a fa yasyfa'ụ lanā au nuraddu fa na'mala gairallażī kunnā na'mal, qad khasirū anfusahum wa ḍalla 'an-hum mā kānụ yaftarụn","at":"Tidakkah mereka hanya menanti-nanti bukti kebenaran (Al-Qur'an) itu. Pada hari bukti kebenaran itu tiba, orang-orang yang sebelum itu mengabaikannya berkata, “Sungguh, rasul-rasul Tuhan kami telah datang membawa kebenaran. Maka adakah pemberi syafaat bagi kami yang akan memberikan pertolongan kepada kami atau agar kami dikembalikan (ke dunia) sehingga kami akan beramal tidak seperti perbuatan yang pernah kami lakukan dahulu?” Mereka sebenarnya telah merugikan dirinya sendiri dan apa yang mereka ada-adakan dahulu telah hilang lenyap dari mereka."},{"id":"54","ar":"اِنَّ رَبَّكُمُ اللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۗ يُغْشِى الَّيْلَ النَّهَارَ يَطْلُبُهٗ حَثِيْثًاۙ وَّالشَّمْسَ وَالْقَمَرَ وَالنُّجُوْمَ مُسَخَّرٰتٍۢ بِاَمْرِهٖٓ ۙاَلَا لَهُ الْخَلْقُ وَالْاَمْرُۗ تَبٰرَكَ اللّٰهُ رَبُّ الْعٰلَمِيْنَ ","bc":"inna rabbakumullāhullażī khalaqas-samāwāti wal-arḍa fī sittati ayyāmin ṡummastawā 'alal-'arsy, yugsyil-lailan-nahāra yaṭlubuhụ ḥaṡīṡaw wasy-syamsa wal-qamara wan-nujụma musakhkharātim bi`amrihī alā lahul-khalqu wal-amr, tabārakallāhu rabbul-'ālamīn","at":"Sungguh, Tuhanmu (adalah) Allah yang menciptakan langit dan bumi dalam enam masa, lalu Dia bersemayam di atas ‘Arsy. Dia menutupkan malam kepada siang yang mengikutinya dengan cepat. (Dia ciptakan) matahari, bulan dan bintang-bintang tunduk kepada perintah-Nya. Ingatlah! Segala penciptaan dan urusan menjadi hak-Nya. Mahasuci Allah, Tuhan seluruh alam."},{"id":"55","ar":"اُدْعُوْا رَبَّكُمْ تَضَرُّعًا وَّخُفْيَةً ۗاِنَّهٗ لَا يُحِبُّ الْمُعْتَدِيْنَۚ ","bc":"ud'ụ rabbakum taḍarru'aw wa khufyah, innahụ lā yuḥibbul-mu'tadīn","at":"Berdoalah kepada Tuhanmu dengan rendah hati dan suara yang lembut. Sungguh, Dia tidak menyukai orang-orang yang melampaui batas."},{"id":"56","ar":"وَلَا تُفْسِدُوْا فِى الْاَرْضِ بَعْدَ اِصْلَاحِهَا وَادْعُوْهُ خَوْفًا وَّطَمَعًاۗ اِنَّ رَحْمَتَ اللّٰهِ قَرِيْبٌ مِّنَ الْمُحْسِنِيْنَ ","bc":"wa lā tufsidụ fil-arḍi ba'da iṣlāḥihā wad'ụhu khaufaw wa ṭama'ā, inna raḥmatallāhi qarībum minal-muḥsinīn","at":"Dan janganlah kamu berbuat kerusakan di bumi setelah (diciptakan) dengan baik. Berdoalah kepada-Nya dengan rasa takut dan penuh harap. Sesungguhnya rahmat Allah sangat dekat kepada orang yang berbuat kebaikan."},{"id":"57","ar":"وَهُوَ الَّذِيْ يُرْسِلُ الرِّيٰحَ بُشْرًاۢ بَيْنَ يَدَيْ رَحْمَتِهٖۗ حَتّٰٓى اِذَآ اَقَلَّتْ سَحَابًا ثِقَالًا سُقْنٰهُ لِبَلَدٍ مَّيِّتٍ فَاَنْزَلْنَا بِهِ الْمَاۤءَ فَاَخْرَجْنَا بِهٖ مِنْ كُلِّ الثَّمَرٰتِۗ كَذٰلِكَ نُخْرِجُ الْمَوْتٰى لَعَلَّكُمْ تَذَكَّرُوْنَ ","bc":"wa huwallażī yursilur-riyāḥa busyram baina yadai raḥmatih, ḥattā iżā aqallat saḥāban ṡiqālan suqnāhu libaladim mayyitin fa anzalnā bihil-mā`a fa akhrajnā bihī ming kulliṡ-ṡamarāt, każālika nukhrijul-mautā la'allakum tażakkarụn","at":"Dialah yang meniupkan angin sebagai pembawa kabar gembira, mendahului kedatangan rahmat-Nya (hujan), sehingga apabila angin itu membawa awan mendung, Kami halau ke suatu daerah yang tandus, lalu Kami turunkan hujan di daerah itu. Kemudian Kami tumbuhkan dengan hujan itu berbagai macam buah-buahan. Seperti itulah Kami membangkitkan orang yang telah mati, mudah-mudahan kamu mengambil pelajaran."},{"id":"58","ar":"وَالْبَلَدُ الطَّيِّبُ يَخْرُجُ نَبَاتُهٗ بِاِذْنِ رَبِّهٖۚ وَالَّذِيْ خَبُثَ لَا يَخْرُجُ اِلَّا نَكِدًاۗ كَذٰلِكَ نُصَرِّفُ الْاٰيٰتِ لِقَوْمٍ يَّشْكُرُوْنَ ","bc":"wal-baladuṭ-ṭayyibu yakhruju nabātuhụ bi`iżni rabbih, wallażī khabuṡa lā yakhruju illā nakidā, każālika nuṣarriful-āyāti liqaumiy yasykurụn","at":"Dan tanah yang baik, tanaman-tanamannya tumbuh subur dengan izin Tuhan; dan tanah yang buruk, tanaman-tanamannya yang tumbuh merana. Demikianlah Kami menjelaskan berulang-ulang tanda-tanda (kebesaran Kami) bagi orang-orang yang bersyukur."},{"id":"59","ar":"لَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيْمٍ ","bc":"laqad arsalnā nụḥan ilā qaumihī fa qāla yā qaumi'budullāha mā lakum min ilāhin gairuh, innī akhāfu 'alaikum 'ażāba yaumin 'aẓīm","at":"Sungguh, Kami benar-benar telah mengutus Nuh kepada kaumnya, lalu dia berkata, “Wahai kaumku! Sembahlah Allah! Tidak ada tuhan (sembahan) bagimu selain Dia. Sesungguhnya aku takut kamu akan ditimpa azab pada hari yang dahsyat (kiamat)."},{"id":"60","ar":"قَالَ الْمَلَاُ مِنْ قَوْمِهٖٓ اِنَّا لَنَرٰىكَ فِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"qālal-mala`u ming qaumihī innā lanarāka fī ḍalālim mubīn","at":"Pemuka-pemuka kaumnya berkata, “Sesungguhnya kami memandang kamu benar-benar berada dalam kesesatan yang nyata.”"},{"id":"61","ar":"قَالَ يٰقَوْمِ لَيْسَ بِيْ ضَلٰلَةٌ وَّلٰكِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ ","bc":"qāla yā qaumi laisa bī ḍalālatuw wa lākinnī rasụlum mir rabbil-'ālamīn","at":"Dia (Nuh) menjawab, “Wahai kaumku! Aku tidak sesat; tetapi aku ini seorang Rasul dari Tuhan seluruh alam."},{"id":"62","ar":"اُبَلِّغُكُمْ رِسٰلٰتِ رَبِّيْ وَاَنْصَحُ لَكُمْ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ ","bc":"uballigukum risālāti rabbī wa anṣaḥu lakum wa a'lamu minallāhi mā lā ta'lamụn","at":"Aku menyampaikan kepadamu amanat Tuhanku, memberi nasihat kepadamu, dan aku mengetahui dari Allah apa yang tidak kamu ketahui.”"},{"id":"63","ar":"اَوَعَجِبْتُمْ اَنْ جَاۤءَكُمْ ذِكْرٌ مِّنْ رَّبِّكُمْ عَلٰى رَجُلٍ مِّنْكُمْ لِيُنْذِرَكُمْ وَلِتَتَّقُوْا وَلَعَلَّكُمْ تُرْحَمُوْنَ ","bc":"a wa 'ajibtum an jā`akum żikrum mir rabbikum 'alā rajulim mingkum liyunżirakum wa litattaqụ wa la'allakum tur-ḥamụn","at":"Dan herankah kamu bahwa ada peringatan yang datang dari Tuhanmu melalui seorang laki-laki dari kalanganmu sendiri, untuk memberi peringatan kepadamu dan agar kamu bertakwa, sehingga kamu mendapat rahmat?"},{"id":"64","ar":"فَكَذَّبُوْهُ فَاَنْجَيْنٰهُ وَالَّذِيْنَ مَعَهٗ فِى الْفُلْكِ وَاَغْرَقْنَا الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ اِنَّهُمْ كَانُوْا قَوْمًا عَمِيْنَ ","bc":"fa każżabụhu fa anjaināhu wallażīna ma'ahụ fil-fulki wa agraqnallażīna każżabụ bi`āyātinā, innahum kānụ qauman 'amīn","at":"Maka mereka mendustakannya (Nuh). Lalu Kami selamatkan dia dan orang-orang yang bersamanya di dalam kapal. Kami tenggelamkan orang-orang yang mendustakan ayat-ayat Kami. Sesungguhnya mereka adalah kaum yang buta (mata hatinya)."},{"id":"65","ar":"۞ وَاِلٰى عَادٍ اَخَاهُمْ هُوْدًاۗ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ ","bc":"wa ilā 'ādin akhāhum hụdā, qāla yā qaumi'budullāha mā lakum min ilāhin gairuh, a fa lā tattaqụn","at":"Dan kepada kaum ‘Ad (Kami utus) Hud, saudara mereka. Dia berkata, “Wahai kaumku! Sembahlah Allah! Tidak ada tuhan (sembahan) bagimu selain Dia. Maka mengapa kamu tidak bertakwa?”"},{"id":"66","ar":"قَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖٓ اِنَّا لَنَرٰىكَ فِيْ سَفَاهَةٍ وَّاِنَّا لَنَظُنُّكَ مِنَ الْكٰذِبِيْنَ ","bc":"qālal-mala`ullażīna kafarụ ming qaumihī innā lanarāka fī safāhatiw wa innā lanaẓunnuka minal-kāżibīn","at":"Pemuka-pemuka orang-orang yang kafir dari kaumnya berkata, “Sesungguhnya kami memandang kamu benar-benar kurang waras dan kami kira kamu termasuk orang-orang yang berdusta.”"},{"id":"67","ar":"قَالَ يٰقَوْمِ لَيْسَ بِيْ سَفَاهَةٌ وَّلٰكِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ ","bc":"qāla yā qaumi laisa bī safāhatuw wa lākinnī rasụlum mir rabbil-'ālamīn","at":"Dia (Hud) menjawab, “Wahai kaumku! Bukan aku kurang waras, tetapi aku ini adalah Rasul dari Tuhan seluruh alam."},{"id":"68","ar":"اُبَلِّغُكُمْ رِسٰلٰتِ رَبِّيْ وَاَنَا۠ لَكُمْ نَاصِحٌ اَمِيْنٌ ","bc":"uballigukum risālāti rabbī wa ana lakum nāṣiḥun amīn","at":"Aku menyampaikan kepadamu amanat Tuhanku dan pemberi nasihat yang terpercaya kepada kamu."},{"id":"69","ar":"اَوَعَجِبْتُمْ اَنْ جَاۤءَكُمْ ذِكْرٌ مِّنْ رَّبِّكُمْ عَلٰى رَجُلٍ مِّنْكُمْ لِيُنْذِرَكُمْۗ وَاذْكُرُوْٓا اِذْ جَعَلَكُمْ خُلَفَاۤءَ مِنْۢ بَعْدِ قَوْمِ نُوْحٍ وَّزَادَكُمْ فِى الْخَلْقِ بَصْۣطَةً ۚفَاذْكُرُوْٓا اٰلَاۤءَ اللّٰهِ لَعَلَّكُمْ تُفْلِحُوْنَ ","bc":"a wa 'ajibtum an jā`akum żikrum mir rabbikum 'alā rajulim mingkum liyunżirakum, ważkurū iż ja'alakum khulafā`a mim ba'di qaumi nụḥiw wa zādakum fil-khalqi baṣṭah, fażkurū ālā`allāhi la'allakum tufliḥụn","at":"Dan herankah kamu bahwa ada peringatan yang datang dari Tuhanmu melalui seorang laki-laki dari kalanganmu sendiri, untuk memberi peringatan kepadamu? Ingatlah ketika Dia menjadikan kamu sebagai khalifah-khalifah setelah kaum Nuh, dan Dia lebihkan kamu dalam kekuatan tubuh dan perawakan. Maka ingatlah akan nikmat-nikmat Allah agar kamu beruntung. ”"},{"id":"70","ar":"قَالُوْٓا اَجِئْتَنَا لِنَعْبُدَ اللّٰهَ وَحْدَهٗ وَنَذَرَ مَا كَانَ يَعْبُدُ اٰبَاۤؤُنَاۚ فَأْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ","bc":"qālū a ji`tanā lina'budallāha waḥdahụ wa nażara mā kāna ya'budu ābā`unā, fa`tinā bimā ta'idunā ing kunta minaṣ-ṣādiqīn","at":"Mereka berkata, “Apakah kedatanganmu kepada kami, agar kami hanya menyembah Allah saja dan meninggalkan apa yang biasa disembah oleh nenek moyang kami? Maka buktikanlah ancamanmu kepada kami, jika kamu benar!”"},{"id":"71","ar":"قَالَ قَدْ وَقَعَ عَلَيْكُمْ مِّنْ رَّبِّكُمْ رِجْسٌ وَّغَضَبٌۗ اَتُجَادِلُوْنَنِيْ فِيْٓ اَسْمَاۤءٍ سَمَّيْتُمُوْهَآ اَنْتُمْ وَاٰبَاۤؤُكُمْ مَّا نَزَّلَ اللّٰهُ بِهَا مِنْ سُلْطٰنٍۗ فَانْتَظِرُوْٓا اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ ","bc":"qāla qad waqa'a 'alaikum mir rabbikum rijsuw wa gaḍab, a tujādilụnanī fī asmā`in sammaitumụhā antum wa ābā`ukum mā nazzalallāhu bihā min sulṭān, fantaẓirū innī ma'akum minal-muntaẓirīn","at":"Dia (Hud) menjawab, “Sungguh, kebencian dan kemurkaan dari Tuhan akan menimpa kamu. Apakah kamu hendak berbantah denganku tentang nama-nama (berhala) yang kamu dan nenek moyangmu buat sendiri, padahal Allah tidak menurunkan keterangan untuk itu? Jika demikian, tunggulah! Sesungguhnya aku pun bersamamu termasuk yang menunggu.”"},{"id":"72","ar":"فَاَنْجَيْنٰهُ وَالَّذِيْنَ مَعَهٗ بِرَحْمَةٍ مِّنَّا وَقَطَعْنَا دَابِرَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَمَا كَانُوْا مُؤْمِنِيْنَ ","bc":"fa anjaināhu wallażīna ma'ahụ biraḥmatim minnā wa qaṭa'nā dābirallażīna każżabụ bi`āyātinā wa mā kānụ mu`minīn","at":"Maka Kami selamatkan dia (Hud) dan orang-orang yang bersamanya dengan rahmat Kami dan Kami musnahkan sampai ke akar-akarnya orang-orang yang mendustakan ayat-ayat Kami. Mereka bukanlah orang-orang beriman."},{"id":"73","ar":"وَاِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًاۘ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ قَدْ جَاۤءَتْكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْۗ هٰذِهٖ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوْهَا تَأْكُلْ فِيْٓ اَرْضِ اللّٰهِ وَلَا تَمَسُّوْهَا بِسُوْۤءٍ فَيَأْخُذَكُمْ عَذَابٌ اَلِيْمٌ ","bc":"wa ilā ṡamụda akhāhum ṣāliḥā, qāla yā qaumi'budullāha mā lakum min ilāhin gairuh, qad jā`atkum bayyinatum mir rabbikum, hāżihī nāqatullāhi lakum āyatan fa żarụhā ta`kul fī arḍillāhi wa lā tamassụhā bisū`in fa ya`khużakum 'ażābun alīm","at":"Dan kepada kaum Samud (Kami utus) saudara mereka Saleh. Dia berkata, “Wahai kaumku! Sembahlah Allah! Tidak ada tuhan (sembahan) bagimu selain Dia. Sesungguhnya telah datang kepadamu bukti yang nyata dari Tuhanmu. Ini (seekor) unta betina dari Allah sebagai tanda untukmu. Biarkanlah ia makan di bumi Allah, janganlah disakiti, nanti akibatnya kamu akan mendapatkan siksaan yang pedih.”"},{"id":"74","ar":"وَاذْكُرُوْٓا اِذْ جَعَلَكُمْ خُلَفَاۤءَ مِنْۢ بَعْدِ عَادٍ وَّبَوَّاَكُمْ فِى الْاَرْضِ تَتَّخِذُوْنَ مِنْ سُهُوْلِهَا قُصُوْرًا وَّتَنْحِتُوْنَ الْجِبَالَ بُيُوْتًا ۚفَاذْكُرُوْٓا اٰلَاۤءَ اللّٰهِ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ ","bc":"ważkurū iż ja'alakum khulafā`a mim ba'di 'ādiw wa bawwa`akum fil-arḍi tattakhiżụna min suhụlihā quṣụraw wa tan-ḥitụnal-jibāla buyụtā, fażkurū ālā`allāhi wa lā ta'ṡau fil-arḍi mufsidīn","at":"Dan ingatlah ketika Dia menjadikan kamu khalifah-khalifah setelah kaum ‘Ad dan menempatkan kamu di bumi. Di tempat yang datar kamu dirikan istana-istana dan di bukit-bukit kamu pahat menjadi rumah-rumah. Maka ingatlah nikmat-nikmat Allah dan janganlah kamu membuat kerusakan di bumi."},{"id":"75","ar":"قَالَ الْمَلَاُ الَّذِيْنَ اسْتَكْبَرُوْا مِنْ قَوْمِهٖ لِلَّذِيْنَ اسْتُضْعِفُوْا لِمَنْ اٰمَنَ مِنْهُمْ اَتَعْلَمُوْنَ اَنَّ صٰلِحًا مُّرْسَلٌ مِّنْ رَّبِّهٖۗ قَالُوْٓا اِنَّا بِمَآ اُرْسِلَ بِهٖ مُؤْمِنُوْنَ ","bc":"qālal-mala`ullażīnastakbarụ ming qaumihī lillażīnastuḍ'ifụ liman āmana min-hum a ta'lamụna anna ṣāliḥam mursalum mir rabbih, qālū innā bimā ursila bihī mu`minụn","at":"Pemuka-pemuka yang menyombongkan diri berkata kepada orang-orang yang dianggap lemah, yaitu orang-orang yang telah beriman di antara kaumnya, “Tahukah kamu bahwa Saleh adalah seorang rasul dari Tuhannya?” Mereka menjawab, “Sesungguhnya kami percaya kepada apa yang disampaikannya.”"},{"id":"76","ar":"قَالَ الَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا بِالَّذِيْٓ اٰمَنْتُمْ بِهٖ كٰفِرُوْنَ","bc":"qālallażīnastakbarū innā billażī āmantum bihī kāfirụn","at":"Orang-orang yang menyombongkan diri berkata, “Sesungguhnya kami mengingkari apa yang kamu percayai.”"},{"id":"77","ar":"فَعَقَرُوا النَّاقَةَ وَعَتَوْا عَنْ اَمْرِ رَبِّهِمْ وَقَالُوْا يٰصٰلِحُ ائْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الْمُرْسَلِيْنَ ","bc":"fa 'aqarun-nāqata wa 'atau 'an amri rabbihim wa qālụ yā ṣāliḥu`tinā bimā ta'idunā ing kunta minal-mursalīn","at":"Kemudian mereka sembelih unta betina itu, dan berlaku angkuh terhadap perintah Tuhannya. Mereka berkata, “Wahai Saleh! Buktikanlah ancaman kamu kepada kami, jika benar engkau salah seorang rasul.”"},{"id":"78","ar":"فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَ","bc":"fa akhażat-humur-rajfatu fa aṣbaḥụ fī dārihim jāṡimīn","at":"Lalu datanglah gempa menimpa mereka, dan mereka pun mati bergelimpangan di dalam reruntuhan rumah mereka."},{"id":"79","ar":"فَتَوَلّٰى عَنْهُمْ وَقَالَ يٰقَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسَالَةَ رَبِّيْ وَنَصَحْتُ لَكُمْ وَلٰكِنْ لَّا تُحِبُّوْنَ النّٰصِحِيْنَ ","bc":"fa tawallā 'an-hum wa qāla yā qaumi laqad ablagtukum risālata rabbī wa naṣaḥtu lakum wa lākil lā tuḥibbụnan-nāṣiḥīn","at":"Kemudian dia (Saleh) pergi meninggalkan mereka sambil berkata, “Wahai kaumku! Sungguh, aku telah menyampaikan amanat Tuhanku kepadamu dan aku telah menasihati kamu. Tetapi kamu tidak menyukai orang yang memberi nasihat.”"},{"id":"80","ar":"وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اَتَأْتُوْنَ الْفَاحِشَةَ مَا سَبَقَكُمْ بِهَا مِنْ اَحَدٍ مِّنَ الْعٰلَمِيْنَ ","bc":"wa lụṭan iż qāla liqaumihī a ta`tụnal-fāḥisyata mā sabaqakum bihā min aḥadim minal-'ālamīn","at":"Dan (Kami juga telah mengutus) Lut, ketika dia berkata kepada kaumnya, “Mengapa kamu melakukan perbuatan keji, yang belum pernah dilakukan oleh seorang pun sebelum kamu (di dunia ini)."},{"id":"81","ar":"اِنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ شَهْوَةً مِّنْ دُوْنِ النِّسَاۤءِۗ بَلْ اَنْتُمْ قَوْمٌ مُّسْرِفُوْنَ ","bc":"innakum lata`tụnar-rijāla syahwatam min dụnin-nisā`, bal antum qaumum musrifụn","at":"Sungguh, kamu telah melampiaskan syahwatmu kepada sesama lelaki bukan kepada perempuan. Kamu benar-benar kaum yang melampaui batas.”"},{"id":"82","ar":"وَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوْٓا اَخْرِجُوْهُمْ مِّنْ قَرْيَتِكُمْۚ اِنَّهُمْ اُنَاسٌ يَّتَطَهَّرُوْنَ ","bc":"wa mā kāna jawāba qaumihī illā ang qālū akhrijụhum ming qaryatikum, innahum unāsuy yataṭahharụn","at":"Dan jawaban kaumnya tidak lain hanya berkata, “Usirlah mereka (Lut dan pengikutnya) dari negerimu ini, mereka adalah orang yang menganggap dirinya suci.”"},{"id":"83","ar":"فَاَنْجَيْنٰهُ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ كَانَتْ مِنَ الْغٰبِرِيْنَ ","bc":"fa anjaināhu wa ahlahū illamra`atahụ kānat minal-gābirīn","at":"Kemudian Kami selamatkan dia dan pengikutnya, kecuali istrinya. Dia (istrinya) termasuk orang-orang yang tertinggal."},{"id":"84","ar":"وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۗ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِيْنَ ","bc":"wa amṭarnā 'alaihim maṭarā, fanẓur kaifa kāna 'āqibatul-mujrimīn","at":"Dan Kami hujani mereka dengan hujan (batu). Maka perhatikanlah bagaimana kesudahan orang yang berbuat dosa itu."},{"id":"85","ar":"وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًاۗ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ قَدْ جَاۤءَتْكُمْ بَيِّنَةٌ مِّنْ رَّبِّكُمْ فَاَوْفُوا الْكَيْلَ وَالْمِيْزَانَ وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تُفْسِدُوْا فِى الْاَرْضِ بَعْدَ اِصْلَاحِهَاۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَۚ ","bc":"wa ilā madyana akhāhum syu'aibā, qāla yā qaumi'budullāha mā lakum min ilāhin gairuh, qad jā`atkum bayyinatum mir rabbikum fa auful-kaila wal mīzāna wa lā tabkhasun-nāsa asy-yā`ahum wa lā tufsidụ fil-arḍi ba'da iṣlāḥihā, żālikum khairul lakum ing kuntum mu`minīn","at":"Dan kepada penduduk Madyan, Kami (utus) Syuaib, saudara mereka sendiri. Dia berkata, “Wahai kaumku! Sembahlah Allah. Tidak ada tuhan (sembahan) bagimu selain Dia. Sesungguhnya telah datang kepadamu bukti yang nyata dari Tuhanmu. Sempurnakanlah takaran dan timbangan, dan jangan kamu merugikan orang sedikit pun. Janganlah kamu berbuat kerusakan di bumi setelah (diciptakan) dengan baik. Itulah yang lebih baik bagimu jika kamu orang beriman.”"},{"id":"86","ar":"وَلَا تَقْعُدُوْا بِكُلِّ صِرَاطٍ تُوْعِدُوْنَ وَتَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ مَنْ اٰمَنَ بِهٖ وَتَبْغُوْنَهَا عِوَجًاۚ وَاذْكُرُوْٓا اِذْ كُنْتُمْ قَلِيْلًا فَكَثَّرَكُمْۖ وَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ ","bc":"wa lā taq'udụ bikulli ṣirāṭin tụ'idụna wa taṣuddụna 'an sabīlillāhi man āmana bihī wa tabgụnahā 'iwajā, ważkurū iż kuntum qalīlan fa kaṡṡarakum wanẓurụ kaifa kāna 'āqibatul-mufsidīn","at":"Dan janganlah kamu duduk di setiap jalan dengan menakut-nakuti dan menghalang-halangi orang-orang yang beriman dari jalan Allah dan ingin membelokkannya. Ingatlah ketika kamu dahulunya sedikit, lalu Allah memperbanyak jumlah kamu. Dan perhatikanlah bagaimana kesudahan orang-orang yang berbuat kerusakan."},{"id":"87","ar":"وَاِنْ كَانَ طَاۤىِٕفَةٌ مِّنْكُمْ اٰمَنُوْا بِالَّذِيْٓ اُرْسِلْتُ بِهٖ وَطَاۤىِٕفَةٌ لَّمْ يُؤْمِنُوْا فَاصْبِرُوْا حَتّٰى يَحْكُمَ اللّٰهُ بَيْنَنَاۚ وَهُوَ خَيْرُ الْحٰكِمِيْنَ ۔","bc":"wa ing kāna ṭā`ifatum mingkum āmanụ billażī ursiltu bihī wa ṭā`ifatul lam yu`minụ faṣbirụ ḥattā yaḥkumallāhu bainanā, wa huwa khairul-ḥākimīn","at":"Jika ada segolongan di antara kamu yang beriman kepada (ajaran) yang aku diutus menyampaikannya, dan ada (pula) segolongan yang tidak beriman, maka bersabarlah sampai Allah menetapkan keputusan di antara kita. Dialah hakim yang terbaik."},{"id":"88","ar":"قَالَ الْمَلَاُ الَّذِيْنَ اسْتَكْبَرُوْا مِنْ قَوْمِهٖ لَنُخْرِجَنَّكَ يٰشُعَيْبُ وَالَّذِيْنَ اٰمَنُوْا مَعَكَ مِنْ قَرْيَتِنَآ اَوْ لَتَعُوْدُنَّ فِيْ مِلَّتِنَاۗ قَالَ اَوَلَوْ كُنَّا كَارِهِيْنَ","bc":"qālal-mala`ullażīnastakbarụ ming qaumihī lanukhrijannaka yā syu'aibu wallażīna āmanụ ma'aka ming qaryatinā au lata'ụdunna fī millatinā, qāla a walau kunnā kārihīn","at":"Pemuka-pemuka yang menyombongkan diri dari kaum Syuaib berkata, “Wahai Syuaib! Pasti kami usir engkau bersama orang-orang yang beriman dari negeri kami, kecuali engkau kembali kepada agama kami.”Syuaib berkata, “Apakah (kamu akan mengusir kami), kendatipun kami tidak suka?"},{"id":"89","ar":"قَدِ افْتَرَيْنَا عَلَى اللّٰهِ كَذِبًا اِنْ عُدْنَا فِيْ مِلَّتِكُمْ بَعْدَ اِذْ نَجّٰىنَا اللّٰهُ مِنْهَاۗ وَمَا يَكُوْنُ لَنَآ اَنْ نَّعُوْدَ فِيْهَآ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ رَبُّنَاۗ وَسِعَ رَبُّنَا كُلَّ شَيْءٍ عِلْمًاۗ عَلَى اللّٰهِ تَوَكَّلْنَاۗ رَبَّنَا افْتَحْ بَيْنَنَا وَبَيْنَ قَوْمِنَا بِالْحَقِّ وَاَنْتَ خَيْرُ الْفَاتِحِيْنَ","bc":"qadiftarainā 'alallāhi każiban in 'udnā fī millatikum ba'da iż najjānallāhu min-hā, wa mā yakụnu lanā an na'ụda fīhā illā ay yasyā`allāhu rabbunā, wasi'a rabbunā kulla syai`in 'ilmā, 'alallāhi tawakkalnā, rabbanaftaḥ bainanā wa baina qauminā bil-ḥaqqi wa anta khairul-fātiḥīn","at":"Sungguh, kami telah mengada-adakan kebohongan yang besar terhadap Allah, jika kami kembali kepada agamamu, setelah Allah melepaskan kami darinya. Dan tidaklah pantas kami kembali kepadanya, kecuali jika Allah, Tuhan kami menghendaki. Pengetahuan Tuhan kami meliputi segala sesuatu. Hanya kepada Allah kami bertawakal. Ya Tuhan kami, berilah keputusan antara kami dan kaum kami dengan hak (adil). Engkaulah pemberi keputusan terbaik.”"},{"id":"90","ar":"وَقَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ لَىِٕنِ اتَّبَعْتُمْ شُعَيْبًا ِانَّكُمْ اِذًا لَّخٰسِرُوْنَ","bc":"wa qālal-mala`ullażīna kafarụ ming qaumihī la`inittaba'tum syu'aiban innakum iżal lakhāsirụn","at":"Dan pemuka-pemuka dari kaumnya (Syuaib) yang kafir berkata (kepada sesamanya), “Sesungguhnya jika kamu mengikuti Syuaib, tentu kamu menjadi orang-orang yang rugi.”"},{"id":"91","ar":"فَاَخَذَتْهُمُ الرَّجْفَةُ فَاَصْبَحُوْا فِيْ دَارِهِمْ جٰثِمِيْنَۙ","bc":"fa akhażat-humur-rajfatu fa aṣbaḥụ fī dārihim jāṡimīn","at":"Lalu datanglah gempa menimpa mereka, dan mereka pun mati bergelimpangan di dalam reruntuhan rumah mereka."},{"id":"92","ar":"الَّذِيْنَ كَذَّبُوْا شُعَيْبًا كَاَنْ لَّمْ يَغْنَوْا فِيْهَاۚ اَلَّذِيْنَ كَذَّبُوْا شُعَيْبًا كَانُوْا هُمُ الْخٰسِرِيْنَ","bc":"allażīna każżabụ syu'aibang ka`al lam yagnau fīhā, allażīna każżabụ syu'aibang kānụ humul-khāsirīn","at":"Orang-orang yang mendustakan Syuaib seakan-akan mereka belum pernah tinggal di (negeri) itu. Mereka yang mendustakan Syuaib, itulah orang-orang yang rugi."},{"id":"93","ar":"فَتَوَلّٰى عَنْهُمْ وَقَالَ يٰقَوْمِ لَقَدْ اَبْلَغْتُكُمْ رِسٰلٰتِ رَبِّيْ وَنَصَحْتُ لَكُمْۚ فَكَيْفَ اٰسٰى عَلٰى قَوْمٍ كٰفِرِيْنَ ","bc":"fa tawallā 'an-hum wa qāla yā qaumi laqad ablagtukum risālāti rabbī wa naṣaḥtu lakum, fa kaifa āsā 'alā qauming kāfirīn","at":"Maka Syuaib meninggalkan mereka seraya berkata, “Wahai kaumku! Sungguh, aku telah menyampaikan amanat Tuhanku kepadamu dan aku telah menasihati kamu. Maka bagaimana aku akan bersedih hati terhadap orang-orang kafir?”"},{"id":"94","ar":"وَمَآ اَرْسَلْنَا فِيْ قَرْيَةٍ مِّنْ نَّبِيٍّ اِلَّآ اَخَذْنَآ اَهْلَهَا بِالْبَأْسَاۤءِ وَالضَّرَّاۤءِ لَعَلَّهُمْ يَضَّرَّعُوْنَ","bc":"wa mā arsalnā fī qaryatim min nabiyyin illā akhażnā ahlahā bil-ba`sā`i waḍ-ḍarrā`i la'allahum yaḍḍarra'ụn","at":"Dan Kami tidak mengutus seorang nabi pun kepada sesuatu negeri, (lalu penduduknya mendustakan nabi itu), melainkan Kami timpakan kepada penduduknya kesempitan dan penderitaan agar mereka (tunduk dengan) merendahkan diri."},{"id":"95","ar":"ثُمَّ بَدَّلْنَا مَكَانَ السَّيِّئَةِ الْحَسَنَةَ حَتّٰى عَفَوْا وَّقَالُوْا قَدْ مَسَّ اٰبَاۤءَنَا الضَّرَّاۤءُ وَالسَّرَّاۤءُ فَاَخَذْنٰهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ ","bc":"ṡumma baddalnā makānas-sayyi`atil-ḥasanata ḥattā 'afaw wa qālụ qad massa ābā`anaḍ-ḍarrā`u was-sarrā`u fa akhażnāhum bagtataw wa hum lā yasy'urụn","at":"Kemudian Kami ganti penderitaan itu dengan kesenangan (sehingga keturunan dan harta mereka) bertambah banyak, lalu mereka berkata, “Sungguh, nenek moyang kami telah merasakan penderitaan dan kesenangan,” maka Kami timpakan siksaan atas mereka dengan tiba-tiba tanpa mereka sadari."},{"id":"96","ar":"وَلَوْ اَنَّ اَهْلَ الْقُرٰٓى اٰمَنُوْا وَاتَّقَوْا لَفَتَحْنَا عَلَيْهِمْ بَرَكٰتٍ مِّنَ السَّمَاۤءِ وَالْاَرْضِ وَلٰكِنْ كَذَّبُوْا فَاَخَذْنٰهُمْ بِمَا كَانُوْا يَكْسِبُوْنَ","bc":"walau anna ahlal-qurā āmanụ wattaqau lafataḥnā 'alaihim barakātim minas-samā`i wal-arḍi wa lāking każżabụ fa akhażnāhum bimā kānụ yaksibụn","at":"Dan sekiranya penduduk negeri beriman dan bertakwa, pasti Kami akan melimpahkan kepada mereka berkah dari langit dan bumi, tetapi ternyata mereka mendustakan (ayat-ayat Kami), maka Kami siksa mereka sesuai dengan apa yang telah mereka kerjakan."},{"id":"97","ar":"اَفَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَّأْتِيَهُمْ بَأْسُنَا بَيَاتًا وَّهُمْ نَاۤىِٕمُوْنَۗ","bc":"a fa amina ahlul-qurā ay ya`tiyahum ba`sunā bayātaw wa hum nā`imụn","at":"Maka apakah penduduk negeri itu merasa aman dari siksaan Kami yang datang malam hari ketika mereka sedang tidur?"},{"id":"98","ar":"اَوَاَمِنَ اَهْلُ الْقُرٰٓى اَنْ يَّأْتِيَهُمْ بَأْسُنَا ضُحًى وَّهُمْ يَلْعَبُوْنَ","bc":"a wa amina ahlul-qurā ay ya`tiyahum ba`sunā ḍuḥaw wa hum yal'abụn","at":"Atau apakah penduduk negeri itu merasa aman dari siksaan Kami yang datang pada pagi hari ketika mereka sedang bermain?"},{"id":"99","ar":"اَفَاَمِنُوْا مَكْرَ اللّٰهِۚ فَلَا يَأْمَنُ مَكْرَ اللّٰهِ اِلَّا الْقَوْمُ الْخٰسِرُوْنَ ","bc":"a fa aminụ makrallāh, fa lā ya`manu makrallāhi illal-qaumul-khāsirụn","at":"Atau apakah mereka merasa aman dari siksaan Allah (yang tidak terduga-duga)? Tidak ada yang merasa aman dari siksaan Allah selain orang-orang yang rugi."},{"id":"100","ar":"اَوَلَمْ يَهْدِ لِلَّذِيْنَ يَرِثُوْنَ الْاَرْضَ مِنْۢ بَعْدِ اَهْلِهَآ اَنْ لَّوْ نَشَاۤءُ اَصَبْنٰهُمْ بِذُنُوْبِهِمْۚ وَنَطْبَعُ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَسْمَعُوْنَ","bc":"a wa lam yahdi lillażīna yariṡụnal-arḍa mim ba'di ahlihā al lau nasyā`u aṣabnāhum biżunụbihim, wa naṭba'u 'alā qulụbihim fa hum lā yasma'ụn","at":"Atau apakah belum jelas bagi orang-orang yang mewarisi suatu negeri setelah (lenyap) penduduknya? Bahwa kalau Kami menghendaki pasti Kami siksa mereka karena dosa-dosanya; dan Kami mengunci hati mereka sehingga mereka tidak dapat mendengar (pelajaran)."},{"id":"101","ar":"تِلْكَ الْقُرٰى نَقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤىِٕهَاۚ وَلَقَدْ جَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۚ فَمَا كَانُوْا لِيُؤْمِنُوْا بِمَا كَذَّبُوْا مِنْ قَبْلُۗ كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوْبِ الْكٰفِرِيْنَ ","bc":"tilkal-qurā naquṣṣu 'alaika min ambā`ihā, wa laqad jā`at-hum rusuluhum bil-bayyināt, fa mā kānụ liyu`minụ bimā każżabụ ming qabl, każālika yaṭba'ullāhu 'alā qulụbil-kāfirīn","at":"Itulah negeri-negeri (yang telah Kami binasakan) itu, Kami ceritakan sebagian kisahnya kepadamu. Rasul-rasul mereka benar-benar telah datang kepada mereka dengan membawa bukti-bukti yang nyata. Tetapi mereka tidak beriman (juga) kepada apa yang telah mereka dustakan sebelumnya. Demikianlah Allah mengunci hati orang-orang kafir."},{"id":"102","ar":"وَمَا وَجَدْنَا لِاَكْثَرِهِمْ مِّنْ عَهْدٍۚ وَاِنْ وَّجَدْنَآ اَكْثَرَهُمْ لَفٰسِقِيْنَ","bc":"wa mā wajadnā li`akṡarihim min 'ahd, wa iw wajadnā akṡarahum lafāsiqīn","at":"Dan Kami tidak mendapati kebanyakan mereka memenuhi janji. Sebaliknya yang Kami dapati kebanyakan mereka adalah orang-orang yang benar-benar fasik."},{"id":"103","ar":"ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهِمْ مُّوْسٰى بِاٰيٰتِنَآ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ فَظَلَمُوْا بِهَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ ","bc":"ṡumma ba'aṡnā mim ba'dihim mụsā bi`āyātinā ilā fir'auna wa mala`ihī fa ẓalamụ bihā, fanẓur kaifa kāna 'āqibatul-mufsidīn","at":"Setelah mereka, kemudian Kami utus Musa dengan membawa bukti-bukti Kami kepada Fir‘aun dan pemuka-pemuka kaumnya, lalu mereka mengingkari bukti-bukti itu. Maka perhatikanlah bagaimana kesudahan orang-orang yang berbuat kerusakan."},{"id":"104","ar":"وَقَالَ مُوْسٰى يٰفِرْعَوْنُ اِنِّيْ رَسُوْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَۙ","bc":"wa qāla mụsā yā fir'aunu innī rasụlum mir rabbil-'ālamīn","at":"Dan Musa berkata, “Wahai Fir‘aun! Sungguh, aku adalah seorang utusan dari Tuhan seluruh alam,"},{"id":"105","ar":"حَقِيْقٌ عَلٰٓى اَنْ لَّآ اَقُوْلَ عَلَى اللّٰهِ اِلَّا الْحَقَّۗ قَدْ جِئْتُكُمْ بِبَيِّنَةٍ مِّنْ رَّبِّكُمْ فَاَرْسِلْ مَعِيَ بَنِيْٓ اِسْرَاۤءِيْلَ ۗ","bc":"ḥaqīqun 'alā al lā aqụla 'alallāhi illal-ḥaqq, qad ji`tukum bibayyinatim mir rabbikum fa arsil ma'iya banī isrā`īl","at":"aku wajib mengatakan yang sebenarnya tentang Allah. Sungguh, aku datang kepadamu dengan membawa bukti yang nyata dari Tuhanmu, maka lepaskanlah Bani Israil (pergi) bersamaku.”"},{"id":"106","ar":"قَالَ اِنْ كُنْتَ جِئْتَ بِاٰيَةٍ فَأْتِ بِهَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ","bc":"qāla ing kunta ji`ta bi`āyatin fa`ti bihā ing kunta minaṣ-ṣādiqīn","at":"Dia (Fir‘aun) menjawab, “Jika benar engkau membawa sesuatu bukti, maka tunjukkanlah, kalau kamu termasuk orang-orang yang benar.”"},{"id":"107","ar":"فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُّبِيْنٌ ۖ ","bc":"fa alqā 'aṣāhu fa iżā hiya ṡu'bānum mubīn","at":"Lalu (Musa) melemparkan tongkatnya, tiba-tiba tongkat itu menjadi ular besar yang sebenarnya."},{"id":"108","ar":"وَّنَزَعَ يَدَهٗ فَاِذَا هِيَ بَيْضَاۤءُ لِلنّٰظِرِيْنَ","bc":"wa naza'a yadahụ fa iżā hiya baiḍā`u lin-nāẓirīn","at":"Dan dia mengeluarkan tangannya, tiba-tiba tangan itu menjadi putih (bercahaya) bagi orang-orang yang melihatnya."},{"id":"109","ar":"قَالَ الْمَلَاُ مِنْ قَوْمِ فِرْعَوْنَ اِنَّ هٰذَا لَسٰحِرٌ عَلِيْمٌۙ ","bc":"qālal-mala`u ming qaumi fir'auna inna hāżā lasāḥirun 'alīm","at":"Pemuka-pemuka kaum Fir‘aun berkata, “Orang ini benar-benar pesihir yang pandai,"},{"id":"110","ar":"يُّرِيْدُ اَنْ يُّخْرِجَكُمْ مِّنْ اَرْضِكُمْ ۚ فَمَاذَا تَأْمُرُوْنَ","bc":"yurīdu ay yukhrijakum min arḍikum, fa māżā ta`murụn","at":"yang hendak mengusir kamu dari negerimu.” (Fir‘aun berkata), “Maka apa saran kamu?”"},{"id":"111","ar":"قَالُوْآ اَرْجِهْ وَاَخَاهُ وَاَرْسِلْ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَۙ ","bc":"qālū arjih wa akhāhu wa arsil fil-madā`ini ḥāsyirīn","at":"(Pemuka-pemuka) itu menjawab, “Tahanlah (untuk sementara) dia dan saudaranya dan utuslah ke kota-kota beberapa orang untuk mengumpulkan (para pesihir),"},{"id":"112","ar":"يَأْتُوْكَ بِكُلِّ سٰحِرٍ عَلِيْمٍ","bc":"ya`tụka bikulli sāḥirin 'alīm","at":"agar mereka membawa semua pesihir yang pandai kepadamu.”"},{"id":"113","ar":"وَجَاۤءَ السَّحَرَةُ فِرْعَوْنَ قَالُوْٓا اِنَّ لَنَا لَاَجْرًا اِنْ كُنَّا نَحْنُ الْغٰلِبِيْنَ","bc":"wa jā`as-saḥaratu fir'auna qālū inna lanā la`ajran ing kunnā naḥnul-gālibīn","at":"Dan para pesihir datang kepada Fir‘aun. Mereka berkata, “(Apakah) kami akan mendapat imbalan, jika kami menang?”"},{"id":"114","ar":"قَالَ نَعَمْ وَاِنَّكُمْ لَمِنَ الْمُقَرَّبِيْنَ","bc":"qāla na'am wa innakum laminal-muqarrabīn","at":"Dia (Fir‘aun) menjawab, “Ya, bahkan kamu pasti termasuk orang-orang yang dekat (kepadaku).”"},{"id":"115","ar":"قَالُوْا يٰمُوْسٰٓى اِمَّآ اَنْ تُلْقِيَ وَاِمَّآ اَنْ نَّكُوْنَ نَحْنُ الْمُلْقِيْنَ","bc":"qālụ yā mụsā immā an tulqiya wa immā an nakụna naḥnul-mulqīn","at":"Mereka (para pesihir) berkata, “Wahai Musa! Engkaukah yang akan melemparkan lebih dahulu, atau kami yang melemparkan?”"},{"id":"116","ar":"قَالَ اَلْقُوْاۚ فَلَمَّآ اَلْقَوْا سَحَرُوْٓا اَعْيُنَ النَّاسِ وَاسْتَرْهَبُوْهُمْ وَجَاۤءُوْ بِسِحْرٍ عَظِيْمٍ","bc":"qāla alqụ, fa lammā alqau saḥarū a'yunan-nāsi wastar-habụhum wa jā`ụ bisiḥrin 'aẓīm","at":"Dia (Musa) menjawab, “Lemparkanlah (lebih dahulu)!” Maka setelah mereka melemparkan, mereka menyihir mata orang banyak dan menjadikan orang banyak itu takut, karena mereka memperlihatkan sihir yang hebat (menakjubkan)."},{"id":"117","ar":"۞ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَلْقِ عَصَاكَۚ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُوْنَۚ ","bc":"wa auḥainā ilā mụsā an alqi 'aṣāk, fa iżā hiya talqafu mā ya`fikụn","at":"Dan Kami wahyukan kepada Musa, “Lemparkanlah tongkatmu!” Maka tiba-tiba ia menelan (habis) segala kepalsuan mereka."},{"id":"118","ar":"فَوَقَعَ الْحَقُّ وَبَطَلَ مَا كَانُوْا يَعْمَلُوْنَۚ ","bc":"fa waqa'al-ḥaqqu wa baṭala mā kānụ ya'malụn","at":"Maka terbuktilah kebenaran, dan segala yang mereka kerjakan jadi sia-sia."},{"id":"119","ar":"فَغُلِبُوْا هُنَالِكَ وَانْقَلَبُوْا صٰغِرِيْنَۚ ","bc":"fa gulibụ hunālika wangqalabụ ṣāgirīn","at":"Maka mereka dikalahkan di tempat itu dan jadilah mereka orang-orang yang hina."},{"id":"120","ar":"وَاُلْقِيَ السَّحَرَةُ سٰجِدِيْنَۙ ","bc":"wa ulqiyas-saḥaratu sājidīn","at":"Dan para pesihir itu serta merta menjatuhkan diri dengan bersujud."},{"id":"121","ar":"قَالُوْٓا اٰمَنَّا بِرَبِّ الْعٰلَمِيْنَۙ ","bc":"qālū āmannā birabbil-'ālamīn","at":"Mereka berkata, “Kami beriman kepada Tuhan seluruh alam,"},{"id":"122","ar":"رَبِّ مُوْسٰى وَهٰرُوْنَ","bc":"rabbi mụsā wa hārụn","at":"(yaitu) Tuhannya Musa dan Harun.”"},{"id":"123","ar":"قَالَ فِرْعَوْنُ اٰمَنْتُمْ بِهٖ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّ هٰذَا لَمَكْرٌ مَّكَرْتُمُوْهُ فِى الْمَدِيْنَةِ لِتُخْرِجُوْا مِنْهَآ اَهْلَهَاۚ فَسَوْفَ تَعْلَمُوْنَ","bc":"qāla fir'aunu āmantum bihī qabla an āżana lakum, inna hāżā lamakrum makartumụhu fil-madīnati litukhrijụ min-hā ahlahā, fa saufa ta'lamụn","at":"Fir‘aun berkata, “Mengapa kamu beriman kepadanya sebelum aku memberi izin kepadamu? Sesungguhnya ini benar-benar tipu muslihat yang telah kamu rencanakan di kota ini, untuk mengusir penduduknya. Kelak kamu akan mengetahui (akibat perbuatanmu ini)."},{"id":"124","ar":"لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ ثُمَّ لَاُصَلِّبَنَّكُمْ اَجْمَعِيْنَ","bc":"la`uqaṭṭi'anna aidiyakum wa arjulakum min khilāfin ṡumma la`uṣallibannakum ajma'īn","at":"Pasti akan aku potong tangan dan kakimu dengan bersilang (tangan kanan dan kaki kiri atau sebaliknya), kemudian aku akan menyalib kamu semua.”"},{"id":"125","ar":"قَالُوْٓا اِنَّآ اِلٰى رَبِّنَا مُنْقَلِبُوْنَۙ ","bc":"qālū innā ilā rabbinā mungqalibụn","at":"Mereka (para pesihir) menjawab, “Sesungguhnya kami akan kembali kepada Tuhan kami."},{"id":"126","ar":"وَمَا تَنْقِمُ مِنَّآ اِلَّآ اَنْ اٰمَنَّا بِاٰيٰتِ رَبِّنَا لَمَّا جَاۤءَتْنَا ۗرَبَّنَآ اَفْرِغْ عَلَيْنَا صَبْرًا وَّتَوَفَّنَا مُسْلِمِيْنَ ","bc":"wa mā tangqimu minnā illā an āmannā bi`āyāti rabbinā lammā jā`atnā, rabbanā afrig 'alainā ṣabraw wa tawaffanā muslimīn","at":"Dan engkau tidak melakukan balas dendam kepada kami, melainkan karena kami beriman kepada ayat-ayat Tuhan kami ketika ayat-ayat itu datang kepada kami.” (Mereka berdoa), “Ya Tuhan kami, limpahkanlah kesabaran kepada kami dan matikanlah kami dalam keadaan muslim (berserah diri kepada-Mu).”"},{"id":"127","ar":"وَقَالَ الْمَلَاُ مِنْ قَوْمِ فِرْعَوْنَ اَتَذَرُ مُوْسٰى وَقَوْمَهٗ لِيُفْسِدُوْا فِى الْاَرْضِ وَيَذَرَكَ وَاٰلِهَتَكَۗ قَالَ سَنُقَتِّلُ اَبْنَاۤءَهُمْ وَنَسْتَحْيٖ نِسَاۤءَهُمْۚ وَاِنَّا فَوْقَهُمْ قَاهِرُوْنَ","bc":"wa qālal-mala`u ming qaumi fir'auna a tażaru mụsā wa qaumahụ liyufsidụ fil-arḍi wa yażaraka wa ālihatak, qāla sanuqattilu abnā`ahum wa nastaḥyī nisā`ahum, wa innā fauqahum qāhirụn","at":"Dan para pemuka dari kaum Fir‘aun berkata, “Apakah engkau akan membiarkan Musa dan kaumnya untuk berbuat kerusakan di negeri ini (Mesir) dan meninggalkanmu dan tuhan-tuhanmu?” (Fir‘aun) menjawab, “Akan kita bunuh anak-anak laki-laki mereka dan kita biarkan hidup anak-anak perempuan mereka dan sesungguhnya kita berkuasa penuh atas mereka.” "},{"id":"128","ar":"قَالَ مُوْسٰى لِقَوْمِهِ اسْتَعِيْنُوْا بِاللّٰهِ وَاصْبِرُوْاۚ اِنَّ الْاَرْضَ لِلّٰهِ ۗيُوْرِثُهَا مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖۗ وَالْعَاقِبَةُ لِلْمُتَّقِيْنَ","bc":"qāla mụsā liqaumihista'īnụ billāhi waṣbirụ, innal-arḍa lillāh, yụriṡuhā may yasyā`u min 'ibādih, wal-'āqibatu lil-muttaqīn","at":"Musa berkata kepada kaumnya, “Mohonlah pertolongan kepada Allah dan bersabarlah. Sesungguhnya bumi (ini) milik Allah; diwariskan-Nya kepada siapa saja yang Dia kehendaki di antara hamba-hamba-Nya. Dan kesudahan (yang baik) adalah bagi orang-orang yang bertakwa.”"},{"id":"129","ar":"قَالُوْٓا اُوْذِيْنَا مِنْ قَبْلِ اَنْ تَأْتِيَنَا وَمِنْۢ بَعْدِ مَا جِئْتَنَا ۗقَالَ عَسٰى رَبُّكُمْ اَنْ يُّهْلِكَ عَدُوَّكُمْ وَيَسْتَخْلِفَكُمْ فِى الْاَرْضِ فَيَنْظُرَ كَيْفَ تَعْمَلُوْنَ ","bc":"qālū ụżīnā ming qabli an ta`tiyana wa mim ba'di mā ji`tana, qāla 'asā rabbukum ay yuhlika 'aduwwakum wa yastakhlifakum fil-arḍi fa yanẓura kaifa ta'malụn","at":"Mereka (kaum Musa) berkata, ”Kami telah ditindas (oleh Fir‘aun) sebelum engkau datang kepada kami dan setelah engkau datang.” (Musa) menjawab, “Mudah-mudahan Tuhanmu membinasakan musuhmu dan menjadikan kamu khalifah di bumi; maka Dia akan melihat bagaimana perbuatanmu.”"},{"id":"130","ar":"وَلَقَدْ اَخَذْنَآ اٰلَ فِرْعَوْنَ بِالسِّنِيْنَ وَنَقْصٍ مِّنَ الثَّمَرٰتِ لَعَلَّهُمْ يَذَّكَّرُوْنَ","bc":"wa laqad akhażnā āla fir'auna bis-sinīna wa naqṣim minaṡ-ṡamarāti la'allahum yażżakkarụn","at":"Dan sungguh, Kami telah menghukum Fir‘aun dan kaumnya dengan (mendatangkan musim kemarau) bertahun-tahun dan kekurangan buah-buahan, agar mereka mengambil pelajaran."},{"id":"131","ar":"فَاِذَا جَاۤءَتْهُمُ الْحَسَنَةُ قَالُوْا لَنَا هٰذِهٖ ۚوَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَّطَّيَّرُوْا بِمُوْسٰى وَمَنْ مَّعَهٗۗ اَلَآ اِنَّمَا طٰۤىِٕرُهُمْ عِنْدَ اللّٰهِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ","bc":"fa iżā jā`at-humul-ḥasanatu qālụ lanā hāżih, wa in tuṣib-hum sayyi`atuy yaṭṭayyarụ bimụsā wa mam ma'ah, alā innamā ṭā`iruhum 'indallāhi wa lākinna akṡarahum lā ya'lamụn","at":"Kemudian apabila kebaikan (kemakmuran) datang kepada mereka, mereka berkata, “Ini adalah karena (usaha) kami.” Dan jika mereka ditimpa kesusahan, mereka lemparkan sebab kesialan itu kepada Musa dan pengikutnya. Ketahuilah, sesungguhnya nasib mereka di tangan Allah, namun kebanyakan mereka tidak mengetahui."},{"id":"132","ar":"وَقَالُوْا مَهْمَا تَأْتِنَا بِهٖ مِنْ اٰيَةٍ لِّتَسْحَرَنَا بِهَاۙ فَمَا نَحْنُ لَكَ بِمُؤْمِنِيْنَ","bc":"wa qālụ mahmā ta`tinā bihī min āyatil litas-ḥaranā bihā fa mā naḥnu laka bimu`minīn","at":"Dan mereka berkata (kepada Musa), “Bukti apa pun yang engkau bawa kepada kami untuk menyihir kami, kami tidak akan beriman kepadamu.”"},{"id":"133","ar":"فَاَرْسَلْنَا عَلَيْهِمُ الطُّوْفَانَ وَالْجَرَادَ وَالْقُمَّلَ وَالضَّفَادِعَ وَالدَّمَ اٰيٰتٍ مُّفَصَّلٰتٍۗ فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا مُّجْرِمِيْنَ","bc":"fa arsalnā 'alaihimuṭ-ṭụfāna wal-jarāda wal-qummala waḍ-ḍafādi'a wad-dama āyātim mufaṣṣalāt, fastakbarụ wa kānụ qaumam mujrimīn","at":"Maka Kami kirimkan kepada mereka topan, belalang, kutu, katak dan darah (air minum berubah menjadi darah) sebagai bukti-bukti yang jelas, tetapi mereka tetap menyombongkan diri dan mereka adalah kaum yang berdosa."},{"id":"134","ar":"وَلَمَّا وَقَعَ عَلَيْهِمُ الرِّجْزُ قَالُوْا يٰمُوْسَى ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَۚ لَىِٕنْ كَشَفْتَ عَنَّا الرِّجْزَ لَنُؤْمِنَنَّ لَكَ وَلَنُرْسِلَنَّ مَعَكَ بَنِيْٓ اِسْرَاۤءِيْلَ ۚ","bc":"wa lammā waqa'a 'alaihimur-rijzu qālụ yā mụsad'u lanā rabbaka bimā 'ahida 'indak, la`ing kasyafta 'annar-rijza lanu`minanna laka wa lanursilanna ma'aka banī isrā`īl","at":"Dan ketika mereka ditimpa azab (yang telah diterangkan itu) mereka pun berkata, “Wahai Musa! Mohonkanlah untuk kami kepada Tuhanmu sesuai dengan janji-Nya kepadamu. Jika engkau dapat menghilangkan azab itu dari kami, niscaya kami akan beriman kepadamu dan pasti akan kami biarkan Bani Israil pergi bersamamu.”"},{"id":"135","ar":"فَلَمَّا كَشَفْنَا عَنْهُمُ الرِّجْزَ اِلٰٓى اَجَلٍ هُمْ بَالِغُوْهُ اِذَا هُمْ يَنْكُثُوْنَ","bc":"fa lammā kasyafnā 'an-humur-rijza ilā ajalin hum bāligụhu iżā hum yangkuṡụn","at":"Tetapi setelah Kami hilangkan azab itu dari mereka hingga batas waktu yang harus mereka penuhi ternyata mereka ingkar janji."},{"id":"136","ar":"فَانْتَقَمْنَا مِنْهُمْ فَاَغْرَقْنٰهُمْ فِى الْيَمِّ بِاَنَّهُمْ كَذَّبُوْا بِاٰيٰتِنَا وَكَانُوْا عَنْهَا غٰفِلِيْنَ ","bc":"fantaqamnā min-hum fa agraqnāhum fil-yammi bi`annahum każżabụ bi`āyātinā wa kānụ 'an-hā gāfilīn","at":"Maka Kami hukum sebagian di antara mereka, lalu Kami tenggelamkan mereka di laut karena mereka telah mendustakan ayat-ayat Kami dan melalaikan ayat-ayat Kami."},{"id":"137","ar":"وَاَوْرَثْنَا الْقَوْمَ الَّذِيْنَ كَانُوْا يُسْتَضْعَفُوْنَ مَشَارِقَ الْاَرْضِ وَمَغَارِبَهَا الَّتِيْ بٰرَكْنَا فِيْهَاۗ وَتَمَّتْ كَلِمَتُ رَبِّكَ الْحُسْنٰى عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَۙ بِمَا صَبَرُوْاۗ وَدَمَّرْنَا مَا كَانَ يَصْنَعُ فِرْعَوْنُ وَقَوْمُهٗ وَمَا كَانُوْا يَعْرِشُوْنَ","bc":"wa auraṡnal-qaumallażīna kānụ yustaḍ'afụna masyāriqal-arḍi wa magāribahallatī bāraknā fīhā, wa tammat kalimatu rabbikal-ḥusnā 'alā banī isrā`īla bimā ṣabarụ, wa dammarnā mā kāna yaṣna'u fir'aunu wa qaumuhụ wa mā kānụ ya'risyụn","at":"Dan Kami wariskan kepada kaum yang tertindas itu, bumi bagian timur dan bagian baratnya yang telah Kami berkahi. Dan telah sempurnalah firman Tuhanmu yang baik itu (sebagai janji) untuk Bani Israil disebabkan kesabaran mereka. Dan Kami hancurkan apa yang telah dibuat Fir‘aun dan kaumnya dan apa yang telah mereka bangun."},{"id":"138","ar":"وَجَاوَزْنَا بِبَنِيْٓ اِسْرَاۤءِيْلَ الْبَحْرَ فَاَتَوْا عَلٰى قَوْمٍ يَّعْكُفُوْنَ عَلٰٓى اَصْنَامٍ لَّهُمْ ۚقَالُوْا يٰمُوْسَى اجْعَلْ لَّنَآ اِلٰهًا كَمَا لَهُمْ اٰلِهَةٌ ۗقَالَ اِنَّكُمْ قَوْمٌ تَجْهَلُوْنَ","bc":"wa jāwaznā bibanī isrā`īlal-baḥra fa atau 'alā qaumiy ya'kufụna 'alā aṣnāmil lahum, qālụ yā mụsaj'al lanā ilāhang kamā lahum ālihah, qāla innakum qaumun taj-halụn","at":"Dan Kami selamatkan Bani Israil menyeberangi laut itu (bagian utara dari Laut Merah). Ketika mereka sampai kepada suatu kaum yang tetap menyembah berhala, mereka (Bani Israil) berkata, “Wahai Musa! Buatlah untuk kami sebuah tuhan (berhala) sebagaimana mereka mempunyai beberapa tuhan (berhala).” (Musa) menjawab, “Sungguh, kamu orang-orang yang bodoh.”"},{"id":"139","ar":"اِنَّ هٰٓؤُلَاۤءِ مُتَبَّرٌ مَّا هُمْ فِيْهِ وَبٰطِلٌ مَّا كَانُوْا يَعْمَلُوْنَ","bc":"inna hā`ulā`i mutabbarum mā hum fīhi wa bāṭilum mā kānụ ya'malụn","at":"Sesungguhnya mereka akan dihancurkan (oleh kepercayaan) yang dianutnya dan akan sia-sia apa yang telah mereka kerjakan."},{"id":"140","ar":"قَالَ اَغَيْرَ اللّٰهِ اَبْغِيْكُمْ اِلٰهًا وَّهُوَ فَضَّلَكُمْ عَلَى الْعٰلَمِيْنَ","bc":"qāla a gairallāhi abgīkum ilāhaw wa huwa faḍḍalakum 'alal-'ālamīn","at":"Dia (Musa) berkata, “Pantaskah aku mencari tuhan untukmu selain Allah, padahal Dia yang telah melebihkan kamu atas segala umat (pada masa itu).”"},{"id":"141","ar":"وَاِذْ اَنْجَيْنٰكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِۚ يُقَتِّلُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْۗ وَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِيْمٌ ","bc":"wa iż anjainākum min āli fir'auna yasụmụnakum sū`al-'ażāb, yuqattilụna abnā`akum wa yastaḥyụna nisā`akum, wa fī żālikum balā`um mir rabbikum 'aẓīm","at":"Dan (ingatlah wahai Bani Israil) ketika Kami menyelamatkan kamu dari (Fir‘aun) dan kaumnya, yang menyiksa kamu dengan siksaan yang sangat berat, mereka membunuh anak-anak laki-lakimu dan membiarkan hidup anak-anak perempuanmu. Dan pada yang demikian itu merupakan cobaan yang besar dari Tuhanmu."},{"id":"142","ar":"۞ وَوٰعَدْنَا مُوْسٰى ثَلٰثِيْنَ لَيْلَةً وَّاَتْمَمْنٰهَا بِعَشْرٍ فَتَمَّ مِيْقَاتُ رَبِّهٖٓ اَرْبَعِيْنَ لَيْلَةً ۚوَقَالَ مُوْسٰى لِاَخِيْهِ هٰرُوْنَ اخْلُفْنِيْ فِيْ قَوْمِيْ وَاَصْلِحْ وَلَا تَتَّبِعْ سَبِيْلَ الْمُفْسِدِيْنَ","bc":"wa wā'adnā mụsā ṡalāṡīna lailataw wa atmamnāhā bi'asyrin fa tamma mīqātu rabbihī arba'īna lailah, wa qāla mụsā li`akhīhi hārụnakhlufnī fī qaumī wa aṣliḥ wa lā tattabi' sabīlal-mufsidīn","at":"Dan Kami telah menjanjikan kepada Musa (memberikan Taurat) tiga puluh malam, dan Kami sempurnakan jumlah malam itu dengan sepuluh (malam lagi), maka sempurnalah waktu yang telah ditentukan Tuhannya empat puluh malam. Dan Musa berkata kepada saudaranya (yaitu) Harun, “Gantikanlah aku dalam (memimpin) kaumku, dan perbaikilah (dirimu dan kaummu), dan janganlah engkau mengikuti jalan orang-orang yang berbuat kerusakan.”"},{"id":"143","ar":"وَلَمَّا جَاۤءَ مُوْسٰى لِمِيْقَاتِنَا وَكَلَّمَهٗ رَبُّهٗۙ قَالَ رَبِّ اَرِنِيْٓ اَنْظُرْ اِلَيْكَۗ قَالَ لَنْ تَرٰىنِيْ وَلٰكِنِ انْظُرْ اِلَى الْجَبَلِ فَاِنِ اسْتَقَرَّ مَكَانَهٗ فَسَوْفَ تَرٰىنِيْۚ فَلَمَّا تَجَلّٰى رَبُّهٗ لِلْجَبَلِ جَعَلَهٗ دَكًّا وَّخَرَّ مُوْسٰى صَعِقًاۚ فَلَمَّآ اَفَاقَ قَالَ سُبْحٰنَكَ تُبْتُ اِلَيْكَ وَاَنَا۠ اَوَّلُ الْمُؤْمِنِيْنَ","bc":"wa lammā jā`a mụsā limīqātinā wa kallamahụ rabbuhụ qāla rabbi arinī anẓur ilaīk, qāla lan tarānī wa lākininẓur ilal-jabali fa inistaqarra makānahụ fa saufa tarānī, fa lammā tajallā rabbuhụ lil-jabali ja'alahụ dakkaw wa kharra mụsā ṣa'iqā, fa lammā afāqa qāla sub-ḥānaka tubtu ilaika wa ana awwalul-mu`minīn","at":"Dan ketika Musa datang untuk (munajat) pada waktu yang telah Kami tentukan dan Tuhan telah berfirman (langsung) kepadanya, (Musa) berkata, “Ya Tuhanku, tampakkanlah (diri-Mu) kepadaku agar aku dapat melihat Engkau.” (Allah) berfirman, “Engkau tidak akan (sanggup) melihat-Ku, namun lihatlah ke gunung itu, jika ia tetap di tempatnya (sebagai sediakala) niscaya engkau dapat melihat-Ku.” Maka ketika Tuhannya menampakkan (keagungan-Nya) kepada gunung itu, gunung itu hancur luluh dan Musa pun jatuh pingsan. Setelah Musa sadar, dia berkata, “Mahasuci Engkau, aku bertobat kepada Engkau dan aku adalah orang yang pertama-tama beriman.”"},{"id":"144","ar":"قَالَ يٰمُوْسٰٓى اِنِّى اصْطَفَيْتُكَ عَلَى النَّاسِ بِرِسٰلٰتِيْ وَبِكَلَامِيْ ۖفَخُذْ مَآ اٰتَيْتُكَ وَكُنْ مِّنَ الشّٰكِرِيْنَ","bc":"qāla yā mụsā inniṣṭafaituka 'alan-nāsi birisālātī wa bikalāmī fa khuż mā ātaituka wa kum minasy-syākirīn","at":"(Allah) berfirman, “Wahai Musa! Sesungguhnya Aku memilih (melebihkan) engkau dari manusia yang lain (pada masamu) untuk membawa risalah-Ku dan firman-Ku, sebab itu berpegang-teguhlah kepada apa yang Aku berikan kepadamu dan hendaklah engkau termasuk orang-orang yang bersyukur.”"},{"id":"145","ar":"وَكَتَبْنَا لَهٗ فِى الْاَلْوَاحِ مِنْ كُلِّ شَيْءٍ مَّوْعِظَةً وَّتَفْصِيْلًا لِّكُلِّ شَيْءٍۚ فَخُذْهَا بِقُوَّةٍ وَّأْمُرْ قَوْمَكَ يَأْخُذُوْا بِاَحْسَنِهَا ۗسَاُورِيْكُمْ دَارَ الْفٰسِقِيْنَ","bc":"wa katabnā lahụ fil-alwāḥi ming kulli syai`im mau'iẓataw wa tafṣīlal likulli syaī`, fa khuż-hā biquwwatiw wa`mur qaumaka ya`khużụ bi`aḥsanihā, sa`urīkum dāral-fāsiqīn","at":"Dan telah Kami tuliskan untuk Musa pada lauh-lauh (Taurat) segala sesuatu sebagai pelajaran dan penjelasan untuk segala hal; maka (Kami berfirman), “Berpegangteguhlah kepadanya dan suruhlah kaummu berpegang kepadanya dengan sebaik-baiknya, Aku akan memperlihatkan kepadamu negeri orang-orang fasik.” "},{"id":"146","ar":"سَاَصْرِفُ عَنْ اٰيٰتِيَ الَّذِيْنَ يَتَكَبَّرُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّۗ وَاِنْ يَّرَوْا كُلَّ اٰيَةٍ لَّا يُؤْمِنُوْا بِهَاۚ وَاِنْ يَّرَوْا سَبِيْلَ الرُّشْدِ لَا يَتَّخِذُوْهُ سَبِيْلًاۚ وَاِنْ يَّرَوْا سَبِيْلَ الْغَيِّ يَتَّخِذُوْهُ سَبِيْلًاۗ ذٰلِكَ بِاَنَّهُمْ كَذَّبُوْا بِاٰيٰتِنَا وَكَانُوْا عَنْهَا غٰفِلِيْنَ","bc":"sa aṣrifu 'an āyātiyallażīna yatakabbarụna fil-arḍi bigairil-ḥaqq, wa iy yarau kulla āyatil lā yu`minụ bihā, wa iy yarau sabīlar-rusydi lā yattakhiżụhu sabīlā, wa iy yarau sabīlal-gayyi yattakhiżụhu sabīlā, żālika bi`annahum każżabụ bi`āyātinā wa kānụ 'an-hā gāfilīn","at":"Akan Aku palingkan dari tanda-tanda (kekuasaan-Ku) orang-orang yang menyombongkan diri di bumi tanpa alasan yang benar. Kalaupun mereka melihat setiap tanda (kekuasaan-Ku) mereka tetap tidak akan beriman kepadanya. Dan jika mereka melihat jalan yang membawa kepada petunjuk, mereka tidak (akan) menempuhnya, tetapi jika mereka melihat jalan kesesatan, mereka menempuhnya. Yang demikian adalah karena mereka mendustakan ayat-ayat Kami dan mereka selalu lengah terhadapnya."},{"id":"147","ar":"وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَلِقَاۤءِ الْاٰخِرَةِ حَبِطَتْ اَعْمَالُهُمْۗ هَلْ يُجْزَوْنَ اِلَّا مَا كَانُوْا يَعْمَلُوْنَ","bc":"wallażīna każżabụ bi`āyātinā wa liqā`il-ākhirati ḥabiṭat a'māluhum, hal yujzauna illā mā kānụ ya'malụn","at":"Dan orang-orang yang mendustakan tanda-tanda (kekuasaan) Kami dan (mendustakan) adanya pertemuan akhirat, sia-sialah amal mereka. Mereka diberi balasan sesuai dengan apa yang telah mereka kerjakan."},{"id":"148","ar":"وَاتَّخَذَ قَوْمُ مُوْسٰى مِنْۢ بَعْدِهٖ مِنْ حُلِيِّهِمْ عِجْلًا جَسَدًا لَّهٗ خُوَارٌۗ اَلَمْ يَرَوْا اَنَّهٗ لَا يُكَلِّمُهُمْ وَلَا يَهْدِيْهِمْ سَبِيْلًاۘ اِتَّخَذُوْهُ وَكَانُوْا ظٰلِمِيْنَ","bc":"wattakhaża qaumu mụsā mim ba'dihī min ḥuliyyihim 'ijlan jasadal lahụ khuwār, a lam yarau annahụ lā yukallimuhum wa lā yahdīhim sabīlā, ittakhażụhu wa kānụ ẓālimīn","at":"Dan kaum Musa, setelah kepergian (Musa ke Gunung Sinai) mereka membuat patung anak sapi yang bertubuh dan dapat melenguh (bersuara) dari perhiasan (emas). Apakah mereka tidak mengetahui bahwa (patung) anak sapi itu tidak dapat berbicara dengan mereka dan tidak dapat (pula) menunjukkan jalan kepada mereka? Mereka menjadikannya (sebagai sembahan). Mereka adalah orang-orang yang zalim."},{"id":"149","ar":"وَلَمَّا سُقِطَ فِيْٓ اَيْدِيْهِمْ وَرَاَوْا اَنَّهُمْ قَدْ ضَلُّوْاۙ قَالُوْا لَىِٕنْ لَّمْ يَرْحَمْنَا رَبُّنَا وَيَغْفِرْ لَنَا لَنَكُوْنَنَّ مِنَ الْخٰسِرِيْنَ","bc":"wa lammā suqiṭa fī aidīhim wa ra`au annahum qad ḍallụ qālụ la`il lam yar-ḥamnā rabbunā wa yagfir lanā lanakụnanna minal-khāsirīn","at":"Dan setelah mereka menyesali perbuatannya dan mengetahui bahwa telah sesat, mereka pun berkata, “Sungguh, jika Tuhan kami tidak memberi rahmat kepada kami dan tidak mengampuni kami, pastilah kami menjadi orang-orang yang rugi.”"},{"id":"150","ar":"وَلَمَّا رَجَعَ مُوْسٰٓى اِلٰى قَوْمِهٖ غَضْبَانَ اَسِفًاۙ قَالَ بِئْسَمَا خَلَفْتُمُوْنِيْ مِنْۢ بَعْدِيْۚ اَعَجِلْتُمْ اَمْرَ رَبِّكُمْۚ وَاَلْقَى الْاَلْوَاحَ وَاَخَذَ بِرَأْسِ اَخِيْهِ يَجُرُّهٗٓ اِلَيْهِ ۗقَالَ ابْنَ اُمَّ اِنَّ الْقَوْمَ اسْتَضْعَفُوْنِيْ وَكَادُوْا يَقْتُلُوْنَنِيْۖ فَلَا تُشْمِتْ بِيَ الْاَعْدَاۤءَ وَلَا تَجْعَلْنِيْ مَعَ الْقَوْمِ الظّٰلِمِيْنَ","bc":"wa lammā raja'a mụsā ilā qaumihī gaḍbāna asifang qāla bi`samā khalaftumụnī mim ba'dī, a 'ajiltum amra rabbikum, wa alqal-alwāḥa wa akhaża bira`si akhīhi yajurruhū ilaīh, qālabna umma innal-qaumastaḍ'afụnī wa kādụ yaqtulụnanī fa lā tusymit biyal-a'dā`a wa lā taj'alnī ma'al qaumiẓ-ẓālimīn","at":"Dan ketika Musa telah kembali kepada kaumnya, dengan marah dan sedih hati dia berkata, “Alangkah buruknya perbuatan yang kamu kerjakan selama kepergianku! Apakah kamu hendak mendahului janji Tuhanmu?” Musa pun melemparkan lauh-lauh (Taurat) itu dan memegang kepala saudaranya (Harun) sambil menarik ke arahnya. (Harun) berkata, “Wahai anak ibuku! Kaum ini telah menganggapku lemah dan hampir saja mereka membunuhku, sebab itu janganlah engkau menjadikan musuh-musuh menyoraki melihat kemalanganku, dan janganlah engkau jadikan aku sebagai orang-orang yang zalim.”"},{"id":"151","ar":"قَالَ رَبِّ اغْفِرْ لِيْ وَلِاَخِيْ وَاَدْخِلْنَا فِيْ رَحْمَتِكَ ۖوَاَنْتَ اَرْحَمُ الرّٰحِمِيْنَ ","bc":"qāla rabbigfir lī wa li`akhī wa adkhilnā fī raḥmatika wa anta ar-ḥamur-rāḥimīn","at":"Dia (Musa) berdoa, “Ya Tuhanku, ampunilah aku dan saudaraku dan masukkanlah kami ke dalam rahmat Engkau, dan Engkau adalah Maha Penyayang dari semua penyayang.”"},{"id":"152","ar":"اِنَّ الَّذِيْنَ اتَّخَذُوا الْعِجْلَ سَيَنَالُهُمْ غَضَبٌ مِّنْ رَّبِّهِمْ وَذِلَّةٌ فِى الْحَيٰوةِ الدُّنْيَاۗ وَكَذٰلِكَ نَجْزِى الْمُفْتَرِيْنَ","bc":"innallażīnattakhażul-'ijla sayanāluhum gaḍabum mir rabbihim wa żillatun fil-ḥayātid-dun-yā, wa każālika najzil-muftarīn","at":"Sesungguhnya orang-orang yang menjadikan (patung) anak sapi (sebagai sembahannya), kelak akan menerima kemurkaan dari Tuhan mereka dan kehinaan dalam kehidupan di dunia. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat kebohongan."},{"id":"153","ar":"وَالَّذِيْنَ عَمِلُوا السَّيِّاٰتِ ثُمَّ تَابُوْا مِنْۢ بَعْدِهَا وَاٰمَنُوْٓا اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ","bc":"wallażīna 'amilus-sayyi`āti ṡumma tābụ mim ba'dihā wa āmanū inna rabbaka mim ba'dihā lagafụrur raḥīm","at":"Dan orang-orang yang telah mengerjakan kejahatan, kemudian bertobat dan beriman, niscaya setelah itu Tuhanmu Maha Pengampun, Maha Penyayang."},{"id":"154","ar":"وَلَمَّا سَكَتَ عَنْ مُّوْسَى الْغَضَبُ اَخَذَ الْاَلْوَاحَۖ وَفِيْ نُسْخَتِهَا هُدًى وَّرَحْمَةٌ لِّلَّذِيْنَ هُمْ لِرَبِّهِمْ يَرْهَبُوْنَ","bc":"wa lammā sakata 'am mụsal-gaḍabu akhażal-alwāḥa wa fī nuskhatihā hudaw wa raḥmatul lillażīna hum lirabbihim yar-habụn","at":"Dan setelah amarah Musa mereda, diambilnya (kembali) lauh-lauh (Taurat) itu; di dalam tulisannya terdapat petunjuk dan rahmat bagi orang-orang yang takut kepada Tuhannya."},{"id":"155","ar":"وَاخْتَارَ مُوْسٰى قَوْمَهٗ سَبْعِيْنَ رَجُلًا لِّمِيْقَاتِنَا ۚفَلَمَّآ اَخَذَتْهُمُ الرَّجْفَةُ قَالَ رَبِّ لَوْ شِئْتَ اَهْلَكْتَهُمْ مِّنْ قَبْلُ وَاِيَّايَۗ اَتُهْلِكُنَا بِمَا فَعَلَ السُّفَهَاۤءُ مِنَّاۚ اِنْ هِيَ اِلَّا فِتْنَتُكَۗ تُضِلُّ بِهَا مَنْ تَشَاۤءُ وَتَهْدِيْ مَنْ تَشَاۤءُۗ اَنْتَ وَلِيُّنَا فَاغْفِرْ لَنَا وَارْحَمْنَا وَاَنْتَ خَيْرُ الْغَافِرِيْنَ","bc":"wakhtāra mụsā qaumahụ sab'īna rajulal limīqātinā, fa lammā akhażat-humur-rajfatu qāla rabbi lau syi`ta ahlaktahum ming qablu wa iyyāy, a tuhlikunā bimā fa'alas-sufahā`u minnā, in hiya illā fitnatuk, tuḍillu bihā man tasyā`u wa tahdī man tasyā`, anta waliyyunā fagfir lanā war-ḥamnā wa anta khairul-gāfirīn","at":"Dan Musa memilih tujuh puluh orang dari kaumnya untuk (memohon tobat kepada Kami) pada waktu yang telah Kami tentukan. Ketika mereka ditimpa gempa bumi, Musa berkata, “Ya Tuhanku, jika Engkau kehendaki, tentulah Engkau binasakan mereka dan aku sebelum ini. Apakah Engkau akan membinasakan kami karena perbuatan orang-orang yang kurang berakal di antara kami? Itu hanyalah cobaan dari-Mu, Engkau sesatkan dengan cobaan itu siapa yang Engkau kehendaki dan Engkau beri petunjuk kepada siapa yang Engkau kehendaki. Engkaulah pemimpin kami, maka ampunilah kami dan berilah kami rahmat. Engkaulah pemberi ampun yang terbaik.”"},{"id":"156","ar":"۞ وَاكْتُبْ لَنَا فِيْ هٰذِهِ الدُّنْيَا حَسَنَةً وَّفِى الْاٰخِرَةِ اِنَّا هُدْنَآ اِلَيْكَۗ قَالَ عَذَابِيْٓ اُصِيْبُ بِهٖ مَنْ اَشَاۤءُۚ وَرَحْمَتِيْ وَسِعَتْ كُلَّ شَيْءٍۗ فَسَاَكْتُبُهَا لِلَّذِيْنَ يَتَّقُوْنَ وَيُؤْتُوْنَ الزَّكٰوةَ وَالَّذِيْنَ هُمْ بِاٰيٰتِنَا يُؤْمِنُوْنَۚ ","bc":"waktub lana fī hāżihid-dun-yā ḥasanataw wa fil-ākhirati innā hudnā ilaīk, qāla 'ażābī uṣību bihī man asyā`, wa raḥmatī wasi'at kulla syaī`, fa sa`aktubuhā lillażīna yattaqụna wa yu`tụnaz-zakāta wallażīna hum bi`āyātinā yu`minụn","at":"Dan tetapkanlah untuk kami kebaikan di dunia ini dan di akhirat. Sungguh, kami kembali (bertobat) kepada Engkau. (Allah) berfirman, “Siksa-Ku akan Aku timpakan kepada siapa yang Aku kehendaki dan rahmat-Ku meliputi segala sesuatu. Maka akan Aku tetapkan rahmat-Ku bagi orang-orang yang bertakwa, yang menunaikan zakat dan orang-orang yang beriman kepada ayat-ayat Kami.”"},{"id":"157","ar":"اَلَّذِيْنَ يَتَّبِعُوْنَ الرَّسُوْلَ النَّبِيَّ الْاُمِّيَّ الَّذِيْ يَجِدُوْنَهٗ مَكْتُوْبًا عِنْدَهُمْ فِى التَّوْرٰىةِ وَالْاِنْجِيْلِ يَأْمُرُهُمْ بِالْمَعْرُوْفِ وَيَنْهٰىهُمْ عَنِ الْمُنْكَرِ وَيُحِلُّ لَهُمُ الطَّيِّبٰتِ وَيُحَرِّمُ عَلَيْهِمُ الْخَبٰۤىِٕثَ وَيَضَعُ عَنْهُمْ اِصْرَهُمْ وَالْاَغْلٰلَ الَّتِيْ كَانَتْ عَلَيْهِمْۗ فَالَّذِيْنَ اٰمَنُوْا بِهٖ وَعَزَّرُوْهُ وَنَصَرُوْهُ وَاتَّبَعُوا النُّوْرَ الَّذِيْٓ اُنْزِلَ مَعَهٗٓ ۙاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ","bc":"allażīna yattabi'ụnar-rasụlan-nabiyyal-ummiyyallażī yajidụnahụ maktụban 'indahum fit-taurāti wal-injīli ya`muruhum bil-ma'rụfi wa yan-hāhum 'anil-mungkari wa yuḥillu lahumuṭ-ṭayyibāti wa yuḥarrimu 'alaihimul-khabā`iṡa wa yaḍa'u 'an-hum iṣrahum wal-aglālallatī kānat 'alaihim, fallażīna āmanụ bihī wa 'azzarụhu wa naṣarụhu wattaba'un-nụrallażī unzila ma'ahū ulā`ika humul-mufliḥụn","at":"(Yaitu) orang-orang yang mengikuti Rasul, Nabi yang ummi (tidak bisa baca tulis) yang (namanya) mereka dapati tertulis di dalam Taurat dan Injil yang ada pada mereka, yang menyuruh mereka berbuat yang makruf dan mencegah dari yang mungkar, dan yang menghalalkan segala yang baik bagi mereka dan mengharamkan segala yang buruk bagi mereka, dan membebaskan beban-beban dan belenggu-belenggu yang ada pada mereka. Adapun orang-orang yang beriman kepadanya, memuliakannya, menolongnya dan mengikuti cahaya yang terang yang diturunkan kepadanya (Al-Qur'an), mereka itulah orang-orang beruntung."},{"id":"158","ar":"قُلْ يٰٓاَيُّهَا النَّاسُ اِنِّيْ رَسُوْلُ اللّٰهِ اِلَيْكُمْ جَمِيْعًا ۨالَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۚ لَآ اِلٰهَ اِلَّا هُوَ يُحْيٖ وَيُمِيْتُۖ فَاٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهِ النَّبِيِّ الْاُمِّيِّ الَّذِيْ يُؤْمِنُ بِاللّٰهِ وَكَلِمٰتِهٖ وَاتَّبِعُوْهُ لَعَلَّكُمْ تَهْتَدُوْنَ","bc":"qul yā ayyuhan-nāsu innī rasụlullāhi ilaikum jamī'anillażī lahụ mulkus-samāwāti wal-arḍ, lā ilāha illā huwa yuḥyī wa yumītu fa āminụ billāhi wa rasụlihin-nabiyyil-ummiyyillażī yu`minu billāhi wa kalimātihī wattabi'ụhu la'allakum tahtadụn","at":"Katakanlah (Muhammad), “Wahai manusia! Sesungguhnya aku ini utusan Allah bagi kamu semua, Yang memiliki kerajaan langit dan bumi; tidak ada tuhan (yang berhak disembah) selain Dia, Yang menghidupkan dan mematikan, maka berimanlah kamu kepada Allah dan Rasul-Nya, (yaitu) Nabi yang ummi yang beriman kepada Allah dan kepada kalimat-kalimat-Nya (kitab-kitab-Nya). Ikutilah dia, agar kamu mendapat petunjuk.” "},{"id":"159","ar":"وَمِنْ قَوْمِ مُوْسٰٓى اُمَّةٌ يَّهْدُوْنَ بِالْحَقِّ وَبِهٖ يَعْدِلُوْنَ","bc":"wa ming qaumi mụsā ummatuy yahdụna bil-ḥaqqi wa bihī ya'dilụn","at":"Dan di antara kaum Musa itu terdapat suatu umat yang memberi petunjuk (kepada manusia) dengan (dasar) kebenaran dan dengan itu (pula) mereka berlaku adil menjalankan keadilan."},{"id":"160","ar":"وَقَطَّعْنٰهُمُ اثْنَتَيْ عَشْرَةَ اَسْبَاطًا اُمَمًاۗ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اِذِ اسْتَسْقٰىهُ قَوْمُهٗٓ اَنِ اضْرِبْ بِّعَصَاكَ الْحَجَرَۚ فَانْۢبَجَسَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًاۗ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَّشْرَبَهُمْۗ وَظَلَّلْنَا عَلَيْهِمُ الْغَمَامَ وَاَنْزَلْنَا عَلَيْهِمُ الْمَنَّ وَالسَّلْوٰىۗ كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْۗ وَمَا ظَلَمُوْنَا وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ","bc":"wa qaṭṭa'nāhumuṡnatai 'asyrata asbāṭan umamā, wa auḥainā ilā mụsā iżistasqāhu qaumuhū aniḍrib bi'aṣākal-ḥajar, fambajasat min-huṡnatā 'asyrata 'ainā, qad 'alima kullu unāsim masyrabahum, wa ẓallalnā 'alaihimul-gamāma wa anzalnā 'alaihimul-manna was-salwā, kulụ min ṭayyibāti mā razaqnākum, wa mā ẓalamụnā wa lāking kānū anfusahum yaẓlimụn","at":"Dan Kami membagi mereka menjadi dua belas suku yang masing-masing berjumlah besar, dan Kami wahyukan kepada Musa ketika kaumnya meminta air kepadanya, “Pukullah batu itu dengan tongkatmu!” Maka memancarlah dari (batu) itu dua belas mata air. Setiap suku telah mengetahui tempat minumnya masing-masing. Dan Kami naungi mereka dengan awan dan Kami turunkan kepada mereka mann dan salwa. (Kami berfirman), “Makanlah yang baik-baik dari rezeki yang telah Kami berikan kepadamu.” Mereka tidak menzalimi Kami, tetapi merekalah yang selalu menzalimi dirinya sendiri."},{"id":"161","ar":"وَاِذْ قِيْلَ لَهُمُ اسْكُنُوْا هٰذِهِ الْقَرْيَةَ وَكُلُوْا مِنْهَا حَيْثُ شِئْتُمْ وَقُوْلُوْا حِطَّةٌ وَّادْخُلُوا الْبَابَ سُجَّدًا نَّغْفِرْ لَكُمْ خَطِيْۤـٰٔتِكُمْۗ سَنَزِيْدُ الْمُحْسِنِيْنَ","bc":"wa iż qīla lahumuskunụ hāżihil-qaryata wa kulụ min-hā ḥaiṡu syi`tum wa qụlụ hiṭṭatuw wadkhulul-bāba sujjadan nagfir lakum khaṭī`ātikum, sanazīdul-muḥsinīn","at":"Dan (ingatlah), ketika dikatakan kepada mereka (Bani Israil), “Diamlah di negeri ini (Baitulmaqdis) dan makanlah dari (hasil bumi)nya di mana saja kamu kehendaki.” Dan katakanlah, “Bebaskanlah kami dari dosa kami dan masukilah pintu gerbangnya sambil membungkuk, niscaya Kami ampuni kesalahan-kesalahanmu.” Kelak akan Kami tambah (pahala) kepada orang-orang yang berbuat baik."},{"id":"162","ar":"فَبَدَّلَ الَّذِيْنَ ظَلَمُوْا مِنْهُمْ قَوْلًا غَيْرَ الَّذِيْ قِيْلَ لَهُمْ فَاَرْسَلْنَا عَلَيْهِمْ رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَظْلِمُوْنَ ","bc":"fa baddalallażīna ẓalamụ min-hum qaulan gairallażī qīla lahum fa arsalnā 'alaihim rijzam minas-samā`i bimā kānụ yaẓlimụn","at":"Maka orang-orang yang zalim di antara mereka mengganti (perkataan itu) dengan perkataan yang tidak dikatakan kepada mereka, maka Kami timpakan kepada mereka azab dari langit disebabkan kezaliman mereka."},{"id":"163","ar":"وَسْـَٔلْهُمْ عَنِ الْقَرْيَةِ الَّتِيْ كَانَتْ حَاضِرَةَ الْبَحْرِۘ اِذْ يَعْدُوْنَ فِى السَّبْتِ اِذْ تَأْتِيْهِمْ حِيْتَانُهُمْ يَوْمَ سَبْتِهِمْ شُرَّعًا وَّيَوْمَ لَا يَسْبِتُوْنَۙ لَا تَأْتِيْهِمْ ۛ كَذٰلِكَ ۛنَبْلُوْهُمْ بِمَا كَانُوْا يَفْسُقُوْنَ","bc":"was`al-hum 'anil-qaryatillatī kānat hāḍiratal-baḥr, iż ya'dụna fis-sabti iż ta`tīhim ḥītānuhum yauma sabtihim syurra'aw wa yauma lā yasbitụna lā ta`tīhim, każālika nablụhum bimā kānụ yafsuqụn","at":"Dan tanyakanlah kepada Bani Israil tentang negeri yang terletak di dekat laut ketika mereka melanggar aturan pada hari Sabtu, (yaitu) ketika datang kepada mereka ikan-ikan (yang berada di sekitar) mereka terapung-apung di permukaan air, padahal pada hari-hari yang bukan Sabat ikan-ikan itu tidak datang kepada mereka. Demikianlah Kami menguji mereka disebabkan mereka berlaku fasik."},{"id":"164","ar":"وَاِذْ قَالَتْ اُمَّةٌ مِّنْهُمْ لِمَ تَعِظُوْنَ قَوْمًاۙ ۨاللّٰهُ مُهْلِكُهُمْ اَوْ مُعَذِّبُهُمْ عَذَابًا شَدِيْدًاۗ قَالُوْا مَعْذِرَةً اِلٰى رَبِّكُمْ وَلَعَلَّهُمْ يَتَّقُوْنَ","bc":"wa iż qālat ummatum min-hum lima ta'iẓụna qaumanillāhu muhlikuhum au mu'ażżibuhum 'ażāban syadīdā, qālụ ma'żiratan ilā rabbikum wa la'allahum yattaqụn","at":"Dan (ingatlah) ketika suatu umat di antara mereka berkata, “Mengapa kamu menasihati kaum yang akan dibinasakan atau diazab Allah dengan azab yang sangat keras?” Mereka menjawab, “Agar kami mempunyai alasan (lepas tanggung jawab) kepada Tuhanmu, dan agar mereka bertakwa.”"},{"id":"165","ar":"فَلَمَّا نَسُوْا مَا ذُكِّرُوْا بِهٖٓ اَنْجَيْنَا الَّذِيْنَ يَنْهَوْنَ عَنِ السُّوْۤءِ وَاَخَذْنَا الَّذِيْنَ ظَلَمُوْا بِعَذَابٍۢ بَـِٔيْسٍۢ بِمَا كَانُوْا يَفْسُقُوْنَ","bc":"fa lammā nasụ mā żukkirụ bihī anjainallażīna yan-hauna 'anis-sū`i wa akhażnallażīna ẓalamụ bi'ażābim ba`īsim bimā kānụ yafsuqụn","at":"Maka setelah mereka melupakan apa yang diperingatkan kepada mereka, Kami selamatkan orang-orang yang melarang orang berbuat jahat dan Kami timpakan kepada orang-orang yang zalim siksaan yang keras, disebabkan mereka selalu berbuat fasik."},{"id":"166","ar":"فَلَمَّا عَتَوْا عَنْ مَّا نُهُوْا عَنْهُ قُلْنَا لَهُمْ كُوْنُوْا قِرَدَةً خَاسِـِٕيْنَ","bc":"fa lammā 'atau 'am mā nuhụ 'an-hu qulnā lahum kụnụ qiradatan khāsi`īn","at":"Maka setelah mereka bersikap sombong terhadap segala apa yang dilarang. Kami katakan kepada mereka, “Jadilah kamu kera yang hina.”"},{"id":"167","ar":"وَاِذْ تَاَذَّنَ رَبُّكَ لَيَبْعَثَنَّ عَلَيْهِمْ اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ يَّسُوْمُهُمْ سُوْۤءَ الْعَذَابِۗ اِنَّ رَبَّكَ لَسَرِيْعُ الْعِقَابِۖ وَاِنَّهٗ لَغَفُوْرٌ رَّحِيْمٌ","bc":"wa iż ta`ażżana rabbuka layab'aṡanna 'alaihim ilā yaumil-qiyāmati may yasụmuhum sū`al-'ażāb, inna rabbaka lasarī'ul-'iqābi wa innahụ lagafụrur raḥīm","at":"Dan (ingatlah), ketika Tuhanmu memberitahukan, bahwa sungguh, Dia akan mengirim orang-orang yang akan menimpakan azab yang seburuk-buruknya kepada mereka (orang Yahudi) sampai hari Kiamat. Sesungguhnya Tuhanmu sangat cepat siksa-Nya, dan sesungguhnya Dia Maha Pengampun, Maha Penyayang."},{"id":"168","ar":"وَقَطَّعْنٰهُمْ فِى الْاَرْضِ اُمَمًاۚ مِنْهُمُ الصّٰلِحُوْنَ وَمِنْهُمْ دُوْنَ ذٰلِكَ ۖوَبَلَوْنٰهُمْ بِالْحَسَنٰتِ وَالسَّيِّاٰتِ لَعَلَّهُمْ يَرْجِعُوْنَ","bc":"wa qaṭṭa'nāhum fil-arḍi umamā, min-humuṣ-ṣāliḥụna wa min-hum dụna żālika wa balaunāhum bil-ḥasanāti was-sayyi`āti la'allahum yarji'ụn","at":"Dan Kami pecahkan mereka di dunia ini menjadi beberapa golongan; di antaranya ada orang-orang yang saleh dan ada yang tidak demikian. Dan Kami uji mereka dengan (nikmat) yang baik-baik dan (bencana) yang buruk-buruk, agar mereka kembali (kepada kebenaran)."},{"id":"169","ar":"فَخَلَفَ مِنْۢ بَعْدِهِمْ خَلْفٌ وَّرِثُوا الْكِتٰبَ يَأْخُذُوْنَ عَرَضَ هٰذَا الْاَدْنٰى وَيَقُوْلُوْنَ سَيُغْفَرُ لَنَاۚ وَاِنْ يَّأْتِهِمْ عَرَضٌ مِّثْلُهٗ يَأْخُذُوْهُۗ اَلَمْ يُؤْخَذْ عَلَيْهِمْ مِّيْثَاقُ الْكِتٰبِ اَنْ لَّا يَقُوْلُوْا عَلَى اللّٰهِ اِلَّا الْحَقَّ وَدَرَسُوْا مَا فِيْهِۗ وَالدَّارُ الْاٰخِرَةُ خَيْرٌ لِّلَّذِيْنَ يَتَّقُوْنَۗ اَفَلَا تَعْقِلُوْنَ","bc":"fa khalafa mim ba'dihim khalfuw wariṡul-kitāba ya`khużụna 'araḍa hāżal-adnā wa yaqụlụna sayugfaru lanā, wa iy ya`tihim 'araḍum miṡluhụ ya`khużụh, a lam yu`khaż 'alaihim mīṡāqul-kitābi al lā yaqụlụ 'alallāhi illal-ḥaqqa wa darasụ mā fīh, wad-dārul-ākhiratu khairul lillażīna yattaqụn, a fa lā ta'qilụn","at":"Maka setelah mereka, datanglah generasi (yang jahat) yang mewarisi Taurat, yang mengambil harta benda dunia yang rendah ini. Lalu mereka berkata, “Kami akan diberi ampun.” Dan kelak jika harta benda dunia datang kepada mereka sebanyak itu (pula), niscaya mereka akan mengambilnya (juga). Bukankah mereka sudah terikat perjanjian dalam Kitab (Taurat) bahwa mereka tidak akan mengatakan terhadap Allah, kecuali yang benar, padahal mereka telah mempelajari apa yang tersebut di dalamnya? Negeri akhirat itu lebih baik bagi mereka yang bertakwa. Maka tidakkah kamu mengerti?"},{"id":"170","ar":"وَالَّذِيْنَ يُمَسِّكُوْنَ بِالْكِتٰبِ وَاَقَامُوا الصَّلٰوةَۗ اِنَّا لَا نُضِيْعُ اَجْرَ الْمُصْلِحِيْنَ","bc":"wallażīna yumassikụna bil-kitābi wa aqāmuṣ-ṣalāh, innā lā nuḍī'u ajral-muṣliḥīn","at":"Dan orang-orang yang berpegang teguh pada Kitab (Taurat) serta melaksanakan salat, (akan diberi pahala). Sungguh, Kami tidak akan menghilangkan pahala orang-orang saleh."},{"id":"171","ar":"۞ وَاِذْ نَتَقْنَا الْجَبَلَ فَوْقَهُمْ كَاَنَّهٗ ظُلَّةٌ وَّظَنُّوْٓا اَنَّهٗ وَاقِعٌۢ بِهِمْۚ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاذْكُرُوْا مَا فِيْهِ لَعَلَّكُمْ تَتَّقُوْنَ ","bc":"wa iż nataqnal-jabala fauqahum ka`annahụ ẓullatuw wa ẓannū annahụ wāqi'um bihim, khużụ mā ātainākum biquwwatiw ważkurụ mā fīhi la'allakum tattaqụn","at":"Dan (ingatlah) ketika Kami mengangkat gunung ke atas mereka, seakan-akan (gunung) itu naungan awan dan mereka yakin bahwa (gunung) itu akan jatuh menimpa mereka. (Dan Kami firmankan kepada mereka), “Peganglah dengan teguh apa yang telah Kami berikan kepadamu, serta ingatlah selalu (amalkanlah) apa yang tersebut di dalamnya agar kamu menjadi orang-orang bertakwa.”"},{"id":"172","ar":"وَاِذْ اَخَذَ رَبُّكَ مِنْۢ بَنِيْٓ اٰدَمَ مِنْ ظُهُوْرِهِمْ ذُرِّيَّتَهُمْ وَاَشْهَدَهُمْ عَلٰٓى اَنْفُسِهِمْۚ اَلَسْتُ بِرَبِّكُمْۗ قَالُوْا بَلٰىۛ شَهِدْنَا ۛاَنْ تَقُوْلُوْا يَوْمَ الْقِيٰمَةِ اِنَّا كُنَّا عَنْ هٰذَا غٰفِلِيْنَۙ ","bc":"wa iż akhaża rabbuka mim banī ādama min ẓuhụrihim żurriyyatahum wa asy-hadahum 'alā anfusihim, a lastu birabbikum, qālụ balā syahidnā, an taqụlụ yaumal-qiyāmati innā kunnā 'an hāżā gāfilīn","at":"Dan (ingatlah) ketika Tuhanmu mengeluarkan dari sulbi (tulang belakang) anak cucu Adam keturunan mereka dan Allah mengambil kesaksian terhadap roh mereka (seraya berfirman), “Bukankah Aku ini Tuhanmu?” Mereka menjawab, “Betul (Engkau Tuhan kami), kami bersaksi.” (Kami lakukan yang demikian itu) agar di hari Kiamat kamu tidak mengatakan, “Sesungguhnya ketika itu kami lengah terhadap ini.”"},{"id":"173","ar":"اَوْ تَقُوْلُوْٓا اِنَّمَآ اَشْرَكَ اٰبَاۤؤُنَا مِنْ قَبْلُ وَكُنَّا ذُرِّيَّةً مِّنْۢ بَعْدِهِمْۚ اَفَتُهْلِكُنَا بِمَا فَعَلَ الْمُبْطِلُوْنَ","bc":"au taqụlū innamā asyraka ābā`unā ming qablu wa kunnā żurriyyatam mim ba'dihim, a fa tuhlikunā bimā fa'alal-mubṭilụn","at":"Atau agar kamu tidak mengatakan, “Sesungguhnya nenek moyang kami telah mempersekutukan Tuhan sejak dahulu, sedang kami adalah keturunan yang (datang) setelah mereka. Maka apakah Engkau akan membinasakan kami karena perbuatan orang-orang (dahulu) yang sesat?”"},{"id":"174","ar":"وَكَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ وَلَعَلَّهُمْ يَرْجِعُوْنَ","bc":"wa każālika nufaṣṣilul-āyāti wa la'allahum yarji'ụn","at":"Dan demikianlah Kami menjelaskan ayat-ayat itu, agar mereka kembali (kepada kebenaran)."},{"id":"175","ar":"وَاتْلُ عَلَيْهِمْ نَبَاَ الَّذِيْٓ اٰتَيْنٰهُ اٰيٰتِنَا فَانْسَلَخَ مِنْهَا فَاَتْبَعَهُ الشَّيْطٰنُ فَكَانَ مِنَ الْغٰوِيْنَ","bc":"watlu 'alaihim naba`allażī ātaināhu āyātinā fansalakha min-hā fa atba'ahusy-syaiṭānu fa kāna minal-gāwīn","at":"Dan bacakanlah (Muhammad) kepada mereka, berita orang yang telah Kami berikan ayat-ayat Kami kepadanya, kemudian dia melepaskan diri dari ayat-ayat itu, lalu dia diikuti oleh setan (sampai dia tergoda), maka jadilah dia termasuk orang yang sesat."},{"id":"176","ar":"وَلَوْ شِئْنَا لَرَفَعْنٰهُ بِهَا وَلٰكِنَّهٗٓ اَخْلَدَ اِلَى الْاَرْضِ وَاتَّبَعَ هَوٰىهُۚ فَمَثَلُهٗ كَمَثَلِ الْكَلْبِۚ اِنْ تَحْمِلْ عَلَيْهِ يَلْهَثْ اَوْ تَتْرُكْهُ يَلْهَثْۗ ذٰلِكَ مَثَلُ الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۚ فَاقْصُصِ الْقَصَصَ لَعَلَّهُمْ يَتَفَكَّرُوْنَ","bc":"walau syi`nā larafa'nāhu bihā wa lākinnahū akhlada ilal-arḍi wattaba'a hawāh, fa maṡaluhụ kamaṡalil-kalb, in taḥmil 'alaihi yal-haṡ au tatruk-hu yal-haṡ, żālika maṡalul-qaumillażīna każżabụ bi`āyātinā, faqṣuṣil-qaṣaṣa la'allahum yatafakkarụn","at":"Dan sekiranya Kami menghendaki niscaya Kami tinggikan (derajat)nya dengan (ayat-ayat) itu, tetapi dia cenderung kepada dunia dan mengikuti keinginannya (yang rendah), maka perumpamaannya seperti anjing, jika kamu menghalaunya dijulurkan lidahnya dan jika kamu membiarkannya dia menjulurkan lidahnya (juga). Demikianlah perumpamaan orang-orang yang mendustakan ayat-ayat Kami. Maka ceritakanlah kisah-kisah itu agar mereka berpikir."},{"id":"177","ar":"سَاۤءَ مَثَلًا ۨالْقَوْمُ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا وَاَنْفُسَهُمْ كَانُوْا يَظْلِمُوْنَ","bc":"sā`a maṡalanil-qaumullażīna każżabụ bi`āyātinā wa anfusahum kānụ yaẓlimụn","at":"Sangat buruk perumpamaan orang-orang yang mendustakan ayat-ayat Kami; mereka menzalimi diri sendiri."},{"id":"178","ar":"مَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِيْۚ وَمَنْ يُّضْلِلْ فَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ","bc":"may yahdillāhu fa huwal-muhtadī, wa may yuḍlil fa ulā`ika humul-khāsirụn","at":"Barangsiapa diberi petunjuk oleh Allah, maka dialah yang mendapat petunjuk; dan barangsiapa disesatkan Allah, maka merekalah orang-orang yang rugi."},{"id":"179","ar":"وَلَقَدْ ذَرَأْنَا لِجَهَنَّمَ كَثِيْرًا مِّنَ الْجِنِّ وَالْاِنْسِۖ لَهُمْ قُلُوْبٌ لَّا يَفْقَهُوْنَ بِهَاۖ وَلَهُمْ اَعْيُنٌ لَّا يُبْصِرُوْنَ بِهَاۖ وَلَهُمْ اٰذَانٌ لَّا يَسْمَعُوْنَ بِهَاۗ اُولٰۤىِٕكَ كَالْاَنْعَامِ بَلْ هُمْ اَضَلُّ ۗ اُولٰۤىِٕكَ هُمُ الْغٰفِلُوْنَ","bc":"wa laqad żara`nā lijahannama kaṡīram minal-jinni wal-insi lahum qulụbul lā yafqahụna bihā wa lahum a'yunul lā yubṣirụna bihā wa lahum āżānul lā yasma'ụna bihā, ulā`ika kal-an'āmi bal hum aḍall, ulā`ika humul-gāfilụn","at":"Dan sungguh, akan Kami isi neraka Jahanam banyak dari kalangan jin dan manusia. Mereka memiliki hati, tetapi tidak dipergunakannya untuk memahami (ayat-ayat Allah) dan mereka memiliki mata (tetapi) tidak dipergunakannya untuk melihat (tanda-tanda kekuasaan Allah), dan mereka mempunyai telinga (tetapi) tidak dipergunakannya untuk mendengarkan (ayat-ayat Allah). Mereka seperti hewan ternak, bahkan lebih sesat lagi. Mereka itulah orang-orang yang lengah."},{"id":"180","ar":"وَلِلّٰهِ الْاَسْمَاۤءُ الْحُسْنٰى فَادْعُوْهُ بِهَاۖ وَذَرُوا الَّذِيْنَ يُلْحِدُوْنَ فِيْٓ اَسْمَاۤىِٕهٖۗ سَيُجْزَوْنَ مَا كَانُوْا يَعْمَلُوْنَ ۖ","bc":"wa lillāhil-asmā`ul-ḥusnā fad'ụhu bihā wa żarullażīna yul-ḥidụna fī asmā`ih, sayujzauna mā kānụ ya'malụn","at":"Dan Allah memiliki Asma'ul-husna (nama-nama yang terbaik), maka bermohonlah kepada-Nya dengan menyebutnya Asma'ul-husna itu dan tinggalkanlah orang-orang yang menyalahartikan nama-nama-Nya. Mereka kelak akan mendapat balasan terhadap apa yang telah mereka kerjakan."},{"id":"181","ar":"وَمِمَّنْ خَلَقْنَآ اُمَّةٌ يَّهْدُوْنَ بِالْحَقِّ وَبِهٖ يَعْدِلُوْنَ ","bc":"wa mim man khalaqnā ummatuy yahdụna bil-ḥaqqi wa bihī ya'dilụn","at":"Dan di antara orang-orang yang telah Kami ciptakan ada umat yang memberi petunjuk dengan (dasar) kebenaran, dan dengan itu (pula) mereka berlaku adil."},{"id":"182","ar":"وَالَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَا سَنَسْتَدْرِجُهُمْ مِّنْ حَيْثُ لَا يَعْلَمُوْنَ","bc":"wallażīna każżabụ bi`āyātinā sanastadrijuhum min ḥaiṡu lā ya'lamụn","at":"Dan orang-orang yang mendustakan ayat-ayat Kami, akan Kami biarkan mereka berangsur-angsur (ke arah kebinasaan), dengan cara yang tidak mereka ketahui."},{"id":"183","ar":"وَاُمْلِيْ لَهُمْ ۗاِنَّ كَيْدِيْ مَتِيْنٌ","bc":"wa umlī lahum, inna kaidī matīn","at":"Dan Aku akan memberikan tenggang waktu kepada mereka. Sungguh, rencana-Ku sangat teguh."},{"id":"184","ar":"اَوَلَمْ يَتَفَكَّرُوْا مَا بِصَاحِبِهِمْ مِّنْ جِنَّةٍۗ اِنْ هُوَ اِلَّا نَذِيْرٌ مُّبِيْنٌ","bc":"a wa lam yatafakkarụ mā biṣāḥibihim min jinnah, in huwa illā nażīrum mubīn","at":"Dan apakah mereka tidak merenungkan bahwa teman mereka (Muhammad) tidak gila. Dia (Muhammad) tidak lain hanyalah seorang pemberi peringatan yang jelas."},{"id":"185","ar":"اَوَلَمْ يَنْظُرُوْا فِيْ مَلَكُوْتِ السَّمٰوٰتِ وَالْاَرْضِ وَمَا خَلَقَ اللّٰهُ مِنْ شَيْءٍ وَّاَنْ عَسٰٓى اَنْ يَّكُوْنَ قَدِ اقْتَرَبَ اَجَلُهُمْۖ فَبِاَيِّ حَدِيْثٍۢ بَعْدَهٗ يُؤْمِنُوْنَ","bc":"a wa lam yanẓurụ fī malakụtis-samāwāti wal-arḍi wa mā khalaqallāhu min syai`iw wa an 'asā ay yakụna qadiqtaraba ajaluhum fa bi`ayyi ḥadīṡim ba'dahụ yu`minụn","at":"Dan apakah mereka tidak memperhatikan kerajaan langit dan bumi dan segala apa yang diciptakan Allah, dan kemungkinan telah dekatnya waktu (kebinasaan) mereka? Lalu berita mana lagi setelah ini yang akan mereka percayai?"},{"id":"186","ar":"مَنْ يُّضْلِلِ اللّٰهُ فَلَا هَادِيَ لَهٗ ۖوَيَذَرُهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ","bc":"may yuḍlilillāhu fa lā hādiya lahụ wa yażaruhum fī ṭugyānihim ya'mahụn","at":"Barangsiapa dibiarkan sesat oleh Allah, maka tidak ada yang mampu memberi petunjuk. Allah membiarkannya terombang-ambing dalam kesesatan."},{"id":"187","ar":"يَسْـَٔلُوْنَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰىهَاۗ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ رَبِّيْۚ لَا يُجَلِّيْهَا لِوَقْتِهَآ اِلَّا هُوَۘ ثَقُلَتْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ لَا تَأْتِيْكُمْ اِلَّا بَغْتَةً ۗيَسْـَٔلُوْنَكَ كَاَنَّكَ حَفِيٌّ عَنْهَاۗ قُلْ اِنَّمَا عِلْمُهَا عِنْدَ اللّٰهِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ","bc":"yas`alụnaka 'anis-sā'ati ayyāna mursāhā, qul innamā 'ilmuhā 'inda rabbī, lā yujallīhā liwaqtihā illā huw, ṡaqulat fis-samāwāti wal-arḍ, lā ta`tīkum illā bagtah, yas`alụnaka ka`annaka ḥafiyyun 'an-hā, qul innamā 'ilmuhā 'indallāhi wa lākinna akṡaran-nāsi lā ya'lamụn","at":"Mereka menanyakan kepadamu (Muhammad) tentang Kiamat, “Kapan terjadi?” Katakanlah, “Sesungguhnya pengetahuan tentang Kiamat itu ada pada Tuhanku; tidak ada (seorang pun) yang dapat menjelaskan waktu terjadinya selain Dia. (Kiamat) itu sangat berat (huru-haranya bagi makhluk) yang di langit dan di bumi, tidak akan datang kepadamu kecuali secara tiba-tiba.” Mereka bertanya kepadamu seakan-akan engkau mengetahuinya. Katakanlah (Muhammad), “Sesungguhnya pengetahuan tentang (hari Kiamat) ada pada Allah, tetapi kebanyakan manusia tidak mengetahui.”"},{"id":"188","ar":"قُلْ لَّآ اَمْلِكُ لِنَفْسِيْ نَفْعًا وَّلَا ضَرًّا اِلَّا مَا شَاۤءَ اللّٰهُ ۗوَلَوْ كُنْتُ اَعْلَمُ الْغَيْبَ لَاسْتَكْثَرْتُ مِنَ الْخَيْرِۛ وَمَا مَسَّنِيَ السُّوْۤءُ ۛاِنْ اَنَا۠ اِلَّا نَذِيْرٌ وَّبَشِيْرٌ لِّقَوْمٍ يُّؤْمِنُوْنَ ","bc":"qul lā amliku linafsī naf'aw wa lā ḍarran illā mā syā`allāh, walau kuntu a'lamul-gaiba lastakṡartu minal-khaīr, wa mā massaniyas-sū`u in ana illā nażīruw wa basyīrul liqaumiy yu`minụn","at":"Katakanlah (Muhammad), “Aku tidak kuasa mendatangkan manfaat maupun menolak mudarat bagi diriku kecuali apa yang dikehendaki Allah. Sekiranya aku mengetahui yang gaib, niscaya aku membuat kebajikan sebanyak-banyaknya dan tidak akan ditimpa bahaya. Aku hanyalah pemberi peringatan, dan pembawa berita gembira bagi orang-orang yang beriman.”"},{"id":"189","ar":"۞ هُوَ الَّذِيْ خَلَقَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ وَّجَعَلَ مِنْهَا زَوْجَهَا لِيَسْكُنَ اِلَيْهَاۚ فَلَمَّا تَغَشّٰىهَا حَمَلَتْ حَمْلًا خَفِيْفًا فَمَرَّتْ بِهٖ ۚفَلَمَّآ اَثْقَلَتْ دَّعَوَا اللّٰهَ رَبَّهُمَا لَىِٕنْ اٰتَيْتَنَا صَالِحًا لَّنَكُوْنَنَّ مِنَ الشّٰكِرِيْنَ","bc":"huwallażī khalaqakum min nafsiw wāḥidatiw wa ja'ala min-hā zaujahā liyaskuna ilaihā, fa lammā tagasysyāhā ḥamalat ḥamlan khafīfan fa marrat bih, fa lammā aṡqalad da'awallāha rabbahumā la`in ātaitanā ṣāliḥal lanakụnanna minasy-syākirīn","at":"Dialah yang menciptakan kamu dari jiwa yang satu (Adam) dan daripadanya Dia menciptakan pasangannya, agar dia merasa senang kepadanya. Maka setelah dicampurinya, (istrinya) mengandung kandungan yang ringan, dan teruslah dia merasa ringan (beberapa waktu). Kemudian ketika dia merasa berat, keduanya (suami istri) bermohon kepada Allah, Tuhan Mereka (seraya berkata), “Jika Engkau memberi kami anak yang saleh, tentulah kami akan selalu bersyukur.”"},{"id":"190","ar":"فَلَمَّآ اٰتٰىهُمَا صَالِحًا جَعَلَا لَهٗ شُرَكَاۤءَ فِيْمَآ اٰتٰىهُمَا ۚفَتَعٰلَى اللّٰهُ عَمَّا يُشْرِكُوْنَ","bc":"fa lammā ātāhumā ṣāliḥan ja'alā lahụ syurakā`a fīmā ātāhumā, fa ta'ālallāhu 'ammā yusyrikụn","at":"Maka setelah Dia memberi keduanya seorang anak yang saleh, mereka menjadikan sekutu bagi Allah terhadap anak yang telah dianugerahkan-Nya itu. Maka Mahatinggi Allah dari apa yang mereka persekutukan."},{"id":"191","ar":"اَيُشْرِكُوْنَ مَا لَا يَخْلُقُ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَۖ ","bc":"a yusyrikụna mā lā yakhluqu syai`aw wa hum yukhlaqụn","at":"Mengapa mereka mempersekutukan (Allah dengan) sesuatu (berhala) yang tidak dapat menciptakan sesuatu apa pun? Padahal (berhala) itu sendiri diciptakan."},{"id":"192","ar":"وَلَا يَسْتَطِيْعُوْنَ لَهُمْ نَصْرًا وَّلَآ اَنْفُسَهُمْ يَنْصُرُوْنَ","bc":"wa lā yastaṭī'ụna lahum naṣraw wa lā anfusahum yanṣurụn","at":"Dan (berhala) itu tidak dapat memberikan pertolongan kepada penyembahnya, dan kepada dirinya sendiri pun mereka tidak dapat memberi pertolongan."},{"id":"193","ar":"وَاِنْ تَدْعُوْهُمْ اِلَى الْهُدٰى لَا يَتَّبِعُوْكُمْۗ سَوَۤاءٌ عَلَيْكُمْ اَدَعَوْتُمُوْهُمْ اَمْ اَنْتُمْ صَامِتُوْنَ","bc":"wa in tad'ụhum ilal-hudā lā yattabi'ụkum, sawā`un 'alaikum a da'autumụhum am antum ṣāmitụn","at":"Dan jika kamu (wahai orang-orang musyrik) menyerunya (berhala-berhala) untuk memberi petunjuk kepadamu, tidaklah berhala-berhala itu dapat memperkenankan seruanmu; sama saja (hasilnya) buat kamu menyeru mereka atau berdiam diri."},{"id":"194","ar":"اِنَّ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ عِبَادٌ اَمْثَالُكُمْ فَادْعُوْهُمْ فَلْيَسْتَجِيْبُوْا لَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ","bc":"innallażīna tad'ụna min dụnillāhi 'ibādun amṡālukum fad'ụhum falyastajībụ lakum ing kuntum ṣādiqīn","at":"Sesungguhnya mereka (berhala-berhala) yang kamu seru selain Allah adalah makhluk (yang lemah) yang serupa juga dengan kamu. Maka serulah mereka lalu biarkanlah mereka memperkenankan permintaanmu, jika kamu orang yang benar."},{"id":"195","ar":"اَلَهُمْ اَرْجُلٌ يَّمْشُوْنَ بِهَآ ۖ اَمْ لَهُمْ اَيْدٍ يَّبْطِشُوْنَ بِهَآ ۖ اَمْ لَهُمْ اَعْيُنٌ يُّبْصِرُوْنَ بِهَآ ۖ اَمْ لَهُمْ اٰذَانٌ يَّسْمَعُوْنَ بِهَاۗ قُلِ ادْعُوْا شُرَكَاۤءَكُمْ ثُمَّ كِيْدُوْنِ فَلَا تُنْظِرُوْنِ ","bc":"a lahum arjuluy yamsyụna bihā am lahum aidiy yabṭisyụna bihā am lahum a'yunuy yubṣirụna bihā am lahum āżānuy yasma'ụna bihā, qulid'ụ syurakā`akum ṡumma kīdụni fa lā tunẓirụn","at":"Apakah mereka (berhala-berhala) mempunyai kaki untuk berjalan, atau mempunyai tangan untuk memegang dengan keras, atau mempunyai mata untuk melihat, atau mempunyai telinga untuk mendengar? Katakanlah (Muhammad), “Panggillah (berhala-berhalamu) yang kamu anggap sekutu Allah, kemudian lakukanlah tipu daya (untuk mencelakakan)ku, dan jangan kamu tunda lagi."},{"id":"196","ar":"اِنَّ وَلِيِّ َۧ اللّٰهُ الَّذِيْ نَزَّلَ الْكِتٰبَۖ وَهُوَ يَتَوَلَّى الصّٰلِحِيْنَ","bc":"inna waliyyiyallāhullażī nazzalal-kitāba wa huwa yatawallaṣ-ṣāliḥīn","at":"Sesungguhnya pelindungku adalah Allah yang telah menurunkan Kitab (Al-Qur'an). Dia melindungi orang-orang saleh."},{"id":"197","ar":"وَالَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِهٖ لَا يَسْتَطِيْعُوْنَ نَصْرَكُمْ وَلَآ اَنْفُسَهُمْ يَنْصُرُوْنَ","bc":"wallażīna tad'ụna min dụnihī lā yastaṭī'ụna naṣrakum wa lā anfusahum yanṣurụn","at":"Dan berhala-berhala yang kamu seru selain Allah tidaklah sanggup menolongmu, bahkan tidak dapat menolong dirinya sendiri.”"},{"id":"198","ar":"وَاِنْ تَدْعُوْهُمْ اِلَى الْهُدٰى لَا يَسْمَعُوْاۗ وَتَرٰىهُمْ يَنْظُرُوْنَ اِلَيْكَ وَهُمْ لَا يُبْصِرُوْنَ","bc":"wa in tad'ụhum ilal-hudā lā yasma'ụ, wa tarāhum yanẓurụna ilaika wa hum lā yubṣirụn","at":"Dan jika kamu menyeru mereka (berhala-berhala) untuk memberi petunjuk, mereka tidak dapat mendengarnya. Dan kamu lihat mereka memandangmu padahal mereka tidak melihat."},{"id":"199","ar":"خُذِ الْعَفْوَ وَأْمُرْ بِالْعُرْفِ وَاَعْرِضْ عَنِ الْجَاهِلِيْنَ","bc":"khużil-'afwa wa`mur bil-'urfi wa a'riḍ 'anil-jāhilīn","at":"Jadilah pemaaf dan suruhlah orang mengerjakan yang makruf, serta jangan pedulikan orang-orang yang bodoh."},{"id":"200","ar":"وَاِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطٰنِ نَزْغٌ فَاسْتَعِذْ بِاللّٰهِ ۗاِنَّهٗ سَمِيْعٌ عَلِيْمٌ","bc":"wa immā yanzagannaka minasy-syaiṭāni nazgun fasta'iż billāh, innahụ samī'un 'alīm","at":"Dan jika setan datang menggodamu, maka berlindunglah kepada Allah. Sungguh, Dia Maha Mendengar, Maha Mengetahui."},{"id":"201","ar":"اِنَّ الَّذِيْنَ اتَّقَوْا اِذَا مَسَّهُمْ طٰۤىِٕفٌ مِّنَ الشَّيْطٰنِ تَذَكَّرُوْا فَاِذَا هُمْ مُّبْصِرُوْنَۚ ","bc":"innallażīnattaqau iżā massahum ṭā`ifum minasy-syaiṭāni tażakkarụ fa iżā hum mubṣirụn","at":"Sesungguhnya orang-orang yang bertakwa apabila mereka dibayang-bayangi pikiran jahat (berbuat dosa) dari setan, mereka pun segera ingat kepada Allah, maka ketika itu juga mereka melihat (kesalahan-kesalahannya)."},{"id":"202","ar":"وَاِخْوَانُهُمْ يَمُدُّوْنَهُمْ فِى الْغَيِّ ثُمَّ لَا يُقْصِرُوْنَ","bc":"wa ikhwānuhum yamuddụnahum fil-gayyi ṡumma lā yuqṣirụn","at":"Dan teman-teman mereka (orang kafir dan fasik) membantu setan-setan dalam menyesatkan dan mereka tidak henti-hentinya (menyesatkan)."},{"id":"203","ar":"وَاِذَا لَمْ تَأْتِهِمْ بِاٰيَةٍ قَالُوْا لَوْلَا اجْتَبَيْتَهَاۗ قُلْ اِنَّمَآ اَتَّبِعُ مَا يُوْحٰٓى اِلَيَّ مِنْ رَّبِّيْۗ هٰذَا بَصَاۤىِٕرُ مِنْ رَّبِّكُمْ وَهُدًى وَّرَحْمَةٌ لِّقَوْمٍ يُّؤْمِنُوْنَ","bc":"wa iżā lam ta`tihim bi`āyating qālụ lau lajtabaitahā, qul innamā attabi'u mā yụḥā ilayya mir rabbī, hāżā baṣā`iru mir rabbikum wa hudaw wa raḥmatul liqaumiy yu`minụn","at":"Dan apabila engkau (Muhammad) tidak membacakan suatu ayat kepada mereka, mereka berkata, “Mengapa tidak engkau buat sendiri ayat itu?” Katakanlah (Muhammad), “Sesungguhnya aku hanya mengikuti apa yang diwahyukan Tuhanku kepadaku. (Al-Qur'an) ini adalah bukti-bukti yang nyata dari Tuhanmu, petunjuk dan rahmat bagi orang-orang yang beriman.”"},{"id":"204","ar":"وَاِذَا قُرِئَ الْقُرْاٰنُ فَاسْتَمِعُوْا لَهٗ وَاَنْصِتُوْا لَعَلَّكُمْ تُرْحَمُوْنَ","bc":"wa iżā quri`al-qur`ānu fastami'ụ lahụ wa anṣitụ la'allakum tur-ḥamụn","at":"Dan apabila dibacakan Al-Qur'an, maka dengarkanlah dan diamlah, agar kamu mendapat rahmat."},{"id":"205","ar":"وَاذْكُرْ رَّبَّكَ فِيْ نَفْسِكَ تَضَرُّعًا وَّخِيْفَةً وَّدُوْنَ الْجَهْرِ مِنَ الْقَوْلِ بِالْغُدُوِّ وَالْاٰصَالِ وَلَا تَكُنْ مِّنَ الْغٰفِلِيْنَ","bc":"ważkur rabbaka fī nafsika taḍarru'aw wa khīfataw wa dụnal-jahri minal-qauli bil-guduwwi wal-āṣāli wa lā takum minal-gāfilīn","at":"Dan ingatlah Tuhanmu dalam hatimu dengan rendah hati dan rasa takut, dan dengan tidak mengeraskan suara, pada waktu pagi dan petang, dan janganlah kamu termasuk orang-orang yang lengah."},{"id":"206","ar":"اِنَّ الَّذِيْنَ عِنْدَ رَبِّكَ لَا يَسْتَكْبِرُوْنَ عَنْ عِبَادَتِهٖ وَيُسَبِّحُوْنَهٗ وَلَهٗ يَسْجُدُوْنَ ۩","bc":"innallażīna 'inda rabbika lā yastakbirụna 'an 'ibādatihī wa yusabbiḥụnahụ wa lahụ yasjudụn","at":"Sesungguhnya orang-orang yang ada di sisi Tuhanmu tidak merasa enggan untuk menyembah Allah dan mereka menyucikan-Nya dan hanya kepada-Nya mereka bersujud."}],al_asr=[{"id":"1","ar":"وَالْعَصْرِۙ","bc":"wal-'aṣr","at":"Demi masa,"},{"id":"2","ar":"اِنَّ الْاِنْسَانَ لَفِيْ خُسْرٍۙ","bc":"innal-insāna lafī khusr","at":"sungguh, manusia berada dalam kerugian,"},{"id":"3","ar":"اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَتَوَاصَوْا بِالْحَقِّ ەۙ وَتَوَاصَوْا بِالصَّبْرِ","bc":"illallażīna āmanụ wa 'amiluṣ-ṣāliḥāti wa tawāṣau bil-ḥaqqi wa tawāṣau biṣ-ṣabr","at":"kecuali orang-orang yang beriman dan mengerjakan kebajikan serta saling menasihati untuk kebenaran dan saling menasihati untuk kesabaran."}],al_balad=[{"id":"1","ar":"لَآ اُقْسِمُ بِهٰذَا الْبَلَدِۙ","bc":"lā uqsimu bihāżal-balad","at":"Aku bersumpah dengan negeri ini (Mekah),"},{"id":"2","ar":"وَاَنْتَ حِلٌّۢ بِهٰذَا الْبَلَدِۙ","bc":"wa anta ḥillum bihāżal-balad","at":"dan engkau (Muhammad), bertempat di negeri (Mekah) ini,"},{"id":"3","ar":"وَوَالِدٍ وَّمَا وَلَدَۙ","bc":"wa wālidiw wa mā walad","at":"dan demi (pertalian) bapak dan anaknya."},{"id":"4","ar":"لَقَدْ خَلَقْنَا الْاِنْسَانَ فِيْ كَبَدٍۗ","bc":"laqad khalaqnal-insāna fī kabad","at":"Sungguh, Kami telah menciptakan manusia berada dalam susah payah."},{"id":"5","ar":"اَيَحْسَبُ اَنْ لَّنْ يَّقْدِرَ عَلَيْهِ اَحَدٌ ۘ","bc":"a yaḥsabu al lay yaqdira 'alaihi aḥad","at":"Apakah dia (manusia) itu mengira bahwa tidak ada sesuatu pun yang berkuasa atasnya?"},{"id":"6","ar":"يَقُوْلُ اَهْلَكْتُ مَالًا لُّبَدًاۗ","bc":"yaqụlu ahlaktu mālal lubadā","at":"Dia mengatakan, “Aku telah menghabiskan harta yang banyak.” "},{"id":"7","ar":"اَيَحْسَبُ اَنْ لَّمْ يَرَهٗٓ اَحَدٌۗ","bc":"a yaḥsabu al lam yarahū aḥad","at":"Apakah dia mengira bahwa tidak ada sesuatu pun yang melihatnya?"},{"id":"8","ar":"اَلَمْ نَجْعَلْ لَّهٗ عَيْنَيْنِۙ","bc":"a lam naj'al lahụ 'ainaīn","at":"Bukankah Kami telah menjadikan untuknya sepasang mata,"},{"id":"9","ar":"وَلِسَانًا وَّشَفَتَيْنِۙ","bc":"wa lisānaw wa syafataīn","at":"dan lidah dan sepasang bibir?"},{"id":"10","ar":"وَهَدَيْنٰهُ النَّجْدَيْنِۙ","bc":"wa hadaināhun-najdaīn","at":"Dan Kami telah menunjukkan kepadanya dua jalan (kebajikan dan kejahatan),"},{"id":"11","ar":"فَلَا اقْتَحَمَ الْعَقَبَةَ ۖ","bc":"fa laqtaḥamal-'aqabah","at":"tetapi dia tidak menempuh jalan yang mendaki dan sukar?"},{"id":"12","ar":"وَمَآ اَدْرٰىكَ مَا الْعَقَبَةُ ۗ","bc":"wa mā adrāka mal-'aqabah","at":"Dan tahukah kamu apakah jalan yang mendaki dan sukar itu?"},{"id":"13","ar":"فَكُّ رَقَبَةٍۙ","bc":"fakku raqabah","at":"(yaitu) melepaskan perbudakan (hamba sahaya),"},{"id":"14","ar":"اَوْ اِطْعَامٌ فِيْ يَوْمٍ ذِيْ مَسْغَبَةٍۙ","bc":"au iṭ'āmun fī yaumin żī masgabah","at":"atau memberi makan pada hari terjadi kelaparan,"},{"id":"15","ar":"يَّتِيْمًا ذَا مَقْرَبَةٍۙ","bc":"yatīman żā maqrabah","at":"(kepada) anak yatim yang ada hubungan kerabat,"},{"id":"16","ar":"اَوْ مِسْكِيْنًا ذَا مَتْرَبَةٍۗ","bc":"au miskīnan żā matrabah","at":"atau orang miskin yang sangat fakir."},{"id":"17","ar":"ثُمَّ كَانَ مِنَ الَّذِيْنَ اٰمَنُوْا وَتَوَاصَوْا بِالصَّبْرِ وَتَوَاصَوْا بِالْمَرْحَمَةِۗ","bc":"ṡumma kāna minallażīna āmanụ wa tawāṣau biṣ-ṣabri wa tawāṣau bil-mar-ḥamah","at":"Kemudian dia termasuk orang-orang yang beriman dan saling berpesan untuk bersabar dan saling berpesan untuk berkasih sayang."},{"id":"18","ar":"اُولٰۤىِٕكَ اَصْحٰبُ الْمَيْمَنَةِۗ","bc":"ulā`ika aṣ-ḥābul-maimanah","at":"Mereka (orang-orang yang beriman dan saling berpesan itu) adalah golongan kanan."},{"id":"19","ar":"وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِنَا هُمْ اَصْحٰبُ الْمَشْئَمَةِۗ","bc":"wallażīna kafarụ bi`āyātinā hum aṣ-ḥābul-masy`amah","at":"Dan orang-orang yang kafir kepada ayat-ayat Kami, mereka itu adalah golongan kiri."},{"id":"20","ar":"عَلَيْهِمْ نَارٌ مُّؤْصَدَةٌ","bc":"'alaihim nārum mu`ṣadah","at":"Mereka berada dalam neraka yang ditutup rapat."}],al_baqarah=[{"id":"1","ar":"الۤمّۤ ۚ","bc":"alif lām mīm","at":"Alif Lam Mim."},{"id":"2","ar":"ذٰلِكَ الْكِتٰبُ لَا رَيْبَ ۛ فِيْهِ ۛ هُدًى لِّلْمُتَّقِيْنَۙ","bc":"żālikal-kitābu lā raiba fīh, hudal lil-muttaqīn","at":"Kitab (Al-Qur'an) ini tidak ada keraguan padanya; petunjuk bagi mereka yang bertakwa,"},{"id":"3","ar":"الَّذِيْنَ يُؤْمِنُوْنَ بِالْغَيْبِ وَيُقِيْمُوْنَ الصَّلٰوةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ۙ","bc":"allażīna yu`minụna bil-gaibi wa yuqīmụnaṣ-ṣalāta wa mimmā razaqnāhum yunfiqụn","at":"(yaitu) mereka yang beriman kepada yang gaib, melaksanakan salat, dan menginfakkan sebagian rezeki yang Kami berikan kepada mereka,"},{"id":"4","ar":"وَالَّذِيْنَ يُؤْمِنُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ ۚ وَبِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَۗ","bc":"wallażīna yu`minụna bimā unzila ilaika wa mā unzila ming qablik, wa bil-ākhirati hum yụqinụn","at":"dan mereka yang beriman kepada (Al-Qur'an) yang diturunkan kepadamu (Muhammad) dan (kitab-kitab) yang telah diturunkan sebelum engkau, dan mereka yakin akan adanya akhirat."},{"id":"5","ar":"اُولٰۤىِٕكَ عَلٰى هُدًى مِّنْ رَّبِّهِمْ ۙ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ","bc":"ulā`ika 'alā hudam mir rabbihim wa ulā`ika humul-mufliḥụn","at":"Merekalah yang mendapat petunjuk dari Tuhannya, dan mereka itulah orang-orang yang beruntung."},{"id":"6","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا سَوَاۤءٌ عَلَيْهِمْ ءَاَنْذَرْتَهُمْ اَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُوْنَ","bc":"innallażīna kafarụ sawā`un 'alaihim a anżartahum am lam tunżir-hum lā yu`minụn","at":"Sesungguhnya orang-orang kafir, sama saja bagi mereka, engkau (Muhammad) beri peringatan atau tidak engkau beri peringatan, mereka tidak akan beriman."},{"id":"7","ar":"خَتَمَ اللّٰهُ عَلٰى قُلُوْبِهِمْ وَعَلٰى سَمْعِهِمْ ۗ وَعَلٰٓى اَبْصَارِهِمْ غِشَاوَةٌ وَّلَهُمْ عَذَابٌ عَظِيْمٌ ","bc":"khatamallāhu 'alā qulụbihim wa 'alā sam'ihim, wa 'alā abṣārihim gisyāwatuw wa lahum 'ażābun 'aẓīm","at":"Allah telah mengunci hati dan pendengaran mereka, penglihatan mereka telah tertutup, dan mereka akan mendapat azab yang berat."},{"id":"8","ar":"وَمِنَ النَّاسِ مَنْ يَّقُوْلُ اٰمَنَّا بِاللّٰهِ وَبِالْيَوْمِ الْاٰخِرِ وَمَا هُمْ بِمُؤْمِنِيْنَۘ","bc":"wa minan-nāsi may yaqụlu āmannā billāhi wa bil-yaumil-ākhiri wa mā hum bimu`minīn","at":"Dan di antara manusia ada yang berkata, “Kami beriman kepada Allah dan hari akhir,” padahal sesungguhnya mereka itu bukanlah orang-orang yang beriman."},{"id":"9","ar":"يُخٰدِعُوْنَ اللّٰهَ وَالَّذِيْنَ اٰمَنُوْا ۚ وَمَا يَخْدَعُوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَۗ","bc":"yukhādi'ụnallāha wallażīna āmanụ, wa mā yakhda'ụna illā anfusahum wa mā yasy'urụn","at":"Mereka menipu Allah dan orang-orang yang beriman, padahal mereka hanyalah menipu diri sendiri tanpa mereka sadari."},{"id":"10","ar":"فِيْ قُلُوْبِهِمْ مَّرَضٌۙ فَزَادَهُمُ اللّٰهُ مَرَضًاۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ ۢ ەۙ بِمَا كَانُوْا يَكْذِبُوْنَ","bc":"fī qulụbihim maraḍun fa zādahumullāhu maraḍā, wa lahum 'ażābun alīmum bimā kānụ yakżibụn","at":"Dalam hati mereka ada penyakit, lalu Allah menambah penyakitnya itu; dan mereka mendapat azab yang pedih, karena mereka berdusta."},{"id":"11","ar":"وَاِذَا قِيْلَ لَهُمْ لَا تُفْسِدُوْا فِى الْاَرْضِۙ قَالُوْٓا اِنَّمَا نَحْنُ مُصْلِحُوْنَ","bc":"wa iżā qīla lahum lā tufsidụ fil arḍi qālū innamā naḥnu muṣliḥụn","at":"Dan apabila dikatakan kepada mereka, “Janganlah berbuat kerusakan di bumi!” Mereka menjawab, “Sesungguhnya kami justru orang-orang yang melakukan perbaikan.”"},{"id":"12","ar":"اَلَآ اِنَّهُمْ هُمُ الْمُفْسِدُوْنَ وَلٰكِنْ لَّا يَشْعُرُوْنَ","bc":"alā innahum humul-mufsidụna wa lākil lā yasy'urụn","at":"Ingatlah, sesungguhnya merekalah yang berbuat kerusakan, tetapi mereka tidak menyadari."},{"id":"13","ar":"وَاِذَا قِيْلَ لَهُمْ اٰمِنُوْا كَمَآ اٰمَنَ النَّاسُ قَالُوْٓا اَنُؤْمِنُ كَمَآ اٰمَنَ السُّفَهَاۤءُ ۗ اَلَآ اِنَّهُمْ هُمُ السُّفَهَاۤءُ وَلٰكِنْ لَّا يَعْلَمُوْنَ ","bc":"wa iżā qīla lahum āminụ kamā āmanan-nāsu qālū a nu`minu kamā āmanas-sufahā`, alā innahum humus-sufahā`u wa lākil lā ya'lamụn","at":"Dan apabila dikatakan kepada mereka, “Berimanlah kamu sebagaimana orang lain telah beriman!” Mereka menjawab, “Apakah kami akan beriman seperti orang-orang yang kurang akal itu beriman?” Ingatlah, sesungguhnya mereka itulah orang-orang yang kurang akal, tetapi mereka tidak tahu."},{"id":"14","ar":"وَاِذَا لَقُوا الَّذِيْنَ اٰمَنُوْا قَالُوْٓا اٰمَنَّا ۚ وَاِذَا خَلَوْا اِلٰى شَيٰطِيْنِهِمْ ۙ قَالُوْٓا اِنَّا مَعَكُمْ ۙاِنَّمَا نَحْنُ مُسْتَهْزِءُوْنَ","bc":"wa iżā laqullażīna āmanụ qālū āmannā, wa iżā khalau ilā syayāṭīnihim qālū innā ma'akum innamā naḥnu mustahzi`ụn","at":"Dan apabila mereka berjumpa dengan orang yang beriman, mereka berkata, “Kami telah beriman.” Tetapi apabila mereka kembali kepada setan-setan (para pemimpin) mereka, mereka berkata, “Sesungguhnya kami bersama kamu, kami hanya berolok-olok.”"},{"id":"15","ar":" اَللّٰهُ يَسْتَهْزِئُ بِهِمْ وَيَمُدُّهُمْ فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ ","bc":"allāhu yastahzi`u bihim wa yamudduhum fī ṭugyānihim ya'mahụn","at":"Allah akan memperolok-olokkan mereka dan membiarkan mereka terombang-ambing dalam kesesatan."},{"id":"16","ar":"اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الضَّلٰلَةَ بِالْهُدٰىۖ فَمَا رَبِحَتْ تِّجَارَتُهُمْ وَمَا كَانُوْا مُهْتَدِيْنَ","bc":"ulā`ikallażīnasytarawuḍ-ḍalālata bil-hudā fa mā rabiḥat tijāratuhum wa mā kānụ muhtadīn","at":"Mereka itulah yang membeli kesesatan dengan petunjuk. Maka perdagangan mereka itu tidak beruntung dan mereka tidak mendapat petunjuk."},{"id":"17","ar":"مَثَلُهُمْ كَمَثَلِ الَّذِى اسْتَوْقَدَ نَارًا ۚ فَلَمَّآ اَضَاۤءَتْ مَا حَوْلَهٗ ذَهَبَ اللّٰهُ بِنُوْرِهِمْ وَتَرَكَهُمْ فِيْ ظُلُمٰتٍ لَّا يُبْصِرُوْنَ","bc":"maṡaluhum kamaṡalillażistauqada nārā, fa lammā aḍā`at mā ḥaulahụ żahaballāhu binụrihim wa tarakahum fī ẓulumātil lā yubṣirụn","at":"Perumpamaan mereka seperti orang-orang yang menyalakan api, setelah menerangi sekelilingnya, Allah melenyapkan cahaya (yang menyinari) mereka dan membiarkan mereka dalam kegelapan, tidak dapat melihat."},{"id":"18","ar":"صُمٌّ ۢ بُكْمٌ عُمْيٌ فَهُمْ لَا يَرْجِعُوْنَۙ","bc":"ṣummum bukmun 'umyun fa hum lā yarji'ụn","at":"Mereka tuli, bisu dan buta, sehingga mereka tidak dapat kembali."},{"id":"19","ar":"اَوْ كَصَيِّبٍ مِّنَ السَّمَاۤءِ فِيْهِ ظُلُمٰتٌ وَّرَعْدٌ وَّبَرْقٌۚ يَجْعَلُوْنَ اَصَابِعَهُمْ فِيْٓ اٰذَانِهِمْ مِّنَ الصَّوَاعِقِ حَذَرَ الْمَوْتِۗ وَاللّٰهُ مُحِيْطٌۢ بِالْكٰفِرِيْنَ","bc":"au kaṣayyibim minas-samā`i fīhi ẓulumātuw wa ra'duw wa barq, yaj'alụna aṣābi'ahum fī āżānihim minaṣ-ṣawā'iqi ḥażaral-maụt, wallāhu muḥīṭum bil-kāfirīn","at":"Atau seperti (orang yang ditimpa) hujan lebat dari langit, yang disertai kegelapan, petir dan kilat. Mereka menyumbat telinga dengan jari-jarinya, (menghindari) suara petir itu karena takut mati. Allah meliputi orang-orang yang kafir."},{"id":"20","ar":"يَكَادُ الْبَرْقُ يَخْطَفُ اَبْصَارَهُمْ ۗ كُلَّمَآ اَضَاۤءَ لَهُمْ مَّشَوْا فِيْهِ ۙ وَاِذَآ اَظْلَمَ عَلَيْهِمْ قَامُوْا ۗوَلَوْ شَاۤءَ اللّٰهُ لَذَهَبَ بِسَمْعِهِمْ وَاَبْصَارِهِمْ ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ","bc":"yakādul-barqu yakhṭafu abṣārahum, kullamā aḍā`a lahum masyau fīhi wa iżā aẓlama 'alaihim qāmụ, walau syā`allāhu lażahaba bisam'ihim wa abṣārihim, innallāha 'alā kulli syai`ing qadīr","at":"Hampir saja kilat itu menyambar penglihatan mereka. Setiap kali (kilat itu) menyinari, mereka berjalan di bawah (sinar) itu, dan apabila gelap menerpa mereka, mereka berhenti. Sekiranya Allah menghendaki, niscaya Dia hilangkan pendengaran dan penglihatan mereka. Sungguh, Allah Mahakuasa atas segala sesuatu."},{"id":"21","ar":" يٰٓاَيُّهَا النَّاسُ اعْبُدُوْا رَبَّكُمُ الَّذِيْ خَلَقَكُمْ وَالَّذِيْنَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُوْنَۙ","bc":"yā ayyuhan-nāsu'budụ rabbakumullażī khalaqakum wallażīna ming qablikum la'allakum tattaqụn","at":"Wahai manusia! Sembahlah Tuhanmu yang telah menciptakan kamu dan orang-orang yang sebelum kamu, agar kamu bertakwa."},{"id":"22","ar":" الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ فِرَاشًا وَّالسَّمَاۤءَ بِنَاۤءً ۖوَّاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَخْرَجَ بِهٖ مِنَ الثَّمَرٰتِ رِزْقًا لَّكُمْ ۚ فَلَا تَجْعَلُوْا لِلّٰهِ اَنْدَادًا وَّاَنْتُمْ تَعْلَمُوْنَ ","bc":"allażī ja'ala lakumul-arḍa firāsyaw was-samā`a binā`aw wa anzala minas-samā`i mā`an fa akhraja bihī minaṡ-ṡamarāti rizqal lakum, fa lā taj'alụ lillāhi andādaw wa antum ta'lamụn","at":"(Dialah) yang menjadikan bumi sebagai hamparan bagimu dan langit sebagai atap, dan Dialah yang menurunkan air (hujan) dari langit, lalu Dia hasilkan dengan (hujan) itu buah-buahan sebagai rezeki untukmu. Karena itu janganlah kamu mengadakan tandingan-tandingan bagi Allah, padahal kamu mengetahui."},{"id":"23","ar":"وَاِنْ كُنْتُمْ فِيْ رَيْبٍ مِّمَّا نَزَّلْنَا عَلٰى عَبْدِنَا فَأْتُوْا بِسُوْرَةٍ مِّنْ مِّثْلِهٖ ۖ وَادْعُوْا شُهَدَاۤءَكُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"wa ing kuntum fī raibim mimmā nazzalnā 'alā 'abdinā fa`tụ bisụratim mim miṡlihī wad'ụ syuhadā`akum min dụnillāhi ing kuntum ṣādiqīn","at":"Dan jika kamu meragukan (Al-Qur'an) yang Kami turunkan kepada hamba Kami (Muhammad), maka buatlah satu surah semisal dengannya dan ajaklah penolong-penolongmu selain Allah, jika kamu orang-orang yang benar."},{"id":"24","ar":"فَاِنْ لَّمْ تَفْعَلُوْا وَلَنْ تَفْعَلُوْا فَاتَّقُوا النَّارَ الَّتِيْ وَقُوْدُهَا النَّاسُ وَالْحِجَارَةُ ۖ اُعِدَّتْ لِلْكٰفِرِيْنَ","bc":"fa il lam taf'alụ wa lan taf'alụ fattaqun-nārallatī waqụduhan-nāsu wal-ḥijāratu u'iddat lil-kāfirīn","at":"Jika kamu tidak mampu membuatnya, dan (pasti) tidak akan mampu, maka takutlah kamu akan api neraka yang bahan bakarnya manusia dan batu, yang disediakan bagi orang-orang kafir."},{"id":"25","ar":"وَبَشِّرِ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اَنَّ لَهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ۗ كُلَّمَا رُزِقُوْا مِنْهَا مِنْ ثَمَرَةٍ رِّزْقًا ۙ قَالُوْا هٰذَا الَّذِيْ رُزِقْنَا مِنْ قَبْلُ وَاُتُوْا بِهٖ مُتَشَابِهًا ۗوَلَهُمْ فِيْهَآ اَزْوَاجٌ مُّطَهَّرَةٌ وَّهُمْ فِيْهَا خٰلِدُوْنَ","bc":"wa basysyirillażīna āmanụ wa 'amiluṣ-ṣāliḥāti anna lahum jannātin tajrī min taḥtihal-an-hār, kullamā ruziqụ min-hā min ṡamaratir rizqang qālụ hāżallażī ruziqnā ming qablu wa utụ bihī mutasyābihā, wa lahum fīhā azwājum muṭahharatuw wa hum fīhā khālidụn","at":"Dan sampaikanlah kabar gembira kepada orang-orang yang beriman dan berbuat kebajikan, bahwa untuk mereka (disediakan) surga-surga yang mengalir di bawahnya sungai-sungai. Setiap kali mereka diberi rezeki buah-buahan dari surga, mereka berkata, “Inilah rezeki yang diberikan kepada kami dahulu.” Mereka telah diberi (buah-buahan) yang serupa. Dan di sana mereka (memperoleh) pasangan-pasangan yang suci. Mereka kekal di dalamnya."},{"id":"26","ar":"۞ اِنَّ اللّٰهَ لَا يَسْتَحْيٖٓ اَنْ يَّضْرِبَ مَثَلًا مَّا بَعُوْضَةً فَمَا فَوْقَهَا ۗ فَاَمَّا الَّذِيْنَ اٰمَنُوْا فَيَعْلَمُوْنَ اَنَّهُ الْحَقُّ مِنْ رَّبِّهِمْ ۚ وَاَمَّا الَّذِيْنَ كَفَرُوْا فَيَقُوْلُوْنَ مَاذَآ اَرَادَ اللّٰهُ بِهٰذَا مَثَلًا ۘ يُضِلُّ بِهٖ كَثِيْرًا وَّيَهْدِيْ بِهٖ كَثِيْرًا ۗ وَمَا يُضِلُّ بِهٖٓ اِلَّا الْفٰسِقِيْنَۙ","bc":"innallāha lā yastaḥyī ay yaḍriba maṡalam mā ba'ụḍatan fa mā fauqahā, fa ammallażīna āmanụ fa ya'lamụna annahul-ḥaqqu mir rabbihim, wa ammallażīna kafarụ fa yaqụlụna māżā arādallāhu bihāżā maṡalā, yuḍillu bihī kaṡīraw wa yahdī bihī kaṡīrā, wa mā yuḍillu bihī illal-fāsiqīn","at":"Sesungguhnya Allah tidak segan membuat perumpamaan seekor nyamuk atau yang lebih kecil dari itu. Adapun orang-orang yang beriman, mereka tahu bahwa itu kebenaran dari Tuhan. Tetapi mereka yang kafir berkata, “Apa maksud Allah dengan perumpamaan ini?” Dengan (perumpamaan) itu banyak orang yang dibiarkan-Nya sesat, dan dengan itu banyak (pula) orang yang diberi-Nya petunjuk. Tetapi tidak ada yang Dia sesatkan dengan (perumpamaan) itu selain orang-orang fasik,"},{"id":"27","ar":"الَّذِيْنَ يَنْقُضُوْنَ عَهْدَ اللّٰهِ مِنْۢ بَعْدِ مِيْثَاقِهٖۖ وَيَقْطَعُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيُفْسِدُوْنَ فِى الْاَرْضِۗ اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ","bc":"allażīna yangquḍụna 'ahdallāhi mim ba'di mīṡāqihī wa yaqṭa'ụna mā amarallāhu bihī ay yụṣala wa yufsidụna fil-arḍ, ulā`ika humul-khāsirụn","at":"(yaitu) orang-orang yang melanggar perjanjian Allah setelah (perjanjian) itu diteguhkan, dan memutuskan apa yang diperintahkan Allah untuk disambungkan dan berbuat kerusakan di bumi. Mereka itulah orang-orang yang rugi."},{"id":"28","ar":"كَيْفَ تَكْفُرُوْنَ بِاللّٰهِ وَكُنْتُمْ اَمْوَاتًا فَاَحْيَاكُمْۚ ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْ ثُمَّ اِلَيْهِ تُرْجَعُوْنَ ","bc":"kaifa takfurụna billāhi wa kuntum amwātan fa aḥyākum, ṡumma yumītukum ṡumma yuḥyīkum ṡumma ilaihi turja'ụn","at":"Bagaimana kamu ingkar kepada Allah, padahal kamu (tadinya) mati, lalu Dia menghidupkan kamu, kemudian Dia mematikan kamu lalu Dia menghidupkan kamu kembali. Kemudian kepada-Nyalah kamu dikembalikan."},{"id":"29","ar":"هُوَ الَّذِيْ خَلَقَ لَكُمْ مَّا فِى الْاَرْضِ جَمِيْعًا ثُمَّ اسْتَوٰٓى اِلَى السَّمَاۤءِ فَسَوّٰىهُنَّ سَبْعَ سَمٰوٰتٍ ۗ وَهُوَ بِكُلِّ شَيْءٍ عَلِيْمٌ","bc":"huwallażī khalaqa lakum mā fil-arḍi jamī'an ṡummastawā ilas-samā`i fa sawwāhunna sab'a samāwāt, wa huwa bikulli syai`in 'alīm","at":"Dialah (Allah) yang menciptakan segala apa yang ada di bumi untukmu kemudian Dia menuju ke langit, lalu Dia menyempurnakannya menjadi tujuh langit. Dan Dia Maha Mengetahui segala sesuatu."},{"id":"30","ar":"وَاِذْ قَالَ رَبُّكَ لِلْمَلٰۤىِٕكَةِ ِانِّيْ جَاعِلٌ فِى الْاَرْضِ خَلِيْفَةً ۗ قَالُوْٓا اَتَجْعَلُ فِيْهَا مَنْ يُّفْسِدُ فِيْهَا وَيَسْفِكُ الدِّمَاۤءَۚ وَنَحْنُ نُسَبِّحُ بِحَمْدِكَ وَنُقَدِّسُ لَكَ ۗ قَالَ اِنِّيْٓ اَعْلَمُ مَا لَا تَعْلَمُوْنَ","bc":"wa iż qāla rabbuka lil-malā`ikati innī jā'ilun fil-arḍi khalīfah, qālū a taj'alu fīhā may yufsidu fīhā wa yasfikud-dimā`, wa naḥnu nusabbiḥu biḥamdika wa nuqaddisu lak, qāla innī a'lamu mā lā ta'lamụn","at":"Dan (ingatlah) ketika Tuhanmu berfirman kepada para malaikat, “Aku hendak menjadikan khalifah di bumi.” Mereka berkata, “Apakah Engkau hendak menjadikan orang yang merusak dan menumpahkan darah di sana, sedangkan kami bertasbih memuji-Mu dan menyucikan nama-Mu?” Dia berfirman, “Sungguh, Aku mengetahui apa yang tidak kamu ketahui.”"},{"id":"31","ar":"وَعَلَّمَ اٰدَمَ الْاَسْمَاۤءَ كُلَّهَا ثُمَّ عَرَضَهُمْ عَلَى الْمَلٰۤىِٕكَةِ فَقَالَ اَنْۢبِـُٔوْنِيْ بِاَسْمَاۤءِ هٰٓؤُلَاۤءِ اِنْ كُنْتُمْ صٰدِقِيْنَ","bc":"wa 'allama ādamal-asmā`a kullahā ṡumma 'araḍahum 'alal-malā`ikati fa qāla ambi`ụnī bi`asmā`i hā`ulā`i ing kuntum ṣādiqīn","at":"Dan Dia ajarkan kepada Adam nama-nama (benda) semuanya, kemudian Dia perlihatkan kepada para malaikat, seraya berfirman, “Sebutkan kepada-Ku nama semua (benda) ini, jika kamu yang benar!”"},{"id":"32","ar":"قَالُوْا سُبْحٰنَكَ لَا عِلْمَ لَنَآ اِلَّا مَا عَلَّمْتَنَا ۗاِنَّكَ اَنْتَ الْعَلِيْمُ الْحَكِيْمُ ","bc":"qālụ sub-ḥānaka lā 'ilma lanā illā mā 'allamtanā, innaka antal-'alīmul-ḥakīm","at":"Mereka menjawab, “Mahasuci Engkau, tidak ada yang kami ketahui selain apa yang telah Engkau ajarkan kepada kami. Sungguh, Engkaulah Yang Maha Mengetahui, Mahabijaksana.”"},{"id":"33","ar":"قَالَ يٰٓاٰدَمُ اَنْۢبِئْهُمْ بِاَسْمَاۤىِٕهِمْ ۚ فَلَمَّآ اَنْۢبَاَهُمْ بِاَسْمَاۤىِٕهِمْۙ قَالَ اَلَمْ اَقُلْ لَّكُمْ اِنِّيْٓ اَعْلَمُ غَيْبَ السَّمٰوٰتِ وَالْاَرْضِۙ وَاَعْلَمُ مَا تُبْدُوْنَ وَمَا كُنْتُمْ تَكْتُمُوْنَ ","bc":"qāla yā ādamu ambi`hum bi`asmā`ihim, fa lammā amba`ahum bi`asmā`ihim qāla a lam aqul lakum innī a'lamu gaibas-samāwāti wal-arḍi wa a'lamu mā tubdụna wa mā kuntum taktumụn","at":"Dia (Allah) berfirman, “Wahai Adam! Beritahukanlah kepada mereka nama-nama itu!” Setelah dia (Adam) menyebutkan nama-namanya, Dia berfirman, “Bukankah telah Aku katakan kepadamu, bahwa Aku mengetahui rahasia langit dan bumi, dan Aku mengetahui apa yang kamu nyatakan dan apa yang kamu sembunyikan?”"},{"id":"34","ar":"وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ اَبٰى وَاسْتَكْبَرَۖ وَكَانَ مِنَ الْكٰفِرِيْنَ","bc":"wa iż qulnā lil-malā`ikatisjudụ li`ādama fa sajadū illā iblīs, abā wastakbara wa kāna minal-kāfirīn","at":"Dan (ingatlah) ketika Kami berfirman kepada para malaikat, “Sujudlah kamu kepada Adam!” Maka mereka pun sujud kecuali Iblis. Ia menolak dan menyombongkan diri, dan ia termasuk golongan yang kafir."},{"id":"35","ar":"وَقُلْنَا يٰٓاٰدَمُ اسْكُنْ اَنْتَ وَزَوْجُكَ الْجَنَّةَ وَكُلَا مِنْهَا رَغَدًا حَيْثُ شِئْتُمَاۖ وَلَا تَقْرَبَا هٰذِهِ الشَّجَرَةَ فَتَكُوْنَا مِنَ الظّٰلِمِيْنَ ","bc":"wa qulnā yā ādamuskun anta wa zaujukal-jannata wa kulā min-hā ragadan ḥaiṡu syi`tumā wa lā taqrabā hāżihisy-syajarata fa takụnā minaẓ-ẓālimīn","at":"Dan Kami berfirman, “Wahai Adam! Tinggallah engkau dan istrimu di dalam surga, dan makanlah dengan nikmat (berbagai makanan) yang ada di sana sesukamu. (Tetapi) janganlah kamu dekati pohon ini, nanti kamu termasuk orang-orang yang zalim!”"},{"id":"36","ar":"فَاَزَلَّهُمَا الشَّيْطٰنُ عَنْهَا فَاَخْرَجَهُمَا مِمَّا كَانَا فِيْهِ ۖ وَقُلْنَا اهْبِطُوْا بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚ وَلَكُمْ فِى الْاَرْضِ مُسْتَقَرٌّ وَّمَتَاعٌ اِلٰى حِيْنٍ","bc":"fa azallahumasy-syaiṭānu 'an-hā fa akhrajahumā mimmā kānā fīhi wa qulnahbiṭụ ba'ḍukum liba'ḍin 'aduww, wa lakum fil-arḍi mustaqarruw wa matā'un ilā ḥīn","at":"Lalu setan memperdayakan keduanya dari surga sehingga keduanya dikeluarkan dari (segala kenikmatan) ketika keduanya di sana (surga). Dan Kami berfirman, “Turunlah kamu! Sebagian kamu menjadi musuh bagi yang lain. Dan bagi kamu ada tempat tinggal dan kesenangan di bumi sampai waktu yang ditentukan.”"},{"id":"37","ar":"فَتَلَقّٰٓى اٰدَمُ مِنْ رَّبِّهٖ كَلِمٰتٍ فَتَابَ عَلَيْهِ ۗ اِنَّهٗ هُوَ التَّوَّابُ الرَّحِيْمُ ","bc":"fa talaqqā ādamu mir rabbihī kalimātin fa tāba 'alaīh, innahụ huwat-tawwābur-raḥīm","at":"Kemudian Adam menerima beberapa kalimat dari Tuhannya, lalu Dia pun menerima tobatnya. Sungguh, Allah Maha Penerima tobat, Maha Penyayang."},{"id":"38","ar":"قُلْنَا اهْبِطُوْا مِنْهَا جَمِيْعًا ۚ فَاِمَّا يَأْتِيَنَّكُمْ مِّنِّيْ هُدًى فَمَنْ تَبِعَ هُدَايَ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ","bc":"qulnahbiṭụ min-hā jamī'ā, fa immā ya`tiyannakum minnī hudan fa man tabi'a hudāya fa lā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Kami berfirman, “Turunlah kamu semua dari surga! Kemudian jika benar-benar datang petunjuk-Ku kepadamu, maka barangsiapa mengikuti petunjuk-Ku, tidak ada rasa takut pada mereka dan mereka tidak bersedih hati.”"},{"id":"39","ar":"وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ","bc":"wallażīna kafarụ wa każżabụ bi`āyātinā ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn","at":"Adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itu penghuni neraka. Mereka kekal di dalamnya."},{"id":"40","ar":"يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَوْفُوْا بِعَهْدِيْٓ اُوْفِ بِعَهْدِكُمْۚ وَاِيَّايَ فَارْهَبُوْنِ","bc":"yā banī isrā`īlażkurụ ni'matiyallatī an'amtu 'alaikum wa aufụ bi'ahdī ụfi bi'ahdikum, wa iyyāya far-habụn","at":"Wahai Bani Israil! Ingatlah nikmat-Ku yang telah Aku berikan kepadamu. Dan penuhilah janjimu kepada-Ku, niscaya Aku penuhi janji-Ku kepadamu, dan takutlah kepada-Ku saja."},{"id":"41","ar":"وَاٰمِنُوْا بِمَآ اَنْزَلْتُ مُصَدِّقًا لِّمَا مَعَكُمْ وَلَا تَكُوْنُوْٓا اَوَّلَ كَافِرٍۢ بِهٖ ۖ وَلَا تَشْتَرُوْا بِاٰيٰتِيْ ثَمَنًا قَلِيْلًا ۖوَّاِيَّايَ فَاتَّقُوْنِ ","bc":"wa āminụ bimā anzaltu muṣaddiqal limā ma'akum wa lā takụnū awwala kāfirim bihī wa lā tasytarụ bi`āyātī ṡamanang qalīlaw wa iyyāya fattaqụn","at":"Dan berimanlah kamu kepada apa (Al-Qur'an) yang telah Aku turunkan yang membenarkan apa (Taurat) yang ada pada kamu, dan janganlah kamu menjadi orang yang pertama kafir kepadanya. Janganlah kamu jual ayat-ayat-Ku dengan harga murah, dan bertakwalah hanya kepada-Ku."},{"id":"42","ar":"وَلَا تَلْبِسُوا الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوا الْحَقَّ وَاَنْتُمْ تَعْلَمُوْنَ ","bc":"wa lā talbisul-ḥaqqa bil-bāṭili wa taktumul-ḥaqqa wa antum ta'lamụn","at":"Dan janganlah kamu campuradukkan kebenaran dengan kebatilan dan (janganlah) kamu sembunyikan kebenaran, sedangkan kamu mengetahuinya."},{"id":"43","ar":"وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَارْكَعُوْا مَعَ الرَّاكِعِيْنَ","bc":"wa aqīmuṣ-ṣalāta wa ātuz-zakāta warka'ụ ma'ar-rāki'īn","at":"Dan laksanakanlah salat, tunaikanlah zakat, dan rukuklah beserta orang yang rukuk."},{"id":"44","ar":"۞ اَتَأْمُرُوْنَ النَّاسَ بِالْبِرِّ وَتَنْسَوْنَ اَنْفُسَكُمْ وَاَنْتُمْ تَتْلُوْنَ الْكِتٰبَ ۗ اَفَلَا تَعْقِلُوْنَ ","bc":"a ta`murụnan-nāsa bil-birri wa tansauna anfusakum wa antum tatlụnal-kitāb, a fa lā ta'qilụn","at":"Mengapa kamu menyuruh orang lain (mengerjakan) kebajikan, sedangkan kamu melupakan dirimu sendiri, padahal kamu membaca Kitab (Taurat)? Tidakkah kamu mengerti?"},{"id":"45","ar":"وَاسْتَعِيْنُوْا بِالصَّبْرِ وَالصَّلٰوةِ ۗ وَاِنَّهَا لَكَبِيْرَةٌ اِلَّا عَلَى الْخٰشِعِيْنَۙ","bc":"wasta'īnụ biṣ-ṣabri waṣ-ṣalāh, wa innahā lakabīratun illā 'alal-khāsyi'īn","at":"Dan mohonlah pertolongan (kepada Allah) dengan sabar dan salat. Dan (salat) itu sungguh berat, kecuali bagi orang-orang yang khusyuk,"},{"id":"46","ar":"الَّذِيْنَ يَظُنُّوْنَ اَنَّهُمْ مُّلٰقُوْا رَبِّهِمْ وَاَنَّهُمْ اِلَيْهِ رٰجِعُوْنَ","bc":"allażīna yaẓunnụna annahum mulāqụ rabbihim wa annahum ilaihi rāji'ụn","at":"(yaitu) mereka yang yakin, bahwa mereka akan menemui Tuhannya, dan bahwa mereka akan kembali kepada-Nya."},{"id":"47","ar":"يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَنِّيْ فَضَّلْتُكُمْ عَلَى الْعٰلَمِيْنَ","bc":"yā banī isrā`īlażkurụ ni'matiyallatī an'amtu 'alaikum wa annī faḍḍaltukum 'alal-'ālamīn","at":"Wahai Bani Israil! Ingatlah nikmat-Ku yang telah Aku berikan kepadamu, dan Aku telah melebihkan kamu dari semua umat yang lain di alam ini (pada masa itu)."},{"id":"48","ar":"وَاتَّقُوْا يَوْمًا لَّا تَجْزِيْ نَفْسٌ عَنْ نَّفْسٍ شَيْـًٔا وَّلَا يُقْبَلُ مِنْهَا شَفَاعَةٌ وَّلَا يُؤْخَذُ مِنْهَا عَدْلٌ وَّلَا هُمْ يُنْصَرُوْنَ ","bc":"wattaqụ yaumal lā tajzī nafsun 'an nafsin syai`aw wa lā yuqbalu min-hā syafā'atuw wa lā yu`khażu min-hā 'adluw wa lā hum yunṣarụn","at":"Dan takutlah kamu pada hari, (ketika) tidak seorang pun dapat membela orang lain sedikit pun. Sedangkan syafaat dan tebusan apa pun darinya tidak diterima dan mereka tidak akan ditolong."},{"id":"49","ar":"وَاِذْ نَجَّيْنٰكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِ يُذَبِّحُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْ ۗ وَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِيْمٌ ","bc":"wa iż najjainākum min āli fir'auna yasụmụnakum sū`al-'ażābi yużabbiḥụna abnā`akum wa yastaḥyụna nisā`akum, wa fī żālikum balā`um mir rabbikum 'aẓīm","at":"Dan (ingatlah) ketika Kami menyelamatkan kamu dari (Fir‘aun dan) pengikut-pengikut Fir‘aun. Mereka menimpakan siksaan yang sangat berat kepadamu. Mereka menyembelih anak-anak laki-lakimu dan membiarkan hidup anak-anak perempuanmu. Dan pada yang demikian itu merupakan cobaan yang besar dari Tuhanmu. "},{"id":"50","ar":"وَاِذْ فَرَقْنَا بِكُمُ الْبَحْرَ فَاَنْجَيْنٰكُمْ وَاَغْرَقْنَآ اٰلَ فِرْعَوْنَ وَاَنْتُمْ تَنْظُرُوْنَ ","bc":"wa iż faraqnā bikumul-baḥra fa anjainākum wa agraqnā āla fir'auna wa antum tanẓurụn","at":"Dan (ingatlah) ketika Kami membelah laut untukmu, sehingga kamu dapat Kami selamatkan dan Kami tenggelamkan (Fir‘aun dan) pengikut-pengikut Fir‘aun, sedang kamu menyaksikan."},{"id":"51","ar":"وَاِذْ وٰعَدْنَا مُوْسٰىٓ اَرْبَعِيْنَ لَيْلَةً ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْۢ بَعْدِهٖ وَاَنْتُمْ ظٰلِمُوْنَ","bc":"wa iż wā'adnā mụsā arba'īna lailatan ṡummattakhażtumul-'ijla mim ba'dihī wa antum ẓālimụn","at":"Dan (ingatlah) ketika Kami menjanjikan kepada Musa empat puluh malam. Kemudian kamu (Bani Israil) menjadikan (patung) anak sapi (sebagai sesembahan) setelah (kepergian)nya, dan kamu (menjadi) orang yang zalim."},{"id":"52","ar":"ثُمَّ عَفَوْنَا عَنْكُمْ مِّنْۢ بَعْدِ ذٰلِكَ لَعَلَّكُمْ تَشْكُرُوْنَ","bc":"ṡumma 'afaunā 'angkum mim ba'di żālika la'allakum tasykurụn","at":"Kemudian Kami memaafkan kamu setelah itu, agar kamu bersyukur."},{"id":"53","ar":"وَاِذْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَالْفُرْقَانَ لَعَلَّكُمْ تَهْتَدُوْنَ","bc":"wa iż ātainā mụsal-kitāba wal-furqāna la'allakum tahtadụn","at":"Dan (ingatlah), ketika Kami memberikan kepada Musa Kitab dan Furqan, agar kamu memperoleh petunjuk. "},{"id":"54","ar":"وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖ يٰقَوْمِ اِنَّكُمْ ظَلَمْتُمْ اَنْفُسَكُمْ بِاتِّخَاذِكُمُ الْعِجْلَ فَتُوْبُوْٓا اِلٰى بَارِىِٕكُمْ فَاقْتُلُوْٓا اَنْفُسَكُمْۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ عِنْدَ بَارِىِٕكُمْۗ فَتَابَ عَلَيْكُمْ ۗ اِنَّهٗ هُوَ التَّوَّابُ الرَّحِيْمُ ","bc":"wa iż qāla mụsā liqaumihī yā qaumi innakum ẓalamtum anfusakum bittikhāżikumul-'ijla fa tụbū ilā bāri`ikum faqtulū anfusakum, żālikum khairul lakum 'inda bāri`ikum, fa tāba 'alaikum, innahụ huwat-tawwābur-raḥīm","at":"Dan (ingatlah) ketika Musa berkata kepada kaumnya, “Wahai kaumku! Kamu benar-benar telah menzalimi dirimu sendiri dengan menjadikan (patung) anak sapi (sebagai sesembahan), karena itu bertobatlah kepada Penciptamu dan bunuhlah dirimu. Itu lebih baik bagimu di sisi Penciptamu. Dia akan menerima tobatmu. Sungguh, Dialah Yang Maha Penerima tobat, Maha Penyayang."},{"id":"55","ar":"وَاِذْ قُلْتُمْ يٰمُوْسٰى لَنْ نُّؤْمِنَ لَكَ حَتّٰى نَرَى اللّٰهَ جَهْرَةً فَاَخَذَتْكُمُ الصّٰعِقَةُ وَاَنْتُمْ تَنْظُرُوْنَ","bc":"wa iż qultum yā mụsā lan nu`mina laka ḥattā narallāha jahratan fa akhażatkumuṣ-ṣā'iqatu wa antum tanẓurụn","at":"Dan (ingatlah) ketika kamu berkata, “Wahai Musa! Kami tidak akan beriman kepadamu sebelum kami melihat Allah dengan jelas,” maka halilintar menyambarmu, sedang kamu menyaksikan."},{"id":"56","ar":"ثُمَّ بَعَثْنٰكُمْ مِّنْۢ بَعْدِ مَوْتِكُمْ لَعَلَّكُمْ تَشْكُرُوْنَ","bc":"ṡumma ba'aṡnākum mim ba'di mautikum la'allakum tasykurụn","at":"Kemudian, Kami membangkitkan kamu setelah kamu mati, agar kamu bersyukur."},{"id":"57","ar":"وَظَلَّلْنَا عَلَيْكُمُ الْغَمَامَ وَاَنْزَلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰى ۗ كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْ ۗ وَمَا ظَلَمُوْنَا وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ","bc":"wa ẓallalnā 'alaikumul-gamāma wa anzalnā 'alaikumul-manna was-salwā, kulụ min ṭayyibāti mā razaqnākum, wa mā ẓalamụnā wa lāking kānū anfusahum yaẓlimụn","at":"Dan Kami menaungi kamu dengan awan, dan Kami menurunkan kepadamu mann dan salwa. Makanlah (makanan) yang baik-baik dari rezeki yang telah Kami berikan kepadamu. Mereka tidak menzalimi Kami, tetapi justru merekalah yang menzalimi diri sendiri."},{"id":"58","ar":"وَاِذْ قُلْنَا ادْخُلُوْا هٰذِهِ الْقَرْيَةَ فَكُلُوْا مِنْهَا حَيْثُ شِئْتُمْ رَغَدًا وَّادْخُلُوا الْبَابَ سُجَّدًا وَّقُوْلُوْا حِطَّةٌ نَّغْفِرْ لَكُمْ خَطٰيٰكُمْ ۗ وَسَنَزِيْدُ الْمُحْسِنِيْنَ ","bc":"wa iż qulnadkhulụ hāżihil-qaryata fa kulụ min-hā ḥaiṡu syi`tum ragadaw wadkhulul-bāba sujjadaw wa qụlụ ḥiṭṭatun nagfir lakum khaṭāyākum, wa sanazīdul-muḥsinīn","at":"Dan (ingatlah) ketika Kami berfirman, “Masuklah ke negeri ini (Baitulmaqdis), maka makanlah dengan nikmat (berbagai makanan) yang ada di sana sesukamu. Dan masukilah pintu gerbangnya sambil membungkuk, dan katakanlah, “Bebaskanlah kami (dari dosa-dosa kami),” niscaya Kami ampuni kesalahan-kesalahanmu. Dan Kami akan menambah (karunia) bagi orang-orang yang berbuat kebaikan.”"},{"id":"59","ar":"فَبَدَّلَ الَّذِيْنَ ظَلَمُوْا قَوْلًا غَيْرَ الَّذِيْ قِيْلَ لَهُمْ فَاَنْزَلْنَا عَلَى الَّذِيْنَ ظَلَمُوْا رِجْزًا مِّنَ السَّمَاۤءِ بِمَا كَانُوْا يَفْسُقُوْنَ","bc":"fa baddalallażīna ẓalamụ qaulan gairallażī qīla lahum fa anzalnā 'alallażīna ẓalamụ rijzam minas-samā`i bimā kānụ yafsuqụn","at":"Lalu orang-orang yang zalim mengganti perintah dengan (perintah lain) yang tidak diperintahkan kepada mereka. Maka Kami turunkan malapetaka dari langit kepada orang-orang yang zalim itu, karena mereka (selalu) berbuat fasik."},{"id":"60","ar":"۞ وَاِذِ اسْتَسْقٰى مُوْسٰى لِقَوْمِهٖ فَقُلْنَا اضْرِبْ بِّعَصَاكَ الْحَجَرَۗ فَانْفَجَرَتْ مِنْهُ اثْنَتَا عَشْرَةَ عَيْنًا ۗ قَدْ عَلِمَ كُلُّ اُنَاسٍ مَّشْرَبَهُمْ ۗ كُلُوْا وَاشْرَبُوْا مِنْ رِّزْقِ اللّٰهِ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ","bc":"wa iżistasqā mụsā liqaumihī fa qulnaḍrib bi'aṣākal-ḥajar, fanfajarat min-huṡnatā 'asyrata 'ainā, qad 'alima kullu unāsim masyrabahum, kulụ wasyrabụ mir rizqillāhi wa lā ta'ṡau fil-arḍi mufsidīn","at":"Dan (ingatlah) ketika Musa memohon air untuk kaumnya, lalu Kami berfirman, “Pukullah batu itu dengan tongkatmu!” Maka memancarlah daripadanya dua belas mata air. Setiap suku telah mengetahui tempat minumnya (masing-masing). Makan dan minumlah dari rezeki (yang diberikan) Allah, dan janganlah kamu melakukan kejahatan di bumi dengan berbuat kerusakan."},{"id":"61","ar":"وَاِذْ قُلْتُمْ يٰمُوْسٰى لَنْ نَّصْبِرَ عَلٰى طَعَامٍ وَّاحِدٍ فَادْعُ لَنَا رَبَّكَ يُخْرِجْ لَنَا مِمَّا تُنْۢبِتُ الْاَرْضُ مِنْۢ بَقْلِهَا وَقِثَّاۤىِٕهَا وَفُوْمِهَا وَعَدَسِهَا وَبَصَلِهَا ۗ قَالَ اَتَسْتَبْدِلُوْنَ الَّذِيْ هُوَ اَدْنٰى بِالَّذِيْ هُوَ خَيْرٌ ۗ اِهْبِطُوْا مِصْرًا فَاِنَّ لَكُمْ مَّا سَاَلْتُمْ ۗ وَضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ وَالْمَسْكَنَةُ وَبَاۤءُوْ بِغَضَبٍ مِّنَ اللّٰهِ ۗ ذٰلِكَ بِاَنَّهُمْ كَانُوْا يَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَيَقْتُلُوْنَ النَّبِيّٖنَ بِغَيْرِ الْحَقِّ ۗ ذٰلِكَ بِمَا عَصَوْا وَّكَانُوْا يَعْتَدُوْنَ","bc":"wa iż qultum yā mụsā lan naṣbira 'alā ṭa'āmiw wāḥidin fad'u lanā rabbaka yukhrij lanā mimmā tumbitul-arḍu mim baqlihā wa qiṡṡā`ihā wa fụmihā wa 'adasihā wa baṣalihā, qāla a tastabdilụnallażī huwa adnā billażī huwa khaīr, ihbiṭụ miṣran fa inna lakum mā sa`altum, wa ḍuribat 'alaihimuż-żillatu wal-maskanatu wa bā`ụ bigaḍabim minallāh, żālika bi`annahum kānụ yakfurụna bi`āyātillāhi wa yaqtulụnan-nabiyyīna bigairil-ḥaqq, żālika bimā 'aṣaw wa kānụ ya'tadụn","at":"Dan (ingatlah), ketika kamu berkata, “Wahai Musa! Kami tidak tahan hanya (makan) dengan satu macam makanan saja, maka mohonkanlah kepada Tuhanmu untuk kami, agar Dia memberi kami apa yang ditumbuhkan bumi, seperti: sayur-mayur, mentimun, bawang putih, kacang adas dan bawang merah.” Dia (Musa) menjawab, “Apakah kamu meminta sesuatu yang buruk sebagai ganti dari sesuatu yang baik? Pergilah ke suatu kota, pasti kamu akan memperoleh apa yang kamu minta.” Kemudian mereka ditimpa kenistaan dan kemiskinan, dan mereka (kembali) mendapat kemurkaan dari Allah. Hal itu (terjadi) karena mereka mengingkari ayat-ayat Allah dan membunuh para nabi tanpa hak (alasan yang benar). Yang demikian itu karena mereka durhaka dan melampaui batas."},{"id":"62","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالنَّصٰرٰى وَالصَّابِــِٕيْنَ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحًا فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ","bc":"innallażīna āmanụ wallażīna hādụ wan-naṣārā waṣ-ṣābi`īna man āmana billāhi wal-yaumil-ākhiri wa 'amila ṣāliḥan fa lahum ajruhum 'inda rabbihim, wa lā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Sesungguhnya orang-orang yang beriman, orang-orang Yahudi, orang-orang Nasrani dan orang-orang sabi'in, siapa saja (di antara mereka) yang beriman kepada Allah dan hari akhir, dan melakukan kebajikan, mereka mendapat pahala dari Tuhannya, tidak ada rasa takut pada mereka, dan mereka tidak bersedih hati."},{"id":"63","ar":"وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّوْرَۗ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاذْكُرُوْا مَا فِيْهِ لَعَلَّكُمْ تَتَّقُوْنَ","bc":"wa iż akhażnā mīṡāqakum wa rafa'nā fauqakumuṭ-ṭụr, khużụ mā ātainākum biquwwatiw ważkurụ mā fīhi la'allakum tattaqụn","at":"Dan (ingatlah) ketika Kami mengambil janji kamu dan Kami angkat gunung (Sinai) di atasmu (seraya berfirman), “Pegang teguhlah apa yang telah Kami berikan kepadamu dan ingatlah apa yang ada di dalamnya, agar kamu bertakwa.”"},{"id":"64","ar":"ثُمَّ تَوَلَّيْتُمْ مِّنْۢ بَعْدِ ذٰلِكَ فَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ لَكُنْتُمْ مِّنَ الْخٰسِرِيْنَ","bc":"ṡumma tawallaitum mim ba'di żālika falau lā faḍlullāhi 'alaikum wa raḥmatuhụ lakuntum minal-khāsirīn","at":"Kemudian setelah itu kamu berpaling. Maka sekiranya bukan karena karunia Allah dan rahmat-Nya kepadamu, pasti kamu termasuk orang yang rugi."},{"id":"65","ar":"وَلَقَدْ عَلِمْتُمُ الَّذِيْنَ اعْتَدَوْا مِنْكُمْ فِى السَّبْتِ فَقُلْنَا لَهُمْ كُوْنُوْا قِرَدَةً خَاسِـِٕيْنَ","bc":"wa laqad 'alimtumullażīna'tadau mingkum fis-sabti fa qulnā lahum kụnụ qiradatan khāsi`īn","at":"Dan sungguh, kamu telah mengetahui orang-orang yang melakukan pelanggaran di antara kamu pada hari Sabat, lalu Kami katakan kepada mereka, “Jadilah kamu kera yang hina!” "},{"id":"66","ar":" فَجَعَلْنٰهَا نَكَالًا لِّمَا بَيْنَ يَدَيْهَا وَمَا خَلْفَهَا وَمَوْعِظَةً لِّلْمُتَّقِيْنَ","bc":"fa ja'alnāhā nakālal limā baina yadaihā wa mā khalfahā wa mau'iẓatal lil-muttaqīn","at":"Maka Kami jadikan (yang demikian) itu peringatan bagi orang-orang pada masa itu dan bagi mereka yang datang kemudian, serta menjadi pelajaran bagi orang-orang yang bertakwa."},{"id":"67","ar":"وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖٓ اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تَذْبَحُوْا بَقَرَةً ۗ قَالُوْٓا اَتَتَّخِذُنَا هُزُوًا ۗ قَالَ اَعُوْذُ بِاللّٰهِ اَنْ اَكُوْنَ مِنَ الْجٰهِلِيْنَ ","bc":"wa iż qāla mụsā liqaumihī innallāha ya`murukum an tażbaḥụ baqarah, qālū a tattakhiżunā huzuwā, qāla a'ụżu billāhi an akụna minal-jāhilīn","at":"Dan (ingatlah) ketika Musa berkata kepada kaumnya, “Allah memerintahkan kamu agar menyembelih seekor sapi betina.” Mereka bertanya, “Apakah engkau akan menjadikan kami sebagai ejekan?” Dia (Musa) menjawab, “Aku berlindung kepada Allah agar tidak termasuk orang-orang yang bodoh.”"},{"id":"68","ar":"قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا هِيَ ۗ قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ لَّا فَارِضٌ وَّلَا بِكْرٌۗ عَوَانٌۢ بَيْنَ ذٰلِكَ ۗ فَافْعَلُوْا مَا تُؤْمَرُوْنَ ","bc":"qālud'u lanā rabbaka yubayyil lanā mā hiy, qāla innahụ yaqụlu innahā baqaratul lā fāriḍuw wa lā bikr, 'awānum baina żālik, faf'alụ mā tu`marụn","at":"Mereka berkata, “Mohonkanlah kepada Tuhanmu untuk kami agar Dia menjelaskan kepada kami tentang (sapi betina) itu.” Dia (Musa) menjawab, “Dia (Allah) berfirman, bahwa sapi betina itu tidak tua dan tidak muda, (tetapi) pertengahan antara itu. Maka kerjakanlah apa yang diperintahkan kepadamu.”"},{"id":"69","ar":"قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا لَوْنُهَا ۗ قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ صَفْرَاۤءُ فَاقِعٌ لَّوْنُهَا تَسُرُّ النّٰظِرِيْنَ","bc":"qālud'u lanā rabbaka yubayyil lanā mā launuhā, qāla innahụ yaqụlu innahā baqaratun ṣafrā`u fāqi'ul launuhā tasurrun-nāẓirīn","at":"Mereka berkata, “Mohonkanlah kepada Tuhanmu untuk kami agar Dia menjelaskan kepada kami apa warnanya.” Dia (Musa) menjawab, “Dia (Allah) berfirman, bahwa (sapi) itu adalah sapi betina yang kuning tua warnanya, yang menyenangkan orang-orang yang memandang(nya).”"},{"id":"70","ar":"قَالُوا ادْعُ لَنَا رَبَّكَ يُبَيِّنْ لَّنَا مَا هِيَۙ اِنَّ الْبَقَرَ تَشٰبَهَ عَلَيْنَاۗ وَاِنَّآ اِنْ شَاۤءَ اللّٰهُ لَمُهْتَدُوْنَ","bc":"qālud'u lanā rabbaka yubayyil lanā mā hiya innal-baqara tasyābaha 'alainā, wa innā in syā`allāhu lamuhtadụn","at":"Mereka berkata, “Mohonkanlah kepada Tuhanmu untuk kami agar Dia menjelaskan kepada kami tentang (sapi betina) itu. (Karena) sesungguhnya sapi itu belum jelas bagi kami, dan jika Allah menghendaki, niscaya kami mendapat petunjuk.”"},{"id":"71","ar":"قَالَ اِنَّهٗ يَقُوْلُ اِنَّهَا بَقَرَةٌ لَّا ذَلُوْلٌ تُثِيْرُ الْاَرْضَ وَلَا تَسْقِى الْحَرْثَۚ مُسَلَّمَةٌ لَّاشِيَةَ فِيْهَا ۗ قَالُوا الْـٰٔنَ جِئْتَ بِالْحَقِّ فَذَبَحُوْهَا وَمَا كَادُوْا يَفْعَلُوْنَ","bc":"qāla innahụ yaqụlu innahā baqaratul lā żalụlun tuṡīrul-arḍa wa lā tasqil-ḥarṡ, musallamatul lā syiyata fīhā, qālul-āna ji`ta bil-ḥaqqi fa żabaḥụhā wa mā kādụ yaf'alụn","at":"Dia (Musa) menjawab, “Dia (Allah) berfirman, (sapi) itu adalah sapi betina yang belum pernah dipakai untuk membajak tanah dan tidak (pula) untuk mengairi tanaman, sehat, dan tanpa belang.” Mereka berkata, “Sekarang barulah engkau menerangkan (hal) yang sebenarnya.” Lalu mereka menyembelihnya, dan nyaris mereka tidak melaksanakan (perintah) itu."},{"id":"72","ar":"وَاِذْ قَتَلْتُمْ نَفْسًا فَادّٰرَءْتُمْ فِيْهَا ۗ وَاللّٰهُ مُخْرِجٌ مَّا كُنْتُمْ تَكْتُمُوْنَ ۚ","bc":"wa iż qataltum nafsan faddāra'tum fīhā, wallāhu mukhrijum mā kuntum taktumụn","at":"Dan (ingatlah) ketika kamu membunuh seseorang, lalu kamu tuduh-menuduh tentang itu. Tetapi Allah menyingkapkan apa yang kamu sembunyikan."},{"id":"73","ar":"فَقُلْنَا اضْرِبُوْهُ بِبَعْضِهَاۗ كَذٰلِكَ يُحْيِ اللّٰهُ الْمَوْتٰى وَيُرِيْكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَعْقِلُوْنَ","bc":"fa qulnaḍribụhu biba'ḍihā, każālika yuḥyillāhul-mautā wa yurīkum āyātihī la'allakum ta'qilụn","at":"Lalu Kami berfirman, “Pukullah (mayat) itu dengan bagian dari (sapi) itu!” Demikianlah Allah menghidupkan (orang) yang telah mati, dan Dia memperlihatkan kepadamu tanda-tanda (kekuasaan-Nya) agar kamu mengerti."},{"id":"74","ar":"ثُمَّ قَسَتْ قُلُوْبُكُمْ مِّنْۢ بَعْدِ ذٰلِكَ فَهِيَ كَالْحِجَارَةِ اَوْ اَشَدُّ قَسْوَةً ۗ وَاِنَّ مِنَ الْحِجَارَةِ لَمَا يَتَفَجَّرُ مِنْهُ الْاَنْهٰرُ ۗ وَاِنَّ مِنْهَا لَمَا يَشَّقَّقُ فَيَخْرُجُ مِنْهُ الْمَاۤءُ ۗوَاِنَّ مِنْهَا لَمَا يَهْبِطُ مِنْ خَشْيَةِ اللّٰهِ ۗوَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ","bc":"ṡumma qasat qulụbukum mim ba'di żālika fa hiya kal-ḥijārati au asyaddu qaswah, wa inna minal-ḥijārati lamā yatafajjaru min-hul-an-hār, wa inna min-hā lamā yasysyaqqaqu fa yakhruju min-hul-mā`, wa inna min-hā lamā yahbiṭu min khasy-yatillāh, wa mallāhu bigāfilin 'ammā ta'malụn","at":"Kemudian setelah itu hatimu menjadi keras, sehingga (hatimu) seperti batu, bahkan lebih keras. Padahal dari batu-batu itu pasti ada sungai-sungai yang (airnya) memancar daripadanya. Ada pula yang terbelah lalu keluarlah mata air daripadanya. Dan ada pula yang meluncur jatuh karena takut kepada Allah. Dan Allah tidaklah lengah terhadap apa yang kamu kerjakan."},{"id":"75","ar":"۞ اَفَتَطْمَعُوْنَ اَنْ يُّؤْمِنُوْا لَكُمْ وَقَدْ كَانَ فَرِيْقٌ مِّنْهُمْ يَسْمَعُوْنَ كَلَامَ اللّٰهِ ثُمَّ يُحَرِّفُوْنَهٗ مِنْۢ بَعْدِ مَا عَقَلُوْهُ وَهُمْ يَعْلَمُوْنَ ","bc":"a fa taṭma'ụna ay yu`minụ lakum wa qad kāna farīqum min-hum yasma'ụna kalāmallāhi ṡumma yuḥarrifụnahụ mim ba'di mā 'aqalụhu wa hum ya'lamụn","at":"Maka apakah kamu (Muslimin) sangat mengharapkan mereka akan percaya kepadamu, sedangkan segolongan dari mereka mendengar firman Allah, lalu mereka mengubahnya setelah memahaminya, padahal mereka mengetahuinya?"},{"id":"76","ar":"وَاِذَا لَقُوا الَّذِيْنَ اٰمَنُوْا قَالُوْٓا اٰمَنَّاۚ وَاِذَا خَلَا بَعْضُهُمْ اِلٰى بَعْضٍ قَالُوْٓا اَتُحَدِّثُوْنَهُمْ بِمَا فَتَحَ اللّٰهُ عَلَيْكُمْ لِيُحَاۤجُّوْكُمْ بِهٖ عِنْدَ رَبِّكُمْ ۗ اَفَلَا تَعْقِلُوْنَ","bc":"wa iżā laqullażīna āmanụ qālū āmannā, wa iżā khalā ba'ḍuhum ilā ba'ḍing qālū a tuḥaddiṡụnahum bimā fataḥallāhu 'alaikum liyuḥājjụkum bihī 'inda rabbikum, a fa lā ta'qilụn","at":"Dan apabila mereka berjumpa dengan orang-orang yang beriman, mereka berkata, “Kami telah beriman.” Tetapi apabila kembali kepada sesamanya, mereka bertanya, “Apakah akan kamu ceritakan kepada mereka apa yang telah diterangkan Allah kepadamu, sehingga mereka dapat menyanggah kamu di hadapan Tuhanmu? Tidakkah kamu mengerti?” "},{"id":"77","ar":"اَوَلَا يَعْلَمُوْنَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَ","bc":"a wa lā ya'lamụna annallāha ya'lamu mā yusirrụna wa mā yu'linụn","at":"Dan tidakkah mereka tahu bahwa Allah mengetahui apa yang mereka sembunyikan dan apa yang mereka nyatakan?"},{"id":"78","ar":" وَمِنْهُمْ اُمِّيُّوْنَ لَا يَعْلَمُوْنَ الْكِتٰبَ اِلَّآ اَمَانِيَّ وَاِنْ هُمْ اِلَّا يَظُنُّوْنَ","bc":"wa min-hum ummiyyụna lā ya'lamụnal-kitāba illā amāniyya wa in hum illā yaẓunnụn","at":"Dan di antara mereka ada yang buta huruf, tidak memahami Kitab (Taurat), kecuali hanya berangan-angan dan mereka hanya menduga-duga."},{"id":"79","ar":"فَوَيْلٌ لِّلَّذِيْنَ يَكْتُبُوْنَ الْكِتٰبَ بِاَيْدِيْهِمْ ثُمَّ يَقُوْلُوْنَ هٰذَا مِنْ عِنْدِ اللّٰهِ لِيَشْتَرُوْا بِهٖ ثَمَنًا قَلِيْلًا ۗفَوَيْلٌ لَّهُمْ مِّمَّا كَتَبَتْ اَيْدِيْهِمْ وَوَيْلٌ لَّهُمْ مِّمَّا يَكْسِبُوْنَ ","bc":"fa wailul lillażīna yaktubụnal-kitāba bi`aidīhim ṡumma yaqụlụna hāżā min 'indillāhi liyasytarụ bihī ṡamanang qalīlā, fa wailul lahum mimmā katabat aidīhim wa wailul lahum mimmā yaksibụn","at":"Maka celakalah orang-orang yang menulis kitab dengan tangan mereka (sendiri), kemudian berkata, “Ini dari Allah,” (dengan maksud) untuk menjualnya dengan harga murah. Maka celakalah mereka, karena tulisan tangan mereka, dan celakalah mereka karena apa yang mereka perbuat."},{"id":"80","ar":"وَقَالُوْا لَنْ تَمَسَّنَا النَّارُ اِلَّآ اَيَّامًا مَّعْدُوْدَةً ۗ قُلْ اَتَّخَذْتُمْ عِنْدَ اللّٰهِ عَهْدًا فَلَنْ يُّخْلِفَ اللّٰهُ عَهْدَهٗٓ اَمْ تَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ","bc":"wa qālụ lan tamassanan-nāru illā ayyāmam ma'dụdah, qul attakhażtum 'indallāhi 'ahdan fa lay yukhlifallāhu 'ahdahū am taqụlụna 'alallāhi mā lā ta'lamụn","at":"Dan mereka berkata, “Neraka tidak akan menyentuh kami, kecuali beberapa hari saja.” Katakanlah, “Sudahkah kamu menerima janji dari Allah, sehingga Allah tidak akan mengingkari janji-Nya, ataukah kamu mengatakan tentang Allah, sesuatu yang tidak kamu ketahui?”"},{"id":"81","ar":"بَلٰى مَنْ كَسَبَ سَيِّئَةً وَّاَحَاطَتْ بِهٖ خَطِيْۤـَٔتُهٗ فَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ ","bc":"balā mang kasaba sayyi`ataw wa `aḥāṭat bihī khaṭī`atuhụ fa ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn","at":"Bukan demikian! Barangsiapa berbuat keburukan, dan dosanya telah menenggelamkannya, maka mereka itu penghuni neraka. Mereka kekal di dalamnya."},{"id":"82","ar":"وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ","bc":"wallażīna āmanụ wa 'amiluṣ-ṣāliḥāti ulā`ika aṣ-ḥābul-jannah, hum fīhā khālidụn","at":"Dan orang-orang yang beriman dan mengerjakan kebajikan, mereka itu penghuni surga. Mereka kekal di dalamnya."},{"id":"83","ar":"وَاِذْ اَخَذْنَا مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَ لَا تَعْبُدُوْنَ اِلَّا اللّٰهَ وَبِالْوَالِدَيْنِ اِحْسَانًا وَّذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَقُوْلُوْا لِلنَّاسِ حُسْنًا وَّاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۗ ثُمَّ تَوَلَّيْتُمْ اِلَّا قَلِيْلًا مِّنْكُمْ وَاَنْتُمْ مُّعْرِضُوْنَ ","bc":"wa iż akhażnā mīṡāqa banī isrā`īla lā ta'budụna illallāha wa bil-wālidaini iḥsānaw wa żil-qurbā wal-yatāmā wal-masākīni wa qụlụ lin-nāsi ḥusnaw wa aqīmuṣ-ṣalāta wa ātuz-zakāh, ṡumma tawallaitum illā qalīlam mingkum wa antum mu'riḍụn","at":"Dan (ingatlah) ketika Kami mengambil janji dari Bani Israil, “Janganlah kamu menyembah selain Allah, dan berbuat-baiklah kepada kedua orang tua, kerabat, anak-anak yatim, dan orang-orang miskin. Dan bertuturkatalah yang baik kepada manusia, laksanakanlah salat dan tunaikanlah zakat.” Tetapi kemudian kamu berpaling (mengingkari), kecuali sebagian kecil dari kamu, dan kamu (masih menjadi) pembangkang."},{"id":"84","ar":"وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ لَا تَسْفِكُوْنَ دِمَاۤءَكُمْ وَلَا تُخْرِجُوْنَ اَنْفُسَكُمْ مِّنْ دِيَارِكُمْ ۖ ثُمَّ اَقْرَرْتُمْ وَاَنْتُمْ تَشْهَدُوْنَ ","bc":"wa iż akhażnā mīṡāqakum lā tasfikụna dimā`akum wa lā tukhrijụna anfusakum min diyārikum ṡumma aqrartum wa antum tasy-hadụn","at":"Dan (ingatlah) ketika Kami mengambil janji kamu, “Janganlah kamu menumpahkan darahmu (membunuh orang), dan mengusir dirimu (saudara sebangsamu) dari kampung halamanmu.” Kemudian kamu berikrar dan bersaksi."},{"id":"85","ar":"ثُمَّ اَنْتُمْ هٰٓؤُلَاۤءِ تَقْتُلُوْنَ اَنْفُسَكُمْ وَتُخْرِجُوْنَ فَرِيْقًا مِّنْكُمْ مِّنْ دِيَارِهِمْۖ تَظٰهَرُوْنَ عَلَيْهِمْ بِالْاِثْمِ وَالْعُدْوَانِۗ وَاِنْ يَّأْتُوْكُمْ اُسٰرٰى تُفٰدُوْهُمْ وَهُوَ مُحَرَّمٌ عَلَيْكُمْ اِخْرَاجُهُمْ ۗ اَفَتُؤْمِنُوْنَ بِبَعْضِ الْكِتٰبِ وَتَكْفُرُوْنَ بِبَعْضٍۚ فَمَا جَزَاۤءُ مَنْ يَّفْعَلُ ذٰلِكَ مِنْكُمْ اِلَّا خِزْيٌ فِى الْحَيٰوةِ الدُّنْيَا ۚوَيَوْمَ الْقِيٰمَةِ يُرَدُّوْنَ اِلٰٓى اَشَدِّ الْعَذَابِۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ","bc":"ṡumma antum hā`ulā`i taqtulụna anfusakum wa tukhrijụna farīqam mingkum min diyārihim taẓāharụna 'alaihim bil-iṡmi wal-'udwān, wa iy ya`tụkum usārā tufādụhum wa huwa muḥarramun 'alaikum ikhrājuhum, a fa tu`minụna biba'ḍil-kitābi wa takfurụna biba'ḍ, fa mā jazā`u may yaf'alu żālika mingkum illā khizyun fil-ḥayātid-dun-yā, wa yaumal-qiyāmati yuraddụna ilā asyaddil-'ażāb, wa mallāhu bigāfilin 'ammā ta'malụn","at":"Kemudian kamu (Bani Israil) membunuh dirimu (sesamamu), dan mengusir segolongan dari kamu dari kampung halamannya. Kamu saling membantu (menghadapi) mereka dalam kejahatan dan permusuhan. Dan jika mereka datang kepadamu sebagai tawanan, kamu tebus mereka, padahal kamu dilarang mengusir mereka. Apakah kamu beriman kepada sebagian Kitab (Taurat) dan ingkar kepada sebagian (yang lain)? Maka tidak ada balasan (yang pantas) bagi orang yang berbuat demikian di antara kamu selain kenistaan dalam kehidupan dunia, dan pada hari Kiamat mereka dikembalikan kepada azab yang paling berat. Dan Allah tidak lengah terhadap apa yang kamu kerjakan."},{"id":"86","ar":" اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِ ۖ فَلَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْصَرُوْنَ","bc":"ulā`ikallażīnasytarawul-ḥayātad-dun-yā bil-ākhirati fa lā yukhaffafu 'an-humul-'ażābu wa lā hum yunṣarụn","at":"Mereka itulah orang-orang yang membeli kehidupan dunia dengan (kehidupan) akhirat. Maka tidak akan diringankan azabnya dan mereka tidak akan ditolong."},{"id":"87","ar":"وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَقَفَّيْنَا مِنْۢ بَعْدِهٖ بِالرُّسُلِ ۖ وَاٰتَيْنَا عِيْسَى ابْنَ مَرْيَمَ الْبَيِّنٰتِ وَاَيَّدْنٰهُ بِرُوْحِ الْقُدُسِۗ اَفَكُلَّمَا جَاۤءَكُمْ رَسُوْلٌۢ بِمَا لَا تَهْوٰىٓ اَنْفُسُكُمُ اسْتَكْبَرْتُمْ ۚ فَفَرِيْقًا كَذَّبْتُمْ وَفَرِيْقًا تَقْتُلُوْنَ ","bc":"wa laqad ātainā mụsal-kitāba wa qaffainā mim ba'dihī bir-rusuli wa ātainā 'īsabna maryamal-bayyināti wa ayyadnāhu birụḥil-qudus, a fa kullamā jā`akum rasụlum bimā lā tahwā anfusukumustakbartum, fa farīqang każżabtum wa farīqan taqtulụn","at":"Dan sungguh, Kami telah memberikan Kitab (Taurat) kepada Musa, dan Kami susulkan setelahnya dengan rasul-rasul, dan Kami telah berikan kepada Isa putra Maryam bukti-bukti kebenaran serta Kami perkuat dia dengan Rohulkudus (Jibril). Mengapa setiap rasul yang datang kepadamu (membawa) sesuatu (pelajaran) yang tidak kamu inginkan, kamu menyombongkan diri, lalu sebagian kamu dustakan dan sebagian kamu bunuh?"},{"id":"88","ar":"وَقَالُوْا قُلُوْبُنَا غُلْفٌ ۗ بَلْ لَّعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَقَلِيْلًا مَّا يُؤْمِنُوْنَ ","bc":"wa qālụ qulụbunā gulf, bal la'anahumullāhu bikufrihim fa qalīlam mā yu`minụn","at":"Dan mereka berkata, “Hati kami tertutup.” Tidak! Allah telah melaknat mereka itu karena keingkaran mereka, tetapi sedikit sekali mereka yang beriman."},{"id":"89","ar":"وَلَمَّا جَاۤءَهُمْ كِتٰبٌ مِّنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِّمَا مَعَهُمْۙ وَكَانُوْا مِنْ قَبْلُ يَسْتَفْتِحُوْنَ عَلَى الَّذِيْنَ كَفَرُوْاۚ فَلَمَّا جَاۤءَهُمْ مَّا عَرَفُوْا كَفَرُوْا بِهٖ ۖ فَلَعْنَةُ اللّٰهِ عَلَى الْكٰفِرِيْنَ ","bc":"wa lammā jā`ahum kitābum min 'indillāhi muṣaddiqul limā ma'ahum wa kānụ ming qablu yastaftiḥụna 'alallażīna kafarụ, fa lammā jā`ahum mā 'arafụ kafarụ bihī fa la'natullāhi 'alal-kāfirīn","at":"Dan setelah sampai kepada mereka Kitab (Al-Qur'an) dari Allah yang membenarkan apa yang ada pada mereka sedangkan sebelumnya mereka memohon kemenangan atas orang-orang kafir, ternyata setelah sampai kepada mereka apa yang telah mereka ketahui itu, mereka mengingkarinya. Maka laknat Allah bagi orang-orang yang ingkar."},{"id":"90","ar":" بِئْسَمَا اشْتَرَوْا بِهٖٓ اَنْفُسَهُمْ اَنْ يَّكْفُرُوْا بِمَآ اَنْزَلَ اللّٰهُ بَغْيًا اَنْ يُّنَزِّلَ اللّٰهُ مِنْ فَضْلِهٖ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۚ فَبَاۤءُوْ بِغَضَبٍ عَلٰى غَضَبٍۗ وَلِلْكٰفِرِيْنَ عَذَابٌ مُّهِيْنٌ","bc":"bi`samasytarau bihī anfusahum ay yakfurụ bimā anzalallāhu bagyan ay yunazzilallāhu min faḍlihī 'alā may yasyā`u min 'ibādih, fa bā`ụ bigaḍabin 'alā gaḍab, wa lil-kāfirīna 'ażābum muhīn","at":"Sangatlah buruk (perbuatan) mereka menjual dirinya, dengan mengingkari apa yang diturunkan Allah, karena dengki bahwa Allah menurunkan karunia-Nya kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya. Karena itulah mereka menanggung kemurkaan demi kemurkaan. Dan kepada orang-orang kafir (ditimpakan) azab yang menghinakan."},{"id":"91","ar":"وَاِذَا قِيْلَ لَهُمْ اٰمِنُوْا بِمَآ اَنْزَلَ اللّٰهُ قَالُوْا نُؤْمِنُ بِمَآ اُنْزِلَ عَلَيْنَا وَيَكْفُرُوْنَ بِمَا وَرَاۤءَهٗ وَهُوَ الْحَقُّ مُصَدِّقًا لِّمَا مَعَهُمْ ۗ قُلْ فَلِمَ تَقْتُلُوْنَ اَنْۢبِيَاۤءَ اللّٰهِ مِنْ قَبْلُ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ","bc":"wa iżā qīla lahum āminụ bimā anzalallāhu qālụ nu`minu bimā unzila 'alainā wa yakfurụna bimā warā`ahụ wa huwal-ḥaqqu muṣaddiqal limā ma'ahum, qul fa lima taqtulụna ambiyā`allāhi ming qablu ing kuntum mu`minīn","at":"Dan apabila dikatakan kepada mereka, “Berimanlah kepada apa yang diturunkan Allah (Al-Qur'an),” mereka menjawab, “Kami beriman kepada apa yang diturunkan kepada kami.” Dan mereka ingkar kepada apa yang setelahnya, padahal (Al-Qur'an) itu adalah yang hak yang membenarkan apa yang ada pada mereka. Katakanlah (Muhammad), “Mengapa kamu dahulu membunuh nabi-nabi Allah jika kamu orang-orang beriman?”"},{"id":"92","ar":"۞ وَلَقَدْ جَاۤءَكُمْ مُّوْسٰى بِالْبَيِّنٰتِ ثُمَّ اتَّخَذْتُمُ الْعِجْلَ مِنْۢ بَعْدِهٖ وَاَنْتُمْ ظٰلِمُوْنَ","bc":"wa laqad jā`akum mụsā bil-bayyināti ṡummattakhażtumul-'ijla mim ba'dihī wa antum ẓālimụn","at":"Dan sungguh, Musa telah datang kepadamu dengan bukti-bukti kebenaran, kemudian kamu mengambil (patung) anak sapi (sebagai sesembahan) setelah (kepergian)nya, dan kamu (menjadi) orang-orang zalim."},{"id":"93","ar":"وَاِذْ اَخَذْنَا مِيْثَاقَكُمْ وَرَفَعْنَا فَوْقَكُمُ الطُّوْرَۗ خُذُوْا مَآ اٰتَيْنٰكُمْ بِقُوَّةٍ وَّاسْمَعُوْا ۗ قَالُوْا سَمِعْنَا وَعَصَيْنَا وَاُشْرِبُوْا فِيْ قُلُوْبِهِمُ الْعِجْلَ بِكُفْرِهِمْ ۗ قُلْ بِئْسَمَا يَأْمُرُكُمْ بِهٖٓ اِيْمَانُكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ","bc":"wa iż akhażnā mīṡāqakum wa rafa'nā fauqakumuṭ-ṭụr, khużụ mā ātainākum biquwwatiw wasma'ụ, qālụ sami'nā wa 'aṣainā wa usyribụ fī qulụbihimul-'ijla bikufrihim, qul bi`samā ya`murukum bihī īmānukum ing kuntum mu`minīn","at":"Dan (ingatlah) ketika Kami mengambil janji kamu dan Kami angkat gunung (Sinai) di atasmu (seraya berfirman), “Pegang teguhlah apa yang Kami berikan kepadamu dan dengarkanlah!” Mereka menjawab, “Kami mendengarkan tetapi kami tidak menaati.” Dan diresapkanlah ke dalam hati mereka itu (kecintaan menyembah patung) anak sapi karena kekafiran mereka. Katakanlah, “Sangat buruk apa yang diperintahkan oleh kepercayaanmu kepadamu jika kamu orang-orang beriman!”"},{"id":"94","ar":" قُلْ اِنْ كَانَتْ لَكُمُ الدَّارُ الْاٰخِرَةُ عِنْدَ اللّٰهِ خَالِصَةً مِّنْ دُوْنِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"qul ing kānat lakumud-dārul-ākhiratu 'indallāhi khāliṣatam min dụnin-nāsi fa tamannawul-mauta ing kuntum ṣādiqīn","at":"Katakanlah (Muhammad), “Jika negeri akhirat di sisi Allah, khusus untukmu saja bukan untuk orang lain, maka mintalah kematian jika kamu orang yang benar.”"},{"id":"95","ar":"وَلَنْ يَّتَمَنَّوْهُ اَبَدًاۢ بِمَا قَدَّمَتْ اَيْدِيْهِمْ ۗ وَاللّٰهُ عَلِيْمٌ ۢ بِالظّٰلِمِيْنَ ","bc":"wa lay yatamannauhu abadam bimā qaddamat aidīhim, wallāhu 'alīmum biẓ-ẓālimīn","at":"Tetapi mereka tidak akan menginginkan kematian itu sama sekali, karena dosa-dosa yang telah dilakukan tangan-tangan mereka. Dan Allah Maha Mengetahui orang-orang zalim."},{"id":"96","ar":"وَلَتَجِدَنَّهُمْ اَحْرَصَ النَّاسِ عَلٰى حَيٰوةٍ ۛوَمِنَ الَّذِيْنَ اَشْرَكُوْا ۛيَوَدُّ اَحَدُهُمْ لَوْ يُعَمَّرُ اَلْفَ سَنَةٍۚ وَمَا هُوَ بِمُزَحْزِحِهٖ مِنَ الْعَذَابِ اَنْ يُّعَمَّرَۗ وَاللّٰهُ بَصِيْرٌۢ بِمَا يَعْمَلُوْنَ","bc":"wa latajidannahum aḥraṣan-nāsi 'alā ḥayāh, wa minallażīna asyrakụ yawaddu aḥaduhum lau yu'ammaru alfa sanah, wa mā huwa bimuzaḥziḥihī minal-'ażābi ay yu'ammar, wallāhu baṣīrum bimā ya'malụn","at":"Dan sungguh, engkau (Muhammad) akan mendapati mereka (orang-orang Yahudi), manusia yang paling tamak akan kehidupan (dunia), bahkan (lebih tamak) dari orang-orang musyrik. Masing-masing dari mereka, ingin diberi umur seribu tahun, padahal umur panjang itu tidak akan menjauhkan mereka dari azab. Dan Allah Maha Melihat apa yang mereka kerjakan."},{"id":"97","ar":" قُلْ مَنْ كَانَ عَدُوًّا لِّجِبْرِيْلَ فَاِنَّهٗ نَزَّلَهٗ عَلٰى قَلْبِكَ بِاِذْنِ اللّٰهِ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ وَهُدًى وَّبُشْرٰى لِلْمُؤْمِنِيْنَ ","bc":"qul mang kāna 'aduwwal lijibrīla fa innahụ nazzalahụ 'alā qalbika bi`iżnillāhi muṣaddiqal limā baina yadaihi wa hudaw wa busyrā lil-mu`minīn","at":"Katakanlah (Muhammad), “Barangsiapa menjadi musuh Jibril, maka (ketahuilah) bahwa dialah yang telah menurunkan (Al-Qur'an) ke dalam hatimu dengan izin Allah, membenarkan apa (kitab-kitab) yang terdahulu, dan menjadi petunjuk serta berita gembira bagi orang-orang beriman.”"},{"id":"98","ar":"مَنْ كَانَ عَدُوًّا لِّلّٰهِ وَمَلٰۤىِٕكَتِهٖ وَرُسُلِهٖ وَجِبْرِيْلَ وَمِيْكٰىلَ فَاِنَّ اللّٰهَ عَدُوٌّ لِّلْكٰفِرِيْنَ","bc":"mang kāna 'aduwwal lillāhi wa malā`ikatihī wa rusulihī wa jibrīla wa mīkāla fa innallāha 'aduwwul lil-kāfirīn","at":"Barangsiapa menjadi musuh Allah, malaikat-malaikat-Nya, rasul-rasul-Nya, Jibril dan Mikail, maka sesungguhnya Allah musuh bagi orang-orang kafir."},{"id":"99","ar":"وَلَقَدْ اَنْزَلْنَآ اِلَيْكَ اٰيٰتٍۢ بَيِّنٰتٍۚ وَمَا يَكْفُرُ بِهَآ اِلَّا الْفٰسِقُوْنَ","bc":"wa laqad anzalnā ilaika āyātim bayyināt, wa mā yakfuru bihā illal-fāsiqụn","at":"Dan sungguh, Kami telah menurunkan ayat-ayat yang jelas kepadamu (Muhammad), dan tidaklah ada yang mengingkarinya selain orang-orang fasik."},{"id":"100","ar":"اَوَكُلَّمَا عٰهَدُوْا عَهْدًا نَّبَذَهٗ فَرِيْقٌ مِّنْهُمْ ۗ بَلْ اَكْثَرُهُمْ لَا يُؤْمِنُوْنَ","bc":"a wa kullamā 'āhadụ 'ahdan nabażahụ farīqum min-hum, bal akṡaruhum lā yu`minụn","at":"Dan mengapa setiap kali mereka mengikat janji, sekelompok mereka melanggarnya? Sedangkan sebagian besar mereka tidak beriman."},{"id":"101","ar":" وَلَمَّا جَاۤءَهُمْ رَسُوْلٌ مِّنْ عِنْدِ اللّٰهِ مُصَدِّقٌ لِّمَا مَعَهُمْ نَبَذَ فَرِيْقٌ مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَۙ كِتٰبَ اللّٰهِ وَرَاۤءَ ظُهُوْرِهِمْ كَاَنَّهُمْ لَا يَعْلَمُوْنَۖ","bc":"wa lammā jā`ahum rasụlum min 'indillāhi muṣaddiqul limā ma'ahum nabaża farīqum minallażīna ụtul-kitāba kitāballāhi warā`a ẓuhụrihim ka`annahum lā ya'lamụn","at":"Dan setelah datang kepada mereka seorang Rasul (Muhammad) dari Allah yang membenarkan apa yang ada pada mereka, sebagian dari orang-orang yang diberi Kitab (Taurat) melemparkan Kitab Allah itu ke belakang (punggung), seakan-akan mereka tidak tahu."},{"id":"102","ar":"وَاتَّبَعُوْا مَا تَتْلُوا الشَّيٰطِيْنُ عَلٰى مُلْكِ سُلَيْمٰنَ ۚ وَمَا كَفَرَ سُلَيْمٰنُ وَلٰكِنَّ الشَّيٰطِيْنَ كَفَرُوْا يُعَلِّمُوْنَ النَّاسَ السِّحْرَ وَمَآ اُنْزِلَ عَلَى الْمَلَكَيْنِ بِبَابِلَ هَارُوْتَ وَمَارُوْتَ ۗ وَمَا يُعَلِّمٰنِ مِنْ اَحَدٍ حَتّٰى يَقُوْلَآ اِنَّمَا نَحْنُ فِتْنَةٌ فَلَا تَكْفُرْ ۗ فَيَتَعَلَّمُوْنَ مِنْهُمَا مَا يُفَرِّقُوْنَ بِهٖ بَيْنَ الْمَرْءِ وَزَوْجِهٖ ۗ وَمَا هُمْ بِضَاۤرِّيْنَ بِهٖ مِنْ اَحَدٍ اِلَّا بِاِذْنِ اللّٰهِ ۗ وَيَتَعَلَّمُوْنَ مَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ ۗ وَلَقَدْ عَلِمُوْا لَمَنِ اشْتَرٰىهُ مَا لَهٗ فِى الْاٰخِرَةِ مِنْ خَلَاقٍ ۗ وَلَبِئْسَ مَاشَرَوْا بِهٖٓ اَنْفُسَهُمْ ۗ لَوْ كَانُوْا يَعْلَمُوْنَ ","bc":"wattaba'ụ mā tatlusy-syayāṭīnu 'alā mulki sulaimān, wa mā kafara sulaimānu wa lākinnasy-syayāṭīna kafarụ yu'allimụnan-nāsas-siḥra wa mā unzila 'alal-malakaini bibābila hārụta wa mārụt, wa mā yu'allimāni min aḥadin ḥattā yaqụlā innamā naḥnu fitnatun fa lā takfur, fa yata'allamụna min-humā mā yufarriqụna bihī bainal-mar'i wa zaujih, wa mā hum biḍārrīna bihī min aḥadin illā bi`iżnillāh, wa yata'allamụna mā yaḍurruhum wa lā yanfa'uhum, wa laqad 'alimụ lamanisytarāhu mā lahụ fil-ākhirati min khalāq, wa labi`sa mā syarau bihī anfusahum, lau kānụ ya'lamụn","at":"Dan mereka mengikuti apa yang dibaca oleh setan-setan pada masa kerajaan Sulaiman. Sulaiman itu tidak kafir tetapi setan-setan itulah yang kafir, mereka mengajarkan sihir kepada manusia dan apa yang diturunkan kepada dua malaikat di negeri Babilonia yaitu Harut dan Marut. Padahal keduanya tidak mengajarkan sesuatu kepada seseorang sebelum mengatakan, “Sesungguhnya kami hanyalah cobaan (bagimu), sebab itu janganlah kafir.” Maka mereka mempelajari dari keduanya (malaikat itu) apa yang (dapat) memisahkan antara seorang (suami) dengan istrinya. Mereka tidak akan dapat mencelakakan seseorang dengan sihirnya kecuali dengan izin Allah. Mereka mempelajari sesuatu yang mencelakakan, dan tidak memberi manfaat kepada mereka. Dan sungguh, mereka sudah tahu, barangsiapa membeli (menggunakan sihir) itu, niscaya tidak akan mendapat keuntungan di akhirat. Dan sungguh, sangatlah buruk perbuatan mereka yang menjual dirinya dengan sihir, sekiranya mereka tahu."},{"id":"103","ar":"وَلَوْ اَنَّهُمْ اٰمَنُوْا وَاتَّقَوْا لَمَثُوْبَةٌ مِّنْ عِنْدِ اللّٰهِ خَيْرٌ ۗ لَوْ كَانُوْا يَعْلَمُوْنَ","bc":"walau annahum āmanụ wattaqau lamaṡụbatum min 'indillāhi khaīr, lau kānụ ya'lamụn","at":"Dan jika mereka beriman dan bertakwa, pahala dari Allah pasti lebih baik, sekiranya mereka tahu."},{"id":"104","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقُوْلُوْا رَاعِنَا وَقُوْلُوا انْظُرْنَا وَاسْمَعُوْا وَلِلْكٰفِرِيْنَ عَذَابٌ اَلِيْمٌ","bc":"yā ayyuhallażīna āmanụ lā taqụlụ rā'inā wa qụlunẓurnā wasma'ụ wa lil-kāfirīna 'ażābun alīm","at":"Wahai orang-orang yang beriman! Janganlah kamu katakan, raa'inaa, tetapi katakanlah, “Unzhurnaa” dan dengarkanlah. Dan orang-orang kafir akan mendapat azab yang pedih."},{"id":"105","ar":"مَا يَوَدُّ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَلَا الْمُشْرِكِيْنَ اَنْ يُّنَزَّلَ عَلَيْكُمْ مِّنْ خَيْرٍ مِّنْ رَّبِّكُمْ ۗ وَاللّٰهُ يَخْتَصُّ بِرَحْمَتِهٖ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ ","bc":"mā yawaddullażīna kafarụ min ahlil-kitābi wa lal-musyrikīna ay yunazzala 'alaikum min khairim mir rabbikum, wallāhu yakhtaṣṣu biraḥmatihī may yasyā`, wallāhu żul-faḍlil-'aẓīm","at":"Orang-orang yang kafir dari Ahli Kitab dan orang-orang musyrik tidak menginginkan diturunkannya kepadamu suatu kebaikan dari Tuhanmu. Tetapi secara khusus Allah memberikan rahmat-Nya kepada orang yang Dia kehendaki. Dan Allah pemilik karunia yang besar."},{"id":"106","ar":"۞ مَا نَنْسَخْ مِنْ اٰيَةٍ اَوْ نُنْسِهَا نَأْتِ بِخَيْرٍ مِّنْهَآ اَوْ مِثْلِهَا ۗ اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ","bc":"mā nansakh min āyatin au nunsihā na`ti bikhairim min-hā au miṡlihā, a lam ta'lam annallāha 'alā kulli syai`ing qadīr","at":"Ayat yang Kami batalkan atau Kami hilangkan dari ingatan, pasti Kami ganti dengan yang lebih baik atau yang sebanding dengannya. Tidakkah kamu tahu bahwa Allah Mahakuasa atas segala sesuatu?"},{"id":"107","ar":"اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ ۗ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ","bc":"a lam ta'lam annallāha lahụ mulkus-samāwāti wal-arḍ, wa mā lakum min dụnillāhi miw waliyyiw wa lā naṣīr","at":"Tidakkah kamu tahu bahwa Allah memiliki kerajaan langit dan bumi? Dan tidak ada bagimu pelindung dan penolong selain Allah."},{"id":"108","ar":"اَمْ تُرِيْدُوْنَ اَنْ تَسْـَٔلُوْا رَسُوْلَكُمْ كَمَا سُىِٕلَ مُوْسٰى مِنْ قَبْلُ ۗوَمَنْ يَّتَبَدَّلِ الْكُفْرَ بِالْاِيْمَانِ فَقَدْ ضَلَّ سَوَاۤءَ السَّبِيْلِ","bc":"am turīdụna an tas`alụ rasụlakum kamā su`ila mụsā ming qabl, wa may yatabaddalil-kufra bil-īmāni fa qad ḍalla sawā`as-sabīl","at":"Ataukah kamu hendak meminta kepada Rasulmu (Muhammad) seperti halnya Musa (pernah) diminta (Bani Israil) dahulu? Barangsiapa mengganti iman dengan kekafiran, maka sungguh, dia telah tersesat dari jalan yang lurus."},{"id":"109","ar":"وَدَّ كَثِيْرٌ مِّنْ اَهْلِ الْكِتٰبِ لَوْ يَرُدُّوْنَكُمْ مِّنْۢ بَعْدِ اِيْمَانِكُمْ كُفَّارًاۚ حَسَدًا مِّنْ عِنْدِ اَنْفُسِهِمْ مِّنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْحَقُّ ۚ فَاعْفُوْا وَاصْفَحُوْا حَتّٰى يَأْتِيَ اللّٰهُ بِاَمْرِهٖ ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"wadda kaṡīrum min ahlil-kitābi lau yaruddụnakum mim ba'di īmānikum kuffārā, ḥasadam min 'indi anfusihim mim ba'di mā tabayyana lahumul-ḥaqq, fa'fụ waṣfaḥụ ḥattā ya`tiyallāhu bi`amrih, innallāha 'alā kulli syai`ing qadīr","at":"Banyak di antara Ahli Kitab menginginkan sekiranya mereka dapat mengembalikan kamu setelah kamu beriman, menjadi kafir kembali, karena rasa dengki dalam diri mereka, setelah kebenaran jelas bagi mereka. Maka maafkanlah dan berlapangdadalah, sampai Allah memberikan perintah-Nya. Sungguh, Allah Mahakuasa atas segala sesuatu."},{"id":"110","ar":" وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ ۗ وَمَا تُقَدِّمُوْا لِاَنْفُسِكُمْ مِّنْ خَيْرٍ تَجِدُوْهُ عِنْدَ اللّٰهِ ۗ اِنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ بَصِيْرٌ","bc":"wa aqīmuṣ-ṣalāta wa ātuz-zakāh, wa mā tuqaddimụ li`anfusikum min khairin tajidụhu 'indallāh, innallāha bimā ta'malụna baṣīr","at":"Dan laksanakanlah salat dan tunaikanlah zakat. Dan segala kebaikan yang kamu kerjakan untuk dirimu, kamu akan mendapatkannya (pahala) di sisi Allah. Sungguh, Allah Maha Melihat apa yang kamu kerjakan."},{"id":"111","ar":"وَقَالُوْا لَنْ يَّدْخُلَ الْجَنَّةَ اِلَّا مَنْ كَانَ هُوْدًا اَوْ نَصٰرٰى ۗ تِلْكَ اَمَانِيُّهُمْ ۗ قُلْ هَاتُوْا بُرْهَانَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ","bc":"wa qālụ lay yadkhulal-jannata illā mang kāna hụdan au naṣārā, tilka amāniyyuhum, qul hātụ bur-hānakum ing kuntum ṣādiqīn","at":"Dan mereka (Yahudi dan Nasrani) berkata, “Tidak akan masuk surga kecuali orang Yahudi atau Nasrani.” Itu (hanya) angan-angan mereka. Katakanlah, “Tunjukkan bukti kebenaranmu jika kamu orang yang benar.”"},{"id":"112","ar":"بَلٰى مَنْ اَسْلَمَ وَجْهَهٗ لِلّٰهِ وَهُوَ مُحْسِنٌ فَلَهٗٓ اَجْرُهٗ عِنْدَ رَبِّهٖۖ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ","bc":"balā man aslama waj-hahụ lillāhi wa huwa muḥsinun fa lahū ajruhụ 'inda rabbihī wa lā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Tidak! Barangsiapa menyerahkan diri sepenuhnya kepada Allah, dan dia berbuat baik, dia mendapat pahala di sisi Tuhannya dan tidak ada rasa takut pada mereka dan mereka tidak bersedih hati."},{"id":"113","ar":"وَقَالَتِ الْيَهُوْدُ لَيْسَتِ النَّصٰرٰى عَلٰى شَيْءٍۖ وَّقَالَتِ النَّصٰرٰى لَيْسَتِ الْيَهُوْدُ عَلٰى شَيْءٍۙ وَّهُمْ يَتْلُوْنَ الْكِتٰبَۗ كَذٰلِكَ قَالَ الَّذِيْنَ لَا يَعْلَمُوْنَ مِثْلَ قَوْلِهِمْ ۚ فَاللّٰهُ يَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ ","bc":"wa qālatil-yahụdu laisatin-naṣārā 'alā syai`iw wa qālatin-naṣārā laisatil-yahụdu 'alā syai`iw wa hum yatlụnal-kitāb, każālika qālallażīna lā ya'lamụna miṡla qaulihim, fallāhu yaḥkumu bainahum yaumal-qiyāmati fīmā kānụ fīhi yakhtalifụn","at":"Dan orang Yahudi berkata, “Orang Nasrani itu tidak memiliki sesuatu (pegangan),” dan orang-orang Nasrani (juga) berkata, “Orang-orang Yahudi tidak memiliki sesuatu (pegangan),” padahal mereka membaca Kitab. Demikian pula orang-orang yang tidak berilmu, berkata seperti ucapan mereka itu. Maka Allah akan mengadili mereka pada hari Kiamat, tentang apa yang mereka perselisihkan."},{"id":"114","ar":"وَمَنْ اَظْلَمُ مِمَّنْ مَّنَعَ مَسٰجِدَ اللّٰهِ اَنْ يُّذْكَرَ فِيْهَا اسْمُهٗ وَسَعٰى فِيْ خَرَابِهَاۗ اُولٰۤىِٕكَ مَا كَانَ لَهُمْ اَنْ يَّدْخُلُوْهَآ اِلَّا خَاۤىِٕفِيْنَ ەۗ لَهُمْ فِى الدُّنْيَا خِزْيٌ وَّلَهُمْ فِى الْاٰخِرَةِ عَذَابٌ عَظِيْمٌ","bc":"wa man aẓlamu mim mam mana'a masājidallāhi ay yużkara fīhasmuhụ wa sa'ā fī kharābihā, ulā`ika mā kāna lahum ay yadkhulụhā illā khā`ifīn, lahum fid-dun-yā khizyuw wa lahum fil-ākhirati 'ażābun 'aẓīm","at":"Dan siapakah yang lebih zalim daripada orang yang melarang di dalam masjid-masjid Allah untuk menyebut nama-Nya, dan berusaha merobohkannya? Mereka itu tidak pantas memasukinya kecuali dengan rasa takut (kepada Allah). Mereka mendapat kehinaan di dunia dan di akhirat mendapat azab yang berat."},{"id":"115","ar":"وَلِلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُ فَاَيْنَمَا تُوَلُّوْا فَثَمَّ وَجْهُ اللّٰهِ ۗ اِنَّ اللّٰهَ وَاسِعٌ عَلِيْمٌ ","bc":"wa lillāhil-masyriqu wal-magribu fa ainamā tuwallụ fa ṡamma waj-hullāh, innallāha wāsi'un 'alīm","at":"Dan milik Allah timur dan barat. Kemanapun kamu menghadap di sanalah wajah Allah. Sungguh, Allah Mahaluas, Maha Mengetahui."},{"id":"116","ar":"وَقَالُوا اتَّخَذَ اللّٰهُ وَلَدًا ۙسُبْحٰنَهٗ ۗ بَلْ لَّهٗ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ كُلٌّ لَّهٗ قَانِتُوْنَ ","bc":"wa qāluttakhażallāhu waladan sub-ḥānah, bal lahụ mā fis-samāwāti wal-arḍ, kullul lahụ qānitụn","at":"Dan mereka berkata, “Allah mempunyai anak.” Mahasuci Allah, bahkan milik-Nyalah apa yang di langit dan di bumi. Semua tunduk kepada-Nya."},{"id":"117","ar":"بَدِيْعُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ","bc":"badī'us-samāwāti wal-arḍ, wa iżā qaḍā amran fa innamā yaqụlu lahụ kun fa yakụn","at":"(Allah) pencipta langit dan bumi. Apabila Dia hendak menetapkan sesuatu, Dia hanya berkata kepadanya, “Jadilah!” Maka jadilah sesuatu itu."},{"id":"118","ar":"وَقَالَ الَّذِيْنَ لَا يَعْلَمُوْنَ لَوْلَا يُكَلِّمُنَا اللّٰهُ اَوْ تَأْتِيْنَآ اٰيَةٌ ۗ كَذٰلِكَ قَالَ الَّذِيْنَ مِنْ قَبْلِهِمْ مِّثْلَ قَوْلِهِمْ ۗ تَشَابَهَتْ قُلُوْبُهُمْ ۗ قَدْ بَيَّنَّا الْاٰيٰتِ لِقَوْمٍ يُّوْقِنُوْنَ","bc":"wa qālallażīna lā ya'lamụna lau lā yukallimunallāhu au ta`tīnā āyah, każālika qālallażīna ming qablihim miṡla qaulihim, tasyābahat qulụbuhum, qad bayyannal-āyāti liqaumiy yụqinụn","at":"Dan orang-orang yang tidak mengetahui berkata, “Mengapa Allah tidak berbicara dengan kita atau datang tanda-tanda (kekuasaan-Nya) kepada kita?” Demikian pula orang-orang yang sebelum mereka telah berkata seperti ucapan mereka itu. Hati mereka serupa. Sesungguhnya telah Kami jelaskan tanda-tanda (kekuasaan Kami) kepada orang-orang yang yakin."},{"id":"119","ar":"اِنَّآ اَرْسَلْنٰكَ بِالْحَقِّ بَشِيْرًا وَّنَذِيْرًاۙ وَّلَا تُسْـَٔلُ عَنْ اَصْحٰبِ الْجَحِيْمِ","bc":"innā arsalnāka bil-ḥaqqi basyīraw wa nażīraw wa lā tus`alu 'an aṣ-ḥābil-jaḥīm","at":"Sungguh, Kami telah mengutusmu (Muhammad) dengan kebenaran, sebagai pembawa berita gembira dan pemberi peringatan. Dan engkau tidak akan diminta (pertanggungjawaban) tentang penghuni-penghuni neraka."},{"id":"120","ar":"وَلَنْ تَرْضٰى عَنْكَ الْيَهُوْدُ وَلَا النَّصٰرٰى حَتّٰى تَتَّبِعَ مِلَّتَهُمْ ۗ قُلْ اِنَّ هُدَى اللّٰهِ هُوَ الْهُدٰى ۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ بَعْدَ الَّذِيْ جَاۤءَكَ مِنَ الْعِلْمِ ۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ","bc":"wa lan tarḍā 'angkal-yahụdu wa lan-naṣārā ḥattā tattabi'a millatahum, qul inna hudallāhi huwal-hudā, wa la`inittaba'ta ahwā`ahum ba'dallażī jā`aka minal-'ilmi mā laka minallāhi miw waliyyiw wa lā naṣīr","at":"Dan orang-orang Yahudi dan Nasrani tidak akan rela kepadamu (Muhammad) sebelum engkau mengikuti agama mereka. Katakanlah, “Sesungguhnya petunjuk Allah itulah petunjuk (yang sebenarnya).” Dan jika engkau mengikuti keinginan mereka setelah ilmu (kebenaran) sampai kepadamu, tidak akan ada bagimu pelindung dan penolong dari Allah."},{"id":"121","ar":"اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَتْلُوْنَهٗ حَقَّ تِلَاوَتِهٖۗ اُولٰۤىِٕكَ يُؤْمِنُوْنَ بِهٖ ۗ وَمَنْ يَّكْفُرْ بِهٖ فَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ","bc":"allażīna ātaināhumul-kitāba yatlụnahụ ḥaqqa tilāwatih, ulā`ika yu`minụna bih, wa may yakfur bihī fa ulā`ika humul-khāsirụn","at":"Orang-orang yang telah Kami beri Kitab, mereka membacanya sebagaimana mestinya, mereka itulah yang beriman kepadanya. Dan barangsiapa ingkar kepadanya, mereka itulah orang-orang yang rugi."},{"id":"122","ar":"يٰبَنِيْٓ اِسْرَاۤءِيْلَ اذْكُرُوْا نِعْمَتِيَ الَّتِيْٓ اَنْعَمْتُ عَلَيْكُمْ وَاَنِّيْ فَضَّلْتُكُمْ عَلَى الْعٰلَمِيْنَ ","bc":"yā banī isrā`īlażkurụ ni'matiyallatī an'amtu 'alaikum wa annī faḍḍaltukum 'alal-'ālamīn","at":"Wahai Bani Israil! Ingatlah nikmat-Ku yang telah Aku berikan kepadamu dan Aku telah melebihkan kamu dari semua umat yang lain di alam ini (pada masa itu)."},{"id":"123","ar":"وَاتَّقُوْا يَوْمًا لَّا تَجْزِيْ نَفْسٌ عَنْ نَّفْسٍ شَيْـًٔا وَّلَا يُقْبَلُ مِنْهَا عَدْلٌ وَّلَا تَنْفَعُهَا شَفَاعَةٌ وَّلَا هُمْ يُنْصَرُوْنَ ","bc":"wattaqụ yaumal lā tajzī nafsun 'an nafsin syai`aw wa lā yuqbalu min-hā 'adluw wa lā tanfa'uhā syafā'atuw wa lā hum yunṣarụn","at":"Dan takutlah kamu pada hari, (ketika) tidak seorang pun dapat menggantikan (membela) orang lain sedikit pun, tebusan tidak diterima, bantuan tidak berguna baginya, dan mereka tidak akan ditolong."},{"id":"124","ar":"۞ وَاِذِ ابْتَلٰٓى اِبْرٰهٖمَ رَبُّهٗ بِكَلِمٰتٍ فَاَتَمَّهُنَّ ۗ قَالَ اِنِّيْ جَاعِلُكَ لِلنَّاسِ اِمَامًا ۗ قَالَ وَمِنْ ذُرِّيَّتِيْ ۗ قَالَ لَا يَنَالُ عَهْدِى الظّٰلِمِيْنَ","bc":"wa iżibtalā ibrāhīma rabbuhụ bikalimātin fa atammahunn, qāla innī jā'iluka lin-nāsi imāmā, qāla wa min żurriyyatī, qāla lā yanālu 'ahdiẓ-ẓālimīn","at":"Dan (ingatlah), ketika Ibrahim diuji Tuhannya dengan beberapa kalimat, lalu dia melaksanakannya dengan sempurna. Dia (Allah) berfirman, “Sesungguhnya Aku menjadikan engkau sebagai pemimpin bagi seluruh manusia.” Dia (Ibrahim) berkata, “Dan (juga) dari anak cucuku?” Allah berfirman, “(Benar, tetapi) janji-Ku tidak berlaku bagi orang-orang zalim.” "},{"id":"125","ar":"وَاِذْ جَعَلْنَا الْبَيْتَ مَثَابَةً لِّلنَّاسِ وَاَمْنًاۗ وَاتَّخِذُوْا مِنْ مَّقَامِ اِبْرٰهٖمَ مُصَلًّىۗ وَعَهِدْنَآ اِلٰٓى اِبْرٰهٖمَ وَاِسْمٰعِيْلَ اَنْ طَهِّرَا بَيْتِيَ لِلطَّاۤىِٕفِيْنَ وَالْعٰكِفِيْنَ وَالرُّكَّعِ السُّجُوْدِ ","bc":"wa iż ja'alnal-baita maṡābatal lin-nāsi wa amnā, wattakhiżụ mim maqāmi ibrāhīma muṣallā, wa 'ahidnā ilā ibrāhīma wa ismā'īla an ṭahhirā baitiya liṭ-ṭā`ifīna wal-'ākifīna war-rukka'is-sujụd","at":"Dan (ingatlah), ketika Kami menjadikan rumah (Ka’bah) tempat berkumpul dan tempat yang aman bagi manusia. Dan jadikanlah maqam Ibrahim itu tempat salat. Dan telah Kami perintahkan kepada Ibrahim dan Ismail, “Bersihkanlah rumah-Ku untuk orang-orang yang tawaf, orang yang iktikaf, orang yang rukuk dan orang yang sujud!”"},{"id":"126","ar":"وَاِذْ قَالَ اِبْرٰهٖمُ رَبِّ اجْعَلْ هٰذَا بَلَدًا اٰمِنًا وَّارْزُقْ اَهْلَهٗ مِنَ الثَّمَرٰتِ مَنْ اٰمَنَ مِنْهُمْ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ قَالَ وَمَنْ كَفَرَ فَاُمَتِّعُهٗ قَلِيْلًا ثُمَّ اَضْطَرُّهٗٓ اِلٰى عَذَابِ النَّارِ ۗ وَبِئْسَ الْمَصِيْرُ ","bc":"wa iż qāla ibrāhīmu rabbij'al hāżā baladan āminaw warzuq ahlahụ minaṡ-ṡamarāti man āmana min-hum billāhi wal-yaumil-ākhir, qāla wa mang kafara fa umatti'uhụ qalīlan ṡumma aḍṭarruhū ilā 'ażābin-nār, wa bi`sal-maṣīr","at":"Dan (ingatlah) ketika Ibrahim berdoa, “Ya Tuhanku, jadikanlah (negeri Mekah) ini negeri yang aman dan berilah rezeki berupa buah-buahan kepada penduduknya, yaitu di antara mereka yang beriman kepada Allah dan hari kemudian,” Dia (Allah) berfirman, “Dan kepada orang yang kafir akan Aku beri kesenangan sementara, kemudian akan Aku paksa dia ke dalam azab neraka dan itulah seburuk-buruk tempat kembali.”"},{"id":"127","ar":"وَاِذْ يَرْفَعُ اِبْرٰهٖمُ الْقَوَاعِدَ مِنَ الْبَيْتِ وَاِسْمٰعِيْلُۗ رَبَّنَا تَقَبَّلْ مِنَّا ۗ اِنَّكَ اَنْتَ السَّمِيْعُ الْعَلِيْمُ ","bc":"wa iż yarfa'u ibrāhīmul-qawā'ida minal-baiti wa ismā'īl, rabbanā taqabbal minnā, innaka antas-samī'ul-'alīm","at":"Dan (ingatlah) ketika Ibrahim meninggikan pondasi Baitullah bersama Ismail, (seraya berdoa), “Ya Tuhan kami, terimalah (amal) dari kami. Sungguh, Engkaulah Yang Maha Mendengar, Maha Mengetahui."},{"id":"128","ar":"رَبَّنَا وَاجْعَلْنَا مُسْلِمَيْنِ لَكَ وَمِنْ ذُرِّيَّتِنَآ اُمَّةً مُّسْلِمَةً لَّكَۖ وَاَرِنَا مَنَاسِكَنَا وَتُبْ عَلَيْنَا ۚ اِنَّكَ اَنْتَ التَّوَّابُ الرَّحِيْمُ ","bc":"rabbanā waj'alnā muslimaini laka wa min żurriyyatinā ummatam muslimatal laka wa arinā manāsikanā wa tub 'alainā, innaka antat-tawwābur-raḥīm","at":"Ya Tuhan kami, jadikanlah kami orang yang berserah diri kepada-Mu, dan anak cucu kami (juga) umat yang berserah diri kepada-Mu dan tunjukkanlah kepada kami cara-cara melakukan ibadah (haji) kami, dan terimalah tobat kami. Sungguh, Engkaulah Yang Maha Penerima tobat, Maha Penyayang."},{"id":"129","ar":"رَبَّنَا وَابْعَثْ فِيْهِمْ رَسُوْلًا مِّنْهُمْ يَتْلُوْا عَلَيْهِمْ اٰيٰتِكَ وَيُعَلِّمُهُمُ الْكِتٰبَ وَالْحِكْمَةَ وَيُزَكِّيْهِمْ ۗ اِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُ","bc":"rabbanā wab'aṡ fīhim rasụlam min-hum yatlụ 'alaihim āyātika wa yu'allimuhumul-kitāba wal-ḥikmata wa yuzakkīhim, innaka antal-'azīzul-ḥakīm","at":"Ya Tuhan kami, utuslah di tengah mereka seorang rasul dari kalangan mereka sendiri, yang akan membacakan kepada mereka ayat-ayat-Mu dan mengajarkan Kitab dan Hikmah kepada mereka, dan menyucikan mereka. Sungguh, Engkaulah Yang Mahaperkasa, Mahabijaksana.”"},{"id":"130","ar":"وَمَنْ يَّرْغَبُ عَنْ مِّلَّةِ اِبْرٰهٖمَ اِلَّا مَنْ سَفِهَ نَفْسَهٗ ۗوَلَقَدِ اصْطَفَيْنٰهُ فِى الدُّنْيَا ۚوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ","bc":"wa may yargabu 'am millati ibrāhīma illā man safiha nafsah, wa laqadiṣṭafaināhu fid-dun-yā, wa innahụ fil-ākhirati laminaṣ-ṣāliḥīn","at":"Dan orang yang membenci agama Ibrahim, hanyalah orang yang memperbodoh dirinya sendiri. Dan sungguh, Kami telah memilihnya (Ibrahim) di dunia ini. Dan sesungguhnya di akhirat dia termasuk orang-orang saleh."},{"id":"131","ar":"اِذْ قَالَ لَهٗ رَبُّهٗٓ اَسْلِمْۙ قَالَ اَسْلَمْتُ لِرَبِّ الْعٰلَمِيْنَ","bc":"iż qāla lahụ rabbuhū aslim qāla aslamtu lirabbil-'ālamīn","at":"(Ingatlah) ketika Tuhan berfirman kepadanya (Ibrahim), “Berserahdirilah!” Dia menjawab, “Aku berserah diri kepada Tuhan seluruh alam.”"},{"id":"132","ar":"وَوَصّٰى بِهَآ اِبْرٰهٖمُ بَنِيْهِ وَيَعْقُوْبُۗ يٰبَنِيَّ اِنَّ اللّٰهَ اصْطَفٰى لَكُمُ الدِّيْنَ فَلَا تَمُوْتُنَّ اِلَّا وَاَنْتُمْ مُّسْلِمُوْنَ ۗ","bc":"wa waṣṣā bihā ibrāhīmu banīhi wa ya'qụb, yā baniyya innallāhaṣṭafā lakumud-dīna fa lā tamụtunna illā wa antum muslimụn","at":"Dan Ibrahim mewasiatkan (ucapan) itu kepada anak-anaknya, demikian pula Yakub. “Wahai anak-anakku! Sesungguhnya Allah telah memilih agama ini untukmu, maka janganlah kamu mati kecuali dalam keadaan Muslim.”"},{"id":"133","ar":"اَمْ كُنْتُمْ شُهَدَاۤءَ اِذْ حَضَرَ يَعْقُوْبَ الْمَوْتُۙ اِذْ قَالَ لِبَنِيْهِ مَا تَعْبُدُوْنَ مِنْۢ بَعْدِيْۗ قَالُوْا نَعْبُدُ اِلٰهَكَ وَاِلٰهَ اٰبَاۤىِٕكَ اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ اِلٰهًا وَّاحِدًاۚ وَنَحْنُ لَهٗ مُسْلِمُوْنَ ","bc":"am kuntum syuhadā`a iż ḥaḍara ya'qụbal-mautu iż qāla libanīhi mā ta'budụna mim ba'dī, qālụ na'budu ilāhaka wa ilāha ābā`ika ibrāhīma wa ismā'īla wa is-ḥāqa ilāhaw wāḥidā, wa naḥnu lahụ muslimụn","at":"Apakah kamu menjadi saksi saat maut akan menjemput Yakub, ketika dia berkata kepada anak-anaknya, “Apa yang kamu sembah sepeninggalku?” Mereka menjawab, “Kami akan menyembah Tuhanmu dan Tuhan nenek moyangmu yaitu Ibrahim, Ismail dan Ishak, (yaitu) Tuhan Yang Maha Esa dan kami (hanya) berserah diri kepada-Nya.”"},{"id":"134","ar":"تِلْكَ اُمَّةٌ قَدْ خَلَتْ ۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَّا كَسَبْتُمْ ۚ وَلَا تُسْـَٔلُوْنَ عَمَّا كَانُوْا يَعْمَلُوْنَ","bc":"tilka ummatung qad khalat, lahā mā kasabat wa lakum mā kasabtum, wa lā tus`alụna 'ammā kānụ ya'malụn","at":"Itulah umat yang telah lalu. Baginya apa yang telah mereka usahakan dan bagimu apa yang telah kamu usahakan. Dan kamu tidak akan diminta (pertanggungjawaban) tentang apa yang dahulu mereka kerjakan."},{"id":"135","ar":"وَقَالُوْا كُوْنُوْا هُوْدًا اَوْ نَصٰرٰى تَهْتَدُوْا ۗ قُلْ بَلْ مِلَّةَ اِبْرٰهٖمَ حَنِيْفًا ۗوَمَا كَانَ مِنَ الْمُشْرِكِيْنَ ","bc":"wa qālụ kụnụ hụdan au naṣārā tahtadụ, qul bal millata ibrāhīma ḥanīfā, wa mā kāna minal-musyrikīn","at":"Dan mereka berkata, “Jadilah kamu (penganut) Yahudi atau Nasrani, niscaya kamu mendapat petunjuk.” Katakanlah, “(Tidak!) Tetapi (kami mengikuti) agama Ibrahim yang lurus dan dia tidak termasuk golongan orang yang mempersekutukan Tuhan.”"},{"id":"136","ar":"قُوْلُوْٓا اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْنَا وَمَآ اُنْزِلَ اِلٰٓى اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَمَآ اُوْتِيَ مُوْسٰى وَعِيْسٰى وَمَآ اُوْتِيَ النَّبِيُّوْنَ مِنْ رَّبِّهِمْۚ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِّنْهُمْۖ وَنَحْنُ لَهٗ مُسْلِمُوْنَ ","bc":"qụlū āmannā billāhi wa mā unzila ilainā wa mā unzila ilā ibrāhīma wa ismā'īla wa is-ḥāqa wa ya'qụba wal-asbāṭi wa mā ụtiya mụsā wa 'īsā wa mā ụtiyan-nabiyyụna mir rabbihim, lā nufarriqu baina aḥadim min-hum wa naḥnu lahụ muslimụn","at":"Katakanlah, “Kami beriman kepada Allah dan kepada apa yang diturunkan kepada kami, dan kepada apa yang diturunkan kepada Ibrahim, Ismail, Ishak, Yakub dan anak cucunya, dan kepada apa yang diberikan kepada Musa dan Isa serta kepada apa yang diberikan kepada nabi-nabi dari Tuhan mereka. Kami tidak membeda-bedakan seorang pun di antara mereka, dan kami berserah diri kepada-Nya.”"},{"id":"137","ar":"فَاِنْ اٰمَنُوْا بِمِثْلِ مَآ اٰمَنْتُمْ بِهٖ فَقَدِ اهْتَدَوْا ۚوَاِنْ تَوَلَّوْا فَاِنَّمَا هُمْ فِيْ شِقَاقٍۚ فَسَيَكْفِيْكَهُمُ اللّٰهُ ۚوَهُوَ السَّمِيْعُ الْعَلِيْمُ ۗ ","bc":"fa in āmanụ bimiṡli mā āmantum bihī fa qadihtadau, wa in tawallau fa innamā hum fī syiqāq, fa sayakfīkahumullāh, wa huwas-samī'ul-'alīm","at":"Maka jika mereka telah beriman sebagaimana yang kamu imani, sungguh, mereka telah mendapat petunjuk. Tetapi jika mereka berpaling, sesungguhnya mereka berada dalam permusuhan (denganmu), maka Allah mencukupkan engkau (Muhammad) terhadap mereka (dengan pertolongan-Nya). Dan Dia Maha Mendengar, Maha Mengetahui."},{"id":"138","ar":"صِبْغَةَ اللّٰهِ ۚ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ صِبْغَةً ۖ وَّنَحْنُ لَهٗ عٰبِدُوْنَ ","bc":"ṣibgatallāh, wa man aḥsanu minallāhi ṣibgataw wa naḥnu lahụ 'ābidụn","at":"Sibgah Allah.” Siapa yang lebih baik sibgah-nya daripada Allah? Dan kepada-Nya kami menyembah."},{"id":"139","ar":"قُلْ اَتُحَاۤجُّوْنَنَا فِى اللّٰهِ وَهُوَ رَبُّنَا وَرَبُّكُمْۚ وَلَنَآ اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْۚ وَنَحْنُ لَهٗ مُخْلِصُوْنَ ۙ","bc":"qul a tuḥājjụnanā fillāhi wa huwa rabbunā wa rabbukum, wa lanā a'mālunā wa lakum a'mālukum, wa naḥnu lahụ mukhliṣụn","at":"Katakanlah (Muhammad), “Apakah kamu hendak berdebat dengan kami tentang Allah, padahal Dia adalah Tuhan kami dan Tuhan kamu. Bagi kami amalan kami, bagi kamu amalan kamu, dan hanya kepada-Nya kami dengan tulus mengabdikan diri."},{"id":"140","ar":"اَمْ تَقُوْلُوْنَ اِنَّ اِبْرٰهٖمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطَ كَانُوْا هُوْدًا اَوْ نَصٰرٰى ۗ قُلْ ءَاَنْتُمْ اَعْلَمُ اَمِ اللّٰهُ ۗ وَمَنْ اَظْلَمُ مِمَّنْ كَتَمَ شَهَادَةً عِنْدَهٗ مِنَ اللّٰهِ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ","bc":"am taqụlụna inna ibrāhīma wa ismā'īla wa is-ḥāqa wa ya'qụba wal-asbāṭa kānụ hụdan au naṣārā, qul a antum a'lamu amillāh, wa man aẓlamu mim mang katama syahādatan 'indahụ minallāh, wa mallāhu bigāfilin 'ammā ta'malụn","at":"Ataukah kamu (orang-orang Yahudi dan Nasrani) berkata bahwa Ibrahim, Ismail, Ishak, Yakub dan anak cucunya adalah penganut Yahudi atau Nasrani? Katakanlah, “Kamukah yang lebih tahu atau Allah, dan siapakah yang lebih zalim daripada orang yang menyembunyikan kesaksian dari Allah yang ada padanya?” Allah tidak lengah terhadap apa yang kamu kerjakan."},{"id":"141","ar":"تِلْكَ اُمَّةٌ قَدْ خَلَتْ ۚ لَهَا مَا كَسَبَتْ وَلَكُمْ مَّا كَسَبْتُمْ ۚ وَلَا تُسْـَٔلُوْنَ عَمَّا كَانُوْا يَعْمَلُوْنَ ۔","bc":"tilka ummatung qad khalat, lahā mā kasabat wa lakum mā kasabtum, wa lā tus`alụna 'ammā kānụ ya'malụn","at":"Itulah umat yang telah lalu. Baginya apa yang telah mereka usahakan dan bagimu apa yang telah kamu usahakan. Dan kamu tidak akan diminta (pertanggungjawaban) tentang apa yang dahulu mereka kerjakan."},{"id":"142","ar":"۞ سَيَقُوْلُ السُّفَهَاۤءُ مِنَ النَّاسِ مَا وَلّٰىهُمْ عَنْ قِبْلَتِهِمُ الَّتِيْ كَانُوْا عَلَيْهَا ۗ قُلْ لِّلّٰهِ الْمَشْرِقُ وَالْمَغْرِبُۗ يَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"sayaqụlus-sufahā`u minan-nāsi mā wallāhum 'ang qiblatihimullatī kānụ 'alaihā, qul lillāhil-masyriqu wal-magrib, yahdī may yasyā`u ilā ṣirāṭim mustaqīm","at":"Orang-orang yang kurang akal di antara manusia akan berkata, “Apakah yang memalingkan mereka (Muslim) dari kiblat yang dahulu mereka (berkiblat) kepadanya?” Katakanlah (Muhammad), “Milik Allah-lah timur dan barat; Dia memberi petunjuk kepada siapa yang Dia kehendaki ke jalan yang lurus.”"},{"id":"143","ar":"وَكَذٰلِكَ جَعَلْنٰكُمْ اُمَّةً وَّسَطًا لِّتَكُوْنُوْا شُهَدَاۤءَ عَلَى النَّاسِ وَيَكُوْنَ الرَّسُوْلُ عَلَيْكُمْ شَهِيْدًا ۗ وَمَا جَعَلْنَا الْقِبْلَةَ الَّتِيْ كُنْتَ عَلَيْهَآ اِلَّا لِنَعْلَمَ مَنْ يَّتَّبِعُ الرَّسُوْلَ مِمَّنْ يَّنْقَلِبُ عَلٰى عَقِبَيْهِۗ وَاِنْ كَانَتْ لَكَبِيْرَةً اِلَّا عَلَى الَّذِيْنَ هَدَى اللّٰهُ ۗوَمَا كَانَ اللّٰهُ لِيُضِيْعَ اِيْمَانَكُمْ ۗ اِنَّ اللّٰهَ بِالنَّاسِ لَرَءُوْفٌ رَّحِيْمٌ ","bc":"wa każālika ja'alnākum ummataw wasaṭal litakụnụ syuhadā`a 'alan-nāsi wa yakụnar-rasụlu 'alaikum syahīdā, wa mā ja'alnal-qiblatallatī kunta 'alaihā illā lina'lama may yattabi'ur-rasụla mim may yangqalibu 'alā 'aqibaīh, wa ing kānat lakabīratan illā 'alallażīna hadallāh, wa mā kānallāhu liyuḍī'a īmānakum, innallāha bin-nāsi lara`ụfur raḥīm","at":"Dan demikian pula Kami telah menjadikan kamu (umat Islam) ”umat pertengahan” agar kamu menjadi saksi atas (perbuatan) manusia dan agar Rasul (Muhammad) menjadi saksi atas (perbuatan) kamu. Kami tidak menjadikan kiblat yang (dahulu) kamu (berkiblat) kepadanya melainkan agar Kami mengetahui siapa yang mengikuti Rasul dan siapa yang berbalik ke belakang. Sungguh, (pemindahan kiblat) itu sangat berat, kecuali bagi orang yang telah diberi petunjuk oleh Allah. Dan Allah tidak akan menyia-nyiakan imanmu. Sungguh, Allah Maha Pengasih, Maha Penyayang kepada manusia."},{"id":"144","ar":"قَدْ نَرٰى تَقَلُّبَ وَجْهِكَ فِى السَّمَاۤءِۚ فَلَنُوَلِّيَنَّكَ قِبْلَةً تَرْضٰىهَا ۖ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوْا وُجُوْهَكُمْ شَطْرَهٗ ۗ وَاِنَّ الَّذِيْنَ اُوْتُوا الْكِتٰبَ لَيَعْلَمُوْنَ اَنَّهُ الْحَقُّ مِنْ رَّبِّهِمْ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا يَعْمَلُوْنَ","bc":"qad narā taqalluba waj-hika fis-samā`, fa lanuwalliyannaka qiblatan tarḍāhā fa walli waj-haka syaṭral-masjidil-ḥarām, wa ḥaiṡu mā kuntum fa wallụ wujụhakum syaṭrah, wa innallażīna ụtul-kitāba laya'lamụna annahul-ḥaqqu mir rabbihim, wa mallāhu bigāfilin 'ammā ya'malụn","at":"Kami melihat wajahmu (Muhammad) sering menengadah ke langit, maka akan Kami palingkan engkau ke kiblat yang engkau senangi. Maka hadapkanlah wajahmu ke arah Masjidilharam. Dan di mana saja engkau berada, hadapkanlah wajahmu ke arah itu. Dan sesungguhnya orang-orang yang diberi Kitab (Taurat dan Injil) tahu, bahwa (pemindahan kiblat) itu adalah kebenaran dari Tuhan mereka. Dan Allah tidak lengah terhadap apa yang mereka kerjakan."},{"id":"145","ar":"وَلَىِٕنْ اَتَيْتَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ بِكُلِّ اٰيَةٍ مَّا تَبِعُوْا قِبْلَتَكَ ۚ وَمَآ اَنْتَ بِتَابِعٍ قِبْلَتَهُمْ ۚ وَمَا بَعْضُهُمْ بِتَابِعٍ قِبْلَةَ بَعْضٍۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ مِّنْۢ بَعْدِ مَاجَاۤءَكَ مِنَ الْعِلْمِ ۙ اِنَّكَ اِذًا لَّمِنَ الظّٰلِمِيْنَ ۘ ","bc":"wa la`in ataitallażīna ụtul-kitāba bikulli āyatim mā tabi'ụ qiblatak, wa mā anta bitābi'ing qiblatahum, wa mā ba'ḍuhum bitābi'ing qiblata ba'ḍ, wa la`inittaba'ta ahwā`ahum mim ba'di mā jā`aka minal-'ilmi innaka iżal laminaẓ-ẓālimīn","at":"Dan walaupun engkau (Muhammad) memberikan semua ayat (keterangan) kepada orang-orang yang diberi Kitab itu, mereka tidak akan mengikuti kiblatmu, dan engkau pun tidak akan mengikuti kiblat mereka. Sebagian mereka tidak akan mengikuti kiblat sebagian yang lain. Dan jika engkau mengikuti keinginan mereka setelah sampai ilmu kepadamu, niscaya eng-kau termasuk orang-orang zalim."},{"id":"146","ar":"اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَعْرِفُوْنَهٗ كَمَا يَعْرِفُوْنَ اَبْنَاۤءَهُمْ ۗ وَاِنَّ فَرِيْقًا مِّنْهُمْ لَيَكْتُمُوْنَ الْحَقَّ وَهُمْ يَعْلَمُوْنَ","bc":"allażīna ātaināhumul-kitāba ya'rifụnahụ kamā ya'rifụna abnā`ahum, wa inna farīqam min-hum layaktumụnal-ḥaqqa wa hum ya'lamụn","at":"Orang-orang yang telah Kami beri Kitab (Taurat dan Injil) mengenalnya (Muhammad) seperti mereka mengenal anak-anak mereka sendiri. Sesungguhnya sebagian mereka pasti menyembunyikan kebenaran, padahal mereka mengetahui(nya)."},{"id":"147","ar":"اَلْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَ","bc":"al-ḥaqqu mir rabbika fa lā takụnanna minal-mumtarīn","at":"Kebenaran itu dari Tuhanmu, maka janganlah sekali-kali engkau (Muhammad) termasuk orang-orang yang ragu."},{"id":"148","ar":"وَلِكُلٍّ وِّجْهَةٌ هُوَ مُوَلِّيْهَا فَاسْتَبِقُوا الْخَيْرٰتِۗ اَيْنَ مَا تَكُوْنُوْا يَأْتِ بِكُمُ اللّٰهُ جَمِيْعًا ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"wa likulliw wij-hatun huwa muwallīhā fastabiqul-khairāt, aina mā takụnụ ya`ti bikumullāhu jamī'ā, innallāha 'alā kulli syai`ing qadīr","at":"Dan setiap umat mempunyai kiblat yang dia menghadap kepadanya. Maka berlomba-lombalah kamu dalam kebaikan. Di mana saja kamu berada, pasti Allah akan mengumpulkan kamu semuanya. Sungguh, Allah Mahakuasa atas segala sesuatu."},{"id":"149","ar":"وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَاِنَّهٗ لَلْحَقُّ مِنْ رَّبِّكَ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ","bc":"wa min ḥaiṡu kharajta fa walli waj-haka syaṭral-masjidil-ḥarām, wa innahụ lal-ḥaqqu mir rabbik, wa mallāhu bigāfilin 'ammā ta'malụn","at":"Dan dari manapun engkau (Muhammad) keluar, hadapkanlah wajahmu ke arah Masjidilharam, sesungguhnya itu benar-benar ketentuan dari Tuhanmu. Allah tidak lengah terhadap apa yang kamu kerjakan."},{"id":"150","ar":"وَمِنْ حَيْثُ خَرَجْتَ فَوَلِّ وَجْهَكَ شَطْرَ الْمَسْجِدِ الْحَرَامِ ۗ وَحَيْثُ مَا كُنْتُمْ فَوَلُّوْا وُجُوْهَكُمْ شَطْرَهٗ ۙ لِئَلَّا يَكُوْنَ لِلنَّاسِ عَلَيْكُمْ حُجَّةٌ اِلَّا الَّذِيْنَ ظَلَمُوْا مِنْهُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِيْ وَلِاُتِمَّ نِعْمَتِيْ عَلَيْكُمْ وَلَعَلَّكُمْ تَهْتَدُوْنَۙ","bc":"wa min ḥaiṡu kharajta fa walli waj-haka syaṭral-masjidil-ḥarām, wa ḥaiṡu mā kuntum fa wallụ wujụhakum syaṭrahụ li`allā yakụna lin-nāsi 'alaikum ḥujjatun illallażīna ẓalamụ min-hum fa lā takhsyauhum wakhsyaunī wa li`utimma ni'matī 'alaikum wa la'allakum tahtadụn","at":"Dan dari manapun engkau (Muhammad) keluar, maka hadapkanlah wajahmu ke arah Masjidilharam. Dan di mana saja kamu berada, maka hadapkanlah wajahmu ke arah itu, agar tidak ada alasan bagi manusia (untuk menentangmu), kecuali orang-orang yang zalim di antara mereka. Janganlah kamu takut kepada mereka, tetapi takutlah kepada-Ku, agar Aku sempurnakan nikmat-Ku kepadamu, dan agar kamu mendapat petunjuk."},{"id":"151","ar":"كَمَآ اَرْسَلْنَا فِيْكُمْ رَسُوْلًا مِّنْكُمْ يَتْلُوْا عَلَيْكُمْ اٰيٰتِنَا وَيُزَكِّيْكُمْ وَيُعَلِّمُكُمُ الْكِتٰبَ وَالْحِكْمَةَ وَيُعَلِّمُكُمْ مَّا لَمْ تَكُوْنُوْا تَعْلَمُوْنَۗ","bc":"kamā arsalnā fīkum rasụlam mingkum yatlụ 'alaikum āyātinā wa yuzakkīkum wa yu'allimukumul-kitāba wal-ḥikmata wa yu'allimukum mā lam takụnụ ta'lamụn","at":"Sebagaimana Kami telah mengutus kepadamu seorang Rasul (Muhammad) dari (kalangan) kamu yang membacakan ayat-ayat Kami, menyucikan kamu, dan mengajarkan kepadamu Kitab (Al-Qur'an) dan Hikmah (Sunnah), serta mengajarkan apa yang belum kamu ketahui."},{"id":"152","ar":"فَاذْكُرُوْنِيْٓ اَذْكُرْكُمْ وَاشْكُرُوْا لِيْ وَلَا تَكْفُرُوْنِ ","bc":"fażkurụnī ażkurkum wasykurụ lī wa lā takfurụn","at":"Maka ingatlah kepada-Ku, Aku pun akan ingat kepadamu. Bersyukurlah kepada-Ku, dan janganlah kamu ingkar kepada-Ku."},{"id":"153","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اسْتَعِيْنُوْا بِالصَّبْرِ وَالصَّلٰوةِ ۗ اِنَّ اللّٰهَ مَعَ الصّٰبِرِيْنَ","bc":"yā ayyuhallażīna āmanusta'īnụ biṣ-ṣabri waṣ-ṣalāh, innallāha ma'aṣ-ṣābirīn","at":"Wahai orang-orang yang beriman! Mohonlah pertolongan (kepada Allah) dengan sabar dan salat. Sungguh, Allah beserta orang-orang yang sabar."},{"id":"154","ar":"وَلَا تَقُوْلُوْا لِمَنْ يُّقْتَلُ فِيْ سَبِيْلِ اللّٰهِ اَمْوَاتٌ ۗ بَلْ اَحْيَاۤءٌ وَّلٰكِنْ لَّا تَشْعُرُوْنَ","bc":"wa lā taqụlụ limay yuqtalu fī sabīlillāhi amwāt, bal aḥyā`uw wa lākil lā tasy'urụn","at":"Dan janganlah kamu mengatakan orang-orang yang terbunuh di jalan Allah (mereka) telah mati. Sebenarnya (mereka) hidup, tetapi kamu tidak menyadarinya."},{"id":"155","ar":"وَلَنَبْلُوَنَّكُمْ بِشَيْءٍ مِّنَ الْخَوْفِ وَالْجُوْعِ وَنَقْصٍ مِّنَ الْاَمْوَالِ وَالْاَنْفُسِ وَالثَّمَرٰتِۗ وَبَشِّرِ الصّٰبِرِيْنَ","bc":"wa lanabluwannakum bisyai`im minal-khaufi wal-jụ'i wa naqṣim minal-amwāli wal-anfusi waṡ-ṡamarāt, wa basysyiriṣ-ṣābirīn","at":"Dan Kami pasti akan menguji kamu dengan sedikit ketakutan, kelaparan, kekurangan harta, jiwa, dan buah-buahan. Dan sampaikanlah kabar gembira kepada orang-orang yang sabar,"},{"id":"156","ar":"اَلَّذِيْنَ اِذَآ اَصَابَتْهُمْ مُّصِيْبَةٌ ۗ قَالُوْٓا اِنَّا لِلّٰهِ وَاِنَّآ اِلَيْهِ رٰجِعُوْنَۗ","bc":"allażīna iżā aṣābat-hum muṣībah, qālū innā lillāhi wa innā ilaihi rāji'ụn","at":"(yaitu) orang-orang yang apabila ditimpa musibah, mereka berkata “Inna lillahi wa inna ilaihi raji‘un” (sesungguhnya kami milik Allah dan kepada-Nyalah kami kembali)."},{"id":"157","ar":"اُولٰۤىِٕكَ عَلَيْهِمْ صَلَوٰتٌ مِّنْ رَّبِّهِمْ وَرَحْمَةٌ ۗوَاُولٰۤىِٕكَ هُمُ الْمُهْتَدُوْنَ","bc":"ulā`ika 'alaihim ṣalawātum mir rabbihim wa raḥmah, wa ulā`ika humul-muhtadụn","at":"Mereka itulah yang memperoleh ampunan dan rahmat dari Tuhannya, dan mereka itulah orang-orang yang mendapat petunjuk."},{"id":"158","ar":"۞ اِنَّ الصَّفَا وَالْمَرْوَةَ مِنْ شَعَاۤىِٕرِ اللّٰهِ ۚ فَمَنْ حَجَّ الْبَيْتَ اَوِ اعْتَمَرَ فَلَا جُنَاحَ عَلَيْهِ اَنْ يَّطَّوَّفَ بِهِمَا ۗ وَمَنْ تَطَوَّعَ خَيْرًاۙ فَاِنَّ اللّٰهَ شَاكِرٌ عَلِيْمٌ ","bc":"innaṣ-ṣafā wal-marwata min sya'ā`irillāh, fa man ḥajjal-baita awi'tamara fa lā junāḥa 'alaihi ay yaṭṭawwafa bihimā, wa man taṭawwa'a khairan fa innallāha syākirun 'alīm","at":"Sesungguhnya Safa dan Marwah merupakan sebagian syi‘ar (agama) Allah. Maka barangsiapa beribadah haji ke Baitullah atau berumrah, tidak ada dosa baginya mengerjakan sa‘i antara keduanya. Dan barangsiapa dengan kerelaan hati mengerjakan kebajikan, maka Allah Maha Mensyukuri, Maha Mengetahui."},{"id":"159","ar":"اِنَّ الَّذِيْنَ يَكْتُمُوْنَ مَآ اَنْزَلْنَا مِنَ الْبَيِّنٰتِ وَالْهُدٰى مِنْۢ بَعْدِ مَا بَيَّنّٰهُ لِلنَّاسِ فِى الْكِتٰبِۙ اُولٰۤىِٕكَ يَلْعَنُهُمُ اللّٰهُ وَيَلْعَنُهُمُ اللّٰعِنُوْنَۙ","bc":"innallażīna yaktumụna mā anzalnā minal-bayyināti wal-hudā mim ba'di mā bayyannāhu lin-nāsi fil-kitābi ulā`ika yal'anuhumullāhu wa yal'anuhumul-lā'inụn","at":"Sungguh, orang-orang yang menyembunyikan apa yang telah Kami turunkan berupa keterangan-keterangan dan petunjuk, setelah Kami jelaskan kepada manusia dalam Kitab (Al-Qur'an), mereka itulah yang dilaknat Allah dan dilaknat (pula) oleh mereka yang melaknat,"},{"id":"160","ar":"اِلَّا الَّذِيْنَ تَابُوْا وَاَصْلَحُوْا وَبَيَّنُوْا فَاُولٰۤىِٕكَ اَتُوْبُ عَلَيْهِمْ ۚ وَاَنَا التَّوَّابُ الرَّحِيْمُ ","bc":"illallażīna tābụ wa aṣlaḥụ wa bayyanụ fa ulā`ika atụbu 'alaihim, wa anat-tawwābur-raḥīm","at":"kecuali mereka yang telah bertobat, mengadakan perbaikan dan menjelaskan(nya), mereka itulah yang Aku terima tobatnya dan Akulah Yang Maha Penerima tobat, Maha Penyayang."},{"id":"161","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا وَمَاتُوْا وَهُمْ كُفَّارٌ اُولٰۤىِٕكَ عَلَيْهِمْ لَعْنَةُ اللّٰهِ وَالْمَلٰۤىِٕكَةِ وَالنَّاسِ اَجْمَعِيْنَۙ","bc":"innallażīna kafarụ wa mātụ wa hum kuffārun ulā`ika 'alaihim la'natullāhi wal-malā`ikati wan-nāsi ajma'īn","at":"Sungguh, orang-orang yang kafir dan mati dalam keadaan kafir, mereka itu mendapat laknat Allah, para malaikat dan manusia seluruhnya,"},{"id":"162","ar":"خٰلِدِيْنَ فِيْهَا ۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُوْنَ","bc":"khālidīna fīhā, lā yukhaffafu 'an-humul-'ażābu wa lā hum yunẓarụn","at":"mereka kekal di dalamnya (laknat), tidak akan diringankan azabnya, dan mereka tidak diberi penangguhan."},{"id":"163","ar":"وَاِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ لَآاِلٰهَ اِلَّا هُوَ الرَّحْمٰنُ الرَّحِيْمُ","bc":"wa ilāhukum ilāhuw wāḥid, lā ilāha illā huwar-raḥmānur-raḥīm","at":"Dan Tuhan kamu adalah Tuhan Yang Maha Esa, tidak ada tuhan selain Dia, Yang Maha Pengasih, Maha Penyayang."},{"id":"164","ar":"اِنَّ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَالْفُلْكِ الَّتِيْ تَجْرِيْ فِى الْبَحْرِ بِمَا يَنْفَعُ النَّاسَ وَمَآ اَنْزَلَ اللّٰهُ مِنَ السَّمَاۤءِ مِنْ مَّاۤءٍ فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَا وَبَثَّ فِيْهَا مِنْ كُلِّ دَاۤبَّةٍ ۖ وَّتَصْرِيْفِ الرِّيٰحِ وَالسَّحَابِ الْمُسَخَّرِ بَيْنَ السَّمَاۤءِ وَالْاَرْضِ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَ","bc":"inna fī khalqis-samāwāti wal-arḍi wakhtilāfil-laili wan-nahāri wal-fulkillatī tajrī fil-baḥri bimā yanfa'un-nāsa wa mā anzalallāhu minas-samā`i mim mā`in fa aḥyā bihil-arḍa ba'da mautihā wa baṡṡa fīhā ming kulli dābbatiw wa taṣrīfir-riyāḥi was-saḥābil-musakhkhari bainas-samā`i wal-arḍi la`āyātil liqaumiy ya'qilụn","at":"Sesungguhnya pada penciptaan langit dan bumi, pergantian malam dan siang, kapal yang berlayar di laut dengan (muatan) yang bermanfaat bagi manusia, apa yang diturunkan Allah dari langit berupa air, lalu dengan itu dihidupkan-Nya bumi setelah mati (kering), dan Dia tebarkan di dalamnya bermacam-macam binatang, dan perkisaran angin dan awan yang dikendalikan antara langit dan bumi, (semua itu) sungguh, merupakan tanda-tanda (kebesaran Allah) bagi orang-orang yang mengerti."},{"id":"165","ar":"وَمِنَ النَّاسِ مَنْ يَّتَّخِذُ مِنْ دُوْنِ اللّٰهِ اَنْدَادًا يُّحِبُّوْنَهُمْ كَحُبِّ اللّٰهِ ۗ وَالَّذِيْنَ اٰمَنُوْٓا اَشَدُّ حُبًّا لِّلّٰهِ ۙوَلَوْ يَرَى الَّذِيْنَ ظَلَمُوْٓا اِذْ يَرَوْنَ الْعَذَابَۙ اَنَّ الْقُوَّةَ لِلّٰهِ جَمِيْعًا ۙوَّاَنَّ اللّٰهَ شَدِيْدُ الْعَذَابِ ","bc":"wa minan-nāsi may yattakhiżu min dụnillāhi andāday yuḥibbụnahum kaḥubbillāh, wallażīna āmanū asyaddu ḥubbal lillāhi walau yarallażīna ẓalamū iż yaraunal-'ażāba annal-quwwata lillāhi jamī'aw wa annallāha syadīdul-'ażāb","at":"Dan di antara manusia ada orang yang menyembah tuhan selain Allah sebagai tandingan, yang mereka cintai seperti mencintai Allah. Adapun orang-orang yang beriman sangat besar cintanya kepada Allah. Sekiranya orang-orang yang berbuat zalim itu melihat, ketika mereka melihat azab (pada hari Kiamat), bahwa kekuatan itu semuanya milik Allah dan bahwa Allah sangat berat azab-Nya (niscaya mereka menyesal)."},{"id":"166","ar":"اِذْ تَبَرَّاَ الَّذِيْنَ اتُّبِعُوْا مِنَ الَّذِيْنَ اتَّبَعُوْا وَرَاَوُا الْعَذَابَ وَتَقَطَّعَتْ بِهِمُ الْاَسْبَابُ","bc":"iż tabarra`allażīnattubi'ụ minallażīnattaba'ụ wa ra`awul-'ażāba wa taqaṭṭa'at bihimul-asbāb","at":"(Yaitu) ketika orang-orang yang diikuti berlepas tangan dari orang-orang yang mengikuti, dan mereka melihat azab, dan (ketika) segala hubungan antara mereka terputus."},{"id":"167","ar":"وَقَالَ الَّذِيْنَ اتَّبَعُوْا لَوْ اَنَّ لَنَا كَرَّةً فَنَتَبَرَّاَ مِنْهُمْ ۗ كَمَا تَبَرَّءُوْا مِنَّا ۗ كَذٰلِكَ يُرِيْهِمُ اللّٰهُ اَعْمَالَهُمْ حَسَرٰتٍ عَلَيْهِمْ ۗ وَمَا هُمْ بِخَارِجِيْنَ مِنَ النَّارِ","bc":"wa qālallażīnattaba'ụ lau anna lanā karratan fa natabarra`a min-hum, kamā tabarra`ụ minnā, każālika yurīhimullāhu a'mālahum ḥasarātin 'alaihim, wa mā hum bikhārijīna minan-nār","at":"Dan orang-orang yang mengikuti berkata, “Sekiranya kami mendapat kesempatan (kembali ke dunia), tentu kami akan berlepas tangan dari mereka, sebagaimana mereka berlepas tangan dari kami.” Demikianlah Allah memperlihatkan kepada mereka amal per-buatan mereka yang menjadi penyesalan mereka. Dan mereka tidak akan keluar dari api neraka."},{"id":"168","ar":"يٰٓاَيُّهَا النَّاسُ كُلُوْا مِمَّا فِى الْاَرْضِ حَلٰلًا طَيِّبًا ۖوَّلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ","bc":"yā ayyuhan-nāsu kulụ mimmā fil-arḍi ḥalālan ṭayyibaw wa lā tattabi'ụ khuṭuwātisy-syaiṭān, innahụ lakum 'aduwwum mubīn","at":"Wahai manusia! Makanlah dari (makanan) yang halal dan baik yang terdapat di bumi, dan janganlah kamu mengikuti langkah-langkah setan. Sungguh, setan itu musuh yang nyata bagimu."},{"id":"169","ar":"اِنَّمَا يَأْمُرُكُمْ بِالسُّوْۤءِ وَالْفَحْشَاۤءِ وَاَنْ تَقُوْلُوْا عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ","bc":"innamā ya`murukum bis-sū`i wal-faḥsyā`i wa an taqụlụ 'alallāhi mā lā ta'lamụn","at":"Sesungguhnya (setan) itu hanya menyuruh kamu agar berbuat jahat dan keji, dan mengatakan apa yang tidak kamu ketahui tentang Allah."},{"id":"170","ar":"وَاِذَا قِيْلَ لَهُمُ اتَّبِعُوْا مَآ اَنْزَلَ اللّٰهُ قَالُوْا بَلْ نَتَّبِعُ مَآ اَلْفَيْنَا عَلَيْهِ اٰبَاۤءَنَا ۗ اَوَلَوْ كَانَ اٰبَاۤؤُهُمْ لَا يَعْقِلُوْنَ شَيْـًٔا وَّلَا يَهْتَدُوْنَ","bc":"wa iżā qīla lahumuttabi'ụ mā anzalallāhu qālụ bal nattabi'u mā alfainā 'alaihi ābā`anā, a walau kāna ābā`uhum lā ya'qilụna syai`aw wa lā yahtadụn","at":"Dan apabila dikatakan kepada mereka, “Ikutilah apa yang telah diturunkan Allah.” Mereka menjawab, “(Tidak!) Kami mengikuti apa yang kami dapati pada nenek moyang kami (melakukannya).” Padahal, nenek moyang mereka itu tidak mengetahui apa pun, dan tidak mendapat petunjuk."},{"id":"171","ar":"وَمَثَلُ الَّذِيْنَ كَفَرُوْا كَمَثَلِ الَّذِيْ يَنْعِقُ بِمَا لَا يَسْمَعُ اِلَّا دُعَاۤءً وَّنِدَاۤءً ۗ صُمٌّ ۢ بُكْمٌ عُمْيٌ فَهُمْ لَا يَعْقِلُوْنَ ","bc":"wa maṡalullażīna kafarụ kamaṡalillażī yan'iqu bimā lā yasma'u illā du'ā`aw wa nidā`ā, ṣummum bukmun 'umyun fa hum lā ya'qilụn","at":"Dan perumpamaan bagi (penyeru) orang yang kafir adalah seperti (penggembala) yang meneriaki (binatang) yang tidak mendengar selain panggilan dan teriakan. (Mereka) tuli, bisu dan buta, maka mereka tidak mengerti."},{"id":"172","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْ وَاشْكُرُوْا لِلّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُوْنَ ","bc":"yā ayyuhallażīna āmanụ kulụ min ṭayyibāti mā razaqnākum wasykurụ lillāhi ing kuntum iyyāhu ta'budụn","at":"Wahai orang-orang yang beriman! Makanlah dari rezeki yang baik yang Kami berikan kepada kamu dan bersyukurlah kepada Allah, jika kamu hanya menyembah kepada-Nya."},{"id":"173","ar":"اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيْرِ وَمَآ اُهِلَّ بِهٖ لِغَيْرِ اللّٰهِ ۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّلَا عَادٍ فَلَآ اِثْمَ عَلَيْهِ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ","bc":"innamā ḥarrama 'alaikumul-maitata wad-dama wa laḥmal-khinzīri wa mā uhilla bihī ligairillāh, fa maniḍṭurra gaira bāgiw wa lā 'ādin fa lā iṡma 'alaīh, innallāha gafụrur raḥīm","at":"Sesungguhnya Dia hanya mengharamkan atasmu bangkai, darah, daging babi, dan (daging) hewan yang disembelih dengan (menyebut nama) selain Allah. Tetapi barangsiapa terpaksa (memakannya), bukan karena menginginkannya dan tidak (pula) melampaui batas, maka tidak ada dosa baginya. Sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"174","ar":"اِنَّ الَّذِيْنَ يَكْتُمُوْنَ مَآ اَنْزَلَ اللّٰهُ مِنَ الْكِتٰبِ وَيَشْتَرُوْنَ بِهٖ ثَمَنًا قَلِيْلًاۙ اُولٰۤىِٕكَ مَا يَأْكُلُوْنَ فِيْ بُطُوْنِهِمْ اِلَّا النَّارَ وَلَا يُكَلِّمُهُمُ اللّٰهُ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكِّيْهِمْ ۚوَلَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"innallażīna yaktumụna mā anzalallāhu minal-kitābi wa yasytarụna bihī ṡamanang qalīlan ulā`ika mā ya`kulụna fī buṭụnihim illan-nāra wa lā yukallimuhumullāhu yaumal-qiyāmati wa lā yuzakkīhim, wa lahum 'ażābun alīm","at":"Sungguh, orang-orang yang menyembunyikan apa yang telah diturunkan Allah, yaitu Kitab, dan menjualnya dengan harga murah, mereka hanya menelan api neraka ke dalam perutnya, dan Allah tidak akan menyapa mereka pada hari Kiamat, dan tidak akan menyucikan mereka. Mereka akan mendapat azab yang sangat pedih."},{"id":"175","ar":"اُولٰۤىِٕكَ الَّذِيْنَ اشْتَرَوُا الضَّلٰلَةَ بِالْهُدٰى وَالْعَذَابَ بِالْمَغْفِرَةِ ۚ فَمَآ اَصْبَرَهُمْ عَلَى النَّارِ ","bc":"ulā`ikallażīnasytarawuḍ-ḍalālata bil-hudā wal-'ażāba bil-magfirah, fa mā aṣbarahum 'alan-nār","at":"Mereka itulah yang membeli kesesatan dengan petunjuk dan azab dengan ampunan. Maka alangkah beraninya mereka menentang api neraka!"},{"id":"176","ar":"ذٰلِكَ بِاَنَّ اللّٰهَ نَزَّلَ الْكِتٰبَ بِالْحَقِّ ۗ وَاِنَّ الَّذِيْنَ اخْتَلَفُوْا فِى الْكِتٰبِ لَفِيْ شِقَاقٍۢ بَعِيْدٍ","bc":"żālika bi`annallāha nazzalal-kitāba bil-ḥaqq, wa innallażīnakhtalafụ fil-kitābi lafī syiqāqim ba'īd","at":"Yang demikian itu karena Allah telah menurunkan Kitab (Al-Qur'an) dengan (membawa) kebenaran, dan sesungguhnya orang-orang yang berselisih paham tentang (kebenaran) Kitab itu, mereka dalam perpecahan yang jauh."},{"id":"177","ar":"۞ لَيْسَ الْبِرَّاَنْ تُوَلُّوْا وُجُوْهَكُمْ قِبَلَ الْمَشْرِقِ وَالْمَغْرِبِ وَلٰكِنَّ الْبِرَّ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَالْمَلٰۤىِٕكَةِ وَالْكِتٰبِ وَالنَّبِيّٖنَ ۚ وَاٰتَى الْمَالَ عَلٰى حُبِّهٖ ذَوِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنَ وَابْنَ السَّبِيْلِۙ وَالسَّاۤىِٕلِيْنَ وَفىِ الرِّقَابِۚ وَاَقَامَ الصَّلٰوةَ وَاٰتَى الزَّكٰوةَ ۚ وَالْمُوْفُوْنَ بِعَهْدِهِمْ اِذَا عَاهَدُوْا ۚ وَالصّٰبِرِيْنَ فِى الْبَأْسَاۤءِ وَالضَّرَّاۤءِ وَحِيْنَ الْبَأْسِۗ اُولٰۤىِٕكَ الَّذِيْنَ صَدَقُوْا ۗوَاُولٰۤىِٕكَ هُمُ الْمُتَّقُوْنَ ","bc":"laisal-birra an tuwallụ wujụhakum qibalal-masyriqi wal-magribi wa lākinnal-birra man āmana billāhi wal-yaumil-ākhiri wal-malā`ikati wal-kitābi wan-nabiyyīn, wa ātal-māla 'alā ḥubbihī żawil-qurbā wal-yatāmā wal-masākīna wabnas-sabīli was-sā`ilīna wa fir-riqāb, wa aqāmaṣ-ṣalāta wa ātaz-zakāh, wal-mụfụna bi'ahdihim iżā 'āhadụ, waṣ-ṣābirīna fil-ba`sā`i waḍ-ḍarrā`i wa ḥīnal-ba`s, ulā`ikallażīna ṣadaqụ, wa ulā`ika humul-muttaqụn","at":"Kebajikan itu bukanlah menghadapkan wajahmu ke arah timur dan ke barat, tetapi kebajikan itu ialah (kebajikan) orang yang beriman kepada Allah, hari akhir, malaikat-malaikat, kitab-kitab, dan nabi-nabi dan memberikan harta yang dicintainya kepada kerabat, anak yatim, orang-orang miskin, orang-orang yang dalam perjalanan (musafir), peminta-minta, dan untuk memerdekakan hamba sahaya, yang melaksanakan salat dan menunaikan zakat, orang-orang yang menepati janji apabila berjanji, dan orang yang sabar dalam kemelaratan, penderitaan dan pada masa peperangan. Mereka itulah orang-orang yang benar, dan mereka itulah orang-orang yang bertakwa."},{"id":"178","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُتِبَ عَلَيْكُمُ الْقِصَاصُ فِى الْقَتْلٰىۗ اَلْحُرُّ بِالْحُرِّ وَالْعَبْدُ بِالْعَبْدِ وَالْاُنْثٰى بِالْاُنْثٰىۗ فَمَنْ عُفِيَ لَهٗ مِنْ اَخِيْهِ شَيْءٌ فَاتِّبَاعٌ ۢبِالْمَعْرُوْفِ وَاَدَاۤءٌ اِلَيْهِ بِاِحْسَانٍ ۗ ذٰلِكَ تَخْفِيْفٌ مِّنْ رَّبِّكُمْ وَرَحْمَةٌ ۗفَمَنِ اعْتَدٰى بَعْدَ ذٰلِكَ فَلَهٗ عَذَابٌ اَلِيْمٌ ","bc":"yā ayyuhallażīna āmanụ kutiba 'alaikumul-qiṣāṣu fil-qatlā, al-ḥurru bil-ḥurri wal-'abdu bil-'abdi wal-unṡā bil-unṡā, fa man 'ufiya lahụ min akhīhi syai`un fattibā'um bil-ma'rụfi wa adā`un ilaihi bi`iḥsān, żālika takhfīfum mir rabbikum wa raḥmah, fa mani'tadā ba'da żālika fa lahụ 'ażābun alīm","at":"Wahai orang-orang yang beriman! Diwajibkan atas kamu (melaksanakan) qisas berkenaan dengan orang yang dibunuh. Orang merdeka dengan orang merdeka, hamba sahaya dengan hamba sahaya, perempuan dengan perempuan. Tetapi barangsiapa memperoleh maaf dari saudaranya, hendaklah dia mengikutinya dengan baik, dan membayar diat (tebusan) kepadanya dengan baik (pula). Yang demikian itu adalah keringanan dan rahmat dari Tuhanmu. Barangsiapa melampaui batas setelah itu, maka ia akan mendapat azab yang sangat pedih."},{"id":"179","ar":"وَلَكُمْ فِى الْقِصَاصِ حَيٰوةٌ يّٰٓاُولِى الْاَلْبَابِ لَعَلَّكُمْ تَتَّقُوْنَ ","bc":"wa lakum fil-qiṣāṣi ḥayātuy yā ulil-albābi la'allakum tattaqụn","at":"Dan dalam qisas itu ada (jaminan) kehidupan bagimu, wahai orang-orang yang berakal, agar kamu bertakwa."},{"id":"180","ar":"كُتِبَ عَلَيْكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ اِنْ تَرَكَ خَيْرًا ۖ ۨالْوَصِيَّةُ لِلْوَالِدَيْنِ وَالْاَقْرَبِيْنَ بِالْمَعْرُوْفِۚ حَقًّا عَلَى الْمُتَّقِيْنَ ۗ ","bc":"kutiba 'alaikum iżā ḥaḍara aḥadakumul-mautu in taraka khairanil-waṣiyyatu lil-wālidaini wal-aqrabīna bil-ma'rụf, ḥaqqan 'alal-muttaqīn","at":"Diwajibkan atas kamu, apabila maut hendak menjemput seseorang di antara kamu, jika dia meninggalkan harta, berwasiat untuk kedua orang tua dan karib kerabat dengan cara yang baik, (sebagai) kewajiban bagi orang-orang yang bertakwa."},{"id":"181","ar":"فَمَنْۢ بَدَّلَهٗ بَعْدَمَا سَمِعَهٗ فَاِنَّمَآ اِثْمُهٗ عَلَى الَّذِيْنَ يُبَدِّلُوْنَهٗ ۗ اِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ ۗ ","bc":"fa mam baddalahụ ba'da mā sami'ahụ fa innamā iṡmuhụ 'alallażīna yubaddilụnah, innallāha samī'un 'alīm","at":"Barangsiapa mengubahnya (wasiat itu), setelah mendengarnya, maka sesungguhnya dosanya hanya bagi orang yang mengubahnya. Sungguh, Allah Maha Mendengar, Maha Mengetahui."},{"id":"182","ar":"فَمَنْ خَافَ مِنْ مُّوْصٍ جَنَفًا اَوْ اِثْمًا فَاَصْلَحَ بَيْنَهُمْ فَلَآ اِثْمَ عَلَيْهِ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"fa man khāfa mim mụṣin janafan au iṡman fa aṣlaḥa bainahum fa lā iṡma 'alaīh, innallāha gafụrur raḥīm","at":"Tetapi barangsiapa khawatir bahwa pemberi wasiat (berlaku) berat sebelah atau berbuat salah, lalu dia mendamaikan antara mereka, maka dia tidak berdosa. Sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"183","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُتِبَ عَلَيْكُمُ الصِّيَامُ كَمَا كُتِبَ عَلَى الَّذِيْنَ مِنْ قَبْلِكُمْ لَعَلَّكُمْ تَتَّقُوْنَۙ","bc":"yā ayyuhallażīna āmanụ kutiba 'alaikumuṣ-ṣiyāmu kamā kutiba 'alallażīna ming qablikum la'allakum tattaqụn","at":"Wahai orang-orang yang beriman! Diwajibkan atas kamu berpuasa sebagaimana diwajibkan atas orang sebelum kamu agar kamu bertakwa,"},{"id":"184","ar":"اَيَّامًا مَّعْدُوْدٰتٍۗ فَمَنْ كَانَ مِنْكُمْ مَّرِيْضًا اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِّنْ اَيَّامٍ اُخَرَ ۗ وَعَلَى الَّذِيْنَ يُطِيْقُوْنَهٗ فِدْيَةٌ طَعَامُ مِسْكِيْنٍۗ فَمَنْ تَطَوَّعَ خَيْرًا فَهُوَ خَيْرٌ لَّهٗ ۗ وَاَنْ تَصُوْمُوْا خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ","bc":"ayyāmam ma'dụdāt, fa mang kāna mingkum marīḍan au 'alā safarin fa 'iddatum min ayyāmin ukhar, wa 'alallażīna yuṭīqụnahụ fidyatun ṭa'āmu miskīn, fa man taṭawwa'a khairan fa huwa khairul lah, wa an taṣụmụ khairul lakum ing kuntum ta'lamụn","at":"(Yaitu) beberapa hari tertentu. Maka barangsiapa di antara kamu sakit atau dalam perjalanan (lalu tidak berpuasa), maka (wajib mengganti) sebanyak hari (yang dia tidak berpuasa itu) pada hari-hari yang lain. Dan bagi orang yang berat menjalankannya, wajib membayar fidyah, yaitu memberi makan seorang miskin. Tetapi barangsiapa dengan kerelaan hati mengerjakan kebajikan, maka itu lebih baik baginya, dan puasamu itu lebih baik bagimu jika kamu mengetahui."},{"id":"185","ar":"شَهْرُ رَمَضَانَ الَّذِيْٓ اُنْزِلَ فِيْهِ الْقُرْاٰنُ هُدًى لِّلنَّاسِ وَبَيِّنٰتٍ مِّنَ الْهُدٰى وَالْفُرْقَانِۚ فَمَنْ شَهِدَ مِنْكُمُ الشَّهْرَ فَلْيَصُمْهُ ۗ وَمَنْ كَانَ مَرِيْضًا اَوْ عَلٰى سَفَرٍ فَعِدَّةٌ مِّنْ اَيَّامٍ اُخَرَ ۗ يُرِيْدُ اللّٰهُ بِكُمُ الْيُسْرَ وَلَا يُرِيْدُ بِكُمُ الْعُسْرَ ۖ وَلِتُكْمِلُوا الْعِدَّةَ وَلِتُكَبِّرُوا اللّٰهَ عَلٰى مَا هَدٰىكُمْ وَلَعَلَّكُمْ تَشْكُرُوْنَ ","bc":"syahru ramaḍānallażī unzila fīhil-qur`ānu hudal lin-nāsi wa bayyinātim minal-hudā wal-furqān, fa man syahida mingkumusy-syahra falyaṣum-h, wa mang kāna marīḍan au 'alā safarin fa 'iddatum min ayyāmin ukhar, yurīdullāhu bikumul-yusra wa lā yurīdu bikumul-'usra wa litukmilul-'iddata wa litukabbirullāha 'alā mā hadākum wa la'allakum tasykurụn","at":"Bulan Ramadan adalah (bulan) yang di dalamnya diturunkan Al-Qur'an, sebagai petunjuk bagi manusia dan penjelasan-penjelasan mengenai petunjuk itu dan pembeda (antara yang benar dan yang batil). Karena itu, barangsiapa di antara kamu ada di bulan itu, maka berpuasalah. Dan barangsiapa sakit atau dalam perjalanan (dia tidak berpuasa), maka (wajib menggantinya), sebanyak hari yang ditinggalkannya itu, pada hari-hari yang lain. Allah menghendaki kemudahan bagimu, dan tidak menghendaki kesukaran bagimu. Hendaklah kamu mencukupkan bilangannya dan mengagungkan Allah atas petunjuk-Nya yang diberikan kepadamu, agar kamu bersyukur."},{"id":"186","ar":"وَاِذَا سَاَلَكَ عِبَادِيْ عَنِّيْ فَاِنِّيْ قَرِيْبٌ ۗ اُجِيْبُ دَعْوَةَ الدَّاعِ اِذَا دَعَانِۙ فَلْيَسْتَجِيْبُوْا لِيْ وَلْيُؤْمِنُوْا بِيْ لَعَلَّهُمْ يَرْشُدُوْنَ ","bc":"wa iżā sa`alaka 'ibādī 'annī fa innī qarīb, ujību da'watad-dā'i iżā da'āni falyastajībụ lī walyu`minụ bī la'allahum yarsyudụn","at":"Dan apabila hamba-hamba-Ku bertanya kepadamu (Muhammad) tentang Aku, maka sesungguhnya Aku dekat. Aku Kabulkan permohonan orang yang berdoa apabila dia berdoa kepada-Ku. Hendaklah mereka itu memenuhi (perintah)-Ku dan beriman kepada-Ku, agar mereka memperoleh kebenaran."},{"id":"187","ar":"اُحِلَّ لَكُمْ لَيْلَةَ الصِّيَامِ الرَّفَثُ اِلٰى نِسَاۤىِٕكُمْ ۗ هُنَّ لِبَاسٌ لَّكُمْ وَاَنْتُمْ لِبَاسٌ لَّهُنَّ ۗ عَلِمَ اللّٰهُ اَنَّكُمْ كُنْتُمْ تَخْتَانُوْنَ اَنْفُسَكُمْ فَتَابَ عَلَيْكُمْ وَعَفَا عَنْكُمْ ۚ فَالْـٰٔنَ بَاشِرُوْهُنَّ وَابْتَغُوْا مَا كَتَبَ اللّٰهُ لَكُمْ ۗ وَكُلُوْا وَاشْرَبُوْا حَتّٰى يَتَبَيَّنَ لَكُمُ الْخَيْطُ الْاَبْيَضُ مِنَ الْخَيْطِ الْاَسْوَدِ مِنَ الْفَجْرِۖ ثُمَّ اَتِمُّوا الصِّيَامَ اِلَى الَّيْلِۚ وَلَا تُبَاشِرُوْهُنَّ وَاَنْتُمْ عَاكِفُوْنَۙ فِى الْمَسٰجِدِ ۗ تِلْكَ حُدُوْدُ اللّٰهِ فَلَا تَقْرَبُوْهَاۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ اٰيٰتِهٖ لِلنَّاسِ لَعَلَّهُمْ يَتَّقُوْنَ","bc":"uḥilla lakum lailataṣ-ṣiyāmir-rafaṡu ilā nisā`ikum, hunna libāsul lakum wa antum libāsul lahunn, 'alimallāhu annakum kuntum takhtānụna anfusakum fa tāba 'alaikum wa 'afā 'angkum, fal-āna bāsyirụhunna wabtagụ mā kataballāhu lakum, wa kulụ wasyrabụ ḥattā yatabayyana lakumul-khaiṭul-abyaḍu minal-khaiṭil-aswadi minal-fajr, ṡumma atimmuṣ-ṣiyāma ilal-laīl, wa lā tubāsyirụhunna wa antum 'ākifụna fil-masājid, tilka ḥudụdullāhi fa lā taqrabụhā, każālika yubayyinullāhu āyātihī lin-nāsi la'allahum yattaqụn","at":"Dihalalkan bagimu pada malam hari puasa bercampur dengan istrimu. Mereka adalah pakaian bagimu, dan kamu adalah pakaian bagi mereka. Allah mengetahui bahwa kamu tidak dapat menahan dirimu sendiri, tetapi Dia menerima tobatmu dan memaafkan kamu. Maka sekarang campurilah mereka dan carilah apa yang telah ditetapkan Allah bagimu. Makan dan minumlah hingga jelas bagimu (perbedaan) antara benang putih dan benang hitam, yaitu fajar. Kemudian sempurnakanlah puasa sampai (datang) malam. Tetapi jangan kamu campuri mereka, ketika kamu beriktikaf dalam masjid. Itulah ketentuan Allah, maka janganlah kamu mendekatinya. Demikianlah Allah menerangkan ayat-ayat-Nya kepada manusia, agar mereka bertakwa."},{"id":"188","ar":"وَلَا تَأْكُلُوْٓا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ وَتُدْلُوْا بِهَآ اِلَى الْحُكَّامِ لِتَأْكُلُوْا فَرِيْقًا مِّنْ اَمْوَالِ النَّاسِ بِالْاِثْمِ وَاَنْتُمْ تَعْلَمُوْنَ","bc":"wa lā ta`kulū amwālakum bainakum bil-bāṭili wa tudlụ bihā ilal-ḥukkāmi lita`kulụ farīqam min amwālin-nāsi bil-iṡmi wa antum ta'lamụn","at":"Dan janganlah kamu makan harta di antara kamu dengan jalan yang batil, dan (janganlah) kamu menyuap dengan harta itu kepada para hakim, dengan maksud agar kamu dapat memakan sebagian harta orang lain itu dengan jalan dosa, padahal kamu mengetahui."},{"id":"189","ar":"۞ يَسـَٔلُوْنَكَ عَنِ الْاَهِلَّةِ ۗ قُلْ هِيَ مَوَاقِيْتُ لِلنَّاسِ وَالْحَجِّ ۗ وَلَيْسَ الْبِرُّ بِاَنْ تَأْتُوا الْبُيُوْتَ مِنْ ظُهُوْرِهَا وَلٰكِنَّ الْبِرَّ مَنِ اتَّقٰىۚ وَأْتُوا الْبُيُوْتَ مِنْ اَبْوَابِهَا ۖ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُوْنَ ","bc":"yas`alụnaka 'anil-ahillah, qul hiya mawāqītu lin-nāsi wal-ḥajj, wa laisal-birru bi`an ta`tul-buyụta min ẓuhụrihā wa lākinnal-birra manittaqā, wa`tul-buyụta min abwābihā wattaqullāha la'allakum tufliḥụn","at":"Mereka bertanya kepadamu (Muhammad) tentang bulan sabit. Katakanlah, “Itu adalah (penunjuk) waktu bagi manusia dan (ibadah) haji.” Dan bukanlah suatu kebajikan memasuki rumah dari atasnya, tetapi kebajikan adalah (kebajikan) orang yang bertakwa. Masukilah rumah-rumah dari pintu-pintunya, dan bertakwalah kepada Allah agar kamu beruntung."},{"id":"190","ar":"وَقَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ الَّذِيْنَ يُقَاتِلُوْنَكُمْ وَلَا تَعْتَدُوْا ۗ اِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَدِيْنَ ","bc":"wa qātilụ fī sabīlillāhillażīna yuqātilụnakum wa lā ta'tadụ, innallāha lā yuḥibbul-mu'tadīn","at":"Dan perangilah di jalan Allah orang-orang yang memerangi kamu, tetapi jangan melampaui batas. Sungguh, Allah tidak menyukai orang-orang yang melampaui batas."},{"id":"191","ar":"وَاقْتُلُوْهُمْ حَيْثُ ثَقِفْتُمُوْهُمْ وَاَخْرِجُوْهُمْ مِّنْ حَيْثُ اَخْرَجُوْكُمْ وَالْفِتْنَةُ اَشَدُّ مِنَ الْقَتْلِ ۚ وَلَا تُقَاتِلُوْهُمْ عِنْدَ الْمَسْجِدِ الْحَرَامِ حَتّٰى يُقٰتِلُوْكُمْ فِيْهِۚ فَاِنْ قٰتَلُوْكُمْ فَاقْتُلُوْهُمْۗ كَذٰلِكَ جَزَاۤءُ الْكٰفِرِيْنَ ","bc":"waqtulụhum ḥaiṡu ṡaqiftumụhum wa akhrijụhum min ḥaiṡu akhrajụkum wal-fitnatu asyaddu minal-qatl, wa lā tuqātilụhum 'indal-masjidil-ḥarāmi ḥattā yuqātilụkum fīh, fa ing qātalụkum faqtulụhum, każālika jazā`ul-kāfirīn","at":"Dan bunuhlah mereka di mana kamu temui mereka, dan usirlah mereka dari mana mereka telah mengusir kamu. Dan fitnah itu lebih kejam daripada pembunuhan. Dan janganlah kamu perangi mereka di Masjidilharam, kecuali jika mereka memerangi kamu di tempat itu. Jika mereka memerangi kamu, maka perangilah mereka. Demikianlah balasan bagi orang kafir."},{"id":"192","ar":"فَاِنِ انْتَهَوْا فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"fa inintahau fa innallāha gafụrur raḥīm","at":"Tetapi jika mereka berhenti, maka sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"193","ar":"وَقٰتِلُوْهُمْ حَتّٰى لَا تَكُوْنَ فِتْنَةٌ وَّيَكُوْنَ الدِّيْنُ لِلّٰهِ ۗ فَاِنِ انْتَهَوْا فَلَا عُدْوَانَ اِلَّا عَلَى الظّٰلِمِيْنَ","bc":"wa qātilụhum ḥattā lā takụna fitnatuw wa yakụnad-dīnu lillāh, fa inintahau fa lā 'udwāna illā 'alaẓ-ẓālimīn","at":"Dan perangilah mereka itu sampai tidak ada lagi fitnah, dan agama hanya bagi Allah semata. Jika mereka berhenti, maka tidak ada (lagi) permusuhan, kecuali terhadap orang-orang zalim."},{"id":"194","ar":"اَلشَّهْرُ الْحَرَامُ بِالشَّهْرِ الْحَرَامِ وَالْحُرُمٰتُ قِصَاصٌۗ فَمَنِ اعْتَدٰى عَلَيْكُمْ فَاعْتَدُوْا عَلَيْهِ بِمِثْلِ مَا اعْتَدٰى عَلَيْكُمْ ۖ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ مَعَ الْمُتَّقِيْنَ ","bc":"asy-syahrul-ḥarāmu bisy-syahril-ḥarāmi wal-ḥurumātu qiṣāṣ, fa mani'tadā 'alaikum fa'tadụ 'alaihi bimiṡli ma'tadā 'alaikum wattaqullāha wa'lamū annallāha ma'al-muttaqīn","at":"Bulan haram dengan bulan haram, dan (terhadap) sesuatu yang dihormati berlaku (hukum) qisas. Oleh sebab itu barangsiapa menyerang kamu, maka seranglah dia setimpal dengan serangannya terhadap kamu. Bertakwalah kepada Allah dan ketahuilah bahwa Allah beserta orang-orang yang bertakwa."},{"id":"195","ar":"وَاَنْفِقُوْا فِيْ سَبِيْلِ اللّٰهِ وَلَا تُلْقُوْا بِاَيْدِيْكُمْ اِلَى التَّهْلُكَةِ ۛ وَاَحْسِنُوْا ۛ اِنَّ اللّٰهَ يُحِبُّ الْمُحْسِنِيْنَ ","bc":"wa anfiqụ fī sabīlillāhi wa lā tulqụ bi`aidīkum ilat-tahlukati wa aḥsinụ, innallāha yuḥibbul-muḥsinīn","at":"Dan infakkanlah (hartamu) di jalan Allah, dan janganlah kamu jatuhkan (diri sendiri) ke dalam kebinasaan dengan tangan sendiri, dan berbuatbaiklah. Sungguh, Allah menyukai orang-orang yang berbuat baik."},{"id":"196","ar":"وَاَتِمُّوا الْحَجَّ وَالْعُمْرَةَ لِلّٰهِ ۗ فَاِنْ اُحْصِرْتُمْ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِۚ وَلَا تَحْلِقُوْا رُءُوْسَكُمْ حَتّٰى يَبْلُغَ الْهَدْيُ مَحِلَّهٗ ۗ فَمَنْ كَانَ مِنْكُمْ مَّرِيْضًا اَوْ بِهٖٓ اَذًى مِّنْ رَّأْسِهٖ فَفِدْيَةٌ مِّنْ صِيَامٍ اَوْ صَدَقَةٍ اَوْ نُسُكٍ ۚ فَاِذَآ اَمِنْتُمْ ۗ فَمَنْ تَمَتَّعَ بِالْعُمْرَةِ اِلَى الْحَجِّ فَمَا اسْتَيْسَرَ مِنَ الْهَدْيِۚ فَمَنْ لَّمْ يَجِدْ فَصِيَامُ ثَلٰثَةِ اَيَّامٍ فِى الْحَجِّ وَسَبْعَةٍ اِذَا رَجَعْتُمْ ۗ تِلْكَ عَشَرَةٌ كَامِلَةٌ ۗذٰلِكَ لِمَنْ لَّمْ يَكُنْ اَهْلُهٗ حَاضِرِى الْمَسْجِدِ الْحَرَامِ ۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ","bc":"wa atimmul-ḥajja wal-'umrata lillāh, fa in uḥṣirtum fa mastaisara minal-hady, wa lā taḥliqụ ru`ụsakum ḥattā yablugal-hadyu maḥillah, fa mang kāna mingkum marīḍan au bihī ażam mir ra`sihī fa fidyatum min ṣiyāmin au ṣadaqatin au nusuk, fa iżā amintum, fa man tamatta'a bil-'umrati ilal-ḥajji fa mastaisara minal-hady, fa mal lam yajid fa ṣiyāmu ṡalāṡati ayyāmin fil-ḥajji wa sab'atin iżā raja'tum, tilka 'asyaratung kāmilah, żālika limal lam yakun ahluhụ ḥāḍiril-masjidil-ḥarām, wattaqullāha wa'lamū annallāha syadīdul-'iqāb","at":"Dan sempurnakanlah ibadah haji dan umrah karena Allah. Tetapi jika kamu terkepung (oleh musuh), maka (sembelihlah) hadyu yang mudah didapat, dan jangan kamu mencukur kepalamu, sebelum hadyu sampai di tempat penyembelihannya. Jika ada di antara kamu yang sakit atau ada gangguan di kepalanya (lalu dia bercukur), maka dia wajib berfidyah, yaitu berpuasa, bersedekah atau berkurban. Apabila kamu dalam keadaan aman, maka barangsiapa mengerjakan umrah sebelum haji, dia (wajib menyembelih) hadyu yang mudah didapat. Tetapi jika dia tidak mendapatkannya, maka dia (wajib) berpuasa tiga hari dalam (musim) haji dan tujuh (hari) setelah kamu kembali. Itu seluruhnya sepuluh (hari). Demikian itu, bagi orang yang keluarganya tidak ada (tinggal) di sekitar Masjidilharam. Bertakwalah kepada Allah dan ketahuilah bahwa Allah sangat keras hukuman-Nya."},{"id":"197","ar":"اَلْحَجُّ اَشْهُرٌ مَّعْلُوْمٰتٌ ۚ فَمَنْ فَرَضَ فِيْهِنَّ الْحَجَّ فَلَا رَفَثَ وَلَا فُسُوْقَ وَلَا جِدَالَ فِى الْحَجِّ ۗ وَمَا تَفْعَلُوْا مِنْ خَيْرٍ يَّعْلَمْهُ اللّٰهُ ۗ وَتَزَوَّدُوْا فَاِنَّ خَيْرَ الزَّادِ التَّقْوٰىۖ وَاتَّقُوْنِ يٰٓاُولِى الْاَلْبَابِ","bc":"al-ḥajju asy-hurum ma'lụmāt, fa man faraḍa fīhinnal-ḥajja fa lā rafaṡa wa lā fusụqa wa lā jidāla fil-ḥajj, wa mā taf'alụ min khairiy ya'lam-hullāh, wa tazawwadụ fa inna khairaz-zādit-taqwā wattaqụni yā ulil-albāb","at":"(Musim) haji itu (pada) bulan-bulan yang telah dimaklumi. Barangsiapa mengerjakan (ibadah) haji dalam (bulan-bulan) itu, maka janganlah dia berkata jorok (rafats), berbuat maksiat dan bertengkar dalam (melakukan ibadah) haji. Segala yang baik yang kamu kerjakan, Allah mengetahuinya. Bawalah bekal, karena sesungguhnya sebaik-baik bekal adalah takwa. Dan bertakwalah kepada-Ku wahai orang-orang yang mempunyai akal sehat!"},{"id":"198","ar":"لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَبْتَغُوْا فَضْلًا مِّنْ رَّبِّكُمْ ۗ فَاِذَآ اَفَضْتُمْ مِّنْ عَرَفَاتٍ فَاذْكُرُوا اللّٰهَ عِنْدَ الْمَشْعَرِ الْحَرَامِ ۖ وَاذْكُرُوْهُ كَمَا هَدٰىكُمْ ۚ وَاِنْ كُنْتُمْ مِّنْ قَبْلِهٖ لَمِنَ الضَّاۤلِّيْنَ ","bc":"laisa 'alaikum junāḥun an tabtagụ faḍlam mir rabbikum, fa iżā afaḍtum min 'arafātin fażkurullāha 'indal-masy'aril-ḥarāmi ważkurụhu kamā hadākum, wa ing kuntum ming qablihī laminaḍ-ḍāllīn","at":"Bukanlah suatu dosa bagimu mencari karunia dari Tuhanmu. Maka apabila kamu bertolak dari Arafah, berzikirlah kepada Allah di Masy’arilharam. Dan berzikirlah kepada-Nya sebagaimana Dia telah memberi petunjuk kepadamu, sekalipun sebelumnya kamu benar-benar termasuk orang yang tidak tahu."},{"id":"199","ar":"ثُمَّ اَفِيْضُوْا مِنْ حَيْثُ اَفَاضَ النَّاسُ وَاسْتَغْفِرُوا اللّٰهَ ۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ","bc":"ṡumma afīḍụ min ḥaiṡu afāḍan-nāsu wastagfirullāh, innallāha gafụrur raḥīm","at":"Kemudian bertolaklah kamu dari tempat orang banyak bertolak (Arafah) dan mohonlah ampunan kepada Allah. Sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"200","ar":"فَاِذَا قَضَيْتُمْ مَّنَاسِكَكُمْ فَاذْكُرُوا اللّٰهَ كَذِكْرِكُمْ اٰبَاۤءَكُمْ اَوْ اَشَدَّ ذِكْرًا ۗ فَمِنَ النَّاسِ مَنْ يَّقُوْلُ رَبَّنَآ اٰتِنَا فِى الدُّنْيَا وَمَا لَهٗ فِى الْاٰخِرَةِ مِنْ خَلَاقٍ ","bc":"fa iżā qaḍaitum manasikakum fażkurullāha każikrikum ābā`akum au asyadda żikrā, fa minan-nāsi may yaqụlu rabbanā ātinā fid-dun-yā wa mā lahụ fil-ākhirati min khalāq","at":"Apabila kamu telah menyelesaikan ibadah haji, maka berzikirlah kepada Allah, sebagaimana kamu menyebut-nyebut nenek moyang kamu, bahkan berzikirlah lebih dari itu. Maka di antara manusia ada yang berdoa, “Ya Tuhan kami, berilah kami (kebaikan) di dunia,” dan di akhirat dia tidak memperoleh bagian apa pun."},{"id":"201","ar":"وَمِنْهُمْ مَّنْ يَّقُوْلُ رَبَّنَآ اٰتِنَا فِى الدُّنْيَا حَسَنَةً وَّفِى الْاٰخِرَةِ حَسَنَةً وَّقِنَا عَذَابَ النَّارِ ","bc":"wa min-hum may yaqụlu rabbanā ātinā fid-dun-yā ḥasanataw wa fil-ākhirati ḥasanataw wa qinā 'ażāban-nār","at":"Dan di antara mereka ada yang berdoa, “Ya Tuhan kami, berilah kami kebaikan di dunia dan kebaikan di akhirat, dan lindungilah kami dari azab neraka.”"},{"id":"202","ar":"اُولٰۤىِٕكَ لَهُمْ نَصِيْبٌ مِّمَّا كَسَبُوْا ۗ وَاللّٰهُ سَرِيْعُ الْحِسَابِ ","bc":"ulā`ika lahum naṣībum mimmā kasabụ, wallāhu sarī'ul-ḥisāb","at":"Mereka itulah yang memperoleh bagian dari apa yang telah mereka kerjakan, dan Allah Mahacepat perhitungan-Nya."},{"id":"203","ar":"۞ وَاذْكُرُوا اللّٰهَ فِيْٓ اَيَّامٍ مَّعْدُوْدٰتٍ ۗ فَمَنْ تَعَجَّلَ فِيْ يَوْمَيْنِ فَلَآ اِثْمَ عَلَيْهِ ۚوَمَنْ تَاَخَّرَ فَلَآ اِثْمَ عَلَيْهِۙ لِمَنِ اتَّقٰىۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّكُمْ اِلَيْهِ تُحْشَرُوْنَ ","bc":"ważkurullāha fī ayyāmim ma'dụdāt, fa man ta'ajjala fī yaumaini fa lā iṡma 'alaīh, wa man ta`akhkhara fa lā iṡma 'alaihi limanittaqā, wattaqullāha wa'lamū annakum ilaihi tuḥsyarụn","at":"Dan berzikirlah kepada Allah pada hari yang telah ditentukan jumlahnya. Barangsiapa mempercepat (meninggalkan Mina) setelah dua hari, maka tidak ada dosa baginya. Dan barangsiapa mengakhirkannya tidak ada dosa (pula) baginya, (yakni) bagi orang yang bertakwa. Dan bertakwalah kepada Allah, dan ketahuilah bahwa kamu akan dikumpulkan-Nya."},{"id":"204","ar":"وَمِنَ النَّاسِ مَنْ يُّعْجِبُكَ قَوْلُهٗ فِى الْحَيٰوةِ الدُّنْيَا وَيُشْهِدُ اللّٰهَ عَلٰى مَا فِيْ قَلْبِهٖ ۙ وَهُوَ اَلَدُّ الْخِصَامِ ","bc":"wa minan-nāsi may yu'jibuka qauluhụ fil-ḥayātid-dun-yā wa yusy-hidullāha 'alā mā fī qalbihī wa huwa aladdul-khiṣām","at":"Dan di antara manusia ada yang pembicaraannya tentang kehidupan dunia mengagumkan engkau (Muhammad), dan dia bersaksi kepada Allah mengenai isi hatinya, padahal dia adalah penentang yang paling keras."},{"id":"205","ar":"وَاِذَا تَوَلّٰى سَعٰى فِى الْاَرْضِ لِيُفْسِدَ فِيْهَا وَيُهْلِكَ الْحَرْثَ وَالنَّسْلَ ۗ وَ اللّٰهُ لَا يُحِبُّ الْفَسَادَ ","bc":"wa iżā tawallā sa'ā fil-arḍi liyufsida fīhā wa yuhlikal-ḥarṡa wan-nasl, wallāhu lā yuḥibbul-fasād","at":"Dan apabila dia berpaling (dari engkau), dia berusaha untuk berbuat kerusakan di bumi, serta merusak tanam-tanaman dan ternak, sedang Allah tidak menyukai kerusakan."},{"id":"206","ar":"وَاِذَا قِيْلَ لَهُ اتَّقِ اللّٰهَ اَخَذَتْهُ الْعِزَّةُ بِالْاِثْمِ فَحَسْبُهٗ جَهَنَّمُ ۗ وَلَبِئْسَ الْمِهَادُ ","bc":"wa iżā qīla lahuttaqillāha akhażat-hul-'izzatu bil-iṡmi fa ḥasbuhụ jahannam, wa labi`sal-mihād","at":"Dan apabila dikatakan kepadanya, “Bertakwalah kepada Allah,” bangkitlah kesombongannya untuk berbuat dosa. Maka pantaslah baginya neraka Jahanam, dan sungguh (Jahanam itu) tempat tinggal yang terburuk."},{"id":"207","ar":"وَمِنَ النَّاسِ مَنْ يَّشْرِيْ نَفْسَهُ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ ۗوَاللّٰهُ رَءُوْفٌۢ بِالْعِبَادِ ","bc":"wa minan-nāsi may yasyrī nafsahubtigā`a marḍātillāh, wallāhu ra`ụfum bil-'ibād","at":"Dan di antara manusia ada orang yang mengorbankan dirinya untuk mencari keridaan Allah. Dan Allah Maha Penyantun kepada hamba-hamba-Nya."},{"id":"208","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا ادْخُلُوْا فِى السِّلْمِ كَاۤفَّةً ۖوَّلَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ","bc":"yā ayyuhallażīna āmanudkhulụ fis-silmi kāffataw wa lā tattabi'ụ khuṭuwātisy-syaiṭān, innahụ lakum 'aduwwum mubīn","at":"Wahai orang-orang yang beriman! Masuklah ke dalam Islam secara keseluruhan, dan janganlah kamu ikuti langkah-langkah setan. Sungguh, ia musuh yang nyata bagimu."},{"id":"209","ar":"فَاِنْ زَلَلْتُمْ مِّنْۢ بَعْدِ مَا جَاۤءَتْكُمُ الْبَيِّنٰتُ فَاعْلَمُوْٓا اَنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ","bc":"fa in zalaltum mim ba'di mā jā`atkumul-bayyinātu fa'lamū annallāha 'azīzun ḥakīm","at":"Tetapi jika kamu tergelincir setelah bukti-bukti yang nyata sampai kepadamu, ketahuilah bahwa Allah Maha-perkasa, Mahabijaksana."},{"id":"210","ar":"هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ يَّأْتِيَهُمُ اللّٰهُ فِيْ ظُلَلٍ مِّنَ الْغَمَامِ وَالْمَلٰۤىِٕكَةُ وَقُضِيَ الْاَمْرُ ۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ","bc":"hal yanẓurụna illā ay ya`tiyahumullāhu fī ẓulalim minal-gamāmi wal-malā`ikatu wa quḍiyal-amr, wa ilallāhi turja'ul-umụr","at":"Tidak ada yang mereka tunggu-tunggu kecuali datangnya (azab) Allah bersama malaikat dalam naungan awan, sedangkan perkara (mereka) telah diputuskan. Dan kepada Allah-lah segala perkara dikembalikan."},{"id":"211","ar":"سَلْ بَنِيْٓ اِسْرَاۤءِيْلَ كَمْ اٰتَيْنٰهُمْ مِّنْ اٰيَةٍ ۢ بَيِّنَةٍ ۗ وَمَنْ يُّبَدِّلْ نِعْمَةَ اللّٰهِ مِنْۢ بَعْدِ مَا جَاۤءَتْهُ فَاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ ","bc":"sal banī isrā`īla kam ātaināhum min āyatim bayyinah, wa may yubaddil ni'matallāhi mim ba'di mā jā`at-hu fa innallāha syadīdul-'iqāb","at":"Tanyakanlah kepada Bani Israil, berapa banyak bukti nyata yang telah Kami berikan kepada mereka. Barangsiapa menukar nikmat Allah setelah (nikmat itu) datang kepadanya, maka sungguh, Allah sangat keras hukuman-Nya."},{"id":"212","ar":"زُيِّنَ لِلَّذِيْنَ كَفَرُوا الْحَيٰوةُ الدُّنْيَا وَيَسْخَرُوْنَ مِنَ الَّذِيْنَ اٰمَنُوْا ۘ وَالَّذِيْنَ اتَّقَوْا فَوْقَهُمْ يَوْمَ الْقِيٰمَةِ ۗ وَاللّٰهُ يَرْزُقُ مَنْ يَّشَاۤءُ بِغَيْرِ حِسَابٍ","bc":"zuyyina lillażīna kafarul-ḥayātud-dun-yā wa yaskharụna minallażīna āmanụ, wallażīnattaqau fauqahum yaumal-qiyāmah, wallāhu yarzuqu may yasyā`u bigairi ḥisāb","at":"Kehidupan dunia dijadikan terasa indah dalam pandangan orang-orang yang kafir, dan mereka menghina orang-orang yang beriman. Padahal orang-orang yang bertakwa itu berada di atas mereka pada hari Kiamat. Dan Allah memberi rezeki kepada orang yang Dia kehendaki tanpa perhitungan."},{"id":"213","ar":"كَانَ النَّاسُ اُمَّةً وَّاحِدَةً ۗ فَبَعَثَ اللّٰهُ النَّبِيّٖنَ مُبَشِّرِيْنَ وَمُنْذِرِيْنَ ۖ وَاَنْزَلَ مَعَهُمُ الْكِتٰبَ بِالْحَقِّ لِيَحْكُمَ بَيْنَ النَّاسِ فِيْمَا اخْتَلَفُوْا فِيْهِ ۗ وَمَا اخْتَلَفَ فِيْهِ اِلَّا الَّذِيْنَ اُوْتُوْهُ مِنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنٰتُ بَغْيًا ۢ بَيْنَهُمْ ۚ فَهَدَى اللّٰهُ الَّذِيْنَ اٰمَنُوْا لِمَا اخْتَلَفُوْا فِيْهِ مِنَ الْحَقِّ بِاِذْنِهٖ ۗ وَاللّٰهُ يَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"kānan-nāsu ummataw wāḥidah, fa ba'aṡallāhun-nabiyyīna mubasysyirīna wa munżirīna wa anzala ma'ahumul-kitāba bil-ḥaqqi liyaḥkuma bainan-nāsi fīmakhtalafụ fīh, wa makhtalafa fīhi illallażīna ụtụhu mim ba'di mā jā`at-humul-bayyinātu bagyam bainahum, fa hadallāhullażīna āmanụ limakhtalafụ fīhi minal-ḥaqqi bi`iżnih, wallāhu yahdī may yasyā`u ilā ṣirāṭim mustaqīm","at":"Manusia itu (dahulunya) satu umat. Lalu Allah mengutus para nabi (untuk) menyampaikan kabar gembira dan peringatan. Dan diturunkan-Nya bersama mereka Kitab yang mengandung kebenaran, untuk memberi keputusan di antara manusia tentang perkara yang mereka perselisihkan. Dan yang berselisih hanyalah orang-orang yang telah diberi (Kitab), setelah bukti-bukti yang nyata sampai kepada mereka, karena kedengkian di antara mereka sendiri. Maka dengan kehendak-Nya, Allah memberi petunjuk kepada mereka yang beriman tentang kebenaran yang mereka perselisihkan. Allah memberi petunjuk kepada siapa yang Dia kehendaki ke jalan yang lurus."},{"id":"214","ar":"اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَأْتِكُمْ مَّثَلُ الَّذِيْنَ خَلَوْا مِنْ قَبْلِكُمْ ۗ مَسَّتْهُمُ الْبَأْسَاۤءُ وَالضَّرَّاۤءُ وَزُلْزِلُوْا حَتّٰى يَقُوْلَ الرَّسُوْلُ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗ مَتٰى نَصْرُ اللّٰهِ ۗ اَلَآ اِنَّ نَصْرَ اللّٰهِ قَرِيْبٌ ","bc":"am ḥasibtum an tadkhulul-jannata wa lammā ya`tikum maṡalullażīna khalau ming qablikum, massat-humul-ba`sā`u waḍ-ḍarrā`u wa zulzilụ ḥattā yaqụlar-rasụlu wallażīna āmanụ ma'ahụ matā naṣrullāh, alā inna naṣrallāhi qarīb","at":"Ataukah kamu mengira bahwa kamu akan masuk surga, padahal belum datang kepadamu (cobaan) seperti (yang dialami) orang-orang terdahulu sebelum kamu. Mereka ditimpa kemelaratan, penderitaan dan diguncang (dengan berbagai cobaan), sehingga Rasul dan orang-orang yang beriman bersamanya berkata, “Kapankah datang pertolongan Allah?” Ingatlah, sesungguhnya pertolongan Allah itu dekat."},{"id":"215","ar":"يَسْـَٔلُوْنَكَ مَاذَا يُنْفِقُوْنَ ۗ قُلْ مَآ اَنْفَقْتُمْ مِّنْ خَيْرٍ فَلِلْوَالِدَيْنِ وَالْاَقْرَبِيْنَ وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَابْنِ السَّبِيْلِ ۗ وَمَا تَفْعَلُوْا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ بِهٖ عَلِيْمٌ ","bc":"yas`alụnaka māżā yunfiqụn, qul mā anfaqtum ming kairin fa lil-wālidaini wal-aqrabīna wal-yatāmā wal-masākīni wabnis-sabīl, wa mā taf'alụ min khairin fa innallāha bihī 'alīm","at":"Mereka bertanya kepadamu (Muhammad) tentang apa yang harus mereka infakkan. Katakanlah, “Harta apa saja yang kamu infakkan, hendaknya diperuntukkan bagi kedua orang tua, kerabat, anak yatim, orang miskin dan orang yang dalam perjalanan.” Dan kebaikan apa saja yang kamu kerjakan, maka sesungguhnya Allah Maha Mengetahui."},{"id":"216","ar":"كُتِبَ عَلَيْكُمُ الْقِتَالُ وَهُوَ كُرْهٌ لَّكُمْ ۚ وَعَسٰٓى اَنْ تَكْرَهُوْا شَيْـًٔا وَّهُوَ خَيْرٌ لَّكُمْ ۚ وَعَسٰٓى اَنْ تُحِبُّوْا شَيْـًٔا وَّهُوَ شَرٌّ لَّكُمْ ۗ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ","bc":"kutiba 'alaikumul-qitālu wa huwa kur-hul lakum, wa 'asā an takrahụ syai`aw wa huwa khairul lakum, wa 'asā an tuḥibbụ syai`aw wa huwa syarrul lakum, wallāhu ya'lamu wa antum lā ta'lamụn","at":"Diwajibkan atas kamu berperang, padahal itu tidak menyenangkan bagimu. Tetapi boleh jadi kamu tidak menyenangi sesuatu, padahal itu baik bagimu, dan boleh jadi kamu menyukai sesuatu, padahal itu tidak baik bagimu. Allah mengetahui, sedang kamu tidak mengetahui."},{"id":"217","ar":"يَسْـَٔلُوْنَكَ عَنِ الشَّهْرِ الْحَرَامِ قِتَالٍ فِيْهِۗ قُلْ قِتَالٌ فِيْهِ كَبِيْرٌ ۗ وَصَدٌّ عَنْ سَبِيْلِ اللّٰهِ وَكُفْرٌۢ بِهٖ وَالْمَسْجِدِ الْحَرَامِ وَاِخْرَاجُ اَهْلِهٖ مِنْهُ اَكْبَرُ عِنْدَ اللّٰهِ ۚ وَالْفِتْنَةُ اَكْبَرُ مِنَ الْقَتْلِ ۗ وَلَا يَزَالُوْنَ يُقَاتِلُوْنَكُمْ حَتّٰى يَرُدُّوْكُمْ عَنْ دِيْنِكُمْ اِنِ اسْتَطَاعُوْا ۗ وَمَنْ يَّرْتَدِدْ مِنْكُمْ عَنْ دِيْنِهٖ فَيَمُتْ وَهُوَ كَافِرٌ فَاُولٰۤىِٕكَ حَبِطَتْ اَعْمَالُهُمْ فِى الدُّنْيَا وَالْاٰخِرَةِ ۚ وَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ ","bc":"yas`alụnaka 'anisy-syahril-ḥarāmi qitālin fīh, qul qitālun fīhi kabīr, wa ṣaddun 'an sabīlillāhi wa kufrum bihī wal-masjidil-ḥarāmi wa ikhrāju ahlihī min-hu akbaru 'indallāh, wal-fitnatu akbaru minal-qatl, wa lā yazālụna yuqātilụnakum ḥattā yaruddụkum 'an dīnikum inistaṭā'ụ, wa may yartadid mingkum 'an dīnihī fa yamut wa huwa kāfirun fa ulā`ika ḥabiṭat a'māluhum fid-dun-yā wal-ākhirah, wa ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn","at":"Mereka bertanya kepadamu (Muhammad) tentang berperang pada bulan haram. Katakanlah, “Berperang dalam bulan itu adalah (dosa) besar. Tetapi menghalangi (orang) dari jalan Allah, ingkar kepada-Nya, (menghalangi orang masuk) Masjidilharam, dan mengusir penduduk dari sekitarnya, lebih besar (dosanya) dalam pandangan Allah. Sedangkan fitnah lebih kejam daripada pembunuhan. Mereka tidak akan berhenti memerangi kamu sampai kamu murtad (keluar) dari agamamu, jika mereka sanggup. Barangsiapa murtad di antara kamu dari agamanya, lalu dia mati dalam kekafiran, maka mereka itu sia-sia amalnya di dunia dan di akhirat, dan mereka itulah penghuni neraka, mereka kekal di dalamnya.”"},{"id":"218","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ ۙ اُولٰۤىِٕكَ يَرْجُوْنَ رَحْمَتَ اللّٰهِ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ","bc":"innallażīna āmanụ wallażīna hājarụ wa jāhadụ fī sabīlillāhi ulā`ika yarjụna raḥmatallāh, wallāhu gafụrur raḥīm","at":"Sesungguhnya orang-orang yang beriman, dan orang-orang yang berhijrah dan berjihad di jalan Allah, mereka itulah yang mengharapkan rahmat Allah. Allah Maha Pengampun, Maha Penyayang."},{"id":"219","ar":"۞ يَسْـَٔلُوْنَكَ عَنِ الْخَمْرِ وَالْمَيْسِرِۗ قُلْ فِيْهِمَآ اِثْمٌ كَبِيْرٌ وَّمَنَافِعُ لِلنَّاسِۖ وَاِثْمُهُمَآ اَكْبَرُ مِنْ نَّفْعِهِمَاۗ وَيَسْـَٔلُوْنَكَ مَاذَا يُنْفِقُوْنَ ەۗ قُلِ الْعَفْوَۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَتَفَكَّرُوْنَۙ","bc":"yas`alụnaka 'anil-khamri wal-maisir, qul fīhimā iṡmung kabīruw wa manafi'u lin-nāsi wa iṡmuhumā akbaru min-naf'ihimā, wa yas`alụnaka māżā yunfiqụn, qulil-'afw, każālika yubayyinullāhu lakumul-āyāti la'allakum tatafakkarụn","at":"Mereka menanyakan kepadamu (Muhammad) tentang khamar dan judi. Katakanlah, “Pada keduanya terdapat dosa besar dan beberapa manfaat bagi manusia. Tetapi dosanya lebih besar daripada manfaatnya.” Dan mereka menanyakan kepadamu (tentang) apa yang (harus) mereka infakkan. Katakanlah, “Kelebihan (dari apa yang diperlukan).” Demikianlah Allah menerangkan ayat-ayat-Nya kepadamu agar kamu memikirkan,"},{"id":"220","ar":"فِى الدُّنْيَا وَالْاٰخِرَةِ ۗ وَيَسْـَٔلُوْنَكَ عَنِ الْيَتٰمٰىۗ قُلْ اِصْلَاحٌ لَّهُمْ خَيْرٌ ۗ وَاِنْ تُخَالِطُوْهُمْ فَاِخْوَانُكُمْ ۗ وَاللّٰهُ يَعْلَمُ الْمُفْسِدَ مِنَ الْمُصْلِحِ ۗ وَلَوْ شَاۤءَ اللّٰهُ لَاَعْنَتَكُمْ اِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ","bc":"fid-dun-yā wal-ākhirah, wa yas`alụnaka 'anil-yatāmā, qul iṣlāḥul lahum khaīr, wa in tukhāliṭụhum fa ikhwānukum, wallāhu ya'lamul-mufsida minal-muṣliḥ, walau syā`allāhu la`a'natakum, innallāha 'azīzun ḥakīm","at":"Tentang dunia dan akhirat. Mereka menanyakan kepadamu (Muhammad) tentang anak-anak yatim. Katakanlah, “Memperbaiki keadaan mereka adalah baik!” Dan jika kamu mempergauli mereka, maka mereka adalah saudara-saudaramu. Allah mengetahui orang yang berbuat kerusakan dan yang berbuat kebaikan. Dan jika Allah menghendaki, niscaya Dia datangkan kesulitan kepadamu. Sungguh, Allah Mahaperkasa, Mahabijaksana."},{"id":"221","ar":"وَلَا تَنْكِحُوا الْمُشْرِكٰتِ حَتّٰى يُؤْمِنَّ ۗ وَلَاَمَةٌ مُّؤْمِنَةٌ خَيْرٌ مِّنْ مُّشْرِكَةٍ وَّلَوْ اَعْجَبَتْكُمْ ۚ وَلَا تُنْكِحُوا الْمُشْرِكِيْنَ حَتّٰى يُؤْمِنُوْا ۗ وَلَعَبْدٌ مُّؤْمِنٌ خَيْرٌ مِّنْ مُّشْرِكٍ وَّلَوْ اَعْجَبَكُمْ ۗ اُولٰۤىِٕكَ يَدْعُوْنَ اِلَى النَّارِ ۖ وَاللّٰهُ يَدْعُوْٓا اِلَى الْجَنَّةِ وَالْمَغْفِرَةِ بِاِذْنِهٖۚ وَيُبَيِّنُ اٰيٰتِهٖ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُوْنَ","bc":"wa lā tangkiḥul-musyrikāti ḥattā yu`minn, wa la`amatum mu`minatun khairum mim musyrikatiw walau a'jabatkum, wa lā tungkiḥul-musyrikīna ḥattā yu`minụ, wa la'abdum mu`minun khairum mim musyrikiw walau a'jabakum, ulā`ika yad'ụna ilan-nāri wallāhu yad'ū ilal-jannati wal-magfirati bi`iżnih, wa yubayyinu āyātihī lin-nāsi la'allahum yatażakkarụn","at":"Dan janganlah kamu nikahi perempuan musyrik, sebelum mereka beriman. Sungguh, hamba sahaya perempuan yang beriman lebih baik daripada perempuan musyrik meskipun dia menarik hatimu. Dan janganlah kamu nikahkan orang (laki-laki) musyrik (dengan perempuan yang beriman) sebelum mereka beriman. Sungguh, hamba sahaya laki-laki yang beriman lebih baik daripada laki-laki musyrik meskipun dia menarik hatimu. Mereka mengajak ke neraka, sedangkan Allah mengajak ke surga dan ampunan dengan izin-Nya. (Allah) menerangkan ayat-ayat-Nya kepada manusia agar mereka mengambil pelajaran."},{"id":"222","ar":"وَيَسْـَٔلُوْنَكَ عَنِ الْمَحِيْضِ ۗ قُلْ هُوَ اَذًىۙ فَاعْتَزِلُوا النِّسَاۤءَ فِى الْمَحِيْضِۙ وَلَا تَقْرَبُوْهُنَّ حَتّٰى يَطْهُرْنَ ۚ فَاِذَا تَطَهَّرْنَ فَأْتُوْهُنَّ مِنْ حَيْثُ اَمَرَكُمُ اللّٰهُ ۗ اِنَّ اللّٰهَ يُحِبُّ التَّوَّابِيْنَ وَيُحِبُّ الْمُتَطَهِّرِيْنَ ","bc":"wa yas`alụnaka 'anil-maḥīḍ, qul huwa ażan fa'tazilun-nisā`a fil-maḥīḍi wa lā taqrabụhunna ḥattā yaṭ-hurn, fa iżā taṭahharna fa`tụhunna min ḥaiṡu amarakumullāh, innallāha yuḥibbut-tawwābīna wa yuḥibbul-mutaṭahhirīn","at":"Dan mereka menanyakan kepadamu (Muhammad) tentang haid. Katakanlah, “Itu adalah sesuatu yang kotor.” Karena itu jauhilah istri pada waktu haid; dan jangan kamu dekati mereka sebelum mereka suci. Apabila mereka telah suci, campurilah mereka sesuai dengan (ketentuan) yang diperintahkan Allah kepadamu. Sungguh, Allah menyukai orang yang tobat dan menyukai orang yang menyucikan diri."},{"id":"223","ar":"نِسَاۤؤُكُمْ حَرْثٌ لَّكُمْ ۖ فَأْتُوْا حَرْثَكُمْ اَنّٰى شِئْتُمْ ۖ وَقَدِّمُوْا لِاَنْفُسِكُمْ ۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّكُمْ مُّلٰقُوْهُ ۗ وَبَشِّرِ الْمُؤْمِنِيْنَ ","bc":"nisā`ukum ḥarṡul lakum fa`tụ ḥarṡakum annā syi`tum wa qaddimụ li`anfusikum, wattaqullāha wa'lamū annakum mulāqụh, wa basysyiril-mu`minīn","at":"Istri-istrimu adalah ladang bagimu, maka datangilah ladangmu itu kapan saja dan dengan cara yang kamu sukai. Dan utamakanlah (yang baik) untuk dirimu. Bertakwalah kepada Allah dan ketahuilah bahwa kamu (kelak) akan menemui-Nya. Dan sampaikanlah kabar gembira kepada orang yang beriman."},{"id":"224","ar":"وَلَا تَجْعَلُوا اللّٰهَ عُرْضَةً لِّاَيْمَانِكُمْ اَنْ تَبَرُّوْا وَتَتَّقُوْا وَتُصْلِحُوْا بَيْنَ النَّاسِۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ","bc":"wa lā taj'alullāha 'urḍatal li`aimānikum an tabarrụ wa tattaqụ wa tuṣliḥụ bainan-nās, wallāhu samī'un 'alīm","at":"Dan janganlah kamu jadikan (nama) Allah dalam sumpahmu sebagai penghalang untuk berbuat kebajikan, bertakwa dan menciptakan kedamaian di antara manusia. Allah Maha Mendengar, Maha Mengetahui."},{"id":"225","ar":"لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ فِيْٓ اَيْمَانِكُمْ وَلٰكِنْ يُّؤَاخِذُكُمْ بِمَا كَسَبَتْ قُلُوْبُكُمْ ۗ وَاللّٰهُ غَفُوْرٌ حَلِيْمٌ ","bc":"lā yu`ākhiżukumullāhu bil-lagwi fī aimānikum wa lākiy yu`ākhiżukum bimā kasabat qulụbukum, wallāhu gafụrun ḥalīm","at":"Allah tidak menghukum kamu karena sumpahmu yang tidak kamu sengaja, tetapi Dia menghukum kamu karena niat yang terkandung dalam hatimu. Allah Maha Pengampun, Maha Penyantun."},{"id":"226","ar":"لِلَّذِيْنَ يُؤْلُوْنَ مِنْ نِّسَاۤىِٕهِمْ تَرَبُّصُ اَرْبَعَةِ اَشْهُرٍۚ فَاِنْ فَاۤءُوْ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"lillażīna yu`lụna min nisā`ihim tarabbuṣu arba'ati asy-hur, fa in fā`ụ fa innallāha gafụrur raḥīm","at":"Bagi orang yang meng-ila' istrinya harus menunggu empat bulan. Kemudian jika mereka kembali (kepada istrinya), maka sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"227","ar":"وَاِنْ عَزَمُوا الطَّلَاقَ فَاِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ ","bc":"wa in 'azamuṭ-ṭalāqa fa innallāha samī'un 'alīm","at":"Dan jika mereka berketetapan hati hendak menceraikan, maka sungguh, Allah Maha Mendengar, Maha Mengetahui."},{"id":"228","ar":"وَالْمُطَلَّقٰتُ يَتَرَبَّصْنَ بِاَنْفُسِهِنَّ ثَلٰثَةَ قُرُوْۤءٍۗ وَلَا يَحِلُّ لَهُنَّ اَنْ يَّكْتُمْنَ مَا خَلَقَ اللّٰهُ فِيْٓ اَرْحَامِهِنَّ اِنْ كُنَّ يُؤْمِنَّ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ وَبُعُوْلَتُهُنَّ اَحَقُّ بِرَدِّهِنَّ فِيْ ذٰلِكَ اِنْ اَرَادُوْٓا اِصْلَاحًا ۗوَلَهُنَّ مِثْلُ الَّذِيْ عَلَيْهِنَّ بِالْمَعْرُوْفِۖ وَلِلرِّجَالِ عَلَيْهِنَّ دَرَجَةٌ ۗ وَاللّٰهُ عَزِيْزٌ حَكِيْمٌ","bc":"wal-muṭallaqātu yatarabbaṣna bi`anfusihinna ṡalāṡata qurū`, wa lā yaḥillu lahunna ay yaktumna mā khalaqallāhu fī ar-ḥāmihinna ing kunna yu`minna billāhi wal-yaumil-ākhir, wa bu'ụlatuhunna aḥaqqu biraddihinna fī żālika in arādū iṣlāḥā, wa lahunna miṡlullażī 'alaihinna bil-ma'rụfi wa lir-rijāli 'alaihinna darajah, wallāhu 'azīzun ḥakīm","at":"Dan para istri yang diceraikan (wajib) menahan diri mereka (menunggu) tiga kali quru'. Tidak boleh bagi mereka menyembunyikan apa yang diciptakan Allah dalam rahim mereka, jika mereka beriman kepada Allah dan hari akhir. Dan para suami mereka lebih berhak kembali kepada mereka dalam (masa) itu, jika mereka menghendaki perbaikan. Dan mereka (para perempuan) mempunyai hak seimbang dengan kewajibannya menurut cara yang patut. Tetapi para suami mempunyai kelebihan di atas mereka. Allah Mahaperkasa, Mahabijaksana."},{"id":"229","ar":"اَلطَّلَاقُ مَرَّتٰنِ ۖ فَاِمْسَاكٌۢ بِمَعْرُوْفٍ اَوْ تَسْرِيْحٌۢ بِاِحْسَانٍ ۗ وَلَا يَحِلُّ لَكُمْ اَنْ تَأْخُذُوْا مِمَّآ اٰتَيْتُمُوْهُنَّ شَيْـًٔا اِلَّآ اَنْ يَّخَافَآ اَلَّا يُقِيْمَا حُدُوْدَ اللّٰهِ ۗ فَاِنْ خِفْتُمْ اَلَّا يُقِيْمَا حُدُوْدَ اللّٰهِ ۙ فَلَا جُنَاحَ عَلَيْهِمَا فِيْمَا افْتَدَتْ بِهٖ ۗ تِلْكَ حُدُوْدُ اللّٰهِ فَلَا تَعْتَدُوْهَا ۚوَمَنْ يَّتَعَدَّ حُدُوْدَ اللّٰهِ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ","bc":"aṭ-ṭalāqu marratāni fa imsākum bima'rụfin au tasrīḥum bi`iḥsān, wa lā yaḥillu lakum an ta`khużụ mimmā ātaitumụhunna syai`an illā ay yakhāfā allā yuqīmā ḥudụdallāh, fa in khiftum allā yuqīmā ḥudụdallāhi fa lā junāḥa 'alaihimā fīmaftadat bih, tilka ḥudụdullāhi fa lā ta'tadụhā, wa may yata'adda ḥudụdallāhi fa ulā`ika humuẓ-ẓālimụn","at":"Talak (yang dapat dirujuk) itu dua kali. (Setelah itu suami dapat) menahan dengan baik, atau melepaskan dengan baik. Tidak halal bagi kamu mengambil kembali sesuatu yang telah kamu berikan kepada mereka, kecuali keduanya (suami dan istri) khawatir tidak mampu menjalankan hukum-hukum Allah. Jika kamu (wali) khawatir bahwa keduanya tidak mampu menjalankan hukum-hukum Allah, maka keduanya tidak berdosa atas bayaran yang (harus) diberikan (oleh istri) untuk menebus dirinya. Itulah hukum-hukum Allah, maka janganlah kamu melanggarnya. Barangsiapa melanggar hukum-hukum Allah, mereka itulah orang-orang zalim."},{"id":"230","ar":"فَاِنْ طَلَّقَهَا فَلَا تَحِلُّ لَهٗ مِنْۢ بَعْدُ حَتّٰى تَنْكِحَ زَوْجًا غَيْرَهٗ ۗ فَاِنْ طَلَّقَهَا فَلَا جُنَاحَ عَلَيْهِمَآ اَنْ يَّتَرَاجَعَآ اِنْ ظَنَّآ اَنْ يُّقِيْمَا حُدُوْدَ اللّٰهِ ۗ وَتِلْكَ حُدُوْدُ اللّٰهِ يُبَيِّنُهَا لِقَوْمٍ يَّعْلَمُوْنَ ","bc":"fa in ṭallaqahā fa lā taḥillu lahụ mim ba'du ḥattā tangkiḥa zaujan gairah, fa in ṭallaqahā fa lā junāḥa 'alaihimā ay yatarāja'ā in ẓannā ay yuqīmā ḥudụdallāh, wa tilka ḥudụdullāhi yubayyinuhā liqaumiy ya'lamụn","at":"Kemudian jika dia menceraikannya (setelah talak yang kedua), maka perempuan itu tidak halal lagi baginya sebelum dia menikah dengan suami yang lain. Kemudian jika suami yang lain itu menceraikannya, maka tidak ada dosa bagi keduanya (suami pertama dan bekas istri) untuk menikah kembali jika keduanya berpendapat akan dapat menjalankan hukum-hukum Allah. Itulah ketentuan-ketentuan Allah yang diterangkan-Nya kepada orang-orang yang berpengetahuan."},{"id":"231","ar":"وَاِذَا طَلَّقْتُمُ النِّسَاۤءَ فَبَلَغْنَ اَجَلَهُنَّ فَاَمْسِكُوْهُنَّ بِمَعْرُوْفٍ اَوْ سَرِّحُوْهُنَّ بِمَعْرُوْفٍۗ وَلَا تُمْسِكُوْهُنَّ ضِرَارًا لِّتَعْتَدُوْا ۚ وَمَنْ يَّفْعَلْ ذٰلِكَ فَقَدْ ظَلَمَ نَفْسَهٗ ۗ وَلَا تَتَّخِذُوْٓا اٰيٰتِ اللّٰهِ هُزُوًا وَّاذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْ وَمَآ اَنْزَلَ عَلَيْكُمْ مِّنَ الْكِتٰبِ وَالْحِكْمَةِ يَعِظُكُمْ بِهٖ ۗوَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ","bc":"wa iżā ṭallaqtumun-nisā`a fa balagna ajalahunna fa amsikụhunna bima'rụfin au sarriḥụhunna bima'rụf, wa lā tumsikụhunna ḍirāral lita'tadụ, wa may yaf'al żālika fa qad ẓalama nafsah, wa lā tattakhiżū āyātillāhi huzuwaw ważkurụ ni'matallāhi 'alaikum wa mā anzala 'alaikum minal-kitābi wal-ḥikmati ya'iẓukum bih, wattaqullāha wa'lamū annallāha bikulli syai`in 'alīm","at":"Dan apabila kamu menceraikan istri-istri (kamu), lalu sampai (akhir) idahnya, maka tahanlah mereka dengan cara yang baik, atau ceraikanlah mereka dengan cara yang baik (pula). Dan janganlah kamu tahan mereka dengan maksud jahat untuk menzalimi mereka. Barangsiapa melakukan demikian, maka dia telah menzalimi dirinya sendiri. Dan janganlah kamu jadikan ayat-ayat Allah sebagai bahan ejekan. Ingatlah nikmat Allah kepada kamu, dan apa yang telah diturunkan Allah kepada kamu yaitu Kitab (Al-Qur'an) dan Hikmah (Sunnah), untuk memberi pengajaran kepadamu. Dan bertakwalah kepada Allah dan ketahuilah bahwa Allah Maha Mengetahui segala sesuatu."},{"id":"232","ar":"وَاِذَا طَلَّقْتُمُ النِّسَاۤءَ فَبَلَغْنَ اَجَلَهُنَّ فَلَا تَعْضُلُوْهُنَّ اَنْ يَّنْكِحْنَ اَزْوَاجَهُنَّ اِذَا تَرَاضَوْا بَيْنَهُمْ بِالْمَعْرُوْفِ ۗ ذٰلِكَ يُوْعَظُ بِهٖ مَنْ كَانَ مِنْكُمْ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ ۗ ذٰلِكُمْ اَزْكٰى لَكُمْ وَاَطْهَرُ ۗ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ ","bc":"wa iżā ṭallaqtumun-nisā`a fa balagna ajalahunna fa lā ta'ḍulụhunna ay yangkiḥna azwājahunna iżā tarāḍau bainahum bil-ma'rụf, żālika yụ'aẓu bihī mang kāna mingkum yu`minu billāhi wal-yaumil-ākhir, żālikum azkā lakum wa aṭ-har, wallāhu ya'lamu wa antum lā ta'lamụn","at":"Dan apabila kamu menceraikan istri-istri (kamu), lalu sampai idahnya, maka jangan kamu halangi mereka menikah (lagi) dengan calon suaminya, apabila telah terjalin kecocokan di antara mereka dengan cara yang baik. Itulah yang dinasihatkan kepada orang-orang di antara kamu yang beriman kepada Allah dan hari akhir. Itu lebih suci bagimu dan lebih bersih. Dan Allah mengetahui, sedangkan kamu tidak mengetahui."},{"id":"233","ar":"۞ وَالْوَالِدٰتُ يُرْضِعْنَ اَوْلَادَهُنَّ حَوْلَيْنِ كَامِلَيْنِ لِمَنْ اَرَادَ اَنْ يُّتِمَّ الرَّضَاعَةَ ۗ وَعَلَى الْمَوْلُوْدِ لَهٗ رِزْقُهُنَّ وَكِسْوَتُهُنَّ بِالْمَعْرُوْفِۗ لَا تُكَلَّفُ نَفْسٌ اِلَّا وُسْعَهَا ۚ لَا تُضَاۤرَّ وَالِدَةٌ ۢبِوَلَدِهَا وَلَا مَوْلُوْدٌ لَّهٗ بِوَلَدِهٖ وَعَلَى الْوَارِثِ مِثْلُ ذٰلِكَ ۚ فَاِنْ اَرَادَا فِصَالًا عَنْ تَرَاضٍ مِّنْهُمَا وَتَشَاوُرٍ فَلَا جُنَاحَ عَلَيْهِمَا ۗوَاِنْ اَرَدْتُّمْ اَنْ تَسْتَرْضِعُوْٓا اَوْلَادَكُمْ فَلَا جُنَاحَ عَلَيْكُمْ اِذَا سَلَّمْتُمْ مَّآ اٰتَيْتُمْ بِالْمَعْرُوْفِۗ وَاتَّقُوا اللّٰهَ وَاعْلَمُوْٓا اَنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ","bc":"wal-wālidātu yurḍi'na aulādahunna ḥaulaini kāmilaini liman arāda ay yutimmar-raḍā'ah, wa 'alal-maulụdi lahụ rizquhunna wa kiswatuhunna bil-ma'rụf, lā tukallafu nafsun illā wus'ahā, lā tuḍārra wālidatum biwaladihā wa lā maulụdul lahụ biwaladihī wa 'alal-wāriṡi miṡlu żālik, fa in arādā fiṣālan 'an tarāḍim min-humā wa tasyāwurin fa lā junāḥa 'alaihimā, wa in arattum an tastarḍi'ū aulādakum fa lā junāḥa 'alaikum iżā sallamtum mā ātaitum bil-ma'rụf, wattaqullāha wa'lamū annallāha bimā ta'malụna baṣīr","at":"Dan ibu-ibu hendaklah menyusui anak-anaknya selama dua tahun penuh, bagi yang ingin menyusui secara sempurna. Dan kewajiban ayah menanggung nafkah dan pakaian mereka dengan cara yang patut. Seseorang tidak dibebani lebih dari kesanggupannya. Janganlah seorang ibu menderita karena anaknya dan jangan pula seorang ayah (menderita) karena anaknya. Ahli waris pun (berkewajiban) seperti itu pula. Apabila keduanya ingin menyapih dengan persetujuan dan permusyawaratan antara keduanya, maka tidak ada dosa atas keduanya. Dan jika kamu ingin menyusukan anakmu kepada orang lain, maka tidak ada dosa bagimu memberikan pembayaran dengan cara yang patut. Bertakwalah kepada Allah dan ketahuilah bahwa Allah Maha Melihat apa yang kamu kerjakan."},{"id":"234","ar":"وَالَّذِيْنَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُوْنَ اَزْوَاجًا يَّتَرَبَّصْنَ بِاَنْفُسِهِنَّ اَرْبَعَةَ اَشْهُرٍ وَّعَشْرًا ۚ فاِذَا بَلَغْنَ اَجَلَهُنَّ فَلَا جُنَاحَ عَلَيْكُمْ فِيْمَا فَعَلْنَ فِيْٓ اَنْفُسِهِنَّ بِالْمَعْرُوْفِۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ ","bc":"wallażīna yutawaffauna mingkum wa yażarụna azwājay yatarabbaṣna bi`anfusihinna arba'ata asy-huriw wa 'asyrā, fa iżā balagna ajalahunna fa lā junāḥa 'alaikum fīmā fa'alna fī anfusihinna bil-ma'rụf, wallāhu bimā ta'malụna khabīr","at":"Dan orang-orang yang mati di antara kamu serta meninggalkan istri-istri hendaklah mereka (istri-istri) menunggu empat bulan sepuluh hari. Kemudian apabila telah sampai (akhir) idah mereka, maka tidak ada dosa bagimu mengenai apa yang mereka lakukan terhadap diri mereka menurut cara yang patut. Dan Allah Maha Mengetahui apa yang kamu kerjakan."},{"id":"235","ar":"وَلَا جُنَاحَ عَلَيْكُمْ فِيْمَا عَرَّضْتُمْ بِهٖ مِنْ خِطْبَةِ النِّسَاۤءِ اَوْ اَكْنَنْتُمْ فِيْٓ اَنْفُسِكُمْ ۗ عَلِمَ اللّٰهُ اَنَّكُمْ سَتَذْكُرُوْنَهُنَّ وَلٰكِنْ لَّا تُوَاعِدُوْهُنَّ سِرًّا اِلَّآ اَنْ تَقُوْلُوْا قَوْلًا مَّعْرُوْفًا ەۗ وَلَا تَعْزِمُوْا عُقْدَةَ النِّكَاحِ حَتّٰى يَبْلُغَ الْكِتٰبُ اَجَلَهٗ ۗوَاعْلَمُوْٓا اَنَّ اللّٰهَ يَعْلَمُ مَا فِيْٓ اَنْفُسِكُمْ فَاحْذَرُوْهُ ۚوَاعْلَمُوْٓا اَنَّ اللّٰهَ غَفُوْرٌ حَلِيْمٌ","bc":"wa lā junāḥa 'alaikum fīmā 'arraḍtum bihī min khiṭbatin-nisā`i au aknantum fī anfusikum, 'alimallāhu annakum satażkurụnahunna wa lākil lā tuwā'idụhunna sirran illā an taqụlụ qaulam ma'rụfā, wa lā ta'zimụ 'uqdatan-nikāḥi ḥattā yablugal-kitābu ajalah, wa'lamū annallāha ya'lamu mā fī anfusikum faḥżarụh, wa'lamū annallāha gafụrun ḥalīm","at":"Dan tidak ada dosa bagimu meminang perempuan-perempuan itu dengan sindiran atau kamu sembunyikan (keinginanmu) dalam hati. Allah mengetahui bahwa kamu akan menyebut-nyebut kepada mereka. Tetapi janganlah kamu membuat perjanjian (untuk menikah) dengan mereka secara rahasia, kecuali sekedar mengucapkan kata-kata yang baik. Dan janganlah kamu menetapkan akad nikah, sebelum habis masa idahnya. Ketahuilah bahwa Allah mengetahui apa yang ada dalam hatimu, maka takutlah kepada-Nya. Dan ketahuilah bahwa Allah Maha Pengampun, Maha Penyantun."},{"id":"236","ar":"لَا جُنَاحَ عَلَيْكُمْ اِنْ طَلَّقْتُمُ النِّسَاۤءَ مَا لَمْ تَمَسُّوْهُنَّ اَوْ تَفْرِضُوْا لَهُنَّ فَرِيْضَةً ۖ وَّمَتِّعُوْهُنَّ عَلَى الْمُوْسِعِ قَدَرُهٗ وَعَلَى الْمُقْتِرِ قَدَرُهٗ ۚ مَتَاعًا ۢبِالْمَعْرُوْفِۚ حَقًّا عَلَى الْمُحْسِنِيْنَ ","bc":"lā junāḥa 'alaikum in ṭallaqtumun-nisā`a mā lam tamassụhunna au tafriḍụ lahunna farīḍataw wa matti'ụhunna 'alal-mụsi'i qadaruhụ wa 'alal-muqtiri qadaruh, matā'am bil-ma'rụf, ḥaqqan 'alal-muḥsinīn","at":"Tidak ada dosa bagimu, jika kamu menceraikan istri-istri kamu yang belum kamu sentuh (campuri) atau belum kamu tentukan maharnya. Dan hendaklah kamu beri mereka mut‘ah, bagi yang mampu menurut kemampuannya dan bagi yang tidak mampu menurut kesanggupannya, yaitu pemberian dengan cara yang patut, yang merupakan kewajiban bagi orang-orang yang berbuat kebaikan."},{"id":"237","ar":"وَاِنْ طَلَّقْتُمُوْهُنَّ مِنْ قَبْلِ اَنْ تَمَسُّوْهُنَّ وَقَدْ فَرَضْتُمْ لَهُنَّ فَرِيْضَةً فَنِصْفُ مَا فَرَضْتُمْ اِلَّآ اَنْ يَّعْفُوْنَ اَوْ يَعْفُوَا الَّذِيْ بِيَدِهٖ عُقْدَةُ النِّكَاحِ ۗ وَاَنْ تَعْفُوْٓا اَقْرَبُ لِلتَّقْوٰىۗ وَلَا تَنْسَوُا الْفَضْلَ بَيْنَكُمْ ۗ اِنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ","bc":"wa in ṭallaqtumụhunna ming qabli an tamassụhunna wa qad faraḍtum lahunna farīḍatan fa niṣfu mā faraḍtum illā ay ya'fụna au ya'fuwallażī biyadihī 'uqdatun-nikāḥ, wa an ta'fū aqrabu lit-taqwā, wa lā tansawul-faḍla bainakum, innallāha bimā ta'malụna baṣīr","at":"Dan jika kamu menceraikan mereka sebelum kamu sentuh (campuri), padahal kamu sudah menentukan Maharnya, maka (bayarlah) seperdua dari yang telah kamu tentukan, kecuali jika mereka (membebaskan) atau dibebaskan oleh orang yang akad nikah ada di tangannya. Pembebasan itu lebih dekat kepada takwa. Dan janganlah kamu lupa kebaikan di antara kamu. Sungguh, Allah Maha Melihat apa yang kamu kerjakan."},{"id":"238","ar":"حَافِظُوْا عَلَى الصَّلَوٰتِ وَالصَّلٰوةِ الْوُسْطٰى وَقُوْمُوْا لِلّٰهِ قٰنِتِيْنَ ","bc":"ḥāfiẓụ 'alaṣ-ṣalawāti waṣ-ṣalātil-wusṭā wa qụmụ lillāhi qānitīn","at":"Peliharalah semua salat dan salat wustha. Dan laksanakanlah (salat) karena Allah dengan khusyuk."},{"id":"239","ar":"فَاِنْ خِفْتُمْ فَرِجَالًا اَوْ رُكْبَانًا ۚ فَاِذَآ اَمِنْتُمْ فَاذْكُرُوا اللّٰهَ كَمَا عَلَّمَكُمْ مَّا لَمْ تَكُوْنُوْا تَعْلَمُوْنَ ","bc":"fa in khiftum fa rijālan au rukbānā, fa iżā amintum fażkurullāha kamā 'allamakum mā lam takụnụ ta'lamụn","at":"Jika kamu takut (ada bahaya), salatlah sambil berjalan kaki atau berkendaraan. Kemudian apabila telah aman, maka ingatlah Allah (salatlah), sebagaimana Dia telah mengajarkan kepadamu apa yang tidak kamu ketahui."},{"id":"240","ar":"وَالَّذِيْنَ يُتَوَفَّوْنَ مِنْكُمْ وَيَذَرُوْنَ اَزْوَاجًاۖ وَّصِيَّةً لِّاَزْوَاجِهِمْ مَّتَاعًا اِلَى الْحَوْلِ غَيْرَ اِخْرَاجٍ ۚ فَاِنْ خَرَجْنَ فَلَا جُنَاحَ عَلَيْكُمْ فِيْ مَا فَعَلْنَ فِيْٓ اَنْفُسِهِنَّ مِنْ مَّعْرُوْفٍۗ وَاللّٰهُ عَزِيْزٌ حَكِيْمٌ ","bc":"wallażīna yutawaffauna mingkum wa yażarụna azwājaw waṣiyyatal li`azwājihim matā'an ilal-ḥauli gaira ikhrāj, fa in kharajna fa lā junāḥa 'alaikum fī mā fa'alna fī anfusihinna mim ma'rụf, wallāhu 'azīzun ḥakīm","at":"Dan orang-orang yang akan mati di antara kamu dan meninggalkan istri-istri, hendaklah membuat wasiat untuk istri-istrinya, (yaitu) nafkah sampai setahun tanpa mengeluarkannya (dari rumah). Tetapi jika mereka keluar (sendiri), maka tidak ada dosa bagimu (mengenai apa) yang mereka lakukan terhadap diri mereka sendiri dalam hal-hal yang baik. Allah Mahaperkasa, Mahabijaksana."},{"id":"241","ar":"وَلِلْمُطَلَّقٰتِ مَتَاعٌ ۢبِالْمَعْرُوْفِۗ حَقًّا عَلَى الْمُتَّقِيْنَ ","bc":"wa lil-muṭallaqāti matā'um bil-ma'rụf, ḥaqqan 'alal-muttaqīn","at":"Dan bagi perempuan-perempuan yang diceraikan hendaklah diberi mut‘ah menurut cara yang patut, sebagai suatu kewajiban bagi orang yang bertakwa."},{"id":"242","ar":"كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَعْقِلُوْنَ","bc":"każālika yubayyinullāhu lakum āyātihī la'allakum ta'qilụn","at":"Demikianlah Allah menerangkan kepadamu ayat-ayat-Nya agar kamu mengerti."},{"id":"243","ar":"۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ خَرَجُوْا مِنْ دِيَارِهِمْ وَهُمْ اُلُوْفٌ حَذَرَ الْمَوْتِۖ فَقَالَ لَهُمُ اللّٰهُ مُوْتُوْا ۗ ثُمَّ اَحْيَاهُمْ ۗ اِنَّ اللّٰهَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُوْنَ ","bc":"a lam tara ilallażīna kharajụ min diyārihim wa hum ulụfun ḥażaral-mauti fa qāla lahumullāhu mụtụ, ṡumma aḥyāhum, innallāha lażụ faḍlin 'alan-nāsi wa lākinna akṡaran-nāsi lā yasykurụn","at":"Tidakkah kamu memperhatikan orang-orang yang keluar dari kampung halamannya, sedang jumlahnya ribuan karena takut mati? Lalu Allah berfirman kepada mereka, “Matilah kamu!” Kemudian Allah menghidupkan mereka. Sesungguhnya Allah memberikan karunia kepada manusia, tetapi kebanyakan manusia tidak bersyukur."},{"id":"244","ar":"وَقَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ وَاعْلَمُوْٓا اَنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ ","bc":"wa qātilụ fī sabīlillāhi wa'lamū annallāha samī'un 'alīm","at":"Dan berperanglah kamu di jalan Allah, dan ketahuilah bahwa Allah Maha Mendengar, Maha Mengetahui."},{"id":"245","ar":"مَنْ ذَا الَّذِيْ يُقْرِضُ اللّٰهَ قَرْضًا حَسَنًا فَيُضٰعِفَهٗ لَهٗٓ اَضْعَافًا كَثِيْرَةً ۗوَاللّٰهُ يَقْبِضُ وَيَبْصُۣطُۖ وَاِلَيْهِ تُرْجَعُوْنَ","bc":"man żallażī yuqriḍullāha qarḍan ḥasanan fa yuḍā'ifahụ lahū aḍ'āfang kaṡīrah, wallāhu yaqbiḍu wa yabṣuṭu wa ilaihi turja'ụn","at":"Barangsiapa meminjami Allah dengan pinjaman yang baik maka Allah melipatgandakan ganti kepadanya dengan banyak. Allah menahan dan melapangkan (rezeki) dan kepada-Nyalah kamu dikembalikan."},{"id":"246","ar":"اَلَمْ تَرَ اِلَى الْمَلَاِ مِنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ مِنْۢ بَعْدِ مُوْسٰىۘ اِذْ قَالُوْا لِنَبِيٍّ لَّهُمُ ابْعَثْ لَنَا مَلِكًا نُّقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ ۗ قَالَ هَلْ عَسَيْتُمْ اِنْ كُتِبَ عَلَيْكُمُ الْقِتَالُ اَلَّا تُقَاتِلُوْا ۗ قَالُوْا وَمَا لَنَآ اَلَّا نُقَاتِلَ فِيْ سَبِيْلِ اللّٰهِ وَقَدْاُخْرِجْنَا مِنْ دِيَارِنَا وَاَبْنَاۤىِٕنَا ۗ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ تَوَلَّوْا اِلَّا قَلِيْلًا مِّنْهُمْ ۗوَاللّٰهُ عَلِيْمٌ ۢبِالظّٰلِمِيْنَ ","bc":"a lam tara ilal-mala`i mim banī isrā`īla mim ba'di mụsā, iż qālụ linabiyyil lahumub'aṡ lanā malikan-nuqātil fī sabīlillāh, qāla hal 'asaitum ing kutiba 'alaikumul-qitālu allā tuqātilụ, qālụ wa mā lanā allā nuqātila fī sabīlillāhi wa qad ukhrijnā min diyārinā wa abnā`inā, fa lammā kutiba 'alaihimul-qitālu tawallau illā qalīlam min-hum, wallāhu 'alīmum biẓ-ẓālimīn","at":"Tidakkah kamu perhatikan para pemuka Bani Israil setelah Musa wafat, ketika mereka berkata kepada seorang nabi mereka, “Angkatlah seorang raja untuk kami, niscaya kami berperang di jalan Allah.” Nabi mereka menjawab, “Jangan-jangan jika diwajibkan atasmu berperang, kamu tidak akan berperang juga?” Mereka menjawab, “Mengapa kami tidak akan berperang di jalan Allah, sedangkan kami telah diusir dari kampung halaman kami dan (dipisahkan dari) anak-anak kami?” Tetapi ketika perang itu diwajibkan atas mereka, mereka berpaling, kecuali sebagian kecil dari mereka. Dan Allah Maha Mengetahui orang-orang yang zalim."},{"id":"247","ar":"وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اللّٰهَ قَدْ بَعَثَ لَكُمْ طَالُوْتَ مَلِكًا ۗ قَالُوْٓا اَنّٰى يَكُوْنُ لَهُ الْمُلْكُ عَلَيْنَا وَنَحْنُ اَحَقُّ بِالْمُلْكِ مِنْهُ وَلَمْ يُؤْتَ سَعَةً مِّنَ الْمَالِۗ قَالَ اِنَّ اللّٰهَ اصْطَفٰىهُ عَلَيْكُمْ وَزَادَهٗ بَسْطَةً فِى الْعِلْمِ وَالْجِسْمِ ۗ وَاللّٰهُ يُؤْتِيْ مُلْكَهٗ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ ","bc":"wa qāla lahum nabiyyuhum innallāha qad ba'aṡa lakum ṭālụta malikā, qālū annā yakụnu lahul-mulku 'alainā wa naḥnu aḥaqqu bil-mulki min-hu wa lam yu`ta sa'atam minal-māl, qāla innallāhaṣṭafāhu 'alaikum wa zādahụ basṭatan fil 'ilmi wal-jism, wallāhu yu`tī mulkahụ may yasyā`, wallāhu wāsi'un 'alīm","at":"Dan nabi mereka berkata kepada mereka, “Sesungguhnya Allah telah mengangkat Talut menjadi rajamu.” Mereka menjawab, “Bagaimana Talut memperoleh kerajaan atas kami, sedangkan kami lebih berhak atas kerajaan itu daripadanya, dan dia tidak diberi kekayaan yang banyak?” (Nabi) menjawab, “Allah telah memilihnya (menjadi raja) kamu dan memberikan kelebihan ilmu dan fisik.” Allah memberikan kerajaan-Nya kepada siapa yang Dia kehendaki, dan Allah Mahaluas, Maha Mengetahui."},{"id":"248","ar":"وَقَالَ لَهُمْ نَبِيُّهُمْ اِنَّ اٰيَةَ مُلْكِهٖٓ اَنْ يَّأْتِيَكُمُ التَّابُوْتُ فِيْهِ سَكِيْنَةٌ مِّنْ رَّبِّكُمْ وَبَقِيَّةٌ مِّمَّا تَرَكَ اٰلُ مُوْسٰى وَاٰلُ هٰرُوْنَ تَحْمِلُهُ الْمَلٰۤىِٕكَةُ ۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ","bc":"wa qāla lahum nabiyyuhum inna āyata mulkihī ay ya`tiyakumut-tābụtu fīhi sakīnatum mir rabbikum wa baqiyyatum mimmā taraka ālu mụsā wa ālu hārụna taḥmiluhul-malā`ikah, inna fī żālika la`āyatal lakum ing kuntum mu`minīn","at":"Dan nabi mereka berkata kepada mereka, “Sesungguhnya tanda kerajaannya ialah datangnya Tabut kepadamu, yang di dalamnya terdapat ketenangan dari Tuhanmu dan sisa peninggalan keluarga Musa dan keluarga Harun, yang dibawa oleh malaikat. Sungguh, pada yang demikian itu terdapat tanda (kebesaran Allah) bagimu, jika kamu orang beriman."},{"id":"249","ar":"فَلَمَّا فَصَلَ طَالُوْتُ بِالْجُنُوْدِ قَالَ اِنَّ اللّٰهَ مُبْتَلِيْكُمْ بِنَهَرٍۚ فَمَنْ شَرِبَ مِنْهُ فَلَيْسَ مِنِّيْۚ وَمَنْ لَّمْ يَطْعَمْهُ فَاِنَّهٗ مِنِّيْٓ اِلَّا مَنِ اغْتَرَفَ غُرْفَةً ۢبِيَدِهٖ ۚ فَشَرِبُوْا مِنْهُ اِلَّا قَلِيْلًا مِّنْهُمْ ۗ فَلَمَّا جَاوَزَهٗ هُوَ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗۙ قَالُوْا لَا طَاقَةَ لَنَا الْيَوْمَ بِجَالُوْتَ وَجُنُوْدِهٖ ۗ قَالَ الَّذِيْنَ يَظُنُّوْنَ اَنَّهُمْ مُّلٰقُوا اللّٰهِ ۙ كَمْ مِّنْ فِئَةٍ قَلِيْلَةٍ غَلَبَتْ فِئَةً كَثِيْرَةً ۢبِاِذْنِ اللّٰهِ ۗ وَاللّٰهُ مَعَ الصّٰبِرِيْنَ ","bc":"fa lammā faṣala ṭālụtu bil-junụdi qāla innallāha mubtalīkum binahar, fa man syariba min-hu fa laisa minnī, wa mal lam yaṭ'am-hu fa innahụ minnī illā manigtarafa gurfatam biyadih, fa syaribụ min-hu illā qalīlam min-hum, fa lammā jāwazahụ huwa wallażīna āmanụ ma'ahụ qālụ lā ṭāqata lanal-yauma bijālụta wa junụdih, qālallażīna yaẓunnụna annahum mulāqullāhi kam min fi`ating qalīlatin galabat fi`atang kaṡīratam bi`iżnillāh, wallāhu ma'aṣ-ṣābirīn","at":"Maka ketika Talut membawa bala tentaranya, dia berkata, “Allah akan menguji kamu dengan sebuah sungai. Maka barangsiapa meminum (airnya), dia bukanlah pengikutku. Dan barangsiapa tidak meminumnya, maka dia adalah pengikutku kecuali menciduk seciduk dengan tangan.” Tetapi mereka meminumnya kecuali sebagian kecil di antara mereka. Ketika dia (Talut) dan orang-orang yang beriman bersamanya menyeberangi sungai itu, mereka berkata, “Kami tidak kuat lagi pada hari ini melawan Jalut dan bala tentaranya.” Mereka yang meyakini bahwa mereka akan menemui Allah berkata, “Betapa banyak kelompok kecil mengalahkan kelompok besar dengan izin Allah.” Dan Allah beserta orang-orang yang sabar."},{"id":"250","ar":"وَلَمَّا بَرَزُوْا لِجَالُوْتَ وَجُنُوْدِهٖ قَالُوْا رَبَّنَآ اَفْرِغْ عَلَيْنَا صَبْرًا وَّثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ ۗ ","bc":"wa lammā barazụ lijālụta wa junụdihī qālụ rabbanā afrig 'alainā ṣabraw wa ṡabbit aqdāmanā wanṣurnā 'alal-qaumil-kāfirīn","at":"Dan ketika mereka maju melawan Jalut dan tentaranya, mereka berdoa, “Ya Tuhan kami, limpahkanlah kesabaran kepada kami, kukuhkanlah langkah kami dan tolonglah kami menghadapi orang-orang kafir.”"},{"id":"251","ar":"فَهَزَمُوْهُمْ بِاِذْنِ اللّٰهِ ۗوَقَتَلَ دَاوٗدُ جَالُوْتَ وَاٰتٰىهُ اللّٰهُ الْمُلْكَ وَالْحِكْمَةَ وَعَلَّمَهٗ مِمَّا يَشَاۤءُ ۗ وَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَّفَسَدَتِ الْاَرْضُ وَلٰكِنَّ اللّٰهَ ذُوْ فَضْلٍ عَلَى الْعٰلَمِيْنَ ","bc":"fa hazamụhum bi`iżnillāh, wa qatala dāwụdu jālụta wa ātāhullāhul-mulka wal-ḥikmata wa 'allamahụ mimmā yasyā`, walau lā daf'ullāhin-nāsa ba'ḍahum biba'ḍil lafasadatil-arḍu wa lākinnallāha żụ faḍlin 'alal-'ālamīn","at":"Maka mereka mengalahkannya dengan izin Allah, dan Dawud membunuh Jalut. Kemudian Allah memberinya (Dawud) kerajaan, dan hikmah, dan mengajarinya apa yang Dia kehendaki. Dan kalau Allah tidak melindungi sebagian manusia dengan sebagian yang lain, niscaya rusaklah bumi ini. Tetapi Allah mempunyai karunia (yang dilimpahkan-Nya) atas seluruh alam."},{"id":"252","ar":"تِلْكَ اٰيٰتُ اللّٰهِ نَتْلُوْهَا عَلَيْكَ بِالْحَقِّ ۗ وَاِنَّكَ لَمِنَ الْمُرْسَلِيْنَ ۔","bc":"tilka āyātullāhi natlụhā 'alaika bil-ḥaqq, wa innaka laminal-mursalīn","at":"Itulah ayat-ayat Allah, Kami bacakan kepadamu dengan benar dan engkau (Muhammad) adalah benar-benar seorang rasul."},{"id":"253","ar":"تِلْكَ الرُّسُلُ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۘ مِنْهُمْ مَّنْ كَلَّمَ اللّٰهُ وَرَفَعَ بَعْضَهُمْ دَرَجٰتٍۗ وَاٰتَيْنَا عِيْسَى ابْنَ مَرْيَمَ الْبَيِّنٰتِ وَاَيَّدْنٰهُ بِرُوْحِ الْقُدُسِۗ وَلَوْ شَاۤءَ اللّٰهُ مَا اقْتَتَلَ الَّذِيْنَ مِنْۢ بَعْدِهِمْ مِّنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنٰتُ وَلٰكِنِ اخْتَلَفُوْا فَمِنْهُمْ مَّنْ اٰمَنَ وَمِنْهُمْ مَّنْ كَفَرَ ۗوَلَوْ شَاۤءَ اللّٰهُ مَا اقْتَتَلُوْاۗ وَلٰكِنَّ اللّٰهَ يَفْعَلُ مَا يُرِيْدُ","bc":"tilkar-rusulu faḍḍalnā ba'ḍahum 'alā ba'ḍ, min-hum mang kallamallāhu wa rafa'a ba'ḍahum darajāt, wa ātainā 'īsabna maryamal-bayyināti wa ayyadnāhu birụḥil-qudus, walau syā`allāhu maqtatalallażīna mim ba'dihim mim ba'di mā jā`at-humul-bayyinātu wa lākinikhtalafụ fa min-hum man āmana wa min-hum mang kafar, walau syā`allāhu maqtatalụ, wa lākinnallāha yaf'alu mā yurīd","at":"Rasul-rasul itu Kami lebihkan sebagian mereka dari sebagian yang lain. Di antara mereka ada yang (langsung) Allah berfirman dengannya dan sebagian lagi ada yang ditinggikan-Nya beberapa derajat. Dan Kami beri Isa putra Maryam beberapa mukjizat dan Kami perkuat dia dengan Rohulkudus. Kalau Allah menghendaki, niscaya orang-orang setelah mereka tidak akan berbunuh-bunuhan, setelah bukti-bukti sampai kepada mereka. Tetapi mereka berselisih, maka ada di antara mereka yang beriman dan ada (pula) yang kafir. Kalau Allah menghendaki, tidaklah mereka berbunuh-bunuhan. Tetapi Allah berbuat menurut kehendak-Nya."},{"id":"254","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَنْفِقُوْا مِمَّا رَزَقْنٰكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا بَيْعٌ فِيْهِ وَلَا خُلَّةٌ وَّلَا شَفَاعَةٌ ۗوَالْكٰفِرُوْنَ هُمُ الظّٰلِمُوْنَ","bc":"yā ayyuhallażīna āmanū anfiqụ mimmā razaqnākum ming qabli ay ya`tiya yaumul lā bai'un fīhi wa lā khullatuw wa lā syafā'ah, wal-kāfirụna humuẓ-ẓālimụn","at":"Wahai orang-orang yang beriman! Infakkanlah sebagian dari rezeki yang telah Kami berikan kepadamu sebelum datang hari ketika tidak ada lagi jual beli, tidak ada lagi persahabatan dan tidak ada lagi syafaat. Orang-orang kafir itulah orang yang zalim."},{"id":"255","ar":"اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۚ اَلْحَيُّ الْقَيُّوْمُ ەۚ لَا تَأْخُذُهٗ سِنَةٌ وَّلَا نَوْمٌۗ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ مَنْ ذَا الَّذِيْ يَشْفَعُ عِنْدَهٗٓ اِلَّا بِاِذْنِهٖۗ يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْۚ وَلَا يُحِيْطُوْنَ بِشَيْءٍ مِّنْ عِلْمِهٖٓ اِلَّا بِمَا شَاۤءَۚ وَسِعَ كُرْسِيُّهُ السَّمٰوٰتِ وَالْاَرْضَۚ وَلَا يَـُٔوْدُهٗ حِفْظُهُمَاۚ وَهُوَ الْعَلِيُّ الْعَظِيْمُ","bc":"allāhu lā ilāha illā huw, al-ḥayyul-qayyụm, lā ta`khużuhụ sinatuw wa lā na`ụm, lahụ mā fis-samāwāti wa mā fil-arḍ, man żallażī yasyfa'u 'indahū illā bi`iżnih, ya'lamu mā baina aidīhim wa mā khalfahum, wa lā yuḥīṭụna bisyai`im min 'ilmihī illā bimā syā`, wasi'a kursiyyuhus-samāwāti wal-arḍ, wa lā ya`ụduhụ ḥifẓuhumā, wa huwal-'aliyyul-'aẓīm","at":"Allah, tidak ada tuhan selain Dia. Yang Mahahidup, Yang terus menerus mengurus (makhluk-Nya), tidak mengantuk dan tidak tidur. Milik-Nya apa yang ada di langit dan apa yang ada di bumi. Tidak ada yang dapat memberi syafaat di sisi-Nya tanpa izin-Nya. Dia mengetahui apa yang di hadapan mereka dan apa yang di belakang mereka, dan mereka tidak mengetahui sesuatu apa pun tentang ilmu-Nya melainkan apa yang Dia kehendaki. Kursi-Nya meliputi langit dan bumi. Dan Dia tidak merasa berat memelihara keduanya, dan Dia Mahatinggi, Mahabesar."},{"id":"256","ar":"لَآ اِكْرَاهَ فِى الدِّيْنِۗ قَدْ تَّبَيَّنَ الرُّشْدُ مِنَ الْغَيِّ ۚ فَمَنْ يَّكْفُرْ بِالطَّاغُوْتِ وَيُؤْمِنْۢ بِاللّٰهِ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰى لَا انْفِصَامَ لَهَا ۗوَاللّٰهُ سَمِيْعٌ عَلِيْمٌ","bc":"lā ikrāha fid-dīn, qat tabayyanar-rusydu minal-gayy, fa may yakfur biṭ-ṭāgụti wa yu`mim billāhi fa qadistamsaka bil-'urwatil-wuṡqā lanfiṣāma lahā, wallāhu samī'un 'alīm","at":"Tidak ada paksaan dalam (menganut) agama (Islam), sesungguhnya telah jelas (perbedaan) antara jalan yang benar dengan jalan yang sesat. Barang siapa ingkar kepada Tagut dan beriman kepada Allah, maka sungguh, dia telah berpegang (teguh) pada tali yang sangat kuat yang tidak akan putus. Allah Maha Mendengar, Maha Mengetahui."},{"id":"257","ar":"اَللّٰهُ وَلِيُّ الَّذِيْنَ اٰمَنُوْا يُخْرِجُهُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَالَّذِيْنَ كَفَرُوْٓا اَوْلِيَاۤؤُهُمُ الطَّاغُوْتُ يُخْرِجُوْنَهُمْ مِّنَ النُّوْرِ اِلَى الظُّلُمٰتِۗ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ","bc":"allāhu waliyyullażīna āmanụ yukhrijuhum minaẓ-ẓulumāti ilan-nụr, wallażīna kafarū auliyā`uhumuṭ-ṭāgụtu yukhrijụnahum minan-nụri ilaẓ-ẓulumāt, ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn","at":"Allah pelindung orang yang beriman. Dia mengeluarkan mereka dari kegelapan kepada cahaya (iman). Dan orang-orang yang kafir, pelindung-pelindungnya adalah setan, yang mengeluarkan mereka dari cahaya kepada kegelapan. Mereka adalah penghuni neraka. Mereka kekal di dalamnya."},{"id":"258","ar":"اَلَمْ تَرَ اِلَى الَّذِيْ حَاۤجَّ اِبْرٰهٖمَ فِيْ رَبِّهٖٓ اَنْ اٰتٰىهُ اللّٰهُ الْمُلْكَ ۘ اِذْ قَالَ اِبْرٰهٖمُ رَبِّيَ الَّذِيْ يُحْيٖ وَيُمِيْتُۙ قَالَ اَنَا۠ اُحْيٖ وَاُمِيْتُ ۗ قَالَ اِبْرٰهٖمُ فَاِنَّ اللّٰهَ يَأْتِيْ بِالشَّمْسِ مِنَ الْمَشْرِقِ فَأْتِ بِهَا مِنَ الْمَغْرِبِ فَبُهِتَ الَّذِيْ كَفَرَ ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَۚ","bc":"a lam tara ilallażī ḥājja ibrāhīma fī rabbihī an ātāhullāhul-mulk, iż qāla ibrāhīmu rabbiyallażī yuḥyī wa yumītu qāla ana uḥyī wa umīt, qāla ibrāhīmu fa innallāha ya`tī bisy-syamsi minal-masyriqi fa`ti bihā minal-magribi fa buhitallażī kafar, wallāhu lā yahdil-qaumaẓ-ẓālimīn","at":"Tidakkah kamu memperhatikan orang yang mendebat Ibrahim mengenai Tuhannya, karena Allah telah memberinya kerajaan (kekuasaan). Ketika Ibrahim berkata, “Tuhanku ialah Yang menghidupkan dan mematikan,” dia berkata, “Aku pun dapat menghidupkan dan mematikan.” Ibrahim berkata, “Allah menerbitkan matahari dari timur, maka terbitkanlah ia dari barat.” Maka bingunglah orang yang kafir itu. Allah tidak memberi petunjuk kepada orang-orang zalim."},{"id":"259","ar":"اَوْ كَالَّذِيْ مَرَّ عَلٰى قَرْيَةٍ وَّهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَاۚ قَالَ اَنّٰى يُحْيٖ هٰذِهِ اللّٰهُ بَعْدَ مَوْتِهَا ۚ فَاَمَاتَهُ اللّٰهُ مِائَةَ عَامٍ ثُمَّ بَعَثَهٗ ۗ قَالَ كَمْ لَبِثْتَ ۗ قَالَ لَبِثْتُ يَوْمًا اَوْ بَعْضَ يَوْمٍۗ قَالَ بَلْ لَّبِثْتَ مِائَةَ عَامٍ فَانْظُرْ اِلٰى طَعَامِكَ وَشَرَابِكَ لَمْ يَتَسَنَّهْ ۚ وَانْظُرْ اِلٰى حِمَارِكَۗ وَلِنَجْعَلَكَ اٰيَةً لِّلنَّاسِ وَانْظُرْ اِلَى الْعِظَامِ كَيْفَ نُنْشِزُهَا ثُمَّ نَكْسُوْهَا لَحْمًا ۗ فَلَمَّا تَبَيَّنَ لَهٗ ۙ قَالَ اَعْلَمُ اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ","bc":"au kallażī marra 'alā qaryatiw wa hiya khāwiyatun 'alā 'urụsyihā, qāla annā yuḥyī hāżihillāhu ba'da mautihā, fa amātahullāhu mi`ata 'āmin ṡumma ba'aṡah, qāla kam labiṡt, qāla labiṡtu yauman au ba'ḍa yaụm, qāla bal labiṡta mi`ata 'āmin fanẓur ilā ṭa'āmika wa syarābika lam yatasannah, wanẓur ilā ḥimārik, wa linaj'alaka āyatal lin-nāsi wanẓur ilal-'iẓāmi kaifa nunsyizuhā ṡumma naksụhā laḥmā, fa lammā tabayyana lahụ qāla a'lamu annallāha 'alā kulli syai`ing qadīr","at":"Atau seperti orang yang melewati suatu negeri yang (bangunan-bangunannya) telah roboh hingga menutupi (reruntuhan) atap-atapnya, dia berkata, “Bagaimana Allah menghidupkan kembali (negeri) ini setelah hancur?” Lalu Allah mematikannya (orang itu) selama seratus tahun, kemudian membangkitkannya (menghidupkannya) kembali. Dan (Allah) bertanya, “Berapa lama engkau tinggal (di sini)?” Dia (orang itu) menjawab, “Aku tinggal (di sini) sehari atau setengah hari.” Allah berfirman, “Tidak! Engkau telah tinggal seratus tahun. Lihatlah makanan dan minumanmu yang belum berubah, tetapi lihatlah keledaimu (yang telah menjadi tulang belulang). Dan agar Kami jadikan engkau tanda kekuasaan Kami bagi manusia. Lihatlah tulang belulang (keledai itu), bagaimana Kami menyusunnya kembali, kemudian Kami membalutnya dengan daging.” Maka ketika telah nyata baginya, dia pun berkata, “Saya mengetahui bahwa Allah Mahakuasa atas segala sesuatu.”"},{"id":"260","ar":"وَاِذْ قَالَ اِبْرٰهٖمُ رَبِّ اَرِنِيْ كَيْفَ تُحْيِ الْمَوْتٰىۗ قَالَ اَوَلَمْ تُؤْمِنْ ۗقَالَ بَلٰى وَلٰكِنْ لِّيَطْمَىِٕنَّ قَلْبِيْ ۗقَالَ فَخُذْ اَرْبَعَةً مِّنَ الطَّيْرِفَصُرْهُنَّ اِلَيْكَ ثُمَّ اجْعَلْ عَلٰى كُلِّ جَبَلٍ مِّنْهُنَّ جُزْءًا ثُمَّ ادْعُهُنَّ يَأْتِيْنَكَ سَعْيًا ۗوَاعْلَمْ اَنَّ اللّٰهَ عَزِيْزٌحَكِيْمٌ","bc":"wa iż qāla ibrāhīmu rabbi arinī kaifa tuḥyil-mautā, qāla a wa lam tu`min, qāla balā wa lākil liyaṭma`inna qalbī, qāla fakhuż arba'atam minaṭ-ṭairi fa ṣur-hunna ilaika ṡummaj'al 'alā kulli jabalim min-hunna juz`an ṡummad'uhunna ya`tīnaka sa'yā, wa'lam annallāha 'azīzun ḥakīm","at":"Dan (ingatlah) ketika Ibrahim berkata, “Ya Tuhanku, perlihatkanlah kepadaku bagaimana Engkau menghidupkan orang mati.” Allah berfirman, “Belum percayakah engkau?” Dia (Ibrahim) menjawab, “Aku percaya, tetapi agar hatiku tenang (mantap).” Dia (Allah) berfirman, “Kalau begitu ambillah empat ekor burung, lalu cincanglah olehmu kemudian letakkan di atas masing-masing bukit satu bagian, kemudian panggillah mereka, niscaya mereka datang kepadamu dengan segera.” Ketahuilah bahwa Allah Mahaperkasa, Mahabijaksana."},{"id":"261","ar":"مَثَلُ الَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ فِيْ سَبِيْلِ اللّٰهِ كَمَثَلِ حَبَّةٍ اَنْۢبَتَتْ سَبْعَ سَنَابِلَ فِيْ كُلِّ سُنْۢبُلَةٍ مِّائَةُ حَبَّةٍ ۗ وَاللّٰهُ يُضٰعِفُ لِمَنْ يَّشَاۤءُ ۗوَاللّٰهُ وَاسِعٌ عَلِيْمٌ","bc":"maṡalullażīna yunfiqụna amwālahum fī sabīlillāhi kamaṡali ḥabbatin ambatat sab'a sanābila fī kulli sumbulatim mi`atu ḥabbah, wallāhu yuḍā'ifu limay yasyā`, wallāhu wāsi'un 'alīm","at":"Perumpamaan orang yang menginfakkan hartanya di jalan Allah seperti sebutir biji yang menumbuhkan tujuh tangkai, pada setiap tangkai ada seratus biji. Allah melipatgandakan bagi siapa yang Dia kehendaki, dan Allah Mahaluas, Maha Mengetahui."},{"id":"262","ar":"اَلَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ فِيْ سَبِيْلِ اللّٰهِ ثُمَّ لَا يُتْبِعُوْنَ مَآ اَنْفَقُوْا مَنًّا وَّلَآ اَذًىۙ لَّهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ","bc":"allażīna yunfiqụna amwālahum fī sabīlillāhi ṡumma lā yutbi'ụna mā anfaqụ mannaw wa lā ażal lahum ajruhum 'inda rabbihim, wa lā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Orang yang menginfakkan hartanya di jalan Allah, kemudian tidak mengiringi apa yang dia infakkan itu dengan menyebut-nyebutnya dan menyakiti (perasaan penerima), mereka memperoleh pahala di sisi Tuhan mereka. Tidak ada rasa takut pada mereka dan mereka tidak bersedih hati."},{"id":"263","ar":"۞ قَوْلٌ مَّعْرُوْفٌ وَّمَغْفِرَةٌ خَيْرٌ مِّنْ صَدَقَةٍ يَّتْبَعُهَآ اَذًى ۗ وَاللّٰهُ غَنِيٌّ حَلِيْمٌ","bc":"qaulum ma'rụfuw wa magfiratun khairum min ṣadaqatiy yatba'uhā ażā, wallāhu ganiyyun ḥalīm","at":"Perkataan yang baik dan pemberian maaf lebih baik daripada sedekah yang diiringi tindakan yang menyakiti. Allah Mahakaya, Maha Penyantun."},{"id":"264","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُبْطِلُوْا صَدَقٰتِكُمْ بِالْمَنِّ وَالْاَذٰىۙ كَالَّذِيْ يُنْفِقُ مَالَهٗ رِئَاۤءَ النَّاسِ وَلَا يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ فَمَثَلُهٗ كَمَثَلِ صَفْوَانٍ عَلَيْهِ تُرَابٌ فَاَصَابَهٗ وَابِلٌ فَتَرَكَهٗ صَلْدًا ۗ لَا يَقْدِرُوْنَ عَلٰى شَيْءٍ مِّمَّا كَسَبُوْا ۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ","bc":"yā ayyuhallażīna āmanụ lā tubṭilụ ṣadaqātikum bil-manni wal-ażā kallażī yunfiqu mālahụ ri`ā`an-nāsi wa lā yu`minu billāhi wal-yaumil-ākhir, fa maṡaluhụ kamaṡali ṣafwānin 'alaihi turābun fa aṣābahụ wābilun fa tarakahụ ṣaldā, lā yaqdirụna 'alā syai`im mimmā kasabụ, wallāhu lā yahdil-qaumal-kāfirīn","at":"Wahai orang-orang yang beriman! Janganlah kamu merusak sedekahmu dengan menyebut-nyebutnya dan menyakiti (perasaan penerima), seperti orang yang menginfakkan hartanya karena ria (pamer) kepada manusia dan dia tidak beriman kepada Allah dan hari akhir. Perumpamaannya (orang itu) seperti batu yang licin yang di atasnya ada debu, kemudian batu itu ditimpa hujan lebat, maka tinggallah batu itu licin lagi. Mereka tidak memperoleh sesuatu apa pun dari apa yang mereka kerjakan. Dan Allah tidak memberi petunjuk kepada orang-orang kafir."},{"id":"265","ar":"وَمَثَلُ الَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمُ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ وَتَثْبِيْتًا مِّنْ اَنْفُسِهِمْ كَمَثَلِ جَنَّةٍۢ بِرَبْوَةٍ اَصَابَهَا وَابِلٌ فَاٰتَتْ اُكُلَهَا ضِعْفَيْنِۚ فَاِنْ لَّمْ يُصِبْهَا وَابِلٌ فَطَلٌّ ۗوَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ","bc":"wa maṡalullażīna yunfiqụna amwālahumubtigā`a marḍātillāhi wa taṡbītam min anfusihim kamaṡali jannatim birabwatin aṣābahā wābilun fa ātat ukulahā ḍi'faīn, fa il lam yuṣib-hā wābilun fa ṭall, wallāhu bimā ta'malụna baṣīr","at":"Dan perumpamaan orang yang menginfakkan hartanya untuk mencari rida Allah dan untuk memperteguh jiwa mereka, seperti sebuah kebun yang terletak di dataran tinggi yang disiram oleh hujan lebat, maka kebun itu menghasilkan buah-buahan dua kali lipat. Jika hujan lebat tidak menyiraminya, maka embun (pun memadai). Allah Maha Melihat apa yang kamu kerjakan."},{"id":"266","ar":"اَيَوَدُّ اَحَدُكُمْ اَنْ تَكُوْنَ لَهٗ جَنَّةٌ مِّنْ نَّخِيْلٍ وَّاَعْنَابٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۙ لَهٗ فِيْهَا مِنْ كُلِّ الثَّمَرٰتِۙ وَاَصَابَهُ الْكِبَرُ وَلَهٗ ذُرِّيَّةٌ ضُعَفَاۤءُۚ فَاَصَابَهَآ اِعْصَارٌ فِيْهِ نَارٌ فَاحْتَرَقَتْ ۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَتَفَكَّرُوْنَ","bc":"a yawaddu aḥadukum an takụna lahụ jannatum min nakhīliw wa a'nābin tajrī min taḥtihal-an-hāru lahụ fīhā ming kulliṡ-ṡamarāti wa aṣābahul-kibaru wa lahụ żurriyyatun ḍu'afā`, fa aṣābahā i'ṣārun fīhi nārun faḥtaraqat, każālika yubayyinullāhu lakumul-āyāti la'allakum tatafakkarụn","at":"Adakah salah seorang di antara kamu yang ingin memiliki kebun kurma dan anggur yang mengalir di bawahnya sungai-sungai, di sana dia memiliki segala macam buah-buahan, kemudian datanglah masa tuanya sedang dia memiliki keturunan yang masih kecil-kecil. Lalu kebun itu ditiup angin keras yang mengandung api, sehingga terbakar. Demikianlah Allah menerangkan ayat-ayat-Nya kepadamu agar kamu memikirkannya."},{"id":"267","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَنْفِقُوْا مِنْ طَيِّبٰتِ مَا كَسَبْتُمْ وَمِمَّآ اَخْرَجْنَا لَكُمْ مِّنَ الْاَرْضِ ۗ وَلَا تَيَمَّمُوا الْخَبِيْثَ مِنْهُ تُنْفِقُوْنَ وَلَسْتُمْ بِاٰخِذِيْهِ اِلَّآ اَنْ تُغْمِضُوْا فِيْهِ ۗ وَاعْلَمُوْٓا اَنَّ اللّٰهَ غَنِيٌّ حَمِيْدٌ","bc":"yā ayyuhallażīna āmanū anfiqụ min ṭayyibāti mā kasabtum wa mimmā akhrajnā lakum minal-arḍ, wa lā tayammamul-khabīṡa min-hu tunfiqụna wa lastum bi`ākhiżīhi illā an tugmiḍụ fīh, wa'lamū annallāha ganiyyun ḥamīd","at":"Wahai orang-orang yang beriman! Infakkanlah sebagian dari hasil usahamu yang baik-baik dan sebagian dari apa yang Kami keluarkan dari bumi untukmu. Janganlah kamu memilih yang buruk untuk kamu keluarkan, padahal kamu sendiri tidak mau mengambilnya melainkan dengan memicingkan mata (enggan) terhadapnya. Dan ketahuilah bahwa Allah Mahakaya, Maha Terpuji."},{"id":"268","ar":"اَلشَّيْطٰنُ يَعِدُكُمُ الْفَقْرَ وَيَأْمُرُكُمْ بِالْفَحْشَاۤءِ ۚ وَاللّٰهُ يَعِدُكُمْ مَّغْفِرَةً مِّنْهُ وَفَضْلًا ۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ ۖ ","bc":"asy-syaiṭānu ya'idukumul-faqra wa ya`murukum bil-faḥsyā`, wallāhu ya'idukum magfiratam min-hu wa faḍlā, wallāhu wāsi'un 'alīm","at":"Setan menjanjikan (menakut-nakuti) kemiskinan kepadamu dan menyuruh kamu berbuat keji (kikir), sedangkan Allah menjanjikan ampunan dan karunia-Nya kepadamu. Dan Allah Mahaluas, Maha Mengetahui."},{"id":"269","ar":"يُّؤْتِى الْحِكْمَةَ مَنْ يَّشَاۤءُ ۚ وَمَنْ يُّؤْتَ الْحِكْمَةَ فَقَدْ اُوْتِيَ خَيْرًا كَثِيْرًا ۗ وَمَا يَذَّكَّرُ اِلَّآ اُولُوا الْاَلْبَابِ","bc":"yu`til-ḥikmata may yasyā`, wa may yu`tal-ḥikmata fa qad ụtiya khairang kaṡīrā, wa mā yażżakkaru illā ulul-albāb","at":"Dia memberikan hikmah kepada siapa yang Dia kehendaki. Barangsiapa diberi hikmah, sesungguhnya dia telah diberi kebaikan yang banyak. Dan tidak ada yang dapat mengambil pelajaran kecuali orang-orang yang mempunyai akal sehat."},{"id":"270","ar":"وَمَآ اَنْفَقْتُمْ مِّنْ نَّفَقَةٍ اَوْ نَذَرْتُمْ مِّنْ نَّذْرٍ فَاِنَّ اللّٰهَ يَعْلَمُهٗ ۗ وَمَا لِلظّٰلِمِيْنَ مِنْ اَنْصَارٍ","bc":"wa mā anfaqtum min nafaqatin au nażartum min nażrin fa innallāha ya'lamuh, wa mā liẓ-ẓālimīna min anṣār","at":"Dan apa pun infak yang kamu berikan atau nazar yang kamu janjikan, maka sungguh, Allah mengetahuinya. Dan bagi orang zalim tidak ada seorang penolong pun"},{"id":"271","ar":"اِنْ تُبْدُوا الصَّدَقٰتِ فَنِعِمَّا هِيَۚ وَاِنْ تُخْفُوْهَا وَتُؤْتُوْهَا الْفُقَرَاۤءَ فَهُوَ خَيْرٌ لَّكُمْ ۗ وَيُكَفِّرُ عَنْكُمْ مِّنْ سَيِّاٰتِكُمْ ۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ","bc":"in tubduṣ-ṣadaqāti fa ni'immā hiy, wa in tukhfụhā wa tu`tụhal-fuqarā`a fa huwa khairul lakum, wa yukaffiru 'angkum min sayyi`ātikum, wallāhu bimā ta'malụna khabīr","at":"Jika kamu menampakkan sedekah-sedekahmu, maka itu baik. Dan jika kamu menyembunyikannya dan memberikannya kepada orang-orang fakir, maka itu lebih baik bagimu dan Allah akan menghapus sebagian kesalahan-kesalahanmu. Dan Allah Mahateliti apa yang kamu kerjakan."},{"id":"272","ar":"۞ لَيْسَ عَلَيْكَ هُدٰىهُمْ وَلٰكِنَّ اللّٰهَ يَهْدِيْ مَنْ يَّشَاۤءُ ۗوَمَا تُنْفِقُوْا مِنْ خَيْرٍ فَلِاَنْفُسِكُمْ ۗوَمَا تُنْفِقُوْنَ اِلَّا ابْتِغَاۤءَ وَجْهِ اللّٰهِ ۗوَمَا تُنْفِقُوْا مِنْ خَيْرٍ يُّوَفَّ اِلَيْكُمْ وَاَنْتُمْ لَا تُظْلَمُوْنَ","bc":"laisa 'alaika hudāhum wa lākinnallāha yahdī may yasyā`, wa mā tunfiqụ min khairin fa li`anfusikum, wa mā tunfiqụna illabtigā`a waj-hillāh, wa mā tunfiqụ min khairiy yuwaffa ilaikum wa antum lā tuẓlamụn","at":"Bukanlah kewajibanmu (Muhammad) menjadikan mereka mendapat petunjuk, tetapi Allah-lah yang memberi petunjuk kepada siapa yang Dia kehendaki. Apa pun harta yang kamu infakkan, maka (kebaikannya) untuk dirimu sendiri. Dan janganlah kamu berinfak melainkan karena mencari rida Allah. Dan apa pun harta yang kamu infakkan, niscaya kamu akan diberi (pahala) secara penuh dan kamu tidak akan dizalimi (dirugikan)."},{"id":"273","ar":"لِلْفُقَرَاۤءِ الَّذِيْنَ اُحْصِرُوْا فِيْ سَبِيْلِ اللّٰهِ لَا يَسْتَطِيْعُوْنَ ضَرْبًا فِى الْاَرْضِۖ يَحْسَبُهُمُ الْجَاهِلُ اَغْنِيَاۤءَ مِنَ التَّعَفُّفِۚ تَعْرِفُهُمْ بِسِيْمٰهُمْۚ لَا يَسْـَٔلُوْنَ النَّاسَ اِلْحَافًا ۗوَمَا تُنْفِقُوْا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ بِهٖ عَلِيْمٌ","bc":"lil-fuqarā`illażīna uḥṣirụ fī sabīlillāhi lā yastaṭī'ụna ḍarban fil-arḍi yaḥsabuhumul-jāhilu agniyā`a minat-ta'affuf, ta'rifuhum bisīmāhum, lā yas`alụnan-nāsa il-ḥāfā, wa mā tunfiqụ min khairin fa innallāha bihī 'alīm","at":"(Apa yang kamu infakkan) adalah untuk orang-orang fakir yang terhalang (usahanya karena jihad) di jalan Allah, sehingga dia yang tidak dapat berusaha di bumi; (orang lain) yang tidak tahu, menyangka bahwa mereka adalah orang-orang kaya karena mereka menjaga diri (dari meminta-minta). Engkau (Muhammad) mengenal mereka dari ciri-cirinya, mereka tidak meminta secara paksa kepada orang lain. Apa pun harta yang baik yang kamu infakkan, sungguh, Allah Maha Mengetahui."},{"id":"274","ar":"اَلَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ بِالَّيْلِ وَالنَّهَارِ سِرًّا وَّعَلَانِيَةً فَلَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ","bc":"allażīna yunfiqụna amwālahum bil-laili wan-nahāri sirraw wa 'alāniyatan fa lahum ajruhum 'inda rabbihim, wa lā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Orang-orang yang menginfakkan hartanya malam dan siang hari (secara) sembunyi-sembunyi maupun terang-terangan, mereka mendapat pahala di sisi Tuhannya. Tidak ada rasa takut pada mereka dan mereka tidak bersedih hati."},{"id":"275","ar":"اَلَّذِيْنَ يَأْكُلُوْنَ الرِّبٰوا لَا يَقُوْمُوْنَ اِلَّا كَمَا يَقُوْمُ الَّذِيْ يَتَخَبَّطُهُ الشَّيْطٰنُ مِنَ الْمَسِّۗ ذٰلِكَ بِاَنَّهُمْ قَالُوْٓا اِنَّمَا الْبَيْعُ مِثْلُ الرِّبٰواۘ وَاَحَلَّ اللّٰهُ الْبَيْعَ وَحَرَّمَ الرِّبٰواۗ فَمَنْ جَاۤءَهٗ مَوْعِظَةٌ مِّنْ رَّبِّهٖ فَانْتَهٰى فَلَهٗ مَا سَلَفَۗ وَاَمْرُهٗٓ اِلَى اللّٰهِ ۗ وَمَنْ عَادَ فَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ","bc":"allażīna ya`kulụnar-ribā lā yaqụmụna illā kamā yaqụmullażī yatakhabbaṭuhusy-syaiṭānu minal-mass, żālika bi`annahum qālū innamal-bai'u miṡlur-ribā, wa aḥallallāhul-bai'a wa ḥarramar-ribā, fa man jā`ahụ mau'iẓatum mir rabbihī fantahā fa lahụ mā salaf, wa amruhū ilallāh, wa man 'āda fa ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn","at":"Orang-orang yang memakan riba tidak dapat berdiri melainkan seperti berdirinya orang yang kemasukan setan karena gila. Yang demikian itu karena mereka berkata bahwa jual beli sama dengan riba. Padahal Allah telah menghalalkan jual beli dan mengharamkan riba. Barangsiapa mendapat peringatan dari Tuhannya, lalu dia berhenti, maka apa yang telah diperolehnya dahulu menjadi miliknya dan urusannya (terserah) kepada Allah. Barangsiapa mengulangi, maka mereka itu penghuni neraka, mereka kekal di dalamnya."},{"id":"276","ar":"يَمْحَقُ اللّٰهُ الرِّبٰوا وَيُرْبِى الصَّدَقٰتِ ۗ وَاللّٰهُ لَا يُحِبُّ كُلَّ كَفَّارٍ اَثِيْمٍ","bc":"yam-ḥaqullāhur-ribā wa yurbiṣ-ṣadaqāt, wallāhu lā yuḥibbu kulla kaffārin aṡīm","at":"Allah memusnahkan riba dan menyuburkan sedekah. Allah tidak menyukai setiap orang yang tetap dalam kekafiran dan bergelimang dosa."},{"id":"277","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْۚ وَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ","bc":"innallażīna āmanụ wa 'amiluṣ-ṣāliḥāti wa aqāmuṣ-ṣalāta wa ātawuz-zakāta lahum ajruhum 'inda rabbihim, wa lā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Sungguh, orang-orang yang beriman, mengerjakan kebajikan, melaksanakan salat dan menunaikan zakat, mereka mendapat pahala di sisi Tuhannya. Tidak ada rasa takut pada mereka dan mereka tidak bersedih hati."},{"id":"278","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَذَرُوْا مَا بَقِيَ مِنَ الرِّبٰوٓا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ","bc":"yā ayyuhallażīna āmanuttaqullāha wa żarụ mā baqiya minar-ribā ing kuntum mu`minīn","at":"Wahai orang-orang yang beriman! Bertakwalah kepada Allah dan tinggalkan sisa riba (yang belum dipungut) jika kamu orang beriman."},{"id":"279","ar":"فَاِنْ لَّمْ تَفْعَلُوْا فَأْذَنُوْا بِحَرْبٍ مِّنَ اللّٰهِ وَرَسُوْلِهٖۚ وَاِنْ تُبْتُمْ فَلَكُمْ رُءُوْسُ اَمْوَالِكُمْۚ لَا تَظْلِمُوْنَ وَلَا تُظْلَمُوْنَ","bc":"fa il lam taf'alụ fa`żanụ biḥarbim minallāhi wa rasụlih, wa in tubtum fa lakum ru`ụsu amwālikum, lā taẓlimụna wa lā tuẓlamụn","at":"Jika kamu tidak melaksanakannya, maka umumkanlah perang dari Allah dan Rasul-Nya. Tetapi jika kamu bertobat, maka kamu berhak atas pokok hartamu. Kamu tidak berbuat zalim (merugikan) dan tidak dizalimi (dirugikan)."},{"id":"280","ar":"وَاِنْ كَانَ ذُوْ عُسْرَةٍ فَنَظِرَةٌ اِلٰى مَيْسَرَةٍ ۗ وَاَنْ تَصَدَّقُوْا خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ","bc":"wa ing kāna żụ 'usratin fa naẓiratun ilā maisarah, wa an taṣaddaqụ khairul lakum ing kuntum ta'lamụn","at":"Dan jika (orang berutang itu) dalam kesulitan, maka berilah tenggang waktu sampai dia memperoleh kelapangan. Dan jika kamu menyedekahkan, itu lebih baik bagimu, jika kamu mengetahui."},{"id":"281","ar":"وَاتَّقُوْا يَوْمًا تُرْجَعُوْنَ فِيْهِ اِلَى اللّٰهِ ۗثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ","bc":"wattaqụ yauman turja'ụna fīhi ilallāh, ṡumma tuwaffā kullu nafsim mā kasabat wa hum lā yuẓlamụn","at":"Dan takutlah pada hari (ketika) kamu semua dikembalikan kepada Allah. Kemudian setiap orang diberi balasan yang sempurna sesuai dengan apa yang telah dilakukannya, dan mereka tidak dizalimi (dirugikan)."},{"id":"282","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا تَدَايَنْتُمْ بِدَيْنٍ اِلٰٓى اَجَلٍ مُّسَمًّى فَاكْتُبُوْهُۗ وَلْيَكْتُبْ بَّيْنَكُمْ كَاتِبٌۢ بِالْعَدْلِۖ وَلَا يَأْبَ كَاتِبٌ اَنْ يَّكْتُبَ كَمَا عَلَّمَهُ اللّٰهُ فَلْيَكْتُبْۚ وَلْيُمْلِلِ الَّذِيْ عَلَيْهِ الْحَقُّ وَلْيَتَّقِ اللّٰهَ رَبَّهٗ وَلَا يَبْخَسْ مِنْهُ شَيْـًٔاۗ فَاِنْ كَانَ الَّذِيْ عَلَيْهِ الْحَقُّ سَفِيْهًا اَوْ ضَعِيْفًا اَوْ لَا يَسْتَطِيْعُ اَنْ يُّمِلَّ هُوَ فَلْيُمْلِلْ وَلِيُّهٗ بِالْعَدْلِۗ وَاسْتَشْهِدُوْا شَهِيْدَيْنِ مِنْ رِّجَالِكُمْۚ فَاِنْ لَّمْ يَكُوْنَا رَجُلَيْنِ فَرَجُلٌ وَّامْرَاَتٰنِ مِمَّنْ تَرْضَوْنَ مِنَ الشُّهَدَۤاءِ اَنْ تَضِلَّ اِحْدٰىهُمَا فَتُذَكِّرَ اِحْدٰىهُمَا الْاُخْرٰىۗ وَلَا يَأْبَ الشُّهَدَۤاءُ اِذَا مَا دُعُوْا ۗ وَلَا تَسْـَٔمُوْٓا اَنْ تَكْتُبُوْهُ صَغِيْرًا اَوْ كَبِيْرًا اِلٰٓى اَجَلِهٖۗ ذٰلِكُمْ اَقْسَطُ عِنْدَ اللّٰهِ وَاَقْوَمُ لِلشَّهَادَةِ وَاَدْنٰىٓ اَلَّا تَرْتَابُوْٓا اِلَّآ اَنْ تَكُوْنَ تِجَارَةً حَاضِرَةً تُدِيْرُوْنَهَا بَيْنَكُمْ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ اَلَّا تَكْتُبُوْهَاۗ وَاَشْهِدُوْٓا اِذَا تَبَايَعْتُمْ ۖ وَلَا يُضَاۤرَّ كَاتِبٌ وَّلَا شَهِيْدٌ ەۗ وَاِنْ تَفْعَلُوْا فَاِنَّهٗ فُسُوْقٌۢ بِكُمْ ۗ وَاتَّقُوا اللّٰهَ ۗ وَيُعَلِّمُكُمُ اللّٰهُ ۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ","bc":"yā ayyuhallażīna āmanū iżā tadāyantum bidainin ilā ajalim musamman faktubụh, walyaktub bainakum kātibum bil-'adli wa lā ya`ba kātibun ay yaktuba kamā 'allamahullāhu falyaktub, walyumlilillażī 'alaihil-ḥaqqu walyattaqillāha rabbahụ wa lā yabkhas min-hu syai`ā, fa ing kānallażī 'alaihil-ḥaqqu safīhan au ḍa'īfan au lā yastaṭī'u ay yumilla huwa falyumlil waliyyuhụ bil-'adl, wastasy-hidụ syahīdaini mir rijālikum, fa il lam yakụnā rajulaini fa rajuluw wamra`atāni mim man tarḍauna minasy-syuhadā`i an taḍilla iḥdāhumā fa tużakkira iḥdāhumal-ukhrā, wa lā ya`basy-syuhadā`u iżā mā du'ụ, wa lā tas`amū an taktubụhu ṣagīran au kabīran ilā ajalih, żālikum aqsaṭu 'indallāhi wa aqwamu lisy-syahādati wa adnā allā tartābū illā an takụna tijāratan ḥāḍiratan tudīrụnahā bainakum fa laisa 'alaikum junāḥun allā taktubụhā, wa asy-hidū iżā tabāya'tum wa lā yuḍārra kātibuw wa lā syahīd, wa in taf'alụ fa innahụ fusụqum bikum, wattaqullāh, wa yu'allimukumullāh, wallāhu bikulli syai`in 'alīm","at":"Wahai orang-orang yang beriman! Apabila kamu melakukan utang piutang untuk waktu yang ditentukan, hendaklah kamu menuliskannya. Dan hendaklah seorang penulis di antara kamu menuliskannya dengan benar. Janganlah penulis menolak untuk menuliskannya sebagaimana Allah telah mengajarkan kepadanya, maka hendaklah dia menuliskan. Dan hendaklah orang yang berutang itu mendiktekan, dan hendaklah dia bertakwa kepada Allah, Tuhannya, dan janganlah dia mengurangi sedikit pun daripadanya. Jika yang berutang itu orang yang kurang akalnya atau lemah (keadaannya), atau tidak mampu mendiktekan sendiri, maka hendaklah walinya mendiktekannya dengan benar. Dan persaksikanlah dengan dua orang saksi laki-laki di antara kamu. Jika tidak ada (saksi) dua orang laki-laki, maka (boleh) seorang laki-laki dan dua orang perempuan di antara orang-orang yang kamu sukai dari para saksi (yang ada), agar jika yang seorang lupa, maka yang seorang lagi mengingatkannya. Dan janganlah saksi-saksi itu menolak apabila dipanggil. Dan janganlah kamu bosan menuliskannya, untuk batas waktunya baik (utang itu) kecil maupun besar. Yang demikian itu, lebih adil di sisi Allah, lebih dapat menguatkan kesaksian, dan lebih mendekatkan kamu kepada ketidakraguan, kecuali jika hal itu merupakan perdagangan tunai yang kamu jalankan di antara kamu, maka tidak ada dosa bagi kamu jika kamu tidak menuliskannya. Dan ambillah saksi apabila kamu berjual beli, dan janganlah penulis dipersulit dan begitu juga saksi. Jika kamu lakukan (yang demikian), maka sungguh, hal itu suatu kefasikan pada kamu. Dan bertakwalah kepada Allah, Allah memberikan pengajaran kepadamu, dan Allah Maha Mengetahui segala sesuatu."},{"id":"283","ar":"۞ وَاِنْ كُنْتُمْ عَلٰى سَفَرٍ وَّلَمْ تَجِدُوْا كَاتِبًا فَرِهٰنٌ مَّقْبُوْضَةٌ ۗفَاِنْ اَمِنَ بَعْضُكُمْ بَعْضًا فَلْيُؤَدِّ الَّذِى اؤْتُمِنَ اَمَانَتَهٗ وَلْيَتَّقِ اللّٰهَ رَبَّهٗ ۗ وَلَا تَكْتُمُوا الشَّهَادَةَۗ وَمَنْ يَّكْتُمْهَا فَاِنَّهٗٓ اٰثِمٌ قَلْبُهٗ ۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ عَلِيْمٌ","bc":"wa ing kuntum 'alā safariw wa lam tajidụ kātiban fa rihānum maqbụḍah, fa in amina ba'ḍukum ba'ḍan falyu`addillażi`tumina amānatahụ walyattaqillāha rabbah, wa lā taktumusy-syahādah, wa may yaktum-hā fa innahū āṡimung qalbuh, wallāhu bimā ta'malụna 'alīm","at":"Dan jika kamu dalam perjalanan sedang kamu tidak mendapatkan seorang penulis, maka hendaklah ada barang jaminan yang dipegang. Tetapi, jika sebagian kamu mempercayai sebagian yang lain, hendaklah yang dipercayai itu menunaikan amanatnya (utangnya) dan hendaklah dia bertakwa kepada Allah, Tuhannya. Dan janganlah kamu menyembunyikan kesaksian, karena barangsiapa menyembunyikannya, sungguh, hatinya kotor (berdosa). Allah Maha Mengetahui apa yang kamu kerjakan."},{"id":"284","ar":" لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗ وَاِنْ تُبْدُوْا مَا فِيْٓ اَنْفُسِكُمْ اَوْ تُخْفُوْهُ يُحَاسِبْكُمْ بِهِ اللّٰهُ ۗ فَيَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ","bc":"lillāhi mā fis-samāwāti wa mā fil-arḍ, wa in tubdụ mā fī anfusikum au tukhfụhu yuḥāsibkum bihillāh, fa yagfiru limay yasyā`u wa yu'ażżibu may yasyā`, wallāhu 'alā kulli syai`ing qadīr","at":"Milik Allah-lah apa yang ada di langit dan apa yang ada di bumi. Jika kamu nyatakan apa yang ada di dalam hatimu atau kamu sembunyikan, niscaya Allah memperhitungkannya (tentang perbuatan itu) bagimu. Dia mengampuni siapa yang Dia kehendaki dan mengazab siapa yang Dia kehendaki. Allah Mahakuasa atas segala sesuatu."},{"id":"285","ar":"اٰمَنَ الرَّسُوْلُ بِمَآ اُنْزِلَ اِلَيْهِ مِنْ رَّبِّهٖ وَالْمُؤْمِنُوْنَۗ كُلٌّ اٰمَنَ بِاللّٰهِ وَمَلٰۤىِٕكَتِهٖ وَكُتُبِهٖ وَرُسُلِهٖۗ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِّنْ رُّسُلِهٖ ۗ وَقَالُوْا سَمِعْنَا وَاَطَعْنَا غُفْرَانَكَ رَبَّنَا وَاِلَيْكَ الْمَصِيْرُ","bc":"āmanar-rasụlu bimā unzila ilaihi mir rabbihī wal-mu`minụn, kullun āmana billāhi wa malā`ikatihī wa kutubihī wa rusulih, lā nufarriqu baina aḥadim mir rusulih, wa qālụ sami'nā wa aṭa'nā gufrānaka rabbanā wa ilaikal-maṣīr","at":"Rasul (Muhammad) beriman kepada apa yang diturunkan kepadanya (Al-Qur'an) dari Tuhannya, demikian pula orang-orang yang beriman. Semua beriman kepada Allah, malaikat-malaikat-Nya, kitab-kitab-Nya dan rasul-rasul-Nya. (Mereka berkata), “Kami tidak membeda-bedakan seorang pun dari rasul-rasul-Nya.” Dan mereka berkata, “Kami dengar dan kami taat. Ampunilah kami Ya Tuhan kami, dan kepada-Mu tempat (kami) kembali.”"},{"id":"286","ar":"لَا يُكَلِّفُ اللّٰهُ نَفْسًا اِلَّا وُسْعَهَا ۗ لَهَا مَا كَسَبَتْ وَعَلَيْهَا مَا اكْتَسَبَتْ ۗ رَبَّنَا لَا تُؤَاخِذْنَآ اِنْ نَّسِيْنَآ اَوْ اَخْطَأْنَا ۚ رَبَّنَا وَلَا تَحْمِلْ عَلَيْنَآ اِصْرًا كَمَا حَمَلْتَهٗ عَلَى الَّذِيْنَ مِنْ قَبْلِنَا ۚ رَبَّنَا وَلَا تُحَمِّلْنَا مَا لَا طَاقَةَ لَنَا بِهٖۚ وَاعْفُ عَنَّاۗ وَاغْفِرْ لَنَاۗ وَارْحَمْنَا ۗ اَنْتَ مَوْلٰىنَا فَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ","bc":"lā yukallifullāhu nafsan illā wus'ahā, lahā mā kasabat wa 'alaihā maktasabat, rabbanā lā tu`ākhiżnā in nasīnā au akhṭa`nā, rabbanā wa lā taḥmil 'alainā iṣrang kamā ḥamaltahụ 'alallażīna ming qablinā, rabbanā wa lā tuḥammilnā mā lā ṭāqata lanā bih, wa'fu 'annā, wagfir lanā, war-ḥamnā, anta maulānā fanṣurnā 'alal-qaumil-kāfirīn","at":"Allah tidak membebani seseorang melainkan sesuai dengan kesanggupannya. Dia mendapat (pahala) dari (kebajikan) yang dikerjakannya dan dia mendapat (siksa) dari (kejahatan) yang diperbuatnya. (Mereka berdoa), “Ya Tuhan kami, janganlah Engkau hukum kami jika kami lupa atau kami melakukan kesalahan. Ya Tuhan kami, janganlah Engkau bebani kami dengan beban yang berat sebagaimana Engkau bebankan kepada orang-orang sebelum kami. Ya Tuhan kami, janganlah Engkau pikulkan kepada kami apa yang tidak sanggup kami memikulnya. Maafkanlah kami, ampunilah kami, dan rahmatilah kami. Engkaulah pelindung kami, maka tolonglah kami menghadapi orang-orang kafir.”"}],al_bayyinah=[{"id":"1","ar":"لَمْ يَكُنِ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَالْمُشْرِكِيْنَ مُنْفَكِّيْنَ حَتّٰى تَأْتِيَهُمُ الْبَيِّنَةُۙ","bc":"lam yakunillażīna kafarụ min ahlil-kitābi wal-musyrikīna munfakkīna ḥattā ta`tiyahumul-bayyinah","at":"Orang-orang yang kafir dari golongan Ahli Kitab dan orang-orang musyrik tidak akan meninggalkan (agama mereka) sampai datang kepada mereka bukti yang nyata,"},{"id":"2","ar":"رَسُوْلٌ مِّنَ اللّٰهِ يَتْلُوْا صُحُفًا مُّطَهَّرَةًۙ","bc":"rasụlum minallāhi yatlụ ṣuḥufam muṭahharah","at":"(yaitu) seorang Rasul dari Allah (Muhammad) yang membacakan lembaran-lembaran yang suci (Al-Qur'an),"},{"id":"3","ar":"فِيْهَا كُتُبٌ قَيِّمَةٌ ۗ","bc":"fīhā kutubung qayyimah","at":"di dalamnya terdapat (isi) kitab-kitab yang lurus (benar)."},{"id":"4","ar":"وَمَا تَفَرَّقَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنَةُ ۗ","bc":"wa mā tafarraqallażīna ụtul-kitāba illā mim ba'di mā jā`at-humul-bayyinah","at":"Dan tidaklah terpecah-belah orang-orang Ahli Kitab melainkan setelah datang kepada mereka bukti yang nyata."},{"id":"5","ar":"وَمَآ اُمِرُوْٓا اِلَّا لِيَعْبُدُوا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۙ حُنَفَاۤءَ وَيُقِيْمُوا الصَّلٰوةَ وَيُؤْتُوا الزَّكٰوةَ وَذٰلِكَ دِيْنُ الْقَيِّمَةِۗ","bc":"wa mā umirū illā liya'budullāha mukhliṣīna lahud-dīna ḥunafā`a wa yuqīmuṣ-ṣalāta wa yu`tuz-zakāta wa żālika dīnul-qayyimah","at":"Padahal mereka hanya diperintah menyembah Allah dengan ikhlas menaati-Nya semata-mata karena (menjalankan) agama, dan juga agar melaksanakan salat dan menunaikan zakat; dan yang demikian itulah agama yang lurus (benar)."},{"id":"6","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ وَالْمُشْرِكِيْنَ فِيْ نَارِ جَهَنَّمَ خٰلِدِيْنَ فِيْهَاۗ اُولٰۤىِٕكَ هُمْ شَرُّ الْبَرِيَّةِۗ","bc":"innallażīna kafarụ min ahlil-kitābi wal-musyrikīna fī nāri jahannama khālidīna fīhā, ulā`ika hum syarrul-bariyyah","at":"Sungguh, orang-orang yang kafir dari golongan Ahli Kitab dan orang-orang musyrik (akan masuk) ke neraka Jahanam; mereka kekal di dalamnya selama-lamanya. Mereka itu adalah sejahat-jahat makhluk."},{"id":"7","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اُولٰۤىِٕكَ هُمْ خَيْرُ الْبَرِيَّةِۗ","bc":"innallażīna āmanụ wa 'amiluṣ-ṣāliḥāti ulā`ika hum khairul-bariyyah","at":"Sungguh, orang-orang yang beriman dan mengerjakan kebajikan, mereka itu adalah sebaik-baik makhluk."},{"id":"8","ar":"جَزَاۤؤُهُمْ عِنْدَ رَبِّهِمْ جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗرَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ ۗ ذٰلِكَ لِمَنْ خَشِيَ رَبَّهٗ","bc":"jazā`uhum 'inda rabbihim jannātu 'adnin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, raḍiyallāhu 'an-hum wa raḍụ 'an-h, żālika liman khasyiya rabbah","at":"Balasan mereka di sisi Tuhan mereka ialah surga ’Adn yang mengalir di bawahnya sungai-sungai; mereka kekal di dalamnya selama-lamanya. Allah rida terhadap mereka dan mereka pun rida kepada-Nya. Yang demikian itu adalah (balasan) bagi orang yang takut kepada Tuhannya. "}],al_buruj=[{"id":"1","ar":"وَالسَّمَاۤءِ ذَاتِ الْبُرُوْجِۙ ","bc":"was-samā`i żātil-burụj","at":"Demi langit yang mempunyai gugusan bintang,"},{"id":"2","ar":"وَالْيَوْمِ الْمَوْعُوْدِۙ ","bc":"wal-yaumil-mau'ụd","at":"dan demi hari yang dijanjikan."},{"id":"3","ar":"وَشَاهِدٍ وَّمَشْهُوْدٍۗ ","bc":"wa syāhidiw wa masy-hụd","at":"Demi yang menyaksikan dan yang disaksikan."},{"id":"4","ar":"قُتِلَ اَصْحٰبُ الْاُخْدُوْدِۙ ","bc":"qutila aṣ-ḥābul-ukhdụd","at":"Binasalah orang-orang yang membuat parit (yaitu para pembesar Najran di Yaman),"},{"id":"5","ar":"النَّارِ ذَاتِ الْوَقُوْدِۙ ","bc":"an-nāri żātil-waqụd","at":"yang berapi (yang mempunyai) kayu bakar,"},{"id":"6","ar":"اِذْ هُمْ عَلَيْهَا قُعُوْدٌۙ ","bc":"iż hum 'alaihā qu'ụd","at":"ketika mereka duduk di sekitarnya, "},{"id":"7","ar":"وَّهُمْ عَلٰى مَا يَفْعَلُوْنَ بِالْمُؤْمِنِيْنَ شُهُوْدٌ ۗ ","bc":"wa hum 'alā mā yaf'alụna bil-mu`minīna syuhụd","at":"sedang mereka menyaksikan apa yang mereka perbuat terhadap orang-orang mukmin."},{"id":"8","ar":"وَمَا نَقَمُوْا مِنْهُمْ اِلَّآ اَنْ يُّؤْمِنُوْا بِاللّٰهِ الْعَزِيْزِ الْحَمِيْدِۙ ","bc":"wa mā naqamụ min-hum illā ay yu`minụ billāhil-'azīzil-ḥamīd","at":"Dan mereka menyiksa orang-orang mukmin itu hanya karena (orang-orang mukmin itu) beriman kepada Allah Yang Mahaperkasa, Maha Terpuji,"},{"id":"9","ar":"الَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ۗ ","bc":"allażī lahụ mulkus-samāwāti wal-arḍ, wallāhu 'alā kulli syai`in syahīd","at":"yang memiliki kerajaan langit dan bumi. Dan Allah Maha Menyaksikan segala sesuatu."},{"id":"10","ar":"اِنَّ الَّذِيْنَ فَتَنُوا الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ ثُمَّ لَمْ يَتُوْبُوْا فَلَهُمْ عَذَابُ جَهَنَّمَ وَلَهُمْ عَذَابُ الْحَرِيْقِۗ ","bc":"innallażīna fatanul-mu`minīna wal-mu`mināti ṡumma lam yatụbụ fa lahum 'ażābu jahannama wa lahum 'ażābul-ḥarīq","at":"Sungguh, orang-orang yang mendatangkan cobaan (bencana, membunuh, menyiksa) kepada orang-orang mukmin laki-laki dan perempuan lalu mereka tidak bertobat, maka mereka akan mendapat azab Jahanam dan mereka akan mendapat azab (neraka) yang membakar."},{"id":"11","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ەۗ ذٰلِكَ الْفَوْزُ الْكَبِيْرُۗ","bc":"innallażīna āmanụ wa 'amiluṣ-ṣāliḥāti lahum jannātun tajrī min taḥtihal-an-hār, żālikal-fauzul-kabīr","at":"Sungguh, orang-orang yang beriman dan mengerjakan kebajikan, mereka akan mendapat surga yang mengalir di bawahnya sungai-sungai, itulah kemenangan yang agung."},{"id":"12","ar":"اِنَّ بَطْشَ رَبِّكَ لَشَدِيْدٌ ۗ ","bc":"inna baṭsya rabbika lasyadīd","at":"Sungguh, azab Tuhanmu sangat keras."},{"id":"13","ar":"اِنَّهٗ هُوَ يُبْدِئُ وَيُعِيْدُۚ ","bc":"innahụ huwa yubdi`u wa yu'īd","at":"Sungguh, Dialah yang memulai pen-ciptaan (makhluk) dan yang menghidupkannya (kembali)."},{"id":"14","ar":"وَهُوَ الْغَفُوْرُ الْوَدُوْدُۙ ","bc":"wa huwal-gafụrul-wadụd","at":"Dan Dialah Yang Maha Pengampun, Maha Pengasih,"},{"id":"15","ar":"ذُو الْعَرْشِ الْمَجِيْدُۙ ","bc":"żul-'arsyil-majīd","at":"yang memiliki ‘Arsy, lagi Mahamulia, "},{"id":"16","ar":"فَعَّالٌ لِّمَا يُرِيْدُۗ ","bc":"fa''ālul limā yurīd","at":"Mahakuasa berbuat apa yang Dia kehendaki. "},{"id":"17","ar":"هَلْ اَتٰىكَ حَدِيْثُ الْجُنُوْدِۙ ","bc":"hal atāka ḥadīṡul-junụd","at":"Sudahkah sampai kepadamu berita tentang bala tentara (penentang),"},{"id":"18","ar":"فِرْعَوْنَ وَثَمُوْدَۗ ","bc":"fir'auna wa ṡamụd","at":"(yaitu) Fir‘aun dan Samud?"},{"id":"19","ar":"بَلِ الَّذِيْنَ كَفَرُوْا فِيْ تَكْذِيْبٍۙ ","bc":"balillażīna kafarụ fī takżīb","at":"Memang orang-orang kafir (selalu) mendustakan,"},{"id":"20","ar":"وَّاللّٰهُ مِنْ وَّرَاۤىِٕهِمْ مُّحِيْطٌۚ ","bc":"wallāhu miw warā`ihim muḥīṭ","at":"padahal Allah mengepung dari belakang mereka (sehingga tidak dapat lolos)."},{"id":"21","ar":"بَلْ هُوَ قُرْاٰنٌ مَّجِيْدٌۙ ","bc":"bal huwa qur`ānum majīd","at":"Bahkan (yang didustakan itu) ialah Al-Qur'an yang mulia,"},{"id":"22","ar":"فِيْ لَوْحٍ مَّحْفُوْظٍ ","bc":"fī lauḥim maḥfụẓ","at":"yang (tersimpan) dalam (tempat) yang terjaga (Lauh Mahfuzh)."}],al_fajr=[{"id":"1","ar":"وَالْفَجْرِۙ","bc":"wal-fajr","at":"Demi fajar,"},{"id":"2","ar":"وَلَيَالٍ عَشْرٍۙ","bc":"wa layālin 'asyr","at":"demi malam yang sepuluh,"},{"id":"3","ar":"وَّالشَّفْعِ وَالْوَتْرِۙ","bc":"wasy-syaf'i wal-watr","at":"demi yang genap dan yang ganjil,"},{"id":"4","ar":"وَالَّيْلِ اِذَا يَسْرِۚ","bc":"wal-laili iżā yasr","at":"demi malam apabila berlalu."},{"id":"5","ar":"هَلْ فِيْ ذٰلِكَ قَسَمٌ لِّذِيْ حِجْرٍۗ","bc":"hal fī żālika qasamul liżī ḥijr","at":"Adakah pada yang demikian itu terdapat sumpah (yang dapat diterima) bagi orang-orang yang berakal?"},{"id":"6","ar":"اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍۖ","bc":"a lam tara kaifa fa'ala rabbuka bi'ād","at":"Tidakkah engkau (Muhammad) memperhatikan bagaimana Tuhanmu berbuat terhadap (kaum) ‘Ad?"},{"id":"7","ar":"اِرَمَ ذَاتِ الْعِمَادِۖ","bc":"irama żātil-'imād","at":"(yaitu) penduduk Iram (ibukota kaum ‘Ad) yang mempunyai bangunan-bangunan yang tinggi,"},{"id":"8","ar":"الَّتِيْ لَمْ يُخْلَقْ مِثْلُهَا فِى الْبِلَادِۖ","bc":"allatī lam yukhlaq miṡluhā fil-bilād","at":"yang belum pernah dibangun (suatu kota) seperti itu di negeri-negeri lain,"},{"id":"9","ar":"وَثَمُوْدَ الَّذِيْنَ جَابُوا الصَّخْرَ بِالْوَادِۖ","bc":"wa ṡamụdallażīna jābuṣ-ṣakhra bil-wād","at":"dan (terhadap) kaum samud yang memotong batu-batu besar di lembah,"},{"id":"10","ar":"وَفِرْعَوْنَ ذِى الْاَوْتَادِۖ","bc":"wa fir'auna żil-autād","at":"dan (terhadap) Fir‘aun yang mempunyai pasak-pasak (bangunan yang besar),"},{"id":"11","ar":"الَّذِيْنَ طَغَوْا فِى الْبِلَادِۖ","bc":"allażīna ṭagau fil-bilād","at":"yang berbuat sewenang-wenang dalam negeri,"},{"id":"12","ar":"فَاَكْثَرُوْا فِيْهَا الْفَسَادَۖ","bc":"fa akṡarụ fīhal-fasād","at":"lalu mereka banyak berbuat kerusakan dalam negeri itu,"},{"id":"13","ar":"فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍۖ","bc":"fa ṣabba 'alaihim rabbuka sauṭa 'ażāb","at":"karena itu Tuhanmu menimpakan cemeti azab kepada mereka,"},{"id":"14","ar":"اِنَّ رَبَّكَ لَبِالْمِرْصَادِۗ","bc":"inna rabbaka labil-mirṣād","at":"sungguh, Tuhanmu benar-benar mengawasi."},{"id":"15","ar":"فَاَمَّا الْاِنْسَانُ اِذَا مَا ابْتَلٰىهُ رَبُّهٗ فَاَكْرَمَهٗ وَنَعَّمَهٗۙ فَيَقُوْلُ رَبِّيْٓ اَكْرَمَنِۗ","bc":"fa ammal-insānu iżā mabtalāhu rabbuhụ fa akramahụ wa na''amahụ fa yaqụlu rabbī akraman","at":"Maka adapun manusia, apabila Tuhan mengujinya lalu memuliakannya dan memberinya kesenangan, maka dia berkata, “Tuhanku telah memuliakanku.”"},{"id":"16","ar":"وَاَمَّآ اِذَا مَا ابْتَلٰىهُ فَقَدَرَ عَلَيْهِ رِزْقَهٗ ەۙ فَيَقُوْلُ رَبِّيْٓ اَهَانَنِۚ","bc":"wa ammā iżā mabtalāhu fa qadara 'alaihi rizqahụ fa yaqụlu rabbī ahānan","at":"Namun apabila Tuhan mengujinya lalu membatasi rezekinya, maka dia berkata, “Tuhanku telah menghinaku.”"},{"id":"17","ar":"كَلَّا بَلْ لَّا تُكْرِمُوْنَ الْيَتِيْمَۙ","bc":"kallā bal lā tukrimụnal-yatīm","at":"Sekali-kali tidak! Bahkan kamu tidak memuliakan anak yatim,"},{"id":"18","ar":"وَلَا تَحٰۤضُّوْنَ عَلٰى طَعَامِ الْمِسْكِيْنِۙ","bc":"wa lā tahāḍḍụna 'alā ṭa'āmil-miskīn","at":"dan kamu tidak saling mengajak memberi makan orang miskin,"},{"id":"19","ar":"وَتَأْكُلُوْنَ التُّرَاثَ اَكْلًا لَّمًّاۙ","bc":"wa ta`kulụnat-turāṡa aklal lammā","at":"sedangkan kamu memakan harta warisan dengan cara mencampurbaurkan (yang halal dan yang haram),"},{"id":"20","ar":"وَّتُحِبُّوْنَ الْمَالَ حُبًّا جَمًّاۗ","bc":"wa tuḥibbụnal-māla ḥubban jammā","at":"dan kamu mencintai harta dengan kecintaan yang berlebihan."},{"id":"21","ar":"كَلَّآ اِذَا دُكَّتِ الْاَرْضُ دَكًّا دَكًّاۙ","bc":"kallā iżā dukkatil-arḍu dakkan dakkā","at":"Sekali-kali tidak! Apabila bumi diguncangkan berturut-turut (berbenturan),"},{"id":"22","ar":"وَّجَآءَ رَبُّكَ وَالْمَلَكُ صَفًّا صَفًّاۚ","bc":"wa jā`a rabbuka wal-malaku ṣaffan ṣaffā","at":"dan datanglah Tuhanmu; dan malaikat berbaris-baris,"},{"id":"23","ar":"وَجِايْۤءَ يَوْمَىِٕذٍۢ بِجَهَنَّمَۙ يَوْمَىِٕذٍ يَّتَذَكَّرُ الْاِنْسَانُ وَاَنّٰى لَهُ الذِّكْرٰىۗ","bc":"wa jī`a yauma`iżim bijahannama yauma`iżiy yatażakkarul-insānu wa annā lahuż-żikrā","at":"dan pada hari itu diperlihatkan neraka Jahanam; pada hari itu sadarlah manusia, tetapi tidak berguna lagi baginya kesadaran itu."},{"id":"24","ar":"يَقُوْلُ يٰلَيْتَنِيْ قَدَّمْتُ لِحَيَاتِيْۚ","bc":"yaqụlu yā laitanī qaddamtu liḥayātī","at":"Dia berkata, “Alangkah baiknya sekiranya dahulu aku mengerjakan (kebajikan) untuk hidupku ini.”"},{"id":"25","ar":"فَيَوْمَىِٕذٍ لَّا يُعَذِّبُ عَذَابَهٗٓ اَحَدٌ ۙ","bc":"fa yauma`iżil lā yu'ażżibu 'ażābahū aḥad","at":"Maka pada hari itu tidak ada seorang pun yang mengazab seperti azab-Nya (yang adil),"},{"id":"26","ar":"وَّلَا يُوْثِقُ وَثَاقَهٗٓ اَحَدٌ ۗ","bc":"wa lā yụṡiqu waṡāqahū aḥad","at":"dan tidak ada seorang pun yang mengikat seperti ikatan-Nya."},{"id":"27","ar":"يٰٓاَيَّتُهَا النَّفْسُ الْمُطْمَىِٕنَّةُۙ","bc":"yā ayyatuhan-nafsul-muṭma`innah","at":"Wahai jiwa yang tenang!"},{"id":"28","ar":"ارْجِعِيْٓ اِلٰى رَبِّكِ رَاضِيَةً مَّرْضِيَّةً ۚ","bc":"irji'ī ilā rabbiki rāḍiyatam marḍiyyah","at":"Kembalilah kepada Tuhanmu dengan hati yang rida dan diridai-Nya."},{"id":"29","ar":"فَادْخُلِيْ فِيْ عِبٰدِيْۙ","bc":"fadkhulī fī 'ibādī","at":"Maka masuklah ke dalam golongan hamba-hamba-Ku,"},{"id":"30","ar":"وَادْخُلِيْ جَنَّتِيْࣖ","bc":"wadkhulī jannatī","at":"dan masuklah ke dalam surga-Ku."}],al_falaq=[{"id":"1","ar":"قُلْ اَعُوْذُ بِرَبِّ الْفَلَقِۙ","bc":"qul a'ụżu birabbil-falaq","at":"Katakanlah, “Aku berlindung kepada Tuhan yang menguasai subuh (fajar),"},{"id":"2","ar":"مِنْ شَرِّ مَا خَلَقَۙ","bc":"min syarri mā khalaq","at":"dari kejahatan (makhluk yang) Dia ciptakan,"},{"id":"3","ar":"وَمِنْ شَرِّ غَاسِقٍ اِذَا وَقَبَۙ","bc":"wa min syarri gāsiqin iżā waqab","at":"dan dari kejahatan malam apabila telah gelap gulita,"},{"id":"4","ar":"وَمِنْ شَرِّ النَّفّٰثٰتِ فِى الْعُقَدِۙ","bc":"wa min syarrin-naffāṡāti fil-'uqad","at":"dan dari kejahatan (perempuan-perempuan) penyihir yang meniup pada buhul-buhul (talinya),"},{"id":"5","ar":"وَمِنْ شَرِّ حَاسِدٍ اِذَا حَسَدَ","bc":"wa min syarri ḥāsidin iżā ḥasad","at":"dan dari kejahatan orang yang dengki apabila dia dengki.”"}],al_fath=[{"id":"1","ar":" اِنَّا فَتَحْنَا لَكَ فَتْحًا مُّبِيْنًاۙ","bc":"innā fataḥnā laka fat-ḥam mubīnā","at":"Sungguh, Kami telah memberikan kepadamu kemenangan yang nyata."},{"id":"2","ar":"لِّيَغْفِرَ لَكَ اللّٰهُ مَا تَقَدَّمَ مِنْ ذَنْۢبِكَ وَمَا تَاَخَّرَ وَيُتِمَّ نِعْمَتَهٗ عَلَيْكَ وَيَهْدِيَكَ صِرَاطًا مُّسْتَقِيْمًاۙ","bc":"liyagfira lakallāhu mā taqaddama min żambika wa mā ta`akhkhara wa yutimma ni'matahụ 'alaika wa yahdiyaka ṣirāṭam mustaqīmā","at":"Agar Allah memberikan ampunan kepadamu (Muhammad) atas dosamu yang lalu dan yang akan datang serta menyempurnakan nikmat-Nya atasmu dan menunjukimu ke jalan yang lurus,"},{"id":"3","ar":"وَّيَنْصُرَكَ اللّٰهُ نَصْرًا عَزِيْزًا ","bc":"wa yanṣurakallāhu naṣran 'azīzā","at":"dan agar Allah menolongmu dengan pertolongan yang kuat (banyak)."},{"id":"4","ar":"هُوَ الَّذِيْٓ اَنْزَلَ السَّكِيْنَةَ فِيْ قُلُوْبِ الْمُؤْمِنِيْنَ لِيَزْدَادُوْٓا اِيْمَانًا مَّعَ اِيْمَانِهِمْ ۗ وَلِلّٰهِ جُنُوْدُ السَّمٰوٰتِ وَالْاَرْضِۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًاۙ","bc":"huwallażī anzalas-sakīnata fī qulụbil-mu`minīna liyazdādū īmānam ma'a īmānihim, wa lillāhi junụdus-samāwāti wal-arḍ, wa kānallāhu 'alīman ḥakīmā","at":"Dialah yang telah menurunkan ketenangan ke dalam hati orang-orang mukmin untuk menambah keimanan atas keimanan mereka (yang telah ada). Dan milik Allah-lah bala tentara langit dan bumi, dan Allah Maha Mengetahui, Mahabijaksana;"},{"id":"5","ar":"لِّيُدْخِلَ الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا وَيُكَفِّرَ عَنْهُمْ سَيِّاٰتِهِمْۗ وَكَانَ ذٰلِكَ عِنْدَ اللّٰهِ فَوْزًا عَظِيْمًاۙ","bc":"liyudkhilal-mu`minīna wal-mu`mināti jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā wa yukaffira 'an-hum sayyi`ātihim, wa kāna żālika 'indallāhi fauzan 'aẓīmā","at":"Agar Dia masukkan orang-orang mukmin laki-laki dan perempuan ke dalam surga yang mengalir di bawahnya sungai-sungai. Mereka kekal di dalamnya dan Dia akan menghapus kesalahan-kesalahan mereka. Dan yang demikian itu menurut Allah suatu keuntungan yang besar,"},{"id":"6","ar":"وَّيُعَذِّبَ الْمُنٰفِقِيْنَ وَالْمُنٰفِقٰتِ وَالْمُشْرِكِيْنَ وَالْمُشْرِكٰتِ الظَّاۤنِّيْنَ بِاللّٰهِ ظَنَّ السَّوْءِۗ عَلَيْهِمْ دَاۤىِٕرَةُ السَّوْءِۚ وَغَضِبَ اللّٰهُ عَلَيْهِمْ وَلَعَنَهُمْ وَاَعَدَّ لَهُمْ جَهَنَّمَۗ وَسَاۤءَتْ مَصِيْرًا ","bc":"wa yu'ażżibal-munāfiqīna wal-munāfiqāti wal-musyrikīna wal-musyrikātiẓ-ẓānnīna billāhi ẓannas-saụ`, 'alaihim dā`iratus-saụ`, wa gaḍiballāhu 'alaihim wa la'anahum wa a'adda lahum jahannam, wa sā`at maṣīrā","at":"dan Dia mengazab orang-orang munafik laki-laki dan perempuan, dan (juga) orang-orang musyrik laki-laki dan perempuan yang berprasangka buruk terhadap Allah. Mereka akan mendapat giliran (azab) yang buruk dan Allah murka kepada mereka dan mengutuk mereka serta menyediakan neraka Jahanam bagi mereka. Dan (neraka Jahanam) itu seburuk-buruk tempat kembali. "},{"id":"7","ar":"وَلِلّٰهِ جُنُوْدُ السَّمٰوٰتِ وَالْاَرْضِۗ وَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا","bc":"wa lillāhi junụdus-samāwāti wal-arḍ, wa kānallāhu 'azīzan ḥakīmā","at":"Dan milik Allah bala tentara langit dan bumi. Dan Allah Mahaperkasa, Mahabijaksana. "},{"id":"8","ar":"اِنَّآ اَرْسَلْنٰكَ شَاهِدًا وَّمُبَشِّرًا وَّنَذِيْرًاۙ","bc":"innā arsalnāka syāhidaw wa mubasysyiraw wa nażīrā","at":"Sesungguhnya Kami mengutus engkau (Muhammad) sebagai saksi, pembawa berita gembira dan pemberi peringatan,"},{"id":"9","ar":"لِّتُؤْمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَتُعَزِّرُوْهُ وَتُوَقِّرُوْهُۗ وَتُسَبِّحُوْهُ بُكْرَةً وَّاَصِيْلًا ","bc":"litu`minụ billāhi wa rasụlihī wa tu'azzirụhu wa tuwaqqirụh, wa tusabbiḥụhu bukrataw wa aṣīlā","at":"agar kamu semua beriman kepada Allah dan Rasul-Nya, menguatkan (agama)-Nya, membesarkan-Nya, dan bertasbih kepada-Nya pagi dan petang."},{"id":"10","ar":"اِنَّ الَّذِيْنَ يُبَايِعُوْنَكَ اِنَّمَا يُبَايِعُوْنَ اللّٰهَ ۗيَدُ اللّٰهِ فَوْقَ اَيْدِيْهِمْ ۚ فَمَنْ نَّكَثَ فَاِنَّمَا يَنْكُثُ عَلٰى نَفْسِهٖۚ وَمَنْ اَوْفٰى بِمَا عٰهَدَ عَلَيْهُ اللّٰهَ فَسَيُؤْتِيْهِ اَجْرًا عَظِيْمًا ","bc":"innallażīna yubāyi'ụnaka innamā yubāyi'ụnallāh, yadullāhi fauqa aidīhim, fa man nakaṡa fa innamā yangkuṡu 'alā nafsih, wa man aufā bimā 'āhada 'alaihullāha fa sayu`tīhi ajran 'aẓīmā","at":"Bahwasanya orang-orang yang berjanji setia kepadamu (Muhammad), sesungguhnya mereka hanya berjanji setia kepada Allah. Tangan Allah di atas tangan mereka, maka barangsiapa melanggar janji, maka sesungguhnya dia melanggar atas (janji) sendiri; dan barangsiapa menepati janjinya kepada Allah maka Dia akan memberinya pahala yang besar. "},{"id":"11","ar":"سَيَقُوْلُ لَكَ الْمُخَلَّفُوْنَ مِنَ الْاَعْرَابِ شَغَلَتْنَآ اَمْوَالُنَا وَاَهْلُوْنَا فَاسْتَغْفِرْ لَنَا ۚيَقُوْلُوْنَ بِاَلْسِنَتِهِمْ مَّا لَيْسَ فِيْ قُلُوْبِهِمْۗ قُلْ فَمَنْ يَّمْلِكُ لَكُمْ مِّنَ اللّٰهِ شَيْـًٔا اِنْ اَرَادَ بِكُمْ ضَرًّا اَوْ اَرَادَ بِكُمْ نَفْعًا ۗبَلْ كَانَ اللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرًا ","bc":"sayaqụlu lakal-mukhallafụna minal-a'rābi syagalatnā amwālunā wa ahlụnā fastagfir lanā, yaqụlụna bi`alsinatihim mā laisa fī qulụbihim, qul fa may yamliku lakum minallāhi syai`an in arāda bikum ḍarran au arāda bikum naf'ā, bal kānallāhu bimā ta'malụna khabīrā","at":"Orang-orang Badui yang tertinggal (tidak turut ke Hudaibiyah) akan berkata kepadamu, “Kami telah disibukkan oleh harta dan keluarga kami, maka mohonkanlah ampunan untuk kami.” Mereka mengucapkan sesuatu dengan mulutnya apa yang tidak ada dalam hatinya. Katakanlah, “Maka siapakah yang dapat menghalang-halangi kehendak Allah jika Dia menghendaki bencana terhadap kamu atau jika Dia menghendaki keuntungan bagimu? Sungguh, Allah Mahateliti dengan apa yang kamu kerjakan.” "},{"id":"12","ar":"بَلْ ظَنَنْتُمْ اَنْ لَّنْ يَّنْقَلِبَ الرَّسُوْلُ وَالْمُؤْمِنُوْنَ اِلٰٓى اَهْلِيْهِمْ اَبَدًا وَّزُيِّنَ ذٰلِكَ فِيْ قُلُوْبِكُمْ وَظَنَنْتُمْ ظَنَّ السَّوْءِۚ وَكُنْتُمْ قَوْمًاۢ بُوْرًا ","bc":"bal ẓanantum al lay yangqalibar-rasụlu wal-mu`minụna ilā ahlīhim abadaw wa zuyyina żālika fī qulụbikum wa ẓanantum ẓannas-saụ`, wa kuntum qaumam bụrā","at":"Bahkan (semula) kamu menyangka bahwa Rasul dan orang-orang mukmin sekali-kali tidak akan kembali lagi kepada keluarga mereka selama-lamanya dan dijadikan terasa indah yang demikian itu di dalam hatimu, dan kamu telah berprasangka dengan prasangka yang buruk, karena itu kamu menjadi kaum yang binasa. "},{"id":"13","ar":"وَمَنْ لَّمْ يُؤْمِنْۢ بِاللّٰهِ وَرَسُوْلِهٖ فَاِنَّآ اَعْتَدْنَا لِلْكٰفِرِيْنَ سَعِيْرًا ","bc":"wa mal lam yu`mim billāhi wa rasụlihī fa innā a'tadnā lil-kāfirīna sa'īrā","at":"Dan barangsiapa tidak beriman kepada Allah dan Rasul-Nya, maka sesungguhnya Kami telah menyediakan untuk orang-orang kafir itu neraka yang menyala-nyala. "},{"id":"14","ar":"وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا","bc":"wa lillāhi mulkus-samāwāti wal-arḍ, yagfiru limay yasyā`u wa yu'ażżibu may yasyā`, wa kānallāhu gafụrar raḥīmā","at":"Dan hanya milik Allah kerajaan langit dan bumi. Dia mengampuni siapa yang Dia kehendaki dan akan mengazab siapa yang Dia kehendaki. Dan Allah Maha Pengampun, Maha Penyayang. "},{"id":"15","ar":"سَيَقُوْلُ الْمُخَلَّفُوْنَ اِذَا انْطَلَقْتُمْ اِلٰى مَغَانِمَ لِتَأْخُذُوْهَا ذَرُوْنَا نَتَّبِعْكُمْ ۚ يُرِيْدُوْنَ اَنْ يُّبَدِّلُوْا كَلٰمَ اللّٰهِ ۗ قُلْ لَّنْ تَتَّبِعُوْنَا كَذٰلِكُمْ قَالَ اللّٰهُ مِنْ قَبْلُ ۖفَسَيَقُوْلُوْنَ بَلْ تَحْسُدُوْنَنَا ۗ بَلْ كَانُوْا لَا يَفْقَهُوْنَ اِلَّا قَلِيْلًا","bc":"sayaqụlul-mukhallafụna iżanṭalaqtum ilā magānima lita`khużụhā żarụnā nattabi'kum, yurīdụna ay yubaddilụ kalāmallāh, qul lan tattabi'ụnā każālikum qālallāhu ming qabl, fa sayaqụlụna bal taḥsudụnanā, bal kānụ lā yafqahụna illā qalīlā","at":"Apabila kamu berangkat untuk mengambil barang rampasan, orang-orang Badui yang tertinggal itu akan berkata, “Biarkanlah kami mengikuti kamu.” Mereka hendak mengubah janji Allah. Katakanlah, “Kamu sekali-kali tidak (boleh) mengikuti kami. Demikianlah yang telah ditetapkan Allah sejak semula.” Maka mereka akan berkata, “Sebenarnya kamu dengki kepada kami.” Padahal mereka tidak mengerti melainkan sedikit sekali. "},{"id":"16","ar":"قُلْ لِّلْمُخَلَّفِيْنَ مِنَ الْاَعْرَابِ سَتُدْعَوْنَ اِلٰى قَوْمٍ اُولِيْ بَأْسٍ شَدِيْدٍ تُقَاتِلُوْنَهُمْ اَوْ يُسْلِمُوْنَ ۚ فَاِنْ تُطِيْعُوْا يُؤْتِكُمُ اللّٰهُ اَجْرًا حَسَنًا ۚ وَاِنْ تَتَوَلَّوْا كَمَا تَوَلَّيْتُمْ مِّنْ قَبْلُ يُعَذِّبْكُمْ عَذَابًا اَلِيْمًا ","bc":"qul lil-mukhallafīna minal-a'rābi satud'auna ilā qaumin ulī ba`sin syadīdin tuqātilụnahum au yuslimụn, fa in tuṭī'ụ yu`tikumullāhu ajran ḥasanā, wa in tatawallau kamā tawallaitum ming qablu yu'ażżibkum 'ażāban alīmā","at":"Katakanlah kepada orang-orang Badui yang tertinggal, “Kamu akan diajak untuk (memerangi) kaum yang mempunyai kekuatan yang besar, kamu harus memerangi mereka kecuali mereka menyerah. Jika kamu patuhi (ajakan itu) Allah akan memberimu pahala yang baik, tetapi jika kamu berpaling seperti yang kamu perbuat sebelumnya, Dia akan mengazab kamu dengan azab yang pedih.” "},{"id":"17","ar":"لَيْسَ عَلَى الْاَعْمٰى حَرَجٌ وَّلَا عَلَى الْاَعْرَجِ حَرَجٌ وَّلَا عَلَى الْمَرِيْضِ حَرَجٌ ۗ وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ يُدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ۚ وَمَنْ يَّتَوَلَّ يُعَذِّبْهُ عَذَابًا اَلِيْمًا ","bc":"laisa 'alal-a'mā ḥarajuw wa lā 'alal-a'raji ḥarajuw wa lā 'alal-marīḍi ḥaraj, wa may yuṭi'illāha wa rasụlahụ yudkhil-hu jannātin tajrī min taḥtihal-an-hār, wa may yatawalla yu'ażżib-hu 'ażāban alīmā","at":"Tidak ada dosa atas orang-orang yang buta, atas orang-orang yang pincang, dan atas orang-orang yang sakit (apabila tidak ikut berperang). Barangsiapa taat kepada Allah dan Rasul-Nya, Dia akan memasukkannya ke dalam surga yang mengalir di bawahnya sungai-sungai; tetapi barangsiapa berpaling, Dia akan mengazabnya dengan azab yang pedih. "},{"id":"18","ar":"۞ لَقَدْ رَضِيَ اللّٰهُ عَنِ الْمُؤْمِنِيْنَ اِذْ يُبَايِعُوْنَكَ تَحْتَ الشَّجَرَةِ فَعَلِمَ مَا فِيْ قُلُوْبِهِمْ فَاَنْزَلَ السَّكِيْنَةَ عَلَيْهِمْ وَاَثَابَهُمْ فَتْحًا قَرِيْبًاۙ","bc":"laqad raḍiyallāhu 'anil-mu`minīna iż yubāyi'ụnaka taḥtasy-syajarati fa 'alima mā fī qulụbihim fa anzalas-sakīnata 'alaihim wa aṡābahum fat-ḥang qarībā","at":"Sungguh, Allah telah meridai orang-orang mukmin ketika mereka berjanji setia kepadamu (Muhammad) di bawah pohon, Dia mengetahui apa yang ada dalam hati mereka lalu Dia memberikan ketenangan atas mereka dan memberi balasan dengan kemenangan yang dekat,"},{"id":"19","ar":"وَّمَغَانِمَ كَثِيْرَةً يَّأْخُذُوْنَهَا ۗ وَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا ","bc":"wa magānima kaṡīratay ya`khużụnahā, wa kānallāhu 'azīzan ḥakīmā","at":"dan harta rampasan perang yang banyak yang akan mereka peroleh. Dan Allah Mahaperkasa, Mahabijaksana. "},{"id":"20","ar":"وَعَدَكُمُ اللّٰهُ مَغَانِمَ كَثِيْرَةً تَأْخُذُوْنَهَا فَعَجَّلَ لَكُمْ هٰذِهٖ وَكَفَّ اَيْدِيَ النَّاسِ عَنْكُمْۚ وَلِتَكُوْنَ اٰيَةً لِّلْمُؤْمِنِيْنَ وَيَهْدِيَكُمْ صِرَاطًا مُّسْتَقِيْمًاۙ","bc":"wa 'adakumullāhu magānima kaṡīratan ta`khużụnahā fa 'ajjala lakum hāżihī wa kaffa aidiyan-nāsi 'angkum, wa litakụna āyatal lil-mu`minīna wa yahdiyakum ṣirāṭam mustaqīmā","at":"Allah menjanjikan kepadamu harta rampasan perang yang banyak yang dapat kamu ambil, maka Dia segerakan (harta rampasan perang) ini untukmu dan Dia menahan tangan manusia dari (membinasakan)mu (agar kamu mensyukuri-Nya) dan agar menjadi bukti bagi orang-orang mukmin dan agar Dia menunjukkan kamu ke jalan yang lurus. "},{"id":"21","ar":"وَّاُخْرٰى لَمْ تَقْدِرُوْا عَلَيْهَا قَدْ اَحَاطَ اللّٰهُ بِهَا ۗوَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرًا ","bc":"wa ukhrā lam taqdirụ 'alaihā qad aḥāṭallāhu bihā, wa kānallāhu 'alā kulli syai`ing qadīrā","at":"Dan (kemenangan-kemenangan) atas negeri-negeri lain yang tidak dapat kamu perkirakan, tetapi sesungguhnya Allah telah menentukannya. Dan Allah Mahakuasa atas segala sesuatu. "},{"id":"22","ar":"وَلَوْ قَاتَلَكُمُ الَّذِيْنَ كَفَرُوْا لَوَلَّوُا الْاَدْبَارَ ثُمَّ لَا يَجِدُوْنَ وَلِيًّا وَّلَا نَصِيْرًا ","bc":"walau qātalakumullażīna kafarụ lawallawul-adbāra ṡumma lā yajidụna waliyyaw wa lā naṣīrā","at":"Dan sekiranya orang-orang yang kafir itu memerangi kamu pastilah mereka akan berbalik melarikan diri (kalah) dan mereka tidak akan mendapatkan pelindung dan penolong. "},{"id":"23","ar":"سُنَّةَ اللّٰهِ الَّتِيْ قَدْ خَلَتْ مِنْ قَبْلُ ۖوَلَنْ تَجِدَ لِسُنَّةِ اللّٰهِ تَبْدِيْلًا ","bc":"sunnatallāhillatī qad khalat ming qabl, wa lan tajida lisunnatillāhi tabdīlā","at":"(Demikianlah) hukum Allah, yang telah berlaku sejak dahulu, kamu sekali-kali tidak akan menemukan perubahan pada hukum Allah itu. "},{"id":"24","ar":"وَهُوَ الَّذِيْ كَفَّ اَيْدِيَهُمْ عَنْكُمْ وَاَيْدِيَكُمْ عَنْهُمْ بِبَطْنِ مَكَّةَ مِنْۢ بَعْدِ اَنْ اَظْفَرَكُمْ عَلَيْهِمْ ۗوَكَانَ اللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرًا ","bc":"wa huwallażī kaffa aidiyahum 'angkum wa aidiyakum 'an-hum bibaṭni makkata mim ba'di an aẓfarakum 'alaihim, wa kānallāhu bimā ta'malụna baṣīrā","at":"Dan Dialah yang mencegah tangan mereka dari (membinasakan) kamu dan (mencegah) tangan kamu dari (membinasakan) mereka di tengah (kota) Mekah setelah Allah memenangkan kamu atas mereka. Dan Allah Maha Melihat apa yang kamu kerjakan. "},{"id":"25","ar":"هُمُ الَّذِيْنَ كَفَرُوْا وَصَدُّوْكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ وَالْهَدْيَ مَعْكُوْفًا اَنْ يَّبْلُغَ مَحِلَّهٗ ۚوَلَوْلَا رِجَالٌ مُّؤْمِنُوْنَ وَنِسَاۤءٌ مُّؤْمِنٰتٌ لَّمْ تَعْلَمُوْهُمْ اَنْ تَطَـُٔوْهُمْ فَتُصِيْبَكُمْ مِّنْهُمْ مَّعَرَّةٌ ۢبِغَيْرِ عِلْمٍ ۚ لِيُدْخِلَ اللّٰهُ فِيْ رَحْمَتِهٖ مَنْ يَّشَاۤءُۚ لَوْ تَزَيَّلُوْا لَعَذَّبْنَا الَّذِيْنَ كَفَرُوْا مِنْهُمْ عَذَابًا اَلِيْمًا","bc":"humullażīna kafarụ wa ṣaddụkum 'anil-masjidil-ḥarāmi wal-hadya ma'kụfan ay yabluga maḥillah, walau lā rijālum mu`minụna wa nisā`um mu`minātul lam ta'lamụhum an taṭa'ụhum fa tuṣībakum min-hum ma'arratum bigairi 'ilm, liyudkhilallāhu fī raḥmatihī may yasyā`, lau tazayyalụ la'ażżabnallażīna kafarụ min-hum 'ażāban alīmā","at":"Merekalah orang-orang kafir yang menghalang-halangi kamu (masuk) Masjidilharam dan menghambat hewan-hewan kurban sampai ke tempat (penyembelihan)nya. Dan kalau bukanlah karena ada beberapa orang beriman laki-laki dan perempuan yang tidak kamu ketahui, tentulah kamu akan membunuh mereka yang menyebabkan kamu ditimpa kesulitan tanpa kamu sadari. Karena Allah hendak memasukkan siapa yang Dia kehendaki ke dalam rahmat-Nya. Sekiranya mereka terpisah, tentu Kami akan mengazab orang-orang yang kafir di antara mereka dengan azab yang pedih. "},{"id":"26","ar":"اِذْ جَعَلَ الَّذِيْنَ كَفَرُوْا فِيْ قُلُوْبِهِمُ الْحَمِيَّةَ حَمِيَّةَ الْجَاهِلِيَّةِ فَاَنْزَلَ اللّٰهُ سَكِيْنَتَهٗ عَلٰى رَسُوْلِهٖ وَعَلَى الْمُؤْمِنِيْنَ وَاَلْزَمَهُمْ كَلِمَةَ التَّقْوٰى وَكَانُوْٓا اَحَقَّ بِهَا وَاَهْلَهَا ۗوَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمًا ","bc":"iż ja'alallażīna kafarụ fī qulụbihimul-ḥamiyyata ḥamiyyatal-jāhiliyyati fa anzalallāhu sakīnatahụ 'alā rasụlihī wa 'alal-mu`minīna wa alzamahum kalimatat-taqwā wa kānū aḥaqqa bihā wa ahlahā, wa kānallāhu bikulli syai`in 'alīmā","at":"Ketika orang-orang yang kafir menanamkan kesombongan dalam hati mereka (yaitu) kesombongan jahiliah, lalu Allah menurunkan ketenangan kepada Rasul-Nya, dan kepada orang-orang mukmin; dan (Allah) mewajibkan kepada mereka tetap taat menjalankan kalimat takwa dan mereka lebih berhak dengan itu dan patut memilikinya. Dan Allah Maha Mengetahui segala sesuatu. "},{"id":"27","ar":"لَقَدْ صَدَقَ اللّٰهُ رَسُوْلَهُ الرُّءْيَا بِالْحَقِّ ۚ لَتَدْخُلُنَّ الْمَسْجِدَ الْحَرَامَ اِنْ شَاۤءَ اللّٰهُ اٰمِنِيْنَۙ مُحَلِّقِيْنَ رُءُوْسَكُمْ وَمُقَصِّرِيْنَۙ لَا تَخَافُوْنَ ۗفَعَلِمَ مَا لَمْ تَعْلَمُوْا فَجَعَلَ مِنْ دُوْنِ ذٰلِكَ فَتْحًا قَرِيْبًا ","bc":"laqad ṣadaqallāhu rasụlahur-ru`yā bil-ḥaqq, latadkhulunnal-masjidal-ḥarāma in syā`allāhu āminīna muḥalliqīna ru`ụsakum wa muqaṣṣirīna lā takhāfụn, fa 'alima mā lam ta'lamụ fa ja'ala min dụni żālika fat-ḥang qarībā","at":"Sungguh, Allah akan membuktikan kepada Rasul-Nya tentang kebenaran mimpinya bahwa kamu pasti akan memasuki Masjidilharam, jika Allah menghendaki dalam keadaan aman, dengan menggundul rambut kepala dan memendekkannya, sedang kamu tidak merasa takut. Maka Allah mengetahui apa yang tidak kamu ketahui dan selain itu Dia telah memberikan kemenangan yang dekat."},{"id":"28","ar":"هُوَ الَّذِيْٓ اَرْسَلَ رَسُوْلَهٗ بِالْهُدٰى وَدِيْنِ الْحَقِّ لِيُظْهِرَهٗ عَلَى الدِّيْنِ كُلِّهٖ ۗوَكَفٰى بِاللّٰهِ شَهِيْدًا ","bc":"huwallażī arsala rasụlahụ bil-hudā wa dīnil-ḥaqqi liyuẓ-hirahụ 'alad-dīni kullih, wa kafā billāhi syahīdā","at":"Dialah yang mengutus Rasul-Nya dengan membawa petunjuk dan agama yang benar agar dimenangkan-Nya terhadap semua agama. Dan cukuplah Allah sebagai saksi. "},{"id":"29","ar":"مُحَمَّدٌ رَّسُوْلُ اللّٰهِ ۗوَالَّذِيْنَ مَعَهٗٓ اَشِدَّاۤءُ عَلَى الْكُفَّارِ رُحَمَاۤءُ بَيْنَهُمْ تَرٰىهُمْ رُكَّعًا سُجَّدًا يَّبْتَغُوْنَ فَضْلًا مِّنَ اللّٰهِ وَرِضْوَانًا ۖ سِيْمَاهُمْ فِيْ وُجُوْهِهِمْ مِّنْ اَثَرِ السُّجُوْدِ ۗذٰلِكَ مَثَلُهُمْ فِى التَّوْرٰىةِ ۖوَمَثَلُهُمْ فِى الْاِنْجِيْلِۚ كَزَرْعٍ اَخْرَجَ شَطْـَٔهٗ فَاٰزَرَهٗ فَاسْتَغْلَظَ فَاسْتَوٰى عَلٰى سُوْقِهٖ يُعْجِبُ الزُّرَّاعَ لِيَغِيْظَ بِهِمُ الْكُفَّارَ ۗوَعَدَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ مِنْهُمْ مَّغْفِرَةً وَّاَجْرًا عَظِيْمًا ","bc":"muḥammadur rasụlullāh, wallażīna ma'ahū asyiddā`u 'alal-kuffāri ruḥamā`u bainahum tarāhum rukka'an sujjaday yabtagụna faḍlam minallāhi wa riḍwānan sīmāhum fī wujụhihim min aṡaris-sujụd, żālika maṡaluhum fit-taurāti wa maṡaluhum fil-injīl, kazar'in akhraja syaṭ`ahụ fa āzarahụ fastaglaẓa fastawā 'alā sụqihī yu'jibuz-zurrā'a liyagīẓa bihimul-kuffār, wa'adallāhullażīna āmanụ wa 'amiluṣ-ṣāliḥāti min-hum magfirataw wa ajran 'aẓīmā","at":"Muhammad adalah utusan Allah dan orang-orang yang bersama dengan dia bersikap keras terhadap orang-orang kafir, tetapi berkasih sayang sesama mereka. Kamu melihat mereka rukuk dan sujud mencari karunia Allah dan keridaan-Nya. Pada wajah mereka tampak tanda-tanda bekas sujud. Demikianlah sifat-sifat mereka (yang diungkapkan) dalam Taurat dan sifat-sifat mereka (yang diungkapkan) dalam Injil, yaitu seperti benih yang mengeluarkan tunasnya, kemudian tunas itu semakin kuat lalu menjadi besar dan tegak lurus di atas batangnya; tanaman itu menyenangkan hati penanam-penanamnya karena Allah hendak menjengkelkan hati orang-orang kafir (dengan kekuatan orang-orang mukmin). Allah menjanjikan kepada orang-orang yang beriman dan mengerjakan kebajikan di antara mereka, ampunan dan pahala yang besar. "}],al_fatihah=[{"id":"1","ar":"بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيْمِ","bc":"bismillāhir-raḥmānir-raḥīm","at":"Dengan nama Allah Yang Maha Pengasih, Maha Penyayang."},{"id":"2","ar":"اَلْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَۙ","bc":"al-ḥamdu lillāhi rabbil-'ālamīn","at":"Segala puji bagi Allah, Tuhan seluruh alam,"},{"id":"3","ar":"الرَّحْمٰنِ الرَّحِيْمِۙ","bc":"ar-raḥmānir-raḥīm","at":"Yang Maha Pengasih, Maha Penyayang,"},{"id":"4","ar":"مٰلِكِ يَوْمِ الدِّيْنِۗ","bc":"māliki yaumid-dīn","at":"Pemilik hari pembalasan."},{"id":"5","ar":"اِيَّاكَ نَعْبُدُ وَاِيَّاكَ نَسْتَعِيْنُۗ","bc":"iyyāka na'budu wa iyyāka nasta'īn","at":"Hanya kepada Engkaulah kami menyembah dan hanya kepada Engkaulah kami mohon pertolongan."},{"id":"6","ar":"اِهْدِنَا الصِّرَاطَ الْمُسْتَقِيْمَ ۙ","bc":"ihdinaṣ-ṣirāṭal-mustaqīm","at":"Tunjukilah kami jalan yang lurus,"},{"id":"7","ar":"صِرَاطَ الَّذِيْنَ اَنْعَمْتَ عَلَيْهِمْ ەۙ غَيْرِ الْمَغْضُوْبِ عَلَيْهِمْ وَلَا الضَّاۤلِّيْنَ","bc":"ṣirāṭallażīna an'amta 'alaihim gairil-magḍụbi 'alaihim wa laḍ-ḍāllīn","at":"(yaitu) jalan orang-orang yang telah Engkau beri nikmat kepadanya; bukan (jalan) mereka yang dimurkai, dan bukan (pula jalan) mereka yang sesat."}],al_fil=[{"id":"1","ar":"اَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِاَصْحٰبِ الْفِيْلِۗ","bc":"a lam tara kaifa fa'ala rabbuka bi`aṣ-ḥābil-fīl","at":"Tidakkah engkau (Muhammad) perhatikan bagaimana Tuhanmu telah bertindak terhadap pasukan bergajah?"},{"id":"2","ar":"اَلَمْ يَجْعَلْ كَيْدَهُمْ فِيْ تَضْلِيْلٍۙ","bc":"a lam yaj'al kaidahum fī taḍlīl","at":"Bukankah Dia telah menjadikan tipu daya mereka itu sia-sia?"},{"id":"3","ar":"وَّاَرْسَلَ عَلَيْهِمْ طَيْرًا اَبَابِيْلَۙ","bc":"wa arsala 'alaihim ṭairan abābīl","at":"dan Dia mengirimkan kepada mereka burung yang berbondong-bondong,"},{"id":"4","ar":"تَرْمِيْهِمْ بِحِجَارَةٍ مِّنْ سِجِّيْلٍۙ","bc":"tarmīhim biḥijāratim min sijjīl","at":"yang melempari mereka dengan batu dari tanah liat yang dibakar,"},{"id":"5","ar":"فَجَعَلَهُمْ كَعَصْفٍ مَّأْكُوْلٍ ","bc":"fa ja'alahum ka'aṣfim ma`kụl","at":"sehingga mereka dijadikan-Nya seperti daun-daun yang dimakan (ulat)."}],al_furqan=[{"id":"1","ar":"تَبٰرَكَ الَّذِيْ نَزَّلَ الْفُرْقَانَ عَلٰى عَبْدِهٖ لِيَكُوْنَ لِلْعٰلَمِيْنَ نَذِيْرًا ۙ","bc":"tabārakallażī nazzalal-furqāna 'alā 'abdihī liyakụna lil-'ālamīna nażīrā","at":"Mahasuci Allah yang telah menurunkan Furqan (Al-Qur'an) kepada hamba-Nya (Muhammad), agar dia menjadi pemberi peringatan kepada seluruh alam (jin dan manusia)."},{"id":"2","ar":" ۨالَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَلَمْ يَتَّخِذْ وَلَدًا وَّلَمْ يَكُنْ لَّهٗ شَرِيْكٌ فِى الْمُلْكِ وَخَلَقَ كُلَّ شَيْءٍ فَقَدَّرَهٗ تَقْدِيْرًا","bc":"allażī lahụ mulkus-samāwāti wal-arḍi wa lam yattakhiż waladaw wa lam yakul lahụ syarīkun fil-mulki wa khalaqa kulla syai`in fa qaddarahụ taqdīrā","at":"Yang memiliki kerajaan langit dan bumi, tidak mempunyai anak, tidak ada sekutu bagi-Nya dalam kekuasaan(-Nya), dan Dia menciptakan segala sesuatu, lalu menetapkan ukuran-ukurannya dengan tepat. "},{"id":"3","ar":"وَاتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةً لَّا يَخْلُقُوْنَ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَ وَلَا يَمْلِكُوْنَ لِاَنْفُسِهِمْ ضَرًّا وَّلَا نَفْعًا وَّلَا يَمْلِكُوْنَ مَوْتًا وَّلَا حَيٰوةً وَّلَا نُشُوْرًا","bc":"wattakhażụ min dụnihī ālihatal lā yakhluqụna syai`aw wa hum yukhlaqụna wa lā yamlikụna li`anfusihim ḍarraw wa lā naf'aw wa lā yamlikụna mautaw wa lā ḥayātaw wa lā nusyụrā","at":"Namun mereka mengambil tuhan-tuhan selain Dia (untuk disembah), padahal mereka (tuhan-tuhan itu) tidak menciptakan apa pun, bahkan mereka sendiri diciptakan dan tidak kuasa untuk (menolak) bahaya terhadap dirinya dan tidak dapat (mendatangkan) manfaat serta tidak kuasa mematikan, menghidupkan dan tidak (pula) membangkitkan."},{"id":"4","ar":"وَقَالَ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَآ اِلَّآ اِفْكُ ِۨافْتَرٰىهُ وَاَعَانَهٗ عَلَيْهِ قَوْمٌ اٰخَرُوْنَۚ فَقَدْ جَاۤءُوْ ظُلْمًا وَّزُوْرًا ۚ","bc":"wa qālallażīna kafarū in hāżā illā ifkuniftarāhu wa a'ānahụ 'alaihi qaumun ākharụn, fa qad jā`ụ ẓulmaw wa zụrā","at":"Dan orang-orang kafir berkata, “(Al-Qur'an) ini tidak lain hanyalah kebohongan yang diada-adakan oleh dia (Muhammad), dibantu oleh orang-orang lain,” Sungguh, mereka telah berbuat zalim dan dusta yang besar."},{"id":"5","ar":"وَقَالُوْٓا اَسَاطِيْرُ الْاَوَّلِيْنَ اكْتَتَبَهَا فَهِيَ تُمْلٰى عَلَيْهِ بُكْرَةً وَّاَصِيْلًا","bc":"wa qālū asāṭīrul-awwalīnaktatabahā fa hiya tumlā 'alaihi bukrataw wa aṣīlā","at":"Dan mereka berkata, “(Itu hanya) dongeng-dongeng orang-orang terdahulu, yang diminta agar dituliskan, lalu dibacakanlah dongeng itu kepadanya setiap pagi dan petang.”"},{"id":"6","ar":"قُلْ اَنْزَلَهُ الَّذِيْ يَعْلَمُ السِّرَّ فِى السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّهٗ كَانَ غَفُوْرًا رَّحِيْمًا","bc":"qul anzalahullażī ya'lamus-sirra fis-samāwāti wal-arḍ, innahụ kāna gafụrar-raḥīmā","at":"Katakanlah (Muhammad), “(Al-Qur'an) itu diturunkan oleh (Allah) yang mengetahui rahasia di langit dan di bumi. Sungguh, Dia Maha Pengampun, Maha Penyayang.” "},{"id":"7","ar":"وَقَالُوْا مَالِ هٰذَا الرَّسُوْلِ يَأْكُلُ الطَّعَامَ وَيَمْشِيْ فِى الْاَسْوَاقِۗ لَوْلَآ اُنْزِلَ اِلَيْهِ مَلَكٌ فَيَكُوْنَ مَعَهٗ نَذِيْرًا ۙ","bc":"wa qālụ mā lihāżar-rasụli ya`kuluṭ-ṭa'āma wa yamsyī fil-aswāq, lau lā unzila ilaihi malakun fa yakụna ma'ahụ nażīrā","at":"Dan mereka berkata, “Mengapa Rasul (Muhammad) ini memakan makanan dan berjalan di pasar-pasar? Mengapa malaikat tidak diturunkan kepadanya (agar malaikat) itu memberikan peringatan bersama dia,"},{"id":"8","ar":"اَوْ يُلْقٰىٓ اِلَيْهِ كَنْزٌ اَوْ تَكُوْنُ لَهٗ جَنَّةٌ يَّأْكُلُ مِنْهَاۗ وَقَالَ الظّٰلِمُوْنَ اِنْ تَتَّبِعُوْنَ اِلَّا رَجُلًا مَّسْحُوْرًا","bc":"au yulqā ilaihi kanzun au takụnu lahụ jannatuy ya`kulu min-hā, wa qālaẓ-ẓālimụna in tattabi'ụna illā rajulam mas-ḥụrā","at":"atau (mengapa tidak) diturunkan kepadanya harta kekayaan atau (mengapa tidak ada) kebun baginya, sehingga dia dapat makan dari (hasil)nya?” Dan orang-orang zalim itu berkata, “Kamu hanyalah mengikuti seorang laki-laki yang kena sihir.”"},{"id":"9","ar":"اُنْظُرْ كَيْفَ ضَرَبُوْا لَكَ الْاَمْثَالَ فَضَلُّوْا فَلَا يَسْتَطِيْعُوْنَ سَبِيْلًا","bc":"unẓur kaifa ḍarabụ lakal-amṡāla fa ḍallụ fa lā yastaṭī'ụna sabīlā","at":"Perhatikanlah, bagaimana mereka membuat perumpamaan-perumpamaan tentang engkau, maka sesatlah mereka, mereka tidak sanggup (mendapatkan) jalan (untuk menentang kerasulanmu). "},{"id":"10","ar":"تَبٰرَكَ الَّذِيْٓ اِنْ شَاۤءَ جَعَلَ لَكَ خَيْرًا مِّنْ ذٰلِكَ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۙ وَيَجْعَلْ لَّكَ قُصُوْرًا","bc":"tabārakallażī in syā`a ja'ala laka khairam min żālika jannātin tajrī min taḥtihal-an-hāru wa yaj'al laka quṣụrā","at":"Mahasuci (Allah) yang jika Dia menghendaki, niscaya Dia jadikan bagimu yang lebih baik daripada itu, (yaitu) surga-surga yang mengalir di bawahnya sungai-sungai, dan Dia jadikan (pula) istana-istana untukmu."},{"id":"11","ar":"بَلْ كَذَّبُوْا بِالسَّاعَةِۙ وَاَعْتَدْنَا لِمَنْ كَذَّبَ بِالسَّاعَةِ سَعِيْرًا","bc":"bal każżabụ bis-sā'ati wa a'tadnā limang każżaba bis-sā'ati sa'īrā","at":"Bahkan mereka mendustakan hari Kiamat. Dan Kami menyediakan neraka yang menyala-nyala bagi siapa yang mendustakan hari Kiamat."},{"id":"12","ar":"اِذَا رَاَتْهُمْ مِّنْ مَّكَانٍۢ بَعِيْدٍ سَمِعُوْا لَهَا تَغَيُّظًا وَّزَفِيْرًا","bc":"iżā ra`at-hum mim makānim ba'īdin sami'ụ lahā tagayyuẓaw wa zafīrā","at":"Apabila ia (neraka) melihat mereka dari tempat yang jauh, mereka mendengar suaranya yang gemuruh karena marahnya."},{"id":"13","ar":"وَاِذَآ اُلْقُوْا مِنْهَا مَكَانًا ضَيِّقًا مُّقَرَّنِيْنَ دَعَوْا هُنَالِكَ ثُبُوْرًا ۗ","bc":"wa iżā ulqụ min-hā makānan ḍayyiqam muqarranīna da'au hunālika ṡubụrā","at":"Dan apabila mereka dilemparkan ke tempat yang sempit di neraka dengan dibelenggu, mereka di sana berteriak mengharapkan kebinasaan."},{"id":"14","ar":"لَا تَدْعُوا الْيَوْمَ ثُبُوْرًا وَّاحِدًا وَّادْعُوْا ثُبُوْرًا كَثِيْرًا ","bc":"lā tad'ul-yauma ṡubụraw wāḥidaw wad'ụ ṡubụrang kaṡīrā","at":"(Akan dikatakan kepada mereka), “Janganlah kamu mengharapkan pada hari ini satu kebinasaan, melainkan harapkanlah kebinasaan yang berulang-ulang.” "},{"id":"15","ar":"قُلْ اَذٰلِكَ خَيْرٌ اَمْ جَنَّةُ الْخُلْدِ الَّتِيْ وُعِدَ الْمُتَّقُوْنَۗ كَانَتْ لَهُمْ جَزَاۤءً وَّمَصِيْرًا ","bc":"qul a żālika khairun am jannatul-khuldillatī wu'idal-muttaqụn, kānat lahum jazā`aw wa maṣīrā","at":"Katakanlah (Muhammad), “Apakah (azab) seperti itu yang baik, atau surga yang kekal yang dijanjikan kepada orang-orang yang bertakwa sebagai balasan, dan tempat kembali bagi mereka?”"},{"id":"16","ar":"لَهُمْ فِيْهَا مَا يَشَاۤءُوْنَ خٰلِدِيْنَۗ كَانَ عَلٰى رَبِّكَ وَعْدًا مَّسْـُٔوْلًا","bc":"lahum fīhā mā yasyā`ụna khālidīn, kāna 'alā rabbika wa'dam mas`ụlā","at":"Bagi mereka segala yang mereka kehendaki ada di dalamnya (surga), mereka kekal (di dalamnya). Itulah janji Tuhanmu yang pantas dimohonkan (kepada-Nya)."},{"id":"17","ar":"وَيَوْمَ يَحْشُرُهُمْ وَمَا يَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ فَيَقُوْلُ ءَاَنْتُمْ اَضْلَلْتُمْ عِبَادِيْ هٰٓؤُلَاۤءِ اَمْ هُمْ ضَلُّوا السَّبِيْلَ ۗ","bc":"wa yauma yaḥsyuruhum wa mā ya'budụna min dụnillāhi fa yaqụlu a antum aḍlaltum 'ibādī hā`ulā`i am hum ḍallus-sabīl","at":"Dan (ingatlah) pada hari (ketika) Allah mengumpulkan mereka bersama apa yang mereka sembah selain Allah, lalu Dia berfirman (kepada yang disembah), “Apakah kamu yang menyesatkan hamba-hamba-Ku itu, atau mereka sendirikah yang sesat dari jalan (yang benar)?” "},{"id":"18","ar":"قَالُوْا سُبْحٰنَكَ مَا كَانَ يَنْۢبَغِيْ لَنَآ اَنْ نَّتَّخِذَ مِنْ دُوْنِكَ مِنْ اَوْلِيَاۤءَ وَلٰكِنْ مَّتَّعْتَهُمْ وَاٰبَاۤءَهُمْ حَتّٰى نَسُوا الذِّكْرَۚ وَكَانُوْا قَوْمًاۢ بُوْرًا","bc":"qālụ sub-ḥānaka mā kāna yambagī lanā an nattakhiża min dụnika min auliyā`a wa lākim matta'tahum wa ābā`ahum ḥattā nasuż-żikr, wa kānụ qaumam bụrā","at":"Mereka (yang disembah itu) menjawab, “Mahasuci Engkau, tidaklah pantas bagi kami mengambil pelindung selain Engkau, tetapi Engkau telah memberi mereka dan nenek moyang mereka kenikmatan hidup, sehingga mereka melupakan peringatan; dan mereka kaum yang binasa.”"},{"id":"19","ar":"فَقَدْ كَذَّبُوْكُمْ بِمَا تَقُوْلُوْنَۙ فَمَا تَسْتَطِيْعُوْنَ صَرْفًا وَّلَا نَصْرًاۚ وَمَنْ يَّظْلِمْ مِّنْكُمْ نُذِقْهُ عَذَابًا كَبِيْرًا","bc":"fa qad każżabụkum bimā taqụlụna fa mā tastaṭī'ụna ṣarfaw wa lā naṣrā, wa may yaẓlim mingkum nużiq-hu 'ażābang kabīrā","at":"Maka sungguh, mereka (yang disembah itu) telah mengingkari apa yang kamu katakan, maka kamu tidak akan dapat menolak (azab) dan tidak dapat (pula) menolong (dirimu), dan barangsiapa di antara kamu berbuat zalim, niscaya Kami timpakan kepadanya rasa azab yang besar."},{"id":"20","ar":"وَمَآ اَرْسَلْنَا قَبْلَكَ مِنَ الْمُرْسَلِيْنَ اِلَّآ اِنَّهُمْ لَيَأْكُلُوْنَ الطَّعَامَ وَيَمْشُوْنَ فِى الْاَسْوَاقِۗ وَجَعَلْنَا بَعْضَكُمْ لِبَعْضٍ فِتْنَةً ۗ اَتَصْبِرُوْنَۚ وَكَانَ رَبُّكَ بَصِيْرًا ۔","bc":"wa mā arsalnā qablaka minal-mursalīna illā innahum laya`kulụnaṭ-ṭa'āma wa yamsyụna fil-aswāq, wa ja'alnā ba'ḍakum liba'ḍin fitnah, a taṣbirụn, wa kāna rabbuka baṣīrā","at":"Dan Kami tidak mengutus rasul-rasul sebelummu (Muhammad), melainkan mereka pasti memakan makanan dan berjalan di pasar-pasar. Dan Kami jadikan sebagian kamu sebagai cobaan bagi sebagian yang lain. Maukah kamu bersabar? Dan Tuhanmu Maha Melihat."},{"id":"21","ar":"۞ وَقَالَ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا لَوْلَآ اُنْزِلَ عَلَيْنَا الْمَلٰۤىِٕكَةُ اَوْ نَرٰى رَبَّنَا ۗ لَقَدِ اسْتَكْبَرُوْا فِيْٓ اَنْفُسِهِمْ وَعَتَوْ عُتُوًّا كَبِيْرًا","bc":"wa qālallażīna lā yarjụna liqā`anā lau lā unzila 'alainal-malā`ikatu au narā rabbanā, laqadistakbarụ fī anfusihim wa 'atau 'utuwwang kabīrā","at":"Dan orang-orang yang tidak mengharapkan pertemuan dengan Kami (di akhirat) berkata, “Mengapa bukan para malaikat yang diturunkan kepada kita atau (mengapa) kita (tidak) melihat Tuhan kita?” Sungguh, mereka telah menyombongkan diri mereka dan benar-benar telah melampaui batas (dalam melakukan kezaliman)."},{"id":"22","ar":"يَوْمَ يَرَوْنَ الْمَلٰۤىِٕكَةَ لَا بُشْرٰى يَوْمَىِٕذٍ لِّلْمُجْرِمِيْنَ وَيَقُوْلُوْنَ حِجْرًا مَّحْجُوْرًا ","bc":"yauma yaraunal-malā`ikata lā busyrā yauma`iżil lil-mujrimīna wa yaqụlụna ḥijram maḥjụrā","at":"(Ingatlah) pada hari (ketika) mereka melihat para malaikat, pada hari itu tidak ada kabar gembira bagi orang-orang yang berdosa dan mereka berkata, “Hijran mahjura.”"},{"id":"23","ar":"وَقَدِمْنَآ اِلٰى مَا عَمِلُوْا مِنْ عَمَلٍ فَجَعَلْنٰهُ هَبَاۤءً مَّنْثُوْرًا ","bc":"wa qadimnā ilā mā 'amilụ min 'amalin fa ja'alnāhu habā`am manṡụrā","at":"Dan Kami akan perlihatkan segala amal yang mereka kerjakan, lalu Kami akan jadikan amal itu (bagaikan) debu yang beterbangan. "},{"id":"24","ar":"اَصْحٰبُ الْجَنَّةِ يَوْمَىِٕذٍ خَيْرٌ مُّسْتَقَرًّا وَّاَحْسَنُ مَقِيْلًا ","bc":"aṣ-ḥābul-jannati yauma`iżin khairum mustaqarraw wa aḥsanu maqīlā","at":"Penghuni-penghuni surga pada hari itu paling baik tempat tinggalnya dan paling indah tempat istirahatnya. "},{"id":"25","ar":"وَيَوْمَ تَشَقَّقُ السَّمَاۤءُ بِالْغَمَامِ وَنُزِّلَ الْمَلٰۤىِٕكَةُ تَنْزِيْلًا ","bc":"wa yauma tasyaqqaqus-samā`u bil-gamāmi wa nuzzilal-malā`ikatu tanzīlā","at":"Dan (ingatlah) pada hari (ketika) langit pecah mengeluarkan kabut putih dan para malaikat diturunkan (secara) bergelombang."},{"id":"26","ar":"اَلْمُلْكُ يَوْمَىِٕذِ ِۨالْحَقُّ لِلرَّحْمٰنِۗ وَكَانَ يَوْمًا عَلَى الْكٰفِرِيْنَ عَسِيْرًا ","bc":"al-mulku yauma`iżinil-ḥaqqu lir-raḥmān, wa kāna yauman 'alal-kāfirīna 'asīrā","at":"Kerajaan yang hak pada hari itu adalah milik Tuhan Yang Maha Pengasih. Dan itulah hari yang sulit bagi orang-orang kafir. "},{"id":"27","ar":"وَيَوْمَ يَعَضُّ الظَّالِمُ عَلٰى يَدَيْهِ يَقُوْلُ يٰلَيْتَنِى اتَّخَذْتُ مَعَ الرَّسُوْلِ سَبِيْلًا ","bc":"wa yauma ya'aḍḍuẓ-ẓālimu 'alā yadaihi yaqụlu yā laitanittakhażtu ma'ar-rasụli sabīlā","at":"Dan (ingatlah) pada hari (ketika) orang-orang zalim menggigit dua jarinya, (menyesali perbuatannya) seraya berkata, “Wahai! Sekiranya (dulu) aku mengambil jalan bersama Rasul. "},{"id":"28","ar":"يٰوَيْلَتٰى لَيْتَنِيْ لَمْ اَتَّخِذْ فُلَانًا خَلِيْلًا ","bc":"yā wailatā laitanī lam attakhiż fulānan khalīlā","at":"Wahai, celaka aku! Sekiranya (dulu) aku tidak menjadikan si fulan itu teman akrab(ku), "},{"id":"29","ar":"لَقَدْ اَضَلَّنِيْ عَنِ الذِّكْرِ بَعْدَ اِذْ جَاۤءَنِيْۗ وَكَانَ الشَّيْطٰنُ لِلْاِنْسَانِ خَذُوْلًا ","bc":"laqad aḍallanī 'aniż-żikri ba'da iż jā`anī, wa kānasy-syaiṭānu lil-insāni khażụlā","at":"sungguh, dia telah menyesatkan aku dari peringatan (Al-Qur'an) ketika (Al-Qur'an) itu telah datang kepadaku. Dan setan memang pengkhianat manusia.” "},{"id":"30","ar":"وَقَالَ الرَّسُوْلُ يٰرَبِّ اِنَّ قَوْمِى اتَّخَذُوْا هٰذَا الْقُرْاٰنَ مَهْجُوْرًا ","bc":"wa qālar-rasụlu yā rabbi inna qaumittakhażụ hāżal-qur`āna mahjụrā","at":"Dan Rasul (Muhammad) berkata, “Ya Tuhanku, sesungguhnya kaumku telah menjadikan Al-Qur'an ini diabaikan.”"},{"id":"31","ar":"وَكَذٰلِكَ جَعَلْنَا لِكُلِّ نَبِيٍّ عَدُوًّا مِّنَ الْمُجْرِمِيْنَۗ وَكَفٰى بِرَبِّكَ هَادِيًا وَّنَصِيْرًا ","bc":"wa każālika ja'alnā likulli nabiyyin 'aduwwam minal-mujrimīn, wa kafā birabbika hādiyaw wa naṣīrā","at":"Begitulah, bagi setiap nabi, telah Kami adakan musuh dari orang-orang yang berdosa. Tetapi cukuplah Tuhanmu menjadi pemberi petunjuk dan penolong. "},{"id":"32","ar":"وَقَالَ الَّذِيْنَ كَفَرُوْا لَوْلَا نُزِّلَ عَلَيْهِ الْقُرْاٰنُ جُمْلَةً وَّاحِدَةً ۛ كَذٰلِكَ ۛ لِنُثَبِّتَ بِهٖ فُؤَادَكَ وَرَتَّلْنٰهُ تَرْتِيْلًا ","bc":"wa qālallażīna kafarụ lau lā nuzzila 'alaihil-qur`ānu jumlataw wāḥidah, każālika linuṡabbita bihī fu`ādaka wa rattalnāhu tartīlā","at":"Dan orang-orang kafir berkata, “Mengapa Al-Qur'an itu tidak diturunkan kepadanya sekaligus?” Demikianlah, agar Kami memperteguh hatimu (Muhammad) dengannya dan Kami membacakannya secara tartil (berangsur-angsur, perlahan dan benar). "},{"id":"33","ar":"وَلَا يَأْتُوْنَكَ بِمَثَلٍ اِلَّا جِئْنٰكَ بِالْحَقِّ وَاَحْسَنَ تَفْسِيْرًا ۗ","bc":"wa lā ya`tụnaka bimaṡalin illā ji`nāka bil-ḥaqqi wa aḥsana tafsīrā","at":"Dan mereka (orang-orang kafir itu) tidak datang kepadamu (membawa) sesuatu yang aneh, melainkan Kami datangkan kepadamu yang benar dan penjelasan yang paling baik. "},{"id":"34","ar":"اَلَّذِيْنَ يُحْشَرُوْنَ عَلٰى وُجُوْهِهِمْ اِلٰى جَهَنَّمَۙ اُولٰۤىِٕكَ شَرٌّ مَّكَانًا وَّاَضَلُّ سَبِيْلًا ","bc":"allażīna yuḥsyarụna 'alā wujụhihim ilā jahannama ulā`ika syarrum makānaw wa aḍallu sabīlā","at":"Orang-orang yang dikumpulkan ke neraka Jahanam dengan diseret wajahnya, mereka itulah yang paling buruk tempatnya dan paling sesat jalannya. "},{"id":"35","ar":"وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ وَجَعَلْنَا مَعَهٗٓ اَخَاهُ هٰرُوْنَ وَزِيْرًا ۚ ","bc":"wa laqad ātainā mụsal-kitāba wa ja'alnā ma'ahū akhāhu hārụna wazīrā","at":"Dan sungguh, Kami telah memberikan Kitab (Taurat) kepada Musa dan Kami telah menjadikan Harun saudaranya, menyertai dia sebagai wazir (pembantu). "},{"id":"36","ar":"فَقُلْنَا اذْهَبَآ اِلَى الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۗ فَدَمَّرْنٰهُمْ تَدْمِيْرًا ۗ","bc":"fa qulnaż-habā ilal-qaumillażīna każżabụ bi`āyātinā, fa dammarnāhum tadmīrā","at":"Kemudian Kami berfirman (kepada keduanya), “Pergilah kamu berdua kepada kaum yang mendustakan ayat-ayat Kami.” Lalu Kami hancurkan mereka dengan sehancur-hancurnya. "},{"id":"37","ar":"وَقَوْمَ نُوْحٍ لَّمَّا كَذَّبُوا الرُّسُلَ اَغْرَقْنٰهُمْ وَجَعَلْنٰهُمْ لِلنَّاسِ اٰيَةًۗ وَاَعْتَدْنَا لِلظّٰلِمِيْنَ عَذَابًا اَلِيْمًا ۚ ","bc":"wa qauma nụḥil lammā każżabur-rusula agraqnāhum wa ja'alnāhum lin-nāsi āyah, wa a'tadnā liẓ-ẓālimīna 'ażāban alīmā","at":"Dan (telah Kami binasakan) kaum Nuh ketika mereka mendustakan para rasul. Kami tenggelamkam mereka dan Kami jadikan (cerita) mereka itu pelajaran bagi manusia. Dan Kami telah sediakan bagi orang-orang zalim azab yang pedih; "},{"id":"38","ar":"وَعَادًا وَّثَمُوْدَا۟ وَاَصْحٰبَ الرَّسِّ وَقُرُوْنًاۢ بَيْنَ ذٰلِكَ كَثِيْرًا ","bc":"wa 'ādaw wa ṡamụda wa aṣ-ḥābar-rassi wa qurụnam baina żālika kaṡīrā","at":"dan (telah Kami binasakan) kaum ‘Ad dan samud dan penduduk Rass serta banyak (lagi) generasi di antara (kaum-kaum) itu. "},{"id":"39","ar":"وَكُلًّا ضَرَبْنَا لَهُ الْاَمْثَالَۖ وَكُلًّا تَبَّرْنَا تَتْبِيْرًا ","bc":"wa kullan ḍarabnā lahul-amṡāla wa kullan tabbarnā tatbīrā","at":"Dan masing-masing telah Kami jadikan perumpamaan dan masing-masing telah Kami hancurkan sehancur-hancurnya. "},{"id":"40","ar":"وَلَقَدْ اَتَوْا عَلَى الْقَرْيَةِ الَّتِيْٓ اُمْطِرَتْ مَطَرَ السَّوْءِۗ اَفَلَمْ يَكُوْنُوْا يَرَوْنَهَاۚ بَلْ كَانُوْا لَا يَرْجُوْنَ نُشُوْرًا ","bc":"wa laqad atau 'alal-qaryatillatī umṭirat maṭaras-saụ`, a fa lam yakụnụ yaraunahā, bal kānụ lā yarjụna nusyụrā","at":"Dan sungguh, mereka (kaum musyrik Mekah) telah melalui negeri (Sodom) yang (dulu) dijatuhi hujan yang buruk (hujan batu). Tidakkah mereka menyaksikannya? Bahkan mereka itu sebenarnya tidak mengharapkan hari kebangkitan. "},{"id":"41","ar":"وَاِذَا رَاَوْكَ اِنْ يَّتَّخِذُوْنَكَ اِلَّا هُزُوًاۗ اَهٰذَا الَّذِيْ بَعَثَ اللّٰهُ رَسُوْلًا ","bc":"wa iżā ra`auka iy yattakhiżụnaka illā huzuwā, a hāżallażī ba'aṡallāhu rasụlā","at":"Dan apabila mereka melihat engkau (Muhammad), mereka hanyalah menjadikan engkau sebagai ejekan (dengan mengatakan), “Inikah orangnya yang diutus Allah sebagai Rasul? "},{"id":"42","ar":"اِنْ كَادَ لَيُضِلُّنَا عَنْ اٰلِهَتِنَا لَوْلَآ اَنْ صَبَرْنَا عَلَيْهَاۗ وَسَوْفَ يَعْلَمُوْنَ حِيْنَ يَرَوْنَ الْعَذَابَ مَنْ اَضَلُّ سَبِيْلًا ","bc":"ing kāda layuḍillunā 'an ālihatinā lau lā an ṣabarnā 'alaihā, wa saufa ya'lamụna ḥīna yaraunal-'ażāba man aḍallu sabīlā","at":"Sungguh, hampir saja dia menyesatkan kita dari sesembahan kita, seandainya kita tidak tetap bertahan (menyembah)nya.” Dan kelak mereka akan mengetahui pada saat mereka melihat azab, siapa yang paling sesat jalannya."},{"id":"43","ar":"اَرَءَيْتَ مَنِ اتَّخَذَ اِلٰهَهٗ هَوٰىهُۗ اَفَاَنْتَ تَكُوْنُ عَلَيْهِ وَكِيْلًا ۙ","bc":"a ra`aita manittakhaża ilāhahụ hawāh, a fa anta takụnu 'alaihi wakīlā","at":"Sudahkah engkau (Muhammad) melihat orang yang menjadikan keinginannya sebagai tuhannya. Apakah engkau akan menjadi pelindungnya? "},{"id":"44","ar":"اَمْ تَحْسَبُ اَنَّ اَكْثَرَهُمْ يَسْمَعُوْنَ اَوْ يَعْقِلُوْنَۗ اِنْ هُمْ اِلَّا كَالْاَنْعَامِ بَلْ هُمْ اَضَلُّ سَبِيْلًا","bc":"am taḥsabu anna akṡarahum yasma'ụna au ya'qilụn, in hum illā kal-an'āmi bal hum aḍallu sabīlā","at":"Atau apakah engkau mengira bahwa kebanyakan mereka itu mendengar atau memahami? Mereka itu hanyalah seperti hewan ternak, bahkan lebih sesat jalannya."},{"id":"45","ar":"اَلَمْ تَرَ اِلٰى رَبِّكَ كَيْفَ مَدَّ الظِّلَّۚ وَلَوْ شَاۤءَ لَجَعَلَهٗ سَاكِنًاۚ ثُمَّ جَعَلْنَا الشَّمْسَ عَلَيْهِ دَلِيْلًا ۙ","bc":"a lam tara ilā rabbika kaifa maddaẓ-ẓill, walau syā`a laja'alahụ sākinā, ṡumma ja'alnasy-syamsa 'alaihi dalīlā","at":"Tidakkah engkau memperhatikan (penciptaan) Tuhanmu, bagaimana Dia memanjangkan (dan memendekkan) bayang-bayang; dan sekiranya Dia menghendaki, niscaya Dia jadikannya (bayang-bayang itu) tetap, kemudian Kami jadikan matahari sebagai petunjuk, "},{"id":"46","ar":"ثُمَّ قَبَضْنٰهُ اِلَيْنَا قَبْضًا يَّسِيْرًا ","bc":"ṡumma qabaḍnāhu ilainā qabḍay yasīrā","at":"kemudian Kami menariknya (bayang-bayang itu) kepada Kami sedikit demi sedikit. "},{"id":"47","ar":"وَهُوَ الَّذِيْ جَعَلَ لَكُمُ الَّيْلَ لِبَاسًا وَّالنَّوْمَ سُبَاتًا وَّجَعَلَ النَّهَارَ نُشُوْرًا ","bc":"wa huwallażī ja'ala lakumul-laila libāsaw wan-nauma subātaw wa ja'alan-nahāra nusyụrā","at":"Dan Dialah yang menjadikan malam untukmu (sebagai) pakaian, dan tidur untuk istirahat, dan Dia menjadikan siang untuk bangkit berusaha. "},{"id":"48","ar":"وَهُوَ الَّذِيْٓ اَرْسَلَ الرِّيٰحَ بُشْرًاۢ بَيْنَ يَدَيْ رَحْمَتِهٖۚ وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً طَهُوْرًا ۙ","bc":"wa huwallażī arsalar-riyāḥa busyram baina yadai raḥmatih, wa anzalnā minas-samā`i mā`an ṭahụrā","at":"Dan Dialah yang meniupkan angin (sebagai) pembawa kabar gembira sebelum kedatangan rahmat-Nya (hujan); dan Kami turunkan dari langit air yang sangat bersih, "},{"id":"49","ar":"لِّنُحْيِ َۧ بِهٖ بَلْدَةً مَّيْتًا وَّنُسْقِيَهٗ مِمَّا خَلَقْنَآ اَنْعَامًا وَّاَنَاسِيَّ كَثِيْرًا ","bc":"linuḥyiya bihī baldatam maitaw wa nusqiyahụ mimmā khalaqnā an'āmaw wa anāsiyya kaṡīrā","at":"agar (dengan air itu) Kami menghidupkan negeri yang mati (tandus), dan Kami memberi minum kepada sebagian apa yang telah Kami ciptakan, (berupa) hewan-hewan ternak dan manusia yang banyak. "},{"id":"50","ar":"وَلَقَدْ صَرَّفْنٰهُ بَيْنَهُمْ لِيَذَّكَّرُوْاۖ فَاَبٰىٓ اَكْثَرُ النَّاسِ اِلَّا كُفُوْرًا ","bc":"wa laqad ṣarrafnāhu bainahum liyażżakkarụ fa abā akṡarun-nāsi illā kufụrā","at":"Dan sungguh, Kami telah mempergilirkan (hujan) itu di antara mereka agar mereka mengambil pelajaran; tetapi kebanyakan manusia tidak mau (bersyukur), bahkan mereka mengingkari (nikmat)."},{"id":"51","ar":"وَلَوْ شِئْنَا لَبَعَثْنَا فِيْ كُلِّ قَرْيَةٍ نَّذِيْرًا ۖ","bc":"walau syi`nā laba'aṡnā fī kulli qaryatin nażīrā","at":"Dan sekiranya Kami menghendaki, niscaya Kami utus seorang pemberi peringatan pada setiap negeri. "},{"id":"52","ar":"فَلَا تُطِعِ الْكٰفِرِيْنَ وَجَاهِدْهُمْ بِهٖ جِهَادًا كَبِيْرًا ","bc":"fa lā tuṭi'il kāfirīna wa jāhid-hum bihī jihādang kabīrā","at":"Maka janganlah engkau taati orang-orang kafir, dan berjuanglah terhadap mereka dengannya (Al-Qur'an) dengan (semangat) perjuangan yang besar. "},{"id":"53","ar":"۞ وَهُوَ الَّذِيْ مَرَجَ الْبَحْرَيْنِ هٰذَا عَذْبٌ فُرَاتٌ وَّهٰذَا مِلْحٌ اُجَاجٌۚ وَجَعَلَ بَيْنَهُمَا بَرْزَخًا وَّحِجْرًا مَّحْجُوْرًا ","bc":"wa huwallażī marajal-baḥraini hāżā 'ażbun furātuw wa hāżā mil-ḥun ujāj, wa ja'ala bainahumā barzakhaw wa ḥijram maḥjụrā","at":"Dan Dialah yang membiarkan dua laut mengalir (berdampingan); yang ini tawar dan segar dan yang lain sangat asin lagi pahit; dan Dia jadikan antara keduanya dinding dan batas yang tidak tembus. "},{"id":"54","ar":"وَهُوَ الَّذِيْ خَلَقَ مِنَ الْمَاۤءِ بَشَرًا فَجَعَلَهٗ نَسَبًا وَّصِهْرًاۗ وَكَانَ رَبُّكَ قَدِيْرًا ","bc":"wa huwallażī khalaqa minal-mā`i basyaran fa ja'alahụ nasabaw wa ṣihrā, wa kāna rabbuka qadīrā","at":"Dan Dia (pula) yang menciptakan manusia dari air, lalu Dia jadikan manusia itu (mempunyai) keturunan dan musaharah dan Tuhanmu adalah Mahakuasa. "},{"id":"55","ar":"وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُهُمْ وَلَا يَضُرُّهُمْۗ وَكَانَ الْكَافِرُ عَلٰى رَبِّهٖ ظَهِيْرًا ","bc":"wa ya'budụna min dụnillāhi mā lā yanfa'uhum wa lā yaḍurruhum, wa kānal-kāfiru 'alā rabbihī ẓahīrā","at":"Dan mereka menyembah selain Allah apa yang tidak memberi manfaat kepada mereka dan tidak (pula) mendatangkan bencana kepada mereka. Orang-orang kafir adalah penolong (setan untuk berbuat durhaka) terhadap Tuhannya. "},{"id":"56","ar":"وَمَآ اَرْسَلْنٰكَ اِلَّا مُبَشِّرًا وَّنَذِيْرًا ","bc":"wa mā arsalnāka illā mubasysyiraw wa nażīrā","at":"Dan tidaklah Kami mengutus engkau (Muhammad) melainkan hanya sebagai pembawa kabar gembira dan pemberi peringatan. "},{"id":"57","ar":"قُلْ مَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِلَّا مَنْ شَاۤءَ اَنْ يَّتَّخِذَ اِلٰى رَبِّهٖ سَبِيْلًا ","bc":"qul mā as`alukum 'alaihi min ajrin illā man syā`a ay yattakhiża ilā rabbihī sabīlā","at":"Katakanlah, “Aku tidak meminta imbalan apa pun dari kamu dalam menyampaikan (risalah) itu, melainkan (mengharapkan agar) orang-orang mau mengambil jalan kepada Tuhannya.” "},{"id":"58","ar":"وَتَوَكَّلْ عَلَى الْحَيِّ الَّذِيْ لَا يَمُوْتُ وَسَبِّحْ بِحَمْدِهٖۗ وَكَفٰى بِهٖ بِذُنُوْبِ عِبَادِهٖ خَبِيْرًا ۚ","bc":"wa tawakkal 'alal-ḥayyillażī lā yamụtu wa sabbiḥ biḥamdih, wa kafā bihī biżunụbi 'ibādihī khabīrā","at":"Dan bertawakallah kepada Allah Yang Hidup, Yang tidak mati, dan bertasbihlah dengan memuji-Nya. Dan cukuplah Dia Maha Mengetahui dosa hamba-hamba-Nya, "},{"id":"59","ar":"اَلَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۚ اَلرَّحْمٰنُ فَسْـَٔلْ بِهٖ خَبِيْرًا ","bc":"allażī khalaqas-samāwāti wal-arḍa wa mā bainahumā fī sittati ayyāmin ṡummastawā 'alal-'arsy, ar-raḥmānu fas`al bihī khabīrā","at":"yang menciptakan langit dan bumi dan apa yang ada di antara keduanya dalam enam masa, kemudian Dia bersemayam di atas ‘Arsy, (Dialah) Yang Maha Pengasih, maka tanyakanlah (tentang Allah) kepada orang yang lebih mengetahui (Muhammad). "},{"id":"60","ar":"وَاِذَا قِيْلَ لَهُمُ اسْجُدُوْا لِلرَّحْمٰنِ قَالُوْا وَمَا الرَّحْمٰنُ اَنَسْجُدُ لِمَا تَأْمُرُنَا وَزَادَهُمْ نُفُوْرًا ۩","bc":"wa iżā qīla lahumusjudụ lir-raḥmāni qālụ wa mar-raḥmānu a nasjudu limā ta`murunā wa zādahum nufụrā","at":"Dan apabila dikatakan kepada mereka, “Sujudlah kepada Yang Maha Pengasih”, mereka menjawab, “Siapakah yang Maha Pengasih itu? Apakah kami harus sujud kepada Allah yang engkau (Muhammad) perintahkan kepada kami (bersujud kepada-Nya)?” Dan mereka makin jauh lari (dari kebenaran). "},{"id":"61","ar":"تَبٰرَكَ الَّذِيْ جَعَلَ فِى السَّمَاۤءِ بُرُوْجًا وَّجَعَلَ فِيْهَا سِرَاجًا وَّقَمَرًا مُّنِيْرًا ","bc":"tabārakallażī ja'ala fis-samā`i burụjaw wa ja'ala fīhā sirājaw wa qamaram munīrā","at":"Mahasuci Allah yang menjadikan di langit gugusan bintang-bintang dan Dia juga menjadikan padanya matahari dan bulan yang bersinar. "},{"id":"62","ar":"وَهُوَ الَّذِيْ جَعَلَ الَّيْلَ وَالنَّهَارَ خِلْفَةً لِّمَنْ اَرَادَ اَنْ يَّذَّكَّرَ اَوْ اَرَادَ شُكُوْرًا ","bc":"wa huwallażī ja'alal-laila wan-nahāra khilfatal liman arāda ay yażżakkara au arāda syukụrā","at":"Dan Dia (pula) yang menjadikan malam dan siang silih berganti bagi orang yang ingin mengambil pelajaran atau yang ingin bersyukur."},{"id":"63","ar":"وَعِبَادُ الرَّحْمٰنِ الَّذِيْنَ يَمْشُوْنَ عَلَى الْاَرْضِ هَوْنًا وَّاِذَا خَاطَبَهُمُ الْجٰهِلُوْنَ قَالُوْا سَلٰمًا ","bc":"wa 'ibādur-raḥmānillażīna yamsyụna 'alal-arḍi haunaw wa iżā khāṭabahumul-jāhilụna qālụ salāmā","at":"Adapun hamba-hamba Tuhan Yang Maha Pengasih itu adalah orang-orang yang berjalan di bumi dengan rendah hati dan apabila orang-orang bodoh menyapa mereka (dengan kata-kata yang menghina), mereka mengucapkan “salam,” "},{"id":"64","ar":"وَالَّذِيْنَ يَبِيْتُوْنَ لِرَبِّهِمْ سُجَّدًا وَّقِيَامًا ","bc":"wallażīna yabītụna lirabbihim sujjadaw wa qiyāmā","at":"dan orang-orang yang menghabiskan waktu malam untuk beribadah kepada Tuhan mereka dengan bersujud dan berdiri. "},{"id":"65","ar":"وَالَّذِيْنَ يَقُوْلُوْنَ رَبَّنَا اصْرِفْ عَنَّا عَذَابَ جَهَنَّمَۖ اِنَّ عَذَابَهَا كَانَ غَرَامًا ۖ","bc":"wallażīna yaqụlụna rabbanaṣrif 'annā 'ażāba jahannama inna 'ażābahā kāna garāmā","at":"Dan orang-orang yang berkata, “Ya Tuhan kami, jauhkanlah azab Jahanam dari kami, karena sesungguhnya azabnya itu membuat kebinasaan yang kekal,”"},{"id":"66","ar":"اِنَّهَا سَاۤءَتْ مُسْتَقَرًّا وَّمُقَامًا ","bc":"innahā sā`at mustaqarraw wa muqāmā","at":"sungguh, Jahanam itu seburuk-buruk tempat menetap dan tempat kediaman."},{"id":"67","ar":"وَالَّذِيْنَ اِذَآ اَنْفَقُوْا لَمْ يُسْرِفُوْا وَلَمْ يَقْتُرُوْا وَكَانَ بَيْنَ ذٰلِكَ قَوَامًا ","bc":"wallażīna iżā anfaqụ lam yusrifụ wa lam yaqturụ wa kāna baina żālika qawāmā","at":"Dan (termasuk hamba-hamba Tuhan Yang Maha Pengasih) orang-orang yang apabila menginfakkan (harta), mereka tidak berlebihan, dan tidak (pula) kikir, di antara keduanya secara wajar,"},{"id":"68","ar":"وَالَّذِيْنَ لَا يَدْعُوْنَ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ وَلَا يَقْتُلُوْنَ النَّفْسَ الَّتِيْ حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّ وَلَا يَزْنُوْنَۚ وَمَنْ يَّفْعَلْ ذٰلِكَ يَلْقَ اَثَامًا ۙ","bc":"wallażīna lā yad'ụna ma'allāhi ilāhan ākhara wa lā yaqtulụnan-nafsallatī ḥarramallāhu illā bil-ḥaqqi wa lā yaznụn, wa may yaf'al żālika yalqa aṡāmā","at":"dan orang-orang yang tidak mempersekutukan Allah dengan sembahan lain dan tidak membunuh orang yang diharamkan Allah kecuali dengan (alasan) yang benar, dan tidak berzina; dan barangsiapa melakukan demikian itu, niscaya dia mendapat hukuman yang berat,"},{"id":"69","ar":"يُّضٰعَفْ لَهُ الْعَذَابُ يَوْمَ الْقِيٰمَةِ وَيَخْلُدْ فِيْهٖ مُهَانًا ۙ","bc":"yuḍā'af lahul-'ażābu yaumal-qiyāmati wa yakhlud fīhī muhānā","at":"(yakni) akan dilipatgandakan azab untuknya pada hari Kiamat dan dia akan kekal dalam azab itu, dalam keadaan terhina, "},{"id":"70","ar":"اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ عَمَلًا صَالِحًا فَاُولٰۤىِٕكَ يُبَدِّلُ اللّٰهُ سَيِّاٰتِهِمْ حَسَنٰتٍۗ وَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ","bc":"illā man tāba wa āmana wa 'amila 'amalan ṣāliḥan fa ulā`ika yubaddilullāhu sayyi`ātihim ḥasanāt, wa kānallāhu gafụrar raḥīmā","at":"kecuali orang-orang yang bertobat dan beriman dan mengerjakan kebajikan; maka kejahatan mereka diganti Allah dengan kebaikan. Allah Maha Pengampun, Maha Penyayang."},{"id":"71","ar":"وَمَنْ تَابَ وَعَمِلَ صَالِحًا فَاِنَّهٗ يَتُوْبُ اِلَى اللّٰهِ مَتَابًا ","bc":"wa man tāba wa 'amila ṣāliḥan fa innahụ yatụbu ilallāhi matābā","at":"Dan barangsiapa bertobat dan mengerjakan kebajikan, maka sesungguhnya dia bertobat kepada Allah dengan tobat yang sebenar-benarnya."},{"id":"72","ar":"وَالَّذِيْنَ لَا يَشْهَدُوْنَ الزُّوْرَۙ وَاِذَا مَرُّوْا بِاللَّغْوِ مَرُّوْا كِرَامًا ","bc":"wallażīna lā yasy-hadụnaz-zụra wa iżā marrụ bil-lagwi marrụ kirāmā","at":"Dan orang-orang yang tidak memberikan kesaksian palsu, dan apabila mereka bertemu dengan (orang-orang) yang mengerjakan perbuatan-perbuatan yang tidak berfaedah, mereka berlalu dengan menjaga kehormatan dirinya,"},{"id":"73","ar":"وَالَّذِيْنَ اِذَا ذُكِّرُوْا بِاٰيٰتِ رَبِّهِمْ لَمْ يَخِرُّوْا عَلَيْهَا صُمًّا وَّعُمْيَانًا ","bc":"wallażīna iżā żukkirụ bi`āyāti rabbihim lam yakhirrụ 'alaihā ṣummaw wa 'umyānā","at":"dan orang-orang yang apabila diberi peringatan dengan ayat-ayat Tuhan mereka, mereka tidak bersikap sebagai orang-orang yang tuli dan buta,"},{"id":"74","ar":"وَالَّذِيْنَ يَقُوْلُوْنَ رَبَّنَا هَبْ لَنَا مِنْ اَزْوَاجِنَا وَذُرِّيّٰتِنَا قُرَّةَ اَعْيُنٍ وَّاجْعَلْنَا لِلْمُتَّقِيْنَ اِمَامًا ","bc":"wallażīna yaqụlụna rabbanā hab lanā min azwājinā wa żurriyyātinā qurrata a'yuniw waj'alnā lil-muttaqīna imāmā","at":"Dan orang-orang yang berkata, “Ya Tuhan kami, anugerahkanlah kepada kami pasangan kami dan keturunan kami sebagai penyenang hati (kami), dan jadikanlah kami pemimpin bagi orang-orang yang bertakwa.”"},{"id":"75","ar":"اُولٰۤىِٕكَ يُجْزَوْنَ الْغُرْفَةَ بِمَا صَبَرُوْا وَيُلَقَّوْنَ فِيْهَا تَحِيَّةً وَّسَلٰمًا ۙ","bc":"ulā`ika yujzaunal-gurfata bimā ṣabarụ wa yulaqqauna fīhā taḥiyyataw wa salāmā","at":"Mereka itu akan diberi balasan dengan tempat yang tinggi (dalam surga) atas kesabaran mereka, dan di sana mereka akan disambut dengan penghormatan dan salam."},{"id":"76","ar":"خٰلِدِيْنَ فِيْهَاۗ حَسُنَتْ مُسْتَقَرًّا وَّمُقَامًا ","bc":"khālidīna fīhā, ḥasunat mustaqarraw wa muqāmā","at":"Mereka kekal di dalamnya. Surga itu sebaik-baik tempat menetap dan tempat kediaman."},{"id":"77","ar":"قُلْ مَا يَعْبَؤُا بِكُمْ رَبِّيْ لَوْلَا دُعَاۤؤُكُمْۚ فَقَدْ كَذَّبْتُمْ فَسَوْفَ يَكُوْنُ لِزَامًا","bc":"qul mā ya'ba`u bikum rabbī lau lā du'ā`ukum, fa qad każżabtum fa saufa yakụnu lizāmā","at":"Katakanlah (Muhammad, kepada orang-orang musyrik), “Tuhanku tidak akan mengindahkan kamu, kalau tidak karena ibadahmu. (Tetapi bagaimana kamu beribadah kepada-Nya), padahal sungguh, kamu telah mendustakan-Nya? Karena itu, kelak (azab) pasti (menimpamu).”"}],al_gasyiyah=[{"id":"1","ar":"هَلْ اَتٰىكَ حَدِيْثُ الْغَاشِيَةِۗ","bc":"hal atāka ḥadīṡul-gāsyiyah","at":"Sudahkah sampai kepadamu berita tentang (hari Kiamat)?"},{"id":"2","ar":"وُجُوْهٌ يَّوْمَىِٕذٍ خَاشِعَةٌ ۙ","bc":"wujụhuy yauma`iżin khāsyi'ah","at":"Pada hari itu banyak wajah yang tertunduk terhina,"},{"id":"3","ar":"عَامِلَةٌ نَّاصِبَةٌ ۙ","bc":"'āmilatun nāṣibah","at":"(karena) bekerja keras lagi kepayahan,"},{"id":"4","ar":"تَصْلٰى نَارًا حَامِيَةً ۙ","bc":"taṣlā nāran ḥāmiyah","at":"mereka memasuki api yang sangat panas (neraka),"},{"id":"5","ar":"تُسْقٰى مِنْ عَيْنٍ اٰنِيَةٍ ۗ","bc":"tusqā min 'ainin āniyah","at":"diberi minum dari sumber mata air yang sangat panas."},{"id":"6","ar":"لَيْسَ لَهُمْ طَعَامٌ اِلَّا مِنْ ضَرِيْعٍۙ","bc":"laisa lahum ṭa'āmun illā min ḍarī'","at":"Tidak ada makanan bagi mereka selain dari pohon yang berduri,"},{"id":"7","ar":"لَّا يُسْمِنُ وَلَا يُغْنِيْ مِنْ جُوْعٍۗ","bc":"lā yusminu wa lā yugnī min jụ'","at":"yang tidak menggemukkan dan tidak menghilangkan lapar."},{"id":"8","ar":"وُجُوْهٌ يَّوْمَىِٕذٍ نَّاعِمَةٌ ۙ","bc":"wujụhuy yauma`iżin nā'imah","at":"Pada hari itu banyak (pula) wajah yang berseri-seri,"},{"id":"9","ar":"لِّسَعْيِهَا رَاضِيَةٌ ۙ ","bc":"lisa'yihā rāḍiyah","at":"merasa senang karena usahanya (sendiri),"},{"id":"10","ar":"فِيْ جَنَّةٍ عَالِيَةٍۙ","bc":"fī jannatin 'āliyah","at":"(mereka) dalam surga yang tinggi, "},{"id":"11","ar":"لَّا تَسْمَعُ فِيْهَا لَاغِيَةً ۗ","bc":"lā tasma'u fīhā lāgiyah","at":"di sana (kamu) tidak mendengar perkataan yang tidak berguna."},{"id":"12","ar":"فِيْهَا عَيْنٌ جَارِيَةٌ ۘ","bc":"fīhā 'ainun jāriyah","at":"Di sana ada mata air yang mengalir."},{"id":"13","ar":"فِيْهَا سُرُرٌ مَّرْفُوْعَةٌ ۙ","bc":"fīhā sururum marfụ'ah","at":"Di sana ada dipan-dipan yang ditinggikan,"},{"id":"14","ar":"وَّاَكْوَابٌ مَّوْضُوْعَةٌ ۙ","bc":"wa akwābum mauḍụ'ah","at":"dan gelas-gelas yang tersedia (di dekatnya),"},{"id":"15","ar":"وَّنَمَارِقُ مَصْفُوْفَةٌ ۙ ","bc":"wa namāriqu maṣfụfah","at":"dan bantal-bantal sandaran yang tersusun,"},{"id":"16","ar":"وَّزَرَابِيُّ مَبْثُوْثَةٌ ۗ","bc":"wa zarābiyyu mabṡụṡah","at":"dan permadani-permadani yang terhampar."},{"id":"17","ar":"اَفَلَا يَنْظُرُوْنَ اِلَى الْاِبِلِ كَيْفَ خُلِقَتْۗ","bc":"a fa lā yanẓurụna ilal-ibili kaifa khuliqat","at":"Maka tidakkah mereka memperhatikan unta, bagaimana diciptakan?"},{"id":"18","ar":"وَاِلَى السَّمَاۤءِ كَيْفَ رُفِعَتْۗ","bc":"wa ilas-samā`i kaifa rufi'at","at":"dan langit, bagaimana ditinggikan?"},{"id":"19","ar":"وَاِلَى الْجِبَالِ كَيْفَ نُصِبَتْۗ","bc":"wa ilal-jibāli kaifa nuṣibat","at":"Dan gunung-gunung bagaimana ditegakkan?"},{"id":"20","ar":"وَاِلَى الْاَرْضِ كَيْفَ سُطِحَتْۗ ","bc":"wa ilal-arḍi kaifa suṭiḥat","at":"Dan bumi bagaimana dihamparkan?"},{"id":"21","ar":"فَذَكِّرْۗ اِنَّمَآ اَنْتَ مُذَكِّرٌۙ","bc":"fa żakkir, innamā anta mużakkir","at":"Maka berilah peringatan, karena sesungguhnya engkau (Muhammad) hanyalah pemberi peringatan."},{"id":"22","ar":"لَّسْتَ عَلَيْهِمْ بِمُصَيْطِرٍۙ","bc":"lasta 'alaihim bimuṣaiṭir","at":"Engkau bukanlah orang yang berkuasa atas mereka,"},{"id":"23","ar":"اِلَّا مَنْ تَوَلّٰى وَكَفَرَۙ","bc":"illā man tawallā wa kafar","at":"kecuali (jika ada) orang yang berpaling dan kafir,"},{"id":"24","ar":"فَيُعَذِّبُهُ اللّٰهُ الْعَذَابَ الْاَكْبَرَۗ","bc":"fa yu'ażżibuhullāhul-'ażābal-akbar","at":"maka Allah akan mengazabnya dengan azab yang besar."},{"id":"25","ar":"اِنَّ اِلَيْنَآ اِيَابَهُمْ","bc":"inna ilainā iyābahum","at":"Sungguh, kepada Kamilah mereka kembali,"},{"id":"26","ar":"ثُمَّ اِنَّ عَلَيْنَا حِسَابَهُمْ","bc":"ṡumma inna 'alainā ḥisābahum","at":"kemudian sesungguhnya (kewajiban) Kamilah membuat perhitungan atas mereka."}],al_hadid=[{"id":"1","ar":"سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ","bc":"sabbaḥa lillāhi mā fis-samāwāti wal-arḍ, wa huwal-'azīzul-ḥakīm","at":"Apa yang di langit dan di bumi bertasbih kepada Allah. Dialah Yang Mahaperkasa, Mahabijaksana."},{"id":"2","ar":"لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۚ يُحْيٖ وَيُمِيْتُۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"lahụ mulkus-samāwāti wal-arḍ, yuḥyī wa yumīt, wa huwa 'alā kulli syai`ing qadīr","at":" Milik-Nyalah kerajaan langit dan bumi, Dia menghidupkan dan mematikan, dan Dia Mahakuasa atas segala sesuatu."},{"id":"3","ar":"هُوَ الْاَوَّلُ وَالْاٰخِرُ وَالظَّاهِرُ وَالْبَاطِنُۚ وَهُوَ بِكُلِّ شَيْءٍ عَلِيْمٌ ","bc":"huwal-awwalu wal-ākhiru waẓ-ẓāhiru wal-bāṭin, wa huwa bikulli syai`in 'alīm","at":"Dialah Yang Awal, Yang Akhir, Yang Zahir dan Yang Batin; dan Dia Maha Mengetahui segala sesuatu."},{"id":"4","ar":"هُوَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۚ يَعْلَمُ مَا يَلِجُ فِى الْاَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاۤءِ وَمَا يَعْرُجُ فِيْهَاۗ وَهُوَ مَعَكُمْ اَيْنَ مَا كُنْتُمْۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌۗ","bc":"huwallażī khalaqas-samāwāti wal-arḍa fī sittati ayyāmin ṡummastawā 'alal-'arsy, ya'lamu mā yaliju fil-arḍi wa mā yakhruju min-hā wa mā yanzilu minas-samā`i wa mā ya'ruju fīhā, wa huwa ma'akum aina mā kuntum, wallāhu bimā ta'malụna baṣīr","at":"Dialah yang menciptakan langit dan bumi dalam enam masa; kemudian Dia bersemayam di atas ‘Arsy. Dia mengetahui apa yang masuk ke dalam bumi dan apa yang keluar dari dalamnya, apa yang turun dari langit dan apa yang naik ke sana. Dan Dia bersama kamu di mana saja kamu berada. Dan Allah Maha Melihat apa yang kamu kerjakan."},{"id":"5","ar":"لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ","bc":"lahụ mulkus-samāwāti wal-arḍ, wa ilallāhi turja'ul-umụr","at":"Milik-Nyalah kerajaan langit dan bumi. Dan hanya kepada Allah segala urusan dikembalikan."},{"id":"6","ar":"يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِۗ وَهُوَ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ ","bc":"yụlijul-laila fin-nahāri wa yụlijun-nahāra fil-laīl, wa huwa 'alīmum biżātiṣ-ṣudụr","at":"Dia memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam. Dan Dia Maha Mengetahui segala isi hati."},{"id":"7","ar":"اٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَاَنْفِقُوْا مِمَّا جَعَلَكُمْ مُّسْتَخْلَفِيْنَ فِيْهِۗ فَالَّذِيْنَ اٰمَنُوْا مِنْكُمْ وَاَنْفَقُوْا لَهُمْ اَجْرٌ كَبِيْرٌ","bc":"āminụ billāhi wa rasụlihī wa anfiqụ mimmā ja'alakum mustakhlafīna fīh, fallażīna āmanụ mingkum wa anfaqụ lahum ajrung kabīr","at":"Berimanlah kamu kepada Allah dan Rasul-Nya dan infakkanlah (di jalan Allah) sebagian dari harta yang Dia telah menjadikan kamu sebagai penguasanya (amanah). Maka orang-orang yang beriman di antara kamu dan menginfakkan (hartanya di jalan Allah) memperoleh pahala yang besar."},{"id":"8","ar":"وَمَا لَكُمْ لَا تُؤْمِنُوْنَ بِاللّٰهِ ۚوَالرَّسُوْلُ يَدْعُوْكُمْ لِتُؤْمِنُوْا بِرَبِّكُمْ وَقَدْ اَخَذَ مِيْثَاقَكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ","bc":"wa mā lakum lā tu`minụna billāh, war-rasụlu yad'ụkum litu`minụ birabbikum wa qad akhaża mīṡāqakum ing kuntum mu`minīn","at":"Dan mengapa kamu tidak beriman kepada Allah, padahal Rasul mengajak kamu beriman kepada Tuhanmu? Dan Dia telah mengambil janji (setia)mu, jika kamu orang-orang mukmin."},{"id":"9","ar":"هُوَ الَّذِيْ يُنَزِّلُ عَلٰى عَبْدِهٖٓ اٰيٰتٍۢ بَيِّنٰتٍ لِّيُخْرِجَكُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَاِنَّ اللّٰهَ بِكُمْ لَرَءُوْفٌ رَّحِيْمٌ ","bc":"huwallażī yunazzilu 'alā 'abdihī āyātim bayyinātil liyukhrijakum minaẓ-ẓulumāti ilan-nụr, wa innallāha bikum lara`ụfur raḥīm","at":"Dialah yang menurunkan ayat-ayat yang terang (Al-Qur'an) kepada hamba-Nya (Muhammad) untuk mengeluarkan kamu dari kegelapan kepada cahaya. Dan sungguh, terhadap kamu Allah Maha Penyantun, Maha Penyayang. "},{"id":"10","ar":"وَمَا لَكُمْ اَلَّا تُنْفِقُوْا فِيْ سَبِيْلِ اللّٰهِ وَلِلّٰهِ مِيْرَاثُ السَّمٰوٰتِ وَالْاَرْضِۗ لَا يَسْتَوِيْ مِنْكُمْ مَّنْ اَنْفَقَ مِنْ قَبْلِ الْفَتْحِ وَقَاتَلَۗ اُولٰۤىِٕكَ اَعْظَمُ دَرَجَةً مِّنَ الَّذِيْنَ اَنْفَقُوْا مِنْۢ بَعْدُ وَقَاتَلُوْاۗ وَكُلًّا وَّعَدَ اللّٰهُ الْحُسْنٰىۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ ","bc":"wa mā lakum allā tunfiqụ fī sabīlillāhi wa lillāhi mīrāṡus-samāwāti wal-arḍ, lā yastawī mingkum man anfaqa ming qablil-fat-ḥi wa qātal, ulā`ika a'ẓamu darajatam minallażīna anfaqụ mim ba'du wa qātalụ, wa kullaw wa'adallāhul-ḥusnā, wallāhu bimā ta'malụna khabīr","at":"Dan mengapa kamu tidak menginfakkan hartamu di jalan Allah, padahal milik Allah semua pusaka langit dan bumi? Tidak sama orang yang menginfakkan (hartanya di jalan Allah) di antara kamu dan berperang sebelum penaklukan (Mekah). Mereka lebih tinggi derajatnya daripada orang-orang yang menginfakkan (hartanya) dan berperang setelah itu. Dan Allah menjanjikan kepada masing-masing mereka (balasan) yang lebih baik. Dan Allah Mahateliti apa yang kamu kerjakan."},{"id":"11","ar":"مَنْ ذَا الَّذِيْ يُقْرِضُ اللّٰهَ قَرْضًا حَسَنًا فَيُضٰعِفَهٗ لَهٗ وَلَهٗٓ اَجْرٌ كَرِيْمٌ ","bc":"man żallażī yuqriḍullāha qarḍan ḥasanan fa yuḍā'ifahụ lahụ wa lahū ajrung karīm","at":"Barangsiapa meminjamkan kepada Allah dengan pinjaman yang baik, maka Allah akan mengembalikannya berlipat ganda untuknya, dan baginya pahala yang mulia,"},{"id":"12","ar":"يَوْمَ تَرَى الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ يَسْعٰى نُوْرُهُمْ بَيْنَ اَيْدِيْهِمْ وَبِاَيْمَانِهِمْ بُشْرٰىكُمُ الْيَوْمَ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ ذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُۚ ","bc":"yauma taral-mu`minīna wal-mu`mināti yas'ā nụruhum baina aidīhim wa bi`aimānihim busyrākumul-yauma jannātun tajrī min taḥtihal-an-hāru khālidīna fīhā, żālika huwal-fauzul-'aẓīm","at":"pada hari engkau akan melihat orang-orang yang beriman laki-laki dan perempuan, betapa cahaya mereka bersinar di depan dan di samping kanan mereka, (dikatakan kepada mereka), “Pada hari ini ada berita gembira untukmu, (yaitu) surga-surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Demikian itulah kemenangan yang agung.”"},{"id":"13","ar":"يَوْمَ يَقُوْلُ الْمُنٰفِقُوْنَ وَالْمُنٰفِقٰتُ لِلَّذِيْنَ اٰمَنُوا انْظُرُوْنَا نَقْتَبِسْ مِنْ نُّوْرِكُمْۚ قِيْلَ ارْجِعُوْا وَرَاۤءَكُمْ فَالْتَمِسُوْا نُوْرًاۗ فَضُرِبَ بَيْنَهُمْ بِسُوْرٍ لَّهٗ بَابٌۗ بَاطِنُهٗ فِيْهِ الرَّحْمَةُ وَظَاهِرُهٗ مِنْ قِبَلِهِ الْعَذَابُۗ","bc":"yauma yaqụlul-munāfiqụna wal-munāfiqātu lillażīna āmanunẓurụnā naqtabis min nụrikum, qīlarji'ụ warā`akum faltamisụ nụrā, fa ḍuriba bainahum bisụril lahụ bāb, bāṭinuhụ fīhir-raḥmatu wa ẓāhiruhụ ming qibalihil-'ażāb","at":"Pada hari orang-orang munafik laki-laki dan perempuan berkata kepada orang-orang yang beriman, “Tunggulah kami! Kami ingin mengambil cahayamu.” (Kepada mereka) dikatakan, ”Kembalilah kamu ke belakang dan carilah sendiri cahaya (untukmu).” Lalu di antara mereka dipasang dinding (pemisah) yang berpintu. Di sebelah dalam ada rahmat dan di luarnya hanya ada azab."},{"id":"14","ar":"يُنَادُوْنَهُمْ اَلَمْ نَكُنْ مَّعَكُمْۗ قَالُوْا بَلٰى وَلٰكِنَّكُمْ فَتَنْتُمْ اَنْفُسَكُمْ وَتَرَبَّصْتُمْ وَارْتَبْتُمْ وَغَرَّتْكُمُ الْاَمَانِيُّ حَتّٰى جَاۤءَ اَمْرُ اللّٰهِ وَغَرَّكُمْ بِاللّٰهِ الْغَرُوْرُ ","bc":"yunādụnahum a lam nakum ma'akum, qālụ balā wa lākinnakum fatantum anfusakum wa tarabbaṣtum wartabtum wa garratkumul-amāniyyu ḥattā jā`a amrullāhi wa garrakum billāhil-garụr","at":"Orang-orang munafik memanggil orang-orang mukmin, “Bukankah kami dahulu bersama kamu?” Mereka menjawab, “Benar, tetapi kamu mencelakakan dirimu sendiri, dan hanya menunggu, meragukan (janji Allah) dan ditipu oleh angan-angan kosong sampai datang ketetapan Allah; dan penipu (setan) datang memperdaya kamu tentang Allah."},{"id":"15","ar":"فَالْيَوْمَ لَا يُؤْخَذُ مِنْكُمْ فِدْيَةٌ وَّلَا مِنَ الَّذِيْنَ كَفَرُوْاۗ مَأْوٰىكُمُ النَّارُۗ هِيَ مَوْلٰىكُمْۗ وَبِئْسَ الْمَصِيْرُ ","bc":"fal-yauma lā yu`khażu mingkum fidyatuw wa lā minallażīna kafarụ, ma`wākumun-nār, hiya maulākum, wa bi`sal-maṣīr","at":"Maka pada hari ini tidak akan diterima tebusan dari kamu maupun dari orang-orang kafir. Tempat kamu di neraka. Itulah tempat berlindungmu, dan itulah seburuk-buruk tempat kembali.”"},{"id":"16","ar":"اَلَمْ يَأْنِ لِلَّذِيْنَ اٰمَنُوْٓا اَنْ تَخْشَعَ قُلُوْبُهُمْ لِذِكْرِ اللّٰهِ وَمَا نَزَلَ مِنَ الْحَقِّۙ وَلَا يَكُوْنُوْا كَالَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلُ فَطَالَ عَلَيْهِمُ الْاَمَدُ فَقَسَتْ قُلُوْبُهُمْۗ وَكَثِيْرٌ مِّنْهُمْ فٰسِقُوْنَ ","bc":"a lam ya`ni lillażīna āmanū an takhsya'a qulụbuhum liżikrillāhi wa mā nazala minal-ḥaqqi wa lā yakụnụ kallażīna ụtul-kitāba ming qablu fa ṭāla 'alaihimul-amadu fa qasat qulụbuhum, wa kaṡīrum min-hum fāsiqụn","at":"Belum tibakah waktunya bagi orang-orang yang beriman, untuk secara khusyuk mengingat Allah dan mematuhi kebenaran yang telah diwahyukan (kepada mereka), dan janganlah mereka (berlaku) seperti orang-orang yang telah menerima kitab sebelum itu, kemudian mereka melalui masa yang panjang sehingga hati mereka menjadi keras. Dan banyak di antara mereka menjadi orang-orang fasik."},{"id":"17","ar":"اِعْلَمُوْٓا اَنَّ اللّٰهَ يُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ قَدْ بَيَّنَّا لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَعْقِلُوْنَ ","bc":"i'lamū annallāha yuḥyil-arḍa ba'da mautihā, qad bayyannā lakumul-āyāti la'allakum ta'qilụn","at":"Ketahuilah bahwa Allah yang menghidupkan bumi setelah matinya (kering). Sungguh, telah Kami jelaskan kepadamu tanda-tanda (kebesaran Kami) agar kamu mengerti."},{"id":"18","ar":"اِنَّ الْمُصَّدِّقِيْنَ وَالْمُصَّدِّقٰتِ وَاَقْرَضُوا اللّٰهَ قَرْضًا حَسَنًا يُّضٰعَفُ لَهُمْ وَلَهُمْ اَجْرٌ كَرِيْمٌ ","bc":"innal-muṣṣaddiqīna wal-muṣṣaddiqāti wa aqraḍullāha qarḍan ḥasanay yuḍā'afu lahum wa lahum ajrung karīm","at":"Sesungguhnya orang-orang yang bersedekah baik laki-laki maupun perempuan dan meminjamkan kepada Allah dengan pinjaman yang baik, akan dilipatgandakan (balasannya) bagi mereka; dan mereka akan mendapat pahala yang mulia."},{"id":"19","ar":"وَالَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرُسُلِهٖٓ اُولٰۤىِٕكَ هُمُ الصِّدِّيْقُوْنَ ۖوَالشُّهَدَاۤءُ عِنْدَ رَبِّهِمْۗ لَهُمْ اَجْرُهُمْ وَنُوْرُهُمْۗ وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ","bc":"wallażīna āmanụ billāhi wa rusulihī ulā`ika humuṣ-ṣiddīqụna wasy-syuhadā`u 'inda rabbihim, lahum ajruhum wa nụruhum, wallażīna kafarụ wa każżabụ bi`āyātinā ulā`ika aṣ-ḥābul-jaḥīm","at":"Dan orang-orang yang beriman kepada Allah dan rasul-rasul-Nya, mereka itu orang-orang yang tulus hati (pencinta kebenaran) dan saksi-saksi di sisi Tuhan mereka. Mereka berhak mendapat pahala dan cahaya. Tetapi orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itu penghuni-penghuni neraka. "},{"id":"20","ar":"اِعْلَمُوْٓا اَنَّمَا الْحَيٰوةُ الدُّنْيَا لَعِبٌ وَّلَهْوٌ وَّزِيْنَةٌ وَّتَفَاخُرٌۢ بَيْنَكُمْ وَتَكَاثُرٌ فِى الْاَمْوَالِ وَالْاَوْلَادِۗ كَمَثَلِ غَيْثٍ اَعْجَبَ الْكُفَّارَ نَبَاتُهٗ ثُمَّ يَهِيْجُ فَتَرٰىهُ مُصْفَرًّا ثُمَّ يَكُوْنُ حُطَامًاۗ وَفِى الْاٰخِرَةِ عَذَابٌ شَدِيْدٌۙ وَّمَغْفِرَةٌ مِّنَ اللّٰهِ وَرِضْوَانٌ ۗوَمَا الْحَيٰوةُ الدُّنْيَآ اِلَّا مَتَاعُ الْغُرُوْرِ ","bc":"i'lamū annamal-ḥayātud-dun-yā la'ibuw wa lahwuw wa zīnatuw wa tafākhurum bainakum wa takāṡurun fil-amwāli wal-aulād, kamaṡali gaiṡin a'jabal-kuffāra nabātuhụ ṡumma yahīju fa tarāhu muṣfarran ṡumma yakụnu huṭāmā, wa fil-ākhirati 'ażābun syadīduw wa magfiratum minallāhi wa riḍwān, wa mal-ḥayātud-dun-yā illā matā'ul-gurụr","at":"Ketahuilah, sesungguhnya kehidupan dunia itu hanyalah permainan dan sendagurauan, perhiasan dan saling berbangga di antara kamu serta berlomba dalam kekayaan dan anak keturunan, seperti hujan yang tanam-tanamannya mengagumkan para petani; kemudian (tanaman) itu menjadi kering dan kamu lihat warnanya kuning kemudian menjadi hancur. Dan di akhirat (nanti) ada azab yang keras dan ampunan dari Allah serta keridaan-Nya. Dan kehidupan dunia tidak lain hanyalah kesenangan yang palsu. "},{"id":"21","ar":"سَابِقُوْٓا اِلٰى مَغْفِرَةٍ مِّنْ رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ السَّمَاۤءِ وَالْاَرْضِۙ اُعِدَّتْ لِلَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرُسُلِهٖۗ ذٰلِكَ فَضْلُ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُ ۚوَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ","bc":"sābiqū ilā magfiratim mir rabbikum wa jannatin 'arḍuhā ka'arḍis-samā`i wal-arḍi u'iddat lillażīna āmanụ billāhi wa rusulih, żālika faḍlullāhi yu`tīhi may yasyā`, wallāhu żul-faḍlil-'aẓīm","at":"Berlomba-lombalah kamu untuk mendapatkan ampunan dari Tuhanmu dan surga yang luasnya seluas langit dan bumi, yang disediakan bagi orang-orang yang beriman kepada Allah dan rasul-rasul-Nya. Itulah karunia Allah, yang diberikan kepada siapa yang Dia kehendaki. Dan Allah mempunyai karunia yang besar. "},{"id":"22","ar":"مَآ اَصَابَ مِنْ مُّصِيْبَةٍ فِى الْاَرْضِ وَلَا فِيْٓ اَنْفُسِكُمْ اِلَّا فِيْ كِتٰبٍ مِّنْ قَبْلِ اَنْ نَّبْرَاَهَا ۗاِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌۖ ","bc":"mā aṣāba mim muṣībatin fil-arḍi wa lā fī anfusikum illā fī kitābim ming qabli an nabra`ahā, inna żālika 'alallāhi yasīr","at":"Setiap bencana yang menimpa di bumi dan yang menimpa dirimu sendiri, semuanya telah tertulis dalam Kitab (Lauh Mahfuzh) sebelum Kami mewujudkannya. Sungguh, yang demikian itu mudah bagi Allah. "},{"id":"23","ar":"لِّكَيْلَا تَأْسَوْا عَلٰى مَا فَاتَكُمْ وَلَا تَفْرَحُوْا بِمَآ اٰتٰىكُمْ ۗوَاللّٰهُ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُوْرٍۙ","bc":"likai lā ta`sau 'alā mā fātakum wa lā tafraḥụ bimā ātākum, wallāhu lā yuḥibbu kulla mukhtālin fakhụr","at":"Agar kamu tidak bersedih hati terhadap apa yang luput dari kamu, dan jangan pula terlalu gembira terhadap apa yang diberikan-Nya kepadamu. Dan Allah tidak menyukai setiap orang yang sombong dan membanggakan diri, "},{"id":"24","ar":" ۨالَّذِيْنَ يَبْخَلُوْنَ وَيَأْمُرُوْنَ النَّاسَ بِالْبُخْلِ ۗوَمَنْ يَّتَوَلَّ فَاِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَمِيْدُ ","bc":"allażīna yabkhalụna wa ya`murụnan-nāsa bil-bukhl, wa may yatawalla fa innallāha huwal-ganiyyul-ḥamīd","at":"yaitu orang-orang yang kikir dan menyuruh orang lain berbuat kikir. Barangsiapa berpaling (dari perintah-perintah Allah), maka sesungguhnya Allah, Dia Mahakaya, Maha Terpuji."},{"id":"25","ar":"لَقَدْ اَرْسَلْنَا رُسُلَنَا بِالْبَيِّنٰتِ وَاَنْزَلْنَا مَعَهُمُ الْكِتٰبَ وَالْمِيْزَانَ لِيَقُوْمَ النَّاسُ بِالْقِسْطِۚ وَاَنْزَلْنَا الْحَدِيْدَ فِيْهِ بَأْسٌ شَدِيْدٌ وَّمَنَافِعُ لِلنَّاسِ وَلِيَعْلَمَ اللّٰهُ مَنْ يَّنْصُرُهٗ وَرُسُلَهٗ بِالْغَيْبِۗ اِنَّ اللّٰهَ قَوِيٌّ عَزِيْزٌ ","bc":"laqad arsalnā rusulanā bil-bayyināti wa anzalnā ma'ahumul-kitāba wal-mīzāna liyaqụman-nāsu bil-qisṭ, wa anzalnal-ḥadīda fīhi ba`sun syadīduw wa manāfi'u lin-nāsi wa liya'lamallāhu may yanṣuruhụ wa rusulahụ bil-gaīb, innallāha qawiyyun 'azīz","at":"Sungguh, Kami telah mengutus rasul-rasul Kami dengan bukti-bukti yang nyata dan kami turunkan bersama mereka kitab dan neraca (keadilan) agar manusia dapat berlaku adil. Dan Kami menciptakan besi yang mempunyai kekuatan, hebat dan banyak manfaat bagi manusia, dan agar Allah mengetahui siapa yang menolong (agama)-Nya dan rasul-rasul-Nya walaupun (Allah) tidak dilihatnya. Sesungguhnya Allah Mahakuat, Mahaperkasa."},{"id":"26","ar":"وَلَقَدْ اَرْسَلْنَا نُوْحًا وَّاِبْرٰهِيْمَ وَجَعَلْنَا فِيْ ذُرِّيَّتِهِمَا النُّبُوَّةَ وَالْكِتٰبَ فَمِنْهُمْ مُّهْتَدٍۚ وَكَثِيْرٌ مِّنْهُمْ فٰسِقُوْنَ","bc":"wa laqad arsalnā nụḥaw wa ibrāhīma wa ja'alnā fī żurriyyatihiman-nubuwwata wal-kitāba fa min-hum muhtad, wa kaṡīrum min-hum fāsiqụn","at":"Dan sungguh, Kami telah mengutus Nuh dan Ibrahim dan Kami berikan kenabian dan kitab (wahyu) kepada keturunan keduanya, di antara mereka ada yang menerima petunjuk dan banyak di antara mereka yang fasik."},{"id":"27","ar":"ثُمَّ قَفَّيْنَا عَلٰٓى اٰثَارِهِمْ بِرُسُلِنَا وَقَفَّيْنَا بِعِيْسَى ابْنِ مَرْيَمَ وَاٰتَيْنٰهُ الْاِنْجِيْلَ ەۙ وَجَعَلْنَا فِيْ قُلُوْبِ الَّذِيْنَ اتَّبَعُوْهُ رَأْفَةً وَّرَحْمَةً ۗوَرَهْبَانِيَّةَ ِۨابْتَدَعُوْهَا مَا كَتَبْنٰهَا عَلَيْهِمْ اِلَّا ابْتِغَاۤءَ رِضْوَانِ اللّٰهِ فَمَا رَعَوْهَا حَقَّ رِعَايَتِهَا ۚفَاٰتَيْنَا الَّذِيْنَ اٰمَنُوْا مِنْهُمْ اَجْرَهُمْ ۚ وَكَثِيْرٌ مِّنْهُمْ فٰسِقُوْنَ","bc":"ṡumma qaffainā 'alā āṡārihim birusulinā wa qaffainā bi'īsabni maryama wa ātaināhul-injīla wa ja'alnā fī qulụbillażīnattaba'ụhu ra`fataw wa raḥmah, wa rahbāniyyatanibtada'ụhā mā katabnāhā 'alaihim illabtigā`a riḍwānillāhi fa mā ra'auhā ḥaqqa ri'āyatihā, fa ātainallażīna āmanụ min-hum ajrahum, wa kaṡīrum min-hum fāsiqụn","at":"Kemudian Kami susulkan rasul-rasul Kami mengikuti jejak mereka dan Kami susulkan (pula) Isa putra Maryam; Dan Kami berikan Injil kepadanya dan Kami jadikan rasa santun dan kasih sayang dalam hati orang-orang yang mengikutinya. Mereka mengada-adakan rahbaniyyah, padahal Kami tidak mewajibkannya kepada mereka (yang Kami wajibkan hanyalah) mencari keridaan Allah, tetapi tidak mereka pelihara dengan semestinya. Maka kepada orang-orang yang beriman di antara mereka Kami berikan pahalanya, dan banyak di antara mereka yang fasik."},{"id":"28","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَاٰمِنُوْا بِرَسُوْلِهٖ يُؤْتِكُمْ كِفْلَيْنِ مِنْ رَّحْمَتِهٖ وَيَجْعَلْ لَّكُمْ نُوْرًا تَمْشُوْنَ بِهٖ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌۙ","bc":"yā ayyuhallażīna āmanuttaqullāha wa āminụ birasụlihī yu`tikum kiflaini mir raḥmatihī wa yaj'al lakum nụran tamsyụna bihī wa yagfir lakum, wallāhu gafụrur raḥīm","at":"Wahai orang-orang yang beriman! Bertakwalah kepada Allah dan berimanlah kepada Rasul-Nya (Muhammad), niscaya Allah memberikan rahmat-Nya kepadamu dua bagian, dan menjadikan cahaya untukmu yang dengan cahaya itu kamu dapat berjalan serta Dia mengampuni kamu. Dan Allah Maha Pengampun, Maha Penyayang,"},{"id":"29","ar":"لِّئَلَّا يَعْلَمَ اَهْلُ الْكِتٰبِ اَلَّا يَقْدِرُوْنَ عَلٰى شَيْءٍ مِّنْ فَضْلِ اللّٰهِ وَاَنَّ الْفَضْلَ بِيَدِ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُ ۗوَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ ۔","bc":"li`allā ya'lama ahlul-kitābi allā yaqdirụna 'alā syai`im min faḍlillāhi wa annal-faḍla biyadillāhi yu`tīhi may yasyā`, wallāhu żul-faḍlil-'aẓīm","at":"agar Ahli Kitab mengetahui bahwa sedikit pun mereka tidak akan mendapat karunia Allah (jika mereka tidak beriman kepada Muhammad), dan bahwa karunia itu ada di tangan Allah, Dia memberikannya kepada siapa yang Dia kehendaki. Dan Allah mempunyai karunia yang besar."}],al_hajj=[{"id":"1","ar":"يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمْۚ اِنَّ زَلْزَلَةَ السَّاعَةِ شَيْءٌ عَظِيْمٌ ","bc":"yā ayyuhan-nāsuttaqụ rabbakum, inna zalzalatas-sā'ati syai`un 'aẓīm","at":"Wahai manusia! Bertakwalah kepada Tuhanmu; sungguh, guncangan (hari) Kiamat itu adalah suatu (kejadian) yang sangat besar."},{"id":"2","ar":"يَوْمَ تَرَوْنَهَا تَذْهَلُ كُلُّ مُرْضِعَةٍ عَمَّآ اَرْضَعَتْ وَتَضَعُ كُلُّ ذَاتِ حَمْلٍ حَمْلَهَا وَتَرَى النَّاسَ سُكٰرٰى وَمَا هُمْ بِسُكٰرٰى وَلٰكِنَّ عَذَابَ اللّٰهِ شَدِيْدٌ ","bc":"yauma taraunahā taż-halu kullu murḍi'atin 'ammā arḍa'at wa taḍa'u kullu żāti ḥamlin ḥamlahā wa taran-nāsa sukārā wa mā hum bisukārā wa lākinna 'ażāballāhi syadīd","at":"(Ingatlah) pada hari ketika kamu melihatnya (goncangan itu), semua perempuan yang menyusui anaknya akan lalai terhadap anak yang disusuinya, dan setiap perempuan yang hamil akan keguguran kandungannya, dan kamu melihat manusia dalam keadaan mabuk, padahal sebenarnya mereka tidak mabuk, tetapi azab Allah itu sangat keras."},{"id":"3","ar":"وَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّيَتَّبِعُ كُلَّ شَيْطٰنٍ مَّرِيْدٍۙ ","bc":"wa minan-nāsi may yujādilu fillāhi bigairi 'ilmiw wa yattabi'u kulla syaiṭānim marīd","at":"Dan di antara manusia ada yang berbantahan tentang Allah tanpa ilmu dan hanya mengikuti para setan yang sangat jahat."},{"id":"4","ar":"كُتِبَ عَلَيْهِ اَنَّهٗ مَنْ تَوَلَّاهُ فَاَنَّهٗ يُضِلُّهٗ وَيَهْدِيْهِ اِلٰى عَذَابِ السَّعِيْرِ ","bc":"kutiba 'alaihi annahụ man tawallāhu fa annahụ yuḍilluhụ wa yahdīhi ilā 'ażābis-sa'īr","at":"(Tentang setan), telah ditetapkan bahwa siapa yang berkawan dengan dia, maka dia akan menyesatkannya, dan membawanya ke azab neraka."},{"id":"5","ar":"يٰٓاَيُّهَا النَّاسُ اِنْ كُنْتُمْ فِيْ رَيْبٍ مِّنَ الْبَعْثِ فَاِنَّا خَلَقْنٰكُمْ مِّنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ مِنْ مُّضْغَةٍ مُّخَلَّقَةٍ وَّغَيْرِ مُخَلَّقَةٍ لِّنُبَيِّنَ لَكُمْۗ وَنُقِرُّ فِى الْاَرْحَامِ مَا نَشَاۤءُ اِلٰٓى اَجَلٍ مُّسَمًّى ثُمَّ نُخْرِجُكُمْ طِفْلًا ثُمَّ لِتَبْلُغُوْٓا اَشُدَّكُمْۚ وَمِنْكُمْ مَّنْ يُّتَوَفّٰى وَمِنْكُمْ مَّنْ يُّرَدُّ اِلٰٓى اَرْذَلِ الْعُمُرِ لِكَيْلَا يَعْلَمَ مِنْۢ بَعْدِ عِلْمٍ شَيْـًٔاۗ وَتَرَى الْاَرْضَ هَامِدَةً فَاِذَآ اَنْزَلْنَا عَلَيْهَا الْمَاۤءَ اهْتَزَّتْ وَرَبَتْ وَاَنْۢبَتَتْ مِنْ كُلِّ زَوْجٍۢ بَهِيْجٍ","bc":"yā ayyuhan-nāsu ing kuntum fī raibim minal-ba'ṡi fa innā khalaqnākum min turābin ṡumma min nuṭfatin ṡumma min 'alaqatin ṡumma mim muḍgatim mukhallaqatiw wa gairi mukhallaqatil linubayyina lakum, wa nuqirru fil-ar-ḥāmi mā nasyā`u ilā ajalim musamman ṡumma nukhrijukum ṭiflan ṡumma litablugū asyuddakum, wa mingkum may yutawaffā wa mingkum may yuraddu ilā arżalil-'umuri likai lā ya'lama mim ba'di 'ilmin syai`ā, wa taral-arḍa hāmidatan fa iżā anzalnā 'alaihal-mā`ahtazzat wa rabat wa ambatat ming kulli zaujim bahīj","at":"Wahai manusia! Jika kamu meragukan (hari) kebangkitan, maka sesungguhnya Kami telah menjadikan kamu dari tanah, kemudian dari setetes mani, kemudian dari segumpal darah, kemudian dari segumpal daging yang sempurna kejadiannya dan yang tidak sempurna, agar Kami jelaskan kepada kamu; dan Kami tetapkan dalam rahim menurut kehendak Kami sampai waktu yang sudah ditentukan, kemudian Kami keluarkan kamu sebagai bayi, kemudian (dengan berangsur-angsur) kamu sampai kepada usia dewasa, dan di antara kamu ada yang diwafatkan dan (ada pula) di antara kamu yang dikembalikan sampai usia sangat tua (pikun), sehingga dia tidak mengetahui lagi sesuatu yang telah diketahuinya. Dan kamu lihat bumi ini kering, kemudian apabila telah Kami turunkan air (hujan) di atasnya, hiduplah bumi itu dan menjadi subur dan menumbuhkan berbagai jenis pasangan (tetumbuhan) yang indah."},{"id":"6","ar":"ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّهٗ يُحْيِ الْمَوْتٰى وَاَنَّهٗ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ۙ","bc":"żālika bi`annallāha huwal-ḥaqqu wa annahụ yuḥyil-mautā wa annahụ 'alā kulli syai`ing qadīr","at":"Yang demikian itu karena sungguh, Allah, Dialah yang hak dan sungguh, Dialah yang menghidupkan segala yang telah mati, dan sungguh, Dia Mahakuasa atas segala sesuatu."},{"id":"7","ar":"وَّاَنَّ السَّاعَةَ اٰتِيَةٌ لَّا رَيْبَ فِيْهَاۙ وَاَنَّ اللّٰهَ يَبْعَثُ مَنْ فِى الْقُبُوْرِ ","bc":"wa annas-sā'ata ātiyatul lā raiba fīhā wa annallāha yab'aṡu man fil-qubụr","at":"Dan sungguh, (hari) Kiamat itu pasti datang, tidak ada keraguan padanya; dan sungguh, Allah akan membangkitkan siapa pun yang di dalam kubur."},{"id":"8","ar":"وَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّلَا هُدًى وَّلَا كِتٰبٍ مُّنِيْرٍ ۙ","bc":"wa minan-nāsi may yujādilu fillāhi bigairi 'ilmiw wa lā hudaw wa lā kitābim munīr","at":"Dan di antara manusia ada yang berbantahan tentang Allah tanpa ilmu, tanpa petunjuk dan tanpa kitab (wahyu) yang memberi penerangan."},{"id":"9","ar":"ثَانِيَ عِطْفِهٖ لِيُضِلَّ عَنْ سَبِيْلِ اللّٰهِ ۗ لَهٗ فِى الدُّنْيَا خِزْيٌ وَّنُذِيْقُهٗ يَوْمَ الْقِيٰمَةِ عَذَابَ الْحَرِيْقِ","bc":"ṡāniya 'iṭfihī liyuḍilla 'an sabīlillāh, lahụ fid-dun-yā khizyuw wa nużīquhụ yaumal-qiyāmati 'ażābal-ḥarīq","at":"Sambil memalingkan lambungnya (dengan congkak) untuk menyesatkan manusia dari jalan Allah. Dia mendapat kehinaan di dunia, dan pada hari Kiamat Kami berikan kepadanya rasa azab neraka yang membakar."},{"id":"10","ar":"ذٰلِكَ بِمَا قَدَّمَتْ يَدٰكَ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِ","bc":"żālika bimā qaddamat yadāka wa annallāha laisa biẓallāmil lil-'abīd","at":"(Akan dikatakan kepadanya), “Itu karena perbuatan yang dilakukan dahulu oleh kedua tanganmu, dan Allah sekali-kali tidak menzalimi hamba-hamba-Nya."},{"id":"11","ar":"وَمِنَ النَّاسِ مَنْ يَّعْبُدُ اللّٰهَ عَلٰى حَرْفٍۚ فَاِنْ اَصَابَهٗ خَيْرُ ِۨاطْمَـَٔنَّ بِهٖۚ وَاِنْ اَصَابَتْهُ فِتْنَةُ ِۨانْقَلَبَ عَلٰى وَجْهِهٖۗ خَسِرَ الدُّنْيَا وَالْاٰخِرَةَۗ ذٰلِكَ هُوَ الْخُسْرَانُ الْمُبِيْنُ ","bc":"wa minan-nāsi may ya'budullāha 'alā ḥarf, fa in aṣābahụ khairuniṭma`anna bih, wa in aṣābat-hu fitnatuningqalaba 'alā waj-hih, khasirad-dun-yā wal-ākhirah, żālika huwal-khusrānul-mubīn","at":"Dan di antara manusia ada yang menyembah Allah hanya di tepi; maka jika dia memperoleh kebajikan, dia merasa puas, dan jika dia ditimpa suatu cobaan, dia berbalik ke belakang. Dia rugi di dunia dan di akhirat. Itulah kerugian yang nyata."},{"id":"12","ar":"يَدْعُوْا مِنْ دُوْنِ اللّٰهِ مَا لَا يَضُرُّهٗ وَمَا لَا يَنْفَعُهٗۗ ذٰلِكَ هُوَ الضَّلٰلُ الْبَعِيْدُ ۚ","bc":"yad'ụ min dụnillāhi mā lā yaḍurruhụ wa mā lā yanfa'uh, żālika huwaḍ-ḍalālul-ba'īd","at":"Dia menyeru kepada selain Allah sesuatu yang tidak dapat mendatangkan bencana dan tidak (pula) memberi manfaat kepadanya. Itulah kesesatan yang jauh."},{"id":"13","ar":"يَدْعُوْا لَمَنْ ضَرُّهٗٓ اَقْرَبُ مِنْ نَّفْعِهٖۗ لَبِئْسَ الْمَوْلٰى وَلَبِئْسَ الْعَشِيْرُ ","bc":"yad'ụ laman ḍarruhū aqrabu min naf'ih, labi`sal-maulā wa labi`sal-'asyīr","at":"Dia menyeru kepada sesuatu yang (sebenarnya) bencananya lebih dekat daripada manfaatnya. Sungguh, itu seburuk-buruk penolong dan sejahat-jahat kawan."},{"id":"14","ar":"اِنَّ اللّٰهَ يُدْخِلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۗ اِنَّ اللّٰهَ يَفْعَلُ مَا يُرِيْدُ ","bc":"innallāha yudkhilullażīna āmanụ wa 'amiluṣ-ṣāliḥāti jannātin tajrī min taḥtihal-an-hār, innallāha yaf'alu mā yurīd","at":"(Sungguh,) Allah akan memasukkan orang-orang yang beriman dan mengerjakan kebajikan ke dalam surga-surga yang mengalir di bawahnya sungai-sungai. Sungguh, Allah berbuat apa yang Dia kehendaki."},{"id":"15","ar":"مَنْ كَانَ يَظُنُّ اَنْ لَّنْ يَّنْصُرَهُ اللّٰهُ فِى الدُّنْيَا وَالْاٰخِرَةِ فَلْيَمْدُدْ بِسَبَبٍ اِلَى السَّمَاۤءِ ثُمَّ لْيَقْطَعْ فَلْيَنْظُرْ هَلْ يُذْهِبَنَّ كَيْدُهٗ مَا يَغِيْظُ ","bc":"mang kāna yaẓunnu al lay yanṣurahullāhu fid-dun-yā wal-ākhirati falyamdud bisababin ilas-samā`i ṡummalyaqṭa' falyanẓur hal yuż-hibanna kaiduhụ mā yagīẓ","at":"Barangsiapa menyangka bahwa Allah tidak akan menolongnya (Muhammad) di dunia dan di akhirat, maka hendaklah dia merentangkan tali ke langit-langit, ) lalu menggantung (diri), kemudian pikirkanlah apakah tipu dayanya itu dapat melenyapkan apa yang menyakitkan hatinya."},{"id":"16","ar":"وَكَذٰلِكَ اَنْزَلْنٰهُ اٰيٰتٍۢ بَيِّنٰتٍۙ وَّاَنَّ اللّٰهَ يَهْدِيْ مَنْ يُّرِيْدُ ","bc":"wa każālika anzalnāhu āyātim bayyinātiw wa annallāha yahdī may yurīd","at":"Dan demikianlah Kami telah menurunkannya (Al-Qur'an) yang merupakan ayat-ayat yang nyata; sesungguhnya Allah memberikan petunjuk kepada siapa yang Dia kehendaki."},{"id":"17","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالصَّابِـِٕيْنَ وَالنَّصٰرٰى وَالْمَجُوْسَ وَالَّذِيْنَ اَشْرَكُوْٓا ۖاِنَّ اللّٰهَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ","bc":"innallażīna āmanụ wallażīna hādụ waṣ-ṣābi`īna wan-naṣārā wal-majụsa wallażīna asyrakū innallāha yafṣilu bainahum yaumal-qiyāmah, innallāha 'alā kulli syai`in syahīd","at":"Sesungguhnya orang-orang beriman, orang Yahudi, orang Sabiin, orang Nasrani, orang Majusi dan orang musyrik, Allah pasti memberi keputusan di antara mereka pada hari Kiamat. Sungguh, Allah menjadi saksi atas segala sesuatu."},{"id":"18","ar":"اَلَمْ تَرَ اَنَّ اللّٰهَ يَسْجُدُ لَهٗ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِ وَالشَّمْسُ وَالْقَمَرُ وَالنُّجُوْمُ وَالْجِبَالُ وَالشَّجَرُ وَالدَّوَاۤبُّ وَكَثِيْرٌ مِّنَ النَّاسِۗ وَكَثِيْرٌ حَقَّ عَلَيْهِ الْعَذَابُۗ وَمَنْ يُّهِنِ اللّٰهُ فَمَا لَهٗ مِنْ مُّكْرِمٍۗ اِنَّ اللّٰهَ يَفْعَلُ مَا يَشَاۤءُ ۩ۗ","bc":"a lam tara annallāha yasjudu lahụ man fis-samāwāti wa man fil-arḍi wasy-syamsu wal-qamaru wan-nujụmu wal-jibālu wasy-syajaru wad-dawābbu wa kaṡīrum minan-nās, wa kaṡīrun ḥaqqa 'alaihil-'ażāb, wa may yuhinillāhu fa mā lahụ mim mukrim, innallāha yaf'alu mā yasyā`,","at":"Tidakkah engkau tahu bahwa siapa yang ada di langit dan siapa yang ada di bumi bersujud kepada Allah, juga matahari, bulan, bintang, gunung-gunung, pohon-pohon, hewan-hewan yang melata dan banyak di antara manusia? Tetapi banyak (manusia) yang pantas mendapatkan azab. Barangsiapa dihinakan Allah, tidak seorang pun yang akan memuliakannya. Sungguh, Allah berbuat apa saja yang Dia kehendaki."},{"id":"19","ar":"۞ هٰذَانِ خَصْمٰنِ اخْتَصَمُوْا فِيْ رَبِّهِمْ فَالَّذِيْنَ كَفَرُوْا قُطِّعَتْ لَهُمْ ثِيَابٌ مِّنْ نَّارٍۗ يُصَبُّ مِنْ فَوْقِ رُءُوْسِهِمُ الْحَمِيْمُ ۚ","bc":"hāżāni khaṣmānikhtaṣamụ fī rabbihim fallażīna kafarụ quṭṭi'at lahum ṡiyābum min nār, yuṣabbu min fauqi ru`ụsihimul-ḥamīm","at":"Inilah dua golongan (golongan mukmin dan kafir) yang bertengkar, mereka bertengkar mengenai Tuhan mereka. Maka bagi orang kafir akan dibuatkan pakaian-pakaian dari api (neraka) untuk mereka. Ke atas kepala mereka akan disiramkan air yang mendidih."},{"id":"20","ar":"يُصْهَرُ بِهٖ مَا فِيْ بُطُوْنِهِمْ وَالْجُلُوْدُ ۗ","bc":"yuṣ-haru bihī mā fī buṭụnihim wal-julụd","at":"Dengan (air mendidih) itu akan dihancurluluhkan apa yang ada dalam perut dan kulit mereka."},{"id":"21","ar":"وَلَهُمْ مَّقَامِعُ مِنْ حَدِيْدٍ ","bc":"wa lahum maqāmi'u min ḥadīd","at":"Dan (azab) untuk mereka cambuk-cambuk dari besi."},{"id":"22","ar":"كُلَّمَآ اَرَادُوْٓا اَنْ يَّخْرُجُوْا مِنْهَا مِنْ غَمٍّ اُعِيْدُوْا فِيْهَا وَذُوْقُوْا عَذَابَ الْحَرِيْقِ","bc":"kullamā arādū ay yakhrujụ min-hā min gammin u'īdụ fīhā wa żụqụ 'ażābal-ḥarīq","at":"Setiap kali mereka hendak keluar darinya (neraka) karena tersiksa, mereka dikembalikan (lagi) ke dalamnya. (Kepada mereka dikatakan), “Rasakanlah azab yang membakar ini!”"},{"id":"23","ar":"اِنَّ اللّٰهَ يُدْخِلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّلُؤْلُؤًاۗ وَلِبَاسُهُمْ فِيْهَا حَرِيْرٌ ","bc":"innallāha yudkhilullażīna āmanụ wa 'amiluṣ-ṣāliḥāti jannātin tajrī min taḥtihal-an-hāru yuḥallauna fīhā min asāwira min żahabiw wa lu`lu`ā, wa libāsuhum fīhā ḥarīr","at":"Sungguh, Allah akan memasukkan orang-orang yang beriman dan mengerjakan kebajikan ke dalam surga-surga yang mengalir di bawahnya sungai-sungai. Di sana mereka diberi perhiasan gelang-gelang emas dan mutiara, dan pakaian mereka dari sutera."},{"id":"24","ar":"وَهُدُوْٓا اِلَى الطَّيِّبِ مِنَ الْقَوْلِۚ وَهُدُوْٓا اِلٰى صِرَاطِ الْحَمِيْدِ ","bc":"wa hudū ilaṭ-ṭayyibi minal-qaụl, wa hudū ilā ṣirāṭil-ḥamīd","at":"Dan mereka diberi petunjuk kepada ucapan-ucapan yang baik dan diberi petunjuk (pula) kepada jalan (Allah) yang terpuji."},{"id":"25","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَالْمَسْجِدِ الْحَرَامِ الَّذِيْ جَعَلْنٰهُ لِلنَّاسِ سَوَاۤءً ۨالْعَاكِفُ فِيْهِ وَالْبَادِۗ وَمَنْ يُّرِدْ فِيْهِ بِاِلْحَادٍۢ بِظُلْمٍ نُّذِقْهُ مِنْ عَذَابٍ اَلِيْمٍ","bc":"innallażīna kafarụ wa yaṣuddụna 'an sabīlillāhi wal-masjidil-ḥarāmillażī ja'alnāhu lin-nāsi sawā`anil-'ākifu fīhi wal-bād, wa may yurid fīhi bi`il-ḥādim biẓulmin nużiq-hu min 'ażābin alīm","at":"Sungguh, orang-orang kafir dan yang menghalangi (manusia) dari jalan Allah dan dari Masjidilharam yang telah Kami jadikan terbuka untuk semua manusia, baik yang bermukim di sana maupun yang datang dari luar dan siapa saja yang bermaksud melakukan kejahatan secara zalim di dalamnya, niscaya akan Kami rasakan kepadanya siksa yang pedih."},{"id":"26","ar":"وَاِذْ بَوَّأْنَا لِاِبْرٰهِيْمَ مَكَانَ الْبَيْتِ اَنْ لَّا تُشْرِكْ بِيْ شَيْـًٔا وَّطَهِّرْ بَيْتِيَ لِلطَّاۤىِٕفِيْنَ وَالْقَاۤىِٕمِيْنَ وَالرُّكَّعِ السُّجُوْدِ ","bc":"wa iż bawwa`nā li`ibrāhīma makānal-baiti al lā tusyrik bī syai`aw wa ṭahhir baitiya liṭ-ṭā`ifīna wal-qā`imīna war-rukka'is-sujụd","at":"Dan (ingatlah), ketika Kami tempatkan Ibrahim di tempat Baitullah (dengan mengatakan), “Janganlah engkau mempersekutukan Aku dengan apa pun dan sucikanlah rumah-Ku bagi orang-orang yang tawaf, dan orang yang beribadah dan orang yang rukuk dan sujud."},{"id":"27","ar":"وَاَذِّنْ فِى النَّاسِ بِالْحَجِّ يَأْتُوْكَ رِجَالًا وَّعَلٰى كُلِّ ضَامِرٍ يَّأْتِيْنَ مِنْ كُلِّ فَجٍّ عَمِيْقٍ ۙ","bc":"wa ażżin fin-nāsi bil-ḥajji ya`tụka rijālaw wa 'alā kulli ḍāmiriy ya`tīna ming kulli fajjin 'amīq","at":"Dan serulah manusia untuk mengerjakan haji, niscaya mereka akan datang kepadamu dengan berjalan kaki, atau mengendarai setiap unta yang kurus, mereka datang dari segenap penjuru yang jauh."},{"id":"28","ar":"لِّيَشْهَدُوْا مَنَافِعَ لَهُمْ وَيَذْكُرُوا اسْمَ اللّٰهِ فِيْٓ اَيَّامٍ مَّعْلُوْمٰتٍ عَلٰى مَا رَزَقَهُمْ مِّنْۢ بَهِيْمَةِ الْاَنْعَامِۚ فَكُلُوْا مِنْهَا وَاَطْعِمُوا الْبَاۤىِٕسَ الْفَقِيْرَ ۖ","bc":"liyasy-hadụ manāfi'a lahum wa yażkurusmallāhi fī ayyāmim ma'lụmātin 'alā mā razaqahum mim bahīmatil-an'ām, fa kulụ min-hā wa aṭ'imul-bā`isal-faqīr","at":"Agar mereka menyaksikan berbagai manfaat untuk mereka dan agar mere-ka menyebut nama Allah pada beberapa hari yang telah ditentukan atas rezeki yang diberikan Dia kepada mereka berupa hewan ternak. Maka makanlah sebagian darinya dan (sebagian lagi) berikanlah untuk dimakan orang-orang yang sengsara dan fakir."},{"id":"29","ar":"ثُمَّ لْيَقْضُوْا تَفَثَهُمْ وَلْيُوْفُوْا نُذُوْرَهُمْ وَلْيَطَّوَّفُوْا بِالْبَيْتِ الْعَتِيْقِ ","bc":"ṡummalyaqḍụ tafaṡahum walyụfụ nużụrahum walyaṭṭawwafụ bil-baitil-'atīq","at":"Kemudian, hendaklah mereka menghilangkan kotoran (yang ada di badan) mereka, menyempurnakan nazar-nazar mereka dan melakukan tawaf sekeliling rumah tua (Baitullah). "},{"id":"30","ar":"ذٰلِكَ وَمَنْ يُّعَظِّمْ حُرُمٰتِ اللّٰهِ فَهُوَ خَيْرٌ لَّهٗ عِنْدَ رَبِّهٖۗ وَاُحِلَّتْ لَكُمُ الْاَنْعَامُ اِلَّا مَا يُتْلٰى عَلَيْكُمْ فَاجْتَنِبُوا الرِّجْسَ مِنَ الْاَوْثَانِ وَاجْتَنِبُوْا قَوْلَ الزُّوْرِ ۙ","bc":"żālika wa may yu'aẓẓim ḥurumātillāhi fa huwa khairul lahụ 'inda rabbih, wa uḥillat lakumul-an'āmu illā mā yutlā 'alaikum fajtanibur-rijsa minal-auṡāni wajtanibụ qaulaz-zụr","at":"Demikianlah (perintah Allah). Dan barang siapa mengagungkan apa yang terhormat di sisi Allah (hurumat) maka itu lebih baik baginya di sisi Tuhannya. Dan dihalalkan bagi kamu semua hewan ternak, kecuali yang diterangkan kepadamu (keharamannya), maka jauhilah olehmu (penyembahan) berhala-berhala yang najis itu dan jauhilah perkataan dusta."},{"id":"31","ar":"حُنَفَاۤءَ لِلّٰهِ غَيْرَ مُشْرِكِيْنَ بِهٖۗ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَكَاَنَّمَا خَرَّ مِنَ السَّمَاۤءِ فَتَخْطَفُهُ الطَّيْرُ اَوْ تَهْوِيْ بِهِ الرِّيْحُ فِيْ مَكَانٍ سَحِيْقٍ ","bc":"hunafā`a lillāhi gaira musyrikīna bih, wa may yusyrik billāhi fa ka`annamā kharra minas-samā`i fa takhṭafuhuṭ-ṭairu au tahwī bihir-rīḥu fī makānin saḥīq","at":"(Beribadahlah) dengan ikhlas kepada Allah, tanpa mempersekutukan-Nya. Barangsiapa mempersekutukan Allah, maka seakan-akan dia jatuh dari langit lalu disambar oleh burung, atau diterbangkan angin ke tempat yang jauh."},{"id":"32","ar":"ذٰلِكَ وَمَنْ يُّعَظِّمْ شَعَاۤىِٕرَ اللّٰهِ فَاِنَّهَا مِنْ تَقْوَى الْقُلُوْبِ ","bc":"żālika wa may yu'aẓẓim sya'ā`irallāhi fa innahā min taqwal-qulụb","at":"Demikianlah (perintah Allah). Dan barangsiapa mengagungkan syiar-syiar Allah, maka sesungguhnya hal itu timbul dari ketakwaan hati."},{"id":"33","ar":"لَكُمْ فِيْهَا مَنَافِعُ اِلٰٓى اَجَلٍ مُّسَمًّى ثُمَّ مَحِلُّهَآ اِلَى الْبَيْتِ الْعَتِيْقِ","bc":"lakum fīhā manāfi'u ilā ajalim musamman ṡumma mahilluhā ilal-baitil-'atīq","at":"Bagi kamu padanya (hewan hadyu) ada beberapa manfaat, sampai waktu yang ditentukan, kemudian tempat penyembelihannya adalah di sekitar Baitul Atiq (Baitullah)."},{"id":"34","ar":"وَلِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكًا لِّيَذْكُرُوا اسْمَ اللّٰهِ عَلٰى مَا رَزَقَهُمْ مِّنْۢ بَهِيْمَةِ الْاَنْعَامِۗ فَاِلٰهُكُمْ اِلٰهٌ وَّاحِدٌ فَلَهٗٓ اَسْلِمُوْاۗ وَبَشِّرِ الْمُخْبِتِيْنَ ۙ","bc":"wa likulli ummatin ja'alnā mansakal liyażkurusmallāhi 'alā mā razaqahum mim bahīmatil-an'ām, fa ilāhukum ilāhuw wāḥidun fa lahū aslimụ, wa basysyiril-mukhbitīn","at":"Dan bagi setiap umat telah Kami syariatkan penyembelihan (kurban), agar mereka menyebut nama Allah atas rezeki yang dikaruniakan Allah kepada mereka berupa hewan ternak. Maka Tuhanmu ialah Tuhan Yang Maha Esa, karena itu berserahdirilah kamu kepada-Nya. Dan sampaikanlah (Muhammad) kabar gembira kepada orang-orang yang tunduk patuh (kepada Allah),"},{"id":"35","ar":"الَّذِيْنَ اِذَا ذُكِرَ اللّٰهُ وَجِلَتْ قُلُوْبُهُمْ وَالصَّابِرِيْنَ عَلٰى مَآ اَصَابَهُمْ وَالْمُقِيْمِى الصَّلٰوةِۙ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ","bc":"allażīna iżā żukirallāhu wajilat qulụbuhum waṣ-ṣābirīna 'alā mā aṣābahum wal-muqīmiṣ-ṣalāti wa mimmā razaqnāhum yunfiqụn","at":"(yaitu) orang-orang yang apabila disebut nama Allah hati mereka bergetar, orang yang sabar atas apa yang menimpa mereka, dan orang yang melaksanakan salat dan orang yang menginfakkan sebagian rezeki yang Kami karuniakan kepada mereka."},{"id":"36","ar":"وَالْبُدْنَ جَعَلْنٰهَا لَكُمْ مِّنْ شَعَاۤىِٕرِ اللّٰهِ لَكُمْ فِيْهَا خَيْرٌۖ فَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهَا صَوَاۤفَّۚ فَاِذَا وَجَبَتْ جُنُوْبُهَا فَكُلُوْا مِنْهَا وَاَطْعِمُوا الْقَانِعَ وَالْمُعْتَرَّۗ كَذٰلِكَ سَخَّرْنٰهَا لَكُمْ لَعَلَّكُمْ تَشْكُرُوْنَ ","bc":"wal-budna ja'alnāhā lakum min sya'ā`irillāhi lakum fīhā khairun fażkurusmallāhi 'alaihā ṣawāff, fa iżā wajabat junụbuhā fa kulụ min-hā wa aṭ'imul-qāni'a wal-mu'tarr, każālika sakhkharnāhā lakum la'allakum tasykurụn","at":"Dan unta-unta itu Kami jadikan untuk-mu bagian dari syiar agama Allah, kamu banyak memperoleh kebaikan padanya. Maka sebutlah nama Allah (ketika kamu akan menyembelihnya) dalam keadaan berdiri (dan kaki-kaki telah terikat). Kemudian apabila telah rebah (mati), maka makanlah sebagiannya dan berilah makanlah orang yang merasa cukup dengan apa yang ada padanya (tidak meminta-minta) dan orang yang meminta. Demikianlah Kami tundukkan (unta-unta itu) untukmu, agar kamu bersyukur."},{"id":"37","ar":"لَنْ يَّنَالَ اللّٰهَ لُحُوْمُهَا وَلَا دِمَاۤؤُهَا وَلٰكِنْ يَّنَالُهُ التَّقْوٰى مِنْكُمْۗ كَذٰلِكَ سَخَّرَهَا لَكُمْ لِتُكَبِّرُوا اللّٰهَ عَلٰى مَا هَدٰىكُمْ ۗ وَبَشِّرِ الْمُحْسِنِيْنَ ","bc":"lay yanālallāha luḥụmuhā wa lā dimā`uhā wa lākiy yanāluhut-taqwā mingkum, każālika sakhkharahā lakum litukabbirullāha 'alā mā hadākum, wa basysyiril-muḥsinīn","at":"Daging (hewan kurban) dan darahnya itu sekali-kali tidak akan sampai kepada Allah, tetapi yang sampai kepada-Nya adalah ketakwaan kamu. Demi-kianlah Dia menundukkannya untuk-mu agar kamu mengagungkan Allah atas petunjuk yang Dia berikan kepadamu. Dan sampaikanlah kabar gembira kepada orang-orang yang berbuat baik."},{"id":"38","ar":"۞ اِنَّ اللّٰهَ يُدَافِعُ عَنِ الَّذِيْنَ اٰمَنُوْاۗ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ خَوَّانٍ كَفُوْرٍ","bc":"innallāha yudāfi'u 'anillażīna āmanū, innallāha lā yuḥibbu kulla khawwāning kafụr","at":"Sesungguhnya Allah membela orang yang beriman. Sungguh, Allah tidak menyukai setiap orang yang berkhianat dan kufur nikmat."},{"id":"39","ar":"اُذِنَ لِلَّذِيْنَ يُقَاتَلُوْنَ بِاَنَّهُمْ ظُلِمُوْاۗ وَاِنَّ اللّٰهَ عَلٰى نَصْرِهِمْ لَقَدِيْرٌ ۙ","bc":"użina lillażīna yuqātalụna bi`annahum ẓulimụ, wa innallāha 'alā naṣrihim laqadīr","at":"Diizinkan (berperang) kepada orang-orang yang diperangi, karena sesungguhnya mereka dizalimi. Dan sung-guh, Allah Mahakuasa menolong mereka itu,"},{"id":"40","ar":" ۨالَّذِيْنَ اُخْرِجُوْا مِنْ دِيَارِهِمْ بِغَيْرِ حَقٍّ اِلَّآ اَنْ يَّقُوْلُوْا رَبُّنَا اللّٰهُ ۗوَلَوْلَا دَفْعُ اللّٰهِ النَّاسَ بَعْضَهُمْ بِبَعْضٍ لَّهُدِّمَتْ صَوَامِعُ وَبِيَعٌ وَّصَلَوٰتٌ وَّمَسٰجِدُ يُذْكَرُ فِيْهَا اسْمُ اللّٰهِ كَثِيْرًاۗ وَلَيَنْصُرَنَّ اللّٰهُ مَنْ يَّنْصُرُهٗۗ اِنَّ اللّٰهَ لَقَوِيٌّ عَزِيْزٌ ","bc":"allażīna ukhrijụ min diyārihim bigairi ḥaqqin illā ay yaqụlụ rabbunallāh, walau lā daf'ullāhin-nāsa ba'ḍahum biba'ḍil lahuddimat ṣawāmi'u wa biya'uw wa ṣalawātuw wa masājidu yużkaru fīhasmullāhi kaṡīrā, wa layanṣurannallāhu may yanṣuruh, innallāha laqawiyyun 'azīz","at":"(yaitu) orang-orang yang diusir dari kampung halamannya tanpa alasan yang benar, hanya karena mereka berkata, “Tuhan kami ialah Allah.” Seandainya Allah tidak menolak (keganasan) sebagian manusia dengan sebagian yang lain, tentu telah dirobohkan biara-biara Nasrani, gereja-gereja, rumah-rumah ibadah orang Yahudi dan masjid-masjid, yang di dalamnya banyak disebut nama Allah. Allah pasti akan menolong orang yang menolong (agama)-Nya. Sungguh, Allah Mahakuat, Mahaperkasa."},{"id":"41","ar":"اَلَّذِيْنَ اِنْ مَّكَّنّٰهُمْ فِى الْاَرْضِ اَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ وَاَمَرُوْا بِالْمَعْرُوْفِ وَنَهَوْا عَنِ الْمُنْكَرِۗ وَلِلّٰهِ عَاقِبَةُ الْاُمُوْرِ ","bc":"allażīna im makkannāhum fil-arḍi aqāmuṣ-ṣalāta wa ātawuz-zakāta wa amarụ bil-ma'rụfi wa nahau 'anil-mungkar, wa lillāhi 'āqibatul-umụr","at":"(Yaitu) orang-orang yang jika Kami beri kedudukan di bumi, mereka melaksanakan salat, menunaikan zakat, dan menyuruh berbuat yang makruf dan mencegah dari yang mungkar; dan kepada Allah-lah kembali segala urusan."},{"id":"42","ar":"وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ وَّعَادٌ وَّثَمُوْدُ ۙ","bc":"wa iy yukażżibụka fa qad każżabat qablahum qaumu nụḥiw wa 'āduw wa ṡamụd","at":"Dan jika mereka (orang-orang musyrik) mendustakan engkau (Muhammad), begitu pulalah kaum-kaum yang sebelum mereka, kaum Nuh, ‘Ad, dan Samud (juga telah mendustakan rasul-rasul-Nya),"},{"id":"43","ar":"وَقَوْمُ اِبْرٰهِيْمَ وَقَوْمُ لُوْطٍ ۙ","bc":"wa qaumu ibrāhīma wa qaumu lụṭ","at":"dan (demikian juga) kaum Ibrahim dan kaum Lut,"},{"id":"44","ar":"وَّاَصْحٰبُ مَدْيَنَۚ وَكُذِّبَ مُوْسٰى فَاَمْلَيْتُ لِلْكٰفِرِيْنَ ثُمَّ اَخَذْتُهُمْۚ فَكَيْفَ كَانَ نَكِيْرِ ","bc":"wa aṣ-ḥābu madyan, wa kużżiba mụsā fa amlaitu lil-kāfirīna ṡumma akhażtuhum, fa kaifa kāna nakīr","at":"dan penduduk Madyan. Dan Musa (juga) telah didustakan, namun Aku beri tenggang waktu kepada orang-orang kafir, kemudian Aku siksa mereka, maka betapa hebatnya siksaan-Ku."},{"id":"45","ar":"فَكَاَيِّنْ مِّنْ قَرْيَةٍ اَهْلَكْنٰهَا وَهِيَ ظَالِمَةٌ فَهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَاۖ وَبِئْرٍ مُّعَطَّلَةٍ وَّقَصْرٍ مَّشِيْدٍ ","bc":"fa ka`ayyim ming qaryatin ahlaknāhā wa hiya ẓālimatun fa hiya khāwiyatun 'alā 'urụsyihā wa bi`rim mu'aṭṭalatiw wa qaṣrim masyīd","at":"Maka betapa banyak negeri yang telah Kami binasakan karena (penduduk)nya dalam keadaan zalim, sehingga runtuh bangunan-bangunannya dan (betapa banyak pula) sumur yang telah ditinggalkan dan istana yang tinggi (ti-dak ada penghuninya)."},{"id":"46","ar":"اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَتَكُوْنَ لَهُمْ قُلُوْبٌ يَّعْقِلُوْنَ بِهَآ اَوْ اٰذَانٌ يَّسْمَعُوْنَ بِهَاۚ فَاِنَّهَا لَا تَعْمَى الْاَبْصَارُ وَلٰكِنْ تَعْمَى الْقُلُوْبُ الَّتِيْ فِى الصُّدُوْرِ ","bc":"a fa lam yasīrụ fil-arḍi fa takụna lahum qulụbuy ya'qilụna bihā au āżānuy yasma'ụna bihā, fa innahā lā ta'mal-abṣāru wa lākin ta'mal-qulụbullatī fiṣ-ṣudụr","at":"Maka tidak pernahkah mereka berjalan di bumi, sehingga hati (akal) mereka dapat memahami, telinga mereka dapat mendengar? Sebenarnya bukan mata itu yang buta, tetapi yang buta ialah hati yang di dalam dada. "},{"id":"47","ar":"وَيَسْتَعْجِلُوْنَكَ بِالْعَذَابِ وَلَنْ يُّخْلِفَ اللّٰهُ وَعْدَهٗۗ وَاِنَّ يَوْمًا عِنْدَ رَبِّكَ كَاَلْفِ سَنَةٍ مِّمَّا تَعُدُّوْنَ ","bc":"wa yasta'jilụnaka bil-'ażābi wa lay yukhlifallāhu wa'dah, wa inna yauman 'inda rabbika ka`alfi sanatim mimmā ta'uddụn","at":"Dan mereka meminta kepadamu (Muhammad) agar azab itu disegerakan, padahal Allah tidak akan menyalahi janji-Nya. Dan sesungguhnya sehari di sisi Tuhanmu adalah seperti seribu tahun menurut perhitunganmu."},{"id":"48","ar":"وَكَاَيِّنْ مِّنْ قَرْيَةٍ اَمْلَيْتُ لَهَا وَهِيَ ظَالِمَةٌ ثُمَّ اَخَذْتُهَاۚ وَاِلَيَّ الْمَصِيْرُ","bc":"wa ka`ayyim ming qaryatin amlaitu lahā wa hiya ẓālimatun ṡumma akhażtuhā, wa ilayyal-maṣīr","at":"Dan berapa banyak negeri yang Aku tangguhkan (penghancuran)nya, karena penduduknya berbuat zalim, kemudian Aku azab mereka, dan hanya kepada-Kulah tempat kembali (segala sesuatu)."},{"id":"49","ar":"قُلْ يٰٓاَيُّهَا النَّاسُ اِنَّمَآ اَنَا۠ لَكُمْ نَذِيْرٌ مُّبِيْنٌ ۚ","bc":"qul yā ayyuhan-nāsu innamā ana lakum nażīrum mubīn","at":"Katakanlah (Muhammad), “Wahai manusia! Sesungguhnya aku (diutus) kepadamu sebagai pemberi peringatan yang nyata.”"},{"id":"50","ar":"فَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ ","bc":"fallażīna āmanụ wa 'amiluṣ-ṣāliḥāti lahum magfiratuw wa rizqung karīm","at":"Maka orang-orang yang beriman dan mengerjakan kebajikan, mereka memperoleh ampunan dan rezeki yang mulia."},{"id":"51","ar":"وَالَّذِيْنَ سَعَوْا فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ ","bc":"wallażīna sa'au fī āyātinā mu'ājizīna ulā`ika aṣ-ḥābul-jaḥīm","at":"Tetapi orang-orang yang berusaha menentang ayat-ayat Kami dengan maksud melemahkan (kemauan untuk beriman), mereka itu adalah penghuni-penghuni neraka Jahim."},{"id":"52","ar":"وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رَّسُوْلٍ وَّلَا نَبِيٍّ اِلَّآ اِذَا تَمَنّٰىٓ اَلْقَى الشَّيْطٰنُ فِيْٓ اُمْنِيَّتِهٖۚ فَيَنْسَخُ اللّٰهُ مَا يُلْقِى الشَّيْطٰنُ ثُمَّ يُحْكِمُ اللّٰهُ اٰيٰتِهٖۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ۙ","bc":"wa mā arsalnā ming qablika mir rasụliw wa lā nabiyyin illā iżā tamannā alqasy-syaiṭānu fī umniyyatih, fa yansakhullāhu mā yulqisy-syaiṭānu ṡumma yuḥkimullāhu āyātih, wallāhu 'alīmun ḥakīm","at":"Dan Kami tidak mengutus seorang rasul dan tidak (pula) seorang nabi sebelum engkau (Muhammad), mela-inkan apabila dia mempunyai suatu keinginan, setan pun memasukkan godaan-godaan ke dalam keinginannya itu. Tetapi Allah menghilangkan apa yang dimasukkan setan itu, dan Allah akan menguatkan ayat-ayat-Nya. Dan Allah Maha Mengetahui, Mahabijaksana,"},{"id":"53","ar":"لِّيَجْعَلَ مَا يُلْقِى الشَّيْطٰنُ فِتْنَةً لِّلَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ وَّالْقَاسِيَةِ قُلُوْبُهُمْۗ وَاِنَّ الظّٰلِمِيْنَ لَفِيْ شِقَاقٍۢ بَعِيْدٍ ۙ","bc":"liyaj'ala mā yulqisy-syaiṭānu fitnatal lillażīna fī qulụbihim maraḍuw wal-qāsiyati qulụbuhum, wa innaẓ-ẓālimīna lafī syiqāqim ba'īd","at":"Dia (Allah) ingin menjadikan godaan yang ditimbulkan setan itu, sebagai cobaan bagi orang-orang yang dalam hatinya ada penyakit dan orang yang berhati keras. Dan orang-orang yang zalim itu benar-benar dalam permu-suhan yang jauh,"},{"id":"54","ar":"وَّلِيَعْلَمَ الَّذِيْنَ اُوْتُوا الْعِلْمَ اَنَّهُ الْحَقُّ مِنْ رَّبِّكَ فَيُؤْمِنُوْا بِهٖ فَتُخْبِتَ لَهٗ قُلُوْبُهُمْۗ وَاِنَّ اللّٰهَ لَهَادِ الَّذِيْنَ اٰمَنُوْٓا اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"wa liya'lamallażīna ụtul-'ilma annahul-ḥaqqu mir rabbika fa yu`minụ bihī fa tukhbita lahụ qulụbuhum, wa innallāha lahādillażīna āmanū ilā ṣirāṭim mustaqīm","at":"dan agar orang-orang yang telah diberi ilmu, meyakini bahwa (Al-Qur'an) itu benar dari Tuhanmu lalu mereka beriman dan hati mereka tunduk kepadanya. Dan sungguh, Allah pemberi petunjuk bagi orang-orang yang beriman kepada jalan yang lurus."},{"id":"55","ar":"وَلَا يَزَالُ الَّذِيْنَ كَفَرُوْا فِيْ مِرْيَةٍ مِّنْهُ حَتّٰى تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً اَوْ يَأْتِيَهُمْ عَذَابُ يَوْمٍ عَقِيْمٍ ","bc":"wa lā yazālullażīna kafarụ fī miryatim min-hu ḥattā ta`tiyahumus-sā'atu bagtatan au ya`tiyahum 'ażābu yaumin 'aqīm","at":"Dan orang-orang kafir itu senantiasa ragu mengenai hal itu (Al-Qur'an), hingga saat (kematiannya) datang kepada mereka dengan tiba-tiba, atau azab hari Kiamat yang datang kepada mereka."},{"id":"56","ar":"اَلْمُلْكُ يَوْمَىِٕذٍ لِّلّٰهِ ۗيَحْكُمُ بَيْنَهُمْۗ فَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فِيْ جَنّٰتِ النَّعِيْمِ ","bc":"al-mulku yauma`iżil lillāh, yaḥkumu bainahum, fallażīna āmanụ wa 'amiluṣ-ṣāliḥāti fī jannātin-na'īm","at":"Kekuasaan pada hari itu ada pada Allah, Dia memberi keputusan di an-tara mereka. Maka orang-orang yang beriman dan mengerjakan kebajikan berada dalam surga-surga yang penuh kenikmatan."},{"id":"57","ar":"وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَا فَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ مُّهِيْنٌ","bc":"wallażīna kafarụ wa każżabụ bi`āyātinā fa ulā`ika lahum 'ażābum muhīn","at":"Dan orang-orang kafir dan yang men-dustakan ayat-ayat Kami, maka mere-ka akan merasakan azab yang meng-hinakan."},{"id":"58","ar":"وَالَّذِيْنَ هَاجَرُوْا فِيْ سَبِيْلِ اللّٰهِ ثُمَّ قُتِلُوْٓا اَوْ مَاتُوْا لَيَرْزُقَنَّهُمُ اللّٰهُ رِزْقًا حَسَنًاۗ وَاِنَّ اللّٰهَ لَهُوَ خَيْرُ الرّٰزِقِيْنَ ","bc":"wallażīna hājarụ fī sabīlillāhi ṡumma qutilū au mātụ layarzuqannahumullāhu rizqan ḥasanā, wa innallāha lahuwa khairur-rāziqīn","at":"Dan orang-orang yang berhijrah di jalan Allah, kemudian mereka terbunuh atau mati, sungguh, Allah akan memberikan kepada mereka rezeki yang baik (surga). Dan sesungguhnya Allah adalah pemberi rezeki yang terbaik."},{"id":"59","ar":"لَيُدْخِلَنَّهُمْ مُّدْخَلًا يَّرْضَوْنَهٗۗ وَاِنَّ اللّٰهَ لَعَلِيْمٌ حَلِيْمٌ","bc":"layudkhilannahum mudkhalay yarḍaunah, wa innallāha la'alīmun ḥalīm","at":"Sungguh, Dia (Allah) pasti akan memasukkan mereka ke tempat masuk (surga) yang mereka sukai. Dan sesungguhnya Allah Maha Mengetahui, Maha Penyantun."},{"id":"60","ar":"۞ ذٰلِكَ وَمَنْ عَاقَبَ بِمِثْلِ مَا عُوْقِبَ بِهٖ ثُمَّ بُغِيَ عَلَيْهِ لَيَنْصُرَنَّهُ اللّٰهُ ۗاِنَّ اللّٰهَ لَعَفُوٌّ غَفُوْرٌ ","bc":"żālika wa man 'āqaba bimiṡli mā 'ụqiba bihī ṡumma bugiya 'alaihi layanṣurannahullāh, innallāha la'afuwwun gafụr","at":"Demikianlah, dan barangsiapa membalas seimbang dengan (kezaliman) penganiayaan yang pernah dia derita kemudian dia dizalimi (lagi), pasti Allah akan menolongnya. Sungguh, Allah Maha Pemaaf, Maha Pengampun."},{"id":"61","ar":"ذٰلِكَ بِاَنَّ اللّٰهَ يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِ وَاَنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ ","bc":"żālika bi`annallāha yụlijul-laila fin-nahāri wa yụlijun-nahāra fil-laili wa annallāha samī'um baṣīr","at":"Demikianlah karena Allah (kuasa) memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan sungguh, Allah Maha Mendengar, Maha Melihat."},{"id":"62","ar":"ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُوْنَ مِنْ دُوْنِهٖ هُوَ الْبَاطِلُ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَبِيْرُ ","bc":"żālika bi`annallāha huwal-ḥaqqu wa anna mā yad'ụna min dụnihī huwal-bāṭilu wa annallāha huwal-'aliyyul-kabīr","at":"Demikianlah (kebesaran Allah) karena Allah, Dialah (Tuhan) Yang Hak. Dan apa saja yang mereka seru selain Dia, itulah yang batil, dan sungguh Allah, Dialah Yang Mahatinggi, Mahabesar."},{"id":"63","ar":"اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۖ فَتُصْبِحُ الْاَرْضُ مُخْضَرَّةًۗ اِنَّ اللّٰهَ لَطِيْفٌ خَبِيْرٌ ۚ","bc":"a lam tara annallāha anzala minas-samā`i mā`an fa tuṣbiḥul-arḍu mukhḍarrah, innallāha laṭīfun khabīr","at":"Tidakkah engkau memperhatikan, bahwa Allah menurunkan air (hujan) dari langit, sehingga bumi menjadi hijau? Sungguh, Allah Mahahalus, Maha Mengetahui."},{"id":"64","ar":"لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَاِنَّ اللّٰهَ لَهُوَ الْغَنِيُّ الْحَمِيْدُ","bc":"lahụ mā fis-samāwāti wa mā fil-arḍ, wa innallāha lahuwal-ganiyyul-ḥamīd","at":"Milik-Nyalah apa yang ada di langit dan apa yang ada di bumi. Dan Allah benar-benar Mahakaya, Maha Terpuji."},{"id":"65","ar":"اَلَمْ تَرَ اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَّا فِى الْاَرْضِ وَالْفُلْكَ تَجْرِيْ فِى الْبَحْرِ بِاَمْرِهٖۗ وَيُمْسِكُ السَّمَاۤءَ اَنْ تَقَعَ عَلَى الْاَرْضِ اِلَّا بِاِذْنِهٖۗ اِنَّ اللّٰهَ بِالنَّاسِ لَرَءُوْفٌ رَّحِيْمٌ ","bc":"a lam tara annallāha sakhkhara lakum mā fil-arḍi wal-fulka tajrī fil-baḥri bi`amrih, wa yumsikus-samā`a an taqa'a 'alal-arḍi illā bi`iżnih, innallāha bin-nāsi lara`ụfur raḥīm","at":"Tidakkah engkau memperhatikan bahwa Allah menundukkan bagimu (manusia) apa yang ada di bumi dan kapal yang berlayar di lautan dengan perintah-Nya. Dan Dia menahan (benda-benda) langit agar tidak jatuh ke bumi, melainkan dengan izin-Nya? Sungguh, Allah Maha Pengasih, Maha Penyayang kepada manusia."},{"id":"66","ar":"وَهُوَ الَّذِيْٓ اَحْيَاكُمْ ۖ ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْۗ اِنَّ الْاِنْسَانَ لَكَفُوْرٌ ","bc":"wa huwallażī aḥyākum ṡumma yumītukum ṡumma yuḥyīkum, innal-insāna lakafụr","at":"Dan Dialah yang menghidupkan kamu, kemudian mematikan kamu, kemudian menghidupkan kamu kembali (pada hari kebangkitan). Sungguh, manusia itu sangat kufur nikmat."},{"id":"67","ar":"لِكُلِّ اُمَّةٍ جَعَلْنَا مَنْسَكًا هُمْ نَاسِكُوْهُ فَلَا يُنَازِعُنَّكَ فِى الْاَمْرِ وَادْعُ اِلٰى رَبِّكَۗ اِنَّكَ لَعَلٰى هُدًى مُّسْتَقِيْمٍ ","bc":"likulli ummatin ja'alnā mansakan hum nāsikụhu fa lā yunāzi'unnaka fil-amri wad'u ilā rabbik, innaka la'alā hudam mustaqīm","at":"Bagi setiap umat telah Kami tetapkan syariat tertentu yang (harus) mereka amalkan, maka tidak sepantasnya mereka berbantahan dengan engkau dalam urusan (syariat) ini dan serulah (mereka) kepada Tuhanmu. Sungguh, engkau (Muhammad) berada di jalan yang lurus."},{"id":"68","ar":"وَاِنْ جَادَلُوْكَ فَقُلِ اللّٰهُ اَعْلَمُ بِمَا تَعْمَلُوْنَ ","bc":"wa in jādalụka fa qulillāhu a'lamu bimā ta'malụn","at":"Dan jika mereka membantah engkau, maka katakanlah, “Allah lebih tahu tentang apa yang kamu kerjakan.”"},{"id":"69","ar":"اَللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ ","bc":"allāhu yaḥkumu bainakum yaumal-qiyāmati fīmā kuntum fīhi takhtalifụn","at":"Allah akan mengadili di antara kamu pada hari Kiamat tentang apa yang dahulu kamu memperselisihkannya. "},{"id":"70","ar":"اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ يَعْلَمُ مَا فِى السَّمَاۤءِ وَالْاَرْضِۗ اِنَّ ذٰلِكَ فِيْ كِتٰبٍۗ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ ","bc":"a lam ta'lam annallāha ya'lamu mā fis-samā`i wal-arḍ, inna żālika fī kitāb, inna żālika 'alallāhi yasīr","at":"Tidakkah engkau tahu bahwa Allah mengetahui apa yang di langit dan di bumi? Sungguh, yang demikian itu sudah terdapat dalam sebuah Kitab (Lauh Mahfuzh). Sesungguhnya yang demikian itu sangat mudah bagi Allah. "},{"id":"71","ar":"وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا وَّمَا لَيْسَ لَهُمْ بِهٖ عِلْمٌ ۗوَمَا لِلظّٰلِمِيْنَ مِنْ نَّصِيْرٍ ","bc":"wa ya'budụna min dụnillāhi mā lam yunazzil bihī sulṭānaw wa mā laisa lahum bihī 'ilm, wa mā liẓ-ẓālimīna min naṣīr","at":"Dan mereka menyembah selain Allah, tanpa dasar yang jelas tentang itu, dan mereka tidak mempunyai pengetahuan (pula) tentang itu. Bagi orang-orang yang zalim tidak ada seorang penolong pun."},{"id":"72","ar":"وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ تَعْرِفُ فِيْ وُجُوْهِ الَّذِيْنَ كَفَرُوا الْمُنْكَرَۗ يَكَادُوْنَ يَسْطُوْنَ بِالَّذِيْنَ يَتْلُوْنَ عَلَيْهِمْ اٰيٰتِنَاۗ قُلْ اَفَاُنَبِّئُكُمْ بِشَرٍّ مِّنْ ذٰلِكُمْۗ اَلنَّارُۗ وَعَدَهَا اللّٰهُ الَّذِيْنَ كَفَرُوْاۗ وَبِئْسَ الْمَصِيْرُ","bc":"wa iżā tutlā 'alaihim āyātunā bayyinātin ta'rifu fī wujụhillażīna kafarul-mungkar, yakādụna yasṭụna billażīna yatlụna 'alaihim āyātinā, qul a fa unabbi`ukum bisyarrim min żālikum, an-nār, wa'adahallāhullażīna kafarụ, wa bi`sal-maṣīr","at":"Dan apabila dibacakan di hadapan mereka ayat-ayat Kami yang terang, niscaya engkau akan melihat (tanda-tanda) keingkaran pada wajah orang-orang yang kafir itu. Hampir-hampir mereka menyerang orang-orang yang membacakan ayat-ayat Kami kepada mereka. Katakanlah (Muhammad), “Apakah akan aku kabarkan kepada-mu (mengenai sesuatu) yang lebih buruk dari itu, (yaitu) neraka?” Allah telah mengancamkannya (neraka) kepada orang-orang kafir. Dan (neraka itu) seburuk-buruk tempat kembali."},{"id":"73","ar":"يٰٓاَيُّهَا النَّاسُ ضُرِبَ مَثَلٌ فَاسْتَمِعُوْا لَهٗ ۗاِنَّ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ لَنْ يَّخْلُقُوْا ذُبَابًا وَّلَوِ اجْتَمَعُوْا لَهٗ ۗوَاِنْ يَّسْلُبْهُمُ الذُّبَابُ شَيْـًٔا لَّا يَسْتَنْقِذُوْهُ مِنْهُۗ ضَعُفَ الطَّالِبُ وَالْمَطْلُوْبُ","bc":"yā ayyuhan-nāsu ḍuriba maṡalun fastami'ụ lah, innallażīna tad'ụna min dụnillāhi lay yakhluqụ żubābaw wa lawijtama'ụ lah, wa iy yaslub-humuż-żubābu syai`al lā yastangqiżụhu min-h, ḍa'ufaṭ-ṭālibu wal-maṭlụb","at":"Wahai manusia! Telah dibuat suatu perumpamaan. Maka dengarkanlah! Sesungguhnya segala yang kamu seru selain Allah tidak dapat menciptakan seekor lalat pun, walaupun mereka bersatu untuk menciptakannya. Dan jika lalat itu merampas sesuatu dari mereka, mereka tidak akan dapat merebutnya kembali dari lalat itu. Sama lemahnya yang menyembah dan yang disembah."},{"id":"74","ar":"مَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهٖۗ اِنَّ اللّٰهَ لَقَوِيٌّ عَزِيْزٌ ","bc":"mā qadarullāha ḥaqqa qadrih, innallāha laqawiyyun 'azīz","at":"Mereka tidak mengagungkan Allah dengan sebenar-benarnya. Sungguh, Allah Mahakuat, Mahaperkasa."},{"id":"75","ar":"اَللّٰهُ يَصْطَفِيْ مِنَ الْمَلٰۤىِٕكَةِ رُسُلًا وَّمِنَ النَّاسِۗ اِنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ ۚ","bc":"allāhu yaṣṭafī minal-malā`ikati rusulaw wa minan-nās, innallāha samī'um baṣīr","at":"Allah memilih para utusan(-Nya) dari malaikat dan dari manusia. Sesungguhnya Allah Maha Mendengar, Maha Melihat."},{"id":"76","ar":"يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ","bc":"ya'lamu mā baina aidīhim wa mā khalfahum, wa ilallāhi turja'ul-umụr","at":"Dia (Allah) mengetahui apa yang di hadapan mereka dan apa yang di belakang mereka. Dan hanya kepada Allah dikembalikan segala urusan."},{"id":"77","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا ارْكَعُوْا وَاسْجُدُوْا وَاعْبُدُوْا رَبَّكُمْ وَافْعَلُوا الْخَيْرَ لَعَلَّكُمْ تُفْلِحُوْنَ ۚ۩","bc":"yā ayyuhallażīna āmanurka'ụ wasjudụ wa'budụ rabbakum waf'alul-khaira la'allakum tufliḥụn","at":"Wahai orang-orang yang beriman! Rukuklah, sujudlah, dan sembahlah Tuhanmu; dan berbuatlah kebaikan, agar kamu beruntung."},{"id":"78","ar":"وَجَاهِدُوْا فِى اللّٰهِ حَقَّ جِهَادِهٖۗ هُوَ اجْتَبٰىكُمْ وَمَا جَعَلَ عَلَيْكُمْ فِى الدِّيْنِ مِنْ حَرَجٍۗ مِلَّةَ اَبِيْكُمْ اِبْرٰهِيْمَۗ هُوَ سَمّٰىكُمُ الْمُسْلِمِيْنَ ەۙ مِنْ قَبْلُ وَفِيْ هٰذَا لِيَكُوْنَ الرَّسُوْلُ شَهِيْدًا عَلَيْكُمْ وَتَكُوْنُوْا شُهَدَاۤءَ عَلَى النَّاسِۖ فَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاعْتَصِمُوْا بِاللّٰهِ ۗهُوَ مَوْلٰىكُمْۚ فَنِعْمَ الْمَوْلٰى وَنِعْمَ النَّصِيْرُ ۔","bc":"wa jāhidụ fillāhi ḥaqqa jihādih, huwajtabākum wa mā ja'ala 'alaikum fid-dīni min ḥaraj, millata abīkum ibrāhīm, huwa sammākumul-muslimīna ming qablu wa fī hāżā liyakụnar-rasụlu syahīdan 'alaikum wa takụnụ syuhadā`a 'alan-nāsi fa aqīmuṣ-ṣalāta wa ātuz-zakāta wa'taṣimụ billāh, huwa maulākum, fa ni'mal-maulā wa ni'man-naṣīr","at":"Dan berjihadlah kamu di jalan Allah dengan jihad yang sebenar-benarnya. Dia telah memilih kamu, dan Dia tidak menjadikan kesukaran untukmu dalam agama. (Ikutilah) agama nenek moyangmu Ibrahim. Dia (Allah) telah menamakan kamu orang-orang muslim sejak dahulu, dan (begitu pula) dalam (Al-Qur'an) ini, agar Rasul (Muhammad) itu menjadi saksi atas dirimu dan agar kamu semua menjadi saksi atas segenap manusia. Maka laksanakanlah salat; tunaikanlah zakat, dan berpegangteguhlah kepada Allah. Dialah Pelindungmu; Dia sebaik-baik pelindung dan sebaik-baik penolong."}],al_haqqah=[{"id":"1","ar":"اَلْحَاۤقَّةُۙ","bc":"al-ḥāqqah","at":"Hari Kiamat,"},{"id":"2","ar":"مَا الْحَاۤقَّةُ ۚ ","bc":"mal-ḥāqqah","at":"apakah hari Kiamat itu?"},{"id":"3","ar":"وَمَآ اَدْرٰىكَ مَا الْحَاۤقَّةُ ۗ ","bc":"wa mā adrāka mal-ḥāqqah","at":"Dan tahukah kamu apakah hari Kiamat itu?"},{"id":"4","ar":"كَذَّبَتْ ثَمُوْدُ وَعَادٌ ۢبِالْقَارِعَةِ ","bc":"każżabaṡ ṡamụdu wa 'ādum bil-qāri'ah","at":"Kaum Samud, dan ‘Ad telah mendustakan hari Kiamat."},{"id":"5","ar":"فَاَمَّا ثَمُوْدُ فَاُهْلِكُوْا بِالطَّاغِيَةِ ","bc":"fa ammā ṡamụdu fa uhlikụ biṭ-ṭāgiyah","at":"Maka adapun kaum Samud, mereka telah dibinasakan dengan suara yang sangat keras,"},{"id":"6","ar":"وَاَمَّا عَادٌ فَاُهْلِكُوْا بِرِيْحٍ صَرْصَرٍ عَاتِيَةٍۙ","bc":"wa ammā 'ādun fa uhlikụ birīḥin ṣarṣarin 'ātiyah","at":"sedangkan kaum ‘Ad, mereka telah dibinasakan dengan angin topan yang sangat dingin,"},{"id":"7","ar":"سَخَّرَهَا عَلَيْهِمْ سَبْعَ لَيَالٍ وَّثَمٰنِيَةَ اَيَّامٍۙ حُسُوْمًا فَتَرَى الْقَوْمَ فِيْهَا صَرْعٰىۙ كَاَنَّهُمْ اَعْجَازُ نَخْلٍ خَاوِيَةٍۚ","bc":"sakhkharahā 'alaihim sab'a layāliw wa ṡamāniyata ayyāmin ḥusụman fa taral-qauma fīhā ṣar'ā ka`annahum a'jāzu nakhlin khāwiyah","at":"Allah menimpakan angin itu kepada mereka selama tujuh malam delapan hari terus-menerus; maka kamu melihat kaum ‘Ad pada waktu itu mati bergelimpangan seperti batang-batang pohon kurma yang telah kosong (lapuk)."},{"id":"8","ar":"فَهَلْ تَرٰى لَهُمْ مِّنْۢ بَاقِيَةٍ ","bc":"fa hal tarā lahum mim bāqiyah","at":"Maka adakah kamu melihat seorang pun yang masih tersisa di antara mereka?"},{"id":"9","ar":"وَجَاۤءَ فِرْعَوْنُ وَمَنْ قَبْلَهٗ وَالْمُؤْتَفِكٰتُ بِالْخَاطِئَةِۚ","bc":"wa jā`a fir'aunu wa mang qablahụ wal-mu`tafikātu bil-khāṭi`ah","at":"Kemudian datang Fir‘aun dan orang-orang yang sebelumnya dan (penduduk) negeri-negeri yang dijungkirbalikkan karena kesalahan yang besar."},{"id":"10","ar":"فَعَصَوْا رَسُوْلَ رَبِّهِمْ فَاَخَذَهُمْ اَخْذَةً رَّابِيَةً ","bc":"fa 'aṣau rasụla rabbihim fa akhażahum akhżatar rābiyah","at":"Maka mereka mendurhakai utusan Tuhannya, Allah menyiksa mereka dengan siksaan yang sangat keras. "},{"id":"11","ar":"اِنَّا لَمَّا طَغَا الْمَاۤءُ حَمَلْنٰكُمْ فِى الْجَارِيَةِۙ","bc":"innā lammā ṭagal-mā`u ḥamalnākum fil-jāriyah","at":"Sesungguhnya ketika air naik (sampai ke gunung), Kami membawa (nenek moyang) kamu ke dalam kapal, "},{"id":"12","ar":"لِنَجْعَلَهَا لَكُمْ تَذْكِرَةً وَّتَعِيَهَآ اُذُنٌ وَّاعِيَةٌ ","bc":"linaj'alahā lakum tażkirataw wa ta'iyahā użunuw wā'iyah","at":"agar Kami jadikan (peristiwa itu) sebagai peringatan bagi kamu dan agar diperhatikan oleh telinga yang mau mendengar. "},{"id":"13","ar":"فَاِذَا نُفِخَ فِى الصُّوْرِ نَفْخَةٌ وَّاحِدَةٌ ۙ","bc":"fa iżā nufikha fiṣ-ṣụri nafkhatuw wāḥidah","at":"Maka apabila sangkakala ditiup sekali tiup,"},{"id":"14","ar":"وَّحُمِلَتِ الْاَرْضُ وَالْجِبَالُ فَدُكَّتَا دَكَّةً وَّاحِدَةًۙ","bc":"wa ḥumilatil-arḍu wal-jibālu fa dukkatā dakkataw wāḥidah","at":"dan diangkatlah bumi dan gunung-gunung, lalu dibenturkan keduanya sekali benturan."},{"id":"15","ar":"فَيَوْمَىِٕذٍ وَّقَعَتِ الْوَاقِعَةُۙ","bc":"fa yauma`iżiw waqa'atil-wāqi'ah","at":"Maka pada hari itu terjadilah hari Kiamat, "},{"id":"16","ar":"وَانْشَقَّتِ السَّمَاۤءُ فَهِيَ يَوْمَىِٕذٍ وَّاهِيَةٌۙ","bc":"wansyaqqatis-samā`u fa hiya yauma`iżiw wāhiyah","at":"dan terbelahlah langit, karena pada hari itu langit menjadi rapuh."},{"id":"17","ar":"وَّالْمَلَكُ عَلٰٓى اَرْجَاۤىِٕهَاۗ وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَىِٕذٍ ثَمٰنِيَةٌ ۗ","bc":"wal-malaku 'alā arjā`ihā, wa yaḥmilu 'arsya rabbika fauqahum yauma`iżin ṡamāniyah","at":"Dan para malaikat berada di berbagai penjuru langit. Pada hari itu delapan malaikat menjunjung ‘Arsy (singgasana) Tuhanmu di atas (kepala) mereka. "},{"id":"18","ar":"يَوْمَىِٕذٍ تُعْرَضُوْنَ لَا تَخْفٰى مِنْكُمْ خَافِيَةٌ ","bc":"yauma`iżin tu'raḍụna lā takhfā mingkum khāfiyah","at":"Pada hari itu kamu dihadapkan (kepada Tuhanmu), tidak ada sesuatu pun dari kamu yang tersembunyi (bagi Allah). "},{"id":"19","ar":"فَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖ فَيَقُوْلُ هَاۤؤُمُ اقْرَءُوْا كِتٰبِيَهْۚ ","bc":"fa ammā man ụtiya kitābahụ biyamīnihī fa yaqụlu hā`umuqra`ụ kitābiyah","at":"Adapun orang yang kitabnya diberikan di tangan kanannya, maka dia berkata, “Ambillah, bacalah kitabku (ini).” "},{"id":"20","ar":"اِنِّيْ ظَنَنْتُ اَنِّيْ مُلٰقٍ حِسَابِيَهْۚ","bc":"innī ẓanantu annī mulāqin ḥisābiyah","at":"Sesungguhnya aku yakin, bahwa (suatu saat) aku akan menerima perhitungan terhadap diriku. "},{"id":"21","ar":"فَهُوَ فِيْ عِيْشَةٍ رَّاضِيَةٍۚ","bc":"fa huwa fī 'īsyatir rāḍiyah","at":"Maka orang itu berada dalam kehidupan yang diridai,"},{"id":"22","ar":"فِيْ جَنَّةٍ عَالِيَةٍۙ","bc":"fī jannatin 'āliyah","at":"dalam surga yang tinggi, "},{"id":"23","ar":"قُطُوْفُهَا دَانِيَةٌ ","bc":"quṭụfuhā dāniyah","at":"buah-buahannya dekat,"},{"id":"24","ar":"كُلُوْا وَاشْرَبُوْا هَنِيْۤـًٔا ۢبِمَآ اَسْلَفْتُمْ فِى الْاَيَّامِ الْخَالِيَةِ ","bc":"kulụ wasyrabụ hanī`am bimā aslaftum fil-ayyāmil-khāliyah","at":"(kepada mereka dikatakan), “Makan dan minumlah dengan nikmat karena amal yang telah kamu kerjakan pada hari-hari yang telah lalu.” "},{"id":"25","ar":"وَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ بِشِمَالِهٖ ەۙ فَيَقُوْلُ يٰلَيْتَنِيْ لَمْ اُوْتَ كِتٰبِيَهْۚ","bc":"wa ammā man ụtiya kitābahụ bisyimālihī fa yaqụlu yā laitanī lam ụta kitābiyah","at":"Dan adapun orang yang kitabnya diberikan di tangan kirinya, maka dia berkata, “Alangkah baiknya jika kitabku (ini) tidak diberikan kepadaku."},{"id":"26","ar":"وَلَمْ اَدْرِ مَا حِسَابِيَهْۚ","bc":"wa lam adri mā ḥisābiyah","at":"Sehingga aku tidak mengetahui bagaimana perhitunganku. "},{"id":"27","ar":"يٰلَيْتَهَا كَانَتِ الْقَاضِيَةَۚ","bc":"yā laitahā kānatil-qāḍiyah","at":"Wahai, kiranya (kematian) itulah yang menyudahi segala sesuatu. "},{"id":"28","ar":"مَآ اَغْنٰى عَنِّيْ مَالِيَهْۚ ","bc":"mā agnā 'annī māliyah","at":"Hartaku sama sekali tidak berguna bagiku. "},{"id":"29","ar":"هَلَكَ عَنِّيْ سُلْطٰنِيَهْۚ","bc":"halaka 'annī sulṭāniyah","at":"Kekuasaanku telah hilang dariku.”"},{"id":"30","ar":"خُذُوْهُ فَغُلُّوْهُۙ","bc":"khużụhu fa gullụh","at":"(Allah berfirman), “Tangkaplah dia lalu belenggulah tangannya ke lehernya.” "},{"id":"31","ar":"ثُمَّ الْجَحِيْمَ صَلُّوْهُۙ","bc":"ṡummal-jaḥīma ṣallụh","at":"Kemudian masukkanlah dia ke dalam api neraka yang menyala-nyala. "},{"id":"32","ar":"ثُمَّ فِيْ سِلْسِلَةٍ ذَرْعُهَا سَبْعُوْنَ ذِرَاعًا فَاسْلُكُوْهُۗ","bc":"ṡumma fī silsilatin żar'uhā sab'ụna żirā'an faslukụh","at":"Kemudian belitlah dia dengan rantai yang panjangnya tujuh puluh hasta. "},{"id":"33","ar":"اِنَّهٗ كَانَ لَا يُؤْمِنُ بِاللّٰهِ الْعَظِيْمِۙ","bc":"innahụ kāna lā yu`minu billāhil-'aẓīm","at":"Sesungguhnya dialah orang yang tidak beriman kepada Allah Yang Mahabesar. "},{"id":"34","ar":"وَلَا يَحُضُّ عَلٰى طَعَامِ الْمِسْكِيْنِۗ","bc":"wa lā yaḥuḍḍu 'alā ṭa'āmil-miskīn","at":"Dan juga dia tidak mendorong (orang lain) untuk memberi makan orang miskin. "},{"id":"35","ar":"فَلَيْسَ لَهُ الْيَوْمَ هٰهُنَا حَمِيْمٌۙ","bc":"fa laisa lahul-yauma hāhunā ḥamīm","at":"Maka pada hari ini di sini tidak ada seorang teman pun baginya. "},{"id":"36","ar":"وَّلَا طَعَامٌ اِلَّا مِنْ غِسْلِيْنٍۙ","bc":"wa lā ṭa'āmun illā min gislīn","at":"Dan tidak ada makanan (baginya) kecuali dari darah dan nanah. "},{"id":"37","ar":"لَّا يَأْكُلُهٗٓ اِلَّا الْخَاطِـُٔوْنَ ","bc":"lā ya`kuluhū illal-khāṭi`ụn","at":"Tidak ada yang memakannya kecuali orang-orang yang berdosa. "},{"id":"38","ar":"فَلَآ اُقْسِمُ بِمَا تُبْصِرُوْنَۙ","bc":"fa lā uqsimu bimā tubṣirụn","at":"Maka Aku bersumpah demi apa yang kamu lihat, "},{"id":"39","ar":"وَمَا لَا تُبْصِرُوْنَۙ","bc":"wa mā lā tubṣirụn","at":"dan demi apa yang tidak kamu lihat."},{"id":"40","ar":"اِنَّهٗ لَقَوْلُ رَسُوْلٍ كَرِيْمٍۙ","bc":"innahụ laqaulu rasụling karīm","at":"Sesungguhnya ia (Al-Qur'an) itu benar-benar wahyu (yang diturunkan kepada) Rasul yang mulia,"},{"id":"41","ar":"وَّمَا هُوَ بِقَوْلِ شَاعِرٍۗ قَلِيْلًا مَّا تُؤْمِنُوْنَۙ","bc":"wa mā huwa biqauli syā'ir, qalīlam mā tu`minụn","at":"dan ia (Al-Qur'an) bukanlah perkataan seorang penyair. Sedikit sekali kamu beriman kepadanya. "},{"id":"42","ar":"وَلَا بِقَوْلِ كَاهِنٍۗ قَلِيْلًا مَّا تَذَكَّرُوْنَۗ","bc":"wa lā biqauli kāhin, qalīlam mā tażakkarụn","at":"Dan bukan pula perkataan tukang tenung. Sedikit sekali kamu mengambil pelajaran darinya. "},{"id":"43","ar":"تَنْزِيْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ ","bc":"tanzīlum mir rabbil-'ālamīn","at":"Ia (Al-Qur'an) adalah wahyu yang diturunkan dari Tuhan seluruh alam. "},{"id":"44","ar":"وَلَوْ تَقَوَّلَ عَلَيْنَا بَعْضَ الْاَقَاوِيْلِۙ","bc":"walau taqawwala 'alainā ba'ḍal-aqāwīl","at":"Dan sekiranya dia (Muhammad) mengada-adakan sebagian perkataan atas (nama) Kami, "},{"id":"45","ar":"لَاَخَذْنَا مِنْهُ بِالْيَمِيْنِۙ","bc":"la`akhażnā min-hu bil-yamīn","at":"pasti Kami pegang dia pada tangan kanannya. "},{"id":"46","ar":"ثُمَّ لَقَطَعْنَا مِنْهُ الْوَتِيْنَۖ","bc":"ṡumma laqaṭa'nā min-hul-watīn","at":"Kemudian Kami potong pembuluh jantungnya. "},{"id":"47","ar":"فَمَا مِنْكُمْ مِّنْ اَحَدٍ عَنْهُ حَاجِزِيْنَۙ","bc":"fa mā mingkum min aḥadin 'an-hu ḥājizīn","at":"Maka tidak seorang pun dari kamu yang dapat menghalangi (Kami untuk menghukumnya). "},{"id":"48","ar":"وَاِنَّهٗ لَتَذْكِرَةٌ لِّلْمُتَّقِيْنَ ","bc":"wa innahụ latażkiratul lil-muttaqīn","at":"Dan sungguh, (Al-Qur'an) itu pelajaran bagi orang-orang yang bertakwa. "},{"id":"49","ar":"وَاِنَّا لَنَعْلَمُ اَنَّ مِنْكُمْ مُّكَذِّبِيْنَۗ","bc":"wa innā lana'lamu anna mingkum mukażżibīn","at":"Dan sungguh, Kami mengetahui bahwa di antara kamu ada orang yang mendustakan."},{"id":"50","ar":"وَاِنَّهٗ لَحَسْرَةٌ عَلَى الْكٰفِرِيْنَۚ","bc":"wa innahụ laḥasratun 'alal-kāfirīn","at":"Dan sungguh, (Al-Qur'an) itu akan menimbulkan penyesalan bagi orang-orang kafir (di akhirat)."},{"id":"51","ar":"وَاِنَّهٗ لَحَقُّ الْيَقِيْنِ ","bc":"wa innahụ laḥaqqul-yaqīn","at":"Dan Sungguh, (Al-Qur'an) itu kebenaran yang meyakinkan. "},{"id":"52","ar":"فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيْمِ","bc":"fa sabbiḥ bismi rabbikal-'aẓīm","at":"Maka bertasbihlah dengan (menyebut) nama Tuhanmu Yang Mahaagung. "}],al_hasyr=[{"id":"1","ar":"سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ","bc":"sabbaḥa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa huwal-'azīzul-ḥakīm","at":"Apa yang ada di langit dan apa yang ada di bumi bertasbih kepada Allah; dan Dialah Yang Mahaperkasa, Mahabijaksana."},{"id":"2","ar":"هُوَ الَّذِيْٓ اَخْرَجَ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ مِنْ دِيَارِهِمْ لِاَوَّلِ الْحَشْرِۗ مَا ظَنَنْتُمْ اَنْ يَّخْرُجُوْا وَظَنُّوْٓا اَنَّهُمْ مَّانِعَتُهُمْ حُصُوْنُهُمْ مِّنَ اللّٰهِ فَاَتٰىهُمُ اللّٰهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوْا وَقَذَفَ فِيْ قُلُوْبِهِمُ الرُّعْبَ يُخْرِبُوْنَ بُيُوْتَهُمْ بِاَيْدِيْهِمْ وَاَيْدِى الْمُؤْمِنِيْنَۙ فَاعْتَبِرُوْا يٰٓاُولِى الْاَبْصَارِ ","bc":"huwallażī akhrajallażīna kafarụ min ahlil-kitābi min diyārihim li`awwalil-ḥasyr, mā ẓanantum ay yakhrujụ wa ẓannū annahum māni'atuhum ḥuṣụnuhum minallāhi fa atāhumullāhu min ḥaiṡu lam yaḥtasibụ wa qażafa fī qulụbihimur-ru'ba yukhribụna buyụtahum bi`aidīhim wa aidil-mu`minīna fa'tabirụ yā ulil-abṣār","at":"Dialah yang mengeluarkan orang-orang kafir di antara Ahli Kitab dari kampung halamannya pada saat pengusiran yang pertama. Kamu tidak menyangka, bahwa mereka akan keluar dan mereka pun yakin, benteng-benteng mereka akan dapat mempertahankan mereka dari (siksaan) Allah; maka Allah mendatangkan (siksaan) kepada mereka dari arah yang tidak mereka sangka-sangka. Dan Allah menanamkan rasa takut ke dalam hati mereka; sehingga memusnahkan rumah-rumah mereka dengan tangannya sendiri dan tangan orang-orang mukmin. Maka ambillah (kejadian itu) untuk menjadi pelajaran, wahai orang-orang yang mempunyai pandangan!"},{"id":"3","ar":"وَلَوْلَآ اَنْ كَتَبَ اللّٰهُ عَلَيْهِمُ الْجَلَاۤءَ لَعَذَّبَهُمْ فِى الدُّنْيَاۗ وَلَهُمْ فِى الْاٰخِرَةِ عَذَابُ النَّارِ ","bc":"walau lā ang kataballāhu 'alaihimul-jalā`a la'ażżabahum fid-dun-yā, wa lahum fil-ākhirati 'ażābun-nār","at":"Dan sekiranya tidak karena Allah telah menetapkan pengusiran terhadap mereka, pasti Allah mengazab mereka di dunia. Dan di akhirat mereka akan mendapat azab neraka. "},{"id":"4","ar":"ذٰلِكَ بِاَنَّهُمْ شَاۤقُّوا اللّٰهَ وَرَسُوْلَهٗ ۖوَمَنْ يُّشَاۤقِّ اللّٰهَ فَاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ","bc":"żālika bi`annahum syāqqullāha wa rasụlahụ wa may yusyāqqillāha fa innallāha syadīdul-'iqāb","at":"Yang demikian itu karena sesungguhnya mereka menentang Allah dan Rasul-Nya. Barangsiapa menentang Allah, maka sesungguhnya Allah sangat keras hukuman-Nya. "},{"id":"5","ar":"مَا قَطَعْتُمْ مِّنْ لِّيْنَةٍ اَوْ تَرَكْتُمُوْهَا قَاۤىِٕمَةً عَلٰٓى اُصُوْلِهَا فَبِاِذْنِ اللّٰهِ وَلِيُخْزِيَ الْفٰسِقِيْنَ ","bc":"mā qaṭa'tum mil līnatin au taraktumụhā qā`imatan 'alā uṣụlihā fa bi`iżnillāhi wa liyukhziyal-fāsiqīn","at":"Apa yang kamu tebang di antara pohon kurma (milik orang-orang kafir) atau yang kamu biarkan (tumbuh) berdiri di atas pokoknya, maka (itu terjadi) dengan izin Allah; dan karena Dia hendak memberikan kehinaan kepada orang-orang fasik."},{"id":"6","ar":"وَمَآ اَفَاۤءَ اللّٰهُ عَلٰى رَسُوْلِهٖ مِنْهُمْ فَمَآ اَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَّلَا رِكَابٍ وَّلٰكِنَّ اللّٰهَ يُسَلِّطُ رُسُلَهٗ عَلٰى مَنْ يَّشَاۤءُۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"wa mā afā`allāhu 'alā rasụlihī min-hum fa mā aujaftum 'alaihi min khailiw wa lā rikābiw wa lākinnallāha yusalliṭu rusulahụ 'alā may yasyā`, wallāhu 'alā kulli syai`ing qadīr","at":"Dan harta rampasan fai' dari mereka yang diberikan Allah kepada Rasul-Nya, kamu tidak memerlukan kuda atau unta untuk mendapatkannya, tetapi Allah memberikan kekuasaan kepada rasul-rasul-Nya terhadap siapa yang Dia kehendaki. Dan Allah Mahakuasa atas segala sesuatu."},{"id":"7","ar":"مَآ اَفَاۤءَ اللّٰهُ عَلٰى رَسُوْلِهٖ مِنْ اَهْلِ الْقُرٰى فَلِلّٰهِ وَلِلرَّسُوْلِ وَلِذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَابْنِ السَّبِيْلِۙ كَيْ لَا يَكُوْنَ دُوْلَةً ۢ بَيْنَ الْاَغْنِيَاۤءِ مِنْكُمْۗ وَمَآ اٰتٰىكُمُ الرَّسُوْلُ فَخُذُوْهُ وَمَا نَهٰىكُمْ عَنْهُ فَانْتَهُوْاۚ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِۘ ","bc":"mā afā`allāhu 'alā rasụlihī min ahlil-qurā fa lillāhi wa lir-rasụli wa liżil-qurbā wal-yatāmā wal-masākīni wabnis-sabīli kai lā yakụna dụlatam bainal-agniyā`i mingkum, wa mā ātākumur-rasụlu fa khużụhu wa mā nahākum 'an-hu fantahụ, wattaqullāh, innallāha syadīdul-'iqāb","at":"Harta rampasan (fai') dari mereka yang diberikan Allah kepada Rasul-Nya (yang berasal) dari penduduk beberapa negeri, adalah untuk Allah, Rasul, kerabat (Rasul), anak-anak yatim, orang-orang miskin dan untuk orang-orang yang dalam perjalanan, agar harta itu jangan hanya beredar di antara orang-orang kaya saja di antara kamu. Apa yang diberikan Rasul kepadamu maka terimalah. Dan apa yang dilarangnya bagimu maka tinggalkanlah. Dan bertakwalah kepada Allah. Sungguh, Allah sangat keras hukuman-Nya. "},{"id":"8","ar":"لِلْفُقَرَاۤءِ الْمُهٰجِرِيْنَ الَّذِيْنَ اُخْرِجُوْا مِنْ دِيَارِهِمْ وَاَمْوَالِهِمْ يَبْتَغُوْنَ فَضْلًا مِّنَ اللّٰهِ وَرِضْوَانًا وَّيَنْصُرُوْنَ اللّٰهَ وَرَسُوْلَهٗ ۗ اُولٰۤىِٕكَ هُمُ الصّٰدِقُوْنَۚ ","bc":"lil-fuqarā`il-muhājirīnallażīna ukhrijụ min diyārihim wa amwālihim yabtagụna faḍlam minallāhi wa riḍwānaw wa yanṣurụnallāha wa rasụlah, ulā`ika humuṣ-ṣādiqụn","at":"(Harta rampasan itu juga) untuk orang-orang fakir yang berhijrah yang terusir dari kampung halamannya dan meninggalkan harta bendanya demi mencari karunia dari Allah dan keridaan(-Nya) dan (demi) menolong (agama) Allah dan Rasul-Nya. Mereka itulah orang-orang yang benar. "},{"id":"9","ar":"وَالَّذِيْنَ تَبَوَّءُو الدَّارَ وَالْاِيْمَانَ مِنْ قَبْلِهِمْ يُحِبُّوْنَ مَنْ هَاجَرَ اِلَيْهِمْ وَلَا يَجِدُوْنَ فِيْ صُدُوْرِهِمْ حَاجَةً مِّمَّآ اُوْتُوْا وَيُؤْثِرُوْنَ عَلٰٓى اَنْفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٌ ۗوَمَنْ يُّوْقَ شُحَّ نَفْسِهٖ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَۚ ","bc":"wallażīna tabawwa`ud-dāra wal-īmāna ming qablihim yuḥibbụna man hājara ilaihim wa lā yajidụna fī ṣudụrihim ḥājatam mimmā ụtụ wa yu`ṡirụna 'alā anfusihim walau kāna bihim khaṣāṣah, wa may yụqa syuḥḥa nafsihī fa ulā`ika humul-mufliḥụn","at":"Dan orang-orang (Ansar) yang telah menempati kota Madinah dan telah beriman sebelum (kedatangan) mereka (Muhajirin), mereka mencintai orang yang berhijrah ke tempat mereka. Dan mereka tidak menaruh keinginan dalam hati mereka terhadap apa yang diberikan kepada mereka (Muhajirin); dan mereka mengutamakan (Muhajirin), atas dirinya sendiri, meskipun mereka juga memerlukan. Dan siapa yang dijaga dirinya dari kekikiran, maka mereka itulah orang-orang yang beruntung. "},{"id":"10","ar":"وَالَّذِيْنَ جَاۤءُوْ مِنْۢ بَعْدِهِمْ يَقُوْلُوْنَ رَبَّنَا اغْفِرْ لَنَا وَلِاِخْوَانِنَا الَّذِيْنَ سَبَقُوْنَا بِالْاِيْمَانِ وَلَا تَجْعَلْ فِيْ قُلُوْبِنَا غِلًّا لِّلَّذِيْنَ اٰمَنُوْا رَبَّنَآ اِنَّكَ رَءُوْفٌ رَّحِيْمٌ ","bc":"wallażīna jā`ụ mim ba'dihim yaqụlụna rabbanagfir lanā wa li`ikhwāninallażīna sabaqụnā bil-īmāni wa lā taj'al fī qulụbinā gillal lillażīna āmanụ rabbanā innaka ra`ụfur raḥīm","at":"Dan orang-orang yang datang sesudah mereka (Muhajirin dan Ansar), mereka berdoa, “Ya Tuhan kami, ampunilah kami dan saudara-saudara kami yang telah beriman lebih dahulu dari kami, dan janganlah Engkau tanamkan kedengkian dalam hati kami terhadap orang-orang yang beriman. Ya Tuhan kami, Sungguh, Engkau Maha Penyantun, Maha Penyayang.” "},{"id":"11","ar":"۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ نَافَقُوْا يَقُوْلُوْنَ لِاِخْوَانِهِمُ الَّذِيْنَ كَفَرُوْا مِنْ اَهْلِ الْكِتٰبِ لَىِٕنْ اُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُطِيْعُ فِيْكُمْ اَحَدًا اَبَدًاۙ وَّاِنْ قُوْتِلْتُمْ لَنَنْصُرَنَّكُمْۗ وَاللّٰهُ يَشْهَدُ اِنَّهُمْ لَكٰذِبُوْنَ ","bc":"a lam tara ilallażīna nāfaqụ yaqụlụna li`ikhwānihimullażīna kafarụ min ahlil-kitābi la`in ukhrijtum lanakhrujanna ma'akum wa lā nuṭī'u fīkum aḥadan abadaw wa ing qụtiltum lananṣurannakum, wallāhu yasy-hadu innahum lakāżibụn","at":"Tidakkah engkau memperhatikan orang-orang munafik yang berkata kepada saudara-saudaranya yang kafir di antara Ahli Kitab, “Sungguh, jika kamu diusir niscaya kami pun akan keluar bersama kamu; dan kami selama-lamanya tidak akan patuh kepada siapa pun demi kamu, dan jika kamu diperangi pasti kami akan membantumu.” Dan Allah menyaksikan, bahwa mereka benar-benar pendusta. "},{"id":"12","ar":"لَىِٕنْ اُخْرِجُوْا لَا يَخْرُجُوْنَ مَعَهُمْۚ وَلَىِٕنْ قُوْتِلُوْا لَا يَنْصُرُوْنَهُمْۚ وَلَىِٕنْ نَّصَرُوْهُمْ لَيُوَلُّنَّ الْاَدْبَارَۙ ثُمَّ لَا يُنْصَرُوْنَ ","bc":"la`in ukhrijụ lā yakhrujụna ma'ahum, wa la`ing qụtilụ lā yanṣurụnahum, wa la`in naṣarụhum layuwallunnal-adbāra ṡumma lā yunṣarụn","at":"Sungguh, jika mereka diusir, orang-orang munafik itu tidak akan keluar bersama mereka, dan jika mereka di-perangi; mereka (juga) tidak akan menolongnya; dan kalau pun mereka menolongnya pastilah mereka akan berpaling lari ke belakang, kemudian mereka tidak akan mendapat pertolongan. "},{"id":"13","ar":"لَاَنْتُمْ اَشَدُّ رَهْبَةً فِيْ صُدُوْرِهِمْ مِّنَ اللّٰهِ ۗذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَّا يَفْقَهُوْنَ ","bc":"la`antum asyaddu rahbatan fī ṣudụrihim minallāh, żālika bi`annahum qaumul lā yafqahụn","at":"Sesungguhnya dalam hati mereka, kamu (Muslimin) lebih ditakuti daripada Allah. Yang demikian itu karena mereka orang-orang yang tidak mengerti."},{"id":"14","ar":"لَا يُقَاتِلُوْنَكُمْ جَمِيْعًا اِلَّا فِيْ قُرًى مُّحَصَّنَةٍ اَوْ مِنْ وَّرَاۤءِ جُدُرٍۗ بَأْسُهُمْ بَيْنَهُمْ شَدِيْدٌ ۗ تَحْسَبُهُمْ جَمِيْعًا وَّقُلُوْبُهُمْ شَتّٰىۗ ذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَّا يَعْقِلُوْنَۚ ","bc":"lā yuqātilụnakum jamī'an illā fī quram muḥaṣṣanatin au miw warā`i judur, ba`suhum bainahum syadīd, taḥsabuhum jamī'aw wa qulụbuhum syattā, żālika bi`annahum qaumul lā ya'qilụn","at":"Mereka tidak akan memerangi kamu (secara) bersama-sama, kecuali di negeri-negeri yang berbenteng atau di balik tembok. Permusuhan antara sesama mereka sangat hebat. Kamu kira mereka itu bersatu padahal hati mereka terpecah belah. Yang demikian itu karena mereka orang-orang yang tidak mengerti. "},{"id":"15","ar":"كَمَثَلِ الَّذِيْنَ مِنْ قَبْلِهِمْ قَرِيْبًا ذَاقُوْا وَبَالَ اَمْرِهِمْۚ وَلَهُمْ عَذَابٌ اَلِيْمٌۚ ","bc":"kamaṡalillażīna ming qablihim qarīban żāqụ wa bāla amrihim, wa lahum 'ażābun alīm","at":"(Mereka) seperti orang-orang yang sebelum mereka (Yahudi) belum lama berselang, telah merasakan akibat buruk (terusir) disebabkan perbuatan mereka sendiri. Dan mereka akan men-dapat azab yang pedih. "},{"id":"16","ar":"كَمَثَلِ الشَّيْطٰنِ اِذْ قَالَ لِلْاِنْسَانِ اكْفُرْۚ فَلَمَّا كَفَرَ قَالَ اِنِّيْ بَرِيْۤءٌ مِّنْكَ اِنِّيْٓ اَخَافُ اللّٰهَ رَبَّ الْعٰلَمِيْنَ ","bc":"kamaṡalisy-syaiṭāni iż qāla lil-insānikfur, fa lammā kafara qāla innī barī`um mingka innī akhāfullāha rabbal-'ālamīn","at":"(Bujukan orang-orang munafik itu) seperti (bujukan) setan ketika ia berkata kepada manusia, “Kafirlah kamu!” Kemudian ketika manusia itu menjadi kafir ia berkata, “Sesungguhnya aku berlepas diri dari kamu, karena sesungguhnya aku takut kepada Allah, Tuhan seluruh alam.” "},{"id":"17","ar":"فَكَانَ عَاقِبَتَهُمَآ اَنَّهُمَا فِى النَّارِ خَالِدَيْنِ فِيْهَاۗ وَذٰلِكَ جَزٰۤؤُا الظّٰلِمِيْنَ","bc":"fa kāna 'āqibatahumā annahumā fin-nāri khālidaini fīhā, wa żālika jazā`uẓ-ẓālimīn","at":"Maka kesudahan bagi keduanya, bahwa keduanya masuk ke dalam neraka, kekal di dalamnya. Demikianlah balasan bagi orang-orang zalim. "},{"id":"18","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَلْتَنْظُرْ نَفْسٌ مَّا قَدَّمَتْ لِغَدٍۚ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ خَبِيْرٌ ۢبِمَا تَعْمَلُوْنَ ","bc":"yā ayyuhallażīna āmanuttaqullāha waltanẓur nafsum mā qaddamat ligad, wattaqullāh, innallāha khabīrum bimā ta'malụn","at":"Wahai orang-orang yang beriman! Bertakwalah kepada Allah dan hendaklah setiap orang memperhatikan apa yang telah diperbuatnya untuk hari esok (akhirat), dan bertakwalah kepada Allah. Sungguh, Allah Mahateliti terhadap apa yang kamu kerjakan. "},{"id":"19","ar":"وَلَا تَكُوْنُوْا كَالَّذِيْنَ نَسُوا اللّٰهَ فَاَنْسٰىهُمْ اَنْفُسَهُمْۗ اُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ ","bc":"wa lā takụnụ kallażīna nasullāha fa ansāhum anfusahum, ulā`ika humul-fāsiqụn","at":"Dan janganlah kamu seperti orang-orang yang lupa kepada Allah, sehingga Allah menjadikan mereka lupa akan diri sendiri. Mereka itulah orang-orang fasik. "},{"id":"20","ar":"لَا يَسْتَوِيْٓ اَصْحٰبُ النَّارِ وَاَصْحٰبُ الْجَنَّةِۗ اَصْحٰبُ الْجَنَّةِ هُمُ الْفَاۤىِٕزُوْنَ ","bc":"lā yastawī aṣ-ḥābun-nāri wa aṣ-ḥābul-jannah, aṣ-ḥābul-jannati humul-fā`izụn","at":"Tidak sama para penghuni neraka dengan para penghuni surga; para penghuni surga itulah orang-orang yang memperoleh kemenangan."},{"id":"21","ar":"لَوْ اَنْزَلْنَا هٰذَا الْقُرْاٰنَ عَلٰى جَبَلٍ لَّرَاَيْتَهٗ خَاشِعًا مُّتَصَدِّعًا مِّنْ خَشْيَةِ اللّٰهِ ۗوَتِلْكَ الْاَمْثَالُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُوْنَ ","bc":"lau anzalnā hāżal-qur`āna 'alā jabalil lara`aitahụ khāsyi'am mutaṣaddi'am min khasy-yatillāh, wa tilkal-amṡālu naḍribuhā lin-nāsi la'allahum yatafakkarụn","at":"Sekiranya Kami turunkan Al-Qur'an ini kepada sebuah gunung, pasti kamu akan melihatnya tunduk terpecah belah disebabkan takut kepada Allah. Dan perumpamaan-perumpamaan itu Kami buat untuk manusia agar mereka berpikir."},{"id":"22","ar":"هُوَ اللّٰهُ الَّذِيْ لَآ اِلٰهَ اِلَّا هُوَۚ عَالِمُ الْغَيْبِ وَالشَّهَادَةِۚ هُوَ الرَّحْمٰنُ الرَّحِيْمُ ","bc":"huwallāhullażī lā ilāha illā huw, 'ālimul-gaibi wasy-syahādah, huwar-raḥmānur-raḥīm","at":"Dialah Allah tidak ada tuhan selain Dia. Yang Mengetahui yang gaib dan yang nyata, Dialah Yang Maha Pengasih, Maha Penyayang. "},{"id":"23","ar":"هُوَ اللّٰهُ الَّذِيْ لَآ اِلٰهَ اِلَّا هُوَ ۚ اَلْمَلِكُ الْقُدُّوْسُ السَّلٰمُ الْمُؤْمِنُ الْمُهَيْمِنُ الْعَزِيْزُ الْجَبَّارُ الْمُتَكَبِّرُۗ سُبْحٰنَ اللّٰهِ عَمَّا يُشْرِكُوْنَ","bc":"huwallāhullażī lā ilāha illā huw, al-malikul-quddụsus-salāmul-mu`minul-muhaiminul-'azīzul-jabbārul-mutakabbir, sub-ḥānallāhi 'ammā yusyrikụn","at":"Dialah Allah tidak ada tuhan selain Dia. Maharaja, Yang Mahasuci, Yang Mahasejahtera, Yang Menjaga Keamanan, Pemelihara Keselamatan, Yang Mahaperkasa, Yang Mahakuasa, Yang Memiliki Segala Keagungan, Mahasuci Allah dari apa yang mereka persekutukan."},{"id":"24","ar":"هُوَ اللّٰهُ الْخَالِقُ الْبَارِئُ الْمُصَوِّرُ لَهُ الْاَسْمَاۤءُ الْحُسْنٰىۗ يُسَبِّحُ لَهٗ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ","bc":"huwallāhul-khāliqul-bāri`ul-muṣawwiru lahul-asmā`ul-ḥusnā, yusabbiḥu lahụ mā fis-samāwāti wal-arḍ, wa huwal-'azīzul-ḥakīm","at":"Dialah Allah Yang Menciptakan, Yang Mengadakan, Yang Membentuk Rupa, Dia memiliki nama-nama yang indah. Apa yang di langit dan di bumi bertasbih kepada-Nya. Dan Dialah Yang Mahaperkasa, Mahabijaksana."}],al_hijr=[{"id":"1","ar":"الۤرٰ ۗتِلْكَ اٰيٰتُ الْكِتٰبِ وَقُرْاٰنٍ مُّبِيْنٍ ۔","bc":"alif lām rā, tilka āyātul-kitābi wa qur`ānim mubīn","at":"Alif Lam Ra. (Surah) ini adalah (sebagian dari) ayat-ayat Kitab (yang sempurna) yaitu (ayat-ayat) Al-Qur'an yang memberi penjelasan."},{"id":"2","ar":"رُبَمَا يَوَدُّ الَّذِيْنَ كَفَرُوْا لَوْ كَانُوْا مُسْلِمِيْنَ ","bc":"rubamā yawaddullażīna kafarụ lau kānụ muslimīn","at":"Orang kafir itu kadang-kadang (nanti di akhirat) menginginkan, sekiranya mereka dahulu (di dunia) menjadi orang Muslim."},{"id":"3","ar":"ذَرْهُمْ يَأْكُلُوْا وَيَتَمَتَّعُوْا وَيُلْهِهِمُ الْاَمَلُ فَسَوْفَ يَعْلَمُوْنَ ","bc":"żar-hum ya`kulụ wa yatamatta'ụ wa yul-hihimul-amalu fa saufa ya'lamụn","at":"Biarkanlah mereka (di dunia ini) makan dan bersenang-senang dan dilalaikan oleh angan-angan (kosong) mereka, kelak mereka akan mengetahui (akibat perbuatannya)."},{"id":"4","ar":"وَمَآ اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا وَلَهَا كِتَابٌ مَّعْلُوْمٌ ","bc":"wa mā ahlaknā ming qaryatin illā wa lahā kitābum ma'lụm","at":"Dan Kami tidak membinasakan suatu negeri, melainkan sudah ada ketentuan yang ditetapkan baginya."},{"id":"5","ar":"مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُوْنَ ","bc":"mā tasbiqu min ummatin ajalahā wa mā yasta`khirụn","at":"Tidak ada suatu umat pun yang dapat mendahului ajalnya, dan tidak (pula) dapat meminta penundaan(nya)."},{"id":"6","ar":"وَقَالُوْا يٰٓاَيُّهَا الَّذِيْ نُزِّلَ عَلَيْهِ الذِّكْرُ اِنَّكَ لَمَجْنُوْنٌ ۗ ","bc":"wa qālụ yā ayyuhallażī nuzzila 'alaihiż-żikru innaka lamajnụn","at":"Dan mereka berkata, “Wahai orang yang kepadanya diturunkan Al-Qur'an, sesungguhnya engkau (Muhammad) benar-benar orang gila."},{"id":"7","ar":"لَوْمَا تَأْتِيْنَا بِالْمَلٰۤىِٕكَةِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ","bc":"lau mā ta`tīnā bil-malā`ikati ing kunta minaṣ-ṣādiqīn","at":"Mengapa engkau tidak mendatangkan malaikat kepada kami, jika engkau termasuk orang yang benar?”"},{"id":"8","ar":"مَا نُنَزِّلُ الْمَلٰۤىِٕكَةَ اِلَّا بِالْحَقِّ وَمَا كَانُوْٓا اِذًا مُّنْظَرِيْنَ ","bc":"mā nunazzilul-malā`ikata illā bil-ḥaqqi wa mā kānū iżam munẓarīn","at":"Kami tidak menurunkan malaikat melainkan dengan kebenaran (untuk membawa azab) dan mereka ketika itu tidak diberikan penangguhan"},{"id":"9","ar":"اِنَّا نَحْنُ نَزَّلْنَا الذِّكْرَ وَاِنَّا لَهٗ لَحٰفِظُوْنَ ","bc":"innā naḥnu nazzalnaż-żikra wa innā lahụ laḥāfiẓụn","at":"Sesungguhnya Kamilah yang menurunkan Al-Qur'an, dan pasti Kami (pula) yang memeliharanya."},{"id":"10","ar":"وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ فِيْ شِيَعِ الْاَوَّلِيْنَ ","bc":"wa laqad arsalnā ming qablika fī syiya'il-awwalīn","at":"Dan sungguh, Kami telah mengutus (beberapa rasul) sebelum engkau (Muhammad) kepada umat-umat terdahulu."},{"id":"11","ar":"وَمَا يَأْتِيْهِمْ مِّنْ رَّسُوْلٍ اِلَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ","bc":"wa mā ya`tīhim mir rasụlin illā kānụ bihī yastahzi`ụn","at":"Dan setiap kali seorang rasul datang kepada mereka, mereka selalu memperolok-olokannya."},{"id":"12","ar":"كَذٰلِكَ نَسْلُكُهٗ فِيْ قُلُوْبِ الْمُجْرِمِيْنَۙ","bc":"każālika naslukuhụ fī qulụbil-mujrimīn","at":"Demikianlah, Kami memasukkannya (olok-olok itu) ke dalam hati orang yang berdosa,"},{"id":"13","ar":"لَا يُؤْمِنُوْنَ بِهٖ وَقَدْ خَلَتْ سُنَّةُ الْاَوَّلِيْنَ ","bc":"lā yu`minụna bihī wa qad khalat sunnatul-awwalīn","at":"mereka tidak beriman kepadanya (Al-Qur'an) padahal telah berlalu sunatullah terhadap orang-orang terdahulu."},{"id":"14","ar":"وَلَوْ فَتَحْنَا عَلَيْهِمْ بَابًا مِّنَ السَّمَاۤءِ فَظَلُّوْا فِيْهِ يَعْرُجُوْنَۙ","bc":"walau fataḥnā 'alaihim bābam minas-samā`i fa ẓallụ fīhi ya'rujụn","at":"Dan kalau Kami bukakan kepada mereka salah satu pintu langit, lalu mereka terus menerus naik ke atasnya,"},{"id":"15","ar":"لَقَالُوْٓا اِنَّمَا سُكِّرَتْ اَبْصَارُنَا بَلْ نَحْنُ قَوْمٌ مَّسْحُوْرُوْنَ ","bc":"laqālū innamā sukkirat abṣārunā bal naḥnu qaumum mas-ḥụrụn","at":"tentulah mereka berkata, “Sesungguhnya pandangan kamilah yang dikaburkan, bahkan kami adalah orang yang terkena sihir.” "},{"id":"16","ar":"وَلَقَدْ جَعَلْنَا فِى السَّمَاۤءِ بُرُوْجًا وَّزَيَّنّٰهَا لِلنّٰظِرِيْنَۙ","bc":"wa laqad ja'alnā fis-samā`i burụjaw wa zayyannāhā lin-nāẓirīn","at":"Dan sungguh, Kami telah menciptakan gugusan bintang di langit dan men-jadikannya terasa indah bagi orang yang memandang(nya),"},{"id":"17","ar":"وَحَفِظْنٰهَا مِنْ كُلِّ شَيْطٰنٍ رَّجِيْمٍۙ","bc":"wa ḥafiẓnāhā ming kulli syaiṭānir rajīm","at":"dan Kami menjaganya dari setiap (gangguan) setan yang terkutuk,"},{"id":"18","ar":"اِلَّا مَنِ اسْتَرَقَ السَّمْعَ فَاَتْبَعَهٗ شِهَابٌ مُّبِيْنٌ ","bc":"illā manistaraqas-sam'a fa atba'ahụ syihābum mubīn","at":"kecuali (setan) yang mencuri-curi (berita) yang dapat didengar (dari malaikat) lalu dikejar oleh semburan api yang terang."},{"id":"19","ar":"وَالْاَرْضَ مَدَدْنٰهَا وَاَلْقَيْنَا فِيْهَا رَوَاسِيَ وَاَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ شَيْءٍ مَّوْزُوْنٍ","bc":"wal-arḍa madadnāhā wa alqainā fīhā rawāsiya wa ambatnā fīhā ming kulli syai`im mauzụn","at":"Dan Kami telah menghamparkan bumi dan Kami pancangkan padanya gunung-gunung serta Kami tumbuhkan di sana segala sesuatu menurut ukuran."},{"id":"20","ar":"وَجَعَلْنَا لَكُمْ فِيْهَا مَعَايِشَ وَمَنْ لَّسْتُمْ لَهٗ بِرَازِقِيْنَ ","bc":"wa ja'alnā lakum fīhā ma'āyisya wa mal lastum lahụ birāziqīn","at":"Dan Kami telah menjadikan padanya sumber-sumber kehidupan untuk keperluanmu, dan (Kami ciptakan pula) makhluk-makhluk yang bukan kamu pemberi rezekinya."},{"id":"21","ar":"وَاِنْ مِّنْ شَيْءٍ اِلَّا عِنْدَنَا خَزَاۤىِٕنُهٗ وَمَا نُنَزِّلُهٗٓ اِلَّا بِقَدَرٍ مَّعْلُوْمٍ ","bc":"wa im min syai`in illā 'indanā khazā`inuhụ wa mā nunazziluhū illā biqadarim ma'lụm","at":"Dan tidak ada sesuatu pun, melainkan pada sisi Kamilah khazanahnya; Kami tidak menurunkannya melainkan dengan ukuran tertentu."},{"id":"22","ar":"وَاَرْسَلْنَا الرِّيٰحَ لَوَاقِحَ فَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً فَاَسْقَيْنٰكُمُوْهُۚ وَمَآ اَنْتُمْ لَهٗ بِخَازِنِيْنَ","bc":"wa arsalnar-riyāḥa lawāqiḥa fa anzalnā minas-samā`i mā`an fa asqainākumụh, wa mā antum lahụ bikhāzinīn","at":"Dan Kami telah meniupkan angin untuk mengawinkan dan Kami turunkan hujan dari langit, lalu Kami beri minum kamu dengan (air) itu, dan bukanlah kamu yang menyimpannya."},{"id":"23","ar":"وَاِنَّا لَنَحْنُ نُحْيٖ وَنُمِيْتُ وَنَحْنُ الْوَارِثُوْنَ ","bc":"wa innā lanaḥnu nuḥyī wa numītu wa naḥnul-wāriṡụn","at":"Dan sungguh, Kamilah yang menghidupkan dan mematikan dan Kami (pulalah) yang mewarisi."},{"id":"24","ar":"وَلَقَدْ عَلِمْنَا الْمُسْتَقْدِمِيْنَ مِنْكُمْ وَلَقَدْ عَلِمْنَا الْمُسْتَأْخِرِيْنَ ","bc":"wa laqad 'alimnal-mustaqdimīna mingkum wa laqad 'alimnal-musta`khirīn","at":"Dan sungguh, Kami mengetahui orang yang terdahulu sebelum kamu dan Kami mengetahui pula orang yang terkemudian."},{"id":"25","ar":"وَاِنَّ رَبَّكَ هُوَ يَحْشُرُهُمْۗ اِنَّهٗ حَكِيْمٌ عَلِيْمٌ ","bc":"wa inna rabbaka huwa yaḥsyuruhum, innahụ ḥakīmun 'alīm","at":"Dan sesungguhnya Tuhanmu, Dialah yang akan mengumpulkan mereka. Sungguh, Dia Mahabijaksana, Maha Mengetahui."},{"id":"26","ar":"وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍۚ ","bc":"wa laqad khalaqnal-insāna min ṣalṣālim min ḥama`im masnụn","at":"Dan sungguh, Kami telah menciptakan manusia (Adam) dari tanah liat kering dari lumpur hitam yang diberi bentuk."},{"id":"27","ar":"وَالْجَاۤنَّ خَلَقْنٰهُ مِنْ قَبْلُ مِنْ نَّارِ السَّمُوْمِ ","bc":"wal-jānna khalaqnāhu ming qablu min nāris-samụm","at":"Dan Kami telah menciptakan jin sebelum (Adam) dari api yang sangat panas."},{"id":"28","ar":"وَاِذْ قَالَ رَبُّكَ لِلْمَلٰۤىِٕكَةِ اِنِّيْ خَالِقٌۢ بَشَرًا مِّنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍۚ ","bc":"wa iż qāla rabbuka lil-malā`ikati innī khāliqum basyaram min ṣalṣālim min ḥama`im masnụn","at":"Dan (ingatlah), ketika Tuhanmu berfirman kepada para malaikat, “Sungguh, Aku akan menciptakan seorang manusia dari tanah liat kering dari lumpur hitam yang diberi bentuk."},{"id":"29","ar":"فَاِذَا سَوَّيْتُهٗ وَنَفَخْتُ فِيْهِ مِنْ رُّوْحِيْ فَقَعُوْا لَهٗ سٰجِدِيْنَ ","bc":"fa iżā sawwaituhụ wa nafakhtu fīhi mir rụḥī faqa'ụ lahụ sājidīn","at":"Maka apabila Aku telah menyempurnakan (kejadian)nya, dan Aku telah meniupkan roh (ciptaan)-Ku ke dalamnya, maka tunduklah kamu kepadanya dengan bersujud.”"},{"id":"30","ar":"فَسَجَدَ الْمَلٰۤىِٕكَةُ كُلُّهُمْ اَجْمَعُوْنَۙ","bc":"fa sajadal-malā`ikatu kulluhum ajma'ụn","at":"Maka bersujudlah para malaikat itu semuanya bersama-sama,"},{"id":"31","ar":"اِلَّآ اِبْلِيْسَۗ اَبٰىٓ اَنْ يَّكُوْنَ مَعَ السّٰجِدِيْنَ ","bc":"illā iblīs, abā ay yakụna ma'as-sājidīn","at":"kecuali Iblis. Ia enggan ikut bersama-sama para (malaikat) yang sujud itu."},{"id":"32","ar":"قَالَ يٰٓاِبْلِيْسُ مَا لَكَ اَلَّا تَكُوْنَ مَعَ السّٰجِدِيْنَ ","bc":"qāla yā iblīsu mā laka allā takụna ma'as-sājidīn","at":"Dia (Allah) berfirman, “Wahai Iblis! Apa sebabnya kamu (tidak ikut) sujud bersama mereka?”"},{"id":"33","ar":"قَالَ لَمْ اَكُنْ لِّاَسْجُدَ لِبَشَرٍ خَلَقْتَهٗ مِنْ صَلْصَالٍ مِّنْ حَمَاٍ مَّسْنُوْنٍ ","bc":"qāla lam akul li`asjuda libasyarin khalaqtahụ min ṣalṣālim min ḥama`im masnụn","at":"Ia (Iblis) berkata, “Aku sekali-kali tidak akan sujud kepada manusia yang Engkau telah menciptakannya dari tanah liat kering dari lumpur hitam yang diberi bentuk.”"},{"id":"34","ar":"قَالَ فَاخْرُجْ مِنْهَا فَاِنَّكَ رَجِيْمٌۙ","bc":"qāla fakhruj min-hā fa innaka rajīm","at":"Dia (Allah) berfirman, “(Kalau begitu) keluarlah dari surga, karena sesungguhnya kamu terkutuk,"},{"id":"35","ar":"وَّاِنَّ عَلَيْكَ اللَّعْنَةَ اِلٰى يَوْمِ الدِّيْنِ ","bc":"wa inna 'alaikal-la'nata ilā yaumid-dīn","at":" dan sesungguhnya kutukan itu tetap menimpamu sampai hari Kiamat.” "},{"id":"36","ar":"قَالَ رَبِّ فَاَنْظِرْنِيْٓ اِلٰى يَوْمِ يُبْعَثُوْنَ ","bc":"qāla rabbi fa anẓirnī ilā yaumi yub'aṡụn","at":"Ia (Iblis) berkata, “Ya Tuhanku, (kalau begitu) maka berilah penangguhan kepadaku sampai hari (manusia) dibangkitkan.”"},{"id":"37","ar":"قَالَ فَاِنَّكَ مِنَ الْمُنْظَرِيْنَۙ","bc":"qāla fa innaka minal-munẓarīn","at":" Allah berfirman, “(Baiklah) maka sesungguhnya kamu termasuk yang diberi penangguhan,"},{"id":"38","ar":"اِلٰى يَوْمِ الْوَقْتِ الْمَعْلُوْمِ ","bc":"ilā yaumil-waqtil-ma'lụm","at":"sampai hari yang telah ditentukan (kiamat).”"},{"id":"39","ar":"قَالَ رَبِّ بِمَآ اَغْوَيْتَنِيْ لَاُزَيِّنَنَّ لَهُمْ فِى الْاَرْضِ وَلَاُغْوِيَنَّهُمْ اَجْمَعِيْنَۙ ","bc":"qāla rabbi bimā agwaitanī la`uzayyinanna lahum fil-arḍi wa la`ugwiyannahum ajma'īn","at":"Ia (Iblis) berkata, “Tuhanku, oleh karena Engkau telah memutuskan bahwa aku sesat, aku pasti akan jadikan (kejahatan) terasa indah bagi mereka di bumi, dan aku akan menyesatkan mereka semuanya,"},{"id":"40","ar":"اِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَصِيْنَ ","bc":"illā 'ibādaka min-humul-mukhlaṣīn","at":" kecuali hamba-hamba-Mu yang terpilih di antara mereka.”"},{"id":"41","ar":"قَالَ هٰذَا صِرَاطٌ عَلَيَّ مُسْتَقِيْمٌ ","bc":"qāla hāżā ṣirāṭun 'alayya mustaqīm","at":"Dia (Allah) berfirman, “Ini adalah jalan yang lurus (menuju) kepada-Ku.”"},{"id":"42","ar":"اِنَّ عِبَادِيْ لَيْسَ لَكَ عَلَيْهِمْ سُلْطٰنٌ اِلَّا مَنِ اتَّبَعَكَ مِنَ الْغٰوِيْنَ ","bc":"inna 'ibādī laisa laka 'alaihim sulṭānun illā manittaba'aka minal-gāwīn","at":"Sesungguhnya kamu (Iblis) tidak kuasa atas hamba-hamba-Ku, kecuali mereka yang mengikutimu, yaitu orang yang sesat."},{"id":"43","ar":"وَاِنَّ جَهَنَّمَ لَمَوْعِدُهُمْ اَجْمَعِيْنَۙ","bc":"wa inna jahannama lamau'iduhum ajma'īn","at":"Dan sungguh, Jahanam itu benar-benar (tempat) yang telah dijanjikan untuk mereka (pengikut setan) semuanya."},{"id":"44","ar":"لَهَا سَبْعَةُ اَبْوَابٍۗ لِكُلِّ بَابٍ مِّنْهُمْ جُزْءٌ مَّقْسُوْمٌ ","bc":"lahā sab'atu abwāb, likulli bābim min-hum juz`um maqsụm","at":"(Jahanam) itu mempunyai tujuh pintu. Setiap pintu (telah ditetapkan) untuk golongan tertentu dari mereka."},{"id":"45","ar":"اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّعُيُوْنٍۗ ","bc":"innal-muttaqīna fī jannātiw wa 'uyụn","at":"Sesungguhnya orang yang bertakwa itu berada dalam surga-surga (taman-taman), dan (di dekat) mata air (yang mengalir)."},{"id":"46","ar":"اُدْخُلُوْهَا بِسَلٰمٍ اٰمِنِيْنَ ","bc":"udkhulụhā bisalāmin āminīn","at":"(Allah berfirman), “Masuklah ke dalamnya dengan sejahtera dan aman.”"},{"id":"47","ar":"وَنَزَعْنَا مَا فِيْ صُدُوْرِهِمْ مِّنْ غِلٍّ اِخْوَانًا عَلٰى سُرُرٍ مُّتَقٰبِلِيْنَ ","bc":"wa naza'nā mā fī ṣudụrihim min gillin ikhwānan 'alā sururim mutaqābilīn","at":"Dan Kami lenyapkan segala rasa dendam yang ada dalam hati mereka; mereka merasa bersaudara, duduk berhadap-hadapan di atas dipan-dipan."},{"id":"48","ar":"لَا يَمَسُّهُمْ فِيْهَا نَصَبٌ وَّمَا هُمْ مِّنْهَا بِمُخْرَجِيْنَ ","bc":"lā yamassuhum fīhā naṣabuw wa mā hum min-hā bimukhrajīn","at":"Mereka tidak merasa lelah di dalamnya dan mereka tidak akan dikeluarkan darinya."},{"id":"49","ar":"۞ نَبِّئْ عِبَادِيْٓ اَنِّيْٓ اَنَا الْغَفُوْرُ الرَّحِيْمُۙ","bc":"nabbi` 'ibādī annī anal-gafụrur-raḥīm","at":"Kabarkanlah kepada hamba-hamba-Ku, bahwa Akulah Yang Maha Pengampun, Maha Penyayang,"},{"id":"50","ar":"وَاَنَّ عَذَابِيْ هُوَ الْعَذَابُ الْاَلِيْمُ ","bc":"wa anna 'ażābī huwal-'ażābul-alīm","at":"dan sesungguhnya azab-Ku adalah azab yang sangat pedih."},{"id":"51","ar":"وَنَبِّئْهُمْ عَنْ ضَيْفِ اِبْرٰهِيْمَۘ ","bc":"wa nabbi`hum 'an ḍaifi ibrāhīm","at":"Dan kabarkanlah (Muhammad) kepada mereka tentang tamu Ibrahim (malaikat)."},{"id":"52","ar":"اِذْ دَخَلُوْا عَلَيْهِ فَقَالُوْا سَلٰمًاۗ قَالَ اِنَّا مِنْكُمْ وَجِلُوْنَ ","bc":"iż dakhalụ 'alaihi fa qālụ salāmā, qāla innā mingkum wajilụn","at":"Ketika mereka masuk ke tempatnya, lalu mereka mengucapkan, “Salam.” Dia (Ibrahim) berkata, “Kami benar-benar merasa takut kepadamu.” "},{"id":"53","ar":"قَالُوْا لَا تَوْجَلْ اِنَّا نُبَشِّرُكَ بِغُلٰمٍ عَلِيْمٍ ","bc":"qālụ lā taujal innā nubasysyiruka bigulāmin 'alīm","at":"(Mereka) berkata, “Janganlah engkau merasa takut, sesungguhnya kami memberi kabar gembira kepadamu dengan (kelahiran seorang) anak laki-laki (yang akan menjadi) orang yang pandai (Ishak).” "},{"id":"54","ar":"قَالَ اَبَشَّرْتُمُوْنِيْ عَلٰٓى اَنْ مَّسَّنِيَ الْكِبَرُ فَبِمَ تُبَشِّرُوْنَ ","bc":"qāla a basysyartumụnī 'alā am massaniyal-kibaru fa bima tubasysyirụn","at":"Dia (Ibrahim) berkata, “Benarkah kamu memberi kabar gembira kepadaku padahal usiaku telah lanjut, lalu (dengan cara) bagaimana kamu memberi (kabar gembira) tersebut?”"},{"id":"55","ar":"قَالُوْا بَشَّرْنٰكَ بِالْحَقِّ فَلَا تَكُنْ مِّنَ الْقٰنِطِيْنَ ","bc":"qālụ basysyarnāka bil-ḥaqqi fa lā takum minal-qāniṭīn","at":"(Mereka) menjawab, “Kami menyampaikan kabar gembira kepadamu dengan benar, maka janganlah engkau termasuk orang yang berputus asa.”"},{"id":"56","ar":"قَالَ وَمَنْ يَّقْنَطُ مِنْ رَّحْمَةِ رَبِّهٖٓ اِلَّا الضَّاۤلُّوْنَ ","bc":"qāla wa may yaqnaṭu mir raḥmati rabbihī illaḍ-ḍāllụn","at":"Dia (Ibrahim) berkata, “Tidak ada yang berputus asa dari rahmat Tuhannya, kecuali orang yang sesat.”"},{"id":"57","ar":"قَالَ فَمَا خَطْبُكُمْ اَيُّهَا الْمُرْسَلُوْنَ ","bc":"qāla fa mā khaṭbukum ayyuhal-mursalụn","at":" Dia (Ibrahim) berkata, “Apakah urusanmu yang penting, wahai para utusan?”"},{"id":"58","ar":"قَالُوْٓا اِنَّآ اُرْسِلْنَآ اِلٰى قَوْمٍ مُّجْرِمِيْنَۙ","bc":"qālū innā ursilnā ilā qaumim mujrimīn","at":"(Mereka) menjawab, “Sesungguhnya kami diutus kepada kaum yang berdosa,"},{"id":"59","ar":"اِلَّآ اٰلَ لُوْطٍۗ اِنَّا لَمُنَجُّوْهُمْ اَجْمَعِيْنَۙ","bc":"illā āla lụṭ, innā lamunajjụhum ajma'īn","at":"kecuali para pengikut Lut. Sesungguhnya kami pasti menyelamatkan mereka semuanya,"},{"id":"60","ar":"اِلَّا امْرَاَتَهٗ قَدَّرْنَآ اِنَّهَا لَمِنَ الْغٰبِرِيْنَ ","bc":"illamra`atahụ qaddarnā innahā laminal-gābirīn","at":"kecuali istrinya, kami telah menentukan, bahwa dia termasuk orang yang tertinggal (bersama orang kafir lainnya).”"},{"id":"61","ar":"فَلَمَّا جَاۤءَ اٰلَ لُوْطِ ِۨالْمُرْسَلُوْنَۙ","bc":"fa lammā jā`a āla lụṭinil-mursalụn","at":"Maka ketika utusan itu datang kepada para pengikut Lut,"},{"id":"62","ar":"قَالَ اِنَّكُمْ قَوْمٌ مُّنْكَرُوْنَ ","bc":"qāla innakum qaumum mungkarụn","at":"dia (Lut) berkata, “Sesungguhnya kamu orang yang tidak kami kenal.” "},{"id":"63","ar":"قَالُوْا بَلْ جِئْنٰكَ بِمَا كَانُوْا فِيْهِ يَمْتَرُوْنَ ","bc":"qālụ bal ji`nāka bimā kānụ fīhi yamtarụn","at":"(Para utusan) menjawab, “Sebenarnya kami ini datang kepadamu membawa azab yang selalu mereka dustakan."},{"id":"64","ar":"وَاَتَيْنٰكَ بِالْحَقِّ وَاِنَّا لَصٰدِقُوْنَ ","bc":"wa ataināka bil-ḥaqqi wa innā laṣādiqụn","at":"Dan kami datang kepadamu membawa kebenaran dan sungguh, kami orang yang benar."},{"id":"65","ar":"فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِّنَ الَّيْلِ وَاتَّبِعْ اَدْبَارَهُمْ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ وَّامْضُوْا حَيْثُ تُؤْمَرُوْنَ ","bc":"fa asri bi`ahlika biqiṭ'im minal-laili wattabi' adbārahum wa lā yaltafit mingkum aḥaduw wamḍụ ḥaiṡu tu`marụn","at":"Maka pergilah kamu pada akhir malam beserta keluargamu, dan ikutilah mereka dari belakang. Jangan ada di antara kamu yang menoleh ke belakang dan teruskanlah perjalanan ke tempat yang diperintahkan kepadamu.” "},{"id":"66","ar":"وَقَضَيْنَآ اِلَيْهِ ذٰلِكَ الْاَمْرَ اَنَّ دَابِرَ هٰٓؤُلَاۤءِ مَقْطُوْعٌ مُّصْبِحِيْنَ ","bc":"wa qaḍainā ilaihi żālikal-amra anna dābira hā`ulā`i maqṭụ'um muṣbiḥīn","at":"Dan telah Kami tetapkan kepadanya (Lut) keputusan itu, bahwa akhirnya mereka akan ditumpas habis pada waktu subuh."},{"id":"67","ar":"وَجَاۤءَ اَهْلُ الْمَدِيْنَةِ يَسْتَبْشِرُوْنَ ","bc":"wa jā`a ahlul-madīnati yastabsyirụn","at":"Dan datanglah penduduk kota itu (ke rumah Lut) dengan gembira (karena kedatangan tamu itu)."},{"id":"68","ar":"قَالَ اِنَّ هٰٓؤُلَاۤءِ ضَيْفِيْ فَلَا تَفْضَحُوْنِۙ","bc":"qāla inna hā`ulā`i ḍaifī fa lā tafḍaḥụn","at":"Dia (Lut) berkata, “Sesungguhnya mereka adalah tamuku; maka jangan kamu mempermalukan aku,"},{"id":"69","ar":"وَاتَّقُوا اللّٰهَ وَلَا تُخْزُوْنِ ","bc":"wattaqullāha wa lā tukhzụn","at":"Dan bertakwalah kepada Allah dan janganlah kamu membuat aku terhina.” "},{"id":"70","ar":"قَالُوْٓا اَوَلَمْ نَنْهَكَ عَنِ الْعٰلَمِيْنَ ","bc":"qālū a wa lam nan-haka 'anil-'ālamīn","at":" (Mereka) berkata, “Bukankah kami telah melarangmu dari (melindungi) manusia?”"},{"id":"71","ar":"قَالَ هٰٓؤُلَاۤءِ بَنٰتِيْٓ اِنْ كُنْتُمْ فٰعِلِيْنَۗ ","bc":"qāla hā`ulā`i banātī ing kuntum fā'ilīn","at":"Dia (Lut) berkata, “Mereka itulah putri-putri (negeri)ku (nikahlah dengan mereka), jika kamu hendak berbuat.”"},{"id":"72","ar":"لَعَمْرُكَ اِنَّهُمْ لَفِيْ سَكْرَتِهِمْ يَعْمَهُوْنَ ","bc":"la'amruka innahum lafī sakratihim ya'mahụn","at":"(Allah berfirman), “Demi umurmu (Muhammad), sungguh, mereka terombang-ambing dalam kemabukan (kesesatan).”"},{"id":"73","ar":"فَاَخَذَتْهُمُ الصَّيْحَةُ مُشْرِقِيْنَۙ","bc":"fa akhażat-humuṣ-ṣaiḥatu musyriqīn","at":"Maka mereka dibinasakan oleh suara keras yang mengguntur, ketika matahari akan terbit."},{"id":"74","ar":"فَجَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهِمْ حِجَارَةً مِّنْ سِجِّيْلٍ ","bc":"fa ja'alnā 'āliyahā sāfilahā wa amṭarnā 'alaihim ḥijāratam min sijjīl","at":"Maka Kami jungkirbalikkan (negeri itu) dan Kami hujani mereka dengan batu dari tanah yang keras."},{"id":"75","ar":"اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّلْمُتَوَسِّمِيْنَۙ","bc":"inna fī żālika la`āyātil lil-mutawassimīn","at":"Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi orang yang memperhatikan tanda-tanda,"},{"id":"76","ar":"وَاِنَّهَا لَبِسَبِيْلٍ مُّقِيْمٍ ","bc":"wa innahā labisabīlim muqīm","at":"dan sungguh, (negeri) itu benar-benar terletak di jalan yang masih tetap (dilalui manusia)."},{"id":"77","ar":"اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّلْمُؤْمِنِيْنَۗ ","bc":"inna fī żālika la`āyatal lil-mu`minīn","at":"Sungguh, pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah) bagi orang yang beriman."},{"id":"78","ar":"وَاِنْ كَانَ اَصْحٰبُ الْاَيْكَةِ لَظٰلِمِيْنَۙ","bc":"wa ing kāna aṣ-ḥābul-aikati laẓālimīn","at":"Dan sesungguhnya penduduk Aikah itu benar-benar kaum yang zalim,"},{"id":"79","ar":"فَانْتَقَمْنَا مِنْهُمْۘ وَاِنَّهُمَا لَبِاِمَامٍ مُّبِيْنٍۗ ","bc":"fantaqamnā min-hum, wa innahumā labi`imāmim mubīn","at":"maka Kami membinasakan mereka. Dan sesungguhnya kedua (negeri) itu terletak di satu jalur jalan raya."},{"id":"80","ar":"وَلَقَدْ كَذَّبَ اَصْحٰبُ الْحِجْرِ الْمُرْسَلِيْنَۙ","bc":"wa laqad każżaba aṣ-ḥābul-ḥijril-mursalīn","at":"Dan sesungguhnya penduduk negeri Hijr benar-benar telah mendustakan para rasul (mereka),"},{"id":"81","ar":"وَاٰتَيْنٰهُمْ اٰيٰتِنَا فَكَانُوْا عَنْهَا مُعْرِضِيْنَۙ","bc":"wa ātaināhum āyātinā fa kānụ 'an-hā mu'riḍīn","at":"dan Kami telah mendatangkan kepada mereka tanda-tanda (kekuasaan) Kami, tetapi mereka selalu berpaling darinya, "},{"id":"82","ar":"وَكَانُوْا يَنْحِتُوْنَ مِنَ الْجِبَالِ بُيُوْتًا اٰمِنِيْنَ ","bc":"wa kānụ yan-ḥitụna minal-jibāli buyụtan āminīn","at":"dan mereka memahat rumah-rumah dari gunung batu, (yang didiami) dengan rasa aman."},{"id":"83","ar":"فَاَخَذَتْهُمُ الصَّيْحَةُ مُصْبِحِيْنَۙ","bc":"fa akhażat-humuṣ-ṣaiḥatu muṣbiḥīn","at":"Kemudian mereka dibinasakan oleh suara keras yang mengguntur pada pagi hari,"},{"id":"84","ar":"فَمَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يَكْسِبُوْنَۗ ","bc":"fa mā agnā 'an-hum mā kānụ yaksibụn","at":"sehingga tidak berguna bagi mereka, apa yang telah mereka usahakan. "},{"id":"85","ar":"وَمَا خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَآ اِلَّا بِالْحَقِّۗ وَاِنَّ السَّاعَةَ لَاٰتِيَةٌ فَاصْفَحِ الصَّفْحَ الْجَمِيْلَ ","bc":"wa mā khalaqnas-samāwāti wal-arḍa wa mā bainahumā illā bil-ḥaqq, wa innas-sā'ata la`ātiyatun faṣfaḥiṣ-ṣaf-ḥal jamīl","at":"Dan Kami tidak menciptakan langit dan bumi serta apa yang ada di antara keduanya, melainkan dengan kebenaran. Dan sungguh, Kiamat pasti akan datang, maka maafkanlah (mereka) dengan cara yang baik."},{"id":"86","ar":"اِنَّ رَبَّكَ هُوَ الْخَلّٰقُ الْعَلِيْمُ ","bc":"inna rabbaka huwal-khallāqul-'alīm","at":"Sungguh, Tuhanmu, Dialah Yang Maha Pencipta, Maha Mengetahui."},{"id":"87","ar":"وَلَقَدْ اٰتَيْنٰكَ سَبْعًا مِّنَ الْمَثَانِيْ وَالْقُرْاٰنَ الْعَظِيْمَ ","bc":"wa laqad ātaināka sab'am minal-maṡānī wal-qur`ānal-'aẓīm","at":"Dan sungguh, Kami telah memberikan kepadamu tujuh (ayat) yang (dibaca) berulang-ulang dan Al-Qur'an yang agung."},{"id":"88","ar":"لَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِهٖٓ اَزْوَاجًا مِّنْهُمْ وَلَا تَحْزَنْ عَلَيْهِمْ وَاخْفِضْ جَنَاحَكَ لِلْمُؤْمِنِيْنَ ","bc":"lā tamuddanna 'ainaika ilā mā matta'nā bihī azwājam min-hum wa lā taḥzan 'alaihim wakhfiḍ janāḥaka lil-mu`minīn","at":"Jangan sekali-kali engkau (Muhammad) tujukan pandanganmu kepada kenikmatan hidup yang telah Kami berikan kepada beberapa golongan di antara mereka (orang kafir), dan jangan engkau bersedih hati terhadap mereka dan berendah hatilah engkau terhadap orang yang beriman."},{"id":"89","ar":"وَقُلْ اِنِّيْٓ اَنَا النَّذِيْرُ الْمُبِيْنُۚ ","bc":"wa qul innī anan-nażīrul-mubīn","at":"Dan katakanlah (Muhammad), “Sesungguhnya aku adalah pemberi peringatan yang jelas.”"},{"id":"90","ar":"كَمَآ اَنْزَلْنَا عَلَى الْمُقْتَسِمِيْنَۙ","bc":"kamā anzalnā 'alal-muqtasimīn","at":"Sebagaimana (Kami telah memberi peringatan), Kami telah menurunkan (azab) kepada orang yang memilah-milah (Kitab Allah),"},{"id":"91","ar":"الَّذِيْنَ جَعَلُوا الْقُرْاٰنَ عِضِيْنَ ","bc":"allażīna ja'alul-qur`āna 'iḍīn","at":"(yaitu) orang-orang yang telah menjadikan Al-Qur'an itu terbagi-bagi."},{"id":"92","ar":"فَوَرَبِّكَ لَنَسْـَٔلَنَّهُمْ اَجْمَعِيْنَۙ","bc":"fa wa rabbika lanas`alannahum ajma'īn","at":"Maka demi Tuhanmu, Kami pasti akan menanyai mereka semua,"},{"id":"93","ar":"عَمَّا كَانُوْا يَعْمَلُوْنَ ","bc":"'ammā kānụ ya'malụn","at":"tentang apa yang telah mereka kerjakan dahulu. "},{"id":"94","ar":"فَاصْدَعْ بِمَا تُؤْمَرُ وَاَعْرِضْ عَنِ الْمُشْرِكِيْنَ ","bc":"faṣda' bimā tu`maru wa a'riḍ 'anil-musyrikīn","at":"Maka sampaikanlah (Muhammad) secara terang-terangan segala apa yang diperintahkan (kepadamu) dan berpalinglah dari orang yang musyrik."},{"id":"95","ar":"اِنَّا كَفَيْنٰكَ الْمُسْتَهْزِءِيْنَۙ","bc":"innā kafainākal-mustahzi`īn","at":"Sesungguhnya Kami memelihara engkau (Muhammad) dari (kejahatan) orang yang memperolok-olokkan (engkau),"},{"id":"96","ar":"الَّذِيْنَ يَجْعَلُوْنَ مَعَ اللّٰهِ اِلٰهًا اٰخَرَۚ فَسَوْفَ يَعْلَمُوْنَ ","bc":"allażīna yaj'alụna ma'allāhi ilāhan ākhar, fa saufa ya'lamụn","at":" (yaitu) orang yang menganggap adanya tuhan selain Allah; mereka kelak akan mengetahui (akibatnya)."},{"id":"97","ar":"وَلَقَدْ نَعْلَمُ اَنَّكَ يَضِيْقُ صَدْرُكَ بِمَا يَقُوْلُوْنَۙ","bc":"wa laqad na'lamu annaka yaḍīqu ṣadruka bimā yaqụlụn","at":"Dan sungguh, Kami mengetahui bahwa dadamu menjadi sempit disebabkan apa yang mereka ucapkan, "},{"id":"98","ar":"فَسَبِّحْ بِحَمْدِ رَبِّكَ وَكُنْ مِّنَ السَّاجِدِيْنَۙ","bc":"fa sabbiḥ biḥamdi rabbika wa kum minas-sājidīn","at":"maka bertasbihlah dengan memuji Tuhanmu dan jadilah engkau di antara orang yang bersujud (salat), "},{"id":"99","ar":"وَاعْبُدْ رَبَّكَ حَتّٰى يَأْتِيَكَ الْيَقِيْنُ","bc":"wa'bud rabbaka ḥattā ya`tiyakal-yaqīn","at":"Dan sembahlah Tuhanmu sampai yakin (ajal) datang kepadamu."}],al_hujurat=[{"id":"1","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُقَدِّمُوْا بَيْنَ يَدَيِ اللّٰهِ وَرَسُوْلِهٖ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ سَمِيْعٌ عَلِيْمٌ ","bc":"yā ayyuhallażīna āmanụ lā tuqaddimụ baina yadayillāhi wa rasụlihī wattaqullāh, innallāha samī'un 'alīm","at":"Wahai orang-orang yang beriman! Janganlah kamu mendahului Allah dan Rasul-Nya dan bertakwalah kepada Allah. Sungguh, Allah Maha Mendengar, Maha Mengetahui."},{"id":"2","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَرْفَعُوْٓا اَصْوَاتَكُمْ فَوْقَ صَوْتِ النَّبِيِّ وَلَا تَجْهَرُوْا لَهٗ بِالْقَوْلِ كَجَهْرِ بَعْضِكُمْ لِبَعْضٍ اَنْ تَحْبَطَ اَعْمَالُكُمْ وَاَنْتُمْ لَا تَشْعُرُوْنَ ","bc":"yā ayyuhallażīna āmanụ lā tarfa'ū aṣwātakum fauqa ṣautin-nabiyyi wa lā taj-harụ lahụ bil-qauli kajahri ba'ḍikum liba'ḍin an taḥbaṭa a'mālukum wa antum lā tasy'urụn","at":"Wahai orang-orang yang beriman! Janganlah kamu meninggikan suaramu melebihi suara Nabi, dan janganlah kamu berkata kepadanya dengan suara keras sebagaimana kerasnya (suara) sebagian kamu terhadap yang lain, nanti (pahala) segala amalmu bisa terhapus sedangkan kamu tidak menyadari. "},{"id":"3","ar":"اِنَّ الَّذِيْنَ يَغُضُّوْنَ اَصْوَاتَهُمْ عِنْدَ رَسُوْلِ اللّٰهِ اُولٰۤىِٕكَ الَّذِيْنَ امْتَحَنَ اللّٰهُ قُلُوْبَهُمْ لِلتَّقْوٰىۗ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ عَظِيْمٌ ","bc":"innallażīna yaguḍḍụna aṣwātahum 'inda rasụlillāhi ulā`ikallażīnamtaḥanallāhu qulụbahum lit-taqwā, lahum magfiratuw wa ajrun 'aẓīm","at":"Sesungguhnya orang-orang yang merendahkan suaranya di sisi Rasulullah, mereka itulah orang-orang yang telah diuji hatinya oleh Allah untuk bertakwa. Mereka akan memperoleh ampunan dan pahala yang besar."},{"id":"4","ar":"اِنَّ الَّذِيْنَ يُنَادُوْنَكَ مِنْ وَّرَاۤءِ الْحُجُرٰتِ اَكْثَرُهُمْ لَا يَعْقِلُوْنَ ","bc":"innallażīna yunādụnaka miw warā`il-ḥujurāti akṡaruhum lā ya'qilụn","at":"Sesungguhnya orang-orang yang memanggil engkau (Muhammad) dari luar kamar(mu) kebanyakan mereka tidak mengerti. "},{"id":"5","ar":"وَلَوْ اَنَّهُمْ صَبَرُوْا حَتّٰى تَخْرُجَ اِلَيْهِمْ لَكَانَ خَيْرًا لَّهُمْ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ","bc":"walau annahum ṣabarụ ḥattā takhruja ilaihim lakāna khairal lahum, wallāhu gafụrur raḥīm","at":"Dan sekiranya mereka bersabar sampai engkau keluar menemui mereka, tentu akan lebih baik bagi mereka. Dan Allah Maha Pengampun, Maha Penyayang. "},{"id":"6","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ جَاۤءَكُمْ فَاسِقٌۢ بِنَبَاٍ فَتَبَيَّنُوْٓا اَنْ تُصِيْبُوْا قَوْمًاۢ بِجَهَالَةٍ فَتُصْبِحُوْا عَلٰى مَا فَعَلْتُمْ نٰدِمِيْنَ ","bc":"yā ayyuhallażīna āmanū in jā`akum fāsiqum binaba`in fa tabayyanū an tuṣībụ qaumam bijahālatin fa tuṣbiḥụ 'alā mā fa'altum nādimīn","at":"Wahai orang-orang yang beriman! Jika seseorang yang fasik datang kepadamu membawa suatu berita, maka telitilah kebenarannya, agar kamu tidak mencelakakan suatu kaum karena kebodohan (kecerobohan), yang akhirnya kamu menyesali perbuatanmu itu. "},{"id":"7","ar":"وَاعْلَمُوْٓا اَنَّ فِيْكُمْ رَسُوْلَ اللّٰهِ ۗ لَوْ يُطِيْعُكُمْ فِيْ كَثِيْرٍ مِّنَ الْاَمْرِ لَعَنِتُّمْ وَلٰكِنَّ اللّٰهَ حَبَّبَ اِلَيْكُمُ الْاِيْمَانَ وَزَيَّنَهٗ فِيْ قُلُوْبِكُمْ وَكَرَّهَ اِلَيْكُمُ الْكُفْرَ وَالْفُسُوْقَ وَالْعِصْيَانَ ۗ اُولٰۤىِٕكَ هُمُ الرَّاشِدُوْنَۙ","bc":"wa'lamū anna fīkum rasụlallāh, lau yuṭī'ukum fī kaṡīrim minal-amri la'anittum wa lākinnallāha ḥabbaba ilaikumul-īmāna wa zayyanahụ fī qulụbikum wa karraha ilaikumul-kufra wal-fusụqa wal-'iṣyān, ulā`ika humur-rāsyidụn","at":"Dan ketahuilah olehmu bahwa di tengah-tengah kamu ada Rasulullah. Kalau dia menuruti (kemauan) kamu dalam banyak hal pasti kamu akan mendapatkan kesusahan. Tetapi Allah menjadikan kamu cinta kepada keimanan dan menjadikan (iman) itu indah dalam hatimu serta menjadikan kamu benci kepada kekafiran, kefasikan, dan kedurhakaan. Mereka itulah orang-orang yang mengikuti jalan yang lurus,"},{"id":"8","ar":"فَضْلًا مِّنَ اللّٰهِ وَنِعْمَةً ۗوَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ","bc":"faḍlam minallāhi wa ni'mah, wallāhu 'alīmun ḥakīm","at":"sebagai karunia dan nikmat dari Allah. Dan Allah Maha Mengetahui, Mahabijaksana."},{"id":"9","ar":"وَاِنْ طَاۤىِٕفَتٰنِ مِنَ الْمُؤْمِنِيْنَ اقْتَتَلُوْا فَاَصْلِحُوْا بَيْنَهُمَاۚ فَاِنْۢ بَغَتْ اِحْدٰىهُمَا عَلَى الْاُخْرٰى فَقَاتِلُوا الَّتِيْ تَبْغِيْ حَتّٰى تَفِيْۤءَ اِلٰٓى اَمْرِ اللّٰهِ ۖفَاِنْ فَاۤءَتْ فَاَصْلِحُوْا بَيْنَهُمَا بِالْعَدْلِ وَاَقْسِطُوْا ۗاِنَّ اللّٰهَ يُحِبُّ الْمُقْسِطِيْنَ ","bc":"wa in ṭā`ifatāni minal-mu`minīnaqtatalụ fa aṣliḥụ bainahumā, fa im bagat iḥdāhumā 'alal-ukhrā fa qātilullatī tabgī ḥattā tafī`a ilā amrillāh, fa in fā`at fa aṣliḥụ bainahumā bil-'adli wa aqsiṭụ, innallāha yuḥibbul-muqsiṭīn","at":"Dan apabila ada dua golongan orang-orang mukmin berperang, maka damaikanlah antara keduanya. Jika salah satu dari keduanya berbuat zalim terhadap (golongan) yang lain, maka perangilah (golongan) yang berbuat zalim itu, sehingga golongan itu kembali kepada perintah Allah. Jika golongan itu telah kembali (kepada perintah Allah), maka damaikanlah antara keduanya dengan adil, dan berlakulah adil. Sungguh, Allah mencintai orang-orang yang berlaku adil. "},{"id":"10","ar":"اِنَّمَا الْمُؤْمِنُوْنَ اِخْوَةٌ فَاَصْلِحُوْا بَيْنَ اَخَوَيْكُمْ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُرْحَمُوْنَ ","bc":"innamal-mu`minụna ikhwatun fa aṣliḥụ baina akhawaikum wattaqullāha la'allakum tur-ḥamụn","at":"Sesungguhnya orang-orang mukmin itu bersaudara, karena itu damaikanlah antara kedua saudaramu (yang berselisih) dan bertakwalah kepada Allah agar kamu mendapat rahmat. "},{"id":"11","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا يَسْخَرْ قَوْمٌ مِّنْ قَوْمٍ عَسٰٓى اَنْ يَّكُوْنُوْا خَيْرًا مِّنْهُمْ وَلَا نِسَاۤءٌ مِّنْ نِّسَاۤءٍ عَسٰٓى اَنْ يَّكُنَّ خَيْرًا مِّنْهُنَّۚ وَلَا تَلْمِزُوْٓا اَنْفُسَكُمْ وَلَا تَنَابَزُوْا بِالْاَلْقَابِۗ بِئْسَ الِاسْمُ الْفُسُوْقُ بَعْدَ الْاِيْمَانِۚ وَمَنْ لَّمْ يَتُبْ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ ","bc":"yā ayyuhallażīna āmanụ lā yaskhar qaumum ming qaumin 'asā ay yakụnụ khairam min-hum wa lā nisā`um min nisā`in 'asā ay yakunna khairam min-hunn, wa lā talmizū anfusakum wa lā tanābazụ bil-alqāb, bi`sa lismul-fusụqu ba'dal-īmān, wa mal lam yatub fa ulā`ika humuẓ-ẓālimụn","at":"Wahai orang-orang yang beriman! Janganlah suatu kaum mengolok-olok kaum yang lain (karena) boleh jadi mereka (yang diperolok-olokkan) lebih baik dari mereka (yang mengolok-olok) dan jangan pula perempuan-perempuan (mengolok-olokkan) perempuan lain (karena) boleh jadi perempuan (yang diperolok-olokkan) lebih baik dari perempuan (yang mengolok-olok). Janganlah kamu saling mencela satu sama lain dan janganlah saling memanggil dengan gelar-gelar yang buruk. Seburuk-buruk panggilan adalah (panggilan) yang buruk (fasik) setelah beriman. Dan barangsiapa tidak bertobat, maka mereka itulah orang-orang yang zalim. "},{"id":"12","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اجْتَنِبُوْا كَثِيْرًا مِّنَ الظَّنِّۖ اِنَّ بَعْضَ الظَّنِّ اِثْمٌ وَّلَا تَجَسَّسُوْا وَلَا يَغْتَبْ بَّعْضُكُمْ بَعْضًاۗ اَيُحِبُّ اَحَدُكُمْ اَنْ يَّأْكُلَ لَحْمَ اَخِيْهِ مَيْتًا فَكَرِهْتُمُوْهُۗ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ تَوَّابٌ رَّحِيْمٌ","bc":"yā ayyuhallażīna āmanujtanibụ kaṡīram minaẓ-ẓanni inna ba'ḍaẓ-ẓanni iṡmuw wa lā tajassasụ wa lā yagtab ba'ḍukum ba'ḍā, a yuḥibbu aḥadukum ay ya`kula laḥma akhīhi maitan fa karihtumụh, wattaqullāh, innallāha tawwābur raḥīm","at":"Wahai orang-orang yang beriman! Jauhilah banyak dari prasangka, sesungguhnya sebagian prasangka itu dosa dan janganlah kamu mencari-cari kesalahan orang lain dan janganlah ada di antara kamu yang menggunjing sebagian yang lain. Apakah ada di antara kamu yang suka memakan daging saudaranya yang sudah mati? Tentu kamu merasa jijik. Dan bertakwalah kepada Allah, sesungguhnya Allah Maha Penerima tobat, Maha Penyayang."},{"id":"13","ar":"يٰٓاَيُّهَا النَّاسُ اِنَّا خَلَقْنٰكُمْ مِّنْ ذَكَرٍ وَّاُنْثٰى وَجَعَلْنٰكُمْ شُعُوْبًا وَّقَبَاۤىِٕلَ لِتَعَارَفُوْا ۚ اِنَّ اَكْرَمَكُمْ عِنْدَ اللّٰهِ اَتْقٰىكُمْ ۗاِنَّ اللّٰهَ عَلِيْمٌ خَبِيْرٌ ","bc":"yā ayyuhan-nāsu innā khalaqnākum min żakariw wa unṡā wa ja'alnākum syu'ụbaw wa qabā`ila lita'ārafụ, inna akramakum 'indallāhi atqākum, innallāha 'alīmun khabīr","at":"Wahai manusia! Sungguh, Kami telah menciptakan kamu dari seorang laki-laki dan seorang perempuan, kemudian Kami jadikan kamu berbangsa-bangsa dan bersuku-suku agar kamu saling mengenal. Sesungguhnya yang paling mulia di antara kamu di sisi Allah ialah orang yang paling bertakwa. Sungguh, Allah Maha Mengetahui, Mahateliti. "},{"id":"14","ar":"۞ قَالَتِ الْاَعْرَابُ اٰمَنَّا ۗ قُلْ لَّمْ تُؤْمِنُوْا وَلٰكِنْ قُوْلُوْٓا اَسْلَمْنَا وَلَمَّا يَدْخُلِ الْاِيْمَانُ فِيْ قُلُوْبِكُمْ ۗوَاِنْ تُطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ لَا يَلِتْكُمْ مِّنْ اَعْمَالِكُمْ شَيْـًٔا ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"qālatil-a'rābu āmannā, qul lam tu`minụ wa lāking qụlū aslamnā wa lammā yadkhulil-īmānu fī qulụbikum, wa in tuṭī'ullāha wa rasụlahụ lā yalitkum min a'mālikum syai`ā, innallāha gafụrur raḥīm","at":"Orang-orang Arab Badui berkata, “Kami telah beriman.” Katakanlah (kepada mereka), “Kamu belum beriman, tetapi katakanlah ‘Kami telah tunduk (Islam),’ karena iman belum masuk ke dalam hatimu. Dan jika kamu taat kepada Allah dan Rasul-Nya, Dia tidak akan mengurangi sedikit pun (pahala) amal perbuatanmu. Sungguh, Allah Maha Pengampun, Maha Penyayang.” "},{"id":"15","ar":"اِنَّمَا الْمُؤْمِنُوْنَ الَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرَسُوْلِهٖ ثُمَّ لَمْ يَرْتَابُوْا وَجَاهَدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ فِيْ سَبِيْلِ اللّٰهِ ۗ اُولٰۤىِٕكَ هُمُ الصّٰدِقُوْنَ ","bc":"innamal-mu`minụnallażīna āmanụ billāhi wa rasụlihī ṡumma lam yartābụ wa jāhadụ bi`amwālihim wa anfusihim fī sabīlillāh, ulā`ika humuṣ-ṣādiqụn","at":"Sesungguhnya orang-orang mukmin yang sebenarnya adalah mereka yang beriman kepada Allah dan Rasul-Nya kemudian mereka tidak ragu-ragu dan mereka berjihad dengan harta dan jiwanya di jalan Allah. Mereka itulah orang-orang yang benar."},{"id":"16","ar":"قُلْ اَتُعَلِّمُوْنَ اللّٰهَ بِدِيْنِكُمْۗ وَاللّٰهُ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ ","bc":"qul a tu'allimụnallāha bidīnikum, wallāhu ya'lamu mā fis-samāwāti wa mā fil-arḍ, wallāhu bikulli syai`in 'alīm","at":"Katakanlah (kepada mereka), “Apakah kamu akan memberitahukan kepada Allah tentang agamamu (keyakinanmu), padahal Allah mengetahui apa yang ada di langit dan apa yang ada di bumi dan Allah Maha Mengetahui segala sesuatu.” "},{"id":"17","ar":"يَمُنُّوْنَ عَلَيْكَ اَنْ اَسْلَمُوْا ۗ قُلْ لَّا تَمُنُّوْا عَلَيَّ اِسْلَامَكُمْ ۚبَلِ اللّٰهُ يَمُنُّ عَلَيْكُمْ اَنْ هَدٰىكُمْ لِلْاِيْمَانِ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"yamunnụna 'alaika an aslamụ, qul lā tamunnụ 'alayya islāmakum, balillāhu yamunnu 'alaikum an hadākum lil-īmāni ing kuntum ṣādiqīn","at":"Mereka merasa berjasa kepadamu dengan keislaman mereka. Katakanlah, “Janganlah kamu merasa berjasa kepadaku dengan keislamanmu, sebenarnya Allah yang melimpahkan nikmat kepadamu dengan menunjukkan kamu kepada keimanan, jika kamu orang yang benar.” "},{"id":"18","ar":"اِنَّ اللّٰهَ يَعْلَمُ غَيْبَ السَّمٰوٰتِ وَالْاَرْضِۗ وَاللّٰهُ بَصِيْرٌۢ بِمَا تَعْمَلُوْنَ ","bc":"innallāha ya'lamu gaibas-samāwāti wal-arḍ, wallāhu baṣīrum bimā ta'malụn","at":"Sungguh, Allah mengetahui apa yang gaib di langit dan di bumi. Dan Allah Maha Melihat apa yang kamu kerjakan. "}],al_humazah=[{"id":"1","ar":"وَيْلٌ لِّكُلِّ هُمَزَةٍ لُّمَزَةٍۙ","bc":"wailul likulli humazatil lumazah","at":"Celakalah bagi setiap pengumpat dan pencela,"},{"id":"2","ar":" ۨالَّذِيْ جَمَعَ مَالًا وَّعَدَّدَهٗۙ","bc":"allażī jama'a mālaw wa 'addadah","at":"yang mengumpulkan harta dan menghitung-hitungnya,"},{"id":"3","ar":"يَحْسَبُ اَنَّ مَالَهٗٓ اَخْلَدَهٗۚ","bc":"yaḥsabu anna mālahū akhladah","at":"dia (manusia) mengira bahwa hartanya itu dapat mengekalkannya."},{"id":"4","ar":"كَلَّا لَيُنْۢبَذَنَّ فِى الْحُطَمَةِۖ","bc":"kallā layumbażanna fil-ḥuṭamah","at":"Sekali-kali tidak! Pasti dia akan dilemparkan ke dalam (neraka) Hutamah."},{"id":"5","ar":"وَمَآ اَدْرٰىكَ مَا الْحُطَمَةُ ۗ","bc":"wa mā adrāka mal-ḥuṭamah","at":"Dan tahukah kamu apakah (neraka) Hutamah itu?"},{"id":"6","ar":"نَارُ اللّٰهِ الْمُوْقَدَةُۙ","bc":"nārullāhil-mụqadah","at":"(Yaitu) api (azab) Allah yang dinyalakan,"},{"id":"7","ar":"الَّتِيْ تَطَّلِعُ عَلَى الْاَفْـِٕدَةِۗ","bc":"allatī taṭṭali'u 'alal-af`idah","at":"yang (membakar) sampai ke hati."},{"id":"8","ar":"اِنَّهَا عَلَيْهِمْ مُّؤْصَدَةٌۙ","bc":"innahā 'alaihim mu`ṣadah","at":"Sungguh, api itu ditutup rapat atas (diri) mereka,"},{"id":"9","ar":"فِيْ عَمَدٍ مُّمَدَّدَةٍ","bc":"fī 'amadim mumaddadah","at":"(sedang mereka itu) diikat pada tiang-tiang yang panjang."}],al_ikhlas=[{"id":"1","ar":"قُلْ هُوَ اللّٰهُ اَحَدٌۚ","bc":"qul huwallāhu aḥad","at":"Katakanlah (Muhammad), “Dialah Allah, Yang Maha Esa."},{"id":"2","ar":"اَللّٰهُ الصَّمَدُۚ","bc":"allāhuṣ-ṣamad","at":"Allah tempat meminta segala sesuatu."},{"id":"3","ar":"لَمْ يَلِدْ وَلَمْ يُوْلَدْۙ","bc":"lam yalid wa lam yụlad","at":"(Allah) tidak beranak dan tidak pula diperanakkan."},{"id":"4","ar":"وَلَمْ يَكُنْ لَّهٗ كُفُوًا اَحَدٌ","bc":"wa lam yakul lahụ kufuwan aḥad","at":"Dan tidak ada sesuatu yang setara dengan Dia.”"}],al_infitar=[{"id":"1","ar":"اِذَا السَّمَاۤءُ انْفَطَرَتْۙ","bc":"iżas-samā`unfaṭarat","at":"Apabila langit terbelah, "},{"id":"2","ar":"وَاِذَا الْكَوَاكِبُ انْتَثَرَتْۙ","bc":"wa iżal-kawākibuntaṡarat","at":"dan apabila bintang-bintang jatuh berserakan, "},{"id":"3","ar":"وَاِذَا الْبِحَارُ فُجِّرَتْۙ","bc":"wa iżal-biḥāru fujjirat","at":"dan apabila lautan dijadikan meluap, "},{"id":"4","ar":"وَاِذَا الْقُبُوْرُ بُعْثِرَتْۙ","bc":"wa iżal-qubụru bu'ṡirat","at":"dan apabila kuburan-kuburan dibongkar,"},{"id":"5","ar":"عَلِمَتْ نَفْسٌ مَّا قَدَّمَتْ وَاَخَّرَتْۗ","bc":"'alimat nafsum mā qaddamat wa akhkharat","at":"(maka) setiap jiwa akan mengetahui apa yang telah dikerjakan dan yang dilalaikan(nya)."},{"id":"6","ar":"يٰٓاَيُّهَا الْاِنْسَانُ مَا غَرَّكَ بِرَبِّكَ الْكَرِيْمِۙ","bc":"yā ayyuhal-insānu mā garraka birabbikal-karīm","at":"Wahai manusia! Apakah yang telah memperdayakan kamu (berbuat durhaka) terhadap Tuhanmu Yang Mahamulia, "},{"id":"7","ar":"الَّذِيْ خَلَقَكَ فَسَوّٰىكَ فَعَدَلَكَۙ","bc":"allażī khalaqaka fa sawwāka fa 'adalak","at":"yang telah menciptakanmu lalu menyempurnakan kejadianmu dan menjadikan (susunan tubuh)mu seimbang, "},{"id":"8","ar":"فِيْٓ اَيِّ صُوْرَةٍ مَّا شَاۤءَ رَكَّبَكَۗ","bc":"fī ayyi ṣụratim mā syā`a rakkabak","at":"dalam bentuk apa saja yang dikehendaki, Dia menyusun tubuhmu. "},{"id":"9","ar":"كَلَّا بَلْ تُكَذِّبُوْنَ بِالدِّيْنِۙ","bc":"kallā bal tukażżibụna bid-dīn","at":"Sekali-kali jangan begitu! Bahkan kamu mendustakan hari pembalasan."},{"id":"10","ar":"وَاِنَّ عَلَيْكُمْ لَحٰفِظِيْنَۙ","bc":"wa inna 'alaikum laḥāfiẓīn","at":"Dan sesungguhnya bagi kamu ada (malaikat-malaikat) yang mengawasi (pekerjaanmu), "},{"id":"11","ar":"كِرَامًا كَاتِبِيْنَۙ","bc":"kirāmang kātibīn","at":"yang mulia (di sisi Allah) dan yang mencatat (amal perbuatanmu), "},{"id":"12","ar":"يَعْلَمُوْنَ مَا تَفْعَلُوْنَ","bc":"ya'lamụna mā taf'alụn","at":"mereka mengetahui apa yang kamu kerjakan. "},{"id":"13","ar":"اِنَّ الْاَبْرَارَ لَفِيْ نَعِيْمٍۙ","bc":"innal-abrāra lafī na'īm","at":"Sesungguhnya orang-orang yang berbakti benar-benar berada dalam (surga yang penuh) kenikmatan, "},{"id":"14","ar":"وَّاِنَّ الْفُجَّارَ لَفِيْ جَحِيْمٍ","bc":"wa innal-fujjāra lafī jaḥīm","at":"dan sesungguhnya orang-orang yang durhaka benar-benar berada dalam neraka. "},{"id":"15","ar":"يَصْلَوْنَهَا يَوْمَ الدِّيْنِ","bc":"yaṣlaunahā yaumad-dīn","at":"Mereka masuk ke dalamnya pada hari pembalasan."},{"id":"16","ar":"وَمَا هُمْ عَنْهَا بِغَاۤىِٕبِيْنَۗ","bc":"wa mā hum 'an-hā bigā`ibīn","at":"Dan mereka tidak mungkin keluar dari neraka itu. "},{"id":"17","ar":"وَمَآ اَدْرٰىكَ مَا يَوْمُ الدِّيْنِۙ","bc":"wa mā adrāka mā yaumud-dīn","at":"Dan tahukah kamu apakah hari pembalasan itu? "},{"id":"18","ar":"ثُمَّ مَآ اَدْرٰىكَ مَا يَوْمُ الدِّيْنِۗ","bc":"ṡumma mā adrāka mā yaumud-dīn","at":"Sekali lagi, tahukah kamu apakah hari pembalasan itu? "},{"id":"19","ar":"يَوْمَ لَا تَمْلِكُ نَفْسٌ لِّنَفْسٍ شَيْـًٔا ۗوَالْاَمْرُ يَوْمَىِٕذٍ لِّلّٰهِ","bc":"yauma lā tamliku nafsul linafsin syai`ā, wal-amru yauma`iżil lillāh","at":"(Yaitu) pada hari (ketika) seseorang sama sekali tidak berdaya (menolong) orang lain. Dan segala urusan pada hari itu dalam kekuasaan Allah. "}],al_insan=[{"id":"1","ar":"هَلْ اَتٰى عَلَى الْاِنْسَانِ حِيْنٌ مِّنَ الدَّهْرِ لَمْ يَكُنْ شَيْـًٔا مَّذْكُوْرًا ","bc":"hal atā 'alal-insāni ḥīnum minad-dahri lam yakun syai`am mażkụrā","at":"Bukankah pernah datang kepada manusia waktu dari masa, yang ketika itu belum merupakan sesuatu yang dapat disebut?"},{"id":"2","ar":"اِنَّا خَلَقْنَا الْاِنْسَانَ مِنْ نُّطْفَةٍ اَمْشَاجٍۖ نَّبْتَلِيْهِ فَجَعَلْنٰهُ سَمِيْعًاۢ بَصِيْرًا ","bc":"innā khalaqnal-insāna min nuṭfatin amsyājin nabtalīhi fa ja'alnāhu samī'an baṣīrā","at":"Sungguh, Kami telah menciptakan manusia dari setetes mani yang bercampur yang Kami hendak mengujinya (dengan perintah dan larangan), karena itu Kami jadikan dia mendengar dan melihat."},{"id":"3","ar":"اِنَّا هَدَيْنٰهُ السَّبِيْلَ اِمَّا شَاكِرًا وَّاِمَّا كَفُوْرًا ","bc":"innā hadaināhus-sabīla immā syākiraw wa immā kafụrā","at":"Sungguh, Kami telah menunjukkan kepadanya jalan yang lurus; ada yang bersyukur dan ada pula yang kufur. "},{"id":"4","ar":"اِنَّآ اَعْتَدْنَا لِلْكٰفِرِيْنَ سَلٰسِلَا۟ وَاَغْلٰلًا وَّسَعِيْرًا ","bc":"innā a'tadnā lil-kāfirīna salāsila wa aglālaw wa sa'īrā","at":"Sungguh, Kami telah menyediakan bagi orang-orang kafir rantai, belenggu dan neraka yang menyala-nyala. "},{"id":"5","ar":"اِنَّ الْاَبْرَارَ يَشْرَبُوْنَ مِنْ كَأْسٍ كَانَ مِزَاجُهَا كَافُوْرًاۚ","bc":"innal-abrāra yasyrabụna ming ka`sing kāna mizājuhā kāfụrā","at":"Sungguh, orang-orang yang berbuat kebajikan akan minum dari gelas (berisi minuman) yang campurannya adalah air kafur, "},{"id":"6","ar":"عَيْنًا يَّشْرَبُ بِهَا عِبَادُ اللّٰهِ يُفَجِّرُوْنَهَا تَفْجِيْرًا ","bc":"'ainay yasyrabu bihā 'ibādullāhi yufajjirụnahā tafjīrā","at":"(yaitu) mata air (dalam surga) yang diminum oleh hamba-hamba Allah dan mereka dapat memancarkannya dengan sebaik-baiknya."},{"id":"7","ar":"يُوْفُوْنَ بِالنَّذْرِ وَيَخَافُوْنَ يَوْمًا كَانَ شَرُّهٗ مُسْتَطِيْرًا ","bc":"yụfụna bin-nażri wa yakhāfụna yaumang kāna syarruhụ mustaṭīrā","at":"Mereka memenuhi nazar dan takut akan suatu hari yang azabnya merata di mana-mana. "},{"id":"8","ar":"وَيُطْعِمُوْنَ الطَّعَامَ عَلٰى حُبِّهٖ مِسْكِيْنًا وَّيَتِيْمًا وَّاَسِيْرًا ","bc":"wa yuṭ'imụnaṭ-ṭa'āma 'alā ḥubbihī miskīnaw wa yatīmaw wa asīrā","at":"Dan mereka memberikan makanan yang disukainya kepada orang miskin, anak yatim dan orang yang ditawan, "},{"id":"9","ar":"اِنَّمَا نُطْعِمُكُمْ لِوَجْهِ اللّٰهِ لَا نُرِيْدُ مِنْكُمْ جَزَاۤءً وَّلَا شُكُوْرًا ","bc":"innamā nuṭ'imukum liwaj-hillāhi lā nurīdu mingkum jazā`aw wa lā syukụrā","at":"(sambil berkata), “Sesungguhnya kami memberi makanan kepadamu hanyalah karena mengharapkan keridaan Allah, kami tidak mengharap balasan dan terima kasih dari kamu."},{"id":"10","ar":"اِنَّا نَخَافُ مِنْ رَّبِّنَا يَوْمًا عَبُوْسًا قَمْطَرِيْرًا ","bc":"innā nakhāfu mir rabbinā yauman 'abụsang qamṭarīrā","at":"Sungguh, kami takut akan (azab) Tuhan pada hari (ketika) orang-orang berwajah masam penuh kesulitan.”"},{"id":"11","ar":"فَوَقٰىهُمُ اللّٰهُ شَرَّ ذٰلِكَ الْيَوْمِ وَلَقّٰىهُمْ نَضْرَةً وَّسُرُوْرًاۚ","bc":"fa waqāhumullāhu syarra żālikal-yaumi wa laqqāhum naḍrataw wa surụrā","at":"Maka Allah melindungi mereka dari kesusahan hari itu, dan memberikan kepada mereka keceriaan dan kegembiraan."},{"id":"12","ar":"وَجَزٰىهُمْ بِمَا صَبَرُوْا جَنَّةً وَّحَرِيْرًاۙ","bc":"wa jazāhum bimā ṣabarụ jannataw wa ḥarīrā","at":"Dan Dia memberi balasan kepada mereka karena kesabarannya (berupa) surga dan (pakaian) sutera."},{"id":"13","ar":"مُّتَّكِـِٕيْنَ فِيْهَا عَلَى الْاَرَاۤىِٕكِۚ لَا يَرَوْنَ فِيْهَا شَمْسًا وَّلَا زَمْهَرِيْرًاۚ","bc":"muttaki`īna fīhā 'alal-arā`ik, lā yarauna fīhā syamsaw wa lā zamharīrā","at":"Di sana mereka duduk bersandar di atas dipan, di sana mereka tidak melihat (merasakan teriknya) matahari dan tidak pula dingin yang berlebihan."},{"id":"14","ar":"وَدَانِيَةً عَلَيْهِمْ ظِلٰلُهَا وَذُلِّلَتْ قُطُوْفُهَا تَذْلِيْلًا ","bc":"wa dāniyatan 'alaihim ẓilāluhā wa żullilat quṭụfuhā tażlīlā","at":"Dan naungan (pepohonan)nya dekat di atas mereka dan dimudahkan semudah-mudahnya untuk memetik (buah)nya."},{"id":"15","ar":"وَيُطَافُ عَلَيْهِمْ بِاٰنِيَةٍ مِّنْ فِضَّةٍ وَّاَكْوَابٍ كَانَتْ قَوَارِيْرَا۠ ","bc":"wa yuṭāfu 'alaihim bi`āniyatim min fiḍḍatiw wa akwābing kānat qawārīrā","at":"Dan kepada mereka diedarkan bejana-bejana dari perak dan piala-piala yang bening laksana kristal,"},{"id":"16","ar":"قَوَارِيْرَا۟ مِنْ فِضَّةٍ قَدَّرُوْهَا تَقْدِيْرًا ","bc":"qawārīra min fiḍḍating qaddarụhā taqdīrā","at":"kristal yang jernih terbuat dari perak, mereka tentukan ukurannya yang sesuai (dengan kehendak mereka). "},{"id":"17","ar":"وَيُسْقَوْنَ فِيْهَا كَأْسًا كَانَ مِزَاجُهَا زَنْجَبِيْلًاۚ","bc":"wa yusqauna fīhā ka`sang kāna mizājuhā zanjabīlā","at":"Dan di sana mereka diberi segelas minuman bercampur jahe."},{"id":"18","ar":"عَيْنًا فِيْهَا تُسَمّٰى سَلْسَبِيْلًا ","bc":"'ainan fīhā tusammā salsabīlā","at":"(Yang didatangkan dari) sebuah mata air (di surga) yang dinamakan Salsabil."},{"id":"19","ar":"۞ وَيَطُوْفُ عَلَيْهِمْ وِلْدَانٌ مُّخَلَّدُوْنَۚ اِذَا رَاَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤًا مَّنْثُوْرًا","bc":"wa yaṭụfu 'alaihim wildānum mukhalladụn, iżā ra`aitahum ḥasibtahum lu`lu`am manṡụrā","at":"Dan mereka dikelilingi oleh para pemuda yang tetap muda. Apabila kamu melihatnya, akan kamu kira mereka, mutiara yang bertaburan."},{"id":"20","ar":"وَاِذَا رَاَيْتَ ثَمَّ رَاَيْتَ نَعِيْمًا وَّمُلْكًا كَبِيْرًا ","bc":"wa iżā ra`aita ṡamma ra`aita na'īmaw wa mulkang kabīrā","at":"Dan apabila engkau melihat (keadaan) di sana (surga), niscaya engkau akan melihat berbagai macam kenikmatan dan kerajaan yang besar."},{"id":"21","ar":"عٰلِيَهُمْ ثِيَابُ سُنْدُسٍ خُضْرٌ وَّاِسْتَبْرَقٌۖ وَّحُلُّوْٓا اَسَاوِرَ مِنْ فِضَّةٍۚ وَسَقٰىهُمْ رَبُّهُمْ شَرَابًا طَهُوْرًا ","bc":"'āliyahum ṡiyābu sundusin khuḍruw wa istabraquw wa ḥullū asāwira min fiḍḍah, wa saqāhum rabbuhum syarāban ṭahụrā","at":"Mereka berpakaian sutera halus yang hijau dan sutera tebal dan memakai gelang terbuat dari perak, dan Tuhan memberikan kepada mereka minuman yang bersih (dan suci)."},{"id":"22","ar":"اِنَّ هٰذَا كَانَ لَكُمْ جَزَاۤءً وَّكَانَ سَعْيُكُمْ مَّشْكُوْرًا ","bc":"inna hāżā kāna lakum jazā`aw wa kāna sa'yukum masykụrā","at":"Inilah balasan untukmu, dan segala usahamu diterima dan diakui (Allah)."},{"id":"23","ar":"اِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ الْقُرْاٰنَ تَنْزِيْلًاۚ","bc":"innā naḥnu nazzalnā 'alaikal-qur`āna tanzīlā","at":"Sesungguhnya Kamilah yang menurunkan Al-Qur'an kepadamu (Muhammad) secara berangsur-angsur."},{"id":"24","ar":"فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ اٰثِمًا اَوْ كَفُوْرًاۚ","bc":"faṣbir liḥukmi rabbika wa lā tuṭi' min-hum āṡiman au kafụrā","at":"Maka bersabarlah untuk (melaksanakan) ketetapan Tuhanmu, dan janganlah engkau ikuti orang yang berdosa dan orang yang kafir di antara mereka."},{"id":"25","ar":"وَاذْكُرِ اسْمَ رَبِّكَ بُكْرَةً وَّاَصِيْلًاۚ","bc":"ważkurisma rabbika bukrataw wa aṣīlā","at":"Dan sebutlah nama Tuhanmu pada (waktu) pagi dan petang."},{"id":"26","ar":"وَمِنَ الَّيْلِ فَاسْجُدْ لَهٗ وَسَبِّحْهُ لَيْلًا طَوِيْلًا ","bc":"wa minal-laili fasjud lahụ wa sabbiḥ-hu lailan ṭawīlā","at":"Dan pada sebagian dari malam, maka bersujudlah kepada-Nya dan bertasbihlah kepada-Nya pada bagian yang panjang di malam hari."},{"id":"27","ar":"اِنَّ هٰٓؤُلَاۤءِ يُحِبُّوْنَ الْعَاجِلَةَ وَيَذَرُوْنَ وَرَاۤءَهُمْ يَوْمًا ثَقِيْلًا ","bc":"inna hā`ulā`i yuḥibbụnal-'ājilata wa yażarụna warā`ahum yauman ṡaqīlā","at":"Sesungguhnya mereka (orang kafir) itu mencintai kehidupan (dunia) dan meninggalkan hari yang berat (hari akhirat) di belakangnya."},{"id":"28","ar":"نَحْنُ خَلَقْنٰهُمْ وَشَدَدْنَآ اَسْرَهُمْۚ وَاِذَا شِئْنَا بَدَّلْنَآ اَمْثَالَهُمْ تَبْدِيْلًا ","bc":"naḥnu khalaqnāhum wa syadadnā asrahum, wa iżā syi`nā baddalnā amṡālahum tabdīlā","at":"Kami telah menciptakan mereka dan menguatkan persendian tubuh mereka. Tetapi, jika Kami menghendaki, Kami dapat mengganti dengan yang serupa mereka."},{"id":"29","ar":"اِنَّ هٰذِهٖ تَذْكِرَةٌ ۚ فَمَنْ شَاۤءَ اتَّخَذَ اِلٰى رَبِّهٖ سَبِيْلًا ","bc":"inna hāżihī tażkirah, fa man syā`attakhaża ilā rabbihī sabīlā","at":"Sungguh, (ayat-ayat) ini adalah peringatan, maka barangsiapa menghendaki (kebaikan bagi dirinya) tentu dia mengambil jalan menuju kepada Tuhannya."},{"id":"30","ar":"وَمَا تَشَاۤءُوْنَ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۗاِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًاۖ","bc":"wa mā tasyā`ụna illā ay yasyā`allāh, innallāha kāna 'alīman ḥakīmā","at":"Tetapi kamu tidak mampu (menempuh jalan itu), kecuali apabila Allah kehendaki Allah. Sungguh, Allah Maha Mengetahui, Mahabijaksana. "},{"id":"31","ar":"يُّدْخِلُ مَنْ يَّشَاۤءُ فِيْ رَحْمَتِهٖۗ وَالظّٰلِمِيْنَ اَعَدَّ لَهُمْ عَذَابًا اَلِيْمًا","bc":"yudkhilu may yasyā`u fī raḥmatih, waẓ-ẓālimīna a'adda lahum 'ażāban alīmā","at":"Dia memasukkan siapa pun yang Dia kehendaki ke dalam rahmat-Nya (surga). Adapun bagi orang-orang zalim disediakan-Nya azab yang pedih."}],al_insyiqaq=[{"id":"1","ar":"اِذَا السَّمَاۤءُ انْشَقَّتْۙ ","bc":"iżas-samā`unsyaqqat","at":"Apabila langit terbelah,"},{"id":"2","ar":"وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۙ ","bc":"wa ażinat lirabbihā wa ḥuqqat","at":"dan patuh kepada Tuhannya, dan sudah semestinya patuh, "},{"id":"3","ar":"وَاِذَا الْاَرْضُ مُدَّتْۙ ","bc":"wa iżal-arḍu muddat","at":"dan apabila bumi diratakan, "},{"id":"4","ar":"وَاَلْقَتْ مَا فِيْهَا وَتَخَلَّتْۙ ","bc":"wa alqat mā fīhā wa takhallat","at":"dan memuntahkan apa yang ada di dalamnya dan menjadi kosong,"},{"id":"5","ar":"وَاَذِنَتْ لِرَبِّهَا وَحُقَّتْۗ ","bc":"wa ażinat lirabbihā wa ḥuqqat","at":"dan patuh kepada Tuhannya, dan sudah semestinya patuh."},{"id":"6","ar":"يٰٓاَيُّهَا الْاِنْسَانُ اِنَّكَ كَادِحٌ اِلٰى رَبِّكَ كَدْحًا فَمُلٰقِيْهِۚ ","bc":"yā ayyuhal-insānu innaka kādiḥun ilā rabbika kad-ḥan fa mulāqīh","at":"Wahai manusia! Sesungguhnya kamu telah bekerja keras menuju Tuhanmu, maka kamu akan menemui-Nya."},{"id":"7","ar":"فَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖۙ ","bc":"fa ammā man ụtiya kitābahụ biyamīnih","at":"Maka adapun orang yang catatannya diberikan dari sebelah kanannya, "},{"id":"8","ar":"فَسَوْفَ يُحَاسَبُ حِسَابًا يَّسِيْرًاۙ ","bc":"fa saufa yuḥāsabu ḥisābay yasīrā","at":"maka dia akan diperiksa dengan pemeriksaan yang mudah, "},{"id":"9","ar":"وَّيَنْقَلِبُ اِلٰٓى اَهْلِهٖ مَسْرُوْرًاۗ ","bc":"wa yangqalibu ilā ahlihī masrụrā","at":"dan dia akan kembali kepada keluarganya (yang sama-sama beriman) dengan gembira."},{"id":"10","ar":"وَاَمَّا مَنْ اُوْتِيَ كِتٰبَهٗ وَرَاۤءَ ظَهْرِهٖۙ ","bc":"wa ammā man ụtiya kitābahụ warā`a ẓahrih","at":"Dan adapun orang yang catatannya diberikan dari sebelah belakang, "},{"id":"11","ar":"فَسَوْفَ يَدْعُوْ ثُبُوْرًاۙ ","bc":"fa saufa yad'ụ ṡubụrā","at":"maka dia akan berteriak, “Celakalah aku!” "},{"id":"12","ar":"وَّيَصْلٰى سَعِيْرًاۗ ","bc":"wa yaṣlā sa'īrā","at":"Dan dia akan masuk ke dalam api yang menyala-nyala (neraka)."},{"id":"13","ar":"اِنَّهٗ كَانَ فِيْٓ اَهْلِهٖ مَسْرُوْرًاۗ ","bc":"innahụ kāna fī ahlihī masrụrā","at":"Sungguh, dia dahulu (di dunia) bergembira di kalangan keluarganya (yang sama-sama kafir). "},{"id":"14","ar":"اِنَّهٗ ظَنَّ اَنْ لَّنْ يَّحُوْرَ ۛ ","bc":"innahụ ẓanna al lay yaḥụr","at":"Sesungguhnya dia mengira bahwa dia tidak akan kembali (kepada Tuhannya). "},{"id":"15","ar":"بَلٰىۛ اِنَّ رَبَّهٗ كَانَ بِهٖ بَصِيْرًاۗ ","bc":"balā inna rabbahụ kāna bihī baṣīrā","at":"Tidak demikian, sesungguhnya Tuhannya selalu melihatnya. "},{"id":"16","ar":"فَلَآ اُقْسِمُ بِالشَّفَقِۙ ","bc":"fa lā uqsimu bisy-syafaq","at":"Maka Aku bersumpah demi cahaya merah pada waktu senja, "},{"id":"17","ar":"وَالَّيْلِ وَمَا وَسَقَۙ ","bc":"wal-laili wa mā wasaq","at":"demi malam dan apa yang diselubunginya,"},{"id":"18","ar":"وَالْقَمَرِ اِذَا اتَّسَقَۙ ","bc":"wal-qamari iżattasaq","at":"demi bulan apabila jadi purnama,"},{"id":"19","ar":"لَتَرْكَبُنَّ طَبَقًا عَنْ طَبَقٍۗ ","bc":"latarkabunna ṭabaqan 'an ṭabaq","at":"sungguh, akan kamu jalani tingkat demi tingkat (dalam kehidupan)."},{"id":"20","ar":"فَمَا لَهُمْ لَا يُؤْمِنُوْنَۙ ","bc":"fa mā lahum lā yu`minụn","at":"Maka mengapa mereka tidak mau beriman?"},{"id":"21","ar":"وَاِذَا قُرِئَ عَلَيْهِمُ الْقُرْاٰنُ لَا يَسْجُدُوْنَ ۗ ۩ ","bc":"wa iżā quri`a 'alaihimul-qur`ānu lā yasjudụn","at":"Dan apabila Al-Qur'an dibacakan kepada mereka, mereka tidak (mau) bersujud,"},{"id":"22","ar":"بَلِ الَّذِيْنَ كَفَرُوْا يُكَذِّبُوْنَۖ ","bc":"balillażīna kafarụ yukażżibụn","at":"bahkan orang-orang kafir itu mendustakan(nya)."},{"id":"23","ar":"وَاللّٰهُ اَعْلَمُ بِمَا يُوْعُوْنَۖ ","bc":"wallāhu a'lamu bimā yụ'ụn","at":"Dan Allah lebih mengetahui apa yang mereka sembunyikan (dalam hati mereka)."},{"id":"24","ar":"فَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍۙ ","bc":"fa basysyir-hum bi'ażābin alīm","at":"Maka sampaikanlah kepada mereka (ancaman) azab yang pedih,"},{"id":"25","ar":"اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍ ","bc":"illallażīna āmanụ wa 'amiluṣ-ṣāliḥāti lahum ajrun gairu mamnụn","at":"kecuali orang-orang yang beriman dan mengerjakan kebajikan, mereka akan mendapat pahala yang tidak putus-putusnya."}],al_isra=[{"id":"1","ar":"سُبْحٰنَ الَّذِيْٓ اَسْرٰى بِعَبْدِهٖ لَيْلًا مِّنَ الْمَسْجِدِ الْحَرَامِ اِلَى الْمَسْجِدِ الْاَقْصَا الَّذِيْ بٰرَكْنَا حَوْلَهٗ لِنُرِيَهٗ مِنْ اٰيٰتِنَاۗ اِنَّهٗ هُوَ السَّمِيْعُ الْبَصِيْرُ","bc":"sub-ḥānallażī asrā bi'abdihī lailam minal-masjidil-ḥarāmi ilal-masjidil-aqṣallażī bāraknā ḥaulahụ linuriyahụ min āyātinā, innahụ huwas-samī'ul-baṣīr","at":"Mahasuci (Allah), yang telah memperjalankan hamba-Nya (Muhammad) pada malam hari dari Masjidilharam ke Masjidil Aqsa yang telah Kami berkahi sekelilingnya agar Kami perlihatkan kepadanya sebagian tanda-tanda (kebesaran) Kami. Sesungguhnya Dia Maha Mendengar, Maha Melihat."},{"id":"2","ar":"وَاٰتَيْنَا مُوْسَى الْكِتٰبَ وَجَعَلْنٰهُ هُدًى لِّبَنِيْٓ اِسْرَاۤءِيْلَ اَلَّا تَتَّخِذُوْا مِنْ دُوْنِيْ وَكِيْلًاۗ ","bc":"wa ātainā mụsal-kitāba wa ja'alnāhu hudal libanī isrā`īla allā tattakhiżụ min dụnī wakīlā","at":"Dan Kami berikan kepada Musa, Kitab (Taurat) dan Kami jadikannya petunjuk bagi Bani Israil (dengan firman), “Janganlah kamu mengambil (pelindung) selain Aku. "},{"id":"3","ar":"ذُرِّيَّةَ مَنْ حَمَلْنَا مَعَ نُوْحٍۗ اِنَّهٗ كَانَ عَبْدًا شَكُوْرًا","bc":"żurriyyata man ḥamalnā ma'a nụḥ, innahụ kāna 'abdan syakụrā","at":"(Wahai) keturunan orang yang Kami bawa bersama Nuh. Sesungguhnya dia (Nuh) adalah hamba (Allah) yang banyak bersyukur.”"},{"id":"4","ar":"وَقَضَيْنَآ اِلٰى بَنِيْٓ اِسْرَاۤءِيْلَ فِى الْكِتٰبِ لَتُفْسِدُنَّ فِى الْاَرْضِ مَرَّتَيْنِ وَلَتَعْلُنَّ عُلُوًّا كَبِيْرًا","bc":"wa qaḍainā ilā banī isrā`īla fil-kitābi latufsidunna fil-arḍi marrataini wa lata'lunna 'uluwwang kabīrā","at":"Dan Kami tetapkan terhadap Bani Israil dalam Kitab itu, “Kamu pasti akan berbuat kerusakan di bumi ini dua kali dan pasti kamu akan menyombongkan diri dengan kesombongan yang besar.”"},{"id":"5","ar":"فَاِذَا جَاۤءَ وَعْدُ اُوْلٰىهُمَا بَعَثْنَا عَلَيْكُمْ عِبَادًا لَّنَآ اُولِيْ بَأْسٍ شَدِيْدٍ فَجَاسُوْا خِلٰلَ الدِّيَارِۗ وَكَانَ وَعْدًا مَّفْعُوْلًا","bc":"fa iżā jā`a wa'du ụlāhumā ba'aṡnā 'alaikum 'ibādal lanā ulī ba`sin syadīdin fa jāsụ khilālad-diyār, wa kāna wa'dam maf'ụlā","at":"Maka apabila datang saat hukuman bagi (kejahatan) yang pertama dari kedua (kejahatan) itu, Kami datangkan kepadamu hamba-hamba Kami yang perkasa, lalu mereka merajalela di kampung-kampung. Dan itulah ketetapan yang pasti terlaksana."},{"id":"6","ar":"ثُمَّ رَدَدْنَا لَكُمُ الْكَرَّةَ عَلَيْهِمْ وَاَمْدَدْنٰكُمْ بِاَمْوَالٍ وَّبَنِيْنَ وَجَعَلْنٰكُمْ اَكْثَرَ نَفِيْرًا","bc":"ṡumma radadnā lakumul-karrata 'alaihim wa amdadnākum bi`amwāliw wa banīna wa ja'alnākum akṡara nafīrā","at":"Kemudian Kami berikan kepadamu giliran untuk mengalahkan mereka, Kami membantumu dengan harta kekayaan dan anak-anak dan Kami jadikan kamu kelompok yang lebih besar."},{"id":"7","ar":"اِنْ اَحْسَنْتُمْ اَحْسَنْتُمْ لِاَنْفُسِكُمْ ۗوَاِنْ اَسَأْتُمْ فَلَهَاۗ فَاِذَا جَاۤءَ وَعْدُ الْاٰخِرَةِ لِيَسٗۤـُٔوْا وُجُوْهَكُمْ وَلِيَدْخُلُوا الْمَسْجِدَ كَمَا دَخَلُوْهُ اَوَّلَ مَرَّةٍ وَّلِيُتَبِّرُوْا مَا عَلَوْا تَتْبِيْرًا","bc":"in aḥsantum aḥsantum li`anfusikum, wa in asa`tum fa lahā, fa iżā jā`a wa'dul-ākhirati liyasū`ụ wujụhakum wa liyadkhulul-masjida kamā dakhalụhu awwala marratiw wa liyutabbirụ mā 'alau tatbīrā","at":"Jika kamu berbuat baik (berarti) kamu berbuat baik untuk dirimu sendiri. Dan jika kamu berbuat jahat, maka (kerugian kejahatan) itu untuk dirimu sendiri. Apabila datang saat hukuman (kejahatan) yang kedua, (Kami bangkitkan musuhmu) untuk menyuramkan wajahmu lalu mereka masuk ke dalam masjid (Masjidil Aqsa), sebagaimana ketika mereka memasukinya pertama kali dan mereka membinasakan apa saja yang mereka kuasai."},{"id":"8","ar":"عَسٰى رَبُّكُمْ اَنْ يَّرْحَمَكُمْۚ وَاِنْ عُدْتُّمْ عُدْنَاۘ وَجَعَلْنَا جَهَنَّمَ لِلْكٰفِرِيْنَ حَصِيْرًا ","bc":"'asā rabbukum ay yar-ḥamakum, wa in 'uttum 'udnā, wa ja'alnā jahannama lil-kāfirīna ḥaṣīrā","at":"Mudah-mudahan Tuhan kamu melimpahkan rahmat kepada kamu; tetapi jika kamu kembali (melakukan kejahatan), niscaya Kami kembali (mengazabmu). Dan Kami jadikan neraka Jahanam penjara bagi orang kafir."},{"id":"9","ar":"اِنَّ هٰذَا الْقُرْاٰنَ يَهْدِيْ لِلَّتِيْ هِيَ اَقْوَمُ وَيُبَشِّرُ الْمُؤْمِنِيْنَ الَّذِيْنَ يَعْمَلُوْنَ الصّٰلِحٰتِ اَنَّ لَهُمْ اَجْرًا كَبِيْرًاۙ ","bc":"inna hāżal-qur`āna yahdī lillatī hiya aqwamu wa yubasysyirul-mu`minīnallażīna ya'malụnaṣ-ṣāliḥāti anna lahum ajrang kabīrā","at":"Sungguh, Al-Qur'an ini memberi petunjuk ke (jalan) yang paling lurus dan memberi kabar gembira kepada orang mukmin yang mengerjakan kebajikan, bahwa mereka akan mendapat pahala yang besar,"},{"id":"10","ar":"وَّاَنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ اَعْتَدْنَا لَهُمْ عَذَابًا اَلِيْمًا","bc":"wa annallażīna lā yu`minụna bil-ākhirati a'tadnā lahum 'ażāban alīmā","at":"dan bahwa orang yang tidak beriman kepada kehidupan akhirat, Kami sediakan bagi mereka azab yang pedih."},{"id":"11","ar":"وَيَدْعُ الْاِنْسَانُ بِالشَّرِّ دُعَاۤءَهٗ بِالْخَيْرِۗ وَكَانَ الْاِنْسَانُ عَجُوْلًا ","bc":"wa yad'ul-insānu bisy-syarri du'ā`ahụ bil-khaīr, wa kānal-insānu 'ajụlā","at":"Dan Manusia (seringkali) berdoa untuk kejahatan sebagaimana (biasanya) dia berdoa untuk kebaikan. Dan memang manusia bersifat tergesa-gesa."},{"id":"12","ar":"وَجَعَلْنَا الَّيْلَ وَالنَّهَارَ اٰيَتَيْنِ فَمَحَوْنَآ اٰيَةَ الَّيْلِ وَجَعَلْنَآ اٰيَةَ النَّهَارِ مُبْصِرَةً لِّتَبْتَغُوْا فَضْلًا مِّنْ رَّبِّكُمْ وَلِتَعْلَمُوْا عَدَدَ السِّنِيْنَ وَالْحِسَابَۗ وَكُلَّ شَيْءٍ فَصَّلْنٰهُ تَفْصِيْلًا","bc":"wa ja'alnal-laila wan-nahāra āyataini fa maḥaunā āyatal-laili wa ja'alnā āyatan-nahāri mubṣiratal litabtagụ faḍlam mir rabbikum wa lita'lamụ 'adadas-sinīna wal-ḥisāb, wa kulla syai`in faṣṣalnāhu tafṣīlā","at":"Dan Kami jadikan malam dan siang sebagai dua tanda (kebesaran Kami), kemudian Kami hapuskan tanda malam dan Kami jadikan tanda siang itu terang benderang, agar kamu (dapat) mencari karunia dari Tuhanmu, dan agar kamu mengetahui bilangan tahun dan perhitungan (waktu). Dan segala sesuatu telah Kami terangkan dengan jelas."},{"id":"13","ar":"وَكُلَّ اِنْسَانٍ اَلْزَمْنٰهُ طٰۤىِٕرَهٗ فِيْ عُنُقِهٖۗ وَنُخْرِجُ لَهٗ يَوْمَ الْقِيٰمَةِ كِتٰبًا يَّلْقٰىهُ مَنْشُوْرًا","bc":"wa kulla insānin alzamnāhu ṭā`irahụ fī 'unuqih, wa nukhriju lahụ yaumal-qiyāmati kitābay yalqāhu mansyụrā","at":"Dan setiap manusia telah Kami kalungkan (catatan) amal perbuatannya di lehernya. Dan pada hari Kiamat Kami keluarkan baginya sebuah kitab dalam keadaan terbuka. "},{"id":"14","ar":"اِقْرَأْ كِتَابَكَۗ كَفٰى بِنَفْسِكَ الْيَوْمَ عَلَيْكَ حَسِيْبًاۗ","bc":"iqra` kitābak, kafā binafsikal-yauma 'alaika ḥasībā","at":"“Bacalah kitabmu, cukuplah dirimu sendiri pada hari ini sebagai penghitung atas dirimu.”"},{"id":"15","ar":"مَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَاۗ وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۗ وَمَا كُنَّا مُعَذِّبِيْنَ حَتّٰى نَبْعَثَ رَسُوْلًا","bc":"manihtadā fa innamā yahtadī linafsih, wa man ḍalla fa innamā yaḍillu 'alaihā, wa lā taziru wāziratuw wizra ukhrā, wa mā kunnā mu'ażżibīna ḥattā nab'aṡa rasụlā","at":"Barangsiapa berbuat sesuai dengan petunjuk (Allah), maka sesungguhnya itu untuk (keselamatan) dirinya sendiri; dan barang siapa tersesat maka sesungguhnya (kerugian) itu bagi dirinya sendiri. Dan seorang yang berdosa tidak dapat memikul dosa orang lain, tetapi Kami tidak akan menyiksa sebelum Kami mengutus seorang rasul."},{"id":"16","ar":"وَاِذَآ اَرَدْنَآ اَنْ نُّهْلِكَ قَرْيَةً اَمَرْنَا مُتْرَفِيْهَا فَفَسَقُوْا فِيْهَا فَحَقَّ عَلَيْهَا الْقَوْلُ فَدَمَّرْنٰهَا تَدْمِيْرًا ","bc":"wa iżā aradnā an nuhlika qaryatan amarnā mutrafīhā fa fasaqụ fīhā fa ḥaqqa 'alaihal-qaulu fa dammarnāhā tadmīrā","at":"Dan jika Kami hendak membinasakan suatu negeri, maka Kami perintahkan kepada orang yang hidup mewah di negeri itu (agar menaati Allah), tetapi bila mereka melakukan kedurhakaan di dalam (negeri) itu, maka sepantasnya berlakulah terhadapnya perkataan (hukuman Kami), kemudian Kami binasakan sama sekali (negeri itu)."},{"id":"17","ar":"وَكَمْ اَهْلَكْنَا مِنَ الْقُرُوْنِ مِنْۢ بَعْدِ نُوْحٍۗ وَكَفٰى بِرَبِّكَ بِذُنُوْبِ عِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا","bc":"wa kam ahlaknā minal-qurụni mim ba'di nụḥ, wa kafā birabbika biżunụbi 'ibādihī khabīram baṣīrā","at":"Dan berapa banyak kaum setelah Nuh, yang telah Kami binasakan. Dan cukuplah Tuhanmu Yang Maha Mengetahui, Maha Melihat dosa hamba-hamba-Nya."},{"id":"18","ar":"مَنْ كَانَ يُرِيْدُ الْعَاجِلَةَ عَجَّلْنَا لَهٗ فِيْهَا مَا نَشَاۤءُ لِمَنْ نُّرِيْدُ ثُمَّ جَعَلْنَا لَهٗ جَهَنَّمَۚ يَصْلٰىهَا مَذْمُوْمًا مَّدْحُوْرًا","bc":"mang kāna yurīdul-'ājilata 'ajjalnā lahụ fīhā mā nasyā`u liman nurīdu ṡumma ja'alnā lahụ jahannam, yaṣlāhā mażmụmam mad-ḥụrā","at":"Barangsiapa menghendaki kehidupan sekarang (duniawi), maka Kami segerakan baginya di (dunia) ini apa yang Kami kehendaki bagi orang yang Kami kehendaki. Kemudian Kami sediakan baginya (di akhirat) neraka Jahanam; dia akan memasukinya dalam keadaan tercela dan terusir."},{"id":"19","ar":"وَمَنْ اَرَادَ الْاٰخِرَةَ وَسَعٰى لَهَا سَعْيَهَا وَهُوَ مُؤْمِنٌ فَاُولٰۤىِٕكَ كَانَ سَعْيُهُمْ مَّشْكُوْرًا","bc":"wa man arādal-ākhirata wa sa'ā lahā sa'yahā wa huwa mu`minun fa ulā`ika kāna sa'yuhum masykụrā","at":"Dan barang siapa menghendaki kehidupan akhirat dan berusaha ke arah itu dengan sungguh-sungguh, sedangkan dia beriman, maka mereka itulah orang yang usahanya dibalas dengan baik."},{"id":"20","ar":"كُلًّا نُّمِدُّ هٰٓؤُلَاۤءِ وَهٰٓؤُلَاۤءِ مِنْ عَطَاۤءِ رَبِّكَ ۗوَمَا كَانَ عَطَاۤءُ رَبِّكَ مَحْظُوْرًا ","bc":"kullan numiddu hā`ulā`i wa hā`ulā`i min 'aṭā`i rabbik, wa mā kāna 'aṭā`u rabbika maḥẓụrā","at":"Kepada masing-masing (golongan), baik (golongan) ini (yang menginginkan dunia) maupun (golongan) itu (yang menginginkan akhirat), Kami berikan bantuan dari kemurahan Tuhanmu. Dan kemurahan Tuhanmu tidak dapat dihalangi."},{"id":"21","ar":"اُنْظُرْ كَيْفَ فَضَّلْنَا بَعْضَهُمْ عَلٰى بَعْضٍۗ وَلَلْاٰخِرَةُ اَكْبَرُ دَرَجٰتٍ وَّاَكْبَرُ تَفْضِيْلًا","bc":"unẓur kaifa faḍḍalnā ba'ḍahum 'alā ba'ḍ, wa lal-ākhiratu akbaru darajātiw wa akbaru tafḍīlā","at":"Perhatikanlah bagaimana Kami melebihkan sebagian mereka atas sebagian (yang lain). Dan kehidupan akhirat lebih tinggi derajatnya dan lebih besar keutamaan."},{"id":"22","ar":"لَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتَقْعُدَ مَذْمُوْمًا مَّخْذُوْلًا ","bc":"lā taj'al ma'allāhi ilāhan ākhara fa taq'uda mażmụmam makhżụlā","at":"Janganlah engkau mengadakan tuhan yang lain di samping Allah, nanti engkau menjadi tercela dan terhina."},{"id":"23","ar":"۞ وَقَضٰى رَبُّكَ اَلَّا تَعْبُدُوْٓا اِلَّآ اِيَّاهُ وَبِالْوَالِدَيْنِ اِحْسٰنًاۗ اِمَّا يَبْلُغَنَّ عِنْدَكَ الْكِبَرَ اَحَدُهُمَآ اَوْ كِلٰهُمَا فَلَا تَقُلْ لَّهُمَآ اُفٍّ وَّلَا تَنْهَرْهُمَا وَقُلْ لَّهُمَا قَوْلًا كَرِيْمًا","bc":"wa qaḍā rabbuka allā ta'budū illā iyyāhu wa bil-wālidaini iḥsānā, immā yabluganna 'indakal-kibara aḥaduhumā au kilāhumā fa lā taqul lahumā uffiw wa lā tan-har-humā wa qul lahumā qaulang karīmā","at":"Dan Tuhanmu telah memerintahkan agar kamu jangan menyembah selain Dia dan hendaklah berbuat baik kepada ibu bapak. Jika salah seorang di antara keduanya atau kedua-duanya sampai berusia lanjut dalam pemeliharaanmu, maka sekali-kali janganlah engkau mengatakan kepada keduanya perkataan “ah” dan janganlah engkau membentak keduanya, dan ucapkanlah kepada keduanya perkataan yang baik."},{"id":"24","ar":"وَاخْفِضْ لَهُمَا جَنَاحَ الذُّلِّ مِنَ الرَّحْمَةِ وَقُلْ رَّبِّ ارْحَمْهُمَا كَمَا رَبَّيٰنِيْ صَغِيْرًاۗ","bc":"wakhfiḍ lahumā janāḥaż-żulli minar-raḥmati wa qur rabbir-ḥam-humā kamā rabbayānī ṣagīrā","at":"Dan rendahkanlah dirimu terhadap keduanya dengan penuh kasih sayang dan ucapkanlah, “Wahai Tuhanku! Sayangilah keduanya sebagaimana mereka berdua telah mendidik aku pada waktu kecil.”"},{"id":"25","ar":"رَبُّكُمْ اَعْلَمُ بِمَا فِيْ نُفُوْسِكُمْ ۗاِنْ تَكُوْنُوْا صٰلِحِيْنَ فَاِنَّهٗ كَانَ لِلْاَوَّابِيْنَ غَفُوْرًا","bc":"rabbukum a'lamu bimā fī nufụsikum, in takụnụ ṣāliḥīna fa innahụ kāna lil-awwābīna gafụrā","at":"Tuhanmu lebih mengetahui apa yang ada dalam hatimu; jika kamu orang yang baik, maka sungguh, Dia Maha Pengampun kepada orang yang bertobat."},{"id":"26","ar":"وَاٰتِ ذَا الْقُرْبٰى حَقَّهٗ وَالْمِسْكِيْنَ وَابْنَ السَّبِيْلِ وَلَا تُبَذِّرْ تَبْذِيْرًا ","bc":"wa āti żal-qurbā ḥaqqahụ wal-miskīna wabnas-sabīli wa lā tubażżir tabżīrā","at":"Dan berikanlah haknya kepada kerabat dekat, juga kepada orang miskin dan orang yang dalam perjalanan; dan janganlah kamu menghambur-hamburkan (hartamu) secara boros."},{"id":"27","ar":"اِنَّ الْمُبَذِّرِيْنَ كَانُوْٓا اِخْوَانَ الشَّيٰطِيْنِ ۗوَكَانَ الشَّيْطٰنُ لِرَبِّهٖ كَفُوْرًا","bc":"innal-mubażżirīna kānū ikhwānasy-syayāṭīn, wa kānasy-syaiṭānu lirabbihī kafụrā","at":"Sesungguhnya orang-orang yang pemboros itu adalah saudara setan dan setan itu sangat ingkar kepada Tuhannya."},{"id":"28","ar":"وَاِمَّا تُعْرِضَنَّ عَنْهُمُ ابْتِغَاۤءَ رَحْمَةٍ مِّنْ رَّبِّكَ تَرْجُوْهَا فَقُلْ لَّهُمْ قَوْلًا مَّيْسُوْرًا","bc":"wa immā tu'riḍanna 'an-humubtigā`a raḥmatim mir rabbika tarjụhā fa qul lahum qaulam maisụrā","at":"Dan jika engkau berpaling dari mereka untuk memperoleh rahmat dari Tuhanmu yang engkau harapkan, maka katakanlah kepada mereka ucapan yang lemah lembut."},{"id":"29","ar":"وَلَا تَجْعَلْ يَدَكَ مَغْلُوْلَةً اِلٰى عُنُقِكَ وَلَا تَبْسُطْهَا كُلَّ الْبَسْطِ فَتَقْعُدَ مَلُوْمًا مَّحْسُوْرًا ","bc":"wa lā taj'al yadaka maglụlatan ilā 'unuqika wa lā tabsuṭ-hā kullal-basṭi fa taq'uda malụmam maḥsụrā","at":"Dan janganlah engkau jadikan tanganmu terbelenggu pada lehermu dan jangan (pula) engkau terlalu mengulurkannya (sangat pemurah) nanti kamu menjadi tercela dan menyesal."},{"id":"30","ar":"اِنَّ رَبَّكَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۗاِنَّهٗ كَانَ بِعِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا","bc":"inna rabbaka yabsuṭur-rizqa limay yasyā`u wa yaqdir, innahụ kāna bi'ibādihī khabīram baṣīrā","at":"Sungguh, Tuhanmu melapangkan rezeki bagi siapa yang Dia kehendaki dan membatasi (bagi siapa yang Dia kehendaki); sungguh, Dia Maha Mengetahui, Maha Melihat hamba-hamba-Nya."},{"id":"31","ar":"وَلَا تَقْتُلُوْٓا اَوْلَادَكُمْ خَشْيَةَ اِمْلَاقٍۗ نَحْنُ نَرْزُقُهُمْ وَاِيَّاكُمْۗ اِنَّ قَتْلَهُمْ كَانَ خِطْـًٔا كَبِيْرًا ","bc":"wa lā taqtulū aulādakum khasy-yata imlāq, naḥnu narzuquhum wa iyyākum, inna qatlahum kāna khiṭ`ang kabīrā","at":"Dan janganlah kamu membunuh anak-anakmu karena takut miskin. Kamilah yang memberi rezeki kepada mereka dan kepadamu. Membunuh mereka itu sungguh suatu dosa yang besar."},{"id":"32","ar":"وَلَا تَقْرَبُوا الزِّنٰىٓ اِنَّهٗ كَانَ فَاحِشَةً ۗوَسَاۤءَ سَبِيْلًا ","bc":"wa lā taqrabuz-zinā innahụ kāna fāḥisyah, wa sā`a sabīlā","at":"Dan janganlah kamu mendekati zina; (zina) itu sungguh suatu perbuatan keji, dan suatu jalan yang buruk."},{"id":"33","ar":"وَلَا تَقْتُلُوا النَّفْسَ الَّتِيْ حَرَّمَ اللّٰهُ اِلَّا بِالْحَقِّۗ وَمَنْ قُتِلَ مَظْلُوْمًا فَقَدْ جَعَلْنَا لِوَلِيِّهٖ سُلْطٰنًا فَلَا يُسْرِفْ فِّى الْقَتْلِۗ اِنَّهٗ كَانَ مَنْصُوْرًا","bc":"wa lā taqtulun-nafsallatī ḥarramallāhu illā bil-ḥaqq, wa mang qutila maẓlụman fa qad ja'alnā liwaliyyihī sulṭānan fa lā yusrif fil-qatl, innahụ kāna manṣụrā","at":"Dan janganlah kamu membunuh orang yang diharamkan Allah (membunuhnya), kecuali dengan suatu (alasan) yang benar. Dan barang siapa dibunuh secara zalim, maka sungguh, Kami telah memberi kekuasaan kepada walinya, tetapi janganlah walinya itu melampaui batas dalam pembunuhan. Sesungguhnya dia adalah orang yang mendapat pertolongan."},{"id":"34","ar":"وَلَا تَقْرَبُوْا مَالَ الْيَتِيْمِ اِلَّا بِالَّتِيْ هِيَ اَحْسَنُ حَتّٰى يَبْلُغَ اَشُدَّهٗۖ وَاَوْفُوْا بِالْعَهْدِۖ اِنَّ الْعَهْدَ كَانَ مَسْـُٔوْلًا","bc":"wa lā taqrabụ mālal-yatīmi illā billatī hiya aḥsanu ḥattā yabluga asyuddahụ wa aufụ bil-'ahdi innal-'ahda kāna mas`ụlā","at":"Dan janganlah kamu mendekati harta anak yatim, kecuali dengan cara yang lebih baik (bermanfaat) sampai dia dewasa, dan penuhilah janji, karena janji itu pasti diminta pertanggungjawabannya."},{"id":"35","ar":"وَاَوْفُوا الْكَيْلَ اِذَا كِلْتُمْ وَزِنُوْا بِالْقِسْطَاسِ الْمُسْتَقِيْمِۗ ذٰلِكَ خَيْرٌ وَّاَحْسَنُ تَأْوِيْلًا","bc":"wa auful-kaila iżā kiltum wazinụ bil-qisṭāsil-mustaqīm, żālika khairuw wa aḥsanu ta`wīlā","at":"Dan sempurnakanlah takaran apabila kamu menakar, dan timbanglah dengan timbangan yang benar. Itulah yang lebih utama (bagimu) dan lebih baik akibatnya."},{"id":"36","ar":"وَلَا تَقْفُ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ ۗاِنَّ السَّمْعَ وَالْبَصَرَ وَالْفُؤَادَ كُلُّ اُولٰۤىِٕكَ كَانَ عَنْهُ مَسْـُٔوْلًا ","bc":"wa lā taqfu mā laisa laka bihī 'ilm, innas-sam'a wal-baṣara wal-fu`āda kullu ulā`ika kāna 'an-hu mas`ụlā","at":"Dan janganlah kamu mengikuti sesuatu yang tidak kamu ketahui. Karena pendengaran, penglihatan dan hati nurani, semua itu akan diminta pertanggungjawabannya."},{"id":"37","ar":"وَلَا تَمْشِ فِى الْاَرْضِ مَرَحًاۚ اِنَّكَ لَنْ تَخْرِقَ الْاَرْضَ وَلَنْ تَبْلُغَ الْجِبَالَ طُوْلًا","bc":"wa lā tamsyi fil-arḍi maraḥā, innaka lan takhriqal-arḍa wa lan tablugal-jibāla ṭụlā","at":"Dan janganlah engkau berjalan di bumi ini dengan sombong, karena sesungguhnya engkau tidak akan dapat menembus bumi dan tidak akan mampu menjulang setinggi gunung."},{"id":"38","ar":"كُلُّ ذٰلِكَ كَانَ سَيِّئُهٗ عِنْدَ رَبِّكَ مَكْرُوْهًا ","bc":"kullu żālika kāna sayyi`uhụ 'inda rabbika makrụhā","at":"Semua itu kejahatannya sangat dibenci di sisi Tuhanmu."},{"id":"39","ar":"ذٰلِكَ مِمَّآ اَوْحٰٓى اِلَيْكَ رَبُّكَ مِنَ الْحِكْمَةِۗ وَلَا تَجْعَلْ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتُلْقٰى فِيْ جَهَنَّمَ مَلُوْمًا مَّدْحُوْرًا ","bc":"żālika mimmā auḥā ilaika rabbuka minal-ḥikmah, wa lā taj'al ma'allāhi ilāhan ākhara fa tulqā fī jahannama malụmam mad-ḥụrā","at":"Itulah sebagian hikmah yang diwahyukan Tuhan kepadamu (Muhammad). Dan janganlah engkau mengadakan tuhan yang lain di samping Allah, nanti engkau dilemparkan ke dalam neraka dalam keadaan tercela dan dijauhkan (dari rahmat Allah)."},{"id":"40","ar":"اَفَاَصْفٰىكُمْ رَبُّكُمْ بِالْبَنِيْنَ وَاتَّخَذَ مِنَ الْمَلٰۤىِٕكَةِ اِنَاثًاۗ اِنَّكُمْ لَتَقُوْلُوْنَ قَوْلًا عَظِيْمًا ","bc":"a fa aṣfākum rabbukum bil-banīna wattakhaża minal-malā`ikati ināṡā, innakum lataqụlụna qaulan 'aẓīmā","at":"Maka apakah pantas Tuhan memilihkan anak laki-laki untukmu dan Dia mengambil anak perempuan dari malaikat? Sungguh, kamu benar-benar mengucapkan kata yang besar (dosanya)."},{"id":"41","ar":"وَلَقَدْ صَرَّفْنَا فِيْ هٰذَا الْقُرْاٰنِ لِيَذَّكَّرُوْاۗ وَمَا يَزِيْدُهُمْ اِلَّا نُفُوْرًا","bc":"wa laqad ṣarrafnā fī hāżal-qur`āni liyażżakkarụ, wa mā yazīduhum illā nufụrā","at":"Dan sungguh, dalam Al-Qur'an ini telah Kami (jelaskan) berulang-ulang (peringatan), agar mereka selalu ingat. Tetapi (peringatan) itu hanya menambah mereka lari (dari kebenaran)."},{"id":"42","ar":"قُلْ لَّوْ كَانَ مَعَهٗ ٓ اٰلِهَةٌ كَمَا يَقُوْلُوْنَ اِذًا لَّابْتَغَوْا اِلٰى ذِى الْعَرْشِ سَبِيْلًا","bc":"qul lau kāna ma'ahū ālihatung kamā yaqụlụna iżal labtagau ilā żil-'arsyi sabīlā","at":"Katakanlah (Muhammad), “Jika ada tuhan-tuhan di samping-Nya, sebagai-mana yang mereka katakan, niscaya tuhan-tuhan itu mencari jalan kepada Tuhan yang mempunyai ’Arsy.”"},{"id":"43","ar":" سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يَقُوْلُوْنَ عُلُوًّا كَبِيْرًا","bc":"sub-ḥānahụ wa ta'ālā 'ammā yaqụlụna 'uluwwang kabīrā","at":"Mahasuci dan Mahatinggi Dia dari apa yang mereka katakan, luhur dan agung (tidak ada bandingannya)."},{"id":"44","ar":"تُسَبِّحُ لَهُ السَّمٰوٰتُ السَّبْعُ وَالْاَرْضُ وَمَنْ فِيْهِنَّۗ وَاِنْ مِّنْ شَيْءٍ اِلَّا يُسَبِّحُ بِحَمْدِهٖ وَلٰكِنْ لَّا تَفْقَهُوْنَ تَسْبِيْحَهُمْۗ اِنَّهٗ كَانَ حَلِيْمًا غَفُوْرًا","bc":"tusabbiḥu lahus-samāwātus-sab'u wal-arḍu wa man fīhinn, wa im min syai`in illā yusabbiḥu biḥamdihī wa lākil lā tafqahụna tasbīḥahum, innahụ kāna ḥalīman gafụrā","at":"Langit yang tujuh, bumi dan semua yang ada di dalamnya bertasbih kepada Allah. Dan tidak ada sesuatu pun melainkan bertasbih dengan memuji-Nya, tetapi kamu tidak mengerti tasbih mereka. Sungguh, Dia Maha Penyantun, Maha Pengampun."},{"id":"45","ar":"وَاِذَا قَرَأْتَ الْقُرْاٰنَ جَعَلْنَا بَيْنَكَ وَبَيْنَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ حِجَابًا مَّسْتُوْرًاۙ ","bc":"wa iżā qara`tal-qur`āna ja'alnā bainaka wa bainallażīna lā yu`minụna bil-ākhirati ḥijābam mastụrā","at":"Dan apabila engkau (Muhammad) membaca Al-Qur'an, Kami adakan suatu dinding yang tidak terlihat antara engkau dan orang-orang yang tidak beriman kepada kehidupan akhirat,"},{"id":"46","ar":"وَّجَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًاۗ وَاِذَا ذَكَرْتَ رَبَّكَ فِى الْقُرْاٰنِ وَحْدَهٗ وَلَّوْا عَلٰٓى اَدْبَارِهِمْ نُفُوْرًا","bc":"wa ja'alnā 'alā qulụbihim akinnatan ay yafqahụhu wa fī āżānihim waqrā, wa iżā żakarta rabbaka fil-qur`āni waḥdahụ wallau 'alā adbārihim nufụrā","at":"Dan Kami jadikan hati mereka tertutup dan telinga mereka tersumbat, agar mereka tidak dapat memahaminya. Dan apabila engkau menyebut Tuhanmu saja dalam Al-Qur'an, mereka berpaling ke belakang melarikan diri (karena benci)."},{"id":"47","ar":"نَحْنُ اَعْلَمُ بِمَا يَسْتَمِعُوْنَ بِهٖٓ اِذْ يَسْتَمِعُوْنَ اِلَيْكَ وَاِذْ هُمْ نَجْوٰٓى اِذْ يَقُوْلُ الظّٰلِمُوْنَ اِنْ تَتَّبِعُوْنَ اِلَّا رَجُلًا مَّسْحُوْرًا ","bc":"naḥnu a'lamu bimā yastami'ụna bihī iż yastami'ụna ilaika wa iż hum najwā iż yaqụluẓ-ẓālimụna in tattabi'ụna illā rajulam mas-ḥụrā","at":"Kami lebih mengetahui dalam keadaan bagaimana mereka mendengarkan sewaktu mereka mendengarkan engkau (Muhammad), dan sewaktu mereka berbisik-bisik (yaitu) ketika orang zalim itu berkata, “Kamu hanyalah mengikuti seorang laki-laki yang kena sihir.”"},{"id":"48","ar":"اُنْظُرْ كَيْفَ ضَرَبُوْا لَكَ الْاَمْثَالَ فَضَلُّوْا فَلَا يَسْتَطِيْعُوْنَ سَبِيْلًا","bc":"unẓur kaifa ḍarabụ lakal-amṡāla fa ḍallụ fa lā yastaṭī'ụna sabīlā","at":"Lihatlah bagaimana mereka membuat perumpamaan untukmu (Muhammad); karena itu mereka menjadi sesat dan tidak dapat lagi menemukan jalan (yang benar)."},{"id":"49","ar":"وَقَالُوْٓا ءَاِذَا كُنَّا عِظَامًا وَّرُفَاتًا ءَاِنَّا لَمَبْعُوْثُوْنَ خَلْقًا جَدِيْدًا ","bc":"wa qālū a iżā kunnā 'iẓāmaw wa rufātan a innā lamab'ụṡụna khalqan jadīdā","at":"Dan mereka berkata, “Apabila kami telah menjadi tulang-belulang dan benda-benda yang hancur, apakah kami benar-benar akan dibangkitkan kembali sebagai makhluk yang baru?”"},{"id":"50","ar":"۞ قُلْ كُوْنُوْا حِجَارَةً اَوْ حَدِيْدًاۙ ","bc":"qul kụnụ ḥijāratan au ḥadīdā","at":"Katakanlah (Muhammad), “Jadilah kamu batu atau besi,"},{"id":"51","ar":"اَوْ خَلْقًا مِّمَّا يَكْبُرُ فِيْ صُدُوْرِكُمْ ۚفَسَيَقُوْلُوْنَ مَنْ يُّعِيْدُنَاۗ قُلِ الَّذِيْ فَطَرَكُمْ اَوَّلَ مَرَّةٍۗ فَسَيُنْغِضُوْنَ اِلَيْكَ رُءُوْسَهُمْ وَيَقُوْلُوْنَ مَتٰى هُوَۗ قُلْ عَسٰٓى اَنْ يَّكُوْنَ قَرِيْبًا","bc":"au khalqam mimmā yakburu fī ṣudụrikum, fa sayaqụlụna may yu'īdunā, qulillażī faṭarakum awwala marrah, fa sayun-giḍụna ilaika ru`ụsahum wa yaqụlụna matā huw, qul 'asā ay yakụna qarībā","at":"atau menjadi makhluk yang besar (yang tidak mungkin hidup kembali) menurut pikiranmu.” Maka mereka akan bertanya, “Siapa yang akan menghidupkan kami kembali?” Katakanlah, “Yang telah menciptakan kamu pertama kali.” Lalu mereka akan menggeleng-gelengkan kepalanya kepadamu dan berkata, “Kapan (Kiamat) itu (akan terjadi)?” Katakanlah, “Barang kali waktunya sudah dekat,”"},{"id":"52","ar":"يَوْمَ يَدْعُوْكُمْ فَتَسْتَجِيْبُوْنَ بِحَمْدِهٖ وَتَظُنُّوْنَ اِنْ لَّبِثْتُمْ اِلَّا قَلِيْلًا ","bc":"yauma yad'ụkum fa tastajībụna biḥamdihī wa taẓunnụna il labiṡtum illā qalīlā","at":"yaitu pada hari (ketika) Dia memanggil kamu, dan kamu mematuhi-Nya sambil memuji-Nya dan kamu mengira, (rasanya) hanya sebentar saja kamu berdiam (di dalam kubur)."},{"id":"53","ar":"وَقُلْ لِّعِبَادِيْ يَقُوْلُوا الَّتِيْ هِيَ اَحْسَنُۗ اِنَّ الشَّيْطٰنَ يَنْزَغُ بَيْنَهُمْۗ اِنَّ الشَّيْطٰنَ كَانَ لِلْاِنْسَانِ عَدُوًّا مُّبِيْنًا","bc":"wa qul li'ibādī yaqụlullatī hiya aḥsan, innasy-syaiṭāna yanzagu bainahum, innasy-syaiṭāna kāna lil-insāni 'aduwwam mubīnā","at":"Dan katakanlah kepada hamba-hamba-Ku, “Hendaklah mereka mengucapkan perkataan yang lebih baik (benar). Sungguh, setan itu (selalu) menimbulkan perselisihan di antara mereka. Sungguh, setan adalah musuh yang nyata bagi manusia."},{"id":"54","ar":"رَبُّكُمْ اَعْلَمُ بِكُمْ اِنْ يَّشَأْ يَرْحَمْكُمْ اَوْ اِنْ يَّشَأْ يُعَذِّبْكُمْۗ وَمَآ اَرْسَلْنٰكَ عَلَيْهِمْ وَكِيْلًا","bc":"rabbukum a'lamu bikum iy yasya` yar-ḥamkum au iy yasya` yu'ażżibkum, wa mā arsalnāka 'alaihim wakīlā","at":"Tuhanmu lebih mengetahui tentang kamu. Jika Dia menghendaki, niscaya Dia akan memberi rahmat kepadamu, dan jika Dia menghendaki, pasti Dia akan mengazabmu. Dan Kami tidaklah mengutusmu (Muhammad) untuk menjadi penjaga bagi mereka."},{"id":"55","ar":"وَرَبُّكَ اَعْلَمُ بِمَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَلَقَدْ فَضَّلْنَا بَعْضَ النَّبِيّٖنَ عَلٰى بَعْضٍ وَّاٰتَيْنَا دَاوٗدَ زَبُوْرًا","bc":"wa rabbuka a'lamu biman fis-samāwāti wal-arḍ, wa laqad faḍḍalnā ba'ḍan-nabiyyīna 'alā ba'ḍiw wa ātainā dāwụda zabụrā","at":"Dan Tuhanmu lebih mengetahui siapa yang di langit dan di bumi. Dan sungguh, Kami telah memberikan kelebihan kepada sebagian nabi-nabi atas sebagian (yang lain), dan Kami berikan Zabur kepada Dawud."},{"id":"56","ar":"قُلِ ادْعُوا الَّذِيْنَ زَعَمْتُمْ مِّنْ دُوْنِهٖ فَلَا يَمْلِكُوْنَ كَشْفَ الضُّرِّ عَنْكُمْ وَلَا تَحْوِيْلًا","bc":"qulid'ullażīna za'amtum min dụnihī fa lā yamlikụna kasyfaḍ-ḍurri 'angkum wa lā taḥwīlā","at":"Katakanlah (Muhammad), “Panggillah mereka yang kamu anggap (tuhan) selain Allah, mereka tidak kuasa untuk menghilangkan bahaya darimu dan tidak (pula) mampu mengubahnya.”"},{"id":"57","ar":"اُولٰۤىِٕكَ الَّذِيْنَ يَدْعُوْنَ يَبْتَغُوْنَ اِلٰى رَبِّهِمُ الْوَسِيْلَةَ اَيُّهُمْ اَقْرَبُ وَيَرْجُوْنَ رَحْمَتَهٗ وَيَخَافُوْنَ عَذَابَهٗۗ اِنَّ عَذَابَ رَبِّكَ كَانَ مَحْذُوْرًا","bc":"ulā`ikallażīna yad'ụna yabtagụna ilā rabbihimul-wasīlata ayyuhum aqrabu wa yarjụna raḥmatahụ wa yakhāfụna 'ażābah, inna 'ażāba rabbika kāna maḥżụrā","at":"Orang-orang yang mereka seru itu, mereka sendiri mencari jalan kepada Tuhan siapa di antara mereka yang lebih dekat (kepada Allah). Mereka mengharapkan rahmat-Nya dan takut akan azab-Nya. Sungguh, azab Tuhanmu itu sesuatu yang (harus) ditakuti.”"},{"id":"58","ar":"وَاِنْ مِّنْ قَرْيَةٍ اِلَّا نَحْنُ مُهْلِكُوْهَا قَبْلَ يَوْمِ الْقِيٰمَةِ اَوْ مُعَذِّبُوْهَا عَذَابًا شَدِيْدًاۗ كَانَ ذٰلِكَ فىِ الْكِتٰبِ مَسْطُوْرًا","bc":"wa im ming qaryatin illā naḥnu muhlikụhā qabla yaumil-qiyāmati au mu'ażżibụhā 'ażāban syadīdā, kāna żālika fil-kitābi masṭụrā","at":"Dan tidak ada suatu negeri pun (yang durhaka penduduknya), melainkan Kami membinasakannya sebelum hari Kiamat atau Kami siksa (penduduknya) dengan siksa yang sangat keras. Yang demikian itu telah tertulis di dalam Kitab (Lauh Mahfuzh)."},{"id":"59","ar":"وَمَا مَنَعَنَآ اَنْ نُّرْسِلَ بِالْاٰيٰتِ اِلَّآ اَنْ كَذَّبَ بِهَا الْاَوَّلُوْنَۗ وَاٰتَيْنَا ثَمُوْدَ النَّاقَةَ مُبْصِرَةً فَظَلَمُوْا بِهَاۗ وَمَا نُرْسِلُ بِالْاٰيٰتِ اِلَّا تَخْوِيْفًا","bc":"wa mā mana'anā an nursila bil-āyāti illā ang każżaba bihal-awwalụn, wa ātainā ṡamụdan-nāqata mubṣiratan fa ẓalamụ bihā, wa mā nursilu bil-āyāti illā takhwīfā","at":"Dan tidak ada yang menghalangi Kami untuk mengirimkan (kepadamu) tanda-tanda (kekuasaan Kami), melainkan karena (tanda-tanda) itu telah didustakan oleh orang terdahulu. Dan telah Kami berikan kepada kaum samud unta betina (sebagai mukjizat) yang dapat dilihat, tetapi mereka menganiaya (unta betina itu). Dan Kami tidak mengirimkan tanda-tanda itu melainkan untuk menakut-nakuti."},{"id":"60","ar":"وَاِذْ قُلْنَا لَكَ اِنَّ رَبَّكَ اَحَاطَ بِالنَّاسِۗ وَمَا جَعَلْنَا الرُّءْيَا الَّتِيْٓ اَرَيْنٰكَ اِلَّا فِتْنَةً لِّلنَّاسِ وَالشَّجَرَةَ الْمَلْعُوْنَةَ فِى الْقُرْاٰنِ ۗ وَنُخَوِّفُهُمْۙ فَمَا يَزِيْدُهُمْ اِلَّا طُغْيَانًا كَبِيْرًا ","bc":"wa iż qulnā laka inna rabbaka aḥāṭa bin-nās, wa mā ja'alnar-ru`yallatī araināka illā fitnatal lin-nāsi wasy-syajaratal-mal'ụnata fil-qur`ān, wa nukhawwifuhum fa mā yazīduhum illā ṭugyānang kabīrā","at":"Dan (ingatlah) ketika Kami wahyukan kepadamu, “Sungguh, (ilmu) Tuhanmu meliputi seluruh manusia.” Dan Kami tidak menjadikan mimpi yang telah Kami perlihatkan kepadamu, melainkan sebagai ujian bagi manusia dan (begitu pula) pohon yang terkutuk (zaqqum) dalam Al-Qur'an. Dan Kami menakut-nakuti mereka, tetapi yang demikian itu hanyalah menambah besar kedurhakaan mereka."},{"id":"61","ar":"وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ قَالَ ءَاَسْجُدُ لِمَنْ خَلَقْتَ طِيْنًاۚ ","bc":"wa iż qulnā lil-malā`ikatisjudụ li`ādama fa sajadū illā iblīs, qāla a asjudu liman khalaqta ṭīnā","at":"Dan (ingatlah), ketika Kami berfirman kepada para malaikat, “Sujudlah kamu semua kepada Adam,” lalu mereka sujud, kecuali Iblis. Ia (Iblis) berkata, “Apakah aku harus bersujud kepada orang yang Engkau ciptakan dari tanah?”"},{"id":"62","ar":"قَالَ اَرَاَيْتَكَ هٰذَا الَّذِيْ كَرَّمْتَ عَلَيَّ لَىِٕنْ اَخَّرْتَنِ اِلٰى يَوْمِ الْقِيٰمَةِ لَاَحْتَنِكَنَّ ذُرِّيَّتَهٗٓ اِلَّا قَلِيْلًا","bc":"qāla a ra`aitaka hāżallażī karramta 'alayya la`in akhkhartani ilā yaumil-qiyāmati la`aḥtanikanna żurriyyatahū illā qalīlā","at":" Ia (Iblis) berkata, “Terangkanlah kepadaku, inikah yang lebih Engkau muliakan daripada aku? Sekiranya Engkau memberi waktu kepadaku sampai hari Kiamat, pasti akan aku sesatkan keturunannya, kecuali sebagian kecil.”"},{"id":"63","ar":"قَالَ اذْهَبْ فَمَنْ تَبِعَكَ مِنْهُمْ فَاِنَّ جَهَنَّمَ جَزَاۤؤُكُمْ جَزَاۤءً مَّوْفُوْرًا","bc":"qālaż-hab fa man tabi'aka min-hum fa inna jahannama jazā`ukum jazā`am maufụrā","at":"Dia (Allah) berfirman, “Pergilah, tetapi barang siapa di antara mereka yang mengikuti kamu, maka sungguh, neraka Jahanamlah balasanmu semua, sebagai pembalasan yang cukup."},{"id":"64","ar":"وَاسْتَفْزِزْ مَنِ اسْتَطَعْتَ مِنْهُمْ بِصَوْتِكَ وَاَجْلِبْ عَلَيْهِمْ بِخَيْلِكَ وَرَجِلِكَ وَشَارِكْهُمْ فِى الْاَمْوَالِ وَالْاَوْلَادِ وَعِدْهُمْۗ وَمَا يَعِدُهُمُ الشَّيْطٰنُ اِلَّا غُرُوْرًا","bc":"wastafziz manistaṭa'ta min-hum biṣautika wa ajlib 'alaihim bikhailika wa rajilika wa syārik-hum fil-amwāli wal-aulādi wa 'id-hum, wa mā ya'iduhumusy-syaiṭānu illā gurụrā","at":"Dan perdayakanlah siapa saja di antara mereka yang engkau (Iblis) sanggup dengan suaramu (yang memukau), kerahkanlah pasukanmu terhadap mereka, yang berkuda dan yang berjalan kaki, dan bersekutulah dengan mereka pada harta dan anak-anak lalu beri janjilah kepada mereka.” Padahal setan itu hanya menjanjikan tipuan belaka kepada mereka."},{"id":"65","ar":"اِنَّ عِبَادِيْ لَيْسَ لَكَ عَلَيْهِمْ سُلْطٰنٌۗ وَكَفٰى بِرَبِّكَ وَكِيْلًا","bc":"inna 'ibādī laisa laka 'alaihim sulṭān, wa kafā birabbika wakīlā","at":"“Sesungguhnya (terhadap) hamba-hamba-Ku, engkau (Iblis) tidaklah dapat berkuasa atas mereka. Dan cukuplah Tuhanmu sebagai penjaga.”"},{"id":"66","ar":"رَبُّكُمُ الَّذِيْ يُزْجِيْ لَكُمُ الْفُلْكَ فِى الْبَحْرِ لِتَبْتَغُوْا مِنْ فَضْلِهٖۗ اِنَّهٗ كَانَ بِكُمْ رَحِيْمًا","bc":"rabbukumullażī yuzjī lakumul-fulka fil-baḥri litabtagụ min faḍlih, innahụ kāna bikum raḥīmā","at":"Tuhanmulah yang melayarkan kapal-kapal di lautan untukmu, agar kamu mencari karunia-Nya. Sungguh, Dia Maha Penyayang terhadapmu."},{"id":"67","ar":"وَاِذَا مَسَّكُمُ الضُّرُّ فِى الْبَحْرِ ضَلَّ مَنْ تَدْعُوْنَ اِلَّآ اِيَّاهُۚ فَلَمَّا نَجّٰىكُمْ اِلَى الْبَرِّ اَعْرَضْتُمْۗ وَكَانَ الْاِنْسَانُ كَفُوْرًا","bc":"wa iżā massakumuḍ-ḍurru fil-baḥri ḍalla man tad'ụna illā iyyāh, fa lammā najjākum ilal-barri a'raḍtum, wa kānal-insānu kafụrā","at":"Dan apabila kamu ditimpa bahaya di lautan, niscaya hilang semua yang (bi-asa) kamu seru, kecuali Dia. Tetapi ketika Dia menyelamatkan kamu ke daratan, kamu berpaling (dari-Nya). Dan manusia memang selalu ingkar (tidak bersyukur)."},{"id":"68","ar":"اَفَاَمِنْتُمْ اَنْ يَّخْسِفَ بِكُمْ جَانِبَ الْبَرِّ اَوْ يُرْسِلَ عَلَيْكُمْ حَاصِبًا ثُمَّ لَا تَجِدُوْا لَكُمْ وَكِيْلًا ۙ ","bc":"a fa amintum ay yakhsifa bikum jānibal-barri au yursila 'alaikum ḥāṣiban ṡumma lā tajidụ lakum wakīlā","at":"Maka apakah kamu merasa aman bahwa Dia tidak akan membenamkan sebagian daratan bersama kamu atau Dia meniupkan (angin keras yang membawa) batu-batu kecil? Dan kamu tidak akan mendapat seorang pelindung pun,"},{"id":"69","ar":"اَمْ اَمِنْتُمْ اَنْ يُّعِيْدَكُمْ فِيْهِ تَارَةً اُخْرٰى فَيُرْسِلَ عَلَيْكُمْ قَاصِفًا مِّنَ الرِّيْحِ فَيُغْرِقَكُمْ بِمَا كَفَرْتُمْۙ ثُمَّ لَا تَجِدُوْا لَكُمْ عَلَيْنَا بِهٖ تَبِيْعًا","bc":"am amintum ay yu'īdakum fīhi tāratan ukhrā fa yursila 'alaikum qāṣifam minar-rīḥi fa yugriqakum bimā kafartum ṡumma lā tajidụ lakum 'alainā bihī tabī'ā","at":"ataukah kamu merasa aman bahwa Dia tidak akan mengembalikan kamu ke laut sekali lagi, lalu Dia tiupkan angin topan kepada kamu dan ditenggelamkan-Nya kamu disebabkan kekafiranmu? Kemudian kamu tidak akan mendapatkan seorang penolong pun dalam menghadapi (siksaan) Kami. "},{"id":"70","ar":"۞ وَلَقَدْ كَرَّمْنَا بَنِيْٓ اٰدَمَ وَحَمَلْنٰهُمْ فِى الْبَرِّ وَالْبَحْرِ وَرَزَقْنٰهُمْ مِّنَ الطَّيِّبٰتِ وَفَضَّلْنٰهُمْ عَلٰى كَثِيْرٍ مِّمَّنْ خَلَقْنَا تَفْضِيْلًا ","bc":"wa laqad karramnā banī ādama wa ḥamalnāhum fil-barri wal-baḥri wa razaqnāhum minaṭ-ṭayyibāti wa faḍḍalnāhum 'alā kaṡīrim mim man khalaqnā tafḍīlā","at":"Dan sungguh, Kami telah memuliakan anak cucu Adam, dan Kami angkut mereka di darat dan di laut, dan Kami beri mereka rezeki dari yang baik-baik dan Kami lebihkan mereka di atas banyak makhluk yang Kami ciptakan dengan kelebihan yang sempurna."},{"id":"71","ar":"يَوْمَ نَدْعُوْا كُلَّ اُنَاسٍۢ بِاِمَامِهِمْۚ فَمَنْ اُوْتِيَ كِتٰبَهٗ بِيَمِيْنِهٖ فَاُولٰۤىِٕكَ يَقْرَءُوْنَ كِتٰبَهُمْ وَلَا يُظْلَمُوْنَ فَتِيْلًا","bc":"yauma nad'ụ kulla unāsim bi`imāmihim, fa man ụtiya kitābahụ biyamīnihī fa ulā`ika yaqra`ụna kitābahum wa lā yuẓlamụna fatīlā","at":"(Ingatlah), pada hari (ketika) Kami panggil setiap umat dengan pemimpinnya; dan barang siapa diberikan catatan amalnya di tangan kanannya mereka akan membaca catatannya (dengan baik), dan mereka tidak akan dirugikan sedikit pun."},{"id":"72","ar":"وَمَنْ كَانَ فِيْ هٰذِهٖٓ اَعْمٰى فَهُوَ فِى الْاٰخِرَةِ اَعْمٰى وَاَضَلُّ سَبِيْلًا","bc":"wa mang kāna fī hāżihī a'mā fa huwa fil-ākhirati a'mā wa aḍallu sabīlā","at":"Dan barang siapa buta (hatinya) di dunia ini, maka di akhirat dia akan buta dan tersesat jauh dari jalan (yang benar)."},{"id":"73","ar":"وَاِنْ كَادُوْا لَيَفْتِنُوْنَكَ عَنِ الَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ لِتَفْتَرِيَ عَلَيْنَا غَيْرَهٗۖ وَاِذًا لَّاتَّخَذُوْكَ خَلِيْلًا","bc":"wa ing kādụ layaftinụnaka 'anillażī auḥainā ilaika litaftariya 'alainā gairahụ wa iżal lattakhażụka khalīlā","at":"Dan mereka hampir memalingkan engkau (Muhammad) dari apa yang telah Kami wahyukan kepadamu, agar engkau mengada-ada yang lain terhadap Kami; dan jika demikian tentu mereka menjadikan engkau sahabat yang setia."},{"id":"74","ar":"وَلَوْلَآ اَنْ ثَبَّتْنٰكَ لَقَدْ كِدْتَّ تَرْكَنُ اِلَيْهِمْ شَيْـًٔا قَلِيْلًا ۙ ","bc":"walau lā an ṡabbatnāka laqad kitta tarkanu ilaihim syai`ang qalīlā","at":"Dan sekiranya Kami tidak memperteguh (hati)mu, niscaya engkau hampir saja condong sedikit kepada mereka."},{"id":"75","ar":"اِذًا لَّاَذَقْنٰكَ ضِعْفَ الْحَيٰوةِ وَضِعْفَ الْمَمَاتِ ثُمَّ لَا تَجِدُ لَكَ عَلَيْنَا نَصِيْرًا","bc":"iżal la`ażaqnāka ḍi'fal-ḥayāti wa ḍi'fal-mamāti ṡumma lā tajidu laka 'alainā naṣīrā","at":"Jika demikian, tentu akan Kami rasakan kepadamu (siksaan) dua kali lipat di dunia ini dan dua kali lipat setelah mati, dan engkau (Muhammad) tidak akan mendapat seorang penolong pun terhadap Kami."},{"id":"76","ar":"وَاِنْ كَادُوْا لَيَسْتَفِزُّوْنَكَ مِنَ الْاَرْضِ لِيُخْرِجُوْكَ مِنْهَا وَاِذًا لَّا يَلْبَثُوْنَ خِلٰفَكَ اِلَّا قَلِيْلًا","bc":"wa ing kādụ layastafizzụnaka minal-arḍi liyukhrijụka min-hā wa iżal lā yalbaṡụna khilāfaka illā qalīlā","at":"Dan sungguh, mereka hampir membuatmu (Muhammad) gelisah di negeri (Mekah) karena engkau harus keluar dari negeri itu, dan kalau terjadi demikian, niscaya sepeninggalmu mereka tidak akan tinggal (di sana), melainkan sebentar saja."},{"id":"77","ar":"سُنَّةَ مَنْ قَدْ اَرْسَلْنَا قَبْلَكَ مِنْ رُّسُلِنَا وَلَا تَجِدُ لِسُنَّتِنَا تَحْوِيْلًا ","bc":"sunnata mang qad arsalnā qablaka mir rusulinā wa lā tajidu lisunnatinā taḥwīlā","at":"(Yang demikian itu) merupakan ketetapan bagi para rasul Kami yang Kami utus sebelum engkau, dan tidak akan engkau dapati perubahan atas ketetapan Kami."},{"id":"78","ar":"اَقِمِ الصَّلٰوةَ لِدُلُوْكِ الشَّمْسِ اِلٰى غَسَقِ الَّيْلِ وَقُرْاٰنَ الْفَجْرِۗ اِنَّ قُرْاٰنَ الْفَجْرِ كَانَ مَشْهُوْدًا ","bc":"aqimiṣ-ṣalāta lidulụkisy-syamsi ilā gasaqil-laili wa qur`ānal-fajr, inna qur`ānal-fajri kāna masy-hụdā","at":"Laksanakanlah salat sejak matahari tergelincir sampai gelapnya malam dan (laksanakan pula salat) Subuh. Sungguh, salat subuh itu disaksikan (oleh malaikat)."},{"id":"79","ar":"وَمِنَ الَّيْلِ فَتَهَجَّدْ بِهٖ نَافِلَةً لَّكَۖ عَسٰٓى اَنْ يَّبْعَثَكَ رَبُّكَ مَقَامًا مَّحْمُوْدًا","bc":"wa minal-laili fa taḥajjad bihī nāfilatal laka 'asā ay yab'aṡaka rabbuka maqāmam maḥmụdā","at":"Dan pada sebagian malam, lakukanlah salat tahajud (sebagai suatu ibadah) tambahan bagimu: mudah-mudahan Tuhanmu mengangkatmu ke tempat yang terpuji."},{"id":"80","ar":"وَقُلْ رَّبِّ اَدْخِلْنِيْ مُدْخَلَ صِدْقٍ وَّاَخْرِجْنِيْ مُخْرَجَ صِدْقٍ وَّاجْعَلْ لِّيْ مِنْ لَّدُنْكَ سُلْطٰنًا نَّصِيْرًا","bc":"wa qur rabbi adkhilnī mudkhala ṣidqiw wa akhrijnī mukhraja ṣidqiw waj'al lī mil ladungka sulṭānan naṣīrā","at":"Dan katakanlah (Muhammad), ya Tuhanku, masukkan aku ke tempat masuk yang benar dan keluarkan (pula) aku ke tempat keluar yang benar dan berikanlah kepadaku dari sisi-Mu kekuasaan yang dapat menolong(ku)."},{"id":"81","ar":"وَقُلْ جَاۤءَ الْحَقُّ وَزَهَقَ الْبَاطِلُ ۖاِنَّ الْبَاطِلَ كَانَ زَهُوْقًا","bc":"wa qul jā`al-ḥaqqu wa zahaqal-bāṭilu innal-bāṭila kāna zahụqā","at":"Dan katakanlah, “Kebenaran telah datang dan yang batil telah lenyap.” Sungguh, yang batil itu pasti lenyap."},{"id":"82","ar":"وَنُنَزِّلُ مِنَ الْقُرْاٰنِ مَا هُوَ شِفَاۤءٌ وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَۙ وَلَا يَزِيْدُ الظّٰلِمِيْنَ اِلَّا خَسَارًا","bc":"wa nunazzilu minal-qur`āni mā huwa syifā`uw wa raḥmatul lil-mu`minīna wa lā yazīduẓ-ẓālimīna illā khasārā","at":"Dan Kami turunkan dari Al-Qur'an (sesuatu) yang menjadi penawar dan rahmat bagi orang yang beriman, sedangkan bagi orang yang zalim (Al-Qur'an itu) hanya akan menambah kerugian."},{"id":"83","ar":"وَاِذَآ اَنْعَمْنَا عَلَى الْاِنْسَانِ اَعْرَضَ وَنَاٰ بِجَانِبِهٖۚ وَاِذَا مَسَّهُ الشَّرُّ كَانَ يَـُٔوْسًا","bc":"wa iżā an'amnā 'alal-insāni a'raḍa wa na`ā bijānibih, wa iżā massahusy-syarru kāna ya`ụsā","at":"Dan apabila Kami berikan kesenangan kepada manusia, niscaya dia berpaling dan menjauhkan diri dengan sombong; dan apabila dia ditimpa kesusahan, niscaya dia berputus asa."},{"id":"84","ar":"قُلْ كُلٌّ يَّعْمَلُ عَلٰى شَاكِلَتِهٖۗ فَرَبُّكُمْ اَعْلَمُ بِمَنْ هُوَ اَهْدٰى سَبِيْلًا","bc":"qul kulluy ya'malu 'alā syākilatih, fa rabbukum a'lamu biman huwa ahdā sabīlā","at":"Katakanlah (Muhammad), “Setiap orang berbuat sesuai dengan pembawaannya masing-masing.” Maka Tuhanmu lebih mengetahui siapa yang lebih benar jalannya."},{"id":"85","ar":"وَيَسْـَٔلُوْنَكَ عَنِ الرُّوْحِۗ قُلِ الرُّوْحُ مِنْ اَمْرِ رَبِّيْ وَمَآ اُوْتِيْتُمْ مِّنَ الْعِلْمِ اِلَّا قَلِيْلًا ","bc":"wa yas`alụnaka 'anir-rụḥ, qulir-rụḥu min amri rabbī wa mā ụtītum minal-'ilmi illā qalīlā","at":"Dan mereka bertanya kepadamu (Muhammad) tentang ruh. Katakanlah, “Ruh itu termasuk urusan Tuhanku, sedangkan kamu diberi pengetahuan hanya sedikit.”"},{"id":"86","ar":"وَلَىِٕنْ شِئْنَا لَنَذْهَبَنَّ بِالَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ ثُمَّ لَا تَجِدُ لَكَ بِهٖ عَلَيْنَا وَكِيْلًاۙ؉؉","bc":"wa la`in syi`nā lanaż-habanna billażī auḥainā ilaika ṡumma lā tajidu laka bihī 'alainā wakīlā","at":"Dan sesungguhnya jika Kami menghendaki, niscaya Kami lenyapkan apa yang telah Kami wahyukan kepadamu (Muhammad), dan engkau tidak akan mendapatkan seorang pembela pun terhadap Kami,"},{"id":"87","ar":"اِلَّا رَحْمَةً مِّنْ رَّبِّكَۗ اِنَّ فَضْلَهٗ كَانَ عَلَيْكَ كَبِيْرًا","bc":"illā raḥmatam mir rabbik, inna faḍlahụ kāna 'alaika kabīrā","at":"kecuali karena rahmat dari Tuhanmu. Sungguh, karunia-Nya atasmu (Muhammad) sangat besar. "},{"id":"88","ar":"قُلْ لَّىِٕنِ اجْتَمَعَتِ الْاِنْسُ وَالْجِنُّ عَلٰٓى اَنْ يَّأْتُوْا بِمِثْلِ هٰذَا الْقُرْاٰنِ لَا يَأْتُوْنَ بِمِثْلِهٖ وَلَوْ كَانَ بَعْضُهُمْ لِبَعْضٍ ظَهِيْرًا","bc":"qul la`inijtama'atil-insu wal-jinnu 'alā ay ya`tụ bimiṡli hāżal-qur`āni lā ya`tụna bimiṡlihī walau kāna ba'ḍuhum liba'ḍin ẓahīrā","at":"Katakanlah, “Sesungguhnya jika manusia dan jin berkumpul untuk membuat yang serupa (dengan) Al-Qur'an ini, mereka tidak akan dapat membuat yang serupa dengannya, sekalipun mereka saling membantu satu sama lain.”"},{"id":"89","ar":"وَلَقَدْ صَرَّفْنَا لِلنَّاسِ فِيْ هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۖ فَاَبٰىٓ اَكْثَرُ النَّاسِ اِلَّا كُفُوْرًا","bc":"wa laqad ṣarrafnā lin-nāsi fī hāżal-qur`āni ming kulli maṡalin fa abā akṡarun-nāsi illā kufụrā","at":" Dan sungguh, Kami telah menjelaskan berulang-ulang kepada manusia dalam Al-Qur'an ini dengan bermacam-macam perumpamaan, tetapi kebanyakan manusia tidak menyukainya bahkan mengingkari(nya)."},{"id":"90","ar":"وَقَالُوْا لَنْ نُّؤْمِنَ لَكَ حَتّٰى تَفْجُرَ لَنَا مِنَ الْاَرْضِ يَنْۢبُوْعًاۙ ","bc":"wa qālụ lan nu`mina laka ḥattā tafjura lanā minal-arḍi yambụ'ā","at":"Dan mereka berkata, “Kami tidak akan percaya kepadamu (Muhammad) sebelum engkau memancarkan mata air dari bumi untuk kami,"},{"id":"91","ar":"اَوْ تَكُوْنَ لَكَ جَنَّةٌ مِّنْ نَّخِيْلٍ وَّعِنَبٍ فَتُفَجِّرَ الْاَنْهٰرَ خِلٰلَهَا تَفْجِيْرًاۙ ","bc":"au takụna laka jannatum min nakhīliw wa 'inabin fa tufajjiral-an-hāra khilālahā tafjīrā","at":"atau engkau mempunyai sebuah kebun kurma dan anggur, lalu engkau alirkan di celah-celahnya sungai yang deras alirannya,"},{"id":"92","ar":"اَوْ تُسْقِطَ السَّمَاۤءَ كَمَا زَعَمْتَ عَلَيْنَا كِسَفًا اَوْ تَأْتِيَ بِاللّٰهِ وَالْمَلٰۤىِٕكَةِ قَبِيْلًاۙ ","bc":"au tusqiṭas-samā`a kamā za'amta 'alainā kisafan au ta`tiya billāhi wal-malā`ikati qabīlā","at":"atau engkau jatuhkan langit berkeping-keping atas kami, sebagaimana engkau katakan, atau (sebelum) engkau datangkan Allah dan para malaikat berhadapan muka dengan kami,"},{"id":"93","ar":"اَوْ يَكُوْنَ لَكَ بَيْتٌ مِّنْ زُخْرُفٍ اَوْ تَرْقٰى فِى السَّمَاۤءِ ۗوَلَنْ نُّؤْمِنَ لِرُقِيِّكَ حَتّٰى تُنَزِّلَ عَلَيْنَا كِتٰبًا نَّقْرَؤُهٗۗ قُلْ سُبْحَانَ رَبِّيْ هَلْ كُنْتُ اِلَّا بَشَرًا رَّسُوْلًا ","bc":"au yakụna laka baitum min zukhrufin au tarqā fis-samā`, wa lan nu`mina liruqiyyika ḥattā tunazzila 'alainā kitāban naqra`uh, qul sub-ḥāna rabbī hal kuntu illā basyarar rasụlā","at":"atau engkau mempunyai sebuah rumah (terbuat) dari emas, atau engkau naik ke langit. Dan kami tidak akan mempercayai kenaikanmu itu sebelum engkau turunkan kepada kami sebuah kitab untuk kami baca.” Katakanlah (Muhammad), “Mahasuci Tuhanku, bukankah aku ini hanya seorang manusia yang menjadi rasul?”"},{"id":"94","ar":"وَمَا مَنَعَ النَّاسَ اَنْ يُّؤْمِنُوْٓا اِذْ جَاۤءَهُمُ الْهُدٰٓى اِلَّآ اَنْ قَالُوْٓا اَبَعَثَ اللّٰهُ بَشَرًا رَّسُوْلًا","bc":"wa mā mana'an-nāsa ay yu`minū iż jā`ahumul-hudā illā ang qālū a ba'aṡallāhu basyarar rasụlā","at":"Dan tidak ada sesuatu yang menghalangi manusia untuk beriman ketika petunjuk datang kepadanya, selain perkataan mereka, “Mengapa Allah mengutus seorang manusia menjadi rasul?”"},{"id":"95","ar":"قُلْ لَّوْ كَانَ فِى الْاَرْضِ مَلٰۤىِٕكَةٌ يَّمْشُوْنَ مُطْمَىِٕنِّيْنَ لَنَزَّلْنَا عَلَيْهِمْ مِّنَ السَّمَاۤءِ مَلَكًا رَّسُوْلًا","bc":"qul lau kāna fil-arḍi malā`ikatuy yamsyụna muṭma`innīna lanazzalnā 'alaihim minas-samā`i malakar rasụlā","at":"Katakanlah (Muhammad), “Sekiranya di bumi ada para malaikat, yang berjalan-jalan dengan tenang, niscaya Kami turunkan kepada mereka malaikat dari langit untuk menjadi rasul.”"},{"id":"96","ar":"قُلْ كَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنِيْ وَبَيْنَكُمْۗ اِنَّهٗ كَانَ بِعِبَادِهٖ خَبِيْرًاۢ بَصِيْرًا","bc":"qul kafā billāhi syahīdam bainī wa bainakum, innahụ kāna bi'ibādihī khabīram baṣīrā","at":"Katakanlah (Muhammad), “Cukuplah Allah menjadi saksi antara aku dan kamu sekalian. Sungguh, Dia Maha Mengetahui, Maha Melihat akan hamba-hamba-Nya.”"},{"id":"97","ar":"وَمَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِۚ وَمَنْ يُّضْلِلْ فَلَنْ تَجِدَ لَهُمْ اَوْلِيَاۤءَ مِنْ دُوْنِهٖۗ وَنَحْشُرُهُمْ يَوْمَ الْقِيٰمَةِ عَلٰى وُجُوْهِهِمْ عُمْيًا وَّبُكْمًا وَّصُمًّاۗ مَأْوٰىهُمْ جَهَنَّمُۗ كُلَّمَا خَبَتْ زِدْنٰهُمْ سَعِيْرًا","bc":"wa may yahdillāhu fa huwal-muhtad, wa may yuḍlil fa lan tajida lahum auliyā`a min dụnih, wa naḥsyuruhum yaumal-qiyāmati 'alā wujụhihim 'umyaw wa bukmaw wa ṣummā, ma`wāhum jahannam, kullamā khabat zidnāhum sa'īrā","at":"Dan barang siapa diberi petunjuk oleh Allah, dialah yang mendapat petunjuk, dan barang siapa Dia sesatkan, maka engkau tidak akan mendapatkan penolong-penolong bagi mereka selain Dia. Dan Kami akan mengumpulkan mereka pada hari Kiamat dengan wajah tersungkur, dalam keadaan buta, bisu, dan tuli. Tempat kediaman mereka adalah neraka Jahanam. Setiap kali nyala api Jahanam itu akan padam, Kami tambah lagi nyalanya bagi mereka."},{"id":"98","ar":"ذٰلِكَ جَزَاۤؤُهُمْ بِاَنَّهُمْ كَفَرُوْا بِاٰيٰتِنَا وَقَالُوْٓا ءَاِذَا كُنَّا عِظَامًا وَّرُفَاتًا ءَاِنَّا لَمَبْعُوْثُوْنَ خَلْقًا جَدِيْدًا ","bc":"żālika jazā`uhum bi`annahum kafarụ bi`āyātinā wa qālū a iżā kunnā 'iẓāmaw wa rufātan a innā lamab'ụṡụna khalqan jadīdā","at":"Itulah balasan bagi mereka, karena sesungguhnya mereka kafir kepada ayat-ayat Kami dan (karena mereka) berkata, “Apabila kami telah menjadi tulang belulang dan benda-benda yang hancur, apakah kami benar-benar akan dibangkitkan kembali sebagai makhluk baru?”"},{"id":"99","ar":"۞ اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ قَادِرٌ عَلٰٓى اَنْ يَّخْلُقَ مِثْلَهُمْ وَجَعَلَ لَهُمْ اَجَلًا لَّا رَيْبَ فِيْهِۗ فَاَبَى الظّٰلِمُوْنَ اِلَّا كُفُوْرًا","bc":"a wa lam yarau annallāhallażī khalaqas-samāwāti wal-arḍa qādirun 'alā ay yakhluqa miṡlahum wa ja'ala lahum ajalal lā raiba fīh, fa abaẓ-ẓālimụna illā kufụrā","at":"Dan apakah mereka tidak memperhatikan bahwa Allah yang menciptakan langit dan bumi adalah Mahakuasa (pula) menciptakan yang serupa dengan mereka, dan Dia telah menetapkan waktu tertentu (mati atau dibangkitkan) bagi mereka, yang tidak diragukan lagi? Maka orang zalim itu tidak menolaknya kecuali dengan kekafiran."},{"id":"100","ar":"قُلْ لَّوْ اَنْتُمْ تَمْلِكُوْنَ خَزَاۤىِٕنَ رَحْمَةِ رَبِّيْٓ اِذًا لَّاَمْسَكْتُمْ خَشْيَةَ الْاِنْفَاقِۗ وَكَانَ الْاِنْسَانُ قَتُوْرًا","bc":"qul lau antum tamlikụna khazā`ina raḥmati rabbī iżal la`amsaktum khasy-yatal-infāq, wa kānal-insānu qatụrā","at":"Katakanlah (Muhammad), “Sekiranya kamu menguasai perbendaharaan rahmat Tuhanku, niscaya (perbendaharaan) itu kamu tahan, karena takut membelanjakannya.” Dan manusia itu memang sangat kikir."},{"id":"101","ar":"وَلَقَدْ اٰتَيْنَا مُوْسٰى تِسْعَ اٰيٰتٍۢ بَيِّنٰتٍ فَسْـَٔلْ بَنِيْٓ اِسْرَاۤءِيْلَ اِذْ جَاۤءَهُمْ فَقَالَ لَهٗ فِرْعَوْنُ اِنِّيْ لَاَظُنُّكَ يٰمُوْسٰى مَسْحُوْرًا ","bc":"wa laqad ātainā mụsā tis'a āyātim bayyinātin fas`al banī isrā`īla iż jā`ahum fa qāla lahụ fir'aunu innī la`aẓunnuka yā mụsā mas-ḥụrā","at":"Dan sungguh, Kami telah memberikan kepada Musa sembilan mukjizat yang nyata maka tanyakanlah kepada Bani Israil, ketika Musa datang kepada mereka lalu Fir‘aun berkata kepadanya, “Wahai Musa! Sesungguhnya aku benar-benar menduga engkau terkena sihir.”"},{"id":"102","ar":"قَالَ لَقَدْ عَلِمْتَ مَآ اَنْزَلَ هٰٓؤُلَاۤءِ اِلَّا رَبُّ السَّمٰوٰتِ وَالْاَرْضِ بَصَاۤىِٕرَۚ وَاِنِّيْ لَاَظُنُّكَ يٰفِرْعَوْنُ مَثْبُوْرًا ","bc":"qāla laqad 'alimta mā anzala hā`ulā`i illā rabbus-samāwāti wal-arḍi baṣā`ir, wa innī la`aẓunnuka yā fir'aunu maṡbụrā","at":"Dia (Musa) menjawab, ”Sungguh, engkau telah mengetahui, bahwa tidak ada yang menurunkan (mukjizat-mukjizat) itu kecuali Tuhan (yang memelihara) langit dan bumi sebagai bukti-bukti yang nyata; dan sungguh, aku benar-benar menduga engkau akan binasa, wahai Fir‘aun.”"},{"id":"103","ar":"فَاَرَادَ اَنْ يَّسْتَفِزَّهُمْ مِّنَ الْاَرْضِ فَاَغْرَقْنٰهُ وَمَنْ مَّعَهٗ جَمِيْعًاۙ ","bc":"fa arāda ay yastafizzahum minal-arḍi fa agraqnāhu wa mam ma'ahụ jamī'ā","at":"Kemudian dia (Fir‘aun) hendak mengusir mereka (Musa dan pengikutnya) dari bumi (Mesir), maka Kami tenggelamkan dia (Fir‘aun) beserta orang yang bersama dia seluruhnya."},{"id":"104","ar":"وَّقُلْنَا مِنْۢ بَعْدِهٖ لِبَنِيْٓ اِسْرَاۤءِيْلَ اسْكُنُوا الْاَرْضَ فَاِذَا جَاۤءَ وَعْدُ الْاٰخِرَةِ جِئْنَا بِكُمْ لَفِيْفًاۗ ","bc":"wa qulnā mim ba'dihī libanī isrā`īlaskunul-arḍa fa iżā jā`a wa'dul-ākhirati ji`nā bikum lafīfā","at":"Dan setelah itu Kami berfirman kepada Bani Israil, “Tinggallah di negeri ini, tetapi apabila masa berbangkit datang, niscaya Kami kumpulkan kamu dalam keadaan bercampur baur.”"},{"id":"105","ar":"وَبِالْحَقِّ اَنْزَلْنٰهُ وَبِالْحَقِّ نَزَلَۗ وَمَآ اَرْسَلْنٰكَ اِلَّا مُبَشِّرًا وَّنَذِيْرًاۘ","bc":"wa bil-ḥaqqi anzalnāhu wa bil-ḥaqqi nazal, wa mā arsalnāka illā mubasysyiraw wa nażīrā","at":"Dan Kami turunkan (Al-Qur'an) itu dengan sebenarnya dan (Al-Qur'an) itu turun dengan (membawa) kebenaran. Dan Kami mengutus engkau (Muhammad), hanya sebagai pembawa berita gembira dan pemberi peringatan."},{"id":"106","ar":"وَقُرْاٰنًا فَرَقْنٰهُ لِتَقْرَاَهٗ عَلَى النَّاسِ عَلٰى مُكْثٍ وَّنَزَّلْنٰهُ تَنْزِيْلًا ","bc":"wa qur`ānan faraqnāhu litaqra`ahụ 'alan-nāsi 'alā mukṡiw wa nazzalnāhu tanzīlā","at":"Dan Al-Qur'an (Kami turunkan) berangsur-angsur agar engkau (Muhammad) membacakannya kepada manusia perlahan-lahan dan Kami menurunkannya secara bertahap."},{"id":"107","ar":"قُلْ اٰمِنُوْا بِهٖٓ اَوْ لَا تُؤْمِنُوْاۗ اِنَّ الَّذِيْنَ اُوْتُوا الْعِلْمَ مِنْ قَبْلِهٖٓ اِذَا يُتْلٰى عَلَيْهِمْ يَخِرُّوْنَ لِلْاَذْقَانِ سُجَّدًاۙ","bc":"qul āminụ bihī au lā tu`minụ, innallażīna ụtul-'ilma ming qablihī iżā yutlā 'alaihim yakhirrụna lil-ażqāni sujjadā","at":"Katakanlah (Muhammad), “Berimanlah kamu kepadanya (Al-Qur'an) atau tidak usah beriman (sama saja bagi Allah). Sesungguhnya orang yang telah diberi pengetahuan sebelumnya, apabila (Al-Qur'an) dibacakan kepada mereka, mereka menyungkurkan wajah, bersujud,”"},{"id":"108","ar":"وَّيَقُوْلُوْنَ سُبْحٰنَ رَبِّنَآ اِنْ كَانَ وَعْدُ رَبِّنَا لَمَفْعُوْلًا","bc":"wa yaqụlụna sub-ḥāna rabbinā ing kāna wa'du rabbinā lamaf'ụlā","at":"dan mereka berkata, “Mahasuci Tuhan kami; sungguh, janji Tuhan kami pasti dipenuhi.” "},{"id":"109","ar":"وَيَخِرُّوْنَ لِلْاَذْقَانِ يَبْكُوْنَ وَيَزِيْدُهُمْ خُشُوْعًا ۩","bc":"wa yakhirrụna lil-ażqāni yabkụna wa yazīduhum khusyụ'ā","at":"Dan mereka menyungkurkan wajah sambil menangis dan mereka bertambah khusyuk."},{"id":"110","ar":"قُلِ ادْعُوا اللّٰهَ اَوِ ادْعُوا الرَّحْمٰنَۗ اَيًّا مَّا تَدْعُوْا فَلَهُ الْاَسْمَاۤءُ الْحُسْنٰىۚ وَلَا تَجْهَرْ بِصَلَاتِكَ وَلَا تُخَافِتْ بِهَا وَابْتَغِ بَيْنَ ذٰلِكَ سَبِيْلًا","bc":"qulid'ullāha awid'ur-raḥmān, ayyam mā tad'ụ fa lahul-asmā`ul-ḥusnā, wa lā taj-har biṣalātika wa lā tukhāfit bihā wabtagi baina żālika sabīlā","at":"Katakanlah (Muhammad), “Serulah Allah atau serulah Ar-Rahman. Dengan nama yang mana saja kamu dapat menyeru, karena Dia mempunyai nama-nama yang terbaik (Asma‘ul husna) dan janganlah engkau mengeraskan suaramu dalam salat dan janganlah (pula) merendahkannya dan usahakan jalan tengah di antara kedua itu.”"},{"id":"111","ar":"وَقُلِ الْحَمْدُ لِلّٰهِ الَّذِيْ لَمْ يَتَّخِذْ وَلَدًا وَّلَمْ يَكُنْ لَّهٗ شَرِيْكٌ فِى الْمُلْكِ وَلَمْ يَكُنْ لَّهٗ وَلِيٌّ مِّنَ الذُّلِّ وَكَبِّرْهُ تَكْبِيْرًا","bc":"wa qulil-ḥamdu lillāhillażī lam yattakhiż waladaw wa lam yakul lahụ syarīkun fil-mulki wa lam yakul lahụ waliyyum minaż-żulli wa kabbir-hu takbīrā","at":"Dan katakanlah, “Segala puji bagi Allah yang tidak mempunyai anak dan tidak (pula) mempunyai sekutu dalam kerajaan-Nya dan Dia tidak memerlukan penolong dari kehinaan dan agungkanlah Dia seagung-agungnya."}],al_jasiyah=[{"id":"1","ar":"حٰمۤ ","bc":"ḥā mīm","at":"Ha Mim"},{"id":"2","ar":"تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِ","bc":"tanzīlul-kitābi minallāhil-'azīzil-ḥakīm","at":"Kitab (ini) diturunkan dari Allah Yang Mahaperkasa, Mahabijaksana."},{"id":"3","ar":"اِنَّ فِى السَّمٰوٰتِ وَالْاَرْضِ لَاٰيٰتٍ لِّلْمُؤْمِنِيْنَۗ","bc":"inna fis-samāwāti wal-arḍi la`āyātil lil-mu`minīn","at":"Sungguh, pada langit dan bumi benar-benar terdapat tanda-tanda (kebesaran Allah) bagi orang-orang mukmin."},{"id":"4","ar":"وَفِيْ خَلْقِكُمْ وَمَا يَبُثُّ مِنْ دَاۤبَّةٍ اٰيٰتٌ لِّقَوْمٍ يُّوْقِنُوْنَۙ","bc":"wa fī khalqikum wa mā yabuṡṡu min dābbatin āyātul liqaumiy yụqinụn","at":"Dan pada penciptaan dirimu dan pada makhluk bergerak yang bernyawa yang bertebaran (di bumi) terdapat tanda-tanda (kebesaran Allah) untuk kaum yang meyakini,"},{"id":"5","ar":"وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَمَآ اَنْزَلَ اللّٰهُ مِنَ السَّمَاۤءِ مِنْ رِّزْقٍ فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَا وَتَصْرِيْفِ الرِّيٰحِ اٰيٰتٌ لِّقَوْمٍ يَّعْقِلُوْنَ","bc":"wakhtilāfil-laili wan-nahāri wa mā anzalallāhu minas-samā`i mir rizqin fa aḥyā bihil-arḍa ba'da mautihā wa taṣrīfir-riyāḥi āyātul liqaumiy ya'qilụn","at":"dan pada pergantian malam dan siang dan hujan yang diturunkan Allah dari langit lalu dengan (air hujan) itu dihidupkan-Nya bumi setelah mati (kering); dan pada perkisaran angin terdapat pula tanda-tanda (kebesaran Allah) bagi kaum yang mengerti."},{"id":"6","ar":"تِلْكَ اٰيٰتُ اللّٰهِ نَتْلُوْهَا عَلَيْكَ بِالْحَقِّۚ فَبِاَيِّ حَدِيْثٍۢ بَعْدَ اللّٰهِ وَاٰيٰتِهٖ يُؤْمِنُوْنَ","bc":"tilka āyātullāhi natlụhā 'alaika bil-ḥaqq, fa bi`ayyi ḥadīṡim ba'dallāhi wa āyātihī yu`minụn","at":"Itulah ayat-ayat Allah yang Kami bacakan kepadamu dengan sebenarnya; maka dengan perkataan mana lagi mereka akan beriman setelah Allah dan ayat-ayat-Nya."},{"id":"7","ar":"وَيْلٌ لِّكُلِّ اَفَّاكٍ اَثِيْمٍۙ","bc":"wailul likulli affākin aṡīm","at":"Celakalah bagi setiap orang yang banyak berdusta lagi banyak berdosa,"},{"id":"8","ar":"يَّسْمَعُ اٰيٰتِ اللّٰهِ تُتْلٰى عَلَيْهِ ثُمَّ يُصِرُّ مُسْتَكْبِرًا كَاَنْ لَّمْ يَسْمَعْهَاۚ فَبَشِّرْهُ بِعَذَابٍ اَلِيْمٍ","bc":"yasma'u āyātillāhi tutlā 'alaihi ṡumma yuṣirru mustakbirang ka`al lam yasma'hā, fa basysyir-hu bi'ażābin alīm","at":"(yaitu) orang yang mendengar ayat-ayat Allah ketika dibacakan kepadanya namun dia tetap menyombongkan diri seakan-akan dia tidak mendengarnya. Maka peringatkanlah dia dengan azab yang pedih."},{"id":"9","ar":"وَاِذَا عَلِمَ مِنْ اٰيٰتِنَا شَيْـًٔا ۨاتَّخَذَهَا هُزُوًاۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ مُّهِيْنٌۗ ","bc":"wa iżā 'alima min āyātinā syai`anittakhażahā huzuwā, ulā`ika lahum 'ażābum muhīn","at":"Dan apabila dia mengetahui sedikit tentang ayat-ayat Kami, maka (ayat-ayat itu) dijadikan olok-olok. Merekalah yang akan menerima azab yang menghinakan."},{"id":"10","ar":"مِنْ وَّرَاۤىِٕهِمْ جَهَنَّمُ ۚوَلَا يُغْنِيْ عَنْهُمْ مَّا كَسَبُوْا شَيْـًٔا وَّلَا مَا اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اَوْلِيَاۤءَۚ وَلَهُمْ عَذَابٌ عَظِيْمٌۗ ","bc":"miw warā`ihim jahannam, wa lā yugnī 'an-hum mā kasabụ syai`aw wa lā mattakhażụ min dụnillāhi auliyā`, wa lahum 'ażābun 'aẓīm","at":"Di hadapan mereka neraka Jahanam dan tidak akan berguna bagi mereka sedikit pun apa yang telah mereka kerjakan, dan tidak pula (bermanfaat) apa yang mereka jadikan sebagai pelindung-pelindung (mereka) selain Allah. Dan mereka akan mendapat azab yang besar."},{"id":"11","ar":"هٰذَا هُدًىۚ وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِ رَبِّهِمْ لَهُمْ عَذَابٌ مِّنْ رِّجْزٍ اَلِيْمٌ","bc":"hāżā hudā, wallażīna kafarụ bi`āyāti rabbihim lahum 'ażābum mir rijzin alīm","at":"Ini (Al-Qur'an) adalah petunjuk. Dan orang-orang yang mengingkari ayat-ayat Tuhannya mereka akan mendapat azab berupa siksaan yang sangat pedih."},{"id":"12","ar":"۞ اَللّٰهُ الَّذِيْ سَخَّرَ لَكُمُ الْبَحْرَ لِتَجْرِيَ الْفُلْكُ فِيْهِ بِاَمْرِهٖ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَۚ ","bc":"allāhullażī sakhkhara lakumul-baḥra litajriyal-fulku fīhi bi`amrihī wa litabtagụ min faḍlihī wa la'allakum tasykurụn","at":"Allah-lah yang menundukkan laut untukmu agar kapal-kapal dapat berlayar di atasnya dengan perintah-Nya, dan agar kamu dapat mencari sebagian karunia-Nya dan agar kamu bersyukur."},{"id":"13","ar":"وَسَخَّرَ لَكُمْ مَّا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ جَمِيْعًا مِّنْهُ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ ","bc":"wa sakhkhara lakum mā fis-samāwāti wa mā fil-arḍi jamī'am min-h, inna fī żālika la`āyātil liqaumiy yatafakkarụn","at":"Dan Dia menundukkan apa yang ada di langit dan apa yang ada di bumi untukmu semuanya (sebagai rahmat) dari-Nya. Sungguh, dalam hal yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang berpikir."},{"id":"14","ar":"قُلْ لِّلَّذِيْنَ اٰمَنُوْا يَغْفِرُوْا لِلَّذِيْنَ لَا يَرْجُوْنَ اَيَّامَ اللّٰهِ لِيَجْزِيَ قَوْمًا ۢبِمَا كَانُوْا يَكْسِبُوْنَ ","bc":"qul lillażīna āmanụ yagfirụ lillażīna lā yarjụna ayyāmallāhi liyajziya qaumam bimā kānụ yaksibụn","at":"Katakanlah (Muhammad) kepada orang-orang yang beriman hendaklah mereka memaafkan orang-orang yang tidak takut akan hari-hari Allah karena Dia akan membalas suatu kaum sesuai dengan apa yang telah mereka kerjakan."},{"id":"15","ar":"مَنْ عَمِلَ صَالِحًا فَلِنَفْسِهٖۚ وَمَنْ اَسَاۤءَ فَعَلَيْهَا ۖ ثُمَّ اِلٰى رَبِّكُمْ تُرْجَعُوْنَ","bc":"man 'amila ṣāliḥan fa linafsih, wa man asā`a fa 'alaihā ṡumma ilā rabbikum turja'ụn","at":"Barangsiapa mengerjakan kebajikan maka itu adalah untuk dirinya sendiri, dan barangsiapa mengerjakan kejahatan, maka itu akan menimpa dirinya sendiri, kemudian kepada Tuhanmu kamu dikembalikan."},{"id":"16","ar":"وَلَقَدْ اٰتَيْنَا بَنِيْٓ اِسْرَاۤءِيْلَ الْكِتٰبَ وَالْحُكْمَ وَالنُّبُوَّةَ وَرَزَقْنٰهُمْ مِّنَ الطَّيِّبٰتِ وَفَضَّلْنٰهُمْ عَلَى الْعٰلَمِيْنَ ۚ ","bc":"wa laqad ātainā banī isrā`īlal-kitāba wal-ḥukma wan-nubuwwata wa razaqnāhum minaṭ-ṭayyibāti wa faḍḍalnāhum 'alal-'ālamīn","at":"Dan sungguh, kepada Bani Israil telah Kami berikan Kitab (Taurat), kekuasaan dan kenabian, Kami anugerahkan kepada mereka rezeki yang baik dan Kami lebihkan mereka atas bangsa-bangsa (pada masa itu)."},{"id":"17","ar":"وَاٰتَيْنٰهُمْ بَيِّنٰتٍ مِّنَ الْاَمْرِۚ فَمَا اخْتَلَفُوْٓا اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْعِلْمُ بَغْيًاۢ بَيْنَهُمْ ۗاِنَّ رَبَّكَ يَقْضِيْ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ","bc":"wa ātaināhum bayyinātim minal-amr, fa makhtalafū illā mim ba'di mā jā`ahumul-'ilmu bagyam bainahum, inna rabbaka yaqḍī bainahum yaumal-qiyāmati fīmā kānụ fīhi yakhtalifụn","at":"Dan Kami berikan kepada mereka keterangan-keterangan yang jelas tentang urusan (agama); maka mereka tidak berselisih kecuali setelah datang ilmu kepada mereka, karena kedengkian (yang ada) di antara mereka. Sungguh, Tuhanmu akan memberi putusan kepada mereka pada hari Kiamat terhadap apa yang selalu mereka perselisihkan."},{"id":"18","ar":"ثُمَّ جَعَلْنٰكَ عَلٰى شَرِيْعَةٍ مِّنَ الْاَمْرِ فَاتَّبِعْهَا وَلَا تَتَّبِعْ اَهْوَاۤءَ الَّذِيْنَ لَا يَعْلَمُوْنَ","bc":"ṡumma ja'alnāka 'alā syarī'atim minal-amri fattabi'hā wa lā tattabi' ahwā`allażīna lā ya'lamụn","at":"Kemudian Kami jadikan engkau (Muhammad) mengikuti syariat (peraturan) dari agama itu, maka ikutilah (syariat itu) dan janganlah engkau ikuti keinginan orang-orang yang tidak mengetahui."},{"id":"19","ar":"اِنَّهُمْ لَنْ يُّغْنُوْا عَنْكَ مِنَ اللّٰهِ شَيْـًٔا ۗوَاِنَّ الظّٰلِمِيْنَ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۚ وَاللّٰهُ وَلِيُّ الْمُتَّقِيْنَ ","bc":"innahum lay yugnụ 'angka minallāhi syai`ā, wa innaẓ-ẓālimīna ba'ḍuhum auliyā`u ba'ḍ, wallāhu waliyyul-muttaqīn","at":"Sungguh, mereka tidak akan dapat menghindarkan engkau sedikit pun dari (azab) Allah. Dan sungguh, orang-orang yang zalim itu sebagian menjadi pelindung atas sebagian yang lain, sedangkan Allah pelindung bagi orang-orang yang bertakwa."},{"id":"20","ar":"هٰذَا بَصَاۤىِٕرُ لِلنَّاسِ وَهُدًى وَّرَحْمَةٌ لِّقَوْمٍ يُّوْقِنُوْنَ ","bc":"hāżā baṣā`iru lin-nāsi wa hudaw wa raḥmatul liqaumiy yụqinụn","at":"(Al-Qur'an) ini adalah pedoman bagi manusia, petunjuk dan rahmat bagi kaum yang meyakini."},{"id":"21","ar":"اَمْ حَسِبَ الَّذِيْنَ اجْتَرَحُوا السَّيِّاٰتِ اَنْ نَّجْعَلَهُمْ كَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَوَاۤءً مَّحْيَاهُمْ وَمَمَاتُهُمْ ۗسَاۤءَ مَا يَحْكُمُوْنَࣖ","bc":"am ḥasiballażīnajtaraḥus-sayyi`āti an naj'alahum kallażīna āmanụ wa 'amiluṣ-ṣāliḥāti sawā`am maḥyāhum wa mamātuhum, sā`a mā yaḥkumụn","at":"Apakah orang-orang yang melakukan kejahatan itu mengira bahwa Kami akan memperlakukan mereka seperti orang-orang yang beriman dan yang mengerjakan kebajikan, yaitu sama dalam kehidupan dan kematian mere-ka? Alangkah buruknya penilaian mereka itu."},{"id":"22","ar":"وَخَلَقَ اللّٰهُ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّ وَلِتُجْزٰى كُلُّ نَفْسٍۢ بِمَا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ","bc":"wa khalaqallāhus-samāwāti wal-arḍa bil-ḥaqqi wa litujzā kullu nafsim bimā kasabat wa hum lā yuẓlamụn","at":"Dan Allah menciptakan langit dan bumi dengan tujuan yang benar dan agar setiap jiwa diberi balasan sesuai dengan apa yang dikerjakannya, dan mereka tidak akan dirugikan."},{"id":"23","ar":"اَفَرَءَيْتَ مَنِ اتَّخَذَ اِلٰهَهٗ هَوٰىهُ وَاَضَلَّهُ اللّٰهُ عَلٰى عِلْمٍ وَّخَتَمَ عَلٰى سَمْعِهٖ وَقَلْبِهٖ وَجَعَلَ عَلٰى بَصَرِهٖ غِشٰوَةًۗ فَمَنْ يَّهْدِيْهِ مِنْۢ بَعْدِ اللّٰهِ ۗ اَفَلَا تَذَكَّرُوْنَ","bc":"a fa ra`aita manittakhaża ilāhahụ hawāhu wa aḍallahullāhu 'alā 'ilmiw wa khatama 'alā sam'ihī wa qalbihī wa ja'ala 'alā baṣarihī gisyāwah, fa may yahdīhi mim ba'dillāh, a fa lā tażakkarụn","at":"Maka pernahkah kamu melihat orang yang menjadikan hawa nafsunya sebagai tuhannya dan Allah membiarkannya sesat dengan sepengetahuan-Nya, dan Allah telah mengunci pendengaran dan hatinya serta meletakkan tutup atas penglihatannya? Maka siapa yang mampu memberinya petunjuk setelah Allah (membiarkannya sesat?) Mengapa kamu tidak mengambil pelajaran?"},{"id":"24","ar":"وَقَالُوْا مَا هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا نَمُوْتُ وَنَحْيَا وَمَا يُهْلِكُنَآ اِلَّا الدَّهْرُۚ وَمَا لَهُمْ بِذٰلِكَ مِنْ عِلْمٍۚ اِنْ هُمْ اِلَّا يَظُنُّوْنَ","bc":"wa qālụ mā hiya illā ḥayātunad-dun-yā namụtu wa naḥyā wa mā yuhlikunā illad-dahr, wa mā lahum biżālika min 'ilm, in hum illā yaẓunnụn","at":"Dan mereka berkata, “Kehidupan ini tidak lain hanyalah kehidupan di dunia saja, kita mati dan kita hidup, dan tidak ada yang membinasakan kita selain masa.” Tetapi mereka tidak mempunyai ilmu tentang itu, mereka hanyalah menduga-duga saja."},{"id":"25","ar":"وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ مَّا كَانَ حُجَّتَهُمْ اِلَّآ اَنْ قَالُوا ائْتُوْا بِاٰبَاۤىِٕنَآ اِنْ كُنْتُمْ صٰدِقِيْنَ","bc":"wa iżā tutlā 'alaihim āyātunā bayyinātim mā kāna ḥujjatahum illā ang qālu`tụ bi`ābā`inā ing kuntum ṣādiqīn","at":"Dan apabila kepada mereka dibacakan ayat-ayat Kami yang jelas, tidak ada bantahan mereka selain mengatakan, “Hidupkanlah kembali nenek moyang kami, jika kamu orang yang benar.”"},{"id":"26","ar":"قُلِ اللّٰهُ يُحْيِيْكُمْ ثُمَّ يُمِيْتُكُمْ ثُمَّ يَجْمَعُكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَارَيْبَ فِيْهِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ","bc":"qulillāhu yuḥyīkum ṡumma yumītukum ṡumma yajma'ukum ilā yaumil-qiyāmati lā raiba fīhi wa lākinna akṡaran-nāsi lā ya'lamụn","at":"Katakanlah, “Allah yang menghidupkan kemudian mematikan kamu, setelah itu mengumpulkan kamu pada hari Kiamat yang tidak diragukan lagi; tetapi kebanyakan manusia tidak mengetahui.”"},{"id":"27","ar":"وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ وَيَوْمَ تَقُوْمُ السَّاعَةُ يَوْمَىِٕذٍ يَّخْسَرُ الْمُبْطِلُوْنَ","bc":"wa lillāhi mulkus-samāwāti wal-arḍ, wa yauma taqụmus-sā'atu yauma`iżiy yakhsarul-mubṭilụn","at":"Dan milik Allah kerajaan langit dan bumi. Dan pada hari terjadinya Kiamat, akan rugilah pada hari itu orang-orang yang mengerjakan kebatilan (dosa)."},{"id":"28","ar":"وَتَرٰى كُلَّ اُمَّةٍ جَاثِيَةً ۗ كُلُّ اُمَّةٍ تُدْعٰٓى اِلٰى كِتٰبِهَاۗ اَلْيَوْمَ تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُوْنَ","bc":"wa tarā kulla ummatin jāṡiyah, kullu ummatin tud'ā ilā kitābihā, al-yauma tujzauna mā kuntum ta'malụn","at":"Dan (pada hari itu) engkau akan melihat setiap umat berlutut. Setiap umat dipanggil untuk (melihat) buku catatan amalnya. Pada hari itu kamu diberi balasan atas apa yang telah kamu kerjakan."},{"id":"29","ar":"هٰذَا كِتٰبُنَا يَنْطِقُ عَلَيْكُمْ بِالْحَقِّ ۗاِنَّا كُنَّا نَسْتَنْسِخُ مَا كُنْتُمْ تَعْمَلُوْنَ","bc":"hāżā kitābunā yanṭiqu 'alaikum bil-ḥaqq, innā kunnā nastansikhu mā kuntum ta'malụn","at":"(Allah berfirman), “Inilah Kitab (catatan) Kami yang menuturkan kepadamu dengan sebenar-benarnya. Sesungguhnya Kami telah menyuruh mencatat apa yang telah kamu kerjakan.”"},{"id":"30","ar":"فَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَيُدْخِلُهُمْ رَبُّهُمْ فِيْ رَحْمَتِهٖۗ ذٰلِكَ هُوَ الْفَوْزُ الْمُبِيْنُ","bc":"fa ammallażīna āmanụ wa 'amiluṣ-ṣāliḥāti fa yudkhiluhum rabbuhum fī raḥmatih, żālika huwal-fauzul-mubīn","at":"Maka adapun orang-orang yang beriman dan mengerjakan kebajikan, maka Tuhan memasukkan mereka ke dalam rahmat-Nya (surga). Demikian itulah kemenangan yang nyata."},{"id":"31","ar":"وَاَمَّا الَّذِيْنَ كَفَرُوْاۗ اَفَلَمْ تَكُنْ اٰيٰتِيْ تُتْلٰى عَلَيْكُمْ فَاسْتَكْبَرْتُمْ وَكُنْتُمْ قَوْمًا مُّجْرِمِيْنَ","bc":"wa ammallażīna kafarụ, a fa lam takun āyātī tutlā 'alaikum fastakbartum wa kuntum qaumam mujrimīn","at":"Dan adapun (kepada) orang-orang yang kafir (difirmankan), “Bukankah ayat-ayat-Ku telah dibacakan kepadamu tetapi kamu menyombongkan diri dan kamu menjadi orang-orang yang berbuat dosa?”"},{"id":"32","ar":"وَاِذَا قِيْلَ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّالسَّاعَةُ لَا رَيْبَ فِيْهَا قُلْتُمْ مَّا نَدْرِيْ مَا السَّاعَةُۙ اِنْ نَّظُنُّ اِلَّا ظَنًّا وَّمَا نَحْنُ بِمُسْتَيْقِنِيْنَ ","bc":"wa iżā qīla inna wa'dallāhi ḥaqquw was-sā'atu lā raiba fīhā qultum mā nadrī mas-sā'atu in naẓunnu illā ẓannaw wa mā naḥnu bimustaiqinīn","at":"Dan apabila dikatakan (kepadamu), “Sungguh, janji Allah itu benar, dan hari Kiamat itu tidak diragukan adanya,” kamu menjawab, “Kami tidak tahu apakah hari Kiamat itu, kami hanyalah menduga-duga saja, dan kami tidak yakin.”"},{"id":"33","ar":"وَبَدَا لَهُمْ سَيِّاٰتُ مَا عَمِلُوْا وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ","bc":"wa badā lahum sayyi`ātu mā 'amilụ wa ḥāqa bihim mā kānụ bihī yastahzi`ụn","at":"Dan nyatalah bagi mereka keburukan-keburukan yang mereka kerjakan, dan berlakulah (azab) terhadap mereka yang dahulu mereka perolok-olokkan."},{"id":"34","ar":"وَقِيْلَ الْيَوْمَ نَنْسٰىكُمْ كَمَا نَسِيْتُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَاۙ وَمَأْوٰىكُمُ النَّارُ وَمَا لَكُمْ مِّنْ نّٰصِرِيْنَ","bc":"wa qīlal-yauma nansākum kamā nasītum liqā`a yaumikum hāżā wa ma`wākumun-nāru wa mā lakum min nāṣirīn","at":"Dan kepada mereka dikatakan, “Pada hari ini Kami melupakan kamu sebagaimana kamu telah melupakan pertemuan (dengan) harimu ini; dan tempat kembalimu ialah neraka dan se-kali-kali tidak akan ada penolong bagimu."},{"id":"35","ar":"ذٰلِكُمْ بِاَنَّكُمُ اتَّخَذْتُمْ اٰيٰتِ اللّٰهِ هُزُوًا وَّغَرَّتْكُمُ الْحَيٰوةُ الدُّنْيَا ۚفَالْيَوْمَ لَا يُخْرَجُوْنَ مِنْهَا وَلَا هُمْ يُسْتَعْتَبُوْنَ","bc":"żālikum bi`annakumuttakhażtum āyātillāhi huzuwaw wa garratkumul-ḥayātud-dun-yā, fal-yauma lā yukhrajụna min-hā wa lā hum yusta'tabụn","at":"Yang demikian itu, karena sesungguhnya kamu telah menjadikan ayat-ayat Allah sebagai olok-olokan dan kamu telah ditipu oleh kehidupan dunia.” Maka pada hari ini mereka tidak dikeluarkan dari neraka dan tidak pula mereka diberi kesempatan untuk bertobat."},{"id":"36","ar":"فَلِلّٰهِ الْحَمْدُ رَبِّ السَّمٰوٰتِ وَرَبِّ الْاَرْضِ رَبِّ الْعٰلَمِيْنَ","bc":"fa lillāhil-ḥamdu rabbis-samāwāti wa rabbil-arḍi rabbil-'ālamīn","at":"Segala puji hanya bagi Allah, Tuhan (pemilik) langit dan bumi, Tuhan seluruh alam."},{"id":"37","ar":"وَلَهُ الْكِبْرِيَاۤءُ فِى السَّمٰوٰتِ وَالْاَرْضِ ۗوَهُوَ الْعَزِيْزُ الْحَكِيْمُ ۔","bc":"wa lahul-kibriyā`u fis-samāwāti wal-arḍ, wa huwal-'azīzul-ḥakīm","at":"Dan hanya bagi-Nya segala keagungan di langit dan di bumi, dan Dialah Yang Mahaperkasa, Mahabijaksana."}],al_jinn=[{"id":"1","ar":"قُلْ اُوْحِيَ اِلَيَّ اَنَّهُ اسْتَمَعَ نَفَرٌ مِّنَ الْجِنِّ فَقَالُوْٓا اِنَّا سَمِعْنَا قُرْاٰنًا عَجَبًاۙ","bc":"qul ụḥiya ilayya annahustama'a nafarum minal-jinni fa qālū innā sami'nā qur`ānan 'ajabā","at":"Katakanlah (Muhammad), “Telah diwahyukan kepadaku bahwa sekumpulan jin telah mendengarkan (bacaan),” lalu mereka berkata, “Kami telah mendengarkan bacaan yang menakjubkan (Al-Qur'an),"},{"id":"2","ar":"يَّهْدِيْٓ اِلَى الرُّشْدِ فَاٰمَنَّا بِهٖۗ وَلَنْ نُّشْرِكَ بِرَبِّنَآ اَحَدًاۖ","bc":"yahdī ilar-rusydi fa āmannā bih, wa lan nusyrika birabbinā aḥadā","at":"(yang) memberi petunjuk kepada jalan yang benar, lalu kami beriman kepadanya. Dan kami sekali-kali tidak akan mempersekutukan sesuatu pun dengan Tuhan kami, "},{"id":"3","ar":"وَّاَنَّهٗ تَعٰلٰى جَدُّ رَبِّنَا مَا اتَّخَذَ صَاحِبَةً وَّلَا وَلَدًاۖ","bc":"wa annahụ ta'ālā jaddu rabbinā mattakhaża ṣāḥibataw wa lā waladā","at":"dan sesungguhnya Mahatinggi keagungan Tuhan kami, Dia tidak beristri dan tidak beranak.”"},{"id":"4","ar":"وَّاَنَّهٗ كَانَ يَقُوْلُ سَفِيْهُنَا عَلَى اللّٰهِ شَطَطًاۖ","bc":"wa annahụ kāna yaqụlu safīhunā 'alallāhi syaṭaṭā","at":"Dan sesungguhnya orang yang bodoh di antara kami dahulu selalu mengucapkan (perkataan) yang melampaui batas terhadap Allah,"},{"id":"5","ar":"وَّاَنَّا ظَنَنَّآ اَنْ لَّنْ تَقُوْلَ الْاِنْسُ وَالْجِنُّ عَلَى اللّٰهِ كَذِبًاۙ","bc":"wa annā ẓanannā al lan taqụlal-insu wal-jinnu 'alallāhi każibā","at":"dan sesungguhnya kami mengira, bahwa manusia dan jin itu tidak akan mengatakan perkataan yang dusta terhadap Allah, "},{"id":"6","ar":"وَّاَنَّهٗ كَانَ رِجَالٌ مِّنَ الْاِنْسِ يَعُوْذُوْنَ بِرِجَالٍ مِّنَ الْجِنِّ فَزَادُوْهُمْ رَهَقًاۖ","bc":"wa annahụ kāna rijālum minal-insi ya'ụżụna birijālim minal-jinni fa zādụhum rahaqā","at":"dan sesungguhnya ada beberapa orang laki-laki dari kalangan manusia yang meminta perlindungan kepada beberapa laki-laki dari jin, tetapi mereka (jin) menjadikan mereka (manusia) bertambah sesat. "},{"id":"7","ar":"وَّاَنَّهُمْ ظَنُّوْا كَمَا ظَنَنْتُمْ اَنْ لَّنْ يَّبْعَثَ اللّٰهُ اَحَدًاۖ","bc":"wa annahum ẓannụ kamā ẓanantum al lay yab'aṡallāhu aḥadā","at":"Dan sesungguhnya mereka (jin) mengira seperti kamu (orang musyrik Mekah) yang juga mengira bahwa Allah tidak akan membangkitkan kembali siapa pun (pada hari Kiamat). "},{"id":"8","ar":"وَّاَنَّا لَمَسْنَا السَّمَاۤءَ فَوَجَدْنٰهَا مُلِئَتْ حَرَسًا شَدِيْدًا وَّشُهُبًاۖ","bc":"wa annā lamasnas-samā`a fa wajadnāhā muli`at ḥarasan syadīdaw wa syuhubā","at":"Dan sesungguhnya kami (jin) telah mencoba mengetahui (rahasia) langit, maka kami mendapatinya penuh dengan penjagaan yang kuat dan panah-panah api, "},{"id":"9","ar":"وَّاَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَاعِدَ لِلسَّمْعِۗ فَمَنْ يَّسْتَمِعِ الْاٰنَ يَجِدْ لَهٗ شِهَابًا رَّصَدًاۖ","bc":"wa annā kunnā naq'udu min-hā maqā'ida lis-sam', fa may yastami'il-āna yajid lahụ syihābar raṣadā","at":"dan sesungguhnya kami (jin) dahulu dapat menduduki beberapa tempat di langit itu untuk mencuri dengar (berita-beritanya). Tetapi sekarang siapa (mencoba) mencuri dengar (seperti itu) pasti akan menjumpai panah-panah api yang mengintai (untuk membakarnya)."},{"id":"10","ar":"وَّاَنَّا لَا نَدْرِيْٓ اَشَرٌّ اُرِيْدَ بِمَنْ فِى الْاَرْضِ اَمْ اَرَادَ بِهِمْ رَبُّهُمْ رَشَدًاۙ","bc":"wa annā lā nadrī asyarrun urīda biman fil-arḍi am arāda bihim rabbuhum rasyadā","at":"Dan sesungguhnya kami (jin) tidak mengetahui (adanya penjagaan itu) apakah keburukan yang dikehendaki orang yang di bumi ataukah Tuhan mereka menghendaki kebaikan baginya. "},{"id":"11","ar":"وَّاَنَّا مِنَّا الصّٰلِحُوْنَ وَمِنَّا دُوْنَ ذٰلِكَۗ كُنَّا طَرَاۤىِٕقَ قِدَدًاۙ","bc":"wa annā minnaṣ-ṣāliḥụna wa minnā dụna żālik, kunnā ṭarā`iqa qidadā","at":"Dan sesungguhnya di antara kami (jin) ada yang saleh dan ada (pula) kebalikannya. Kami menempuh jalan yang berbeda-beda. "},{"id":"12","ar":"وَّاَنَّا ظَنَنَّآ اَنْ لَّنْ نُّعْجِزَ اللّٰهَ فِى الْاَرْضِ وَلَنْ نُّعْجِزَهٗ هَرَبًاۖ","bc":"wa annā ẓanannā al lan nu'jizallāha fil-arḍi wa lan nu'jizahụ harabā","at":"Dan sesungguhnya kami (jin) telah menduga, bahwa kami tidak akan mampu melepaskan diri (dari kekuasaan) Allah di bumi dan tidak (pula) dapat lari melepaskan diri (dari)-Nya. "},{"id":"13","ar":"وَّاَنَّا لَمَّا سَمِعْنَا الْهُدٰىٓ اٰمَنَّا بِهٖۗ فَمَنْ يُّؤْمِنْۢ بِرَبِّهٖ فَلَا يَخَافُ بَخْسًا وَّلَا رَهَقًاۖ","bc":"wa annā lammā sami'nal-hudā āmannā bih, fa may yu`mim birabbihī fa lā yakhāfu bakhsaw wa lā rahaqā","at":"Dan sesungguhnya ketika kami (jin) mendengar petunjuk (Al-Qur'an), kami beriman kepadanya. Maka barangsiapa beriman kepada Tuhan, maka tidak perlu ia takut rugi atau berdosa. "},{"id":"14","ar":"وَّاَنَّا مِنَّا الْمُسْلِمُوْنَ وَمِنَّا الْقَاسِطُوْنَۗ فَمَنْ اَسْلَمَ فَاُولٰۤىِٕكَ تَحَرَّوْا رَشَدًا ","bc":"wa annā minnal-muslimụna wa minnal-qāsiṭụn, fa man aslama fa ulā`ika taḥarrau rasyadā","at":"Dan di antara kami ada yang Islam dan ada yang menyimpang dari kebenaran. Siapa yang Islam, maka mereka itu telah memilih jalan yang lurus. "},{"id":"15","ar":"وَاَمَّا الْقَاسِطُوْنَ فَكَانُوْا لِجَهَنَّمَ حَطَبًاۙ","bc":"wa ammal-qāsiṭụna fa kānụ lijahannama ḥaṭabā","at":"Dan adapun yang menyimpang dari kebenaran, maka mereka menjadi bahan bakar bagi neraka Jahanam.” "},{"id":"16","ar":"وَّاَنْ لَّوِ اسْتَقَامُوْا عَلَى الطَّرِيْقَةِ لَاَسْقَيْنٰهُمْ مَّاۤءً غَدَقًاۙ","bc":"wa al lawistaqāmụ 'alaṭ-ṭarīqati la`asqaināhum mā`an gadaqā","at":"Dan sekiranya mereka tetap berjalan lurus di atas jalan itu (agama Islam), niscaya Kami akan mencurahkan kepada mereka air yang cukup."},{"id":"17","ar":"لِّنَفْتِنَهُمْ فِيْهِۗ وَمَنْ يُّعْرِضْ عَنْ ذِكْرِ رَبِّهٖ يَسْلُكْهُ عَذَابًا صَعَدًاۙ","bc":"linaftinahum fīh, wa may yu'riḍ 'an żikri rabbihī yasluk-hu 'ażāban ṣa'adā","at":"Dengan (cara) itu Kami hendak menguji mereka. Dan barangsiapa berpaling dari peringatan Tuhannya, niscaya akan dimasukkan-Nya ke dalam azab yang sangat berat. "},{"id":"18","ar":"وَّاَنَّ الْمَسٰجِدَ لِلّٰهِ فَلَا تَدْعُوْا مَعَ اللّٰهِ اَحَدًاۖ","bc":"wa annal-masājida lillāhi fa lā tad'ụ ma'allāhi aḥadā","at":"Dan sesungguhnya masjid-masjid itu adalah untuk Allah. Maka janganlah kamu menyembah apa pun di dalamnya selain Allah. "},{"id":"19","ar":"وَّاَنَّهٗ لَمَّا قَامَ عَبْدُ اللّٰهِ يَدْعُوْهُ كَادُوْا يَكُوْنُوْنَ عَلَيْهِ لِبَدًاۗ ","bc":"wa annahụ lammā qāma 'abdullāhi yad'ụhu kādụ yakụnụna 'alaihi libadā","at":"Dan sesungguhnya ketika hamba Allah (Muhammad) berdiri menyembah-Nya (melaksanakan salat), mereka (jin-jin) itu berdesakan mengerumuninya."},{"id":"20","ar":"قُلْ اِنَّمَآ اَدْعُوْا رَبِّيْ وَلَآ اُشْرِكُ بِهٖٓ اَحَدًا ","bc":"qul innamā ad'ụ rabbī wa lā usyriku bihī aḥadā","at":"Katakanlah (Muhammad), “Sesungguhnya aku hanya menyembah Tuhanku dan aku tidak mempersekutukan sesuatu pun dengan-Nya.” "},{"id":"21","ar":"قُلْ اِنِّيْ لَآ اَمْلِكُ لَكُمْ ضَرًّا وَّلَا رَشَدًا ","bc":"qul innī lā amliku lakum ḍarraw wa lā rasyadā","at":"Katakanlah (Muhammad), “Aku tidak kuasa menolak mudarat maupun mendatangkan kebaikan kepadamu.” "},{"id":"22","ar":"قُلْ اِنِّيْ لَنْ يُّجِيْرَنِيْ مِنَ اللّٰهِ اَحَدٌ ەۙ وَّلَنْ اَجِدَ مِنْ دُوْنِهٖ مُلْتَحَدًا ۙ","bc":"qul innī lay yujīranī minallāhi aḥaduw wa lan ajida min dụnihī multaḥadā","at":"Katakanlah (Muhammad), “Sesungguhnya tidak ada sesuatu pun yang dapat melindungiku dari (azab) Allah dan aku tidak akan memperoleh tempat berlindung selain dari-Nya. "},{"id":"23","ar":"اِلَّا بَلٰغًا مِّنَ اللّٰهِ وَرِسٰلٰتِهٖۗ وَمَنْ يَّعْصِ اللّٰهَ وَرَسُوْلَهٗ فَاِنَّ لَهٗ نَارَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ","bc":"illā balāgam minallāhi wa risālātih, wa may ya'ṣillāha wa rasụlahụ fa inna lahụ nāra jahannama khālidīna fīhā abadā","at":"(Aku hanya) menyampaikan (peringatan) dari Allah dan risalah-Nya. Dan barangsiapa mendurhakai Allah dan Rasul-Nya maka sesungguhnya dia akan mendapat (azab) neraka Jahanam, mereka kekal di dalamnya selama-lamanya.” "},{"id":"24","ar":"حَتّٰىٓ اِذَا رَاَوْا مَا يُوْعَدُوْنَ فَسَيَعْلَمُوْنَ مَنْ اَضْعَفُ نَاصِرًا وَّاَقَلُّ عَدَدًاۗ","bc":"ḥattā iżā ra`au mā yụ'adụna fa saya'lamụna man aḍ'afu nāṣiraw wa aqallu 'adadā","at":"Sehingga apabila mereka melihat (azab) yang diancamkan kepadanya, maka mereka akan mengetahui siapakah yang lebih lemah penolongnya dan lebih sedikit jumlahnya. "},{"id":"25","ar":"قُلْ اِنْ اَدْرِيْٓ اَقَرِيْبٌ مَّا تُوْعَدُوْنَ اَمْ يَجْعَلُ لَهٗ رَبِّيْٓ اَمَدًا ","bc":"qul in adrī a qarībum mā tụ'adụna am yaj'alu lahụ rabbī amadā","at":"Katakanlah (Muhammad), “Aku tidak mengetahui, apakah azab yang diancamkan kepadamu itu sudah dekat ataukah Tuhanku menetapkan waktunya masih lama.” "},{"id":"26","ar":"عٰلِمُ الْغَيْبِ فَلَا يُظْهِرُ عَلٰى غَيْبِهٖٓ اَحَدًاۙ","bc":"'ālimul-gaibi fa lā yuẓ-hiru 'alā gaibihī aḥadā","at":"Dia Mengetahui yang gaib, tetapi Dia tidak memperlihatkan kepada siapa pun tentang yang gaib itu. "},{"id":"27","ar":"اِلَّا مَنِ ارْتَضٰى مِنْ رَّسُوْلٍ فَاِنَّهٗ يَسْلُكُ مِنْۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهٖ رَصَدًاۙ","bc":"illā manirtaḍā mir rasụlin fa innahụ yasluku mim baini yadaihi wa min khalfihī raṣadā","at":"Kecuali kepada rasul yang diridai-Nya, maka sesungguhnya Dia mengadakan penjaga-penjaga (malaikat) di depan dan di belakangnya. "},{"id":"28","ar":"لِّيَعْلَمَ اَنْ قَدْ اَبْلَغُوْا رِسٰلٰتِ رَبِّهِمْ وَاَحَاطَ بِمَا لَدَيْهِمْ وَاَحْصٰى كُلَّ شَيْءٍ عَدَدًا","bc":"liya'lama ang qad ablagụ risālāti rabbihim wa aḥāṭa bimā ladaihim wa aḥṣā kulla syai`in 'adadā","at":"Agar Dia mengetahui bahwa rasul-rasul itu sungguh telah menyampaikan risalah Tuhannya, sedang (ilmu-Nya) meliputi apa yang ada pada mereka, dan Dia menghitung segala sesuatu satu persatu. "}],al_jumuah=[{"id":"1","ar":"يُسَبِّحُ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ الْمَلِكِ الْقُدُّوْسِ الْعَزِيْزِ الْحَكِيْمِ ","bc":"yusabbiḥu lillāhi mā fis-samāwāti wa mā fil-arḍil-malikil-quddụsil-'azīzil-ḥakīm","at":"Apa yang ada di langit dan apa yang ada di bumi senantiasa bertasbih kepada Allah. Maharaja, Yang Mahasuci, Yang Mahaperkasa, Mahabijaksana."},{"id":"2","ar":"هُوَ الَّذِيْ بَعَثَ فِى الْاُمِّيّٖنَ رَسُوْلًا مِّنْهُمْ يَتْلُوْا عَلَيْهِمْ اٰيٰتِهٖ وَيُزَكِّيْهِمْ وَيُعَلِّمُهُمُ الْكِتٰبَ وَالْحِكْمَةَ وَاِنْ كَانُوْا مِنْ قَبْلُ لَفِيْ ضَلٰلٍ مُّبِيْنٍۙ ","bc":"huwallażī ba'aṡa fil-ummiyyīna rasụlam min-hum yatlụ 'alaihim āyātihī wa yuzakkīhim wa yu'allimuhumul-kitāba wal-ḥikmata wa ing kānụ ming qablu lafī ḍalālim mubīn","at":"Dialah yang mengutus seorang Rasul kepada kaum yang buta huruf dari kalangan mereka sendiri, yang membacakan kepada mereka ayat-ayat-Nya, menyucikan (jiwa) mereka dan mengajarkan kepada mereka Kitab dan Hikmah (Sunnah), meskipun sebelumnya, mereka benar-benar dalam kesesatan yang nyata."},{"id":"3","ar":"وَّاٰخَرِيْنَ مِنْهُمْ لَمَّا يَلْحَقُوْا بِهِمْۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُۙ ","bc":"wa ākharīna min-hum lammā yal-ḥaqụ bihim, wa huwal-'azīzul-ḥakīm","at":"Dan (juga) kepada kaum yang lain dari mereka yang belum berhubungan dengan mereka. Dan Dialah Yang Mahaperkasa, Mahabijaksana. "},{"id":"4","ar":"ذٰلِكَ فَضْلُ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُۗ وَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ","bc":"żālika faḍlullāhi yu`tīhi may yasyā`, wallāhu żul-faḍlil-'aẓīm","at":"Demikianlah karunia Allah, yang diberikan kepada siapa yang Dia kehendaki; dan Allah memiliki karunia yang besar. "},{"id":"5","ar":"مَثَلُ الَّذِيْنَ حُمِّلُوا التَّوْرٰىةَ ثُمَّ لَمْ يَحْمِلُوْهَا كَمَثَلِ الْحِمَارِ يَحْمِلُ اَسْفَارًاۗ بِئْسَ مَثَلُ الْقَوْمِ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِ اللّٰهِ ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ","bc":"maṡalullażīna ḥummilut-taurāta ṡumma lam yaḥmilụhā kamaṡalil-ḥimāri yaḥmilu asfārā, bi`sa maṡalul-qaumillażīna każżabụ bi`āyātillāh, wallāhu lā yahdil-qaumaẓ-ẓālimīn","at":"Perumpamaan orang-orang yang diberi tugas membawa Taurat, kemudian mereka tidak membawanya (tidak mengamalkannya) adalah seperti keledai yang membawa kitab-kitab yang tebal. Sangat buruk perumpamaan kaum yang mendustakan ayat-ayat Allah. Dan Allah tidak memberi petunjuk kepada orang-orang yang zalim. "},{"id":"6","ar":"قُلْ يٰٓاَيُّهَا الَّذِيْنَ هَادُوْٓا اِنْ زَعَمْتُمْ اَنَّكُمْ اَوْلِيَاۤءُ لِلّٰهِ مِنْ دُوْنِ النَّاسِ فَتَمَنَّوُا الْمَوْتَ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"qul yā ayyuhallażīna hādū in za'amtum annakum auliyā`u lillāhi min dụnin-nāsi fa tamannawul-mauta ing kuntum ṣādiqīn","at":"Katakanlah (Muhammad), “Wahai orang-orang Yahudi! Jika kamu mengira bahwa kamulah kekasih Allah, bukan orang-orang yang lain, maka harapkanlah kematianmu, jika kamu orang yang benar.” "},{"id":"7","ar":"وَلَا يَتَمَنَّوْنَهٗٓ اَبَدًاۢ بِمَا قَدَّمَتْ اَيْدِيْهِمْۗ وَاللّٰهُ عَلِيْمٌۢ بِالظّٰلِمِيْنَ ","bc":"wa lā yatamannaunahū abadam bimā qaddamat aidīhim, wallāhu 'alīmum biẓ-ẓālimīn","at":"Dan mereka tidak akan mengharapkan kematian itu selamanya disebabkan kejahatan yang telah mereka perbuat dengan tangan mereka sendiri. Dan Allah Maha Mengetahui orang-orang yang zalim. "},{"id":"8","ar":"قُلْ اِنَّ الْمَوْتَ الَّذِيْ تَفِرُّوْنَ مِنْهُ فَاِنَّهٗ مُلٰقِيْكُمْ ثُمَّ تُرَدُّوْنَ اِلٰى عَالِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ ","bc":"qul innal-mautallażī tafirrụna min-hu fa innahụ mulāqīkum ṡumma turaddụna ilā 'ālimil-gaibi wasy-syahādati fa yunabbi`ukum bimā kuntum ta'malụn","at":"Katakanlah, “Sesungguhnya kematian yang kamu lari dari padanya, ia pasti menemui kamu, kemudian kamu akan dikembalikan kepada (Allah), yang mengetahui yang gaib dan yang nyata, lalu Dia beritakan kepadamu apa yang telah kamu kerjakan.” "},{"id":"9","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا نُوْدِيَ لِلصَّلٰوةِ مِنْ يَّوْمِ الْجُمُعَةِ فَاسْعَوْا اِلٰى ذِكْرِ اللّٰهِ وَذَرُوا الْبَيْعَۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ","bc":"yā ayyuhallażīna āmanū iżā nụdiya liṣ-ṣalāti miy yaumil-jumu'ati fas'au ilā żikrillāhi wa żarul baī', żālikum khairul lakum ing kuntum ta'lamụn","at":"Wahai orang-orang yang beriman! Apabila telah diseru untuk melaksanakan salat pada hari Jum‘at, maka segeralah kamu mengingat Allah dan tinggalkanlah jual beli. Yang demikian itu lebih baik bagimu jika kamu mengetahui."},{"id":"10","ar":"فَاِذَا قُضِيَتِ الصَّلٰوةُ فَانْتَشِرُوْا فِى الْاَرْضِ وَابْتَغُوْا مِنْ فَضْلِ اللّٰهِ وَاذْكُرُوا اللّٰهَ كَثِيْرًا لَّعَلَّكُمْ تُفْلِحُوْنَ ","bc":"fa iżā quḍiyatiṣ-ṣalātu fantasyirụ fil-arḍi wabtagụ min faḍlillāhi ważkurullāha kaṡīral la'allakum tufliḥụn","at":"Apabila salat telah dilaksanakan, maka bertebaranlah kamu di bumi; carilah karunia Allah dan ingatlah Allah banyak-banyak agar kamu beruntung. "},{"id":"11","ar":"وَاِذَا رَاَوْا تِجَارَةً اَوْ لَهْوًا ۨانْفَضُّوْٓا اِلَيْهَا وَتَرَكُوْكَ قَاۤىِٕمًاۗ قُلْ مَا عِنْدَ اللّٰهِ خَيْرٌ مِّنَ اللَّهْوِ وَمِنَ التِّجَارَةِۗ وَاللّٰهُ خَيْرُ الرّٰزِقِيْنَ ","bc":"wa iżā ra`au tijāratan au lahwaninfaḍḍū ilaihā wa tarakụka qā`imā, qul mā 'indallāhi khairum minal-lahwi wa minat-tijārah, wallāhu khairur-rāziqīn","at":"Dan apabila mereka melihat perdagangan atau permainan, mereka segera menuju kepadanya dan mereka tinggalkan engkau (Muhammad) sedang berdiri (berkhotbah). Katakanlah, “Apa yang ada di sisi Allah lebih baik daripada permainan dan perdagangan,” dan Allah pemberi rezeki yang terbaik."}],al_kafirun=[{"id":"1","ar":"قُلْ يٰٓاَيُّهَا الْكٰفِرُوْنَۙ","bc":"qul yā ayyuhal-kāfirụn","at":"Katakanlah (Muhammad), “Wahai orang-orang kafir!"},{"id":"2","ar":"لَآ اَعْبُدُ مَا تَعْبُدُوْنَۙ","bc":"lā a'budu mā ta'budụn","at":"aku tidak akan menyembah apa yang kamu sembah,"},{"id":"3","ar":"وَلَآ اَنْتُمْ عٰبِدُوْنَ مَآ اَعْبُدُۚ","bc":"wa lā antum 'ābidụna mā a'bud","at":"dan kamu bukan penyembah apa yang aku sembah,"},{"id":"4","ar":"وَلَآ اَنَا۠ عَابِدٌ مَّا عَبَدْتُّمْۙ","bc":"wa lā ana 'ābidum mā 'abattum","at":"dan aku tidak pernah menjadi penyembah apa yang kamu sembah,"},{"id":"5","ar":"وَلَآ اَنْتُمْ عٰبِدُوْنَ مَآ اَعْبُدُۗ","bc":"wa lā antum 'ābidụna mā a'bud","at":"dan kamu tidak pernah (pula) menjadi penyembah apa yang aku sembah."},{"id":"6","ar":"لَكُمْ دِيْنُكُمْ وَلِيَ دِيْنِ","bc":"lakum dīnukum wa liya dīn","at":"Untukmu agamamu, dan untukku agamaku.”"}],al_kahfi=[{"id":"1","ar":"اَلْحَمْدُ لِلّٰهِ الَّذِيْٓ اَنْزَلَ عَلٰى عَبْدِهِ الْكِتٰبَ وَلَمْ يَجْعَلْ لَّهٗ عِوَجًا ۜ","bc":"al-ḥamdu lillāhillażī anzala 'alā 'abdihil-kitāba wa lam yaj'al lahụ 'iwajā","at":"Segala puji bagi Allah yang telah menurunkan Kitab (Al-Qur'an) kepada hamba-Nya dan Dia tidak menjadikannya bengkok;"},{"id":"2","ar":"قَيِّمًا لِّيُنْذِرَ بَأْسًا شَدِيْدًا مِّنْ لَّدُنْهُ وَيُبَشِّرَ الْمُؤْمِنِيْنَ الَّذِيْنَ يَعْمَلُوْنَ الصّٰلِحٰتِ اَنَّ لَهُمْ اَجْرًا حَسَنًاۙ ","bc":"qayyimal liyunżira ba`san syadīdam mil ladun-hu wa yubasysyiral-mu`minīnallażīna ya'malụnaṣ-ṣāliḥāti anna lahum ajran ḥasanā","at":"sebagai bimbingan yang lurus, untuk memperingatkan akan siksa yang sangat pedih dari sisi-Nya dan memberikan kabar gembira kepada orang-orang mukmin yang mengerjakan kebajikan bahwa mereka akan mendapat balasan yang baik,"},{"id":"3","ar":"مَّاكِثِيْنَ فِيْهِ اَبَدًاۙ ","bc":"mākiṡīna fīhi abadā","at":"mereka kekal di dalamnya untuk selama-lamanya."},{"id":"4","ar":"وَّيُنْذِرَ الَّذِيْنَ قَالُوا اتَّخَذَ اللّٰهُ وَلَدًاۖ ","bc":"wa yunżirallażīna qāluttakhażallāhu waladā","at":"Dan untuk memperingatkan kepada orang yang berkata, “Allah mengambil seorang anak.”"},{"id":"5","ar":"مَّا لَهُمْ بِهٖ مِنْ عِلْمٍ وَّلَا لِاٰبَاۤىِٕهِمْۗ كَبُرَتْ كَلِمَةً تَخْرُجُ مِنْ اَفْوَاهِهِمْۗ اِنْ يَّقُوْلُوْنَ اِلَّا كَذِبًا","bc":"mā lahum bihī min 'ilmiw wa lā li`ābā`ihim, kaburat kalimatan takhruju min afwāhihim, iy yaqụlụna illā każibā","at":"Mereka sama sekali tidak mempunyai pengetahuan tentang hal itu, begitu pula nenek moyang mereka. Alangkah jeleknya kata-kata yang keluar dari mulut mereka; mereka hanya mengatakan (sesuatu) kebohongan belaka."},{"id":"6","ar":"فَلَعَلَّكَ بَاخِعٌ نَّفْسَكَ عَلٰٓى اٰثَارِهِمْ اِنْ لَّمْ يُؤْمِنُوْا بِهٰذَا الْحَدِيْثِ اَسَفًا","bc":"fa la'allaka bākhi'un nafsaka 'alā āṡārihim il lam yu`minụ bihāżal-ḥadīṡi asafā","at":"Maka barangkali engkau (Muhammad) akan mencelakakan dirimu karena bersedih hati setelah mereka berpaling, sekiranya mereka tidak beriman kepada keterangan ini (Al-Qur'an)."},{"id":"7","ar":"اِنَّا جَعَلْنَا مَا عَلَى الْاَرْضِ زِيْنَةً لَّهَا لِنَبْلُوَهُمْ اَيُّهُمْ اَحْسَنُ عَمَلًا","bc":"innā ja'alnā mā 'alal-arḍi zīnatal lahā linabluwahum ayyuhum aḥsanu 'amalā","at":"Sesungguhnya Kami telah menjadikan apa yang ada di bumi sebagai perhiasan baginya, untuk Kami menguji mereka, siapakah di antaranya yang terbaik perbuatannya."},{"id":"8","ar":"وَاِنَّا لَجَاعِلُوْنَ مَا عَلَيْهَا صَعِيْدًا جُرُزًاۗ ","bc":"wa innā lajā'ilụna mā 'alaihā ṣa'īdan juruzā","at":"Dan Kami benar-benar akan menjadikan (pula) apa yang di atasnya menjadi tanah yang tandus lagi kering."},{"id":"9","ar":"اَمْ حَسِبْتَ اَنَّ اَصْحٰبَ الْكَهْفِ وَالرَّقِيْمِ كَانُوْا مِنْ اٰيٰتِنَا عَجَبًا","bc":"am ḥasibta anna aṣ-ḥābal-kahfi war-raqīmi kānụ min āyātinā 'ajabā","at":"Apakah engkau mengira bahwa orang yang mendiami gua, dan (yang mempunyai) raqim itu, termasuk tanda-tanda (kebesaran) Kami yang menakjubkan?"},{"id":"10","ar":"اِذْ اَوَى الْفِتْيَةُ اِلَى الْكَهْفِ فَقَالُوْا رَبَّنَآ اٰتِنَا مِنْ لَّدُنْكَ رَحْمَةً وَّهَيِّئْ لَنَا مِنْ اَمْرِنَا رَشَدًا","bc":"iż awal-fityatu ilal-kahfi fa qālụ rabbanā ātinā mil ladungka raḥmataw wa hayyi` lanā min amrinā rasyadā","at":"(Ingatlah) ketika pemuda-pemuda itu berlindung ke dalam gua lalu mereka berdoa, “Ya Tuhan kami. Berikanlah rahmat kepada kami dari sisi-Mu dan sempurnakanlah petunjuk yang lurus bagi kami dalam urusan kami.”"},{"id":"11","ar":"فَضَرَبْنَا عَلٰٓى اٰذَانِهِمْ فِى الْكَهْفِ سِنِيْنَ عَدَدًاۙ ","bc":"fa ḍarabnā 'alā āżānihim fil-kahfi sinīna 'adadā","at":"Maka Kami tutup telinga mereka di dalam gua itu, selama beberapa tahun."},{"id":"12","ar":"ثُمَّ بَعَثْنٰهُمْ لِنَعْلَمَ اَيُّ الْحِزْبَيْنِ اَحْصٰى لِمَا لَبِثُوْٓا اَمَدًا","bc":"ṡumma ba'aṡnāhum lina'lama ayyul-ḥizbaini aḥṣā limā labiṡū amadā","at":"Kemudian Kami bangunkan mereka, agar Kami mengetahui manakah di antara ke dua golongan itu yang lebih tepat dalam menghitung berapa lamanya mereka tinggal (dalam gua itu)."},{"id":"13","ar":"نَحْنُ نَقُصُّ عَلَيْكَ نَبَاَهُمْ بِالْحَقِّۗ اِنَّهُمْ فِتْيَةٌ اٰمَنُوْا بِرَبِّهِمْ وَزِدْنٰهُمْ هُدًىۖ ","bc":"naḥnu naquṣṣu 'alaika naba`ahum bil-ḥaqq, innahum fityatun āmanụ birabbihim wa zidnāhum hudā","at":"Kami ceritakan kepadamu (Muhammad) kisah mereka dengan sebenarnya. Sesungguhnya mereka adalah pemuda-pemuda yang beriman kepada Tuhan mereka, dan Kami tambahkan petunjuk kepada mereka."},{"id":"14","ar":"وَّرَبَطْنَا عَلٰى قُلُوْبِهِمْ اِذْ قَامُوْا فَقَالُوْا رَبُّنَا رَبُّ السَّمٰوٰتِ وَالْاَرْضِ لَنْ نَّدْعُوَا۟ مِنْ دُوْنِهٖٓ اِلٰهًا لَّقَدْ قُلْنَآ اِذًا شَطَطًا","bc":"wa rabaṭnā 'alā qulụbihim iż qāmụ fa qālụ rabbunā rabbus-samāwāti wal-arḍi lan nad'uwa min dụnihī ilāhal laqad qulnā iżan syaṭaṭā","at":"Dan Kami teguhkan hati mereka ketika mereka berdiri lalu mereka berkata, “Tuhan kami adalah Tuhan langit dan bumi; kami tidak menyeru tuhan selain Dia. Sungguh, kalau kami berbuat demikian, tentu kami telah mengucapkan perkataan yang sangat jauh dari kebenaran.”"},{"id":"15","ar":"هٰٓؤُلَاۤءِ قَوْمُنَا اتَّخَذُوْا مِنْ دُوْنِهٖٓ اٰلِهَةًۗ لَوْلَا يَأْتُوْنَ عَلَيْهِمْ بِسُلْطٰنٍۢ بَيِّنٍۗ فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًاۗ ","bc":"hā`ulā`i qaumunattakhażụ min dụnihī ālihah, lau lā ya`tụna 'alaihim bisulṭānim bayyin, fa man aẓlamu mim maniftarā 'alallāhi każibā","at":"Mereka itu kaum kami yang telah menjadikan tuhan-tuhan (untuk disembah) selain Dia. Mengapa mereka tidak mengemukakan alasan yang jelas (tentang kepercayaan mereka)? Maka siapakah yang lebih zalim daripada orang yang mengada-adakan kebohongan terhadap Allah?"},{"id":"16","ar":"وَاِذِ اعْتَزَلْتُمُوْهُمْ وَمَا يَعْبُدُوْنَ اِلَّا اللّٰهَ فَأْوٗٓا اِلَى الْكَهْفِ يَنْشُرْ لَكُمْ رَبُّكُمْ مِّنْ رَّحْمَتِهٖ وَيُهَيِّئْ لَكُمْ مِّنْ اَمْرِكُمْ مِّرْفَقًا","bc":"wa iżi'tazaltumụhum wa mā ya'budụna illallāha fa`wū ilal-kahfi yansyur lakum rabbukum mir raḥmatihī wa yuhayyi` lakum min amrikum mirfaqā","at":"Dan apabila kamu meninggalkan mereka dan apa yang mereka sembah selain Allah, maka carilah tempat berlindung ke dalam gua itu, niscaya Tuhanmu akan melimpahkan sebagian rahmat-Nya kepadamu dan menyediakan sesuatu yang berguna bagimu dalam urusanmu."},{"id":"17","ar":"۞ وَتَرَى الشَّمْسَ اِذَا طَلَعَتْ تَّزَاوَرُ عَنْ كَهْفِهِمْ ذَاتَ الْيَمِيْنِ وَاِذَا غَرَبَتْ تَّقْرِضُهُمْ ذَاتَ الشِّمَالِ وَهُمْ فِيْ فَجْوَةٍ مِّنْهُۗ ذٰلِكَ مِنْ اٰيٰتِ اللّٰهِ ۗمَنْ يَّهْدِ اللّٰهُ فَهُوَ الْمُهْتَدِ وَمَنْ يُّضْلِلْ فَلَنْ تَجِدَ لَهٗ وَلِيًّا مُّرْشِدًا ","bc":"wa tarasy-syamsa iżā ṭala'at tazāwaru 'ang kahfihim żātal-yamīni wa iżā garabat taqriḍuhum żātasy-syimāli wa hum fī fajwatim min-h, żālika min āyātillāh, may yahdillāhu fa huwal-muhtadi wa may yuḍlil fa lan tajida lahụ waliyyam mursyidā","at":"Dan engkau akan melihat matahari ketika terbit, condong dari gua mereka ke sebelah kanan, dan apabila matahari itu terbenam, menjauhi mereka ke sebelah kiri sedang mereka berada dalam tempat yang luas di dalam (gua) itu. Itulah sebagian dari tanda-tanda (kebesaran) Allah. Barangsiapa diberi petunjuk oleh Allah, maka dialah yang mendapat petunjuk; dan barangsiapa disesatkan-Nya, maka engkau tidak akan mendapatkan seorang penolong yang dapat memberi petunjuk kepadanya."},{"id":"18","ar":"وَتَحْسَبُهُمْ اَيْقَاظًا وَّهُمْ رُقُوْدٌ ۖوَّنُقَلِّبُهُمْ ذَاتَ الْيَمِيْنِ وَذَاتَ الشِّمَالِ ۖوَكَلْبُهُمْ بَاسِطٌ ذِرَاعَيْهِ بِالْوَصِيْدِۗ لَوِ اطَّلَعْتَ عَلَيْهِمْ لَوَلَّيْتَ مِنْهُمْ فِرَارًا وَّلَمُلِئْتَ مِنْهُمْ رُعْبًا","bc":"wa taḥsabuhum aiqāẓaw wa hum ruqụduw wa nuqallibuhum żātal-yamīni wa żātasy-syimāli wa kalbuhum bāsiṭun żirā'aihi bil-waṣīd, lawiṭṭala'ta 'alaihim lawallaita min-hum firāraw wa lamuli`ta min-hum ru'bā","at":"Dan engkau mengira mereka itu tidak tidur, padahal mereka tidur; dan Kami bolak-balikkan mereka ke kanan dan ke kiri, sedang anjing mereka membentangkan kedua lengannya di depan pintu gua. Dan jika kamu menyaksikan mereka tentu kamu akan berpaling melarikan (diri) dari mereka dan pasti kamu akan dipenuhi rasa takut terhadap mereka."},{"id":"19","ar":"وَكَذٰلِكَ بَعَثْنٰهُمْ لِيَتَسَاۤءَلُوْا بَيْنَهُمْۗ قَالَ قَاۤىِٕلٌ مِّنْهُمْ كَمْ لَبِثْتُمْۗ قَالُوْا لَبِثْنَا يَوْمًا اَوْ بَعْضَ يَوْمٍۗ قَالُوْا رَبُّكُمْ اَعْلَمُ بِمَا لَبِثْتُمْۗ فَابْعَثُوْٓا اَحَدَكُمْ بِوَرِقِكُمْ هٰذِهٖٓ اِلَى الْمَدِيْنَةِ فَلْيَنْظُرْ اَيُّهَآ اَزْكٰى طَعَامًا فَلْيَأْتِكُمْ بِرِزْقٍ مِّنْهُ وَلْيَتَلَطَّفْ وَلَا يُشْعِرَنَّ بِكُمْ اَحَدًا ","bc":"wa każālika ba'aṡnāhum liyatasā`alụ bainahum, qāla qā`ilum min-hum kam labiṡtum, qālụ labiṡnā yauman au ba'ḍa yaụm, qālụ rabbukum a'lamu bimā labiṡtum, fab'aṡū aḥadakum biwariqikum hāżihī ilal-madīnati falyanẓur ayyuhā azkā ṭa'āman falya`tikum birizqim min-hu walyatalaṭṭaf wa lā yusy'iranna bikum aḥadā","at":"Dan demikianlah Kami bangunkan mereka, agar di antara mereka saling bertanya. Salah seorang di antara mereka berkata, “Sudah berapa lama kamu berada (di sini)?” Mereka menjawab, “Kita berada (di sini) sehari atau setengah hari.” Berkata (yang lain lagi), “Tuhanmu lebih mengetahui berapa lama kamu berada (di sini). Maka suruhlah salah seorang di antara kamu pergi ke kota dengan membawa uang perakmu ini, dan hendaklah dia lihat manakah makanan yang lebih baik, dan bawalah sebagian makanan itu untukmu, dan hendaklah dia berlaku lemah lembut dan jangan sekali-kali menceritakan halmu kepada siapa pun."},{"id":"20","ar":"اِنَّهُمْ اِنْ يَّظْهَرُوْا عَلَيْكُمْ يَرْجُمُوْكُمْ اَوْ يُعِيْدُوْكُمْ فِيْ مِلَّتِهِمْ وَلَنْ تُفْلِحُوْٓا اِذًا اَبَدًا","bc":"innahum iy yaẓ-harụ 'alaikum yarjumụkum au yu'īdụkum fī millatihim wa lan tufliḥū iżan abadā","at":"Sesungguhnya jika mereka dapat mengetahui tempatmu, niscaya mereka akan melempari kamu dengan batu, atau memaksamu kembali kepada agama mereka, dan jika demikian niscaya kamu tidak akan beruntung selama-lamanya.” "},{"id":"21","ar":"وَكَذٰلِكَ اَعْثَرْنَا عَلَيْهِمْ لِيَعْلَمُوْٓا اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَّاَنَّ السَّاعَةَ لَا رَيْبَ فِيْهَاۚ اِذْ يَتَنَازَعُوْنَ بَيْنَهُمْ اَمْرَهُمْ فَقَالُوا ابْنُوْا عَلَيْهِمْ بُنْيَانًاۗ رَبُّهُمْ اَعْلَمُ بِهِمْۗ قَالَ الَّذِيْنَ غَلَبُوْا عَلٰٓى اَمْرِهِمْ لَنَتَّخِذَنَّ عَلَيْهِمْ مَّسْجِدًا","bc":"wa każālika a'ṡarnā 'alaihim liya'lamū anna wa'dallāhi ḥaqquw wa annas-sā'ata lā raiba fīhā, iż yatanāza'ụna bainahum amrahum fa qālubnụ 'alaihim bun-yānā, rabbuhum a'lamu bihim, qālallażīna galabụ 'alā amrihim lanattakhiżanna 'alaihim masjidā","at":"Dan demikian (pula) Kami perlihatkan (manusia) dengan mereka, agar mereka tahu, bahwa janji Allah benar, dan bahwa (kedatangan) hari Kiamat tidak ada keraguan padanya. Ketika mereka berselisih tentang urusan mereka, maka mereka berkata, “Dirikanlah sebuah bangunan di atas (gua) mereka, Tuhan mereka lebih mengetahui tentang mereka.” Orang yang berkuasa atas urusan mereka berkata, “Kami pasti akan mendirikan sebuah rumah ibadah di atasnya.”"},{"id":"22","ar":"سَيَقُوْلُوْنَ ثَلٰثَةٌ رَّابِعُهُمْ كَلْبُهُمْۚ وَيَقُوْلُوْنَ خَمْسَةٌ سَادِسُهُمْ كَلْبُهُمْ رَجْمًاۢ بِالْغَيْبِۚ وَيَقُوْلُوْنَ سَبْعَةٌ وَّثَامِنُهُمْ كَلْبُهُمْ ۗقُلْ رَّبِّيْٓ اَعْلَمُ بِعِدَّتِهِمْ مَّا يَعْلَمُهُمْ اِلَّا قَلِيْلٌ ەۗ فَلَا تُمَارِ فِيْهِمْ اِلَّا مِرَاۤءً ظَاهِرًا ۖوَّلَا تَسْتَفْتِ فِيْهِمْ مِّنْهُمْ اَحَدًا","bc":"sayaqụlụna ṡalāṡatur rābi'uhum kalbuhum, wa yaqụlụna khamsatun sādisuhum kalbuhum rajmam bil-gaīb, wa yaqụlụna sab'atuw wa ṡāminuhum kalbuhum, qur rabbī a'lamu bi'iddatihim mā ya'lamuhum illā qalīl, fa lā tumāri fīhim illā mirā`an ẓāhiraw wa lā tastafti fīhim min-hum aḥadā","at":"Nanti (ada orang yang akan) mengatakan, ”(Jumlah mereka) tiga (orang), yang ke empat adalah anjingnya,” dan (yang lain) mengatakan, “(Jumlah mereka) lima (orang), yang ke enam adalah anjingnya,” sebagai terkaan terhadap yang gaib; dan (yang lain lagi) mengatakan, “(Jumlah mereka) tujuh (orang), yang ke delapan adalah anjingnya.” Katakanlah (Muhammad), “Tuhanku lebih mengetahui jumlah mereka; tidak ada yang mengetahui (bilangan) mereka kecuali sedikit.” Karena itu janganlah engkau (Muhammad) berbantah tentang hal mereka, kecuali perbantahan lahir saja dan jangan engkau menanyakan tentang mereka (pemuda-pemuda itu) kepada siapa pun."},{"id":"23","ar":"وَلَا تَقُوْلَنَّ لِشَا۟يْءٍ اِنِّيْ فَاعِلٌ ذٰلِكَ غَدًاۙ ","bc":"wa lā taqụlanna lisyai`in innī fā'ilun żālika gadā","at":"Dan jangan sekali-kali engkau mengatakan terhadap sesuatu, “Aku pasti melakukan itu besok pagi,”"},{"id":"24","ar":"اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۖوَاذْكُرْ رَّبَّكَ اِذَا نَسِيْتَ وَقُلْ عَسٰٓى اَنْ يَّهْدِيَنِ رَبِّيْ لِاَقْرَبَ مِنْ هٰذَا رَشَدًا","bc":"illā ay yasyā`allāhu ważkur rabbaka iżā nasīta wa qul 'asā ay yahdiyani rabbī li`aqraba min hāżā rasyadā","at":"kecuali (dengan mengatakan), “Insya Allah.” Dan ingatlah kepada Tuhanmu apabila engkau lupa dan katakanlah, “Mudah-mudahan Tuhanku akan memberiku petunjuk kepadaku agar aku yang lebih dekat (kebenarannya) daripada ini.”"},{"id":"25","ar":"وَلَبِثُوْا فِيْ كَهْفِهِمْ ثَلٰثَ مِائَةٍ سِنِيْنَ وَازْدَادُوْا تِسْعًا","bc":"wa labiṡụ fī kahfihim ṡalāṡa mi`atin sinīna wazdādụ tis'ā","at":"Dan mereka tinggal dalam gua selama tiga ratus tahun dan ditambah sembilan tahun."},{"id":"26","ar":"قُلِ اللّٰهُ اَعْلَمُ بِمَا لَبِثُوْا ۚ لَهٗ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِۗ اَبْصِرْ بِهٖ وَاَسْمِعْۗ مَا لَهُمْ مِّنْ دُوْنِهٖ مِنْ وَّلِيٍّۗ وَلَا يُشْرِكُ فِيْ حُكْمِهٖٓ اَحَدًا","bc":"qulillāhu a'lamu bimā labiṡụ, lahụ gaibus-samāwāti wal-arḍ, abṣir bihī wa asmi', mā lahum min dụnihī miw waliyy, wa lā yusyriku fī ḥukmihī aḥadā","at":"Katakanlah, “Allah lebih mengetahui berapa lamanya mereka tinggal (di gua); milik-Nya semua yang tersembunyi di langit dan di bumi. Alangkah terang penglihatan-Nya dan alangkah tajam pendengaran-Nya; tidak ada seorang pelindung pun bagi mereka selain Dia; dan Dia tidak mengambil seorang pun menjadi sekutu-Nya dalam menetapkan keputusan.”"},{"id":"27","ar":"وَاتْلُ مَآ اُوْحِيَ اِلَيْكَ مِنْ كِتَابِ رَبِّكَۗ لَا مُبَدِّلَ لِكَلِمٰتِهٖۗ وَلَنْ تَجِدَ مِنْ دُوْنِهٖ مُلْتَحَدًا","bc":"watlu mā ụḥiya ilaika ming kitābi rabbik, lā mubaddila likalimātih, wa lan tajida min dụnihī multaḥadā","at":"Dan bacakanlah (Muhammad) apa yang diwahyukan kepadamu, yaitu Kitab Tuhanmu (Al-Qur'an). Tidak ada yang dapat mengubah kalimat-kalimat-Nya. Dan engkau tidak akan dapat menemukan tempat berlindung selain kepada-Nya."},{"id":"28","ar":"وَاصْبِرْ نَفْسَكَ مَعَ الَّذِيْنَ يَدْعُوْنَ رَبَّهُمْ بِالْغَدٰوةِ وَالْعَشِيِّ يُرِيْدُوْنَ وَجْهَهٗ وَلَا تَعْدُ عَيْنٰكَ عَنْهُمْۚ تُرِيْدُ زِيْنَةَ الْحَيٰوةِ الدُّنْيَاۚ وَلَا تُطِعْ مَنْ اَغْفَلْنَا قَلْبَهٗ عَنْ ذِكْرِنَا وَاتَّبَعَ هَوٰىهُ وَكَانَ اَمْرُهٗ فُرُطًا","bc":"waṣbir nafsaka ma'allażīna yad'ụna rabbahum bil-gadāti wal-'asyiyyi yurīdụna waj-hahụ wa lā ta'du 'aināka 'an-hum, turīdu zīnatal-ḥayātid-dun-yā, wa lā tuṭi' man agfalnā qalbahụ 'an żikrinā wattaba'a hawāhu wa kāna amruhụ furuṭā","at":"Dan bersabarlah engkau (Muhammad) bersama orang yang menyeru Tuhannya pada pagi dan senja hari dengan mengharap keridaan-Nya; dan janganlah kedua matamu berpaling dari mereka (karena) mengharapkan perhiasan kehidupan dunia; dan janganlah engkau mengikuti orang yang hatinya telah Kami lalaikan dari mengingat Kami, serta menuruti keinginannya dan keadaannya sudah melewati batas."},{"id":"29","ar":"وَقُلِ الْحَقُّ مِنْ رَّبِّكُمْۗ فَمَنْ شَاۤءَ فَلْيُؤْمِنْ وَّمَنْ شَاۤءَ فَلْيَكْفُرْۚ اِنَّآ اَعْتَدْنَا لِلظّٰلِمِيْنَ نَارًاۙ اَحَاطَ بِهِمْ سُرَادِقُهَاۗ وَاِنْ يَّسْتَغِيْثُوْا يُغَاثُوْا بِمَاۤءٍ كَالْمُهْلِ يَشْوِى الْوُجُوْهَۗ بِئْسَ الشَّرَابُۗ وَسَاۤءَتْ مُرْتَفَقًا","bc":"wa qulil-ḥaqqu mir rabbikum, fa man syā`a falyu`miw wa man syā`a falyakfur, innā a'tadnā liẓ-ẓālimīna nāran aḥāṭa bihim surādiquhā, wa iy yastagīṡụ yugāṡụ bimā`ing kal-muhli yasywil-wujụh, bi`sasy-syarāb, wa sā`at murtafaqā","at":"Dan katakanlah (Muhammad), “Kebenaran itu datangnya dari Tuhanmu; barangsiapa menghendaki (beriman) hendaklah dia beriman, dan barangsiapa menghendaki (kafir) biarlah dia kafir.” Sesungguhnya Kami telah menyediakan neraka bagi orang zalim, yang gejolaknya mengepung mereka. Jika mereka meminta pertolongan (minum), mereka akan diberi air seperti besi yang mendidih yang menghanguskan wajah. (Itulah) minuman yang paling buruk dan tempat istirahat yang paling jelek."},{"id":"30","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ اِنَّا لَا نُضِيْعُ اَجْرَ مَنْ اَحْسَنَ عَمَلًاۚ ","bc":"innallażīna āmanụ wa 'amiluṣ-ṣāliḥāti innā lā nuḍī'u ajra man aḥsana 'amalā","at":"Sungguh, mereka yang beriman dan mengerjakan kebajikan, Kami benar-benar tidak akan menyia-nyiakan pahala orang yang mengerjakan perbuatan yang baik itu."},{"id":"31","ar":"اُولٰۤىِٕكَ لَهُمْ جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهِمُ الْاَنْهٰرُ يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّيَلْبَسُوْنَ ثِيَابًا خُضْرًا مِّنْ سُنْدُسٍ وَّاِسْتَبْرَقٍ مُّتَّكِىِٕيْنَ فِيْهَا عَلَى الْاَرَاۤىِٕكِۗ نِعْمَ الثَّوَابُۗ وَحَسُنَتْ مُرْتَفَقًا","bc":"ulā`ika lahum jannātu 'adnin tajrī min taḥtihimul-an-hāru yuḥallauna fīhā min asāwira min żahabiw wa yalbasụna ṡiyāban khuḍram min sundusiw wa istabraqim muttaki`īna fīhā 'alal-arā`ik, ni'maṡ-ṡawāb, wa ḥasunat murtafaqā","at":"Mereka itulah yang memperoleh Surga ‘Adn, yang mengalir di bawahnya sungai-sungai; (dalam surga itu) mereka diberi hiasan gelang emas dan mereka memakai pakaian hijau dari sutera halus dan sutera tebal, sedang mereka duduk sambil bersandar di atas dipan-dipan yang indah. (Itulah) sebaik-baik pahala dan tempat istirahat yang indah."},{"id":"32","ar":"۞ وَاضْرِبْ لَهُمْ مَّثَلًا رَّجُلَيْنِ جَعَلْنَا لِاَحَدِهِمَا جَنَّتَيْنِ مِنْ اَعْنَابٍ وَّحَفَفْنٰهُمَا بِنَخْلٍ وَّجَعَلْنَا بَيْنَهُمَا زَرْعًاۗ ","bc":"waḍrib lahum maṡalar rajulaini ja'alnā li`aḥadihimā jannataini min a'nābiw wa ḥafafnāhumā binakhliw wa ja'alnā bainahumā zar'ā","at":"Dan berikanlah (Muhammad) kepada mereka sebuah perumpamaan, dua orang laki-laki, yang seorang (yang kafir) Kami beri dua buah kebun anggur dan Kami kelilingi kedua kebun itu dengan pohon-pohon kurma dan di antara keduanya (kebun itu) Kami buatkan ladang."},{"id":"33","ar":"كِلْتَا الْجَنَّتَيْنِ اٰتَتْ اُكُلَهَا وَلَمْ تَظْلِمْ مِّنْهُ شَيْـًٔاۙ وَّفَجَّرْنَا خِلٰلَهُمَا نَهَرًاۙ ","bc":"kiltal-jannataini ātat ukulahā wa lam taẓlim min-hu syai`aw wa fajjarnā khilālahumā naharā","at":"Kedua kebun itu menghasilkan buahnya, dan tidak berkurang (buahnya) sedikit pun, dan di celah-celah kedua kebun itu Kami alirkan sungai, "},{"id":"34","ar":"وَّكَانَ لَهٗ ثَمَرٌۚ فَقَالَ لِصَاحِبِهٖ وَهُوَ يُحَاوِرُهٗٓ اَنَا۠ اَكْثَرُ مِنْكَ مَالًا وَّاَعَزُّ نَفَرًا","bc":"wa kāna lahụ ṡamar, fa qāla liṣāḥibihī wa huwa yuḥāwiruhū ana akṡaru mingka mālaw wa a'azzu nafarā","at":"dan dia memiliki kekayaan besar, maka dia berkata kepada kawannya (yang beriman) ketika bercakap-cakap dengan dia, “Hartaku lebih banyak daripada hartamu dan pengikutku lebih kuat.” "},{"id":"35","ar":"وَدَخَلَ جَنَّتَهٗ وَهُوَ ظَالِمٌ لِّنَفْسِهٖۚ قَالَ مَآ اَظُنُّ اَنْ تَبِيْدَ هٰذِهٖٓ اَبَدًاۙ ","bc":"wa dakhala jannatahụ wa huwa ẓālimul linafsih, qāla mā aẓunnu an tabīda hāżihī abadā","at":"Dan dia memasuki kebunnya dengan sikap merugikan dirinya sendiri (karena angkuh dan kafir); dia berkata, “Aku kira kebun ini tidak akan binasa selama-lamanya,"},{"id":"36","ar":"وَّمَآ اَظُنُّ السَّاعَةَ قَاۤىِٕمَةً وَّلَىِٕنْ رُّدِدْتُّ اِلٰى رَبِّيْ لَاَجِدَنَّ خَيْرًا مِّنْهَا مُنْقَلَبًا ","bc":"wa mā aẓunnus-sā'ata qā`imataw wa la`ir rudittu ilā rabbī la`ajidanna khairam min-hā mungqalabā","at":"dan aku kira hari Kiamat itu tidak akan datang, dan sekiranya aku dikembalikan kepada Tuhanku, pasti aku akan mendapat tempat kembali yang lebih baik dari pada ini.”"},{"id":"37","ar":"قَالَ لَهٗ صَاحِبُهٗ وَهُوَ يُحَاوِرُهٗٓ اَكَفَرْتَ بِالَّذِيْ خَلَقَكَ مِنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ سَوّٰىكَ رَجُلًاۗ ","bc":"qāla lahụ ṣāḥibuhụ wa huwa yuḥāwiruhū a kafarta billażī khalaqaka min turābin ṡumma min nuṭfatin ṡumma sawwāka rajulā","at":"Kawannya (yang beriman) berkata kepadanya sambil bercakap-cakap dengannya, “Apakah engkau ingkar kepada (Tuhan) yang menciptakan engkau dari tanah, kemudian dari setetes air mani, lalu Dia menjadikan engkau seorang laki-laki yang sempurna?"},{"id":"38","ar":"لٰكِنَّا۠ هُوَ اللّٰهُ رَبِّيْ وَلَآ اُشْرِكُ بِرَبِّيْٓ اَحَدًا ","bc":"lākinna huwallāhu rabbī wa lā usyriku birabbī aḥadā","at":"Tetapi aku (percaya bahwa), Dialah Allah, Tuhanku, dan aku tidak mempersekutukan Tuhanku dengan sesuatu pun."},{"id":"39","ar":"وَلَوْلَآ اِذْ دَخَلْتَ جَنَّتَكَ قُلْتَ مَا شَاۤءَ اللّٰهُ ۙ لَا قُوَّةَ اِلَّا بِاللّٰهِ ۚاِنْ تَرَنِ اَنَا۠ اَقَلَّ مِنْكَ مَالًا وَّوَلَدًاۚ ","bc":"walau lā iż dakhalta jannataka qulta mā syā`allāhu lā quwwata illā billāh, in tarani ana aqalla mingka mālaw wa waladā","at":"Dan mengapa ketika engkau memasuki kebunmu tidak mengucapkan ”Masya Allah, la quwwata illa billah” (Sungguh, atas kehendak Allah, semua ini terwujud), tidak ada kekuatan kecuali dengan (pertolongan) Allah, sekalipun engkau anggap harta dan keturunanku lebih sedikit daripadamu."},{"id":"40","ar":"فَعَسٰى رَبِّيْٓ اَنْ يُّؤْتِيَنِ خَيْرًا مِّنْ جَنَّتِكَ وَيُرْسِلَ عَلَيْهَا حُسْبَانًا مِّنَ السَّمَاۤءِ فَتُصْبِحَ صَعِيْدًا زَلَقًاۙ ","bc":"fa asā rabbī ay yu`tiyani khairam min jannatika wa yursila 'alaihā ḥusbānam minas-samā`i fa tuṣbiḥa ṣa'īdan zalaqā","at":"Maka mudah-mudahan Tuhanku, akan memberikan kepadaku (kebun) yang lebih baik dari kebunmu (ini); dan Dia mengirimkan petir dari langit ke kebunmu, sehingga (kebun itu) menjadi tanah yang licin,"},{"id":"41","ar":"اَوْ يُصْبِحَ مَاۤؤُهَا غَوْرًا فَلَنْ تَسْتَطِيْعَ لَهٗ طَلَبًا ","bc":"au yuṣbiḥa mā`uhā gauran fa lan tastaṭī'a lahụ ṭalabā","at":"atau airnya menjadi surut ke dalam tanah, maka engkau tidak akan dapat menemukannya lagi.”"},{"id":"42","ar":"وَاُحِيْطَ بِثَمَرِهٖ فَاَصْبَحَ يُقَلِّبُ كَفَّيْهِ عَلٰى مَآ اَنْفَقَ فِيْهَا وَهِيَ خَاوِيَةٌ عَلٰى عُرُوْشِهَا وَيَقُوْلُ يٰلَيْتَنِيْ لَمْ اُشْرِكْ بِرَبِّيْٓ اَحَدًا","bc":"wa uḥīṭa biṡamarihī fa aṣbaḥa yuqallibu kaffaihi 'alā mā anfaqa fīhā wa hiya khāwiyatun 'alā 'urụsyihā wa yaqụlu yā laitanī lam usyrik birabbī aḥadā","at":"Dan harta kekayaannya dibinasakan, lalu dia membolak-balikkan kedua telapak tangannya (tanda menyesal) terhadap apa yang telah dia belanjakan untuk itu, sedang pohon anggur roboh bersama penyangganya (para-para) lalu dia berkata, “Betapa sekiranya dahulu aku tidak mempersekutukan Tuhanku dengan sesuatu pun.”"},{"id":"43","ar":"وَلَمْ تَكُنْ لَّهٗ فِئَةٌ يَّنْصُرُوْنَهٗ مِنْ دُوْنِ اللّٰهِ وَمَا كَانَ مُنْتَصِرًاۗ ","bc":"wa lam takul lahụ fi`atuy yanṣurụnahụ min dụnillāhi wa mā kāna muntaṣirā","at":"Dan tidak ada (lagi) baginya segolongan pun yang dapat menolongnya selain Allah; dan dia pun tidak akan dapat membela dirinya."},{"id":"44","ar":"هُنَالِكَ الْوَلَايَةُ لِلّٰهِ الْحَقِّۗ هُوَ خَيْرٌ ثَوَابًا وَّخَيْرٌ عُقْبًا","bc":"hunālikal-walāyatu lillāhil-ḥaqq, huwa khairun ṡawābaw wa khairun 'uqbā","at":"Di sana, pertolongan itu hanya dari Allah Yang Mahabenar. Dialah (pemberi) pahala terbaik dan (pemberi) balasan terbaik."},{"id":"45","ar":"وَاضْرِبْ لَهُمْ مَّثَلَ الْحَيٰوةِ الدُّنْيَا كَمَاۤءٍ اَنْزَلْنٰهُ مِنَ السَّمَاۤءِ فَاخْتَلَطَ بِهٖ نَبَاتُ الْاَرْضِ فَاَصْبَحَ هَشِيْمًا تَذْرُوْهُ الرِّيٰحُ ۗوَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُّقْتَدِرًا","bc":"waḍrib lahum maṡalal-ḥayātid-dun-yā kamā`in anzalnāhu minas-samā`i fakhtalaṭa bihī nabātul-arḍi fa aṣbaḥa hasyīman tażrụhur-riyāḥ, wa kānallāhu 'alā kulli syai`im muqtadirā","at":"Dan buatkanlah untuk mereka (manusia) perumpamaan kehidupan dunia ini, ibarat air (hujan) yang Kami turunkan dari langit, sehingga menyuburkan tumbuh-tumbuhan di bumi, kemudian (tumbuh-tumbuhan) itu menjadi kering yang diterbangkan oleh angin. Dan Allah Mahakuasa atas segala sesuatu."},{"id":"46","ar":"اَلْمَالُ وَالْبَنُوْنَ زِيْنَةُ الْحَيٰوةِ الدُّنْيَاۚ وَالْبٰقِيٰتُ الصّٰلِحٰتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا وَّخَيْرٌ اَمَلًا","bc":"al-mālu wal-banụna zīnatul-ḥayātid-dun-yā, wal-bāqiyātuṣ-ṣāliḥātu khairun 'inda rabbika ṡawābaw wa khairun amalā","at":"Harta dan anak-anak adalah perhiasan kehidupan dunia tetapi amal kebajikan yang terus-menerus adalah lebih baik pahalanya di sisi Tuhanmu serta lebih baik untuk menjadi harapan."},{"id":"47","ar":"وَيَوْمَ نُسَيِّرُ الْجِبَالَ وَتَرَى الْاَرْضَ بَارِزَةًۙ وَّحَشَرْنٰهُمْ فَلَمْ نُغَادِرْ مِنْهُمْ اَحَدًاۚ ","bc":"wa yauma nusayyirul-jibāla wa taral-arḍa bārizataw wa ḥasyarnāhum fa lam nugādir min-hum aḥadā","at":"Dan (ingatlah) pada hari (ketika) Kami perjalankan gunung-gunung dan engkau akan melihat bumi itu rata dan Kami kumpulkan mereka (seluruh manusia), dan tidak Kami tinggalkan seorang pun dari mereka."},{"id":"48","ar":"وَعُرِضُوْا عَلٰى رَبِّكَ صَفًّاۗ لَقَدْ جِئْتُمُوْنَا كَمَا خَلَقْنٰكُمْ اَوَّلَ مَرَّةٍۢ ۖبَلْ زَعَمْتُمْ اَلَّنْ نَّجْعَلَ لَكُمْ مَّوْعِدًا","bc":"wa 'uriḍụ 'alā rabbika ṣaffā, laqad ji`tumụnā kamā khalaqnākum awwala marratim bal za'amtum allan naj'ala lakum mau'idā","at":"Dan mereka akan dibawa ke hadapan Tuhanmu dengan berbaris. (Allah berfirman), “Sesungguhnya kamu datang kepada Kami, sebagaimana Kami menciptakan kamu pada pertama kali; bahkan kamu menganggap bahwa Kami tidak akan menetapkan bagi kamu waktu (berbangkit untuk memenuhi) perjanjian.”"},{"id":"49","ar":"وَوُضِعَ الْكِتٰبُ فَتَرَى الْمُجْرِمِيْنَ مُشْفِقِيْنَ مِمَّا فِيْهِ وَيَقُوْلُوْنَ يٰوَيْلَتَنَا مَالِ هٰذَا الْكِتٰبِ لَا يُغَادِرُ صَغِيْرَةً وَّلَا كَبِيْرَةً اِلَّآ اَحْصٰىهَاۚ وَوَجَدُوْا مَا عَمِلُوْا حَاضِرًاۗ وَلَا يَظْلِمُ رَبُّكَ اَحَدًا ","bc":"wa wuḍi'al kitābu fa taral-mujrimīna musyfiqīna mimmā fīhi wa yaqụlụna yā wailatanā māli hāżal-kitābi lā yugādiru ṣagīrataw wa lā kabīratan illā aḥṣāhā, wa wajadụ mā 'amilụ ḥāḍirā, wa lā yaẓlimu rabbuka aḥadā","at":"Dan diletakkanlah kitab (catatan amal), lalu engkau akan melihat orang yang berdosa merasa ketakutan terhadap apa yang (tertulis) di dalamnya, dan mereka berkata, “Betapa celaka kami, kitab apakah ini, tidak ada yang tertinggal, yang kecil dan yang besar melainkan tercatat semuanya,” dan mereka dapati (semua) apa yang telah mereka kerjakan (tertulis). Dan Tuhanmu tidak menzalimi seorang jua pun."},{"id":"50","ar":"وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَۗ كَانَ مِنَ الْجِنِّ فَفَسَقَ عَنْ اَمْرِ رَبِّهٖۗ اَفَتَتَّخِذُوْنَهٗ وَذُرِّيَّتَهٗٓ اَوْلِيَاۤءَ مِنْ دُوْنِيْ وَهُمْ لَكُمْ عَدُوٌّۗ بِئْسَ لِلظّٰلِمِيْنَ بَدَلًا ","bc":"wa iż qulnā lil-malā`ikatisjudụ li`ādama fa sajadū illā iblīs, kāna minal-jinni fa fasaqa 'an amri rabbih, a fa tattakhiżụnahụ wa żurriyyatahū auliyā`a min dụnī wa hum lakum 'aduww, bi`sa liẓ-ẓālimīna badalā","at":"Dan (ingatlah) ketika Kami berfirman kepada para malaikat, “Sujudlah kamu kepada Adam!” Maka mereka pun sujud kecuali Iblis. Dia adalah dari (golongan) jin, maka dia mendurhakai perintah Tuhannya. Pantaskah kamu menjadikan dia dan keturunannya sebagai pemimpin selain Aku, padahal mereka adalah musuhmu? Sangat buruklah (Iblis itu) sebagai pengganti (Allah) bagi orang yang zalim."},{"id":"51","ar":"۞ مَآ اَشْهَدْتُّهُمْ خَلْقَ السَّمٰوٰتِ وَالْاَرْضِ وَلَا خَلْقَ اَنْفُسِهِمْۖ وَمَا كُنْتُ مُتَّخِذَ الْمُضِلِّيْنَ عَضُدًا","bc":"mā asy-hattuhum khalqas-samāwāti wal-arḍi wa lā khalqa anfusihim wa mā kuntu muttakhiżal-muḍillīna 'aḍudā","at":"Aku tidak menghadirkan mereka (Iblis dan anak cucunya) untuk menyaksikan penciptaan langit dan bumi dan tidak (pula) penciptaan diri mereka sendiri; dan Aku tidak menjadikan orang yang menyesatkan itu sebagai penolong."},{"id":"52","ar":"وَيَوْمَ يَقُوْلُ نَادُوْا شُرَكَاۤءِيَ الَّذِيْنَ زَعَمْتُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيْبُوْا لَهُمْ وَجَعَلْنَا بَيْنَهُمْ مَّوْبِقًا","bc":"wa yauma yaqụlu nādụ syurakā`iyallażīna za'amtum fa da'auhum fa lam yastajībụ lahum wa ja'alnā bainahum maubiqā","at":"Dan (ingatlah) pada hari (ketika) Dia berfirman, “Panggillah olehmu sekutu-sekutu-Ku yang kamu anggap itu.” Mereka lalu memanggilnya, tetapi mereka (sekutu-sekutu) tidak membalas (seruan) mereka dan Kami adakan untuk mereka tempat kebinasaan (neraka)."},{"id":"53","ar":"وَرَاَ الْمُجْرِمُوْنَ النَّارَ فَظَنُّوْٓا اَنَّهُمْ مُّوَاقِعُوْهَا وَلَمْ يَجِدُوْا عَنْهَا مَصْرِفًا ","bc":"wa ra`al-mujrimụnan-nāra fa ẓannū annahum muwāqi'ụhā wa lam yajidụ 'an-hā maṣrifā","at":"Dan orang yang berdosa melihat neraka, lalu mereka menduga, bahwa mereka akan jatuh ke dalamnya, dan mereka tidak menemukan tempat berpaling darinya. "},{"id":"54","ar":"وَلَقَدْ صَرَّفْنَا فِيْ هٰذَا الْقُرْاٰنِ لِلنَّاسِ مِنْ كُلِّ مَثَلٍۗ وَكَانَ الْاِنْسَانُ اَكْثَرَ شَيْءٍ جَدَلًا ","bc":"wa laqad ṣarrafnā fī hāżal-qur`āni lin-nāsi ming kulli maṡal, wa kānal-insānu akṡara syai`in jadalā","at":"Dan sesungguhnya Kami telah menjelaskan berulang-ulang kepada manusia dalam Al-Qur'an ini dengan bermacam-macam perumpamaan. Tetapi manusia adalah memang yang paling banyak membantah."},{"id":"55","ar":"وَمَا مَنَعَ النَّاسَ اَنْ يُّؤْمِنُوْٓا اِذْ جَاۤءَهُمُ الْهُدٰى وَيَسْتَغْفِرُوْا رَبَّهُمْ اِلَّآ اَنْ تَأْتِيَهُمْ سُنَّةُ الْاَوَّلِيْنَ اَوْ يَأْتِيَهُمُ الْعَذَابُ قُبُلًا","bc":"wa mā mana'an-nāsa ay yu`minū iż jā`ahumul-hudā wa yastagfirụ rabbahum illā an ta`tiyahum sunnatul-awwalīna au ya`tiyahumul-'ażābu qubulā","at":"Dan tidak ada (sesuatu pun) yang menghalangi manusia untuk beriman ketika petunjuk telah datang kepada mereka dan memohon ampunan kepada Tuhannya, kecuali (keinginan menanti) datangnya hukum (Allah yang telah berlaku pada) umat yang terdahulu atau datangnya azab atas mereka dengan nyata."},{"id":"56","ar":"وَمَا نُرْسِلُ الْمُرْسَلِيْنَ اِلَّا مُبَشِّرِيْنَ وَمُنْذِرِيْنَۚ وَيُجَادِلُ الَّذِيْنَ كَفَرُوْا بِالْبَاطِلِ لِيُدْحِضُوْا بِهِ الْحَقَّ وَاتَّخَذُوْٓا اٰيٰتِيْ وَمَآ اُنْذِرُوْا هُزُوًا","bc":"wa mā nursilul-mursalīna illā mubasysyirīna wa munżirīn, wa yujādilullażīna kafarụ bil-bāṭili liyud-ḥiḍụ bihil-ḥaqqa wattakhażū āyātī wa mā unżirụ huzuwā","at":"Dan Kami tidak mengutus rasul-rasul melainkan sebagai pembawa kabar gembira dan pemberi peringatan; tetapi orang yang kafir membantah dengan (cara) yang batil agar dengan demikian mereka dapat melenyapkan yang hak (kebenaran), dan mereka menjadikan ayat-ayat-Ku dan apa yang diperingatkan terhadap mereka sebagai olok-olokan."},{"id":"57","ar":"وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيٰتِ رَبِّهٖ فَاَعْرَضَ عَنْهَا وَنَسِيَ مَا قَدَّمَتْ يَدَاهُۗ اِنَّا جَعَلْنَا عَلٰى قُلُوْبِهِمْ اَكِنَّةً اَنْ يَّفْقَهُوْهُ وَفِيْٓ اٰذَانِهِمْ وَقْرًاۗ وَاِنْ تَدْعُهُمْ اِلَى الْهُدٰى فَلَنْ يَّهْتَدُوْٓا اِذًا اَبَدًا ","bc":"wa man aẓlamu mim man żukkira bi`āyāti rabbihī fa a'raḍa 'an-hā wa nasiya mā qaddamat yadāh, innā ja'alnā 'alā qulụbihim akinnatan ay yafqahụhu wa fī āżānihim waqrā, wa in tad'uhum ilal-hudā fa lay yahtadū iżan abadā","at":"Dan siapakah yang lebih zalim dari pada orang yang telah diperingatkan dengan ayat-ayat Tuhannya, lalu dia berpaling darinya dan melupakan apa yang telah dikerjakan oleh kedua tangannya? Sungguh, Kami telah menjadikan hati mereka tertutup, (sehingga mereka tidak) memahaminya, dan (Kami letakkan pula) sumbatan di telinga mereka. Kendatipun engkau (Muhammad) menyeru mereka kepada petunjuk, niscaya mereka tidak akan mendapat petunjuk untuk selama-lamanya."},{"id":"58","ar":"وَرَبُّكَ الْغَفُوْرُ ذُو الرَّحْمَةِۗ لَوْ يُؤَاخِذُهُمْ بِمَا كَسَبُوْا لَعَجَّلَ لَهُمُ الْعَذَابَۗ بَلْ لَّهُمْ مَّوْعِدٌ لَّنْ يَّجِدُوْا مِنْ دُوْنِهٖ مَوْىِٕلًا","bc":"wa rabbukal-gafụru żur-raḥmah, lau yu`ākhiżuhum bimā kasabụ la'ajjala lahumul-'ażāb, bal lahum mau'idul lay yajidụ min dụnihī mau`ilā","at":"Dan Tuhanmu Maha Pengampun, memiliki kasih sayang. Jika Dia hendak menyiksa mereka karena perbuatan mereka, tentu Dia akan menyegerakan siksa bagi mereka. Tetapi bagi mereka ada waktu tertentu (untuk mendapat siksa) yang mereka tidak akan menemukan tempat berlindung dari-Nya."},{"id":"59","ar":"وَتِلْكَ الْقُرٰٓى اَهْلَكْنٰهُمْ لَمَّا ظَلَمُوْا وَجَعَلْنَا لِمَهْلِكِهِمْ مَّوْعِدًا","bc":"wa tilkal-qurā ahlaknāhum lammā ẓalamụ wa ja'alnā limahlikihim mau'idā","at":"Dan (penduduk) negeri itu telah Kami binasakan ketika mereka berbuat zalim, dan telah Kami tetapkan waktu tertentu bagi kebinasaan mereka."},{"id":"60","ar":"وَاِذْ قَالَ مُوْسٰى لِفَتٰىهُ لَآ اَبْرَحُ حَتّٰٓى اَبْلُغَ مَجْمَعَ الْبَحْرَيْنِ اَوْ اَمْضِيَ حُقُبًا","bc":"wa iż qāla mụsā lifatāhu lā abraḥu ḥattā abluga majma'al-baḥraini au amḍiya ḥuqubā","at":"Dan (ingatlah) ketika Musa berkata kepada pembantunya, “Aku tidak akan berhenti (berjalan) sebelum sampai ke pertemuan dua laut; atau aku akan berjalan (terus sampai) bertahun-tahun.”"},{"id":"61","ar":"فَلَمَّا بَلَغَا مَجْمَعَ بَيْنِهِمَا نَسِيَا حُوْتَهُمَا فَاتَّخَذَ سَبِيْلَهٗ فِى الْبَحْرِ سَرَبًا ","bc":"fa lammā balagā majma'a bainihimā nasiyā ḥụtahumā fattakhaża sabīlahụ fil-baḥri sarabā","at":" Maka ketika mereka sampai ke pertemuan dua laut itu, mereka lupa ikannya, lalu (ikan) itu melompat mengambil jalannya ke laut itu."},{"id":"62","ar":"فَلَمَّا جَاوَزَا قَالَ لِفَتٰىهُ اٰتِنَا غَدَاۤءَنَاۖ لَقَدْ لَقِيْنَا مِنْ سَفَرِنَا هٰذَا نَصَبًا ","bc":"fa lammā jāwazā qāla lifatāhu ātinā gadā`anā laqad laqīnā min safarinā hāżā naṣabā","at":"Maka ketika mereka telah melewati (tempat itu), Musa berkata kepada pembantunya, “Bawalah kemari makanan kita; sungguh kita telah merasa letih karena perjalanan kita ini.”"},{"id":"63","ar":"قَالَ اَرَاَيْتَ اِذْ اَوَيْنَآ اِلَى الصَّخْرَةِ فَاِنِّيْ نَسِيْتُ الْحُوْتَۖ وَمَآ اَنْسٰىنِيْهُ اِلَّا الشَّيْطٰنُ اَنْ اَذْكُرَهٗۚ وَاتَّخَذَ سَبِيْلَهٗ فِى الْبَحْرِ عَجَبًا ","bc":"qāla a ra`aita iż awainā ilaṣ-ṣakhrati fa innī nasītul-ḥụta wa mā ansānīhu illasy-syaiṭānu an ażkurah, wattakhaża sabīlahụ fil-baḥri 'ajabā","at":"Dia (pembantunya) menjawab, “Tahukah engkau ketika kita mencari tempat berlindung di batu tadi, maka aku lupa (menceritakan tentang) ikan itu dan tidak ada yang membuat aku lupa untuk mengingatnya kecuali setan, dan (ikan) itu mengambil jalannya ke laut dengan cara yang aneh sekali.”"},{"id":"64","ar":"قَالَ ذٰلِكَ مَا كُنَّا نَبْغِۖ فَارْتَدَّا عَلٰٓى اٰثَارِهِمَا قَصَصًاۙ ","bc":"qāla żālika mā kunnā nabgi fartaddā 'alā āṡārihimā qaṣaṣā","at":"Dia (Musa) berkata, “Itulah (tempat) yang kita cari.” Lalu keduanya kembali, mengikuti jejak mereka semula."},{"id":"65","ar":"فَوَجَدَا عَبْدًا مِّنْ عِبَادِنَآ اٰتَيْنٰهُ رَحْمَةً مِّنْ عِنْدِنَا وَعَلَّمْنٰهُ مِنْ لَّدُنَّا عِلْمًا","bc":"fa wajadā 'abdam min 'ibādinā ātaināhu raḥmatam min 'indinā wa 'allamnāhu mil ladunnā 'ilmā","at":" Lalu mereka berdua bertemu dengan seorang hamba di antara hamba-hamba Kami, yang telah Kami berikan rahmat kepadanya dari sisi Kami, dan yang telah Kami ajarkan ilmu kepadanya dari sisi Kami."},{"id":"66","ar":"قَالَ لَهٗ مُوسٰى هَلْ اَتَّبِعُكَ عَلٰٓى اَنْ تُعَلِّمَنِ مِمَّا عُلِّمْتَ رُشْدًا","bc":"qāla lahụ mụsā hal attabi'uka 'alā an tu'allimani mimmā 'ullimta rusydā","at":"Musa berkata kepadanya, “Bolehkah aku mengikutimu agar engkau mengajarkan kepadaku (ilmu yang benar) yang telah diajarkan kepadamu (untuk menjadi) petunjuk?”"},{"id":"67","ar":"قَالَ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا ","bc":"qāla innaka lan tastaṭī'a ma'iya ṣabrā","at":"Dia menjawab, “Sungguh, engkau tidak akan sanggup sabar bersamaku."},{"id":"68","ar":"وَكَيْفَ تَصْبِرُ عَلٰى مَا لَمْ تُحِطْ بِهٖ خُبْرًا","bc":"wa kaifa taṣbiru 'alā mā lam tuḥiṭ bihī khubrā","at":"Dan bagaimana engkau akan dapat bersabar atas sesuatu, sedang engkau belum mempunyai pengetahuan yang cukup tentang hal itu?”"},{"id":"69","ar":"قَالَ سَتَجِدُنِيْٓ اِنْ شَاۤءَ اللّٰهُ صَابِرًا وَّلَآ اَعْصِيْ لَكَ اَمْرًا","bc":"qāla satajidunī in syā`allāhu ṣābiraw wa lā a'ṣī laka amrā","at":"Dia (Musa) berkata, “Insya Allah akan engkau dapati aku orang yang sabar, dan aku tidak akan menentangmu dalam urusan apa pun.”"},{"id":"70","ar":"قَالَ فَاِنِ اتَّبَعْتَنِيْ فَلَا تَسْـَٔلْنِيْ عَنْ شَيْءٍ حَتّٰٓى اُحْدِثَ لَكَ مِنْهُ ذِكْرًا ","bc":"qāla fa inittaba'tanī fa lā tas`alnī 'an syai`in ḥattā uḥdiṡa laka min-hu żikrā","at":"Dia berkata, “Jika engkau mengikutiku, maka janganlah engkau menanyakan kepadaku tentang sesuatu apa pun, sampai aku menerangkannya kepadamu.”"},{"id":"71","ar":"فَانْطَلَقَاۗ حَتّٰٓى اِذَا رَكِبَا فِى السَّفِيْنَةِ خَرَقَهَاۗ قَالَ اَخَرَقْتَهَا لِتُغْرِقَ اَهْلَهَاۚ لَقَدْ جِئْتَ شَيْـًٔا اِمْرًا ","bc":"fanṭalaqā, ḥattā iżā rakibā fis-safīnati kharaqahā, qāla a kharaqtahā litugriqa ahlahā, laqad ji`ta syai`an imrā","at":"Maka berjalanlah keduanya, hingga ketika keduanya menaiki perahu lalu dia melubanginya. Dia (Musa) berkata, “Mengapa engkau melubangi perahu itu, apakah untuk menenggelamkan penumpangnya?” Sungguh, engkau telah berbuat suatu kesalahan yang besar."},{"id":"72","ar":"قَالَ اَلَمْ اَقُلْ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا ","bc":"qāla a lam aqul innaka lan tastaṭī'a ma'iya ṣabrā","at":"Dia berkata, “Bukankah sudah aku katakan, bahwa sesungguhnya engkau tidak akan mampu sabar bersamaku?”"},{"id":"73","ar":"قَالَ لَا تُؤَاخِذْنِيْ بِمَا نَسِيْتُ وَلَا تُرْهِقْنِيْ مِنْ اَمْرِيْ عُسْرًا","bc":"qāla lā tu`ākhiżnī bimā nasītu wa lā tur-hiqnī min amrī 'usrā","at":"Dia (Musa) berkata, “Janganlah engkau menghukum aku karena kelupaanku dan janganlah engkau membebani aku dengan suatu kesulitan dalam urusanku.”"},{"id":"74","ar":"فَانْطَلَقَا ۗحَتّٰٓى اِذَا لَقِيَا غُلٰمًا فَقَتَلَهٗ ۙقَالَ اَقَتَلْتَ نَفْسًا زَكِيَّةً؈ۢبِغَيْرِ نَفْسٍۗ لَقَدْ جِئْتَ شَيْـًٔا نُكْرًا ۔","bc":"fanṭalaqā, ḥattā iżā laqiyā gulāman fa qatalahụ qāla a qatalta nafsan zakiyyatam bigairi nafs, laqad ji`ta syai`an nukrā","at":"Maka berjalanlah keduanya; hingga ketika keduanya berjumpa dengan seorang anak muda, maka dia membunuhnya. Dia (Musa) berkata, “Mengapa engkau bunuh jiwa yang bersih, bukan karena dia membunuh orang lain? Sungguh, engkau telah melakukan sesuatu yang sangat mungkar.”"},{"id":"75","ar":"۞ قَالَ اَلَمْ اَقُلْ لَّكَ اِنَّكَ لَنْ تَسْتَطِيْعَ مَعِيَ صَبْرًا ","bc":"qāla a lam aqul laka innaka lan tastaṭī'a ma'iya ṣabrā","at":"Dia berkata, “Bukankah sudah kukatakan kepadamu, bahwa engkau tidak akan mampu sabar bersamaku?”"},{"id":"76","ar":"قَالَ اِنْ سَاَلْتُكَ عَنْ شَيْءٍۢ بَعْدَهَا فَلَا تُصٰحِبْنِيْۚ قَدْ بَلَغْتَ مِنْ لَّدُنِّيْ عُذْرًا ","bc":"qāla in sa`altuka 'an syai`im ba'dahā fa lā tuṣāḥibnī, qad balagta mil ladunnī 'użrā","at":"Dia (Musa) berkata, “Jika aku bertanya kepadamu tentang sesuatu setelah ini, maka jangan lagi engkau memperbolehkan aku menyertaimu, sesungguhnya engkau sudah cukup (bersabar) menerima alasan dariku.”"},{"id":"77","ar":"فَانْطَلَقَا ۗحَتّٰىٓ اِذَآ اَتَيَآ اَهْلَ قَرْيَةِ ِۨاسْتَطْعَمَآ اَهْلَهَا فَاَبَوْا اَنْ يُّضَيِّفُوْهُمَا فَوَجَدَا فِيْهَا جِدَارًا يُّرِيْدُ اَنْ يَّنْقَضَّ فَاَقَامَهٗ ۗقَالَ لَوْ شِئْتَ لَتَّخَذْتَ عَلَيْهِ اَجْرًا ","bc":"fanṭalaqā, ḥattā iżā atayā ahla qaryatinistaṭ'amā ahlahā fa abau ay yuḍayyifụhumā fa wajadā fīhā jidāray yurīdu ay yangqaḍḍa fa aqāmah, qāla lau syi`ta lattakhażta 'alaihi ajrā","at":"Maka keduanya berjalan; hingga ketika keduanya sampai kepada penduduk suatu negeri, mereka berdua meminta dijamu oleh penduduknya, tetapi mereka (penduduk negeri itu) tidak mau menjamu mereka, kemudian keduanya mendapatkan dinding rumah yang hampir roboh (di negeri itu), lalu dia menegakkannya. Dia (Musa) berkata, “Jika engkau mau, niscaya engkau dapat meminta imbalan untuk itu.”"},{"id":"78","ar":"قَالَ هٰذَا فِرَاقُ بَيْنِيْ وَبَيْنِكَۚ سَاُنَبِّئُكَ بِتَأْوِيْلِ مَا لَمْ تَسْتَطِعْ عَّلَيْهِ صَبْرًا ","bc":"qāla hāżā firāqu bainī wa bainik, sa`unabbi`uka bita`wīli mā lam tastaṭi' 'alaihi ṣabrā","at":"Dia berkata, “Inilah perpisahan antara aku dengan engkau; aku akan memberikan penjelasan kepadamu atas perbuatan yang engkau tidak mampu sabar terhadapnya."},{"id":"79","ar":"اَمَّا السَّفِيْنَةُ فَكَانَتْ لِمَسٰكِيْنَ يَعْمَلُوْنَ فِى الْبَحْرِ فَاَرَدْتُّ اَنْ اَعِيْبَهَاۗ وَكَانَ وَرَاۤءَهُمْ مَّلِكٌ يَّأْخُذُ كُلَّ سَفِيْنَةٍ غَصْبًا ","bc":"ammas-safīnatu fa kānat limasākīna ya'malụna fil-baḥri fa arattu an a'ībahā, wa kāna warā`ahum malikuy ya`khużu kulla safīnatin gaṣbā","at":" Adapun perahu itu adalah milik orang miskin yang bekerja di laut; aku bermaksud merusaknya, karena di hadapan mereka ada seorang raja yang akan merampas setiap perahu."},{"id":"80","ar":"وَاَمَّا الْغُلٰمُ فَكَانَ اَبَوَاهُ مُؤْمِنَيْنِ فَخَشِيْنَآ اَنْ يُّرْهِقَهُمَا طُغْيَانًا وَّكُفْرًا ۚ","bc":"wa ammal-gulāmu fa kāna abawāhu mu`minaini fa khasyīnā ay yur-hiqahumā ṭugyānaw wa kufrā","at":"Dan adapun anak muda (kafir) itu, kedua orang tuanya mukmin, dan kami khawatir kalau dia akan memaksa kedua orang tuanya kepada kesesatan dan kekafiran."},{"id":"81","ar":"فَاَرَدْنَآ اَنْ يُّبْدِلَهُمَا رَبُّهُمَا خَيْرًا مِّنْهُ زَكٰوةً وَّاَقْرَبَ رُحْمًا ","bc":"fa aradnā ay yubdilahumā rabbuhumā khairam min-hu zakātaw wa aqraba ruḥmā","at":" Kemudian kami menghendaki, sekiranya Tuhan mereka menggantinya dengan (seorang anak) lain yang lebih baik kesuciannya daripada (anak) itu dan lebih sayang (kepada ibu bapaknya)."},{"id":"82","ar":"وَاَمَّا الْجِدَارُ فَكَانَ لِغُلٰمَيْنِ يَتِيْمَيْنِ فِى الْمَدِيْنَةِ وَكَانَ تَحْتَهٗ كَنْزٌ لَّهُمَا وَكَانَ اَبُوْهُمَا صَالِحًا ۚفَاَرَادَ رَبُّكَ اَنْ يَّبْلُغَآ اَشُدَّهُمَا وَيَسْتَخْرِجَا كَنْزَهُمَا رَحْمَةً مِّنْ رَّبِّكَۚ وَمَا فَعَلْتُهٗ عَنْ اَمْرِيْۗ ذٰلِكَ تَأْوِيْلُ مَا لَمْ تَسْطِعْ عَّلَيْهِ صَبْرًاۗ ","bc":"wa ammal-jidāru fa kāna ligulāmaini yatīmaini fil-madīnati wa kāna taḥtahụ kanzul lahumā wa kāna abụhumā ṣāliḥā, fa arāda rabbuka ay yablugā asyuddahumā wa yastakhrijā kanzahumā raḥmatam mir rabbik, wa mā fa'altuhụ 'an amrī, żālika ta`wīlu mā lam tasṭi' 'alaihi ṣabrā","at":". Dan adapun dinding rumah itu adalah milik dua anak yatim di kota itu, yang di bawahnya tersimpan harta bagi mereka berdua, dan ayahnya seorang yang saleh. Maka Tuhanmu menghendaki agar keduanya sampai dewasa dan keduanya mengeluarkan simpanannya itu sebagai rahmat dari Tuhanmu. Apa yang kuperbuat bukan menurut kemauanku sendiri. Itulah keterangan perbuatan-perbuatan yang engkau tidak sabar terhadapnya.” "},{"id":"83","ar":"وَيَسْـَٔلُوْنَكَ عَنْ ذِى الْقَرْنَيْنِۗ قُلْ سَاَتْلُوْا عَلَيْكُمْ مِّنْهُ ذِكْرًا ۗ","bc":"wa yas`alụnaka 'an żil-qarnaīn, qul sa`atlụ 'alaikum min-hu żikrā","at":" Dan mereka bertanya kepadamu (Muhammad) tentang Zulkarnain. Katakanlah, “Akan kubacakan kepadamu kisahnya.”"},{"id":"84","ar":"اِنَّا مَكَّنَّا لَهٗ فِى الْاَرْضِ وَاٰتَيْنٰهُ مِنْ كُلِّ شَيْءٍ سَبَبًا ۙ","bc":"innā makkannā lahụ fil-arḍi wa ātaināhu ming kulli syai`in sababā","at":" Sungguh, Kami telah memberi kedudukan kepadanya di bumi, dan Kami telah memberikan jalan kepadanya (untuk mencapai) segala sesuatu,"},{"id":"85","ar":"فَاَتْبَعَ سَبَبًا","bc":"fa atba'a sababā","at":"maka dia pun menempuh suatu jalan."},{"id":"86","ar":"حَتّٰىٓ اِذَا بَلَغَ مَغْرِبَ الشَّمْسِ وَجَدَهَا تَغْرُبُ فِيْ عَيْنٍ حَمِئَةٍ وَّوَجَدَ عِنْدَهَا قَوْمًا ەۗ قُلْنَا يٰذَا الْقَرْنَيْنِ اِمَّآ اَنْ تُعَذِّبَ وَاِمَّآ اَنْ تَتَّخِذَ فِيْهِمْ حُسْنًا","bc":"ḥattā iżā balaga magribasy-syamsi wajadahā tagrubu fī 'ainin ḥami`atiw wa wajada 'indahā qaumā, qulnā yā żal-qarnaini immā an tu'ażżiba wa immā an tattakhiża fīhim ḥusnā","at":" Hingga ketika dia telah sampai di tempat matahari terbenam, dia melihatnya (matahari) terbenam di dalam laut yang berlumpur hitam, dan di sana ditemukannya suatu kaum (tidak beragama). Kami berfirman, “Wahai Zulkarnain! Engkau boleh menghukum atau berbuat kebaikan (mengajak beriman) kepada mereka.”"},{"id":"87","ar":"قَالَ اَمَّا مَنْ ظَلَمَ فَسَوْفَ نُعَذِّبُهٗ ثُمَّ يُرَدُّ اِلٰى رَبِّهٖ فَيُعَذِّبُهٗ عَذَابًا نُّكْرًا ","bc":"qāla ammā man ẓalama fa saufa nu'ażżibuhụ ṡumma yuraddu ilā rabbihī fa yu'ażżibuhụ 'ażāban nukrā","at":" Dia (Zulkarnain) berkata, “Barangsiapa berbuat zalim, kami akan menghukumnya, lalu dia akan dikembalikan kepada Tuhannya, kemudian Tuhan mengazabnya dengan azab yang sangat keras."},{"id":"88","ar":"وَاَمَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحًا فَلَهٗ جَزَاۤءً ۨالْحُسْنٰىۚ وَسَنَقُوْلُ لَهٗ مِنْ اَمْرِنَا يُسْرًا ۗ","bc":"wa ammā man āmana wa 'amila ṣāliḥan fa lahụ jazā`anil-ḥusnā, wa sanaqụlu lahụ min amrinā yusrā","at":" Adapun orang yang beriman dan mengerjakan kebajikan, maka dia mendapat (pahala) yang terbaik sebagai balasan, dan akan kami sampaikan kepadanya perintah kami yang mudah-mudah.”"},{"id":"89","ar":"ثُمَّ اَتْبَعَ سَبَبًا ","bc":"ṡumma atba'a sababā","at":"Kemudian dia menempuh suatu jalan (yang lain)."},{"id":"90","ar":"حَتّٰىٓ اِذَا بَلَغَ مَطْلِعَ الشَّمْسِ وَجَدَهَا تَطْلُعُ عَلٰى قَوْمٍ لَّمْ نَجْعَلْ لَّهُمْ مِّنْ دُوْنِهَا سِتْرًا ۙ","bc":"ḥattā iżā balaga maṭli'asy-syamsi wajadahā taṭlu'u 'alā qaumil lam naj'al lahum min dụnihā sitrā","at":" Hingga ketika dia sampai di tempat terbit matahari (sebelah timur) didapatinya (matahari) bersinar di atas suatu kaum yang tidak Kami buatkan suatu pelindung bagi mereka dari (cahaya matahari) itu,"},{"id":"91","ar":"كَذٰلِكَۗ وَقَدْ اَحَطْنَا بِمَا لَدَيْهِ خُبْرًا ","bc":"każālik, wa qad aḥaṭnā bimā ladaihi khubrā","at":"demikianlah, dan sesungguhnya Kami mengetahui segala sesuatu yang ada padanya (Zulkarnain)."},{"id":"92","ar":"ثُمَّ اَتْبَعَ سَبَبًا ","bc":"ṡumma atba'a sababā","at":"Kemudian dia menempuh suatu jalan (yang lain lagi)."},{"id":"93","ar":"حَتّٰىٓ اِذَا بَلَغَ بَيْنَ السَّدَّيْنِ وَجَدَ مِنْ دُوْنِهِمَا قَوْمًاۙ لَّا يَكَادُوْنَ يَفْقَهُوْنَ قَوْلًا ","bc":"ḥattā iżā balaga bainas-saddaini wajada min dụnihimā qaumal lā yakādụna yafqahụna qaulā","at":" Hingga ketika dia sampai di antara dua gunung, didapatinya di belakang (kedua gunung itu) suatu kaum yang hampir tidak memahami pembicaraan."},{"id":"94","ar":"قَالُوْا يٰذَا الْقَرْنَيْنِ اِنَّ يَأْجُوْجَ وَمَأْجُوْجَ مُفْسِدُوْنَ فِى الْاَرْضِ فَهَلْ نَجْعَلُ لَكَ خَرْجًا عَلٰٓى اَنْ تَجْعَلَ بَيْنَنَا وَبَيْنَهُمْ سَدًّا ","bc":"qālụ yā żal-qarnaini inna ya`jụja wa ma`jụja mufsidụna fil-arḍi fa hal naj'alu laka kharjan 'alā an taj'ala bainanā wa bainahum saddā","at":" Mereka berkata, “Wahai Zulkarnain! Sungguh, Yakjuj dan Makjuj itu (makhluk yang) berbuat kerusakan di bumi, maka bolehkah kami membayarmu imbalan agar engkau membuatkan dinding penghalang antara kami dan mereka?”"},{"id":"95","ar":"قَالَ مَا مَكَّنِّيْ فِيْهِ رَبِّيْ خَيْرٌ فَاَعِيْنُوْنِيْ بِقُوَّةٍ اَجْعَلْ بَيْنَكُمْ وَبَيْنَهُمْ رَدْمًا ۙ","bc":"qāla mā makkannī fīhi rabbī khairun fa a'īnụnī biquwwatin aj'al bainakum wa bainahum radmā","at":"Dia (Zulkarnain) berkata, “Apa yang telah dianugerahkan Tuhan kepadaku lebih baik (daripada imbalanmu), maka bantulah aku dengan kekuatan, agar aku dapat membuatkan dinding penghalang antara kamu dan mereka."},{"id":"96","ar":"اٰتُوْنِيْ زُبَرَ الْحَدِيْدِۗ حَتّٰىٓ اِذَا سَاوٰى بَيْنَ الصَّدَفَيْنِ قَالَ انْفُخُوْا ۗحَتّٰىٓ اِذَا جَعَلَهٗ نَارًاۙ قَالَ اٰتُوْنِيْٓ اُفْرِغْ عَلَيْهِ قِطْرًا ۗ","bc":"ātụnī zubaral-ḥadīd, ḥattā iżā sāwā bainaṣ-ṣadafaini qālanfukhụ, ḥattā iżā ja'alahụ nārang qāla ātụnī ufrig 'alaihi qiṭrā","at":" Berilah aku potongan-potongan besi!” Hingga ketika (potongan) besi itu telah (terpasang) sama rata dengan kedua (puncak) gunung itu, dia (Zulkarnain) berkata, “Tiuplah (api itu)!” Ketika (besi) itu sudah menjadi (merah seperti) api, dia pun berkata, “Berilah aku tembaga (yang mendidih) agar kutuangkan ke atasnya (besi panas itu).”"},{"id":"97","ar":"فَمَا اسْطَاعُوْٓا اَنْ يَّظْهَرُوْهُ وَمَا اسْتَطَاعُوْا لَهٗ نَقْبًا ","bc":"fa masṭā'ū ay yaẓ-harụhu wa mastaṭā'ụ lahụ naqbā","at":" Maka mereka (Yakjuj dan Makjuj) tidak dapat mendakinya dan tidak dapat (pula) melubanginya."},{"id":"98","ar":"قَالَ هٰذَا رَحْمَةٌ مِّنْ رَّبِّيْۚ فَاِذَا جَاۤءَ وَعْدُ رَبِّيْ جَعَلَهٗ دَكَّاۤءَۚ وَكَانَ وَعْدُ رَبِّيْ حَقًّا ۗ","bc":"qāla hāżā raḥmatum mir rabbī, fa iżā jā`a wa'du rabbī ja'alahụ dakkā`, wa kāna wa'du rabbī ḥaqqā","at":"Dia (Zulkarnain) berkata, “(Dinding) ini adalah rahmat dari Tuhanku, maka apabila janji Tuhanku sudah datang, Dia akan menghancurluluhkannya; dan janji Tuhanku itu benar.”"},{"id":"99","ar":"۞ وَتَرَكْنَا بَعْضَهُمْ يَوْمَىِٕذٍ يَّمُوْجُ فِيْ بَعْضٍ وَّنُفِخَ فِى الصُّوْرِ فَجَمَعْنٰهُمْ جَمْعًا ۙ","bc":"wa taraknā ba'ḍahum yauma`iżiy yamụju fī ba'ḍiw wa nufikha fiṣ-ṣụri fa jama'nāhum jam'ā","at":"Dan pada hari itu Kami biarkan mereka (Yakjuj dan Makjuj) berbaur antara satu dengan yang lain, dan (apabila) sangkakala ditiup (lagi), akan Kami kumpulkan mereka semuanya."},{"id":"100","ar":"وَّعَرَضْنَا جَهَنَّمَ يَوْمَىِٕذٍ لِّلْكٰفِرِيْنَ عَرْضًا ۙ","bc":"wa 'araḍnā jahannama yauma`iżil lil-kāfirīna 'arḍā","at":"Dan Kami perlihatkan (neraka) Jahanam dengan jelas pada hari itu kepada orang kafir,"},{"id":"101","ar":" ۨالَّذِيْنَ كَانَتْ اَعْيُنُهُمْ فِيْ غِطَاۤءٍ عَنْ ذِكْرِيْ وَكَانُوْا لَا يَسْتَطِيْعُوْنَ سَمْعًا","bc":"allażīna kānat a'yunuhum fī giṭā`in 'an żikrī wa kānụ lā yastaṭī'ụna sam'ā","at":"(yaitu) orang yang mata (hati)nya dalam keadaan tertutup (tidak mampu) dari memperhatikan tanda-tanda (kebesaran)-Ku, dan mereka tidak sanggup mendengar."},{"id":"102","ar":"اَفَحَسِبَ الَّذِيْنَ كَفَرُوْٓا اَنْ يَّتَّخِذُوْا عِبَادِيْ مِنْ دُوْنِيْٓ اَوْلِيَاۤءَ ۗاِنَّآ اَعْتَدْنَا جَهَنَّمَ لِلْكٰفِرِيْنَ نُزُلًا ","bc":"a fa ḥasiballażīna kafarū ay yattakhiżụ 'ibādī min dụnī auliyā`, innā a'tadnā jahannama lil-kāfirīna nuzulā","at":"Maka apakah orang kafir menyangka bahwa mereka (dapat) mengambil hamba-hamba-Ku menjadi penolong selain Aku? Sungguh, Kami telah menyediakan (neraka) Jahanam sebagai tempat tinggal bagi orang-orang kafir."},{"id":"103","ar":"قُلْ هَلْ نُنَبِّئُكُمْ بِالْاَخْسَرِيْنَ اَعْمَالًا ۗ","bc":"qul hal nunabbi`ukum bil-akhsarīna a'mālā","at":"Katakanlah (Muhammad), “Apakah perlu Kami beritahukan kepadamu tentang orang yang paling rugi perbuatannya?”"},{"id":"104","ar":"اَلَّذِيْنَ ضَلَّ سَعْيُهُمْ فِى الْحَيٰوةِ الدُّنْيَا وَهُمْ يَحْسَبُوْنَ اَنَّهُمْ يُحْسِنُوْنَ صُنْعًا ","bc":"allażīna ḍalla sa'yuhum fil-ḥayātid-dun-yā wa hum yaḥsabụna annahum yuḥsinụna ṣun'ā","at":"(Yaitu) orang yang sia-sia perbuatannya dalam kehidupan dunia, sedangkan mereka mengira telah berbuat sebaik-baiknya."},{"id":"105","ar":"اُولٰۤىِٕكَ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِ رَبِّهِمْ وَلِقَاۤىِٕهٖ فَحَبِطَتْ اَعْمَالُهُمْ فَلَا نُقِيْمُ لَهُمْ يَوْمَ الْقِيٰمَةِ وَزْنًا ","bc":"ulā`ikallażīna kafarụ bi`āyāti rabbihim wa liqā`ihī fa ḥabiṭat a'māluhum fa lā nuqīmu lahum yaumal-qiyāmati waznā","at":"Mereka itu adalah orang yang mengingkari ayat-ayat Tuhan mereka dan (tidak percaya) terhadap pertemuan dengan-Nya. Maka sia-sia amal mereka, dan Kami tidak memberikan penimbangan terhadap (amal) mereka pada hari Kiamat."},{"id":"106","ar":"ذٰلِكَ جَزَاۤؤُهُمْ جَهَنَّمُ بِمَا كَفَرُوْا وَاتَّخَذُوْٓا اٰيٰتِيْ وَرُسُلِيْ هُزُوًا ","bc":"żālika jazā`uhum jahannamu bimā kafarụ wattakhażū āyātī wa rusulī huzuwā","at":"Demikianlah, balasan mereka itu neraka Jahanam, karena kekafiran mereka, dan karena mereka menjadikan ayat-ayat-Ku dan rasul-rasul-Ku sebagai bahan olok-olok."},{"id":"107","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ كَانَتْ لَهُمْ جَنّٰتُ الْفِرْدَوْسِ نُزُلًا ۙ","bc":"innallażīna āmanụ wa 'amiluṣ-ṣāliḥāti kānat lahum jannātul-firdausi nuzulā","at":"Sungguh, orang yang beriman dan mengerjakan kebajikan, untuk mereka disediakan surga Firdaus sebagai tempat tinggal,"},{"id":"108","ar":"خٰلِدِيْنَ فِيْهَا لَا يَبْغُوْنَ عَنْهَا حِوَلًا ","bc":"khālidīna fīhā lā yabgụna 'an-hā ḥiwalā","at":"mereka kekal di dalamnya, mereka tidak ingin pindah dari sana."},{"id":"109","ar":"قُلْ لَّوْ كَانَ الْبَحْرُ مِدَادًا لِّكَلِمٰتِ رَبِّيْ لَنَفِدَ الْبَحْرُ قَبْلَ اَنْ تَنْفَدَ كَلِمٰتُ رَبِّيْ وَلَوْ جِئْنَا بِمِثْلِهٖ مَدَدًا ","bc":"qul lau kānal-baḥru midādal likalimāti rabbī lanafidal-baḥru qabla an tanfada kalimātu rabbī walau ji`nā bimiṡlihī madadā","at":"Katakanlah (Muhammad), “Seandainya lautan menjadi tinta untuk (menulis) kalimat-kalimat Tuhanku, maka pasti habislah lautan itu sebelum selesai (penulisan) kalimat-kalimat Tuhanku, meskipun Kami datangkan tambahan sebanyak itu (pula).”"},{"id":"110","ar":"قُلْ اِنَّمَآ اَنَا۠ بَشَرٌ مِّثْلُكُمْ يُوْحٰٓى اِلَيَّ اَنَّمَآ اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌۚ فَمَنْ كَانَ يَرْجُوْا لِقَاۤءَ رَبِّهٖ فَلْيَعْمَلْ عَمَلًا صَالِحًا وَّلَا يُشْرِكْ بِعِبَادَةِ رَبِّهٖٓ اَحَدًا","bc":"qul innamā ana basyarum miṡlukum yụḥā ilayya annamā ilāhukum ilāhuw wāḥid, fa mang kāna yarjụ liqā`a rabbihī falya'mal 'amalan ṣāliḥaw wa lā yusyrik bi'ibādati rabbihī aḥadā","at":"Katakanlah (Muhammad), “Sesungguhnya aku ini hanya seorang manusia seperti kamu, yang telah menerima wahyu, bahwa sesungguhnya Tuhan kamu adalah Tuhan Yang Maha Esa.” Maka barangsiapa mengharap pertemuan dengan Tuhannya maka hendaklah dia mengerjakan kebajikan dan janganlah dia mempersekutukan dengan sesuatu pun dalam beribadah kepada Tuhannya.”"}],al_kausar=[{"id":"1","ar":"اِنَّآ اَعْطَيْنٰكَ الْكَوْثَرَۗ","bc":"innā a'ṭainākal-kauṡar","at":"Sungguh, Kami telah memberimu (Muhammad) nikmat yang banyak."},{"id":"2","ar":"فَصَلِّ لِرَبِّكَ وَانْحَرْۗ","bc":"fa ṣalli lirabbika wan-ḥar","at":"Maka laksanakanlah salat karena Tuhanmu, dan berkurbanlah (sebagai ibadah dan mendekatkan diri kepada Allah)."},{"id":"3","ar":"اِنَّ شَانِئَكَ هُوَ الْاَبْتَرُ","bc":"inna syāni`aka huwal-abtar","at":"Sungguh, orang-orang yang membencimu dialah yang terputus (dari rahmat Allah)."}],al_lahab=[{"id":"1","ar":"تَبَّتْ يَدَآ اَبِيْ لَهَبٍ وَّتَبَّۗ","bc":"tabbat yadā abī lahabiw wa tabb","at":"Binasalah kedua tangan Abu Lahab dan benar-benar binasa dia!"},{"id":"2","ar":"مَآ اَغْنٰى عَنْهُ مَالُهٗ وَمَا كَسَبَۗ","bc":"mā agnā 'an-hu māluhụ wa mā kasab","at":"Tidaklah berguna baginya hartanya dan apa yang dia usahakan."},{"id":"3","ar":"سَيَصْلٰى نَارًا ذَاتَ لَهَبٍۙ","bc":"sayaṣlā nāran żāta lahab","at":"Kelak dia akan masuk ke dalam api yang bergejolak (neraka)."},{"id":"4","ar":"وَّامْرَاَتُهٗ ۗحَمَّالَةَ الْحَطَبِۚ","bc":"wamra`atuh, ḥammālatal-ḥaṭab","at":"Dan (begitu pula) istrinya, pembawa kayu bakar (penyebar fitnah). "},{"id":"5","ar":"فِيْ جِيْدِهَا حَبْلٌ مِّنْ مَّسَدٍ","bc":"fī jīdihā ḥablum mim masad","at":"Di lehernya ada tali dari sabut yang dipintal."}],al_lail=[{"id":"1","ar":"وَالَّيْلِ اِذَا يَغْشٰىۙ","bc":"wal-laili iżā yagsyā","at":"Demi malam apabila menutupi (cahaya siang),"},{"id":"2","ar":"وَالنَّهَارِ اِذَا تَجَلّٰىۙ","bc":"wan-nahāri iżā tajallā","at":"demi siang apabila terang benderang,"},{"id":"3","ar":"وَمَا خَلَقَ الذَّكَرَ وَالْاُنْثٰىٓ ۙ","bc":"wa mā khalaqaż-żakara wal-unṡā","at":"demi penciptaan laki-laki dan perempuan,"},{"id":"4","ar":"اِنَّ سَعْيَكُمْ لَشَتّٰىۗ","bc":"inna sa'yakum lasyattā","at":"sungguh, usahamu memang beraneka macam."},{"id":"5","ar":"فَاَمَّا مَنْ اَعْطٰى وَاتَّقٰىۙ","bc":"fa ammā man a'ṭā wattaqā","at":"Maka barangsiapa memberikan (hartanya di jalan Allah) dan bertakwa,"},{"id":"6","ar":"وَصَدَّقَ بِالْحُسْنٰىۙ","bc":"wa ṣaddaqa bil-ḥusnā","at":"dan membenarkan (adanya pahala) yang terbaik (surga),"},{"id":"7","ar":"فَسَنُيَسِّرُهٗ لِلْيُسْرٰىۗ","bc":"fa sanuyassiruhụ lil-yusrā","at":"maka akan Kami mudahkan baginya jalan menuju kemudahan (kebahagiaan),"},{"id":"8","ar":"وَاَمَّا مَنْۢ بَخِلَ وَاسْتَغْنٰىۙ","bc":"wa ammā mam bakhila wastagnā","at":"dan adapun orang yang kikir dan merasa dirinya cukup (tidak perlu pertolongan Allah),"},{"id":"9","ar":"وَكَذَّبَ بِالْحُسْنٰىۙ","bc":"wa każżaba bil-ḥusnā","at":"serta mendustakan (pahala) yang terbaik,"},{"id":"10","ar":"فَسَنُيَسِّرُهٗ لِلْعُسْرٰىۗ","bc":"fa sanuyassiruhụ lil-'usrā","at":"maka akan Kami mudahkan baginya jalan menuju kesukaran (kesengsaraan),"},{"id":"11","ar":"وَمَا يُغْنِيْ عَنْهُ مَالُهٗٓ اِذَا تَرَدّٰىٓۙ","bc":"wa mā yugnī 'an-hu māluhū iżā taraddā","at":"dan hartanya tidak bermanfaat baginya apabila dia telah binasa."},{"id":"12","ar":"اِنَّ عَلَيْنَا لَلْهُدٰىۖ","bc":"inna 'alainā lal-hudā","at":"Sesungguhnya Kamilah yang memberi petunjuk,"},{"id":"13","ar":"وَاِنَّ لَنَا لَلْاٰخِرَةَ وَالْاُوْلٰىۗ","bc":"wa inna lanā lal-ākhirata wal-ụlā","at":"dan sesungguhnya milik Kamilah akhirat dan dunia itu."},{"id":"14","ar":"فَاَنْذَرْتُكُمْ نَارًا تَلَظّٰىۚ","bc":"fa anżartukum nāran talaẓẓā","at":"Maka Aku memperingatkan kamu dengan neraka yang menyala-nyala,"},{"id":"15","ar":"لَا يَصْلٰىهَآ اِلَّا الْاَشْقَىۙ","bc":"lā yaṣlāhā illal-asyqā","at":"yang hanya dimasuki oleh orang yang paling celaka,"},{"id":"16","ar":"الَّذِيْ كَذَّبَ وَتَوَلّٰىۗ","bc":"allażī każżaba wa tawallā","at":"yang mendustakan (kebenaran) dan berpaling (dari iman)."},{"id":"17","ar":"وَسَيُجَنَّبُهَا الْاَتْقَىۙ","bc":"wa sayujannabuhal-atqā","at":"Dan akan dijauhkan darinya (neraka) orang yang paling bertakwa, "},{"id":"18","ar":"الَّذِيْ يُؤْتِيْ مَالَهٗ يَتَزَكّٰىۚ","bc":"allażī yu`tī mālahụ yatazakkā","at":"yang menginfakkan hartanya (di jalan Allah) untuk membersihkan (dirinya),"},{"id":"19","ar":"وَمَا لِاَحَدٍ عِنْدَهٗ مِنْ نِّعْمَةٍ تُجْزٰىٓۙ","bc":"wa mā li`aḥadin 'indahụ min ni'matin tujzā","at":"dan tidak ada seorang pun memberikan suatu nikmat padanya yang harus dibalasnya,"},{"id":"20","ar":"اِلَّا ابْتِغَاۤءَ وَجْهِ رَبِّهِ الْاَعْلٰىۚ","bc":"illabtigā`a waj-hi rabbihil-a'lā","at":"tetapi (dia memberikan itu semata-mata) karena mencari keridaan Tuhannya Yang Mahatinggi."},{"id":"21","ar":"وَلَسَوْفَ يَرْضٰى","bc":"wa lasaufa yarḍā","at":"Dan niscaya kelak dia akan mendapat kesenangan (yang sempurna)."}],al_maarij=[{"id":"1","ar":"سَاَلَ سَاۤىِٕلٌۢ بِعَذَابٍ وَّاقِعٍۙ","bc":"sa`ala sā`ilum bi'ażābiw wāqi'","at":"Seseorang bertanya tentang azab yang pasti terjadi, "},{"id":"2","ar":"لِّلْكٰفِرِيْنَ لَيْسَ لَهٗ دَافِعٌۙ","bc":"lil-kāfirīna laisa lahụ dāfi'","at":"bagi orang-orang kafir, yang tidak seorang pun dapat menolaknya,"},{"id":"3","ar":"مِّنَ اللّٰهِ ذِى الْمَعَارِجِۗ","bc":"minallāhi żil-ma'ārij","at":"(Azab) dari Allah, yang memiliki tempat-tempat naik."},{"id":"4","ar":"تَعْرُجُ الْمَلٰۤىِٕكَةُ وَالرُّوْحُ اِلَيْهِ فِيْ يَوْمٍ كَانَ مِقْدَارُهٗ خَمْسِيْنَ اَلْفَ سَنَةٍۚ","bc":"ta'rujul-malā`ikatu war-rụḥu ilaihi fī yauming kāna miqdāruhụ khamsīna alfa sanah","at":"Para malaikat dan Jibril naik (menghadap) kepada Tuhan, dalam sehari setara dengan lima puluh ribu tahun."},{"id":"5","ar":"فَاصْبِرْ صَبْرًا جَمِيْلًا","bc":"faṣbir ṣabran jamīlā","at":"Maka bersabarlah engkau (Muhammad) dengan kesabaran yang baik."},{"id":"6","ar":"اِنَّهُمْ يَرَوْنَهٗ بَعِيْدًاۙ","bc":"innahum yaraunahụ ba'īdā","at":"Mereka memandang (azab) itu jauh (mustahil). "},{"id":"7","ar":"وَّنَرٰىهُ قَرِيْبًاۗ","bc":"wa narāhu qarībā","at":"Sedangkan Kami memandangnya dekat (pasti terjadi). "},{"id":"8","ar":"يَوْمَ تَكُوْنُ السَّمَاۤءُ كَالْمُهْلِۙ","bc":"yauma takụnus-samā`u kal-muhl","at":"(Ingatlah) pada hari ketika langit men-jadi bagaikan cairan tembaga, "},{"id":"9","ar":"وَتَكُوْنُ الْجِبَالُ كَالْعِهْنِۙ","bc":"wa takụnul-jibālu kal-'ihn","at":"dan gunung-gunung bagaikan bulu (yang beterbangan), "},{"id":"10","ar":"وَلَا يَسْـَٔلُ حَمِيْمٌ حَمِيْمًاۚ ","bc":"wa lā yas`alu ḥamīmun ḥamīmā","at":"dan tidak ada seorang teman karib pun menanyakan temannya,"},{"id":"11","ar":"يُبَصَّرُوْنَهُمْۗ يَوَدُّ الْمُجْرِمُ لَوْ يَفْتَدِيْ مِنْ عَذَابِ يَوْمِىِٕذٍۢ بِبَنِيْهِۙ","bc":"yubaṣṣarụnahum, yawaddul-mujrimu lau yaftadī min 'ażābi yaumi`iżim bibanīh","at":"sedang mereka saling melihat. Pada hari itu, orang yang berdosa ingin sekiranya dia dapat menebus (dirinya) dari azab dengan anak-anaknya, "},{"id":"12","ar":"وَصَاحِبَتِهٖ وَاَخِيْهِۙ","bc":"wa ṣāḥibatihī wa akhīh","at":"dan istrinya dan saudaranya, "},{"id":"13","ar":"وَفَصِيْلَتِهِ الَّتِيْ تُـْٔوِيْهِۙ","bc":"wa faṣīlatihillatī tu`wīh","at":"dan keluarga yang melindunginya (di dunia),"},{"id":"14","ar":"وَمَنْ فِى الْاَرْضِ جَمِيْعًاۙ ثُمَّ يُنْجِيْهِۙ","bc":"wa man fil-arḍi jamī'an ṡumma yunjīh","at":"dan orang-orang di bumi seluruhnya, kemudian mengharapkan (tebusan) itu dapat menyelamatkannya."},{"id":"15","ar":"كَلَّاۗ اِنَّهَا لَظٰىۙ","bc":"kallā, innahā laẓā","at":"Sama sekali tidak! Sungguh, neraka itu api yang bergejolak,"},{"id":"16","ar":"نَزَّاعَةً لِّلشَّوٰىۚ","bc":"nazzā'atal lisy-syawā","at":"yang mengelupaskan kulit kepala. "},{"id":"17","ar":"تَدْعُوْا مَنْ اَدْبَرَ وَتَوَلّٰىۙ","bc":"tad'ụ man adbara wa tawallā","at":"Yang memanggil orang yang membelakangi dan yang berpaling (dari agama),"},{"id":"18","ar":"وَجَمَعَ فَاَوْعٰى ","bc":"wa jama'a fa au'ā","at":"dan orang yang mengumpulkan (harta benda) lalu menyimpannya. "},{"id":"19","ar":"۞ اِنَّ الْاِنْسَانَ خُلِقَ هَلُوْعًاۙ","bc":"innal-insāna khuliqa halụ'ā","at":"Sungguh, manusia diciptakan bersifat suka mengeluh. "},{"id":"20","ar":"اِذَا مَسَّهُ الشَّرُّ جَزُوْعًاۙ","bc":"iżā massahusy-syarru jazụ'ā","at":"Apabila dia ditimpa kesusahan dia berkeluh kesah, "},{"id":"21","ar":"وَّاِذَا مَسَّهُ الْخَيْرُ مَنُوْعًاۙ","bc":"wa iżā massahul-khairu manụ'ā","at":"dan apabila mendapat kebaikan (harta) dia jadi kikir, "},{"id":"22","ar":"اِلَّا الْمُصَلِّيْنَۙ","bc":"illal-muṣallīn","at":"kecuali orang-orang yang melaksanakan salat, "},{"id":"23","ar":"الَّذِيْنَ هُمْ عَلٰى صَلَاتِهِمْ دَاۤىِٕمُوْنَۖ","bc":"allażīna hum 'alā ṣalātihim dā`imụn","at":"mereka yang tetap setia melaksanakan salatnya, "},{"id":"24","ar":"وَالَّذِيْنَ فِيْٓ اَمْوَالِهِمْ حَقٌّ مَّعْلُوْمٌۖ","bc":"wallażīna fī amwālihim ḥaqqum ma'lụm","at":"dan orang-orang yang dalam hartanya disiapkan bagian tertentu, "},{"id":"25","ar":"لِّلسَّاۤىِٕلِ وَالْمَحْرُوْمِۖ","bc":"lis-sā`ili wal-maḥrụm","at":"bagi orang (miskin) yang meminta dan yang tidak meminta, "},{"id":"26","ar":"وَالَّذِيْنَ يُصَدِّقُوْنَ بِيَوْمِ الدِّيْنِۖ","bc":"wallażīna yuṣaddiqụna biyaumid-dīn","at":"dan orang-orang yang mempercayai hari pembalasan, "},{"id":"27","ar":"وَالَّذِيْنَ هُمْ مِّنْ عَذَابِ رَبِّهِمْ مُّشْفِقُوْنَۚ","bc":"wallażīna hum min 'ażābi rabbihim musyfiqụn","at":"dan orang-orang yang takut terhadap azab Tuhannya, "},{"id":"28","ar":"اِنَّ عَذَابَ رَبِّهِمْ غَيْرُ مَأْمُوْنٍۖ","bc":"inna 'ażāba rabbihim gairu ma`mụn","at":"sesungguhnya terhadap azab Tuhan mereka, tidak ada seseorang yang merasa aman (dari kedatangannya), "},{"id":"29","ar":"وَّالَّذِيْنَ هُمْ لِفُرُوْجِهِمْ حٰفِظُوْنَۙ","bc":"wallażīna hum lifurụjihim ḥāfiẓụn","at":"dan orang-orang yang memelihara kemaluannya, "},{"id":"30","ar":"اِلَّا عَلٰٓى اَزْوَاجِهِمْ اَوْ مَا مَلَكَتْ اَيْمَانُهُمْ فَاِنَّهُمْ غَيْرُ مَلُوْمِيْنَۚ","bc":"illā 'alā azwājihim au mā malakat aimānuhum fa innahum gairu malụmīn","at":"kecuali terhadap istri-istri mereka atau hamba sahaya yang mereka miliki maka sesungguhnya mereka tidak tercela. "},{"id":"31","ar":"فَمَنِ ابْتَغٰى وَرَاۤءَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْعٰدُوْنَۚ","bc":"fa manibtagā warā`a żālika fa ulā`ika humul-'ādụn","at":"Maka barangsiapa mencari di luar itu (seperti zina, homoseks dan lesbian), mereka itulah orang-orang yang melampaui batas. "},{"id":"32","ar":"وَالَّذِيْنَ هُمْ لِاَمٰنٰتِهِمْ وَعَهْدِهِمْ رَاعُوْنَۖ","bc":"wallażīna hum li`amānātihim wa 'ahdihim rā'ụn","at":"Dan orang-orang yang memelihara amanat dan janjinya, "},{"id":"33","ar":"وَالَّذِيْنَ هُمْ بِشَهٰدٰتِهِمْ قَاۤىِٕمُوْنَۖ","bc":"wallażīna hum bisyahādātihim qā`imụn","at":"dan orang-orang yang berpegang teguh pada kesaksiannya, "},{"id":"34","ar":"وَالَّذِيْنَ هُمْ عَلٰى صَلَاتِهِمْ يُحَافِظُوْنَۖ","bc":"wallażīna hum 'alā ṣalātihim yuḥāfiẓụn","at":"dan orang-orang yang memelihara salatnya."},{"id":"35","ar":"اُولٰۤىِٕكَ فِيْ جَنّٰتٍ مُّكْرَمُوْنَ ۗ","bc":"ulā`ika fī jannātim mukramụn","at":"Mereka itu dimuliakan di dalam surga. "},{"id":"36","ar":"فَمَالِ الَّذِيْنَ كَفَرُوْا قِبَلَكَ مُهْطِعِيْنَۙ","bc":"fa mālillażīna kafarụ qibalaka muhṭi'īn","at":"Maka mengapa orang-orang kafir itu datang bergegas ke hadapanmu (Muhammad), "},{"id":"37","ar":"عَنِ الْيَمِيْنِ وَعَنِ الشِّمَالِ عِزِيْنَ ","bc":"'anil-yamīni wa 'anisy-syimāli 'izīn","at":"dari kanan dan dari kiri dengan berkelompok-kelompok?"},{"id":"38","ar":"اَيَطْمَعُ كُلُّ امْرِئٍ مِّنْهُمْ اَنْ يُّدْخَلَ جَنَّةَ نَعِيْمٍۙ","bc":"a yaṭma'u kullumri`im min-hum ay yudkhala jannata na'īm","at":"Apakah setiap orang dari orang-orang kafir itu ingin masuk surga yang penuh kenikmatan?"},{"id":"39","ar":"كَلَّاۗ اِنَّا خَلَقْنٰهُمْ مِّمَّا يَعْلَمُوْنَ ","bc":"kallā, innā khalaqnāhum mimmā ya'lamụn","at":"Tidak mungkin! Sesungguhnya Kami menciptakan mereka dari apa yang mereka ketahui."},{"id":"40","ar":"فَلَآ اُقْسِمُ بِرَبِّ الْمَشَارِقِ وَالْمَغٰرِبِ اِنَّا لَقٰدِرُوْنَۙ","bc":"fa lā uqsimu birabbil-masyāriqi wal-magāribi innā laqādirụn","at":"Maka Aku bersumpah demi Tuhan yang mengatur tempat-tempat terbit dan terbenamnya (matahari, bulan dan bintang), sungguh, Kami pasti mampu, "},{"id":"41","ar":"عَلٰٓى اَنْ نُّبَدِّلَ خَيْرًا مِّنْهُمْۙ وَمَا نَحْنُ بِمَسْبُوْقِيْنَ ","bc":"'alā an nubaddila khairam min-hum wa mā naḥnu bimasbụqīn","at":"untuk mengganti (mereka) dengan kaum yang lebih baik dari mereka, dan Kami tidak dapat dikalahkan. "},{"id":"42","ar":"فَذَرْهُمْ يَخُوْضُوْا وَيَلْعَبُوْا حَتّٰى يُلٰقُوْا يَوْمَهُمُ الَّذِيْ يُوْعَدُوْنَۙ","bc":"fa żar-hum yakhụḍụ wa yal'abụ ḥattā yulāqụ yaumahumullażī yụ'adụn","at":"Maka biarkanlah mereka tenggelam dan bermain-main (dalam kesesatan) sampai mereka menjumpai hari yang diancamkan kepada mereka, "},{"id":"43","ar":"يَوْمَ يَخْرُجُوْنَ مِنَ الْاَجْدَاثِ سِرَاعًا كَاَنَّهُمْ اِلٰى نُصُبٍ يُّوْفِضُوْنَۙ","bc":"yauma yakhrujụna minal-ajdāṡi sirā'ang ka`annahum ilā nuṣubiy yụfiḍụn","at":"(yaitu) pada hari ketika mereka keluar dari kubur dengan cepat seakan-akan mereka pergi dengan segera kepada berhala-berhala (sewaktu di dunia),"},{"id":"44","ar":"خَاشِعَةً اَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ۗذٰلِكَ الْيَوْمُ الَّذِيْ كَانُوْا يُوْعَدُوْنَ ","bc":"khāsyi'atan abṣāruhum tar-haquhum żillah, żālikal-yaumullażī kānụ yụ'adụn","at":"pandangan mereka tertunduk ke bawah diliputi kehinaan. Itulah hari yang diancamkan kepada mereka."}],al_maidah=[{"id":"1","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَوْفُوْا بِالْعُقُوْدِۗ اُحِلَّتْ لَكُمْ بَهِيْمَةُ الْاَنْعَامِ اِلَّا مَا يُتْلٰى عَلَيْكُمْ غَيْرَ مُحِلِّى الصَّيْدِ وَاَنْتُمْ حُرُمٌۗ اِنَّ اللّٰهَ يَحْكُمُ مَا يُرِيْدُ","bc":"yā ayyuhallażīna āmanū aufụ bil-'uqụd, uḥillat lakum bahīmatul-an'āmi illā mā yutlā 'alaikum gaira muḥilliṣ-ṣaidi wa antum ḥurum, innallāha yaḥkumu mā yurīd","at":"Wahai orang-orang yang beriman! Penuhilah janji-janji. Hewan ternak dihalalkan bagimu, kecuali yang akan disebutkan kepadamu, dengan tidak menghalalkan berburu ketika kamu sedang berihram (haji atau umrah). Sesungguhnya Allah menetapkan hukum sesuai dengan yang Dia kehendaki."},{"id":"2","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُحِلُّوْا شَعَاۤىِٕرَ اللّٰهِ وَلَا الشَّهْرَ الْحَرَامَ وَلَا الْهَدْيَ وَلَا الْقَلَاۤىِٕدَ وَلَآ اٰۤمِّيْنَ الْبَيْتَ الْحَرَامَ يَبْتَغُوْنَ فَضْلًا مِّنْ رَّبِّهِمْ وَرِضْوَانًا ۗوَاِذَا حَلَلْتُمْ فَاصْطَادُوْا ۗوَلَا يَجْرِمَنَّكُمْ شَنَاٰنُ قَوْمٍ اَنْ صَدُّوْكُمْ عَنِ الْمَسْجِدِ الْحَرَامِ اَنْ تَعْتَدُوْۘا وَتَعَاوَنُوْا عَلَى الْبِرِّ وَالتَّقْوٰىۖ وَلَا تَعَاوَنُوْا عَلَى الْاِثْمِ وَالْعُدْوَانِ ۖوَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ شَدِيْدُ الْعِقَابِ","bc":"yā ayyuhallażīna āmanụ lā tuḥillụ sya'ā`irallāhi wa lasy-syahral-ḥarāma wa lal-hadya wa lal-qalā`ida wa lā āmmīnal-baital-ḥarāma yabtagụna faḍlam mir rabbihim wa riḍwānā, wa iżā ḥalaltum faṣṭādụ, wa lā yajrimannakum syana`ānu qaumin an ṣaddụkum 'anil-masjidil-ḥarāmi an ta'tadụ, wa ta'āwanụ 'alal-birri wat-taqwā wa lā ta'āwanụ 'alal-iṡmi wal-'udwāni wattaqullāh, innallāha syadīdul-'iqāb","at":"Wahai orang-orang yang beriman! Janganlah kamu melanggar syiar-syiar kesucian Allah, dan jangan (melanggar kehormatan) bulan-bulan haram, jangan (mengganggu) hadyu (hewan-hewan kurban) dan qala'id (hewan-hewan kurban yang diberi tanda), dan jangan (pula) mengganggu orang-orang yang mengunjungi Baitulharam; mereka mencari karunia dan keridaan Tuhannya. Tetapi apabila kamu telah menyelesaikan ihram, maka bolehlah kamu berburu. Jangan sampai kebencian(mu) kepada suatu kaum karena mereka menghalang-halangimu dari Masjidilharam, mendorongmu berbuat melampaui batas (kepada mereka). Dan tolong-menolonglah kamu dalam (mengerjakan) kebajikan dan takwa, dan jangan tolong-menolong dalam berbuat dosa dan permusuhan. Bertakwalah kepada Allah, sungguh, Allah sangat berat siksaan-Nya."},{"id":"3","ar":"حُرِّمَتْ عَلَيْكُمُ الْمَيْتَةُ وَالدَّمُ وَلَحْمُ الْخِنْزِيْرِ وَمَآ اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖ وَالْمُنْخَنِقَةُ وَالْمَوْقُوْذَةُ وَالْمُتَرَدِّيَةُ وَالنَّطِيْحَةُ وَمَآ اَكَلَ السَّبُعُ اِلَّا مَا ذَكَّيْتُمْۗ وَمَا ذُبِحَ عَلَى النُّصُبِ وَاَنْ تَسْتَقْسِمُوْا بِالْاَزْلَامِۗ ذٰلِكُمْ فِسْقٌۗ اَلْيَوْمَ يَىِٕسَ الَّذِيْنَ كَفَرُوْا مِنْ دِيْنِكُمْ فَلَا تَخْشَوْهُمْ وَاخْشَوْنِۗ اَلْيَوْمَ اَكْمَلْتُ لَكُمْ دِيْنَكُمْ وَاَتْمَمْتُ عَلَيْكُمْ نِعْمَتِيْ وَرَضِيْتُ لَكُمُ الْاِسْلَامَ دِيْنًاۗ فَمَنِ اضْطُرَّ فِيْ مَخْمَصَةٍ غَيْرَ مُتَجَانِفٍ لِّاِثْمٍۙ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ","bc":"ḥurrimat 'alaikumul-maitatu wad-damu wa laḥmul-khinzīri wa mā uhilla ligairillāhi bihī wal-munkhaniqatu wal-mauqụżatu wal-mutaraddiyatu wan-naṭīḥatu wa mā akalas-sabu'u illā mā żakkaitum, wa mā żubiḥa 'alan-nuṣubi wa an tastaqsimụ bil-azlām, żālikum fisq, al-yauma ya`isallażīna kafarụ min dīnikum fa lā takhsyauhum wakhsyaụn, al-yauma akmaltu lakum dīnakum wa atmamtu 'alaikum ni'matī wa raḍītu lakumul-islāma dīnā, fa maniḍṭurra fī makhmaṣatin gaira mutajānifil li`iṡmin fa innallāha gafụrur raḥīm","at":"Diharamkan bagimu (memakan) bangkai, darah, daging babi, dan (daging) hewan yang disembelih bukan atas (nama) Allah, yang tercekik, yang dipukul, yang jatuh, yang ditanduk, dan yang diterkam binatang buas, kecuali yang sempat kamu sembelih. Dan (diharamkan pula) yang disembelih untuk berhala. Dan (diharamkan pula) mengundi nasib dengan azlam (anak panah), (karena) itu suatu perbuatan fasik. Pada hari ini orang-orang kafir telah putus asa untuk (mengalahkan) agamamu, sebab itu janganlah kamu takut kepada mereka, tetapi takutlah kepada-Ku. Pada hari ini telah Aku sempurnakan agamamu untukmu, dan telah Aku cukupkan nikmat-Ku bagimu, dan telah Aku ridai Islam sebagai agamamu. Tetapi barangsiapa terpaksa karena lapar, bukan karena ingin berbuat dosa, maka sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"4","ar":"يَسْـَٔلُوْنَكَ مَاذَآ اُحِلَّ لَهُمْۗ قُلْ اُحِلَّ لَكُمُ الطَّيِّبٰتُۙ وَمَا عَلَّمْتُمْ مِّنَ الْجَوَارِحِ مُكَلِّبِيْنَ تُعَلِّمُوْنَهُنَّ مِمَّا عَلَّمَكُمُ اللّٰهُ فَكُلُوْا مِمَّآ اَمْسَكْنَ عَلَيْكُمْ وَاذْكُرُوا اسْمَ اللّٰهِ عَلَيْهِ ۖوَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ","bc":"yas`alụnaka māżā uḥilla lahum, qul uḥilla lakumuṭ-ṭayyibātu wa mā 'allamtum minal-jawāriḥi mukallibīna tu'allimụnahunna mimmā 'allamakumullāhu fa kulụ mimmā amsakna 'alaikum ważkurusmallāhi 'alaihi wattaqullāh, innallāha sarī'ul-ḥisāb","at":"Mereka bertanya kepadamu (Muhammad), “Apakah yang dihalalkan bagi mereka?” Katakanlah, ”Yang dihalalkan bagimu (adalah makanan) yang baik-baik dan (buruan yang ditangkap) oleh binatang pemburu yang telah kamu latih untuk berburu, yang kamu latih menurut apa yang telah diajarkan Allah kepadamu. Maka makanlah apa yang ditangkapnya untukmu, dan sebutlah nama Allah (waktu melepasnya). Dan bertakwalah kepada Allah, sungguh, Allah sangat cepat perhitungan-Nya.”"},{"id":"5","ar":"اَلْيَوْمَ اُحِلَّ لَكُمُ الطَّيِّبٰتُۗ وَطَعَامُ الَّذِيْنَ اُوْتُوا الْكِتٰبَ حِلٌّ لَّكُمْ ۖوَطَعَامُكُمْ حِلٌّ لَّهُمْ ۖوَالْمُحْصَنٰتُ مِنَ الْمُؤْمِنٰتِ وَالْمُحْصَنٰتُ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ اِذَآ اٰتَيْتُمُوْهُنَّ اُجُوْرَهُنَّ مُحْصِنِيْنَ غَيْرَ مُسَافِحِيْنَ وَلَا مُتَّخِذِيْٓ اَخْدَانٍۗ وَمَنْ يَّكْفُرْ بِالْاِيْمَانِ فَقَدْ حَبِطَ عَمَلُهٗ ۖوَهُوَ فِى الْاٰخِرَةِ مِنَ الْخٰسِرِيْنَ ","bc":"al-yauma uḥilla lakumuṭ-ṭayyibāt, wa ṭa'āmullażīna ụtul-kitāba ḥillul lakum wa ṭa'āmukum ḥillul lahum wal-muḥṣanātu minal-mu`mināti wal-muḥṣanātu minallażīna ụtul-kitāba ming qablikum iżā ātaitumụhunna ujụrahunna muḥṣinīna gaira musāfiḥīna wa lā muttakhiżī akhdān, wa may yakfur bil-īmāni fa qad ḥabiṭa 'amaluhụ wa huwa fil-ākhirati minal-khāsirīn","at":"Pada hari ini dihalalkan bagimu segala yang baik-baik. Makanan (sembelihan) Ahli Kitab itu halal bagimu, dan makananmu halal bagi mereka. Dan (dihalalkan bagimu menikahi) perempuan-perempuan yang menjaga kehormatan di antara perempuan-perempuan yang beriman dan perempuan-perempuan yang menjaga kehormatan di antara orang-orang yang diberi kitab sebelum kamu, apabila kamu membayar maskawin mereka untuk menikahinya, tidak dengan maksud berzina dan bukan untuk menjadikan perempuan piaraan. Barangsiapa kafir setelah beriman, maka sungguh, sia-sia amal mereka, dan di akhirat dia termasuk orang-orang yang rugi."},{"id":"6","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا قُمْتُمْ اِلَى الصَّلٰوةِ فَاغْسِلُوْا وُجُوْهَكُمْ وَاَيْدِيَكُمْ اِلَى الْمَرَافِقِ وَامْسَحُوْا بِرُءُوْسِكُمْ وَاَرْجُلَكُمْ اِلَى الْكَعْبَيْنِۗ وَاِنْ كُنْتُمْ جُنُبًا فَاطَّهَّرُوْاۗ وَاِنْ كُنْتُمْ مَّرْضٰٓى اَوْ عَلٰى سَفَرٍ اَوْ جَاۤءَ اَحَدٌ مِّنْكُمْ مِّنَ الْغَاۤىِٕطِ اَوْ لٰمَسْتُمُ النِّسَاۤءَ فَلَمْ تَجِدُوْا مَاۤءً فَتَيَمَّمُوْا صَعِيْدًا طَيِّبًا فَامْسَحُوْا بِوُجُوْهِكُمْ وَاَيْدِيْكُمْ مِّنْهُ ۗمَا يُرِيْدُ اللّٰهُ لِيَجْعَلَ عَلَيْكُمْ مِّنْ حَرَجٍ وَّلٰكِنْ يُّرِيْدُ لِيُطَهِّرَكُمْ وَلِيُتِمَّ نِعْمَتَهٗ عَلَيْكُمْ لَعَلَّكُمْ تَشْكُرُوْنَ","bc":"yā ayyuhallażīna āmanū iżā qumtum ilaṣ-ṣalāti fagsilụ wujụhakum wa aidiyakum ilal-marāfiqi wamsaḥụ biru`ụsikum wa arjulakum ilal-ka'baīn, wa ing kuntum junuban faṭṭahharụ, wa ing kuntum marḍā au 'alā safarin au jā`a aḥadum mingkum minal-gā`iṭi au lāmastumun-nisā`a fa lam tajidụ mā`an fa tayammamụ ṣa'īdan ṭayyiban famsaḥụ biwujụhikum wa aidīkum min-h, mā yurīdullāhu liyaj'ala 'alaikum min ḥarajiw wa lākiy yurīdu liyuṭahhirakum wa liyutimma ni'matahụ 'alaikum la'allakum tasykurụn","at":"Wahai orang-orang yang beriman! Apabila kamu hendak melaksanakan salat, maka basuhlah wajahmu dan tanganmu sampai ke siku, dan sapulah kepalamu dan (basuh) kedua kakimu sampai ke kedua mata kaki. Jika kamu junub, maka mandilah. Dan jika kamu sakit atau dalam perjalanan atau kembali dari tempat buang air (kakus) atau menyentuh perempuan, maka jika kamu tidak memperoleh air, maka bertayamumlah dengan debu yang baik (suci); usaplah wajahmu dan tanganmu dengan (debu) itu. Allah tidak ingin menyulitkan kamu, tetapi Dia hendak membersihkan kamu dan menyempurnakan nikmat-Nya bagimu, agar kamu bersyukur."},{"id":"7","ar":"وَاذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ وَمِيْثَاقَهُ الَّذِيْ وَاثَقَكُمْ بِهٖٓ ۙاِذْ قُلْتُمْ سَمِعْنَا وَاَطَعْنَا ۖوَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ","bc":"ważkurụ ni'matallāhi 'alaikum wa mīṡāqahullażī wāṡaqakum bihī iż qultum sami'nā wa aṭa'nā wattaqullāh, innallāha 'alīmum biżātiṣ-ṣudụr","at":"Dan ingatlah akan karunia Allah kepadamu dan perjanjian-Nya yang telah diikatkan kepadamu, ketika kamu mengatakan, “Kami mendengar dan kami menaati.” Dan bertakwalah kepada Allah, sungguh, Allah Maha Mengetahui segala isi hati."},{"id":"8","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُوْنُوْا قَوَّامِيْنَ لِلّٰهِ شُهَدَاۤءَ بِالْقِسْطِۖ وَلَا يَجْرِمَنَّكُمْ شَنَاٰنُ قَوْمٍ عَلٰٓى اَلَّا تَعْدِلُوْا ۗاِعْدِلُوْاۗ هُوَ اَقْرَبُ لِلتَّقْوٰىۖ وَاتَّقُوا اللّٰهَ ۗاِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ","bc":"yā ayyuhallażīna āmanụ kụnụ qawwāmīna lillāhi syuhadā`a bil-qisṭi wa lā yajrimannakum syana`ānu qaumin 'alā allā ta'dilụ, i'dilụ, huwa aqrabu lit-taqwā wattaqullāh, innallāha khabīrum bimā ta'malụn","at":"Wahai orang-orang yang beriman! Jadilah kamu sebagai penegak keadilan karena Allah, (ketika) menjadi saksi dengan adil. Dan janganlah kebencianmu terhadap suatu kaum mendorong kamu untuk berlaku tidak adil. Berlaku adillah. Karena (adil) itu lebih dekat kepada takwa. Dan bertakwalah kepada Allah, sungguh, Allah Mahateliti terhadap apa yang kamu kerjakan."},{"id":"9","ar":"وَعَدَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِۙ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ عَظِيْمٌ","bc":"wa'adallāhullażīna āmanụ wa 'amiluṣ-ṣāliḥāti lahum magfiratuw wa ajrun 'aẓīm","at":"Allah telah menjanjikan kepada orang-orang yang beriman dan beramal saleh, (bahwa) mereka akan mendapat ampunan dan pahala yang besar."},{"id":"10","ar":"وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ","bc":"wallażīna kafarụ wa każżabụ bi`āyātinā ulā`ika aṣ-ḥābul-jaḥīm","at":"Adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itulah penghuni neraka."},{"id":"11","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ هَمَّ قَوْمٌ اَنْ يَّبْسُطُوْٓا اِلَيْكُمْ اَيْدِيَهُمْ فَكَفَّ اَيْدِيَهُمْ عَنْكُمْۚ وَاتَّقُوا اللّٰهَ ۗوَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ","bc":"yā ayyuhallażīna āmanużkurụ ni'matallāhi 'alaikum iż hamma qaumun ay yabsuṭū ilaikum aidiyahum fa kaffa aidiyahum 'angkum, wattaqullāh, wa 'alallāhi falyatawakkalil-mu`minụn","at":"Wahai orang-orang yang beriman! Ingatlah nikmat Allah (yang diberikan) kepadamu, ketika suatu kaum bermaksud hendak menyerangmu dengan tangannya, lalu Allah menahan tangan mereka dari kamu. Dan bertakwalah kepada Allah, dan hanya kepada Allah-lah hendaknya orang-orang beriman itu bertawakal."},{"id":"12","ar":"۞ وَلَقَدْ اَخَذَ اللّٰهُ مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَۚ وَبَعَثْنَا مِنْهُمُ اثْنَيْ عَشَرَ نَقِيْبًاۗ وَقَالَ اللّٰهُ اِنِّيْ مَعَكُمْ ۗ لَىِٕنْ اَقَمْتُمُ الصَّلٰوةَ وَاٰتَيْتُمُ الزَّكٰوةَ وَاٰمَنْتُمْ بِرُسُلِيْ وَعَزَّرْتُمُوْهُمْ وَاَقْرَضْتُمُ اللّٰهَ قَرْضًا حَسَنًا لَّاُكَفِّرَنَّ عَنْكُمْ سَيِّاٰتِكُمْ وَلَاُدْخِلَنَّكُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۚ فَمَنْ كَفَرَ بَعْدَ ذٰلِكَ مِنْكُمْ فَقَدْ ضَلَّ سَوَاۤءَ السَّبِيْلِ ","bc":"wa laqad akhażallāhu mīṡāqa banī isrā`īl, wa ba'aṡnā min-humuṡnai 'asyara naqībā, wa qālallāhu innī ma'akum, la`in aqamtumuṣ-ṣalāta wa ātaitumuz-zakāta wa āmantum birusulī wa 'azzartumụhum wa aqraḍtumullāha qarḍan ḥasanal la`ukaffiranna 'angkum sayyi`ātikum wa la`udkhilannakum jannātin tajrī min taḥtihal-an-hār, fa mang kafara ba'da żālika mingkum fa qad ḍalla sawā`as-sabīl","at":"Dan sungguh, Allah telah mengambil perjanjian dari Bani Israil dan Kami telah mengangkat dua belas orang pemimpin di antara mereka. Dan Allah berfirman, “Aku bersamamu.” Sungguh, jika kamu melaksanakan salat dan menunaikan zakat serta beriman kepada rasul-rasul-Ku dan kamu bantu mereka dan kamu pinjamkan kepada Allah pinjaman yang baik, pasti akan Aku hapus kesalahan-kesalahanmu, dan pasti akan Aku masukkan ke dalam surga yang mengalir di bawahnya sungai-sungai. Tetapi barangsiapa kafir di antaramu setelah itu, maka sesungguhnya dia telah tersesat dari jalan yang lurus.”"},{"id":"13","ar":"فَبِمَا نَقْضِهِمْ مِّيْثَاقَهُمْ لَعَنّٰهُمْ وَجَعَلْنَا قُلُوْبَهُمْ قٰسِيَةً ۚ يُحَرِّفُوْنَ الْكَلِمَ عَنْ مَّوَاضِعِهٖۙ وَنَسُوْا حَظًّا مِّمَّا ذُكِّرُوْا بِهٖۚ وَلَا تَزَالُ تَطَّلِعُ عَلٰى خَاۤىِٕنَةٍ مِّنْهُمْ اِلَّا قَلِيْلًا مِّنْهُمْ ۖ فَاعْفُ عَنْهُمْ وَاصْفَحْ ۗاِنَّ اللّٰهَ يُحِبُّ الْمُحْسِنِيْنَ","bc":"fa bimā naqḍihim mīṡāqahum la'annāhum wa ja'alnā qulụbahum qāsiyah, yuḥarrifụnal-kalima 'am mawāḍi'ihī wa nasụ ḥaẓẓam mimmā żukkirụ bih, wa lā tazālu taṭṭali'u 'alā khā`inatim min-hum illā qalīlam min-hum fa'fu 'an-hum waṣfaḥ, innallāha yuḥibbul-muḥsinīn","at":"(Tetapi) karena mereka melanggar janjinya, maka Kami melaknat mereka, dan Kami jadikan hati mereka keras membatu. Mereka suka mengubah firman (Allah) dari tempatnya, dan mereka (sengaja) melupakan sebagian pesan yang telah diperingatkan kepada mereka. Engkau (Muhammad) senantiasa akan melihat pengkhianatan dari mereka kecuali sekelompok kecil di antara mereka (yang tidak berkhianat), maka maafkanlah mereka dan biarkan mereka. Sungguh, Allah menyukai orang-orang yang berbuat baik."},{"id":"14","ar":"وَمِنَ الَّذِيْنَ قَالُوْٓا اِنَّا نَصٰرٰٓى اَخَذْنَا مِيْثَاقَهُمْ فَنَسُوْا حَظًّا مِّمَّا ذُكِّرُوْا بِهٖۖ فَاَغْرَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاۤءَ اِلٰى يَوْمِ الْقِيٰمَةِ ۗ وَسَوْفَ يُنَبِّئُهُمُ اللّٰهُ بِمَا كَانُوْا يَصْنَعُوْنَ","bc":"wa minallażīna qālū innā naṣārā akhażnā mīṡāqahum fa nasụ haẓẓam mimmā żukkirụ bihī fa agrainā bainahumul-'adāwata wal-bagḍā`a ilā yaumil-qiyāmah, wa saufa yunabbi`uhumullāhu bimā kānụ yaṣna'ụn","at":"Dan di antara orang-orang yang mengatakan, “Kami ini orang Nasrani,” Kami telah mengambil perjanjian mereka, tetapi mereka (sengaja) melupakan sebagian pesan yang telah diperingatkan kepada mereka, maka Kami timbulkan permusuhan dan kebencian di antara mereka hingga hari Kiamat. Dan kelak Allah akan memberitakan kepada mereka apa yang telah mereka kerjakan."},{"id":"15","ar":"يٰٓاَهْلَ الْكِتٰبِ قَدْ جَاۤءَكُمْ رَسُوْلُنَا يُبَيِّنُ لَكُمْ كَثِيْرًا مِّمَّا كُنْتُمْ تُخْفُوْنَ مِنَ الْكِتٰبِ وَيَعْفُوْا عَنْ كَثِيْرٍەۗ قَدْ جَاۤءَكُمْ مِّنَ اللّٰهِ نُوْرٌ وَّكِتٰبٌ مُّبِيْنٌۙ","bc":"yā ahlal-kitābi qad jā`akum rasụlunā yubayyinu lakum kaṡīram mimmā kuntum tukhfụna minal-kitābi wa ya'fụ 'ang kaṡīr, qad jā`akum minallāhi nụruw wa kitābum mubīn","at":"Wahai Ahli Kitab! Sungguh, Rasul Kami telah datang kepadamu, menjelaskan kepadamu banyak hal dari (isi) kitab yang kamu sembunyikan, dan banyak (pula) yang dibiarkannya. Sungguh, telah datang kepadamu cahaya dari Allah, dan Kitab yang menjelaskan."},{"id":"16","ar":"يَّهْدِيْ بِهِ اللّٰهُ مَنِ اتَّبَعَ رِضْوَانَهٗ سُبُلَ السَّلٰمِ وَيُخْرِجُهُمْ مِّنَ الظُّلُمٰتِ اِلَى النُّوْرِ بِاِذْنِهٖ وَيَهْدِيْهِمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ","bc":"yahdī bihillāhu manittaba'a riḍwānahụ subulas-salāmi wa yukhrijuhum minaẓ-ẓulumāti ilan-nụri bi`iżnihī wa yahdīhim ilā ṣirāṭim mustaqīm","at":"Dengan Kitab itulah Allah memberi petunjuk kepada orang yang mengikuti keridaan-Nya ke jalan keselamatan, dan (dengan Kitab itu pula) Allah mengeluarkan orang itu dari gelap gulita kepada cahaya dengan izin-Nya, dan menunjukkan ke jalan yang lurus."},{"id":"17","ar":"لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ هُوَ الْمَسِيْحُ ابْنُ مَرْيَمَۗ قُلْ فَمَنْ يَّمْلِكُ مِنَ اللّٰهِ شَيْـًٔا اِنْ اَرَادَ اَنْ يُّهْلِكَ الْمَسِيْحَ ابْنَ مَرْيَمَ وَاُمَّهٗ وَمَنْ فِى الْاَرْضِ جَمِيْعًا ۗوَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۗ يَخْلُقُ مَا يَشَاۤءُ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"laqad kafarallażīna qālū innallāha huwal-masīḥubnu maryam, qul fa may yamliku minallāhi syai`an in arāda ay yuhlikal-masīḥabna maryama wa ummahụ wa man fil-arḍi jamī'ā, wa lillāhi mulkus-samāwāti wal-arḍi wa mā bainahumā, yakhluqu mā yasyā`, wallāhu 'alā kulli syai`ing qadīr","at":"Sungguh, telah kafir orang yang berkata, “Sesungguhnya Allah itu dialah Al-Masih putra Maryam.” Katakanlah (Muhammad), “Siapakah yang dapat menghalang-halangi kehendak Allah, jika Dia hendak membinasakan Al-Masih putra Maryam beserta ibunya dan seluruh (manusia) yang berada di bumi?” Dan milik Allah-lah kerajaan langit dan bumi dan apa yang ada di antara keduanya. Dia menciptakan apa yang Dia Kehendaki. Dan Allah Mahakuasa atas segala sesuatu."},{"id":"18","ar":"وَقَالَتِ الْيَهُوْدُ وَالنَّصٰرٰى نَحْنُ اَبْنٰۤؤُ اللّٰهِ وَاَحِبَّاۤؤُهٗ ۗ قُلْ فَلِمَ يُعَذِّبُكُمْ بِذُنُوْبِكُمْ ۗ بَلْ اَنْتُمْ بَشَرٌ مِّمَّنْ خَلَقَۗ يَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُۗ وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۖوَاِلَيْهِ الْمَصِيْرُ","bc":"wa qālatil-yahụdu wan-naṣārā naḥnu abnā`ullāhi wa aḥibbā`uh, qul fa lima yu'ażżibukum biżunụbikum, bal antum basyarum mim man khalaq, yagfiru limay yasyā`u wa yu'ażżibu may yasyā`, wa lillāhi mulkus-samāwāti wal-arḍi wa mā bainahumā wa ilaihil-maṣīr","at":"Orang Yahudi dan Nasrani berkata, “Kami adalah anak-anak Allah dan kekasih-kekasih-Nya.” Katakanlah, “Mengapa Allah menyiksa kamu karena dosa-dosamu? Tidak, kamu adalah manusia (biasa) di antara orang-orang yang Dia ciptakan. Dia mengampuni siapa yang Dia kehendaki dan menyiksa siapa yang Dia kehendaki. Dan milik Allah seluruh kerajaan langit dan bumi serta apa yang ada di antara keduanya. Dan kepada-Nya semua akan kembali.”"},{"id":"19","ar":"يٰٓاَهْلَ الْكِتٰبِ قَدْ جَاۤءَكُمْ رَسُوْلُنَا يُبَيِّنُ لَكُمْ عَلٰى فَتْرَةٍ مِّنَ الرُّسُلِ اَنْ تَقُوْلُوْا مَا جَاۤءَنَا مِنْۢ بَشِيْرٍ وَّلَا نَذِيْرٍۗ فَقَدْ جَاۤءَكُمْ بَشِيْرٌ وَّنَذِيْرٌ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ","bc":"yā ahlal-kitābi qad jā`akum rasụlunā yubayyinu lakum 'alā fatratim minar-rusuli an taqụlụ mā jā`anā mim basyīriw wa lā nażīr, fa qad jā`akum basyīruw wa nażīr, wallāhu 'alā kulli syai`ing qadīr","at":"Wahai Ahli Kitab! Sungguh, Rasul Kami telah datang kepadamu, menjelaskan (syariat Kami) kepadamu ketika terputus (pengiriman) rasul-rasul, agar kamu tidak mengatakan, “Tidak ada yang datang kepada kami baik seorang pembawa berita gembira maupun seorang pemberi peringatan.” Sungguh, telah datang kepadamu pembawa berita gembira dan pemberi peringatan. Dan Allah Mahakuasa atas segala sesuatu."},{"id":"20","ar":"وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖ يٰقَوْمِ اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ جَعَلَ فِيْكُمْ اَنْۢبِيَاۤءَ وَجَعَلَكُمْ مُّلُوْكًاۙ وَّاٰتٰىكُمْ مَّا لَمْ يُؤْتِ اَحَدًا مِّنَ الْعٰلَمِيْنَ","bc":"wa iż qāla mụsā liqaumihī yā qaumiżkurụ ni'matallāhi 'alaikum iż ja'ala fīkum ambiyā`a wa ja'alakum mulụkaw wa ātākum mā lam yu`ti aḥadam minal-'ālamīn","at":"Dan (ingatlah) ketika Musa berkata kepada kaumnya, “Wahai kaumku! Ingatlah akan nikmat Allah kepadamu ketika Dia mengangkat nabi-nabi di antaramu, dan menjadikan kamu sebagai orang-orang merdeka, dan memberikan kepada kamu apa yang belum pernah diberikan kepada seorang pun di antara umat yang lain.”"},{"id":"21","ar":"يٰقَوْمِ ادْخُلُوا الْاَرْضَ الْمُقَدَّسَةَ الَّتِيْ كَتَبَ اللّٰهُ لَكُمْ وَلَا تَرْتَدُّوْا عَلٰٓى اَدْبَارِكُمْ فَتَنْقَلِبُوْا خٰسِرِيْنَ","bc":"yā qaumidkhulul-arḍal-muqaddasatallatī kataballāhu lakum wa lā tartaddụ 'alā adbārikum fa tangqalibụ khāsirīn","at":"Wahai kaumku! Masuklah ke tanah suci (Palestina) yang telah ditentukan Allah bagimu, dan janganlah kamu berbalik ke belakang (karena takut kepada musuh), nanti kamu menjadi orang yang rugi."},{"id":"22","ar":"قَالُوْا يٰمُوْسٰٓى اِنَّ فِيْهَا قَوْمًا جَبَّارِيْنَۖ وَاِنَّا لَنْ نَّدْخُلَهَا حَتّٰى يَخْرُجُوْا مِنْهَاۚ فَاِنْ يَّخْرُجُوْا مِنْهَا فَاِنَّا دٰخِلُوْنَ","bc":"qālụ yā mụsā inna fīhā qauman jabbārīna wa innā lan nadkhulahā ḥattā yakhrujụ min-hā, fa iy yakhrujụ min-hā fa innā dākhilụn","at":"Mereka berkata, “Wahai Musa! Sesungguhnya di dalam negeri itu ada orang-orang yang sangat kuat dan kejam, kami tidak akan memasukinya sebelum mereka keluar darinya. Jika mereka keluar dari sana, niscaya kami akan masuk.”"},{"id":"23","ar":"قَالَ رَجُلَانِ مِنَ الَّذِيْنَ يَخَافُوْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمَا ادْخُلُوْا عَلَيْهِمُ الْبَابَۚ فَاِذَا دَخَلْتُمُوْهُ فَاِنَّكُمْ غٰلِبُوْنَ ەۙ وَعَلَى اللّٰهِ فَتَوَكَّلُوْٓا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ","bc":"qāla rajulāni minallażīna yakhāfụna an'amallāhu 'alaihimadkhulụ 'alaihimul-bāb, fa iżā dakhaltumụhu fa innakum gālibụna wa 'alallāhi fa tawakkalū ing kuntum mu`minīn","at":"Berkatalah dua orang laki-laki di antara mereka yang bertakwa, yang telah diberi nikmat oleh Allah, “Serbulah mereka melalui pintu gerbang (negeri) itu. Jika kamu memasukinya niscaya kamu akan menang. Dan bertawakallah kamu hanya kepada Allah, jika kamu orang-orang beriman.”"},{"id":"24","ar":"قَالُوْا يٰمُوْسٰٓى اِنَّا لَنْ نَّدْخُلَهَآ اَبَدًا مَّا دَامُوْا فِيْهَا ۖفَاذْهَبْ اَنْتَ وَرَبُّكَ فَقَاتِلَآ اِنَّا هٰهُنَا قٰعِدُوْنَ","bc":"qālụ yā mụsā innā lan nadkhulahā abadam mā dāmụ fīhā faż-hab anta wa rabbuka fa qātilā innā hāhunā qā'idụn","at":"Mereka berkata, “Wahai Musa! Sampai kapan pun kami tidak akan memasukinya selama mereka masih ada di dalamnya, karena itu pergilah engkau bersama Tuhanmu, dan berperanglah kamu berdua. Biarlah kami tetap (menanti) di sini saja.”"},{"id":"25","ar":"قَالَ رَبِّ اِنِّيْ لَآ اَمْلِكُ اِلَّا نَفْسِيْ وَاَخِيْ فَافْرُقْ بَيْنَنَا وَبَيْنَ الْقَوْمِ الْفٰسِقِيْنَ","bc":"qāla rabbi innī lā amliku illā nafsī wa akhī fafruq bainanā wa bainal-qaumil-fāsiqīn","at":"Dia (Musa) berkata, “Ya Tuhanku, aku hanya menguasai diriku sendiri dan saudaraku. Sebab itu pisahkanlah antara kami dengan orang-orang yang fasik itu.”"},{"id":"26","ar":"قَالَ فَاِنَّهَا مُحَرَّمَةٌ عَلَيْهِمْ اَرْبَعِيْنَ سَنَةً ۚيَتِيْهُوْنَ فِى الْاَرْضِۗ فَلَا تَأْسَ عَلَى الْقَوْمِ الْفٰسِقِيْنَ","bc":"qāla fa innahā muḥarramatun 'alaihim arba'īna sanah, yatīhụna fil-arḍ, fa lā ta`sa 'alal-qaumil-fāsiqīn","at":"(Allah) berfirman, “(Jika demikian), maka (negeri) itu terlarang buat mereka selama empat puluh tahun, (selama itu) mereka akan mengembara kebingungan di bumi. Maka janganlah eng-kau (Musa) bersedih hati (memikirkan nasib) orang-orang yang fasik itu.”"},{"id":"27","ar":"۞ وَاتْلُ عَلَيْهِمْ نَبَاَ ابْنَيْ اٰدَمَ بِالْحَقِّۘ اِذْ قَرَّبَا قُرْبَانًا فَتُقُبِّلَ مِنْ اَحَدِهِمَا وَلَمْ يُتَقَبَّلْ مِنَ الْاٰخَرِۗ قَالَ لَاَقْتُلَنَّكَ ۗ قَالَ اِنَّمَا يَتَقَبَّلُ اللّٰهُ مِنَ الْمُتَّقِيْنَ","bc":"watlu 'alaihim naba`abnai ādama bil-ḥaqq, iż qarrabā qurbānan fa tuqubbila min aḥadihimā wa lam yutaqabbal minal-ākhar, qāla la`aqtulannak, qāla innamā yataqabbalullāhu minal-muttaqīn","at":"Dan ceritakanlah (Muhammad) yang sebenarnya kepada mereka tentang kisah kedua putra Adam, ketika keduanya mempersembahkan kurban, maka (kurban) salah seorang dari mereka berdua (Habil) diterima dan dari yang lain (Qabil) tidak diterima. Dia (Qabil) berkata, “Sungguh, aku pasti membunuhmu!” Dia (Habil) berkata, “Sesungguhnya Allah hanya menerima (amal) dari orang yang bertakwa.”"},{"id":"28","ar":"لَىِٕنْۢ بَسَطْتَّ اِلَيَّ يَدَكَ لِتَقْتُلَنِيْ مَآ اَنَا۠ بِبَاسِطٍ يَّدِيَ اِلَيْكَ لِاَقْتُلَكَۚ اِنِّيْٓ اَخَافُ اللّٰهَ رَبَّ الْعٰلَمِيْنَ","bc":"la`im basatta ilayya yadaka litaqtulanī mā ana bibāsiṭiy yadiya ilaika li`aqtulak, innī akhāfullāha rabbal-'ālamīn","at":"”Sungguh, jika engkau (Qabil) menggerakkan tanganmu kepadaku untuk membunuhku, aku tidak akan menggerakkan tanganku kepadamu untuk membunuhmu. Aku takut kepada Allah, Tuhan seluruh alam.”"},{"id":"29","ar":"اِنِّيْٓ اُرِيْدُ اَنْ تَبُوْۤاَ بِاِثْمِيْ وَاِثْمِكَ فَتَكُوْنَ مِنْ اَصْحٰبِ النَّارِۚ وَذٰلِكَ جَزَاۤؤُا الظّٰلِمِيْنَۚ ","bc":"innī urīdu an tabū`a bi`iṡmī wa iṡmika fa takụna min aṣ-ḥābin-nār, wa żālika jazā`uẓ-ẓālimīn","at":"”Sesungguhnya aku ingin agar engkau kembali dengan (membawa) dosa (membunuh)ku dan dosamu sendiri, maka engkau akan menjadi penghuni neraka; dan itulah balasan bagi orang yang zalim.”"},{"id":"30","ar":"فَطَوَّعَتْ لَهٗ نَفْسُهٗ قَتْلَ اَخِيْهِ فَقَتَلَهٗ فَاَصْبَحَ مِنَ الْخٰسِرِيْنَ ","bc":"fa ṭawwa'at lahụ nafsuhụ qatla akhīhi fa qatalahụ fa aṣbaḥa minal-khāsirīn","at":"Maka nafsu (Qabil) mendorongnya untuk membunuh saudaranya, kemudian dia pun (benar-benar) membunuhnya, maka jadilah dia termasuk orang yang rugi."},{"id":"31","ar":"فَبَعَثَ اللّٰهُ غُرَابًا يَّبْحَثُ فِى الْاَرْضِ لِيُرِيَهٗ كَيْفَ يُوَارِيْ سَوْءَةَ اَخِيْهِ ۗ قَالَ يٰوَيْلَتٰٓى اَعَجَزْتُ اَنْ اَكُوْنَ مِثْلَ هٰذَا الْغُرَابِ فَاُوَارِيَ سَوْءَةَ اَخِيْۚ فَاَصْبَحَ مِنَ النّٰدِمِيْنَ ۛ","bc":"fa ba'aṡallāhu gurābay yab-ḥaṡu fil-arḍi liyuriyahụ kaifa yuwārī sau`ata akhīh, qāla yā wailatā a 'ajaztu an akụna miṡla hāżal-gurābi fa uwāriya sau`ata akhī, fa aṣbaḥa minan-nādimīn","at":"Kemudian Allah mengutus seekor burung gagak menggali tanah untuk diperlihatkan kepadanya (Qabil). Bagaimana dia seharusnya menguburkan mayat saudaranya. Qabil berkata, “Oh, celaka aku! Mengapa aku tidak mampu berbuat seperti burung gagak ini, sehingga aku dapat menguburkan mayat saudaraku ini?” Maka jadilah dia termasuk orang yang menyesal."},{"id":"32","ar":"مِنْ اَجْلِ ذٰلِكَ ۛ كَتَبْنَا عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَ اَنَّهٗ مَنْ قَتَلَ نَفْسًاۢ بِغَيْرِ نَفْسٍ اَوْ فَسَادٍ فِى الْاَرْضِ فَكَاَنَّمَا قَتَلَ النَّاسَ جَمِيْعًاۗ وَمَنْ اَحْيَاهَا فَكَاَنَّمَآ اَحْيَا النَّاسَ جَمِيْعًا ۗوَلَقَدْ جَاۤءَتْهُمْ رُسُلُنَا بِالْبَيِّنٰتِ ثُمَّ اِنَّ كَثِيْرًا مِّنْهُمْ بَعْدَ ذٰلِكَ فِى الْاَرْضِ لَمُسْرِفُوْنَ","bc":"min ajli żālika katabnā 'alā banī isrā`īla annahụ mang qatala nafsam bigairi nafsin au fasādin fil-arḍi fa ka`annamā qatalan-nāsa jamī'ā, wa man aḥyāhā fa ka`annamā aḥyan-nāsa jamī'ā, wa laqad jā`at-hum rusulunā bil-bayyināti ṡumma inna kaṡīram min-hum ba'da żālika fil-arḍi lamusrifụn","at":"Oleh karena itu Kami tetapkan (suatu hukum) bagi Bani Israil, bahwa barangsiapa membunuh seseorang, bukan karena orang itu membunuh orang lain, atau bukan karena berbuat kerusakan di bumi, maka seakan-akan dia telah membunuh semua manusia. Barangsiapa memelihara kehidupan seorang manusia, maka seakan-akan dia telah memelihara kehidupan semua manusia. Sesungguhnya Rasul Kami telah datang kepada mereka dengan (membawa) keterangan-keterangan yang jelas. Tetapi kemudian banyak di antara mereka setelah itu melampaui batas di bumi."},{"id":"33","ar":"اِنَّمَا جَزٰۤؤُا الَّذِيْنَ يُحَارِبُوْنَ اللّٰهَ وَرَسُوْلَهٗ وَيَسْعَوْنَ فِى الْاَرْضِ فَسَادًا اَنْ يُّقَتَّلُوْٓا اَوْ يُصَلَّبُوْٓا اَوْ تُقَطَّعَ اَيْدِيْهِمْ وَاَرْجُلُهُمْ مِّنْ خِلَافٍ اَوْ يُنْفَوْا مِنَ الْاَرْضِۗ ذٰلِكَ لَهُمْ خِزْيٌ فِى الدُّنْيَا وَلَهُمْ فِى الْاٰخِرَةِ عَذَابٌ عَظِيْمٌ","bc":"innamā jazā`ullażīna yuḥāribụnallāha wa rasụlahụ wa yas'auna fil-arḍi fasādan ay yuqattalū au yuṣallabū au tuqaṭṭa'a aidīhim wa arjuluhum min khilāfin au yunfau minal-arḍ, żālika lahum khizyun fid-dun-yā wa lahum fil-ākhirati 'ażābun 'aẓīm","at":"Hukuman bagi orang-orang yang memerangi Allah dan Rasul-Nya dan membuat kerusakan di bumi hanyalah dibunuh atau disalib, atau dipotong tangan dan kaki mereka secara silang, atau diasingkan dari tempat kediamannya. Yang demikian itu kehinaan bagi mereka di dunia, dan di akhirat mereka mendapat azab yang besar."},{"id":"34","ar":"اِلَّا الَّذِيْنَ تَابُوْا مِنْ قَبْلِ اَنْ تَقْدِرُوْا عَلَيْهِمْۚ فَاعْلَمُوْٓا اَنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"illallażīna tābụ ming qabli an taqdirụ 'alaihim, fa'lamū annallāha gafụrur raḥīm","at":"Kecuali orang-orang yang bertobat sebelum kamu dapat menguasai mereka; maka ketahuilah, bahwa Allah Maha Pengampun, Maha Penyayang."},{"id":"35","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَابْتَغُوْٓا اِلَيْهِ الْوَسِيْلَةَ وَجَاهِدُوْا فِيْ سَبِيْلِهٖ لَعَلَّكُمْ تُفْلِحُوْنَ","bc":"yā ayyuhallażīna āmanuttaqullāha wabtagū ilaihil-wasīlata wa jāhidụ fī sabīlihī la'allakum tufliḥụn","at":"Wahai orang-orang yang beriman! Bertakwalah kepada Allah dan carilah wasilah (jalan) untuk mendekatkan diri kepada-Nya, dan berjihadlah (berjuanglah) di jalan-Nya, agar kamu beruntung."},{"id":"36","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا لَوْ اَنَّ لَهُمْ مَّا فِى الْاَرْضِ جَمِيْعًا وَّمِثْلَهٗ مَعَهٗ لِيَفْتَدُوْا بِهٖ مِنْ عَذَابِ يَوْمِ الْقِيٰمَةِ مَا تُقُبِّلَ مِنْهُمْ ۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ","bc":"innallażīna kafarụ lau anna lahum mā fil-arḍi jamī'aw wa miṡlahụ ma'ahụ liyaftadụ bihī min 'ażābi yaumil-qiyāmati mā tuqubbila min-hum, wa lahum 'ażābun alīm","at":"Sesungguhnya orang-orang yang kafir, seandainya mereka memiliki segala apa yang ada di bumi dan ditambah dengan sebanyak itu (lagi) untuk menebus diri mereka dari azab pada hari Kiamat, niscaya semua (tebusan) itu tidak akan diterima dari mereka. Mereka (tetap) mendapat azab yang pedih."},{"id":"37","ar":"يُرِيْدُوْنَ اَنْ يَّخْرُجُوْا مِنَ النَّارِ وَمَا هُمْ بِخَارِجِيْنَ مِنْهَا ۖوَلَهُمْ عَذَابٌ مُّقِيمٌ","bc":"yurīdụna ay yakhrujụ minan-nāri wa mā hum bikhārijīna min-hā wa lahum 'ażābum muqīm","at":"Mereka ingin keluar dari neraka, tetapi tidak akan dapat keluar dari sana. Dan mereka mendapat azab yang kekal."},{"id":"38","ar":"وَالسَّارِقُ وَالسَّارِقَةُ فَاقْطَعُوْٓا اَيْدِيَهُمَا جَزَاۤءًۢ بِمَا كَسَبَا نَكَالًا مِّنَ اللّٰهِ ۗوَاللّٰهُ عَزِيْزٌ حَكِيْمٌ ","bc":"was-sāriqu was-sāriqatu faqṭa'ū aidiyahumā jazā`am bimā kasabā nakālam minallāh, wallāhu 'azīzun ḥakīm","at":"Adapun orang laki-laki maupun perempuan yang mencuri, potonglah tangan keduanya (sebagai) balasan atas perbuatan yang mereka lakukan dan sebagai siksaan dari Allah. Dan Allah Mahaperkasa, Mahabijaksana."},{"id":"39","ar":"فَمَنْ تَابَ مِنْۢ بَعْدِ ظُلْمِهٖ وَاَصْلَحَ فَاِنَّ اللّٰهَ يَتُوْبُ عَلَيْهِ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"fa man tāba mim ba'di ẓulmihī wa aṣlaḥa fa innallāha yatụbu 'alaīh, innallāha gafụrur raḥīm","at":"Tetapi barangsiapa bertobat setelah melakukan kejahatan itu dan memperbaiki diri, maka sesungguhnya Allah menerima tobatnya. Sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"40","ar":"اَلَمْ تَعْلَمْ اَنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يُعَذِّبُ مَنْ يَّشَاۤءُ وَيَغْفِرُ لِمَنْ يَّشَاۤءُ ۗوَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ","bc":"a lam ta'lam annallāha lahụ mulkus-samāwāti wal-arḍ, yu'ażżibu may yasyā`u wa yagfiru limay yasyā`, wallāhu 'alā kulli syai`ing qadīr","at":"Tidakkah kamu tahu, bahwa Allah memiliki seluruh kerajaan langit dan bumi, Dia menyiksa siapa yang Dia kehendaki dan mengampuni siapa yang Dia kehendaki. Allah Mahakuasa atas segala sesuatu."},{"id":"41","ar":"۞ يٰٓاَيُّهَا الرَّسُوْلُ لَا يَحْزُنْكَ الَّذِيْنَ يُسَارِعُوْنَ فِى الْكُفْرِ مِنَ الَّذِيْنَ قَالُوْٓا اٰمَنَّا بِاَفْوَاهِهِمْ وَلَمْ تُؤْمِنْ قُلُوْبُهُمْ ۛ وَمِنَ الَّذِيْنَ هَادُوْا ۛ سَمّٰعُوْنَ لِلْكَذِبِ سَمّٰعُوْنَ لِقَوْمٍ اٰخَرِيْنَۙ لَمْ يَأْتُوْكَ ۗ يُحَرِّفُوْنَ الْكَلِمَ مِنْۢ بَعْدِ مَوَاضِعِهٖۚ يَقُوْلُوْنَ اِنْ اُوْتِيْتُمْ هٰذَا فَخُذُوْهُ وَاِنْ لَّمْ تُؤْتَوْهُ فَاحْذَرُوْا ۗوَمَنْ يُّرِدِ اللّٰهُ فِتْنَتَهٗ فَلَنْ تَمْلِكَ لَهٗ مِنَ اللّٰهِ شَيْـًٔا ۗ اُولٰۤىِٕكَ الَّذِيْنَ لَمْ يُرِدِ اللّٰهُ اَنْ يُّطَهِّرَ قُلُوْبَهُمْ ۗ لَهُمْ فِى الدُّنْيَا خِزْيٌ ۖوَّلَهُمْ فِى الْاٰخِرَةِ عَذَابٌ عَظِيْمٌ ","bc":"yā ayyuhar-rasụlu lā yaḥzungkallażīna yusāri'ụna fil-kufri minallażīna qālū āmannā bi`afwāhihim wa lam tu`ming qulụbuhum, wa minallażīna hādụ sammā'ụna lil-każibi sammā'ụna liqaumin ākharīna lam ya`tụk, yuḥarrifụnal-kalima mim ba'di mawāḍi'ihī, yaqụlụna in ụtītum hāżā fa khużụhu wa il lam tu`tauhu faḥżarụ, wa may yuridillāhu fitnatahụ fa lan tamlika lahụ minallāhi syai`ā, ulā`ikallażīna lam yuridillāhu ay yuṭahhira qulụbahum, lahum fid-dun-yā khizyuw wa lahum fil-ākhirati 'ażābun 'aẓīm","at":"Wahai Rasul (Muhammad)! Janganlah engkau disedihkan karena mereka berlomba-lomba dalam kekafirannya. Yaitu orang-orang (munafik) yang mengatakan dengan mulut mereka, “Kami telah beriman,” padahal hati mereka belum beriman; dan juga orang-orang Yahudi yang sangat suka mendengar (berita-berita) bohong dan sangat suka mendengar (perkataan-perkataan) orang lain yang belum pernah datang kepadamu. Mereka mengubah kata-kata (Taurat) dari makna yang sebenarnya. Mereka mengatakan, “Jika ini yang diberikan kepadamu (yang sudah diubah) terimalah, dan jika kamu diberi yang bukan ini, maka hati-hatilah.” Barangsiapa dikehendaki Allah untuk dibiarkan sesat, sedikit pun engkau tidak akan mampu menolak sesuatu pun dari Allah (untuk menolongnya). Mereka itu adalah orang-orang yang sudah tidak dikehendaki Allah untuk menyucikan hati mereka. Di dunia mereka mendapat kehinaan dan di akhirat akan mendapat azab yang besar."},{"id":"42","ar":"سَمّٰعُوْنَ لِلْكَذِبِ اَكّٰلُوْنَ لِلسُّحْتِۗ فَاِنْ جَاۤءُوْكَ فَاحْكُمْ بَيْنَهُمْ اَوْ اَعْرِضْ عَنْهُمْ ۚوَاِنْ تُعْرِضْ عَنْهُمْ فَلَنْ يَّضُرُّوْكَ شَيْـًٔا ۗ وَاِنْ حَكَمْتَ فَاحْكُمْ بَيْنَهُمْ بِالْقِسْطِۗ اِنَّ اللّٰهَ يُحِبُّ الْمُقْسِطِيْنَ","bc":"sammā'ụna lil-każibi akkālụna lis-suḥt, fa in jā`ụka faḥkum bainahum au a'riḍ 'an-hum, wa in tu'riḍ 'an-hum fa lay yaḍurrụka syai`ā, wa in ḥakamta faḥkum bainahum bil-qisṭ, innallāha yuḥibbul-muqsiṭīn","at":"Mereka sangat suka mendengar berita bohong, banyak memakan (makanan) yang haram. Jika mereka (orang Yahudi) datang kepadamu (Muhammad untuk meminta putusan), maka berilah putusan di antara mereka atau berpalinglah dari mereka, dan jika engkau berpaling dari mereka maka mereka tidak akan membahayakanmu sedikit pun. Tetapi jika engkau memutuskan (perkara mereka), maka putuskanlah dengan adil. Sesungguhnya Allah menyukai orang-orang yang adil."},{"id":"43","ar":"وَكَيْفَ يُحَكِّمُوْنَكَ وَعِنْدَهُمُ التَّوْرٰىةُ فِيْهَا حُكْمُ اللّٰهِ ثُمَّ يَتَوَلَّوْنَ مِنْۢ بَعْدِ ذٰلِكَ ۗوَمَآ اُولٰۤىِٕكَ بِالْمُؤْمِنِيْنَ ","bc":"wa kaifa yuḥakkimụnaka wa 'indahumut-taurātu fīhā ḥukmullāhi ṡumma yatawallauna mim ba'di żālik, wa mā ulā`ika bil-mu`minīn","at":"Dan bagaimana mereka akan mengangkatmu menjadi hakim mereka, padahal mereka mempunyai Taurat yang di dalamnya (ada) hukum Allah, nanti mereka berpaling (dari putusanmu) setelah itu? Sungguh, mereka bukan orang-orang yang beriman."},{"id":"44","ar":"اِنَّآ اَنْزَلْنَا التَّوْرٰىةَ فِيْهَا هُدًى وَّنُوْرٌۚ يَحْكُمُ بِهَا النَّبِيُّوْنَ الَّذِيْنَ اَسْلَمُوْا لِلَّذِيْنَ هَادُوْا وَالرَّبَّانِيُّوْنَ وَالْاَحْبَارُ بِمَا اسْتُحْفِظُوْا مِنْ كِتٰبِ اللّٰهِ وَكَانُوْا عَلَيْهِ شُهَدَاۤءَۚ فَلَا تَخْشَوُا النَّاسَ وَاخْشَوْنِ وَلَا تَشْتَرُوْا بِاٰيٰتِيْ ثَمَنًا قَلِيْلًا ۗوَمَنْ لَّمْ يَحْكُمْ بِمَآ اَنْزَلَ اللّٰهُ فَاُولٰۤىِٕكَ هُمُ الْكٰفِرُوْنَ","bc":"innā anzalnat-taurāta fīhā hudaw wa nụr, yaḥkumu bihan-nabiyyụnallażīna aslamụ lillażīna hādụ war-rabbāniyyụna wal-aḥbāru bimastuḥfiẓụ ming kitābillāhi wa kānụ 'alaihi syuhadā`, fa lā takhsyawun-nāsa wakhsyauni wa lā tasytarụ bi`āyātī ṡamanang qalīlā, wa mal lam yaḥkum bimā anzalallāhu fa ulā`ika humul-kāfirụn","at":"Sungguh, Kami yang menurunkan Kitab Taurat; di dalamnya (ada) petunjuk dan cahaya. Yang dengan Kitab itu para nabi yang berserah diri kepada Allah memberi putusan atas perkara orang Yahudi, demikian juga para ulama dan pendeta-pendeta mereka, sebab mereka diperintahkan memelihara kitab-kitab Allah dan mereka menjadi saksi terhadapnya. Karena itu janganlah kamu takut kepada manusia, (tetapi) takutlah kepada-Ku. Dan janganlah kamu jual ayat-ayat-Ku dengan harga murah. Barangsiapa tidak memutuskan dengan apa yang diturunkan Allah, maka mereka itulah orang-orang kafir."},{"id":"45","ar":"وَكَتَبْنَا عَلَيْهِمْ فِيْهَآ اَنَّ النَّفْسَ بِالنَّفْسِ وَالْعَيْنَ بِالْعَيْنِ وَالْاَنْفَ بِالْاَنْفِ وَالْاُذُنَ بِالْاُذُنِ وَالسِّنَّ بِالسِّنِّۙ وَالْجُرُوْحَ قِصَاصٌۗ فَمَنْ تَصَدَّقَ بِهٖ فَهُوَ كَفَّارَةٌ لَّهٗ ۗوَمَنْ لَّمْ يَحْكُمْ بِمَآ اَنْزَلَ اللّٰهُ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ","bc":"wa katabnā 'alaihim fīhā annan-nafsa bin-nafsi wal-'aina bil-'aini wal-anfa bil-anfi wal-użuna bil-użuni was-sinna bis-sinni wal-jurụḥa qiṣāṣ, fa man taṣaddaqa bihī fa huwa kaffāratul lah, wa mal lam yaḥkum bimā anzalallāhu fa ulā`ika humuẓ-ẓālimụn","at":"Kami telah menetapkan bagi mereka di dalamnya (Taurat) bahwa nyawa (dibalas) dengan nyawa, mata dengan mata, hidung dengan hidung, telinga dengan telinga, gigi dengan gigi, dan luka-luka (pun) ada qisas-nya (balasan yang sama). Barangsiapa melepaskan (hak qisas)nya, maka itu (menjadi) penebus dosa baginya. Barangsiapa tidak memutuskan perkara menurut apa yang diturunkan Allah, maka mereka itulah orang-orang zalim."},{"id":"46","ar":"وَقَفَّيْنَا عَلٰٓى اٰثَارِهِمْ بِعِيْسَى ابْنِ مَرْيَمَ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰىةِ ۖواٰتَيْنٰهُ الْاِنْجِيْلَ فِيْهِ هُدًى وَّنُوْرٌۙ وَّمُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ التَّوْرٰىةِ وَهُدًى وَّمَوْعِظَةً لِّلْمُتَّقِيْنَۗ ","bc":"wa qaffainā 'alā āṡārihim bi'īsabni maryama muṣaddiqal limā baina yadaihi minat-taurāti wa ātaināhul-injīla fīhi hudaw wa nụruw wa muṣaddiqal limā baina yadaihi minat-taurāti wa hudaw wa mau'iẓatal lil-muttaqīn","at":"Dan Kami teruskan jejak mereka dengan mengutus Isa putra Maryam, membenarkan Kitab yang sebelumnya, yaitu Taurat. Dan Kami menurunkan Injil kepadanya, di dalamnya terdapat petunjuk dan cahaya, dan membenarkan Kitab yang sebelumnya yaitu Taurat, dan sebagai petunjuk serta pengajaran untuk orang-orang yang bertakwa."},{"id":"47","ar":"وَلْيَحْكُمْ اَهْلُ الْاِنْجِيْلِ بِمَآ اَنْزَلَ اللّٰهُ فِيْهِۗ وَمَنْ لَّمْ يَحْكُمْ بِمَآ اَنْزَلَ اللّٰهُ فَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ","bc":"walyaḥkum ahlul-injīli bimā anzalallāhu fīh, wa mal lam yaḥkum bimā anzalallāhu fa ulā`ika humul-fāsiqụn","at":"Dan hendaklah pengikut Injil memutuskan perkara menurut apa yang diturunkan Allah di dalamnya. Barangsiapa tidak memutuskan perkara menurut apa yang diturunkan Allah, maka mereka itulah orang-orang fasik."},{"id":"48","ar":"وَاَنْزَلْنَآ اِلَيْكَ الْكِتٰبَ بِالْحَقِّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ مِنَ الْكِتٰبِ وَمُهَيْمِنًا عَلَيْهِ فَاحْكُمْ بَيْنَهُمْ بِمَآ اَنْزَلَ اللّٰهُ وَلَا تَتَّبِعْ اَهْوَاۤءَهُمْ عَمَّا جَاۤءَكَ مِنَ الْحَقِّۗ لِكُلٍّ جَعَلْنَا مِنْكُمْ شِرْعَةً وَّمِنْهَاجًا ۗوَلَوْ شَاۤءَ اللّٰهُ لَجَعَلَكُمْ اُمَّةً وَّاحِدَةً وَّلٰكِنْ لِّيَبْلُوَكُمْ فِيْ مَآ اٰتٰىكُمْ فَاسْتَبِقُوا الْخَيْرٰتِۗ اِلَى اللّٰهِ مَرْجِعُكُمْ جَمِيْعًا فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَۙ","bc":"wa anzalnā ilaikal-kitāba bil-ḥaqqi muṣaddiqal limā baina yadaihi minal-kitābi wa muhaiminan 'alaihi faḥkum bainahum bimā anzalallāhu wa lā tattabi' ahwā`ahum 'ammā jā`aka minal-ḥaqq, likullin ja'alnā mingkum syir'ataw wa min-hājā, walau syā`allāhu laja'alakum ummataw wāḥidataw wa lākil liyabluwakum fī mā ātākum fastabiqul-khairāt, ilallāhi marji'ukum jamī'an fa yunabbi`ukum bimā kuntum fīhi takhtalifụn","at":"Dan Kami telah menurunkan Kitab (Al-Qur'an) kepadamu (Muhammad) dengan membawa kebenaran, yang membenarkan kitab-kitab yang diturunkan sebelumnya dan menjaganya, maka putuskanlah perkara mereka menurut apa yang diturunkan Allah dan janganlah engkau mengikuti keinginan mereka dengan meninggalkan kebenaran yang telah datang kepadamu. Untuk setiap umat di antara kamu, Kami berikan aturan dan jalan yang terang. Kalau Allah menghendaki, niscaya kamu dijadikan-Nya satu umat (saja), tetapi Allah hendak menguji kamu terhadap karunia yang telah diberikan-Nya kepadamu, maka berlomba-lombalah berbuat kebajikan. Hanya kepada Allah kamu semua kembali, lalu diberitahukan-Nya kepadamu terhadap apa yang dahulu kamu perselisihkan,"},{"id":"49","ar":"وَاَنِ احْكُمْ بَيْنَهُمْ بِمَآ اَنْزَلَ اللّٰهُ وَلَا تَتَّبِعْ اَهْوَاۤءَهُمْ وَاحْذَرْهُمْ اَنْ يَّفْتِنُوْكَ عَنْۢ بَعْضِ مَآ اَنْزَلَ اللّٰهُ اِلَيْكَۗ فَاِنْ تَوَلَّوْا فَاعْلَمْ اَنَّمَا يُرِيْدُ اللّٰهُ اَنْ يُّصِيْبَهُمْ بِبَعْضِ ذُنُوْبِهِمْ ۗوَاِنَّ كَثِيْرًا مِّنَ النَّاسِ لَفٰسِقُوْنَ","bc":"wa aniḥkum bainahum bimā anzalallāhu wa lā tattabi' ahwā`ahum waḥżar-hum ay yaftinụka 'am ba'ḍi mā anzalallāhu ilaīk, fa in tawallau fa'lam annamā yurīdullāhu ay yuṣībahum biba'ḍi żunụbihim, wa inna kaṡīram minan-nāsi lafāsiqụn","at":"dan hendaklah engkau memutuskan perkara di antara mereka menurut apa yang diturunkan Allah, dan janganlah engkau mengikuti keinginan mereka. Dan waspadalah terhadap mereka, jangan sampai mereka memperdayakan engkau terhadap sebagian apa yang telah diturunkan Allah kepadamu. Jika mereka berpaling (dari hukum yang telah diturunkan Allah), maka ketahuilah bahwa sesungguhnya Allah berkehendak menimpakan musibah kepada mereka disebabkan sebagian dosa-dosa mereka. Dan sungguh, kebanyakan manusia adalah orang-orang yang fasik."},{"id":"50","ar":"اَفَحُكْمَ الْجَاهِلِيَّةِ يَبْغُوْنَۗ وَمَنْ اَحْسَنُ مِنَ اللّٰهِ حُكْمًا لِّقَوْمٍ يُّوْقِنُوْنَ ","bc":"a fa ḥukmal-jāhiliyyati yabgụn, wa man aḥsanu minallāhi ḥukmal liqaumiy yụqinụn","at":"Apakah hukum Jahiliah yang mereka kehendaki? (Hukum) siapakah yang lebih baik daripada (hukum) Allah bagi orang-orang yang meyakini (agamanya)?"},{"id":"51","ar":"۞ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الْيَهُوْدَ وَالنَّصٰرٰٓى اَوْلِيَاۤءَ ۘ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۗ وَمَنْ يَّتَوَلَّهُمْ مِّنْكُمْ فَاِنَّهٗ مِنْهُمْ ۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ","bc":"yā ayyuhallażīna āmanụ lā tattakhiżul-yahụda wan-naṣārā auliyā`, ba'ḍuhum auliyā`u ba'ḍ, wa may yatawallahum mingkum fa innahụ min-hum, innallāha lā yahdil-qaumaẓ-ẓālimīn","at":"Wahai orang-orang yang beriman! Janganlah kamu menjadikan orang Yahudi dan Nasrani sebagai teman setia(mu); mereka satu sama lain saling melindungi. Barangsiapa di antara kamu yang menjadikan mereka teman setia, maka sesungguhnya dia termasuk golongan mereka. Sungguh, Allah tidak memberi petunjuk kepada orang-orang yang zalim."},{"id":"52","ar":"فَتَرَى الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ يُّسَارِعُوْنَ فِيْهِمْ يَقُوْلُوْنَ نَخْشٰٓى اَنْ تُصِيْبَنَا دَاۤىِٕرَةٌ ۗفَعَسَى اللّٰهُ اَنْ يَّأْتِيَ بِالْفَتْحِ اَوْ اَمْرٍ مِّنْ عِنْدِهٖ فَيُصْبِحُوْا عَلٰى مَآ اَسَرُّوْا فِيْٓ اَنْفُسِهِمْ نٰدِمِيْنَۗ ","bc":"fa tarallażīna fī qulụbihim maraḍuy yusāri'ụna fīhim yaqụlụna nakhsyā an tuṣībanā dā`irah, fa 'asallāhu ay ya`tiya bil-fat-ḥi au amrim min 'indihī fa yuṣbiḥụ 'alā mā asarrụ fī anfusihim nādimīn","at":"Maka kamu akan melihat orang-orang yang hatinya berpenyakit segera mendekati mereka (Yahudi dan Nasrani), seraya berkata, “Kami takut akan mendapat bencana.” Mudah-mudahan Allah akan mendatangkan kemenangan (kepada Rasul-Nya), atau suatu keputusan dari sisi-Nya, sehingga mereka menjadi menyesal terhadap apa yang mereka rahasiakan dalam diri mereka."},{"id":"53","ar":"وَيَقُوْلُ الَّذِيْنَ اٰمَنُوْٓا اَهٰٓؤُلَاۤءِ الَّذِيْنَ اَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ اِنَّهُمْ لَمَعَكُمْۗ حَبِطَتْ اَعْمَالُهُمْ فَاَصْبَحُوْا خٰسِرِيْنَ","bc":"wa yaqụlullażīna āmanū a hā`ulā`illażīna aqsamụ billāhi jahda aimānihim innahum lama'akum, ḥabiṭat a'māluhum fa aṣbaḥụ khāsirīn","at":"Dan orang-orang yang beriman akan berkata, “Inikah orang yang bersumpah secara sungguh-sungguh dengan (nama) Allah, bahwa mereka benar-benar beserta kamu?” Segala amal mereka menjadi sia-sia, sehingga mereka menjadi orang yang rugi."},{"id":"54","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا مَنْ يَّرْتَدَّ مِنْكُمْ عَنْ دِيْنِهٖ فَسَوْفَ يَأْتِى اللّٰهُ بِقَوْمٍ يُّحِبُّهُمْ وَيُحِبُّوْنَهٗٓ ۙاَذِلَّةٍ عَلَى الْمُؤْمِنِيْنَ اَعِزَّةٍ عَلَى الْكٰفِرِيْنَۖ يُجَاهِدُوْنَ فِيْ سَبِيْلِ اللّٰهِ وَلَا يَخَافُوْنَ لَوْمَةَ لَاۤىِٕمٍ ۗذٰلِكَ فَضْلُ اللّٰهِ يُؤْتِيْهِ مَنْ يَّشَاۤءُۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ","bc":"yā ayyuhallażīna āmanụ may yartadda mingkum 'an dīnihī fa saufa ya`tillāhu biqaumiy yuḥibbuhum wa yuḥibbụnahū ażillatin 'alal-mu`minīna a'izzatin 'alal-kāfirīna yujāhidụna fī sabīlillāhi wa lā yakhāfụna laumata lā`im, żālika faḍlullāhi yu`tīhi may yasyā`, wallāhu wāsi'un 'alīm","at":"Wahai orang-orang yang beriman! Barangsiapa di antara kamu yang murtad (keluar) dari agamanya, maka kelak Allah akan mendatangkan suatu kaum, Dia mencintai mereka dan mereka pun mencintai-Nya, dan bersikap lemah lembut terhadap orang-orang yang beriman, tetapi bersikap keras terhadap orang-orang kafir, yang berjihad di jalan Allah, dan yang tidak takut kepada celaan orang yang suka mencela. Itulah karunia Allah yang diberikan-Nya kepada siapa yang Dia kehendaki. Dan Allah Mahaluas (pemberian-Nya), Maha Mengetahui."},{"id":"55","ar":"اِنَّمَا وَلِيُّكُمُ اللّٰهُ وَرَسُوْلُهٗ وَالَّذِيْنَ اٰمَنُوا الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ رَاكِعُوْنَ","bc":"innamā waliyyukumullāhu wa rasụluhụ wallażīna āmanullażīna yuqīmụnaṣ-ṣalāta wa yu`tụnaz-zakāta wa hum rāki'ụn","at":"Sesungguhnya penolongmu hanyalah Allah, Rasul-Nya, dan orang-orang yang beriman, yang melaksanakan salat dan menunaikan zakat, seraya tunduk (kepada Allah)."},{"id":"56","ar":"وَمَنْ يَّتَوَلَّ اللّٰهَ وَرَسُوْلَهٗ وَالَّذِيْنَ اٰمَنُوْا فَاِنَّ حِزْبَ اللّٰهِ هُمُ الْغٰلِبُوْنَ ","bc":"wa may yatawallallāha wa rasụlahụ wallażīna āmanụ fa inna ḥizballāhi humul-gālibụn","at":"Dan barangsiapa menjadikan Allah, Rasul-Nya dan orang-orang yang beriman sebagai penolongnya, maka sungguh, pengikut (agama) Allah itulah yang menang."},{"id":"57","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الَّذِيْنَ اتَّخَذُوْا دِيْنَكُمْ هُزُوًا وَّلَعِبًا مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَالْكُفَّارَ اَوْلِيَاۤءَۚ وَاتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ","bc":"yā ayyuhallażīna āmanụ lā tattakhiżullażīnattakhażụ dīnakum huzuwaw wa la'ibam minallażīna ụtul-kitāba ming qablikum wal-kuffāra auliyā`, wattaqullāha ing kuntum mu`minīn","at":"Wahai orang-orang yang beriman! Janganlah kamu menjadikan pemimpinmu orang-orang yang membuat agamamu jadi bahan ejekan dan permainan, (yaitu) di antara orang-orang yang telah diberi kitab sebelummu dan orang-orang kafir (orang musyrik). Dan bertakwalah kepada Allah jika kamu orang-orang beriman."},{"id":"58","ar":"وَاِذَا نَادَيْتُمْ اِلَى الصَّلٰوةِ اتَّخَذُوْهَا هُزُوًا وَّلَعِبًا ۗذٰلِكَ بِاَ نَّهُمْ قَوْمٌ لَّا يَعْقِلُوْنَ","bc":"wa iżā nādaitum ilaṣ-ṣalātittakhażụhā huzuwaw wa la'ibā, żālika bi`annahum qaumul lā ya'qilụn","at":"Dan apabila kamu menyeru (mereka) untuk (melaksanakan) salat, mereka menjadikannya bahan ejekan dan permainan. Yang demikian itu adalah karena mereka orang-orang yang tidak mengerti."},{"id":"59","ar":"قُلْ يٰٓاَهْلَ الْكِتٰبِ هَلْ تَنْقِمُوْنَ مِنَّآ اِلَّآ اَنْ اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْنَا وَمَآ اُنْزِلَ مِنْ قَبْلُۙ وَاَنَّ اَكْثَرَكُمْ فٰسِقُوْنَ","bc":"qul yā ahlal-kitābi hal tangqimụna minnā illā an āmannā billāhi wa mā unzila ilainā wa mā unzila ming qablu wa anna akṡarakum fāsiqụn","at":"Katakanlah, “Wahai Ahli Kitab! Apakah kamu memandang kami salah, hanya karena kami beriman kepada Allah, kepada apa yang diturunkan kepada kami dan kepada apa yang diturunkan sebelumnya? Sungguh, kebanyakan dari kamu adalah orang-orang yang fasik.”"},{"id":"60","ar":"قُلْ هَلْ اُنَبِّئُكُمْ بِشَرٍّ مِّنْ ذٰلِكَ مَثُوْبَةً عِنْدَ اللّٰهِ ۗمَنْ لَّعَنَهُ اللّٰهُ وَغَضِبَ عَلَيْهِ وَجَعَلَ مِنْهُمُ الْقِرَدَةَ وَالْخَنَازِيْرَ وَعَبَدَ الطَّاغُوْتَۗ اُولٰۤىِٕكَ شَرٌّ مَّكَانًا وَّاَضَلُّ عَنْ سَوَاۤءِ السَّبِيْلِ","bc":"qul hal unabbi`ukum bisyarrim min żālika maṡụbatan 'indallāh, mal la'anahullāhu wa gaḍiba 'alaihi wa ja'ala min-humul-qiradata wal-khanāzīra wa 'abadaṭ-ṭāgụt, ulā`ika syarrum makānaw wa aḍallu 'an sawā`is-sabīl","at":"Katakanlah (Muhammad), “Apakah akan aku beritakan kepadamu tentang orang yang lebih buruk pembalasannya dari (orang fasik) di sisi Allah? Yaitu, orang yang dilaknat dan dimurkai Allah, di antara mereka (ada) yang dijadikan kera dan babi dan (orang yang) menyembah Thaghut.” Mereka itu lebih buruk tempatnya dan lebih tersesat dari jalan yang lurus."},{"id":"61","ar":"وَاِذَا جَاۤءُوْكُمْ قَالُوْٓا اٰمَنَّا وَقَدْ دَّخَلُوْا بِالْكُفْرِ وَهُمْ قَدْ خَرَجُوْا بِهٖ ۗوَاللّٰهُ اَعْلَمُ بِمَا كَانُوْا يَكْتُمُوْنَ","bc":"wa iżā jā`ụkum qālū āmannā wa qad dakhalụ bil-kufri wa hum qad kharajụ bih, wallāhu a'lamu bimā kānụ yaktumụn","at":"Dan apabila mereka (Yahudi atau munafik) datang kepadamu, mereka mengatakan, “Kami telah beriman,” padahal mereka datang kepadamu dengan kekafiran dan mereka pergi pun demikian; dan Allah lebih mengetahui apa yang mereka sembunyikan."},{"id":"62","ar":"وَتَرٰى كَثِيْرًا مِّنْهُمْ يُسَارِعُوْنَ فِى الْاِثْمِ وَالْعُدْوَانِ وَاَكْلِهِمُ السُّحْتَۗ لَبِئْسَ مَا كَانُوْا يَعْمَلُوْنَ","bc":"wa tarā kaṡīram min-hum yusāri'ụna fil-iṡmi wal-'udwāni wa aklihimus-suḥt, labi`sa mā kānụ ya'malụn","at":"Dan kamu akan melihat banyak di antara mereka (orang Yahudi) berlomba dalam berbuat dosa, permusuhan dan memakan yang haram. Sungguh, sangat buruk apa yang mereka perbuat."},{"id":"63","ar":"لَوْلَا يَنْهٰىهُمُ الرَّبَّانِيُّوْنَ وَالْاَحْبَارُ عَنْ قَوْلِهِمُ الْاِثْمَ وَاَكْلِهِمُ السُّحْتَۗ لَبِئْسَ مَا كَانُوْا يَصْنَعُوْنَ","bc":"lau lā yan-hāhumur-rabbāniyyụna wal-aḥbāru 'ang qaulihimul-iṡma wa aklihimus-suḥt, labi`sa mā kānụ yaṣna'ụn","at":"Mengapa para ulama dan para pendeta mereka tidak melarang mereka mengucapkan perkataan bohong dan memakan yang haram? Sungguh, sangat buruk apa yang mereka perbuat."},{"id":"64","ar":"وَقَالَتِ الْيَهُوْدُ يَدُ اللّٰهِ مَغْلُوْلَةٌ ۗغُلَّتْ اَيْدِيْهِمْ وَلُعِنُوْا بِمَا قَالُوْا ۘ بَلْ يَدٰهُ مَبْسُوْطَتٰنِۙ يُنْفِقُ كَيْفَ يَشَاۤءُۗ وَلَيَزِيْدَنَّ كَثِيْرًا مِّنْهُمْ مَّآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ طُغْيَانًا وَّكُفْرًاۗ وَاَلْقَيْنَا بَيْنَهُمُ الْعَدَاوَةَ وَالْبَغْضَاۤءَ اِلٰى يَوْمِ الْقِيٰمَةِۗ كُلَّمَآ اَوْقَدُوْا نَارًا لِّلْحَرْبِ اَطْفَاَهَا اللّٰهُ ۙوَيَسْعَوْنَ فِى الْاَرْضِ فَسَادًاۗ وَاللّٰهُ لَا يُحِبُّ الْمُفْسِدِيْنَ","bc":"wa qālatil-yahụdu yadullāhi maglụlah, gullat aidīhim wa lu'inụ bimā qālụ, bal yadāhu mabsụṭatāni yunfiqu kaifa yasyā`, wa layazīdanna kaṡīram min-hum mā unzila ilaika mir rabbika ṭugyānaw wa kufrā, wa alqainā bainahumul-'adāwata wal-bagḍā`a ilā yaumil-qiyāmah, kullamā auqadụ nāral lil-ḥarbi aṭfa`ahallāhu wa yas'auna fil-arḍi fasādā, wallāhu lā yuḥibbul-mufsidīn","at":"Dan orang-orang Yahudi berkata, “Tangan Allah terbelenggu.” Sebenarnya tangan merekalah yang dibelenggu dan merekalah yang dilaknat disebabkan apa yang telah mereka katakan itu, padahal kedua tangan Allah terbuka; Dia memberi rezeki sebagaimana Dia kehendaki. Dan (Al-Qur'an) yang diturunkan kepadamu dari Tuhanmu itu pasti akan menambah kedurhakaan dan kekafiran bagi kebanyakan mereka. Dan Kami timbulkan permusuhan dan kebencian di antara mereka sampai hari Kiamat. Setiap mereka menyalakan api peperangan, Allah memadamkannya. Dan mereka berusaha (menimbulkan) kerusakan di bumi. Dan Allah tidak menyukai orang-orang yang berbuat kerusakan."},{"id":"65","ar":"وَلَوْ اَنَّ اَهْلَ الْكِتٰبِ اٰمَنُوْا وَاتَّقَوْا لَكَفَّرْنَا عَنْهُمْ سَيِّاٰتِهِمْ وَلَاَدْخَلْنٰهُمْ جَنّٰتِ النَّعِيْمِ","bc":"walau anna ahlal-kitābi āmanụ wattaqau lakaffarnā 'an-hum sayyi`ātihim wa la`adkhalnāhum jannātin-na'īm","at":"Dan sekiranya Ahli Kitab itu beriman dan bertakwa, niscaya Kami hapus kesalahan-kesalahan mereka, dan mereka tentu Kami masukkan ke dalam surga-surga yang penuh kenikmatan."},{"id":"66","ar":"وَلَوْ اَنَّهُمْ اَقَامُوا التَّوْرٰىةَ وَالْاِنْجِيْلَ وَمَآ اُنْزِلَ اِلَيْهِمْ مِّنْ رَّبِّهِمْ لَاَكَلُوْا مِنْ فَوْقِهِمْ وَمِنْ تَحْتِ اَرْجُلِهِمْۗ مِنْهُمْ اُمَّةٌ مُّقْتَصِدَةٌ ۗ وَكَثِيْرٌ مِّنْهُمْ سَاۤءَ مَا يَعْمَلُوْنَ ","bc":"walau annahum aqāmut-taurāta wal-injīla wa mā unzila ilaihim mir rabbihim la`akalụ min fauqihim wa min taḥti arjulihim, min-hum ummatum muqtaṣidah, wa kaṡīrum min-hum sā`a mā ya'malụn","at":"Dan sekiranya mereka sungguh-sungguh menjalankan (hukum) Taurat, Injil dan (Al-Qur'an) yang diturunkan kepada mereka dari Tuhannya, niscaya mereka akan mendapat makanan dari atas mereka dan dari bawah kaki mereka. Di antara mereka ada sekelompok yang jujur dan taat. Dan banyak di antara mereka sangat buruk apa yang mereka kerjakan."},{"id":"67","ar":"۞ يٰٓاَيُّهَا الرَّسُوْلُ بَلِّغْ مَآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ ۗوَاِنْ لَّمْ تَفْعَلْ فَمَا بَلَّغْتَ رِسٰلَتَهٗ ۗوَاللّٰهُ يَعْصِمُكَ مِنَ النَّاسِۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ","bc":"yā ayyuhar-rasụlu ballig mā unzila ilaika mir rabbik, wa il lam taf'al fa mā ballagta risālatah, wallāhu ya'ṣimuka minan-nās, innallāha lā yahdil-qaumal-kāfirīn","at":"Wahai Rasul! Sampaikanlah apa yang diturunkan Tuhanmu kepadamu. Jika tidak engkau lakukan (apa yang diperintahkan itu) berarti engkau tidak menyampaikan amanat-Nya. Dan Allah memelihara engkau dari (gangguan) manusia. Sungguh, Allah tidak memberi petunjuk kepada orang-orang kafir."},{"id":"68","ar":"قُلْ يٰٓاَهْلَ الْكِتٰبِ لَسْتُمْ عَلٰى شَيْءٍ حَتّٰى تُقِيْمُوا التَّوْرٰىةَ وَالْاِنْجِيْلَ وَمَآ اُنْزِلَ اِلَيْكُمْ مِّنْ رَّبِّكُمْ ۗوَلَيَزِيْدَنَّ كَثِيْرًا مِّنْهُمْ مَّآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ طُغْيَانًا وَّكُفْرًاۚ فَلَا تَأْسَ عَلَى الْقَوْمِ الْكٰفِرِيْنَ","bc":"qul yā ahlal-kitābi lastum 'alā syai`in ḥattā tuqīmut-taurāta wal-injīla wa mā unzila ilaikum mir rabbikum, wa layazīdanna kaṡīram min-hum mā unzila ilaika mir rabbika ṭugyānaw wa kufrā, fa lā ta`sa 'alal-qaumil-kāfirīn","at":"Katakanlah (Muhammad), “Wahai Ahli Kitab! Kamu tidak dipandang beragama sedikit pun hingga kamu menegakkan ajaran-ajaran Taurat, Injil dan (Al-Qur'an) yang diturunkan Tuhanmu kepadamu.” Dan apa yang diturunkan Tuhanmu kepadamu pasti akan membuat banyak di antara mereka lebih durhaka dan lebih ingkar, maka janganlah engkau berputus asa terhadap orang-orang kafir itu."},{"id":"69","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَالَّذِيْنَ هَادُوْا وَالصَّابِـُٔوْنَ وَالنَّصٰرٰى مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَعَمِلَ صَالِحًا فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَ","bc":"innallażīna āmanụ wallażīna hādụ waṣ-ṣābi`ụna wan-naṣārā man āmana billāhi wal-yaumil-ākhiri wa 'amila ṣāliḥan fa lā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Sesungguhnya orang-orang yang beriman, orang-orang Yahudi, shabiin dan orang-orang Nasrani, barangsiapa beriman kepada Allah, kepada hari kemudian, dan berbuat kebajikan, maka tidak ada rasa khawatir padanya dan mereka tidak bersedih hati."},{"id":"70","ar":"لَقَدْ اَخَذْنَا مِيْثَاقَ بَنِيْٓ اِسْرَاۤءِيْلَ وَاَرْسَلْنَآ اِلَيْهِمْ رُسُلًا ۗ كُلَّمَا جَاۤءَهُمْ رَسُوْلٌۢ بِمَا لَا تَهْوٰٓى اَنْفُسُهُمْۙ فَرِيْقًا كَذَّبُوْا وَفَرِيْقًا يَّقْتُلُوْنَ","bc":"laqad akhażnā mīṡāqa banī isrā`īla wa arsalnā ilaihim rusulā, kullamā jā`ahum rasụlum bimā lā tahwā anfusuhum farīqang każżabụ wa farīqay yaqtulụn","at":"Sesungguhnya Kami telah mengambil perjanjian dari Bani Israil, dan telah Kami utus kepada mereka rasul-rasul. Tetapi setiap rasul datang kepada mereka dengan membawa apa yang tidak sesuai dengan keinginan mereka, (maka) sebagian (dari rasul itu) mereka dustakan dan sebagian yang lain mereka bunuh."},{"id":"71","ar":"وَحَسِبُوْٓا اَلَّا تَكُوْنَ فِتْنَةٌ فَعَمُوْا وَصَمُّوْا ثُمَّ تَابَ اللّٰهُ عَلَيْهِمْ ثُمَّ عَمُوْا وَصَمُّوْا كَثِيْرٌ مِّنْهُمْۗ وَاللّٰهُ بَصِيْرٌۢ بِمَا يَعْمَلُوْنَ","bc":"wa ḥasibū allā takụna fitnatun fa 'amụ wa ṣammụ ṡumma tāballāhu 'alaihim ṡumma 'amụ wa ṣammụ kaṡīrum min-hum, wallāhu baṣīrum bimā ya'malụn","at":"Dan mereka mengira bahwa tidak akan terjadi bencana apa pun (terhadap mereka dengan membunuh nabi-nabi itu), karena itu mereka menjadi buta dan tuli, kemudian Allah menerima tobat mereka, lalu banyak di antara mereka buta dan tuli. Dan Allah Maha Melihat apa yang mereka kerjakan."},{"id":"72","ar":"لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ هُوَ الْمَسِيْحُ ابْنُ مَرْيَمَ ۗوَقَالَ الْمَسِيْحُ يٰبَنِيْٓ اِسْرَاۤءِيْلَ اعْبُدُوا اللّٰهَ رَبِّيْ وَرَبَّكُمْ ۗاِنَّهٗ مَنْ يُّشْرِكْ بِاللّٰهِ فَقَدْ حَرَّمَ اللّٰهُ عَلَيْهِ الْجَنَّةَ وَمَأْوٰىهُ النَّارُ ۗوَمَا لِلظّٰلِمِيْنَ مِنْ اَنْصَارٍ ","bc":"laqad kafarallażīna qālū innallāha huwal-masīḥubnu maryam, wa qālal-masīḥu yā banī isrā`īla'budullāha rabbī wa rabbakum, innahụ may yusyrik billāhi fa qad ḥarramallāhu 'alaihil-jannata wa ma`wāhun-nār, wa mā liẓ-ẓālimīna min anṣār","at":"Sungguh, telah kafir orang-orang yang berkata, “Sesungguhnya Allah itu dialah Al-Masih putra Maryam.” Padahal Al-Masih (sendiri) berkata, “Wahai Bani Israil! Sembahlah Allah, Tuhanku dan Tuhanmu.” Sesungguhnya barangsiapa mempersekutukan (sesuatu dengan) Allah, maka sungguh, Allah mengharamkan surga baginya, dan tempatnya ialah neraka. Dan tidak ada seorang penolong pun bagi orang-orang zalim itu."},{"id":"73","ar":"لَقَدْ كَفَرَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ ثَالِثُ ثَلٰثَةٍ ۘ وَمَا مِنْ اِلٰهٍ اِلَّآ اِلٰهٌ وَّاحِدٌ ۗوَاِنْ لَّمْ يَنْتَهُوْا عَمَّا يَقُوْلُوْنَ لَيَمَسَّنَّ الَّذِيْنَ كَفَرُوْا مِنْهُمْ عَذَابٌ اَلِيْمٌ","bc":"laqad kafarallażīna qālū innallāha ṡāliṡu ṡalāṡah, wa mā min ilāhin illā ilāhuw wāḥid, wa il lam yantahụ 'ammā yaqụlụna layamassannallażīna kafarụ min-hum 'ażābun alīm","at":"Sungguh, telah kafir orang-orang yang mengatakan, bahwa Allah adalah salah satu dari yang tiga, padahal tidak ada tuhan (yang berhak disembah) selain Tuhan Yang Esa. Jika mereka tidak berhenti dari apa yang mereka katakan, pasti orang-orang yang kafir di antara mereka akan ditimpa azab yang pedih."},{"id":"74","ar":"اَفَلَا يَتُوْبُوْنَ اِلَى اللّٰهِ وَيَسْتَغْفِرُوْنَهٗۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ","bc":"a fa lā yatụbụna ilallāhi wa yastagfirụnah, wallāhu gafụrur raḥīm","at":"Mengapa mereka tidak bertobat kepada Allah dan memohon ampunan kepada-Nya? Allah Maha Pengampun, Maha Penyayang."},{"id":"75","ar":"مَا الْمَسِيْحُ ابْنُ مَرْيَمَ اِلَّا رَسُوْلٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُۗ وَاُمُّهٗ صِدِّيْقَةٌ ۗ كَانَا يَأْكُلَانِ الطَّعَامَ ۗ اُنْظُرْ كَيْفَ نُبَيِّنُ لَهُمُ الْاٰيٰتِ ثُمَّ انْظُرْ اَنّٰى يُؤْفَكُوْنَ","bc":"mal-masīḥubnu maryama illā rasụl, qad khalat ming qablihir-rusul, wa ummuhụ ṣiddīqah, kānā ya`kulāniṭ-ṭa'ām, unẓur kaifa nubayyinu lahumul-āyāti ṡummanẓur annā yu`fakụn","at":"Al-Masih putra Maryam hanyalah seorang Rasul. Sebelumnya pun sudah berlalu beberapa rasul. Dan ibunya seorang yang berpegang teguh pada kebenaran. Keduanya biasa memakan makanan. Perhatikanlah bagaimana Kami menjelaskan ayat-ayat (tanda-tanda kekuasaan) kepada mereka (Ahli Kitab), kemudian perhatikanlah bagaimana mereka dipalingkan (oleh keinginan mereka)."},{"id":"76","ar":"قُلْ اَتَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَمْلِكُ لَكُمْ ضَرًّا وَّلَا نَفْعًا ۗوَاللّٰهُ هُوَ السَّمِيْعُ الْعَلِيْمُ","bc":"qul a ta'budụna min dụnillāhi mā lā yamliku lakum ḍarraw wa lā naf'ā, wallāhu huwas-samī'ul-'alīm","at":"Katakanlah (Muhammad), “Mengapa kamu menyembah yang selain Allah, sesuatu yang tidak dapat menimbulkan bencana kepadamu dan tidak (pula) memberi manfaat?” Dan Allah Maha Mendengar, Maha Mengetahui."},{"id":"77","ar":"قُلْ يٰٓاَهْلَ الْكِتٰبِ لَا تَغْلُوْا فِيْ دِيْنِكُمْ غَيْرَ الْحَقِّ وَلَا تَتَّبِعُوْٓا اَهْوَاۤءَ قَوْمٍ قَدْ ضَلُّوْا مِنْ قَبْلُ وَاَضَلُّوْا كَثِيْرًا وَّضَلُّوْا عَنْ سَوَاۤءِ السَّبِيْلِ ","bc":"qul yā ahlal-kitābi lā taglụ fī dīnikum gairal-ḥaqqi wa lā tattabi'ū ahwā`a qauming qad ḍallụ ming qablu wa aḍallụ kaṡīraw wa ḍallụ 'an sawā`is-sabīl","at":"Katakanlah (Muhammad), “Wahai Ahli Kitab! Janganlah kamu berlebih-lebihan dengan cara yang tidak benar dalam agamamu. Dan janganlah kamu mengikuti keinginan orang-orang yang telah tersesat dahulu dan (telah) menyesatkan banyak (manusia), dan mereka sendiri tersesat dari jalan yang lurus.”"},{"id":"78","ar":"لُعِنَ الَّذِيْنَ كَفَرُوْا مِنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ عَلٰى لِسَانِ دَاوٗدَ وَعِيْسَى ابْنِ مَرْيَمَ ۗذٰلِكَ بِمَا عَصَوْا وَّكَانُوْا يَعْتَدُوْنَ","bc":"lu'inallażīna kafarụ mim banī isrā`īla 'alā lisāni dāwụda wa 'īsabni maryam, żālika bimā 'aṣaw wa kānụ ya'tadụn","at":"Orang-orang kafir dari Bani Israil telah dilaknat melalui lisan (ucapan) Dawud dan Isa putra Maryam. Yang demikian itu karena mereka durhaka dan selalu melampaui batas."},{"id":"79","ar":"كَانُوْا لَا يَتَنَاهَوْنَ عَنْ مُّنْكَرٍ فَعَلُوْهُۗ لَبِئْسَ مَا كَانُوْا يَفْعَلُوْنَ","bc":"kānụ lā yatanāhauna 'am mungkarin fa'alụh, labi`sa mā kānụ yaf'alụn","at":"Mereka tidak saling mencegah perbuatan mungkar yang selalu mereka perbuat. Sungguh, sangat buruk apa yang mereka perbuat."},{"id":"80","ar":"تَرٰى كَثِيْرًا مِّنْهُمْ يَتَوَلَّوْنَ الَّذِيْنَ كَفَرُوْا ۗ لَبِئْسَ مَا قَدَّمَتْ لَهُمْ اَنْفُسُهُمْ اَنْ سَخِطَ اللّٰهُ عَلَيْهِمْ وَفِى الْعَذَابِ هُمْ خٰلِدُوْنَ","bc":"tarā kaṡīram min-hum yatawallaunallażīna kafarụ, labi`sa mā qaddamat lahum anfusuhum an sakhiṭallāhu 'alaihim wa fil-'ażābi hum khālidụn","at":"Kamu melihat banyak di antara mereka tolong-menolong dengan orang-orang kafir (musyrik). Sungguh, sangat buruk apa yang mereka lakukan untuk diri mereka sendiri, yaitu kemurkaan Allah, dan mereka akan kekal dalam azab."},{"id":"81","ar":"وَلَوْ كَانُوْا يُؤْمِنُوْنَ بِاللّٰهِ وَالنَّبِيِّ وَمَآ اُنْزِلَ اِلَيْهِ مَا اتَّخَذُوْهُمْ اَوْلِيَاۤءَ وَلٰكِنَّ كَثِيْرًا مِّنْهُمْ فٰسِقُوْنَ","bc":"walau kānụ yu`minụna billāhi wan-nabiyyi wa mā unzila ilaihi mattakhażụhum auliyā`a wa lākinna kaṡīram min-hum fāsiqụn","at":"Dan sekiranya mereka beriman kepada Allah, kepada Nabi (Muhammad) dan kepada apa yang diturunkan kepadanya, niscaya mereka tidak akan menjadikan orang musyrik itu sebagai teman setia. Tetapi banyak di antara mereka orang-orang yang fasik."},{"id":"82","ar":"۞ لَتَجِدَنَّ اَشَدَّ النَّاسِ عَدَاوَةً لِّلَّذِيْنَ اٰمَنُوا الْيَهُوْدَ وَالَّذِيْنَ اَشْرَكُوْاۚ وَلَتَجِدَنَّ اَقْرَبَهُمْ مَّوَدَّةً لِّلَّذِيْنَ اٰمَنُوا الَّذِيْنَ قَالُوْٓا اِنَّا نَصٰرٰىۗ ذٰلِكَ بِاَنَّ مِنْهُمْ قِسِّيْسِيْنَ وَرُهْبَانًا وَّاَنَّهُمْ لَا يَسْتَكْبِرُوْنَ ۔","bc":"latajidanna asyaddan-nāsi 'adāwatal lillażīna āmanul-yahụda wallażīna asyrakụ, wa latajidanna aqrabahum mawaddatal lillażīna āmanullażīna qālū innā naṣārā, żālika bi`anna min-hum qissīsīna wa ruhbānaw wa annahum lā yastakbirụn","at":"Pasti akan kamu dapati orang yang paling keras permusuhannya terhadap orang-orang yang beriman, yaitu orang-orang Yahudi dan orang-orang musyrik. Dan pasti akan kamu dapati orang yang paling dekat persahabatannya dengan orang-orang yang beriman ialah orang-orang yang berkata, “Sesungguhnya kami adalah orang Nasrani.” Yang demikian itu karena di antara mereka terdapat para pendeta dan para rahib, (juga) karena mereka tidak menyombongkan diri."},{"id":"83","ar":"وَاِذَا سَمِعُوْا مَآ اُنْزِلَ اِلَى الرَّسُوْلِ تَرٰٓى اَعْيُنَهُمْ تَفِيْضُ مِنَ الدَّمْعِ مِمَّا عَرَفُوْا مِنَ الْحَقِّۚ يَقُوْلُوْنَ رَبَّنَآ اٰمَنَّا فَاكْتُبْنَا مَعَ الشّٰهِدِيْنَ ","bc":"wa iżā sami'ụ mā unzila ilar-rasụli tarā a'yunahum tafīḍu minad-dam'i mimmā 'arafụ minal-ḥaqq, yaqụlụna rabbanā āmannā faktubnā ma'asy-syāhidīn","at":"Dan apabila mereka mendengarkan apa (Al-Qur'an) yang diturunkan kepada Rasul (Muhammad), kamu lihat mata mereka mencucurkan air mata disebabkan kebenaran yang telah mereka ketahui (dari kitab-kitab mereka sendiri), seraya berkata, “Ya Tuhan, kami telah beriman, maka catatlah kami bersama orang-orang yang menjadi saksi (atas kebenaran Al-Qur'an dan kenabian Muhammad)."},{"id":"84","ar":"وَمَا لَنَا لَا نُؤْمِنُ بِاللّٰهِ وَمَا جَاۤءَنَا مِنَ الْحَقِّۙ وَنَطْمَعُ اَنْ يُّدْخِلَنَا رَبُّنَا مَعَ الْقَوْمِ الصّٰلِحِيْنَ","bc":"wa mā lanā lā nu`minu billāhi wa mā jā`anā minal-ḥaqqi wa naṭma'u ay yudkhilanā rabbunā ma'al-qaumiṣ-ṣāliḥīn","at":"Dan mengapa kami tidak akan beriman kepada Allah dan kepada kebenaran yang datang kepada kami, padahal kami sangat ingin agar Tuhan kami memasukkan kami ke dalam golongan orang-orang saleh?”"},{"id":"85","ar":"فَاَثَابَهُمُ اللّٰهُ بِمَا قَالُوْا جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ وَذٰلِكَ جَزَاۤءُ الْمُحْسِنِيْنَ ","bc":"fa aṡābahumullāhu bimā qālụ jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā, wa żālika jazā`ul-muḥsinīn","at":"Maka Allah memberi pahala kepada mereka atas perkataan yang telah mereka ucapkan, (yaitu) surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Dan itulah balasan (bagi) orang-orang yang berbuat kebaikan."},{"id":"86","ar":"وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ الْجَحِيْمِ","bc":"wallażīna kafarụ wa każżabụ bi`āyātinā ulā`ika aṣ-ḥābul-jaḥīm","at":"Dan orang-orang yang kafir serta mendustakan ayat-ayat Kami, mereka itulah penghuni neraka."},{"id":"87","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُحَرِّمُوْا طَيِّبٰتِ مَآ اَحَلَّ اللّٰهُ لَكُمْ وَلَا تَعْتَدُوْا ۗاِنَّ اللّٰهَ لَا يُحِبُّ الْمُعْتَدِيْنَ ","bc":"yā ayyuhallażīna āmanụ lā tuḥarrimụ ṭayyibāti mā aḥallallāhu lakum wa lā ta'tadụ, innallāha lā yuḥibbul-mu'tadīn","at":"Wahai orang-orang yang beriman! Janganlah kamu mengharamkan apa yang baik yang telah dihalalkan Allah kepadamu, dan janganlah kamu melampaui batas. Sesungguhnya Allah tidak menyukai orang-orang yang melampaui batas."},{"id":"88","ar":"وَكُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ حَلٰلًا طَيِّبًا ۖوَّاتَّقُوا اللّٰهَ الَّذِيْٓ اَنْتُمْ بِهٖ مُؤْمِنُوْنَ ","bc":"wa kulụ mimmā razaqakumullāhu ḥalālan ṭayyibaw wattaqullāhallażī antum bihī mu`minụn","at":"Dan makanlah dari apa yang telah diberikan Allah kepadamu sebagai rezeki yang halal dan baik, dan bertakwalah kepada Allah yang kamu beriman kepada-Nya."},{"id":"89","ar":"لَا يُؤَاخِذُكُمُ اللّٰهُ بِاللَّغْوِ فِيْٓ اَيْمَانِكُمْ وَلٰكِنْ يُّؤَاخِذُكُمْ بِمَا عَقَّدْتُّمُ الْاَيْمَانَۚ فَكَفَّارَتُهٗٓ اِطْعَامُ عَشَرَةِ مَسٰكِيْنَ مِنْ اَوْسَطِ مَا تُطْعِمُوْنَ اَهْلِيْكُمْ اَوْ كِسْوَتُهُمْ اَوْ تَحْرِيْرُ رَقَبَةٍ ۗفَمَنْ لَّمْ يَجِدْ فَصِيَامُ ثَلٰثَةِ اَيَّامٍ ۗذٰلِكَ كَفَّارَةُ اَيْمَانِكُمْ اِذَا حَلَفْتُمْ ۗوَاحْفَظُوْٓا اَيْمَانَكُمْ ۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَشْكُرُوْنَ ","bc":"lā yu`ākhiżukumullāhu bil-lagwi fī aimānikum wa lākiy yu`ākhiżukum bimā 'aqqattumul-aimān, fa kaffāratuhū iṭ'āmu 'asyarati masākīna min ausaṭi mā tuṭ'imụna ahlīkum au kiswatuhum au taḥrīru raqabah, fa mal lam yajid fa ṣiyāmu ṡalāṡati ayyām, żālika kaffāratu aimānikum iżā ḥalaftum, waḥfaẓū aimānakum, każālika yubayyinullāhu lakum āyātihī la'allakum tasykurụn","at":"Allah tidak menghukum kamu disebabkan sumpah-sumpahmu yang tidak disengaja (untuk bersumpah), tetapi Dia menghukum kamu disebabkan sumpah-sumpah yang kamu sengaja, maka kafaratnya (denda pelanggaran sumpah) ialah memberi makan sepuluh orang miskin, yaitu dari makanan yang biasa kamu berikan kepada keluargamu, atau memberi mereka pakaian atau memerdekakan seorang hamba sahaya. Barangsiapa tidak mampu melakukannya, maka (kafaratnya) berpuasalah tiga hari. Itulah kafarat sumpah-sumpahmu apabila kamu bersumpah. Dan jagalah sumpahmu. Demikianlah Allah menerangkan hukum-hukum-Nya kepadamu agar kamu bersyukur (kepada-Nya)."},{"id":"90","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنَّمَا الْخَمْرُ وَالْمَيْسِرُ وَالْاَنْصَابُ وَالْاَزْلَامُ رِجْسٌ مِّنْ عَمَلِ الشَّيْطٰنِ فَاجْتَنِبُوْهُ لَعَلَّكُمْ تُفْلِحُوْنَ ","bc":"yā ayyuhallażīna āmanū innamal-khamru wal-maisiru wal-anṣābu wal-azlāmu rijsum min 'amalisy-syaiṭāni fajtanibụhu la'allakum tufliḥụn","at":"Wahai orang-orang yang beriman! Sesungguhnya minuman keras, berjudi, (berkurban untuk) berhala, dan mengundi nasib dengan anak panah, adalah perbuatan keji dan termasuk perbuatan setan. Maka jauhilah (perbuatan-perbuatan) itu agar kamu beruntung."},{"id":"91","ar":"اِنَّمَا يُرِيْدُ الشَّيْطٰنُ اَنْ يُّوْقِعَ بَيْنَكُمُ الْعَدَاوَةَ وَالْبَغْضَاۤءَ فِى الْخَمْرِ وَالْمَيْسِرِ وَيَصُدَّكُمْ عَنْ ذِكْرِ اللّٰهِ وَعَنِ الصَّلٰوةِ فَهَلْ اَنْتُمْ مُّنْتَهُوْنَ ","bc":"innamā yurīdusy-syaiṭānu ay yụqi'a bainakumul-'adāwata wal-bagḍā`a fil-khamri wal-maisiri wa yaṣuddakum 'an żikrillāhi wa 'aniṣ-ṣalāti fa hal antum muntahụn","at":"Dengan minuman keras dan judi itu, setan hanyalah bermaksud menimbulkan permusuhan dan kebencian di antara kamu, dan menghalang-halangi kamu dari mengingat Allah dan melaksanakan salat, maka tidakkah kamu mau berhenti?"},{"id":"92","ar":"وَاَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَ وَاحْذَرُوْا ۚفَاِنْ تَوَلَّيْتُمْ فَاعْلَمُوْٓا اَنَّمَا عَلٰى رَسُوْلِنَا الْبَلٰغُ الْمُبِيْنُ ","bc":"wa aṭī'ullāha wa aṭī'ur-rasụla waḥżarụ, fa in tawallaitum fa'lamū annamā 'alā rasụlinal-balāgul-mubīn","at":"Dan taatlah kamu kepada Allah dan taatlah kamu kepada Rasul serta berhati-hatilah. Jika kamu berpaling, maka ketahuilah bahwa kewajiban Rasul Kami hanyalah menyampaikan (amanat) dengan jelas."},{"id":"93","ar":"لَيْسَ عَلَى الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جُنَاحٌ فِيْمَا طَعِمُوْٓا اِذَا مَا اتَّقَوْا وَّاٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ ثُمَّ اتَّقَوْا وَّاٰمَنُوْا ثُمَّ اتَّقَوْا وَّاَحْسَنُوْا ۗوَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَ ","bc":"laisa 'alallażīna āmanụ wa 'amiluṣ-ṣāliḥāti junāḥun fīmā ṭa'imū iżā mattaqaw wa āmanụ wa 'amiluṣ-ṣāliḥāti ṡummattaqaw wa āmanụ ṡummattaqaw wa aḥsanụ, wallāhu yuḥibbul-muḥsinīn","at":"Tidak berdosa bagi orang-orang yang beriman dan mengerjakan kebajikan tentang apa yang mereka makan (dahulu), apabila mereka bertakwa dan beriman, serta mengerjakan kebajikan, kemudian mereka tetap bertakwa dan beriman, selanjutnya mereka (tetap juga) bertakwa dan berbuat kebajikan. Dan Allah menyukai orang-orang yang berbuat kebajikan."},{"id":"94","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَيَبْلُوَنَّكُمُ اللّٰهُ بِشَيْءٍ مِّنَ الصَّيْدِ تَنَالُهٗٓ اَيْدِيْكُمْ وَرِمَاحُكُمْ لِيَعْلَمَ اللّٰهُ مَنْ يَّخَافُهٗ بِالْغَيْبِۚ فَمَنِ اعْتَدٰى بَعْدَ ذٰلِكَ فَلَهٗ عَذَابٌ اَلِيْمٌ ","bc":"yā ayyuhallażīna āmanụ layabluwannakumullāhu bisyai`im minaṣ-ṣaidi tanāluhū aidīkum wa rimāḥukum liya'lamallāhu may yakhāfuhụ bil-gaīb, fa mani'tadā ba'da żālika fa lahụ 'ażābun alīm","at":"Wahai orang-orang yang beriman! Allah pasti akan menguji kamu dengan hewan buruan yang dengan mudah kamu peroleh dengan tangan dan tombakmu agar Allah mengetahui siapa yang takut kepada-Nya, meskipun dia tidak melihat-Nya. Barangsiapa melampaui batas setelah itu, maka dia akan mendapat azab yang pedih."},{"id":"95","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقْتُلُوا الصَّيْدَ وَاَنْتُمْ حُرُمٌ ۗوَمَنْ قَتَلَهٗ مِنْكُمْ مُّتَعَمِّدًا فَجَزَۤاءٌ مِّثْلُ مَا قَتَلَ مِنَ النَّعَمِ يَحْكُمُ بِهٖ ذَوَا عَدْلٍ مِّنْكُمْ هَدْيًاۢ بٰلِغَ الْكَعْبَةِ اَوْ كَفَّارَةٌ طَعَامُ مَسٰكِيْنَ اَوْ عَدْلُ ذٰلِكَ صِيَامًا لِّيَذُوْقَ وَبَالَ اَمْرِهٖ ۗعَفَا اللّٰهُ عَمَّا سَلَفَ ۗوَمَنْ عَادَ فَيَنْتَقِمُ اللّٰهُ مِنْهُ ۗوَاللّٰهُ عَزِيْزٌ ذُو انْتِقَامٍ ","bc":"yā ayyuhallażīna āmanụ lā taqtuluṣ-ṣaida wa antum ḥurum, wa mang qatalahụ mingkum muta'ammidan fa jazā`um miṡlu mā qatala minan-na'ami yaḥkumu bihī żawā 'adlim mingkum hadyam bāligal-ka'bati au kaffāratun ṭa'āmu masākīna au 'adlu żālika ṣiyāmal liyażụqa wa bāla amrih, 'afallāhu 'ammā salaf, wa man 'āda fa yantaqimullāhu min-h, wallāhu 'azīzun żuntiqām","at":"Wahai orang-orang yang beriman! Janganlah kamu membunuh hewan buruan, ketika kamu sedang ihram (haji atau umrah). Barangsiapa di antara kamu membunuhnya dengan sengaja, maka dendanya ialah mengganti dengan hewan ternak yang sepadan dengan buruan yang dibunuhnya, menurut putusan dua orang yang adil di antara kamu sebagai hadyu yang dibawa ke Ka‘bah, atau kafarat (membayar tebusan dengan) memberi makan kepada orang-orang miskin, atau berpuasa, seimbang dengan makanan yang dikeluarkan itu, agar dia merasakan akibat buruk dari perbuatannya. Allah telah memaafkan apa yang telah lalu. Dan barangsiapa kembali mengerjakannya, niscaya Allah akan menyiksanya. Dan Allah Mahaperkasa, memiliki (kekuasaan untuk) menyiksa."},{"id":"96","ar":"اُحِلَّ لَكُمْ صَيْدُ الْبَحْرِ وَطَعَامُهٗ مَتَاعًا لَّكُمْ وَلِلسَّيَّارَةِ ۚوَحُرِّمَ عَلَيْكُمْ صَيْدُ الْبَرِّ مَا دُمْتُمْ حُرُمًا ۗوَاتَّقُوا اللّٰهَ الَّذِيْٓ اِلَيْهِ تُحْشَرُوْنَ ","bc":"uḥilla lakum ṣaidul-baḥri wa ṭa'āmuhụ matā'al lakum wa lis-sayyārah, wa ḥurrima 'alaikum ṣaidul-barri mā dumtum ḥurumā, wattaqullāhallażī ilaihi tuḥsyarụn","at":"Dihalalkan bagimu hewan buruan laut dan makanan (yang berasal) dari laut sebagai makanan yang lezat bagimu, dan bagi orang-orang yang dalam perjalanan; dan diharamkan atasmu (menangkap) hewan darat, selama kamu sedang ihram. Dan bertakwalah kepada Allah yang kepada-Nya kamu akan dikumpulkan (kembali)."},{"id":"97","ar":"۞ جَعَلَ اللّٰهُ الْكَعْبَةَ الْبَيْتَ الْحَرَامَ قِيٰمًا لِّلنَّاسِ وَالشَّهْرَ الْحَرَامَ وَالْهَدْيَ وَالْقَلَاۤىِٕدَ ۗذٰلِكَ لِتَعْلَمُوْٓا اَنَّ اللّٰهَ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۙ وَاَنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ ","bc":"ja'alallāhul-ka'batal-baital-ḥarāma qiyāmal lin-nāsi wasy-syahral-ḥarāma wal-hadya wal-qalā`id, żālika lita'lamū annallāha ya'lamu mā fis-samāwāti wa mā fil-arḍi wa annallāha bikulli syai`in 'alīm","at":"Allah telah menjadikan Ka‘bah rumah suci tempat manusia berkumpul. Demikian pula bulan haram, hadyu dan qala'id. Yang demikian itu agar kamu mengetahui, bahwa Allah mengetahui apa yang ada di langit dan apa yang ada di bumi, dan bahwa Allah Maha Mengetahui segala sesuatu."},{"id":"98","ar":"اِعْلَمُوْٓا اَنَّ اللّٰهَ شَدِيْدُ الْعِقَابِۙ وَاَنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌۗ ","bc":"i'lamū annallāha syadīdul-'iqābi wa annallāha gafụrur raḥīm","at":"Ketahuilah, bahwa Allah sangat keras siksaan-Nya dan bahwa Allah Maha Pengampun, Maha Penyayang."},{"id":"99","ar":"مَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ ۗوَاللّٰهُ يَعْلَمُ مَا تُبْدُوْنَ وَمَا تَكْتُمُوْنَ ","bc":"mā 'alar-rasụli illal-balāg, wallāhu ya'lamu mā tubdụna wa mā taktumụn","at":"Kewajiban Rasul tidak lain hanyalah menyampaikan (amanat Allah), dan Allah mengetahui apa yang kamu tampakkan dan apa yang kamu sembunyikan."},{"id":"100","ar":"قُلْ لَّا يَسْتَوِى الْخَبِيْثُ وَالطَّيِّبُ وَلَوْ اَعْجَبَكَ كَثْرَةُ الْخَبِيْثِۚ فَاتَّقُوا اللّٰهَ يٰٓاُولِى الْاَلْبَابِ لَعَلَّكُمْ تُفْلِحُوْنَ","bc":"qul lā yastawil-khabīṡu waṭ-ṭayyibu walau a'jabaka kaṡratul khabīṡ, fattaqullāha yā ulil-albābi la'allakum tufliḥụn","at":"Katakanlah (Muhammad), “Tidaklah sama yang buruk dengan yang baik, meskipun banyaknya keburukan itu menarik hatimu, maka bertakwalah kepada Allah wahai orang-orang yang mempunyai akal sehat, agar kamu beruntung.”"},{"id":"101","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَسْـَٔلُوْا عَنْ اَشْيَاۤءَ اِنْ تُبْدَ لَكُمْ تَسُؤْكُمْ ۚوَاِنْ تَسْـَٔلُوْا عَنْهَا حِيْنَ يُنَزَّلُ الْقُرْاٰنُ تُبْدَ لَكُمْ ۗعَفَا اللّٰهُ عَنْهَا ۗوَاللّٰهُ غَفُوْرٌ حَلِيْمٌ ","bc":"yā ayyuhallażīna āmanụ lā tas`alụ 'an asy-yā`a in tubda lakum tasu`kum, wa in tas`alụ 'an-hā ḥīna yunazzalul-qur`ānu tubda lakum, 'afallāhu 'an-hā, wallāhu gafụrun ḥalīm","at":"Wahai orang-orang yang beriman! Janganlah kamu menanyakan (kepada Nabimu) hal-hal yang jika diterangkan kepadamu (justru) menyusahkan kamu. Jika kamu menanyakannya ketika Al-Qur'an sedang diturunkan, (niscaya) akan diterangkan kepadamu. Allah telah memaafkan (kamu) tentang hal itu. Dan Allah Maha Pengampun, Maha Penyantun."},{"id":"102","ar":"قَدْ سَاَلَهَا قَوْمٌ مِّنْ قَبْلِكُمْ ثُمَّ اَصْبَحُوْا بِهَا كٰفِرِيْنَ ","bc":"qad sa`alahā qaumum ming qablikum ṡumma aṣbaḥụ bihā kāfirīn","at":"Sesungguhnya sebelum kamu telah ada segolongan manusia yang menanyakan hal-hal serupa itu (kepada nabi mereka), kemudian mereka menjadi kafir."},{"id":"103","ar":"مَا جَعَلَ اللّٰهُ مِنْۢ بَحِيْرَةٍ وَّلَا سَاۤىِٕبَةٍ وَّلَا وَصِيْلَةٍ وَّلَا حَامٍ ۙوَّلٰكِنَّ الَّذِيْنَ كَفَرُوْا يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَۗ وَاَكْثَرُهُمْ لَا يَعْقِلُوْنَ ","bc":"mā ja'alallāhu mim baḥīratiw wa lā sā`ibatiw wa lā waṣīlatiw wa lā hāmiw wa lākinnallażīna kafarụ yaftarụna 'alallāhil-każib, wa akṡaruhum lā ya'qilụn","at":"Allah tidak pernah mensyariatkan adanya Bahirah, Sa'ibah, Wasilah dan haam. Tetapi orang-orang kafir membuat-buat kedustaan terhadap Allah, dan kebanyakan mereka tidak mengerti."},{"id":"104","ar":"وَاِذَا قِيْلَ لَهُمْ تَعَالَوْا اِلٰى مَآ اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُوْلِ قَالُوْا حَسْبُنَا مَا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَا ۗ اَوَلَوْ كَانَ اٰبَاۤؤُهُمْ لَا يَعْلَمُوْنَ شَيْـًٔا وَّلَا يَهْتَدُوْنَ ","bc":"wa iżā qīla lahum ta'ālau ilā mā anzalallāhu wa ilar-rasụli qālụ ḥasbunā mā wajadnā 'alaihi ābā`anā, a walau kāna ābā`uhum lā ya'lamụna syai`aw wa lā yahtadụn","at":"Dan apabila dikatakan kepada mereka, “Marilah (mengikuti) apa yang diturunkan Allah dan (mengikuti) Rasul.” Mereka menjawab, “Cukuplah bagi kami apa yang kami dapati nenek moyang kami (mengerjakannya).” Apakah (mereka akan mengikuti) juga nenek moyang mereka walaupun nenek moyang mereka itu tidak mengetahui apa-apa dan tidak (pula) mendapat petunjuk?"},{"id":"105","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا عَلَيْكُمْ اَنْفُسَكُمْ ۚ لَا يَضُرُّكُمْ مَّنْ ضَلَّ اِذَا اهْتَدَيْتُمْ ۗ اِلَى اللّٰهِ مَرْجِعُكُمْ جَمِيْعًا فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ","bc":"yā ayyuhallażīna āmanụ 'alaikum anfusakum, lā yaḍurrukum man ḍalla iżahtadaitum, ilallāhi marji'ukum jamī'an fa yunabbi`ukum bimā kuntum ta'malụn","at":"Wahai orang-orang yang beriman! Jagalah dirimu; (karena) orang yang sesat itu tidak akan membahayakanmu apabila kamu telah mendapat petunjuk. Hanya kepada Allah kamu semua akan kembali, kemudian Dia akan menerangkan kepadamu apa yang telah kamu kerjakan."},{"id":"106","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا شَهَادَةُ بَيْنِكُمْ اِذَا حَضَرَ اَحَدَكُمُ الْمَوْتُ حِيْنَ الْوَصِيَّةِ اثْنٰنِ ذَوَا عَدْلٍ مِّنْكُمْ اَوْ اٰخَرٰنِ مِنْ غَيْرِكُمْ اِنْ اَنْتُمْ ضَرَبْتُمْ فِى الْاَرْضِ فَاَصَابَتْكُمْ مُّصِيْبَةُ الْمَوْتِۗ تَحْبِسُوْنَهُمَا مِنْۢ بَعْدِ الصَّلٰوةِ فَيُقْسِمٰنِ بِاللّٰهِ اِنِ ارْتَبْتُمْ لَا نَشْتَرِيْ بِهٖ ثَمَنًا وَّلَوْ كَانَ ذَا قُرْبٰىۙ وَلَا نَكْتُمُ شَهَادَةَ اللّٰهِ اِنَّآ اِذًا لَّمِنَ الْاٰثِمِيْنَ ","bc":"yā ayyuhallażīna āmanụ syahādatu bainikum iżā ḥaḍara aḥadakumul-mautu ḥīnal-waṣiyyatiṡnāni żawā 'adlim mingkum au ākharāni min gairikum in antum ḍarabtum fil-arḍi fa aṣābatkum muṣībatul-maụt, taḥbisụnahumā mim ba'diṣ-ṣalāti fa yuqsimāni billāhi inirtabtum lā nasytarī bihī ṡamanaw walau kāna żā qurbā wa lā naktumu syahādatallāhi innā iżal laminal-āṡimīn","at":"Wahai orang-orang yang beriman! Apabila salah seorang (di antara) kamu menghadapi kematian, sedang dia akan berwasiat, maka hendaklah (wasiat itu) disaksikan oleh dua orang yang adil di antara kamu, atau dua orang yang berlainan (agama) dengan kamu. Jika kamu dalam perjalanan di bumi lalu kamu ditimpa bahaya kematian, hendaklah kamu tahan kedua saksi itu setelah salat, agar keduanya bersumpah dengan nama Allah jika kamu ragu-ragu, “Demi Allah kami tidak akan mengambil keuntungan dengan sumpah ini, walaupun dia karib kerabat, dan kami tidak menyembunyikan kesaksian Allah; sesungguhnya jika demikian tentu kami termasuk orang-orang yang berdosa.”"},{"id":"107","ar":"فَاِنْ عُثِرَ عَلٰٓى اَنَّهُمَا اسْتَحَقَّآ اِثْمًا فَاٰخَرٰنِ يَقُوْمٰنِ مَقَامَهُمَا مِنَ الَّذِيْنَ اسْتَحَقَّ عَلَيْهِمُ الْاَوْلَيٰنِ فَيُقْسِمٰنِ بِاللّٰهِ لَشَهَادَتُنَآ اَحَقُّ مِنْ شَهَادَتِهِمَا وَمَا اعْتَدَيْنَآ ۖاِنَّآ اِذًا لَّمِنَ الظّٰلِمِيْنَ ","bc":"fa in 'uṡira 'alā annahumastaḥaqqā iṡman fa ākharāni yaqụmāni maqāmahumā minallażīnastaḥaqqa 'alaihimul-aulayāni fa yuqsimāni billāhi lasyahādatunā aḥaqqu min syahādatihimā wa ma'tadainā innā iżal laminaẓ-ẓālimīn","at":"Jika terbukti kedua saksi itu berbuat dosa, maka dua orang yang lain menggantikan kedudukannya, yaitu di antara ahli waris yang berhak dan lebih dekat kepada orang yang mati, lalu keduanya bersumpah dengan nama Allah, “Sungguh, kesaksian kami lebih layak diterima daripada kesaksian kedua saksi itu, dan kami tidak melanggar batas. Sesungguhnya jika kami berbuat demikian tentu kami termasuk orang-orang zalim.”"},{"id":"108","ar":"ذٰلِكَ اَدْنٰٓى اَنْ يَّأْتُوْا بِالشَّهَادَةِ عَلٰى وَجْهِهَآ اَوْ يَخَافُوْٓا اَنْ تُرَدَّ اَيْمَانٌۢ بَعْدَ اَيْمَانِهِمْۗ وَاتَّقُوا اللّٰهَ وَاسْمَعُوْا ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ","bc":"żālika adnā ay ya`tụ bisy-syahādati 'alā waj-hihā au yakhāfū an turadda aimānum ba'da aimānihim, wattaqullāha wasma'ụ, wallāhu lā yahdil-qaumal-fāsiqīn","at":"Dengan cara itu mereka lebih patut memberikan kesaksiannya menurut yang sebenarnya, dan mereka merasa takut akan dikembalikan sumpahnya (kepada ahli waris) setelah mereka bersumpah. Bertakwalah kepada Allah dan dengarkanlah (perintah-Nya). Dan Allah tidak memberi petunjuk kepada orang-orang yang fasik."},{"id":"109","ar":"۞ يَوْمَ يَجْمَعُ اللّٰهُ الرُّسُلَ فَيَقُوْلُ مَاذَٓا اُجِبْتُمْ ۗ قَالُوْا لَا عِلْمَ لَنَا ۗاِنَّكَ اَنْتَ عَلَّامُ الْغُيُوْبِ","bc":"yauma yajma'ullāhur-rusula fa yaqụlu māżā ujibtum, qālụ lā 'ilma lanā, innaka anta 'allāmul-guyụb","at":"(Ingatlah) pada hari ketika Allah mengumpulkan para rasul, lalu Dia bertanya (kepada mereka), “Apa jawaban (kaummu) terhadap (seruan)mu?” Mereka (para rasul) menjawab, “Kami tidak tahu (tentang itu). Sesungguhnya Engkaulah Yang Maha Mengetahui segala yang gaib.”"},{"id":"110","ar":"اِذْ قَالَ اللّٰهُ يٰعِيْسَى ابْنَ مَرْيَمَ اذْكُرْ نِعْمَتِيْ عَلَيْكَ وَعَلٰى وَالِدَتِكَ ۘاِذْ اَيَّدْتُّكَ بِرُوْحِ الْقُدُسِۗ تُكَلِّمُ النَّاسَ فِى الْمَهْدِ وَكَهْلًا ۚوَاِذْ عَلَّمْتُكَ الْكِتٰبَ وَالْحِكْمَةَ وَالتَّوْرٰىةَ وَالْاِنْجِيْلَ ۚوَاِذْ تَخْلُقُ مِنَ الطِّيْنِ كَهَيْـَٔةِ الطَّيْرِ بِاِذْنِيْ فَتَنْفُخُ فِيْهَا فَتَكُوْنُ طَيْرًاۢ بِاِذْنِيْ وَتُبْرِئُ الْاَكْمَهَ وَالْاَبْرَصَ بِاِذْنِيْ ۚوَاِذْ تُخْرِجُ الْمَوْتٰى بِاِذْنِيْ ۚوَاِذْ كَفَفْتُ بَنِيْٓ اِسْرَاۤءِيْلَ عَنْكَ اِذْ جِئْتَهُمْ بِالْبَيِّنٰتِ فَقَالَ الَّذِيْنَ كَفَرُوْا مِنْهُمْ اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ ","bc":"iż qālallāhu yā 'īsabna maryamażkur ni'matī 'alaika wa 'alā wālidatik, iż ayyattuka birụḥil-qudus, tukallimun-nāsa fil-mahdi wa kahlā, wa iż 'allamtukal-kitāba wal-ḥikmata wat-taurāta wal-injīl, wa iż takhluqu minaṭ-ṭīni kahai`atiṭ-ṭairi bi`iżnī fa tanfukhu fīhā fa takụnu ṭairam bi`iżnī wa tubri`ul-akmaha wal-abraṣa bi`iżnī, wa iż tukhrijul-mautā bi`iżnī, wa iż kafaftu banī isrā`īla 'angka iż ji`tahum bil-bayyināti fa qālallażīna kafarụ min-hum in hāżā illā siḥrum mubīn","at":"Dan ingatlah ketika Allah berfirman, “Wahai Isa putra Maryam! Ingatlah nikmat-Ku kepadamu dan kepada ibumu sewaktu Aku menguatkanmu dengan Rohulkudus. Engkau dapat berbicara dengan manusia pada waktu masih dalam buaian dan setelah dewasa. Dan ingatlah ketika Aku mengajarkan menulis kepadamu, (juga) Hikmah, Taurat dan Injil. Dan ingatlah ketika engkau membentuk dari tanah berupa burung dengan seizin-Ku, kemudian engkau meniupnya, lalu menjadi seekor burung (yang sebenarnya) dengan seizin-Ku. Dan ingatlah ketika engkau menyembuhkan orang yang buta sejak lahir dan orang yang berpenyakit kusta dengan seizin-Ku. Dan ingatlah ketika engkau mengeluarkan orang mati (dari kubur menjadi hidup) dengan seizin-Ku. Dan ingatlah ketika Aku menghalangi Bani Israil (dari keinginan mereka membunuhmu) di kala waktu engkau mengemukakan kepada mereka keterangan-keterangan yang nyata, lalu orang-orang kafir di antara mereka berkata, “Ini tidak lain hanyalah sihir yang nyata.”"},{"id":"111","ar":"وَاِذْ اَوْحَيْتُ اِلَى الْحَوَارِيّٖنَ اَنْ اٰمِنُوْا بِيْ وَبِرَسُوْلِيْ ۚ قَالُوْٓا اٰمَنَّا وَاشْهَدْ بِاَنَّنَا مُسْلِمُوْنَ ","bc":"wa iż auḥaitu ilal-ḥawāriyyīna an āminụ bī wa birasụlī, qālū āmannā wasy-had bi`annanā muslimụn","at":"Dan (ingatlah), ketika Aku ilhamkan kepada pengikut-pengikut Isa yang setia, “Berimanlah kamu kepada-Ku dan kepada Rasul-Ku.” Mereka menjawab, “Kami telah beriman, dan saksikanlah (wahai Rasul) bahwa kami adalah orang-orang yang berserah diri (Muslim).”"},{"id":"112","ar":"اِذْ قَالَ الْحَوَارِيُّوْنَ يٰعِيْسَى ابْنَ مَرْيَمَ هَلْ يَسْتَطِيْعُ رَبُّكَ اَنْ يُّنَزِّلَ عَلَيْنَا مَاۤىِٕدَةً مِّنَ السَّمَاۤءِ ۗقَالَ اتَّقُوا اللّٰهَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ","bc":"iż qālal-ḥawāriyyụna yā 'īsabna maryama hal yastaṭī'u rabbuka ay yunazzila 'alainā mā`idatam minas-samā`, qālattaqullāha ing kuntum mu`minīn","at":"(Ingatlah), ketika pengikut-pengikut Isa yang setia berkata, “Wahai Isa putra Maryam! Bersediakah Tuhanmu menurunkan hidangan dari langit kepada kami?” Isa menjawab, “Bertakwalah kepada Allah jika kamu orang-orang beriman.”"},{"id":"113","ar":"قَالُوْا نُرِيْدُ اَنْ نَّأْكُلَ مِنْهَا وَتَطْمَىِٕنَّ قُلُوْبُنَا وَنَعْلَمَ اَنْ قَدْ صَدَقْتَنَا وَنَكُوْنَ عَلَيْهَا مِنَ الشّٰهِدِيْنَ ","bc":"qālụ nurīdu an na`kula min-hā wa taṭma`inna qulụbunā wa na'lama ang qad ṣadaqtanā wa nakụna 'alaihā minasy-syāhidīn","at":"Mereka berkata, “Kami ingin memakan hidangan itu agar tenteram hati kami dan agar kami yakin bahwa engkau telah berkata benar kepada kami, dan kami menjadi orang-orang yang menyaksikan (hidangan itu).”"},{"id":"114","ar":"قَالَ عِيْسَى ابْنُ مَرْيَمَ اللهم رَبَّنَآ اَنْزِلْ عَلَيْنَا مَاۤىِٕدَةً مِّنَ السَّمَاۤءِ تَكُوْنُ لَنَا عِيْدًا لِّاَوَّلِنَا وَاٰخِرِنَا وَاٰيَةً مِّنْكَ وَارْزُقْنَا وَاَنْتَ خَيْرُ الرّٰزِقِيْنَ","bc":"qāla 'īsabnu maryamallāhumma rabbanā anzil 'alainā mā`idatam minas-samā`i takụnu lanā 'īdal li`awwalinā wa ākhirinā wa āyatam mingka warzuqnā wa anta khairur-rāziqīn","at":" Isa putra Maryam berdoa, “Ya Tuhan kami, turunkanlah kepada kami hidangan dari langit (yang hari turunnya) akan menjadi hari raya bagi kami, yaitu bagi orang-orang yang sekarang bersama kami maupun yang datang setelah kami, dan menjadi tanda bagi kekuasaan Engkau; berilah kami rezeki, dan Engkaulah sebaik-baik pemberi rezeki.”"},{"id":"115","ar":"قَالَ اللّٰهُ اِنِّيْ مُنَزِّلُهَا عَلَيْكُمْ ۚ فَمَنْ يَّكْفُرْ بَعْدُ مِنْكُمْ فَاِنِّيْٓ اُعَذِّبُهٗ عَذَابًا لَّآ اُعَذِّبُهٗٓ اَحَدًا مِّنَ الْعٰلَمِيْنَ ","bc":"qālallāhu innī munazziluhā 'alaikum, fa may yakfur ba'du mingkum fa innī u'ażżibuhụ 'ażābal lā u'ażżibuhū aḥadam minal-'ālamīn","at":"Allah berfirman, “Sungguh, Aku akan menurunkan hidangan itu kepadamu, tetapi barangsiapa kafir di antaramu setelah (turun hidangan) itu, maka sungguh, Aku akan mengazabnya dengan azab yang tidak pernah Aku timpakan kepada seorang pun di antara umat manusia (seluruh alam).”"},{"id":"116","ar":"وَاِذْ قَالَ اللّٰهُ يٰعِيْسَى ابْنَ مَرْيَمَ ءَاَنْتَ قُلْتَ لِلنَّاسِ اتَّخِذُوْنِيْ وَاُمِّيَ اِلٰهَيْنِ مِنْ دُوْنِ اللّٰهِ ۗقَالَ سُبْحٰنَكَ مَا يَكُوْنُ لِيْٓ اَنْ اَقُوْلَ مَا لَيْسَ لِيْ بِحَقٍّ ۗاِنْ كُنْتُ قُلْتُهٗ فَقَدْ عَلِمْتَهٗ ۗتَعْلَمُ مَا فِيْ نَفْسِيْ وَلَآ اَعْلَمُ مَا فِيْ نَفْسِكَ ۗاِنَّكَ اَنْتَ عَلَّامُ الْغُيُوْبِ","bc":"wa iż qālallāhu yā 'īsabna maryama a anta qulta lin-nāsittakhiżụnī wa ummiya ilāhaini min dụnillāh, qāla sub-ḥānaka mā yakụnu lī an aqụla mā laisa lī biḥaqq, ing kuntu qultuhụ fa qad 'alimtah, ta'lamu mā fī nafsī wa lā a'lamu mā fī nafsik, innaka anta 'allāmul-guyụb","at":"Dan (ingatlah) ketika Allah berfirman, “Wahai Isa putra Maryam! Engkaukah yang mengatakan kepada orang-orang, jadikanlah aku dan ibuku sebagai dua tuhan selain Allah?” (Isa) menjawab, “Mahasuci Engkau, tidak patut bagiku mengatakan apa yang bukan hakku. Jika aku pernah mengatakannya tentulah Engkau telah mengetahuinya. Engkau mengetahui apa yang ada pada diriku dan aku tidak mengetahui apa yang ada pada-Mu. Sungguh, Engkaulah Yang Maha Mengetahui segala yang gaib.”"},{"id":"117","ar":"مَا قُلْتُ لَهُمْ اِلَّا مَآ اَمَرْتَنِيْ بِهٖٓ اَنِ اعْبُدُوا اللّٰهَ رَبِّيْ وَرَبَّكُمْ ۚوَكُنْتُ عَلَيْهِمْ شَهِيْدًا مَّا دُمْتُ فِيْهِمْ ۚ فَلَمَّا تَوَفَّيْتَنِيْ كُنْتَ اَنْتَ الرَّقِيْبَ عَلَيْهِمْ ۗوَاَنْتَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ","bc":"mā qultu lahum illā mā amartanī bihī ani'budullāha rabbī wa rabbakum, wa kuntu 'alaihim syahīdam mā dumtu fīhim, fa lammā tawaffaitanī kunta antar-raqība 'alaihim, wa anta 'alā kulli syai`in syahīd","at":"Aku tidak pernah mengatakan kepada mereka kecuali apa yang Engkau perintahkan kepadaku (yaitu), “Sembahlah Allah, Tuhanku dan Tuhanmu,” dan aku menjadi saksi terhadap mereka, selama aku berada di tengah-tengah mereka. Maka setelah Engkau mewafatkan aku, Engkaulah yang mengawasi mereka. Dan Engkaulah Yang Maha Menyaksikan atas segala sesuatu."},{"id":"118","ar":"اِنْ تُعَذِّبْهُمْ فَاِنَّهُمْ عِبَادُكَ ۚوَاِنْ تَغْفِرْ لَهُمْ فَاِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُ ","bc":"in tu'ażżib-hum fa innahum 'ibāduk, wa in tagfir lahum fa innaka antal-'azīzul-ḥakīm","at":"Jika Engkau menyiksa mereka, maka sesungguhnya mereka adalah hamba-hamba-Mu, dan jika Engkau mengampuni mereka, sesungguhnya Engkaulah Yang Mahaperkasa, Mahabijaksana.”"},{"id":"119","ar":"قَالَ اللّٰهُ هٰذَا يَوْمُ يَنْفَعُ الصّٰدِقِيْنَ صِدْقُهُمْ ۗ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗرَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ ۗذٰلِكَ الْفَوْزُ الْعَظِيْمُ ","bc":"qālallāhu hāżā yaumu yanfa'uṣ-ṣādiqīna ṣidquhum, lahum jannātun tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, raḍiyallāhu 'an-hum wa raḍụ 'an-h, żālikal-fauzul-'aẓīm","at":"Allah berfirman, “Inilah saat orang yang benar memperoleh manfaat dari kebenarannya. Mereka memperoleh surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya selama-lamanya. Allah rida kepada mereka dan mereka pun rida kepada-Nya. Itulah kemenangan yang agung.”"},{"id":"120","ar":"لِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا فِيْهِنَّ ۗوَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"lillāhi mulkus-samāwāti wal-arḍi wa mā fīhinn, wa huwa 'alā kulli syai`ing qadīr","at":"Milik Allah kerajaan langit dan bumi dan apa yang ada di dalamnya; dan Dia Mahakuasa atas segala sesuatu."}],al_maun=[{"id":"1","ar":"اَرَءَيْتَ الَّذِيْ يُكَذِّبُ بِالدِّيْنِۗ","bc":"a ra`aitallażī yukażżibu bid-dīn","at":"Tahukah kamu (orang) yang mendustakan agama?"},{"id":"2","ar":"فَذٰلِكَ الَّذِيْ يَدُعُّ الْيَتِيْمَۙ","bc":"fa żālikallażī yadu''ul-yatīm","at":"Maka itulah orang yang menghardik anak yatim,"},{"id":"3","ar":"وَلَا يَحُضُّ عَلٰى طَعَامِ الْمِسْكِيْنِۗ","bc":"wa lā yaḥuḍḍu 'alā ṭa'āmil-miskīn","at":"dan tidak mendorong memberi makan orang miskin."},{"id":"4","ar":"فَوَيْلٌ لِّلْمُصَلِّيْنَۙ","bc":"fa wailul lil-muṣallīn","at":"Maka celakalah orang yang salat,"},{"id":"5","ar":"الَّذِيْنَ هُمْ عَنْ صَلَاتِهِمْ سَاهُوْنَۙ","bc":"allażīna hum 'an ṣalātihim sāhụn","at":"(yaitu) orang-orang yang lalai terhadap salatnya,"},{"id":"6","ar":"الَّذِيْنَ هُمْ يُرَاۤءُوْنَۙ","bc":"allażīna hum yurā`ụn","at":"yang berbuat ria,"},{"id":"7","ar":"وَيَمْنَعُوْنَ الْمَاعُوْنَ","bc":"wa yamna'ụnal-mā'ụn","at":"dan enggan (memberikan) bantuan."}],al_muddassir=[{"id":"1","ar":"يٰٓاَيُّهَا الْمُدَّثِّرُۙ","bc":"yā ayyuhal-muddaṡṡir","at":"Wahai orang yang berkemul (berselimut)!"},{"id":"2","ar":"قُمْ فَاَنْذِرْۖ","bc":"qum fa anżir","at":"bangunlah, lalu berilah peringatan!"},{"id":"3","ar":"وَرَبَّكَ فَكَبِّرْۖ","bc":"wa rabbaka fa kabbir","at":"dan agungkanlah Tuhanmu,"},{"id":"4","ar":"وَثِيَابَكَ فَطَهِّرْۖ","bc":"wa ṡiyābaka fa ṭahhir","at":"dan bersihkanlah pakaianmu,"},{"id":"5","ar":"وَالرُّجْزَ فَاهْجُرْۖ ","bc":"war-rujza fahjur","at":"dan tinggalkanlah segala (perbuatan) yang keji,"},{"id":"6","ar":"وَلَا تَمْنُنْ تَسْتَكْثِرُۖ ","bc":"wa lā tamnun tastakṡir","at":"dan janganlah engkau (Muhammad) memberi (dengan maksud) memperoleh (balasan) yang lebih banyak."},{"id":"7","ar":"وَلِرَبِّكَ فَاصْبِرْۗ ","bc":"wa lirabbika faṣbir","at":"Dan karena Tuhanmu, bersabarlah."},{"id":"8","ar":"فَاِذَا نُقِرَ فِى النَّاقُوْرِۙ ","bc":"fa iżā nuqira fin-nāqụr","at":"Maka apabila sangkakala ditiup,"},{"id":"9","ar":"فَذٰلِكَ يَوْمَىِٕذٍ يَّوْمٌ عَسِيْرٌۙ ","bc":"fa żālika yauma`iżiy yaumun 'asīr","at":"maka itulah hari yang serba sulit,"},{"id":"10","ar":"عَلَى الْكٰفِرِيْنَ غَيْرُ يَسِيْرٍ ","bc":"'alal-kāfirīna gairu yasīr","at":"bagi orang-orang kafir tidak mudah."},{"id":"11","ar":"ذَرْنِيْ وَمَنْ خَلَقْتُ وَحِيْدًاۙ ","bc":"żarnī wa man khalaqtu waḥīdā","at":"Biarkanlah Aku (yang bertindak) terhadap orang yang Aku sendiri telah menciptakannya,"},{"id":"12","ar":"وَّجَعَلْتُ لَهٗ مَالًا مَّمْدُوْدًاۙ ","bc":"wa ja'altu lahụ mālam mamdụdā","at":"dan Aku beri kekayaan yang melimpah,"},{"id":"13","ar":"وَّبَنِيْنَ شُهُوْدًاۙ ","bc":"wa banīna syuhụdā","at":"dan anak-anak yang selalu bersamanya,"},{"id":"14","ar":"وَّمَهَّدْتُّ لَهٗ تَمْهِيْدًاۙ ","bc":"wa mahhattu lahụ tamhīdā","at":"dan Aku beri kelapangan (hidup) seluas-luasnya."},{"id":"15","ar":"ثُمَّ يَطْمَعُ اَنْ اَزِيْدَۙ ","bc":"ṡumma yaṭma'u an azīd","at":"Kemudian dia ingin sekali agar Aku menambahnya."},{"id":"16","ar":"كَلَّاۗ اِنَّهٗ كَانَ لِاٰيٰتِنَا عَنِيْدًاۗ ","bc":"kallā, innahụ kāna li`āyātinā 'anīdā","at":"Tidak bisa! Sesungguhnya dia telah menentang ayat-ayat Kami (Al-Qur'an)."},{"id":"17","ar":"سَاُرْهِقُهٗ صَعُوْدًاۗ ","bc":"sa`ur-hiquhụ ṣa'ụdā","at":"Aku akan membebaninya dengan pendakian yang memayahkan."},{"id":"18","ar":"اِنَّهٗ فَكَّرَ وَقَدَّرَۙ ","bc":"innahụ fakkara wa qaddar","at":"Sesungguhnya dia telah memikirkan dan menetapkan (apa yang ditetapkannya),"},{"id":"19","ar":"فَقُتِلَ كَيْفَ قَدَّرَۙ ","bc":"fa qutila kaifa qaddar","at":"maka celakalah dia! Bagaimana dia menetapkan?"},{"id":"20","ar":"ثُمَّ قُتِلَ كَيْفَ قَدَّرَۙ ","bc":"ṡumma qutila kaifa qaddar","at":"Sekali lagi, celakalah dia! Bagaimana dia menetapkan?"},{"id":"21","ar":"ثُمَّ نَظَرَۙ ","bc":"ṡumma naẓar","at":"Kemudian dia (merenung) memikirkan,"},{"id":"22","ar":"ثُمَّ عَبَسَ وَبَسَرَۙ ","bc":"ṡumma 'abasa wa basar","at":"lalu berwajah masam dan cemberut,"},{"id":"23","ar":"ثُمَّ اَدْبَرَ وَاسْتَكْبَرَۙ ","bc":"ṡumma adbara wastakbar","at":"kemudian berpaling (dari kebenaran) dan menyombongkan diri,"},{"id":"24","ar":"فَقَالَ اِنْ هٰذَآ اِلَّا سِحْرٌ يُّؤْثَرُۙ ","bc":"fa qāla in hāżā illā siḥruy yu`ṡar","at":"lalu dia berkata, “(Al-Qur'an) ini hanyalah sihir yang dipelajari (dari orang-orang dahulu)."},{"id":"25","ar":"اِنْ هٰذَآ اِلَّا قَوْلُ الْبَشَرِۗ ","bc":"in hāżā illā qaulul-basyar","at":"Ini hanyalah perkataan manusia.”"},{"id":"26","ar":"سَاُصْلِيْهِ سَقَرَ","bc":"sa`uṣlīhi saqar","at":"Kelak, Aku akan memasukkannya ke dalam (neraka) Saqar,"},{"id":"27","ar":"وَمَآ اَدْرٰىكَ مَا سَقَرُۗ ","bc":"wa mā adrāka mā saqar","at":"dan tahukah kamu apa (neraka) Saqar itu?"},{"id":"28","ar":"لَا تُبْقِيْ وَلَا تَذَرُۚ ","bc":"lā tubqī wa lā tażar","at":"Ia (Saqar itu) tidak meninggalkan dan tidak membiarkan,"},{"id":"29","ar":"لَوَّاحَةٌ لِّلْبَشَرِۚ ","bc":"lawwāḥatul lil-basyar","at":"yang menghanguskan kulit manusia."},{"id":"30","ar":"عَلَيْهَا تِسْعَةَ عَشَرَۗ ","bc":"'alaihā tis'ata 'asyar","at":"Di atasnya ada sembilan belas (malaikat penjaga)."},{"id":"31","ar":"وَمَا جَعَلْنَآ اَصْحٰبَ النَّارِ اِلَّا مَلٰۤىِٕكَةً ۖوَّمَا جَعَلْنَا عِدَّتَهُمْ اِلَّا فِتْنَةً لِّلَّذِيْنَ كَفَرُوْاۙ لِيَسْتَيْقِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ وَيَزْدَادَ الَّذِيْنَ اٰمَنُوْٓا اِيْمَانًا وَّلَا يَرْتَابَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ وَالْمُؤْمِنُوْنَۙ وَلِيَقُوْلَ الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ وَّالْكٰفِرُوْنَ مَاذَآ اَرَادَ اللّٰهُ بِهٰذَا مَثَلًاۗ كَذٰلِكَ يُضِلُّ اللّٰهُ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُۗ وَمَا يَعْلَمُ جُنُوْدَ رَبِّكَ اِلَّا هُوَۗ وَمَا هِيَ اِلَّا ذِكْرٰى لِلْبَشَرِ","bc":"wa mā ja'alnā aṣ-ḥāban-nāri illā malā`ikataw wa mā ja'alnā 'iddatahum illā fitnatal lillażīna kafarụ liyastaiqinallażīna ụtul-kitāba wa yazdādallażīna āmanū īmānaw wa lā yartāballażīna ụtul-kitāba wal-mu`minụna wa liyaqụlallażīna fī qulụbihim maraḍuw wal-kāfirụna māżā arādallāhu bihāżā maṡalā, każālika yuḍillullāhu may yasyā`u wa yahdī may yasyā`, wa mā ya'lamu junụda rabbika illā huw, wa mā hiya illā żikrā lil-basyar","at":"Dan yang Kami jadikan penjaga neraka itu hanya dari malaikat; dan Kami menentukan bilangan mereka itu hanya sebagai cobaan bagi orang-orang kafir, agar orang-orang yang diberi kitab menjadi yakin, agar orang yang beriman bertambah imannya, agar orang-orang yang diberi kitab dan orang-orang mukmin itu tidak ragu-ragu; dan agar orang-orang yang di dalam hatinya ada penyakit dan orang-orang kafir (berkata), “Apakah yang dikehendaki Allah dengan (bilangan) ini sebagai suatu perumpamaan?” Demikianlah Allah membiarkan sesat orang-orang yang Dia kehendaki dan memberi petunjuk kepada orang-orang yang Dia kehendaki. Dan tidak ada yang mengetahui bala tentara Tuhanmu kecuali Dia sendiri. Dan Saqar itu tidak lain hanyalah peringatan bagi manusia."},{"id":"32","ar":"كَلَّا وَالْقَمَرِۙ","bc":"kallā wal-qamar","at":"Tidak! Demi bulan,"},{"id":"33","ar":"وَالَّيْلِ اِذْ اَدْبَرَۙ","bc":"wal-laili iż adbar","at":"dan demi malam ketika telah berlalu,"},{"id":"34","ar":"وَالصُّبْحِ اِذَآ اَسْفَرَۙ","bc":"waṣ-ṣub-ḥi iżā asfar","at":"dan demi subuh apabila mulai terang, "},{"id":"35","ar":"اِنَّهَا لَاِحْدَى الْكُبَرِۙ","bc":"innahā la`iḥdal-kubar","at":"sesunggunya (Saqar itu) adalah salah satu (bencana) yang sangat besar,"},{"id":"36","ar":"نَذِيْرًا لِّلْبَشَرِۙ","bc":"nażīral lil-basyar","at":"sebagai peringatan bagi manusia, "},{"id":"37","ar":"لِمَنْ شَاۤءَ مِنْكُمْ اَنْ يَّتَقَدَّمَ اَوْ يَتَاَخَّرَۗ","bc":"liman syā`a mingkum ay yataqaddama au yata`akhkhar","at":"(yaitu) bagi siapa di antara kamu yang ingin maju atau mundur."},{"id":"38","ar":"كُلُّ نَفْسٍۢ بِمَا كَسَبَتْ رَهِيْنَةٌۙ","bc":"kullu nafsim bimā kasabat rahīnah","at":"Setiap orang bertanggung jawab atas apa yang telah dilakukannya,"},{"id":"39","ar":"اِلَّآ اَصْحٰبَ الْيَمِيْنِ ۛ ","bc":"illā aṣ-ḥābal-yamīn","at":"kecuali golongan kanan,"},{"id":"40","ar":"فِيْ جَنّٰتٍ ۛ يَتَسَاۤءَلُوْنَۙ","bc":"fī jannātiy yatasā`alụn","at":"berada di dalam surga, mereka saling menanyakan,"},{"id":"41","ar":"عَنِ الْمُجْرِمِيْنَۙ","bc":"'anil-mujrimīn","at":"tentang (keadaan) orang-orang yang berdosa, "},{"id":"42","ar":"مَا سَلَكَكُمْ فِيْ سَقَرَ ","bc":"mā salakakum fī saqar","at":"”Apa yang menyebabkan kamu masuk ke dalam (neraka) Saqar?”"},{"id":"43","ar":"قَالُوْا لَمْ نَكُ مِنَ الْمُصَلِّيْنَۙ","bc":"qālụ lam naku minal-muṣallīn","at":"Mereka menjawab, “Dahulu kami tidak termasuk orang-orang yang me-laksanakan salat, "},{"id":"44","ar":"وَلَمْ نَكُ نُطْعِمُ الْمِسْكِيْنَۙ","bc":"wa lam naku nuṭ'imul-miskīn","at":"dan kami (juga) tidak memberi makan orang miskin, "},{"id":"45","ar":"وَكُنَّا نَخُوْضُ مَعَ الْخَاۤىِٕضِيْنَۙ","bc":"wa kunnā nakhụḍu ma'al-khā`iḍīn","at":"bahkan kami biasa berbincang (untuk tujuan yang batil), bersama orang-orang yang membicarakannya, "},{"id":"46","ar":"وَكُنَّا نُكَذِّبُ بِيَوْمِ الدِّيْنِۙ","bc":"wa kunnā nukażżibu biyaumid-dīn","at":"dan kami mendustakan hari pembalasan, "},{"id":"47","ar":"حَتّٰىٓ اَتٰىنَا الْيَقِيْنُۗ","bc":"ḥattā atānal-yaqīn","at":"sampai datang kepada kami kematian.” "},{"id":"48","ar":"فَمَا تَنْفَعُهُمْ شَفَاعَةُ الشَّافِعِيْنَۗ","bc":"fa mā tanfa'uhum syafā'atusy-syāfi'īn","at":"Maka tidak berguna lagi bagi mereka syafaat (pertolongan) dari orang-orang yang memberikan syafaat. "},{"id":"49","ar":"فَمَا لَهُمْ عَنِ التَّذْكِرَةِ مُعْرِضِيْنَۙ","bc":"fa mā lahum 'anit-tażkirati mu'riḍīn","at":"Lalu mengapa mereka (orang-orang kafir) berpaling dari peringatan (Allah)?"},{"id":"50","ar":"كَاَنَّهُمْ حُمُرٌ مُّسْتَنْفِرَةٌۙ","bc":"ka`annahum ḥumurum mustanfirah","at":"seakan-akan mereka keledai liar yang lari terkejut,"},{"id":"51","ar":"فَرَّتْ مِنْ قَسْوَرَةٍۗ","bc":"farrat ming qaswarah","at":"lari dari singa. "},{"id":"52","ar":"بَلْ يُرِيْدُ كُلُّ امْرِئٍ مِّنْهُمْ اَنْ يُّؤْتٰى صُحُفًا مُّنَشَّرَةًۙ","bc":"bal yurīdu kullumri`im min-hum ay yu`tā ṣuḥufam munasysyarah","at":"Bahkan setiap orang dari mereka ingin agar diberikan kepadanya lembaran-lembaran (kitab) yang terbuka. "},{"id":"53","ar":"كَلَّاۗ بَلْ لَّا يَخَافُوْنَ الْاٰخِرَةَۗ","bc":"kallā, bal lā yakhāfụnal-ākhirah","at":"Tidak! Sebenarnya mereka tidak takut kepada akhirat."},{"id":"54","ar":"كَلَّآ اِنَّهٗ تَذْكِرَةٌ ۚ","bc":"kallā innahụ tażkirah","at":"Tidak! Sesungguhnya (Al-Qur'an) itu benar-benar suatu peringatan. "},{"id":"55","ar":"فَمَنْ شَاۤءَ ذَكَرَهٗۗ","bc":"fa man syā`a żakarah","at":"Maka barangsiapa menghendaki, tentu dia mengambil pelajaran darinya. "},{"id":"56","ar":"وَمَا يَذْكُرُوْنَ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۗهُوَ اَهْلُ التَّقْوٰى وَاَهْلُ الْمَغْفِرَةِ","bc":"wa mā yażkurụna illā ay yasyā`allāh, huwa ahlut-taqwā wa ahlul-magfirah","at":"Dan mereka tidak akan mengambil pelajaran darinya (Al-Qur'an) kecuali (jika) Allah menghendakinya. Dialah Tuhan yang patut (kita) bertakwa kepada-Nya dan yang berhak memberi ampunan. "}],al_mujadilah=[{"id":"1","ar":"قَدْ سَمِعَ اللّٰهُ قَوْلَ الَّتِيْ تُجَادِلُكَ فِيْ زَوْجِهَا وَتَشْتَكِيْٓ اِلَى اللّٰهِ ۖوَاللّٰهُ يَسْمَعُ تَحَاوُرَكُمَاۗ اِنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ ","bc":"qad sami'allāhu qaulallatī tujādiluka fī zaujihā wa tasytakī ilallāhi wallāhu yasma'u taḥāwurakumā, innallāha samī'um baṣīr","at":"Sungguh, Allah telah mendengar ucapan perempuan yang mengajukan gugatan kepadamu (Muhammad) tentang suaminya, dan mengadukan (halnya) kepada Allah, dan Allah mendengar percakapan antara kamu berdua. Sesungguhnya Allah Maha Mendengar, Maha Melihat."},{"id":"2","ar":"اَلَّذِيْنَ يُظٰهِرُوْنَ مِنْكُمْ مِّنْ نِّسَاۤىِٕهِمْ مَّا هُنَّ اُمَّهٰتِهِمْۗ اِنْ اُمَّهٰتُهُمْ اِلَّا الّٰۤـِٔيْ وَلَدْنَهُمْۗ وَاِنَّهُمْ لَيَقُوْلُوْنَ مُنْكَرًا مِّنَ الْقَوْلِ وَزُوْرًاۗ وَاِنَّ اللّٰهَ لَعَفُوٌّ غَفُوْرٌ ","bc":"allażīna yuẓāhirụna mingkum min nisā`ihim mā hunna ummahātihim, in ummahātuhum illal-lā`ī waladnahum, wa innahum layaqụlụna mungkaram minal-qauli wazụrā, wa innallāha la'afuwwun gafụr","at":"Orang-orang di antara kamu yang menzihar istrinya, (menganggap istrinya sebagai ibunya, padahal) istri mereka itu bukanlah ibunya. Ibu-ibu mereka hanyalah perempuan yang melahirkannya. Dan sesungguhnya mereka benar-benar telah mengucapkan suatu perkataan yang mungkar dan dusta. Dan sesungguhnya Allah Maha Pemaaf, Maha Pengampun. "},{"id":"3","ar":"وَالَّذِيْنَ يُظٰهِرُوْنَ مِنْ نِّسَاۤىِٕهِمْ ثُمَّ يَعُوْدُوْنَ لِمَا قَالُوْا فَتَحْرِيْرُ رَقَبَةٍ مِّنْ قَبْلِ اَنْ يَّتَمَاۤسَّاۗ ذٰلِكُمْ تُوْعَظُوْنَ بِهٖۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ ","bc":"wallażīna yuẓāhirụna min nisā`ihim ṡumma ya'ụdụna limā qālụ fa taḥrīru raqabatim ming qabli ay yatamāssā, żālikum tụ'aẓụna bih, wallāhu bimā ta'malụna khabīr","at":"Dan mereka yang menzihar istrinya, kemudian menarik kembali apa yang telah mereka ucapkan, maka (mereka diwajibkan) memerdekakan seorang budak sebelum kedua suami istri itu bercampur. Demikianlah yang diajarkan kepadamu, dan Allah Mahateliti terhadap apa yang kamu kerjakan. "},{"id":"4","ar":"فَمَنْ لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِنْ قَبْلِ اَنْ يَّتَمَاۤسَّاۗ فَمَنْ لَّمْ يَسْتَطِعْ فَاِطْعَامُ سِتِّيْنَ مِسْكِيْنًاۗ ذٰلِكَ لِتُؤْمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖۗ وَتِلْكَ حُدُوْدُ اللّٰهِ ۗوَلِلْكٰفِرِيْنَ عَذَابٌ اَلِيْمٌ ","bc":"fa mal lam yajid fa ṣiyāmu syahraini mutatābi'aini ming qabli ay yatamāssā, fa mal lam yastaṭi' fa iṭ'āmu sittīna miskīnā, żālika litu`minụ billāhi wa rasụlih, wa tilka ḥudụdullāh, wa lil-kāfirīna 'ażābun alīm","at":"Maka barangsiapa tidak dapat (memerdekakan hamba sahaya), maka (dia wajib) berpuasa dua bulan berturut-turut sebelum keduanya bercampur. Tetapi barangsiapa tidak mampu, maka (wajib) memberi makan enam puluh orang miskin. Demikianlah agar kamu beriman kepada Allah dan Rasul-Nya. Itulah hukum-hukum Allah, dan bagi orang-orang yang mengingkarinya akan mendapat azab yang sangat pedih. "},{"id":"5","ar":"اِنَّ الَّذِيْنَ يُحَاۤدُّوْنَ اللّٰهَ وَرَسُوْلَهٗ كُبِتُوْا كَمَا كُبِتَ الَّذِيْنَ مِنْ قَبْلِهِمْ وَقَدْ اَنْزَلْنَآ اٰيٰتٍۢ بَيِّنٰتٍۗ وَلِلْكٰفِرِيْنَ عَذَابٌ مُّهِيْنٌۚ ","bc":"innallażīna yuḥāddụnallāha wa rasụlahụ kubitụ kamā kubitallażīna ming qablihim wa qad anzalnā āyātim bayyināt, wa lil-kāfirīna 'ażābum muhīn","at":"Sesungguhnya orang-orang yang menentang Allah dan Rasul-Nya pasti mendapat kehinaan sebagaimana kehinaan yang telah didapat oleh orang-orang sebelum mereka. Dan sungguh, Kami telah menurunkan bukti-bukti yang nyata. Dan bagi orang-orang yang mengingkarinya akan mendapat azab yang menghinakan. "},{"id":"6","ar":"يَوْمَ يَبْعَثُهُمُ اللّٰهُ جَمِيْعًا فَيُنَبِّئُهُمْ بِمَا عَمِلُوْاۗ اَحْصٰىهُ اللّٰهُ وَنَسُوْهُۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ","bc":"yauma yab'aṡuhumullāhu jamī'an fa yunabbi`uhum bimā 'amilụ, aḥṣāhullāhu wa nasụh, wallāhu 'alā kulli syai`in syahīd","at":"Pada hari itu mereka semuanya dibangkitkan Allah, lalu diberitakan-Nya kepada mereka apa yang telah mereka kerjakan. Allah menghitungnya (semua amal perbuatan itu), meskipun mereka telah melupakannya. Dan Allah Maha Menyaksikan segala sesuatu. "},{"id":"7","ar":"اَلَمْ تَرَ اَنَّ اللّٰهَ يَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ مَا يَكُوْنُ مِنْ نَّجْوٰى ثَلٰثَةٍ اِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ اِلَّا هُوَ سَادِسُهُمْ وَلَآ اَدْنٰى مِنْ ذٰلِكَ وَلَآ اَكْثَرَ اِلَّا هُوَ مَعَهُمْ اَيْنَ مَا كَانُوْاۚ ثُمَّ يُنَبِّئُهُمْ بِمَا عَمِلُوْا يَوْمَ الْقِيٰمَةِۗ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ ","bc":"a lam tara annallāha ya'lamu mā fis-samāwāti wa mā fil-arḍ, mā yakụnu min najwā ṡalāṡatin illā huwa rābi'uhum wa lā khamsatin illā huwa sādisuhum wa lā adnā min żālika wa lā akṡara illā huwa ma'ahum aina mā kānụ, ṡumma yunabbi`uhum bimā 'amilụ yaumal-qiyāmah, innallāha bikulli syai`in 'alīm","at":"Tidakkah engkau perhatikan, bahwa Allah mengetahui apa yang ada di langit dan apa yang ada di bumi? Tidak ada pembicaraan rahasia antara tiga orang, melainkan Dialah yang keempatnya. Dan tidak ada lima orang, melainkan Dialah yang keenamnya. Dan tidak ada yang kurang dari itu atau lebih banyak, melainkan Dia pasti ada bersama mereka di mana pun mereka berada. Kemudian Dia akan memberitakan kepada mereka pada hari Kiamat apa yang telah mereka kerjakan. Sesungguhnya Allah Maha Mengetahui segala sesuatu. "},{"id":"8","ar":"اَلَمْ تَرَ اِلَى الَّذِيْنَ نُهُوْا عَنِ النَّجْوٰى ثُمَّ يَعُوْدُوْنَ لِمَا نُهُوْا عَنْهُ وَيَتَنٰجَوْنَ بِالْاِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُوْلِۖ وَاِذَا جَاۤءُوْكَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ اللّٰهُ ۙوَيَقُوْلُوْنَ فِيْٓ اَنْفُسِهِمْ لَوْلَا يُعَذِّبُنَا اللّٰهُ بِمَا نَقُوْلُۗ حَسْبُهُمْ جَهَنَّمُۚ يَصْلَوْنَهَاۚ فَبِئْسَ الْمَصِيْرُ ","bc":"a lam tara ilallażīna nuhụ 'anin-najwā ṡumma ya'ụdụna limā nuhụ 'an-hu wa yatanājauna bil-iṡmi wal-'udwāni wa ma'ṣiyatir-rasụli wa iżā jā`ụka ḥayyauka bimā lam yuḥayyika bihillāhu wa yaqụlụna fī anfusihim lau lā yu'ażżibunallāhu bimā naqụl, ḥasbuhum jahannam, yaṣlaunahā, fa bi`sal-maṣīr","at":"Tidakkah engkau perhatikan orang-orang yang telah dilarang mengadakan pembicaraan rahasia, kemudian mereka kembali (mengerjakan) larangan itu dan mereka mengadakan pembicaraan rahasia untuk berbuat dosa, permusuhan dan durhaka kepada Rasul. Dan apabila mereka datang kepadamu (Muhammad), mereka mengucapkan salam dengan cara yang bukan seperti yang ditentukan Allah untukmu. Dan mereka mengatakan pada diri mereka sendiri, “Mengapa Allah tidak menyiksa kita atas apa yang kita katakan itu?” Cukuplah bagi mereka neraka Jahanam yang akan mereka masuki. Maka neraka itu seburuk-buruk tempat kembali. "},{"id":"9","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا تَنَاجَيْتُمْ فَلَا تَتَنَاجَوْا بِالْاِثْمِ وَالْعُدْوَانِ وَمَعْصِيَتِ الرَّسُوْلِ وَتَنَاجَوْا بِالْبِرِّ وَالتَّقْوٰىۗ وَاتَّقُوا اللّٰهَ الَّذِيْٓ اِلَيْهِ تُحْشَرُوْنَ","bc":"yā ayyuhallażīna āmanū iżā tanājaitum fa lā tatanājau bil-iṡmi wal-'udwāni wa ma'ṣiyatir-rasụli wa tanājau bil-birri wat-taqwā, wattaqullāhallażī ilaihi tuḥsyarụn","at":"Wahai orang-orang yang beriman! Apabila kamu mengadakan pembicaraan rahasia, janganlah kamu membicarakan perbuatan dosa, permusuhan dan durhaka kepada Rasul. Tetapi bicarakanlah tentang perbuatan kebajikan dan takwa. Dan bertakwalah kepada Allah yang kepada-Nya kamu akan dikumpulkan kembali. "},{"id":"10","ar":"اِنَّمَا النَّجْوٰى مِنَ الشَّيْطٰنِ لِيَحْزُنَ الَّذِيْنَ اٰمَنُوْا وَلَيْسَ بِضَاۤرِّهِمْ شَيْـًٔا اِلَّا بِاِذْنِ اللّٰهِ ۗوَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ","bc":"innaman-najwā minasy-syaiṭāni liyaḥzunallażīna āmanụ wa laisa biḍārrihim syai`an illā bi`iżnillāh, wa 'alallāhi falyatawakkalil-mu`minụn","at":"Sesungguhnya pembicaraan rahasia itu termasuk (perbuatan) setan, agar orang-orang yang beriman itu bersedih hati, sedang (pembicaraan) itu tidaklah memberi bencana sedikit pun kepada mereka, kecuali dengan izin Allah. Dan kepada Allah hendaknya orang-orang yang beriman bertawakal. "},{"id":"11","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا قِيْلَ لَكُمْ تَفَسَّحُوْا فِى الْمَجٰلِسِ فَافْسَحُوْا يَفْسَحِ اللّٰهُ لَكُمْۚ وَاِذَا قِيْلَ انْشُزُوْا فَانْشُزُوْا يَرْفَعِ اللّٰهُ الَّذِيْنَ اٰمَنُوْا مِنْكُمْۙ وَالَّذِيْنَ اُوْتُوا الْعِلْمَ دَرَجٰتٍۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ","bc":"yā ayyuhallażīna āmanū iżā qīla lakum tafassaḥụ fil-majālisi fafsaḥụ yafsaḥillāhu lakum, wa iżā qīlansyuzụ fansyuzụ yarfa'illāhullażīna āmanụ mingkum wallażīna ụtul-'ilma darajāt, wallāhu bimā ta'malụna khabīr","at":"Wahai orang-orang yang beriman! Apabila dikatakan kepadamu, “Berilah kelapangan di dalam majelis-majelis,” maka lapangkanlah, niscaya Allah akan memberi kelapangan untukmu. Dan apabila dikatakan, “Berdirilah kamu,” maka berdirilah, niscaya Allah akan mengangkat (derajat) orang-orang yang beriman di antaramu dan orang-orang yang diberi ilmu beberapa derajat. Dan Allah Mahateliti apa yang kamu kerjakan. "},{"id":"12","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا نَاجَيْتُمُ الرَّسُوْلَ فَقَدِّمُوْا بَيْنَ يَدَيْ نَجْوٰىكُمْ صَدَقَةً ۗذٰلِكَ خَيْرٌ لَّكُمْ وَاَطْهَرُۗ فَاِنْ لَّمْ تَجِدُوْا فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"yā ayyuhallażīna āmanū iżā nājaitumur-rasụla fa qaddimụ baina yadai najwākum ṣadaqah, żālika khairul lakum wa aṭ-har, fa il lam tajidụ fa innallāha gafụrur raḥīm","at":"Wahai orang-orang yang beriman! Apabila kamu mengadakan pembicaraan khusus dengan Rasul, hendaklah kamu mengeluarkan sedekah (kepada orang miskin) sebelum (melakukan) pembicaraan itu. Yang demikian itu lebih baik bagimu dan lebih bersih. Tetapi jika kamu tidak memperoleh (yang akan disedekahkan) maka sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"13","ar":"ءَاَشْفَقْتُمْ اَنْ تُقَدِّمُوْا بَيْنَ يَدَيْ نَجْوٰىكُمْ صَدَقٰتٍۗ فَاِذْ لَمْ تَفْعَلُوْا وَتَابَ اللّٰهُ عَلَيْكُمْ فَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَطِيْعُوا اللّٰهَ وَرَسُوْلَهٗ ۗوَاللّٰهُ خَبِيْرٌ ۢبِمَا تَعْمَلُوْنَ ","bc":"a asyfaqtum an tuqaddimụ baina yadai najwākum ṣadaqāt, fa iż lam taf'alụ wa tāballāhu 'alaikum fa aqīmuṣ-ṣalāta wa ātuz-zakāta wa aṭī'ullāha wa rasụlah, wallāhu khabīrum bimā ta'malụn","at":"Apakah kamu takut akan (menjadi miskin) karena kamu memberikan sedekah sebelum (melakukan) pembicaraan dengan Rasul? Tetapi jika kamu tidak melakukannya dan Allah telah memberi ampun kepadamu, maka laksanakanlah salat, dan tunaikanlah zakat serta taatlah kepada Allah dan Rasul-Nya! Dan Allah Mahateliti terhadap apa yang kamu kerjakan."},{"id":"14","ar":"۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ تَوَلَّوْا قَوْمًا غَضِبَ اللّٰهُ عَلَيْهِمْۗ مَا هُمْ مِّنْكُمْ وَلَا مِنْهُمْۙ وَيَحْلِفُوْنَ عَلَى الْكَذِبِ وَهُمْ يَعْلَمُوْنَ ","bc":"a lam tara ilallażīna tawallau qauman gaḍiballāhu 'alaihim, mā hum mingkum wa lā min-hum wa yaḥlifụna 'alal-każibi wa hum ya'lamụn","at":"Tidakkah engkau perhatikan orang-orang (munafik) yang menjadikan suatu kaum yang telah dimurkai Allah sebagai sahabat? Orang-orang itu bukan dari (kaum) kamu dan bukan dari (kaum) mereka. Dan mereka bersumpah atas kebohongan, sedang mereka mengetahuinya. "},{"id":"15","ar":"اَعَدَّ اللّٰهُ لَهُمْ عَذَابًا شَدِيْدًاۗ اِنَّهُمْ سَاۤءَ مَا كَانُوْا يَعْمَلُوْنَ","bc":"a'addallāhu lahum 'ażāban syadīdā, innahum sā`a mā kānụ ya'malụn","at":"Allah telah menyediakan azab yang sangat keras bagi mereka. Sungguh, betapa buruknya apa yang telah mereka kerjakan. "},{"id":"16","ar":"اِتَّخَذُوْٓا اَيْمَانَهُمْ جُنَّةً فَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ فَلَهُمْ عَذَابٌ مُّهِيْنٌ ","bc":"ittakhażū aimānahum junnatan fa ṣaddụ 'an sabīlillāhi fa lahum 'ażābum muhīn","at":"Mereka menjadikan sumpah-sumpah mereka sebagai perisai, lalu mereka menghalang-halangi (manusia) dari jalan Allah; maka bagi mereka azab yang menghinakan."},{"id":"17","ar":"لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ مِّنَ اللّٰهِ شَيْـًٔاۗ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِۗ هُمْ فِيْهَا خٰلِدُوْنَ ","bc":"lan tugniya 'an-hum amwāluhum wa lā aulāduhum minallāhi syai`ā, ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn","at":"Harta benda dan anak-anak mereka tidak berguna sedikit pun (untuk menolong) mereka dari azab Allah. Mereka itulah penghuni neraka, mereka kekal di dalamnya. "},{"id":"18","ar":"يَوْمَ يَبْعَثُهُمُ اللّٰهُ جَمِيْعًا فَيَحْلِفُوْنَ لَهٗ كَمَا يَحْلِفُوْنَ لَكُمْ وَيَحْسَبُوْنَ اَنَّهُمْ عَلٰى شَيْءٍۗ اَلَآ اِنَّهُمْ هُمُ الْكٰذِبُوْنَ","bc":"yauma yab'aṡuhumullāhu jamī'an fa yaḥlifụna lahụ kamā yaḥlifụna lakum wa yaḥsabụna annahum 'alā syaī`, alā innahum humul-kāżibụn","at":"(Ingatlah) pada hari (ketika) mereka semua dibangkitkan Allah, lalu mereka bersumpah kepada-Nya (bahwa mereka bukan orang musyrik) sebagaimana mereka bersumpah kepadamu; dan mereka menyangka bahwa mereka akan memperoleh sesuatu (manfaat). Ketahuilah, bahwa mereka orang-orang pendusta. "},{"id":"19","ar":"اِسْتَحْوَذَ عَلَيْهِمُ الشَّيْطٰنُ فَاَنْسٰىهُمْ ذِكْرَ اللّٰهِ ۗ اُولٰۤىِٕكَ حِزْبُ الشَّيْطٰنِۗ اَلَآ اِنَّ حِزْبَ الشَّيْطٰنِ هُمُ الْخٰسِرُوْنَ","bc":"istaḥważa 'alaihimusy-syaiṭānu fa ansāhum żikrallāh, ulā`ika ḥizbusy-syaiṭān, alā inna ḥizbasy-syaiṭāni humul-khāsirụn","at":"Setan telah menguasai mereka, lalu menjadikan mereka lupa mengingat Allah; mereka itulah golongan setan. Ketahuilah, bahwa golongan setan itulah golongan yang rugi. "},{"id":"20","ar":"اِنَّ الَّذِيْنَ يُحَاۤدُّوْنَ اللّٰهَ وَرَسُوْلَهٗٓ اُولٰۤىِٕكَ فِى الْاَذَلِّيْنَ","bc":"innallażīna yuḥāddụnallāha wa rasụlahū ulā`ika fil-ażallīn","at":"Sesungguhnya orang-orang yang menentang Allah dan Rasul-Nya, mereka termasuk orang-orang yang sangat hina. "},{"id":"21","ar":"كَتَبَ اللّٰهُ لَاَغْلِبَنَّ اَنَا۠ وَرُسُلِيْۗ اِنَّ اللّٰهَ قَوِيٌّ عَزِيْزٌ ","bc":"kataballāhu la`aglibanna ana wa rusulī, innallāha qawiyyun 'azīz","at":"Allah telah menetapkan, “Aku dan rasul-rasul-Ku pasti menang.” Sungguh, Allah Mahakuat, Mahaperkasa."},{"id":"22","ar":"لَا تَجِدُ قَوْمًا يُّؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ يُوَاۤدُّوْنَ مَنْ حَاۤدَّ اللّٰهَ وَرَسُوْلَهٗ وَلَوْ كَانُوْٓا اٰبَاۤءَهُمْ اَوْ اَبْنَاۤءَهُمْ اَوْ اِخْوَانَهُمْ اَوْ عَشِيْرَتَهُمْۗ اُولٰۤىِٕكَ كَتَبَ فِيْ قُلُوْبِهِمُ الْاِيْمَانَ وَاَيَّدَهُمْ بِرُوْحٍ مِّنْهُ ۗوَيُدْخِلُهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ رَضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُۗ اُولٰۤىِٕكَ حِزْبُ اللّٰهِ ۗ اَلَآ اِنَّ حِزْبَ اللّٰهِ هُمُ الْمُفْلِحُوْنَ ","bc":"lā tajidu qaumay yu`minụna billāhi wal-yaumil-ākhiri yuwāddụna man ḥāddallāha wa rasụlahụ walau kānū ābā`ahum au abnā`ahum au ikhwānahum au 'asyīratahum, ulā`ika kataba fī qulụbihimul-īmāna wa ayyadahum birụḥim min-h, wa yudkhiluhum jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā, raḍiyallāhu 'an-hum wa raḍụ 'an-h, ulā`ika ḥizbullāh, alā inna ḥizballāhi humul-mufliḥụn","at":"Engkau (Muhammad) tidak akan mendapatkan suatu kaum yang beriman kepada Allah dan hari akhirat saling berkasih sayang dengan orang-orang yang menentang Allah dan Rasul-Nya, sekalipun orang-orang itu bapaknya, anaknya, saudaranya atau keluarganya. Mereka itulah orang-orang yang dalam hatinya telah ditanamkan Allah keimanan dan Allah telah menguatkan mereka dengan pertolongan yang datang dari Dia. Lalu dimasukkan-Nya mereka ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Allah rida terhadap mereka dan mereka pun merasa puas terhadap (limpahan rahmat)-Nya. Merekalah golongan Allah. Ingatlah, sesungguhnya golongan Allah itulah yang beruntung. "}],al_mulk=[{"id":"1","ar":"تَبٰرَكَ الَّذِيْ بِيَدِهِ الْمُلْكُۖ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌۙ","bc":"tabārakallażī biyadihil-mulku wa huwa 'alā kulli syai`ing qadīr","at":"Mahasuci Allah yang menguasai (segala) kerajaan, dan Dia Mahakuasa atas segala sesuatu."},{"id":"2","ar":" ۨالَّذِيْ خَلَقَ الْمَوْتَ وَالْحَيٰوةَ لِيَبْلُوَكُمْ اَيُّكُمْ اَحْسَنُ عَمَلًاۗ وَهُوَ الْعَزِيْزُ الْغَفُوْرُۙ","bc":"allażī khalaqal-mauta wal-ḥayāta liyabluwakum ayyukum aḥsanu 'amalā, wa huwal-'azīzul-gafụr","at":"Yang menciptakan mati dan hidup, untuk menguji kamu, siapa di antara kamu yang lebih baik amalnya. Dan Dia Mahaperkasa, Maha Pengampun."},{"id":"3","ar":"الَّذِيْ خَلَقَ سَبْعَ سَمٰوٰتٍ طِبَاقًاۗ مَا تَرٰى فِيْ خَلْقِ الرَّحْمٰنِ مِنْ تَفٰوُتٍۗ فَارْجِعِ الْبَصَرَۙ هَلْ تَرٰى مِنْ فُطُوْرٍ ","bc":"allażī khalaqa sab'a samāwātin ṭibāqā, mā tarā fī khalqir-raḥmāni min tafāwut, farji'il-baṣara hal tarā min fuṭụr","at":"Yang menciptakan tujuh langit berlapis-lapis. Tidak akan kamu lihat sesuatu yang tidak seimbang pada ciptaan Tuhan Yang Maha Pengasih. Maka lihatlah sekali lagi, adakah kamu lihat sesuatu yang cacat?"},{"id":"4","ar":"ثُمَّ ارْجِعِ الْبَصَرَ كَرَّتَيْنِ يَنْقَلِبْ اِلَيْكَ الْبَصَرُ خَاسِئًا وَّهُوَ حَسِيْرٌ ","bc":"ṡummarji'il-baṣara karrataini yangqalib ilaikal-baṣaru khāsi`aw wa huwa ḥasīr","at":"Kemudian ulangi pandangan(mu) sekali lagi (dan) sekali lagi, niscaya pandanganmu akan kembali kepadamu tanpa menemukan cacat dan ia (pandanganmu) dalam keadaan letih."},{"id":"5","ar":"وَلَقَدْ زَيَّنَّا السَّمَاۤءَ الدُّنْيَا بِمَصَابِيْحَ وَجَعَلْنٰهَا رُجُوْمًا لِّلشَّيٰطِيْنِ وَاَعْتَدْنَا لَهُمْ عَذَابَ السَّعِيْرِ ","bc":"wa laqad zayyannas-samā`ad-dun-yā bimaṣābīḥa wa ja'alnāhā rujụmal lisy-syayāṭīni wa a'tadnā lahum 'ażābas-sa'īr","at":"Dan sungguh, telah Kami hiasi langit yang dekat, dengan bintang-bintang dan Kami jadikannya (bintang-bintang itu) sebagai alat-alat pelempar setan, dan Kami sediakan bagi mereka azab neraka yang menyala-nyala."},{"id":"6","ar":"وَلِلَّذِيْنَ كَفَرُوْا بِرَبِّهِمْ عَذَابُ جَهَنَّمَۗ وَبِئْسَ الْمَصِيْرُ","bc":"wa lillażīna kafarụ birabbihim 'ażābu jahannam, wa bi`sal-maṣīr","at":"Dan orang-orang yang ingkar kepada Tuhannya akan mendapat azab Jahanam. Dan itulah seburuk-buruk tempat kembali."},{"id":"7","ar":"اِذَآ اُلْقُوْا فِيْهَا سَمِعُوْا لَهَا شَهِيْقًا وَّهِيَ تَفُوْرُۙ","bc":"iżā ulqụ fīhā sami'ụ lahā syahīqaw wa hiya tafụr","at":"Apabila mereka dilemparkan ke dalamnya mereka mendengar suara neraka yang mengerikan, sedang neraka itu membara,"},{"id":"8","ar":"تَكَادُ تَمَيَّزُ مِنَ الْغَيْظِۗ كُلَّمَآ اُلْقِيَ فِيْهَا فَوْجٌ سَاَلَهُمْ خَزَنَتُهَآ اَلَمْ يَأْتِكُمْ نَذِيْرٌۙ","bc":"takādu tamayyazu minal-gaīẓ, kullamā ulqiya fīhā faujun sa`alahum khazanatuhā a lam ya`tikum nażīr","at":"hampir meledak karena marah. Setiap kali ada sekumpulan (orang-orang kafir) dilemparkan ke dalamnya, penjaga-penjaga (neraka itu) bertanya kepada mereka, “Apakah belum pernah ada orang yang datang memberi peringatan kepadamu (di dunia)?”"},{"id":"9","ar":"قَالُوْا بَلٰى قَدْ جَاۤءَنَا نَذِيْرٌ ەۙ فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ اللّٰهُ مِنْ شَيْءٍۖ اِنْ اَنْتُمْ اِلَّا فِيْ ضَلٰلٍ كَبِيْرٍ","bc":"qālụ balā qad jā`anā nażīrun fa każżabnā wa qulnā mā nazzalallāhu min syai`in in antum illā fī ḍalāling kabīr","at":"Mereka menjawab, “Benar, sungguh, seorang pemberi peringatan telah datang kepada kami, tetapi kami mendustakan(nya) dan kami katakan, “Allah tidak menurunkan sesuatu apa pun, kamu sebenarnya di dalam kesesatan yang besar.”"},{"id":"10","ar":"وَقَالُوْا لَوْ كُنَّا نَسْمَعُ اَوْ نَعْقِلُ مَا كُنَّا فِيْٓ اَصْحٰبِ السَّعِيْرِ","bc":"wa qālụ lau kunnā nasma'u au na'qilu mā kunnā fī aṣ-ḥābis-sa'īr","at":"Dan mereka berkata, “Sekiranya (dahulu) kami mendengarkan atau memikirkan (peringatan itu) tentulah kami tidak termasuk penghuni neraka yang menyala-nyala.”"},{"id":"11","ar":"فَاعْتَرَفُوْا بِذَنْۢبِهِمْۚ فَسُحْقًا لِّاَصْحٰبِ السَّعِيْرِ ","bc":"fa'tarafụ biżambihim, fa suḥqal li`aṣ-ḥābis-sa'īr","at":"Maka mereka mengakui dosanya. Tetapi jauhlah (dari rahmat Allah) bagi penghuni neraka yang menyala-nyala itu."},{"id":"12","ar":"اِنَّ الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ كَبِيْرٌ ","bc":"innallażīna yakhsyauna rabbahum bil-gaibi lahum magfiratuw wa ajrung kabīr","at":"Sesungguhnya orang-orang yang takut kepada Tuhannya yang tidak terlihat oleh mereka, mereka memperoleh ampunan dan pahala yang besar. "},{"id":"13","ar":"وَاَسِرُّوْا قَوْلَكُمْ اَوِ اجْهَرُوْا بِهٖۗ اِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ ","bc":"wa asirrụ qaulakum awij-harụ bih, innahụ 'alīmum biżātiṣ-ṣudụr","at":"Dan rahasiakanlah perkataanmu atau nyatakanlah. Sungguh, Dia Maha Mengetahui segala isi hati. "},{"id":"14","ar":"اَلَا يَعْلَمُ مَنْ خَلَقَۗ وَهُوَ اللَّطِيْفُ الْخَبِيْرُ ","bc":"alā ya'lamu man khalaq, wa huwal-laṭīful-khabīr","at":"Apakah (pantas) Allah yang menciptakan itu tidak mengetahui? Dan Dia Mahahalus, Maha Mengetahui."},{"id":"15","ar":"هُوَ الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ ذَلُوْلًا فَامْشُوْا فِيْ مَنَاكِبِهَا وَكُلُوْا مِنْ رِّزْقِهٖۗ وَاِلَيْهِ النُّشُوْرُ ","bc":"huwallażī ja'ala lakumul-arḍa żalụlan famsyụ fī manākibihā wa kulụ mir rizqih, wa ilaihin-nusyụr","at":"Dialah yang menjadikan bumi untuk kamu yang mudah dijelajahi, maka jelajahilah di segala penjurunya dan makanlah sebagian dari rezeki-Nya. Dan hanya kepada-Nyalah kamu (kembali setelah) dibangkitkan. "},{"id":"16","ar":"ءَاَمِنْتُمْ مَّنْ فِى السَّمَاۤءِ اَنْ يَّخْسِفَ بِكُمُ الْاَرْضَ فَاِذَا هِيَ تَمُوْرُۙ","bc":"a amintum man fis-samā`i ay yakhsifa bikumul-arḍa fa iżā hiya tamụr","at":"Sudah merasa amankah kamu, bahwa Dia yang di langit tidak akan membuat kamu ditelan bumi ketika tiba-tiba ia terguncang?"},{"id":"17","ar":"اَمْ اَمِنْتُمْ مَّنْ فِى السَّمَاۤءِ اَنْ يُّرْسِلَ عَلَيْكُمْ حَاصِبًاۗ فَسَتَعْلَمُوْنَ كَيْفَ نَذِيْرِ ","bc":"am amintum man fis-samā`i ay yursila 'alaikum ḥāṣibā, fa sata'lamụna kaifa nażīr","at":"Atau sudah merasa amankah kamu, bahwa Dia yang di langit tidak akan mengirimkan badai yang berbatu kepadamu? Namun kelak kamu akan mengetahui bagaimana (akibat mendustakan) peringatan-Ku."},{"id":"18","ar":"وَلَقَدْ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَكَيْفَ كَانَ نَكِيْرِ ","bc":"wa laqad każżaballażīna ming qablihim fa kaifa kāna nakīr","at":"Dan sungguh, orang-orang yang sebelum mereka pun telah mendustakan (rasul-rasul-Nya). Maka betapa hebatnya kemurkaan-Ku! "},{"id":"19","ar":"اَوَلَمْ يَرَوْا اِلَى الطَّيْرِ فَوْقَهُمْ صٰۤفّٰتٍ وَّيَقْبِضْنَۘ مَا يُمْسِكُهُنَّ اِلَّا الرَّحْمٰنُۗ اِنَّهٗ بِكُلِّ شَيْءٍۢ بَصِيْرٌ ","bc":"a wa lam yarau ilaṭ-ṭairi fauqahum ṣāffātiw wa yaqbiḍn, mā yumsikuhunna illar-raḥmān, innahụ bikulli syai`im baṣīr","at":"Tidakkah mereka memperhatikan burung-burung yang mengembangkan dan mengatupkan sayapnya di atas mereka? Tidak ada yang menahannya (di udara) selain Yang Maha Pengasih. Sungguh, Dia Maha Melihat segala sesuatu. "},{"id":"20","ar":"اَمَّنْ هٰذَا الَّذِيْ هُوَ جُنْدٌ لَّكُمْ يَنْصُرُكُمْ مِّنْ دُوْنِ الرَّحْمٰنِۗ اِنِ الْكٰفِرُوْنَ اِلَّا فِيْ غُرُوْرٍۚ ","bc":"am man hāżallażī huwa jundul lakum yanṣurukum min dụnir-raḥmān, inil-kāfirụna illā fī gurụr","at":"Atau siapakah yang akan menjadi bala tentara bagimu yang dapat membelamu selain (Allah) Yang Maha Pengasih? Orang-orang kafir itu hanyalah dalam (keadaan) tertipu. "},{"id":"21","ar":"اَمَّنْ هٰذَا الَّذِيْ يَرْزُقُكُمْ اِنْ اَمْسَكَ رِزْقَهٗ ۚ بَلْ لَّجُّوْا فِيْ عُتُوٍّ وَّنُفُوْرٍ ","bc":"am man hāżallażī yarzuqukum in amsaka rizqah, bal lajjụ fī 'utuwwiw wa nufụr","at":"Atau siapakah yang dapat memberimu rezeki jika Dia menahan rezeki-Nya? Bahkan mereka terus-menerus dalam kesombongan dan menjauhkan diri (dari kebenaran)."},{"id":"22","ar":"اَفَمَنْ يَّمْشِيْ مُكِبًّا عَلٰى وَجْهِهٖٓ اَهْدٰىٓ اَمَّنْ يَّمْشِيْ سَوِيًّا عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"a fa may yamsyī mukibban 'alā waj-hihī ahdā am may yamsyī sawiyyan 'alā ṣirāṭim mustaqīm","at":"Apakah orang yang merangkak dengan wajah tertelungkup yang lebih terpimpin (dalam kebenaran) ataukah orang yang berjalan tegap di atas jalan yang lurus?"},{"id":"23","ar":"قُلْ هُوَ الَّذِيْٓ اَنْشَاَكُمْ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ ","bc":"qul huwallażī ansya`akum wa ja'ala lakumus-sam'a wal-abṣāra wal-af`idah, qalīlam mā tasykurụn","at":"Katakanlah, “Dialah yang menciptakan kamu dan menjadikan pendengaran, penglihatan dan hati nurani bagi kamu. (Tetapi) sedikit sekali kamu bersyukur.”"},{"id":"24","ar":"قُلْ هُوَ الَّذِيْ ذَرَاَكُمْ فِى الْاَرْضِ وَاِلَيْهِ تُحْشَرُوْنَ ","bc":"qul huwallażī żara`akum fil-arḍi wa ilaihi tuḥsyarụn","at":"Katakanlah, “Dialah yang menjadikan kamu berkembang biak di muka bumi, dan hanya kepada-Nya kamu akan dikumpulkan.” "},{"id":"25","ar":"وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"wa yaqụlụna matā hāżal-wa'du ing kuntum ṣādiqīn","at":"Dan mereka berkata, “Kapan (datangnya) ancaman itu jika kamu orang yang benar?”"},{"id":"26","ar":"قُلْ اِنَّمَا الْعِلْمُ عِنْدَ اللّٰهِ ۖوَاِنَّمَآ اَنَا۠ نَذِيْرٌ مُّبِيْنٌ ","bc":"qul innamal-'ilmu 'indallāhi wa innamā ana nażīrum mubīn","at":"Katakanlah (Muhammad), “Sesungguhnya ilmu (tentang hari Kiamat itu) hanya ada pada Allah. Dan aku hanyalah seorang pemberi peringatan yang menjelaskan.” "},{"id":"27","ar":"فَلَمَّا رَاَوْهُ زُلْفَةً سِيْۤـَٔتْ وُجُوْهُ الَّذِيْنَ كَفَرُوْا وَقِيْلَ هٰذَا الَّذِيْ كُنْتُمْ بِهٖ تَدَّعُوْنَ ","bc":"fa lammā ra`auhu zulfatan sī`at wujụhullażīna kafarụ wa qīla hāżallażī kuntum bihī tadda'ụn","at":"Maka ketika mereka melihat azab (pada hari Kiamat) sudah dekat, wajah orang-orang kafir itu menjadi muram. Dan dikatakan (kepada mereka), “Inilah (azab) yang dahulunya kamu minta.” "},{"id":"28","ar":"قُلْ اَرَءَيْتُمْ اِنْ اَهْلَكَنِيَ اللّٰهُ وَمَنْ مَّعِيَ اَوْ رَحِمَنَاۙ فَمَنْ يُّجِيْرُ الْكٰفِرِيْنَ مِنْ عَذَابٍ اَلِيْمٍ ","bc":"qul ara`aitum in ahlakaniyallāhu wa mam ma'iya au raḥimanā fa may yujīrul-kāfirīna min 'ażābin alīm","at":"Katakanlah (Muhammad), “Tahukah kamu jika Allah mematikan aku dan orang-orang yang bersamaku atau memberi rahmat kepada kami, (maka kami akan masuk surga), lalu siapa yang dapat melindungi orang-orang kafir dari azab yang pedih?”"},{"id":"29","ar":"قُلْ هُوَ الرَّحْمٰنُ اٰمَنَّا بِهٖ وَعَلَيْهِ تَوَكَّلْنَاۚ فَسَتَعْلَمُوْنَ مَنْ هُوَ فِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"qul huwar-raḥmānu āmannā bihī wa 'alaihi tawakkalnā, fa sata'lamụna man huwa fī ḍalālim mubīn","at":"Katakanlah, “Dialah Yang Maha Pengasih, kami beriman kepada-Nya dan kepada-Nya kami bertawakal. Maka kelak kamu akan tahu siapa yang berada dalam kesesatan yang nyata.” "},{"id":"30","ar":"قُلْ اَرَءَيْتُمْ اِنْ اَصْبَحَ مَاۤؤُكُمْ غَوْرًا فَمَنْ يَّأْتِيْكُمْ بِمَاۤءٍ مَّعِيْنٍ ","bc":"qul ara`aitum in aṣbaḥa mā`ukum gauran fa may ya`tīkum bimā`im ma'īn","at":"Katakanlah (Muhammad), “Terangkanlah kepadaku jika sumber air kamu menjadi kering; maka siapa yang akan memberimu air yang mengalir?”"}],al_muminun=[{"id":"1","ar":" قَدْ اَفْلَحَ الْمُؤْمِنُوْنَ ۙ","bc":"qad aflaḥal-mu`minụn","at":"Sungguh beruntung orang-orang yang beriman,"},{"id":"2","ar":" الَّذِيْنَ هُمْ فِيْ صَلٰو تِهِمْ خَاشِعُوْنَ","bc":"allażīna hum fī ṣalātihim khāsyi'ụn","at":"(yaitu) orang yang khusyuk dalam salatnya,"},{"id":"3","ar":"وَالَّذِيْنَ هُمْ عَنِ اللَّغْوِ مُعْرِضُوْنَ ۙ","bc":"wallażīna hum 'anil-lagwi mu'riḍụn","at":"dan orang yang menjauhkan diri dari (perbuatan dan perkataan) yang tidak berguna,"},{"id":"4","ar":"وَالَّذِيْنَ هُمْ لِلزَّكٰوةِ فَاعِلُوْنَ ۙ","bc":"wallażīna hum liz-zakāti fā'ilụn","at":"dan orang yang menunaikan zakat,"},{"id":"5","ar":" وَالَّذِيْنَ هُمْ لِفُرُوْجِهِمْ حٰفِظُوْنَ ۙ","bc":"wallażīna hum lifurụjihim ḥāfiẓụn","at":"dan orang yang memelihara kemaluannya,"},{"id":"6","ar":"اِلَّا عَلٰٓى اَزْوَاجِهِمْ اَوْ مَا مَلَكَتْ اَيْمَانُهُمْ فَاِنَّهُمْ غَيْرُ مَلُوْمِيْنَۚ","bc":"illā 'alā azwājihim au mā malakat aimānuhum fa innahum gairu malụmīn","at":"kecuali terhadap istri-istri mereka atau hamba sahaya yang mereka miliki; maka sesungguhnya mereka tidak tercela."},{"id":"7","ar":"فَمَنِ ابْتَغٰى وَرَاۤءَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْعٰدُوْنَ ۚ","bc":"fa manibtagā warā`a żālika fa ulā`ika humul-'ādụn","at":"Tetapi barang siapa mencari di balik itu (zina, dan sebagainya), maka mereka itulah orang-orang yang melampaui batas."},{"id":"8","ar":"وَالَّذِيْنَ هُمْ لِاَمٰنٰتِهِمْ وَعَهْدِهِمْ رَاعُوْنَ ۙ","bc":"wallażīna hum li`amānātihim wa 'ahdihim rā'ụn","at":"Dan (sungguh beruntung) orang yang memelihara amanat-amanat dan janjinya,"},{"id":"9","ar":"وَالَّذِيْنَ هُمْ عَلٰى صَلَوٰتِهِمْ يُحَافِظُوْنَ ۘ","bc":"wallażīna hum 'alā ṣalawātihim yuḥāfiẓụn","at":"serta orang yang memelihara salatnya."},{"id":"10","ar":"اُولٰۤىِٕكَ هُمُ الْوَارِثُوْنَ ۙ","bc":"ulā`ika humul-wāriṡụn","at":"Mereka itulah orang yang akan mewarisi,"},{"id":"11","ar":"الَّذِيْنَ يَرِثُوْنَ الْفِرْدَوْسَۗ هُمْ فِيْهَا خٰلِدُوْنَ ","bc":"allażīna yariṡụnal-firdaụs, hum fīhā khālidụn","at":"(yakni) yang akan mewarisi (surga) Firdaus. Mereka kekal di dalamnya."},{"id":"12","ar":"وَلَقَدْ خَلَقْنَا الْاِنْسَانَ مِنْ سُلٰلَةٍ مِّنْ طِيْنٍ ۚ","bc":"wa laqad khalaqnal-insāna min sulālatim min ṭīn","at":"Dan sungguh, Kami telah menciptakan manusia dari saripati (berasal) dari tanah."},{"id":"13","ar":"ثُمَّ جَعَلْنٰهُ نُطْفَةً فِيْ قَرَارٍ مَّكِيْنٍ ۖ","bc":"ṡumma ja'alnāhu nuṭfatan fī qarārim makīn","at":"Kemudian Kami menjadikannya air mani (yang disimpan) dalam tempat yang kokoh (rahim)."},{"id":"14","ar":"ثُمَّ خَلَقْنَا النُّطْفَةَ عَلَقَةً فَخَلَقْنَا الْعَلَقَةَ مُضْغَةً فَخَلَقْنَا الْمُضْغَةَ عِظٰمًا فَكَسَوْنَا الْعِظٰمَ لَحْمًا ثُمَّ اَنْشَأْنٰهُ خَلْقًا اٰخَرَۗ فَتَبَارَكَ اللّٰهُ اَحْسَنُ الْخَالِقِيْنَۗ ","bc":"ṡumma khalaqnan-nuṭfata 'alaqatan fa khalaqnal-'alaqata muḍgatan fa khalaqnal-muḍgata 'iẓāman fa kasaunal-'iẓāma laḥman ṡumma ansya`nāhu khalqan ākhar, fa tabārakallāhu aḥsanul-khāliqīn","at":"Kemudian, air mani itu Kami jadikan sesuatu yang melekat, lalu sesuatu yang melekat itu Kami jadikan segumpal daging, dan segumpal daging itu Kami jadikan tulang belulang, lalu tulang belulang itu Kami bungkus dengan daging. Kemudian, Kami menjadikannya makhluk yang (berbentuk) lain. Mahasuci Allah, Pencipta yang paling baik."},{"id":"15","ar":" ثُمَّ اِنَّكُمْ بَعْدَ ذٰلِكَ لَمَيِّتُوْنَ ۗ","bc":"ṡumma innakum ba'da żālika lamayyitụn","at":"Kemudian setelah itu, sesungguhnya kamu pasti mati."},{"id":"16","ar":"ثُمَّ اِنَّكُمْ يَوْمَ الْقِيٰمَةِ تُبْعَثُوْنَ","bc":"ṡumma innakum yaumal-qiyāmati tub'aṡụn","at":"Kemudian, sesungguhnya kamu akan dibangkitkan (dari kuburmu) pada hari Kiamat."},{"id":"17","ar":"وَلَقَدْ خَلَقْنَا فَوْقَكُمْ سَبْعَ طَرَاۤىِٕقَۖ وَمَا كُنَّا عَنِ الْخَلْقِ غٰفِلِيْنَ","bc":"wa laqad khalaqnā fauqakum sab'a ṭarā`iqa wa mā kunnā 'anil-khalqi gāfilīn","at":"Dan sungguh, Kami telah menciptakan tujuh (lapis) langit di atas kamu, dan Kami tidaklah lengah terhadap ciptaan (Kami)."},{"id":"18","ar":"وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءًۢ بِقَدَرٍ فَاَسْكَنّٰهُ فِى الْاَرْضِۖ وَاِنَّا عَلٰى ذَهَابٍۢ بِهٖ لَقٰدِرُوْنَ ۚ","bc":"wa anzalnā minas-samā`i mā`am biqadarin fa askannāhu fil-arḍi wa innā 'alā żahābim bihī laqādirụn","at":"Dan Kami turunkan air dari langit dengan suatu ukuran; lalu Kami jadikan air itu menetap di bumi, dan pasti Kami berkuasa melenyapkannya. "},{"id":"19","ar":"فَاَنْشَأْنَا لَكُمْ بِهٖ جَنّٰتٍ مِّنْ نَّخِيْلٍ وَّاَعْنَابٍۘ لَكُمْ فِيْهَا فَوَاكِهُ كَثِيْرَةٌ وَّمِنْهَا تَأْكُلُوْنَ ۙ","bc":"fa ansya`nā lakum bihī jannātim min nakhīliw wa a'nāb, lakum fīhā fawākihu kaṡīratuw wa min-hā ta`kulụn","at":"Lalu dengan (air) itu, Kami tumbuhkan untukmu kebun-kebun kurma dan anggur; di sana kamu memperoleh buah-buahan yang banyak dan sebagian dari (buah-buahan) itu kamu makan, "},{"id":"20","ar":"وَشَجَرَةً تَخْرُجُ مِنْ طُوْرِ سَيْنَاۤءَ تَنْۢبُتُ بِالدُّهْنِ وَصِبْغٍ لِّلْاٰكِلِيْنَ ","bc":"wa syajaratan takhruju min ṭụri sainā`a tambutu bid-duhni wa ṣibgil lil-ākilīn","at":"dan (Kami tumbuhkan) pohon (zaitun) yang tumbuh dari gunung Sinai, yang menghasilkan minyak, dan bahan pembangkit selera bagi orang-orang yang makan. "},{"id":"21","ar":"وَاِنَّ لَكُمْ فِى الْاَنْعَامِ لَعِبْرَةًۗ نُسْقِيْكُمْ مِّمَّا فِيْ بُطُوْنِهَا وَلَكُمْ فِيْهَا مَنَافِعُ كَثِيْرَةٌ وَّمِنْهَا تَأْكُلُوْنَ ۙ ","bc":"wa inna lakum fil-an'āmi la'ibrah, nusqīkum mimmā fī buṭụnihā wa lakum fīhā manāfi'u kaṡīratuw wa min-hā ta`kulụn","at":"Dan sesungguhnya pada hewan-hewan ternak terdapat suatu pelajaran bagimu. Kami memberi minum kamu dari (air susu) yang ada dalam perutnya, dan padanya juga terdapat banyak manfaat untukmu, dan sebagian darinya kamu makan,"},{"id":"22","ar":"وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُوْنَ","bc":"wa 'alaihā wa 'alal-fulki tuḥmalụn","at":"di atasnya (hewan-hewan ternak) dan di atas kapal-kapal kamu diangkut."},{"id":"23","ar":"وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖ فَقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ ","bc":"wa laqad arsalnā nụḥan ilā qaumihī fa qāla yā qaumi'budullāha mā lakum min ilāhin gairuh, a fa lā tattaqụn","at":"Dan sungguh, Kami telah mengutus Nuh kepada kaumnya, lalu dia berkata, “Wahai kaumku! Sembahlah Allah, (karena) tidak ada tuhan (yang berhak disembah) bagimu selain Dia. Maka mengapa kamu tidak bertakwa (kepada-Nya)?”"},{"id":"24","ar":"فَقَالَ الْمَلَؤُا الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ مَا هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۙ يُرِيْدُ اَنْ يَّتَفَضَّلَ عَلَيْكُمْۗ وَلَوْ شَاۤءَ اللّٰهُ لَاَنْزَلَ مَلٰۤىِٕكَةً ۖمَّا سَمِعْنَا بِهٰذَا فِيْٓ اٰبَاۤىِٕنَا الْاَوَّلِيْنَ ۚ","bc":"fa qālal-mala`ullażīna kafarụ ming qaumihī mā hāżā illā basyarum miṡlukum yurīdu ay yatafaḍḍala 'alaikum, walau syā`allāhu la`anzala malā`ikatam mā sami'nā bihāżā fī ābā`inal-awwalīn","at":"Maka berkatalah para pemuka orang kafir dari kaumnya, “Orang ini tidak lain hanyalah manusia seperti kamu, yang ingin menjadi orang yang lebih mulia daripada kamu. Dan seandainya Allah menghendaki, tentu Dia mengutus malaikat. Belum pernah kami mendengar (seruan yang seperti) ini pada (masa) nenek moyang kami dahulu."},{"id":"25","ar":"اِنْ هُوَ اِلَّا رَجُلٌۢ بِهٖ جِنَّةٌ فَتَرَبَّصُوْا بِهٖ حَتّٰى حِيْنٍ","bc":"in huwa illā rajulum bihī jinnatun fa tarabbaṣụ bihī ḥattā ḥīn","at":"Dia hanyalah seorang laki-laki yang gila, maka tunggulah (sabarlah) terhadapnya sampai waktu yang ditentukan.”"},{"id":"26","ar":"قَالَ رَبِّ انْصُرْنِيْ بِمَا كَذَّبُوْنِ ","bc":"qāla rabbinṣurnī bimā każżabụn","at":"Dia (Nuh) berdoa, “Ya Tuhanku, tolonglah aku karena mereka mendustakan aku.”"},{"id":"27","ar":"فَاَوْحَيْنَآ اِلَيْهِ اَنِ اصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا فَاِذَا جَاۤءَ اَمْرُنَا وَفَارَ التَّنُّوْرُۙ فَاسْلُكْ فِيْهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ مِنْهُمْۚ وَلَا تُخَاطِبْنِيْ فِى الَّذِيْنَ ظَلَمُوْاۚ اِنَّهُمْ مُّغْرَقُوْنَ","bc":"fa auḥainā ilaihi aniṣna'il-fulka bi`a'yuninā wa waḥyinā fa iżā jā`a amrunā wa fārat-tannụru fasluk fīhā ming kullin zaujainiṡnaini wa ahlaka illā man sabaqa 'alaihil-qaulu min-hum, wa lā tukhāṭibnī fillażīna ẓalamụ, innahum mugraqụn","at":"Lalu Kami wahyukan kepadanya, “Buatlah kapal di bawah pengawasan dan petunjuk Kami, maka apabila perintah Kami datang dan tanur (dapur) telah memancarkan air, maka masukkanlah ke dalam (kapal) itu sepasang-sepasang dari setiap jenis, juga keluargamu, kecuali orang yang lebih dahulu ditetapkan (akan ditimpa siksaan) di antara mereka. Dan janganlah engkau bicarakan dengan-Ku tentang orang-orang yang zalim, sesungguhnya mereka itu akan ditenggelamkan."},{"id":"28","ar":"فَاِذَا اسْتَوَيْتَ اَنْتَ وَمَنْ مَّعَكَ عَلَى الْفُلْكِ فَقُلِ الْحَمْدُ لِلّٰهِ الَّذِيْ نَجّٰىنَا مِنَ الْقَوْمِ الظّٰلِمِيْنَ","bc":"fa iżastawaita anta wa mam ma'aka 'alal-fulki fa qulil-ḥamdu lillāhillażī najjānā minal-qaumiẓ-ẓālimīn","at":"Dan apabila engkau dan orang-orang yang bersamamu telah berada di atas kapal, maka ucapkanlah, “Segala puji bagi Allah yang telah menyelamatkan kami dari orang-orang yang zalim.”"},{"id":"29","ar":"وَقُلْ رَّبِّ اَنْزِلْنِيْ مُنْزَلًا مُّبٰرَكًا وَّاَنْتَ خَيْرُ الْمُنْزِلِيْنَ","bc":"wa qur rabbi anzilnī munzalam mubārakaw wa anta khairul-munzilīn","at":"Dan berdoalah, “Ya Tuhanku, tempatkanlah aku pada tempat yang diberkahi, dan Engkau adalah sebaik-baik pemberi tempat.”"},{"id":"30","ar":"اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ وَّاِنْ كُنَّا لَمُبْتَلِيْنَ","bc":"inna fī żālika la`āyātiw wa ing kunnā lamubtalīn","at":"Sungguh, pada (kejadian) itu benar-benar terdapat tanda-tanda (kebesaran Allah); dan sesungguhnya Kami benar-benar menimpakan siksaan (kepada kaum Nuh itu)."},{"id":"31","ar":"ثُمَّ اَنْشَأْنَا مِنْۢ بَعْدِهِمْ قَرْنًا اٰخَرِيْنَ ۚ","bc":"ṡumma ansya`nā mim ba'dihim qarnan ākharīn","at":"Kemudian setelah mereka, Kami ciptakan umat yang lain (kaum ‘Ad)."},{"id":"32","ar":"فَاَرْسَلْنَا فِيْهِمْ رَسُوْلًا مِّنْهُمْ اَنِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗۗ اَفَلَا تَتَّقُوْنَ ","bc":"fa arsalnā fīhim rasụlam min-hum ani'budullāha mā lakum min ilāhin gairuh, a fa lā tattaqụn","at":"Lalu Kami utus kepada mereka, seorang rasul dari kalangan mereka sendiri (yang berkata), “Sembahlah Allah! Tidak ada tuhan (yang berhak disembah) bagimu selain Dia. Maka mengapa kamu tidak bertakwa (kepada-Nya)?”"},{"id":"33","ar":"وَقَالَ الْمَلَاُ مِنْ قَوْمِهِ الَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِلِقَاۤءِ الْاٰخِرَةِ وَاَتْرَفْنٰهُمْ فِى الْحَيٰوةِ الدُّنْيَاۙ مَا هٰذَآ اِلَّا بَشَرٌ مِّثْلُكُمْۙ يَأْكُلُ مِمَّا تَأْكُلُوْنَ مِنْهُ وَيَشْرَبُ مِمَّا تَشْرَبُوْنَ ","bc":"wa qālal-mala`u ming qaumihillażīna kafarụ wa każżabụ biliqā`il-ākhirati wa atrafnāhum fil-ḥayātid-dun-yā mā hāżā illā basyarum miṡlukum ya`kulu mimmā ta`kulụna min-hu wa yasyrabu mimmā tasyrabụn","at":"Dan berkatalah para pemuka orang kafir dari kaumnya dan yang mendustakan pertemuan hari akhirat serta mereka yang telah Kami beri kemewahan dan kesenangan dalam kehidupan di dunia, “(Orang) ini tidak lain hanyalah manusia seperti kamu, dia makan apa yang kamu makan, dan dia minum apa yang kamu minum.”"},{"id":"34","ar":"وَلَىِٕنْ اَطَعْتُمْ بَشَرًا مِّثْلَكُمْ اِنَّكُمْ اِذًا لَّخٰسِرُوْنَ ۙ","bc":"wa la`in aṭa'tum basyaram miṡlakum innakum iżal lakhāsirụn","at":"Dan sungguh, jika kamu menaati manusia seperti kamu, niscaya kamu pasti rugi."},{"id":"35","ar":"اَيَعِدُكُمْ اَنَّكُمْ اِذَا مِتُّمْ وَكُنْتُمْ تُرَابًا وَّعِظَامًا اَنَّكُمْ مُّخْرَجُوْنَ ۖ ","bc":"a ya'idukum annakum iżā mittum wa kuntum turābaw wa 'iẓāman annakum mukhrajụn","at":"Adakah dia menjanjikan kepada kamu, bahwa apabila kamu telah mati dan menjadi tanah dan tulang belulang, sesungguhnya kamu akan dikeluarkan (dari kuburmu)?"},{"id":"36","ar":"۞ هَيْهَاتَ هَيْهَاتَ لِمَا تُوْعَدُوْنَ ۖ ","bc":"haihāta haihāta limā tụ'adụn","at":"Jauh! Jauh sekali (dari kebenaran) apa yang diancamkan kepada kamu, "},{"id":"37","ar":"اِنْ هِيَ اِلَّا حَيَاتُنَا الدُّنْيَا نَمُوْتُ وَنَحْيَا وَمَا نَحْنُ بِمَبْعُوْثِيْنَ ۖ ","bc":"in hiya illā ḥayātunad-dun-yā namụtu wa naḥyā wa mā naḥnu bimab'ụṡīn","at":"(kehidupan itu) tidak lain hanyalah kehidupan kita di dunia ini, (di sanalah) kita mati dan hidup dan tidak akan dibangkitkan (lagi),"},{"id":"38","ar":"اِنْ هُوَ اِلَّا رَجُلُ ِۨافْتَرٰى عَلَى اللّٰهِ كَذِبًا وَّمَا نَحْنُ لَهٗ بِمُؤْمِنِيْنَ","bc":"in huwa illā rajuluniftarā 'alallāhi każibaw wa mā naḥnu lahụ bimu`minīn","at":"Dia tidak lain hanyalah seorang laki-laki yang mengada-adakan kebohongan terhadap Allah, dan kita tidak akan mempercayainya."},{"id":"39","ar":"قَالَ رَبِّ انْصُرْنِيْ بِمَا كَذَّبُوْنِ","bc":"qāla rabbinṣurnī bimā każżabụn","at":"Dia (Hud) berdoa, “Ya Tuhanku, tolonglah aku karena mereka mendustakan aku.”"},{"id":"40","ar":"قَالَ عَمَّا قَلِيْلٍ لَّيُصْبِحُنَّ نٰدِمِيْنَ ۚ ","bc":"qāla 'ammā qalīlil layuṣbiḥunna nādimīn","at":"Dia (Allah) berfirman, “Tidak lama lagi mereka pasti akan menyesal.”"},{"id":"41","ar":"فَاَخَذَتْهُمُ الصَّيْحَةُ بِالْحَقِّ فَجَعَلْنٰهُمْ غُثَاۤءًۚ فَبُعْدًا لِّلْقَوْمِ الظّٰلِمِيْنَ","bc":"fa akhażat-humuṣ-ṣaiḥatu bil-ḥaqqi fa ja'alnāhum guṡā`ā, fa bu'dal lil-qaumiẓ-ẓālimīn","at":"Lalu mereka benar-benar dimusnahkan oleh suara yang mengguntur, dan Kami jadikan mereka (seperti) sampah yang dibawa banjir. Maka binasalah bagi orang-orang yang zalim. "},{"id":"42","ar":"ثُمَّ اَنْشَأْنَا مِنْۢ بَعْدِهِمْ قُرُوْنًا اٰخَرِيْنَ ۗ","bc":"ṡumma ansya`nā mim ba'dihim qurụnan ākharīn","at":"Kemudian setelah mereka Kami ciptakan umat-umat yang lain."},{"id":"43","ar":"مَا تَسْبِقُ مِنْ اُمَّةٍ اَجَلَهَا وَمَا يَسْتَأْخِرُوْنَ ۗ","bc":"mā tasbiqu min ummatin ajalahā wa mā yasta`khirụn","at":"Tidak ada satu umat pun yang dapat menyegerakan ajalnya, dan tidak (pula) menangguhkannya."},{"id":"44","ar":"ثُمَّ اَرْسَلْنَا رُسُلَنَا تَتْرَاۗ كُلَّمَا جَاۤءَ اُمَّةً رَّسُوْلُهَا كَذَّبُوْهُ فَاَتْبَعْنَا بَعْضَهُمْ بَعْضًا وَّجَعَلْنٰهُمْ اَحَادِيْثَۚ فَبُعْدًا لِّقَوْمٍ لَّا يُؤْمِنُوْنَ ","bc":"ṡumma arsalnā rusulanā tatrā, kullamā jā`a ummatar rasụluhā każżabụhu fa atba'nā ba'ḍahum ba'ḍaw wa ja'alnāhum aḥādīṡ, fa bu'dal liqaumil lā yu`minụn","at":"Kemudian, Kami utus rasul-rasul Kami berturut-turut. Setiap kali seorang rasul datang kepada suatu umat, mereka mendustakannya, maka Kami silihgantikan sebagian mereka dengan sebagian yang lain (dalam kebinasaan). Dan Kami jadikan mereka bahan cerita (bagi manusia). Maka kebinasaanlah bagi kaum yang tidak beriman."},{"id":"45","ar":"ثُمَّ اَرْسَلْنَا مُوْسٰى وَاَخَاهُ هٰرُوْنَ ەۙ بِاٰيٰتِنَا وَسُلْطٰنٍ مُّبِيْنٍۙ","bc":"ṡumma arsalnā mụsā wa akhāhu hārụna bi`āyātinā wa sulṭānim mubīn","at":"Kemudian Kami utus Musa dan saudaranya Harun dengan membawa tanda-tanda (kebesaran) Kami, dan bukti yang nyata,"},{"id":"46","ar":"اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا عَالِيْنَ ۚ","bc":"ilā fir'auna wa malā`ihī fastakbarụ wa kānụ qauman 'ālīn","at":"kepada Fir‘aun dan para pemuka kaumnya, tetapi mereka angkuh dan mereka memang kaum yang sombong."},{"id":"47","ar":"فَقَالُوْٓا اَنُؤْمِنُ لِبَشَرَيْنِ مِثْلِنَا وَقَوْمُهُمَا لَنَا عٰبِدُوْنَ ۚ","bc":"fa qālū a nu`minu libasyaraini miṡlinā wa qaumuhumā lanā 'ābidụn","at":"Maka mereka berkata, “Apakah (pantas) kita percaya kepada dua orang manusia seperti kita, padahal kaum mereka (Bani Israil) adalah orang-orang yang menghambakan diri kepada kita?”"},{"id":"48","ar":"فَكَذَّبُوْهُمَا فَكَانُوْا مِنَ الْمُهْلَكِيْنَ ","bc":"fa każżabụhumā fa kānụ minal-muhlakīn","at":"Maka mereka mendustakan keduanya, sebab itu mereka termasuk orang yang dibinasakan."},{"id":"49","ar":"وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ لَعَلَّهُمْ يَهْتَدُوْنَ ","bc":"wa laqad ātainā mụsal-kitāba la'allahum yahtadụn","at":"Dan sungguh, telah Kami anugerahi kepada Musa Kitab (Taurat), agar mereka (Bani Israil) mendapat petunjuk."},{"id":"50","ar":"وَجَعَلْنَا ابْنَ مَرْيَمَ وَاُمَّهٗٓ اٰيَةً وَّاٰوَيْنٰهُمَآ اِلٰى رَبْوَةٍ ذَاتِ قَرَارٍ وَّمَعِيْنٍ","bc":"wa ja'alnabna maryama wa ummahū āyataw wa āwaināhumā ilā rabwatin żāti qarāriw wa ma'īn","at":"Dan telah Kami jadikan (Isa) putra Maryam bersama ibunya sebagai suatu bukti yang nyata bagi (kebesaran Kami), dan Kami melindungi mereka di sebuah dataran tinggi, (tempat yang tenang, rindang dan banyak buah-buahan) dengan mata air yang mengalir."},{"id":"51","ar":"يٰٓاَيُّهَا الرُّسُلُ كُلُوْا مِنَ الطَّيِّبٰتِ وَاعْمَلُوْا صَالِحًاۗ اِنِّيْ بِمَا تَعْمَلُوْنَ عَلِيْمٌ ۗ","bc":"yā ayyuhar-rusulu kulụ minaṭ-ṭayyibāti wa'malụ ṣāliḥā, innī bimā ta'malụna 'alīm","at":"Allah berfirman, “Wahai para rasul! Makanlah dari (makanan) yang baik-baik, dan kerjakanlah kebajikan. Sungguh, Aku Maha Mengetahui apa yang kamu kerjakan."},{"id":"52","ar":"وَاِنَّ هٰذِهٖٓ اُمَّتُكُمْ اُمَّةً وَّاحِدَةً وَّاَنَا۠ رَبُّكُمْ فَاتَّقُوْنِ","bc":"wa inna hāżihī ummatukum ummataw wāḥidataw wa ana rabbukum fattaqụn","at":"Dan sungguh, (agama tauhid) inilah agama kamu, agama yang satu dan Aku adalah Tuhanmu, maka bertakwalah kepada-Ku.”"},{"id":"53","ar":"فَتَقَطَّعُوْٓا اَمْرَهُمْ بَيْنَهُمْ زُبُرًاۗ كُلُّ حِزْبٍۢ بِمَا لَدَيْهِمْ فَرِحُوْنَ ","bc":"fa taqaṭṭa'ū amrahum bainahum zuburā, kullu ḥizbim bimā ladaihim fariḥụn","at":"Kemudian mereka terpecah belah dalam urusan (agama)nya menjadi beberapa golongan. Setiap golongan (merasa) bangga dengan apa yang ada pada mereka (masing-masing)."},{"id":"54","ar":"فَذَرْهُمْ فِيْ غَمْرَتِهِمْ حَتّٰى حِيْنٍ ","bc":"fa żar-hum fī gamratihim ḥattā ḥīn","at":"Maka biarkanlah mereka dalam kesesatannya sampai waktu yang ditentukan."},{"id":"55","ar":"اَيَحْسَبُوْنَ اَنَّمَا نُمِدُّهُمْ بِهٖ مِنْ مَّالٍ وَّبَنِيْنَ ۙ","bc":"a yaḥsabụna annamā numidduhum bihī mim māliw wa banīn","at":"Apakah mereka mengira bahwa Kami memberikan harta dan anak-anak kepada mereka itu (berarti bahwa),"},{"id":"56","ar":"نُسَارِعُ لَهُمْ فِى الْخَيْرٰتِۗ بَلْ لَّا يَشْعُرُوْنَ","bc":"nusāri'u lahum fil-khairāt, bal lā yasy'urụn","at":"Kami segera memberikan kebaikan-kebaikan kepada mereka? (Tidak), tetapi mereka tidak menyadarinya."},{"id":"57","ar":"اِنَّ الَّذِيْنَ هُمْ مِّنْ خَشْيَةِ رَبِّهِمْ مُّشْفِقُوْنَ ۙ","bc":"innallażīna hum min khasy-yati rabbihim musyfiqụn","at":"Sungguh, orang-orang yang karena takut (azab) Tuhannya, mereka sangat berhati-hati,"},{"id":"58","ar":"وَالَّذِيْنَ هُمْ بِاٰيٰتِ رَبِّهِمْ يُؤْمِنُوْنَ ۙ","bc":"wallażīna hum bi`āyāti rabbihim yu`minụn","at":"dan mereka yang beriman dengan tanda-tanda (kekuasaan) Tuhannya,"},{"id":"59","ar":"وَالَّذِيْنَ هُمْ بِرَبِّهِمْ لَا يُشْرِكُوْنَ ۙ","bc":"wallażīna hum birabbihim lā yusyrikụn","at":"dan mereka yang tidak mempersekutukan Tuhannya,"},{"id":"60","ar":"وَالَّذِيْنَ يُؤْتُوْنَ مَآ اٰتَوْا وَّقُلُوْبُهُمْ وَجِلَةٌ اَنَّهُمْ اِلٰى رَبِّهِمْ رٰجِعُوْنَ ۙ","bc":"wallażīna yu`tụna mā ātaw wa qulụbuhum wajilatun annahum ilā rabbihim rāji'ụn","at":"dan mereka yang memberikan apa yang mereka berikan (sedekah) dengan hati penuh rasa takut (karena mereka tahu) bahwa sesungguhnya mereka akan kembali kepada Tuhannya,"},{"id":"61","ar":"اُولٰۤىِٕكَ يُسَارِعُوْنَ فِى الْخَيْرٰتِ وَهُمْ لَهَا سٰبِقُوْنَ","bc":"ulā`ika yusāri'ụna fil-khairāti wa hum lahā sābiqụn","at":"mereka itu bersegera dalam kebaikan-kebaikan, dan merekalah orang-orang yang lebih dahulu memperolehnya."},{"id":"62","ar":"وَلَا نُكَلِّفُ نَفْسًا اِلَّا وُسْعَهَاۖ وَلَدَيْنَا كِتٰبٌ يَّنْطِقُ بِالْحَقِّ وَهُمْ لَا يُظْلَمُوْنَ ","bc":"wa lā nukallifu nafsan illā wus'ahā waladainā kitābuy yanṭiqu bil-ḥaqqi wa hum lā yuẓlamụn","at":"Dan Kami tidak membebani seseorang melainkan menurut kesanggupannya, dan pada Kami ada suatu catatan yang menuturkan dengan sebenarnya, dan mereka tidak dizalimi (dirugikan). "},{"id":"63","ar":"بَلْ قُلُوْبُهُمْ فِيْ غَمْرَةٍ مِّنْ هٰذَا وَلَهُمْ اَعْمَالٌ مِّنْ دُوْنِ ذٰلِكَ هُمْ لَهَا عَامِلُوْنَ","bc":"bal qulụbuhum fī gamratim min hāżā wa lahum a'mālum min dụni żālika hum lahā 'āmilụn","at":"Tetapi, hati mereka (orang-orang kafir) itu dalam kesesatan dari (memahami Al-Qur'an) ini, dan mereka mempunyai (kebiasaan banyak mengerjakan) perbuatan-perbuatan lain (buruk) yang terus mereka kerjakan."},{"id":"64","ar":"حَتّٰٓى اِذَآ اَخَذْنَا مُتْرَفِيْهِمْ بِالْعَذَابِ اِذَا هُمْ يَجْـَٔرُوْنَ ۗ","bc":"ḥattā iżā akhażnā mutrafīhim bil-'ażābi iżā hum yaj`arụn","at":"Sehingga apabila Kami timpakan siksaan kepada orang-orang yang hidup bermewah-mewah di antara mereka, seketika itu mereka berteriak-teriak meminta tolong."},{"id":"65","ar":"لَا تَجْـَٔرُوا الْيَوْمَۖ اِنَّكُمْ مِّنَّا لَا تُنْصَرُوْنَ ","bc":"lā taj`arul-yauma innakum minnā lā tunṣarụn","at":"Janganlah kamu berteriak-teriak meminta tolong pada hari ini! Sungguh, kamu tidak akan mendapat pertolongan dari Kami."},{"id":"66","ar":"قَدْ كَانَتْ اٰيٰتِيْ تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ عَلٰٓى اَعْقَابِكُمْ تَنْكِصُوْنَ ۙ","bc":"qad kānat āyātī tutlā 'alaikum fakuntum 'alā a'qābikum tangkiṣụn","at":"Sesungguhnya ayat-ayat-Ku (Al-Qur'an) selalu dibacakan kepada kamu, tetapi kamu selalu berpaling ke belakang,"},{"id":"67","ar":"مُسْتَكْبِرِيْنَۙ بِهٖ سٰمِرًا تَهْجُرُوْنَ ","bc":"mustakbirīna bihī sāmiran tahjurụn","at":"dengan menyombongkan diri dan mengucapkan perkataan-perkataan keji terhadapnya (Al-Qur'an) pada waktu kamu bercakap-cakap pada malam hari."},{"id":"68","ar":"اَفَلَمْ يَدَّبَّرُوا الْقَوْلَ اَمْ جَاۤءَهُمْ مَّا لَمْ يَأْتِ اٰبَاۤءَهُمُ الْاَوَّلِيْنَ ۖ","bc":"a fa lam yaddabbarul-qaula am jā`ahum mā lam ya`ti ābā`ahumul-awwalīn","at":"Maka tidakkah mereka menghayati firman (Allah), atau adakah telah datang kepada mereka apa yang tidak pernah datang kepada nenek moyang mereka terdahulu?"},{"id":"69","ar":"اَمْ لَمْ يَعْرِفُوْا رَسُوْلَهُمْ فَهُمْ لَهٗ مُنْكِرُوْنَ ۖ","bc":"am lam ya'rifụ rasụlahum fa hum lahụ mungkirụn","at":"Ataukah mereka tidak mengenal Rasul mereka (Muhammad), karena itu mereka mengingkarinya?"},{"id":"70","ar":"اَمْ يَقُوْلُوْنَ بِهٖ جِنَّةٌ ۗ بَلْ جَاۤءَهُمْ بِالْحَقِّ وَاَكْثَرُهُمْ لِلْحَقِّ كٰرِهُوْنَ ","bc":"am yaqụlụna bihī jinnah, bal jā`ahum bil-ḥaqqi wa akṡaruhum lil-ḥaqqi kārihụn","at":"Atau mereka berkata, “Orang itu (Muhammad) gila.” Padahal, dia telah datang membawa kebenaran kepada mereka, tetapi kebanyakan mereka membenci kebenaran. "},{"id":"71","ar":"وَلَوِ اتَّبَعَ الْحَقُّ اَهْوَاۤءَهُمْ لَفَسَدَتِ السَّمٰوٰتُ وَالْاَرْضُ وَمَنْ فِيْهِنَّۗ بَلْ اَتَيْنٰهُمْ بِذِكْرِهِمْ فَهُمْ عَنْ ذِكْرِهِمْ مُّعْرِضُوْنَ ۗ","bc":"wa lawittaba'al-ḥaqqu ahwā`ahum lafasadatis-samāwātu wal-arḍu wa man fīhinn, bal ataināhum biżikrihim fa hum 'an żikrihim mu'riḍụn","at":"Dan seandainya kebenaran itu menuruti keinginan mereka, pasti binasalah langit dan bumi, dan semua yang ada di dalamnya. Bahkan Kami telah memberikan peringatan kepada mereka, tetapi mereka berpaling dari peringatan itu."},{"id":"72","ar":"اَمْ تَسْـَٔلُهُمْ خَرْجًا فَخَرَاجُ رَبِّكَ خَيْرٌ ۖوَّهُوَ خَيْرُ الرّٰزِقِيْنَ","bc":"am tas`aluhum kharjan fa kharāju rabbika khairuw wa huwa khairur-rāziqīn","at":"Atau engkau (Muhammad) meminta imbalan kepada mereka? Sedangkan imbalan dari Tuhanmu lebih baik, karena Dia pemberi rezeki yang terbaik."},{"id":"73","ar":"وَاِنَّكَ لَتَدْعُوْهُمْ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"wa innaka latad'ụhum ilā ṣirāṭim mustaqīm","at":"Dan sesungguhnya engkau pasti telah menyeru mereka kepada jalan yang lurus."},{"id":"74","ar":"وَاِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ عَنِ الصِّرَاطِ لَنَاكِبُوْنَ ","bc":"wa innallażīna lā yu`minụna bil-ākhirati 'aniṣ-ṣirāṭi lanākibụn","at":"Dan sesungguhnya orang-orang yang tidak beriman kepada akhirat benar-benar telah menyimpang jauh dari jalan (yang lurus)."},{"id":"75","ar":"۞ وَلَوْ رَحِمْنٰهُمْ وَكَشَفْنَا مَا بِهِمْ مِّنْ ضُرٍّ لَّلَجُّوْا فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ","bc":"walau raḥimnāhum wa kasyafnā mā bihim min ḍurril lalajjụ fī ṭugyānihim ya'mahụn","at":"Dan seandainya mereka Kami kasihani, dan Kami lenyapkan malapetaka yang menimpa mereka, pasti mereka akan terus-menerus terombang-ambing dalam kesesatan mereka."},{"id":"76","ar":"وَلَقَدْ اَخَذْنٰهُمْ بِالْعَذَابِ فَمَا اسْتَكَانُوْا لِرَبِّهِمْ وَمَا يَتَضَرَّعُوْنَ","bc":"wa laqad akhażnāhum bil-'ażābi fa mastakānụ lirabbihim wa mā yataḍarra'ụn","at":"Dan sungguh Kami telah menimpakan siksaan kepada mereka, tetapi mereka tidak mau tunduk kepada Tuhannya, dan (juga) tidak merendahkan diri."},{"id":"77","ar":"حَتّٰٓى اِذَا فَتَحْنَا عَلَيْهِمْ بَابًا ذَا عَذَابٍ شَدِيْدٍ اِذَا هُمْ فِيْهِ مُبْلِسُوْنَ ","bc":"ḥattā iżā fataḥnā 'alaihim bāban żā 'ażābin syadīdin iżā hum fīhi mublisụn","at":"Sehingga apabila Kami bukakan untuk mereka pintu azab yang sangat keras, seketika itu mereka menjadi putus asa."},{"id":"78","ar":"وَهُوَ الَّذِيْٓ اَنْشَاَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ","bc":"wa huwallażī ansya`a lakumus-sam'a wal-abṣāra wal-af`idah, qalīlam mā tasykurụn","at":"Dan Dialah yang telah menciptakan bagimu pendengaran, penglihatan dan hati nurani, tetapi sedikit sekali kamu bersyukur."},{"id":"79","ar":"وَهُوَ الَّذِيْ ذَرَاَكُمْ فِى الْاَرْضِ وَاِلَيْهِ تُحْشَرُوْنَ","bc":"wa huwallażī żara`akum fil-arḍi wa ilaihi tuḥsyarụn","at":"Dan Dialah yang menciptakan dan mengembangbiakkan kamu di bumi dan kepada-Nyalah kamu akan dikumpulkan."},{"id":"80","ar":"وَهُوَ الَّذِيْ يُحْيٖ وَيُمِيْتُ وَلَهُ اخْتِلَافُ الَّيْلِ وَالنَّهَارِۗ اَفَلَا تَعْقِلُوْنَ","bc":"wa huwallażī yuḥyī wa yumītu wa lahukhtilāful-laili wan-nahār, a fa lā ta'qilụn","at":"Dan Dialah yang menghidupkan dan mematikan, dan Dialah yang (mengatur) pergantian malam dan siang. Tidakkah kamu mengerti?"},{"id":"81","ar":"بَلْ قَالُوْا مِثْلَ مَا قَالَ الْاَوَّلُوْنَ","bc":"bal qālụ miṡla mā qālal-awwalụn","at":"Bahkan mereka mengucapkan perkataan yang serupa dengan apa yang diucapkan oleh orang-orang terdahulu."},{"id":"82","ar":"قَالُوْٓا ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَبْعُوْثُوْنَ","bc":"qālū a iżā mitnā wa kunnā turābaw wa 'iẓāman a innā lamab'ụṡụn","at":"Mereka berkata, “Apakah betul, apabila kami telah mati dan telah menjadi tanah dan tulang belulang, kami benar-benar akan dibangkitkan kembali? "},{"id":"83","ar":"لَقَدْ وُعِدْنَا نَحْنُ وَاٰبَاۤؤُنَا هٰذَا مِنْ قَبْلُ اِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ ","bc":"laqad wu'idnā naḥnu wa ābā`unā hāżā ming qablu in hāżā illā asāṭīrul-awwalīn","at":"Sungguh, yang demikian ini sudah dijanjikan kepada kami dan kepada nenek moyang kami dahulu, ini hanyalah dongeng orang-orang terdahulu!”"},{"id":"84","ar":"قُلْ لِّمَنِ الْاَرْضُ وَمَنْ فِيْهَآ اِنْ كُنْتُمْ تَعْلَمُوْنَ ","bc":"qul limanil-arḍu wa man fīhā ing kuntum ta'lamụn","at":"Katakanlah (Muhammad), “Milik siapakah bumi, dan semua yang ada di dalamnya, jika kamu mengetahui?”"},{"id":"85","ar":"سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ اَفَلَا تَذَكَّرُوْنَ ","bc":"sayaqụlụna lillāh, qul a fa lā tażakkarụn","at":"Mereka akan menjawab, “Milik Allah.” Katakanlah, “Maka apakah kamu tidak ingat?”"},{"id":"86","ar":"قُلْ مَنْ رَّبُّ السَّمٰوٰتِ السَّبْعِ وَرَبُّ الْعَرْشِ الْعَظِيْمِ","bc":"qul mar rabbus-samāwātis-sab'i wa rabbul-'arsyil-'aẓīm","at":"Katakanlah, “Siapakah Tuhan yang memiliki langit yang tujuh dan yang memiliki ‘Arsy yang agung?”"},{"id":"87","ar":"سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ اَفَلَا تَتَّقُوْنَ","bc":"sayaqụlụna lillāh, qul a fa lā tattaqụn","at":"Mereka akan menjawab, “(Milik) Allah.” Katakanlah, “Maka mengapa kamu tidak bertakwa?”"},{"id":"88","ar":"قُلْ مَنْۢ بِيَدِهٖ مَلَكُوْتُ كُلِّ شَيْءٍ وَّهُوَ يُجِيْرُ وَلَا يُجَارُ عَلَيْهِ اِنْ كُنْتُمْ تَعْلَمُوْنَ ","bc":"qul mam biyadihī malakụtu kulli syai`iw wa huwa yujīru wa lā yujāru 'alaihi ing kuntum ta'lamụn","at":"Katakanlah, “Siapakah yang di tangan-Nya berada kekuasaan segala sesuatu. Dia melindungi, dan tidak ada yang dapat dilindungi dari (azab-Nya), jika kamu mengetahui?”"},{"id":"89","ar":"سَيَقُوْلُوْنَ لِلّٰهِ ۗقُلْ فَاَنّٰى تُسْحَرُوْنَ","bc":"sayaqụlụna lillāh, qul fa annā tus-ḥarụn","at":"Mereka akan menjawab, “(Milik) Allah.” Katakanlah, “(Kalau demikian), maka bagaimana kamu sampai tertipu?”"},{"id":"90","ar":"بَلْ اَتَيْنٰهُمْ بِالْحَقِّ وَاِنَّهُمْ لَكٰذِبُوْنَ","bc":"bal ataināhum bil-ḥaqqi wa innahum lakāżibụn","at":"Padahal Kami telah membawa kebenaran kepada mereka, tetapi mere-ka benar-benar pendusta."},{"id":"91","ar":"مَا اتَّخَذَ اللّٰهُ مِنْ وَّلَدٍ وَّمَا كَانَ مَعَهٗ مِنْ اِلٰهٍ اِذًا لَّذَهَبَ كُلُّ اِلٰهٍۢ بِمَا خَلَقَ وَلَعَلَا بَعْضُهُمْ عَلٰى بَعْضٍۗ سُبْحٰنَ اللّٰهِ عَمَّا يَصِفُوْنَ ۙ","bc":"mattakhażallāhu miw waladiw wa mā kāna ma'ahụ min ilāhin iżal lażahaba kullu ilāhim bimā khalaqa wa la'alā ba'ḍuhum 'alā ba'ḍ, sub-ḥānallāhi 'ammā yaṣifụn","at":"Allah tidak mempunyai anak, dan tidak ada tuhan (yang lain) bersama-Nya, (sekiranya tuhan banyak), maka masing-masing tuhan itu akan membawa apa (makhluk) yang diciptakannya, dan sebagian dari tuhan-tuhan itu akan mengalahkan sebagian yang lain. Mahasuci Allah dari apa yang mereka sifatkan itu,"},{"id":"92","ar":"عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَتَعٰلٰى عَمَّا يُشْرِكُوْنَ","bc":"'ālimil-gaibi wasy-syahādati fa ta'ālā 'ammā yusyrikụn","at":"(Dialah Tuhan) yang mengetahui semua yang gaib dan semua yang tampak. Mahatinggi (Allah) dari apa yang mereka persekutukan."},{"id":"93","ar":"قُلْ رَّبِّ اِمَّا تُرِيَنِّيْ مَا يُوْعَدُوْنَ ۙ","bc":"qur rabbi immā turiyannī mā yụ'adụn","at":"Katakanlah (Muhammad), “Ya Tuhanku, seandainya Engkau hendak memperlihatkan kepadaku apa (azab) yang diancamkan kepada mereka,"},{"id":"94","ar":"رَبِّ فَلَا تَجْعَلْنِيْ فِى الْقَوْمِ الظّٰلِمِيْنَ","bc":"rabbi fa lā taj'alnī fil-qaumiẓ-ẓālimīn","at":"Ya Tuhanku, maka janganlah Engkau jadikan aku dalam golongan orang-orang zalim.”"},{"id":"95","ar":"وَاِنَّا عَلٰٓى اَنْ نُّرِيَكَ مَا نَعِدُهُمْ لَقٰدِرُوْنَ","bc":"wa innā 'alā an nuriyaka mā na'iduhum laqādirụn","at":"Dan sungguh, Kami kuasa untuk memperlihatkan kepadamu (Muhammad) apa yang Kami ancamkan kepada mereka."},{"id":"96","ar":"اِدْفَعْ بِالَّتِيْ هِيَ اَحْسَنُ السَّيِّئَةَۗ نَحْنُ اَعْلَمُ بِمَا يَصِفُوْنَ ","bc":"idfa' billatī hiya aḥsanus-sayyi`ah, naḥnu a'lamu bimā yaṣifụn","at":"Tolaklah perbuatan buruk mereka dengan (cara) yang lebih baik, Kami lebih mengetahui apa yang mereka sifatkan (kepada Allah)."},{"id":"97","ar":"وَقُلْ رَّبِّ اَعُوْذُ بِكَ مِنْ هَمَزٰتِ الشَّيٰطِيْنِ ۙ","bc":"wa qur rabbi a'ụżu bika min hamazātisy-syayāṭīn","at":"Dan katakanlah, “Ya Tuhanku, aku berlindung kepada Engkau dari bisikan-bisikan setan,"},{"id":"98","ar":"وَاَعُوْذُ بِكَ رَبِّ اَنْ يَّحْضُرُوْنِ ","bc":"wa a'ụżu bika rabbi ay yaḥḍurụn","at":"dan aku berlindung (pula) kepada Engkau ya Tuhanku, agar mereka tidak mendekati aku.”"},{"id":"99","ar":"حَتّٰٓى اِذَا جَاۤءَ اَحَدَهُمُ الْمَوْتُ قَالَ رَبِّ ارْجِعُوْنِ ۙ","bc":"ḥattā iżā jā`a aḥadahumul-mautu qāla rabbirji'ụn","at":"(Demikianlah keadaan orang-orang kafir itu), hingga apabila datang kematian kepada seseorang dari mereka, dia berkata, “Ya Tuhanku, kembalikanlah aku (ke dunia),"},{"id":"100","ar":"لَعَلِّيْٓ اَعْمَلُ صَالِحًا فِيْمَا تَرَكْتُ كَلَّاۗ اِنَّهَا كَلِمَةٌ هُوَ قَاۤىِٕلُهَاۗ وَمِنْ وَّرَاۤىِٕهِمْ بَرْزَخٌ اِلٰى يَوْمِ يُبْعَثُوْنَ ","bc":"la'allī a'malu ṣāliḥan fīmā taraktu kallā, innahā kalimatun huwa qā`iluhā, wa miw warā`ihim barzakhun ilā yaumi yub'aṡụn","at":"agar aku dapat berbuat kebajikan yang telah aku tinggalkan.” Sekali-kali tidak! Sesungguhnya itu adalah dalih yang diucapkannya saja. Dan di hadapan mereka ada barzakh sampai pada hari mereka dibangkitkan."},{"id":"101","ar":"فَاِذَا نُفِخَ فِى الصُّوْرِ فَلَآ اَنْسَابَ بَيْنَهُمْ يَوْمَىِٕذٍ وَّلَا يَتَسَاۤءَلُوْنَ","bc":"fa iżā nufikha fiṣ-ṣụri fa lā ansāba bainahum yauma`iżiw wa lā yatasā`alụn","at":"Apabila sangkakala ditiup maka tidak ada lagi pertalian keluarga di antara mereka pada hari itu (hari Kiamat), dan tidak (pula) mereka saling bertanya."},{"id":"102","ar":"فَمَنْ ثَقُلَتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ","bc":"fa man ṡaqulat mawāzīnuhụ fa ulā`ika humul-mufliḥụn","at":"Barangsiapa berat timbangan (kebaikan)nya, maka mereka itulah orang-orang yang beruntung."},{"id":"103","ar":"وَمَنْ خَفَّتْ مَوَازِيْنُهٗ فَاُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ فِيْ جَهَنَّمَ خٰلِدُوْنَ ۚ","bc":"wa man khaffat mawāzīnuhụ fa ulā`ikallażīna khasirū anfusahum fī jahannama khālidụn","at":"Dan barang siapa ringan timbangan (kebaikan)nya, maka mereka itulah orang-orang yang merugikan dirinya sendiri, mereka kekal di dalam neraka Jahanam"},{"id":"104","ar":"تَلْفَحُ وُجُوْهَهُمُ النَّارُ وَهُمْ فِيْهَا كَالِحُوْنَ ","bc":"talfaḥu wujụhahumun-nāru wa hum fīhā kāliḥụn","at":"Wajah mereka dibakar api neraka, dan mereka di neraka dalam keadaan muram dengan bibir yang cacat."},{"id":"105","ar":"اَلَمْ تَكُنْ اٰيٰتِيْ تُتْلٰى عَلَيْكُمْ فَكُنْتُمْ بِهَا تُكَذِّبُوْنَ","bc":"a lam takun āyātī tutlā 'alaikum fa kuntum bihā tukażżibụn","at":"Bukankah ayat-ayat-Ku telah dibacakan kepadamu, tetapi kamu selalu mendustakannya?"},{"id":"106","ar":"قَالُوْا رَبَّنَا غَلَبَتْ عَلَيْنَا شِقْوَتُنَا وَكُنَّا قَوْمًا ضَاۤلِّيْنَ","bc":"qālụ rabbanā galabat 'alainā syiqwatunā wa kunnā qauman ḍāllīn","at":"Mereka berkata, “Ya Tuhan kami, kami telah dikuasai oleh kejahatan kami, dan kami adalah orang-orang yang sesat."},{"id":"107","ar":"رَبَّنَآ اَخْرِجْنَا مِنْهَا فَاِنْ عُدْنَا فَاِنَّا ظٰلِمُوْنَ","bc":"rabbanā akhrijnā min-hā fa in 'udnā fa innā ẓālimụn","at":"Ya Tuhan kami, keluarkanlah kami darinya (kembalikanlah kami ke dunia), jika kami masih juga kembali (kepada kekafiran), sungguh, kami adalah orang-orang yang zalim.”"},{"id":"108","ar":"قَالَ اخْسَـُٔوْا فِيْهَا وَلَا تُكَلِّمُوْنِ","bc":"qālakhsa`ụ fīhā wa lā tukallimụn","at":"Dia (Allah) berfirman, “Tinggallah dengan hina di dalamnya, dan janganlah kamu berbicara dengan Aku.”"},{"id":"109","ar":"اِنَّهٗ كَانَ فَرِيْقٌ مِّنْ عِبَادِيْ يَقُوْلُوْنَ رَبَّنَآ اٰمَنَّا فَاغْفِرْ لَنَا وَارْحَمْنَا وَاَنْتَ خَيْرُ الرّٰحِمِيْنَ ۚ","bc":"innahụ kāna farīqum min 'ibādī yaqụlụna rabbanā āmannā fagfir lanā war-ḥamnā wa anta khairur-rāḥimīn","at":"Sesungguhnya ada segolongan dari hamba-hamba-Ku berdoa, “Ya Tuhan kami, kami telah beriman, maka ampunilah kami dan berilah kami rahmat, Engkau adalah pemberi rahmat yang terbaik.”"},{"id":"110","ar":"فَاتَّخَذْتُمُوْهُمْ سِخْرِيًّا حَتّٰٓى اَنْسَوْكُمْ ذِكْرِيْ وَكُنْتُمْ مِّنْهُمْ تَضْحَكُوْنَ","bc":"fattakhażtumụhum sikhriyyan ḥattā ansaukum żikrī wa kuntum min-hum taḍ-ḥakụn","at":"Lalu kamu jadikan mereka buah ejekan, sehingga kamu lupa mengingat Aku, dan kamu (selalu) menertawakan mereka,"},{"id":"111","ar":"اِنِّيْ جَزَيْتُهُمُ الْيَوْمَ بِمَا صَبَرُوْٓاۙ اَنَّهُمْ هُمُ الْفَاۤىِٕزُوْنَ","bc":"innī jazaituhumul-yauma bimā ṣabarū annahum humul-fā`izụn","at":"sesungguhnya pada hari ini Aku memberi balasan kepada mereka, karena kesabaran mereka; sesungguhnya mereka itulah orang-orang yang memperoleh kemenangan."},{"id":"112","ar":"قٰلَ كَمْ لَبِثْتُمْ فِى الْاَرْضِ عَدَدَ سِنِيْنَ","bc":"qāla kam labiṡtum fil-arḍi 'adada sinīn","at":"Dia (Allah) berfirman, “Berapa tahunkah lamanya kamu tinggal di bumi?”"},{"id":"113","ar":"قَالُوْا لَبِثْنَا يَوْمًا اَوْ بَعْضَ يَوْمٍ فَسْـَٔلِ الْعَاۤدِّيْنَ ","bc":"qālụ labiṡnā yauman au ba'ḍa yaumin fas`alil-'āddīn","at":"Mereka menjawab, “Kami tinggal (di bumi) sehari atau setengah hari, maka tanyakanlah kepada mereka yang menghitung.”"},{"id":"114","ar":"قٰلَ اِنْ لَّبِثْتُمْ اِلَّا قَلِيْلًا لَّوْ اَنَّكُمْ كُنْتُمْ تَعْلَمُوْنَ","bc":"qāla il labiṡtum illā qalīlal lau annakum kuntum ta'lamụn","at":"Dia (Allah) berfirman, “Kamu tinggal (di bumi) hanya sebentar saja, jika kamu benar-benar mengetahui.” "},{"id":"115","ar":"اَفَحَسِبْتُمْ اَنَّمَا خَلَقْنٰكُمْ عَبَثًا وَّاَنَّكُمْ اِلَيْنَا لَا تُرْجَعُوْنَ","bc":"a fa ḥasibtum annamā khalaqnākum 'abaṡaw wa annakum ilainā lā turja'ụn","at":"Maka apakah kamu mengira, bahwa Kami menciptakan kamu main-main (tanpa ada maksud) dan bahwa kamu tidak akan dikembalikan kepada Kami?"},{"id":"116","ar":"فَتَعٰلَى اللّٰهُ الْمَلِكُ الْحَقُّۚ لَآ اِلٰهَ اِلَّا هُوَۚ رَبُّ الْعَرْشِ الْكَرِيْمِ","bc":"fa ta'ālallāhul-malikul-ḥaqq, lā ilāha illā huw, rabbul-'arsyil-karīm","at":"Maka Mahatinggi Allah, Raja yang sebenarnya; tidak ada tuhan (yang berhak disembah) selain Dia, Tuhan (yang memiliki) ‘Arsy yang mulia."},{"id":"117","ar":"وَمَنْ يَّدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ لَا بُرْهَانَ لَهٗ بِهٖۙ فَاِنَّمَا حِسَابُهٗ عِنْدَ رَبِّهٖۗ اِنَّهٗ لَا يُفْلِحُ الْكٰفِرُوْنَ","bc":"wa may yad'u ma'allāhi ilāhan ākhara lā bur-hāna lahụ bihī fa innamā ḥisābuhụ 'inda rabbih, innahụ lā yufliḥul-kāfirụn","at":"Dan barang siapa menyembah tuhan yang lain selain Allah, padahal tidak ada suatu bukti pun baginya tentang itu, maka perhitungannya hanya pada Tuhannya. Sesungguhnya orang-orang kafir itu tidak akan beruntung."},{"id":"118","ar":"وَقُلْ رَّبِّ اغْفِرْ وَارْحَمْ وَاَنْتَ خَيْرُ الرّٰحِمِيْنَ","bc":"wa qur rabbigfir war-ḥam wa anta khairur-rāḥimīn","at":"Dan katakanlah (Muhammad), “Ya Tuhanku, berilah ampunan dan (berilah) rahmat, Engkaulah pemberi rahmat yang terbaik.”"}],al_mumtahanah=[{"id":"1","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوْا عَدُوِّيْ وَعَدُوَّكُمْ اَوْلِيَاۤءَ تُلْقُوْنَ اِلَيْهِمْ بِالْمَوَدَّةِ وَقَدْ كَفَرُوْا بِمَا جَاۤءَكُمْ مِّنَ الْحَقِّۚ يُخْرِجُوْنَ الرَّسُوْلَ وَاِيَّاكُمْ اَنْ تُؤْمِنُوْا بِاللّٰهِ رَبِّكُمْۗ اِنْ كُنْتُمْ خَرَجْتُمْ جِهَادًا فِيْ سَبِيْلِيْ وَابْتِغَاۤءَ مَرْضَاتِيْ تُسِرُّوْنَ اِلَيْهِمْ بِالْمَوَدَّةِ وَاَنَا۠ اَعْلَمُ بِمَآ اَخْفَيْتُمْ وَمَآ اَعْلَنْتُمْۗ وَمَنْ يَّفْعَلْهُ مِنْكُمْ فَقَدْ ضَلَّ سَوَاۤءَ السَّبِيْلِ ","bc":"yā ayyuhallażīna āmanụ lā tattakhiżụ 'aduwwī wa 'aduwwakum auliyā`a tulqụna ilaihim bil-mawaddati wa qad kafarụ bimā jā`akum minal-ḥaqq, yukhrijụnar-rasụla wa iyyākum an tu`minụ billāhi rabbikum, ing kuntum kharajtum jihādan fī sabīlī wabtigā`a marḍātī tusirrụna ilaihim bil-mawaddati wa ana a'lamu bimā akhfaitum wa mā a'lantum, wa may yaf'al-hu mingkum fa qad ḍalla sawā`as-sabīl","at":"Wahai orang-orang yang beriman! Janganlah kamu menjadikan musuh-Ku dan musuhmu sebagai teman-teman setia sehingga kamu sampaikan kepada mereka (berita-berita Muhammad), karena rasa kasih sayang; padahal mereka telah ingkar kepada kebenaran yang disampaikan kepadamu. Mereka mengusir Rasul dan kamu sendiri karena kamu beriman kepada Allah, Tuhanmu. Jika kamu benar-benar keluar untuk berjihad pada jalan-Ku dan mencari keridaan-Ku (janganlah kamu berbuat demikian). Kamu memberitahukan secara rahasia (berita-berita Muhammad) kepada mereka, karena rasa kasih sayang, dan Aku lebih mengetahui apa yang kamu sembunyikan dan apa yang kamu nyatakan. Dan barangsiapa di antara kamu yang melakukannya, maka sungguh, dia telah tersesat dari jalan yang lurus. "},{"id":"2","ar":"اِنْ يَّثْقَفُوْكُمْ يَكُوْنُوْا لَكُمْ اَعْدَاۤءً وَّيَبْسُطُوْٓا اِلَيْكُمْ اَيْدِيَهُمْ وَاَلْسِنَتَهُمْ بِالسُّوْۤءِ وَوَدُّوْا لَوْ تَكْفُرُوْنَۗ ","bc":"iy yaṡqafụkum yakụnụ lakum a'dā`aw wa yabsuṭū ilaikum aidiyahum wa alsinatahum bis-sū`i wa waddụ lau takfurụn","at":"Jika mereka menangkapmu, niscaya mereka bertindak sebagai musuh bagimu lalu melepaskan tangan dan lidahnya kepadamu untuk menyakiti dan mereka ingin agar kamu (kembali) kafir. "},{"id":"3","ar":"لَنْ تَنْفَعَكُمْ اَرْحَامُكُمْ وَلَآ اَوْلَادُكُمْ ۛيَوْمَ الْقِيٰمَةِ ۛيَفْصِلُ بَيْنَكُمْۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ","bc":"lan tanfa'akum ar-ḥāmukum wa lā aulādukum, yaumal-qiyāmati yafṣilu bainakum, wallāhu bimā ta'malụna baṣīr","at":"Kaum kerabatmu dan anak-anakmu tidak akan bermanfaat bagimu pada hari Kiamat. Dia akan memisahkan antara kamu. Dan Allah Maha Melihat apa yang kamu kerjakan."},{"id":"4","ar":"قَدْ كَانَتْ لَكُمْ اُسْوَةٌ حَسَنَةٌ فِيْٓ اِبْرٰهِيْمَ وَالَّذِيْنَ مَعَهٗۚ اِذْ قَالُوْا لِقَوْمِهِمْ اِنَّا بُرَءٰۤؤُا مِنْكُمْ وَمِمَّا تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ ۖ كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ الْعَدَاوَةُ وَالْبَغْضَاۤءُ اَبَدًا حَتّٰى تُؤْمِنُوْا بِاللّٰهِ وَحْدَهٗٓ اِلَّا قَوْلَ اِبْرٰهِيْمَ لِاَبِيْهِ لَاَسْتَغْفِرَنَّ لَكَ وَمَآ اَمْلِكُ لَكَ مِنَ اللّٰهِ مِنْ شَيْءٍۗ رَبَّنَا عَلَيْكَ تَوَكَّلْنَا وَاِلَيْكَ اَنَبْنَا وَاِلَيْكَ الْمَصِيْرُ ","bc":"qad kānat lakum uswatun ḥasanatun fī ibrāhīma wallażīna ma'ah, iż qālụ liqaumihim innā bura`ā`u mingkum wa mimmā ta'budụna min dụnillāhi kafarnā bikum wa badā bainanā wa bainakumul-'adāwatu wal-bagḍā`u abadan ḥattā tu`minụ billāhi waḥdahū illā qaula ibrāhīma li`abīhi la`astagfiranna laka wa mā amliku laka minallāhi min syaī`, rabbanā 'alaika tawakkalnā wa ilaika anabnā wa ilaikal-maṣīr","at":"Sungguh, telah ada suri teladan yang baik bagimu pada Ibrahim dan orang-orang yang bersama dengannya, ketika mereka berkata kepada kaumnya, “Sesungguhnya kami berlepas diri dari kamu dan dari apa yang kamu sembah selain Allah, kami mengingkari (kekafiran)mu dan telah nyata antara kami dan kamu ada permusuhan dan kebencian untuk selama-lamanya sampai kamu beriman kepada Allah saja,” kecuali perkataan Ibrahim kepada ayahnya, ”Sungguh, aku akan memohonkan ampunan bagimu, namun aku sama sekali tidak dapat menolak (siksaan) Allah terhadapmu.” (Ibrahim berkata), “Ya Tuhan kami, hanya kepada Engkau kami bertawakal dan hanya kepada Engkau kami bertobat dan hanya kepada Engkaulah kami kembali,"},{"id":"5","ar":"رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِّلَّذِيْنَ كَفَرُوْا وَاغْفِرْ لَنَا رَبَّنَاۚ اِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُ ","bc":"rabbanā lā taj'alnā fitnatal lillażīna kafarụ wagfir lanā rabbanā, innaka antal-'azīzul-ḥakīm","at":"Ya Tuhan kami, janganlah Engkau jadikan kami (sasaran) fitnah bagi orang-orang kafir. Dan ampunilah kami, ya Tuhan kami. Sesungguhnya Engkau yang Mahaperkasa, Mahabijaksana.”"},{"id":"6","ar":"لَقَدْ كَانَ لَكُمْ فِيْهِمْ اُسْوَةٌ حَسَنَةٌ لِّمَنْ كَانَ يَرْجُو اللّٰهَ وَالْيَوْمَ الْاٰخِرَۗ وَمَنْ يَّتَوَلَّ فَاِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَمِيْدُ ","bc":"laqad kāna lakum fīhim uswatun ḥasanatul limang kāna yarjullāha wal-yaumal-ākhir, wa may yatawalla fa innallāha huwal-ganiyyul-ḥamīd","at":"Sungguh, pada mereka itu (Ibrahim dan umatnya) terdapat suri teladan yang baik bagimu; (yaitu) bagi orang yang mengharap (pahala) Allah dan (keselamatan pada) hari kemudian, dan barangsiapa berpaling, maka sesungguhnya Allah, Dialah Yang Mahakaya, Maha Terpuji."},{"id":"7","ar":"۞ عَسَى اللّٰهُ اَنْ يَّجْعَلَ بَيْنَكُمْ وَبَيْنَ الَّذِيْنَ عَادَيْتُمْ مِّنْهُمْ مَّوَدَّةًۗ وَاللّٰهُ قَدِيْرٌۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ","bc":"'asallāhu ay yaj'ala bainakum wa bainallażīna 'ādaitum min-hum mawaddah, wallāhu qadīrun, wallāhu gafụrur raḥīm","at":"Mudah-mudahan Allah menimbulkan kasih sayang di antara kamu dengan orang-orang yang pernah kamu musuhi di antara mereka. Allah Mahakuasa. Dan Allah Maha Pengampun, Maha Penyayang."},{"id":"8","ar":"لَا يَنْهٰىكُمُ اللّٰهُ عَنِ الَّذِيْنَ لَمْ يُقَاتِلُوْكُمْ فِى الدِّيْنِ وَلَمْ يُخْرِجُوْكُمْ مِّنْ دِيَارِكُمْ اَنْ تَبَرُّوْهُمْ وَتُقْسِطُوْٓا اِلَيْهِمْۗ اِنَّ اللّٰهَ يُحِبُّ الْمُقْسِطِيْنَ ","bc":"lā yan-hākumullāhu 'anillażīna lam yuqātilụkum fid-dīni wa lam yukhrijụkum min diyārikum an tabarrụhum wa tuqsiṭū ilaihim, innallāha yuḥibbul-muqsiṭīn","at":"Allah tidak melarang kamu berbuat baik dan berlaku adil terhadap orang-orang yang tidak memerangimu dalam urusan agama dan tidak mengusir kamu dari kampung halamanmu. Sesungguhnya Allah mencintai orang-orang yang berlaku adil."},{"id":"9","ar":"اِنَّمَا يَنْهٰىكُمُ اللّٰهُ عَنِ الَّذِيْنَ قَاتَلُوْكُمْ فِى الدِّيْنِ وَاَخْرَجُوْكُمْ مِّنْ دِيَارِكُمْ وَظَاهَرُوْا عَلٰٓى اِخْرَاجِكُمْ اَنْ تَوَلَّوْهُمْۚ وَمَنْ يَّتَوَلَّهُمْ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ ","bc":"innamā yan-hākumullāhu 'anillażīna qātalụkum fid-dīni wa akhrajụkum min diyārikum wa ẓāharụ 'alā ikhrājikum an tawallauhum, wa may yatawallahum fa ulā`ika humuẓ-ẓālimụn","at":"Sesungguhnya Allah hanya melarang kamu menjadikan mereka sebagai kawanmu orang-orang yang memerangi kamu dalam urusan agama dan mengusir kamu dari kampung halamanmu dan membantu (orang lain) untuk mengusirmu. Barangsiapa menjadikan mereka sebagai kawan, mereka itulah orang-orang yang zalim."},{"id":"10","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا جَاۤءَكُمُ الْمُؤْمِنٰتُ مُهٰجِرٰتٍ فَامْتَحِنُوْهُنَّۗ اَللّٰهُ اَعْلَمُ بِاِيْمَانِهِنَّ فَاِنْ عَلِمْتُمُوْهُنَّ مُؤْمِنٰتٍ فَلَا تَرْجِعُوْهُنَّ اِلَى الْكُفَّارِۗ لَا هُنَّ حِلٌّ لَّهُمْ وَلَا هُمْ يَحِلُّوْنَ لَهُنَّۗ وَاٰتُوْهُمْ مَّآ اَنْفَقُوْاۗ وَلَا جُنَاحَ عَلَيْكُمْ اَنْ تَنْكِحُوْهُنَّ اِذَآ اٰتَيْتُمُوْهُنَّ اُجُوْرَهُنَّۗ وَلَا تُمْسِكُوْا بِعِصَمِ الْكَوَافِرِ وَسْـَٔلُوْا مَآ اَنْفَقْتُمْ وَلْيَسْـَٔلُوْا مَآ اَنْفَقُوْاۗ ذٰلِكُمْ حُكْمُ اللّٰهِ ۗيَحْكُمُ بَيْنَكُمْۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ","bc":"yā ayyuhallażīna āmanū iżā jā`akumul-mu`minātu muhājirātin famtaḥinụhunn, allāhu a'lamu bi`īmānihinna fa in 'alimtumụhunna mu`minātin fa lā tarji'ụhunna ilal-kuffār, lā hunna ḥillul lahum wa lā hum yaḥillụna lahunn, wa ātụhum mā anfaqụ, wa lā junāḥa 'alaikum an tangkiḥụhunna iżā ātaitumụhunna ujụrahunn, wa lā tumsikụ bi'iṣamil-kawāfiri was`alụ mā anfaqtum walyas`alụ mā anfaqụ, żālikum ḥukmullāh, yaḥkumu bainakum, wallāhu 'alīmun ḥakīm","at":"Wahai orang-orang yang beriman! Apabila perempuan-perempuan mukmin datang berhijrah kepadamu, maka hendaklah kamu uji (keimanan) mereka. Allah lebih mengetahui tentang keimanan mereka; jika kamu telah mengetahui bahwa mereka (benar-benar) beriman maka janganlah kamu kembalikan mereka kepada orang-orang kafir (suami-suami mereka). Mereka tidak halal bagi orang-orang kafir itu dan orang-orang kafir itu tidak halal bagi mereka. Dan berikanlah kepada (suami) mereka mahar yang telah mereka berikan. Dan tidak ada dosa bagimu menikahi mereka apabila kamu bayar kepada mereka maharnya. Dan janganlah kamu tetap berpegang pada tali (pernikahan) dengan perempuan-perempuan kafir; dan hendaklah kamu minta kembali mahar yang telah kamu berikan; dan (jika suaminya tetap kafir) biarkan mereka meminta kembali mahar yang telah mereka bayar (kepada mantan istrinya yang telah beriman). Demikianlah hukum Allah yang ditetapkan-Nya di antara kamu. Dan Allah Maha Mengetahui, Mahabijaksana."},{"id":"11","ar":"وَاِنْ فَاتَكُمْ شَيْءٌ مِّنْ اَزْوَاجِكُمْ اِلَى الْكُفَّارِ فَعَاقَبْتُمْ فَاٰتُوا الَّذِيْنَ ذَهَبَتْ اَزْوَاجُهُمْ مِّثْلَ مَآ اَنْفَقُوْاۗ وَاتَّقُوا اللّٰهَ الَّذِيْٓ اَنْتُمْ بِهٖ مُؤْمِنُوْنَ ","bc":"wa in fātakum syai`um min azwājikum ilal-kuffāri fa āqabtum fa ātullażīna żahabat azwājuhum miṡla mā anfaqụ, wattaqullāhallażī antum bihī mu`minụn","at":"Dan jika ada sesuatu (pengembalian mahar) yang belum kamu selesaikan dari istri-istrimu yang lari kepada orang-orang kafir, lalu kamu dapat mengalahkan mereka maka berikanlah (dari harta rampasan) kepada orang-orang yang istrinya lari itu sebanyak mahar yang telah mereka berikan. Dan bertakwalah kamu kepada Allah yang kepada-Nya kamu beriman."},{"id":"12","ar":"يٰٓاَيُّهَا النَّبِيُّ اِذَا جَاۤءَكَ الْمُؤْمِنٰتُ يُبَايِعْنَكَ عَلٰٓى اَنْ لَّا يُشْرِكْنَ بِاللّٰهِ شَيْـًٔا وَّلَا يَسْرِقْنَ وَلَا يَزْنِيْنَ وَلَا يَقْتُلْنَ اَوْلَادَهُنَّ وَلَا يَأْتِيْنَ بِبُهْتَانٍ يَّفْتَرِيْنَهٗ بَيْنَ اَيْدِيْهِنَّ وَاَرْجُلِهِنَّ وَلَا يَعْصِيْنَكَ فِيْ مَعْرُوْفٍ فَبَايِعْهُنَّ وَاسْتَغْفِرْ لَهُنَّ اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"yā ayyuhan-nabiyyu iżā jā`akal-mu`minātu yubāyi'naka 'alā al lā yusyrikna billāhi syai`aw wa lā yasriqna wa lā yaznīna wa lā yaqtulna aulādahunna wa lā ya`tīna bibuhtāniy yaftarīnahụ baina aidīhinna wa arjulihinna wa lā ya'ṣīnaka fī ma'rụfin fa bāyi'hunna wastagfir lahunnallāh, innallāha gafụrur raḥīm","at":"Wahai Nabi! Apabila perempuan-perempuan yang mukmin datang kepadamu untuk mengadakan bai‘at (janji setia), bahwa mereka tidak akan mempersekutukan sesuatu apa pun dengan Allah; tidak akan mencuri, tidak akan berzina, tidak akan membunuh anak-anaknya, tidak akan berbuat dusta yang mereka ada-adakan antara tangan dan kaki mereka dan tidak akan mendurhakaimu dalam urusan yang baik, maka terimalah janji setia mereka dan mohonkanlah ampunan untuk mereka kepada Allah. Sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"13","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَوَلَّوْا قَوْمًا غَضِبَ اللّٰهُ عَلَيْهِمْ قَدْ يَىِٕسُوْا مِنَ الْاٰخِرَةِ كَمَا يَىِٕسَ الْكُفَّارُ مِنْ اَصْحٰبِ الْقُبُوْرِ ","bc":"yā ayyuhallażīna āmanụ lā tatawallau qauman gaḍiballāhu 'alaihim qad ya`isụ minal-ākhirati kamā ya`isal-kuffāru min aṣ-ḥābil-qubụr","at":"Wahai orang-orang yang beriman! Janganlah kamu jadikan orang-orang yang dimurkai Allah sebagai penolongmu, sungguh, mereka telah putus asa terhadap akhirat sebagaimana orang-orang kafir yang telah berada dalam kubur juga berputus asa."}],al_munafiqun=[{"id":"1","ar":"اِذَا جَاۤءَكَ الْمُنٰفِقُوْنَ قَالُوْا نَشْهَدُ اِنَّكَ لَرَسُوْلُ اللّٰهِ ۘوَاللّٰهُ يَعْلَمُ اِنَّكَ لَرَسُوْلُهٗ ۗوَاللّٰهُ يَشْهَدُ اِنَّ الْمُنٰفِقِيْنَ لَكٰذِبُوْنَۚ ","bc":"iżā jā`akal munāfiqụna qālụ nasy-hadu innaka larasụlullāh, wallāhu ya'lamu innaka larasụluh, wallāhu yasy-hadu innal-munāfiqīna lakāżibụn","at":"Apabila orang-orang munafik datang kepadamu (Muhammad), mereka berkata, “Kami mengakui, bahwa engkau adalah Rasul Allah.” Dan Allah mengetahui bahwa engkau benar-benar Rasul-Nya; dan Allah menyaksikan bahwa orang-orang munafik itu benar-benar pendusta."},{"id":"2","ar":"اِتَّخَذُوْٓا اَيْمَانَهُمْ جُنَّةً فَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ ۗاِنَّهُمْ سَاۤءَ مَا كَانُوْا يَعْمَلُوْنَ ","bc":"ittakhażū aimānahum junnatan fa ṣaddụ 'an sabīlillāh, innahum sā`a mā kānụ ya'malụn","at":"Mereka menjadikan sumpah-sumpah mereka sebagai perisai, lalu mereka menghalang-halangi (manusia) dari jalan Allah. Sungguh, betapa buruknya apa yang telah mereka kerjakan."},{"id":"3","ar":"ذٰلِكَ بِاَنَّهُمْ اٰمَنُوْا ثُمَّ كَفَرُوْا فَطُبِعَ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَفْقَهُوْنَ ","bc":"żālika bi`annahum āmanụ ṡumma kafarụ fa ṭubi'a 'alā qulụbihim fa hum lā yafqahụn","at":"Yang demikian itu karena sesungguhnya mereka telah beriman, kemudian menjadi kafir, maka hati mereka dikunci, sehingga mereka tidak dapat mengerti. "},{"id":"4","ar":"۞ وَاِذَا رَاَيْتَهُمْ تُعْجِبُكَ اَجْسَامُهُمْۗ وَاِنْ يَّقُوْلُوْا تَسْمَعْ لِقَوْلِهِمْۗ كَاَنَّهُمْ خُشُبٌ مُّسَنَّدَةٌ ۗيَحْسَبُوْنَ كُلَّ صَيْحَةٍ عَلَيْهِمْۗ هُمُ الْعَدُوُّ فَاحْذَرْهُمْۗ قَاتَلَهُمُ اللّٰهُ ۖاَنّٰى يُؤْفَكُوْنَ","bc":"wa iżā ra`aitahum tu'jibuka ajsāmuhum, wa iy yaqụlụ tasma' liqaulihim, ka`annahum khusyubum musannadah, yaḥsabụna kulla ṣaiḥatin 'alaihim, humul-'aduwwu faḥżar-hum, qātalahumullāhu annā yu`fakụn","at":"Dan apabila engkau melihat mereka, tubuh mereka mengagumkanmu. Dan jika mereka berkata, engkau mendengarkan tutur-katanya. Mereka seakan-akan kayu yang tersandar. Mereka mengira bahwa setiap teriakan ditujukan kepada mereka. Mereka itulah musuh (yang sebenarnya), maka waspadalah terhadap mereka; Allah membinasakan mereka. Bagaimanakah mereka dapat dipalingkan (dari kebenaran)?"},{"id":"5","ar":"وَاِذَا قِيْلَ لَهُمْ تَعَالَوْا يَسْتَغْفِرْ لَكُمْ رَسُوْلُ اللّٰهِ لَوَّوْا رُءُوْسَهُمْ وَرَاَيْتَهُمْ يَصُدُّوْنَ وَهُمْ مُّسْتَكْبِرُوْنَ ","bc":"wa iżā qīla lahum ta'ālau yastagfir lakum rasụlullāhi lawwau ru`ụsahum wa ra`aitahum yaṣuddụna wa hum mustakbirụn","at":"Dan apabila dikatakan kepada mereka, “Marilah (beriman), agar Rasulullah memohonkan ampunan bagimu,” mereka membuang muka dan engkau lihat mereka berpaling dengan menyombongkan diri. "},{"id":"6","ar":"سَوَاۤءٌ عَلَيْهِمْ اَسْتَغْفَرْتَ لَهُمْ اَمْ لَمْ تَسْتَغْفِرْ لَهُمْۗ لَنْ يَّغْفِرَ اللّٰهُ لَهُمْۗ اِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ ","bc":"sawā`un 'alaihim astagfarta lahum am lam tastagfir lahum, lay yagfirallāhu lahum, innallāha lā yahdil-qaumal-fāsiqīn","at":"Sama saja bagi mereka, engkau (Muhammad) mohonkan ampunan untuk mereka atau tidak engkau mohonkan ampunan bagi mereka, Allah tidak akan mengampuni mereka; sesungguhnya Allah tidak akan memberi petunjuk kepada orang-orang yang fasik."},{"id":"7","ar":"هُمُ الَّذِيْنَ يَقُوْلُوْنَ لَا تُنْفِقُوْا عَلٰى مَنْ عِنْدَ رَسُوْلِ اللّٰهِ حَتّٰى يَنْفَضُّوْاۗ وَلِلّٰهِ خَزَاۤىِٕنُ السَّمٰوٰتِ وَالْاَرْضِۙ وَلٰكِنَّ الْمُنٰفِقِيْنَ لَا يَفْقَهُوْنَ ","bc":"humullażīna yaqụlụna lā tunfiqụ 'alā man 'inda rasụlillāhi ḥattā yanfaḍḍụ, wa lillāhi khazā`inus-samāwāti wal-arḍi wa lākinnal-munāfiqīna lā yafqahụn","at":"Mereka yang berkata (kepada orang-orang Ansar), “Janganlah kamu bersedekah kepada orang-orang (Muhajirin) yang ada di sisi Rasulullah sampai mereka bubar (meninggalkan Rasulullah).” Padahal milik Allah-lah perbendaharaan langit dan bumi, tetapi orang-orang munafik itu tidak memahami."},{"id":"8","ar":"يَقُوْلُوْنَ لَىِٕنْ رَّجَعْنَآ اِلَى الْمَدِيْنَةِ لَيُخْرِجَنَّ الْاَعَزُّ مِنْهَا الْاَذَلَّ ۗوَلِلّٰهِ الْعِزَّةُ وَلِرَسُوْلِهٖ وَلِلْمُؤْمِنِيْنَ وَلٰكِنَّ الْمُنٰفِقِيْنَ لَا يَعْلَمُوْنَ ","bc":"yaqụlụna la`ir raja'nā ilal-madīnati layukhrijannal-a'azzu min-hal-ażall, wa lillāhil-'izzatu wa lirasụlihī wa lil-mu`minīna wa lākinnal-munāfiqīna lā ya'lamụn","at":"Mereka berkata, “Sungguh, jika kita kembali ke Madinah (kembali dari perang Bani Mustalik), pastilah orang yang kuat akan mengusir orang-orang yang lemah dari sana.” Padahal kekuatan itu hanyalah bagi Allah, Rasul-Nya dan bagi orang-orang mukmin, tetapi orang-orang munafik itu tidak mengetahui."},{"id":"9","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تُلْهِكُمْ اَمْوَالُكُمْ وَلَآ اَوْلَادُكُمْ عَنْ ذِكْرِ اللّٰهِ ۚوَمَنْ يَّفْعَلْ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ","bc":"yā ayyuhallażīna āmanụ lā tul-hikum amwālukum wa lā aulādukum 'an żikrillāh, wa may yaf'al żālika fa ulā`ika humul-khāsirụn","at":"Wahai orang-orang yang beriman! Janganlah harta bendamu dan anak-anakmu melalaikan kamu dari mengingat Allah. Dan barangsiapa berbuat demikian, maka mereka itulah orang-orang yang rugi."},{"id":"10","ar":"وَاَنْفِقُوْا مِنْ مَّا رَزَقْنٰكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَ اَحَدَكُمُ الْمَوْتُ فَيَقُوْلَ رَبِّ لَوْلَآ اَخَّرْتَنِيْٓ اِلٰٓى اَجَلٍ قَرِيْبٍۚ فَاَصَّدَّقَ وَاَكُنْ مِّنَ الصّٰلِحِيْنَ ","bc":"wa anfiqụ mimmā razaqnākum ming qabli ay ya`tiya aḥadakumul-mautu fa yaqụla rabbi lau lā akhkhartanī ilā ajaling qarībin fa aṣṣaddaqa wa akum minaṣ-ṣāliḥīn","at":"Dan infakkanlah sebagian dari apa yang telah Kami berikan kepadamu sebelum kematian datang kepada salah seorang di antara kamu; lalu dia berkata (menyesali), “Ya Tuhanku, sekiranya Engkau berkenan menunda (kematian)ku sedikit waktu lagi, maka aku dapat bersedekah dan aku akan termasuk orang-orang yang saleh.”"},{"id":"11","ar":"وَلَنْ يُّؤَخِّرَ اللّٰهُ نَفْسًا اِذَا جَاۤءَ اَجَلُهَاۗ وَاللّٰهُ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ ","bc":"wa lay yu`akhkhirallāhu nafsan iżā jā`a ajaluhā, wallāhu khabīrum bimā ta'malụn","at":"Dan Allah tidak akan menunda (kematian) seseorang apabila waktu kematiannya telah datang. Dan Allah Mahateliti apa yang kamu kerjakan."}],al_mursalat=[{"id":"1","ar":"وَالْمُرْسَلٰتِ عُرْفًاۙ","bc":"wal-mursalāti 'urfā","at":"Demi (malaikat-malaikat) yang diutus untuk membawa kebaikan,"},{"id":"2","ar":"فَالْعٰصِفٰتِ عَصْفًاۙ","bc":"fal-'āṣifāti 'aṣfā","at":"dan (malaikat-malaikat) yang terbang dengan kencangnya,"},{"id":"3","ar":"وَّالنّٰشِرٰتِ نَشْرًاۙ","bc":"wan-nāsyirāti nasyrā","at":"dan (malaikat-malaikat) yang menyebarkan (rahmat Allah) dengan seluas-luasnya,"},{"id":"4","ar":"فَالْفٰرِقٰتِ فَرْقًاۙ","bc":"fal-fāriqāti farqā","at":"dan (malaikat-malaikat) yang membedakan (antara yang baik dan yang buruk) dengan sejelas-jelasnya,"},{"id":"5","ar":"فَالْمُلْقِيٰتِ ذِكْرًاۙ","bc":"fal-mulqiyāti żikrā","at":"dan (malaikat-malaikat) yang menyampaikan wahyu,"},{"id":"6","ar":"عُذْرًا اَوْ نُذْرًاۙ","bc":"'użran au nużrā","at":"untuk menolak alasan-alasan atau memberi peringatan."},{"id":"7","ar":"اِنَّمَا تُوْعَدُوْنَ لَوَاقِعٌۗ","bc":"innamā tụ'adụna lawāqi'","at":"Sungguh, apa yang dijanjikan kepadamu pasti terjadi."},{"id":"8","ar":"فَاِذَا النُّجُوْمُ طُمِسَتْۙ","bc":"fa iżan-nujụmu ṭumisat","at":"Maka apabila bintang-bintang dihapuskan,"},{"id":"9","ar":"وَاِذَا السَّمَاۤءُ فُرِجَتْۙ","bc":"wa iżas-samā`u furijat","at":"dan apabila langit terbelah,"},{"id":"10","ar":"وَاِذَا الْجِبَالُ نُسِفَتْۙ","bc":"wa iżal-jibālu nusifat","at":"dan apabila gunung-gunung dihancurkan menjadi debu,"},{"id":"11","ar":"وَاِذَا الرُّسُلُ اُقِّتَتْۗ","bc":"wa iżar-rusulu uqqitat","at":"dan apabila rasul-rasul telah ditetapkan waktunya."},{"id":"12","ar":"لِاَيِّ يَوْمٍ اُجِّلَتْۗ","bc":"li`ayyi yaumin ujjilat","at":"(Niscaya dikatakan kepada mereka), “Sampai hari apakah ditangguhkan (azab orang-orang kafir itu)?”"},{"id":"13","ar":"لِيَوْمِ الْفَصْلِۚ","bc":"liyaumil-faṣl","at":"Sampai hari keputusan."},{"id":"14","ar":"وَمَآ اَدْرٰىكَ مَا يَوْمُ الْفَصْلِۗ","bc":"wa mā adrāka mā yaumul-faṣl","at":"Dan tahukah kamu apakah hari ke-putusan itu?"},{"id":"15","ar":"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ","bc":"wailuy yauma`iżil lil-mukażżibīn","at":"Celakalah pada hari itu, bagi mereka yang mendustakan (kebenaran)."},{"id":"16","ar":"اَلَمْ نُهْلِكِ الْاَوَّلِيْنَۗ","bc":"a lam nuhlikil-awwalīn","at":"Bukankah telah Kami binasakan orang-orang yang dahulu?"},{"id":"17","ar":"ثُمَّ نُتْبِعُهُمُ الْاٰخِرِيْنَ ","bc":"ṡumma nutbi'uhumul-ākhirīn","at":"Lalu Kami susulkan (azab Kami terhadap) orang-orang yang datang kemudian."},{"id":"18","ar":"كَذٰلِكَ نَفْعَلُ بِالْمُجْرِمِيْنَ ","bc":"każālika naf'alu bil-mujrimīn","at":"Demikianlah Kami perlakukan orang-orang yang berdosa."},{"id":"19","ar":"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ","bc":"wailuy yauma`iżil lil-mukażżibīn","at":"Celakalah pada hari itu, bagi mereka yang mendustakan (kebenaran)."},{"id":"20","ar":"اَلَمْ نَخْلُقْكُّمْ مِّنْ مَّاۤءٍ مَّهِيْنٍۙ","bc":"a lam nakhlukkum mim mā`im mahīn","at":"Bukankah Kami menciptakan kamu dari air yang hina (mani),"},{"id":"21","ar":"فَجَعَلْنٰهُ فِيْ قَرَارٍ مَّكِيْنٍ","bc":"fa ja'alnāhu fī qarārim makīn","at":"kemudian Kami letakkan ia dalam tempat yang kokoh (rahim),"},{"id":"22","ar":"اِلٰى قَدَرٍ مَّعْلُوْمٍۙ","bc":"ilā qadarim ma'lụm","at":"sampai waktu yang ditentukan,"},{"id":"23","ar":"فَقَدَرْنَاۖ فَنِعْمَ الْقٰدِرُوْنَ ","bc":"fa qadarnā fa ni'mal-qādirụn","at":"lalu Kami tentukan (bentuknya), maka (Kamilah) sebaik-baik yang menentukan."},{"id":"24","ar":"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ","bc":"wailuy yauma`iżil lil-mukażżibīn","at":"Celakalah pada hari itu, bagi mereka yang mendustakan (kebenaran)."},{"id":"25","ar":"اَلَمْ نَجْعَلِ الْاَرْضَ كِفَاتًاۙ","bc":"a lam naj'alil-arḍa kifātā","at":"Bukankah Kami jadikan bumi untuk (tempat) berkumpul,"},{"id":"26","ar":"اَحْيَاۤءً وَّاَمْوَاتًاۙ","bc":"aḥyā`aw wa amwātā","at":"bagi yang masih hidup dan yang sudah mati?"},{"id":"27","ar":"وَّجَعَلْنَا فِيْهَا رَوَاسِيَ شٰمِخٰتٍ وَّاَسْقَيْنٰكُمْ مَّاۤءً فُرَاتًاۗ","bc":"wa ja'alnā fīhā rawāsiya syāmikhātiw wa asqainākum mā`an furātā","at":"Dan Kami jadikan padanya gunung-gunung yang tinggi, dan Kami beri minum kamu dengan air tawar?"},{"id":"28","ar":"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ","bc":"wailuy yauma`iżil lil-mukażżibīn","at":"Celakalah pada hari itu, bagi mereka yang mendustakan (kebenaran)."},{"id":"29","ar":"اِنْطَلِقُوْٓا اِلٰى مَا كُنْتُمْ بِهٖ تُكَذِّبُوْنَۚ","bc":"inṭaliqū ilā mā kuntum bihī tukażżibụn","at":"(Akan dikatakan), “Pergilah kamu mendapatkan apa (azab) yang dahulu kamu dustakan."},{"id":"30","ar":"اِنْطَلِقُوْٓا اِلٰى ظِلٍّ ذِيْ ثَلٰثِ شُعَبٍ ","bc":"inṭaliqū ilā ẓillin żī ṡalāṡi syu'ab","at":"Pergilah kamu mendapatkan naungan (asap api neraka) yang mempunyai tiga cabang, "},{"id":"31","ar":"لَا ظَلِيْلٍ وَّلَا يُغْنِيْ مِنَ اللَّهَبِۗ","bc":"lā ẓalīliw wa lā yugnī minal-lahab","at":"yang tidak melindungi dan tidak pula menolak nyala api neraka.” "},{"id":"32","ar":"اِنَّهَا تَرْمِيْ بِشَرَرٍ كَالْقَصْرِۚ","bc":"innahā tarmī bisyararing kal-qaṣr","at":"Sungguh, (neraka) itu menyemburkan bunga api (sebesar dan setinggi) istana, "},{"id":"33","ar":"كَاَنَّهٗ جِمٰلَتٌ صُفْرٌۗ","bc":"ka`annahụ jimālatun ṣufr","at":"seakan-akan iring-iringan unta yang kuning."},{"id":"34","ar":"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ","bc":"wailuy yauma`iżil lil-mukażżibīn","at":"Celakalah pada hari itu, bagi mereka yang mendustakan (kebenaran). "},{"id":"35","ar":"هٰذَا يَوْمُ لَا يَنْطِقُوْنَۙ","bc":"hāżā yaumu lā yanṭiqụn","at":"Inilah hari, saat mereka tidak dapat berbicara, "},{"id":"36","ar":"وَلَا يُؤْذَنُ لَهُمْ فَيَعْتَذِرُوْنَ ","bc":"wa lā yu`żanu lahum fa ya'tażirụn","at":"dan tidak diizinkan kepada mereka mengemukakan alasan agar mereka dimaafkan. "},{"id":"37","ar":"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ","bc":"wailuy yauma`iżil lil-mukażżibīn","at":"Celakalah pada hari itu, bagi mereka yang mendustakan (kebenaran). "},{"id":"38","ar":"هٰذَا يَوْمُ الْفَصْلِ جَمَعْنٰكُمْ وَالْاَوَّلِيْنَ ","bc":"hāżā yaumul-faṣli jama'nākum wal-awwalīn","at":"Inilah hari keputusan; (pada hari ini) Kami kumpulkan kamu dan orang-orang yang terdahulu. "},{"id":"39","ar":"فَاِنْ كَانَ لَكُمْ كَيْدٌ فَكِيْدُوْنِ ","bc":"fa ing kāna lakum kaidun fa kīdụn","at":"Maka jika kamu punya tipu daya, maka lakukanlah (tipu daya) itu terhadap-Ku. "},{"id":"40","ar":"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ","bc":"wailuy yauma`iżil lil-mukażżibīn","at":"Celakalah pada hari itu, bagi mereka yang mendustakan (kebenaran)."},{"id":"41","ar":"اِنَّ الْمُتَّقِيْنَ فِيْ ظِلٰلٍ وَّعُيُوْنٍۙ","bc":"innal-muttaqīna fī ẓilāliw wa 'uyụn","at":"Sungguh, orang-orang yang bertakwa berada dalam naungan (pepohonan surga yang teduh) dan (di sekitar) mata air, "},{"id":"42","ar":"وَّفَوَاكِهَ مِمَّا يَشْتَهُوْنَۗ","bc":"wa fawākiha mimmā yasytahụn","at":"dan buah-buahan yang mereka sukai. "},{"id":"43","ar":"كُلُوْا وَاشْرَبُوْا هَنِيْۤـًٔا ۢبِمَا كُنْتُمْ تَعْمَلُوْنَ ","bc":"kulụ wasyrabụ hanī`am bimā kuntum ta'malụn","at":"(Katakan kepada mereka), “Makan dan minumlah dengan rasa nikmat sebagai balasan dari apa yang telah kamu kerjakan.” "},{"id":"44","ar":"اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ ","bc":"innā każālika najzil-muḥsinīn","at":"Sungguh, demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik. "},{"id":"45","ar":"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ","bc":"wailuy yauma`iżil lil-mukażżibīn","at":"Celakalah pada hari itu, bagi mereka yang mendustakan (kebenaran). "},{"id":"46","ar":"كُلُوْا وَتَمَتَّعُوْا قَلِيْلًا اِنَّكُمْ مُّجْرِمُوْنَ ","bc":"kulụ wa tamatta'ụ qalīlan innakum mujrimụn","at":"(Katakan kepada orang-orang kafir), “Makan dan bersenang-senanglah kamu (di dunia) sebentar, sesungguhnya kamu orang-orang durhaka!”"},{"id":"47","ar":"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ","bc":"wailuy yauma`iżil lil-mukażżibīn","at":"Celakalah pada hari itu, bagi mereka yang mendustakan (kebenaran). "},{"id":"48","ar":"وَاِذَا قِيْلَ لَهُمُ ارْكَعُوْا لَا يَرْكَعُوْنَ ","bc":"wa iżā qīla lahumurka'ụ lā yarka'ụn","at":"Dan apabila dikatakan kepada mereka, “Rukuklah,” mereka tidak mau rukuk."},{"id":"49","ar":"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَ ","bc":"wailuy yauma`iżil lil-mukażżibīn","at":"Celakalah pada hari itu, bagi mereka yang mendustakan (kebenaran)! "},{"id":"50","ar":"فَبِاَيِّ حَدِيْثٍۢ بَعْدَهٗ يُؤْمِنُوْنَ ۔","bc":"fa bi`ayyi ḥadīṡim ba'dahụ yu`minụn","at":"Maka kepada ajaran manakah (selain Al-Qur'an) ini mereka akan beriman?"}],al_mutaffifin=[{"id":"1","ar":"وَيْلٌ لِّلْمُطَفِّفِيْنَۙ","bc":"wailul lil-muṭaffifīn","at":"Celakalah bagi orang-orang yang curang (dalam menakar dan menimbang)! "},{"id":"2","ar":"الَّذِيْنَ اِذَا اكْتَالُوْا عَلَى النَّاسِ يَسْتَوْفُوْنَۖ","bc":"allażīna iżaktālụ 'alan-nāsi yastaufụn","at":"(Yaitu) orang-orang yang apabila menerima takaran dari orang lain mereka minta dicukupkan, "},{"id":"3","ar":"وَاِذَا كَالُوْهُمْ اَوْ وَّزَنُوْهُمْ يُخْسِرُوْنَۗ","bc":"wa iżā kālụhum aw wazanụhum yukhsirụn","at":"dan apabila mereka menakar atau menimbang (untuk orang lain), mereka mengurangi. "},{"id":"4","ar":"اَلَا يَظُنُّ اُولٰۤىِٕكَ اَنَّهُمْ مَّبْعُوْثُوْنَۙ","bc":"alā yaẓunnu ulā`ika annahum mab'ụṡụn","at":"Tidakkah mereka itu mengira, bahwa sesungguhnya mereka akan dibangkitkan, "},{"id":"5","ar":"لِيَوْمٍ عَظِيْمٍۙ","bc":"liyaumin 'aẓīm","at":"pada suatu hari yang besar, "},{"id":"6","ar":"يَّوْمَ يَقُوْمُ النَّاسُ لِرَبِّ الْعٰلَمِيْنَۗ","bc":"yauma yaqụmun-nāsu lirabbil-'ālamīn","at":"(yaitu) pada hari (ketika) semua orang bangkit menghadap Tuhan seluruh alam."},{"id":"7","ar":"كَلَّآ اِنَّ كِتٰبَ الْفُجَّارِ لَفِيْ سِجِّيْنٍۗ","bc":"kallā inna kitābal-fujjāri lafī sijjīn","at":"Sekali-kali jangan begitu! Sesungguhnya catatan orang yang durhaka benar-benar tersimpan dalam Sijjin. "},{"id":"8","ar":"وَمَآ اَدْرٰىكَ مَا سِجِّيْنٌۗ","bc":"wa mā adrāka mā sijjīn","at":"Dan tahukah engkau apakah Sijjin itu? "},{"id":"9","ar":"كِتٰبٌ مَّرْقُوْمٌۗ","bc":"kitābum marqụm","at":"(Yaitu) Kitab yang berisi catatan (amal)."},{"id":"10","ar":"وَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَۙ","bc":"wailuy yauma`iżil lil-mukażżibīn","at":"Celakalah pada hari itu, bagi orang-orang yang mendustakan! "},{"id":"11","ar":"الَّذِيْنَ يُكَذِّبُوْنَ بِيَوْمِ الدِّيْنِۗ","bc":"allażīna yukażżibụna biyaumid-dīn","at":"(yaitu) orang-orang yang mendustakannya (hari pembalasan)."},{"id":"12","ar":"وَمَا يُكَذِّبُ بِهٖٓ اِلَّا كُلُّ مُعْتَدٍ اَثِيْمٍۙ","bc":"wa mā yukażżibu bihī illā kullu mu'tadin aṡīm","at":"Dan tidak ada yang mendustakannya (hari pembalasan) kecuali setiap orang yang melampaui batas dan berdosa,"},{"id":"13","ar":"اِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا قَالَ اَسَاطِيْرُ الْاَوَّلِيْنَۗ","bc":"iżā tutlā 'alaihi āyātunā qāla asāṭīrul-awwalīn","at":"yang apabila dibacakan kepadanya ayat-ayat Kami, dia berkata, “Itu adalah dongeng orang-orang dahulu.” "},{"id":"14","ar":"كَلَّا بَلْ ۜرَانَ عَلٰى قُلُوْبِهِمْ مَّا كَانُوْا يَكْسِبُوْنَ ","bc":"kallā bal rāna 'alā qulụbihim mā kānụ yaksibụn","at":"Sekali-kali tidak! Bahkan apa yang mereka kerjakan itu telah menutupi hati mereka. "},{"id":"15","ar":"كَلَّآ اِنَّهُمْ عَنْ رَّبِّهِمْ يَوْمَىِٕذٍ لَّمَحْجُوْبُوْنَۗ","bc":"kallā innahum 'ar rabbihim yauma`iżil lamaḥjụbụn","at":"Sekali-kali tidak! Sesungguhnya mereka pada hari itu benar-benar terhalang dari (melihat) Tuhannya. "},{"id":"16","ar":"ثُمَّ اِنَّهُمْ لَصَالُوا الْجَحِيْمِۗ","bc":"ṡumma innahum laṣālul-jaḥīm","at":"Kemudian, sesungguhnya mereka benar-benar masuk neraka. "},{"id":"17","ar":"ثُمَّ يُقَالُ هٰذَا الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَۗ","bc":"ṡumma yuqālu hāżallażī kuntum bihī tukażżibụn","at":"Kemudian, dikatakan (kepada mereka), “Inilah (azab) yang dahulu kamu dustakan.”"},{"id":"18","ar":"كَلَّآ اِنَّ كِتٰبَ الْاَبْرَارِ لَفِيْ عِلِّيِّيْنَۗ ","bc":"kallā inna kitābal-abrāri lafī 'illiyyīn","at":"Sekali-kali tidak! Sesungguhnya catatan orang-orang yang berbakti benar-benar tersimpan dalam ’Illiyyin. "},{"id":"19","ar":"وَمَآ اَدْرٰىكَ مَا عِلِّيُّوْنَۗ","bc":"wa mā adrāka mā 'illiyyụn","at":"Dan tahukah engkau apakah ’Illiyyin itu?"},{"id":"20","ar":"كِتٰبٌ مَّرْقُوْمٌۙ","bc":"kitābum marqụm","at":"(Yaitu) Kitab yang berisi catatan (amal),"},{"id":"21","ar":"يَّشْهَدُهُ الْمُقَرَّبُوْنَۗ","bc":"yasy-haduhul-muqarrabụn","at":"yang disaksikan oleh (malaikat-malaikat) yang didekatkan (kepada Allah). "},{"id":"22","ar":"اِنَّ الْاَبْرَارَ لَفِيْ نَعِيْمٍۙ","bc":"innal-abrāra lafī na'īm","at":"Sesungguhnya orang-orang yang berbakti benar-benar berada dalam (surga yang penuh) kenikmatan,"},{"id":"23","ar":"عَلَى الْاَرَاۤىِٕكِ يَنْظُرُوْنَۙ","bc":"'alal-arā`iki yanẓurụn","at":"mereka (duduk) di atas dipan-dipan melepas pandangan. "},{"id":"24","ar":"تَعْرِفُ فِيْ وُجُوْهِهِمْ نَضْرَةَ النَّعِيْمِۚ","bc":"ta'rifu fī wujụhihim naḍratan na'īm","at":"Kamu dapat mengetahui dari wajah mereka kesenangan hidup yang penuh kenikmatan."},{"id":"25","ar":"يُسْقَوْنَ مِنْ رَّحِيْقٍ مَّخْتُوْمٍۙ","bc":"yusqauna mir raḥīqim makhtụm","at":"Mereka diberi minum dari khamar murni (tidak memabukkan) yang (tempatnya) masih dilak (disegel), "},{"id":"26","ar":"خِتٰمُهٗ مِسْكٌ ۗوَفِيْ ذٰلِكَ فَلْيَتَنَافَسِ الْمُتَنَافِسُوْنَۗ","bc":"khitāmuhụ misk, wa fī żālika falyatanāfasil-mutanāfisụn","at":"laknya dari kasturi. Dan untuk yang demikian itu hendaknya orang berlomba-lomba. "},{"id":"27","ar":"وَمِزَاجُهٗ مِنْ تَسْنِيْمٍۙ","bc":"wa mizājuhụ min tasnīm","at":"Dan campurannya dari tasnim, "},{"id":"28","ar":"عَيْنًا يَّشْرَبُ بِهَا الْمُقَرَّبُوْنَۗ","bc":"'ainay yasyrabu bihal-muqarrabụn","at":"(yaitu) mata air yang diminum oleh mereka yang dekat (kepada Allah). "},{"id":"29","ar":"اِنَّ الَّذِيْنَ اَجْرَمُوْا كَانُوْا مِنَ الَّذِيْنَ اٰمَنُوْا يَضْحَكُوْنَۖ","bc":"innallażīna ajramụ kānụ minallażīna āmanụ yaḍ-ḥakụn","at":"Sesungguhnya orang-orang yang berdosa adalah mereka yang dahulu menertawakan orang-orang yang beriman."},{"id":"30","ar":"وَاِذَا مَرُّوْا بِهِمْ يَتَغَامَزُوْنَۖ","bc":"wa iżā marrụ bihim yatagāmazụn","at":"Dan apabila mereka (orang-orang yang beriman) melintas di hadapan mereka, mereka saling mengedip-ngedipkan matanya,"},{"id":"31","ar":"وَاِذَا انْقَلَبُوْٓا اِلٰٓى اَهْلِهِمُ انْقَلَبُوْا فَكِهِيْنَۖ","bc":"wa iżangqalabū ilā ahlihimungqalabụ fakihīn","at":"dan apabila kembali kepada kaumnya, mereka kembali dengan gembira ria. "},{"id":"32","ar":"وَاِذَا رَاَوْهُمْ قَالُوْٓا اِنَّ هٰٓؤُلَاۤءِ لَضَاۤلُّوْنَۙ","bc":"wa iżā ra`auhum qālū inna hā`ulā`i laḍāllụn","at":"Dan apabila mereka melihat (orang-orang mukmin), mereka mengatakan, “Sesungguhnya mereka benar-benar orang-orang sesat,”"},{"id":"33","ar":"وَمَآ اُرْسِلُوْا عَلَيْهِمْ حٰفِظِيْنَۗ","bc":"wa mā ursilụ 'alaihim ḥāfiẓīn","at":"padahal (orang-orang yang berdosa itu), mereka tidak diutus sebagai penjaga (orang-orang mukmin). "},{"id":"34","ar":"فَالْيَوْمَ الَّذِيْنَ اٰمَنُوْا مِنَ الْكُفَّارِ يَضْحَكُوْنَۙ","bc":"fal-yaumallażīna āmanụ minal-kuffāri yaḍ-ḥakụn","at":"Maka pada hari ini, orang-orang yang beriman yang menertawakan orang-orang kafir, "},{"id":"35","ar":"عَلَى الْاَرَاۤىِٕكِ يَنْظُرُوْنَۗ","bc":"'alal-arā`iki yanẓurụn","at":"mereka (duduk) di atas dipan-dipan melepas pandangan."},{"id":"36","ar":"هَلْ ثُوِّبَ الْكُفَّارُ مَا كَانُوْا يَفْعَلُوْنَ","bc":"hal ṡuwwibal-kuffāru mā kānụ yaf'alụn","at":"Apakah orang-orang kafir itu diberi balasan (hukuman) terhadap apa yang telah mereka perbuat? "}],al_muzzammil=[{"id":"1","ar":"يٰٓاَيُّهَا الْمُزَّمِّلُۙ","bc":"yā ayyuhal-muzzammil","at":"Wahai orang yang berselimut (Muhammad)!"},{"id":"2","ar":"قُمِ الَّيْلَ اِلَّا قَلِيْلًاۙ","bc":"qumil-laila illā qalīlā","at":"Bangunlah (untuk salat) pada malam hari, kecuali sebagian kecil,"},{"id":"3","ar":"نِّصْفَهٗٓ اَوِ انْقُصْ مِنْهُ قَلِيْلًاۙ","bc":"niṣfahū awingquṣ min-hu qalīlā","at":"(yaitu) separuhnya atau kurang sedikit dari itu,"},{"id":"4","ar":"اَوْ زِدْ عَلَيْهِ وَرَتِّلِ الْقُرْاٰنَ تَرْتِيْلًاۗ","bc":"au zid 'alaihi wa rattilil-qur`āna tartīlā","at":"atau lebih dari (seperdua) itu, dan bacalah Al-Qur'an itu dengan perlahan-lahan."},{"id":"5","ar":"اِنَّا سَنُلْقِيْ عَلَيْكَ قَوْلًا ثَقِيْلًا","bc":"innā sanulqī 'alaika qaulan ṡaqīlā","at":"Sesungguhnya Kami akan menurunkan perkataan yang berat kepadamu."},{"id":"6","ar":"اِنَّ نَاشِئَةَ الَّيْلِ هِيَ اَشَدُّ وَطْـًٔا وَّاَقْوَمُ قِيْلًاۗ","bc":"inna nāsyi`atal-laili hiya asyaddu waṭ`aw wa aqwamu qīlā","at":"Sungguh, bangun malam itu lebih kuat (mengisi jiwa); dan (bacaan pada waktu itu) lebih berkesan. "},{"id":"7","ar":"اِنَّ لَكَ فِى النَّهَارِ سَبْحًا طَوِيْلًاۗ","bc":"inna laka fin-nahāri sab-ḥan ṭawīlā","at":"Sesungguhnya pada siang hari engkau sangat sibuk dengan urusan-urusan yang panjang."},{"id":"8","ar":"وَاذْكُرِ اسْمَ رَبِّكَ وَتَبَتَّلْ اِلَيْهِ تَبْتِيْلًاۗ","bc":"ważkurisma rabbika wa tabattal ilaihi tabtīlā","at":"Dan sebutlah nama Tuhanmu, dan beribadahlah kepada-Nya dengan sepenuh hati."},{"id":"9","ar":"رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ لَآ اِلٰهَ اِلَّا هُوَ فَاتَّخِذْهُ وَكِيْلًا","bc":"rabbul-masyriqi wal-magribi lā ilāha illā huwa fattakhiż-hu wakīlā","at":"(Dialah) Tuhan timur dan barat, tidak ada tuhan selain Dia, maka jadikanlah Dia sebagai pelindung."},{"id":"10","ar":"وَاصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَاهْجُرْهُمْ هَجْرًا جَمِيْلًا ","bc":"waṣbir 'alā mā yaqụlụna wahjur-hum hajran jamīlā","at":"Dan bersabarlah (Muhammad) terhadap apa yang mereka katakan dan tinggalkanlah mereka dengan cara yang baik."},{"id":"11","ar":"وَذَرْنِيْ وَالْمُكَذِّبِيْنَ اُولِى النَّعْمَةِ وَمَهِّلْهُمْ قَلِيْلًا ","bc":"wa żarnī wal-mukażżibīna ulin-na'mati wa mahhil-hum qalīlā","at":"Dan biarkanlah Aku (yang bertindak) terhadap orang-orang yang mendustakan, yang memiliki segala kenikmatan hidup, dan berilah mereka penangguhan sebentar."},{"id":"12","ar":"اِنَّ لَدَيْنَآ اَنْكَالًا وَّجَحِيْمًاۙ","bc":"inna ladainā angkālaw wa jaḥīmā","at":"Sungguh, di sisi Kami ada belenggu-belenggu (yang berat) dan neraka yang menyala-nyala,"},{"id":"13","ar":"وَّطَعَامًا ذَا غُصَّةٍ وَّعَذَابًا اَلِيْمًا ","bc":"wa ṭa'āman żā guṣṣatiw wa 'ażāban alīmā","at":"dan (ada) makanan yang menyumbat di kerongkongan dan azab yang pedih."},{"id":"14","ar":"يَوْمَ تَرْجُفُ الْاَرْضُ وَالْجِبَالُ وَكَانَتِ الْجِبَالُ كَثِيْبًا مَّهِيْلًا ","bc":"yauma tarjuful-arḍu wal-jibālu wa kānatil-jibālu kaṡībam mahīlā","at":"(Ingatlah) pada hari (ketika) bumi dan gunung-gunung berguncang keras, dan menjadilah gunung-gunung itu seperti onggokan pasir yang dicurahkan."},{"id":"15","ar":"اِنَّآ اَرْسَلْنَآ اِلَيْكُمْ رَسُوْلًا ەۙ شَاهِدًا عَلَيْكُمْ كَمَآ اَرْسَلْنَآ اِلٰى فِرْعَوْنَ رَسُوْلًا ۗ","bc":"innā arsalnā ilaikum rasụlan syāhidan 'alaikum kamā arsalnā ilā fir'auna rasụlā","at":"Sesungguhnya Kami telah mengutus seorang Rasul (Muhammad) kepada kamu, yang menjadi saksi terhadapmu, sebagaimana Kami telah mengutus seorang Rasul kepada Fir‘aun."},{"id":"16","ar":"فَعَصٰى فِرْعَوْنُ الرَّسُوْلَ فَاَخَذْنٰهُ اَخْذًا وَّبِيْلًاۚ","bc":"fa 'aṣā fir'aunur-rasụla fa akhażnāhu akhżaw wabīlā","at":"Namun Fir‘aun mendurhakai Rasul itu, maka Kami siksa dia dengan siksaan yang berat."},{"id":"17","ar":"فَكَيْفَ تَتَّقُوْنَ اِنْ كَفَرْتُمْ يَوْمًا يَّجْعَلُ الْوِلْدَانَ شِيْبًاۖ","bc":"fa kaifa tattaqụna ing kafartum yaumay yaj'alul-wildāna syībā","at":"Lalu bagaimanakah kamu akan dapat menjaga dirimu jika kamu tetap kafir kepada hari yang menjadikan anak-anak beruban."},{"id":"18","ar":" ۨالسَّمَاۤءُ مُنْفَطِرٌۢ بِهٖۗ كَانَ وَعْدُهٗ مَفْعُوْلًا","bc":"as-samā`u munfaṭirum bih, kāna wa'duhụ maf'ụlā","at":"Langit terbelah pada hari itu. Janji Allah pasti terlaksana."},{"id":"19","ar":"اِنَّ هٰذِهٖ تَذْكِرَةٌ ۚ فَمَنْ شَاۤءَ اتَّخَذَ اِلٰى رَبِّهٖ سَبِيْلًا ","bc":"inna hāżihī tażkirah, fa man syā`attakhaża ilā rabbihī sabīlā","at":"Sungguh, ini adalah peringatan. Barangsiapa menghendaki, niscaya dia mengambil jalan (yang lurus) kepada Tuhannya."},{"id":"20","ar":"۞ اِنَّ رَبَّكَ يَعْلَمُ اَنَّكَ تَقُوْمُ اَدْنٰى مِنْ ثُلُثَيِ الَّيْلِ وَنِصْفَهٗ وَثُلُثَهٗ وَطَاۤىِٕفَةٌ مِّنَ الَّذِيْنَ مَعَكَۗ وَاللّٰهُ يُقَدِّرُ الَّيْلَ وَالنَّهَارَۗ عَلِمَ اَنْ لَّنْ تُحْصُوْهُ فَتَابَ عَلَيْكُمْ فَاقْرَءُوْا مَا تَيَسَّرَ مِنَ الْقُرْاٰنِۗ عَلِمَ اَنْ سَيَكُوْنُ مِنْكُمْ مَّرْضٰىۙ وَاٰخَرُوْنَ يَضْرِبُوْنَ فِى الْاَرْضِ يَبْتَغُوْنَ مِنْ فَضْلِ اللّٰهِ ۙوَاٰخَرُوْنَ يُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ ۖفَاقْرَءُوْا مَا تَيَسَّرَ مِنْهُۙ وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَقْرِضُوا اللّٰهَ قَرْضًا حَسَنًاۗ وَمَا تُقَدِّمُوْا لِاَنْفُسِكُمْ مِّنْ خَيْرٍ تَجِدُوْهُ عِنْدَ اللّٰهِ ۙهُوَ خَيْرًا وَّاَعْظَمَ اَجْرًاۗ وَاسْتَغْفِرُوا اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"inna rabbaka ya'lamu annaka taqụmu adnā min ṡuluṡayil-laili wa niṣfahụ wa ṡuluṡahụ wa ṭā`ifatum minallażīna ma'ak, wallāhu yuqaddirul-laila wan-nahār, 'alima al lan tuḥṣụhu fa tāba 'alaikum faqra`ụ mā tayassara minal-qur`ān, 'alima an sayakụnu mingkum marḍā wa ākharụna yaḍribụna fil-arḍi yabtagụna min faḍlillāhi wa ākharụna yuqātilụna fī sabīlillāhi faqra`ụ mā tayassara min-hu wa aqīmuṣ-ṣalāta wa ātuz-zakāta wa aqriḍullāha qarḍan ḥasanā, wa mā tuqaddimụ li`anfusikum min khairin tajidụhu 'indallāhi huwa khairaw wa a'ẓama ajrā, wastagfirullāh, innallāha gafụrur raḥīm","at":"Sesungguhnya Tuhanmu mengetahui bahwa engkau (Muhammad) berdiri (salat) kurang dari dua pertiga malam, atau seperdua malam atau sepertiganya dan (demikian pula) segolongan dari orang-orang yang bersamamu. Allah menetapkan ukuran malam dan siang. Allah mengetahui bahwa kamu tidak dapat menentukan batas-batas waktu itu, maka Dia memberi keringanan kepadamu, karena itu bacalah apa yang mudah (bagimu) dari Al-Qur'an; Dia mengetahui bahwa akan ada di antara kamu orang-orang yang sakit, dan yang lain berjalan di bumi mencari sebagian karunia Allah; dan yang lain berperang di jalan Allah, maka bacalah apa yang mudah (bagimu) dari Al-Qur'an dan laksanakanlah salat, tunaikanlah zakat dan berikanlah pinjaman kepada Allah pinjaman yang baik. Kebaikan apa saja yang kamu perbuat untuk dirimu niscaya kamu memperoleh (balasan)nya di sisi Allah sebagai balasan yang paling baik dan yang paling besar pahalanya. Dan mohonlah ampunan kepada Allah; sungguh, Allah Maha Pengampun, Maha Penyayang."}],al_qadr=[{"id":"1","ar":"اِنَّآ اَنْزَلْنٰهُ فِيْ لَيْلَةِ الْقَدْرِ ","bc":"innā anzalnāhu fī lailatil-qadr","at":"Sesungguhnya Kami telah menurunkannya (Al-Qur'an) pada malam qadar."},{"id":"2","ar":"وَمَآ اَدْرٰىكَ مَا لَيْلَةُ الْقَدْرِۗ","bc":"wa mā adrāka mā lailatul-qadr","at":"Dan tahukah kamu apakah malam kemuliaan itu?"},{"id":"3","ar":"لَيْلَةُ الْقَدْرِ ەۙ خَيْرٌ مِّنْ اَلْفِ شَهْرٍۗ","bc":"lailatul-qadri khairum min alfi syahr","at":"Malam kemuliaan itu lebih baik daripada seribu bulan."},{"id":"4","ar":"تَنَزَّلُ الْمَلٰۤىِٕكَةُ وَالرُّوْحُ فِيْهَا بِاِذْنِ رَبِّهِمْۚ مِنْ كُلِّ اَمْرٍۛ","bc":"tanazzalul-malā`ikatu war-rụḥu fīhā bi`iżni rabbihim, ming kulli amr","at":"Pada malam itu turun para malaikat dan Ruh (Jibril) dengan izin Tuhannya untuk mengatur semua urusan."},{"id":"5","ar":"سَلٰمٌ ۛهِيَ حَتّٰى مَطْلَعِ الْفَجْرِ","bc":"salāmun hiya ḥattā maṭla'il-fajr","at":"Sejahteralah (malam itu) sampai terbit fajar."}],al_qalam=[{"id":"1","ar":"نۤ ۚوَالْقَلَمِ وَمَا يَسْطُرُوْنَۙ","bc":"nūn, wal-qalami wa mā yasṭurụn","at":"Nun. Demi pena dan apa yang mereka tuliskan, "},{"id":"2","ar":"مَآ اَنْتَ بِنِعْمَةِ رَبِّكَ بِمَجْنُوْنٍ ","bc":"mā anta bini'mati rabbika bimajnụn","at":"dengan karunia Tuhanmu engkau (Muhammad) bukanlah orang gila. "},{"id":"3","ar":"وَاِنَّ لَكَ لَاَجْرًا غَيْرَ مَمْنُوْنٍۚ","bc":"wa inna laka la`ajran gaira mamnụn","at":"Dan sesungguhnya engkau pasti mendapat pahala yang besar yang tidak putus-putusnya. "},{"id":"4","ar":"وَاِنَّكَ لَعَلٰى خُلُقٍ عَظِيْمٍ ","bc":"wa innaka la'alā khuluqin 'aẓīm","at":"Dan sesungguhnya engkau benar-benar berbudi pekerti yang luhur. "},{"id":"5","ar":"فَسَتُبْصِرُ وَيُبْصِرُوْنَۙ","bc":"fa satubṣiru wa yubṣirụn","at":"Maka kelak engkau akan melihat dan mereka (orang-orang kafir) pun akan melihat, "},{"id":"6","ar":"بِاَيِّىكُمُ الْمَفْتُوْنُ ","bc":"bi`ayyikumul-maftụn","at":"siapa di antara kamu yang gila? "},{"id":"7","ar":"اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيْلِهٖۖ وَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ ","bc":"inna rabbaka huwa a'lamu biman ḍalla 'an sabīlihī wa huwa a'lamu bil-muhtadīn","at":"Sungguh, Tuhanmu, Dialah yang paling mengetahui siapa yang sesat dari jalan-Nya; dan Dialah yang paling mengetahui siapa orang yang mendapat petunjuk. "},{"id":"8","ar":"فَلَا تُطِعِ الْمُكَذِّبِيْنَ ","bc":"fa lā tuṭi'il-mukażżibīn","at":"Maka janganlah engkau patuhi orang-orang yang mendustakan (ayat-ayat Allah)."},{"id":"9","ar":"وَدُّوْا لَوْ تُدْهِنُ فَيُدْهِنُوْنَۚ","bc":"waddụ lau tud-hinu fa yud-hinụn","at":"Mereka menginginkan agar engkau bersikap lunak maka mereka bersikap lunak (pula). "},{"id":"10","ar":"وَلَا تُطِعْ كُلَّ حَلَّافٍ مَّهِيْنٍۙ","bc":"wa lā tuṭi' kulla ḥallāfim mahīn","at":"Dan janganlah engkau patuhi setiap orang yang suka bersumpah dan suka menghina,"},{"id":"11","ar":"هَمَّازٍ مَّشَّاۤءٍۢ بِنَمِيْمٍۙ","bc":"hammāzim masysyā`im binamīm","at":"suka mencela, yang kian ke mari menyebarkan fitnah, "},{"id":"12","ar":"مَّنَّاعٍ لِّلْخَيْرِ مُعْتَدٍ اَثِيْمٍۙ","bc":"mannā'il lil-khairi mu'tadin aṡīm","at":"yang merintangi segala yang baik, yang melampaui batas dan banyak dosa, "},{"id":"13","ar":"عُتُلٍّۢ بَعْدَ ذٰلِكَ زَنِيْمٍۙ","bc":"'utullim ba'da żālika zanīm","at":"yang bertabiat kasar, selain itu juga terkenal kejahatannya,"},{"id":"14","ar":"اَنْ كَانَ ذَا مَالٍ وَّبَنِيْنَۗ","bc":"ang kāna żā māliw wa banīn","at":"karena dia kaya dan banyak anak."},{"id":"15","ar":"اِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا قَالَ اَسَاطِيْرُ الْاَوَّلِيْنَۗ","bc":"iżā tutlā 'alaihi āyātunā qāla asāṭīrul-awwalīn","at":"Apabila ayat-ayat Kami dibacakan kepadanya, dia berkata, “(Ini adalah) dongeng-dongeng orang dahulu.” "},{"id":"16","ar":"سَنَسِمُهٗ عَلَى الْخُرْطُوْمِ ","bc":"sanasimuhụ 'alal-khurṭụm","at":"Kelak dia akan Kami beri tanda pada belalai(nya). "},{"id":"17","ar":"اِنَّا بَلَوْنٰهُمْ كَمَا بَلَوْنَآ اَصْحٰبَ الْجَنَّةِۚ اِذْ اَقْسَمُوْا لَيَصْرِمُنَّهَا مُصْبِحِيْنَۙ","bc":"innā balaunāhum kamā balaunā aṣ-ḥābal-jannah, iż aqsamụ layaṣrimunnahā muṣbiḥīn","at":"Sungguh, Kami telah menguji mereka (orang musyrik Mekah) sebagaimana Kami telah menguji pemilik-pemilik kebun, ketika mereka bersumpah pasti akan memetik (hasil)nya pada pagi hari,"},{"id":"18","ar":"وَلَا يَسْتَثْنُوْنَ ","bc":"wa lā yastaṡnụn","at":"tetapi mereka tidak menyisihkan (dengan mengucapkan, “Insya Allah”)."},{"id":"19","ar":"فَطَافَ عَلَيْهَا طَاۤىِٕفٌ مِّنْ رَّبِّكَ وَهُمْ نَاۤىِٕمُوْنَ ","bc":"fa ṭāfa 'alaihā ṭā`ifum mir rabbika wa hum nā`imụn","at":"Lalu kebun itu ditimpa bencana (yang datang) dari Tuhanmu ketika mereka sedang tidur."},{"id":"20","ar":"فَاَصْبَحَتْ كَالصَّرِيْمِۙ","bc":"fa aṣbaḥat kaṣ-ṣarīm","at":"Maka jadilah kebun itu hitam seperti malam yang gelap gulita,"},{"id":"21","ar":"فَتَنَادَوْا مُصْبِحِيْنَۙ","bc":"fa tanādau muṣbiḥīn","at":"lalu pada pagi hari mereka saling memanggil."},{"id":"22","ar":"اَنِ اغْدُوْا عَلٰى حَرْثِكُمْ اِنْ كُنْتُمْ صَارِمِيْنَ ","bc":"anigdụ 'alā ḥarṡikum ing kuntum ṣārimīn","at":"”Pergilah pagi-pagi ke kebunmu jika kamu hendak memetik hasil.” "},{"id":"23","ar":"فَانْطَلَقُوْا وَهُمْ يَتَخَافَتُوْنَۙ","bc":"fanṭalaqụ wa hum yatakhāfatụn","at":"Maka mereka pun berangkat sambil berbisik-bisik. "},{"id":"24","ar":"اَنْ لَّا يَدْخُلَنَّهَا الْيَوْمَ عَلَيْكُمْ مِّسْكِيْنٌۙ","bc":"al lā yadkhulannahal-yauma 'alaikum miskīn","at":"”Pada hari ini jangan sampai ada orang miskin masuk ke dalam kebunmu.” "},{"id":"25","ar":"وَّغَدَوْا عَلٰى حَرْدٍ قَادِرِيْنَ ","bc":"wa gadau 'alā ḥarding qādirīn","at":"Dan berangkatlah mereka pada pagi hari dengan niat menghalangi (orang-orang miskin) padahal mereka mampu (menolongnya). "},{"id":"26","ar":"فَلَمَّا رَاَوْهَا قَالُوْٓا اِنَّا لَضَاۤلُّوْنَۙ","bc":"fa lammā ra`auhā qālū innā laḍāllụn","at":"Maka ketika mereka melihat kebun itu, mereka berkata, “Sungguh, kita ini benar-benar orang-orang yang sesat,"},{"id":"27","ar":"بَلْ نَحْنُ مَحْرُوْمُوْنَ ","bc":"bal naḥnu maḥrụmụn","at":"bahkan kita tidak memperoleh apa pun,”"},{"id":"28","ar":"قَالَ اَوْسَطُهُمْ اَلَمْ اَقُلْ لَّكُمْ لَوْلَا تُسَبِّحُوْنَ ","bc":"qāla ausaṭuhum a lam aqul lakum lau lā tusabbiḥụn","at":"berkatalah seorang yang paling bijak di antara mereka, “Bukankah aku telah mengatakan kepadamu, mengapa kamu tidak bertasbih (kepada Tuhanmu).”"},{"id":"29","ar":"قَالُوْا سُبْحٰنَ رَبِّنَآ اِنَّا كُنَّا ظٰلِمِيْنَ ","bc":"qālụ sub-ḥāna rabbinā innā kunnā ẓālimīn","at":"Mereka mengucapkan, “Mahasuci Tuhan kami, sungguh, kami adalah orang-orang yang zalim.” "},{"id":"30","ar":"فَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَلَاوَمُوْنَ ","bc":"fa aqbala ba'ḍuhum 'alā ba'ḍiy yatalāwamụn","at":"Lalu mereka saling berhadapan dan saling menyalahkan."},{"id":"31","ar":"قَالُوْا يٰوَيْلَنَآ اِنَّا كُنَّا طٰغِيْنَ ","bc":"qālụ yā wailanā innā kunnā ṭāgīn","at":"Mereka berkata, “Celaka kita! Sesungguhnya kita orang-orang yang melampaui batas. "},{"id":"32","ar":"عَسٰى رَبُّنَآ اَنْ يُّبْدِلَنَا خَيْرًا مِّنْهَآ اِنَّآ اِلٰى رَبِّنَا رَاغِبُوْنَ ","bc":"'asā rabbunā ay yubdilanā khairam min-hā innā ilā rabbinā rāgibụn","at":"Mudah-mudahan Tuhan memberikan ganti kepada kita dengan (kebun) yang lebih baik daripada yang ini, sungguh, kita mengharapkan ampunan dari Tuhan kita.” "},{"id":"33","ar":"كَذٰلِكَ الْعَذَابُۗ وَلَعَذَابُ الْاٰخِرَةِ اَكْبَرُۘ لَوْ كَانُوْا يَعْلَمُوْنَ ","bc":"każālikal-'ażāb, wa la'ażābul-ākhirati akbar, lau kānụ ya'lamụn","at":"Seperti itulah azab (di dunia). Dan sungguh, azab akhirat lebih besar se-kiranya mereka mengetahui."},{"id":"34","ar":"اِنَّ لِلْمُتَّقِيْنَ عِنْدَ رَبِّهِمْ جَنّٰتِ النَّعِيْمِ ","bc":"inna lil-muttaqīna 'inda rabbihim jannātin na'īm","at":"Sungguh, bagi orang-orang yang bertakwa (disediakan) surga yang penuh kenikmatan di sisi Tuhannya."},{"id":"35","ar":"اَفَنَجْعَلُ الْمُسْلِمِيْنَ كَالْمُجْرِمِيْنَۗ","bc":"a fa naj'alul-muslimīna kal-mujrimīn","at":"Apakah patut Kami memperlakukan orang-orang Islam itu seperti orang-orang yang berdosa (orang kafir)?"},{"id":"36","ar":"مَا لَكُمْۗ كَيْفَ تَحْكُمُوْنَۚ","bc":"mā lakum, kaifa taḥkumụn","at":"Mengapa kamu (berbuat demikian)? Bagaimana kamu mengambil keputusan?"},{"id":"37","ar":"اَمْ لَكُمْ كِتٰبٌ فِيْهِ تَدْرُسُوْنَۙ","bc":"am lakum kitābun fīhi tadrusụn","at":"Atau apakah kamu mempunyai kitab (yang diturunkan Allah) yang kamu pelajari? "},{"id":"38","ar":"اِنَّ لَكُمْ فِيْهِ لَمَا تَخَيَّرُوْنَۚ","bc":"inna lakum fīhi lamā takhayyarụn","at":"sesungguhnya kamu dapat memilih apa saja yang ada di dalamnya."},{"id":"39","ar":"اَمْ لَكُمْ اَيْمَانٌ عَلَيْنَا بَالِغَةٌ اِلٰى يَوْمِ الْقِيٰمَةِۙ اِنَّ لَكُمْ لَمَا تَحْكُمُوْنَۚ","bc":"am lakum aimānun 'alainā bāligatun ilā yaumil-qiyāmati inna lakum lamā taḥkumụn","at":"Atau apakah kamu memperoleh (janji-janji yang diperkuat dengan) sumpah dari Kami, yang tetap berlaku sampai hari Kiamat; bahwa kamu dapat mengambil keputusan (sekehendakmu)? "},{"id":"40","ar":"سَلْهُمْ اَيُّهُمْ بِذٰلِكَ زَعِيْمٌۚ","bc":"sal-hum ayyuhum biżālika za'īm","at":"Tanyakanlah kepada mereka, “Siapakah di antara mereka yang bertanggung jawab terhadap (keputusan yang diambil itu)?”"},{"id":"41","ar":"اَمْ لَهُمْ شُرَكَاۤءُۚ فَلْيَأْتُوْا بِشُرَكَاۤىِٕهِمْ اِنْ كَانُوْا صٰدِقِيْنَ ","bc":"am lahum syurakā`, falya`tụ bisyurakā`ihim ing kānụ ṣādiqīn","at":"Atau apakah mereka mempunyai sekutu-sekutu? Kalau begitu hendaklah mereka mendatangkan sekutu-sekutunya jika mereka orang-orang yang benar."},{"id":"42","ar":"يَوْمَ يُكْشَفُ عَنْ سَاقٍ وَّيُدْعَوْنَ اِلَى السُّجُوْدِ فَلَا يَسْتَطِيْعُوْنَۙ","bc":"yauma yuksyafu 'an sāqiw wa yud'auna ilas-sujụdi fa lā yastaṭī'ụn","at":"(Ingatlah) pada hari ketika betis disingkapkan dan mereka diseru untuk bersujud; maka mereka tidak mampu,"},{"id":"43","ar":"خَاشِعَةً اَبْصَارُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ۗوَقَدْ كَانُوْا يُدْعَوْنَ اِلَى السُّجُوْدِ وَهُمْ سَالِمُوْنَ ","bc":"khāsyi'atan abṣāruhum tar-haquhum żillah, wa qad kānụ yud'auna ilas-sujụdi wa hum sālimụn","at":"pandangan mereka tertunduk ke bawah, diliputi kehinaan. Dan sungguh, dahulu (di dunia) mereka telah diseru untuk bersujud pada waktu mereka sehat (tetapi mereka tidak melakukan). "},{"id":"44","ar":"فَذَرْنِيْ وَمَنْ يُّكَذِّبُ بِهٰذَا الْحَدِيْثِۗ سَنَسْتَدْرِجُهُمْ مِّنْ حَيْثُ لَا يَعْلَمُوْنَۙ","bc":"fa żarnī wa may yukażżibu bihāżal-ḥadīṡ, sanastadrijuhum min ḥaiṡu lā ya'lamụn","at":"Maka serahkanlah kepada-Ku (urusannya) dan orang-orang yang mendustakan perkataan ini (Al-Qur'an). Kelak akan Kami hukum mereka berangsur-angsur dari arah yang tidak mereka ketahui, "},{"id":"45","ar":"وَاُمْلِيْ لَهُمْۗ اِنَّ كَيْدِيْ مَتِيْنٌ ","bc":"wa umlī lahum, inna kaidī matīn","at":"dan Aku memberi tenggang waktu kepada mereka. Sungguh, rencana-Ku sangat teguh. "},{"id":"46","ar":"اَمْ تَسْـَٔلُهُمْ اَجْرًا فَهُمْ مِّنْ مَّغْرَمٍ مُّثْقَلُوْنَۚ ","bc":"am tas`aluhum ajran fa hum mim magramim muṡqalụn","at":"Ataukah engkau (Muhammad) meminta imbalan kepada mereka, sehingga mereka dibebani dengan utang?"},{"id":"47","ar":"اَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُوْنَ ","bc":"am 'indahumul-gaibu fa hum yaktubụn","at":"Ataukah mereka mengetahui yang gaib, lalu mereka menuliskannya?"},{"id":"48","ar":"فَاصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تَكُنْ كَصَاحِبِ الْحُوْتِۘ اِذْ نَادٰى وَهُوَ مَكْظُوْمٌۗ","bc":"faṣbir liḥukmi rabbika wa lā takung kaṣāḥibil-ḥụt, iż nādā wa huwa makẓụm","at":"Maka bersabarlah engkau (Muhammad) terhadap ketetapan Tuhanmu, dan janganlah engkau seperti (Yunus) orang yang berada dalam (perut) ikan ketika dia berdoa dengan hati sedih."},{"id":"49","ar":"لَوْلَآ اَنْ تَدَارَكَهٗ نِعْمَةٌ مِّنْ رَّبِّهٖ لَنُبِذَ بِالْعَرَاۤءِ وَهُوَ مَذْمُوْمٌ ","bc":"lau lā an tadārakahụ ni'matum mir rabbihī lanubiża bil-'arā`i wa huwa mażmụm","at":"Sekiranya dia tidak segera mendapat nikmat dari Tuhannya, pastilah dia dicampakkan ke tanah tandus dalam keadaan tercela. "},{"id":"50","ar":"فَاجْتَبٰىهُ رَبُّهٗ فَجَعَلَهٗ مِنَ الصّٰلِحِيْنَ ","bc":"fajtabāhu rabbuhụ fa ja'alahụ minaṣ-ṣāliḥīn","at":"Lalu Tuhannya memilihnya dan menjadikannya termasuk orang yang saleh. "},{"id":"51","ar":"وَاِنْ يَّكَادُ الَّذِيْنَ كَفَرُوْا لَيُزْلِقُوْنَكَ بِاَبْصَارِهِمْ لَمَّا سَمِعُوا الذِّكْرَ وَيَقُوْلُوْنَ اِنَّهٗ لَمَجْنُوْنٌ ۘ","bc":"wa iy yakādullażīna kafarụ layuzliqụnaka bi`abṣārihim lammā sami'uż-żikra wa yaqụlụna innahụ lamajnụn","at":"Dan sungguh, orang-orang kafir itu hampir-hampir menggelincirkanmu dengan pandangan mata mereka, ketika mereka mendengar Al-Qur'an dan mereka berkata, “Dia (Muhammad) itu benar-benar orang gila.”"},{"id":"52","ar":"وَمَا هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَ ","bc":"wa mā huwa illā żikrul lil-'ālamīn","at":"Padahal (Al-Qur'an) itu tidak lain adalah peringatan bagi seluruh alam."}],al_qamar=[{"id":"1","ar":"اِقْتَرَبَتِ السَّاعَةُ وَانْشَقَّ الْقَمَرُ ","bc":"iqtarabatis-sā'atu wansyaqqal-qamar","at":" Saat (hari Kiamat) semakin dekat, bulan pun terbelah. "},{"id":"2","ar":"وَاِنْ يَّرَوْا اٰيَةً يُّعْرِضُوْا وَيَقُوْلُوْا سِحْرٌ مُّسْتَمِرٌّ","bc":"wa iy yarau āyatay yu'riḍụ wa yaqụlụ siḥrum mustamirr","at":"Dan jika mereka (orang-orang musyrikin) melihat suatu tanda (mukjizat), mereka berpaling dan berkata, “(Ini adalah) sihir yang terus menerus.” "},{"id":"3","ar":"وَكَذَّبُوْا وَاتَّبَعُوْٓا اَهْوَاۤءَهُمْ وَكُلُّ اَمْرٍ مُّسْتَقِرٌّ","bc":"wa każżabụ wattaba'ū ahwā`ahum wa kullu amrim mustaqirr","at":"Dan mereka mendustakan (Muhammad) dan mengikuti keinginannya, padahal setiap urusan telah ada ketetapannya."},{"id":"4","ar":"وَلَقَدْ جَاۤءَهُمْ مِّنَ الْاَنْبَاۤءِ مَا فِيْهِ مُزْدَجَرٌۙ ","bc":"wa laqad jā`ahum minal-ambā`i mā fīhi muzdajar","at":"Dan sungguh, telah datang kepada mereka beberapa kisah yang di dalamnya terdapat ancaman (terhadap kekafiran),"},{"id":"5","ar":"حِكْمَةٌ ۢ بَالِغَةٌ فَمَا تُغْنِ النُّذُرُۙ ","bc":"ḥikmatum bāligatun fa mā tugnin-nużur","at":"(itulah) suatu hikmah yang sempurna, tetapi peringatan-peringatan itu tidak berguna (bagi mereka),"},{"id":"6","ar":"فَتَوَلَّ عَنْهُمْ ۘ يَوْمَ يَدْعُ الدَّاعِ اِلٰى شَيْءٍ نُّكُرٍۙ ","bc":"fa tawalla 'an-hum, yauma yad'ud-dā'i ilā syai`in nukur","at":"maka berpalinglah engkau (Muhammad) dari mereka pada hari (ketika) penyeru (malaikat) mengajak (mereka) kepada sesuatu yang tidak menyenangkan (hari pembalasan), "},{"id":"7","ar":"خُشَّعًا اَبْصَارُهُمْ يَخْرُجُوْنَ مِنَ الْاَجْدَاثِ كَاَنَّهُمْ جَرَادٌ مُّنْتَشِرٌۙ ","bc":"khusysya'an abṣāruhum yakhrujụna minal-ajdāṡi ka`annahum jarādum muntasyir","at":"pandangan mereka tertunduk, ketika mereka keluar dari kuburan, seakan-akan mereka belalang yang beterbangan,"},{"id":"8","ar":"مُّهْطِعِيْنَ اِلَى الدَّاعِۗ يَقُوْلُ الْكٰفِرُوْنَ هٰذَا يَوْمٌ عَسِرٌ ","bc":"muhṭi'īna ilad-dā', yaqụlul-kāfirụna hāżā yaumun 'asir","at":"dengan patuh mereka segera datang kepada penyeru itu. Orang-orang kafir berkata, “Ini adalah hari yang sulit.” "},{"id":"9","ar":"۞ كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ فَكَذَّبُوْا عَبْدَنَا وَقَالُوْا مَجْنُوْنٌ وَّازْدُجِرَ","bc":"każżabat qablahum qaumu nụḥin fa każżabụ 'abdanā wa qālụ majnụnuw wazdujir","at":"Sebelum mereka, kaum Nuh juga telah mendustakan (rasul), maka mereka mendustakan hamba Kami (Nuh) dan mengatakan, “Dia orang gila!” Lalu diusirnya dengan ancaman. "},{"id":"10","ar":"فَدَعَا رَبَّهٗٓ اَنِّيْ مَغْلُوْبٌ فَانْتَصِرْ","bc":"fa da'ā rabbahū annī maglụbun fantaṣir","at":"Maka dia (Nuh) mengadu kepada Tuhannya, “Sesungguhnya aku telah dikalahkan, maka tolonglah (aku).”"},{"id":"11","ar":"فَفَتَحْنَآ اَبْوَابَ السَّمَاۤءِ بِمَاۤءٍ مُّنْهَمِرٍۖ ","bc":"fa fataḥnā abwābas-samā`i bimā`im mun-hamir","at":"Lalu Kami bukakan pintu-pintu langit dengan (menurunkan) air yang tercurah,"},{"id":"12","ar":"وَّفَجَّرْنَا الْاَرْضَ عُيُوْنًا فَالْتَقَى الْمَاۤءُ عَلٰٓى اَمْرٍ قَدْ قُدِرَ ۚ","bc":"wa fajjarnal-arḍa 'uyụnan faltaqal-mā`u 'alā amring qad qudir","at":"dan Kami jadikan bumi menyemburkan mata-mata air maka bertemulah (air-air) itu sehingga (meluap menimbulkan) keadaan (bencana) yang telah ditetapkan. "},{"id":"13","ar":"وَحَمَلْنٰهُ عَلٰى ذَاتِ اَلْوَاحٍ وَّدُسُرٍۙ ","bc":"wa ḥamalnāhu 'alā żāti alwāḥiw wa dusur","at":"Dan Kami angkut dia (Nuh) ke atas (kapal) yang terbuat dari papan dan pasak, "},{"id":"14","ar":"تَجْرِيْ بِاَعْيُنِنَاۚ جَزَاۤءً لِّمَنْ كَانَ كُفِرَ ","bc":"tajrī bi`a'yuninā, jazā`al limang kāna kufir","at":"yang berlayar dengan pemeliharaan (pengawasan) Kami sebagai balasan bagi orang yang telah diingkari (kaumnya)."},{"id":"15","ar":"وَلَقَدْ تَّرَكْنٰهَآ اٰيَةً فَهَلْ مِنْ مُّدَّكِرٍ","bc":"wa laqat taraknāhā āyatan fa hal mim muddakir","at":"Dan sungguh, kapal itu telah Kami jadikan sebagai tanda (pelajaran). Maka adakah orang yang mau mengambil pelajaran?"},{"id":"16","ar":"فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ ","bc":"fa kaifa kāna 'ażābī wa nużur","at":"Maka betapa dahsyatnya azab-Ku dan peringatan-Ku! "},{"id":"17","ar":"وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ ","bc":"wa laqad yassarnal-qur`āna liż-żikri fa hal mim muddakir","at":"Dan sungguh, telah Kami mudahkan Al-Qur'an untuk peringatan, maka adakah orang yang mau mengambil pelajaran?"},{"id":"18","ar":"كَذَّبَتْ عَادٌ فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ ","bc":"każżabat 'ādun fa kaifa kāna 'ażābī wa nużur","at":"Kaum ‘Ad pun telah mendustakan. Maka betapa dahsyatnya azab-Ku dan peringatan-Ku! "},{"id":"19","ar":"اِنَّآ اَرْسَلْنَا عَلَيْهِمْ رِيْحًا صَرْصَرًا فِيْ يَوْمِ نَحْسٍ مُّسْتَمِرٍّۙ ","bc":"innā arsalnā 'alaihim rīḥan ṣarṣaran fī yaumi naḥsim mustamirr","at":"Sesungguhnya Kami telah menghembuskan angin yang sangat kencang kepada mereka pada hari nahas yang terus menerus, "},{"id":"20","ar":"تَنْزِعُ النَّاسَۙ كَاَنَّهُمْ اَعْجَازُ نَخْلٍ مُّنْقَعِرٍ ","bc":"tanzi'un-nāsa ka`annahum a'jāzu nakhlim mungqa'ir","at":"yang membuat manusia bergelimpangan, mereka bagaikan pohon-pohon kurma yang tumbang dengan akar-akarnya. "},{"id":"21","ar":"فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ","bc":"fa kaifa kāna 'ażābī wa nużur","at":"Maka betapa dahsyatnya azab-Ku dan peringatan-Ku! "},{"id":"22","ar":"وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ ","bc":"wa laqad yassarnal-qur`āna liż-żikri fa hal mim muddakir","at":"Dan sungguh, telah Kami mudahkan Al-Qur'an untuk peringatan, maka adakah orang yang mau mengambil pelajaran?"},{"id":"23","ar":"كَذَّبَتْ ثَمُوْدُ بِالنُّذُرِ","bc":"każżabaṡ ṡamụdu bin-nużur","at":"Kaum Samud pun telah mendustakan peringatan itu."},{"id":"24","ar":"فَقَالُوْٓا اَبَشَرًا مِّنَّا وَاحِدًا نَّتَّبِعُهٗٓ ۙاِنَّآ اِذًا لَّفِيْ ضَلٰلٍ وَّسُعُرٍ","bc":"fa qālū abasyaram minnā wāḥidan nattabi'uhū innā iżal lafī ḍalāliw wa su'ur","at":"Maka mereka berkata, “Bagaimana kita akan mengikuti seorang manusia (biasa) di antara kita? Sungguh, kalau begitu kita benar-benar telah sesat dan gila. "},{"id":"25","ar":"ءَاُلْقِيَ الذِّكْرُ عَلَيْهِ مِنْۢ بَيْنِنَا بَلْ هُوَ كَذَّابٌ اَشِرٌ ","bc":"a ulqiyaż-żikru 'alaihi mim baininā bal huwa każżābun asyir","at":"Apakah wahyu itu diturunkan kepadanya di antara kita? Pastilah dia (Saleh) seorang yang sangat pendusta (dan) sombong.” "},{"id":"26","ar":"سَيَعْلَمُوْنَ غَدًا مَّنِ الْكَذَّابُ الْاَشِرُ ","bc":"saya'lamụna gadam manil-każżābul-asyir","at":"Kelak mereka akan mengetahui siapa yang sebenarnya sangat pendusta (dan) sombong itu."},{"id":"27","ar":"اِنَّا مُرْسِلُوا النَّاقَةِ فِتْنَةً لَّهُمْ فَارْتَقِبْهُمْ وَاصْطَبِرْۖ ","bc":"innā mursilun-nāqati fitnatal lahum fartaqib-hum waṣṭabir","at":"Sesungguhnya Kami akan mengirimkan unta betina sebagai cobaan bagi mereka, maka tunggulah mereka dan bersabarlah (Saleh)."},{"id":"28","ar":"وَنَبِّئْهُمْ اَنَّ الْمَاۤءَ قِسْمَةٌ ۢ بَيْنَهُمْۚ كُلُّ شِرْبٍ مُّحْتَضَرٌ ","bc":"wa nabbi`hum annal-mā`a qismatum bainahum, kullu syirbim muḥtaḍar","at":"Dan beritahukanlah kepada mereka bahwa air itu dibagi di antara mereka (dengan unta betina itu); setiap orang berhak mendapat giliran minum."},{"id":"29","ar":"فَنَادَوْا صَاحِبَهُمْ فَتَعَاطٰى فَعَقَرَ ","bc":"fa nādau ṣāḥibahum fa ta'āṭā fa 'aqar","at":"Maka mereka memanggil kawannya, lalu dia menangkap (unta itu) dan memotongnya. "},{"id":"30","ar":"فَكَيْفَ كَانَ عَذَابِيْ وَنُذُرِ ","bc":"fa kaifa kāna 'ażābī wa nużur","at":"Maka betapa dahsyatnya azab-Ku dan peringatan-Ku!"},{"id":"31","ar":"اِنَّآ اَرْسَلْنَا عَلَيْهِمْ صَيْحَةً وَّاحِدَةً فَكَانُوْا كَهَشِيْمِ الْمُحْتَظِرِ","bc":"innā arsalnā 'alaihim ṣaiḥataw wāḥidatan fa kānụ kahasyīmil-muḥtaẓir","at":"Kami kirimkan atas mereka satu suara yang keras mengguntur, maka jadilah mereka seperti batang-batang kering yang lapuk. "},{"id":"32","ar":"وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ ","bc":"wa laqad yassarnal-qur`āna liż-żikri fa hal mim muddakir","at":"Dan sungguh, telah Kami mudahkan Al-Qur'an untuk peringatan, maka adakah orang yang mau mengambil pelajaran?"},{"id":"33","ar":"كَذَّبَتْ قَوْمُ لُوْطٍ ۢبِالنُّذُرِ ","bc":"każżabat qaumu lụṭim bin-nużur","at":"Kaum Lut pun telah mendustakan peringatan itu. "},{"id":"34","ar":"اِنَّآ اَرْسَلْنَا عَلَيْهِمْ حَاصِبًا اِلَّآ اٰلَ لُوْطٍ ۗنَجَّيْنٰهُمْ بِسَحَرٍۙ","bc":"innā arsalnā 'alaihim ḥāṣiban illā āla lụṭ, najjaināhum bisaḥar","at":"Sesungguhnya Kami kirimkan kepada mereka badai yang membawa batu-batu (yang menimpa mereka), kecuali keluarga Lut. Kami selamatkan mereka sebelum fajar menyingsing,"},{"id":"35","ar":"نِّعْمَةً مِّنْ عِنْدِنَاۗ كَذٰلِكَ نَجْزِيْ مَنْ شَكَرَ ","bc":"ni'matam min 'indinā, każālika najzī man syakar","at":"sebagai nikmat dari Kami. Demikianlah Kami memberi balasan kepada orang-orang yang bersyukur. "},{"id":"36","ar":"وَلَقَدْ اَنْذَرَهُمْ بَطْشَتَنَا فَتَمَارَوْا بِالنُّذُرِ ","bc":"wa laqad anżarahum baṭsyatanā fa tamārau bin-nużur","at":"Dan sungguh, dia (Lut) telah memperingatkan mereka akan hukuman Kami, tetapi mereka mendustakan peringatan-Ku. "},{"id":"37","ar":"وَلَقَدْ رَاوَدُوْهُ عَنْ ضَيْفِهٖ فَطَمَسْنَآ اَعْيُنَهُمْ فَذُوْقُوْا عَذَابِيْ وَنُذُرِ ","bc":"wa laqad rāwadụhu 'an ḍaifihī fa ṭamasnā a'yunahum fa żụqụ 'ażābī wa nużur","at":"Dan sungguh, mereka telah membujuknya (agar menyerahkan) tamunya (kepada mereka), lalu Kami butakan mata mereka, maka rasakanlah azab-Ku dan peringatan-Ku!"},{"id":"38","ar":"وَلَقَدْ صَبَّحَهُمْ بُكْرَةً عَذَابٌ مُّسْتَقِرٌّۚ ","bc":"wa laqad ṣabbaḥahum bukratan 'ażābum mustaqirr","at":"Dan sungguh, pada esok harinya mereka benar-benar ditimpa azab yang tetap."},{"id":"39","ar":"فَذُوْقُوْا عَذَابِيْ وَنُذُرِ ","bc":"fa żụqụ 'ażābī wa nużur","at":"Maka rasakanlah azab-Ku dan peringatan-Ku!"},{"id":"40","ar":"وَلَقَدْ يَسَّرْنَا الْقُرْاٰنَ لِلذِّكْرِ فَهَلْ مِنْ مُّدَّكِرٍ","bc":"wa laqad yassarnal-qur`āna liż-żikri fa hal mim muddakir","at":"Dan sungguh, telah Kami mudahkan Al-Qur'an untuk peringatan, maka adakah orang yang mau mengambil pelajaran? "},{"id":"41","ar":"وَلَقَدْ جَاۤءَ اٰلَ فِرْعَوْنَ النُّذُرُۚ ","bc":"wa laqad jā`a āla fir'aunan-nużur","at":"Dan sungguh, peringatan telah datang kepada keluarga Fir‘aun. "},{"id":"42","ar":"كَذَّبُوْا بِاٰيٰتِنَا كُلِّهَا فَاَخَذْنٰهُمْ اَخْذَ عَزِيْزٍ مُّقْتَدِرٍ ","bc":"każżabụ bi`āyātinā kullihā fa`akhażnāhum akhża 'azīzim muqtadir","at":"Mereka mendustakan mukjizat-mukjizat Kami semuanya, maka Kami azab mereka dengan azab dari Yang Mahaperkasa, Mahakuasa."},{"id":"43","ar":"اَكُفَّارُكُمْ خَيْرٌ مِّنْ اُولٰۤىِٕكُمْ اَمْ لَكُمْ بَرَاۤءَةٌ فِى الزُّبُرِۚ","bc":"a kuffārukum khairum min ulā`ikum am lakum barā`atun fiz-zubur","at":"Apakah orang-orang kafir di lingkunganmu (kaum musyrikin) lebih baik dari mereka, ataukah kamu telah mempunyai jaminan kebebasan (dari azab) dalam kitab-kitab terdahulu? "},{"id":"44","ar":"اَمْ يَقُوْلُوْنَ نَحْنُ جَمِيْعٌ مُّنْتَصِرٌ ","bc":"am yaqụlụna naḥnu jamī'um muntaṣir","at":"Atau mereka mengatakan, “Kami ini golongan yang bersatu yang pasti menang.” "},{"id":"45","ar":"سَيُهْزَمُ الْجَمْعُ وَيُوَلُّوْنَ الدُّبُرَ","bc":"sayuhzamul-jam'u wa yuwallụnad-dubur","at":"Golongan itu pasti akan dikalahkan dan mereka akan mundur ke belakang. "},{"id":"46","ar":"بَلِ السَّاعَةُ مَوْعِدُهُمْ وَالسَّاعَةُ اَدْهٰى وَاَمَرُّ ","bc":"balis-sā'atu mau'iduhum was-sā'atu ad-hā wa amarr","at":"Bahkan hari Kiamat itulah hari yang dijanjikan kepada mereka dan hari Kiamat itu lebih dahsyat dan lebih pahit. "},{"id":"47","ar":"اِنَّ الْمُجْرِمِيْنَ فِيْ ضَلٰلٍ وَّسُعُرٍۘ ","bc":"innal-mujrimīna fī ḍalāliw wa su'ur","at":"Sungguh, orang-orang yang berdosa berada dalam kesesatan (di dunia) dan akan berada dalam neraka (di akhirat). "},{"id":"48","ar":"يَوْمَ يُسْحَبُوْنَ فِى النَّارِ عَلٰى وُجُوْهِهِمْۗ ذُوْقُوْا مَسَّ سَقَرَ","bc":"yauma yus-ḥabụna fin-nāri 'alā wujụhihim, żụqụ massa saqar","at":"Pada hari mereka diseret ke neraka pada wajahnya. (Dikatakan kepada mereka), “Rasakanlah sentuhan api neraka.”"},{"id":"49","ar":"اِنَّا كُلَّ شَيْءٍ خَلَقْنٰهُ بِقَدَرٍ ","bc":"innā kulla syai`in khalaqnāhu biqadar","at":"Sungguh, Kami menciptakan segala sesuatu menurut ukuran. "},{"id":"50","ar":"وَمَآ اَمْرُنَآ اِلَّا وَاحِدَةٌ كَلَمْحٍ ۢبِالْبَصَرِ ","bc":"wa mā amrunā illā wāḥidatung kalam-ḥim bil-baṣar","at":"Dan perintah Kami hanyalah (dengan) satu perkataan seperti kejapan mata. "},{"id":"51","ar":"وَلَقَدْ اَهْلَكْنَآ اَشْيَاعَكُمْ فَهَلْ مِنْ مُّدَّكِرٍ ","bc":"wa laqad ahlaknā asy-yā'akum fa hal mim muddakir","at":"Dan sungguh, telah Kami binasakan orang yang serupa dengan kamu (kekafirannya). Maka adakah orang yang mau mengambil pelajaran?"},{"id":"52","ar":"وَكُلُّ شَيْءٍ فَعَلُوْهُ فِى الزُّبُرِ ","bc":"wa kullu syai`in fa'alụhu fiz-zubur","at":"Dan segala sesuatu yang telah mereka perbuat tercatat dalam buku-buku catatan."},{"id":"53","ar":"وَكُلُّ صَغِيْرٍ وَّكَبِيْرٍ مُّسْتَطَرٌ ","bc":"wa kullu ṣagīriw wa kabīrim mustaṭar","at":"Dan segala (sesuatu) yang kecil maupun yang besar (semuanya) tertulis. "},{"id":"54","ar":"اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّنَهَرٍۙ ","bc":"innal-muttaqīna fī jannātiw wa nahar","at":"Sungguh, orang-orang yang bertakwa berada di taman-taman dan sungai-sungai,"},{"id":"55","ar":"فِيْ مَقْعَدِ صِدْقٍ عِنْدَ مَلِيْكٍ مُّقْتَدِرٍ ","bc":"fī maq'adi ṣidqin 'inda malīkim muqtadir","at":"di tempat yang disenangi di sisi Tuhan Yang Mahakuasa."}],al_qariah=[{"id":"1","ar":"اَلْقَارِعَةُۙ","bc":"al-qāri'ah","at":"Hari Kiamat,"},{"id":"2","ar":"مَا الْقَارِعَةُ ۚ","bc":"mal-qāri'ah","at":"Apakah hari Kiamat itu?"},{"id":"3","ar":"وَمَآ اَدْرٰىكَ مَا الْقَارِعَةُ ۗ","bc":"wa mā adrāka mal-qāri'ah","at":"Dan tahukah kamu apakah hari Kiamat itu?"},{"id":"4","ar":"يَوْمَ يَكُوْنُ النَّاسُ كَالْفَرَاشِ الْمَبْثُوْثِۙ","bc":"yauma yakụnun-nāsu kal-farāsyil-mabṡụṡ","at":"Pada hari itu manusia seperti laron yang beterbangan,"},{"id":"5","ar":"وَتَكُوْنُ الْجِبَالُ كَالْعِهْنِ الْمَنْفُوْشِۗ","bc":"wa takụnul-jibālu kal-'ihnil-manfụsy","at":"dan gunung-gunung seperti bulu yang dihambur-hamburkan."},{"id":"6","ar":"فَاَمَّا مَنْ ثَقُلَتْ مَوَازِينُهٗۙ","bc":"fa ammā man ṡaqulat mawāzīnuh","at":"Maka adapun orang yang berat timbangan (kebaikan)nya,"},{"id":"7","ar":"فَهُوَ فِيْ عِيْشَةٍ رَّاضِيَةٍۗ","bc":"fa huwa fī 'īsyatir rāḍiyah","at":"maka dia berada dalam kehidupan yang memuaskan (senang)."},{"id":"8","ar":"وَاَمَّا مَنْ خَفَّتْ مَوَازِيْنُهٗۙ","bc":"wa ammā man khaffat mawāzīnuh","at":"Dan adapun orang yang ringan timbangan (kebaikan)nya,"},{"id":"9","ar":"فَاُمُّهُ هَاوِيَةٌ ۗ","bc":"fa ummuhụ hāwiyah","at":"maka tempat kembalinya adalah neraka Hawiyah."},{"id":"10","ar":"وَمَآ اَدْرٰىكَ مَا هِيَهْۗ","bc":"wa mā adrāka mā hiyah","at":"Dan tahukah kamu apakah neraka Hawiyah itu?"},{"id":"11","ar":"نَارٌ حَامِيَةٌ","bc":"nārun ḥāmiyah","at":"(Yaitu) api yang sangat panas."}],al_qasas=[{"id":"1","ar":" طٰسۤمّۤ ","bc":"ṭā sīm mīm","at":"Tha Sin Mim"},{"id":"2","ar":"تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِ ","bc":"tilka āyātul-kitābil-mubīn","at":"Ini ayat-ayat Kitab (Al-Qur'an) yang jelas (dari Allah)."},{"id":"3","ar":"نَتْلُوْا عَلَيْكَ مِنْ نَّبَاِ مُوْسٰى وَفِرْعَوْنَ بِالْحَقِّ لِقَوْمٍ يُّؤْمِنُوْنَ ","bc":"natlụ 'alaika min naba`i mụsā wa fir'auna bil-ḥaqqi liqaumiy yu`minụn","at":"Kami membacakan kepadamu sebagian dari kisah Musa dan Fir‘aun dengan sebenarnya untuk orang-orang yang beriman."},{"id":"4","ar":"اِنَّ فِرْعَوْنَ عَلَا فِى الْاَرْضِ وَجَعَلَ اَهْلَهَا شِيَعًا يَّسْتَضْعِفُ طَاۤىِٕفَةً مِّنْهُمْ يُذَبِّحُ اَبْنَاۤءَهُمْ وَيَسْتَحْيٖ نِسَاۤءَهُمْ ۗاِنَّهٗ كَانَ مِنَ الْمُفْسِدِيْنَ ","bc":"inna fir'auna 'alā fil-arḍi wa ja'ala ahlahā syiya'ay yastaḍ'ifu ṭā`ifatam min-hum yużabbiḥu abnā`ahum wa yastaḥyī nisā`ahum, innahụ kāna minal-mufsidīn","at":"Sungguh, Fir‘aun telah berbuat sewenang-wenang di bumi dan menjadikan penduduknya berpecah belah, dia menindas segolongan dari mereka (Bani Israil), dia menyembelih anak laki-laki mereka dan membiarkan hidup anak perempuan mereka. Sungguh, dia (Fir‘aun) termasuk orang yang berbuat kerusakan."},{"id":"5","ar":"وَنُرِيْدُ اَنْ نَّمُنَّ عَلَى الَّذِيْنَ اسْتُضْعِفُوْا فِى الْاَرْضِ وَنَجْعَلَهُمْ اَىِٕمَّةً وَّنَجْعَلَهُمُ الْوٰرِثِيْنَ ۙ","bc":"wa nurīdu an namunna 'alallażīnastuḍ'ifụ fil-arḍi wa naj'alahum a immataw wa naj'alahumul-wāriṡīn","at":"Dan Kami hendak memberi karunia kepada orang-orang yang tertindas di bumi (Mesir) itu, dan hendak menjadikan mereka pemimpin dan menjadikan mereka orang-orang yang mewarisi (bumi),"},{"id":"6","ar":"وَنُمَكِّنَ لَهُمْ فِى الْاَرْضِ وَنُرِيَ فِرْعَوْنَ وَهَامٰنَ وَجُنُوْدَهُمَا مِنْهُمْ مَّا كَانُوْا يَحْذَرُوْنَ ","bc":"wa numakkina lahum fil-arḍi wa nuriya fir'auna wa hāmāna wa junụdahumā min-hum mā kānụ yaḥżarụn","at":"dan Kami teguhkan kedudukan mereka di bumi dan Kami perlihatkan kepada Fir‘aun dan Haman bersama bala tentaranya apa yang selalu mereka takutkan dari mereka."},{"id":"7","ar":"وَاَوْحَيْنَآ اِلٰٓى اُمِّ مُوْسٰٓى اَنْ اَرْضِعِيْهِۚ فَاِذَا خِفْتِ عَلَيْهِ فَاَلْقِيْهِ فِى الْيَمِّ وَلَا تَخَافِيْ وَلَا تَحْزَنِيْ ۚاِنَّا رَاۤدُّوْهُ اِلَيْكِ وَجَاعِلُوْهُ مِنَ الْمُرْسَلِيْنَ ","bc":"wa auḥainā ilā ummi mụsā an arḍi'īh, fa iżā khifti 'alaihi fa alqīhi fil-yammi wa lā takhāfī wa lā taḥzanī, innā rāddụhu ilaiki wa jā'ilụhu minal-mursalīn","at":"Dan Kami ilhamkan kepada ibunya Musa, “Susuilah dia (Musa), dan apabila engkau khawatir terhadapnya maka hanyutkanlah dia ke sungai (Nil). Dan janganlah engkau takut dan jangan (pula) bersedih hati, sesungguhnya Kami akan mengembalikannya kepadamu, dan menjadikannya salah seorang rasul.”"},{"id":"8","ar":"فَالْتَقَطَهٗٓ اٰلُ فِرْعَوْنَ لِيَكُوْنَ لَهُمْ عَدُوًّا وَّحَزَنًاۗ اِنَّ فِرْعَوْنَ وَهَامٰنَ وَجُنُوْدَهُمَا كَانُوْا خٰطِـِٕيْنَ ","bc":"faltaqaṭahū ālu fir'auna liyakụna lahum 'aduwwaw wa ḥazanā, inna fir'auna wa hāmāna wa junụdahumā kānụ khāṭi`īn","at":"Maka dia dipungut oleh keluarga Fir‘aun agar (kelak) dia menjadi musuh dan kesedihan bagi mereka. Sungguh, Fir‘aun dan Haman bersama bala tentaranya adalah orang-orang yang bersalah."},{"id":"9","ar":"وَقَالَتِ امْرَاَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍ لِّيْ وَلَكَۗ لَا تَقْتُلُوْهُ ۖعَسٰٓى اَنْ يَّنْفَعَنَآ اَوْ نَتَّخِذَهٗ وَلَدًا وَّهُمْ لَا يَشْعُرُوْنَ ","bc":"wa qālatimra`atu fir'auna qurratu 'ainil lī wa lak, lā taqtulụhu 'asā ay yanfa'anā au nattakhiżahụ waladaw wa hum lā yasy'urụn","at":"Dan istri Fir‘aun berkata, “(Dia) adalah penyejuk mata hati bagiku dan bagimu. Janganlah kamu membunuhnya, mudah-mudahan dia bermanfaat kepada kita atau kita ambil dia menjadi anak,” sedang mereka tidak menyadari."},{"id":"10","ar":"وَاَصْبَحَ فُؤَادُ اُمِّ مُوْسٰى فٰرِغًاۗ اِنْ كَادَتْ لَتُبْدِيْ بِهٖ لَوْلَآ اَنْ رَّبَطْنَا عَلٰى قَلْبِهَا لِتَكُوْنَ مِنَ الْمُؤْمِنِيْنَ ","bc":"wa aṣbaḥa fu`ādu ummi mụsā fārigā, ing kādat latubdī bihī lau lā ar rabaṭnā 'alā qalbihā litakụna minal-mu`minīn","at":"Dan hati ibu Musa menjadi kosong. Sungguh, hampir saja dia menyatakannya (rahasia tentang Musa), seandainya tidak Kami teguhkan hatinya, agar dia termasuk orang-orang yang beriman (kepada janji Allah)."},{"id":"11","ar":"وَقَالَتْ لِاُخْتِهٖ قُصِّيْهِۗ فَبَصُرَتْ بِهٖ عَنْ جُنُبٍ وَّهُمْ لَا يَشْعُرُوْنَ ۙ","bc":"wa qālat li`ukhtihī quṣṣīh, fa baṣurat bihī 'an junubiw wa hum lā yasy'urụn","at":"Dan dia (ibunya Musa) berkata kepada saudara perempuan Musa, “Ikutilah dia (Musa).” Maka kelihatan olehnya (Musa) dari jauh, sedang mereka tidak menyadarinya."},{"id":"12","ar":"۞ وَحَرَّمْنَا عَلَيْهِ الْمَرَاضِعَ مِنْ قَبْلُ فَقَالَتْ هَلْ اَدُلُّكُمْ عَلٰٓى اَهْلِ بَيْتٍ يَّكْفُلُوْنَهٗ لَكُمْ وَهُمْ لَهٗ نَاصِحُوْنَ ","bc":"wa ḥarramnā 'alaihil-marāḍi'a ming qablu fa qālat hal adullukum 'alā ahli baitiy yakfulụnahụ lakum wa hum lahụ nāṣiḥụn","at":"Dan Kami cegah dia (Musa) menyusu kepada perempuan-perempuan yang mau menyusui(nya) sebelum itu; maka berkatalah dia (saudaranya Musa), “Maukah aku tunjukkan kepadamu, keluarga yang akan memeliharanya untukmu dan mereka dapat berlaku baik padanya?”"},{"id":"13","ar":"فَرَدَدْنٰهُ اِلٰٓى اُمِّهٖ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ وَلِتَعْلَمَ اَنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ","bc":"fa radadnāhu ilā ummihī kai taqarra 'ainuhā wa lā taḥzana wa lita'lama anna wa'dallāhi ḥaqquw wa lākinna akṡarahum lā ya'lamụn","at":"Maka Kami kembalikan dia (Musa) kepada ibunya, agar senang hatinya dan tidak bersedih hati, dan agar dia mengetahui bahwa janji Allah adalah benar, tetapi kebanyakan mereka tidak mengetahuinya."},{"id":"14","ar":"وَلَمَّا بَلَغَ اَشُدَّهٗ وَاسْتَوٰىٓ اٰتَيْنٰهُ حُكْمًا وَّعِلْمًاۗ وَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ ","bc":"wa lammā balaga asyuddahụ wastawā ātaināhu ḥukmaw wa 'ilmā, wa każālika najzil-muḥsinīn","at":"Dan setelah dia (Musa) dewasa dan sempurna akalnya, Kami anugerahkan kepadanya hikmah (kenabian) dan pengetahuan. Dan demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik."},{"id":"15","ar":"وَدَخَلَ الْمَدِيْنَةَ عَلٰى حِيْنِ غَفْلَةٍ مِّنْ اَهْلِهَا فَوَجَدَ فِيْهَا رَجُلَيْنِ يَقْتَتِلٰنِۖ هٰذَا مِنْ شِيْعَتِهٖ وَهٰذَا مِنْ عَدُوِّهٖۚ فَاسْتَغَاثَهُ الَّذِيْ مِنْ شِيْعَتِهٖ عَلَى الَّذِيْ مِنْ عَدُوِّهٖ ۙفَوَكَزَهٗ مُوْسٰى فَقَضٰى عَلَيْهِۖ قَالَ هٰذَا مِنْ عَمَلِ الشَّيْطٰنِۗ اِنَّهٗ عَدُوٌّ مُّضِلٌّ مُّبِيْنٌ ","bc":"wa dakhalal-madīnata 'alā ḥīni gaflatim min ahlihā fa wajada fīhā rajulaini yaqtatilāni hāżā min syī'atihī wa hāżā min 'aduwwih, fastagāṡahullażī min syī'atihī 'alallażī min 'aduwwihī fa wakazahụ mụsā fa qaḍā 'alaihi qāla hāżā min 'amalisy-syaiṭān, innahụ 'aduwwum muḍillum mubīn","at":"Dan dia (Musa) masuk ke kota (Memphis) ketika penduduknya sedang lengah, maka dia mendapati di dalam kota itu dua orang laki-laki sedang berkelahi; yang seorang dari golongannya (Bani Israil) dan yang seorang (lagi) dari pihak musuhnya (kaum Fir‘aun). Orang yang dari golongannya meminta pertolongan kepadanya, untuk (mengalahkan) orang yang dari pihak musuhnya, lalu Musa meninjunya, dan matilah musuhnya itu. Dia (Musa) berkata, “Ini adalah perbuatan setan. Sungguh, dia (setan itu) adalah musuh yang jelas menyesatkan.”"},{"id":"16","ar":"قَالَ رَبِّ اِنِّيْ ظَلَمْتُ نَفْسِيْ فَاغْفِرْ لِيْ فَغَفَرَ لَهٗ ۗاِنَّهٗ هُوَ الْغَفُوْرُ الرَّحِيْمُ ","bc":"qāla rabbi innī ẓalamtu nafsī fagfir lī fa gafara lah, innahụ huwal-gafụrur-raḥīm","at":"Dia (Musa) berdoa, “Ya Tuhanku, sesungguhnya aku telah menzalimi diriku sendiri, maka ampunilah aku.” Maka Dia (Allah) mengampuninya. Sungguh, Allah, Dialah Yang Maha Pengampun, Maha Penyayang."},{"id":"17","ar":"قَالَ رَبِّ بِمَآ اَنْعَمْتَ عَلَيَّ فَلَنْ اَكُوْنَ ظَهِيْرًا لِّلْمُجْرِمِيْنَ ","bc":"qāla rabbi bimā an'amta 'alayya fa lan akụna ẓahīral lil-mujrimīn","at":"Dia (Musa) berkata, “Ya Tuhanku! Demi nikmat yang telah Engkau anugerahkan kepadaku, maka aku tidak akan menjadi penolong bagi orang-orang yang berdosa.”"},{"id":"18","ar":"فَاَصْبَحَ فِى الْمَدِيْنَةِ خَاۤىِٕفًا يَّتَرَقَّبُ فَاِذَا الَّذِى اسْتَنْصَرَهٗ بِالْاَمْسِ يَسْتَصْرِخُهٗ ۗقَالَ لَهٗ مُوْسٰٓى اِنَّكَ لَغَوِيٌّ مُّبِيْنٌ ","bc":"fa aṣbaḥa fil-madīnati khā`ifay yataraqqabu fa iżallażistanṣarahụ bil-amsi yastaṣrikhuh, qāla lahụ mụsā innaka lagawiyyum mubīn","at":"Karena itu, dia (Musa) menjadi ketakutan berada di kota itu sambil menunggu (akibat perbuatannya), tiba-tiba orang yang kemarin meminta pertolongan berteriak meminta pertolongan kepadanya. Musa berkata kepadanya, “Engkau sungguh, orang yang nyata-nyata sesat.”"},{"id":"19","ar":"فَلَمَّآ اَنْ اَرَادَ اَنْ يَّبْطِشَ بِالَّذِيْ هُوَ عَدُوٌّ لَّهُمَاۙ قَالَ يٰمُوْسٰٓى اَتُرِيْدُ اَنْ تَقْتُلَنِيْ كَمَا قَتَلْتَ نَفْسًاۢ بِالْاَمْسِۖ اِنْ تُرِيْدُ اِلَّآ اَنْ تَكُوْنَ جَبَّارًا فِى الْاَرْضِ وَمَا تُرِيْدُ اَنْ تَكُوْنَ مِنَ الْمُصْلِحِيْنَ ","bc":"fa lammā an arāda ay yabṭisya billażī huwa 'aduwwul lahumā qāla yā mụsā a turīdu an taqtulanī kamā qatalta nafsam bil-amsi in turīdu illā an takụna jabbāran fil-arḍi wa mā turīdu an takụna minal-muṣliḥīn","at":"Maka ketika dia (Musa) hendak memukul dengan keras orang yang menjadi musuh mereka berdua, dia (musuhnya) berkata, “Wahai Musa! Apakah engkau bermaksud membunuhku, sebagaimana kemarin engkau membunuh seseorang? Engkau hanya bermaksud menjadi orang yang berbuat sewenang-wenang di negeri (ini), dan engkau tidak bermaksud menjadi salah seorang dari orang-orang yang mengadakan perdamaian.”"},{"id":"20","ar":"وَجَاۤءَ رَجُلٌ مِّنْ اَقْصَى الْمَدِيْنَةِ يَسْعٰىۖ قَالَ يٰمُوْسٰٓى اِنَّ الْمَلَاَ يَأْتَمِرُوْنَ بِكَ لِيَقْتُلُوْكَ فَاخْرُجْ اِنِّيْ لَكَ مِنَ النّٰصِحِيْنَ ","bc":"wa jā`a rajulum min aqṣal-madīnati yas'ā qāla yā mụsā innal-mala`a ya`tamirụna bika liyaqtulụka fakhruj innī laka minan-nāṣiḥīn","at":"Dan seorang laki-laki datang bergegas dari ujung kota seraya berkata, “Wahai Musa! Sesungguhnya para pembesar negeri sedang berunding tentang engkau untuk membunuhmu, maka keluarlah (dari kota ini), sesungguhnya aku termasuk orang-orang yang memberi nasihat kepadamu.”"},{"id":"21","ar":"فَخَرَجَ مِنْهَا خَاۤىِٕفًا يَّتَرَقَّبُ ۖقَالَ رَبِّ نَجِّنِيْ مِنَ الْقَوْمِ الظّٰلِمِيْنَ ","bc":"fa kharaja min-hā khā`ifay yataraqqabu qāla rabbi najjinī minal-qaumiẓ-ẓālimīn","at":"Maka keluarlah dia (Musa) dari kota itu dengan rasa takut, waspada (kalau ada yang menyusul atau menangkapnya), dia berdoa, “Ya Tuhanku, selamatkanlah aku dari orang-orang yang zalim itu.”"},{"id":"22","ar":"وَلَمَّا تَوَجَّهَ تِلْقَاۤءَ مَدْيَنَ قَالَ عَسٰى رَبِّيْٓ اَنْ يَّهْدِيَنِيْ سَوَاۤءَ السَّبِيْلِ ","bc":"wa lammā tawajjaha tilqā`a madyana qāla 'asā rabbī ay yahdiyanī sawā`as-sabīl","at":"Dan ketika dia menuju ke arah negeri Madyan dia berdoa lagi, “Mudah-mudahan Tuhanku memimpin aku ke jalan yang benar.”"},{"id":"23","ar":"وَلَمَّا وَرَدَ مَاۤءَ مَدْيَنَ وَجَدَ عَلَيْهِ اُمَّةً مِّنَ النَّاسِ يَسْقُوْنَ ەۖ وَوَجَدَ مِنْ دُوْنِهِمُ امْرَاَتَيْنِ تَذُوْدٰنِۚ قَالَ مَا خَطْبُكُمَا ۗقَالَتَا لَا نَسْقِيْ حَتّٰى يُصْدِرَ الرِّعَاۤءُ وَاَبُوْنَا شَيْخٌ كَبِيْرٌ","bc":"wa lammā warada mā`a madyana wajada 'alaihi ummatam minan-nāsi yasqụna wa wajada min dụnihimumra`ataini tażụdān, qāla mā khaṭbukumā, qālatā lā nasqī ḥattā yuṣdirar-ri'ā`u wa abụnā syaikhung kabīr","at":"Dan ketika dia sampai di sumber air negeri Madyan, dia menjumpai di sana sekumpulan orang yang sedang memberi minum (ternaknya), dan dia menjumpai di belakang orang banyak itu, dua orang perempuan sedang menghambat (ternaknya). Dia (Musa) berkata, “Apakah maksudmu (dengan berbuat begitu)?” Kedua (perempuan) itu menjawab, “Kami tidak dapat memberi minum (ternak kami), sebelum penggembala-penggembala itu memulangkan (ternaknya), sedang ayah kami adalah orang tua yang telah lanjut usianya.”"},{"id":"24","ar":"فَسَقٰى لَهُمَا ثُمَّ تَوَلّٰىٓ اِلَى الظِّلِّ فَقَالَ رَبِّ اِنِّيْ لِمَآ اَنْزَلْتَ اِلَيَّ مِنْ خَيْرٍ فَقِيْرٌ ","bc":"fa saqā lahumā ṡumma tawallā ilaẓ-ẓilli fa qāla rabbi innī limā anzalta ilayya min khairin faqīr","at":"Maka dia (Musa) memberi minum (ternak) kedua perempuan itu, kemudian dia kembali ke tempat yang teduh lalu berdoa, “Ya Tuhanku, sesungguhnya aku sangat memerlukan sesuatu kebaikan (makanan) yang Engkau turunkan kepadaku.”"},{"id":"25","ar":"فَجَاۤءَتْهُ اِحْدٰىهُمَا تَمْشِيْ عَلَى اسْتِحْيَاۤءٍ ۖقَالَتْ اِنَّ اَبِيْ يَدْعُوْكَ لِيَجْزِيَكَ اَجْرَ مَا سَقَيْتَ لَنَاۗ فَلَمَّا جَاۤءَهٗ وَقَصَّ عَلَيْهِ الْقَصَصَۙ قَالَ لَا تَخَفْۗ نَجَوْتَ مِنَ الْقَوْمِ الظّٰلِمِيْنَ ","bc":"fa jā`at-hu iḥdāhumā tamsyī 'alastiḥyā`ing qālat inna abī yad'ụka liyajziyaka ajra mā saqaita lanā, fa lammā jā`ahụ wa qaṣṣa 'alaihil-qaṣaṣa qāla lā takhaf, najauta minal-qaumiẓ-ẓālimīn","at":"Kemudian datanglah kepada Musa salah seorang dari kedua perempuan itu berjalan dengan malu-malu, dia berkata, “Sesungguhnya ayahku mengundangmu untuk memberi balasan sebagai imbalan atas (kebaikan)mu memberi minum (ternak) kami.” Ketika (Musa) mendatangi ayahnya dan dia menceritakan kepadanya kisah (mengenai dirinya), dia berkata, “Janganlah engkau takut! Engkau telah selamat dari orang-orang yang zalim itu.”"},{"id":"26","ar":"قَالَتْ اِحْدٰىهُمَا يٰٓاَبَتِ اسْتَأْجِرْهُ ۖاِنَّ خَيْرَ مَنِ اسْتَأْجَرْتَ الْقَوِيُّ الْاَمِيْنُ ","bc":"qālat iḥdāhumā yā abatista`jir-hu inna khaira manista`jartal-qawiyyul-amīn","at":"Dan salah seorang dari kedua (perempuan) itu berkata, “Wahai ayahku! Jadikanlah dia sebagai pekerja (pada kita), sesungguhnya orang yang paling baik yang engkau ambil sebagai pekerja (pada kita) ialah orang yang kuat dan dapat dipercaya.”"},{"id":"27","ar":"قَالَ اِنِّيْٓ اُرِيْدُ اَنْ اُنْكِحَكَ اِحْدَى ابْنَتَيَّ هٰتَيْنِ عَلٰٓى اَنْ تَأْجُرَنِيْ ثَمٰنِيَ حِجَجٍۚ فَاِنْ اَتْمَمْتَ عَشْرًا فَمِنْ عِنْدِكَۚ وَمَآ اُرِيْدُ اَنْ اَشُقَّ عَلَيْكَۗ سَتَجِدُنِيْٓ اِنْ شَاۤءَ اللّٰهُ مِنَ الصّٰلِحِيْنَ ","bc":"qāla innī urīdu an ungkiḥaka iḥdabnatayya hātaini 'alā an ta`juranī ṡamāniya ḥijaj, fa in atmamta 'asyran fa min 'indik, wa mā urīdu an asyuqqa 'alaīk, satajidunī in syā`allāhu minaṣ-ṣāliḥīn","at":"Dia (Syekh Madyan) berkata, “Sesungguhnya aku bermaksud ingin menikahkan engkau dengan salah seorang dari kedua anak perempuanku ini, dengan ketentuan bahwa engkau bekerja padaku selama delapan tahun dan jika engkau sempurnakan sepuluh tahun maka itu adalah (suatu kebaikan) darimu, dan aku tidak bermaksud memberatkan engkau. Insya Allah engkau akan mendapatiku termasuk orang yang baik.”"},{"id":"28","ar":"قَالَ ذٰلِكَ بَيْنِيْ وَبَيْنَكَۗ اَيَّمَا الْاَجَلَيْنِ قَضَيْتُ فَلَا عُدْوَانَ عَلَيَّۗ وَاللّٰهُ عَلٰى مَا نَقُوْلُ وَكِيْلٌ","bc":"qāla żālika bainī wa bainak, ayyamal-ajalaini qaḍaitu fa lā 'udwāna 'alayy, wallāhu 'alā mā naqụlu wakīl","at":"Dia (Musa) berkata, “Itu (perjanjian) antara aku dan engkau. Yang mana saja dari kedua waktu yang ditentukan itu yang aku sempurnakan, maka tidak ada tuntutan (tambahan) atas diriku (lagi). Dan Allah menjadi saksi atas apa yang kita ucapkan.”"},{"id":"29","ar":"۞ فَلَمَّا قَضٰى مُوْسَى الْاَجَلَ وَسَارَ بِاَهْلِهٖٓ اٰنَسَ مِنْ جَانِبِ الطُّوْرِ نَارًاۗ قَالَ لِاَهْلِهِ امْكُثُوْٓا اِنِّيْٓ اٰنَسْتُ نَارًا لَّعَلِّيْٓ اٰتِيْكُمْ مِّنْهَا بِخَبَرٍ اَوْ جَذْوَةٍ مِّنَ النَّارِ لَعَلَّكُمْ تَصْطَلُوْنَ","bc":"fa lammā qaḍā mụsal-ajala wa sāra bi`ahlihī ānasa min jānibiṭ-ṭụri nārā, qāla li`ahlihimkuṡū innī ānastu nāral-la'allī ātīkum min-hā bikhabarin au jażwatim minan-nāri la'allakum taṣṭalụn","at":"Maka ketika Musa telah menyelesaikan waktu yang ditentukan itu dan dia berangkat dengan keluarganya, dia melihat api di lereng gunung. Dia berkata kepada keluarganya, “Tunggulah (di sini), sesungguhnya aku melihat api, mudah-mudahan aku dapat membawa suatu berita kepadamu dari (tempat) api itu atau (membawa) sepercik api, agar kamu dapat menghangatkan badan.”"},{"id":"30","ar":"فَلَمَّآ اَتٰىهَا نُوْدِيَ مِنْ شَاطِئِ الْوَادِ الْاَيْمَنِ فِى الْبُقْعَةِ الْمُبٰرَكَةِ مِنَ الشَّجَرَةِ اَنْ يّٰمُوْسٰٓى اِنِّيْٓ اَنَا اللّٰهُ رَبُّ الْعٰلَمِيْنَ ۙ","bc":"fa lammā atāhā nụdiya min syāṭi`il-wādil-aimani fil-buq'atil-mubārakati minasy-syajarati ay yā mụsā innī anallāhu rabbul-'ālamīn","at":"Maka ketika dia (Musa) sampai ke (tempat) api itu, dia diseru dari (arah) pinggir sebelah kanan lembah, dari sebatang pohon, di sebidang tanah yang diberkahi, “Wahai Musa! Sungguh, Aku adalah Allah, Tuhan seluruh alam!"},{"id":"31","ar":"وَاَنْ اَلْقِ عَصَاكَ ۗفَلَمَّا رَاٰهَا تَهْتَزُّ كَاَنَّهَا جَاۤنٌّ وَّلّٰى مُدْبِرًا وَّلَمْ يُعَقِّبْۗ يٰمُوْسٰٓى اَقْبِلْ وَلَا تَخَفْۗ اِنَّكَ مِنَ الْاٰمِنِيْنَ ","bc":"wa an alqi 'aṣāk, fa lammā ra`āhā tahtazzu ka`annahā jānnuw wallā mudbiraw wa lam yu'aqqib, yā mụsā aqbil wa lā takhaf, innaka minal-āminīn","at":"Dan lemparkanlah tongkatmu.” Maka ketika dia (Musa) melihatnya bergerak-gerak seakan-akan seekor ular yang (gesit), dia lari berbalik ke belakang tanpa menoleh. (Allah berfirman), “Wahai Musa! Kemarilah dan jangan takut. Sesungguhnya engkau termasuk orang yang aman."},{"id":"32","ar":"اُسْلُكْ يَدَكَ فِيْ جَيْبِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍ ۖوَّاضْمُمْ اِلَيْكَ جَنَاحَكَ مِنَ الرَّهْبِ فَذٰنِكَ بُرْهَانٰنِ مِنْ رَّبِّكَ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖۗ اِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ ","bc":"usluk yadaka fī jaibika takhruj baiḍā`a min gairi sū`iw waḍmum ilaika janāḥaka minar-rahbi fażānika bur-hānāni mir rabbika ilā fir'auna wa mala`ih, innahum kānụ qauman fāsiqīn","at":"Masukkanlah tanganmu ke leher bajumu, dia akan keluar putih (bercahaya) tanpa cacat, dan dekapkanlah kedua tanganmu ke dadamu apabila ketakutan. Itulah dua mukjizat dari Tuhanmu (yang akan engkau pertunjukkan) kepada Fir‘aun dan para pembesarnya. Sungguh, mereka adalah orang-orang fasik.” "},{"id":"33","ar":"قَالَ رَبِّ اِنِّيْ قَتَلْتُ مِنْهُمْ نَفْسًا فَاَخَافُ اَنْ يَّقْتُلُوْنِ ","bc":"qāla rabbi innī qataltu min-hum nafsan fa akhāfu ay yaqtulụn","at":"Dia (Musa) berkata, “Ya Tuhanku, sungguh aku telah membunuh seorang dari golongan mereka, sehingga aku takut mereka akan membunuhku. "},{"id":"34","ar":"وَاَخِيْ هٰرُوْنُ هُوَ اَفْصَحُ مِنِّيْ لِسَانًا فَاَرْسِلْهُ مَعِيَ رِدْءًا يُّصَدِّقُنِيْٓ ۖاِنِّيْٓ اَخَافُ اَنْ يُّكَذِّبُوْنِ ","bc":"wa akhī hārụnu huwa afṣaḥu minnī lisānan fa arsil-hu ma'iya rid`ay yuṣaddiqunī innī akhāfu ay yukażżibụn","at":"Dan saudaraku Harun, dia lebih fasih lidahnya daripada aku, maka utuslah dia bersamaku sebagai pembantuku untuk membenarkan (perkataan)ku; sungguh, aku takut mereka akan mendustakanku.” "},{"id":"35","ar":"قَالَ سَنَشُدُّ عَضُدَكَ بِاَخِيْكَ وَنَجْعَلُ لَكُمَا سُلْطٰنًا فَلَا يَصِلُوْنَ اِلَيْكُمَا ۛبِاٰيٰتِنَا ۛ اَنْتُمَا وَمَنِ اتَّبَعَكُمَا الْغٰلِبُوْنَ ","bc":"qāla sanasyuddu 'aḍudaka bi`akhīka wa naj'alu lakumā sulṭānan fa lā yaṣilụna ilaikumā bi`āyātinā, antumā wa manittaba'akumal-gālibụn","at":"Dia (Allah) berfirman, “Kami akan menguatkan engkau (membantumu) dengan saudaramu, dan Kami berikan kepadamu berdua kekuasaan yang besar, maka mereka tidak akan dapat mencapaimu; (berangkatlah kamu berdua) dengan membawa mukjizat Kami, kamu berdua dan orang yang mengikuti kamu yang akan menang.” "},{"id":"36","ar":"فَلَمَّا جَاۤءَهُمْ مُّوْسٰى بِاٰيٰتِنَا بَيِّنٰتٍ قَالُوْا مَا هٰذَآ اِلَّا سِحْرٌ مُّفْتَرًىۙ وَّمَا سَمِعْنَا بِهٰذَا فِيْٓ اٰبَاۤىِٕنَا الْاَوَّلِيْنَ ","bc":"fa lammā jā`ahum mụsā bi`āyātinā bayyināting qālụ mā hāżā illā siḥrum muftaraw wa mā sami'nā bihāżā fī ābā`inal-awwalīn","at":"Maka ketika Musa datang kepada mereka dengan (membawa) mukjizat Kami yang nyata, mereka berkata, “Ini hanyalah sihir yang dibuat-buat, dan kami tidak pernah mendengar (yang seperti) ini pada nenek moyang kami dahulu.” "},{"id":"37","ar":"وَقَالَ مُوْسٰى رَبِّيْٓ اَعْلَمُ بِمَنْ جَاۤءَ بِالْهُدٰى مِنْ عِنْدِهٖ وَمَنْ تَكُوْنُ لَهٗ عَاقِبَةُ الدَّارِۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ ","bc":"wa qāla mụsā rabbī a'lamu biman jā`a bil-hudā min 'indihī wa man takụnu lahụ 'āqibatud-dār, innahụ lā yufliḥuẓ-ẓālimụn","at":"Dan dia (Musa) menjawab, “Tuhanku lebih mengetahui siapa yang (pantas) membawa petunjuk dari sisi-Nya dan siapa yang akan mendapat kesudahan (yang baik) di akhirat. Sesungguhnya orang-orang yang zalim tidak akan mendapat kemenangan.” "},{"id":"38","ar":"وَقَالَ فِرْعَوْنُ يٰٓاَيُّهَا الْمَلَاُ مَا عَلِمْتُ لَكُمْ مِّنْ اِلٰهٍ غَيْرِيْۚ فَاَوْقِدْ لِيْ يٰهَامٰنُ عَلَى الطِّيْنِ فَاجْعَلْ لِّيْ صَرْحًا لَّعَلِّيْٓ اَطَّلِعُ اِلٰٓى اِلٰهِ مُوْسٰىۙ وَاِنِّيْ لَاَظُنُّهٗ مِنَ الْكٰذِبِيْنَ ","bc":"wa qāla fir'aunu yā ayyuhal-mala`u mā 'alimtu lakum min ilāhin gairī, fa auqid lī yā hāmānu 'alaṭ-ṭīni faj'al lī ṣarḥal la'allī aṭṭali'u ilā ilāhi mụsā wa innī la`aẓunnuhụ minal-kāżibīn","at":"Dan Fir‘aun berkata, “Wahai para pembesar kaumku! Aku tidak mengetahui ada Tuhan bagimu selain aku. Maka bakarlah tanah liat untukku wahai Haman (untuk membuat batu bata), kemudian buatkanlah bangunan yang tinggi untukku agar aku dapat naik melihat Tuhannya Musa, dan aku yakin bahwa dia termasuk pendusta.” "},{"id":"39","ar":"وَاسْتَكْبَرَ هُوَ وَجُنُوْدُهٗ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَظَنُّوْٓا اَنَّهُمْ اِلَيْنَا لَا يُرْجَعُوْنَ ","bc":"wastakbara huwa wa junụduhụ fil-arḍi bigairil-ḥaqqi wa ẓannū annahum ilainā lā yurja'ụn","at":"Dan dia (Fir‘aun) dan bala tentaranya berlaku sombong, di bumi tanpa alasan yang benar, dan mereka mengira bahwa mereka tidak akan dikembalikan kepada Kami."},{"id":"40","ar":"فَاَخَذْنٰهُ وَجُنُوْدَهٗ فَنَبَذْنٰهُمْ فِى الْيَمِّ ۚفَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظّٰلِمِيْنَ ","bc":"fa akhażnāhu wa junụdahụ fa nabażnāhum fil-yamm, fanẓur kaifa kāna 'āqibatuẓ-ẓālimīn","at":"Maka Kami siksa dia (Fir‘aun) dan bala tentaranya, lalu Kami lemparkan mereka ke dalam laut. Maka perhatikanlah bagaimana kesudahan orang yang zalim."},{"id":"41","ar":"وَجَعَلْنٰهُمْ اَىِٕمَّةً يَّدْعُوْنَ اِلَى النَّارِۚ وَيَوْمَ الْقِيٰمَةِ لَا يُنْصَرُوْنَ ","bc":"wa ja'alnāhum a immatay yad'ụna ilan-nār, wa yaumal-qiyāmati lā yunṣarụn","at":"Dan Kami jadikan mereka para pemimpin yang mengajak (manusia) ke neraka dan pada hari Kiamat mereka tidak akan ditolong."},{"id":"42","ar":"وَاَتْبَعْنٰهُمْ فِيْ هٰذِهِ الدُّنْيَا لَعْنَةً ۚوَيَوْمَ الْقِيٰمَةِ هُمْ مِّنَ الْمَقْبُوْحِيْنَ","bc":"wa atba'nāhum fī hāżihid-dun-yā la'nah, wa yaumal-qiyāmati hum minal-maqbụḥīn","at":"Dan Kami susulkan laknat kepada mereka di dunia ini; sedangkan pada hari Kiamat mereka termasuk orang-orang yang dijauhkan (dari rahmat Allah)."},{"id":"43","ar":"وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ مِنْۢ بَعْدِ مَآ اَهْلَكْنَا الْقُرُوْنَ الْاُوْلٰى بَصَاۤىِٕرَ لِلنَّاسِ وَهُدًى وَّرَحْمَةً لَّعَلَّهُمْ يَتَذَكَّرُوْنَ ","bc":"wa laqad ātainā mụsal-kitāba mim ba'di mā ahlaknal-qurụnal-ụlā baṣā`ira lin-nāsi wa hudaw wa raḥmatal la'allahum yatażakkarụn","at":"Dan sungguh, telah Kami berikan kepada Musa Kitab (Taurat) setelah Kami binasakan umat-umat terdahulu, untuk menjadi pelita bagi manusia dan petunjuk serta rahmat, agar mereka mendapat pelajaran."},{"id":"44","ar":"وَمَا كُنْتَ بِجَانِبِ الْغَرْبِيِّ اِذْ قَضَيْنَآ اِلٰى مُوْسَى الْاَمْرَ وَمَا كُنْتَ مِنَ الشّٰهِدِيْنَ ۙ","bc":"wa mā kunta bijānibil-garbiyyi iż qaḍainā ilā mụsal-amra wa mā kunta minasy-syāhidīn","at":"Dan engkau (Muhammad) tidak berada di sebelah barat (lembah suci Tuwa) ketika Kami menyampaikan perintah kepada Musa, dan engkau tidak (pula) termasuk orang-orang yang menyaksikan (kejadian itu)."},{"id":"45","ar":"وَلٰكِنَّآ اَنْشَأْنَا قُرُوْنًا فَتَطَاوَلَ عَلَيْهِمُ الْعُمُرُۚ وَمَا كُنْتَ ثَاوِيًا فِيْٓ اَهْلِ مَدْيَنَ تَتْلُوْا عَلَيْهِمْ اٰيٰتِنَاۙ وَلٰكِنَّا كُنَّا مُرْسِلِيْنَ ","bc":"wa lākinnā ansya`nā qurụnan fa taṭāwala 'alaihimul-'umur, wa mā kunta ṡāwiyan fī ahli madyana tatlụ 'alaihim āyātinā wa lākinnā kunnā mursilīn","at":"Tetapi Kami telah menciptakan beberapa umat, dan telah berlalu atas mereka masa yang panjang, dan engkau (Muhammad) tidak tinggal bersama-sama penduduk Madyan dengan membacakan ayat-ayat Kami kepada mereka, tetapi Kami telah mengutus rasul-rasul."},{"id":"46","ar":"وَمَا كُنْتَ بِجَانِبِ الطُّوْرِ اِذْ نَادَيْنَا وَلٰكِنْ رَّحْمَةً مِّنْ رَّبِّكَ لِتُنْذِرَ قَوْمًا مَّآ اَتٰىهُمْ مِّنْ نَّذِيْرٍ مِّنْ قَبْلِكَ لَعَلَّهُمْ يَتَذَكَّرُوْنَ ","bc":"wa mā kunta bijānibiṭ-ṭụri iż nādainā wa lākir raḥmatam mir rabbika litunżira qaumam mā atāhum min nażīrim ming qablika la'allahum yatażakkarụn","at":"Dan engkau (Muhammad) tidak berada di dekat Tur (gunung) ketika Kami menyeru (Musa), tetapi (Kami utus engkau) sebagai rahmat dari Tuhanmu, agar engkau memberi peringatan kepada kaum (Quraisy) yang tidak didatangi oleh pemberi peringatan sebelum engkau agar mereka mendapat pelajaran."},{"id":"47","ar":"وَلَوْلَآ اَنْ تُصِيْبَهُمْ مُّصِيْبَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ فَيَقُوْلُوْا رَبَّنَا لَوْلَآ اَرْسَلْتَ اِلَيْنَا رَسُوْلًا فَنَتَّبِعَ اٰيٰتِكَ وَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ ","bc":"walau lā an tuṣībahum muṣībatum bimā qaddamat aidīhim fa yaqụlụ rabbanā lau lā arsalta ilainā rasụlan fa nattabi'a āyātika wa nakụna minal-mu`minīn","at":"Dan agar mereka tidak mengatakan ketika azab menimpa mereka disebabkan apa yang mereka kerjakan, “Ya Tuhan kami, mengapa Engkau tidak mengutus seorang rasul kepada kami, agar kami mengikuti ayat-ayat Engkau dan termasuk orang mukmin.”"},{"id":"48","ar":"فَلَمَّا جَاۤءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوْا لَوْلَآ اُوْتِيَ مِثْلَ مَآ اُوْتِيَ مُوْسٰىۗ اَوَلَمْ يَكْفُرُوْا بِمَآ اُوْتِيَ مُوْسٰى مِنْ قَبْلُۚ قَالُوْا سِحْرٰنِ تَظَاهَرَاۗ وَقَالُوْٓا اِنَّا بِكُلٍّ كٰفِرُوْنَ ","bc":"fa lammā jā`ahumul-ḥaqqu min 'indinā qālụ lau lā ụtiya miṡla mā ụtiya mụsā, a wa lam yakfurụ bimā ụtiya mụsā ming qabl, qālụ siḥrāni taẓāharā, wa qālū innā bikulling kāfirụn","at":"Maka ketika telah datang kepada mereka kebenaran (Al-Qur'an) dari sisi Kami, mereka berkata, “Mengapa tidak diberikan kepadanya (Muhammad) seperti apa yang telah diberikan kepada Musa dahulu?” Bukankah mereka itu telah ingkar (juga) kepada apa yang diberikan kepada Musa dahulu? Mereka dahulu berkata, “(Musa dan Harun adalah) dua pesihir yang bantu-membantu.” Dan mereka (juga) berkata, “Sesungguhnya kami sama sekali tidak mempercayai masing-masing mereka itu.”"},{"id":"49","ar":"قُلْ فَأْتُوْا بِكِتٰبٍ مِّنْ عِنْدِ اللّٰهِ هُوَ اَهْدٰى مِنْهُمَآ اَتَّبِعْهُ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"qul fa`tụ bikitābim min 'indillāhi huwa ahdā min-humā attabi'hu ing kuntum ṣādiqīn","at":"Katakanlah (Muhammad), “Datangkanlah olehmu sebuah kitab dari sisi Allah yang kitab itu lebih memberi petunjuk daripada keduanya (Taurat dan Al-Qur'an), niscaya aku mengikutinya, jika kamu orang yang benar.”"},{"id":"50","ar":"فَاِنْ لَّمْ يَسْتَجِيْبُوْا لَكَ فَاعْلَمْ اَنَّمَا يَتَّبِعُوْنَ اَهْوَاۤءَهُمْۗ وَمَنْ اَضَلُّ مِمَّنِ اتَّبَعَ هَوٰىهُ بِغَيْرِ هُدًى مِّنَ اللّٰهِ ۗاِنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ","bc":"fa il lam yastajībụ laka fa'lam annamā yattabi'ụna ahwā`ahum, wa man aḍallu mim manittaba'a hawāhu bigairi hudam minallāh, innallāha lā yahdil-qaumaẓ-ẓālimīn","at":"Maka jika mereka tidak menjawab (tantanganmu), maka ketahuilah bahwa mereka hanyalah mengikuti keinginan mereka. Dan siapakah yang lebih sesat daripada orang yang mengikuti keinginannya tanpa mendapat petunjuk dari Allah sedikit pun? Sungguh, Allah tidak memberi petunjuk kepada orang-orang yang zalim."},{"id":"51","ar":"۞ وَلَقَدْ وَصَّلْنَا لَهُمُ الْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُوْنَ ۗ ","bc":"wa laqad waṣṣalnā lahumul-qaula la'allahum yatażakkarụn","at":"Dan sungguh, Kami telah menyampaikan perkataan ini (Al-Qur'an) kepada mereka agar mereka selalu mengingatnya."},{"id":"52","ar":"اَلَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ مِنْ قَبْلِهٖ هُمْ بِهٖ يُؤْمِنُوْنَ ","bc":"allażīna ātaināhumul-kitāba ming qablihī hum bihī yu`minụn","at":"Orang-orang yang telah Kami berikan kepada mereka Al-Kitab sebelum Al-Qur'an, mereka beriman (pula) kepadanya (Al-Qur'an)."},{"id":"53","ar":"وَاِذَا يُتْلٰى عَلَيْهِمْ قَالُوْٓا اٰمَنَّا بِهٖٓ اِنَّهُ الْحَقُّ مِنْ رَّبِّنَآ اِنَّا كُنَّا مِنْ قَبْلِهٖ مُسْلِمِيْنَ ","bc":"wa iżā yutlā 'alaihim qālū āmannā bihī innahul-ḥaqqu mir rabbinā innā kunnā ming qablihī muslimīn","at":"Dan apabila (Al-Qur'an) dibacakan kepada mereka, mereka berkata, “Kami beriman kepadanya, sesungguhnya (Al-Qur'an) itu adalah suatu kebenaran dari Tuhan kami. Sungguh, sebelumnya kami adalah orang muslim.”"},{"id":"54","ar":"اُولٰۤىِٕكَ يُؤْتَوْنَ اَجْرَهُمْ مَّرَّتَيْنِ بِمَا صَبَرُوْا وَيَدْرَءُوْنَ بِالْحَسَنَةِ السَّيِّئَةَ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ","bc":"ulā`ika yu`tauna ajrahum marrataini bimā ṣabarụ wa yadra`ụna bil-ḥasanatis-sayyi`ata wa mimmā razaqnāhum yunfiqụn","at":"Mereka itu diberi pahala dua kali (karena beriman kepada Taurat dan Al-Qur'an) disebabkan kesabaran mereka, dan mereka menolak kejahatan dengan kebaikan, dan menginfakkan sebagian dari rezeki yang telah Kami berikan kepada mereka."},{"id":"55","ar":"وَاِذَا سَمِعُوا اللَّغْوَ اَعْرَضُوْا عَنْهُ وَقَالُوْا لَنَآ اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْ ۖسَلٰمٌ عَلَيْكُمْ ۖ لَا نَبْتَغِى الْجٰهِلِيْنَ","bc":"wa iżā sami'ul-lagwa a'raḍụ 'an-hu wa qālụ lanā a'mālunā wa lakum a'mālukum salāmun 'alaikum lā nabtagil-jāhilīn","at":"Dan apabila mereka mendengar perkataan yang buruk, mereka berpaling darinya dan berkata, “Bagi kami amal-amal kami dan bagimu amal-amal kamu, semoga selamatlah kamu, kami tidak ingin (bergaul) dengan orang-orang bodoh.”"},{"id":"56","ar":"اِنَّكَ لَا تَهْدِيْ مَنْ اَحْبَبْتَ وَلٰكِنَّ اللّٰهَ يَهْدِيْ مَنْ يَّشَاۤءُ ۚوَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ ","bc":"innaka lā tahdī man aḥbabta wa lākinnallāha yahdī may yasyā`, wa huwa a'lamu bil-muhtadīn","at":"Sungguh, engkau (Muhammad) tidak dapat memberi petunjuk kepada orang yang engkau kasihi, tetapi Allah memberi petunjuk kepada orang yang Dia kehendaki, dan Dia lebih mengetahui orang-orang yang mau menerima petunjuk."},{"id":"57","ar":"وَقَالُوْٓا اِنْ نَّتَّبِعِ الْهُدٰى مَعَكَ نُتَخَطَّفْ مِنْ اَرْضِنَاۗ اَوَلَمْ نُمَكِّنْ لَّهُمْ حَرَمًا اٰمِنًا يُّجْبٰٓى اِلَيْهِ ثَمَرٰتُ كُلِّ شَيْءٍ رِّزْقًا مِّنْ لَّدُنَّا وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ ","bc":"wa qālū in nattabi'il-hudā ma'aka nutakhaṭṭaf min arḍinā, a wa lam numakkil lahum ḥaraman āminay yujbā ilaihi ṡamarātu kulli syai`ir rizqam mil ladunnā wa lākinna akṡarahum lā ya'lamụn","at":"Dan mereka berkata, “Jika kami mengikuti petunjuk bersama engkau, niscaya kami akan diusir dari negeri kami.” (Allah berfirman) Bukankah Kami telah meneguhkan kedudukan mereka dalam tanah haram (tanah suci) yang aman, yang didatangkan ke tempat itu buah-buahan dari segala macam (tumbuh-tumbuhan) sebagai rezeki (bagimu) dari sisi Kami? Tetapi kebanyakan mereka tidak mengetahui."},{"id":"58","ar":"وَكَمْ اَهْلَكْنَا مِنْ قَرْيَةٍ ۢ بَطِرَتْ مَعِيْشَتَهَا ۚفَتِلْكَ مَسٰكِنُهُمْ لَمْ تُسْكَنْ مِّنْۢ بَعْدِهِمْ اِلَّا قَلِيْلًاۗ وَكُنَّا نَحْنُ الْوَارِثِيْنَ ","bc":"wa kam ahlaknā ming qaryatim baṭirat ma'īsyatahā, fa tilka masākinuhum lam tuskam mim ba'dihim illā qalīlā, wa kunnā naḥnul-wāriṡīn","at":"Dan betapa banyak (penduduk) negeri yang sudah bersenang-senang dalam kehidupannya yang telah Kami binasakan, maka itulah tempat kediaman mereka yang tidak didiami (lagi) setelah mereka, kecuali sebagian kecil. Dan Kamilah yang mewarisinya.” "},{"id":"59","ar":"وَمَا كَانَ رَبُّكَ مُهْلِكَ الْقُرٰى حَتّٰى يَبْعَثَ فِيْٓ اُمِّهَا رَسُوْلًا يَّتْلُوْا عَلَيْهِمْ اٰيٰتِنَاۚ وَمَا كُنَّا مُهْلِكِى الْقُرٰىٓ اِلَّا وَاَهْلُهَا ظٰلِمُوْنَ ","bc":"wa mā kāna rabbuka muhlikal-qurā ḥattā yab'aṡa fī ummihā rasụlay yatlụ 'alaihim āyātinā, wa mā kunnā muhlikil-qurā illā wa ahluhā ẓālimụn","at":"Dan Tuhanmu tidak akan membinasakan negeri-negeri, sebelum Dia mengutus seorang rasul di ibukotanya yang membacakan ayat-ayat Kami kepada mereka; dan tidak pernah (pula) Kami membinasakan (penduduk) negeri; kecuali penduduknya melakukan kezaliman. "},{"id":"60","ar":"وَمَآ اُوْتِيْتُمْ مِّنْ شَيْءٍ فَمَتَاعُ الْحَيٰوةِ الدُّنْيَا وَزِيْنَتُهَا ۚوَمَا عِنْدَ اللّٰهِ خَيْرٌ وَّاَبْقٰىۗ اَفَلَا تَعْقِلُوْنَ","bc":"wa mā ụtītum min syai`in fa matā'ul-ḥayātid-dun-yā wa zīnatuhā, wa mā 'indallāhi khairuw wa abqā, a fa lā ta'qilụn","at":"Dan apa saja (kekayaan, jabatan, keturunan) yang diberikan kepada kamu, maka itu adalah kesenangan hidup duniawi dan perhiasannya; sedang apa yang di sisi Allah adalah lebih baik dan lebih kekal. Tidakkah kamu mengerti?"},{"id":"61","ar":"اَفَمَنْ وَّعَدْنٰهُ وَعْدًا حَسَنًا فَهُوَ لَاقِيْهِ كَمَنْ مَّتَّعْنٰهُ مَتَاعَ الْحَيٰوةِ الدُّنْيَا ثُمَّ هُوَ يَوْمَ الْقِيٰمَةِ مِنَ الْمُحْضَرِيْنَ ","bc":"a fa maw wa'adnāhu wa'dan ḥasanan fa huwa lāqīhi kamam matta'nāhu matā'al-ḥayātid-dun-yā ṡumma huwa yaumal-qiyāmati minal-muḥḍarīn","at":"Maka apakah sama orang yang Kami janjikan kepadanya suatu janji yang baik (surga) lalu dia memperolehnya, dengan orang yang Kami berikan kepadanya kesenangan hidup duniawi; kemudian pada hari Kiamat dia termasuk orang-orang yang diseret (ke dalam neraka)?"},{"id":"62","ar":"وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ ","bc":"wa yauma yunādīhim fa yaqụlu aina syurakā`iyallażīna kuntum taz'umụn","at":"Dan (ingatlah) pada hari ketika Dia (Allah) menyeru mereka dan berfirman, “Di manakah sekutu-sekutu-Ku yang dahulu kamu sangka?”"},{"id":"63","ar":"قَالَ الَّذِيْنَ حَقَّ عَلَيْهِمُ الْقَوْلُ رَبَّنَا هٰٓؤُلَاۤءِ الَّذِيْنَ اَغْوَيْنَاۚ اَغْوَيْنٰهُمْ كَمَا غَوَيْنَاۚ تَبَرَّأْنَآ اِلَيْكَ مَا كَانُوْٓا اِيَّانَا يَعْبُدُوْنَ ","bc":"qālallażīna ḥaqqa 'alaihimul-qaulu rabbanā hā`ulā`illażīna agwainā, agwaināhum kamā gawainā, tabarra`nā ilaika mā kānū iyyānā ya'budụn","at":"Orang-orang yang sudah pasti akan mendapatkan hukuman berkata, “Ya Tuhan kami, mereka inilah orang-orang yang kami sesatkan itu; kami telah menyesatkan mereka sebagaimana kami (sendiri) sesat, kami menyatakan kepada Engkau berlepas diri (dari mereka), mereka sekali-kali tidak menyembah kami.” "},{"id":"64","ar":"وَقِيْلَ ادْعُوْا شُرَكَاۤءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيْبُوْا لَهُمْ ۗوَرَاَوُا الْعَذَابَۚ لَوْ اَنَّهُمْ كَانُوْا يَهْتَدُوْنَ","bc":"wa qīlad'ụ syurakā`akum fa da'auhum fa lam yastajībụ lahum, wa ra`awul-'ażāb, lau annahum kānụ yahtadụn","at":"Dan dikatakan (kepada mereka), “Serulah sekutu-sekutumu,” lalu mereka menyerunya, tetapi yang diseru tidak menyambutnya, dan mereka melihat azab. (Mereka itu berkeinginan) sekiranya mereka dahulu menerima petunjuk. "},{"id":"65","ar":"وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ مَاذَآ اَجَبْتُمُ الْمُرْسَلِيْنَ ","bc":"wa yauma yunādīhim fa yaqụlu māżā ajabtumul-mursalīn","at":"Dan (ingatlah) pada hari ketika Dia (Allah) menyeru mereka, dan berfirman, “Apakah jawabanmu terhadap para rasul?”"},{"id":"66","ar":"فَعَمِيَتْ عَلَيْهِمُ الْاَنْۢبَاۤءُ يَوْمَىِٕذٍ فَهُمْ لَا يَتَسَاۤءَلُوْنَ ","bc":"fa 'amiyat 'alaihimul-ambā`u yauma`iżin fa hum lā yatasā`alụn","at":"Maka gelaplah bagi mereka segala macam alasan pada hari itu, karena itu mereka tidak saling bertanya. "},{"id":"67","ar":"فَاَمَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا فَعَسٰٓى اَنْ يَّكُوْنَ مِنَ الْمُفْلِحِيْنَ ","bc":"fa ammā man tāba wa āmana wa 'amila ṣāliḥan fa 'asā ay yakụna minal-mufliḥīn","at":"Maka adapun orang yang bertobat dan beriman, serta mengerjakan kebajikan, maka mudah-mudahan dia termasuk orang yang beruntung. "},{"id":"68","ar":"وَرَبُّكَ يَخْلُقُ مَا يَشَاۤءُ وَيَخْتَارُ ۗمَا كَانَ لَهُمُ الْخِيَرَةُ ۗسُبْحٰنَ اللّٰهِ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ","bc":"wa rabbuka yakhluqu mā yasyā`u wa yakhtār, mā kāna lahumul-khiyarah, sub-ḥānallāhi wa ta'ālā 'ammā yusyrikụn","at":"Dan Tuhanmu menciptakan dan memilih apa yang Dia kehendaki. Bagi mereka (manusia) tidak ada pilihan. Mahasuci Allah dan Mahatinggi Dia dari apa yang mereka persekutukan."},{"id":"69","ar":"وَرَبُّكَ يَعْلَمُ مَا تُكِنُّ صُدُوْرُهُمْ وَمَا يُعْلِنُوْنَ ","bc":"wa rabbuka ya'lamu mā tukinnu ṣudụruhum wa mā yu'linụn","at":"Dan Tuhanmu mengetahui apa yang disembunyikan dalam dada mereka dan apa yang mereka nyatakan. "},{"id":"70","ar":"وَهُوَ اللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ ۗ لَهُ الْحَمْدُ فِى الْاُوْلٰى وَالْاٰخِرَةِ ۖوَلَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُوْنَ ","bc":"wa huwallāhu lā ilāha illā huw, lahul-ḥamdu fil-ụlā wal-ākhirati wa lahul-ḥukmu wa ilaihi turja'ụn","at":"Dan Dialah Allah, tidak ada tuhan (yang berhak disembah) selain Dia, segala puji bagi-Nya di dunia dan di akhirat, dan bagi-Nya segala penentuan dan kepada-Nya kamu dikembalikan."},{"id":"71","ar":"قُلْ اَرَءَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ الَّيْلَ سَرْمَدًا اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِضِيَاۤءٍ ۗ اَفَلَا تَسْمَعُوْنَ ","bc":"qul a ra`aitum in ja'alallāhu 'alaikumul-laila sarmadan ilā yaumil-qiyāmati man ilāhun gairullāhi ya`tīkum biḍiyā`, a fa lā tasma'ụn","at":"Katakanlah (Muhammad), “Bagaimana pendapatmu, jika Allah menjadikan untukmu malam itu terus-menerus sampai hari Kiamat. Siapakah tuhan selain Allah yang akan mendatangkan sinar terang kepadamu? Apakah kamu tidak mendengar?”"},{"id":"72","ar":"قُلْ اَرَءَيْتُمْ اِنْ جَعَلَ اللّٰهُ عَلَيْكُمُ النَّهَارَ سَرْمَدًا اِلٰى يَوْمِ الْقِيٰمَةِ مَنْ اِلٰهٌ غَيْرُ اللّٰهِ يَأْتِيْكُمْ بِلَيْلٍ تَسْكُنُوْنَ فِيْهِ ۗ اَفَلَا تُبْصِرُوْنَ ","bc":"qul a ra`aitum in ja'alallāhu 'alaikumun-nahāra sarmadan ilā yaumil-qiyāmati man ilāhun gairullāhi ya`tīkum bilailin taskunụna fīh, a fa lā tubṣirụn","at":"Katakanlah (Muhammad), “Bagaimana pendapatmu, jika Allah menjadikan untukmu siang itu terus-menerus sampai hari Kiamat. Siapakah tuhan selain Allah yang akan mendatangkan malam kepadamu sebagai waktu istirahatmu? Apakah kamu tidak memperhatikan?”"},{"id":"73","ar":"وَمِنْ رَّحْمَتِهٖ جَعَلَ لَكُمُ الَّيْلَ وَالنَّهَارَ لِتَسْكُنُوْا فِيْهِ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ ","bc":"wa mir raḥmatihī ja'ala lakumul-laila wan-nahāra litaskunụ fīhi wa litabtagụ min faḍlihī wa la'allakum tasykurụn","at":"Dan adalah karena rahmat-Nya, Dia jadikan untukmu malam dan siang, agar kamu beristirahat pada malam hari dan agar kamu mencari sebagian karunia-Nya (pada siang hari) dan agar kamu bersyukur kepada-Nya."},{"id":"74","ar":"وَيَوْمَ يُنَادِيْهِمْ فَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تَزْعُمُوْنَ ","bc":"wa yauma yunādīhim fa yaqụlu aina syurakā`iyallażīna kuntum taz'umụn","at":"Dan (ingatlah) pada hari ketika Dia (Allah) menyeru mereka, dan berfirman, “Di manakah sekutu-sekutu-Ku yang dahulu kamu sangka?”"},{"id":"75","ar":"وَنَزَعْنَا مِنْ كُلِّ اُمَّةٍ شَهِيْدًا فَقُلْنَا هَاتُوْا بُرْهَانَكُمْ فَعَلِمُوْٓا اَنَّ الْحَقَّ لِلّٰهِ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ","bc":"wa naza'nā ming kulli ummatin syahīdan fa qulnā hātụ burhānakum fa 'alimū annal-ḥaqqa lillāhi wa ḍalla 'an-hum mā kānụ yaftarụn","at":"Dan Kami datangkan dari setiap umat seorang saksi, lalu Kami katakan, “Kemukakanlah bukti kebenaranmu,” maka tahulah mereka bahwa yang hak (kebenaran) itu milik Allah dan lenyaplah dari mereka apa yang dahulu mereka ada-adakan."},{"id":"76","ar":"۞ اِنَّ قَارُوْنَ كَانَ مِنْ قَوْمِ مُوْسٰى فَبَغٰى عَلَيْهِمْ ۖوَاٰتَيْنٰهُ مِنَ الْكُنُوْزِ مَآ اِنَّ مَفَاتِحَهٗ لَتَنُوْۤاُ بِالْعُصْبَةِ اُولِى الْقُوَّةِ اِذْ قَالَ لَهٗ قَوْمُهٗ لَا تَفْرَحْ اِنَّ اللّٰهَ لَا يُحِبُّ الْفَرِحِيْنَ ","bc":"inna qārụna kāna ming qaumi mụsā fa bagā 'alaihim wa ātaināhu minal-kunụzi mā inna mafātiḥahụ latanū`u bil-'uṣbati ulil-quwwati iż qāla lahụ qaumuhụ lā tafraḥ innallāha lā yuḥibbul-fariḥīn","at":"Sesungguhnya Karun termasuk kaum Musa, tetapi dia berlaku zalim terhadap mereka, dan Kami telah menganugerahkan kepadanya perbendaharaan harta yang kunci-kuncinya sungguh berat dipikul oleh sejumlah orang yang kuat-kuat. (Ingatlah) ketika kaumnya berkata kepadanya, “Janganlah engkau terlalu bangga. Sungguh, Allah tidak menyukai orang yang membanggakan diri.”"},{"id":"77","ar":"وَابْتَغِ فِيْمَآ اٰتٰىكَ اللّٰهُ الدَّارَ الْاٰخِرَةَ وَلَا تَنْسَ نَصِيْبَكَ مِنَ الدُّنْيَا وَاَحْسِنْ كَمَآ اَحْسَنَ اللّٰهُ اِلَيْكَ وَلَا تَبْغِ الْفَسَادَ فِى الْاَرْضِ ۗاِنَّ اللّٰهَ لَا يُحِبُّ الْمُفْسِدِيْنَ","bc":"wabtagi fīmā ātākallāhud-dāral-ākhirata wa lā tansa naṣībaka minad-dun-yā wa aḥsing kamā aḥsanallāhu ilaika wa lā tabgil-fasāda fil-arḍ, innallāha lā yuḥibbul-mufsidīn","at":"Dan carilah (pahala) negeri akhirat dengan apa yang telah dianugerahkan Allah kepadamu, tetapi janganlah kamu lupakan bagianmu di dunia dan berbuatbaiklah (kepada orang lain) sebagaimana Allah telah berbuat baik kepadamu, dan janganlah kamu berbuat kerusakan di bumi. Sungguh, Allah tidak menyukai orang yang berbuat kerusakan."},{"id":"78","ar":"قَالَ اِنَّمَآ اُوْتِيْتُهٗ عَلٰى عِلْمٍ عِنْدِيْۗ اَوَلَمْ يَعْلَمْ اَنَّ اللّٰهَ قَدْ اَهْلَكَ مِنْ قَبْلِهٖ مِنَ الْقُرُوْنِ مَنْ هُوَ اَشَدُّ مِنْهُ قُوَّةً وَّاَكْثَرُ جَمْعًا ۗوَلَا يُسْـَٔلُ عَنْ ذُنُوْبِهِمُ الْمُجْرِمُوْنَ ","bc":"qāla innamā ụtītuhụ 'alā 'ilmin 'indī, a wa lam ya'lam annallāha qad ahlaka ming qablihī minal-qurụni man huwa asyaddu min-hu quwwataw wa akṡaru jam'ā, wa lā yus`alu 'an żunụbihimul-mujrimụn","at":"Dia (Karun) berkata, “Sesungguhnya aku diberi (harta itu), semata-mata karena ilmu yang ada padaku.” Tidakkah dia tahu, bahwa Allah telah membinasakan umat-umat sebelumnya yang lebih kuat daripadanya, dan lebih banyak mengumpulkan harta? Dan orang-orang yang berdosa itu tidak perlu ditanya tentang dosa-dosa mereka."},{"id":"79","ar":"فَخَرَجَ عَلٰى قَوْمِهٖ فِيْ زِيْنَتِهٖ ۗقَالَ الَّذِيْنَ يُرِيْدُوْنَ الْحَيٰوةَ الدُّنْيَا يٰلَيْتَ لَنَا مِثْلَ مَآ اُوْتِيَ قَارُوْنُۙ اِنَّهٗ لَذُوْ حَظٍّ عَظِيْمٍ ","bc":"fa kharaja 'alā qaumihī fī zīnatih, qālallażīna yurīdụnal-ḥayātad-dun-yā yā laita lanā miṡla mā ụtiya qārụnu innahụ lażụ haẓẓin 'aẓīm","at":"Maka keluarlah dia (Karun) kepada kaumnya dengan kemegahannya. Orang-orang yang menginginkan kehidupan dunia berkata, “Mudah-mudahan kita mempunyai harta kekayaan seperti apa yang telah diberikan kepada Karun, sesungguhnya dia benar-benar mempunyai keberuntungan yang besar.”"},{"id":"80","ar":"وَقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ وَيْلَكُمْ ثَوَابُ اللّٰهِ خَيْرٌ لِّمَنْ اٰمَنَ وَعَمِلَ صَالِحًا ۚوَلَا يُلَقّٰىهَآ اِلَّا الصّٰبِرُوْنَ ","bc":"wa qālallażīna ụtul-'ilma wailakum ṡawābullāhi khairul liman āmana wa 'amila ṣāliḥā, wa lā yulaqqāhā illaṣ-ṣābirụn","at":"Tetapi orang-orang yang dianugerahi ilmu berkata, “Celakalah kamu! Ketahuilah, pahala Allah lebih baik bagi orang-orang yang beriman dan mengerjakan kebajikan, dan (pahala yang besar) itu hanya diperoleh oleh orang-orang yang sabar.”"},{"id":"81","ar":"فَخَسَفْنَا بِهٖ وَبِدَارِهِ الْاَرْضَ ۗفَمَا كَانَ لَهٗ مِنْ فِئَةٍ يَّنْصُرُوْنَهٗ مِنْ دُوْنِ اللّٰهِ ۖوَمَا كَانَ مِنَ الْمُنْتَصِرِيْنَ ","bc":"fa khasafnā bihī wa bidārihil-arḍ, fa mā kāna lahụ min fi`atiy yanṣurụnahụ min dụnillāhi wa mā kāna minal-muntaṣirīn","at":"Maka Kami benamkan dia (Karun) bersama rumahnya ke dalam bumi. Maka tidak ada baginya satu golongan pun yang akan menolongnya selain Allah, dan dia tidak termasuk orang-orang yang dapat membela diri."},{"id":"82","ar":"وَاَصْبَحَ الَّذِيْنَ تَمَنَّوْا مَكَانَهٗ بِالْاَمْسِ يَقُوْلُوْنَ وَيْكَاَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُۚ لَوْلَآ اَنْ مَّنَّ اللّٰهُ عَلَيْنَا لَخَسَفَ بِنَا ۗوَيْكَاَنَّهٗ لَا يُفْلِحُ الْكٰفِرُوْنَ ","bc":"wa aṣbaḥallażīna tamannau makānahụ bil-amsi yaqụlụna waika`annallāha yabsuṭur-rizqa limay yasyā`u min 'ibādihī wa yaqdir, lau lā am mannallāhu 'alainā lakhasafa binā, waika`annahụ lā yufliḥul-kāfirụn","at":"Dan orang-orang yang kemarin mengangan-angankan kedudukannya (Karun) itu berkata, “Aduhai, benarlah kiranya Allah yang melapangkan rezeki bagi siapa yang Dia kehendaki di antara hamba-hamba-Nya dan membatasi (bagi siapa yang Dia kehendaki di antara hamba-hamba-Nya). Sekiranya Allah tidak melimpahkan karunia-Nya pada kita, tentu Dia telah membenamkan kita pula. Aduhai, benarlah kiranya tidak akan beruntung orang-orang yang mengingkari (nikmat Allah).”"},{"id":"83","ar":"تِلْكَ الدَّارُ الْاٰخِرَةُ نَجْعَلُهَا لِلَّذِيْنَ لَا يُرِيْدُوْنَ عُلُوًّا فِى الْاَرْضِ وَلَا فَسَادًا ۗوَالْعَاقِبَةُ لِلْمُتَّقِيْنَ ","bc":"tilkad-dārul-ākhiratu naj'aluhā lillażīna lā yurīdụna 'uluwwan fil-arḍi wa lā fasādā, wal-'āqibatu lil-muttaqīn","at":"Negeri akhirat itu Kami jadikan bagi orang-orang yang tidak menyombongkan diri dan tidak berbuat kerusakan di bumi. Dan kesudahan (yang baik) itu bagi orang-orang yang bertakwa."},{"id":"84","ar":"مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ خَيْرٌ مِّنْهَاۚ وَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَلَا يُجْزَى الَّذِيْنَ عَمِلُوا السَّيِّاٰتِ اِلَّا مَا كَانُوْا يَعْمَلُوْنَ ","bc":"man jā`a bil-ḥasanati fa lahụ khairum min-hā, wa man jā`a bis-sayyi`ati fa lā yujzallażīna 'amilus-sayyi`āti illā mā kānụ ya'malụn","at":"Barangsiapa datang dengan (membawa) kebaikan, maka dia akan mendapat (pahala) yang lebih baik daripada kebaikannya itu; dan barang siapa datang dengan (membawa) kejahatan, maka orang-orang yang telah mengerjakan kejahatan itu hanya diberi balasan (seimbang) dengan apa yang dahulu mereka kerjakan."},{"id":"85","ar":"اِنَّ الَّذِيْ فَرَضَ عَلَيْكَ الْقُرْاٰنَ لَرَاۤدُّكَ اِلٰى مَعَادٍ ۗقُلْ رَّبِّيْٓ اَعْلَمُ مَنْ جَاۤءَ بِالْهُدٰى وَمَنْ هُوَ فِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"innallażī faraḍa 'alaikal-qur`āna larādduka ilā ma'ād, qul rabbī a'lamu man jā`a bil-hudā wa man huwa fī ḍalālim mubīn","at":"Sesungguhnya (Allah) yang mewajibkan engkau (Muhammad) untuk (melaksanakan hukum-hukum) Al-Qur'an, benar-benar akan mengembalikanmu ke tempat kembali. Katakanlah (Muhammad), “Tuhanku mengetahui orang yang membawa petunjuk dan orang yang berada dalam kesesatan yang nyata.”"},{"id":"86","ar":"وَمَا كُنْتَ تَرْجُوْٓا اَنْ يُّلْقٰٓى اِلَيْكَ الْكِتٰبُ اِلَّا رَحْمَةً مِّنْ رَّبِّكَ فَلَا تَكُوْنَنَّ ظَهِيْرًا لِّلْكٰفِرِيْنَ ۖ ","bc":"wa mā kunta tarjū ay yulqā ilaikal-kitābu illā raḥmatam mir rabbika fa lā takụnanna ẓahīral lil-kāfirīn","at":"Dan engkau (Muhammad) tidak pernah mengharap agar Kitab (Al-Qur'an) itu diturunkan kepadamu, tetapi ia (diturunkan) sebagai rahmat dari Tuhanmu, sebab itu janganlah sekali-kali engkau menjadi penolong bagi orang-orang kafir,"},{"id":"87","ar":"وَلَا يَصُدُّنَّكَ عَنْ اٰيٰتِ اللّٰهِ بَعْدَ اِذْ اُنْزِلَتْ اِلَيْكَ وَادْعُ اِلٰى رَبِّكَ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ ۚ ","bc":"wa lā yaṣuddunnaka 'an āyātillāhi ba'da iż unzilat ilaika wad'u ilā rabbika wa lā takụnanna minal-musyrikīn","at":"dan jangan sampai mereka menghalang-halangi engkau (Muhammad) untuk (menyampaikan) ayat-ayat Allah, setelah ayat-ayat itu diturunkan kepadamu, dan serulah (manusia) agar (beriman) kepada Tuhanmu, dan janganlah engkau termasuk orang-orang musyrik."},{"id":"88","ar":"وَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَۘ لَآ اِلٰهَ اِلَّا هُوَۗ كُلُّ شَيْءٍ هَالِكٌ اِلَّا وَجْهَهٗ ۗ لَهُ الْحُكْمُ وَاِلَيْهِ تُرْجَعُوْنَ ","bc":"wa lā tad'u ma'allāhi ilāhan ākhar, lā ilāha illā huw, kullu syai`in hālikun illā waj-hah, lahul-ḥukmu wa ilaihi turja'ụn","at":"Dan jangan (pula) engkau sembah tuhan yang lain selain Allah. Tidak ada tuhan (yang berhak disembah) selain Dia. Segala sesuatu pasti binasa, kecuali Allah. Segala keputusan menjadi wewenang-Nya, dan hanya kepada-Nya kamu dikembalikan."}],al_qiyamah=[{"id":"1","ar":"لَآ اُقْسِمُ بِيَوْمِ الْقِيٰمَةِۙ","bc":"lā uqsimu biyaumil-qiyāmah","at":"Aku bersumpah dengan hari Kiamat,"},{"id":"2","ar":"وَلَآ اُقْسِمُ بِالنَّفْسِ اللَّوَّامَةِ ","bc":"wa lā uqsimu bin-nafsil-lawwāmah","at":"dan aku bersumpah demi jiwa yang selalu menyesali (dirinya sendiri)."},{"id":"3","ar":"اَيَحْسَبُ الْاِنْسَانُ اَلَّنْ نَّجْمَعَ عِظَامَهٗ ۗ","bc":"a yaḥsabul-insānu allan najma'a 'iẓāmah","at":"Apakah manusia mengira bahwa Kami tidak akan mengumpulkan (kembali) tulang-belulangnya?"},{"id":"4","ar":"بَلٰى قَادِرِيْنَ عَلٰٓى اَنْ نُّسَوِّيَ بَنَانَهٗ ","bc":"balā qādirīna 'alā an nusawwiya banānah","at":"(Bahkan) Kami mampu menyusun (kembali) jari-jemarinya dengan sempurna. "},{"id":"5","ar":"بَلْ يُرِيْدُ الْاِنْسَانُ لِيَفْجُرَ اَمَامَهٗۚ","bc":"bal yurīdul-insānu liyafjura amāmah","at":"Tetapi manusia hendak membuat maksiat terus-menerus. "},{"id":"6","ar":"يَسْـَٔلُ اَيَّانَ يَوْمُ الْقِيٰمَةِۗ","bc":"yas`alu ayyāna yaumul-qiyāmah","at":"Dia bertanya, “Kapankah hari Kiamat itu?”"},{"id":"7","ar":"فَاِذَا بَرِقَ الْبَصَرُۙ","bc":"fa iżā bariqal-baṣar","at":"Maka apabila mata terbelalak (ketakutan), "},{"id":"8","ar":"وَخَسَفَ الْقَمَرُۙ","bc":"wa khasafal-qamar","at":"dan bulan pun telah hilang cahayanya,"},{"id":"9","ar":"وَجُمِعَ الشَّمْسُ وَالْقَمَرُۙ","bc":"wa jumi'asy-syamsu wal-qamar","at":"lalu matahari dan bulan dikumpulkan, "},{"id":"10","ar":"يَقُوْلُ الْاِنْسَانُ يَوْمَىِٕذٍ اَيْنَ الْمَفَرُّۚ","bc":"yaqụlul-insānu yauma`iżin ainal-mafarr","at":"pada hari itu manusia berkata, “Ke mana tempat lari?”"},{"id":"11","ar":"كَلَّا لَا وَزَرَۗ","bc":"kallā lā wazar","at":"Tidak! Tidak ada tempat berlindung!"},{"id":"12","ar":"اِلٰى رَبِّكَ يَوْمَىِٕذِ ِۨالْمُسْتَقَرُّۗ","bc":"ilā rabbika yauma`iżinil-mustaqarr","at":"Hanya kepada Tuhanmu tempat kembali pada hari itu."},{"id":"13","ar":"يُنَبَّؤُا الْاِنْسَانُ يَوْمَىِٕذٍۢ بِمَا قَدَّمَ وَاَخَّرَۗ","bc":"yunabba`ul-insānu yauma`iżim bimā qaddama wa akhkhar","at":"Pada hari itu diberitakan kepada manusia apa yang telah dikerjakannya dan apa yang dilalaikannya."},{"id":"14","ar":"بَلِ الْاِنْسَانُ عَلٰى نَفْسِهٖ بَصِيْرَةٌۙ","bc":"balil-insānu 'alā nafsihī baṣīrah","at":"Bahkan manusia menjadi saksi atas dirinya sendiri,"},{"id":"15","ar":"وَّلَوْ اَلْقٰى مَعَاذِيْرَهٗۗ","bc":"walau alqā ma'āżīrah","at":"dan meskipun dia mengemukakan alasan-alasannya."},{"id":"16","ar":"لَا تُحَرِّكْ بِهٖ لِسَانَكَ لِتَعْجَلَ بِهٖۗ","bc":"lā tuḥarrik bihī lisānaka lita'jala bih","at":"Jangan engkau (Muhammad) gerakkan lidahmu (untuk membaca Al-Qur'an) karena hendak cepat-cepat (menguasai)nya."},{"id":"17","ar":"اِنَّ عَلَيْنَا جَمْعَهٗ وَقُرْاٰنَهٗ ۚ","bc":"inna 'alainā jam'ahụ wa qur`ānah","at":"Sesungguhnya Kami yang akan mengumpulkannya (di dadamu) dan membacakannya. "},{"id":"18","ar":"فَاِذَا قَرَأْنٰهُ فَاتَّبِعْ قُرْاٰنَهٗ ۚ","bc":"fa iżā qara`nāhu fattabi' qur`ānah","at":"Apabila Kami telah selesai membacakannya maka ikutilah bacaannya itu. "},{"id":"19","ar":"ثُمَّ اِنَّ عَلَيْنَا بَيَانَهٗ ۗ","bc":"ṡumma inna 'alainā bayānah","at":"Kemudian sesungguhnya Kami yang akan menjelaskannya. "},{"id":"20","ar":"كَلَّا بَلْ تُحِبُّوْنَ الْعَاجِلَةَۙ","bc":"kallā bal tuḥibbụnal-'ājilah","at":"Tidak! Bahkan kamu mencintai kehidupan dunia, "},{"id":"21","ar":"وَتَذَرُوْنَ الْاٰخِرَةَۗ","bc":"wa tażarụnal-ākhirah","at":"dan mengabaikan (kehidupan) akhirat. "},{"id":"22","ar":"وُجُوْهٌ يَّوْمَىِٕذٍ نَّاضِرَةٌۙ","bc":"wujụhuy yauma`iżin nāḍirah","at":"Wajah-wajah (orang mukmin) pada hari itu berseri-seri,"},{"id":"23","ar":"اِلٰى رَبِّهَا نَاظِرَةٌ ۚ","bc":"ilā rabbihā nāẓirah","at":"memandang Tuhannya. "},{"id":"24","ar":"وَوُجُوْهٌ يَّوْمَىِٕذٍۢ بَاسِرَةٌۙ","bc":"wa wujụhuy yauma`iżim bāsirah","at":"Dan wajah-wajah (orang kafir) pada hari itu muram, "},{"id":"25","ar":"تَظُنُّ اَنْ يُّفْعَلَ بِهَا فَاقِرَةٌ ۗ","bc":"taẓunnu ay yuf'ala bihā fāqirah","at":"mereka yakin bahwa akan ditimpakan kepadanya malapetaka yang sangat dahsyat."},{"id":"26","ar":"كَلَّآ اِذَا بَلَغَتِ التَّرَاقِيَۙ","bc":"kallā iżā balagatit-tarāqī","at":"Tidak! Apabila (nyawa) telah sampai ke kerongkongan,"},{"id":"27","ar":"وَقِيْلَ مَنْ ۜرَاقٍۙ","bc":"wa qīla man rāq","at":"dan dikatakan (kepadanya), “Siapa yang dapat menyembuhkan?”"},{"id":"28","ar":"وَّظَنَّ اَنَّهُ الْفِرَاقُۙ","bc":"wa ẓanna annahul-firāq","at":"Dan dia yakin bahwa itulah waktu perpisahan (dengan dunia), "},{"id":"29","ar":"وَالْتَفَّتِ السَّاقُ بِالسَّاقِۙ","bc":"waltaffatis-sāqu bis-sāq","at":"dan bertaut betis (kiri) dengan betis (kanan),"},{"id":"30","ar":"اِلٰى رَبِّكَ يَوْمَىِٕذِ ِۨالْمَسَاقُ ۗ","bc":"ilā rabbika yauma`iżinil-masāq","at":"kepada Tuhanmulah pada hari itu kamu dihalau. "},{"id":"31","ar":"فَلَا صَدَّقَ وَلَا صَلّٰىۙ","bc":"fa lā ṣaddaqa wa lā ṣallā","at":"Karena dia (dahulu) tidak mau membenarkan (Al-Qur'an dan Rasul) dan tidak mau melaksanakan salat, "},{"id":"32","ar":"وَلٰكِنْ كَذَّبَ وَتَوَلّٰىۙ","bc":"wa lāking każżaba wa tawallā","at":"tetapi justru dia mendustakan (Rasul) dan berpaling (dari kebenaran), "},{"id":"33","ar":"ثُمَّ ذَهَبَ اِلٰٓى اَهْلِهٖ يَتَمَطّٰىۗ","bc":"ṡumma żahaba ilā ahlihī yatamaṭṭā","at":"kemudian dia pergi kepada keluarganya dengan sombong."},{"id":"34","ar":"اَوْلٰى لَكَ فَاَوْلٰىۙ","bc":"aulā laka fa aulā","at":"Celakalah kamu! Maka celakalah! "},{"id":"35","ar":"ثُمَّ اَوْلٰى لَكَ فَاَوْلٰىۗ","bc":"ṡumma aulā laka fa aulā","at":"Sekali lagi, celakalah kamu (manusia)! Maka celakalah! "},{"id":"36","ar":"اَيَحْسَبُ الْاِنْسَانُ اَنْ يُّتْرَكَ سُدًىۗ","bc":"a yaḥsabul-insānu ay yutraka sudā","at":"Apakah manusia mengira, dia akan dibiarkan begitu saja (tanpa pertanggungjawaban)?"},{"id":"37","ar":"اَلَمْ يَكُ نُطْفَةً مِّنْ مَّنِيٍّ يُّمْنٰى ","bc":"a lam yaku nuṭfatam mim maniyyiy yumnā","at":"Bukankah dia mulanya hanya setetes mani yang ditumpahkan (ke dalam rahim), "},{"id":"38","ar":"ثُمَّ كَانَ عَلَقَةً فَخَلَقَ فَسَوّٰىۙ","bc":"ṡumma kāna 'alaqatan fa khalaqa fa sawwā","at":"kemudian (mani itu) menjadi sesuatu yang melekat, lalu Allah menciptakannya dan menyempurnakannya, "},{"id":"39","ar":"فَجَعَلَ مِنْهُ الزَّوْجَيْنِ الذَّكَرَ وَالْاُنْثٰىۗ","bc":"fa ja'ala min-huz-zaujainiż-żakara wal-unṡā","at":"lalu Dia menjadikan darinya sepasang laki-laki dan perempuan. "},{"id":"40","ar":"اَلَيْسَ ذٰلِكَ بِقٰدِرٍ عَلٰٓى اَنْ يُّحْيِ َۧ الْمَوْتٰى","bc":"a laisa żālika biqādirin 'alā ay yuḥyiyal-mautā","at":"Bukankah (Allah yang berbuat) demikian berkuasa (pula) menghidupkan orang mati?"}],al_waqiah=[{"id":"1","ar":"اِذَا وَقَعَتِ الْوَاقِعَةُۙ ","bc":"iżā waqa'atil-wāqi'ah","at":"Apabila terjadi hari Kiamat,"},{"id":"2","ar":"لَيْسَ لِوَقْعَتِهَا كَاذِبَةٌ ۘ ","bc":"laisa liwaq'atihā kāżibah","at":"terjadinya tidak dapat didustakan (disangkal)."},{"id":"3","ar":"خَافِضَةٌ رَّافِعَةٌ ","bc":"khāfiḍatur rāfi'ah","at":"(Kejadian itu) merendahkan (satu golongan) dan meninggikan (golongan yang lain)."},{"id":"4","ar":"اِذَا رُجَّتِ الْاَرْضُ رَجًّاۙ ","bc":"iżā rujjatil-arḍu rajjā","at":"Apabila bumi diguncangkan sedahsyat-dahsyatnya,"},{"id":"5","ar":"وَّبُسَّتِ الْجِبَالُ بَسًّاۙ ","bc":"wa bussatil-jibālu bassā","at":"dan gunung-gunung dihancurluluhkan sehancur-hancurnya,"},{"id":"6","ar":"فَكَانَتْ هَبَاۤءً مُّنْۢبَثًّاۙ ","bc":"fa kānat habā`am mumbaṡṡā","at":"maka jadilah ia debu yang beterbangan,"},{"id":"7","ar":"وَّكُنْتُمْ اَزْوَاجًا ثَلٰثَةً ۗ","bc":"wa kuntum azwājan ṡalāṡah","at":"dan kamu menjadi tiga golongan,"},{"id":"8","ar":"فَاَصْحٰبُ الْمَيْمَنَةِ ەۙ مَآ اَصْحٰبُ الْمَيْمَنَةِ ۗ","bc":"fa aṣ-ḥābul-maimanati mā aṣ-ḥābul-maimanah","at":"yaitu golongan kanan, alangkah mulianya golongan kanan itu,"},{"id":"9","ar":"وَاَصْحٰبُ الْمَشْـَٔمَةِ ەۙ مَآ اَصْحٰبُ الْمَشْـَٔمَةِ ۗ","bc":"wa aṣ-ḥābul-masy`amati mā aṣ-ḥābul-masy`amah","at":"dan golongan kiri, alangkah sengsaranya golongan kiri itu,"},{"id":"10","ar":"وَالسّٰبِقُوْنَ السّٰبِقُوْنَۙ ","bc":"was-sābiqụnas-sābiqụn","at":"dan orang-orang yang paling dahulu (beriman), merekalah yang paling dahulu (masuk surga)."},{"id":"11","ar":"اُولٰۤىِٕكَ الْمُقَرَّبُوْنَۚ ","bc":"ulā`ikal-muqarrabụn","at":"Mereka itulah orang yang dekat (kepada Allah),"},{"id":"12","ar":"فِيْ جَنّٰتِ النَّعِيْمِ ","bc":"fī jannātin-na'īm","at":"Berada dalam surga kenikmatan,"},{"id":"13","ar":"ثُلَّةٌ مِّنَ الْاَوَّلِيْنَۙ ","bc":"ṡullatum minal-awwalīn","at":"segolongan besar dari orang-orang yang terdahulu,"},{"id":"14","ar":"وَقَلِيْلٌ مِّنَ الْاٰخِرِيْنَۗ ","bc":"wa qalīlum minal-ākhirīn","at":"dan segolongan kecil dari orang-orang yang kemudian."},{"id":"15","ar":"عَلٰى سُرُرٍ مَّوْضُوْنَةٍۙ ","bc":"'alā sururim mauḍụnah","at":"Mereka berada di atas dipan-dipan yang bertahtakan emas dan permata,"},{"id":"16","ar":"مُّتَّكِـِٕيْنَ عَلَيْهَا مُتَقٰبِلِيْنَ","bc":"muttaki`īna 'alaihā mutaqābilīn","at":"mereka bersandar di atasnya berhadap-hadapan."},{"id":"17","ar":"يَطُوْفُ عَلَيْهِمْ وِلْدَانٌ مُّخَلَّدُوْنَۙ ","bc":"yaṭụfu 'alaihim wildānum mukhalladụn","at":"Mereka dikelilingi oleh anak-anak muda yang tetap muda,"},{"id":"18","ar":"بِاَكْوَابٍ وَّاَبَارِيْقَۙ وَكَأْسٍ مِّنْ مَّعِيْنٍۙ ","bc":"bi`akwābiw wa abārīqa wa ka`sim mim ma'īn","at":"dengan membawa gelas, cerek dan sloki (piala) berisi minuman yang diambil dari air yang mengalir,"},{"id":"19","ar":"لَّا يُصَدَّعُوْنَ عَنْهَا وَلَا يُنْزِفُوْنَۙ ","bc":"lā yuṣadda'ụna 'an-hā wa lā yunzifụn","at":"mereka tidak pening karenanya dan tidak pula mabuk,"},{"id":"20","ar":"وَفَاكِهَةٍ مِّمَّا يَتَخَيَّرُوْنَۙ ","bc":"wa fākihatim mimmā yatakhayyarụn","at":"dan buah-buahan apa pun yang mereka pilih,"},{"id":"21","ar":"وَلَحْمِ طَيْرٍ مِّمَّا يَشْتَهُوْنَۗ ","bc":"wa laḥmi ṭairim mimmā yasytahụn","at":"dan daging burung apa pun yang mereka inginkan."},{"id":"22","ar":"وَحُوْرٌ عِيْنٌۙ ","bc":"wa ḥụrun 'īn","at":"Dan ada bidadari-bidadari yang bermata indah,"},{"id":"23","ar":"كَاَمْثَالِ اللُّؤْلُؤِ الْمَكْنُوْنِۚ ","bc":"ka`amṡālil-lu`lu`il-maknụn","at":"laksana mutiara yang tersimpan baik."},{"id":"24","ar":"جَزَاۤءًۢ بِمَا كَانُوْا يَعْمَلُوْنَ","bc":"jazā`am bimā kānụ ya'malụn","at":"Sebagai balasan atas apa yang mereka kerjakan."},{"id":"25","ar":"لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا وَّلَا تَأْثِيْمًاۙ ","bc":"lā yasma'ụna fīhā lagwaw wa lā ta`ṡīmā","at":"Di sana mereka tidak mendengar percakapan yang sia-sia maupun yang menimbulkan dosa,"},{"id":"26","ar":"اِلَّا قِيْلًا سَلٰمًا سَلٰمًا ","bc":"illā qīlan salāman salāmā","at":"tetapi mereka mendengar ucapan salam."},{"id":"27","ar":"وَاَصْحٰبُ الْيَمِينِ ەۙ مَآ اَصْحٰبُ الْيَمِيْنِۗ","bc":"wa aṣ-ḥābul-yamīni mā aṣ-ḥābul-yamīn","at":"Dan golongan kanan, siapakah golongan kanan itu."},{"id":"28","ar":"فِيْ سِدْرٍ مَّخْضُوْدٍۙ ","bc":"fī sidrim makhḍụd","at":"(Mereka) berada di antara pohon bidara yang tidak berduri,"},{"id":"29","ar":"وَّطَلْحٍ مَّنْضُوْدٍۙ ","bc":"wa ṭal-ḥim manḍụd","at":"dan pohon pisang yang bersusun-susun (buahnya),"},{"id":"30","ar":"وَّظِلٍّ مَّمْدُوْدٍۙ ","bc":"wa ẓillim mamdụd","at":"dan naungan yang terbentang luas,"},{"id":"31","ar":"وَّمَاۤءٍ مَّسْكُوْبٍۙ ","bc":"wa mā`im maskụb","at":"dan air yang mengalir terus-menerus,"},{"id":"32","ar":"وَّفَاكِهَةٍ كَثِيْرَةٍۙ ","bc":"wa fākihating kaṡīrah","at":"dan buah-buahan yang banyak,"},{"id":"33","ar":"لَّا مَقْطُوْعَةٍ وَّلَا مَمْنُوْعَةٍۙ ","bc":"lā maqṭụ'atiw wa lā mamnụ'ah","at":"yang tidak berhenti berbuah dan tidak terlarang mengambilnya,"},{"id":"34","ar":"وَّفُرُشٍ مَّرْفُوْعَةٍۗ ","bc":"wa furusyim marfụ'ah","at":"dan kasur-kasur yang tebal lagi empuk."},{"id":"35","ar":"اِنَّآ اَنْشَأْنٰهُنَّ اِنْشَاۤءًۙ ","bc":"innā ansya`nāhunna insyā`ā","at":"Kami menciptakan mereka (bidadari-bidadari itu) secara langsung,"},{"id":"36","ar":"فَجَعَلْنٰهُنَّ اَبْكَارًاۙ ","bc":"fa ja'alnāhunna abkārā","at":"lalu Kami jadikan mereka perawan-perawan,"},{"id":"37","ar":"عُرُبًا اَتْرَابًاۙ ","bc":"'uruban atrābā","at":"yang penuh cinta (dan) sebaya umurnya,"},{"id":"38","ar":"لِّاَصْحٰبِ الْيَمِيْنِۗ ","bc":"li`aṣ-ḥābil-yamīn","at":"untuk golongan kanan,"},{"id":"39","ar":"ثُلَّةٌ مِّنَ الْاَوَّلِيْنَۙ ","bc":"ṡullatum minal-awwalīn","at":"segolongan besar dari orang-orang yang terdahulu,"},{"id":"40","ar":"وَثُلَّةٌ مِّنَ الْاٰخِرِيْنَۗ ","bc":"wa ṡullatum minal-ākhirīn","at":"dan segolongan besar pula dari orang yang kemudian."},{"id":"41","ar":"وَاَصْحٰبُ الشِّمَالِ ەۙ مَآ اَصْحٰبُ الشِّمَالِۗ","bc":"wa aṣ-ḥābusy-syimāli mā aṣ-ḥābusy-syimāl","at":"Dan golongan kiri, alangkah sengsaranya golongan kiri itu."},{"id":"42","ar":"فِيْ سَمُوْمٍ وَّحَمِيْمٍۙ ","bc":"fī samụmiw wa ḥamīm","at":"(Mereka) dalam siksaan angin yang sangat panas dan air yang mendidih,"},{"id":"43","ar":"وَّظِلٍّ مِّنْ يَّحْمُوْمٍۙ ","bc":"wa ẓillim miy yaḥmụm","at":"dan naungan asap yang hitam,"},{"id":"44","ar":"لَّا بَارِدٍ وَّلَا كَرِيْمٍ ","bc":"lā bāridiw wa lā karīm","at":"tidak sejuk dan tidak menyenangkan."},{"id":"45","ar":"اِنَّهُمْ كَانُوْا قَبْلَ ذٰلِكَ مُتْرَفِيْنَۚ ","bc":"innahum kānụ qabla żālika mutrafīn","at":"Sesungguhnya mereka sebelum itu (dahulu) hidup bermewah-mewah,"},{"id":"46","ar":"وَكَانُوْا يُصِرُّوْنَ عَلَى الْحِنْثِ الْعَظِيْمِۚ ","bc":"wa kānụ yuṣirrụna 'alal-ḥinṡil-'aẓīm","at":"dan mereka terus-menerus mengerjakan dosa yang besar,"},{"id":"47","ar":"وَكَانُوْا يَقُوْلُوْنَ ەۙ اَىِٕذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَبْعُوْثُوْنَۙ","bc":"wa kānụ yaqụlụna a iżā mitnā wa kunnā turābaw wa 'iẓāman a innā lamab'ụṡụn","at":"dan mereka berkata, “Apabila kami sudah mati, menjadi tanah dan tulang-belulang, apakah kami benar-benar akan dibangkitkan kembali?"},{"id":"48","ar":"اَوَاٰبَاۤؤُنَا الْاَوَّلُوْنَ ","bc":"a wa ābā`unal-awwalụn","at":"Apakah nenek moyang kami yang terdahulu (dibangkitkan pula)?”"},{"id":"49","ar":"قُلْ اِنَّ الْاَوَّلِيْنَ وَالْاٰخِرِيْنَۙ ","bc":"qul innal-awwalīna wal-ākhirīn","at":"Katakanlah, “(Ya), sesungguhnya orang-orang yang terdahulu dan yang kemudian,"},{"id":"50","ar":"لَمَجْمُوْعُوْنَۙ اِلٰى مِيْقَاتِ يَوْمٍ مَّعْلُوْمٍ ","bc":"lamajmụ'ụna ilā mīqāti yaumim ma'lụm","at":"pasti semua akan dikumpulkan pada waktu tertentu, pada hari yang sudah dimaklumi."},{"id":"51","ar":"ثُمَّ اِنَّكُمْ اَيُّهَا الضَّاۤ لُّوْنَ الْمُكَذِّبُوْنَۙ ","bc":"ṡumma innakum ayyuhaḍ-ḍāllụnal-mukażżibụn","at":"Kemudian sesungguhnya kamu, wahai orang-orang yang sesat lagi mendustakan!"},{"id":"52","ar":"لَاٰكِلُوْنَ مِنْ شَجَرٍ مِّنْ زَقُّوْمٍۙ ","bc":"la`ākilụna min syajarim min zaqqụm","at":"pasti akan memakan pohon zaqqum,"},{"id":"53","ar":"فَمَالِـُٔوْنَ مِنْهَا الْبُطُوْنَۚ ","bc":"fa māli`ụna min-hal-buṭụn","at":"maka akan penuh perutmu dengannya."},{"id":"54","ar":"فَشَارِبُوْنَ عَلَيْهِ مِنَ الْحَمِيْمِۚ ","bc":"fa syāribụna 'alaihi minal-ḥamīm","at":"Setelah itu kamu akan meminum air yang sangat panas."},{"id":"55","ar":"فَشَارِبُوْنَ شُرْبَ الْهِيْمِۗ ","bc":"fa syāribụna syurbal-hīm","at":"Maka kamu minum seperti unta (yang sangat haus) minum."},{"id":"56","ar":"هٰذَا نُزُلُهُمْ يَوْمَ الدِّيْنِۗ ","bc":"hāżā nuzuluhum yaumad-dīn","at":"Itulah hidangan untuk mereka pada hari pembalasan.”"},{"id":"57","ar":"نَحْنُ خَلَقْنٰكُمْ فَلَوْلَا تُصَدِّقُوْنَ ","bc":"naḥnu khalaqnākum falau lā tuṣaddiqụn","at":"Kami telah menciptakan kamu, mengapa kamu tidak membenarkan (hari berbangkit)?"},{"id":"58","ar":"اَفَرَءَيْتُمْ مَّا تُمْنُوْنَۗ ","bc":"a fa ra`aitum mā tumnụn","at":"Maka adakah kamu perhatikan, tentang (benih manusia) yang kamu pancarkan."},{"id":"59","ar":"ءَاَنْتُمْ تَخْلُقُوْنَهٗٓ اَمْ نَحْنُ الْخَالِقُوْنَ ","bc":"a antum takhluqụnahū am naḥnul-khāliqụn","at":"Kamukah yang menciptakannya, ataukah Kami penciptanya?"},{"id":"60","ar":"نَحْنُ قَدَّرْنَا بَيْنَكُمُ الْمَوْتَ وَمَا نَحْنُ بِمَسْبُوْقِيْنَۙ ","bc":"naḥnu qaddarnā bainakumul-mauta wa mā naḥnu bimasbụqīn","at":"Kami telah menentukan kematian masing-masing kamu dan Kami tidak lemah,"},{"id":"61","ar":"عَلٰٓى اَنْ نُّبَدِّلَ اَمْثَالَكُمْ وَنُنْشِئَكُمْ فِيْ مَا لَا تَعْلَمُوْنَ","bc":"'alā an nubaddila amṡālakum wa nunsyi`akum fī mā lā ta'lamụn","at":"untuk menggantikan kamu dengan orang-orang yang seperti kamu (di dunia) dan membangkitkan kamu kelak (di akhirat) dalam keadaan yang tidak kamu ketahui."},{"id":"62","ar":"وَلَقَدْ عَلِمْتُمُ النَّشْاَةَ الْاُوْلٰى فَلَوْلَا تَذَكَّرُوْنَ ","bc":"wa laqad 'alimtumun-nasy`atal-ụlā falau lā tażakkarụn","at":"Dan sungguh, kamu telah tahu penciptaan yang pertama, mengapa kamu tidak mengambil pelajaran?"},{"id":"63","ar":"اَفَرَءَيْتُمْ مَّا تَحْرُثُوْنَۗ ","bc":"a fa ra`aitum mā taḥruṡụn","at":"Pernahkah kamu perhatikan benih yang kamu tanam?"},{"id":"64","ar":"ءَاَنْتُمْ تَزْرَعُوْنَهٗٓ اَمْ نَحْنُ الزَّارِعُوْنَ ","bc":"a antum tazra'ụnahū am naḥnuz-zāri'ụn","at":"Kamukah yang menumbuhkannya ataukah Kami yang menumbuhkan?"},{"id":"65","ar":"لَوْ نَشَاۤءُ لَجَعَلْنٰهُ حُطَامًا فَظَلْتُمْ تَفَكَّهُوْنَۙ ","bc":"lau nasyā`u laja'alnāhu huṭāman fa ẓaltum tafakkahụn","at":"Sekiranya Kami kehendaki, niscaya Kami hancurkan sampai lumat; maka kamu akan heran tercengang,"},{"id":"66","ar":"اِنَّا لَمُغْرَمُوْنَۙ ","bc":"innā lamugramụn","at":"(sambil berkata), “Sungguh, kami benar-benar menderita kerugian,"},{"id":"67","ar":"بَلْ نَحْنُ مَحْرُوْمُوْنَ ","bc":"bal naḥnu mahrụmụn","at":"bahkan kami tidak mendapat hasil apa pun.”"},{"id":"68","ar":"اَفَرَءَيْتُمُ الْمَاۤءَ الَّذِيْ تَشْرَبُوْنَۗ ","bc":"a fa ra`aitumul-mā`allażī tasyrabụn","at":"Pernahkah kamu memperhatikan air yang kamu minum?"},{"id":"69","ar":"ءَاَنْتُمْ اَنْزَلْتُمُوْهُ مِنَ الْمُزْنِ اَمْ نَحْنُ الْمُنْزِلُوْنَ ","bc":"a antum anzaltumụhu minal-muzni am naḥnul-munzilụn","at":"Kamukah yang menurunkannya dari awan ataukah Kami yang menurunkan?"},{"id":"70","ar":"لَوْ نَشَاۤءُ جَعَلْنٰهُ اُجَاجًا فَلَوْلَا تَشْكُرُوْنَ ","bc":"lau nasyā`u ja'alnāhu ujājan falau lā tasykurụn","at":"Sekiranya Kami menghendaki, niscaya Kami menjadikannya asin, mengapa kamu tidak bersyukur?"},{"id":"71","ar":"اَفَرَءَيْتُمُ النَّارَ الَّتِيْ تُوْرُوْنَۗ ","bc":"a fa ra`aitumun-nārallatī tụrụn","at":"Maka pernahkah kamu memperhatikan tentang api yang kamu nyalakan (dengan kayu)?"},{"id":"72","ar":"ءَاَنْتُمْ اَنْشَأْتُمْ شَجَرَتَهَآ اَمْ نَحْنُ الْمُنْشِـُٔوْنَ ","bc":"a antum ansya`tum syajaratahā am naḥnul-munsyi`ụn","at":"Kamukah yang menumbuhkan kayu itu ataukah Kami yang menumbuhkan?"},{"id":"73","ar":"نَحْنُ جَعَلْنٰهَا تَذْكِرَةً وَّمَتَاعًا لِّلْمُقْوِيْنَۚ ","bc":"naḥnu ja'alnāhā tażkirataw wa matā'al lil-muqwīn","at":"Kami menjadikannya (api itu) untuk peringatan dan bahan yang berguna bagi musafir."},{"id":"74","ar":"فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيْمِ ","bc":"fa sabbiḥ bismi rabbikal-'aẓīm","at":"Maka bertasbihlah dengan (menyebut) nama Tuhanmu Yang Mahabesar."},{"id":"75","ar":"فَلَآ اُقْسِمُ بِمَوٰقِعِ النُّجُوْمِ ","bc":"fa lā uqsimu bimawāqi'in-nujụm","at":"Lalu Aku bersumpah dengan tempat beredarnya bintang-bintang."},{"id":"76","ar":"وَاِنَّهٗ لَقَسَمٌ لَّوْ تَعْلَمُوْنَ عَظِيْمٌۙ ","bc":"wa innahụ laqasamul lau ta'lamụna 'aẓīm","at":"Dan sesungguhnya itu benar-benar sumpah yang besar sekiranya kamu mengetahui,"},{"id":"77","ar":"اِنَّهٗ لَقُرْاٰنٌ كَرِيْمٌۙ ","bc":"innahụ laqur`ānung karīm","at":"dan (ini) sesungguhnya Al-Qur'an yang sangat mulia,"},{"id":"78","ar":"فِيْ كِتٰبٍ مَّكْنُوْنٍۙ ","bc":"fī kitābim maknụn","at":"dalam Kitab yang terpelihara (Lauh Mahfuzh),"},{"id":"79","ar":"لَّا يَمَسُّهٗٓ اِلَّا الْمُطَهَّرُوْنَۙ ","bc":"lā yamassuhū illal-muṭahharụn","at":"tidak ada yang menyentuhnya selain hamba-hamba yang disucikan."},{"id":"80","ar":"تَنْزِيْلٌ مِّنْ رَّبِّ الْعٰلَمِيْنَ ","bc":"tanzīlum mir rabbil-'ālamīn","at":"Diturunkan dari Tuhan seluruh alam."},{"id":"81","ar":"اَفَبِهٰذَا الْحَدِيْثِ اَنْتُمْ مُّدْهِنُوْنَ ","bc":"a fa bihāżal-ḥadīṡi antum mud-hinụn","at":"Apakah kamu menganggap remeh berita ini (Al-Qur'an),"},{"id":"82","ar":"وَتَجْعَلُوْنَ رِزْقَكُمْ اَنَّكُمْ تُكَذِّبُوْنَ ","bc":"wa taj'alụna rizqakum annakum tukażżibụn","at":"dan kamu menjadikan rezeki yang kamu terima (dari Allah) justru untuk mendustakan(-Nya)."},{"id":"83","ar":"فَلَوْلَآ اِذَا بَلَغَتِ الْحُلْقُوْمَۙ ","bc":"falau lā iżā balagatil-ḥulqụm","at":"Maka kalau begitu mengapa (tidak mencegah) ketika (nyawa) telah sampai di kerongkongan,"},{"id":"84","ar":"وَاَنْتُمْ حِيْنَىِٕذٍ تَنْظُرُوْنَۙ ","bc":"wa antum ḥīna`iżin tanẓurụn","at":"dan kamu ketika itu melihat,"},{"id":"85","ar":"وَنَحْنُ اَقْرَبُ اِلَيْهِ مِنْكُمْ وَلٰكِنْ لَّا تُبْصِرُوْنَ ","bc":"wa naḥnu aqrabu ilaihi mingkum wa lākil lā tubṣirụn","at":"dan Kami lebih dekat kepadanya daripada kamu, tetapi kamu tidak melihat,"},{"id":"86","ar":"فَلَوْلَآ اِنْ كُنْتُمْ غَيْرَ مَدِيْنِيْنَۙ ","bc":"falau lā ing kuntum gaira madīnīn","at":"maka mengapa jika kamu memang tidak dikuasai (oleh Allah),"},{"id":"87","ar":"تَرْجِعُوْنَهَآ اِنْ كُنْتُمْ صٰدِقِيْنَ","bc":"tarji'ụnahā ing kuntum ṣādiqīn","at":"kamu tidak mengembalikannya (nyawa itu) jika kamu orang yang benar?"},{"id":"88","ar":"فَاَمَّآ اِنْ كَانَ مِنَ الْمُقَرَّبِيْنَۙ ","bc":"fa ammā ing kāna minal-muqarrabīn","at":"Jika dia (orang yang mati) itu termasuk yang didekatkan (kepada Allah),"},{"id":"89","ar":"فَرَوْحٌ وَّرَيْحَانٌ ەۙ وَّجَنَّتُ نَعِيْمٍ","bc":"fa rauḥuw wa raiḥānuw wa jannatu na'īm","at":"maka dia memperoleh ketenteraman dan rezeki serta surga (yang penuh) kenikmatan."},{"id":"90","ar":"وَاَمَّآ اِنْ كَانَ مِنْ اَصْحٰبِ الْيَمِيْنِۙ ","bc":"wa ammā ing kāna min aṣ-ḥābil-yamīn","at":"Dan adapun jika dia termasuk golongan kanan,"},{"id":"91","ar":"فَسَلٰمٌ لَّكَ مِنْ اَصْحٰبِ الْيَمِيْنِۗ ","bc":"fa salāmul laka min aṣ-ḥābil-yamīn","at":"maka, “Salam bagimu (wahai) dari golongan kanan!” (sambut malaikat)."},{"id":"92","ar":"وَاَمَّآ اِنْ كَانَ مِنَ الْمُكَذِّبِيْنَ الضَّاۤلِّيْنَۙ ","bc":"wa ammā ing kāna minal-mukażżibīnaḍ-ḍāllīn","at":"Dan adapun jika dia termasuk golongan orang yang mendustakan dan sesat,"},{"id":"93","ar":"فَنُزُلٌ مِّنْ حَمِيْمٍۙ ","bc":"fa nuzulum min ḥamīm","at":"maka dia disambut siraman air yang mendidih,"},{"id":"94","ar":"وَّتَصْلِيَةُ جَحِيْمٍ ","bc":"wa taṣliyatu jaḥīm","at":"dan dibakar di dalam neraka."},{"id":"95","ar":"اِنَّ هٰذَا لَهُوَ حَقُّ الْيَقِيْنِۚ ","bc":"inna hāżā lahuwa ḥaqqul-yaqīn","at":"Sungguh, inilah keyakinan yang benar."},{"id":"96","ar":"فَسَبِّحْ بِاسْمِ رَبِّكَ الْعَظِيْمِ ","bc":"fa sabbiḥ bismi rabbikal-'aẓīm","at":"Maka bertasbihlah dengan (menyebut) nama Tuhanmu Yang Mahabesar."}],al_zalzalah=[{"id":"1","ar":"اِذَا زُلْزِلَتِ الْاَرْضُ زِلْزَالَهَاۙ","bc":"iżā zulzilatil-arḍu zilzālahā","at":"Apabila bumi diguncangkan dengan guncangan yang dahsyat,"},{"id":"2","ar":"وَاَخْرَجَتِ الْاَرْضُ اَثْقَالَهَاۙ","bc":"wa akhrajatil-arḍu aṡqālahā","at":"dan bumi telah mengeluarkan beban-beban berat (yang dikandung)nya,"},{"id":"3","ar":"وَقَالَ الْاِنْسَانُ مَا لَهَاۚ","bc":"wa qālal-insānu mā lahā","at":"Dan manusia bertanya, “Apa yang terjadi pada bumi ini?”"},{"id":"4","ar":"يَوْمَىِٕذٍ تُحَدِّثُ اَخْبَارَهَاۙ","bc":"yauma`iżin tuḥaddiṡu akhbārahā","at":"Pada hari itu bumi menyampaikan beritanya,"},{"id":"5","ar":"بِاَنَّ رَبَّكَ اَوْحٰى لَهَاۗ","bc":"bi`anna rabbaka auḥā lahā","at":"karena sesungguhnya Tuhanmu telah memerintahkan (yang sedemikian itu) padanya."},{"id":"6","ar":"يَوْمَىِٕذٍ يَّصْدُرُ النَّاسُ اَشْتَاتًا ەۙ لِّيُرَوْا اَعْمَالَهُمْۗ","bc":"yauma`iżiy yaṣdurun-nāsu asytātal liyurau a'mālahum","at":"Pada hari itu manusia keluar dari kuburnya dalam keadaan berkelompok-kelompok, untuk diperlihatkan kepada mereka (balasan) semua perbuatannya."},{"id":"7","ar":"فَمَنْ يَّعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْرًا يَّرَهٗۚ","bc":"fa may ya'mal miṡqāla żarratin khairay yarah","at":"Maka barangsiapa mengerjakan kebaikan seberat zarrah, niscaya dia akan melihat (balasan)nya,"},{"id":"8","ar":"وَمَنْ يَّعْمَلْ مِثْقَالَ ذَرَّةٍ شَرًّا يَّرَهٗ","bc":"wa may ya'mal miṡqāla żarratin syarray yarah","at":"dan barangsiapa mengerjakan kejahatan seberat zarrah, niscaya dia akan melihat (balasan)nya."}],ali_imran=[{"id":"1","ar":"الۤمّۤ ","bc":"alif lām mīm","at":"Alif Lam Mim."},{"id":"2","ar":"اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ الْحَيُّ الْقَيُّوْمُۗ","bc":"allāhu lā ilāha illā huwal-ḥayyul-qayyụm","at":"Allah, tidak ada tuhan selain Dia. Yang Mahahidup, Yang terus-menerus mengurus (makhluk-Nya)."},{"id":"3","ar":"نَزَّلَ عَلَيْكَ الْكِتٰبَ بِالْحَقِّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ وَاَنْزَلَ التَّوْرٰىةَ وَالْاِنْجِيْلَۙ","bc":"nazzala 'alaikal-kitāba bil-ḥaqqi muṣaddiqal limā baina yadaihi wa anzalat-taurāta wal-injīl","at":"Dia menurunkan Kitab (Al-Qur'an) kepadamu (Muhammad) yang mengandung kebenaran, membenarkan (kitab-kitab) sebelumnya, dan menurunkan Taurat dan Injil,"},{"id":"4","ar":"مِنْ قَبْلُ هُدًى لِّلنَّاسِ وَاَنْزَلَ الْفُرْقَانَ ەۗ اِنَّ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِ اللّٰهِ لَهُمْ عَذَابٌ شَدِيْدٌ ۗوَاللّٰهُ عَزِيْزٌ ذُو انْتِقَامٍۗ","bc":"ming qablu hudal lin-nāsi wa anzalal-furqān, innallażīna kafarụ bi`āyātillāhi lahum 'ażābun syadīd, wallāhu 'azīzun żuntiqām","at":"sebelumnya, sebagai petunjuk bagi manusia, dan Dia menurunkan Al-Furqan. Sungguh, orang-orang yang ingkar terhadap ayat-ayat Allah akan memperoleh azab yang berat. Allah Mahaperkasa lagi mempunyai hukuman."},{"id":"5","ar":"اِنَّ اللّٰهَ لَا يَخْفٰى عَلَيْهِ شَيْءٌ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ","bc":"innallāha lā yakhfā 'alaihi syai`un fil-arḍi wa lā fis-samā`","at":"Bagi Allah tidak ada sesuatu pun yang tersembunyi di bumi dan di langit."},{"id":"6","ar":"هُوَ الَّذِيْ يُصَوِّرُكُمْ فِى الْاَرْحَامِ كَيْفَ يَشَاۤءُ ۗ لَآ اِلٰهَ اِلَّا هُوَ الْعَزِيْزُ الْحَكِيْمُ","bc":"huwallażī yuṣawwirukum fil-ar-ḥāmi kaifa yasyā`, lā ilāha illā huwal-'azīzul-ḥakīm","at":"Dialah yang membentuk kamu dalam rahim menurut yang Dia kehendaki. Tidak ada tuhan selain Dia. Yang Mahaperkasa, Mahabijaksana."},{"id":"7","ar":"هُوَ الَّذِيْٓ اَنْزَلَ عَلَيْكَ الْكِتٰبَ مِنْهُ اٰيٰتٌ مُّحْكَمٰتٌ هُنَّ اُمُّ الْكِتٰبِ وَاُخَرُ مُتَشٰبِهٰتٌ ۗ فَاَمَّا الَّذِيْنَ فِيْ قُلُوْبِهِمْ زَيْغٌ فَيَتَّبِعُوْنَ مَا تَشَابَهَ مِنْهُ ابْتِغَاۤءَ الْفِتْنَةِ وَابْتِغَاۤءَ تَأْوِيْلِهٖۚ وَمَا يَعْلَمُ تَأْوِيْلَهٗٓ اِلَّا اللّٰهُ ۘوَالرَّاسِخُوْنَ فِى الْعِلْمِ يَقُوْلُوْنَ اٰمَنَّا بِهٖۙ كُلٌّ مِّنْ عِنْدِ رَبِّنَا ۚ وَمَا يَذَّكَّرُ اِلَّآ اُولُوا الْاَلْبَابِ","bc":"huwallażī anzala 'alaikal-kitāba min-hu āyātum muḥkamātun hunna ummul-kitābi wa ukharu mutasyābihāt, fa ammallażīna fī qulụbihim zaigun fayattabi'ụna mā tasyābaha min-hubtigā`al-fitnati wabtigā`a ta`wīlih, wa mā ya'lamu ta`wīlahū illallāh, war-rāsikhụna fil-'ilmi yaqụlụna āmannā bihī kullum min 'indi rabbinā, wa mā yażżakkaru illā ulul-albāb","at":"Dialah yang menurunkan Kitab (Al-Qur'an) kepadamu (Muhammad). Di antaranya ada ayat-ayat yang muhkamat, itulah pokok-pokok Kitab (Al-Qur'an) dan yang lain mutasyabihat. Adapun orang-orang yang dalam hatinya condong pada kesesatan, mereka mengikuti yang mutasyabihat untuk mencari-cari fitnah dan untuk mencari-cari takwilnya, padahal tidak ada yang mengetahui takwilnya kecuali Allah. Dan orang-orang yang ilmunya mendalam berkata, “Kami beriman kepadanya (Al-Qur'an), semuanya dari sisi Tuhan kami.” Tidak ada yang dapat mengambil pelajaran kecuali orang yang berakal."},{"id":"8","ar":"رَبَّنَا لَا تُزِغْ قُلُوْبَنَا بَعْدَ اِذْ هَدَيْتَنَا وَهَبْ لَنَا مِنْ لَّدُنْكَ رَحْمَةً ۚاِنَّكَ اَنْتَ الْوَهَّابُ ","bc":"rabbanā lā tuzig qulụbanā ba'da iż hadaitanā wa hab lanā mil ladungka raḥmah, innaka antal-wahhāb","at":"(Mereka berdoa), “Ya Tuhan kami, janganlah Engkau condongkan hati kami kepada kesesatan setelah Engkau berikan petunjuk kepada kami, dan karuniakanlah kepada kami rahmat dari sisi-Mu, sesungguhnya Engkau Maha Pemberi.”"},{"id":"9","ar":"رَبَّنَآ اِنَّكَ جَامِعُ النَّاسِ لِيَوْمٍ لَّا رَيْبَ فِيْهِ ۗاِنَّ اللّٰهَ لَا يُخْلِفُ الْمِيْعَادَ","bc":"rabbanā innaka jāmi'un-nāsi liyaumil lā raiba fīh, innallāha lā yukhliful-mī'ād","at":"”Ya Tuhan kami, Engkaulah yang mengumpulkan manusia pada hari yang tidak ada keraguan padanya.” Sungguh, Allah tidak menyalahi janji."},{"id":"10","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ مِّنَ اللّٰهِ شَيْـًٔا ۗوَاُولٰۤىِٕكَ هُمْ وَقُوْدُ النَّارِۗ","bc":"innallażīna kafarụ lan tugniya 'an-hum amwāluhum wa lā aulāduhum minallāhi syai`ā, wa ulā`ika hum waqụdun-nār","at":"Sesungguhnya orang-orang yang kafir, bagi mereka tidak akan berguna sedikit pun harta benda dan anak-anak mereka terhadap (azab) Allah. Dan mereka itu (menjadi) bahan bakar api neraka."},{"id":"11","ar":"كَدَأْبِ اٰلِ فِرْعَوْنَۙ وَالَّذِيْنَ مِنْ قَبْلِهِمْۗ كَذَّبُوْا بِاٰيٰتِنَاۚ فَاَخَذَهُمُ اللّٰهُ بِذُنُوْبِهِمْ ۗ وَاللّٰهُ شَدِيْدُ الْعِقَابِ","bc":"kada`bi āli fir'auna wallażīna ming qablihim, każżabụ bi`āyātinā, fa akhażahumullāhu biżunụbihim, wallāhu syadīdul-'iqāb","at":"(Keadaan mereka) seperti keadaan pengikut Fir’aun dan orang-orang yang sebelum mereka. Mereka mendustakan ayat-ayat Kami, maka Allah menyiksa mereka disebabkan dosa-dosanya. Allah sangat berat hukuman-Nya."},{"id":"12","ar":"قُلْ لِّلَّذِيْنَ كَفَرُوْا سَتُغْلَبُوْنَ وَتُحْشَرُوْنَ اِلٰى جَهَنَّمَ ۗ وَبِئْسَ الْمِهَادُ","bc":"qul lillażīna kafarụ satuglabụna wa tuḥsyarụna ilā jahannam, wa bi`sal-mihād","at":"Katakanlah (Muhammad) kepada orang-orang yang kafir, “Kamu (pasti) akan dikalahkan dan digiring ke dalam neraka Jahanam. Dan itulah seburuk-buruk tempat tinggal.”"},{"id":"13","ar":"قَدْ كَانَ لَكُمْ اٰيَةٌ فِيْ فِئَتَيْنِ الْتَقَتَا ۗفِئَةٌ تُقَاتِلُ فِيْ سَبِيْلِ اللّٰهِ وَاُخْرٰى كَافِرَةٌ يَّرَوْنَهُمْ مِّثْلَيْهِمْ رَأْيَ الْعَيْنِ ۗوَاللّٰهُ يُؤَيِّدُ بِنَصْرِهٖ مَنْ يَّشَاۤءُ ۗ اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّاُولِى الْاَبْصَارِ","bc":"qad kāna lakum āyatun fī fi`atainiltaqatā, fi`atun tuqātilu fī sabīlillāhi wa ukhrā kāfiratuy yaraunahum miṡlaihim ra`yal-'aīn, wallāhu yu`ayyidu binaṣrihī may yasyā`, inna fī żālika la'ibratal li`ulil-abṣār","at":"Sungguh, telah ada tanda bagi kamu pada dua golongan yang berhadap-hadapan. Satu golongan berperang di jalan Allah dan yang lain (golongan) kafir yang melihat dengan mata kepala, bahwa mereka (golongan Muslim) dua kali lipat mereka. Allah menguatkan dengan pertolongan-Nya bagi siapa yang Dia kehendaki. Sungguh, pada yang demikian itu terdapat pelajaran bagi orang-orang yang mempunyai penglihatan (mata hati)."},{"id":"14","ar":"زُيِّنَ لِلنَّاسِ حُبُّ الشَّهَوٰتِ مِنَ النِّسَاۤءِ وَالْبَنِيْنَ وَالْقَنَاطِيْرِ الْمُقَنْطَرَةِ مِنَ الذَّهَبِ وَالْفِضَّةِ وَالْخَيْلِ الْمُسَوَّمَةِ وَالْاَنْعَامِ وَالْحَرْثِ ۗ ذٰلِكَ مَتَاعُ الْحَيٰوةِ الدُّنْيَا ۗوَاللّٰهُ عِنْدَهٗ حُسْنُ الْمَاٰبِ","bc":"zuyyina lin-nāsi ḥubbusy-syahawāti minan-nisā`i wal-banīna wal-qanaṭīril-muqanṭarati minaż-żahabi wal-fiḍḍati wal-khailil-musawwamati wal-an'āmi wal-ḥarṡ, żālika matā'ul-ḥayātid-dun-yā, wallāhu 'indahụ ḥusnul-ma`āb","at":"Dijadikan terasa indah dalam pandangan manusia cinta terhadap apa yang diinginkan, berupa perempuan-perempuan, anak-anak, harta benda yang bertumpuk dalam bentuk emas dan perak, kuda pilihan, hewan ternak dan sawah ladang. Itulah kesenangan hidup di dunia, dan di sisi Allah-lah tempat kembali yang baik."},{"id":"15","ar":"۞ قُلْ اَؤُنَبِّئُكُمْ بِخَيْرٍ مِّنْ ذٰلِكُمْ ۗ لِلَّذِيْنَ اتَّقَوْا عِنْدَ رَبِّهِمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا وَاَزْوَاجٌ مُّطَهَّرَةٌ وَّرِضْوَانٌ مِّنَ اللّٰهِ ۗ وَاللّٰهُ بَصِيْرٌۢ بِالْعِبَادِۚ","bc":"qul a unabbi`ukum bikhairim min żālikum, lillażīnattaqau 'inda rabbihim jannātun tajrī min taḥtihal-an-hāru khālidīna fīhā wa azwājum muṭahharatuw wa riḍwānum minallāh, wallāhu baṣīrum bil-'ibād","at":"Katakanlah, “Maukah aku kabarkan kepadamu apa yang lebih baik dari yang demikian itu?” Bagi orang-orang yang bertakwa (tersedia) di sisi Tuhan mereka surga-surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya, dan pasangan-pasangan yang suci, serta rida Allah. Dan Allah Maha Melihat hamba-hamba-Nya."},{"id":"16","ar":"اَلَّذِيْنَ يَقُوْلُوْنَ رَبَّنَآ اِنَّنَآ اٰمَنَّا فَاغْفِرْ لَنَا ذُنُوْبَنَا وَقِنَا عَذَابَ النَّارِۚ","bc":"allażīna yaqụlụna rabbanā innanā āmannā fagfir lanā żunụbanā wa qinā 'ażāban-nār","at":"(Yaitu) orang-orang yang berdoa, “Ya Tuhan kami, kami benar-benar beriman, maka ampunilah dosa-dosa kami dan lindungilah kami dari azab neraka.”"},{"id":"17","ar":"اَلصّٰبِرِيْنَ وَالصّٰدِقِيْنَ وَالْقٰنِتِيْنَ وَالْمُنْفِقِيْنَ وَالْمُسْتَغْفِرِيْنَ بِالْاَسْحَارِ ","bc":"aṣ-ṣābirīna waṣ-ṣādiqīna wal-qānitīna wal-munfiqīna wal-mustagfirīna bil-as-ḥār","at":"(Juga) orang yang sabar, orang yang benar, orang yang taat, orang yang menginfakkan hartanya, dan orang yang memohon ampunan pada waktu sebelum fajar."},{"id":"18","ar":"شَهِدَ اللّٰهُ اَنَّهٗ لَآ اِلٰهَ اِلَّا هُوَۙ وَالْمَلٰۤىِٕكَةُ وَاُولُوا الْعِلْمِ قَاۤىِٕمًاۢ بِالْقِسْطِۗ لَآ اِلٰهَ اِلَّا هُوَ الْعَزِيْزُ الْحَكِيْمُ","bc":"syahidallāhu annahụ lā ilāha illā huwa wal-malā`ikatu wa ulul-'ilmi qā`imam bil-qisṭ, lā ilāha illā huwal-'azīzul-ḥakīm","at":"Allah menyatakan bahwa tidak ada tuhan selain Dia; (demikian pula) para malaikat dan orang berilmu yang menegakkan keadilan, tidak ada tuhan selain Dia, Yang Mahaperkasa, Maha-bijaksana."},{"id":"19","ar":"اِنَّ الدِّيْنَ عِنْدَ اللّٰهِ الْاِسْلَامُ ۗ وَمَا اخْتَلَفَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْعِلْمُ بَغْيًاۢ بَيْنَهُمْ ۗوَمَنْ يَّكْفُرْ بِاٰيٰتِ اللّٰهِ فَاِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ","bc":"innad-dīna 'indallāhil-islām, wa makhtalafallażīna ụtul-kitāba illā mim ba'di mā jā`ahumul-'ilmu bagyam bainahum, wa may yakfur bi`āyātillāhi fa innallāha sarī'ul-ḥisāb","at":"Sesungguhnya agama di sisi Allah ialah Islam. Tidaklah berselisih orang-orang yang telah diberi Kitab kecuali setelah mereka memperoleh ilmu, karena kedengkian di antara mereka. Barangsiapa ingkar terhadap ayat-ayat Allah, maka sungguh, Allah sangat cepat perhitungan-Nya."},{"id":"20","ar":"فَاِنْ حَاۤجُّوْكَ فَقُلْ اَسْلَمْتُ وَجْهِيَ لِلّٰهِ وَمَنِ اتَّبَعَنِ ۗوَقُلْ لِّلَّذِيْنَ اُوْتُوا الْكِتٰبَ وَالْاُمِّيّٖنَ ءَاَسْلَمْتُمْ ۗ فَاِنْ اَسْلَمُوْا فَقَدِ اهْتَدَوْا ۚ وَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلٰغُ ۗ وَاللّٰهُ بَصِيْرٌۢ بِالْعِبَادِ","bc":"fa in ḥājjụka fa qul aslamtu waj-hiya lillāhi wa manittaba'an, wa qul lillażīna ụtul-kitāba wal-ummiyyīna a aslamtum, fa in aslamụ fa qadihtadau, wa in tawallau fa innamā 'alaikal-balāg, wallāhu baṣīrum bil-'ibād","at":"Kemudian jika mereka membantah engkau (Muhammad) katakanlah, “Aku berserah diri kepada Allah dan (demikian pula) orang-orang yang mengikutiku.” Dan katakanlah kepada orang-orang yang telah diberi Kitab dan kepada orang-orang buta huruf, ”Sudahkah kamu masuk Islam?” Jika mereka masuk Islam, berarti mereka telah mendapat petunjuk, tetapi jika mereka berpaling, maka kewajibanmu hanyalah menyampaikan. Dan Allah Maha Melihat hamba-hamba-Nya."},{"id":"21","ar":"اِنَّ الَّذِيْنَ يَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَيَقْتُلُوْنَ النَّبِيّٖنَ بِغَيْرِحَقٍّۖ وَّيَقْتُلُوْنَ الَّذِيْنَ يَأْمُرُوْنَ بِالْقِسْطِ مِنَ النَّاسِۙ فَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍ","bc":"innallażīna yakfurụna bi`āyātillāhi wa yaqtulụnan-nabiyyīna bigairi ḥaqqiw wa yaqtulụnallażīna ya`murụna bil-qisṭi minan-nāsi fa basysyir-hum bi'ażābin alīm","at":"Sesungguhnya orang-orang yang mengingkari ayat-ayat Allah dan membunuh para nabi tanpa hak (alasan yang benar) dan membunuh orang-orang yang menyuruh manusia berbuat adil, sampaikanlah kepada mereka kabar gembira yaitu azab yang pedih."},{"id":"22","ar":"اُولٰۤىِٕكَ الَّذِيْنَ حَبِطَتْ اَعْمَالُهُمْ فِى الدُّنْيَا وَالْاٰخِرَةِ ۖ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ","bc":"ulā`ikallażīna ḥabiṭat a'māluhum fid-dun-yā wal-ākhirati wa mā lahum min nāṣirīn","at":"Mereka itulah orang-orang yang sia-sia pekerjaannya di dunia dan di akhirat, dan mereka tidak memperoleh penolong."},{"id":"23","ar":"اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يُدْعَوْنَ اِلٰى كِتٰبِ اللّٰهِ لِيَحْكُمَ بَيْنَهُمْ ثُمَّ يَتَوَلّٰى فَرِيْقٌ مِّنْهُمْ وَهُمْ مُّعْرِضُوْنَ","bc":"a lam tara ilallażīna ụtụ naṣībam minal-kitābi yud'auna ilā kitābillāhi liyaḥkuma bainahum ṡumma yatawallā farīqum min-hum wa hum mu'riḍụn","at":"Tidakkah engkau memperhatikan orang-orang yang telah diberi bagian Kitab (Taurat)? Mereka diajak (berpegang) pada Kitab Allah untuk memutuskan (perkara) di antara mereka. Kemudian sebagian dari mereka berpaling seraya menolak (kebenaran)."},{"id":"24","ar":"ذٰلِكَ بِاَنَّهُمْ قَالُوْا لَنْ تَمَسَّنَا النَّارُ اِلَّآ اَيَّامًا مَّعْدُوْدٰتٍ ۖ وَّغَرَّهُمْ فِيْ دِيْنِهِمْ مَّا كَانُوْا يَفْتَرُوْنَ","bc":"żālika bi`annahum qālụ lan tamassanan-nāru illā ayyāmam ma'dụdātiw wa garrahum fī dīnihim mā kānụ yaftarụn","at":"Hal itu adalah karena mereka berkata, “Api neraka tidak akan menyentuh kami kecuali beberapa hari saja.” Mereka teperdaya dalam agama mereka oleh apa yang mereka ada-adakan."},{"id":"25","ar":"فَكَيْفَ اِذَا جَمَعْنٰهُمْ لِيَوْمٍ لَّا رَيْبَ فِيْهِۗ وَوُفِّيَتْ كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ","bc":"fa kaifa iżā jama'nāhum liyaumil lā raiba fīh, wa wuffiyat kullu nafsim mā kasabat wa hum lā yuẓlamụn","at":"Bagaimana jika (nanti) mereka Kami kumpulkan pada hari (Kiamat) yang tidak diragukan terjadinya dan kepada setiap jiwa diberi balasan yang sempurna sesuai dengan apa yang telah dikerjakannya dan mereka tidak dizalimi (dirugikan)?"},{"id":"26","ar":"قُلِ اللهم مٰلِكَ الْمُلْكِ تُؤْتِى الْمُلْكَ مَنْ تَشَاۤءُ وَتَنْزِعُ الْمُلْكَ مِمَّنْ تَشَاۤءُۖ وَتُعِزُّ مَنْ تَشَاۤءُ وَتُذِلُّ مَنْ تَشَاۤءُ ۗ بِيَدِكَ الْخَيْرُ ۗ اِنَّكَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ","bc":"qulillāhumma mālikal-mulki tu`til-mulka man tasyā`u wa tanzi'ul-mulka mim man tasyā`u wa tu'izzu man tasyā`u wa tużillu man tasyā`, biyadikal-khaīr, innaka 'alā kulli syai`ing qadīr","at":"Katakanlah (Muhammad), “Wahai Tuhan pemilik kekuasaan, Engkau berikan kekuasaan kepada siapa pun yang Engkau kehendaki, dan Engkau cabut kekuasaan dari siapa pun yang Engkau kehendaki. Engkau muliakan siapa pun yang Engkau kehendaki dan Engkau hinakan siapa pun yang Engkau kehendaki. Di tangan Engkaulah segala kebajikan. Sungguh, Engkau Mahakuasa atas segala sesuatu."},{"id":"27","ar":"تُوْلِجُ الَّيْلَ فِى النَّهَارِ وَتُوْلِجُ النَّهَارَ فِى الَّيْلِ وَتُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَتُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَتَرْزُقُ مَنْ تَشَاۤءُ بِغَيْرِ حِسَابٍ","bc":"tụlijul-laila fin-nahāri wa tụlijun-nahāra fil-laili wa tukhrijul-ḥayya minal-mayyiti wa tukhrijul-mayyita minal-ḥayyi wa tarzuqu man tasyā`u bigairi ḥisāb","at":"Engkau masukkan malam ke dalam siang dan Engkau masukkan siang ke dalam malam. Dan Engkau keluarkan yang hidup dari yang mati, dan Engkau keluarkan yang mati dari yang hidup. Dan Engkau berikan rezeki kepada siapa yang Engkau kehendaki tanpa perhitungan.”"},{"id":"28","ar":"لَا يَتَّخِذِ الْمُؤْمِنُوْنَ الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَۚ وَمَنْ يَّفْعَلْ ذٰلِكَ فَلَيْسَ مِنَ اللّٰهِ فِيْ شَيْءٍ اِلَّآ اَنْ تَتَّقُوْا مِنْهُمْ تُقٰىةً ۗ وَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهٗ ۗ وَاِلَى اللّٰهِ الْمَصِيْرُ","bc":"lā yattakhiżil-mu`minụnal-kāfirīna auliyā`a min dụnil-mu`minīn, wa may yaf'al żālika fa laisa minallāhi fī syai`in illā an tattaqụ min-hum tuqāh, wa yuḥażżirukumullāhu nafsah, wa ilallāhil-maṣīr","at":"Janganlah orang-orang beriman menjadikan orang kafir sebagai pemimpin, melainkan orang-orang beriman. Barang siapa berbuat demikian, niscaya dia tidak akan memperoleh apa pun dari Allah, kecuali karena (siasat) menjaga diri dari sesuatu yang kamu takuti dari mereka. Dan Allah memperingatkan kamu akan diri (siksa)-Nya, dan hanya kepada Allah tempat kembali."},{"id":"29","ar":"قُلْ اِنْ تُخْفُوْا مَا فِيْ صُدُوْرِكُمْ اَوْ تُبْدُوْهُ يَعْلَمْهُ اللّٰهُ ۗوَيَعْلَمُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ","bc":"qul in tukhfụ mā fī ṣudụrikum au tubdụhu ya'lam-hullāh, wa ya'lamu mā fis-samāwāti wa mā fil-arḍ, wallāhu 'alā kulli syai`ing qadīr","at":"Katakanlah, “Jika kamu sembunyikan apa yang ada dalam hatimu atau kamu nyatakan, Allah pasti mengetahuinya.” Dia mengetahui apa yang ada di langit dan apa yang ada di bumi. Allah Mahakuasa atas segala sesuatu."},{"id":"30","ar":"يَوْمَ تَجِدُ كُلُّ نَفْسٍ مَّا عَمِلَتْ مِنْ خَيْرٍ مُّحْضَرًا ۛوَمَا عَمِلَتْ مِنْ سُوْۤءٍ ۛ تَوَدُّ لَوْ اَنَّ بَيْنَهَا وَبَيْنَهٗٓ اَمَدًاۢ بَعِيْدًا ۗوَيُحَذِّرُكُمُ اللّٰهُ نَفْسَهٗ ۗوَاللّٰهُ رَءُوْفٌۢ بِالْعِبَادِ","bc":"yauma tajidu kullu nafsim mā 'amilat min khairim muḥḍaraw wa mā 'amilat min sū`, tawaddu lau anna bainahā wa bainahū amadam ba'īdā, wa yuḥażżirukumullāhu nafsah, wallāhu ra`ụfum bil-'ibād","at":"(Ingatlah) pada hari (ketika) setiap jiwa mendapatkan (balasan) atas kebajikan yang telah dikerjakan dihadapkan kepadanya, (begitu juga balasan) atas kejahatan yang telah dia kerjakan. Dia berharap sekiranya ada jarak yang jauh antara dia dengan (hari) itu. Dan Allah memperingatkan kamu akan diri (siksa)-Nya. Allah Maha Penyayang terhadap hamba-hamba-Nya."},{"id":"31","ar":"قُلْ اِنْ كُنْتُمْ تُحِبُّوْنَ اللّٰهَ فَاتَّبِعُوْنِيْ يُحْبِبْكُمُ اللّٰهُ وَيَغْفِرْ لَكُمْ ذُنُوْبَكُمْ ۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ","bc":"qul ing kuntum tuḥibbụnallāha fattabi'ụnī yuḥbibkumullāhu wa yagfir lakum żunụbakum, wallāhu gafụrur raḥīm","at":"Katakanlah (Muhammad), “Jika kamu mencintai Allah, ikutilah aku, niscaya Allah mencintaimu dan mengampuni dosa-dosamu.” Allah Maha Pengampun, Maha Penyayang."},{"id":"32","ar":"قُلْ اَطِيْعُوا اللّٰهَ وَالرَّسُوْلَ ۚ فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ لَا يُحِبُّ الْكٰفِرِيْنَ","bc":"qul aṭī'ullāha war-rasụl, fa in tawallau fa innallāha lā yuḥibbul-kāfirīn","at":"Katakanlah (Muhammad), “Taatilah Allah dan Rasul. Jika kamu berpaling, ketahuilah bahwa Allah tidak menyukai orang-orang kafir.”"},{"id":"33","ar":"۞ اِنَّ اللّٰهَ اصْطَفٰىٓ اٰدَمَ وَنُوْحًا وَّاٰلَ اِبْرٰهِيْمَ وَاٰلَ عِمْرَانَ عَلَى الْعٰلَمِيْنَۙ","bc":"innallāhaṣṭafā ādama wa nụḥaw wa āla ibrāhīma wa āla 'imrāna 'alal-'ālamīn","at":"Sesungguhnya Allah telah memilih Adam, Nuh, keluarga Ibrahim dan keluarga Imran melebihi segala umat (pada masa masing-masing),"},{"id":"34","ar":"ذُرِّيَّةً ۢ بَعْضُهَا مِنْۢ بَعْضٍۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌۚ","bc":"żurriyyatam ba'ḍuhā mim ba'ḍ, wallāhu samī'un 'alīm","at":"(sebagai) satu keturunan, sebagiannya adalah (keturunan) dari sebagian yang lain. Allah Maha Mendengar, Maha Mengetahui."},{"id":"35","ar":"اِذْ قَالَتِ امْرَاَتُ عِمْرَانَ رَبِّ اِنِّيْ نَذَرْتُ لَكَ مَا فِيْ بَطْنِيْ مُحَرَّرًا فَتَقَبَّلْ مِنِّيْ ۚ اِنَّكَ اَنْتَ السَّمِيْعُ الْعَلِيْمُ","bc":"iż qālatimra`atu 'imrāna rabbi innī nażartu laka mā fī baṭnī muḥarraran fa taqabbal minnī, innaka antas-samī'ul-'alīm","at":"(Ingatlah), ketika istri Imran berkata, “Ya Tuhanku, sesungguhnya aku bernazar kepada-Mu, apa (janin) yang dalam kandunganku (kelak) menjadi hamba yang mengabdi (kepada-Mu), maka terimalah (nazar itu) dariku. Sungguh, Engkaulah Yang Maha Mendengar, Maha Mengetahui.”"},{"id":"36","ar":"فَلَمَّا وَضَعَتْهَا قَالَتْ رَبِّ اِنِّيْ وَضَعْتُهَآ اُنْثٰىۗ وَاللّٰهُ اَعْلَمُ بِمَا وَضَعَتْۗ وَلَيْسَ الذَّكَرُ كَالْاُنْثٰى ۚ وَاِنِّيْ سَمَّيْتُهَا مَرْيَمَ وَاِنِّيْٓ اُعِيْذُهَا بِكَ وَذُرِّيَّتَهَا مِنَ الشَّيْطٰنِ الرَّجِيْمِ","bc":"fa lammā waḍa'at-hā qālat rabbi innī waḍa'tuhā unṡā, wallāhu a'lamu bimā waḍa'at, wa laisaż-żakaru kal-unṡā, wa innī sammaituhā maryama wa innī u'īżuhā bika wa żurriyyatahā minasy-syaiṭānir-rajīm","at":"Maka ketika melahirkannya, dia berkata, “Ya Tuhanku, aku telah melahirkan anak perempuan.” Padahal Allah lebih tahu apa yang dia lahirkan, dan laki-laki tidak sama dengan perempuan. ”Dan aku memberinya nama Maryam, dan aku mohon perlindungan-Mu untuknya dan anak cucunya dari (gangguan) setan yang terkutuk.”"},{"id":"37","ar":"فَتَقَبَّلَهَا رَبُّهَا بِقَبُوْلٍ حَسَنٍ وَّاَنْۢبَتَهَا نَبَاتًا حَسَنًاۖ وَّكَفَّلَهَا زَكَرِيَّا ۗ كُلَّمَا دَخَلَ عَلَيْهَا زَكَرِيَّا الْمِحْرَابَۙ وَجَدَ عِنْدَهَا رِزْقًا ۚ قَالَ يٰمَرْيَمُ اَنّٰى لَكِ هٰذَا ۗ قَالَتْ هُوَ مِنْ عِنْدِ اللّٰهِ ۗ اِنَّ اللّٰهَ يَرْزُقُ مَنْ يَّشَاۤءُ بِغَيْرِ حِسَابٍ","bc":"fa taqabbalahā rabbuhā biqabụlin ḥasaniw wa ambatahā nabātan ḥasanaw wa kaffalahā zakariyyā, kullamā dakhala 'alaihā zakariyyal-miḥrāba wajada 'indahā rizqā, qāla yā maryamu annā laki hāżā, qālat huwa min 'indillāh, innallāha yarzuqu may yasyā`u bigairi ḥisāb","at":"Maka Dia (Allah) menerimanya dengan penerimaan yang baik, membesarkannya dengan pertumbuhan yang baik dan menyerahkan pemeliharaannya kepada Zakaria. Setiap kali Zakaria masuk menemuinya di mihrab (kamar khusus ibadah), dia dapati makanan di sisinya. Dia berkata, “Wahai Maryam! Dari mana ini engkau peroleh?” Dia (Maryam) menjawab, “Itu dari Allah.” Sesungguhnya Allah memberi rezeki kepada siapa yang Dia kehendaki tanpa perhitungan."},{"id":"38","ar":"هُنَالِكَ دَعَا زَكَرِيَّا رَبَّهٗ ۚ قَالَ رَبِّ هَبْ لِيْ مِنْ لَّدُنْكَ ذُرِّيَّةً طَيِّبَةً ۚ اِنَّكَ سَمِيْعُ الدُّعَاۤءِ","bc":"hunālika da'ā zakariyyā rabbah, qāla rabbi hab lī mil ladungka żurriyyatan ṭayyibah, innaka samī'ud-du'ā`","at":"Di sanalah Zakaria berdoa kepada Tuhannya. Dia berkata, “Ya Tuhanku, berilah aku keturunan yang baik dari sisi-Mu, sesungguhnya Engkau Maha Mendengar doa.”"},{"id":"39","ar":"فَنَادَتْهُ الْمَلٰۤىِٕكَةُ وَهُوَ قَاۤىِٕمٌ يُّصَلِّيْ فِى الْمِحْرَابِۙ اَنَّ اللّٰهَ يُبَشِّرُكَ بِيَحْيٰى مُصَدِّقًاۢ بِكَلِمَةٍ مِّنَ اللّٰهِ وَسَيِّدًا وَّحَصُوْرًا وَّنَبِيًّا مِّنَ الصّٰلِحِيْنَ","bc":"fa nādat-hul-malā`ikatu wa huwa qā`imuy yuṣallī fil-miḥrābi annallāha yubasysyiruka biyaḥyā muṣaddiqam bikalimatim minallāhi wa sayyidaw wa ḥaṣụraw wa nabiyyam minaṣ-ṣāliḥīn","at":"Kemudian para malaikat memanggilnya, ketika dia berdiri melaksanakan salat di mihrab, “Allah menyampaikan kabar gembira kepadamu dengan (kelahiran) Yahya, yang membenarkan sebuah kalimat (firman) dari Allah, panutan, berkemampuan menahan diri (dari hawa nafsu) dan seorang nabi di antara orang-orang saleh.”"},{"id":"40","ar":"قَالَ رَبِّ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّقَدْ بَلَغَنِيَ الْكِبَرُ وَامْرَاَتِيْ عَاقِرٌ ۗ قَالَ كَذٰلِكَ اللّٰهُ يَفْعَلُ مَا يَشَاۤءُ","bc":"qāla rabbi annā yakụnu lī gulāmuw wa qad balaganiyal-kibaru wamra`atī 'āqir, qāla każālikallāhu yaf'alu mā yasyā`","at":"Dia (Zakaria) berkata, “Ya Tuhanku, bagaimana aku bisa mendapat anak sedang aku sudah sangat tua dan istriku pun mandul?” Dia (Allah) berfirman, “Demikianlah, Allah berbuat apa yang Dia kehendaki.”"},{"id":"41","ar":"قَالَ رَبِّ اجْعَلْ لِّيْٓ اٰيَةً ۗ قَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَةَ اَيَّامٍ اِلَّا رَمْزًا ۗ وَاذْكُرْ رَّبَّكَ كَثِيْرًا وَّسَبِّحْ بِالْعَشِيِّ وَالْاِبْكَارِ","bc":"qāla rabbij'al lī āyah, qāla āyatuka allā tukalliman-nāsa ṡalāṡata ayyāmin illā ramzā, ważkur rabbaka kaṡīraw wa sabbiḥ bil-'asyiyyi wal-ibkār","at":"Dia (Zakaria) berkata, “Ya Tuhanku, berilah aku suatu tanda.” Allah berfirman, “Tanda bagimu, adalah bahwa engkau tidak berbicara dengan manusia selama tiga hari, kecuali dengan isyarat. Dan sebutlah (nama) Tuhanmu banyak-banyak, dan bertasbihlah (memuji-Nya) pada waktu petang dan pagi hari.”"},{"id":"42","ar":"وَاِذْ قَالَتِ الْمَلٰۤىِٕكَةُ يٰمَرْيَمُ اِنَّ اللّٰهَ اصْطَفٰىكِ وَطَهَّرَكِ وَاصْطَفٰىكِ عَلٰى نِسَاۤءِ الْعٰلَمِيْنَ","bc":"wa iż qālatil-malā`ikatu yā maryamu innallāhaṣṭafāki wa ṭahharaki waṣṭafāki 'alā nisā`il-'ālamīn","at":"Dan (ingatlah) ketika para malaikat berkata, “Wahai Maryam! Sesungguhnya Allah telah memilihmu, menyucikanmu, dan melebihkanmu di atas segala perempuan di seluruh alam (pada masa itu)."},{"id":"43","ar":"يٰمَرْيَمُ اقْنُتِيْ لِرَبِّكِ وَاسْجُدِيْ وَارْكَعِيْ مَعَ الرَّاكِعِيْنَ","bc":"yā maryamuqnutī lirabbiki wasjudī warka'ī ma'ar-rāki'īn","at":"Wahai Maryam! Taatilah Tuhanmu, sujud dan rukuklah bersama orang-orang yang rukuk.”"},{"id":"44","ar":"ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهِ اِلَيْكَ ۗوَمَا كُنْتَ لَدَيْهِمْ اِذْ يُلْقُوْنَ اَقْلَامَهُمْ اَيُّهُمْ يَكْفُلُ مَرْيَمَۖ وَمَا كُنْتَ لَدَيْهِمْ اِذْ يَخْتَصِمُوْنَ","bc":"żālika min ambā`il-gaibi nụḥīhi ilaīk, wa mā kunta ladaihim iż yulqụna aqlāmahum ayyuhum yakfulu maryama wa mā kunta ladaihim iż yakhtaṣimụn","at":"Itulah sebagian dari berita-berita gaib yang Kami wahyukan kepadamu (Muhammad), padahal engkau tidak bersama mereka ketika mereka melemparkan pena mereka (untuk mengundi) siapa di antara mereka yang akan memelihara Maryam. Dan engkau pun tidak bersama mereka ketika mereka bertengkar."},{"id":"45","ar":"اِذْ قَالَتِ الْمَلٰۤىِٕكَةُ يٰمَرْيَمُ اِنَّ اللّٰهَ يُبَشِّرُكِ بِكَلِمَةٍ مِّنْهُۖ اسْمُهُ الْمَسِيْحُ عِيْسَى ابْنُ مَرْيَمَ وَجِيْهًا فِى الدُّنْيَا وَالْاٰخِرَةِ وَمِنَ الْمُقَرَّبِيْنَۙ","bc":"iż qālatil-malā`ikatu yā maryamu innallāha yubasysyiruki bikalimatim min-husmuhul-masīḥu 'īsabnu maryama wajīhan fid-dun-yā wal-ākhirati wa minal-muqarrabīn","at":"(Ingatlah), ketika para malaikat berkata, “Wahai Maryam! Sesungguhnya Allah menyampaikan kabar gembira kepadamu tentang sebuah kalimat (fir-man) dari-Nya (yaitu seorang putra), namanya Al-Masih Isa putra Maryam, seorang terkemuka di dunia dan di akhirat, dan termasuk orang-orang yang didekatkan (kepada Allah),"},{"id":"46","ar":"وَيُكَلِّمُ النَّاسَ فِى الْمَهْدِ وَكَهْلًا وَّمِنَ الصّٰلِحِيْنَ","bc":"wa yukallimun-nāsa fil-mahdi wa kahlaw wa minaṣ-ṣāliḥīn","at":"dan dia berbicara dengan manusia (sewaktu) dalam buaian dan ketika sudah dewasa, dan dia termasuk di antara orang-orang saleh.”"},{"id":"47","ar":"قَالَتْ رَبِّ اَنّٰى يَكُوْنُ لِيْ وَلَدٌ وَّلَمْ يَمْسَسْنِيْ بَشَرٌ ۗ قَالَ كَذٰلِكِ اللّٰهُ يَخْلُقُ مَا يَشَاۤءُ ۗاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ","bc":"qālat rabbi annā yakụnu lī waladuw wa lam yamsasnī basyar, qāla każālikillāhu yakhluqu mā yasyā`, iżā qaḍā amran fa innamā yaqụlu lahụ kun fa yakụn","at":"Dia (Maryam) berkata, “Ya Tuhanku, bagaimana mungkin aku akan mempunyai anak, padahal tidak ada seorang laki-laki pun yang menyentuhku?” Dia (Allah) berfirman, “Demikianlah Allah menciptakan apa yang Dia kehendaki. Apabila Dia hendak menetapkan sesuatu, Dia hanya berkata kepadanya, “Jadilah!” Maka jadilah sesuatu itu."},{"id":"48","ar":"وَيُعَلِّمُهُ الْكِتٰبَ وَالْحِكْمَةَ وَالتَّوْرٰىةَ وَالْاِنْجِيْلَۚ","bc":"wa yu'allimuhul-kitāba wal-ḥikmata wat-taurāta wal-injīl","at":"Dan Dia (Allah) mengajarkan kepadanya (Isa) Kitab, Hikmah, Taurat, dan Injil."},{"id":"49","ar":"وَرَسُوْلًا اِلٰى بَنِيْٓ اِسْرَاۤءِيْلَ ەۙ اَنِّيْ قَدْ جِئْتُكُمْ بِاٰيَةٍ مِّنْ رَّبِّكُمْ ۙاَنِّيْٓ اَخْلُقُ لَكُمْ مِّنَ الطِّيْنِ كَهَيْـَٔةِ الطَّيْرِ فَاَنْفُخُ فِيْهِ فَيَكُوْنُ طَيْرًاۢ بِاِذْنِ اللّٰهِ ۚوَاُبْرِئُ الْاَكْمَهَ وَالْاَبْرَصَ وَاُحْيِ الْمَوْتٰى بِاِذْنِ اللّٰهِ ۚوَاُنَبِّئُكُمْ بِمَا تَأْكُلُوْنَ وَمَا تَدَّخِرُوْنَ ۙفِيْ بُيُوْتِكُمْ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيَةً لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَۚ","bc":"wa rasụlan ilā banī isrā`īla annī qad ji`tukum bi`āyatim mir rabbikum annī akhluqu lakum minaṭ-ṭīni kahai`atiṭ-ṭairi fa anfukhu fīhi fa yakụnu ṭairam bi`iżnillāh, wa ubri`ul-akmaha wal-abraṣa wa uḥyil-mautā bi`iżnillāh, wa unabbi`ukum bimā ta`kulụna wa mā taddakhirụna fī buyụtikum, inna fī żālika la`āyatal lakum ing kuntum mu`minīn","at":"Dan sebagai Rasul kepada Bani Israil (dia berkata), “Aku telah datang kepada kamu dengan sebuah tanda (mukjizat) dari Tuhanmu, yaitu aku membuatkan bagimu (sesuatu) dari tanah berbentuk seperti burung, lalu aku meniupnya, maka ia menjadi seekor burung dengan izin Allah. Dan aku menyembuhkan orang yang buta sejak dari lahir dan orang yang berpenyakit kusta. Dan aku menghidupkan orang mati dengan izin Allah, dan aku beritahukan kepadamu apa yang kamu makan dan apa yang kamu simpan di rumahmu. Sesungguhnya pada yang demikian itu terdapat suatu tanda (kebenaran kerasulanku) bagimu, jika kamu orang beriman."},{"id":"50","ar":"وَمُصَدِّقًا لِّمَا بَيْنَ يَدَيَّ مِنَ التَّوْرٰىةِ وَلِاُحِلَّ لَكُمْ بَعْضَ الَّذِيْ حُرِّمَ عَلَيْكُمْ وَجِئْتُكُمْ بِاٰيَةٍ مِّنْ رَّبِّكُمْۗ فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ","bc":"wa muṣaddiqal limā baina yadayya minat-taurāti wa li`uḥilla lakum ba'ḍallażī ḥurrima 'alaikum wa ji`tukum bi`āyatim mir rabbikum, fattaqullāha wa aṭī'ụn","at":"Dan sebagai seorang yang membenarkan Taurat yang datang sebelumku, dan agar aku menghalalkan bagi kamu sebagian dari yang telah diharamkan untukmu. Dan aku datang kepadamu membawa suatu tanda (mukjizat) dari Tuhanmu. Karena itu, bertakwalah kepada Allah dan taatlah kepadaku."},{"id":"51","ar":"اِنَّ اللّٰهَ رَبِّيْ وَرَبُّكُمْ فَاعْبُدُوْهُ ۗهٰذَا صِرَاطٌ مُّسْتَقِيْمٌ","bc":"innallāha rabbī wa rabbukum fa'budụh, hāżā ṣirāṭum mustaqīm","at":"Sesungguhnya Allah itu Tuhanku dan Tuhanmu, karena itu sembahlah Dia. Inilah jalan yang lurus.”"},{"id":"52","ar":"۞ فَلَمَّآ اَحَسَّ عِيْسٰى مِنْهُمُ الْكُفْرَ قَالَ مَنْ اَنْصَارِيْٓ اِلَى اللّٰهِ ۗ قَالَ الْحَوَارِيُّوْنَ نَحْنُ اَنْصَارُ اللّٰهِ ۚ اٰمَنَّا بِاللّٰهِ ۚ وَاشْهَدْ بِاَنَّا مُسْلِمُوْنَ","bc":"fa lammā aḥassa 'īsā min-humul-kufra qāla man anṣārī ilallāh, qālal-ḥawāriyyụna naḥnu anṣārullāh, āmannā billāh, wasy-had bi`annā muslimụn","at":"Maka ketika Isa merasakan keingkaran mereka (Bani Israil), dia berkata, “Siapakah yang akan menjadi penolong untuk (menegakkan agama) Allah?” Para Hawariyyun (sahabat setianya) menjawab, “Kamilah penolong (agama) Allah. Kami beriman kepada Allah, dan saksikanlah, bahwa kami adalah orang-orang Muslim."},{"id":"53","ar":"رَبَّنَآ اٰمَنَّا بِمَآ اَنْزَلْتَ وَاتَّبَعْنَا الرَّسُوْلَ فَاكْتُبْنَا مَعَ الشّٰهِدِيْنَ","bc":"rabbanā āmannā bimā anzalta wattaba'nar-rasụla faktubnā ma'asy-syāhidīn","at":"Ya Tuhan kami, kami telah beriman kepada apa yang Engkau turunkan dan kami telah mengikuti Rasul, karena itu tetapkanlah kami bersama golongan orang yang memberikan kesaksian.”"},{"id":"54","ar":"وَمَكَرُوْا وَمَكَرَ اللّٰهُ ۗوَاللّٰهُ خَيْرُ الْمَاكِرِيْنَ","bc":"wa makarụ wa makarallāh, wallāhu khairul-mākirīn","at":"Dan mereka (orang-orang kafir) membuat tipu daya, maka Allah pun membalas tipu daya. Dan Allah sebaik-baik pembalas tipu daya."},{"id":"55","ar":"اِذْ قَالَ اللّٰهُ يٰعِيْسٰٓى اِنِّيْ مُتَوَفِّيْكَ وَرَافِعُكَ اِلَيَّ وَمُطَهِّرُكَ مِنَ الَّذِيْنَ كَفَرُوْا وَجَاعِلُ الَّذِيْنَ اتَّبَعُوْكَ فَوْقَ الَّذِيْنَ كَفَرُوْٓا اِلٰى يَوْمِ الْقِيٰمَةِ ۚ ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاَحْكُمُ بَيْنَكُمْ فِيْمَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ","bc":"iż qālallāhu yā 'īsā innī mutawaffīka wa rāfi'uka ilayya wa muṭahhiruka minallażīna kafarụ wa jā'ilullażīnattaba'ụka fauqallażīna kafarū ilā yaumil-qiyāmah, ṡumma ilayya marji'ukum fa aḥkumu bainakum fīmā kuntum fīhi takhtalifụn","at":"(Ingatlah), ketika Allah berfirman, “Wahai Isa! Aku mengambilmu dan mengangkatmu kepada-Ku, serta menyucikanmu dari orang-orang yang kafir, dan menjadikan orang-orang yang mengikutimu di atas orang-orang yang kafir hingga hari Kiamat. Kemudian kepada-Ku engkau kembali, lalu Aku beri keputusan tentang apa yang kamu perselisihkan.”"},{"id":"56","ar":"فَاَمَّا الَّذِيْنَ كَفَرُوْا فَاُعَذِّبُهُمْ عَذَابًا شَدِيْدًا فِى الدُّنْيَا وَالْاٰخِرَةِۖ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ ","bc":"fa ammallażīna kafarụ fa u'ażżibuhum 'ażāban syadīdan fid-dun-yā wal-ākhirati wa mā lahum min nāṣirīn","at":"Maka adapun orang-orang yang kafir, maka akan Aku azab mereka dengan azab yang sangat keras di dunia dan di akhirat, sedang mereka tidak memperoleh penolong."},{"id":"57","ar":"وَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَيُوَفِّيْهِمْ اُجُوْرَهُمْ ۗ وَاللّٰهُ لَا يُحِبُّ الظّٰلِمِيْنَ","bc":"wa ammallażīna āmanụ wa 'amiluṣ-ṣāliḥāti fa yuwaffīhim ujụrahum, wallāhu lā yuḥibbuẓ-ẓālimīn","at":"Dan adapun orang yang beriman dan melakukan kebajikan, maka Dia akan memberikan pahala kepada mereka dengan sempurna. Dan Allah tidak menyukai orang zalim."},{"id":"58","ar":"ذٰلِكَ نَتْلُوْهُ عَلَيْكَ مِنَ الْاٰيٰتِ وَالذِّكْرِ الْحَكِيْمِ ","bc":"żālika natlụhu 'alaika minal-āyāti waż-żikril-ḥakīm","at":"Demikianlah Kami bacakan kepadamu (Muhammad) sebagian ayat-ayat dan peringatan yang penuh hikmah."},{"id":"59","ar":"اِنَّ مَثَلَ عِيْسٰى عِنْدَ اللّٰهِ كَمَثَلِ اٰدَمَ ۗ خَلَقَهٗ مِنْ تُرَابٍ ثُمَّ قَالَ لَهٗ كُنْ فَيَكُوْنُ","bc":"inna maṡala 'īsā 'indallāhi kamaṡali ādam, khalaqahụ min turābin ṡumma qāla lahụ kun fa yakụn","at":"Sesungguhnya perumpamaan (penciptaan) Isa bagi Allah, seperti (penciptaan) Adam. Dia menciptakannya dari tanah, kemudian Dia berkata kepadanya, “Jadilah!” Maka jadilah sesuatu itu."},{"id":"60","ar":"اَلْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُنْ مِّنَ الْمُمْتَرِيْنَ","bc":"al-ḥaqqu mir rabbika fa lā takum minal-mumtarīn","at":"Kebenaran itu dari Tuhanmu, karena itu janganlah engkau (Muhammad) termasuk orang-orang yang ragu."},{"id":"61","ar":"فَمَنْ حَاۤجَّكَ فِيْهِ مِنْۢ بَعْدِ مَا جَاۤءَكَ مِنَ الْعِلْمِ فَقُلْ تَعَالَوْا نَدْعُ اَبْنَاۤءَنَا وَاَبْنَاۤءَكُمْ وَنِسَاۤءَنَا وَنِسَاۤءَكُمْ وَاَنْفُسَنَا وَاَنْفُسَكُمْۗ ثُمَّ نَبْتَهِلْ فَنَجْعَلْ لَّعْنَتَ اللّٰهِ عَلَى الْكٰذِبِيْنَ","bc":"fa man ḥājjaka fīhi mim ba'di mā jā`aka minal-'ilmi fa qul ta'ālau nad'u abnā`anā wa abnā`akum wa nisā`anā wa nisā`akum wa anfusanā wa anfusakum, ṡumma nabtahil fa naj'al la'natallāhi 'alal-kāżibīn","at":"Siapa yang membantahmu dalam hal ini setelah engkau memperoleh ilmu, katakanlah (Muhammad), “Marilah kita panggil anak-anak kami dan anak-anak kamu, istri-istri kami dan istri-istrimu, kami sendiri dan kamu juga, kemudian marilah kita bermubahalah agar laknat Allah ditimpakan kepada orang-orang yang dusta.”"},{"id":"62","ar":"اِنَّ هٰذَا لَهُوَ الْقَصَصُ الْحَقُّ ۚ وَمَا مِنْ اِلٰهٍ اِلَّا اللّٰهُ ۗوَاِنَّ اللّٰهَ لَهُوَ الْعَزِيْزُ الْحَكِيْمُ","bc":"inna hāżā lahuwal-qaṣaṣul-ḥaqq, wa mā min ilāhin illallāh, wa innallāha lahuwal-'azīzul-ḥakīm","at":"Sungguh, ini adalah kisah yang benar. Tidak ada tuhan selain Allah, dan sungguh, Allah Mahaperkasa, Mahabijaksana."},{"id":"63","ar":"فَاِنْ تَوَلَّوْا فَاِنَّ اللّٰهَ عَلِيْمٌ ۢبِالْمُفْسِدِيْنَ ","bc":"fa in tawallau fa innallāha 'alīmum bil-mufsidīn","at":"Kemudian jika mereka berpaling, maka (ketahuilah) bahwa Allah Maha Mengetahui orang-orang yang berbuat kerusakan."},{"id":"64","ar":"قُلْ يٰٓاَهْلَ الْكِتٰبِ تَعَالَوْا اِلٰى كَلِمَةٍ سَوَاۤءٍۢ بَيْنَنَا وَبَيْنَكُمْ اَلَّا نَعْبُدَ اِلَّا اللّٰهَ وَلَا نُشْرِكَ بِهٖ شَيْـًٔا وَّلَا يَتَّخِذَ بَعْضُنَا بَعْضًا اَرْبَابًا مِّنْ دُوْنِ اللّٰهِ ۗ فَاِنْ تَوَلَّوْا فَقُوْلُوا اشْهَدُوْا بِاَنَّا مُسْلِمُوْنَ","bc":"qul yā ahlal-kitābi ta'ālau ilā kalimatin sawā`im bainanā wa bainakum allā na'buda illallāha wa lā nusyrika bihī syai`aw wa lā yattakhiża ba'ḍunā ba'ḍan arbābam min dụnillāh, fa in tawallau fa qụlusy-hadụ bi`annā muslimụn","at":"Katakanlah (Muhammad), “Wahai Ahli Kitab! Marilah (kita) menuju kepada satu kalimat (pegangan) yang sama antara kami dan kamu, bahwa kita tidak menyembah selain Allah dan kita tidak mempersekutukan-Nya dengan sesuatu pun, dan bahwa kita tidak menjadikan satu sama lain tuhan-tuhan selain Allah. Jika mereka berpaling maka katakanlah (kepada mereka), “Saksikanlah, bahwa kami adalah orang Muslim.”"},{"id":"65","ar":"يٰٓاَهْلَ الْكِتٰبِ لِمَ تُحَاۤجُّوْنَ فِيْٓ اِبْرٰهِيْمَ وَمَآ اُنْزِلَتِ التَّوْرٰىةُ وَالْاِنْجِيْلُ اِلَّا مِنْۢ بَعْدِهٖۗ اَفَلَا تَعْقِلُوْنَ","bc":"yā ahlal-kitābi lima tuḥājjụna fī ibrāhīma wa mā unzilatit-taurātu wal-injīlu illā mim ba'dih, a fa lā ta'qilụn","at":"Wahai Ahli Kitab! Mengapa kamu berbantah-bantahan tentang Ibrahim, padahal Taurat dan Injil diturunkan setelah dia (Ibrahim)? Apakah kamu tidak mengerti?"},{"id":"66","ar":"هٰٓاَنْتُمْ هٰٓؤُلَاۤءِ حَاجَجْتُمْ فِيْمَا لَكُمْ بِهٖ عِلْمٌ فَلِمَ تُحَاۤجُّوْنَ فِيْمَا لَيْسَ لَكُمْ بِهٖ عِلْمٌ ۗ وَاللّٰهُ يَعْلَمُ واَنْتُمْ لَا تَعْلَمُوْنَ","bc":"hā`antum hā`ulā`i ḥājajtum fīmā lakum bihī 'ilmun fa lima tuḥājjụna fīmā laisa lakum bihī 'ilm, wallāhu ya'lamu wa antum lā ta'lamụn","at":"Begitulah kamu! Kamu berbantah-bantahan tentang apa yang kamu ketahui, tetapi mengapa kamu berbantah-bantahan juga tentang apa yang tidak kamu ketahui? Allah mengetahui sedang kamu tidak mengetahui."},{"id":"67","ar":"مَاكَانَ اِبْرٰهِيْمُ يَهُوْدِيًّا وَّلَا نَصْرَانِيًّا وَّلٰكِنْ كَانَ حَنِيْفًا مُّسْلِمًاۗ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ","bc":"mā kāna ibrāhīmu yahụdiyyaw wa lā naṣrāniyyaw wa lāking kāna ḥanīfam muslimā, wa mā kāna minal-musyrikīn","at":"Ibrahim bukanlah seorang Yahudi dan bukan (pula) seorang Nasrani, tetapi dia adalah seorang yang lurus, Muslim dan dia tidaklah termasuk orang-orang musyrik."},{"id":"68","ar":"اِنَّ اَوْلَى النَّاسِ بِاِبْرٰهِيْمَ لَلَّذِيْنَ اتَّبَعُوْهُ وَهٰذَا النَّبِيُّ وَالَّذِيْنَ اٰمَنُوْا ۗ وَاللّٰهُ وَلِيُّ الْمُؤْمِنِيْنَ","bc":"inna aulan-nāsi bi`ibrāhīma lallażīnattaba'ụhu wa hāżan-nabiyyu wallażīna āmanụ, wallāhu waliyyul-mu`minīn","at":"Orang yang paling dekat kepada Ibrahim ialah orang yang mengikutinya, dan Nabi ini (Muhammad), dan orang yang beriman. Allah adalah pelindung orang-orang yang beriman."},{"id":"69","ar":"وَدَّتْ طَّاۤىِٕفَةٌ مِّنْ اَهْلِ الْكِتٰبِ لَوْ يُضِلُّوْنَكُمْۗ وَمَا يُضِلُّوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَشْعُرُوْنَ","bc":"waddaṭ ṭā`ifatum min ahlil-kitābi lau yuḍillụnakum, wa mā yuḍillụna illā anfusahum wa mā yasy'urụn","at":"Segolongan Ahli Kitab ingin menyesatkan kamu. Padahal (sesungguhnya), mereka tidak menyesatkan melainkan diri mereka sendiri, tetapi mereka tidak menyadari."},{"id":"70","ar":"يٰٓاَهْلَ الْكِتٰبِ لِمَ تَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَاَنْتُمْ تَشْهَدُوْنَ","bc":"yā ahlal-kitābi lima takfurụna bi`āyātillāhi wa antum tasy-hadụn","at":"Wahai Ahli Kitab! Mengapa kamu mengingkari ayat-ayat Allah, padahal kamu mengetahui (kebenarannya)?"},{"id":"71","ar":"يٰٓاَهْلَ الْكِتٰبِ لِمَ تَلْبِسُوْنَ الْحَقَّ بِالْبَاطِلِ وَتَكْتُمُوْنَ الْحَقَّ وَاَنْتُمْ تَعْلَمُوْنَ","bc":"yā ahlal-kitābi lima talbisụnal-ḥaqqa bil-bāṭili wa taktumụnal-ḥaqqa wa antum ta'lamụn","at":"Wahai Ahli Kitab! Mengapa kamu mencampuradukkan kebenaran dengan kebatilan, dan kamu menyembunyikan kebenaran, padahal kamu mengetahui?"},{"id":"72","ar":"وَقَالَتْ طَّاۤىِٕفَةٌ مِّنْ اَهْلِ الْكِتٰبِ اٰمِنُوْا بِالَّذِيْٓ اُنْزِلَ عَلَى الَّذِيْنَ اٰمَنُوْا وَجْهَ النَّهَارِ وَاكْفُرُوْٓا اٰخِرَهٗ لَعَلَّهُمْ يَرْجِعُوْنَۚ","bc":"wa qālaṭ ṭā`ifatum min ahlil-kitābi āminụ billażī unzila 'alallażīna āmanụ waj-han-nahāri wakfurū ākhirahụ la'allahum yarji'ụn","at":"Dan segolongan Ahli Kitab berkata (kepada sesamanya), “Berimanlah kamu kepada apa yang diturunkan kepada orang-orang beriman pada awal siang dan ingkarilah di akhirnya, agar mereka kembali (kepada kekafiran)."},{"id":"73","ar":"وَلَا تُؤْمِنُوْٓا اِلَّا لِمَنْ تَبِعَ دِيْنَكُمْ ۗ قُلْ اِنَّ الْهُدٰى هُدَى اللّٰهِ ۙ اَنْ يُّؤْتٰىٓ اَحَدٌ مِّثْلَ مَآ اُوْتِيْتُمْ اَوْ يُحَاۤجُّوْكُمْ عِنْدَ رَبِّكُمْ ۗ قُلْ اِنَّ الْفَضْلَ بِيَدِ اللّٰهِ ۚ يُؤْتِيْهِ مَنْ يَّشَاۤءُ ۗوَاللّٰهُ وَاسِعٌ عَلِيْمٌ ۚ","bc":"wa lā tu`minū illā liman tabi'a dīnakum, qul innal-hudā hudallāhi ay yu`tā aḥadum miṡla mā ụtītum au yuḥājjụkum 'inda rabbikum, qul innal-faḍla biyadillāh, yu`tīhi may yasyā`, wallāhu wāsi'un 'alīm","at":"Dan janganlah kamu percaya selain kepada orang yang mengikuti agamamu.” Katakanlah (Muhammad), “Sesungguhnya petunjuk itu hanyalah petunjuk Allah. (Janganlah kamu percaya) bahwa seseorang akan diberi seperti apa yang diberikan kepada kamu, atau bahwa mereka akan menyanggah kamu di hadapan Tuhanmu.” Katakanlah (Muhammad), “Sesungguhnya karunia itu di tangan Allah, Dia memberikannya kepada siapa yang Dia kehendaki. Allah Mahaluas, Maha Mengetahui.”"},{"id":"74","ar":"يَخْتَصُّ بِرَحْمَتِهٖ مَنْ يَّشَاۤءُ ۗوَاللّٰهُ ذُو الْفَضْلِ الْعَظِيْمِ","bc":"yakhtaṣṣu biraḥmatihī may yasyā`, wallāhu żul-faḍlil-'aẓīm","at":"Dia menentukan rahmat-Nya kepada siapa yang Dia kehendaki. Dan Allah memiliki karunia yang besar."},{"id":"75","ar":"۞ وَمِنْ اَهْلِ الْكِتٰبِ مَنْ اِنْ تَأْمَنْهُ بِقِنْطَارٍ يُّؤَدِّهٖٓ اِلَيْكَۚ وَمِنْهُمْ مَّنْ اِنْ تَأْمَنْهُ بِدِيْنَارٍ لَّا يُؤَدِّهٖٓ اِلَيْكَ اِلَّا مَا دُمْتَ عَلَيْهِ قَاۤىِٕمًا ۗ ذٰلِكَ بِاَنَّهُمْ قَالُوْا لَيْسَ عَلَيْنَا فِى الْاُمِّيّٖنَ سَبِيْلٌۚ وَيَقُوْلُوْنَ عَلَى اللّٰهِ الْكَذِبَ وَهُمْ يَعْلَمُوْنَ ","bc":"wa min ahlil-kitābi man in ta`man-hu biqinṭāriy yu`addihī ilaīk, wa min-hum man in ta`man-hu bidīnāril lā yu`addihī ilaika illā mā dumta 'alaihi qā`imā, żālika bi`annahum qālụ laisa 'alainā fil-ummiyyīna sabīl, wa yaqụlụna 'alallāhil-każiba wa hum ya'lamụn","at":"Dan di antara Ahli Kitab ada yang jika engkau percayakan kepadanya harta yang banyak, niscaya dia mengembalikannya kepadamu. Tetapi ada (pula) di antara mereka yang jika engkau percayakan kepadanya satu dinar, dia tidak mengembalikannya kepadamu, kecuali jika engkau selalu menagihnya. Yang demikian itu disebabkan mereka berkata, “Tidak ada dosa bagi kami terhadap orang-orang buta huruf.” Mereka mengatakan hal yang dusta terhadap Allah, padahal mereka mengetahui."},{"id":"76","ar":"بَلٰى مَنْ اَوْفٰى بِعَهْدِهٖ وَاتَّقٰى فَاِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ","bc":"balā man aufā bi'ahdihī wattaqā fa innallāha yuḥibbul-muttaqīn","at":"Sebenarnya barangsiapa menepati janji dan bertakwa, maka sungguh, Allah mencintai orang-orang yang bertakwa."},{"id":"77","ar":"اِنَّ الَّذِيْنَ يَشْتَرُوْنَ بِعَهْدِ اللّٰهِ وَاَيْمَانِهِمْ ثَمَنًا قَلِيْلًا اُولٰۤىِٕكَ لَا خَلَاقَ لَهُمْ فِى الْاٰخِرَةِ وَلَا يُكَلِّمُهُمُ اللّٰهُ وَلَا يَنْظُرُ اِلَيْهِمْ يَوْمَ الْقِيٰمَةِ وَلَا يُزَكِّيْهِمْ ۖ وَلَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"innallażīna yasytarụna bi'ahdillāhi wa aimānihim ṡamanang qalīlan ulā`ika lā khalāqa lahum fil-ākhirati wa lā yukallimuhumullāhu wa lā yanẓuru ilaihim yaumal-qiyāmati wa lā yuzakkīhim wa lahum 'ażābun alīm","at":"Sesungguhnya orang-orang yang memperjualbelikan janji Allah dan sumpah-sumpah mereka dengan harga murah, mereka itu tidak memperoleh bagian di akhirat, Allah tidak akan menyapa mereka, tidak akan memperhatikan mereka pada hari Kiamat, dan tidak akan menyucikan mereka. Bagi mereka azab yang pedih."},{"id":"78","ar":"وَاِنَّ مِنْهُمْ لَفَرِيْقًا يَّلْوٗنَ اَلْسِنَتَهُمْ بِالْكِتٰبِ لِتَحْسَبُوْهُ مِنَ الْكِتٰبِ وَمَا هُوَ مِنَ الْكِتٰبِۚ وَيَقُوْلُوْنَ هُوَ مِنْ عِنْدِ اللّٰهِ وَمَا هُوَ مِنْ عِنْدِ اللّٰهِ ۚ وَيَقُوْلُوْنَ عَلَى اللّٰهِ الْكَذِبَ وَهُمْ يَعْلَمُوْنَ","bc":"wa inna min-hum lafarīqay yalwụna alsinatahum bil-kitābi litaḥsabụhu minal-kitābi wa mā huwa minal-kitāb, wa yaqụlụna huwa min 'indillāhi wa mā huwa min 'indillāh, wa yaqụlụna 'alallāhil-każiba wa hum ya'lamụn","at":"Dan sungguh, di antara mereka niscaya ada segolongan yang memutarbalikkan lidahnya membaca Kitab, agar kamu menyangka (yang mereka baca) itu sebagian dari Kitab, padahal itu bukan dari Kitab dan mereka berkata, “Itu dari Allah,” padahal itu bukan dari Allah. Mereka mengatakan hal yang dusta terhadap Allah, padahal mereka mengetahui."},{"id":"79","ar":"مَا كَانَ لِبَشَرٍ اَنْ يُّؤْتِيَهُ اللّٰهُ الْكِتٰبَ وَالْحُكْمَ وَالنُّبُوَّةَ ثُمَّ يَقُوْلَ لِلنَّاسِ كُوْنُوْا عِبَادًا لِّيْ مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ كُوْنُوْا رَبَّانِيّٖنَ بِمَا كُنْتُمْ تُعَلِّمُوْنَ الْكِتٰبَ وَبِمَا كُنْتُمْ تَدْرُسُوْنَ ۙ","bc":"mā kāna libasyarin ay yu`tiyahullāhul-kitāba wal-ḥukma wan-nubuwwata ṡumma yaqụla lin-nāsi kụnụ 'ibādal lī min dụnillāhi wa lāking kụnụ rabbāniyyīna bimā kuntum tu'allimụnal-kitāba wa bimā kuntum tadrusụn","at":"Tidak mungkin bagi seseorang yang telah diberi kitab oleh Allah, serta hikmah dan kenabian, kemudian dia berkata kepada manusia, “Jadilah kamu penyembahku, bukan penyembah Allah,” tetapi (dia berkata), “Jadilah kamu pengabdi-pengabdi Allah, karena kamu mengajarkan kitab dan karena kamu mempelajarinya!”"},{"id":"80","ar":"وَلَا يَأْمُرَكُمْ اَنْ تَتَّخِذُوا الْمَلٰۤىِٕكَةَ وَالنَّبِيّٖنَ اَرْبَابًا ۗ اَيَأْمُرُكُمْ بِالْكُفْرِ بَعْدَ اِذْ اَنْتُمْ مُّسْلِمُوْنَ","bc":"wa lā ya`murakum an tattakhiżul-malā`ikata wan-nabiyyīna arbābā, a ya`murukum bil-kufri ba'da iż antum muslimụn","at":"dan tidak (mungkin pula baginya) menyuruh kamu menjadikan para malaikat dan para nabi sebagai Tuhan. Apakah (patut) dia menyuruh kamu menjadi kafir setelah kamu menjadi Muslim?"},{"id":"81","ar":"وَاِذْ اَخَذَ اللّٰهُ مِيْثَاقَ النَّبِيّٖنَ لَمَآ اٰتَيْتُكُمْ مِّنْ كِتٰبٍ وَّحِكْمَةٍ ثُمَّ جَاۤءَكُمْ رَسُوْلٌ مُّصَدِّقٌ لِّمَا مَعَكُمْ لَتُؤْمِنُنَّ بِهٖ وَلَتَنْصُرُنَّهٗ ۗ قَالَ ءَاَقْرَرْتُمْ وَاَخَذْتُمْ عَلٰى ذٰلِكُمْ اِصْرِيْ ۗ قَالُوْٓا اَقْرَرْنَا ۗ قَالَ فَاشْهَدُوْا وَاَنَا۠ مَعَكُمْ مِّنَ الشّٰهِدِيْنَ","bc":"wa iż akhażallāhu mīṡāqan-nabiyyīna lamā ātaitukum ming kitābiw wa ḥikmatin ṡumma jā`akum rasụlum muṣaddiqul limā ma'akum latu`minunna bihī wa latanṣurunnah, qāla a aqrartum wa akhażtum 'alā żālikum iṣrī, qālū aqrarnā, qāla fasy-hadụ wa ana ma'akum minasy-syāhidīn","at":"Dan (ingatlah), ketika Allah mengambil perjanjian dari para nabi, “Manakala Aku memberikan kitab dan hikmah kepadamu lalu datang kepada kamu seorang Rasul yang membenarkan apa yang ada pada kamu, niscaya kamu akan sungguh-sungguh beriman kepadanya dan menolongnya.” Allah berfirman, “Apakah kamu setuju dan menerima perjanjian dengan-Ku atas yang demikian itu?” Mereka menjawab, “Kami setuju.” Allah berfirman, ”Kalau begitu bersaksilah kamu (para nabi) dan Aku menjadi saksi bersama kamu.”"},{"id":"82","ar":"فَمَنْ تَوَلّٰى بَعْدَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ","bc":"fa man tawallā ba'da żālika fa ulā`ika humul-fāsiqụn","at":"Maka barangsiapa berpaling setelah itu, maka mereka itulah orang yang fasik."},{"id":"83","ar":"اَفَغَيْرَ دِيْنِ اللّٰهِ يَبْغُوْنَ وَلَهٗ ٓ اَسْلَمَ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ طَوْعًا وَّكَرْهًا وَّاِلَيْهِ يُرْجَعُوْنَ","bc":"a fa gaira dīnillāhi yabgụna wa lahū aslama man fis-samāwāti wal-arḍi ṭau'aw wa kar-haw wa ilaihi yurja'ụn","at":"Maka mengapa mereka mencari agama yang lain selain agama Allah, padahal apa yang di langit dan di bumi berserah diri kepada-Nya, (baik) dengan suka maupun terpaksa, dan hanya kepada-Nya mereka dikembalikan?"},{"id":"84","ar":"قُلْ اٰمَنَّا بِاللّٰهِ وَمَآ اُنْزِلَ عَلَيْنَا وَمَآ اُنْزِلَ عَلٰٓى اِبْرٰهِيْمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَمَآ اُوْتِيَ مُوْسٰى وَعِيْسٰى وَالنَّبِيُّوْنَ مِنْ رَّبِّهِمْۖ لَا نُفَرِّقُ بَيْنَ اَحَدٍ مِّنْهُمْۖ وَنَحْنُ لَهٗ مُسْلِمُوْنَ","bc":"qul āmannā billāhi wa mā unzila 'alainā wa mā unzila 'alā ibrāhīma wa ismā'īla wa is-ḥāqa wa ya'qụba wal-asbāṭi wa mā ụtiya mụsā wa 'īsā wan-nabiyyụna mir rabbihim lā nufarriqu baina aḥadim min-hum wa naḥnu lahụ muslimụn","at":"Katakanlah (Muhammad), “Kami beriman kepada Allah dan kepada apa yang diturunkan kepada kami dan yang diturunkan kepada Ibrahim, Ismail, Ishak, Yakub, dan anak cucunya, dan apa yang diberikan kepada Musa, Isa dan para nabi dari Tuhan mereka. Kami tidak membeda-bedakan seorang pun di antara mereka dan hanya kepada-Nya kami berserah diri.”"},{"id":"85","ar":"وَمَنْ يَّبْتَغِ غَيْرَ الْاِسْلَامِ دِيْنًا فَلَنْ يُّقْبَلَ مِنْهُۚ وَهُوَ فِى الْاٰخِرَةِ مِنَ الْخٰسِرِيْنَ","bc":"wa may yabtagi gairal-islāmi dīnan fa lay yuqbala min-h, wa huwa fil-ākhirati minal-khāsirīn","at":"Dan barangsiapa mencari agama selain Islam, dia tidak akan diterima, dan di akhirat dia termasuk orang yang rugi."},{"id":"86","ar":"كَيْفَ يَهْدِى اللّٰهُ قَوْمًا كَفَرُوْا بَعْدَ اِيْمَانِهِمْ وَشَهِدُوْٓا اَنَّ الرَّسُوْلَ حَقٌّ وَّجَاۤءَهُمُ الْبَيِّنٰتُ ۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ","bc":"kaifa yahdillāhu qaumang kafarụ ba'da īmānihim wa syahidū annar-rasụla ḥaqquw wa jā`ahumul-bayyināt, wallāhu lā yahdil-qaumaẓ-ẓālimīn","at":"Bagaimana Allah akan memberi petunjuk kepada suatu kaum yang kafir setelah mereka beriman, serta mengakui bahwa Rasul (Muhammad) itu benar-benar (rasul), dan bukti-bukti yang jelas telah sampai kepada mereka? Allah tidak memberi petunjuk kepada orang zalim."},{"id":"87","ar":"اُولٰۤىِٕكَ جَزَاۤؤُهُمْ اَنَّ عَلَيْهِمْ لَعْنَةَ اللّٰهِ وَالْمَلٰۤىِٕكَةِ وَالنَّاسِ اَجْمَعِيْنَۙ","bc":"ulā`ika jazā`uhum anna 'alaihim la'natallāhi wal-malā`ikati wan-nāsi ajma'īn","at":"Mereka itu, balasannya ialah ditimpa laknat Allah, para malaikat, dan manusia seluruhnya,"},{"id":"88","ar":"خٰلِدِيْنَ فِيْهَا ۚ لَا يُخَفَّفُ عَنْهُمُ الْعَذَابُ وَلَا هُمْ يُنْظَرُوْنَۙ","bc":"khālidīna fīhā, lā yukhaffafu 'an-humul-'ażābu wa lā hum yunẓarụn","at":"mereka kekal di dalamnya, tidak akan diringankan azabnya, dan mereka tidak diberi penangguhan,"},{"id":"89","ar":"اِلَّا الَّذِيْنَ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْاۗ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ","bc":"illallażīna tābụ mim ba'di żālika wa aṣlaḥụ, fa innallāha gafụrur raḥīm","at":"kecuali orang-orang yang bertobat setelah itu, dan melakukan perbaikan, maka sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"90","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا بَعْدَ اِيْمَانِهِمْ ثُمَّ ازْدَادُوْا كُفْرًا لَّنْ تُقْبَلَ تَوْبَتُهُمْ ۚ وَاُولٰۤىِٕكَ هُمُ الضَّاۤلُّوْنَ","bc":"innallażīna kafarụ ba'da īmānihim ṡummazdādụ kufral lan tuqbala taubatuhum, wa ulā`ika humuḍ-ḍāllụn","at":"Sungguh, orang-orang yang kafir setelah beriman, kemudian bertambah kekafirannya, tidak akan diterima tobatnya, dan mereka itulah orang-orang yang sesat."},{"id":"91","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا وَمَاتُوْا وَهُمْ كُفَّارٌ فَلَنْ يُّقْبَلَ مِنْ اَحَدِهِمْ مِّلْءُ الْاَرْضِ ذَهَبًا وَّلَوِ افْتَدٰى بِهٖۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ اَلِيْمٌ وَّمَا لَهُمْ مِّنْ نّٰصِرِيْنَ ۔","bc":"innallażīna kafarụ wa mātụ wa hum kuffārun fa lay yuqbala min aḥadihim mil`ul-arḍi żahabaw wa lawiftadā bih, ulā`ika lahum 'ażābun alīmuw wa mā lahum min nāṣirīn","at":"Sungguh, orang-orang yang kafir dan mati dalam kekafiran, tidak akan diterima (tebusan) dari seseorang di antara mereka sekalipun (berupa) emas sepenuh bumi, sekiranya dia hendak menebus diri dengannya. Mereka itulah orang-orang yang mendapat azab yang pedih dan tidak memperoleh penolong."},{"id":"92","ar":"لَنْ تَنَالُوا الْبِرَّ حَتّٰى تُنْفِقُوْا مِمَّا تُحِبُّوْنَ ۗوَمَا تُنْفِقُوْا مِنْ شَيْءٍ فَاِنَّ اللّٰهَ بِهٖ عَلِيْمٌ ","bc":"lan tanālul-birra ḥattā tunfiqụ mimmā tuḥibbụn, wa mā tunfiqụ min syai`in fa innallāha bihī 'alīm","at":"Kamu tidak akan memperoleh kebajikan, sebelum kamu menginfakkan sebagian harta yang kamu cintai. Dan apa pun yang kamu infakkan, tentang hal itu sungguh, Allah Maha Mengetahui."},{"id":"93","ar":"۞ كُلُّ الطَّعَامِ كَانَ حِلًّا لِّبَنِيْٓ اِسْرَاۤءِيْلَ اِلَّا مَا حَرَّمَ اِسْرَاۤءِيْلُ عَلٰى نَفْسِهٖ مِنْ قَبْلِ اَنْ تُنَزَّلَ التَّوْرٰىةُ ۗ قُلْ فَأْتُوْا بِالتَّوْرٰىةِ فَاتْلُوْهَآ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"kulluṭ-ṭa'āmi kāna ḥillal libanī isrā`īla illā mā ḥarrama isrā`īlu 'alā nafsihī ming qabli an tunazzalat-taurāh, qul fa`tụ bit-taurāti fatlụhā ing kuntum ṣādiqīn","at":"Semua makanan itu halal bagi Bani Israil, kecuali makanan yang diharamkan oleh Israil (Yakub) atas dirinya sebelum Taurat diturunkan. Katakanlah (Muhammad), “Maka bawalah Taurat lalu bacalah, jika kamu orang-orang yang benar.”"},{"id":"94","ar":"فَمَنِ افْتَرٰى عَلَى اللّٰهِ الْكَذِبَ مِنْۢ بَعْدِ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ","bc":"fa maniftarā 'alallāhil-każiba mim ba'di żālika fa ulā`ika humuẓ-ẓālimụn","at":"Maka barangsiapa mengada-adakan kebohongan terhadap Allah setelah itu, maka mereka itulah orang-orang zalim."},{"id":"95","ar":"قُلْ صَدَقَ اللّٰهُ ۗ فَاتَّبِعُوْا مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًاۗ وَمَا كَانَ مِنَ الْمُشْرِكِيْنَ","bc":"qul ṣadaqallāh, fattabi'ụ millata ibrāhīma ḥanīfā, wa mā kāna minal-musyrikīn","at":"Katakanlah (Muhammad), “Benarlah (segala yang difirmankan) Allah.” Maka ikutilah agama Ibrahim yang lurus, dan dia tidaklah termasuk orang musyrik."},{"id":"96","ar":"اِنَّ اَوَّلَ بَيْتٍ وُّضِعَ لِلنَّاسِ لَلَّذِيْ بِبَكَّةَ مُبٰرَكًا وَّهُدًى لِّلْعٰلَمِيْنَۚ","bc":"inna awwala baitiw wuḍi'a lin-nāsi lallażī bibakkata mubārakaw wa hudal lil-'ālamīn","at":"Sesungguhnya rumah (ibadah) pertama yang dibangun untuk manusia, ialah (Baitullah) yang di Bakkah (Mekah) yang diberkahi dan menjadi petunjuk bagi seluruh alam."},{"id":"97","ar":"فِيْهِ اٰيٰتٌۢ بَيِّنٰتٌ مَّقَامُ اِبْرٰهِيْمَ ەۚ وَمَنْ دَخَلَهٗ كَانَ اٰمِنًا ۗ وَلِلّٰهِ عَلَى النَّاسِ حِجُّ الْبَيْتِ مَنِ اسْتَطَاعَ اِلَيْهِ سَبِيْلًا ۗ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ عَنِ الْعٰلَمِيْنَ","bc":"fīhi āyātum bayyinātum maqāmu ibrāhīm, wa man dakhalahụ kāna āminā, wa lillāhi 'alan-nāsi ḥijjul-baiti manistaṭā'a ilaihi sabīlā, wa mang kafara fa innallāha ganiyyun 'anil-'ālamīn","at":"Di sana terdapat tanda-tanda yang jelas, (di antaranya) maqam Ibrahim. Barangsiapa memasukinya (Baitullah) amanlah dia. Dan (di antara) kewajiban manusia terhadap Allah adalah melaksanakan ibadah haji ke Baitullah, yaitu bagi orang-orang yang mampu mengadakan perjalanan ke sana. Barangsiapa mengingkari (kewajiban) haji, maka ketahuilah bahwa Allah Mahakaya (tidak memerlukan sesuatu) dari seluruh alam."},{"id":"98","ar":"قُلْ يٰٓاَهْلَ الْكِتٰبِ لِمَ تَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَاللّٰهُ شَهِيْدٌ عَلٰى مَا تَعْمَلُوْنَ","bc":"qul yā ahlal-kitābi lima takfurụna bi`āyātillāhi wallāhu syahīdun 'alā mā ta'malụn","at":"Katakanlah (Muhammad), “Wahai Ahli Kitab! Mengapa kamu mengingkari ayat-ayat Allah, padahal Allah Maha Menyaksikan apa yang kamu kerjakan?”"},{"id":"99","ar":"قُلْ يٰٓاَهْلَ الْكِتٰبِ لِمَ تَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ مَنْ اٰمَنَ تَبْغُوْنَهَا عِوَجًا وَّاَنْتُمْ شُهَدَاۤءُ ۗ وَمَا اللّٰهُ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ","bc":"qul yā ahlal-kitābi lima taṣuddụna 'an sabīlillāhi man āmana tabgụnahā 'iwajaw wa antum syuhadā`, wa mallāhu bigāfilin 'ammā ta'malụn","at":"Katakanlah (Muhammad), “Wahai Ahli Kitab! Mengapa kamu menghalang-halangi orang-orang yang beriman dari jalan Allah, kamu menghendakinya (jalan Allah) bengkok, padahal kamu menyaksikan?” Dan Allah tidak lengah terhadap apa yang kamu kerjakan."},{"id":"100","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تُطِيْعُوْا فَرِيْقًا مِّنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ يَرُدُّوْكُمْ بَعْدَ اِيْمَانِكُمْ كٰفِرِيْنَ ","bc":"yā ayyuhallażīna āmanū in tuṭī'ụ farīqam minallażīna ụtul-kitāba yaruddụkum ba'da īmānikum kāfirīn","at":"Wahai orang-orang yang beriman! Jika kamu mengikuti sebagian dari orang yang diberi Kitab, niscaya mereka akan mengembalikan kamu menjadi orang kafir setelah beriman."},{"id":"101","ar":"وَكَيْفَ تَكْفُرُوْنَ وَاَنْتُمْ تُتْلٰى عَلَيْكُمْ اٰيٰتُ اللّٰهِ وَفِيْكُمْ رَسُوْلُهٗ ۗ وَمَنْ يَّعْتَصِمْ بِاللّٰهِ فَقَدْ هُدِيَ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ","bc":"wa kaifa takfurụna wa antum tutlā 'alaikum āyātullāhi wa fīkum rasụluh, wa may ya'taṣim billāhi fa qad hudiya ilā ṣirāṭim mustaqīm","at":"Dan bagaimana kamu (sampai) menjadi kafir, padahal ayat-ayat Allah dibacakan kepada kamu, dan Rasul-Nya (Muhammad) pun berada di tengah-tengah kamu? Barangsiapa berpegang teguh kepada (agama) Allah, maka sungguh, dia diberi petunjuk kepada jalan yang lurus."},{"id":"102","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ حَقَّ تُقٰىتِهٖ وَلَا تَمُوْتُنَّ اِلَّا وَاَنْتُمْ مُّسْلِمُوْنَ ","bc":"yā ayyuhallażīna āmanuttaqullāha ḥaqqa tuqātihī wa lā tamụtunna illā wa antum muslimụn","at":"Wahai orang-orang yang beriman! Bertakwalah kepada Allah sebenar-benar takwa kepada-Nya dan janganlah kamu mati kecuali dalam keadaan Muslim."},{"id":"103","ar":"وَاعْتَصِمُوْا بِحَبْلِ اللّٰهِ جَمِيْعًا وَّلَا تَفَرَّقُوْا ۖوَاذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْ اِذْ كُنْتُمْ اَعْدَاۤءً فَاَلَّفَ بَيْنَ قُلُوْبِكُمْ فَاَصْبَحْتُمْ بِنِعْمَتِهٖٓ اِخْوَانًاۚ وَكُنْتُمْ عَلٰى شَفَا حُفْرَةٍ مِّنَ النَّارِ فَاَنْقَذَكُمْ مِّنْهَا ۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖ لَعَلَّكُمْ تَهْتَدُوْنَ ","bc":"wa'taṣimụ biḥablillāhi jamī'aw wa lā tafarraqụ ważkurụ ni'matallāhi 'alaikum iż kuntum a'dā`an fa allafa baina qulụbikum fa aṣbaḥtum bini'matihī ikhwānā, wa kuntum 'alā syafā ḥufratim minan-nāri fa angqażakum min-hā, każālika yubayyinullāhu lakum āyātihī la'allakum tahtadụn","at":"Dan berpegangteguhlah kamu semuanya pada tali (agama) Allah, dan janganlah kamu bercerai berai, dan ingatlah nikmat Allah kepadamu ketika kamu dahulu (masa jahiliah) bermusuhan, lalu Allah mempersatukan hatimu, sehingga dengan karunia-Nya kamu menjadi bersaudara, sedangkan (ketika itu) kamu berada di tepi jurang neraka, lalu Allah menyelamatkan kamu dari sana. Demikianlah, Allah menerangkan ayat-ayat-Nya kepadamu agar kamu mendapat petunjuk."},{"id":"104","ar":"وَلْتَكُنْ مِّنْكُمْ اُمَّةٌ يَّدْعُوْنَ اِلَى الْخَيْرِ وَيَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ ۗ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ","bc":"waltakum mingkum ummatuy yad'ụna ilal-khairi wa ya`murụna bil-ma'rụfi wa yan-hauna 'anil-mungkar, wa ulā`ika humul-mufliḥụn","at":"Dan hendaklah di antara kamu ada segolongan orang yang menyeru kepada kebajikan, menyuruh (berbuat) yang makruf, dan mencegah dari yang mungkar. Dan mereka itulah orang-orang yang beruntung."},{"id":"105","ar":"وَلَا تَكُوْنُوْا كَالَّذِيْنَ تَفَرَّقُوْا وَاخْتَلَفُوْا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْبَيِّنٰتُ ۗ وَاُولٰۤىِٕكَ لَهُمْ عَذَابٌ عَظِيْمٌ ۙ","bc":"wa lā takụnụ kallażīna tafarraqụ wakhtalafụ mim ba'di mā jā`ahumul-bayyināt, wa ulā`ika lahum 'ażābun 'aẓīm","at":"Dan janganlah kamu menjadi seperti orang-orang yang bercerai berai dan berselisih setelah sampai kepada mereka keterangan yang jelas. Dan Mereka itulah orang-orang yang mendapat azab yang berat,"},{"id":"106","ar":"يَّوْمَ تَبْيَضُّ وُجُوْهٌ وَّتَسْوَدُّ وُجُوْهٌ ۚ فَاَمَّا الَّذِيْنَ اسْوَدَّتْ وُجُوْهُهُمْۗ اَ كَفَرْتُمْ بَعْدَ اِيْمَانِكُمْ فَذُوْقُوا الْعَذَابَ بِمَا كُنْتُمْ تَكْفُرُوْنَ","bc":"yauma tabyaḍḍu wujụhuw wa taswaddu wujụh, fa ammallażīnaswaddat wujụhuhum, a kafartum ba'da īmānikum fa żụqul-'ażāba bimā kuntum takfurụn","at":"pada hari itu ada wajah yang putih berseri, dan ada pula wajah yang hitam muram. Adapun orang-orang yang berwajah hitam muram (kepada mereka dikatakan), “Mengapa kamu kafir setelah beriman? Karena itu rasakanlah azab disebabkan kekafiranmu itu.”"},{"id":"107","ar":"وَاَمَّا الَّذِيْنَ ابْيَضَّتْ وُجُوْهُهُمْ فَفِيْ رَحْمَةِ اللّٰهِ ۗ هُمْ فِيْهَا خٰلِدُوْنَ","bc":"wa ammallażīnabyaḍḍat wujụhuhum fa fī raḥmatillāh, hum fīhā khālidụn","at":"Dan adapun orang-orang yang berwajah putih berseri, mereka berada dalam rahmat Allah (surga); mereka kekal di dalamnya."},{"id":"108","ar":"تِلْكَ اٰيٰتُ اللّٰهِ نَتْلُوْهَا عَلَيْكَ بِالْحَقِّ ۗ وَمَا اللّٰهُ يُرِيْدُ ظُلْمًا لِّلْعٰلَمِيْنَ","bc":"tilka āyātullāhi natlụhā 'alaika bil-ḥaqq, wa mallāhu yurīdu ẓulmal lil-'ālamīn","at":"Itulah ayat-ayat Allah yang Kami bacakan kepada kamu dengan benar, dan Allah tidaklah berkehendak menzalimi (siapa pun) di seluruh alam."},{"id":"109","ar":"وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗوَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ","bc":"wa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa ilallāhi turja'ul-umụr","at":"Dan milik Allah-lah apa yang ada di langit dan apa yang ada di bumi, dan hanya kepada Allah segala urusan dikembalikan."},{"id":"110","ar":"كُنْتُمْ خَيْرَ اُمَّةٍ اُخْرِجَتْ لِلنَّاسِ تَأْمُرُوْنَ بِالْمَعْرُوْفِ وَتَنْهَوْنَ عَنِ الْمُنْكَرِ وَتُؤْمِنُوْنَ بِاللّٰهِ ۗ وَلَوْ اٰمَنَ اَهْلُ الْكِتٰبِ لَكَانَ خَيْرًا لَّهُمْ ۗ مِنْهُمُ الْمُؤْمِنُوْنَ وَاَكْثَرُهُمُ الْفٰسِقُوْنَ ","bc":"kuntum khaira ummatin ukhrijat lin-nāsi ta`murụna bil-ma'rụfi wa tan-hauna 'anil-mungkari wa tu`minụna billāh, walau āmana ahlul-kitābi lakāna khairal lahum, min-humul-mu`minụna wa akṡaruhumul-fāsiqụn","at":"Kamu (umat Islam) adalah umat terbaik yang dilahirkan untuk manusia, (karena kamu) menyuruh (berbuat) yang makruf, dan mencegah dari yang mungkar, dan beriman kepada Allah. Sekiranya Ahli Kitab beriman, tentulah itu lebih baik bagi mereka. Di antara mereka ada yang beriman, namun kebanyakan mereka adalah orang-orang fasik."},{"id":"111","ar":"لَنْ يَّضُرُّوْكُمْ اِلَّآ اَذًىۗ وَاِنْ يُّقَاتِلُوْكُمْ يُوَلُّوْكُمُ الْاَدْبَارَۗ ثُمَّ لَا يُنْصَرُوْنَ","bc":"lay yaḍurrụkum illā ażā, wa iy yuqātilụkum yuwallụkumul-adbār, ṡumma lā yunṣarụn","at":"Mereka tidak akan membahayakan kamu, kecuali gangguan-gangguan kecil saja, dan jika mereka memerangi kamu, niscaya mereka mundur berbalik ke belakang (kalah). Selanjutnya mereka tidak mendapat pertolongan."},{"id":"112","ar":"ضُرِبَتْ عَلَيْهِمُ الذِّلَّةُ اَيْنَ مَا ثُقِفُوْٓا اِلَّا بِحَبْلٍ مِّنَ اللّٰهِ وَحَبْلٍ مِّنَ النَّاسِ وَبَاۤءُوْ بِغَضَبٍ مِّنَ اللّٰهِ وَضُرِبَتْ عَلَيْهِمُ الْمَسْكَنَةُ ۗ ذٰلِكَ بِاَنَّهُمْ كَانُوْا يَكْفُرُوْنَ بِاٰيٰتِ اللّٰهِ وَيَقْتُلُوْنَ الْاَنْبِۢيَاۤءَ بِغَيْرِ حَقٍّۗ ذٰلِكَ بِمَا عَصَوْا وَّكَانُوْا يَعْتَدُوْنَ ","bc":"ḍuribat 'alaihimuż-żillatu aina mā ṡuqifū illā biḥablim minallāhi wa ḥablim minan-nāsi wa bā`ụ bigaḍabim minallāhi wa ḍuribat 'alaihimul-maskanah, żālika bi`annahum kānụ yakfurụna bi`āyātillāhi wa yaqtulụnal-ambiyā`a bigairi ḥaqq, żālika bimā 'aṣaw wa kānụ ya'tadụn","at":"Mereka diliputi kehinaan di mana saja mereka berada, kecuali jika mereka (berpegang) pada tali (agama) Allah dan tali (perjanjian) dengan manusia. Mereka mendapat murka dari Allah dan (selalu) diliputi kesengsaraan. Yang demikian itu karena mereka mengingkari ayat-ayat Allah dan membunuh para nabi, tanpa hak (alasan yang benar). Yang demikian itu karena mereka durhaka dan melampaui batas."},{"id":"113","ar":"۞ لَيْسُوْا سَوَاۤءً ۗ مِنْ اَهْلِ الْكِتٰبِ اُمَّةٌ قَاۤىِٕمَةٌ يَّتْلُوْنَ اٰيٰتِ اللّٰهِ اٰنَاۤءَ الَّيْلِ وَهُمْ يَسْجُدُوْنَ ","bc":"laisụ sawā`ā, min ahlil-kitābi ummatung qā`imatuy yatlụna āyātillāhi ānā`al-laili wa hum yasjudụn","at":"Mereka itu tidak (seluruhnya) sama. Di antara Ahli Kitab ada golongan yang jujur, mereka membaca ayat-ayat Allah pada malam hari, dan mereka (juga) bersujud (salat)."},{"id":"114","ar":"يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُسَارِعُوْنَ فِى الْخَيْرٰتِۗ وَاُولٰۤىِٕكَ مِنَ الصّٰلِحِيْنَ ","bc":"yu`minụna billāhi wal-yaumil-ākhiri wa ya`murụna bil-ma'rụfi wa yan-hauna 'anil-mungkari wa yusāri'ụna fil-khairāt, wa ulā`ika minaṣ-ṣāliḥīn","at":"Mereka beriman kepada Allah dan hari akhir, menyuruh (berbuat) yang makruf, dan mencegah dari yang mungkar dan bersegera (mengerjakan) berbagai kebajikan. Mereka termasuk orang-orang saleh."},{"id":"115","ar":"وَمَا يَفْعَلُوْا مِنْ خَيْرٍ فَلَنْ يُّكْفَرُوْهُ ۗ وَاللّٰهُ عَلِيْمٌ ۢبِالْمُتَّقِيْنَ","bc":"wa mā yaf'alụ min khairin fa lay yukfarụh, wallāhu 'alīmum bil-muttaqīn","at":"Dan kebajikan apa pun yang mereka kerjakan, tidak ada yang mengingkarinya. Dan Allah Maha Mengetahui orang-orang yang bertakwa."},{"id":"116","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا لَنْ تُغْنِيَ عَنْهُمْ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ مِّنَ اللّٰهِ شَيْـًٔا ۗ وَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚ هُمْ فِيْهَا خٰلِدُوْنَ ","bc":"innallażīna kafarụ lan tugniya 'an-hum amwāluhum wa lā aulāduhum minallāhi syai`ā, wa ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn","at":"Sesungguhnya orang-orang kafir, baik harta maupun anak-anak mereka, sedikit pun tidak dapat menolak azab Allah. Mereka itu penghuni neraka, (dan) mereka kekal di dalamnya."},{"id":"117","ar":"مَثَلُ مَا يُنْفِقُوْنَ فِيْ هٰذِهِ الْحَيٰوةِ الدُّنْيَا كَمَثَلِ رِيْحٍ فِيْهَا صِرٌّ اَصَابَتْ حَرْثَ قَوْمٍ ظَلَمُوْٓا اَنْفُسَهُمْ فَاَهْلَكَتْهُ ۗ وَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ اَنْفُسَهُمْ يَظْلِمُوْنَ ","bc":"maṡalu mā yunfiqụna fī hāżihil-ḥayātid-dun-yā kamaṡali rīḥin fīhā ṣirrun aṣābat ḥarṡa qaumin ẓalamū anfusahum fa ahlakat-h, wa mā ẓalamahumullāhu wa lākin anfusahum yaẓlimụn","at":"Perumpamaan harta yang mereka infakkan di dalam kehidupan dunia ini, ibarat angin yang mengandung hawa sangat dingin, yang menimpa tanaman (milik) suatu kaum yang menzalimi diri sendiri, lalu angin itu merusaknya. Allah tidak menzalimi mereka, tetapi mereka yang menzalimi diri sendiri."},{"id":"118","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوْا بِطَانَةً مِّنْ دُوْنِكُمْ لَا يَأْلُوْنَكُمْ خَبَالًاۗ وَدُّوْا مَا عَنِتُّمْۚ قَدْ بَدَتِ الْبَغْضَاۤءُ مِنْ اَفْوَاهِهِمْۖ وَمَا تُخْفِيْ صُدُوْرُهُمْ اَكْبَرُ ۗ قَدْ بَيَّنَّا لَكُمُ الْاٰيٰتِ اِنْ كُنْتُمْ تَعْقِلُوْنَ","bc":"yā ayyuhallażīna āmanụ lā tattakhiżụ biṭānatam min dụnikum lā ya`lụnakum khabālā, waddụ mā 'anittum, qad badatil-bagḍā`u min afwāhihim wa mā tukhfī ṣudụruhum akbar, qad bayyannā lakumul-āyāti ing kuntum ta'qilụn","at":"Wahai orang-orang yang beriman! Janganlah kamu menjadikan teman orang-orang yang di luar kalanganmu (seagama) sebagai teman kepercayaanmu, (karena) mereka tidak henti-hentinya menyusahkan kamu. Mereka mengharapkan kehancuranmu. Sungguh, telah nyata kebencian dari mulut mereka, dan apa yang tersembunyi di hati mereka lebih jahat. Sungguh, telah Kami terangkan kepadamu ayat-ayat (Kami), jika kamu mengerti."},{"id":"119","ar":"هٰٓاَنْتُمْ اُولَاۤءِ تُحِبُّوْنَهُمْ وَلَا يُحِبُّوْنَكُمْ وَتُؤْمِنُوْنَ بِالْكِتٰبِ كُلِّهٖۚ وَاِذَا لَقُوْكُمْ قَالُوْٓا اٰمَنَّاۖ وَاِذَا خَلَوْا عَضُّوْا عَلَيْكُمُ الْاَنَامِلَ مِنَ الْغَيْظِ ۗ قُلْ مُوْتُوْا بِغَيْظِكُمْ ۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ","bc":"hā`antum ulā`i tuḥibbụnahum wa lā yuḥibbụnakum wa tu`minụna bil-kitābi kullih, wa iżā laqụkum qālū āmannā wa iżā khalau 'aḍḍụ 'alaikumul-anāmila minal-gaīẓ, qul mụtụ bigaiẓikum, innallāha 'alīmum biżātiṣ-ṣudụr","at":"Beginilah kamu! Kamu menyukai mereka, padahal mereka tidak menyukaimu, dan kamu beriman kepada semua kitab. Apabila mereka berjumpa kamu, mereka berkata, “Kami beriman,” dan apabila mereka menyendiri, mereka menggigit ujung jari karena marah dan benci kepadamu. Katakanlah, “Matilah kamu karena kemarahanmu itu!” Sungguh, Allah Maha Mengetahui segala isi hati."},{"id":"120","ar":"اِنْ تَمْسَسْكُمْ حَسَنَةٌ تَسُؤْهُمْۖ وَاِنْ تُصِبْكُمْ سَيِّئَةٌ يَّفْرَحُوْا بِهَا ۗ وَاِنْ تَصْبِرُوْا وَتَتَّقُوْا لَا يَضُرُّكُمْ كَيْدُهُمْ شَيْـًٔا ۗ اِنَّ اللّٰهَ بِمَا يَعْمَلُوْنَ مُحِيْطٌ","bc":"in tamsaskum ḥasanatun tasu`hum wa in tuṣibkum sayyi`atuy yafraḥụ bihā, wa in taṣbirụ wa tattaqụ lā yaḍurrukum kaiduhum syai`ā, innallāha bimā ya'malụna muḥīṭ","at":"Jika kamu memperoleh kebaikan, (niscaya) mereka bersedih hati, tetapi jika kamu tertimpa bencana, mereka bergembira karenanya. Jika kamu bersabar dan bertakwa, tipu daya mereka tidak akan menyusahkan kamu sedikit pun. Sungguh, Allah Maha Meliputi segala apa yang mereka kerjakan."},{"id":"121","ar":"وَاِذْ غَدَوْتَ مِنْ اَهْلِكَ تُبَوِّئُ الْمُؤْمِنِيْنَ مَقَاعِدَ لِلْقِتَالِ ۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌۙ","bc":"wa iż gadauta min ahlika tubawwi`ul-mu`minīna maqā'ida lil-qitāl, wallāhu samī'un 'alīm","at":"Dan (ingatlah), ketika engkau (Muhammad) berangkat pada pagi hari meninggalkan keluargamu untuk mengatur orang-orang beriman pada pos-pos pertempuran. Allah Maha Mendengar, Maha Mengetahui."},{"id":"122","ar":"اِذْ هَمَّتْ طَّۤاىِٕفَتٰنِ مِنْكُمْ اَنْ تَفْشَلَاۙ وَاللّٰهُ وَلِيُّهُمَا ۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ","bc":"iż hammaṭ ṭā`ifatāni mingkum an tafsyalā wallāhu waliyyuhumā, wa 'alallāhi falyatawakkalil-mu`minụn","at":"Ketika dua golongan dari pihak kamu ingin (mundur) karena takut, padahal Allah adalah penolong mereka. Karena itu, hendaklah kepada Allah saja orang-orang mukmin bertawakal."},{"id":"123","ar":"وَلَقَدْ نَصَرَكُمُ اللّٰهُ بِبَدْرٍ وَّاَنْتُمْ اَذِلَّةٌ ۚ فَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تَشْكُرُوْنَ ","bc":"wa laqad naṣarakumullāhu bibadriw wa antum ażillah, fattaqullāha la'allakum tasykurụn","at":"Dan sungguh, Allah telah menolong kamu dalam perang Badar, padahal kamu dalam keadaan lemah. Karena itu bertakwalah kepada Allah, agar kamu mensyukuri-Nya."},{"id":"124","ar":"اِذْ تَقُوْلُ لِلْمُؤْمِنِيْنَ اَلَنْ يَّكْفِيَكُمْ اَنْ يُّمِدَّكُمْ رَبُّكُمْ بِثَلٰثَةِ اٰلَافٍ مِّنَ الْمَلٰۤىِٕكَةِ مُنْزَلِيْنَۗ","bc":"iż taqụlu lil-mu`minīna a lay yakfiyakum ay yumiddakum rabbukum biṡalāṡati ālāfim minal-malā`ikati munzalīn","at":"(Ingatlah), ketika engkau (Muhammad) mengatakan kepada orang-orang beriman, “Apakah tidak cukup bagimu bahwa Allah membantu kamu dengan tiga ribu malaikat yang diturunkan (dari langit)?”"},{"id":"125","ar":"بَلٰٓى ۙاِنْ تَصْبِرُوْا وَتَتَّقُوْا وَيَأْتُوْكُمْ مِّنْ فَوْرِهِمْ هٰذَا يُمْدِدْكُمْ رَبُّكُمْ بِخَمْسَةِ اٰلَافٍ مِّنَ الْمَلٰۤىِٕكَةِ مُسَوِّمِيْنَ ","bc":"balā in taṣbirụ wa tattaqụ wa ya`tụkum min faurihim hāżā yumdidkum rabbukum bikhamsati ālāfim minal-malā`ikati musawwimīn","at":"“Ya” (cukup). Jika kamu bersabar dan bertakwa ketika mereka datang menyerang kamu dengan tiba-tiba, niscaya Allah menolongmu dengan lima ribu malaikat yang memakai tanda."},{"id":"126","ar":"وَمَا جَعَلَهُ اللّٰهُ اِلَّا بُشْرٰى لَكُمْ وَلِتَطْمَىِٕنَّ قُلُوْبُكُمْ بِهٖ ۗ وَمَا النَّصْرُ اِلَّا مِنْ عِنْدِ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِۙ","bc":"wa mā ja'alahullāhu illā busyrā lakum wa litaṭma`inna qulụbukum bih, wa man-naṣru illā min 'indillāhil-'azīzil-ḥakīm","at":"Dan Allah tidak menjadikannya (pemberian bala-bantuan itu) melainkan sebagai kabar gembira bagi (kemenangan)mu, dan agar hatimu tenang karenanya. Dan tidak ada kemenangan itu, selain dari Allah Yang Mahaperkasa, Mahabijaksana."},{"id":"127","ar":"لِيَقْطَعَ طَرَفًا مِّنَ الَّذِيْنَ كَفَرُوْٓا اَوْ يَكْبِتَهُمْ فَيَنْقَلِبُوْا خَاۤىِٕبِيْنَ","bc":"liyaqṭa'a ṭarafam minallażīna kafarū au yakbitahum fa yangqalibụ khā`ibīn","at":"(Allah menolong kamu dalam perang Badar dan memberi bantuan) adalah untuk membinasakan segolongan orang kafir, atau untuk menjadikan mereka hina, sehingga mereka kembali tanpa memperoleh apa pun."},{"id":"128","ar":"لَيْسَ لَكَ مِنَ الْاَمْرِ شَيْءٌ اَوْ يَتُوْبَ عَلَيْهِمْ اَوْ يُعَذِّبَهُمْ فَاِنَّهُمْ ظٰلِمُوْنَ ","bc":"laisa laka minal-amri syai`un au yatụba 'alaihim au yu'ażżibahum fa innahum ẓālimụn","at":"Itu bukan menjadi urusanmu (Muhammad) apakah Allah menerima tobat mereka, atau mengazabnya, karena sesungguhnya mereka orang-orang zalim."},{"id":"129","ar":"وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ يَغْفِرُ لِمَنْ يَّشَاۤءُ وَيُعَذِّبُ مَنْ يَّشَاۤءُ ۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ","bc":"wa lillāhi mā fis-samāwāti wa mā fil-arḍ, yagfiru limay yasyā`u wa yu'ażżibu may yasyā`, wallāhu gafụrur raḥīm","at":"Dan milik Allah-lah apa yang ada di langit dan apa yang ada di bumi. Dia mengampuni siapa yang Dia kehendaki, dan mengazab siapa yang Dia kehendaki. Dan Allah Maha Pengampun, Maha Penyayang."},{"id":"130","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَأْكُلُوا الرِّبٰوٓا اَضْعَافًا مُّضٰعَفَةً ۖوَّاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُوْنَۚ ","bc":"yā ayyuhallażīna āmanụ lā ta`kulur-ribā aḍ'āfam muḍā'afataw wattaqullāha la'allakum tufliḥụn","at":"Wahai orang-orang yang beriman! Janganlah kamu memakan riba dengan berlipat ganda dan bertakwalah kepada Allah agar kamu beruntung."},{"id":"131","ar":"وَاتَّقُوا النَّارَ الَّتِيْٓ اُعِدَّتْ لِلْكٰفِرِيْنَ ۚ ","bc":"wattaqun-nārallatī u'iddat lil-kāfirīn","at":"Dan peliharalah dirimu dari api neraka, yang disediakan bagi orang kafir."},{"id":"132","ar":"وَاَطِيْعُوا اللّٰهَ وَالرَّسُوْلَ لَعَلَّكُمْ تُرْحَمُوْنَۚ ","bc":"wa aṭī'ullāha war-rasụla la'allakum tur-ḥamụn","at":"Dan taatlah kepada Allah dan Rasul (Muhammad), agar kamu diberi rahmat."},{"id":"133","ar":"۞ وَسَارِعُوْٓا اِلٰى مَغْفِرَةٍ مِّنْ رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا السَّمٰوٰتُ وَالْاَرْضُۙ اُعِدَّتْ لِلْمُتَّقِيْنَۙ","bc":"wa sāri'ū ilā magfiratim mir rabbikum wa jannatin 'arḍuhas-samāwātu wal-arḍu u'iddat lil-muttaqīn","at":"Dan bersegeralah kamu mencari ampunan dari Tuhanmu dan mendapatkan surga yang luasnya seluas langit dan bumi yang disediakan bagi orang-orang yang bertakwa,"},{"id":"134","ar":"الَّذِيْنَ يُنْفِقُوْنَ فِى السَّرَّۤاءِ وَالضَّرَّۤاءِ وَالْكَاظِمِيْنَ الْغَيْظَ وَالْعَافِيْنَ عَنِ النَّاسِۗ وَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَۚ ","bc":"allażīna yunfiqụna fis-sarrā`i waḍ-ḍarrā`i wal-kāẓimīnal-gaiẓa wal-'āfīna 'anin-nās, wallāhu yuḥibbul-muḥsinīn","at":"(yaitu) orang yang berinfak, baik di waktu lapang maupun sempit, dan orang-orang yang menahan amarahnya dan memaafkan (kesalahan) orang lain. Dan Allah mencintai orang yang berbuat kebaikan,"},{"id":"135","ar":"وَالَّذِيْنَ اِذَا فَعَلُوْا فَاحِشَةً اَوْ ظَلَمُوْٓا اَنْفُسَهُمْ ذَكَرُوا اللّٰهَ فَاسْتَغْفَرُوْا لِذُنُوْبِهِمْۗ وَمَنْ يَّغْفِرُ الذُّنُوْبَ اِلَّا اللّٰهُ ۗ وَلَمْ يُصِرُّوْا عَلٰى مَا فَعَلُوْا وَهُمْ يَعْلَمُوْنَ ","bc":"wallażīna iżā fa'alụ fāḥisyatan au ẓalamū anfusahum żakarullāha fastagfarụ liżunụbihim, wa may yagfiruż-żunụba illallāh, wa lam yuṣirrụ 'alā mā fa'alụ wa hum ya'lamụn","at":"dan (juga) orang-orang yang apabila mengerjakan perbuatan keji atau menzalimi diri sendiri, (segera) mengingat Allah, lalu memohon ampunan atas dosa-dosanya, dan siapa (lagi) yang dapat mengampuni dosa-dosa selain Allah? Dan mereka tidak meneruskan perbuatan dosa itu, sedang mereka mengetahui."},{"id":"136","ar":"اُولٰۤىِٕكَ جَزَاۤؤُهُمْ مَّغْفِرَةٌ مِّنْ رَّبِّهِمْ وَجَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗ وَنِعْمَ اَجْرُ الْعٰمِلِيْنَۗ","bc":"ulā`ika jazā`uhum magfiratum mir rabbihim wa jannātun tajrī min taḥtihal-an-hāru khālidīna fīhā, wa ni'ma ajrul-'āmilīn","at":"Balasan bagi mereka ialah ampunan dari Tuhan mereka dan surga-surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Dan (itulah) sebaik-baik pahala bagi orang-orang yang beramal."},{"id":"137","ar":"قَدْ خَلَتْ مِنْ قَبْلِكُمْ سُنَنٌۙ فَسِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ ","bc":"qad khalat ming qablikum sunanun fa sīrụ fil-arḍi fanẓurụ kaifa kāna 'āqibatul-mukażżibīn","at":"Sungguh, telah berlalu sebelum kamu sunnah-sunnah (Allah), karena itu berjalanlah kamu ke (segenap penjuru) bumi dan perhatikanlah bagai-mana kesudahan orang yang mendustakan (rasul-rasul). "},{"id":"138","ar":"هٰذَا بَيَانٌ لِّلنَّاسِ وَهُدًى وَّمَوْعِظَةٌ لِّلْمُتَّقِيْنَ ","bc":"hāżā bayānul lin-nāsi wa hudaw wa mau'iẓatul lil-muttaqīn","at":"Inilah (Al-Qur'an) suatu keterangan yang jelas untuk semua manusia, dan menjadi petunjuk serta pelajaran bagi orang-orang yang bertakwa."},{"id":"139","ar":"وَلَا تَهِنُوْا وَلَا تَحْزَنُوْا وَاَنْتُمُ الْاَعْلَوْنَ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ","bc":"wa lā tahinụ wa lā taḥzanụ wa antumul-a'launa ing kuntum mu`minīn","at":"Dan janganlah kamu (merasa) lemah, dan jangan (pula) bersedih hati, sebab kamu paling tinggi (derajatnya), jika kamu orang beriman."},{"id":"140","ar":"اِنْ يَّمْسَسْكُمْ قَرْحٌ فَقَدْ مَسَّ الْقَوْمَ قَرْحٌ مِّثْلُهٗ ۗوَتِلْكَ الْاَيَّامُ نُدَاوِلُهَا بَيْنَ النَّاسِۚ وَلِيَعْلَمَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَيَتَّخِذَ مِنْكُمْ شُهَدَاۤءَ ۗوَاللّٰهُ لَا يُحِبُّ الظّٰلِمِيْنَۙ","bc":"iy yamsaskum qar-ḥun fa qad massal-qauma qar-ḥum miṡluh, wa tilkal-ayyāmu nudāwiluhā bainan-nās, wa liya'lamallāhullażīna āmanụ wa yattakhiża mingkum syuhadā`, wallāhu lā yuḥibbuẓ-ẓālimīn","at":"Jika kamu (pada Perang Uhud) mendapat luka, maka mereka pun (pada Perang Badar) mendapat luka yang serupa. Dan masa (kejayaan dan kehancuran) itu, Kami pergilirkan di antara manusia (agar mereka mendapat pelajaran), dan agar Allah membedakan orang-orang yang beriman (dengan orang-orang kafir) dan agar sebagian kamu dijadikan-Nya (gugur sebagai) syuhada. Dan Allah tidak menyukai orang-orang zalim,"},{"id":"141","ar":"وَلِيُمَحِّصَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا وَيَمْحَقَ الْكٰفِرِيْنَ","bc":"wa liyumaḥḥiṣallāhullażīna āmanụ wa yam-ḥaqal-kāfirīn","at":"dan agar Allah membersihkan orang-orang yang beriman (dari dosa mereka) dan membinasakan orang-orang kafir."},{"id":"142","ar":"اَمْ حَسِبْتُمْ اَنْ تَدْخُلُوا الْجَنَّةَ وَلَمَّا يَعْلَمِ اللّٰهُ الَّذِيْنَ جَاهَدُوْا مِنْكُمْ وَيَعْلَمَ الصّٰبِرِيْنَ ","bc":"am ḥasibtum an tadkhulul-jannata wa lammā ya'lamillāhullażīna jāhadụ mingkum wa ya'lamaṣ-ṣābirīn","at":"Apakah kamu mengira bahwa kamu akan masuk surga, padahal belum nyata bagi Allah orang-orang yang berjihad di antara kamu, dan belum nyata orang-orang yang sabar."},{"id":"143","ar":"وَلَقَدْ كُنْتُمْ تَمَنَّوْنَ الْمَوْتَ مِنْ قَبْلِ اَنْ تَلْقَوْهُۖ فَقَدْ رَاَيْتُمُوْهُ وَاَنْتُمْ تَنْظُرُوْنَ","bc":"wa laqad kuntum tamannaunal-mauta ming qabli an talqauhu fa qad ra`aitumụhu wa antum tanẓurụn","at":"Dan kamu benar-benar mengharapkan mati (syahid) sebelum kamu menghadapinya; maka (sekarang) kamu sungguh, telah melihatnya dan kamu menyaksikannya."},{"id":"144","ar":"وَمَا مُحَمَّدٌ اِلَّا رَسُوْلٌۚ قَدْ خَلَتْ مِنْ قَبْلِهِ الرُّسُلُ ۗ اَفَا۟ىِٕنْ مَّاتَ اَوْ قُتِلَ انْقَلَبْتُمْ عَلٰٓى اَعْقَابِكُمْ ۗ وَمَنْ يَّنْقَلِبْ عَلٰى عَقِبَيْهِ فَلَنْ يَّضُرَّ اللّٰهَ شَيْـًٔا ۗوَسَيَجْزِى اللّٰهُ الشّٰكِرِيْنَ ","bc":"wa mā muḥammadun illā rasụl, qad khalat ming qablihir-rusul, a fa im māta au qutilangqalabtum 'alā a'qābikum, wa may yangqalib 'alā 'aqibaihi fa lay yaḍurrallāha syai`ā, wa sayajzillāhusy-syākirīn","at":"Dan Muhammad hanyalah seorang Rasul; sebelumnya telah berlalu beberapa rasul. Apakah jika dia wafat atau dibunuh kamu berbalik ke belakang (murtad)? Barangsiapa berbalik ke belakang, maka ia tidak akan merugikan Allah sedikit pun. Allah akan memberi balasan kepada orang yang bersyukur."},{"id":"145","ar":"وَمَا كَانَ لِنَفْسٍ اَنْ تَمُوْتَ اِلَّا بِاِذْنِ اللّٰهِ كِتٰبًا مُّؤَجَّلًا ۗ وَمَنْ يُّرِدْ ثَوَابَ الدُّنْيَا نُؤْتِهٖ مِنْهَاۚ وَمَنْ يُّرِدْ ثَوَابَ الْاٰخِرَةِ نُؤْتِهٖ مِنْهَا ۗ وَسَنَجْزِى الشّٰكِرِيْنَ ","bc":"wa mā kāna linafsin an tamụta illā bi`iżnillāhi kitābam mu`ajjalā, wa may yurid ṡawābad-dun-yā nu`tihī min-hā, wa may yurid ṡawābal-ākhirati nu`tihī min-hā, wa sanajzisy-syākirīn","at":"Dan setiap yang bernyawa tidak akan mati kecuali dengan izin Allah, sebagai ketetapan yang telah ditentukan waktunya. Barangsiapa menghendaki pahala dunia, niscaya Kami berikan kepadanya pahala (dunia) itu, dan barangsiapa menghendaki pahala akhirat, Kami berikan (pula) kepadanya pahala (akhirat) itu, dan Kami akan memberi balasan kepada orang-orang yang bersyukur."},{"id":"146","ar":"وَكَاَيِّنْ مِّنْ نَّبِيٍّ قَاتَلَۙ مَعَهٗ رِبِّيُّوْنَ كَثِيْرٌۚ فَمَا وَهَنُوْا لِمَآ اَصَابَهُمْ فِيْ سَبِيْلِ اللّٰهِ وَمَا ضَعُفُوْا وَمَا اسْتَكَانُوْا ۗ وَاللّٰهُ يُحِبُّ الصّٰبِرِيْنَ ","bc":"wa ka`ayyim min nabiyying qātala ma'ahụ ribbiyyụna kaṡīr, fa mā wahanụ limā aṣābahum fī sabīlillāhi wa mā ḍa'ufụ wa mastakānụ, wallāhu yuḥibbuṣ-ṣābirīn","at":"Dan betapa banyak nabi yang berperang didampingi sejumlah besar dari pengikut(nya) yang bertakwa. Mereka tidak (menjadi) lemah karena bencana yang menimpanya di jalan Allah, tidak patah semangat dan tidak (pula) menyerah (kepada musuh). Dan Allah mencintai orang-orang yang sabar."},{"id":"147","ar":"وَمَا كَانَ قَوْلَهُمْ اِلَّآ اَنْ قَالُوْا رَبَّنَا اغْفِرْ لَنَا ذُنُوْبَنَا وَاِسْرَافَنَا فِيْٓ اَمْرِنَا وَثَبِّتْ اَقْدَامَنَا وَانْصُرْنَا عَلَى الْقَوْمِ الْكٰفِرِيْنَ ","bc":"wa mā kāna qaulahum illā ang qālụ rabbanagfir lanā żunụbanā wa isrāfanā fī amrinā wa ṡabbit aqdāmanā wanṣurnā 'alal-qaumil-kāfirīn","at":"Dan tidak lain ucapan mereka hanyalah doa, “Ya Tuhan kami, ampunilah dosa-dosa kami dan tindakan-tindakan kami yang berlebihan (dalam) urusan kami dan tetapkanlah pendirian kami, dan tolonglah kami terhadap orang-orang kafir.”"},{"id":"148","ar":"فَاٰتٰىهُمُ اللّٰهُ ثَوَابَ الدُّنْيَا وَحُسْنَ ثَوَابِ الْاٰخِرَةِ ۗ وَاللّٰهُ يُحِبُّ الْمُحْسِنِيْنَ ","bc":"fa ātāhumullāhu ṡawābad-dun-yā wa ḥusna ṡawābil-ākhirah, wallāhu yuḥibbul-muḥsinīn","at":"Maka Allah memberi mereka pahala di dunia dan pahala yang baik di akhirat. Dan Allah mencintai orang-orang yang berbuat kebaikan."},{"id":"149","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تُطِيْعُوا الَّذِيْنَ كَفَرُوْا يَرُدُّوْكُمْ عَلٰٓى اَعْقَابِكُمْ فَتَنْقَلِبُوْا خٰسِرِيْنَ ","bc":"yā ayyuhallażīna āmanū in tuṭī'ullażīna kafarụ yaruddụkum 'alā a'qābikum fa tangqalibụ khāsirīn","at":"Wahai orang-orang yang beriman! Jika kamu menaati orang-orang yang kafir, niscaya mereka akan mengembalikan kamu ke belakang (murtad), maka kamu akan kembali menjadi orang yang rugi."},{"id":"150","ar":"بَلِ اللّٰهُ مَوْلٰىكُمْ ۚ وَهُوَ خَيْرُ النّٰصِرِيْنَ ","bc":"balillāhu maulākum, wa huwa khairun nāṣirīn","at":"Tetapi hanya Allah-lah pelindungmu, dan Dia penolong yang terbaik."},{"id":"151","ar":"سَنُلْقِيْ فِيْ قُلُوْبِ الَّذِيْنَ كَفَرُوا الرُّعْبَ بِمَٓا اَشْرَكُوْا بِاللّٰهِ مَا لَمْ يُنَزِّلْ بِهٖ سُلْطٰنًا ۚ وَمَأْوٰىهُمُ النَّارُ ۗ وَبِئْسَ مَثْوَى الظّٰلِمِيْنَ ","bc":"sanulqī fī qulụbillażīna kafarur-ru'ba bimā asyrakụ billāhi mā lam yunazzil bihī sulṭānā, wa ma`wāhumun-nār, wa bi`sa maṡwaẓ-ẓālimīn","at":"Akan Kami masukkan rasa takut ke dalam hati orang-orang kafir, karena mereka mempersekutukan Allah dengan sesuatu yang Allah tidak menurunkan keterangan tentang itu. Dan tempat kembali mereka ialah neraka. Dan (itulah) seburuk-buruk tempat tinggal (bagi) orang-orang zalim."},{"id":"152","ar":"وَلَقَدْ صَدَقَكُمُ اللّٰهُ وَعْدَهٗٓ اِذْ تَحُسُّوْنَهُمْ بِاِذْنِهٖ ۚ حَتّٰىٓ اِذَا فَشِلْتُمْ وَتَنَازَعْتُمْ فِى الْاَمْرِ وَعَصَيْتُمْ مِّنْۢ بَعْدِ مَآ اَرٰىكُمْ مَّا تُحِبُّوْنَ ۗ مِنْكُمْ مَّنْ يُّرِيْدُ الدُّنْيَا وَمِنْكُمْ مَّنْ يُّرِيْدُ الْاٰخِرَةَ ۚ ثُمَّ صَرَفَكُمْ عَنْهُمْ لِيَبْتَلِيَكُمْ ۚ وَلَقَدْ عَفَا عَنْكُمْ ۗ وَاللّٰهُ ذُوْ فَضْلٍ عَلَى الْمُؤْمِنِيْنَ ","bc":"wa laqad ṣadaqakumullāhu wa'dahū iż taḥussụnahum bi`iżnih, ḥattā iżā fasyiltum wa tanāza'tum fil-amri wa 'aṣaitum mim ba'di mā arākum mā tuḥibbụn, mingkum may yurīdud-dun-yā wa mingkum may yurīdul-ākhirah, ṡumma ṣarafakum 'an-hum liyabtaliyakum, wa laqad 'afā 'angkum, wallāhu żụ faḍlin 'alal-mu`minīn","at":"Dan sungguh, Allah telah memenuhi janji-Nya kepadamu, ketika kamu membunuh mereka dengan izin-Nya sampai pada saat kamu lemah dan berselisih dalam urusan itu dan mengabaikan perintah Rasul setelah Allah memperlihatkan kepadamu apa yang kamu sukai. Di antara kamu ada orang yang menghendaki dunia dan di antara kamu ada (pula) orang yang menghendaki akhirat. Kemudian Allah memalingkan kamu dari mereka untuk mengujimu, tetapi Dia benar-benar telah memaafkan kamu. Dan Allah mempunyai karunia (yang diberikan) kepada orang-orang mukmin."},{"id":"153","ar":"۞ اِذْ تُصْعِدُوْنَ وَلَا تَلْوٗنَ عَلٰٓى اَحَدٍ وَّالرَّسُوْلُ يَدْعُوْكُمْ فِيْٓ اُخْرٰىكُمْ فَاَثَابَكُمْ غَمًّا ۢبِغَمٍّ لِّكَيْلَا تَحْزَنُوْا عَلٰى مَا فَاتَكُمْ وَلَا مَآ اَصَابَكُمْ ۗ وَاللّٰهُ خَبِيْرٌ ۢبِمَا تَعْمَلُوْنَ ","bc":"iż tuṣ'idụna wa lā talwụna 'alā aḥadiw war-rasụlu yad'ụkum fī ukhrākum fa aṡābakum gammam bigammil likai lā taḥzanụ 'alā mā fātakum wa lā mā aṣābakum, wallāhu khabīrum bimā ta'malụn","at":"(Ingatlah) ketika kamu lari dan tidak menoleh kepada siapa pun, sedang Rasul (Muhammad) yang berada di antara (kawan-kawan)mu yang lain memanggil kamu (kelompok yang lari), karena itu Allah menimpakan kepadamu kesedihan demi kesedihan, agar kamu tidak bersedih hati (lagi) terhadap apa yang luput dari kamu dan terhadap apa yang menimpamu. Dan Allah Mahateliti apa yang kamu kerjakan."},{"id":"154","ar":"ثُمَّ اَنْزَلَ عَلَيْكُمْ مِّنْۢ بَعْدِ الْغَمِّ اَمَنَةً نُّعَاسًا يَّغْشٰى طَۤاىِٕفَةً مِّنْكُمْ ۙ وَطَۤاىِٕفَةٌ قَدْ اَهَمَّتْهُمْ اَنْفُسُهُمْ يَظُنُّوْنَ بِاللّٰهِ غَيْرَ الْحَقِّ ظَنَّ الْجَاهِلِيَّةِ ۗ يَقُوْلُوْنَ هَلْ لَّنَا مِنَ الْاَمْرِ مِنْ شَيْءٍ ۗ قُلْ اِنَّ الْاَمْرَ كُلَّهٗ لِلّٰهِ ۗ يُخْفُوْنَ فِيْٓ اَنْفُسِهِمْ مَّا لَا يُبْدُوْنَ لَكَ ۗ يَقُوْلُوْنَ لَوْ كَانَ لَنَا مِنَ الْاَمْرِ شَيْءٌ مَّا قُتِلْنَا هٰهُنَا ۗ قُلْ لَّوْ كُنْتُمْ فِيْ بُيُوْتِكُمْ لَبَرَزَ الَّذِيْنَ كُتِبَ عَلَيْهِمُ الْقَتْلُ اِلٰى مَضَاجِعِهِمْ ۚ وَلِيَبْتَلِيَ اللّٰهُ مَا فِيْ صُدُوْرِكُمْ وَلِيُمَحِّصَ مَا فِيْ قُلُوْبِكُمْ ۗ وَاللّٰهُ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ","bc":"ṡumma anzala 'alaikum mim ba'dil-gammi amanatan nu'āsay yagsyā ṭā`ifatam mingkum wa ṭā`ifatung qad ahammat-hum anfusuhum yaẓunnụna billāhi gairal-ḥaqqi ẓannal-jāhiliyyah, yaqụlụna hal lanā minal-amri min syaī`, qul innal-amra kullahụ lillāh, yukhfụna fī anfusihim mā lā yubdụna lak, yaqụlụna lau kāna lanā minal-amri syai`um mā qutilnā hāhunā, qul lau kuntum fī buyụtikum labarazallażīna kutiba 'alaihimul-qatlu ilā maḍāji'ihim, wa liyabtaliyallāhu mā fī ṣudụrikum wa liyumaḥḥiṣa mā fī qulụbikum, wallāhu 'alīmum biżātiṣ-ṣudụr","at":"Kemudian setelah kamu ditimpa kesedihan, Dia menurunkan rasa aman kepadamu (berupa) kantuk yang meliputi segolongan dari kamu, sedangkan segolongan lagi telah dicemaskan oleh diri mereka sendiri; mereka menyangka yang tidak benar terhadap Allah seperti sangkaan jahiliah. Mereka berkata, “Adakah sesuatu yang dapat kita perbuat dalam urusan ini?” Katakanlah (Muhammad), “Sesungguhnya segala urusan itu di tangan Allah.” Mereka menyembunyikan dalam hatinya apa yang tidak mereka terangkan kepadamu. Mereka berkata, “Sekiranya ada sesuatu yang dapat kita perbuat dalam urusan ini, niscaya kita tidak akan dibunuh (dikalahkan) di sini.” Katakanlah (Muhammad), “Meskipun kamu ada di rumahmu, niscaya orang-orang yang telah ditetapkan akan mati terbunuh itu keluar (juga) ke tempat mereka terbunuh.” Allah (berbuat demikian) untuk menguji apa yang ada dalam dadamu dan untuk membersihkan apa yang ada dalam hatimu. Dan Allah Maha Mengetahui isi hati."},{"id":"155","ar":"اِنَّ الَّذِيْنَ تَوَلَّوْا مِنْكُمْ يَوْمَ الْتَقَى الْجَمْعٰنِۙ اِنَّمَا اسْتَزَلَّهُمُ الشَّيْطٰنُ بِبَعْضِ مَا كَسَبُوْا ۚ وَلَقَدْ عَفَا اللّٰهُ عَنْهُمْ ۗ اِنَّ اللّٰهَ غَفُوْرٌ حَلِيْمٌ","bc":"innallażīna tawallau mingkum yaumaltaqal-jam'āni innamastazallahumusy-syaiṭānu biba'ḍi mā kasabụ, wa laqad 'afallāhu 'an-hum, innallāha gafụrun ḥalīm","at":"Sesungguhnya orang-orang yang berpaling di antara kamu ketika terjadi pertemuan (pertempuran) antara dua pasukan itu, sesungguhnya mereka digelincirkan oleh setan, disebabkan sebagian kesalahan (dosa) yang telah mereka perbuat (pada masa lampau), tetapi Allah benar-benar telah memaafkan mereka. Sungguh, Allah Maha Pengampun, Maha Penyantun."},{"id":"156","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَكُوْنُوْا كَالَّذِيْنَ كَفَرُوْا وَقَالُوْا لِاِخْوَانِهِمْ اِذَا ضَرَبُوْا فِى الْاَرْضِ اَوْ كَانُوْا غُزًّى لَّوْ كَانُوْا عِنْدَنَا مَا مَاتُوْا وَمَا قُتِلُوْاۚ لِيَجْعَلَ اللّٰهُ ذٰلِكَ حَسْرَةً فِيْ قُلُوْبِهِمْ ۗ وَاللّٰهُ يُحْيٖ وَيُمِيْتُ ۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ","bc":"yā ayyuhallażīna āmanụ lā takụnụ kallażīna kafarụ wa qālụ li`ikhwānihim iżā ḍarabụ fil-arḍi au kānụ guzzal lau kānụ 'indanā mā mātụ wa mā qutilụ, liyaj'alallāhu żālika ḥasratan fī qulụbihim, wallāhu yuḥyī wa yumīt, wallāhu bimā ta'malụna baṣīr","at":"Wahai orang-orang yang beriman! Janganlah kamu seperti orang-orang kafir yang mengatakan kepada saudara-saudaranya apabila mereka mengadakan perjalanan di bumi atau berperang, “Sekiranya mereka tetap bersama kita, tentulah mereka tidak mati dan tidak terbunuh.” (Dengan perkataan) yang demikian itu, karena Allah hendak menimbulkan rasa penyesalan di hati mereka. Allah menghidupkan dan mematikan, dan Allah Maha Melihat apa yang kamu kerjakan."},{"id":"157","ar":"وَلَىِٕنْ قُتِلْتُمْ فِيْ سَبِيْلِ اللّٰهِ اَوْ مُتُّمْ لَمَغْفِرَةٌ مِّنَ اللّٰهِ وَرَحْمَةٌ خَيْرٌ مِّمَّا يَجْمَعُوْنَ ","bc":"wa la`ing qutiltum fī sabīlillāhi au muttum lamagfiratum minallāhi wa raḥmatun khairum mimmā yajma'ụn","at":"Dan sungguh, sekiranya kamu gugur di jalan Allah atau mati, sungguh, pastilah ampunan Allah dan rahmat-Nya lebih baik (bagimu) daripada apa (harta rampasan) yang mereka kumpulkan."},{"id":"158","ar":"وَلَىِٕنْ مُّتُّمْ اَوْ قُتِلْتُمْ لَاِلَى اللّٰهِ تُحْشَرُوْنَ","bc":"wa la`im muttum au qutiltum la`ilallāhi tuḥsyarụn","at":"Dan sungguh, sekiranya kamu mati atau gugur, pastilah kepada Allah kamu dikumpulkan."},{"id":"159","ar":"فَبِمَا رَحْمَةٍ مِّنَ اللّٰهِ لِنْتَ لَهُمْ ۚ وَلَوْ كُنْتَ فَظًّا غَلِيْظَ الْقَلْبِ لَانْفَضُّوْا مِنْ حَوْلِكَ ۖ فَاعْفُ عَنْهُمْ وَاسْتَغْفِرْ لَهُمْ وَشَاوِرْهُمْ فِى الْاَمْرِۚ فَاِذَا عَزَمْتَ فَتَوَكَّلْ عَلَى اللّٰهِ ۗ اِنَّ اللّٰهَ يُحِبُّ الْمُتَوَكِّلِيْنَ","bc":"fa bimā raḥmatim minallāhi linta lahum, walau kunta faẓẓan galīẓal-qalbi lanfaḍḍụ min ḥaulika fa'fu 'an-hum wastagfir lahum wa syāwir-hum fil-amr, fa iżā 'azamta fa tawakkal 'alallāh, innallāha yuḥibbul-mutawakkilīn","at":"Maka berkat rahmat Allah engkau (Muhammad) berlaku lemah lembut terhadap mereka. Sekiranya engkau bersikap keras dan berhati kasar, tentulah mereka menjauhkan diri dari sekitarmu. Karena itu maafkanlah mereka dan mohonkanlah ampunan untuk mereka, dan bermusyawarahlah dengan mereka dalam urusan itu. Kemudian, apabila engkau telah membulatkan tekad, maka bertawakallah kepada Allah. Sungguh, Allah mencintai orang yang bertawakal."},{"id":"160","ar":"اِنْ يَّنْصُرْكُمُ اللّٰهُ فَلَا غَالِبَ لَكُمْ ۚ وَاِنْ يَّخْذُلْكُمْ فَمَنْ ذَا الَّذِيْ يَنْصُرُكُمْ مِّنْۢ بَعْدِهٖ ۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ","bc":"iy yanṣurkumullāhu fa lā gāliba lakum, wa iy yakhżulkum fa man żallażī yanṣurukum mim ba'dih, wa 'alallāhi falyatawakkalil-mu`minụn","at":"Jika Allah menolong kamu, maka tidak ada yang dapat mengalahkanmu, tetapi jika Allah membiarkan kamu (tidak memberi pertolongan), maka siapa yang dapat menolongmu setelah itu? Karena itu, hendaklah kepada Allah saja orang-orang mukmin bertawakal."},{"id":"161","ar":"وَمَا كَانَ لِنَبِيٍّ اَنْ يَّغُلَّ ۗوَمَنْ يَّغْلُلْ يَأْتِ بِمَا غَلَّ يَوْمَ الْقِيٰمَةِ ۚ ثُمَّ تُوَفّٰى كُلُّ نَفْسٍ مَّا كَسَبَتْ وَهُمْ لَا يُظْلَمُوْنَ","bc":"wa mā kāna linabiyyin ay yagull, wa may yaglul ya`ti bimā galla yaumal-qiyāmah, ṡumma tuwaffā kullu nafsim mā kasabat wa hum lā yuẓlamụn","at":"Dan tidak mungkin seorang nabi berkhianat (dalam urusan harta rampasan perang). Barangsiapa berkhianat, niscaya pada hari Kiamat dia akan datang membawa apa yang dikhianatkannya itu. Kemudian setiap orang akan diberi balasan yang sempurna sesuai dengan apa yang dilakukannya, dan mereka tidak dizalimi."},{"id":"162","ar":"اَفَمَنِ اتَّبَعَ رِضْوَانَ اللّٰهِ كَمَنْۢ بَاۤءَ بِسَخَطٍ مِّنَ اللّٰهِ وَمَأْوٰىهُ جَهَنَّمُ ۗ وَبِئْسَ الْمَصِيْرُ ","bc":"a fa manittaba'a riḍwānallāhi kamam bā`a bisakhaṭim minallāhi wa ma`wāhu jahannam, wa bi`sal-maṣīr","at":"Maka adakah orang yang mengikuti keridaan Allah sama dengan orang yang kembali membawa kemurkaan dari Allah dan tempatnya di neraka Jahanam? Itulah seburuk-buruk tempat kembali."},{"id":"163","ar":"هُمْ دَرَجٰتٌ عِنْدَ اللّٰهِ ۗ وَاللّٰهُ بَصِيْرٌ ۢبِمَا يَعْمَلُوْنَ ","bc":"hum darajātun 'indallāh, wallāhu baṣīrum bimā ya'malụn","at":"(Kedudukan) mereka itu bertingkat-tingkat di sisi Allah, dan Allah Maha Melihat apa yang mereka kerjakan."},{"id":"164","ar":"لَقَدْ مَنَّ اللّٰهُ عَلَى الْمُؤْمِنِيْنَ اِذْ بَعَثَ فِيْهِمْ رَسُوْلًا مِّنْ اَنْفُسِهِمْ يَتْلُوْا عَلَيْهِمْ اٰيٰتِهٖ وَيُزَكِّيْهِمْ وَيُعَلِّمُهُمُ الْكِتٰبَ وَالْحِكْمَةَۚ وَاِنْ كَانُوْا مِنْ قَبْلُ لَفِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"laqad mannallāhu 'alal-mu`minīna iż ba'aṡa fīhim rasụlam min anfusihim yatlụ 'alaihim āyātihī wa yuzakkīhim wa yu'allimuhumul-kitāba wal-ḥikmah, wa ing kānụ ming qablu lafī ḍalālim mubīn","at":"Sungguh, Allah telah memberi karunia kepada orang-orang beriman ketika (Allah) mengutus seorang Rasul (Muhammad) di tengah-tengah mereka dari kalangan mereka sendiri, yang membacakan kepada mereka ayat-ayat-Nya, menyucikan (jiwa) mereka, dan mengajarkan kepada mereka Kitab (Al-Qur'an) dan Hikmah (Sunnah), meskipun sebelumnya, mereka benar-benar dalam kesesatan yang nyata."},{"id":"165","ar":"اَوَلَمَّآ اَصَابَتْكُمْ مُّصِيْبَةٌ قَدْ اَصَبْتُمْ مِّثْلَيْهَاۙ قُلْتُمْ اَنّٰى هٰذَا ۗ قُلْ هُوَ مِنْ عِنْدِ اَنْفُسِكُمْ ۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"a wa lammā aṣābatkum muṣībatung qad aṣabtum miṡlaihā qultum annā hāżā, qul huwa min 'indi anfusikum, innallāha 'alā kulli syai`ing qadīr","at":"Dan mengapa kamu (heran) ketika ditimpa musibah (kekalahan pada Perang Uhud), padahal kamu telah menimpakan musibah dua kali lipat (kepada musuh-musuhmu pada Perang Badar) kamu berkata, “Dari mana datangnya (kekalahan) ini?” Katakanlah, “Itu dari (kesalahan) dirimu sendiri.” Sungguh, Allah Mahakuasa atas segala sesuatu."},{"id":"166","ar":"وَمَآ اَصَابَكُمْ يَوْمَ الْتَقَى الْجَمْعٰنِ فَبِاِذْنِ اللّٰهِ وَلِيَعْلَمَ الْمُؤْمِنِيْنَۙ","bc":"wa mā aṣābakum yaumaltaqal-jam'āni fa bi`iżnillāhi wa liya'lamal-mu`minīn","at":"Dan apa yang menimpa kamu ketika terjadi pertemuan (pertempuran) antara dua pasukan itu adalah dengan izin Allah, dan agar Allah menguji siapa orang (yang benar-benar) beriman."},{"id":"167","ar":"وَلِيَعْلَمَ الَّذِيْنَ نَافَقُوْا ۖوَقِيْلَ لَهُمْ تَعَالَوْا قَاتِلُوْا فِيْ سَبِيْلِ اللّٰهِ اَوِ ادْفَعُوْا ۗ قَالُوْا لَوْ نَعْلَمُ قِتَالًا لَّاتَّبَعْنٰكُمْ ۗ هُمْ لِلْكُفْرِ يَوْمَىِٕذٍ اَقْرَبُ مِنْهُمْ لِلْاِيْمَانِ ۚ يَقُوْلُوْنَ بِاَفْوَاهِهِمْ مَّا لَيْسَ فِيْ قُلُوْبِهِمْ ۗ وَاللّٰهُ اَعْلَمُ بِمَا يَكْتُمُوْنَۚ","bc":"wa liya'lamallażīna nāfaqụ wa qīla lahum ta'ālau qātilụ fī sabīlillāhi awidfa'ụ, qālụ lau na'lamu qitālal lattaba'nākum, hum lil-kufri yauma`iżin aqrabu min-hum lil-īmān, yaqụlụna bi`afwāhihim mā laisa fī qulụbihim, wallāhu a'lamu bimā yaktumụn","at":"Dan untuk menguji orang-orang yang munafik, kepada mereka dikatakan, “Marilah berperang di jalan Allah atau pertahankanlah (dirimu).” Mereka berkata, “Sekiranya kami mengetahui (bagaimana cara) berperang, tentulah kami mengikuti kamu.” Mereka pada hari itu lebih dekat kepada kekafiran dari pada keimanan. Mereka mengatakan dengan mulutnya apa yang tidak sesuai dengan isi hatinya. Dan Allah lebih mengetahui apa yang mereka sembunyikan."},{"id":"168","ar":"اَلَّذِيْنَ قَالُوْا لِاِخْوَانِهِمْ وَقَعَدُوْا لَوْ اَطَاعُوْنَا مَا قُتِلُوْا ۗ قُلْ فَادْرَءُوْا عَنْ اَنْفُسِكُمُ الْمَوْتَ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"allażīna qālụ li`ikhwānihim wa qa'adụ lau aṭā'ụnā mā qutilụ, qul fadra`ụ 'an anfusikumul-mauta ing kuntum ṣādiqīn","at":"(Mereka itu adalah) orang-orang yang berkata kepada saudara-saudaranya dan mereka tidak turut pergi berperang, “Sekiranya mereka mengikuti kita, tentulah mereka tidak terbunuh.” Katakanlah, “Cegahlah kematian itu dari dirimu, jika kamu orang yang benar.”"},{"id":"169","ar":"وَلَا تَحْسَبَنَّ الَّذِيْنَ قُتِلُوْا فِيْ سَبِيْلِ اللّٰهِ اَمْوَاتًا ۗ بَلْ اَحْيَاۤءٌ عِنْدَ رَبِّهِمْ يُرْزَقُوْنَۙ","bc":"wa lā taḥsabannallażīna qutilụ fī sabīlillāhi amwātā, bal aḥyā`un 'inda rabbihim yurzaqụn","at":"Dan jangan sekali-kali kamu mengira bahwa orang-orang yang gugur di jalan Allah itu mati; sebenarnya mereka itu hidup di sisi Tuhannya mendapat rezeki,"},{"id":"170","ar":"فَرِحِيْنَ بِمَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۙ وَيَسْتَبْشِرُوْنَ بِالَّذِيْنَ لَمْ يَلْحَقُوْا بِهِمْ مِّنْ خَلْفِهِمْ ۙ اَلَّا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَۘ","bc":"fariḥīna bimā ātāhumullāhu min faḍlihī wa yastabsyirụna billażīna lam yal-ḥaqụ bihim min khalfihim allā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Mereka bergembira dengan karunia yang diberikan Allah kepadanya, dan bergirang hati terhadap orang yang masih tinggal di belakang yang belum menyusul mereka, bahwa tidak ada rasa takut pada mereka dan mereka tidak bersedih hati."},{"id":"171","ar":"۞ يَسْتَبْشِرُوْنَ بِنِعْمَةٍ مِّنَ اللّٰهِ وَفَضْلٍۗ وَاَنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُؤْمِنِيْنَ ","bc":"yastabsyirụna bini'matim minallāhi wa faḍl, wa annallāha lā yuḍī'u ajral-mu`minīn","at":"Mereka bergirang hati dengan nikmat dan karunia dari Allah. Dan sungguh, Allah tidak menyia-nyiakan pahala orang-orang yang beriman,"},{"id":"172","ar":"اَلَّذِيْنَ اسْتَجَابُوْا لِلّٰهِ وَالرَّسُوْلِ مِنْۢ بَعْدِ مَآ اَصَابَهُمُ الْقَرْحُ ۖ لِلَّذِيْنَ اَحْسَنُوْا مِنْهُمْ وَاتَّقَوْا اَجْرٌ عَظِيْمٌۚ","bc":"allażīnastajābụ lillāhi war-rasụli mim ba'di mā aṣābahumul-qar-ḥu lillażīna aḥsanụ min-hum wattaqau ajrun 'aẓīm","at":"(yaitu) orang-orang yang menaati (perintah) Allah dan Rasul setelah mereka mendapat luka (dalam Perang Uhud). Orang-orang yang berbuat kebajikan dan bertakwa di antara mereka mendapat pahala yang besar."},{"id":"173","ar":"اَلَّذِيْنَ قَالَ لَهُمُ النَّاسُ اِنَّ النَّاسَ قَدْ جَمَعُوْا لَكُمْ فَاخْشَوْهُمْ فَزَادَهُمْ اِيْمَانًاۖ وَّقَالُوْا حَسْبُنَا اللّٰهُ وَنِعْمَ الْوَكِيْلُ ","bc":"allażīna qāla lahumun-nāsu innan-nāsa qad jama'ụ lakum fakhsyauhum fa zādahum īmānaw wa qālụ ḥasbunallāhu wa ni'mal-wakīl","at":"(Yaitu) orang-orang (yang menaati Allah dan Rasul) yang ketika ada orang-orang mengatakan kepadanya, “Orang-orang (Quraisy) telah mengumpulkan pasukan untuk menyerang kamu, karena itu takutlah kepada mereka,” ternyata (ucapan) itu menambah (kuat) iman mereka dan mereka menjawab, “Cukuplah Allah (menjadi penolong) bagi kami dan Dia sebaik-baik pelindung.”"},{"id":"174","ar":"فَانْقَلَبُوْا بِنِعْمَةٍ مِّنَ اللّٰهِ وَفَضْلٍ لَّمْ يَمْسَسْهُمْ سُوْۤءٌۙ وَّاتَّبَعُوْا رِضْوَانَ اللّٰهِ ۗ وَاللّٰهُ ذُوْ فَضْلٍ عَظِيْمٍ ","bc":"fangqalabụ bini'matim minallāhi wa faḍlil lam yamsas-hum sū`uw wattaba'ụ riḍwānallāh, wallāhu żụ faḍlin 'aẓīm","at":"Maka mereka kembali dengan nikmat dan karunia (yang besar) dari Allah, mereka tidak ditimpa suatu bencana dan mereka mengikuti keridaan Allah. Allah mempunyai karunia yang besar."},{"id":"175","ar":"اِنَّمَا ذٰلِكُمُ الشَّيْطٰنُ يُخَوِّفُ اَوْلِيَاۤءَهٗۖ فَلَا تَخَافُوْهُمْ وَخَافُوْنِ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ","bc":"innamā żālikumusy-syaiṭānu yukhawwifu auliyā`ahụ fa lā takhāfụhum wa khāfụni ing kuntum mu`minīn","at":"Sesungguhnya mereka hanyalah setan yang menakut-nakuti (kamu) dengan teman-teman setianya, karena itu janganlah kamu takut kepada mereka, tetapi takutlah kepada-Ku, jika kamu orang-orang beriman."},{"id":"176","ar":"وَلَا يَحْزُنْكَ الَّذِيْنَ يُسَارِعُوْنَ فِى الْكُفْرِۚ اِنَّهُمْ لَنْ يَّضُرُّوا اللّٰهَ شَيْـًٔا ۗ يُرِيْدُ اللّٰهُ اَلَّا يَجْعَلَ لَهُمْ حَظًّا فِى الْاٰخِرَةِ وَلَهُمْ عَذَابٌ عَظِيْمٌۚ ","bc":"wa lā yaḥzungkallażīna yusāri'ụna fil-kufr, innahum lay yaḍurrullāha syai`ā, yurīdullāhu allā yaj'ala lahum ḥaẓẓan fil-ākhirati wa lahum 'ażābun 'aẓīm","at":"Dan janganlah engkau (Muhammad) dirisaukan oleh orang-orang yang dengan mudah kembali menjadi kafir; sesungguhnya sedikit pun mereka tidak merugikan Allah. Allah tidak akan memberi bagian (pahala) kepada mereka di akhirat, dan mereka akan mendapat azab yang besar."},{"id":"177","ar":"اِنَّ الَّذِيْنَ اشْتَرَوُا الْكُفْرَ بِالْاِيْمَانِ لَنْ يَّضُرُّوا اللّٰهَ شَيْـًٔاۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"innallażīnasytarawul-kufra bil-īmāni lay yaḍurrullāha syai`ā, wa lahum 'ażābun alīm","at":"Sesungguhnya orang-orang yang membeli kekafiran dengan iman, sedikit pun tidak merugikan Allah; dan mereka akan mendapat azab yang pedih."},{"id":"178","ar":"وَلَا يَحْسَبَنَّ الَّذِيْنَ كَفَرُوْٓا اَنَّمَا نُمْلِيْ لَهُمْ خَيْرٌ لِّاَنْفُسِهِمْ ۗ اِنَّمَا نُمْلِيْ لَهُمْ لِيَزْدَادُوْٓا اِثْمًا ۚ وَلَهُمْ عَذَابٌ مُّهِيْنٌ ","bc":"wa lā yaḥsabannallażīna kafarū annamā numlī lahum khairul li`anfusihim, innamā numlī lahum liyazdādū iṡmā, wa lahum 'ażābum muhīn","at":"Dan jangan sekali-kali orang-orang kafir itu mengira bahwa tenggang waktu yang Kami berikan kepada mereka lebih baik baginya. Sesungguhnya tenggang waktu yang Kami berikan kepada mereka hanyalah agar dosa mereka semakin bertambah; dan mereka akan mendapat azab yang menghinakan."},{"id":"179","ar":"مَا كَانَ اللّٰهُ لِيَذَرَ الْمُؤْمِنِيْنَ عَلٰى مَآ اَنْتُمْ عَلَيْهِ حَتّٰى يَمِيْزَ الْخَبِيْثَ مِنَ الطَّيِّبِ ۗ وَمَا كَانَ اللّٰهُ لِيُطْلِعَكُمْ عَلَى الْغَيْبِ وَلٰكِنَّ اللّٰهَ يَجْتَبِيْ مِنْ رُّسُلِهٖ مَنْ يَّشَاۤءُ ۖ فَاٰمِنُوْا بِاللّٰهِ وَرُسُلِهٖ ۚ وَاِنْ تُؤْمِنُوْا وَتَتَّقُوْا فَلَكُمْ اَجْرٌ عَظِيْمٌ ","bc":"mā kānallāhu liyażaral-mu`minīna 'alā mā antum 'alaihi ḥattā yamīzal-khabīṡa minaṭ-ṭayyib, wa mā kānallāhu liyuṭli'akum 'alal-gaibi wa lākinnallāha yajtabī mir rusulihī may yasyā`u fa āminụ billāhi wa rusulih, wa in tu`minụ wa tattaqụ fa lakum ajrun 'aẓīm","at":"Allah tidak akan membiarkan orang-orang yang beriman sebagaimana dalam keadaan kamu sekarang ini, sehingga Dia membedakan yang buruk dari yang baik. Allah tidak akan memperlihatkan kepadamu hal-hal yang gaib, tetapi Allah memilih siapa yang Dia kehendaki di antara rasul-rasul-Nya. Karena itu, berimanlah kepada Allah dan rasul-rasul-Nya. Jika kamu beriman dan bertakwa, maka kamu akan mendapat pahala yang besar."},{"id":"180","ar":"وَلَا يَحْسَبَنَّ الَّذِيْنَ يَبْخَلُوْنَ بِمَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖ هُوَ خَيْرًا لَّهُمْ ۗ بَلْ هُوَ شَرٌّ لَّهُمْ ۗ سَيُطَوَّقُوْنَ مَا بَخِلُوْا بِهٖ يَوْمَ الْقِيٰمَةِ ۗ وَلِلّٰهِ مِيْرَاثُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ ","bc":"wa lā yaḥsabannallażīna yabkhalụna bimā ātāhumullāhu min faḍlihī huwa khairal lahum, bal huwa syarrul lahum, sayuṭawwaqụna mā bakhilụ bihī yaumal-qiyāmah, wa lillāhi mīrāṡus-samāwāti wal-arḍ, wallāhu bimā ta'malụna khabīr","at":"Dan jangan sekali-kali orang-orang yang kikir dengan apa yang diberikan Allah kepada mereka dari karunia-Nya mengira bahwa (kikir) itu baik bagi mereka, padahal (kikir) itu buruk bagi mereka. Apa (harta) yang mereka kikirkan itu akan dikalungkan (di lehernya) pada hari Kiamat. Milik Allah-lah warisan (apa yang ada) di langit dan di bumi. Allah Mahateliti apa yang kamu kerjakan."},{"id":"181","ar":"لَقَدْ سَمِعَ اللّٰهُ قَوْلَ الَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ فَقِيْرٌ وَّنَحْنُ اَغْنِيَاۤءُ ۘ سَنَكْتُبُ مَا قَالُوْا وَقَتْلَهُمُ الْاَنْۢبِيَاۤءَ بِغَيْرِ حَقٍّۙ وَّنَقُوْلُ ذُوْقُوْا عَذَابَ الْحَرِيْقِ ","bc":"laqad sami'allāhu qaulallażīna qālū innallāha faqīruw wa naḥnu agniyā`, sanaktubu mā qālụ wa qatlahumul-ambiyā`a bigairi ḥaqqiw wa naqụlu żụqụ 'ażābal-ḥarīq","at":"Sungguh, Allah telah mendengar perkataan orang-orang (Yahudi) yang mengatakan, “Sesungguhnya Allah itu miskin dan kami kaya.” Kami akan mencatat perkataan mereka dan perbuatan mereka membunuh nabi-nabi tanpa hak (alasan yang benar), dan Kami akan mengatakan (kepada mereka), “Rasakanlah olehmu azab yang membakar!”"},{"id":"182","ar":"ذٰلِكَ بِمَا قَدَّمَتْ اَيْدِيْكُمْ وَاَنَّ اللّٰهَ لَيْسَ بِظَلَّامٍ لِّلْعَبِيْدِۚ ","bc":"żālika bimā qaddamat aidīkum wa annallāha laisa biẓallāmil lil-'abīd","at":"Demikian itu disebabkan oleh perbuatan tanganmu sendiri, dan sesungguhnya Allah tidak menzalimi hamba-hamba-Nya."},{"id":"183","ar":"اَلَّذِيْنَ قَالُوْٓا اِنَّ اللّٰهَ عَهِدَ اِلَيْنَآ اَلَّا نُؤْمِنَ لِرَسُوْلٍ حَتّٰى يَأْتِيَنَا بِقُرْبَانٍ تَأْكُلُهُ النَّارُ ۗ قُلْ قَدْ جَاۤءَكُمْ رُسُلٌ مِّنْ قَبْلِيْ بِالْبَيِّنٰتِ وَبِالَّذِيْ قُلْتُمْ فَلِمَ قَتَلْتُمُوْهُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"allażīna qālū innallāha 'ahida ilainā allā nu`mina lirasụlin ḥattā ya`tiyanā biqurbānin ta`kuluhun-nār, qul qad jā`akum rusulum ming qablī bil-bayyināti wa billażī qultum fa lima qataltumụhum ing kuntum ṣādiqīn","at":"(Yaitu) orang-orang (Yahudi) yang mengatakan, “Sesungguhnya Allah telah memerintahkan kepada kami, agar kami tidak beriman kepada seorang rasul, sebelum dia mendatangkan kepada kami kurban yang dimakan api.” Katakanlah (Muhammad), “Sungguh, beberapa orang rasul sebelumku telah datang kepadamu, (dengan) membawa bukti-bukti yang nyata dan membawa apa yang kamu sebutkan, tetapi mengapa kamu membunuhnya jika kamu orang-orang yang benar.”"},{"id":"184","ar":"فَاِنْ كَذَّبُوْكَ فَقَدْ كُذِّبَ رُسُلٌ مِّنْ قَبْلِكَ جَاۤءُوْ بِالْبَيِّنٰتِ وَالزُّبُرِ وَالْكِتٰبِ الْمُنِيْرِ","bc":"fa ing każżabụka fa qad kużżiba rusulum ming qablika jā`ụ bil-bayyināti waz-zuburi wal-kitābil-munīr","at":"Maka jika mereka mendustakan engkau (Muhammad), maka (ketahuilah) rasul-rasul sebelum engkau pun telah didustakan (pula), mereka membawa mukjizat-mukjizat yang nyata, Zubur dan Kitab yang memberi penjelasan yang sempurna."},{"id":"185","ar":"كُلُّ نَفْسٍ ذَاۤىِٕقَةُ الْمَوْتِۗ وَاِنَّمَا تُوَفَّوْنَ اُجُوْرَكُمْ يَوْمَ الْقِيٰمَةِ ۗ فَمَنْ زُحْزِحَ عَنِ النَّارِ وَاُدْخِلَ الْجَنَّةَ فَقَدْ فَازَ ۗ وَمَا الْحَيٰوةُ الدُّنْيَآ اِلَّا مَتَاعُ الْغُرُوْرِ ","bc":"kullu nafsin żā`iqatul maụt, wa innamā tuwaffauna ujụrakum yaumal-qiyāmah, fa man zuḥziḥa 'anin-nāri wa udkhilal-jannata fa qad fāz, wa mal-ḥayātud-dun-yā illā matā'ul-gurụr","at":"Setiap yang bernyawa akan merasakan mati. Dan hanya pada hari Kiamat sajalah diberikan dengan sempurna balasanmu. Barangsiapa dijauhkan dari neraka dan dimasukkan ke dalam surga, sungguh, dia memperoleh kemenangan. Kehidupan dunia hanyalah kesenangan yang memperdaya."},{"id":"186","ar":"۞ لَتُبْلَوُنَّ فِيْٓ اَمْوَالِكُمْ وَاَنْفُسِكُمْۗ وَلَتَسْمَعُنَّ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَمِنَ الَّذِيْنَ اَشْرَكُوْٓا اَذًى كَثِيْرًا ۗ وَاِنْ تَصْبِرُوْا وَتَتَّقُوْا فَاِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُوْرِ ","bc":"latublawunna fī amwālikum wa anfusikum, wa latasma'unna minallażīna ụtul-kitāba ming qablikum wa minallażīna asyrakū ażang kaṡīrā, wa in taṣbirụ wa tattaqụ fa inna żālika min 'azmil-umụr","at":"Kamu pasti akan diuji dengan hartamu dan dirimu. Dan pasti kamu akan mendengar banyak hal yang sangat menyakitkan hati dari orang-orang yang diberi Kitab sebelum kamu dan dari orang-orang musyrik. Jika kamu bersabar dan bertakwa, maka sesungguhnya yang demikian itu termasuk urusan yang (patut) diutamakan."},{"id":"187","ar":"وَاِذْ اَخَذَ اللّٰهُ مِيْثَاقَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ لَتُبَيِّنُنَّهٗ لِلنَّاسِ وَلَا تَكْتُمُوْنَهٗۖ فَنَبَذُوْهُ وَرَاۤءَ ظُهُوْرِهِمْ وَاشْتَرَوْا بِهٖ ثَمَنًا قَلِيْلًا ۗ فَبِئْسَ مَا يَشْتَرُوْنَ ","bc":"wa iż akhażallāhu mīṡāqallażīna ụtul-kitāba latubayyinunnahụ lin-nāsi wa lā taktumụnahụ fa nabażụhu warā`a ẓuhụrihim wasytarau bihī ṡamanang qalīlā, fa bi`sa mā yasytarụn","at":"Dan (ingatlah), ketika Allah mengambil janji dari orang-orang yang telah diberi Kitab (yaitu), “Hendaklah kamu benar-benar menerangkannya (isi Kitab itu) kepada manusia, dan janganlah kamu menyembunyikannya,” lalu mereka melemparkan (janji itu) ke belakang punggung mereka dan menjualnya dengan harga murah. Maka itu seburuk-buruk jual-beli yang mereka lakukan."},{"id":"188","ar":"لَا تَحْسَبَنَّ الَّذِيْنَ يَفْرَحُوْنَ بِمَآ اَتَوْا وَّيُحِبُّوْنَ اَنْ يُّحْمَدُوْا بِمَا لَمْ يَفْعَلُوْا فَلَا تَحْسَبَنَّهُمْ بِمَفَازَةٍ مِّنَ الْعَذَابِۚ وَلَهُمْ عَذَابٌ اَلِيْمٌ","bc":"lā taḥsabannallażīna yafraḥụna bimā ataw wa yuḥibbụna ay yuḥmadụ bimā lam yaf'alụ fa lā taḥsabannahum bimafāzatim minal-'ażāb, wa lahum 'ażābun alīm","at":"Jangan sekali-kali kamu mengira bahwa orang yang gembira dengan apa yang telah mereka kerjakan dan mereka suka dipuji atas perbuatan yang tidak mereka lakukan, jangan sekali-kali kamu mengira bahwa mereka akan lolos dari azab. Mereka akan mendapat azab yang pedih."},{"id":"189","ar":"وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"wa lillāhi mulkus-samāwāti wal-arḍ, wallāhu 'alā kulli syai`ing qadīr","at":"Dan milik Allah-lah kerajaan langit dan bumi; dan Allah Mahakuasa atas segala sesuatu."},{"id":"190","ar":"اِنَّ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافِ الَّيْلِ وَالنَّهَارِ لَاٰيٰتٍ لِّاُولِى الْاَلْبَابِۙ","bc":"inna fī khalqis-samāwāti wal-arḍi wakhtilāfil-laili wan-nahāri la`āyātil li`ulil-albāb","at":"Sesungguhnya dalam penciptaan langit dan bumi, dan pergantian malam dan siang terdapat tanda-tanda (kebesaran Allah) bagi orang yang berakal,"},{"id":"191","ar":"الَّذِيْنَ يَذْكُرُوْنَ اللّٰهَ قِيَامًا وَّقُعُوْدًا وَّعَلٰى جُنُوْبِهِمْ وَيَتَفَكَّرُوْنَ فِيْ خَلْقِ السَّمٰوٰتِ وَالْاَرْضِۚ رَبَّنَا مَا خَلَقْتَ هٰذَا بَاطِلًاۚ سُبْحٰنَكَ فَقِنَا عَذَابَ النَّارِ ","bc":"allażīna yażkurụnallāha qiyāmaw wa qu'ụdaw wa 'alā junụbihim wa yatafakkarụna fī khalqis-samāwāti wal-arḍ, rabbanā mā khalaqta hāżā bāṭilā, sub-ḥānaka fa qinā 'ażāban-nār","at":"(yaitu) orang-orang yang mengingat Allah sambil berdiri, duduk atau dalam keadaan berbaring, dan mereka memikirkan tentang penciptaan langit dan bumi (seraya berkata), “Ya Tuhan kami, tidaklah Engkau menciptakan semua ini sia-sia; Mahasuci Engkau, lindungilah kami dari azab neraka."},{"id":"192","ar":"رَبَّنَآ اِنَّكَ مَنْ تُدْخِلِ النَّارَ فَقَدْ اَخْزَيْتَهٗ ۗ وَمَا لِلظّٰلِمِيْنَ مِنْ اَنْصَارٍ","bc":"rabbanā innaka man tudkhilin-nāra fa qad akhzaitah, wa mā liẓ-ẓālimīna min anṣār","at":"Ya Tuhan kami, sesungguhnya orang yang Engkau masukkan ke dalam neraka, maka sungguh, Engkau telah menghinakannya, dan tidak ada seorang penolong pun bagi orang yang zalim."},{"id":"193","ar":"رَبَّنَآ اِنَّنَا سَمِعْنَا مُنَادِيًا يُّنَادِيْ لِلْاِيْمَانِ اَنْ اٰمِنُوْا بِرَبِّكُمْ فَاٰمَنَّا ۖرَبَّنَا فَاغْفِرْ لَنَا ذُنُوْبَنَا وَكَفِّرْ عَنَّا سَيِّاٰتِنَا وَتَوَفَّنَا مَعَ الْاَبْرَارِۚ ","bc":"rabbanā innanā sami'nā munādiyay yunādī lil-īmāni an āminụ birabbikum fa āmannā rabbanā fagfir lanā żunụbanā wa kaffir 'annā sayyi`ātinā wa tawaffanā ma'al-abrār","at":"Ya Tuhan kami, sesungguhnya kami mendengar orang yang menyeru kepada iman, (yaitu), “Berimanlah kamu kepada Tuhanmu,” maka kami pun beriman. Ya Tuhan kami, ampunilah dosa-dosa kami dan hapuskanlah kesalahan-kesalahan kami, dan matikanlah kami beserta orang-orang yang berbakti."},{"id":"194","ar":"رَبَّنَا وَاٰتِنَا مَا وَعَدْتَّنَا عَلٰى رُسُلِكَ وَلَا تُخْزِنَا يَوْمَ الْقِيٰمَةِ ۗ اِنَّكَ لَا تُخْلِفُ الْمِيْعَادَ ","bc":"rabbanā wa ātinā mā wa'attanā 'alā rusulika wa lā tukhzinā yaumal-qiyāmah, innaka lā tukhliful-mī'ād","at":"Ya Tuhan kami, berilah kami apa yang telah Engkau janjikan kepada kami melalui rasul-rasul-Mu. Dan janganlah Engkau hinakan kami pada hari Kiamat. Sungguh, Engkau tidak pernah mengingkari janji.”"},{"id":"195","ar":"فَاسْتَجَابَ لَهُمْ رَبُّهُمْ اَنِّيْ لَآ اُضِيْعُ عَمَلَ عَامِلٍ مِّنْكُمْ مِّنْ ذَكَرٍ اَوْ اُنْثٰى ۚ بَعْضُكُمْ مِّنْۢ بَعْضٍ ۚ فَالَّذِيْنَ هَاجَرُوْا وَاُخْرِجُوْا مِنْ دِيَارِهِمْ وَاُوْذُوْا فِيْ سَبِيْلِيْ وَقٰتَلُوْا وَقُتِلُوْا لَاُكَفِّرَنَّ عَنْهُمْ سَيِّاٰتِهِمْ وَلَاُدْخِلَنَّهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۚ ثَوَابًا مِّنْ عِنْدِ اللّٰهِ ۗ وَاللّٰهُ عِنْدَهٗ حُسْنُ الثَّوَابِ ","bc":"fastajāba lahum rabbuhum annī lā uḍī'u 'amala 'āmilim mingkum min żakarin au unṡā, ba'ḍukum mim ba'ḍ, fallażīna hājarụ wa ukhrijụ min diyārihim wa ụżụ fī sabīlī wa qātalụ wa qutilụ la`ukaffiranna 'an-hum sayyi`ātihim wa la`udkhilannahum jannātin tajrī min taḥtihal-an-hār, ṡawābam min 'indillāh, wallāhu 'indahụ ḥusnuṡ-ṡawāb","at":"Maka Tuhan mereka memperkenankan permohonannya (dengan berfirman), “Sesungguhnya Aku tidak menyia-nyiakan amal orang yang beramal di antara kamu, baik laki-laki maupun perempuan, (karena) sebagian kamu adalah (keturunan) dari sebagian yang lain. Maka orang yang berhijrah, yang diusir dari kampung halamannya, yang disakiti pada jalan-Ku, yang berperang dan yang terbunuh, pasti akan Aku hapus kesalahan mereka dan pasti Aku masukkan mereka ke dalam surga-surga yang mengalir di bawahnya sungai-sungai, sebagai pahala dari Allah. Dan di sisi Allah ada pahala yang baik.”"},{"id":"196","ar":"لَا يَغُرَّنَّكَ تَقَلُّبُ الَّذِيْنَ كَفَرُوْا فِى الْبِلَادِۗ","bc":"lā yagurrannaka taqallubullażīna kafarụ fil-bilād","at":"Jangan sekali-kali kamu teperdaya oleh kegiatan orang-orang kafir (yang bergerak) di seluruh negeri."},{"id":"197","ar":"مَتَاعٌ قَلِيْلٌ ۗ ثُمَّ مَأْوٰىهُمْ جَهَنَّمُ ۗوَبِئْسَ الْمِهَادُ ","bc":"matā'ung qalīl, ṡumma ma`wāhum jahannam, wa bi`sal-mihād","at":"Itu hanyalah kesenangan sementara, kemudian tempat kembali mereka ialah neraka Jahanam. (Jahanam) itu seburuk-buruk tempat tinggal."},{"id":"198","ar":"لٰكِنِ الَّذِيْنَ اتَّقَوْا رَبَّهُمْ لَهُمْ جَنّٰتٌ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا نُزُلًا مِّنْ عِنْدِ اللّٰهِ ۗ وَمَا عِنْدَ اللّٰهِ خَيْرٌ لِّلْاَبْرَارِ","bc":"lākinillażīnattaqau rabbahum lahum jannātun tajrī min taḥtihal-an-hāru khālidīna fīhā nuzulam min 'indillāh, wa mā 'indallāhi khairul lil-abrār","at":"Tetapi orang-orang yang bertakwa kepada Tuhannya, mereka akan mendapat surga-surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya sebagai karunia dari Allah. Dan apa yang di sisi Allah lebih baik bagi orang-orang yang berbakti."},{"id":"199","ar":"وَاِنَّ مِنْ اَهْلِ الْكِتٰبِ لَمَنْ يُّؤْمِنُ بِاللّٰهِ وَمَآ اُنْزِلَ اِلَيْكُمْ وَمَآ اُنْزِلَ اِلَيْهِمْ خٰشِعِيْنَ لِلّٰهِ ۙ لَا يَشْتَرُوْنَ بِاٰيٰتِ اللّٰهِ ثَمَنًا قَلِيْلًا ۗ اُولٰۤىِٕكَ لَهُمْ اَجْرُهُمْ عِنْدَ رَبِّهِمْ ۗ اِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ ","bc":"wa inna min ahlil-kitābi lamay yu`minu billāhi wa mā unzila ilaikum wa mā unzila ilaihim khāsyi'īna lillāhi lā yasytarụna bi`āyātillāhi ṡamanang qalīlā, ulā`ika lahum ajruhum 'inda rabbihim, innallāha sarī'ul-ḥisāb","at":"Dan sesungguhnya di antara Ahli Kitab ada yang beriman kepada Allah, dan kepada apa yang diturunkan kepada kamu, dan yang diturunkan kepada mereka, karena mereka berendah hati kepada Allah, dan mereka tidak memperjualbelikan ayat-ayat Allah dengan harga murah. Mereka memperoleh pahala di sisi Tuhannya. Sungguh, Allah sangat cepat perhitungan-Nya."},{"id":"200","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اصْبِرُوْا وَصَابِرُوْا وَرَابِطُوْاۗ وَاتَّقُوا اللّٰهَ لَعَلَّكُمْ تُفْلِحُوْنَ","bc":"yā ayyuhallażīna āmanuṣbirụ wa ṣābirụ wa rābiṭụ, wattaqullāha la'allakum tufliḥụn","at":"Wahai orang-orang yang beriman! Bersabarlah kamu dan kuatkanlah kesabaranmu dan tetaplah bersiap-siaga (di perbatasan negerimu) dan bertakwalah kepada Allah agar kamu beruntung."}],an_naba=[{"id":"1","ar":"عَمَّ يَتَسَاۤءَلُوْنَۚ ","bc":"'amma yatasā`alụn","at":"Tentang apakah mereka saling bertanya-tanya?"},{"id":"2","ar":"عَنِ النَّبَاِ الْعَظِيْمِۙ ","bc":"'anin-naba`il-'aẓīm","at":"Tentang berita yang besar (hari kebangkitan),"},{"id":"3","ar":"الَّذِيْ هُمْ فِيْهِ مُخْتَلِفُوْنَۗ ","bc":"allażī hum fīhi mukhtalifụn","at":"yang dalam hal itu mereka berselisih."},{"id":"4","ar":"كَلَّا سَيَعْلَمُوْنَۙ ","bc":"kallā saya'lamụn","at":"Tidak! Kelak mereka akan mengetahui,"},{"id":"5","ar":"ثُمَّ كَلَّا سَيَعْلَمُوْنَ","bc":"ṡumma kallā saya'lamụn","at":"sekali lagi tidak! Kelak mereka akan mengetahui. "},{"id":"6","ar":"اَلَمْ نَجْعَلِ الْاَرْضَ مِهٰدًاۙ","bc":"a lam naj'alil-arḍa mihādā","at":"Bukankah Kami telah menjadikan bumi sebagai hamparan, "},{"id":"7","ar":"وَّالْجِبَالَ اَوْتَادًاۖ","bc":"wal-jibāla autādā","at":"dan gunung-gunung sebagai pasak?"},{"id":"8","ar":"وَّخَلَقْنٰكُمْ اَزْوَاجًاۙ ","bc":"wa khalaqnākum azwājā","at":"Dan Kami menciptakan kamu berpasang-pasangan,"},{"id":"9","ar":"وَّجَعَلْنَا نَوْمَكُمْ سُبَاتًاۙ ","bc":"wa ja'alnā naumakum subātā","at":"dan Kami menjadikan tidurmu untuk istirahat,"},{"id":"10","ar":"وَّجَعَلْنَا الَّيْلَ لِبَاسًاۙ ","bc":"wa ja'alnal-laila libāsā","at":"dan Kami menjadikan malam sebagai pakaian,"},{"id":"11","ar":"وَّجَعَلْنَا النَّهَارَ مَعَاشًاۚ","bc":"wa ja'alnan-nahāra ma'āsyā","at":"dan Kami menjadikan siang untuk mencari penghidupan,"},{"id":"12","ar":"وَبَنَيْنَا فَوْقَكُمْ سَبْعًا شِدَادًاۙ ","bc":"wa banainā fauqakum sab'an syidādā","at":"dan Kami membangun di atas kamu tujuh (langit) yang kokoh,"},{"id":"13","ar":"وَّجَعَلْنَا سِرَاجًا وَّهَّاجًاۖ","bc":"wa ja'alnā sirājaw wahhājā","at":"dan Kami menjadikan pelita yang terang-benderang (matahari),"},{"id":"14","ar":"وَّاَنْزَلْنَا مِنَ الْمُعْصِرٰتِ مَاۤءً ثَجَّاجًاۙ","bc":"wa anzalnā minal-mu'ṣirāti mā`an ṡajjājā","at":"dan Kami turunkan dari awan, air hujan yang tercurah dengan hebatnya,"},{"id":"15","ar":"لِّنُخْرِجَ بِهٖ حَبًّا وَّنَبَاتًاۙ","bc":"linukhrija bihī ḥabbaw wa nabātā","at":"untuk Kami tumbuhkan dengan air itu biji-bijian dan tanam-tanaman,"},{"id":"16","ar":"وَّجَنّٰتٍ اَلْفَافًاۗ","bc":"wa jannātin alfāfā","at":"dan kebun-kebun yang rindang."},{"id":"17","ar":"اِنَّ يَوْمَ الْفَصْلِ كَانَ مِيْقَاتًاۙ","bc":"inna yaumal-faṣli kāna mīqātā","at":"Sungguh, hari keputusan adalah suatu waktu yang telah ditetapkan,"},{"id":"18","ar":"يَّوْمَ يُنْفَخُ فِى الصُّوْرِ فَتَأْتُوْنَ اَفْوَاجًاۙ","bc":"yauma yunfakhu fiṣ-ṣụri fa ta`tụna afwājā","at":"(yaitu) pada hari (ketika) sangkakala ditiup, lalu kamu datang berbondong-bondong,"},{"id":"19","ar":"وَّفُتِحَتِ السَّمَاۤءُ فَكَانَتْ اَبْوَابًاۙ","bc":"wa futiḥatis-samā`u fa kānat abwābā","at":"dan langit pun dibukalah, maka terdapatlah beberapa pintu,"},{"id":"20","ar":"وَّسُيِّرَتِ الْجِبَالُ فَكَانَتْ سَرَابًاۗ","bc":"wa suyyiratil-jibālu fa kānat sarābā","at":"dan gunung-gunung pun dijalankan sehingga menjadi fatamorgana."},{"id":"21","ar":"اِنَّ جَهَنَّمَ كَانَتْ مِرْصَادًاۙ","bc":"inna jahannama kānat mirṣādā","at":"Sungguh, (neraka) Jahanam itu (sebagai) tempat mengintai (bagi penjaga yang mengawasi isi neraka), "},{"id":"22","ar":"لِّلطّٰغِيْنَ مَاٰبًاۙ","bc":"liṭ-ṭāgīna ma`ābā","at":"menjadi tempat kembali bagi orang-orang yang melampaui batas."},{"id":"23","ar":"لّٰبِثِيْنَ فِيْهَآ اَحْقَابًاۚ","bc":"lābiṡīna fīhā aḥqābā","at":"Mereka tinggal di sana dalam masa yang lama,"},{"id":"24","ar":"لَا يَذُوْقُوْنَ فِيْهَا بَرْدًا وَّلَا شَرَابًاۙ","bc":"lā yażụqụna fīhā bardaw wa lā syarābā","at":"mereka tidak merasakan kesejukan di dalamnya dan tidak (pula mendapat) minuman,"},{"id":"25","ar":"اِلَّا حَمِيْمًا وَّغَسَّاقًاۙ","bc":"illā ḥamīmaw wa gassāqā","at":"selain air yang mendidih dan nanah,"},{"id":"26","ar":"جَزَاۤءً وِّفَاقًاۗ","bc":"jazā`aw wifāqā","at":"sebagai pembalasan yang setimpal."},{"id":"27","ar":"اِنَّهُمْ كَانُوْا لَا يَرْجُوْنَ حِسَابًاۙ","bc":"innahum kānụ lā yarjụna ḥisābā","at":"Sesungguhnya dahulu mereka tidak pernah mengharapkan perhitungan."},{"id":"28","ar":"وَّكَذَّبُوْا بِاٰيٰتِنَا كِذَّابًاۗ","bc":"wa każżabụ bi`āyātinā kiżżābā","at":"Dan mereka benar-benar mendustakan ayat-ayat Kami."},{"id":"29","ar":"وَكُلَّ شَيْءٍ اَحْصَيْنٰهُ كِتٰبًاۙ","bc":"wa kulla syai`in aḥṣaināhu kitābā","at":"Dan segala sesuatu telah Kami catat dalam suatu Kitab (buku catatan amalan manusia). "},{"id":"30","ar":"فَذُوْقُوْا فَلَنْ نَّزِيْدَكُمْ اِلَّا عَذَابًا","bc":"fa żụqụ fa lan nazīdakum illā 'ażābā","at":"Maka karena itu rasakanlah! Maka tidak ada yang akan Kami tambahkan kepadamu selain azab."},{"id":"31","ar":"اِنَّ لِلْمُتَّقِيْنَ مَفَازًاۙ","bc":"inna lil-muttaqīna mafāzā","at":"Sungguh, orang-orang yang bertakwa mendapat kemenangan,"},{"id":"32","ar":"حَدَاۤىِٕقَ وَاَعْنَابًاۙ","bc":"ḥadā`iqa wa a'nābā","at":"(yaitu) kebun-kebun dan buah anggur,"},{"id":"33","ar":"وَّكَوَاعِبَ اَتْرَابًاۙ","bc":"wa kawā'iba atrābā","at":"dan gadis-gadis montok yang sebaya,"},{"id":"34","ar":"وَّكَأْسًا دِهَاقًاۗ","bc":"wa ka`san dihāqā","at":"dan gelas-gelas yang penuh (berisi minuman)."},{"id":"35","ar":"لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا وَّلَا كِذَّابًا","bc":"lā yasma'ụna fīhā lagwaw wa lā kiżżābā","at":"Di sana mereka tidak mendengar percakapan yang sia-sia maupun (perkataan) dusta."},{"id":"36","ar":"جَزَاۤءً مِّنْ رَّبِّكَ عَطَاۤءً حِسَابًاۙ","bc":"jazā`am mir rabbika 'aṭā`an ḥisābā","at":"Sebagai balasan dan pemberian yang cukup banyak dari Tuhanmu,"},{"id":"37","ar":"رَّبِّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا الرَّحْمٰنِ لَا يَمْلِكُوْنَ مِنْهُ خِطَابًاۚ ","bc":"rabbis-samāwāti wal-arḍi wa mā bainahumar-raḥmāni lā yamlikụna min-hu khiṭābā","at":"Tuhan (yang memelihara) langit dan bumi dan apa yang ada di antara keduanya; Yang Maha Pengasih, mereka tidak mampu berbicara dengan Dia."},{"id":"38","ar":"يَوْمَ يَقُوْمُ الرُّوْحُ وَالْمَلٰۤىِٕكَةُ صَفًّاۙ لَّا يَتَكَلَّمُوْنَ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَقَالَ صَوَابًا","bc":"yauma yaqụmur-rụḥu wal-malā`ikatu ṣaffal lā yatakallamụna illā man ażina lahur-raḥmānu wa qāla ṣawābā","at":"Pada hari, ketika ruh dan para malaikat berdiri bersaf-saf, mereka tidak berkata-kata, kecuali siapa yang telah diberi izin kepadanya oleh Tuhan Yang Maha Pengasih dan dia hanya mengatakan yang benar."},{"id":"39","ar":"ذٰلِكَ الْيَوْمُ الْحَقُّۚ فَمَنْ شَاۤءَ اتَّخَذَ اِلٰى رَبِّهٖ مَاٰبًا","bc":"żālikal-yaumul-ḥaqq, fa man syā`attakhaża ilā rabbihī ma`ābā","at":"Itulah hari yang pasti terjadi. Maka barang siapa menghendaki, niscaya dia menempuh jalan kembali kepada Tuhannya."},{"id":"40","ar":"اِنَّآ اَنْذَرْنٰكُمْ عَذَابًا قَرِيْبًا ەۙ يَّوْمَ يَنْظُرُ الْمَرْءُ مَا قَدَّمَتْ يَدَاهُ وَيَقُوْلُ الْكٰفِرُ يٰلَيْتَنِيْ كُنْتُ تُرَابًا","bc":"innā anżarnākum 'ażābang qarībay yauma yanẓurul-mar`u mā qaddamat yadāhu wa yaqụlul-kāfiru yā laitanī kuntu turābā","at":"Sesungguhnya Kami telah memperingatkan kepadamu (orang kafir) azab yang dekat, pada hari manusia melihat apa yang telah diperbuat oleh kedua tangannya; dan orang kafir berkata, “Alangkah baiknya seandainya dahulu aku jadi tanah.”"}],an_nahl=[{"id":"1","ar":"اَتٰىٓ اَمْرُ اللّٰهِ فَلَا تَسْتَعْجِلُوْهُ ۗسُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ","bc":"atā amrullāhi fa lā tasta'jilụh, sub-ḥānahụ wa ta'ālā 'ammā yusyrikụn","at":"Ketetapan Allah pasti datang, maka janganlah kamu meminta agar dipercepat (datang)nya. Mahasuci Allah dan Mahatinggi Dia dari apa yang mereka persekutukan."},{"id":"2","ar":"يُنَزِّلُ الْمَلٰۤىِٕكَةَ بِالرُّوْحِ مِنْ اَمْرِهٖ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖٓ اَنْ اَنْذِرُوْٓا اَنَّهٗ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاتَّقُوْنِ ","bc":"yunazzilul-malā`ikata bir-rụḥi min amrihī 'alā may yasyā`u min 'ibādihī an anżirū annahụ lā ilāha illā ana fattaqụn","at":"Dia menurunkan para malaikat membawa wahyu dengan perintah-Nya kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya, (dengan berfirman) yaitu, “Peringatkanlah (hamba-hamba-Ku), bahwa tidak ada tuhan selain Aku, maka hendaklah kamu bertakwa kepada-Ku.” "},{"id":"3","ar":"خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ تَعٰلٰى عَمَّا يُشْرِكُوْنَ ","bc":"khalaqas-samāwāti wal-arḍa bil-ḥaqq, ta'ālā 'ammā yusyrikụn","at":"Dia menciptakan langit dan bumi dengan kebenaran. Mahatinggi Allah dari apa yang mereka persekutukan."},{"id":"4","ar":"خَلَقَ الْاِنْسَانَ مِنْ نُّطْفَةٍ فَاِذَا هُوَ خَصِيْمٌ مُّبِيْنٌ ","bc":"khalaqal-insāna min nuṭfatin fa iżā huwa khaṣīmum mubīn","at":"Dia telah menciptakan manusia dari mani, ternyata dia menjadi pembantah yang nyata."},{"id":"5","ar":"وَالْاَنْعَامَ خَلَقَهَا لَكُمْ فِيْهَا دِفْءٌ وَّمَنَافِعُ وَمِنْهَا تَأْكُلُوْنَ ","bc":"wal-an'āma khalaqahā lakum fīhā dif`uw wa manāfi'u wa min-hā ta`kulụn","at":"Dan hewan ternak telah diciptakan-Nya, untuk kamu padanya ada (bulu) yang menghangatkan dan berbagai manfaat, dan sebagiannya kamu makan."},{"id":"6","ar":"وَلَكُمْ فِيْهَا جَمَالٌ حِيْنَ تُرِيْحُوْنَ وَحِيْنَ تَسْرَحُوْنَۖ","bc":"wa lakum fīhā jamālun ḥīna turīḥụna wa ḥīna tasraḥụn","at":"Dan kamu memperoleh keindahan padanya, ketika kamu membawanya kembali ke kandang dan ketika kamu melepaskannya (ke tempat penggembalaan)."},{"id":"7","ar":"وَتَحْمِلُ اَثْقَالَكُمْ اِلٰى بَلَدٍ لَّمْ تَكُوْنُوْا بٰلِغِيْهِ اِلَّا بِشِقِّ الْاَنْفُسِۗ اِنَّ رَبَّكُمْ لَرَءُوْفٌ رَّحِيْمٌۙ","bc":"wa taḥmilu aṡqālakum ilā baladil lam takụnụ bāligīhi illā bisyiqqil-anfus, inna rabbakum lara`ụfur raḥīm","at":"Dan ia mengangkut beban-bebanmu ke suatu negeri yang kamu tidak sanggup mencapainya, kecuali dengan susah payah. Sungguh, Tuhanmu Maha Pengasih, Maha Penyayang,"},{"id":"8","ar":"وَّالْخَيْلَ وَالْبِغَالَ وَالْحَمِيْرَ لِتَرْكَبُوْهَا وَزِيْنَةًۗ وَيَخْلُقُ مَا لَا تَعْلَمُوْنَ","bc":"wal-khaila wal-bigāla wal-ḥamīra litarkabụhā wa zīnah, wa yakhluqu mā lā ta'lamụn","at":"dan (Dia telah menciptakan) kuda, bagal, dan keledai, untuk kamu tunggangi dan (menjadi) perhiasan. Allah menciptakan apa yang tidak kamu ketahui."},{"id":"9","ar":"وَعَلَى اللّٰهِ قَصْدُ السَّبِيْلِ وَمِنْهَا جَاۤىِٕرٌ ۗوَلَوْ شَاۤءَ لَهَدٰىكُمْ اَجْمَعِيْنَ ","bc":"wa 'alallāhi qaṣdus-sabīli wa min-hā jā`ir, walau syā`a lahadākum ajma'īn","at":"Dan hak Allah menerangkan jalan yang lurus, dan di antaranya ada (jalan) yang menyimpang. Dan jika Dia menghendaki, tentu Dia memberi petunjuk kamu semua (ke jalan yang benar)."},{"id":"10","ar":"هُوَ الَّذِيْٓ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً لَّكُمْ مِّنْهُ شَرَابٌ وَّمِنْهُ شَجَرٌ فِيْهِ تُسِيْمُوْنَ ","bc":"huwallażī anzala minas-samā`i mā`al lakum min-hu syarābuw wa min-hu syajarun fīhi tusīmụn","at":"Dialah yang telah menurunkan air (hujan) dari langit untuk kamu, sebagiannya menjadi minuman dan sebagiannya (menyuburkan) tumbuhan, padanya kamu menggembalakan ternakmu."},{"id":"11","ar":"يُنْۢبِتُ لَكُمْ بِهِ الزَّرْعَ وَالزَّيْتُوْنَ وَالنَّخِيْلَ وَالْاَعْنَابَ وَمِنْ كُلِّ الثَّمَرٰتِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّتَفَكَّرُوْنَ","bc":"yumbitu lakum bihiz-zar'a waz-zaitụna wan-nakhīla wal-a'nāba wa ming kulliṡ-ṡamarāt, inna fī żālika la`āyatal liqaumiy yatafakkarụn","at":"Dengan (air hujan) itu Dia menumbuhkan untuk kamu tanam-tanaman, zaitun, kurma, anggur dan segala macam buah-buahan. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kebesaran Allah) bagi orang yang berpikir."},{"id":"12","ar":"وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَۙ وَالشَّمْسَ وَالْقَمَرَ ۗوَالنُّجُوْمُ مُسَخَّرٰتٌۢ بِاَمْرِهٖ ۗاِنَّ فِي ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَۙ","bc":"wa sakhkhara lakumul-laila wan-nahāra wasy-syamsa wal-qamar, wan-nujụmu musakhkharātum bi`amrih, inna fī żālika la`āyātil liqaumiy ya'qilụn","at":"Dia menundukkan malam dan siang, matahari dan bulan untukmu, dan bin-tang-bintang dikendalikan dengan perintah-Nya. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Allah) bagi orang yang mengerti,"},{"id":"13","ar":"وَمَا ذَرَاَ لَكُمْ فِى الْاَرْضِ مُخْتَلِفًا اَلْوَانُهٗ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّذَّكَّرُوْنَ ","bc":"wa mā żara`a lakum fil-arḍi mukhtalifan alwānuh, inna fī żālika la`āyatal liqaumiy yażżakkarụn","at":"dan (Dia juga mengendalikan) apa yang Dia ciptakan untukmu di bumi ini dengan berbagai jenis dan macam warnanya. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kebesaran Allah) bagi kaum yang mengambil pelajaran."},{"id":"14","ar":"وَهُوَ الَّذِيْ سَخَّرَ الْبَحْرَ لِتَأْكُلُوْا مِنْهُ لَحْمًا طَرِيًّا وَّتَسْتَخْرِجُوْا مِنْهُ حِلْيَةً تَلْبَسُوْنَهَاۚ وَتَرَى الْفُلْكَ مَوَاخِرَ فِيْهِ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ ","bc":"wa huwallażī sakhkharal-baḥra lita`kulụ min-hu laḥman ṭariyyaw wa tastakhrijụ min-hu ḥilyatan talbasụnahā, wa taral-fulka mawākhira fīhi wa litabtagụ min faḍlihī wa la'allakum tasykurụn","at":"Dan Dialah yang menundukkan lautan (untukmu), agar kamu dapat memakan daging yang segar (ikan) darinya, dan (dari lautan itu) kamu mengeluarkan perhiasan yang kamu pakai. Kamu (juga) melihat perahu berlayar padanya, dan agar kamu mencari sebagian karunia-Nya, dan agar kamu bersyukur."},{"id":"15","ar":"وَاَلْقٰى فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِكُمْ وَاَنْهٰرًا وَّسُبُلًا لَّعَلَّكُمْ تَهْتَدُوْنَۙ","bc":"wa alqā fil-arḍi rawāsiya an tamīda bikum wa an-hāraw wa subulal la'allakum tahtadụn","at":"Dan Dia menancapkan gunung di bumi agar bumi itu tidak goncang bersama kamu, (dan Dia menciptakan) sungai-sungai dan jalan-jalan agar kamu mendapat petunjuk,"},{"id":"16","ar":"وَعَلٰمٰتٍۗ وَبِالنَّجْمِ هُمْ يَهْتَدُوْنَ ","bc":"wa 'alāmāt, wa bin-najmi hum yahtadụn","at":"dan (Dia menciptakan) tanda-tanda (penunjuk jalan). Dan dengan bintang-bintang mereka mendapat petunjuk."},{"id":"17","ar":"اَفَمَنْ يَّخْلُقُ كَمَنْ لَّا يَخْلُقُۗ اَفَلَا تَذَكَّرُوْنَ ","bc":"a fa may yakhluqu kamal lā yakhluq, a fa lā tażakkarụn","at":"Maka apakah (Allah) yang menciptakan sama dengan yang tidak dapat menciptakan (sesuatu)? Mengapa kamu tidak mengambil pelajaran?"},{"id":"18","ar":"وَاِنْ تَعُدُّوْا نِعْمَةَ اللّٰهِ لَا تُحْصُوْهَا ۗاِنَّ اللّٰهَ لَغَفُوْرٌ رَّحِيْمٌ ","bc":"wa in ta'uddụ ni'matallāhi lā tuḥṣụhā, innallāha lagafụrur raḥīm","at":"Dan jika kamu menghitung nikmat Allah, niscaya kamu tidak akan mampu menghitungnya. Sungguh, Allah benar-benar Maha Pengampun, Maha Penyayang."},{"id":"19","ar":"وَاللّٰهُ يَعْلَمُ مَا تُسِرُّوْنَ وَمَا تُعْلِنُوْنَ ","bc":"wallāhu ya'lamu mā tusirrụna wa mā tu'linụn","at":"Dan Allah mengetahui apa yang kamu rahasiakan dan apa yang kamu lahirkan."},{"id":"20","ar":"وَالَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ لَا يَخْلُقُوْنَ شَيْـًٔا وَّهُمْ يُخْلَقُوْنَۗ ","bc":"wallażīna yad'ụna min dụnillāhi lā yakhluqụna syai`aw wa hum yukhlaqụn","at":"Dan (berhala-berhala) yang mereka seru selain Allah, tidak dapat membuat sesuatu apa pun, sedang berhala-berhala itu (sendiri) dibuat orang."},{"id":"21","ar":"اَمْوَاتٌ غَيْرُ اَحْيَاۤءٍ ۗوَمَا يَشْعُرُوْنَۙ اَيَّانَ يُبْعَثُوْنَ ","bc":"amwātun gairu aḥyā`, wa mā yasy'urụna ayyāna yub'aṡụn","at":"(Berhala-berhala itu) benda mati, tidak hidup, dan berhala-berhala itu tidak mengetahui kapankah (penyembahnya) dibangkitkan."},{"id":"22","ar":"اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌ ۚفَالَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ قُلُوْبُهُمْ مُّنْكِرَةٌ وَّهُمْ مُّسْتَكْبِرُوْنَ ","bc":"ilāhukum ilāhuw wāḥid, fallażīna lā yu`minụna bil-ākhirati qulụbuhum mungkiratuw wa hum mustakbirụn","at":"Tuhan kamu adalah Tuhan Yang Maha Esa. Maka orang yang tidak beriman kepada akhirat, hati mereka mengingkari (keesaan Allah), dan mereka adalah orang yang sombong."},{"id":"23","ar":"لَا جَرَمَ اَنَّ اللّٰهَ يَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَ ۗاِنَّهٗ لَا يُحِبُّ الْمُسْتَكْبِرِيْنَ ","bc":"lā jarama annallāha ya'lamu mā yusirrụna wa mā yu'linụn, innahụ lā yuḥibbul-mustakbirīn","at":"Tidak diragukan lagi bahwa Allah mengetahui apa yang mereka rahasiakan dan apa yang mereka lahirkan. Sesungguhnya Dia tidak menyukai orang yang sombong."},{"id":"24","ar":"وَاِذَا قِيْلَ لَهُمْ مَّاذَآ اَنْزَلَ رَبُّكُمْ ۙقَالُوْٓا اَسَاطِيْرُ الْاَوَّلِيْنَ ","bc":"wa iżā qīla lahum māżā anzala rabbukum qālū asāṭīrul-awwalīn","at":"Dan apabila dikatakan kepada mereka, “Apakah yang telah diturunkan Tuhanmu?” Mereka menjawab, “Dongeng-dongeng orang dahulu,”"},{"id":"25","ar":"لِيَحْمِلُوْٓا اَوْزَارَهُمْ كَامِلَةً يَّوْمَ الْقِيٰمَةِ ۙوَمِنْ اَوْزَارِ الَّذِيْنَ يُضِلُّوْنَهُمْ بِغَيْرِ عِلْمٍ ۗ اَلَا سَاۤءَ مَا يَزِرُوْنَ ","bc":"liyaḥmilū auzārahum kāmilatay yaumal-qiyāmati wa min auzārillażīna yuḍillụnahum bigairi 'ilm, alā sā`a mā yazirụn","at":"(ucapan mereka) menyebabkan mereka pada hari Kiamat memikul dosa-dosanya sendiri secara sempurna, dan sebagian dosa-dosa orang yang mereka sesatkan yang tidak mengetahui sedikit pun (bahwa mereka disesatkan). Ingatlah, alangkah buruknya (dosa) yang mereka pikul itu."},{"id":"26","ar":"قَدْ مَكَرَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَاَتَى اللّٰهُ بُنْيَانَهُمْ مِّنَ الْقَوَاعِدِ فَخَرَّ عَلَيْهِمُ السَّقْفُ مِنْ فَوْقِهِمْ وَاَتٰىهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُوْنَ ","bc":"qad makarallażīna ming qablihim fa atallāhu bun-yānahum minal-qawā'idi fa kharra 'alaihimus-saqfu min fauqihim wa atāhumul-'ażābu min ḥaiṡu lā yasy'urụn","at":"Sungguh, orang-orang yang sebelum mereka telah mengadakan tipu daya, maka Allah menghancurkan rumah-rumah mereka mulai dari pondasinya, lalu atap (rumah itu) jatuh menimpa mereka dari atas, dan siksa itu datang kepada mereka dari arah yang tidak mereka sadari."},{"id":"27","ar":"ثُمَّ يَوْمَ الْقِيٰمَةِ يُخْزِيْهِمْ وَيَقُوْلُ اَيْنَ شُرَكَاۤءِيَ الَّذِيْنَ كُنْتُمْ تُشَاۤقُّوْنَ فِيْهِمْ ۗقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ اِنَّ الْخِزْيَ الْيَوْمَ وَالسُّوْۤءَ عَلَى الْكٰفِرِيْنَۙ","bc":"ṡumma yaumal-qiyāmati yukhzīhim wa yaqụlu aina syurakā`iyallażīna kuntum tusyāqqụna fīhim, qālallażīna ụtul-'ilma innal-khizyal-yauma was-sū`a 'alal-kāfirīn","at":"Kemudian Allah menghinakan mereka pada hari Kiamat, dan berfirman, “Di manakah sekutu-sekutu-Ku itu (yang karena membelanya) kamu selalu memusuhi mereka (nabi-nabi dan orang yang beriman)?” Orang-orang yang diberi ilmu berkata, “Sesungguhnya kehinaan dan azab pada hari ini ditimpakan kepada orang yang kafir,”"},{"id":"28","ar":"الَّذِيْنَ تَتَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ ظَالِمِيْٓ اَنْفُسِهِمْ ۖفَاَلْقَوُا السَّلَمَ مَا كُنَّا نَعْمَلُ مِنْ سُوْۤءٍ ۗبَلٰىٓ اِنَّ اللّٰهَ عَلِيْمٌۢ بِمَا كُنْتُمْ تَعْمَلُوْنَ ","bc":"allażīna tatawaffāhumul-malā`ikatu ẓālimī anfusihim fa alqawus-salama mā kunnā na'malu min sū`, balā innallāha 'alīmum bimā kuntum ta'malụn","at":"(yaitu) orang yang dicabut nyawanya oleh para malaikat dalam keadaan (berbuat) zalim kepada diri sendiri, lalu mereka menyerahkan diri (sambil berkata), “Kami tidak pernah mengerjakan sesuatu kejahatan pun.” (Malaikat menjawab), “Pernah! Sesungguhnya Allah Maha Mengetahui apa yang telah kamu kerjakan.”"},{"id":"29","ar":"فَادْخُلُوْٓا اَبْوَابَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَا ۗفَلَبِئْسَ مَثْوَى الْمُتَكَبِّرِيْنَ ","bc":"fadkhulū abwāba jahannama khālidīna fīhā, fa labi`sa maṡwal-mutakabbirīn","at":"Maka masukilah pintu-pintu neraka Jahanam, kamu kekal di dalamnya. Pasti itu seburuk-buruk tempat orang yang menyombongkan diri."},{"id":"30","ar":"۞ وَقِيْلَ لِلَّذِيْنَ اتَّقَوْا مَاذَآ اَنْزَلَ رَبُّكُمْ ۗقَالُوْا خَيْرًا ۚلِلَّذِيْنَ اَحْسَنُوْا فِيْ هٰذِهِ الدُّنْيَا حَسَنَةٌ ۗوَلَدَارُ الْاٰخِرَةِ خَيْرٌ ۗوَلَنِعْمَ دَارُ الْمُتَّقِيْنَۙ","bc":"wa qīla lillażīnattaqau māżā anzala rabbukum, qālụ khairā, lillażīna aḥsanụ fī hāżihid-dun-yā ḥasanah, wa ladārul-ākhirati khaīr, wa lani'ma dārul-muttaqīn","at":"Dan kemudian dikatakan kepada orang yang bertakwa, “Apakah yang telah diturunkan oleh Tuhanmu?” Mereka menjawab, “Kebaikan.” Bagi orang yang berbuat baik di dunia ini mendapat (balasan) yang baik. Dan sesungguhnya negeri akhirat pasti lebih baik. Dan itulah sebaik-baik tempat bagi orang yang bertakwa,"},{"id":"31","ar":"جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ لَهُمْ فِيْهَا مَا يَشَاۤءُوْنَ ۗ كَذٰلِكَ يَجْزِى اللّٰهُ الْمُتَّقِيْنَۙ","bc":"jannātu 'adniy yadkhulụnahā tajrī min taḥtihal-an-hāru lahum fīhā mā yasyā`ụn, każālika yajzillāhul-muttaqīn","at":"(yaitu) surga-surga ‘Adn yang mereka masuki, mengalir di bawahnya sungai-sungai, di dalam (surga) itu mereka mendapat segala apa yang diinginkan. Demikianlah Allah memberi balasan kepada orang yang bertakwa,"},{"id":"32","ar":"الَّذِيْنَ تَتَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ طَيِّبِيْنَ ۙيَقُوْلُوْنَ سَلٰمٌ عَلَيْكُمُ ادْخُلُوا الْجَنَّةَ بِمَا كُنْتُمْ تَعْمَلُوْنَ ","bc":"allażīna tatawaffāhumul-malā`ikatu ṭayyibīna yaqụlụna salāmun 'alaikumudkhulul-jannata bimā kuntum ta'malụn","at":"(yaitu) orang yang ketika diwafatkan oleh para malaikat dalam keadaan baik, mereka (para malaikat) mengatakan (kepada mereka), “Salamun ‘alaikum, masuklah ke dalam surga karena apa yang telah kamu kerjakan.”"},{"id":"33","ar":"هَلْ يَنْظُرُوْنَ اِلَّآ اَنْ تَأْتِيَهُمُ الْمَلٰۤىِٕكَةُ اَوْ يَأْتِيَ اَمْرُ رَبِّكَ ۗ كَذٰلِكَ فَعَلَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۗوَمَا ظَلَمَهُمُ اللّٰهُ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ ","bc":"hal yanẓurụna illā an ta`tiyahumul-malā`ikatu au ya`tiya amru rabbik, każālika fa'alallażīna ming qablihim, wa mā ẓalamahumullāhu wa lāking kānū anfusahum yaẓlimụn","at":"Tidak ada yang ditunggu mereka (orang kafir) selain datangnya para malaikat kepada mereka atau datangnya perintah Tuhanmu. Demikianlah yang telah diperbuat oleh orang-orang (kafir) sebelum mereka. Allah tidak menzalimi mereka, justru merekalah yang (selalu) menzalimi diri mereka sendiri."},{"id":"34","ar":"فَاَصَابَهُمْ سَيِّاٰتُ مَا عَمِلُوْا وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ","bc":"fa aṣābahum sayyi`ātu mā 'amilụ wa ḥāqa bihim mā kānụ bihī yastahzi`ụn","at":"Maka mereka ditimpa azab (akibat) perbuatan mereka dan diliputi oleh azab yang dulu selalu mereka perolok-olokkan."},{"id":"35","ar":"وَقَالَ الَّذِيْنَ اَشْرَكُوْا لَوْ شَاۤءَ اللّٰهُ مَا عَبَدْنَا مِنْ دُوْنِهٖ مِنْ شَيْءٍ نَّحْنُ وَلَآ اٰبَاۤؤُنَا وَلَا حَرَّمْنَا مِنْ دُوْنِهٖ مِنْ شَيْءٍ ۗ كَذٰلِكَ فَعَلَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۚفَهَلْ عَلَى الرُّسُلِ اِلَّا الْبَلٰغُ الْمُبِيْنُ ","bc":"wa qālallażīna asyrakụ lau syā`allāhu mā 'abadnā min dụnihī min syai`in naḥnu wa lā ābā`unā wa lā ḥarramnā min dụnihī min syaī`, każālika fa'alallażīna ming qablihim, fa hal 'alar-rusuli illal-balāgul-mubīn","at":"Dan orang musyrik berkata, “Jika Allah menghendaki, niscaya kami tidak akan menyembah sesuatu apa pun selain Dia, baik kami maupun bapak-bapak kami, dan tidak (pula) kami mengharamkan sesuatu pun tanpa (izin)-Nya.” Demikianlah yang diperbuat oleh orang sebelum mereka. Bukankah kewajiban para rasul hanya menyampaikan (amanat Allah) dengan jelas."},{"id":"36","ar":"وَلَقَدْ بَعَثْنَا فِيْ كُلِّ اُمَّةٍ رَّسُوْلًا اَنِ اعْبُدُوا اللّٰهَ وَاجْتَنِبُوا الطَّاغُوْتَۚ فَمِنْهُمْ مَّنْ هَدَى اللّٰهُ وَمِنْهُمْ مَّنْ حَقَّتْ عَلَيْهِ الضَّلٰلَةُ ۗ فَسِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ ","bc":"wa laqad ba'aṡnā fī kulli ummatir rasụlan ani'budullāha wajtanibuṭ-ṭāgụt, fa min-hum man hadallāhu wa min-hum man ḥaqqat 'alaihiḍ-ḍalālah, fa sīrụ fil-arḍi fanẓurụ kaifa kāna 'āqibatul-mukażżibīn","at":"Dan sungguh, Kami telah mengutus seorang rasul untuk setiap umat (untuk menyerukan), “Sembahlah Allah, dan jauhilah tagut”, kemudian di antara mereka ada yang diberi petunjuk oleh Allah dan ada pula yang tetap dalam kesesatan. Maka berjalanlah kamu di bumi dan perhatikanlah bagaimana kesudahan orang yang mendustakan (rasul-rasul)."},{"id":"37","ar":"اِنْ تَحْرِصْ عَلٰى هُدٰىهُمْ فَاِنَّ اللّٰهَ لَا يَهْدِيْ مَنْ يُّضِلُّ وَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ ","bc":"in taḥriṣ 'alā hudāhum fa innallāha lā yahdī may yuḍillu wa mā lahum min nāṣirīn","at":"Jika engkau (Muhammad) sangat mengharapkan agar mereka mendapat petunjuk, maka sesungguhnya Allah tidak akan memberi petunjuk kepada orang yang disesatkan-Nya, dan mereka tidak mempunyai penolong."},{"id":"38","ar":"وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْۙ لَا يَبْعَثُ اللّٰهُ مَنْ يَّمُوْتُۗ بَلٰى وَعْدًا عَلَيْهِ حَقًّا وَّلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَۙ","bc":"wa aqsamụ billāhi jahda aimānihim lā yab'aṡullāhu may yamụt, balā wa'dan 'alaihi ḥaqqaw wa lākinna akṡaran-nāsi lā ya'lamụn","at":"Dan mereka bersumpah dengan (nama) Allah dengan sumpah yang sungguh-sungguh, “Allah tidak akan membangkitkan orang yang mati.” Tidak demikian (pasti Allah akan membangkitkannya), sebagai suatu janji yang benar dari-Nya, tetapi kebanyakan manusia tidak mengetahui,"},{"id":"39","ar":"لِيُبَيِّنَ لَهُمُ الَّذِيْ يَخْتَلِفُوْنَ فِيْهِ وَلِيَعْلَمَ الَّذِيْنَ كَفَرُوْٓا اَنَّهُمْ كَانُوْا كٰذِبِيْنَ ","bc":"liyubayyina lahumullażī yakhtalifụna fīhi wa liya'lamallażīna kafarū annahum kānụ kāżibīn","at":"agar Dia menjelaskan kepada mereka apa yang mereka perselisihkan itu, dan agar orang kafir itu mengetahui bahwa mereka adalah orang yang berdusta. "},{"id":"40","ar":"اِنَّمَا قَوْلُنَا لِشَيْءٍ اِذَآ اَرَدْنٰهُ اَنْ نَّقُوْلَ لَهٗ كُنْ فَيَكُوْنُ ","bc":"innamā qaulunā lisyai`in iżā aradnāhu an naqụla lahụ kun fa yakụn","at":"Sesungguhnya firman Kami terhadap sesuatu apabila Kami menghendakinya, Kami hanya mengatakan kepadanya, “Jadilah!” Maka jadilah sesuatu itu."},{"id":"41","ar":"وَالَّذِيْنَ هَاجَرُوْا فِى اللّٰهِ مِنْۢ بَعْدِ مَا ظُلِمُوْا لَنُبَوِّئَنَّهُمْ فِى الدُّنْيَا حَسَنَةً ۗوَلَاَجْرُ الْاٰخِرَةِ اَكْبَرُۘ لَوْ كَانُوْا يَعْلَمُوْنَۙ","bc":"wallażīna hājarụ fillāhi mim ba'di mā ẓulimụ lanubawwi`annahum fid-dun-yā ḥasanah, wa la`ajrul-ākhirati akbar, lau kānụ ya'lamụn","at":"Dan orang yang berhijrah karena Allah setelah mereka dizalimi, pasti Kami akan memberikan tempat yang baik kepada mereka di dunia. Dan pahala di akhirat pasti lebih besar, sekiranya mereka mengetahui,"},{"id":"42","ar":"الَّذِيْنَ صَبَرُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ ","bc":"allażīna ṣabarụ wa 'alā rabbihim yatawakkalụn","at":"(yaitu) orang yang sabar dan hanya kepada Tuhan mereka bertawakal."},{"id":"43","ar":"وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ فَاسْـَٔلُوْٓا اَهْلَ الذِّكْرِ اِنْ كُنْتُمْ لَا تَعْلَمُوْنَۙ","bc":"wa mā arsalnā ming qablika illā rijālan nụḥī ilaihim fas`alū ahlaż-żikri ing kuntum lā ta'lamụn","at":"Dan Kami tidak mengutus sebelum engkau (Muhammad), melainkan orang laki-laki yang Kami beri wahyu kepada mereka; maka bertanyalah kepada orang yang mempunyai pengetahuan jika kamu tidak mengetahui,"},{"id":"44","ar":"بِالْبَيِّنٰتِ وَالزُّبُرِۗ وَاَنْزَلْنَآ اِلَيْكَ الذِّكْرَ لِتُبَيِّنَ لِلنَّاسِ مَا نُزِّلَ اِلَيْهِمْ وَلَعَلَّهُمْ يَتَفَكَّرُوْنَ ","bc":"bil-bayyināti waz-zubur, wa anzalnā ilaikaż-żikra litubayyina lin-nāsi mā nuzzila ilaihim wa la'allahum yatafakkarụn","at":"(mereka Kami utus) dengan membawa keterangan-keterangan (mukjizat) dan kitab-kitab. Dan Kami turunkan Ad-Dzikr (Al-Qur'an) kepadamu, agar engkau menerangkan kepada manusia apa yang telah diturunkan kepada mereka dan agar mereka memikirkan,"},{"id":"45","ar":"اَفَاَمِنَ الَّذِيْنَ مَكَرُوا السَّيِّاٰتِ اَنْ يَّخْسِفَ اللّٰهُ بِهِمُ الْاَرْضَ اَوْ يَأْتِيَهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُوْنَۙ","bc":"a fa aminallażīna makarus-sayyi`āti ay yakhsifallāhu bihimul-arḍa au ya`tiyahumul-'ażābu min ḥaiṡu lā yasy'urụn","at":"maka apakah orang yang membuat tipu daya yang jahat itu, merasa aman (dari bencana) dibenamkannya bumi oleh Allah bersama mereka, atau (terhadap) datangnya siksa kepada mereka dari arah yang tidak mereka sadari, "},{"id":"46","ar":"اَوْ يَأْخُذَهُمْ فِيْ تَقَلُّبِهِمْ فَمَا هُمْ بِمُعْجِزِيْنَۙ","bc":"au ya`khużahum fī taqallubihim fa mā hum bimu'jizīn","at":"atau Allah mengazab mereka pada waktu mereka dalam perjalanan; sehingga mereka tidak berdaya menolak (azab itu),"},{"id":"47","ar":"اَوْ يَأْخُذَهُمْ عَلٰى تَخَوُّفٍۗ فَاِنَّ رَبَّكُمْ لَرَءُوْفٌ رَّحِيْمٌ ","bc":"au ya`khużahum 'alā takhawwuf, fa inna rabbakum lara`ụfur raḥīm","at":"atau Allah mengazab mereka dengan berangsur-angsur (sampai binasa). Maka sungguh, Tuhanmu Maha Pengasih, Maha Penyayang."},{"id":"48","ar":"اَوَلَمْ يَرَوْا اِلٰى مَا خَلَقَ اللّٰهُ مِنْ شَيْءٍ يَّتَفَيَّؤُا ظِلٰلُهٗ عَنِ الْيَمِيْنِ وَالشَّمَاۤىِٕلِ سُجَّدًا لِّلّٰهِ وَهُمْ دَاخِرُوْنَ ","bc":"a wa lam yarau ilā mā khalaqallāhu min syai`iy yatafayya`u ẓilāluhụ 'anil-yamīni wasy-syamā`ili sujjadal lillāhi wa hum dākhirụn","at":"Dan apakah mereka tidak memperhatikan suatu benda yang diciptakan Allah, bayang-bayangnya berbolak-balik ke kanan dan ke kiri, dalam keadaan sujud kepada Allah, dan mereka (bersikap) rendah hati."},{"id":"49","ar":"وَلِلّٰهِ يَسْجُدُ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ مِنْ دَاۤبَّةٍ وَّالْمَلٰۤىِٕكَةُ وَهُمْ لَا يَسْتَكْبِرُوْنَ ","bc":"wa lillāhi yasjudu mā fis-samāwāti wa mā fil-arḍi min dābbatiw wal-malā`ikatu wa hum lā yastakbirụn","at":"Dan segala apa yang ada di langit dan di bumi hanya bersujud kepada Allah yaitu semua makhluk bergerak (bernyawa) dan (juga) para malaikat, dan mereka (malaikat) tidak menyombongkan diri."},{"id":"50","ar":"يَخَافُوْنَ رَبَّهُمْ مِّنْ فَوْقِهِمْ وَيَفْعَلُوْنَ مَا يُؤْمَرُوْنَ ۩ ","bc":"yakhāfụna rabbahum min fauqihim wa yaf'alụna mā yu`marụn","at":"Mereka takut kepada Tuhan yang (berkuasa) di atas mereka dan melaksanakan apa yang diperintahkan (kepada mereka)."},{"id":"51","ar":"۞ وَقَالَ اللّٰهُ لَا تَتَّخِذُوْٓا اِلٰهَيْنِ اثْنَيْنِۚ اِنَّمَا هُوَ اِلٰهٌ وَّاحِدٌ فَاِيَّايَ فَارْهَبُوْنِ ","bc":"wa qālallāhu lā tattakhiżū ilāhainiṡnaīn, innamā huwa ilāhuw wāḥidun fa iyyāya far-habụn","at":"Dan Allah berfirman, “Janganlah kamu menyembah dua tuhan; hanyalah Dia Tuhan Yang Maha Esa. Maka hendaklah kepada-Ku saja kamu takut.”"},{"id":"52","ar":"وَلَهٗ مَا فِى السَّمٰوٰتِ وَالْاَرْضِ وَلَهُ الدِّيْنُ وَاصِبًاۗ اَفَغَيْرَ اللّٰهِ تَتَّقُوْنَ ","bc":"wa lahụ mā fis-samāwāti wal-arḍi wa lahud-dīnu wāṣibā, a fa gairallāhi tattaqụn","at":"Dan milik-Nya meliputi segala apa yang ada di langit dan di bumi, dan kepada-Nyalah (ibadah dan) ketaatan selama-lamanya. Mengapa kamu takut kepada selain Allah?"},{"id":"53","ar":"وَمَا بِكُمْ مِّنْ نِّعْمَةٍ فَمِنَ اللّٰهِ ثُمَّ اِذَا مَسَّكُمُ الضُّرُّ فَاِلَيْهِ تَجْـَٔرُوْنَۚ ","bc":"wa mā bikum min ni'matin fa minallāhi ṡumma iżā massakumuḍ-ḍurru fa ilaihi taj`arụn","at":"Dan segala nikmat yang ada padamu (datangnya) dari Allah, kemudian apabila kamu ditimpa kesengsaraan, maka kepada-Nyalah kamu meminta pertolongan."},{"id":"54","ar":"ثُمَّ اِذَا كَشَفَ الضُّرَّ عَنْكُمْ اِذَا فَرِيْقٌ مِّنْكُمْ بِرَبِّهِمْ يُشْرِكُوْنَۙ","bc":"ṡumma iżā kasyafaḍ-ḍurra 'angkum iżā farīqum mingkum birabbihim yusyrikụn","at":"Kemudian apabila Dia telah menghilangkan bencana dari kamu, malah sebagian kamu mempersekutukan Tuhan dengan (yang lain)."},{"id":"55","ar":"لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۗ فَتَمَتَّعُوْاۗ فَسَوْفَ تَعْلَمُوْنَ ","bc":"liyakfurụ bimā ātaināhum, fa tamatta'ụ, fa saufa ta'lamụn","at":"Biarlah mereka mengingkari nikmat yang telah Kami berikan kepada mereka; bersenang-senanglah kamu. Kelak kamu akan mengetahui (akibatnya)."},{"id":"56","ar":"وَيَجْعَلُوْنَ لِمَا لَا يَعْلَمُوْنَ نَصِيْبًا مِّمَّا رَزَقْنٰهُمْۗ تَاللّٰهِ لَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَفْتَرُوْنَ ","bc":"wa yaj'alụna limā lā ya'lamụna naṣībam mimmā razaqnāhum, tallāhi latus`alunna 'ammā kuntum taftarụn","at":"Dan mereka menyediakan sebagian dari rezeki yang telah Kami berikan kepada mereka, untuk berhala-berhala yang mereka tidak mengetahui (kekuasaannya). Demi Allah, kamu pasti akan ditanyai tentang apa yang telah kamu ada-adakan."},{"id":"57","ar":"وَيَجْعَلُوْنَ لِلّٰهِ الْبَنٰتِ سُبْحٰنَهٗۙ وَلَهُمْ مَّا يَشْتَهُوْنَ ","bc":"wa yaj'alụna lillāhil-banāti sub-ḥānahụ wa lahum mā yasytahụn","at":"Dan mereka menetapkan anak perempuan bagi Allah. Mahasuci Dia, sedang untuk mereka sendiri apa yang mereka sukai (anak laki-laki)."},{"id":"58","ar":"وَاِذَا بُشِّرَ اَحَدُهُمْ بِالْاُنْثٰى ظَلَّ وَجْهُهٗ مُسْوَدًّا وَّهُوَ كَظِيْمٌۚ ","bc":"wa iżā busysyira aḥaduhum bil-unṡā ẓalla waj-huhụ muswaddaw wa huwa kaẓīm","at":"Padahal apabila seseorang dari mereka diberi kabar dengan (kelahiran) anak perempuan, wajahnya menjadi hitam (merah padam), dan dia sangat marah."},{"id":"59","ar":"يَتَوٰرٰى مِنَ الْقَوْمِ مِنْ سُوْۤءِ مَا بُشِّرَ بِهٖۗ اَيُمْسِكُهٗ عَلٰى هُوْنٍ اَمْ يَدُسُّهٗ فِى التُّرَابِۗ اَلَا سَاۤءَ مَا يَحْكُمُوْنَ ","bc":"yatawarā minal-qaumi min sū`i mā busysyira bih, a yumsikuhụ 'alā hụnin am yadussuhụ fit-turāb, alā sā`a mā yaḥkumụn","at":"Dia bersembunyi dari orang banyak, disebabkan kabar buruk yang disampaikan kepadanya. Apakah dia akan memeliharanya dengan (menanggung) kehinaan atau akan membenamkannya ke dalam tanah (hidup-hidup)? Ingatlah alangkah buruknya (putusan) yang mereka tetapkan itu."},{"id":"60","ar":"لِلَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ مَثَلُ السَّوْءِۚ وَلِلّٰهِ الْمَثَلُ الْاَعْلٰىۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ","bc":"lillażīna lā yu`minụna bil-ākhirati maṡalus-saụ`, walillāhil-maṡalul-a'lā, wa huwal-'azīzul-ḥakīm","at":"Bagi orang-orang yang tidak beriman pada (kehidupan) akhirat, (mempunyai) sifat yang buruk; dan Allah mempunyai sifat Yang Mahatinggi. Dan Dia Mahaperkasa, Mahabijaksana."},{"id":"61","ar":"وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِظُلْمِهِمْ مَّا تَرَكَ عَلَيْهَا مِنْ دَاۤبَّةٍ وَّلٰكِنْ يُّؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۚ فَاِذَا جَاۤءَ اَجَلُهُمْ لَا يَسْتَأْخِرُوْنَ سَاعَةً وَّلَا يَسْتَقْدِمُوْنَ ","bc":"walau yu`ākhiżullāhun-nāsa biẓulmihim mā taraka 'alaihā min dābbatiw wa lākiy yu`akhkhiruhum ilā ajalim musammā, fa iżā jā`a ajaluhum lā yasta`khirụna sā'ataw wa lā yastaqdimụn","at":"Dan kalau Allah menghukum manusia karena kezalimannya, niscaya tidak akan ada yang ditinggalkan-Nya (di bumi) dari makhluk yang melata sekalipun, tetapi Allah menangguhkan mereka sampai waktu yang sudah ditentukan. Maka apabila ajalnya tiba, mereka tidak dapat meminta penundaan atau percepatan sesaat pun. "},{"id":"62","ar":"وَيَجْعَلُوْنَ لِلّٰهِ مَا يَكْرَهُوْنَ وَتَصِفُ اَلْسِنَتُهُمُ الْكَذِبَ اَنَّ لَهُمُ الْحُسْنٰى لَا جَرَمَ اَنَّ لَهُمُ النَّارَ وَاَنَّهُمْ مُّفْرَطُوْنَ ","bc":"wa yaj'alụna lillāhi mā yakrahụna wa taṣifu alsinatuhumul-każiba anna lahumul-ḥusnā lā jarama anna lahumun-nāra wa annahum mufraṭụn","at":"Dan mereka menetapkan bagi Allah apa yang mereka sendiri membencinya, dan lidah mereka mengucapkan kebohongan, bahwa sesungguhnya (segala) yang baik-baik untuk mereka. Tidaklah diragukan bahwa nerakalah bagi mereka, dan sesungguhnya mereka segera akan dimasukkan (ke dalamnya)."},{"id":"63","ar":"تَاللّٰهِ لَقَدْ اَرْسَلْنَآ اِلٰٓى اُمَمٍ مِّنْ قَبْلِكَ فَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَهُوَ وَلِيُّهُمُ الْيَوْمَ وَلَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"tallāhi laqad arsalnā ilā umamim ming qablika fa zayyana lahumusy-syaiṭānu a'mālahum fa huwa waliyyuhumul-yauma wa lahum 'ażābun alīm","at":"Demi Allah, sungguh Kami telah mengutus (rasul-rasul) kepada umat-umat sebelum engkau (Muhammad), tetapi setan menjadikan terasa indah bagi mereka perbuatan mereka (yang buruk), sehingga dia (setan) menjadi pemimpin mereka pada hari ini dan mereka akan mendapat azab yang sangat pedih."},{"id":"64","ar":"وَمَآ اَنْزَلْنَا عَلَيْكَ الْكِتٰبَ اِلَّا لِتُبَيِّنَ لَهُمُ الَّذِى اخْتَلَفُوْا فِيْهِۙ وَهُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ ","bc":"wa mā anzalnā 'alaikal-kitāba illā litubayyina lahumullażikhtalafụ fīhi wa hudaw wa raḥmatal liqaumiy yu`minụn","at":"Dan Kami tidak menurunkan Kitab (Al-Qur'an) ini kepadamu (Muhammad), melainkan agar engkau dapat menjelaskan kepada mereka apa yang mereka perselisihkan, serta menjadi petunjuk dan rahmat bagi orang-orang yang beriman."},{"id":"65","ar":"وَاللّٰهُ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَحْيَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّسْمَعُوْنَ ","bc":"wallāhu anzala minas-samā`i mā`an fa aḥyā bihil-arḍa ba'da mautihā, inna fī żālika la`āyatal liqaumiy yasma'ụn","at":"Dan Allah menurunkan air (hujan) dari langit dan dengan air itu dihidupkan-Nya bumi yang tadinya sudah mati. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang mendengarkan (pelajaran)."},{"id":"66","ar":"وَاِنَّ لَكُمْ فِى الْاَنْعَامِ لَعِبْرَةً ۚ نُسْقِيْكُمْ مِّمَّا فِيْ بُطُوْنِهٖ مِنْۢ بَيْنِ فَرْثٍ وَّدَمٍ لَّبَنًا خَالِصًا سَاۤىِٕغًا لِّلشّٰرِبِيْنَ ","bc":"wa inna lakum fil-an'āmi la'ibrah, nusqīkum mimmā fī buṭụnihī mim baini farṡiw wa damil labanan khāliṣan sā`igal lisy-syāribīn","at":"Dan sungguh, pada hewan ternak itu benar-benar terdapat pelajaran bagi kamu. Kami memberimu minum dari apa yang ada dalam perutnya (berupa) susu murni antara kotoran dan darah, yang mudah ditelan bagi orang yang meminumnya."},{"id":"67","ar":"وَمِنْ ثَمَرٰتِ النَّخِيْلِ وَالْاَعْنَابِ تَتَّخِذُوْنَ مِنْهُ سَكَرًا وَّرِزْقًا حَسَنًاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّعْقِلُوْنَ ","bc":"wa min ṡamarātin-nakhīli wal-a'nābi tattakhiżụna min-hu sakaraw wa rizqan ḥasanā, inna fī żālika la`āyatal liqaumiy ya'qilụn","at":"Dan dari buah kurma dan anggur, kamu membuat minuman yang memabukkan dan rezeki yang baik. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kebesaran Allah) bagi orang yang mengerti."},{"id":"68","ar":"وَاَوْحٰى رَبُّكَ اِلَى النَّحْلِ اَنِ اتَّخِذِيْ مِنَ الْجِبَالِ بُيُوْتًا وَّمِنَ الشَّجَرِ وَمِمَّا يَعْرِشُوْنَۙ","bc":"wa auḥā rabbuka ilan-naḥli anittakhiżī minal-jibāli buyụtaw wa minasy-syajari wa mimmā ya'risyụn","at":"Dan Tuhanmu mengilhamkan kepada lebah, “Buatlah sarang di gunung-gunung, di pohon-pohon kayu, dan di tempat-tempat yang dibikin manusia,"},{"id":"69","ar":"ثُمَّ كُلِيْ مِنْ كُلِّ الثَّمَرٰتِ فَاسْلُكِيْ سُبُلَ رَبِّكِ ذُلُلًاۗ يَخْرُجُ مِنْ بُطُوْنِهَا شَرَابٌ مُّخْتَلِفٌ اَلْوَانُهٗ ۖفِيْهِ شِفَاۤءٌ لِّلنَّاسِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّتَفَكَّرُوْنَ ","bc":"ṡumma kulī ming kulliṡ-ṡamarāti faslukī subula rabbiki żululā, yakhruju mim buṭụnihā syarābum mukhtalifun alwānuhụ fīhi syifā`ul lin-nās, inna fī żālika la`āyatal liqaumiy yatafakkarụn","at":"kemudian makanlah dari segala (macam) buah-buahan lalu tempuhlah jalan Tuhanmu yang telah dimudahkan (bagimu).” Dari perut lebah itu keluar minuman (madu) yang bermacam-macam warnanya, di dalamnya terdapat obat yang menyembuhkan bagi manusia. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kebesaran Allah) bagi orang yang berpikir."},{"id":"70","ar":"وَاللّٰهُ خَلَقَكُمْ ثُمَّ يَتَوَفّٰىكُمْ وَمِنْكُمْ مَّنْ يُّرَدُّ اِلٰٓى اَرْذَلِ الْعُمُرِ لِكَيْ لَا يَعْلَمَ بَعْدَ عِلْمٍ شَيْـًٔاۗ اِنَّ اللّٰهَ عَلِيْمٌ قَدِيْرٌ ","bc":"wallāhu khalaqakum ṡumma yatawaffākum wa mingkum may yuraddu ilā arżalil-'umuri likai lā ya'lama ba'da 'ilmin syai`ā, innallāha 'alīmung qadīr","at":"Dan Allah telah menciptakan kamu, kemudian mewafatkanmu, di antara kamu ada yang dikembalikan kepada usia yang tua renta (pikun), sehingga dia tidak mengetahui lagi sesuatu yang pernah diketahuinya. Sungguh, Allah Maha Mengetahui, Mahakuasa."},{"id":"71","ar":"وَاللّٰهُ فَضَّلَ بَعْضَكُمْ عَلٰى بَعْضٍ فِى الرِّزْقِۚ فَمَا الَّذِيْنَ فُضِّلُوْا بِرَاۤدِّيْ رِزْقِهِمْ عَلٰى مَا مَلَكَتْ اَيْمَانُهُمْ فَهُمْ فِيْهِ سَوَاۤءٌۗ اَفَبِنِعْمَةِ اللّٰهِ يَجْحَدُوْنَ ","bc":"wallāhu faḍḍala ba'ḍakum 'alā ba'ḍin fir-rizq, fa mallażīna fuḍḍilụ birāddī rizqihim 'alā mā malakat aimānuhum fa hum fīhi sawā`, a fa bini'matillāhi yaj-ḥadụn","at":"Dan Allah melebihkan sebagian kamu atas sebagian yang lain dalam hal rezeki, tetapi orang yang dilebihkan (rezekinya itu) tidak mau memberikan rezekinya kepada para hamba sahaya yang mereka miliki, sehingga mereka sama-sama (merasakan) rezeki itu. Mengapa mereka mengingkari nikmat Allah?"},{"id":"72","ar":"وَاللّٰهُ جَعَلَ لَكُمْ مِّنْ اَنْفُسِكُمْ اَزْوَاجًا وَّجَعَلَ لَكُمْ مِّنْ اَزْوَاجِكُمْ بَنِيْنَ وَحَفَدَةً وَّرَزَقَكُمْ مِّنَ الطَّيِّبٰتِۗ اَفَبِالْبَاطِلِ يُؤْمِنُوْنَ وَبِنِعْمَتِ اللّٰهِ هُمْ يَكْفُرُوْنَۙ","bc":"wallāhu ja'ala lakum min anfusikum azwājaw wa ja'ala lakum min azwājikum banīna wa ḥafadataw wa razaqakum minaṭ-ṭayyibāt, a fa bil-bāṭili yu`minụna wa bini'matillāhi hum yakfurụn","at":"Dan Allah menjadikan bagimu pasangan (suami atau istri) dari jenis kamu sendiri dan menjadikan anak dan cucu bagimu dari pasanganmu, serta memberimu rezeki dari yang baik. Mengapa mereka beriman kepada yang batil dan mengingkari nikmat Allah?"},{"id":"73","ar":"وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَمْلِكُ لَهُمْ رِزْقًا مِّنَ السَّمٰوٰتِ وَالْاَرْضِ شَيْـًٔا وَّلَا يَسْتَطِيْعُوْنَ ۚ ","bc":"wa ya'budụna min dụnillāhi mā lā yamliku lahum rizqam minas-samāwāti wal-arḍi syai`aw wa lā yastaṭī'ụn","at":"Dan mereka menyembah selain Allah, sesuatu yang sama sekali tidak dapat memberikan rezeki kepada mereka, dari langit dan bumi, dan tidak akan sanggup (berbuat apa pun)."},{"id":"74","ar":"فَلَا تَضْرِبُوْا لِلّٰهِ الْاَمْثَالَ ۗاِنَّ اللّٰهَ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ ","bc":"fa lā taḍribụ lillāhil-amṡāl, innallāha ya'lamu wa antum lā ta'lamụn","at":"Maka janganlah kamu mengadakan sekutu-sekutu bagi Allah. Sungguh, Allah mengetahui, sedang kamu tidak mengetahui."},{"id":"75","ar":"۞ ضَرَبَ اللّٰهُ مَثَلًا عَبْدًا مَّمْلُوْكًا لَّا يَقْدِرُ عَلٰى شَيْءٍ وَّمَنْ رَّزَقْنٰهُ مِنَّا رِزْقًا حَسَنًا فَهُوَ يُنْفِقُ مِنْهُ سِرًّا وَّجَهْرًاۗ هَلْ يَسْتَوٗنَ ۚ اَلْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ","bc":"ḍaraballāhu maṡalan 'abdam mamlụkal lā yaqdiru 'alā syai`iw wa mar razaqnāhu minnā rizqan ḥasanan fa huwa yunfiqu min-hu sirraw wa jahrā, hal yastawụn, al-ḥamdu lillāh, bal akṡaruhum lā ya'lamụn","at":"Allah membuat perumpamaan seorang hamba sahaya di bawah kekuasaan orang lain, yang tidak berdaya berbuat sesuatu, dan seorang yang Kami beri rezeki yang baik, lalu dia menginfakkan sebagian rezeki itu secara sembunyi-sembunyi dan secara terang-terangan. Samakah mereka itu? Segala puji hanya bagi Allah, tetapi kebanyakan mereka tidak mengetahui."},{"id":"76","ar":"وَضَرَبَ اللّٰهُ مَثَلًا رَّجُلَيْنِ اَحَدُهُمَآ اَبْكَمُ لَا يَقْدِرُ عَلٰى شَيْءٍ وَّهُوَ كَلٌّ عَلٰى مَوْلٰىهُ ۗ اَيْنَمَا يُوَجِّهْهُّ لَا يَأْتِ بِخَيْرٍ ۖهَلْ يَسْتَوِيْ هُوَۙ وَمَنْ يَّأْمُرُ بِالْعَدْلِ وَهُوَ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"wa ḍaraballāhu maṡalar rajulaini aḥaduhumā abkamu lā yaqdiru 'alā syai`iw wa huwa kallun 'alā maulāh, ainamā yuwajjihhu lā ya`ti bikhairin hal yastawī huwa wa may ya`muru bil-'adli wa huwa 'alā ṣirāṭim mustaqīm","at":"Dan Allah (juga) membuat perumpamaan, dua orang laki-laki, yang seorang bisu, tidak dapat berbuat sesuatu dan dia menjadi beban penanggungnya, ke mana saja dia disuruh (oleh penanggungnya itu), dia sama sekali tidak dapat mendatangkan suatu kebaikan. Samakah orang itu dengan orang yang menyuruh berbuat keadilan, dan dia berada di jalan yang lurus?"},{"id":"77","ar":"وَلِلّٰهِ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِۗ وَمَآ اَمْرُ السَّاعَةِ اِلَّا كَلَمْحِ الْبَصَرِ اَوْ هُوَ اَقْرَبُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"wa lillāhi gaibus-samāwāti wal-arḍ, wa mā amrus-sā'ati illā kalam-ḥil-baṣari au huwa aqrab, innallāha 'alā kulli syai`ing qadīr","at":"Dan milik Allah (segala) yang tersembunyi di langit dan di bumi. Urusan kejadian Kiamat itu, hanya seperti sekejap mata atau lebih cepat (lagi). Sesungguhnya Allah Mahakuasa atas segala sesuatu."},{"id":"78","ar":"وَاللّٰهُ اَخْرَجَكُمْ مِّنْۢ بُطُوْنِ اُمَّهٰتِكُمْ لَا تَعْلَمُوْنَ شَيْـًٔاۙ وَّجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَ ۙ لَعَلَّكُمْ تَشْكُرُوْنَ ","bc":"wallāhu akhrajakum mim buṭụni ummahātikum lā ta'lamụna syai`aw wa ja'ala lakumus-sam'a wal-abṣāra wal-af`idata la'allakum tasykurụn","at":"Dan Allah mengeluarkan kamu dari perut ibumu dalam keadaan tidak mengetahui sesuatu pun, dan Dia memberimu pendengaran, penglihatan, dan hati nurani, agar kamu bersyukur."},{"id":"79","ar":"اَلَمْ يَرَوْا اِلَى الطَّيْرِ مُسَخَّرٰتٍ فِيْ جَوِّ السَّمَاۤءِ ۗمَا يُمْسِكُهُنَّ اِلَّا اللّٰهُ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ","bc":"a lam yarau ilaṭ-ṭairi musakhkharātin fī jawwis-samā`, mā yumsikuhunna illallāh, inna fī żālika la`āyātil liqaumiy yu`minụn","at":"Tidakkah mereka memperhatikan burung-burung yang dapat terbang di angkasa dengan mudah. Tidak ada yang menahannya selain Allah. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang beriman."},{"id":"80","ar":"وَاللّٰهُ جَعَلَ لَكُمْ مِّنْۢ بُيُوْتِكُمْ سَكَنًا وَّجَعَلَ لَكُمْ مِّنْ جُلُوْدِ الْاَنْعَامِ بُيُوْتًا تَسْتَخِفُّوْنَهَا يَوْمَ ظَعْنِكُمْ وَيَوْمَ اِقَامَتِكُمْ ۙ وَمِنْ اَصْوَافِهَا وَاَوْبَارِهَا وَاَشْعَارِهَآ اَثَاثًا وَّمَتَاعًا اِلٰى حِيْنٍ ","bc":"wallāhu ja'ala lakum mim buyụtikum sakanaw wa ja'ala lakum min julụdil-an'āmi buyụtan tastakhiffụnahā yauma ẓa'nikum wa yauma iqāmatikum wa min aṣwāfihā wa aubārihā wa asy'ārihā aṡāṡaw wa matā'an ilā ḥīn","at":"Dan Allah menjadikan rumah-rumah bagimu sebagai tempat tinggal dan Dia menjadikan bagimu rumah-rumah (kemah-kemah) dari kulit hewan ternak yang kamu merasa ringan (membawa)nya pada waktu kamu bepergian dan pada waktu kamu bermukim dan (dijadikan-Nya pula) dari bulu domba, bulu unta, dan bulu kambing, alat-alat rumah tangga dan kesenangan sampai waktu (tertentu)."},{"id":"81","ar":"وَاللّٰهُ جَعَلَ لَكُمْ مِّمَّا خَلَقَ ظِلٰلًا وَّجَعَلَ لَكُمْ مِّنَ الْجِبَالِ اَكْنَانًا وَّجَعَلَ لَكُمْ سَرَابِيْلَ تَقِيْكُمُ الْحَرَّ وَسَرَابِيْلَ تَقِيْكُمْ بَأْسَكُمْ ۚ كَذٰلِكَ يُتِمُّ نِعْمَتَهٗ عَلَيْكُمْ لَعَلَّكُمْ تُسْلِمُوْنَ","bc":"wallāhu ja'ala lakum mimmā khalaqa ẓilālaw wa ja'ala lakum minal-jibāli aknānaw wa ja'ala lakum sarābīla taqīkumul-ḥarra wa sarābīla taqīkum ba`sakum, każālika yutimmu ni'matahụ 'alaikum la'allakum tuslimụn","at":"Dan Allah menjadikan tempat bernaung bagimu dari apa yang telah Dia ciptakan, Dia menjadikan bagimu tempat-tempat tinggal di gunung-gunung, dan Dia menjadikan pakaian bagimu yang memeliharamu dari panas dan pakaian (baju besi) yang memelihara kamu dalam peperangan. Demikian Allah menyempurnakan nikmat-Nya kepadamu agar kamu berserah diri (kepada-Nya). "},{"id":"82","ar":"فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْكَ الْبَلٰغُ الْمُبِيْنُ ","bc":"fa in tawallau fa innamā 'alaikal-balāgul-mubīn","at":"Maka jika mereka berpaling, maka ketahuilah kewajiban yang dibebankan atasmu (Muhammad) hanyalah menyampaikan (amanat Allah) dengan terang."},{"id":"83","ar":"يَعْرِفُوْنَ نِعْمَتَ اللّٰهِ ثُمَّ يُنْكِرُوْنَهَا وَاَكْثَرُهُمُ الْكٰفِرُوْنَ ","bc":"ya'rifụna ni'matallāhi ṡumma yungkirụnahā wa akṡaruhumul-kāfirụn","at":"Mereka mengetahui nikmat Allah, kemudian mereka mengingkarinya dan kebanyakan mereka adalah orang yang ingkar kepada Allah."},{"id":"84","ar":"وَيَوْمَ نَبْعَثُ مِنْ كُلِّ اُمَّةٍ شَهِيْدًا ثُمَّ لَا يُؤْذَنُ لِلَّذِيْنَ كَفَرُوْا وَلَا هُمْ يُسْتَعْتَبُوْنَ ","bc":"wa yauma nab'aṡu ming kulli ummatin syahīdan ṡumma lā yu`żanu lillażīna kafarụ wa lā hum yusta'tabụn","at":"Dan (ingatlah) pada hari (ketika) Kami bangkitkan seorang saksi (rasul) dari setiap umat, kemudian tidak diizinkan kepada orang yang kafir (untuk membela diri) dan tidak (pula) dibolehkan memohon ampunan."},{"id":"85","ar":"وَاِذَا رَاَ الَّذِيْنَ ظَلَمُوا الْعَذَابَ فَلَا يُخَفَّفُ عَنْهُمْ وَلَا هُمْ يُنْظَرُوْنَ","bc":"wa iżā ra`allażīna ẓalamul-'ażāba fa lā yukhaffafu 'an-hum wa lā hum yunẓarụn","at":"Dan apabila orang zalim telah menyaksikan azab, maka mereka tidak mendapat keringanan dan tidak (pula) diberi penangguhan."},{"id":"86","ar":"وَاِذَا رَاَ الَّذِيْنَ اَشْرَكُوْا شُرَكَاۤءَهُمْ قَالُوْا رَبَّنَا هٰٓؤُلَاۤءِ شُرَكَاۤؤُنَا الَّذِيْنَ كُنَّا نَدْعُوْا مِنْ دُوْنِكَۚ فَاَلْقَوْا اِلَيْهِمُ الْقَوْلَ اِنَّكُمْ لَكٰذِبُوْنَۚ ","bc":"wa iżā ra`allażīna asyrakụ syurakā`ahum qālụ rabbanā hā`ulā`i syurakā`unallażīna kunnā nad'ụ min dụnik, fa alqau ilaihimul-qaula innakum lakāżibụn","at":"Dan apabila orang yang mempersekutukan (Allah) melihat sekutu-sekutu mereka, mereka berkata, “Ya Tuhan kami, mereka inilah sekutu-sekutu kami yang dahulu kami sembah selain Engkau.” Lalu sekutu mereka menyatakan kepada mereka, “Kamu benar-benar pendusta.”"},{"id":"87","ar":"وَاَلْقَوْا اِلَى اللّٰهِ يَوْمَىِٕذِ ِۨالسَّلَمَ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ ","bc":"wa alqau ilallāhi yauma`iżinis-salama wa ḍalla 'an-hum mā kānụ yaftarụn","at":"Dan pada hari itu mereka menyatakan tunduk kepada Allah dan lenyaplah segala yang mereka ada-adakan."},{"id":"88","ar":"اَلَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ زِدْنٰهُمْ عَذَابًا فَوْقَ الْعَذَابِ بِمَا كَانُوْا يُفْسِدُوْنَ ","bc":"allażīna kafarụ wa ṣaddụ 'an sabīlillāhi zidnāhum 'ażāban fauqal-'ażābi bimā kānụ yufsidụn","at":"Orang yang kafir dan menghalangi (manusia) dari jalan Allah, Kami tambahkan kepada mereka siksaan demi siksaan disebabkan mereka selalu berbuat kerusakan."},{"id":"89","ar":"وَيَوْمَ نَبْعَثُ فِيْ كُلِّ اُمَّةٍ شَهِيْدًا عَلَيْهِمْ مِّنْ اَنْفُسِهِمْ وَجِئْنَا بِكَ شَهِيْدًا عَلٰى هٰٓؤُلَاۤءِۗ وَنَزَّلْنَا عَلَيْكَ الْكِتٰبَ تِبْيَانًا لِّكُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً وَّبُشْرٰى لِلْمُسْلِمِيْنَ ","bc":"wa yauma nab'aṡu fī kulli ummatin syahīdan 'alaihim min anfusihim wa ji`nā bika syahīdan 'alā hā`ulā`, wa nazzalnā 'alaikal-kitāba tibyānal likulli syai`iw wa hudaw wa raḥmataw wa busyrā lil-muslimīn","at":"Dan (ingatlah) pada hari (ketika) Kami bangkitkan pada setiap umat seorang saksi atas mereka dari mereka sendiri, dan Kami datangkan engkau (Muhammad) menjadi saksi atas mereka. Dan Kami turunkan Kitab (Al-Qur'an) kepadamu untuk menjelaskan segala sesuatu, sebagai petunjuk, serta rahmat dan kabar gembira bagi orang yang berserah diri (Muslim)."},{"id":"90","ar":"۞ اِنَّ اللّٰهَ يَأْمُرُ بِالْعَدْلِ وَالْاِحْسَانِ وَاِيْتَاۤئِ ذِى الْقُرْبٰى وَيَنْهٰى عَنِ الْفَحْشَاۤءِ وَالْمُنْكَرِ وَالْبَغْيِ يَعِظُكُمْ لَعَلَّكُمْ تَذَكَّرُوْنَ ","bc":"innallāha ya`muru bil-'adli wal-iḥsāni wa ītā`i żil-qurbā wa yan-hā 'anil-faḥsyā`i wal-mungkari wal-bagyi ya'iẓukum la'allakum tażakkarụn","at":"Sesungguhnya Allah menyuruh (kamu) berlaku adil dan berbuat kebajikan, memberi bantuan kepada kerabat, dan Dia melarang (melakukan) perbuatan keji, kemungkaran, dan permusuhan. Dia memberi pengajaran kepadamu agar kamu dapat mengambil pelajaran."},{"id":"91","ar":"وَاَوْفُوْا بِعَهْدِ اللّٰهِ اِذَا عَاهَدْتُّمْ وَلَا تَنْقُضُوا الْاَيْمَانَ بَعْدَ تَوْكِيْدِهَا وَقَدْ جَعَلْتُمُ اللّٰهَ عَلَيْكُمْ كَفِيْلًا ۗاِنَّ اللّٰهَ يَعْلَمُ مَا تَفْعَلُوْنَ ","bc":"wa aufụ bi'ahdillāhi iżā 'āhattum wa lā tangquḍul-aimāna ba'da taukīdihā wa qad ja'altumullāha 'alaikum kafīlā, innallāha ya'lamu mā taf'alụn","at":"Dan tepatilah janji dengan Allah apabila kamu berjanji dan janganlah kamu melanggar sumpah, setelah diikrarkan, sedang kamu telah menjadikan Allah sebagai saksimu (terhadap sumpah itu). Sesungguhnya Allah mengetahui apa yang kamu perbuat."},{"id":"92","ar":"وَلَا تَكُوْنُوْا كَالَّتِيْ نَقَضَتْ غَزْلَهَا مِنْۢ بَعْدِ قُوَّةٍ اَنْكَاثًاۗ تَتَّخِذُوْنَ اَيْمَانَكُمْ دَخَلًا ۢ بَيْنَكُمْ اَنْ تَكُوْنَ اُمَّةٌ هِيَ اَرْبٰى مِنْ اُمَّةٍ ۗاِنَّمَا يَبْلُوْكُمُ اللّٰهُ بِهٖۗ وَلَيُبَيِّنَنَّ لَكُمْ يَوْمَ الْقِيٰمَةِ مَا كُنْتُمْ فِيْهِ تَخْتَلِفُوْنَ ","bc":"wa lā takụnụ kallatī naqaḍat gazlahā mim ba'di quwwatin angkāṡā, tattakhiżụna aimānakum dakhalam bainakum an takụna ummatun hiya arbā min ummah, innamā yablụkumullāhu bih, wa layubayyinanna lakum yaumal-qiyāmati mā kuntum fīhi takhtalifụn","at":"Dan janganlah kamu seperti seorang perempuan yang menguraikan benangnya yang sudah dipintal dengan kuat, menjadi cerai berai kembali. Kamu menjadikan sumpah (perjanjian)mu sebagai alat penipu di antaramu, disebabkan adanya satu golongan yang lebih banyak jumlahnya dari golongan yang lain. Allah hanya menguji kamu dengan hal itu, dan pasti pada hari Kiamat akan dijelaskan-Nya kepadamu apa yang dahulu kamu perselisihkan itu."},{"id":"93","ar":"وَلَوْ شَاۤءَ اللّٰهُ لَجَعَلَكُمْ اُمَّةً وَّاحِدَةً وَّلٰكِنْ يُّضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُۗ وَلَتُسْـَٔلُنَّ عَمَّا كُنْتُمْ تَعْمَلُوْنَ ","bc":"walau syā`allāhu laja'alakum ummataw wāḥidataw wa lākiy yuḍillu may yasyā`u wa yahdī may yasyā`, wa latus`alunna 'ammā kuntum ta'malụn","at":"Dan jika Allah menghendaki niscaya Dia menjadikan kamu satu umat (saja), tetapi Dia menyesatkan siapa yang Dia kehendaki dan memberi petunjuk kepada siapa yang Dia kehendaki. Tetapi kamu pasti akan ditanya tentang apa yang telah kamu kerjakan."},{"id":"94","ar":"وَلَا تَتَّخِذُوْٓا اَيْمَانَكُمْ دَخَلًا ۢ بَيْنَكُمْ فَتَزِلَّ قَدَمٌۢ بَعْدَ ثُبُوْتِهَا وَتَذُوْقُوا السُّوْۤءَ بِمَا صَدَدْتُّمْ عَنْ سَبِيْلِ اللّٰهِ ۚوَلَكُمْ عَذَابٌ عَظِيْمٌ ","bc":"wa lā tattakhiżū aimānakum dakhalam bainakum fa tazilla qadamum ba'da ṡubụtihā wa tażụqus-sū`a bimā ṣadattum 'an sabīlillāh, wa lakum 'ażābun 'aẓīm","at":"Dan janganlah kamu jadikan sumpah-sumpahmu sebagai alat penipu di antaramu, yang menyebabkan kaki(mu) tergelincir setelah tegaknya (kukuh), dan kamu akan merasakan keburukan (di dunia) karena kamu menghalangi (manusia) dari jalan Allah, dan kamu akan mandapat azab yang besar."},{"id":"95","ar":"وَلَا تَشْتَرُوْا بِعَهْدِ اللّٰهِ ثَمَنًا قَلِيْلًاۗ اِنَّمَا عِنْدَ اللّٰهِ هُوَ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ","bc":"wa lā tasytarụ bi'ahdillāhi ṡamanang qalīlā, innamā 'indallāhi huwa khairul lakum ing kuntum ta'lamụn","at":"Dan janganlah kamu jual perjanjian (dengan) Allah dengan harga murah, karena sesungguhnya apa yang ada di sisi Allah lebih baik bagimu jika kamu mengetahui."},{"id":"96","ar":"مَا عِنْدَكُمْ يَنْفَدُ وَمَا عِنْدَ اللّٰهِ بَاقٍۗ وَلَنَجْزِيَنَّ الَّذِيْنَ صَبَرُوْٓا اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوْا يَعْمَلُوْنَ ","bc":"mā 'indakum yanfadu wa mā 'indallāhi bāq, wa lanajziyannallażīna ṣabarū ajrahum bi`aḥsani mā kānụ ya'malụn","at":"Apa yang ada di sisimu akan lenyap, dan apa yang ada di sisi Allah adalah kekal. Dan Kami pasti akan memberi balasan kepada orang yang sabar dengan pahala yang lebih baik dari apa yang telah mereka kerjakan."},{"id":"97","ar":"مَنْ عَمِلَ صَالِحًا مِّنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَلَنُحْيِيَنَّهٗ حَيٰوةً طَيِّبَةًۚ وَلَنَجْزِيَنَّهُمْ اَجْرَهُمْ بِاَحْسَنِ مَا كَانُوْا يَعْمَلُوْنَ ","bc":"man 'amila ṣāliḥam min żakarin au unṡā wa huwa mu`minun fa lanuḥyiyannahụ ḥayātan ṭayyibah, wa lanajziyannahum ajrahum bi`aḥsani mā kānụ ya'malụn","at":"Barangsiapa mengerjakan kebajikan, baik laki-laki maupun perempuan dalam keadaan beriman, maka pasti akan Kami berikan kepadanya kehidupan yang baik dan akan Kami beri balasan dengan pahala yang lebih baik dari apa yang telah mereka kerjakan."},{"id":"98","ar":"فَاِذَا قَرَأْتَ الْقُرْاٰنَ فَاسْتَعِذْ بِاللّٰهِ مِنَ الشَّيْطٰنِ الرَّجِيْمِ ","bc":"fa iżā qara`tal-qur`āna fasta'iż billāhi minasy-syaiṭānir-rajīm","at":"Maka apabila engkau (Muhammad) hendak membaca Al-Qur'an, mohonlah perlindungan kepada Allah dari setan yang terkutuk."},{"id":"99","ar":"اِنَّهٗ لَيْسَ لَهٗ سُلْطٰنٌ عَلَى الَّذِيْنَ اٰمَنُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَ ","bc":"innahụ laisa lahụ sulṭānun 'alallażīna āmanụ wa 'alā rabbihim yatawakkalụn","at":"Sungguh, setan itu tidak akan berpengaruh terhadap orang yang beriman dan bertawakal kepada Tuhan."},{"id":"100","ar":"اِنَّمَا سُلْطٰنُهٗ عَلَى الَّذِيْنَ يَتَوَلَّوْنَهٗ وَالَّذِيْنَ هُمْ بِهٖ مُشْرِكُوْنَ","bc":"innamā sulṭānuhụ 'alallażīna yatawallaunahụ wallażīna hum bihī musyrikụn","at":"Pengaruhnya hanyalah terhadap orang yang menjadikannya pemimpin dan terhadap orang yang mempersekutukannya dengan Allah."},{"id":"101","ar":"وَاِذَا بَدَّلْنَآ اٰيَةً مَّكَانَ اٰيَةٍ ۙوَّاللّٰهُ اَعْلَمُ بِمَا يُنَزِّلُ قَالُوْٓا اِنَّمَآ اَنْتَ مُفْتَرٍۗ بَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ ","bc":"wa iżā baddalnā āyatam makāna āyatiw wallāhu a'lamu bimā yunazzilu qālū innamā anta muftar, bal akṡaruhum lā ya'lamụn","at":"Dan apabila Kami mengganti suatu ayat dengan ayat yang lain, dan Allah lebih mengetahui apa yang diturunkan-Nya, mereka berkata, “Sesungguhnya engkau (Muhammad) hanya mengada-ada saja.” Sebenarnya kebanyakan mereka tidak mengetahui."},{"id":"102","ar":"قُلْ نَزَّلَهٗ رُوْحُ الْقُدُسِ مِنْ رَّبِّكَ بِالْحَقِّ لِيُثَبِّتَ الَّذِيْنَ اٰمَنُوْا وَهُدًى وَّبُشْرٰى لِلْمُسْلِمِيْنَ ","bc":"qul nazzalahụ rụḥul-qudusi mir rabbika bil-ḥaqqi liyuṡabbitallażīna āmanụ wa hudaw wa busyrā lil-muslimīn","at":"Katakanlah, “Rohulkudus (Jibril) menurunkan Al-Qur'an itu dari Tuhanmu dengan kebenaran, untuk meneguhkan (hati) orang yang telah beriman, dan menjadi petunjuk serta kabar gembira bagi orang yang berserah diri (kepada Allah).”"},{"id":"103","ar":"وَلَقَدْ نَعْلَمُ اَنَّهُمْ يَقُوْلُوْنَ اِنَّمَا يُعَلِّمُهٗ بَشَرٌۗ لِسَانُ الَّذِيْ يُلْحِدُوْنَ اِلَيْهِ اَعْجَمِيٌّ وَّهٰذَا لِسَانٌ عَرَبِيٌّ مُّبِيْنٌ ","bc":"wa laqad na'lamu annahum yaqụlụna innamā yu'allimuhụ basyar, lisānullażī yul-ḥidụna ilaihi a'jamiyyuw wa hāżā lisānun 'arabiyyum mubīn","at":"Dan sesungguhnya Kami mengetahui bahwa mereka berkata, “Sesungguhnya Al-Qur'an itu hanya diajarkan oleh seorang manusia kepadanya (Muhammad).” Bahasa orang yang mereka tuduhkan (bahwa Muhammad belajar) kepadanya adalah bahasa ‘Ajam, padahal ini (Al-Qur'an) adalah dalam bahasa Arab yang jelas."},{"id":"104","ar":"اِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاٰيٰتِ اللّٰهِۙ لَا يَهْدِيْهِمُ اللّٰهُ وَلَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"innallażīna lā yu`minụna bi`āyātillāhi lā yahdīhimullāhu wa lahum 'ażābun alīm","at":"Sesungguhnya orang yang tidak beriman kepada ayat-ayat Allah (Al-Qur'an), Allah tidak akan memberi petunjuk kepada mereka dan mereka akan mendapat azab yang pedih."},{"id":"105","ar":"اِنَّمَا يَفْتَرِى الْكَذِبَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاٰيٰتِ اللّٰهِۚ وَاُولٰۤىِٕكَ هُمُ الْكٰذِبُوْنَ ","bc":"innamā yaftaril-każiballażīna lā yu`minụna bi`āyātillāh, wa ulā`ika humul-kāżibụn","at":"Sesungguhnya yang mengada-adakan kebohongan, hanyalah orang yang tidak beriman kepada ayat-ayat Allah, dan mereka itulah pembohong."},{"id":"106","ar":"مَنْ كَفَرَ بِاللّٰهِ مِنْۢ بَعْدِ اِيْمَانِهٖٓ اِلَّا مَنْ اُكْرِهَ وَقَلْبُهٗ مُطْمَىِٕنٌّۢ بِالْاِيْمَانِ وَلٰكِنْ مَّنْ شَرَحَ بِالْكُفْرِ صَدْرًا فَعَلَيْهِمْ غَضَبٌ مِّنَ اللّٰهِ ۗوَلَهُمْ عَذَابٌ عَظِيْمٌ ","bc":"mang kafara billāhi mim ba'di īmānihī illā man ukriha wa qalbuhụ muṭma`innum bil-īmāni wa lākim man syaraḥa bil-kufri ṣadran fa 'alaihim gaḍabum minallāh, wa lahum 'ażābun 'aẓīm","at":"Barangsiapa kafir kepada Allah setelah dia beriman (dia mendapat kemurkaan Allah), kecuali orang yang dipaksa kafir padahal hatinya tetap tenang dalam beriman (dia tidak berdosa), tetapi orang yang melapangkan dadanya untuk kekafiran, maka kemurkaan Allah menimpanya dan mereka akan mendapat azab yang besar."},{"id":"107","ar":"ذٰلِكَ بِاَنَّهُمُ اسْتَحَبُّوا الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِۙ وَاَنَّ اللّٰهَ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ ","bc":"żālika bi`annahumustaḥabbul-ḥayātad-dun-yā 'alal-ākhirati wa annallāha lā yahdil-qaumal-kāfirīn","at":"Yang demikian itu disebabkan karena mereka lebih mencintai kehidupan di dunia daripada akhirat, dan Allah tidak memberi petunjuk kepada kaum yang kafir."},{"id":"108","ar":"اُولٰۤىِٕكَ الَّذِيْنَ طَبَعَ اللّٰهُ عَلٰى قُلُوْبِهِمْ وَسَمْعِهِمْ وَاَبْصَارِهِمْۗ وَاُولٰۤىِٕكَ هُمُ الْغٰفِلُوْنَ ","bc":"ulā`ikallażīna ṭaba'allāhu 'alā qulụbihim wa sam'ihim wa abṣārihim, wa ulā`ika humul-gāfilụn","at":"Mereka itulah orang yang hati, pendengaran, dan penglihatannya telah dikunci oleh Allah. Mereka itulah orang yang lalai."},{"id":"109","ar":"لَا جَرَمَ اَنَّهُمْ فِى الْاٰخِرَةِ هُمُ الْخٰسِرُوْنَ ","bc":"lā jarama annahum fil-ākhirati humul-khāsirụn","at":"Pastilah mereka termasuk orang yang rugi di akhirat nanti."},{"id":"110","ar":"ثُمَّ اِنَّ رَبَّكَ لِلَّذِيْنَ هَاجَرُوْا مِنْۢ بَعْدِ مَا فُتِنُوْا ثُمَّ جَاهَدُوْا وَصَبَرُوْاۚ اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ ","bc":"ṡumma inna rabbaka lillażīna hājarụ mim ba'di mā futinụ ṡumma jāhadụ wa ṣabarụ, inna rabbaka mim ba'dihā lagafụrur raḥīm","at":"Kemudian Tuhanmu (pelindung) bagi orang yang berhijrah setelah menderita cobaan, kemudian mereka berjihad dan bersabar, sungguh, Tuhanmu setelah itu benar-benar Maha Pengampun, Maha Penyayang."},{"id":"111","ar":"۞ يَوْمَ تَأْتِيْ كُلُّ نَفْسٍ تُجَادِلُ عَنْ نَّفْسِهَا وَتُوَفّٰى كُلُّ نَفْسٍ مَّا عَمِلَتْ وَهُمْ لَا يُظْلَمُوْنَ ","bc":"yauma ta`tī kullu nafsin tujādilu 'an nafsihā wa tuwaffā kullu nafsim mā 'amilat wa hum lā yuẓlamụn","at":"(Ingatlah) pada hari (ketika) setiap orang datang untuk membela dirinya sendiri dan bagi setiap orang diberi (balasan) penuh sesuai dengan apa yang telah dikerjakannya, dan mereka tidak dizalimi (dirugikan)."},{"id":"112","ar":"وَضَرَبَ اللّٰهُ مَثَلًا قَرْيَةً كَانَتْ اٰمِنَةً مُّطْمَىِٕنَّةً يَّأْتِيْهَا رِزْقُهَا رَغَدًا مِّنْ كُلِّ مَكَانٍ فَكَفَرَتْ بِاَنْعُمِ اللّٰهِ فَاَذَاقَهَا اللّٰهُ لِبَاسَ الْجُوْعِ وَالْخَوْفِ بِمَا كَانُوْا يَصْنَعُوْنَ ","bc":"wa ḍaraballāhu maṡalang qaryatang kānat āminatam muṭma`innatay ya`tīhā rizquhā ragadam ming kulli makānin fa kafarat bi`an'umillāhi fa ażāqahallāhu libāsal-jụ'i wal-khaufi bimā kānụ yaṣna'ụn","at":"Dan Allah telah membuat suatu perumpamaan (dengan) sebuah negeri yang dahulunya aman lagi tenteram, rezeki datang kepadanya melimpah ruah dari segenap tempat, tetapi (pen-duduk)nya mengingkari nikmat-nikmat Allah, karena itu Allah menimpakan kepada mereka bencana kelaparan dan ketakutan, disebabkan apa yang mereka perbuat."},{"id":"113","ar":"وَلَقَدْ جَاۤءَهُمْ رَسُوْلٌ مِّنْهُمْ فَكَذَّبُوْهُ فَاَخَذَهُمُ الْعَذَابُ وَهُمْ ظٰلِمُوْنَ ","bc":"wa laqad jā`ahum rasụlum min-hum fa każżabụhu fa akhażahumul-'ażābu wa hum ẓālimụn","at":"Dan sungguh, telah datang kepada mereka seorang rasul dari (kalangan) mereka sendiri, tetapi mereka mendustakannya, karena itu mereka ditimpa azab dan mereka adalah orang yang zalim."},{"id":"114","ar":"فَكُلُوْا مِمَّا رَزَقَكُمُ اللّٰهُ حَلٰلًا طَيِّبًاۖ وَّاشْكُرُوْا نِعْمَتَ اللّٰهِ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُوْنَ ","bc":"fa kulụ mimmā razaqakumullāhu ḥalālan ṭayyibaw wasykurụ ni'matallāhi ing kuntum iyyāhu ta'budụn","at":"Maka makanlah yang halal lagi baik dari rezeki yang telah diberikan Allah kepadamu; dan syukurilah nikmat Allah, jika kamu hanya menyembah kepada-Nya."},{"id":"115","ar":"اِنَّمَا حَرَّمَ عَلَيْكُمُ الْمَيْتَةَ وَالدَّمَ وَلَحْمَ الْخِنْزِيْرِ وَمَآ اُهِلَّ لِغَيْرِ اللّٰهِ بِهٖۚ فَمَنِ اضْطُرَّ غَيْرَ بَاغٍ وَّلَا عَادٍ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"innamā ḥarrama 'alaikumul-maitata wad-dama wa laḥmal-khinzīri wa mā uḥilla ligairillāhi bih, fa maniḍṭurra gaira bāgiw wa lā 'ādin fa innallāha gafụrur raḥīm","at":"Sesungguhnya Allah hanya mengharamkan atasmu bangkai, darah, daging babi, dan (hewan) yang disembelih dengan (menyebut nama) selain Allah, tetapi barangsiapa terpaksa (memakannya) bukan karena menginginkannya dan tidak (pula) melampaui batas, maka sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"116","ar":"وَلَا تَقُوْلُوْا لِمَا تَصِفُ اَلْسِنَتُكُمُ الْكَذِبَ هٰذَا حَلٰلٌ وَّهٰذَا حَرَامٌ لِّتَفْتَرُوْا عَلَى اللّٰهِ الْكَذِبَۗ اِنَّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُوْنَۗ ","bc":"wa lā taqụlụ limā taṣifu alsinatukumul-każiba hāżā ḥalāluw wa hāżā ḥarāmul litaftarụ 'alallāhil-każib, innallażīna yaftarụna 'alallāhil-każiba lā yufliḥụn","at":"Dan janganlah kamu mengatakan terhadap apa yang disebut-sebut oleh lidahmu secara dusta ”Ini halal dan ini haram,” untuk mengada-adakan kebohongan terhadap Allah. Sesungguhnya orang yang mengada-adakan kebohongan terhadap Allah tidak akan beruntung."},{"id":"117","ar":"مَتَاعٌ قَلِيْلٌ ۖوَّلَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"matā'ung qalīluw wa lahum 'ażābun alīm","at":"(Itu adalah) kesenangan yang sedikit; dan mereka akan mendapat azab yang pedih."},{"id":"118","ar":"وَعَلَى الَّذِيْنَ هَادُوْا حَرَّمْنَا مَا قَصَصْنَا عَلَيْكَ مِنْ قَبْلُ ۗوَمَا ظَلَمْنٰهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ ","bc":"wa 'alallażīna hādụ ḥarramnā mā qaṣaṣnā 'alaika ming qabl, wa mā ẓalamnāhum wa lāking kānū anfusahum yaẓlimụn","at":"Dan terhadap orang Yahudi, Kami haramkan apa yang telah Kami ceritakan dahulu kepadamu (Muhammad). Kami tidak menzalimi mereka, justru merekalah yang menzalimi diri sendiri."},{"id":"119","ar":"ثُمَّ اِنَّ رَبَّكَ لِلَّذِيْنَ عَمِلُوا السُّوْۤءَ بِجَهَالَةٍ ثُمَّ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْٓا اِنَّ رَبَّكَ مِنْۢ بَعْدِهَا لَغَفُوْرٌ رَّحِيْمٌ ","bc":"ṡumma inna rabbaka lillażīna 'amilus-sū`a bijahālatin ṡumma tābụ mim ba'di żālika wa aṣlaḥū inna rabbaka mim ba'dihā lagafụrur raḥīm","at":"Kemudian, sesungguhnya Tuhanmu (mengampuni) orang yang mengerjakan kesalahan karena kebodohannya, kemudian mereka bertobat setelah itu dan memperbaiki (dirinya), sungguh, Tuhanmu setelah itu benar-benar Maha Pengampun, Maha Penyayang."},{"id":"120","ar":"اِنَّ اِبْرٰهِيْمَ كَانَ اُمَّةً قَانِتًا لِّلّٰهِ حَنِيْفًاۗ وَلَمْ يَكُ مِنَ الْمُشْرِكِيْنَۙ","bc":"inna ibrāhīma kāna ummatang qānital lillāhi ḥanīfā, wa lam yaku minal-musyrikīn","at":"Sungguh, Ibrahim adalah seorang imam (yang dapat dijadikan teladan), patuh kepada Allah dan hanif. Dan dia bukanlah termasuk orang musyrik (yang mempersekutukan Allah),"},{"id":"121","ar":"شَاكِرًا لِّاَنْعُمِهِ ۖاجْتَبٰىهُ وَهَدٰىهُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"syākiral li`an'umihijtabāhu wa hadāhu ilā ṣirāṭim mustaqīm","at":"dia mensyukuri nikmat-nikmat-Nya. Allah telah memilihnya dan menunjukinya ke jalan yang lurus."},{"id":"122","ar":"وَاٰتَيْنٰهُ فِى الدُّنْيَا حَسَنَةً ۗوَاِنَّهٗ فِى الْاٰخِرَةِ لَمِنَ الصّٰلِحِيْنَ ۗ ","bc":"wa ātaināhu fid-dun-yā ḥasanah, wa innahụ fil-ākhirati laminaṣ-ṣāliḥīn","at":"Dan Kami berikan kepadanya kebaikan di dunia, dan sesungguhnya di akhirat dia termasuk orang yang saleh."},{"id":"123","ar":"ثُمَّ اَوْحَيْنَآ اِلَيْكَ اَنِ اتَّبِعْ مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًا ۗوَمَا كَانَ مِنَ الْمُشْرِكِيْنَ ","bc":"ṡumma auḥainā ilaika anittabi' millata ibrāhīma ḥanīfā, wa mā kāna minal-musyrikīn","at":"Kemudian Kami wahyukan kepadamu (Muhammad), “Ikutilah agama Ibrahim yang lurus, dan dia bukanlah termasuk orang musyrik.”"},{"id":"124","ar":"اِنَّمَا جُعِلَ السَّبْتُ عَلَى الَّذِيْنَ اخْتَلَفُوْا فِيْهِۗ وَاِنَّ رَبَّكَ لَيَحْكُمُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ ","bc":"innamā ju'ilas-sabtu 'alallażīnakhtalafụ fīh, wa inna rabbaka layaḥkumu bainahum yaumal-qiyāmati fīmā kānụ fīhi yakhtalifụn","at":"Sesungguhnya (menghormati) hari Sabtu hanya diwajibkan atas orang (Yahudi) yang memperselisihkannya. Dan sesungguhnya Tuhanmu pasti akan memberi keputusan di antara mereka pada hari Kiamat terhadap apa yang telah mereka perselisihkan itu."},{"id":"125","ar":"اُدْعُ اِلٰى سَبِيْلِ رَبِّكَ بِالْحِكْمَةِ وَالْمَوْعِظَةِ الْحَسَنَةِ وَجَادِلْهُمْ بِالَّتِيْ هِيَ اَحْسَنُۗ اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيْلِهٖ وَهُوَ اَعْلَمُ بِالْمُهْتَدِيْنَ ","bc":"ud'u ilā sabīli rabbika bil-ḥikmati wal-mau'iẓatil-ḥasanati wa jādil-hum billatī hiya aḥsan, inna rabbaka huwa a'lamu biman ḍalla 'an sabīlihī wa huwa a'lamu bil-muhtadīn","at":"Serulah (manusia) kepada jalan Tuhanmu dengan hikmah dan pengajaran yang baik, dan berdebatlah dengan mereka dengan cara yang baik. Sesungguhnya Tuhanmu, Dialah yang lebih mengetahui siapa yang sesat dari jalan-Nya dan Dialah yang lebih mengetahui siapa yang mendapat petunjuk."},{"id":"126","ar":"وَاِنْ عَاقَبْتُمْ فَعَاقِبُوْا بِمِثْلِ مَا عُوْقِبْتُمْ بِهٖۗ وَلَىِٕنْ صَبَرْتُمْ لَهُوَ خَيْرٌ لِّلصّٰبِرِيْنَ ","bc":"wa in 'āqabtum fa 'āqibụ bimiṡli mā 'ụqibtum bih, wa la`in ṣabartum lahuwa khairul liṣ-ṣābirīn","at":"Dan jika kamu membalas, maka balaslah dengan (balasan) yang sama dengan siksaan yang ditimpakan kepadamu. Tetapi jika kamu bersabar, sesungguhnya itulah yang lebih baik bagi orang yang sabar."},{"id":"127","ar":"وَاصْبِرْ وَمَا صَبْرُكَ اِلَّا بِاللّٰهِ وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُ فِيْ ضَيْقٍ مِّمَّا يَمْكُرُوْنَ ","bc":"waṣbir wa mā ṣabruka illā billāhi wa lā taḥzan 'alaihim wa lā taku fī ḍaiqim mimmā yamkurụn","at":"Dan bersabarlah (Muhammad) dan kesabaranmu itu semata-mata dengan pertolongan Allah dan janganlah engkau bersedih hati terhadap (kekafiran) mereka dan jangan (pula) bersempit dada terhadap tipu daya yang mereka rencanakan."},{"id":"128","ar":"اِنَّ اللّٰهَ مَعَ الَّذِيْنَ اتَّقَوْا وَّالَّذِيْنَ هُمْ مُّحْسِنُوْنَ ۔","bc":"innallāha ma'allażīnattaqaw wallażīna hum muḥsinụn","at":"Sungguh, Allah beserta orang-orang yang bertakwa dan orang-orang yang berbuat kebaikan."}],an_najm=[{"id":"1","ar":"وَالنَّجْمِ اِذَا هَوٰىۙ ","bc":"wan-najmi iżā hawā","at":"Demi bintang ketika terbenam, "},{"id":"2","ar":"مَا ضَلَّ صَاحِبُكُمْ وَمَا غَوٰىۚ ","bc":"mā ḍalla ṣāḥibukum wa mā gawā","at":"kawanmu (Muhammad) tidak sesat dan tidak (pula) keliru,"},{"id":"3","ar":"وَمَا يَنْطِقُ عَنِ الْهَوٰى","bc":"wa mā yanṭiqu 'anil-hawā","at":"dan tidaklah yang diucapkannya itu (Al-Qur'an) menurut keinginannya. "},{"id":"4","ar":"اِنْ هُوَ اِلَّا وَحْيٌ يُّوْحٰىۙ ","bc":"in huwa illā waḥyuy yụḥā","at":"Tidak lain (Al-Qur'an itu) adalah wahyu yang diwahyukan (kepadanya),"},{"id":"5","ar":"عَلَّمَهٗ شَدِيْدُ الْقُوٰىۙ ","bc":"'allamahụ syadīdul-quwā","at":"yang diajarkan kepadanya oleh (Jibril) yang sangat kuat, "},{"id":"6","ar":"ذُوْ مِرَّةٍۗ فَاسْتَوٰىۙ ","bc":"żụ mirrah, fastawā","at":"yang mempunyai keteguhan; maka (Jibril itu) menampakkan diri dengan rupa yang asli (rupa yang bagus dan perkasa)"},{"id":"7","ar":"وَهُوَ بِالْاُفُقِ الْاَعْلٰىۗ ","bc":"wa huwa bil-ufuqil-a'lā","at":"Sedang dia berada di ufuk yang tinggi."},{"id":"8","ar":"ثُمَّ دَنَا فَتَدَلّٰىۙ ","bc":"ṡumma danā fa tadallā","at":"Kemudian dia mendekat (pada Muhammad), lalu bertambah dekat, "},{"id":"9","ar":"فَكَانَ قَابَ قَوْسَيْنِ اَوْ اَدْنٰىۚ","bc":"fa kāna qāba qausaini au adnā","at":"sehingga jaraknya (sekitar) dua busur panah atau lebih dekat (lagi)."},{"id":"10","ar":"فَاَوْحٰىٓ اِلٰى عَبْدِهٖ مَآ اَوْحٰىۗ","bc":"fa auḥā ilā 'abdihī mā auḥā","at":"Lalu disampaikannya wahyu kepada hamba-Nya (Muhammad) apa yang telah diwahyukan Allah. "},{"id":"11","ar":"مَا كَذَبَ الْفُؤَادُ مَا رَاٰى","bc":"mā każabal-fu`ādu mā ra`ā","at":"Hatinya tidak mendustakan apa yang telah dilihatnya. "},{"id":"12","ar":"اَفَتُمٰرُوْنَهٗ عَلٰى مَا يَرٰى ","bc":"a fa tumārụnahụ 'alā mā yarā","at":"Maka apakah kamu (musyrikin Mekah) hendak membantahnya tentang apa yang dilihatnya itu?"},{"id":"13","ar":"وَلَقَدْ رَاٰهُ نَزْلَةً اُخْرٰىۙ","bc":"wa laqad ra`āhu nazlatan ukhrā","at":"Dan sungguh, dia (Muhammad) telah melihatnya (dalam rupanya yang asli) pada waktu yang lain,"},{"id":"14","ar":"عِنْدَ سِدْرَةِ الْمُنْتَهٰى ","bc":"'inda sidratil-muntahā","at":"(yaitu) di Sidratul Muntaha,"},{"id":"15","ar":"عِنْدَهَا جَنَّةُ الْمَأْوٰىۗ ","bc":"'indahā jannatul-ma`wā","at":"di dekatnya ada surga tempat tinggal,"},{"id":"16","ar":"اِذْ يَغْشَى السِّدْرَةَ مَا يَغْشٰىۙ ","bc":"iż yagsyas-sidrata mā yagsyā","at":"(Muhammad melihat Jibril) ketika Sidratil muntaha diliputi oleh sesuatu yang meliputinya,"},{"id":"17","ar":"مَا زَاغَ الْبَصَرُ وَمَا طَغٰى","bc":"mā zāgal-baṣaru wa mā ṭagā","at":"penglihatannya (Muhammad) tidak menyimpang dari yang dilihatnya itu dan tidak (pula) melampauinya."},{"id":"18","ar":"لَقَدْ رَاٰى مِنْ اٰيٰتِ رَبِّهِ الْكُبْرٰى","bc":"laqad ra`ā min āyāti rabbihil-kubrā","at":"Sungguh, dia telah melihat sebagian tanda-tanda (kebesaran) Tuhannya yang paling besar. "},{"id":"19","ar":"اَفَرَءَيْتُمُ اللّٰتَ وَالْعُزّٰى","bc":"a fa ra`aitumul-lāta wal-'uzzā","at":"Maka apakah patut kamu (orang-orang musyrik) menganggap (berhala) Al-Lata dan Al-‘Uzza,"},{"id":"20","ar":"وَمَنٰوةَ الثَّالِثَةَ الْاُخْرٰى ","bc":"wa manātaṡ-ṡāliṡatal-ukhrā","at":"dan Manat, yang ketiga (yang) kemudian (sebagai anak perempuan Allah)."},{"id":"21","ar":"اَلَكُمُ الذَّكَرُ وَلَهُ الْاُنْثٰى ","bc":"a lakumuż-żakaru wa lahul-unṡā","at":"Apakah (pantas) untuk kamu yang laki-laki dan untuk-Nya yang perempuan?"},{"id":"22","ar":"تِلْكَ اِذًا قِسْمَةٌ ضِيْزٰى ","bc":"tilka iżang qismatun ḍīzā","at":"Yang demikian itu tentulah suatu pembagian yang tidak adil. "},{"id":"23","ar":"اِنْ هِيَ اِلَّآ اَسْمَاۤءٌ سَمَّيْتُمُوْهَآ اَنْتُمْ وَاٰبَاۤؤُكُمْ مَّآ اَنْزَلَ اللّٰهُ بِهَا مِنْ سُلْطٰنٍۗ اِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَمَا تَهْوَى الْاَنْفُسُۚ وَلَقَدْ جَاۤءَهُمْ مِّنْ رَّبِّهِمُ الْهُدٰىۗ","bc":"in hiya illā asmā`un sammaitumụhā antum wa ābā`ukum mā anzalallāhu bihā min sulṭān, iy yattabi'ụna illaẓ-ẓanna wa mā tahwal-anfus, wa laqad jā`ahum mir rabbihimul-hudā","at":"Itu tidak lain hanyalah nama-nama yang kamu dan nenek moyangmu mengada-adakannya; Allah tidak menurunkan suatu keterangan apa pun untuk (menyembah)nya. Mereka hanya mengikuti dugaan, dan apa yang diingini oleh keinginannya. Padahal sungguh, telah datang petunjuk dari Tuhan mereka. "},{"id":"24","ar":"اَمْ لِلْاِنْسَانِ مَا تَمَنّٰىۖ ","bc":"am lil-insāni mā tamannā","at":"Atau apakah manusia akan mendapat segala yang dicita-citakannya?"},{"id":"25","ar":"فَلِلّٰهِ الْاٰخِرَةُ وَالْاُوْلٰى ","bc":"fa lillāhil-ākhiratu wal-ụlā","at":"(Tidak!) Maka milik Allah-lah kehidupan akhirat dan kehidupan dunia. "},{"id":"26","ar":"وَكَمْ مِّنْ مَّلَكٍ فِى السَّمٰوٰتِ لَا تُغْنِيْ شَفَاعَتُهُمْ شَيْـًٔا اِلَّا مِنْۢ بَعْدِ اَنْ يَّأْذَنَ اللّٰهُ لِمَنْ يَّشَاۤءُ وَيَرْضٰى ","bc":"wa kam mim malakin fis-samāwāti lā tugnī syafā'atuhum syai`an illā mim ba'di ay ya`żanallāhu limay yasyā`u wa yarḍā","at":"Dan betapa banyak malaikat di langit, syafaat (pertolongan) mereka sedikit pun tidak berguna kecuali apabila Allah telah mengizinkan (dan hanya) bagi siapa yang Dia kehendaki dan Dia ridai. "},{"id":"27","ar":"اِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ لَيُسَمُّوْنَ الْمَلٰۤىِٕكَةَ تَسْمِيَةَ الْاُنْثٰى ","bc":"innallażīna lā yu`minụna bil-ākhirati layusammụnal-malā`ikata tasmiyatal-unṡā","at":"Sesungguhnya orang-orang yang tidak beriman kepada kehidupan akhirat, sungguh mereka menamakan para malaikat dengan nama perempuan. "},{"id":"28","ar":"وَمَا لَهُمْ بِهٖ مِنْ عِلْمٍۗ اِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنَّ الظَّنَّ لَا يُغْنِيْ مِنَ الْحَقِّ شَيْـًٔاۚ ","bc":"wa mā lahum bihī min 'ilm, iy yattabi'ụna illaẓ-ẓanna wa innaẓ-ẓanna lā yugnī minal-ḥaqqi syai`ā","at":"Dan mereka tidak mempunyai ilmu tentang itu. Mereka tidak lain hanyalah mengikuti dugaan, dan sesungguhnya dugaan itu tidak berfaedah sedikit pun terhadap kebenaran. "},{"id":"29","ar":"فَاَعْرِضْ عَنْ مَّنْ تَوَلّٰىۙ عَنْ ذِكْرِنَا وَلَمْ يُرِدْ اِلَّا الْحَيٰوةَ الدُّنْيَاۗ ","bc":"fa a'riḍ 'am man tawallā 'an żikrinā wa lam yurid illal-ḥayātad-dun-yā","at":"Maka tinggalkanlah (Muhammad) orang yang berpaling dari peringatan Kami, dan dia hanya mengingini kehidupan dunia."},{"id":"30","ar":"ذٰلِكَ مَبْلَغُهُمْ مِّنَ الْعِلْمِۗ اِنَّ رَبَّكَ هُوَ اَعْلَمُ بِمَنْ ضَلَّ عَنْ سَبِيْلِهٖۙ وَهُوَ اَعْلَمُ بِمَنِ اهْتَدٰى","bc":"żālika mablaguhum minal-'ilm, inna rabbaka huwa a'lamu biman ḍalla 'an sabīlihī wa huwa a'lamu bimanihtadā","at":"Itulah kadar ilmu mereka. Sungguh, Tuhanmu, Dia lebih mengetahui siapa yang tersesat dari jalan-Nya dan Dia pula yang mengetahui siapa yang mendapat petunjuk. "},{"id":"31","ar":"وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ لِيَجْزِيَ الَّذِيْنَ اَسَاۤءُوْا بِمَا عَمِلُوْا وَيَجْزِيَ الَّذِيْنَ اَحْسَنُوْا بِالْحُسْنٰىۚ ","bc":"wa lillāhi mā fis-samāwāti wa mā fil-arḍ, liyajziyallażīna asā`ụ bimā 'amilụ wa yajziyallażīna aḥsanụ bil-ḥusnā","at":"Dan milik Allah-lah apa yang ada di langit dan apa yang ada di bumi. (Dengan demikian) Dia akan memberi balasan kepada orang-orang yang berbuat jahat sesuai dengan apa yang telah mereka kerjakan dan Dia akan memberi balasan kepada orang-orang yang berbuat baik dengan pahala yang lebih baik (surga). "},{"id":"32","ar":"اَلَّذِيْنَ يَجْتَنِبُوْنَ كَبٰۤىِٕرَ الْاِثْمِ وَالْفَوَاحِشَ اِلَّا اللَّمَمَۙ اِنَّ رَبَّكَ وَاسِعُ الْمَغْفِرَةِۗ هُوَ اَعْلَمُ بِكُمْ اِذْ اَنْشَاَكُمْ مِّنَ الْاَرْضِ وَاِذْ اَنْتُمْ اَجِنَّةٌ فِيْ بُطُوْنِ اُمَّهٰتِكُمْۗ فَلَا تُزَكُّوْٓا اَنْفُسَكُمْۗ هُوَ اَعْلَمُ بِمَنِ اتَّقٰى ","bc":"allażīna yajtanibụna kabā`iral-iṡmi wal-fawāḥisya illal-lamama inna rabbaka wāsi'ul-magfirah, huwa a'lamu bikum iż ansya`akum minal-arḍi wa iż antum ajinnatun fī buṭụni ummahātikum, fa lā tuzakkū anfusakum, huwa a'lamu bimanittaqā","at":"Yaitu) mereka yang menjauhi dosa-dosa besar dan perbuatan keji, kecuali kesalahan-kesalahan kecil. Sungguh, Tuhanmu Mahaluas ampunan-Nya. Dia mengetahui tentang kamu, sejak Dia menjadikan kamu dari tanah lalu ketika kamu masih janin dalam perut ibumu. Maka janganlah kamu menganggap dirimu suci. Dia mengetahui tentang orang yang bertakwa. "},{"id":"33","ar":"اَفَرَءَيْتَ الَّذِيْ تَوَلّٰىۙ ","bc":"a fa ra`aitallażī tawallā","at":"Maka tidakkah engkau melihat orang yang berpaling (dari Al-Qur'an)?"},{"id":"34","ar":"وَاَعْطٰى قَلِيْلًا وَّاَكْدٰى ","bc":"wa a'ṭā qalīlaw wa akdā","at":"dan dia memberikan sedikit (dari apa yang dijanjikan) lalu menahan sisanya."},{"id":"35","ar":"اَعِنْدَهٗ عِلْمُ الْغَيْبِ فَهُوَ يَرٰى ","bc":"a 'indahụ 'ilmul-gaibi fa huwa yarā","at":"Apakah dia mempunyai ilmu tentang yang gaib sehingga dia dapat melihat(nya)?"},{"id":"36","ar":"اَمْ لَمْ يُنَبَّأْ بِمَا فِيْ صُحُفِ مُوْسٰى ","bc":"am lam yunabba` bimā fī ṣuḥufi mụsā","at":"Ataukah belum diberitakan (kepadanya) apa yang ada dalam lembaran-lembaran (Kitab Suci yang diturunkan kepada) Musa?"},{"id":"37","ar":"وَاِبْرٰهِيْمَ الَّذِيْ وَفّٰىٓ ۙ ","bc":"wa ibrāhīmallażī waffā","at":"Dan (lembaran-lembaran) Ibrahim yang selalu menyempurnakan janji?"},{"id":"38","ar":"اَلَّا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۙ ","bc":"allā taziru wāziratuw wizra ukhrā","at":"(yaitu) bahwa seseorang yang berdosa tidak akan memikul dosa orang lain,"},{"id":"39","ar":"وَاَنْ لَّيْسَ لِلْاِنْسَانِ اِلَّا مَا سَعٰىۙ ","bc":"wa al laisa lil-insāni illā mā sa'ā","at":"dan bahwa manusia hanya memperoleh apa yang telah diusahakannya,"},{"id":"40","ar":"وَاَنَّ سَعْيَهٗ سَوْفَ يُرٰىۖ","bc":"wa anna sa'yahụ saufa yurā","at":"dan sesungguhnya usahanya itu kelak akan diperlihatkan (kepadanya),"},{"id":"41","ar":"ثُمَّ يُجْزٰىهُ الْجَزَاۤءَ الْاَوْفٰىۙ ","bc":"ṡumma yujzāhul-jazā`al-aufā","at":"kemudian akan diberi balasan kepadanya dengan balasan yang paling sempurna,"},{"id":"42","ar":"وَاَنَّ اِلٰى رَبِّكَ الْمُنْتَهٰىۙ ","bc":"wa anna ilā rabbikal-muntahā","at":"dan sesungguhnya kepada Tuhanmulah kesudahannya (segala sesuatu),"},{"id":"43","ar":"وَاَنَّهٗ هُوَ اَضْحَكَ وَاَبْكٰى","bc":"wa annahụ huwa aḍ-ḥaka wa abkā","at":"dan sesungguhnya Dialah yang menjadikan orang tertawa dan menangis, "},{"id":"44","ar":"وَاَنَّهٗ هُوَ اَمَاتَ وَاَحْيَاۙ ","bc":"wa annahụ huwa amāta wa aḥyā","at":"dan sesungguhnya Dialah yang mematikan dan menghidupkan, "},{"id":"45","ar":"وَاَنَّهٗ خَلَقَ الزَّوْجَيْنِ الذَّكَرَ وَالْاُنْثٰى ","bc":"wa annahụ khalaqaz-zaujainiż-żakara wal-unṡā","at":"dan sesungguhnya Dialah yang men-ciptakan pasangan laki-laki dan perempuan,"},{"id":"46","ar":"مِنْ نُّطْفَةٍ اِذَا تُمْنٰىۙ ","bc":"min nuṭfatin iżā tumnā","at":"dari mani, apabila dipancarkan,"},{"id":"47","ar":"وَاَنَّ عَلَيْهِ النَّشْاَةَ الْاُخْرٰىۙ ","bc":"wa anna 'alaihin-nasy`atal-ukhrā","at":"dan sesungguhnya Dialah yang menetapkan penciptaan yang lain (kebangkitan setelah mati),"},{"id":"48","ar":"وَاَنَّهٗ هُوَ اَغْنٰى وَاَقْنٰىۙ ","bc":"wa annahụ huwa agnā wa aqnā","at":"dan sesungguhnya Dialah yang memberikan kekayaan dan kecukupan. "},{"id":"49","ar":"وَاَنَّهٗ هُوَ رَبُّ الشِّعْرٰىۙ","bc":"wa annahụ huwa rabbusy-syi'rā","at":"dan sesungguhnya Dialah Tuhan (yang memiliki) bintang Syi‘ra, "},{"id":"50","ar":"وَاَنَّهٗٓ اَهْلَكَ عَادًا ۨالْاُوْلٰىۙ","bc":"wa annahū ahlaka 'ādanil-ụlā","at":"dan sesungguhnya Dialah yang telah membinasakan kaum ‘Ad dahulu kala, "},{"id":"51","ar":"وَثَمُوْدَا۟ فَمَآ اَبْقٰىۙ","bc":"wa ṡamụda fa mā abqā","at":"dan kaum Samud, tidak seorang pun yang ditinggalkan-Nya (hidup), "},{"id":"52","ar":"وَقَوْمَ نُوْحٍ مِّنْ قَبْلُۗ اِنَّهُمْ كَانُوْا هُمْ اَظْلَمَ وَاَطْغٰىۗ ","bc":"wa qauma nụḥim ming qabl, innahum kānụ hum aẓlama wa aṭgā","at":"dan (juga) kaum Nuh sebelum itu. Sungguh, mereka adalah orang-orang yang paling zalim dan paling durhaka."},{"id":"53","ar":"وَالْمُؤْتَفِكَةَ اَهْوٰىۙ ","bc":"wal-mu`tafikata ahwā","at":"Dan prahara angin telah meruntuhkan (negeri kaum Lut), "},{"id":"54","ar":"فَغَشّٰىهَا مَا غَشّٰىۚ ","bc":"fa gasysyāhā mā gasysyā","at":"lalu menimbuni negeri itu (sebagai azab) dengan (puing-puing) yang menimpanya. "},{"id":"55","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكَ تَتَمَارٰى","bc":"fa bi`ayyi ālā`i rabbika tatamārā","at":"Maka terhadap nikmat Tuhanmu yang manakah yang masih kamu ragukan?"},{"id":"56","ar":"هٰذَا نَذِيْرٌ مِّنَ النُّذُرِ الْاُوْلٰى","bc":"hāżā nażīrum minan-nużuril-ụlā","at":"Ini (Muhammad) salah seorang pemberi peringatan di antara para pemberi peringatan yang telah terdahulu. "},{"id":"57","ar":"اَزِفَتِ الْاٰزِفةُ ۚ ","bc":"azifatil-āzifah","at":"Yang dekat (hari Kiamat) telah makin mendekat. "},{"id":"58","ar":"لَيْسَ لَهَا مِنْ دُوْنِ اللّٰهِ كَاشِفَةٌ ۗ ","bc":"laisa lahā min dụnillāhi kāsyifah","at":"Tidak ada yang akan dapat mengungkapkan (terjadinya hari itu) selain Allah."},{"id":"59","ar":"اَفَمِنْ هٰذَا الْحَدِيْثِ تَعْجَبُوْنَۙ ","bc":"a fa min hāżal-ḥadīṡi ta'jabụn","at":"Maka apakah kamu merasa heran terhadap pemberitaan ini?"},{"id":"60","ar":"وَتَضْحَكُوْنَ وَلَا تَبْكُوْنَۙ ","bc":"wa taḍ-ḥakụna wa lā tabkụn","at":"dan kamu tertawakan dan tidak menangis, "},{"id":"61","ar":"وَاَنْتُمْ سَامِدُوْنَ ","bc":"wa antum sāmidụn","at":"sedang kamu lengah (darinya)."},{"id":"62","ar":"فَاسْجُدُوْا لِلّٰهِ وَاعْبُدُوْا ۩ ","bc":"fasjudụ lillāhi wa'budụ","at":"Maka bersujudlah kepada Allah dan sembahlah (Dia). "}],an_naml=[{"id":"1","ar":"طٰسۤ ۚ تِلْكَ اٰيٰتُ الْقُرْاٰنِ وَكِتَابٍ مُّبِيْنٍ ۙ ","bc":"ṭā sīn, tilka āyātul-qur`āni wa kitābim mubīn","at":"Tha Sin. Inilah ayat-ayat Al-Qur'an, dan Kitab yang jelas,"},{"id":"2","ar":"هُدًى وَّبُشْرٰى لِلْمُؤْمِنِيْنَ ۙ ","bc":"hudaw wa busyrā lil-mu`minīn","at":"petunjuk dan berita gembira bagi orang-orang yang beriman,"},{"id":"3","ar":"الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَ ","bc":"allażīna yuqīmụnaṣ-ṣalāta wa yu`tụnaz-zakāta wa hum bil-ākhirati hum yụqinụn","at":"(yaitu) orang-orang yang melaksanakan salat dan menunaikan zakat, dan mereka meyakini adanya akhirat."},{"id":"4","ar":"اِنَّ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ زَيَّنَّا لَهُمْ اَعْمَالَهُمْ فَهُمْ يَعْمَهُوْنَ ۗ ","bc":"innallażīna lā yu`minụna bil-ākhirati zayyannā lahum a'mālahum fa hum ya'mahụn","at":"Sesungguhnya orang-orang yang tidak beriman kepada akhirat, Kami jadikan terasa indah bagi mereka perbuatan-perbuatan mereka (yang buruk), sehingga mereka bergelimang dalam kesesatan."},{"id":"5","ar":"اُولٰۤىِٕكَ الَّذِيْنَ لَهُمْ سُوْۤءُ الْعَذَابِ وَهُمْ فِى الْاٰخِرَةِ هُمُ الْاَخْسَرُوْنَ ","bc":"ulā`ikallażīna lahum sū`ul-'ażābi wa hum fil-ākhirati humul-akhsarụn","at":"Mereka itulah orang-orang yang akan mendapat siksaan buruk (di dunia) dan mereka di akhirat adalah orang-orang yang paling rugi."},{"id":"6","ar":"وَاِنَّكَ لَتُلَقَّى الْقُرْاٰنَ مِنْ لَّدُنْ حَكِيْمٍ عَلِيْمٍ ","bc":"wa innaka latulaqqal-qur`āna mil ladun ḥakīmin 'alīm","at":"Dan sesungguhnya engkau (Muhammad) benar-benar telah diberi Al-Qur'an dari sisi (Allah) Yang Mahabijaksana, Maha Mengetahui."},{"id":"7","ar":"اِذْ قَالَ مُوْسٰى لِاَهْلِهٖٓ اِنِّيْٓ اٰنَسْتُ نَارًاۗ سَاٰتِيْكُمْ مِّنْهَا بِخَبَرٍ اَوْ اٰتِيْكُمْ بِشِهَابٍ قَبَسٍ لَّعَلَّكُمْ تَصْطَلُوْنَ ","bc":"iż qāla mụsā li`ahlihī innī ānastu nārā, sa`ātīkum min-hā bikhabarin au ātīkum bisyihābing qabasil la'allakum taṣṭalụn","at":"(Ingatlah) ketika Musa berkata kepada keluarganya, “Sungguh, aku melihat api. Aku akan membawa kabar tentang itu kepadamu, atau aku akan membawa suluh api (obor) kepadamu agar kamu dapat berdiang (menghangatkan badan dekat api).”"},{"id":"8","ar":"فَلَمَّا جَاۤءَهَا نُوْدِيَ اَنْۢ بُوْرِكَ مَنْ فِى النَّارِ وَمَنْ حَوْلَهَاۗ وَسُبْحٰنَ اللّٰهِ رَبِّ الْعٰلَمِيْنَ ","bc":"fa lammā jā`ahā nụdiya am bụrika man fin-nāri wa man ḥaulahā, wa sub-ḥānallāhi rabbil-'ālamīn","at":"Maka ketika dia tiba di sana (tempat api itu), dia diseru, “Telah diberkahi orang-orang yang berada di dekat api, dan orang-orang yang berada di sekitarnya. Mahasuci Allah, Tuhan seluruh alam.”"},{"id":"9","ar":"يٰمُوْسٰٓى اِنَّهٗٓ اَنَا اللّٰهُ الْعَزِيْزُ الْحَكِيْمُ ۙ ","bc":"yā mụsā innahū anallāhul-'azīzul-ḥakīm","at":"(Allah berfirman), “Wahai Musa! Sesungguhnya Aku adalah Allah, Yang Mahaperkasa, Mahabijaksana."},{"id":"10","ar":"وَاَلْقِ عَصَاكَ ۗفَلَمَّا رَاٰهَا تَهْتَزُّ كَاَنَّهَا جَاۤنٌّ وَّلّٰى مُدْبِرًا وَّلَمْ يُعَقِّبْۗ يٰمُوْسٰى لَا تَخَفْۗ اِنِّيْ لَا يَخَافُ لَدَيَّ الْمُرْسَلُوْنَ ۖ ","bc":"wa alqi 'aṣāk, fa lammā ra`āhā tahtazzu ka`annahā jānnuw wallā mudbiraw wa lam yu'aqqib, yā mụsā lā takhaf, innī lā yakhāfu ladayyal-mursalụn","at":"Dan lemparkanlah tongkatmu!” Maka ketika (tongkat itu menjadi ular dan) Musa melihatnya bergerak-gerak seperti seekor ular yang gesit, larilah dia berbalik ke belakang tanpa menoleh. ”Wahai Musa! Jangan takut! Sesungguhnya di hadapan-Ku, para rasul tidak perlu takut,"},{"id":"11","ar":"اِلَّا مَنْ ظَلَمَ ثُمَّ بَدَّلَ حُسْنًاۢ بَعْدَ سُوْۤءٍ فَاِنِّيْ غَفُوْرٌ رَّحِيْمٌ ","bc":"illā man ẓalama ṡumma baddala ḥusnam ba'da sū`in fa innī gafụrur raḥīm","at":"kecuali orang yang berlaku zalim yang kemudian mengubah (dirinya) dengan kebaikan setelah kejahatan (bertobat); maka sungguh, Aku Maha Pengampun, Maha Penyayang."},{"id":"12","ar":"وَاَدْخِلْ يَدَكَ فِيْ جَيْبِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍۙ فِيْ تِسْعِ اٰيٰتٍ اِلٰى فِرْعَوْنَ وَقَوْمِهٖۚ اِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ ","bc":"wa adkhil yadaka fī jaibika takhruj baiḍā`a min gairi sū`in fī tis'i āyātin ilā fir'auna wa qaumih, innahum kānụ qauman fāsiqīn","at":"Dan masukkanlah tanganmu ke leher bajumu, niscaya ia akan keluar menjadi putih (bersinar) tanpa cacat. (Kedua mukjizat ini) termasuk sembilan macam mukjizat (yang akan dikemukakan) kepada Fir‘aun dan kaumnya. Mereka benar-benar orang-orang yang fasik.”"},{"id":"13","ar":"فَلَمَّا جَاۤءَتْهُمْ اٰيٰتُنَا مُبْصِرَةً قَالُوْا هٰذَا سِحْرٌ مُّبِيْنٌ ۚ ","bc":"fa lammā jā`at-hum āyātunā mubṣiratang qālụ hāżā siḥrum mubīn","at":"Maka ketika mukjizat-mukjizat Kami yang terang itu sampai kepada mereka, mereka berkata, “Ini sihir yang nyata.”"},{"id":"14","ar":"وَجَحَدُوْا بِهَا وَاسْتَيْقَنَتْهَآ اَنْفُسُهُمْ ظُلْمًا وَّعُلُوًّاۗ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِيْنَ ","bc":"wa jaḥadụ bihā wastaiqanat-hā anfusuhum ẓulmaw wa 'uluwwā, fanẓur kaifa kāna 'āqibatul-mufsidīn","at":"Dan mereka mengingkarinya karena kezaliman dan kesombongannya, padahal hati mereka meyakini (kebenaran)nya. Maka perhatikanlah bagaimana kesudahan orang-orang yang berbuat kerusakan."},{"id":"15","ar":"وَلَقَدْ اٰتَيْنَا دَاوٗدَ وَسُلَيْمٰنَ عِلْمًاۗ وَقَالَا الْحَمْدُ لِلّٰهِ الَّذِيْ فَضَّلَنَا عَلٰى كَثِيْرٍ مِّنْ عِبَادِهِ الْمُؤْمِنِيْنَ ","bc":"wa laqad ātainā dāwụda wa sulaimāna 'ilmā, wa qālal-ḥamdu lillāhillażī faḍḍalanā 'alā kaṡīrim min 'ibādihil-mu`minīn","at":"Dan sungguh, Kami telah memberikan ilmu kepada Dawud dan Sulaiman; dan keduanya berkata, “Segala puji bagi Allah yang melebihkan kami dari banyak hamba-hamba-Nya yang beriman.”"},{"id":"16","ar":"وَوَرِثَ سُلَيْمٰنُ دَاوٗدَ وَقَالَ يٰٓاَيُّهَا النَّاسُ عُلِّمْنَا مَنْطِقَ الطَّيْرِ وَاُوْتِيْنَا مِنْ كُلِّ شَيْءٍۗ اِنَّ هٰذَا لَهُوَ الْفَضْلُ الْمُبِيْنُ ","bc":"wa wariṡa sulaimānu dāwụda wa qāla yā ayyuhan-nāsu 'ullimnā manṭiqaṭ-ṭairi wa ụtīnā ming kulli syaī`, inna hāżā lahuwal-faḍlul-mubīn","at":"Dan Sulaiman telah mewarisi Dawud, dan dia (Sulaiman) berkata, “Wahai manusia! Kami telah diajari bahasa burung dan kami diberi segala sesuatu. Sungguh, (semua) ini benar-benar karunia yang nyata.”"},{"id":"17","ar":"وَحُشِرَ لِسُلَيْمٰنَ جُنُوْدُهٗ مِنَ الْجِنِّ وَالْاِنْسِ وَالطَّيْرِ فَهُمْ يُوْزَعُوْنَ ","bc":"wa ḥusyira lisulaimāna junụduhụ minal-jinni wal-insi waṭ-ṭairi fa hum yụza'ụn","at":"Dan untuk Sulaiman dikumpulkan bala tentaranya dari jin, manusia dan burung, lalu mereka berbaris dengan tertib."},{"id":"18","ar":"حَتّٰىٓ اِذَآ اَتَوْا عَلٰى وَادِ النَّمْلِۙ قَالَتْ نَمْلَةٌ يّٰٓاَيُّهَا النَّمْلُ ادْخُلُوْا مَسٰكِنَكُمْۚ لَا يَحْطِمَنَّكُمْ سُلَيْمٰنُ وَجُنُوْدُهٗۙ وَهُمْ لَا يَشْعُرُوْنَ ","bc":"hattā iżā atau 'alā wādin-namli qālat namlatuy yā ayyuhan-namludkhulụ masākinakum, lā yahṭimannakum sulaimānu wa junụduhụ wa hum lā yasy'urụn","at":"Hingga ketika mereka sampai di lembah semut, berkatalah seekor semut, “Wahai semut-semut! Masuklah ke dalam sarang-sarangmu, agar kamu tidak diinjak oleh Sulaiman dan bala tentaranya, sedangkan mereka tidak menyadari.”"},{"id":"19","ar":"فَتَبَسَّمَ ضَاحِكًا مِّنْ قَوْلِهَا وَقَالَ رَبِّ اَوْزِعْنِيْٓ اَنْ اَشْكُرَ نِعْمَتَكَ الَّتِيْٓ اَنْعَمْتَ عَلَيَّ وَعَلٰى وَالِدَيَّ وَاَنْ اَعْمَلَ صَالِحًا تَرْضٰىهُ وَاَدْخِلْنِيْ بِرَحْمَتِكَ فِيْ عِبَادِكَ الصّٰلِحِيْنَ ","bc":"fa tabassama ḍāḥikam ming qaulihā wa qāla rabbi auzi'nī an asykura ni'matakallatī an'amta 'alayya wa 'alā wālidayya wa an a'mala ṣāliḥan tarḍāhu wa adkhilnī biraḥmatika fī 'ibādikaṣ-ṣāliḥīn","at":"Maka dia (Sulaiman) tersenyum lalu tertawa karena (mendengar) perkataan semut itu. Dan dia berdoa, “Ya Tuhanku, anugerahkanlah aku ilham untuk tetap mensyukuri nikmat-Mu yang telah Engkau anugerahkan kepadaku dan kepada kedua orang tuaku dan agar aku mengerjakan kebajikan yang Engkau ridai; dan masukkanlah aku dengan rahmat-Mu ke dalam golongan hamba-hamba-Mu yang saleh.”"},{"id":"20","ar":"وَتَفَقَّدَ الطَّيْرَ فَقَالَ مَا لِيَ لَآ اَرَى الْهُدْهُدَۖ اَمْ كَانَ مِنَ الْغَاۤىِٕبِيْنَ ","bc":"wa tafaqqadaṭ-ṭaira fa qāla mā liya lā aral-hudhuda am kāna minal-gā`ibīn","at":"Dan dia memeriksa burung-burung lalu berkata, “Mengapa aku tidak melihat Hud-hud, apakah ia termasuk yang tidak hadir?"},{"id":"21","ar":"لَاُعَذِّبَنَّهٗ عَذَابًا شَدِيْدًا اَوْ لَاَا۟ذْبَحَنَّهٗٓ اَوْ لَيَأْتِيَنِّيْ بِسُلْطٰنٍ مُّبِيْنٍ ","bc":"la`u'ażżibannahụ 'ażāban syadīdan au la`ażbaḥannahū au laya`tiyannī bisulṭānim mubīn","at":"Pasti akan kuhukum ia dengan hukuman yang berat atau kusembelih ia, kecuali jika ia datang kepadaku dengan alasan yang jelas.”"},{"id":"22","ar":"فَمَكَثَ غَيْرَ بَعِيْدٍ فَقَالَ اَحَطْتُّ بِمَا لَمْ تُحِطْ بِهٖ وَجِئْتُكَ مِنْ سَبَاٍ ۢبِنَبَاٍ يَّقِيْنٍ ","bc":"fa makaṡa gaira ba'īdin fa qāla aḥaṭtu bimā lam tuḥiṭ bihī wa ji`tuka min saba`im binaba`iy yaqīn","at":"Maka tidak lama kemudian (datanglah Hud-hud), lalu ia berkata, “Aku telah mengetahui sesuatu yang belum engkau ketahui. Aku datang kepadamu dari negeri Saba' membawa suatu berita yang meyakinkan."},{"id":"23","ar":"اِنِّيْ وَجَدْتُّ امْرَاَةً تَمْلِكُهُمْ وَاُوْتِيَتْ مِنْ كُلِّ شَيْءٍ وَّلَهَا عَرْشٌ عَظِيْمٌ ","bc":"innī wajattumra`atan tamlikuhum wa ụtiyat ming kulli syai`iu wa lahā 'arsyun 'aẓīm","at":"Sungguh, kudapati ada seorang perempuan yang memerintah mereka, dan dia dianugerahi segala sesuatu serta memiliki singgasana yang besar."},{"id":"24","ar":"وَجَدْتُّهَا وَقَوْمَهَا يَسْجُدُوْنَ لِلشَّمْسِ مِنْ دُوْنِ اللّٰهِ وَزَيَّنَ لَهُمُ الشَّيْطٰنُ اَعْمَالَهُمْ فَصَدَّهُمْ عَنِ السَّبِيْلِ فَهُمْ لَا يَهْتَدُوْنَۙ ","bc":"wajattuhā wa qaumahā yasjudụna lisy-syamsi min dụnillāhi wa zayyana lahumusy-syaiṭānu a'mālahum fa ṣaddahum 'anis-sabīli fa hum lā yahtadụn","at":"Aku (burung Hud) dapati dia dan kaumnya menyembah matahari, bukan kepada Allah; dan setan telah menjadikan terasa indah bagi mereka perbuatan-perbuatan (buruk) mereka, sehingga menghalangi mereka dari jalan (Allah), maka mereka tidak mendapat petunjuk,"},{"id":"25","ar":"اَلَّا يَسْجُدُوْا لِلّٰهِ الَّذِيْ يُخْرِجُ الْخَبْءَ فِى السَّمٰوٰتِ وَالْاَرْضِ وَيَعْلَمُ مَا تُخْفُوْنَ وَمَا تُعْلِنُوْنَ ","bc":"allā yasjudụ lillāhillażī yukhrijul-khab`a fis-samāwāti wal-arḍi wa ya'lamu mā tukhfụna wa mā tu'linụn","at":"mereka (juga) tidak menyembah Allah yang mengeluarkan apa yang terpendam di langit dan di bumi dan yang mengetahui apa yang kamu sembunyikan dan yang kamu nyatakan."},{"id":"26","ar":"اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۙ رَبُّ الْعَرْشِ الْعَظِيْمِ ۩ ","bc":"allāhu lā ilāha illā huwa rabbul-'arsyil-'aẓīm","at":"Allah, tidak ada tuhan melainkan Dia, Tuhan yang mempunyai ‘Arsy yang agung.”"},{"id":"27","ar":"۞ قَالَ سَنَنْظُرُ اَصَدَقْتَ اَمْ كُنْتَ مِنَ الْكٰذِبِيْنَ ","bc":"qāla sananẓuru a ṣadaqta am kunta minal-kāżibīn","at":"Dia (Sulaiman) berkata, “Akan kami lihat, apa kamu benar, atau termasuk yang berdusta."},{"id":"28","ar":"اِذْهَبْ بِّكِتٰبِيْ هٰذَا فَاَلْقِهْ اِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَانْظُرْ مَاذَا يَرْجِعُوْنَ ","bc":"iż-hab bikitābī hāżā fa alqih ilaihim ṡumma tawalla 'an-hum fanẓur māżā yarji'ụn","at":"Pergilah dengan (membawa) suratku ini, lalu jatuhkanlah kepada mereka, kemudian berpalinglah dari mereka, lalu perhatikanlah apa yang mereka bicarakan.”"},{"id":"29","ar":"قَالَتْ يٰٓاَيُّهَا الْمَلَؤُا اِنِّيْٓ اُلْقِيَ اِلَيَّ كِتٰبٌ كَرِيْمٌ ","bc":"qālat yā ayyuhal-mala`u innī ulqiya ilayya kitābung karīm","at":"Dia (Balqis) berkata, “Wahai para pembesar! Sesungguhnya telah disampaikan kepadaku sebuah surat yang mulia.”"},{"id":"30","ar":"اِنَّهٗ مِنْ سُلَيْمٰنَ وَاِنَّهٗ بِسْمِ اللّٰهِ الرَّحْمٰنِ الرَّحِيْمِ ۙ ","bc":"innahụ min sulaimāna wa innahụ bismillāhir-raḥmānir-raḥīm","at":"Sesungguhnya (surat) itu dari Sulaiman yang isinya, “Dengan nama Allah Yang Maha Pengasih, Maha Penyayang,"},{"id":"31","ar":"اَلَّا تَعْلُوْا عَلَيَّ وَأْتُوْنِيْ مُسْلِمِيْنَ ","bc":"allā ta'lụ 'alayya wa`tụnī muslimīn","at":"janganlah engkau berlaku sombong terhadapku dan datanglah kepadaku sebagai orang-orang yang berserah diri.”"},{"id":"32","ar":"قَالَتْ يٰٓاَيُّهَا الْمَلَؤُا اَفْتُوْنِيْ فِيْٓ اَمْرِيْۚ مَا كُنْتُ قَاطِعَةً اَمْرًا حَتّٰى تَشْهَدُوْنِ ","bc":"qālat yā ayyuhal-mala`u aftụnī fī amrī, mā kuntu qāṭi'atan amran ḥattā tasy-hadụn","at":"Dia (Balqis) berkata, “Wahai para pembesar! Berilah aku pertimbangan dalam perkaraku (ini). Aku tidak pernah memutuskan suatu perkara sebelum kamu hadir dalam majelis(ku).”"},{"id":"33","ar":"قَالُوْا نَحْنُ اُولُوْا قُوَّةٍ وَّاُولُوْا بَأْسٍ شَدِيْدٍ ەۙ وَّالْاَمْرُ اِلَيْكِ فَانْظُرِيْ مَاذَا تَأْمُرِيْنَ","bc":"qālụ naḥnu ulụ quwwatiw wa ulụ ba`sin syadīdiw wal-amru ilaiki fanẓurī māżā ta`murīn","at":"Mereka menjawab, “Kita memiliki kekuatan dan keberanian yang luar biasa (untuk berperang), tetapi keputusan berada di tanganmu; maka pertimbangkanlah apa yang akan engkau perintahkan.”"},{"id":"34","ar":"قَالَتْ اِنَّ الْمُلُوْكَ اِذَا دَخَلُوْا قَرْيَةً اَفْسَدُوْهَا وَجَعَلُوْٓا اَعِزَّةَ اَهْلِهَآ اَذِلَّةً ۚوَكَذٰلِكَ يَفْعَلُوْنَ ","bc":"qālat innal-mulụka iżā dakhalụ qaryatan afsadụhā wa ja'alū a'izzata ahlihā ażillah, wa każālika yaf'alụn","at":"Dia (Balqis) berkata, “Sesungguhnya raja-raja apabila menaklukkan suatu negeri, mereka tentu membinasakannya, dan menjadikan penduduknya yang mulia jadi hina; dan demikian yang akan mereka perbuat."},{"id":"35","ar":"وَاِنِّيْ مُرْسِلَةٌ اِلَيْهِمْ بِهَدِيَّةٍ فَنٰظِرَةٌ ۢبِمَ يَرْجِعُ الْمُرْسَلُوْنَ ","bc":"wa innī mursilatun ilaihim bihadiyyatin fa nāẓiratum bima yarji'ul-mursalụn","at":"Dan sungguh, aku akan mengirim utusan kepada mereka dengan (membawa) hadiah, dan (aku) akan menunggu apa yang akan dibawa kembali oleh para utusan itu.”"},{"id":"36","ar":"فَلَمَّا جَاۤءَ سُلَيْمٰنَ قَالَ اَتُمِدُّوْنَنِ بِمَالٍ فَمَآ اٰتٰىنِ َۧ اللّٰهُ خَيْرٌ مِّمَّآ اٰتٰىكُمْۚ بَلْ اَنْتُمْ بِهَدِيَّتِكُمْ تَفْرَحُوْنَ ","bc":"fa lammā jā`a sulaimāna qāla a tumiddụnani bimālin fa mā ātāniyallāhu khairum mimmā ātākum, bal antum bihadiyyatikum tafraḥụn","at":"Maka ketika para (utusan itu) sampai kepada Sulaiman, dia (Sulaiman) berkata, “Apakah kamu akan memberi harta kepadaku? Apa yang Allah berikan kepadaku lebih baik daripada apa yang Allah berikan kepadamu; tetapi kamu merasa bangga dengan hadiahmu. "},{"id":"37","ar":"اِرْجِعْ اِلَيْهِمْ فَلَنَأْتِيَنَّهُمْ بِجُنُوْدٍ لَّا قِبَلَ لَهُمْ بِهَا وَلَنُخْرِجَنَّهُمْ مِّنْهَآ اَذِلَّةً وَّهُمْ صَاغِرُوْنَ ","bc":"irji' ilaihim falana`tiyannahum bijunụdil lā qibala lahum bihā wa lanukhrijannahum min-hā ażillataw wa hum ṣāgirụn","at":"Kembalilah kepada mereka! Sungguh, Kami pasti akan mendatangi mereka dengan bala tentara yang mereka tidak mampu melawannya, dan akan kami usir mereka dari negeri itu (Saba') secara terhina dan mereka akan menjadi (tawanan) yang hina dina.”"},{"id":"38","ar":"قَالَ يٰٓاَيُّهَا الْمَلَؤُا اَيُّكُمْ يَأْتِيْنِيْ بِعَرْشِهَا قَبْلَ اَنْ يَّأْتُوْنِيْ مُسْلِمِيْنَ ","bc":"qāla yā ayyuhal-mala`u ayyukum ya`tīnī bi'arsyihā qabla ay ya`tụnī muslimīn","at":"Dia (Sulaiman) berkata, “Wahai para pembesar! Siapakah di antara kamu yang sanggup membawa singgasananya kepadaku sebelum mereka datang kepadaku menyerahkan diri?”"},{"id":"39","ar":"قَالَ عِفْرِيْتٌ مِّنَ الْجِنِّ اَنَا۠ اٰتِيْكَ بِهٖ قَبْلَ اَنْ تَقُوْمَ مِنْ مَّقَامِكَۚ وَاِنِّيْ عَلَيْهِ لَقَوِيٌّ اَمِيْنٌ ","bc":"qāla 'ifrītum minal-jinni ana ātīka bihī qabla an taqụma mim maqāmik, wa innī 'alaihi laqawiyyun amīn","at":"‘Ifrit dari golongan jin berkata, “Akulah yang akan membawanya kepadamu sebelum engkau berdiri dari tempat dudukmu; dan sungguh, aku kuat melakukannya dan dapat dipercaya.”"},{"id":"40","ar":"قَالَ الَّذِيْ عِنْدَهٗ عِلْمٌ مِّنَ الْكِتٰبِ اَنَا۠ اٰتِيْكَ بِهٖ قَبْلَ اَنْ يَّرْتَدَّ اِلَيْكَ طَرْفُكَۗ فَلَمَّا رَاٰهُ مُسْتَقِرًّا عِنْدَهٗ قَالَ هٰذَا مِنْ فَضْلِ رَبِّيْۗ لِيَبْلُوَنِيْٓ ءَاَشْكُرُ اَمْ اَكْفُرُۗ وَمَنْ شَكَرَ فَاِنَّمَا يَشْكُرُ لِنَفْسِهٖۚ وَمَنْ كَفَرَ فَاِنَّ رَبِّيْ غَنِيٌّ كَرِيْمٌ ","bc":"qālallażī 'indahụ 'ilmum minal-kitābi ana ātīka bihī qabla ay yartadda ilaika ṭarfuk, fa lammā ra`āhu mustaqirran 'indahụ qāla hāżā min faḍli rabbī, liyabluwanī a asykuru am akfur, wa man syakara fa innamā yasykuru linafsih, wa mang kafara fa inna rabbī ganiyyung karīm","at":"Seorang yang mempunyai ilmu dari Kitab berkata, “Aku akan membawa singgasana itu kepadamu sebelum matamu berkedip.” Maka ketika dia (Sulaiman) melihat singgasana itu terletak di hadapannya, dia pun berkata, “Ini termasuk karunia Tuhanku untuk mengujiku, apakah aku bersyukur atau mengingkari (nikmat-Nya). Barangsiapa bersyukur, maka sesungguhnya dia bersyukur untuk (kebaikan) dirinya sendiri, dan barangsiapa ingkar, maka sesungguhnya Tuhanku Mahakaya, Mahamulia.”"},{"id":"41","ar":"قَالَ نَكِّرُوْا لَهَا عَرْشَهَا نَنْظُرْ اَتَهْتَدِيْٓ اَمْ تَكُوْنُ مِنَ الَّذِيْنَ لَا يَهْتَدُوْنَ ","bc":"qāla nakkirụ lahā 'arsyahā nanẓur a tahtadī am takụnu minallażīna lā yahtadụn","at":"Dia (Sulaiman) berkata, “Ubahlah untuknya singgasananya; kita akan melihat apakah dia (Balqis) mengenal; atau tidak mengenalnya lagi.”"},{"id":"42","ar":"فَلَمَّا جَاۤءَتْ قِيْلَ اَهٰكَذَا عَرْشُكِۗ قَالَتْ كَاَنَّهٗ هُوَۚ وَاُوْتِيْنَا الْعِلْمَ مِنْ قَبْلِهَا وَكُنَّا مُسْلِمِيْنَ ","bc":"fa lammā jā`at qīla a hākażā 'arsyuk, qālat ka`annahụ huw, wa ụtīnal-'ilma ming qablihā wa kunnā muslimīn","at":"Maka ketika dia (Balqis) datang, ditanyakanlah (kepadanya), “Serupa inikah singgasanamu?” Dia (Balqis) menjawab, “Seakan-akan itulah dia.” (Dan dia Balqis berkata), “Kami telah diberi pengetahuan sebelumnya dan kami adalah orang-orang yang berserah diri (kepada Allah).”"},{"id":"43","ar":"وَصَدَّهَا مَا كَانَتْ تَّعْبُدُ مِنْ دُوْنِ اللّٰهِ ۗاِنَّهَا كَانَتْ مِنْ قَوْمٍ كٰفِرِيْنَ ","bc":"wa ṣaddahā mā kānat ta'budu min dụnillāh, innahā kānat ming qauming kāfirīn","at":"Dan kebiasaannya menyembah selain Allah mencegahnya (untuk melahirkan keislamannya), sesungguhnya dia (Balqis) dahulu termasuk orang-orang kafir."},{"id":"44","ar":"قِيْلَ لَهَا ادْخُلِى الصَّرْحَۚ فَلَمَّا رَاَتْهُ حَسِبَتْهُ لُجَّةً وَّكَشَفَتْ عَنْ سَاقَيْهَاۗ قَالَ اِنَّهٗ صَرْحٌ مُّمَرَّدٌ مِّنْ قَوَارِيْرَ ەۗ قَالَتْ رَبِّ اِنِّيْ ظَلَمْتُ نَفْسِيْ وَاَسْلَمْتُ مَعَ سُلَيْمٰنَ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ","bc":"qīla lahadkhuliṣ-ṣar-ḥ, fa lammā ra`at-hu ḥasibat-hu lujjataw wa kasyafat 'an sāqaihā, qāla innahụ ṣar-ḥum mumarradum ming qawārīr, qālat rabbi innī ẓalamtu nafsī wa aslamtu ma'a sulaimāna lillāhi rabbil-'ālamīn","at":"Dikatakan kepadanya (Balqis), “Masuklah ke dalam istana.” Maka ketika dia (Balqis) melihat (lantai istana) itu, dikiranya kolam air yang besar, dan disingkapkannya (penutup) kedua betisnya. Dia (Sulaiman) berkata, “Sesungguhnya ini hanyalah lantai istana yang dilapisi kaca.” Dia (Balqis) berkata, “Ya Tuhanku, sungguh, aku telah berbuat zalim terhadap diriku. Aku berserah diri bersama Sulaiman kepada Allah, Tuhan seluruh alam.”"},{"id":"45","ar":"وَلَقَدْ اَرْسَلْنَآ اِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًا اَنِ اعْبُدُوا اللّٰهَ فَاِذَا هُمْ فَرِيْقٰنِ يَخْتَصِمُوْنَ ","bc":"wa laqad arsalnā ilā ṡamụda akhāhum ṣāliḥan ani'budullāha fa iżā hum farīqāni yakhtaṣimụn","at":"Dan sungguh, Kami telah mengutus kepada (kaum) Samud saudara mereka yaitu Saleh (yang menyeru), “Sembahlah Allah!” Tetapi tiba-tiba mereka (menjadi) dua golongan yang bermusuhan."},{"id":"46","ar":"قَالَ يٰقَوْمِ لِمَ تَسْتَعْجِلُوْنَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِۚ لَوْلَا تَسْتَغْفِرُوْنَ اللّٰهَ لَعَلَّكُمْ تُرْحَمُوْنَ ","bc":"qāla yā qaumi lima tasta'jilụna bis-sayyi`ati qablal-ḥasanah, lau lā tastagfirụnallāha la'allakum tur-ḥamụn","at":"Dia (Saleh) berkata, “Wahai kaumku! Mengapa kamu meminta disegerakan keburukan sebelum (kamu meminta) kebaikan? Mengapa kamu tidak memohon ampunan kepada Allah, agar kamu mendapat rahmat?”"},{"id":"47","ar":"قَالُوا اطَّيَّرْنَا بِكَ وَبِمَنْ مَّعَكَۗ قَالَ طٰۤىِٕرُكُمْ عِنْدَ اللّٰهِ بَلْ اَنْتُمْ قَوْمٌ تُفْتَنُوْنَ ","bc":"qāluṭ ṭayyarnā bika wa bimam ma'ak, qāla ṭā`irukum 'indallāhi bal antum qaumun tuftanụn","at":"Mereka menjawab, “Kami mendapat nasib yang malang disebabkan oleh kamu dan orang-orang yang bersamamu.” Dia (Saleh) berkata, “Nasibmu ada pada Allah (bukan kami yang menjadi sebab), tetapi kamu adalah kaum yang sedang diuji.”"},{"id":"48","ar":"وَكَانَ فِى الْمَدِيْنَةِ تِسْعَةُ رَهْطٍ يُّفْسِدُوْنَ فِى الْاَرْضِ وَلَا يُصْلِحُوْنَ ","bc":"wa kāna fil-madīnati tis'atu rahṭiy yufsidụna fil-arḍi wa lā yuṣliḥụn","at":"Dan di kota itu ada sembilan orang laki-laki yang berbuat kerusakan di bumi, mereka tidak melakukan perbaikan."},{"id":"49","ar":"قَالُوْا تَقَاسَمُوْا بِاللّٰهِ لَنُبَيِّتَنَّهٗ وَاَهْلَهٗ ثُمَّ لَنَقُوْلَنَّ لِوَلِيِّهٖ مَا شَهِدْنَا مَهْلِكَ اَهْلِهٖ وَاِنَّا لَصٰدِقُوْنَ ","bc":"qālụ taqāsamụ billāhi lanubayyitannahụ wa ahlahụ ṡumma lanaqụlanna liwaliyyihī mā syahidnā mahlika ahlihī wa innā laṣādiqụn","at":"Mereka berkata, “Bersumpahlah kamu dengan (nama) Allah, bahwa kita pasti akan menyerang dia bersama keluarganya pada malam hari, kemudian kita akan mengatakan kepada ahli warisnya (bahwa) kita tidak menyaksikan kebinasaan keluarganya itu, dan sungguh, kita orang yang benar.”"},{"id":"50","ar":"وَمَكَرُوْا مَكْرًا وَّمَكَرْنَا مَكْرًا وَّهُمْ لَا يَشْعُرُوْنَ ","bc":"wa makarụ makraw wa makarnā makraw wa hum lā yasy'urụn","at":"Dan mereka membuat tipu daya, dan Kami pun menyusun tipu daya, sedang mereka tidak menyadari."},{"id":"51","ar":"فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ مَكْرِهِمْ اَنَّا دَمَّرْنٰهُمْ وَقَوْمَهُمْ اَجْمَعِيْنَ ","bc":"fanẓur kaifa kāna 'āqibatu makrihim annā dammarnāhum wa qaumahum ajma'īn","at":"Maka perhatikanlah bagaimana akibat dari tipu daya mereka, bahwa Kami membinasakan mereka dan kaum mereka semuanya."},{"id":"52","ar":"فَتِلْكَ بُيُوْتُهُمْ خَاوِيَةً ۢبِمَا ظَلَمُوْاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّقَوْمٍ يَّعْلَمُوْنَ ","bc":"fa tilka buyụtuhum khāwiyatam bimā ẓalamụ, inna fī żālika la`āyatal liqaumiy ya'lamụn","at":"Maka itulah rumah-rumah mereka yang runtuh karena kezaliman mereka. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah) bagi orang-orang yang mengetahui."},{"id":"53","ar":"وَاَنْجَيْنَا الَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَ ","bc":"wa anjainallażīna āmanụ wa kānụ yattaqụn","at":"Dan Kami selamatkan orang-orang yang beriman dan mereka selalu bertakwa."},{"id":"54","ar":"وَلُوْطًا اِذْ قَالَ لِقَوْمِهٖٓ اَتَأْتُوْنَ الْفَاحِشَةَ وَاَنْتُمْ تُبْصِرُوْنَ ","bc":"wa lụṭan iż qāla liqaumihī a ta`tụnal-fāḥisyata wa antum tubṣirụn","at":"Dan (ingatlah kisah) Lut, ketika dia berkata kepada kaumnya, “Mengapa kamu mengerjakan perbuatan fahisyah (keji), padahal kamu melihatnya (kekejian perbuatan maksiat itu)?”"},{"id":"55","ar":"اَىِٕنَّكُمْ لَتَأْتُوْنَ الرِّجَالَ شَهْوَةً مِّنْ دُوْنِ النِّسَاۤءِ ۗبَلْ اَنْتُمْ قَوْمٌ تَجْهَلُوْنَ","bc":"a innakum lata`tụnar-rijāla syahwatam min dụnin-nisā`, bal antum qaumun taj-halụn","at":"Mengapa kamu mendatangi laki-laki untuk (memenuhi) syahwat(mu), bukan (mendatangi) perempuan? Sungguh, kamu adalah kaum yang tidak mengetahui (akibat perbuatanmu)."},{"id":"56","ar":"۞ فَمَا كَانَ جَوَابَ قَوْمِهٖٓ اِلَّآ اَنْ قَالُوْٓا اَخْرِجُوْٓا اٰلَ لُوْطٍ مِّنْ قَرْيَتِكُمْۙ اِنَّهُمْ اُنَاسٌ يَّتَطَهَّرُوْنَ ","bc":"fa mā kāna jawāba qaumihī illā ang qālū akhrijū āla lụṭim ming qaryatikum innahum unāsuy yataṭahharụn","at":"Jawaban kaumnya tidak lain hanya dengan mengatakan, “Usirlah Lut dan keluarganya dari negerimu; sesungguhnya mereka adalah orang-orang yang (menganggap dirinya) suci.”"},{"id":"57","ar":"فَاَنْجَيْنٰهُ وَاَهْلَهٗٓ اِلَّا امْرَاَتَهٗ قَدَّرْنٰهَا مِنَ الْغٰبِرِيْنَ ","bc":"fa anjaināhu wa ahlahū illamra`atahụ qaddarnāhā minal-gābirīn","at":"Maka Kami selamatkan dia dan keluarganya, kecuali istrinya. Kami telah menentukan dia termasuk orang-orang yang tertinggal (dibinasakan)."},{"id":"58","ar":"وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۚ فَسَاۤءَ مَطَرُ الْمُنْذَرِيْنَ ","bc":"wa amṭarnā 'alaihim maṭarā, fa sā`a maṭarul-munżarīn","at":"Dan Kami hujani mereka dengan hujan (batu), maka sangat buruklah hujan (yang ditimpakan) pada orang-orang yang diberi peringatan itu (tetapi tidak mengindahkan). "},{"id":"59","ar":"قُلِ الْحَمْدُ لِلّٰهِ وَسَلٰمٌ عَلٰى عِبَادِهِ الَّذِيْنَ اصْطَفٰىۗ ءٰۤاللّٰهُ خَيْرٌ اَمَّا يُشْرِكُوْنَ ۔ ","bc":"qulil-ḥamdu lillāhi wa salāmun 'alā 'ibādihillażīnaṣṭafā, āllāhu khairun ammā yusyrikụn","at":"Katakanlah (Muhammad), “Segala puji bagi Allah dan salam sejahtera atas hamba-hamba-Nya yang dipilih-Nya. Apakah Allah yang lebih baik, ataukah apa yang mereka persekutukan (dengan Dia)?”"},{"id":"60","ar":"اَمَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَاَنْزَلَ لَكُمْ مِّنَ السَّمَاۤءِ مَاۤءً فَاَنْۢبَتْنَا بِهٖ حَدَاۤىِٕقَ ذَاتَ بَهْجَةٍۚ مَا كَانَ لَكُمْ اَنْ تُنْۢبِتُوْا شَجَرَهَاۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗبَلْ هُمْ قَوْمٌ يَّعْدِلُوْنَ ۗ ","bc":"am man khalaqas-samāwāti wal-arḍa wa anzala lakum minas-samā`i mā`an fa ambatnā bihī ḥadā`iqa żāta bahjah, mā kāna lakum an tumbitụ syajarahā, a ilāhum ma'allāh, bal hum qaumuy ya'dilụn","at":"Bukankah Dia (Allah) yang menciptakan langit dan bumi dan yang menurunkan air dari langit untukmu, lalu Kami tumbuhkan dengan air itu kebun-kebun yang berpemandangan indah? Kamu tidak akan mampu menumbuhkan pohon-pohonnya. Apakah di samping Allah ada tuhan (yang lain)? Sebenarnya mereka adalah orang-orang yang menyimpang (dari kebenaran)."},{"id":"61","ar":"اَمَّنْ جَعَلَ الْاَرْضَ قَرَارًا وَّجَعَلَ خِلٰلَهَآ اَنْهٰرًا وَّجَعَلَ لَهَا رَوَاسِيَ وَجَعَلَ بَيْنَ الْبَحْرَيْنِ حَاجِزًاۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ ۗ ","bc":"am man ja'alal-arḍa qarāraw wa ja'ala khilālahā an-hāraw wa ja'ala lahā rawāsiya wa ja'ala bainal-baḥraini ḥājizā, a ilāhum ma'allāh, bal akṡaruhum lā ya'lamụn","at":"Bukankah Dia (Allah) yang telah menjadikan bumi sebagai tempat berdiam, yang menjadikan sungai-sungai di celah-celahnya, yang menjadikan gunung-gunung untuk (mengokohkan)nya dan yang menjadikan suatu pemisah antara dua laut? Apakah di samping Allah ada tuhan (yang lain)? Sebenarnya kebanyakan mereka tidak mengetahui."},{"id":"62","ar":"اَمَّنْ يُّجِيْبُ الْمُضْطَرَّ اِذَا دَعَاهُ وَيَكْشِفُ السُّوْۤءَ وَيَجْعَلُكُمْ خُلَفَاۤءَ الْاَرْضِۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗقَلِيْلًا مَّا تَذَكَّرُوْنَۗ ","bc":"am may yujībul-muḍṭarra iżā da'āhu wa yaksyifus-sū`a wa yaj'alukum khulafā`al-arḍ, a ilāhum ma'allāh, qalīlam mā tażakkarụn","at":"Bukankah Dia (Allah) yang memperkenankan (doa) orang yang dalam kesulitan apabila dia berdoa kepada-Nya, dan menghilangkan kesusahan dan menjadikan kamu (manusia) sebagai khalifah (pemimpin) di bumi? Apakah di samping Allah ada tuhan (yang lain)? Sedikit sekali (nikmat Allah) yang kamu ingat."},{"id":"63","ar":"اَمَّنْ يَّهْدِيْكُمْ فِيْ ظُلُمٰتِ الْبَرِّ وَالْبَحْرِ وَمَنْ يُّرْسِلُ الرِّيٰحَ بُشْرًا ۢ بَيْنَ يَدَيْ رَحْمَتِهٖۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗتَعٰلَى اللّٰهُ عَمَّا يُشْرِكُوْنَ ","bc":"am may yahdīkum fī ẓulumātil-barri wal-baḥri wa may yursilur-riyāḥa busyram baina yadai raḥmatih, a ilāhum ma'allāh, ta'ālallāhu 'ammā yusyrikụn","at":"Bukankah Dia (Allah) yang memberi petunjuk kepada kamu dalam kegelapan di daratan dan lautan dan yang mendatangkan angin sebagai kabar gembira sebelum (kedatangan) rahmat-Nya? Apakah di samping Allah ada tuhan (yang lain)? Mahatinggi Allah terhadap apa yang mereka persekutukan. "},{"id":"64","ar":"اَمَّنْ يَّبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ وَمَنْ يَّرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ ءَاِلٰهٌ مَّعَ اللّٰهِ ۗقُلْ هَاتُوْا بُرْهَانَكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"am may yabda`ul-khalqa ṡumma yu'īduhụ wa may yarzuqukum minas-samā`i wal-arḍ, a ilāhum ma'allāh, qul hātụ bur-hānakum ing kuntum ṣādiqīn","at":"Bukankah Dia (Allah) yang menciptakan (makhluk) dari permulaannya, kemudian mengulanginya (lagi) dan yang memberikan rezeki kepadamu dari langit dan bumi? Apakah di samping Allah ada tuhan (yang lain)? Katakanlah, “Kemukakanlah bukti kebenaranmu, jika kamu orang yang benar.”"},{"id":"65","ar":"قُلْ لَّا يَعْلَمُ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ الْغَيْبَ اِلَّا اللّٰهُ ۗوَمَا يَشْعُرُوْنَ اَيَّانَ يُبْعَثُوْنَ ","bc":"qul lā ya'lamu man fis-samāwāti wal-arḍil-gaiba illallāh, wa mā yasy'urụna ayyāna yub'aṡụn","at":"Katakanlah (Muhammad), “Tidak ada sesuatu pun di langit dan di bumi yang mengetahui perkara yang gaib, kecuali Allah. Dan mereka tidak mengetahui kapan mereka akan dibangkitkan.”"},{"id":"66","ar":"بَلِ ادّٰرَكَ عِلْمُهُمْ فِى الْاٰخِرَةِۗ بَلْ هُمْ فِيْ شَكٍّ مِّنْهَاۗ بَلْ هُمْ مِّنْهَا عَمُوْنَ ","bc":"baliddāraka 'ilmuhum fil-ākhirah, bal hum fī syakkim min-hā, bal hum min-hā 'amụn","at":"Bahkan pengetahuan mereka tentang akhirat tidak sampai (ke sana). Bahkan mereka ragu-ragu tentangnya (akhirat itu). Bahkan mereka buta tentang itu."},{"id":"67","ar":"وَقَالَ الَّذِيْنَ كَفَرُوْٓا ءَاِذَا كُنَّا تُرَابًا وَّاٰبَاۤؤُنَآ اَىِٕنَّا لَمُخْرَجُوْنَ ","bc":"wa qālallażīna kafarū a iżā kunnā turābaw wa ābā`unā a innā lamukhrajụn","at":"Dan orang-orang yang kafir berkata, “Setelah kita menjadi tanah dan (begitu pula) nenek moyang kita, apakah benar kita akan dikeluarkan (dari kubur)?"},{"id":"68","ar":"لَقَدْ وُعِدْنَا هٰذَا نَحْنُ وَاٰبَاۤؤُنَا مِنْ قَبْلُۙ اِنْ هٰذَآ اِلَّآ اَسَاطِيْرُ الْاَوَّلِيْنَ ","bc":"laqad wu'idnā hāżā naḥnu wa ābā`unā ming qablu in hāżā illā asāṭīrul-awwalīn","at":"Sejak dahulu kami telah diberi ancaman dengan ini (hari kebangkitan); kami dan nenek moyang kami. Sebenarnya ini hanyalah dongeng orang-orang terdahulu.”"},{"id":"69","ar":"قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الْمُجْرِمِيْنَ ","bc":"qul sīrụ fil-arḍi fanẓurụ kaifa kāna 'āqibatul-mujrimīn","at":"Katakanlah (Muhammad), “Berjalanlah kamu di bumi, lalu perhatikanlah bagaimana kesudahan orang-orang yang berdosa. "},{"id":"70","ar":"وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُنْ فِيْ ضَيْقٍ مِّمَّا يَمْكُرُوْنَ ","bc":"wa lā taḥzan 'alaihim wa lā takun fī ḍaiqim mimmā yamkurụn","at":"Dan janganlah engkau bersedih hati terhadap mereka, dan janganlah (dadamu) merasa sempit terhadap upaya tipu daya mereka.”"},{"id":"71","ar":"وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"wa yaqụlụna matā hāżal-wa'du ing kuntum ṣādiqīn","at":"Dan mereka (orang kafir) berkata, “Kapankah datangnya janji (azab) itu, jika kamu orang yang benar.”"},{"id":"72","ar":"قُلْ عَسٰٓى اَنْ يَّكُوْنَ رَدِفَ لَكُمْ بَعْضُ الَّذِيْ تَسْتَعْجِلُوْنَ ","bc":"qul 'asā ay yakụna radifa lakum ba'ḍullażī tasta'jilụn","at":"Katakanlah (Muhammad), “Boleh jadi sebagian dari (azab) yang kamu minta disegerakan itu telah hampir sampai kepadamu.”"},{"id":"73","ar":"وَاِنَّ رَبَّكَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُوْنَ","bc":"wa inna rabbaka lażụ faḍlin 'alan-nāsi wa lākinna akṡarahum lā yasykurụn","at":"Dan sungguh, Tuhanmu benar-benar memiliki karunia (yang diberikan-Nya) kepada manusia, tetapi kebanyakan mereka tidak mensyukuri(nya)."},{"id":"74","ar":"وَاِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُوْرُهُمْ وَمَا يُعْلِنُوْنَ ","bc":"wa inna rabbaka laya'lamu mā tukinnu ṣudụruhum wa mā yu'linụn","at":"Dan sungguh, Tuhanmu mengetahui apa yang disembunyikan dalam dada mereka dan apa yang mereka nyatakan."},{"id":"75","ar":"وَمَا مِنْ غَاۤىِٕبَةٍ فِى السَّمَاۤءِ وَالْاَرْضِ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍ ","bc":"wa mā min gā`ibatin fis-samā`i wal-arḍi illā fī kitābim mubīn","at":"Dan tidak ada sesuatu pun yang tersembunyi di langit dan di bumi, melainkan (tercatat) dalam Kitab yang jelas (Lauh Mahfuzh)."},{"id":"76","ar":"اِنَّ هٰذَا الْقُرْاٰنَ يَقُصُّ عَلٰى بَنِيْٓ اِسْرَاۤءِيْلَ اَكْثَرَ الَّذِيْ هُمْ فِيْهِ يَخْتَلِفُوْنَ ","bc":"inna hāżal-qur`āna yaquṣṣu 'alā banī isrā`īla akṡarallażī hum fīhi yakhtalifụn","at":"Sungguh, Al-Qur'an ini menjelaskan kepada Bani Israil sebagian besar dari (perkara) yang mereka perselisihkan."},{"id":"77","ar":"وَاِنَّهٗ لَهُدًى وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَ ","bc":"wa innahụ lahudaw wa raḥmatul lil-mu`minīn","at":"Dan sungguh, (Al-Qur'an) itu benar-benar menjadi petunjuk dan rahmat bagi orang-orang yang beriman."},{"id":"78","ar":"اِنَّ رَبَّكَ يَقْضِيْ بَيْنَهُمْ بِحُكْمِهٖۚ وَهُوَ الْعَزِيْزُ الْعَلِيْمُۚ ","bc":"inna rabbaka yaqḍī bainahum biḥukmih, wa huwal-'azīzul-'alīm","at":"Sungguh, Tuhanmu akan menyelesaikan (perkara) di antara mereka dengan hukum-Nya, dan Dia Mahaperkasa, Maha Mengetahui."},{"id":"79","ar":"فَتَوَكَّلْ عَلَى اللّٰهِ ۗاِنَّكَ عَلَى الْحَقِّ الْمُبِيْنِ ","bc":"fa tawakkal 'alallāh, innaka 'alal-ḥaqqil-mubīn","at":"Maka bertawakallah kepada Allah, sungguh engkau (Muhammad) berada di atas kebenaran yang nyata."},{"id":"80","ar":"اِنَّكَ لَا تُسْمِعُ الْمَوْتٰى وَلَا تُسْمِعُ الصُّمَّ الدُّعَاۤءَ اِذَا وَلَّوْا مُدْبِرِيْنَ ","bc":"innaka lā tusmi'ul-mautā wa lā tusmi'uṣ-ṣummad-du'ā`a iżā wallau mudbirīn","at":"Sungguh, engkau tidak dapat menjadikan orang yang mati dapat mendengar dan (tidak pula) menjadikan orang yang tuli dapat mendengar seruan, apabila mereka telah berpaling ke belakang."},{"id":"81","ar":"وَمَآ اَنْتَ بِهٰدِى الْعُمْيِ عَنْ ضَلٰلَتِهِمْۗ اِنْ تُسْمِعُ اِلَّا مَنْ يُّؤْمِنُ بِاٰيٰتِنَا فَهُمْ مُّسْلِمُوْنَ ","bc":"wa mā anta bihādil-'umyi 'an ḍalālatihim, in tusmi'u illā may yu`minu bi`āyātinā fa hum muslimụn","at":"Dan engkau tidak akan dapat memberi petunjuk orang buta dari kesesatannya. Engkau tidak dapat menjadikan (seorang pun) mendengar, kecuali orang-orang yang beriman kepada ayat-ayat Kami, lalu mereka berserah diri."},{"id":"82","ar":"۞ وَاِذَا وَقَعَ الْقَوْلُ عَلَيْهِمْ اَخْرَجْنَا لَهُمْ دَاۤبَّةً مِّنَ الْاَرْضِ تُكَلِّمُهُمْ اَنَّ النَّاسَ كَانُوْا بِاٰيٰتِنَا لَا يُوْقِنُوْنَ","bc":"wa iżā waqa'al-qaulu 'alaihim akhrajnā lahum dābbatam minal-arḍi tukallimuhum annan-nāsa kānụ bi`āyātinā lā yụqinụn","at":"Dan apabila perkataan (ketentuan masa kehancuran alam) telah berlaku atas mereka, Kami keluarkan makhluk bergerak yang bernyawa dari bumi yang akan mengatakan kepada mereka bahwa manusia dahulu tidak yakin kepada ayat-ayat Kami. "},{"id":"83","ar":"وَيَوْمَ نَحْشُرُ مِنْ كُلِّ اُمَّةٍ فَوْجًا مِّمَّنْ يُّكَذِّبُ بِاٰيٰتِنَا فَهُمْ يُوْزَعُوْنَ ","bc":"wa yauma naḥsyuru ming kulli ummatin faujam mim may yukażżibu bi`āyātinā fa hum yụza'ụn","at":"Dan (ingatlah) pada hari (ketika) Kami mengumpulkan dari setiap umat, segolongan orang yang mendustakan ayat-ayat Kami, lalu mereka dibagi-bagi (dalam kelompok-kelompok)."},{"id":"84","ar":"حَتّٰٓى اِذَا جَاۤءُوْ قَالَ اَكَذَّبْتُمْ بِاٰيٰتِيْ وَلَمْ تُحِيْطُوْا بِهَا عِلْمًا اَمَّاذَا كُنْتُمْ تَعْمَلُوْنَ ","bc":"ḥattā iżā jā`ụ qāla a każżabtum bi`āyātī wa lam tuḥīṭụ bihā 'ilman ammāżā kuntum ta'malụn","at":"Hingga apabila mereka datang, Dia (Allah) berfirman, “Mengapa kamu telah mendustakan ayat-ayat-Ku, pada-hal kamu tidak mempunyai pengetahuan tentang itu, atau apakah yang telah kamu kerjakan?”"},{"id":"85","ar":"وَوَقَعَ الْقَوْلُ عَلَيْهِمْ بِمَا ظَلَمُوْا فَهُمْ لَا يَنْطِقُوْنَ ","bc":"wa waqa'al-qaulu 'alaihim bimā ẓalamụ fa hum lā yanṭiqụn","at":"Dan berlakulah perkataan (janji azab) atas mereka karena kezaliman mereka, maka mereka tidak dapat berkata."},{"id":"86","ar":"اَلَمْ يَرَوْا اَنَّا جَعَلْنَا الَّيْلَ لِيَسْكُنُوْا فِيْهِ وَالنَّهَارَ مُبْصِرًاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ ","bc":"a lam yarau annā ja'alnal-laila liyaskunụ fīhi wan-nahāra mubṣirā, inna fī żālika la`āyātil liqaumiy yu`minụn","at":"Apakah mereka tidak memperhatikan bahwa Kami telah menjadikan malam agar mereka beristirahat padanya dan (menjadikan) siang yang menerangi? Sungguh, pada yang demikian itu terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang beriman."},{"id":"87","ar":"وَيَوْمَ يُنْفَخُ فِى الصُّوْرِ فَفَزِعَ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِ اِلَّا مَنْ شَاۤءَ اللّٰهُ ۗوَكُلٌّ اَتَوْهُ دَاخِرِيْنَ ","bc":"wa yauma yunfakhu fiṣ-ṣụri fa fazi'a man fis-samāwāti wa man fil-arḍi illā man syā`allāh, wa kullun atauhu dākhirīn","at":"Dan (ingatlah) pada hari (ketika) sangkakala ditiup, maka terkejutlah apa yang ada di langit dan apa yang ada di bumi, kecuali siapa yang dikehendaki Allah. Dan semua mereka datang menghadap-Nya dengan merendahkan diri."},{"id":"88","ar":"وَتَرَى الْجِبَالَ تَحْسَبُهَا جَامِدَةً وَّهِيَ تَمُرُّ مَرَّ السَّحَابِۗ صُنْعَ اللّٰهِ الَّذِيْٓ اَتْقَنَ كُلَّ شَيْءٍۗ اِنَّهٗ خَبِيْرٌ ۢبِمَا تَفْعَلُوْنَ","bc":"wa taral-jibāla taḥsabuhā jāmidataw wa hiya tamurru marras-saḥāb, ṣun'allāhillażī atqana kulla syaī`, innahụ khabīrum bimā taf'alụn","at":"Dan engkau akan melihat gunung-gunung, yang engkau kira tetap di tempatnya, padahal ia berjalan (seperti) awan berjalan. (Itulah) ciptaan Allah yang mencipta dengan sempurna segala sesuatu. Sungguh, Dia Mahateliti apa yang kamu kerjakan."},{"id":"89","ar":"مَنْ جَاۤءَ بِالْحَسَنَةِ فَلَهٗ خَيْرٌ مِّنْهَاۚ وَهُمْ مِّنْ فَزَعٍ يَّوْمَىِٕذٍ اٰمِنُوْنَ ","bc":"man jā`a bil-ḥasanati fa lahụ khairum min-hā, wa hum min faza'iy yauma`iżin āminụn","at":"Barangsiapa membawa kebaikan, maka dia memperoleh (balasan) yang lebih baik daripadanya, sedang mereka merasa aman dari kejutan (yang dahsyat) pada hari itu."},{"id":"90","ar":"وَمَنْ جَاۤءَ بِالسَّيِّئَةِ فَكُبَّتْ وُجُوْهُهُمْ فِى النَّارِۗ هَلْ تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُوْنَ ","bc":"wa man jā`a bis-sayyi`ati fa kubbat wujụhuhum fin-nār, hal tujzauna illā mā kuntum ta'malụn","at":"Dan barangsiapa membawa kejahatan, maka disungkurkanlah wajah mereka ke dalam neraka. Kamu tidak diberi balasan, melainkan (setimpal) dengan apa yang telah kamu kerjakan."},{"id":"91","ar":"اِنَّمَآ اُمِرْتُ اَنْ اَعْبُدَ رَبَّ هٰذِهِ الْبَلْدَةِ الَّذِيْ حَرَّمَهَا وَلَهٗ كُلُّ شَيْءٍ وَّاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُسْلِمِيْنَ ۙ","bc":"innamā umirtu an a'buda rabba hāżihil-baldatillażī ḥarramahā wa lahụ kullu syai`iw wa umirtu an akụna minal-muslimīn","at":"Aku (Muhammad) hanya diperintahkan menyembah Tuhan negeri ini (Mekah) yang Dia telah menjadikan suci padanya dan segala sesuatu adalah milik-Nya. Dan aku diperintahkan agar aku termasuk orang Muslim,"},{"id":"92","ar":"وَاَنْ اَتْلُوَا الْقُرْاٰنَ ۚفَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖۚ وَمَنْ ضَلَّ فَقُلْ اِنَّمَآ اَنَا۠ مِنَ الْمُنْذِرِيْنَ ","bc":"wa an atluwal-qur`ān, fa manihtadā fa innamā yahtadī linafsih, wa man ḍalla fa qul innamā ana minal-munżirīn","at":"dan agar aku membacakan Al-Qur'an (kepada manusia). Maka barangsiapa mendapat petunjuk maka sesungguhnya dia mendapat petunjuk untuk (kebaikan) dirinya, dan barangsiapa sesat, maka katakanlah, “Sesungguhnya aku (ini) tidak lain hanyalah salah seorang pemberi peringatan.”"},{"id":"93","ar":"وَقُلِ الْحَمْدُ لِلّٰهِ سَيُرِيْكُمْ اٰيٰتِهٖ فَتَعْرِفُوْنَهَاۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ","bc":"wa qulil-ḥamdu lillāhi sayurīkum āyātihī fa ta'rifụnahā, wa mā rabbuka bigāfilin 'ammā ta'malụn","at":"Dan katakanlah (Muhammad), “Segala puji bagi Allah, Dia akan memperlihatkan kepadamu tanda-tanda (kebesaran)-Nya, maka kamu akan mengetahuinya. Dan Tuhanmu tidak lengah terhadap apa yang kamu kerjakan.”"}],an_nas=[{"id":"1","ar":"قُلْ اَعُوْذُ بِرَبِّ النَّاسِۙ","bc":"qul a'ụżu birabbin-nās","at":"Katakanlah, “Aku berlindung kepada Tuhannya manusia,"},{"id":"2","ar":"مَلِكِ النَّاسِۙ","bc":"malikin-nās","at":"Raja manusia,"},{"id":"3","ar":"اِلٰهِ النَّاسِۙ","bc":"ilāhin-nās","at":"sembahan manusia,"},{"id":"4","ar":"مِنْ شَرِّ الْوَسْوَاسِ ەۙ الْخَنَّاسِۖ","bc":"min syarril-waswāsil-khannās","at":"dari kejahatan (bisikan) setan yang bersembunyi,"},{"id":"5","ar":"الَّذِيْ يُوَسْوِسُ فِيْ صُدُوْرِ النَّاسِۙ","bc":"allażī yuwaswisu fī ṣudụrin-nās","at":"yang membisikkan (kejahatan) ke dalam dada manusia,"},{"id":"6","ar":"مِنَ الْجِنَّةِ وَالنَّاسِ ","bc":"minal-jinnati wan-nās","at":"dari (golongan) jin dan manusia.”"}],an_nasr=[{"id":"1","ar":"اِذَا جَاۤءَ نَصْرُ اللّٰهِ وَالْفَتْحُۙ","bc":"iżā jā`a naṣrullāhi wal-fat-ḥ","at":"Apabila telah datang pertolongan Allah dan kemenangan,"},{"id":"2","ar":"وَرَاَيْتَ النَّاسَ يَدْخُلُوْنَ فِيْ دِيْنِ اللّٰهِ اَفْوَاجًاۙ","bc":"wa ra`aitan-nāsa yadkhulụna fī dīnillāhi afwājā","at":"dan engkau melihat manusia berbondong-bondong masuk agama Allah,"},{"id":"3","ar":"فَسَبِّحْ بِحَمْدِ رَبِّكَ وَاسْتَغْفِرْهُۗ اِنَّهٗ كَانَ تَوَّابًا","bc":"fa sabbiḥ biḥamdi rabbika wastagfir-h, innahụ kāna tawwābā","at":"maka bertasbihlah dalam dengan Tuhanmu dan mohonlah ampunan kepada-Nya. Sungguh, Dia Maha Penerima tobat."}],an_naziat=[{"id":"1","ar":"وَالنّٰزِعٰتِ غَرْقًاۙ","bc":"wan-nāzi'āti garqā","at":"Demi (malaikat) yang mencabut (nyawa) dengan keras."},{"id":"2","ar":"وَّالنّٰشِطٰتِ نَشْطًاۙ","bc":"wan-nāsyiṭāti nasyṭā","at":"Demi (malaikat) yang mencabut (nyawa) dengan lemah lembut."},{"id":"3","ar":"وَّالسّٰبِحٰتِ سَبْحًاۙ","bc":"was-sābiḥāti sab-ḥā","at":"Demi (malaikat) yang turun dari langit dengan cepat,"},{"id":"4","ar":"فَالسّٰبِقٰتِ سَبْقًاۙ","bc":"fas-sābiqāti sabqā","at":"dan (malaikat) yang mendahului dengan kencang,"},{"id":"5","ar":"فَالْمُدَبِّرٰتِ اَمْرًاۘ","bc":"fal-mudabbirāti amrā","at":"dan (malaikat) yang mengatur urusan (dunia)."},{"id":"6","ar":"يَوْمَ تَرْجُفُ الرَّاجِفَةُۙ","bc":"yauma tarjufur-rājifah","at":"(Sungguh, kamu akan dibangkitkan) pada hari ketika tiupan pertama mengguncangkan alam,"},{"id":"7","ar":"تَتْبَعُهَا الرَّادِفَةُ ۗ","bc":"tatba'uhar-rādifah","at":"(tiupan pertama) itu diiringi oleh tiupan kedua."},{"id":"8","ar":"قُلُوْبٌ يَّوْمَىِٕذٍ وَّاجِفَةٌۙ","bc":"qulụbuy yauma`iżiw wājifah","at":"Hati manusia pada waktu itu merasa sangat takut,"},{"id":"9","ar":"اَبْصَارُهَا خَاشِعَةٌ ۘ","bc":"abṣāruhā khāsyi'ah","at":"pandangannya tunduk."},{"id":"10","ar":"يَقُوْلُوْنَ ءَاِنَّا لَمَرْدُوْدُوْنَ فِى الْحَافِرَةِۗ","bc":"yaqụlụna a innā lamardụdụna fil-ḥāfirah","at":"(Orang-orang kafir) berkata, “Apakah kita benar-benar akan dikembalikan kepada kehidupan yang semula? "},{"id":"11","ar":"ءَاِذَا كُنَّا عِظَامًا نَّخِرَةً ۗ","bc":"a iżā kunnā 'iẓāman nakhirah","at":"Apakah (akan dibangkitkan juga) apabila kita telah menjadi tulang belulang yang hancur?”"},{"id":"12","ar":"قَالُوْا تِلْكَ اِذًا كَرَّةٌ خَاسِرَةٌ ۘ","bc":"qālụ tilka iżang karratun khāsirah","at":"Mereka berkata, “Kalau demikian, itu adalah suatu pengembalian yang merugikan.”"},{"id":"13","ar":"فَاِنَّمَا هِيَ زَجْرَةٌ وَّاحِدَةٌۙ","bc":"fa innamā hiya zajratuw wāḥidah","at":"Maka pengembalian itu hanyalah dengan sekali tiupan saja."},{"id":"14","ar":"فَاِذَا هُمْ بِالسَّاهِرَةِۗ","bc":"fa iżā hum bis-sāhirah","at":"Maka seketika itu mereka hidup kembali di bumi (yang baru)."},{"id":"15","ar":"هَلْ اَتٰىكَ حَدِيْثُ مُوْسٰىۘ","bc":"hal atāka ḥadīṡu mụsā","at":"Sudahkah sampai kepadamu (Muhammad) kisah Musa?"},{"id":"16","ar":"اِذْ نَادٰىهُ رَبُّهٗ بِالْوَادِ الْمُقَدَّسِ طُوًىۚ","bc":"iż nādāhu rabbuhụ bil-wādil-muqaddasi ṭuwā","at":"Ketika Tuhan memanggilnya (Musa) di lembah suci yaitu Lembah Tuwa;"},{"id":"17","ar":"اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰىۖ","bc":"iż-hab ilā fir'auna innahụ ṭagā","at":"pergilah engkau kepada Fir‘aun! Sesungguhnya dia telah melampaui batas,"},{"id":"18","ar":"فَقُلْ هَلْ لَّكَ اِلٰٓى اَنْ تَزَكّٰىۙ","bc":"fa qul hal laka ilā an tazakkā","at":"Maka katakanlah (kepada Fir‘aun), “Adakah keinginanmu untuk membersihkan diri (dari kesesatan),"},{"id":"19","ar":"وَاَهْدِيَكَ اِلٰى رَبِّكَ فَتَخْشٰىۚ","bc":"wa ahdiyaka ilā rabbika fa takhsyā","at":"dan engkau akan kupimpin ke jalan Tuhanmu agar engkau takut kepada-Nya?”"},{"id":"20","ar":"فَاَرٰىهُ الْاٰيَةَ الْكُبْرٰىۖ","bc":"fa arāhul-āyatal-kubrā","at":"Lalu (Musa) memperlihatkan kepadanya mukjizat yang besar."},{"id":"21","ar":"فَكَذَّبَ وَعَصٰىۖ","bc":"fa każżaba wa 'aṣā","at":"Tetapi dia (Fir‘aun) mendustakan dan mendurhakai."},{"id":"22","ar":"ثُمَّ اَدْبَرَ يَسْعٰىۖ","bc":"ṡumma adbara yas'ā","at":"Kemudian dia berpaling seraya berusaha menantang (Musa)."},{"id":"23","ar":"فَحَشَرَ فَنَادٰىۖ","bc":"fa ḥasyara fa nādā","at":"Kemudian dia mengumpulkan (pembesar-pembesarnya) lalu berseru (memanggil kaumnya)."},{"id":"24","ar":"فَقَالَ اَنَا۠ رَبُّكُمُ الْاَعْلٰىۖ","bc":"fa qāla ana rabbukumul-a'lā","at":"(Seraya) berkata, “Akulah tuhanmu yang paling tinggi.”"},{"id":"25","ar":"فَاَخَذَهُ اللّٰهُ نَكَالَ الْاٰخِرَةِ وَالْاُوْلٰىۗ","bc":"fa akhażahullāhu nakālal-ākhirati wal-ụlā","at":"Maka Allah menghukumnya dengan azab di akhirat dan siksaan di dunia."},{"id":"26","ar":"اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّمَنْ يَّخْشٰى ۗ","bc":"inna fī żālika la'ibratal limay yakhsyā","at":"Sungguh, pada yang demikian itu terdapat pelajaran bagi orang yang takut (kepada Allah)."},{"id":"27","ar":"ءَاَنْتُمْ اَشَدُّ خَلْقًا اَمِ السَّمَاۤءُ ۚ بَنٰىهَاۗ","bc":"a antum asyaddu khalqan amis-samā`, banāhā","at":"Apakah penciptaan kamu yang lebih hebat ataukah langit yang telah dibangun-Nya?"},{"id":"28","ar":"رَفَعَ سَمْكَهَا فَسَوّٰىهَاۙ","bc":"rafa'a samkahā fa sawwāhā","at":"Dia telah meninggikan bangunannya lalu menyempurnakannya,"},{"id":"29","ar":"وَاَغْطَشَ لَيْلَهَا وَاَخْرَجَ ضُحٰىهَاۖ","bc":"wa agṭasya lailahā wa akhraja ḍuḥāhā","at":"dan Dia menjadikan malamnya (gelap gulita), dan menjadikan siangnya (terang benderang)."},{"id":"30","ar":"وَالْاَرْضَ بَعْدَ ذٰلِكَ دَحٰىهَاۗ","bc":"wal-arḍa ba'da żālika daḥāhā","at":"Dan setelah itu bumi Dia hamparkan. "},{"id":"31","ar":"اَخْرَجَ مِنْهَا مَاۤءَهَا وَمَرْعٰىهَاۖ","bc":"akhraja min-hā mā`ahā wa mar'āhā","at":"Darinya Dia pancarkan mata air, dan (ditumbuhkan) tumbuh-tumbuhannya."},{"id":"32","ar":"وَالْجِبَالَ اَرْسٰىهَاۙ","bc":"wal-jibāla arsāhā","at":"Dan gunung-gunung Dia pancangkan dengan teguh."},{"id":"33","ar":"مَتَاعًا لَّكُمْ وَلِاَنْعَامِكُمْۗ","bc":"matā'al lakum wa li`an'āmikum","at":"(Semua itu) untuk kesenanganmu dan untuk hewan-hewan ternakmu."},{"id":"34","ar":"فَاِذَا جَاۤءَتِ الطَّاۤمَّةُ الْكُبْرٰىۖ","bc":"fa iżā jā`atiṭ-ṭāmmatul-kubrā","at":"Maka apabila malapetaka besar (hari Kiamat) telah datang,"},{"id":"35","ar":"يَوْمَ يَتَذَكَّرُ الْاِنْسَانُ مَا سَعٰىۙ","bc":"yauma yatażakkarul-insānu mā sa'ā","at":"yaitu pada hari (ketika) manusia teringat akan apa yang telah dikerjakannya,"},{"id":"36","ar":"وَبُرِّزَتِ الْجَحِيْمُ لِمَنْ يَّرٰى","bc":"wa burrizatil-jaḥīmu limay yarā","at":"dan neraka diperlihatkan dengan jelas kepada setiap orang yang melihat."},{"id":"37","ar":"فَاَمَّا مَنْ طَغٰىۖ","bc":"fa ammā man ṭagā","at":"Maka adapun orang yang melampaui batas,"},{"id":"38","ar":"وَاٰثَرَ الْحَيٰوةَ الدُّنْيَاۙ","bc":"wa āṡaral-ḥayātad-dun-yā","at":"dan lebih mengutamakan kehidupan dunia,"},{"id":"39","ar":"فَاِنَّ الْجَحِيْمَ هِيَ الْمَأْوٰىۗ","bc":"fa innal-jaḥīma hiyal-ma`wā","at":"maka sungguh, nerakalah tempat tinggalnya."},{"id":"40","ar":"وَاَمَّا مَنْ خَافَ مَقَامَ رَبِّهٖ وَنَهَى النَّفْسَ عَنِ الْهَوٰىۙ","bc":"wa ammā man khāfa maqāma rabbihī wa nahan-nafsa 'anil-hawā","at":"Dan adapun orang-orang yang takut kepada kebesaran Tuhannya dan menahan diri dari (keinginan) hawa nafsunya,"},{"id":"41","ar":"فَاِنَّ الْجَنَّةَ هِيَ الْمَأْوٰىۗ","bc":"fa innal-jannata hiyal-ma`wā","at":"maka sungguh, surgalah tempat tinggal(nya)."},{"id":"42","ar":"يَسْـَٔلُوْنَكَ عَنِ السَّاعَةِ اَيَّانَ مُرْسٰىهَاۗ","bc":"yas`alụnaka 'anis-sā'ati ayyāna mursāhā","at":"Mereka (orang-orang kafir) bertanya kepadamu (Muhammad) tentang hari Kiamat, “Kapankah terjadinya?” "},{"id":"43","ar":"فِيْمَ اَنْتَ مِنْ ذِكْرٰىهَاۗ","bc":"fīma anta min żikrāhā","at":"Untuk apa engkau perlu menyebutkannya (waktunya)?"},{"id":"44","ar":"اِلٰى رَبِّكَ مُنْتَهٰىهَاۗ","bc":"ilā rabbika muntahāhā","at":"Kepada Tuhanmulah (dikembalikan) kesudahannya (ketentuan waktunya)."},{"id":"45","ar":"اِنَّمَآ اَنْتَ مُنْذِرُ مَنْ يَّخْشٰىهَاۗ","bc":"innamā anta munżiru may yakhsyāhā","at":"Engkau (Muhammad) hanyalah pemberi peringatan bagi siapa yang takut kepadanya (hari Kiamat)."},{"id":"46","ar":"كَاَنَّهُمْ يَوْمَ يَرَوْنَهَا لَمْ يَلْبَثُوْٓا اِلَّا عَشِيَّةً اَوْ ضُحٰىهَا","bc":"ka`annahum yauma yaraunahā lam yalbaṡū illā 'asyiyyatan au ḍuḥāhā","at":"Pada hari ketika mereka melihat hari Kiamat itu (karena suasananya hebat), mereka merasa seakan-akan hanya (sebentar saja) tinggal (di dunia) pada waktu sore atau pagi hari."}],an_nisa=[{"id":"1","ar":"يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمُ الَّذِيْ خَلَقَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ وَّخَلَقَ مِنْهَا زَوْجَهَا وَبَثَّ مِنْهُمَا رِجَالًا كَثِيْرًا وَّنِسَاۤءً ۚ وَاتَّقُوا اللّٰهَ الَّذِيْ تَسَاۤءَلُوْنَ بِهٖ وَالْاَرْحَامَ ۗ اِنَّ اللّٰهَ كَانَ عَلَيْكُمْ رَقِيْبًا","bc":"yā ayyuhan-nāsuttaqụ rabbakumullażī khalaqakum min nafsiw wāḥidatiw wa khalaqa min-hā zaujahā wa baṡṡa min-humā rijālang kaṡīraw wa nisā`ā, wattaqullāhallażī tasā`alụna bihī wal-ar-ḥām, innallāha kāna 'alaikum raqībā","at":"Wahai manusia! Bertakwalah kepada Tuhanmu yang telah menciptakan kamu dari diri yang satu (Adam), dan (Allah) menciptakan pasangannya (Hawa) dari (diri)-nya; dan dari keduanya Allah memperkembangbiakkan laki-laki dan perempuan yang banyak. Bertakwalah kepada Allah yang dengan nama-Nya kamu saling meminta, dan (peliharalah) hubungan kekeluargaan. Sesungguhnya Allah selalu menjaga dan mengawasimu."},{"id":"2","ar":"وَاٰتُوا الْيَتٰمٰىٓ اَمْوَالَهُمْ وَلَا تَتَبَدَّلُوا الْخَبِيْثَ بِالطَّيِّبِ ۖ وَلَا تَأْكُلُوْٓا اَمْوَالَهُمْ اِلٰٓى اَمْوَالِكُمْ ۗ اِنَّهٗ كَانَ حُوْبًا كَبِيْرًا","bc":"wa ātul-yatāmā amwālahum wa lā tatabaddalul-khabīṡa biṭ-ṭayyibi wa lā ta`kulū amwālahum ilā amwālikum, innahụ kāna ḥụbang kabīrā","at":"Dan berikanlah kepada anak-anak yatim (yang sudah dewasa) harta mereka, janganlah kamu menukar yang baik dengan yang buruk, dan janganlah kamu makan harta mereka bersama hartamu. Sungguh, (tindakan menukar dan memakan) itu adalah dosa yang besar."},{"id":"3","ar":"وَاِنْ خِفْتُمْ اَلَّا تُقْسِطُوْا فِى الْيَتٰمٰى فَانْكِحُوْا مَا طَابَ لَكُمْ مِّنَ النِّسَاۤءِ مَثْنٰى وَثُلٰثَ وَرُبٰعَ ۚ فَاِنْ خِفْتُمْ اَلَّا تَعْدِلُوْا فَوَاحِدَةً اَوْ مَا مَلَكَتْ اَيْمَانُكُمْ ۗ ذٰلِكَ اَدْنٰٓى اَلَّا تَعُوْلُوْاۗ","bc":"wa in khiftum allā tuqsiṭụ fil-yatāmā fangkiḥụ mā ṭāba lakum minan-nisā`i maṡnā wa ṡulāṡa wa rubā', fa in khiftum allā ta'dilụ fa wāḥidatan au mā malakat aimānukum, żālika adnā allā ta'ụlụ","at":"Dan jika kamu khawatir tidak akan mampu berlaku adil terhadap (hak-hak) perempuan yatim (bilamana kamu menikahinya), maka nikahilah perempuan (lain) yang kamu senangi: dua, tiga atau empat. Tetapi jika kamu khawatir tidak akan mampu berlaku adil, maka (nikahilah) seorang saja, atau hamba sahaya perempuan yang kamu miliki. Yang demikian itu lebih dekat agar kamu tidak berbuat zalim."},{"id":"4","ar":"وَاٰتُوا النِّسَاۤءَ صَدُقٰتِهِنَّ نِحْلَةً ۗ فَاِنْ طِبْنَ لَكُمْ عَنْ شَيْءٍ مِّنْهُ نَفْسًا فَكُلُوْهُ هَنِيْۤـًٔا مَّرِيْۤـًٔا ","bc":"wa ātun-nisā`a ṣaduqātihinna niḥlah, fa in ṭibna lakum 'an syai`im min-hu nafsan fa kulụhu hanī`am marī`ā","at":"Dan berikanlah maskawin (mahar) kepada perempuan (yang kamu nikahi) sebagai pemberian yang penuh kerelaan. Kemudian, jika mereka menyerahkan kepada kamu sebagian dari (maskawin) itu dengan senang hati, maka terimalah dan nikmatilah pemberian itu dengan senang hati."},{"id":"5","ar":"وَلَا تُؤْتُوا السُّفَهَاۤءَ اَمْوَالَكُمُ الَّتِيْ جَعَلَ اللّٰهُ لَكُمْ قِيٰمًا وَّارْزُقُوْهُمْ فِيْهَا وَاكْسُوْهُمْ وَقُوْلُوْا لَهُمْ قَوْلًا مَّعْرُوْفًا","bc":"wa lā tu`tus-sufahā`a amwālakumullatī ja'alallāhu lakum qiyāmaw warzuqụhum fīhā waksụhum wa qụlụ lahum qaulam ma'rụfā","at":"Dan janganlah kamu serahkan kepada orang yang belum sempurna akalnya, harta (mereka yang ada dalam kekuasaan) kamu yang dijadikan Allah sebagai pokok kehidupan. Berilah mereka belanja dan pakaian (dari hasil harta itu) dan ucapkanlah kepada mereka perkataan yang baik."},{"id":"6","ar":"وَابْتَلُوا الْيَتٰمٰى حَتّٰىٓ اِذَا بَلَغُوا النِّكَاحَۚ فَاِنْ اٰنَسْتُمْ مِّنْهُمْ رُشْدًا فَادْفَعُوْٓا اِلَيْهِمْ اَمْوَالَهُمْ ۚ وَلَا تَأْكُلُوْهَآ اِسْرَافًا وَّبِدَارًا اَنْ يَّكْبَرُوْا ۗ وَمَنْ كَانَ غَنِيًّا فَلْيَسْتَعْفِفْ ۚ وَمَنْ كَانَ فَقِيْرًا فَلْيَأْكُلْ بِالْمَعْرُوْفِ ۗ فَاِذَا دَفَعْتُمْ اِلَيْهِمْ اَمْوَالَهُمْ فَاَشْهِدُوْا عَلَيْهِمْ ۗ وَكَفٰى بِاللّٰهِ حَسِيْبًا ","bc":"wabtalul-yatāmā ḥattā iżā balagun-nikāḥ, fa in ānastum min-hum rusydan fadfa'ū ilaihim amwālahum, wa lā ta`kulụhā isrāfaw wa bidāran ay yakbarụ, wa mang kāna ganiyyan falyasta'fif, wa mang kāna faqīran falya`kul bil-ma'rụf, fa iżā dafa'tum ilaihim amwālahum fa asy-hidụ 'alaihim, wa kafā billāhi ḥasībā","at":"Dan ujilah anak-anak yatim itu sampai mereka cukup umur untuk menikah. Kemudian jika menurut pendapatmu mereka telah cerdas (pandai memelihara harta), maka serahkanlah kepada mereka hartanya. Dan janganlah kamu memakannya (harta anak yatim) melebihi batas kepatutan dan (janganlah kamu) tergesa-gesa (menyerahkannya) sebelum mereka dewasa. Barangsiapa (di antara pemelihara itu) mampu, maka hendaklah dia menahan diri (dari memakan harta anak yatim itu) dan barangsiapa miskin, maka bolehlah dia makan harta itu menurut cara yang patut. Kemudian, apabila kamu menyerahkan harta itu kepada mereka, maka hendaklah kamu adakan saksi-saksi. Dan cukuplah Allah sebagai pengawas."},{"id":"7","ar":"لِلرِّجَالِ نَصِيْبٌ مِّمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَۖ وَلِلنِّسَاۤءِ نَصِيْبٌ مِّمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَ مِمَّا قَلَّ مِنْهُ اَوْ كَثُرَ ۗ نَصِيْبًا مَّفْرُوْضًا","bc":"lir-rijāli naṣībum mimmā tarakal-wālidāni wal-aqrabụna wa lin-nisā`i naṣībum mimmā tarakal-wālidāni wal-aqrabụna mimmā qalla min-hu au kaṡur, naṣībam mafrụḍā","at":"Bagi laki-laki ada hak bagian dari harta peninggalan kedua orang tua dan kerabatnya, dan bagi perempuan ada hak bagian (pula) dari harta peninggalan kedua orang tua dan kerabatnya, baik sedikit atau banyak menurut bagian yang telah ditetapkan."},{"id":"8","ar":"وَاِذَا حَضَرَ الْقِسْمَةَ اُولُوا الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنُ فَارْزُقُوْهُمْ مِّنْهُ وَقُوْلُوْا لَهُمْ قَوْلًا مَّعْرُوْفًا","bc":"wa iżā ḥaḍaral-qismata ulul-qurbā wal-yatāmā wal-masākīnu farzuqụhum min-hu wa qụlụ lahum qaulam ma'rụfā","at":"Dan apabila sewaktu pembagian itu hadir beberapa kerabat, anak-anak yatim dan orang-orang miskin, maka berilah mereka dari harta itu (sekedarnya) dan ucapkanlah kepada mereka perkataan yang baik."},{"id":"9","ar":"وَلْيَخْشَ الَّذِيْنَ لَوْ تَرَكُوْا مِنْ خَلْفِهِمْ ذُرِّيَّةً ضِعٰفًا خَافُوْا عَلَيْهِمْۖ فَلْيَتَّقُوا اللّٰهَ وَلْيَقُوْلُوْا قَوْلًا سَدِيْدًا","bc":"walyakhsyallażīna lau tarakụ min khalfihim żurriyyatan ḍi'āfan khāfụ 'alaihim falyattaqullāha walyaqụlụ qaulan sadīdā","at":"Dan hendaklah takut (kepada Allah) orang-orang yang sekiranya mereka meninggalkan keturunan yang lemah di belakang mereka yang mereka khawatir terhadap (kesejahteraan)nya. Oleh sebab itu, hendaklah mereka bertakwa kepada Allah, dan hendaklah mereka berbicara dengan tutur kata yang benar."},{"id":"10","ar":"اِنَّ الَّذِيْنَ يَأْكُلُوْنَ اَمْوَالَ الْيَتٰمٰى ظُلْمًا اِنَّمَا يَأْكُلُوْنَ فِيْ بُطُوْنِهِمْ نَارًا ۗ وَسَيَصْلَوْنَ سَعِيْرًا ","bc":"innallażīna ya`kulụna amwālal-yatāmā ẓulman innamā ya`kulụna fī buṭụnihim nārā, wa sayaṣlauna sa'īrā","at":"Sesungguhnya orang-orang yang memakan harta anak yatim secara zalim, sebenarnya mereka itu menelan api dalam perutnya dan mereka akan masuk ke dalam api yang menyala-nyala (neraka)."},{"id":"11","ar":"يُوْصِيْكُمُ اللّٰهُ فِيْٓ اَوْلَادِكُمْ لِلذَّكَرِ مِثْلُ حَظِّ الْاُنْثَيَيْنِ ۚ فَاِنْ كُنَّ نِسَاۤءً فَوْقَ اثْنَتَيْنِ فَلَهُنَّ ثُلُثَا مَا تَرَكَ ۚ وَاِنْ كَانَتْ وَاحِدَةً فَلَهَا النِّصْفُ ۗ وَلِاَبَوَيْهِ لِكُلِّ وَاحِدٍ مِّنْهُمَا السُّدُسُ مِمَّا تَرَكَ اِنْ كَانَ لَهٗ وَلَدٌ ۚ فَاِنْ لَّمْ يَكُنْ لَّهٗ وَلَدٌ وَّوَرِثَهٗٓ اَبَوٰهُ فَلِاُمِّهِ الثُّلُثُ ۚ فَاِنْ كَانَ لَهٗٓ اِخْوَةٌ فَلِاُمِّهِ السُّدُسُ مِنْۢ بَعْدِ وَصِيَّةٍ يُّوْصِيْ بِهَآ اَوْ دَيْنٍ ۗ اٰبَاۤؤُكُمْ وَاَبْنَاۤؤُكُمْۚ لَا تَدْرُوْنَ اَيُّهُمْ اَقْرَبُ لَكُمْ نَفْعًا ۗ فَرِيْضَةً مِّنَ اللّٰهِ ۗ اِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًا","bc":"yụṣīkumullāhu fī aulādikum liż-żakari miṡlu ḥaẓẓil-unṡayaīn, fa ing kunna nisā`an fauqaṡnataini fa lahunna ṡuluṡā mā tarak, wa ing kānat wāḥidatan fa lahan-niṣf, wa li`abawaihi likulli wāḥidim min-humas-sudusu mimmā taraka ing kāna lahụ walad, fa il lam yakul lahụ waladuw wa wariṡahū abawāhu fa li`ummihiṡ-ṡuluṡ, fa ing kāna lahū ikhwatun fa li`ummihis-sudusu mim ba'di waṣiyyatiy yụṣī bihā au daīn, ābā`ukum wa abnā`ukum, lā tadrụna ayyuhum aqrabu lakum naf'ā, farīḍatam minallāh, innallāha kāna 'alīman ḥakīmā","at":"Allah mensyariatkan (mewajibkan) kepadamu tentang (pembagian warisan untuk) anak-anakmu, (yaitu) bagian seorang anak laki-laki sama dengan bagian dua orang anak perempuan. Dan jika anak itu semuanya perempuan yang jumlahnya lebih dari dua, maka bagian mereka dua pertiga dari harta yang ditinggalkan. Jika dia (anak perempuan) itu seorang saja, maka dia memperoleh setengah (harta yang ditinggalkan). Dan untuk kedua ibu-bapak, bagian masing-masing seperenam dari harta yang ditinggalkan, jika dia (yang meninggal) mempunyai anak. Jika dia (yang meninggal) tidak mempunyai anak dan dia diwarisi oleh kedua ibu-bapaknya (saja), maka ibunya mendapat sepertiga. Jika dia (yang meninggal) mempunyai beberapa saudara, maka ibunya mendapat seperenam. (Pembagian-pembagian tersebut di atas) setelah (dipenuhi) wasiat yang dibuatnya atau (dan setelah dibayar) utangnya. (Tentang) orang tuamu dan anak-anakmu, kamu tidak mengetahui siapa di antara mereka yang lebih banyak manfaatnya bagimu. Ini adalah ketetapan Allah. Sungguh, Allah Maha Mengetahui, Mahabijaksana."},{"id":"12","ar":"۞ وَلَكُمْ نِصْفُ مَا تَرَكَ اَزْوَاجُكُمْ اِنْ لَّمْ يَكُنْ لَّهُنَّ وَلَدٌ ۚ فَاِنْ كَانَ لَهُنَّ وَلَدٌ فَلَكُمُ الرُّبُعُ مِمَّا تَرَكْنَ مِنْۢ بَعْدِ وَصِيَّةٍ يُّوْصِيْنَ بِهَآ اَوْ دَيْنٍ ۗ وَلَهُنَّ الرُّبُعُ مِمَّا تَرَكْتُمْ اِنْ لَّمْ يَكُنْ لَّكُمْ وَلَدٌ ۚ فَاِنْ كَانَ لَكُمْ وَلَدٌ فَلَهُنَّ الثُّمُنُ مِمَّا تَرَكْتُمْ مِّنْۢ بَعْدِ وَصِيَّةٍ تُوْصُوْنَ بِهَآ اَوْ دَيْنٍ ۗ وَاِنْ كَانَ رَجُلٌ يُّوْرَثُ كَلٰلَةً اَوِ امْرَاَةٌ وَّلَهٗٓ اَخٌ اَوْ اُخْتٌ فَلِكُلِّ وَاحِدٍ مِّنْهُمَا السُّدُسُۚ فَاِنْ كَانُوْٓا اَكْثَرَ مِنْ ذٰلِكَ فَهُمْ شُرَكَاۤءُ فِى الثُّلُثِ مِنْۢ بَعْدِ وَصِيَّةٍ يُّوْصٰى بِهَآ اَوْ دَيْنٍۙ غَيْرَ مُضَاۤرٍّ ۚ وَصِيَّةً مِّنَ اللّٰهِ ۗ وَاللّٰهُ عَلِيْمٌ حَلِيْمٌۗ ","bc":"wa lakum niṣfu mā taraka azwājukum il lam yakul lahunna walad, fa ing kāna lahunna waladun fa lakumur-rubu'u mimmā tarakna mim ba'di waṣiyyatiy yụṣīna bihā au daīn, wa lahunnar-rubu'u mimmā taraktum il lam yakul lakum walad, fa ing kāna lakum waladun fa lahunnaṡ-ṡumunu mimmā taraktum mim ba'di waṣiyyatin tụṣụna bihā au daīn, wa ing kāna rajuluy yụraṡu kalālatan awimra`atuw wa lahū akhun au ukhtun fa likulli wāḥidim min-humas-sudus, fa ing kānū akṡara min żālika fa hum syurakā`u fiṡ-ṡuluṡi mim ba'di waṣiyyatiy yụṣā bihā au dainin gaira muḍārr, waṣiyyatam minallāh, wallāhu 'alīmun ḥalīm","at":"Dan bagianmu (suami-suami) adalah seperdua dari harta yang ditinggalkan oleh istri-istrimu, jika mereka tidak mempunyai anak. Jika mereka (istri-istrimu) itu mempunyai anak, maka kamu mendapat seperempat dari harta yang ditinggalkannya setelah (dipenuhi) wasiat yang mereka buat atau (dan setelah dibayar) utangnya. Para istri memperoleh seperempat harta yang kamu tinggalkan jika kamu tidak mempunyai anak. Jika kamu mempunyai anak, maka para istri memperoleh seperdelapan dari harta yang kamu tinggalkan (setelah dipenuhi) wasiat yang kamu buat atau (dan setelah dibayar) utang-utangmu. Jika seseorang meninggal, baik laki-laki maupun perempuan yang tidak meninggalkan ayah dan tidak meninggalkan anak, tetapi mempunyai seorang saudara laki-laki (seibu) atau seorang saudara perempuan (seibu), maka bagi masing-masing dari kedua jenis saudara itu seperenam harta. Tetapi jika saudara-saudara seibu itu lebih dari seorang, maka mereka bersama-sama dalam bagian yang sepertiga itu, setelah (dipenuhi wasiat) yang dibuatnya atau (dan setelah dibayar) utangnya dengan tidak menyusahkan (kepada ahli waris). Demikianlah ketentuan Allah. Allah Maha Mengetahui, Maha Penyantun."},{"id":"13","ar":"تِلْكَ حُدُوْدُ اللّٰهِ ۗ وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ يُدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗ وَذٰلِكَ الْفَوْزُ الْعَظِيْمُ ","bc":"tilka ḥudụdullāh, wa may yuṭi'illāha wa rasụlahụ yudkhil-hu jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā, wa żālikal-fauzul-'aẓīm","at":"Itulah batas-batas (hukum) Allah. Barangsiapa taat kepada Allah dan Rasul-Nya, Dia akan memasukkannya ke dalam surga-surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Dan itulah kemenangan yang agung."},{"id":"14","ar":"وَمَنْ يَّعْصِ اللّٰهَ وَرَسُوْلَهٗ وَيَتَعَدَّ حُدُوْدَهٗ يُدْخِلْهُ نَارًا خَالِدًا فِيْهَاۖ وَلَهٗ عَذَابٌ مُّهِيْنٌ","bc":"wa may ya'ṣillāha wa rasụlahụ wa yata'adda ḥudụdahụ yudkhil-hu nāran khālidan fīhā wa lahụ 'ażābum muḥīn","at":"Dan barangsiapa mendurhakai Allah dan Rasul-Nya dan melanggar batas-batas hukum-Nya, niscaya Allah memasukkannya ke dalam api neraka, dia kekal di dalamnya dan dia akan mendapat azab yang menghinakan."},{"id":"15","ar":"وَالّٰتِيْ يَأْتِيْنَ الْفَاحِشَةَ مِنْ نِّسَاۤىِٕكُمْ فَاسْتَشْهِدُوْا عَلَيْهِنَّ اَرْبَعَةً مِّنْكُمْ ۚ فَاِنْ شَهِدُوْا فَاَمْسِكُوْهُنَّ فِى الْبُيُوْتِ حَتّٰى يَتَوَفّٰىهُنَّ الْمَوْتُ اَوْ يَجْعَلَ اللّٰهُ لَهُنَّ سَبِيْلًا ","bc":"wallātī ya`tīnal-fāḥisyata min nisā`ikum fastasy-hidụ 'alaihinna arba'atam mingkum, fa in syahidụ fa amsikụhunna fil-buyụti ḥattā yatawaffāhunnal-mautu au yaj'alallāhu lahunna sabīlā","at":"Dan para perempuan yang melakukan perbuatan keji di antara perempuan-perempuan kamu, hendaklah terhadap mereka ada empat orang saksi di antara kamu (yang menyaksikannya). Apabila mereka telah memberi kesaksian, maka kurunglah mereka (perempuan itu) dalam rumah sampai mereka menemui ajalnya, atau sampai Allah memberi jalan (yang lain) kepadanya."},{"id":"16","ar":"وَالَّذٰنِ يَأْتِيٰنِهَا مِنْكُمْ فَاٰذُوْهُمَا ۚ فَاِنْ تَابَا وَاَصْلَحَا فَاَعْرِضُوْا عَنْهُمَا ۗ اِنَّ اللّٰهَ كَانَ تَوَّابًا رَّحِيْمًا ","bc":"wallażāni ya`tiyānihā mingkum fa āżụhumā, fa in tābā wa aṣlaḥā fa a'riḍụ 'an-humā, innallāha kāna tawwābar raḥīmā","at":"Dan terhadap dua orang yang melakukan perbuatan keji di antara kamu, maka berilah hukuman kepada keduanya. Jika keduanya tobat dan memperbaiki diri, maka biarkanlah mereka. Sungguh, Allah Maha Penerima tobat, Maha Penyayang."},{"id":"17","ar":"اِنَّمَا التَّوْبَةُ عَلَى اللّٰهِ لِلَّذِيْنَ يَعْمَلُوْنَ السُّوْۤءَ بِجَهَالَةٍ ثُمَّ يَتُوْبُوْنَ مِنْ قَرِيْبٍ فَاُولٰۤىِٕكَ يَتُوْبُ اللّٰهُ عَلَيْهِمْ ۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا ","bc":"innamat-taubatu 'alallāhi lillażīna ya'malụnas-sū`a bijahālatin ṡumma yatụbụna ming qarībin fa ulā`ika yatụbullāhu 'alaihim, wa kānallāhu 'alīman ḥakīmā","at":"Sesungguhnya bertobat kepada Allah itu hanya (pantas) bagi mereka yang melakukan kejahatan karena tidak mengerti, kemudian segera bertobat. Tobat mereka itulah yang diterima Allah. Allah Maha Mengetahui, Mahabijaksana."},{"id":"18","ar":"وَلَيْسَتِ التَّوْبَةُ لِلَّذِيْنَ يَعْمَلُوْنَ السَّيِّاٰتِۚ حَتّٰىٓ اِذَا حَضَرَ اَحَدَهُمُ الْمَوْتُ قَالَ اِنِّيْ تُبْتُ الْـٰٔنَ وَلَا الَّذِيْنَ يَمُوْتُوْنَ وَهُمْ كُفَّارٌ ۗ اُولٰۤىِٕكَ اَعْتَدْنَا لَهُمْ عَذَابًا اَلِيْمًا ","bc":"wa laisatit-taubatu lillażīna ya'malụnas-sayyi`āt, ḥattā iżā ḥaḍara aḥadahumul-mautu qāla innī tubtul-āna wa lallażīna yamụtụna wa hum kuffār, ulā`ika a'tadnā lahum 'ażāban alīmā","at":"Dan tobat itu tidaklah (diterima Allah) dari mereka yang melakukan kejahatan hingga apabila datang ajal kepada seseorang di antara mereka, (barulah) dia mengatakan, “Saya benar-benar bertobat sekarang.” Dan tidak (pula diterima tobat) dari orang-orang yang meninggal sedang mereka di dalam kekafiran. Bagi orang-orang itu telah Kami sediakan azab yang pedih."},{"id":"19","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا يَحِلُّ لَكُمْ اَنْ تَرِثُوا النِّسَاۤءَ كَرْهًا ۗ وَلَا تَعْضُلُوْهُنَّ لِتَذْهَبُوْا بِبَعْضِ مَآ اٰتَيْتُمُوْهُنَّ اِلَّآ اَنْ يَّأْتِيْنَ بِفَاحِشَةٍ مُّبَيِّنَةٍ ۚ وَعَاشِرُوْهُنَّ بِالْمَعْرُوْفِ ۚ فَاِنْ كَرِهْتُمُوْهُنَّ فَعَسٰٓى اَنْ تَكْرَهُوْا شَيْـًٔا وَّيَجْعَلَ اللّٰهُ فِيْهِ خَيْرًا كَثِيْرًا ","bc":"yā ayyuhallażīna āmanụ lā yaḥillu lakum an tariṡun-nisā`a kar-hā, wa lā ta'ḍulụhunna litaż-habụ biba'ḍi mā ātaitumụhunna illā ay ya`tīna bifāḥisyatim mubayyinah, wa 'āsyirụhunna bil-ma'rụf, fa ing karihtumụhunna fa 'asā an takrahụ syai`aw wa yaj'alallāhu fīhi khairang kaṡīrā","at":"Wahai orang-orang beriman! Tidak halal bagi kamu mewarisi perempuan dengan jalan paksa dan janganlah kamu menyusahkan mereka karena hendak mengambil kembali sebagian dari apa yang telah kamu berikan kepadanya, kecuali apabila mereka melakukan perbuatan keji yang nyata. Dan bergaullah dengan mereka menurut cara yang patut. Jika kamu tidak menyukai mereka, (maka bersabarlah) karena boleh jadi kamu tidak menyukai sesuatu, padahal Allah menjadikan kebaikan yang banyak padanya."},{"id":"20","ar":"وَاِنْ اَرَدْتُّمُ اسْتِبْدَالَ زَوْجٍ مَّكَانَ زَوْجٍۙ وَّاٰتَيْتُمْ اِحْدٰىهُنَّ قِنْطَارًا فَلَا تَأْخُذُوْا مِنْهُ شَيْـًٔا ۗ اَتَأْخُذُوْنَهٗ بُهْتَانًا وَّاِثْمًا مُّبِيْنًا ","bc":"wa in arattumustibdāla zaujim makāna zaujiw wa ātaitum iḥdāhunna qinṭāran fa lā ta`khużụ min-hu syai`ā, a ta`khużụnahụ buhtānaw wa iṡmam mubīnā","at":"Dan jika kamu ingin mengganti istrimu dengan istri yang lain, sedang kamu telah memberikan kepada seorang di antara mereka harta yang banyak, maka janganlah kamu mengambil kembali sedikit pun darinya. Apakah kamu akan mengambilnya kembali dengan jalan tuduhan yang dusta dan dengan (menanggung) dosa yang nyata?"},{"id":"21","ar":"وَكَيْفَ تَأْخُذُوْنَهٗ وَقَدْ اَفْضٰى بَعْضُكُمْ اِلٰى بَعْضٍ وَّاَخَذْنَ مِنْكُمْ مِّيْثَاقًا غَلِيْظًا ","bc":"wa kaifa ta`khużụnahụ wa qad afḍā ba'ḍukum ilā ba'ḍiw wa akhażna mingkum mīṡāqan galīẓā","at":"Dan bagaimana kamu akan mengambilnya kembali, padahal kamu telah bergaul satu sama lain (sebagai suami-istri). Dan mereka (istri-istrimu) telah mengambil perjanjian yang kuat (ikatan pernikahan) dari kamu."},{"id":"22","ar":"وَلَا تَنْكِحُوْا مَا نَكَحَ اٰبَاۤؤُكُمْ مِّنَ النِّسَاۤءِ اِلَّا مَا قَدْ سَلَفَ ۗ اِنَّهٗ كَانَ فَاحِشَةً وَّمَقْتًاۗ وَسَاۤءَ سَبِيْلًا ","bc":"wa lā tangkiḥụ mā nakaḥa ābā`ukum minan-nisā`i illā mā qad salaf, innahụ kāna fāḥisyataw wa maqtā, wa sā`a sabīlā","at":"Dan janganlah kamu menikahi perempuan-perempuan yang telah dinikahi oleh ayahmu, kecuali (kejadian pada masa) yang telah lampau. Sungguh, perbuatan itu sangat keji dan dibenci (oleh Allah) dan seburuk-buruk jalan (yang ditempuh)."},{"id":"23","ar":"حُرِّمَتْ عَلَيْكُمْ اُمَّهٰتُكُمْ وَبَنٰتُكُمْ وَاَخَوٰتُكُمْ وَعَمّٰتُكُمْ وَخٰلٰتُكُمْ وَبَنٰتُ الْاَخِ وَبَنٰتُ الْاُخْتِ وَاُمَّهٰتُكُمُ الّٰتِيْٓ اَرْضَعْنَكُمْ وَاَخَوٰتُكُمْ مِّنَ الرَّضَاعَةِ وَاُمَّهٰتُ نِسَاۤىِٕكُمْ وَرَبَاۤىِٕبُكُمُ الّٰتِيْ فِيْ حُجُوْرِكُمْ مِّنْ نِّسَاۤىِٕكُمُ الّٰتِيْ دَخَلْتُمْ بِهِنَّۖ فَاِنْ لَّمْ تَكُوْنُوْا دَخَلْتُمْ بِهِنَّ فَلَا جُنَاحَ عَلَيْكُمْ ۖ وَحَلَاۤىِٕلُ اَبْنَاۤىِٕكُمُ الَّذِيْنَ مِنْ اَصْلَابِكُمْۙ وَاَنْ تَجْمَعُوْا بَيْنَ الْاُخْتَيْنِ اِلَّا مَا قَدْ سَلَفَ ۗ اِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًا ۔","bc":"ḥurrimat 'alaikum ummahātukum wa banatukum wa akhawātukum wa 'ammātukum wa khālātukum wa banatul-akhi wa banatul-ukhti wa ummahātukumullātī arḍa'nakum wa akhawātukum minar-raḍā'ati wa ummahātu nisā`ikum wa raba`ibukumullātī fī ḥujụrikum min-nisā`ikumullātī dakhaltum bihinna fa il lam takụnụ dakhaltum bihinna fa lā junāḥa 'alaikum wa ḥalā`ilu abnā`ikumullażīna min aṣlābikum wa an tajma'ụ bainal-ukhtaini illā mā qad salaf, innallāha kāna gafụrar raḥīmā","at":"Diharamkan atas kamu (menikahi) ibu-ibumu, anak-anakmu yang perempuan, saudara-saudaramu yang perempuan, saudara-saudara ayahmu yang perempuan, saudara-saudara ibumu yang perempuan, anak-anak perempuan dari saudara-saudaramu yang laki-laki, anak-anak perempuan dari saudara-saudaramu yang perempuan, ibu-ibumu yang menyusui kamu, saudara-saudara perempuanmu sesusuan, ibu-ibu istrimu (mertua), anak-anak perempuan dari istrimu (anak tiri) yang dalam pemeliharaanmu dari istri yang telah kamu campuri, tetapi jika kamu belum campur dengan istrimu itu (dan sudah kamu ceraikan), maka tidak berdosa kamu (menikahinya), (dan diharamkan bagimu) istri-istri anak kandungmu (menantu), dan (diharamkan) mengumpulkan (dalam pernikahan) dua perempuan yang bersaudara, kecuali yang telah terjadi pada masa lampau. Sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"24","ar":"۞ وَالْمُحْصَنٰتُ مِنَ النِّسَاۤءِ اِلَّا مَا مَلَكَتْ اَيْمَانُكُمْ ۚ كِتٰبَ اللّٰهِ عَلَيْكُمْ ۚ وَاُحِلَّ لَكُمْ مَّا وَرَاۤءَ ذٰلِكُمْ اَنْ تَبْتَغُوْا بِاَمْوَالِكُمْ مُّحْصِنِيْنَ غَيْرَ مُسَافِحِيْنَ ۗ فَمَا اسْتَمْتَعْتُمْ بِهٖ مِنْهُنَّ فَاٰتُوْهُنَّ اُجُوْرَهُنَّ فَرِيْضَةً ۗوَلَا جُنَاحَ عَلَيْكُمْ فِيْمَا تَرَاضَيْتُمْ بِهٖ مِنْۢ بَعْدِ الْفَرِيْضَةِۗ اِنَّ اللّٰهَ كَانَ عَلِيْمًا حَكِيْمًا","bc":"wal-muḥṣanātu minan-nisā`i illā mā malakat aimānukum, kitāballāhi 'alaikum, wa uḥilla lakum mā warā`a żālikum an tabtagụ bi`amwālikum muḥṣinīna gaira musāfiḥīn, fa mastamta'tum bihī min-hunna fa ātụhunna ujụrahunna farīḍah, wa lā junāḥa 'alaikum fīmā tarāḍaitum bihī mim ba'dil-farīḍah, innallāha kāna 'alīman ḥakīmā","at":"Dan (diharamkan juga kamu menikahi) perempuan yang bersuami, kecuali hamba sahaya perempuan (tawanan perang) yang kamu miliki sebagai ketetapan Allah atas kamu. Dan dihalalkan bagimu selain (perempuan-perempuan) yang demikian itu jika kamu berusaha dengan hartamu untuk menikahinya bukan untuk berzina. Maka karena kenikmatan yang telah kamu dapatkan dari mereka, berikanlah maskawinnya kepada mereka sebagai suatu kewajiban. Tetapi tidak mengapa jika ternyata di antara kamu telah saling merelakannya, setelah ditetapkan. Sungguh, Allah Maha Mengetahui, Mahabijaksana."},{"id":"25","ar":"وَمَنْ لَّمْ يَسْتَطِعْ مِنْكُمْ طَوْلًا اَنْ يَّنْكِحَ الْمُحْصَنٰتِ الْمُؤْمِنٰتِ فَمِنْ مَّا مَلَكَتْ اَيْمَانُكُمْ مِّنْ فَتَيٰتِكُمُ الْمُؤْمِنٰتِۗ وَاللّٰهُ اَعْلَمُ بِاِيْمَانِكُمْ ۗ بَعْضُكُمْ مِّنْۢ بَعْضٍۚ فَانْكِحُوْهُنَّ بِاِذْنِ اَهْلِهِنَّ وَاٰتُوْهُنَّ اُجُوْرَهُنَّ بِالْمَعْرُوْفِ مُحْصَنٰتٍ غَيْرَ مُسٰفِحٰتٍ وَّلَا مُتَّخِذٰتِ اَخْدَانٍ ۚ فَاِذَآ اُحْصِنَّ فَاِنْ اَتَيْنَ بِفَاحِشَةٍ فَعَلَيْهِنَّ نِصْفُ مَا عَلَى الْمُحْصَنٰتِ مِنَ الْعَذَابِۗ ذٰلِكَ لِمَنْ خَشِيَ الْعَنَتَ مِنْكُمْ ۗ وَاَنْ تَصْبِرُوْا خَيْرٌ لَّكُمْ ۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ","bc":"wa mal lam yastaṭi' mingkum ṭaulan ay yangkiḥal-muḥṣanātil-mu`mināti fa mimmā malakat aimānukum min fatayātikumul-mu`mināt, wallāhu a'lamu bi`īmānikum, ba'ḍukum mim ba'ḍ, fangkiḥụhunna bi`iżni ahlihinna wa ātụhunna ujụrahunna bil-ma'rụfi muḥṣanātin gaira musāfiḥātiw wa lā muttakhiżāti akhdān, fa iżā uḥṣinna fa in ataina bifāḥisyatin fa 'alaihinna niṣfu mā 'alal-muḥṣanāti minal-'ażāb, żālika liman khasyiyal-'anata mingkum, wa an taṣbirụ khairul lakum, wallāhu gafụrur raḥīm","at":"Dan barangsiapa di antara kamu tidak mempunyai biaya untuk menikahi perempuan merdeka yang beriman, maka (dihalalkan menikahi perempuan) yang beriman dari hamba sahaya yang kamu miliki. Allah mengetahui keimananmu. Sebagian dari kamu adalah dari sebagian yang lain (sama-sama keturunan Adam-Hawa), karena itu nikahilah mereka dengan izin tuannya dan berilah mereka maskawin yang pantas, karena mereka adalah perempuan-perempuan yang memelihara diri, bukan pezina dan bukan (pula) perempuan yang mengambil laki-laki lain sebagai piaraannya. Apabila mereka telah berumah tangga (bersuami), tetapi melakukan perbuatan keji (zina), maka (hukuman) bagi mereka setengah dari apa (hukuman) perempuan-perempuan merdeka (yang tidak bersuami). (Kebolehan menikahi hamba sahaya) itu, adalah bagi orang-orang yang takut terhadap kesulitan dalam menjaga diri (dari perbuatan zina). Tetapi jika kamu bersabar, itu lebih baik bagimu. Allah Maha Pengampun, Maha Penyayang."},{"id":"26","ar":"يُرِيْدُ اللّٰهُ لِيُبَيِّنَ لَكُمْ وَيَهْدِيَكُمْ سُنَنَ الَّذِيْنَ مِنْ قَبْلِكُمْ وَيَتُوْبَ عَلَيْكُمْ ۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ","bc":"yurīdullāhu liyubayyina lakum wa yahdiyakum sunanallażīna ming qablikum wa yatụba 'alaikum, wallāhu 'alīmun ḥakīm","at":"Allah hendak menerangkan (syariat-Nya) kepadamu, dan menunjukkan jalan-jalan (kehidupan) orang yang sebelum kamu (para nabi dan orang-orang saleh) dan Dia menerima tobatmu. Allah Maha Mengetahui, Mahabijaksana."},{"id":"27","ar":"وَاللّٰهُ يُرِيْدُ اَنْ يَّتُوْبَ عَلَيْكُمْ ۗ وَيُرِيْدُ الَّذِيْنَ يَتَّبِعُوْنَ الشَّهَوٰتِ اَنْ تَمِيْلُوْا مَيْلًا عَظِيْمًا","bc":"wallāhu yurīdu ay yatụba 'alaikum, wa yurīdullażīna yattabi'ụnasy-syahawāti an tamīlụ mailan 'aẓīmā","at":"Dan Allah hendak menerima tobatmu, sedang orang-orang yang mengikuti keinginannya menghendaki agar kamu berpaling sejauh-jauhnya (dari kebenaran)."},{"id":"28","ar":"يُرِيْدُ اللّٰهُ اَنْ يُّخَفِّفَ عَنْكُمْ ۚ وَخُلِقَ الْاِنْسَانُ ضَعِيْفًا ","bc":"yurīdullāhu ay yukhaffifa 'angkum, wa khuliqal-insānu ḍa'īfā","at":"Allah hendak memberikan keringanan kepadamu, karena manusia diciptakan (bersifat) lemah."},{"id":"29","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَأْكُلُوْٓا اَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ اِلَّآ اَنْ تَكُوْنَ تِجَارَةً عَنْ تَرَاضٍ مِّنْكُمْ ۗ وَلَا تَقْتُلُوْٓا اَنْفُسَكُمْ ۗ اِنَّ اللّٰهَ كَانَ بِكُمْ رَحِيْمًا ","bc":"yā ayyuhallażīna āmanụ lā ta`kulū amwālakum bainakum bil-bāṭili illā an takụna tijāratan 'an tarāḍim mingkum, wa lā taqtulū anfusakum, innallāha kāna bikum raḥīmā","at":"Wahai orang-orang yang beriman! Janganlah kamu saling memakan harta sesamamu dengan jalan yang batil (tidak benar), kecuali dalam perdagangan yang berlaku atas dasar suka sama suka di antara kamu. Dan janganlah kamu membunuh dirimu. Sungguh, Allah Maha Penyayang kepadamu."},{"id":"30","ar":"وَمَنْ يَّفْعَلْ ذٰلِكَ عُدْوَانًا وَّظُلْمًا فَسَوْفَ نُصْلِيْهِ نَارًا ۗوَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا ","bc":"wa may yaf'al żālika 'udwānaw wa ẓulman fa saufa nuṣlīhi nārā, wa kāna żālika 'alallāhi yasīrā","at":"Dan barangsiapa berbuat demikian dengan cara melanggar hukum dan zalim, akan Kami masukkan dia ke dalam neraka. Yang demikian itu mudah bagi Allah."},{"id":"31","ar":"اِنْ تَجْتَنِبُوْا كَبَاۤىِٕرَ مَا تُنْهَوْنَ عَنْهُ نُكَفِّرْ عَنْكُمْ سَيِّاٰتِكُمْ وَنُدْخِلْكُمْ مُّدْخَلًا كَرِيْمًا ","bc":"in tajtanibụ kabā`ira mā tun-hauna 'an-hu nukaffir 'angkum sayyi`ātikum wa nudkhilkum mudkhalang karīmā","at":"Jika kamu menjauhi dosa-dosa besar di antara dosa-dosa yang dilarang mengerjakannya, niscaya Kami hapus kesalahan-kesalahanmu dan akan Kami masukkan kamu ke tempat yang mulia (surga)."},{"id":"32","ar":"وَلَا تَتَمَنَّوْا مَا فَضَّلَ اللّٰهُ بِهٖ بَعْضَكُمْ عَلٰى بَعْضٍ ۗ لِلرِّجَالِ نَصِيْبٌ مِّمَّا اكْتَسَبُوْا ۗ وَلِلنِّسَاۤءِ نَصِيْبٌ مِّمَّا اكْتَسَبْنَ ۗوَسْـَٔلُوا اللّٰهَ مِنْ فَضْلِهٖ ۗ اِنَّ اللّٰهَ كَانَ بِكُلِّ شَيْءٍ عَلِيْمًا ","bc":"wa lā tatamannau mā faḍḍalallāhu bihī ba'ḍakum 'alā ba'ḍ, lir-rijāli naṣībum mimmaktasabụ, wa lin-nisā`i naṣībum mimmaktasabn, was`alullāha min faḍlih, innallāha kāna bikulli syai`in 'alīmā","at":"Dan janganlah kamu iri hati terhadap karunia yang telah dilebihkan Allah kepada sebagian kamu atas sebagian yang lain. (Karena) bagi laki-laki ada bagian dari apa yang mereka usahakan, dan bagi perempuan (pun) ada bagian dari apa yang mereka usahakan. Mohonlah kepada Allah sebagian dari karunia-Nya. Sungguh, Allah Maha Mengetahui segala sesuatu."},{"id":"33","ar":"وَلِكُلٍّ جَعَلْنَا مَوَالِيَ مِمَّا تَرَكَ الْوَالِدٰنِ وَالْاَقْرَبُوْنَ ۗ وَالَّذِيْنَ عَقَدَتْ اَيْمَانُكُمْ فَاٰتُوْهُمْ نَصِيْبَهُمْ ۗ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ شَهِيْدًا ","bc":"wa likullin ja'alnā mawāliya mimmā tarakal-wālidāni wal-aqrabụn, wallażīna 'aqadat aimānukum fa ātụhum naṣībahum, innallāha kāna 'alā kulli syai`in syahīdā","at":"Dan untuk masing-masing (laki-laki dan perempuan) Kami telah menetapkan para ahli waris atas apa yang ditinggalkan oleh kedua orang tuanya dan karib kerabatnya. Dan orang-orang yang kamu telah bersumpah setia dengan mereka, maka berikanlah kepada mereka bagiannya. Sungguh, Allah Maha Menyaksikan segala sesuatu."},{"id":"34","ar":"اَلرِّجَالُ قَوَّامُوْنَ عَلَى النِّسَاۤءِ بِمَا فَضَّلَ اللّٰهُ بَعْضَهُمْ عَلٰى بَعْضٍ وَّبِمَآ اَنْفَقُوْا مِنْ اَمْوَالِهِمْ ۗ فَالصّٰلِحٰتُ قٰنِتٰتٌ حٰفِظٰتٌ لِّلْغَيْبِ بِمَا حَفِظَ اللّٰهُ ۗوَالّٰتِيْ تَخَافُوْنَ نُشُوْزَهُنَّ فَعِظُوْهُنَّ وَاهْجُرُوْهُنَّ فِى الْمَضَاجِعِ وَاضْرِبُوْهُنَّ ۚ فَاِنْ اَطَعْنَكُمْ فَلَا تَبْغُوْا عَلَيْهِنَّ سَبِيْلًا ۗاِنَّ اللّٰهَ كَانَ عَلِيًّا كَبِيْرًا ","bc":"ar-rijālu qawwāmụna 'alan-nisā`i bimā faḍḍalallāhu ba'ḍahum 'alā ba'ḍiw wa bimā anfaqụ min amwālihim, faṣ-ṣāliḥātu qānitātun ḥāfiẓātul lil-gaibi bimā ḥafiẓallāh, wallātī takhāfụna nusyụzahunna fa'iẓụhunna wahjurụhunna fil-maḍāji'i waḍribụhunn, fa in aṭa'nakum fa lā tabgụ 'alaihinna sabīlā, innallāha kāna 'aliyyang kabīrā","at":"Laki-laki (suami) itu pelindung bagi perempuan (istri), karena Allah telah melebihkan sebagian mereka (laki-laki) atas sebagian yang lain (perempuan), dan karena mereka (laki-laki) telah memberikan nafkah dari hartanya. Maka perempuan-perempuan yang saleh adalah mereka yang taat (kepada Allah) dan menjaga diri ketika (suaminya) tidak ada, karena Allah telah menjaga (mereka). Perempuan-perempuan yang kamu khawatirkan akan nusyuz, hendaklah kamu beri nasihat kepada mereka, tinggalkanlah mereka di tempat tidur (pisah ranjang), dan (kalau perlu) pukullah mereka. Tetapi jika mereka menaatimu, maka janganlah kamu mencari-cari alasan untuk menyusahkannya. Sungguh, Allah Mahatinggi, Mahabesar."},{"id":"35","ar":"وَاِنْ خِفْتُمْ شِقَاقَ بَيْنِهِمَا فَابْعَثُوْا حَكَمًا مِّنْ اَهْلِهٖ وَحَكَمًا مِّنْ اَهْلِهَا ۚ اِنْ يُّرِيْدَآ اِصْلَاحًا يُّوَفِّقِ اللّٰهُ بَيْنَهُمَا ۗ اِنَّ اللّٰهَ كَانَ عَلِيْمًا خَبِيْرًا ","bc":"wa in khiftum syiqāqa bainihimā fab'aṡụ ḥakamam min ahlihī wa ḥakamam min ahlihā, iy yurīdā iṣlāḥay yuwaffiqillāhu bainahumā, innallāha kāna 'alīman khabīrā","at":"Dan jika kamu khawatir terjadi persengketaan antara keduanya, maka kirimlah seorang juru damai dari keluarga laki-laki dan seorang juru damai dari keluarga perempuan. Jika keduanya (juru damai itu) bermaksud mengadakan perbaikan, niscaya Allah memberi taufik kepada suami-istri itu. Sungguh, Allah Mahateliti, Maha Mengenal."},{"id":"36","ar":"۞ وَاعْبُدُوا اللّٰهَ وَلَا تُشْرِكُوْا بِهٖ شَيْـًٔا وَّبِالْوَالِدَيْنِ اِحْسَانًا وَّبِذِى الْقُرْبٰى وَالْيَتٰمٰى وَالْمَسٰكِيْنِ وَالْجَارِ ذِى الْقُرْبٰى وَالْجَارِ الْجُنُبِ وَالصَّاحِبِ بِالْجَنْۢبِ وَابْنِ السَّبِيْلِۙ وَمَا مَلَكَتْ اَيْمَانُكُمْ ۗ اِنَّ اللّٰهَ لَا يُحِبُّ مَنْ كَانَ مُخْتَالًا فَخُوْرًاۙ","bc":"wa'budullāha wa lā tusyrikụ bihī syai`aw wa bil-wālidaini iḥsānaw wa biżil-qurbā wal-yatāmā wal-masākīni wal-jāri żil-qurbā wal-jāril-junubi waṣ-ṣāḥibi bil-jambi wabnis-sabīli wa mā malakat aimānukum, innallāha lā yuḥibbu mang kāna mukhtālan fakhụrā","at":"Dan sembahlah Allah dan janganlah kamu mempersekutukan-Nya dengan sesuatu apa pun. Dan berbuat-baiklah kepada kedua orang tua, karib-kerabat, anak-anak yatim, orang-orang miskin, tetangga dekat dan tetangga jauh, teman sejawat, ibnu sabil dan hamba sahaya yang kamu miliki. Sungguh, Allah tidak menyukai orang yang sombong dan membanggakan diri,"},{"id":"37","ar":" ۨالَّذِيْنَ يَبْخَلُوْنَ وَيَأْمُرُوْنَ النَّاسَ بِالْبُخْلِ وَيَكْتُمُوْنَ مَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۗ وَاَعْتَدْنَا لِلْكٰفِرِيْنَ عَذَابًا مُّهِيْنًاۚ","bc":"allażīna yabkhalụna wa ya`murụnan-nāsa bil-bukhli wa yaktumụna mā ātāhumullāhu min faḍlih, wa a'tadnā lil-kāfirīna 'ażābam muhīnā","at":"(yaitu) orang yang kikir, dan menyuruh orang lain berbuat kikir, dan menyembunyikan karunia yang telah diberikan Allah kepadanya. Kami telah menyediakan untuk orang-orang kafir azab yang menghinakan."},{"id":"38","ar":"وَالَّذِيْنَ يُنْفِقُوْنَ اَمْوَالَهُمْ رِئَاۤءَ النَّاسِ وَلَا يُؤْمِنُوْنَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِ ۗ وَمَنْ يَّكُنِ الشَّيْطٰنُ لَهٗ قَرِيْنًا فَسَاۤءَ قَرِيْنًا ","bc":"wallażīna yunfiqụna amwālahum ri`ā`an-nāsi wa lā yu`minụna billāhi wa lā bil-yaumil-ākhir, wa may yakunisy-syaiṭānu lahụ qarīnan fa sā`a qarīnā","at":"Dan (juga) orang-orang yang menginfakkan hartanya karena ria dan kepada orang lain (ingin dilihat dan dipuji), dan orang-orang yang tidak beriman kepada Allah dan kepada hari kemudian. Barangsiapa menjadikan setan sebagai temannya, maka (ketahuilah) dia (setan itu) adalah teman yang sangat jahat."},{"id":"39","ar":"وَمَاذَا عَلَيْهِمْ لَوْ اٰمَنُوْا بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَنْفَقُوْا مِمَّا رَزَقَهُمُ اللّٰهُ ۗوَكَانَ اللّٰهُ بِهِمْ عَلِيْمًا ","bc":"wa māżā 'alaihim lau āmanụ billāhi wal-yaumil-ākhiri wa anfaqụ mimmā razaqahumullāh, wa kānallāhu bihim 'alīmā","at":"Dan apa (keberatan) bagi mereka jika mereka beriman kepada Allah dan hari kemudian dan menginfakkan sebagian rezeki yang telah diberikan Allah kepadanya? Dan Allah Maha Mengetahui keadaan mereka."},{"id":"40","ar":"اِنَّ اللّٰهَ لَا يَظْلِمُ مِثْقَالَ ذَرَّةٍ ۚوَاِنْ تَكُ حَسَنَةً يُّضٰعِفْهَا وَيُؤْتِ مِنْ لَّدُنْهُ اَجْرًا عَظِيْمًا","bc":"innallāha lā yaẓlimu miṡqāla żarrah, wa in taku ḥasanatay yuḍā'if-hā wa yu`ti mil ladun-hu ajran 'aẓīmā","at":"Sungguh, Allah tidak akan menzalimi seseorang walaupun sebesar dzarrah, dan jika ada kebajikan (sekecil dzarrah), niscaya Allah akan melipatgandakannya dan memberikan pahala yang besar dari sisi-Nya."},{"id":"41","ar":"فَكَيْفَ اِذَا جِئْنَا مِنْ كُلِّ اُمَّةٍۢ بِشَهِيْدٍ وَّجِئْنَا بِكَ عَلٰى هٰٓؤُلَاۤءِ شَهِيْدًاۗ","bc":"fa kaifa iżā ji`nā ming kulli ummatim bisyahīdiw wa ji`nā bika 'alā hā`ulā`i syahīdā","at":"Dan bagaimanakah (keadaan orang kafir nanti), jika Kami mendatangkan seorang saksi (Rasul) dari setiap umat dan Kami mendatangkan engkau (Muhammad) sebagai saksi atas mereka."},{"id":"42","ar":"يَوْمَىِٕذٍ يَّوَدُّ الَّذِيْنَ كَفَرُوْا وَعَصَوُا الرَّسُوْلَ لَوْ تُسَوّٰى بِهِمُ الْاَرْضُۗ وَلَا يَكْتُمُوْنَ اللّٰهَ حَدِيْثًا","bc":"yauma`iżiy yawaddullażīna kafarụ wa 'aṣawur-rasụla lau tusawwā bihimul-arḍ, wa lā yaktumụnallāha ḥadīṡā","at":"Pada hari itu, orang yang kafir dan orang yang mendurhakai Rasul (Muhammad), berharap sekiranya mereka diratakan dengan tanah (dikubur atau hancur luluh menjadi tanah), padahal mereka tidak dapat menyembunyikan sesuatu kejadian apa pun dari Allah."},{"id":"43","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَقْرَبُوا الصَّلٰوةَ وَاَنْتُمْ سُكَارٰى حَتّٰى تَعْلَمُوْا مَا تَقُوْلُوْنَ وَلَا جُنُبًا اِلَّا عَابِرِيْ سَبِيْلٍ حَتّٰى تَغْتَسِلُوْا ۗوَاِنْ كُنْتُمْ مَّرْضٰٓى اَوْ عَلٰى سَفَرٍ اَوْ جَاۤءَ اَحَدٌ مِّنْكُمْ مِّنَ الْغَاۤىِٕطِ اَوْ لٰمَسْتُمُ النِّسَاۤءَ فَلَمْ تَجِدُوْا مَاۤءً فَتَيَمَّمُوْا صَعِيْدًا طَيِّبًا فَامْسَحُوْا بِوُجُوْهِكُمْ وَاَيْدِيْكُمْ ۗ اِنَّ اللّٰهَ كَانَ عَفُوًّا غَفُوْرًا","bc":"yā ayyuhallażīna āmanụ lā taqrabuṣ-ṣalāta wa antum sukārā ḥattā ta'lamụ mā taqụlụna wa lā junuban illā 'ābirī sabīlin ḥattā tagtasilụ, wa ing kuntum marḍā au 'alā safarin au jā`a aḥadum mingkum minal-gā`iṭi au lāmastumun-nisā`a fa lam tajidụ mā`an fa tayammamụ ṣa'īdan ṭayyiban famsaḥụ biwujụhikum wa aidīkum, innallāha kāna 'afuwwan gafụrā","at":"Wahai orang yang beriman! Janganlah kamu mendekati salat ketika kamu dalam keadaan mabuk, sampai kamu sadar apa yang kamu ucapkan, dan jangan pula (kamu hampiri masjid ketika kamu) dalam keadaan junub kecuali sekedar melewati jalan saja, sebelum kamu mandi (mandi junub). Adapun jika kamu sakit atau sedang dalam perjalanan atau sehabis buang air atau kamu telah menyentuh perempuan, sedangkan kamu tidak mendapat air, maka bertayamumlah kamu dengan debu yang baik (suci); usaplah wajahmu dan tanganmu dengan (debu) itu. Sungguh, Allah Maha Pemaaf, Maha Pengampun."},{"id":"44","ar":"اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يَشْتَرُوْنَ الضَّلٰلَةَ وَيُرِيْدُوْنَ اَنْ تَضِلُّوا السَّبِيْلَۗ","bc":"a lam tara ilallażīna ụtụ naṣībam minal-kitābi yasytarụnaḍ-ḍalālata wa yurīdụna an taḍillus-sabīl","at":"Tidakkah kamu memperhatikan orang yang telah diberi bagian Kitab (Taurat)? Mereka membeli kesesatan dan mereka menghendaki agar kamu tersesat (menyimpang) dari jalan (yang benar)."},{"id":"45","ar":"وَاللّٰهُ اَعْلَمُ بِاَعْدَاۤىِٕكُمْ ۗوَكَفٰى بِاللّٰهِ وَلِيًّا ۙوَّكَفٰى بِاللّٰهِ نَصِيْرًا ","bc":"wallāhu a'lamu bi`a'dā`ikum, wa kafā billāhi waliyyaw wa kafā billāhi naṣīrā","at":"Dan Allah lebih mengetahui tentang musuh-musuhmu. Cukuplah Allah menjadi pelindung dan cukuplah Allah menjadi penolong (bagimu)."},{"id":"46","ar":"مِنَ الَّذِيْنَ هَادُوْا يُحَرِّفُوْنَ الْكَلِمَ عَنْ مَّوَاضِعِهٖ وَيَقُوْلُوْنَ سَمِعْنَا وَعَصَيْنَا وَاسْمَعْ غَيْرَ مُسْمَعٍ وَّرَاعِنَا لَيًّاۢ بِاَلْسِنَتِهِمْ وَطَعْنًا فِى الدِّيْنِۗ وَلَوْ اَنَّهُمْ قَالُوْا سَمِعْنَا وَاَطَعْنَا وَاسْمَعْ وَانْظُرْنَا لَكَانَ خَيْرًا لَّهُمْ وَاَقْوَمَۙ وَلٰكِنْ لَّعَنَهُمُ اللّٰهُ بِكُفْرِهِمْ فَلَا يُؤْمِنُوْنَ اِلَّا قَلِيْلًا ","bc":"minallażīna hādụ yuḥarrifụnal-kalima 'am mawāḍi'ihī wa yaqụlụna sami'nā wa 'aṣainā wasma' gaira musma'iw wa rā'inā layyam bi`alsinatihim wa ṭa'nan fid-dīn, walau annahum qālụ sami'nā wa aṭa'nā wasma' wanẓurnā lakāna khairal lahum wa aqwama wa lākil la'anahumullāhu bikufrihim fa lā yu`minụna illā qalīlā","at":"(Yaitu) di antara orang Yahudi, yang mengubah perkataan dari tempat-tempatnya. Dan mereka berkata, “Kami mendengar, tetapi kami tidak mau menurutinya.” Dan (mereka mengatakan pula), “Dengarlah,” sedang (engkau Muhammad sebenarnya) tidak mendengar apa pun. Dan (mereka mengatakan), “Raa‘ina” dengan memutar-balikkan lidahnya dan mencela agama. Sekiranya mereka mengatakan, “Kami mendengar dan patuh, dan dengarlah, dan perhatikanlah kami,” tentulah itu lebih baik bagi mereka dan lebih tepat, tetapi Allah melaknat mereka, karena kekafiran mereka. Mereka tidak beriman kecuali sedikit sekali."},{"id":"47","ar":"يٰٓاَيُّهَا الَّذِيْنَ اُوْتُوا الْكِتٰبَ اٰمِنُوْا بِمَا نَزَّلْنَا مُصَدِّقًا لِّمَا مَعَكُمْ مِّنْ قَبْلِ اَنْ نَّطْمِسَ وُجُوْهًا فَنَرُدَّهَا عَلٰٓى اَدْبَارِهَآ اَوْ نَلْعَنَهُمْ كَمَا لَعَنَّآ اَصْحٰبَ السَّبْتِ ۗ وَكَانَ اَمْرُ اللّٰهِ مَفْعُوْلًا","bc":"yā ayyuhallażīna ụtul-kitāba āminụ bimā nazzalnā muṣaddiqal limā ma'akum ming qabli an naṭmisa wujụhan fa naruddahā 'alā adbārihā au nal'anahum kamā la'annā aṣ-ḥābas-sabt, wa kāna amrullāhi maf'ụlā","at":"Wahai orang-orang yang telah diberi Kitab! Berimanlah kamu kepada apa yang telah Kami turunkan (Al-Qur'an) yang membenarkan Kitab yang ada pada kamu, sebelum Kami mengubah wajah-wajah(mu), lalu Kami putar ke belakang atau Kami laknat mereka sebagaimana Kami melaknat orang-orang (yang berbuat maksiat) pada hari Sabat (Sabtu). Dan ketetapan Allah pasti berlaku."},{"id":"48","ar":"اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُّشْرَكَ بِهٖ وَيَغْفِرُ مَا دُوْنَ ذٰلِكَ لِمَنْ يَّشَاۤءُ ۚ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَقَدِ افْتَرٰٓى اِثْمًا عَظِيْمًا ","bc":"innallāha lā yagfiru ay yusyraka bihī wa yagfiru mā dụna żālika limay yasyā`, wa may yusyrik billāhi fa qadiftarā iṡman 'aẓīmā","at":"Sesungguhnya Allah tidak akan mengampuni (dosa) karena mempersekutukan-Nya (syirik), dan Dia mengampuni apa (dosa) yang selain (syirik) itu bagi siapa yang Dia kehendaki. Barangsiapa mempersekutukan Allah, maka sungguh, dia telah berbuat dosa yang besar."},{"id":"49","ar":"اَلَمْ تَرَ اِلَى الَّذِيْنَ يُزَكُّوْنَ اَنْفُسَهُمْ ۗ بَلِ اللّٰهُ يُزَكِّيْ مَنْ يَّشَاۤءُ وَلَا يُظْلَمُوْنَ فَتِيْلًا ","bc":"a lam tara ilallażīna yuzakkụna anfusahum, balillāhu yuzakkī may yasyā`u wa lā yuẓlamụna fatīlā","at":"Tidakkah engkau memperhatikan orang-orang yang menganggap dirinya suci (orang Yahudi dan Nasrani)? Sebenarnya Allah menyucikan siapa yang Dia kehendaki dan mereka tidak dizalimi sedikit pun."},{"id":"50","ar":"اُنْظُرْ كَيْفَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَۗ وَكَفٰى بِهٖٓ اِثْمًا مُّبِيْنًا ","bc":"unẓur kaifa yaftarụna 'alallāhil-każib, wa kafā bihī iṡmam mubīnā","at":"Perhatikanlah, betapa mereka mengada-adakan dusta terhadap Allah! Dan cukuplah perbuatan itu menjadi dosa yang nyata (bagi mereka)."},{"id":"51","ar":"اَلَمْ تَرَ اِلَى الَّذِيْنَ اُوْتُوْا نَصِيْبًا مِّنَ الْكِتٰبِ يُؤْمِنُوْنَ بِالْجِبْتِ وَالطَّاغُوْتِ وَيَقُوْلُوْنَ لِلَّذِيْنَ كَفَرُوْا هٰٓؤُلَاۤءِ اَهْدٰى مِنَ الَّذِيْنَ اٰمَنُوْا سَبِيْلًا ","bc":"a lam tara ilallażīna ụtụ naṣībam minal-kitābi yu`minụna bil-jibti waṭ-ṭāgụti wa yaqụlụna lillażīna kafarụ hā`ulā`i ahdā minallażīna āmanụ sabīlā","at":"Tidakkah kamu memperhatikan orang-orang yang diberi bagian dari Kitab (Taurat)? Mereka percaya kepada Jibt dan thaghut, dan mengatakan kepada orang-orang kafir (musyrik Mekah), bahwa mereka itu lebih benar jalannya daripada orang-orang yang beriman."},{"id":"52","ar":"اُولٰۤىِٕكَ الَّذِيْنَ لَعَنَهُمُ اللّٰهُ ۗوَمَنْ يَّلْعَنِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ نَصِيْرًا ","bc":"ulā`ikallażīna la'anahumullāh, wa may yal'anillāhu fa lan tajida lahụ naṣīrā","at":"Mereka itulah orang-orang yang dilaknat Allah. Dan barangsiapa dilaknat Allah, niscaya engkau tidak akan mendapatkan penolong baginya."},{"id":"53","ar":"اَمْ لَهُمْ نَصِيْبٌ مِّنَ الْمُلْكِ فَاِذًا لَّا يُؤْتُوْنَ النَّاسَ نَقِيْرًاۙ","bc":"am lahum naṣībum minal-mulki fa iżal lā yu`tụnan-nāsa naqīrā","at":"Ataukah mereka mempunyai bagian dari kerajaan (kekuasaan), meskipun mereka tidak akan memberikan sedikit pun (kebajikan) kepada manusia,"},{"id":"54","ar":"اَمْ يَحْسُدُوْنَ النَّاسَ عَلٰى مَآ اٰتٰىهُمُ اللّٰهُ مِنْ فَضْلِهٖۚ فَقَدْ اٰتَيْنَآ اٰلَ اِبْرٰهِيْمَ الْكِتٰبَ وَالْحِكْمَةَ وَاٰتَيْنٰهُمْ مُّلْكًا عَظِيْمًا ","bc":"am yaḥsudụnan-nāsa 'alā mā ātāhumullāhu min faḍlih, fa qad ātainā āla ibrāhīmal-kitāba wal-ḥikmata wa ātaināhum mulkan 'aẓīmā","at":"ataukah mereka dengki kepada manusia (Muhammad) karena karunia yang telah diberikan Allah kepadanya? Sungguh, Kami telah memberikan Kitab dan Hikmah kepada keluarga Ibrahim, dan Kami telah memberikan kepada mereka kerajaan (kekuasaan) yang besar."},{"id":"55","ar":"فَمِنْهُمْ مَّنْ اٰمَنَ بِهٖ وَمِنْهُمْ مَّنْ صَدَّ عَنْهُ ۗ وَكَفٰى بِجَهَنَّمَ سَعِيْرًا","bc":"fa min-hum man āmana bihī wa min-hum man ṣadda 'an-h, wa kafā bijahannama sa'īrā","at":"Maka di antara mereka (yang dengki itu), ada yang beriman kepadanya dan ada pula yang menghalangi (manusia beriman) kepadanya. Cukuplah (bagi mereka) neraka Jahanam yang menyala-nyala apinya."},{"id":"56","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا بِاٰيٰتِنَا سَوْفَ نُصْلِيْهِمْ نَارًاۗ كُلَّمَا نَضِجَتْ جُلُوْدُهُمْ بَدَّلْنٰهُمْ جُلُوْدًا غَيْرَهَا لِيَذُوْقُوا الْعَذَابَۗ اِنَّ اللّٰهَ كَانَ عَزِيْزًا حَكِيْمًا ","bc":"innallażīna kafarụ bi`āyātinā saufa nuṣlīhim nārā, kullamā naḍijat julụduhum baddalnāhum julụdan gairahā liyażụqul-'ażāb, innallāha kāna 'azīzan ḥakīmā","at":"Sungguh, orang-orang yang kafir kepada ayat-ayat Kami, kelak akan Kami masukkan ke dalam neraka. Setiap kali kulit mereka hangus, Kami ganti dengan kulit yang lain, agar mereka merasakan azab. Sungguh, Allah Maha-perkasa, Mahabijaksana."},{"id":"57","ar":"وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَنُدْخِلُهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ لَهُمْ فِيْهَآ اَزْوَاجٌ مُّطَهَّرَةٌ ۙ وَّنُدْخِلُهُمْ ظِلًّا ظَلِيْلًا ","bc":"wallażīna āmanụ wa 'amiluṣ-ṣāliḥāti sanudkhiluhum jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, lahum fīhā azwājum muṭahharatuw wa nudkhiluhum ẓillan ẓalīlā","at":"Adapun orang-orang yang beriman dan mengerjakan kebajikan, kelak akan Kami masukkan ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya selama-lamanya. Di sana mereka mempunyai pasangan-pasangan yang suci, dan Kami masukkan mereka ke tempat yang teduh lagi nyaman."},{"id":"58","ar":"۞ اِنَّ اللّٰهَ يَأْمُرُكُمْ اَنْ تُؤَدُّوا الْاَمٰنٰتِ اِلٰٓى اَهْلِهَاۙ وَاِذَا حَكَمْتُمْ بَيْنَ النَّاسِ اَنْ تَحْكُمُوْا بِالْعَدْلِ ۗ اِنَّ اللّٰهَ نِعِمَّا يَعِظُكُمْ بِهٖ ۗ اِنَّ اللّٰهَ كَانَ سَمِيْعًاۢ بَصِيْرًا","bc":"innallāha ya`murukum an tu`addul-amānāti ilā ahlihā wa iżā ḥakamtum bainan-nāsi an taḥkumụ bil-'adl, innallāha ni'immā ya'iẓukum bih, innallāha kāna samī'am baṣīrā","at":"Sungguh, Allah menyuruhmu menyampaikan amanat kepada yang berhak menerimanya, dan apabila kamu menetapkan hukum di antara manusia hendaknya kamu menetapkannya dengan adil. Sungguh, Allah sebaik-baik yang memberi pengajaran kepadamu. Sungguh, Allah Maha Mendengar, Maha Melihat."},{"id":"59","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَ وَاُولِى الْاَمْرِ مِنْكُمْۚ فَاِنْ تَنَازَعْتُمْ فِيْ شَيْءٍ فَرُدُّوْهُ اِلَى اللّٰهِ وَالرَّسُوْلِ اِنْ كُنْتُمْ تُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ ذٰلِكَ خَيْرٌ وَّاَحْسَنُ تَأْوِيْلًا ","bc":"yā ayyuhallażīna āmanū aṭī'ullāha wa aṭī'ur-rasụla wa ulil-amri mingkum, fa in tanāza'tum fī syai`in fa ruddụhu ilallāhi war-rasụli ing kuntum tu`minụna billāhi wal-yaumil-ākhir, żālika khairuw wa aḥsanu ta`wīlā","at":"Wahai orang-orang yang beriman! Taatilah Allah dan taatilah Rasul (Muhammad), dan Ulil Amri (pemegang kekuasaan) di antara kamu. Kemudian, jika kamu berbeda pendapat tentang sesuatu, maka kembalikanlah kepada Allah (Al-Qur'an) dan Rasul (sunnahnya), jika kamu beriman kepada Allah dan hari kemudian. Yang demikian itu lebih utama (bagimu) dan lebih baik akibatnya."},{"id":"60","ar":"اَلَمْ تَرَ اِلَى الَّذِيْنَ يَزْعُمُوْنَ اَنَّهُمْ اٰمَنُوْا بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ يُرِيْدُوْنَ اَنْ يَّتَحَاكَمُوْٓا اِلَى الطَّاغُوْتِ وَقَدْ اُمِرُوْٓا اَنْ يَّكْفُرُوْا بِهٖ ۗوَيُرِيْدُ الشَّيْطٰنُ اَنْ يُّضِلَّهُمْ ضَلٰلًا ۢ بَعِيْدًا","bc":"a lam tara ilallażīna yaz'umụna annahum āmanụ bimā unzila ilaika wa mā unzila ming qablika yurīdụna ay yataḥākamū ilaṭ-ṭāgụti wa qad umirū ay yakfurụ bih, wa yurīdusy-syaiṭānu ay yuḍillahum ḍalālam ba'īdā","at":"Tidakkah engkau (Muhammad) memperhatikan orang-orang yang mengaku bahwa mereka telah beriman kepada apa yang diturunkan kepadamu dan kepada apa yang diturunkan sebelummu? Tetapi mereka masih menginginkan ketetapan hukum kepada thaghut, padahal mereka telah diperintahkan untuk mengingkari thaghut itu. Dan setan bermaksud menyesatkan mereka (dengan) kesesatan yang sejauh-jauhnya."},{"id":"61","ar":"وَاِذَا قِيْلَ لَهُمْ تَعَالَوْا اِلٰى مَآ اَنْزَلَ اللّٰهُ وَاِلَى الرَّسُوْلِ رَاَيْتَ الْمُنٰفِقِيْنَ يَصُدُّوْنَ عَنْكَ صُدُوْدًاۚ","bc":"wa iżā qīla lahum ta'ālau ilā mā anzalallāhu wa ilar-rasụli ra`aital-munāfiqīna yaṣuddụna 'angka ṣudụdā","at":"Dan apabila dikatakan kepada mereka, “Marilah (patuh) kepada apa yang telah diturunkan Allah dan (patuh) kepada Rasul,” (niscaya) engkau (Muhammad) melihat orang munafik menghalangi dengan keras darimu."},{"id":"62","ar":"فَكَيْفَ اِذَآ اَصَابَتْهُمْ مُّصِيْبَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ ثُمَّ جَاۤءُوْكَ يَحْلِفُوْنَ بِاللّٰهِ ۖاِنْ اَرَدْنَآ اِلَّآ اِحْسَانًا وَّتَوْفِيْقًا ","bc":"fa kaifa iżā aṣābat-hum muṣībatum bimā qaddamat aidīhim ṡumma jā`ụka yaḥlifụna billāhi in aradnā illā iḥsānaw wa taufīqā","at":"Maka bagaimana halnya apabila (kelak) musibah menimpa mereka (orang munafik) disebabkan perbuatan tangannya sendiri, kemudian mereka datang kepadamu (Muhammad) sambil bersumpah, “Demi Allah, kami sekali-kali tidak menghendaki selain kebaikan dan kedamaian.”"},{"id":"63","ar":"اُولٰۤىِٕكَ الَّذِيْنَ يَعْلَمُ اللّٰهُ مَا فِيْ قُلُوْبِهِمْ فَاَعْرِضْ عَنْهُمْ وَعِظْهُمْ وَقُلْ لَّهُمْ فِيْٓ اَنْفُسِهِمْ قَوْلًا ۢ بَلِيْغًا ","bc":"ulā`ikallażīna ya'lamullāhu mā fī qulụbihim fa a'riḍ 'an-hum wa'iẓ-hum wa qul lahum fī anfusihim qaulam balīgā","at":"Mereka itu adalah orang-orang yang (sesungguhnya) Allah mengetahui apa yang ada di dalam hatinya. Karena itu berpalinglah kamu dari mereka, dan berilah mereka nasihat, dan katakanlah kepada mereka perkataan yang membekas pada jiwanya."},{"id":"64","ar":"وَمَآ اَرْسَلْنَا مِنْ رَّسُوْلٍ اِلَّا لِيُطَاعَ بِاِذْنِ اللّٰهِ ۗوَلَوْ اَنَّهُمْ اِذْ ظَّلَمُوْٓا اَنْفُسَهُمْ جَاۤءُوْكَ فَاسْتَغْفَرُوا اللّٰهَ وَاسْتَغْفَرَ لَهُمُ الرَّسُوْلُ لَوَجَدُوا اللّٰهَ تَوَّابًا رَّحِيْمًا ","bc":"wa mā arsalnā mir rasụlin illā liyuṭā'a bi`iżnillāh, walau annahum iż ẓalamū anfusahum jā`ụka fastagfarullāha wastagfara lahumur-rasụlu lawajadullāha tawwābar raḥīmā","at":"Dan Kami tidak mengutus seorang rasul melainkan untuk ditaati dengan izin Allah. Dan sungguh, sekiranya mereka setelah menzalimi dirinya datang kepadamu (Muhammad), lalu memohon ampunan kepada Allah, dan Rasul pun memohonkan ampunan untuk mereka, niscaya mereka mendapati Allah Maha Penerima tobat, Maha Penyayang."},{"id":"65","ar":"فَلَا وَرَبِّكَ لَا يُؤْمِنُوْنَ حَتّٰى يُحَكِّمُوْكَ فِيْمَا شَجَرَ بَيْنَهُمْ ثُمَّ لَا يَجِدُوْا فِيْٓ اَنْفُسِهِمْ حَرَجًا مِّمَّا قَضَيْتَ وَيُسَلِّمُوْا تَسْلِيْمًا ","bc":"fa lā wa rabbika lā yu`minụna ḥattā yuḥakkimụka fīmā syajara bainahum ṡumma lā yajidụ fī anfusihim ḥarajam mimmā qaḍaita wa yusallimụ taslīmā","at":"Maka demi Tuhanmu, mereka tidak beriman sebelum mereka menjadikan engkau (Muhammad) sebagai hakim dalam perkara yang mereka perselisihkan, (sehingga) kemudian tidak ada rasa keberatan dalam hati mereka terhadap putusan yang engkau berikan, dan mereka menerima dengan sepenuhnya."},{"id":"66","ar":"وَلَوْ اَنَّا كَتَبْنَا عَلَيْهِمْ اَنِ اقْتُلُوْٓا اَنْفُسَكُمْ اَوِ اخْرُجُوْا مِنْ دِيَارِكُمْ مَّا فَعَلُوْهُ اِلَّا قَلِيْلٌ مِّنْهُمْ ۗوَلَوْ اَنَّهُمْ فَعَلُوْا مَا يُوْعَظُوْنَ بِهٖ لَكَانَ خَيْرًا لَّهُمْ وَاَشَدَّ تَثْبِيْتًاۙ","bc":"walau annā katabnā 'alaihim aniqtulū anfusakum awikhrujụ min diyārikum mā fa'alụhu illā qalīlum min-hum, walau annahum fa'alụ mā yụ'aẓụna bihī lakāna khairal lahum wa asyadda taṡbītā","at":"Dan sekalipun telah Kami perintahkan kepada mereka, “Bunuhlah dirimu atau keluarlah kamu dari kampung halamanmu,” ternyata mereka tidak akan melakukannya, kecuali sebagian kecil dari mereka. Dan sekiranya mereka benar-benar melaksanakan perintah yang diberikan, niscaya itu lebih baik bagi mereka dan lebih menguatkan (iman mereka),"},{"id":"67","ar":"وَّاِذًا لَّاٰ تَيْنٰهُمْ مِّنْ لَّدُنَّآ اَجْرًا عَظِيْمًاۙ","bc":"wa iżal la`ātaināhum mil ladunnā ajran 'aẓīmā","at":"dan dengan demikian, pasti Kami berikan kepada mereka pahala yang besar dari sisi Kami,"},{"id":"68","ar":"وَّلَهَدَيْنٰهُمْ صِرَاطًا مُّسْتَقِيْمًا ","bc":"wa lahadaināhum ṣirāṭam mustaqīmā","at":"dan pasti Kami tunjukkan kepada mereka jalan yang lurus."},{"id":"69","ar":"وَمَنْ يُّطِعِ اللّٰهَ وَالرَّسُوْلَ فَاُولٰۤىِٕكَ مَعَ الَّذِيْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِّنَ النَّبِيّٖنَ وَالصِّدِّيْقِيْنَ وَالشُّهَدَاۤءِ وَالصّٰلِحِيْنَ ۚ وَحَسُنَ اُولٰۤىِٕكَ رَفِيْقًا ","bc":"wa may yuṭi'illāha war-rasụla fa ulā`ika ma'allażīna an'amallāhu 'alaihim minan-nabiyyīna waṣ-ṣiddīqīna wasy-syuhadā`i waṣ-ṣāliḥīn, wa ḥasuna ulā`ika rafīqā","at":"Dan barangsiapa menaati Allah dan Rasul (Muhammad), maka mereka itu akan bersama-sama dengan orang yang diberikan nikmat oleh Allah, (yaitu) para nabi, para pencinta kebenaran, orang-orang yang mati syahid dan orang-orang saleh. Mereka itulah teman yang sebaik-baiknya."},{"id":"70","ar":"ذٰلِكَ الْفَضْلُ مِنَ اللّٰهِ ۗوَكَفٰى بِاللّٰهِ عَلِيْمًا","bc":"żālikal-faḍlu minallāh, wa kafā billāhi 'alīmā","at":"Yang demikian itu adalah karunia dari Allah, dan cukuplah Allah yang Maha Mengetahui."},{"id":"71","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا خُذُوْا حِذْرَكُمْ فَانْفِرُوْا ثُبَاتٍ اَوِ انْفِرُوْا جَمِيْعًا ","bc":"yā ayyuhallażīna āmanụ khużụ ḥiżrakum fanfirụ ṡubātin awinfirụ jamī'ā","at":"Wahai orang-orang yang beriman! Bersiapsiagalah kamu, dan majulah (ke medan pertempuran) secara berkelompok, atau majulah bersama-sama (serentak)."},{"id":"72","ar":"وَاِنَّ مِنْكُمْ لَمَنْ لَّيُبَطِّئَنَّۚ فَاِنْ اَصَابَتْكُمْ مُّصِيْبَةٌ قَالَ قَدْ اَنْعَمَ اللّٰهُ عَلَيَّ اِذْ لَمْ اَكُنْ مَّعَهُمْ شَهِيْدًا ","bc":"wa inna mingkum lamal layubaṭṭi`ann, fa in aṣābatkum muṣībatung qāla qad an'amallāhu 'alayya iż lam akum ma'ahum syahīdā","at":"Dan sesungguhnya di antara kamu pasti ada orang yang sangat enggan (ke medan pertempuran). Lalu jika kamu ditimpa musibah dia berkata, “Sungguh, Allah telah memberikan nikmat kepadaku karena aku tidak ikut berperang bersama mereka.”"},{"id":"73","ar":"وَلَىِٕنْ اَصَابَكُمْ فَضْلٌ مِّنَ اللّٰهِ لَيَقُوْلَنَّ كَاَنْ لَّمْ تَكُنْۢ بَيْنَكُمْ وَبَيْنَهٗ مَوَدَّةٌ يّٰلَيْتَنِيْ كُنْتُ مَعَهُمْ فَاَفُوْزَ فَوْزًا عَظِيْمًا ","bc":"wa la`in aṣābakum faḍlum minallāhi layaqụlanna ka`al lam takum bainakum wa bainahụ mawaddatuy yā laitanī kuntu ma'ahum fa afụza fauzan 'aẓīmā","at":"Dan sungguh, jika kamu mendapat karunia (kemenangan) dari Allah, tentulah dia mengatakan seakan-akan belum pernah ada hubungan kasih sayang antara kamu dengan dia, “Wahai, sekiranya aku bersama mereka, tentu aku akan memperoleh kemenangan yang agung (pula).”"},{"id":"74","ar":"۞ فَلْيُقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ الَّذِيْنَ يَشْرُوْنَ الْحَيٰوةَ الدُّنْيَا بِالْاٰخِرَةِ ۗ وَمَنْ يُّقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ فَيُقْتَلْ اَوْ يَغْلِبْ فَسَوْفَ نُؤْتِيْهِ اَجْرًا عَظِيْمًا ","bc":"falyuqātil fī sabīlillāhillażīna yasyrụnal-ḥayātad-dun-yā bil-ākhirah, wa may yuqātil fī sabīlillāhi fa yuqtal au yaglib fa saufa nu`tīhi ajran 'aẓīmā","at":"Karena itu, hendaklah orang-orang yang menjual kehidupan dunia untuk (kehidupan) akhirat berperang di jalan Allah. Dan barangsiapa berperang di jalan Allah, lalu gugur atau memperoleh kemenangan maka akan Kami berikan pahala yang besar kepadanya."},{"id":"75","ar":"وَمَا لَكُمْ لَا تُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ وَالْمُسْتَضْعَفِيْنَ مِنَ الرِّجَالِ وَالنِّسَاۤءِ وَالْوِلْدَانِ الَّذِيْنَ يَقُوْلُوْنَ رَبَّنَآ اَخْرِجْنَا مِنْ هٰذِهِ الْقَرْيَةِ الظَّالِمِ اَهْلُهَاۚ وَاجْعَلْ لَّنَا مِنْ لَّدُنْكَ وَلِيًّاۚ وَاجْعَلْ لَّنَا مِنْ لَّدُنْكَ نَصِيْرًا ","bc":"wa mā lakum lā tuqātilụna fī sabīlillāhi wal-mustaḍ'afīna minar-rijāli wan-nisā`i wal-wildānillażīna yaqụlụna rabbanā akhrijnā min hāżihil-qaryatiẓ-ẓālimi ahluhā, waj'al lanā mil ladungka waliyyā, waj'al lanā mil ladungka naṣīrā","at":"Dan mengapa kamu tidak mau berperang di jalan Allah dan (membela) orang yang lemah, baik laki-laki, perempuan maupun anak-anak yang berdoa, “Ya Tuhan kami, keluarkanlah kami dari negeri ini (Mekah) yang penduduknya zalim. Berilah kami pelindung dari sisi-Mu, dan berilah kami penolong dari sisi-Mu.”"},{"id":"76","ar":"اَلَّذِيْنَ اٰمَنُوْا يُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ ۚ وَالَّذِيْنَ كَفَرُوْا يُقَاتِلُوْنَ فِيْ سَبِيْلِ الطَّاغُوْتِ فَقَاتِلُوْٓا اَوْلِيَاۤءَ الشَّيْطٰنِ ۚ اِنَّ كَيْدَ الشَّيْطٰنِ كَانَ ضَعِيْفًا ۚ ","bc":"allażīna āmanụ yuqātilụna fī sabīlillāh, wallażīna kafarụ yuqātilụna fī sabīliṭ-ṭāgụti fa qātilū auliyā`asy-syaiṭān, inna kaidasy-syaiṭāni kāna ḍa'īfā","at":"Orang-orang yang beriman berperang di jalan Allah, dan orang-orang yang kafir berperang di jalan thaghut, maka perangilah kawan-kawan setan itu, (karena) sesungguhnya tipu daya setan itu lemah."},{"id":"77","ar":"اَلَمْ تَرَ اِلَى الَّذِيْنَ قِيْلَ لَهُمْ كُفُّوْٓا اَيْدِيَكُمْ وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَۚ فَلَمَّا كُتِبَ عَلَيْهِمُ الْقِتَالُ اِذَا فَرِيْقٌ مِّنْهُمْ يَخْشَوْنَ النَّاسَ كَخَشْيَةِ اللّٰهِ اَوْ اَشَدَّ خَشْيَةً ۚ وَقَالُوْا رَبَّنَا لِمَ كَتَبْتَ عَلَيْنَا الْقِتَالَۚ لَوْلَآ اَخَّرْتَنَآ اِلٰٓى اَجَلٍ قَرِيْبٍۗ قُلْ مَتَاعُ الدُّنْيَا قَلِيْلٌۚ وَالْاٰخِرَةُ خَيْرٌ لِّمَنِ اتَّقٰىۗ وَلَا تُظْلَمُوْنَ فَتِيْلًا ","bc":"a lam tara ilallażīna qīla lahum kuffū aidiyakum wa aqīmuṣ-ṣalāta wa ātuz-zakāh, fa lammā kutiba 'alaihimul-qitālu iżā farīqum min-hum yakhsyaunan-nāsa kakhasy-yatillāhi au asyadda khasy-yah, wa qālụ rabbanā lima katabta 'alainal-qitāl, lau lā akhkhartanā ilā ajaling qarīb, qul matā'ud-dun-yā qalīl, wal-ākhiratu khairul limanittaqā, wa lā tuẓlamụna fatīlā","at":"Tidakkah engkau memperhatikan orang-orang yang dikatakan kepada mereka, ”Tahanlah tanganmu (dari berperang), laksanakanlah salat dan tunaikanlah zakat!” Ketika mereka diwajibkan berperang, tiba-tiba sebagian mereka (golongan munafik) takut kepada manusia (musuh), seperti takutnya kepada Allah, bahkan lebih takut (dari itu). Mereka berkata, “Ya Tuhan kami, mengapa Engkau wajibkan berperang kepada kami? Mengapa tidak Engkau tunda (kewajiban berperang) kepada kami beberapa waktu lagi?” Katakanlah, “Kesenangan di dunia ini hanya sedikit dan akhirat itu lebih baik bagi orang-orang yang bertakwa (mendapat pahala turut berperang) dan kamu tidak akan dizalimi sedikit pun.”"},{"id":"78","ar":"اَيْنَمَا تَكُوْنُوْا يُدْرِكْكُّمُ الْمَوْتُ وَلَوْ كُنْتُمْ فِيْ بُرُوْجٍ مُّشَيَّدَةٍ ۗ وَاِنْ تُصِبْهُمْ حَسَنَةٌ يَّقُوْلُوْا هٰذِهٖ مِنْ عِنْدِ اللّٰهِ ۚ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ يَّقُوْلُوْا هٰذِهٖ مِنْ عِنْدِكَ ۗ قُلْ كُلٌّ مِّنْ عِنْدِ اللّٰهِ ۗ فَمَالِ هٰٓؤُلَاۤءِ الْقَوْمِ لَا يَكَادُوْنَ يَفْقَهُوْنَ حَدِيْثًا ","bc":"aina mā takụnụ yudrikkumul-mautu walau kuntum fī burụjim musyayyadah, wa in tuṣib-hum ḥasanatuy yaqụlụ hāżihī min 'indillāh, wa in tuṣib-hum sayyi`atuy yaqụlụ hāżihī min 'indik, qul kullum min 'indillāh, fa māli hā`ulā`il-qaumi lā yakādụna yafqahụna ḥadīṡā","at":"Di manapun kamu berada, kematian akan mendapatkan kamu, kendatipun kamu berada di dalam benteng yang tinggi dan kukuh. Jika mereka memperoleh kebaikan, mereka mengatakan, “Ini dari sisi Allah,” dan jika mereka ditimpa suatu keburukan, mereka mengatakan, “Ini dari engkau (Muham-mad).” Katakanlah, “Semuanya (datang) dari sisi Allah.” Maka mengapa orang-orang itu (orang-orang munafik) hampir-hampir tidak memahami pembicaraan (sedikit pun)?”"},{"id":"79","ar":"مَآ اَصَابَكَ مِنْ حَسَنَةٍ فَمِنَ اللّٰهِ ۖ وَمَآ اَصَابَكَ مِنْ سَيِّئَةٍ فَمِنْ نَّفْسِكَ ۗ وَاَرْسَلْنٰكَ لِلنَّاسِ رَسُوْلًا ۗ وَكَفٰى بِاللّٰهِ شَهِيْدًا ","bc":"mā aṣābaka min ḥasanatin fa minallāhi wa mā aṣābaka min sayyi`atin fa min nafsik, wa arsalnāka lin-nāsi rasụlā, wa kafā billāhi syahīdā","at":"Kebajikan apa pun yang kamu peroleh, adalah dari sisi Allah, dan keburukan apa pun yang menimpamu, itu dari (kesalahan) dirimu sendiri. Kami mengutusmu (Muhammad) menjadi Rasul kepada (seluruh) manusia. Dan cukuplah Allah yang menjadi saksi."},{"id":"80","ar":"مَنْ يُّطِعِ الرَّسُوْلَ فَقَدْ اَطَاعَ اللّٰهَ ۚ وَمَنْ تَوَلّٰى فَمَآ اَرْسَلْنٰكَ عَلَيْهِمْ حَفِيْظًا ۗ ","bc":"may yuṭi'ir-rasụla fa qad aṭā'allāh, wa man tawallā fa mā arsalnāka 'alaihim ḥafīẓā","at":"Barangsiapa menaati Rasul (Muhammad), maka sesungguhnya dia telah menaati Allah. Dan barangsiapa berpaling (dari ketaatan itu), maka (ketahuilah) Kami tidak mengutusmu (Muhammad) untuk menjadi pemelihara mereka."},{"id":"81","ar":"وَيَقُوْلُوْنَ طَاعَةٌ ۖ فَاِذَا بَرَزُوْا مِنْ عِنْدِكَ بَيَّتَ طَاۤىِٕفَةٌ مِّنْهُمْ غَيْرَ الَّذِيْ تَقُوْلُ ۗ وَاللّٰهُ يَكْتُبُ مَا يُبَيِّتُوْنَ ۚ فَاَعْرِضْ عَنْهُمْ وَتَوَكَّلْ عَلَى اللّٰهِ ۗ وَكَفٰى بِاللّٰهِ وَكِيْلًا ","bc":"wa yaqụlụna ṭā'atun fa iżā barazụ min 'indika bayyata ṭā`ifatum min-hum gairallażī taqụl, wallāhu yaktubu mā yubayyitụn, fa a'riḍ 'an-hum wa tawakkal 'alallāh, wa kafā billāhi wakīlā","at":"Dan mereka (orang-orang munafik) mengatakan, “(Kewajiban kami hanyalah) taat.” Tetapi, apabila mereka telah pergi dari sisimu (Muhammad), sebagian dari mereka mengatur siasat di malam hari (mengambil keputusan) lain dari yang telah mereka katakan tadi. Allah mencatat siasat yang mereka atur di malam hari itu, maka berpalinglah dari mereka dan bertawakallah kepada Allah. Cukuplah Allah yang menjadi pelindung."},{"id":"82","ar":"اَفَلَا يَتَدَبَّرُوْنَ الْقُرْاٰنَ ۗ وَلَوْ كَانَ مِنْ عِنْدِ غَيْرِ اللّٰهِ لَوَجَدُوْا فِيْهِ اخْتِلَافًا كَثِيْرًا ","bc":"a fa lā yatadabbarụnal-qur`ān, walau kāna min 'indi gairillāhi lawajadụ fīhikhtilāfang kaṡīrā","at":"Maka tidakkah mereka menghayati (mendalami) Al-Qur'an? Sekiranya (Al-Qur'an) itu bukan dari Allah, pastilah mereka menemukan banyak hal yang bertentangan di dalamnya."},{"id":"83","ar":"وَاِذَا جَاۤءَهُمْ اَمْرٌ مِّنَ الْاَمْنِ اَوِ الْخَوْفِ اَذَاعُوْا بِهٖ ۗ وَلَوْ رَدُّوْهُ اِلَى الرَّسُوْلِ وَاِلٰٓى اُولِى الْاَمْرِ مِنْهُمْ لَعَلِمَهُ الَّذِيْنَ يَسْتَنْۢبِطُوْنَهٗ مِنْهُمْ ۗ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ لَاتَّبَعْتُمُ الشَّيْطٰنَ اِلَّا قَلِيْلًا ","bc":"wa iżā jā`ahum amrum minal-amni awil-khaufi ażā'ụ bih, walau raddụhu ilar-rasụli wa ilā ulil-amri min-hum la'alimahullażīna yastambiṭụnahụ min-hum, walau lā faḍlullāhi 'alaikum wa raḥmatuhụ lattaba'tumusy-syaiṭāna illā qalīlā","at":"Dan apabila sampai kepada mereka suatu berita tentang keamanan ataupun ketakutan, mereka (langsung) menyiarkannya. (Padahal) apabila mereka menyerahkannya kepada Rasul dan Ulil Amri di antara mereka, tentulah orang-orang yang ingin mengetahui kebenarannya (akan dapat) mengetahuinya (secara resmi) dari mereka (Rasul dan Ulil Amri). Sekiranya bukan karena karunia dan rahmat Allah kepadamu, tentulah kamu mengikuti setan, kecuali sebagian kecil saja (di antara kamu)."},{"id":"84","ar":"فَقَاتِلْ فِيْ سَبِيْلِ اللّٰهِ ۚ لَا تُكَلَّفُ اِلَّا نَفْسَكَ وَحَرِّضِ الْمُؤْمِنِيْنَ ۚ عَسَى اللّٰهُ اَنْ يَّكُفَّ بَأْسَ الَّذِيْنَ كَفَرُوْا ۗوَاللّٰهُ اَشَدُّ بَأْسًا وَّاَشَدُّ تَنْكِيْلًا ","bc":"fa qātil fī sabīlillāh, lā tukallafu illā nafsaka wa ḥarriḍil-mu`minīn, 'asallāhu ay yakuffa ba`sallażīna kafarụ, wallāhu asyaddu ba`saw wa asyaddu tangkīlā","at":"Maka berperanglah engkau (Muhammad) di jalan Allah, engkau tidaklah dibebani melainkan atas dirimu sendiri. Kobarkanlah (semangat) orang-orang beriman (untuk berperang). Mudah-mudahan Allah menolak (mematahkan) serangan orang-orang yang kafir itu. Allah sangat besar kekuatan(-Nya) dan sangat keras siksaan(-Nya)."},{"id":"85","ar":"مَنْ يَّشْفَعْ شَفَاعَةً حَسَنَةً يَّكُنْ لَّهٗ نَصِيْبٌ مِّنْهَا ۚ وَمَنْ يَّشْفَعْ شَفَاعَةً سَيِّئَةً يَّكُنْ لَّهٗ كِفْلٌ مِّنْهَا ۗ وَكَانَ اللّٰهُ عَلٰى كُلِّ شَيْءٍ مُّقِيْتًا ","bc":"may yasyfa' syafā'atan ḥasanatay yakul lahụ naṣībum min-hā, wa may yasyfa' syafā'atan sayyi`atay yakul lahụ kiflum min-hā, wa kānallāhu 'alā kulli syai`im muqītā","at":"Barangsiapa memberi pertolongan dengan pertolongan yang baik, niscaya dia akan memperoleh bagian dari (pahala)nya. Dan barangsiapa memberi pertolongan dengan pertolongan yang buruk, niscaya dia akan memikul bagian dari (dosa)nya. Allah Mahakuasa atas segala sesuatu."},{"id":"86","ar":"وَاِذَا حُيِّيْتُمْ بِتَحِيَّةٍ فَحَيُّوْا بِاَحْسَنَ مِنْهَآ اَوْ رُدُّوْهَا ۗ اِنَّ اللّٰهَ كَانَ عَلٰى كُلِّ شَيْءٍ حَسِيْبًا","bc":"wa iżā ḥuyyītum bitaḥiyyatin fa ḥayyụ bi`aḥsana min-hā au ruddụhā, innallāha kāna 'alā kulli syai`in ḥasībā","at":"Dan apabila kamu dihormati dengan suatu (salam) penghormatan, maka balaslah penghormatan itu dengan yang lebih baik, atau balaslah (penghormatan itu, yang sepadan) dengannya. Sungguh, Allah memperhitungkan segala sesuatu."},{"id":"87","ar":"اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۗ لَيَجْمَعَنَّكُمْ اِلٰى يَوْمِ الْقِيٰمَةِ لَا رَيْبَ فِيْهِ ۗ وَمَنْ اَصْدَقُ مِنَ اللّٰهِ حَدِيْثًا","bc":"allāhu lā ilāha illā huw, layajma'annakum ilā yaumil-qiyāmati lā raiba fīh, wa man aṣdaqu minallāhi ḥadīṡā","at":"Allah, tidak ada tuhan selain Dia. Dia pasti akan mengumpulkan kamu pada hari Kiamat yang tidak diragukan terjadinya. Siapakah yang lebih benar perkataan(nya) daripada Allah?"},{"id":"88","ar":"۞ فَمَا لَكُمْ فِى الْمُنٰفِقِيْنَ فِئَتَيْنِ وَاللّٰهُ اَرْكَسَهُمْ بِمَا كَسَبُوْا ۗ اَتُرِيْدُوْنَ اَنْ تَهْدُوْا مَنْ اَضَلَّ اللّٰهُ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ سَبِيْلًا ","bc":"fa mā lakum fil-munāfiqīna fi`ataini wallāhu arkasahum bimā kasabụ, a turīdụna an tahdụ man aḍallallāh, wa may yuḍlilillāhu fa lan tajida lahụ sabīlā","at":"Maka mengapa kamu (terpecah) menjadi dua golongan dalam (menghadapi) orang-orang munafik, padahal Allah telah mengembalikan mereka (kepada kekafiran), disebabkan usaha mereka sendiri? Apakah kamu bermaksud memberi petunjuk kepada orang yang telah dibiarkan sesat oleh Allah? Barangsiapa dibiarkan sesat oleh Allah, kamu tidak akan mendapatkan jalan (untuk memberi petunjuk) baginya."},{"id":"89","ar":"وَدُّوْا لَوْ تَكْفُرُوْنَ كَمَا كَفَرُوْا فَتَكُوْنُوْنَ سَوَاۤءً فَلَا تَتَّخِذُوْا مِنْهُمْ اَوْلِيَاۤءَ حَتّٰى يُهَاجِرُوْا فِيْ سَبِيْلِ اللّٰهِ ۗ فَاِنْ تَوَلَّوْا فَخُذُوْهُمْ وَاقْتُلُوْهُمْ حَيْثُ وَجَدْتُّمُوْهُمْ ۖ وَلَا تَتَّخِذُوْا مِنْهُمْ وَلِيًّا وَّلَا نَصِيْرًاۙ ","bc":"waddụ lau takfurụna kamā kafarụ fa takụnụna sawā`an fa lā tattakhiżụ min-hum auliyā`a ḥattā yuhājirụ fī sabīlillāh, fa in tawallau fa khużụhum waqtulụhum ḥaiṡu wajattumụhum wa lā tattakhiżụ min-hum waliyyaw wa lā naṣīrā","at":"Mereka ingin agar kamu menjadi kafir sebagaimana mereka telah menjadi kafir, sehingga kamu menjadi sama (dengan mereka). Janganlah kamu jadikan dari antara mereka sebagai teman-teman(mu), sebelum mereka berpindah pada jalan Allah. Apabila mereka berpaling, maka tawanlah mereka dan bunuhlah mereka di mana pun mereka kamu temukan, dan janganlah kamu jadikan seorang pun di antara mereka sebagai teman setia dan penolong,"},{"id":"90","ar":"اِلَّا الَّذِيْنَ يَصِلُوْنَ اِلٰى قَوْمٍۢ بَيْنَكُمْ وَبَيْنَهُمْ مِّيْثَاقٌ اَوْ جَاۤءُوْكُمْ حَصِرَتْ صُدُوْرُهُمْ اَنْ يُّقَاتِلُوْكُمْ اَوْ يُقَاتِلُوْا قَوْمَهُمْ ۗ وَلَوْ شَاۤءَ اللّٰهُ لَسَلَّطَهُمْ عَلَيْكُمْ فَلَقَاتَلُوْكُمْ ۚ فَاِنِ اعْتَزَلُوْكُمْ فَلَمْ يُقَاتِلُوْكُمْ وَاَلْقَوْا اِلَيْكُمُ السَّلَمَ ۙ فَمَا جَعَلَ اللّٰهُ لَكُمْ عَلَيْهِمْ سَبِيْلًا ","bc":"illallażīna yaṣilụna ilā qaumim bainakum wa bainahum mīṡāqun au jā`ụkum ḥaṣirat ṣudụruhum ay yuqātilụkum au yuqātilụ qaumahum, walau syā`allāhu lasallaṭahum 'alaikum fa laqātalụkum, fa ini'tazalụkum fa lam yuqātilụkum wa alqau ilaikumus-salama fa mā ja'alallāhu lakum 'alaihim sabīlā","at":"kecuali orang-orang yang meminta perlindungan kepada suatu kaum, yang antara kamu dan kaum itu telah ada perjanjian (damai) atau orang yang datang kepadamu sedang hati mereka merasa keberatan untuk memerangi kamu atau memerangi kaumnya. Sekiranya Allah menghendaki, niscaya diberikan-Nya kekuasaan kepada mereka (dalam) menghadapi kamu, maka pastilah mereka memerangimu. Tetapi jika mereka membiarkan kamu, dan tidak memerangimu serta menawarkan perdamaian kepadamu (menyerah), maka Allah tidak memberi jalan bagimu (untuk menawan dan membunuh) mereka."},{"id":"91","ar":"سَتَجِدُوْنَ اٰخَرِيْنَ يُرِيْدُوْنَ اَنْ يَّأْمَنُوْكُمْ وَيَأْمَنُوْا قَوْمَهُمْ ۗ كُلَّ مَا رُدُّوْٓا اِلَى الْفِتْنَةِ اُرْكِسُوْا فِيْهَا ۚ فَاِنْ لَّمْ يَعْتَزِلُوْكُمْ وَيُلْقُوْٓا اِلَيْكُمُ السَّلَمَ وَيَكُفُّوْٓا اَيْدِيَهُمْ فَخُذُوْهُمْ وَاقْتُلُوْهُمْ حَيْثُ ثَقِفْتُمُوْهُمْ ۗ وَاُولٰۤىِٕكُمْ جَعَلْنَا لَكُمْ عَلَيْهِمْ سُلْطٰنًا مُّبِيْنًا ","bc":"satajidụna ākharīna yurīdụna ay ya`manụkum wa ya`manụ qaumahum, kulla mā ruddū ilal-fitnati urkisụ fīhā, fa il lam ya'tazilụkum wa yulqū ilaikumus-salama wa yakuffū aidiyahum fa khużụhum waqtulụhum ḥaiṡu ṡaqiftumụhum, wa ulā`ikum ja'alnā lakum 'alaihim sulṭānam mubīnā","at":"Kelak akan kamu dapati (golongan-golongan) yang lain, yang menginginkan agar mereka hidup aman bersamamu dan aman (pula) bersama kaumnya. Setiap kali mereka diajak kembali kepada fitnah (syirik), mereka pun terjun ke dalamnya. Karena itu jika mereka tidak membiarkan kamu dan tidak mau menawarkan perdamaian kepadamu, serta tidak menahan tangan mereka (dari memerangimu), maka tawanlah mereka dan bunuhlah mereka di mana saja kamu temui, dan merekalah orang yang Kami berikan kepadamu alasan yang nyata (untuk memerangi, menawan dan membunuh) mereka."},{"id":"92","ar":"وَمَا كَانَ لِمُؤْمِنٍ اَنْ يَّقْتُلَ مُؤْمِنًا اِلَّا خَطَـًٔا ۚ وَمَنْ قَتَلَ مُؤْمِنًا خَطَـًٔا فَتَحْرِيْرُ رَقَبَةٍ مُّؤْمِنَةٍ وَّدِيَةٌ مُّسَلَّمَةٌ اِلٰٓى اَهْلِهٖٓ اِلَّآ اَنْ يَّصَّدَّقُوْا ۗ فَاِنْ كَانَ مِنْ قَوْمٍ عَدُوٍّ لَّكُمْ وَهُوَ مُؤْمِنٌ فَتَحْرِيْرُ رَقَبَةٍ مُّؤْمِنَةٍ ۗوَاِنْ كَانَ مِنْ قَوْمٍۢ بَيْنَكُمْ وَبَيْنَهُمْ مِّيْثَاقٌ فَدِيَةٌ مُّسَلَّمَةٌ اِلٰٓى اَهْلِهٖ وَتَحْرِيْرُ رَقَبَةٍ مُّؤْمِنَةٍ ۚ فَمَنْ لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِۖ تَوْبَةً مِّنَ اللّٰهِ ۗوَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا ","bc":"wa mā kāna limu`minin ay yaqtula mu`minan illā khaṭa`ā, wa mang qatala mu`minan khaṭa`an fa taḥrīru raqabatim mu`minatiw wa diyatum musallamatun ilā ahlihī illā ay yaṣṣaddaqụ, fa ing kāna ming qaumin 'aduwwil lakum wa huwa mu`minun fa taḥrīru raqabatim mu`minah, wa ing kāna ming qaumim bainakum wa bainahum mīṡāqun fa diyatum musallamatun ilā ahlihī wa taḥrīru raqabatim mu`minah, fa mal lam yajid fa ṣiyāmu syahraini mutatābi'aini taubatam minallāh, wa kānallāhu 'alīman ḥakīmā","at":"Dan tidak patut bagi seorang yang beriman membunuh seorang yang beriman (yang lain), kecuali karena tersalah (tidak sengaja). Barangsiapa membunuh seorang yang beriman karena tersalah (hendaklah) dia memerdekakan seorang hamba sahaya yang beriman serta (membayar) tebusan yang diserahkan kepada keluarganya (si terbunuh itu), kecuali jika mereka (keluarga si terbunuh) membebaskan pembayaran. Jika dia (si terbunuh) dari kaum yang memusuhimu, padahal dia orang beriman, maka (hendaklah si pembunuh) memerdekakan hamba sahaya yang beriman. Dan jika dia (si terbunuh) dari kaum (kafir) yang ada perjanjian (damai) antara mereka dengan kamu, maka (hendaklah si pembunuh) membayar tebusan yang diserahkan kepada keluarganya (si terbunuh) serta memerdekakan hamba sahaya yang beriman. Barangsiapa tidak mendapatkan (hamba sahaya), maka hendaklah dia (si pembunuh) berpuasa dua bulan berturut-turut sebagai tobat kepada Allah. Dan Allah Maha Mengetahui, Mahabijaksana."},{"id":"93","ar":"وَمَنْ يَّقْتُلْ مُؤْمِنًا مُّتَعَمِّدًا فَجَزَاۤؤُهٗ جَهَنَّمُ خَالِدًا فِيْهَا وَغَضِبَ اللّٰهُ عَلَيْهِ وَلَعَنَهٗ وَاَعَدَّ لَهٗ عَذَابًا عَظِيْمًا ","bc":"wa may yaqtul mu`minam muta'ammidan fa jazā`uhụ jahannamu khālidan fīhā wa gaḍiballāhu 'alaihi wa la'anahụ wa a'adda lahụ 'ażāban 'aẓīmā","at":"Dan barangsiapa membunuh seorang yang beriman dengan sengaja, maka balasannya ialah neraka Jahanam, dia kekal di dalamnya. Allah murka kepadanya, dan melaknatnya serta menyediakan azab yang besar baginya."},{"id":"94","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِذَا ضَرَبْتُمْ فِيْ سَبِيْلِ اللّٰهِ فَتَبَيَّنُوْا وَلَا تَقُوْلُوْا لِمَنْ اَلْقٰىٓ اِلَيْكُمُ السَّلٰمَ لَسْتَ مُؤْمِنًاۚ تَبْتَغُوْنَ عَرَضَ الْحَيٰوةِ الدُّنْيَا ۖفَعِنْدَ اللّٰهِ مَغَانِمُ كَثِيْرَةٌ ۗ كَذٰلِكَ كُنْتُمْ مِّنْ قَبْلُ فَمَنَّ اللّٰهُ عَلَيْكُمْ فَتَبَيَّنُوْاۗ اِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًا","bc":"yā ayyuhallażīna āmanū iżā ḍarabtum fī sabīlillāhi fa tabayyanụ wa lā taqụlụ liman alqā ilaikumus-salāma lasta mu`minā, tabtagụna 'araḍal-ḥayātid-dun-yā fa 'indallāhi magānimu kaṡīrah, każālika kuntum ming qablu fa mannallāhu 'alaikum fa tabayyanụ, innallāha kāna bimā ta'malụna khabīrā","at":"Wahai orang-orang yang beriman! Apabila kamu pergi (berperang) di jalan Allah, maka telitilah (carilah keterangan) dan janganlah kamu mengatakan kepada orang yang mengucapkan ”salam” kepadamu, ”Kamu bukan seorang yang beriman,” (lalu kamu membunuhnya), dengan maksud mencari harta benda kehidupan dunia, padahal di sisi Allah ada harta yang banyak. Begitu jugalah keadaan kamu dahulu, lalu Allah memberikan nikmat-Nya kepadamu, maka telitilah. Sungguh, Allah Mahateliti terhadap apa yang kamu kerjakan."},{"id":"95","ar":"لَا يَسْتَوِى الْقَاعِدُوْنَ مِنَ الْمُؤْمِنِيْنَ غَيْرُ اُولِى الضَّرَرِ وَالْمُجَاهِدُوْنَ فِيْ سَبِيْلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ فَضَّلَ اللّٰهُ الْمُجٰهِدِيْنَ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ عَلَى الْقٰعِدِيْنَ دَرَجَةً ۗ وَكُلًّا وَّعَدَ اللّٰهُ الْحُسْنٰىۗ وَفَضَّلَ اللّٰهُ الْمُجٰهِدِيْنَ عَلَى الْقٰعِدِيْنَ اَجْرًا عَظِيْمًاۙ ","bc":"lā yastawil-qā'idụna minal-mu`minīna gairu uliḍ-ḍarari wal-mujāhidụna fī sabīlillāhi bi`amwālihim wa anfusihim, faḍḍalallāhul-mujāhidīna bi`amwālihim wa anfusihim 'alal-qā'idīna darajah, wa kullaw wa'adallāhul-ḥusnā, wa faḍḍalallāhul-mujāhidīna 'alal-qā'idīna ajran 'aẓīmā","at":"Tidaklah sama antara orang beriman yang duduk (yang tidak turut berperang) tanpa mempunyai uzur (halangan) dengan orang yang berjihad di jalan Allah dengan harta dan jiwanya. Allah melebihkan derajat orang-orang yang berjihad dengan harta dan jiwanya atas orang-orang yang duduk (tidak ikut berperang tanpa halangan). Kepada masing-masing, Allah menjanjikan (pahala) yang baik (surga) dan Allah melebihkan orang-orang yang berjihad atas orang yang duduk dengan pahala yang besar,"},{"id":"96","ar":"دَرَجٰتٍ مِّنْهُ وَمَغْفِرَةً وَّرَحْمَةً ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ","bc":"darajātim min-hu wa magfirataw wa raḥmah, wa kānallāhu gafụrar raḥīmā","at":"(yaitu) beberapa derajat daripada-Nya, serta ampunan dan rahmat. Allah Ma-ha Pengampun, Maha Penyayang."},{"id":"97","ar":"اِنَّ الَّذِيْنَ تَوَفّٰىهُمُ الْمَلٰۤىِٕكَةُ ظَالِمِيْٓ اَنْفُسِهِمْ قَالُوْا فِيْمَ كُنْتُمْ ۗ قَالُوْا كُنَّا مُسْتَضْعَفِيْنَ فِى الْاَرْضِۗ قَالُوْٓا اَلَمْ تَكُنْ اَرْضُ اللّٰهِ وَاسِعَةً فَتُهَاجِرُوْا فِيْهَا ۗ فَاُولٰۤىِٕكَ مَأْوٰىهُمْ جَهَنَّمُ ۗ وَسَاۤءَتْ مَصِيْرًاۙ ","bc":"innallażīna tawaffāhumul-malā`ikatu ẓālimī anfusihim qālụ fīma kuntum, qālụ kunnā mustaḍ'afīna fil-arḍ, qālū a lam takun arḍullāhi wāsi'atan fa tuhājirụ fīhā, fa ulā`ika ma`wāhum jahannam, wa sā`at maṣīrā","at":"Sesungguhnya orang-orang yang dicabut nyawanya oleh malaikat dalam keadaan menzalimi sendiri, mereka (para malaikat) bertanya, “Bagaimana kamu ini?” Mereka menjawab, “Kami orang-orang yang tertindas di bumi (Mekah).” Mereka (para malaikat) bertanya, “Bukankah bumi Allah itu luas, sehingga kamu dapat berhijrah (berpindah-pindah) di bumi itu?” Maka orang-orang itu tempatnya di neraka Jahanam, dan (Jahanam) itu seburuk-buruk tempat kembali,"},{"id":"98","ar":"اِلَّا الْمُسْتَضْعَفِيْنَ مِنَ الرِّجَالِ وَالنِّسَاۤءِ وَالْوِلْدَانِ لَا يَسْتَطِيْعُوْنَ حِيْلَةً وَّلَا يَهْتَدُوْنَ سَبِيْلًاۙ ","bc":"illal-mustaḍ'afīna minar-rijāli wan-nisā`i wal-wildāni lā yastaṭī'ụna ḥīlataw wa lā yahtadụna sabīlā","at":"kecuali mereka yang tertindas baik laki-laki atau perempuan dan anak-anak yang tidak berdaya dan tidak mengetahui jalan (untuk berhijrah),"},{"id":"99","ar":"فَاُولٰۤىِٕكَ عَسَى اللّٰهُ اَنْ يَّعْفُوَ عَنْهُمْ ۗ وَكَانَ اللّٰهُ عَفُوًّا غَفُوْرًا ","bc":"fa ulā`ika 'asallāhu ay ya'fuwa 'an-hum, wa kānallāhu 'afuwwan gafụrā","at":"maka mereka itu, mudah-mudahan Allah memaafkannya. Allah Maha Pemaaf, Maha Pengampun."},{"id":"100","ar":"۞ وَمَنْ يُّهَاجِرْ فِيْ سَبِيْلِ اللّٰهِ يَجِدْ فِى الْاَرْضِ مُرَاغَمًا كَثِيْرًا وَّسَعَةً ۗوَمَنْ يَّخْرُجْ مِنْۢ بَيْتِهٖ مُهَاجِرًا اِلَى اللّٰهِ وَرَسُوْلِهٖ ثُمَّ يُدْرِكْهُ الْمَوْتُ فَقَدْ وَقَعَ اَجْرُهٗ عَلَى اللّٰهِ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ","bc":"wa may yuhājir fī sabīlillāhi yajid fil-arḍi murāgamang kaṡīraw wa sa'ah, wa may yakhruj mim baitihī muhājiran ilallāhi wa rasụlihī ṡumma yudrik-hul-mautu fa qad waqa'a ajruhụ 'alallāh, wa kānallāhu gafụrar raḥīmā","at":"Dan barangsiapa berhijrah di jalan Allah, niscaya mereka akan mendapatkan di bumi ini tempat hijrah yang luas dan (rezeki) yang banyak. Barangsiapa keluar dari rumahnya dengan maksud berhijrah karena Allah dan Rasul-Nya, kemudian kematian menimpanya (sebelum sampai ke tempat yang dituju), maka sungguh, pahalanya telah ditetapkan di sisi Allah. Dan Allah Maha Pengampun, Maha Penyayang."},{"id":"101","ar":"وَاِذَا ضَرَبْتُمْ فِى الْاَرْضِ فَلَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَقْصُرُوْا مِنَ الصَّلٰوةِ ۖ اِنْ خِفْتُمْ اَنْ يَّفْتِنَكُمُ الَّذِيْنَ كَفَرُوْاۗ اِنَّ الْكٰفِرِيْنَ كَانُوْا لَكُمْ عَدُوًّا مُّبِيْنًا ","bc":"wa iżā ḍarabtum fil-arḍi fa laisa 'alaikum junāḥun an taqṣurụ minaṣ-ṣalāti in khiftum ay yaftinakumullażīna kafarụ, innal-kāfirīna kānụ lakum 'aduwwam mubīnā","at":"Dan apabila kamu bepergian di bumi, maka tidaklah berdosa kamu meng-qasar salat, jika kamu takut diserang orang kafir. Sesungguhnya orang kafir itu adalah musuh yang nyata bagimu."},{"id":"102","ar":"وَاِذَا كُنْتَ فِيْهِمْ فَاَقَمْتَ لَهُمُ الصَّلٰوةَ فَلْتَقُمْ طَاۤىِٕفَةٌ مِّنْهُمْ مَّعَكَ وَلْيَأْخُذُوْٓا اَسْلِحَتَهُمْ ۗ فَاِذَا سَجَدُوْا فَلْيَكُوْنُوْا مِنْ وَّرَاۤىِٕكُمْۖ وَلْتَأْتِ طَاۤىِٕفَةٌ اُخْرٰى لَمْ يُصَلُّوْا فَلْيُصَلُّوْا مَعَكَ وَلْيَأْخُذُوْا حِذْرَهُمْ وَاَسْلِحَتَهُمْ ۗ وَدَّ الَّذِيْنَ كَفَرُوْا لَوْ تَغْفُلُوْنَ عَنْ اَسْلِحَتِكُمْ وَاَمْتِعَتِكُمْ فَيَمِيْلُوْنَ عَلَيْكُمْ مَّيْلَةً وَّاحِدَةً ۗوَلَا جُنَاحَ عَلَيْكُمْ اِنْ كَانَ بِكُمْ اَذًى مِّنْ مَّطَرٍ اَوْ كُنْتُمْ مَّرْضٰٓى اَنْ تَضَعُوْٓا اَسْلِحَتَكُمْ وَخُذُوْا حِذْرَكُمْ ۗ اِنَّ اللّٰهَ اَعَدَّ لِلْكٰفِرِيْنَ عَذَابًا مُّهِيْنًا ","bc":"wa iżā kunta fīhim fa aqamta lahumuṣ-ṣalāta faltaqum ṭā`ifatum min-hum ma'aka walya`khużū asliḥatahum, fa iżā sajadụ falyakụnụ miw warā`ikum walta`ti ṭā`ifatun ukhrā lam yuṣallụ falyuṣallụ ma'aka walya`khużụ ḥiżrahum wa asliḥatahum, waddallażīna kafarụ lau tagfulụna 'an asliḥatikum wa amti'atikum fa yamīlụna 'alaikum mailataw wāḥidah, wa lā junāḥa 'alaikum ing kāna bikum ażam mim maṭarin au kuntum marḍā an taḍa'ū asliḥatakum, wa khużụ ḥiżrakum, innallāha a'adda lil-kāfirīna 'ażābam muhīnā","at":"Dan apabila engkau (Muhammad) berada di tengah-tengah mereka (sahabatmu) lalu engkau hendak melaksanakan salat bersama-sama mereka, maka hendaklah segolongan dari mereka berdiri (salat) besertamu dan menyandang senjata mereka, kemudian apabila mereka (yang salat besertamu) sujud (telah menyempurnakan satu rakaat), maka hendaklah mereka pindah dari belakangmu (untuk menghadapi musuh) dan hendaklah datang golongan yang lain yang belum salat, lalu mereka salat denganmu, dan hendaklah mereka bersiap siaga dan menyandang senjata mereka. Orang-orang kafir ingin agar kamu lengah terhadap senjatamu dan harta bendamu, lalu mereka menyerbu kamu sekaligus. Dan tidak mengapa kamu meletakkan senjata-senjatamu, jika kamu mendapat suatu kesusahan karena hujan atau karena kamu sakit, dan bersiap siagalah kamu. Sungguh, Allah telah menyediakan azab yang menghinakan bagi orang-orang kafir itu."},{"id":"103","ar":"فَاِذَا قَضَيْتُمُ الصَّلٰوةَ فَاذْكُرُوا اللّٰهَ قِيَامًا وَّقُعُوْدًا وَّعَلٰى جُنُوْبِكُمْ ۚ فَاِذَا اطْمَأْنَنْتُمْ فَاَقِيْمُوا الصَّلٰوةَ ۚ اِنَّ الصَّلٰوةَ كَانَتْ عَلَى الْمُؤْمِنِيْنَ كِتٰبًا مَّوْقُوْتًا ","bc":"fa iżā qaḍaitumuṣ-ṣalāta fażkurullāha qiyāmaw wa qu'ụdaw wa 'alā junụbikum, fa iżaṭma`nantum fa aqīmuṣ-ṣalāh, innaṣ-ṣalāta kānat 'alal-mu`minīna kitābam mauqụtā","at":"Selanjutnya, apabila kamu telah menyelesaikan salat(mu), ingatlah Allah ketika kamu berdiri, pada waktu duduk dan ketika berbaring. Kemudian, apabila kamu telah merasa aman, maka laksanakanlah salat itu (sebagaimana biasa). Sungguh, salat itu adalah kewajiban yang ditentukan waktunya atas orang-orang yang beriman."},{"id":"104","ar":"وَلَا تَهِنُوْا فِى ابْتِغَاۤءِ الْقَوْمِ ۗ اِنْ تَكُوْنُوْا تَأْلَمُوْنَ فَاِنَّهُمْ يَأْلَمُوْنَ كَمَا تَأْلَمُوْنَ ۚوَتَرْجُوْنَ مِنَ اللّٰهِ مَا لَا يَرْجُوْنَ ۗوَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا ","bc":"wa lā tahinụ fibtigā`il-qaụm, in takụnụ ta`lamụna fa innahum ya`lamụna kamā ta`lamụn, wa tarjụna minallāhi mā lā yarjụn, wa kānallāhu 'alīman ḥakīmā","at":"Dan janganlah kamu berhati lemah dalam mengejar mereka (musuhmu). Jika kamu menderita kesakitan, maka ketahuilah mereka pun menderita kesakitan (pula), sebagaimana kamu rasakan, sedang kamu masih dapat mengharapkan dari Allah apa yang tidak dapat mereka harapkan. Allah Maha Mengetahui, Mahabijaksana."},{"id":"105","ar":"اِنَّآ اَنْزَلْنَآ اِلَيْكَ الْكِتٰبَ بِالْحَقِّ لِتَحْكُمَ بَيْنَ النَّاسِ بِمَآ اَرٰىكَ اللّٰهُ ۗوَلَا تَكُنْ لِّلْخَاۤىِٕنِيْنَ خَصِيْمًا ۙ","bc":"innā anzalnā ilaikal-kitāba bil-ḥaqqi litaḥkuma bainan-nāsi bimā arākallāh, wa lā takul lil-khā`inīna khaṣīmā","at":"Sungguh, Kami telah menurunkan Kitab (Al-Qur'an) kepadamu (Muhammad) membawa kebenaran, agar engkau mengadili antara manusia dengan apa yang telah diajarkan Allah kepadamu, dan janganlah engkau menjadi penentang (orang yang tidak bersalah), karena (membela) orang yang berkhianat,"},{"id":"106","ar":"وَّاسْتَغْفِرِ اللّٰهَ ۗاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًاۚ ","bc":"wastagfirillāh, innallāha kāna gafụrar raḥīmā","at":"dan mohonkanlah ampunan kepada Allah. Sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"107","ar":"وَلَا تُجَادِلْ عَنِ الَّذِيْنَ يَخْتَانُوْنَ اَنْفُسَهُمْ ۗ اِنَّ اللّٰهَ لَا يُحِبُّ مَنْ كَانَ خَوَّانًا اَثِيْمًاۙ","bc":"wa lā tujādil 'anillażīna yakhtānụna anfusahum, innallāha lā yuḥibbu mang kāna khawwānan aṡīmā","at":"Dan janganlah kamu berdebat untuk (membela) orang-orang yang mengkhianati dirinya. Sungguh, Allah tidak menyukai orang-orang yang selalu berkhianat dan bergelimang dosa,"},{"id":"108","ar":"يَّسْتَخْفُوْنَ مِنَ النَّاسِ وَلَا يَسْتَخْفُوْنَ مِنَ اللّٰهِ وَهُوَ مَعَهُمْ اِذْ يُبَيِّتُوْنَ مَا لَا يَرْضٰى مِنَ الْقَوْلِ ۗ وَكَانَ اللّٰهُ بِمَا يَعْمَلُوْنَ مُحِيْطًا ","bc":"yastakhfụna minan-nāsi wa lā yastakhfụna minallāhi wa huwa ma'ahum iż yubayyitụna mā lā yarḍā minal-qaụl, wa kānallāhu bimā ya'malụna muḥīṭā","at":"mereka dapat bersembunyi dari manusia, tetapi mereka tidak dapat bersembunyi dari Allah, karena Allah beserta mereka, ketika pada suatu malam mereka menetapkan keputusan rahasia yang tidak diridai-Nya. Dan Allah Ma-ha Meliputi terhadap apa yang mereka kerjakan."},{"id":"109","ar":"هٰٓاَنْتُمْ هٰٓؤُلَاۤءِ جَادَلْتُمْ عَنْهُمْ فِى الْحَيٰوةِ الدُّنْيَاۗ فَمَنْ يُّجَادِلُ اللّٰهَ عَنْهُمْ يَوْمَ الْقِيٰمَةِ اَمْ مَّنْ يَّكُوْنُ عَلَيْهِمْ وَكِيْلًا ","bc":"hā`antum hā`ulā`i jādaltum 'an-hum fil-ḥayātid-dun-yā, fa may yujādilullāha 'an-hum yaumal-qiyāmati am may yakụnu 'alaihim wakīlā","at":"Itulah kamu! Kamu berdebat untuk (membela) mereka dalam kehidupan dunia ini, tetapi siapa yang akan menentang Allah untuk (membela) mereka pada hari Kiamat? Atau siapakah yang menjadi pelindung mereka (terhadap azab Allah)?"},{"id":"110","ar":"وَمَنْ يَّعْمَلْ سُوْۤءًا اَوْ يَظْلِمْ نَفْسَهٗ ثُمَّ يَسْتَغْفِرِ اللّٰهَ يَجِدِ اللّٰهَ غَفُوْرًا رَّحِيْمًا ","bc":"wa may ya'mal sū`an au yaẓlim nafsahụ ṡumma yastagfirillāha yajidillāha gafụrar raḥīmā","at":"Dan barangsiapa berbuat kejahatan dan menganiaya dirinya, kemudian dia memohon ampunan kepada Allah, niscaya dia akan mendapatkan Allah Maha Pengampun, Maha Penyayang."},{"id":"111","ar":"وَمَنْ يَّكْسِبْ اِثْمًا فَاِنَّمَا يَكْسِبُهٗ عَلٰى نَفْسِهٖ ۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا ","bc":"wa may yaksib iṡman fa innamā yaksibuhụ 'alā nafsih, wa kānallāhu 'alīman ḥakīmā","at":"Dan barangsiapa berbuat dosa, maka sesungguhnya dia mengerjakannya untuk (kesulitan) dirinya sendiri. Dan Allah Maha Mengetahui, Mahabijak-sana."},{"id":"112","ar":"وَمَنْ يَّكْسِبْ خَطِيْۤـَٔةً اَوْ اِثْمًا ثُمَّ يَرْمِ بِهٖ بَرِيْۤـًٔا فَقَدِ احْتَمَلَ بُهْتَانًا وَّاِثْمًا مُّبِيْنًا ","bc":"wa may yaksib khaṭī`atan au iṡman ṡumma yarmi bihī barī`an fa qadiḥtamala buhtānaw wa iṡmam mubīnā","at":"Dan barangsiapa berbuat kesalahan atau dosa, kemudian dia tuduhkan kepada orang yang tidak bersalah, maka sungguh, dia telah memikul suatu kebohongan dan dosa yang nyata."},{"id":"113","ar":"وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكَ وَرَحْمَتُهٗ لَهَمَّتْ طَّاۤىِٕفَةٌ مِّنْهُمْ اَنْ يُّضِلُّوْكَۗ وَمَا يُضِلُّوْنَ اِلَّآ اَنْفُسَهُمْ وَمَا يَضُرُّوْنَكَ مِنْ شَيْءٍ ۗ وَاَنْزَلَ اللّٰهُ عَلَيْكَ الْكِتٰبَ وَالْحِكْمَةَ وَعَلَّمَكَ مَا لَمْ تَكُنْ تَعْلَمُۗ وَكَانَ فَضْلُ اللّٰهِ عَلَيْكَ عَظِيْمًا ","bc":"walau lā faḍlullāhi 'alaika wa raḥmatuhụ lahammaṭ ṭā`ifatum min-hum ay yuḍillụk, wa mā yuḍillụna illā anfusahum wa mā yaḍurrụnaka min syaī`, wa anzalallāhu 'alaikal-kitāba wal-ḥikmata wa 'allamaka mā lam takun ta'lam, wa kāna faḍlullāhi 'alaika 'aẓīmā","at":"Dan kalau bukan karena karunia Allah dan rahmat-Nya kepadamu (Muhammad), tentulah segolongan dari mereka berkeinginan keras untuk menyesatkanmu. Tetapi mereka hanya menyesatkan dirinya sendiri, dan tidak membahayakanmu sedikit pun. Dan (juga karena) Allah telah menurunkan Kitab (Al-Qur'an) dan Hikmah (Sunnah) kepadamu, dan telah mengajarkan kepadamu apa yang belum engkau ketahui. Karunia Allah yang dilimpahkan kepadamu itu sangat besar."},{"id":"114","ar":"۞ لَا خَيْرَ فِيْ كَثِيْرٍ مِّنْ نَّجْوٰىهُمْ اِلَّا مَنْ اَمَرَ بِصَدَقَةٍ اَوْ مَعْرُوْفٍ اَوْ اِصْلَاحٍۢ بَيْنَ النَّاسِۗ وَمَنْ يَّفْعَلْ ذٰلِكَ ابْتِغَاۤءَ مَرْضَاتِ اللّٰهِ فَسَوْفَ نُؤْتِيْهِ اَجْرًا عَظِيْمًا ","bc":"lā khaira fī kaṡīrim min najwāhum illā man amara biṣadaqatin au ma'rụfin au iṣlāḥim bainan-nās, wa may yaf'al żālikabtigā`a marḍātillāhi fa saufa nu`tīhi ajran 'aẓīmā","at":"Tidak ada kebaikan dari banyak pembicaraan rahasia mereka, kecuali pembicaraan rahasia dari orang yang menyuruh (orang) bersedekah, atau berbuat kebaikan, atau mengadakan perdamaian di antara manusia. Barangsiapa berbuat demikian karena mencari keridaan Allah, maka kelak Kami akan memberinya pahala yang besar."},{"id":"115","ar":"وَمَنْ يُّشَاقِقِ الرَّسُوْلَ مِنْۢ بَعْدِ مَا تَبَيَّنَ لَهُ الْهُدٰى وَيَتَّبِعْ غَيْرَ سَبِيْلِ الْمُؤْمِنِيْنَ نُوَلِّهٖ مَا تَوَلّٰى وَنُصْلِهٖ جَهَنَّمَۗ وَسَاۤءَتْ مَصِيْرًا ","bc":"wa may yusyāqiqir-rasụla mim ba'di mā tabayyana lahul-hudā wa yattabi' gaira sabīlil-mu`minīna nuwallihī mā tawallā wa nuṣlihī jahannam, wa sā`at maṣīrā","at":"Dan barangsiapa menentang Rasul (Muhammad) setelah jelas kebenaran baginya, dan mengikuti jalan yang bukan jalan orang-orang mukmin, Kami biarkan dia dalam kesesatan yang telah dilakukannya itu dan akan Kami masukkan dia ke dalam neraka Jahanam, dan itu seburuk-buruk tempat kembali."},{"id":"116","ar":"اِنَّ اللّٰهَ لَا يَغْفِرُ اَنْ يُّشْرَكَ بِهٖ وَيَغْفِرُ مَا دُوْنَ ذٰلِكَ لِمَنْ يَّشَاۤءُ ۗ وَمَنْ يُّشْرِكْ بِاللّٰهِ فَقَدْ ضَلَّ ضَلٰلًا ۢ بَعِيْدًا ","bc":"innallāha lā yagfiru ay yusyraka bihī wa yagfiru mā dụna żālika limay yasyā`, wa may yusyrik billāhi fa qad ḍalla ḍalālam ba'īdā","at":"Allah tidak akan mengampuni dosa syirik (mempersekutukan Allah dengan sesuatu), dan Dia mengampuni dosa selain itu bagi siapa yang Dia kehendaki. Dan barangsiapa mempersekutukan (sesuatu) dengan Allah, maka sungguh, dia telah tersesat jauh sekali."},{"id":"117","ar":"اِنْ يَّدْعُوْنَ مِنْ دُوْنِهٖٓ اِلَّآ اِنَاثًاۚ وَاِنْ يَّدْعُوْنَ اِلَّا شَيْطٰنًا مَّرِيْدًاۙ","bc":"iy yad'ụna min dụnihī illā ināṡā, wa iy yad'ụna illā syaiṭānam marīdā","at":"Yang mereka sembah selain Allah itu tidak lain hanyalah (berhala), dan mereka tidak lain hanyalah menyembah setan yang durhaka,"},{"id":"118","ar":"لَّعَنَهُ اللّٰهُ ۘ وَقَالَ لَاَتَّخِذَنَّ مِنْ عِبَادِكَ نَصِيْبًا مَّفْرُوْضًاۙ","bc":"la'anahullāh, wa qāla la`attakhiżanna min 'ibādika naṣībam mafrụḍā","at":"yang dilaknati Allah, dan (setan) itu mengatakan, “Aku pasti akan mengambil bagian tertentu dari hamba-hamba-Mu,"},{"id":"119","ar":"وَّلَاُضِلَّنَّهُمْ وَلَاُمَنِّيَنَّهُمْ وَلَاٰمُرَنَّهُمْ فَلَيُبَتِّكُنَّ اٰذَانَ الْاَنْعَامِ وَلَاٰمُرَنَّهُمْ فَلَيُغَيِّرُنَّ خَلْقَ اللّٰهِ ۚ وَمَنْ يَّتَّخِذِ الشَّيْطٰنَ وَلِيًّا مِّنْ دُوْنِ اللّٰهِ فَقَدْ خَسِرَ خُسْرَانًا مُّبِيْنًا ","bc":"wa la`uḍillannahum wa la`umanniyannahum wa la`āmurannahum fa layubattikunna āżānal-an'āmi wa la`āmurannahum fa layugayyirunna khalqallāh, wa may yattakhiżisy-syaiṭāna waliyyam min dụnillāhi fa qad khasira khusrānam mubīnā","at":"dan pasti kusesatkan mereka, dan akan kubangkitkan angan-angan kosong pada mereka dan akan kusuruh mereka memotong telinga-telinga binatang ternak, (lalu mereka benar-benar memotongnya), dan akan aku suruh mereka mengubah ciptaan Allah, (lalu mereka benar-benar mengubahnya).” Barangsiapa menjadikan setan sebagai pelindung selain Allah, maka sungguh, dia menderita kerugian yang nyata."},{"id":"120","ar":"يَعِدُهُمْ وَيُمَنِّيْهِمْۗ وَمَا يَعِدُهُمُ الشَّيْطٰنُ اِلَّا غُرُوْرًا ","bc":"ya'iduhum wa yumannīhim, wa mā ya'iduhumusy-syaiṭānu illā gurụrā","at":"(Setan itu) memberikan janji-janji kepada mereka dan membangkitkan angan-angan kosong pada mereka, padahal setan itu hanya menjanjikan tipuan belaka kepada mereka."},{"id":"121","ar":"اُولٰۤىِٕكَ مَأْوٰىهُمْ جَهَنَّمُۖ وَلَا يَجِدُوْنَ عَنْهَا مَحِيْصًا ","bc":"ulā`ika ma`wāhum jahannamu wa lā yajidụna 'an-hā mahīṣā","at":"Mereka (yang tertipu) itu tempatnya di neraka Jahanam dan mereka tidak akan mendapat tempat (lain untuk) lari darinya."},{"id":"122","ar":"وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَنُدْخِلُهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ وَعْدَ اللّٰهِ حَقًّا ۗوَمَنْ اَصْدَقُ مِنَ اللّٰهِ قِيْلًا ","bc":"wallażīna āmanụ wa 'amiluṣ-ṣāliḥāti sanudkhiluhum jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, wa'dallāhi ḥaqqā, wa man aṣdaqu minallāhi qīlā","at":"Dan orang yang beriman dan mengerjakan amal kebajikan, kelak akan Kami masukkan ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya selama-lamanya. Dan janji Allah itu benar. Siapakah yang lebih benar perkataannya daripada Allah?"},{"id":"123","ar":"لَيْسَ بِاَمَانِيِّكُمْ وَلَآ اَمَانِيِّ اَهْلِ الْكِتٰبِ ۗ مَنْ يَّعْمَلْ سُوْۤءًا يُّجْزَ بِهٖۙ وَلَا يَجِدْ لَهٗ مِنْ دُوْنِ اللّٰهِ وَلِيًّا وَّلَا نَصِيْرًا ","bc":"laisa bi`amāniyyikum wa lā amāniyyi ahlil-kitāb, may ya'mal sū`ay yujza bihī wa lā yajid lahụ min dụnillāhi waliyyaw wa lā naṣīrā","at":"(Pahala dari Allah) itu bukanlah angan-anganmu dan bukan (pula) angan-angan Ahli Kitab. Barangsiapa mengerjakan kejahatan, niscaya akan dibalas sesuai dengan kejahatan itu, dan dia tidak akan mendapatkan pelindung dan penolong selain Allah."},{"id":"124","ar":"وَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ مِنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَاُولٰۤىِٕكَ يَدْخُلُوْنَ الْجَنَّةَ وَلَا يُظْلَمُوْنَ نَقِيْرًا ","bc":"wa may ya'mal minaṣ-ṣāliḥāti min żakarin au unṡā wa huwa mu`minun fa ulā`ika yadkhulụnal-jannata wa lā yuẓlamụna naqīrā","at":"Dan barangsiapa mengerjakan amal kebajikan, baik laki-laki maupun perempuan sedang dia beriman, maka mereka itu akan masuk ke dalam surga dan mereka tidak dizalimi sedikit pun."},{"id":"125","ar":"وَمَنْ اَحْسَنُ دِيْنًا مِّمَّنْ اَسْلَمَ وَجْهَهٗ لِلّٰهِ وَهُوَ مُحْسِنٌ وَّاتَّبَعَ مِلَّةَ اِبْرٰهِيْمَ حَنِيْفًا ۗوَاتَّخَذَ اللّٰهُ اِبْرٰهِيْمَ خَلِيْلًا ","bc":"wa man aḥsanu dīnam mim man aslama waj-hahụ lillāhi wa huwa muḥsinuw wattaba'a millata ibrāhīma ḥanīfā, wattakhażallāhu ibrāhīma khalīlā","at":"Dan siapakah yang lebih baik agamanya daripada orang yang dengan ikhlas berserah diri kepada Allah, sedang dia mengerjakan kebaikan, dan mengikuti agama Ibrahim yang lurus? Dan Allah telah memilih Ibrahim menjadi kesayangan(-Nya)."},{"id":"126","ar":"وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَكَانَ اللّٰهُ بِكُلِّ شَيْءٍ مُّحِيْطًا ","bc":"wa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa kānallāhu bikulli syai`im muḥīṭā","at":"Dan milik Allah-lah apa yang ada di langit dan apa yang ada di bumi, dan (pengetahuan) Allah meliputi segala sesuatu."},{"id":"127","ar":"وَيَسْتَفْتُوْنَكَ فِى النِّسَاۤءِۗ قُلِ اللّٰهُ يُفْتِيْكُمْ فِيْهِنَّ ۙوَمَا يُتْلٰى عَلَيْكُمْ فِى الْكِتٰبِ فِيْ يَتٰمَى النِّسَاۤءِ الّٰتِيْ لَا تُؤْتُوْنَهُنَّ مَا كُتِبَ لَهُنَّ وَتَرْغَبُوْنَ اَنْ تَنْكِحُوْهُنَّ وَالْمُسْتَضْعَفِيْنَ مِنَ الْوِلْدَانِۙ وَاَنْ تَقُوْمُوْا لِلْيَتٰمٰى بِالْقِسْطِ ۗوَمَا تَفْعَلُوْا مِنْ خَيْرٍ فَاِنَّ اللّٰهَ كَانَ بِهٖ عَلِيْمًا ","bc":"wa yastaftụnaka fin-nisā`, qulillāhu yuftīkum fīhinna wa mā yutlā 'alaikum fil-kitābi fī yatāman-nisā`illātī lā tu`tụnahunna mā kutiba lahunna wa targabụna an tangkiḥụhunna wal-mustaḍ'afīna minal-wildāni wa an taqụmụ lil-yatāmā bil-qisṭ, wa mā taf'alụ min khairin fa innallāha kāna bihī 'alīmā","at":"Dan mereka meminta fatwa kepadamu tentang perempuan. Katakanlah, “Allah memberi fatwa kepadamu tentang mereka, dan apa yang dibacakan kepadamu dalam Al-Qur'an (juga memfatwakan) tentang para perempuan yatim yang tidak kamu berikan sesuatu (maskawin) yang ditetapkan untuk mereka, sedang kamu ingin menikahi mereka dan (tentang) anak-anak yang masih dipandang lemah. Dan (Allah menyuruh kamu) agar mengurus anak-anak yatim secara adil. Dan kebajikan apa pun yang kamu kerjakan, sesungguhnya Allah Maha Mengetahui.”"},{"id":"128","ar":"وَاِنِ امْرَاَةٌ خَافَتْ مِنْۢ بَعْلِهَا نُشُوْزًا اَوْ اِعْرَاضًا فَلَا جُنَاحَ عَلَيْهِمَآ اَنْ يُّصْلِحَا بَيْنَهُمَا صُلْحًا ۗوَالصُّلْحُ خَيْرٌ ۗوَاُحْضِرَتِ الْاَنْفُسُ الشُّحَّۗ وَاِنْ تُحْسِنُوْا وَتَتَّقُوْا فَاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًا ","bc":"wa inimra`atun khāfat mim ba'lihā nusyụzan au i'rāḍan fa lā junāḥa 'alaihimā ay yuṣliḥā bainahumā ṣul-ḥā, waṣ-ṣul-ḥu khaīr, wa uḥḍiratil-anfususy-syuḥḥ, wa in tuḥsinụ wa tattaqụ fa innallāha kāna bimā ta'malụna khabīrā","at":"Dan jika seorang perempuan khawatir suaminya akan nusyuz atau bersikap tidak acuh, maka keduanya dapat mengadakan perdamaian yang sebenarnya, dan perdamaian itu lebih baik (bagi mereka) walaupun manusia itu menurut tabiatnya kikir. Dan jika kamu memperbaiki (pergaulan dengan istrimu) dan memelihara dirimu (dari nusyuz dan sikap acuh tak acuh), maka sungguh, Allah Mahateliti terhadap apa yang kamu kerjakan."},{"id":"129","ar":"وَلَنْ تَسْتَطِيْعُوْٓا اَنْ تَعْدِلُوْا بَيْنَ النِّسَاۤءِ وَلَوْ حَرَصْتُمْ فَلَا تَمِيْلُوْا كُلَّ الْمَيْلِ فَتَذَرُوْهَا كَالْمُعَلَّقَةِ ۗوَاِنْ تُصْلِحُوْا وَتَتَّقُوْا فَاِنَّ اللّٰهَ كَانَ غَفُوْرًا رَّحِيْمًا","bc":"wa lan tastaṭī'ū an ta'dilụ bainan-nisā`i walau ḥaraṣtum fa lā tamīlụ kullal-maili fa tażarụhā kal-mu'allaqah, wa in tuṣliḥụ wa tattaqụ fa innallāha kāna gafụrar raḥīmā","at":"Dan kamu tidak akan dapat berlaku adil di antara istri-istri(mu), walaupun kamu sangat ingin berbuat demikian, karena itu janganlah kamu terlalu cenderung (kepada yang kamu cintai), sehingga kamu biarkan yang lain terkatung-katung. Dan jika kamu mengadakan perbaikan dan memelihara diri (dari kecurangan), maka sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"130","ar":"وَاِنْ يَّتَفَرَّقَا يُغْنِ اللّٰهُ كُلًّا مِّنْ سَعَتِهٖۗ وَكَانَ اللّٰهُ وَاسِعًا حَكِيْمًا ","bc":"wa iy yatafarraqā yugnillāhu kullam min sa'atih, wa kānallāhu wāsi'an ḥakīmā","at":"Dan jika keduanya bercerai, maka Allah akan memberi kecukupan kepada masing-masing dari karunia-Nya. Dan Allah Mahaluas (karunia-Nya), Mahabijaksana."},{"id":"131","ar":"وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَلَقَدْ وَصَّيْنَا الَّذِيْنَ اُوْتُوا الْكِتٰبَ مِنْ قَبْلِكُمْ وَاِيَّاكُمْ اَنِ اتَّقُوا اللّٰهَ ۗوَاِنْ تَكْفُرُوْا فَاِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَكَانَ اللّٰهُ غَنِيًّا حَمِيْدًا ","bc":"wa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa laqad waṣṣainallażīna ụtul-kitāba ming qablikum wa iyyākum anittaqullāh, wa in takfurụ fa inna lillāhi mā fis-samāwāti wa mā fil-arḍ, wa kānallāhu ganiyyan ḥamīdā","at":"Dan milik Allah-lah apa yang ada di langit dan apa yang ada di bumi, dan sungguh, Kami telah memerintahkan kepada orang yang diberi kitab suci sebelum kamu dan (juga) kepadamu agar bertakwa kepada Allah. Tetapi jika kamu ingkar, maka (ketahuilah), milik Allah-lah apa yang ada di langit dan apa yang ada di bumi, dan Allah Mahakaya, Maha Terpuji."},{"id":"132","ar":"وَلِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ ۗوَكَفٰى بِاللّٰهِ وَكِيْلًا ","bc":"wa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa kafā billāhi wakīlā","at":"Dan milik Allah-lah apa yang ada di langit dan apa yang ada di bumi. Cukuplah Allah sebagai pemeliharanya."},{"id":"133","ar":"اِنْ يَّشَأْ يُذْهِبْكُمْ اَيُّهَا النَّاسُ وَيَأْتِ بِاٰخَرِيْنَۗ وَكَانَ اللّٰهُ عَلٰى ذٰلِكَ قَدِيْرًا ","bc":"iy yasya` yuż-hibkum ayyuhan-nāsu wa ya`ti bi`ākharīn, wa kānallāhu 'alā żālika qadīrā","at":"Kalau Allah menghendaki, niscaya dimusnahkan-Nya kamu semua wahai manusia! Kemudian Dia datangkan (umat) yang lain (sebagai penggantimu). Dan Allah Mahakuasa berbuat demikian."},{"id":"134","ar":"مَنْ كَانَ يُرِيْدُ ثَوَابَ الدُّنْيَا فَعِنْدَ اللّٰهِ ثَوَابُ الدُّنْيَا وَالْاٰخِرَةِ ۗوَكَانَ اللّٰهُ سَمِيْعًاۢ بَصِيْرًا ","bc":"mang kāna yurīdu ṡawābad-dun-yā fa 'indallāhi ṡawābud-dun-yā wal-ākhirah, wa kānallāhu samī'am baṣīrā","at":"Barangsiapa menghendaki pahala di dunia maka ketahuilah bahwa di sisi Allah ada pahala dunia dan akhirat. Dan Allah Maha Mendengar, Maha Melihat."},{"id":"135","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُوْنُوْا قَوَّامِيْنَ بِالْقِسْطِ شُهَدَاۤءَ لِلّٰهِ وَلَوْ عَلٰٓى اَنْفُسِكُمْ اَوِ الْوَالِدَيْنِ وَالْاَقْرَبِيْنَ ۚ اِنْ يَّكُنْ غَنِيًّا اَوْ فَقِيْرًا فَاللّٰهُ اَوْلٰى بِهِمَاۗ فَلَا تَتَّبِعُوا الْهَوٰٓى اَنْ تَعْدِلُوْا ۚ وَاِنْ تَلْوٗٓا اَوْ تُعْرِضُوْا فَاِنَّ اللّٰهَ كَانَ بِمَا تَعْمَلُوْنَ خَبِيْرًا ","bc":"yā ayyuhallażīna āmanụ kụnụ qawwāmīna bil-qisṭi syuhadā`a lillāhi walau 'alā anfusikum awil-wālidaini wal-aqrabīn, iy yakun ganiyyan au faqīran fallāhu aulā bihimā, fa lā tattabi'ul-hawā an ta'dilụ, wa in talwū au tu'riḍụ fa innallāha kāna bimā ta'malụna khabīrā","at":"Wahai orang-orang yang beriman! Jadilah kamu penegak keadilan, menjadi saksi karena Allah, walaupun terhadap dirimu sendiri atau terhadap ibu bapak dan kaum kerabatmu. Jika dia (yang terdakwa) kaya ataupun miskin, maka Allah lebih tahu kemaslahatan (kebaikannya). Maka janganlah kamu mengikuti hawa nafsu karena ingin menyimpang dari kebenaran. Dan jika kamu memutarbalikkan (kata-kata) atau enggan menjadi saksi, maka ketahuilah Allah Mahateliti terhadap segala apa yang kamu kerjakan."},{"id":"136","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَالْكِتٰبِ الَّذِيْ نَزَّلَ عَلٰى رَسُوْلِهٖ وَالْكِتٰبِ الَّذِيْٓ اَنْزَلَ مِنْ قَبْلُ ۗوَمَنْ يَّكْفُرْ بِاللّٰهِ وَمَلٰۤىِٕكَتِهٖ وَكُتُبِهٖ وَرُسُلِهٖ وَالْيَوْمِ الْاٰخِرِ فَقَدْ ضَلَّ ضَلٰلًا ۢ بَعِيْدًا ","bc":"yā ayyuhallażīna āmanū āminụ billāhi wa rasụlihī wal-kitābillażī nazzala 'alā rasụlihī wal-kitābillażī anzala ming qabl, wa may yakfur billāhi wa malā`ikatihī wa kutubihī wa rusulihī wal-yaumil-ākhiri fa qad ḍalla ḍalālam ba'īdā","at":"Wahai orang-orang yang beriman! Tetaplah beriman kepada Allah dan Rasul-Nya (Muhammad) dan kepada Kitab (Al-Qur'an) yang diturunkan kepada Rasul-Nya, serta kitab yang diturunkan sebelumnya. Barangsiapa ingkar kepada Allah, malaikat-malaikat-Nya, kitab-kitab-Nya, rasul-rasul-Nya, dan hari kemudian, maka sungguh, orang itu telah tersesat sangat jauh."},{"id":"137","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا ثُمَّ كَفَرُوْا ثُمَّ اٰمَنُوْا ثُمَّ كَفَرُوْا ثُمَّ ازْدَادُوْا كُفْرًا لَّمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ سَبِيْلًاۗ ","bc":"innallażīna āmanụ ṡumma kafarụ ṡumma āmanụ ṡumma kafarụ ṡummazdādụ kufral lam yakunillāhu liyagfira lahum wa lā liyahdiyahum sabīlā","at":"Sesungguhnya orang-orang yang beriman lalu kafir, kemudian beriman (lagi), kemudian kafir lagi, lalu bertambah kekafirannya, maka Allah tidak akan mengampuni mereka, dan tidak (pula) menunjukkan kepada mereka jalan (yang lurus)."},{"id":"138","ar":"بَشِّرِ الْمُنٰفِقِيْنَ بِاَنَّ لَهُمْ عَذَابًا اَلِيْمًاۙ","bc":"basysyiril-munāfiqīna bi`anna lahum 'ażāban alīmā","at":"Kabarkanlah kepada orang-orang munafik bahwa mereka akan mendapat siksaan yang pedih,"},{"id":"139","ar":" ۨالَّذِيْنَ يَتَّخِذُوْنَ الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَ ۗ اَيَبْتَغُوْنَ عِنْدَهُمُ الْعِزَّةَ فَاِنَّ الْعِزَّةَ لِلّٰهِ جَمِيْعًاۗ","bc":"allażīna yattakhiżụnal-kāfirīna auliyā`a min dụnil-mu`minīn, a yabtagụna 'indahumul-'izzata fa innal-'izzata lillāhi jamī'ā","at":"(yaitu) orang-orang yang menjadikan orang-orang kafir sebagai pemimpin dengan meninggalkan orang-orang mukmin. Apakah mereka mencari kekuatan di sisi orang kafir itu? Ketahuilah bahwa semua kekuatan itu milik Allah."},{"id":"140","ar":"وَقَدْ نَزَّلَ عَلَيْكُمْ فِى الْكِتٰبِ اَنْ اِذَا سَمِعْتُمْ اٰيٰتِ اللّٰهِ يُكْفَرُ بِهَا وَيُسْتَهْزَاُ بِهَا فَلَا تَقْعُدُوْا مَعَهُمْ حَتّٰى يَخُوْضُوْا فِيْ حَدِيْثٍ غَيْرِهٖٓ ۖ اِنَّكُمْ اِذًا مِّثْلُهُمْ ۗ اِنَّ اللّٰهَ جَامِعُ الْمُنٰفِقِيْنَ وَالْكٰفِرِيْنَ فِيْ جَهَنَّمَ جَمِيْعًاۙ ","bc":"wa qad nazzala 'alaikum fil-kitābi an iżā sami'tum āyātillāhi yukfaru bihā wa yustahza`u bihā fa lā taq'udụ ma'ahum ḥattā yakhụḍụ fī ḥadīṡin gairihī innakum iżam miṡluhum, innallāha jāmi'ul-munāfiqīna wal-kāfirīna fī jahannama jamī'ā","at":"Dan sungguh, Allah telah menurunkan (ketentuan) bagimu di dalam Kitab (Al-Qur'an) bahwa apabila kamu mendengar ayat-ayat Allah diingkari dan diperolok-olokkan (oleh orang-orang kafir), maka janganlah kamu duduk bersama mereka, sebelum mereka memasuki pembicaraan yang lain. Karena (kalau tetap duduk dengan mereka), tentulah kamu serupa dengan mereka. Sungguh, Allah akan mengumpulkan semua orang-orang munafik dan orang-orang kafir di neraka Jahanam,"},{"id":"141","ar":" ۨالَّذِيْنَ يَتَرَبَّصُوْنَ بِكُمْۗ فَاِنْ كَانَ لَكُمْ فَتْحٌ مِّنَ اللّٰهِ قَالُوْٓا اَلَمْ نَكُنْ مَّعَكُمْ ۖ وَاِنْ كَانَ لِلْكٰفِرِيْنَ نَصِيْبٌ قَالُوْٓا اَلَمْ نَسْتَحْوِذْ عَلَيْكُمْ وَنَمْنَعْكُمْ مِّنَ الْمُؤْمِنِيْنَ ۗ فَاللّٰهُ يَحْكُمُ بَيْنَكُمْ يَوْمَ الْقِيٰمَةِ ۗ وَلَنْ يَّجْعَلَ اللّٰهُ لِلْكٰفِرِيْنَ عَلَى الْمُؤْمِنِيْنَ سَبِيْلًا","bc":"allażīna yatarabbaṣụna bikum, fa ing kāna lakum fat-ḥum minallāhi qālū a lam nakum ma'akum wa ing kāna lil-kāfirīna naṣībung qālū a lam nastaḥwiż 'alaikum wa namna'kum minal-mu`minīn, fallāhu yaḥkumu bainakum yaumal-qiyāmah, wa lay yaj'alallāhu lil-kāfirīna 'alal-mu`minīna sabīlā","at":"(yaitu) orang yang menunggu-nunggu (peristiwa) yang akan terjadi pada dirimu. Apabila kamu mendapat kemenangan dari Allah mereka berkata, “Bukankah kami (turut berperang) bersama kamu?” Dan jika orang kafir mendapat bagian, mereka berkata, “Bukankah kami turut memenangkanmu, dan membela kamu dari orang mukmin?” Maka Allah akan memberi keputusan di antara kamu pada hari Kiamat. Allah tidak akan memberi jalan kepada orang kafir untuk mengalahkan orang-orang beriman."},{"id":"142","ar":"اِنَّ الْمُنٰفِقِيْنَ يُخٰدِعُوْنَ اللّٰهَ وَهُوَ خَادِعُهُمْۚ وَاِذَا قَامُوْٓا اِلَى الصَّلٰوةِ قَامُوْا كُسَالٰىۙ يُرَاۤءُوْنَ النَّاسَ وَلَا يَذْكُرُوْنَ اللّٰهَ اِلَّا قَلِيْلًاۖ ","bc":"innal-munāfiqīna yukhādi'ụnallāha wa huwa khādi'ụhum, wa iżā qāmū ilaṣ-ṣalāti qāmụ kusālā yurā`ụnan-nāsa wa lā yażkurụnallāha illā qalīlā","at":"Sesungguhnya orang munafik itu hendak menipu Allah, tetapi Allah-lah yang menipu mereka. Apabila mereka berdiri untuk salat, mereka lakukan dengan malas. Mereka bermaksud ria (ingin dipuji) di hadapan manusia. Dan mereka tidak mengingat Allah kecuali sedikit sekali."},{"id":"143","ar":"مُّذَبْذَبِيْنَ بَيْنَ ذٰلِكَۖ لَآ اِلٰى هٰٓؤُلَاۤءِ وَلَآ اِلٰى هٰٓؤُلَاۤءِ ۗ وَمَنْ يُّضْلِلِ اللّٰهُ فَلَنْ تَجِدَ لَهٗ سَبِيْلًا ","bc":"mużabżabīna baina żālika lā ilā hā`ulā`i wa lā ilā hā`ulā`, wa may yuḍlilillāhu fa lan tajida lahụ sabīlā","at":"Mereka dalam keadaan ragu antara yang demikian (iman atau kafir), tidak termasuk kepada golongan ini (orang beriman) dan tidak (pula) kepada golongan itu (orang kafir). Barangsiapa dibiarkan sesat oleh Allah, maka kamu tidak akan mendapatkan jalan (untuk memberi petunjuk) baginya."},{"id":"144","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوا الْكٰفِرِيْنَ اَوْلِيَاۤءَ مِنْ دُوْنِ الْمُؤْمِنِيْنَ ۚ اَتُرِيْدُوْنَ اَنْ تَجْعَلُوْا لِلّٰهِ عَلَيْكُمْ سُلْطٰنًا مُّبِيْنًا ","bc":"yā ayyuhallażīna āmanụ lā tattakhiżul-kāfirīna auliyā`a min dụnil-mu`minīn, a turīdụna an taj'alụ lillāhi 'alaikum sulṭānam mubīnā","at":"Wahai orang-orang yang beriman! Janganlah kamu menjadikan orang-orang kafir sebagai pemimpin selain dari orang-orang mukmin. Apakah kamu ingin memberi alasan yang jelas bagi Allah (untuk menghukummu)?"},{"id":"145","ar":"اِنَّ الْمُنٰفِقِيْنَ فِى الدَّرْكِ الْاَسْفَلِ مِنَ النَّارِۚ وَلَنْ تَجِدَ لَهُمْ نَصِيْرًاۙ","bc":"innal-munāfiqīna fid-darkil-asfali minan-nār, wa lan tajida lahum naṣīrā","at":"Sungguh, orang-orang munafik itu (ditempatkan) pada tingkatan yang paling bawah dari neraka. Dan kamu tidak akan mendapat seorang penolong pun bagi mereka."},{"id":"146","ar":"اِلَّا الَّذِيْنَ تَابُوْا وَاَصْلَحُوْا وَاعْتَصَمُوْا بِاللّٰهِ وَاَخْلَصُوْا دِيْنَهُمْ لِلّٰهِ فَاُولٰۤىِٕكَ مَعَ الْمُؤْمِنِيْنَۗ وَسَوْفَ يُؤْتِ اللّٰهُ الْمُؤْمِنِيْنَ اَجْرًا عَظِيْمًا ","bc":"illallażīna tābụ wa aṣlaḥụ wa'taṣamụ billāhi wa akhlaṣụ dīnahum lillāhi fa ulā`ika ma'al-mu`minīn, wa saufa yu`tillāhul-mu`minīna ajran 'aẓīmā","at":"Kecuali orang-orang yang bertobat dan memperbaiki diri dan berpegang teguh pada (agama) Allah dan dengan tulus ikhlas (menjalankan) agama mereka karena Allah. Maka mereka itu bersama-sama orang-orang yang beriman dan kelak Allah akan memberikan pahala yang besar kepada orang-orang yang beriman. "},{"id":"147","ar":"مَا يَفْعَلُ اللّٰهُ بِعَذَابِكُمْ اِنْ شَكَرْتُمْ وَاٰمَنْتُمْۗ وَكَانَ اللّٰهُ شَاكِرًا عَلِيْمًا ۔","bc":"mā yaf'alullāhu bi'ażābikum in syakartum wa āmantum, wa kānallāhu syākiran 'alīmā","at":"Allah tidak akan menyiksamu, jika kamu bersyukur dan beriman. Dan Allah Maha Mensyukuri, Maha Mengetahui."},{"id":"148","ar":"۞ لَا يُحِبُّ اللّٰهُ الْجَهْرَ بِالسُّوْۤءِ مِنَ الْقَوْلِ اِلَّا مَنْ ظُلِمَ ۗ وَكَانَ اللّٰهُ سَمِيْعًا عَلِيْمًا ","bc":"lā yuḥibbullāhul-jahra bis-sū`i minal-qauli illā man ẓulim, wa kānallāhu samī'an 'alīmā","at":"Allah tidak menyukai perkataan buruk, (yang diucapkan) secara terus terang kecuali oleh orang yang dizalimi. Dan Allah Maha Mendengar, Maha Mengetahui."},{"id":"149","ar":"اِنْ تُبْدُوْا خَيْرًا اَوْ تُخْفُوْهُ اَوْ تَعْفُوْا عَنْ سُوْۤءٍ فَاِنَّ اللّٰهَ كَانَ عَفُوًّا قَدِيْرًا","bc":"in tubdụ khairan au tukhfụhu au ta'fụ 'an sū`in fa innallāha kāna 'afuwwang qadīrā","at":"Jika kamu menyatakan sesuatu kebajikan, menyembunyikannya atau memaafkan suatu kesalahan (orang lain), maka sungguh, Allah Maha Pemaaf, Mahakuasa."},{"id":"150","ar":"اِنَّ الَّذِيْنَ يَكْفُرُوْنَ بِاللّٰهِ وَرُسُلِهٖ وَيُرِيْدُوْنَ اَنْ يُّفَرِّقُوْا بَيْنَ اللّٰهِ وَرُسُلِهٖ وَيَقُوْلُوْنَ نُؤْمِنُ بِبَعْضٍ وَّنَكْفُرُ بِبَعْضٍۙ وَّيُرِيْدُوْنَ اَنْ يَّتَّخِذُوْا بَيْنَ ذٰلِكَ سَبِيْلًاۙ ","bc":"innallażīna yakfurụna billāhi wa rusulihī wa yurīdụna ay yufarriqụ bainallāhi wa rusulihī wa yaqụlụna nu`minu biba'ḍiw wa nakfuru biba'ḍiw wa yurīdụna ay yattakhiżụ baina żālika sabīlā","at":"Sesungguhnya orang-orang yang ingkar kepada Allah dan rasul-rasul-Nya, dan bermaksud membeda-bedakan antara (keimanan kepada) Allah dan rasul-rasul-Nya, dengan mengatakan, “Kami beriman kepada sebagian dan kami mengingkari sebagian (yang lain),” serta bermaksud mengambil jalan tengah (iman atau kafir),"},{"id":"151","ar":"اُولٰۤىِٕكَ هُمُ الْكٰفِرُوْنَ حَقًّا ۚوَاَعْتَدْنَا لِلْكٰفِرِيْنَ عَذَابًا مُّهِيْنًا","bc":"ulā`ika humul-kāfirụna ḥaqqā, wa a'tadnā lil-kāfirīna 'ażābam muhīnā","at":"merekalah orang-orang kafir yang sebenarnya. Dan Kami sediakan untuk orang-orang kafir itu azab yang menghinakan."},{"id":"152","ar":"وَالَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرُسُلِهٖ وَلَمْ يُفَرِّقُوْا بَيْنَ اَحَدٍ مِّنْهُمْ اُولٰۤىِٕكَ سَوْفَ يُؤْتِيْهِمْ اُجُوْرَهُمْ ۗوَكَانَ اللّٰهُ غَفُوْرًا رَّحِيْمًا ","bc":"wallażīna āmanụ billāhi wa rusulihī wa lam yufarriqụ baina aḥadim min-hum ulā`ika saufa yu`tīhim ujụrahum, wa kānallāhu gafụrar raḥīmā","at":"Adapun orang-orang yang beriman kepada Allah dan rasul-rasul-Nya dan tidak membeda-bedakan di antara mereka (para rasul), kelak Allah akan memberikan pahala kepada mereka. Allah Maha Pengampun, Maha Penyayang."},{"id":"153","ar":"يَسْـَٔلُكَ اَهْلُ الْكِتٰبِ اَنْ تُنَزِّلَ عَلَيْهِمْ كِتٰبًا مِّنَ السَّمَاۤءِ فَقَدْ سَاَلُوْا مُوْسٰٓى اَكْبَرَ مِنْ ذٰلِكَ فَقَالُوْٓا اَرِنَا اللّٰهَ جَهْرَةً فَاَخَذَتْهُمُ الصَّاعِقَةُ بِظُلْمِهِمْۚ ثُمَّ اتَّخَذُوا الْعِجْلَ مِنْۢ بَعْدِ مَا جَاۤءَتْهُمُ الْبَيِّنٰتُ فَعَفَوْنَا عَنْ ذٰلِكَ ۚ وَاٰتَيْنَا مُوْسٰى سُلْطٰنًا مُّبِيْنًا","bc":"yas`aluka ahlul-kitābi an tunazzila 'alaihim kitābam minas-samā`i fa qad sa`alụ mụsā akbara min żālika fa qālū arinallāha jahratan fa akhażat-humuṣ-ṣā'iqatu biẓulmihim, ṡummattakhażul-'ijla mim ba'di mā jā`at-humul-bayyinātu fa 'afaunā 'an żālik, wa ātainā mụsā sulṭānam mubīnā","at":"(Orang-orang) Ahli Kitab meminta kepadamu (Muhammad) agar engkau menurunkan sebuah kitab dari langit kepada mereka. Sesungguhnya mereka telah meminta kepada Musa yang lebih besar dari itu. Mereka berkata, “Perlihatkanlah Allah kepada kami secara nyata.” Maka mereka disambar petir karena kezalimannya. Kemudian mereka menyembah anak sapi, setelah mereka melihat bukti-bukti yang nyata, namun demikian Kami maafkan mereka, dan telah Kami berikan kepada Musa kekuasaan yang nyata."},{"id":"154","ar":"وَرَفَعْنَا فَوْقَهُمُ الطُّوْرَ بِمِيْثَاقِهِمْ وَقُلْنَا لَهُمُ ادْخُلُوا الْبَابَ سُجَّدًا وَّقُلْنَا لَهُمْ لَا تَعْدُوْا فِى السَّبْتِ وَاَخَذْنَا مِنْهُمْ مِّيْثَاقًا غَلِيْظًا","bc":"wa rafa'nā fauqahumuṭ-ṭụra bimīṡāqihim wa qulnā lahumudkhulul-bāba sujjadaw wa qulnā lahum lā ta'dụ fis-sabti wa akhażnā min-hum mīṡāqan galīẓā","at":"Dan Kami angkat gunung (Sinai) di atas mereka untuk (menguatkan) perjanjian mereka. Dan Kami perintahkan kepada mereka, “Masukilah pintu gerbang (Baitulmaqdis) itu sambil bersujud,” dan Kami perintahkan (pula) kepada mereka, “Janganlah kamu melanggar peraturan mengenai hari Sabat.” Dan Kami telah mengambil dari mereka perjanjian yang kukuh."},{"id":"155","ar":"فَبِمَا نَقْضِهِمْ مِّيْثَاقَهُمْ وَكُفْرِهِمْ بِاٰيٰتِ اللّٰهِ وَقَتْلِهِمُ الْاَنْۢبِيَاۤءَ بِغَيْرِ حَقٍّ وَّقَوْلِهِمْ قُلُوْبُنَا غُلْفٌ ۗ بَلْ طَبَعَ اللّٰهُ عَلَيْهَا بِكُفْرِهِمْ فَلَا يُؤْمِنُوْنَ اِلَّا قَلِيْلًاۖ ","bc":"fa bimā naqḍihim mīṡāqahum wa kufrihim bi`āyātillāhi wa qatlihimul-ambiyā`a bigairi ḥaqqiw wa qaulihim qulụbunā gulf, bal ṭaba'allāhu 'alaihā bikufrihim fa lā yu`minụna illā qalīlā","at":"Maka (Kami hukum mereka), karena mereka melanggar perjanjian itu, karena kekafiran mereka terhadap keterangan-keterangan Allah, dan karena mereka telah membunuh nabi-nabi tanpa hak (alasan yang benar) dan karena mereka mengatakan, “Hati kami tertutup.” Sebenarnya Allah telah mengunci hati mereka karena kekafirannya, karena itu hanya sebagian kecil dari mereka yang beriman,"},{"id":"156","ar":"وَّبِكُفْرِهِمْ وَقَوْلِهِمْ عَلٰى مَرْيَمَ بُهْتَانًا عَظِيْمًاۙ ","bc":"wa bikufrihim wa qaulihim 'alā maryama buhtānan 'aẓīmā","at":"dan (Kami hukum juga) karena kekafiran mereka (terhadap Isa), dan tuduhan mereka yang sangat keji terhadap Maryam,"},{"id":"157","ar":"وَّقَوْلِهِمْ اِنَّا قَتَلْنَا الْمَسِيْحَ عِيْسَى ابْنَ مَرْيَمَ رَسُوْلَ اللّٰهِۚ وَمَا قَتَلُوْهُ وَمَا صَلَبُوْهُ وَلٰكِنْ شُبِّهَ لَهُمْ ۗوَاِنَّ الَّذِيْنَ اخْتَلَفُوْا فِيْهِ لَفِيْ شَكٍّ مِّنْهُ ۗمَا لَهُمْ بِهٖ مِنْ عِلْمٍ اِلَّا اتِّبَاعَ الظَّنِّ وَمَا قَتَلُوْهُ يَقِيْنًاۢ ۙ ","bc":"wa qaulihim innā qatalnal-masīḥa 'īsabna maryama rasụlallāh, wa mā qatalụhu wa mā ṣalabụhu wa lākin syubbiha lahum, wa innallażīnakhtalafụ fīhi lafī syakkim min-h, mā lahum bihī min 'ilmin illattibā'aẓ-ẓanni wa mā qatalụhu yaqīnā","at":"dan (Kami hukum juga) karena ucapan mereka, “Sesungguhnya kami telah membunuh Al-Masih, Isa putra Maryam, Rasul Allah,” padahal mereka tidak membunuhnya dan tidak (pula) menyalibnya, tetapi (yang mereka bunuh adalah) orang yang diserupakan dengan Isa. Sesungguhnya mereka yang berselisih pendapat tentang (pembunuhan) Isa, selalu dalam keragu-raguan tentang yang dibunuh itu. Mereka benar-benar tidak tahu (siapa sebenarnya yang dibunuh itu), melainkan mengikuti persangkaan belaka, jadi mereka tidak yakin telah membunuhnya."},{"id":"158","ar":"بَلْ رَّفَعَهُ اللّٰهُ اِلَيْهِ ۗوَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا","bc":"bal rafa'ahullāhu ilaīh, wa kānallāhu 'azīzan ḥakīmā","at":"Tetapi Allah telah mengangkat Isa ke hadirat-Nya. Allah Mahaperkasa, Mahabijaksana."},{"id":"159","ar":"وَاِنْ مِّنْ اَهْلِ الْكِتٰبِ اِلَّا لَيُؤْمِنَنَّ بِهٖ قَبْلَ مَوْتِهٖ ۚوَيَوْمَ الْقِيٰمَةِ يَكُوْنُ عَلَيْهِمْ شَهِيْدًاۚ ","bc":"wa im min ahlil-kitābi illā layu`minanna bihī qabla mautih, wa yaumal-qiyāmati yakụnu 'alaihim syahīdā","at":"Tidak ada seorang pun di antara Ahli Kitab yang tidak beriman kepadanya (Isa) menjelang kematiannya. Dan pada hari Kiamat dia (Isa) akan menjadi saksi mereka."},{"id":"160","ar":"فَبِظُلْمٍ مِّنَ الَّذِيْنَ هَادُوْا حَرَّمْنَا عَلَيْهِمْ طَيِّبٰتٍ اُحِلَّتْ لَهُمْ وَبِصَدِّهِمْ عَنْ سَبِيْلِ اللّٰهِ كَثِيْرًاۙ ","bc":"fa biẓulmim minallażīna hādụ ḥarramnā 'alaihim ṭayyibātin uḥillat lahum wa biṣaddihim 'an sabīlillāhi kaṡīrā","at":"Karena kezaliman orang-orang Yahudi, Kami haramkan bagi mereka makanan yang baik-baik yang (dahulu) pernah dihalalkan; dan karena mereka sering menghalangi (orang lain) dari jalan Allah,"},{"id":"161","ar":"وَّاَخْذِهِمُ الرِّبٰوا وَقَدْ نُهُوْا عَنْهُ وَاَكْلِهِمْ اَمْوَالَ النَّاسِ بِالْبَاطِلِ ۗوَاَعْتَدْنَا لِلْكٰفِرِيْنَ مِنْهُمْ عَذَابًا اَلِيْمًا","bc":"wa akhżihimur-ribā wa qad nuhụ 'an-hu wa aklihim amwālan-nāsi bil-bāṭil, wa a'tadnā lil-kāfirīna min-hum 'ażāban alīmā","at":"dan karena mereka menjalankan riba, padahal sungguh mereka telah dilarang darinya, dan karena mereka memakan harta orang dengan cara tidak sah (batil). Dan Kami sediakan untuk orang-orang kafir di antara mereka azab yang pedih."},{"id":"162","ar":"لٰكِنِ الرَّاسِخُوْنَ فِى الْعِلْمِ مِنْهُمْ وَالْمُؤْمِنُوْنَ يُؤْمِنُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمَآ اُنْزِلَ مِنْ قَبْلِكَ وَالْمُقِيْمِيْنَ الصَّلٰوةَ وَالْمُؤْتُوْنَ الزَّكٰوةَ وَالْمُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۗ اُولٰۤىِٕكَ سَنُؤْتِيْهِمْ اَجْرًا عَظِيْمًا","bc":"lākinir-rāsikhụna fil-'ilmi min-hum wal-mu`minụna yu`minụna bimā unzila ilaika wa mā unzila ming qablika wal-muqīmīnaṣ-ṣalāta wal-mu`tụnaz-zakāta wal-mu`minụna billāhi wal-yaumil-ākhir, ulā`ika sanu`tīhim ajran 'aẓīmā","at":"Tetapi orang-orang yang ilmunya mendalam di antara mereka, dan orang-orang yang beriman, mereka beriman kepada (Al-Qur'an) yang diturunkan kepadamu (Muhammad), dan kepada (kitab-kitab) yang diturunkan sebelummu, begitu pula mereka yang melaksanakan salat dan menunaikan zakat dan beriman kepada Allah dan hari kemudian. Kepada mereka akan Kami berikan pahala yang besar."},{"id":"163","ar":"۞ اِنَّآ اَوْحَيْنَآ اِلَيْكَ كَمَآ اَوْحَيْنَآ اِلٰى نُوْحٍ وَّالنَّبِيّٖنَ مِنْۢ بَعْدِهٖۚ وَاَوْحَيْنَآ اِلٰٓى اِبْرٰهِيْمَ وَاِسْمٰعِيْلَ وَاِسْحٰقَ وَيَعْقُوْبَ وَالْاَسْبَاطِ وَعِيْسٰى وَاَيُّوْبَ وَيُوْنُسَ وَهٰرُوْنَ وَسُلَيْمٰنَ ۚوَاٰتَيْنَا دَاوٗدَ زَبُوْرًاۚ ","bc":"innā auḥainā ilaika kamā auḥainā ilā nụḥiw wan-nabiyyīna mim ba'dih, wa auḥainā ilā ibrāhīma wa ismā'īla wa is-ḥāqa wa ya'qụba wal-asbāṭi wa 'īsā wa ayyụba wa yụnusa wa hārụna wa sulaimān, wa ātainā dāwụda zabụrā","at":"Sesungguhnya Kami mewahyukan kepadamu (Muhammad) sebagaimana Kami telah mewahyukan kepada Nuh dan nabi-nabi setelahnya, dan Kami telah mewahyukan (pula) kepada Ibrahim, Ismail, Ishak, Yakub dan anak cucunya; Isa, Ayyub, Yunus, Harun dan Sulaiman. Dan Kami telah memberikan Kitab Zabur kepada Dawud."},{"id":"164","ar":"وَرُسُلًا قَدْ قَصَصْنٰهُمْ عَلَيْكَ مِنْ قَبْلُ وَرُسُلًا لَّمْ نَقْصُصْهُمْ عَلَيْكَ ۗوَكَلَّمَ اللّٰهُ مُوْسٰى تَكْلِيْمًاۚ ","bc":"wa rusulang qad qaṣaṣnāhum 'alaika ming qablu wa rusulal lam naqṣuṣ-hum 'alaīk, wa kallamallāhu mụsā taklīmā","at":"Dan ada beberapa rasul yang telah Kami kisahkan mereka kepadamu sebelumnya dan ada beberapa rasul (la-in) yang tidak Kami kisahkan mereka kepadamu. Dan kepada Musa, Allah berfirman langsung."},{"id":"165","ar":"رُسُلًا مُّبَشِّرِيْنَ وَمُنْذِرِيْنَ لِئَلَّا يَكُوْنَ لِلنَّاسِ عَلَى اللّٰهِ حُجَّةٌ ۢ بَعْدَ الرُّسُلِ ۗوَكَانَ اللّٰهُ عَزِيْزًا حَكِيْمًا","bc":"rusulam mubasysyirīna wa munżirīna li`allā yakụna lin-nāsi 'alallāhi ḥujjatum ba'dar-rusul, wa kānallāhu 'azīzan ḥakīmā","at":"Rasul-rasul itu adalah sebagai pembawa berita gembira dan pemberi peringatan, agar tidak ada alasan bagi manusia untuk membantah Allah setelah rasul-rasul itu diutus. Allah Mahaperkasa, Mahabijaksana."},{"id":"166","ar":"لٰكِنِ اللّٰهُ يَشْهَدُ بِمَآ اَنْزَلَ اِلَيْكَ اَنْزَلَهٗ بِعِلْمِهٖ ۚوَالْمَلٰۤىِٕكَةُ يَشْهَدُوْنَ ۗوَكَفٰى بِاللّٰهِ شَهِيْدًاۗ ","bc":"lākinillāhu yasy-hadu bimā anzala ilaika anzalahụ bi'ilmih, wal-malā`ikatu yasy-hadụn, wa kafā billāhi syahīdā","at":"Tetapi Allah menjadi saksi atas (Al-Qur'an) yang diturunkan-Nya kepadamu (Muhammad). Dia menurunkannya dengan ilmu-Nya, dan para malaikat pun menyaksikan. Dan cukuplah Allah yang menjadi saksi."},{"id":"167","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ قَدْ ضَلُّوْا ضَلٰلًا ۢ بَعِيْدًا","bc":"innallażīna kafarụ wa ṣaddụ 'an sabīlillāhi qad ḍallụ ḍalālam ba'īdā","at":"Sesungguhnya orang-orang yang kafir dan menghalang-halangi (orang lain) dari jalan Allah, benar-benar telah sesat sejauh-jauhnya. "},{"id":"168","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا وَظَلَمُوْا لَمْ يَكُنِ اللّٰهُ لِيَغْفِرَ لَهُمْ وَلَا لِيَهْدِيَهُمْ طَرِيْقًاۙ ","bc":"innallażīna kafarụ wa ẓalamụ lam yakunillāhu liyagfira lahum wa lā liyahdiyahum ṭarīqā","at":"Sesungguhnya orang-orang yang kafir dan melakukan kezaliman, Allah tidak akan mengampuni mereka, dan tidak (pula) akan menunjukkan kepada mereka jalan (yang lurus), "},{"id":"169","ar":"اِلَّا طَرِيْقَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗوَكَانَ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرًا","bc":"illā ṭarīqa jahannama khālidīna fīhā abadā, wa kāna żālika 'alallāhi yasīrā","at":"kecuali jalan ke neraka Jahanam; mereka kekal di dalamnya selama-lamanya. Dan hal itu (sangat) mudah bagi Allah."},{"id":"170","ar":"يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمُ الرَّسُوْلُ بِالْحَقِّ مِنْ رَّبِّكُمْ فَاٰمِنُوْا خَيْرًا لَّكُمْ ۗوَاِنْ تَكْفُرُوْا فَاِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ وَكَانَ اللّٰهُ عَلِيْمًا حَكِيْمًا","bc":"yā ayyuhan-nāsu qad jā`akumur-rasụlu bil-ḥaqqi mir rabbikum fa āminụ khairal lakum, wa in takfurụ fa inna lillāhi mā fis-samāwāti wal-arḍ, wa kānallāhu 'alīman ḥakīmā","at":"Wahai manusia! Sungguh, telah datang Rasul (Muhammad) kepadamu dengan (membawa) kebenaran dari Tuhanmu, maka berimanlah (kepadanya), itu lebih baik bagimu. Dan jika kamu kafir, (itu tidak merugikan Allah sedikit pun) karena sesungguhnya milik Allah-lah apa yang di langit dan di bumi. Allah Maha Mengetahui, Mahabijaksana."},{"id":"171","ar":"يٰٓاَهْلَ الْكِتٰبِ لَا تَغْلُوْا فِيْ دِيْنِكُمْ وَلَا تَقُوْلُوْا عَلَى اللّٰهِ اِلَّا الْحَقَّۗ اِنَّمَا الْمَسِيْحُ عِيْسَى ابْنُ مَرْيَمَ رَسُوْلُ اللّٰهِ وَكَلِمَتُهٗ ۚ اَلْقٰهَآ اِلٰى مَرْيَمَ وَرُوْحٌ مِّنْهُ ۖفَاٰمِنُوْا بِاللّٰهِ وَرُسُلِهٖۗ وَلَا تَقُوْلُوْا ثَلٰثَةٌ ۗاِنْتَهُوْا خَيْرًا لَّكُمْ ۗ اِنَّمَا اللّٰهُ اِلٰهٌ وَّاحِدٌ ۗ سُبْحٰنَهٗٓ اَنْ يَّكُوْنَ لَهٗ وَلَدٌ ۘ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَكَفٰى بِاللّٰهِ وَكِيْلًا ","bc":"yā ahlal-kitābi lā taglụ fī dīnikum wa lā taqụlụ 'alallāhi illal-ḥaqq, innamal-masīḥu 'īsabnu maryama rasụlullāhi wa kalimatuh, alqāhā ilā maryama wa rụḥum min-hu fa āminụ billāhi wa rusulih, wa lā taqụlụ ṡalāṡah, intahụ khairal lakum, innamallāhu ilāhuw wāḥid, sub-ḥānahū ay yakụna lahụ walad, lahụ mā fis-samāwāti wa mā fil-arḍ, wa kafā billāhi wakīlā","at":"Wahai Ahli Kitab! Janganlah kamu melampaui batas dalam agamamu, dan janganlah kamu mengatakan terhadap Allah kecuali yang benar. Sungguh, Al-Masih Isa putra Maryam itu adalah utusan Allah dan (yang diciptakan dengan) kalimat-Nya yang disampaikan-Nya kepada Maryam, dan (dengan tiupan) roh dari-Nya. Maka berimanlah kepada Allah dan rasul-rasul-Nya dan janganlah kamu mengatakan, “(Tuhan itu) tiga,” berhentilah (dari ucapan itu). (Itu) lebih baik bagimu. Sesungguhnya Allah Tuhan Yang Maha Esa, Mahasuci Dia dari (anggapan) mempunyai anak. Milik-Nyalah apa yang ada di langit dan apa yang ada di bumi. Dan cukuplah Allah sebagai pelindung."},{"id":"172","ar":"لَنْ يَّسْتَنْكِفَ الْمَسِيْحُ اَنْ يَّكُوْنَ عَبْدًا لِّلّٰهِ وَلَا الْمَلٰۤىِٕكَةُ الْمُقَرَّبُوْنَۗ وَمَنْ يَّسْتَنْكِفْ عَنْ عِبَادَتِهٖ وَيَسْتَكْبِرْ فَسَيَحْشُرُهُمْ اِلَيْهِ جَمِيْعًا","bc":"lay yastangkifal-masīḥu ay yakụna 'abdal lillāhi wa lal-malā`ikatul-muqarrabụn, wa may yastangkif 'an 'ibādatihī wa yastakbir fa sayaḥsyuruhum ilaihi jamī'ā","at":"Al-Masih sama sekali tidak enggan menjadi hamba Allah, dan begitu pula para malaikat yang terdekat (kepada Allah). Dan barangsiapa enggan menyembah-Nya dan menyombongkan diri, maka Allah akan mengumpulkan mereka semua kepada-Nya."},{"id":"173","ar":"فَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَيُوَفِّيْهِمْ اُجُوْرَهُمْ وَيَزِيْدُهُمْ مِّنْ فَضْلِهٖۚ وَاَمَّا الَّذِيْنَ اسْتَنْكَفُوْا وَاسْتَكْبَرُوْا فَيُعَذِّبُهُمْ عَذَابًا اَلِيْمًاۙ وَّلَا يَجِدُوْنَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ وَلِيًّا وَّلَا نَصِيْرًا","bc":"fa ammallażīna āmanụ wa 'amiluṣ-ṣāliḥāti fa yuwaffīhim ujụrahum wa yazīduhum min faḍlih, wa ammallażīnastangkafụ wastakbarụ fa yu'ażżibuhum 'ażāban alīmaw wa lā yajidụna lahum min dụnillāhi waliyyaw wa lā naṣīrā","at":"Adapun orang-orang yang beriman dan mengerjakan kebajikan, Allah akan menyempurnakan pahala bagi mereka dan menambah sebagian dari karunia-Nya. Sedangkan orang-orang yang enggan (menyembah Allah) dan menyombongkan diri, maka Allah akan mengazab mereka dengan azab yang pedih. Dan mereka tidak akan mendapatkan pelindung dan penolong selain Allah."},{"id":"174","ar":"يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمْ بُرْهَانٌ مِّنْ رَّبِّكُمْ وَاَنْزَلْنَآ اِلَيْكُمْ نُوْرًا مُّبِيْنًا","bc":"yā ayyuhan-nāsu qad jā`akum bur-hānum mir rabbikum wa anzalnā ilaikum nụram mubīnā","at":"Wahai manusia! Sesungguhnya telah sampai kepadamu bukti kebenaran dari Tuhanmu, (Muhammad dengan mukjizatnya) dan telah Kami turunkan kepadamu cahaya yang terang benderang (Al-Qur'an)."},{"id":"175","ar":"فَاَمَّا الَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَاعْتَصَمُوْا بِهٖ فَسَيُدْخِلُهُمْ فِيْ رَحْمَةٍ مِّنْهُ وَفَضْلٍۙ وَّيَهْدِيْهِمْ اِلَيْهِ صِرَاطًا مُّسْتَقِيْمًاۗ ","bc":"fa ammallażīna āmanụ billāhi wa'taṣamụ bihī fa sayudkhiluhum fī raḥmatim min-hu wa faḍliw wa yahdīhim ilaihi ṣirāṭam mustaqīmā","at":"Adapun orang-orang yang beriman kepada Allah dan berpegang teguh kepada (agama)-Nya, maka Allah akan memasukkan mereka ke dalam rahmat dan karunia dari-Nya (surga), dan menunjukkan mereka jalan yang lurus kepada-Nya."},{"id":"176","ar":"يَسْتَفْتُوْنَكَۗ قُلِ اللّٰهُ يُفْتِيْكُمْ فِى الْكَلٰلَةِ ۗاِنِ امْرُؤٌا هَلَكَ لَيْسَ لَهٗ وَلَدٌ وَّلَهٗٓ اُخْتٌ فَلَهَا نِصْفُ مَا تَرَكَۚ وَهُوَ يَرِثُهَآ اِنْ لَّمْ يَكُنْ لَّهَا وَلَدٌ ۚ فَاِنْ كَانَتَا اثْنَتَيْنِ فَلَهُمَا الثُّلُثٰنِ مِمَّا تَرَكَ ۗوَاِنْ كَانُوْٓا اِخْوَةً رِّجَالًا وَّنِسَاۤءً فَلِلذَّكَرِ مِثْلُ حَظِّ الْاُنْثَيَيْنِۗ يُبَيِّنُ اللّٰهُ لَكُمْ اَنْ تَضِلُّوْا ۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ ","bc":"yastaftụnak, qulillāhu yuftīkum fil-kalālah, inimru`un halaka laisa lahụ waladuw wa lahū ukhtun fa lahā niṣfu mā tarak, wa huwa yariṡuhā il lam yakul lahā walad, fa ing kānataṡnataini fa lahumaṡ-ṡuluṡāni mimmā tarak, wa ing kānū ikhwatar rijālaw wa nisā`an fa liż-żakari miṡlu ḥaẓẓil-unṡayaīn, yubayyinullāhu lakum an taḍillụ, wallāhu bikulli syai`in 'alīm","at":"Mereka meminta fatwa kepadamu (tentang kalalah). Katakanlah, “Allah memberi fatwa kepadamu tentang kalalah (yaitu), jika seseorang mati dan dia tidak mempunyai anak tetapi mempunyai saudara perempuan, maka bagiannya (saudara perempuannya itu) seperdua dari harta yang ditinggalkannya, dan saudaranya yang laki-laki mewarisi (seluruh harta saudara perempuan), jika dia tidak mempunyai anak. Tetapi jika saudara perempuan itu dua orang, maka bagi keduanya dua pertiga dari harta yang ditinggalkan. Dan jika mereka (ahli waris itu terdiri dari) saudara-saudara laki-laki dan perempuan, maka bagian seorang saudara laki-laki sama dengan bagian dua saudara perempuan. Allah menerangkan (hukum ini) kepadamu, agar kamu tidak sesat. Allah Maha Mengetahui segala sesuatu.”"}],an_nur=[{"id":"1","ar":"سُوْرَةٌ اَنْزَلْنٰهَا وَفَرَضْنٰهَا وَاَنْزَلْنَا فِيْهَآ اٰيٰتٍۢ بَيِّنٰتٍ لَّعَلَّكُمْ تَذَكَّرُوْنَ","bc":"sụratun anzalnāhā wa faraḍnāhā wa anzalnā fīhā āyātim bayyinātil la'allakum tażakkarụn","at":"(Inilah) suatu surah yang Kami turunkan dan Kami wajibkan (menjalankan hukum-hukum)nya, dan Kami turunkan di dalamnya tanda-tanda (kebesaran Allah) yang jelas, agar kamu ingat."},{"id":"2","ar":"اَلزَّانِيَةُ وَالزَّانِيْ فَاجْلِدُوْا كُلَّ وَاحِدٍ مِّنْهُمَا مِائَةَ جَلْدَةٍ ۖوَّلَا تَأْخُذْكُمْ بِهِمَا رَأْفَةٌ فِيْ دِيْنِ اللّٰهِ اِنْ كُنْتُمْ تُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِۚ وَلْيَشْهَدْ عَذَابَهُمَا طَاۤىِٕفَةٌ مِّنَ الْمُؤْمِنِيْنَ","bc":"az-zāniyatu waz-zānī fajlidụ kulla wāḥidim min-humā mi`ata jaldatiw wa lā ta`khużkum bihimā ra`fatun fī dīnillāhi ing kuntum tu`minụna billāhi wal-yaumil-ākhir, walyasy-had 'ażābahumā ṭā`ifatum minal-mu`minīn","at":"Pezina perempuan dan pezina laki-laki, deralah masing-masing dari keduanya seratus kali, dan janganlah rasa belas kasihan kepada keduanya mencegah kamu untuk (menjalankan) agama (hukum) Allah, jika kamu beriman kepada Allah dan hari kemudian; dan hendaklah (pelaksanaan) hukuman mereka disaksikan oleh sebagian orang-orang yang beriman."},{"id":"3","ar":"اَلزَّانِيْ لَا يَنْكِحُ اِلَّا زَانِيَةً اَوْ مُشْرِكَةً ۖوَّالزَّانِيَةُ لَا يَنْكِحُهَآ اِلَّا زَانٍ اَوْ مُشْرِكٌۚ وَحُرِّمَ ذٰلِكَ عَلَى الْمُؤْمِنِيْنَ","bc":"az-zānī lā yangkiḥu illā zāniyatan au musyrikataw waz-zāniyatu lā yangkiḥuhā illā zānin au musyrik, wa ḥurrima żālika 'alal-mu`minīn","at":"Pezina laki-laki tidak boleh menikah kecuali dengan pezina perempuan, atau dengan perempuan musyrik; dan pezina perempuan tidak boleh menikah kecuali dengan pezina laki-laki atau dengan laki-laki musyrik; dan yang demikian itu diharamkan bagi orang-orang mukmin."},{"id":"4","ar":"وَالَّذِيْنَ يَرْمُوْنَ الْمُحْصَنٰتِ ثُمَّ لَمْ يَأْتُوْا بِاَرْبَعَةِ شُهَدَاۤءَ فَاجْلِدُوْهُمْ ثَمٰنِيْنَ جَلْدَةً وَّلَا تَقْبَلُوْا لَهُمْ شَهَادَةً اَبَدًاۚ وَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ ۙ","bc":"wallażīna yarmụnal-muḥṣanāti ṡumma lam ya`tụ bi`arba'ati syuhadā`a fajlidụhum ṡamānīna jaldataw wa lā taqbalụ lahum syahādatan abadā, wa ulā`ika humul-fāsiqụn","at":"Dan orang-orang yang menuduh perempuan-perempuan yang baik (berzina) dan mereka tidak mendatangkan empat orang saksi, maka deralah mereka delapan puluh kali, dan janganlah kamu terima kesaksian mereka untuk selama-lamanya. Mereka itulah orang-orang yang fasik,"},{"id":"5","ar":"اِلَّا الَّذِيْنَ تَابُوْا مِنْۢ بَعْدِ ذٰلِكَ وَاَصْلَحُوْاۚ فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ","bc":"illallażīna tābụ mim ba'di żālika wa aṣlaḥụ, fa innallāha gafụrur raḥīm","at":"kecuali mereka yang bertobat setelah itu dan memperbaiki (dirinya), maka sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"6","ar":"وَالَّذِيْنَ يَرْمُوْنَ اَزْوَاجَهُمْ وَلَمْ يَكُنْ لَّهُمْ شُهَدَاۤءُ اِلَّآ اَنْفُسُهُمْ فَشَهَادَةُ اَحَدِهِمْ اَرْبَعُ شَهٰدٰتٍۢ بِاللّٰهِ ۙاِنَّهٗ لَمِنَ الصّٰدِقِيْنَ","bc":"wallażīna yarmụna azwājahum wa lam yakul lahum syuhadā`u illā anfusuhum fa syahādatu aḥadihim arba'u syahādātim billāhi innahụ laminaṣ-ṣādiqīn","at":"Dan orang-orang yang menuduh istrinya (berzina), padahal mereka tidak mempunyai saksi-saksi selain diri mereka sendiri, maka kesaksian masing-masing orang itu ialah empat kali bersumpah dengan (nama) Allah, bahwa sesungguhnya dia termasuk orang yang berkata benar."},{"id":"7","ar":"وَالْخَامِسَةُ اَنَّ لَعْنَتَ اللّٰهِ عَلَيْهِ اِنْ كَانَ مِنَ الْكٰذِبِيْنَ","bc":"wal-khāmisatu anna la'natallāhi 'alaihi ing kāna minal-kāżibīn","at":"Dan (sumpah) yang kelima bahwa laknat Allah akan menimpanya, jika dia termasuk orang yang berdusta."},{"id":"8","ar":"وَيَدْرَؤُا عَنْهَا الْعَذَابَ اَنْ تَشْهَدَ اَرْبَعَ شَهٰدٰتٍۢ بِاللّٰهِ اِنَّهٗ لَمِنَ الْكٰذِبِيْنَ ۙ","bc":"wa yadra`u 'an-hal-'ażāba an tasy-hada arba'a syahādātim billāhi innahụ laminal-kāżibīn","at":"Dan istri itu terhindar dari hukuman apabila dia bersumpah empat kali atas (nama) Allah bahwa dia (suaminya) benar-benar termasuk orang-orang yang berdusta,"},{"id":"9","ar":"وَالْخَامِسَةَ اَنَّ غَضَبَ اللّٰهِ عَلَيْهَآ اِنْ كَانَ مِنَ الصّٰدِقِيْنَ ","bc":"wal-khāmisata anna gaḍaballāhi 'alaihā ing kāna minaṣ-ṣādiqīn","at":"dan (sumpah) yang kelima bahwa kemurkaan Allah akan menimpanya (istri), jika dia (suaminya) itu termasuk orang yang berkata benar. "},{"id":"10","ar":"وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ وَاَنَّ اللّٰهَ تَوَّابٌ حَكِيْمٌ","bc":"walau lā faḍlullāhi 'alaikum wa raḥmatuhụ wa annallāha tawwābun ḥakīm","at":"Dan seandainya bukan karena karunia Allah dan rahmat-Nya kepadamu (niscaya kamu akan menemui kesulitan). Dan sesungguhnya Allah Maha Penerima Tobat, Mahabijaksana."},{"id":"11","ar":"اِنَّ الَّذِيْنَ جَاۤءُوْ بِالْاِفْكِ عُصْبَةٌ مِّنْكُمْۗ لَا تَحْسَبُوْهُ شَرًّا لَّكُمْۗ بَلْ هُوَ خَيْرٌ لَّكُمْۗ لِكُلِّ امْرِئٍ مِّنْهُمْ مَّا اكْتَسَبَ مِنَ الْاِثْمِۚ وَالَّذِيْ تَوَلّٰى كِبْرَهٗ مِنْهُمْ لَهٗ عَذَابٌ عَظِيْمٌ","bc":"innallażīna jā`ụ bil-ifki 'uṣbatum mingkum, lā taḥsabụhu syarral lakum, bal huwa khairul lakum, likullimri`im min-hum maktasaba minal-iṡm, wallażī tawallā kibrahụ min-hum lahụ 'ażābun 'aẓīm","at":"Sesungguhnya orang-orang yang membawa berita bohong itu adalah dari golongan kamu (juga). Janganlah kamu mengira berita itu buruk bagi kamu bahkan itu baik bagi kamu. Setiap orang dari mereka akan mendapat balasan dari dosa yang diperbuatnya. Dan barangsiapa di antara mereka yang mengambil bagian terbesar (dari dosa yang diperbuatnya), dia mendapat azab yang besar (pula)."},{"id":"12","ar":"لَوْلَآ اِذْ سَمِعْتُمُوْهُ ظَنَّ الْمُؤْمِنُوْنَ وَالْمُؤْمِنٰتُ بِاَنْفُسِهِمْ خَيْرًاۙ وَّقَالُوْا هٰذَآ اِفْكٌ مُّبِيْنٌ","bc":"lau lā iż sami'tumụhu ẓannal-mu`minụna wal-mu`minātu bi`anfusihim khairaw wa qālụ hāżā ifkum mubīn","at":"Mengapa orang-orang mukmin dan mukminat tidak berbaik sangka terhadap diri mereka sendiri, ketika kamu mendengar berita bohong itu dan berkata, “Ini adalah (suatu berita) bohong yang nyata.”"},{"id":"13","ar":"لَوْلَا جَاۤءُوْ عَلَيْهِ بِاَرْبَعَةِ شُهَدَاۤءَۚ فَاِذْ لَمْ يَأْتُوْا بِالشُّهَدَاۤءِ فَاُولٰۤىِٕكَ عِنْدَ اللّٰهِ هُمُ الْكٰذِبُوْنَ","bc":"lau lā jā`ụ 'alaihi bi`arba'ati syuhadā`, fa iż lam ya`tụ bisy-syuhadā`i fa ulā`ika 'indallāhi humul-kāżibụn","at":"Mengapa mereka (yang menuduh itu) tidak datang membawa empat saksi? Oleh karena mereka tidak membawa saksi-saksi, maka mereka itu dalam pandangan Allah adalah orang-orang yang berdusta."},{"id":"14","ar":"وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ فِى الدُّنْيَا وَالْاٰخِرَةِ لَمَسَّكُمْ فِيْ مَآ اَفَضْتُمْ فِيْهِ عَذَابٌ عَظِيْمٌ ","bc":"walau lā faḍlullāhi 'alaikum wa raḥmatuhụ fid-dun-yā wal-ākhirati lamassakum fī mā afaḍtum fīhi 'ażābun 'aẓīm","at":"Dan seandainya bukan karena karunia Allah dan rahmat-Nya kepadamu di dunia dan di akhirat, niscaya kamu ditimpa azab yang besar, disebabkan oleh pembicaraan kamu tentang hal itu (berita bohong itu)."},{"id":"15","ar":"اِذْ تَلَقَّوْنَهٗ بِاَلْسِنَتِكُمْ وَتَقُوْلُوْنَ بِاَفْوَاهِكُمْ مَّا لَيْسَ لَكُمْ بِهٖ عِلْمٌ وَّتَحْسَبُوْنَهٗ هَيِّنًاۙ وَّهُوَ عِنْدَ اللّٰهِ عَظِيْمٌ ۚ","bc":"iż talaqqaunahụ bi`alsinatikum wa taqụlụna bi`afwāhikum mā laisa lakum bihī 'ilmuw wa taḥsabụnahụ hayyinaw wa huwa 'indallāhi 'aẓīm","at":"(Ingatlah) ketika kamu menerima (berita bohong) itu dari mulut ke mulut dan kamu katakan dengan mulutmu apa yang tidak kamu ketahui sedikit pun, dan kamu menganggapnya remeh, padahal dalam pandangan Allah itu soal besar."},{"id":"16","ar":"وَلَوْلَآ اِذْ سَمِعْتُمُوْهُ قُلْتُمْ مَّا يَكُوْنُ لَنَآ اَنْ نَّتَكَلَّمَ بِهٰذَاۖ سُبْحٰنَكَ هٰذَا بُهْتَانٌ عَظِيْمٌ","bc":"walau lā iż sami'tumụhu qultum mā yakụnu lanā an natakallama bihāżā sub-ḥānaka hāżā buhtānun 'aẓīm","at":"Dan mengapa kamu tidak berkata ketika mendengarnya, “Tidak pantas bagi kita membicarakan ini. Mahasuci Engkau, ini adalah kebohongan yang besar.”"},{"id":"17","ar":"يَعِظُكُمُ اللّٰهُ اَنْ تَعُوْدُوْا لِمِثْلِهٖٓ اَبَدًا اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ۚ","bc":"ya'iẓukumullāhu an ta'ụdụ limiṡlihī abadan ing kuntum mu`minīn","at":"Allah memperingatkan kamu agar (jangan) kembali mengulangi seperti itu selama-lamanya, jika kamu orang beriman,"},{"id":"18","ar":"وَيُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ","bc":"wa yubayyinullāhu lakumul-āyāt, wallāhu 'alīmun ḥakīm","at":"dan Allah menjelaskan ayat-ayat(-Nya) kepada kamu. Dan Allah Maha Mengetahui, Mahabijaksana."},{"id":"19","ar":"اِنَّ الَّذِيْنَ يُحِبُّوْنَ اَنْ تَشِيْعَ الْفَاحِشَةُ فِى الَّذِيْنَ اٰمَنُوْا لَهُمْ عَذَابٌ اَلِيْمٌۙ فِى الدُّنْيَا وَالْاٰخِرَةِۗ وَاللّٰهُ يَعْلَمُ وَاَنْتُمْ لَا تَعْلَمُوْنَ","bc":"innallażīna yuḥibbụna an tasyī'al-fāḥisyatu fillażīna āmanụ lahum 'ażābun alīmun fid-dun-yā wal-ākhirah, wallāhu ya'lamu wa antum lā ta'lamụn","at":"Sesungguhnya orang-orang yang ingin agar perbuatan yang sangat keji itu (berita bohong) tersiar di kalangan orang-orang yang beriman, mereka mendapat azab yang pedih di dunia dan di akhirat. Dan Allah mengetahui, sedang kamu tidak mengetahui."},{"id":"20","ar":"وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ وَاَنَّ اللّٰهَ رَءُوْفٌ رَّحِيْمٌ ","bc":"walau lā faḍlullāhi 'alaikum wa raḥmatuhụ wa annallāha ra`ụfur raḥīm","at":"Dan kalau bukan karena karunia Allah dan rahmat-Nya kepadamu (niscaya kamu akan ditimpa azab yang besar). Sungguh, Allah Maha Penyantun, Ma-ha Penyayang. "},{"id":"21","ar":"۞ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّبِعُوْا خُطُوٰتِ الشَّيْطٰنِۗ وَمَنْ يَّتَّبِعْ خُطُوٰتِ الشَّيْطٰنِ فَاِنَّهٗ يَأْمُرُ بِالْفَحْشَاۤءِ وَالْمُنْكَرِۗ وَلَوْلَا فَضْلُ اللّٰهِ عَلَيْكُمْ وَرَحْمَتُهٗ مَا زَكٰى مِنْكُمْ مِّنْ اَحَدٍ اَبَدًاۙ وَّلٰكِنَّ اللّٰهَ يُزَكِّيْ مَنْ يَّشَاۤءُۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ","bc":"yā ayyuhallażīna āmanụ lā tattabi'ụ khuṭuwātisy-syaiṭān, wa may yattabi' khuṭuwātisy-syaiṭāni fa innahụ ya`muru bil-faḥsyā`i wal-mungkar, walau lā faḍlullāhi 'alaikum wa raḥmatuhụ mā zakā mingkum min aḥadin abadaw wa lākinnallāha yuzakkī may yasyā`, wallāhu samī'un 'alīm","at":"Wahai orang-orang yang beriman! Janganlah kamu mengikuti langkah-langkah setan. Barangsiapa mengikuti langkah-langkah setan, maka sesungguhnya dia (setan) menyuruh mengerjakan perbuatan yang keji dan mungkar. Kalau bukan karena karunia Allah dan rahmat-Nya kepadamu, niscaya tidak seorang pun di antara kamu bersih (dari perbuatan keji dan mungkar itu) selama-lamanya, tetapi Allah membersihkan siapa yang Dia kehendaki. Dan Allah Maha Mendengar, Maha Mengetahui."},{"id":"22","ar":"وَلَا يَأْتَلِ اُولُو الْفَضْلِ مِنْكُمْ وَالسَّعَةِ اَنْ يُّؤْتُوْٓا اُولِى الْقُرْبٰى وَالْمَسٰكِيْنَ وَالْمُهٰجِرِيْنَ فِيْ سَبِيْلِ اللّٰهِ ۖوَلْيَعْفُوْا وَلْيَصْفَحُوْاۗ اَلَا تُحِبُّوْنَ اَنْ يَّغْفِرَ اللّٰهُ لَكُمْ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ","bc":"wa lā ya`tali ulul-faḍli mingkum was-sa'ati ay yu`tū ulil-qurbā wal-masākīna wal-muhājirīna fī sabīlillāhi walya'fụ walyaṣfaḥụ, alā tuḥibbụna ay yagfirallāhu lakum, wallāhu gafụrur raḥīm","at":"Dan janganlah orang-orang yang mempunyai kelebihan dan kelapangan di antara kamu bersumpah bahwa mereka (tidak) akan memberi (bantuan) kepada kerabat(nya), orang-orang miskin dan orang-orang yang berhijrah di jalan Allah, dan hendaklah mereka memaafkan dan berlapang dada. Apakah kamu tidak suka bahwa Allah mengampunimu? Dan Allah Maha Pengampun, Maha Penyayang."},{"id":"23","ar":"اِنَّ الَّذِيْنَ يَرْمُوْنَ الْمُحْصَنٰتِ الْغٰفِلٰتِ الْمُؤْمِنٰتِ لُعِنُوْا فِى الدُّنْيَا وَالْاٰخِرَةِۖ وَلَهُمْ عَذَابٌ عَظِيْمٌ ۙ","bc":"innallażīna yarmụnal-muḥṣanātil-gāfilātil-mu`mināti lu'inụ fid-dun-yā wal-ākhirati wa lahum 'ażābun 'aẓīm","at":"Sungguh, orang-orang yang menuduh perempuan-perempuan baik, yang lengah dan beriman (dengan tuduhan berzina), mereka dilaknat di dunia dan di akhirat, dan mereka akan mendapat azab yang besar,"},{"id":"24","ar":"يَّوْمَ تَشْهَدُ عَلَيْهِمْ اَلْسِنَتُهُمْ وَاَيْدِيْهِمْ وَاَرْجُلُهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ","bc":"yauma tasy-hadu 'alaihim alsinatuhum wa aidīhim wa arjuluhum bimā kānụ ya'malụn","at":"pada hari, (ketika) lidah, tangan dan kaki mereka menjadi saksi atas mereka terhadap apa yang dahulu mereka kerjakan."},{"id":"25","ar":"يَوْمَىِٕذٍ يُّوَفِّيْهِمُ اللّٰهُ دِيْنَهُمُ الْحَقَّ وَيَعْلَمُوْنَ اَنَّ اللّٰهَ هُوَ الْحَقُّ الْمُبِيْنُ","bc":"yauma`iżiy yuwaffīhimullāhu dīnahumul-ḥaqqa wa ya'lamụna annallāha huwal-ḥaqqul-mubīn","at":"Pada hari itu Allah menyempurnakan balasan yang sebenarnya bagi mereka, dan mereka tahu bahwa Allah Maha-benar, Maha Menjelaskan."},{"id":"26","ar":"اَلْخَبِيْثٰتُ لِلْخَبِيْثِيْنَ وَالْخَبِيْثُوْنَ لِلْخَبِيْثٰتِۚ وَالطَّيِّبٰتُ لِلطَّيِّبِيْنَ وَالطَّيِّبُوْنَ لِلطَّيِّبٰتِۚ اُولٰۤىِٕكَ مُبَرَّءُوْنَ مِمَّا يَقُوْلُوْنَۗ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ","bc":"al-khabīṡātu lil-khabīṡīna wal-khabīṡụna lil-khabīṡāt, waṭ-ṭayyibātu liṭ-ṭayyibīna waṭ-ṭayyibụna liṭ-ṭayyibāt, ulā`ika mubarra`ụna mimmā yaqụlụn, lahum magfiratuw wa rizqung karīm","at":"Perempuan-perempuan yang keji untuk laki-laki yang keji, dan laki-laki yang keji untuk perempuan-perempuan yang keji (pula), sedangkan perempuan-perempuan yang baik untuk laki-laki yang baik dan laki-laki yang baik untuk perempuan-perempuan yang baik (pula). Mereka itu bersih dari apa yang dituduhkan orang. Mereka memperoleh ampunan dan rezeki yang mulia (surga)."},{"id":"27","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَدْخُلُوْا بُيُوْتًا غَيْرَ بُيُوْتِكُمْ حَتّٰى تَسْتَأْنِسُوْا وَتُسَلِّمُوْا عَلٰٓى اَهْلِهَاۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ لَعَلَّكُمْ تَذَكَّرُوْنَ ","bc":"yā ayyuhallażīna āmanụ lā tadkhulụ buyụtan gaira buyụtikum ḥattā tasta`nisụ wa tusallimụ 'alā ahlihā, żālikum khairul lakum la'allakum tażakkarụn","at":"Wahai orang-orang yang beriman! Janganlah kamu memasuki rumah yang bukan rumahmu sebelum meminta izin dan memberi salam kepada penghuninya. Yang demikian itu lebih baik bagimu, agar kamu (selalu) ingat."},{"id":"28","ar":"فَاِنْ لَّمْ تَجِدُوْا فِيْهَآ اَحَدًا فَلَا تَدْخُلُوْهَا حَتّٰى يُؤْذَنَ لَكُمْ وَاِنْ قِيْلَ لَكُمُ ارْجِعُوْا فَارْجِعُوْا هُوَ اَزْكٰى لَكُمْ ۗوَاللّٰهُ بِمَا تَعْمَلُوْنَ عَلِيْمٌ ","bc":"fa il lam tajidụ fīhā aḥadan fa lā tadkhulụhā ḥattā yu`żana lakum wa ing qīla lakumurji'ụ farji'ụ huwa azkā lakum, wallāhu bimā ta'malụna 'alīm","at":"Dan jika kamu tidak menemui seorang pun di dalamnya, maka janganlah kamu masuk sebelum kamu mendapat izin. Dan jika dikatakan kepadamu, “Kembalilah!” Maka (hendaklah) kamu kembali. Itu lebih suci bagimu, dan Allah Maha Mengetahui apa yang kamu kerjakan."},{"id":"29","ar":"لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَدْخُلُوْا بُيُوْتًا غَيْرَ مَسْكُوْنَةٍ فِيْهَا مَتَاعٌ لَّكُمْۗ وَاللّٰهُ يَعْلَمُ مَا تُبْدُوْنَ وَمَا تَكْتُمُوْنَ","bc":"laisa 'alaikum junāḥun an tadkhulụ buyụtan gaira maskụnatin fīhā matā'ul lakum, wallāhu ya'lamu mā tubdụna wa mā taktumụn","at":"Tidak ada dosa atasmu memasuki rumah yang tidak dihuni, yang di dalamnya ada kepentingan kamu; Allah mengetahui apa yang kamu nyatakan dan apa yang kamu sembunyikan."},{"id":"30","ar":"قُلْ لِّلْمُؤْمِنِيْنَ يَغُضُّوْا مِنْ اَبْصَارِهِمْ وَيَحْفَظُوْا فُرُوْجَهُمْۗ ذٰلِكَ اَزْكٰى لَهُمْۗ اِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا يَصْنَعُوْنَ","bc":"qul lil-mu`minīna yaguḍḍụ min abṣārihim wa yaḥfaẓụ furụjahum, żālika azkā lahum, innallāha khabīrum bimā yaṣna'ụn","at":"Katakanlah kepada laki-laki yang beriman, agar mereka menjaga pandangannya, dan memelihara kemaluannya; yang demikian itu, lebih suci bagi mereka. Sungguh, Allah Maha Mengetahui apa yang mereka perbuat."},{"id":"31","ar":"وَقُلْ لِّلْمُؤْمِنٰتِ يَغْضُضْنَ مِنْ اَبْصَارِهِنَّ وَيَحْفَظْنَ فُرُوْجَهُنَّ وَلَا يُبْدِيْنَ زِيْنَتَهُنَّ اِلَّا مَا ظَهَرَ مِنْهَا وَلْيَضْرِبْنَ بِخُمُرِهِنَّ عَلٰى جُيُوْبِهِنَّۖ وَلَا يُبْدِيْنَ زِيْنَتَهُنَّ اِلَّا لِبُعُوْلَتِهِنَّ اَوْ اٰبَاۤىِٕهِنَّ اَوْ اٰبَاۤءِ بُعُوْلَتِهِنَّ اَوْ اَبْنَاۤىِٕهِنَّ اَوْ اَبْنَاۤءِ بُعُوْلَتِهِنَّ اَوْ اِخْوَانِهِنَّ اَوْ بَنِيْٓ اِخْوَانِهِنَّ اَوْ بَنِيْٓ اَخَوٰتِهِنَّ اَوْ نِسَاۤىِٕهِنَّ اَوْ مَا مَلَكَتْ اَيْمَانُهُنَّ اَوِ التَّابِعِيْنَ غَيْرِ اُولِى الْاِرْبَةِ مِنَ الرِّجَالِ اَوِ الطِّفْلِ الَّذِيْنَ لَمْ يَظْهَرُوْا عَلٰى عَوْرٰتِ النِّسَاۤءِ ۖوَلَا يَضْرِبْنَ بِاَرْجُلِهِنَّ لِيُعْلَمَ مَا يُخْفِيْنَ مِنْ زِيْنَتِهِنَّۗ وَتُوْبُوْٓا اِلَى اللّٰهِ جَمِيْعًا اَيُّهَ الْمُؤْمِنُوْنَ لَعَلَّكُمْ تُفْلِحُوْنَ","bc":"wa qul lil-mu`mināti yagḍuḍna min abṣārihinna wa yaḥfaẓna furụjahunna wa lā yubdīna zīnatahunna illā mā ẓahara min-hā walyaḍribna bikhumurihinna 'alā juyụbihinna wa lā yubdīna zīnatahunna illā libu'ụlatihinna au ābā`ihinna au ābā`i bu'ụlatihinna au abnā`ihinna au abnā`i bu'ụlatihinna au ikhwānihinna au banī ikhwānihinna au banī akhawātihinna au nisā`ihinna au mā malakat aimānuhunna awittābi'īna gairi ulil-irbati minar-rijāli awiṭ-ṭiflillażīna lam yaẓ-harụ 'alā 'aurātin-nisā`i wa lā yaḍribna bi`arjulihinna liyu'lama mā yukhfīna min zīnatihinn, wa tụbū ilallāhi jamī'an ayyuhal-mu`minụna la'allakum tufliḥụn","at":"Dan katakanlah kepada para perempuan yang beriman, agar mereka menjaga pandangannya, dan memelihara kemaluannya, dan janganlah menampakkan perhiasannya (auratnya), kecuali yang (biasa) terlihat. Dan hendaklah mereka menutupkan kain kerudung ke dadanya, dan janganlah menampakkan perhiasannya (auratnya), kecuali kepada suami mereka, atau ayah mereka, atau ayah suami mereka, atau putra-putra mereka, atau putra-putra suami mereka, atau saudara-saudara laki-laki mereka, atau putra-putra saudara laki-laki mereka, atau putra-putra saudara perempuan mereka, atau para perempuan (sesama Islam) mereka, atau hamba sahaya yang mereka miliki, atau para pelayan laki-laki (tua) yang tidak mempunyai keinginan (terhadap perempuan) atau anak-anak yang belum mengerti tentang aurat perempuan. Dan janganlah mereka menghentakkan kakinya agar diketahui perhiasan yang mereka sembunyikan. Dan bertobatlah kamu semua kepada Allah, wahai orang-orang yang beriman, agar kamu beruntung."},{"id":"32","ar":"وَاَنْكِحُوا الْاَيَامٰى مِنْكُمْ وَالصّٰلِحِيْنَ مِنْ عِبَادِكُمْ وَاِمَاۤىِٕكُمْۗ اِنْ يَّكُوْنُوْا فُقَرَاۤءَ يُغْنِهِمُ اللّٰهُ مِنْ فَضْلِهٖۗ وَاللّٰهُ وَاسِعٌ عَلِيْمٌ ","bc":"wa angkiḥul-ayāmā mingkum waṣ-ṣāliḥīna min 'ibādikum wa imā`ikum, iy yakụnụ fuqarā`a yugnihimullāhu min faḍlih, wallāhu wāsi'un 'alīm","at":"Dan nikahkanlah orang-orang yang masih membujang di antara kamu, dan juga orang-orang yang layak (menikah) dari hamba-hamba sahayamu yang laki-laki dan perempuan. Jika mereka miskin, Allah akan memberi kemampuan kepada mereka dengan karunia-Nya. Dan Allah Mahaluas (pemberian-Nya), Maha Mengetahui."},{"id":"33","ar":"وَلْيَسْتَعْفِفِ الَّذِيْنَ لَا يَجِدُوْنَ نِكَاحًا حَتّٰى يُغْنِيَهُمُ اللّٰهُ مِنْ فَضْلِهٖ ۗوَالَّذِيْنَ يَبْتَغُوْنَ الْكِتٰبَ مِمَّا مَلَكَتْ اَيْمَانُكُمْ فَكَاتِبُوْهُمْ اِنْ عَلِمْتُمْ فِيْهِمْ خَيْرًا وَّاٰتُوْهُمْ مِّنْ مَّالِ اللّٰهِ الَّذِيْٓ اٰتٰىكُمْ ۗوَلَا تُكْرِهُوْا فَتَيٰتِكُمْ عَلَى الْبِغَاۤءِ اِنْ اَرَدْنَ تَحَصُّنًا لِّتَبْتَغُوْا عَرَضَ الْحَيٰوةِ الدُّنْيَا ۗوَمَنْ يُّكْرِهْهُّنَّ فَاِنَّ اللّٰهَ مِنْۢ بَعْدِ اِكْرَاهِهِنَّ غَفُوْرٌ رَّحِيْمٌ","bc":"walyasta'fifillażīna lā yajidụna nikāḥan ḥattā yugniyahumullāhu min faḍlih, wallażīna yabtagụnal-kitāba mimmā malakat aimānukum fa kātibụhum in 'alimtum fīhim khairaw wa ātụhum mim mālillāhillażī ātākum, wa lā tukrihụ fatayātikum 'alal-bigā`i in aradna taḥaṣṣunal litabtagụ 'araḍal ḥayātid-dun-yā, wa may yukrihhunna fa innallāha mim ba'di ikrāhihinna gafụrur raḥīm","at":"Dan orang-orang yang tidak mampu menikah hendaklah menjaga kesucian (diri)nya, sampai Allah memberi kemampuan kepada mereka dengan karunia-Nya. Dan jika hamba sahaya yang kamu miliki menginginkan perjanjian (kebebasan), hendaklah kamu buat perjanjian kepada mereka, jika kamu mengetahui ada kebaikan pada mereka, dan berikanlah kepada mereka sebagian dari harta Allah yang dikaruniakan-Nya kepadamu. Dan janganlah kamu paksa hamba sahaya perempuanmu untuk melakukan pelacuran, sedang mereka sendiri menginginkan kesucian, karena kamu hendak mencari keuntungan kehidupan duniawi. Barangsiapa memaksa mereka, maka sungguh, Allah Maha Pengampun, Maha Penyayang (kepada mereka) setelah mereka dipaksa."},{"id":"34","ar":"وَلَقَدْ اَنْزَلْنَآ اِلَيْكُمْ اٰيٰتٍ مُّبَيِّنٰتٍ وَّمَثَلًا مِّنَ الَّذِيْنَ خَلَوْا مِنْ قَبْلِكُمْ وَمَوْعِظَةً لِّلْمُتَّقِيْنَ","bc":"wa laqad anzalnā ilaikum āyātim mubayyinātiw wa maṡalam minallażīna khalau ming qablikum wa mau'iẓatal lil-muttaqīn","at":"Dan sungguh, Kami telah menurunkan kepada kamu ayat-ayat yang memberi penjelasan, dan contoh-contoh dari orang-orang yang terdahulu sebelum kamu dan sebagai pelajaran bagi orang-orang yang bertakwa."},{"id":"35","ar":"۞ اَللّٰهُ نُوْرُ السَّمٰوٰتِ وَالْاَرْضِۗ مَثَلُ نُوْرِهٖ كَمِشْكٰوةٍ فِيْهَا مِصْبَاحٌۗ اَلْمِصْبَاحُ فِيْ زُجَاجَةٍۗ اَلزُّجَاجَةُ كَاَنَّهَا كَوْكَبٌ دُرِّيٌّ يُّوْقَدُ مِنْ شَجَرَةٍ مُّبٰرَكَةٍ زَيْتُوْنَةٍ لَّا شَرْقِيَّةٍ وَّلَا غَرْبِيَّةٍۙ يَّكَادُ زَيْتُهَا يُضِيْۤءُ وَلَوْ لَمْ تَمْسَسْهُ نَارٌۗ نُوْرٌ عَلٰى نُوْرٍۗ يَهْدِى اللّٰهُ لِنُوْرِهٖ مَنْ يَّشَاۤءُۗ وَيَضْرِبُ اللّٰهُ الْاَمْثَالَ لِلنَّاسِۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ ۙ ","bc":"allāhu nụrus-samāwāti wal-arḍ, maṡalu nụrihī kamisykātin fīhā miṣbāḥ, al-miṣbāḥu fī zujājah, az-zujājatu ka`annahā kaukabun durriyyuy yụqadu min syajaratim mubārakatin zaitụnatil lā syarqiyyatiw wa lā garbiyyatiy yakādu zaituhā yuḍī`u walau lam tamsas-hu nār, nụrun 'alā nụr, yahdillāhu linụrihī may yasyā`, wa yaḍribullāhul-amṡāla lin-nās, wallāhu bikulli syai`in 'alīm","at":"Allah (pemberi) cahaya (kepada) langit dan bumi. Perumpamaan cahaya-Nya, seperti sebuah lubang yang tidak tembus, yang di dalamnya ada pelita besar. Pelita itu di dalam tabung kaca (dan) tabung kaca itu bagaikan bintang yang berkilauan, yang dinyalakan dengan minyak dari pohon yang diberkahi, (yaitu) pohon zaitun yang tumbuh tidak di timur dan tidak pula di barat, yang minyaknya (saja) hampir-hampir menerangi, walaupun tidak disentuh api. Cahaya di atas cahaya (berlapis-lapis), Allah memberi petunjuk kepada cahaya-Nya bagi orang yang Dia kehendaki, dan Allah membuat perumpamaan-perumpamaan bagi manusia. Dan Allah Maha Mengetahui segala sesuatu."},{"id":"36","ar":"فِيْ بُيُوْتٍ اَذِنَ اللّٰهُ اَنْ تُرْفَعَ وَيُذْكَرَ فِيْهَا اسْمُهٗۙ يُسَبِّحُ لَهٗ فِيْهَا بِالْغُدُوِّ وَالْاٰصَالِ ۙ","bc":"fī buyụtin ażinallāhu an turfa'a wa yużkara fīhasmuhụ yusabbiḥu lahụ fīhā bil-guduwwi wal-āṣāl","at":"(Cahaya itu) di rumah-rumah yang di sana telah diperintahkan Allah untuk memuliakan dan menyebut nama-Nya, di sana bertasbih (menyucikan) nama-Nya pada waktu pagi dan petang,"},{"id":"37","ar":"رِجَالٌ لَّا تُلْهِيْهِمْ تِجَارَةٌ وَّلَا بَيْعٌ عَنْ ذِكْرِ اللّٰهِ وَاِقَامِ الصَّلٰوةِ وَاِيْتَاۤءِ الزَّكٰوةِ ۙيَخَافُوْنَ يَوْمًا تَتَقَلَّبُ فِيْهِ الْقُلُوْبُ وَالْاَبْصَارُ ۙ","bc":"rijālul lā tul-hīhim tijāratuw wa lā bai'un 'an żikrillāhi wa iqāmiṣ-ṣalāti wa ītā`iz-zakāti yakhāfụna yauman tataqallabu fīhil qulụbu wal-abṣār","at":"orang yang tidak dilalaikan oleh perdagangan dan jual beli dari mengingat Allah, melaksanakan salat, dan menunaikan zakat. Mereka takut kepada hari ketika hati dan penglihatan menjadi guncang (hari Kiamat),"},{"id":"38","ar":"لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا عَمِلُوْا وَيَزِيْدَهُمْ مِّنْ فَضْلِهٖۗ وَاللّٰهُ يَرْزُقُ مَنْ يَّشَاۤءُ بِغَيْرِ حِسَابٍ","bc":"liyajziyahumullāhu aḥsana mā 'amilụ wa yazīdahum min faḍlih, wallāhu yarzuqu may yasyā`u bigairi ḥisāb","at":"(mereka melakukan itu) agar Allah memberi balasan kepada mereka dengan yang lebih baik daripada apa yang telah mereka kerjakan, dan agar Dia menambah karunia-Nya kepada mereka. Dan Allah memberi rezeki kepada siapa saja yang Dia kehendaki tanpa batas."},{"id":"39","ar":"وَالَّذِيْنَ كَفَرُوْٓا اَعْمَالُهُمْ كَسَرَابٍۢ بِقِيْعَةٍ يَّحْسَبُهُ الظَّمْاٰنُ مَاۤءًۗ حَتّٰٓى اِذَا جَاۤءَهٗ لَمْ يَجِدْهُ شَيْـًٔا وَّوَجَدَ اللّٰهَ عِنْدَهٗ فَوَفّٰىهُ حِسَابَهٗ ۗ وَاللّٰهُ سَرِيْعُ الْحِسَابِ ۙ","bc":"wallażīna kafarū a'māluhum kasarābim biqī'atiy yaḥsabuhuẓ-ẓam`ānu mā`ā, ḥattā iżā jā`ahụ lam yajid-hu syai`aw wa wajadallāha 'indahụ fa waffāhu ḥisābah, wallāhu sarī'ul ḥisāb","at":"Dan orang-orang yang kafir, amal perbuatan mereka seperti fatamorgana di tanah yang datar, yang disangka air oleh orang-orang yang dahaga, tetapi apabila (air) itu didatangi tidak ada apa pun. Dan didapatinya (ketetapan) Allah baginya. Lalu Allah memberikan kepadanya perhitungan (amal-amal) dengan sempurna dan Allah sangat cepat perhitungan-Nya."},{"id":"40","ar":"اَوْ كَظُلُمٰتٍ فِيْ بَحْرٍ لُّجِّيٍّ يَّغْشٰىهُ مَوْجٌ مِّنْ فَوْقِهٖ مَوْجٌ مِّنْ فَوْقِهٖ سَحَابٌۗ ظُلُمٰتٌۢ بَعْضُهَا فَوْقَ بَعْضٍۗ اِذَآ اَخْرَجَ يَدَهٗ لَمْ يَكَدْ يَرٰىهَاۗ وَمَنْ لَّمْ يَجْعَلِ اللّٰهُ لَهٗ نُوْرًا فَمَا لَهٗ مِنْ نُّوْرٍ","bc":"au kaẓulumātin fī baḥril lujjiyyiy yagsyāhu maujum min fauqihī maujum min fauqihī saḥāb, ẓulumātum ba'ḍuhā fauqa ba'ḍ, iżā akhraja yadahụ lam yakad yarāhā, wa mal lam yaj'alillāhu lahụ nụran fa mā lahụ min nụr","at":"Atau (keadaan orang-orang kafir) seperti gelap gulita di lautan yang dalam, yang diliputi oleh gelombang demi gelombang, di atasnya ada (lagi) awan gelap. Itulah gelap gulita yang berlapis-lapis. Apabila dia mengeluarkan tangannya hampir tidak dapat melihatnya. Barangsiapa tidak diberi cahaya (petunjuk) oleh Allah, maka dia tidak mempunyai cahaya sedikit pun. "},{"id":"41","ar":"اَلَمْ تَرَ اَنَّ اللّٰهَ يُسَبِّحُ لَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ وَالطَّيْرُ صٰۤفّٰتٍۗ كُلٌّ قَدْ عَلِمَ صَلَاتَهٗ وَتَسْبِيْحَهٗۗ وَاللّٰهُ عَلِيْمٌۢ بِمَا يَفْعَلُوْنَ ","bc":"a lam tara annallāha yusabbiḥu lahụ man fis-samāwāti wal-arḍi waṭ-ṭairu ṣāffāt, kullung qad 'alima ṣalātahụ wa tasbīḥah, wallāhu 'alīmum bimā yaf'alụn","at":"Tidakkah engkau (Muhammad) tahu bahwa kepada Allah-lah bertasbih apa yang di langit dan di bumi, dan juga burung yang mengembangkan sayapnya. Masing-masing sungguh, telah mengetahui (cara) berdoa dan bertasbih. Allah Maha Mengetahui apa yang mereka kerjakan."},{"id":"42","ar":"وَلِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۚ وَاِلَى اللّٰهِ الْمَصِيْرُ","bc":"wa lillāhi mulkus-samāwāti wal-arḍ, wa ilallāhil-maṣīr","at":"Dan milik Allah-lah kerajaan langit dan bumi dan hanya kepada Allah-lah kembali (seluruh makhluk)."},{"id":"43","ar":"اَلَمْ تَرَ اَنَّ اللّٰهَ يُزْجِيْ سَحَابًا ثُمَّ يُؤَلِّفُ بَيْنَهٗ ثُمَّ يَجْعَلُهٗ رُكَامًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلٰلِهٖۚ وَيُنَزِّلُ مِنَ السَّمَاۤءِ مِنْ جِبَالٍ فِيْهَا مِنْۢ بَرَدٍ فَيُصِيْبُ بِهٖ مَنْ يَّشَاۤءُ وَيَصْرِفُهٗ عَنْ مَّنْ يَّشَاۤءُۗ يَكَادُ سَنَا بَرْقِهٖ يَذْهَبُ بِالْاَبْصَارِ ۗ","bc":"a lam tara annallāha yuzjī saḥāban ṡumma yu`allifu bainahụ ṡumma yaj'aluhụ rukāman fa taral-wadqa yakhruju min khilālih, wa yunazzilu minas-samā`i min jibālin fīhā mim baradin fa yuṣību bihī may yasyā`u wa yaṣrifuhụ 'am may yasyā`, yakādu sanā barqihī yaż-habu bil-abṣār","at":"Tidakkah engkau melihat bahwa Allah menjadikan awan bergerak perlahan, kemudian mengumpulkannya, lalu Dia menjadikannya bertumpuk-tumpuk, lalu engkau lihat hujan keluar dari celah-celahnya dan Dia (juga) menurunkan (butiran-butiran) es dari langit, (yaitu) dari (gumpalan-gumpalan awan seperti) gunung-gunung, maka ditimpakan-Nya (butiran-butiran es) itu kepada siapa yang Dia kehendaki dan dihindarkan-Nya dari siapa yang Dia kehendaki. Kilauan kilatnya hampir-hampir menghilangkan penglihatan."},{"id":"44","ar":"يُقَلِّبُ اللّٰهُ الَّيْلَ وَالنَّهَارَۗ اِنَّ فِيْ ذٰلِكَ لَعِبْرَةً لِّاُولِى الْاَبْصَارِ ","bc":"yuqallibullāhul-laila wan-nahār, inna fī żālika la'ibratal li`ulil-abṣār","at":"Allah mempergantikan malam dan siang. Sesungguhnya pada yang demikian itu, pasti terdapat pelajaran bagi orang-orang yang mempunyai penglihatan (yang tajam)."},{"id":"45","ar":"وَاللّٰهُ خَلَقَ كُلَّ دَاۤبَّةٍ مِّنْ مَّاۤءٍۚ فَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰى بَطْنِهٖۚ وَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰى رِجْلَيْنِۚ وَمِنْهُمْ مَّنْ يَّمْشِيْ عَلٰٓى اَرْبَعٍۗ يَخْلُقُ اللّٰهُ مَا يَشَاۤءُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ","bc":"wallāhu khalaqa kulla dābbatim mim mā`, fa min-hum may yamsyī 'alā baṭnih, wa min-hum may yamsyī 'alā rijlaīn, wa min-hum may yamsyī 'alā arba', yakhluqullāhu mā yasyā`, innallāha 'alā kulli syai`ing qadīr","at":"Dan Allah menciptakan semua jenis hewan dari air, maka sebagian ada yang berjalan di atas perutnya dan sebagian berjalan dengan dua kaki, sedang sebagian (yang lain) berjalan dengan empat kaki. Allah menciptakan apa yang Dia kehendaki. Sungguh, Allah Mahakuasa atas segala sesuatu."},{"id":"46","ar":"لَقَدْ اَنْزَلْنَآ اٰيٰتٍ مُّبَيِّنٰتٍۗ وَاللّٰهُ يَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"laqad anzalnā āyātim mubayyināt, wallāhu yahdī may yasyā`u ilā ṣirāṭim mustaqīm","at":"Sungguh, Kami telah menurunkan ayat-ayat yang memberi penjelasan. Dan Allah memberi petunjuk siapa yang Dia kehendaki ke jalan yang lurus."},{"id":"47","ar":"وَيَقُوْلُوْنَ اٰمَنَّا بِاللّٰهِ وَبِالرَّسُوْلِ وَاَطَعْنَا ثُمَّ يَتَوَلّٰى فَرِيْقٌ مِّنْهُمْ مِّنْۢ بَعْدِ ذٰلِكَۗ وَمَآ اُولٰۤىِٕكَ بِالْمُؤْمِنِيْنَ","bc":"wa yaqụlụna āmannā billāhi wa bir-rasụli wa aṭa'nā ṡumma yatawallā farīqum min-hum mim ba'di żālik, wa mā ulā`ika bil-mu`minīn","at":"Dan mereka (orang-orang munafik) berkata, “Kami telah beriman kepada Allah dan Rasul (Muhammad), dan kami menaati (keduanya).” Kemudian sebagian dari mereka berpaling setelah itu. Mereka itu bukanlah orang-orang beriman."},{"id":"48","ar":"وَاِذَا دُعُوْٓا اِلَى اللّٰهِ وَرَسُوْلِهٖ لِيَحْكُمَ بَيْنَهُمْ اِذَا فَرِيْقٌ مِّنْهُمْ مُّعْرِضُوْنَ ","bc":"wa iżā du'ū ilallāhi wa rasụlihī liyaḥkuma bainahum iżā farīqum min-hum mu'riḍụn","at":"Dan apabila mereka diajak kepada Allah dan Rasul-Nya, agar (Rasul) memutuskan perkara di antara mereka, tiba-tiba sebagian dari mereka menolak (untuk datang)."},{"id":"49","ar":"وَاِنْ يَّكُنْ لَّهُمُ الْحَقُّ يَأْتُوْٓا اِلَيْهِ مُذْعِنِيْنَ","bc":"wa iy yakul lahumul-ḥaqqu ya`tū ilaihi muż'inīn","at":"Tetapi, jika kebenaran di pihak mereka, mereka datang kepadanya (Rasul) dengan patuh."},{"id":"50","ar":"اَفِيْ قُلُوْبِهِمْ مَّرَضٌ اَمِ ارْتَابُوْٓا اَمْ يَخَافُوْنَ اَنْ يَّحِيْفَ اللّٰهُ عَلَيْهِمْ وَرَسُوْلُهٗ ۗبَلْ اُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ","bc":"a fī qulụbihim maraḍun amirtābū am yakhāfụna ay yaḥīfallāhu 'alaihim wa rasụluh, bal ulā`ika humuẓ-ẓālimụn","at":"Apakah (ketidakhadiran mereka karena) dalam hati mereka ada penyakit, atau (karena) mereka ragu-ragu ataukah (karena) takut kalau-kalau Allah dan Rasul-Nya berlaku zalim kepada mereka? Sebenarnya, mereka itulah orang-orang yang zalim."},{"id":"51","ar":"اِنَّمَا كَانَ قَوْلَ الْمُؤْمِنِيْنَ اِذَا دُعُوْٓا اِلَى اللّٰهِ وَرَسُوْلِهٖ لِيَحْكُمَ بَيْنَهُمْ اَنْ يَّقُوْلُوْا سَمِعْنَا وَاَطَعْنَاۗ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ","bc":"innamā kāna qaulal-mu`minīna iżā du'ū ilallāhi wa rasụlihī liyaḥkuma bainahum ay yaqụlụ sami'nā wa aṭa'nā, wa ulā`ika humul-mufliḥụn","at":"Hanya ucapan orang-orang mukmin, yang apabila mereka diajak kepada Allah dan Rasul-Nya agar Rasul memutuskan (perkara) di antara mereka, mereka berkata, “Kami mendengar, dan kami taat.” Dan mereka itulah orang-orang yang beruntung."},{"id":"52","ar":"وَمَنْ يُّطِعِ اللّٰهَ وَرَسُوْلَهٗ وَيَخْشَ اللّٰهَ وَيَتَّقْهِ فَاُولٰۤىِٕكَ هُمُ الْفَاۤىِٕزُوْنَ","bc":"wa may yuṭi'illāha wa rasụlahụ wa yakhsyallāha wa yattaq-hi fa ulā`ika humul-fā`izụn","at":"Dan barangsiapa taat kepada Allah dan Rasul-Nya serta takut kepada Allah dan bertakwa kepada-Nya, mereka itulah orang-orang yang mendapat kemenangan."},{"id":"53","ar":"۞ وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ اَمَرْتَهُمْ لَيَخْرُجُنَّۗ قُلْ لَّا تُقْسِمُوْاۚ طَاعَةٌ مَّعْرُوْفَةٌ ۗاِنَّ اللّٰهَ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ","bc":"wa aqsamụ billāhi jahda aimānihim la`in amartahum layakhrujunn, qul lā tuqsimụ, ṭā'atum ma'rụfah, innallāha khabīrum bimā ta'malụn","at":"Dan mereka bersumpah dengan (nama) Allah dengan sumpah sungguh-sungguh, bahwa jika engkau suruh mereka berperang, pastilah mereka akan pergi. Katakanlah (Muhammad), “Janganlah kamu bersumpah, (karena yang diminta) adalah ketaatan yang baik. Sungguh, Allah Mahateliti terhadap apa yang kamu kerjakan.”"},{"id":"54","ar":"قُلْ اَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَۚ فَاِنْ تَوَلَّوْا فَاِنَّمَا عَلَيْهِ مَا حُمِّلَ وَعَلَيْكُمْ مَّا حُمِّلْتُمْۗ وَاِنْ تُطِيْعُوْهُ تَهْتَدُوْاۗ وَمَا عَلَى الرَّسُوْلِ اِلَّا الْبَلٰغُ الْمُبِيْنُ ","bc":"qul aṭī'ullāha wa aṭī'ur-rasụl, fa in tawallau fa innamā 'alaihi mā ḥummila wa 'alaikum mā ḥummiltum, wa in tuṭī'ụhu tahtadụ, wa mā 'alar-rasụli illal-balāgul-mubīn","at":"Katakanlah, “Taatlah kepada Allah dan taatlah kepada Rasul; jika kamu berpaling, maka sesungguhnya kewajiban Rasul (Muhammad) itu hanyalah apa yang dibebankan kepadanya, dan kewajiban kamu hanyalah apa yang dibebankan kepadamu. Jika kamu taat kepadanya, niscaya kamu mendapat petunjuk. Kewajiban Rasul hanyalah menyampaikan (amanat Allah) dengan jelas.”"},{"id":"55","ar":"وَعَدَ اللّٰهُ الَّذِيْنَ اٰمَنُوْا مِنْكُمْ وَعَمِلُوا الصّٰلِحٰتِ لَيَسْتَخْلِفَنَّهُمْ فِى الْاَرْضِ كَمَا اسْتَخْلَفَ الَّذِيْنَ مِنْ قَبْلِهِمْۖ وَلَيُمَكِّنَنَّ لَهُمْ دِيْنَهُمُ الَّذِى ارْتَضٰى لَهُمْ وَلَيُبَدِّلَنَّهُمْ مِّنْۢ بَعْدِ خَوْفِهِمْ اَمْنًاۗ يَعْبُدُوْنَنِيْ لَا يُشْرِكُوْنَ بِيْ شَيْـًٔاۗ وَمَنْ كَفَرَ بَعْدَ ذٰلِكَ فَاُولٰۤىِٕكَ هُمُ الْفٰسِقُوْنَ","bc":"wa'adallāhullażīna āmanụ mingkum wa 'amiluṣ-ṣāliḥāti layastakhlifannahum fil-arḍi kamastakhlafallażīna ming qablihim wa layumakkinanna lahum dīnahumullażirtaḍā lahum wa layubaddilannahum mim ba'di khaufihim amnā, ya'budụnanī lā yusyrikụna bī syai`ā, wa mang kafara ba'da żālika fa ulā`ika humul-fāsiqụn","at":"Allah telah menjanjikan kepada orang-orang di antara kamu yang beriman dan yang mengerjakan kebajikan, bahwa Dia sungguh, akan menjadikan mereka berkuasa di bumi, sebagaimana Dia telah menjadikan orang-orang sebelum mereka berkuasa, dan sungguh, Dia akan meneguhkan bagi mereka dengan agama yang telah Dia ridai. Dan Dia benar-benar mengubah (keadaan) mereka, setelah berada dalam ketakutan menjadi aman sentosa. Mereka (tetap) menyembah-Ku dengan tidak mempersekutukan-Ku dengan sesuatu apa pun. Tetapi barangsiapa (tetap) kafir setelah (janji) itu, maka mereka itulah orang-orang yang fasik."},{"id":"56","ar":"وَاَقِيْمُوا الصَّلٰوةَ وَاٰتُوا الزَّكٰوةَ وَاَطِيْعُوا الرَّسُوْلَ لَعَلَّكُمْ تُرْحَمُوْنَ","bc":"wa aqīmuṣ-ṣalāta wa ātuz-zakāta wa aṭī'ur-rasụla la'allakum tur-ḥamụn","at":"Dan laksanakanlah salat, tunaikanlah zakat, dan taatlah kepada Rasul (Muhammad), agar kamu diberi rahmat."},{"id":"57","ar":"لَا تَحْسَبَنَّ الَّذِيْنَ كَفَرُوْا مُعْجِزِيْنَ فِى الْاَرْضِۚ وَمَأْوٰىهُمُ النَّارُۗ وَلَبِئْسَ الْمَصِيْرُ","bc":"lā taḥsabannallażīna kafarụ mu'jizīna fil-arḍ, wa ma`wāhumun-nār, wa labi`sal-maṣīr","at":"Janganlah engkau mengira bahwa orang-orang yang kafir itu dapat luput dari siksaan Allah di bumi; sedang tempat kembali mereka (di akhirat) adalah neraka. Dan itulah seburuk-buruk tempat kembali."},{"id":"58","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لِيَسْتَأْذِنْكُمُ الَّذِيْنَ مَلَكَتْ اَيْمَانُكُمْ وَالَّذِيْنَ لَمْ يَبْلُغُوا الْحُلُمَ مِنْكُمْ ثَلٰثَ مَرّٰتٍۗ مِنْ قَبْلِ صَلٰوةِ الْفَجْرِ وَحِيْنَ تَضَعُوْنَ ثِيَابَكُمْ مِّنَ الظَّهِيْرَةِ وَمِنْۢ بَعْدِ صَلٰوةِ الْعِشَاۤءِۗ ثَلٰثُ عَوْرٰتٍ لَّكُمْۗ لَيْسَ عَلَيْكُمْ وَلَا عَلَيْهِمْ جُنَاحٌۢ بَعْدَهُنَّۗ طَوَّافُوْنَ عَلَيْكُمْ بَعْضُكُمْ عَلٰى بَعْضٍۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ","bc":"yā ayyuhallażīna āmanụ liyasta`żingkumullażīna malakat aimānukum wallażīna lam yablugul-ḥuluma mingkum ṡalāṡa marrāt, ming qabli ṣalātil-fajri wa ḥīna taḍa'ụna ṡiyābakum minaẓ-ẓahīrati wa mim ba'di ṣalātil-'isyā`, ṡalāṡu 'aurātil lakum, laisa 'alaikum wa lā 'alaihim junāḥum ba'dahunn, ṭawwāfụna 'alaikum ba'ḍukum 'alā ba'ḍ, każālika yubayyinullāhu lakumul-āyāt, wallāhu 'alīmun ḥakīm","at":"Wahai orang-orang yang beriman! Hendaklah hamba sahaya (laki-laki dan perempuan) yang kamu miliki, dan orang-orang yang belum balig (dewasa) di antara kamu, meminta izin kepada kamu pada tiga kali (kesempatan) yaitu, sebelum salat Subuh, ketika kamu menanggalkan pakaian (luar)mu di tengah hari, dan setelah salat Isya. (Itulah) tiga aurat (waktu) bagi kamu. Tidak ada dosa bagimu dan tidak (pula) bagi mereka selain dari (tiga waktu) itu; mereka keluar masuk melayani kamu, sebagian kamu atas sebagian yang lain. Demikianlah Allah menjelaskan ayat-ayat itu kepadamu. Dan Allah Maha Mengetahui, Mahabijaksana."},{"id":"59","ar":"وَاِذَا بَلَغَ الْاَطْفَالُ مِنْكُمُ الْحُلُمَ فَلْيَسْتَأْذِنُوْا كَمَا اسْتَأْذَنَ الَّذِيْنَ مِنْ قَبْلِهِمْۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمْ اٰيٰتِهٖۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ","bc":"wa iżā balagal-aṭfālu mingkumul-ḥuluma falyasta`żinụ kamasta`żanallażīna ming qablihim, każālika yubayyinullāhu lakum āyātih, wallāhu 'alīmun ḥakīm","at":"Dan apabila anak-anakmu telah sampai umur dewasa, maka hendaklah mereka (juga) meminta izin, seperti orang-orang yang lebih dewasa meminta izin. Demikianlah Allah menjelaskan ayat-ayat-Nya kepadamu. Allah Maha Mengetahui, Mahabijaksana."},{"id":"60","ar":"وَالْقَوَاعِدُ مِنَ النِّسَاۤءِ الّٰتِيْ لَا يَرْجُوْنَ نِكَاحًا فَلَيْسَ عَلَيْهِنَّ جُنَاحٌ اَنْ يَّضَعْنَ ثِيَابَهُنَّ غَيْرَ مُتَبَرِّجٰتٍۢ بِزِيْنَةٍۗ وَاَنْ يَّسْتَعْفِفْنَ خَيْرٌ لَّهُنَّۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ","bc":"wal-qawā'idu minan-nisā`illātī lā yarjụna nikāḥan fa laisa 'alaihinna junāḥun ay yaḍa'na ṡiyābahunna gaira mutabarrijātim bizīnah, wa ay yasta'fifna khairul lahunn, wallāhu samī'un 'alīm","at":"Dan para perempuan tua yang telah berhenti (dari haid dan mengandung) yang tidak ingin menikah (lagi), maka tidak ada dosa menanggalkan pakaian (luar) mereka dengan tidak (bermaksud) menampakkan perhiasan; tetapi memelihara kehormatan adalah lebih baik bagi mereka. Allah Maha Mendengar, Maha Mengetahui."},{"id":"61","ar":"لَيْسَ عَلَى الْاَعْمٰى حَرَجٌ وَّلَا عَلَى الْاَعْرَجِ حَرَجٌ وَّلَا عَلَى الْمَرِيْضِ حَرَجٌ وَّلَا عَلٰٓى اَنْفُسِكُمْ اَنْ تَأْكُلُوْا مِنْۢ بُيُوْتِكُمْ اَوْ بُيُوْتِ اٰبَاۤىِٕكُمْ اَوْ بُيُوْتِ اُمَّهٰتِكُمْ اَوْ بُيُوْتِ اِخْوَانِكُمْ اَوْ بُيُوْتِ اَخَوٰتِكُمْ اَوْ بُيُوْتِ اَعْمَامِكُمْ اَوْ بُيُوْتِ عَمّٰتِكُمْ اَوْ بُيُوْتِ اَخْوَالِكُمْ اَوْ بُيُوْتِ خٰلٰتِكُمْ اَوْ مَا مَلَكْتُمْ مَّفَاتِحَهٗٓ اَوْ صَدِيْقِكُمْۗ لَيْسَ عَلَيْكُمْ جُنَاحٌ اَنْ تَأْكُلُوْا جَمِيْعًا اَوْ اَشْتَاتًاۗ فَاِذَا دَخَلْتُمْ بُيُوْتًا فَسَلِّمُوْا عَلٰٓى اَنْفُسِكُمْ تَحِيَّةً مِّنْ عِنْدِ اللّٰهِ مُبٰرَكَةً طَيِّبَةً ۗ كَذٰلِكَ يُبَيِّنُ اللّٰهُ لَكُمُ الْاٰيٰتِ لَعَلَّكُمْ تَعْقِلُوْنَ","bc":"laisa 'alal-a'mā ḥarajuw wa lā 'alal-a'raji ḥarajuw wa lā 'alal-marīḍi ḥarajuw wa lā 'alā anfusikum an ta`kulụ mim buyụtikum au buyụti ābā`ikum au buyụti ummahātikum au buyụti ikhwānikum au buyụti akhawātikum au buyụti a'māmikum au buyụti 'ammātikum au buyụti akhwālikum au buyụti khālātikum au mā malaktum mafātiḥahū au ṣadīqikum, laisa 'alaikum junāḥun an ta`kulụ jamī'an au asytātā, fa iżā dakhaltum buyụtan fa sallimụ 'alā anfusikum taḥiyyatam min 'indillāhi mubārakatan ṭayyibah, każālika yubayyinullāhu lakumul-āyāti la'allakum ta'qilụn","at":"Tidak ada halangan bagi orang buta, tidak (pula) bagi orang pincang, tidak (pula) bagi orang sakit, dan tidak (pula) bagi dirimu, makan (bersama-sama mereka) di rumah kamu atau di rumah bapak-bapakmu, di rumah ibu-ibumu, di rumah saudara-saudaramu yang laki-laki, di rumah saudara-saudaramu yang perempuan, di rumah saudara-saudara bapakmu yang laki-laki, di rumah saudara-saudara bapakmu yang perempuan, di rumah saudara-saudara ibumu yang laki-laki, di rumah saudara-saudara ibumu yang perempuan, (di rumah) yang kamu miliki kuncinya atau (di rumah) kawan-kawanmu. Tidak ada halangan bagi kamu makan bersama-sama mereka atau sendiri-sendiri. Apabila kamu memasuki rumah-rumah hendaklah kamu memberi salam (kepada penghuninya, yang berarti memberi salam) kepada dirimu sendiri, dengan salam yang penuh berkah dan baik dari sisi Allah. Demikianlah Allah menjelaskan ayat-ayat(-Nya) bagimu, agar kamu mengerti. "},{"id":"62","ar":"اِنَّمَا الْمُؤْمِنُوْنَ الَّذِيْنَ اٰمَنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَاِذَا كَانُوْا مَعَهٗ عَلٰٓى اَمْرٍ جَامِعٍ لَّمْ يَذْهَبُوْا حَتّٰى يَسْتَأْذِنُوْهُۗ اِنَّ الَّذِيْنَ يَسْتَأْذِنُوْنَكَ اُولٰۤىِٕكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاللّٰهِ وَرَسُوْلِهٖۚ فَاِذَا اسْتَأْذَنُوْكَ لِبَعْضِ شَأْنِهِمْ فَأْذَنْ لِّمَنْ شِئْتَ مِنْهُمْ وَاسْتَغْفِرْ لَهُمُ اللّٰهَ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ","bc":"innamal-mu`minụnallażīna āmanụ billāhi wa rasụlihī wa iżā kānụ ma'ahụ 'alā amrin jāmi'il lam yaż-habụ ḥattā yasta`żinụh, innallażīna yasta`żinụnaka ulā`ikallażīna yu`minụna billāhi wa rasụlih, fa iżasta`żanụka liba'ḍi sya`nihim fa`żal liman syi`ta min-hum wastagfir lahumullāh, innallāha gafụrur raḥīm","at":"(Yang disebut) orang mukmin hanyalah orang yang beriman kepada Allah dan Rasul-Nya (Muhammad), dan apabila mereka berada bersama-sama dengan dia (Muhammad) dalam suatu urusan bersama, mereka tidak meninggalkan (Rasulullah) sebelum meminta izin kepadanya. Sesungguhnya orang-orang yang meminta izin kepadamu (Muhammad), mereka itulah orang-orang yang (benar-benar) beriman kepada Allah dan Rasul-Nya. Maka apabila mereka meminta izin kepadamu karena suatu keperluan, berilah izin kepada siapa yang engkau kehendaki di antara mereka, dan mohonkanlah ampunan untuk mereka kepada Allah. Sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"63","ar":"لَا تَجْعَلُوْا دُعَاۤءَ الرَّسُوْلِ بَيْنَكُمْ كَدُعَاۤءِ بَعْضِكُمْ بَعْضًاۗ قَدْ يَعْلَمُ اللّٰهُ الَّذِيْنَ يَتَسَلَّلُوْنَ مِنْكُمْ لِوَاذًاۚ فَلْيَحْذَرِ الَّذِيْنَ يُخَالِفُوْنَ عَنْ اَمْرِهٖٓ اَنْ تُصِيْبَهُمْ فِتْنَةٌ اَوْ يُصِيْبَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"lā taj'alụ du'ā`ar-rasụli bainakum kadu'ā`i ba'ḍikum ba'ḍā, qad ya'lamullāhullażīna yatasallalụna mingkum liwāżā, falyaḥżarillażīna yukhālifụna 'an amrihī an tuṣībahum fitnatun au yuṣībahum 'ażābun alīm","at":"Janganlah kamu jadikan panggilan Rasul (Muhammad) di antara kamu seperti panggilan sebagian kamu kepada sebagian (yang lain). Sungguh, Allah mengetahui orang-orang yang keluar (secara) sembunyi-sembunyi di antara kamu dengan berlindung (kepada kawannya), maka hendaklah orang-orang yang menyalahi perintah Rasul-Nya takut akan mendapat cobaan atau ditimpa azab yang pedih."},{"id":"64","ar":"اَلَآ اِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ قَدْ يَعْلَمُ مَآ اَنْتُمْ عَلَيْهِۗ وَيَوْمَ يُرْجَعُوْنَ اِلَيْهِ فَيُنَبِّئُهُمْ بِمَا عَمِلُوْاۗ وَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ","bc":"alā inna lillāhi mā fis-samāwāti wal-arḍ, qad ya'lamu mā antum 'alaīh, wa yauma yurja'ụna ilaihi fa yunabbi`uhum bimā 'amilụ, wallāhu bikulli syai`in 'alīm","at":"Ketahuilah, sesungguhnya milik Allah-lah apa yang di langit dan di bumi. Dia mengetahui keadaan kamu sekarang. Dan (mengetahui pula) hari (ketika mereka) dikembalikan kepada-Nya, lalu diterangkan-Nya kepada mereka apa yang telah mereka kerjakan. Dan Allah Maha Mengetahui segala sesuatu."}],ar_rad=[{"id":"1","ar":"الۤمّۤرٰۗ تِلْكَ اٰيٰتُ الْكِتٰبِۗ وَالَّذِيْٓ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ الْحَقُّ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُوْنَ ","bc":"alif lām mīm rā, tilka āyātul-kitāb, wallażī unzila ilaika mir rabbikal-ḥaqqu wa lākinna akṡaran-nāsi lā yu`minụn","at":"Alif Lam Mim Ra. Ini adalah ayat-ayat Kitab (Al-Qur'an). Dan (Kitab) yang diturunkan kepadamu (Muhammad) dari Tuhanmu itu adalah benar; tetapi kebanyakan manusia tidak beriman (kepadanya)."},{"id":"2","ar":"اَللّٰهُ الَّذِيْ رَفَعَ السَّمٰوٰتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۗ كُلٌّ يَّجْرِيْ لِاَجَلٍ مُّسَمًّىۗ يُدَبِّرُ الْاَمْرَ يُفَصِّلُ الْاٰيٰتِ لَعَلَّكُمْ بِلِقَاۤءِ رَبِّكُمْ تُوْقِنُوْنَ ","bc":"allāhullażī rafa'as-samāwāti bigairi 'amadin taraunahā ṡummastawā 'alal-'arsyi wa sakhkharasy-syamsa wal-qamar, kulluy yajrī li`ajalim musammā, yudabbirul-amra yufaṣṣilul-āyāti la'allakum biliqā`i rabbikum tụqinụn","at":"Allah yang meninggikan langit tanpa tiang (sebagaimana) yang kamu lihat, kemudian Dia bersemayam di atas ‘Arsy. Dia menundukkan matahari dan bulan; masing-masing beredar menurut waktu yang telah ditentukan. Dia mengatur urusan (makhluk-Nya), dan menjelaskan tanda-tanda (kebesaran-Nya), agar kamu yakin akan pertemuan dengan Tuhanmu."},{"id":"3","ar":"وَهُوَ الَّذِيْ مَدَّ الْاَرْضَ وَجَعَلَ فِيْهَا رَوَاسِيَ وَاَنْهٰرًا ۗوَمِنْ كُلِّ الثَّمَرٰتِ جَعَلَ فِيْهَا زَوْجَيْنِ اثْنَيْنِ يُغْشِى الَّيْلَ النَّهَارَۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ ","bc":"wa huwallażī maddal-arḍa wa ja'ala fīhā rawāsiya wa an-hārā, wa ming kulliṡ-ṡamarāti ja'ala fīhā zaujainiṡnaini yugsyil-lailan-nahār, inna fī żālika la`āyātil liqaumiy yatafakkarụn","at":"Dan Dia yang menghamparkan bumi dan menjadikan gunung-gunung dan sungai-sungai di atasnya. Dan padanya Dia menjadikan semua buah-buahan berpasang-pasangan; Dia menutupkan malam kepada siang. Sungguh, pada yang demikian itu terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang berpikir."},{"id":"4","ar":"وَفِى الْاَرْضِ قِطَعٌ مُّتَجٰوِرٰتٌ وَّجَنّٰتٌ مِّنْ اَعْنَابٍ وَّزَرْعٌ وَّنَخِيْلٌ صِنْوَانٌ وَّغَيْرُ صِنْوَانٍ يُّسْقٰى بِمَاۤءٍ وَّاحِدٍۙ وَّنُفَضِّلُ بَعْضَهَا عَلٰى بَعْضٍ فِى الْاُكُلِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَ","bc":"wa fil-arḍi qiṭa'um mutajāwirātuw wa jannātum min a'nābiw wa zar'uw wa nakhīlun ṣinwānuw wa gairu ṣinwāniy yusqā bimā`iw wāḥidiw wa nufaḍḍilu ba'ḍahā 'alā ba'ḍin fil-ukul, inna fī żālika la`āyātil liqaumiy ya'qilụn","at":"Dan di bumi terdapat bagian-bagian yang berdampingan, kebun-kebun anggur, tanaman-tanaman, pohon kurma yang bercabang, dan yang tidak bercabang; disirami dengan air yang sama, tetapi Kami lebihkan tanaman yang satu dari yang lainnya dalam hal rasanya. Sungguh, pada yang demikian itu terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang mengerti."},{"id":"5","ar":"۞ وَاِنْ تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ ءَاِذَا كُنَّا تُرَابًا ءَاِنَّا لَفِيْ خَلْقٍ جَدِيْدٍ ەۗ اُولٰۤىِٕكَ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْۚ وَاُولٰۤىِٕكَ الْاَغْلٰلُ فِيْٓ اَعْنَاقِهِمْۚ وَاُولٰۤىِٕكَ اَصْحٰبُ النَّارِۚ هُمْ فِيْهَا خٰلِدُوْنَ","bc":"wa in ta'jab fa 'ajabung qauluhum a iżā kunnā turāban a innā lafī khalqin jadīd, ulā`ikallażīna kafarụ birabbihim, wa ulā`ikal-aglālu fī a'nāqihim, wa ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn","at":"Dan jika engkau merasa heran, maka yang mengherankan adalah ucapan mereka, “Apabila kami telah menjadi tanah, apakah kami akan (dikembalikan) menjadi makhluk yang baru?” Mereka itulah yang ingkar kepada Tuhannya; dan mereka itulah (yang dilekatkan) belenggu di lehernya. Mereka adalah penghuni neraka, mereka kekal di dalamnya."},{"id":"6","ar":"وَيَسْتَعْجِلُوْنَكَ بِالسَّيِّئَةِ قَبْلَ الْحَسَنَةِ وَقَدْ خَلَتْ مِنْ قَبْلِهِمُ الْمَثُلٰتُۗ وَاِنَّ رَبَّكَ لَذُوْ مَغْفِرَةٍ لِّلنَّاسِ عَلٰى ظُلْمِهِمْۚ وَاِنَّ رَبَّكَ لَشَدِيْدُ الْعِقَابِ ","bc":"wa yasta'jilụnaka bis-sayyi`ati qablal-ḥasanati wa qad khalat ming qablihimul-maṡulāt, wa inna rabbaka lażụ magfiratil lin-nāsi 'alā ẓulmihim, wa inna rabbaka lasyadīdul-'iqāb","at":"Dan mereka meminta kepadamu agar dipercepat (datangnya) siksaan, sebelum (mereka meminta) kebaikan, padahal telah terjadi bermacam-macam contoh siksaan sebelum mereka. Sungguh, Tuhanmu benar-benar memiliki ampunan bagi manusia atas kezaliman mereka, dan sungguh, Tuhanmu sangat keras siksaan-Nya."},{"id":"7","ar":"وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ اِنَّمَآ اَنْتَ مُنْذِرٌ وَّلِكُلِّ قَوْمٍ هَادٍ ","bc":"wa yaqụlullażīna kafarụ lau lā unzila 'alaihi āyatum mir rabbih, innamā anta munżiruw wa likulli qaumin hād","at":"Dan orang-orang kafir berkata, “Mengapa tidak diturunkan kepadanya (Muhammad) suatu tanda (mukjizat) dari Tuhannya?” Sesungguhnya engkau hanyalah seorang pemberi peringatan; dan bagi setiap kaum ada orang yang memberi petunjuk."},{"id":"8","ar":"اَللّٰهُ يَعْلَمُ مَا تَحْمِلُ كُلُّ اُنْثٰى وَمَا تَغِيْضُ الْاَرْحَامُ وَمَا تَزْدَادُ ۗوَكُلُّ شَيْءٍ عِنْدَهٗ بِمِقْدَارٍ ","bc":"allāhu ya'lamu mā taḥmilu kullu unṡā wa mā tagīḍul-ar-ḥāmu wa mā tazdād, wa kullu syai`in 'indahụ bimiqdār","at":"Allah mengetahui apa yang dikandung oleh setiap perempuan, apa yang kurang sempurna dan apa yang bertambah dalam rahim. Dan segala sesuatu ada ukuran di sisi-Nya."},{"id":"9","ar":"عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ الْكَبِيْرُ الْمُتَعَالِ ","bc":"'ālimul-gaibi wasy-syahādatil-kabīrul-muta'āl","at":"(Allah) Yang mengetahui semua yang gaib dan yang nyata; Yang Mahabesar, Mahatinggi."},{"id":"10","ar":"سَوَاۤءٌ مِّنْكُمْ مَّنْ اَسَرَّ الْقَوْلَ وَمَنْ جَهَرَ بِهٖ وَمَنْ هُوَ مُسْتَخْفٍۢ بِالَّيْلِ وَسَارِبٌۢ بِالنَّهَارِ ","bc":"sawā`um mingkum man asarral-qaula wa man jahara bihī wa man huwa mustakhfim bil-laili wa sāribum bin-nahār","at":"Sama saja (bagi Allah), siapa di antaramu yang merahasiakan ucapannya dan siapa yang berterus terang dengannya; dan siapa yang bersembunyi pada malam hari dan yang berjalan pada siang hari."},{"id":"11","ar":"لَهٗ مُعَقِّبٰتٌ مِّنْۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهٖ يَحْفَظُوْنَهٗ مِنْ اَمْرِ اللّٰهِ ۗاِنَّ اللّٰهَ لَا يُغَيِّرُ مَا بِقَوْمٍ حَتّٰى يُغَيِّرُوْا مَا بِاَنْفُسِهِمْۗ وَاِذَآ اَرَادَ اللّٰهُ بِقَوْمٍ سُوْۤءًا فَلَا مَرَدَّ لَهٗ ۚوَمَا لَهُمْ مِّنْ دُوْنِهٖ مِنْ وَّالٍ ","bc":"lahụ mu'aqqibātum mim baini yadaihi wa min khalfihī yaḥfaẓụnahụ min amrillāh, innallāha lā yugayyiru mā biqaumin ḥattā yugayyirụ mā bi`anfusihim, wa iżā arādallāhu biqaumin sū`an fa lā maradda lah, wa mā lahum min dụnihī miw wāl","at":"Baginya (manusia) ada malaikat-malaikat yang selalu menjaganya bergiliran, dari depan dan belakangnya. Mereka menjaganya atas perintah Allah. Sesungguhnya Allah tidak akan mengubah keadaan suatu kaum sebelum mereka mengubah keadaan diri mereka sendiri. Dan apabila Allah menghendaki keburukan terhadap suatu kaum, maka tak ada yang dapat menolaknya dan tidak ada pelindung bagi mereka selain Dia."},{"id":"12","ar":"هُوَ الَّذِيْ يُرِيْكُمُ الْبَرْقَ خَوْفًا وَّطَمَعًا وَّيُنْشِئُ السَّحَابَ الثِّقَالَۚ ","bc":"huwallażī yurīkumul-barqa khaufaw wa ṭama'aw wa yunsyi`us-saḥābaṡ-ṡiqāl","at":"Dialah yang memperlihatkan kilat kepadamu, yang menimbulkan ketakutan dan harapan, dan Dia menjadikan mendung. "},{"id":"13","ar":"وَيُسَبِّحُ الرَّعْدُ بِحَمْدِهٖ وَالْمَلٰۤىِٕكَةُ مِنْ خِيْفَتِهٖۚ وَيُرْسِلُ الصَّوَاعِقَ فَيُصِيْبُ بِهَا مَنْ يَّشَاۤءُ وَهُمْ يُجَادِلُوْنَ فِى اللّٰهِ ۚوَهُوَ شَدِيْدُ الْمِحَالِۗ ","bc":"wa yusabbiḥur-ra'du biḥamdihī wal-malā`ikatu min khīfatih, wa yursiluṣ-ṣawā'iqa fa yuṣību bihā may yasyā`u wa hum yujādilụna fillāh, wa huwa syadīdul miḥāl","at":"Dan guruh bertasbih memuji-Nya, (demikian pula) para malaikat karena takut kepada-Nya, dan Allah melepaskan halilintar, lalu menimpakannya kepada siapa yang Dia kehendaki, sementara mereka berbantah-bantahan tentang Allah, dan Dia Mahakeras siksaan-Nya."},{"id":"14","ar":"لَهٗ دَعْوَةُ الْحَقِّۗ وَالَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِهٖ لَا يَسْتَجِيْبُوْنَ لَهُمْ بِشَيْءٍ اِلَّا كَبَاسِطِ كَفَّيْهِ اِلَى الْمَاۤءِ لِيَبْلُغَ فَاهُ وَمَا هُوَ بِبَالِغِهٖۗ وَمَا دُعَاۤءُ الْكٰفِرِيْنَ اِلَّا فِيْ ضَلٰلٍ ","bc":"lahụ da'watul-ḥaqq, wallażīna yad'ụna min dụnihī lā yastajībụna lahum bisyai`in illā kabāsiṭi kaffaihi ilal-mā`i liyabluga fāhu wa mā huwa bibāligih, wa mā du'ā`ul-kāfirīna illā fī ḍalāl","at":"Hanya kepada Allah doa yang benar. Berhala-berhala yang mereka sembah selain Allah tidak dapat mengabulkan apa pun bagi mereka, tidak ubahnya seperti orang yang membukakan kedua telapak tangannya ke dalam air agar (air) sampai ke mulutnya. Padahal air itu tidak akan sampai ke mulutnya. Dan doa orang-orang kafir itu, hanyalah sia-sia belaka."},{"id":"15","ar":"وَلِلّٰهِ يَسْجُدُ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ طَوْعًا وَّكَرْهًا وَّظِلٰلُهُمْ بِالْغُدُوِّ وَالْاٰصَالِ ۩","bc":"wa lillāhi yasjudu man fis-samāwāti wal-arḍi ṭau'aw wa kar-haw wa ẓilāluhum bil-guduwwi wal-āṣāl","at":"Dan semua sujud kepada Allah baik yang di langit maupun yang di bumi, baik dengan kemauan sendiri maupun terpaksa (dan sujud pula) bayang-bayang mereka, pada waktu pagi dan petang hari."},{"id":"16","ar":"قُلْ مَنْ رَّبُّ السَّمٰوٰتِ وَالْاَرْضِۗ قُلِ اللّٰهُ ۗقُلْ اَفَاتَّخَذْتُمْ مِّنْ دُوْنِهٖٓ اَوْلِيَاۤءَ لَا يَمْلِكُوْنَ لِاَنْفُسِهِمْ نَفْعًا وَّلَا ضَرًّاۗ قُلْ هَلْ يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُ ەۙ اَمْ هَلْ تَسْتَوِى الظُّلُمٰتُ وَالنُّوْرُ ەۚ اَمْ جَعَلُوْا لِلّٰهِ شُرَكَاۤءَ خَلَقُوْا كَخَلْقِهٖ فَتَشَابَهَ الْخَلْقُ عَلَيْهِمْۗ قُلِ اللّٰهُ خَالِقُ كُلِّ شَيْءٍ وَّهُوَ الْوَاحِدُ الْقَهَّارُ","bc":"qul mar rabbus-samāwāti wal-arḍ, qulillāh, qul a fattakhażtum min dụnihī auliyā`a lā yamlikụna li`anfusihim naf'aw wa lā ḍarrā, qul hal yastawil-a'mā wal-baṣīru am hal tastawiẓ-ẓulumātu wan-nụr, am ja'alụ lillāhi syurakā`a khalaqụ kakhalqihī fa tasyābahal-khalqu 'alaihim, qulillāhu khāliqu kulli syai`iw wa huwal-wāḥidul-qahhār","at":"Katakanlah (Muhammad), “Siapakah Tuhan langit dan bumi?” Katakanlah, “Allah.” Katakanlah, “Pantaskah kamu mengambil pelindung-pelindung selain Allah, padahal mereka tidak kuasa mendatangkan manfaat maupun menolak mudarat bagi dirinya sendiri?” Katakanlah, “Samakah orang yang buta dengan yang dapat melihat? Atau samakah yang gelap dengan yang terang? Apakah mereka menjadikan sekutu-sekutu bagi Allah yang dapat menciptakan seperti ciptaan-Nya sehingga kedua ciptaan itu serupa menurut pandangan mereka?” Katakanlah, “Allah adalah Pencipta segala sesuatu dan Dia Tuhan Yang Maha Esa, Mahaperkasa.”"},{"id":"17","ar":"اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَسَالَتْ اَوْدِيَةٌ ۢ بِقَدَرِهَا فَاحْتَمَلَ السَّيْلُ زَبَدًا رَّابِيًا ۗوَمِمَّا يُوْقِدُوْنَ عَلَيْهِ فِى النَّارِ ابْتِغَاۤءَ حِلْيَةٍ اَوْ مَتَاعٍ زَبَدٌ مِّثْلُهٗ ۗ كَذٰلِكَ يَضْرِبُ اللّٰهُ الْحَقَّ وَالْبَاطِلَ ەۗ فَاَمَّا الزَّبَدُ فَيَذْهَبُ جُفَاۤءً ۚوَاَمَّا مَا يَنْفَعُ النَّاسَ فَيَمْكُثُ فِى الْاَرْضِۗ كَذٰلِكَ يَضْرِبُ اللّٰهُ الْاَمْثَالَ ۗ","bc":"anzala minas-samā`i mā`an fa sālat audiyatum biqadarihā faḥtamalas-sailu zabadar rābiyā, wa mimmā yụqidụna 'alaihi fin-nāribtigā`a ḥilyatin au matā'in zabadum miṡluh, każālika yaḍribullāhul-ḥaqqa wal-bāṭil, fa ammaz-zabadu fa yaż-habu jufā`ā, wa ammā mā yanfa'un-nāsa fa yamkuṡu fil-arḍ, każālika yaḍribullāhul-amṡāl","at":"Allah telah menurunkan air (hujan) dari langit, maka mengalirlah ia (air) di lembah-lembah menurut ukurannya, maka arus itu membawa buih yang mengambang. Dan dari apa (logam) yang mereka lebur dalam api untuk membuat perhiasan atau alat-alat, ada (pula) buihnya seperti (buih arus) itu. Demikianlah Allah membuat perumpamaan tentang yang benar dan yang batil. Adapun buih, akan hilang sebagai sesuatu yang tidak ada gunanya; tetapi yang bermanfaat bagi manusia, akan tetap ada di bumi. Demikianlah Allah membuat perumpamaan."},{"id":"18","ar":"لِلَّذِيْنَ اسْتَجَابُوْا لِرَبِّهِمُ الْحُسْنٰىۗ وَالَّذِيْنَ لَمْ يَسْتَجِيْبُوْا لَهٗ لَوْ اَنَّ لَهُمْ مَّا فِى الْاَرْضِ جَمِيْعًا وَّمِثْلَهٗ مَعَهٗ لَافْتَدَوْا بِهٖ ۗ اُولٰۤىِٕكَ لَهُمْ سُوْۤءُ الْحِسَابِ ەۙ وَمَأْوٰىهُمْ جَهَنَّمُ ۗوَبِئْسَ الْمِهَادُ","bc":"lillażīnastajābụ lirabbihimul-ḥusnā, wallażīna lam yastajībụ lahụ lau anna lahum mā fil-arḍi jamī'aw wa miṡlahụ ma'ahụ laftadau bih, ulā`ika lahum sū`ul-ḥisābi wa ma`wāhum jahannam, wa bi`sal-mihād","at":"Bagi orang-orang yang memenuhi seruan Tuhan, mereka (disediakan) balasan yang baik. Dan orang-orang yang tidak memenuhi seruan-Nya, sekiranya mereka memiliki semua yang ada di bumi dan (ditambah) sebanyak itu lagi, niscaya mereka akan menebus dirinya dengan itu. Orang-orang itu mendapat hisab (perhitungan) yang buruk dan tempat kediaman mereka Jahanam, dan itulah seburuk-buruk tempat kediaman."},{"id":"19","ar":"۞ اَفَمَنْ يَّعْلَمُ اَنَّمَآ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ الْحَقُّ كَمَنْ هُوَ اَعْمٰىۗ اِنَّمَا يَتَذَكَّرُ اُولُوا الْاَلْبَابِۙ ","bc":"a fa may ya'lamu annamā unzila ilaika mir rabbikal-ḥaqqu kaman huwa a'mā, innamā yatażakkaru ulul-albāb","at":"Maka apakah orang yang mengetahui bahwa apa yang diturunkan Tuhan kepadamu adalah kebenaran, sama dengan orang yang buta? Hanya orang berakal saja yang dapat mengambil pelajaran,"},{"id":"20","ar":"الَّذِيْنَ يُوْفُوْنَ بِعَهْدِ اللّٰهِ وَلَا يَنْقُضُوْنَ الْمِيْثَاقَۙ ","bc":"allażīna yụfụna bi'ahdillāhi wa lā yangquḍụnal-mīṡāq","at":"(yaitu) orang yang memenuhi janji Allah dan tidak melanggar perjanjian,"},{"id":"21","ar":"وَالَّذِيْنَ يَصِلُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيَخْشَوْنَ رَبَّهُمْ وَيَخَافُوْنَ سُوْۤءَ الْحِسَابِ ۗ ","bc":"wallażīna yaṣilụna mā amarallāhu bihī ay yụṣala wa yakhsyauna rabbahum wa yakhāfụna sū`al-ḥisāb","at":"dan orang-orang yang menghubungkan apa yang diperintahkan Allah agar dihubungkan, dan mereka takut kepada Tuhannya dan takut kepada hisab yang buruk."},{"id":"22","ar":"وَالَّذِيْنَ صَبَرُوا ابْتِغَاۤءَ وَجْهِ رَبِّهِمْ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً وَّيَدْرَءُوْنَ بِالْحَسَنَةِ السَّيِّئَةَ اُولٰۤىِٕكَ لَهُمْ عُقْبَى الدَّارِۙ ","bc":"wallażīna ṣabarubtigā`a waj-hi rabbihim wa aqāmuṣ-ṣalāta wa anfaqụ mimmā razaqnāhum sirraw wa 'alāniyataw wa yadra`ụna bil-ḥasanatis-sayyi`ata ulā`ika lahum 'uqbad-dār","at":"Dan orang yang sabar karena mengharap keridaan Tuhannya, melaksanakan salat, dan menginfakkan sebagian rezeki yang Kami berikan kepada mereka, secara sembunyi atau terang-terangan serta menolak kejahatan dengan kebaikan; orang itulah yang men-dapat tempat kesudahan (yang baik),"},{"id":"23","ar":"جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا وَمَنْ صَلَحَ مِنْ اٰبَاۤىِٕهِمْ وَاَزْوَاجِهِمْ وَذُرِّيّٰتِهِمْ وَالْمَلٰۤىِٕكَةُ يَدْخُلُوْنَ عَلَيْهِمْ مِّنْ كُلِّ بَابٍۚ ","bc":"jannātu 'adniy yadkhulụnahā wa man ṣalaḥa min ābā`ihim wa azwājihim wa żurriyyātihim wal-malā`ikatu yadkhulụna 'alaihim ming kulli bāb","at":"(yaitu) surga-surga ‘Adn, mereka masuk ke dalamnya bersama dengan orang yang saleh dari nenek moyangnya, pasangan-pasangannya, dan anak cucunya, sedang para malaikat masuk ke tempat-tempat mereka dari semua pintu;"},{"id":"24","ar":"سَلٰمٌ عَلَيْكُمْ بِمَا صَبَرْتُمْ فَنِعْمَ عُقْبَى الدَّارِۗ ","bc":"salāmun 'alaikum bimā ṣabartum fa ni'ma 'uqbad-dār","at":"(sambil mengucapkan), “Selamat sejahtera atasmu karena kesabaranmu.” Maka alangkah nikmatnya tempat kesudahan itu."},{"id":"25","ar":"وَالَّذِيْنَ يَنْقُضُوْنَ عَهْدَ اللّٰهِ مِنْ ۢ بَعْدِ مِيْثَاقِهٖ وَيَقْطَعُوْنَ مَآ اَمَرَ اللّٰهُ بِهٖٓ اَنْ يُّوْصَلَ وَيُفْسِدُوْنَ فِى الْاَرْضِۙ اُولٰۤىِٕكَ لَهُمُ اللَّعْنَةُ وَلَهُمْ سُوْۤءُ الدَّارِ ","bc":"wallażīna yangquḍụna 'ahdallāhi mim ba'di mīṡāqihī wa yaqṭa'ụna mā amarallāhu bihī ay yụṣala wa yufsidụna fil-arḍi ulā`ika lahumul-la'natu wa lahum sū`ud-dār","at":"Dan orang-orang yang melanggar janji Allah setelah diikrarkannya, dan memutuskan apa yang diperintahkan Allah agar disambungkan dan berbuat kerusakan di bumi; mereka itu memperoleh kutukan dan tempat kediaman yang buruk (Jahanam)."},{"id":"26","ar":"اَللّٰهُ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۗوَفَرِحُوْا بِالْحَيٰوةِ الدُّنْيَاۗ وَمَا الْحَيٰوةُ الدُّنْيَا فِى الْاٰخِرَةِ اِلَّا مَتَاعٌ ","bc":"allāhu yabsuṭur-rizqa limay yasyā`u wa yaqdir, wa fariḥụ bil-ḥayātid-dun-yā, wa mal-ḥayātud-dun-yā fil-ākhirati illā matā'","at":"Allah melapangkan rezeki bagi siapa yang Dia kehendaki dan membatasi (bagi siapa yang Dia kehendaki). Mereka bergembira dengan kehidupan dunia, padahal kehidupan dunia hanyalah kesenangan (yang sedikit) dibanding kehidupan akhirat."},{"id":"27","ar":"وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۗ قُلْ اِنَّ اللّٰهَ يُضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْٓ اِلَيْهِ مَنْ اَنَابَۖ ","bc":"wa yaqụlullażīna kafarụ lau lā unzila 'alaihi āyatum mir rabbih, qul innallāha yuḍillu may yasyā`u wa yahdī ilaihi man anāb","at":"Dan orang-orang kafir berkata, “Mengapa tidak diturunkan kepadanya (Muhammad) tanda (mukjizat) dari Tuhannya?” Katakanlah (Muhammad), “Sesungguhnya Allah menyesatkan siapa yang Dia kehendaki dan memberi petunjuk orang yang bertobat kepada-Nya,”"},{"id":"28","ar":"الَّذِيْنَ اٰمَنُوْا وَتَطْمَىِٕنُّ قُلُوْبُهُمْ بِذِكْرِ اللّٰهِ ۗ اَلَا بِذِكْرِ اللّٰهِ تَطْمَىِٕنُّ الْقُلُوْبُ ۗ ","bc":"allażīna āmanụ wa taṭma`innu qulụbuhum biżikrillāh, alā biżikrillāhi taṭma`innul-qulụb","at":"(yaitu) orang-orang yang beriman dan hati mereka menjadi tenteram dengan mengingat Allah. Ingatlah, hanya dengan mengingat Allah hati menjadi tenteram."},{"id":"29","ar":"اَلَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ طُوْبٰى لَهُمْ وَحُسْنُ مَاٰبٍ ","bc":"allażīna āmanụ wa 'amiluṣ-ṣāliḥāti ṭụbā lahum wa ḥusnu ma`āb","at":"Orang-orang yang beriman dan mengerjakan kebajikan, mereka mendapat kebahagiaan dan tempat kembali yang baik."},{"id":"30","ar":"كَذٰلِكَ اَرْسَلْنٰكَ فِيْٓ اُمَّةٍ قَدْ خَلَتْ مِنْ قَبْلِهَآ اُمَمٌ لِّتَتْلُوَا۟ عَلَيْهِمُ الَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ وَهُمْ يَكْفُرُوْنَ بِالرَّحْمٰنِۗ قُلْ هُوَ رَبِّيْ لَآ اِلٰهَ اِلَّا هُوَۚ عَلَيْهِ تَوَكَّلْتُ وَاِلَيْهِ مَتَابِ ","bc":"każālika arsalnāka fī ummating qad khalat ming qablihā umamul litatluwā 'alaihimullażī auḥainā ilaika wa hum yakfurụna bir-raḥmān, qul huwa rabbī lā ilāha illā huw, 'alaihi tawakkaltu wa ilaihi matāb","at":"Demikianlah, Kami telah mengutus engkau (Muhammad) kepada suatu umat yang sungguh sebelumnya telah berlalu beberapa umat, agar engkau bacakan kepada mereka (Al-Qur'an) yang Kami wahyukan kepadamu, padahal mereka ingkar kepada Tuhan Yang Maha Pengasih. Katakanlah, “Dia Tuhanku, tidak ada tuhan selain Dia; hanya kepada-Nya aku bertawakal dan hanya kepada-Nya aku bertobat.”"},{"id":"31","ar":"وَلَوْ اَنَّ قُرْاٰنًا سُيِّرَتْ بِهِ الْجِبَالُ اَوْ قُطِّعَتْ بِهِ الْاَرْضُ اَوْ كُلِّمَ بِهِ الْمَوْتٰىۗ بَلْ لِّلّٰهِ الْاَمْرُ جَمِيْعًاۗ اَفَلَمْ يَا۟يْـَٔسِ الَّذِيْنَ اٰمَنُوْٓا اَنْ لَّوْ يَشَاۤءُ اللّٰهُ لَهَدَى النَّاسَ جَمِيْعًاۗ وَلَا يَزَالُ الَّذِيْنَ كَفَرُوْا تُصِيْبُهُمْ بِمَا صَنَعُوْا قَارِعَةٌ اَوْ تَحُلُّ قَرِيْبًا مِّنْ دَارِهِمْ حَتّٰى يَأْتِيَ وَعْدُ اللّٰهِ ۗاِنَّ اللّٰهَ لَا يُخْلِفُ الْمِيْعَادَ ","bc":"walau anna qur`ānan suyyirat bihil-jibālu au quṭṭi'at bihil-arḍu au kullima bihil-mautā, bal lillāhil-amru jamī'ā, a fa lam yai`asillażīna āmanū al lau yasyā`ullāhu laḥadan-nāsa jamī'ā, wa lā yazālullażīna kafarụ tuṣībuhum bimā ṣana'ụ qāri'atun au taḥullu qarībam min dārihim ḥattā ya`tiya wa'dullāh, innallāha lā yukhliful-mī'ād","at":"Dan sekiranya ada suatu bacaan (Kitab Suci) yang dengan itu gunung-gunung dapat digoncangkan, atau bumi jadi terbelah, atau orang yang sudah mati dapat berbicara, (itulah Al-Qur'an). Sebenarnya segala urusan itu milik Allah. Maka tidakkah orang-orang yang beriman mengetahui bahwa sekiranya Allah menghendaki (semua manusia beriman), tentu Allah memberi petunjuk kepada manusia semuanya. Dan orang-orang kafir senantiasa ditimpa bencana disebabkan perbuatan mereka sendiri atau bencana itu terjadi dekat tempat kediaman mereka, sampai datang janji Allah (penaklukkan Mekah). Sungguh, Allah tidak menyalahi janji."},{"id":"32","ar":"وَلَقَدِ اسْتُهْزِئَ بِرُسُلٍ مِّنْ قَبْلِكَ فَاَمْلَيْتُ لِلَّذِيْنَ كَفَرُوْا ثُمَّ اَخَذْتُهُمْ فَكَيْفَ كَانَ عِقَابِ ","bc":"wa laqadistuhzi`a birusulim ming qablika fa amlaitu lillażīna kafarụ ṡumma akhażtuhum fa kaifa kāna 'iqāb","at":"Dan sesungguhnya beberapa rasul sebelum engkau (Muhammad) telah diperolok-olokkan, maka Aku beri tenggang waktu kepada orang-orang kafir itu, kemudian Aku binasakan mereka. Maka alangkah hebatnya siksaan-Ku itu! "},{"id":"33","ar":"اَفَمَنْ هُوَ قَاۤىِٕمٌ عَلٰى كُلِّ نَفْسٍۢ بِمَا كَسَبَتْۚ وَجَعَلُوْا لِلّٰهِ شُرَكَاۤءَ ۗ قُلْ سَمُّوْهُمْۗ اَمْ تُنَبِّـُٔوْنَهٗ بِمَا لَا يَعْلَمُ فِى الْاَرْضِ اَمْ بِظَاهِرٍ مِّنَ الْقَوْلِ ۗبَلْ زُيِّنَ لِلَّذِيْنَ كَفَرُوْا مَكْرُهُمْ وَصُدُّوْا عَنِ السَّبِيْلِ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍ ","bc":"a fa man huwa qā`imun 'alā kulli nafsim bimā kasabat, wa ja'alụ lillāhi syurakā`, qul sammụhum, am tunabbi`ụnahụ bimā lā ya'lamu fil-arḍi am biẓāhirim minal-qaụl, bal zuyyina lillażīna kafarụ makruhum wa ṣuddụ 'anis-sabīl, wa may yuḍlilillāhu fa mā lahụ min hād","at":"Maka apakah Tuhan yang menjaga setiap jiwa terhadap apa yang diperbuatnya (sama dengan yang lain)? Mereka menjadikan sekutu-sekutu bagi Allah. Katakanlah, “Sebutkanlah sifat-sifat mereka itu.” Atau apakah kamu hendak memberitahukan kepada Allah apa yang tidak diketahui-Nya di bumi, atau (mengatakan tentang hal itu) sekedar perkataan pada lahirnya saja. Sebenarnya bagi orang kafir, tipu daya mereka itu dijadikan terasa indah, dan mereka dihalangi dari jalan (yang benar). Dan barangsiapa disesatkan Allah, maka tidak ada seorang pun yang memberi petunjuk baginya."},{"id":"34","ar":"لَهُمْ عَذَابٌ فِى الْحَيٰوةِ الدُّنْيَا وَلَعَذَابُ الْاٰخِرَةِ اَشَقُّۚ وَمَا لَهُمْ مِّنَ اللّٰهِ مِنْ وَّاقٍ ","bc":"lahum 'ażābun fil-ḥayātid-dun-yā wa la'ażābul-ākhirati asyaqq, wa mā lahum minallāhi miw wāq","at":"Mereka mendapat siksaan dalam kehidupan dunia, dan azab akhirat pasti lebih keras. Tidak ada seorang pun yang melindungi mereka dari (azab) Allah."},{"id":"35","ar":"۞ مَثَلُ الْجَنَّةِ الَّتِيْ وُعِدَ الْمُتَّقُوْنَۗ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۗ اُكُلُهَا دَاۤىِٕمٌ وَّظِلُّهَاۗ تِلْكَ عُقْبَى الَّذِيْنَ اتَّقَوْا ۖوَّعُقْبَى الْكٰفِرِيْنَ النَّارُ ","bc":"maṡalul-jannatillatī wu'idal-muttaqụn, tajrī min taḥtihal-an-hār, ukuluhā dā`imuw wa ẓilluhā, tilka 'uqballażīnattaqaw wa 'uqbal-kāfirīnan-nār","at":"Perumpamaan surga yang dijanjikan kepada orang yang bertakwa (ialah seperti taman), mengalir di bawahnya sungai-sungai; senantiasa berbuah dan teduh. Itulah tempat kesudahan bagi orang yang bertakwa; sedang tempat kesudahan bagi orang yang ingkar kepada Tuhan ialah neraka."},{"id":"36","ar":"وَالَّذِيْنَ اٰتَيْنٰهُمُ الْكِتٰبَ يَفْرَحُوْنَ بِمَآ اُنْزِلَ اِلَيْكَ وَمِنَ الْاَحْزَابِ مَنْ يُّنْكِرُ بَعْضَهٗ ۗ قُلْ اِنَّمَآ اُمِرْتُ اَنْ اَعْبُدَ اللّٰهَ وَلَآ اُشْرِكَ بِهٖ ۗاِلَيْهِ اَدْعُوْا وَاِلَيْهِ مَاٰبِ ","bc":"wallażīna ātaināhumul-kitāba yafraḥụna bimā unzila ilaika wa minal-aḥzābi may yungkiru ba'ḍah, qul innamā umirtu an a'budallāha wa lā usyrika bih, ilaihi ad'ụ wa ilaihi ma`āb","at":"Dan orang yang telah Kami berikan kitab kepada mereka bergembira dengan apa (kitab) yang diturunkan kepadamu (Muhammad), dan ada di antara golongan (Yahudi dan Nasrani), yang mengingkari sebagiannya. Katakanlah, “Aku hanya diperintah untuk menyembah Allah dan tidak mempersekutukan-Nya. Hanya kepada-Nya aku seru (manusia) dan hanya kepada-Nya aku kembali.”"},{"id":"37","ar":"وَكَذٰلِكَ اَنْزَلْنٰهُ حُكْمًا عَرَبِيًّاۗ وَلَىِٕنِ اتَّبَعْتَ اَهْوَاۤءَهُمْ بَعْدَمَا جَاۤءَكَ مِنَ الْعِلْمِۙ مَا لَكَ مِنَ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا وَاقٍ ","bc":"wa każālika anzalnāhu ḥukman 'arabiyyā, wa la`inittaba'ta ahwā`ahum ba'da mā jā`aka minal-'ilmi mā laka minallāhi miw waliyyiw wa lā wāq","at":"Dan demikianlah Kami telah menurunkannya (Al-Qur'an) sebagai peraturan (yang benar) dalam bahasa Arab. Sekiranya engkau mengikuti keinginan mereka setelah datang pengetahuan kepadamu, maka tidak ada yang melindungi dan yang menolong engkau dari (siksaan) Allah."},{"id":"38","ar":"وَلَقَدْ اَرْسَلْنَا رُسُلًا مِّنْ قَبْلِكَ وَجَعَلْنَا لَهُمْ اَزْوَاجًا وَّذُرِّيَّةً ۗوَمَا كَانَ لِرَسُوْلٍ اَنْ يَّأْتِيَ بِاٰيَةٍ اِلَّا بِاِذْنِ اللّٰهِ ۗلِكُلِّ اَجَلٍ كِتَابٌ ","bc":"wa laqad arsalnā rusulam ming qablika wa ja'alnā lahum azwājaw wa żurriyyah, wa mā kāna lirasụlin ay ya`tiya bi`āyatin illā bi`iżnillāh, likulli ajaling kitāb","at":"Dan sungguh, Kami telah mengutus beberapa rasul sebelum engkau (Muhammad) dan Kami berikan kepada mereka istri-istri dan keturunan. Tidak ada hak bagi seorang rasul mendatangkan sesuatu bukti (mukjizat) melainkan dengan izin Allah. Untuk setiap masa ada Kitab (tertentu)."},{"id":"39","ar":"يَمْحُوا اللّٰهُ مَا يَشَاۤءُ وَيُثْبِتُ ۚوَعِنْدَهٗٓ اُمُّ الْكِتٰبِ ","bc":"yam-ḥullāhu mā yasyā`u wa yuṡbit, wa 'indahū ummul-kitāb","at":"Allah menghapus dan menetapkan apa yang Dia kehendaki. Dan di sisi-Nya terdapat Ummul-Kitab (Lauh Mahfuzh)."},{"id":"40","ar":"وَاِنْ مَّا نُرِيَنَّكَ بَعْضَ الَّذِيْ نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِنَّمَا عَلَيْكَ الْبَلٰغُ وَعَلَيْنَا الْحِسَابُ ","bc":"wa im mā nuriyannaka ba'ḍallażī na'iduhum au natawaffayannaka fa innamā 'alaikal-balāgu wa 'alainal-ḥisāb","at":"Dan sungguh jika Kami perlihatkan kepadamu (Muhammad) sebagian (siksaan) yang Kami ancamkan kepada mereka atau Kami wafatkan engkau, maka sesungguhnya tugasmu hanya menyampaikan saja, dan Kamilah yang memperhitungkan (amal mereka)."},{"id":"41","ar":"اَوَلَمْ يَرَوْا اَنَّا نَأْتِى الْاَرْضَ نَنْقُصُهَا مِنْ اَطْرَافِهَاۗ وَاللّٰهُ يَحْكُمُ لَا مُعَقِّبَ لِحُكْمِهٖۗ وَهُوَ سَرِيْعُ الْحِسَابِ ","bc":"a wa lam yarau annā na`til-arḍa nangquṣuhā min aṭrāfihā, wallāhu yaḥkumu lā mu'aqqiba liḥukmih, wa huwa sarī'ul-ḥisāb","at":"Dan apakah mereka tidak melihat bahwa Kami mendatangi daerah-daerah (orang yang ingkar kepada Allah), lalu Kami kurangi (daerah-daerah) itu (sedikit demi sedikit) dari tepi-tepinya? Dan Allah menetapkan hukum (menurut kehendak-Nya), tidak ada yang dapat menolak ketetapan-Nya; Dia Mahacepat perhitungan-Nya."},{"id":"42","ar":"وَقَدْ مَكَرَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَلِلّٰهِ الْمَكْرُ جَمِيْعًا ۗيَعْلَمُ مَا تَكْسِبُ كُلُّ نَفْسٍۗ وَسَيَعْلَمُ الْكُفّٰرُ لِمَنْ عُقْبَى الدَّارِ ","bc":"wa qad makarallażīna ming qablihim fa lillāhil-makru jamī'ā, ya'lamu mā taksibu kullu nafs, wa saya'lamul-kuffāru liman 'uqbad-dār","at":"Dan sungguh, orang sebelum mereka (kafir Mekah) telah mengadakan tipu daya, tetapi semua tipu daya itu dalam kekuasaan Allah. Dia mengetahui apa yang diusahakan oleh setiap orang, dan orang yang ingkar kepada Tuhan akan mengetahui untuk siapa tempat kesudahan (yang baik)."},{"id":"43","ar":"وَيَقُوْلُ الَّذِيْنَ كَفَرُوْا لَسْتَ مُرْسَلًا ۗ قُلْ كَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنِيْ وَبَيْنَكُمْۙ وَمَنْ عِنْدَهٗ عِلْمُ الْكِتٰبِ ","bc":"wa yaqụlullażīna kafarụ lasta mursalā, qul kafā billāhi syahīdam bainī wa bainakum wa man 'indahụ 'ilmul-kitāb","at":" Dan orang-orang kafir berkata, “Engkau (Muhammad) bukanlah seorang Rasul.” Katakanlah, “Cukuplah Allah dan orang yang menguasai ilmu Al-Kitab menjadi saksi antara aku dan kamu.” "}],ar_rahman=[{"id":"1","ar":"اَلرَّحْمٰنُۙ ","bc":"ar-raḥmān","at":"(Allah) Yang Maha Pengasih,"},{"id":"2","ar":"عَلَّمَ الْقُرْاٰنَۗ ","bc":"'allamal-qur`ān","at":"Yang telah mengajarkan Al-Qur'an."},{"id":"3","ar":"خَلَقَ الْاِنْسَانَۙ ","bc":"khalaqal-insān","at":"Dia menciptakan manusia, "},{"id":"4","ar":"عَلَّمَهُ الْبَيَانَ ","bc":"'allamahul-bayān","at":"mengajarnya pandai berbicara."},{"id":"5","ar":"اَلشَّمْسُ وَالْقَمَرُ بِحُسْبَانٍۙ ","bc":"asy-syamsu wal-qamaru biḥusbān","at":"Matahari dan bulan beredar menurut perhitungan,"},{"id":"6","ar":"وَّالنَّجْمُ وَالشَّجَرُ يَسْجُدَانِ ","bc":"wan-najmu wasy-syajaru yasjudān","at":"dan tetumbuhan dan pepohonan, keduanya tunduk (kepada-Nya). "},{"id":"7","ar":"وَالسَّمَاۤءَ رَفَعَهَا وَوَضَعَ الْمِيْزَانَۙ ","bc":"was-samā`a rafa'ahā wa waḍa'al-mīzān","at":"Dan langit telah ditinggikan-Nya dan Dia ciptakan keseimbangan,"},{"id":"8","ar":"اَلَّا تَطْغَوْا فِى الْمِيْزَانِ ","bc":"allā taṭgau fil-mīzān","at":"agar kamu jangan merusak keseimbangan itu, "},{"id":"9","ar":"وَاَقِيْمُوا الْوَزْنَ بِالْقِسْطِ وَلَا تُخْسِرُوا الْمِيْزَانَ ","bc":"wa aqīmul-wazna bil-qisṭi wa lā tukhsirul-mīzān","at":"dan tegakkanlah keseimbangan itu dengan adil dan janganlah kamu mengurangi keseimbangan itu. "},{"id":"10","ar":"وَالْاَرْضَ وَضَعَهَا لِلْاَنَامِۙ ","bc":"wal-arḍa waḍa'ahā lil-anām","at":"Dan bumi telah dibentangkan-Nya untuk makhluk(-Nya), "},{"id":"11","ar":"فِيْهَا فَاكِهَةٌ وَّالنَّخْلُ ذَاتُ الْاَكْمَامِۖ ","bc":"fīhā fākihatuw wan-nakhlu żātul-akmām","at":"di dalamnya ada buah-buahan dan pohon kurma yang mempunyai kelopak mayang, "},{"id":"12","ar":"وَالْحَبُّ ذُو الْعَصْفِ وَالرَّيْحَانُۚ ","bc":"wal-ḥabbu żul-'aṣfi war-raiḥān","at":"dan biji-bijian yang berkulit dan bunga-bunga yang harum baunya. "},{"id":"13","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan? "},{"id":"14","ar":"خَلَقَ الْاِنْسَانَ مِنْ صَلْصَالٍ كَالْفَخَّارِ ","bc":"khalaqal-insāna min ṣalṣāling kal-fakhkhār","at":"Dia menciptakan manusia dari tanah kering seperti tembikar,"},{"id":"15","ar":"وَخَلَقَ الْجَاۤنَّ مِنْ مَّارِجٍ مِّنْ نَّارٍۚ ","bc":"wa khalaqal-jānna mim mārijim min nār","at":"dan Dia menciptakan jin dari nyala api tanpa asap."},{"id":"16","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"17","ar":"رَبُّ الْمَشْرِقَيْنِ وَرَبُّ الْمَغْرِبَيْنِۚ ","bc":"rabbul-masyriqaini wa rabbul-magribaīn","at":"Tuhan (yang memelihara) dua timur dan Tuhan (yang memelihara) dua barat."},{"id":"18","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"19","ar":"مَرَجَ الْبَحْرَيْنِ يَلْتَقِيٰنِۙ","bc":"marajal-baḥraini yaltaqiyān","at":"Dia membiarkan dua laut mengalir yang (kemudian) keduanya bertemu, "},{"id":"20","ar":"بَيْنَهُمَا بَرْزَخٌ لَّا يَبْغِيٰنِۚ","bc":"bainahumā barzakhul lā yabgiyān","at":"di antara keduanya ada batas yang tidak dilampaui oleh masing-masing."},{"id":"21","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"22","ar":"يَخْرُجُ مِنْهُمَا اللُّؤْلُؤُ وَالْمَرْجَانُۚ","bc":"yakhruju min-humal-lu`lu`u wal-marjān","at":"Dari keduanya keluar mutiara dan marjan."},{"id":"23","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan? "},{"id":"24","ar":"وَلَهُ الْجَوَارِ الْمُنْشَاٰتُ فِى الْبَحْرِ كَالْاَعْلَامِۚ ","bc":"wa lahul-jawāril-munsya`ātu fil-baḥri kal-a'lām","at":"Milik-Nyalah kapal-kapal yang berlayar di lautan bagaikan gunung-gunung."},{"id":"25","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan? "},{"id":"26","ar":"كُلُّ مَنْ عَلَيْهَا فَانٍۖ ","bc":"kullu man 'alaihā fān","at":"Semua yang ada di bumi itu akan binasa, "},{"id":"27","ar":"وَّيَبْقٰى وَجْهُ رَبِّكَ ذُو الْجَلٰلِ وَالْاِكْرَامِۚ ","bc":"wa yabqā waj-hu rabbika żul-jalāli wal-ikrām","at":"tetapi wajah Tuhanmu yang memiliki kebesaran dan kemuliaan tetap kekal. "},{"id":"28","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"29","ar":"يَسْـَٔلُهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ كُلَّ يَوْمٍ هُوَ فِيْ شَأْنٍۚ","bc":"yas`aluhụ man fis-samāwāti wal-arḍ, kulla yaumin huwa fī sya`n","at":"Apa yang di langit dan di bumi selalu meminta kepada-Nya. Setiap waktu Dia dalam kesibukan. "},{"id":"30","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"31","ar":"سَنَفْرُغُ لَكُمْ اَيُّهَ الثَّقَلٰنِۚ ","bc":"sanafrugu lakum ayyuhaṡ-ṡaqalān","at":"Kami akan memberi perhatian sepenuhnya kepadamu wahai (golongan) manusia dan jin! "},{"id":"32","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"33","ar":"يٰمَعْشَرَ الْجِنِّ وَالْاِنْسِ اِنِ اسْتَطَعْتُمْ اَنْ تَنْفُذُوْا مِنْ اَقْطَارِ السَّمٰوٰتِ وَالْاَرْضِ فَانْفُذُوْاۗ لَا تَنْفُذُوْنَ اِلَّا بِسُلْطٰنٍۚ ","bc":"yā ma'syaral-jinni wal-insi inistaṭa'tum an tanfużụ min aqṭāris-samāwāti wal-arḍi fanfużụ, lā tanfużụna illā bisulṭān","at":"Wahai golongan jin dan manusia! Jika kamu sanggup menembus (melintasi) penjuru langit dan bumi, maka tembuslah. Kamu tidak akan mampu menembusnya kecuali dengan kekuatan (dari Allah). "},{"id":"34","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"35","ar":"يُرْسَلُ عَلَيْكُمَا شُوَاظٌ مِّنْ نَّارٍۙ وَّنُحَاسٌ فَلَا تَنْتَصِرَانِۚ ","bc":"yursalu 'alaikumā syuwāẓum min nāriw wa nuḥāsun fa lā tantaṣirān","at":"Kepada kamu (jin dan manusia), akan dikirim nyala api dan cairan tembaga (panas) sehingga kamu tidak dapat menyelamatkan diri (darinya)."},{"id":"36","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"37","ar":"فَاِذَا انْشَقَّتِ السَّمَاۤءُ فَكَانَتْ وَرْدَةً كَالدِّهَانِۚ ","bc":"fa iżansyaqqatis-samā`u fa kānat wardatang kad-dihān","at":"Maka apabila langit telah terbelah dan menjadi merah mawar seperti (kilauan) minyak. "},{"id":"38","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"39","ar":"فَيَوْمَئِذٍ لَّا يُسْـَٔلُ عَنْ ذَنْۢبِهٖٓ اِنْسٌ وَّلَا جَاۤنٌّۚ ","bc":"fa yauma`iżil lā yus`alu 'an żambihī insuw wa lā jānn","at":"Maka pada hari itu manusia dan jin tidak ditanya tentang dosanya. "},{"id":"40","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"41","ar":"يُعْرَفُ الْمُجْرِمُوْنَ بِسِيْمٰهُمْ فَيُؤْخَذُ بِالنَّوَاصِيْ وَالْاَقْدَامِۚ ","bc":"yu'raful-mujrimụna bisīmāhum fa yu`khażu bin-nawāṣī wal-aqdām","at":"Orang-orang yang berdosa itu diketahui dengan tanda-tandanya, lalu direnggut ubun-ubun dan kakinya."},{"id":"42","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"43","ar":"هٰذِهٖ جَهَنَّمُ الَّتِيْ يُكَذِّبُ بِهَا الْمُجْرِمُوْنَۘ ","bc":"hāżihī jahannamullatī yukażżibu bihal-mujrimụn","at":"Inilah neraka Jahanam yang didustakan oleh orang-orang yang berdosa."},{"id":"44","ar":"يَطُوْفُوْنَ بَيْنَهَا وَبَيْنَ حَمِيْمٍ اٰنٍۚ ","bc":"yaṭụfụna bainahā wa baina ḥamīmin ān","at":"Mereka berkeliling di sana dan di antara air yang mendidih. "},{"id":"45","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"46","ar":"وَلِمَنْ خَافَ مَقَامَ رَبِّهٖ جَنَّتٰنِۚ","bc":"wa liman khāfa maqāma rabbihī jannatān","at":"Dan bagi siapa yang takut akan saat menghadap Tuhannya ada dua surga."},{"id":"47","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۙ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"48","ar":"ذَوَاتَآ اَفْنَانٍۚ ","bc":"żawātā afnān","at":"kedua surga itu mempunyai aneka pepohonan dan buah-buahan."},{"id":"49","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"50","ar":"فِيْهِمَا عَيْنٰنِ تَجْرِيٰنِۚ ","bc":"fīhimā 'aināni tajriyān","at":"Di dalam kedua surga itu ada dua buah mata air yang memancar."},{"id":"51","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"52","ar":"فِيْهِمَا مِنْ كُلِّ فَاكِهَةٍ زَوْجٰنِۚ ","bc":"fīhimā ming kulli fākihatin zaujān","at":"Di dalam kedua surga itu terdapat aneka buah-buahan yang berpasang-pasangan. "},{"id":"53","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"54","ar":"مُتَّكِـِٕيْنَ عَلٰى فُرُشٍۢ بَطَاۤىِٕنُهَا مِنْ اِسْتَبْرَقٍۗ وَجَنَا الْجَنَّتَيْنِ دَانٍۚ ","bc":"muttaki`īna 'alā furusyim baṭā`inuhā min istabraq, wa janal-jannataini dān","at":"Mereka bersandar di atas permadani yang bagian dalamnya dari sutera tebal. Dan buah-buahan di kedua surga itu dapat (dipetik) dari dekat. "},{"id":"55","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"56","ar":"فِيْهِنَّ قٰصِرٰتُ الطَّرْفِۙ لَمْ يَطْمِثْهُنَّ اِنْسٌ قَبْلَهُمْ وَلَا جَاۤنٌّۚ ","bc":"fīhinna qāṣirātuṭ-ṭarfi lam yaṭmiṡ-hunna insung qablahum wa lā jānn","at":"Di dalam surga itu ada bidadari-bidadari yang membatasi pandangan, yang tidak pernah disentuh oleh manusia maupun jin sebelumnya. "},{"id":"57","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"58","ar":"كَاَنَّهُنَّ الْيَاقُوْتُ وَالْمَرْجَانُۚ ","bc":"ka`annahunnal-yāqụtu wal-marjān","at":"Seakan-akan mereka itu permata yakut dan marjan. "},{"id":"59","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"60","ar":"هَلْ جَزَاۤءُ الْاِحْسَانِ اِلَّا الْاِحْسَانُۚ ","bc":"hal jazā`ul-iḥsāni illal-iḥsān","at":"Tidak ada balasan untuk kebaikan selain kebaikan (pula)."},{"id":"61","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"62","ar":"وَمِنْ دُوْنِهِمَا جَنَّتٰنِۚ ","bc":"wa min dụnihimā jannatān","at":"Dan selain dari dua surga itu ada dua surga lagi."},{"id":"63","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۙ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan,"},{"id":"64","ar":"مُدْهَاۤمَّتٰنِۚ ","bc":"mud-hāmmatān","at":"kedua surga itu (kelihatan) hijau tua warnanya."},{"id":"65","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"66","ar":"فِيْهِمَا عَيْنٰنِ نَضَّاخَتٰنِۚ ","bc":"fīhimā 'aināni naḍḍākhatān","at":"Di dalam keduanya (surga itu) ada dua buah mata air yang memancar."},{"id":"67","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"68","ar":"فِيْهِمَا فَاكِهَةٌ وَّنَخْلٌ وَّرُمَّانٌۚ ","bc":"fīhimā fākihatuw wa nakhluw wa rummān","at":"Di dalam kedua surga itu ada buah-buahan, kurma dan delima."},{"id":"69","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"70","ar":"فِيْهِنَّ خَيْرٰتٌ حِسَانٌۚ ","bc":"fīhinna khairātun ḥisān","at":"Di dalam surga-surga itu ada bidadari-bidadari yang baik-baik dan jelita."},{"id":"71","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"72","ar":"حُوْرٌ مَّقْصُوْرٰتٌ فِى الْخِيَامِۚ ","bc":"ḥụrum maqṣụrātun fil-khiyām","at":"Bidadari-bidadari yang dipelihara di dalam kemah-kemah."},{"id":"73","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"74","ar":"لَمْ يَطْمِثْهُنَّ اِنْسٌ قَبْلَهُمْ وَلَا جَاۤنٌّۚ ","bc":"lam yaṭmiṡ-hunna insung qablahum wa lā jānn","at":"Mereka tidak pernah disentuh oleh manusia maupun oleh jin sebelumnya."},{"id":"75","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"76","ar":"مُتَّكِـِٕيْنَ عَلٰى رَفْرَفٍ خُضْرٍ وَّعَبْقَرِيٍّ حِسَانٍۚ ","bc":"muttaki`īna 'alā rafrafin khuḍriw wa 'abqariyyin ḥisān","at":"Mereka bersandar pada bantal-bantal yang hijau dan permadani-permadani yang indah."},{"id":"77","ar":"فَبِاَيِّ اٰلَاۤءِ رَبِّكُمَا تُكَذِّبٰنِۚ ","bc":"fa bi`ayyi ālā`i rabbikumā tukażżibān","at":"Maka nikmat Tuhanmu yang manakah yang kamu dustakan?"},{"id":"78","ar":"تَبٰرَكَ اسْمُ رَبِّكَ ذِى الْجَلٰلِ وَالْاِكْرَامِ ","bc":"tabārakasmu rabbika żil-jalāli wal-ikrām","at":"Mahasuci nama Tuhanmu Pemilik Keagungan dan Kemuliaan."}],ar_rum=[{"id":"1","ar":" الۤمّۤ ۚ ","bc":"alif lām mīm","at":"Alif Lam Mim."},{"id":"2","ar":"غُلِبَتِ الرُّوْمُۙ ","bc":"gulibatir-rụm","at":"Bangsa Romawi telah dikalahkan,"},{"id":"3","ar":"فِيْٓ اَدْنَى الْاَرْضِ وَهُمْ مِّنْۢ بَعْدِ غَلَبِهِمْ سَيَغْلِبُوْنَۙ ","bc":"fī adnal-arḍi wa hum mim ba'di galabihim sayaglibụn","at":"di negeri yang terdekat dan mereka setelah kekalahannya itu akan menang, "},{"id":"4","ar":"فِيْ بِضْعِ سِنِيْنَ ەۗ لِلّٰهِ الْاَمْرُ مِنْ قَبْلُ وَمِنْۢ بَعْدُ ۗوَيَوْمَىِٕذٍ يَّفْرَحُ الْمُؤْمِنُوْنَۙ","bc":"fī biḍ'i sinīn, lillāhil-amru ming qablu wa mim ba'd, wa yauma`iżiy yafraḥul-mu`minụn","at":"dalam beberapa tahun (lagi). Bagi Allah-lah urusan sebelum dan setelah (mereka menang). Dan pada hari (kemenangan bangsa Romawi) itu bergembiralah orang-orang yang beriman, "},{"id":"5","ar":"بِنَصْرِ اللّٰهِ ۗيَنْصُرُ مَنْ يَّشَاۤءُۗ وَهُوَ الْعَزِيْزُ الرَّحِيْمُ","bc":"binaṣrillāh, yanṣuru may yasyā`, wa huwal-'azīzur-raḥīm","at":"karena pertolongan Allah. Dia menolong siapa yang Dia kehendaki. Dia Mahaperkasa, Maha Penyayang."},{"id":"6","ar":"وَعْدَ اللّٰهِ ۗ لَا يُخْلِفُ اللّٰهُ وَعْدَهٗ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ","bc":"wa'dallāh, lā yukhlifullāhu wa'dahụ wa lākinna akṡaran-nāsi lā ya'lamụn","at":"(Itulah) janji Allah. Allah tidak akan menyalahi janji-Nya, tetapi kebanyakan manusia tidak mengetahui. "},{"id":"7","ar":"يَعْلَمُوْنَ ظَاهِرًا مِّنَ الْحَيٰوةِ الدُّنْيَاۖ وَهُمْ عَنِ الْاٰخِرَةِ هُمْ غٰفِلُوْنَ","bc":"ya'lamụna ẓāhiram minal-ḥayātid-dun-yā wa hum 'anil-ākhirati hum gāfilụn","at":"Mereka mengetahui yang lahir (tampak) dari kehidupan dunia; sedangkan terhadap (kehidupan) akhirat mereka lalai. "},{"id":"8","ar":"اَوَلَمْ يَتَفَكَّرُوْا فِيْٓ اَنْفُسِهِمْ ۗ مَا خَلَقَ اللّٰهُ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَآ اِلَّا بِالْحَقِّ وَاَجَلٍ مُّسَمًّىۗ وَاِنَّ كَثِيْرًا مِّنَ النَّاسِ بِلِقَاۤئِ رَبِّهِمْ لَكٰفِرُوْنَ","bc":"a wa lam yatafakkarụ fī anfusihim, mā khalaqallāhus-samāwāti wal-arḍa wa mā bainahumā illā bil-ḥaqqi wa ajalim musammā, wa inna kaṡīram minan-nāsi biliqā`i rabbihim lakāfirụn","at":"Dan mengapa mereka tidak memikirkan tentang (kejadian) diri mereka? Allah tidak menciptakan langit dan bumi dan apa yang ada di antara keduanya melainkan dengan (tujuan) yang benar dan dalam waktu yang ditentukan. Dan sesungguhnya kebanyakan di antara manusia benar-benar mengingkari pertemuan dengan Tuhannya. "},{"id":"9","ar":"اَوَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْۗ كَانُوْٓا اَشَدَّ مِنْهُمْ قُوَّةً وَّاَثَارُوا الْاَرْضَ وَعَمَرُوْهَآ اَكْثَرَ مِمَّا عَمَرُوْهَا وَجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۗ فَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَۗ","bc":"a wa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna 'āqibatullażīna ming qablihim, kānū asyadda min-hum quwwataw wa aṡārul-arḍa wa 'amarụhā akṡara mimmā 'amarụhā wa jā`at-hum rusuluhum bil-bayyināt, fa mā kānallāhu liyaẓlimahum wa lāking kānū anfusahum yaẓlimụn","at":"Dan tidakkah mereka bepergian di bumi lalu melihat bagaimana kesudahan orang-orang sebelum mereka (yang mendustakan rasul)? Orang-orang itu lebih kuat dari mereka (sendiri) dan mereka telah mengolah bumi (tanah) serta memakmurkannya melebihi apa yang telah mereka makmurkan. Dan telah datang kepada mereka rasul-rasul mereka dengan membawa bukti-bukti yang jelas. Maka Allah sama sekali tidak berlaku zalim kepada mereka, tetapi merekalah yang berlaku zalim kepada diri mereka sendiri. "},{"id":"10","ar":"ثُمَّ كَانَ عَاقِبَةَ الَّذِيْنَ اَسَاۤءُوا السُّوْۤاٰىٓ اَنْ كَذَّبُوْا بِاٰيٰتِ اللّٰهِ وَكَانُوْا بِهَا يَسْتَهْزِءُوْنَ ","bc":"ṡumma kāna 'āqibatallażīna asā`us-sū`ā ang każżabụ bi`āyātillāhi wa kānụ bihā yastahzi`ụn","at":"Kemudian, azab yang lebih buruk adalah kesudahan bagi orang-orang yang mengerjakan kejahatan. Karena mereka mendustakan ayat-ayat Allah dan mereka selalu memperolok-olokkannya. "},{"id":"11","ar":"اَللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ ثُمَّ اِلَيْهِ تُرْجَعُوْنَ","bc":"allāhu yabda`ul-khalqa ṡumma yu'īduhụ ṡumma ilaihi turja'ụn","at":"Allah yang memulai penciptaan (makhluk), kemudian mengulanginya kembali; kemudian kepada-Nya kamu dikembalikan."},{"id":"12","ar":"وَيَوْمَ تَقُوْمُ السَّاعَةُ يُبْلِسُ الْمُجْرِمُوْنَ","bc":"wa yauma taqụmus-sā'atu yublisul-mujrimụn","at":"Dan pada hari (ketika) terjadi Kiamat, orang-orang yang berdosa (kaum musyrik) terdiam berputus asa. "},{"id":"13","ar":"وَلَمْ يَكُنْ لَّهُمْ مِّنْ شُرَكَاۤىِٕهِمْ شُفَعٰۤؤُا وَكَانُوْا بِشُرَكَاۤىِٕهِمْ كٰفِرِيْنَ","bc":"wa lam yakul lahum min syurakā`ihim syufa'ā`u wa kānụ bisyurakā`ihim kāfirīn","at":"Dan tidak mungkin ada pemberi syafaat (pertolongan) bagi mereka dari berhala-berhala mereka, sedangkan mereka mengingkari berhala-berhala mereka itu. "},{"id":"14","ar":"وَيَوْمَ تَقُوْمُ السَّاعَةُ يَوْمَىِٕذٍ يَّتَفَرَّقُوْنَ ","bc":"wa yauma taqụmus-sā'atu yauma`iżiy yatafarraqụn","at":"Dan pada hari (ketika) terjadi Kiamat, pada hari itu manusia terpecah-pecah (dalam kelompok). "},{"id":"15","ar":"فَاَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَهُمْ فِيْ رَوْضَةٍ يُّحْبَرُوْنَ","bc":"fa ammallażīna āmanụ wa 'amiluṣ-ṣāliḥāti fa hum fī rauḍatiy yuḥbarụn","at":"Maka adapun orang-orang yang beriman dan mengerjakan kebajikan, maka mereka di dalam taman (surga) bergembira. "},{"id":"16","ar":"وَاَمَّا الَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَا وَلِقَاۤئِ الْاٰخِرَةِ فَاُولٰۤىِٕكَ فِى الْعَذَابِ مُحْضَرُوْنَ","bc":"wa ammallażīna kafarụ wa każżabụ bi`āyātinā wa liqā`il-ākhirati fa ulā`ika fil-'ażābi muḥḍarụn","at":"Dan adapun orang-orang yang kafir dan mendustakan ayat-ayat Kami serta (mendustakan) pertemuan hari akhirat, maka mereka tetap berada di dalam azab (neraka). "},{"id":"17","ar":"فَسُبْحٰنَ اللّٰهِ حِيْنَ تُمْسُوْنَ وَحِيْنَ تُصْبِحُوْنَ","bc":"fa sub-ḥānallāhi ḥīna tumsụna wa ḥīna tuṣbiḥụn","at":"Maka bertasbihlah kepada Allah pada petang hari dan pada pagi hari (waktu subuh), "},{"id":"18","ar":"وَلَهُ الْحَمْدُ فِى السَّمٰوٰتِ وَالْاَرْضِ وَعَشِيًّا وَّحِيْنَ تُظْهِرُوْنَ","bc":"wa lahul-ḥamdu fis-samāwāti wal-arḍi wa 'asyiyyaw wa ḥīna tuẓ-hirụn","at":"dan segala puji bagi-Nya baik di langit, di bumi, pada malam hari dan pada waktu zuhur (tengah hari). "},{"id":"19","ar":"يُخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَيُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَا ۗوَكَذٰلِكَ تُخْرَجُوْنَ","bc":"yukhrijul-ḥayya minal-mayyiti wa yukhrijul-mayyita minal-ḥayyi wa yuḥyil-arḍa ba'da mautihā, wa każālika tukhrajụn","at":"Dia mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup dan menghidupkan bumi setelah mati (kering). Dan seperti itulah kamu akan dikeluarkan (dari kubur). "},{"id":"20","ar":"وَمِنْ اٰيٰتِهٖٓ اَنْ خَلَقَكُمْ مِّنْ تُرَابٍ ثُمَّ اِذَآ اَنْتُمْ بَشَرٌ تَنْتَشِرُوْنَ","bc":"wa min āyātihī an khalaqakum min turābin ṡumma iżā antum basyarun tantasyirụn","at":"Dan di antara tanda-tanda (kebesaran)-Nya ialah Dia menciptakan kamu dari tanah, kemudian tiba-tiba kamu (menjadi) manusia yang berkembang biak. "},{"id":"21","ar":"وَمِنْ اٰيٰتِهٖٓ اَنْ خَلَقَ لَكُمْ مِّنْ اَنْفُسِكُمْ اَزْوَاجًا لِّتَسْكُنُوْٓا اِلَيْهَا وَجَعَلَ بَيْنَكُمْ مَّوَدَّةً وَّرَحْمَةً ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ","bc":"wa min āyātihī an khalaqa lakum min anfusikum azwājal litaskunū ilaihā wa ja'ala bainakum mawaddataw wa raḥmah, inna fī żālika la`āyātil liqaumiy yatafakkarụn","at":"Dan di antara tanda-tanda (kebesaran)-Nya ialah Dia menciptakan pasangan-pasangan untukmu dari jenismu sendiri, agar kamu cenderung dan merasa tenteram kepadanya, dan Dia menjadikan di antaramu rasa kasih dan sayang. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Allah) bagi kaum yang berpikir. "},{"id":"22","ar":"وَمِنْ اٰيٰتِهٖ خَلْقُ السَّمٰوٰتِ وَالْاَرْضِ وَاخْتِلَافُ اَلْسِنَتِكُمْ وَاَلْوَانِكُمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّلْعٰلِمِيْنَ ","bc":"wa min āyātihī khalqus-samāwāti wal-arḍi wakhtilāfu alsinatikum wa alwānikum, inna fī żālika la`āyātil lil-'ālimīn","at":"Dan di antara tanda-tanda (kebesaran)-Nya ialah penciptaan langit dan bumi, perbedaan bahasamu dan warna kulitmu. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda bagi orang-orang yang mengetahui. "},{"id":"23","ar":"وَمِنْ اٰيٰتِهٖ مَنَامُكُمْ بِالَّيْلِ وَالنَّهَارِ وَابْتِغَاۤؤُكُمْ مِّنْ فَضْلِهٖۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّسْمَعُوْنَ","bc":"wa min āyātihī manāmukum bil-laili wan-nahāri wabtigā`ukum min faḍlih, inna fī żālika la`āyātil liqaumiy yasma'ụn","at":"Dan di antara tanda-tanda (kebesaran)-Nya ialah tidurmu pada waktu malam dan siang hari dan usahamu mencari sebagian dari karunia-Nya. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda bagi kaum yang mendengarkan. "},{"id":"24","ar":"وَمِنْ اٰيٰتِهٖ يُرِيْكُمُ الْبَرْقَ خَوْفًا وَّطَمَعًا وَّيُنَزِّلُ مِنَ السَّمَاۤءِ مَاۤءً فَيُحْيٖ بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّعْقِلُوْنَ","bc":"wa min āyātihī yurīkumul-barqa khaufaw wa ṭama'aw wa yunazzilu minas-samā`i mā`an fa yuḥyī bihil-arḍa ba'da mautihā, inna fī żālika la`āyātil liqaumiy ya'qilụn","at":"Dan di antara tanda-tanda (kebesaran)-Nya, Dia memperlihatkan kilat kepadamu untuk (menimbulkan) ketakutan dan harapan, dan Dia menurunkan air (hujan) dari langit, lalu dengan air itu dihidupkannya bumi setelah mati (kering). Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda bagi kaum yang mengerti. "},{"id":"25","ar":"وَمِنْ اٰيٰتِهٖٓ اَنْ تَقُوْمَ السَّمَاۤءُ وَالْاَرْضُ بِاَمْرِهٖۗ ثُمَّ اِذَا دَعَاكُمْ دَعْوَةًۖ مِّنَ الْاَرْضِ اِذَآ اَنْتُمْ تَخْرُجُوْنَ","bc":"wa min āyātihī an taqụmas-samā`u wal-arḍu bi`amrih, ṡumma iżā da'ākum da'watam minal-arḍi iżā antum takhrujụn","at":"Dan di antara tanda-tanda (kebesaran)-Nya ialah berdirinya langit dan bumi dengan kehendak-Nya. Kemudian apabila Dia memanggil kamu sekali panggil dari bumi, seketika itu kamu keluar (dari kubur). "},{"id":"26","ar":"وَلَهٗ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِۗ كُلٌّ لَّهٗ قَانِتُوْنَ","bc":"wa lahụ man fis-samāwāti wal-arḍ, kullul lahụ qānitụn","at":"Dan milik-Nya apa yang di langit dan di bumi. Semuanya hanya kepada-Nya tunduk. "},{"id":"27","ar":"وَهُوَ الَّذِيْ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ وَهُوَ اَهْوَنُ عَلَيْهِۗ وَلَهُ الْمَثَلُ الْاَعْلٰى فِى السَّمٰوٰتِ وَالْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ","bc":"wa huwallażī yabda`ul-khalqa ṡumma yu'īduhụ wa huwa ahwanu 'alaīh, wa lahul-maṡalul-a'lā fis-samāwāti wal-arḍ, wa huwal-'azīzul-ḥakīm","at":"Dan Dialah yang memulai penciptaan, kemudian mengulanginya kembali, dan itu lebih mudah bagi-Nya. Dia memiliki sifat yang Mahatinggi di langit dan di bumi. Dan Dialah Yang Mahaperkasa, Mahabijaksana. "},{"id":"28","ar":"ضَرَبَ لَكُمْ مَّثَلًا مِّنْ اَنْفُسِكُمْۗ هَلْ لَّكُمْ مِّنْ مَّا مَلَكَتْ اَيْمَانُكُمْ مِّنْ شُرَكَاۤءَ فِيْ مَا رَزَقْنٰكُمْ فَاَنْتُمْ فِيْهِ سَوَاۤءٌ تَخَافُوْنَهُمْ كَخِيْفَتِكُمْ اَنْفُسَكُمْۗ كَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْقِلُوْنَ","bc":"ḍaraba lakum maṡalam min anfusikum, hal lakum mimmā malakat aimānukum min syurakā`a fī mā razaqnākum fa antum fīhi sawā`un takhāfụnahum kakhīfatikum anfusakum, każālika nufaṣṣilul-āyāti liqaumiy ya'qilụn","at":"Dia membuat perumpamaan bagimu dari dirimu sendiri. Apakah (kamu rela jika) ada di antara hamba sahaya yang kamu miliki, menjadi sekutu bagimu dalam (memiliki) rezeki yang telah Kami berikan kepadamu, sehingga kamu menjadi setara dengan mereka dalam hal ini, lalu kamu takut kepada mereka sebagaimana kamu takut kepada sesamamu. Demikianlah Kami jelaskan ayat-ayat itu bagi kaum yang mengerti."},{"id":"29","ar":"بَلِ اتَّبَعَ الَّذِيْنَ ظَلَمُوْٓا اَهْوَاۤءَهُمْ بِغَيْرِ عِلْمٍۗ فَمَنْ يَّهْدِيْ مَنْ اَضَلَّ اللّٰهُ ۗوَمَا لَهُمْ مِّنْ نّٰصِرِيْنَ","bc":"balittaba'allażīna ẓalamū ahwā`ahum bigairi 'ilm, fa may yahdī man aḍallallāh, wa mā lahum min nāṣirīn","at":"Tetapi orang-orang yang zalim, mengikuti keinginannya tanpa ilmu pengetahuan; maka siapakah yang dapat memberi petunjuk kepada orang yang telah disesatkan Allah. Dan tidak ada seorang penolong pun bagi mereka. "},{"id":"30","ar":"فَاَقِمْ وَجْهَكَ لِلدِّيْنِ حَنِيْفًاۗ فِطْرَتَ اللّٰهِ الَّتِيْ فَطَرَ النَّاسَ عَلَيْهَاۗ لَا تَبْدِيْلَ لِخَلْقِ اللّٰهِ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُۙ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَۙ ","bc":"fa aqim waj-haka lid-dīni ḥanīfā, fiṭratallāhillatī faṭaran-nāsa 'alaihā, lā tabdīla likhalqillāh, żālikad-dīnul qayyimu wa lākinna akṡaran-nāsi lā ya'lamụn","at":"Maka hadapkanlah wajahmu dengan lurus kepada agama (Islam); (sesuai) fitrah Allah disebabkan Dia telah menciptakan manusia menurut (fitrah) itu. Tidak ada perubahan pada ciptaan Allah. (Itulah) agama yang lurus, tetapi kebanyakan manusia tidak mengetahui,"},{"id":"31","ar":"۞ مُنِيْبِيْنَ اِلَيْهِ وَاتَّقُوْهُ وَاَقِيْمُوا الصَّلٰوةَ وَلَا تَكُوْنُوْا مِنَ الْمُشْرِكِيْنَۙ","bc":"munībīna ilaihi wattaqụhu wa aqīmuṣ-ṣalāta wa lā takụnụ minal-musyrikīn","at":"dengan kembali bertobat kepada-Nya dan bertakwalah kepada-Nya serta laksanakanlah salat dan janganlah kamu termasuk orang-orang yang mempersekutukan Allah, "},{"id":"32","ar":"مِنَ الَّذِيْنَ فَرَّقُوْا دِيْنَهُمْ وَكَانُوْا شِيَعًا ۗ كُلُّ حِزْبٍۢ بِمَا لَدَيْهِمْ فَرِحُوْنَ","bc":"minallażīna farraqụ dīnahum wa kānụ syiya'ā, kullu ḥizbim bimā ladaihim fariḥụn","at":"yaitu orang-orang yang memecah belah agama mereka dan mereka menjadi beberapa golongan. Setiap golongan merasa bangga dengan apa yang ada pada golongan mereka. "},{"id":"33","ar":"وَاِذَا مَسَّ النَّاسَ ضُرٌّ دَعَوْا رَبَّهُمْ مُّنِيْبِيْنَ اِلَيْهِ ثُمَّ اِذَآ اَذَاقَهُمْ مِّنْهُ رَحْمَةً اِذَا فَرِيْقٌ مِّنْهُمْ بِرَبِّهِمْ يُشْرِكُوْنَۙ","bc":"wa iżā massan-nāsa ḍurrun da'au rabbahum munībīna ilaihi ṡumma iżā ażāqahum min-hu raḥmatan iżā farīqum min-hum birabbihim yusyrikụn","at":"Dan apabila manusia ditimpa oleh suatu bahaya, mereka menyeru Tuhannya dengan kembali (bertobat) kepada-Nya, kemudian apabila Dia memberikan sedikit rahmat-Nya kepada mereka, tiba-tiba sebagian mereka mempersekutukan Allah."},{"id":"34","ar":"لِيَكْفُرُوْا بِمَآ اٰتَيْنٰهُمْۗ فَتَمَتَّعُوْاۗ فَسَوْفَ تَعْلَمُوْنَ","bc":"liyakfurụ bimā ātaināhum, fa tamatta'ụ, fa saufa ta'lamụn","at":"Biarkan mereka mengingkari rahmat yang telah Kami berikan. Dan bersenang-senanglah kamu, maka kelak kamu akan mengetahui (akibat perbuatanmu). "},{"id":"35","ar":"اَمْ اَنْزَلْنَا عَلَيْهِمْ سُلْطٰنًا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوْا بِهٖ يُشْرِكُوْنَ ","bc":"am anzalnā 'alaihim sulṭānan fa huwa yatakallamu bimā kānụ bihī yusyrikụn","at":"Atau pernahkah Kami menurunkan kepada mereka keterangan, yang menjelaskan (membenarkan) apa yang (selalu) mereka persekutukan dengan Tuhan? "},{"id":"36","ar":"وَاِذَآ اَذَقْنَا النَّاسَ رَحْمَةً فَرِحُوْا بِهَاۗ وَاِنْ تُصِبْهُمْ سَيِّئَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ اِذَا هُمْ يَقْنَطُوْنَ","bc":"wa iżā ażaqnan-nāsa raḥmatan fariḥụ bihā, wa in tuṣib-hum sayyi`atum bimā qaddamat aidīhim iżā hum yaqnaṭụn","at":"Dan apabila Kami berikan sesuatu rahmat kepada manusia, niscaya mereka gembira dengan (rahmat) itu. Tetapi apabila mereka ditimpa sesuatu musibah (bahaya) karena kesalahan mereka sendiri, seketika itu mereka berputus asa. "},{"id":"37","ar":"اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ","bc":"a wa lam yarau annallāha yabsuṭur-rizqa limay yasyā`u wa yaqdir, inna fī żālika la`āyātil liqaumiy yu`minụn","at":"Dan tidakkah mereka memperhatikan bahwa Allah yang melapangkan rezeki bagi siapa yang Dia kehendaki dan Dia (pula) yang membatasi (bagi siapa yang Dia kehendaki). Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kebesaran Allah) bagi kaum yang beriman. "},{"id":"38","ar":"فَاٰتِ ذَا الْقُرْبٰى حَقَّهٗ وَالْمِسْكِيْنَ وَابْنَ السَّبِيْلِۗ ذٰلِكَ خَيْرٌ لِّلَّذِيْنَ يُرِيْدُوْنَ وَجْهَ اللّٰهِ ۖوَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ","bc":"fa āti żal-qurbā ḥaqqahụ wal-miskīna wabnas-sabīl, żālika khairul lillażīna yurīdụna waj-hallāhi wa ulā`ika humul-mufliḥụn","at":"Maka berikanlah haknya kepada kerabat dekat, juga kepada orang miskin dan orang-orang yang dalam perjalanan. Itulah yang lebih baik bagi orang-orang yang mencari keridaan Allah. Dan mereka itulah orang-orang yang beruntung. "},{"id":"39","ar":"وَمَآ اٰتَيْتُمْ مِّنْ رِّبًا لِّيَرْبُوَا۠ فِيْٓ اَمْوَالِ النَّاسِ فَلَا يَرْبُوْا عِنْدَ اللّٰهِ ۚوَمَآ اٰتَيْتُمْ مِّنْ زَكٰوةٍ تُرِيْدُوْنَ وَجْهَ اللّٰهِ فَاُولٰۤىِٕكَ هُمُ الْمُضْعِفُوْنَ","bc":"wa mā ātaitum mir ribal liyarbuwa fī amwālin-nāsi fa lā yarbụ 'indallāh, wa mā ātaitum min zakātin turīdụna waj-hallāhi fa ulā`ika humul-muḍ'ifụn","at":"Dan sesuatu riba (tambahan) yang kamu berikan agar harta manusia bertambah, maka tidak bertambah dalam pandangan Allah. Dan apa yang kamu berikan berupa zakat yang kamu maksudkan untuk memperoleh keridaan Allah, maka itulah orang-orang yang melipatgandakan (pahalanya). "},{"id":"40","ar":"اَللّٰهُ الَّذِيْ خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُمِيْتُكُمْ ثُمَّ يُحْيِيْكُمْۗ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّفْعَلُ مِنْ ذٰلِكُمْ مِّنْ شَيْءٍۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ","bc":"allāhullażī khalaqakum ṡumma razaqakum ṡumma yumītukum ṡumma yuḥyīkum, hal min syurakā`ikum may yaf'alu min żālikum min syaī`, sub-ḥānahụ wa ta'ālā 'ammā yusyrikụn","at":"Allah yang menciptakan kamu, kemudian memberimu rezeki, lalu mematikanmu, kemudian menghidupkanmu (kembali). Adakah di antara mereka yang kamu sekutukan dengan Allah itu yang dapat berbuat sesuatu yang demikian itu? Mahasuci Dia dan Mahatinggi dari apa yang mereka persekutukan. "},{"id":"41","ar":"ظَهَرَ الْفَسَادُ فِى الْبَرِّ وَالْبَحْرِ بِمَا كَسَبَتْ اَيْدِى النَّاسِ لِيُذِيْقَهُمْ بَعْضَ الَّذِيْ عَمِلُوْا لَعَلَّهُمْ يَرْجِعُوْنَ","bc":"ẓaharal-fasādu fil-barri wal-baḥri bimā kasabat aidin-nāsi liyużīqahum ba'ḍallażī 'amilụ la'allahum yarji'ụn","at":"Telah tampak kerusakan di darat dan di laut disebabkan karena perbuatan tangan manusia; Allah menghendaki agar mereka merasakan sebagian dari (akibat) perbuatan mereka, agar mereka kembali (ke jalan yang benar). "},{"id":"42","ar":"قُلْ سِيْرُوْا فِى الْاَرْضِ فَانْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلُۗ كَانَ اَكْثَرُهُمْ مُّشْرِكِيْنَ","bc":"qul sīrụ fil-arḍi fanẓurụ kaifa kāna 'āqibatullażīna ming qabl, kāna akṡaruhum musyrikīn","at":"Katakanlah (Muhammad), “Bepergianlah di bumi lalu lihatlah bagaimana kesudahan orang-orang dahulu. Kebanyakan dari mereka adalah orang-orang yang mempersekutukan (Allah).”"},{"id":"43","ar":"فَاَقِمْ وَجْهَكَ لِلدِّيْنِ الْقَيِّمِ مِنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا مَرَدَّ لَهٗ مِنَ اللّٰهِ يَوْمَىِٕذٍ يَّصَّدَّعُوْنَ","bc":"fa aqim waj-haka liddīnil-qayyimi ming qabli ay ya`tiya yaumul lā maradda lahụ minallāhi yauma`iżiy yaṣṣadda'ụn","at":"Oleh karena itu, hadapkanlah wajahmu kepada agama yang lurus (Islam) sebelum datang dari Allah suatu hari (Kiamat) yang tidak dapat ditolak, pada hari itu mereka terpisah-pisah."},{"id":"44","ar":"مَنْ كَفَرَ فَعَلَيْهِ كُفْرُهٗۚ وَمَنْ عَمِلَ صَالِحًا فَلِاَنْفُسِهِمْ يَمْهَدُوْنَۙ","bc":"mang kafara fa 'alaihi kufruh, wa man 'amila ṣāliḥan fa li`anfusihim yam-hadụn","at":"Barangsiapa kafir maka dia sendirilah yang menanggung (akibat) kekafirannya itu; dan barangsiapa mengerjakan kebajikan maka mereka menyiapkan untuk diri mereka sendiri (tempat yang menyenangkan), "},{"id":"45","ar":"لِيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ مِنْ فَضْلِهٖۗ اِنَّهٗ لَا يُحِبُّ الْكٰفِرِيْنَ","bc":"liyajziyallażīna āmanụ wa 'amiluṣ-ṣāliḥāti min faḍlih, innahụ lā yuḥibbul-kāfirīn","at":"agar Allah memberi balasan (pahala) kepada orang-orang yang beriman dan mengerjakan kebajikan dari karunia-Nya. Sungguh, Dia tidak menyukai orang-orang yang ingkar (kafir). "},{"id":"46","ar":"وَمِنْ اٰيٰتِهٖٓ اَنْ يُّرْسِلَ الرِّيٰحَ مُبَشِّرٰتٍ وَّلِيُذِيْقَكُمْ مِّنْ رَّحْمَتِهٖ وَلِتَجْرِيَ الْفُلْكُ بِاَمْرِهٖ وَلِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ","bc":"wa min āyātihī ay yursilar-riyāḥa mubasysyirātiw wa liyużīqakum mir raḥmatihī wa litajriyal-fulku bi`amrihī wa litabtagụ min faḍlihī wa la'allakum tasykurụn","at":"Dan di antara tanda-tanda (kebesaran)-Nya adalah bahwa Dia mengirimkan angin sebagai pembawa berita gembira dan agar kamu merasakan sebagian dari rahmat-Nya dan agar kapal dapat berlayar dengan perintah-Nya dan (juga) agar kamu dapat mencari sebagian dari karunia-Nya, dan agar kamu bersyukur."},{"id":"47","ar":"وَلَقَدْ اَرْسَلْنَا مِنْ قَبْلِكَ رُسُلًا اِلٰى قَوْمِهِمْ فَجَاۤءُوْهُمْ بِالْبَيِّنٰتِ فَانْتَقَمْنَا مِنَ الَّذِيْنَ اَجْرَمُوْاۗ وَكَانَ حَقًّاۖ عَلَيْنَا نَصْرُ الْمُؤْمِنِيْنَ","bc":"wa laqad arsalnā ming qablika rusulan ilā qaumihim fa jā`ụhum bil-bayyināti fantaqamnā minallażīna ajramụ, wa kāna ḥaqqan 'alainā naṣrul-mu`minīn","at":"Dan sungguh, Kami telah mengutus sebelum engkau (Muhammad) beberapa orang rasul kepada kaumnya, mereka datang kepadanya dengan membawa keterangan-keterangan (yang cukup), lalu Kami melakukan pembalasan terhadap orang-orang yang berdosa. Dan merupakan hak Kami untuk menolong orang-orang yang beriman. "},{"id":"48","ar":"اَللّٰهُ الَّذِيْ يُرْسِلُ الرِّيٰحَ فَتُثِيْرُ سَحَابًا فَيَبْسُطُهٗ فِى السَّمَاۤءِ كَيْفَ يَشَاۤءُ وَيَجْعَلُهٗ كِسَفًا فَتَرَى الْوَدْقَ يَخْرُجُ مِنْ خِلٰلِهٖۚ فَاِذَآ اَصَابَ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖٓ اِذَا هُمْ يَسْتَبْشِرُوْنَۚ","bc":"allāhullażī yursilur-riyāḥa fa tuṡīru saḥāban fa yabsuṭuhụ fis-samā`i kaifa yasyā`u wa yaj'aluhụ kisafan fa taral-wadqa yakhruju min khilālih, fa iżā aṣāba bihī may yasyā`u min 'ibādihī iżā hum yastabsyirụn","at":"Allah-lah yang mengirimkan angin, lalu angin itu menggerakkan awan dan Allah membentangkannya di langit menurut yang Dia kehendaki, dan menjadikannya bergumpal-gumpal, lalu engkau lihat hujan keluar dari celah-celahnya, maka apabila Dia menurunkannya kepada hamba-hamba-Nya yang Dia kehendaki tiba-tiba mereka bergembira. "},{"id":"49","ar":"وَاِنْ كَانُوْا مِنْ قَبْلِ اَنْ يُّنَزَّلَ عَلَيْهِمْ مِّنْ قَبْلِهٖ لَمُبْلِسِيْنَۚ","bc":"wa ing kānụ ming qabli ay yunazzala 'alaihim ming qablihī lamublisīn","at":"Padahal walaupun sebelum hujan diturunkan kepada mereka, mereka benar-benar telah berputus asa. "},{"id":"50","ar":"فَانْظُرْ اِلٰٓى اٰثٰرِ رَحْمَتِ اللّٰهِ كَيْفَ يُحْيِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ اِنَّ ذٰلِكَ لَمُحْيِ الْمَوْتٰىۚ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ","bc":"fanẓur ilā āṡāri raḥmatillāhi kaifa yuḥyil-arḍa ba'da mautihā, inna żālika lamuḥyil mautā, wa huwa 'alā kulli syai`ing qadīr","at":"Maka perhatikanlah bekas-bekas rahmat Allah, bagaimana Allah menghidupkan bumi setelah mati (kering). Sungguh, itu berarti Dia pasti (berkuasa) menghidupkan yang telah mati. Dan Dia Mahakuasa atas segala sesuatu. "},{"id":"51","ar":"وَلَىِٕنْ اَرْسَلْنَا رِيْحًا فَرَاَوْهُ مُصْفَرًّا لَّظَلُّوْا مِنْۢ بَعْدِهٖ يَكْفُرُوْنَ","bc":"wa la`in arsalnā rīḥan fa ra`auhu muṣfarral laẓallụ mim ba'dihī yakfurụn","at":"Dan sungguh, jika Kami mengirimkan angin lalu mereka melihat (tumbuh-tumbuhan itu) menjadi kuning (kering), niscaya setelah itu mereka tetap ingkar. "},{"id":"52","ar":"فَاِنَّكَ لَا تُسْمِعُ الْمَوْتٰى وَلَا تُسْمِعُ الصُّمَّ الدُّعَاۤءَ اِذَا وَلَّوْا مُدْبِرِيْنَ","bc":"fa innaka lā tusmi'ul-mautā wa lā tusmi'uṣ-ṣummad-du'ā`a iżā wallau mudbirīn","at":"Maka sungguh, engkau tidak akan sanggup menjadikan orang-orang yang mati itu dapat mendengar, dan menjadikan orang-orang yang tuli dapat mendengar seruan, apabila mereka berpaling ke belakang. "},{"id":"53","ar":"وَمَآ اَنْتَ بِهٰدِ الْعُمْيِ عَنْ ضَلٰلَتِهِمْۗ اِنْ تُسْمِعُ اِلَّا مَنْ يُّؤْمِنُ بِاٰيٰتِنَا فَهُمْ مُّسْلِمُوْنَ","bc":"wa mā anta bihādil-'umyi 'an ḍalālatihim, in tusmi'u illā may yu`minu bi`āyātinā fa hum muslimụn","at":"Dan engkau tidak akan dapat memberi petunjuk kepada orang-orang yang buta (mata hatinya) dari kesesatannya. Dan engkau tidak dapat memperdengarkan (petunjuk Tuhan) kecuali kepada orang-orang yang beriman dengan ayat-ayat Kami, maka mereka itulah orang-orang yang berserah diri (kepada Kami). "},{"id":"54","ar":"۞ اَللّٰهُ الَّذِيْ خَلَقَكُمْ مِّنْ ضَعْفٍ ثُمَّ جَعَلَ مِنْۢ بَعْدِ ضَعْفٍ قُوَّةً ثُمَّ جَعَلَ مِنْۢ بَعْدِ قُوَّةٍ ضَعْفًا وَّشَيْبَةً ۗيَخْلُقُ مَا يَشَاۤءُۚ وَهُوَ الْعَلِيْمُ الْقَدِيْرُ","bc":"allāhullażī khalaqakum min ḍa'fin ṡumma ja'ala mim ba'di ḍa'fing quwwatan ṡumma ja'ala mim ba'di quwwatin ḍa'faw wa syaibah, yakhluqu mā yasyā`, wa huwal-'alīmul-qadīr","at":"Allah-lah yang menciptakan kamu dari keadaan lemah, kemudian Dia menjadikan (kamu) setelah keadaan lemah itu menjadi kuat, kemudian Dia menjadikan (kamu) setelah kuat itu lemah (kembali) dan beruban. Dia menciptakan apa yang Dia kehendaki. Dan Dia Maha Mengetahui, Mahakuasa. "},{"id":"55","ar":"وَيَوْمَ تَقُوْمُ السَّاعَةُ يُقْسِمُ الْمُجْرِمُوْنَ ەۙ مَا لَبِثُوْا غَيْرَ سَاعَةٍ ۗ كَذٰلِكَ كَانُوْا يُؤْفَكُوْنَ","bc":"wa yauma taqụmus-sā'atu yuqsimul-mujrimụna mā labiṡụ gaira sā'ah, każālika kānụ yu`fakụn","at":"Dan pada hari (ketika) terjadinya Kiamat, orang-orang yang berdosa bersumpah, bahwa mereka berdiam (dalam kubur) hanya sesaat (saja). Begitulah dahulu mereka dipalingkan (dari kebenaran). "},{"id":"56","ar":"وَقَالَ الَّذِيْنَ اُوْتُوا الْعِلْمَ وَالْاِيْمَانَ لَقَدْ لَبِثْتُمْ فِيْ كِتٰبِ اللّٰهِ اِلٰى يَوْمِ الْبَعْثِۖ فَهٰذَا يَوْمُ الْبَعْثِ وَلٰكِنَّكُمْ كُنْتُمْ لَا تَعْلَمُوْنَ","bc":"wa qālallażīna ụtul-'ilma wal-īmāna laqad labiṡtum fī kitābillāhi ilā yaumil-ba'ṡi fa hāżā yaumul-ba'ṡi wa lākinnakum kuntum lā ta'lamụn","at":"Dan orang-orang yang diberi ilmu dan keimanan berkata (kepada orang-orang kafir), “Sungguh, kamu telah berdiam (dalam kubur) menurut ketetapan Allah, sampai hari kebangkitan. Maka inilah hari kebangkitan itu, tetapi (dahulu) kamu tidak meyakini(nya).” "},{"id":"57","ar":"فَيَوْمَىِٕذٍ لَّا يَنْفَعُ الَّذِيْنَ ظَلَمُوْا مَعْذِرَتُهُمْ وَلَا هُمْ يُسْتَعْتَبُوْنَ","bc":"fa yauma`iżil lā yanfa'ullażīna ẓalamụ ma'żiratuhum wa lā hum yusta'tabụn","at":"Maka pada hari itu tidak bermanfaat (lagi) permintaan maaf orang-orang yang zalim, dan mereka tidak pula diberi kesempatan bertobat lagi. "},{"id":"58","ar":"وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِيْ هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍۗ وَلَىِٕنْ جِئْتَهُمْ بِاٰيَةٍ لَّيَقُوْلَنَّ الَّذِيْنَ كَفَرُوْٓا اِنْ اَنْتُمْ اِلَّا مُبْطِلُوْنَ","bc":"wa laqad ḍarabnā lin-nāsi fī hāżal-qur`āni ming kulli maṡal, wa la`in ji`tahum bi`āyatil layaqụlannallażīna kafarū in antum illā mubṭilụn","at":"Dan sesungguhnya telah Kami jelaskan kepada manusia segala macam perumpamaan dalam Al-Qur'an ini. Dan jika engkau membawa suatu ayat kepada mereka, pastilah orang-orang kafir itu akan berkata, ”Kamu hanyalah orang-orang yang membuat kepalsuan belaka.” "},{"id":"59","ar":"كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى قُلُوْبِ الَّذِيْنَ لَا يَعْلَمُوْنَ","bc":"każālika yaṭba'ullāhu 'alā qulụbillażīna lā ya'lamụn","at":"Demikianlah Allah mengunci hati orang-orang yang tidak (mau) memahami. "},{"id":"60","ar":"فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلَا يَسْتَخِفَّنَّكَ الَّذِيْنَ لَا يُوْقِنُوْنَ ","bc":"faṣbir inna wa'dallāhi ḥaqquw wa lā yastakhiffannakallażīna lā yụqinụn","at":"Maka bersabarlah engkau (Muhammad), sungguh, janji Allah itu benar dan sekali-kali jangan sampai orang-orang yang tidak meyakini (kebenaran ayat-ayat Allah) itu menggelisahkan engkau. "}],as_saff=[{"id":"1","ar":"سَبَّحَ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۚ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ","bc":"sabbaḥa lillāhi mā fis-samāwāti wa mā fil-arḍ, wa huwal-'azīzul-ḥakīm","at":"Apa yang ada di langit dan apa yang ada di bumi bertasbih kepada Allah; dan Dialah Yang Mahaperkasa, Mahabijaksana."},{"id":"2","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لِمَ تَقُوْلُوْنَ مَا لَا تَفْعَلُوْنَ ","bc":"yā ayyuhallażīna āmanụ lima taqụlụna mā lā taf'alụn","at":"Wahai orang-orang yang beriman! Mengapa kamu mengatakan sesuatu yang tidak kamu kerjakan?"},{"id":"3","ar":"كَبُرَ مَقْتًا عِنْدَ اللّٰهِ اَنْ تَقُوْلُوْا مَا لَا تَفْعَلُوْنَ ","bc":"kabura maqtan 'indallāhi an taqụlụ mā lā taf'alụn","at":"(Itu) sangatlah dibenci di sisi Allah jika kamu mengatakan apa-apa yang tidak kamu kerjakan."},{"id":"4","ar":"اِنَّ اللّٰهَ يُحِبُّ الَّذِيْنَ يُقَاتِلُوْنَ فِيْ سَبِيْلِهٖ صَفًّا كَاَنَّهُمْ بُنْيَانٌ مَّرْصُوْصٌ","bc":"innallāha yuḥibbullażīna yuqātilụna fī sabīlihī ṣaffang ka`annahum bun-yānum marṣụṣ","at":"Sesungguhnya Allah mencintai orang-orang yang berperang di jalan-Nya dalam barisan yang teratur, mereka seakan-akan seperti suatu bangunan yang tersusun kokoh."},{"id":"5","ar":"وَاِذْ قَالَ مُوْسٰى لِقَوْمِهٖ يٰقَوْمِ لِمَ تُؤْذُوْنَنِيْ وَقَدْ تَّعْلَمُوْنَ اَنِّيْ رَسُوْلُ اللّٰهِ اِلَيْكُمْۗ فَلَمَّا زَاغُوْٓا اَزَاغَ اللّٰهُ قُلُوْبَهُمْۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ ","bc":"wa iż qāla mụsā liqaumihī yā qaumi lima tu`żụnanī wa qat ta'lamụna annī rasụlullāhi ilaikum, fa lammā zāgū azāgallāhu qulụbahum, wallāhu lā yahdil-qaumal-fāsiqīn","at":"Dan (ingatlah) ketika Musa berkata kepada kaumnya, “Wahai kaumku! Mengapa kamu menyakitiku, padahal kamu sungguh mengetahui bahwa sesungguhnya aku utusan Allah kepadamu?” Maka ketika mereka berpaling (dari kebenaran), Allah memalingkan hati mereka. Dan Allah tidak memberi petunjuk kepada kaum yang fasik."},{"id":"6","ar":"وَاِذْ قَالَ عِيْسَى ابْنُ مَرْيَمَ يٰبَنِيْٓ اِسْرَاۤءِيْلَ اِنِّيْ رَسُوْلُ اللّٰهِ اِلَيْكُمْ مُّصَدِّقًا لِّمَا بَيْنَ يَدَيَّ مِنَ التَّوْرٰىةِ وَمُبَشِّرًاۢ بِرَسُوْلٍ يَّأْتِيْ مِنْۢ بَعْدِى اسْمُهٗٓ اَحْمَدُۗ فَلَمَّا جَاۤءَهُمْ بِالْبَيِّنٰتِ قَالُوْا هٰذَا سِحْرٌ مُّبِيْنٌ ","bc":"wa iż qāla 'īsabnu maryama yā banī isrā`īla innī rasụlullāhi ilaikum muṣaddiqal limā baina yadayya minat-taurāti wa mubasysyiram birasụliy ya`tī mim ba'dismuhū aḥmad, fa lammā jā`ahum bil-bayyināti qālụ hāżā siḥrum mubīn","at":"Dan (ingatlah) ketika Isa putra Maryam berkata, “Wahai Bani Israil! Sesungguhnya aku utusan Allah kepadamu, yang membenarkan kitab (yang turun) sebelumku, yaitu Taurat dan memberi kabar gembira dengan seorang Rasul yang akan datang setelahku, yang namanya Ahmad (Muhammad).” Namun ketika Rasul itu datang kepada mereka dengan membawa bukti-bukti yang nyata, mereka berkata, “Ini adalah sihir yang nyata.” "},{"id":"7","ar":"وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ الْكَذِبَ وَهُوَ يُدْعٰىٓ اِلَى الْاِسْلَامِۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ","bc":"wa man aẓlamu mim maniftarā 'alallāhil-każiba wa huwa yud'ā ilal-islām, wallāhu lā yahdil-qaumaẓ-ẓālimīn","at":"Dan siapakah yang lebih zalim daripada orang yang mengada-adakan kebohongan terhadap Allah padahal dia diajak kepada (agama) Islam? Dan Allah tidak memberi petunjuk kepada orang-orang yang zalim. "},{"id":"8","ar":"يُرِيْدُوْنَ لِيُطْفِـُٔوْا نُوْرَ اللّٰهِ بِاَفْوَاهِهِمْۗ وَاللّٰهُ مُتِمُّ نُوْرِهٖ وَلَوْ كَرِهَ الْكٰفِرُوْنَ ","bc":"yurīdụna liyuṭfi`ụ nụrallāhi bi`afwāhihim, wallāhu mutimmu nụrihī walau karihal-kāfirụn","at":"Mereka hendak memadamkan cahaya (agama) Allah dengan mulut (ucapan-ucapan) mereka, tetapi Allah tetap menyempurnakan cahaya-Nya meskipun orang-orang kafir membencinya. "},{"id":"9","ar":"هُوَ الَّذِيْٓ اَرْسَلَ رَسُوْلَهٗ بِالْهُدٰى وَدِيْنِ الْحَقِّ لِيُظْهِرَهٗ عَلَى الدِّيْنِ كُلِّهٖۙ وَلَوْ كَرِهَ الْمُشْرِكُوْنَ ","bc":"huwallażī arsala rasụlahụ bil-hudā wa dīnil-ḥaqqi liyuẓ-hirahụ 'alad-dīni kullihī walau karihal-musyrikụn","at":"Dialah yang mengutus Rasul-Nya dengan membawa petunjuk dan agama yang benar, untuk memenangkannya di atas segala agama meskipun orang-orang musyrik membencinya. "},{"id":"10","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا هَلْ اَدُلُّكُمْ عَلٰى تِجَارَةٍ تُنْجِيْكُمْ مِّنْ عَذَابٍ اَلِيْمٍ ","bc":"yā ayyuhallażīna āmanụ hal adullukum 'alā tijāratin tunjīkum min 'ażābin alīm","at":"Wahai orang-orang yang beriman! Maukah kamu Aku tunjukkan suatu perdagangan yang dapat menyelamatkan kamu dari azab yang pedih?"},{"id":"11","ar":"تُؤْمِنُوْنَ بِاللّٰهِ وَرَسُوْلِهٖ وَتُجَاهِدُوْنَ فِيْ سَبِيْلِ اللّٰهِ بِاَمْوَالِكُمْ وَاَنْفُسِكُمْۗ ذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَۙ ","bc":"tu`minụna billāhi wa rasụlihī wa tujāhidụna fī sabīlillāhi bi`amwālikum wa anfusikum, żālikum khairul lakum ing kuntum ta'lamụn","at":"Yaitu kamu beriman kepada Allah dan Rasul-Nya dan berjihad di jalan Allah dengan harta dan jiwamu. Itulah yang lebih baik bagi kamu jika kamu mengetahui,"},{"id":"12","ar":"يَغْفِرْ لَكُمْ ذُنُوْبَكُمْ وَيُدْخِلْكُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ وَمَسٰكِنَ طَيِّبَةً فِيْ جَنّٰتِ عَدْنٍۗ ذٰلِكَ الْفَوْزُ الْعَظِيْمُۙ ","bc":"yagfir lakum żunụbakum wa yudkhilkum jannātin tajrī min taḥtihal-an-hāru wa masākina ṭayyibatan fī jannāti 'adn, żālikal-fauzul-'aẓīm","at":"niscaya Allah mengampuni dosa-dosamu dan memasukkan kamu ke dalam surga yang mengalir di bawahnya sungai-sungai, dan ke tempat-tempat tinggal yang baik di dalam surga ‘Adn. Itulah kemenangan yang agung. "},{"id":"13","ar":"وَاُخْرٰى تُحِبُّوْنَهَاۗ نَصْرٌ مِّنَ اللّٰهِ وَفَتْحٌ قَرِيْبٌۗ وَبَشِّرِ الْمُؤْمِنِيْنَ ","bc":"wa ukhrā tuḥibbụnahā, naṣrum minallāhi wa fat-ḥung qarīb, wa basysyiril-mu`minīn","at":"Dan (ada lagi) karunia yang lain yang kamu sukai (yaitu) pertolongan dari Allah dan kemenangan yang dekat (waktunya). Dan sampaikanlah berita gembira kepada orang-orang mukmin. "},{"id":"14","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا كُوْنُوْٓا اَنْصَارَ اللّٰهِ كَمَا قَالَ عِيْسَى ابْنُ مَرْيَمَ لِلْحَوَارِيّٖنَ مَنْ اَنْصَارِيْٓ اِلَى اللّٰهِ ۗقَالَ الْحَوَارِيُّوْنَ نَحْنُ اَنْصَارُ اللّٰهِ فَاٰمَنَتْ طَّاۤىِٕفَةٌ مِّنْۢ بَنِيْٓ اِسْرَاۤءِيْلَ وَكَفَرَتْ طَّاۤىِٕفَةٌ ۚفَاَيَّدْنَا الَّذِيْنَ اٰمَنُوْا عَلٰى عَدُوِّهِمْ فَاَصْبَحُوْا ظَاهِرِيْنَ ","bc":"yā ayyuhallażīna āmanụ kụnū anṣārallāhi kamā qāla 'īsabnu maryama lil-ḥawāriyyīna man anṣārī ilallāh, qālal-ḥawāriyyụna naḥnu anṣārullāhi fa āmanat ṭā`ifatum mim banī isrā`īla wa kafarat ṭā`ifah, fa ayyadnallażīna āmanụ 'alā 'aduwwihim fa aṣbaḥụ ẓāhirīn","at":"Wahai orang-orang yang beriman! Jadilah kamu penolong-penolong (agama) Allah sebagaimana Isa putra Maryam telah berkata kepada pengikut-pengikutnya yang setia, “Siapakah yang akan menjadi penolong-penolongku (untuk menegakkan agama) Allah?” Pengikut-pengikutnya yang setia itu berkata, “Kamilah penolong-penolong (agama) Allah,” lalu segolongan dari Bani Israil beriman dan segolongan (yang lain) kafir; lalu Kami berikan kekuatan ke-pada orang-orang yang beriman terhadap musuh-musuh mereka, sehingga mereka menjadi orang-orang yang menang. "}],as_saffat=[{"id":"1","ar":"وَالصّٰۤفّٰتِ صَفًّاۙ","bc":"waṣ-ṣāffāti ṣaffā","at":"Demi (rombongan malaikat) yang berbaris bersaf-saf,"},{"id":"2","ar":"فَالزّٰجِرٰتِ زَجْرًاۙ","bc":"faz-zājirāti zajrā","at":"demi (rombongan) yang mencegah dengan sungguh-sungguh, "},{"id":"3","ar":"فَالتّٰلِيٰتِ ذِكْرًاۙ","bc":"fat-tāliyāti żikrā","at":"demi (rombongan) yang membacakan peringatan,"},{"id":"4","ar":"اِنَّ اِلٰهَكُمْ لَوَاحِدٌۗ ","bc":"inna ilāhakum lawāḥid","at":"sungguh, Tuhanmu benar-benar Esa."},{"id":"5","ar":"رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا وَرَبُّ الْمَشَارِقِۗ ","bc":"rabbus-samāwāti wal-arḍi wa mā bainahumā wa rabbul-masyāriq","at":"Tuhan langit dan bumi dan apa yang berada di antara keduanya dan Tuhan tempat-tempat terbitnya matahari."},{"id":"6","ar":"اِنَّا زَيَّنَّا السَّمَاۤءَ الدُّنْيَا بِزِيْنَةِ ِۨالْكَوَاكِبِۙ","bc":"innā zayyannas-samā`ad-dun-yā bizīnatinil-kawākib","at":"Sesungguhnya Kami telah menghias langit dunia (yang terdekat), dengan hiasan bintang-bintang."},{"id":"7","ar":"وَحِفْظًا مِّنْ كُلِّ شَيْطٰنٍ مَّارِدٍۚ ","bc":"wa ḥifẓam ming kulli syaiṭānim mārid","at":"Dan (Kami) telah menjaganya dari setiap setan yang durhaka,"},{"id":"8","ar":"لَا يَسَّمَّعُوْنَ اِلَى الْمَلَاِ الْاَعْلٰى وَيُقْذَفُوْنَ مِنْ كُلِّ جَانِبٍۖ ","bc":"lā yassamma'ụna ilal-mala`il-a'lā wa yuqżafụna ming kulli jānib","at":"mereka (setan-setan itu) tidak dapat mendengar (pembicaraan) para malaikat dan mereka dilempari dari segala penjuru,"},{"id":"9","ar":"دُحُوْرًا وَّلَهُمْ عَذَابٌ وَّاصِبٌ ","bc":"duḥụraw wa lahum 'ażābuw wāṣib","at":"untuk mengusir mereka dan mereka akan mendapat azab yang kekal,"},{"id":"10","ar":"اِلَّا مَنْ خَطِفَ الْخَطْفَةَ فَاَتْبَعَهٗ شِهَابٌ ثَاقِبٌ ","bc":"illā man khaṭifal-khaṭfata fa atba'ahụ syihābun ṡāqib","at":"kecuali (setan) yang mencuri (pembicaraan); maka ia dikejar oleh bintang yang menyala."},{"id":"11","ar":"فَاسْتَفْتِهِمْ اَهُمْ اَشَدُّ خَلْقًا اَمْ مَّنْ خَلَقْنَا ۗاِنَّا خَلَقْنٰهُمْ مِّنْ طِيْنٍ لَّازِبٍ ","bc":"fastaftihim a hum asyaddu khalqan am man khalaqnā, innā khalaqnāhum min ṭīnil lāzib","at":"Maka tanyakanlah kepada mereka (musyrik Mekah), “Apakah penciptaan mereka yang lebih sulit ataukah apa yang telah Kami ciptakan itu?” Sesungguhnya Kami telah menciptakan mereka dari tanah liat."},{"id":"12","ar":"بَلْ عَجِبْتَ وَيَسْخَرُوْنَ ۖ ","bc":"bal 'ajibta wa yaskharụn","at":"Bahkan engkau (Muhammad) menjadi heran (terhadap keingkaran mereka) dan mereka menghinakan (engkau)."},{"id":"13","ar":"وَاِذَا ذُكِّرُوْا لَا يَذْكُرُوْنَ ۖ ","bc":"wa iżā żukkirụ lā yażkurụn","at":"Dan apabila mereka diberi peringatan, mereka tidak mengindahkannya."},{"id":"14","ar":"وَاِذَا رَاَوْا اٰيَةً يَّسْتَسْخِرُوْنَۖ ","bc":"wa iżā ra`au āyatay yastaskhirụn","at":"Dan apabila mereka melihat suatu tanda (kebesaran) Allah, mereka memperolok-olokkan."},{"id":"15","ar":"وَقَالُوْٓا اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ ۚ ","bc":"wa qālū in hāżā illā siḥrum mubīn","at":"Dan mereka berkata, “Ini tidak lain hanyalah sihir yang nyata."},{"id":"16","ar":"ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَبْعُوْثُوْنَۙ","bc":"a iżā mitnā wa kunnā turābaw wa 'iẓāman a innā lamab'ụṡụn","at":"Apabila kami telah mati dan telah menjadi tanah dan tulang-belulang, apakah benar kami akan dibangkitkan (kembali)?"},{"id":"17","ar":"اَوَاٰبَاۤؤُنَا الْاَوَّلُوْنَۗ ","bc":"a wa ābā`unal-awwalụn","at":"dan apakah nenek moyang kami yang telah terdahulu (akan dibangkitkan pula)?”"},{"id":"18","ar":"قُلْ نَعَمْ وَاَنْتُمْ دَاخِرُوْنَۚ ","bc":"qul na'am wa antum dākhirụn","at":"Katakanlah (Muhammad), “Ya, dan kamu akan terhina.”"},{"id":"19","ar":"فَاِنَّمَا هِيَ زَجْرَةٌ وَّاحِدَةٌ فَاِذَا هُمْ يَنْظُرُوْنَ ","bc":"fa innamā hiya zajratuw wāḥidatun fa iżā hum yanẓurụn","at":"Maka sesungguhnya kebangkitan itu hanya dengan satu teriakan saja; maka seketika itu mereka melihatnya."},{"id":"20","ar":"وَقَالُوْا يٰوَيْلَنَا هٰذَا يَوْمُ الدِّيْنِ ","bc":"wa qālụ yā wailanā hāżā yaumud-dīn","at":"Dan mereka berkata, “Alangkah celaka kami! (Kiranya) inilah hari pembalasan itu.”"},{"id":"21","ar":"هٰذَا يَوْمُ الْفَصْلِ الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَ","bc":"hāżā yaumul-faṣlillażī kuntum bihī tukażżibụn","at":"Inilah hari keputusan yang dahulu kamu dustakan."},{"id":"22","ar":"اُحْشُرُوا الَّذِيْنَ ظَلَمُوْا وَاَزْوَاجَهُمْ وَمَا كَانُوْا يَعْبُدُوْنَ ۙ","bc":"uḥsyurullażīna ẓalamụ wa azwājahum wa mā kānụ ya'budụn","at":"(Diperintahkan kepada malaikat), “Kumpulkanlah orang-orang yang zalim beserta teman sejawat mereka dan apa yang dahulu mereka sembah,"},{"id":"23","ar":"مِنْ دُوْنِ اللّٰهِ فَاهْدُوْهُمْ اِلٰى صِرَاطِ الْجَحِيْمِ ","bc":"min dụnillāhi fahdụhum ilā ṣirāṭil-jaḥīm","at":"selain Allah, lalu tunjukkanlah kepada mereka jalan ke neraka."},{"id":"24","ar":"وَقِفُوْهُمْ اِنَّهُمْ مَّسْـُٔوْلُوْنَ ۙ","bc":"waqifụhum innahum mas`ụlụn","at":"Tahanlah mereka (di tempat perhentian), sesungguhnya mereka akan ditanya,"},{"id":"25","ar":"مَا لَكُمْ لَا تَنَاصَرُوْنَ ","bc":"mā lakum lā tanāṣarụn","at":"”Mengapa kamu tidak tolong-menolong?”"},{"id":"26","ar":"بَلْ هُمُ الْيَوْمَ مُسْتَسْلِمُوْنَ","bc":"bal humul-yauma mustaslimụn","at":"Bahkan mereka pada hari itu menyerah (kepada keputusan Allah)."},{"id":"27","ar":"وَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَسَاۤءَلُوْنَ ","bc":"wa aqbala ba'ḍuhum 'alā ba'ḍiy yatasā`alụn","at":"Dan sebagian mereka menghadap kepada sebagian yang lain saling berbantah-bantahan."},{"id":"28","ar":"قَالُوْٓا اِنَّكُمْ كُنْتُمْ تَأْتُوْنَنَا عَنِ الْيَمِيْنِ ","bc":"qālū innakum kuntum ta`tụnanā 'anil-yamīn","at":"Sesungguhnya (pengikut-pengikut) mereka berkata (kepada pemimpin-pemimpin mereka), “Kamulah yang dahulu datang kepada kami dari kanan.”"},{"id":"29","ar":"قَالُوْا بَلْ لَّمْ تَكُوْنُوْا مُؤْمِنِيْنَۚ ","bc":"qālụ bal lam takụnụ mu`minīn","at":"(Pemimpin-pemimpin) mereka menjawab, “(Tidak), bahkan kamulah yang tidak (mau) menjadi orang mukmin,"},{"id":"30","ar":"وَمَا كَانَ لَنَا عَلَيْكُمْ مِّنْ سُلْطٰنٍۚ بَلْ كُنْتُمْ قَوْمًا طٰغِيْنَ ","bc":"wa mā kāna lanā 'alaikum min sulṭān, bal kuntum qauman ṭāgīn","at":"sedangkan kami tidak berkuasa terhadapmu, bahkan kamu menjadi kaum yang melampaui batas."},{"id":"31","ar":"فَحَقَّ عَلَيْنَا قَوْلُ رَبِّنَآ ۖاِنَّا لَذَاۤىِٕقُوْنَ ","bc":"fa ḥaqqa 'alainā qaulu rabbinā innā lażā`iqụn","at":"Maka pantas putusan (azab) Tuhan menimpa kita; pasti kita akan merasakan (azab itu)."},{"id":"32","ar":"فَاَغْوَيْنٰكُمْ اِنَّا كُنَّا غٰوِيْنَ ","bc":"fa agwainākum innā kunnā gāwīn","at":"Maka kami telah menyesatkan kamu, sesungguhnya kami sendiri, orang-orang yang sesat.”"},{"id":"33","ar":"فَاِنَّهُمْ يَوْمَىِٕذٍ فِى الْعَذَابِ مُشْتَرِكُوْنَ","bc":"fa innahum yauma`iżin fil-'ażābi musytarikụn","at":"Maka sesungguhnya mereka pada hari itu bersama-sama merasakan azab."},{"id":"34","ar":"اِنَّا كَذٰلِكَ نَفْعَلُ بِالْمُجْرِمِيْنَ","bc":"innā każālika naf'alu bil-mujrimīn","at":"Sungguh, demikianlah Kami memperlakukan terhadap orang-orang yang berbuat dosa."},{"id":"35","ar":"اِنَّهُمْ كَانُوْٓا اِذَا قِيْلَ لَهُمْ لَآ اِلٰهَ اِلَّا اللّٰهُ يَسْتَكْبِرُوْنَ ۙ","bc":"innahum kānū iżā qīla lahum lā ilāha illallāhu yastakbirụn","at":"Sungguh, dahulu apabila dikatakan kepada mereka, “La ilaha illallah” (Tidak ada tuhan selain Allah), mereka menyombongkan diri,"},{"id":"36","ar":"وَيَقُوْلُوْنَ اَىِٕنَّا لَتَارِكُوْٓا اٰلِهَتِنَا لِشَاعِرٍ مَّجْنُوْنٍ ۗ ","bc":"wa yaqụlụna a innā latārikū ālihatinā lisyā'irim majnụn","at":"dan mereka berkata, “Apakah kami harus meninggalkan sesembahan kami karena seorang penyair gila?”"},{"id":"37","ar":"بَلْ جَاۤءَ بِالْحَقِّ وَصَدَّقَ الْمُرْسَلِيْنَ","bc":"bal jā`a bil-ḥaqqi wa ṣaddaqal-mursalīn","at":"Padahal dia (Muhammad) datang dengan membawa kebenaran dan membenarkan rasul-rasul (sebelumnya)."},{"id":"38","ar":"اِنَّكُمْ لَذَاۤىِٕقُوا الْعَذَابِ الْاَلِيْمِ ۚ ","bc":"innakum lażā`iqul-'ażābil-alīm","at":"Sungguh, kamu pasti akan merasakan azab yang pedih."},{"id":"39","ar":"وَمَا تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُوْنَۙ","bc":"wa mā tujzauna illā mā kuntum ta'malụn","at":"Dan kamu tidak diberi balasan melainkan terhadap apa yang telah kamu kerjakan,"},{"id":"40","ar":"اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ ","bc":"illā 'ibādallāhil-mukhlaṣīn","at":"tetapi hamba-hamba Allah yang dibersihkan (dari dosa),"},{"id":"41","ar":"اُولٰۤىِٕكَ لَهُمْ رِزْقٌ مَّعْلُوْمٌۙ","bc":"ulā`ika lahum rizqum ma'lụm","at":"mereka itu memperoleh rezeki yang sudah ditentukan,"},{"id":"42","ar":"فَوَاكِهُ ۚوَهُمْ مُّكْرَمُوْنَۙ","bc":"fawākih, wa hum mukramụn","at":"(yaitu) buah-buahan. Dan mereka orang yang dimuliakan,"},{"id":"43","ar":"فِيْ جَنّٰتِ النَّعِيْمِۙ","bc":"fī jannātin na'īm","at":"di dalam surga-surga yang penuh kenikmatan,"},{"id":"44","ar":"عَلٰى سُرُرٍ مُّتَقٰبِلِيْنَ","bc":"'alā sururim mutaqābilīn","at":"(mereka duduk) berhadap-hadapan di atas dipan-dipan."},{"id":"45","ar":"يُطَافُ عَلَيْهِمْ بِكَأْسٍ مِّنْ مَّعِيْنٍۢ ۙ","bc":"yuṭāfu 'alaihim bika`sim mim ma'īn","at":"Kepada mereka diedarkan gelas (yang berisi air) dari mata air (surga),"},{"id":"46","ar":"بَيْضَاۤءَ لَذَّةٍ لِّلشّٰرِبِيْنَۚ ","bc":"baiḍā`a lażżatil lisy-syāribīn","at":"(warnanya) putih bersih, sedap rasanya bagi orang-orang yang minum."},{"id":"47","ar":"لَا فِيْهَا غَوْلٌ وَّلَا هُمْ عَنْهَا يُنْزَفُوْنَ","bc":"lā fīhā gauluw wa lā hum 'an-hā yunzafụn","at":"Tidak ada di dalamnya (unsur) yang memabukkan dan mereka tidak mabuk karenanya."},{"id":"48","ar":"وَعِنْدَهُمْ قٰصِرٰتُ الطَّرْفِ عِيْنٌ ۙ","bc":"wa 'indahum qāṣirātuṭ-ṭarfi 'īn","at":"Dan di sisi mereka ada (bidadari-bidadari) yang bermata indah, dan membatasi pandangannya,"},{"id":"49","ar":"كَاَنَّهُنَّ بَيْضٌ مَّكْنُوْنٌ","bc":"ka`annahunna baiḍum maknụn","at":"seakan-akan mereka adalah telur yang tersimpan dengan baik."},{"id":"50","ar":"فَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَسَاۤءَلُوْنَ","bc":"fa aqbala ba'ḍuhum 'alā ba'ḍiy yatasā`alụn","at":"Lalu mereka berhadap-hadapan satu sama lain sambil bercakap-cakap."},{"id":"51","ar":"قَالَ قَاۤىِٕلٌ مِّنْهُمْ اِنِّيْ كَانَ لِيْ قَرِيْنٌۙ","bc":"qāla qā`ilum min-hum innī kāna lī qarīn","at":"Berkatalah salah seorang di antara mereka, “Sesungguhnya aku dahulu (di dunia) pernah mempunyai seorang teman,"},{"id":"52","ar":"يَّقُوْلُ اَىِٕنَّكَ لَمِنَ الْمُصَدِّقِيْنَ","bc":"yaqụlu a innaka laminal-muṣaddiqīn","at":"yang berkata, “Apakah sesungguhnya kamu termasuk orang-orang yang membenarkan (hari berbangkit)?"},{"id":"53","ar":"ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا وَّعِظَامًا ءَاِنَّا لَمَدِيْنُوْنَ","bc":"a iżā mitnā wa kunnā turābaw wa 'iẓāman a innā lamadīnụn","at":"Apabila kita telah mati dan telah menjadi tanah dan tulang-belulang, apakah kita benar-benar (akan dibangkitkan) untuk diberi pembalasan?”"},{"id":"54","ar":"قَالَ هَلْ اَنْتُمْ مُّطَّلِعُوْنَ","bc":"qāla hal antum muṭṭali'ụn","at":"Dia berkata, “Maukah kamu meninjau (temanku itu)?”"},{"id":"55","ar":"فَاطَّلَعَ فَرَاٰهُ فِيْ سَوَاۤءِ الْجَحِيْمِ","bc":"faṭṭala'a fa ra`āhu fī sawā`il-jaḥīm","at":"Maka dia meninjaunya, lalu dia melihat (teman)nya itu di tengah-tengah neraka yang menyala-nyala."},{"id":"56","ar":"قَالَ تَاللّٰهِ اِنْ كِدْتَّ لَتُرْدِيْنِ ۙ","bc":"qāla tallāhi ing kitta laturdīn","at":"Dia berkata, “Demi Allah, engkau hampir saja mencelakakanku,"},{"id":"57","ar":"وَلَوْلَا نِعْمَةُ رَبِّيْ لَكُنْتُ مِنَ الْمُحْضَرِيْنَ","bc":"walau lā ni'matu rabbī lakuntu minal-muḥḍarīn","at":"dan sekiranya bukan karena nikmat Tuhanku pastilah aku termasuk orang-orang yang diseret (ke neraka).”"},{"id":"58","ar":"اَفَمَا نَحْنُ بِمَيِّتِيْنَۙ","bc":"a fa mā naḥnu bimayyitīn","at":"Maka apakah kita tidak akan mati?"},{"id":"59","ar":"اِلَّا مَوْتَتَنَا الْاُوْلٰى وَمَا نَحْنُ بِمُعَذَّبِيْنَ","bc":"illā mautatanal-ụlā wa mā naḥnu bimu'ażżabīn","at":"Kecuali kematian kita yang pertama saja (di dunia), dan kita tidak akan diazab (di akhirat ini)?”"},{"id":"60","ar":"اِنَّ هٰذَا لَهُوَ الْفَوْزُ الْعَظِيْمُ","bc":"inna hāżā lahuwal-fauzul-'aẓīm","at":"Sungguh, ini benar-benar kemenangan yang agung."},{"id":"61","ar":"لِمِثْلِ هٰذَا فَلْيَعْمَلِ الْعٰمِلُوْنَ","bc":"limiṡli hāżā falya'malil-'āmilụn","at":"Untuk (kemenangan) serupa ini, hendaklah beramal orang-orang yang mampu beramal."},{"id":"62","ar":"اَذٰلِكَ خَيْرٌ نُّزُلًا اَمْ شَجَرَةُ الزَّقُّوْمِ","bc":"a żālika khairun nuzulan am syajaratuz-zaqqụm","at":"Apakah (makanan surga) itu hidangan yang lebih baik ataukah pohon zaqqum."},{"id":"63","ar":"اِنَّا جَعَلْنٰهَا فِتْنَةً لِّلظّٰلِمِيْنَ","bc":"innā ja'alnāhā fitnatal liẓ-ẓālimīn","at":"Sungguh, Kami menjadikannya (pohon zaqqum itu) sebagai azab bagi orang-orang zalim."},{"id":"64","ar":"اِنَّهَا شَجَرَةٌ تَخْرُجُ فِيْٓ اَصْلِ الْجَحِيْمِۙ","bc":"innahā syajaratun takhruju fī aṣlil-jaḥīm","at":"Sungguh, itu adalah pohon yang keluar dari dasar neraka Jahim,"},{"id":"65","ar":"طَلْعُهَا كَاَنَّهٗ رُءُوْسُ الشَّيٰطِيْنِ","bc":"ṭal'uhā ka`annahụ ru`ụsusy-syayāṭīn","at":"Mayangnya seperti kepala-kepala setan."},{"id":"66","ar":"فَاِنَّهُمْ لَاٰكِلُوْنَ مِنْهَا فَمَالِـُٔوْنَ مِنْهَا الْبُطُوْنَۗ ","bc":"fa innahum la`ākilụna min-hā famāli`ụna min-hal buṭụn","at":"Maka sungguh, mereka benar-benar memakan sebagian darinya (buah pohon itu), dan mereka memenuhi perutnya dengan buahnya (zaqqum)."},{"id":"67","ar":"ثُمَّ اِنَّ لَهُمْ عَلَيْهَا لَشَوْبًا مِّنْ حَمِيْمٍۚ ","bc":"ṡumma inna lahum 'alaihā lasyaubam min ḥamīm","at":"Kemudian sungguh, setelah makan (buah zaqqum) mereka mendapat minuman yang dicampur dengan air yang sangat panas."},{"id":"68","ar":"ثُمَّ اِنَّ مَرْجِعَهُمْ لَاِلَى الْجَحِيْمِ","bc":"ṡumma inna marji'ahum la`ilal-jaḥīm","at":"Kemudian pasti tempat kembali mereka ke neraka Jahim."},{"id":"69","ar":"اِنَّهُمْ اَلْفَوْا اٰبَاۤءَهُمْ ضَاۤلِّيْنَۙ","bc":"innahum alfau ābā`ahum ḍāllīn","at":"Sesungguhnya mereka mendapati nenek moyang mereka dalam keadaan sesat, "},{"id":"70","ar":"فَهُمْ عَلٰٓى اٰثٰرِهِمْ يُهْرَعُوْنَ","bc":"fa hum 'alā āṡārihim yuhra'ụn","at":"lalu mereka tergesa-gesa mengikuti jejak (nenek moyang) mereka."},{"id":"71","ar":"وَلَقَدْ ضَلَّ قَبْلَهُمْ اَكْثَرُ الْاَوَّلِيْنَۙ","bc":"wa laqad ḍalla qablahum akṡarul-awwalīn","at":"Dan sungguh, sebelum mereka (Suku Quraisy), telah sesat sebagian besar dari orang-orang yang dahulu,"},{"id":"72","ar":"وَلَقَدْ اَرْسَلْنَا فِيْهِمْ مُّنْذِرِيْنَ","bc":"wa laqad arsalnā fīhim munżirīn","at":"dan sungguh, Kami telah mengutus (rasul) pemberi peringatan di kalangan mereka."},{"id":"73","ar":"فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَرِيْنَۙ","bc":"fanẓur kaifa kāna 'āqibatul-munżarīn","at":"Maka perhatikanlah bagaimana kesudahan orang-orang yang diberi peringatan itu,"},{"id":"74","ar":"اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ ","bc":"illā 'ibādallāhil-mukhlaṣīn","at":"kecuali hamba-hamba Allah yang disucikan (dari dosa)."},{"id":"75","ar":"وَلَقَدْ نَادٰىنَا نُوْحٌ فَلَنِعْمَ الْمُجِيْبُوْنَۖ ","bc":"wa laqad nādānā nụḥun fa lani'mal-mujībụn","at":"Dan sungguh, Nuh telah berdoa kepada Kami, maka sungguh, Kamilah sebaik-baik yang memperkenankan doa."},{"id":"76","ar":"وَنَجَّيْنٰهُ وَاَهْلَهٗ مِنَ الْكَرْبِ الْعَظِيْمِۖ ","bc":"wa najjaināhu wa ahlahụ minal-karbil-'aẓīm","at":"Kami telah menyelamatkan dia dan pengikutnya dari bencana yang besar."},{"id":"77","ar":"وَجَعَلْنَا ذُرِّيَّتَهٗ هُمُ الْبٰقِيْنَ","bc":"wa ja'alnā żurriyyatahụ humul-bāqīn","at":"Dan Kami jadikan anak cucunya orang-orang yang melanjutkan keturunan."},{"id":"78","ar":"وَتَرَكْنَا عَلَيْهِ فِى الْاٰخِرِيْنَ ۖ ","bc":"wa taraknā 'alaihi fil-ākhirīn","at":"Dan Kami abadikan untuk Nuh (pujian) di kalangan orang-orang yang datang kemudian;"},{"id":"79","ar":"سَلٰمٌ عَلٰى نُوْحٍ فِى الْعٰلَمِيْنَ","bc":"salāmun 'alā nụḥin fil-'ālamīn","at":"”Kesejahteraan (Kami limpahkan) atas Nuh di seluruh alam.”"},{"id":"80","ar":"اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ","bc":"innā każālika najzil-muḥsinīn","at":"Sungguh, demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik."},{"id":"81","ar":"اِنَّهٗ مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ","bc":"innahụ min 'ibādinal-mu`minīn","at":"Sungguh, dia termasuk di antara hamba-hamba Kami yang beriman."},{"id":"82","ar":"ثُمَّ اَغْرَقْنَا الْاٰخَرِيْنَ","bc":"ṡumma agraqnal-ākharīn","at":"Kemudian Kami tenggelamkan yang lain."},{"id":"83","ar":"وَاِنَّ مِنْ شِيْعَتِهٖ لَاِبْرٰهِيْمَ ۘ ","bc":"wa inna min syī'atihī la`ibrāhīm","at":"Dan sungguh, Ibrahim termasuk golongannya (Nuh)."},{"id":"84","ar":"اِذْ جَاۤءَ رَبَّهٗ بِقَلْبٍ سَلِيْمٍۙ","bc":"iż jā`a rabbahụ biqalbin salīm","at":"(Ingatlah) ketika dia datang kepada Tuhannya dengan hati yang suci,"},{"id":"85","ar":"اِذْ قَالَ لِاَبِيْهِ وَقَوْمِهٖ مَاذَا تَعْبُدُوْنَ ۚ ","bc":"iż qāla li`abīhi wa qaumihī māżā ta'budụn","at":"(ingatlah) ketika dia berkata kepada ayahnya dan kaumnya, “Apakah yang kamu sembah itu?"},{"id":"86","ar":"اَىِٕفْكًا اٰلِهَةً دُوْنَ اللّٰهِ تُرِيْدُوْنَۗ ","bc":"a ifkan āliḥatan dụnallāhi turīdụn","at":"Apakah kamu menghendaki kebohongan dengan sesembahan selain Allah itu?"},{"id":"87","ar":"فَمَا ظَنُّكُمْ بِرَبِّ الْعٰلَمِيْنَ","bc":"fa mā ẓannukum birabbil-'ālamīn","at":"Maka bagaimana anggapanmu terhadap Tuhan seluruh alam?”"},{"id":"88","ar":"فَنَظَرَ نَظْرَةً فِى النُّجُوْمِۙ","bc":"fa naẓara naẓratan fin-nujụm","at":"Lalu dia memandang sekilas ke bintang-bintang,"},{"id":"89","ar":"فَقَالَ اِنِّيْ سَقِيْمٌ","bc":"fa qāla innī saqīm","at":"kemudian dia (Ibrahim) berkata, “Sesungguhnya aku sakit.”"},{"id":"90","ar":"فَتَوَلَّوْا عَنْهُ مُدْبِرِيْنَ","bc":"fa tawallau 'an-hu mudbirīn","at":"Lalu mereka berpaling dari dia dan pergi meninggalkannya."},{"id":"91","ar":"فَرَاغَ اِلٰٓى اٰلِهَتِهِمْ فَقَالَ اَلَا تَأْكُلُوْنَۚ ","bc":"fa rāga ilā ālihatihim fa qāla alā ta`kulụn","at":"Kemudian dia (Ibrahim) pergi dengan diam-diam kepada berhala-berhala mereka; lalu dia berkata, “Mengapa kamu tidak makan?"},{"id":"92","ar":"مَا لَكُمْ لَا تَنْطِقُوْنَ","bc":"mā lakum lā tanṭiqụn","at":"Mengapa kamu tidak menjawab?”"},{"id":"93","ar":"فَرَاغَ عَلَيْهِمْ ضَرْبًا ۢبِالْيَمِيْنِ","bc":"fa rāga 'alaihim ḍarbam bil-yamīn","at":"Lalu dihadapinya (berhala-berhala) itu sambil memukulnya dengan tangan kanannya."},{"id":"94","ar":"فَاَقْبَلُوْٓا اِلَيْهِ يَزِفُّوْنَ","bc":"fa aqbalū ilaihi yaziffụn","at":"Kemudian mereka (kaumnya) datang bergegas kepadanya."},{"id":"95","ar":"قَالَ اَتَعْبُدُوْنَ مَا تَنْحِتُوْنَۙ","bc":"qāla a ta'budụna mā tan-ḥitụn","at":"Dia (Ibrahim) berkata, “Apakah kamu menyembah patung-patung yang kamu pahat itu?"},{"id":"96","ar":"وَاللّٰهُ خَلَقَكُمْ وَمَا تَعْمَلُوْنَ","bc":"wallāhu khalaqakum wa mā ta'malụn","at":"Padahal Allah-lah yang menciptakan kamu dan apa yang kamu perbuat itu.”"},{"id":"97","ar":"قَالُوا ابْنُوْا لَهٗ بُنْيَانًا فَاَلْقُوْهُ فِى الْجَحِيْمِ","bc":"qālubnụ lahụ bun-yānan fa alqụhu fil-jaḥīm","at":"Mereka berkata, “Buatlah bangunan (perapian) untuknya (membakar Ibrahim); lalu lemparkan dia ke dalam api yang menyala-nyala itu.”"},{"id":"98","ar":"فَاَرَادُوْا بِهٖ كَيْدًا فَجَعَلْنٰهُمُ الْاَسْفَلِيْنَ","bc":"fa arādụ bihī kaidan fa ja'alnāhumul-asfalīn","at":"Maka mereka bermaksud memperdayainya dengan (membakar)nya, (namun Allah menyelamatkannya), lalu Kami jadikan mereka orang-orang yang hina."},{"id":"99","ar":"وَقَالَ اِنِّيْ ذَاهِبٌ اِلٰى رَبِّيْ سَيَهْدِيْنِ","bc":"wa qāla innī żāhibun ilā rabbī sayahdīn","at":"Dan dia (Ibrahim) berkata, “Sesungguhnya aku harus pergi (menghadap) kepada Tuhanku, Dia akan memberi petunjuk kepadaku."},{"id":"100","ar":"رَبِّ هَبْ لِيْ مِنَ الصّٰلِحِيْنَ","bc":"rabbi hab lī minaṣ-ṣāliḥīn","at":"Ya Tuhanku, anugerahkanlah kepadaku (seorang anak) yang termasuk orang yang saleh.”"},{"id":"101","ar":"فَبَشَّرْنٰهُ بِغُلٰمٍ حَلِيْمٍ","bc":"fa basysyarnāhu bigulāmin ḥalīm","at":"Maka Kami beri kabar gembira kepadanya dengan (kelahiran) seorang anak yang sangat sabar (Ismail)."},{"id":"102","ar":"فَلَمَّا بَلَغَ مَعَهُ السَّعْيَ قَالَ يٰبُنَيَّ اِنِّيْٓ اَرٰى فِى الْمَنَامِ اَنِّيْٓ اَذْبَحُكَ فَانْظُرْ مَاذَا تَرٰىۗ قَالَ يٰٓاَبَتِ افْعَلْ مَا تُؤْمَرُۖ سَتَجِدُنِيْٓ اِنْ شَاۤءَ اللّٰهُ مِنَ الصّٰبِرِيْنَ","bc":"fa lammā balaga ma'ahus-sa'ya qāla yā bunayya innī arā fil-manāmi annī ażbaḥuka fanẓur māżā tarā, qāla yā abatif'al mā tu`maru satajidunī in syā`allāhu minaṣ-ṣābirīn","at":"Maka ketika anak itu sampai (pada umur) sanggup berusaha bersamanya, (Ibrahim) berkata, “Wahai anakku! Sesungguhnya aku bermimpi bahwa aku menyembelihmu. Maka pikirkanlah bagaimana pendapatmu!” Dia (Ismail) menjawab, “Wahai ayahku! Lakukanlah apa yang diperintahkan (Allah) kepadamu; insya Allah engkau akan mendapatiku termasuk orang yang sabar.”"},{"id":"103","ar":"فَلَمَّآ اَسْلَمَا وَتَلَّهٗ لِلْجَبِيْنِۚ ","bc":"fa lammā aslamā wa tallahụ lil-jabīn","at":"Maka ketika keduanya telah berserah diri dan dia (Ibrahim) membaringkan anaknya atas pelipis(nya), (untuk melaksanakan perintah Allah)."},{"id":"104","ar":"وَنَادَيْنٰهُ اَنْ يّٰٓاِبْرٰهِيْمُ ۙ","bc":"wa nādaināhu ay yā ibrāhīm","at":"Lalu Kami panggil dia, “Wahai Ibrahim!"},{"id":"105","ar":"قَدْ صَدَّقْتَ الرُّؤْيَا ۚاِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ","bc":"qad ṣaddaqtar-ru`yā, innā każālika najzil-muḥsinīn","at":"sungguh, engkau telah membenarkan mimpi itu.” Sungguh, demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik."},{"id":"106","ar":"اِنَّ هٰذَا لَهُوَ الْبَلٰۤؤُا الْمُبِيْنُ","bc":"inna hāżā lahuwal-balā`ul mubīn","at":"Sesungguhnya ini benar-benar suatu ujian yang nyata."},{"id":"107","ar":"وَفَدَيْنٰهُ بِذِبْحٍ عَظِيْمٍ","bc":"wa fadaināhu biżib-ḥin 'aẓīm","at":"Dan Kami tebus anak itu dengan seekor sembelihan yang besar."},{"id":"108","ar":"وَتَرَكْنَا عَلَيْهِ فِى الْاٰخِرِيْنَ ۖ ","bc":"wa taraknā 'alaihi fil-ākhirīn","at":"Dan Kami abadikan untuk Ibrahim (pujian) di kalangan orang-orang yang datang kemudian,"},{"id":"109","ar":"سَلٰمٌ عَلٰٓى اِبْرٰهِيْمَ","bc":"salāmun 'alā ibrāhīm","at":"”Selamat sejahtera bagi Ibrahim.”"},{"id":"110","ar":"كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ","bc":"każālika najzil-muḥsinīn","at":"Demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik."},{"id":"111","ar":"اِنَّهٗ مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ","bc":"innahụ min 'ibādinal-mu`minīn","at":"Sungguh, dia termasuk hamba-hamba Kami yang beriman."},{"id":"112","ar":"وَبَشَّرْنٰهُ بِاِسْحٰقَ نَبِيًّا مِّنَ الصّٰلِحِيْنَ","bc":"wa basysyarnāhu bi`is-ḥāqa nabiyyam minaṣ-ṣāliḥīn","at":"Dan Kami beri dia kabar gembira dengan (kelahiran) Ishak seorang nabi yang termasuk orang-orang yang saleh."},{"id":"113","ar":"وَبٰرَكْنَا عَلَيْهِ وَعَلٰٓى اِسْحٰقَۗ وَمِنْ ذُرِّيَّتِهِمَا مُحْسِنٌ وَّظَالِمٌ لِّنَفْسِهٖ مُبِيْنٌ","bc":"wa bāraknā 'alaihi wa 'alā is-ḥāq, wa min żurriyyatihimā muḥsinuw wa ẓālimul linafsihī mubīn","at":"Dan Kami limpahkan keberkahan kepadanya dan kepada Ishak. Dan di antara keturunan keduanya ada yang berbuat baik dan ada (pula) yang terang-terangan berbuat zalim terhadap dirinya sendiri."},{"id":"114","ar":"وَلَقَدْ مَنَنَّا عَلٰى مُوْسٰى وَهٰرُوْنَ ۚ ","bc":"wa laqad manannā 'alā mụsā wa hārụn","at":"Dan sungguh, Kami telah melimpahkan nikmat kepada Musa dan Harun."},{"id":"115","ar":"وَنَجَّيْنٰهُمَا وَقَوْمَهُمَا مِنَ الْكَرْبِ الْعَظِيْمِۚ ","bc":"wa najjaināhumā wa qaumahumā minal-karbil-'aẓīm","at":"Dan Kami selamatkan keduanya dan kaumnya dari bencana yang besar,"},{"id":"116","ar":"وَنَصَرْنٰهُمْ فَكَانُوْا هُمُ الْغٰلِبِيْنَۚ ","bc":"wa naṣarnāhum fa kānụ humul-gālibīn","at":"dan Kami tolong mereka, sehingga jadilah mereka orang-orang yang menang."},{"id":"117","ar":"وَاٰتَيْنٰهُمَا الْكِتٰبَ الْمُسْتَبِيْنَ ۚ ","bc":"wa ātaināhumal-kitābal-mustabīn","at":"Dan Kami berikan kepada keduanya Kitab yang sangat jelas,"},{"id":"118","ar":"وَهَدَيْنٰهُمَا الصِّرَاطَ الْمُسْتَقِيْمَۚ ","bc":"wa hadaināhumaṣ-ṣirāṭal-mustaqīm","at":"dan Kami tunjukkan keduanya jalan yang lurus."},{"id":"119","ar":"وَتَرَكْنَا عَلَيْهِمَا فِى الْاٰخِرِيْنَ ۖ ","bc":"wa taraknā 'alaihimā fil-ākhirīn","at":"Dan Kami abadikan untuk keduanya (pujian) di kalangan orang-orang yang datang kemudian,"},{"id":"120","ar":"سَلٰمٌ عَلٰى مُوْسٰى وَهٰرُوْنَ","bc":"salāmun 'alā mụsā wa hārụn","at":"”Selamat sejahtera bagi Musa dan Harun.”"},{"id":"121","ar":"اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ","bc":"innā każālika najzil-muḥsinīn","at":"Demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik."},{"id":"122","ar":"اِنَّهُمَا مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ","bc":"innahumā min 'ibādinal-mu`minīn","at":"Sungguh, keduanya termasuk hamba-hamba Kami yang beriman."},{"id":"123","ar":"وَاِنَّ اِلْيَاسَ لَمِنَ الْمُرْسَلِيْنَۗ ","bc":"wa inna ilyāsa laminal-mursalīn","at":"Dan sungguh, Ilyas benar-benar termasuk salah seorang rasul."},{"id":"124","ar":"اِذْ قَالَ لِقَوْمِهٖٓ اَلَا تَتَّقُوْنَ","bc":"iż qāla liqaumihī alā tattaqụn","at":"(Ingatlah) ketika dia berkata kepada kaumnya, “Mengapa kamu tidak bertakwa?"},{"id":"125","ar":"اَتَدْعُوْنَ بَعْلًا وَّتَذَرُوْنَ اَحْسَنَ الْخَالِقِيْنَۙ","bc":"a tad'ụna ba'law wa tażarụna aḥsanal-khāliqīn","at":"Patutkah kamu menyembah Ba’l dan kamu tinggalkan (Allah) sebaik-baik pencipta."},{"id":"126","ar":"اللّٰهَ رَبَّكُمْ وَرَبَّ اٰبَاۤىِٕكُمُ الْاَوَّلِيْنَ","bc":"allāha rabbakum wa rabba ābā`ikumul-awwalīn","at":"(Yaitu) Allah Tuhanmu dan Tuhan nenek moyangmu yang terdahulu?”"},{"id":"127","ar":"فَكَذَّبُوْهُ فَاِنَّهُمْ لَمُحْضَرُوْنَۙ","bc":"fa każżabụhu fa innahum lamuḥḍarụn","at":"Tetapi mereka mendustakannya (Ilyas), maka sungguh, mereka akan diseret (ke neraka),"},{"id":"128","ar":"اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ","bc":"illā 'ibādallāhil-mukhlaṣīn","at":"kecuali hamba-hamba Allah yang disucikan (dari dosa),"},{"id":"129","ar":"وَتَرَكْنَا عَلَيْهِ فِى الْاٰخِرِيْنَ ۙ","bc":"wa taraknā 'alaihi fil-ākhirīn","at":"Dan Kami abadikan untuk Ilyas (pujian) di kalangan orang-orang yang datang kemudian."},{"id":"130","ar":"سَلٰمٌ عَلٰٓى اِلْ يَاسِيْنَ","bc":"salāmun 'alā ilyāsīn","at":"”Selamat sejahtera bagi Ilyas.”"},{"id":"131","ar":"اِنَّا كَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ","bc":"innā każālika najzil-muḥsinīn","at":"Demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik."},{"id":"132","ar":"اِنَّهٗ مِنْ عِبَادِنَا الْمُؤْمِنِيْنَ","bc":"innahụ min 'ibādinal-mu`minīn","at":"Sungguh, dia termasuk hamba-hamba Kami yang beriman."},{"id":"133","ar":"وَاِنَّ لُوْطًا لَّمِنَ الْمُرْسَلِيْنَۗ ","bc":"wa inna lụṭal laminal-mursalīn","at":"Dan sungguh, Lut benar-benar termasuk salah seorang rasul."},{"id":"134","ar":"اِذْ نَجَّيْنٰهُ وَاَهْلَهٗٓ اَجْمَعِيْۙنَ","bc":"iż najjaināhu wa ahlahū ajma'īn","at":"(Ingatlah) ketika Kami telah menyelamatkan dia dan pengikutnya semua,"},{"id":"135","ar":"اِلَّا عَجُوْزًا فِى الْغٰبِرِيْنَ","bc":"illā 'ajụzan fil-gābirīn","at":"kecuali seorang perempuan tua (istrinya) bersama-sama orang yang tinggal (di kota)."},{"id":"136","ar":"ثُمَّ دَمَّرْنَا الْاٰخَرِيْنَ","bc":"ṡumma dammarnal-ākharīn","at":"Kemudian Kami binasakan orang-orang yang lain."},{"id":"137","ar":"وَاِنَّكُمْ لَتَمُرُّوْنَ عَلَيْهِمْ مُّصْبِحِيْنَۙ","bc":"wa innakum latamurrụna 'alaihim muṣbiḥīn","at":"Dan sesungguhnya kamu (penduduk Mekah) benar-benar akan melalui (bekas-bekas) mereka pada waktu pagi,"},{"id":"138","ar":"وَبِالَّيْلِۗ اَفَلَا تَعْقِلُوْنَ ","bc":"wa bil-laīl, a fa lā ta'qilụn","at":"dan pada waktu malam. Maka mengapa kamu tidak mengerti?"},{"id":"139","ar":"وَاِنَّ يُوْنُسَ لَمِنَ الْمُرْسَلِيْنَۗ ","bc":"wa inna yụnusa laminal-mursalīn","at":"Dan sungguh, Yunus benar-benar termasuk salah seorang rasul,"},{"id":"140","ar":"اِذْ اَبَقَ اِلَى الْفُلْكِ الْمَشْحُوْنِۙ","bc":"iż abaqa ilal-fulkil-masy-ḥụn","at":"(ingatlah) ketika dia lari, ke kapal yang penuh muatan,"},{"id":"141","ar":"فَسَاهَمَ فَكَانَ مِنَ الْمُدْحَضِيْنَۚ","bc":"fa sāhama fa kāna minal-mud-ḥaḍīn","at":"kemudian dia ikut diundi ternyata dia termasuk orang-orang yang kalah (dalam undian)."},{"id":"142","ar":"فَالْتَقَمَهُ الْحُوْتُ وَهُوَ مُلِيْمٌ ","bc":"faltaqamahul-ḥụtu wa huwa mulīm","at":"Maka dia ditelan oleh ikan besar dalam keadaan tercela."},{"id":"143","ar":"فَلَوْلَآ اَنَّهٗ كَانَ مِنَ الْمُسَبِّحِيْنَ ۙ","bc":"falau lā annahụ kāna minal-musabbiḥīn","at":"Maka sekiranya dia tidak termasuk orang yang banyak berzikir (bertasbih) kepada Allah,"},{"id":"144","ar":"لَلَبِثَ فِيْ بَطْنِهٖٓ اِلٰى يَوْمِ يُبْعَثُوْنَۚ ","bc":"lalabiṡa fī baṭnihī ilā yaumi yub'aṡụn","at":"niscaya dia akan tetap tinggal di perut (ikan itu) sampai hari kebangkitan."},{"id":"145","ar":"فَنَبَذْنٰهُ بِالْعَرَاۤءِ وَهُوَ سَقِيْمٌ ۚ ","bc":"fa nabażnāhu bil-'arā`i wa huwa saqīm","at":"Kemudian Kami lemparkan dia ke daratan yang tandus, sedang dia dalam keadaan sakit."},{"id":"146","ar":"وَاَنْۢبَتْنَا عَلَيْهِ شَجَرَةً مِّنْ يَّقْطِيْنٍۚ ","bc":"wa ambatnā 'alaihi syajaratam miy yaqṭīn","at":"Kemudian untuk dia Kami tumbuhkan sebatang pohon dari jenis labu."},{"id":"147","ar":"وَاَرْسَلْنٰهُ اِلٰى مِائَةِ اَلْفٍ اَوْ يَزِيْدُوْنَۚ ","bc":"wa arsalnāhu ilā mi`ati alfin au yazīdụn","at":"Dan Kami utus dia kepada seratus ribu (orang) atau lebih,"},{"id":"148","ar":"فَاٰمَنُوْا فَمَتَّعْنٰهُمْ اِلٰى حِيْنٍ","bc":"fa āmanụ fa matta'nāhum ilā ḥīn","at":"sehingga mereka beriman, karena itu Kami anugerahkan kenikmatan hidup kepada mereka hingga waktu tertentu."},{"id":"149","ar":"فَاسْتَفْتِهِمْ اَلِرَبِّكَ الْبَنَاتُ وَلَهُمُ الْبَنُوْنَۚ ","bc":"fastaftihim a lirabbikal-banātu wa lahumul-banụn","at":"Maka tanyakanlah (Muhammad) kepada mereka (orang-orang kafir Mekah), “Apakah anak-anak perempuan itu untuk Tuhanmu sedangkan untuk mereka anak-anak laki-laki?”"},{"id":"150","ar":"اَمْ خَلَقْنَا الْمَلٰۤىِٕكَةَ اِنَاثًا وَّهُمْ شَاهِدُوْنَ","bc":"am khalaqnal-malā`ikata ināṡaw wa hum syāhidụn","at":"atau apakah Kami menciptakan malaikat-malaikat berupa perempuan sedangkan mereka menyaksikan(nya)?"},{"id":"151","ar":"اَلَآ اِنَّهُمْ مِّنْ اِفْكِهِمْ لَيَقُوْلُوْنَۙ","bc":"alā innahum min ifkihim layaqụlụn","at":"Ingatlah, sesungguhnya di antara kebohongannya mereka benar-benar mengatakan,"},{"id":"152","ar":"وَلَدَ اللّٰهُ ۙوَاِنَّهُمْ لَكٰذِبُوْنَۙ","bc":"waladallāhu wa innahum lakāżibụn","at":"”Allah mempunyai anak.” Dan sungguh, mereka benar-benar pendusta,"},{"id":"153","ar":"اَصْطَفَى الْبَنَاتِ عَلَى الْبَنِيْنَۗ ","bc":"aṣṭafal-banāti 'alal-banīn","at":"apakah Dia (Allah) memilih anak-anak perempuan daripada anak-anak laki-laki?"},{"id":"154","ar":"مَا لَكُمْۗ كَيْفَ تَحْكُمُوْنَ","bc":"mā lakum, kaifa taḥkumụn","at":"Mengapa kamu ini? Bagaimana (caranya) kamu menetapkan?"},{"id":"155","ar":"اَفَلَا تَذَكَّرُوْنَۚ ","bc":"a fa lā tażakkarụn","at":"Maka mengapa kamu tidak memikirkan?"},{"id":"156","ar":"اَمْ لَكُمْ سُلْطٰنٌ مُّبِيْنٌۙ","bc":"am lakum sulṭānum mubīn","at":"Ataukah kamu mempunyai bukti yang jelas?"},{"id":"157","ar":"فَأْتُوْا بِكِتٰبِكُمْ اِنْ كُنْتُمْ صٰدِقِيْنَ","bc":"fa`tụ bikitābikum ing kuntum ṣādiqīn","at":"(Kalau begitu) maka bawalah kitabmu jika kamu orang yang benar."},{"id":"158","ar":"وَجَعَلُوْا بَيْنَهٗ وَبَيْنَ الْجِنَّةِ نَسَبًا ۗوَلَقَدْ عَلِمَتِ الْجِنَّةُ اِنَّهُمْ لَمُحْضَرُوْنَۙ","bc":"wa ja'alụ bainahụ wa bainal-jinnati nasabā, wa laqad 'alimatil-jinnatu innahum lamuḥḍarụn","at":"Dan mereka mengadakan (hubungan) nasab (keluarga) antara Dia (Allah) dan jin. Dan sungguh, jin telah mengetahui bahwa mereka pasti akan diseret (ke neraka),"},{"id":"159","ar":"سُبْحٰنَ اللّٰهِ عَمَّا يَصِفُوْنَۙ","bc":"sub-ḥanallāhi 'ammā yaṣifụn","at":"Mahasuci Allah dari apa yang mereka sifatkan,"},{"id":"160","ar":"اِلَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ","bc":"illā 'ibādallāhil-mukhlaṣīn","at":"kecuali hamba-hamba Allah yang disucikan (dari dosa)."},{"id":"161","ar":"فَاِنَّكُمْ وَمَا تَعْبُدُوْنَۙ","bc":"fa innakum wa mā ta'budụn","at":"Maka sesungguhnya kamu dan apa yang kamu sembah itu,"},{"id":"162","ar":"مَآ اَنْتُمْ عَلَيْهِ بِفَاتِنِيْنَۙ","bc":"mā antum 'alaihi bifātinīn","at":"tidak akan dapat menyesatkan (seseorang) terhadap Allah,"},{"id":"163","ar":"اِلَّا مَنْ هُوَ صَالِ الْجَحِيْمِ","bc":"illā man huwa ṣālil-jaḥīm","at":"kecuali orang-orang yang akan masuk ke neraka Jahim."},{"id":"164","ar":"وَمَا مِنَّآ اِلَّا لَهٗ مَقَامٌ مَّعْلُوْمٌۙ","bc":"wa mā minnā illā lahụ maqāmum ma'lụm","at":"Dan tidak satu pun di antara kami (malaikat) melainkan masing-masing mempunyai kedudukan tertentu,"},{"id":"165","ar":"وَاِنَّا لَنَحْنُ الصَّۤافُّوْنَۖ","bc":"wa innā lanaḥnuṣ-ṣāffụn","at":"dan sesungguhnya kami selalu teratur dalam barisan (dalam melaksanakan perintah Allah)."},{"id":"166","ar":"وَاِنَّا لَنَحْنُ الْمُسَبِّحُوْنَ","bc":"wa innā lanaḥnul-musabbiḥụn","at":"Dan sungguh, kami benar-benar terus bertasbih (kepada Allah)."},{"id":"167","ar":"وَاِنْ كَانُوْا لَيَقُوْلُوْنَۙ","bc":"wa ing kānụ layaqụlụn","at":"Dan sesungguhnya mereka (orang kafir Mekah) benar-benar pernah berkata,"},{"id":"168","ar":"لَوْ اَنَّ عِنْدَنَا ذِكْرًا مِّنَ الْاَوَّلِيْنَۙ","bc":"lau anna 'indanā żikram minal-awwalīn","at":"”Sekiranya di sisi kami ada sebuah kitab dari (kitab-kitab yang diturunkan) kepada orang-orang dahulu,"},{"id":"169","ar":"لَكُنَّا عِبَادَ اللّٰهِ الْمُخْلَصِيْنَ","bc":"lakunnā 'ibādallāhil-mukhlaṣīn","at":"tentu kami akan menjadi hamba Allah yang disucikan (dari dosa).”"},{"id":"170","ar":"فَكَفَرُوْا بِهٖۚ فَسَوْفَ يَعْلَمُوْنَ","bc":"fa kafarụ bih, fa saufa ya'lamụn","at":"Tetapi ternyata mereka mengingkarinya (Al-Qur'an); maka kelak mereka akan mengetahui (akibat keingkarannya itu)."},{"id":"171","ar":"وَلَقَدْ سَبَقَتْ كَلِمَتُنَا لِعِبَادِنَا الْمُرْسَلِيْنَ ۖ","bc":"wa laqad sabaqat kalimatunā li'ibādinal-mursalīn","at":"Dan sungguh, janji Kami telah tetap bagi hamba-hamba Kami yang menjadi rasul,"},{"id":"172","ar":"اِنَّهُمْ لَهُمُ الْمَنْصُوْرُوْنَۖ","bc":"innahum lahumul-manṣụrụn","at":"(yaitu) mereka itu pasti akan mendapat pertolongan."},{"id":"173","ar":"وَاِنَّ جُنْدَنَا لَهُمُ الْغٰلِبُوْنَ","bc":"wa inna jundanā lahumul-gālibụn","at":"Dan sesungguhnya bala tentara Kami itulah yang pasti menang."},{"id":"174","ar":"فَتَوَلَّ عَنْهُمْ حَتّٰى حِيْنٍۙ","bc":"fa tawalla 'an-hum ḥattā ḥīn","at":"Maka berpalinglah engkau (Muhammad) dari mereka sampai waktu tertentu,"},{"id":"175","ar":"وَّاَبْصِرْهُمْۗ فَسَوْفَ يُبْصِرُوْنَ","bc":"wa abṣir-hum, fa saufa yubṣirụn","at":"dan perlihatkanlah kepada mereka, maka kelak mereka akan melihat (azab itu)."},{"id":"176","ar":"اَفَبِعَذَابِنَا يَسْتَعْجِلُوْنَ","bc":"a fa bi'ażābinā yasta'jilụn","at":"Maka apakah mereka meminta agar azab Kami disegerakan?"},{"id":"177","ar":"فَاِذَا نَزَلَ بِسَاحَتِهِمْ فَسَاۤءَ صَبَاحُ الْمُنْذَرِيْنَ","bc":"fa iżā nazala bisāḥatihim fa sā`a ṣabāḥul-munżarīn","at":"Maka apabila (siksaan) itu turun di halaman mereka, maka sangat buruklah pagi hari bagi orang-orang yang diperingatkan itu."},{"id":"178","ar":"وَتَوَلَّ عَنْهُمْ حَتّٰى حِيْنٍۙ","bc":"wa tawalla 'an-hum ḥattā ḥīn","at":"Dan berpalinglah engkau dari mereka sampai waktu tertentu. "},{"id":"179","ar":"وَّاَبْصِرْۗ فَسَوْفَ يُبْصِرُوْنَ","bc":"wa abṣir, fa saufa yubṣirụn","at":"Dan perlihatkanlah, maka kelak mereka akan melihat (azab itu)."},{"id":"180","ar":"سُبْحٰنَ رَبِّكَ رَبِّ الْعِزَّةِ عَمَّا يَصِفُوْنَۚ","bc":"sub-ḥāna rabbika rabbil-'izzati 'ammā yaṣifụn","at":"Mahasuci Tuhanmu, Tuhan Yang Mahaperkasa dari sifat yang mereka katakan."},{"id":"181","ar":"وَسَلٰمٌ عَلَى الْمُرْسَلِيْنَۚ ","bc":"wa salāmun 'alal-mursalīn","at":"Dan selamat sejahtera bagi para rasul."},{"id":"182","ar":"وَالْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ","bc":"wal-ḥamdu lillāhi rabbil-'ālamīn","at":"Dan segala puji bagi Allah Tuhan seluruh alam."}],as_sajdah=[{"id":"1","ar":" الۤمّۤ ۗ ","bc":"alif lām mīm","at":"Alif Lam Mim."},{"id":"2","ar":"تَنْزِيْلُ الْكِتٰبِ لَا رَيْبَ فِيْهِ مِنْ رَّبِّ الْعٰلَمِيْنَۗ ","bc":"tanzīlul-kitābi lā raiba fīhi mir rabbil-'ālamīn","at":"Turunnya Al-Qur'an itu tidak ada keraguan padanya, (yaitu) dari Tuhan seluruh alam. "},{"id":"3","ar":"اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۚ بَلْ هُوَ الْحَقُّ مِنْ رَّبِّكَ لِتُنْذِرَ قَوْمًا مَّآ اَتٰىهُمْ مِّنْ نَّذِيْرٍ مِّنْ قَبْلِكَ لَعَلَّهُمْ يَهْتَدُوْنَ","bc":"am yaqụlụnaftarāh, bal huwal-ḥaqqu mir rabbika litunżira qaumam mā atāhum min nażīrim ming qablika la'allahum yahtadụn","at":"Tetapi mengapa mereka (orang kafir) mengatakan, “Dia (Muhammad) telah mengada-adakannya.” Tidak, Al-Qur'an itu kebenaran (yang datang) dari Tuhanmu, agar engkau memberi peringatan kepada kaum yang belum pernah didatangi orang yang memberi peringatan sebelum engkau; agar mereka mendapat petunjuk."},{"id":"4","ar":"اَللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِۗ مَا لَكُمْ مِّنْ دُوْنِهٖ مِنْ وَّلِيٍّ وَّلَا شَفِيْعٍۗ اَفَلَا تَتَذَكَّرُوْنَ","bc":"allāhullażī khalaqas-samāwāti wal-arḍa wa mā bainahumā fī sittati ayyāmin ṡummastawā 'alal-'arsy, mā lakum min dụnihī miw waliyyiw wa lā syafī', a fa lā tatażakkarụn","at":"Allah yang menciptakan langit dan bumi dan apa yang ada di antara keduanya dalam enam masa, kemudian Dia bersemayam di atas ‘Arsy. Bagimu tidak ada seorang pun penolong maupun pemberi syafaat selain Dia. Maka apakah kamu tidak memperhatikan?"},{"id":"5","ar":"يُدَبِّرُ الْاَمْرَ مِنَ السَّمَاۤءِ اِلَى الْاَرْضِ ثُمَّ يَعْرُجُ اِلَيْهِ فِيْ يَوْمٍ كَانَ مِقْدَارُهٗٓ اَلْفَ سَنَةٍ مِّمَّا تَعُدُّوْنَ","bc":"yudabbirul-amra minas-samā`i ilal-arḍi ṡumma ya'ruju ilaihi fī yauming kāna miqdāruhū alfa sanatim mimmā ta'uddụn","at":"Dia mengatur segala urusan dari langit ke bumi, kemudian (urusan) itu naik kepada-Nya dalam satu hari yang kadarnya (lamanya) adalah seribu tahun menurut perhitunganmu."},{"id":"6","ar":"ذٰلِكَ عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيْزُ الرَّحِيْمُۙ ","bc":"żālika 'ālimul-gaibi wasy-syahādatil-'azīzur-raḥīm","at":"Yang demikian itu, ialah Tuhan yang mengetahui yang gaib dan yang nyata, Yang Mahaperkasa, Maha Penyayang."},{"id":"7","ar":"الَّذِيْٓ اَحْسَنَ كُلَّ شَيْءٍ خَلَقَهٗ وَبَدَاَ خَلْقَ الْاِنْسَانِ مِنْ طِيْنٍ","bc":"allażī aḥsana kulla syai`in khalaqahụ wa bada`a khalqal-insāni min ṭīn","at":"Yang memperindah segala sesuatu yang Dia ciptakan dan yang memulai penciptaan manusia dari tanah,"},{"id":"8","ar":"ثُمَّ جَعَلَ نَسْلَهٗ مِنْ سُلٰلَةٍ مِّنْ مَّاۤءٍ مَّهِيْنٍ ۚ ","bc":"ṡumma ja'ala naslahụ min sulālatim mim mā`im mahīn","at":"kemudian Dia menjadikan keturunannya dari sari pati air yang hina (air mani)."},{"id":"9","ar":"ثُمَّ سَوّٰىهُ وَنَفَخَ فِيْهِ مِنْ رُّوْحِهٖ وَجَعَلَ لَكُمُ السَّمْعَ وَالْاَبْصَارَ وَالْاَفْـِٕدَةَۗ قَلِيْلًا مَّا تَشْكُرُوْنَ ","bc":"ṡumma sawwāhu wa nafakha fīhi mir rụḥihī wa ja'ala lakumus-sam'a wal-abṣāra wal-af`idah, qalīlam mā tasykurụn","at":"Kemudian Dia menyempurnakannya dan meniupkan roh (ciptaan)-Nya ke dalam (tubuh)nya dan Dia menjadikan pendengaran, penglihatan dan hati bagimu, (tetapi) sedikit sekali kamu bersyukur."},{"id":"10","ar":"وَقَالُوْٓا ءَاِذَا ضَلَلْنَا فِى الْاَرْضِ ءَاِنَّا لَفِيْ خَلْقٍ جَدِيْدٍ ەۗ بَلْ هُمْ بِلِقَاۤءِ رَبِّهِمْ كٰفِرُوْنَ ","bc":"wa qālū a iżā ḍalalnā fil-arḍi a innā lafī khalqin jadīd, bal hum biliqā`i rabbihim kāfirụn","at":"Dan mereka berkata, “Apakah apabila kami telah lenyap (hancur) di dalam tanah, kami akan berada dalam ciptaan yang baru?” Bahkan mereka mengingkari pertemuan dengan Tuhannya."},{"id":"11","ar":"۞ قُلْ يَتَوَفّٰىكُمْ مَّلَكُ الْمَوْتِ الَّذِيْ وُكِّلَ بِكُمْ ثُمَّ اِلٰى رَبِّكُمْ تُرْجَعُوْنَ ","bc":"qul yatawaffākum malakul-mautillażī wukkila bikum ṡumma ilā rabbikum turja'ụn","at":"Katakanlah, “Malaikat maut yang diserahi untuk (mencabut nyawa)mu akan mematikan kamu, kemudian kepada Tuhanmu, kamu akan dikembalikan.”"},{"id":"12","ar":"وَلَوْ تَرٰىٓ اِذِ الْمُجْرِمُوْنَ نَاكِسُوْا رُءُوْسِهِمْ عِنْدَ رَبِّهِمْۗ رَبَّنَآ اَبْصَرْنَا وَسَمِعْنَا فَارْجِعْنَا نَعْمَلْ صَالِحًا اِنَّا مُوْقِنُوْنَ ","bc":"walau tarā iżil-mujrimụna nākisụ ru`ụsihim 'inda rabbihim, rabbanā abṣarnā wa sami'nā farji'nā na'mal ṣāliḥan innā mụqinụn","at":"Dan (alangkah ngerinya), jika sekiranya kamu melihat orang-orang yang berdosa itu menundukkan kepalanya di hadapan Tuhannya, (mereka berkata), “Ya Tuhan kami, kami telah melihat dan mendengar, maka kembalikanlah kami (ke dunia), niscaya kami akan mengerjakan kebajikan. Sungguh, kami adalah orang-orang yang yakin.”"},{"id":"13","ar":"وَلَوْ شِئْنَا لَاٰتَيْنَا كُلَّ نَفْسٍ هُدٰىهَا وَلٰكِنْ حَقَّ الْقَوْلُ مِنِّيْ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَعِيْنَ ","bc":"walau syi`nā la`ātainā kulla nafsin hudāhā wa lākin ḥaqqal-qaulu minnī la`amla`anna jahannama minal-jinnati wan-nāsi ajma'īn","at":"Dan jika Kami menghendaki niscaya Kami berikan kepada setiap jiwa petunjuk (bagi)nya, tetapi telah ditetapkan perkataan (ketetapan) dari-Ku, “Pasti akan Aku penuhi neraka Jahanam dengan jin dan manusia bersama-sama."},{"id":"14","ar":"فَذُوْقُوْا بِمَا نَسِيْتُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَاۚ اِنَّا نَسِيْنٰكُمْ وَذُوْقُوْا عَذَابَ الْخُلْدِ بِمَا كُنْتُمْ تَعْمَلُوْنَ","bc":"fa żụqụ bimā nasītum liqā`a yaumikum hāżā, innā nasīnākum wa żụqụ 'ażābal-khuldi bimā kuntum ta'malụn","at":"Maka rasakanlah olehmu (azab ini) disebabkan kamu melalaikan pertemuan dengan harimu ini (hari Kiamat), sesungguhnya Kami pun melalaikan kamu dan rasakanlah azab yang kekal, atas apa yang telah kamu kerjakan.”"},{"id":"15","ar":"اِنَّمَا يُؤْمِنُ بِاٰيٰتِنَا الَّذِيْنَ اِذَا ذُكِّرُوْا بِهَا خَرُّوْا سُجَّدًا وَّسَبَّحُوْا بِحَمْدِ رَبِّهِمْ وَهُمْ لَا يَسْتَكْبِرُوْنَ ۩ ","bc":"innamā yu`minu bi`āyātinallażīna iżā żukkirụ bihā kharrụ sujjadaw wa sabbaḥụ biḥamdi rabbihim wa hum lā yastakbirụn","at":"Orang-orang yang beriman dengan ayat-ayat Kami, hanyalah orang-orang yang apabila diperingatkan dengannya (ayat-ayat Kami), mereka menyungkur sujud dan bertasbih serta memuji Tuhannya, dan mereka tidak menyombongkan diri."},{"id":"16","ar":"تَتَجَافٰى جُنُوْبُهُمْ عَنِ الْمَضَاجِعِ يَدْعُوْنَ رَبَّهُمْ خَوْفًا وَّطَمَعًاۖ وَّمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ","bc":"tatajāfā junụbuhum 'anil-maḍāji'i yad'ụna rabbahum khaufaw wa ṭama'aw wa mimmā razaqnāhum yunfiqụn","at":"Lambung mereka jauh dari tempat tidurnya, mereka berdoa kepada Tuhannya dengan rasa takut dan penuh harap, dan mereka menginfakkan sebagian dari rezeki yang Kami berikan kepada mereka."},{"id":"17","ar":"فَلَا تَعْلَمُ نَفْسٌ مَّآ اُخْفِيَ لَهُمْ مِّنْ قُرَّةِ اَعْيُنٍۚ جَزَاۤءًۢ بِمَا كَانُوْا يَعْمَلُوْنَ","bc":"fa lā ta'lamu nafsum mā ukhfiya lahum ming qurrati a'yun, jazā`am bimā kānụ ya'malụn","at":"Maka tidak seorang pun mengetahui apa yang disembunyikan untuk mereka yaitu (bermacam-macam nikmat) yang menyenangkan hati sebagai balasan terhadap apa yang mereka kerjakan."},{"id":"18","ar":"اَفَمَنْ كَانَ مُؤْمِنًا كَمَنْ كَانَ فَاسِقًاۗ لَا يَسْتَوٗنَ","bc":"a fa mang kāna mu`minang kamang kāna fāsiqā, lā yastawụn","at":"Maka apakah orang yang beriman seperti orang yang fasik (kafir)? Mereka tidak sama."},{"id":"19","ar":"اَمَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَلَهُمْ جَنّٰتُ الْمَأْوٰىۖ نُزُلًا ۢبِمَا كَانُوْا يَعْمَلُوْنَ","bc":"ammallażīna āmanụ wa 'amiluṣ-ṣāliḥāti fa lahum jannātul-ma`wā nuzulam bimā kānụ ya'malụn","at":"Adapun orang-orang yang beriman dan mengerjakan kebajikan, maka mereka akan mendapat surga-surga tempat kediaman, sebagai pahala atas apa yang telah mereka kerjakan."},{"id":"20","ar":"وَاَمَّا الَّذِيْنَ فَسَقُوْا فَمَأْوٰىهُمُ النَّارُ كُلَّمَآ اَرَادُوْٓا اَنْ يَّخْرُجُوْا مِنْهَآ اُعِيْدُوْا فِيْهَا وَقِيْلَ لَهُمْ ذُوْقُوْا عَذَابَ النَّارِ الَّذِيْ كُنْتُمْ بِهٖ تُكَذِّبُوْنَ","bc":"wa ammallażīna fasaqụ fa ma`wāhumun-nāru kullamā arādū ay yakhrujụ min-hā u'īdụ fīhā wa qīla lahum żụqụ 'ażāban-nārillażī kuntum bihī tukażżibụn","at":"Dan adapun orang-orang yang fasik (kafir), maka tempat kediaman mereka adalah neraka. Setiap kali mereka hendak keluar darinya, mereka dikembalikan (lagi) ke dalamnya dan dikatakan kepada mereka, “Rasakanlah azab neraka yang dahulu kamu dustakan.”"},{"id":"21","ar":"وَلَنُذِيْقَنَّهُمْ مِّنَ الْعَذَابِ الْاَدْنٰى دُوْنَ الْعَذَابِ الْاَكْبَرِ لَعَلَّهُمْ يَرْجِعُوْنَ","bc":"wa lanużīqannahum minal-'ażābil-adnā dụnal-'ażābil-akbari la'allahum yarji'ụn","at":"Dan pasti Kami timpakan kepada mereka sebagian siksa yang dekat (di dunia) sebelum azab yang lebih besar (di akhirat); agar mereka kembali (ke jalan yang benar)."},{"id":"22","ar":"وَمَنْ اَظْلَمُ مِمَّنْ ذُكِّرَ بِاٰيٰتِ رَبِّهٖ ثُمَّ اَعْرَضَ عَنْهَا ۗاِنَّا مِنَ الْمُجْرِمِيْنَ مُنْتَقِمُوْنَ","bc":"wa man aẓlamu mim man żukkira bi`āyāti rabbihī ṡumma a'raḍa 'an-hā, innā minal-mujrimīna muntaqimụn","at":"Dan siapakah yang lebih zalim daripada orang yang telah diperingatkan dengan ayat-ayat Tuhannya, kemudian dia berpaling darinya? Sungguh, Kami akan memberikan balasan kepada orang-orang yang berdosa."},{"id":"23","ar":"وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ فَلَا تَكُنْ فِيْ مِرْيَةٍ مِّنْ لِّقَاۤىِٕهٖ وَجَعَلْنٰهُ هُدًى لِّبَنِيْٓ اِسْرَاۤءِيْلَۚ","bc":"wa laqad ātainā mụsal-kitāba fa lā takun fī miryatim mil liqā`ihī wa ja'alnāhu hudal libanī isrā`īl","at":"Dan sungguh, telah Kami anugerahkan Kitab (Taurat) kepada Musa, maka janganlah engkau (Muhammad) ragu-ragu menerimanya (Al-Qur'an) dan Kami jadikan Kitab (Taurat) itu petunjuk bagi Bani Israil."},{"id":"24","ar":"وَجَعَلْنَا مِنْهُمْ اَىِٕمَّةً يَّهْدُوْنَ بِاَمْرِنَا لَمَّا صَبَرُوْاۗ وَكَانُوْا بِاٰيٰتِنَا يُوْقِنُوْنَ","bc":"wa ja'alnā min-hum a`immatay yahdụna bi`amrinā lammā ṣabarụ, wa kānụ bi`āyātinā yụqinụn","at":"Dan Kami jadikan di antara mereka itu pemimpin-pemimpin yang memberi petunjuk dengan perintah Kami selama mereka sabar. Mereka meyakini ayat-ayat Kami."},{"id":"25","ar":"اِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ","bc":"inna rabbaka huwa yafṣilu bainahum yaumal-qiyāmati fīmā kānụ fīhi yakhtalifụn","at":"Sungguh Tuhanmu, Dia yang memberikan keputusan di antara mereka pada hari Kiamat tentang apa yang dahulu mereka perselisihkan padanya."},{"id":"26","ar":"اَوَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِّنَ الْقُرُوْنِ يَمْشُوْنَ فِيْ مَسٰكِنِهِمْ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍۗ اَفَلَا يَسْمَعُوْنَ","bc":"a wa lam yahdi lahum kam ahlaknā ming qablihim minal-qurụni yamsyụna fī masākinihim, inna fī żālika la`āyāt, a fa lā yasma'ụn","at":"Dan tidakkah menjadi petunjuk bagi mereka, betapa banyak umat-umat sebelum mereka yang telah Kami binasakan, sedangkan mereka sendiri berjalan di tempat-tempat kediaman mereka itu. Sungguh, pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah). Apakah mereka tidak mendengarkan (memperhatikan)?"},{"id":"27","ar":"اَوَلَمْ يَرَوْا اَنَّا نَسُوْقُ الْمَاۤءَ اِلَى الْاَرْضِ الْجُرُزِ فَنُخْرِجُ بِهٖ زَرْعًا تَأْكُلُ مِنْهُ اَنْعَامُهُمْ وَاَنْفُسُهُمْۗ اَفَلَا يُبْصِرُوْنَ","bc":"a wa lam yarau annā nasụqul-mā`a ilal-arḍil-juruzi fa nukhriju bihī zar'an ta`kulu min-hu an'āmuhum wa anfusuhum, a fa lā yubṣirụn","at":"Dan tidakkah mereka memperhatikan, bahwa Kami mengarahkan (awan yang mengandung) air ke bumi yang tandus, lalu Kami tumbuhkan (dengan air hujan itu) tanam-tanaman sehingga hewan-hewan ternak mereka dan mereka sendiri dapat makan darinya. Maka mengapa mereka tidak memperhatikan?"},{"id":"28","ar":"وَيَقُوْلُوْنَ مَتٰى هٰذَا الْفَتْحُ اِنْ كُنْتُمْ صٰدِقِيْنَ","bc":"wa yaqụlụna matā hāżal-fat-ḥu ing kuntum ṣādiqīn","at":"Dan mereka bertanya, “Kapankah kemenangan itu (datang) jika engkau orang yang benar?”"},{"id":"29","ar":"قُلْ يَوْمَ الْفَتْحِ لَا يَنْفَعُ الَّذِيْنَ كَفَرُوْٓا اِيْمَانُهُمْ وَلَا هُمْ يُنْظَرُوْنَ","bc":"qul yaumal-fat-ḥi lā yanfa'ullażīna kafarū īmānuhum wa lā hum yunẓarụn","at":"Katakanlah, “Pada hari kemenangan itu, tidak berguna lagi bagi orang-orang kafir keimanan mereka dan mereka tidak diberi penangguhan.”"},{"id":"30","ar":"فَاَعْرِضْ عَنْهُمْ وَانْتَظِرْ اِنَّهُمْ مُّنْتَظِرُوْنَ","bc":"fa a'riḍ 'an-hum wantaẓir innahum muntaẓirụn","at":"Maka berpalinglah engkau dari mereka dan tunggulah, sesungguhnya mereka (juga) menunggu."}],asy_syams=[{"id":"1","ar":"وَالشَّمْسِ وَضُحٰىهَاۖ","bc":"wasy-syamsi wa ḍuḥāhā","at":"Demi matahari dan sinarnya pada pagi hari,"},{"id":"2","ar":"وَالْقَمَرِ اِذَا تَلٰىهَاۖ","bc":"wal-qamari iżā talāhā","at":"demi bulan apabila mengiringinya,"},{"id":"3","ar":"وَالنَّهَارِ اِذَا جَلّٰىهَاۖ","bc":"wan-nahāri iżā jallāhā","at":"demi siang apabila menampakkannya,"},{"id":"4","ar":"وَالَّيْلِ اِذَا يَغْشٰىهَاۖ","bc":"wal-laili iżā yagsyāhā","at":"demi malam apabila menutupinya (gelap gulita),"},{"id":"5","ar":"وَالسَّمَاۤءِ وَمَا بَنٰىهَاۖ","bc":"was-samā`i wa mā banāhā","at":"demi langit serta pembinaannya (yang menakjubkan),"},{"id":"6","ar":"وَالْاَرْضِ وَمَا طَحٰىهَاۖ","bc":"wal-arḍi wa mā ṭaḥāhā","at":"demi bumi serta penghamparannya,"},{"id":"7","ar":"وَنَفْسٍ وَّمَا سَوّٰىهَاۖ","bc":"wa nafsiw wa mā sawwāhā","at":"demi jiwa serta penyempurnaan (ciptaan)nya,"},{"id":"8","ar":"فَاَلْهَمَهَا فُجُوْرَهَا وَتَقْوٰىهَاۖ","bc":"fa al-hamahā fujụrahā wa taqwāhā","at":"maka Dia mengilhamkan kepadanya (jalan) kejahatan dan ketakwaannya,"},{"id":"9","ar":"قَدْ اَفْلَحَ مَنْ زَكّٰىهَاۖ","bc":"qad aflaḥa man zakkāhā","at":"sungguh beruntung orang yang menyucikannya (jiwa itu),"},{"id":"10","ar":"وَقَدْ خَابَ مَنْ دَسّٰىهَاۗ","bc":"wa qad khāba man dassāhā","at":"dan sungguh rugi orang yang mengotorinya."},{"id":"11","ar":"كَذَّبَتْ ثَمُوْدُ بِطَغْوٰىهَآ ۖ","bc":"każżabaṡ ṡamụdu biṭagwāhā","at":"(Kaum) samud telah mendustakan (rasulnya) karena mereka melampaui batas (zalim),"},{"id":"12","ar":"اِذِ انْۢبَعَثَ اَشْقٰىهَاۖ","bc":"iżimba'aṡa asyqāhā","at":"ketika bangkit orang yang paling celaka di antara mereka,"},{"id":"13","ar":"فَقَالَ لَهُمْ رَسُوْلُ اللّٰهِ نَاقَةَ اللّٰهِ وَسُقْيٰهَاۗ","bc":"fa qāla lahum rasụlullāhi nāqatallāhi wa suqyāhā","at":"lalu Rasul Allah (Saleh) berkata kepada mereka, “(Biarkanlah) unta betina dari Allah ini dengan minumannya.”"},{"id":"14","ar":"فَكَذَّبُوْهُ فَعَقَرُوْهَاۖ فَدَمْدَمَ عَلَيْهِمْ رَبُّهُمْ بِذَنْۢبِهِمْ فَسَوّٰىهَاۖ","bc":"fa każżabụhu fa 'aqarụhā fa damdama 'alaihim rabbuhum biżambihim fa sawwāhā","at":"Namun mereka mendustakannya dan menyembelihnya, karena itu Tuhan membinasakan mereka karena dosanya, lalu diratakan-Nya (dengan tanah),"},{"id":"15","ar":"وَلَا يَخَافُ عُقْبٰهَا","bc":"wa lā yakhāfu 'uqbāhā","at":"dan Dia tidak takut terhadap akibatnya."}],asy_syarh=[{"id":"1","ar":"اَلَمْ نَشْرَحْ لَكَ صَدْرَكَۙ","bc":"a lam nasyraḥ laka ṣadrak","at":"Bukankah Kami telah melapangkan dadamu (Muhammad)?"},{"id":"2","ar":"وَوَضَعْنَا عَنْكَ وِزْرَكَۙ","bc":"wa waḍa'nā 'angka wizrak","at":"dan Kami pun telah menurunkan bebanmu darimu,"},{"id":"3","ar":"الَّذِيْٓ اَنْقَضَ ظَهْرَكَۙ","bc":"allażī angqaḍa ẓahrak","at":"yang memberatkan punggungmu, "},{"id":"4","ar":"وَرَفَعْنَا لَكَ ذِكْرَكَۗ","bc":"wa rafa'nā laka żikrak","at":"dan Kami tinggikan sebutan (nama)mu bagimu."},{"id":"5","ar":"فَاِنَّ مَعَ الْعُسْرِ يُسْرًاۙ","bc":"fa inna ma'al-'usri yusrā","at":"Maka sesungguhnya beserta kesulitan ada kemudahan,"},{"id":"6","ar":"اِنَّ مَعَ الْعُسْرِ يُسْرًاۗ","bc":"inna ma'al-'usri yusrā","at":"sesungguhnya beserta kesulitan itu ada kemudahan."},{"id":"7","ar":"فَاِذَا فَرَغْتَ فَانْصَبْۙ","bc":"fa iżā faragta fanṣab","at":"Maka apabila engkau telah selesai (dari sesuatu urusan), tetaplah bekerja keras (untuk urusan yang lain),"},{"id":"8","ar":"وَاِلٰى رَبِّكَ فَارْغَبْ","bc":"wa ilā rabbika fargab","at":"dan hanya kepada Tuhanmulah engkau berharap."}],asy_syuara=[{"id":"1","ar":"طٰسۤمّۤ ","bc":"ṭā sīm mīm","at":"Tha Sin Mim"},{"id":"2","ar":"تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِ","bc":"tilka āyātul-kitābil-mubīn","at":"Inilah ayat-ayat Kitab (Al-Qur'an) yang jelas."},{"id":"3","ar":"لَعَلَّكَ بَاخِعٌ نَّفْسَكَ اَلَّا يَكُوْنُوْا مُؤْمِنِيْنَ ","bc":"la'allaka bākhi'un nafsaka allā yakụnụ mu`minīn","at":"Boleh jadi engkau (Muhammad) akan membinasakan dirimu (dengan kesedihan), karena mereka (penduduk Mekah) tidak beriman."},{"id":"4","ar":"اِنْ نَّشَأْ نُنَزِّلْ عَلَيْهِمْ مِّنَ السَّمَاۤءِ اٰيَةً فَظَلَّتْ اَعْنَاقُهُمْ لَهَا خَاضِعِيْنَ ","bc":"in nasya` nunazzil 'alaihim minas-samā`i āyatan fa ẓallat a'nāquhum lahā khāḍi'īn","at":"Jika Kami menghendaki, niscaya Kami turunkan kepada mereka mukjizat dari langit, yang akan membuat tengkuk mereka tunduk dengan rendah hati kepadanya."},{"id":"5","ar":"وَمَا يَأْتِيْهِمْ مِّنْ ذِكْرٍ مِّنَ الرَّحْمٰنِ مُحْدَثٍ اِلَّا كَانُوْا عَنْهُ مُعْرِضِيْنَ ","bc":"wa mā ya`tīhim min żikrim minar-raḥmāni muḥdaṡin illā kānụ 'an-hu mu'riḍīn","at":"Dan setiap kali disampaikan kepada mereka suatu peringatan baru (ayat Al-Qur'an yang diturunkan) dari Tuhan Yang Maha Pengasih, mereka selalu berpaling darinya."},{"id":"6","ar":"فَقَدْ كَذَّبُوْا فَسَيَأْتِيْهِمْ اَنْۢبـٰۤؤُا مَا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ","bc":"fa qad każżabụ fa saya`tīhim ambā`u mā kānụ bihī yastahzi`ụn","at":"Sungguh, mereka telah mendustakan (Al-Qur'an), maka kelak akan datang kepada mereka (kebenaran) berita-berita mengenai apa (azab) yang dulu mereka perolok-olokkan."},{"id":"7","ar":"اَوَلَمْ يَرَوْا اِلَى الْاَرْضِ كَمْ اَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ زَوْجٍ كَرِيْمٍ ","bc":"a wa lam yarau ilal-arḍi kam ambatnā fīhā ming kulli zaujing karīm","at":"Dan apakah mereka tidak memperhatikan bumi, betapa banyak Kami tumbuhkan di bumi itu berbagai macam pasangan (tumbuh-tumbuhan) yang baik?"},{"id":"8","ar":"اِنَّ فِيْ ذٰلِكَ لَاٰيَةًۗ وَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ ","bc":"inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn","at":"Sungguh, pada yang demikian itu terdapat tanda (kebesaran Allah), tetapi kebanyakan mereka tidak beriman."},{"id":"9","ar":"وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ","bc":"wa inna rabbaka lahuwal-'azīzur-raḥīm","at":"Dan sungguh, Tuhanmu Dialah Yang Mahaperkasa, Maha Penyayang."},{"id":"10","ar":"وَاِذْ نَادٰى رَبُّكَ مُوْسٰٓى اَنِ ائْتِ الْقَوْمَ الظّٰلِمِيْنَ ۙ","bc":"wa iż nādā rabbuka mụsā ani`til-qaumaẓ-ẓālimīn","at":"Dan (ingatlah) ketika Tuhanmu menyeru Musa (dengan firman-Nya), “Datangilah kaum yang zalim itu,"},{"id":"11","ar":"قَوْمَ فِرْعَوْنَ ۗ اَلَا يَتَّقُوْنَ ","bc":"qauma fir'aụn, alā yattaqụn","at":"(yaitu) kaum Fir‘aun. Mengapa mereka tidak bertakwa?”"},{"id":"12","ar":"قَالَ رَبِّ اِنِّيْٓ اَخَافُ اَنْ يُّكَذِّبُوْنِ ۗ","bc":"qāla rabbi innī akhāfu ay yukażżibụn","at":"Dia (Musa) berkata, “Ya Tuhanku, sungguh, aku takut mereka akan mendustakan aku,"},{"id":"13","ar":"وَيَضِيْقُ صَدْرِيْ وَلَا يَنْطَلِقُ لِسَانِيْ فَاَرْسِلْ اِلٰى هٰرُوْنَ ","bc":"wa yaḍīqu ṣadrī wa lā yanṭaliqu lisānī fa arsil ilā hārụn","at":"sehingga dadaku terasa sempit dan lidahku tidak lancar, maka utuslah Harun (bersamaku)."},{"id":"14","ar":"وَلَهُمْ عَلَيَّ ذَنْۢبٌ فَاَخَافُ اَنْ يَّقْتُلُوْنِ ۚ","bc":"wa lahum 'alayya żambun fa akhāfu ay yaqtulụn","at":"Sebab aku berdosa terhadap mereka, maka aku takut mereka akan membunuhku.”"},{"id":"15","ar":"قَالَ كَلَّاۚ فَاذْهَبَا بِاٰيٰتِنَآ اِنَّا مَعَكُمْ مُّسْتَمِعُوْنَ ۙ ","bc":"qāla kallā, faż-habā bi`āyātinā innā ma'akum mustami'ụn","at":"(Allah) berfirman, “Jangan takut (mereka tidak akan dapat membunuhmu)! Maka pergilah kamu berdua dengan membawa ayat-ayat Kami (mukjizat-mukjizat); sungguh, Kami bersamamu mendengarkan (apa yang mereka katakan),"},{"id":"16","ar":"فَأْتِيَا فِرْعَوْنَ فَقُوْلَآ اِنَّا رَسُوْلُ رَبِّ الْعٰلَمِيْنَ ۙ","bc":"fa`tiyā fir'auna fa qụlā innā rasụlu rabbil-'ālamīn","at":"maka datanglah kamu berdua kepada Fir‘aun dan katakan, “Sesungguhnya kami adalah rasul-rasul Tuhan seluruh alam,"},{"id":"17","ar":"اَنْ اَرْسِلْ مَعَنَا بَنِيْٓ اِسْرَاۤءِيْلَ ۗ ","bc":"an arsil ma'anā banī isrā`īl","at":"lepaskanlah Bani Israil (pergi) bersama kami.”"},{"id":"18","ar":"قَالَ اَلَمْ نُرَبِّكَ فِيْنَا وَلِيْدًا وَّلَبِثْتَ فِيْنَا مِنْ عُمُرِكَ سِنِيْنَ ۗ ","bc":"qāla a lam nurabbika fīnā walīdaw wa labiṡta fīnā min 'umurika sinīn","at":"Dia (Fir‘aun) menjawab, “Bukankah kami telah mengasuhmu dalam lingkungan (keluarga) kami, waktu engkau masih kanak-kanak dan engkau tinggal bersama kami beberapa tahun dari umurmu."},{"id":"19","ar":"وَفَعَلْتَ فَعْلَتَكَ الَّتِيْ فَعَلْتَ وَاَنْتَ مِنَ الْكٰفِرِيْنَ ","bc":"wa fa'alta fa'latakallatī fa'alta wa anta minal-kāfirīn","at":"Dan engkau (Musa) telah melakukan (kesalahan dari) perbuatan yang telah engkau lakukan dan engkau termasuk orang yang tidak tahu berterima kasih.”"},{"id":"20","ar":"قَالَ فَعَلْتُهَآ اِذًا وَّاَنَا۠ مِنَ الضَّاۤلِّيْنَ ","bc":"qāla fa'altuhā iżaw wa ana minaḍ-ḍāllīn","at":"Dia (Musa) berkata, “Aku telah melakukannya, dan ketika itu aku termasuk orang yang khilaf."},{"id":"21","ar":"فَفَرَرْتُ مِنْكُمْ لَمَّا خِفْتُكُمْ فَوَهَبَ لِيْ رَبِّيْ حُكْمًا وَّجَعَلَنِيْ مِنَ الْمُرْسَلِيْنَ ","bc":"fa farartu mingkum lammā khiftukum fa wahaba lī rabbī ḥukmaw wa ja'alanī minal-mursalīn","at":"Lalu aku lari darimu karena aku takut kepadamu, kemudian Tuhanku menganugerahkan ilmu kepadaku serta Dia menjadikan aku salah seorang di antara rasul-rasul."},{"id":"22","ar":"وَتِلْكَ نِعْمَةٌ تَمُنُّهَا عَلَيَّ اَنْ عَبَّدْتَّ بَنِيْٓ اِسْرَاۤءِيْلَ ۗ","bc":"wa tilka ni'matun tamunnuhā 'alayya an 'abbatta banī isrā`īl","at":"Dan itulah kebaikan yang telah engkau berikan kepadaku, (sementara) itu engkau telah memperbudak Bani Israil.”"},{"id":"23","ar":"قَالَ فِرْعَوْنُ وَمَا رَبُّ الْعٰلَمِيْنَ ۗ ","bc":"qāla fir'aunu wa mā rabbul-'ālamīn","at":"Fir‘aun bertanya, “Siapa Tuhan seluruh alam itu?”"},{"id":"24","ar":"قَالَ رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَاۗ اِنْ كُنْتُمْ مُّوْقِنِيْنَ ","bc":"qāla rabbus-samāwāti wal-arḍi wa mā bainahumā, ing kuntum mụqinīn","at":"Dia (Musa) menjawab, “Tuhan pencipta langit dan bumi dan apa yang ada di antara keduanya (itulah Tuhanmu), jika kamu mempercayai-Nya.”"},{"id":"25","ar":"قَالَ لِمَنْ حَوْلَهٗٓ اَلَا تَسْتَمِعُوْنَ ","bc":"qāla liman ḥaulahū alā tastami'ụn","at":"Dia (Fir‘aun) berkata kepada orang-orang di sekelilingnya, “Apakah kamu tidak mendengar (apa yang dikatakannya)?”"},{"id":"26","ar":"قَالَ رَبُّكُمْ وَرَبُّ اٰبَاۤىِٕكُمُ الْاَوَّلِيْنَ ","bc":"qāla rabbukum wa rabbu ābā`ikumul-awwalīn","at":"Dia (Musa) berkata, “(Dia) Tuhanmu dan juga Tuhan nenek moyangmu terdahulu.”"},{"id":"27","ar":"قَالَ اِنَّ رَسُوْلَكُمُ الَّذِيْٓ اُرْسِلَ اِلَيْكُمْ لَمَجْنُوْنٌ ","bc":"qāla inna rasụlakumullażī ursila ilaikum lamajnụn","at":"Dia (Fir‘aun) berkata, “Sungguh, Rasulmu yang diutus kepada kamu benar-benar orang gila.” "},{"id":"28","ar":"قَالَ رَبُّ الْمَشْرِقِ وَالْمَغْرِبِ وَمَا بَيْنَهُمَاۗ اِنْ كُنْتُمْ تَعْقِلُوْنَ ","bc":"qāla rabbul-masyriqi wal-magribi wa mā bainahumā, ing kuntum ta'qilụn","at":"Dia (Musa) berkata, “(Dialah) Tuhan (yang menguasai) timur dan barat dan apa yang ada di antara keduanya; jika kamu mengerti.”"},{"id":"29","ar":"قَالَ لَىِٕنِ اتَّخَذْتَ اِلٰهًا غَيْرِيْ لَاَجْعَلَنَّكَ مِنَ الْمَسْجُوْنِيْنَ ","bc":"qāla la`inittakhażta ilāhan gairī la`aj'alannaka minal-masjụnīn","at":"Dia (Fir‘aun) berkata, “Sungguh, jika engkau menyembah Tuhan selain aku, pasti aku masukkan engkau ke dalam penjara.”"},{"id":"30","ar":"قَالَ اَوَلَوْ جِئْتُكَ بِشَيْءٍ مُّبِيْنٍ ","bc":"qāla a walau ji`tuka bisyai`im mubīn","at":"Dia (Musa) berkata, “Apakah (engkau akan melakukan itu) sekalipun aku tunjukkan kepadamu sesuatu (bukti) yang nyata?”"},{"id":"31","ar":"قَالَ فَأْتِ بِهٖٓ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ","bc":"qāla fa`ti bihī ing kunta minaṣ-ṣādiqīn","at":"Dia (Fir‘aun) berkata, “Tunjukkan sesuatu (bukti yang nyata) itu, jika engkau termasuk orang yang benar!”"},{"id":"32","ar":"فَاَلْقٰى عَصَاهُ فَاِذَا هِيَ ثُعْبَانٌ مُّبِيْنٌ ۚ ","bc":"fa alqā 'aṣāhu fa iżā hiya ṡu'bānum mubīn","at":"Maka dia (Musa) melemparkan tongkatnya, tiba-tiba tongkat itu menjadi ular besar yang sebenarnya."},{"id":"33","ar":"وَنَزَعَ يَدَهٗ فَاِذَا هِيَ بَيْضَاۤءُ لِلنّٰظِرِيْنَ ","bc":"wa naza'a yadahụ fa iżā hiya baiḍā`u lin-nāẓirīn","at":"Dan dia mengeluarkan tangannya (dari dalam bajunya), tiba-tiba tangan itu menjadi putih (bercahaya) bagi orang-orang yang melihatnya."},{"id":"34","ar":"قَالَ لِلْمَلَاِ حَوْلَهٗٓ اِنَّ هٰذَا لَسٰحِرٌ عَلِيْمٌ ۙ ","bc":"qāla lil-mala`i ḥaulahū inna hāżā lasāḥirun 'alīm","at":"Dia (Fir‘aun) berkata kepada para pemuka di sekelilingnya, “Sesungguhnya dia (Musa) ini pasti seorang pesihir yang pandai,"},{"id":"35","ar":"يُّرِيْدُ اَنْ يُّخْرِجَكُمْ مِّنْ اَرْضِكُمْ بِسِحْرِهٖۖ فَمَاذَا تَأْمُرُوْنَ ","bc":"yurīdu ay yukhrijakum min arḍikum bisiḥrihī fa māżā ta`murụn","at":"dia hendak mengusir kamu dari negerimu dengan sihirnya; karena itu apakah yang kamu sarankan?”"},{"id":"36","ar":"قَالُوْٓا اَرْجِهْ وَاَخَاهُ وَابْعَثْ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَ ۙ ","bc":"qālū arjih wa akhāhu wab'aṡ fil-madā`ini ḥāsyirīn","at":"Mereka menjawab, “Tahanlah (untuk sementara) dia dan saudaranya, dan utuslah ke seluruh negeri orang-orang yang akan mengumpulkan (pesihir),"},{"id":"37","ar":"يَأْتُوْكَ بِكُلِّ سَحَّارٍ عَلِيْمٍ ","bc":"ya`tụka bikulli saḥḥārin 'alīm","at":"niscaya mereka akan mendatangkan semua pesihir yang pandai kepadamu.” "},{"id":"38","ar":"فَجُمِعَ السَّحَرَةُ لِمِيْقَاتِ يَوْمٍ مَّعْلُوْمٍ ۙ ","bc":"fa jumi'as-saḥaratu limīqāti yaumim ma'lụm","at":"Lalu dikumpulkanlah para pesihir pada waktu (yang ditetapkan) pada hari yang telah ditentukan, "},{"id":"39","ar":"وَّقِيْلَ لِلنَّاسِ هَلْ اَنْتُمْ مُّجْتَمِعُوْنَ ۙ ","bc":"wa qīla lin-nāsi hal antum mujtami'ụn","at":"dan diumumkan kepada orang banyak, “Berkumpullah kamu semua,"},{"id":"40","ar":"لَعَلَّنَا نَتَّبِعُ السَّحَرَةَ اِنْ كَانُوْا هُمُ الْغٰلِبِيْنَ ","bc":"la'allanā nattabi'us-saḥarata ing kānụ humul-gālibīn","at":"agar kita mengikuti para pesihir itu, jika mereka yang menang.”"},{"id":"41","ar":"فَلَمَّا جَاۤءَ السَّحَرَةُ قَالُوْا لِفِرْعَوْنَ اَىِٕنَّ لَنَا لَاَجْرًا اِنْ كُنَّا نَحْنُ الْغٰلِبِيْنَ ","bc":"fa lammā jā`as-saḥaratu qālụ lifir'auna a inna lanā la`ajran ing kunnā naḥnul-gālibīn","at":"Maka ketika para pesihir datang, mereka berkata kepada Fir‘aun, “Apakah kami benar-benar akan mendapat imbalan yang besar jika kami yang menang?”"},{"id":"42","ar":"قَالَ نَعَمْ وَاِنَّكُمْ اِذًا لَّمِنَ الْمُقَرَّبِيْنَ ","bc":"qāla na'am wa innakum iżal laminal-muqarrabīn","at":"Dia (Fir‘aun) menjawab, “Ya, dan bahkan kamu pasti akan mendapat kedudukan yang dekat (kepadaku).”"},{"id":"43","ar":"قَالَ لَهُمْ مُّوْسٰٓى اَلْقُوْا مَآ اَنْتُمْ مُّلْقُوْنَ ","bc":"qāla lahum mụsā alqụ mā antum mulqụn","at":"Dia (Musa) berkata kepada mereka, “Lemparkanlah apa yang hendak kamu lemparkan.”"},{"id":"44","ar":"فَاَلْقَوْا حِبَالَهُمْ وَعِصِيَّهُمْ وَقَالُوْا بِعِزَّةِ فِرْعَوْنَ اِنَّا لَنَحْنُ الْغٰلِبُوْنَ ","bc":"fa alqau ḥibālahum wa 'iṣiyyahum wa qālụ bi'izzati fir'auna innā lanaḥnul-gālibụn","at":"Lalu mereka melemparkan tali temali dan tongkat-tongkat mereka seraya berkata, “Demi kekuasaan Fir‘aun, pasti kamilah yang akan menang.”"},{"id":"45","ar":"فَاَلْقٰى مُوْسٰى عَصَاهُ فَاِذَا هِيَ تَلْقَفُ مَا يَأْفِكُوْنَ ۚ ","bc":"fa alqā mụsā 'aṣāhu fa iżā hiya talqafu mā ya`fikụn","at":"Kemudian Musa melemparkan tongkatnya, maka tiba-tiba ia menelan benda-benda palsu yang mereka ada-adakan itu."},{"id":"46","ar":"فَاُلْقِيَ السَّحَرَةُ سٰجِدِيْنَ ۙ ","bc":"fa ulqiyas-saḥaratu sājidīn","at":"Maka menyungkurlah para pesihir itu, bersujud."},{"id":"47","ar":"قَالُوْٓا اٰمَنَّا بِرَبِّ الْعٰلَمِيْنَ ۙ ","bc":"qālū āmannā birabbil-'ālamīn","at":"Mereka berkata, “Kami beriman kepada Tuhan seluruh alam,"},{"id":"48","ar":"رَبِّ مُوْسٰى وَهٰرُوْنَ ","bc":"rabbi mụsā wa hārụn","at":"(yaitu) Tuhannya Musa dan Harun.”"},{"id":"49","ar":"قَالَ اٰمَنْتُمْ لَهٗ قَبْلَ اَنْ اٰذَنَ لَكُمْۚ اِنَّهٗ لَكَبِيْرُكُمُ الَّذِيْ عَلَّمَكُمُ السِّحْرَۚ فَلَسَوْفَ تَعْلَمُوْنَ ەۗ لَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ وَّلَاُصَلِّبَنَّكُمْ اَجْمَعِيْنَۚ","bc":"qāla āmantum lahụ qabla an āżana lakum, innahụ lakabīrukumullażī 'allamakumus-siḥr, fa lasaufa ta'lamụn, la`uqaṭṭi'anna aidiyakum wa arjulakum min khilāfiw wa la`uṣallibannakum ajma'īn","at":"Dia (Fir‘aun) berkata, “Mengapa kamu beriman kepada Musa sebelum aku memberi izin kepadamu? Sesungguhnya dia pemimpinmu yang mengajarkan sihir kepadamu. Nanti kamu pasti akan tahu (akibat perbuatanmu). Pasti akan kupotong tangan dan kakimu bersilang dan sungguh, akan kusalib kamu semuanya.”"},{"id":"50","ar":"قَالُوْا لَا ضَيْرَ ۖاِنَّآ اِلٰى رَبِّنَا مُنْقَلِبُوْنَ ۚ ","bc":"qālụ lā ḍaira innā ilā rabbinā mungqalibụn","at":"Mereka berkata, “Tidak ada yang kami takutkan, karena kami akan kembali kepada Tuhan kami."},{"id":"51","ar":" اِنَّا نَطْمَعُ اَنْ يَّغْفِرَ لَنَا رَبُّنَا خَطٰيٰنَآ اَنْ كُنَّآ اَوَّلَ الْمُؤْمِنِيْنَ ۗ","bc":"innā naṭma'u ay yagfira lanā rabbunā khaṭāyānā ang kunnā awwalal-mu`minīn","at":"Sesungguhnya kami sangat menginginkan sekiranya Tuhan kami akan mengampuni kesalahan kami, karena kami menjadi orang yang pertama-tama beriman.”"},{"id":"52","ar":"۞ وَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَسْرِ بِعِبَادِيْٓ اِنَّكُمْ مُّتَّبَعُوْنَ ","bc":"wa auḥainā ilā mụsā an asri bi'ibādī innakum muttaba'ụn","at":"Dan Kami wahyukan (perintahkan) kepada Musa, “Pergilah pada malam hari dengan membawa hamba-hamba-Ku (Bani Israil), sebab pasti kamu akan dikejar.”"},{"id":"53","ar":"فَاَرْسَلَ فِرْعَوْنُ فِى الْمَدَاۤىِٕنِ حٰشِرِيْنَ ۚ ","bc":"fa arsala fir'aunu fil-madā`ini ḥāsyirīn","at":"Kemudian Fir‘aun mengirimkan orang ke kota-kota (untuk mengumpulkan bala tentaranya)."},{"id":"54","ar":"اِنَّ هٰٓؤُلَاۤءِ لَشِرْذِمَةٌ قَلِيْلُوْنَۙ ","bc":"inna hā`ulā`i lasyirżimatung qalīlụn","at":"(Fir‘aun berkata), “Sesungguhnya mereka (Bani Israil) hanya sekelompok kecil,"},{"id":"55","ar":"وَاِنَّهُمْ لَنَا لَغَاۤىِٕظُوْنَ ۙ ","bc":"wa innahum lanā lagā`iẓụn","at":"dan sesungguhnya mereka telah berbuat hal-hal yang menimbulkan amarah kita,"},{"id":"56","ar":"وَاِنَّا لَجَمِيْعٌ حٰذِرُوْنَ ۗ ","bc":"wa innā lajamī'un ḥāżirụn","at":"dan sesungguhnya kita semua tanpa kecuali harus selalu waspada.”"},{"id":"57","ar":"فَاَخْرَجْنٰهُمْ مِّنْ جَنّٰتٍ وَّعُيُوْنٍ ۙ ","bc":"fa akhrajnāhum min jannātiw wa 'uyụn","at":"Kemudian, Kami keluarkan mereka (Fir‘aun dan kaumnya) dari taman-taman dan mata air,"},{"id":"58","ar":"وَّكُنُوْزٍ وَّمَقَامٍ كَرِيْمٍ ۙ ","bc":"wa kunụziw wa maqāming karīm","at":"dan (dari) harta kekayaan dan kedudukan yang mulia, "},{"id":"59","ar":"كَذٰلِكَۚ وَاَوْرَثْنٰهَا بَنِيْٓ اِسْرَاۤءِيْلَ ۗ ","bc":"każālik, wa auraṡnāhā banī isrā`īl","at":"demikianlah, dan Kami anugerahkan semuanya (itu) kepada Bani Israil."},{"id":"60","ar":"فَاَتْبَعُوْهُمْ مُّشْرِقِيْنَ ","bc":"fa atba'ụhum musyriqīn","at":"Lalu (Fir‘aun dan bala tentaranya) dapat menyusul mereka pada waktu matahari terbit."},{"id":"61","ar":"فَلَمَّا تَرَاۤءَ الْجَمْعٰنِ قَالَ اَصْحٰبُ مُوْسٰٓى اِنَّا لَمُدْرَكُوْنَ ۚ ","bc":"fa lammā tarā`al-jam'āni qāla aṣ-ḥābu mụsā innā lamudrakụn","at":"Maka ketika kedua golongan itu saling melihat, berkatalah pengikut-pengikut Musa, “Kita benar-benar akan tersusul.”"},{"id":"62","ar":"قَالَ كَلَّاۗ اِنَّ مَعِيَ رَبِّيْ سَيَهْدِيْنِ ","bc":"qāla kallā, inna ma'iya rabbī sayahdīn","at":"Dia (Musa) menjawab, “Sekali-kali tidak akan (tersusul); sesungguhnya Tuhanku bersamaku, Dia akan memberi petunjuk kepadaku.”"},{"id":"63","ar":"فَاَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنِ اضْرِبْ بِّعَصَاكَ الْبَحْرَۗ فَانْفَلَقَ فَكَانَ كُلُّ فِرْقٍ كَالطَّوْدِ الْعَظِيْمِ ۚ ","bc":"fa auḥainā ilā mụsā aniḍrib bi'aṣākal-baḥr, fanfalaqa fa kāna kullu firqing kaṭ-ṭaudil-'aẓīm","at":"Lalu Kami wahyukan kepada Musa, “Pukullah laut itu dengan tongkatmu.” Maka terbelahlah lautan itu, dan setiap belahan seperti gunung yang besar."},{"id":"64","ar":"وَاَزْلَفْنَا ثَمَّ الْاٰخَرِيْنَ ۚ ","bc":"wa azlafnā ṡammal-ākharīn","at":"Dan di sanalah Kami dekatkan golongan yang lain. "},{"id":"65","ar":"وَاَنْجَيْنَا مُوْسٰى وَمَنْ مَّعَهٗٓ اَجْمَعِيْنَ ۚ ","bc":"wa anjainā mụsā wa mam ma'ahū ajma'īn","at":"Dan Kami selamatkan Musa dan orang-orang yang bersamanya."},{"id":"66","ar":"ثُمَّ اَغْرَقْنَا الْاٰخَرِيْنَ ۗ ","bc":"ṡumma agraqnal-ākharīn","at":"Kemudian Kami tenggelamkan golongan yang lain."},{"id":"67","ar":"اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ ","bc":"inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn","at":"Sungguh, pada yang demikian itu terdapat suatu tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman."},{"id":"68","ar":"وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ","bc":"wa inna rabbaka lahuwal-'azīzur-raḥīm","at":"Dan sesungguhnya Tuhanmu Dialah Yang Mahaperkasa, Maha Penyayang."},{"id":"69","ar":"وَاتْلُ عَلَيْهِمْ نَبَاَ اِبْرٰهِيْمَ ۘ ","bc":"watlu 'alaihim naba`a ibrāhīm","at":"Dan bacakanlah kepada mereka kisah Ibrahim."},{"id":"70","ar":"اِذْ قَالَ لِاَبِيْهِ وَقَوْمِهٖ مَا تَعْبُدُوْنَ ","bc":"iż qāla li`abīhi wa qaumihī mā ta'budụn","at":"Ketika dia (Ibrahim) berkata kepada ayahnya dan kaumnya, “Apakah yang kamu sembah?”"},{"id":"71","ar":"قَالُوْا نَعْبُدُ اَصْنَامًا فَنَظَلُّ لَهَا عٰكِفِيْنَ ","bc":"qālụ na'budu aṣnāman fa naẓallu lahā 'ākifīn","at":"Mereka menjawab, “Kami menyembah berhala-berhala dan kami senantiasa tekun menyembahnya.”"},{"id":"72","ar":"قَالَ هَلْ يَسْمَعُوْنَكُمْ اِذْ تَدْعُوْنَ ۙ ","bc":"qāla hal yasma'ụnakum iż tad'ụn","at":"Dia (Ibrahim) berkata, “Apakah mereka mendengarmu ketika kamu berdoa (kepadanya)?"},{"id":"73","ar":"اَوْ يَنْفَعُوْنَكُمْ اَوْ يَضُرُّوْنَ ","bc":"au yanfa'ụnakum au yaḍurrụn","at":"Atau (dapatkah) mereka memberi manfaat atau mencelakakan kamu?”"},{"id":"74","ar":"قَالُوْا بَلْ وَجَدْنَآ اٰبَاۤءَنَا كَذٰلِكَ يَفْعَلُوْنَ ","bc":"qālụ bal wajadnā ābā`anā każālika yaf'alụn","at":"Mereka menjawab, “Tidak, tetapi kami dapati nenek moyang kami berbuat begitu.”"},{"id":"75","ar":"قَالَ اَفَرَءَيْتُمْ مَّا كُنْتُمْ تَعْبُدُوْنَ ۙ ","bc":"qāla a fa ra`aitum mā kuntum ta'budụn","at":"Dia (Ibrahim) berkata, “Apakah kamu memperhatikan apa yang kamu sembah,"},{"id":"76","ar":"اَنْتُمْ وَاٰبَاۤؤُكُمُ الْاَقْدَمُوْنَ ۙ ","bc":"antum wa ābā`ukumul-aqdamụn","at":"kamu dan nenek moyang kamu yang terdahulu?"},{"id":"77","ar":"فَاِنَّهُمْ عَدُوٌّ لِّيْٓ اِلَّا رَبَّ الْعٰلَمِيْنَ ۙ ","bc":"fa innahum 'aduwwul lī illā rabbal-'ālamīn","at":"Sesungguhnya mereka (apa yang kamu sembah) itu musuhku, lain halnya Tuhan seluruh alam,"},{"id":"78","ar":"الَّذِيْ خَلَقَنِيْ فَهُوَ يَهْدِيْنِ ۙ ","bc":"allażī khalaqanī fa huwa yahdīn","at":"(yaitu) Yang telah menciptakan aku, maka Dia yang memberi petunjuk kepadaku,"},{"id":"79","ar":"وَالَّذِيْ هُوَ يُطْعِمُنِيْ وَيَسْقِيْنِ ۙ ","bc":"wallażī huwa yuṭ'imunī wa yasqīn","at":"dan Yang memberi makan dan minum kepadaku;"},{"id":"80","ar":"وَاِذَا مَرِضْتُ فَهُوَ يَشْفِيْنِ ۙ ","bc":"wa iżā mariḍtu fa huwa yasyfīn","at":"dan apabila aku sakit, Dialah yang menyembuhkan aku, "},{"id":"81","ar":"وَالَّذِيْ يُمِيْتُنِيْ ثُمَّ يُحْيِيْنِ ۙ ","bc":"wallażī yumītunī ṡumma yuḥyīn","at":"dan Yang akan mematikan aku, kemudian akan menghidupkan aku (kembali),"},{"id":"82","ar":"وَالَّذِيْٓ اَطْمَعُ اَنْ يَّغْفِرَ لِيْ خَطِيْۤـَٔتِيْ يَوْمَ الدِّيْنِ ۗ","bc":"wallażī aṭma'u ay yagfira lī khaṭī`atī yaumad-dīn","at":"dan Yang sangat kuinginkan akan mengampuni kesalahanku pada hari Kiamat.”"},{"id":"83","ar":"رَبِّ هَبْ لِيْ حُكْمًا وَّاَلْحِقْنِيْ بِالصّٰلِحِيْنَ ۙ","bc":"rabbi hab lī ḥukmaw wa al-ḥiqnī biṣ-ṣāliḥīn","at":"Ibrahim berdoa), “Ya Tuhanku, berikanlah kepadaku ilmu dan masukkanlah aku ke dalam golongan orang-orang yang saleh,"},{"id":"84","ar":"وَاجْعَلْ لِّيْ لِسَانَ صِدْقٍ فِى الْاٰخِرِيْنَ ۙ ","bc":"waj'al lī lisāna ṣidqin fil-ākhirīn","at":"dan jadikanlah aku buah tutur yang baik bagi orang-orang (yang datang) kemudian,"},{"id":"85","ar":"وَاجْعَلْنِيْ مِنْ وَّرَثَةِ جَنَّةِ النَّعِيْمِ ۙ","bc":"waj'alnī miw waraṡati jannatin-na'īm","at":"dan jadikanlah aku termasuk orang yang mewarisi surga yang penuh kenikmatan,"},{"id":"86","ar":"وَاغْفِرْ لِاَبِيْٓ اِنَّهٗ كَانَ مِنَ الضَّاۤلِّيْنَ ۙ ","bc":"wagfir li`abī innahụ kāna minaḍ-ḍāllīn","at":"dan ampunilah ayahku, sesungguhnya dia termasuk orang yang sesat,"},{"id":"87","ar":"وَلَا تُخْزِنِيْ يَوْمَ يُبْعَثُوْنَۙ","bc":"wa lā tukhzinī yauma yub'aṡụn","at":"dan janganlah Engkau hinakan aku pada hari mereka dibangkitkan,"},{"id":"88","ar":"يَوْمَ لَا يَنْفَعُ مَالٌ وَّلَا بَنُوْنَ ۙ ","bc":"yauma lā yanfa'u māluw wa lā banụn","at":"(yaitu) pada hari (ketika) harta dan anak-anak tidak berguna,"},{"id":"89","ar":"اِلَّا مَنْ اَتَى اللّٰهَ بِقَلْبٍ سَلِيْمٍ ۗ ","bc":"illā man atallāha biqalbin salīm","at":"kecuali orang-orang yang menghadap Allah dengan hati yang bersih,"},{"id":"90","ar":"وَاُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِيْنَ ۙ ","bc":"wa uzlifatil-jannatu lil-muttaqīn","at":"dan surga didekatkan kepada orang-orang yang bertakwa,"},{"id":"91","ar":"وَبُرِّزَتِ الْجَحِيْمُ لِلْغٰوِيْنَ ۙ ","bc":"wa burrizatil-jaḥīmu lil-gāwīn","at":"dan neraka Jahim diperlihatkan dengan jelas kepada orang-orang yang sesat,”"},{"id":"92","ar":"وَقِيْلَ لَهُمْ اَيْنَ مَا كُنْتُمْ تَعْبُدُوْنَ ۙ ","bc":"wa qīla lahum aina mā kuntum ta'budụn","at":"dan dikatakan kepada mereka, “Di mana berhala-berhala yang dahulu kamu sembah,"},{"id":"93","ar":"مِنْ دُوْنِ اللّٰهِ ۗهَلْ يَنْصُرُوْنَكُمْ اَوْ يَنْتَصِرُوْنَ ۗ ","bc":"min dụnillāh, hal yanṣurụnakum au yantaṣirụn","at":"selain Allah? Dapatkah mereka menolong kamu atau menolong diri mereka sendiri?”"},{"id":"94","ar":"فَكُبْكِبُوْا فِيْهَا هُمْ وَالْغَاوٗنَ ۙ ","bc":"fakubkibụ fīhā hum wal-gāwụn","at":"Maka mereka (sesembahan itu) dijungkirkan ke dalam neraka bersama orang-orang yang sesat,"},{"id":"95","ar":"وَجُنُوْدُ اِبْلِيْسَ اَجْمَعُوْنَ ۗ ","bc":"wa junụdu iblīsa ajma'ụn","at":"dan bala tentara Iblis semuanya."},{"id":"96","ar":"قَالُوْا وَهُمْ فِيْهَا يَخْتَصِمُوْنَ ","bc":"qālụ wa hum fīhā yakhtaṣimụn","at":"Mereka berkata sambil bertengkar di dalamnya (neraka),"},{"id":"97","ar":"تَاللّٰهِ اِنْ كُنَّا لَفِيْ ضَلٰلٍ مُّبِيْنٍ ۙ ","bc":"tallāhi ing kunnā lafī ḍalālim mubīn","at":"”Demi Allah, sesungguhnya kita dahulu (di dunia) dalam kesesatan yang nyata,"},{"id":"98","ar":"اِذْ نُسَوِّيْكُمْ بِرَبِّ الْعٰلَمِيْنَ ","bc":"iż nusawwīkum birabbil-'ālamīn","at":"karena kita mempersamakan kamu (berhala-berhala) dengan Tuhan seluruh alam."},{"id":"99","ar":"وَمَآ اَضَلَّنَآ اِلَّا الْمُجْرِمُوْنَ ","bc":"wa mā aḍallanā illal-mujrimụn","at":"Dan tidak ada yang menyesatkan kita kecuali orang-orang yang berdosa."},{"id":"100","ar":"فَمَا لَنَا مِنْ شَافِعِيْنَ ۙ ","bc":"fa mā lanā min syāfi'īn","at":"Maka sehingga (sekarang) kita tidak mempunyai pemberi syafaat (penolong),"},{"id":"101","ar":"وَلَا صَدِيْقٍ حَمِيْمٍ ","bc":"wa lā ṣadīqin ḥamīm","at":"dan tidak pula mempunyai teman yang akrab,"},{"id":"102","ar":"فَلَوْ اَنَّ لَنَا كَرَّةً فَنَكُوْنَ مِنَ الْمُؤْمِنِيْنَ ","bc":"falau anna lanā karratan fa nakụna minal-mu`minīn","at":"Maka seandainya kita dapat kembali (ke dunia) niscaya kita menjadi orang-orang yang beriman.”"},{"id":"103","ar":"اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ ","bc":"inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn","at":"Sungguh, pada yang demikian itu terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman."},{"id":"104","ar":"وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ","bc":"wa inna rabbaka lahuwal-'azīzur-raḥīm","at":"Dan sungguh, Tuhanmu benar-benar Dialah Mahaperkasa, Maha Penyayang."},{"id":"105","ar":"كَذَّبَتْ قَوْمُ نُوْحِ ِۨالْمُرْسَلِيْنَ ۚ ","bc":"każżabat qaumu nụḥinil-mursalīn","at":"Kaum Nuh telah mendustakan para rasul."},{"id":"106","ar":"اِذْ قَالَ لَهُمْ اَخُوْهُمْ نُوْحٌ اَلَا تَتَّقُوْنَ ۚ ","bc":"iż qāla lahum akhụhum nụḥun alā tattaqụn","at":"Ketika saudara mereka (Nuh) berkata kepada mereka, “Mengapa kamu tidak bertakwa?"},{"id":"107","ar":"اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ ","bc":"innī lakum rasụlun amīn","at":"Sesungguhnya aku ini seorang rasul kepercayaan (yang diutus) kepadamu,"},{"id":"108","ar":"فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِۚ ","bc":"fattaqullāha wa aṭī'ụn","at":"Maka bertakwalah kamu kepada Allah dan taatlah kepadaku."},{"id":"109","ar":"وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۚ ","bc":"wa mā as`alukum 'alaihi min ajr, in ajriya illā 'alā rabbil-'ālamīn","at":"Dan aku tidak meminta imbalan kepadamu atas ajakan itu; imbalanku hanyalah dari Tuhan seluruh alam."},{"id":"110","ar":"فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ","bc":"fattaqullāha wa aṭī'ụn","at":"Maka bertakwalah kamu kepada Allah dan taatlah kepadaku.”"},{"id":"111","ar":"۞ قَالُوْٓا اَنُؤْمِنُ لَكَ وَاتَّبَعَكَ الْاَرْذَلُوْنَ ۗ ","bc":"qālū a nu`minu laka wattaba'akal-arżalụn","at":"Mereka berkata, “Apakah kami harus beriman kepadamu, padahal pengikut-pengikutmu orang-orang yang hina?”"},{"id":"112","ar":"قَالَ وَمَا عِلْمِيْ بِمَا كَانُوْا يَعْمَلُوْنَ ۚ ","bc":"qāla wa mā 'ilmī bimā kānụ ya'malụn","at":"Dia (Nuh) menjawab, “Tidak ada pengetahuanku tentang apa yang mereka kerjakan."},{"id":"113","ar":"اِنْ حِسَابُهُمْ اِلَّا عَلٰى رَبِّيْ لَوْ تَشْعُرُوْنَ ۚ ","bc":"in ḥisābuhum illā 'alā rabbī lau tasy'urụn","at":"Perhitungan (amal perbuatan) mereka tidak lain hanyalah kepada Tuhanku, jika kamu menyadari."},{"id":"114","ar":"وَمَآ اَنَا۠ بِطَارِدِ الْمُؤْمِنِيْنَ ۚ ","bc":"wa mā ana biṭāridil-mu`minīn","at":"Dan aku tidak akan mengusir orang-orang yang beriman."},{"id":"115","ar":"اِنْ اَنَا۠ اِلَّا نَذِيْرٌ مُّبِيْنٌ ۗ ","bc":"in ana illā nażīrum mubīn","at":"Aku (ini) hanyalah pemberi peringatan yang jelas.”"},{"id":"116","ar":"قَالُوْا لَىِٕنْ لَّمْ تَنْتَهِ يٰنُوْحُ لَتَكُوْنَنَّ مِنَ الْمَرْجُوْمِيْنَۗ ","bc":"qālụ la`il lam tantahi yā nụḥu latakụnanna minal-marjụmīn","at":"Mereka berkata, “Wahai Nuh! Sungguh, jika engkau tidak (mau) berhenti, niscaya engkau termasuk orang yang dirajam (dilempari batu sampai mati).”"},{"id":"117","ar":"قَالَ رَبِّ اِنَّ قَوْمِيْ كَذَّبُوْنِۖ ","bc":"qāla rabbi inna qaumī każżabụn","at":"Dia (Nuh) berkata, “Ya Tuhanku, sungguh kaumku telah mendustakan aku;"},{"id":"118","ar":"فَافْتَحْ بَيْنِيْ وَبَيْنَهُمْ فَتْحًا وَّنَجِّنِيْ وَمَنْ مَّعِيَ مِنَ الْمُؤْمِنِيْنَ ","bc":"faftaḥ bainī wa bainahum fat-ḥaw wa najjinī wa mam ma'iya minal-mu`minīn","at":"maka berilah keputusan antara aku dengan mereka, dan selamatkanlah aku dan mereka yang beriman bersamaku.” "},{"id":"119","ar":"فَاَنْجَيْنٰهُ وَمَنْ مَّعَهٗ فِى الْفُلْكِ الْمَشْحُوْنِ ","bc":"fa anjaināhu wa mam ma'ahụ fil-fulkil-masy-ḥụn","at":"Kemudian Kami menyelamatkannya Nuh dan orang-orang yang bersamanya di dalam kapal yang penuh muatan."},{"id":"120","ar":"ثُمَّ اَغْرَقْنَا بَعْدُ الْبٰقِيْنَ ","bc":"ṡumma agraqnā ba'dul-bāqīn","at":"Kemudian setelah itu Kami tenggelamkan orang-orang yang tinggal."},{"id":"121","ar":"اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ ","bc":"inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn","at":"Sungguh, pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman."},{"id":"122","ar":"وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ","bc":"wa inna rabbaka lahuwal-'azīzur-raḥīm","at":"Dan sungguh, Tuhanmu, Dialah Yang Mahaperkasa, Maha Penyayang."},{"id":"123","ar":"كَذَّبَتْ عَادُ ِۨالْمُرْسَلِيْنَ ۖ ","bc":"każżabat 'ādunil-mursalīn","at":"(Kaum) ‘Ad telah mendustakan para rasul."},{"id":"124","ar":"اِذْ قَالَ لَهُمْ اَخُوْهُمْ هُوْدٌ اَلَا تَتَّقُوْنَ ۚ ","bc":"iż qāla lahum akhụhum hụdun alā tattaqụn","at":"Ketika saudara mereka Hud berkata kepada mereka, “Mengapa kamu tidak bertakwa?"},{"id":"125","ar":"اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ ","bc":"innī lakum rasụlun amīn","at":"Sungguh, aku ini seorang rasul kepercayaan (yang diutus) kepadamu,"},{"id":"126","ar":"فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ","bc":"fattaqullāha wa aṭī'ụn","at":"karena itu bertakwalah kepada Allah dan taatlah kepadaku."},{"id":"127","ar":"وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ","bc":"wa mā as`alukum 'alaihi min ajr, in ajriya illā 'alā rabbil-'ālamīn","at":"Dan aku tidak meminta imbalan kepadamu atas ajakan itu; imbalanku hanyalah dari Tuhan seluruh alam."},{"id":"128","ar":"اَتَبْنُوْنَ بِكُلِّ رِيْعٍ اٰيَةً تَعْبَثُوْنَ ۙ ","bc":"a tabnụna bikulli rī'in āyatan ta'baṡụn","at":"Apakah kamu mendirikan istana-istana pada setiap tanah yang tinggi untuk kemegahan tanpa ditempati, "},{"id":"129","ar":"وَتَتَّخِذُوْنَ مَصَانِعَ لَعَلَّكُمْ تَخْلُدُوْنَۚ ","bc":"wa tattakhiżụna maṣāni'a la'allakum takhludụn","at":"dan kamu membuat benteng-benteng dengan harapan kamu hidup kekal?"},{"id":"130","ar":"وَاِذَا بَطَشْتُمْ بَطَشْتُمْ جَبَّارِيْنَۚ ","bc":"wa iżā baṭasytum baṭasytum jabbārīn","at":"Dan apabila kamu menyiksa, maka kamu lakukan secara kejam dan bengis."},{"id":"131","ar":"فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِۚ ","bc":"fattaqullāha wa aṭī'ụn","at":"Maka bertakwalah kepada Allah dan taatlah kepadaku,"},{"id":"132","ar":"وَاتَّقُوا الَّذِيْٓ اَمَدَّكُمْ بِمَا تَعْلَمُوْنَ ۚ","bc":"wattaqullażī amaddakum bimā ta'lamụn","at":"dan tetaplah kamu bertakwa kepada-Nya yang telah menganugerahkan kepadamu apa yang kamu ketahui."},{"id":"133","ar":"اَمَدَّكُمْ بِاَنْعَامٍ وَّبَنِيْنَۙ ","bc":"amaddakum bi`an'āmiw wa banīn","at":"Dia (Allah) telah menganugerahkan kepadamu hewan ternak dan anak-anak,"},{"id":"134","ar":"وَجَنّٰتٍ وَّعُيُوْنٍۚ ","bc":"wa jannātiw wa 'uyụn","at":"dan kebun-kebun, dan mata air,"},{"id":"135","ar":"اِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيْمٍ ۗ ","bc":"innī akhāfu 'alaikum 'ażāba yaumin 'aẓīm","at":"sesungguhnya aku takut kamu akan ditimpa azab pada hari yang besar.” "},{"id":"136","ar":"قَالُوْا سَوَاۤءٌ عَلَيْنَآ اَوَعَظْتَ اَمْ لَمْ تَكُنْ مِّنَ الْوَاعِظِيْنَ ۙ ","bc":"qālụ sawā`un 'alainā a wa'aẓta am lam takum minal-wā'iẓīn","at":"Mereka menjawab, “Sama saja bagi kami, apakah engkau memberi nasihat atau tidak memberi nasihat,"},{"id":"137","ar":"اِنْ هٰذَآ اِلَّا خُلُقُ الْاَوَّلِيْنَ ۙ ","bc":"in hāżā illā khuluqul-awwalīn","at":"(agama kami) ini tidak lain hanyalah adat kebiasaan orang-orang terdahulu,"},{"id":"138","ar":"وَمَا نَحْنُ بِمُعَذَّبِيْنَ ۚ ","bc":"wa mā naḥnu bimu'ażżabīn","at":"dan kami (sama sekali) tidak akan diazab.”"},{"id":"139","ar":"فَكَذَّبُوْهُ فَاَهْلَكْنٰهُمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ ","bc":"fa każżabụhu fa ahlaknāhum, inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn","at":"Maka mereka mendustakannya (Hud), lalu Kami binasakan mereka. Sungguh, pada yang demikian itu terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman."},{"id":"140","ar":"وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ","bc":"wa inna rabbaka lahuwal-'azīzur-raḥīm","at":"Dan sungguh, Tuhanmu, Dialah Yang Mahaperkasa, Maha Penyayang."},{"id":"141","ar":"كَذَّبَتْ ثَمُوْدُ الْمُرْسَلِيْنَ ۖ ","bc":"każżabat ṡamụdul-mursalīn","at":"Kaum Samud telah mendustakan para rasul."},{"id":"142","ar":"اِذْ قَالَ لَهُمْ اَخُوْهُمْ صٰلِحٌ اَلَا تَتَّقُوْنَ ۚ ","bc":"iż qāla lahum akhụhum ṣāliḥun alā tattaqụn","at":"Ketika saudara mereka Saleh berkata kepada mereka, “Mengapa kamu tidak bertakwa?"},{"id":"143","ar":"اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ ","bc":"innī lakum rasụlun amīn","at":"Sungguh, aku ini seorang rasul kepercayaan (yang diutus) kepadamu,"},{"id":"144","ar":"فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ","bc":"fattaqullāha wa aṭī'ụn","at":"karena itu bertakwalah kepada Allah dan taatlah kepadaku."},{"id":"145","ar":"وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍۚ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ","bc":"wa mā as`alukum 'alaihi min ajr, in ajriya illā 'alā rabbil-'ālamīn","at":"Dan aku tidak meminta sesuatu imbalan kepadamu atas ajakan itu, imbalanku hanyalah dari Tuhan seluruh alam."},{"id":"146","ar":"اَتُتْرَكُوْنَ فِيْ مَا هٰهُنَآ اٰمِنِيْنَ ۙ ","bc":"a tutrakụna fī mā hāhunā āminīn","at":"Apakah kamu (mengira) akan dibiarkan tinggal di sini (di negeri kamu ini) dengan aman,"},{"id":"147","ar":"فِيْ جَنّٰتٍ وَّعُيُوْنٍ ۙ ","bc":"fī jannātiw wa 'uyụn","at":"di dalam kebun-kebun dan mata air,"},{"id":"148","ar":"وَّزُرُوْعٍ وَّنَخْلٍ طَلْعُهَا هَضِيْمٌ ۚ ","bc":"wa zurụ'iw wa nakhlin ṭal'uhā haḍīm","at":"dan tanaman-tanaman dan pohon-pohon kurma yang mayangnya lembut."},{"id":"149","ar":"وَتَنْحِتُوْنَ مِنَ الْجِبَالِ بُيُوْتًا فٰرِهِيْنَ ","bc":"wa tan-ḥitụna minal-jibāli buyụtan fārihīn","at":"Dan kamu pahat dengan terampil sebagian gunung-gunung untuk dijadikan rumah-rumah;"},{"id":"150","ar":"فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ","bc":"fattaqullāha wa aṭī'ụn","at":"maka bertakwalah kepada Allah dan taatlah kepadaku;"},{"id":"151","ar":"وَلَا تُطِيْعُوْٓا اَمْرَ الْمُسْرِفِيْنَ ۙ ","bc":"wa lā tuṭī'ū amral-musrifīn","at":"dan janganlah kamu menaati perintah orang-orang yang melampaui batas,"},{"id":"152","ar":"الَّذِيْنَ يُفْسِدُوْنَ فِى الْاَرْضِ وَلَا يُصْلِحُوْنَ ","bc":"allażīna yufsidụna fil-arḍi wa lā yuṣliḥụn","at":"yang berbuat kerusakan di bumi dan tidak mengadakan perbaikan.”"},{"id":"153","ar":"قَالُوْٓا اِنَّمَآ اَنْتَ مِنَ الْمُسَحَّرِيْنَ ۙ ","bc":"qālū innamā anta minal-musaḥḥarīn","at":"Mereka berkata, “Sungguh, engkau hanyalah termasuk orang yang kena sihir;"},{"id":"154","ar":"مَآ اَنْتَ اِلَّا بَشَرٌ مِّثْلُنَاۙ فَأْتِ بِاٰيَةٍ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ","bc":"mā anta illā basyarum miṡlunā fa`ti bi`āyatin ing kunta minaṣ-ṣādiqīn","at":"engkau hanyalah manusia seperti kami; maka datangkanlah sesuatu mukjizat jika engkau termasuk orang yang benar.”"},{"id":"155","ar":"قَالَ هٰذِهٖ نَاقَةٌ لَّهَا شِرْبٌ وَّلَكُمْ شِرْبُ يَوْمٍ مَّعْلُوْمٍ ۚ ","bc":"qāla hāżihī nāqatul lahā syirbuw wa lakum syirbu yaumim ma'lụm","at":"Dia (Saleh) menjawab, “Ini seekor unta betina, yang berhak mendapatkan (giliran) minum, dan kamu juga berhak mendapatkan minum pada hari yang ditentukan."},{"id":"156","ar":"وَلَا تَمَسُّوْهَا بِسُوْۤءٍ فَيَأْخُذَكُمْ عَذَابُ يَوْمٍ عَظِيْمٍ ","bc":"wa lā tamassụhā bisū`in fa ya`khużakum 'ażābu yaumin 'aẓīm","at":"Dan jangan kamu menyentuhnya (unta itu) dengan sesuatu kejahatan, nanti kamu akan ditimpa azab pada hari yang dahsyat.”"},{"id":"157","ar":"فَعَقَرُوْهَا فَاَصْبَحُوْا نٰدِمِيْنَ ۙ ","bc":"fa 'aqarụhā fa aṣbaḥụ nādimīn","at":"Kemudian mereka membunuhnya, lalu mereka merasa menyesal,"},{"id":"158","ar":"فَاَخَذَهُمُ الْعَذَابُۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ ","bc":"fa akhażahumul-'ażāb, inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn","at":"maka mereka ditimpa azab. Sungguh, pada yang demikian itu terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman."},{"id":"159","ar":"وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ","bc":"wa inna rabbaka lahuwal-'azīzur-raḥīm","at":"Dan sungguh, Tuhanmu, Dialah Yang Mahaperkasa, Maha Penyayang."},{"id":"160","ar":"كَذَّبَتْ قَوْمُ لُوْطِ ِۨالْمُرْسَلِيْنَ ۖ ","bc":"każżabat qaumu lụṭinil-mursalīn","at":"Kaum Lut telah mendustakan para rasul,"},{"id":"161","ar":"اِذْ قَالَ لَهُمْ اَخُوْهُمْ لُوْطٌ اَلَا تَتَّقُوْنَ ۚ ","bc":"iż qāla lahum akhụhum lụṭun alā tattaqụn","at":"ketika saudara mereka Lut berkata kepada mereka, “Mengapa kamu tidak bertakwa?”"},{"id":"162","ar":"اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ ","bc":"innī lakum rasụlun amīn","at":"Sungguh, aku ini seorang rasul kepercayaan (yang diutus) kepadamu,"},{"id":"163","ar":"فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ","bc":"fattaqullāha wa aṭī'ụn","at":"maka bertakwalah kepada Allah dan taatlah kepadaku."},{"id":"164","ar":"وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ","bc":"wa mā as`alukum 'alaihi min ajrin in ajriya illā 'alā rabbil-'ālamīn","at":"Dan aku tidak meminta imbalan kepadamu atas ajakan itu; imbalanku hanyalah dari Tuhan seluruh alam."},{"id":"165","ar":"اَتَأْتُوْنَ الذُّكْرَانَ مِنَ الْعٰلَمِيْنَ ۙ ","bc":"a ta`tụnaż-żukrāna minal-'ālamīn","at":"Mengapa kamu mendatangi jenis laki-laki di antara manusia (berbuat homoseks),"},{"id":"166","ar":"وَتَذَرُوْنَ مَا خَلَقَ لَكُمْ رَبُّكُمْ مِّنْ اَزْوَاجِكُمْۗ بَلْ اَنْتُمْ قَوْمٌ عٰدُوْنَ ","bc":"wa tażarụna mā khalaqa lakum rabbukum min azwājikum, bal antum qaumun 'ādụn","at":"dan kamu tinggalkan (perempuan) yang diciptakan Tuhan untuk menjadi istri-istri kamu? Kamu (memang) orang-orang yang melampaui batas.” "},{"id":"167","ar":"قَالُوْا لَىِٕنْ لَّمْ تَنْتَهِ يٰلُوْطُ لَتَكُوْنَنَّ مِنَ الْمُخْرَجِيْنَ ","bc":"qālụ la`il lam tantahi yā lụṭu latakụnanna minal-mukhrajīn","at":"Mereka menjawab, “Wahai Lut! Jika engkau tidak berhenti, engkau termasuk orang-orang yang terusir.”"},{"id":"168","ar":"قَالَ ِانِّيْ لِعَمَلِكُمْ مِّنَ الْقَالِيْنَ ۗ","bc":"qāla innī li'amalikum minal-qālīn","at":"Dia (Lut) berkata, “Aku sungguh benci kepada perbuatanmu.”"},{"id":"169","ar":"رَبِّ نَجِّنِيْ وَاَهْلِيْ مِمَّا يَعْمَلُوْنَ ","bc":"rabbi najjinī wa ahlī mimmā ya'malụn","at":"(Lut berdoa), “Ya Tuhanku, selamatkanlah aku dan keluargaku dari (akibat) perbuatan yang mereka kerjakan.”"},{"id":"170","ar":"فَنَجَّيْنٰهُ وَاَهْلَهٗٓ اَجْمَعِيْنَ ۙ ","bc":"fa najjaināhu wa ahlahū ajma'īn","at":"Lalu Kami selamatkan dia bersama keluarganya semua,"},{"id":"171","ar":"اِلَّا عَجُوْزًا فِى الْغٰبِرِيْنَ ۚ ","bc":"illā 'ajụzan fil-gābirīn","at":"kecuali seorang perempuan tua (istrinya), yang termasuk dalam golongan yang tinggal."},{"id":"172","ar":"ثُمَّ دَمَّرْنَا الْاٰخَرِيْنَ ۚ ","bc":"ṡumma dammarnal-ākharīn","at":"Kemudian Kami binasakan yang lain."},{"id":"173","ar":"وَاَمْطَرْنَا عَلَيْهِمْ مَّطَرًاۚ فَسَاۤءَ مَطَرُ الْمُنْذَرِيْنَ ","bc":"wa amṭarnā 'alaihim maṭarā, fa sā`a maṭarul-munżarīn","at":"Dan Kami hujani mereka (dengan hujan batu), maka betapa buruk hujan yang menimpa orang-orang yang telah diberi peringatan itu."},{"id":"174","ar":"اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ ","bc":"inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn","at":"Sungguh, pada yang demikian itu terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman."},{"id":"175","ar":"وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ","bc":"wa inna rabbaka lahuwal-'azīzur-raḥīm","at":"Dan sungguh, Tuhanmu, Dialah Yang Mahaperkasa, Maha Penyayang."},{"id":"176","ar":"كَذَّبَ اَصْحٰبُ لْـَٔيْكَةِ الْمُرْسَلِيْنَ ۖ ","bc":"każżaba aṣ-ḥābul-aikatil-mursalīn","at":"Penduduk Aikah telah mendustakan para rasul;"},{"id":"177","ar":"اِذْ قَالَ لَهُمْ شُعَيْبٌ اَلَا تَتَّقُوْنَ ۚ ","bc":"iż qāla lahum syu'aibun alā tattaqụn","at":"ketika Syuaib berkata kepada mereka, “Mengapa kamu tidak bertakwa?"},{"id":"178","ar":"اِنِّيْ لَكُمْ رَسُوْلٌ اَمِيْنٌ ۙ ","bc":"innī lakum rasụlun amīn","at":"Sungguh, aku adalah rasul kepercayaan (yang diutus) kepadamu,"},{"id":"179","ar":"فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ۚ ","bc":"fattaqullāha wa aṭī'ụn","at":"maka bertakwalah kepada Allah dan taatlah kepadaku;"},{"id":"180","ar":"وَمَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ اِنْ اَجْرِيَ اِلَّا عَلٰى رَبِّ الْعٰلَمِيْنَ ۗ ","bc":"wa mā as`alukum 'alaihi min ajrin in ajriya illā 'alā rabbil-'ālamīn","at":"Dan aku tidak meminta imbalan kepadamu atas ajakan itu; imbalanku hanyalah dari Tuhan seluruh alam."},{"id":"181","ar":"۞ اَوْفُوا الْكَيْلَ وَلَا تَكُوْنُوْا مِنَ الْمُخْسِرِيْنَ ۚ ","bc":"auful-kaila wa lā takụnụ minal-mukhsirīn","at":"Sempurnakanlah takaran dan janganlah kamu merugikan orang lain."},{"id":"182","ar":"وَزِنُوْا بِالْقِسْطَاسِ الْمُسْتَقِيْمِ ۚ ","bc":"wa zinụ bil-qisṭāsil-mustaqīm","at":"Dan timbanglah dengan timbangan yang benar."},{"id":"183","ar":"وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ ۚ ","bc":"wa lā tabkhasun-nāsa asy-yā`ahum wa lā ta'ṡau fil-arḍi mufsidīn","at":"Dan janganlah kamu merugikan manusia dengan mengurangi hak-haknya dan janganlah membuat kerusakan di bumi;"},{"id":"184","ar":"وَاتَّقُوا الَّذِيْ خَلَقَكُمْ وَالْجِبِلَّةَ الْاَوَّلِيْنَ ۗ ","bc":"wattaqullażī khalaqakum wal-jibillatal-awwalīn","at":"dan bertakwalah kepada Allah yang telah menciptakan kamu dan umat-umat yang terdahulu.”"},{"id":"185","ar":"قَالُوْٓا اِنَّمَآ اَنْتَ مِنَ الْمُسَحَّرِيْنَ ۙ ","bc":"qālū innamā anta minal-musaḥḥarīn","at":"Mereka berkata, “Engkau tidak lain hanyalah orang-orang yang kena sihir."},{"id":"186","ar":"وَمَآ اَنْتَ اِلَّا بَشَرٌ مِّثْلُنَا وَاِنْ نَّظُنُّكَ لَمِنَ الْكٰذِبِيْنَ ۚ ","bc":"wa mā anta illā basyarum miṡlunā wa in naẓunnuka laminal-kāżibīn","at":"Dan engkau hanyalah manusia seperti kami, dan sesungguhnya kami yakin engkau termasuk orang-orang yang berdusta."},{"id":"187","ar":"فَاَسْقِطْ عَلَيْنَا كِسَفًا مِّنَ السَّمَاۤءِ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ ۗ ","bc":"fa asqiṭ 'alainā kisafam minas-samā`i ing kunta minaṣ-ṣādiqīn","at":"Maka jatuhkanlah kepada kami gumpalan dari langit, jika engkau termasuk orang-orang yang benar.”"},{"id":"188","ar":"قَالَ رَبِّيْٓ اَعْلَمُ بِمَا تَعْمَلُوْنَ ","bc":"qāla rabbī a'lamu bimā ta'malụn","at":"Dia (Syuaib) berkata, “Tuhanku lebih mengetahui apa yang kamu kerjakan.”"},{"id":"189","ar":"فَكَذَّبُوْهُ فَاَخَذَهُمْ عَذَابُ يَوْمِ الظُّلَّةِ ۗاِنَّهٗ كَانَ عَذَابَ يَوْمٍ عَظِيْمٍ ","bc":"fa każżabụhu fa akhażahum 'ażābu yaumiẓ-ẓullah, innahụ kāna 'ażāba yaumin 'aẓīm","at":"Kemudian mereka mendustakannya (Syuaib), lalu mereka ditimpa azab pada hari yang gelap. Sungguh, itulah azab pada hari yang dahsyat."},{"id":"190","ar":"اِنَّ فِيْ ذٰلِكَ لَاٰيَةً ۗوَمَا كَانَ اَكْثَرُهُمْ مُّؤْمِنِيْنَ ","bc":"inna fī żālika la`āyah, wa mā kāna akṡaruhum mu`minīn","at":"Sungguh, pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah), tetapi kebanyakan mereka tidak beriman."},{"id":"191","ar":"وَاِنَّ رَبَّكَ لَهُوَ الْعَزِيْزُ الرَّحِيْمُ ","bc":"wa inna rabbaka lahuwal-'azīzur-raḥīm","at":"Dan sungguh, Tuhanmu, Dialah yang Mahaperkasa, Maha Penyayang."},{"id":"192","ar":"وَاِنَّهٗ لَتَنْزِيْلُ رَبِّ الْعٰلَمِيْنَ ۗ ","bc":"wa innahụ latanzīlu rabbil-'ālamīn","at":"Dan sungguh, (Al-Qur'an) ini benar-benar diturunkan oleh Tuhan seluruh alam,"},{"id":"193","ar":"نَزَلَ بِهِ الرُّوْحُ الْاَمِيْنُ ۙ ","bc":"nazala bihir-rụḥul-amīn","at":"Yang dibawa turun oleh ar-Ruh al-Amin (Jibril), "},{"id":"194","ar":"عَلٰى قَلْبِكَ لِتَكُوْنَ مِنَ الْمُنْذِرِيْنَ ۙ ","bc":"'alā qalbika litakụna minal-munżirīn","at":"ke dalam hatimu (Muhammad) agar engkau termasuk orang yang memberi peringatan,"},{"id":"195","ar":"بِلِسَانٍ عَرَبِيٍّ مُّبِيْنٍ ۗ ","bc":"bilisānin 'arabiyyim mubīn","at":"dengan bahasa Arab yang jelas."},{"id":"196","ar":"وَاِنَّهٗ لَفِيْ زُبُرِ الْاَوَّلِيْنَ ","bc":"wa innahụ lafī zuburil-awwalīn","at":"Dan sungguh, (Al-Qur'an) itu (disebut) dalam kitab-kitab orang yang terdahulu."},{"id":"197","ar":"اَوَلَمْ يَكُنْ لَّهُمْ اٰيَةً اَنْ يَّعْلَمَهٗ عُلَمٰۤؤُا بَنِيْٓ اِسْرَاۤءِيْلَ ","bc":"a wa lam yakul lahum āyatan ay ya'lamahụ 'ulamā`u banī isrā`īl","at":"Apakah tidak (cukup) menjadi bukti bagi mereka, bahwa para ulama Bani Israil mengetahuinya?"},{"id":"198","ar":"وَلَوْ نَزَّلْنٰهُ عَلٰى بَعْضِ الْاَعْجَمِيْنَ ۙ ","bc":"walau nazzalnāhu 'alā ba'ḍil-a'jamīn","at":"Dan seandainya (Al-Qur'an) itu Kami turunkan kepada sebagian dari golongan bukan Arab,"},{"id":"199","ar":"فَقَرَاَهٗ عَلَيْهِمْ مَّا كَانُوْا بِهٖ مُؤْمِنِيْنَ ۗ ","bc":"faqara`ahụ 'alaihim mā kānụ bihī mu`minīn","at":"lalu dia membacakannya kepada mereka (orang-orang kafir); niscaya mereka tidak juga akan beriman kepadanya."},{"id":"200","ar":"كَذٰلِكَ سَلَكْنٰهُ فِيْ قُلُوْبِ الْمُجْرِمِيْنَ ۗ ","bc":"każālika salaknāhu fī qulụbil-mujrimīn","at":"Demikianlah, Kami masukkan (sifat dusta dan ingkar) ke dalam hati orang-orang yang berdosa,"},{"id":"201","ar":"لَا يُؤْمِنُوْنَ بِهٖ حَتّٰى يَرَوُا الْعَذَابَ الْاَلِيْمَ ","bc":"lā yu`minụna bihī ḥattā yarawul-'ażābal-alīm","at":"mereka tidak akan beriman kepadanya, hingga mereka melihat azab yang pedih,"},{"id":"202","ar":"فَيَأْتِيَهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ ۙ ","bc":"fa ya`tiyahum bagtataw wa hum lā yasy'urụn","at":"maka datang azab kepada mereka secara mendadak, ketika mereka tidak menyadarinya,"},{"id":"203","ar":"فَيَقُوْلُوْا هَلْ نَحْنُ مُنْظَرُوْنَ ۗ ","bc":"fa yaqụlụ hal naḥnu munẓarụn","at":"lalu mereka berkata, “Apakah kami diberi penangguhan waktu?” "},{"id":"204","ar":"اَفَبِعَذَابِنَا يَسْتَعْجِلُوْنَ ","bc":"a fa bi'ażābinā yasta'jilụn","at":"Bukankah mereka yang meminta agar azab Kami dipercepat?"},{"id":"205","ar":"اَفَرَءَيْتَ اِنْ مَّتَّعْنٰهُمْ سِنِيْنَ ۙ ","bc":"a fa ra`aita im matta'nāhum sinīn","at":"Maka bagaimana pendapatmu jika kepada mereka Kami berikan kenikmatan hidup beberapa tahun,"},{"id":"206","ar":"ثُمَّ جَاۤءَهُمْ مَّا كَانُوْا يُوْعَدُوْنَ ۙ ","bc":"ṡumma jā`ahum mā kānụ yụ'adụn","at":"kemudian datang kepada mereka azab yang diancamkan kepada mereka,"},{"id":"207","ar":"مَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يُمَتَّعُوْنَ ۗ ","bc":"mā agnā 'an-hum mā kānụ yumatta'ụn","at":"niscaya tidak berguna bagi mereka kenikmatan yang mereka rasakan."},{"id":"208","ar":"وَمَآ اَهْلَكْنَا مِنْ قَرْيَةٍ اِلَّا لَهَا مُنْذِرُوْنَ ۖ ","bc":"wa mā ahlaknā ming qaryatin illā lahā munżirụn","at":"Dan Kami tidak membinasakan sesuatu negeri, kecuali setelah ada orang-orang yang memberi peringatan kepadanya;"},{"id":"209","ar":"ذِكْرٰىۚ وَمَا كُنَّا ظٰلِمِيْنَ ","bc":"żikrā, wa mā kunnā ẓālimīn","at":"untuk (menjadi) peringatan. Dan Kami tidak berlaku zalim. "},{"id":"210","ar":"وَمَا تَنَزَّلَتْ بِهِ الشَّيٰطِيْنُ ","bc":"wa mā tanazzalat bihisy-syayāṭīn","at":"Dan (Al-Qur'an) itu tidaklah dibawa turun oleh setan-setan."},{"id":"211","ar":"وَمَا يَنْۢبَغِيْ لَهُمْ وَمَا يَسْتَطِيْعُوْنَ ۗ ","bc":"wa mā yambagī lahum wa mā yastaṭī'ụn","at":"Dan tidaklah pantas bagi mereka (Al-Qur'an itu), dan mereka pun tidak akan sanggup."},{"id":"212","ar":"اِنَّهُمْ عَنِ السَّمْعِ لَمَعْزُوْلُوْنَ ۗ ","bc":"innahum 'anis-sam'i lama'zụlụn","at":"Sesungguhnya untuk mendengarkannya pun mereka dijauhkan."},{"id":"213","ar":"فَلَا تَدْعُ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَتَكُوْنَ مِنَ الْمُعَذَّبِيْنَ","bc":"fa lā tad'u ma'allāhi ilāhan ākhara fa takụna minal-mu'ażżabīn","at":"Maka janganlah kamu menyeru (menyembah) tuhan selain Allah, nanti kamu termasuk orang-orang yang diazab."},{"id":"214","ar":"وَاَنْذِرْ عَشِيْرَتَكَ الْاَقْرَبِيْنَ ۙ ","bc":"wa anżir 'asyīratakal-aqrabīn","at":"Dan berilah peringatan kepada kerabat-kerabatmu (Muhammad) yang terdekat,"},{"id":"215","ar":"وَاخْفِضْ جَنَاحَكَ لِمَنِ اتَّبَعَكَ مِنَ الْمُؤْمِنِيْنَ ۚ ","bc":"wakhfiḍ janāḥaka limanittaba'aka minal-mu`minīn","at":"dan rendahkanlah dirimu terhadap orang-orang yang beriman yang mengikutimu."},{"id":"216","ar":"فَاِنْ عَصَوْكَ فَقُلْ اِنِّيْ بَرِيْۤءٌ مِّمَّا تَعْمَلُوْنَ ۚ ","bc":"fa in 'aṣauka fa qul innī barī`um mimmā ta'malụn","at":"Kemudian jika mereka mendurhakaimu maka katakanlah (Muhammad), “Sesungguhnya aku tidak bertanggung jawab terhadap apa yang kamu kerjakan.”"},{"id":"217","ar":"وَتَوَكَّلْ عَلَى الْعَزِيْزِ الرَّحِيْمِ ۙ ","bc":"wa tawakkal 'alal-'azīzir-raḥīm","at":"Dan bertawakallah kepada (Allah) Yang Mahaperkasa, Maha Penyayang."},{"id":"218","ar":"الَّذِيْ يَرٰىكَ حِيْنَ تَقُوْمُ ","bc":"allażī yarāka ḥīna taqụm","at":"Yang melihat engkau ketika engkau berdiri (untuk salat),"},{"id":"219","ar":"وَتَقَلُّبَكَ فِى السّٰجِدِيْنَ ","bc":"wa taqallubaka fis-sājidīn","at":"dan (melihat) perubahan gerakan badanmu di antara orang-orang yang sujud."},{"id":"220","ar":"اِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ ","bc":"innahụ huwas-samī'ul-'alīm","at":"Sungguh, Dia Maha Mendengar, Maha Mengetahui."},{"id":"221","ar":"هَلْ اُنَبِّئُكُمْ عَلٰى مَنْ تَنَزَّلُ الشَّيٰطِيْنُ ۗ ","bc":"hal unabbi`ukum 'alā man tanazzalusy-syayāṭīn","at":"Maukah Aku beritakan kepadamu, kepada siapa setan-setan itu turun?"},{"id":"222","ar":"تَنَزَّلُ عَلٰى كُلِّ اَفَّاكٍ اَثِيْمٍ ۙ ","bc":"tanazzalu 'alā kulli affākin aṡīm","at":"Mereka (setan) turun kepada setiap pendusta yang banyak berdosa,"},{"id":"223","ar":"يُّلْقُوْنَ السَّمْعَ وَاَكْثَرُهُمْ كٰذِبُوْنَ ۗ ","bc":"yulqụnas-sam'a wa akṡaruhum kāżibụn","at":"mereka menyampaikan hasil pendengaran mereka, sedangkan kebanyakan mereka orang-orang pendusta."},{"id":"224","ar":"وَالشُّعَرَاۤءُ يَتَّبِعُهُمُ الْغَاوٗنَ ۗ ","bc":"wasy-syu'arā`u yattabi'uhumul-gāwụn","at":"Dan penyair-penyair itu diikuti oleh orang-orang yang sesat."},{"id":"225","ar":"اَلَمْ تَرَ اَنَّهُمْ فِيْ كُلِّ وَادٍ يَّهِيْمُوْنَ ۙ ","bc":"a lam tara annahum fī kulli wādiy yahīmụn","at":"Tidakkah engkau melihat bahwa mereka mengembara di setiap lembah,"},{"id":"226","ar":"وَاَنَّهُمْ يَقُوْلُوْنَ مَا لَا يَفْعَلُوْنَ ۙ ","bc":"wa annahum yaqụlụna mā lā yaf'alụn","at":"dan bahwa mereka mengatakan apa yang mereka sendiri tidak mengerjakan(nya)?"},{"id":"227","ar":"اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَذَكَرُوا اللّٰهَ كَثِيْرًا وَّانْتَصَرُوْا مِنْۢ بَعْدِ مَا ظُلِمُوْا ۗوَسَيَعْلَمُ الَّذِيْنَ ظَلَمُوْٓا اَيَّ مُنْقَلَبٍ يَّنْقَلِبُوْنَ ","bc":"illallażīna āmanụ wa 'amiluṣ-ṣāliḥāti wa żakarullāha kaṡīraw wantaṣarụ mim ba'di mā ẓulimụ, wa saya'lamullażīna ẓalamū ayya mungqalabiy yangqalibụn","at":"Kecuali orang-orang (penyair-penyair) yang beriman dan berbuat kebajikan dan banyak mengingat Allah dan mendapat kemenangan setelah terzalimi (karena menjawab puisi-puisi orang-orang kafir). Dan orang-orang yang zalim kelak akan tahu ke tempat mana mereka akan kembali."}],asy_syura=[{"id":"1","ar":" حٰمۤ ۚ ","bc":"ḥā mīm","at":"Ha Mim"},{"id":"2","ar":"عۤسۤقۤ ۗ ","bc":"'Aīn sīn qāf","at":"Ain Sin Qaf"},{"id":"3","ar":"كَذٰلِكَ يُوْحِيْٓ اِلَيْكَ وَاِلَى الَّذِيْنَ مِنْ قَبْلِكَۙ اللّٰهُ الْعَزِيْزُ الْحَكِيْمُ ","bc":"każālika yụḥī ilaika wa ilallażīna ming qablikallāhul-'azīzul-ḥakīm","at":"Demikianlah Allah Yang Mahaperkasa, Mahabijaksana mewahyukan kepadamu (Muhammad) dan kepada orang-orang yang sebelummu."},{"id":"4","ar":"لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَهُوَ الْعَلِيُّ الْعَظِيْمُ ","bc":"lahụ mā fis-samāwāti wa mā fil-arḍ, wa huwal-'aliyyul-'aẓīm","at":"Milik-Nyalah apa yang ada di langit dan apa yang ada di bumi. Dan Dialah Yang Mahaagung, Mahabesar."},{"id":"5","ar":"تَكَادُ السَّمٰوٰتُ يَتَفَطَّرْنَ مِنْ فَوْقِهِنَّ وَالْمَلٰۤىِٕكَةُ يُسَبِّحُوْنَ بِحَمْدِ رَبِّهِمْ وَيَسْتَغْفِرُوْنَ لِمَنْ فِى الْاَرْضِۗ اَلَآ اِنَّ اللّٰهَ هُوَ الْغَفُوْرُ الرَّحِيْمُ ","bc":"takādus-samāwātu yatafaṭṭarna min fauqihinna wal-malā`ikatu yusabbiḥụna biḥamdi rabbihim wa yastagfirụna liman fil-arḍ, alā innallāha huwal-gafụrur-raḥīm","at":"Hampir saja langit itu pecah dari sebelah atasnya (karena kebesaran Allah) dan malaikat-malaikat bertasbih memuji Tuhannya dan memohonkan ampunan untuk orang yang ada di bumi. Ingatlah, sesungguhnya Allah Dialah Yang Maha Pengampun, Maha Penyayang."},{"id":"6","ar":"وَالَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَ اللّٰهُ حَفِيْظٌ عَلَيْهِمْۖ وَمَآ اَنْتَ عَلَيْهِمْ بِوَكِيْلٍ ","bc":"wallażīnattakhażụ min dụnihī auliyā`allāhu ḥafīẓun 'alaihim wa mā anta 'alaihim biwakīl","at":"Dan orang-orang yang mengambil pelindung-pelindung selain Allah, Allah mengawasi (perbuatan) mereka; adapun engkau (Muhammad) bukanlah orang yang diserahi mengawasi mereka."},{"id":"7","ar":"وَكَذٰلِكَ اَوْحَيْنَآ اِلَيْكَ قُرْاٰنًا عَرَبِيًّا لِّتُنْذِرَ اُمَّ الْقُرٰى وَمَنْ حَوْلَهَا وَتُنْذِرَ يَوْمَ الْجَمْعِ لَا رَيْبَ فِيْهِ ۗفَرِيْقٌ فِى الْجَنَّةِ وَفَرِيْقٌ فِى السَّعِيْرِ ","bc":"wa każālika auḥainā ilaika qur`ānan 'arabiyyal litunżira ummal-qurā wa man ḥaulahā wa tunżira yaumal-jam'i lā raiba fīh, farīqun fil-jannati wa farīqun fis-sa'īr","at":"Dan demikianlah Kami wahyukan Al-Qur'an kepadamu dalam bahasa Arab, agar engkau memberi peringatan kepada penduduk ibukota (Mekah) dan penduduk (negeri-negeri) di sekelilingnya serta memberi peringatan tentang hari berkumpul (Kiamat) yang tidak diragukan adanya. Segolongan masuk surga dan segolongan masuk neraka."},{"id":"8","ar":"وَلَوْ شَاۤءَ اللّٰهُ لَجَعَلَهُمْ اُمَّةً وَّاحِدَةً وَّلٰكِنْ يُّدْخِلُ مَنْ يَّشَاۤءُ فِيْ رَحْمَتِهٖۗ وَالظّٰلِمُوْنَ مَا لَهُمْ مِّنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ","bc":"walau syā`allāhu laja'alahum ummataw wāḥidataw wa lākiy yudkhilu may yasyā`u fī raḥmatih, waẓ-ẓālimụna mā lahum miw waliyyiw wa lā naṣīr","at":"Dan sekiranya Allah menghendaki niscaya Dia jadikan mereka satu umat, tetapi Dia memasukkan orang-orang yang Dia kehendaki ke dalam rahmat-Nya. Dan orang-orang yang zalim tidak ada bagi mereka pelindung dan penolong."},{"id":"9","ar":"اَمِ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَۚ فَاللّٰهُ هُوَ الْوَلِيُّ وَهُوَ يُحْيِ الْمَوْتٰى ۖوَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"amittakhażụ min dụnihī auliyā`, fallāhu huwal-waliyyu wa huwa yuḥyil-mautā wa huwa 'alā kulli syai`ing qadīr","at":"Atau mereka mengambil pelindung-pelindung selain Dia? Padahal Allah, Dialah pelindung (yang sebenarnya). Dan Dia menghidupkan orang yang mati, dan Dia Mahakuasa atas segala sesuatu."},{"id":"10","ar":"وَمَا اخْتَلَفْتُمْ فِيْهِ مِنْ شَيْءٍ فَحُكْمُهٗٓ اِلَى اللّٰهِ ۗذٰلِكُمُ اللّٰهُ رَبِّيْ عَلَيْهِ تَوَكَّلْتُۖ وَاِلَيْهِ اُنِيْبُ ","bc":"wa makhtalaftum fīhi min syai`in fa ḥukmuhū ilallāh, żālikumullāhu rabbī 'alaihi tawakkaltu wa ilaihi unīb","at":"Dan apa pun yang kamu perselisihkan padanya tentang sesuatu, keputusannya (terserah) kepada Allah. (Yang memiliki sifat-sifat demikian) itulah Allah Tuhanku. Kepada-Nya aku bertawakal dan kepada-Nya aku kembali."},{"id":"11","ar":"فَاطِرُ السَّمٰوٰتِ وَالْاَرْضِۗ جَعَلَ لَكُمْ مِّنْ اَنْفُسِكُمْ اَزْوَاجًا وَّمِنَ الْاَنْعَامِ اَزْوَاجًاۚ يَذْرَؤُكُمْ فِيْهِۗ لَيْسَ كَمِثْلِهٖ شَيْءٌ ۚوَهُوَ السَّمِيْعُ الْبَصِيْرُ ","bc":"fāṭirus-samāwāti wal-arḍ, ja'ala lakum min anfusikum azwājaw wa minal-an'āmi azwājā, yażra`ukum fīh, laisa kamiṡlihī syaī`, wa huwas-samī'ul-baṣīr","at":"(Allah) Pencipta langit dan bumi. Dia menjadikan bagi kamu pasangan-pasangan dari jenis kamu sendiri, dan dari jenis hewan ternak pasangan-pasangan (juga). Dijadikan-Nya kamu berkembang biak dengan jalan itu. Tidak ada sesuatu pun yang serupa dengan Dia. Dan Dia Yang Maha Mendengar, Maha Melihat."},{"id":"12","ar":"لَهٗ مَقَالِيْدُ السَّمٰوٰتِ وَالْاَرْضِۚ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۚاِنَّهٗ بِكُلِّ شَيْءٍ عَلِيْمٌ ","bc":"lahụ maqālīdus-samāwāti wal-arḍ, yabsuṭur-rizqa limay yasyā`u wa yaqdir, innahụ bikulli syai`in 'alīm","at":"Milik-Nyalah perbendaharaan langit dan bumi; Dia melapangkan rezeki dan membatasinya bagi siapa yang Dia kehendaki. Sungguh, Dia Maha Mengetahui segala sesuatu."},{"id":"13","ar":"۞ شَرَعَ لَكُمْ مِّنَ الدِّيْنِ مَا وَصّٰى بِهٖ نُوْحًا وَّالَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ وَمَا وَصَّيْنَا بِهٖٓ اِبْرٰهِيْمَ وَمُوْسٰى وَعِيْسٰٓى اَنْ اَقِيْمُوا الدِّيْنَ وَلَا تَتَفَرَّقُوْا فِيْهِۗ كَبُرَ عَلَى الْمُشْرِكِيْنَ مَا تَدْعُوْهُمْ اِلَيْهِۗ اَللّٰهُ يَجْتَبِيْٓ اِلَيْهِ مَنْ يَّشَاۤءُ وَيَهْدِيْٓ اِلَيْهِ مَنْ يُّنِيْبُۗ","bc":"syara'a lakum minad-dīni mā waṣṣā bihī nụḥaw wallażī auḥainā ilaika wa mā waṣṣainā bihī ibrāhīma wa mụsā wa 'īsā an aqīmud-dīna wa lā tatafarraqụ fīh, kabura 'alal-musyrikīna mā tad'ụhum ilaīh, allāhu yajtabī ilaihi may yasyā`u wa yahdī ilaihi may yunīb","at":"diwasiatkan-Nya kepada Nuh dan apa yang telah Kami wahyukan kepadamu (Muhammad) dan apa yang telah Kami wasiatkan kepada Ibrahim, Musa dan Isa yaitu tegakkanlah agama (keimanan dan ketakwaan) dan janganlah kamu berpecah belah di dalamnya. Sangat berat bagi orang-orang musyrik (untuk mengikuti) agama yang kamu serukan kepada mereka. Allah memilih orang yang Dia kehendaki kepada agama tauhid dan memberi petunjuk kepada (agama)-Nya bagi orang yang kembali (kepada-Nya)."},{"id":"14","ar":"وَمَا تَفَرَّقُوْٓا اِلَّا مِنْۢ بَعْدِ مَا جَاۤءَهُمُ الْعِلْمُ بَغْيًاۢ بَيْنَهُمْۗ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ اِلٰٓى اَجَلٍ مُّسَمًّى لَّقُضِيَ بَيْنَهُمْۗ وَاِنَّ الَّذِيْنَ اُوْرِثُوا الْكِتٰبَ مِنْۢ بَعْدِهِمْ لَفِيْ شَكٍّ مِّنْهُ مُرِيْبٍ ","bc":"wa mā tafarraqū illā mim ba'di mā jā`ahumul-'ilmu bagyam bainahum, walau lā kalimatun sabaqat mir rabbika ilā ajalim musammal laquḍiya bainahum, wa innallażīna ụriṡul-kitāba mim ba'dihim lafī syakkim min-hu murīb","at":"Dan mereka (Ahli Kitab) tidak berpecah belah kecuali setelah datang kepada mereka ilmu (kebenaran yang disampaikan oleh para nabi) karena kedengkian antara sesama mereka. Jika tidaklah karena suatu ketetapan yang telah ada dahulunya dari Tuhanmu (untuk menangguhkan azab) sampai batas waktu yang ditentukan, pastilah hukuman bagi mereka telah dilaksanakan. Dan sesungguhnya orang-orang yang mewarisi Kitab (Taurat dan Injil) setelah mereka (pada zaman Muhammad), benar-benar berada dalam keraguan yang mendalam tentang Kitab (Al-Qur'an) itu."},{"id":"15","ar":"فَلِذٰلِكَ فَادْعُ ۚوَاسْتَقِمْ كَمَآ اُمِرْتَۚ وَلَا تَتَّبِعْ اَهْوَاۤءَهُمْۚ وَقُلْ اٰمَنْتُ بِمَآ اَنْزَلَ اللّٰهُ مِنْ كِتٰبٍۚ وَاُمِرْتُ لِاَعْدِلَ بَيْنَكُمْ ۗ اَللّٰهُ رَبُّنَا وَرَبُّكُمْ ۗ لَنَآ اَعْمَالُنَا وَلَكُمْ اَعْمَالُكُمْ ۗ لَاحُجَّةَ بَيْنَنَا وَبَيْنَكُمْ ۗ اَللّٰهُ يَجْمَعُ بَيْنَنَا ۚوَاِلَيْهِ الْمَصِيْرُ ۗ ","bc":"fa liżālika fad', wastaqim kamā umirt, wa lā tattabi' ahwā`ahum, wa qul āmantu bimā anzalallāhu ming kitāb, wa umirtu li`a'dila bainakum, allāhu rabbunā wa rabbukum, lanā a'mālunā wa lakum a'mālukum, lā ḥujjata bainanā wa bainakum, allāhu yajma'u bainanā, wa ilaihil-maṣīr","at":"Karena itu, serulah (mereka beriman) dan tetaplah (beriman dan berdakwah) sebagaimana diperintahkan kepadamu (Muhammad) dan janganlah mengikuti keinginan mereka dan katakanlah, “Aku beriman kepada Kitab yang diturunkan Allah dan aku diperintahkan agar berlaku adil di antara kamu. Allah Tuhan kami dan Tuhan kamu. Bagi kami perbuatan kami dan bagi kamu perbuatan kamu. Tidak (perlu) ada pertengkaran antara kami dan kamu, Allah mengumpulkan antara kita dan kepada-Nyalah (kita) kembali.”"},{"id":"16","ar":"وَالَّذِيْنَ يُحَاۤجُّوْنَ فِى اللّٰهِ مِنْۢ بَعْدِ مَا اسْتُجِيْبَ لَهٗ حُجَّتُهُمْ دَاحِضَةٌ عِنْدَ رَبِّهِمْ وَعَلَيْهِمْ غَضَبٌ وَّلَهُمْ عَذَابٌ شَدِيْدٌ ","bc":"wallażīna yuḥājjụna fillāhi mim ba'di mastujība lahụ ḥujjatuhum dāḥiḍatun 'inda rabbihim wa 'alaihim gaḍabuw wa lahum 'ażābun syadīd","at":"Dan orang-orang yang berbantah-bantah tentang (agama) Allah setelah (agama itu) diterima, perbantahan mereka itu sia-sia di sisi Tuhan mereka. Mereka mendapat kemurkaan (Allah) dan mereka mendapat azab yang sangat keras."},{"id":"17","ar":"اَللّٰهُ الَّذِيْٓ اَنْزَلَ الْكِتٰبَ بِالْحَقِّ وَالْمِيْزَانَ ۗوَمَا يُدْرِيْكَ لَعَلَّ السَّاعَةَ قَرِيْبٌ ","bc":"allāhullażī anzalal-kitāba bil-ḥaqqi wal mīzān, wa mā yudrīka la'allas-sā'ata qarīb","at":"Allah yang menurunkan Kitab (Al-Qur'an) dengan (membawa) kebenaran dan neraca (keadilan). Dan tahukah kamu, boleh jadi hari Kiamat itu sudah dekat?"},{"id":"18","ar":"يَسْتَعْجِلُ بِهَا الَّذِيْنَ لَا يُؤْمِنُوْنَ بِهَاۚ وَالَّذِيْنَ اٰمَنُوْا مُشْفِقُوْنَ مِنْهَاۙ وَيَعْلَمُوْنَ اَنَّهَا الْحَقُّ ۗ اَلَآ اِنَّ الَّذِيْنَ يُمَارُوْنَ فِى السَّاعَةِ لَفِيْ ضَلٰلٍۢ بَعِيْدٍ ","bc":"yasta'jilu bihallażīna lā yu`minụna bihā, wallażīna āmanụ musyfiqụna min-hā wa ya'lamụna annahal-ḥaqq, alā innallażīna yumārụna fis-sā'ati lafī ḍalālim ba'īd","at":"Orang-orang yang tidak percaya adanya hari Kiamat meminta agar hari itu segera terjadi, dan orang-orang yang beriman merasa takut kepadanya dan mereka yakin bahwa Kiamat itu adalah benar (akan terjadi). Ketahuilah bahwa sesungguhnya orang-orang yang membantah tentang terjadinya Kiamat itu benar-benar telah tersesat jauh."},{"id":"19","ar":"اَللّٰهُ لَطِيْفٌۢ بِعِبَادِهٖ يَرْزُقُ مَنْ يَّشَاۤءُ ۚوَهُوَ الْقَوِيُّ الْعَزِيْزُ ","bc":"allāhu laṭīfum bi'ibādihī yarzuqu may yasyā`, wa huwal-qawiyyul-'azīz","at":"Allah Mahalembut terhadap hamba-hamba-Nya; Dia memberi rezeki kepada siapa yang Dia kehendaki dan Dia Mahakuat, Mahaperkasa."},{"id":"20","ar":"مَنْ كَانَ يُرِيْدُ حَرْثَ الْاٰخِرَةِ نَزِدْ لَهٗ فِيْ حَرْثِهٖۚ وَمَنْ كَانَ يُرِيْدُ حَرْثَ الدُّنْيَا نُؤْتِهٖ مِنْهَاۙ وَمَا لَهٗ فِى الْاٰخِرَةِ مِنْ نَّصِيْبٍ ","bc":"mang kāna yurīdu ḥarṡal-ākhirati nazid lahụ fī ḥarṡih, wa mang kāna yurīdu ḥarṡad-dun-yā nu`tihī min-hā wa mā lahụ fil-ākhirati min naṣīb","at":"Barangsiapa menghendaki keuntungan di akhirat akan Kami tambahkan keuntungan itu baginya dan barangsiapa menghendaki keuntungan di dunia Kami berikan kepadanya sebagian darinya (keuntungan dunia), tetapi dia tidak akan mendapat bagian di akhirat."},{"id":"21","ar":"اَمْ لَهُمْ شُرَكٰۤؤُا شَرَعُوْا لَهُمْ مِّنَ الدِّيْنِ مَا لَمْ يَأْذَنْۢ بِهِ اللّٰهُ ۗوَلَوْلَا كَلِمَةُ الْفَصْلِ لَقُضِيَ بَيْنَهُمْ ۗوَاِنَّ الظّٰلِمِيْنَ لَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"am lahum syurakā`u syara'ụ lahum minad-dīni mā lam ya`żam bihillāh, walau lā kalimatul-faṣli laquḍiya bainahum, wa innaẓ-ẓālimīna lahum 'ażābun alīm","at":"Apakah mereka mempunyai sesembahan selain Allah yang menetapkan aturan agama bagi mereka yang tidak diizinkan (diridai) Allah? Dan sekiranya tidak ada ketetapan yang menunda (hukuman dari Allah) tentulah hukuman di antara mereka telah dilaksanakan. Dan sungguh, orang-orang zalim itu akan mendapat azab yang sangat pedih."},{"id":"22","ar":"تَرَى الظّٰلِمِيْنَ مُشْفِقِيْنَ مِمَّا كَسَبُوْا وَهُوَ وَاقِعٌۢ بِهِمْ ۗوَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فِيْ رَوْضٰتِ الْجَنّٰتِۚ لَهُمْ مَّا يَشَاۤءُوْنَ عِنْدَ رَبِّهِمْ ۗذٰلِكَ هُوَ الْفَضْلُ الْكَبِيْرُ ","bc":"taraẓ-ẓālimīna musyfiqīna mimmā kasabụ wa huwa wāqi'um bihim, wallażīna āmanụ wa 'amiluṣ-ṣāliḥāti fī rauḍātil-jannāt, lahum mā yasyā`ụna 'inda rabbihim, żālika huwal-faḍlul-kabīr","at":"Kamu akan melihat orang-orang zalim itu sangat ketakutan karena (kejahatan-kejahatan) yang telah mereka lakukan, dan (azab) menimpa mereka. Dan orang-orang yang beriman dan mengerjakan kebajikan (berada) di dalam taman-taman surga, mereka memperoleh apa yang mereka kehendaki di sisi Tuhan. Yang demikian itu adalah karunia yang besar."},{"id":"23","ar":"ذٰلِكَ الَّذِيْ يُبَشِّرُ اللّٰهُ عِبَادَهُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِۗ قُلْ لَّآ اَسْـَٔلُكُمْ عَلَيْهِ اَجْرًا اِلَّا الْمَوَدَّةَ فِى الْقُرْبٰىۗ وَمَنْ يَّقْتَرِفْ حَسَنَةً نَّزِدْ لَهٗ فِيْهَا حُسْنًا ۗاِنَّ اللّٰهَ غَفُوْرٌ شَكُوْرٌ ","bc":"żālikallażī yubasysyirullāhu 'ibādahullażīna āmanụ wa 'amiluṣ-ṣāliḥāt, qul lā as`alukum 'alaihi ajran illal-mawaddata fil-qurbā, wa may yaqtarif ḥasanatan nazid lahụ fīhā ḥusnā, innallāha gafụrun syakụr","at":"Itulah (karunia) yang diberitahukan Allah untuk menggembirakan hamba-hamba-Nya yang beriman dan mengerjakan kebajikan. Katakanlah (Muhammad), “Aku tidak meminta kepadamu sesuatu imbalan pun atas seruanku kecuali kasih sayang dalam kekeluargaan.” Dan barangsiapa mengerjakan kebaikan akan Kami tambahkan kebaikan baginya. Sungguh, Allah Maha Pengampun, Maha Mensyukuri."},{"id":"24","ar":"اَمْ يَقُوْلُوْنَ افْتَرٰى عَلَى اللّٰهِ كَذِبًاۚ فَاِنْ يَّشَاِ اللّٰهُ يَخْتِمْ عَلٰى قَلْبِكَ ۗوَيَمْحُ اللّٰهُ الْبَاطِلَ وَيُحِقُّ الْحَقَّ بِكَلِمٰتِهٖ ۗاِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ ","bc":"am yaqụlụnaftarā 'alallāhi każibā, fa iy yasya`illāhu yakhtim 'alā qalbik, wa yam-ḥullāhul-bāṭila wa yuḥiqqul-ḥaqqa bikalimātih, innahụ 'alīmum biżātiṣ-ṣudụr","at":"Ataukah mereka mengatakan, “Dia (Muhammad) telah mengada-adakan kebohongan tentang Allah.” Sekiranya Allah menghendaki niscaya Dia kunci hatimu. Dan Allah menghapus yang batil dan membenarkan yang benar dengan firman-Nya (Al-Qur'an). Sungguh, Dia Maha Mengetahui segala isi hati."},{"id":"25","ar":"وَهُوَ الَّذِيْ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهٖ وَيَعْفُوْا عَنِ السَّيِّاٰتِ وَيَعْلَمُ مَا تَفْعَلُوْنَۙ ","bc":"wa huwallażī yaqbalut-taubata 'an 'ibādihī wa ya'fụ 'anis-sayyi`āti wa ya'lamu mā taf'alụn","at":"Dan Dialah yang menerima tobat dari hamba-hamba-Nya dan memaafkan kesalahan-kesalahan dan mengetahui apa yang kamu kerjakan,"},{"id":"26","ar":"وَيَسْتَجِيْبُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَيَزِيْدُهُمْ مِّنْ فَضْلِهٖ ۗوَالْكٰفِرُوْنَ لَهُمْ عَذَابٌ شَدِيْدٌ ","bc":"wa yastajībullażīna āmanụ wa 'amiluṣ-ṣāliḥāti wa yazīduhum min faḍlih, wal-kāfirụna lahum 'ażābun syadīd","at":"dan Dia memperkenankan (doa) orang-orang yang beriman dan mengerjakan kebajikan serta menambah (pahala) kepada mereka dari karunia-Nya. Orang-orang yang ingkar akan mendapat azab yang sangat keras."},{"id":"27","ar":"۞ وَلَوْ بَسَطَ اللّٰهُ الرِّزْقَ لِعِبَادِهٖ لَبَغَوْا فِى الْاَرْضِ وَلٰكِنْ يُنَزِّلُ بِقَدَرٍ مَّا يَشَاۤءُ ۗاِنَّهٗ بِعِبَادِهٖ خَبِيْرٌۢ بَصِيْرٌ ","bc":"walau basaṭallāhur-rizqa li'ibādihī labagau fil-arḍi wa lākiy yunazzilu biqadarim mā yasyā`, innahụ bi'ibādihī khabīrum baṣīr","at":"Dan sekiranya Allah melapangkan rezeki kepada hamba-hamba-Nya niscaya mereka akan berbuat melampaui batas di bumi, tetapi Dia menurunkan dengan ukuran yang Dia kehendaki. Sungguh, Dia Mahateliti terhadap (keadaan) hamba-hamba-Nya, Maha Melihat."},{"id":"28","ar":"وَهُوَ الَّذِيْ يُنَزِّلُ الْغَيْثَ مِنْۢ بَعْدِ مَا قَنَطُوْا وَيَنْشُرُ رَحْمَتَهٗ ۗوَهُوَ الْوَلِيُّ الْحَمِيْدُ ","bc":"wa huwallażī yunazzilul-gaiṡa mim ba'di mā qanaṭụ wa yansyuru raḥmatah, wa huwal-waliyyul-ḥamīd","at":"Dan Dialah yang menurunkan hujan setelah mereka berputus asa dan menyebarkan rahmat-Nya. Dan Dialah Maha Pelindung, Maha Terpuji."},{"id":"29","ar":"وَمِنْ اٰيٰتِهٖ خَلْقُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَثَّ فِيْهِمَا مِنْ دَاۤبَّةٍ ۗوَهُوَ عَلٰى جَمْعِهِمْ اِذَا يَشَاۤءُ قَدِيْرٌ","bc":"wa min āyātihī khalqus-samāwāti wal-arḍi wa mā baṡṡa fīhimā min dābbah, wa huwa 'alā jam'ihim iżā yasyā`u qadīr","at":"Dan di antara tanda-tanda (kebesaran)-Nya adalah penciptaan langit dan bumi dan makhluk-makhluk yang melata yang Dia sebarkan pada keduanya. Dan Dia Mahakuasa mengumpulkan semuanya apabila Dia kehendaki."},{"id":"30","ar":"وَمَآ اَصَابَكُمْ مِّنْ مُّصِيْبَةٍ فَبِمَا كَسَبَتْ اَيْدِيْكُمْ وَيَعْفُوْا عَنْ كَثِيْرٍۗ ","bc":"wa mā aṣābakum mim muṣībatin fa bimā kasabat aidīkum wa ya'fụ 'ang kaṡīr","at":"Dan musibah apa pun yang menimpa kamu adalah disebabkan oleh perbuatan tanganmu sendiri, dan Allah memaafkan banyak (dari kesalahan-kesalahanmu)."},{"id":"31","ar":"وَمَآ اَنْتُمْ بِمُعْجِزِيْنَ فِى الْاَرْضِۚ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ","bc":"wa mā antum bimu'jizīna fil-arḍ, wa mā lakum min dụnillāhi miw waliyyiw wa lā naṣīr","at":"Dan kamu tidak dapat melepaskan diri (dari siksaan Allah) di bumi, dan kamu tidak memperoleh pelindung atau penolong selain Allah."},{"id":"32","ar":"وَمِنْ اٰيٰتِهِ الْجَوَارِ فِى الْبَحْرِ كَالْاَعْلَامِ ۗ ","bc":"wa min āyātihil-jawāri fil-baḥri kal-a'lām","at":"Dan di antara tanda-tanda (kebesaran)-Nya ialah kapal-kapal (yang berlayar) di laut seperti gunung-gunung."},{"id":"33","ar":"اِنْ يَّشَأْ يُسْكِنِ الرِّيْحَ فَيَظْلَلْنَ رَوَاكِدَ عَلٰى ظَهْرِهٖۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍۙ","bc":"iy yasya` yuskinir-rīḥa fa yaẓlalna rawākida 'alā ẓahrih, inna fī żālika la`āyātil likulli ṣabbārin syakụr","at":"Jika Dia menghendaki, Dia akan menghentikan angin, sehingga jadilah (kapal-kapal) itu terhenti di permukaan laut. Sungguh, pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi orang yang selalu bersabar dan banyak bersyukur,"},{"id":"34","ar":"اَوْ يُوْبِقْهُنَّ بِمَا كَسَبُوْا وَيَعْفُ عَنْ كَثِيْرٍۙ ","bc":"au yụbiq-hunna bimā kasabụ wa ya'fu 'ang kaṡīr","at":"atau (Dia akan) menghancurkan kapal-kapal itu karena perbuatan (dosa) mereka, dan Dia memaafkan banyak (dari mereka),"},{"id":"35","ar":"وَّيَعْلَمَ الَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِنَاۗ مَا لَهُمْ مِّنْ مَّحِيْصٍ ","bc":"wa ya'lamallażīna yujādilụna fī āyātinā, mā lahum mim maḥīṣ","at":"dan agar orang-orang yang membantah tanda-tanda (kekuasaan) Kami mengetahui bahwa mereka tidak akan memperoleh jalan keluar (dari siksaan)."},{"id":"36","ar":"فَمَآ اُوْتِيْتُمْ مِّنْ شَيْءٍ فَمَتَاعُ الْحَيٰوةِ الدُّنْيَا ۚوَمَا عِنْدَ اللّٰهِ خَيْرٌ وَّاَبْقٰى لِلَّذِيْنَ اٰمَنُوْا وَعَلٰى رَبِّهِمْ يَتَوَكَّلُوْنَۚ ","bc":"fa mā ụtītum min syai`in fa matā'ul-ḥayātid-dun-yā, wa mā 'indallāhi khairuw wa abqā lillażīna āmanụ wa 'alā rabbihim yatawakkalụn","at":"Apa pun (kenikmatan) yang diberikan kepadamu, maka itu adalah kesenangan hidup di dunia. Sedangkan apa (kenikmatan) yang ada di sisi Allah lebih baik dan lebih kekal bagi orang-orang yang beriman, dan hanya kepada Tuhan mereka bertawakal,"},{"id":"37","ar":"وَالَّذِيْنَ يَجْتَنِبُوْنَ كَبٰۤىِٕرَ الْاِثْمِ وَالْفَوَاحِشَ وَاِذَا مَا غَضِبُوْا هُمْ يَغْفِرُوْنَ ۚ ","bc":"wallażīna yajtanibụna kabā`iral-iṡmi wal-fawāḥisya wa iżā mā gaḍibụ hum yagfirụn","at":"dan juga (bagi) orang-orang yang menjauhi dosa-dosa besar dan perbuatan-perbuatan keji, dan apabila mereka marah segera memberi maaf,"},{"id":"38","ar":"وَالَّذِيْنَ اسْتَجَابُوْا لِرَبِّهِمْ وَاَقَامُوا الصَّلٰوةَۖ وَاَمْرُهُمْ شُوْرٰى بَيْنَهُمْۖ وَمِمَّا رَزَقْنٰهُمْ يُنْفِقُوْنَ ۚ ","bc":"wallażīnastajābụ lirabbihim wa aqāmuṣ-ṣalāta wa amruhum syụrā bainahum wa mimmā razaqnāhum yunfiqụn","at":"dan (bagi) orang-orang yang menerima (mematuhi) seruan Tuhan dan melaksanakan salat, sedang urusan mereka (diputuskan) dengan musyawarah antara mereka; dan mereka menginfakkan sebagian dari rezeki yang Kami berikan kepada mereka,"},{"id":"39","ar":"وَالَّذِيْنَ اِذَآ اَصَابَهُمُ الْبَغْيُ هُمْ يَنْتَصِرُوْنَ ","bc":"wallażīna iżā aṣābahumul-bagyu hum yantaṣirụn","at":"dan (bagi) orang-orang yang apabila mereka diperlakukan dengan zalim, mereka membela diri."},{"id":"40","ar":"وَجَزٰۤؤُا سَيِّئَةٍ سَيِّئَةٌ مِّثْلُهَا ۚفَمَنْ عَفَا وَاَصْلَحَ فَاَجْرُهٗ عَلَى اللّٰهِ ۗاِنَّهٗ لَا يُحِبُّ الظّٰلِمِيْنَ","bc":"wa jazā`u sayyi`atin sayyi`atum miṡluhā, fa man 'afā wa aṣlaḥa fa ajruhụ 'alallāh, innahụ lā yuḥibbuẓ-ẓālimīn","at":"Dan balasan suatu kejahatan adalah kejahatan yang setimpal, tetapi barangsiapa memaafkan dan berbuat baik (kepada orang yang berbuat jahat) maka pahalanya dari Allah. Sungguh, Dia tidak menyukai orang-orang zalim."},{"id":"41","ar":"وَلَمَنِ انْتَصَرَ بَعْدَ ظُلْمِهٖ فَاُولٰۤىِٕكَ مَا عَلَيْهِمْ مِّنْ سَبِيْلٍۗ ","bc":"wa lamanintaṣara ba'da ẓulmihī fa ulā`ika mā 'alaihim min sabīl","at":"Tetapi orang-orang yang membela diri setelah dizalimi, tidak ada alasan untuk menyalahkan mereka."},{"id":"42","ar":"اِنَّمَا السَّبِيْلُ عَلَى الَّذِيْنَ يَظْلِمُوْنَ النَّاسَ وَيَبْغُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"innamas-sabīlu 'alallażīna yaẓlimụnan-nāsa wa yabgụna fil-arḍi bigairil-ḥaqq, ulā`ika lahum 'ażābun alīm","at":"Sesungguhnya kesalahan hanya ada pada orang-orang yang berbuat zalim kepada manusia dan melampaui batas di bumi tanpa (mengindahkan) kebenaran. Mereka itu mendapat siksa yang pedih."},{"id":"43","ar":"وَلَمَنْ صَبَرَ وَغَفَرَ اِنَّ ذٰلِكَ لَمِنْ عَزْمِ الْاُمُوْرِ ","bc":"wa laman ṣabara wa gafara inna żālika lamin 'azmil-umụr","at":"Tetapi barangsiapa bersabar dan memaafkan, sungguh yang demikian itu termasuk perbuatan yang mulia."},{"id":"44","ar":"وَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ وَّلِيٍّ مِّنْۢ بَعْدِهٖ ۗوَتَرَى الظّٰلِمِيْنَ لَمَّا رَاَوُا الْعَذَابَ يَقُوْلُوْنَ هَلْ اِلٰى مَرَدٍّ مِّنْ سَبِيْلٍۚ ","bc":"wa may yuḍlilillāhu fa mā lahụ miw waliyyim mim ba'dih, wa taraẓ-ẓālimīna lammā ra`awul-'ażāba yaqụlụna hal ilā maraddim min sabīl","at":"Dan barangsiapa dibiarkan sesat oleh Allah, maka tidak ada baginya pelindung setelah itu. Kamu akan melihat orang-orang zalim ketika mereka melihat azab berkata, “Adakah kiranya jalan untuk kembali (ke dunia)?”"},{"id":"45","ar":"وَتَرٰىهُمْ يُعْرَضُوْنَ عَلَيْهَا خٰشِعِيْنَ مِنَ الذُّلِّ يَنْظُرُوْنَ مِنْ طَرْفٍ خَفِيٍّۗ وَقَالَ الَّذِيْنَ اٰمَنُوْٓا اِنَّ الْخٰسِرِيْنَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ وَاَهْلِيْهِمْ يَوْمَ الْقِيٰمَةِ ۗ اَلَآ اِنَّ الظّٰلِمِيْنَ فِيْ عَذَابٍ مُّقِيْمٍ ","bc":"wa tarāhum yu'raḍụna 'alaihā khāsyi'īna minaż-żulli yanẓurụna min ṭarfin khafiyy, wa qālallażīna āmanū innal-khāsirīnallażīna khasirū anfusahum wa ahlīhim yaumal-qiyāmah, alā innaẓ-ẓālimīna fī 'ażābim muqīm","at":"Dan kamu akan melihat mereka dihadapkan ke neraka dalam keadaan tertunduk karena (merasa) hina, mereka melihat dengan pandangan yang lesu. Dan orang-orang yang beriman berkata, “Sesungguhnya orang-orang yang rugi ialah orang-orang yang merugikan diri mereka sendiri dan keluarganya pada hari Kiamat.” Ingatlah, sesungguhnya orang-orang zalim itu berada dalam azab yang kekal."},{"id":"46","ar":"وَمَا كَانَ لَهُمْ مِّنْ اَوْلِيَاۤءَ يَنْصُرُوْنَهُمْ مِّنْ دُوْنِ اللّٰهِ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ سَبِيْلٍ ۗ ","bc":"wa mā kāna lahum min auliyā`a yanṣurụnahum min dụnillāh, wa may yuḍlilillāhu fa mā lahụ min sabīl","at":"Dan mereka tidak akan mempunyai pelindung yang dapat menolong mereka selain Allah. Barangsiapa dibiarkan sesat oleh Allah tidak akan ada jalan keluar baginya (untuk mendapat petunjuk)."},{"id":"47","ar":"اِسْتَجِيْبُوْا لِرَبِّكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا مَرَدَّ لَهٗ مِنَ اللّٰهِ ۗمَا لَكُمْ مِّنْ مَّلْجَاٍ يَّوْمَىِٕذٍ وَّمَا لَكُمْ مِّنْ نَّكِيْرٍ ","bc":"istajībụ lirabbikum ming qabli ay ya`tiya yaumul lā maradda lahụ minallāh, mā lakum mim malja`iy yauma`iżiw wa mā lakum min nakīr","at":"Patuhilah seruan Tuhanmu sebelum datang dari Allah suatu hari yang tidak dapat ditolak (atas perintah dari Allah). Pada hari itu kamu tidak memperoleh tempat berlindung dan tidak (pula) dapat mengingkari (dosa-dosamu)."},{"id":"48","ar":"فَاِنْ اَعْرَضُوْا فَمَآ اَرْسَلْنٰكَ عَلَيْهِمْ حَفِيْظًا ۗاِنْ عَلَيْكَ اِلَّا الْبَلٰغُ ۗوَاِنَّآ اِذَآ اَذَقْنَا الْاِنْسَانَ مِنَّا رَحْمَةً فَرِحَ بِهَا ۚوَاِنْ تُصِبْهُمْ سَيِّئَةٌ ۢبِمَا قَدَّمَتْ اَيْدِيْهِمْ فَاِنَّ الْاِنْسَانَ كَفُوْرٌ ","bc":"fa in a'raḍụ fa mā arsalnāka 'alaihim ḥafīẓā, in 'alaika illal-balāg, wa innā iżā ażaqnal-insāna minnā raḥmatan fariḥa bihā, wa in tuṣib-hum sayyi`atum bimā qaddamat aidīhim fa innal-insāna kafụr","at":"Jika mereka berpaling, maka (ingatlah) Kami tidak mengutus engkau sebagai pengawas bagi mereka. Kewajibanmu tidak lain hanyalah menyampaikan (risalah). Dan sungguh, apabila Kami merasakan kepada manusia suatu rahmat dari Kami, dia menyambutnya dengan gembira; tetapi jika mereka ditimpa kesusahan disebabkan perbuatan tangan mereka sendiri (niscaya mereka ingkar), sungguh, manusia itu sangat ingkar (kepada nikmat)."},{"id":"49","ar":"لِلّٰهِ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يَخْلُقُ مَا يَشَاۤءُ ۗيَهَبُ لِمَنْ يَّشَاۤءُ اِنَاثًا وَّيَهَبُ لِمَنْ يَّشَاۤءُ الذُّكُوْرَ ۙ","bc":"lillahi mulkus-samāwāti wal-arḍ, yakhluqu mā yasyā`, yahabu limay yasyā`u ināṡaw wa yahabu limay yasyā`uż-żukụr","at":"Milik Allah-lah kerajaan langit dan bumi; Dia menciptakan apa yang Dia kehendaki, memberikan anak perempuan kepada siapa yang Dia kehendaki dan memberikan anak laki-laki kepada siapa yang Dia kehendaki,"},{"id":"50","ar":"اَوْ يُزَوِّجُهُمْ ذُكْرَانًا وَّاِنَاثًا ۚوَيَجْعَلُ مَنْ يَّشَاۤءُ عَقِيْمًا ۗاِنَّهٗ عَلِيْمٌ قَدِيْرٌ ","bc":"au yuzawwijuhum żukrānaw wa ināṡā, wa yaj'alu may yasyā`u 'aqīmā, innahụ 'alīmung qadīr","at":"atau Dia menganugerahkan jenis laki-laki dan perempuan, dan menjadikan mandul siapa yang Dia kehendaki. Dia Maha Mengetahui, Mahakuasa."},{"id":"51","ar":"۞ وَمَا كَانَ لِبَشَرٍ اَنْ يُّكَلِّمَهُ اللّٰهُ اِلَّا وَحْيًا اَوْ مِنْ وَّرَاۤئِ حِجَابٍ اَوْ يُرْسِلَ رَسُوْلًا فَيُوْحِيَ بِاِذْنِهٖ مَا يَشَاۤءُ ۗاِنَّهٗ عَلِيٌّ حَكِيْمٌ ","bc":"wa mā kāna libasyarin ay yukallimahullāhu illā waḥyan au miw warā`i ḥijābin au yursila rasụlan fa yụḥiya bi`iżnihī mā yasyā`, innahụ 'aliyyun ḥakīm","at":"Dan tidaklah patut bagi seorang manusia bahwa Allah akan berbicara kepadanya kecuali dengan perantaraan wahyu atau dari belakang tabir atau dengan mengutus utusan (malaikat) lalu diwahyukan kepadanya dengan izin-Nya apa yang Dia kehendaki. Sungguh, Dia Mahatinggi, Mahabijaksana."},{"id":"52","ar":"وَكَذٰلِكَ اَوْحَيْنَآ اِلَيْكَ رُوْحًا مِّنْ اَمْرِنَا ۗمَا كُنْتَ تَدْرِيْ مَا الْكِتٰبُ وَلَا الْاِيْمَانُ وَلٰكِنْ جَعَلْنٰهُ نُوْرًا نَّهْدِيْ بِهٖ مَنْ نَّشَاۤءُ مِنْ عِبَادِنَا ۗوَاِنَّكَ لَتَهْدِيْٓ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍۙ ","bc":"wa każālika auḥainā ilaika rụḥam min amrinā, mā kunta tadrī mal-kitābu wa lal-īmānu wa lākin ja'alnāhu nụran nahdī bihī man nasyā`u min 'ibādinā, wa innaka latahdī ilā ṣirāṭim mustaqīm","at":"Dan demikianlah Kami wahyukan kepadamu (Muhammad) ruh (Al-Qur'an) dengan perintah Kami. Sebelumnya engkau tidaklah mengetahui apakah Kitab (Al-Qur'an) dan apakah iman itu, tetapi Kami jadikan Al-Qur'an itu cahaya, dengan itu Kami memberi petunjuk siapa yang Kami kehendaki di antara hamba-hamba Kami. Dan sungguh, engkau benar-benar membimbing (manusia) kepada jalan yang lurus,"},{"id":"53","ar":"صِرَاطِ اللّٰهِ الَّذِيْ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ اَلَآ اِلَى اللّٰهِ تَصِيْرُ الْاُمُوْرُ ","bc":"ṣirāṭillāhillażī lahụ mā fis-samāwāti wa mā fil-arḍ, alā ilallāhi taṣīrul-umụr","at":"(Yaitu) jalan Allah yang milik-Nyalah apa yang ada di langit dan apa yang ada di bumi. Ingatlah, segala urusan kembali kepada Allah."}],at_tagabun=[{"id":"1","ar":"يُسَبِّحُ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ لَهُ الْمُلْكُ وَلَهُ الْحَمْدُۖ وَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"yusabbiḥu lillāhi mā fis-samāwāti wa mā fil-arḍ, lahul-mulku wa lahul-ḥamdu wa huwa 'alā kulli syai`ing qadīr","at":"Apa yang ada di langit dan apa yang ada di bumi senantiasa bertasbih kepada Allah; milik-Nya semua kerajaan dan bagi-Nya (pula) segala puji; dan Dia Mahakuasa atas segala sesuatu. "},{"id":"2","ar":"هُوَ الَّذِيْ خَلَقَكُمْ فَمِنْكُمْ كَافِرٌ وَّمِنْكُمْ مُّؤْمِنٌۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ","bc":"huwallażī khalaqakum fa mingkum kāfiruw wa mingkum mu`min, wallāhu bimā ta'malụna baṣīr","at":"Dialah yang menciptakan kamu, lalu di antara kamu ada yang kafir dan di antara kamu (juga) ada yang mukmin. Dan Allah Maha Melihat apa yang kamu kerjakan."},{"id":"3","ar":"خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّ وَصَوَّرَكُمْ فَاَحْسَنَ صُوَرَكُمْۚ وَاِلَيْهِ الْمَصِيْرُ ","bc":"khalaqas-samāwāti wal-arḍa bil-ḥaqqi wa ṣawwarakum fa aḥsana ṣuwarakum, wa ilaihil-maṣīr","at":"Dia menciptakan langit dan bumi dengan (tujuan) yang benar, Dia membentuk rupamu lalu memperbagus rupamu, dan kepada-Nya tempat kembali. "},{"id":"4","ar":"يَعْلَمُ مَا فِى السَّمٰوٰتِ وَالْاَرْضِ وَيَعْلَمُ مَا تُسِرُّوْنَ وَمَا تُعْلِنُوْنَۗ وَاللّٰهُ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ ","bc":"ya'lamu mā fis-samāwāti wal-arḍi wa ya'lamu mā tusirrụna wa mā tu'linụn, wallāhu 'alīmum biżātiṣ-ṣudụr","at":"Dia mengetahui apa yang di langit dan di bumi, dan mengetahui apa yang kamu rahasiakan dan apa yang kamu nyatakan. Dan Allah Maha Mengetahui segala isi hati. "},{"id":"5","ar":"اَلَمْ يَأْتِكُمْ نَبَؤُا الَّذِيْنَ كَفَرُوْا مِنْ قَبْلُ ۖفَذَاقُوْا وَبَالَ اَمْرِهِمْ وَلَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"a lam ya`tikum naba`ullażīna kafarụ ming qablu fa żāqụ wa bāla amrihim wa lahum 'ażābun alīm","at":"Apakah belum sampai kepadamu (orang-orang kafir) berita orang-orang kafir dahulu? Maka mereka telah merasakan akibat buruk dari perbuatannya dan mereka memperoleh azab yang pedih. "},{"id":"6","ar":"ذٰلِكَ بِاَنَّهٗ كَانَتْ تَّأْتِيْهِمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَقَالُوْٓا اَبَشَرٌ يَّهْدُوْنَنَاۖ فَكَفَرُوْا وَتَوَلَّوْا وَّاسْتَغْنَى اللّٰهُ ۗوَاللّٰهُ غَنِيٌّ حَمِيْدٌ ","bc":"żālika bi`annahụ kānat ta`tīhim rusuluhum bil-bayyināti fa qālū abasyaruy yahdụnanā fa kafarụ wa tawallaw wastagnallāh, wallāhu ganiyyun ḥamīd","at":"Yang demikian itu karena sesungguhnya ketika rasul-rasul datang kepada mereka (membawa) keterangan-ke-terangan lalu mereka berkata, “Apakah (pantas) manusia yang memberi petunjuk kepada kami?” Lalu mereka ingkar dan berpaling; padahal Allah tidak memerlukan (mereka). Dan Allah Mahakaya, Maha Terpuji. "},{"id":"7","ar":"زَعَمَ الَّذِيْنَ كَفَرُوْٓا اَنْ لَّنْ يُّبْعَثُوْاۗ قُلْ بَلٰى وَرَبِّيْ لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْۗ وَذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ ","bc":"za'amallażīna kafarū al lay yub'aṡụ, qul balā wa rabbī latub'aṡunna ṡumma latunabba`unna bimā 'amiltum, wa żālika 'alallāhi yasīr","at":"Orang-orang yang kafir mengira, bahwa mereka tidak akan dibangkitkan. Katakanlah (Muhammad), “Tidak demikian, demi Tuhanku, kamu pasti dibangkitkan, kemudian diberitakan semua yang telah kamu kerjakan.” Dan yang demikian itu mudah bagi Allah. "},{"id":"8","ar":"فَاٰمِنُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَالنُّوْرِ الَّذِيْٓ اَنْزَلْنَاۗ وَاللّٰهُ بِمَا تَعْمَلُوْنَ خَبِيْرٌ ","bc":"fa āminụ billāhi wa rasụlihī wan-nụrillażī anzalnā, wallāhu bimā ta'malụna khabīr","at":"Maka berimanlah kamu kepada Allah dan Rasul-Nya dan kepada cahaya (Al-Qur'an) yang telah Kami turunkan. Dan Allah Mahateliti terhadap apa yang kamu kerjakan."},{"id":"9","ar":"يَوْمَ يَجْمَعُكُمْ لِيَوْمِ الْجَمْعِ ذٰلِكَ يَوْمُ التَّغَابُنِۗ وَمَنْ يُّؤْمِنْۢ بِاللّٰهِ وَيَعْمَلْ صَالِحًا يُّكَفِّرْ عَنْهُ سَيِّاٰتِهٖ وَيُدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ ذٰلِكَ الْفَوْزُ الْعَظِيْمُ ","bc":"yauma yajma'ukum liyaumil-jam'i żālika yaumut-tagābun, wa may yu`mim billāhi wa ya'mal ṣāliḥay yukaffir 'an-hu sayyi`ātihī wa yudkhil-hu jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, żālikal-fauzul-'aẓīm","at":"(Ingatlah) pada hari (ketika) Allah mengumpulkan kamu pada hari berhimpun, itulah hari pengungkapan kesalahan-kesalahan. Dan barangsiapa beriman kepada Allah dan mengerjakan kebajikan niscaya Allah akan menghapus kesalahan-kesalahannya dan memasukkannya ke dalam surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya selama-lamanya. Itulah kemenangan yang agung. "},{"id":"10","ar":"وَالَّذِيْنَ كَفَرُوْا وَكَذَّبُوْا بِاٰيٰتِنَآ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِ خٰلِدِيْنَ فِيْهَاۗ وَبِئْسَ الْمَصِيْرُ ","bc":"wallażīna kafarụ wa każżabụ bi`āyātinā ulā`ika aṣ-ḥābun-nāri khālidīna fīhā, wa bi`sal-maṣīr","at":"Dan orang-orang yang kafir dan mendustakan ayat-ayat Kami, mereka itulah penghuni neraka, mereka kekal di dalamnya. Dan itulah seburuk-buruk tempat kembali. "},{"id":"11","ar":"مَآ اَصَابَ مِنْ مُّصِيْبَةٍ اِلَّا بِاِذْنِ اللّٰهِ ۗوَمَنْ يُّؤْمِنْۢ بِاللّٰهِ يَهْدِ قَلْبَهٗ ۗوَاللّٰهُ بِكُلِّ شَيْءٍ عَلِيْمٌ ","bc":"mā aṣāba mim muṣībatin illā bi`iżnillāh, wa may yu`mim billāhi yahdi qalbah, wallāhu bikulli syai`in 'alīm","at":"Tidak ada sesuatu musibah yang menimpa (seseorang), kecuali dengan izin Allah; dan barangsiapa beriman kepada Allah, niscaya Allah akan memberi petunjuk kepada hatinya. Dan Allah Maha Mengetahui segala sesuatu. "},{"id":"12","ar":"وَاَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَۚ فَاِنْ تَوَلَّيْتُمْ فَاِنَّمَا عَلٰى رَسُوْلِنَا الْبَلٰغُ الْمُبِيْنُ ","bc":"wa aṭī'ullāha wa aṭī'ur-rasụl, fa in tawallaitum fa innamā 'alā rasụlinal-balāgul-mubīn","at":"Dan taatlah kepada Allah dan taatlah kepada Rasul. Jika kamu berpaling maka sesungguhnya kewajiban Rasul Kami hanyalah menyampaikan (amanah Allah) dengan terang. "},{"id":"13","ar":"اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ","bc":"allāhu lā ilāha illā huw, wa 'alallāhi falyatawakkalil-mu`minụn","at":"(Dialah) Allah, tidak ada tuhan selain Dia. Dan hendaklah orang-orang mukmin bertawakal kepada Allah."},{"id":"14","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنَّ مِنْ اَزْوَاجِكُمْ وَاَوْلَادِكُمْ عَدُوًّا لَّكُمْ فَاحْذَرُوْهُمْۚ وَاِنْ تَعْفُوْا وَتَصْفَحُوْا وَتَغْفِرُوْا فَاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"yā ayyuhallażīna āmanū inna min azwājikum wa aulādikum 'aduwwal lakum faḥżarụhum, wa in ta'fụ wa taṣfaḥụ wa tagfirụ fa innallāha gafụrur raḥīm","at":"Wahai orang-orang yang beriman! Sesungguhnya di antara istri-istrimu dan anak-anakmu ada yang menjadi musuh bagimu, maka berhati-hatilah kamu terhadap mereka; dan jika kamu maafkan dan kamu santuni serta ampuni (mereka), maka sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"15","ar":"اِنَّمَآ اَمْوَالُكُمْ وَاَوْلَادُكُمْ فِتْنَةٌ ۗوَاللّٰهُ عِنْدَهٗٓ اَجْرٌ عَظِيْمٌ ","bc":"innamā amwālukum wa aulādukum fitnah, wallāhu 'indahū ajrun 'aẓīm","at":"Sesungguhnya hartamu dan anak-anakmu hanyalah cobaan (bagimu), dan di sisi Allah pahala yang besar. "},{"id":"16","ar":"فَاتَّقُوا اللّٰهَ مَا اسْتَطَعْتُمْ وَاسْمَعُوْا وَاَطِيْعُوْا وَاَنْفِقُوْا خَيْرًا لِّاَنْفُسِكُمْۗ وَمَنْ يُّوْقَ شُحَّ نَفْسِهٖ فَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ","bc":"fattaqullāha mastaṭa'tum wasma'ụ wa aṭī'ụ wa anfiqụ khairal li`anfusikum, wa may yụqa syuḥḥa nafsihī fa ulā`ika humul-mufliḥụn","at":"Maka bertakwalah kamu kepada Allah menurut kesanggupanmu dan dengarlah serta taatlah; dan infakkanlah harta yang baik untuk dirimu. Dan barang-siapa dijaga dirinya dari kekikiran, mereka itulah orang-orang yang beruntung. "},{"id":"17","ar":"اِنْ تُقْرِضُوا اللّٰهَ قَرْضًا حَسَنًا يُّضٰعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْۗ وَاللّٰهُ شَكُوْرٌ حَلِيْمٌۙ ","bc":"in tuqriḍullāha qarḍan ḥasanay yuḍā'if-hu lakum wa yagfir lakum, wallāhu syakụrun ḥalīm","at":"Jika kamu meminjamkan kepada Allah dengan pinjaman yang baik, niscaya Dia melipatgandakan (balasan) untukmu dan mengampuni kamu. Dan Allah Maha Mensyukuri, Maha Penyantun."},{"id":"18","ar":"عٰلِمُ الْغَيْبِ وَالشَّهَادَةِ الْعَزِيْزُ الْحَكِيْمُ ","bc":"'ālimul-gaibi wasy-syahādatil-'azīzul-ḥakīm","at":"Yang Mengetahui yang gaib dan yang nyata. Yang Mahaperkasa, Mahabijaksana."}],at_tahrim=[{"id":"1","ar":"يٰٓاَيُّهَا النَّبِيُّ لِمَ تُحَرِّمُ مَآ اَحَلَّ اللّٰهُ لَكَۚ تَبْتَغِيْ مَرْضَاتَ اَزْوَاجِكَۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ","bc":"yā ayyuhan-nabiyyu lima tuḥarrimu mā aḥallallāhu lak, tabtagī marḍāta azwājik, wallāhu gafụrur raḥīm","at":"Wahai Nabi! Mengapa engkau mengharamkan apa yang dihalalkan Allah bagimu? Engkau ingin menyenangkan hati istri-istrimu? Dan Allah Maha Pengampun, Maha Penyayang. "},{"id":"2","ar":"قَدْ فَرَضَ اللّٰهُ لَكُمْ تَحِلَّةَ اَيْمَانِكُمْۚ وَاللّٰهُ مَوْلٰىكُمْۚ وَهُوَ الْعَلِيْمُ الْحَكِيْمُ ","bc":"qad faraḍallāhu lakum taḥillata aimānikum, wallāhu maulākum, wa huwal-'alīmul-ḥakīm","at":"Sungguh, Allah telah mewajibkan kepadamu membebaskan diri dari sumpahmu; dan Allah adalah pelindungmu dan Dia Maha Mengetahui, Mahabijaksana. "},{"id":"3","ar":"وَاِذْ اَسَرَّ النَّبِيُّ اِلٰى بَعْضِ اَزْوَاجِهٖ حَدِيْثًاۚ فَلَمَّا نَبَّاَتْ بِهٖ وَاَظْهَرَهُ اللّٰهُ عَلَيْهِ عَرَّفَ بَعْضَهٗ وَاَعْرَضَ عَنْۢ بَعْضٍۚ فَلَمَّا نَبَّاَهَا بِهٖ قَالَتْ مَنْ اَنْۢبَاَكَ هٰذَاۗ قَالَ نَبَّاَنِيَ الْعَلِيْمُ الْخَبِيْرُ ","bc":"wa iż asarran-nabiyyu ilā ba'ḍi azwājihī ḥadīṡā, fa lammā nabba`at bihī wa aẓ-harahullāhu 'alaihi 'arrafa ba'ḍahụ wa a'raḍa 'am ba'ḍ, fa lammā nabba`ahā bihī qālat man amba`aka hāżā, qāla nabba`aniyal-'alīmul-khabīr","at":"Dan ingatlah ketika secara rahasia Nabi membicarakan suatu peristiwa kepada salah seorang istrinya (Hafsah). Lalu dia menceritakan peristiwa itu (kepada Aisyah) dan Allah memberitahukan peristiwa itu kepadanya (Nabi), lalu (Nabi) memberitahukan (kepada Hafsah) sebagian dan menyembunyikan sebagian yang lain. Maka ketika dia (Nabi) memberitahukan pembicaraan itu kepadanya (Hafsah), dia bertanya, “Siapa yang telah memberitahukan hal ini kepadamu?” Nabi menjawab, “Yang memberitahukan kepadaku adalah Allah Yang Maha Mengetahui, Mahateliti.” "},{"id":"4","ar":"اِنْ تَتُوْبَآ اِلَى اللّٰهِ فَقَدْ صَغَتْ قُلُوْبُكُمَاۚ وَاِنْ تَظٰهَرَا عَلَيْهِ فَاِنَّ اللّٰهَ هُوَ مَوْلٰىهُ وَجِبْرِيْلُ وَصَالِحُ الْمُؤْمِنِيْنَۚ وَالْمَلٰۤىِٕكَةُ بَعْدَ ذٰلِكَ ظَهِيْرٌ ","bc":"in tatụbā ilallāhi fa qad ṣagat qulụbukumā, wa in taẓāharā 'alaihi fa innallāha huwa maulāhu wa jibrīlu wa ṣāliḥul-mu`minīn, wal-malā`ikatu ba'da żālika ẓahīr","at":"Jika kamu berdua bertobat kepada Allah, maka sungguh, hati kamu berdua telah condong (untuk menerima kebenaran); dan jika kamu berdua saling bantu-membantu menyusahkan Nabi, maka sungguh, Allah menjadi pelindungnya dan (juga) Jibril dan orang-orang mukmin yang baik; dan selain itu malaikat-malaikat adalah penolongnya. "},{"id":"5","ar":"عَسٰى رَبُّهٗٓ اِنْ طَلَّقَكُنَّ اَنْ يُّبْدِلَهٗٓ اَزْوَاجًا خَيْرًا مِّنْكُنَّ مُسْلِمٰتٍ مُّؤْمِنٰتٍ قٰنِتٰتٍ تٰۤىِٕبٰتٍ عٰبِدٰتٍ سٰۤىِٕحٰتٍ ثَيِّبٰتٍ وَّاَبْكَارًا ","bc":"'asā rabbuhū in ṭallaqakunna ay yubdilahū azwājan khairam mingkunna muslimātim mu`mināting qānitātin tā`ibātin 'ābidātin sā`iḥātin ṡayyibātiw wa abkārā","at":"Jika dia (Nabi) menceraikan kamu, boleh jadi Tuhan akan memberi ganti kepadanya dengan istri-istri yang lebih baik dari kamu, perempuan-perempuan yang patuh, yang beriman, yang taat, yang bertobat, yang beribadah, yang berpuasa, yang janda dan yang perawan. "},{"id":"6","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا قُوْٓا اَنْفُسَكُمْ وَاَهْلِيْكُمْ نَارًا وَّقُوْدُهَا النَّاسُ وَالْحِجَارَةُ عَلَيْهَا مَلٰۤىِٕكَةٌ غِلَاظٌ شِدَادٌ لَّا يَعْصُوْنَ اللّٰهَ مَآ اَمَرَهُمْ وَيَفْعَلُوْنَ مَا يُؤْمَرُوْنَ ","bc":"yā ayyuhallażīna āmanụ qū anfusakum wa ahlīkum nāraw wa qụduhan-nāsu wal-ḥijāratu 'alaihā malā`ikatun gilāẓun syidādul lā ya'ṣụnallāha mā amarahum wa yaf'alụna mā yu`marụn","at":"Wahai orang-orang yang beriman! Peliharalah dirimu dan keluargamu dari api neraka yang bahan bakarnya adalah manusia dan batu; penjaganya malaikat-malaikat yang kasar, dan keras, yang tidak durhaka kepada Allah terhadap apa yang Dia perintahkan kepada mereka dan selalu mengerjakan apa yang diperintahkan. "},{"id":"7","ar":"يٰٓاَيُّهَا الَّذِيْنَ كَفَرُوْا لَا تَعْتَذِرُوا الْيَوْمَۗ اِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُوْنَ","bc":"yā ayyuhallażīna kafarụ lā ta'tażirul-yaụm, innamā tujzauna mā kuntum ta'malụn","at":"Wahai orang-orang kafir! Janganlah kamu mengemukakan alasan pada hari ini. Sesungguhnya kamu hanya diberi balasan menurut apa yang telah kamu kerjakan. "},{"id":"8","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا تُوْبُوْٓا اِلَى اللّٰهِ تَوْبَةً نَّصُوْحًاۗ عَسٰى رَبُّكُمْ اَنْ يُّكَفِّرَ عَنْكُمْ سَيِّاٰتِكُمْ وَيُدْخِلَكُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُۙ يَوْمَ لَا يُخْزِى اللّٰهُ النَّبِيَّ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗۚ نُوْرُهُمْ يَسْعٰى بَيْنَ اَيْدِيْهِمْ وَبِاَيْمَانِهِمْ يَقُوْلُوْنَ رَبَّنَآ اَتْمِمْ لَنَا نُوْرَنَا وَاغْفِرْ لَنَاۚ اِنَّكَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"yā ayyuhallażīna āmanụ tụbū ilallāhi taubatan naṣụḥā, 'asā rabbukum ay yukaffira 'angkum sayyi`ātikum wa yudkhilakum jannātin tajrī min taḥtihal-an-hāru yauma lā yukhzillāhun-nabiyya wallażīna āmanụ ma'ah, nụruhum yas'ā baina aidīhim wa bi`aimānihim yaqụlụna rabbanā atmim lanā nụranā wagfir lanā, innaka 'alā kulli syai`ing qadīr","at":"Wahai orang-orang yang beriman! Bertobatlah kepada Allah dengan tobat yang semurni-murninya, mudah-mudahan Tuhan kamu akan menghapus kesalahan-kesalahanmu dan memasukkan kamu ke dalam surga yang mengalir di bawahnya sungai-sungai, pada hari ketika Allah tidak mengecewakan Nabi dan orang-orang yang beriman bersama dengannya; sedang cahaya mereka memancar di hadapan dan di sebelah kanan mereka, sambil mereka berkata, “Ya Tuhan kami, sempurnakanlah untuk kami cahaya kami dan ampunilah kami; Sungguh, Engkau Mahakuasa atas segala sesuatu.” "},{"id":"9","ar":"يٰٓاَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنٰفِقِيْنَ وَاغْلُظْ عَلَيْهِمْۗ وَمَأْوٰىهُمْ جَهَنَّمُۗ وَبِئْسَ الْمَصِيْرُ ","bc":"yā ayyuhan-nabiyyu jāhidil-kuffāra wal-munāfiqīna wagluẓ 'alaihim, wa ma`wāhum jahannam, wa bi`sal-maṣīr","at":"Wahai Nabi! Perangilah orang-orang kafir dan orang-orang munafik dan bersikap keraslah terhadap mereka. Tempat mereka adalah neraka Jahanam dan itulah seburuk-buruk tempat kembali. "},{"id":"10","ar":"ضَرَبَ اللّٰهُ مَثَلًا لِّلَّذِيْنَ كَفَرُوا امْرَاَتَ نُوْحٍ وَّامْرَاَتَ لُوْطٍۗ كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَالِحَيْنِ فَخَانَتٰهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ اللّٰهِ شَيْـًٔا وَّقِيْلَ ادْخُلَا النَّارَ مَعَ الدَّاخِلِيْنَ ","bc":"ḍaraballāhu maṡalal lillażīna kafarumra`ata nụḥiw wamra`ata lụṭ, kānatā taḥta 'abdaini min 'ibādinā ṣāliḥaini fa khānatāhumā fa lam yugniyā 'an-humā minallāhi syai`aw wa qīladkhulan-nāra ma'ad-dākhilīn","at":"Allah membuat perumpamaan bagi orang-orang kafir, istri Nuh dan istri Lut. Keduanya berada di bawah pengawasan dua orang hamba yang saleh di antara hamba-hamba Kami; lalu kedua istri itu berkhianat kepada kedua suaminya, tetapi kedua suaminya itu tidak dapat membantu mereka sedikit pun dari (siksaan) Allah; dan dikatakan (kepada kedua istri itu), “Masuklah kamu berdua ke neraka bersama orang-orang yang masuk (neraka).” "},{"id":"11","ar":"وَضَرَبَ اللّٰهُ مَثَلًا لِّلَّذِيْنَ اٰمَنُوا امْرَاَتَ فِرْعَوْنَۘ اِذْ قَالَتْ رَبِّ ابْنِ لِيْ عِنْدَكَ بَيْتًا فِى الْجَنَّةِ وَنَجِّنِيْ مِنْ فِرْعَوْنَ وَعَمَلِهٖ وَنَجِّنِيْ مِنَ الْقَوْمِ الظّٰلِمِيْنَۙ ","bc":"wa ḍaraballāhu maṡalal lillażīna āmanumra`ata fir'aụn, iż qālat rabbibni lī 'indaka baitan fil-jannati wa najjinī min fir'auna wa 'amalihī wa najjinī minal-qaumiẓ-ẓālimīn","at":"Dan Allah membuat perumpamaan bagi orang-orang yang beriman, istri Fir‘aun, ketika dia berkata, “Ya Tuhanku, bangunkanlah untukku sebuah rumah di sisi-Mu dalam surga dan selamatkanlah aku dari Fir‘aun dan perbuatannya, dan selamatkanlah aku dari kaum yang zalim,” "},{"id":"12","ar":"وَمَرْيَمَ ابْنَتَ عِمْرٰنَ الَّتِيْٓ اَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيْهِ مِنْ رُّوْحِنَا وَصَدَّقَتْ بِكَلِمٰتِ رَبِّهَا وَكُتُبِهٖ وَكَانَتْ مِنَ الْقٰنِتِيْنَ ۔","bc":"wa maryamabnata 'imrānallatī aḥṣanat farjahā fa nafakhnā fīhi mir rụḥinā wa ṣaddaqat bikalimāti rabbihā wa kutubihī wa kānat minal-qānitīn","at":"dan Maryam putri Imran yang memelihara kehormatannya, maka Kami tiupkan ke dalam rahimnya sebagian dari roh (ciptaan) Kami; dan dia membenarkan kalimat-kalimat Tuhannya dan kitab-kitab-Nya; dan dia termasuk orang-orang yang taat. "}],at_takasur=[{"id":"1","ar":"اَلْهٰىكُمُ التَّكَاثُرُۙ","bc":"al-hākumut-takāṡur","at":"Bermegah-megahan telah melalaikan kamu,"},{"id":"2","ar":"حَتّٰى زُرْتُمُ الْمَقَابِرَۗ","bc":"ḥattā zurtumul-maqābir","at":"sampai kamu masuk ke dalam kubur."},{"id":"3","ar":"كَلَّا سَوْفَ تَعْلَمُوْنَۙ","bc":"kallā saufa ta'lamụn","at":"Sekali-kali tidak! Kelak kamu akan mengetahui (akibat perbuatanmu itu),"},{"id":"4","ar":"ثُمَّ كَلَّا سَوْفَ تَعْلَمُوْنَ ","bc":"ṡumma kallā saufa ta'lamụn","at":"kemudian sekali-kali tidak! Kelak kamu akan mengetahui."},{"id":"5","ar":"كَلَّا لَوْ تَعْلَمُوْنَ عِلْمَ الْيَقِيْنِۗ","bc":"kallā lau ta'lamụna 'ilmal-yaqīn","at":"Sekali-kali tidak! Sekiranya kamu mengetahui dengan pasti,"},{"id":"6","ar":"لَتَرَوُنَّ الْجَحِيْمَۙ","bc":"latarawunnal-jaḥīm","at":"niscaya kamu benar-benar akan melihat neraka Jahim,"},{"id":"7","ar":"ثُمَّ لَتَرَوُنَّهَا عَيْنَ الْيَقِيْنِۙ","bc":"ṡumma latarawunnahā 'ainal-yaqīn","at":"kemudian kamu benar-benar akan melihatnya dengan mata kepala sendiri,"},{"id":"8","ar":"ثُمَّ لَتُسْـَٔلُنَّ يَوْمَىِٕذٍ عَنِ النَّعِيْمِ","bc":"ṡumma latus`alunna yauma`iżin 'anin-na'īm","at":"kemudian kamu benar-benar akan ditanya pada hari itu tentang kenikmatan (yang megah di dunia itu)."}],at_takwir=[{"id":"1","ar":"اِذَا الشَّمْسُ كُوِّرَتْۖ","bc":"iżasy-syamsu kuwwirat","at":"Apabila matahari digulung,"},{"id":"2","ar":"وَاِذَا النُّجُوْمُ انْكَدَرَتْۖ","bc":"wa iżan-nujụmungkadarat","at":"dan apabila bintang-bintang berjatuhan,"},{"id":"3","ar":"وَاِذَا الْجِبَالُ سُيِّرَتْۖ","bc":"wa iżal-jibālu suyyirat","at":"dan apabila gunung-gunung dihancurkan,"},{"id":"4","ar":"وَاِذَا الْعِشَارُ عُطِّلَتْۖ","bc":"wa iżal-'isyāru 'uṭṭilat","at":"dan apabila unta-unta yang bunting ditinggalkan (tidak terurus),"},{"id":"5","ar":"وَاِذَا الْوُحُوْشُ حُشِرَتْۖ","bc":"wa iżal-wuḥụsyu ḥusyirat","at":"dan apabila binatang-binatang liar dikumpulkan,"},{"id":"6","ar":"وَاِذَا الْبِحَارُ سُجِّرَتْۖ","bc":"wa iżal-biḥāru sujjirat","at":"dan apabila lautan dipanaskan,"},{"id":"7","ar":"وَاِذَا النُّفُوْسُ زُوِّجَتْۖ","bc":"wa iżan-nufụsu zuwwijat","at":"dan apabila roh-roh dipertemukan (dengan tubuh), "},{"id":"8","ar":"وَاِذَا الْمَوْءٗدَةُ سُىِٕلَتْۖ ","bc":"wa iżal-mau`ụdatu su`ilat","at":"dan apabila bayi-bayi perempuan yang dikubur hidup-hidup ditanya, "},{"id":"9","ar":"بِاَيِّ ذَنْۢبٍ قُتِلَتْۚ","bc":"bi`ayyi żambing qutilat","at":"karena dosa apa dia dibunuh? "},{"id":"10","ar":"وَاِذَا الصُّحُفُ نُشِرَتْۖ","bc":"wa iżaṣ-ṣuḥufu nusyirat","at":"Dan apabila lembaran-lembaran (catatan amal) telah dibuka lebar-lebar, "},{"id":"11","ar":"وَاِذَا السَّمَاۤءُ كُشِطَتْۖ","bc":"wa iżas-samā`u kusyiṭat","at":"dan apabila langit dilenyapkan, "},{"id":"12","ar":"وَاِذَا الْجَحِيْمُ سُعِّرَتْۖ","bc":"wa iżal-jaḥīmu su''irat","at":"dan apabila neraka Jahim dinyalakan, "},{"id":"13","ar":"وَاِذَا الْجَنَّةُ اُزْلِفَتْۖ","bc":"wa iżal-jannatu uzlifat","at":"dan apabila surga didekatkan, "},{"id":"14","ar":"عَلِمَتْ نَفْسٌ مَّآ اَحْضَرَتْۗ","bc":"'alimat nafsum mā aḥḍarat","at":"setiap jiwa akan mengetahui apa yang telah dikerjakannya. "},{"id":"15","ar":"فَلَآ اُقْسِمُ بِالْخُنَّسِۙ","bc":"fa lā uqsimu bil-khunnas","at":"Aku bersumpah demi bintang-bintang,"},{"id":"16","ar":"الْجَوَارِ الْكُنَّسِۙ","bc":"al-jawāril-kunnas","at":"yang beredar dan terbenam, "},{"id":"17","ar":"وَالَّيْلِ اِذَا عَسْعَسَۙ","bc":"wal-laili iżā 'as'as","at":"demi malam apabila telah larut, "},{"id":"18","ar":"وَالصُّبْحِ اِذَا تَنَفَّسَۙ","bc":"waṣ-ṣub-ḥi iżā tanaffas","at":"dan demi subuh apabila fajar telah menyingsing, "},{"id":"19","ar":"اِنَّهٗ لَقَوْلُ رَسُوْلٍ كَرِيْمٍۙ","bc":"innahụ laqaulu rasụling karīm","at":"sesungguhnya (Al-Qur'an) itu benar-benar firman (Allah yang dibawa oleh) utusan yang mulia (Jibril),"},{"id":"20","ar":"ذِيْ قُوَّةٍ عِنْدَ ذِى الْعَرْشِ مَكِيْنٍۙ","bc":"żī quwwatin 'inda żil-'arsyi makīn","at":"yang memiliki kekuatan, memiliki kedudukan tinggi di sisi (Allah) yang memiliki ‘Arsy,"},{"id":"21","ar":"مُّطَاعٍ ثَمَّ اَمِيْنٍۗ","bc":"muṭā'in ṡamma amīn","at":"yang di sana (di alam malaikat) ditaati dan dipercaya."},{"id":"22","ar":"وَمَا صَاحِبُكُمْ بِمَجْنُوْنٍۚ","bc":"wa mā ṣāḥibukum bimajnụn","at":"Dan temanmu (Muhammad) itu bukanlah orang gila. "},{"id":"23","ar":"وَلَقَدْ رَاٰهُ بِالْاُفُقِ الْمُبِيْنِۚ","bc":"wa laqad ra`āhu bil-ufuqil-mubīn","at":"Dan sungguh, dia (Muhammad) telah melihatnya (Jibril) di ufuk yang terang. "},{"id":"24","ar":"وَمَا هُوَ عَلَى الْغَيْبِ بِضَنِيْنٍۚ","bc":"wa mā huwa 'alal-gaibi biḍanīn","at":"Dan dia (Muhammad) bukanlah seorang yang kikir (enggan) untuk menerangkan yang gaib."},{"id":"25","ar":"وَمَا هُوَ بِقَوْلِ شَيْطٰنٍ رَّجِيْمٍۚ","bc":"wa mā huwa biqauli syaiṭānir rajīm","at":"Dan (Al-Qur'an) itu bukanlah perkataan setan yang terkutuk,"},{"id":"26","ar":"فَاَيْنَ تَذْهَبُوْنَۗ","bc":"fa aina taż-habụn","at":"maka ke manakah kamu akan pergi?"},{"id":"27","ar":"اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَۙ","bc":"in huwa illā żikrul lil-'ālamīn","at":"(Al-Qur'an) itu tidak lain adalah peringatan bagi seluruh alam, "},{"id":"28","ar":"لِمَنْ شَاۤءَ مِنْكُمْ اَنْ يَّسْتَقِيْمَۗ","bc":"liman syā`a mingkum ay yastaqīm","at":"(yaitu) bagi siapa di antara kamu yang menghendaki menempuh jalan yang lurus. "},{"id":"29","ar":"وَمَا تَشَاۤءُوْنَ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ رَبُّ الْعٰلَمِيْنَ","bc":"wa mā tasyā`ụna illā ay yasyā`allāhu rabbul-'ālamīn","at":"Dan kamu tidak dapat menghendaki (menempuh jalan itu) kecuali apabila dikehendaki Allah, Tuhan seluruh alam."}],at_talaq=[{"id":"1","ar":"يٰٓاَيُّهَا النَّبِيُّ اِذَا طَلَّقْتُمُ النِّسَاۤءَ فَطَلِّقُوْهُنَّ لِعِدَّتِهِنَّ وَاَحْصُوا الْعِدَّةَۚ وَاتَّقُوا اللّٰهَ رَبَّكُمْۚ لَا تُخْرِجُوْهُنَّ مِنْۢ بُيُوْتِهِنَّ وَلَا يَخْرُجْنَ اِلَّآ اَنْ يَّأْتِيْنَ بِفَاحِشَةٍ مُّبَيِّنَةٍۗ وَتِلْكَ حُدُوْدُ اللّٰهِ ۗوَمَنْ يَّتَعَدَّ حُدُوْدَ اللّٰهِ فَقَدْ ظَلَمَ نَفْسَهٗ ۗ لَا تَدْرِيْ لَعَلَّ اللّٰهَ يُحْدِثُ بَعْدَ ذٰلِكَ اَمْرًا","bc":"yā ayyuhan-nabiyyu iżā ṭallaqtumun-nisā`a fa ṭalliqụhunna li'iddatihinna wa aḥṣul-'iddah, wattaqullāha rabbakum, lā tukhrijụhunna mim buyụtihinna wa lā yakhrujna illā ay ya`tīna bifāḥisyatim mubayyinah, wa tilka ḥudụdullāh, wa may yata'adda ḥudụdallāhi fa qad ẓalama nafsah, lā tadrī la'allallāha yuḥdiṡu ba'da żālika amrā","at":"Wahai Nabi! Apabila kamu menceraikan istri-istrimu maka hendaklah kamu ceraikan mereka pada waktu mereka dapat (menghadapi) idahnya (yang wajar), dan hitunglah waktu idah itu, serta bertakwalah kepada Allah Tuhanmu. Janganlah kamu keluarkan mereka dari rumahnya dan janganlah (diizinkan) keluar kecuali jika mereka mengerjakan perbuatan keji yang jelas. Itulah hukum-hukum Allah, dan barangsiapa melanggar hukum-hukum Allah, maka sungguh, dia telah berbuat zalim terhadap dirinya sendiri. Kamu tidak mengetahui barangkali setelah itu Allah mengadakan suatu ketentuan yang baru. "},{"id":"2","ar":"فَاِذَا بَلَغْنَ اَجَلَهُنَّ فَاَمْسِكُوْهُنَّ بِمَعْرُوْفٍ اَوْ فَارِقُوْهُنَّ بِمَعْرُوْفٍ وَّاَشْهِدُوْا ذَوَيْ عَدْلٍ مِّنْكُمْ وَاَقِيْمُوا الشَّهَادَةَ لِلّٰهِ ۗذٰلِكُمْ يُوْعَظُ بِهٖ مَنْ كَانَ يُؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ ەۗ وَمَنْ يَّتَّقِ اللّٰهَ يَجْعَلْ لَّهٗ مَخْرَجًا ۙ","bc":"fa iżā balagna ajalahunna fa amsikụhunna bima'rụfin au fāriqụhunna bima'rụfiw wa asy-hidụ żawai 'adlim mingkum wa aqīmusy-syahādata lillāh, żālikum yụ'aẓu bihī mang kāna yu`minu billāhi wal-yaumil-ākhir, wa may yattaqillāha yaj'al lahụ makhrajā","at":"Maka apabila mereka telah mendekati akhir idahnya, maka rujuklah (kembali kepada) mereka dengan baik atau lepaskanlah mereka dengan baik dan persaksikanlah dengan dua orang saksi yang adil di antara kamu dan hendaklah kamu tegakkan kesaksian itu karena Allah. Demikianlah pengajaran itu diberikan bagi orang yang beriman kepada Allah dan hari akhirat. Barangsiapa bertakwa kepada Allah niscaya Dia akan membukakan jalan keluar baginya, "},{"id":"3","ar":"وَّيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُۗ وَمَنْ يَّتَوَكَّلْ عَلَى اللّٰهِ فَهُوَ حَسْبُهٗ ۗاِنَّ اللّٰهَ بَالِغُ اَمْرِهٖۗ قَدْ جَعَلَ اللّٰهُ لِكُلِّ شَيْءٍ قَدْرًا ","bc":"wa yarzuq-hu min ḥaiṡu lā yaḥtasib, wa may yatawakkal 'alallāhi fa huwa ḥasbuh, innallāha bāligu amrih, qad ja'alallāhu likulli syai`ing qadrā","at":"dan Dia memberinya rezeki dari arah yang tidak disangka-sangkanya. Dan barangsiapa bertawakal kepada Allah, niscaya Allah akan mencukupkan (keperluan)nya. Sesungguhnya Allah melaksanakan urusan-Nya. Sungguh, Allah telah mengadakan ketentuan bagi setiap sesuatu. "},{"id":"4","ar":"وَالّٰۤـِٔيْ يَىِٕسْنَ مِنَ الْمَحِيْضِ مِنْ نِّسَاۤىِٕكُمْ اِنِ ارْتَبْتُمْ فَعِدَّتُهُنَّ ثَلٰثَةُ اَشْهُرٍۙ وَّالّٰۤـِٔيْ لَمْ يَحِضْنَۗ وَاُولَاتُ الْاَحْمَالِ اَجَلُهُنَّ اَنْ يَّضَعْنَ حَمْلَهُنَّۗ وَمَنْ يَّتَّقِ اللّٰهَ يَجْعَلْ لَّهٗ مِنْ اَمْرِهٖ يُسْرًا ","bc":"wal-lā`i ya`isna minal-maḥīḍi min nisā`ikum inirtabtum fa 'iddatuhunna ṡalāṡatu asy-huriw wal-lā`i lam yahiḍn, wa ulātul-aḥmāli ajaluhunna ay yaḍa'na ḥamlahunn, wa may yattaqillāha yaj'al lahụ min amrihī yusrā","at":"Perempuan-perempuan yang tidak haid lagi (menopause) di antara istri-istrimu jika kamu ragu-ragu (tentang masa idahnya) maka idahnya adalah tiga bulan; dan begitu (pula) perempuan-perempuan yang tidak haid. Sedangkan perempuan-perempuan yang hamil, waktu idah mereka itu ialah sampai mereka melahirkan kandungannya. Dan barangsiapa bertakwa kepada Allah, niscaya Dia menjadikan kemudahan baginya dalam urusannya. "},{"id":"5","ar":"ذٰلِكَ اَمْرُ اللّٰهِ اَنْزَلَهٗٓ اِلَيْكُمْۗ وَمَنْ يَّتَّقِ اللّٰهَ يُكَفِّرْ عَنْهُ سَيِّاٰتِهٖ وَيُعْظِمْ لَهٗٓ اَجْرًا ","bc":"żālika amrullāhi anzalahū ilaikum, wa may yattaqillāha yukaffir 'an-hu sayyi`ātihī wa yu'ẓim lahū ajrā","at":"Itulah perintah Allah yang diturunkan-Nya kepada kamu; barangsiapa bertakwa kepada Allah, niscaya Allah akan menghapus kesalahan-kesalahannya dan akan melipatgandakan pahala baginya. "},{"id":"6","ar":"اَسْكِنُوْهُنَّ مِنْ حَيْثُ سَكَنْتُمْ مِّنْ وُّجْدِكُمْ وَلَا تُضَاۤرُّوْهُنَّ لِتُضَيِّقُوْا عَلَيْهِنَّۗ وَاِنْ كُنَّ اُولَاتِ حَمْلٍ فَاَنْفِقُوْا عَلَيْهِنَّ حَتّٰى يَضَعْنَ حَمْلَهُنَّۚ فَاِنْ اَرْضَعْنَ لَكُمْ فَاٰتُوْهُنَّ اُجُوْرَهُنَّۚ وَأْتَمِرُوْا بَيْنَكُمْ بِمَعْرُوْفٍۚ وَاِنْ تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهٗٓ اُخْرٰىۗ ","bc":"askinụhunna min ḥaiṡu sakantum miw wujdikum wa lā tuḍārrụhunna lituḍayyiqụ 'alaihinn, wa ing kunna ulāti ḥamlin fa anfiqụ 'alaihinna ḥattā yaḍa'na ḥamlahunn, fa in arḍa'na lakum fa ātụhunna ujụrahunn, wa`tamirụ bainakum bima'rụf, wa in ta'āsartum fa saturḍi'u lahū ukhrā","at":"Tempatkanlah mereka (para istri) di mana kamu bertempat tinggal menurut kemampuanmu dan janganlah kamu menyusahkan mereka untuk menyempitkan (hati) mereka. Dan jika mereka (istri-istri yang sudah ditalak) itu sedang hamil, maka berikanlah kepada mereka nafkahnya sampai mereka melahirkan, kemudian jika mereka menyusukan (anak-anak)mu maka berikanlah imbalannya kepada mereka; dan musyawarahkanlah di antara kamu (segala sesuatu) dengan baik; dan jika kamu menemui kesulitan, maka perempuan lain boleh menyusukan (anak itu) untuknya. "},{"id":"7","ar":"لِيُنْفِقْ ذُوْ سَعَةٍ مِّنْ سَعَتِهٖۗ وَمَنْ قُدِرَ عَلَيْهِ رِزْقُهٗ فَلْيُنْفِقْ مِمَّآ اٰتٰىهُ اللّٰهُ ۗ لَا يُكَلِّفُ اللّٰهُ نَفْسًا اِلَّا مَآ اٰتٰىهَاۗ سَيَجْعَلُ اللّٰهُ بَعْدَ عُسْرٍ يُّسْرًا ","bc":"liyunfiq żụ sa'atim min sa'atih, wa mang qudira 'alaihi rizquhụ falyunfiq mimmā ātāhullāh, lā yukallifullāhu nafsan illā mā ātāhā, sayaj'alullāhu ba'da 'usriy yusrā","at":"Hendaklah orang yang mempunyai keluasan memberi nafkah menurut kemampuannya, dan orang yang terbatas rezekinya, hendaklah memberi nafkah dari harta yang diberikan Allah kepadanya. Allah tidak membebani kepada seseorang melainkan (sesuai) dengan apa yang diberikan Allah kepadanya. Allah kelak akan memberikan kelapangan setelah kesempitan. "},{"id":"8","ar":"وَكَاَيِّنْ مِّنْ قَرْيَةٍ عَتَتْ عَنْ اَمْرِ رَبِّهَا وَرُسُلِهٖ فَحَاسَبْنٰهَا حِسَابًا شَدِيْدًاۙ وَّعَذَّبْنٰهَا عَذَابًا نُّكْرًا ","bc":"wa ka`ayyim ming qaryatin 'atat 'an amri rabbihā wa rusulihī fa ḥāsabnāhā ḥisāban syadīdaw wa 'ażżabnāhā 'ażāban nukrā","at":"Dan betapa banyak (penduduk) negeri yang mendurhakai perintah Tuhan mereka dan rasul-rasul-Nya, maka Kami buat perhitungan terhadap penduduk negeri itu dengan perhitungan yang ketat, dan Kami azab mereka dengan azab yang mengerikan (di akhirat),"},{"id":"9","ar":"فَذَاقَتْ وَبَالَ اَمْرِهَا وَكَانَ عَاقِبَةُ اَمْرِهَا خُسْرًا ","bc":"fa żāqat wa bāla amrihā wa kāna 'āqibatu amrihā khusrā","at":"sehingga mereka merasakan akibat yang buruk dari perbuatannya, dan akibat perbuatan mereka, itu adalah kerugian yang besar. "},{"id":"10","ar":"اَعَدَّ اللّٰهُ لَهُمْ عَذَابًا شَدِيْدًا ۖفَاتَّقُوا اللّٰهَ يٰٓاُولِى الْاَلْبَابِۛ الَّذِيْنَ اٰمَنُوْا ۛ قَدْ اَنْزَلَ اللّٰهُ اِلَيْكُمْ ذِكْرًاۙ ","bc":"a'addallāhu lahum 'ażāban syadīdan fattaqullāha yā ulil-albāb, allażīna āmanụ qad anzalallāhu ilaikum żikrā","at":"Allah menyediakan azab yang keras bagi mereka, maka bertakwalah kepada Allah wahai orang-orang yang mempunyai akal! (Yaitu) orang-orang yang beriman. Sungguh, Allah telah menurunkan peringatan kepadamu, "},{"id":"11","ar":"رَّسُوْلًا يَّتْلُوْا عَلَيْكُمْ اٰيٰتِ اللّٰهِ مُبَيِّنٰتٍ لِّيُخْرِجَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ مِنَ الظُّلُمٰتِ اِلَى النُّوْرِۗ وَمَنْ يُّؤْمِنْۢ بِاللّٰهِ وَيَعْمَلْ صَالِحًا يُّدْخِلْهُ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًاۗ قَدْ اَحْسَنَ اللّٰهُ لَهٗ رِزْقًا ","bc":"rasụlay yatlụ 'alaikum āyātillāhi mubayyinātil liyukhrijallażīna āmanụ wa 'amiluṣ-ṣāliḥāti minaẓ-ẓulumāti ilan-nụr, wa may yu`mim billāhi wa ya'mal ṣāliḥay yudkhil-hu jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā abadā, qad aḥsanallāhu lahụ rizqā","at":"(dengan mengutus) seorang Rasul yang membacakan ayat-ayat Allah kepadamu yang menerangkan (bermacam-macam hukum), agar Dia mengeluarkan orang-orang yang beriman dan mengerjakan kebajikan, dari kegelapan kepada cahaya. Dan barangsiapa beriman kepada Allah dan mengerjakan kebajikan, niscaya Dia akan memasukkannya ke dalam surga-surga yang mengalir di bawahnya sungai-sungai; mereka kekal di dalamnya selama-lamanya. Sungguh, Allah memberikan rezeki yang baik kepadanya. "},{"id":"12","ar":"اَللّٰهُ الَّذِيْ خَلَقَ سَبْعَ سَمٰوٰتٍ وَّمِنَ الْاَرْضِ مِثْلَهُنَّۗ يَتَنَزَّلُ الْاَمْرُ بَيْنَهُنَّ لِتَعْلَمُوْٓا اَنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ەۙ وَّاَنَّ اللّٰهَ قَدْ اَحَاطَ بِكُلِّ شَيْءٍ عِلْمًا","bc":"allāhullażī khalaqa sab'a samāwātiw wa minal-arḍi miṡlahunn, yatanazzalul-amru bainahunna lita'lamū annallāha 'alā kulli syai`ing qadīruw wa annallāha qad aḥāṭa bikulli syai`in 'ilmā","at":"Allah yang menciptakan tujuh langit dan dari (penciptaan) bumi juga serupa. Perintah Allah berlaku padanya, agar kamu mengetahui bahwa Allah Mahakuasa atas segala sesuatu, dan ilmu Allah benar-benar meliputi segala sesuatu. "}],at_tariq=[{"id":"1","ar":"وَالسَّمَاۤءِ وَالطَّارِقِۙ ","bc":"was-samā`i waṭ-ṭāriq","at":"Demi langit dan yang datang pada malam hari."},{"id":"2","ar":"وَمَآ اَدْرٰىكَ مَا الطَّارِقُۙ ","bc":"wa mā adrāka maṭ-ṭāriq","at":"Dan tahukah kamu apakah yang datang pada malam hari itu?"},{"id":"3","ar":"النَّجْمُ الثَّاقِبُۙ ","bc":"an-najmuṡ-ṡāqib","at":"(yaitu) bintang yang bersinar tajam,"},{"id":"4","ar":"اِنْ كُلُّ نَفْسٍ لَّمَّا عَلَيْهَا حَافِظٌۗ ","bc":"ing kullu nafsil lammā 'alaihā ḥāfiẓ","at":"setiap orang pasti ada penjaganya."},{"id":"5","ar":"فَلْيَنْظُرِ الْاِنْسَانُ مِمَّ خُلِقَ ","bc":"falyanẓuril-insānu mimma khuliq","at":"Maka hendaklah manusia memperhatikan dari apa dia diciptakan."},{"id":"6","ar":"خُلِقَ مِنْ مَّاۤءٍ دَافِقٍۙ ","bc":"khuliqa mim mā`in dāfiq","at":"Dia diciptakan dari air (mani) yang terpancar,"},{"id":"7","ar":"يَّخْرُجُ مِنْۢ بَيْنِ الصُّلْبِ وَالتَّرَاۤىِٕبِۗ ","bc":"yakhruju mim bainiṣ-ṣulbi wat-tarā`ib","at":"yang keluar dari antara tulang punggung (sulbi) dan tulang dada."},{"id":"8","ar":"اِنَّهٗ عَلٰى رَجْعِهٖ لَقَادِرٌۗ ","bc":"innahụ 'alā raj'ihī laqādir","at":"Sungguh, Allah benar-benar kuasa untuk mengembalikannya (hidup setelah mati)."},{"id":"9","ar":"يَوْمَ تُبْلَى السَّرَاۤىِٕرُۙ ","bc":"yauma tublas-sarā`ir","at":"Pada hari ditampakkan segala rahasia,"},{"id":"10","ar":"فَمَا لَهٗ مِنْ قُوَّةٍ وَّلَا نَاصِرٍۗ ","bc":"fa mā lahụ ming quwwatiw wa lā nāṣir","at":"maka manusia tidak lagi mempunyai suatu kekuatan dan tidak (pula) ada penolong."},{"id":"11","ar":"وَالسَّمَاۤءِ ذَاتِ الرَّجْعِۙ ","bc":"was-samā`i żātir-raj'","at":"Demi langit yang mengandung hujan,"},{"id":"12","ar":"وَالْاَرْضِ ذَاتِ الصَّدْعِۙ ","bc":"wal-arḍi żātiṣ-ṣad'","at":"dan bumi yang mempunyai tumbuh-tumbuhan,"},{"id":"13","ar":"اِنَّهٗ لَقَوْلٌ فَصْلٌۙ ","bc":"innahụ laqaulun faṣl","at":"sungguh, (Al-Qur'an) itu benar-benar firman pemisah (antara yang hak dan yang batil),"},{"id":"14","ar":"وَّمَا هُوَ بِالْهَزْلِۗ ","bc":"wa mā huwa bil-hazl","at":"dan (Al-Qur'an) itu bukanlah sendagurauan."},{"id":"15","ar":"اِنَّهُمْ يَكِيْدُوْنَ كَيْدًاۙ ","bc":"innahum yakīdụna kaidā","at":"Sungguh, mereka (orang kafir) merencanakan tipu daya yang jahat."},{"id":"16","ar":"وَّاَكِيْدُ كَيْدًاۖ ","bc":"wa akīdu kaidā","at":"Dan Aku pun membuat rencana (tipu daya) yang jitu."},{"id":"17","ar":"فَمَهِّلِ الْكٰفِرِيْنَ اَمْهِلْهُمْ رُوَيْدًا ","bc":"fa mahhilil-kāfirīna am-hil-hum ruwaidā","at":"Karena itu berilah penangguhan kepada orang-orang kafir itu. Berilah mereka itu kesempatan untuk sementara waktu."}],at_taubah=[{"id":"1","ar":"بَرَاۤءَةٌ مِّنَ اللّٰهِ وَرَسُوْلِهٖٓ اِلَى الَّذِيْنَ عَاهَدْتُّمْ مِّنَ الْمُشْرِكِيْنَۗ","bc":"barā`atum minallāhi wa rasụlihī ilallażīna 'āhattum minal-musyrikīn","at":"(Inilah pernyataan) pemutusan hubungan dari Allah dan Rasul-Nya kepada orang-orang musyrik yang kamu telah mengadakan perjanjian (dengan mereka)."},{"id":"2","ar":"فَسِيْحُوْا فِى الْاَرْضِ اَرْبَعَةَ اَشْهُرٍ وَّاعْلَمُوْٓا اَنَّكُمْ غَيْرُ مُعْجِزِى اللّٰهِ ۙوَاَنَّ اللّٰهَ مُخْزِى الْكٰفِرِيْنَ ","bc":"fa sīḥụ fil-arḍi arba'ata asy-huriw wa'lamū annakum gairu mu'jizillāhi wa annallāha mukhzil-kāfirīn","at":"Maka berjalanlah kamu (kaum musyrikin) di bumi selama empat bulan dan ketahuilah bahwa kamu tidak dapat melemahkan Allah, dan sesungguhnya Allah menghinakan orang-orang kafir."},{"id":"3","ar":"وَاَذَانٌ مِّنَ اللّٰهِ وَرَسُوْلِهٖٓ اِلَى النَّاسِ يَوْمَ الْحَجِّ الْاَكْبَرِ اَنَّ اللّٰهَ بَرِيْۤءٌ مِّنَ الْمُشْرِكِيْنَ ەۙ وَرَسُوْلُهٗ ۗفَاِنْ تُبْتُمْ فَهُوَ خَيْرٌ لَّكُمْۚ وَاِنْ تَوَلَّيْتُمْ فَاعْلَمُوْٓا اَنَّكُمْ غَيْرُ مُعْجِزِى اللّٰهِ ۗوَبَشِّرِ الَّذِيْنَ كَفَرُوْا بِعَذَابٍ اَلِيْمٍۙ","bc":"wa ażānum minallāhi wa rasụlihī ilan-nāsi yaumal-ḥajjil-akbari annallāha barī`um minal-musyrikīna wa rasụluh, fa in tubtum fa huwa khairul lakum, wa in tawallaitum fa'lamū annakum gairu mu'jizillāh, wa basysyirillażīna kafarụ bi'ażābin alīm","at":"Dan satu maklumat (pemberitahuan) dari Allah dan Rasul-Nya kepada umat manusia pada hari haji akbar, bahwa sesungguhnya Allah dan Rasul-Nya berlepas diri dari orang-orang musyrik. Kemudian jika kamu (kaum musyrikin) bertobat, maka itu lebih baik bagimu; dan jika kamu berpaling, maka ketahuilah bahwa kamu tidak dapat melemahkan Allah. Dan berilah kabar gembira kepada orang-orang kafir (bahwa mereka akan mendapat) azab yang pedih,"},{"id":"4","ar":"اِلَّا الَّذِيْنَ عَاهَدْتُّمْ مِّنَ الْمُشْرِكِيْنَ ثُمَّ لَمْ يَنْقُصُوْكُمْ شَيْـًٔا وَّلَمْ يُظَاهِرُوْا عَلَيْكُمْ اَحَدًا فَاَتِمُّوْٓا اِلَيْهِمْ عَهْدَهُمْ اِلٰى مُدَّتِهِمْۗ اِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ ","bc":"illallażīna 'āhattum minal-musyrikīna ṡumma lam yangquṣụkum syai`aw wa lam yuẓāhirụ 'alaikum aḥadan fa atimmū ilaihim 'ahdahum ilā muddatihim, innallāha yuḥibbul muttaqīn","at":"kecuali orang-orang musyrik yang telah mengadakan perjanjian dengan kamu dan mereka sedikit pun tidak mengurangi (isi perjanjian) dan tidak (pula) mereka membantu seorang pun yang memusuhi kamu, maka terhadap mereka itu penuhilah janjinya sampai batas waktunya. Sungguh, Allah menyukai orang-orang yang bertakwa."},{"id":"5","ar":"فَاِذَا انْسَلَخَ الْاَشْهُرُ الْحُرُمُ فَاقْتُلُوا الْمُشْرِكِيْنَ حَيْثُ وَجَدْتُّمُوْهُمْ وَخُذُوْهُمْ وَاحْصُرُوْهُمْ وَاقْعُدُوْا لَهُمْ كُلَّ مَرْصَدٍۚ فَاِنْ تَابُوْا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَخَلُّوْا سَبِيْلَهُمْۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ","bc":"fa iżansalakhal-asy-hurul-ḥurumu faqtulul-musyrikīna ḥaiṡu wajattumụhum wa khużụhum waḥṣurụhum waq'udụ lahum kulla marṣad, fa in tābụ wa aqāmuṣ-ṣalāta wa ātawuz-zakāta fa khallụ sabīlahum, innallāha gafụrur raḥīm","at":"Apabila telah habis bulan-bulan haram, maka perangilah orang-orang musyrik di mana saja kamu temui, tangkaplah dan kepunglah mereka, dan awasilah di tempat pengintaian. Jika mereka bertobat dan melaksanakan salat serta menunaikan zakat, maka berilah kebebasan kepada mereka. Sungguh, Allah Maha Pengampun, Maha Penyayang."},{"id":"6","ar":"وَاِنْ اَحَدٌ مِّنَ الْمُشْرِكِيْنَ اسْتَجَارَكَ فَاَجِرْهُ حَتّٰى يَسْمَعَ كَلٰمَ اللّٰهِ ثُمَّ اَبْلِغْهُ مَأْمَنَهٗ ۗذٰلِكَ بِاَنَّهُمْ قَوْمٌ لَّا يَعْلَمُوْنَ","bc":"wa in aḥadum minal-musyrikīnastajāraka fa ajir-hu ḥattā yasma'a kalāmallāhi ṡumma ablig-hu ma`manah, żālika bi`annahum qaumul lā ya'lamụn","at":"Dan jika di antara kaum musyrikin ada yang meminta perlindungan kepadamu, maka lindungilah agar dia dapat mendengar firman Allah, kemudian antarkanlah dia ke tempat yang aman baginya. (Demikian) itu karena sesungguhnya mereka kaum yang tidak mengetahui."},{"id":"7","ar":"كَيْفَ يَكُوْنُ لِلْمُشْرِكِيْنَ عَهْدٌ عِنْدَ اللّٰهِ وَعِنْدَ رَسُوْلِهٖٓ اِلَّا الَّذِيْنَ عَاهَدْتُّمْ عِنْدَ الْمَسْجِدِ الْحَرَامِۚ فَمَا اسْتَقَامُوْا لَكُمْ فَاسْتَقِيْمُوْا لَهُمْ ۗاِنَّ اللّٰهَ يُحِبُّ الْمُتَّقِيْنَ ","bc":"kaifa yakụnu lil-musyrikīna 'ahdun 'indallāhi wa 'inda rasụlihī illallażīna 'āhattum 'indal-masjidil-ḥarām, famastaqāmụ lakum fastaqīmụ lahum, innallāha yuḥibbul-muttaqīn","at":"Bagaimana mungkin ada perjanjian (aman) di sisi Allah dan Rasul-Nya dengan orang-orang musyrik, kecuali dengan orang-orang yang kamu telah mengadakan perjanjian (dengan mereka) di dekat Masjidilharam (Hudaibiyah), maka selama mereka berlaku jujur terhadapmu, hendaklah kamu berlaku jujur (pula) terhadap mereka. Sungguh, Allah menyukai orang-orang yang bertakwa."},{"id":"8","ar":"كَيْفَ وَاِنْ يَّظْهَرُوْا عَلَيْكُمْ لَا يَرْقُبُوْا فِيْكُمْ اِلًّا وَّلَا ذِمَّةً ۗيُرْضُوْنَكُمْ بِاَفْوَاهِهِمْ وَتَأْبٰى قُلُوْبُهُمْۚ وَاَكْثَرُهُمْ فٰسِقُوْنَۚ","bc":"kaifa wa iy yaẓ-harụ 'alaikum lā yarqubụ fīkum illaw wa lā żimmah, yurḍụnakum bi`afwāhihim wa ta`bā qulụbuhum, wa akṡaruhum fāsiqụn","at":"Bagaimana mungkin (ada perjanjian demikian), padahal jika mereka memperoleh kemenangan atas kamu, mereka tidak memelihara hubungan kekerabatan denganmu dan tidak (pula mengindahkan) perjanjian. Mereka menyenangkan hatimu dengan mulutnya, sedang hatinya menolak. Kebanyakan mereka adalah orang-orang fasik (tidak menepati janji)."},{"id":"9","ar":"اِشْتَرَوْا بِاٰيٰتِ اللّٰهِ ثَمَنًا قَلِيْلًا فَصَدُّوْا عَنْ سَبِيْلِهٖۗ اِنَّهُمْ سَاۤءَ مَاكَانُوْا يَعْمَلُوْنَ ","bc":"isytarau bi`āyātillāhi ṡamanang qalīlan fa ṣaddụ 'an sabīlih, innahum sā`a mā kānụ ya'malụn","at":"Mereka memperjualbelikan ayat-ayat Allah dengan harga murah, lalu mereka menghalang-halangi (orang) dari jalan Allah. Sungguh, betapa buruknya apa yang mereka kerjakan."},{"id":"10","ar":"لَا يَرْقُبُوْنَ فِيْ مُؤْمِنٍ اِلًّا وَّلَا ذِمَّةً ۗوَاُولٰۤىِٕكَ هُمُ الْمُعْتَدُوْنَ ","bc":"lā yarqubụna fī mu`minin illaw wa lā żimmah, wa ulā`ika humul-mu'tadụn","at":"Mereka tidak memelihara (hubungan) kekerabatan dengan orang mukmin dan tidak (pula mengindahkan) perjanjian. Dan mereka itulah orang-orang yang melampaui batas."},{"id":"11","ar":"فَاِنْ تَابُوْا وَاَقَامُوا الصَّلٰوةَ وَاٰتَوُا الزَّكٰوةَ فَاِخْوَانُكُمْ فِى الدِّيْنِ ۗوَنُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ","bc":"fa in tābụ wa aqāmuṣ-ṣalāta wa ātawuz-zakāta fa ikhwānukum fid-dīn, wa nufaṣṣilul-āyāti liqaumiy ya'lamụn","at":"Dan jika mereka bertobat, melaksanakan salat dan menunaikan zakat, maka (berarti mereka itu) adalah saudara-saudaramu seagama. Kami menjelaskan ayat-ayat itu bagi orang-orang yang mengetahui."},{"id":"12","ar":"وَاِنْ نَّكَثُوْٓا اَيْمَانَهُمْ مِّنْۢ بَعْدِ عَهْدِهِمْ وَطَعَنُوْا فِيْ دِيْنِكُمْ فَقَاتِلُوْٓا اَىِٕمَّةَ الْكُفْرِۙ اِنَّهُمْ لَآ اَيْمَانَ لَهُمْ لَعَلَّهُمْ يَنْتَهُوْنَ ","bc":"wa in nakaṡū aimānahum mim ba'di 'ahdihim wa ṭa'anụ fī dīnikum fa qātilū a`immatal-kufri innahum lā aimāna lahum la'allahum yantahụn","at":"Dan jika mereka melanggar sumpah setelah ada perjanjian, dan mencerca agamamu, maka perangilah pemimpin-pemimpin kafir itu. Sesungguhnya mereka adalah orang-orang yang tidak dapat dipegang janjinya, mudah-mudahan mereka berhenti."},{"id":"13","ar":"اَلَا تُقَاتِلُوْنَ قَوْمًا نَّكَثُوْٓا اَيْمَانَهُمْ وَهَمُّوْا بِاِخْرَاجِ الرَّسُوْلِ وَهُمْ بَدَءُوْكُمْ اَوَّلَ مَرَّةٍۗ اَتَخْشَوْنَهُمْ ۚفَاللّٰهُ اَحَقُّ اَنْ تَخْشَوْهُ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ","bc":"alā tuqātilụna qauman nakaṡū aimānahum wa hammụ bi`ikhrājir-rasụli wa hum bada`ụkum awwala marrah, a takhsyaunahum, fallāhu aḥaqqu an takhsyauhu ing kuntum mu`minīn","at":"Mengapa kamu tidak memerangi orang-orang yang melanggar sumpah (janjinya), dan telah merencanakan untuk mengusir Rasul, dan mereka yang pertama kali memerangi kamu? Apakah kamu takut kepada mereka, padahal Allah-lah yang lebih berhak untuk kamu takuti, jika kamu orang-orang beriman."},{"id":"14","ar":"قَاتِلُوْهُمْ يُعَذِّبْهُمُ اللّٰهُ بِاَيْدِيْكُمْ وَيُخْزِهِمْ وَيَنْصُرْكُمْ عَلَيْهِمْ وَيَشْفِ صُدُوْرَ قَوْمٍ مُّؤْمِنِيْنَۙ","bc":"qātilụhum yu'ażżib-humullāhu bi`aidīkum wa yukhzihim wa yanṣurkum 'alaihim wa yasyfi ṣudụra qaumim mu`minīn","at":"Perangilah mereka, niscaya Allah akan menyiksa mereka dengan (perantaraan) tanganmu dan Dia akan menghina mereka dan menolongmu (dengan kemenangan) atas mereka, serta melegakan hati orang-orang yang beriman,"},{"id":"15","ar":"وَيُذْهِبْ غَيْظَ قُلُوْبِهِمْۗ وَيَتُوْبُ اللّٰهُ عَلٰى مَنْ يَّشَاۤءُۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ","bc":"wa yuż-hib gaiẓa qulụbihim, wa yatụbullāhu 'alā may yasyā`, wallāhu 'alīmun ḥakīm","at":"dan Dia menghilangkan kemarahan hati mereka (orang mukmin). Dan Allah menerima tobat orang yang Dia kehendaki. Allah Maha Mengetahui, Mahabijaksana."},{"id":"16","ar":"اَمْ حَسِبْتُمْ اَنْ تُتْرَكُوْا وَلَمَّا يَعْلَمِ اللّٰهُ الَّذِيْنَ جَاهَدُوْا مِنْكُمْ وَلَمْ يَتَّخِذُوْا مِنْ دُوْنِ اللّٰهِ وَلَا رَسُوْلِهٖ وَلَا الْمُؤْمِنِيْنَ وَلِيْجَةً ۗوَاللّٰهُ خَبِيْرٌۢ بِمَا تَعْمَلُوْنَ","bc":"am ḥasibtum an tutrakụ wa lammā ya'lamillāhullażīna jāhadụ mingkum wa lam yattakhiżụ min dụnillāhi wa lā rasụlihī wa lal-mu`minīna walījah, wallāhu khabīrum bimā ta'malụn","at":"Apakah kamu mengira bahwa kamu akan dibiarkan (begitu saja), padahal Allah belum mengetahui orang-orang yang berjihad di antara kamu dan tidak mengambil teman yang setia selain Allah, Rasul-Nya dan orang-orang yang beriman. Allah Mahateliti terhadap apa yang kamu kerjakan."},{"id":"17","ar":"مَا كَانَ لِلْمُشْرِكِيْنَ اَنْ يَّعْمُرُوْا مَسٰجِدَ اللّٰهِ شٰهِدِيْنَ عَلٰٓى اَنْفُسِهِمْ بِالْكُفْرِۗ اُولٰۤىِٕكَ حَبِطَتْ اَعْمَالُهُمْۚ وَ فِى النَّارِ هُمْ خٰلِدُوْنَ ","bc":"mā kāna lil-musyrikīna ay ya'murụ masājidallāhi syāhidīna 'alā anfusihim bil-kufr, ulā`ika ḥabiṭat a'māluhum, wa fin-nāri hum khālidụn","at":"Tidaklah pantas orang-orang musyrik memakmurkan masjid Allah, padahal mereka mengakui bahwa mereka sendiri kafir. Mereka itu sia-sia amalnya, dan mereka kekal di dalam neraka."},{"id":"18","ar":"اِنَّمَا يَعْمُرُ مَسٰجِدَ اللّٰهِ مَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَاَقَامَ الصَّلٰوةَ وَاٰتَى الزَّكٰوةَ وَلَمْ يَخْشَ اِلَّا اللّٰهَ ۗفَعَسٰٓى اُولٰۤىِٕكَ اَنْ يَّكُوْنُوْا مِنَ الْمُهْتَدِيْنَ ","bc":"innamā ya'muru masājidallāhi man āmana billāhi wal-yaumil-ākhiri wa aqāmaṣ-ṣalāta wa ātaz-zakāta wa lam yakhsya illallāh, fa 'asā ulā`ika ay yakụnụ minal-muhtadīn","at":"Sesungguhnya yang memakmurkan masjid Allah hanyalah orang-orang yang beriman kepada Allah dan hari kemudian, serta (tetap) melaksanakan salat, menunaikan zakat dan tidak takut (kepada apa pun) kecuali kepada Allah. Maka mudah-mudahan mereka termasuk orang-orang yang mendapat petunjuk."},{"id":"19","ar":"۞ اَجَعَلْتُمْ سِقَايَةَ الْحَاۤجِّ وَعِمَارَةَ الْمَسْجِدِ الْحَرَامِ كَمَنْ اٰمَنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَجَاهَدَ فِيْ سَبِيْلِ اللّٰهِ ۗ لَا يَسْتَوٗنَ عِنْدَ اللّٰهِ ۗوَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَۘ","bc":"a ja'altum siqāyatal-ḥājji wa 'imāratal-masjidil-ḥarāmi kaman āmana billāhi wal-yaumil-ākhiri wa jāhada fī sabīlillāh, lā yastawụna 'indallāh, wallāhu lā yahdil-qaumaẓ-ẓālimīn","at":"Apakah (orang-orang) yang memberi minuman kepada orang-orang yang mengerjakan haji dan mengurus Masjidilharam, kamu samakan dengan orang yang beriman kepada Allah dan hari kemudian serta berjihad di jalan Allah? Mereka tidak sama di sisi Allah. Allah tidak memberikan petunjuk kepada orang-orang zalim."},{"id":"20","ar":"اَلَّذِيْنَ اٰمَنُوْا وَهَاجَرُوْا وَجَاهَدُوْا فِيْ سَبِيْلِ اللّٰهِ بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۙ اَعْظَمُ دَرَجَةً عِنْدَ اللّٰهِ ۗوَاُولٰۤىِٕكَ هُمُ الْفَاۤىِٕزُوْنَ","bc":"allażīna āmanụ wa hājarụ wa jāhadụ fī sabīlillāhi bi`amwālihim wa anfusihim a'ẓamu darajatan 'indallāh, wa ulā`ika humul-fā`izụn","at":"Orang-orang yang beriman dan berhijrah serta berjihad di jalan Allah, dengan harta dan jiwa mereka, adalah lebih tinggi derajatnya di sisi Allah. Mereka itulah orang-orang yang memperoleh kemenangan."},{"id":"21","ar":"يُبَشِّرُهُمْ رَبُّهُمْ بِرَحْمَةٍ مِّنْهُ وَرِضْوَانٍ وَّجَنّٰتٍ لَّهُمْ فِيْهَا نَعِيْمٌ مُّقِيْمٌۙ","bc":"yubasysyiruhum rabbuhum biraḥmatim min-hu wa riḍwāniw wa jannātil lahum fīhā na'īmum muqīm","at":"Tuhan menggembirakan mereka dengan memberikan rahmat, keridaan dan surga, mereka memperoleh kesenangan yang kekal di dalamnya,"},{"id":"22","ar":"خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗاِنَّ اللّٰهَ عِنْدَهٗٓ اَجْرٌ عَظِيْمٌ ","bc":"khālidīna fīhā abadā, innallāha 'indahū ajrun 'aẓīm","at":"mereka kekal di dalamnya selama-lamanya. Sungguh, di sisi Allah terdapat pahala yang besar."},{"id":"23","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا لَا تَتَّخِذُوْٓا اٰبَاۤءَكُمْ وَاِخْوَانَكُمْ اَوْلِيَاۤءَ اِنِ اسْتَحَبُّوا الْكُفْرَ عَلَى الْاِيْمَانِۗ وَمَنْ يَّتَوَلَّهُمْ مِّنْكُمْ فَاُولٰۤىِٕكَ هُمُ الظّٰلِمُوْنَ ","bc":"yā ayyuhallażīna āmanụ lā tattakhiżū ābā`akum wa ikhwānakum auliyā`a inistaḥabbul-kufra 'alal-īmān, wa may yatawallahum mingkum fa ulā`ika humuẓ-ẓālimụn","at":"Wahai orang-orang yang beriman! Janganlah kamu jadikan bapak-bapakmu dan saudara-saudaramu sebagai pelindung, jika mereka lebih menyukai kekafiran daripada keimanan. Barangsiapa di antara kamu yang menjadikan mereka pelindung, maka mereka itulah orang-orang yang zalim."},{"id":"24","ar":"قُلْ اِنْ كَانَ اٰبَاۤؤُكُمْ وَاَبْنَاۤؤُكُمْ وَاِخْوَانُكُمْ وَاَزْوَاجُكُمْ وَعَشِيْرَتُكُمْ وَاَمْوَالُ ِۨاقْتَرَفْتُمُوْهَا وَتِجَارَةٌ تَخْشَوْنَ كَسَادَهَا وَمَسٰكِنُ تَرْضَوْنَهَآ اَحَبَّ اِلَيْكُمْ مِّنَ اللّٰهِ وَرَسُوْلِهٖ وَجِهَادٍ فِيْ سَبِيْلِهٖ فَتَرَبَّصُوْا حَتّٰى يَأْتِيَ اللّٰهُ بِاَمْرِهٖۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ","bc":"qul ing kāna ābā`ukum wa abnā`ukum wa ikhwānukum wa azwājukum wa 'asyīratukum wa amwāluniqtaraftumụhā wa tijāratun takhsyauna kasādahā wa masākinu tarḍaunahā aḥabba ilaikum minallāhi wa rasụlihī wa jihādin fī sabīlihī fa tarabbaṣụ ḥattā ya`tiyallāhu bi`amrih, wallāhu lā yahdil-qaumal-fāsiqīn","at":"Katakanlah, “Jika bapak-bapakmu, anak-anakmu, saudara-saudaramu, istri-istrimu, keluargamu, harta kekayaan yang kamu usahakan, perdagangan yang kamu khawatirkan kerugiannya, dan rumah-rumah tempat tinggal yang kamu sukai, lebih kamu cintai dari pada Allah dan Rasul-Nya serta berjihad di jalan-Nya, maka tunggulah sampai Allah memberikan keputusan-Nya.” Dan Allah tidak memberi petunjuk kepada orang-orang fasik."},{"id":"25","ar":"لَقَدْ نَصَرَكُمُ اللّٰهُ فِيْ مَوَاطِنَ كَثِيْرَةٍۙ وَّيَوْمَ حُنَيْنٍۙ اِذْ اَعْجَبَتْكُمْ كَثْرَتُكُمْ فَلَمْ تُغْنِ عَنْكُمْ شَيْـًٔا وَّضَاقَتْ عَلَيْكُمُ الْاَرْضُ بِمَا رَحُبَتْ ثُمَّ وَلَّيْتُمْ مُّدْبِرِيْنَۚ","bc":"laqad naṣarakumullāhu fī mawāṭina kaṡīratiw wa yauma ḥunainin iż a'jabatkum kaṡratukum fa lam tugni 'angkum syai`aw wa ḍāqat 'alaikumul-arḍu bimā raḥubat ṡumma wallaitum mudbirīn","at":"Sungguh, Allah telah menolong kamu (mukminin) di banyak medan perang, dan (ingatlah) Perang Hunain, ketika jumlahmu yang besar itu membanggakan kamu, tetapi (jumlah yang banyak itu) sama sekali tidak berguna bagimu, dan bumi yang luas itu terasa sempit bagimu, kemudian kamu berbalik ke belakang dan lari tunggang-langgang."},{"id":"26","ar":"ثُمَّ اَنْزَلَ اللّٰهُ سَكِيْنَتَهٗ عَلٰى رَسُوْلِهٖ وَعَلَى الْمُؤْمِنِيْنَ وَاَنْزَلَ جُنُوْدًا لَّمْ تَرَوْهَا وَعَذَّبَ الَّذِيْنَ كَفَرُوْاۗ وَذٰلِكَ جَزَاۤءُ الْكٰفِرِيْنَ ","bc":"ṡumma anzalallāhu sakīnatahụ 'alā rasụlihī wa 'alal-mu`minīna wa anzala junụdal lam tarauhā wa 'ażżaballażīna kafarụ, wa żālika jazā`ul-kāfirīn","at":"Kemudian Allah menurunkan ketenangan kepada Rasul-Nya dan kepada orang-orang yang beriman, dan Dia menurunkan bala tentara (para malaikat) yang tidak terlihat olehmu, dan Dia menimpakan azab kepada orang-orang kafir. Itulah balasan bagi orang-orang kafir."},{"id":"27","ar":"ثُمَّ يَتُوْبُ اللّٰهُ مِنْۢ بَعْدِ ذٰلِكَ عَلٰى مَنْ يَّشَاۤءُۗ وَاللّٰهُ غَفُوْرٌ رَّحِيْمٌ ","bc":"ṡumma yatụbullāhu mim ba'di żālika 'alā may yasyā`, wallāhu gafụrur raḥīm","at":"Setelah itu Allah menerima tobat orang yang Dia kehendaki. Allah Maha Pengampun, Maha Penyayang."},{"id":"28","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنَّمَا الْمُشْرِكُوْنَ نَجَسٌ فَلَا يَقْرَبُوا الْمَسْجِدَ الْحَرَامَ بَعْدَ عَامِهِمْ هٰذَا ۚوَاِنْ خِفْتُمْ عَيْلَةً فَسَوْفَ يُغْنِيْكُمُ اللّٰهُ مِنْ فَضْلِهٖٓ اِنْ شَاۤءَۗ اِنَّ اللّٰهَ عَلِيْمٌ حَكِيْمٌ ","bc":"yā ayyuhallażīna āmanū innamal-musyrikụna najasun fa lā yaqrabul-masjidal-ḥarāma ba'da 'āmihim hāżā, wa in khiftum 'ailatan fa saufa yugnīkumullāhu min faḍlihī in syā`, innallāha 'alīmun ḥakīm","at":"Wahai orang-orang yang beriman! Sesungguhnya orang-orang musyrik itu najis (kotor jiwa), karena itu janganlah mereka mendekati Masjidilharam setelah tahun ini. Dan jika kamu khawatir menjadi miskin (karena orang kafir tidak datang), maka Allah nanti akan memberikan kekayaan kepadamu dari karunia-Nya, jika Dia menghendaki. Sesungguhnya Allah Maha Mengetahui, Mahabijaksana."},{"id":"29","ar":"قَاتِلُوا الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاللّٰهِ وَلَا بِالْيَوْمِ الْاٰخِرِ وَلَا يُحَرِّمُوْنَ مَا حَرَّمَ اللّٰهُ وَرَسُوْلُهٗ وَلَا يَدِيْنُوْنَ دِيْنَ الْحَقِّ مِنَ الَّذِيْنَ اُوْتُوا الْكِتٰبَ حَتّٰى يُعْطُوا الْجِزْيَةَ عَنْ يَّدٍ وَّهُمْ صَاغِرُوْنَ","bc":"qātilullażīna lā yu`minụna billāhi wa lā bil-yaumil-ākhiri wa lā yuḥarrimụna mā ḥarramallāhu wa rasụluhụ wa lā yadīnụna dīnal-ḥaqqi minallażīna ụtul-kitāba ḥattā yu'ṭul-jizyata 'ay yadiw wa hum ṣāgirụn","at":"Perangilah orang-orang yang tidak beriman kepada Allah dan hari kemudian, mereka yang tidak mengharamkan apa yang telah diharamkan Allah dan Rasul-Nya dan mereka yang tidak beragama dengan agama yang benar (agama Allah), (yaitu orang-orang) yang telah diberikan Kitab, hingga mereka membayar jizyah (pajak) dengan patuh sedang mereka dalam keadaan tunduk."},{"id":"30","ar":"وَقَالَتِ الْيَهُوْدُ عُزَيْرُ ِۨابْنُ اللّٰهِ وَقَالَتِ النَّصٰرَى الْمَسِيْحُ ابْنُ اللّٰهِ ۗذٰلِكَ قَوْلُهُمْ بِاَفْوَاهِهِمْۚ يُضَاهِـُٔوْنَ قَوْلَ الَّذِيْنَ كَفَرُوْا مِنْ قَبْلُ ۗقَاتَلَهُمُ اللّٰهُ ۚ اَنّٰى يُؤْفَكُوْنَ ","bc":"wa qālatil-yahụdu 'uzairunibnullāhi wa qālatin-naṣāral-masīḥubnullāh, żālika qauluhum bi`afwāhihim, yuḍāhi`ụna qaulallażīna kafarụ ming qabl, qātalahumullāh, annā yu`fakụn","at":"Dan orang-orang Yahudi berkata, “Uzair putra Allah,” dan orang-orang Nasrani berkata, “Al-Masih putra Allah.” Itulah ucapan yang keluar dari mulut mereka. Mereka meniru ucapan orang-orang kafir yang terdahulu. Allah melaknat mereka; bagaimana mereka sampai berpaling?"},{"id":"31","ar":"اِتَّخَذُوْٓا اَحْبَارَهُمْ وَرُهْبَانَهُمْ اَرْبَابًا مِّنْ دُوْنِ اللّٰهِ وَالْمَسِيْحَ ابْنَ مَرْيَمَۚ وَمَآ اُمِرُوْٓا اِلَّا لِيَعْبُدُوْٓا اِلٰهًا وَّاحِدًاۚ لَآ اِلٰهَ اِلَّا هُوَۗ سُبْحٰنَهٗ عَمَّا يُشْرِكُوْنَ ","bc":"ittakhażū aḥbārahum wa ruhbānahum arbābam min dụnillāhi wal-masīḥabna maryam, wa mā umirū illā liya'budū ilāhaw wāḥidā, lā ilāha illā huw, sub-ḥānahụ 'ammā yusyrikụn","at":"Mereka menjadikan orang-orang alim (Yahudi), dan rahib-rahibnya (Nasrani) sebagai tuhan selain Allah, dan (juga) Al-Masih putra Maryam; padahal mereka hanya disuruh menyembah Tuhan Yang Maha Esa; tidak ada tuhan selain Dia. Mahasuci Dia dari apa yang mereka persekutukan."},{"id":"32","ar":"يُرِيْدُوْنَ اَنْ يُّطْفِـُٔوْا نُوْرَ اللّٰهِ بِاَفْوَاهِهِمْ وَيَأْبَى اللّٰهُ اِلَّآ اَنْ يُّتِمَّ نُوْرَهٗ وَلَوْ كَرِهَ الْكٰفِرُوْنَ ","bc":"yurīdụna ay yuṭfi`ụ nụrallāhi bi`afwāhihim wa ya`ballāhu illā ay yutimma nụrahụ walau karihal-kāfirụn","at":"Mereka hendak memadamkan cahaya (agama) Allah dengan mulut (ucapan-ucapan) mereka, tetapi Allah menolaknya, malah berkehendak menyempurnakan cahaya-Nya, walaupun orang-orang kafir itu tidak menyukai."},{"id":"33","ar":"هُوَ الَّذِيْٓ اَرْسَلَ رَسُوْلَهٗ بِالْهُدٰى وَدِيْنِ الْحَقِّ لِيُظْهِرَهٗ عَلَى الدِّيْنِ كُلِّهٖۙ وَلَوْ كَرِهَ الْمُشْرِكُوْنَ ","bc":"huwallażī arsala rasụlahụ bil-hudā wa dīnil-ḥaqqi liyuẓ-hirahụ 'alad-dīni kullihī walau karihal-musyrikụn","at":"Dialah yang telah mengutus Rasul-Nya dengan petunjuk (Al-Qur'an) dan agama yang benar untuk diunggulkan atas segala agama, walaupun orang-orang musyrik tidak menyukai."},{"id":"34","ar":"۞ يٰٓاَيُّهَا الَّذِينَ اٰمَنُوْٓا اِنَّ كَثِيْرًا مِّنَ الْاَحْبَارِ وَالرُّهْبَانِ لَيَأْكُلُوْنَ اَمْوَالَ النَّاسِ بِالْبَاطِلِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ ۗوَالَّذِيْنَ يَكْنِزُوْنَ الذَّهَبَ وَالْفِضَّةَ وَلَا يُنْفِقُوْنَهَا فِيْ سَبِيْلِ اللّٰهِ ۙفَبَشِّرْهُمْ بِعَذَابٍ اَلِيْمٍۙ","bc":"yā ayyuhallażīna āmanū inna kaṡīram minal-aḥbāri war-ruhbāni laya`kulụna amwālan-nāsi bil-bāṭili wa yaṣuddụna 'an sabīlillāh, wallażīna yaknizụnaż-żahaba wal-fiḍḍata wa lā yunfiqụnahā fī sabīlillāhi fa basysyir-hum bi'ażābin alīm","at":"Wahai orang-orang yang beriman! Sesungguhnya banyak dari orang-orang alim dan rahib-rahib mereka benar-benar memakan harta orang dengan jalan yang batil, dan (mereka) menghalang-halangi (manusia) dari jalan Allah. Dan orang-orang yang menyimpan emas dan perak dan tidak menginfakkannya di jalan Allah, maka berikanlah kabar gembira kepada mereka, (bahwa mereka akan mendapat) azab yang pedih."},{"id":"35","ar":"يَّوْمَ يُحْمٰى عَلَيْهَا فِيْ نَارِ جَهَنَّمَ فَتُكْوٰى بِهَا جِبَاهُهُمْ وَجُنُوْبُهُمْ وَظُهُوْرُهُمْۗ هٰذَا مَا كَنَزْتُمْ لِاَنْفُسِكُمْ فَذُوْقُوْا مَا كُنْتُمْ تَكْنِزُوْنَ ","bc":"yauma yuḥmā 'alaihā fī nāri jahannama fa tukwā bihā jibāhuhum wa junụbuhum wa ẓuhụruhum, hāżā mā kanaztum li`anfusikum fa żụqụ mā kuntum taknizụn","at":"(Ingatlah) pada hari ketika emas dan perak dipanaskan dalam neraka Jahanam, lalu dengan itu disetrika dahi, lambung dan punggung mereka (seraya dikatakan) kepada mereka, “Inilah harta bendamu yang kamu simpan untuk dirimu sendiri, maka rasakanlah (akibat dari) apa yang kamu simpan itu.”"},{"id":"36","ar":"اِنَّ عِدَّةَ الشُّهُوْرِ عِنْدَ اللّٰهِ اثْنَا عَشَرَ شَهْرًا فِيْ كِتٰبِ اللّٰهِ يَوْمَ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ مِنْهَآ اَرْبَعَةٌ حُرُمٌ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُ ەۙ فَلَا تَظْلِمُوْا فِيْهِنَّ اَنْفُسَكُمْ وَقَاتِلُوا الْمُشْرِكِيْنَ كَاۤفَّةً كَمَا يُقَاتِلُوْنَكُمْ كَاۤفَّةً ۗوَاعْلَمُوْٓا اَنَّ اللّٰهَ مَعَ الْمُتَّقِيْنَ","bc":"inna 'iddatasy-syuhụri 'indallāhiṡnā 'asyara syahran fī kitābillāhi yauma khalaqas-samāwāti wal-arḍa min-hā arba'atun ḥurum, żālikad-dīnul-qayyimu fa lā taẓlimụ fīhinna anfusakum wa qātilul-musyrikīna kāffatang kamā yuqātilụnakum kāffah, wa'lamū annallāha ma'al-muttaqīn","at":"Sesungguhnya jumlah bulan menurut Allah ialah dua belas bulan, (sebagaimana) dalam ketetapan Allah pada waktu Dia menciptakan langit dan bumi, di antaranya ada empat bulan haram. Itulah (ketetapan) agama yang lurus, maka janganlah kamu menzalimi dirimu dalam (bulan yang empat) itu, dan perangilah kaum musyrikin semuanya sebagaimana mereka pun memerangi kamu semuanya. Dan ketahuilah bahwa Allah beserta orang-orang yang takwa."},{"id":"37","ar":"اِنَّمَا النَّسِيْۤءُ زِيَادَةٌ فِى الْكُفْرِ يُضَلُّ بِهِ الَّذِيْنَ كَفَرُوْا يُحِلُّوْنَهٗ عَامًا وَّيُحَرِّمُوْنَهٗ عَامًا لِّيُوَاطِـُٔوْا عِدَّةَ مَا حَرَّمَ اللّٰهُ فَيُحِلُّوْا مَا حَرَّمَ اللّٰهُ ۗزُيِّنَ لَهُمْ سُوْۤءُ اَعْمَالِهِمْۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْكٰفِرِيْنَ","bc":"innaman-nasī`u ziyādatun fil-kufri yuḍallu bihillażīna kafarụ yuḥillụnahụ 'āmaw wa yuḥarrimụnahụ 'āmal liyuwāṭi`ụ 'iddata mā ḥarramallāhu fa yuḥillụ mā ḥarramallāh, zuyyina lahum sū`u a'mālihim, wallāhu lā yahdil-qaumal-kāfirīn","at":"Sesungguhnya pengunduran (bulan haram) itu hanya menambah kekafiran. Orang-orang kafir disesatkan dengan (pengunduran) itu, mereka menghalalkannya suatu tahun dan mengharamkannya pada suatu tahun yang lain, agar mereka dapat menyesuaikan dengan bilangan yang diharamkan Allah, sekaligus mereka menghalalkan apa yang diharamkan Allah. (Setan) dijadikan terasa indah bagi mereka perbuatan-perbuatan buruk mereka. Dan Allah tidak memberi petunjuk kepada orang-orang yang kafir."},{"id":"38","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا مَا لَكُمْ اِذَا قِيْلَ لَكُمُ انْفِرُوْا فِيْ سَبِيْلِ اللّٰهِ اثَّاقَلْتُمْ اِلَى الْاَرْضِۗ اَرَضِيْتُمْ بِالْحَيٰوةِ الدُّنْيَا مِنَ الْاٰخِرَةِۚ فَمَا مَتَاعُ الْحَيٰوةِ الدُّنْيَا فِى الْاٰخِرَةِ اِلَّا قَلِيْلٌ ","bc":"yā ayyuhallażīna āmanụ mā lakum iżā qīla lakumunfirụ fī sabīlillāhiṡ ṡāqaltum ilal-arḍ, a raḍītum bil-ḥayātid-dun-yā minal-ākhirah, fa mā matā'ul-ḥayātid-dun-yā fil-ākhirati illā qalīl","at":"Wahai orang-orang yang beriman! Mengapa apabila dikatakan kepada kamu, “Berangkatlah (untuk berperang) di jalan Allah,” kamu merasa berat dan ingin tinggal di tempatmu? Apakah kamu lebih menyenangi kehidupan di dunia daripada kehidupan di akhirat? Padahal kenikmatan hidup di dunia ini (dibandingkan dengan kehidupan) di akhirat hanyalah sedikit."},{"id":"39","ar":"اِلَّا تَنْفِرُوْا يُعَذِّبْكُمْ عَذَابًا اَلِيمًاۙ وَّيَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ وَلَا تَضُرُّوْهُ شَيْـًٔاۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"illā tanfirụ yu'ażżibkum 'ażāban alīmaw wa yastabdil qauman gairakum wa lā taḍurrụhu syai`ā, wallāhu 'alā kulli syai`ing qadīr","at":"Jika kamu tidak berangkat (untuk berperang), niscaya Allah akan menghukum kamu dengan azab yang pedih dan menggantikan kamu dengan kaum yang lain, dan kamu tidak akan merugikan-Nya sedikit pun. Dan Allah Mahakuasa atas segala sesuatu."},{"id":"40","ar":"اِلَّا تَنْصُرُوْهُ فَقَدْ نَصَرَهُ اللّٰهُ اِذْ اَخْرَجَهُ الَّذِيْنَ كَفَرُوْا ثَانِيَ اثْنَيْنِ اِذْ هُمَا فِى الْغَارِ اِذْ يَقُوْلُ لِصَاحِبِهٖ لَا تَحْزَنْ اِنَّ اللّٰهَ مَعَنَاۚ فَاَنْزَلَ اللّٰهُ سَكِيْنَتَهٗ عَلَيْهِ وَاَيَّدَهٗ بِجُنُوْدٍ لَّمْ تَرَوْهَا وَجَعَلَ كَلِمَةَ الَّذِيْنَ كَفَرُوا السُّفْلٰىۗ وَكَلِمَةُ اللّٰهِ هِيَ الْعُلْيَاۗ وَاللّٰهُ عَزِيْزٌ حَكِيْمٌ ","bc":"illā tanṣurụhu fa qad naṣarahullāhu iż akhrajahullażīna kafarụ ṡāniyaṡnaini iż humā fil-gāri iż yaqụlu liṣāḥibihī lā taḥzan innallāha ma'anā, fa anzalallāhu sakīnatahụ 'alaihi wa ayyadahụ bijunụdil lam tarauhā wa ja'ala kalimatallażīna kafarus-suflā, wa kalimatullāhi hiyal-'ulyā, wallāhu 'azīzun ḥakīm","at":"Jika kamu tidak menolongnya (Muhammad), sesungguhnya Allah telah menolongnya (yaitu) ketika orang-orang kafir mengusirnya (dari Mekah); sedang dia salah seorang dari dua orang ketika keduanya berada dalam gua, ketika itu dia berkata kepada sahabatnya, “Jangan engkau bersedih, sesungguhnya Allah bersama kita.” Maka Allah menurunkan ketenangan kepadanya (Muhammad) dan membantu dengan bala tentara (malaikat-malaikat) yang tidak terlihat olehmu, dan Dia menjadikan seruan orang-orang kafir itu rendah. Dan firman Allah itulah yang tinggi. Allah Mahaperkasa, Mahabijaksana."},{"id":"41","ar":"اِنْفِرُوْا خِفَافًا وَّثِقَالًا وَّجَاهِدُوْا بِاَمْوَالِكُمْ وَاَنْفُسِكُمْ فِيْ سَبِيْلِ اللّٰهِ ۗذٰلِكُمْ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ تَعْلَمُوْنَ ","bc":"infirụ khifāfaw wa ṡiqālaw wa jāhidụ bi`amwālikum wa anfusikum fī sabīlillāh, żālikum khairul lakum ing kuntum ta'lamụn","at":"Berangkatlah kamu baik dengan rasa ringan maupun dengan rasa berat, dan berjihadlah dengan harta dan jiwamu di jalan Allah. Yang demikian itu adalah lebih baik bagimu jika kamu mengetahui."},{"id":"42","ar":"لَوْ كَانَ عَرَضًا قَرِيْبًا وَّسَفَرًا قَاصِدًا لَّاتَّبَعُوْكَ وَلٰكِنْۢ بَعُدَتْ عَلَيْهِمُ الشُّقَّةُۗ وَسَيَحْلِفُوْنَ بِاللّٰهِ لَوِ اسْتَطَعْنَا لَخَرَجْنَا مَعَكُمْۚ يُهْلِكُوْنَ اَنْفُسَهُمْۚ وَاللّٰهُ يَعْلَمُ اِنَّهُمْ لَكٰذِبُوْنَ ","bc":"lau kāna 'araḍang qarībaw wa safarang qāṣidal lattaba'ụka wa lākim ba'udat 'alaihimusy-syuqqah, wa sayaḥlifụna billāhi lawistaṭa'nā lakharajnā ma'akum, yuhlikụna anfusahum, wallāhu ya'lamu innahum lakāżibụn","at":"Sekiranya (yang kamu serukan kepada mereka) ada keuntungan yang mudah diperoleh dan perjalanan yang tidak seberapa jauh, niscaya mereka mengikutimu, tetapi tempat yang dituju itu terasa sangat jauh bagi mereka. Mereka akan bersumpah dengan (nama) Allah, “Jikalau kami sanggup niscaya kami berangkat bersamamu.” Mereka membinasakan diri sendiri dan Allah mengetahui bahwa mereka benar-benar orang-orang yang berdusta."},{"id":"43","ar":"عَفَا اللّٰهُ عَنْكَۚ لِمَ اَذِنْتَ لَهُمْ حَتّٰى يَتَبَيَّنَ لَكَ الَّذِيْنَ صَدَقُوْا وَتَعْلَمَ الْكٰذِبِيْنَ ","bc":"'afallāhu 'angk, lima ażinta lahum ḥattā yatabayyana lakallażīna ṣadaqụ wa ta'lamal-kāżibīn","at":"Allah memaafkanmu (Muhammad). Mengapa engkau memberi izin kepada mereka (untuk tidak pergi berperang), sebelum jelas bagimu orang-orang yang benar-benar (berhalangan) dan sebelum engkau mengetahui orang-orang yang berdusta?"},{"id":"44","ar":"لَا يَسْتَأْذِنُكَ الَّذِيْنَ يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ اَنْ يُّجَاهِدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ وَاللّٰهُ عَلِيْمٌۢ بِالْمُتَّقِيْنَ ","bc":"lā yasta`żinukallażīna yu`minụna billāhi wal-yaumil-ākhiri ay yujāhidụ bi`amwālihim wa anfusihim, wallāhu 'alīmum bil-muttaqīn","at":"Orang-orang yang beriman kepada Allah dan hari kemudian, tidak akan meminta izin (tidak ikut) kepadamu untuk berjihad dengan harta dan jiwa mereka. Allah mengetahui orang-orang yang bertakwa."},{"id":"45","ar":"اِنَّمَا يَسْتَأْذِنُكَ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَارْتَابَتْ قُلُوْبُهُمْ فَهُمْ فِيْ رَيْبِهِمْ يَتَرَدَّدُوْنَ ","bc":"innamā yasta`żinukallażīna lā yu`minụna billāhi wal-yaumil-ākhiri wartābat qulụbuhum fa hum fī raibihim yataraddadụn","at":"Sesungguhnya yang akan meminta izin kepadamu (Muhammad), hanyalah orang-orang yang tidak beriman kepada Allah dan hari kemudian, dan hati mereka ragu, karena itu mereka selalu bimbang dalam keraguan."},{"id":"46","ar":"۞ وَلَوْ اَرَادُوا الْخُرُوْجَ لَاَعَدُّوْا لَهٗ عُدَّةً وَّلٰكِنْ كَرِهَ اللّٰهُ انْۢبِعَاثَهُمْ فَثَبَّطَهُمْ وَقِيْلَ اقْعُدُوْا مَعَ الْقٰعِدِيْنَ ","bc":"walau arādul-khurụja la`a'addụ lahụ 'uddataw wa lāking karihallāhumbi'āṡahum fa ṡabbaṭahum wa qīlaq'udụ ma'al-qā'idīn","at":"Dan jika mereka mau berangkat, niscaya mereka menyiapkan persiapan untuk keberangkatan itu, tetapi Allah tidak menyukai keberangkatan mereka, maka Dia melemahkan keinginan mereka, dan dikatakan (kepada mereka), “Tinggallah kamu bersama orang-orang yang tinggal itu.”"},{"id":"47","ar":"لَوْ خَرَجُوْا فِيْكُمْ مَّا زَادُوْكُمْ اِلَّا خَبَالًا وَّلَاَوْضَعُوْا خِلٰلَكُمْ يَبْغُوْنَكُمُ الْفِتْنَةَۚ وَفِيْكُمْ سَمّٰعُوْنَ لَهُمْۗ وَاللّٰهُ عَلِيْمٌۢ بِالظّٰلِمِيْنَ ","bc":"lau kharajụ fīkum mā zādụkum illā khabālaw wa la`auḍa'ụ khilālakum yabgụnakumul-fitnah, wa fīkum sammā'ụna lahum, wallāhu 'alīmum biẓ-ẓālimīn","at":"Jika (mereka berangkat bersamamu), niscaya mereka tidak akan menambah (kekuatan)mu, malah hanya akan membuat kekacauan, dan mereka tentu bergegas maju ke depan di celah-celah barisanmu untuk mengadakan kekacauan (di barisanmu); sedang di antara kamu ada orang-orang yang sangat suka mendengarkan (perkataan) mereka. Allah mengetahui orang-orang yang zalim."},{"id":"48","ar":"لَقَدِ ابْتَغَوُا الْفِتْنَةَ مِنْ قَبْلُ وَقَلَّبُوْا لَكَ الْاُمُوْرَ حَتّٰى جَاۤءَ الْحَقُّ وَظَهَرَ اَمْرُ اللّٰهِ وَهُمْ كٰرِهُوْنَ ","bc":"laqadibtagawul-fitnata ming qablu wa qallabụ lakal-umụra ḥattā jā`al-ḥaqqu wa ẓahara amrullāhi wa hum kārihụn","at":"Sungguh, sebelum itu mereka memang sudah berusaha membuat kekacauan dan mengatur berbagai macam tipu daya bagimu (memutarbalikkan persoalan), hingga datanglah kebenaran (pertolongan Allah), dan menanglah urusan (agama) Allah, padahal mereka tidak menyukainya."},{"id":"49","ar":"وَمِنْهُمْ مَّنْ يَّقُوْلُ ائْذَنْ لِّيْ وَلَا تَفْتِنِّيْۗ اَلَا فِى الْفِتْنَةِ سَقَطُوْاۗ وَاِنَّ جَهَنَّمَ لَمُحِيْطَةٌ ۢ بِالْكٰفِرِيْنَ ","bc":"wa min-hum may yaqụlu`żal lī wa lā taftinnī, alā fil-fitnati saqaṭụ, wa inna jahannama lamuḥīṭatum bil-kāfirīn","at":"Dan di antara mereka ada orang yang berkata, “Berilah aku izin (tidak pergi berperang) dan janganlah engkau (Muhammad) menjadikan aku terjerumus ke dalam fitnah.” Ketahuilah, bahwa mereka telah terjerumus ke dalam fitnah. Dan sungguh, Jahanam meliputi orang-orang yang kafir."},{"id":"50","ar":"اِنْ تُصِبْكَ حَسَنَةٌ تَسُؤْهُمْۚ وَاِنْ تُصِبْكَ مُصِيْبَةٌ يَّقُوْلُوْا قَدْ اَخَذْنَآ اَمْرَنَا مِنْ قَبْلُ وَيَتَوَلَّوْا وَّهُمْ فَرِحُوْنَ ","bc":"in tuṣibka ḥasanatun tasu`hum, wa in tuṣibka muṣībatuy yaqụlụ qad akhażnā amranā ming qablu wa yatawallaw wa hum fariḥụn","at":"Jika engkau (Muhammad) mendapat kebaikan, mereka tidak senang; tetapi jika engkau ditimpa bencana, mereka berkata, “Sungguh, sejak semula kami telah berhati-hati (tidak pergi berperang),” dan mereka berpaling dengan (perasaan) gembira."},{"id":"51","ar":"قُلْ لَّنْ يُّصِيْبَنَآ اِلَّا مَا كَتَبَ اللّٰهُ لَنَاۚ هُوَ مَوْلٰىنَا وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ","bc":"qul lay yuṣībanā illā mā kataballāhu lanā, huwa maulānā wa 'alallāhi falyatawakkalil-mu`minụn","at":"Katakanlah (Muhammad), “Tidak akan menimpa kami melainkan apa yang telah ditetapkan Allah bagi kami. Dialah pelindung kami, dan hanya kepada Allah bertawakallah orang-orang yang beriman.”"},{"id":"52","ar":"قُلْ هَلْ تَرَبَّصُوْنَ بِنَآ اِلَّآ اِحْدَى الْحُسْنَيَيْنِۗ وَنَحْنُ نَتَرَبَّصُ بِكُمْ اَنْ يُّصِيْبَكُمُ اللّٰهُ بِعَذَابٍ مِّنْ عِنْدِهٖٓ اَوْ بِاَيْدِيْنَاۖ فَتَرَبَّصُوْٓا اِنَّا مَعَكُمْ مُّتَرَبِّصُوْنَ ","bc":"qul hal tarabbaṣụna binā illā iḥdal-ḥusnayaīn, wa naḥnu natarabbaṣu bikum ay yuṣībakumullāhu bi'ażābim min 'indihī au bi`aidīnā fa tarabbaṣū innā ma'akum mutarabbiṣụn","at":"Katakanlah (Muhammad), “Tidak ada yang kamu tunggu-tunggu bagi kami, kecuali salah satu dari dua kebaikan (menang atau mati syahid). Dan kami menunggu-nunggu bagi kamu bahwa Allah akan menimpakan azab kepadamu dari sisi-Nya, atau (azab) melalui tangan kami. Maka tunggulah, sesungguhnya kami menunggu (pula) bersamamu.”"},{"id":"53","ar":"قُلْ اَنْفِقُوْا طَوْعًا اَوْ كَرْهًا لَّنْ يُّتَقَبَّلَ مِنْكُمْ ۗاِنَّكُمْ كُنْتُمْ قَوْمًا فٰسِقِيْنَ ","bc":"qul anfiqụ ṭau'an au kar-hal lay yutaqabbala mingkum, innakum kuntum qauman fāsiqīn","at":"Katakanlah (Muhammad), “Infakkanlah hartamu baik dengan sukarela maupun dengan terpaksa, namun (infakmu) tidak akan diterima. Sesungguhnya kamu adalah orang-orang yang fasik.”"},{"id":"54","ar":"وَمَا مَنَعَهُمْ اَنْ تُقْبَلَ مِنْهُمْ نَفَقٰتُهُمْ اِلَّآ اَنَّهُمْ كَفَرُوْا بِاللّٰهِ وَبِرَسُوْلِهٖ وَلَا يَأْتُوْنَ الصَّلٰوةَ اِلَّا وَهُمْ كُسَالٰى وَلَا يُنْفِقُوْنَ اِلَّا وَهُمْ كٰرِهُوْنَ ","bc":"wa mā mana'ahum an tuqbala min-hum nafaqātuhum illā annahum kafarụ billāhi wa birasụlihī wa lā ya`tụnaṣ-ṣalāta illā wa hum kusālā wa lā yunfiqụna illā wa hum kārihụn","at":"Dan yang menghalang-halangi infak mereka untuk diterima adalah karena mereka kafir (ingkar) kepada Allah dan Rasul-Nya dan mereka tidak melaksanakan salat, melainkan dengan malas dan tidak (pula) menginfakkan (harta) mereka, melainkan dengan rasa enggan (terpaksa)."},{"id":"55","ar":"فَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَلَآ اَوْلَادُهُمْ ۗاِنَّمَا يُرِيْدُ اللّٰهُ لِيُعَذِّبَهُمْ بِهَا فِى الْحَيٰوةِ الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كٰفِرُوْنَ ","bc":"fa lā tu'jibka amwāluhum wa lā aulāduhum, innamā yurīdullāhu liyu'ażżibahum bihā fil-ḥayātid-dun-yā wa taz-haqa anfusuhum wa hum kāfirụn","at":"Maka janganlah harta dan anak-anak mereka membuatmu kagum. Sesungguhnya maksud Allah dengan itu adalah untuk menyiksa mereka dalam kehidupan dunia dan kelak akan mati dalam keadaan kafir."},{"id":"56","ar":"وَيَحْلِفُوْنَ بِاللّٰهِ اِنَّهُمْ لَمِنْكُمْۗ وَمَا هُمْ مِّنْكُمْ وَلٰكِنَّهُمْ قَوْمٌ يَّفْرَقُوْنَ ","bc":"wa yaḥlifụna billāhi innahum lamingkum, wa mā hum mingkum wa lākinnahum qaumuy yafraqụn","at":"Dan mereka (orang-orang munafik) bersumpah dengan (nama) Allah, bahwa sesungguhnya mereka termasuk golonganmu; namun mereka bukanlah dari golonganmu, tetapi mereka orang-orang yang sangat takut (kepadamu)."},{"id":"57","ar":"لَوْ يَجِدُوْنَ مَلْجَاً اَوْ مَغٰرٰتٍ اَوْ مُدَّخَلًا لَّوَلَّوْا اِلَيْهِ وَهُمْ يَجْمَحُوْنَ ","bc":"lau yajidụna malja`an au magārātin au muddakhalal lawallau ilaihi wa hum yajmaḥụn","at":"Sekiranya mereka memperoleh tempat perlindungan, gua-gua atau lubang-lubang (dalam tanah), niscaya mereka pergi (lari) ke sana dengan secepat-cepatnya."},{"id":"58","ar":"وَمِنْهُمْ مَّنْ يَّلْمِزُكَ فِى الصَّدَقٰتِۚ فَاِنْ اُعْطُوْا مِنْهَا رَضُوْا وَاِنْ لَّمْ يُعْطَوْا مِنْهَآ اِذَا هُمْ يَسْخَطُوْنَ ","bc":"wa min-hum may yalmizuka fiṣ-ṣadaqāt, fa in u'ṭụ min-hā raḍụ wa il lam yu'ṭau min-hā iżā hum yaskhaṭụn","at":"Dan di antara mereka ada yang mencelamu tentang (pembagian) sedekah (zakat); jika mereka diberi bagian, mereka bersenang hati, dan jika mereka tidak diberi bagian, tiba-tiba mereka marah."},{"id":"59","ar":"وَلَوْ اَنَّهُمْ رَضُوْا مَآ اٰتٰىهُمُ اللّٰهُ وَرَسُوْلُهٗۙ وَقَالُوْا حَسْبُنَا اللّٰهُ سَيُؤْتِيْنَا اللّٰهُ مِنْ فَضْلِهٖ وَرَسُوْلُهٗٓ اِنَّآ اِلَى اللّٰهِ رَاغِبُوْنَ ","bc":"walau annahum raḍụ mā ātāhumullāhu wa rasụluhụ wa qālụ ḥasbunallāhu sayu`tīnallāhu min faḍlihī wa rasụluhụ innā ilallāhi rāgibụn","at":"Dan sekiranya mereka benar-benar rida dengan apa yang diberikan kepada mereka oleh Allah dan Rasul-Nya, dan berkata, “Cukuplah Allah bagi kami, Allah dan Rasul-Nya akan memberikan kepada kami sebagian dari karunia-Nya. Sesungguhnya kami orang-orang yang berharap kepada Allah.”"},{"id":"60","ar":"۞ اِنَّمَا الصَّدَقٰتُ لِلْفُقَرَاۤءِ وَالْمَسٰكِيْنِ وَالْعَامِلِيْنَ عَلَيْهَا وَالْمُؤَلَّفَةِ قُلُوْبُهُمْ وَفِى الرِّقَابِ وَالْغَارِمِيْنَ وَفِيْ سَبِيْلِ اللّٰهِ وَابْنِ السَّبِيْلِۗ فَرِيْضَةً مِّنَ اللّٰهِ ۗوَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ","bc":"innamaṣ-ṣadaqātu lil-fuqarā`i wal-masākīni wal-'āmilīna 'alaihā wal-mu`allafati qulụbuhum wa fir-riqābi wal-gārimīna wa fī sabīlillāhi wabnis-sabīl, farīḍatam minallāh, wallāhu 'alīmun ḥakīm","at":"Sesungguhnya zakat itu hanyalah untuk orang-orang fakir, orang miskin, amil zakat, yang dilunakkan hatinya (mualaf), untuk (memerdekakan) hamba sahaya, untuk (membebaskan) orang yang berutang, untuk jalan Allah dan untuk orang yang sedang dalam perjalanan, sebagai kewajiban dari Allah. Allah Maha Mengetahui, Mahabijaksana."},{"id":"61","ar":"وَمِنْهُمُ الَّذِيْنَ يُؤْذُوْنَ النَّبِيَّ وَيَقُوْلُوْنَ هُوَ اُذُنٌ ۗقُلْ اُذُنُ خَيْرٍ لَّكُمْ يُؤْمِنُ بِاللّٰهِ وَيُؤْمِنُ لِلْمُؤْمِنِيْنَ وَرَحْمَةٌ لِّلَّذِيْنَ اٰمَنُوْا مِنْكُمْۗ وَالَّذِيْنَ يُؤْذُوْنَ رَسُوْلَ اللّٰهِ لَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"wa min-humullażīna yu`żụnan-nabiyya wa yaqụlụna huwa użun, qul użunu khairil lakum yu`minu billāhi wa yu`minu lil-mu`minīna wa raḥmatul lillażīna āmanụ mingkum, wallażīna yu`żụna rasụlallāhi lahum 'ażābun alīm","at":"Dan di antara mereka (orang munafik) ada orang-orang yang menyakiti hati Nabi (Muhammad) dan mengatakan, “Nabi mempercayai semua apa yang didengarnya.” Katakanlah, “Dia mempercayai semua yang baik bagi kamu, dia beriman kepada Allah, mempercayai orang-orang mukmin, dan menjadi rahmat bagi orang-orang yang beriman di antara kamu.” Dan orang-orang yang menyakiti Rasulullah akan mendapat azab yang pedih."},{"id":"62","ar":"يَحْلِفُوْنَ بِاللّٰهِ لَكُمْ لِيُرْضُوْكُمْ وَاللّٰهُ وَرَسُوْلُهٗٓ اَحَقُّ اَنْ يُّرْضُوْهُ اِنْ كَانُوْا مُؤْمِنِيْنَ ","bc":"yaḥlifụna billāhi lakum liyurḍụkum wallāhu wa rasụluhū aḥaqqu ay yurḍụhu ing kānụ mu`minīn","at":"Mereka bersumpah kepadamu dengan (nama) Allah untuk menyenangkan kamu, padahal Allah dan Rasul-Nya lebih pantas mereka mencari keridaan-Nya jika mereka orang mukmin."},{"id":"63","ar":"اَلَمْ يَعْلَمُوْٓا اَنَّهٗ مَنْ يُّحَادِدِ اللّٰهَ وَرَسُوْلَهٗ فَاَنَّ لَهٗ نَارَ جَهَنَّمَ خَالِدًا فِيْهَاۗ ذٰلِكَ الْخِزْيُ الْعَظِيْمُ ","bc":"a lam ya'lamū annahụ may yuḥādidillāha wa rasụlahụ fa anna lahụ nāra jahannama khālidan fīhā, żālikal-khizyul-'aẓīm","at":"Tidakkah mereka (orang munafik) mengetahui bahwa barangsiapa menentang Allah dan Rasul-Nya, maka sesungguhnya neraka Jahanamlah baginya, dia kekal di dalamnya. Itulah kehinaan yang besar."},{"id":"64","ar":"يَحْذَرُ الْمُنٰفِقُوْنَ اَنْ تُنَزَّلَ عَلَيْهِمْ سُوْرَةٌ تُنَبِّئُهُمْ بِمَا فِيْ قُلُوْبِهِمْۗ قُلِ اسْتَهْزِءُوْاۚ اِنَّ اللّٰهَ مُخْرِجٌ مَّا تَحْذَرُوْنَ ","bc":"yaḥżarul-munāfiqụna an tunazzala 'alaihim sụratun tunabbi`uhum bimā fī qulụbihim, qulistahzi`ụ, innallāha mukhrijum mā taḥżarụn","at":"Orang-orang munafik itu takut jika diturunkan suatu surah yang menerangkan apa yang tersembunyi di dalam hati mereka. Katakanlah (kepada mereka), “Teruskanlah berolok-olok (terhadap Allah dan Rasul-Nya).” Sesungguhnya Allah akan mengungkapkan apa yang kamu takuti itu."},{"id":"65","ar":"وَلَىِٕنْ سَاَلْتَهُمْ لَيَقُوْلُنَّ اِنَّمَا كُنَّا نَخُوْضُ وَنَلْعَبُۗ قُلْ اَبِاللّٰهِ وَاٰيٰتِهٖ وَرَسُوْلِهٖ كُنْتُمْ تَسْتَهْزِءُوْنَ ","bc":"wa la`in sa`altahum layaqụlunna innamā kunnā nakhụḍu wa nal'ab, qul a billāhi wa āyātihī wa rasụlihī kuntum tastahzi`ụn","at":"Dan jika kamu tanyakan kepada mereka, niscaya mereka akan menjawab, “Sesungguhnya kami hanya bersenda gurau dan bermain-main saja.” Katakanlah, “Mengapa kepada Allah, dan ayat-ayat-Nya serta Rasul-Nya kamu selalu berolok-olok?”"},{"id":"66","ar":"لَا تَعْتَذِرُوْا قَدْ كَفَرْتُمْ بَعْدَ اِيْمَانِكُمْ ۗ اِنْ نَّعْفُ عَنْ طَاۤىِٕفَةٍ مِّنْكُمْ نُعَذِّبْ طَاۤىِٕفَةً ۢ بِاَنَّهُمْ كَانُوْا مُجْرِمِيْنَ","bc":"lā ta'tażirụ qad kafartum ba'da īmānikum, in na'fu 'an ṭā`ifatim mingkum nu'ażżib ṭā`ifatam bi`annahum kānụ mujrimīn","at":"Tidak perlu kamu meminta maaf, karena kamu telah kafir setelah beriman. Jika Kami memaafkan sebagian dari kamu (karena telah tobat), niscaya Kami akan mengazab golongan (yang lain) karena sesungguhnya mereka adalah orang-orang yang (selalu) berbuat dosa."},{"id":"67","ar":"اَلْمُنٰفِقُوْنَ وَالْمُنٰفِقٰتُ بَعْضُهُمْ مِّنْۢ بَعْضٍۘ يَأْمُرُوْنَ بِالْمُنْكَرِ وَيَنْهَوْنَ عَنِ الْمَعْرُوْفِ وَيَقْبِضُوْنَ اَيْدِيَهُمْۗ نَسُوا اللّٰهَ فَنَسِيَهُمْ ۗ اِنَّ الْمُنٰفِقِيْنَ هُمُ الْفٰسِقُوْنَ ","bc":"al-munāfiqụna wal-munāfiqātu ba'ḍuhum mim ba'ḍ, ya`murụna bil-mungkari wa yan-hauna 'anil-ma'rụfi wa yaqbiḍụna aidiyahum, nasullāha fa nasiyahum, innal-munāfiqīna humul-fāsiqụn","at":"Orang-orang munafik laki-laki dan perempuan, satu dengan yang lain adalah (sama), mereka menyuruh (berbuat) yang mungkar dan mencegah (perbuatan) yang makruf dan mereka menggenggamkan tangannya (kikir). Mereka telah melupakan kepada Allah, maka Allah melupakan mereka (pula). Sesungguhnya orang-orang munafik itulah orang-orang yang fasik."},{"id":"68","ar":"وَعَدَ اللّٰهُ الْمُنٰفِقِيْنَ وَالْمُنٰفِقٰتِ وَالْكُفَّارَ نَارَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَاۗ هِيَ حَسْبُهُمْ ۚوَلَعَنَهُمُ اللّٰهُ ۚوَلَهُمْ عَذَابٌ مُّقِيْمٌۙ","bc":"wa'adallāhul-munāfiqīna wal-munāfiqāti wal-kuffāra nāra jahannama khālidīna fīhā, hiya ḥasbuhum, wa la'anahumullāh, wa lahum 'ażābum muqīm","at":"Allah menjanjikan (mengancam) orang-orang munafik laki-laki dan perempuan dan orang-orang kafir dengan neraka Jahanam. Mereka kekal di dalamnya. Cukuplah (neraka) itu bagi mereka. Allah melaknat mereka; dan mereka mendapat azab yang kekal,"},{"id":"69","ar":"كَالَّذِيْنَ مِنْ قَبْلِكُمْ كَانُوْٓا اَشَدَّ مِنْكُمْ قُوَّةً وَّاَكْثَرَ اَمْوَالًا وَّاَوْلَادًاۗ فَاسْتَمْتَعُوْا بِخَلَاقِهِمْ فَاسْتَمْتَعْتُمْ بِخَلَاقِكُمْ كَمَا اسْتَمْتَعَ الَّذِيْنَ مِنْ قَبْلِكُمْ بِخَلَاقِهِمْ وَخُضْتُمْ كَالَّذِيْ خَاضُوْاۗ اُولٰۤىِٕكَ حَبِطَتْ اَعْمَالُهُمْ فِى الدُّنْيَا وَالْاٰخِرَةِ ۚوَاُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ","bc":"kallażīna ming qablikum kānū asyadda mingkum quwwataw wa akṡara amwālaw wa aulādā, fastamta'ụ bikhalāqihim fastamta'tum bikhalāqikum kamastamta'allażīna ming qablikum bikhalāqihim wa khuḍtum kallażī khāḍụ, ulā`ika ḥabiṭat a'māluhum fid-dun-yā wal-ākhirah, wa ulā`ika humul-khāsirụn","at":"(keadaan kamu kaum munafik dan musyrikin) seperti orang-orang sebelum kamu, mereka lebih kuat daripada kamu, dan lebih banyak harta dan anak-anaknya. Maka mereka telah menikmati bagiannya, dan kamu telah menikmati bagianmu sebagaimana orang-orang yang sebelummu menikmati bagiannya, dan kamu mempercakapkan (hal-hal yang batil) sebagaimana mereka mempercakapkannya. Mereka itu sia-sia amalnya di dunia dan di akhirat. Mereka itulah orang-orang yang rugi."},{"id":"70","ar":"اَلَمْ يَأْتِهِمْ نَبَاُ الَّذِيْنَ مِنْ قَبْلِهِمْ قَوْمِ نُوْحٍ وَّعَادٍ وَّثَمُوْدَ ەۙ وَقَوْمِ اِبْرٰهِيْمَ وَاَصْحٰبِ مَدْيَنَ وَالْمُؤْتَفِكٰتِۗ اَتَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِۚ فَمَا كَانَ اللّٰهُ لِيَظْلِمَهُمْ وَلٰكِنْ كَانُوْٓا اَنْفُسَهُمْ يَظْلِمُوْنَ","bc":"a lam ya`tihim naba`ullażīna ming qablihim qaumi nụḥiw wa 'ādiw wa ṡamụda wa qaumi ibrāhīma wa aṣ-ḥābi madyana wal-mu`tafikāt, atat-hum rusuluhum bil-bayyināt, fa mā kānallāhu liyaẓlimahum wa lāking kānū anfusahum yaẓlimụn","at":"Apakah tidak sampai kepada mereka berita (tentang) orang-orang yang sebelum mereka, (yaitu) kaum Nuh, ‘Ad, samud, kaum Ibrahim, penduduk Madyan, dan (penduduk) negeri-negeri yang telah musnah? Telah datang kepada mereka rasul-rasul dengan membawa bukti-bukti yang nyata; Allah tidak menzalimi mereka, tetapi merekalah yang menzalimi diri mereka sendiri."},{"id":"71","ar":"وَالْمُؤْمِنُوْنَ وَالْمُؤْمِنٰتُ بَعْضُهُمْ اَوْلِيَاۤءُ بَعْضٍۘ يَأْمُرُوْنَ بِالْمَعْرُوْفِ وَيَنْهَوْنَ عَنِ الْمُنْكَرِ وَيُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَيُطِيْعُوْنَ اللّٰهَ وَرَسُوْلَهٗ ۗاُولٰۤىِٕكَ سَيَرْحَمُهُمُ اللّٰهُ ۗاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ ","bc":"wal-mu`minụna wal-mu`minātu ba'ḍuhum auliyā`u ba'ḍ, ya`murụna bil-ma'rụfi wa yan-hauna 'anil-mungkari wa yuqīmụnaṣ-ṣalāta wa yu`tụnaz-zakāta wa yuṭī'ụnallāha wa rasụlah, ulā`ika sayar-ḥamuhumullāh, innallāha 'azīzun ḥakīm","at":"Dan orang-orang yang beriman, laki-laki dan perempuan, sebagian mereka menjadi penolong bagi sebagian yang lain. Mereka menyuruh (berbuat) yang makruf, dan mencegah dari yang mungkar, melaksanakan salat, menunaikan zakat, dan taat kepada Allah dan Rasul-Nya. Mereka akan diberi rahmat oleh Allah. Sungguh, Allah Mahaperkasa, Mahabijaksana."},{"id":"72","ar":"وَعَدَ اللّٰهُ الْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا وَمَسٰكِنَ طَيِّبَةً فِيْ جَنّٰتِ عَدْنٍ ۗوَرِضْوَانٌ مِّنَ اللّٰهِ اَكْبَرُ ۗذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ ","bc":"wa'adallāhul-mu`minīna wal-mu`mināti jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā wa masākina ṭayyibatan fī jannāti 'adn, wa riḍwānum minallāhi akbar, żālika huwal-fauzul-'aẓīm","at":"Allah menjanjikan kepada orang-orang mukmin laki-laki dan perempuan, (akan mendapat) surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya, dan (mendapat) tempat yang baik di surga ‘Adn. Dan keridaan Allah lebih besar. Itulah kemenangan yang agung."},{"id":"73","ar":"يٰٓاَيُّهَا النَّبِيُّ جَاهِدِ الْكُفَّارَ وَالْمُنٰفِقِيْنَ وَاغْلُظْ عَلَيْهِمْ ۗوَمَأْوٰىهُمْ جَهَنَّمُ وَبِئْسَ الْمَصِيْرُ ","bc":"yā ayyuhan-nabiyyu jāhidil-kuffāra wal-munāfiqīna wagluẓ 'alaihim, wa ma`wāhum jahannamu wa bi`sal-maṣīr","at":"Wahai Nabi! Berjihadlah (melawan) orang-orang kafir dan orang-orang munafik, dan bersikap keraslah terhadap mereka. Tempat mereka adalah neraka Jahanam. Dan itulah seburuk-buruk tempat kembali."},{"id":"74","ar":"يَحْلِفُوْنَ بِاللّٰهِ مَا قَالُوْا ۗوَلَقَدْ قَالُوْا كَلِمَةَ الْكُفْرِ وَكَفَرُوْا بَعْدَ اِسْلَامِهِمْ وَهَمُّوْا بِمَا لَمْ يَنَالُوْاۚ وَمَا نَقَمُوْٓا اِلَّآ اَنْ اَغْنٰىهُمُ اللّٰهُ وَرَسُوْلُهٗ مِنْ فَضْلِهٖ ۚفَاِنْ يَّتُوْبُوْا يَكُ خَيْرًا لَّهُمْ ۚوَاِنْ يَّتَوَلَّوْا يُعَذِّبْهُمُ اللّٰهُ عَذَابًا اَلِيْمًا فِى الدُّنْيَا وَالْاٰخِرَةِ ۚوَمَا لَهُمْ فِى الْاَرْضِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ","bc":"yaḥlifụna billāhi mā qālụ, wa laqad qālụ kalimatal-kufri wa kafarụ ba'da islāmihim wa hammụ bimā lam yanalụ, wa mā naqamū illā an agnāhumullāhu wa rasụluhụ min faḍlih, fa iy yatụbụ yaku khairal lahum, wa iy yatawallau yu'ażżib-humullāhu 'ażāban alīman fid-dun-yā wal-ākhirah, wa mā lahum fil-arḍi miw waliyyiw wa lā naṣīr","at":"Mereka (orang munafik) bersumpah dengan (nama) Allah, bahwa mereka tidak mengatakan (sesuatu yang menyakiti Muhammad). Sungguh, mereka telah mengucapkan perkataan kekafiran, dan telah menjadi kafir setelah Islam, dan menginginkan apa yang mereka tidak dapat mencapainya; dan mereka tidak mencela (Allah dan Rasul-Nya), sekiranya Allah dan Rasul-Nya telah melimpahkan karunia-Nya kepada mereka. Maka jika mereka bertobat, itu adalah lebih baik bagi mereka, dan jika mereka berpaling, niscaya Allah akan mengazab mereka dengan azab yang pedih di dunia dan akhirat; dan mereka tidak mempunyai pelindung dan tidak (pula) penolong di bumi."},{"id":"75","ar":"۞ وَمِنْهُمْ مَّنْ عٰهَدَ اللّٰهَ لَىِٕنْ اٰتٰىنَا مِنْ فَضْلِهٖ لَنَصَّدَّقَنَّ وَلَنَكُوْنَنَّ مِنَ الصّٰلِحِيْنَ ","bc":"wa min-hum man 'āhadallāha la`in ātānā min faḍlihī lanaṣṣaddaqanna wa lanakụnanna minaṣ-ṣāliḥīn","at":"Dan di antara mereka ada orang yang telah berjanji kepada Allah, “Sesungguhnya jika Allah memberikan sebagian dari karunia-Nya kepada kami, niscaya kami akan bersedekah dan niscaya kami termasuk orang-orang yang saleh.”"},{"id":"76","ar":"فَلَمَّآ اٰتٰىهُمْ مِّنْ فَضْلِهٖ بَخِلُوْا بِهٖ وَتَوَلَّوْا وَّهُمْ مُّعْرِضُوْنَ ","bc":"fa lammā ātāhum min faḍlihī bakhilụ bihī wa tawallaw wa hum mu'riḍụn","at":"Ketika Allah memberikan kepada mereka sebagian dari karunia-Nya, mereka menjadi kikir dan berpaling, dan selalu menentang (kebenaran)."},{"id":"77","ar":"فَاَعْقَبَهُمْ نِفَاقًا فِيْ قُلُوْبِهِمْ اِلٰى يَوْمِ يَلْقَوْنَهٗ بِمَآ اَخْلَفُوا اللّٰهَ مَا وَعَدُوْهُ وَبِمَا كَانُوْا يَكْذِبُوْنَ ","bc":"fa a'qabahum nifāqan fī qulụbihim ilā yaumi yalqaunahụ bimā akhlafullāha mā wa'adụhu wa bimā kānụ yakżibụn","at":"Maka Allah menanamkan kemunafikan dalam hati mereka sampai pada waktu mereka menemui-Nya, karena mereka telah mengingkari janji yang telah mereka ikrarkan kepada-Nya dan (juga) karena mereka selalu berdusta."},{"id":"78","ar":"اَلَمْ يَعْلَمُوْٓا اَنَّ اللّٰهَ يَعْلَمُ سِرَّهُمْ وَنَجْوٰىهُمْ وَاَنَّ اللّٰهَ عَلَّامُ الْغُيُوْبِ ","bc":"a lam ya'lamū annallāha ya'lamu sirrahum wa najwāhum wa annallāha 'allāmul-guyụb","at":"Tidakkah mereka mengetahui bahwa Allah mengetahui rahasia dan bisikan mereka, dan bahwa Allah mengetahui segala yang gaib?"},{"id":"79","ar":"اَلَّذِيْنَ يَلْمِزُوْنَ الْمُطَّوِّعِيْنَ مِنَ الْمُؤْمِنِيْنَ فِى الصَّدَقٰتِ وَالَّذِيْنَ لَا يَجِدُوْنَ اِلَّا جُهْدَهُمْ فَيَسْخَرُوْنَ مِنْهُمْ ۗسَخِرَ اللّٰهُ مِنْهُمْ ۖ وَلَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"allażīna yalmizụnal-muṭṭawwi'īna minal-mu`minīna fiṣ-ṣadaqāti wallażīna lā yajidụna illā juhdahum fa yaskharụna min-hum, sakhirallāhu min-hum wa lahum 'ażābun alīm","at":"(Orang munafik) yaitu mereka yang mencela orang-orang beriman yang memberikan sedekah dengan sukarela dan yang (mencela) orang-orang yang hanya memperoleh (untuk disedekahkan) sekedar kesanggupannya, maka orang-orang munafik itu menghina mereka. Allah akan membalas penghinaan mereka, dan mereka akan mendapat azab yang pedih."},{"id":"80","ar":"اِسْتَغْفِرْ لَهُمْ اَوْ لَا تَسْتَغْفِرْ لَهُمْۗ اِنْ تَسْتَغْفِرْ لَهُمْ سَبْعِيْنَ مَرَّةً فَلَنْ يَّغْفِرَ اللّٰهُ لَهُمْ ۗذٰلِكَ بِاَنَّهُمْ كَفَرُوْا بِاللّٰهِ وَرَسُوْلِهٖۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الْفٰسِقِيْنَ ","bc":"istagfir lahum au lā tastagfir lahum, in tastagfir lahum sab'īna marratan fa lay yagfirallāhu lahum, żālika bi`annahum kafarụ billāhi wa rasụlih, wallāhu lā yahdil-qaumal-fāsiqīn","at":"(Sama saja) engkau (Muhammad) memohonkan ampunan bagi mereka atau tidak memohonkan ampunan bagi mereka. Walaupun engkau memohonkan ampunan bagi mereka tujuh puluh kali, Allah tidak akan memberi ampunan kepada mereka. Yang demikian itu karena mereka ingkar (kafir) kepada Allah dan Rasul-Nya. Dan Allah tidak memberi petunjuk kepada orang-orang yang fasik."},{"id":"81","ar":"فَرِحَ الْمُخَلَّفُوْنَ بِمَقْعَدِهِمْ خِلٰفَ رَسُوْلِ اللّٰهِ وَكَرِهُوْٓا اَنْ يُّجَاهِدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْ فِيْ سَبِيْلِ اللّٰهِ وَقَالُوْا لَا تَنْفِرُوْا فِى الْحَرِّۗ قُلْ نَارُ جَهَنَّمَ اَشَدُّ حَرًّاۗ لَوْ كَانُوْا يَفْقَهُوْنَ ","bc":"fariḥal-mukhallafụna bimaq'adihim khilāfa rasụlillāhi wa karihū ay yujāhidụ bi`amwālihim wa anfusihim fī sabīlillāhi wa qālụ lā tanfirụ fil-ḥarr, qul nāru jahannama asyaddu ḥarrā, lau kānụ yafqahụn","at":"Orang-orang yang ditinggalkan (tidak ikut berperang), merasa gembira dengan duduk-duduk diam sepeninggal Rasulullah. Mereka tidak suka berjihad dengan harta dan jiwa mereka di jalan Allah dan mereka berkata, “Janganlah kamu berangkat (pergi berperang) dalam panas terik ini.” Katakanlah (Muhammad), “Api neraka Jahanam lebih panas,” jika mereka mengetahui."},{"id":"82","ar":"فَلْيَضْحَكُوْا قَلِيْلًا وَّلْيَبْكُوْا كَثِيْرًاۚ جَزَاۤءًۢ بِمَا كَانُوْا يَكْسِبُوْنَ ","bc":"falyaḍ-ḥakụ qalīlaw walyabkụ kaṡīrā, jazā`am bimā kānụ yaksibụn","at":"Maka biarkanlah mereka tertawa sedikit dan menangis yang banyak, sebagai balasan terhadap apa yang selalu mereka perbuat."},{"id":"83","ar":"فَاِنْ رَّجَعَكَ اللّٰهُ اِلٰى طَاۤىِٕفَةٍ مِّنْهُمْ فَاسْتَأْذَنُوْكَ لِلْخُرُوْجِ فَقُلْ لَّنْ تَخْرُجُوْا مَعِيَ اَبَدًا وَّلَنْ تُقَاتِلُوْا مَعِيَ عَدُوًّاۗ اِنَّكُمْ رَضِيْتُمْ بِالْقُعُوْدِ اَوَّلَ مَرَّةٍۗ فَاقْعُدُوْا مَعَ الْخَالِفِيْنَ ","bc":"fa ir raja'akallāhu ilā ṭā`ifatim min-hum fasta`żanụka lil-khurụji fa qul lan takhrujụ ma'iya abadaw wa lan tuqātilụ ma'iya 'aduwwā, innakum raḍītum bil-qu'ụdi awwala marrah, faq'udụ ma'al-khālifīn","at":"Maka jika Allah mengembalikanmu (Muhammad) kepada suatu golongan dari mereka (orang-orang munafik), kemudian mereka meminta izin kepadamu untuk keluar (pergi berperang), maka katakanlah, “Kamu tidak boleh keluar bersamaku selama-lamanya dan tidak boleh memerangi musuh bersamaku. Sesungguhnya kamu telah rela tidak pergi (berperang) sejak semula. Karena itu duduklah (tinggallah) bersama orang-orang yang tidak ikut (berperang).”"},{"id":"84","ar":"وَلَا تُصَلِّ عَلٰٓى اَحَدٍ مِّنْهُمْ مَّاتَ اَبَدًا وَّلَا تَقُمْ عَلٰى قَبْرِهٖۗ اِنَّهُمْ كَفَرُوْا بِاللّٰهِ وَرَسُوْلِهٖ وَمَاتُوْا وَهُمْ فٰسِقُوْنَ ","bc":"wa lā tuṣalli 'alā aḥadim min-hum māta abadaw wa lā taqum 'alā qabrih, innahum kafarụ billāhi wa rasụlihī wa mātụ wa hum fāsiqụn","at":"Dan janganlah engkau (Muhammad) melaksanakan salat untuk seseorang yang mati di antara mereka (orang-orang munafik), selama-lamanya dan janganlah engkau berdiri (mendoakan) di atas kuburnya. Sesungguhnya mereka ingkar kepada Allah dan Rasul-Nya dan mereka mati dalam keadaan fasik."},{"id":"85","ar":"وَلَا تُعْجِبْكَ اَمْوَالُهُمْ وَاَوْلَادُهُمْۗ اِنَّمَا يُرِيْدُ اللّٰهُ اَنْ يُّعَذِّبَهُمْ بِهَا فِى الدُّنْيَا وَتَزْهَقَ اَنْفُسُهُمْ وَهُمْ كٰفِرُوْنَ ","bc":"wa lā tu'jibka amwāluhum wa aulāduhum, innamā yurīdullāhu ay yu'ażżibahum bihā fid-dun-yā wa taz-haqa anfusuhum wa hum kāfirụn","at":"Dan janganlah engkau (Muhammad) kagum terhadap harta dan anak-anak mereka. Sesungguhnya dengan itu Allah hendak menyiksa mereka di dunia dan agar nyawa mereka melayang, sedang mereka dalam keadaan kafir."},{"id":"86","ar":"وَاِذَآ اُنْزِلَتْ سُوْرَةٌ اَنْ اٰمِنُوْا بِاللّٰهِ وَجَاهِدُوْا مَعَ رَسُوْلِهِ اسْتَأْذَنَكَ اُولُوا الطَّوْلِ مِنْهُمْ وَقَالُوْا ذَرْنَا نَكُنْ مَّعَ الْقٰعِدِيْنَ ","bc":"wa iżā unzilat sụratun an āminụ billāhi wa jāhidụ ma'a rasụlihista`żanaka uluṭ-ṭauli min-hum wa qālụ żarnā nakum ma'al-qā'idīn","at":"Dan apabila diturunkan suatu surah (yang memerintahkan kepada orang-orang munafik), “Berimanlah kepada Allah dan berjihadlah bersama Rasul-Nya,” niscaya orang-orang yang kaya dan berpengaruh di antara mereka meminta izin kepadamu (untuk tidak berjihad) dan mereka berkata, “Biarkanlah kami berada bersama orang-orang yang duduk (tinggal di rumah).”"},{"id":"87","ar":"رَضُوْا بِاَنْ يَّكُوْنُوْا مَعَ الْخَوَالِفِ وَطُبِعَ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَفْقَهُوْنَ ","bc":"raḍụ bi`ay yakụnụ ma'al-khawālifi wa ṭubi'a 'alā qulụbihim fa hum lā yafqahụn","at":"Mereka rela berada bersama orang-orang yang tidak pergi berperang, dan hati mereka telah tertutup, sehingga mereka tidak memahami (kebahagiaan beriman dan berjihad)."},{"id":"88","ar":"لٰكِنِ الرَّسُوْلُ وَالَّذِيْنَ اٰمَنُوْا مَعَهٗ جَاهَدُوْا بِاَمْوَالِهِمْ وَاَنْفُسِهِمْۗ وَاُولٰۤىِٕكَ لَهُمُ الْخَيْرٰتُ ۖوَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ ","bc":"lākinir-rasụlu wallażīna āmanụ ma'ahụ jāhadụ bi`amwālihim wa anfusihim, wa ulā`ika lahumul-khairātu wa ulā`ika humul-mufliḥụn","at":"Tetapi Rasul dan orang-orang yang beriman bersama dia, (mereka) berjihad dengan harta dan jiwa. Mereka itu memperoleh kebaikan. Mereka itulah orang-orang yang beruntung."},{"id":"89","ar":"اَعَدَّ اللّٰهُ لَهُمْ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَاۗ ذٰلِكَ الْفَوْزُ الْعَظِيْمُ ","bc":"a'addallāhu lahum jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā, żālikal-fauzul-'aẓīm","at":"Allah telah menyediakan bagi mereka surga yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Itulah kemenangan yang agung."},{"id":"90","ar":"وَجَاۤءَ الْمُعَذِّرُوْنَ مِنَ الْاَعْرَابِ لِيُؤْذَنَ لَهُمْ وَقَعَدَ الَّذِيْنَ كَذَبُوا اللّٰهَ وَرَسُوْلَهٗ ۗسَيُصِيْبُ الَّذِيْنَ كَفَرُوْا مِنْهُمْ عَذَابٌ اَلِيْمٌ ","bc":"wa jā`al-mu'ażżirụna minal-a'rābi liyu`żana lahum wa qa'adallażīna każabullāha wa rasụlah, sayuṣībullażīna kafarụ min-hum 'ażābun alīm","at":"Dan di antara orang-orang Arab Badui datang (kepada Nabi) mengemukakan alasan, agar diberi izin (untuk tidak pergi berperang), sedang orang-orang yang mendustakan Allah dan Rasul-Nya, duduk berdiam. Kelak orang-orang yang kafir di antara mereka akan ditimpa azab yang pedih."},{"id":"91","ar":"لَيْسَ عَلَى الضُّعَفَاۤءِ وَلَا عَلَى الْمَرْضٰى وَلَا عَلَى الَّذِيْنَ لَا يَجِدُوْنَ مَا يُنْفِقُوْنَ حَرَجٌ اِذَا نَصَحُوْا لِلّٰهِ وَرَسُوْلِهٖۗ مَا عَلَى الْمُحْسِنِيْنَ مِنْ سَبِيْلٍ ۗوَاللّٰهُ غَفُوْرٌ رَّحِيْمٌۙ ","bc":"laisa 'alaḍ-ḍu'afā`i wa lā 'alal-marḍā wa lā 'alallażīna lā yajidụna mā yunfiqụna ḥarajun iżā naṣaḥụ lillāhi wa rasụlih, mā 'alal-muḥsinīna min sabīl, wallāhu gafụrur raḥīm","at":"Tidak ada dosa (karena tidak pergi berperang) atas orang yang lemah, orang yang sakit dan orang yang tidak memperoleh apa yang akan mereka infakkan, apabila mereka berlaku ikhlas kepada Allah dan Rasul-Nya. Tidak ada alasan apa pun untuk menyalahkan orang-orang yang berbuat baik. Dan Allah Maha Pengampun, Maha Penyayang,"},{"id":"92","ar":"وَّلَا عَلَى الَّذِيْنَ اِذَا مَآ اَتَوْكَ لِتَحْمِلَهُمْ قُلْتَ لَآ اَجِدُ مَآ اَحْمِلُكُمْ عَلَيْهِ ۖتَوَلَّوْا وَّاَعْيُنُهُمْ تَفِيْضُ مِنَ الدَّمْعِ حَزَنًا اَلَّا يَجِدُوْا مَا يُنْفِقُوْنَۗ ","bc":"wa lā 'alallażīna iżā mā atauka litaḥmilahum qulta lā ajidu mā aḥmilukum 'alaihi tawallaw wa a'yunuhum tafīḍu minad-dam'i ḥazanan allā yajidụ mā yunfiqụn","at":"dan tidak ada (pula dosa) atas orang-orang yang datang kepadamu (Muhammad), agar engkau memberi kendaraan kepada mereka, lalu engkau berkata, “Aku tidak memperoleh kendaraan untuk membawamu,” lalu mereka kembali, sedang mata mereka bercucuran air mata karena sedih, disebabkan mereka tidak memperoleh apa yang akan mereka infakkan (untuk ikut berperang)."},{"id":"93","ar":"اِنَّمَا السَّبِيْلُ عَلَى الَّذِيْنَ يَسْتَأْذِنُوْنَكَ وَهُمْ اَغْنِيَاۤءُۚ رَضُوْا بِاَنْ يَّكُوْنُوْا مَعَ الْخَوَالِفِۙ وَطَبَعَ اللّٰهُ عَلٰى قُلُوْبِهِمْ فَهُمْ لَا يَعْلَمُوْنَ ۔","bc":"innamas-sabīlu 'alallażīna yasta`żinụnaka wa hum agniyā`, raḍụ bi`ay yakụnụ ma'al-khawālifi wa ṭaba'allāhu 'alā qulụbihim fa hum lā ya'lamụn","at":"Sesungguhnya alasan (untuk menyalahkan) hanyalah terhadap orang-orang yang meminta izin kepadamu (untuk tidak ikut berperang), padahal mereka orang kaya. Mereka rela berada bersama orang-orang yang tidak ikut berperang dan Allah telah mengunci hati mereka, sehingga mereka tidak mengetahui (akibat perbuatan mereka)."},{"id":"94","ar":"يَعْتَذِرُوْنَ اِلَيْكُمْ اِذَا رَجَعْتُمْ اِلَيْهِمْ ۗ قُلْ لَّا تَعْتَذِرُوْا لَنْ نُّؤْمِنَ لَكُمْ قَدْ نَبَّاَنَا اللّٰهُ مِنْ اَخْبَارِكُمْ وَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُوْلُهٗ ثُمَّ تُرَدُّوْنَ اِلٰى عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ","bc":"ya'tażirụna ilaikum iżā raja'tum ilaihim, qul lā ta'tażirụ lan nu`mina lakum qad nabba`anallāhu min akhbārikum wa sayarallāhu 'amalakum wa rasụluhụ ṡumma turaddụna ilā 'ālimil-gaibi wasy-syahādati fa yunabbi`ukum bimā kuntum ta'malụn","at":"Mereka (orang-orang munafik yang tidak ikut berperang) akan mengemukakan alasannya kepadamu ketika kamu telah kembali kepada mereka. Katakanlah (Muhammad), “Janganlah kamu mengemukakan alasan; kami tidak percaya lagi kepadamu, sungguh, Allah telah memberitahukan kepada kami tentang beritamu. Dan Allah akan melihat pekerjaanmu, (demikian pula) Rasul-Nya, kemudian kamu dikembalikan kepada (Allah) Yang Maha Mengetahui segala yang gaib dan yang nyata, lalu Dia memberitakan kepadamu apa yang telah kamu kerjakan.”"},{"id":"95","ar":"سَيَحْلِفُوْنَ بِاللّٰهِ لَكُمْ اِذَا انْقَلَبْتُمْ اِلَيْهِمْ لِتُعْرِضُوْا عَنْهُمْ ۗ فَاَعْرِضُوْا عَنْهُمْ ۗ اِنَّهُمْ رِجْسٌۙ وَّمَأْوٰىهُمْ جَهَنَّمُ جَزَاۤءً ۢبِمَا كَانُوْا يَكْسِبُوْنَ","bc":"sayaḥlifụna billāhi lakum iżangqalabtum ilaihim litu'riḍụ 'an-hum, fa a'riḍụ 'an-hum, innahum rijsuw wa ma`wāhum jahannamu jazā`am bimā kānụ yaksibụn","at":"Mereka akan bersumpah kepadamu dengan nama Allah, ketika kamu kembali kepada mereka, agar kamu berpaling dari mereka. Maka berpalinglah dari mereka; karena sesungguhnya mereka itu berjiwa kotor dan tempat mereka neraka Jahanam, sebagai balasan atas apa yang telah mereka kerjakan."},{"id":"96","ar":"يَحْلِفُوْنَ لَكُمْ لِتَرْضَوْا عَنْهُمْ ۚفَاِنْ تَرْضَوْا عَنْهُمْ فَاِنَّ اللّٰهَ لَا يَرْضٰى عَنِ الْقَوْمِ الْفٰسِقِيْنَ","bc":"yaḥlifụna lakum litarḍau 'an-hum, fa in tarḍau 'an-hum fa innallāha lā yarḍā 'anil-qaumil-fāsiqīn","at":"Mereka akan bersumpah kepadamu agar kamu bersedia menerima mereka. Tetapi sekalipun kamu menerima mereka, Allah tidak akan rida kepada orang-orang yang fasik."},{"id":"97","ar":"اَلْاَعْرَابُ اَشَدُّ كُفْرًا وَّنِفَاقًا وَّاَجْدَرُ اَلَّا يَعْلَمُوْا حُدُوْدَ مَآ اَنْزَلَ اللّٰهُ عَلٰى رَسُوْلِهٖ ۗوَاللّٰهُ عَلِيْمٌ حَكِيْمٌ","bc":"al-a'rābu asyaddu kufraw wa nifāqaw wa ajdaru allā ya'lamụ ḥudụda mā anzalallāhu 'alā rasụlih, wallāhu 'alīmun ḥakīm","at":"Orang-orang Arab Badui itu lebih kuat kekafiran dan kemunafikannya, dan sangat wajar tidak mengetahui hukum-hukum yang diturunkan Allah kepada Rasul-Nya. Allah Maha Mengetahui, Mahabijaksana."},{"id":"98","ar":"وَمِنَ الْاَعْرَابِ مَنْ يَّتَّخِذُ مَا يُنْفِقُ مَغْرَمًا وَّيَتَرَبَّصُ بِكُمُ الدَّوَاۤىِٕرَ ۗعَلَيْهِمْ دَاۤىِٕرَةُ السَّوْءِ ۗوَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ","bc":"wa minal-a'rābi may yattakhiżu mā yunfiqu magramaw wa yatarabbaṣu bikumud-dawā`ir, 'alaihim dā`iratus-saụ`, wallāhu samī'un 'alīm","at":"Dan di antara orang-orang Arab Badui itu ada yang memandang apa yang diinfakkannya (di jalan Allah) sebagai suatu kerugian; dia menanti-nanti marabahaya menimpamu, merekalah yang akan ditimpa marabahaya. Allah Maha Mendengar, Maha Mengetahui."},{"id":"99","ar":"وَمِنَ الْاَعْرَابِ مَنْ يُّؤْمِنُ بِاللّٰهِ وَالْيَوْمِ الْاٰخِرِ وَيَتَّخِذُ مَا يُنْفِقُ قُرُبٰتٍ عِنْدَ اللّٰهِ وَصَلَوٰتِ الرَّسُوْلِ ۗ اَلَآ اِنَّهَا قُرْبَةٌ لَّهُمْ ۗ سَيُدْخِلُهُمُ اللّٰهُ فِيْ رَحْمَتِهٖ ۗاِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ","bc":"wa minal-a'rābi may yu`minu billāhi wal-yaumil-ākhiri wa yattakhiżu mā yunfiqu qurubātin 'indallāhi wa ṣalawātir-rasụl, alā innahā qurbatul lahum, sayudkhiluhumullāhu fī raḥmatih, innallāha gafụrur raḥīm","at":"Dan di antara orang-orang Arab Badui itu ada yang beriman kepada Allah dan hari kemudian, dan memandang apa yang diinfakkannya (di jalan Allah) sebagai jalan mendekatkan kepada Allah dan sebagai jalan untuk (memperoleh) doa Rasul. Ketahuilah, sesungguhnya infak itu suatu jalan bagi mereka untuk mendekatkan diri (kepada Allah). Kelak Allah akan memasukkan mereka ke dalam rahmat (surga)-Nya; sesungguhnya Allah Maha Pengampun, Maha Penyayang."},{"id":"100","ar":"وَالسّٰبِقُوْنَ الْاَوَّلُوْنَ مِنَ الْمُهٰجِرِيْنَ وَالْاَنْصَارِ وَالَّذِيْنَ اتَّبَعُوْهُمْ بِاِحْسَانٍۙ رَّضِيَ اللّٰهُ عَنْهُمْ وَرَضُوْا عَنْهُ وَاَعَدَّ لَهُمْ جَنّٰتٍ تَجْرِيْ تَحْتَهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَآ اَبَدًا ۗذٰلِكَ الْفَوْزُ الْعَظِيْمُ ","bc":"was-sābiqụnal-awwalụna minal-muhājirīna wal-anṣāri wallażīnattaba'ụhum bi`iḥsānir raḍiyallāhu 'an-hum wa raḍụ 'an-hu wa a'adda lahum jannātin tajrī taḥtahal-an-hāru khālidīna fīhā abadā, żālikal-fauzul-'aẓīm","at":"Dan orang-orang yang terdahulu lagi yang pertama-tama (masuk Islam) di antara orang-orang Muhajirin dan Ansar dan orang-orang yang mengikuti mereka dengan baik, Allah rida kepada mereka dan mereka pun rida kepada Allah. Allah menyediakan bagi mereka surga-surga yang mengalir di bawahnya sungai-sungai. Mereka kekal di dalamnya selama-lamanya. Itulah kemenangan yang agung."},{"id":"101","ar":"وَمِمَّنْ حَوْلَكُمْ مِّنَ الْاَعْرَابِ مُنٰفِقُوْنَ ۗوَمِنْ اَهْلِ الْمَدِيْنَةِ مَرَدُوْا عَلَى النِّفَاقِۗ لَا تَعْلَمُهُمْۗ نَحْنُ نَعْلَمُهُمْۗ سَنُعَذِّبُهُمْ مَّرَّتَيْنِ ثُمَّ يُرَدُّوْنَ اِلٰى عَذَابٍ عَظِيْمٍ ۚ ","bc":"wa mim man ḥaulakum minal-a'rābi munāfiqụn, wa min ahlil-madīnati maradụ 'alan-nifāq, lā ta'lamuhum, naḥnu na'lamuhum, sanu'ażżibuhum marrataini ṡumma yuraddụna ilā 'ażābin 'aẓīm","at":"Dan di antara orang-orang Arab Badui yang (tinggal) di sekitarmu, ada orang-orang munafik. Dan di antara penduduk Madinah (ada juga orang-orang munafik), mereka keterlaluan dalam kemunafikannya. Engkau (Muhammad) tidak mengetahui mereka, tetapi Kami mengetahuinya. Nanti mereka akan Kami siksa dua kali, kemudian mereka akan dikembalikan kepada azab yang besar."},{"id":"102","ar":"وَاٰخَرُوْنَ اعْتَرَفُوْا بِذُنُوْبِهِمْ خَلَطُوْا عَمَلًا صَالِحًا وَّاٰخَرَ سَيِّئًاۗ عَسَى اللّٰهُ اَنْ يَّتُوْبَ عَلَيْهِمْۗ اِنَّ اللّٰهَ غَفُوْرٌ رَّحِيْمٌ ","bc":"wa ākharụna'tarafụ biżunụbihim khalaṭụ 'amalan ṣāliḥaw wa ākhara sayyi`ā, 'asallāhu ay yatụba 'alaihim, innallāha gafụrur raḥīm","at":"Dan (ada pula) orang lain yang mengakui dosa-dosa mereka, mereka mencampuradukkan pekerjaan yang baik dengan pekerjaan lain yang buruk. Mudah-mudahan Allah menerima tobat mereka. Sesungguhnya Allah Maha Pengampun, Maha Penyayang."},{"id":"103","ar":"خُذْ مِنْ اَمْوَالِهِمْ صَدَقَةً تُطَهِّرُهُمْ وَتُزَكِّيْهِمْ بِهَا وَصَلِّ عَلَيْهِمْۗ اِنَّ صَلٰوتَكَ سَكَنٌ لَّهُمْۗ وَاللّٰهُ سَمِيْعٌ عَلِيْمٌ ","bc":"khuż min amwālihim ṣadaqatan tuṭahhiruhum wa tuzakkīhim bihā wa ṣalli 'alaihim, inna ṣalātaka sakanul lahum, wallāhu samī'un 'alīm","at":"Ambillah zakat dari harta mereka, guna membersihkan dan menyucikan mereka, dan berdoalah untuk mereka. Sesungguhnya doamu itu (menumbuhkan) ketenteraman jiwa bagi mereka. Allah Maha Mendengar, Maha Mengetahui."},{"id":"104","ar":"اَلَمْ يَعْلَمُوْٓا اَنَّ اللّٰهَ هُوَ يَقْبَلُ التَّوْبَةَ عَنْ عِبَادِهٖ وَيَأْخُذُ الصَّدَقٰتِ وَاَنَّ اللّٰهَ هُوَ التَّوَّابُ الرَّحِيْمُ ","bc":"a lam ya'lamū annallāha huwa yaqbalut-taubata 'an 'ibādihī wa ya`khużuṣ-ṣadaqāti wa annallāha huwat-tawwābur-raḥīm","at":"Tidakkah mereka mengetahui, bahwa Allah menerima tobat hamba-hamba-Nya dan menerima zakat(nya), dan bahwa Allah Maha Penerima tobat, Maha Penyayang?"},{"id":"105","ar":"وَقُلِ اعْمَلُوْا فَسَيَرَى اللّٰهُ عَمَلَكُمْ وَرَسُوْلُهٗ وَالْمُؤْمِنُوْنَۗ وَسَتُرَدُّوْنَ اِلٰى عٰلِمِ الْغَيْبِ وَالشَّهَادَةِ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَۚ ","bc":"wa quli'malụ fa sayarallāhu 'amalakum wa rasụluhụ wal-mu`minụn, wa saturaddụna ilā 'ālimil-gaibi wasy-syahādati fa yunabbi`ukum bimā kuntum ta'malụn","at":"Dan katakanlah, “Bekerjalah kamu, maka Allah akan melihat pekerjaanmu, begitu juga Rasul-Nya dan orang-orang mukmin, dan kamu akan dikembalikan kepada (Allah) Yang Mengetahui yang gaib dan yang nyata, lalu diberitakan-Nya kepada kamu apa yang telah kamu kerjakan.”"},{"id":"106","ar":"وَاٰخَرُوْنَ مُرْجَوْنَ لِاَمْرِ اللّٰهِ اِمَّا يُعَذِّبُهُمْ وَاِمَّا يَتُوْبُ عَلَيْهِمْۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ","bc":"wa ākharụna murjauna li`amrillāhi immā yu'ażżibuhum wa immā yatụbu 'alaihim, wallāhu 'alīmun ḥakīm","at":"Dan ada (pula) orang-orang lain yang ditangguhkan sampai ada keputusan Allah; mungkin Allah akan mengazab mereka dan mungkin Allah akan menerima tobat mereka. Allah Maha Mengetahui, Mahabijaksana."},{"id":"107","ar":"وَالَّذِيْنَ اتَّخَذُوْا مَسْجِدًا ضِرَارًا وَّكُفْرًا وَّتَفْرِيْقًاۢ بَيْنَ الْمُؤْمِنِيْنَ وَاِرْصَادًا لِّمَنْ حَارَبَ اللّٰهَ وَرَسُوْلَهٗ مِنْ قَبْلُ ۗوَلَيَحْلِفُنَّ اِنْ اَرَدْنَآ اِلَّا الْحُسْنٰىۗ وَاللّٰهُ يَشْهَدُ اِنَّهُمْ لَكٰذِبُوْنَ ","bc":"wallażīnattakhażụ masjidan ḍirāraw wa kufraw wa tafrīqam bainal-mu`minīna wa irṣādal liman ḥāraballāha wa rasụlahụ ming qabl, wa layaḥlifunna in aradnā illal-ḥusnā, wallāhu yasy-hadu innahum lakāżibụn","at":"Dan (di antara orang-orang munafik itu) ada yang mendirikan masjid untuk menimbulkan bencana (pada orang-orang yang beriman), untuk kekafiran dan untuk memecah belah di antara orang-orang yang beriman serta menunggu kedatangan orang-orang yang telah memerangi Allah dan Rasul-Nya sejak dahulu. Mereka dengan pasti bersumpah, “Kami hanya menghendaki kebaikan.” Dan Allah menjadi saksi bahwa mereka itu pendusta (dalam sumpahnya)."},{"id":"108","ar":"لَا تَقُمْ فِيْهِ اَبَدًاۗ لَمَسْجِدٌ اُسِّسَ عَلَى التَّقْوٰى مِنْ اَوَّلِ يَوْمٍ اَحَقُّ اَنْ تَقُوْمَ فِيْهِۗ فِيْهِ رِجَالٌ يُّحِبُّوْنَ اَنْ يَّتَطَهَّرُوْاۗ وَاللّٰهُ يُحِبُّ الْمُطَّهِّرِيْنَ ","bc":"lā taqum fīhi abadā, lamasjidun ussisa 'alat-taqwā min awwali yaumin aḥaqqu an taqụma fīh, fīhi rijāluy yuḥibbụna ay yataṭahharụ, wallāhu yuḥibbul-muṭṭahhirīn","at":"Janganlah engkau melaksanakan salat dalam masjid itu selama-lamanya. Sungguh, masjid yang didirikan atas dasar takwa, sejak hari pertama adalah lebih pantas engkau melaksanakan salat di dalamnya. Di dalamnya ada orang-orang yang ingin membersihkan diri. Allah menyukai orang-orang yang bersih."},{"id":"109","ar":"اَفَمَنْ اَسَّسَ بُنْيَانَهٗ عَلٰى تَقْوٰى مِنَ اللّٰهِ وَرِضْوَانٍ خَيْرٌ اَمْ مَّنْ اَسَّسَ بُنْيَانَهٗ عَلٰى شَفَا جُرُفٍ هَارٍ فَانْهَارَ بِهٖ فِيْ نَارِ جَهَنَّمَۗ وَاللّٰهُ لَا يَهْدِى الْقَوْمَ الظّٰلِمِيْنَ ","bc":"a fa man assasa bun-yānahụ 'alā taqwā minallāhi wa riḍwānin khairun am man assasa bun-yānahụ 'alā syafā jurufin hārin fan-hāra bihī fī nāri jahannam, wallāhu lā yahdil qaumaẓ-ẓālimīn","at":"Maka apakah orang-orang yang mendirikan bangunan (masjid) atas dasar takwa kepada Allah dan keridaan(-Nya) itu lebih baik, ataukah orang-orang yang mendirikan bangunannya di tepi jurang yang runtuh, lalu (bangunan) itu roboh bersama-sama dengan dia ke dalam neraka Jahanam? Allah tidak memberi petunjuk kepada orang-orang yang zalim."},{"id":"110","ar":"لَا يَزَالُ بُنْيَانُهُمُ الَّذِيْ بَنَوْا رِيْبَةً فِيْ قُلُوْبِهِمْ اِلَّآ اَنْ تَقَطَّعَ قُلُوْبُهُمْۗ وَاللّٰهُ عَلِيْمٌ حَكِيْمٌ ","bc":"lā yazālu bun-yānuhumullażī banau rībatan fī qulụbihim illā an taqaṭṭa'a qulụbuhum, wallāhu 'alīmun ḥakīm","at":"Bangunan yang mereka dirikan itu senantiasa menjadi penyebab keraguan dalam hati mereka, sampai hati mereka hancur. Dan Allah Maha Mengetahui, Mahabijaksana."},{"id":"111","ar":"۞ اِنَّ اللّٰهَ اشْتَرٰى مِنَ الْمُؤْمِنِيْنَ اَنْفُسَهُمْ وَاَمْوَالَهُمْ بِاَنَّ لَهُمُ الْجَنَّةَۗ يُقَاتِلُوْنَ فِيْ سَبِيْلِ اللّٰهِ فَيَقْتُلُوْنَ وَيُقْتَلُوْنَ وَعْدًا عَلَيْهِ حَقًّا فِى التَّوْرٰىةِ وَالْاِنْجِيْلِ وَالْقُرْاٰنِۗ وَمَنْ اَوْفٰى بِعَهْدِهٖ مِنَ اللّٰهِ فَاسْتَبْشِرُوْا بِبَيْعِكُمُ الَّذِيْ بَايَعْتُمْ بِهٖۗ وَذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ ","bc":"innallāhasytarā minal-mu`minīna anfusahum wa amwālahum bi`anna lahumul-jannah, yuqātilụna fī sabīlillāhi fa yaqtulụna wa yuqtalụna wa'dan 'alaihi ḥaqqan fit-taurāti wal-injīli wal-qur`ān, wa man aufā bi'ahdihī minallāhi fastabsyirụ bibai'ikumullażī bāya'tum bih, wa żālika huwal-fauzul-'aẓīm","at":"Sesungguhnya Allah membeli dari orang-orang mukmin, baik diri mau-pun harta mereka dengan memberikan surga untuk mereka. Mereka berperang di jalan Allah; sehingga mereka membunuh atau terbunuh, (sebagai) janji yang benar dari Allah di dalam Taurat, Injil, dan Al-Qur'an. Dan siapakah yang lebih menepati janjinya selain Allah? Maka bergembiralah dengan jual beli yang telah kamu lakukan itu, dan demikian itulah kemenangan yang agung."},{"id":"112","ar":"اَلتَّاۤىِٕبُوْنَ الْعٰبِدُوْنَ الْحَامِدُوْنَ السَّاۤىِٕحُوْنَ الرَّاكِعُوْنَ السَّاجِدُوْنَ الْاٰمِرُوْنَ بِالْمَعْرُوْفِ وَالنَّاهُوْنَ عَنِ الْمُنْكَرِ وَالْحٰفِظُوْنَ لِحُدُوْدِ اللّٰهِ ۗوَبَشِّرِ الْمُؤْمِنِيْنَ ","bc":"attā`ibụnal-'ābidụnal-ḥāmidụnas-sā`iḥụnar-rāki'ụnas-sājidụnal-āmirụna bil-ma'rụfi wan-nāhụna 'anil-mungkari wal-ḥāfiẓụna liḥudụdillāh, wa basysyiril-mu`minīn","at":"Mereka itu adalah orang-orang yang bertobat, beribadah, memuji (Allah), mengembara (demi ilmu dan agama), rukuk, sujud, menyuruh berbuat makruf dan mencegah dari yang mungkar dan yang memelihara hukum-hukum Allah. Dan gembirakanlah orang-orang yang beriman."},{"id":"113","ar":"مَا كَانَ لِلنَّبِيِّ وَالَّذِيْنَ اٰمَنُوْٓا اَنْ يَّسْتَغْفِرُوْا لِلْمُشْرِكِيْنَ وَلَوْ كَانُوْٓا اُولِيْ قُرْبٰى مِنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمْ اَنَّهُمْ اَصْحٰبُ الْجَحِيْمِ ","bc":"mā kāna lin-nabiyyi wallażīna āmanū ay yastagfirụ lil-musyrikīna walau kānū ulī qurbā mim ba'di mā tabayyana lahum annahum aṣ-ḥābul-jaḥīm","at":"Tidak pantas bagi Nabi dan orang-orang yang beriman memohonkan ampunan (kepada Allah) bagi orang-orang musyrik, sekalipun orang-orang itu kaum kerabat(nya), setelah jelas bagi mereka, bahwa orang-orang musyrik itu penghuni neraka Jahanam."},{"id":"114","ar":"وَمَا كَانَ اسْتِغْفَارُ اِبْرٰهِيْمَ لِاَبِيْهِ اِلَّا عَنْ مَّوْعِدَةٍ وَّعَدَهَآ اِيَّاهُۚ فَلَمَّا تَبَيَّنَ لَهٗٓ اَنَّهٗ عَدُوٌّ لِّلّٰهِ تَبَرَّاَ مِنْهُۗ اِنَّ اِبْرٰهِيْمَ لَاَوَّاهٌ حَلِيْمٌ ","bc":"wa mā kānastigfāru ibrāhīma li`abīhi illā 'am mau'idatiw wa'adahā iyyāh, fa lammā tabayyana lahū annahụ 'aduwwul lillāhi tabarra`a min-h, inna ibrāhīma la`awwāhun ḥalīm","at":"Adapun permohonan ampunan Ibrahim (kepada Allah) untuk bapaknya, tidak lain hanyalah karena suatu janji yang telah diikrarkannya kepada bapaknya. Maka ketika jelas bagi Ibrahim bahwa bapaknya adalah musuh Allah, maka Ibrahim berlepas diri darinya. Sungguh, Ibrahim itu seorang yang sangat lembut hatinya lagi penyantun."},{"id":"115","ar":"وَمَا كَانَ اللّٰهُ لِيُضِلَّ قَوْمًاۢ بَعْدَ اِذْ هَدٰىهُمْ حَتّٰى يُبَيِّنَ لَهُمْ مَّا يَتَّقُوْنَۗ اِنَّ اللّٰهَ بِكُلِّ شَيْءٍ عَلِيْمٌ ","bc":"wa mā kānallāhu liyuḍilla qaumam ba'da iż hadāhum ḥattā yubayyina lahum mā yattaqụn, innallāha bikulli syai`in 'alīm","at":"Dan Allah sekali-kali tidak akan menyesatkan suatu kaum, setelah mereka diberi-Nya petunjuk, sehingga dapat dijelaskan kepada mereka apa yang harus mereka jauhi. Sungguh, Allah Maha Mengetahui segala sesuatu."},{"id":"116","ar":"اِنَّ اللّٰهَ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ يُحْيٖ وَيُمِيْتُۗ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ وَّلِيٍّ وَّلَا نَصِيْرٍ ","bc":"innallāha lahụ mulkus-samāwāti wal-arḍ, yuḥyī wa yumīt, wa mā lakum min dụnillāhi miw waliyyiw wa lā naṣīr","at":"Sesungguhnya Allah memiliki kekuasaan langit dan bumi. Dia menghidupkan dan mematikan. Tidak ada pelindung dan penolong bagimu selain Allah."},{"id":"117","ar":"لَقَدْ تَّابَ اللّٰهُ عَلَى النَّبِيِّ وَالْمُهٰجِرِيْنَ وَالْاَنْصَارِ الَّذِيْنَ اتَّبَعُوْهُ فِيْ سَاعَةِ الْعُسْرَةِ مِنْۢ بَعْدِ مَا كَادَ يَزِيْغُ قُلُوْبُ فَرِيْقٍ مِّنْهُمْ ثُمَّ تَابَ عَلَيْهِمْۗ اِنَّهٗ بِهِمْ رَءُوْفٌ رَّحِيْمٌ ۙ","bc":"laqat tāballāhu 'alan-nabiyyi wal-muhājirīna wal-anṣārillażīnattaba'ụhu fī sā'atil-'usrati mim ba'di mā kāda yazīgu qulụbu farīqim min-hum ṡumma tāba 'alaihim, innahụ bihim ra`ụfur raḥīm","at":"Sungguh, Allah telah menerima tobat Nabi, orang-orang Muhajirin dan orang-orang Ansar, yang mengikuti Nabi pada masa-masa sulit, setelah hati segolongan dari mereka hampir berpaling, kemudian Allah menerima tobat mereka. Sesungguhnya Allah Maha Pengasih, Maha Penyayang kepada mereka,"},{"id":"118","ar":"وَّعَلَى الثَّلٰثَةِ الَّذِيْنَ خُلِّفُوْاۗ حَتّٰٓى اِذَا ضَاقَتْ عَلَيْهِمُ الْاَرْضُ بِمَا رَحُبَتْ وَضَاقَتْ عَلَيْهِمْ اَنْفُسُهُمْ وَظَنُّوْٓا اَنْ لَّا مَلْجَاَ مِنَ اللّٰهِ اِلَّآ اِلَيْهِۗ ثُمَّ تَابَ عَلَيْهِمْ لِيَتُوْبُوْاۗ اِنَّ اللّٰهَ هُوَ التَّوَّابُ الرَّحِيْمُ ","bc":"wa 'alaṡ-ṡalāṡatillażīna khullifụ, ḥattā iżā ḍāqat 'alaihimul-arḍu bimā raḥubat wa ḍāqat 'alaihim anfusuhum wa ẓannū al lā malja`a minallāhi illā ilaīh, ṡumma tāba 'alaihim liyatụbụ, innallāha huwat-tawwābur-raḥīm","at":"dan terhadap tiga orang yang ditinggalkan. Hingga ketika bumi terasa sempit bagi mereka, padahal bumi itu luas dan jiwa mereka pun telah (pula terasa) sempit bagi mereka, serta mereka telah mengetahui bahwa tidak ada tempat lari dari (siksaan) Allah, melainkan kepada-Nya saja, kemudian Allah menerima tobat mereka agar mereka tetap dalam tobatnya. Sesungguhnya Allah Maha Penerima tobat, Maha Penyayang."},{"id":"119","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوا اتَّقُوا اللّٰهَ وَكُوْنُوْا مَعَ الصّٰدِقِيْنَ ","bc":"yā ayyuhallażīna āmanuttaqullāha wa kụnụ ma'aṣ-ṣādiqīn","at":"Wahai orang-orang yang beriman! Bertakwalah kepada Allah, dan bersamalah kamu dengan orang-orang yang benar."},{"id":"120","ar":"مَا كَانَ لِاَهْلِ الْمَدِيْنَةِ وَمَنْ حَوْلَهُمْ مِّنَ الْاَعْرَابِ اَنْ يَّتَخَلَّفُوْا عَنْ رَّسُوْلِ اللّٰهِ وَلَا يَرْغَبُوْا بِاَنْفُسِهِمْ عَنْ نَّفْسِهٖۗ ذٰلِكَ بِاَنَّهُمْ لَا يُصِيْبُهُمْ ظَمَاٌ وَّلَا نَصَبٌ وَّلَا مَخْمَصَةٌ فِيْ سَبِيْلِ اللّٰهِ وَلَا يَطَـُٔوْنَ مَوْطِئًا يَّغِيْظُ الْكُفَّارَ وَلَا يَنَالُوْنَ مِنْ عَدُوٍّ نَّيْلًا اِلَّا كُتِبَ لَهُمْ بِهٖ عَمَلٌ صَالِحٌۗ اِنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ ","bc":"mā kāna li`ahlil-madīnati wa man ḥaulahum minal-a'rābi ay yatakhallafụ 'ar rasụlillāhi wa lā yargabụ bi`anfusihim 'an nafsih, żālika bi`annahum lā yuṣībuhum ẓama`uw wa lā naṣabuw wa lā makhmaṣatun fī sabīlillāhi wa lā yaṭa`ụna mauṭi`ay yagīẓul-kuffāra wa lā yanalụna min 'aduwwin nailan illā kutiba lahum bihī 'amalun ṣāliḥ, innallāha lā yuḍī'u ajral-muḥsinīn","at":"Tidak pantas bagi penduduk Madinah dan orang-orang Arab Badui yang berdiam di sekitar mereka, tidak turut menyertai Rasulullah (pergi berperang) dan tidak pantas (pula) bagi mereka lebih mencintai diri mereka daripada (mencintai) diri Rasul. Yang demikian itu karena mereka tidak ditimpa kehausan, kepayahan dan kelaparan di jalan Allah, dan tidak (pula) menginjak suatu tempat yang membangkitkan amarah orang-orang kafir, dan tidak menimpakan suatu bencana kepada musuh, kecuali (semua) itu akan dituliskan bagi mereka sebagai suatu amal kebajikan. Sungguh, Allah tidak menyia-nyiakan pahala orang-orang yang berbuat baik,"},{"id":"121","ar":"وَلَا يُنْفِقُوْنَ نَفَقَةً صَغِيْرَةً وَّلَا كَبِيْرَةً وَّلَا يَقْطَعُوْنَ وَادِيًا اِلَّا كُتِبَ لَهُمْ لِيَجْزِيَهُمُ اللّٰهُ اَحْسَنَ مَا كَانُوْا يَعْمَلُوْنَ ","bc":"wa lā yunfiqụna nafaqatan ṣagīrataw wa lā kabīrataw wa lā yaqṭa'ụna wādiyan illā kutiba lahum liyajziyahumullāhu aḥsana mā kānụ ya'malụn","at":"dan tidaklah mereka memberikan infak, baik yang kecil maupun yang besar dan tidak (pula) melintasi suatu lembah (berjihad), kecuali akan dituliskan bagi mereka (sebagai amal kebajikan), untuk diberi balasan oleh Allah (dengan) yang lebih baik daripada apa yang telah mereka kerjakan."},{"id":"122","ar":"۞ وَمَا كَانَ الْمُؤْمِنُوْنَ لِيَنْفِرُوْا كَاۤفَّةًۗ فَلَوْلَا نَفَرَ مِنْ كُلِّ فِرْقَةٍ مِّنْهُمْ طَاۤىِٕفَةٌ لِّيَتَفَقَّهُوْا فِى الدِّيْنِ وَلِيُنْذِرُوْا قَوْمَهُمْ اِذَا رَجَعُوْٓا اِلَيْهِمْ لَعَلَّهُمْ يَحْذَرُوْنَ ","bc":"wa mā kānal-mu`minụna liyanfirụ kāffah, falau lā nafara ming kulli firqatim min-hum ṭā`ifatul liyatafaqqahụ fid-dīni wa liyunżirụ qaumahum iżā raja'ū ilaihim la'allahum yaḥżarụn","at":"Dan tidak sepatutnya orang-orang mukmin itu semuanya pergi (ke medan perang). Mengapa sebagian dari setiap golongan di antara mereka tidak pergi untuk memperdalam pengetahuan agama mereka dan untuk memberi peringatan kepada kaumnya apabila mereka telah kembali, agar mereka dapat menjaga dirinya."},{"id":"123","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْا قَاتِلُوا الَّذِيْنَ يَلُوْنَكُمْ مِّنَ الْكُفَّارِ وَلْيَجِدُوْا فِيْكُمْ غِلْظَةًۗ وَاعْلَمُوْٓا اَنَّ اللّٰهَ مَعَ الْمُتَّقِيْنَ ","bc":"yā ayyuhallażīna āmanụ qātilullażīna yalụnakum minal-kuffāri walyajidụ fīkum gilẓah, wa'lamū annallāha ma'al-muttaqīn","at":"Wahai orang yang beriman! Perangilah orang-orang kafir yang di sekitar kamu, dan hendaklah mereka merasakan sikap tegas darimu, dan ketahuilah bahwa Allah beserta orang yang bertakwa."},{"id":"124","ar":"وَاِذَا مَآ اُنْزِلَتْ سُوْرَةٌ فَمِنْهُمْ مَّنْ يَّقُوْلُ اَيُّكُمْ زَادَتْهُ هٰذِهٖٓ اِيْمَانًاۚ فَاَمَّا الَّذِيْنَ اٰمَنُوْا فَزَادَتْهُمْ اِيْمَانًا وَّهُمْ يَسْتَبْشِرُوْنَ","bc":"wa iżā mā unzilat sụratun fa min-hum may yaqụlu ayyukum zādat-hu hāżihī īmānā, fa ammallażīna āmanụ fa zādat-hum īmānaw wa hum yastabsyirụn","at":"Dan apabila diturunkan suatu surah, maka di antara mereka (orang-orang munafik) ada yang berkata, “Siapakah di antara kamu yang bertambah imannya dengan (turunnya) surah ini?” Adapun orang-orang yang beriman, maka surah ini menambah imannya, dan mereka merasa gembira."},{"id":"125","ar":"وَاَمَّا الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ فَزَادَتْهُمْ رِجْسًا اِلٰى رِجْسِهِمْ وَمَاتُوْا وَهُمْ كٰفِرُوْنَ ","bc":"wa ammallażīna fī qulụbihim maraḍun fa zādat-hum rijsan ilā rijsihim wa mātụ wa hum kāfirụn","at":"Dan adapun orang-orang yang di dalam hatinya ada penyakit, maka (dengan surah itu) akan menambah kekafiran mereka yang telah ada dan mereka akan mati dalam keadaan kafir."},{"id":"126","ar":"اَوَلَا يَرَوْنَ اَنَّهُمْ يُفْتَنُوْنَ فِيْ كُلِّ عَامٍ مَّرَّةً اَوْ مَرَّتَيْنِ ثُمَّ لَا يَتُوْبُوْنَ وَلَا هُمْ يَذَّكَّرُوْنَ ","bc":"a wa lā yarauna annahum yuftanụna fī kulli 'āmim marratan au marrataini ṡumma lā yatụbụna wa lā hum yażżakkarụn","at":"Dan tidakkah mereka (orang-orang munafik) memperhatikan bahwa mereka diuji sekali atau dua kali setiap tahun, namun mereka tidak (juga) bertobat dan tidak (pula) mengambil pelajaran?"},{"id":"127","ar":"وَاِذَا مَآ اُنْزِلَتْ سُوْرَةٌ نَّظَرَ بَعْضُهُمْ اِلٰى بَعْضٍۗ هَلْ يَرٰىكُمْ مِّنْ اَحَدٍ ثُمَّ انْصَرَفُوْاۗ صَرَفَ اللّٰهُ قُلُوْبَهُمْ بِاَنَّهُمْ قَوْمٌ لَّا يَفْقَهُوْنَ ","bc":"wa iżā mā unzilat sụratun naẓara ba'ḍuhum ilā ba'ḍ, hal yarākum min aḥadin ṡummanṣarafụ, ṣarafallāhu qulụbahum bi`annahum qaumul lā yafqahụn","at":"Dan apabila diturunkan suatu surah, satu sama lain di antara mereka saling berpandangan (sambil berkata), “Adakah seseorang (dari kaum muslimin) yang melihat kamu?” Setelah itu mereka pun pergi. Allah memalingkan hati mereka disebabkan mereka adalah kaum yang tidak memahami."},{"id":"128","ar":"لَقَدْ جَاۤءَكُمْ رَسُوْلٌ مِّنْ اَنْفُسِكُمْ عَزِيْزٌ عَلَيْهِ مَا عَنِتُّمْ حَرِيْصٌ عَلَيْكُمْ بِالْمُؤْمِنِيْنَ رَءُوْفٌ رَّحِيْمٌ ","bc":"laqad jā`akum rasụlum min anfusikum 'azīzun 'alaihi mā 'anittum ḥarīṣun 'alaikum bil-mu`minīna ra`ụfur raḥīm","at":"Sungguh, telah datang kepadamu seorang rasul dari kaummu sendiri, berat terasa olehnya penderitaan yang kamu alami, (dia) sangat menginginkan (keimanan dan keselamatan) bagimu, penyantun dan penyayang terhadap orang-orang yang beriman."},{"id":"129","ar":"فَاِنْ تَوَلَّوْا فَقُلْ حَسْبِيَ اللّٰهُ لَآ اِلٰهَ اِلَّا هُوَ ۗ عَلَيْهِ تَوَكَّلْتُ وَهُوَ رَبُّ الْعَرْشِ الْعَظِيْمِ","bc":"fa in tawallau fa qul ḥasbiyallāhu lā ilāha illā huw, 'alaihi tawakkaltu wa huwa rabbul-'arsyil-'aẓīm","at":"Maka jika mereka berpaling (dari keimanan), maka katakanlah (Muhammad), “Cukuplah Allah bagiku; tidak ada tuhan selain Dia. Hanya kepada-Nya aku bertawakal, dan Dia adalah Tuhan yang memiliki ‘Arsy (singgasana) yang agung.” "}],at_tin=[{"id":"1","ar":"وَالتِّيْنِ وَالزَّيْتُوْنِۙ","bc":"wat-tīni waz-zaitụn","at":"Demi (buah) Tin dan (buah) Zaitun,"},{"id":"2","ar":"وَطُوْرِ سِيْنِيْنَۙ","bc":"wa ṭụri sīnīn","at":"demi gunung Sinai,"},{"id":"3","ar":"وَهٰذَا الْبَلَدِ الْاَمِيْنِۙ","bc":"wa hāżal-baladil-amīn","at":"dan demi negeri (Mekah) yang aman ini."},{"id":"4","ar":"لَقَدْ خَلَقْنَا الْاِنْسَانَ فِيْٓ اَحْسَنِ تَقْوِيْمٍۖ","bc":"laqad khalaqnal-insāna fī aḥsani taqwīm","at":"Sungguh, Kami telah menciptakan manusia dalam bentuk yang sebaik-baiknya,"},{"id":"5","ar":"ثُمَّ رَدَدْنٰهُ اَسْفَلَ سَافِلِيْنَۙ","bc":"ṡumma radadnāhu asfala sāfilīn","at":"kemudian Kami kembalikan dia ke tempat yang serendah-rendahnya,"},{"id":"6","ar":"اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ فَلَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍۗ","bc":"illallażīna āmanụ wa 'amiluṣ-ṣāliḥāti fa lahum ajrun gairu mamnụn","at":"kecuali orang-orang yang beriman dan mengerjakan kebajikan; maka mereka akan mendapat pahala yang tidak ada putus-putusnya."},{"id":"7","ar":"فَمَا يُكَذِّبُكَ بَعْدُ بِالدِّيْنِۗ","bc":"fa mā yukażżibuka ba'du bid-dīn","at":"Maka apa yang menyebabkan (mereka) mendustakanmu (tentang) hari pembalasan setelah (adanya keterangan-keterangan) itu?"},{"id":"8","ar":"اَلَيْسَ اللّٰهُ بِاَحْكَمِ الْحٰكِمِيْنَ","bc":"a laisallāhu bi`aḥkamil-ḥākimīn","at":"Bukankah Allah hakim yang paling adil?"}],at_tur=[{"id":"1","ar":"وَالطُّوْرِۙ ","bc":"waṭ-ṭụr","at":"Demi gunung (Sinai),"},{"id":"2","ar":"وَكِتٰبٍ مَّسْطُوْرٍۙ ","bc":"wa kitābim masṭụr","at":"dan demi Kitab yang ditulis, "},{"id":"3","ar":"فِيْ رَقٍّ مَّنْشُوْرٍۙ ","bc":"fī raqqim mansyụr","at":"pada lembaran yang terbuka,"},{"id":"4","ar":"وَّالْبَيْتِ الْمَعْمُوْرِۙ ","bc":"wal-baitil-ma'mụr","at":"demi Baitulma‘mur (Ka‘bah),"},{"id":"5","ar":"وَالسَّقْفِ الْمَرْفُوْعِۙ ","bc":"was-saqfil-marfụ'","at":"demi atap yang ditinggikan (langit),"},{"id":"6","ar":"وَالْبَحْرِ الْمَسْجُوْرِۙ ","bc":"wal-baḥril-masjụr","at":"demi lautan yang penuh gelombang, "},{"id":"7","ar":"اِنَّ عَذَابَ رَبِّكَ لَوَاقِعٌۙ ","bc":"inna 'ażāba rabbika lawāqi'","at":"sungguh, azab Tuhanmu pasti terjadi, "},{"id":"8","ar":"مَّا لَهٗ مِنْ دَافِعٍۙ ","bc":"mā lahụ min dāfi'","at":"tidak sesuatu pun yang dapat menolaknya, "},{"id":"9","ar":"يَّوْمَ تَمُوْرُ السَّمَاۤءُ مَوْرًاۙ ","bc":"yauma tamụrus-samā`u maurā","at":"pada hari (ketika) langit berguncang sekeras-kerasnya,"},{"id":"10","ar":"وَّتَسِيْرُ الْجِبَالُ سَيْرًاۗ ","bc":"wa tasīrul-jibālu sairā","at":"dan gunung berjalan (berpindah-pindah)."},{"id":"11","ar":"فَوَيْلٌ يَّوْمَىِٕذٍ لِّلْمُكَذِّبِيْنَۙ ","bc":"fa wailuy yauma`iżil lil-mukażżibīn","at":"Maka celakalah pada hari itu bagi orang-orang yang mendustakan."},{"id":"12","ar":"الَّذِيْنَ هُمْ فِيْ خَوْضٍ يَّلْعَبُوْنَۘ ","bc":"allażīna hum fī khauḍiy yal'abụn","at":"Orang-orang yang bermain-main dalam kebatilan (perbuatan dosa),"},{"id":"13","ar":"يَوْمَ يُدَعُّوْنَ اِلٰى نَارِ جَهَنَّمَ دَعًّاۗ","bc":"yauma yuda''ụna ilā nāri jahannama da''ā","at":"pada hari (ketika) itu mereka didorong ke neraka Jahanam dengan sekuat-kuatnya."},{"id":"14","ar":"هٰذِهِ النَّارُ الَّتِيْ كُنْتُمْ بِهَا تُكَذِّبُوْنَ ","bc":"hāżihin-nārullatī kuntum bihā tukażżibụn","at":"(Dikatakan kepada mereka), “Inilah neraka yang dahulu kamu mendustakannya.” "},{"id":"15","ar":"اَفَسِحْرٌ هٰذَآ اَمْ اَنْتُمْ لَا تُبْصِرُوْنَ ","bc":"a fa siḥrun hāżā am antum lā tubṣirụn","at":"Maka apakah ini sihir? Ataukah kamu tidak melihat?"},{"id":"16","ar":"اِصْلَوْهَا فَاصْبِرُوْٓا اَوْ لَا تَصْبِرُوْاۚ سَوَاۤءٌ عَلَيْكُمْۗ اِنَّمَا تُجْزَوْنَ مَا كُنْتُمْ تَعْمَلُوْنَ ","bc":"iṣlauhā faṣbirū au lā taṣbirụ, sawā`un 'alaikum, innamā tujzauna mā kuntum ta'malụn","at":"Masuklah ke dalamnya (rasakanlah panas apinya); baik kamu bersabar atau tidak, sama saja bagimu; sesungguhnya kamu hanya diberi balasan atas apa yang telah kamu kerjakan. "},{"id":"17","ar":"اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّنَعِيْمٍۙ ","bc":"innal-muttaqīna fī jannātiw wa na'īm","at":"Sesungguhnya orang-orang yang bertakwa berada dalam surga dan kenikmatan,"},{"id":"18","ar":"فَاكِهِيْنَ بِمَآ اٰتٰىهُمْ رَبُّهُمْۚ وَوَقٰىهُمْ رَبُّهُمْ عَذَابَ الْجَحِيْمِ","bc":"fākihīna bimā ātāhum rabbuhum, wa waqāhum rabbuhum 'ażābal-jaḥīm","at":"mereka bersuka ria dengan apa yang diberikan Tuhan kepada mereka; dan Tuhan memelihara mereka dari azab neraka."},{"id":"19","ar":"كُلُوْا وَاشْرَبُوْا هَنِيْۤئًا ۢبِمَا كُنْتُمْ تَعْمَلُوْنَۙ ","bc":"kulụ wasyrabụ hanī`am bimā kuntum ta'malụn","at":"(Dikatakan kepada mereka), “Makan dan minumlah dengan rasa nikmat sebagai balasan dari apa yang telah kamu kerjakan.” "},{"id":"20","ar":"مُتَّكِـِٕيْنَ عَلٰى سُرُرٍ مَّصْفُوْفَةٍۚ وَزَوَّجْنٰهُمْ بِحُوْرٍ عِيْنٍ","bc":"muttaki`īna 'alā sururim maṣfụfah, wa zawwajnāhum biḥụrin 'īn","at":"Mereka bersandar di atas dipan-dipan yang tersusun dan Kami berikan kepada mereka pasangan bidadari yang bermata indah. "},{"id":"21","ar":"وَالَّذِيْنَ اٰمَنُوْا وَاتَّبَعَتْهُمْ ذُرِّيَّتُهُمْ بِاِيْمَانٍ اَلْحَقْنَا بِهِمْ ذُرِّيَّتَهُمْ وَمَآ اَلَتْنٰهُمْ مِّنْ عَمَلِهِمْ مِّنْ شَيْءٍۚ كُلُّ امْرِئٍ ۢبِمَا كَسَبَ رَهِيْنٌ ","bc":"wallażīna āmanụ wattaba'at-hum żurriyyatuhum bi`īmānin alḥaqnā bihim żurriyyatahum wa mā alatnāhum min 'amalihim min syaī`, kullumri`im bimā kasaba rahīn","at":"Dan orang-orang yang beriman, beserta anak cucu mereka yang mengikuti mereka dalam keimanan, Kami pertemukan mereka dengan anak cucu mereka (di dalam surga), dan Kami tidak mengurangi sedikit pun pahala amal (kebajikan) mereka. Setiap orang terikat dengan apa yang dikerjakannya. "},{"id":"22","ar":"وَاَمْدَدْنٰهُمْ بِفَاكِهَةٍ وَّلَحْمٍ مِّمَّا يَشْتَهُوْنَ ","bc":"wa amdadnāhum bifākihatiw wa laḥmim mimmā yasytahụn","at":"Dan Kami berikan kepada mereka tambahan berupa buah-buahan dan daging dari segala jenis yang mereka ingini. "},{"id":"23","ar":"يَتَنَازَعُوْنَ فِيْهَا كَأْسًا لَّا لَغْوٌ فِيْهَا وَلَا تَأْثِيْمٌ ","bc":"yatanāza'ụna fīhā ka`sal lā lagwun fīhā wa lā ta`ṡīm","at":"(Di dalam surga itu) mereka saling mengulurkan gelas yang isinya tidak (menimbulkan) ucapan yang tidak berfaedah ataupun perbuatan dosa. "},{"id":"24","ar":"وَيَطُوْفُ عَلَيْهِمْ غِلْمَانٌ لَّهُمْ كَاَنَّهُمْ لُؤْلُؤٌ مَّكْنُوْنٌۚ ","bc":"wa yaṭụfu 'alaihim gilmānul lahum ka`annahum lu`lu`um maknụn","at":"Dan di sekitar mereka ada anak-anak muda yang berkeliling untuk (melayani) mereka, seakan-akan mereka itu mutiara yang tersimpan. "},{"id":"25","ar":"وَاَقْبَلَ بَعْضُهُمْ عَلٰى بَعْضٍ يَّتَسَاۤءَلُوْنَ ","bc":"wa aqbala ba'ḍuhum 'alā ba'ḍiy yatasā`alụn","at":"Dan sebagian mereka berhadap-hadapan satu sama lain saling bertegur sapa. "},{"id":"26","ar":"قَالُوْٓا اِنَّا كُنَّا قَبْلُ فِيْٓ اَهْلِنَا مُشْفِقِيْنَ ","bc":"qālū innā kunnā qablu fī ahlinā musyfiqīn","at":"Mereka berkata, “Sesungguhnya kami dahulu, sewaktu berada di tengah-tengah keluarga kami merasa takut (akan diazab). "},{"id":"27","ar":"فَمَنَّ اللّٰهُ عَلَيْنَا وَوَقٰىنَا عَذَابَ السَّمُوْمِ ","bc":"fa mannallāhu 'alainā wa waqānā 'ażābas-samụm","at":"Maka Allah memberikan karunia kepada kami dan memelihara kami dari azab neraka. "},{"id":"28","ar":"اِنَّا كُنَّا مِنْ قَبْلُ نَدْعُوْهُۗ اِنَّهٗ هُوَ الْبَرُّ الرَّحِيْمُ ","bc":"innā kunnā ming qablu nad'ụh, innahụ huwal-barrur-raḥīm","at":"Sesungguhnya kami menyembah-Nya sejak dahulu. Dialah Yang Maha Melimpahkan Kebaikan, Maha Penyayang.” "},{"id":"29","ar":"فَذَكِّرْ فَمَآ اَنْتَ بِنِعْمَتِ رَبِّكَ بِكَاهِنٍ وَّلَا مَجْنُوْنٍۗ ","bc":"fa żakkir fa mā anta bini'mati rabbika bikāhiniw wa lā majnụn","at":"Maka peringatkanlah, karena dengan nikmat Tuhanmu engkau (Muhammad) bukanlah seorang tukang tenung dan bukan pula orang gila."},{"id":"30","ar":"اَمْ يَقُوْلُوْنَ شَاعِرٌ نَّتَرَبَّصُ بِهٖ رَيْبَ الْمَنُوْنِ ","bc":"am yaqụlụna syā'irun natarabbaṣu bihī raibal-manụn","at":"Bahkan mereka berkata, “Dia adalah seorang penyair yang kami tunggu-tunggu kecelakaan menimpanya.” "},{"id":"31","ar":"قُلْ تَرَبَّصُوْا فَاِنِّيْ مَعَكُمْ مِّنَ الْمُتَرَبِّصِيْنَۗ ","bc":"qul tarabbaṣụ fa innī ma'akum minal-mutarabbiṣīn","at":"Katakanlah (Muhammad), “Tunggulah! Sesungguhnya aku pun termasuk orang yang sedang menunggu bersama kamu.” "},{"id":"32","ar":"اَمْ تَأْمُرُهُمْ اَحْلَامُهُمْ بِهٰذَآ اَمْ هُمْ قَوْمٌ طَاغُوْنَۚ ","bc":"am ta`muruhum aḥlāmuhum bihāżā am hum qaumun ṭāgụn","at":"Apakah mereka diperintah oleh pikiran-pikiran mereka untuk mengucapkan (tuduhan-tuduhan) ini ataukah mereka kaum yang melampaui batas?"},{"id":"33","ar":"اَمْ يَقُوْلُوْنَ تَقَوَّلَهٗۚ بَلْ لَّا يُؤْمِنُوْنَۚ ","bc":"am yaqụlụna taqawwalah, bal lā yu`minụn","at":"Ataukah mereka berkata, “Dia (Muhammad) mereka-rekanya.” Tidak! Merekalah yang tidak beriman. "},{"id":"34","ar":"فَلْيَأْتُوْا بِحَدِيْثٍ مِّثْلِهٖٓ اِنْ كَانُوْا صٰدِقِيْنَۗ ","bc":"falya`tụ biḥadīṡim miṡlihī ing kānụ ṣādiqīn","at":"Maka cobalah mereka membuat yang semisal dengannya (Al-Qur'an) jika mereka orang-orang yang benar. "},{"id":"35","ar":"اَمْ خُلِقُوْا مِنْ غَيْرِ شَيْءٍ اَمْ هُمُ الْخَالِقُوْنَۗ ","bc":"am khuliqụ min gairi syai`in am humul-khāliqụn","at":"Atau apakah mereka tercipta tanpa asal-usul ataukah mereka yang menciptakan (diri mereka sendiri)?"},{"id":"36","ar":"اَمْ خَلَقُوا السَّمٰوٰتِ وَالْاَرْضَۚ بَلْ لَّا يُوْقِنُوْنَۗ ","bc":"am khalaqus-samāwāti wal-arḍ, bal lā yụqinụn","at":"Ataukah mereka telah menciptakan langit dan bumi? Sebenarnya mereka tidak meyakini (apa yang mereka katakan)."},{"id":"37","ar":"اَمْ عِنْدَهُمْ خَزَاۤىِٕنُ رَبِّكَ اَمْ هُمُ الْمُصَۣيْطِرُوْنَۗ ","bc":"am 'indahum khazā`inu rabbika am humul-muṣaiṭirụn","at":"Ataukah di sisi mereka ada perbendaharaan Tuhanmu ataukah mereka yang berkuasa?"},{"id":"38","ar":"اَمْ لَهُمْ سُلَّمٌ يَّسْتَمِعُوْنَ فِيْهِۚ فَلْيَأْتِ مُسْتَمِعُهُمْ بِسُلْطٰنٍ مُّبِيْنٍۗ ","bc":"am lahum sullamuy yastami'ụna fīh, falya`ti mustami'uhum bisulṭānim mubīn","at":"Atau apakah mereka mempunyai tangga (ke langit) untuk mendengarkan (hal-hal yang gaib)? Maka hendaklah orang yang mendengarkan di antara mereka itu datang membawa keterangan yang nyata. "},{"id":"39","ar":"اَمْ لَهُ الْبَنٰتُ وَلَكُمُ الْبَنُوْنَۗ ","bc":"am lahul-banātu wa lakumul-banụn","at":"Ataukah (pantas) untuk Dia anak-anak perempuan sedangkan untuk kamu anak-anak laki-laki?"},{"id":"40","ar":"اَمْ تَسْـَٔلُهُمْ اَجْرًا فَهُمْ مِّنْ مَّغْرَمٍ مُّثْقَلُوْنَۗ ","bc":"am tas`aluhum ajran fa hum mim magramim muṡqalụn","at":"Ataukah engkau (Muhamamd) meminta imbalan kepada mereka sehingga mereka dibebani dengan utang?"},{"id":"41","ar":"اَمْ عِنْدَهُمُ الْغَيْبُ فَهُمْ يَكْتُبُوْنَۗ ","bc":"am 'indahumul-gaibu fa hum yaktubụn","at":"Ataukah di sisi mereka mempunyai (pengetahuan) tentang yang gaib lalu mereka menuliskannya?"},{"id":"42","ar":"اَمْ يُرِيْدُوْنَ كَيْدًاۗ فَالَّذِيْنَ كَفَرُوْا هُمُ الْمَكِيْدُوْنَۗ ","bc":"am yurīdụna kaidā, fallażīna kafarụ humul-makīdụn","at":"Ataukah mereka hendak melakukan tipu daya? Tetapi orang-orang yang kafir itu, justru merekalah yang terkena tipu daya."},{"id":"43","ar":"اَمْ لَهُمْ اِلٰهٌ غَيْرُ اللّٰهِ ۗسُبْحٰنَ اللّٰهِ عَمَّا يُشْرِكُوْنَ ","bc":"am lahum ilāhun gairullāh, sub-ḥānallāhi 'ammā yusyrikụn","at":"Ataukah mereka mempunyai tuhan selain Allah? Mahasuci Allah dari apa yang mereka persekutukan."},{"id":"44","ar":"وَاِنْ يَّرَوْا كِسْفًا مِّنَ السَّمَاۤءِ سَاقِطًا يَّقُوْلُوْا سَحَابٌ مَّرْكُوْمٌ ","bc":"wa iy yarau kisfam minas-samā`i sāqiṭay yaqụlụ saḥābum markụm","at":"Dan jika mereka melihat gumpalan-gumpalan awan berjatuhan dari langit, mereka berkata, “Itu adalah awan yang bertumpuk-tumpuk.” "},{"id":"45","ar":"فَذَرْهُمْ حَتّٰى يُلٰقُوْا يَوْمَهُمُ الَّذِيْ فِيْهِ يُصْعَقُوْنَۙ ","bc":"fażar-hum ḥattā yulāqụ yaumahumullażī fīhi yuṣ'aqụn","at":"Maka biarkanlah mereka hingga mereka menemui hari (yang dijanjikan kepada) mereka, pada hari itu mereka dibinasakan, "},{"id":"46","ar":"يَوْمَ لَا يُغْنِيْ عَنْهُمْ كَيْدُهُمْ شَيْـًٔا وَّلَا هُمْ يُنْصَرُوْنَۗ ","bc":"yauma lā yugnī 'an-hum kaiduhum syai`aw wa lā hum yunṣarụn","at":"(yaitu) pada hari (ketika) tipu daya mereka tidak berguna sedikit pun bagi mereka dan mereka tidak akan diberi pertolongan."},{"id":"47","ar":"وَاِنَّ لِلَّذِيْنَ ظَلَمُوْا عَذَابًا دُوْنَ ذٰلِكَ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ ","bc":"wa inna lillażīna ẓalamụ 'ażāban dụna żālika wa lākinna akṡarahum lā ya'lamụn","at":"Dan sesungguhnya bagi orang-orang yang zalim masih ada azab selain itu. Tetapi kebanyakan mereka tidak mengetahui."},{"id":"48","ar":"وَاصْبِرْ لِحُكْمِ رَبِّكَ فَاِنَّكَ بِاَعْيُنِنَا وَسَبِّحْ بِحَمْدِ رَبِّكَ حِيْنَ تَقُوْمُۙ ","bc":"waṣbir liḥukmi rabbika fa innaka bi`a'yuninā wa sabbiḥ biḥamdi rabbika ḥīna taqụm","at":"Dan bersabarlah (Muhammad) menunggu ketetapan Tuhanmu, karena sesungguhnya engkau berada dalam pengawasan Kami, dan bertasbihlah dengan memuji Tuhanmu ketika engkau bangun, "},{"id":"49","ar":"وَمِنَ الَّيْلِ فَسَبِّحْهُ وَاِدْبَارَ النُّجُوْمِ","bc":"wa minal-laili fa sabbiḥ-hu wa idbāran-nujụm","at":"dan pada sebagian malam bertasbihlah kepada-Nya dan (juga) pada waktu terbenamnya bintang-bintang (pada waktu fajar)."}],az_zariyat=[{"id":"1","ar":" وَالذّٰرِيٰتِ ذَرْوًاۙ","bc":"waż-żāriyāti żarwā","at":"Demi (angin) yang menerbangkan debu, "},{"id":"2","ar":"فَالْحٰمِلٰتِ وِقْرًاۙ","bc":"fal-ḥāmilāti wiqrā","at":"dan awan yang mengandung (hujan),"},{"id":"3","ar":"فَالْجٰرِيٰتِ يُسْرًاۙ","bc":"fal-jāriyāti yusrā","at":"dan (kapal-kapal) yang berlayar dengan mudah, "},{"id":"4","ar":"فَالْمُقَسِّمٰتِ اَمْرًاۙ","bc":"fal-muqassimāti amrā","at":"dan (malaikat-malaikat) yang membagi-bagi urusan,"},{"id":"5","ar":"اِنَّمَا تُوْعَدُوْنَ لَصَادِقٌۙ","bc":"innamā tụ'adụna laṣādiq","at":"sungguh, apa yang dijanjikan kepadamu pasti benar, "},{"id":"6","ar":"وَّاِنَّ الدِّيْنَ لَوَاقِعٌۗ","bc":"wa innad-dīna lawāqi'","at":"dan sungguh, (hari) pembalasan pasti terjadi."},{"id":"7","ar":"وَالسَّمَاۤءِ ذَاتِ الْحُبُكِۙ","bc":"was-samā`i żātil-ḥubuk","at":"Demi langit yang mempunyai jalan-jalan,"},{"id":"8","ar":"اِنَّكُمْ لَفِيْ قَوْلٍ مُّخْتَلِفٍۙ","bc":"innakum lafī qaulim mukhtalif","at":"sungguh, kamu benar-benar dalam keadaan berbeda-beda pendapat,"},{"id":"9","ar":"يُّؤْفَكُ عَنْهُ مَنْ اُفِكَۗ","bc":"yu`faku 'an-hu man ufik","at":"dipalingkan darinya (Al-Qur'an dan Rasul) orang yang dipalingkan."},{"id":"10","ar":"قُتِلَ الْخَرَّاصُوْنَۙ","bc":"qutilal-kharrāṣụn","at":"Terkutuklah orang-orang yang banyak berdusta,"},{"id":"11","ar":"الَّذِيْنَ هُمْ فِيْ غَمْرَةٍ سَاهُوْنَۙ","bc":"allażīna hum fī gamratin sāhụn","at":"(yaitu) orang-orang yang terbenam dalam kebodohan dan kelalaian, "},{"id":"12","ar":"يَسْـَٔلُوْنَ اَيَّانَ يَوْمُ الدِّيْنِۗ","bc":"yas`alụna ayyāna yaumud-dīn","at":"mereka bertanya, “Kapankah hari pembalasan itu?”"},{"id":"13","ar":"يَوْمَ هُمْ عَلَى النَّارِ يُفْتَنُوْنَ ","bc":"yauma hum 'alan-nāri yuftanụn","at":"(Hari pembalasan itu ialah) pada hari (ketika) mereka diazab di dalam api neraka. "},{"id":"14","ar":"ذُوْقُوْا فِتْنَتَكُمْۗ هٰذَا الَّذِيْ كُنْتُمْ بِهٖ تَسْتَعْجِلُوْنَ ","bc":"żụqụ fitnatakum, hāżallażī kuntum bihī tasta'jilụn","at":"(Dikatakan kepada mereka), “Rasakanlah azabmu ini. Inilah azab yang dahulu kamu minta agar disegerakan.” "},{"id":"15","ar":"اِنَّ الْمُتَّقِيْنَ فِيْ جَنّٰتٍ وَّعُيُوْنٍۙ","bc":"innal-muttaqīna fī jannātiw wa 'uyụn","at":"Sesungguhnya orang-orang yang bertakwa berada di dalam taman-taman (surga) dan mata air,"},{"id":"16","ar":"اٰخِذِيْنَ مَآ اٰتٰىهُمْ رَبُّهُمْ ۗ اِنَّهُمْ كَانُوْا قَبْلَ ذٰلِكَ مُحْسِنِيْنَۗ","bc":"ākhiżīna mā ātāhum rabbuhum, innahum kānụ qabla żālika muḥsinīn","at":"mereka mengambil apa yang diberikan Tuhan kepada mereka. Sesungguhnya mereka sebelum itu (di dunia) adalah orang-orang yang berbuat baik;"},{"id":"17","ar":"كَانُوْا قَلِيْلًا مِّنَ الَّيْلِ مَا يَهْجَعُوْنَ ","bc":"kānụ qalīlam minal-laili mā yahja'ụn","at":"mereka sedikit sekali tidur pada waktu malam;"},{"id":"18","ar":"وَبِالْاَسْحَارِ هُمْ يَسْتَغْفِرُوْنَ ","bc":"wa bil-as-ḥāri hum yastagfirụn","at":"dan pada akhir malam mereka memohon ampunan (kepada Allah)."},{"id":"19","ar":"وَفِيْٓ اَمْوَالِهِمْ حَقٌّ لِّلسَّاۤىِٕلِ وَالْمَحْرُوْمِ ","bc":"wa fī amwālihim ḥaqqul lis-sā`ili wal-maḥrụm","at":"Dan pada harta benda mereka ada hak untuk orang miskin yang meminta dan orang miskin yang tidak meminta. "},{"id":"20","ar":"وَفِى الْاَرْضِ اٰيٰتٌ لِّلْمُوْقِنِيْنَۙ","bc":"wa fil-arḍi āyātul lil-mụqinīn","at":"Dan di bumi terdapat tanda-tanda (kebesaran Allah) bagi orang-orang yang yakin,"},{"id":"21","ar":"وَفِيْٓ اَنْفُسِكُمْ ۗ اَفَلَا تُبْصِرُوْنَ ","bc":"wa fī anfusikum, a fa lā tubṣirụn","at":"dan (juga) pada dirimu sendiri. Maka apakah kamu tidak memperhatikan?"},{"id":"22","ar":"وَفِى السَّمَاۤءِ رِزْقُكُمْ وَمَا تُوْعَدُوْنَ ","bc":"wa fis-samā`i rizqukum wa mā tụ'adụn","at":"Dan di langit terdapat (sebab-sebab) rezekimu dan apa yang dijanjikan kepadamu."},{"id":"23","ar":"فَوَرَبِّ السَّمَاۤءِ وَالْاَرْضِ اِنَّهٗ لَحَقٌّ مِّثْلَ مَآ اَنَّكُمْ تَنْطِقُوْنَ","bc":"fa wa rabbis-samā`i wal-arḍi innahụ laḥaqqum miṡla mā annakum tanṭiqụn","at":"Maka demi Tuhan langit dan bumi, sungguh, apa yang dijanjikan itu pasti terjadi seperti apa yang kamu ucapkan. "},{"id":"24","ar":"هَلْ اَتٰىكَ حَدِيْثُ ضَيْفِ اِبْرٰهِيْمَ الْمُكْرَمِيْنَۘ ","bc":"hal atāka ḥadīṡu ḍaifi ibrāhīmal-mukramīn","at":"Sudahkah sampai kepadamu (Muhammad) cerita tamu Ibrahim (malaikat-malaikat) yang dimuliakan?"},{"id":"25","ar":"اِذْ دَخَلُوْا عَلَيْهِ فَقَالُوْا سَلٰمًا ۗقَالَ سَلٰمٌۚ قَوْمٌ مُّنْكَرُوْنَ ","bc":"iż dakhalụ 'alaihi fa qālụ salāmā, qāla salām, qaumum mungkarụn","at":"(Ingatlah) ketika mereka masuk ke tempatnya lalu mengucapkan, “Salaman” (salam), Ibrahim menjawab, “Salamun” (salam). (Mereka itu) orang-orang yang belum dikenalnya. "},{"id":"26","ar":"فَرَاغَ اِلٰٓى اَهْلِهٖ فَجَاۤءَ بِعِجْلٍ سَمِيْنٍۙ","bc":"fa rāga ilā ahlihī fa jā`a bi'ijlin samīn","at":"Maka diam-diam dia (Ibrahim) pergi menemui keluarganya, kemudian dibawanya daging anak sapi gemuk (yang dibakar),"},{"id":"27","ar":"فَقَرَّبَهٗٓ اِلَيْهِمْۚ قَالَ اَلَا تَأْكُلُوْنَ ","bc":"fa qarrabahū ilaihim, qāla alā ta`kulụn","at":"lalu dihidangkannya kepada mereka (tetapi mereka tidak mau makan). Ibrahim berkata, “Mengapa tidak kamu makan.”"},{"id":"28","ar":"فَاَوْجَسَ مِنْهُمْ خِيْفَةً ۗقَالُوْا لَا تَخَفْۗ وَبَشَّرُوْهُ بِغُلٰمٍ عَلِيْمٍ ","bc":"fa aujasa min-hum khīfah, qālụ lā takhaf, wa basysyarụhu bigulāmin 'alīm","at":"Maka dia (Ibrahim) merasa takut terhadap mereka. Mereka berkata, “Janganlah kamu takut,” dan mereka memberi kabar gembira kepadanya dengan (kelahiran) seorang anak yang alim (Ishak). "},{"id":"29","ar":"فَاَقْبَلَتِ امْرَاَتُهٗ فِيْ صَرَّةٍ فَصَكَّتْ وَجْهَهَا وَقَالَتْ عَجُوْزٌ عَقِيْمٌ ","bc":"fa aqbalatimra`atuhụ fī ṣarratin fa ṣakkat waj-hahā wa qālat 'ajụzun 'aqīm","at":"Kemudian istrinya datang memekik (tercengang) lalu menepuk wajahnya sendiri seraya berkata, “(Aku ini) seorang perempuan tua yang mandul.” "},{"id":"30","ar":"قَالُوْا كَذٰلِكِۙ قَالَ رَبُّكِ ۗاِنَّهٗ هُوَ الْحَكِيْمُ الْعَلِيْمُ ۔","bc":"qālụ każāliki qāla rabbuk, innahụ huwal-ḥakīmul-'alīm","at":"Mereka berkata, “Demikianlah Tuhanmu berfirman. Sungguh, Dialah Yang Mahabijaksana, Maha Mengetahui.”"},{"id":"31","ar":"قَالَ فَمَا خَطْبُكُمْ اَيُّهَا الْمُرْسَلُوْنَۚ ","bc":"qāla fa mā khaṭbukum ayyuhal-mursalụn","at":"Dia (Ibrahim) berkata, “Apakah urusanmu yang penting wahai para utusan?”"},{"id":"32","ar":"قَالُوْٓ اِنَّآ اُرْسِلْنَآ اِلٰى قَوْمٍ مُّجْرِمِيْنَۙ ","bc":"qālū innā ursilnā ilā qaumim mujrimīn","at":"Mereka menjawab, “Sesungguhnya kami diutus kepada kaum yang berdosa (kaum Lut),"},{"id":"33","ar":"لِنُرْسِلَ عَلَيْهِمْ حِجَارَةً مِّنْ طِيْنٍۙ","bc":"linursila 'alaihim ḥijāratam min ṭīn","at":"agar Kami menimpa mereka dengan batu-batu dari tanah (yang keras),"},{"id":"34","ar":"مُّسَوَّمَةً عِنْدَ رَبِّكَ لِلْمُسْرِفِيْنَ ","bc":"musawwamatan 'inda rabbika lil-musrifīn","at":"yang ditandai dari Tuhanmu untuk (membinasakan) orang-orang yang melampaui batas.”"},{"id":"35","ar":"فَاَخْرَجْنَا مَنْ كَانَ فِيْهَا مِنَ الْمُؤْمِنِيْنَۚ ","bc":"fa akhrajnā mang kāna fīhā minal-mu`minīn","at":"Lalu Kami keluarkan orang-orang yang beriman yang berada di dalamnya (negeri kaum Lut) itu."},{"id":"36","ar":"فَمَا وَجَدْنَا فِيْهَا غَيْرَ بَيْتٍ مِّنَ الْمُسْلِمِيْنَۚ ","bc":"fa mā wajadnā fīhā gaira baitim minal-muslimīn","at":"Maka Kami tidak mendapati di dalamnya (negeri itu), kecuali sebuah rumah dari orang-orang Muslim (Lut)."},{"id":"37","ar":"وَتَرَكْنَا فِيْهَآ اٰيَةً لِّلَّذِيْنَ يَخَافُوْنَ الْعَذَابَ الْاَلِيْمَۗ ","bc":"wa taraknā fīhā āyatal lillażīna yakhāfụnal-'ażābal-alīm","at":"Dan Kami tinggalkan padanya (negeri itu) suatu tanda bagi orang-orang yang takut kepada azab yang pedih."},{"id":"38","ar":"وَفِيْ مُوْسٰىٓ اِذْ اَرْسَلْنٰهُ اِلٰى فِرْعَوْنَ بِسُلْطٰنٍ مُّبِيْنٍ ","bc":"wa fī mụsā iż arsalnāhu ilā fir'auna bisulṭānim mubīn","at":"Dan pada Musa (terdapat tanda-tanda kekuasaan Allah) ketika Kami mengutusnya kepada Fir‘aun dengan membawa mukjizat yang nyata. "},{"id":"39","ar":"فَتَوَلّٰى بِرُكْنِهٖ وَقَالَ سٰحِرٌ اَوْ مَجْنُوْنٌ","bc":"fa tawallā biruknihī wa qāla sāḥirun au majnụn","at":"Tetapi dia (Fir‘aun) bersama bala tentaranya berpaling dan berkata, “Dia adalah seorang pesihir atau orang gila.” "},{"id":"40","ar":"فَاَخَذْنٰهُ وَجُنُوْدَهٗ فَنَبَذْنٰهُمْ فِى الْيَمِّ وَهُوَ مُلِيْمٌۗ ","bc":"fa akhażnāhu wa junụdahụ fa nabażnāhum fil-yammi wa huwa mulīm","at":"Maka Kami siksa dia beserta bala tentaranya, lalu Kami lemparkan mereka ke dalam laut, dalam keadaan tercela. "},{"id":"41","ar":"وَفِيْ عَادٍ اِذْ اَرْسَلْنَا عَلَيْهِمُ الرِّيْحَ الْعَقِيْمَۚ ","bc":"wa fī 'ādin iż arsalnā 'alaihimur-rīḥal-'aqīm","at":"Dan (juga) pada (kisah kaum) ‘Ad, ketika Kami kirimkan kepada mereka angin yang membinasakan,"},{"id":"42","ar":"مَا تَذَرُ مِنْ شَيْءٍ اَتَتْ عَلَيْهِ اِلَّا جَعَلَتْهُ كَالرَّمِيْمِۗ ","bc":"mā tażaru min syai`in atat 'alaihi illā ja'alat-hu kar-ramīm","at":"(angin itu) tidak membiarkan suatu apa pun yang dilandanya, bahkan dijadikannya seperti serbuk."},{"id":"43","ar":"وَفِيْ ثَمُوْدَ اِذْ قِيْلَ لَهُمْ تَمَتَّعُوْا حَتّٰى حِيْنٍ ","bc":"wa fī ṡamụda iż qīla lahum tamatta'ụ ḥattā ḥīn","at":"Dan pada (kisah kaum) Samud, ketika dikatakan kepada mereka, “Bersenang-senanglah kamu sampai waktu yang ditentukan.” "},{"id":"44","ar":"فَعَتَوْا عَنْ اَمْرِ رَبِّهِمْ فَاَخَذَتْهُمُ الصّٰعِقَةُ وَهُمْ يَنْظُرُوْنَ","bc":"fa 'atau 'an amri rabbihim fa akhażat-humuṣ-ṣā'iqatu wa hum yanẓurụn","at":"Lalu mereka berlaku angkuh terhadap perintah Tuhannya, maka mereka disambar petir sedang mereka melihatnya."},{"id":"45","ar":"فَمَا اسْتَطَاعُوْا مِنْ قِيَامٍ وَّمَا كَانُوْا مُنْتَصِرِيْنَۙ ","bc":"fa mastaṭā'ụ ming qiyāmiw wa mā kānụ muntaṣirīn","at":"Maka mereka tidak mampu bangun dan juga tidak mendapat pertolongan,"},{"id":"46","ar":"وَقَوْمَ نُوْحٍ مِّنْ قَبْلُ ۗ اِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ ","bc":"wa qauma nụḥim ming qabl, innahum kānụ qauman fāsiqīn","at":"dan sebelum itu (telah Kami binasakan) kaum Nuh. Sungguh, mereka adalah kaum yang fasik. "},{"id":"47","ar":"وَالسَّمَاۤءَ بَنَيْنٰهَا بِاَيْىدٍ وَّاِنَّا لَمُوْسِعُوْنَ","bc":"was-samā`a banaināhā bi`aidiw wa innā lamụsi'ụn","at":"Dan langit Kami bangun dengan kekuasaan (Kami), dan Kami benar-benar meluaskannya."},{"id":"48","ar":"وَالْاَرْضَ فَرَشْنٰهَا فَنِعْمَ الْمَاهِدُوْنَ","bc":"wal-arḍa farasynāhā fa ni'mal-māhidụn","at":"Dan bumi Kami hamparkan; maka (Kami) sebaik-baik yang telah menghamparkan. "},{"id":"49","ar":"وَمِنْ كُلِّ شَيْءٍ خَلَقْنَا زَوْجَيْنِ لَعَلَّكُمْ تَذَكَّرُوْنَ","bc":"wa ming kulli syai`in khalaqnā zaujaini la'allakum tażakkarụn","at":"Dan segala sesuatu Kami ciptakan berpasang-pasangan agar kamu mengingat (kebesaran Allah). "},{"id":"50","ar":"فَفِرُّوْٓا اِلَى اللّٰهِ ۗاِنِّيْ لَكُمْ مِّنْهُ نَذِيْرٌ مُّبِيْنٌۚ ","bc":"fa firrū ilallāh, innī lakum min-hu nażīrum mubīn","at":"Maka segeralah kembali kepada (menaati) Allah. Sungguh, aku seorang pemberi peringatan yang jelas dari Allah untukmu. "},{"id":"51","ar":"وَلَا تَجْعَلُوْا مَعَ اللّٰهِ اِلٰهًا اٰخَرَۗ اِنِّيْ لَكُمْ مِّنْهُ نَذِيْرٌ مُّبِيْنٌ","bc":"wa lā taj'alụ ma'allāhi ilāhan ākhar, innī lakum min-hu nażīrum mubīn","at":"Dan janganlah kamu mengadakan tuhan yang lain selain Allah. Sungguh, aku seorang pemberi peringatan yang jelas dari Allah untukmu. "},{"id":"52","ar":"كَذٰلِكَ مَآ اَتَى الَّذِيْنَ مِنْ قَبْلِهِمْ مِّنْ رَّسُوْلٍ اِلَّا قَالُوْا سَاحِرٌ اَوْ مَجْنُوْنٌ","bc":"każālika mā atallażīna ming qablihim mir rasụlin illā qālụ sāḥirun au majnụn","at":"Demikianlah setiap kali seorang Rasul yang datang kepada orang-orang yang sebelum mereka, mereka (kaumnya) pasti mengatakan, “Dia itu pesihir atau orang gila.” "},{"id":"53","ar":"اَتَوَاصَوْا بِهٖۚ بَلْ هُمْ قَوْمٌ طَاغُوْنَۚ ","bc":"a tawāṣau bih, bal hum qaumun ṭāgụn","at":"Apakah mereka saling berpesan tentang apa yang dikatakan itu. Sebenarnya mereka adalah kaum yang melampaui batas. "},{"id":"54","ar":"فَتَوَلَّ عَنْهُمْ فَمَآ اَنْتَ بِمَلُوْمٍ ","bc":"fa tawalla 'an-hum fa mā anta bimalụm","at":"Maka berpalinglah engkau dari mereka, dan engkau sama sekali tidak tercela. "},{"id":"55","ar":"وَذَكِّرْ فَاِنَّ الذِّكْرٰى تَنْفَعُ الْمُؤْمِنِيْنَ ","bc":"wa żakkir fa innaż-żikrā tanfa'ul-mu`minīn","at":"Dan tetaplah memberi peringatan, karena sesungguhnya peringatan itu bermanfaat bagi orang-orang mukmin. "},{"id":"56","ar":"وَمَا خَلَقْتُ الْجِنَّ وَالْاِنْسَ اِلَّا لِيَعْبُدُوْنِ","bc":"wa mā khalaqtul-jinna wal-insa illā liya'budụn","at":"Aku tidak menciptakan jin dan manusia melainkan agar mereka beribadah kepada-Ku. "},{"id":"57","ar":"مَآ اُرِيْدُ مِنْهُمْ مِّنْ رِّزْقٍ وَّمَآ اُرِيْدُ اَنْ يُّطْعِمُوْنِ ","bc":"mā urīdu min-hum mir rizqiw wa mā urīdu ay yuṭ'imụn","at":"Aku tidak menghendaki rezeki sedikit pun dari mereka dan Aku tidak menghendaki agar mereka memberi makan kepada-Ku."},{"id":"58","ar":"اِنَّ اللّٰهَ هُوَ الرَّزَّاقُ ذُو الْقُوَّةِ الْمَتِيْنُ ","bc":"innallāha huwar-razzāqu żul-quwwatil-matīn","at":"Sungguh Allah, Dialah Pemberi rezeki Yang Mempunyai Kekuatan lagi Sangat Kokoh."},{"id":"59","ar":"فَاِنَّ لِلَّذِيْنَ ظَلَمُوْا ذَنُوْبًا مِّثْلَ ذَنُوْبِ اَصْحٰبِهِمْ فَلَا يَسْتَعْجِلُوْنِ","bc":"fa inna lillażīna ẓalamụ żanụbam miṡla żanụbi aṣ-ḥābihim fa lā yasta'jilụn","at":"Maka sungguh, untuk orang-orang yang zalim ada bagian (azab) seperti bagian teman-teman mereka (dahulu); maka janganlah mereka meminta kepada-Ku untuk menyegerakannya. "},{"id":"60","ar":"فَوَيْلٌ لِّلَّذِيْنَ كَفَرُوْا مِنْ يَّوْمِهِمُ الَّذِيْ يُوْعَدُوْنَ ","bc":"fa wailul lillażīna kafarụ miy yaumihimullażī yụ'adụn","at":"Maka celakalah orang-orang yang kafir pada hari yang telah dijanjikan kepada mereka (hari Kiamat)."}],az_zukhruf=[{"id":"1","ar":" حٰمۤ ۚ ","bc":"ḥā mīm","at":"Ha Mim"},{"id":"2","ar":"وَالْكِتٰبِ الْمُبِيْنِ ۙ ","bc":"wal-kitābil-mubīn","at":"Demi Kitab (Al-Qur'an) yang jelas."},{"id":"3","ar":"اِنَّا جَعَلْنٰهُ قُرْاٰنًا عَرَبِيًّا لَّعَلَّكُمْ تَعْقِلُوْنَۚ ","bc":"innā ja'alnāhu qur`ānan 'arabiyyal la'allakum ta'qilụn","at":"Kami menjadikan Al-Qur'an dalam bahasa Arab agar kamu mengerti."},{"id":"4","ar":"وَاِنَّهٗ فِيْٓ اُمِّ الْكِتٰبِ لَدَيْنَا لَعَلِيٌّ حَكِيْمٌ ۗ ","bc":"wa innahụ fī ummil-kitābi ladainā la'aliyyun ḥakīm","at":"Dan sesungguhnya Al-Qur'an itu dalam Ummul Kitab (Lauh Mahfuzh) di sisi Kami, benar-benar (bernilai) tinggi dan penuh hikmah."},{"id":"5","ar":"اَفَنَضْرِبُ عَنْكُمُ الذِّكْرَ صَفْحًا اَنْ كُنْتُمْ قَوْمًا مُّسْرِفِيْنَ","bc":"a fa naḍribu 'angkumuż-żikra ṣaf-ḥan ang kuntum qaumam musrifīn","at":"Maka apakah Kami akan berhenti menurunkan ayat-ayat (sebagai peringatan) Al-Qur'an kepadamu, karena kamu kaum yang melampaui batas?"},{"id":"6","ar":"وَكَمْ اَرْسَلْنَا مِنْ نَّبِيٍّ فِى الْاَوَّلِيْنَ ","bc":"wa kam arsalnā min nabiyyin fil-awwalīn","at":"Dan betapa banyak nabi-nabi yang telah Kami utus kepada umat-umat yang terdahulu."},{"id":"7","ar":"وَمَا يَأْتِيْهِمْ مِّنْ نَّبِيٍّ اِلَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ","bc":"wa mā ya`tīhim min nabiyyin illā kānụ bihī yastahzi`ụn","at":"Dan setiap kali seorang nabi datang kepada mereka, mereka selalu memperolok-olokkannya."},{"id":"8","ar":"فَاَهْلَكْنَٓا اَشَدَّ مِنْهُمْ بَطْشًا وَّمَضٰى مَثَلُ الْاَوَّلِيْنَ ","bc":"fa ahlaknā asyadda min-hum baṭsyaw wa maḍā maṡalul-awwalīn","at":"Karena itu Kami binasakan orang-orang yang lebih besar kekuatannya di antara mereka dan telah berlalu contoh umat-umat terdahulu."},{"id":"9","ar":"وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ لَيَقُوْلُنَّ خَلَقَهُنَّ الْعَزِيْزُ الْعَلِيْمُۙ","bc":"wa la`in sa`altahum man khalaqas-samāwāti wal-arḍa layaqụlunna khalaqahunnal-'azīzul-'alīm","at":"Dan jika kamu tanyakan kepada mereka, “Siapakah yang menciptakan langit dan bumi?” Pastilah mereka akan menjawab, “Semuanya diciptakan oleh Yang Mahaperkasa, Maha Mengetahui.”"},{"id":"10","ar":"الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ مَهْدًا وَّجَعَلَ لَكُمْ فِيْهَا سُبُلًا لَّعَلَّكُمْ تَهْتَدُوْنَ ۚ ","bc":"allażī ja'ala lakumul-arḍa mahdaw wa ja'ala lakum fīhā subulal la'allakum tahtadụn","at":"Yang menjadikan bumi sebagai tempat menetap bagimu dan Dia menjadikan jalan-jalan di atas bumi untukmu agar kamu mendapat petunjuk."},{"id":"11","ar":"وَالَّذِيْ نَزَّلَ مِنَ السَّمَاۤءِ مَاۤءًۢ بِقَدَرٍۚ فَاَنْشَرْنَا بِهٖ بَلْدَةً مَّيْتًا ۚ كَذٰلِكَ تُخْرَجُوْنَ ","bc":"wallażī nazzala minas-samā`i mā`am biqadar, fa ansyarnā bihī baldatam maitā, każālika tukhrajụn","at":"Dan yang menurunkan air dari langit menurut ukuran (yang diperlukan) lalu dengan air itu Kami hidupkan negeri yang mati (tandus). Seperti itulah kamu akan dikeluarkan (dari kubur)."},{"id":"12","ar":"وَالَّذِيْ خَلَقَ الْاَزْوَاجَ كُلَّهَا وَجَعَلَ لَكُمْ مِّنَ الْفُلْكِ وَالْاَنْعَامِ مَا تَرْكَبُوْنَۙ ","bc":"wallażī khalaqal-azwāja kullahā wa ja'ala lakum minal-fulki wal-an'āmi mā tarkabụn","at":"Dan yang menciptakan semua berpasang-pasangan dan menjadikan kapal untukmu dan hewan ternak yang kamu tunggangi."},{"id":"13","ar":"لِتَسْتَوٗا عَلٰى ظُهُوْرِهٖ ثُمَّ تَذْكُرُوْا نِعْمَةَ رَبِّكُمْ اِذَا اسْتَوَيْتُمْ عَلَيْهِ وَتَقُوْلُوْا سُبْحٰنَ الَّذِيْ سَخَّرَ لَنَا هٰذَا وَمَا كُنَّا لَهٗ مُقْرِنِيْنَۙ ","bc":"litastawụ 'alā ẓuhụrihī ṡumma tażkurụ ni'mata rabbikum iżastawaitum 'alaihi wa taqụlụ sub-ḥānallażī sakhkhara lanā hāżā wa mā kunnā lahụ muqrinīn","at":"Agar kamu duduk di atas punggungnya kemudian kamu ingat nikmat Tuhanmu apabila kamu telah duduk di atasnya; dan agar kamu mengucapkan, “Maha-suci (Allah) yang telah menundukkan semua ini bagi kami padahal kami sebelumnya tidak mampu menguasainya,"},{"id":"14","ar":"وَاِنَّآ اِلٰى رَبِّنَا لَمُنْقَلِبُوْنَ ","bc":"wa innā ilā rabbinā lamungqalibụn","at":"dan sesungguhnya kami akan kembali kepada Tuhan kami.”"},{"id":"15","ar":"وَجَعَلُوْا لَهٗ مِنْ عِبَادِهٖ جُزْءًا ۗاِنَّ الْاِنْسَانَ لَكَفُوْرٌ مُّبِيْنٌ ۗ ","bc":"wa ja'alụ lahụ min 'ibādihī juz`ā, innal-insāna lakafụrum mubīn","at":"Dan mereka menjadikan sebagian dari hamba-hamba-Nya sebagai bagian dari-Nya. Sungguh, manusia itu pengingkar (nikmat Tuhan) yang nyata."},{"id":"16","ar":"اَمِ اتَّخَذَ مِمَّا يَخْلُقُ بَنٰتٍ وَّاَصْفٰىكُمْ بِالْبَنِيْنَ ۗ ","bc":"amittakhaża mimmā yakhluqu banātiw wa aṣfākum bil-banīn","at":"Pantaskah Dia mengambil anak perempuan dari yang diciptakan-Nya dan memberikan anak laki-laki kepadamu?"},{"id":"17","ar":"وَاِذَا بُشِّرَ اَحَدُهُمْ بِمَا ضَرَبَ لِلرَّحْمٰنِ مَثَلًا ظَلَّ وَجْهُهٗ مُسْوَدًّا وَّهُوَ كَظِيْمٌ ","bc":"wa iżā busysyira aḥaduhum bimā ḍaraba lir-raḥmāni maṡalan ẓalla waj-huhụ muswaddaw wa huwa kaẓīm","at":"Dan apabila salah seorang di antara mereka diberi kabar gembira dengan apa (kelahiran anak perempuan) yang dijadikan sebagai perumpamaan bagi (Allah) Yang Maha Pengasih, jadilah wajahnya hitam pekat, karena menahan sedih (dan marah)."},{"id":"18","ar":"اَوَمَنْ يُّنَشَّؤُا فِى الْحِلْيَةِ وَهُوَ فِى الْخِصَامِ غَيْرُ مُبِيْنٍ ","bc":"a wa may yunasysya`u fil-ḥilyati wa huwa fil-khiṣāmi gairu mubīn","at":"Dan apakah patut (menjadi anak Allah) orang yang dibesarkan sebagai perhiasan sedang dia tidak mampu memberi alasan yang tegas dan jelas dalam pertengkaran."},{"id":"19","ar":"وَجَعَلُوا الْمَلٰۤىِٕكَةَ الَّذِيْنَ هُمْ عِبٰدُ الرَّحْمٰنِ اِنَاثًا ۗ اَشَهِدُوْا خَلْقَهُمْ ۗسَتُكْتَبُ شَهَادَتُهُمْ وَيُسْٔـَلُوْنَ ","bc":"wa ja'alul-malā`ikatallażīna hum 'ibādur-raḥmāni ināṡā, a syahidụ khalqahum, satuktabu syahādatuhum wa yus`alụn","at":"Dan mereka menjadikan malaikat-malaikat hamba-hamba (Allah) Yang Maha Pengasih itu sebagai jenis perempuan. Apakah mereka menyaksikan penciptaan (malaikat-malaikat itu)? Kelak akan dituliskan kesaksian mereka dan akan dimintakan pertanggungjawaban."},{"id":"20","ar":"وَقَالُوْا لَوْ شَاۤءَ الرَّحْمٰنُ مَا عَبَدْنٰهُمْ ۗمَا لَهُمْ بِذٰلِكَ مِنْ عِلْمٍ اِنْ هُمْ اِلَّا يَخْرُصُوْنَۗ ","bc":"wa qālụ lau syā`ar-raḥmānu mā 'abadnāhum, mā lahum biżālika min 'ilmin in hum illā yakhruṣụn","at":"Dan mereka berkata, “Sekiranya (Allah) Yang Maha Pengasih menghendaki, tentulah kami tidak menyembah mereka (malaikat).” Mereka tidak mempunyai ilmu sedikit pun tentang itu. Tidak lain mereka hanyalah menduga-duga belaka."},{"id":"21","ar":"اَمْ اٰتَيْنٰهُمْ كِتٰبًا مِّنْ قَبْلِهٖ فَهُمْ بِهٖ مُسْتَمْسِكُوْنَ ","bc":"am ātaināhum kitābam ming qablihī fa hum bihī mustamsikụn","at":"Atau apakah pernah Kami berikan sebuah kitab kepada mereka sebelumnya, lalu mereka berpegang (pada kitab itu)?"},{"id":"22","ar":"بَلْ قَالُوْٓا اِنَّا وَجَدْنَآ اٰبَاۤءَنَا عَلٰٓى اُمَّةٍ وَّاِنَّا عَلٰٓى اٰثٰرِهِمْ مُّهْتَدُوْنَ ","bc":"bal qālū innā wajadnā ābā`anā 'alā ummatiw wa innā 'alā āṡārihim muhtadụn","at":"Bahkan mereka berkata, “Sesungguhnya kami mendapati nenek moyang kami menganut suatu agama, dan kami mendapat petunjuk untuk mengikuti jejak mereka.”"},{"id":"23","ar":"وَكَذٰلِكَ مَآ اَرْسَلْنَا مِنْ قَبْلِكَ فِيْ قَرْيَةٍ مِّنْ نَّذِيْرٍۙ اِلَّا قَالَ مُتْرَفُوْهَآ ۙاِنَّا وَجَدْنَآ اٰبَاۤءَنَا عَلٰٓى اُمَّةٍ وَّاِنَّا عَلٰٓى اٰثٰرِهِمْ مُّقْتَدُوْنَ","bc":"wa każālika mā arsalnā ming qablika fī qaryatim min nażīrin illā qāla mutrafụhā innā wajadnā ābā`anā 'alā ummatiw wa innā 'alā āṡārihim muqtadụn","at":"Dan demikian juga ketika Kami mengutus seorang pemberi peringatan sebelum engkau (Muhammad) dalam suatu negeri, orang-orang yang hidup mewah (di negeri itu) selalu berkata, “Sesungguhnya kami mendapati nenek moyang kami menganut suatu (agama) dan sesungguhnya kami sekedar pengikut jejak-jejak mereka.”"},{"id":"24","ar":"۞ قٰلَ اَوَلَوْ جِئْتُكُمْ بِاَهْدٰى مِمَّا وَجَدْتُّمْ عَلَيْهِ اٰبَاۤءَكُمْۗ قَالُوْٓا اِنَّا بِمَآ اُرْسِلْتُمْ بِهٖ كٰفِرُوْنَ ","bc":"qāla a walau ji`tukum bi`ahdā mimmā wajattum 'alaihi ābā`akum, qālū innā bimā ursiltum bihī kāfirụn","at":"(Rasul itu) berkata, “Apakah (kamu akan mengikutinya juga) sekalipun aku membawa untukmu (agama) yang lebih baik daripada apa yang kamu peroleh dari (agama) yang dianut nenek moyangmu.” Mereka menjawab, “Sesungguhnya kami mengingkari (agama) yang kamu diperintahkan untuk menyampaikannya.”"},{"id":"25","ar":"فَانْتَقَمْنَا مِنْهُمْ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُكَذِّبِيْنَ","bc":"fantaqamnā min-hum fanẓur kaifa kāna 'āqibatul-mukażżibīn","at":"Lalu Kami binasakan mereka, maka perhatikanlah bagaimana kesudahan orang-orang yang mendustakan (kebenaran)."},{"id":"26","ar":"وَاِذْ قَالَ اِبْرٰهِيْمُ لِاَبِيْهِ وَقَوْمِهٖٓ اِنَّنِيْ بَرَاۤءٌ مِّمَّا تَعْبُدُوْنَۙ","bc":"wa iż qāla ibrāhīmu li`abīhi wa qaumihī innanī barā`um mimmā ta'budụn","at":"Dan (ingatlah) ketika Ibrahim berkata kepada ayahnya dan kaumnya, “Sesungguhnya aku berlepas diri dari apa yang kamu sembah,"},{"id":"27","ar":"اِلَّا الَّذِيْ فَطَرَنِيْ فَاِنَّهٗ سَيَهْدِيْنِ ","bc":"illallażī faṭaranī fa innahụ sayahdīn","at":"kecuali (kamu menyembah) Allah yang menciptakanku; karena sungguh, Dia akan memberi petunjuk kepadaku.”"},{"id":"28","ar":"وَجَعَلَهَا كَلِمَةً ۢ بَاقِيَةً فِيْ عَقِبِهٖ لَعَلَّهُمْ يَرْجِعُوْنَۗ ","bc":"wa ja'alahā kalimatam bāqiyatan fī 'aqibihī la'allahum yarji'ụn","at":"Dan (Ibrahim) menjadikan (kalimat tauhid) itu kalimat yang kekal pada keturunannya agar mereka kembali (kepada kalimat tauhid itu)."},{"id":"29","ar":"بَلْ مَتَّعْتُ هٰٓؤُلَاۤءِ وَاٰبَاۤءَهُمْ حَتّٰى جَاۤءَهُمُ الْحَقُّ وَرَسُوْلٌ مُّبِيْنٌ ","bc":"bal matta'tu hā`ulā`i wa ābā`ahum ḥattā jā`ahumul-ḥaqqu wa rasụlum mubīn","at":"Bahkan Aku telah memberikan kenikmatan hidup kepada mereka dan nenek moyang mereka sampai kebenaran (Al-Qur'an) datang kepada mereka beserta seorang Rasul yang memberi penjelasan."},{"id":"30","ar":"وَلَمَّا جَاۤءَهُمُ الْحَقُّ قَالُوْا هٰذَا سِحْرٌ وَّاِنَّا بِهٖ كٰفِرُوْنَ ","bc":"wa lammā jā`ahumul-ḥaqqu qālụ hāżā siḥruw wa innā bihī kāfirụn","at":"Tetapi ketika kebenaran (Al-Qur'an) itu datang kepada mereka, mereka berkata, “Ini adalah sihir dan sesungguhnya kami mengingkarinya.”"},{"id":"31","ar":"وَقَالُوْا لَوْلَا نُزِّلَ هٰذَا الْقُرْاٰنُ عَلٰى رَجُلٍ مِّنَ الْقَرْيَتَيْنِ عَظِيْمٍ","bc":"wa qālụ lau lā nuzzila hāżal-qur`ānu 'alā rajulim minal-qaryataini 'aẓīm","at":"Dan mereka (juga) berkata, “Mengapa Al-Qur'an ini tidak diturunkan kepada orang besar (kaya dan berpengaruh) dari salah satu dua negeri ini (Mekah dan Taif)?”"},{"id":"32","ar":"اَهُمْ يَقْسِمُوْنَ رَحْمَتَ رَبِّكَۗ نَحْنُ قَسَمْنَا بَيْنَهُمْ مَّعِيْشَتَهُمْ فِى الْحَيٰوةِ الدُّنْيَاۙ وَرَفَعْنَا بَعْضَهُمْ فَوْقَ بَعْضٍ دَرَجٰتٍ لِّيَتَّخِذَ بَعْضُهُمْ بَعْضًا سُخْرِيًّا ۗوَرَحْمَتُ رَبِّكَ خَيْرٌ مِّمَّا يَجْمَعُوْنَ ","bc":"a hum yaqsimụna raḥmata rabbik, naḥnu qasamnā bainahum ma'īsyatahum fil-ḥayātid-dun-yā wa rafa'nā ba'ḍahum fauqa ba'ḍin darajātil liyattakhiża ba'ḍuhum ba'ḍan sukhriyyā, wa raḥmatu rabbika khairum mimmā yajma'ụn","at":"Apakah mereka yang membagi-bagi rahmat Tuhanmu? Kamilah yang menentukan penghidupan mereka dalam kehidupan dunia, dan Kami telah meninggikan sebagian mereka atas sebagian yang lain beberapa derajat, agar sebagian mereka dapat memanfaatkan sebagian yang lain. Dan rahmat Tuhanmu lebih baik dari apa yang mereka kumpulkan."},{"id":"33","ar":"وَلَوْلَآ اَنْ يَّكُوْنَ النَّاسُ اُمَّةً وَّاحِدَةً لَّجَعَلْنَا لِمَنْ يَّكْفُرُ بِالرَّحْمٰنِ لِبُيُوْتِهِمْ سُقُفًا مِّنْ فِضَّةٍ وَّمَعَارِجَ عَلَيْهَا يَظْهَرُوْنَۙ ","bc":"walau lā ay yakụnan-nāsu ummataw wāḥidatal laja'alnā limay yakfuru bir-raḥmāni libuyụtihim suqufam min fiḍḍatiw wa ma'ārija 'alaihā yaẓ-harụn","at":"Dan sekiranya bukan karena menghindarkan manusia menjadi umat yang satu (dalam kekafiran), pastilah sudah Kami buatkan bagi orang-orang yang kafir kepada (Allah) Yang Maha Pengasih, loteng-loteng rumah mereka dari perak, demikian pula tangga-tangga yang mereka naiki,"},{"id":"34","ar":"وَلِبُيُوْتِهِمْ اَبْوَابًا وَّسُرُرًا عَلَيْهَا يَتَّكِـُٔوْنَۙ ","bc":"wa libuyụtihim abwābaw wa sururan 'alaihā yattaki`ụn","at":"dan (Kami buatkan pula) pintu-pintu (perak) bagi rumah-rumah mereka, dan (begitu pula) dipan-dipan tempat mereka bersandar, "},{"id":"35","ar":"وَزُخْرُفًاۗ وَاِنْ كُلُّ ذٰلِكَ لَمَّا مَتَاعُ الْحَيٰوةِ الدُّنْيَا ۗوَالْاٰخِرَةُ عِنْدَ رَبِّكَ لِلْمُتَّقِيْنَ ","bc":"wa zukhrufā, wa ing kullu żālika lammā matā'ul-ḥayātid-dun-yā, wal-ākhiratu 'inda rabbika lil-muttaqīn","at":"dan (Kami buatkan pula) perhiasan-perhiasan dari emas. Dan semuanya itu tidak lain hanyalah kesenangan kehidupan dunia, sedangkan kehidupan akhirat di sisi Tuhanmu disediakan bagi orang-orang yang bertakwa."},{"id":"36","ar":"وَمَنْ يَّعْشُ عَنْ ذِكْرِ الرَّحْمٰنِ نُقَيِّضْ لَهٗ شَيْطٰنًا فَهُوَ لَهٗ قَرِيْنٌ ","bc":"wa may ya'syu 'an żikrir-raḥmāni nuqayyiḍ lahụ syaiṭānan fa huwa lahụ qarīn","at":"Dan barangsiapa berpaling dari pengajaran Allah Yang Maha Pengasih (Al-Qur'an), Kami biarkan setan (menyesatkannya) dan menjadi teman karibnya."},{"id":"37","ar":"وَاِنَّهُمْ لَيَصُدُّوْنَهُمْ عَنِ السَّبِيْلِ وَيَحْسَبُوْنَ اَنَّهُمْ مُّهْتَدُوْنَ ","bc":"wa innahum layaṣuddụnahum 'anis-sabīli wa yaḥsabụna annahum muhtadụn","at":"Dan sungguh, mereka (setan-setan itu) benar-benar menghalang-halangi mereka dari jalan yang benar, sedang mereka menyangka bahwa mereka mendapat petunjuk."},{"id":"38","ar":"حَتّٰىٓ اِذَا جَاۤءَنَا قَالَ يٰلَيْتَ بَيْنِيْ وَبَيْنَكَ بُعْدَ الْمَشْرِقَيْنِ فَبِئْسَ الْقَرِيْنُ","bc":"ḥattā iżā jā`anā qāla yā laita bainī wa bainaka bu'dal-masyriqaini fa bi`sal-qarīn","at":"Sehingga apabila orang–orang yang berpaling itu datang kepada Kami (pada hari Kiamat) dia berkata, “Wahai! Sekiranya (jarak) antara aku dan kamu seperti jarak antara timur dan barat! Memang (setan itu) teman yang paling jahat (bagi manusia).”"},{"id":"39","ar":"وَلَنْ يَّنْفَعَكُمُ الْيَوْمَ اِذْ ظَّلَمْتُمْ اَنَّكُمْ فِى الْعَذَابِ مُشْتَرِكُوْنَ ","bc":"wa lay yanfa'akumul-yauma iẓ ẓalamtum annakum fil-'ażābi musytarikụn","at":"Dan (harapanmu itu) sekali-kali tidak akan memberi manfaat kepadamu pada hari itu karena kamu telah menzalimi (dirimu sendiri). Sesungguhnya kamu pantas bersama-sama dalam azab itu."},{"id":"40","ar":"اَفَاَنْتَ تُسْمِعُ الصُّمَّ اَوْ تَهْدِى الْعُمْيَ وَمَنْ كَانَ فِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"a fa anta tusmi'uṣ-ṣumma au tahdil-'umya wa mang kāna fī ḍalālim mubīn","at":"Maka apakah engkau (Muhammad) dapat menjadikan orang yang tuli bisa mendengar, atau (dapatkah) engkau memberi petunjuk kepada orang yang buta (hatinya) dan kepada orang yang tetap dalam kesesatan yang nyata?"},{"id":"41","ar":"فَاِمَّا نَذْهَبَنَّ بِكَ فَاِنَّا مِنْهُمْ مُّنْتَقِمُوْنَۙ ","bc":"fa immā naż-habanna bika fa innā min-hum muntaqimụn","at":"Maka sungguh, sekiranya Kami mewafatkanmu (sebelum engkau mencapai kemenangan), maka sesungguhnya Kami akan tetap memberikan azab kepada mereka (di akhirat),"},{"id":"42","ar":"اَوْ نُرِيَنَّكَ الَّذِيْ وَعَدْنٰهُمْ فَاِنَّا عَلَيْهِمْ مُّقْتَدِرُوْنَ ","bc":"au nuriyannakallażī wa'adnāhum fa innā 'alaihim muqtadirụn","at":"atau Kami perlihatkan kepadamu (azab) yang telah Kami ancamkan kepada mereka. Maka sungguh, Kami berkuasa atas mereka."},{"id":"43","ar":"فَاسْتَمْسِكْ بِالَّذِيْٓ اُوْحِيَ اِلَيْكَ ۚاِنَّكَ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"fastamsik billażī ụḥiya ilaīk, innaka 'alā ṣirāṭim mustaqīm","at":"Maka berpegang teguhlah engkau kepada (agama) yang telah diwahyukan kepadamu. Sungguh, engkau berada di jalan yang lurus."},{"id":"44","ar":"وَاِنَّهٗ لَذِكْرٌ لَّكَ وَلِقَوْمِكَ ۚوَسَوْفَ تُسْٔـَلُوْنَ ","bc":"wa innahụ lażikrul laka wa liqaumik, wa saufa tus`alụn","at":"Dan sungguh, Al-Qur'an itu benar-benar suatu peringatan bagimu dan bagi kaummu, dan kelak kamu akan diminta pertanggungjawaban."},{"id":"45","ar":"وَسْٔـَلْ مَنْ اَرْسَلْنَا مِنْ قَبْلِكَ مِنْ رُّسُلِنَآ ۖ اَجَعَلْنَا مِنْ دُوْنِ الرَّحْمٰنِ اٰلِهَةً يُّعْبَدُوْنَ","bc":"was`al man arsalnā ming qablika mir rusulinā a ja'alnā min dụnir-raḥmāni ālihatay yu'badụn","at":"Dan tanyakanlah (Muhammad) kepada rasul-rasul Kami yang telah Kami utus sebelum engkau, “Apakah Kami menentukan tuhan-tuhan selain (Allah) Yang Maha Pengasih untuk disembah?”"},{"id":"46","ar":"وَلَقَدْ اَرْسَلْنَا مُوْسٰى بِاٰيٰتِنَآ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ فَقَالَ اِنِّيْ رَسُوْلُ رَبِّ الْعٰلَمِيْنَ ","bc":"wa laqad arsalnā mụsā bi`āyātinā ilā fir'auna wa mala`ihī fa qāla innī rasụlu rabbil-'ālamīn","at":"Dan sungguh, Kami telah mengutus Musa dengan membawa mukjizat-mukjizat Kami kepada Fir’aun dan pemuka-pemuka kaumnya. Maka dia (Musa) berkata, “Sesungguhnya aku adalah utusan dari Tuhan seluruh alam.”"},{"id":"47","ar":"فَلَمَّا جَاۤءَهُمْ بِاٰيٰتِنَآ اِذَا هُمْ مِّنْهَا يَضْحَكُوْنَ ","bc":"fa lammā jā`ahum bi`āyātinā iżā hum min-hā yaḍ-ḥakụn","at":"Maka ketika dia (Musa) datang kepada mereka membawa mukjizat-mukjizat Kami, seketika itu mereka menertawakannya."},{"id":"48","ar":"وَمَا نُرِيْهِمْ مِّنْ اٰيَةٍ اِلَّا هِيَ اَكْبَرُ مِنْ اُخْتِهَاۗ وَاَخَذْنٰهُمْ بِالْعَذَابِ لَعَلَّهُمْ يَرْجِعُوْنَ ","bc":"wa mā nurīhim min āyatin illā hiya akbaru min ukhtihā, wa akhażnāhum bil-'ażābi la'allahum yarji'ụn","at":"Dan tidaklah Kami perlihatkan suatu mukjizat kepada mereka kecuali (mukjizat itu) lebih besar dari mukjizat-mukjizat (yang sebelumnya). Dan Kami timpakan kepada mereka azab agar mereka kembali (ke jalan yang benar)."},{"id":"49","ar":"وَقَالُوْا يٰٓاَيُّهَ السّٰحِرُ ادْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِنْدَكَۚ اِنَّنَا لَمُهْتَدُوْنَ ","bc":"wa qālụ yā ayyuhas-sāḥirud'u lanā rabbaka bimā 'ahida 'indak, innanā lamuhtadụn","at":"Dan mereka berkata, “Wahai pesihir! Berdoalah kepada Tuhanmu untuk (melepaskan) kami sesuai dengan apa yang telah dijanjikan-Nya kepadamu; sesungguhnya kami (jika doamu dikabulkan) akan menjadi orang yang mendapat petunjuk.”"},{"id":"50","ar":"فَلَمَّا كَشَفْنَا عَنْهُمُ الْعَذَابَ اِذَا هُمْ يَنْكُثُوْنَ","bc":"fa lammā kasyafnā 'an-humul-'ażāba iżā hum yangkuṡụn","at":"Maka ketika Kami hilangkan azab itu dari mereka, seketika itu (juga) mereka ingkar janji."},{"id":"51","ar":"وَنَادٰى فِرْعَوْنُ فِيْ قَوْمِهٖ قَالَ يٰقَوْمِ اَلَيْسَ لِيْ مُلْكُ مِصْرَ وَهٰذِهِ الْاَنْهٰرُ تَجْرِيْ مِنْ تَحْتِيْۚ اَفَلَا تُبْصِرُوْنَۗ ","bc":"wa nādā fir'aunu fī qaumihī qāla yā qaumi a laisa lī mulku miṣra wa hāżihil-an-hāru tajrī min taḥtī, a fa lā tubṣirụn","at":"Dan Fir‘aun berseru kepada kaumnya (seraya) berkata, “Wahai kaumku! Bukankah kerajaan Mesir itu milikku dan (bukankah) sungai-sungai ini mengalir di bawahku; apakah kamu tidak melihat?"},{"id":"52","ar":"اَمْ اَنَا۠ خَيْرٌ مِّنْ هٰذَا الَّذِيْ هُوَ مَهِيْنٌ ەۙ وَّلَا يَكَادُ يُبِيْنُ ","bc":"am ana khairum min hāżallażī huwa mahīnuw wa lā yakādu yubīn","at":"Bukankah aku lebih baik dari orang (Musa) yang hina ini dan yang hampir tidak dapat menjelaskan (perkataannya)?"},{"id":"53","ar":"فَلَوْلَٓا اُلْقِيَ عَلَيْهِ اَسْوِرَةٌ مِّنْ ذَهَبٍ اَوْ جَاۤءَ مَعَهُ الْمَلٰۤىِٕكَةُ مُقْتَرِنِيْنَ ","bc":"falau lā ulqiya 'alaihi aswiratum min żahabin au jā`a ma'ahul-malā`ikatu muqtarinīn","at":"Maka mengapa dia (Musa) tidak dipakaikan gelang dari emas atau malaikat datang bersama-sama dia untuk mengiringkannya?”"},{"id":"54","ar":"فَاسْتَخَفَّ قَوْمَهٗ فَاَطَاعُوْهُ ۗاِنَّهُمْ كَانُوْا قَوْمًا فٰسِقِيْنَ ","bc":"fastakhaffa qaumahụ fa aṭā'ụh, innahum kānụ qauman fāsiqīn","at":"Maka (Fir‘aun) dengan perkataan itu telah mempengaruhi kaumnya, sehingga mereka patuh kepadanya. Sungguh, mereka adalah kaum yang fasik."},{"id":"55","ar":"فَلَمَّآ اٰسَفُوْنَا انْتَقَمْنَا مِنْهُمْ فَاَغْرَقْنٰهُمْ اَجْمَعِيْنَۙ ","bc":"fa lammā āsafụnantaqamnā min-hum fa agraqnāhum ajma'īn","at":"Maka ketika mereka membuat Kami murka, Kami hukum mereka, lalu Kami tenggelamkan mereka semuanya (di laut),"},{"id":"56","ar":"فَجَعَلْنٰهُمْ سَلَفًا وَّمَثَلًا لِّلْاٰخِرِيْنَ","bc":"fa ja'alnāhum salafaw wa maṡalal lil-ākhirīn","at":"maka Kami jadikan mereka sebagai (kaum) terdahulu dan pelajaran bagi orang-orang yang kemudian."},{"id":"57","ar":"وَلَمَّا ضُرِبَ ابْنُ مَرْيَمَ مَثَلًا اِذَا قَوْمُكَ مِنْهُ يَصِدُّوْنَ","bc":"wa lammā ḍuribabnu maryama maṡalan iżā qaumuka min-hu yaṣiddụn","at":"Dan ketika putra Maryam (Isa) dijadikan perumpamaan, tiba-tiba kaummu (Suku Quraisy) bersorak karenanya."},{"id":"58","ar":"وَقَالُوْٓا ءَاٰلِهَتُنَا خَيْرٌ اَمْ هُوَ ۗمَا ضَرَبُوْهُ لَكَ اِلَّا جَدَلًا ۗبَلْ هُمْ قَوْمٌ خَصِمُوْنَ","bc":"wa qālū a ālihatunā khairun am huw, mā ḍarabụhu laka illā jadalā, bal hum qaumun khaṣimụn","at":"Dan mereka berkata, “Manakah yang lebih baik tuhan-tuhan kami atau dia (Isa)?” Mereka tidak memberikan (perumpamaan itu) kepadamu melainkan dengan maksud membantah saja, sebenarnya mereka adalah kaum yang suka bertengkar."},{"id":"59","ar":"اِنْ هُوَ اِلَّا عَبْدٌ اَنْعَمْنَا عَلَيْهِ وَجَعَلْنٰهُ مَثَلًا لِّبَنِيْٓ اِسْرَاۤءِيْلَ ۗ ","bc":"in huwa illā 'abdun an'amnā 'alaihi wa ja'alnāhu maṡalal libanī isrā`īl","at":"Dia (Isa) tidak lain hanyalah seorang hamba yang Kami berikan nikmat (kenabian) kepadanya dan Kami jadikan dia sebagai contoh pelajaran bagi Bani Israil."},{"id":"60","ar":"وَلَوْ نَشَاۤءُ لَجَعَلْنَا مِنْكُمْ مَّلٰۤىِٕكَةً فِى الْاَرْضِ يَخْلُفُوْنَ ","bc":"walau nasyā`u laja'alnā mingkum malā`ikatan fil-arḍi yakhlufụn","at":"Dan sekiranya Kami menghendaki, niscaya ada di antara kamu yang Kami jadikan malaikat-malaikat (yang turun temurun) sebagai pengganti kamu di bumi."},{"id":"61","ar":"وَاِنَّهٗ لَعِلْمٌ لِّلسَّاعَةِ فَلَا تَمْتَرُنَّ بِهَا وَاتَّبِعُوْنِۗ هٰذَا صِرَاطٌ مُّسْتَقِيْمٌ ","bc":"wa innahụ la'ilmul lis-sā'ati fa lā tamtarunna bihā wattabi'ụn, hāżā ṣirāṭum mustaqīm","at":"Dan sungguh, dia (Isa) itu benar-benar menjadi pertanda akan datangnya hari Kiamat. Karena itu, janganlah kamu ragu-ragu tentang (Kiamat) itu dan ikutilah Aku. Inilah jalan yang lurus."},{"id":"62","ar":"وَلَا يَصُدَّنَّكُمُ الشَّيْطٰنُۚ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ ","bc":"wa lā yaṣuddannakumusy-syaiṭān, innahụ lakum 'aduwwum mubīn","at":"Dan janganlah kamu sekali-kali dipalingkan oleh setan; sungguh, setan itu musuh yang nyata bagimu."},{"id":"63","ar":"وَلَمَّا جَاۤءَ عِيْسٰى بِالْبَيِّنٰتِ قَالَ قَدْ جِئْتُكُمْ بِالْحِكْمَةِ وَلِاُبَيِّنَ لَكُمْ بَعْضَ الَّذِيْ تَخْتَلِفُوْنَ فِيْهِۚ فَاتَّقُوا اللّٰهَ وَاَطِيْعُوْنِ ","bc":"wa lammā jā`a 'īsā bil-bayyināti qāla qad ji`tukum bil-ḥikmati wa li`ubayyina lakum ba'ḍallażī takhtalifụna fīh, fattaqullāha wa aṭī'ụn","at":"Dan ketika Isa datang membawa keterangan, dia berkata, “Sungguh, aku datang kepadamu dengan membawa hikmah dan untuk menjelaskan kepadamu sebagian dari apa yang kamu perselisihkan, maka bertakwalah kepada Allah dan taatlah kepadaku."},{"id":"64","ar":"اِنَّ اللّٰهَ هُوَ رَبِّيْ وَرَبُّكُمْ فَاعْبُدُوْهُۗ هٰذَا صِرَاطٌ مُّسْتَقِيْمٌ ","bc":"innallāha huwa rabbī wa rabbukum fa'budụh, hāżā ṣirāṭum mustaqīm","at":"Sungguh Allah, Dia Tuhanku dan Tuhanmu, maka sembahlah Dia. Ini adalah jalan yang lurus.”"},{"id":"65","ar":"فَاخْتَلَفَ الْاَحْزَابُ مِنْۢ بَيْنِهِمْ ۚفَوَيْلٌ لِّلَّذِيْنَ ظَلَمُوْا مِنْ عَذَابِ يَوْمٍ اَلِيْمٍ ","bc":"fakhtalafal-aḥzābu mim bainihim, fa wailul lillażīna ẓalamụ min 'ażābi yaumin alīm","at":"Tetapi golongan-golongan (yang ada) saling berselisih di antara mereka; maka celakalah orang-orang yang zalim karena azab pada hari yang pedih (Kiamat)."},{"id":"66","ar":"هَلْ يَنْظُرُوْنَ اِلَّا السَّاعَةَ اَنْ تَأْتِيَهُمْ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ ","bc":"hal yanẓurụna illas-sā'ata an ta`tiyahum bagtataw wa hum lā yasy'urụn","at":"Apakah mereka hanya menunggu saja kedatangan hari Kiamat yang datang kepada mereka secara mendadak sedang mereka tidak menyadarinya?"},{"id":"67","ar":"اَلْاَخِلَّاۤءُ يَوْمَىِٕذٍۢ بَعْضُهُمْ لِبَعْضٍ عَدُوٌّ اِلَّا الْمُتَّقِيْنَ ۗ","bc":"al-akhillā`u yauma`iżim ba'ḍuhum liba'ḍin 'aduwwun illal-muttaqīn","at":"Teman-teman karib pada hari itu saling bermusuhan satu sama lain, kecuali mereka yang bertakwa."},{"id":"68","ar":"يٰعِبَادِ لَاخَوْفٌ عَلَيْكُمُ الْيَوْمَ وَلَآ اَنْتُمْ تَحْزَنُوْنَۚ ","bc":"yā 'ibādi lā khaufun 'alaikumul-yauma wa lā antum taḥzanụn","at":"”Wahai hamba-hamba-Ku! Tidak ada ketakutan bagimu pada hari itu dan tidak pula kamu bersedih hati."},{"id":"69","ar":"اَلَّذِيْنَ اٰمَنُوْا بِاٰيٰتِنَا وَكَانُوْا مُسْلِمِيْنَۚ ","bc":"allażīna āmanụ bi`āyātinā wa kānụ muslimīn","at":"(Yaitu) orang-orang yang beriman kepada ayat-ayat Kami dan mereka berserah diri."},{"id":"70","ar":"اُدْخُلُوا الْجَنَّةَ اَنْتُمْ وَاَزْوَاجُكُمْ تُحْبَرُوْنَ ","bc":"udkhulul-jannata antum wa azwājukum tuḥbarụn","at":"Masuklah kamu ke dalam surga, kamu dan pasanganmu akan digembirakan.”"},{"id":"71","ar":"يُطَافُ عَلَيْهِمْ بِصِحَافٍ مِّنْ ذَهَبٍ وَّاَكْوَابٍ ۚوَفِيْهَا مَا تَشْتَهِيْهِ الْاَنْفُسُ وَتَلَذُّ الْاَعْيُنُ ۚوَاَنْتُمْ فِيْهَا خٰلِدُوْنَۚ ","bc":"yuṭāfu 'alaihim biṣiḥāfim min żahabiw wa akwāb, wa fīhā mā tasytahīhil-anfusu wa talażżul-a'yun, wa antum fīhā khālidụn","at":"Kepada mereka diedarkan piring-piring dan gelas-gelas dari emas, dan di dalam surga itu terdapat apa yang diingini oleh hati dan segala yang sedap (dipandang) mata. Dan kamu kekal di dalamnya."},{"id":"72","ar":"وَتِلْكَ الْجَنَّةُ الَّتِيْٓ اُوْرِثْتُمُوْهَا بِمَا كُنْتُمْ تَعْمَلُوْنَ ","bc":"wa tilkal-jannatullatī ụriṡtumụhā bimā kuntum ta'malụn","at":"Dan itulah surga yang diwariskan kepada kamu disebabkan amal perbuatan yang telah kamu kerjakan."},{"id":"73","ar":"لَكُمْ فِيْهَا فَاكِهَةٌ كَثِيْرَةٌ مِّنْهَا تَأْكُلُوْنَ","bc":"lakum fīhā fākihatung kaṡīratum min-hā ta`kulụn","at":"Di dalam surga itu terdapat banyak buah-buahan untukmu yang sebagiannya kamu makan."},{"id":"74","ar":"اِنَّ الْمُجْرِمِيْنَ فِيْ عَذَابِ جَهَنَّمَ خٰلِدُوْنَۖ ","bc":"innal-mujrimīna fī 'ażābi jahannama khālidụn","at":"Sungguh, orang-orang yang berdosa itu kekal di dalam azab neraka Jahanam."},{"id":"75","ar":"لَا يُفَتَّرُ عَنْهُمْ وَهُمْ فِيْهِ مُبْلِسُوْنَ ۚ ","bc":"lā yufattaru 'an-hum wa hum fīhi mublisụn","at":"Tidak diringankan (azab) itu dari mereka, dan mereka berputus asa di dalamnya."},{"id":"76","ar":"وَمَا ظَلَمْنٰهُمْ وَلٰكِنْ كَانُوْا هُمُ الظّٰلِمِيْنَ","bc":"wa mā ẓalamnāhum wa lāking kānụ humuẓ-ẓālimīn","at":"Dan tidaklah Kami menzalimi mereka, tetapi merekalah yang menzalimi diri mereka sendiri."},{"id":"77","ar":"وَنَادَوْا يٰمٰلِكُ لِيَقْضِ عَلَيْنَا رَبُّكَۗ قَالَ اِنَّكُمْ مَّاكِثُوْنَ","bc":"wa nādau yā māliku liyaqḍi 'alainā rabbuk, qāla innakum mākiṡụn","at":"Dan mereka berseru, “Wahai (Malaikat) Malik! Biarlah Tuhanmu mematikan kami saja.” Dia menjawab, “Sungguh, kamu akan tetap tinggal (di neraka ini).”"},{"id":"78","ar":"لَقَدْ جِئْنٰكُمْ بِالْحَقِّ وَلٰكِنَّ اَكْثَرَكُمْ لِلْحَقِّ كٰرِهُوْنَ","bc":"laqad ji`nākum bil-ḥaqqi wa lākinna akṡarakum lil-ḥaqqi kārihụn","at":"Sungguh, Kami telah datang membawa kebenaran kepada kamu tetapi kebanyakan di antara kamu benci pada kebenaran itu."},{"id":"79","ar":"اَمْ اَبْرَمُوْٓا اَمْرًا فَاِنَّا مُبْرِمُوْنَۚ","bc":"am abramū amran fa innā mubrimụn","at":"Ataukah mereka telah merencanakan suatu tipu daya (jahat), maka sesungguhnya Kami telah berencana (mengatasi tipu daya mereka)."},{"id":"80","ar":"اَمْ يَحْسَبُوْنَ اَنَّا لَا نَسْمَعُ سِرَّهُمْ وَنَجْوٰىهُمْ ۗ بَلٰى وَرُسُلُنَا لَدَيْهِمْ يَكْتُبُوْنَ ","bc":"am yaḥsabụna annā lā nasma'u sirrahum wa najwāhum, balā wa rusulunā ladaihim yaktubụn","at":"Ataukah mereka mengira, bahwa Kami tidak mendengar rahasia dan bisikan-bisikan mereka? Sebenarnya (Kami mendengar), dan utusan-utusan Kami (malaikat) selalu mencatat di sisi mereka."},{"id":"81","ar":"قُلْ اِنْ كَانَ لِلرَّحْمٰنِ وَلَدٌ ۖفَاَنَا۠ اَوَّلُ الْعٰبِدِيْنَ ","bc":"qul ing kāna lir-raḥmāni waladun fa ana awwalul-'ābidīn","at":"Katakanlah (Muhammad), “Jika benar Tuhan Yang Maha Pengasih mempunyai anak, maka akulah orang yang mula-mula memuliakan (anak itu)."},{"id":"82","ar":"سُبْحٰنَ رَبِّ السَّمٰوٰتِ وَالْاَرْضِ رَبِّ الْعَرْشِ عَمَّا يَصِفُوْنَ ","bc":"sub-ḥāna rabbis-samāwāti wal-arḍi rabbil-'arsyi 'ammā yaṣifụn","at":"Mahasuci Tuhan pemilik langit dan bumi, Tuhan pemilik ‘Arsy, dari apa yang mereka sifatkan itu.”"},{"id":"83","ar":"فَذَرْهُمْ يَخُوْضُوْا وَيَلْعَبُوْا حَتّٰى يُلٰقُوْا يَوْمَهُمُ الَّذِيْ يُوْعَدُوْنَ","bc":"fa żar-hum yakhụḍụ wa yal'abụ ḥattā yulāqụ yaumahumullażī yụ'adụn","at":"Maka biarkanlah mereka tenggelam (dalam kesesatan) dan bermain-main sampai mereka menemui hari yang dijanjikan kepada mereka."},{"id":"84","ar":"وَهُوَ الَّذِيْ فِى السَّمَاۤءِ اِلٰهٌ وَّ فِى الْاَرْضِ اِلٰهٌ ۗوَهُوَ الْحَكِيْمُ الْعَلِيْمُ ","bc":"wa huwallażī fis-samā`i ilāhuw wa fil-arḍi ilāh, wa huwal-ḥakīmul-'alīm","at":"Dan Dialah Tuhan (yang disembah) di langit dan Tuhan (yang disembah) di bumi dan Dialah Yang Mahabijaksana, Maha Mengetahui."},{"id":"85","ar":"وَتَبٰرَكَ الَّذِيْ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۚوَعِنْدَهٗ عِلْمُ السَّاعَةِۚ وَاِلَيْهِ تُرْجَعُوْنَ","bc":"wa tabārakallażī lahụ mulkus-samāwāti wal-arḍi wa mā bainahumā, wa 'indahụ 'ilmus-sā'ah, wa ilaihi turja'ụn","at":"Dan Mahasuci (Allah) yang memiliki kerajaan langit dan bumi, dan apa yang ada di antara keduanya; dan di sisi-Nyalah ilmu tentang hari Kiamat dan hanya kepada-Nyalah kamu dikembalikan."},{"id":"86","ar":"وَلَا يَمْلِكُ الَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِهِ الشَّفَاعَةَ اِلَّا مَنْ شَهِدَ بِالْحَقِّ وَهُمْ يَعْلَمُوْنَ","bc":"wa lā yamlikullażīna yad'ụna min dụnihisy-syafā'ata illā man syahida bil-ḥaqqi wa hum ya'lamụn","at":"Dan orang-orang yang menyeru kepada selain Allah tidak mendapat syafaat (pertolongan di akhirat); kecuali orang yang mengakui yang hak (tauhid) dan mereka meyakini."},{"id":"87","ar":"وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَهُمْ لَيَقُوْلُنَّ اللّٰهُ فَاَنّٰى يُؤْفَكُوْنَۙ","bc":"wa la`in sa`altahum man khalaqahum layaqụlunnallāhu fa annā yu`fakụn","at":"Dan jika engkau bertanya kepada mereka, “Siapakah yang menciptakan mereka, niscaya mereka menjawab, “Allah,” jadi bagaimana mereka dapat dipalingkan (dari menyembah Allah),”"},{"id":"88","ar":"وَقِيْلِهٖ يٰرَبِّ اِنَّ هٰٓؤُلَاۤءِ قَوْمٌ لَّا يُؤْمِنُوْنَۘ","bc":"wa qīlihī yā rabbi inna hā`ulā`i qaumul lā yu`minụn","at":"dan (Allah mengetahui) ucapannya (Muhammad), “Ya Tuhanku, sesungguhnya mereka itu adalah kaum yang tidak beriman.”"},{"id":"89","ar":"فَاصْفَحْ عَنْهُمْ وَقُلْ سَلٰمٌۗ فَسَوْفَ يَعْلَمُوْنَ","bc":"faṣfaḥ 'an-hum wa qul salām, fa saufa ya'lamụn","at":"Maka berpalinglah dari mereka dan katakanlah, “Salam (selamat tinggal).” Kelak mereka akan mengetahui (nasib mereka yang buruk)."}],az_zumar=[{"id":"1","ar":" تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْحَكِيْمِ ","bc":"tanzīlul-kitābi minallāhil-'azīzil-ḥakīm","at":"Kitab (Al-Qur'an) ini diturunkan oleh Allah Yang Mahamulia, Mahabijaksana."},{"id":"2","ar":"اِنَّآ اَنْزَلْنَآ اِلَيْكَ الْكِتٰبَ بِالْحَقِّ فَاعْبُدِ اللّٰهَ مُخْلِصًا لَّهُ الدِّيْنَۗ","bc":"innā anzalnā ilaikal-kitāba bil-ḥaqqi fa'budillāha mukhliṣal lahud-dīn","at":"Sesungguhnya Kami menurunkan Kitab (Al-Qur'an) kepadamu (Muhammad) dengan (membawa) kebenaran. Maka sembahlah Allah dengan tulus ikhlas beragama kepada-Nya."},{"id":"3","ar":"اَلَا لِلّٰهِ الدِّيْنُ الْخَالِصُ ۗوَالَّذِيْنَ اتَّخَذُوْا مِنْ دُوْنِهٖٓ اَوْلِيَاۤءَۘ مَا نَعْبُدُهُمْ اِلَّا لِيُقَرِّبُوْنَآ اِلَى اللّٰهِ زُلْفٰىۗ اِنَّ اللّٰهَ يَحْكُمُ بَيْنَهُمْ فِيْ مَا هُمْ فِيْهِ يَخْتَلِفُوْنَ ەۗ اِنَّ اللّٰهَ لَا يَهْدِيْ مَنْ هُوَ كٰذِبٌ كَفَّارٌ","bc":"alā lillāhid-dīnul-khāliṣ, wallażīnattakhażụ min dụnihī auliyā`, mā na'buduhum illā liyuqarribụnā ilallāhi zulfā, innallāha yaḥkumu bainahum fī mā hum fīhi yakhtalifụn, innallāha lā yahdī man huwa kāżibung kaffār","at":"Ingatlah! Hanya milik Allah agama yang murni (dari syirik). Dan orang-orang yang mengambil pelindung selain Dia (berkata), “Kami tidak menyembah mereka melainkan (berharap) agar mereka mendekatkan kami kepada Allah dengan sedekat-dekatnya.” Sungguh, Allah akan memberi putusan di antara mereka tentang apa yang mereka perselisihkan. Sungguh, Allah tidak memberi petunjuk kepada pendusta dan orang yang sangat ingkar."},{"id":"4","ar":"لَوْ اَرَادَ اللّٰهُ اَنْ يَّتَّخِذَ وَلَدًا لَّاصْطَفٰى مِمَّا يَخْلُقُ مَا يَشَاۤءُ ۙ سُبْحٰنَهٗ ۗهُوَ اللّٰهُ الْوَاحِدُ الْقَهَّارُ","bc":"lau arādallāhu ay yattakhiża waladal laṣṭafā mimmā yakhluqu mā yasyā`u sub-ḥānah, huwallāhul-wāḥidul-qahhār","at":"Sekiranya Allah hendak mengambil anak, tentu Dia akan memilih apa yang Dia kehendaki dari apa yang telah diciptakan-Nya. Mahasuci Dia. Dialah Allah Yang Maha Esa, Mahaperkasa."},{"id":"5","ar":"خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۚ يُكَوِّرُ الَّيْلَ عَلَى النَّهَارِ وَيُكَوِّرُ النَّهَارَ عَلَى الَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۗ كُلٌّ يَّجْرِيْ لِاَجَلٍ مُّسَمًّىۗ اَلَا هُوَ الْعَزِيْزُ الْغَفَّارُ","bc":"khalaqas-samāwāti wal-arḍa bil-ḥaqq, yukawwirul-laila 'alan-nahāri wa yukawwirun-nahāra 'alal-laili wa sakhkharasy-syamsa wal-qamar, kulluy yajrī li`ajalim musammā, alā huwal-'azīzul-gaffār","at":"Dia menciptakan langit dan bumi dengan (tujuan) yang benar; Dia memasukkan malam atas siang dan memasukkan siang atas malam dan menundukkan matahari dan bulan, masing-masing berjalan menurut waktu yang ditentukan. Ingatlah! Dialah Yang Mahamulia, Maha Pengampun."},{"id":"6","ar":"خَلَقَكُمْ مِّنْ نَّفْسٍ وَّاحِدَةٍ ثُمَّ جَعَلَ مِنْهَا زَوْجَهَا وَاَنْزَلَ لَكُمْ مِّنَ الْاَنْعَامِ ثَمٰنِيَةَ اَزْوَاجٍ ۗ يَخْلُقُكُمْ فِيْ بُطُوْنِ اُمَّهٰتِكُمْ خَلْقًا مِّنْۢ بَعْدِ خَلْقٍ فِيْ ظُلُمٰتٍ ثَلٰثٍۗ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ لَهُ الْمُلْكُۗ لَآ اِلٰهَ اِلَّا هُوَۚ فَاَنّٰى تُصْرَفُوْنَ","bc":"khalaqakum min nafsiw wāḥidatin ṡumma ja'ala min-hā zaujahā wa anzala lakum minal-an'āmi ṡamāniyata azwāj, yakhluqukum fī buṭụni ummahātikum khalqam mim ba'di khalqin fī ẓulumātin ṡalāṡ, żālikumullāhu rabbukum lahul-mulk, lā ilāha illā huw, fa annā tuṣrafụn","at":"Dia menciptakan kamu dari diri yang satu (Adam) kemudian darinya Dia jadikan pasangannya dan Dia menurunkan delapan pasang hewan ternak untukmu. Dia menjadikan kamu dalam perut ibumu kejadian demi kejadian dalam tiga kegelapan. Yang (berbuat) demikian itu adalah Allah, Tuhan kamu, Tuhan yang memiliki kerajaan. Tidak ada tuhan selain Dia; maka mengapa kamu dapat dipalingkan?"},{"id":"7","ar":"اِنْ تَكْفُرُوْا فَاِنَّ اللّٰهَ غَنِيٌّ عَنْكُمْ ۗوَلَا يَرْضٰى لِعِبَادِهِ الْكُفْرَۚ وَاِنْ تَشْكُرُوْا يَرْضَهُ لَكُمْۗ وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰىۗ ثُمَّ اِلٰى رَبِّكُمْ مَّرْجِعُكُمْ فَيُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَۗ اِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ","bc":"in takfurụ fa innallāha ganiyyun 'angkum, wa lā yarḍā li'ibādihil-kufr, wa in tasykurụ yarḍahu lakum, wa lā taziru wāziratuw wizra ukhrā, ṡumma ilā rabbikum marji'ukum fa yunabbi`ukum bimā kuntum ta'malụn, innahụ 'alīmum biżātiṣ-ṣudụr","at":"Jika kamu kafir (ketahuilah) maka sesungguhnya Allah tidak memerlukanmu dan Dia tidak meridai kekafiran hamba-hamba-Nya. Jika kamu bersyukur, Dia meridai kesyukuranmu itu. Seseorang yang berdosa tidak memikul dosa orang lain. Kemudian kepada Tuhanmulah kembalimu lalu Dia beritakan kepadamu apa yang telah kamu kerjakan. Sungguh, Dia Maha Mengetahui apa yang tersimpan dalam dada(mu)."},{"id":"8","ar":"وَاِذَا مَسَّ الْاِنْسَانَ ضُرٌّ دَعَا رَبَّهٗ مُنِيْبًا اِلَيْهِ ثُمَّ اِذَا خَوَّلَهٗ نِعْمَةً مِّنْهُ نَسِيَ مَا كَانَ يَدْعُوْٓا اِلَيْهِ مِنْ قَبْلُ وَجَعَلَ لِلّٰهِ اَنْدَادًا لِّيُضِلَّ عَنْ سَبِيْلِهٖ ۗ قُلْ تَمَتَّعْ بِكُفْرِكَ قَلِيْلًا ۖاِنَّكَ مِنْ اَصْحٰبِ النَّارِ","bc":"wa iżā massal-insāna ḍurrun da'ā rabbahụ munīban ilaihi ṡumma iżā khawwalahụ ni'matam min-hu nasiya mā kāna yad'ū ilaihi ming qablu wa ja'ala lillāhi andādal liyuḍilla 'an sabīlih, qul tamatta' bikufrika qalīlan innaka min aṣ-ḥābin-nār","at":"Dan apabila manusia ditimpa bencana, dia memohon (pertolongan) kepada Tuhannya dengan kembali (taat) kepada-Nya; tetapi apabila Dia memberikan nikmat kepadanya dia lupa (akan bencana) yang pernah dia berdoa kepada Allah sebelum itu, dan diadakannya sekutu-sekutu bagi Allah untuk menyesatkan (manusia) dari jalan-Nya. Katakanlah, “Bersenang-senanglah kamu dengan kekafiranmu itu untuk sementara waktu. Sungguh, kamu termasuk penghuni neraka.”"},{"id":"9","ar":"اَمَّنْ هُوَ قَانِتٌ اٰنَاۤءَ الَّيْلِ سَاجِدًا وَّقَاۤىِٕمًا يَّحْذَرُ الْاٰخِرَةَ وَيَرْجُوْا رَحْمَةَ رَبِّهٖۗ قُلْ هَلْ يَسْتَوِى الَّذِيْنَ يَعْلَمُوْنَ وَالَّذِيْنَ لَا يَعْلَمُوْنَ ۗ اِنَّمَا يَتَذَكَّرُ اُولُوا الْاَلْبَابِ","bc":"am man huwa qānitun ānā`al-laili sājidaw wa qā`imay yaḥżarul-ākhirata wa yarjụ raḥmata rabbih, qul hal yastawillażīna ya'lamụna wallażīna lā ya'lamụn, innamā yatażakkaru ulul-albāb","at":"(Apakah kamu orang musyrik yang lebih beruntung) ataukah orang yang beribadah pada waktu malam dengan sujud dan berdiri, karena takut kepada (azab) akhirat dan mengharapkan rahmat Tuhannya? Katakanlah, “Apakah sama orang-orang yang mengetahui dengan orang-orang yang tidak mengetahui?” Sebenarnya hanya orang yang berakal sehat yang dapat menerima pelajaran."},{"id":"10","ar":"قُلْ يٰعِبَادِ الَّذِيْنَ اٰمَنُوا اتَّقُوْا رَبَّكُمْ ۗلِلَّذِيْنَ اَحْسَنُوْا فِيْ هٰذِهِ الدُّنْيَا حَسَنَةٌ ۗوَاَرْضُ اللّٰهِ وَاسِعَةٌ ۗاِنَّمَا يُوَفَّى الصّٰبِرُوْنَ اَجْرَهُمْ بِغَيْرِ حِسَابٍ ","bc":"qul yā 'ibādillażīna āmanuttaqụ rabbakum, lillażīna aḥsanụ fī hāżihid-dun-yā ḥasanah, wa arḍullāhi wāsi'ah, innamā yuwaffaṣ-ṣābirụna ajrahum bigairi ḥisāb","at":"Katakanlah (Muhammad), “Wahai hamba-hamba-Ku yang beriman! Bertakwalah kepada Tuhanmu.” Bagi orang-orang yang berbuat baik di dunia ini akan memperoleh kebaikan. Dan bumi Allah itu luas. Hanya orang-orang yang bersabarlah yang disempurnakan pahalanya tanpa batas."},{"id":"11","ar":"قُلْ اِنِّيْٓ اُمِرْتُ اَنْ اَعْبُدَ اللّٰهَ مُخْلِصًا لَّهُ الدِّيْنَ","bc":"qul innī umirtu an a'budallāha mukhliṣal lahud-dīn","at":"Katakanlah, “Sesungguhnya aku diperintahkan agar menyembah Allah dengan penuh ketaatan kepada-Nya dalam (menjalankan) agama."},{"id":"12","ar":"وَاُمِرْتُ لِاَنْ اَكُوْنَ اَوَّلَ الْمُسْلِمِيْنَ","bc":"wa umirtu li`an akụna awwalal-muslimīn","at":"Dan aku diperintahkan agar menjadi orang yang pertama-tama berserah diri.”"},{"id":"13","ar":"قُلْ اِنِّيْٓ اَخَافُ اِنْ عَصَيْتُ رَبِّيْ عَذَابَ يَوْمٍ عَظِيْمٍ","bc":"qul innī akhāfu in 'aṣaitu rabbī 'ażāba yaumin 'aẓīm","at":"Katakanlah, “Sesungguhnya aku takut akan azab pada hari yang besar jika aku durhaka kepada Tuhanku.”"},{"id":"14","ar":"قُلِ اللّٰهَ اَعْبُدُ مُخْلِصًا لَّهٗ دِيْنِيْۚ","bc":"qulillāha a'budu mukhliṣal lahụ dīnī","at":"Katakanlah, “Hanya Allah yang aku sembah dengan penuh ketaatan kepada-Nya dalam (menjalankan) agamaku.”"},{"id":"15","ar":"فَاعْبُدُوْا مَا شِئْتُمْ مِّنْ دُوْنِهٖۗ قُلْ اِنَّ الْخٰسِرِيْنَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ وَاَهْلِيْهِمْ يَوْمَ الْقِيٰمَةِۗ اَلَا ذٰلِكَ هُوَ الْخُسْرَانُ الْمُبِيْنُ","bc":"fa'budụ mā syi`tum min dụnih, qul innal-khāsirīnallażīna khasirū anfusahum wa ahlīhim yaumal-qiyāmah, alā żālika huwal-khusrānul-mubīn","at":"Maka sembahlah selain Dia sesukamu! (wahai orang-orang musyrik). Katakanlah, “Sesungguhnya orang-orang yang rugi ialah orang-orang yang merugikan diri mereka sendiri dan keluarganya pada hari Kiamat.” Ingatlah! Yang demikian itu adalah kerugian yang nyata."},{"id":"16","ar":"لَهُمْ مِّنْ فَوْقِهِمْ ظُلَلٌ مِّنَ النَّارِ وَمِنْ تَحْتِهِمْ ظُلَلٌ ۗذٰلِكَ يُخَوِّفُ اللّٰهُ بِهٖ عِبَادَهٗ ۗيٰعِبَادِ فَاتَّقُوْنِ","bc":"lahum min fauqihim ẓulalum minan-nāri wa min taḥtihim ẓulal, żālika yukhawwifullāhu bihī 'ibādah, yā 'ibādi fattaqụn","at":"Di atas mereka ada lapisan-lapisan dari api dan di bawahnya juga ada lapisan-lapisan yang disediakan bagi mereka. Demikianlah Allah mengancam hamba-hamba-Nya (dengan azab itu). “Wahai hamba-hamba-Ku, maka bertakwalah kepada-Ku.”"},{"id":"17","ar":"وَالَّذِيْنَ اجْتَنَبُوا الطَّاغُوْتَ اَنْ يَّعْبُدُوْهَا وَاَنَابُوْٓا اِلَى اللّٰهِ لَهُمُ الْبُشْرٰىۚ فَبَشِّرْ عِبَادِۙ","bc":"wallażīnajtanabuṭ-ṭāgụta ay ya'budụhā wa anābū ilallāhi lahumul-busyrā, fa basysyir 'ibād","at":"Dan orang-orang yang menjauhi tagut (yaitu) tidak menyembahnya dan kembali kepada Allah, mereka pantas mendapat berita gembira; sebab itu sampaikanlah kabar gembira itu kepada hamba-hamba-Ku,"},{"id":"18","ar":"الَّذِيْنَ يَسْتَمِعُوْنَ الْقَوْلَ فَيَتَّبِعُوْنَ اَحْسَنَهٗ ۗ اُولٰۤىِٕكَ الَّذِيْنَ هَدٰىهُمُ اللّٰهُ وَاُولٰۤىِٕكَ هُمْ اُولُوا الْاَلْبَابِ","bc":"allażīna yastami'ụnal-qaula fa yattabi'ụna aḥsanah, ulā`ikallażīna hadāhumullāhu wa ulā`ika hum ulul-albāb","at":"(yaitu) mereka yang mendengarkan perkataan lalu mengikuti apa yang paling baik di antaranya. Mereka itulah orang-orang yang telah diberi petunjuk oleh Allah dan mereka itulah orang-orang yang mempunyai akal sehat."},{"id":"19","ar":"اَفَمَنْ حَقَّ عَلَيْهِ كَلِمَةُ الْعَذَابِۗ اَفَاَنْتَ تُنْقِذُ مَنْ فِى النَّارِ ۚ","bc":"a fa man ḥaqqa 'alaihi kalimatul-'ażāb, a fa anta tungqiżu man fin-nār","at":"Maka apakah (engkau hendak mengubah nasib) orang-orang yang telah dipastikan mendapat azab? Apakah engkau (Muhammad) akan menyelamatkan orang yang berada dalam api neraka?"},{"id":"20","ar":"لٰكِنِ الَّذِيْنَ اتَّقَوْا رَبَّهُمْ لَهُمْ غُرَفٌ مِّنْ فَوْقِهَا غُرَفٌ مَّبْنِيَّةٌ ۙتَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ەۗ وَعْدَ اللّٰهِ ۗ لَا يُخْلِفُ اللّٰهُ الْمِيْعَادَ","bc":"lākinillażīnattaqau rabbahum lahum gurafum min fauqihā gurafum mabniyyatun tajrī min taḥtihal-an-hār, wa'dallāh, lā yukhlifullāhul-mī'ād","at":"Tetapi orang-orang yang bertakwa kepada Tuhannya, mereka mendapat kamar-kamar (di surga), di atasnya terdapat pula kamar-kamar yang dibangun (bertingkat-tingkat), yang mengalir di bawahnya sungai-sungai. (Itulah) janji Allah. Allah tidak akan memungkiri janji(-Nya)."},{"id":"21","ar":"اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَسَلَكَهٗ يَنَابِيْعَ فِى الْاَرْضِ ثُمَّ يُخْرِجُ بِهٖ زَرْعًا مُّخْتَلِفًا اَلْوَانُهٗ ثُمَّ يَهِيْجُ فَتَرٰىهُ مُصْفَرًّا ثُمَّ يَجْعَلُهٗ حُطَامًا ۗاِنَّ فِيْ ذٰلِكَ لَذِكْرٰى لِاُولِى الْاَلْبَابِ ","bc":"a lam tara annallāha anzala minas-samā`i mā`an fa salakahụ yanābī'a fil-arḍi ṡumma yukhriju bihī zar'am mukhtalifan alwānuhụ ṡumma yahīju fa tarāhu muṣfarran ṡumma yaj'aluhụ huṭāmā, inna fī żālika lażikrā li`ulil-albāb","at":"Apakah engkau tidak memperhatikan, bahwa Allah menurunkan air dari langit, lalu diaturnya menjadi sumber-sumber air di bumi, kemudian dengan air itu ditumbuhkan-Nya tanam-tanaman yang bermacam-macam warnanya, kemudian menjadi kering, lalu engkau melihatnya kekuning-kuningan, kemudian dijadikan-Nya hancur berderai-derai. Sungguh, pada yang demikian itu terdapat pelajaran bagi orang-orang yang mempunyai akal sehat."},{"id":"22","ar":"اَفَمَنْ شَرَحَ اللّٰهُ صَدْرَهٗ لِلْاِسْلَامِ فَهُوَ عَلٰى نُوْرٍ مِّنْ رَّبِّهٖ ۗفَوَيْلٌ لِّلْقٰسِيَةِ قُلُوْبُهُمْ مِّنْ ذِكْرِ اللّٰهِ ۗ اُولٰۤىِٕكَ فِيْ ضَلٰلٍ مُّبِيْنٍ","bc":"a fa man syaraḥallāhu ṣadrahụ lil-islāmi fa huwa 'alā nụrim mir rabbih, fa wailul lil-qāsiyati qulụbuhum min żikrillāh, ulā`ika fī ḍalālim mubīn","at":"Maka apakah orang-orang yang dibukakan hatinya oleh Allah untuk (menerima) agama Islam lalu dia mendapat cahaya dari Tuhannya (sama dengan orang yang hatinya membatu)? Maka celakalah mereka yang hatinya telah membatu untuk mengingat Allah. Mereka itu dalam kesesatan yang nyata."},{"id":"23","ar":"اَللّٰهُ نَزَّلَ اَحْسَنَ الْحَدِيْثِ كِتٰبًا مُّتَشَابِهًا مَّثَانِيَۙ تَقْشَعِرُّ مِنْهُ جُلُوْدُ الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ ۚ ثُمَّ تَلِيْنُ جُلُوْدُهُمْ وَقُلُوْبُهُمْ اِلٰى ذِكْرِ اللّٰهِ ۗذٰلِكَ هُدَى اللّٰهِ يَهْدِيْ بِهٖ مَنْ يَّشَاۤءُ ۗوَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍ","bc":"allāhu nazzala aḥsanal-ḥadīṡi kitābam mutasyābiham maṡāniya taqsya'irru min-hu julụdullażīna yakhsyauna rabbahum, ṡumma talīnu julụduhum wa qulụbuhum ilā żikrillāh, żālika hudallāhi yahdī bihī may yasyā`, wa may yuḍlilillāhu fa mā lahụ min hād","at":"Allah telah menurunkan perkataan yang paling baik (yaitu) Al-Qur'an yang serupa (ayat-ayatnya) lagi berulang-ulang, gemetar karenanya kulit orang-orang yang takut kepada Tuhannya, kemudian menjadi tenang kulit dan hati mereka ketika mengingat Allah. Itulah petunjuk Allah, dengan Kitab itu Dia memberi petunjuk kepada siapa yang Dia kehendaki. Dan barangsiapa dibiarkan sesat oleh Allah, maka tidak seorang pun yang dapat memberi petunjuk. "},{"id":"24","ar":"اَفَمَنْ يَّتَّقِيْ بِوَجْهِهٖ سُوْۤءَ الْعَذَابِ يَوْمَ الْقِيٰمَةِ ۗوَقِيْلَ لِلظّٰلِمِيْنَ ذُوْقُوْا مَا كُنْتُمْ تَكْسِبُوْنَ","bc":"a fa may yattaqī biwaj-hihī sū`al-'ażābi yaumal-qiyāmah, wa qīla liẓ-ẓālimīna żụqụ mā kuntum taksibụn","at":"Maka apakah orang-orang yang melindungi wajahnya menghindari azab yang buruk pada hari Kiamat (sama dengan orang mukmin yang tidak kena azab)? Dan dikatakan kepada orang-orang yang zalim, “Rasakanlah olehmu balasan apa yang telah kamu kerjakan.”"},{"id":"25","ar":"كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَاَتٰىهُمُ الْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُوْنَ","bc":"każżaballażīna ming qablihim fa atāhumul-'ażābu min ḥaiṡu lā yasy'urụn","at":"Orang-orang yang sebelum mereka telah mendustakan (rasul-rasul), maka datanglah kepada mereka azab dari arah yang tidak mereka sangka."},{"id":"26","ar":"فَاَذَاقَهُمُ اللّٰهُ الْخِزْيَ فِى الْحَيٰوةِ الدُّنْيَا ۚوَلَعَذَابُ الْاٰخِرَةِ اَكْبَرُ ۘ لَوْ كَانُوْا يَعْلَمُوْنَ","bc":"fa ażāqahumullāhul-khizya fil-ḥayātid-dun-yā, wa la'ażābul-ākhirati akbar, lau kānụ ya'lamụn","at":"Maka Allah menimpakan kepada mereka kehinaan pada kehidupan dunia. Dan sungguh, azab akhirat lebih besar, kalau (saja) mereka mengetahui."},{"id":"27","ar":"وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِيْ هٰذَا الْقُرْاٰنِ مِنْ كُلِّ مَثَلٍ لَّعَلَّهُمْ يَتَذَكَّرُوْنَۚ","bc":"wa laqad darabnā lin-nāsi fī hāżal-qur`āni ming kulli maṡalil la'allahum yatażakkarụn","at":"Dan sungguh, telah Kami buatkan dalam Al-Qur'an ini segala macam perumpamaan bagi manusia agar mereka dapat pelajaran."},{"id":"28","ar":"قُرْاٰنًا عَرَبِيًّا غَيْرَ ذِيْ عِوَجٍ لَّعَلَّهُمْ يَتَّقُوْنَ","bc":"qur`ānan 'arabiyyan gaira żī 'iwajil la'allahum yattaqụn","at":"(Yaitu) Al-Qur'an dalam bahasa Arab, tidak ada kebengkokan (di dalamnya) agar mereka bertakwa."},{"id":"29","ar":"ضَرَبَ اللّٰهُ مَثَلًا رَّجُلًا فِيْهِ شُرَكَاۤءُ مُتَشَاكِسُوْنَ وَرَجُلًا سَلَمًا لِّرَجُلٍ هَلْ يَسْتَوِيٰنِ مَثَلًا ۗ اَلْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ","bc":"ḍaraballāhu maṡalar rajulan fīhi syurakā`u mutasyākisụna wa rajulan salamal lirajulin hal yastawiyāni maṡalā, al-ḥamdu lillāh, bal akṡaruhum lā ya'lamụn","at":"Allah membuat perumpamaan (yaitu) seorang laki-laki (hamba sahaya) yang dimiliki oleh beberapa orang yang berserikat yang dalam perselisihan, dan seorang hamba sahaya yang menjadi milik penuh dari seorang (saja). Adakah kedua hamba sahaya itu sama keadaannya? Segala puji bagi Allah, tetapi kebanyakan mereka tidak mengetahui."},{"id":"30","ar":"اِنَّكَ مَيِّتٌ وَّاِنَّهُمْ مَّيِّتُوْنَ ۖ","bc":"innaka mayyituw wa innahum mayyitụn","at":"Sesungguhnya engkau (Muhammad) akan mati dan mereka akan mati (pula)."},{"id":"31","ar":"ثُمَّ اِنَّكُمْ يَوْمَ الْقِيٰمَةِ عِنْدَ رَبِّكُمْ تَخْتَصِمُوْنَ ۔","bc":"ṡumma innakum yaumal-qiyāmati 'inda rabbikum takhtaṣimụn","at":"Kemudian sesungguhnya kamu pada hari Kiamat akan berbantah-bantahan di hadapan Tuhanmu."},{"id":"32","ar":"۞ فَمَنْ اَظْلَمُ مِمَّنْ كَذَبَ عَلَى اللّٰهِ وَكَذَّبَ بِالصِّدْقِ اِذْ جَاۤءَهٗۗ اَلَيْسَ فِيْ جَهَنَّمَ مَثْوًى لِّلْكٰفِرِيْنَ ","bc":"fa man aẓlamu mim mang każaba 'alallāhi wa każżaba biṣ-ṣidqi iż jā`ah, a laisa fī jahannama maṡwal lil-kāfirīn","at":"Maka siapakah yang lebih zalim daripada orang yang membuat-buat kebohongan terhadap Allah dan mendustakan kebenaran yang datang kepadanya? Bukankah di neraka Jahanam tempat tinggal bagi orang-orang kafir?"},{"id":"33","ar":"وَالَّذِيْ جَاۤءَ بِالصِّدْقِ وَصَدَّقَ بِهٖٓ اُولٰۤىِٕكَ هُمُ الْمُتَّقُوْنَ","bc":"wallażī jā`a biṣ-ṣidqi wa ṣaddaqa bihī ulā`ika humul-muttaqụn","at":"Dan orang yang membawa kebenaran (Muhammad) dan orang yang membenarkannya, mereka itulah orang yang bertakwa."},{"id":"34","ar":"لَهُمْ مَّا يَشَاۤءُوْنَ عِنْدَ رَبِّهِمْ ۗ ذٰلِكَ جَزٰۤؤُا الْمُحْسِنِيْنَۚ","bc":"lahum mā yasyā`ụna 'inda rabbihim, żālika jazā`ul-muḥsinīn","at":"Mereka memperoleh apa yang mereka kehendaki di sisi Tuhannya. Demikianlah balasan bagi orang-orang yang berbuat baik,"},{"id":"35","ar":"لِيُكَفِّرَ اللّٰهُ عَنْهُمْ اَسْوَاَ الَّذِيْ عَمِلُوْا وَيَجْزِيَهُمْ اَجْرَهُمْ بِاَحْسَنِ الَّذِيْ كَانُوْا يَعْمَلُوْنَ","bc":"liyukaffirallāhu 'an-hum aswa`allażī 'amilụ wa yajziyahum ajrahum bi-aḥsanillażī kānụ ya'malụn","at":"agar Allah menghapus perbuatan mereka yang paling buruk yang pernah mereka lakukan dan memberi pahala kepada mereka dengan yang lebih baik daripada apa yang mereka kerjakan."},{"id":"36","ar":"اَلَيْسَ اللّٰهُ بِكَافٍ عَبْدَهٗۗ وَيُخَوِّفُوْنَكَ بِالَّذِيْنَ مِنْ دُوْنِهٖۗ وَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍۚ","bc":"a laisallāhu bikāfin 'abdah, wa yukhawwifụnaka billażīna min dụnih, wa may yuḍlilillāhu fa mā lahụ min hād","at":"Bukankah Allah yang mencukupi hamba-Nya? Mereka menakut-nakutimu dengan (sesembahan) yang selain Dia. Barangsiapa dibiarkan sesat oleh Allah maka tidak seorang pun yang dapat memberi petunjuk kepadanya."},{"id":"37","ar":"وَمَنْ يَّهْدِ اللّٰهُ فَمَا لَهٗ مِنْ مُّضِلٍّ ۗ اَلَيْسَ اللّٰهُ بِعَزِيْزٍ ذِى انْتِقَامٍ","bc":"wa may yahdillāhu fa mā lahụ mim muḍill, a laisallāhu bi'azīzin żintiqām","at":"Dan barangsiapa diberi petunjuk oleh Allah, maka tidak seorang pun yang dapat menyesatkannya. Bukankah Allah Mahaperkasa dan mempunyai (kekuasaan untuk) menghukum?"},{"id":"38","ar":"وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ لَيَقُوْلُنَّ اللّٰهُ ۗ قُلْ اَفَرَءَيْتُمْ مَّا تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ اِنْ اَرَادَنِيَ اللّٰهُ بِضُرٍّ هَلْ هُنَّ كٰشِفٰتُ ضُرِّهٖٓ اَوْ اَرَادَنِيْ بِرَحْمَةٍ هَلْ هُنَّ مُمْسِكٰتُ رَحْمَتِهٖۗ قُلْ حَسْبِيَ اللّٰهُ ۗعَلَيْهِ يَتَوَكَّلُ الْمُتَوَكِّلُوْنَ ","bc":"wa la`in sa`altahum man khalaqas-samāwāti wal-arḍa layaqụlunnallāh, qul a fa ra`aitum mā tad'ụna min dụnillāhi in arādaniyallāhu biḍurrin hal hunna kāsyifātu ḍurrihī au arādanī biraḥmatin hal hunna mumsikātu raḥmatih, qul ḥasbiyallāh, 'alaihi yatawakkalul-mutawakkilụn","at":"Dan sungguh, jika engkau tanyakan kepada mereka, “Siapakah yang menciptakan langit dan bumi?” Niscaya mereka menjawab, “Allah.” Katakanlah, “Kalau begitu tahukah kamu tentang apa yang kamu sembah selain Allah, jika Allah hendak mendatangkan bencana kepadaku, apakah mereka mampu menghilangkan bencana itu, atau jika Allah hendak memberi rahmat kepadaku, apakah mereka dapat mencegah rahmat-Nya?” Katakanlah, “Cukuplah Allah bagiku. Kepada-Nyalah orang-orang yang bertawakal berserah diri.”"},{"id":"39","ar":"قُلْ يٰقَوْمِ اعْمَلُوْا عَلٰى مَكَانَتِكُمْ اِنِّيْ عَامِلٌ ۚفَسَوْفَ تَعْلَمُوْنَۙ","bc":"qul yā qaumi'malụ 'alā makānatikum innī 'āmil, fa saufa ta'lamụn","at":"Katakanlah (Muhammad), “Wahai kaumku! Berbuatlah menurut kedudukanmu, aku pun berbuat (demikian). Kelak kamu akan mengetahui,"},{"id":"40","ar":"مَنْ يَّأْتِيْهِ عَذَابٌ يُّخْزِيْهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُّقِيْمٌ","bc":"may ya`tīhi 'ażābuy yukhzīhi wa yaḥillu 'alaihi 'ażābum muqīm","at":"siapa yang mendapat siksa yang menghinakan dan kepadanya ditimpakan azab yang kekal.”"},{"id":"41","ar":"اِنَّآ اَنْزَلْنَا عَلَيْكَ الْكِتٰبَ لِلنَّاسِ بِالْحَقِّۚ فَمَنِ اهْتَدٰى فَلِنَفْسِهٖۚ وَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَا ۚوَمَآ اَنْتَ عَلَيْهِمْ بِوَكِيْلٍ","bc":"innā anzalnā 'alaikal-kitāba lin-nāsi bil-ḥaqq, fa manihtadā fa linafsih, wa man ḍalla fa innamā yaḍillu 'alaihā, wa mā anta 'alaihim biwakīl","at":"Sungguh, Kami menurunkan kepadamu Kitab (Al-Qur'an) dengan membawa kebenaran untuk manusia; barangsiapa mendapat petunjuk maka (petunjuk itu) untuk dirinya sendiri, dan siapa sesat maka sesungguhnya kesesatan itu untuk dirinya sendiri, dan engkau bukanlah orang yang bertanggung jawab terhadap mereka."},{"id":"42","ar":"اَللّٰهُ يَتَوَفَّى الْاَنْفُسَ حِيْنَ مَوْتِهَا وَالَّتِيْ لَمْ تَمُتْ فِيْ مَنَامِهَا ۚ فَيُمْسِكُ الَّتِي قَضٰى عَلَيْهَا الْمَوْتَ وَيُرْسِلُ الْاُخْرٰىٓ اِلٰٓى اَجَلٍ مُّسَمًّىۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّتَفَكَّرُوْنَ","bc":"allāhu yatawaffal-anfusa ḥīna mautihā wallatī lam tamut fī manāmihā, fa yumsikullatī qaḍā 'alaihal-mauta wa yursilul-ukhrā ilā ajalim musammā, inna fī żālika la`āyātil liqaumiy yatafakkarụn","at":"Allah memegang nyawa (seseorang) pada saat kematiannya dan nyawa (seseorang) yang belum mati ketika dia tidur; maka Dia tahan nyawa (orang) yang telah Dia tetapkan kematiannya dan Dia lepaskan nyawa yang lain sampai waktu yang ditentukan. Sungguh, pada yang demikian itu terdapat tanda-tanda (kebesaran) Allah bagi kaum yang berpikir."},{"id":"43","ar":"اَمِ اتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ شُفَعَاۤءَۗ قُلْ اَوَلَوْ كَانُوْا لَا يَمْلِكُوْنَ شَيْـًٔا وَّلَا يَعْقِلُوْنَ ","bc":"amittakhażụ min dụnillāhi syufa'ā`, qul a walau kānụ lā yamlikụna syai`aw wa lā ya'qilụn","at":"Ataukah mereka mengambil penolong selain Allah. Katakanlah, “Apakah (kamu mengambilnya juga) meskipun mereka tidak memiliki sesuatu apa pun dan tidak mengerti?”"},{"id":"44","ar":"قُلْ لِّلّٰهِ الشَّفَاعَةُ جَمِيْعًا ۗ لَهٗ مُلْكُ السَّمٰوٰتِ وَالْاَرْضِۗ ثُمَّ اِلَيْهِ تُرْجَعُوْنَ","bc":"qul lillahisy-syafā'atu jamī'ā, lahụ mulkus-samāwāti wal-arḍ, ṡumma ilaihi turja'ụn","at":"Katakanlah, “Pertolongan itu hanya milik Allah semuanya. Dia memiliki kerajaan langit dan bumi. Kemudian kepada-Nya kamu dikembalikan.”"},{"id":"45","ar":"وَاِذَا ذُكِرَ اللّٰهُ وَحْدَهُ اشْمَـَٔزَّتْ قُلُوْبُ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِۚ وَاِذَا ذُكِرَ الَّذِيْنَ مِنْ دُوْنِهٖٓ اِذَا هُمْ يَسْتَبْشِرُوْنَ","bc":"wa iżā żukirallāhu waḥdahusyma`azzat qulụbullażīna lā yu`minụna bil-ākhirah, wa iżā żukirallażīna min dụnihī iżā hum yastabsyirụn","at":"Dan apabila yang disebut hanya nama Allah, kesal sekali hati orang-orang yang tidak beriman kepada akhirat. Namun apabila nama-nama sembahan selain Allah yang disebut, tiba-tiba mereka menjadi bergembira."},{"id":"46","ar":"قُلِ اللهم فَاطِرَ السَّمٰوٰتِ وَالْاَرْضِ عٰلِمَ الْغَيْبِ وَالشَّهَادَةِ اَنْتَ تَحْكُمُ بَيْنَ عِبَادِكَ فِيْ مَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ","bc":"qulillāhumma fāṭiras-samāwāti wal-arḍi 'ālimal-gaibi wasy-syahādati anta taḥkumu baina 'ibādika fī mā kānụ fīhi yakhtalifụn","at":"Katakanlah, “Ya Allah, Pencipta langit dan bumi, yang mengetahui segala yang gaib dan yang nyata, Engkaulah yang memutuskan di antara hamba-hamba-Mu tentang apa yang selalu mereka perselisihkan.”"},{"id":"47","ar":"وَلَوْ اَنَّ لِلَّذِيْنَ ظَلَمُوْا مَا فِى الْاَرْضِ جَمِيْعًا وَّمِثْلَهٗ مَعَهٗ لَافْتَدَوْا بِهٖ مِنْ سُوْۤءِ الْعَذَابِ يَوْمَ الْقِيٰمَةِۗ وَبَدَا لَهُمْ مِّنَ اللّٰهِ مَا لَمْ يَكُوْنُوْا يَحْتَسِبُوْنَ","bc":"walau anna lillażīna ẓalamụ mā fil-arḍi jamī'aw wa miṡlahụ ma'ahụ laftadau bihī min sū`il-'ażābi yaumal-qiyāmah, wa badā lahum minallāhi mā lam yakụnụ yaḥtasibụn","at":"Dan sekiranya orang-orang yang zalim mempunyai segala apa yang ada di bumi dan ditambah lagi sebanyak itu, niscaya mereka akan menebus dirinya dengan itu dari azab yang buruk pada hari Kiamat. Dan jelaslah bagi mereka azab dari Allah yang dahulu tidak pernah mereka perkirakan."},{"id":"48","ar":"وَبَدَا لَهُمْ سَيِّاٰتُ مَا كَسَبُوْا وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ","bc":"wa badā lahum sayyi`ātu mā kasabụ wa ḥāqa bihim mā kānụ bihī yastahzi`ụn","at":"Dan jelaslah bagi mereka kejahatan apa yang telah mereka kerjakan dan mereka diliputi oleh apa yang dahulu mereka selalu memperolok-olokkannya."},{"id":"49","ar":"فَاِذَا مَسَّ الْاِنْسَانَ ضُرٌّ دَعَانَاۖ ثُمَّ اِذَا خَوَّلْنٰهُ نِعْمَةً مِّنَّاۙ قَالَ اِنَّمَآ اُوْتِيْتُهٗ عَلٰى عِلْمٍ ۗبَلْ هِيَ فِتْنَةٌ وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ","bc":"fa iżā massal-insāna ḍurrun da'ānā ṡumma iżā khawwalnāhu ni'matam minnā qāla innamā ụtītuhụ 'alā 'ilm, bal hiya fitnatuw wa lākinna akṡarahum lā ya'lamụn","at":"Maka apabila manusia ditimpa bencana dia menyeru Kami, kemudian apabila Kami berikan nikmat Kami kepadanya dia berkata, “Sesungguhnya aku diberi nikmat ini hanyalah karena kepintaranku.” Sebenarnya, itu adalah ujian, tetapi kebanyakan mereka tidak mengetahui."},{"id":"50","ar":"قَدْ قَالَهَا الَّذِيْنَ مِنْ قَبْلِهِمْ فَمَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يَكْسِبُوْنَ","bc":"qad qālahallażīna ming qablihim fa mā agnā 'an-hum mā kānụ yaksibụn","at":"Sungguh, orang-orang yang sebelum mereka pun telah mengatakan hal itu, maka tidak berguna lagi bagi mereka apa yang dahulu mereka kerjakan."},{"id":"51","ar":"فَاَصَابَهُمْ سَيِّاٰتُ مَا كَسَبُوْا ۗوَالَّذِيْنَ ظَلَمُوْا مِنْ هٰٓؤُلَاۤءِ سَيُصِيْبُهُمْ سَيِّاٰتُ مَا كَسَبُوْا ۙوَمَا هُمْ بِمُعْجِزِيْنَ","bc":"fa aṣābahum sayyi`ātu mā kasabụ, wallażīna ẓalamụ min hā`ulā`i sayuṣībuhum sayyi`ātu mā kasabụ wa mā hum bimu'jizīn","at":"Lalu mereka ditimpa (bencana) dari akibat buruk apa yang mereka perbuat. Dan orang-orang yang zalim di antara mereka juga akan ditimpa (bencana) dari akibat buruk apa yang mereka kerjakan dan mereka tidak dapat melepaskan diri."},{"id":"52","ar":"اَوَلَمْ يَعْلَمُوْٓا اَنَّ اللّٰهَ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يُّؤْمِنُوْنَ","bc":"a wa lam ya'lamū annallāha yabsuṭur-rizqa limay yasyā`u wa yaqdir, inna fī żālika la`āyātil liqaumiy yu`minụn","at":"Dan tidakkah mereka mengetahui bahwa Allah melapangkan rezeki bagi siapa yang Dia kehendaki dan membatasinya (bagi siapa yang Dia kehendaki)? Sesungguhnya pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi kaum yang beriman."},{"id":"53","ar":"۞ قُلْ يٰعِبَادِيَ الَّذِيْنَ اَسْرَفُوْا عَلٰٓى اَنْفُسِهِمْ لَا تَقْنَطُوْا مِنْ رَّحْمَةِ اللّٰهِ ۗاِنَّ اللّٰهَ يَغْفِرُ الذُّنُوْبَ جَمِيْعًا ۗاِنَّهٗ هُوَ الْغَفُوْرُ الرَّحِيْمُ","bc":"qul yā 'ibādiyallażīna asrafụ 'alā anfusihim lā taqnaṭụ mir raḥmatillāh, innallāha yagfiruż-żunụba jamī'ā, innahụ huwal-gafụrur-raḥīm","at":"Katakanlah, “Wahai hamba-hamba-Ku yang melampaui batas terhadap diri mereka sendiri! Janganlah kamu berputus asa dari rahmat Allah. Sesungguhnya Allah mengampuni dosa-dosa semuanya. Sungguh, Dialah Yang Maha Pengampun, Maha Penyayang."},{"id":"54","ar":"وَاَنِيْبُوْٓا اِلٰى رَبِّكُمْ وَاَسْلِمُوْا لَهٗ مِنْ قَبْلِ اَنْ يَّأْتِيَكُمُ الْعَذَابُ ثُمَّ لَا تُنْصَرُوْنَ","bc":"wa anībū ilā rabbikum wa aslimụ lahụ ming qabli ay ya`tiyakumul-'ażābu ṡumma lā tunṣarụn","at":"Dan kembalilah kamu kepada Tuhanmu, dan berserah dirilah kepada-Nya sebelum datang azab kepadamu, kemudian kamu tidak dapat ditolong."},{"id":"55","ar":"وَاتَّبِعُوْٓا اَحْسَنَ مَآ اُنْزِلَ اِلَيْكُمْ مِّنْ رَّبِّكُمْ مِّنْ قَبْلِ اَنْ يَّأْتِيَكُمُ الْعَذَابُ بَغْتَةً وَّاَنْتُمْ لَا تَشْعُرُوْنَ ۙ ","bc":"wattabi'ū aḥsana mā unzila ilaikum mir rabbikum ming qabli ay ya`tiyakumul-'ażābu bagtataw wa antum lā tasy'urụn","at":"Dan ikutilah sebaik-baik apa yang telah diturunkan kepadamu (Al-Qur'an) dari Tuhanmu sebelum datang azab kepadamu secara mendadak, sedang kamu tidak menyadarinya,"},{"id":"56","ar":"اَنْ تَقُوْلَ نَفْسٌ يّٰحَسْرَتٰى عَلٰى مَا فَرَّطْتُّ فِيْ جَنْۢبِ اللّٰهِ وَاِنْ كُنْتُ لَمِنَ السَّاخِرِيْنَۙ ","bc":"an taqụla nafsuy yā ḥasratā 'alā mā farrattu fī jambillāhi wa ing kuntu laminas-sākhirīn","at":"agar jangan ada orang yang mengatakan, ‘Alangkah besar penyesalanku atas kelalaianku dalam (menunaikan kewajiban) terhadap Allah, dan sesungguhnya aku termasuk orang-orang yang memperolok-olokkan (agama Allah),’"},{"id":"57","ar":"اَوْ تَقُوْلَ لَوْ اَنَّ اللّٰهَ هَدٰىنِيْ لَكُنْتُ مِنَ الْمُتَّقِيْنَ ۙ","bc":"au taqụla lau annallāha hadānī lakuntu minal-muttaqīn","at":"atau (agar jangan) ada yang berkata, ‘Sekiranya Allah memberi petunjuk kepadaku tentulah aku termasuk orang-orang yang bertakwa,’"},{"id":"58","ar":"اَوْ تَقُوْلَ حِيْنَ تَرَى الْعَذَابَ لَوْ اَنَّ لِيْ كَرَّةً فَاَكُوْنَ مِنَ الْمُحْسِنِيْنَ","bc":"au taqụla ḥīna taral-'ażāba lau anna lī karratan fa akụna minal-muḥsinīn","at":"atau (agar jangan) ada yang berkata ketika melihat azab, ‘Sekiranya aku dapat kembali (ke dunia), tentu aku termasuk orang-orang yang berbuat baik.’"},{"id":"59","ar":"بَلٰى قَدْ جَاۤءَتْكَ اٰيٰتِيْ فَكَذَّبْتَ بِهَا وَاسْتَكْبَرْتَ وَكُنْتَ مِنَ الْكٰفِرِيْنَ","bc":"balā qad jā`atka āyātī fa każżabta bihā wastakbarta wa kunta minal-kāfirīn","at":"Sungguh, sebenarnya keterangan-keterangan-Ku telah datang kepadamu, tetapi kamu mendustakannya, malah kamu menyombongkan diri dan termasuk orang kafir.”"},{"id":"60","ar":"وَيَوْمَ الْقِيٰمَةِ تَرَى الَّذِيْنَ كَذَبُوْا عَلَى اللّٰهِ وُجُوْهُهُمْ مُّسْوَدَّةٌ ۗ اَلَيْسَ فِيْ جَهَنَّمَ مَثْوًى لِّلْمُتَكَبِّرِيْنَ","bc":"wa yaumal-qiyāmati tarallażīna każabụ 'alallāhi wujụhuhum muswaddah, a laisa fī jahannama maṡwal lil-mutakabbirīn","at":"Dan pada hari Kiamat engkau akan melihat orang-orang yang berbuat dusta terhadap Allah, wajahnya menghitam. Bukankah neraka Jahanam itu tempat tinggal bagi orang yang menyombongkan diri?"},{"id":"61","ar":"وَيُنَجِّى اللّٰهُ الَّذِيْنَ اتَّقَوْا بِمَفَازَتِهِمْۖ لَا يَمَسُّهُمُ السُّوْۤءُ وَلَا هُمْ يَحْزَنُوْنَ","bc":"wa yunajjillāhullażīnattaqau bimafāzatihim lā yamassuhumus-sū`u wa lā hum yaḥzanụn","at":"Dan Allah menyelamatkan orang-orang yang bertakwa karena kemenangan mereka. Mereka tidak disentuh oleh azab dan tidak bersedih hati."},{"id":"62","ar":"اَللّٰهُ خَالِقُ كُلِّ شَيْءٍ ۙوَّهُوَ عَلٰى كُلِّ شَيْءٍ وَّكِيْلٌ","bc":"allāhu khāliqu kulli syai`iw wa huwa 'alā kulli syai`iw wakīl","at":"Allah pencipta segala sesuatu dan Dia Maha Pemelihara atas segala sesuatu."},{"id":"63","ar":"لَهٗ مَقَالِيْدُ السَّمٰوٰتِ وَالْاَرْضِۗ وَالَّذِيْنَ كَفَرُوْا بِاٰيٰتِ اللّٰهِ اُولٰۤىِٕكَ هُمُ الْخٰسِرُوْنَ ","bc":"lahụ maqālīdus-samāwāti wal-arḍ, wallażīna kafarụ bi`āyātillāhi ulā`ika humul-khāsirụn","at":"Milik-Nyalah kunci-kunci (perbenda-haraan) langit dan bumi. Dan orang-orang yang kafir terhadap ayat-ayat Allah, mereka itulah orang yang rugi."},{"id":"64","ar":"قُلْ اَفَغَيْرَ اللّٰهِ تَأْمُرُوْۤنِّيْٓ اَعْبُدُ اَيُّهَا الْجٰهِلُوْنَ","bc":"qul a fa gairallāhi ta`murūnnī a'budu ayyuhal-jāhilụn","at":"Katakanlah (Muhammad), “Apakah kamu menyuruh aku menyembah selain Allah, wahai orang-orang yang bodoh?”"},{"id":"65","ar":"وَلَقَدْ اُوْحِيَ اِلَيْكَ وَاِلَى الَّذِيْنَ مِنْ قَبْلِكَۚ لَىِٕنْ اَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ وَلَتَكُوْنَنَّ مِنَ الْخٰسِرِيْنَ","bc":"wa laqad ụḥiya ilaika wa ilallażīna ming qablik, la`in asyrakta layaḥbaṭanna 'amaluka wa latakụnanna minal-khāsirīn","at":"Dan sungguh, telah diwahyukan kepadamu dan kepada (nabi-nabi) yang sebelummu, “Sungguh, jika engkau mempersekutukan (Allah), niscaya akan hapuslah amalmu dan tentulah engkau termasuk orang yang rugi."},{"id":"66","ar":"بَلِ اللّٰهَ فَاعْبُدْ وَكُنْ مِّنَ الشّٰكِرِيْنَ","bc":"balillāha fa'bud wa kum minasy-syākirīn","at":"Karena itu, hendaklah Allah saja yang engkau sembah dan hendaklah engkau termasuk orang yang bersyukur.”"},{"id":"67","ar":"وَمَا قَدَرُوا اللّٰهَ حَقَّ قَدْرِهٖۖ وَالْاَرْضُ جَمِيْعًا قَبْضَتُهٗ يَوْمَ الْقِيٰمَةِ وَالسَّمٰوٰتُ مَطْوِيّٰتٌۢ بِيَمِيْنِهٖ ۗسُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ","bc":"wa mā qadarullāha ḥaqqa qadrihī wal-arḍu jamī'ang qabḍatuhụ yaumal-qiyāmati was-samāwātu maṭwiyyātum biyamīnih, sub-ḥānahụ wa ta'ālā 'ammā yusyrikụn","at":"Dan mereka tidak mengagungkan Allah sebagaimana mestinya padahal bumi seluruhnya dalam genggaman-Nya pada hari Kiamat dan langit digulung dengan tangan kanan-Nya. Mahasuci Dia dan Mahatinggi Dia dari apa yang mereka persekutukan."},{"id":"68","ar":"وَنُفِخَ فِى الصُّوْرِ فَصَعِقَ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِ اِلَّا مَنْ شَاۤءَ اللّٰهُ ۗ ثُمَّ نُفِخَ فِيْهِ اُخْرٰى فَاِذَا هُمْ قِيَامٌ يَّنْظُرُوْنَ","bc":"wa nufikha fiṣ-ṣụri fa ṣa'iqa man fis-samāwāti wa man fil-arḍi illā man syā`allāh, ṡumma nufikha fīhi ukhrā fa iżā hum qiyāmuy yanẓurụn","at":"Dan sangkakala pun ditiup, maka matilah semua (makhluk) yang di langit dan di bumi kecuali mereka yang dikehendaki Allah. Kemudian ditiup sekali lagi (sangkakala itu) maka seketika itu mereka bangun (dari kuburnya) menunggu (keputusan Allah)."},{"id":"69","ar":"وَاَشْرَقَتِ الْاَرْضُ بِنُوْرِ رَبِّهَا وَوُضِعَ الْكِتٰبُ وَجِايْۤءَ بِالنَّبِيّٖنَ وَالشُّهَدَاۤءِ وَقُضِيَ بَيْنَهُمْ بِالْحَقِّ وَهُمْ لَا يُظْلَمُوْنَ","bc":"wa asyraqatil-arḍu binụri rabbihā wa wuḍi'al-kitābu wajī`a bin-nabiyyīna wasy-syuhadā`i wa quḍiya bainahum bil-ḥaqqi wa hum lā yuẓlamụn","at":"Dan bumi (padang Mahsyar) menjadi terang benderang dengan cahaya (keadilan) Tuhannya; dan buku-buku (perhitungan perbuatan mereka) diberikan (kepada masing-masing), nabi-nabi dan saksi-saksi pun dihadirkan, lalu diberikan keputusan di antara mereka secara adil, sedang mereka tidak dirugikan."},{"id":"70","ar":"وَوُفِّيَتْ كُلُّ نَفْسٍ مَّا عَمِلَتْ وَهُوَ اَعْلَمُ بِمَا يَفْعَلُوْنَ ","bc":"wa wuffiyat kullu nafsim mā 'amilat wa huwa a'lamu bimā yaf'alụn","at":"Dan kepada setiap jiwa diberi balasan dengan sempurna sesuai dengan apa yang telah dikerjakannya dan Dia lebih mengetahui apa yang mereka kerjakan."},{"id":"71","ar":"وَسِيْقَ الَّذِيْنَ كَفَرُوْٓا اِلٰى جَهَنَّمَ زُمَرًا ۗحَتّٰىٓ اِذَا جَاۤءُوْهَا فُتِحَتْ اَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُهَآ اَلَمْ يَأْتِكُمْ رُسُلٌ مِّنْكُمْ يَتْلُوْنَ عَلَيْكُمْ اٰيٰتِ رَبِّكُمْ وَيُنْذِرُوْنَكُمْ لِقَاۤءَ يَوْمِكُمْ هٰذَا ۗقَالُوْا بَلٰى وَلٰكِنْ حَقَّتْ كَلِمَةُ الْعَذَابِ عَلَى الْكٰفِرِيْنَ","bc":"wa sīqallażīna kafarū ilā jahannama zumarā, ḥattā iżā jā`ụhā futiḥat abwābuhā wa qāla lahum khazanatuhā a lam ya`tikum rusulum mingkum yatlụna 'alaikum āyāti rabbikum wa yunżirụnakum liqā`a yaumikum hāżā, qālụ balā wa lākin ḥaqqat kalimatul-'ażābi 'alal-kāfirīn","at":"Orang-orang yang kafir digiring ke neraka Jahanam secara berombongan. Sehingga apabila mereka sampai kepadanya (neraka) pintu-pintunya dibukakan dan penjaga-penjaga berkata kepada mereka, “Apakah belum pernah datang kepadamu rasul-rasul dari kalangan kamu yang membacakan ayat-ayat Tuhanmu dan memperingatkan kepadamu akan pertemuan (dengan) harimu ini?” Mereka menjawab, “Benar, ada,” tetapi ketetapan azab pasti berlaku terhadap orang-orang kafir."},{"id":"72","ar":"قِيْلَ ادْخُلُوْٓا اَبْوَابَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَا ۚفَبِئْسَ مَثْوَى الْمُتَكَبِّرِيْنَ","bc":"qīladkhulū abwāba jahannama khālidīna fīhā, fa bi`sa maṡwal-mutakabbirīn","at":"Dikatakan (kepada mereka), “Masukilah pintu-pintu neraka Jahanam itu, (kamu) kekal di dalamnya.” Maka (neraka Jahanam) itulah seburuk-buruk tempat tinggal bagi orang-orang yang menyombongkan diri."},{"id":"73","ar":"وَسِيْقَ الَّذِيْنَ اتَّقَوْا رَبَّهُمْ اِلَى الْجَنَّةِ زُمَرًا ۗحَتّٰىٓ اِذَا جَاۤءُوْهَا وَفُتِحَتْ اَبْوَابُهَا وَقَالَ لَهُمْ خَزَنَتُهَا سَلٰمٌ عَلَيْكُمْ طِبْتُمْ فَادْخُلُوْهَا خٰلِدِيْنَ","bc":"wa sīqallażīnattaqau rabbahum ilal-jannati zumarā, ḥattā iżā jā`ụhā wa futiḥat abwābuhā wa qāla lahum khazanatuhā salāmun 'alaikum ṭibtum fadkhulụhā khālidīn","at":"Dan orang-orang yang bertakwa kepada Tuhannya diantar ke dalam surga secara berombongan. Sehingga apabila mereka sampai kepadanya (surga) dan pintu-pintunya telah dibukakan, penjaga-penjaganya berkata kepada mereka, “Kesejahteraan (dilimpahkan) atasmu, berbahagialah kamu! Maka masuklah, kamu kekal di dalamnya.”"},{"id":"74","ar":"وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذِيْ صَدَقَنَا وَعْدَهٗ وَاَوْرَثَنَا الْاَرْضَ نَتَبَوَّاُ مِنَ الْجَنَّةِ حَيْثُ نَشَاۤءُ ۚفَنِعْمَ اَجْرُ الْعٰمِلِيْنَ","bc":"wa qālul-ḥamdu lillāhillażī ṣadaqanā wa'dahụ wa auraṡanal-arḍa natabawwa`u minal-jannati ḥaiṡu nasyā`, fa ni'ma ajrul-'āmilīn","at":"Dan mereka berkata, “Segala puji bagi Allah yang telah memenuhi janji-Nya kepada kami dan telah memberikan tempat ini kepada kami sedang kami (diperkenankan) menempati surga di mana saja yang kami kehendaki.” Maka (surga itulah) sebaik-baik balasan bagi orang-orang yang beramal."},{"id":"75","ar":"وَتَرَى الْمَلٰۤىِٕكَةَ حَاۤفِّيْنَ مِنْ حَوْلِ الْعَرْشِ يُسَبِّحُوْنَ بِحَمْدِ رَبِّهِمْۚ وَقُضِيَ بَيْنَهُمْ بِالْحَقِّ وَقِيْلَ الْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ","bc":"wa taral-malā`ikata ḥāffīna min ḥaulil-'arsyi yusabbiḥụna biḥamdi rabbihim, wa quḍiya bainahum bil-ḥaqqi wa qīlal-ḥamdu lillāhi rabbil-'ālamīn","at":"Dan engkau (Muhammad) akan melihat malaikat-malaikat melingkar di sekeliling ‘Arsy, bertasbih sambil memuji Tuhannya; lalu diberikan keputusan di antara mereka (hamba-hamba Allah) secara adil dan dikatakan, “Segala puji bagi Allah, Tuhan seluruh alam.”"}],fatir=[{"id":"1","ar":"اَلْحَمْدُ لِلّٰهِ فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِ جَاعِلِ الْمَلٰۤىِٕكَةِ رُسُلًاۙ اُولِيْٓ اَجْنِحَةٍ مَّثْنٰى وَثُلٰثَ وَرُبٰعَۗ يَزِيْدُ فِى الْخَلْقِ مَا يَشَاۤءُۗ اِنَّ اللّٰهَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"al-ḥamdu lillāhi fāṭiris-samāwāti wal-arḍi jā'ilil-malā`ikati rusulan ulī ajniḥatim maṡnā wa ṡulāṡa wa rubā', yazīdu fil-khalqi mā yasyā`, innallāha 'alā kulli syai`ing qadīr","at":"Segala puji bagi Allah Pencipta langit dan bumi, yang menjadikan malaikat sebagai utusan-utusan (untuk mengurus berbagai macam urusan) yang mempunyai sayap, masing-masing (ada yang) dua, tiga dan empat. Allah menambahkan pada ciptaan-Nya apa yang Dia kehendaki. Sungguh, Allah Mahakuasa atas segala sesuatu."},{"id":"2","ar":"مَا يَفْتَحِ اللّٰهُ لِلنَّاسِ مِنْ رَّحْمَةٍ فَلَا مُمْسِكَ لَهَا ۚوَمَا يُمْسِكْۙ فَلَا مُرْسِلَ لَهٗ مِنْۢ بَعْدِهٖۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ ","bc":"mā yaftaḥillāhu lin-nāsi mir raḥmatin fa lā mumsika lahā, wa mā yumsik fa lā mursila lahụ mim ba'dih, wa huwal-'azīzul-ḥakīm","at":"Apa saja di antara rahmat Allah yang dianugerahkan kepada manusia, maka tidak ada yang dapat menahannya; dan apa saja yang ditahan-Nya maka tidak ada yang sanggup untuk melepaskannya setelah itu. Dan Dialah Yang Mahaperkasa, Mahabijaksana."},{"id":"3","ar":"يٰٓاَيُّهَا النَّاسُ اذْكُرُوْا نِعْمَتَ اللّٰهِ عَلَيْكُمْۗ هَلْ مِنْ خَالِقٍ غَيْرُ اللّٰهِ يَرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ لَآ اِلٰهَ اِلَّا هُوَۖ فَاَنّٰى تُؤْفَكُوْنَ ","bc":"yā ayyuhan-nāsużkurụ ni'matallāhi 'alaikum, hal min khāliqin gairullāhi yarzuqukum minas-samā`i wal-arḍ, lā ilāha illā huwa fa annā tu`fakụn","at":"Wahai manusia! Ingatlah akan nikmat Allah kepadamu. Adakah pencipta selain Allah yang dapat memberikan rezeki kepadamu dari langit dan bumi? Tidak ada tuhan selain Dia; maka mengapa kamu berpaling (dari ketauhidan)?"},{"id":"4","ar":"وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِّنْ قَبْلِكَۗ وَاِلَى اللّٰهِ تُرْجَعُ الْاُمُوْرُ ","bc":"wa iy yukażżibụka fa qad kużżibat rusulum ming qablik, wa ilallāhi turja'ul-umụr","at":"Dan jika mereka mendustakan engkau (setelah engkau beri peringatan), maka sungguh, rasul-rasul sebelum engkau telah didustakan pula. Dan hanya kepada Allah segala urusan dikembalikan."},{"id":"5","ar":"يٰٓاَيُّهَا النَّاسُ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَاۗ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ الْغَرُوْرُ ","bc":"yā ayyuhan-nāsu inna wa'dallāhi ḥaqqun fa lā tagurrannakumul-ḥayātud-dun-yā, wa lā yagurrannakum billāhil-garụr","at":"Wahai manusia! Sungguh, janji Allah itu benar, maka janganlah kehidupan dunia memperdayakan kamu dan janganlah (setan) yang pandai menipu, memperdayakan kamu tentang Allah."},{"id":"6","ar":"اِنَّ الشَّيْطٰنَ لَكُمْ عَدُوٌّ فَاتَّخِذُوْهُ عَدُوًّاۗ اِنَّمَا يَدْعُوْا حِزْبَهٗ لِيَكُوْنُوْا مِنْ اَصْحٰبِ السَّعِيْرِۗ ","bc":"innasy-syaiṭāna lakum 'aduwwun fattakhiżụhu 'aduwwā, innamā yad'ụ ḥizbahụ liyakụnụ min aṣ-ḥābis-sa'īr","at":"Sungguh, setan itu musuh bagimu, maka perlakukanlah ia sebagai musuh, karena sesungguhnya setan itu hanya mengajak golongannya agar mereka menjadi penghuni neraka yang menyala-nyala."},{"id":"7","ar":"اَلَّذِيْنَ كَفَرُوْا لَهُمْ عَذَابٌ شَدِيْدٌ ەۗ وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ كَبِيْرٌ ","bc":"allażīna kafarụ lahum 'ażābun syadīd, wallażīna āmanụ wa 'amiluṣ-ṣāliḥāti lahum magfiratuw wa ajrung kabīr","at":"Orang-orang yang kafir, mereka akan mendapat azab yang sangat keras. Dan orang-orang yang beriman dan mengerjakan kebajikan, mereka memperoleh ampunan dan pahala yang besar."},{"id":"8","ar":"اَفَمَنْ زُيِّنَ لَهٗ سُوْۤءُ عَمَلِهٖ فَرَاٰهُ حَسَنًاۗ فَاِنَّ اللّٰهَ يُضِلُّ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُۖ فَلَا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرٰتٍۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢبِمَا يَصْنَعُوْنَ ","bc":"a fa man zuyyina lahụ sū`u 'amalihī fa ra`āhu ḥasanā, fa innallāha yuḍillu may yasyā`u wa yahdī may yasyā`u fa lā taż-hab nafsuka 'alaihim ḥasarāt, innallāha 'alīmum bimā yaṣna'ụn","at":"Maka apakah pantas orang yang dijadikan terasa indah perbuatan buruknya, lalu menganggap baik perbuatannya itu? Sesungguhnya Allah menyesatkan siapa yang Dia kehendaki dan memberi petunjuk kepada siapa yang Dia kehendaki. Maka jangan engkau (Muhammad) biarkan dirimu binasa karena kesedihan terhadap mereka. Sesungguhnya Allah Maha Mengetahui apa yang mereka perbuat."},{"id":"9","ar":"وَاللّٰهُ الَّذِيْٓ اَرْسَلَ الرِّيٰحَ فَتُثِيْرُ سَحَابًا فَسُقْنٰهُ اِلٰى بَلَدٍ مَّيِّتٍ فَاَحْيَيْنَا بِهِ الْاَرْضَ بَعْدَ مَوْتِهَاۗ كَذٰلِكَ النُّشُوْرُ ","bc":"wallāhullażī arsalar-riyāḥa fa tuṡīru saḥāban fa suqnāhu ilā baladim mayyitin fa aḥyainā bihil-arḍa ba'da mautihā, każālikan-nusyụr","at":"Dan Allah-lah yang mengirimkan angin; lalu (angin itu) menggerakkan awan, maka Kami arahkan awan itu ke suatu negeri yang mati (tandus) lalu dengan hujan itu Kami hidupkan bumi setelah mati (kering). Seperti itulah kebangkitan itu."},{"id":"10","ar":"مَنْ كَانَ يُرِيْدُ الْعِزَّةَ فَلِلّٰهِ الْعِزَّةُ جَمِيْعًاۗ اِلَيْهِ يَصْعَدُ الْكَلِمُ الطَّيِّبُ وَالْعَمَلُ الصَّالِحُ يَرْفَعُهٗ ۗوَالَّذِيْنَ يَمْكُرُوْنَ السَّيِّاٰتِ لَهُمْ عَذَابٌ شَدِيْدٌ ۗوَمَكْرُ اُولٰۤىِٕكَ هُوَ يَبُوْرُ ","bc":"mang kāna yurīdul-'izzata fa lillāhil-'izzatu jamī'ā, ilaihi yaṣ'adul-kalimuṭ-ṭayyibu wal-'amaluṣ-ṣāliḥu yarfa'uh, wallażīna yamkurụnas-sayyi`āti lahum 'ażābun syadīd, wa makru ulā`ika huwa yabụr","at":"Barangsiapa menghendaki kemuliaan, maka (ketahuilah) kemuliaan itu semuanya milik Allah. Kepada-Nyalah akan naik perkataan-perkataan yang baik, dan amal kebajikan Dia akan mengangkatnya. Adapun orang-orang yang merencanakan kejahatan mereka akan mendapat azab yang sangat keras, dan rencana jahat mereka akan hancur."},{"id":"11","ar":"وَاللّٰهُ خَلَقَكُمْ مِّنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ جَعَلَكُمْ اَزْوَاجًاۗ وَمَا تَحْمِلُ مِنْ اُنْثٰى وَلَا تَضَعُ اِلَّا بِعِلْمِهٖۗ وَمَا يُعَمَّرُ مِنْ مُّعَمَّرٍ وَّلَا يُنْقَصُ مِنْ عُمُرِهٖٓ اِلَّا فِيْ كِتٰبٍۗ اِنَّ ذٰلِكَ عَلَى اللّٰهِ يَسِيْرٌ ","bc":"wallāhu khalaqakum min turābin ṡumma min nuṭfatin ṡumma ja'alakum azwājā, wa mā taḥmilu min unṡā wa lā taḍa'u illā bi'ilmih, wa mā yu'ammaru mim mu'ammariw wa lā yungqaṣu min 'umurihī illā fī kitāb, inna żālika 'alallāhi yasīr","at":"Dan Allah menciptakan kamu dari tanah kemudian dari air mani, kemudian Dia menjadikan kamu berpasangan (laki-laki dan perempuan). Tidak ada seorang perempuan pun yang mengandung dan melahirkan, melainkan dengan sepengetahuan-Nya. Dan tidak dipanjangkan umur seseorang dan tidak pula dikurangi umurnya, melainkan (sudah ditetapkan) dalam Kitab (Lauh Mahfuzh). Sungguh, yang demikian itu mudah bagi Allah."},{"id":"12","ar":"وَمَا يَسْتَوِى الْبَحْرٰنِۖ هٰذَا عَذْبٌ فُرَاتٌ سَاۤىِٕغٌ شَرَابُهٗ وَهٰذَا مِلْحٌ اُجَاجٌۗ وَمِنْ كُلٍّ تَأْكُلُوْنَ لَحْمًا طَرِيًّا وَّتَسْتَخْرِجُوْنَ حِلْيَةً تَلْبَسُوْنَهَا ۚوَتَرَى الْفُلْكَ فِيْهِ مَوَاخِرَ لِتَبْتَغُوْا مِنْ فَضْلِهٖ وَلَعَلَّكُمْ تَشْكُرُوْنَ ","bc":"wa mā yastawil-baḥrāni hāżā 'ażbun furātun sā`igun syarābuhụ wa hāżā mil-ḥun ujāj, wa ming kullin ta`kulụna laḥman ṭariyyaw wa tastakhrijụna ḥilyatan talbasụnahā, wa taral-fulka fīhi mawākhira litabtagụ min faḍlihī wa la'allakum tasykurụn","at":"Dan tidak sama (antara) dua lautan; yang ini tawar, segar, sedap diminum dan yang lain asin lagi pahit. Dan dari (masing-masing lautan) itu kamu dapat memakan daging yang segar dan kamu dapat mengeluarkan perhiasan yang kamu pakai, dan di sana kamu melihat kapal-kapal berlayar membelah laut agar kamu dapat mencari karunia-Nya dan agar kamu bersyukur."},{"id":"13","ar":"يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِۚ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَ كُلٌّ يَّجْرِيْ لِاَجَلٍ مُّسَمًّىۗ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ لَهُ الْمُلْكُۗ وَالَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِهٖ مَا يَمْلِكُوْنَ مِنْ قِطْمِيْرٍۗ ","bc":"yụlijul-laila fin-nahāri wa yụlijun-nahāra fil-laīl, wa sakhkharasy-syamsa wal-qamara kulluy yajrī li`ajalim musammā, żālikumullāhu rabbukum lahul-mulk, wallażīna tad'ụna min dụnihī mā yamlikụna ming qiṭmīr","at":"Dia memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan menundukkan matahari dan bulan, masing-masing beredar menurut waktu yang ditentukan. Yang (berbuat) demikian itulah Allah Tuhanmu, milik-Nyalah segala kerajaan. Dan orang-orang yang kamu seru (sembah) selain Allah tidak mempunyai apa-apa walaupun setipis kulit ari."},{"id":"14","ar":"اِنْ تَدْعُوْهُمْ لَا يَسْمَعُوْا دُعَاۤءَكُمْۚ وَلَوْ سَمِعُوْا مَا اسْتَجَابُوْا لَكُمْۗ وَيَوْمَ الْقِيٰمَةِ يَكْفُرُوْنَ بِشِرْكِكُمْۗ وَلَا يُنَبِّئُكَ مِثْلُ خَبِيْرٍ","bc":"in tad'ụhum lā yasma'ụ du'ā`akum, walau sami'ụ mastajābụ lakum, wa yaumal-qiyāmati yakfurụna bisyirkikum, wa lā yunabbi`uka miṡlu khabīr","at":"Jika kamu menyeru mereka, mereka tidak mendengar seruanmu, dan sekiranya mereka mendengar, mereka juga tidak memperkenankan permintaanmu. Dan pada hari Kiamat mereka akan mengingkari kemusyrikanmu dan tidak ada yang dapat memberikan keterangan kepadamu seperti yang diberikan oleh (Allah) Yang Mahateliti."},{"id":"15","ar":"۞ يٰٓاَيُّهَا النَّاسُ اَنْتُمُ الْفُقَرَاۤءُ اِلَى اللّٰهِ ۚوَاللّٰهُ هُوَ الْغَنِيُّ الْحَمِيْدُ ","bc":"yā ayyuhan-nāsu antumul-fuqarā`u ilallāh, wallāhu huwal-ganiyyul-ḥamīd","at":"Wahai manusia! Kamulah yang memerlukan Allah; dan Allah Dialah Yang Mahakaya (tidak memerlukan sesuatu), Maha Terpuji."},{"id":"16","ar":"اِنْ يَّشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيْدٍۚ ","bc":"iy yasya` yuż-hibkum wa ya`ti bikhalqin jadīd","at":"Jika Dia menghendaki, niscaya Dia membinasakan kamu dan mendatangkan makhluk yang baru (untuk menggantikan kamu)."},{"id":"17","ar":"وَمَا ذٰلِكَ عَلَى اللّٰهِ بِعَزِيْزٍ ","bc":"wa mā żālika 'alallāhi bi'azīz","at":"Dan yang demikian itu tidak sulit bagi Allah."},{"id":"18","ar":"وَلَا تَزِرُ وَازِرَةٌ وِّزْرَ اُخْرٰى ۗوَاِنْ تَدْعُ مُثْقَلَةٌ اِلٰى حِمْلِهَا لَا يُحْمَلْ مِنْهُ شَيْءٌ وَّلَوْ كَانَ ذَا قُرْبٰىۗ اِنَّمَا تُنْذِرُ الَّذِيْنَ يَخْشَوْنَ رَبَّهُمْ بِالْغَيْبِ وَاَقَامُوا الصَّلٰوةَ ۗوَمَنْ تَزَكّٰى فَاِنَّمَا يَتَزَكّٰى لِنَفْسِهٖ ۗوَاِلَى اللّٰهِ الْمَصِيْرُ ","bc":"wa lā taziru wāziratuw wizra ukhrā, wa in tad'u muṡqalatun ilā ḥimlihā lā yuḥmal min-hu syai`uw walau kāna żā qurbā, innamā tunżirullażīna yakhsyauna rabbahum bil-gaibi wa aqāmuṣ-ṣalāh, wa man tazakkā fa innamā yatazakkā linafsih, wa ilallāhil-maṣīr","at":"Dan orang yang berdosa tidak akan memikul dosa orang lain. Dan jika seseorang yang dibebani berat dosanya memanggil (orang lain) untuk memikul bebannya itu tidak akan dipikulkan sedikit pun, meskipun (yang dipanggilnya itu) kaum kerabatnya. Sesungguhnya yang dapat engkau beri peringatan hanya orang-orang yang takut kepada (azab) Tuhannya (sekalipun) mereka tidak melihat-Nya dan mereka yang melaksanakan salat. Dan barangsiapa menyucikan dirinya, sesungguhnya dia menyucikan diri untuk kebaikan dirinya sendiri. Dan kepada Allah-lah tempat kembali."},{"id":"19","ar":"وَمَا يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُ ۙ ","bc":"wa mā yastawil-a'mā wal-baṣīr","at":"Dan tidaklah sama orang yang buta dengan orang yang melihat,"},{"id":"20","ar":"وَلَا الظُّلُمٰتُ وَلَا النُّوْرُۙ ","bc":"wa laẓ-ẓulumātu wa lan-nụr","at":"dan tidak (pula) sama gelap gulita dengan cahaya,"},{"id":"21","ar":"وَلَا الظِّلُّ وَلَا الْحَرُوْرُۚ ","bc":"wa laẓ-ẓillu wa lal-ḥarụr","at":"dan tidak (pula) sama yang teduh dengan yang panas,"},{"id":"22","ar":"وَمَا يَسْتَوِى الْاَحْيَاۤءُ وَلَا الْاَمْوَاتُۗ اِنَّ اللّٰهَ يُسْمِعُ مَنْ يَّشَاۤءُ ۚوَمَآ اَنْتَ بِمُسْمِعٍ مَّنْ فِى الْقُبُوْرِ ","bc":"wa mā yastawil-aḥyā`u wa lal-amwāt, innallāha yusmi'u may yasyā`, wa mā anta bimusmi'im man fil-qubụr","at":"dan tidak (pula) sama orang yang hi-dup dengan orang yang mati. Sungguh, Allah memberikan pendengaran kepada siapa yang Dia kehendaki dan engkau (Muhammad) tidak akan sanggup menjadikan orang yang di dalam kubur dapat mendengar."},{"id":"23","ar":"اِنْ اَنْتَ اِلَّا نَذِيْرٌ","bc":"in anta illā nażīr","at":"Engkau tidak lain hanyalah seorang pemberi peringatan."},{"id":"24","ar":"اِنَّآ اَرْسَلْنٰكَ بِالْحَقِّ بَشِيْرًا وَّنَذِيْرًا ۗوَاِنْ مِّنْ اُمَّةٍ اِلَّا خَلَا فِيْهَا نَذِيْرٌ ","bc":"innā arsalnāka bil-ḥaqqi basyīraw wa nażīrā, wa im min ummatin illā khalā fīhā nażīr","at":"Sungguh, Kami mengutus engkau dengan membawa kebenaran sebagai pembawa berita gembira dan sebagai pemberi peringatan. Dan tidak ada satu pun umat melainkan di sana telah datang seorang pemberi peringatan."},{"id":"25","ar":"وَاِنْ يُّكَذِّبُوْكَ فَقَدْ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ ۚجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ وَبِالزُّبُرِ وَبِالْكِتٰبِ الْمُنِيْرِ ","bc":"wa iy yukażżibụka fa qad każżaballażīna ming qablihim, jā`at-hum rusuluhum bil-bayyināti wa biz-zuburi wa bil-kitābil-munīr","at":"Dan jika mereka mendustakanmu, maka sungguh, orang-orang yang sebelum mereka pun telah mendustakan (rasul-rasul); ketika rasul-rasulnya datang dengan membawa keterangan yang nyata (mukjizat), zubur, dan kitab yang memberi penjelasan yang sempurna."},{"id":"26","ar":"ثُمَّ اَخَذْتُ الَّذِيْنَ كَفَرُوْا فَكَيْفَ كَانَ نَكِيْرِ ","bc":"ṡumma akhażtullażīna kafarụ fa kaifa kāna nakīr","at":"Kemudian Aku azab orang-orang yang kafir; maka (lihatlah) bagaimana akibat kemurkaan-Ku."},{"id":"27","ar":"اَلَمْ تَرَ اَنَّ اللّٰهَ اَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۚ فَاَخْرَجْنَا بِهٖ ثَمَرٰتٍ مُّخْتَلِفًا اَلْوَانُهَا ۗوَمِنَ الْجِبَالِ جُدَدٌ ۢبِيْضٌ وَّحُمْرٌ مُّخْتَلِفٌ اَلْوَانُهَا وَغَرَابِيْبُ سُوْدٌ ","bc":"a lam tara annallāha anzala minas-samā`i mā`ā, fa akhrajnā bihī ṡamarātim mukhtalifan alwānuhā, wa minal-jibāli judadum bīḍuw wa ḥumrum mukhtalifun alwānuhā wa garābību sụd","at":"Tidakkah engkau melihat bahwa Allah menurunkan air dari langit lalu dengan air itu Kami hasilkan buah-buahan yang beraneka macam jenisnya. Dan di antara gunung-gunung itu ada garis-garis putih dan merah yang beraneka macam warnanya dan ada (pula) yang hitam pekat."},{"id":"28","ar":"وَمِنَ النَّاسِ وَالدَّوَاۤبِّ وَالْاَنْعَامِ مُخْتَلِفٌ اَلْوَانُهٗ كَذٰلِكَۗ اِنَّمَا يَخْشَى اللّٰهَ مِنْ عِبَادِهِ الْعُلَمٰۤؤُاۗ اِنَّ اللّٰهَ عَزِيْزٌ غَفُوْرٌ ","bc":"wa minan-nāsi wad-dawābbi wal-an'āmi mukhtalifun alwānuhụ każālik, innamā yakhsyallāha min 'ibādihil-'ulamā`, innallāha 'azīzun gafụr","at":"Dan demikian (pula) di antara manusia, makhluk bergerak yang bernyawa dan hewan-hewan ternak ada yang bermacam-macam warnanya (dan jenisnya). Di antara hamba-hamba Allah yang takut kepada-Nya, hanyalah para ulama. Sungguh, Allah Mahaperkasa, Maha Pengampun."},{"id":"29","ar":"اِنَّ الَّذِيْنَ يَتْلُوْنَ كِتٰبَ اللّٰهِ وَاَقَامُوا الصَّلٰوةَ وَاَنْفَقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً يَّرْجُوْنَ تِجَارَةً لَّنْ تَبُوْرَۙ ","bc":"innallażīna yatlụna kitāballāhi wa aqāmuṣ-ṣalāta wa anfaqụ mimmā razaqnāhum sirraw wa 'alāniyatay yarjụna tijāratal lan tabụr","at":"Sesungguhnya orang-orang yang selalu membaca Kitab Allah (Al-Qur'an) dan melaksanakan salat dan menginfakkan sebagian rezeki yang Kami anugerahkan kepadanya dengan diam-diam dan terang-terangan, mereka itu mengharapkan perdagangan yang tidak akan rugi,"},{"id":"30","ar":"لِيُوَفِّيَهُمْ اُجُوْرَهُمْ وَيَزِيْدَهُمْ مِّنْ فَضْلِهٖۗ اِنَّهٗ غَفُوْرٌ شَكُوْرٌ ","bc":"liyuwaffiyahum ujụrahum wa yazīdahum min faḍlih, innahụ gafụrun syakụr","at":"agar Allah menyempurnakan pahalanya kepada mereka dan menambah karunia-Nya. Sungguh, Allah Maha Pengampun, Maha Mensyukuri."},{"id":"31","ar":"وَالَّذِيْٓ اَوْحَيْنَآ اِلَيْكَ مِنَ الْكِتٰبِ هُوَ الْحَقُّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِۗ اِنَّ اللّٰهَ بِعِبَادِهٖ لَخَبِيْرٌۢ بَصِيْرٌ ","bc":"wallażī auḥainā ilaika minal-kitābi huwal-ḥaqqu muṣaddiqal limā baina yadaīh, innallāha bi'ibādihī lakhabīrum baṣīr","at":"Dan apa yang telah Kami wahyukan kepadamu (Muhammad) yaitu Kitab (Al-Qur'an) itulah yang benar, membenarkan kitab-kitab yang sebelumnya. Sungguh, Allah benar-benar Maha Mengetahui, Maha Melihat (keadaan) hamba-hamba-Nya."},{"id":"32","ar":"ثُمَّ اَوْرَثْنَا الْكِتٰبَ الَّذِيْنَ اصْطَفَيْنَا مِنْ عِبَادِنَاۚ فَمِنْهُمْ ظَالِمٌ لِّنَفْسِهٖ ۚوَمِنْهُمْ مُّقْتَصِدٌ ۚوَمِنْهُمْ سَابِقٌۢ بِالْخَيْرٰتِ بِاِذْنِ اللّٰهِ ۗذٰلِكَ هُوَ الْفَضْلُ الْكَبِيْرُۗ ","bc":"ṡumma auraṡnal-kitāballażīnaṣṭafainā min 'ibādinā, fa min-hum ẓālimul linafsih, wa min-hum muqtaṣid, wa min-hum sābiqum bil-khairāti bi`iżnillāh, żālika huwal-faḍlul-kabīr","at":"Kemudian Kitab itu Kami wariskan kepada orang-orang yang Kami pilih di antara hamba-hamba Kami, lalu di antara mereka ada yang menzalimi diri sendiri, ada yang pertengahan dan ada (pula) yang lebih dahulu berbuat kebaikan dengan izin Allah. Yang demikian itu adalah karunia yang besar."},{"id":"33","ar":"جَنّٰتُ عَدْنٍ يَّدْخُلُوْنَهَا يُحَلَّوْنَ فِيْهَا مِنْ اَسَاوِرَ مِنْ ذَهَبٍ وَّلُؤْلُؤًا ۚوَلِبَاسُهُمْ فِيْهَا حَرِيْرٌ ","bc":"jannātu 'adniy yadkhulụnahā yuḥallauna fīhā min asāwira min żahabiw wa lu`lu`ā, wa libāsuhum fīhā ḥarīr","at":"(Mereka akan mendapat) surga ‘Adn, mereka masuk ke dalamnya, di dalamnya mereka diberi perhiasan gelang-gelang dari emas dan mutiara, dan pakaian mereka di dalamnya adalah sutera."},{"id":"34","ar":"وَقَالُوا الْحَمْدُ لِلّٰهِ الَّذِيْٓ اَذْهَبَ عَنَّا الْحَزَنَۗ اِنَّ رَبَّنَا لَغَفُوْرٌ شَكُوْرٌۙ ","bc":"wa qālul-ḥamdu lillāhillażī aż-haba 'annal-ḥazan, inna rabbanā lagafụrun syakụr","at":"Dan mereka berkata, “Segala puji bagi Allah yang telah menghilangkan kesedihan dari kami. Sungguh, Tuhan kami benar-benar Maha Pengampun, Maha Mensyukuri,"},{"id":"35","ar":" ۨالَّذِيْٓ اَحَلَّنَا دَارَ الْمُقَامَةِ مِنْ فَضْلِهٖۚ لَا يَمَسُّنَا فِيْهَا نَصَبٌ وَّلَا يَمَسُّنَا فِيْهَا لُغُوْبٌ ","bc":"allażī aḥallanā dāral-muqāmati min faḍlih, lā yamassunā fīhā naṣabuw wa lā yamassunā fīhā lugụb","at":"yang dengan karunia-Nya menempatkan kami dalam tempat yang kekal (surga); di dalamnya kami tidak merasa lelah dan tidak pula merasa lesu.”"},{"id":"36","ar":"وَالَّذِيْنَ كَفَرُوْا لَهُمْ نَارُ جَهَنَّمَۚ لَا يُقْضٰى عَلَيْهِمْ فَيَمُوْتُوْا وَلَا يُخَفَّفُ عَنْهُمْ مِّنْ عَذَابِهَاۗ كَذٰلِكَ نَجْزِيْ كُلَّ كَفُوْرٍ ۚ","bc":"wallażīna kafarụ lahum nāru jahannam, lā yuqḍā 'alaihim fa yamụtụ wa lā yukhaffafu 'an-hum min 'ażābihā, każālika najzī kulla kafụr","at":"Dan orang-orang yang kafir, bagi mereka neraka Jahanam. Mereka tidak dibinasakan hingga mereka mati, dan tidak diringankan dari mereka azabnya. Demikianlah Kami membalas setiap orang yang sangat kafir."},{"id":"37","ar":"وَهُمْ يَصْطَرِخُوْنَ فِيْهَاۚ رَبَّنَآ اَخْرِجْنَا نَعْمَلْ صَالِحًا غَيْرَ الَّذِيْ كُنَّا نَعْمَلُۗ اَوَلَمْ نُعَمِّرْكُمْ مَّا يَتَذَكَّرُ فِيْهِ مَنْ تَذَكَّرَ وَجَاۤءَكُمُ النَّذِيْرُۗ فَذُوْقُوْا فَمَا لِلظّٰلِمِيْنَ مِنْ نَّصِيْرٍ ","bc":"wa hum yaṣṭarikhụna fīhā, rabbanā akhrijnā na'mal ṣāliḥan gairallażī kunnā na'mal, a wa lam nu'ammirkum mā yatażakkaru fīhi man tażakkara wa jā`akumun-nażīr, fa żụqụ fa mā liẓ-ẓālimīna min naṣīr","at":"Dan mereka berteriak di dalam neraka itu, “Ya Tuhan kami, keluarkanlah kami (dari neraka), niscaya kami akan mengerjakan kebajikan, yang berlainan dengan yang telah kami kerjakan dahulu.” (Dikatakan kepada mereka), “Bukankah Kami telah memanjangkan umurmu untuk dapat berpikir bagi orang yang mau berpikir, padahal telah datang kepadamu seorang pemberi peringatan? Maka rasakanlah (azab Kami), dan bagi orang-orang zalim tidak ada seorang penolong pun.”"},{"id":"38","ar":"اِنَّ اللّٰهَ عَالِمُ غَيْبِ السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّهٗ عَلِيْمٌ ۢبِذَاتِ الصُّدُوْرِ ","bc":"innallāha 'ālimu gaibis-samāwāti wal-arḍ, innahụ 'alīmum biżātiṣ-ṣudụr","at":"Sungguh, Allah mengetahui yang gaib (tersembunyi) di langit dan di bumi. Sungguh, Dia Maha Mengetahui segala isi hati."},{"id":"39","ar":"هُوَ الَّذِيْ جَعَلَكُمْ خَلٰۤىِٕفَ فِى الْاَرْضِۗ فَمَنْ كَفَرَ فَعَلَيْهِ كُفْرُهٗۗ وَلَا يَزِيْدُ الْكٰفِرِيْنَ كُفْرُهُمْ عِنْدَ رَبِّهِمْ اِلَّا مَقْتًا ۚوَلَا يَزِيْدُ الْكٰفِرِيْنَ كُفْرُهُمْ اِلَّا خَسَارًا ","bc":"huwallażī ja'alakum khalā`ifa fil-arḍ, fa mang kafara fa 'alaihi kufruh, wa lā yazīdul-kāfirīna kufruhum 'inda rabbihim illā maqtā, wa lā yazīdul-kāfirīna kufruhum illā khasārā","at":"Dialah yang menjadikan kamu sebagai khalifah-khalifah di bumi. Barangsiapa kafir, maka (akibat) kekafirannya akan menimpa dirinya sendiri. Dan kekafiran orang-orang kafir itu hanya akan menambah kemurkaan di sisi Tuhan mereka. Dan kekafiran orang-orang kafir itu hanya akan menambah kerugian mereka belaka."},{"id":"40","ar":"قُلْ اَرَاَيْتُمْ شُرَكَاۤءَكُمُ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ ۗاَرُوْنِيْ مَاذَا خَلَقُوْا مِنَ الْاَرْضِ اَمْ لَهُمْ شِرْكٌ فِى السَّمٰوٰتِۚ اَمْ اٰتَيْنٰهُمْ كِتٰبًا فَهُمْ عَلٰى بَيِّنَتٍ مِّنْهُۚ بَلْ اِنْ يَّعِدُ الظّٰلِمُوْنَ بَعْضُهُمْ بَعْضًا اِلَّا غُرُوْرًا ","bc":"qul a ra`aitum syurakā`akumullażīna tad'ụna min dụnillāh, arụnī māżā khalaqụ minal-arḍi am lahum syirkun fis-samāwāt, am ātaināhum kitāban fa hum 'alā bayyinatim min-h, bal iy ya'iduẓ-ẓālimụna ba'ḍuhum ba'ḍan illā gurụrā","at":"Katakanlah, “Terangkanlah olehmu tentang sekutu-sekutumu yang kamu seru selain Allah.” Perlihatkanlah kepada-Ku (bagian) manakah dari bumi ini yang telah mereka ciptakan; ataukah mereka mempunyai peran serta dalam (penciptaan) langit; atau adakah Kami memberikan kitab kepada mereka sehingga mereka mendapat keterangan-keterangan yang jelas darinya? Sebenarnya orang-orang zalim itu, sebagian mereka hanya menjanjikan tipuan belaka kepada sebagian yang lain. "},{"id":"41","ar":"۞ اِنَّ اللّٰهَ يُمْسِكُ السَّمٰوٰتِ وَالْاَرْضَ اَنْ تَزُوْلَا ەۚ وَلَىِٕنْ زَالَتَآ اِنْ اَمْسَكَهُمَا مِنْ اَحَدٍ مِّنْۢ بَعْدِهٖ ۗاِنَّهٗ كَانَ حَلِيْمًا غَفُوْرًا","bc":"innallāha yumsikus-samāwāti wal-arḍa an tazụlā, wa la`in zālatā in amsakahumā min aḥadim mim ba'dih, innahụ kāna ḥalīman gafụrā","at":"Sungguh, Allah yang menahan langit dan bumi agar tidak lenyap; dan jika keduanya akan lenyap tidak ada seorang pun yang mampu menahannya selain Allah. Sungguh, Dia Maha Penyantun, Maha Pengampun."},{"id":"42","ar":"وَاَقْسَمُوْا بِاللّٰهِ جَهْدَ اَيْمَانِهِمْ لَىِٕنْ جَاۤءَهُمْ نَذِيْرٌ لَّيَكُوْنُنَّ اَهْدٰى مِنْ اِحْدَى الْاُمَمِۚ فَلَمَّا جَاۤءَهُمْ نَذِيْرٌ مَّا زَادَهُمْ اِلَّا نُفُوْرًاۙ ","bc":"wa aqsamụ billāhi jahda aimānihim la`in jā`ahum nażīrul layakụnunna ahdā min iḥdal-umam, fa lammā jā`ahum nażīrum mā zādahum illā nufụrā","at":"Dan mereka bersumpah dengan nama Allah dengan sungguh-sungguh bahwa jika datang kepada mereka seorang pemberi peringatan, niscaya mereka akan lebih mendapat petunjuk dari salah satu umat-umat (yang lain). Tetapi ketika pemberi peringatan datang kepada mereka, tidak menambah (apa-apa) kepada mereka, bahkan semakin jauh mereka dari (kebenaran),"},{"id":"43","ar":" ۨاسْتِكْبَارًا فِى الْاَرْضِ وَمَكْرَ السَّيِّئِۗ وَلَا يَحِيْقُ الْمَكْرُ السَّيِّئُ اِلَّا بِاَهْلِهٖ ۗفَهَلْ يَنْظُرُوْنَ اِلَّا سُنَّتَ الْاَوَّلِيْنَۚ فَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَبْدِيْلًا ەۚ وَلَنْ تَجِدَ لِسُنَّتِ اللّٰهِ تَحْوِيْلًا","bc":"istikbāran fil-arḍi wa makras-sayyi`, wa lā yaḥīqul-makrus-sayyi`u illā bi`ahlih, fa hal yanẓurụna illā sunnatal-awwalīn, fa lan tajida lisunnatillāhi tabdīlā, wa lan tajida lisunnatillāhi taḥwīlā","at":"karena kesombongan (mereka) di bumi dan karena rencana (mereka) yang jahat. Rencana yang jahat itu hanya akan menimpa orang yang merencanakannya sendiri. Mereka hanyalah menunggu (berlakunya) ketentuan kepada orang-orang yang terdahulu. Maka kamu tidak akan mendapatkan perubahan bagi Allah, dan tidak (pula) akan menemui penyimpangan bagi ketentuan Allah itu."},{"id":"44","ar":"اَوَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْ وَكَانُوْٓا اَشَدَّ مِنْهُمْ قُوَّةً ۗوَمَا كَانَ اللّٰهُ لِيُعْجِزَهٗ مِنْ شَيْءٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِۗ اِنَّهٗ كَانَ عَلِيْمًا قَدِيْرًا ","bc":"a wa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna 'āqibatullażīna ming qablihim wa kānū asyadda min-hum quwwah, wa mā kānallāhu liyu'jizahụ min syai`in fis-samāwāti wa lā fil-arḍ, innahụ kāna 'alīmang qadīrā","at":"Dan tidakkah mereka bepergian di bumi lalu melihat bagaimana kesudahan orang-orang sebelum mereka (yang mendustakan rasul), padahal orang-orang itu lebih besar kekuatannya dari mereka? Dan tidak ada sesuatu pun yang dapat melemahkan Allah baik di langit maupun di bumi. Sungguh, Dia Maha Mengetahui, Mahakuasa."},{"id":"45","ar":"وَلَوْ يُؤَاخِذُ اللّٰهُ النَّاسَ بِمَا كَسَبُوْا مَا تَرَكَ عَلٰى ظَهْرِهَا مِنْ دَاۤبَّةٍ وَّلٰكِنْ يُّؤَخِّرُهُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۚ فَاِذَا جَاۤءَ اَجَلُهُمْ فَاِنَّ اللّٰهَ كَانَ بِعِبَادِهٖ بَصِيْرًا ","bc":"walau yu`ākhiżullāhun-nāsa bimā kasabụ mā taraka 'alā ẓahrihā min dābbatiw wa lākiy yu`akhkhiruhum ilā ajalim musammā, fa iżā jā`a ajaluhum fa innallāha kāna bi'ibādihī baṣīrā","at":"Dan sekiranya Allah menghukum manusia disebabkan apa yang telah mereka perbuat, niscaya Dia tidak akan menyisakan satu pun makhluk bergerak yang bernyawa di bumi ini, tetapi Dia menangguhkan (hukuman)nya, sampai waktu yang sudah ditentukan. Nanti apabila ajal mereka tiba, maka Allah Maha Melihat (keadaan) hamba-hamba-Nya."}],fussilat=[{"id":"1","ar":" حٰمۤ ۚ ","bc":"ḥā mīm","at":"Ha Mim"},{"id":"2","ar":"تَنْزِيْلٌ مِّنَ الرَّحْمٰنِ الرَّحِيْمِ ۚ ","bc":"tanzīlum minar-raḥmānir-raḥīm","at":"(Al-Qur'an ini) diturunkan dari Tuhan Yang Maha Pengasih, Maha Penyayang."},{"id":"3","ar":"كِتٰبٌ فُصِّلَتْ اٰيٰتُهٗ قُرْاٰنًا عَرَبِيًّا لِّقَوْمٍ يَّعْلَمُوْنَۙ ","bc":"kitābun fuṣṣilat āyātuhụ qur`ānan 'arabiyyal liqaumiy ya'lamụn","at":"Kitab yang ayat-ayatnya dijelaskan, bacaan dalam bahasa Arab, untuk kaum yang mengetahui,"},{"id":"4","ar":"بَشِيْرًا وَّنَذِيْرًاۚ فَاَعْرَضَ اَكْثَرُهُمْ فَهُمْ لَا يَسْمَعُوْنَ ","bc":"basyīraw wa nażīrā, fa a'raḍa akṡaruhum fa hum lā yasma'ụn","at":"yang membawa berita gembira dan peringatan, tetapi kebanyakan mereka berpaling (darinya) serta tidak mendengarkan."},{"id":"5","ar":"وَقَالُوْا قُلُوْبُنَا فِيْٓ اَكِنَّةٍ مِّمَّا تَدْعُوْنَآ اِلَيْهِ وَفِيْٓ اٰذَانِنَا وَقْرٌ وَّمِنْۢ بَيْنِنَا وَبَيْنِكَ حِجَابٌ فَاعْمَلْ اِنَّنَا عٰمِلُوْنَ","bc":"wa qālụ qulụbunā fī akinnatim mimmā tad'ụnā ilaihi wa fī āżāninā waqruw wa mim baininā wa bainika ḥijābun fa'mal innanā 'āmilụn","at":"Dan mereka berkata, “Hati kami sudah tertutup dari apa yang engkau seru kami kepadanya dan telinga kami sudah tersumbat, dan di antara kami dan engkau ada dinding, karena itu lakukanlah (sesuai kehendakmu), sesungguhnya kami akan melakukan (sesuai kehendak kami).”"},{"id":"6","ar":"قُلْ اِنَّمَآ اَنَا۟ بَشَرٌ مِّثْلُكُمْ يُوْحٰىٓ اِلَيَّ اَنَّمَآ اِلٰهُكُمْ اِلٰهٌ وَّاحِدٌ فَاسْتَقِيْمُوْٓا اِلَيْهِ وَاسْتَغْفِرُوْهُ ۗوَوَيْلٌ لِّلْمُشْرِكِيْنَۙ ","bc":"qul innamā ana basyarum miṡlukum yụḥā ilayya annamā ilāhukum ilāhuw wāḥidun fastaqīmū ilaihi wastagfirụh, wa wailul lil-musyrikīn","at":"Katakanlah (Muhammad), “Aku ini hanyalah seorang manusia seperti kamu, yang diwahyukan kepadaku bahwa Tuhan kamu adalah Tuhan Yang Maha Esa, karena itu tetaplah kamu (beribadah) kepada-Nya dan mohonlah ampunan kepada-Nya. Dan celakalah bagi orang-orang yang mempersekutukan-(Nya),"},{"id":"7","ar":"الَّذِيْنَ لَا يُؤْتُوْنَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ كٰفِرُوْنَ","bc":"allażīna lā yu`tụnaz-zakāta wa hum bil-ākhirati hum kāfirụn","at":"(yaitu) orang-orang yang tidak menunaikan zakat dan mereka ingkar terhadap kehidupan akhirat."},{"id":"8","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ اَجْرٌ غَيْرُ مَمْنُوْنٍ ","bc":"innallażīna āmanụ wa 'amiluṣ-ṣāliḥāti lahum ajrun gairu mamnụn","at":"Sesungguhnya orang-orang yang beriman dan mengerjakan kebajikan, mereka mendapat pahala yang tidak ada putus-putusnya.”"},{"id":"9","ar":"۞ قُلْ اَىِٕنَّكُمْ لَتَكْفُرُوْنَ بِالَّذِيْ خَلَقَ الْاَرْضَ فِيْ يَوْمَيْنِ وَتَجْعَلُوْنَ لَهٗٓ اَنْدَادًا ۗذٰلِكَ رَبُّ الْعٰلَمِيْنَ ۚ ","bc":"qul a innakum latakfurụna billażī khalaqal-arḍa fī yaumaini wa taj'alụna lahū andādā, żālika rabbul-'ālamīn","at":"Katakanlah, “Pantaskah kamu ingkar kepada Tuhan yang menciptakan bumi dalam dua masa dan kamu adakan pula sekutu-sekutu bagi-Nya? Itulah Tuhan seluruh alam.”"},{"id":"10","ar":"وَجَعَلَ فِيْهَا رَوَاسِيَ مِنْ فَوْقِهَا وَبٰرَكَ فِيْهَا وَقَدَّرَ فِيْهَآ اَقْوَاتَهَا فِيْٓ اَرْبَعَةِ اَيَّامٍۗ سَوَاۤءً لِّلسَّاۤىِٕلِيْنَ","bc":"wa ja'ala fīhā rawāsiya min fauqihā wa bāraka fīhā wa qaddara fīhā aqwātahā fī arba'ati ayyām, sawā`al lis-sā`ilīn","at":"Dan Dia ciptakan padanya gunung-gunung yang kokoh di atasnya. Dan kemudian Dia berkahi, dan Dia tentukan makanan-makanan (bagi penghuni)nya dalam empat masa, memadai untuk (memenuhi kebutuhan) mereka yang memerlukannya."},{"id":"11","ar":"ثُمَّ اسْتَوٰىٓ اِلَى السَّمَاۤءِ وَهِيَ دُخَانٌ فَقَالَ لَهَا وَلِلْاَرْضِ ائْتِيَا طَوْعًا اَوْ كَرْهًاۗ قَالَتَآ اَتَيْنَا طَاۤىِٕعِيْنَ ","bc":"ṡummastawā ilas-samā`i wa hiya dukhānun fa qāla lahā wa lil-arḍi`tiyā ṭau'an au karhā, qālatā atainā ṭā`i'īn","at":"Kemudian Dia menuju ke langit dan (langit) itu masih berupa asap, lalu Dia berfirman kepadanya dan kepada bumi, “Datanglah kamu berdua menurut perintah-Ku dengan patuh atau terpaksa.” Keduanya menjawab, “Kami datang dengan patuh.”"},{"id":"12","ar":"فَقَضٰىهُنَّ سَبْعَ سَمٰوٰتٍ فِيْ يَوْمَيْنِ وَاَوْحٰى فِيْ كُلِّ سَمَاۤءٍ اَمْرَهَا ۗوَزَيَّنَّا السَّمَاۤءَ الدُّنْيَا بِمَصَابِيْحَۖ وَحِفْظًا ۗذٰلِكَ تَقْدِيْرُ الْعَزِيْزِ الْعَلِيْمِ","bc":"fa qaḍāhunna sab'a samāwātin fī yaumaini wa auḥā fī kulli samā`in amrahā, wa zayyannas-samā`ad-dun-yā bimaṣābīḥa wa ḥifẓā, żālika taqdīrul-'azīzil-'alīm","at":"Lalu diciptakan-Nya tujuh langit dalam dua masa dan pada setiap langit Dia mewahyukan urusan masing-masing. Kemudian langit yang dekat (dengan bumi), Kami hiasi dengan bintang-bintang, dan (Kami ciptakan itu) untuk memelihara. Demikianlah ketentuan (Allah) Yang Mahaperkasa, Maha Mengetahui."},{"id":"13","ar":"فَاِنْ اَعْرَضُوْا فَقُلْ اَنْذَرْتُكُمْ صٰعِقَةً مِّثْلَ صٰعِقَةِ عَادٍ وَّثَمُوْدَ ۗ ","bc":"fa in a'raḍụ fa qul anżartukum ṣā'iqatam miṡla ṣā'iqati 'ādiw wa ṡamụd","at":"Jika mereka berpaling maka katakanlah, “Aku telah memperingatkan kamu akan (bencana) petir seperti petir yang menimpa kaum ’Ad dan kaum Samud.”"},{"id":"14","ar":"اِذْ جَاۤءَتْهُمُ الرُّسُلُ مِنْۢ بَيْنِ اَيْدِيْهِمْ وَمِنْ خَلْفِهِمْ اَلَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۗقَالُوْا لَوْ شَاۤءَ رَبُّنَا لَاَنْزَلَ مَلٰۤىِٕكَةً فَاِنَّا بِمَآ اُرْسِلْتُمْ بِهٖ كٰفِرُوْنَ ","bc":"iż jā`at-humur-rusulu mim baini aidīhim wa min khalfihim allā ta'budū illallāh, qālụ lau syā`a rabbunā la`anzala malā`ikatan fa innā bimā ursiltum bihī kāfirụn","at":"Ketika para rasul datang kepada mereka dari depan dan dari belakang mereka (dengan menyerukan), “Janganlah kamu menyembah selain Allah.” Mereka menjawab, “Kalau Tuhan kami menghendaki tentu Dia menurunkan malaikat-malaikat-Nya, maka sesungguhnya kami mengingkari wahyu yang engkau diutus menyampaikannya.”"},{"id":"15","ar":"فَاَمَّا عَادٌ فَاسْتَكْبَرُوْا فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَقَالُوْا مَنْ اَشَدُّ مِنَّا قُوَّةً ۗ اَوَلَمْ يَرَوْا اَنَّ اللّٰهَ الَّذِيْ خَلَقَهُمْ هُوَ اَشَدُّ مِنْهُمْ قُوَّةً ۗ وَكَانُوْا بِاٰيٰتِنَا يَجْحَدُوْنَ","bc":"fa ammā 'ādun fastakbarụ fil-arḍi bigairil-ḥaqqi wa qālụ man asyaddu minnā quwwah, a wa lam yarau annallāhallażī khalaqahum huwa asyaddu min-hum quwwah, wa kānụ bi`āyātinā yaj-ḥadụn","at":"Maka adapun kaum ‘Ad, mereka menyombongkan diri di bumi tanpa (mengindahkan) kebenaran dan mereka berkata, “Siapakah yang lebih hebat kekuatannya dari kami?” Tidakkah mereka memperhatikan bahwa sesungguhnya Allah yang menciptakan mereka. Dia lebih hebat kekuatan-Nya dari mereka? Dan mereka telah mengingkari tanda-tanda (kebesaran) Kami."},{"id":"16","ar":"فَاَرْسَلْنَا عَلَيْهِمْ رِيْحًا صَرْصَرًا فِيْٓ اَيَّامٍ نَّحِسَاتٍ لِّنُذِيْقَهُمْ عَذَابَ الْخِزْيِ فِى الْحَيٰوةِ الدُّنْيَا ۗوَلَعَذَابُ الْاٰخِرَةِ اَخْزٰى وَهُمْ لَا يُنْصَرُوْنَ","bc":"fa arsalnā 'alaihim rīḥan ṣarṣaran fī ayyāmin naḥisātil linużīqahum 'ażābal-khizyi fil-ḥayātid-dun-yā, wa la'ażābul-ākhirati akhzā wa hum lā yunṣarụn","at":"Maka Kami tiupkan angin yang sangat bergemuruh kepada mereka dalam beberapa hari yang nahas, karena Kami ingin agar mereka itu merasakan siksaan yang menghinakan dalam kehidupan di dunia. Sedangkan azab akhirat pasti lebih menghinakan dan mereka tidak diberi pertolongan."},{"id":"17","ar":"وَاَمَّا ثَمُوْدُ فَهَدَيْنٰهُمْ فَاسْتَحَبُّوا الْعَمٰى عَلَى الْهُدٰى فَاَخَذَتْهُمْ صٰعِقَةُ الْعَذَابِ الْهُوْنِ بِمَا كَانُوْا يَكْسِبُوْنَ ۚ ","bc":"wa ammā ṡamụdu fa hadaināhum fastaḥabbul-'amā 'alal-hudā fa akhażat-hum ṣā'iqatul-'ażābil-hụni bimā kānụ yaksibụn","at":"Dan adapun kaum Samud, mereka telah Kami beri petunjuk tetapi mereka lebih menyukai kebutaan (kesesatan) daripada petunjuk itu, maka mereka disambar petir sebagai azab yang menghinakan disebabkan apa yang telah mereka kerjakan."},{"id":"18","ar":"وَنَجَّيْنَا الَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَ ","bc":"wa najjainallażīna āmanụ wa kānụ yattaqụn","at":"Dan Kami selamatkan orang-orang yang beriman karena mereka adalah orang-orang yang bertakwa."},{"id":"19","ar":"وَيَوْمَ يُحْشَرُ اَعْدَاۤءُ اللّٰهِ اِلَى النَّارِ فَهُمْ يُوْزَعُوْنَ","bc":"wa yauma yuḥsyaru a'dā`ullāhi ilan-nāri fa hum yụza'ụn","at":"Dan (ingatlah) pada hari (ketika) musuh-musuh Allah digiring ke neraka lalu mereka dipisah-pisahkan."},{"id":"20","ar":"حَتّٰىٓ اِذَا مَا جَاۤءُوْهَا شَهِدَ عَلَيْهِمْ سَمْعُهُمْ وَاَبْصَارُهُمْ وَجُلُوْدُهُمْ بِمَا كَانُوْا يَعْمَلُوْنَ ","bc":"ḥattā iżā mā jā`ụhā syahida 'alaihim sam'uhum wa abṣāruhum wa julụduhum bimā kānụ ya'malụn","at":"Sehingga apabila mereka sampai ke neraka, pendengaran, penglihatan dan kulit mereka menjadi saksi terhadap apa yang telah mereka lakukan."},{"id":"21","ar":"وَقَالُوْا لِجُلُوْدِهِمْ لِمَ شَهِدْتُّمْ عَلَيْنَا ۗقَالُوْٓا اَنْطَقَنَا اللّٰهُ الَّذِيْٓ اَنْطَقَ كُلَّ شَيْءٍ وَّهُوَ خَلَقَكُمْ اَوَّلَ مَرَّةٍۙ وَّاِلَيْهِ تُرْجَعُوْنَ","bc":"wa qālụ lijulụdihim lima syahittum 'alainā, qālū anṭaqanallāhullażī anṭaqa kulla syai`iw wa huwa khalaqakum awwala marratiw wa ilaihi turja'ụn","at":"Dan mereka berkata kepada kulit mereka, “Mengapa kamu menjadi saksi terhadap kami?” (Kulit) mereka men-jawab, “Yang menjadikan kami dapat berbicara adalah Allah, yang (juga) menjadikan segala sesuatu dapat berbicara, dan Dialah yang menciptakan kamu yang pertama kali dan hanya kepada-Nya kamu dikembalikan.”"},{"id":"22","ar":"وَمَا كُنْتُمْ تَسْتَتِرُوْنَ اَنْ يَّشْهَدَ عَلَيْكُمْ سَمْعُكُمْ وَلَآ اَبْصَارُكُمْ وَلَا جُلُوْدُكُمْ وَلٰكِنْ ظَنَنْتُمْ اَنَّ اللّٰهَ لَا يَعْلَمُ كَثِيْرًا مِّمَّا تَعْمَلُوْنَ","bc":"wa mā kuntum tastatirụna ay yasy-hada 'alaikum sam'ukum wa lā abṣārukum wa lā julụdukum wa lākin ẓanantum annallāha lā ya'lamu kaṡīram mimmā ta'malụn","at":"Dan kamu tidak dapat bersembunyi dari kesaksian pendengaran, penglihatan dan kulitmu terhadapmu ) bahkan kamu mengira Allah tidak mengetahui banyak tentang apa yang kamu lakukan."},{"id":"23","ar":"وَذٰلِكُمْ ظَنُّكُمُ الَّذِيْ ظَنَنْتُمْ بِرَبِّكُمْ اَرْدٰىكُمْ فَاَصْبَحْتُمْ مِّنَ الْخٰسِرِيْنَ","bc":"wa żālikum ẓannukumullażī ẓanantum birabbikum ardākum fa aṣbaḥtum minal-khāsirīn","at":"Dan itulah dugaanmu yang telah kamu sangkakan terhadap Tuhanmu (dugaan itu) telah membinasakan kamu, sehingga jadilah kamu termasuk orang yang rugi."},{"id":"24","ar":"فَاِنْ يَّصْبِرُوْا فَالنَّارُ مَثْوًى لَّهُمْ ۚوَاِنْ يَّسْتَعْتِبُوْا فَمَا هُمْ مِّنَ الْمُعْتَبِيْنَ","bc":"fa iy yaṣbirụ fan-nāru maṡwal lahum, wa iy yasta'tibụ fa mā hum minal-mu'tabīn","at":"Meskipun mereka bersabar (atas azab neraka) maka nerakalah tempat tinggal mereka dan jika mereka minta belas kasihan, maka mereka itu tidak termasuk orang yang pantas dikasihani."},{"id":"25","ar":"۞ وَقَيَّضْنَا لَهُمْ قُرَنَاۤءَ فَزَيَّنُوْا لَهُمْ مَّا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ وَحَقَّ عَلَيْهِمُ الْقَوْلُ فِيْٓ اُمَمٍ قَدْ خَلَتْ مِنْ قَبْلِهِمْ مِّنَ الْجِنِّ وَالْاِنْسِۚ اِنَّهُمْ كَانُوْا خٰسِرِيْنَ ","bc":"wa qayyaḍnā lahum quranā`a fa zayyanụ lahum mā baina aidīhim wa mā khalfahum wa ḥaqqa 'alaihimul-qaulu fī umaming qad khalat ming qablihim minal-jinni wal-ins, innahum kānụ khāsirīn","at":"Dan Kami tetapkan bagi mereka teman-teman (setan) yang memuji-muji apa saja yang ada di hadapan dan di belakang mereka dan tetaplah atas mereka putusan azab bersama umat-umat yang terdahulu sebelum mereka dari (golongan) jin dan manusia. Sungguh, mereka adalah orang-orang yang rugi."},{"id":"26","ar":"وَقَالَ الَّذِيْنَ كَفَرُوْا لَا تَسْمَعُوْا لِهٰذَا الْقُرْاٰنِ وَالْغَوْا فِيْهِ لَعَلَّكُمْ تَغْلِبُوْنَ","bc":"wa qālallażīna kafarụ lā tasma'ụ lihāżal-qur`āni walgau fīhi la'allakum taglibụn","at":"Dan orang-orang yang kafir berkata, “Janganlah kamu mendengarkan (bacaan) Al-Qur'an ini dan buatlah kegaduhan terhadapnya, agar kamu dapat mengalahkan (mereka).”"},{"id":"27","ar":"فَلَنُذِيْقَنَّ الَّذِيْنَ كَفَرُوْا عَذَابًا شَدِيْدًاۙ وَّلَنَجْزِيَنَّهُمْ اَسْوَاَ الَّذِيْ كَانُوْا يَعْمَلُوْنَ","bc":"fa lanużīqannallażīna kafarụ 'ażāban syadīdaw wa lanajziyannahum aswa`allażī kānụ ya'malụn","at":"Maka sungguh, akan Kami timpakan azab yang keras kepada orang-orang yang kafir itu dan sungguh, akan Kami beri balasan mereka dengan seburuk-buruk balasan terhadap apa yang telah mereka kerjakan."},{"id":"28","ar":"ذٰلِكَ جَزَاۤءُ اَعْدَاۤءِ اللّٰهِ النَّارُ لَهُمْ فِيْهَا دَارُ الْخُلْدِ ۗجَزَاۤءً ۢبِمَا كَانُوْا بِاٰيٰتِنَا يَجْحَدُوْنَ","bc":"żālika jazā`u a'dā`illāhin-nāru lahum fīhā dārul-khuld, jazā`am bimā kānụ bi`āyātinā yaj-ḥadụn","at":"Demikianlah balasan (terhadap) musuh-musuh Allah (yaitu) neraka; mereka mendapat tempat tinggal yang kekal di dalamnya sebagai balasan atas keingkaran mereka terhadap ayat-ayat Kami."},{"id":"29","ar":"وَقَالَ الَّذِيْنَ كَفَرُوْا رَبَّنَآ اَرِنَا الَّذَيْنِ اَضَلّٰنَا مِنَ الْجِنِّ وَالْاِنْسِ نَجْعَلْهُمَا تَحْتَ اَقْدَامِنَا لِيَكُوْنَا مِنَ الْاَسْفَلِيْنَ","bc":"wa qālallażīna kafarụ rabbanā arinallażaini aḍallānā minal-jinni wal-insi naj'al-humā taḥta aqdāminā liyakụnā minal-asfalīn","at":"Dan orang-orang yang kafir berkata, “Ya Tuhan kami, perlihatkanlah kepada kami dua golongan yang telah menyesatkan kami yaitu (golongan) jin dan manusia, agar kami letakkan keduanya di bawah telapak kaki kami agar kedua golongan itu menjadi yang paling bawah (hina).”"},{"id":"30","ar":"اِنَّ الَّذِيْنَ قَالُوْا رَبُّنَا اللّٰهُ ثُمَّ اسْتَقَامُوْا تَتَنَزَّلُ عَلَيْهِمُ الْمَلٰۤىِٕكَةُ اَلَّا تَخَافُوْا وَلَا تَحْزَنُوْا وَاَبْشِرُوْا بِالْجَنَّةِ الَّتِيْ كُنْتُمْ تُوْعَدُوْنَ","bc":"innallażīna qālụ rabbunallāhu ṡummastaqāmụ tatanazzalu 'alaihimul-malā`ikatu allā takhāfụ wa lā taḥzanụ wa absyirụ bil-jannatillatī kuntum tụ'adụn","at":"Sesungguhnya orang-orang yang berkata, “Tuhan kami adalah Allah” kemudian mereka meneguhkan pendirian mereka, maka malaikat-malaikat akan turun kepada mereka (dengan berkata), “Janganlah kamu merasa takut dan janganlah kamu bersedih hati; dan bergembiralah kamu dengan (memperoleh) surga yang telah dijanjikan kepadamu.”"},{"id":"31","ar":"نَحْنُ اَوْلِيَاۤؤُكُمْ فِى الْحَيٰوةِ الدُّنْيَا وَفِى الْاٰخِرَةِ ۚوَلَكُمْ فِيْهَا مَا تَشْتَهِيْٓ اَنْفُسُكُمْ وَلَكُمْ فِيْهَا مَا تَدَّعُوْنَ ۗ ","bc":"naḥnu auliyā`ukum fil-ḥayātid-dun-yā wa fil-ākhirah, wa lakum fīhā mā tasytahī anfusukum wa lakum fīhā mā tadda'ụn","at":"Kamilah pelindung-pelindungmu dalam kehidupan dunia dan akhirat; di dalamnya (surga) kamu memperoleh apa yang kamu inginkan dan memperoleh apa yang kamu minta."},{"id":"32","ar":"نُزُلًا مِّنْ غَفُوْرٍ رَّحِيْمٍ ","bc":"nuzulam min gafụrir raḥīm","at":"Sebagai penghormatan (bagimu) dari (Allah) Yang Maha Pengampun, Maha Penyayang."},{"id":"33","ar":"وَمَنْ اَحْسَنُ قَوْلًا مِّمَّنْ دَعَآ اِلَى اللّٰهِ وَعَمِلَ صَالِحًا وَّقَالَ اِنَّنِيْ مِنَ الْمُسْلِمِيْنَ ","bc":"wa man aḥsanu qaulam mim man da'ā ilallāhi wa 'amila ṣāliḥaw wa qāla innanī minal-muslimīn","at":"Dan siapakah yang lebih baik perkataannya daripada orang yang menyeru kepada Allah dan mengerjakan kebajikan dan berkata, “Sungguh, aku termasuk orang-orang muslim (yang berserah diri)?”"},{"id":"34","ar":"وَلَا تَسْتَوِى الْحَسَنَةُ وَلَا السَّيِّئَةُ ۗاِدْفَعْ بِالَّتِيْ هِيَ اَحْسَنُ فَاِذَا الَّذِيْ بَيْنَكَ وَبَيْنَهٗ عَدَاوَةٌ كَاَنَّهٗ وَلِيٌّ حَمِيْمٌ ","bc":"wa lā tastawil-ḥasanatu wa las-sayyi`ah, idfa' billatī hiya aḥsanu fa iżallażī bainaka wa bainahụ 'adāwatung ka`annahụ waliyyun ḥamīm","at":"Dan tidaklah sama kebaikan dengan kejahatan. Tolaklah (kejahatan itu) dengan cara yang lebih baik, sehingga orang yang ada rasa permusuhan an-tara kamu dan dia akan seperti teman yang setia."},{"id":"35","ar":"وَمَا يُلَقّٰىهَآ اِلَّا الَّذِيْنَ صَبَرُوْاۚ وَمَا يُلَقّٰىهَآ اِلَّا ذُوْ حَظٍّ عَظِيْمٍ","bc":"wa mā yulaqqāhā illallażīna ṣabarụ, wa mā yulaqqāhā illā żụ ḥaẓẓin 'aẓīm","at":"Dan (sifat-sifat yang baik itu) tidak akan dianugerahkan kecuali kepada orang-orang yang sabar dan tidak dianugerahkan kecuali kepada orang-orang yang mempunyai keberuntungan yang besar."},{"id":"36","ar":"وَاِمَّا يَنْزَغَنَّكَ مِنَ الشَّيْطٰنِ نَزْغٌ فَاسْتَعِذْ بِاللّٰهِ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ ","bc":"wa immā yanzagannaka minasy-syaiṭāni nazgun fasta'iż billāh, innahụ huwas-samī'ul-'alīm","at":"Dan jika setan mengganggumu dengan suatu godaan, maka mohonlah perlindungan kepada Allah. Sungguh, Dialah Yang Maha Mendengar, Maha Mengetahui."},{"id":"37","ar":"وَمِنْ اٰيٰتِهِ الَّيْلُ وَالنَّهَارُ وَالشَّمْسُ وَالْقَمَرُۗ لَا تَسْجُدُوْا لِلشَّمْسِ وَلَا لِلْقَمَرِ وَاسْجُدُوْا لِلّٰهِ الَّذِيْ خَلَقَهُنَّ اِنْ كُنْتُمْ اِيَّاهُ تَعْبُدُوْنَ ","bc":"wa min āyātihil-lailu wan-nahāru wasy-syamsu wal-qamar, lā tasjudụ lisy-syamsi wa lā lil-qamari wasjudụ lillāhillażī khalaqahunna ing kuntum iyyāhu ta'budụn","at":"Dan sebagian dari tanda-tanda kebesaran-Nya ialah malam, siang, mata-hari dan bulan. Janganlah bersujud kepada matahari dan jangan (pula) kepada bulan, tetapi bersujudlah kepada Allah yang menciptakannya, jika kamu hanya menyembah kepada-Nya."},{"id":"38","ar":"فَاِنِ اسْتَكْبَرُوْا فَالَّذِيْنَ عِنْدَ رَبِّكَ يُسَبِّحُوْنَ لَهٗ بِالَّيْلِ وَالنَّهَارِ وَهُمْ لَا يَسْـَٔمُوْنَ ۩","bc":"fa inistakbarụ fallażīna 'inda rabbika yusabbiḥụna lahụ bil-laili wan-nahāri wa hum lā yas`amụn","at":"Jika mereka menyombongkan diri, maka mereka (malaikat) yang di sisi Tuhanmu bertasbih kepada-Nya pada malam dan siang hari, sedang mereka tidak pernah jemu."},{"id":"39","ar":"وَمِنْ اٰيٰتِهٖٓ اَنَّكَ تَرَى الْاَرْضَ خَاشِعَةً فَاِذَآ اَنْزَلْنَا عَلَيْهَا الْمَاۤءَ اهْتَزَّتْ وَرَبَتْۗ اِنَّ الَّذِيْٓ اَحْيَاهَا لَمُحْيِ الْمَوْتٰى ۗاِنَّهٗ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"wa min āyātihī annaka taral-arḍa khāsyi'atan fa iżā anzalnā 'alaihal-mā`ahtazzat wa rabat, innallażī aḥyāhā lamuḥyil-mautā, innahụ 'alā kulli syai`ing qadīr","at":"Dan sebagian dari tanda-tanda (kebesaran)-Nya, engkau melihat bumi itu kering dan tandus, tetapi apabila Kami turunkan hujan di atasnya, niscaya ia bergerak dan subur. Sesungguhnya (Allah) yang menghidupkannya pasti dapat menghidupkan yang mati; sesungguhnya Dia Mahakuasa atas segala sesuatu."},{"id":"40","ar":"اِنَّ الَّذِيْنَ يُلْحِدُوْنَ فِيْٓ اٰيٰتِنَا لَا يَخْفَوْنَ عَلَيْنَاۗ اَفَمَنْ يُّلْقٰى فِى النَّارِ خَيْرٌ اَمَّنْ يَّأْتِيْٓ اٰمِنًا يَّوْمَ الْقِيٰمَةِ ۗاِعْمَلُوْا مَا شِئْتُمْ ۙاِنَّهٗ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ","bc":"innallażīna yul-ḥidụna fī āyātinā lā yakhfauna 'alainā, a fa may yulqā fin-nāri khairun am may ya`tī āminay yaumal-qiyāmah, i'malụ mā syi`tum innahụ bimā ta'malụna baṣīr","at":"Sesungguhnya orang-orang yang mengingkari tanda-tanda (kebesaran) Kami, mereka tidak tersembunyi dari Kami. Apakah orang-orang yang dilemparkan ke dalam neraka yang lebih baik ataukah mereka yang datang dengan aman sentosa pada hari Kiamat? Lakukanlah apa yang kamu kehendaki! Sungguh, Dia Maha Melihat apa yang kamu kerjakan."},{"id":"41","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا بِالذِّكْرِ لَمَّا جَاۤءَهُمْ ۗوَاِنَّهٗ لَكِتٰبٌ عَزِيْزٌ ۙ","bc":"innallażīna kafarụ biż-żikri lammā jā`ahum, wa innahụ lakitābun 'azīz","at":"Sesungguhnya orang-orang yang mengingkari Al-Qur'an ketika (Al-Qur'an) itu disampaikan kepada mereka (mereka itu pasti akan celaka), dan sesungguhnya (Al-Qur'an) itu adalah Kitab yang mulia,"},{"id":"42","ar":"لَّا يَأْتِيْهِ الْبَاطِلُ مِنْۢ بَيْنِ يَدَيْهِ وَلَا مِنْ خَلْفِهٖ ۗتَنْزِيْلٌ مِّنْ حَكِيْمٍ حَمِيْدٍ ","bc":"lā ya`tīhil-bāṭilu mim baini yadaihi wa lā min khalfih, tanzīlum min ḥakīmin ḥamīd","at":"yang) tidak akan didatangi oleh kebatilan baik dari depan maupun dari belakang (pada masa lalu dan yang akan datang), yang diturunkan dari Tuhan Yang Mahabijaksana, Maha Terpuji."},{"id":"43","ar":"مَا يُقَالُ لَكَ اِلَّا مَا قَدْ قِيْلَ لِلرُّسُلِ مِنْ قَبْلِكَ ۗاِنَّ رَبَّكَ لَذُوْ مَغْفِرَةٍ وَّذُوْ عِقَابٍ اَلِيْمٍ ","bc":"mā yuqālu laka illā mā qad qīla lir-rusuli ming qablik, inna rabbaka lażụ magfiratiw wa żụ 'iqābin alīm","at":"Apa yang dikatakan (oleh orang-orang kafir) kepadamu tidak lain adalah apa yang telah dikatakan kepada rasul-rasul sebelummu. Sungguh, Tuhanmu mempunyai ampunan dan azab yang pedih."},{"id":"44","ar":"وَلَوْ جَعَلْنٰهُ قُرْاٰنًا اَعْجَمِيًّا لَّقَالُوْا لَوْلَا فُصِّلَتْ اٰيٰتُهٗ ۗ ءَاَ۬عْجَمِيٌّ وَّعَرَبِيٌّ ۗ قُلْ هُوَ لِلَّذِيْنَ اٰمَنُوْا هُدًى وَّشِفَاۤءٌ ۗوَالَّذِيْنَ لَا يُؤْمِنُوْنَ فِيْٓ اٰذَانِهِمْ وَقْرٌ وَّهُوَ عَلَيْهِمْ عَمًىۗ اُولٰۤىِٕكَ يُنَادَوْنَ مِنْ مَّكَانٍۢ بَعِيْدٍ","bc":"walau ja'alnāhu qur`ānan a'jamiyyal laqālụ lau lā fuṣṣilat āyātuh, a a'jamiyyuw wa 'arabiyy, qul huwa lillażīna āmanụ hudaw wa syifā`, wallażīna lā yu`minụna fī āżānihim waqruw wa huwa 'alaihim 'amā, ulā`ika yunādauna mim makānim ba'īd","at":"Dan sekiranya Al-Qur'an Kami jadikan sebagai bacaan dalam bahasa selain bahasa Arab niscaya mereka mengatakan, “Mengapa tidak dijelaskan ayat-ayatnya?” Apakah patut (Al-Qur'an) dalam bahasa selain bahasa Arab sedang (rasul), orang Arab? Katakanlah, “Al-Qur'an adalah petunjuk dan penyembuh bagi orang-orang yang beriman. Dan orang-orang yang tidak beriman pada telinga mereka ada sumbatan, dan (Al-Qur'an) itu merupakan kegelapan bagi mereka. Mereka itu (seperti) orang-orang yang dipanggil dari tempat yang jauh.”"},{"id":"45","ar":"وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ فَاخْتُلِفَ فِيْهِ ۗوَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَقُضِيَ بَيْنَهُمْ ۗوَاِنَّهُمْ لَفِيْ شَكٍّ مِّنْهُ مُرِيْبٍ ","bc":"wa laqad ātainā mụsal-kitāba fakhtulifa fīh, walau lā kalimatun sabaqat mir rabbika laquḍiya bainahum, wa innahum lafī syakkim min-hu murīb","at":"Dan sungguh, telah Kami berikan kepada Musa Kitab (Taurat) lalu diperselisihkan. Sekiranya tidak ada keputusan yang terdahulu dari Tuhanmu, orang-orang kafir itu pasti sudah dibinasakan. Dan sesungguhnya mereka benar-benar dalam keraguan yang mendalam terhadapnya."},{"id":"46","ar":"مَنْ عَمِلَ صَالِحًا فَلِنَفْسِهٖ ۙوَمَنْ اَسَاۤءَ فَعَلَيْهَا ۗوَمَا رَبُّكَ بِظَلَّامٍ لِّلْعَبِيْدِ ۔ ","bc":"man 'amila ṣāliḥan falinafsihī wa man asā`a fa 'alaihā, wa mā rabbuka biẓallāmil lil-'abīd","at":"Barangsiapa mengerjakan kebajikan maka (pahalanya) untuk dirinya sendiri dan barangsiapa berbuat jahat maka (dosanya) menjadi tanggungan dirinya sendiri. Dan Tuhanmu sama sekali tidak menzalimi hamba-hamba(-Nya)."},{"id":"47","ar":"۞ اِلَيْهِ يُرَدُّ عِلْمُ السَّاعَةِ ۗوَمَا تَخْرُجُ مِنْ ثَمَرٰتٍ ِمّنْ اَكْمَامِهَا وَمَا تَحْمِلُ مِنْ اُنْثٰى وَلَا تَضَعُ اِلَّا بِعِلْمِهٖ ۗوَيَوْمَ يُنَادِيْهِمْ اَيْنَ شُرَكَاۤءِيْۙ قَالُوْٓا اٰذَنّٰكَ مَا مِنَّا مِنْ شَهِيْدٍ ۚ ","bc":"ilaihi yuraddu 'ilmus-sā'ah, wa mā takhruju min ṡamarātim min akmāmihā wa mā taḥmilu min unṡā wa lā taḍa'u illā bi'ilmih, wa yauma yunādīhim aina syurakā`ī qālū āżannāka mā minnā min syahīd","at":"Kepada-Nyalah ilmu tentang hari Kiamat itu dikembalikan. Tidak ada buah-buahan yang keluar dari kelopaknya dan tidak seorang perempuan pun yang mengandung dan yang melahirkan, melainkan semuanya dengan sepengetahuan-Nya. Pada hari ketika Dia (Allah) menyeru mereka, “Dimanakah sekutu-sekutu-Ku itu?” Mereka menjawab, “Kami nyatakan kepada Engkau bahwa tidak ada seorang pun di antara kami yang dapat memberi kesaksian (bahwa Engkau mempunyai sekutu).”"},{"id":"48","ar":"وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَدْعُوْنَ مِنْ قَبْلُ وَظَنُّوْا مَا لَهُمْ مِّنْ مَّحِيْصٍ ","bc":"wa ḍalla 'an-hum mā kānụ yad'ụna ming qablu wa ẓannụ mā lahum mim maḥīṣ","at":"Dan lenyaplah dari mereka apa yang dahulu selalu mereka sembah, dan mereka pun tahu bahwa tidak ada jalan keluar (dari azab Allah) bagi mereka."},{"id":"49","ar":"لَا يَسْـَٔمُ الْاِنْسَانُ مِنْ دُعَاۤءِ الْخَيْرِۖ وَاِنْ مَّسَّهُ الشَّرُّ فَيَـُٔوْسٌ قَنُوْطٌ ","bc":"lā yas`amul-insānu min du'ā`il-khairi wa im massahusy-syarru fa ya`ụsung qanụṭ","at":"Manusia tidak jemu memohon kebaikan, dan jika ditimpa malapetaka, mereka berputus asa dan hilang harapannya."},{"id":"50","ar":"وَلَىِٕنْ اَذَقْنٰهُ رَحْمَةً مِّنَّا مِنْۢ بَعْدِ ضَرَّاۤءَ مَسَّتْهُ لَيَقُوْلَنَّ هٰذَا لِيْۙ وَمَآ اَظُنُّ السَّاعَةَ قَاۤىِٕمَةًۙ وَّلَىِٕنْ رُّجِعْتُ اِلٰى رَبِّيْٓ اِنَّ لِيْ عِنْدَهٗ لَلْحُسْنٰىۚ فَلَنُنَبِّئَنَّ الَّذِيْنَ كَفَرُوْا بِمَا عَمِلُوْاۖ وَلَنُذِيْقَنَّهُمْ مِّنْ عَذَابٍ غَلِيْظٍ ","bc":"wa la`in ażaqnāhu raḥmatam minnā mim ba'di ḍarrā`a massat-hu layaqụlanna hāżā lī wa mā aẓunnus-sā'ata qā`imataw wa la`ir ruji'tu ilā rabbī inna lī 'indahụ lal-ḥusnā, fa lanunabbi`annallażīna kafarụ bimā 'amilụ wa lanużīqannahum min 'ażābin galīẓ","at":"Dan jika Kami berikan kepadanya suatu rahmat dari Kami setelah ditimpa kesusahan, pastilah dia berkata, “Ini adalah hakku, dan aku tidak yakin bahwa hari Kiamat itu akan terjadi. Dan jika aku dikembalikan kepada Tuhanku, sesungguhnya aku akan memperoleh kebaikan di sisi-Nya.” Maka sungguh, akan Kami beritahukan kepada orang-orang kafir tentang apa yang telah mereka kerjakan, dan sungguh, akan Kami timpakan kepada mereka azab yang berat."},{"id":"51","ar":"وَاِذَآ اَنْعَمْنَا عَلَى الْاِنْسَانِ اَعْرَضَ وَنَاٰ بِجَانِبِهٖۚ وَاِذَا مَسَّهُ الشَّرُّ فَذُوْ دُعَاۤءٍ عَرِيْضٍ ","bc":"wa iżā an'amnā 'alal-insāni a'raḍa wa na`ā bijānibih, wa iżā massahusy-syarru fa żụ du'ā`in 'arīḍ","at":"Dan apabila Kami berikan nikmat kepada manusia, dia berpaling dan menjauhkan diri (dengan sombong); tetapi apabila ditimpa malapetaka maka dia banyak berdoa."},{"id":"52","ar":"قُلْ اَرَءَيْتُمْ اِنْ كَانَ مِنْ عِنْدِ اللّٰهِ ثُمَّ كَفَرْتُمْ بِهٖ مَنْ اَضَلُّ مِمَّنْ هُوَ فِيْ شِقَاقٍۢ بَعِيْدٍ ","bc":"qul a ra`aitum ing kāna min 'indillāhi ṡumma kafartum bihī man aḍallu mim man huwa fī syiqāqim ba'īd","at":"Katakanlah, “Bagaimana pendapatmu jika (Al-Qur'an) itu datang dari sisi Allah, kemudian kamu mengingkarinya. Siapakah yang lebih sesat daripada orang yang selalu berada dalam penyimpangan yang jauh (dari kebenaran)?”"},{"id":"53","ar":"سَنُرِيْهِمْ اٰيٰتِنَا فِى الْاٰفَاقِ وَفِيْٓ اَنْفُسِهِمْ حَتّٰى يَتَبَيَّنَ لَهُمْ اَنَّهُ الْحَقُّۗ اَوَلَمْ يَكْفِ بِرَبِّكَ اَنَّهٗ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ","bc":"sanurīhim āyātinā fil-āfāqi wa fī anfusihim ḥattā yatabayyana lahum annahul-ḥaqq, a wa lam yakfi birabbika annahụ 'alā kulli syai`in syahīd","at":"Kami akan memperlihatkan kepada mereka tanda-tanda (kebesaran) Kami di segenap penjuru dan pada diri mereka sendiri, sehingga jelaslah bagi mereka bahwa Al-Qur'an itu adalah benar. Tidak cukupkah (bagi kamu) bahwa Tuhanmu menjadi saksi atas segala sesuatu?"},{"id":"54","ar":"اَلَآ اِنَّهُمْ فِيْ مِرْيَةٍ مِّنْ لِّقَاۤءِ رَبِّهِمْ ۗ اَلَآ اِنَّهٗ بِكُلِّ شَيْءٍ مُّحِيْطٌ","bc":"alā innahum fī miryatim mil liqā`i rabbihim, alā innahụ bikulli syai`im muḥīṭ","at":"Ingatlah, sesungguhnya mereka dalam keraguan tentang pertemuan dengan Tuhan mereka. Ingatlah, sesungguhnya Dia Maha Meliputi segala sesuatu. "}],gafir=[{"id":"1","ar":"حٰمۤ ۚ ","bc":"ḥā mīm","at":"Ha Mim"},{"id":"2","ar":"تَنْزِيْلُ الْكِتٰبِ مِنَ اللّٰهِ الْعَزِيْزِ الْعَلِيْمِۙ","bc":"tanzīlul-kitābi minallāhil-'azīzil-'alīm","at":"Kitab ini (Al-Qur'an) diturunkan dari Allah Yang Mahaperkasa, Maha Mengetahui,"},{"id":"3","ar":"غَافِرِ الذَّنْۢبِ وَقَابِلِ التَّوْبِ شَدِيْدِ الْعِقَابِ ذِى الطَّوْلِۗ لَآ اِلٰهَ اِلَّا هُوَ ۗاِلَيْهِ الْمَصِيْرُ ","bc":"gāfiriż-żambi wa qābilit-taubi syadīdil-'iqābi żiṭ-ṭaụl, lā ilāha illā huw, ilaihil-maṣīr","at":"yang mengampuni dosa dan menerima tobat dan keras hukuman-Nya; yang memiliki karunia. Tidak ada tuhan selain Dia. Hanya kepada-Nyalah (semua makhluk) kembali."},{"id":"4","ar":"مَا يُجَادِلُ فِيْٓ اٰيٰتِ اللّٰهِ اِلَّا الَّذِيْنَ كَفَرُوْا فَلَا يَغْرُرْكَ تَقَلُّبُهُمْ فِى الْبِلَادِ","bc":"mā yujādilu fī āyātillāhi illallażīna kafarụ fa lā yagrurka taqallubuhum fil-bilād","at":"Tidak ada yang memperdebatkan tentang ayat-ayat Allah, kecuali orang-orang yang kafir. Karena itu janganlah engkau (Muhammad) tertipu oleh keberhasilan usaha mereka di seluruh negeri."},{"id":"5","ar":"كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَّالْاَحْزَابُ مِنْۢ بَعْدِهِمْ ۖوَهَمَّتْ كُلُّ اُمَّةٍۢ بِرَسُوْلِهِمْ لِيَأْخُذُوْهُ وَجَادَلُوْا بِالْبَاطِلِ لِيُدْحِضُوا بِهِ الْحَقَّ فَاَخَذْتُهُمْ ۗفَكَيْفَ كَانَ عِقَابِ","bc":"każżabat qablahum qaumu nụḥiw wal-aḥzābu mim ba'dihim wa hammat kullu ummatim birasụlihim liya`khużụhu wa jādalụ bil-bāṭili liyud-ḥiḍụ bihil-ḥaqqa fa akhażtuhum, fa kaifa kāna 'iqāb","at":"Sebelum mereka, kaum Nuh dan golongan-golongan yang bersekutu setelah mereka telah mendustakan (rasul) dan setiap umat telah merencanakan (tipu daya) terhadap rasul mereka untuk menawannya dan mereka membantah dengan (alasan) yang batil untuk melenyapkan kebenaran; karena itu Aku tawan mereka (dengan azab). Maka betapa (pedihnya) azab-Ku?"},{"id":"6","ar":"وَكَذٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِيْنَ كَفَرُوْٓا اَنَّهُمْ اَصْحٰبُ النَّارِۘ ","bc":"wa każālika ḥaqqat kalimatu rabbika 'alallażīna kafarū annahum aṣ-ḥābun-nār","at":"Dan demikianlah telah pasti berlaku ketetapan Tuhanmu terhadap orang-orang kafir, (yaitu) sesungguhnya mereka adalah penghuni neraka."},{"id":"7","ar":"اَلَّذِيْنَ يَحْمِلُوْنَ الْعَرْشَ وَمَنْ حَوْلَهٗ يُسَبِّحُوْنَ بِحَمْدِ رَبِّهِمْ وَيُؤْمِنُوْنَ بِهٖ وَيَسْتَغْفِرُوْنَ لِلَّذِيْنَ اٰمَنُوْاۚ رَبَّنَا وَسِعْتَ كُلَّ شَيْءٍ رَّحْمَةً وَّعِلْمًا فَاغْفِرْ لِلَّذِيْنَ تَابُوْا وَاتَّبَعُوْا سَبِيْلَكَ وَقِهِمْ عَذَابَ الْجَحِيْمِ ","bc":"allażīna yaḥmilụnal-'arsya wa man ḥaulahụ yusabbiḥụna biḥamdi rabbihim wa yu`minụna bihī wa yastagfirụna lillażīna āmanụ, rabbanā wasi'ta kulla syai`ir raḥmataw wa 'ilman fagfir lillażīna tābụ wattaba'ụ sabīlaka wa qihim 'ażābal-jaḥīm","at":"(Malaikat-malaikat) yang memikul ‘Arsy dan (malaikat) yang berada di sekelilingnya bertasbih dengan memuji Tuhannya dan mereka beriman kepada-Nya serta memohonkan ampunan untuk orang-orang yang beriman (seraya berkata), “Ya Tuhan kami, rahmat dan ilmu yang ada pada-Mu meliputi segala sesuatu, maka berilah ampunan kepada orang-orang yang bertobat dan mengikuti jalan (agama)-Mu dan peliharalah mereka dari azab neraka yang menyala-nyala."},{"id":"8","ar":"رَبَّنَا وَاَدْخِلْهُمْ جَنّٰتِ عَدْنِ ِۨالَّتِيْ وَعَدْتَّهُمْ وَمَنْ صَلَحَ مِنْ اٰبَاۤىِٕهِمْ وَاَزْوَاجِهِمْ وَذُرِّيّٰتِهِمْ ۗاِنَّكَ اَنْتَ الْعَزِيْزُ الْحَكِيْمُۙ ","bc":"rabbanā wa adkhil-hum jannāti 'adninillatī wa'attahum wa man ṣalaḥa min ābā`ihim wa azwājihim wa żurriyyātihim, innaka antal-'azīzul-ḥakīm","at":"Ya Tuhan kami, masukkanlah mereka ke dalam surga ‘Adn yang telah Engkau janjikan kepada mereka, dan orang yang saleh di antara nenek moyang mereka, istri-istri, dan keturunan mereka. Sungguh, Engkaulah Yang Mahaperkasa, Mahabijaksana,"},{"id":"9","ar":"وَقِهِمُ السَّيِّاٰتِۗ وَمَنْ تَقِ السَّيِّاٰتِ يَوْمَىِٕذٍ فَقَدْ رَحِمْتَهٗ ۗوَذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُ ","bc":"wa qihimus-sayyi`āt, wa man taqis-sayyi`āti yauma`iżin fa qad raḥimtah, wa żālika huwal-fauzul-'aẓīm","at":"dan peliharalah mereka dari (bencana) kejahatan. Dan orang-orang yang Engkau pelihara dari (bencana) kejahatan pada hari itu, maka sungguh, Engkau telah menganugerahkan rahmat kepadanya dan demikian itulah kemenangan yang agung.”"},{"id":"10","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا يُنَادَوْنَ لَمَقْتُ اللّٰهِ اَكْبَرُ مِنْ مَّقْتِكُمْ اَنْفُسَكُمْ اِذْ تُدْعَوْنَ اِلَى الْاِيْمَانِ فَتَكْفُرُوْنَ","bc":"innallażīna kafarụ yunādauna lamaqtullāhi akbaru mim maqtikum anfusakum iż tud'auna ilal-īmāni fa takfurụn","at":"Sesungguhnya orang-orang yang kafir, kepada mereka (pada hari Kiamat) diserukan, “Sungguh, kebencian Allah (kepadamu) jauh lebih besar daripada kebencianmu kepada dirimu sendiri, ketika kamu diseru untuk beriman lalu kamu mengingkarinya.”"},{"id":"11","ar":"قَالُوْا رَبَّنَآ اَمَتَّنَا اثْنَتَيْنِ وَاَحْيَيْتَنَا اثْنَتَيْنِ فَاعْتَرَفْنَا بِذُنُوْبِنَا فَهَلْ اِلٰى خُرُوْجٍ مِّنْ سَبِيْلٍ","bc":"qālụ rabbanā amattanaṡnataini wa aḥyaitanaṡnataini fa'tarafnā biżunụbinā fa hal ilā khurụjim min sabīl","at":"Mereka menjawab, “Ya Tuhan kami, Engkau telah mematikan kami dua kali dan telah menghidupkan kami dua kali (pula), lalu kami mengakui dosa-dosa kami. Maka adakah jalan (bagi kami) untuk keluar (dari neraka)?”"},{"id":"12","ar":"ذٰلِكُمْ بِاَنَّهٗٓ اِذَا دُعِيَ اللّٰهُ وَحْدَهٗ كَفَرْتُمْۚ وَاِنْ يُّشْرَكْ بِهٖ تُؤْمِنُوْا ۗفَالْحُكْمُ لِلّٰهِ الْعَلِيِّ الْكَبِيْرِ","bc":"żālikum bi`annahū iżā du'iyallāhu waḥdahụ kafartum, wa iy yusyrak bihī tu`minụ, fal-ḥukmu lillāhil-'aliyyil-kabīr","at":"Yang demikian itu karena sesungguhnya kamu mengingkari apabila diseru untuk menyembah Allah saja. Dan jika Allah dipersekutukan, kamu percaya. Maka keputusan (sekarang ini) adalah pada Allah Yang Mahatinggi, Mahabesar."},{"id":"13","ar":"هُوَ الَّذِيْ يُرِيْكُمْ اٰيٰتِهٖ وَيُنَزِّلُ لَكُمْ مِّنَ السَّمَاۤءِ رِزْقًا ۗوَمَا يَتَذَكَّرُ اِلَّا مَنْ يُّنِيْبُ","bc":"huwallażī yurīkum āyātihī wa yunazzilu lakum minas-samā`i rizqā, wa mā yatażakkaru illā may yunīb","at":"Dialah yang memperlihatkan tanda-tanda (kekuasaan)-Nya kepadamu dan menurunkan rezeki dari langit untukmu. Dan tidak lain yang mendapat pelajaran hanyalah orang-orang yang kembali (kepada Allah)."},{"id":"14","ar":"فَادْعُوا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ وَلَوْ كَرِهَ الْكٰفِرُوْنَ","bc":"fad'ullāha mukhliṣīna lahud-dīna walau karihal-kāfirụn","at":"Maka sembahlah Allah dengan tulus ikhlas beragama kepada-Nya, meskipun orang-orang kafir tidak menyukai(nya)."},{"id":"15","ar":"رَفِيْعُ الدَّرَجٰتِ ذُو الْعَرْشِۚ يُلْقِى الرُّوْحَ مِنْ اَمْرِهٖ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ لِيُنْذِرَ يَوْمَ التَّلَاقِۙ","bc":"rafī'ud-darajāti żul-'arsy, yulqir-rụḥa min amrihī 'alā may yasyā`u min 'ibādihī liyunżira yaumat-talāq","at":"(Dialah) Yang Mahatinggi derajat-Nya, yang memiliki ’Arsy, yang menurunkan wahyu dengan perintah-Nya kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya, agar memperingatkan (manusia) tentang hari pertemuan (hari Kiamat),"},{"id":"16","ar":"يَوْمَ هُمْ بَارِزُوْنَ ۚ لَا يَخْفٰى عَلَى اللّٰهِ مِنْهُمْ شَيْءٌ ۗلِمَنِ الْمُلْكُ الْيَوْمَ ۗ لِلّٰهِ الْوَاحِدِ الْقَهَّارِ","bc":"yauma hum bārizụn, lā yakhfā 'alallāhi min-hum syaī`, limanil-mulkul-yaụm, lillāhil-wāḥidil-qahhār","at":"(yaitu) pada hari (ketika) mereka keluar (dari kubur); tidak sesuatu pun keadaan mereka yang tersembunyi di sisi Allah. (Lalu Allah berfirman), “Milik siapakah kerajaan pada hari ini?” Milik Allah Yang Maha Esa, Maha Mengalahkan."},{"id":"17","ar":"اَلْيَوْمَ تُجْزٰى كُلُّ نَفْسٍۢ بِمَا كَسَبَتْ ۗ لَا ظُلْمَ الْيَوْمَ ۗاِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ","bc":"al-yauma tujzā kullu nafsim bimā kasabat, lā ẓulmal-yaụm, innallāha sarī'ul-ḥisāb","at":"Pada hari ini setiap jiwa diberi balasan sesuai dengan apa yang telah dikerjakannya. Tidak ada yang dirugikan pada hari ini. Sungguh, Allah sangat cepat perhitungan-Nya."},{"id":"18","ar":"وَاَنْذِرْهُمْ يَوْمَ الْاٰزِفَةِ اِذِ الْقُلُوْبُ لَدَى الْحَنَاجِرِ كَاظِمِيْنَ ەۗ مَا لِلظّٰلِمِيْنَ مِنْ حَمِيْمٍ وَّلَا شَفِيْعٍ يُّطَاعُۗ","bc":"wa anżir-hum yaumal-āzifati iżil-qulụbu ladal-ḥanājiri kāẓimīn, mā liẓ-ẓālimīna min ḥamīmiw wa lā syafī'iy yuṭā'","at":"Dan berilah mereka peringatan akan hari yang semakin dekat (hari Kiamat, yaitu) ketika hati (menyesak) sampai di kerongkongan karena menahan kesedihan. Tidak ada seorang pun teman setia bagi orang yang zalim dan tidak ada baginya seorang penolong yang diterima (pertolongannya)."},{"id":"19","ar":"يَعْلَمُ خَاۤىِٕنَةَ الْاَعْيُنِ وَمَا تُخْفِى الصُّدُوْرُ","bc":"ya'lamu khā`inatal-a'yuni wa mā tukhfiṣ-ṣudụr","at":"Dia mengetahui (pandangan) mata yang khianat dan apa yang tersembunyi dalam dada."},{"id":"20","ar":"وَاللّٰهُ يَقْضِيْ بِالْحَقِّ ۗوَالَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِهٖ لَا يَقْضُوْنَ بِشَيْءٍ ۗاِنَّ اللّٰهَ هُوَ السَّمِيْعُ الْبَصِيْرُ","bc":"wallāhu yaqḍī bil-ḥaqq, wallażīna yad'ụna min dụnihī lā yaqḍụna bisyaī`, innallāha huwas-samī'ul-baṣīr","at":"Dan Allah memutuskan dengan kebenaran. Sedang mereka yang disembah selain-Nya tidak mampu memutuskan dengan sesuatu apa pun. Sesungguhnya Allah, Dialah Yang Maha Mendengar, Maha Melihat."},{"id":"21","ar":"۞ اَوَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ كَانُوْا مِنْ قَبْلِهِمْ ۗ كَانُوْا هُمْ اَشَدَّ مِنْهُمْ قُوَّةً وَّاٰثَارًا فِى الْاَرْضِ فَاَخَذَهُمُ اللّٰهُ بِذُنُوْبِهِمْ ۗوَمَا كَانَ لَهُمْ مِّنَ اللّٰهِ مِنْ وَّاقٍ","bc":"a wa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna 'āqibatullażīna kānụ ming qablihim, kānụ hum asyadda min-hum quwwataw wa āṡāran fil-arḍi fa akhażahumullāhu biżunụbihim, wa mā kāna lahum minallāhi miw wāq","at":"Dan apakah mereka tidak mengadakan perjalanan di bumi, lalu memperhatikan bagaimana kesudahan orang-orang yang sebelum mereka? Orang-orang itu lebih hebat kekuatannya daripada mereka dan (lebih banyak) peninggalan-peninggalan (peradaban)nya di bumi, tetapi Allah mengazab mereka karena dosa-dosanya. Dan tidak akan ada sesuatu pun yang melindungi mereka dari (azab) Allah."},{"id":"22","ar":"ذٰلِكَ بِاَنَّهُمْ كَانَتْ تَّأْتِيْهِمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَكَفَرُوْا فَاَخَذَهُمُ اللّٰهُ ۗاِنَّهٗ قَوِيٌّ شَدِيْدُ الْعِقَابِ","bc":"żālika bi`annahum kānat ta`tīhim rusuluhum bil-bayyināti fa kafarụ fa akhażahumullāh, innahụ qawiyyun syadīdul-'iqāb","at":"Yang demikian itu adalah karena sesungguhnya rasul-rasul telah datang kepada mereka dengan membawa bukti-bukti yang nyata lalu mereka ingkar; maka Allah mengazab mereka. Sungguh, Dia Mahakuat, Mahakeras hukuman-Nya."},{"id":"23","ar":"وَلَقَدْ اَرْسَلْنَا مُوسٰى بِاٰيٰتِنَا وَسُلْطٰنٍ مُّبِيْنٍۙ","bc":"wa laqad arsalnā mụsā bi`āyātinā wa sulṭānim mubīn","at":"Dan sungguh, Kami telah mengutus Musa dengan membawa ayat-ayat Kami dan keterangan yang nyata,"},{"id":"24","ar":"اِلٰى فِرْعَوْنَ وَهَامٰنَ وَقَارُوْنَ فَقَالُوْا سٰحِرٌ كَذَّابٌ","bc":"ilā fir'auna wa hāmāna wa qārụna fa qālụ sāḥirung każżāb","at":"kepada Fir‘aun, Haman dan Karun; lalu mereka berkata, “(Musa) itu seorang pesihir dan pendusta.”"},{"id":"25","ar":"فَلَمَّا جَاۤءَهُمْ بِالْحَقِّ مِنْ عِنْدِنَا قَالُوا اقْتُلُوْٓا اَبْنَاۤءَ الَّذِيْنَ اٰمَنُوْا مَعَهٗ وَاسْتَحْيُوْا نِسَاۤءَهُمْ ۗوَمَا كَيْدُ الْكٰفِرِيْنَ اِلَّا فِيْ ضَلٰلٍ","bc":"fa lammā jā`ahum bil-ḥaqqi min 'indinā qāluqtulū abnā`allażīna āmanụ ma'ahụ wastaḥyụ nisā`ahum, wa mā kaidul-kāfirīna illā fī ḍalāl","at":"Maka ketika dia (Musa) datang kepada mereka membawa kebenaran dari Kami, mereka berkata, “Bunuhlah anak-anak laki-laki dari orang-orang yang beriman bersama dia dan biarkan hidup perempuan-perempuan mereka.” Namun tipu daya orang-orang kafir itu sia-sia belaka."},{"id":"26","ar":"وَقَالَ فِرْعَوْنُ ذَرُوْنِيْٓ اَقْتُلْ مُوْسٰى وَلْيَدْعُ رَبَّهٗ ۚاِنِّيْٓ اَخَافُ اَنْ يُّبَدِّلَ دِيْنَكُمْ اَوْ اَنْ يُّظْهِرَ فِى الْاَرْضِ الْفَسَادَ","bc":"wa qāla fir'aunu żarụnī aqtul mụsā walyad'u rabbah, innī akhāfu ay yubaddila dīnakum au ay yuẓ-hira fil-arḍil-fasād","at":"Dan Fir‘aun berkata (kepada pembesar-pembesarnya), “Biar aku yang membunuh Musa dan suruh dia memohon kepada Tuhannya. Sesungguhnya aku khawatir dia akan menukar agamamu atau menimbulkan kerusakan di bumi.”"},{"id":"27","ar":"وَقَالَ مُوْسٰىٓ اِنِّيْ عُذْتُ بِرَبِّيْ وَرَبِّكُمْ مِّنْ كُلِّ مُتَكَبِّرٍ لَّا يُؤْمِنُ بِيَوْمِ الْحِسَابِ ","bc":"wa qāla mụsā innī 'użtu birabbī wa rabbikum ming kulli mutakabbiril lā yu`minu biyaumil-ḥisāb","at":"Dan (Musa) berkata, “Sesungguhnya aku berlindung kepada Tuhanku dan Tuhanmu dari setiap orang yang menyombongkan diri yang tidak beriman kepada hari perhitungan.”"},{"id":"28","ar":"وَقَالَ رَجُلٌ مُّؤْمِنٌۖ مِّنْ اٰلِ فِرْعَوْنَ يَكْتُمُ اِيْمَانَهٗٓ اَتَقْتُلُوْنَ رَجُلًا اَنْ يَّقُوْلَ رَبِّيَ اللّٰهُ وَقَدْ جَاۤءَكُمْ بِالْبَيِّنٰتِ مِنْ رَّبِّكُمْ ۗوَاِنْ يَّكُ كَاذِبًا فَعَلَيْهِ كَذِبُهٗ ۚوَاِنْ يَّكُ صَادِقًا يُّصِبْكُمْ بَعْضُ الَّذِيْ يَعِدُكُمْ ۗاِنَّ اللّٰهَ لَا يَهْدِيْ مَنْ هُوَ مُسْرِفٌ كَذَّابٌ","bc":"wa qāla rajulum mu`minum min āli fir'auna yaktumu īmānahū a taqtulụna rajulan ay yaqụla rabbiyallāhu wa qad jā`akum bil-bayyināti mir rabbikum, wa iy yaku kāżiban fa 'alaihi każibuh, wa iy yaku ṣādiqay yuṣibkum ba'ḍullażī ya'idukum, innallāha lā yahdī man huwa musrifung każżāb","at":"Dan seseorang yang beriman di antara keluarga Fir‘aun yang menyembunyikan imannya berkata, “Apakah kamu akan membunuh seseorang karena dia berkata, “Tuhanku adalah Allah,” padahal sungguh, dia telah datang kepadamu dengan membawa bukti-bukti yang nyata dari Tuhanmu. Dan jika dia seorang pendusta maka dialah yang akan menanggung (dosa) dustanya itu; dan jika dia seorang yang benar, nis-caya sebagian (bencana) yang diancamkannya kepadamu akan menimpamu. Sesungguhnya Allah tidak memberi petunjuk kepada orang yang melampaui batas dan pendusta."},{"id":"29","ar":"يٰقَوْمِ لَكُمُ الْمُلْكُ الْيَوْمَ ظَاهِرِيْنَ فِى الْاَرْضِۖ فَمَنْ يَّنْصُرُنَا مِنْۢ بَأْسِ اللّٰهِ اِنْ جَاۤءَنَا ۗقَالَ فِرْعَوْنُ مَآ اُرِيْكُمْ اِلَّا مَآ اَرٰى وَمَآ اَهْدِيْكُمْ اِلَّا سَبِيْلَ الرَّشَادِ","bc":"yā qaumi lakumul-mulkul-yauma ẓāhirīna fil-arḍi fa may yanṣurunā mim ba`sillāhi in jā`anā, qāla fir'aunu mā urīkum illā mā arā wa mā ahdīkum illā sabīlar-rasyād","at":"Wahai kaumku! Pada hari ini kerajaan ada padamu dengan berkuasa di bumi, tetapi siapa yang akan menolong kita dari azab Allah jika (azab itu) menimpa kita?” Fir‘aun berkata, “Aku hanya mengemukakan kepadamu, apa yang aku pandang baik; dan aku hanya menunjukkan kepadamu jalan yang benar.”"},{"id":"30","ar":"وَقَالَ الَّذِيْٓ اٰمَنَ يٰقَوْمِ اِنِّيْٓ اَخَافُ عَلَيْكُمْ مِّثْلَ يَوْمِ الْاَحْزَابِۙ ","bc":"wa qālallażī āmana yā qaumi innī akhāfu 'alaikum miṡla yaumil-aḥzāb","at":"Dan orang yang beriman itu berkata, “Wahai kaumku! Sesungguhnya aku khawatir kamu akan ditimpa (bencana) seperti hari kehancuran golongan yang bersekutu,"},{"id":"31","ar":"مِثْلَ دَأْبِ قَوْمِ نُوْحٍ وَّعَادٍ وَّثَمُوْدَ وَالَّذِيْنَ مِنْۢ بَعْدِهِمْ ۗوَمَا اللّٰهُ يُرِيْدُ ظُلْمًا لِّلْعِبَادِ","bc":"miṡla da`bi qaumi nụḥiw wa 'ādiw wa ṡamụda wallażīna mim ba'dihim, wa mallāhu yurīdu ẓulmal lil-'ibād","at":"(yakni) seperti kebiasaan kaum Nuh, ’Ad, samud dan orang-orang yang datang setelah mereka. Padahal Allah tidak menghendaki kezaliman terhadap hamba-hamba-Nya.”"},{"id":"32","ar":"وَيٰقَوْمِ اِنِّيْٓ اَخَافُ عَلَيْكُمْ يَوْمَ التَّنَادِۙ ","bc":"wa yā qaumi innī akhāfu 'alaikum yaumat-tanād","at":"Dan wahai kaumku! Sesungguhnya aku benar-benar khawatir terhadapmu akan (siksaan) hari saling memanggil,"},{"id":"33","ar":"يَوْمَ تُوَلُّوْنَ مُدْبِرِيْنَۚ مَا لَكُمْ مِّنَ اللّٰهِ مِنْ عَاصِمٍۚ وَمَنْ يُّضْلِلِ اللّٰهُ فَمَا لَهٗ مِنْ هَادٍ","bc":"yauma tuwallụna mudbirīn, mā lakum minallāhi min 'āṣim, wa may yuḍlilillāhu fa mā lahụ min hād","at":"(yaitu) pada hari (ketika) kamu berpaling ke belakang (lari), tidak ada seorang pun yang mampu menyelamatkan kamu dari (azab) Allah. Dan barangsiapa dibiarkan sesat oleh Allah, niscaya tidak ada sesuatu pun yang mampu memberi petunjuk.”"},{"id":"34","ar":"وَلَقَدْ جَاۤءَكُمْ يُوْسُفُ مِنْ قَبْلُ بِالْبَيِّنٰتِ فَمَا زِلْتُمْ فِيْ شَكٍّ مِّمَّا جَاۤءَكُمْ بِهٖ ۗحَتّٰىٓ اِذَا هَلَكَ قُلْتُمْ لَنْ يَّبْعَثَ اللّٰهُ مِنْۢ بَعْدِهٖ رَسُوْلًا ۗ كَذٰلِكَ يُضِلُّ اللّٰهُ مَنْ هُوَ مُسْرِفٌ مُّرْتَابٌۙ","bc":"wa laqad jā`akum yụsufu ming qablu bil-bayyināti fa mā ziltum fī syakkim mimmā jā`akum bih, hattā iżā halaka qultum lay yab'aṡallāhu mim ba'dihī rasụlā, każālika yuḍillullāhu man huwa musrifum murtāb,","at":"Dan sungguh, sebelum itu Yusuf telah datang kepadamu dengan membawa bukti-bukti yang nyata, tetapi kamu senantiasa meragukan apa yang dibawanya, bahkan ketika dia wafat, kamu berkata, “Allah tidak akan mengirim seorang rasul pun setelahnya.” Demikianlah Allah membiarkan sesat orang yang melampaui batas dan ragu-ragu,"},{"id":"35","ar":" ۨالَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِ اللّٰهِ بِغَيْرِ سُلْطٰنٍ اَتٰىهُمْۗ كَبُرَ مَقْتًا عِنْدَ اللّٰهِ وَعِنْدَ الَّذِيْنَ اٰمَنُوْا ۗ كَذٰلِكَ يَطْبَعُ اللّٰهُ عَلٰى كُلِّ قَلْبِ مُتَكَبِّرٍ جَبَّارٍ","bc":"allażīna yujādilụna fī āyātillāhi bigairi sulṭānin atāhum, kabura maqtan 'indallāhi wa 'indallażīna āmanụ, każālika yaṭba'ullāhu 'alā kulli qalbi mutakabbirin jabbār","at":"(yaitu) orang-orang yang memperdebatkan ayat-ayat Allah tanpa alasan yang sampai kepada mereka. Sangat besar kemurkaan (bagi mereka) di sisi Allah dan orang-orang yang beriman. Demikianlah Allah mengunci hati setiap orang yang sombong dan berlaku sewenang-wenang."},{"id":"36","ar":"وَقَالَ فِرْعَوْنُ يٰهَامٰنُ ابْنِ لِيْ صَرْحًا لَّعَلِّيْٓ اَبْلُغُ الْاَسْبَابَۙ","bc":"wa qāla fir'aunu yā hāmānubni lī ṣar-ḥal la'allī ablugul-asbāb","at":"Dan Fir‘aun berkata, “Wahai Haman! Buatkanlah untukku sebuah bangunan yang tinggi agar aku sampai ke pintu-pintu,"},{"id":"37","ar":"اَسْبَابَ السَّمٰوٰتِ فَاَطَّلِعَ اِلٰٓى اِلٰهِ مُوْسٰى وَاِنِّيْ لَاَظُنُّهٗ كَاذِبًا ۗوَكَذٰلِكَ زُيِّنَ لِفِرْعَوْنَ سُوْۤءُ عَمَلِهٖ وَصُدَّ عَنِ السَّبِيْلِ ۗوَمَا كَيْدُ فِرْعَوْنَ اِلَّا فِيْ تَبَابٍ ","bc":"asbābas-samāwāti fa aṭṭali'a ilā ilāhi mụsā wa innī la`aẓunnuhụ kāżibā, wa każālika zuyyina lifir'auna sū`u 'amalihī wa ṣudda 'anis-sabīl, wa mā kaidu fir'auna illā fī tabāb","at":"(yaitu) pintu-pintu langit, agar aku dapat melihat Tuhannya Musa, tetapi aku tetap memandangnya seorang pendusta.” Dan demikianlah dijadikan terasa indah bagi Fir‘aun perbuatan buruknya itu, dan dia tertutup dari jalan (yang benar); dan tipu daya Fir‘aun itu tidak lain hanyalah membawa kerugian."},{"id":"38","ar":"وَقَالَ الَّذِيْٓ اٰمَنَ يٰقَوْمِ اتَّبِعُوْنِ اَهْدِكُمْ سَبِيْلَ الرَّشَادِۚ","bc":"wa qālallażī āmana yā qaumittabi'ụni ahdikum sabīlar-rasyād","at":"Dan orang yang beriman itu berkata, “Wahai kaumku! Ikutilah aku, aku akan menunjukkan kepadamu jalan yang benar."},{"id":"39","ar":"يٰقَوْمِ اِنَّمَا هٰذِهِ الْحَيٰوةُ الدُّنْيَا مَتَاعٌ ۖوَّاِنَّ الْاٰخِرَةَ هِيَ دَارُ الْقَرَارِ","bc":"yā qaumi innamā hāżihil-ḥayātud-dun-yā matā'uw wa innal-ākhirata hiya dārul-qarār","at":"Wahai kaumku! Sesungguhnya kehidupan dunia ini hanyalah kesenangan (sementara) dan sesungguhnya akhirat itulah negeri yang kekal."},{"id":"40","ar":"مَنْ عَمِلَ سَيِّئَةً فَلَا يُجْزٰىٓ اِلَّا مِثْلَهَاۚ وَمَنْ عَمِلَ صَالِحًا مِّنْ ذَكَرٍ اَوْ اُنْثٰى وَهُوَ مُؤْمِنٌ فَاُولٰۤىِٕكَ يَدْخُلُوْنَ الْجَنَّةَ يُرْزَقُوْنَ فِيْهَا بِغَيْرِ حِسَابٍ ","bc":"man 'amila sayyi`atan fa lā yujzā illā miṡlahā, wa man 'amila ṣāliḥam min żakarin au unṡā wa huwa mu`minun fa ulā`ika yadkhulụnal-jannata yurzaqụna fīhā bigairi ḥisāb","at":"Barangsiapa mengerjakan perbuatan jahat, maka dia akan dibalas sebanding dengan kejahatan itu. Dan barang siapa mengerjakan kebajikan, baik laki-laki maupun perempuan sedangkan dia dalam keadaan beriman, maka mereka akan masuk surga, mereka diberi rezeki di dalamnya tidak terhingga."},{"id":"41","ar":"۞ وَيٰقَوْمِ مَا لِيْٓ اَدْعُوْكُمْ اِلَى النَّجٰوةِ وَتَدْعُوْنَنِيْٓ اِلَى النَّارِۗ","bc":"wa yā qaumi mā lī ad'ụkum ilan-najāti wa tad'ụnanī ilan-nār","at":"Dan wahai kaumku! Bagaimanakah ini, aku menyerumu kepada keselamatan, tetapi kamu menyeruku ke neraka?"},{"id":"42","ar":"تَدْعُوْنَنِيْ لِاَكْفُرَ بِاللّٰهِ وَاُشْرِكَ بِهٖ مَا لَيْسَ لِيْ بِهٖ عِلْمٌ وَّاَنَا۠ اَدْعُوْكُمْ اِلَى الْعَزِيْزِ الْغَفَّارِ","bc":"tad'ụnanī li`akfura billāhi wa usyrika bihī mā laisa lī bihī 'ilmuw wa ana ad'ụkum ilal-'azīzil-gaffār","at":"(Mengapa) kamu menyeruku agar kafir kepada Allah dan mempersekutukan-Nya dengan sesuatu yang aku tidak mempunyai ilmu tentang itu, padahal aku menyerumu (beriman) kepada Yang Mahaperkasa, Maha Pengampun?"},{"id":"43","ar":"لَا جَرَمَ اَنَّمَا تَدْعُوْنَنِيْٓ اِلَيْهِ لَيْسَ لَهٗ دَعْوَةٌ فِى الدُّنْيَا وَلَا فِى الْاٰخِرَةِ وَاَنَّ مَرَدَّنَآ اِلَى اللّٰهِ وَاَنَّ الْمُسْرِفِيْنَ هُمْ اَصْحٰبُ النَّارِ","bc":"lā jarama annamā tad'ụnanī ilaihi laisa lahụ da'watun fid-dun-yā wa lā fil-ākhirati wa anna maraddanā ilallāhi wa annal-musrifīna hum aṣ-ḥābun-nār","at":"Sudah pasti bahwa apa yang kamu serukan aku kepadanya bukanlah suatu seruan yang berguna baik di dunia maupun di akhirat. Dan sesungguhnya tempat kembali kita pasti kepada Allah, dan sesungguhnya orang-orang yang melampaui batas, mereka itu akan menjadi penghuni neraka."},{"id":"44","ar":"فَسَتَذْكُرُوْنَ مَآ اَقُوْلُ لَكُمْۗ وَاُفَوِّضُ اَمْرِيْٓ اِلَى اللّٰهِ ۗاِنَّ اللّٰهَ بَصِيْرٌ ۢبِالْعِبَادِ","bc":"fasatażkurụna mā aqụlu lakum, wa ufawwiḍu amrī ilallāh, innallāha baṣīrum bil-'ibād","at":"Maka kelak kamu akan ingat kepada apa yang kukatakan kepadamu. Dan aku menyerahkan urusanku kepada Allah. Sungguh, Allah Maha Melihat akan hamba-hamba-Nya.”"},{"id":"45","ar":"فَوَقٰىهُ اللّٰهُ سَيِّاٰتِ مَا مَكَرُوْا وَحَاقَ بِاٰلِ فِرْعَوْنَ سُوْۤءُ الْعَذَابِۚ","bc":"fa waqāhullāhu sayyi`āti mā makarụ wa ḥāqa bi`āli fir'auna sū`ul-'ażāb","at":"Maka Allah memeliharanya dari kejahatan tipu daya mereka, sedangkan Fir‘aun beserta kaumnya dikepung oleh azab yang sangat buruk."},{"id":"46","ar":"اَلنَّارُ يُعْرَضُوْنَ عَلَيْهَا غُدُوًّا وَّعَشِيًّا ۚوَيَوْمَ تَقُوْمُ السَّاعَةُ ۗ اَدْخِلُوْٓا اٰلَ فِرْعَوْنَ اَشَدَّ الْعَذَابِ","bc":"an-nāru yu'raḍụna 'alaihā guduwwaw wa 'asyiyyā, wa yauma taqụmus-sā'ah, adkhilū āla fir'auna asyaddal-'ażāb","at":"Kepada mereka diperlihatkan neraka, pada pagi dan petang, dan pada hari terjadinya Kiamat. (Lalu kepada malaikat diperintahkan), “Masukkanlah Fir‘aun dan kaumnya ke dalam azab yang sangat keras!”"},{"id":"47","ar":"وَاِذْ يَتَحَاۤجُّوْنَ فِى النَّارِ فَيَقُوْلُ الضُّعَفٰۤؤُ لِلَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ اَنْتُمْ مُّغْنُوْنَ عَنَّا نَصِيْبًا مِّنَ النَّارِ","bc":"wa iż yataḥājjụna fin-nāri fa yaqụlud-du'afā`u lillażīnastakbarū innā kunnā lakum taba'an fa hal antum mugnụna 'annā naṣībam minan-nār","at":"Dan (Ingatlah), ketika mereka berbantah-bantahan dalam neraka, maka orang yang lemah berkata kepada orang-orang yang menyombongkan diri, “Sesungguhnya kami dahulu adalah pengikut-pengikutmu, maka dapatkah kamu melepaskan sebagian (azab) api neraka yang menimpa kami?”"},{"id":"48","ar":"قَالَ الَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا كُلٌّ فِيْهَآ اِنَّ اللّٰهَ قَدْ حَكَمَ بَيْنَ الْعِبَادِ","bc":"qālallażīnastakbarū innā kullun fīhā innallāha qad ḥakama bainal-'ibād","at":"Orang-orang yang menyombongkan diri menjawab, “Sesungguhnya kita semua sama-sama dalam neraka karena Allah telah menetapkan keputusan antara hamba-hamba-(Nya).”"},{"id":"49","ar":"وَقَالَ الَّذِيْنَ فِى النَّارِ لِخَزَنَةِ جَهَنَّمَ ادْعُوْا رَبَّكُمْ يُخَفِّفْ عَنَّا يَوْمًا مِّنَ الْعَذَابِ ","bc":"wa qālallażīna fin-nāri likhazanati jahannamad'ụ rabbakum yukhaffif 'annā yaumam minal-'ażāb","at":"Dan orang-orang yang berada dalam neraka berkata kepada penjaga-penjaga neraka Jahanam, “Mohonkanlah kepada Tuhanmu agar Dia meringankan azab atas kami sehari saja.”"},{"id":"50","ar":"قَالُوْٓا اَوَلَمْ تَكُ تَأْتِيْكُمْ رُسُلُكُمْ بِالْبَيِّنٰتِ ۗقَالُوْا بَلٰىۗ قَالُوْا فَادْعُوْا ۚوَمَا دُعٰۤؤُا الْكٰفِرِيْنَ اِلَّا فِيْ ضَلٰلٍ","bc":"qālū a wa lam taku ta`tīkum rusulukum bil-bayyināt, qālụ balā, qālụ fad'ụ, wa mā du'ā`ul-kāfirīna illā fī ḍalāl","at":"Maka (penjaga-penjaga Jahanam) berkata, “Apakah rasul-rasul belum datang kepadamu dengan membawa bukti-bukti yang nyata?” Mereka menjawab, “Benar, sudah datang.” (Penjaga-penjaga Jahanam) berkata, “Berdoalah kamu (sendiri!)” Namun doa orang-orang kafir itu sia-sia belaka."},{"id":"51","ar":"اِنَّا لَنَنْصُرُ رُسُلَنَا وَالَّذِيْنَ اٰمَنُوْا فِى الْحَيٰوةِ الدُّنْيَا وَيَوْمَ يَقُوْمُ الْاَشْهَادُۙ ","bc":"innā lananṣuru rusulanā wallażīna āmanụ fil-ḥayātid-dun-yā wa yauma yaqụmul-asy-hād","at":"Sesungguhnya Kami akan menolong rasul-rasul Kami dan orang-orang yang beriman dalam kehidupan dunia dan pada hari tampilnya para saksi (hari Kiamat),"},{"id":"52","ar":"يَوْمَ لَا يَنْفَعُ الظّٰلِمِيْنَ مَعْذِرَتُهُمْ وَلَهُمُ اللَّعْنَةُ وَلَهُمْ سُوْۤءُ الدَّارِ ","bc":"yauma lā yanfa'uẓ-ẓālimīna ma'żiratuhum wa lahumul-la'natu wa lahum sū`ud-dār","at":"(yaitu) hari ketika permintaan maaf tidak berguna bagi orang-orang zalim dan mereka mendapat laknat dan tempat tinggal yang buruk."},{"id":"53","ar":"وَلَقَدْاٰتَيْنَا مُوْسٰى الْهُدٰى وَاَوْرَثْنَا بَنِيْٓ اِسْرَاۤءِيْلَ الْكِتٰبَۙ ","bc":"wa laqad ātainā mụsal-hudā wa auraṡnā banī isrā`īlal-kitāb","at":"Dan sungguh, Kami telah memberikan petunjuk kepada Musa; dan mewariskan Kitab (Taurat) kepada Bani Israil,"},{"id":"54","ar":"هُدًى وَّذِكْرٰى لِاُولِى الْاَلْبَابِ","bc":"hudaw wa żikrā li`ulil-albāb","at":"untuk menjadi petunjuk dan peringatan bagi orang-orang yang berpikiran sehat."},{"id":"55","ar":"فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّاسْتَغْفِرْ لِذَنْۢبِكَ وَسَبِّحْ بِحَمْدِ رَبِّكَ بِالْعَشِيِّ وَالْاِبْكَارِ","bc":"faṣbir inna wa'dallāhi ḥaqquw wastagfir liżambika wa sabbiḥ biḥamdi rabbika bil-'asyiyyi wal-ibkār","at":"Maka bersabarlah kamu, sesungguhnya janji Allah itu benar, dan mohonlah ampun untuk dosamu dan bertasbihlah seraya memuji Tuhanmu pada waktu petang dan pagi."},{"id":"56","ar":"اِنَّ الَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِ اللّٰهِ بِغَيْرِ سُلْطٰنٍ اَتٰىهُمْ ۙاِنْ فِيْ صُدُوْرِهِمْ اِلَّا كِبْرٌ مَّا هُمْ بِبَالِغِيْهِۚ فَاسْتَعِذْ بِاللّٰهِ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْبَصِيْرُ","bc":"innallażīna yujādilụna fī āyātillāhi bigairi sulṭānin atāhum in fī ṣudụrihim illā kibrum mā hum bibāligīh, fasta'iż billāh, innahụ huwas-samī'ul-baṣīr","at":"Sesungguhnya orang-orang yang memperdebatkan ayat-ayat Allah tanpa alasan (bukti) yang sampai kepada mereka, yang ada dalam dada mereka hanyalah (keinginan akan) kebesaran yang tidak akan mereka capai, maka mintalah perlindungan kepada Allah. Sungguh, Dia Maha Mendengar, Maha Melihat."},{"id":"57","ar":"لَخَلْقُ السَّمٰوٰتِ وَالْاَرْضِ اَكْبَرُ مِنْ خَلْقِ النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ","bc":"lakhalqus-samāwāti wal-arḍi akbaru min khalqin-nāsi wa lākinna akṡaran-nāsi lā ya'lamụn","at":"Sungguh, penciptaan langit dan bumi itu lebih besar daripada penciptaan manusia, akan tetapi kebanyakan manusia tidak mengetahui."},{"id":"58","ar":"وَمَا يَسْتَوِى الْاَعْمٰى وَالْبَصِيْرُ ەۙ وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَلَا الْمُسِيْۤئُ ۗقَلِيْلًا مَّا تَتَذَكَّرُوْنَ","bc":"wa mā yastawil-a'mā wal-baṣīru wallażīna āmanụ wa 'amiluṣ-ṣāliḥāti wa lal-musī`, qalīlam mā tatażakkarụn","at":"Dan tidak sama orang yang buta dengan orang yang melihat, dan tidak (sama) pula orang-orang yang beriman dan mengerjakan kebajikan dengan orang-orang yang berbuat kejahatan. Hanya sedikit sekali yang kamu ambil pelajaran."},{"id":"59","ar":"اِنَّ السَّاعَةَ لَاٰتِيَةٌ لَّا رَيْبَ فِيْهَا ۖوَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُوْنَ","bc":"innas-sā'ata la`ātiyatul lā raiba fīhā wa lākinna akṡaran-nāsi lā yu`minụn","at":"Sesungguhnya hari Kiamat pasti akan datang, tidak ada keraguan tentangnya, akan tetapi kebanyakan manusia tidak beriman."},{"id":"60","ar":"وَقَالَ رَبُّكُمُ ادْعُوْنِيْٓ اَسْتَجِبْ لَكُمْ ۗاِنَّ الَّذِيْنَ يَسْتَكْبِرُوْنَ عَنْ عِبَادَتِيْ سَيَدْخُلُوْنَ جَهَنَّمَ دَاخِرِيْنَࣖ","bc":"wa qāla rabbukumud'ụnī astajib lakum, innallażīna yastakbirụna 'an 'ibādatī sayadkhulụna jahannama dākhirīn","at":"Dan Tuhanmu berfirman, “Berdoalah kepada-Ku, niscaya akan Aku perkenankan bagimu. Sesungguhnya orang-orang yang sombong tidak mau menyembah-Ku akan masuk neraka Jahanam dalam keadaan hina dina.”"},{"id":"61","ar":"اَللّٰهُ الَّذِيْ جَعَلَ لَكُمُ الَّيْلَ لِتَسْكُنُوْا فِيْهِ وَالنَّهَارَ مُبْصِرًا ۗاِنَّ اللّٰهَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُوْنَ","bc":"allāhullażī ja'ala lakumul-laila litaskunụ fīhi wan-nahāra mubṣirā, innallāha lażụ faḍlin 'alan-nāsi wa lākinna akṡaran-nāsi lā yasykurụn","at":"Allah-lah yang menjadikan malam untukmu agar kamu beristirahat padanya; (dan menjadikan) siang terang benderang. Sungguh, Allah benar-benar memiliki karunia yang dilimpahkan kepada manusia, tetapi kebanyakan manusia tidak bersyukur."},{"id":"62","ar":"ذٰلِكُمُ اللّٰهُ رَبُّكُمْ خَالِقُ كُلِّ شَيْءٍۘ لَآ اِلٰهَ اِلَّا هُوَ ۖفَاَنّٰى تُؤْفَكُوْنَ","bc":"żālikumullāhu rabbukum khāliqu kulli syaī`, lā ilāha illā huwa fa annā tu`fakụn","at":"Demikianlah Allah, Tuhanmu, Pencipta segala sesuatu, tidak ada tuhan selain Dia; maka bagaimanakah kamu dapat dipalingkan?"},{"id":"63","ar":"كَذٰلِكَ يُؤْفَكُ الَّذِيْنَ كَانُوْا بِاٰيٰتِ اللّٰهِ يَجْحَدُوْنَ","bc":"każālika yu`fakullażīna kānụ bi`āyātillāhi yaj-ḥadụn","at":"Demikianlah orang-orang yang selalu mengingkari ayat-ayat Allah dipalingkan."},{"id":"64","ar":"اَللّٰهُ الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ قَرَارًا وَّالسَّمَاۤءَ بِنَاۤءً وَّصَوَّرَكُمْ فَاَحْسَنَ صُوَرَكُمْ وَرَزَقَكُمْ مِّنَ الطَّيِّبٰتِ ۗذٰلِكُمُ اللّٰهُ رَبُّكُمْ ۚ فَتَبٰرَكَ اللّٰهُ رَبُّ الْعٰلَمِيْنَ","bc":"allāhullażī ja'ala lakumul-arḍa qarāraw was-samā`a binā`aw wa ṣawwarakum fa aḥsana ṣuwarakum wa razaqakum minaṭ-ṭayyibāt, żālikumullāhu rabbukum, fa tabārakallāhu rabbul-'ālamīn","at":"Allah-lah yang menjadikan bumi untukmu sebagai tempat menetap dan langit sebagai atap, dan membentukmu lalu memperindah rupamu serta memberimu rezeki dari yang baik-baik. Demikianlah Allah, Tuhanmu, Mahasuci Allah, Tuhan seluruh alam."},{"id":"65","ar":"هُوَ الْحَيُّ لَآ اِلٰهَ اِلَّا هُوَ فَادْعُوْهُ مُخْلِصِيْنَ لَهُ الدِّيْنَ ۗ اَلْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ","bc":"huwal-ḥayyu lā ilāha illā huwa fad'ụhu mukhliṣīna lahud-dīn, al-ḥamdu lillāhi rabbil-'ālamīn","at":"Dialah yang hidup kekal, tidak ada tuhan selain Dia; maka sembahlah Dia dengan tulus ikhlas beragama kepada-Nya. Segala puji bagi Allah Tuhan seluruh alam."},{"id":"66","ar":"۞ قُلْ اِنِّيْ نُهِيْتُ اَنْ اَعْبُدَ الَّذِيْنَ تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ لَمَّا جَاۤءَنِيَ الْبَيِّنٰتُ مِنْ رَّبِّيْ وَاُمِرْتُ اَنْ اُسْلِمَ لِرَبِّ الْعٰلَمِيْنَ","bc":"qul innī nuhītu an a'budallażīna tad'ụna min dụnillāhi lammā jā`aniyal-bayyinātu mir rabbī wa umirtu an uslima lirabbil-'ālamīn","at":"Katakanlah (Muhammad), “Sungguh, aku dilarang menyembah sembahan yang kamu sembah selain Allah, setelah datang kepadaku keterangan-keterangan dari Tuhanku; dan aku diperintahkan agar berserah diri kepada Tuhan seluruh alam.”"},{"id":"67","ar":"هُوَ الَّذِيْ خَلَقَكُمْ مِّنْ تُرَابٍ ثُمَّ مِنْ نُّطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ يُخْرِجُكُمْ طِفْلًا ثُمَّ لِتَبْلُغُوْٓا اَشُدَّكُمْ ثُمَّ لِتَكُوْنُوْا شُيُوْخًا ۚوَمِنْكُمْ مَّنْ يُّتَوَفّٰى مِنْ قَبْلُ وَلِتَبْلُغُوْٓا اَجَلًا مُّسَمًّى وَّلَعَلَّكُمْ تَعْقِلُوْنَ","bc":"huwallażī khalaqakum min turābin ṡumma min nuṭfatin ṡumma min 'alaqatin ṡumma yukhrijukum ṭiflan ṡumma litablugū asyuddakum ṡumma litakụnụ syuyụkhā, wa mingkum may yutawaffā ming qablu wa litablugū ajalam musammaw wa la'allakum ta'qilụn","at":"Dialah yang menciptakanmu dari tanah, kemudian dari setetes mani, lalu dari segumpal darah, kemudian kamu dilahirkan sebagai seorang anak, kemudian dibiarkan kamu sampai dewasa, lalu menjadi tua. Tetapi di antara kamu ada yang dimatikan sebelum itu. (Kami perbuat demikian) agar kamu sampai kepada kurun waktu yang ditentukan, agar kamu mengerti."},{"id":"68","ar":"هُوَ الَّذِيْ يُحْيٖ وَيُمِيْتُۚ فَاِذَا قَضٰىٓ اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ ","bc":"huwallażī yuḥyī wa yumīt, fa iżā qaḍā amran fa innamā yaqụlu lahụ kun fa yakụn","at":"Dialah yang menghidupkan dan mematikan. Maka apabila Dia hendak menetapkan sesuatu urusan, Dia hanya berkata kepadanya, “Jadilah!” Maka jadilah sesuatu itu."},{"id":"69","ar":"اَلَمْ تَرَ اِلَى الَّذِيْنَ يُجَادِلُوْنَ فِيْٓ اٰيٰتِ اللّٰهِ ۗاَنّٰى يُصْرَفُوْنَۚ","bc":"a lam tara ilallażīna yujādilụna fī āyātillāh, annā yuṣrafụn","at":"Apakah kamu tidak memperhatikan orang-orang yang (selalu) membantah ayat-ayat Allah? Bagaimana mereka dapat dipalingkan?"},{"id":"70","ar":"اَلَّذِيْنَ كَذَّبُوْا بِالْكِتٰبِ وَبِمَآ اَرْسَلْنَا بِهٖ رُسُلَنَا ۗفَسَوْفَ يَعْلَمُوْنَۙ","bc":"allażīna każżabụ bil-kitābi wa bimā arsalnā bihī rusulanā, fa saufa ya'lamụn","at":"(Yaitu) orang-orang yang mendustakan Kitab (Al-Qur'an) dan wahyu yang dibawa oleh rasul-rasul Kami yang telah Kami utus. Kelak mereka akan mengetahui,"},{"id":"71","ar":"اِذِ الْاَغْلٰلُ فِيْٓ اَعْنَاقِهِمْ وَالسَّلٰسِلُۗ يُسْحَبُوْنَۙ","bc":"iżil-aglālu fī a'nāqihim was-salāsil, yus-ḥabụn","at":"ketika belenggu dan rantai dipasang di leher mereka, seraya mereka diseret,"},{"id":"72","ar":"فِى الْحَمِيْمِ ەۙ ثُمَّ فِى النَّارِ يُسْجَرُوْنَۚ","bc":"fil-ḥamīmi ṡumma fin-nāri yusjarụn","at":"ke dalam air yang sangat panas, kemudian mereka dibakar dalam api,"},{"id":"73","ar":"ثُمَّ قِيْلَ لَهُمْ اَيْنَ مَا كُنْتُمْ تُشْرِكُوْنَۙ","bc":"ṡumma qīla lahum aina mā kuntum tusyrikụn","at":"kemudian dikatakan kepada mereka, “Manakah berhala-berhala yang selalu kamu persekutukan,"},{"id":"74","ar":"مِنْ دُوْنِ اللّٰهِ ۗقَالُوْا ضَلُّوْا عَنَّا بَلْ لَّمْ نَكُنْ نَّدْعُوْا مِنْ قَبْلُ شَيْـًٔاۚ كَذٰلِكَ يُضِلُّ اللّٰهُ الْكٰفِرِيْنَ","bc":"min dụnillāh, qālụ ḍallụ 'annā bal lam nakun nad'ụ ming qablu syai`ā, każālika yuḍillullāhul-kāfirīn","at":"(yang kamu sembah) selain Allah?” Mereka menjawab, “Mereka telah hilang lenyap dari kami, bahkan kami dahulu tidak pernah menyembah sesuatu.” Demikianlah Allah membiarkan sesat orang-orang kafir."},{"id":"75","ar":"ذٰلِكُمْ بِمَا كُنْتُمْ تَفْرَحُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ وَبِمَا كُنْتُمْ تَمْرَحُوْنَ","bc":"żālikum bimā kuntum tafraḥụna fil-arḍi bigairil-ḥaqqi wa bimā kuntum tamraḥụn","at":"Yang demikian itu disebabkan karena kamu bersuka ria di bumi (tanpa) mengindahkan kebenaran dan karena kamu selalu bersuka ria (dalam kemaksiatan)."},{"id":"76","ar":"اُدْخُلُوْٓا اَبْوَابَ جَهَنَّمَ خٰلِدِيْنَ فِيْهَا ۚفَبِئْسَ مَثْوَى الْمُتَكَبِّرِيْنَ","bc":"udkhulū abwāba jahannama khālidīna fīhā, fa bi`sa maṡwal-mutakabbirīn","at":"(Dikatakan kepada mereka), “Masuklah kamu ke pintu-pintu neraka Jahanam, dan kamu kekal di dalamnya. Maka itulah seburuk-buruk tempat bagi orang-orang yang sombong.”"},{"id":"77","ar":"فَاصْبِرْ اِنَّ وَعْدَ اللّٰهِ حَقٌّ ۚفَاِمَّا نُرِيَنَّكَ بَعْضَ الَّذِيْ نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِلَيْنَا يُرْجَعُوْنَ","bc":"faṣbir inna wa'dallāhi ḥaqq, fa immā nuriyannaka ba'ḍallażī na'iduhum au natawaffayannaka fa ilainā yurja'ụn","at":"Maka bersabarlah engkau (Muhammad), sesungguhnya janji Allah itu benar. Meskipun Kami perlihatkan kepadamu sebagian siksa yang Kami ancamkan kepada mereka, atau pun Kami wafatkan engkau (sebelum ajal menimpa mereka), namun kepada Kamilah mereka dikembalikan."},{"id":"78","ar":"وَلَقَدْ اَرْسَلْنَا رُسُلًا مِّنْ قَبْلِكَ مِنْهُمْ مَّنْ قَصَصْنَا عَلَيْكَ وَمِنْهُمْ مَّنْ لَّمْ نَقْصُصْ عَلَيْكَ ۗوَمَا كَانَ لِرَسُوْلٍ اَنْ يَّأْتِيَ بِاٰيَةٍ اِلَّا بِاِذْنِ اللّٰهِ ۚفَاِذَا جَاۤءَ اَمْرُ اللّٰهِ قُضِيَ بِالْحَقِّ وَخَسِرَ هُنَالِكَ الْمُبْطِلُوْنَ ","bc":"wa laqad arsalnā rusulam ming qablika min-hum mang qaṣaṣnā 'alaika wa min-hum mal lam naqṣuṣ 'alaīk, wa mā kāna lirasụlin ay ya`tiya bi`āyatin illā bi`iżnillāh, fa iżā jā`a amrullāhi quḍiya bil-ḥaqqi wa khasira hunālikal-mubṭilụn","at":"Dan sungguh, Kami telah mengutus beberapa rasul sebelum engkau (Muhammad), di antara mereka ada yang Kami ceritakan kepadamu dan di antaranya ada (pula) yang tidak Kami ceritakan kepadamu. Tidak ada seorang rasul membawa suatu mukjizat, kecuali seizin Allah. Maka apabila telah datang perintah Allah, (untuk semua perkara) diputuskan dengan adil. Dan ketika itu rugilah orang-orang yang berpegang kepada yang batil."},{"id":"79","ar":"اَللّٰهُ الَّذِيْ جَعَلَ لَكُمُ الْاَنْعَامَ لِتَرْكَبُوْا مِنْهَا وَمِنْهَا تَأْكُلُوْنَۖ","bc":"allāhullażī ja'ala lakumul-an'āma litarkabụ min-hā wa min-hā ta`kulụn","at":"Allah-lah yang menjadikan hewan ternak untukmu, sebagian untuk kamu kendarai dan sebagian lagi kamu makan."},{"id":"80","ar":"وَلَكُمْ فِيْهَا مَنَافِعُ وَلِتَبْلُغُوْا عَلَيْهَا حَاجَةً فِيْ صُدُوْرِكُمْ وَعَلَيْهَا وَعَلَى الْفُلْكِ تُحْمَلُوْنَۗ","bc":"wa lakum fīhā manāfi'u wa litablugụ 'alaihā ḥājatan fī ṣudụrikum wa 'alaihā wa 'alal-fulki tuḥmalụn","at":"Dan bagi kamu (ada lagi) manfaat-manfaat yang lain padanya (hewan ternak itu) dan agar kamu mencapai suatu keperluan (tujuan) yang tersimpan dalam hatimu (dengan mengendarainya). Dan dengan mengendarai binatang-binatang itu, dan di atas kapal mereka diangkut."},{"id":"81","ar":"وَيُرِيْكُمْ اٰيٰتِهٖۖ فَاَيَّ اٰيٰتِ اللّٰهِ تُنْكِرُوْنَ","bc":"wa yurīkum āyātihī fa ayya āyātillāhi tungkirụn","at":"Dan Dia memperlihatkan tanda-tanda (kebesaran-Nya) kepadamu. Lalu tanda-tanda (kebesaran) Allah yang mana yang kamu ingkari?"},{"id":"82","ar":"اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْ ۗ كَانُوْٓا اَكْثَرَ مِنْهُمْ وَاَشَدَّ قُوَّةً وَّاٰثَارًا فِى الْاَرْضِ فَمَآ اَغْنٰى عَنْهُمْ مَّا كَانُوْا يَكْسِبُوْنَ","bc":"a fa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna 'āqibatullażīna ming qablihim, kānū akṡara min-hum wa asyadda quwwataw wa āṡāran fil-arḍi fa mā agnā 'an-hum mā kānụ yaksibụn","at":"Maka apakah mereka tidak mengadakan perjalanan di bumi, lalu mereka memperhatikan bagaimana kesudahan orang-orang yang sebelum mereka. Mereka itu lebih banyak dan lebih hebat kekuatannya serta (lebih banyak) peninggalan-peninggalan peradabannya di bumi, maka apa yang mereka usahakan itu tidak dapat menolong mereka."},{"id":"83","ar":"فَلَمَّا جَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَرِحُوْا بِمَا عِنْدَهُمْ مِّنَ الْعِلْمِ وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ","bc":"fa lammā jā`at-hum rusuluhum bil-bayyināti fariḥụ bimā 'indahum minal-'ilmi wa ḥāqa bihim mā kānụ bihī yastahzi`ụn","at":"Maka ketika para rasul datang kepada mereka dengan membawa bukti-bukti yang nyata, mereka merasa senang dengan ilmu yang ada pada mereka dan mereka dikepung oleh (azab) yang dahulu mereka memperolok-olokkannya."},{"id":"84","ar":"فَلَمَّا رَاَوْا بَأْسَنَاۗ قَالُوْٓا اٰمَنَّا بِاللّٰهِ وَحْدَهٗ وَكَفَرْنَا بِمَا كُنَّا بِهٖ مُشْرِكِيْنَ","bc":"fa lammā ra`au ba`sanā, qālū āmannā billāhi waḥdahụ wa kafarnā bimā kunnā bihī musyrikīn","at":"Maka ketika mereka melihat azab Kami, mereka berkata, “Kami hanya beriman kepada Allah saja dan kami ingkar kepada sembahan-sembahan yang telah kami persekutukan dengan Allah.”"},{"id":"85","ar":"فَلَمْ يَكُ يَنْفَعُهُمْ اِيْمَانُهُمْ لَمَّا رَاَوْا بَأْسَنَا ۗسُنَّتَ اللّٰهِ الَّتِيْ قَدْ خَلَتْ فِيْ عِبَادِهِۚ وَخَسِرَ هُنَالِكَ الْكٰفِرُوْنَ ","bc":"fa lam yaku yanfa'uhum īmānuhum lammā ra`au ba`sanā, sunnatallāhillatī qad khalat fī 'ibādih, wa khasira hunālikal-kāfirụn","at":"Maka iman mereka ketika mereka telah melihat azab Kami tidak berguna lagi bagi mereka. Itulah (ketentuan) Allah yang telah berlaku terhadap hamba-hamba-Nya. Dan ketika itu rugilah orang-orang kafir."}],hud=[{"id":"1","ar":"الۤرٰ ۗ كِتٰبٌ اُحْكِمَتْ اٰيٰتُهٗ ثُمَّ فُصِّلَتْ مِنْ لَّدُنْ حَكِيْمٍ خَبِيْرٍۙ","bc":"alif lām rā, kitābun uḥkimat āyātuhụ ṡumma fuṣṣilat mil ladun ḥakīmin khabīr","at":"Alif Lam Ra. (Inilah) Kitab yang ayat-ayatnya disusun dengan rapi kemudian dijelaskan secara terperinci, (yang diturunkan) dari sisi (Allah) Yang Mahabijaksana, Mahateliti,"},{"id":"2","ar":"اَلَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۗاِنَّنِيْ لَكُمْ مِّنْهُ نَذِيْرٌ وَّبَشِيْرٌۙ ","bc":"allā ta'budū illallāh, innanī lakum min-hu nażīruw wa basyīr","at":"agar kamu tidak menyembah selain Allah. Sesungguhnya aku (Muhammad) adalah pemberi peringatan dan pembawa berita gembira dari-Nya untukmu."},{"id":"3","ar":"وَّاَنِ اسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ يُمَتِّعْكُمْ مَّتَاعًا حَسَنًا اِلٰٓى اَجَلٍ مُّسَمًّى وَّيُؤْتِ كُلَّ ذِيْ فَضْلٍ فَضْلَهٗ ۗوَاِنْ تَوَلَّوْا فَاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ كَبِيْرٍ","bc":"wa anistagfirụ rabbakum ṡumma tụbū ilaihi yumatti'kum matā'an ḥasanan ilā ajalim musamman wa yu`ti kulla żī faḍlin faḍlah, wa in tawallau fa innī akhāfu 'alaikum 'ażāba yauming kabīr","at":"Dan hendaklah kamu memohon ampunan kepada Tuhanmu dan bertobat kepada-Nya, niscaya Dia akan memberi kenikmatan yang baik kepadamu sampai waktu yang telah ditentukan. Dan Dia akan memberikan karunia-Nya kepada setiap orang yang berbuat baik. Dan jika kamu berpaling, maka sungguh, aku takut kamu akan ditimpa azab pada hari yang besar (Kiamat)."},{"id":"4","ar":"اِلَى اللّٰهِ مَرْجِعُكُمْ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ قَدِيْرٌ ","bc":"ilallāhi marji'ukum, wa huwa 'alā kulli syai`ing qadīr","at":"Kepada Allah-lah kamu kembali. Dia Mahakuasa atas segala sesuatu."},{"id":"5","ar":"اَلَآ اِنَّهُمْ يَثْنُوْنَ صُدُوْرَهُمْ لِيَسْتَخْفُوْا مِنْهُۗ اَلَا حِيْنَ يَسْتَغْشُوْنَ ثِيَابَهُمْ ۙيَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَۚ اِنَّهٗ عَلِيْمٌ ۢ بِذَاتِ الصُّدُوْرِ ۔","bc":"alā innahum yaṡnụna ṣudụrahum liyastakhfụ min-h, alā ḥīna yastagsyụna ṡiyābahum ya'lamu mā yusirrụna wa mā yu'linụn, innahụ 'alīmum biżātiṣ-ṣudụr","at":"Ingatlah, sesungguhnya mereka (orang-orang munafik) memalingkan dada untuk menyembunyikan diri dari dia (Muhammad). Ingatlah, ketika mereka menyelimuti dirinya dengan kain, Allah mengetahui apa yang mereka sembunyikan dan apa yang mereka nyatakan, sungguh, Allah Maha Mengetahui (segala) isi hati."},{"id":"6","ar":"۞ وَمَا مِنْ دَاۤبَّةٍ فِى الْاَرْضِ اِلَّا عَلَى اللّٰهِ رِزْقُهَا وَيَعْلَمُ مُسْتَقَرَّهَا وَمُسْتَوْدَعَهَا ۗ كُلٌّ فِيْ كِتٰبٍ مُّبِيْنٍ","bc":"wa mā min dābbatin fil-arḍi illā 'alallāhi rizquhā wa ya'lamu mustaqarrahā wa mustauda'ahā, kullun fī kitābim mubīn","at":"Dan tidak satupun makhluk bergerak (bernyawa) di bumi melainkan semuanya dijamin Allah rezekinya. Dia mengetahui tempat kediamannya dan tempat penyimpanannya. Semua (tertulis) dalam Kitab yang nyata (Lauh Mahfuzh)."},{"id":"7","ar":"وَهُوَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ وَّكَانَ عَرْشُهٗ عَلَى الْمَاۤءِ لِيَبْلُوَكُمْ اَيُّكُمْ اَحْسَنُ عَمَلًا ۗوَلَىِٕنْ قُلْتَ اِنَّكُمْ مَّبْعُوْثُوْنَ مِنْۢ بَعْدِ الْمَوْتِ لَيَقُوْلَنَّ الَّذِيْنَ كَفَرُوْٓا اِنْ هٰذَٓا اِلَّا سِحْرٌ مُّبِيْنٌ","bc":"wa huwallażī khalaqas-samāwāti wal-arḍa fī sittati ayyāmiw wa kāna 'arsyuhụ 'alal-mā`i liyabluwakum ayyukum aḥsanu 'amalā, wa la`ing qulta innakum mab'ụṡụna mim ba'dil-mauti layaqụlannallażīna kafarū in hāżā illā siḥrum mubīn","at":"Dan Dialah yang menciptakan langit dan bumi dalam enam masa, dan ‘Arsy-Nya di atas air, agar Dia menguji siapakah di antara kamu yang lebih baik amalnya. Jika engkau berkata (kepada penduduk Mekah), “Sesungguhnya kamu akan dibangkitkan setelah mati,” niscaya orang kafir itu akan berkata, “Ini hanyalah sihir yang nyata.”"},{"id":"8","ar":"وَلَىِٕنْ اَخَّرْنَا عَنْهُمُ الْعَذَابَ اِلٰٓى اُمَّةٍ مَّعْدُوْدَةٍ لَّيَقُوْلُنَّ مَا يَحْبِسُهٗ ۗ اَلَا يَوْمَ يَأْتِيْهِمْ لَيْسَ مَصْرُوْفًا عَنْهُمْ وَحَاقَ بِهِمْ مَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ","bc":"wa la`in akhkharnā 'an-humul-'ażāba ilā ummatim ma'dụdatil layaqụlunna mā yaḥbisuh, alā yauma ya`tīhim laisa maṣrụfan 'an-hum wa ḥāqa bihim mā kānụ bihī yastahzi`ụn","at":"Dan sungguh, jika Kami tangguhkan azab terhadap mereka sampai waktu yang ditentukan, niscaya mereka akan berkata, “Apakah yang menghalanginya?” Ketahuilah, ketika azab itu datang kepada mereka, tidaklah dapat dielakkan oleh mereka. Mereka dikepung oleh (azab) yang dahulu mereka memperolok-olokkannya."},{"id":"9","ar":"وَلَىِٕنْ اَذَقْنَا الْاِنْسَانَ مِنَّا رَحْمَةً ثُمَّ نَزَعْنٰهَا مِنْهُۚ اِنَّهٗ لَيَـُٔوْسٌ كَفُوْرٌ","bc":"wa la`in ażaqnal-insāna minnā raḥmatan ṡumma naza'nāhā min-h, innahụ laya`ụsung kafụr","at":"Dan jika Kami berikan rahmat Kami kepada manusia, kemudian (rahmat itu) Kami cabut kembali, pastilah dia menjadi putus asa dan tidak berterima kasih."},{"id":"10","ar":"وَلَىِٕنْ اَذَقْنٰهُ نَعْمَاۤءَ بَعْدَ ضَرَّاۤءَ مَسَّتْهُ لَيَقُوْلَنَّ ذَهَبَ السَّيِّاٰتُ عَنِّيْ ۗاِنَّهٗ لَفَرِحٌ فَخُوْرٌۙ ","bc":"wa la`in ażaqnāhu na'mā`a ba'da ḍarrā`a massat-hu layaqụlanna żahabas-sayyi`ātu 'annī, innahụ lafariḥun fakhụr","at":"Dan jika Kami berikan kebahagiaan kepadanya setelah ditimpa bencana yang menimpanya, niscaya dia akan berkata, “Telah hilang bencana itu dariku.” Sesungguhnya dia (merasa) sangat gembira dan bangga,"},{"id":"11","ar":"اِلَّا الَّذِيْنَ صَبَرُوْا وَعَمِلُوا الصّٰلِحٰتِۗ اُولٰۤىِٕكَ لَهُمْ مَّغْفِرَةٌ وَّاَجْرٌ كَبِيْرٌ","bc":"illallażīna ṣabarụ wa 'amiluṣ-ṣāliḥāt, ulā`ika lahum magfiratuw wa ajrung kabīr","at":"kecuali orang-orang yang sabar, dan mengerjakan kebajikan, mereka memperoleh ampunan dan pahala yang besar."},{"id":"12","ar":"فَلَعَلَّكَ تَارِكٌۢ بَعْضَ مَا يُوْحٰىٓ اِلَيْكَ وَضَاۤىِٕقٌۢ بِهٖ صَدْرُكَ اَنْ يَّقُوْلُوْا لَوْلَآ اُنْزِلَ عَلَيْهِ كَنْزٌ اَوْ جَاۤءَ مَعَهٗ مَلَكٌ ۗاِنَّمَآ اَنْتَ نَذِيْرٌ ۗ وَاللّٰهُ عَلٰى كُلِّ شَيْءٍ وَّكِيْلٌ ۗ","bc":"fa la'allaka tārikum ba'ḍa mā yụḥā ilaika wa ḍā`iqum bihī ṣadruka ay yaqụlụ lau lā unzila 'alaihi kanzun au jā`a ma'ahụ malak, innamā anta nażīr, wallāhu 'alā kulli syai`iw wakīl","at":"Maka boleh jadi engkau (Muhammad) hendak meninggalkan sebagian dari apa yang diwahyukan kepadamu dan dadamu sempit karenanya, karena mereka akan mengatakan, “Mengapa tidak diturunkan kepadanya harta (kekayaan) atau datang bersamanya malaikat?” Sungguh, engkau hanyalah seorang pemberi peringatan dan Allah pemelihara segala sesuatu."},{"id":"13","ar":"اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۗقُلْ فَأْتُوْا بِعَشْرِ سُوَرٍ مِّثْلِهٖ مُفْتَرَيٰتٍ وَّادْعُوْا مَنِ اسْتَطَعْتُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"am yaqụlụnaftarāh, qul fa`tụ bi'asyri suwarim miṡlihī muftarayātiw wad'ụ manistaṭa'tum min dụnillāhi ing kuntum ṣādiqīn","at":"Bahkan mereka mengatakan, “Dia (Muhammad) telah membuat-buat Al-Qur'an itu.” Katakanlah, “(Kalau demikian), datangkanlah sepuluh surah semisal dengannya (Al-Qur'an) yang dibuat-buat, dan ajaklah siapa saja di antara kamu yang sanggup selain Allah, jika kamu orang-orang yang benar.”"},{"id":"14","ar":"فَاِلَّمْ يَسْتَجِيْبُوْا لَكُمْ فَاعْلَمُوْٓا اَنَّمَآ اُنْزِلَ بِعِلْمِ اللّٰهِ وَاَنْ لَّآ اِلٰهَ اِلَّا هُوَ ۚفَهَلْ اَنْتُمْ مُّسْلِمُوْنَ","bc":"fa il lam yastajībụ lakum fa'lamū annamā unzila bi'ilmillāhi wa al lā ilāha illā huw, fa hal antum muslimụn","at":"Maka jika mereka tidak memenuhi tantanganmu, maka (katakanlah), “Ketahuilah, bahwa (Al-Qur'an) itu diturunkan dengan ilmu Allah, dan bahwa tidak ada tuhan selain Dia, maka maukah kamu berserah diri (masuk Islam)?”"},{"id":"15","ar":"مَنْ كَانَ يُرِيْدُ الْحَيٰوةَ الدُّنْيَا وَزِيْنَتَهَا نُوَفِّ اِلَيْهِمْ اَعْمَالَهُمْ فِيْهَا وَهُمْ فِيْهَا لَا يُبْخَسُوْنَ","bc":"mang kāna yurīdul-ḥayātad-dun-yā wa zīnatahā nuwaffi ilaihim a'mālahum fīhā wa hum fīhā lā yubkhasụn","at":"Barangsiapa menghendaki kehidupan dunia dan perhiasannya, pasti Kami berikan (balasan) penuh atas pekerjaan mereka di dunia (dengan sempurna) dan mereka di dunia tidak akan dirugikan."},{"id":"16","ar":"اُولٰۤىِٕكَ الَّذِيْنَ لَيْسَ لَهُمْ فِى الْاٰخِرَةِ اِلَّا النَّارُ ۖوَحَبِطَ مَا صَنَعُوْا فِيْهَا وَبٰطِلٌ مَّا كَانُوْا يَعْمَلُوْنَ","bc":"ulā`ikallażīna laisa lahum fil-ākhirati illan-nāru wa ḥabiṭa mā ṣana'ụ fīhā wa bāṭilum mā kānụ ya'malụn","at":"Itulah orang-orang yang tidak memperoleh (sesuatu) di akhirat kecuali neraka, dan sia-sialah di sana apa yang telah mereka usahakan (di dunia) dan terhapuslah apa yang telah mereka kerjakan. "},{"id":"17","ar":"اَفَمَنْ كَانَ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّهٖ وَيَتْلُوْهُ شَاهِدٌ مِّنْهُ وَمِنْ قَبْلِهٖ كِتٰبُ مُوْسٰىٓ اِمَامًا وَّرَحْمَةًۗ اُولٰۤىِٕكَ يُؤْمِنُوْنَ بِهٖ ۗوَمَنْ يَّكْفُرْ بِهٖ مِنَ الْاَحْزَابِ فَالنَّارُ مَوْعِدُهٗ فَلَا تَكُ فِيْ مِرْيَةٍ مِّنْهُ اِنَّهُ الْحَقُّ مِنْ رَّبِّكَ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يُؤْمِنُوْنَ","bc":"a fa mang kāna 'alā bayyinatim mir rabbihī wa yatlụhu syāhidum min-hu wa ming qablihī kitābu mụsā imāmaw wa raḥmah, ulā`ika yu`minụna bih, wa may yakfur bihī minal-aḥzābi fan-nāru mau'iduhụ fa lā taku fī miryatim min-hu innahul-ḥaqqu mir rabbika wa lākinna akṡaran-nāsi lā yu`minụn","at":"Maka apakah (orang-orang kafir itu sama dengan) orang yang sudah mempunyai bukti yang nyata (Al-Qur'an) dari Tuhannya, dan diikuti oleh saksi dari-Nya dan sebelumnya sudah ada pula Kitab Musa yang menjadi pedoman dan rahmat? Mereka beriman kepadanya (Al-Qur'an). Barangsiapa mengingkarinya (Al-Qur'an) di antara kelompok-kelompok (orang Quraisy), maka nerakalah tempat yang diancamkan baginya, karena itu janganlah engkau ragu terhadap Al-Qur'an. Sungguh, Al-Qur'an itu benar-benar dari Tuhanmu, tetapi kebanyakan manusia tidak beriman."},{"id":"18","ar":"وَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًاۗ اُولٰۤىِٕكَ يُعْرَضُوْنَ عَلٰى رَبِّهِمْ وَيَقُوْلُ الْاَشْهَادُ هٰٓؤُلَاۤءِ الَّذِيْنَ كَذَبُوْا عَلٰى رَبِّهِمْۚ اَلَا لَعْنَةُ اللّٰهِ عَلَى الظّٰلِمِيْنَ ۙ ","bc":"wa man aẓlamu mim maniftarā 'alallāhi każibā, ulā`ika yu'raḍụna 'alā rabbihim wa yaqụlul-asy-hādu hā`ulā`illażīna każabụ 'alā rabbihim, alā la'natullāhi 'alaẓ-ẓālimīn","at":"Dan siapakah yang lebih zalim daripada orang yang mengada-adakan suatu kebohongan terhadap Allah? Mereka itu akan dihadapkan kepada Tuhan mereka, dan para saksi akan berkata, “Orang-orang inilah yang telah berbohong terhadap Tuhan mereka.” Ingatlah, laknat Allah (ditimpakan) kepada orang yang zalim,"},{"id":"19","ar":"الَّذِيْنَ يَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًاۗ وَهُمْ بِالْاٰخِرَةِ هُمْ كفِٰرُوْنَ","bc":"allażīna yaṣuddụna 'an sabīlillāhi wa yabgụnahā 'iwajā, wa hum bil-ākhirati hum kāfirụn","at":"(yaitu) mereka yang menghalangi dari jalan Allah dan menghendaki agar jalan itu bengkok. Dan mereka itulah orang yang tidak percaya adanya hari akhirat."},{"id":"20","ar":"اُولٰۤىِٕكَ لَمْ يَكُوْنُوْا مُعْجِزِيْنَ فِى الْاَرْضِ وَمَا كَانَ لَهُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ اَوْلِيَاۤءَ ۘ يُضٰعَفُ لَهُمُ الْعَذَابُ ۗمَا كَانُوْا يَسْتَطِيْعُوْنَ السَّمْعَ وَمَا كَانُوْا يُبْصِرُوْنَ","bc":"ulā`ika lam yakụnụ mu'jizīna fil-arḍi wa mā kāna lahum min dụnillāhi min auliyā`, yuḍā'afu lahumul-'ażāb, mā kānụ yastaṭī'ụnas-sam'a wa mā kānụ yubṣirụn","at":"Mereka tidak mampu menghalangi (siksaan Allah) di bumi, dan tidak akan ada bagi mereka penolong selain Allah. Azab itu dilipatgandakan kepada mereka. Mereka tidak mampu mendengar (kebenaran) dan tidak dapat melihat(nya)."},{"id":"21","ar":"اُولٰۤىِٕكَ الَّذِيْنَ خَسِرُوْٓا اَنْفُسَهُمْ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ","bc":"ulā`ikallażīna khasirū anfusahum wa ḍalla 'an-hum mā kānụ yaftarụn","at":"Mereka itulah orang yang merugikan dirinya sendiri, dan lenyaplah dari mereka apa yang selalu mereka ada-adakan."},{"id":"22","ar":"لَاجَرَمَ اَنَّهُمْ فِى الْاٰخِرَةِ هُمُ الْاَخْسَرُوْنَ","bc":"lā jarama annahum fil-ākhirati humul-akhsarụn","at":"Pasti mereka itu (menjadi) orang yang paling rugi di akhirat."},{"id":"23","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَاَخْبَتُوْٓا اِلٰى رَبِّهِمْۙ اُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِۚ هُمْ فِيْهَا خٰلِدُوْنَ","bc":"innallażīna āmanụ wa 'amiluṣ-ṣāliḥāti wa akhbatū ilā rabbihim ulā`ika aṣ-ḥābul-jannah, hum fīhā khālidụn","at":"Sesungguhnya orang-orang yang beriman dan mengerjakan kebajikan dan merendahkan diri kepada Tuhan, mereka itu penghuni surga, mereka kekal di dalamnya."},{"id":"24","ar":"۞ مَثَلُ الْفَرِيْقَيْنِ كَالْاَعْمٰى وَالْاَصَمِّ وَالْبَصِيْرِ وَالسَّمِيْعِۗ هَلْ يَسْتَوِيٰنِ مَثَلًا ۗ اَفَلَا تَذَكَّرُوْنَ ","bc":"maṡalul-farīqaini kal-a'mā wal-aṣammi wal-baṣīri was-samī', hal yastawiyāni maṡalā, a fa lā tażakkarụn","at":"Perumpamaan kedua golongan (orang kafir dan mukmin), seperti orang buta dan tuli dengan orang yang dapat melihat dan dapat mendengar. Samakah kedua golongan itu? Maka tidakkah kamu mengambil pelajaran?"},{"id":"25","ar":"وَلَقَدْ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖٓ اِنِّيْ لَكُمْ نَذِيْرٌ مُّبِيْنٌ ۙ","bc":"wa laqad arsalnā nụḥan ilā qaumihī innī lakum nażīrum mubīn","at":"Dan sungguh, Kami telah mengutus Nuh kepada kaumnya, (dia berkata), “Sungguh, aku ini adalah pemberi peringatan yang nyata bagi kamu,"},{"id":"26","ar":"اَنْ لَّا تَعْبُدُوْٓا اِلَّا اللّٰهَ ۖاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ اَلِيْمٍ","bc":"al lā ta'budū illallāh, innī akhāfu 'alaikum 'ażāba yaumin alīm","at":"agar kamu tidak menyembah selain Allah. Aku benar-benar khawatir kamu akan ditimpa azab (pada) hari yang sangat pedih.”"},{"id":"27","ar":"فَقَالَ الْمَلَاُ الَّذِيْنَ كَفَرُوْا مِنْ قَوْمِهٖ مَا نَرٰىكَ اِلَّا بَشَرًا مِّثْلَنَا وَمَا نَرٰىكَ اتَّبَعَكَ اِلَّا الَّذِيْنَ هُمْ اَرَاذِلُنَا بَادِيَ الرَّأْيِۚ وَمَا نَرٰى لَكُمْ عَلَيْنَا مِنْ فَضْلٍۢ بَلْ نَظُنُّكُمْ كٰذِبِيْنَ","bc":"fa qālal-mala`ullażīna kafarụ ming qaumihī mā narāka illā basyaram miṡlanā wa mā narākattaba'aka illallażīna hum arāżilunā bādiyar-ra`y, wa mā narā lakum 'alainā min faḍlim bal naẓunnukum kāżibīn","at":"Maka berkatalah para pemuka yang kafir dari kaumnya, “Kami tidak melihat engkau, melainkan hanyalah seorang manusia (biasa) seperti kami, dan kami tidak melihat orang yang mengikuti engkau, melainkan orang yang hina dina di antara kami yang lekas percaya. Kami tidak melihat kamu memiliki suatu kelebihan apa pun atas kami, bahkan kami menganggap kamu adalah orang pendusta.”"},{"id":"28","ar":"قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَاٰتٰىنِيْ رَحْمَةً مِّنْ عِنْدِهٖ فَعُمِّيَتْ عَلَيْكُمْۗ اَنُلْزِمُكُمُوْهَا وَاَنْتُمْ لَهَا كٰرِهُوْنَ","bc":"qāla yā qaumi a ra`aitum ing kuntu 'alā bayyinatim mir rabbī wa ātānī raḥmatam min 'indihī fa 'ummiyat 'alaikum, a nulzimukumụhā wa antum lahā kārihụn","at":"Dia (Nuh) berkata, ”Wahai kaumku! Apa pendapatmu jika aku mempunyai bukti yang nyata dari Tuhanku, dan aku diberi rahmat dari sisi-Nya, sedangkan (rahmat itu) disamarkan bagimu. Apa kami akan memaksa kamu untuk menerimanya, padahal kamu tidak menyukainya?"},{"id":"29","ar":"وَيٰقَوْمِ لَآ اَسْـَٔلُكُمْ عَلَيْهِ مَالًاۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ وَمَآ اَنَا۠ بِطَارِدِ الَّذِيْنَ اٰمَنُوْاۗ اِنَّهُمْ مُّلٰقُوْا رَبِّهِمْ وَلٰكِنِّيْٓ اَرٰىكُمْ قَوْمًا تَجْهَلُوْنَ","bc":"wa yā qaumi lā as`alukum 'alaihi mālā, in ajriya illā 'alallāhi wa mā ana biṭāridillażīna āmanụ, innahum mulāqụ rabbihim wa lākinnī arākum qauman taj-halụn","at":"Dan wahai kaumku! Aku tidak meminta harta kepada kamu (sebagai imbalan) atas seruanku. Imbalanku hanyalah dari Allah dan aku sekali-kali tidak akan mengusir orang yang telah beriman. Sungguh, mereka akan bertemu dengan Tuhannya, dan sebaliknya aku memandangmu sebagai kaum yang bodoh."},{"id":"30","ar":"وَيٰقَوْمِ مَنْ يَّنْصُرُنِيْ مِنَ اللّٰهِ اِنْ طَرَدْتُّهُمْ ۗ اَفَلَا تَذَكَّرُوْنَ","bc":"wa yā qaumi may yanṣurunī minallāhi in ṭarattuhum, a fa lā tażakkarụn","at":"Dan wahai kaumku! Siapakah yang akan menolongku dari (azab) Allah jika aku mengusir mereka. Tidakkah kamu mengambil pelajaran?"},{"id":"31","ar":"وَلَآ اَقُوْلُ لَكُمْ عِنْدِيْ خَزَاۤىِٕنُ اللّٰهِ وَلَآ اَعْلَمُ الْغَيْبَ وَلَآ اَقُوْلُ اِنِّيْ مَلَكٌ وَّلَآ اَقُوْلُ لِلَّذِيْنَ تَزْدَرِيْٓ اَعْيُنُكُمْ لَنْ يُّؤْتِيَهُمُ اللّٰهُ خَيْرًا ۗ اَللّٰهُ اَعْلَمُ بِمَا فِيْٓ اَنْفُسِهِمْ ۚاِنِّيْٓ اِذًا لَّمِنَ الظّٰلِمِيْنَ","bc":"wa lā aqụlu lakum 'indī khazā`inullāhi wa lā a'lamul-gaiba wa lā aqụlu innī malakuw wa lā aqụlu lillażīna tazdarī a'yunukum lay yu`tiyahumullāhu khairā, allāhu a'lamu bimā fī anfusihim, innī iżal laminaẓ-ẓālimīn","at":"Dan aku tidak mengatakan kepadamu, bahwa aku mempunyai gudang-gudang rezeki dan kekayaan dari Allah, dan aku tidak mengetahui yang gaib, dan tidak (pula) mengatakan bahwa sesungguhnya aku adalah malaikat, dan aku tidak (juga) mengatakan kepada orang yang dipandang hina oleh penglihatanmu, “Bahwa Allah tidak akan memberikan kebaikan kepada mereka. Allah lebih mengetahui apa yang ada pada diri mereka. Sungguh, jika demikian aku benar-benar termasuk orang-orang yang zalim.”"},{"id":"32","ar":"قَالُوْا يٰنُوْحُ قَدْ جَادَلْتَنَا فَاَ كْثَرْتَ جِدَالَنَا فَأْتِنَا بِمَا تَعِدُنَآ اِنْ كُنْتَ مِنَ الصّٰدِقِيْنَ","bc":"qālụ yā nụḥu qad jādaltana fa akṡarta jidālana fa`tinā bimā ta'idunā ing kunta minaṣ-ṣādiqīn","at":"Mereka berkata, “Wahai Nuh! Sungguh, engkau telah berbantah dengan kami, dan engkau telah memperpanjang bantahanmu terhadap kami, maka datangkanlah kepada kami azab yang engkau ancamkan, jika kamu termasuk orang yang benar.”"},{"id":"33","ar":"قَالَ اِنَّمَا يَأْتِيْكُمْ بِهِ اللّٰهُ اِنْ شَاۤءَ وَمَآ اَنْتُمْ بِمُعْجِزِيْنَ","bc":"qāla innamā ya`tīkum bihillāhu in syā`a wa mā antum bimu'jizīn","at":"Dia (Nuh) menjawab, “Hanya Allah yang akan mendatangkan azab kepadamu jika Dia menghendaki, dan kamu tidak akan dapat melepaskan diri."},{"id":"34","ar":"وَلَا يَنْفَعُكُمْ نُصْحِيْٓ اِنْ اَرَدْتُّ اَنْ اَنْصَحَ لَكُمْ اِنْ كَانَ اللّٰهُ يُرِيْدُ اَنْ يُّغْوِيَكُمْ ۗهُوَ رَبُّكُمْ ۗوَاِلَيْهِ تُرْجَعُوْنَۗ ","bc":"wa lā yanfa'ukum nuṣ-ḥī in arattu an anṣaḥa lakum ing kānallāhu yurīdu ay yugwiyakum, huwa rabbukum, wa ilaihi turja'ụn","at":"Dan nasihatku tidak akan bermanfaat bagimu sekalipun aku ingin memberi nasihat kepadamu, kalau Allah hendak menyesatkan kamu. Dia adalah Tuhanmu, dan kepada-Nyalah kamu dikembalikan.”"},{"id":"35","ar":"اَمْ يَقُوْلُوْنَ افْتَرٰىهُۗ قُلْ اِنِ افْتَرَيْتُهٗ فَعَلَيَّ اِجْرَامِيْ وَاَنَا۠ بَرِيْۤءٌ مِّمَّا تُجْرِمُوْنَ ","bc":"am yaqụlụnaftarāh, qul iniftaraituhụ fa 'alayya ijrāmī wa ana barī`um mimmā tujrimụn","at":"Bahkan mereka (orang kafir) berkata, “Dia cuma mengada-ada saja.” Katakanlah (Muhammad), “Jika aku mengada-ada, akulah yang akan memikul dosanya, dan aku bebas dari dosa yang kamu perbuat.”"},{"id":"36","ar":"وَاُوْحِيَ اِلٰى نُوْحٍ اَنَّهٗ لَنْ يُّؤْمِنَ مِنْ قَوْمِكَ اِلَّا مَنْ قَدْ اٰمَنَ فَلَا تَبْتَىِٕسْ بِمَا كَانُوْا يَفْعَلُوْنَۖ ","bc":"wa ụḥiya ilā nụḥin annahụ lay yu`mina ming qaumika illā mang qad āmana fa lā tabta`is bimā kānụ yaf'alụn","at":"Dan diwahyukan kepada Nuh, “Ketahuilah tidak akan beriman di an-tara kaummu, kecuali orang yang benar-benar beriman (saja), karena itu janganlah engkau bersedih hati tentang apa yang mereka perbuat."},{"id":"37","ar":"وَاصْنَعِ الْفُلْكَ بِاَعْيُنِنَا وَوَحْيِنَا وَلَا تُخَاطِبْنِيْ فِى الَّذِيْنَ ظَلَمُوْا ۚاِنَّهُمْ مُّغْرَقُوْنَ","bc":"waṣna'il-fulka bi`a'yuninā wa waḥyinā wa lā tukhāṭibnī fillażīna ẓalamụ, innahum mugraqụn","at":"Dan buatlah kapal itu dengan pengawasan dan petunjuk wahyu Kami, dan janganlah engkau bicarakan dengan Aku tentang orang-orang yang zalim. Sesungguhnya mereka itu akan ditenggelamkan.”"},{"id":"38","ar":"وَيَصْنَعُ الْفُلْكَۗ وَكُلَّمَا مَرَّ عَلَيْهِ مَلَاٌ مِّنْ قَوْمِهٖ سَخِرُوْا مِنْهُ ۗقَالَ اِنْ تَسْخَرُوْا مِنَّا فَاِنَّا نَسْخَرُ مِنْكُمْ كَمَا تَسْخَرُوْنَۗ ","bc":"wa yaṣna'ul-fulk, wa kullamā marra 'alaihi mala`um ming qaumihī sakhirụ min-h, qāla in taskharụ minnā fa innā naskharu mingkum kamā taskharụn","at":"Dan mulailah dia (Nuh) membuat kapal. Setiap kali pemimpin kaumnya berjalan melewatinya, mereka mengejeknya. Dia (Nuh) berkata, “Jika kamu mengejek kami, maka kami (pun) akan mengejekmu sebagaimana kamu mengejek (kami)."},{"id":"39","ar":"فَسَوْفَ تَعْلَمُوْنَۙ مَنْ يَّأْتِيْهِ عَذَابٌ يُّخْزِيْهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُّقِيْمٌ","bc":"fa saufa ta'lamụna may ya`tīhi 'ażābuy yukhzīhi wa yaḥillu 'alaihi 'ażābum muqīm","at":"Maka kelak kamu akan mengetahui siapa yang akan ditimpa azab yang menghinakan dan (siapa) yang akan ditimpa azab yang kekal.”"},{"id":"40","ar":"حَتّٰىٓ اِذَا جَاۤءَ اَمْرُنَا وَفَارَ التَّنُّوْرُۙ قُلْنَا احْمِلْ فِيْهَا مِنْ كُلٍّ زَوْجَيْنِ اثْنَيْنِ وَاَهْلَكَ اِلَّا مَنْ سَبَقَ عَلَيْهِ الْقَوْلُ وَمَنْ اٰمَنَ ۗوَمَآ اٰمَنَ مَعَهٗٓ اِلَّا قَلِيْلٌ","bc":"ḥattā iżā jā`a amrunā wa fārat-tannụru qulnaḥmil fīhā ming kullin zaujainiṡnaini wa ahlaka illā man sabaqa 'alaihil-qaulu wa man āman, wa mā āmana ma'ahū illā qalīl","at":"Hingga apabila perintah Kami datang dan tanur (dapur) telah memancarkan air, Kami berfirman, “Muatkanlah ke dalamnya (kapal itu) dari masing-masing (hewan) sepasang (jantan dan betina), dan (juga) keluargamu kecuali orang yang telah terkena ketetapan terdahulu dan (muatkan pula) orang yang beriman.” Ternyata orang-orang beriman yang bersama dengan Nuh hanya sedikit."},{"id":"41","ar":"۞ وَقَالَ ارْكَبُوْا فِيْهَا بِسْمِ اللّٰهِ مَجْرٰ۪ىهَا وَمُرْسٰىهَا ۗاِنَّ رَبِّيْ لَغَفُوْرٌ رَّحِيْمٌ","bc":"wa qālarkabụ fīhā bismillāhi majr)hā wa mursāhā, inna rabbī lagafụrur raḥīm","at":"Dan dia berkata, ”Naiklah kamu semua ke dalamnya (kapal) dengan (menyebut) nama Allah pada waktu berlayar dan berlabuhnya. Sesungguhnya Tuhanku Maha Pengampun, Maha Penyayang.”"},{"id":"42","ar":"وَهِيَ تَجْرِيْ بِهِمْ فِيْ مَوْجٍ كَالْجِبَالِۗ وَنَادٰى نُوْحُ ِۨابْنَهٗ وَكَانَ فِيْ مَعْزِلٍ يّٰبُنَيَّ ارْكَبْ مَّعَنَا وَلَا تَكُنْ مَّعَ الْكٰفِرِيْنَ","bc":"wa hiya tajrī bihim fī maujing kal-jibāl, wa nādā nụḥunibnahụ wa kāna fī ma'ziliy yā bunayyarkam ma'anā wa lā takum ma'al-kāfirīn","at":"Dan kapal itu berlayar membawa mereka ke dalam gelombang laksana gunung-gunung. Dan Nuh memanggil anaknya, ketika dia (anak itu) berada di tempat yang jauh terpencil, “Wahai anakku! Naiklah (ke kapal) bersama kami dan janganlah engkau bersama orang-orang kafir.”"},{"id":"43","ar":"قَالَ سَاٰوِيْٓ اِلٰى جَبَلٍ يَّعْصِمُنِيْ مِنَ الْمَاۤءِ ۗقَالَ لَا عَاصِمَ الْيَوْمَ مِنْ اَمْرِ اللّٰهِ اِلَّا مَنْ رَّحِمَ ۚوَحَالَ بَيْنَهُمَا الْمَوْجُ فَكَانَ مِنَ الْمُغْرَقِيْنَ","bc":"qāla sa`āwī ilā jabaliy ya'ṣimunī minal-mā`, qāla lā 'āṣimal-yauma min amrillāhi illā mar raḥim, wa ḥāla bainahumal-mauju fa kāna minal-mugraqīn","at":"Dia (anaknya) menjawab, “Aku akan mencari perlindungan ke gunung yang dapat menghindarkan aku dari air bah!” (Nuh) berkata, “Tidak ada yang melindungi dari siksaan Allah pada hari ini selain Allah yang Maha Penyayang.” Dan gelombang menjadi penghalang antara keduanya; maka dia (anak itu) termasuk orang yang ditenggelamkan."},{"id":"44","ar":"وَقِيْلَ يٰٓاَرْضُ ابْلَعِيْ مَاۤءَكِ وَيَا سَمَاۤءُ اَقْلِعِيْ وَغِيْضَ الْمَاۤءُ وَقُضِيَ الْاَمْرُ وَاسْتَوَتْ عَلَى الْجُوْدِيِّ وَقِيْلَ بُعْدًا لِّلْقَوْمِ الظّٰلِمِيْنَ","bc":"wa qīla yā arḍubla'ī mā`aki wa yā samā`u aqli'ī wa gīḍal-mā`u wa quḍiyal-amru wastawat 'alal-jụdiyyi wa qīla bu'dal lil-qaumiẓ-ẓālimīn","at":"Dan difirmankan, “Wahai bumi! Telanlah airmu dan wahai langit (hujan!) berhentilah.” Dan air pun disurutkan, dan perintah pun diselesaikan dan kapal itupun berlabuh di atas gunung Judi, dan dikatakan, ”Binasalah orang-orang zalim.”"},{"id":"45","ar":"وَنَادٰى نُوْحٌ رَّبَّهٗ فَقَالَ رَبِّ اِنَّ ابْنِيْ مِنْ اَهْلِيْۚ وَاِنَّ وَعْدَكَ الْحَقُّ وَاَنْتَ اَحْكَمُ الْحٰكِمِيْنَ","bc":"wa nādā nụḥur rabbahụ fa qāla rabbi innabnī min ahlī, wa inna wa'dakal-ḥaqqu wa anta aḥkamul-ḥākimīn","at":"Dan Nuh memohon kepada Tuhannya sambil berkata, “Ya Tuhanku, sesungguhnya anakku adalah termasuk keluargaku, dan janji-Mu itu pasti benar. Engkau adalah hakim yang paling adil.”"},{"id":"46","ar":"قَالَ يٰنُوْحُ اِنَّهٗ لَيْسَ مِنْ اَهْلِكَ ۚاِنَّهٗ عَمَلٌ غَيْرُ صَالِحٍ فَلَا تَسْـَٔلْنِ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ ۗاِنِّيْٓ اَعِظُكَ اَنْ تَكُوْنَ مِنَ الْجٰهِلِيْنَ","bc":"qāla yā nụḥu innahụ laisa min ahlik, innahụ 'amalun gairu ṣāliḥin fa lā tas`alni mā laisa laka bihī 'ilm, innī a'iẓuka an takụna minal-jāhilīn","at":"Dia (Allah) berfirman, “Wahai Nuh! Sesungguhnya dia bukanlah termasuk keluargamu, karena perbuatannya sungguh tidak baik, sebab itu jangan engkau memohon kepada-Ku sesuatu yang tidak engkau ketahui (hakikatnya). Aku menasihatimu agar (engkau) tidak termasuk orang yang bodoh.”"},{"id":"47","ar":"قَالَ رَبِّ اِنِّيْٓ اَعُوْذُ بِكَ اَنْ اَسْـَٔلَكَ مَا لَيْسَ لِيْ بِهٖ عِلْمٌ ۗوَاِلَّا تَغْفِرْ لِيْ وَتَرْحَمْنِيْٓ اَكُنْ مِّنَ الْخٰسِرِيْنَ","bc":"qāla rabbi innī a'ụżu bika an as`alaka mā laisa lī bihī 'ilm, wa illā tagfir lī wa tar-ḥamnī akum minal-khāsirīn","at":"Dia (Nuh) berkata, “Ya Tuhanku, sesungguhnya aku berlindung kepada-Mu untuk memohon kepada-Mu sesuatu yang aku tidak mengetahui (hakikatnya). Kalau Engkau tidak mengampuniku, dan (tidak) menaruh belas kasihan kepadaku, niscaya aku termasuk orang yang rugi.”"},{"id":"48","ar":"قِيْلَ يٰنُوْحُ اهْبِطْ بِسَلٰمٍ مِّنَّا وَبَرَكٰتٍ عَلَيْكَ وَعَلٰٓى اُمَمٍ مِّمَّنْ مَّعَكَ ۗوَاُمَمٌ سَنُمَتِّعُهُمْ ثُمَّ يَمَسُّهُمْ مِّنَّا عَذَابٌ اَلِيْمٌ","bc":"qīla yā nụḥuhbiṭ bisalāmim minnā wa barakātin 'alaika wa 'alā umamim mim mam ma'ak, wa umamun sanumatti'uhum ṡumma yamassuhum minnā 'ażābun alīm","at":"Difirmankan, “Wahai Nuh! Turunlah dengan selamat sejahtera dan penuh keberkahan dari Kami, bagimu dan bagi semua umat (mukmin) yang bersamamu. Dan ada umat-umat yang Kami beri kesenangan (dalam kehidupan dunia), kemudian mereka akan ditimpa azab Kami yang pedih.”"},{"id":"49","ar":"تِلْكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهَآ اِلَيْكَ ۚمَا كُنْتَ تَعْلَمُهَآ اَنْتَ وَلَا قَوْمُكَ مِنْ قَبْلِ هٰذَاۚ فَاصْبِرْۚ اِنَّ الْعَاقِبَةَ لِلْمُتَّقِيْنَ ","bc":"tilka min ambā`il-gaibi nụḥīhā ilaīk, mā kunta ta'lamuhā anta wa lā qaumuka ming qabli hāżā, faṣbir, innal-'āqibata lil-muttaqīn","at":"Itulah sebagian dari berita-berita gaib yang Kami wahyukan kepadamu (Muhammad); tidak pernah engkau mengetahuinya dan tidak (pula) kaummu sebelum ini. Maka bersabarlah, sungguh, kesudahan (yang baik) adalah bagi orang yang bertakwa."},{"id":"50","ar":"وَاِلٰى عَادٍ اَخَاهُمْ هُوْدًا ۗقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗاِنْ اَنْتُمْ اِلَّا مُفْتَرُوْنَ","bc":"wa ilā 'ādin akhāhum hụdā, qāla yā qaumi'budullāha mā lakum min ilāhin gairuh, in antum illā muftarụn","at":"Dan kepada kaum ‘Ad (Kami utus) saudara mereka, Hud. Dia berkata, “Wahai kaumku! Sembahlah Allah, tidak ada tuhan bagimu selain Dia. (Selama ini) kamu hanyalah mengada-ada."},{"id":"51","ar":"يٰقَوْمِ لَآ اَسْـَٔلُكُمْ عَلَيْهِ اَجْرًا ۗاِنْ اَجْرِيَ اِلَّا عَلَى الَّذِيْ فَطَرَنِيْ ۗ اَفَلَا تَعْقِلُوْنَ ","bc":"yā qaumi lā as`alukum 'alaihi ajrā, in ajriya illā 'alallażī faṭaranī, a fa lā ta'qilụn","at":"Wahai kaumku! Aku tidak meminta imbalan kepadamu atas (seruanku) ini. Imbalanku hanyalah dari Allah yang telah menciptakanku. Tidakkah kamu mengerti?”"},{"id":"52","ar":"وَيٰقَوْمِ اسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ يُرْسِلِ السَّمَاۤءَ عَلَيْكُمْ مِّدْرَارًا وَّيَزِدْكُمْ قُوَّةً اِلٰى قُوَّتِكُمْ وَلَا تَتَوَلَّوْا مُجْرِمِيْنَ","bc":"wa yā qaumistagfirụ rabbakum ṡumma tụbū ilaihi yursilis-samā`a 'alaikum midrāraw wa yazidkum quwwatan ilā quwwatikum wa lā tatawallau mujrimīn","at":"Dan (Hud berkata), “Wahai kaumku! Mohonlah ampunan kepada Tuhanmu lalu bertobatlah kepada-Nya, niscaya Dia menurunkan hujan yang sangat deras, Dia akan menambahkan kekuatan di atas kekuatanmu, dan janganlah kamu berpaling menjadi orang yang berdosa.”"},{"id":"53","ar":"قَالُوْا يٰهُوْدُ مَاجِئْتَنَا بِبَيِّنَةٍ وَّمَا نَحْنُ بِتَارِكِيْٓ اٰلِهَتِنَا عَنْ قَوْلِكَ وَمَا نَحْنُ لَكَ بِمُؤْمِنِيْنَ","bc":"qālụ yā hụdu mā ji`tanā bibayyinatiw wa mā naḥnu bitārikī ālihatinā 'ang qaulika wa mā naḥnu laka bimu`minīn","at":" Mereka (kaum ‘Ad) berkata, “Wahai Hud! Engkau tidak mendatangkan suatu bukti yang nyata kepada kami, dan kami tidak akan meninggalkan sesembahan kami karena perkataanmu dan kami tidak akan mempercayaimu,"},{"id":"54","ar":"اِنْ نَّقُوْلُ اِلَّا اعْتَرٰىكَ بَعْضُ اٰلِهَتِنَا بِسُوْۤءٍ ۗقَالَ اِنِّيْٓ اُشْهِدُ اللّٰهَ وَاشْهَدُوْٓا اَنِّيْ بَرِيْۤءٌ مِّمَّا تُشْرِكُوْنَ","bc":"in naqụlu illa'tarāka ba'ḍu ālihatinā bisū`, qāla innī usy-hidullāha wasy-hadū annī barī`um mimmā tusyrikụn","at":"kami hanya mengatakan bahwa sebagian sesembahan kami telah menimpakan penyakit gila atas dirimu.” Dia (Hud) menjawab, “Sesungguhnya aku bersaksi kepada Allah dan saksikanlah bahwa aku berlepas diri dari apa yang kamu persekutukan,"},{"id":"55","ar":"مِنْ دُوْنِهٖ فَكِيْدُوْنِيْ جَمِيْعًا ثُمَّ لَا تُنْظِرُوْنِ","bc":"min dụnihī fa kīdụnī jamī'an ṡumma lā tunẓirụn","at":"dengan yang lain, sebab itu jalankanlah semua tipu dayamu terhadapku dan jangan kamu tunda lagi."},{"id":"56","ar":"اِنِّيْ تَوَكَّلْتُ عَلَى اللّٰهِ رَبِّيْ وَرَبِّكُمْ ۗمَا مِنْ دَاۤبَّةٍ اِلَّا هُوَ اٰخِذٌۢ بِنَاصِيَتِهَا ۗاِنَّ رَبِّيْ عَلٰى صِرَاطٍ مُّسْتَقِيْمٍ","bc":"innī tawakkaltu 'alallāhi rabbī wa rabbikum, mā min dābbatin illā huwa ākhiżum bināṣiyatihā, inna rabbī 'alā ṣirāṭim mustaqīm","at":"Sesungguhnya aku bertawakal kepada Allah Tuhanku dan Tuhanmu. Tidak satu pun makhluk bergerak yang bernyawa melainkan Dialah yang memegang ubun-ubunnya (menguasainya). Sungguh, Tuhanku di jalan yang lurus (adil)."},{"id":"57","ar":"فَاِنْ تَوَلَّوْا فَقَدْ اَبْلَغْتُكُمْ مَّآ اُرْسِلْتُ بِهٖٓ اِلَيْكُمْ ۗوَيَسْتَخْلِفُ رَبِّيْ قَوْمًا غَيْرَكُمْۗ وَلَا تَضُرُّوْنَهٗ شَيْـًٔا ۗاِنَّ رَبِّيْ عَلٰى كُلِّ شَيْءٍ حَفِيْظٌ","bc":"fa in tawallau fa qad ablagtukum mā ursiltu bihī ilaikum, wa yastakhlifu rabbī qauman gairakum, wa lā taḍurrụnahụ syai`ā, inna rabbī 'alā kulli syai`in ḥafīẓ","at":"Maka jika kamu berpaling, maka sungguh, aku telah menyampaikan kepadamu apa yang menjadi tugasku sebagai rasul kepadamu. Dan Tuhanku akan mengganti kamu dengan kaum yang lain, sedang kamu tidak dapat mendatangkan mudarat kepada-Nya sedikit pun. Sesungguhnya Tuhanku Maha Pemelihara segala sesuatu.” "},{"id":"58","ar":"وَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا هُوْدًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّاۚ وَنَجَّيْنٰهُمْ مِّنْ عَذَابٍ غَلِيْظٍ","bc":"wa lammā jā`a amrunā najjainā hụdaw wallażīna āmanụ ma'ahụ biraḥmatim minnā, wa najjaināhum min 'ażābin galīẓ","at":"Dan ketika azab Kami datang, Kami selamatkan Hud dan orang-orang yang beriman bersama dia dengan rahmat Kami. Kami selamatkan (pula) mereka (di akhirat) dari azab yang berat."},{"id":"59","ar":"وَتِلْكَ عَادٌ ۖجَحَدُوْا بِاٰيٰتِ رَبِّهِمْ وَعَصَوْا رُسُلَهٗ وَاتَّبَعُوْٓا اَمْرَ كُلِّ جَبَّارٍ عَنِيْدٍ","bc":"wa tilka 'ādun jaḥadụ bi`āyāti rabbihim wa 'aṣau rusulahụ wattaba'ū amra kulli jabbārin 'anīd","at":"Dan itulah (kisah) kaum ‘Ad yang mengingkari tanda-tanda (kekuasaan) Tuhan. Mereka mendurhakai rasul-rasul-Nya dan menuruti perintah semua penguasa yang sewenang-wenang lagi durhaka."},{"id":"60","ar":"وَاُتْبِعُوْا فِيْ هٰذِهِ الدُّنْيَا لَعْنَةً وَّيَوْمَ الْقِيٰمَةِ ۗ اَلَآ اِنَّ عَادًا كَفَرُوْا رَبَّهُمْ ۗ اَلَا بُعْدًا لِّعَادٍ قَوْمِ هُوْدٍ ","bc":"wa utbi'ụ fī hāżihid-dun-yā la'nataw wa yaumal-qiyāmah, alā inna 'ādang kafarụ rabbahum, alā bu'dal li'āding qaumi hụd","at":"Dan mereka selalu diikuti dengan laknat di dunia ini dan (begitu pula) di hari Kiamat. Ingatlah, kaum ‘Ad itu ingkar kepada Tuhan mereka. Sungguh, binasalah kaum ‘Ad, umat Hud itu,"},{"id":"61","ar":"۞ وَاِلٰى ثَمُوْدَ اَخَاهُمْ صٰلِحًا ۘ قَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗهُوَ اَنْشَاَكُمْ مِّنَ الْاَرْضِ وَاسْتَعْمَرَكُمْ فِيْهَا فَاسْتَغْفِرُوْهُ ثُمَّ تُوْبُوْٓا اِلَيْهِ ۗاِنَّ رَبِّيْ قَرِيْبٌ مُّجِيْبٌ","bc":"wa ilā ṡamụda akhāhum ṣāliḥā, qāla yā qaumi'budullāha mā lakum min ilāhin gairuh, huwa ansya`akum minal-arḍi wasta'marakum fīhā fastagfirụhu ṡumma tụbū ilaīh, inna rabbī qarībum mujīb","at":"dan kepada kaum samud (Kami utus) saudara mereka, Saleh. Dia berkata, “Wahai kaumku! Sembahlah Allah, tidak ada tuhan bagimu selain Dia. Dia telah menciptakanmu dari bumi (tanah) dan menjadikanmu pemakmurnya, karena itu mohonlah ampunan kepada-Nya, kemudian bertobatlah kepada-Nya. Sesungguhnya Tuhanku sangat dekat (rahmat-Nya) dan memperkenankan (doa hamba-Nya).”"},{"id":"62","ar":"قَالُوْا يٰصٰلِحُ قَدْ كُنْتَ فِيْنَا مَرْجُوًّا قَبْلَ هٰذَآ اَتَنْهٰىنَآ اَنْ نَّعْبُدَ مَا يَعْبُدُ اٰبَاۤؤُنَا وَاِنَّنَا لَفِيْ شَكٍّ مِّمَّا تَدْعُوْنَآ اِلَيْهِ مُرِيْبٍ","bc":"qālụ yā ṣāliḥu qad kunta fīnā marjuwwang qabla hāżā a tan-hānā an na'buda mā ya'budu ābā`unā wa innanā lafī syakkim mimmā tad'ụnā ilaihi murīb","at":"Mereka (kaum samud) berkata, “Wahai Saleh! Sungguh, engkau sebelum ini berada di tengah-tengah kami merupakan orang yang di harapkan, mengapa engkau melarang kami menyembah apa yang disembah oleh nenek moyang kami? Sungguh, kami benar-benar dalam keraguan dan kegelisahan terhadap apa (agama) yang engkau serukan kepada kami.”"},{"id":"63","ar":"قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْۗ وَاٰتٰىنِيْ مِنْهُ رَحْمَةً فَمَنْ يَّنْصُرُنِيْ مِنَ اللّٰهِ اِنْ عَصَيْتُهٗ ۗفَمَا تَزِيْدُوْنَنِيْ غَيْرَ تَخْسِيْرٍ","bc":"qāla yā qaumi a ra`aitum ing kuntu 'alā bayyinatim mir rabbī, wa ātānī min-hu raḥmatan fa may yanṣurunī minallāhi in 'aṣaituh, fa mā tazīdụnanī gaira takhsīr","at":"Dia (Saleh) berkata, “Wahai kaumku! Terangkanlah kepadaku jika aku mempunyai bukti yang nyata dari Tuhanku dan diberi-Nya aku rahmat (kenabian) dari-Nya, maka siapa yang akan menolongku dari (azab) Allah jika aku mendurhakai-Nya? Maka kamu hanya akan menambah kerugian kepadaku."},{"id":"64","ar":"وَيٰقَوْمِ هٰذِهٖ نَاقَةُ اللّٰهِ لَكُمْ اٰيَةً فَذَرُوْهَا تَأْكُلْ فِيْٓ اَرْضِ اللّٰهِ وَلَا تَمَسُّوْهَا بِسُوْۤءٍ فَيَأْخُذَكُمْ عَذَابٌ قَرِيْبٌ","bc":"wa yā qaumi hāżihī nāqatullāhi lakum āyatan fa żarụhā ta`kul fī arḍillāhi wa lā tamassụhā bisū`in fa ya`khużakum 'ażābung qarīb","at":"Dan wahai kaumku! Inilah unta betina dari Allah, sebagai mukjizat untukmu, sebab itu biarkanlah dia makan di bumi Allah, dan janganlah kamu mengganggunya dengan gangguan apa pun yang akan menyebabkan kamu segera ditimpa (azab).”"},{"id":"65","ar":"فَعَقَرُوْهَا فَقَالَ تَمَتَّعُوْا فِيْ دَارِكُمْ ثَلٰثَةَ اَيَّامٍ ۗذٰلِكَ وَعْدٌ غَيْرُ مَكْذُوْبٍ","bc":"fa 'aqarụhā fa qāla tamatta'ụ fī dārikum ṡalāṡata ayyām, żālika wa'dun gairu makżụb","at":"Maka mereka menyembelih unta itu, kemudian dia (Saleh) berkata, “Bersukarialah kamu semua di rumahmu selama tiga hari. Itu adalah janji yang tidak dapat didustakan.”"},{"id":"66","ar":"فَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا صٰلِحًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّا وَمِنْ خِزْيِ يَوْمِىِٕذٍ ۗاِنَّ رَبَّكَ هُوَ الْقَوِيُّ الْعَزِيْزُ","bc":"fa lammā jā`a amrunā najjainā ṣāliḥaw wallażīna āmanụ ma'ahụ biraḥmatim minnā wa min khizyi yaumi`iż, inna rabbaka huwal-qawiyyul-'azīz","at":"Maka ketika keputusan Kami datang, Kami selamatkan Saleh dan orang-orang yang beriman bersamanya dengan rahmat Kami dan (Kami selamatkan) dari kehinaan pada hari itu. Sungguh, Tuhanmu, Dia Mahakuat, Mahaperkasa."},{"id":"67","ar":"وَاَخَذَ الَّذِيْنَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوْا فِيْ دِيَارِهِمْ جٰثِمِيْنَۙ ","bc":"wa akhażallażīna ẓalamuṣ-ṣaiḥatu fa aṣbaḥụ fī diyārihim jāṡimīn","at":" Kemudian suara yang mengguntur menimpa orang-orang zalim itu, sehingga mereka mati bergelimpangan di rumahnya."},{"id":"68","ar":"كَاَنْ لَّمْ يَغْنَوْا فِيْهَا ۗ اَلَآ اِنَّ ثَمُوْدَا۠ كَفَرُوْا رَبَّهُمْ ۗ اَلَا بُعْدًا لِّثَمُوْدَ ","bc":"ka`al lam yagnau fīhā, alā inna ṡamụda kafarụ rabbahum, alā bu'dal liṡamụd","at":"Seolah-olah mereka belum pernah tinggal di tempat itu. Ingatlah, kaum samud mengingkari Tuhan mereka. Ingatlah, binasalah kaum samud."},{"id":"69","ar":"وَلَقَدْ جَاۤءَتْ رُسُلُنَآ اِبْرٰهِيْمَ بِالْبُشْرٰى قَالُوْا سَلٰمًا ۖقَالَ سَلٰمٌ فَمَا لَبِثَ اَنْ جَاۤءَ بِعِجْلٍ حَنِيْذٍ","bc":"wa laqad jā`at rusulunā ibrāhīma bil-busyrā qālụ salāmā, qāla salāmun fa mā labiṡa an jā`a bi'ijlin ḥanīż","at":"Dan para utusan Kami (para malaikat) telah datang kepada Ibrahim dengan membawa kabar gembira, mereka mengucapkan, “Selamat.” Dia (Ibrahim) menjawab, “Selamat (atas kamu).” Ma-ka tidak lama kemudian Ibrahim menyuguhkan daging anak sapi yang dipanggang."},{"id":"70","ar":"فَلَمَّا رَاٰىٓ اَيْدِيَهُمْ لَا تَصِلُ اِلَيْهِ نَكِرَهُمْ وَاَوْجَسَ مِنْهُمْ خِيْفَةً ۗقَالُوْا لَا تَخَفْ اِنَّآ اُرْسِلْنَآ اِلٰى قَوْمِ لُوْطٍۗ ","bc":"fa lammā ra`ā aidiyahum lā taṣilu ilaihi nakirahum wa aujasa min-hum khīfah, qālụ lā takhaf innā ursilnā ilā qaumi lụṭ","at":"Maka ketika dilihatnya tangan mereka tidak menjamahnya, dia (Ibrahim) mencurigai mereka, dan merasa takut kepada mereka. Mereka (malaikat) berkata, “Jangan takut, sesungguhnya kami diutus kepada kaum Lut.”"},{"id":"71","ar":"وَامْرَاَتُهٗ قَاۤىِٕمَةٌ فَضَحِكَتْ فَبَشَّرْنٰهَا بِاِسْحٰقَۙ وَمِنْ وَّرَاۤءِ اِسْحٰقَ يَعْقُوْبَ","bc":"wamra`atuhụ qā`imatun fa ḍaḥikat fa basysyarnāhā bi`is-ḥāqa wa miw warā`i is-ḥāqa ya'qụb","at":"Dan istrinya berdiri lalu dia tersenyum. Maka Kami sampaikan kepadanya kabar gembira tentang (kelahiran) Ishak dan setelah Ishak (akan lahir) Yakub."},{"id":"72","ar":"قَالَتْ يٰوَيْلَتٰىٓ ءَاَلِدُ وَاَنَا۠ عَجُوْزٌ وَّهٰذَا بَعْلِيْ شَيْخًا ۗاِنَّ هٰذَا لَشَيْءٌ عَجِيْبٌ","bc":"qālat yā wailatā a alidu wa ana 'ajụzuw wa hāżā ba'lī syaikhā, inna hāżā lasyai`un 'ajīb","at":"Dia (istrinya) berkata, “Sungguh ajaib, mungkinkah aku akan melahirkan anak padahal aku sudah tua, dan suamiku ini sudah sangat tua? Ini benar-benar sesuatu yang ajaib.”"},{"id":"73","ar":"قَالُوْٓا اَتَعْجَبِيْنَ مِنْ اَمْرِ اللّٰهِ رَحْمَتُ اللّٰهِ وَبَرَكٰتُهٗ عَلَيْكُمْ اَهْلَ الْبَيْتِۗ اِنَّهُ حَمِيْدٌ مَّجِيْدٌ","bc":"qālū a ta'jabīna min amrillāhi raḥmatullāhi wa barakātuhụ 'alaikum ahlal-baīt, innahụ ḥamīdum majīd","at":"Mereka (para malaikat) berkata, “Mengapa engkau merasa heran tentang ketetapan Allah? (Itu adalah) rahmat dan berkah Allah, dicurahkan kepada kamu, wahai ahlulbait! Sesungguhnya Allah Maha Terpuji, Maha Pengasih.”"},{"id":"74","ar":"فَلَمَّا ذَهَبَ عَنْ اِبْرٰهِيْمَ الرَّوْعُ وَجَاۤءَتْهُ الْبُشْرٰى يُجَادِلُنَا فِيْ قَوْمِ لُوْطٍ","bc":"fa lammā żahaba 'an ibrāhīmar-rau'u wa jā`at-hul-busyrā yujādilunā fī qaumi lụṭ","at":"Maka ketika rasa takut hilang dari Ibrahim dan kabar gembira telah datang kepadanya, dia pun bersoal jawab dengan (para malaikat) Kami tentang kaum Lut."},{"id":"75","ar":"اِنَّ اِبْرٰهِيْمَ لَحَلِيْمٌ اَوَّاهٌ مُّنِيْبٌ","bc":"inna ibrāhīma laḥalīmun awwāhum munīb","at":"Ibrahim sungguh penyantun, lembut hati dan suka kembali (kepada Allah)."},{"id":"76","ar":"يٰٓاِبْرٰهِيْمُ اَعْرِضْ عَنْ هٰذَا ۚاِنَّهٗ قَدْ جَاۤءَ اَمْرُ رَبِّكَۚ وَاِنَّهُمْ اٰتِيْهِمْ عَذَابٌ غَيْرُ مَرْدُوْدٍ","bc":"yā ibrāhīmu a'riḍ 'an hāżā, innahụ qad jā`a amru rabbik, wa innahum ātīhim 'ażābun gairu mardụd","at":"Wahai Ibrahim! Tinggalkanlah (perbincangan) ini, sungguh, ketetapan Tuhanmu telah datang, dan mereka itu akan ditimpa azab yang tidak dapat ditolak."},{"id":"77","ar":"وَلَمَّا جَاۤءَتْ رُسُلُنَا لُوْطًا سِيْۤءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَّقَالَ هٰذَا يَوْمٌ عَصِيْبٌ","bc":"wa lammā jā`at rusulunā lụṭan sī`a bihim wa ḍāqa bihim żar'aw wa qāla hāżā yaumun 'aṣīb","at":"Dan ketika para utusan Kami (para malaikat) itu datang kepada Lut, dia merasa curiga dan dadanya merasa sempit karena (kedatangan)nya. Dia (Lut) berkata, “Ini hari yang sangat sulit.”"},{"id":"78","ar":"وَجَاۤءَهٗ قَوْمُهٗ يُهْرَعُوْنَ اِلَيْهِۗ وَمِنْ قَبْلُ كَانُوْا يَعْمَلُوْنَ السَّيِّاٰتِۗ قَالَ يٰقَوْمِ هٰٓؤُلَاۤءِ بَنَاتِيْ هُنَّ اَطْهَرُ لَكُمْ فَاتَّقُوا اللّٰهَ وَلَا تُخْزُوْنِ فِيْ ضَيْفِيْۗ اَلَيْسَ مِنْكُمْ رَجُلٌ رَّشِيْدٌ","bc":"wa jā`ahụ qaumuhụ yuhra'ụna ilaīh, wa ming qablu kānụ ya'malụnas-sayyi`āt, qāla yā qaumi hā`ulā`i banatī hunna aṭ-haru lakum fattaqullāha wa lā tukhzụni fī ḍaifī, a laisa mingkum rajulur rasyīd","at":"Dan kaumnya segera datang kepadanya. Dan sejak dahulu mereka selalu melakukan perbuatan keji. Lut berkata, “Wahai kaumku! Inilah putri-putri (negeri)ku mereka lebih suci bagimu, maka bertakwalah kepada Allah dan janganlah kamu mencemarkan (nama)ku terhadap tamuku ini. Tidak adakah di antaramu orang yang pandai?”"},{"id":"79","ar":"قَالُوْا لَقَدْ عَلِمْتَ مَا لَنَا فِيْ بَنٰتِكَ مِنْ حَقٍّۚ وَاِنَّكَ لَتَعْلَمُ مَا نُرِيْدُ","bc":"qālụ laqad 'alimta mā lanā fī banātika min ḥaqq, wa innaka lata'lamu mā nurīd","at":"Mereka menjawab, “Sesungguhnya eng-kau pasti tahu bahwa kami tidak mempunyai keinginan (syahwat) terhadap putri-putrimu; dan engkau tentu mengetahui apa yang (sebenarnya) kami kehendaki.”"},{"id":"80","ar":"قَالَ لَوْ اَنَّ لِيْ بِكُمْ قُوَّةً اَوْ اٰوِيْٓ اِلٰى رُكْنٍ شَدِيْدٍ","bc":"qāla lau anna lī bikum quwwatan au āwī ilā ruknin syadīd","at":"Dia (Lut) berkata, “Sekiranya aku mempunyai kekuatan (untuk menolakmu) atau aku dapat berlindung kepada keluarga yang kuat (tentu aku lakukan).”"},{"id":"81","ar":"قَالُوْا يٰلُوْطُ اِنَّا رُسُلُ رَبِّكَ لَنْ يَّصِلُوْٓا اِلَيْكَ فَاَسْرِ بِاَهْلِكَ بِقِطْعٍ مِّنَ الَّيْلِ وَلَا يَلْتَفِتْ مِنْكُمْ اَحَدٌ اِلَّا امْرَاَتَكَۗ اِنَّهٗ مُصِيْبُهَا مَآ اَصَابَهُمْ ۗاِنَّ مَوْعِدَهُمُ الصُّبْحُ ۗ اَلَيْسَ الصُّبْحُ بِقَرِيْبٍ","bc":"qālụ yā lụṭu innā rusulu rabbika lay yaṣilū ilaika fa asri bi`ahlika biqiṭ'im minal-laili wa lā yaltafit mingkum aḥadun illamra`atak, innahụ muṣībuhā mā aṣābahum, inna mau'idahumuṣ-ṣub-h, a laisaṣ-ṣub-ḥu biqarīb","at":"Mereka (para malaikat) berkata, “Wahai Lut! Sesungguhnya kami adalah para utusan Tuhanmu, mereka tidak akan dapat mengganggu kamu, sebab itu pergilah beserta keluargamu pada akhir malam dan jangan ada seorang pun di antara kamu yang menoleh ke belakang, kecuali istrimu. Sesungguhnya dia (juga) akan ditimpa (siksaan) yang menimpa mereka. Sesungguhnya saat terjadinya siksaan bagi mereka itu pada waktu subuh. Bukankah subuh itu sudah dekat?”"},{"id":"82","ar":"فَلَمَّا جَاۤءَ اَمْرُنَا جَعَلْنَا عَالِيَهَا سَافِلَهَا وَاَمْطَرْنَا عَلَيْهَا حِجَارَةً مِّنْ سِجِّيْلٍ مَّنْضُوْدٍ","bc":"fa lammā jā`a amrunā ja'alnā 'āliyahā sāfilahā wa amṭarnā 'alaihā ḥijāratam min sijjīlim manḍụd","at":"Maka ketika keputusan Kami datang, Kami menjungkirbalikkannya negeri kaum Lut, dan Kami hujani mereka bertubi-tubi dengan batu dari tanah yang terbakar, "},{"id":"83","ar":"مُسَوَّمَةً عِنْدَ رَبِّكَۗ وَمَا هِيَ مِنَ الظّٰلِمِيْنَ بِبَعِيْدٍ ","bc":"musawwamatan 'inda rabbik, wa mā hiya minaẓ-ẓālimīna biba'īd","at":"yang diberi tanda oleh Tuhanmu. Dan siksaan itu tiadalah jauh dari orang yang zalim."},{"id":"84","ar":"۞ وَاِلٰى مَدْيَنَ اَخَاهُمْ شُعَيْبًا ۗقَالَ يٰقَوْمِ اعْبُدُوا اللّٰهَ مَا لَكُمْ مِّنْ اِلٰهٍ غَيْرُهٗ ۗوَلَا تَنْقُصُوا الْمِكْيَالَ وَالْمِيْزَانَ اِنِّيْٓ اَرٰىكُمْ بِخَيْرٍ وَّاِنِّيْٓ اَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ مُّحِيْطٍ","bc":"wa ilā madyana akhāhum syu'aibā, qāla yā qaumi'budullāha mā lakum min ilāhin gairuh, wa lā tangquṣul-mikyāla wal-mīzāna innī arākum bikhairiw wa innī akhāfu 'alaikum 'ażāba yaumim muḥīṭ","at":"Dan kepada (penduduk) Madyan (Kami utus) saudara mereka, Syuaib. Dia berkata, “Wahai kaumku! Sembahlah Allah, tidak ada tuhan bagimu selain Dia. Dan janganlah kamu kurangi takaran dan timbangan. Sesungguhnya aku melihat kamu dalam keadaan yang baik (makmur). Dan sesungguhnya aku khawatir kamu akan ditimpa azab pada hari yang membinasakan (Kiamat)."},{"id":"85","ar":"وَيٰقَوْمِ اَوْفُوا الْمِكْيَالَ وَالْمِيْزَانَ بِالْقِسْطِ وَلَا تَبْخَسُوا النَّاسَ اَشْيَاۤءَهُمْ وَلَا تَعْثَوْا فِى الْاَرْضِ مُفْسِدِيْنَ","bc":"wa yā qaumi auful-mikyāla wal-mīzāna bil-qisṭi wa lā tabkhasun-nāsa asy-yā`ahum wa lā ta'ṡau fil-arḍi mufsidīn","at":"Dan wahai kaumku! Penuhilah takaran dan timbangan dengan adil, dan janganlah kamu merugikan manusia terhadap hak-hak mereka dan jangan kamu membuat kejahatan di bumi dengan berbuat kerusakan."},{"id":"86","ar":"بَقِيَّتُ اللّٰهِ خَيْرٌ لَّكُمْ اِنْ كُنْتُمْ مُّؤْمِنِيْنَ ەۚ وَمَآ اَنَا۠ عَلَيْكُمْ بِحَفِيْظٍ","bc":"baqiyyatullāhi khairul lakum ing kuntum mu`minīn, wa mā ana 'alaikum biḥafīẓ","at":"Sisa (yang halal) dari Allah adalah lebih baik bagimu jika kamu orang yang beriman. Dan aku bukanlah seorang penjaga atas dirimu.”"},{"id":"87","ar":"قَالُوْا يٰشُعَيْبُ اَصَلٰوتُكَ تَأْمُرُكَ اَنْ نَّتْرُكَ مَا يَعْبُدُ اٰبَاۤؤُنَآ اَوْ اَنْ نَّفْعَلَ فِيْٓ اَمْوَالِنَا مَا نَشٰۤؤُا ۗاِنَّكَ لَاَنْتَ الْحَلِيْمُ الرَّشِيْدُ","bc":"qālụ yā syu'aibu a ṣalātuka ta`muruka an natruka mā ya'budu ābā`unā au an naf'ala fī amwālinā mā nasyā`, innaka la`antal-ḥalīmur-rasyīd","at":"Mereka berkata, “Wahai Syuaib! Apakah agamamu yang menyuruhmu agar kami meninggalkan apa yang disembah nenek moyang kami atau melarang kami mengelola harta kami menurut cara yang kami kehendaki? Sesungguhnya engkau benar-benar orang yang sangat penyantun dan pandai.”"},{"id":"88","ar":"قَالَ يٰقَوْمِ اَرَءَيْتُمْ اِنْ كُنْتُ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّيْ وَرَزَقَنِيْ مِنْهُ رِزْقًا حَسَنًا وَّمَآ اُرِيْدُ اَنْ اُخَالِفَكُمْ اِلٰى مَآ اَنْهٰىكُمْ عَنْهُ ۗاِنْ اُرِيْدُ اِلَّا الْاِصْلَاحَ مَا اسْتَطَعْتُۗ وَمَا تَوْفِيْقِيْٓ اِلَّا بِاللّٰهِ ۗعَلَيْهِ تَوَكَّلْتُ وَاِلَيْهِ اُنِيْبُ ","bc":"qāla yā qaumi a ra`aitum ing kuntu 'alā bayyinatim mir rabbī wa razaqanī min-hu rizqan ḥasanaw wa mā urīdu an ukhālifakum ilā mā an-hākum 'an-h, in urīdu illal-iṣlāḥa mastaṭa't, wa mā taufīqī illā billāh, 'alaihi tawakkaltu wa ilaihi unīb","at":"Dia (Syuaib) berkata, “Wahai kaumku! Terangkan padaku jika aku mempunyai bukti yang nyata dari Tuhanku dan aku dianugerahi-Nya rezeki yang baik (pantaskah aku menyalahi perintah-Nya)? Aku tidak bermaksud menyalahi kamu terhadap apa yang aku larang darinya. Aku hanya bermaksud (mendatangkan) perbaikan selama aku masih sanggup. Dan petunjuk yang aku ikuti hanya dari Allah. Kepada-Nya aku bertawakal dan kepada-Nya (pula) aku kembali."},{"id":"89","ar":"وَيٰقَوْمِ لَا يَجْرِمَنَّكُمْ شِقَاقِيْٓ اَنْ يُّصِيْبَكُمْ مِّثْلُ مَآ اَصَابَ قَوْمَ نُوْحٍ اَوْ قَوْمَ هُوْدٍ اَوْ قَوْمَ صٰلِحٍ ۗوَمَا قَوْمُ لُوْطٍ مِّنْكُمْ بِبَعِيْدٍ","bc":"wa yā qaumi lā yajrimannakum syiqāqī ay yuṣībakum miṡlu mā aṣāba qauma nụḥin au qauma hụdin au qauma ṣāliḥ, wa mā qaumu lụṭim mingkum biba'īd","at":"Dan wahai kaumku! Janganlah pertentangan antara aku (dengan kamu) menyebabkan kamu berbuat dosa, sehingga kamu ditimpa siksaan seperti yang menimpa kaum Nuh, kaum Hud atau kaum Saleh, sedang kaum Lut tidak jauh dari kamu."},{"id":"90","ar":"وَاسْتَغْفِرُوْا رَبَّكُمْ ثُمَّ تُوْبُوْٓا اِلَيْهِ ۗاِنَّ رَبِّيْ رَحِيْمٌ وَّدُوْدٌ","bc":"wastagfirụ rabbakum ṡumma tụbū ilaīh, inna rabbī raḥīmuw wadụd","at":" Dan mohonlah ampunan kepada Tuhanmu, kemudian bertobatlah kepada-Nya. Sungguh, Tuhanku Maha Penyayang, Maha Pengasih.”"},{"id":"91","ar":"قَالُوْا يٰشُعَيْبُ مَا نَفْقَهُ كَثِيْرًا مِّمَّا تَقُوْلُ وَاِنَّا لَنَرٰىكَ فِيْنَا ضَعِيْفًا ۗوَلَوْلَا رَهْطُكَ لَرَجَمْنٰكَ ۖوَمَآ اَنْتَ عَلَيْنَا بِعَزِيْزٍ ","bc":"qālụ yā syu'aibu mā nafqahu kaṡīram mimmā taqụlu wa innā lanarāka fīnā ḍa'īfā, walau lā rahṭuka larajamnāka wa mā anta 'alainā bi'azīz","at":"Mereka berkata, “Wahai Syuaib! Kami tidak banyak mengerti tentang apa yang engkau katakan itu, sedang kenyataannya kami memandang engkau seorang yang lemah di antara kami. Kalau tidak karena keluargamu, tentu kami telah merajam engkau, sedang engkau pun bukan seorang yang berpengaruh di lingkungan kami.”"},{"id":"92","ar":"قَالَ يٰقَوْمِ اَرَهْطِيْٓ اَعَزُّ عَلَيْكُمْ مِّنَ اللّٰهِ ۗوَاتَّخَذْتُمُوْهُ وَرَاۤءَكُمْ ظِهْرِيًّا ۗاِنَّ رَبِّيْ بِمَا تَعْمَلُوْنَ مُحِيْطٌ","bc":"qāla yā qaumi a rahṭī a'azzu 'alaikum minallāh, wattakhażtumụhu warā`akum ẓihriyyā, inna rabbī bimā ta'malụna muḥīṭ","at":"Dia (Syuaib) menjawab, “Wahai kaumku! Apakah keluargaku lebih terhormat menurut pandanganmu daripada Allah, bahkan Dia kamu tempatkan di belakangmu (diabaikan)? Ketahuilah sesungguhnya (pengetahuan) Tuhanku meliputi apa yang kamu kerjakan."},{"id":"93","ar":"وَيٰقَوْمِ اعْمَلُوْا عَلٰى مَكَانَتِكُمْ اِنِّيْ عَامِلٌ ۗسَوْفَ تَعْلَمُوْنَۙ مَنْ يَّأْتِيْهِ عَذَابٌ يُّخْزِيْهِ وَمَنْ هُوَ كَاذِبٌۗ وَارْتَقِبُوْٓا اِنِّيْ مَعَكُمْ رَقِيْبٌ","bc":"wa yā qaumi'malụ 'alā makānatikum innī 'āmil, saufa ta'lamụna may ya`tīhi 'ażābuy yukhzīhi wa man huwa kāżib, wartaqibū innī ma'akum raqīb","at":"Dan wahai kaumku! Berbuatlah menurut kemampuanmu, sesungguhnya aku pun berbuat (pula). Kelak kamu akan mengetahui siapa yang akan ditimpa azab yang menghinakan dan siapa yang berdusta. Dan tunggulah! Sesungguhnya aku bersamamu adalah orang yang menunggu.”"},{"id":"94","ar":"وَلَمَّا جَاۤءَ اَمْرُنَا نَجَّيْنَا شُعَيْبًا وَّالَّذِيْنَ اٰمَنُوْا مَعَهٗ بِرَحْمَةٍ مِّنَّاۚ وَاَخَذَتِ الَّذِيْنَ ظَلَمُوا الصَّيْحَةُ فَاَصْبَحُوْا فِيْ دِيَارِهِمْ جٰثِمِيْنَۙ ","bc":"wa lammā jā`a amrunā najjainā syu'aibaw wallażīna āmanụ ma'ahụ biraḥmatim minnā, wa akhażatillażīna ẓalamuṣ-ṣaiḥatu fa aṣbaḥụ fī diyārihim jāṡimīn","at":"Maka ketika keputusan Kami datang, Kami selamatkan Syuaib dan orang-orang yang beriman bersamanya dengan rahmat Kami. Sedang orang yang zalim dibinasakan oleh suara yang mengguntur, sehingga mereka mati bergelimpangan di rumahnya,"},{"id":"95","ar":"كَاَنْ لَّمْ يَغْنَوْا فِيْهَا ۗ اَلَا بُعْدًا لِّمَدْيَنَ كَمَا بَعِدَتْ ثَمُوْدُ ","bc":"ka`al lam yagnau fīhā, alā bu'dal limadyana kamā ba'idat ṡamụd","at":"seolah-olah mereka belum pernah tinggal di tempat itu. Ingatlah, binasalah penduduk Madyan sebagaimana kaum samud (juga) telah binasa."},{"id":"96","ar":"وَلَقَدْ اَرْسَلْنَا مُوْسٰى بِاٰيٰتِنَا وَسُلْطٰنٍ مُّبِيْنٍۙ ","bc":"wa laqad arsalnā mụsā bi`āyātinā wa sulṭānim mubīn","at":"Dan sungguh, Kami telah mengutus Musa dengan tanda-tanda (kekuasaan) Kami dan bukti yang nyata,"},{"id":"97","ar":"اِلٰى فِرْعَوْنَ وَملَا۟ىِٕهٖ فَاتَّبَعُوْٓا اَمْرَ فِرْعَوْنَ ۚوَمَآ اَمْرُ فِرْعَوْنَ بِرَشِيْدٍ","bc":"ilā fir'auna wa mala`ihī fattaba'ū amra fir'aụn, wa mā amru fir'auna birasyīd","at":"Kepada Fir‘aun dan para pemuka kaumnya, tetapi mereka mengikuti perintah Fir‘aun, padahal perintah Fir‘aun bukanlah (perintah) yang benar."},{"id":"98","ar":"يَقْدُمُ قَوْمَهٗ يَوْمَ الْقِيٰمَةِ فَاَوْرَدَهُمُ النَّارَ ۗوَبِئْسَ الْوِرْدُ الْمَوْرُوْدُ","bc":"yaqdumu qaumahụ yaumal-qiyāmati fa auradahumun-nār, wa bi`sal-wirdul-maurụd","at":"Dia (Fir‘aun) berjalan di depan kaumnya di hari Kiamat, lalu membawa mereka masuk ke dalam neraka. Neraka itu seburuk-buruk tempat yang dimasuki."},{"id":"99","ar":"وَاُتْبِعُوْا فِيْ هٰذِهٖ لَعْنَةً وَّيَوْمَ الْقِيٰمَةِۗ بِئْسَ الرِّفْدُ الْمَرْفُوْدُ","bc":"wa utbi'ụ fī hāżihī la'nataw wa yaumal-qiyāmah, bi`sar-rifdul-marfụd","at":"Dan mereka diikuti dengan laknat di sini (dunia) dan (begitu pula) pada hari Kiamat. (Laknat) itu seburuk-buruk pemberian yang diberikan."},{"id":"100","ar":"ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْقُرٰى نَقُصُّهٗ عَلَيْكَ مِنْهَا قَاۤىِٕمٌ وَّحَصِيْدٌ","bc":"żālika min ambā`il-qurā naquṣṣuhụ 'alaika min-hā qā`imuw wa ḥaṣīd","at":"Itulah beberapa berita tentang negeri-negeri (yang telah dibinasakan) yang Kami ceritakan kepadamu (Muhammad). Di antara negeri-negeri itu sebagian masih ada bekas-bekasnya dan ada (pula) yang telah musnah."},{"id":"101","ar":"وَمَا ظَلَمْنٰهُمْ وَلٰكِنْ ظَلَمُوْٓا اَنْفُسَهُمْ فَمَآ اَغْنَتْ عَنْهُمْ اٰلِهَتُهُمُ الَّتِيْ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ مِنْ شَيْءٍ لَّمَّا جَاۤءَ اَمْرُ رَبِّكَۗ وَمَا زَادُوْهُمْ غَيْرَ تَتْبِيْبٍ ","bc":"wa mā ẓalamnāhum wa lākin ẓalamū anfusahum fa mā agnat 'an-hum ālihatuhumullatī yad'ụna min dụnillāhi min syai`il lammā jā`a amru rabbik, wa mā zādụhum gaira tatbīb","at":"Dan Kami tidak menzalimi mereka, tetapi merekalah yang menzalimi diri mereka sendiri, karena itu tidak bermanfaat sedikit pun bagi mereka sesembahan yang mereka sembah selain Allah, ketika siksaan Tuhanmu datang. Sesembahan itu hanya menambah kebinasaan bagi mereka."},{"id":"102","ar":"وَكَذٰلِكَ اَخْذُ رَبِّكَ اِذَآ اَخَذَ الْقُرٰى وَهِيَ ظَالِمَةٌ ۗاِنَّ اَخْذَهٗٓ اَلِيْمٌ شَدِيْدٌ","bc":"wa każālika akhżu rabbika iżā akhażal-qurā wa hiya ẓālimah, inna akhżahū alīmun syadīd","at":"Dan begitulah siksa Tuhanmu apabila Dia menyiksa (penduduk) negeri-negeri yang berbuat zalim. Sungguh, siksa-Nya sangat pedih, sangat berat."},{"id":"103","ar":"اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّمَنْ خَافَ عَذَابَ الْاٰخِرَةِ ۗذٰلِكَ يَوْمٌ مَّجْمُوْعٌۙ لَّهُ النَّاسُ وَذٰلِكَ يَوْمٌ مَّشْهُوْدٌ","bc":"inna fī żālika la`āyatal liman khāfa 'ażābal-ākhirah, żālika yaumum majmụ'ul lahun-nāsu wa żālika yaumum masy-hụd","at":"Sesungguhnya pada yang demikian itu pasti terdapat pelajaran bagi orang-orang yang takut kepada azab akhirat. Itulah hari ketika semua manusia dikumpulkan (untuk dihisab), dan itulah hari yang disaksikan (oleh semua makhluk)."},{"id":"104","ar":"وَمَا نُؤَخِّرُهٗٓ اِلَّا لِاَجَلٍ مَّعْدُوْدٍۗ ","bc":"wa mā nu`akhkhiruhū illā li`ajalim ma'dụd","at":"Dan Kami tidak akan menunda, kecuali sampai waktu yang sudah ditentukan."},{"id":"105","ar":"يَوْمَ يَأْتِ لَا تَكَلَّمُ نَفْسٌ اِلَّا بِاِذْنِهٖۚ فَمِنْهُمْ شَقِيٌّ وَّسَعِيْدٌ","bc":"yauma ya`ti lā takallamu nafsun illā bi`iżnih, fa min-hum syaqiyyuw wa sa'īd","at":"Ketika hari itu datang, tidak seorang pun yang berbicara, kecuali dengan izin-Nya; maka di antara mereka ada yang sengsara dan ada yang berbahagia."},{"id":"106","ar":"فَاَمَّا الَّذِيْنَ شَقُوْا فَفِى النَّارِ لَهُمْ فِيْهَا زَفِيْرٌ وَّشَهِيْقٌۙ ","bc":"fa ammallażīna syaqụ fa fin-nāri lahum fīhā zafīruw wa syahīq","at":"Maka adapun orang-orang yang sengsara, maka (tempatnya) di dalam neraka, di sana mereka mengeluarkan dan menarik nafas dengan merintih,"},{"id":"107","ar":"خٰلِدِيْنَ فِيْهَا مَا دَامَتِ السَّمٰوٰتُ وَالْاَرْضُ اِلَّا مَا شَاۤءَ رَبُّكَۗ اِنَّ رَبَّكَ فَعَّالٌ لِّمَا يُرِيْدُ","bc":"khālidīna fīhā mā dāmatis-samāwātu wal-arḍu illā mā syā`a rabbuk, inna rabbaka fa''ālul limā yurīd","at":"mereka kekal di dalamnya selama ada langit dan bumi, kecuali jika Tuhanmu menghendaki (yang lain). Sungguh, Tuhanmu Maha Pelaksana terhadap apa yang Dia kehendaki."},{"id":"108","ar":"۞ وَاَمَّا الَّذِيْنَ سُعِدُوْا فَفِى الْجَنَّةِ خٰلِدِيْنَ فِيْهَا مَا دَامَتِ السَّمٰوٰتُ وَالْاَرْضُ اِلَّا مَا شَاۤءَ رَبُّكَۗ عَطَاۤءً غَيْرَ مَجْذُوْذٍ","bc":"wa ammallażīna su'idụ fa fil-jannati khālidīna fīhā mā dāmatis-samāwātu wal-arḍu illā mā syā`a rabbuk, 'aṭā`an gaira majżụż","at":"Dan adapun orang-orang yang berbahagia, maka (tempatnya) di dalam surga; mereka kekal di dalamnya selama ada langit dan bumi, kecuali jika Tuhanmu menghendaki (yang lain); sebagai karunia yang tidak ada putus-putusnya."},{"id":"109","ar":"فَلَا تَكُ فِيْ مِرْيَةٍ مِّمَّا يَعْبُدُ هٰٓؤُلَاۤءِ ۗمَا يَعْبُدُوْنَ اِلَّا كَمَا يَعْبُدُ اٰبَاۤؤُهُمْ مِّنْ قَبْلُ ۗوَاِنَّا لَمُوَفُّوْهُمْ نَصِيْبَهُمْ غَيْرَ مَنْقُوْصٍ ","bc":"fa lā taku fī miryatim mimmā ya'budu hā`ulā`, mā ya'budụna illā kamā ya'budu ābā`uhum ming qabl, wa innā lamuwaffụhum naṣībahum gaira mangqụṣ","at":"Maka janganlah engkau (Muhammad) ragu-ragu tentang apa yang mereka sembah. Mereka menyembah sebagaimana nenek moyang mereka dahulu menyembah. Kami pasti akan menyempurnakan pembalasan (terhadap) mereka tanpa dikurangi sedikit pun."},{"id":"110","ar":"وَلَقَدْ اٰتَيْنَا مُوْسَى الْكِتٰبَ فَاخْتُلِفَ فِيْهِ ۗوَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَقُضِيَ بَيْنَهُمْ ۚوَاِنَّهُمْ لَفِيْ شَكٍّ مِّنْهُ مُرِيْبٍ","bc":"wa laqad ātainā mụsal-kitāba fakhtulifa fīh, walau lā kalimatun sabaqat mir rabbika laquḍiya bainahum, wa innahum lafī syakkim min-hu murīb","at":"Dan sungguh, Kami telah memberikan Kitab (Taurat) kepada Musa, lalu diperselisihkannya. Dan kalau tidak ada ketetapan yang terdahulu dari Tuhanmu, niscaya telah dilaksanakan hukuman di antara mereka. Sungguh, mereka (orang kafir Mekah) benar-benar dalam kebimbangan dan keraguan terhadapnya (Al-Qur'an)."},{"id":"111","ar":"وَاِنَّ كُلًّا لَّمَّا لَيُوَفِّيَنَّهُمْ رَبُّكَ اَعْمَالَهُمْ ۗاِنَّهٗ بِمَا يَعْمَلُوْنَ خَبِيْرٌ","bc":"wa inna kullal lammā layuwaffiyannahum rabbuka a'mālahum, innahụ bimā ya'malụna khabīr","at":"Dan sesungguhnya kepada masing-masing (yang berselisih itu) pasti Tuhanmu akan memberi balasan secara penuh atas perbuatan mereka. Sungguh, Dia Mahateliti terhadap apa yang mereka kerjakan."},{"id":"112","ar":"فَاسْتَقِمْ كَمَآ اُمِرْتَ وَمَنْ تَابَ مَعَكَ وَلَا تَطْغَوْاۗ اِنَّهٗ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ","bc":"fastaqim kamā umirta wa man tāba ma'aka wa lā taṭgau, innahụ bimā ta'malụna baṣīr","at":"Maka tetaplah engkau (Muhammad) (di jalan yang benar), sebagaimana telah diperintahkan kepadamu dan (juga) orang yang bertobat bersamamu, dan janganlah kamu melampaui batas. Sungguh, Dia Maha Melihat apa yang kamu kerjakan."},{"id":"113","ar":"وَلَا تَرْكَنُوْٓا اِلَى الَّذِيْنَ ظَلَمُوْا فَتَمَسَّكُمُ النَّارُۙ وَمَا لَكُمْ مِّنْ دُوْنِ اللّٰهِ مِنْ اَوْلِيَاۤءَ ثُمَّ لَا تُنْصَرُوْنَ","bc":"wa lā tarkanū ilallażīna ẓalamụ fa tamassakumun-nāru wa mā lakum min dụnillāhi min auliyā`a ṡumma lā tunṣarụn","at":"Dan janganlah kamu cenderung kepada orang yang zalim yang menyebabkan kamu disentuh api neraka, sedangkan kamu tidak mempunyai seorang penolong pun selain Allah, sehingga kamu tidak akan diberi pertolongan."},{"id":"114","ar":"وَاَقِمِ الصَّلٰوةَ طَرَفَيِ النَّهَارِ وَزُلَفًا مِّنَ الَّيْلِ ۗاِنَّ الْحَسَنٰتِ يُذْهِبْنَ السَّيِّاٰتِۗ ذٰلِكَ ذِكْرٰى لِلذَّاكِرِيْنَ","bc":"wa aqimiṣ-ṣalāta ṭarafayin-nahāri wa zulafam minal-laīl, innal-ḥasanāti yuż-hibnas-sayyi`āt, żālika żikrā liż-żākirīn","at":"Dan laksanakanlah salat pada kedua ujung siang (pagi dan petang) dan pada bagian permulaan malam. Perbuatan-perbuatan baik itu menghapus kesalahan-kesalahan. Itulah peringatan bagi orang-orang yang selalu mengingat (Allah)."},{"id":"115","ar":"وَاصْبِرْ فَاِنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ","bc":"waṣbir fa innallāha lā yuḍī'u ajral-muḥsinīn","at":"Dan bersabarlah, karena sesungguhnya Allah tidak menyia-nyiakan pahala orang yang berbuat kebaikan."},{"id":"116","ar":"فَلَوْلَا كَانَ مِنَ الْقُرُوْنِ مِنْ قَبْلِكُمْ اُولُوْا بَقِيَّةٍ يَّنْهَوْنَ عَنِ الْفَسَادِ فِى الْاَرْضِ اِلَّا قَلِيْلًا مِّمَّنْ اَنْجَيْنَا مِنْهُمْ ۚوَاتَّبَعَ الَّذِيْنَ ظَلَمُوْا مَآ اُتْرِفُوْا فِيْهِ وَكَانُوْا مُجْرِمِيْنَ","bc":"falau lā kāna minal-qurụni ming qablikum ulụ baqiyyatiy yan-hauna 'anil-fasādi fil-arḍi illā qalīlam mim man anjainā min-hum, wattaba'allażīna ẓalamụ mā utrifụ fīhi wa kānụ mujrimīn","at":"Maka mengapa tidak ada di antara umat-umat sebelum kamu orang yang mempunyai keutamaan yang melarang (berbuat) kerusakan di bumi, kecuali sebagian kecil di antara orang yang telah Kami selamatkan. Dan orang-orang yang zalim hanya mementingkan kenikmatan dan kemewahan. Dan mereka adalah orang-orang yang berdosa. "},{"id":"117","ar":"وَمَا كَانَ رَبُّكَ لِيُهْلِكَ الْقُرٰى بِظُلْمٍ وَّاَهْلُهَا مُصْلِحُوْنَ","bc":"wa mā kāna rabbuka liyuhlikal-qurā biẓulmiw wa ahluhā muṣliḥụn","at":"Dan Tuhanmu tidak akan membinasakan negeri-negeri secara zalim, selama penduduknya orang-orang yang berbuat kebaikan."},{"id":"118","ar":"وَلَوْ شَاۤءَ رَبُّكَ لَجَعَلَ النَّاسَ اُمَّةً وَّاحِدَةً وَّلَا يَزَالُوْنَ مُخْتَلِفِيْنَۙ ","bc":"walau syā`a rabbuka laja'alan-nāsa ummataw wāḥidataw wa lā yazālụna mukhtalifīn","at":"Dan jika Tuhanmu menghendaki, tentu Dia jadikan manusia umat yang satu, tetapi mereka senantiasa berselisih (pendapat),"},{"id":"119","ar":"اِلَّا مَنْ رَّحِمَ رَبُّكَ ۗوَلِذٰلِكَ خَلَقَهُمْ ۗوَتَمَّتْ كَلِمَةُ رَبِّكَ لَاَمْلَـَٔنَّ جَهَنَّمَ مِنَ الْجِنَّةِ وَالنَّاسِ اَجْمَعِيْنَ","bc":"illā mar raḥima rabbuk, wa liżālika khalaqahum, wa tammat kalimatu rabbika la`amla`anna jahannama minal-jinnati wan-nāsi ajma'īn","at":"kecuali orang yang diberi rahmat oleh Tuhanmu. Dan untuk itulah Allah menciptakan mereka. Kalimat (keputusan) Tuhanmu telah tetap, “Aku pasti akan memenuhi neraka Jahanam dengan jin dan manusia (yang durhaka) semuanya.”"},{"id":"120","ar":"وَكُلًّا نَّقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤءِ الرُّسُلِ مَا نُثَبِّتُ بِهٖ فُؤَادَكَ وَجَاۤءَكَ فِيْ هٰذِهِ الْحَقُّ وَمَوْعِظَةٌ وَّذِكْرٰى لِلْمُؤْمِنِيْنَ ","bc":"wa kullan naquṣṣu 'alaika min ambā`ir-rusuli mā nuṡabbitu bihī fu`ādaka wa jā`aka fī hāżihil-ḥaqqu wa mau'iẓatuw wa żikrā lil-mu`minīn","at":"Dan semua kisah rasul-rasul, Kami ceritakan kepadamu (Muhammad), agar dengan kisah itu Kami teguhkan hatimu; dan di dalamnya telah diberikan kepadamu (segala) kebenaran, nasihat dan peringatan bagi orang yang beriman."},{"id":"121","ar":"وَقُلْ لِّلَّذِيْنَ لَا يُؤْمِنُوْنَ اعْمَلُوْا عَلٰى مَكَانَتِكُمْۗ اِنَّا عٰمِلُوْنَۙ ","bc":"wa qul lillażīna lā yu`minụna'malụ 'alā makānatikum, innā 'āmilụn","at":"Dan katakanlah (Muhammad) kepada orang yang tidak beriman, “Berbuatlah menurut kedudukanmu; kami pun benar-benar akan berbuat,"},{"id":"122","ar":"وَانْتَظِرُوْاۚ اِنَّا مُنْتَظِرُوْنَ","bc":"wantaẓirụ, innā muntaẓirụn","at":"dan tunggulah, sesungguhnya kami pun termasuk yang menunggu.”"},{"id":"123","ar":"وَلِلّٰهِ غَيْبُ السَّمٰوٰتِ وَالْاَرْضِ وَاِلَيْهِ يُرْجَعُ الْاَمْرُ كُلُّهٗ فَاعْبُدْهُ وَتَوَكَّلْ عَلَيْهِۗ وَمَا رَبُّكَ بِغَافِلٍ عَمَّا تَعْمَلُوْنَ ","bc":"wa lillāhi gaibus-samāwāti wal-arḍi wa ilaihi yurja'ul-amru kulluhụ fa'bud-hu wa tawakkal 'alaīh, wa mā rabbuka bigāfilin 'ammā ta'malụn","at":"Dan milik Allah meliputi rahasia langit dan bumi dan kepada-Nya segala urusan dikembalikan. Maka sembahlah Dia dan bertawakallah kepada-Nya. Dan Tuhanmu tidak akan lengah terhadap apa yang kamu kerjakan."}],ibrahim=[{"id":"1","ar":"الۤرٰ ۗ كِتٰبٌ اَنْزَلْنٰهُ اِلَيْكَ لِتُخْرِجَ النَّاسَ مِنَ الظُّلُمٰتِ اِلَى النُّوْرِ ەۙ بِاِذْنِ رَبِّهِمْ اِلٰى صِرَاطِ الْعَزِيْزِ الْحَمِيْدِۙ","bc":"alif lām rā, kitābun anzalnāhu ilaika litukhrijan-nāsa minaẓ-ẓulumāti ilan-nụri bi`iżni rabbihim ilā ṣirāṭil-'azīzil-ḥamīd","at":"Alif Lam Ra. (Ini adalah) Kitab yang Kami turunkan kepadamu (Muhammad) agar engkau mengeluarkan manusia dari kegelapan kepada cahaya terang-benderang dengan izin Tuhan, (yaitu) menuju jalan Tuhan Yang Mahaperkasa, Maha Terpuji."},{"id":"2","ar":"اللّٰهِ الَّذِيْ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ وَوَيْلٌ لِّلْكٰفِرِيْنَ مِنْ عَذَابٍ شَدِيْدٍۙ ","bc":"allāhillażī lahụ mā fis-samāwāti wa mā fil-arḍ, wa wailul lil-kāfirīna min 'ażābin syadīd","at":"Allah yang memiliki apa yang ada di langit dan apa yang ada di bumi. Celakalah bagi orang yang ingkar kepada Tuhan karena siksaan yang sangat berat, "},{"id":"3","ar":" ۨالَّذِيْنَ يَسْتَحِبُّوْنَ الْحَيٰوةَ الدُّنْيَا عَلَى الْاٰخِرَةِ وَيَصُدُّوْنَ عَنْ سَبِيْلِ اللّٰهِ وَيَبْغُوْنَهَا عِوَجًا ۗ اُولٰۤىِٕكَ فِيْ ضَلٰلٍۢ بَعِيْدٍ","bc":"allażīna yastaḥibbụnal-ḥayātad-dun-yā 'alal-ākhirati wa yaṣuddụna 'an sabīlillāhi wa yabgụnahā 'iwajā, ulā`ika fī ḍalālim ba'īd","at":"(yaitu) orang yang lebih menyukai kehidupan dunia daripada (kehidupan) akhirat, dan menghalang-halangi (manusia) dari jalan Allah dan menginginkan (jalan yang) bengkok. Mereka itu berada dalam kesesatan yang jauh."},{"id":"4","ar":"وَمَآ اَرْسَلْنَا مِنْ رَّسُوْلٍ اِلَّا بِلِسَانِ قَوْمِهٖ لِيُبَيِّنَ لَهُمْ ۗفَيُضِلُّ اللّٰهُ مَنْ يَّشَاۤءُ وَيَهْدِيْ مَنْ يَّشَاۤءُ ۗوَهُوَ الْعَزِيْزُ الْحَكِيْمُ ","bc":"wa mā arsalnā mir rasụlin illā bilisāni qaumihī liyubayyina lahum, fa yuḍillullāhu may yasyā`u wa yahdī may yasyā`, wa huwal-'azīzul-ḥakīm","at":"Dan Kami tidak mengutus seorang rasul pun, melainkan dengan bahasa kaumnya, agar dia dapat memberi penjelasan kepada mereka. Maka Allah menyesatkan siapa yang Dia kehendaki, dan memberi petunjuk kepada siapa yang Dia kehendaki. Dia Yang Mahaperkasa, Mahabijaksana."},{"id":"5","ar":"وَلَقَدْ اَرْسَلْنَا مُوْسٰى بِاٰيٰتِنَآ اَنْ اَخْرِجْ قَوْمَكَ مِنَ الظُّلُمٰتِ اِلَى النُّوْرِ ەۙ وَذَكِّرْهُمْ بِاَيّٰىمِ اللّٰهِ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ","bc":"wa laqad arsalnā mụsā bi`āyātinā an akhrij qaumaka minaẓ-ẓulumāti ilan-nụri wa żakkir-hum bi`ayyāmillāh, inna fī żālika la`āyātil likulli ṣabbārin syakụr","at":"Dan sungguh, Kami telah mengutus Musa dengan membawa tanda-tanda (kekuasaan) Kami, (dan Kami perintahkan kepadanya), “Keluarkanlah kaummu dari kegelapan kepada cahaya terang-benderang dan ingatkanlah mereka kepada hari-hari Allah.” Sungguh, pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi setiap orang penyabar dan banyak bersyukur."},{"id":"6","ar":"وَاِذْ قَالَ مُوْسٰى لِقَوْمِهِ اذْكُرُوْا نِعْمَةَ اللّٰهِ عَلَيْكُمْ اِذْ اَنْجٰىكُمْ مِّنْ اٰلِ فِرْعَوْنَ يَسُوْمُوْنَكُمْ سُوْۤءَ الْعَذَابِ وَيُذَبِّحُوْنَ اَبْنَاۤءَكُمْ وَيَسْتَحْيُوْنَ نِسَاۤءَكُمْ ۗوَفِيْ ذٰلِكُمْ بَلَاۤءٌ مِّنْ رَّبِّكُمْ عَظِيْمٌ","bc":"wa iż qāla mụsā liqaumihiżkurụ ni'matallāhi 'alaikum iż anjākum min āli fir'auna yasụmụnakum sū`al-'ażābi wa yużabbiḥụna abnā`akum wa yastaḥyụna nisā`akum, wa fī żālikum balā`um mir rabbikum 'aẓīm","at":"Dan (ingatlah) ketika Musa berkata kepada kaumnya, “Ingatlah nikmat Allah atasmu ketika Dia menyelamatkan kamu dari pengikut-pengikut Fir‘aun; mereka menyiksa kamu dengan siksa yang pedih, dan menyembelih anak-anakmu yang laki-laki, dan membiarkan hidup anak-anak perempuanmu; pada yang demikian itu suatu cobaan yang besar dari Tuhanmu."},{"id":"7","ar":"وَاِذْ تَاَذَّنَ رَبُّكُمْ لَىِٕنْ شَكَرْتُمْ لَاَزِيْدَنَّكُمْ وَلَىِٕنْ كَفَرْتُمْ اِنَّ عَذَابِيْ لَشَدِيْدٌ ","bc":"wa iż ta`ażżana rabbukum la`in syakartum la`azīdannakum wa la`ing kafartum inna 'ażābī lasyadīd","at":"Dan (ingatlah) ketika Tuhanmu memaklumkan, “Sesungguhnya jika kamu bersyukur, niscaya Aku akan menambah (nikmat) kepadamu, tetapi jika kamu mengingkari (nikmat-Ku), maka pasti azab-Ku sangat berat.”"},{"id":"8","ar":"وَقَالَ مُوْسٰٓى اِنْ تَكْفُرُوْٓا اَنْتُمْ وَمَنْ فِى الْاَرْضِ جَمِيْعًا ۙفَاِنَّ اللّٰهَ لَغَنِيٌّ حَمِيْدٌ ","bc":"wa qāla mụsā in takfurū antum wa man fil-arḍi jamī'an fa innallāha laganiyyun ḥamīd","at":"Dan Musa berkata, “Jika kamu dan orang yang ada di bumi semuanya mengingkari (nikmat Allah), maka sesungguhnya Allah Mahakaya, Maha Terpuji."},{"id":"9","ar":"اَلَمْ يَأْتِكُمْ نَبَؤُا الَّذِيْنَ مِنْ قَبْلِكُمْ قَوْمِ نُوْحٍ وَّعَادٍ وَّثَمُوْدَ ەۗ وَالَّذِيْنَ مِنْۢ بَعْدِهِمْ ۗ لَا يَعْلَمُهُمْ اِلَّا اللّٰهُ ۗجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ فَرَدُّوْٓا اَيْدِيَهُمْ فِيْٓ اَفْوَاهِهِمْ وَقَالُوْٓا اِنَّا كَفَرْنَا بِمَآ اُرْسِلْتُمْ بِهٖ وَاِنَّا لَفِيْ شَكٍّ مِّمَّا تَدْعُوْنَنَآ اِلَيْهِ مُرِيْبٍ","bc":"a lam ya`tikum naba`ullażīna ming qablikum qaumi nụḥiw wa 'ādiw wa ṡamụd, wallażīna mim ba'dihim, lā ya'lamuhum illallāh, jā`at-hum rusuluhum bil-bayyināti fa raddū aidiyahum fī afwāhihim wa qālū innā kafarnā bimā ursiltum bihī wa innā lafī syakkim mimmā tad'ụnanā ilaihi murīb","at":"Apakah belum sampai kepadamu berita orang-orang sebelum kamu (yaitu) kaum Nuh, ‘Ad, samud dan orang-orang setelah mereka. Tidak ada yang mengetahui mereka selain Allah. Rasul-rasul telah datang kepada mereka membawa bukti-bukti (yang nyata), namun mereka menutupkan tangannya ke mulutnya (karena kebencian), dan berkata, “Sesungguhnya kami tidak percaya akan (bukti bahwa) kamu diutus (kepada kami), dan kami benar-benar dalam keraguan yang menggelisahkan terhadap apa yang kamu serukan kepada kami.”"},{"id":"10","ar":"۞ قَالَتْ رُسُلُهُمْ اَفِى اللّٰهِ شَكٌّ فَاطِرِ السَّمٰوٰتِ وَالْاَرْضِۗ يَدْعُوْكُمْ لِيَغْفِرَ لَكُمْ مِّنْ ذُنُوْبِكُمْ وَيُؤَخِّرَكُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۗ قَالُوْٓا اِنْ اَنْتُمْ اِلَّا بَشَرٌ مِّثْلُنَا ۗ تُرِيْدُوْنَ اَنْ تَصُدُّوْنَا عَمَّا كَانَ يَعْبُدُ اٰبَاۤؤُنَا فَأْتُوْنَا بِسُلْطٰنٍ مُّبِيْنٍ ","bc":"qālat rusuluhum a fillāhi syakkun fāṭiris-samāwāti wal-arḍ, yad'ụkum liyagfira lakum min żunụbikum wa yu`akhkhirakum ilā ajalim musammā, qālū in antum illā basyarum miṡlunā, turīdụna an taṣuddụnā 'ammā kāna ya'budu ābā`unā fa`tụnā bisulṭānim mubīn","at":"Rasul-rasul mereka berkata, “Apakah ada keraguan terhadap Allah, Pencipta langit dan bumi? Dia menyeru kamu (untuk beriman) agar Dia mengampuni sebagian dosa-dosamu dan menangguhkan (siksaan)mu sampai waktu yang ditentukan?” Mereka berkata, “Kamu hanyalah manusia seperti kami juga. Kamu ingin menghalangi kami (menyembah) apa yang dari dahulu disembah nenek moyang kami, karena itu datangkanlah kepada kami bukti yang nyata.”"},{"id":"11","ar":"قَالَتْ لَهُمْ رُسُلُهُمْ اِنْ نَّحْنُ اِلَّا بَشَرٌ مِّثْلُكُمْ وَلٰكِنَّ اللّٰهَ يَمُنُّ عَلٰى مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖۗ وَمَا كَانَ لَنَآ اَنْ نَّأْتِيَكُمْ بِسُلْطٰنٍ اِلَّا بِاِذْنِ اللّٰهِ ۗوَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُؤْمِنُوْنَ ","bc":"qālat lahum rusuluhum in naḥnu illā basyarum miṡlukum wa lākinnallāha yamunnu 'alā may yasyā`u min 'ibādih, wa mā kāna lanā an na`tiyakum bisulṭānin illā bi`iżnillāh, wa 'alallāhi falyatawakkalil-mu`minụn","at":"Rasul-rasul mereka berkata kepada mereka, “Kami hanyalah manusia seperti kamu, tetapi Allah memberi karunia kepada siapa yang Dia kehendaki di antara hamba-hamba-Nya. Tidak pantas bagi kami mendatangkan suatu bukti kepada kamu melainkan dengan izin Allah. Dan hanya kepada Allah saja hendaknya orang yang beriman bertawakal."},{"id":"12","ar":"وَمَا لَنَآ اَلَّا نَتَوَكَّلَ عَلَى اللّٰهِ وَقَدْ هَدٰىنَا سُبُلَنَاۗ وَلَنَصْبِرَنَّ عَلٰى مَآ اٰذَيْتُمُوْنَاۗ وَعَلَى اللّٰهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُوْنَ ","bc":"wa mā lanā allā natawakkala 'alallāhi wa qad hadānā subulanā, wa lanaṣbiranna 'alā mā āżaitumụnā, wa 'alallāhi falyatawakkalil-mutawakkilụn","at":"Dan mengapa kami tidak akan bertawakal kepada Allah, sedangkan Dia telah menunjukkan jalan kepada kami, dan kami sungguh, akan tetap bersabar terhadap gangguan yang kamu lakukan kepada kami. Dan hanya kepada Allah saja orang yang bertawakal berserah diri.”"},{"id":"13","ar":"وَقَالَ الَّذِيْنَ كَفَرُوْا لِرُسُلِهِمْ لَنُخْرِجَنَّكُمْ مِّنْ اَرْضِنَآ اَوْ لَتَعُوْدُنَّ فِيْ مِلَّتِنَاۗ فَاَوْحٰٓى اِلَيْهِمْ رَبُّهُمْ لَنُهْلِكَنَّ الظّٰلِمِيْنَ ۗ ","bc":"wa qālallażīna kafarụ lirusulihim lanukhrijannakum min arḍinā au lata'ụdunna fī millatinā, fa auḥā ilaihim rabbuhum lanuhlikannaẓ-ẓālimīn","at":"Dan orang-orang kafir berkata kepada rasul-rasul mereka, “Kami pasti akan mengusir kamu dari negeri kami atau kamu benar-benar kembali kepada agama kami.” Maka Tuhan mewahyukan kepada mereka, “Kami pasti akan membinasakan orang yang zalim itu."},{"id":"14","ar":"وَلَنُسْكِنَنَّكُمُ الْاَرْضَ مِنْۢ بَعْدِهِمْ ۗذٰلِكَ لِمَنْ خَافَ مَقَامِيْ وَخَافَ وَعِيْدِ ","bc":"wa lanuskinannakumul-arḍa mim ba'dihim, żālika liman khāfa maqāmī wa khāfa wa'īd","at":"Dan Kami pasti akan menempatkan kamu di negeri-negeri itu setelah mereka. Yang demikian itu (adalah untuk) orang-orang yang takut (menghadap) ke hadirat-Ku dan takut akan ancaman-Ku.”"},{"id":"15","ar":"وَاسْتَفْتَحُوْا وَخَابَ كُلُّ جَبَّارٍ عَنِيْدٍۙ ","bc":"wastaftaḥụ wa khāba kullu jabbārin 'anīd","at":"Dan mereka memohon diberi kemenangan dan binasalah semua orang yang berlaku sewenang-wenang lagi keras kepala, "},{"id":"16","ar":"مِّنْ وَّرَاۤىِٕهٖ جَهَنَّمُ وَيُسْقٰى مِنْ مَّاۤءٍ صَدِيْدٍۙ ","bc":"miw warā`ihī jahannamu wa yusqā mim mā`in ṣadīd","at":"di hadapannya ada neraka Jahanam dan dia akan diberi minuman dengan air nanah,"},{"id":"17","ar":"يَّتَجَرَّعُهٗ وَلَا يَكَادُ يُسِيْغُهٗ وَيَأْتِيْهِ الْمَوْتُ مِنْ كُلِّ مَكَانٍ وَّمَا هُوَ بِمَيِّتٍۗ وَمِنْ وَّرَاۤىِٕهٖ عَذَابٌ غَلِيْظٌ ","bc":"yatajarra'uhụ wa lā yakādu yusīguhụ wa ya`tīhil-mautu ming kulli makāniw wa mā huwa bimayyit, wa miw warā`ihī 'ażābun galīẓ","at":"diteguk-teguknya (air nanah itu) dan dia hampir tidak bisa menelannya dan datanglah (bahaya) maut kepadanya dari segenap penjuru, tetapi dia tidak juga mati; dan di hadapannya (masih ada) azab yang berat."},{"id":"18","ar":"مَثَلُ الَّذِيْنَ كَفَرُوْا بِرَبِّهِمْ اَعْمَالُهُمْ كَرَمَادِ ِۨاشْتَدَّتْ بِهِ الرِّيْحُ فِيْ يَوْمٍ عَاصِفٍۗ لَا يَقْدِرُوْنَ مِمَّا كَسَبُوْا عَلٰى شَيْءٍ ۗذٰلِكَ هُوَ الضَّلٰلُ الْبَعِيْدُ ","bc":"maṡalullażīna kafarụ birabbihim a'māluhum karamādinisytaddat bihir-rīḥu fī yaumin 'āṣif, lā yaqdirụna mimmā kasabụ 'alā syaī`, żālika huwaḍ-ḍalālul-ba'īd","at":"Perumpamaan orang yang ingkar kepada Tuhannya, perbuatan mereka seperti abu yang ditiup oleh angin keras pada suatu hari yang berangin kencang. Mereka tidak kuasa (mendatangkan manfaat) sama sekali dari apa yang telah mereka usahakan (di dunia). Yang demikian itu adalah kesesatan yang jauh."},{"id":"19","ar":"اَلَمْ تَرَ اَنَّ اللّٰهَ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِالْحَقِّۗ اِنْ يَّشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيْدٍۙ ","bc":"a lam tara annallāha khalaqas-samāwāti wal-arḍa bil-ḥaqq, iy yasya` yuż-hibkum wa ya`ti bikhalqin jadīd","at":"Tidakkah kamu memperhatikan, bahwa sesungguhnya Allah telah menciptakan langit dan bumi dengan hak (benar)? Jika Dia menghendaki, niscaya Dia membinasakan kamu dan mendatangkan makhluk yang baru (untuk menggantikan kamu),"},{"id":"20","ar":"وَّمَا ذٰلِكَ عَلَى اللّٰهِ بِعَزِيْزٍ","bc":"wa mā żālika 'alallāhi bi'azīz","at":"dan yang demikian itu tidak sukar bagi Allah."},{"id":"21","ar":"وَبَرَزُوْا لِلّٰهِ جَمِيْعًا فَقَالَ الضُّعَفٰۤؤُا لِلَّذِيْنَ اسْتَكْبَرُوْٓا اِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ اَنْتُمْ مُّغْنُوْنَ عَنَّا مِنْ عَذَابِ اللّٰهِ مِنْ شَيْءٍ ۗقَالُوْا لَوْ هَدٰىنَا اللّٰهُ لَهَدَيْنٰكُمْۗ سَوَاۤءٌ عَلَيْنَآ اَجَزِعْنَآ اَمْ صَبَرْنَا مَا لَنَا مِنْ مَّحِيْصٍ ","bc":"wa barazụ lillāhi jamī'an fa qālaḍ-ḍu'afā`u lillażīnastakbarū innā kunnā lakum taba'an fa hal antum mugnụna 'annā min 'ażābillāhi min syaī`, qālụ lau hadānallāhu lahadainākum, sawā`un 'alainā ajazi'nā am ṣabarnā mā lanā mim maḥīṣ","at":"Dan mereka semua (di padang Mahsyar) berkumpul untuk menghadap ke hadirat Allah, lalu orang yang lemah berkata kepada orang yang sombong, “Sesungguhnya kami dahulu adalah pengikut-pengikutmu, maka dapatkah kamu menghindarkan kami dari azab Allah (walaupun) sedikit saja?” Mereka menjawab, “Sekiranya Allah memberi petunjuk kepada kami, niscaya kami dapat memberi petunjuk kepadamu. Sama saja bagi kita, apakah kita mengeluh atau bersabar. Kita tidak mempunyai tempat untuk melarikan diri.”"},{"id":"22","ar":"وَقَالَ الشَّيْطٰنُ لَمَّا قُضِيَ الْاَمْرُ اِنَّ اللّٰهَ وَعَدَكُمْ وَعْدَ الْحَقِّ وَوَعَدْتُّكُمْ فَاَخْلَفْتُكُمْۗ وَمَا كَانَ لِيَ عَلَيْكُمْ مِّنْ سُلْطٰنٍ اِلَّآ اَنْ دَعَوْتُكُمْ فَاسْتَجَبْتُمْ لِيْ ۚفَلَا تَلُوْمُوْنِيْ وَلُوْمُوْٓا اَنْفُسَكُمْۗ مَآ اَنَا۠ بِمُصْرِخِكُمْ وَمَآ اَنْتُمْ بِمُصْرِخِيَّۗ اِنِّيْ كَفَرْتُ بِمَآ اَشْرَكْتُمُوْنِ مِنْ قَبْلُ ۗاِنَّ الظّٰلِمِيْنَ لَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"wa qālasy-syaiṭānu lammā quḍiyal-amru innallāha wa'adakum wa'dal-ḥaqqi wa wa'attukum fa akhlaftukum, wa mā kāna liya 'alaikum min sulṭānin illā an da'autukum fastajabtum lī, fa lā talụmụnī wa lụmū anfusakum, mā ana bimuṣrikhikum wa mā antum bimuṣrikhiyy, innī kafartu bimā asyraktumụni ming qabl, innaẓ-ẓālimīna lahum 'ażābun alīm","at":"Dan setan berkata ketika perkara (hisab) telah diselesaikan, “Sesungguhnya Allah telah menjanjikan kepadamu janji yang benar, dan aku pun telah menjanjikan kepadamu tetapi aku menyalahinya. Tidak ada kekuasaan bagiku terhadapmu, melainkan (sekedar) aku menyeru kamu lalu kamu mematuhi seruanku, oleh sebab itu janganlah kamu mencerca aku, tetapi cercalah dirimu sendiri. Aku tidak dapat menolongmu, dan kamu pun tidak dapat menolongku. Sesungguhnya aku tidak membenarkan perbuatanmu mempersekutukan aku (dengan Allah) sejak dahulu.” Sungguh, orang yang zalim akan mendapat siksaan yang pedih."},{"id":"23","ar":"وَاُدْخِلَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا بِاِذْنِ رَبِّهِمْۗ تَحِيَّتُهُمْ فِيْهَا سَلٰمٌ ","bc":"wa udkhilallażīna āmanụ wa 'amiluṣ-ṣāliḥāti jannātin tajrī min taḥtihal-an-hāru khālidīna fīhā bi`iżni rabbihim, taḥiyyatuhum fīhā salām","at":"Dan orang yang beriman dan mengerjakan kebajikan dimasukkan ke dalam surga-surga yang mengalir di bawahnya sungai-sungai. Mereka kekal di dalamnya dengan seizin Tuhan mereka. Ucapan penghormatan mereka dalam (surga) itu ialah salam."},{"id":"24","ar":"اَلَمْ تَرَ كَيْفَ ضَرَبَ اللّٰهُ مَثَلًا كَلِمَةً طَيِّبَةً كَشَجَرَةٍ طَيِّبَةٍ اَصْلُهَا ثَابِتٌ وَّفَرْعُهَا فِى السَّمَاۤءِۙ ","bc":"a lam tara kaifa ḍaraballāhu maṡalang kalimatan ṭayyibatang kasyajaratin ṭayyibatin aṣluhā ṡābituw wa far'uhā fis-samā`","at":"Tidakkah kamu memperhatikan bagai-mana Allah telah membuat perumpamaan kalimat yang baik seperti pohon yang baik, akarnya kuat dan cabangnya (menjulang) ke langit,"},{"id":"25","ar":"تُؤْتِيْٓ اُكُلَهَا كُلَّ حِيْنٍ ۢبِاِذْنِ رَبِّهَاۗ وَيَضْرِبُ اللّٰهُ الْاَمْثَالَ لِلنَّاسِ لَعَلَّهُمْ يَتَذَكَّرُوْنَ ","bc":"tu`tī ukulahā kulla ḥīnim bi`iżni rabbihā, wa yaḍribullāhul-amṡāla lin-nāsi la'allahum yatażakkarụn","at":"(pohon) itu menghasilkan buahnya pada setiap waktu dengan seizin Tuhannya. Dan Allah membuat perumpamaan itu untuk manusia agar mereka selalu ingat."},{"id":"26","ar":"وَمَثَلُ كَلِمَةٍ خَبِيْثَةٍ كَشَجَرَةٍ خَبِيْثَةِ ِۨاجْتُثَّتْ مِنْ فَوْقِ الْاَرْضِ مَا لَهَا مِنْ قَرَارٍ ","bc":"wa maṡalu kalimatin khabīṡating kasyajaratin khabīṡatinijtuṡṡat min fauqil-arḍi mā lahā ming qarār","at":"Dan perumpamaan kalimat yang buruk seperti pohon yang buruk, yang telah dicabut akar-akarnya dari permukaan bumi; tidak dapat tetap (tegak) sedikit pun."},{"id":"27","ar":"يُثَبِّتُ اللّٰهُ الَّذِيْنَ اٰمَنُوْا بِالْقَوْلِ الثَّابِتِ فِى الْحَيٰوةِ الدُّنْيَا وَفِى الْاٰخِرَةِۚ وَيُضِلُّ اللّٰهُ الظّٰلِمِيْنَۗ وَيَفْعَلُ اللّٰهُ مَا يَشَاۤءُ ","bc":"yuṡabbitullāhullażīna āmanụ bil-qauliṡ-ṡābiti fil-ḥayātid-dun-yā wa fil-ākhirah, wa yuḍillullāhuẓ-ẓālimīn, wa yaf'alullāhu mā yasyā`","at":" Allah meneguhkan (iman) orang-orang yang beriman dengan ucapan yang teguh (dalam kehidupan) di dunia dan di akhirat; dan Allah menyesatkan orang-orang yang zalim dan Allah berbuat apa yang Dia kehendaki."},{"id":"28","ar":"۞ اَلَمْ تَرَ اِلَى الَّذِيْنَ بَدَّلُوْا نِعْمَتَ اللّٰهِ كُفْرًا وَّاَحَلُّوْا قَوْمَهُمْ دَارَ الْبَوَارِۙ ","bc":"a lam tara ilallażīna baddalụ ni'matallāhi kufraw wa aḥallụ qaumahum dāral-bawār","at":"Tidakkah kamu memperhatikan orang-orang yang telah menukar nikmat Allah dengan ingkar kepada Allah dan menjatuhkan kaumnya ke lembah kebinasaan?"},{"id":"29","ar":"جَهَنَّمَ ۚيَصْلَوْنَهَاۗ وَبِئْسَ الْقَرَارُ ","bc":"jahannam, yaṣlaunahā, wa bi`sal-qarār","at":" yaitu neraka Jahanam; mereka masuk ke dalamnya; dan itulah seburuk-buruk tempat kediaman."},{"id":"30","ar":"وَجَعَلُوْا لِلّٰهِ اَنْدَادًا لِّيُضِلُّوْا عَنْ سَبِيْلِهٖۗ قُلْ تَمَتَّعُوْا فَاِنَّ مَصِيْرَكُمْ اِلَى النَّارِ ","bc":"wa ja'alụ lillāhi andādal liyuḍillụ 'an sabīlih, qul tamatta'ụ fa inna maṣīrakum ilan-nār","at":"Dan mereka (orang kafir) itu telah menjadikan tandingan bagi Allah untuk menyesatkan (manusia) dari jalan-Nya. Katakanlah (Muhammad), “Bersenang-senanglah kamu, karena sesungguhnya tempat kembalimu ke neraka.”"},{"id":"31","ar":"قُلْ لِّعِبَادِيَ الَّذِيْنَ اٰمَنُوْا يُقِيْمُوا الصَّلٰوةَ وَيُنْفِقُوْا مِمَّا رَزَقْنٰهُمْ سِرًّا وَّعَلَانِيَةً مِّنْ قَبْلِ اَنْ يَّأْتِيَ يَوْمٌ لَّا بَيْعٌ فِيْهِ وَلَا خِلٰلٌ ","bc":"qul li'ibādiyallażīna āmanụ yuqīmuṣ-ṣalāta wa yunfiqụ mimmā razaqnāhum sirraw wa 'alāniyatam ming qabli ay ya`tiya yaumul lā bai'un fīhi wa lā khilāl","at":"Katakanlah (Muhammad) kepada hamba-hamba-Ku yang telah beriman, “Hendaklah mereka melaksanakan salat, menginfakkan sebagian rezeki yang Kami berikan secara sembunyi atau terang-terangan sebelum datang hari, ketika tidak ada lagi jual beli dan persahabatan.” "},{"id":"32","ar":"اَللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ وَاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءً فَاَخْرَجَ بِهٖ مِنَ الثَّمَرٰتِ رِزْقًا لَّكُمْ ۚوَسَخَّرَ لَكُمُ الْفُلْكَ لِتَجْرِيَ فِى الْبَحْرِ بِاَمْرِهٖ ۚوَسَخَّرَ لَكُمُ الْاَنْهٰرَ ","bc":"allāhullażī khalaqas-samāwāti wal-arḍa wa anzala minas-samā`i mā`an fa akhraja bihī minaṡ-ṡamarāti rizqal lakum, wa sakhkhara lakumul-fulka litajriya fil-baḥri bi`amrih, wasakhkhara lakumul-an-hār","at":"Allah-lah yang telah menciptakan langit dan bumi dan menurunkan air (hujan) dari langit, kemudian dengan (air hujan) itu Dia mengeluarkan berbagai buah-buahan sebagai rezeki untukmu; dan Dia telah menundukkan kapal bagimu agar berlayar di lautan dengan kehendak-Nya, dan Dia telah menundukkan sungai-sungai bagimu. "},{"id":"33","ar":"وَسَخَّرَ لَكُمُ الشَّمْسَ وَالْقَمَرَ دَاۤىِٕبَيْنِۚ وَسَخَّرَ لَكُمُ الَّيْلَ وَالنَّهَارَ ۚ ","bc":"wa sakhkhara lakumusy-syamsa wal-qamara dā`ibaīn, wa sakhkhara lakumul-laila wan-nahār","at":"Dan Dia telah menundukkan matahari dan bulan bagimu yang terus-menerus beredar (dalam orbitnya); dan telah menundukkan malam dan siang bagimu."},{"id":"34","ar":"وَاٰتٰىكُمْ مِّنْ كُلِّ مَا سَاَلْتُمُوْهُۗ وَاِنْ تَعُدُّوْا نِعْمَتَ اللّٰهِ لَا تُحْصُوْهَاۗ اِنَّ الْاِنْسَانَ لَظَلُوْمٌ كَفَّارٌ ","bc":"wa ātākum ming kulli mā sa`altumụh, wa in ta'uddụ ni'matallāhi lā tuḥṣụhā, innal-insāna laẓalụmung kaffār","at":"Dan Dia telah memberikan kepadamu segala apa yang kamu mohonkan kepada-Nya. Dan jika kamu menghitung nikmat Allah, niscaya kamu tidak akan mampu menghitungnya. Sungguh, manusia itu sangat zalim dan sangat mengingkari (nikmat Allah)."},{"id":"35","ar":"وَاِذْ قَالَ اِبْرٰهِيْمُ رَبِّ اجْعَلْ هٰذَا الْبَلَدَ اٰمِنًا وَّاجْنُبْنِيْ وَبَنِيَّ اَنْ نَّعْبُدَ الْاَصْنَامَ ۗ ","bc":"wa iż qāla ibrāhīmu rabbij'al hāżal-balada āminaw wajnubnī wa baniyya an na'budal-aṣnām","at":"Dan (ingatlah), ketika Ibrahim berdoa, “Ya Tuhan, jadikanlah negeri ini (Mekah), negeri yang aman, dan jauhkanlah aku beserta anak cucuku agar tidak menyembah berhala."},{"id":"36","ar":"رَبِّ اِنَّهُنَّ اَضْلَلْنَ كَثِيْرًا مِّنَ النَّاسِۚ فَمَنْ تَبِعَنِيْ فَاِنَّهٗ مِنِّيْۚ وَمَنْ عَصَانِيْ فَاِنَّكَ غَفُوْرٌ رَّحِيْمٌ ","bc":"rabbi innahunna aḍlalna kaṡīram minan-nās, fa man tabi'anī fa innahụ minnī, wa man 'aṣānī fa innaka gafụrur raḥīm","at":"Ya Tuhan, berhala-berhala itu telah menyesatkan banyak dari manusia. Barangsiapa mengikutiku, maka orang itu termasuk golonganku, dan barang-siapa mendurhakaiku, maka Engkau Maha Pengampun, Maha Penyayang."},{"id":"37","ar":"رَبَّنَآ اِنِّيْٓ اَسْكَنْتُ مِنْ ذُرِّيَّتِيْ بِوَادٍ غَيْرِ ذِيْ زَرْعٍ عِنْدَ بَيْتِكَ الْمُحَرَّمِۙ رَبَّنَا لِيُقِيْمُوا الصَّلٰوةَ فَاجْعَلْ اَفْـِٕدَةً مِّنَ النَّاسِ تَهْوِيْٓ اِلَيْهِمْ وَارْزُقْهُمْ مِّنَ الثَّمَرٰتِ لَعَلَّهُمْ يَشْكُرُوْنَ","bc":"rabbanā innī askantu min żurriyyatī biwādin gairi żī zar'in 'inda baitikal-muḥarrami rabbanā liyuqīmuṣ-ṣalāta faj'al af`idatam minan-nāsi tahwī ilaihim warzuq-hum minaṡ-ṡamarāti la'allahum yasykurụn","at":"Ya Tuhan, sesungguhnya aku telah menempatkan sebagian keturunanku di lembah yang tidak mempunyai tanam-tanaman di dekat rumah Engkau (Baitullah) yang dihormati, ya Tuhan (yang demikian itu) agar mereka melaksanakan salat, maka jadikanlah hati sebagian manusia cenderung kepada mereka dan berilah mereka rezeki dari buah-buahan, mudah-mudahan mereka bersyukur."},{"id":"38","ar":"رَبَّنَآ اِنَّكَ تَعْلَمُ مَا نُخْفِيْ وَمَا نُعْلِنُۗ وَمَا يَخْفٰى عَلَى اللّٰهِ مِنْ شَيْءٍ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ","bc":"rabbanā innaka ta'lamu mā nukhfī wa mā nu'lin, wa mā yakhfā 'alallāhi min syai`in fil-arḍi wa lā fis-samā`","at":"Ya Tuhan kami, sesungguhnya Engkau mengetahui apa yang kami sembunyikan dan apa yang kami tampakkan; dan tidak ada sesuatu pun yang tersembunyi bagi Allah, baik yang ada di bumi maupun yang ada di langit."},{"id":"39","ar":"اَلْحَمْدُ لِلّٰهِ الَّذِيْ وَهَبَ لِيْ عَلَى الْكِبَرِ اِسْمٰعِيْلَ وَاِسْحٰقَۗ اِنَّ رَبِّيْ لَسَمِيْعُ الدُّعَاۤءِ ","bc":"al-ḥamdu lillāhillażī wahaba lī 'alal-kibari ismā'īla wa is-ḥāq, inna rabbī lasamī'ud-du'ā`","at":"Segala puji bagi Allah yang telah menganugerahkan kepadaku di hari tua(ku) Ismail dan Ishak. Sungguh, Tuhanku benar-benar Maha Mendengar (memperkenankan) doa."},{"id":"40","ar":"رَبِّ اجْعَلْنِيْ مُقِيْمَ الصَّلٰوةِ وَمِنْ ذُرِّيَّتِيْۖ رَبَّنَا وَتَقَبَّلْ دُعَاۤءِ ","bc":"rabbij'alnī muqīmaṣ-ṣalāti wa min żurriyyatī rabbanā wa taqabbal du'ā`","at":" Ya Tuhanku, jadikanlah aku dan anak cucuku orang yang tetap melaksanakan salat, ya Tuhan kami, perkenankanlah doaku."},{"id":"41","ar":"رَبَّنَا اغْفِرْ لِيْ وَلِوَالِدَيَّ وَلِلْمُؤْمِنِيْنَ يَوْمَ يَقُوْمُ الْحِسَابُ ","bc":"rabbanagfir lī wa liwālidayya wa lil-mu`minīna yauma yaqụmul-ḥisāb","at":" Ya Tuhan kami, ampunilah aku dan kedua ibu bapakku dan semua orang yang beriman pada hari diadakan perhitungan (hari Kiamat).”"},{"id":"42","ar":"وَلَا تَحْسَبَنَّ اللّٰهَ غَافِلًا عَمَّا يَعْمَلُ الظّٰلِمُوْنَ ەۗ اِنَّمَا يُؤَخِّرُهُمْ لِيَوْمٍ تَشْخَصُ فِيْهِ الْاَبْصَارُۙ","bc":"wa lā taḥsabannallāha gāfilan 'ammā ya'maluẓ-ẓālimụn, innamā yu`akhkhiruhum liyaumin tasykhaṣu fīhil-abṣār","at":"Dan janganlah engkau mengira, bahwa Allah lengah dari apa yang diperbuat oleh orang yang zalim. Sesungguhnya Allah menangguhkan mereka sampai hari yang pada waktu itu mata (mereka) terbelalak,"},{"id":"43","ar":"مُهْطِعِيْنَ مُقْنِعِيْ رُءُوْسِهِمْ لَا يَرْتَدُّ اِلَيْهِمْ طَرْفُهُمْ ۚوَاَفْـِٕدَتُهُمْ هَوَاۤءٌ ۗ ","bc":"muhṭi'īna muqni'ī ru`ụsihim lā yartaddu ilaihim ṭarfuhum, wa af`idatuhum hawā`","at":"mereka datang tergesa-gesa (memenuhi panggilan) dengan mengangkat kepalanya, sedang mata mereka tidak berkedip-kedip dan hati mereka kosong. "},{"id":"44","ar":"وَاَنْذِرِ النَّاسَ يَوْمَ يَأْتِيْهِمُ الْعَذَابُۙ فَيَقُوْلُ الَّذِيْنَ ظَلَمُوْا رَبَّنَآ اَخِّرْنَآ اِلٰٓى اَجَلٍ قَرِيْبٍۙ نُّجِبْ دَعْوَتَكَ وَنَتَّبِعِ الرُّسُلَۗ اَوَلَمْ تَكُوْنُوْٓا اَقْسَمْتُمْ مِّنْ قَبْلُ مَا لَكُمْ مِّنْ زَوَالٍۙ ","bc":"wa anżirin-nāsa yauma ya`tīhimul-'ażābu fa yaqụlullażīna ẓalamụ rabbanā akhkhirnā ilā ajaling qarībin nujib da'wataka wa nattabi'ir-rusul, a wa lam takụnū aqsamtum ming qablu mā lakum min zawāl","at":"Dan berikanlah peringatan (Muhammad) kepada manusia pada hari (ketika) azab datang kepada mereka, maka orang yang zalim berkata, “Ya Tuhan kami, berilah kami kesempatan (kembali ke dunia) walaupun sebentar, niscaya kami akan mematuhi seruan Engkau dan akan mengikuti rasul-rasul.” (Kepada mereka dikatakan), “Bukankah dahulu (di dunia) kamu telah bersumpah bahwa sekali-kali kamu tidak akan binasa?"},{"id":"45","ar":"وَّسَكَنْتُمْ فِيْ مَسٰكِنِ الَّذِيْنَ ظَلَمُوْٓا اَنْفُسَهُمْ وَتَبَيَّنَ لَكُمْ كَيْفَ فَعَلْنَا بِهِمْ وَضَرَبْنَا لَكُمُ الْاَمْثَالَ ","bc":"wa sakantum fī masākinillażīna ẓalamū anfusahum wa tabayyana lakum kaifa fa'alnā bihim wa ḍarabnā lakumul-amṡāl","at":"Dan kamu telah tinggal di tempat orang yang menzalimi diri sendiri, dan telah nyata bagimu bagaimana Kami telah berbuat terhadap mereka dan telah Kami berikan kepadamu beberapa perumpamaan.”"},{"id":"46","ar":"وَقَدْ مَكَرُوْا مَكْرَهُمْ وَعِنْدَ اللّٰهِ مَكْرُهُمْۗ وَاِنْ كَانَ مَكْرُهُمْ لِتَزُوْلَ مِنْهُ الْجِبَالُ ","bc":"wa qad makarụ makrahum wa 'indallāhi makruhum, wa ing kāna makruhum litazụla min-hul-jibāl","at":"Dan sungguh, mereka telah membuat tipu daya padahal Allah (mengetahui dan akan membalas) tipu daya mereka. Dan sesungguhnya tipu daya mereka tidak mampu melenyapkan gunung-gunung."},{"id":"47","ar":"فَلَا تَحْسَبَنَّ اللّٰهَ مُخْلِفَ وَعْدِهٖ رُسُلَهٗ ۗاِنَّ اللّٰهَ عَزِيْزٌ ذُو انْتِقَامٍۗ ","bc":"fa lā taḥsabannallāha mukhlifa wa'dihī rusulah, innallāha 'azīzun żuntiqām","at":" Maka karena itu jangan sekali-kali kamu mengira bahwa Allah mengingkari janji-Nya kepada rasul-rasul-Nya. Sungguh, Allah Mahaperkasa dan mempunyai pembalasan."},{"id":"48","ar":"يَوْمَ تُبَدَّلُ الْاَرْضُ غَيْرَ الْاَرْضِ وَالسَّمٰوٰتُ وَبَرَزُوْا لِلّٰهِ الْوَاحِدِ الْقَهَّارِ ","bc":"yauma tubaddalul-arḍu gairal-arḍi was-samāwātu wa barazụ lillāhil-wāḥidil-qahhār","at":"(Yaitu) pada hari (ketika) bumi diganti dengan bumi yang lain dan (demikian pula) langit, dan mereka (manusia) berkumpul (di Padang Mahsyar) menghadap Allah Yang Maha Esa, Mahaperkasa. "},{"id":"49","ar":"وَتَرَى الْمُجْرِمِيْنَ يَوْمَىِٕذٍ مُّقَرَّنِيْنَ فِى الْاَصْفَادِۚ ","bc":"wa taral-mujrimīna yauma`iżim muqarranīna fil-aṣfād","at":"Dan pada hari itu engkau akan melihat orang yang berdosa bersama-sama diikat dengan belenggu."},{"id":"50","ar":"سَرَابِيْلُهُمْ مِّنْ قَطِرَانٍ وَّتَغْشٰى وُجُوْهَهُمُ النَّارُۙ ","bc":"sarābīluhum ming qaṭirāniw wa tagsyā wujụhahumun-nār","at":"Pakaian mereka dari cairan aspal, dan wajah mereka ditutup oleh api neraka,"},{"id":"51","ar":"لِيَجْزِيَ اللّٰهُ كُلَّ نَفْسٍ مَّا كَسَبَتْۗ اِنَّ اللّٰهَ سَرِيْعُ الْحِسَابِ ","bc":"liyajziyallāhu kulla nafsim mā kasabat, innallāha sarī'ul-ḥisāb","at":"agar Allah memberi balasan kepada setiap orang terhadap apa yang dia usahakan. Sungguh, Allah Mahacepat perhitungan-Nya. "},{"id":"52","ar":"هٰذَا بَلٰغٌ لِّلنَّاسِ وَلِيُنْذَرُوْا بِهٖ وَلِيَعْلَمُوْٓا اَنَّمَا هُوَ اِلٰهٌ وَّاحِدٌ وَّلِيَذَّكَّرَ اُولُوا الْاَلْبَابِ ","bc":"hāżā balāgul lin-nāsi wa liyunżarụ bihī wa liya'lamū annamā huwa ilāhuw wāḥiduw wa liyażżakkara ulul-albāb","at":"Dan (Al-Qur'an) ini adalah penjelasan (yang sempurna) bagi manusia, agar mereka diberi peringatan dengannya, agar mereka mengetahui bahwa Dia adalah Tuhan Yang Maha Esa dan agar orang yang berakal mengambil pelajaran."}],luqman=[{"id":"1","ar":" الۤمّۤ ۗ ","bc":"alif lām mīm","at":"Alif Lam Mim."},{"id":"2","ar":"تِلْكَ اٰيٰتُ الْكِتٰبِ الْحَكِيْمِۙ ","bc":"tilka āyātul-kitābil-ḥakīm","at":"Inilah ayat-ayat Al-Qur'an yang mengandung hikmah, "},{"id":"3","ar":"هُدًى وَّرَحْمَةً لِّلْمُحْسِنِيْنَۙ ","bc":"hudaw wa raḥmatal lil-muḥsinīn","at":"sebagai petunjuk dan rahmat bagi orang-orang yang berbuat kebaikan, "},{"id":"4","ar":"الَّذِيْنَ يُقِيْمُوْنَ الصَّلٰوةَ وَيُؤْتُوْنَ الزَّكٰوةَ وَهُمْ بِالْاٰخِرَةِ هُمْ يُوْقِنُوْنَۗ ","bc":"allażīna yuqīmụnaṣ-ṣalāta wa yu`tụnaz-zakāta wa hum bil-ākhirati hum yụqinụn","at":"(yaitu) orang-orang yang melaksanakan salat, menunaikan zakat dan mereka meyakini adanya akhirat. "},{"id":"5","ar":"اُولٰۤىِٕكَ عَلٰى هُدًى مِّنْ رَّبِّهِمْ وَاُولٰۤىِٕكَ هُمُ الْمُفْلِحُوْنَ","bc":"ulā`ika 'alā hudam mir rabbihim wa ulā`ika humul-mufliḥụn","at":"Merekalah orang-orang yang tetap mendapat petunjuk dari Tuhannya dan mereka itulah orang-orang yang beruntung. "},{"id":"6","ar":"وَمِنَ النَّاسِ مَنْ يَّشْتَرِيْ لَهْوَ الْحَدِيْثِ لِيُضِلَّ عَنْ سَبِيْلِ اللّٰهِ بِغَيْرِ عِلْمٍۖ وَّيَتَّخِذَهَا هُزُوًاۗ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ مُّهِيْنٌ","bc":"wa minan-nāsi may yasytarī lahwal-ḥadīṡi liyuḍilla 'an sabīlillāhi bigairi 'ilmiw wa yattakhiżahā huzuwā, ulā`ika lahum 'ażābum muhīn","at":"Dan di antara manusia (ada) orang yang mempergunakan percakapan kosong untuk menyesatkan (manusia) dari jalan Allah tanpa ilmu dan menjadikannya olok-olokan. Mereka itu akan memperoleh azab yang menghinakan."},{"id":"7","ar":"وَاِذَا تُتْلٰى عَلَيْهِ اٰيٰتُنَا وَلّٰى مُسْتَكْبِرًا كَاَنْ لَّمْ يَسْمَعْهَا كَاَنَّ فِيْٓ اُذُنَيْهِ وَقْرًاۚ فَبَشِّرْهُ بِعَذَابٍ اَلِيْمٍ","bc":"wa iżā tutlā 'alaihi āyātunā wallā mustakbirang ka`al lam yasma'hā ka`anna fī użunaihi waqrā, fa basysyir-hu bi'ażābin alīm","at":"Dan apabila dibacakan kepadanya ayat-ayat Kami, dia berpaling dengan menyombongkan diri seolah-olah dia belum mendengarnya, seakan-akan ada sumbatan di kedua telinganya, maka gembirakanlah dia dengan azab yang pedih."},{"id":"8","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ لَهُمْ جَنّٰتُ النَّعِيْمِۙ","bc":"innallażīna āmanu wa 'amiluṣ-ṣāliḥāti lahum jannātun na'īm","at":"Sesungguhnya orang-orang yang beriman dan mengerjakan kebajikan, mereka akan mendapat surga-surga yang penuh kenikmatan, "},{"id":"9","ar":"خٰلِدِيْنَ فِيْهَاۗ وَعْدَ اللّٰهِ حَقًّاۗ وَهُوَ الْعَزِيْزُ الْحَكِيْمُ","bc":"khālidīna fīhā, wa'dallāhi ḥaqqā, wa huwal-'azīzul-ḥakīm","at":"mereka kekal di dalamnya, sebagai janji Allah yang benar. Dan Dia Mahaperkasa, Mahabijaksana. "},{"id":"10","ar":"خَلَقَ السَّمٰوٰتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا وَاَلْقٰى فِى الْاَرْضِ رَوَاسِيَ اَنْ تَمِيْدَ بِكُمْ وَبَثَّ فِيْهَا مِنْ كُلِّ دَاۤبَّةٍۗ وَاَنْزَلْنَا مِنَ السَّمَاۤءِ مَاۤءً فَاَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ زَوْجٍ كَرِيْمٍ ","bc":"khalaqas-samāwāti bigairi 'amadin taraunahā wa alqā fil-arḍi rawāsiya an tamīda bikum wa baṡṡa fīhā ming kulli dābbah, wa anzalnā minas-samā`i mā`an fa ambatnā fīhā ming kulli zaujing karīm","at":"Dia menciptakan langit tanpa tiang sebagaimana kamu melihatnya, dan Dia meletakkan gunung-gunung (di permukaan) bumi agar ia (bumi) tidak menggoyangkan kamu; dan memperkembangbiakkan segala macam jenis makhluk bergerak yang bernyawa di bumi. Dan Kami turunkan air hujan dari langit, lalu Kami tumbuhkan padanya segala macam tumbuh-tumbuhan yang baik. "},{"id":"11","ar":"هٰذَا خَلْقُ اللّٰهِ فَاَرُوْنِيْ مَاذَا خَلَقَ الَّذِيْنَ مِنْ دُوْنِهٖۗ بَلِ الظّٰلِمُوْنَ فِيْ ضَلٰلٍ مُّبِيْنٍ","bc":"hāżā khalqullāhi fa arụnī māżā khalaqallażīna min dụnih, baliẓ-ẓālimụna fī ḍalālim mubīn","at":"Inilah ciptaan Allah, maka perlihatkanlah olehmu kepadaku apa yang telah diciptakan oleh (sesembahanmu) selain Allah. Sebenarnya orang-orang yang zalim itu berada di dalam kesesatan yang nyata. "},{"id":"12","ar":"وَلَقَدْ اٰتَيْنَا لُقْمٰنَ الْحِكْمَةَ اَنِ اشْكُرْ لِلّٰهِ ۗوَمَنْ يَّشْكُرْ فَاِنَّمَا يَشْكُرُ لِنَفْسِهٖۚ وَمَنْ كَفَرَ فَاِنَّ اللّٰهَ غَنِيٌّ حَمِيْدٌ ","bc":"wa laqad ātainā luqmānal-ḥikmata anisykur lillāh, wa may yasykur fa innamā yasykuru linafsih, wa mang kafara fa innallāha ganiyyun ḥamīd","at":"Dan sungguh, telah Kami berikan hikmah kepada Lukman, yaitu, ”Bersyukurlah kepada Allah! Dan barangsiapa bersyukur (kepada Allah), maka sesungguhnya dia bersyukur untuk dirinya sendiri; dan barangsiapa tidak bersyukur (kufur), maka sesungguhnya Allah Mahakaya, Maha Terpuji.” "},{"id":"13","ar":"وَاِذْ قَالَ لُقْمٰنُ لِابْنِهٖ وَهُوَ يَعِظُهٗ يٰبُنَيَّ لَا تُشْرِكْ بِاللّٰهِ ۗاِنَّ الشِّرْكَ لَظُلْمٌ عَظِيْمٌ ","bc":"wa iż qāla luqmānu libnihī wa huwa ya'iẓuhụ yā bunayya lā tusyrik billāh, innasy-syirka laẓulmun 'aẓīm","at":"Dan (ingatlah) ketika Lukman berkata kepada anaknya, ketika dia memberi pelajaran kepadanya, ”Wahai anakku! Janganlah engkau mempersekutukan Allah, sesungguhnya mempersekutukan (Allah) adalah benar-benar kezaliman yang besar.” "},{"id":"14","ar":"وَوَصَّيْنَا الْاِنْسَانَ بِوَالِدَيْهِۚ حَمَلَتْهُ اُمُّهٗ وَهْنًا عَلٰى وَهْنٍ وَّفِصَالُهٗ فِيْ عَامَيْنِ اَنِ اشْكُرْ لِيْ وَلِوَالِدَيْكَۗ اِلَيَّ الْمَصِيْرُ","bc":"wa waṣṣainal-insāna biwālidaīh, ḥamalat-hu ummuhụ wahnan 'alā wahniw wa fiṣāluhụ fī 'āmaini anisykur lī wa liwālidaīk, ilayyal-maṣīr","at":"Dan Kami perintahkan kepada manusia (agar berbuat baik) kepada kedua orang tuanya. Ibunya telah mengandungnya dalam keadaan lemah yang bertambah-tambah, dan menyapihnya dalam usia dua tahun. Bersyukurlah kepada-Ku dan kepada kedua orang tuamu. Hanya kepada Aku kembalimu. "},{"id":"15","ar":"وَاِنْ جَاهَدٰكَ عَلٰٓى اَنْ تُشْرِكَ بِيْ مَا لَيْسَ لَكَ بِهٖ عِلْمٌ فَلَا تُطِعْهُمَا وَصَاحِبْهُمَا فِى الدُّنْيَا مَعْرُوْفًا ۖوَّاتَّبِعْ سَبِيْلَ مَنْ اَنَابَ اِلَيَّۚ ثُمَّ اِلَيَّ مَرْجِعُكُمْ فَاُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ","bc":"wa in jāhadāka 'alā an tusyrika bī mā laisa laka bihī 'ilmun fa lā tuṭi'humā wa ṣāḥib-humā fid-dun-yā ma'rụfaw wattabi' sabīla man anāba ilayy, ṡumma ilayya marji'ukum fa unabbi`ukum bimā kuntum ta'malụn","at":"Dan jika keduanya memaksamu untuk mempersekutukan Aku dengan sesuatu yang engkau tidak mempunyai ilmu tentang itu, maka janganlah engkau menaati keduanya, dan pergaulilah keduanya di dunia dengan baik, dan ikutilah jalan orang yang kembali kepada-Ku. Kemudian hanya kepada-Ku tempat kembalimu, maka akan Aku beritahukan kepadamu apa yang telah kamu kerjakan. "},{"id":"16","ar":"يٰبُنَيَّ اِنَّهَآ اِنْ تَكُ مِثْقَالَ حَبَّةٍ مِّنْ خَرْدَلٍ فَتَكُنْ فِيْ صَخْرَةٍ اَوْ فِى السَّمٰوٰتِ اَوْ فِى الْاَرْضِ يَأْتِ بِهَا اللّٰهُ ۗاِنَّ اللّٰهَ لَطِيْفٌ خَبِيْرٌ","bc":"yā bunayya innahā in taku miṡqāla ḥabbatim min khardalin fa takun fī ṣakhratin au fis-samāwāti au fil-arḍi ya`ti bihallāh, innallāha laṭīfun khabīr","at":"(Lukman berkata), ”Wahai anakku! Sungguh, jika ada (sesuatu perbuatan) seberat biji sawi, dan berada dalam batu atau di langit atau di bumi, niscaya Allah akan memberinya (balasan). Sesungguhnya Allah Mahahalus, Mahateliti. "},{"id":"17","ar":"يٰبُنَيَّ اَقِمِ الصَّلٰوةَ وَأْمُرْ بِالْمَعْرُوْفِ وَانْهَ عَنِ الْمُنْكَرِ وَاصْبِرْ عَلٰى مَآ اَصَابَكَۗ اِنَّ ذٰلِكَ مِنْ عَزْمِ الْاُمُوْرِ","bc":"yā bunayya aqimiṣ-ṣalāta wa`mur bil-ma'rụfi wan-ha 'anil-mungkari waṣbir 'alā mā aṣābak, inna żālika min 'azmil-umụr","at":"Wahai anakku! Laksanakanlah salat dan suruhlah (manusia) berbuat yang makruf dan cegahlah (mereka) dari yang mungkar dan bersabarlah terhadap apa yang menimpamu, sesungguhnya yang demikian itu termasuk perkara yang penting."},{"id":"18","ar":"وَلَا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَلَا تَمْشِ فِى الْاَرْضِ مَرَحًاۗ اِنَّ اللّٰهَ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُوْرٍۚ","bc":"wa lā tuṣa''ir khaddaka lin-nāsi wa lā tamsyi fil-arḍi maraḥā, innallāha lā yuḥibbu kulla mukhtālin fakhụr","at":"Dan janganlah kamu memalingkan wajah dari manusia (karena sombong) dan janganlah berjalan di bumi dengan angkuh. Sungguh, Allah tidak menyukai orang-orang yang sombong dan membanggakan diri."},{"id":"19","ar":"وَاقْصِدْ فِيْ مَشْيِكَ وَاغْضُضْ مِنْ صَوْتِكَۗ اِنَّ اَنْكَرَ الْاَصْوَاتِ لَصَوْتُ الْحَمِيْرِ","bc":"waqṣid fī masy-yika wagḍuḍ min ṣautik, inna angkaral-aṣwāti laṣautul-ḥamīr","at":"Dan sederhanakanlah dalam berjalan dan lunakkanlah suaramu. Sesungguhnya seburuk-buruk suara ialah suara keledai.”"},{"id":"20","ar":"اَلَمْ تَرَوْا اَنَّ اللّٰهَ سَخَّرَ لَكُمْ مَّا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَاَسْبَغَ عَلَيْكُمْ نِعَمَهٗ ظَاهِرَةً وَّبَاطِنَةً ۗوَمِنَ النَّاسِ مَنْ يُّجَادِلُ فِى اللّٰهِ بِغَيْرِ عِلْمٍ وَّلَا هُدًى وَّلَا كِتٰبٍ مُّنِيْرٍ","bc":"a lam tarau annallāha sakhkhara lakum mā fis-samāwāti wa mā fil-arḍi wa asbaga 'alaikum ni'amahụ ẓāhirataw wa bāṭinah, wa minan-nāsi may yujādilu fillāhi bigairi 'ilmiw wa lā hudaw wa lā kitābim munīr","at":"Tidakkah kamu memperhatikan bahwa Allah telah menundukkan apa yang ada di langit dan apa yang ada di bumi untuk (kepentingan)mu dan menyempurnakan nikmat-Nya untukmu lahir dan batin. Tetapi di antara manusia ada yang membantah tentang (keesaan) Allah tanpa ilmu atau petunjuk dan tanpa Kitab yang memberi penerangan."},{"id":"21","ar":"وَاِذَا قِيْلَ لَهُمُ اتَّبِعُوْا مَآ اَنْزَلَ اللّٰهُ قَالُوْا بَلْ نَتَّبِعُ مَا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَاۗ اَوَلَوْ كَانَ الشَّيْطٰنُ يَدْعُوْهُمْ اِلٰى عَذَابِ السَّعِيْرِ","bc":"wa iżā qīla lahumuttabi'ụ ma anzalallāhu qālụ bal nattabi'u mā wajadnā 'alaihi ābā`anā, a walau kānasy-syaiṭānu yad'ụhum ilā 'ażābis-sa'īr","at":"Dan apabila dikatakan kepada mereka, ”Ikutilah apa yang diturunkan Allah!” Mereka menjawab, ”(Tidak), tetapi kami (hanya) mengikuti kebiasaan yang kami dapati dari nenek moyang kami.” Apakah mereka (akan mengikuti nenek moyang mereka) walaupun sebenarnya setan menyeru mereka ke dalam azab api yang menyala-nyala (neraka)?"},{"id":"22","ar":"۞ وَمَنْ يُّسْلِمْ وَجْهَهٗٓ اِلَى اللّٰهِ وَهُوَ مُحْسِنٌ فَقَدِ اسْتَمْسَكَ بِالْعُرْوَةِ الْوُثْقٰىۗ وَاِلَى اللّٰهِ عَاقِبَةُ الْاُمُوْرِ","bc":"wa may yuslim waj-hahū ilallāhi wa huwa muḥsinun fa qadistamsaka bil-'urwatil-wuṡqā, wa ilallāhi 'āqibatul-umụr","at":"Dan barangsiapa berserah diri kepada Allah, sedang dia orang yang berbuat kebaikan, maka sesungguhnya dia telah berpegang kepada buhul (tali) yang kokoh. Hanya kepada Allah kesudahan segala urusan."},{"id":"23","ar":"وَمَنْ كَفَرَ فَلَا يَحْزُنْكَ كُفْرُهٗۗ اِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُمْ بِمَا عَمِلُوْاۗ اِنَّ اللّٰهَ عَلِيْمٌۢ بِذَاتِ الصُّدُوْرِ","bc":"wa mang kafara fa lā yaḥzungka kufruh, ilainā marji'uhum fa nunabbi`uhum bimā 'amilụ, innallāha 'alīmum biżātiṣ-ṣudụr","at":"Dan barangsiapa kafir maka kekafirannya itu janganlah menyedihkanmu (Muhammad). Hanya kepada Kami tempat kembali mereka, lalu Kami beritakan kepada mereka apa yang telah mereka kerjakan. Sesungguhnya Allah Maha Mengetahui segala isi hati."},{"id":"24","ar":"نُمَتِّعُهُمْ قَلِيْلًا ثُمَّ نَضْطَرُّهُمْ اِلٰى عَذَابٍ غَلِيْظٍ","bc":"numatti'uhum qalīlan ṡumma naḍṭarruhum ilā 'ażābin galīẓ","at":"Kami biarkan mereka bersenang-senang sebentar, kemudian Kami paksa mereka (masuk) ke dalam azab yang keras."},{"id":"25","ar":"وَلَىِٕنْ سَاَلْتَهُمْ مَّنْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ لَيَقُوْلُنَّ اللّٰهُ ۗقُلِ الْحَمْدُ لِلّٰهِ ۗبَلْ اَكْثَرُهُمْ لَا يَعْلَمُوْنَ","bc":"wa la`in sa`altahum man khalaqas-samāwāti wal-arḍa layaqụlunnallāh, qulil-ḥamdu lillāh, bal akṡaruhum lā ya'lamụn","at":"Dan sungguh, jika engkau (Muhammad) tanyakan kepada mereka, ”Siapakah yang menciptakan langit dan bumi?” Tentu mereka akan menjawab, ”Allah.” Katakanlah, ”Segala puji bagi Allah,” tetapi kebanyakan mereka tidak mengetahui."},{"id":"26","ar":"لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ اِنَّ اللّٰهَ هُوَ الْغَنِيُّ الْحَمِيْدُ","bc":"lillāhi mā fis-samāwāti wal-arḍ, innallāha huwal-ganiyyul-ḥamīd","at":"Milik Allah-lah apa yang di langit dan di bumi. Sesungguhnya Allah, Dialah Yang Mahakaya, Maha Terpuji."},{"id":"27","ar":"وَلَوْ اَنَّ مَا فِى الْاَرْضِ مِنْ شَجَرَةٍ اَقْلَامٌ وَّالْبَحْرُ يَمُدُّهٗ مِنْۢ بَعْدِهٖ سَبْعَةُ اَبْحُرٍ مَّا نَفِدَتْ كَلِمٰتُ اللّٰهِ ۗاِنَّ اللّٰهَ عَزِيْزٌ حَكِيْمٌ","bc":"walau anna mā fil-arḍi min syajaratin aqlāmuw wal-baḥru yamudduhụ mim ba'dihī sab'atu ab-ḥurim mā nafidat kalimātullāh, innallāha 'azīzun ḥakīm","at":"Dan seandainya pohon-pohon di bumi menjadi pena dan lautan (menjadi tinta), ditambahkan kepadanya tujuh lautan (lagi) setelah (kering)nya, niscaya tidak akan habis-habisnya (dituliskan) kalimat-kalimat Allah. Sesungguhnya Allah Mahaperkasa, Mahabijaksana."},{"id":"28","ar":"مَا خَلْقُكُمْ وَلَا بَعْثُكُمْ اِلَّا كَنَفْسٍ وَّاحِدَةٍ ۗاِنَّ اللّٰهَ سَمِيْعٌۢ بَصِيْرٌ","bc":"mā khalqukum wa lā ba'ṡukum illā kanafsiw wāḥidah, innallāha samī'um baṣīr","at":"Menciptakan dan membangkitkan kamu (bagi Allah) hanyalah seperti (menciptakan dan membangkitkan) satu jiwa saja (mudah). Sesungguhnya Allah Maha Mendengar, Maha Melihat."},{"id":"29","ar":"اَلَمْ تَرَ اَنَّ اللّٰهَ يُوْلِجُ الَّيْلَ فِى النَّهَارِ وَيُوْلِجُ النَّهَارَ فِى الَّيْلِ وَسَخَّرَ الشَّمْسَ وَالْقَمَرَۖ كُلٌّ يَّجْرِيْٓ اِلٰٓى اَجَلٍ مُّسَمًّى وَّاَنَّ اللّٰهَ بِمَا تَعْمَلُوْنَ خَبِيْرٌ","bc":"a lam tara annallāha yụlijul-laila fin-nahāri wa yụlijun-nahāra fil-laili wa sakhkharasy-syamsa wal-qamara kullui yajrī ilā ajalim musammaw wa annallāha bimā ta'malụna khabīr","at":"Tidakkah engkau memperhatikan, bahwa Allah memasukkan malam ke dalam siang dan memasukkan siang ke dalam malam dan Dia menundukkan matahari dan bulan, masing-masing beredar sampai kepada waktu yang ditentukan. Sungguh, Allah Mahateliti apa yang kamu kerjakan."},{"id":"30","ar":"ذٰلِكَ بِاَنَّ اللّٰهَ هُوَ الْحَقُّ وَاَنَّ مَا يَدْعُوْنَ مِنْ دُوْنِهِ الْبَاطِلُۙ وَاَنَّ اللّٰهَ هُوَ الْعَلِيُّ الْكَبِيْرُ","bc":"żālika bi`annallāha huwal-ḥaqqu wa anna mā yad'ụna min dụnihil-bāṭilu wa annallāha huwal-'aliyyul-kabīr","at":"Demikianlah, karena sesungguhnya Allah, Dialah (Tuhan) yang sebenarnya dan apa saja yang mereka seru selain Allah adalah batil. Dan sesungguhnya Allah, Dialah Yang Mahatinggi, Mahabesar."},{"id":"31","ar":"اَلَمْ تَرَ اَنَّ الْفُلْكَ تَجْرِيْ فِى الْبَحْرِ بِنِعْمَتِ اللّٰهِ لِيُرِيَكُمْ مِّنْ اٰيٰتِهٖۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ","bc":"a lam tara annal-fulka tajrī fil-baḥri bini'matillāhi liyuriyakum min āyātih, inna fī żālika la`āyātil likulli ṣabbārin syakụr","at":"Tidakkah engkau memperhatikan bahwa sesungguhnya kapal itu berlayar di laut dengan nikmat Allah, agar diperlihatkan-Nya kepadamu sebagian dari tanda-tanda (kebesaran)-Nya. Sungguh, pada yang demikian itu terdapat tanda-tanda (kebesaran)-Nya bagi setiap orang yang sangat sabar dan banyak bersyukur."},{"id":"32","ar":"وَاِذَا غَشِيَهُمْ مَّوْجٌ كَالظُّلَلِ دَعَوُا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۚ فَلَمَّا نَجّٰىهُمْ اِلَى الْبَرِّ فَمِنْهُمْ مُّقْتَصِدٌۗ وَمَا يَجْحَدُ بِاٰيٰتِنَآ اِلَّا كُلُّ خَتَّارٍ كَفُوْرٍ","bc":"wa iżā gasyiyahum maujung kaẓ-ẓulali da'awullāha mukhliṣīna lahud-dīn, fa lammā najjāhum ilal-barri fa min-hum muqtaṣid, wa mā yaj-ḥadu bi`āyātinā illā kullu khattāring kafụr","at":"Dan apabila mereka digulung ombak yang besar seperti gunung, mereka menyeru Allah dengan tulus ikhlas beragama kepada-Nya. Tetapi ketika Allah menyelamatkan mereka sampai di daratan, lalu sebagian mereka tetap menempuh jalan yang lurus. Adapun yang mengingkari ayat-ayat Kami hanyalah pengkhianat yang tidak berterima kasih."},{"id":"33","ar":"يٰٓاَيُّهَا النَّاسُ اتَّقُوْا رَبَّكُمْ وَاخْشَوْا يَوْمًا لَّا يَجْزِيْ وَالِدٌ عَنْ وَّلَدِهٖۖ وَلَا مَوْلُوْدٌ هُوَ جَازٍ عَنْ وَّالِدِهٖ شَيْـًٔاۗ اِنَّ وَعْدَ اللّٰهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ الْحَيٰوةُ الدُّنْيَاۗ وَلَا يَغُرَّنَّكُمْ بِاللّٰهِ الْغَرُوْرُ","bc":"yā ayyuhan-nāsuttaqụ rabbakum wakhsyau yaumal lā yajzī wālidun 'aw waladihī wa lā maulụdun huwa jāzin 'aw wālidihī syai`ā, inna wa'dallāhi ḥaqqun fa lā tagurrannakumul-ḥayātud-dun-yā, wa lā yagurrannakum billāhil-garụr","at":"Wahai manusia! Bertakwalah kepada Tuhanmu dan takutlah pada hari yang (ketika itu) seorang bapak tidak dapat menolong anaknya, dan seorang anak tidak dapat (pula) menolong bapaknya sedikit pun. Sungguh, janji Allah pasti benar, maka janganlah sekali-kali kamu teperdaya oleh kehidupan dunia, dan jangan sampai kamu teperdaya oleh penipu dalam (menaati) Allah."},{"id":"34","ar":"اِنَّ اللّٰهَ عِنْدَهٗ عِلْمُ السَّاعَةِۚ وَيُنَزِّلُ الْغَيْثَۚ وَيَعْلَمُ مَا فِى الْاَرْحَامِۗ وَمَا تَدْرِيْ نَفْسٌ مَّاذَا تَكْسِبُ غَدًاۗ وَمَا تَدْرِيْ نَفْسٌۢ بِاَيِّ اَرْضٍ تَمُوْتُۗ اِنَّ اللّٰهَ عَلِيْمٌ خَبِيْرٌ","bc":"innallāha 'indahụ 'ilmus-sā'ah, wa yunazzilul-gaīṡ, wa ya'lamu mā fil-ar-ḥām, wa mā tadrī nafsum māżā taksibu gadā, wa mā tadrī nafsum bi`ayyi arḍin tamụt, innallāha 'alīmun khabīr","at":"Sesungguhnya hanya di sisi Allah ilmu tentang hari Kiamat; dan Dia yang menurunkan hujan, dan mengetahui apa yang ada dalam rahim. Dan tidak ada seorang pun yang dapat mengetahui (dengan pasti) apa yang akan dikerjakannya besok. Dan tidak ada seorang pun yang dapat mengetahui di bumi mana dia akan mati. Sungguh, Allah Maha Mengetahui, Maha Mengenal."}],maryam=[{"id":"1","ar":"كۤهٰيٰعۤصۤ ۚ ","bc":"k&amp;f hā yā 'aīn ṣ&amp;d","at":"Kaf Ha Ya ‘Ain Shad."},{"id":"2","ar":"ذِكْرُ رَحْمَتِ رَبِّكَ عَبْدَهٗ زَكَرِيَّا ۚ","bc":"żikru raḥmati rabbika 'abdahụ zakariyyā","at":"Yang dibacakan ini adalah) penjelasan tentang rahmat Tuhanmu kepada hamba-Nya, Zakaria,"},{"id":"3","ar":"اِذْ نَادٰى رَبَّهٗ نِدَاۤءً خَفِيًّا ","bc":"iż nādā rabbahụ nid&amp;`an khafiyyā","at":" (yaitu) ketika dia berdoa kepada Tuhannya dengan suara yang lembut."},{"id":"4","ar":"قَالَ رَبِّ اِنِّيْ وَهَنَ الْعَظْمُ مِنِّيْ وَاشْتَعَلَ الرَّأْسُ شَيْبًا وَّلَمْ اَكُنْۢ بِدُعَاۤىِٕكَ رَبِّ شَقِيًّا ","bc":"qāla rabbi innī wahanal-'aẓmu minnī wasyta'alar-ra`su syaibaw wa lam akum bidu'&amp;`ika rabbi syaqiyyā","at":"Dia (Zakaria) berkata, “Ya Tuhanku, sungguh tulangku telah lemah dan kepalaku telah dipenuhi uban, dan aku belum pernah kecewa dalam berdoa kepada-Mu, ya Tuhanku."},{"id":"5","ar":"وَاِنِّيْ خِفْتُ الْمَوَالِيَ مِنْ وَّرَاۤءِيْ وَكَانَتِ امْرَاَتِيْ عَاقِرًا فَهَبْ لِيْ مِنْ لَّدُنْكَ وَلِيًّا ۙ","bc":"wa innī khiftul-mawāliya miw war&amp;`ī wa kānatimra`atī 'āqiran fa hab lī mil ladungka waliyyā","at":"Dan sungguh, aku khawatir terhadap kerabatku sepeninggalku, padahal istriku seorang yang mandul, maka anugerahilah aku seorang anak dari sisi-Mu,"},{"id":"6","ar":"يَّرِثُنِيْ وَيَرِثُ مِنْ اٰلِ يَعْقُوْبَ وَاجْعَلْهُ رَبِّ رَضِيًّا ","bc":"yariṡunī wa yariṡu min āli ya'qụba waj'al-hu rabbi raḍiyyā","at":"yang akan mewarisi aku dan mewarisi dari keluarga Yakub; dan jadikanlah dia, ya Tuhanku, seorang yang diridai.”"},{"id":"7","ar":"يٰزَكَرِيَّآ اِنَّا نُبَشِّرُكَ بِغُلٰمِ ِۨاسْمُهٗ يَحْيٰىۙ لَمْ نَجْعَلْ لَّهٗ مِنْ قَبْلُ سَمِيًّا ","bc":"yā zakariyy&amp; innā nubasysyiruka bigulāminismuhụ yaḥyā lam naj'al lahụ ming qablu samiyyā","at":"(Allah berfirman), “Wahai Zakaria! Kami memberi kabar gembira kepadamu dengan seorang anak laki-laki namanya Yahya, yang Kami belum pernah memberikan nama seperti itu sebelumnya.”"},{"id":"8","ar":"قَالَ رَبِّ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّكَانَتِ امْرَاَتِيْ عَاقِرًا وَّقَدْ بَلَغْتُ مِنَ الْكِبَرِ عِتِيًّا ","bc":"qāla rabbi annā yakụnu lī gulāmuw wa kānatimra`atī 'āqiraw wa qad balagtu minal-kibari 'itiyyā","at":"Dia (Zakaria) berkata, “Ya Tuhanku, bagaimana aku akan mempunyai anak, padahal istriku seorang yang mandul dan aku (sendiri) sesungguhnya sudah mencapai usia yang sangat tua?”"},{"id":"9","ar":"قَالَ كَذٰلِكَۗ قَالَ رَبُّكَ هُوَ عَلَيَّ هَيِّنٌ وَّقَدْ خَلَقْتُكَ مِنْ قَبْلُ وَلَمْ تَكُ شَيْـًٔا ","bc":"qāla każālik, qāla rabbuka huwa 'alayya hayyinuw wa qad khalaqtuka ming qablu wa lam taku syai`ā","at":"(Allah) berfirman, “Demikianlah.” Tuhanmu berfirman, “Hal itu mudah bagi-Ku; sungguh, engkau telah Aku ciptakan sebelum itu, padahal (pada waktu itu) engkau belum berwujud sama sekali.”"},{"id":"10","ar":"قَالَ رَبِّ اجْعَلْ لِّيْٓ اٰيَةً ۗقَالَ اٰيَتُكَ اَلَّا تُكَلِّمَ النَّاسَ ثَلٰثَ لَيَالٍ سَوِيًّا ","bc":"qāla rabbij'al lī āyah, qāla āyatuka allā tukalliman-nāsa ṡalāṡa layālin sawiyyā","at":"Dia (Zakaria) berkata, “Ya Tuhanku, berilah aku suatu tanda.” (Allah) berfirman, “Tandamu ialah engkau tidak dapat bercakap-cakap dengan manusia selama tiga malam, padahal engkau sehat.”"},{"id":"11","ar":"فَخَرَجَ عَلٰى قَوْمِهٖ مِنَ الْمِحْرَابِ فَاَوْحٰٓى اِلَيْهِمْ اَنْ سَبِّحُوْا بُكْرَةً وَّعَشِيًّا ","bc":"fa kharaja 'alā qaumihī minal-miḥrābi fa auḥ&amp; ilaihim an sabbiḥụ bukrataw wa 'asyiyyā","at":"Maka dia keluar dari mihrab menuju kaumnya, lalu dia memberi isyarat kepada mereka; bertasbihlah kamu pada waktu pagi dan petang."},{"id":"12","ar":"يٰيَحْيٰى خُذِ الْكِتٰبَ بِقُوَّةٍ ۗوَاٰتَيْنٰهُ الْحُكْمَ صَبِيًّاۙ ","bc":"yā yaḥyā khużil-kitāba biquwwah, wa ātaināhul-ḥukma ṣabiyyā","at":" ”Wahai Yahya! Ambillah (pelajarilah) Kitab (Taurat) itu dengan sungguh-sungguh.” Dan Kami berikan hikmah kepadanya (Yahya) selagi dia masih kanak-kanak,"},{"id":"13","ar":"وَّحَنَانًا مِّنْ لَّدُنَّا وَزَكٰوةً ۗوَكَانَ تَقِيًّا ۙ","bc":"wa ḥanānam mil ladunnā wa zakāh, wa kāna taqiyyā","at":"dan (Kami jadikan) rasa kasih sayang (kepada sesama) dari Kami dan bersih (dari dosa). Dan dia pun seorang yang bertakwa, "},{"id":"14","ar":"وَّبَرًّاۢ بِوَالِدَيْهِ وَلَمْ يَكُنْ جَبَّارًا عَصِيًّا ","bc":"wa barram biwālidaihi wa lam yakun jabbāran 'aṣiyyā","at":" dan sangat berbakti kepada kedua orang tuanya, dan dia bukan orang yang sombong (bukan pula) orang yang durhaka."},{"id":"15","ar":"وَسَلٰمٌ عَلَيْهِ يَوْمَ وُلِدَ وَيَوْمَ يَمُوْتُ وَيَوْمَ يُبْعَثُ حَيًّا","bc":"wa salāmun 'alaihi yauma wulida wa yauma yamụtu wa yauma yub'aṡu ḥayyā","at":"Dan kesejahteraan bagi dirinya pada hari lahirnya, pada hari wafatnya, dan pada hari dia dibangkitkan hidup kembali."},{"id":"16","ar":"وَاذْكُرْ فِى الْكِتٰبِ مَرْيَمَۘ اِذِ انْتَبَذَتْ مِنْ اَهْلِهَا مَكَانًا شَرْقِيًّا ۙ","bc":"ważkur fil-kitābi maryam, iżintabażat min ahlihā makānan syarqiyyā","at":"Dan ceritakanlah (Muhammad) kisah Maryam di dalam Kitab (Al-Qur'an), (yaitu) ketika dia mengasingkan diri dari keluarganya ke suatu tempat di sebelah timur (Baitulmaqdis),"},{"id":"17","ar":"فَاتَّخَذَتْ مِنْ دُوْنِهِمْ حِجَابًاۗ فَاَرْسَلْنَآ اِلَيْهَا رُوْحَنَا فَتَمَثَّلَ لَهَا بَشَرًا سَوِيًّا ","bc":"fattakhażat min dụnihim ḥijābā, fa arsaln&amp; ilaihā rụḥanā fa tamaṡṡala lahā basyaran sawiyyā","at":"lalu dia memasang tabir (yang melindunginya) dari mereka; lalu Kami mengutus roh Kami (Jibril) kepadanya, maka dia menampakkan diri di hadapannya dalam bentuk manusia yang sempurna."},{"id":"18","ar":"قَالَتْ اِنِّيْٓ اَعُوْذُ بِالرَّحْمٰنِ مِنْكَ اِنْ كُنْتَ تَقِيًّا ","bc":"qālat innī a'ụżu bir-raḥmāni mingka ing kunta taqiyyā","at":"Dia (Maryam) berkata, “Sungguh, aku berlindung kepada Tuhan Yang Maha Pengasih terhadapmu, jika engkau orang yang bertakwa.”"},{"id":"19","ar":"قَالَ اِنَّمَآ اَنَا۠ رَسُوْلُ رَبِّكِۖ لِاَهَبَ لَكِ غُلٰمًا زَكِيًّا ","bc":"qāla innam&amp; ana rasụlu rabbiki li`ahaba laki gulāman zakiyyā","at":"Dia (Jibril) berkata, “Sesungguhnya aku hanyalah utusan Tuhanmu, untuk menyampaikan anugerah kepadamu seorang anak laki-laki yang suci.”"},{"id":"20","ar":"قَالَتْ اَنّٰى يَكُوْنُ لِيْ غُلٰمٌ وَّلَمْ يَمْسَسْنِيْ بَشَرٌ وَّلَمْ اَكُ بَغِيًّا ","bc":"qālat annā yakụnu lī gulāmuw wa lam yamsasnī basyaruw wa lam aku bagiyyā","at":"Dia (Maryam) berkata, “Bagaimana mungkin aku mempunyai anak laki-laki, padahal tidak pernah ada orang (laki-laki) yang menyentuhku dan aku bukan seorang pezina!”"},{"id":"21","ar":"قَالَ كَذٰلِكِۚ قَالَ رَبُّكِ هُوَ عَلَيَّ هَيِّنٌۚ وَلِنَجْعَلَهٗٓ اٰيَةً لِّلنَّاسِ وَرَحْمَةً مِّنَّاۚ وَكَانَ اَمْرًا مَّقْضِيًّا ","bc":"qāla każālik, qāla rabbuki huwa 'alayya hayyin, wa linaj'alahū āyatal lin-nāsi wa raḥmatam minnā, wa kāna amram maqḍiyyā","at":"Dia (Jibril) berkata, “Demikianlah.” Tuhanmu berfirman, “Hal itu mudah bagi-Ku, dan agar Kami menjadikannya suatu tanda (kebesaran Allah) bagi manusia dan sebagai rahmat dari Kami; dan hal itu adalah suatu urusan yang (sudah) diputuskan.”"},{"id":"22","ar":"۞ فَحَمَلَتْهُ فَانْتَبَذَتْ بِهٖ مَكَانًا قَصِيًّا ","bc":"fa ḥamalat-hu fantabażat bihī makānang qaṣiyyā","at":" Maka dia (Maryam) mengandung, lalu dia mengasingkan diri dengan kandungannya itu ke tempat yang jauh."},{"id":"23","ar":"فَاَجَاۤءَهَا الْمَخَاضُ اِلٰى جِذْعِ النَّخْلَةِۚ قَالَتْ يٰلَيْتَنِيْ مِتُّ قَبْلَ هٰذَا وَكُنْتُ نَسْيًا مَّنْسِيًّا ","bc":"fa aj&amp;`ahal-makhāḍu ilā jiż'in-nakhlah, qālat yā laitanī mittu qabla hāżā wa kuntu nas-yam mansiyyā","at":"Kemudian rasa sakit akan melahirkan memaksanya (bersandar) pada pangkal pohon kurma, dia (Maryam) berkata, “Wahai, betapa (baiknya) aku mati sebelum ini, dan aku menjadi seorang yang tidak diperhatikan dan dilupakan.”"},{"id":"24","ar":"فَنَادٰىهَا مِنْ تَحْتِهَآ اَلَّا تَحْزَنِيْ قَدْ جَعَلَ رَبُّكِ تَحْتَكِ سَرِيًّا ","bc":"fa nādāhā min taḥtih&amp; allā taḥzanī qad ja'ala rabbuki taḥtaki sariyyā","at":"Maka dia (Jibril) berseru kepadanya dari tempat yang rendah, “Janganlah engkau bersedih hati, sesungguhnya Tuhanmu telah menjadikan anak sungai di bawahmu."},{"id":"25","ar":"وَهُزِّيْٓ اِلَيْكِ بِجِذْعِ النَّخْلَةِ تُسٰقِطْ عَلَيْكِ رُطَبًا جَنِيًّا ۖ","bc":"wa huzzī ilaiki bijiż'in-nakhlati tusāqiṭ 'alaiki ruṭaban janiyyā","at":"Dan goyanglah pangkal pohon kurma itu ke arahmu, niscaya (pohon) itu akan menggugurkan buah kurma yang masak kepadamu."},{"id":"26","ar":"فَكُلِيْ وَاشْرَبِيْ وَقَرِّيْ عَيْنًا ۚفَاِمَّا تَرَيِنَّ مِنَ الْبَشَرِ اَحَدًاۙ فَقُوْلِيْٓ اِنِّيْ نَذَرْتُ لِلرَّحْمٰنِ صَوْمًا فَلَنْ اُكَلِّمَ الْيَوْمَ اِنْسِيًّا ۚ","bc":"fa kulī wasyrabī wa qarrī 'ainā, fa immā tarayinna minal-basyari aḥadan fa qụlī innī nażartu lir-raḥmāni ṣauman fa lan ukallimal-yauma insiyyā","at":"Maka makan, minum dan bersenanghatilah engkau. Jika engkau melihat seseorang, maka katakanlah, “Sesungguhnya aku telah bernazar berpuasa untuk Tuhan Yang Maha Pengasih, maka aku tidak akan berbicara dengan siapa pun pada hari ini.”"},{"id":"27","ar":"فَاَتَتْ بِهٖ قَوْمَهَا تَحْمِلُهٗ ۗقَالُوْا يٰمَرْيَمُ لَقَدْ جِئْتِ شَيْـًٔا فَرِيًّا ","bc":"fa atat bihī qaumahā taḥmiluh, qālụ yā maryamu laqad ji`ti syai`an fariyyā","at":"Kemudian dia (Maryam) membawa dia (bayi itu) kepada kaumnya dengan menggendongnya. Mereka (kaumnya) berkata, “Wahai Maryam! Sungguh, engkau telah membawa sesuatu yang sangat mungkar."},{"id":"28","ar":"يٰٓاُخْتَ هٰرُوْنَ مَا كَانَ اَبُوْكِ امْرَاَ سَوْءٍ وَّمَا كَانَتْ اُمُّكِ بَغِيًّا ۖ","bc":"y&amp; ukhta hārụna mā kāna abụkimra`a sau`iw wa mā kānat ummuki bagiyyā","at":"Wahai saudara perempuan Harun (Maryam)! Ayahmu bukan seorang yang buruk perangai dan ibumu bukan seorang perempuan pezina.”"},{"id":"29","ar":"فَاَشَارَتْ اِلَيْهِۗ قَالُوْا كَيْفَ نُكَلِّمُ مَنْ كَانَ فِى الْمَهْدِ صَبِيًّا ","bc":"fa asyārat ilaīh, qālụ kaifa nukallimu mang kāna fil-mahdi ṣabiyyā","at":"Maka dia (Maryam) menunjuk kepada (anak)nya. Mereka berkata, “Bagaimana kami akan berbicara dengan anak kecil yang masih dalam ayunan?” "},{"id":"30","ar":"قَالَ اِنِّيْ عَبْدُ اللّٰهِ ۗاٰتٰنِيَ الْكِتٰبَ وَجَعَلَنِيْ نَبِيًّا ۙ","bc":"qāla innī 'abdullāh, ātāniyal-kitāba wa ja'alanī nabiyyā","at":"Dia (Isa) berkata, “Sesungguhnya aku hamba Allah, Dia memberiku Kitab (Injil) dan Dia menjadikan aku seorang Nabi."},{"id":"31","ar":"وَّجَعَلَنِيْ مُبٰرَكًا اَيْنَ مَا كُنْتُۖ وَاَوْصٰنِيْ بِالصَّلٰوةِ وَالزَّكٰوةِ مَا دُمْتُ حَيًّا ۖ","bc":"wa ja'alanī mubārakan aina mā kuntu wa auṣānī biṣ-ṣalāti waz-zakāti mā dumtu ḥayyā","at":"Dan Dia menjadikan aku seorang yang diberkahi di mana saja aku berada, dan Dia memerintahkan kepadaku (melaksanakan) salat dan (menunaikan) zakat selama aku hidup;"},{"id":"32","ar":"وَّبَرًّاۢ بِوَالِدَتِيْ وَلَمْ يَجْعَلْنِيْ جَبَّارًا شَقِيًّا ","bc":"wa barram biwālidatī wa lam yaj'alnī jabbāran syaqiyyā","at":"dan berbakti kepada ibuku, dan Dia tidak menjadikan aku seorang yang sombong lagi celaka."},{"id":"33","ar":"وَالسَّلٰمُ عَلَيَّ يَوْمَ وُلِدْتُّ وَيَوْمَ اَمُوْتُ وَيَوْمَ اُبْعَثُ حَيًّا ","bc":"was-salāmu 'alayya yauma wulittu wa yauma amụtu wa yauma ub'aṡu ḥayyā","at":" Dan kesejahteraan semoga dilimpahkan kepadaku, pada hari kelahiranku, pada hari wafatku, dan pada hari aku dibangkitkan hidup kembali.”"},{"id":"34","ar":"ذٰلِكَ عِيْسَى ابْنُ مَرْيَمَ ۚقَوْلَ الْحَقِّ الَّذِيْ فِيْهِ يَمْتَرُوْنَ ","bc":"żālika 'īsabnu maryam, qaulal-ḥaqqillażī fīhi yamtarụn","at":"Itulah Isa putra Maryam, (yang mengatakan) perkataan yang benar, yang mereka ragukan kebenarannya."},{"id":"35","ar":"مَا كَانَ لِلّٰهِ اَنْ يَّتَّخِذَ مِنْ وَّلَدٍ سُبْحٰنَهٗ ۗاِذَا قَضٰٓى اَمْرًا فَاِنَّمَا يَقُوْلُ لَهٗ كُنْ فَيَكُوْنُ ۗ","bc":"mā kāna lillāhi ay yattakhiża miw waladin sub-ḥānah, iżā qaḍ&amp; amran fa innamā yaqụlu lahụ kun fa yakụn","at":"Tidak patut bagi Allah mempunyai anak, Mahasuci Dia. Apabila Dia hendak menetapkan sesuatu, maka Dia hanya berkata kepadanya, “Jadilah!” Maka jadilah sesuatu itu."},{"id":"36","ar":"وَاِنَّ اللّٰهَ رَبِّيْ وَرَبُّكُمْ فَاعْبُدُوْهُ ۗهٰذَا صِرَاطٌ مُّسْتَقِيْمٌ ","bc":"wa innallāha rabbī wa rabbukum fa'budụh, hāżā ṣirāṭum mustaqīm","at":"(Isa berkata), “Dan sesungguhnya Allah itu Tuhanku dan Tuhanmu, maka sembahlah Dia. Ini adalah jalan yang lurus.”"},{"id":"37","ar":"فَاخْتَلَفَ الْاَحْزَابُ مِنْۢ بَيْنِهِمْۚ فَوَيْلٌ لِّلَّذِيْنَ كَفَرُوْا مِنْ مَّشْهَدِ يَوْمٍ عَظِيْمٍ ","bc":"fakhtalafal-aḥzābu mim bainihim, fa wailul lillażīna kafarụ mim masy-hadi yaumin 'aẓīm","at":"Maka berselisihlah golongan-golongan (yang ada) di antara mereka (Yahudi dan Nasrani). Maka celakalah orang-orang kafir pada waktu menyaksikan hari yang agung!"},{"id":"38","ar":"اَسْمِعْ بِهِمْ وَاَبْصِرْۙ يَوْمَ يَأْتُوْنَنَا لٰكِنِ الظّٰلِمُوْنَ الْيَوْمَ فِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"asmi' bihim wa abṣir yauma ya`tụnanā lākiniẓ-ẓālimụnal-yauma fī ḍalālim mubīn","at":"Alangkah tajam pendengaran mereka dan alangkah terang penglihatan mereka pada hari mereka datang kepada Kami. Tetapi orang-orang yang zalim pada hari ini (di dunia) berada dalam kesesatan yang nyata."},{"id":"39","ar":"وَاَنْذِرْهُمْ يَوْمَ الْحَسْرَةِ اِذْ قُضِيَ الْاَمْرُۘ وَهُمْ فِيْ غَفْلَةٍ وَّهُمْ لَا يُؤْمِنُوْنَ ","bc":"wa anżir-hum yaumal-ḥasrati iż quḍiyal-amr, wa hum fī gaflatiw wa hum lā yu`minụn","at":" Dan berilah mereka peringatan (Muhammad) tentang hari penyesalan, (yaitu) ketika segala perkara telah diputus, sedang mereka dalam kelalaian dan mereka tidak beriman."},{"id":"40","ar":"اِنَّا نَحْنُ نَرِثُ الْاَرْضَ وَمَنْ عَلَيْهَا وَاِلَيْنَا يُرْجَعُوْنَ","bc":"innā naḥnu nariṡul-arḍa wa man 'alaihā wa ilainā yurja'ụn","at":"Sesungguhnya Kamilah yang mewarisi bumi dan semua yang ada di atasnya, dan hanya kepada Kami mereka dikembalikan."},{"id":"41","ar":"وَاذْكُرْ فِى الْكِتٰبِ اِبْرٰهِيْمَ ەۗ اِنَّهٗ كَانَ صِدِّيْقًا نَّبِيًّا","bc":"ważkur fil-kitābi ibrāhīm, innahụ kāna ṣiddīqan nabiyyā","at":"Dan ceritakanlah (Muhammad) kisah Ibrahim di dalam Kitab (Al-Qur'an), sesungguhnya dia adalah seorang yang sangat membenarkan, seorang Nabi. "},{"id":"42","ar":"اِذْ قَالَ لِاَبِيْهِ يٰٓاَبَتِ لِمَ تَعْبُدُ مَا لَا يَسْمَعُ وَلَا يُبْصِرُ وَلَا يُغْنِيْ عَنْكَ شَيْـًٔا ","bc":"iż qāla li`abīhi y&amp; abati lima ta'budu mā lā yasma'u wa lā yubṣiru wa lā yugnī 'angka syai`ā","at":"(Ingatlah) ketika dia (Ibrahim) berkata kepada ayahnya, “Wahai ayahku! Mengapa engkau menyembah sesuatu yang tidak mendengar, tidak melihat, dan tidak dapat menolongmu sedikit pun?"},{"id":"43","ar":"يٰٓاَبَتِ اِنِّي قَدْ جَاۤءَنِيْ مِنَ الْعِلْمِ مَا لَمْ يَأْتِكَ فَاتَّبِعْنِيْٓ اَهْدِكَ صِرَاطًا سَوِيًّا ","bc":"y&amp; abati innī qad j&amp;`anī minal-'ilmi mā lam ya`tika fattabi'nī ahdika ṣirāṭan sawiyyā","at":"Wahai ayahku! Sungguh, telah sampai kepadaku sebagian ilmu yang tidak diberikan kepadamu, maka ikutilah aku, niscaya aku akan menunjukkan kepadamu jalan yang lurus."},{"id":"44","ar":"يٰٓاَبَتِ لَا تَعْبُدِ الشَّيْطٰنَۗ اِنَّ الشَّيْطٰنَ كَانَ لِلرَّحْمٰنِ عَصِيًّا ","bc":"y&amp; abati lā ta'budisy-syaiṭān, innasy-syaiṭāna kāna lir-raḥmāni 'aṣiyyā","at":"Wahai ayahku! Janganlah engkau menyembah setan. Sungguh, setan itu durhaka kepada Tuhan Yang Maha Pengasih."},{"id":"45","ar":"يٰٓاَبَتِ اِنِّيْٓ اَخَافُ اَنْ يَّمَسَّكَ عَذَابٌ مِّنَ الرَّحْمٰنِ فَتَكُوْنَ لِلشَّيْطٰنِ وَلِيًّا ","bc":"y&amp; abati innī akhāfu ay yamassaka 'ażābum minar-raḥmāni fa takụna lisy-syaiṭāni waliyyā","at":"Wahai ayahku! Aku sungguh khawatir engkau akan ditimpa azab dari Tuhan Yang Maha Pengasih, sehingga engkau menjadi teman bagi setan.”"},{"id":"46","ar":"قَالَ اَرَاغِبٌ اَنْتَ عَنْ اٰلِهَتِيْ يٰٓاِبْرٰهِيْمُ ۚ لَىِٕنْ لَّمْ تَنْتَهِ لَاَرْجُمَنَّكَ وَاهْجُرْنِيْ مَلِيًّا ","bc":"qāla a rāgibun anta 'an ālihatī y&amp; ibrāhīm, la`il lam tantahi la`arjumannaka wahjurnī maliyyā","at":"Dia (ayahnya) berkata, “Bencikah engkau kepada tuhan-tuhanku, wahai Ibrahim? Jika engkau tidak berhenti, pasti engkau akan kurajam, maka tinggalkanlah aku untuk waktu yang lama.” "},{"id":"47","ar":"قَالَ سَلٰمٌ عَلَيْكَۚ سَاَسْتَغْفِرُ لَكَ رَبِّيْۗ اِنَّهٗ كَانَ بِيْ حَفِيًّا ","bc":"qāla salāmun 'alaīk, sa`astagfiru laka rabbī, innahụ kāna bī ḥafiyyā","at":"Dia (Ibrahim) berkata, “Semoga keselamatan dilimpahkan kepadamu, aku akan memohonkan ampunan bagimu kepada Tuhanku. Sesungguhnya Dia sangat baik kepadaku."},{"id":"48","ar":"وَاَعْتَزِلُكُمْ وَمَا تَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ وَاَدْعُوْ رَبِّيْۖ عَسٰٓى اَلَّآ اَكُوْنَ بِدُعَاۤءِ رَبِّيْ شَقِيًّا ","bc":"wa a'tazilukum wa mā tad'ụna min dụnillāhi wa ad'ụ rabbī 'as&amp; all&amp; akụna bidu'&amp;`i rabbī syaqiyyā","at":" Dan aku akan menjauhkan diri darimu dan dari apa yang engkau sembah selain Allah, dan aku akan berdoa kepada Tuhanku, mudah-mudahan aku tidak akan kecewa dengan berdoa kepada Tuhanku.”"},{"id":"49","ar":"فَلَمَّا اعْتَزَلَهُمْ وَمَا يَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ ۙوَهَبْنَا لَهٗٓ اِسْحٰقَ وَيَعْقُوْبَۗ وَكُلًّا جَعَلْنَا نَبِيًّا ","bc":"fa lamma'tazalahum wa mā ya'budụna min dụnillāhi wahabnā lahū is-ḥāqa wa ya'qụb, wa kullan ja'alnā nabiyyā","at":" Maka ketika dia (Ibrahim) sudah menjauhkan diri dari mereka dan dari apa yang mereka sembah selain Allah, Kami anugerahkan kepadanya Ishak dan Yakub. Dan masing-masing Kami angkat menjadi nabi."},{"id":"50","ar":"وَوَهَبْنَا لَهُمْ مِّنْ رَّحْمَتِنَا وَجَعَلْنَا لَهُمْ لِسَانَ صِدْقٍ عَلِيًّا","bc":"wa wahabnā lahum mir raḥmatinā wa ja'alnā lahum lisāna ṣidqin 'aliyyā","at":"Dan Kami anugerahkan kepada mereka sebagian dari rahmat Kami dan Kami jadikan mereka buah tutur yang baik dan mulia."},{"id":"51","ar":"وَاذْكُرْ فِى الْكِتٰبِ مُوْسٰٓىۖ اِنَّهٗ كَانَ مُخْلَصًا وَّكَانَ رَسُوْلًا نَّبِيًّا ","bc":"ważkur fil-kitābi mụs&amp; innahụ kāna mukhlaṣaw wa kāna rasụlan nabiyyā","at":"Dan ceritakanlah (Muhammad), kisah Musa di dalam Kitab (Al-Qur'an). Dia benar-benar orang yang terpilih, seorang rasul dan nabi."},{"id":"52","ar":"وَنَادَيْنٰهُ مِنْ جَانِبِ الطُّوْرِ الْاَيْمَنِ وَقَرَّبْنٰهُ نَجِيًّا ","bc":"wa nādaināhu min jānibiṭ-ṭụril-aimani wa qarrabnāhu najiyyā","at":"Dan Kami telah memanggilnya dari sebelah kanan gunung (Sinai) dan Kami dekatkan dia untuk bercakap-cakap."},{"id":"53","ar":"وَوَهَبْنَا لَهٗ مِنْ رَّحْمَتِنَآ اَخَاهُ هٰرُوْنَ نَبِيًّا ","bc":"wa wahabnā lahụ mir raḥmatin&amp; akhāhu hārụna nabiyyā","at":" Dan Kami telah menganugerahkan sebagian rahmat Kami kepadanya, yaitu (bahwa) saudaranya, Harun, menjadi seorang nabi."},{"id":"54","ar":"وَاذْكُرْ فِى الْكِتٰبِ اِسْمٰعِيْلَ ۖاِنَّهٗ كَانَ صَادِقَ الْوَعْدِ وَكَانَ رَسُوْلًا نَّبِيًّا ۚ","bc":"ważkur fil-kitābi ismā'īla innahụ kāna ṣādiqal-wa'di wa kāna rasụlan nabiyyā","at":"Dan ceritakanlah (Muhammad), kisah Ismail di dalam Kitab (Al-Qur'an). Dia benar-benar seorang yang benar janjinya, seorang rasul dan nabi."},{"id":"55","ar":"وَكَانَ يَأْمُرُ اَهْلَهٗ بِالصَّلٰوةِ وَالزَّكٰوةِۖ وَكَانَ عِنْدَ رَبِّهٖ مَرْضِيًّا ","bc":"wa kāna ya`muru ahlahụ biṣ-ṣalāti waz-zakāti wa kāna 'inda rabbihī marḍiyyā","at":"Dan dia menyuruh keluarganya untuk (melaksanakan) salat dan (menunaikan) zakat, dan dia seorang yang diridai di sisi Tuhannya."},{"id":"56","ar":"وَاذْكُرْ فِى الْكِتٰبِ اِدْرِيْسَۖ اِنَّهٗ كَانَ صِدِّيْقًا نَّبِيًّا ۙ","bc":"ważkur fil-kitābi idrīsa innahụ kāna ṣiddīqan nabiyyā","at":"Dan ceritakanlah (Muhammad) kisah Idris di dalam Kitab (Al-Qur'an). Sesungguhnya dia seorang yang sangat mencintai kebenaran dan seorang nabi,"},{"id":"57","ar":"وَّرَفَعْنٰهُ مَكَانًا عَلِيًّا ","bc":"wa rafa'nāhu makānan 'aliyyā","at":"dan Kami telah mengangkatnya ke martabat yang tinggi."},{"id":"58","ar":"اُولٰۤىِٕكَ الَّذِيْنَ اَنْعَمَ اللّٰهُ عَلَيْهِمْ مِّنَ النَّبِيّٖنَ مِنْ ذُرِّيَّةِ اٰدَمَ وَمِمَّنْ حَمَلْنَا مَعَ نُوْحٍۖ وَّمِنْ ذُرِّيَّةِ اِبْرٰهِيْمَ وَاِسْرَاۤءِيْلَ ۖوَمِمَّنْ هَدَيْنَا وَاجْتَبَيْنَاۗ اِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُ الرَّحْمٰنِ خَرُّوْا سُجَّدًا وَّبُكِيًّا ۩","bc":"ul&amp;`ikallażīna an'amallāhu 'alaihim minan-nabiyyīna min żurriyyati ādama wa mim man ḥamalnā ma'a nụḥiw wa min żurriyyati ibrāhīma wa isr&amp;`īla wa mim man hadainā wajtabainā, iżā tutlā 'alaihim āyātur-raḥmāni kharrụ sujjadaw wa bukiyyā","at":"Mereka itulah orang yang telah diberi nikmat oleh Allah, yaitu dari (golongan) para nabi dari keturunan Adam, dan dari orang yang Kami bawa (dalam kapal) bersama Nuh, dan dari keturunan Ibrahim dan Israil (Yakub) dan dari orang yang telah Kami beri petunjuk dan telah Kami pilih. Apabila dibacakan ayat-ayat Allah Yang Maha Pengasih kepada mereka, maka mereka tunduk sujud dan menangis."},{"id":"59","ar":"۞ فَخَلَفَ مِنْۢ بَعْدِهِمْ خَلْفٌ اَضَاعُوا الصَّلٰوةَ وَاتَّبَعُوا الشَّهَوٰتِ فَسَوْفَ يَلْقَوْنَ غَيًّا ۙ","bc":"fa khalafa mim ba'dihim khalfun aḍā'uṣ-ṣalāta wattaba'usy-syahawāti fa saufa yalqauna gayyā","at":"Kemudian datanglah setelah mereka pengganti yang mengabaikan salat dan mengikuti keinginannya, maka mereka kelak akan tersesat,"},{"id":"60","ar":"اِلَّا مَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا فَاُولٰۤىِٕكَ يَدْخُلُوْنَ الْجَنَّةَ وَلَا يُظْلَمُوْنَ شَيْـًٔا ۙ","bc":"illā man tāba wa āmana wa 'amila ṣāliḥan fa ul&amp;`ika yadkhulụnal-jannata wa lā yuẓlamụna syai`ā","at":"Kecuali orang yang bertobat, beriman dan mengerjakan kebajikan, maka mereka itu akan masuk surga dan tidak dizalimi (dirugikan) sedikit pun,"},{"id":"61","ar":"جَنّٰتِ عَدْنِ ِۨالَّتِيْ وَعَدَ الرَّحْمٰنُ عِبَادَهٗ بِالْغَيْبِۗ اِنَّهٗ كَانَ وَعْدُهٗ مَأْتِيًّا ","bc":"jannāti 'adninillatī wa'adar-raḥmānu 'ibādahụ bil-gaīb, innahụ kāna wa'duhụ ma`tiyyā","at":"yaitu surga ‘Adn yang telah dijanjikan oleh Tuhan Yang Maha Pengasih kepada hamba-hamba-Nya, sekalipun (surga itu) tidak tampak. Sungguh, (janji Allah) itu pasti ditepati."},{"id":"62","ar":"لَا يَسْمَعُوْنَ فِيْهَا لَغْوًا اِلَّا سَلٰمًاۗ وَلَهُمْ رِزْقُهُمْ فِيْهَا بُكْرَةً وَّعَشِيًّا ","bc":"lā yasma'ụna fīhā lagwan illā salāmā, wa lahum rizquhum fīhā bukrataw wa 'asyiyyā","at":"Di dalamnya mereka tidak mendengar perkataan yang tidak berguna, kecuali (ucapan) salam. Dan di dalamnya bagi mereka ada rezeki pagi dan petang."},{"id":"63","ar":"تِلْكَ الْجَنَّةُ الَّتِيْ نُوْرِثُ مِنْ عِبَادِنَا مَنْ كَانَ تَقِيًّا ","bc":"tilkal-jannatullatī nụriṡu min 'ibādinā mang kāna taqiyyā","at":"Itulah surga yang akan Kami wariskan kepada hamba-hamba Kami yang selalu bertakwa."},{"id":"64","ar":"وَمَا نَتَنَزَّلُ اِلَّا بِاَمْرِ رَبِّكَۚ لَهٗ مَا بَيْنَ اَيْدِيْنَا وَمَا خَلْفَنَا وَمَا بَيْنَ ذٰلِكَ وَمَا كَانَ رَبُّكَ نَسِيًّا ۚ","bc":"wa mā natanazzalu illā bi`amri rabbik, lahụ mā baina aidīnā wa mā khalfanā wa mā baina żālika wa mā kāna rabbuka nasiyyā","at":" Dan tidaklah kami (Jibril) turun, kecuali atas perintah Tuhanmu. Milik-Nya segala yang ada di hadapan kita, yang ada di belakang kita, dan segala yang ada di antara keduanya, dan Tuhanmu tidak lupa."},{"id":"65","ar":"رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا فَاعْبُدْهُ وَاصْطَبِرْ لِعِبَادَتِهٖۗ هَلْ تَعْلَمُ لَهٗ سَمِيًّا","bc":"rabbus-samāwāti wal-arḍi wa mā bainahumā fa'bud-hu waṣṭabir li'ibādatih, hal ta'lamu lahụ samiyyā","at":"(Dialah) Tuhan (yang menguasai) langit dan bumi dan segala yang ada di antara keduanya, maka sembahlah Dia dan berteguhhatilah dalam beribadah kepada-Nya. Apakah engkau mengetahui ada sesuatu yang sama dengan-Nya?"},{"id":"66","ar":"وَيَقُوْلُ الْاِنْسَانُ ءَاِذَا مَا مِتُّ لَسَوْفَ اُخْرَجُ حَيًّا ","bc":"wa yaqụlul-insānu a iżā mā mittu lasaufa ukhraju ḥayyā","at":" Dan orang (kafir) berkata, “Betulkah apabila aku telah mati, kelak aku sungguh-sungguh akan dibangkitkan hidup kembali?”"},{"id":"67","ar":"اَوَلَا يَذْكُرُ الْاِنْسَانُ اَنَّا خَلَقْنٰهُ مِنْ قَبْلُ وَلَمْ يَكُ شَيْـًٔا ","bc":"a wa lā yażkurul-insānu annā khalaqnāhu ming qablu wa lam yaku syai`ā","at":"Dan tidakkah manusia itu memikirkan bahwa sesungguhnya Kami telah menciptakannya dahulu, padahal (sebelumnya) dia belum berwujud sama sekali?"},{"id":"68","ar":"فَوَرَبِّكَ لَنَحْشُرَنَّهُمْ وَالشَّيٰطِيْنَ ثُمَّ لَنُحْضِرَنَّهُمْ حَوْلَ جَهَنَّمَ جِثِيًّا ","bc":"fa wa rabbika lanaḥsyurannahum wasy-syayāṭīna ṡumma lanuḥḍirannahum ḥaula jahannama jiṡiyyā","at":"Maka demi Tuhanmu, sungguh, pasti akan Kami kumpulkan mereka bersama setan, kemudian pasti akan Kami datangkan mereka ke sekeliling Jahanam dengan berlutut."},{"id":"69","ar":"ثُمَّ لَنَنْزِعَنَّ مِنْ كُلِّ شِيْعَةٍ اَيُّهُمْ اَشَدُّ عَلَى الرَّحْمٰنِ عِتِيًّا ۚ","bc":"ṡumma lananzi'anna ming kulli syī'atin ayyuhum asyaddu 'alar-raḥmāni 'itiyyā","at":"Kemudian pasti akan Kami tarik dari setiap golongan siapa di antara mereka yang sangat durhaka kepada Tuhan Yang Maha Pengasih."},{"id":"70","ar":"ثُمَّ لَنَحْنُ اَعْلَمُ بِالَّذِيْنَ هُمْ اَوْ لٰى بِهَا صِلِيًّا ","bc":"ṡumma lanaḥnu a'lamu billażīna hum aulā bihā ṣiliyyā","at":"Selanjutnya Kami sungguh lebih mengetahui orang yang seharusnya (dimasukkan) ke dalam neraka."},{"id":"71","ar":"وَاِنْ مِّنْكُمْ اِلَّا وَارِدُهَا ۚ كَانَ عَلٰى رَبِّكَ حَتْمًا مَّقْضِيًّا ۚ","bc":"wa im mingkum illā wāriduhā, kāna 'alā rabbika ḥatmam maqḍiyyā","at":"Dan tidak ada seorang pun di antara kamu yang tidak mendatanginya (neraka). Hal itu bagi Tuhanmu adalah suatu ketentuan yang sudah ditetapkan."},{"id":"72","ar":"ثُمَّ نُنَجِّى الَّذِيْنَ اتَّقَوْا وَّنَذَرُ الظّٰلِمِيْنَ فِيْهَا جِثِيًّا ","bc":"ṡumma nunajjillażīnattaqaw wa nażaruẓ-ẓālimīna fīhā jiṡiyyā","at":" Kemudian Kami akan menyelamatkan orang-orang yang bertakwa dan membiarkan orang-orang yang zalim di dalam (neraka) dalam keadaan berlutut."},{"id":"73","ar":"وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ قَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوْٓاۙ اَيُّ الْفَرِيْقَيْنِ خَيْرٌ مَّقَامًا وَّاَحْسَنُ نَدِيًّا ","bc":"wa iżā tutlā 'alaihim āyātunā bayyināting qālallażīna kafarụ lillażīna āmanū ayyul-farīqaini khairum maqāmaw wa aḥsanu nadiyyā","at":" Dan apabila dibacakan kepada mereka ayat-ayat Kami yang jelas (maksudnya), orang-orang yang kafir berkata kepada orang-orang yang beriman, “Manakah di antara kedua golongan yang lebih baik tempat tinggalnya dan lebih indah tempat pertemuan(nya)?”"},{"id":"74","ar":"وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنْ قَرْنٍ هُمْ اَحْسَنُ اَثَاثًا وَّرِءْيًا ","bc":"wa kam ahlaknā qablahum ming qarnin hum aḥsanu aṡāṡaw wa ri`yā","at":" Dan berapa banyak umat (yang ingkar) yang telah Kami binasakan sebelum mereka, padahal mereka lebih bagus perkakas rumah tangganya dan (lebih sedap) dipandang mata."},{"id":"75","ar":"قُلْ مَنْ كَانَ فِى الضَّلٰلَةِ فَلْيَمْدُدْ لَهُ الرَّحْمٰنُ مَدًّا ەۚ حَتّٰىٓ اِذَا رَاَوْا مَا يُوْعَدُوْنَ اِمَّا الْعَذَابَ وَاِمَّا السَّاعَةَ ۗفَسَيَعْلَمُوْنَ مَنْ هُوَ شَرٌّ مَّكَانًا وَّاَضْعَفُ جُنْدًا","bc":"qul mang kāna fiḍ-ḍalālati falyamdud lahur-raḥmānu maddā, ḥatt&amp; iżā ra`au mā yụ'adụna immal-'ażāba wa immas-sā'ah, fa saya'lamụna man huwa syarrum makānaw wa aḍ'afu jundā","at":"Katakanlah (Muhammad), “Barangsiapa berada dalam kesesatan, maka biarlah Tuhan Yang Maha Pengasih memperpanjang (waktu) baginya; sehingga apabila mereka telah melihat apa yang diancamkan kepada mereka, baik azab maupun Kiamat, maka mereka akan mengetahui siapa yang lebih jelek kedudukannya dan lebih lemah bala tentaranya.”"},{"id":"76","ar":"وَيَزِيْدُ اللّٰهُ الَّذِيْنَ اهْتَدَوْا هُدًىۗ وَالْبٰقِيٰتُ الصّٰلِحٰتُ خَيْرٌ عِنْدَ رَبِّكَ ثَوَابًا وَّخَيْرٌ مَّرَدًّا ","bc":"wa yazīdullāhullażīnahtadau hudā, wal-bāqiyātuṣ-ṣāliḥātu khairun 'inda rabbika ṡawābaw wa khairum maraddā","at":"Dan Allah akan menambah petunjuk kepada mereka yang telah mendapat petunjuk. Dan amal kebajikan yang kekal itu lebih baik pahalanya di sisi Tuhanmu dan lebih baik kesudahannya."},{"id":"77","ar":"اَفَرَاَيْتَ الَّذِيْ كَفَرَ بِاٰيٰتِنَا وَقَالَ لَاُوْتَيَنَّ مَالًا وَّوَلَدًا ۗ","bc":"a fa ra`aitallażī kafara bi`āyātinā wa qāla la`ụtayanna mālaw wa waladā","at":"Lalu apakah engkau telah melihat orang yang mengingkari ayat-ayat Kami dan dia mengatakan, “Pasti aku akan diberi harta dan anak.”"},{"id":"78","ar":"اَطَّلَعَ الْغَيْبَ اَمِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْدًا ۙ","bc":"aṭṭala'al-gaiba amittakhaża 'indar-raḥmāni 'ahdā","at":"Adakah dia melihat yang gaib atau dia telah membuat perjanjian di sisi Tuhan Yang Maha Pengasih?"},{"id":"79","ar":"كَلَّاۗ سَنَكْتُبُ مَا يَقُوْلُ وَنَمُدُّ لَهٗ مِنَ الْعَذَابِ مَدًّا ۙ","bc":"kallā, sanaktubu mā yaqụlu wa namuddu lahụ minal-'ażābi maddā","at":"Sama sekali tidak! Kami akan menulis apa yang dia katakan, dan Kami akan memperpanjang azab untuknya secara sempurna,"},{"id":"80","ar":"وَّنَرِثُهٗ مَا يَقُوْلُ وَيَأْتِيْنَا فَرْدًا ","bc":"wa nariṡuhụ mā yaqụlu wa ya`tīnā fardā","at":"dan Kami akan mewarisi apa yang dia katakan itu, dan dia akan datang kepada Kami seorang diri."},{"id":"81","ar":"وَاتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اٰلِهَةً لِّيَكُوْنُوْا لَهُمْ عِزًّا ۙ","bc":"wattakhażụ min dụnillāhi ālihatal liyakụnụ lahum 'izzā","at":"Dan mereka telah memilih tuhan-tuhan selain Allah, agar tuhan-tuhan itu menjadi pelindung bagi mereka."},{"id":"82","ar":"كَلَّا ۗسَيَكْفُرُوْنَ بِعِبَادَتِهِمْ وَيَكُوْنُوْنَ عَلَيْهِمْ ضِدًّا","bc":"kallā, sayakfurụna bi'ibādatihim wa yakụnụna 'alaihim ḍiddā","at":"Sama sekali tidak! Kelak mereka (sesembahan) itu akan mengingkari penyembahan mereka terhadapnya, dan akan menjadi musuh bagi mereka."},{"id":"83","ar":"اَلَمْ تَرَ اَنَّآ اَرْسَلْنَا الشَّيٰطِيْنَ عَلَى الْكٰفِرِيْنَ تَؤُزُّهُمْ اَزًّا ۙ","bc":"a lam tara ann&amp; arsalnasy-syayāṭīna 'alal-kāfirīna ta`uzzuhum azzā","at":"Tidakkah engkau melihat, bahwa sesungguhnya Kami telah mengutus setan-setan itu kepada orang-orang kafir untuk mendorong mereka (berbuat maksiat) dengan sungguh-sungguh?"},{"id":"84","ar":"فَلَا تَعْجَلْ عَلَيْهِمْۗ اِنَّمَا نَعُدُّ لَهُمْ عَدًّا ۗ","bc":"fa lā ta'jal 'alaihim, innamā na'uddu lahum 'addā","at":"Maka janganlah engkau (Muhammad) tergesa-gesa (memintakan azab) terhadap mereka, karena Kami menghitung dengan hitungan teliti (datangnya hari siksaan) untuk mereka."},{"id":"85","ar":"يَوْمَ نَحْشُرُ الْمُتَّقِيْنَ اِلَى الرَّحْمٰنِ وَفْدًا ","bc":"yauma naḥsyurul-muttaqīna ilar-raḥmāni wafdā","at":"(Ingatlah) pada hari (ketika) Kami mengumpulkan orang-orang yang bertakwa kepada (Allah) Yang Maha Pengasih, bagaikan kafilah yang terhormat,"},{"id":"86","ar":"وَنَسُوْقُ الْمُجْرِمِيْنَ اِلٰى جَهَنَّمَ وِرْدًا ۘ","bc":"wa nasụqul-mujrimīna ilā jahannama wirdā","at":"dan Kami akan menggiring orang yang durhaka ke neraka Jahanam dalam keadaan dahaga."},{"id":"87","ar":"لَا يَمْلِكُوْنَ الشَّفَاعَةَ اِلَّا مَنِ اتَّخَذَ عِنْدَ الرَّحْمٰنِ عَهْدًا ۘ","bc":"lā yamlikụnasy-syafā'ata illā manittakhaża 'indar-raḥmāni 'ahdā","at":" Mereka tidak berhak mendapat syafaat, (pertolongan) kecuali orang yang telah mengadakan perjanjian di sisi (Allah) Yang Maha Pengasih."},{"id":"88","ar":"وَقَالُوا اتَّخَذَ الرَّحْمٰنُ وَلَدًا ۗ","bc":"wa qāluttakhażar-raḥmānu waladā","at":"Dan mereka berkata, “(Allah) Yang Maha Pengasih mempunyai anak.”"},{"id":"89","ar":"لَقَدْ جِئْتُمْ شَيْـًٔا اِدًّا ۙ","bc":"laqad ji`tum syai`an iddā","at":"Sungguh, kamu telah membawa sesuatu yang sangat mungkar,"},{"id":"90","ar":"تَكَادُ السَّمٰوٰتُ يَتَفَطَّرْنَ مِنْهُ وَتَنْشَقُّ الْاَرْضُ وَتَخِرُّ الْجِبَالُ هَدًّا ۙ","bc":"takādus-samāwātu yatafaṭṭarna min-hu wa tansyaqqul-arḍu wa takhirrul-jibālu haddā","at":"hampir saja langit pecah, dan bumi terbelah, dan gunung-gunung runtuh, (karena ucapan itu),"},{"id":"91","ar":"اَنْ دَعَوْا لِلرَّحْمٰنِ وَلَدًا ۚ","bc":"an da'au lir-raḥmāni waladā","at":"karena mereka menganggap (Allah) Yang Maha Pengasih mempunyai anak."},{"id":"92","ar":"وَمَا يَنْۢبَغِيْ لِلرَّحْمٰنِ اَنْ يَّتَّخِذَ وَلَدًا ۗ","bc":"wa mā yambagī lir-raḥmāni ay yattakhiża waladā","at":"Dan tidak mungkin bagi (Allah) Yang Maha Pengasih mempunyai anak."},{"id":"93","ar":"اِنْ كُلُّ مَنْ فِى السَّمٰوٰتِ وَالْاَرْضِ اِلَّآ اٰتِى الرَّحْمٰنِ عَبْدًا ۗ","bc":"ing kullu man fis-samāwāti wal-arḍi ill&amp; ātir-raḥmāni 'abdā","at":"Tidak ada seorang pun di langit dan di bumi, melainkan akan datang kepada (Allah) Yang Maha Pengasih sebagai seorang hamba."},{"id":"94","ar":"لَقَدْ اَحْصٰىهُمْ وَعَدَّهُمْ عَدًّا ۗ","bc":"laqad aḥṣāhum wa 'addahum 'addā","at":"Dia (Allah) benar-benar telah menentukan jumlah mereka dan menghitung mereka dengan hitungan yang teliti."},{"id":"95","ar":"وَكُلُّهُمْ اٰتِيْهِ يَوْمَ الْقِيٰمَةِ فَرْدًا ","bc":"wa kulluhum ātīhi yaumal-qiyāmati fardā","at":"Dan setiap orang dari mereka akan datang kepada Allah sendiri-sendiri pada hari Kiamat."},{"id":"96","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ سَيَجْعَلُ لَهُمُ الرَّحْمٰنُ وُدًّا ","bc":"innallażīna āmanụ wa 'amiluṣ-ṣāliḥāti sayaj'alu lahumur-raḥmānu wuddā","at":"Sungguh, orang-orang yang beriman dan mengerjakan kebajikan, kelak (Allah) Yang Maha Pengasih akan menanamkan rasa kasih sayang (dalam hati mereka)."},{"id":"97","ar":"فَاِنَّمَا يَسَّرْنٰهُ بِلِسَانِكَ لِتُبَشِّرَ بِهِ الْمُتَّقِيْنَ وَتُنْذِرَ بِهٖ قَوْمًا لُّدًّا ","bc":"fa innamā yassarnāhu bilisānika litubasysyira bihil-muttaqīna wa tunżira bihī qaumal luddā","at":"Maka sungguh, telah Kami mudahkan (Al-Qur'an) itu dengan bahasamu (Muhammad), agar dengan itu engkau dapat memberi kabar gembira kepada orang-orang yang bertakwa, dan agar engkau dapat memberi peringatan kepada kaum yang membangkang."},{"id":"98","ar":"وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنْ قَرْنٍۗ هَلْ تُحِسُّ مِنْهُمْ مِّنْ اَحَدٍ اَوْ تَسْمَعُ لَهُمْ رِكْزًا","bc":"wa kam ahlaknā qablahum ming qarn, hal tuḥissu min-hum min aḥadin au tasma'u lahum rikzā","at":"Dan berapa banyak umat yang telah Kami binasakan sebelum mereka. Adakah engkau (Muhammad) melihat salah seorang dari mereka atau engkau mendengar bisikan mereka?"}],muhammad=[{"id":"1","ar":"اَلَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ اَضَلَّ اَعْمَالَهُمْ ","bc":"allażīna kafarụ wa ṣaddụ 'an sabīlillāhi aḍalla a'mālahum","at":"Orang-orang yang kafir dan menghalang-halangi (manusia) dari jalan Allah, Allah menghapus segala amal mereka."},{"id":"2","ar":"وَالَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَاٰمَنُوْا بِمَا نُزِّلَ عَلٰى مُحَمَّدٍ وَّهُوَ الْحَقُّ مِنْ رَّبِّهِمْ ۚ كَفَّرَ عَنْهُمْ سَيِّاٰتِهِمْ وَاَصْلَحَ بَالَهُمْ ","bc":"wallażīna āmanụ wa 'amiluṣ-ṣāliḥāti wa āmanụ bimā nuzzila 'alā muḥammadiw wa huwal-ḥaqqu mir rabbihim, kaffara 'an-hum sayyi`ātihim wa aṣlaḥa bālahum","at":"Dan orang-orang yang beriman (kepada Allah) dan mengerjakan kebajikan serta beriman kepada apa yang diturunkan kepada Muhammad; dan itulah kebenaran dari Tuhan mereka; Allah menghapus kesalahan-kesalahan mereka, dan memperbaiki keadaan mereka."},{"id":"3","ar":"ذٰلِكَ بِاَنَّ الَّذِيْنَ كَفَرُوا اتَّبَعُوا الْبَاطِلَ وَاَنَّ الَّذِيْنَ اٰمَنُوا اتَّبَعُوا الْحَقَّ مِنْ رَّبِّهِمْ ۗ كَذٰلِكَ يَضْرِبُ اللّٰهُ لِلنَّاسِ اَمْثَالَهُمْ ","bc":"żālika bi`annallażīna kafaruttaba'ul-bāṭila wa annallażīna āmanuttaba'ul-ḥaqqa mir rabbihim, każālika yaḍribullāhu lin-nāsi amṡālahum","at":"Yang demikian itu, karena sesungguhnya orang-orang kafir mengikuti yang batil (sesat) dan sesungguhnya orang-orang yang beriman mengikuti kebenaran dari Tuhan mereka. Demikianlah Allah membuat perumpamaan-perumpamaan bagi manusia."},{"id":"4","ar":"فَاِذَا لَقِيْتُمُ الَّذِيْنَ كَفَرُوْا فَضَرْبَ الرِّقَابِۗ حَتّٰٓى اِذَآ اَثْخَنْتُمُوْهُمْ فَشُدُّوا الْوَثَاقَۖ فَاِمَّا مَنًّاۢ بَعْدُ وَاِمَّا فِدَاۤءً حَتّٰى تَضَعَ الْحَرْبُ اَوْزَارَهَا ەۛ ذٰلِكَ ۛ وَلَوْ يَشَاۤءُ اللّٰهُ لَانْتَصَرَ مِنْهُمْ وَلٰكِنْ لِّيَبْلُوَا۟ بَعْضَكُمْ بِبَعْضٍۗ وَالَّذِيْنَ قُتِلُوْا فِيْ سَبِيْلِ اللّٰهِ فَلَنْ يُّضِلَّ اَعْمَالَهُمْ ","bc":"fa iżā laqītumullażīna kafarụ fa ḍarbar-riqāb, ḥattā iżā aṡkhantumụhum fa syuddul-waṡāqa fa immā mannam ba'du wa immā fidā`an ḥattā taḍa'al-ḥarbu auzārahā, żālika walau yasyā`ullāhu lantaṣara min-hum wa lākil liyabluwa ba'ḍakum biba'ḍ, wallażīna qutilụ fī sabīlillāhi fa lay yuḍilla a'mālahum","at":"Maka apabila kamu bertemu dengan orang-orang yang kafir (di medan perang), maka pukullah batang leher mereka. Selanjutnya apabila kamu telah mengalahkan mereka, tawanlah mereka, dan setelah itu kamu boleh membebaskan mereka atau menerima tebusan sampai perang selesai. Demikianlah, dan sekiranya Allah menghendaki niscaya Dia membinasakan mereka, tetapi Dia hendak menguji kamu satu sama lain. Dan orang-orang yang gugur di jalan Allah, Allah tidak menyia-nyiakan amal mereka."},{"id":"5","ar":"سَيَهْدِيْهِمْ وَيُصْلِحُ بَالَهُمْۚ","bc":"sayahdīhim wa yuṣliḥu bālahum","at":"Allah akan memberi petunjuk kepada mereka dan memperbaiki keadaan mereka,"},{"id":"6","ar":"وَيُدْخِلُهُمُ الْجَنَّةَ عَرَّفَهَا لَهُمْ ","bc":"wa yudkhiluhumul-jannata 'arrafahā lahum","at":"dan memasukkan mereka ke dalam surga yang telah diperkenalkan-Nya kepada mereka."},{"id":"7","ar":"يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اِنْ تَنْصُرُوا اللّٰهَ يَنْصُرْكُمْ وَيُثَبِّتْ اَقْدَامَكُمْ ","bc":"yā ayyuhallażīna āmanū in tanṣurullāha yanṣurkum wa yuṡabbit aqdāmakum","at":"Wahai orang-orang yang beriman! Jika kamu menolong (agama) Allah, niscaya Dia akan menolongmu dan meneguhkan kedudukanmu."},{"id":"8","ar":"وَالَّذِيْنَ كَفَرُوْا فَتَعْسًا لَّهُمْ وَاَضَلَّ اَعْمَالَهُمْ ","bc":"wallażīna kafarụ fa ta'sal lahum wa aḍalla a'mālahum","at":"Dan orang-orang yang kafir maka celakalah mereka dan Allah menghapus segala amalnya."},{"id":"9","ar":"ذٰلِكَ بِاَنَّهُمْ كَرِهُوْا مَآ اَنْزَلَ اللّٰهُ فَاَحْبَطَ اَعْمَالَهُمْ ","bc":"żālika bi`annahum karihụ mā anzalallāhu fa aḥbaṭa a'mālahum","at":"Yang demikian itu karena mereka membenci apa (Al-Qur'an) yang diturunkan Allah, maka Allah menghapus segala amal mereka."},{"id":"10","ar":"۞ اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْ ۗ دَمَّرَ اللّٰهُ عَلَيْهِمْ ۖوَلِلْكٰفِرِيْنَ اَمْثَالُهَا ","bc":"a fa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna 'āqibatullażīna ming qablihim, dammarallāhu 'alaihim wa lil-kāfirīna amṡāluhā","at":"Maka apakah mereka tidak pernah mengadakan perjalanan di bumi sehingga dapat memperhatikan bagaimana kesudahan orang-orang yang sebelum mereka. Allah telah membinasakan mereka, dan bagi orang-orang kafir akan menerima (nasib) yang serupa itu."},{"id":"11","ar":"ذٰلِكَ بِاَنَّ اللّٰهَ مَوْلَى الَّذِيْنَ اٰمَنُوْا وَاَنَّ الْكٰفِرِيْنَ لَا مَوْلٰى لَهُمْ","bc":"żālika bi`annallāha maulallażīna āmanụ wa annal-kāfirīna lā maulā lahum","at":"Yang demikian itu karena Allah pelindung bagi orang-orang yang beriman; sedang orang-orang kafir tidak ada pelindung bagi mereka."},{"id":"12","ar":"اِنَّ اللّٰهَ يُدْخِلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ جَنّٰتٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ ۗوَالَّذِيْنَ كَفَرُوْا يَتَمَتَّعُوْنَ وَيَأْكُلُوْنَ كَمَا تَأْكُلُ الْاَنْعَامُ وَالنَّارُ مَثْوًى لَّهُمْ ","bc":"innallāha yudkhilullażīna āmanụ wa 'amiluṣ-ṣāliḥāti jannātin tajrī min taḥtihal-an-hār, wallażīna kafarụ yatamatta'ụna wa ya`kulụna kamā ta`kulul-an'āmu wan-nāru maṡwal lahum","at":"Sungguh, Allah akan memasukkan orang-orang yang beriman dan mengerjakan kebajikan ke dalam surga yang mengalir di bawahnya sungai-sungai. Dan orang-orang yang kafir menikmati kesenangan (dunia) dan mereka makan seperti hewan makan; dan (kelak) nerakalah tempat tinggal bagi mereka."},{"id":"13","ar":"وَكَاَيِّنْ مِّنْ قَرْيَةٍ هِيَ اَشَدُّ قُوَّةً مِّنْ قَرْيَتِكَ الَّتِيْٓ اَخْرَجَتْكَۚ اَهْلَكْنٰهُمْ فَلَا نَاصِرَ لَهُمْ ","bc":"wa ka`ayyim ming qaryatin hiya asyaddu quwwatam ming qaryatikallatī akhrajatk, ahlaknāhum fa lā nāṣira lahum","at":"Dan betapa banyak negeri yang (penduduknya) lebih kuat dari (penduduk) negerimu (Muhammad) yang telah mengusirmu itu. Kami telah membinasakan mereka; maka tidak ada seorang pun yang menolong mereka."},{"id":"14","ar":"اَفَمَنْ كَانَ عَلٰى بَيِّنَةٍ مِّنْ رَّبِّهٖ كَمَنْ زُيِّنَ لَهٗ سُوْۤءُ عَمَلِهٖ وَاتَّبَعُوْٓا اَهْوَاۤءَهُمْ ","bc":"a fa mang kāna 'alā bayyinatim mir rabbihī kaman zuyyina lahụ sū`u 'amalihī wattaba'ū ahwā`ahum","at":"Maka apakah orang yang berpegang pada keterangan yang datang dari Tuhannya sama dengan orang yang dijadikan terasa indah baginya perbuatan buruknya itu dan mengikuti keinginannya?"},{"id":"15","ar":"مَثَلُ الْجَنَّةِ الَّتِيْ وُعِدَ الْمُتَّقُوْنَ ۗفِيْهَآ اَنْهٰرٌ مِّنْ مَّاۤءٍ غَيْرِ اٰسِنٍۚ وَاَنْهٰرٌ مِّنْ لَّبَنٍ لَّمْ يَتَغَيَّرْ طَعْمُهٗ ۚوَاَنْهٰرٌ مِّنْ خَمْرٍ لَّذَّةٍ لِّلشّٰرِبِيْنَ ەۚ وَاَنْهٰرٌ مِّنْ عَسَلٍ مُّصَفًّى ۗوَلَهُمْ فِيْهَا مِنْ كُلِّ الثَّمَرٰتِ وَمَغْفِرَةٌ مِّنْ رَّبِّهِمْ ۗ كَمَنْ هُوَ خَالِدٌ فِى النَّارِ وَسُقُوْا مَاۤءً حَمِيْمًا فَقَطَّعَ اَمْعَاۤءَهُمْ","bc":"maṡalul-jannatillatī wu'idal-muttaqụn, fīhā an-hārum mim mā`in gairi āsin, wa an-hārum mil labanil lam yatagayyar ṭa'muh, wa an-hārum min khamril lażżatil lisy-syāribīn, wa an-hārum min 'asalim muṣaffā, wa lahum fīhā ming kulliṡ-ṡamarāti wa magfiratum mir rabbihim, kaman huwa khālidun fin-nāri wa suqụ mā`an ḥamīman fa qaṭṭa'a am'ā`ahum","at":"Perumpamaan taman surga yang dijanjikan kepada orang-orang yang bertakwa; di sana ada sungai-sungai yang airnya tidak payau, dan sungai-sungai air susu yang tidak berubah rasanya, dan sungai-sungai khamar (anggur yang tidak memabukkan) yang lezat rasanya bagi peminumnya dan sungai-sungai madu yang murni. Di dalamnya mereka memperoleh segala macam buah-buahan dan ampunan dari Tuhan mereka. Samakah mereka dengan orang yang kekal dalam neraka, dan diberi minuman dengan air yang mendidih sehingga ususnya terpotong-potong?"},{"id":"16","ar":"وَمِنْهُمْ مَّنْ يَّسْتَمِعُ اِلَيْكَۚ حَتّٰىٓ اِذَا خَرَجُوْا مِنْ عِنْدِكَ قَالُوْا لِلَّذِيْنَ اُوْتُوا الْعِلْمَ مَاذَا قَالَ اٰنِفًا ۗ اُولٰۤىِٕكَ الَّذِيْنَ طَبَعَ اللّٰهُ عَلٰى قُلُوْبِهِمْ وَاتَّبَعُوْٓا اَهْوَاۤءَهُمْ","bc":"wa min-hum may yastami'u ilaīk, ḥattā iżā kharajụ min 'indika qālụ lillażīna ụtul-'ilma māżā qāla ānifā, ulā`ikallażīna ṭaba'allāhu 'alā qulụbihim wattaba'ū ahwā`ahum","at":"Dan di antara mereka ada orang yang mendengarkan perkataanmu (Muhammad), tetapi apabila mereka telah keluar dari sisimu mereka berkata kepada orang yang telah diberi ilmu (sahabat-sahabat Nabi), “Apakah yang dikatakannya tadi?” Mereka itulah orang-orang yang dikunci hatinya oleh Allah dan mengikuti keinginannya."},{"id":"17","ar":"وَالَّذِيْنَ اهْتَدَوْا زَادَهُمْ هُدًى وَّاٰتٰىهُمْ تَقْوٰىهُمْ ","bc":"wallażīnahtadau zādahum hudaw wa ātāhum taqwāhum","at":"Dan orang-orang yang mendapat petunjuk, Allah akan menambah petunjuk kepada mereka dan menganugerahi ketakwaan mereka."},{"id":"18","ar":"فَهَلْ يَنْظُرُوْنَ اِلَّا السَّاعَةَ اَنْ تَأْتِيَهُمْ بَغْتَةً ۚ فَقَدْ جَاۤءَ اَشْرَاطُهَا ۚ فَاَنّٰى لَهُمْ اِذَا جَاۤءَتْهُمْ ذِكْرٰىهُمْ ","bc":"fa hal yanẓurụna illas-sā'ata an ta`tiyahum bagtah, fa qad jā`a asyrāṭuhā, fa annā lahum iżā jā`at-hum żikrāhum","at":"Maka apalagi yang mereka tunggu-tunggu selain hari Kiamat, yang akan datang kepada mereka secara tiba-tiba, karena tanda-tandanya sungguh telah datang. Maka apa gunanya bagi mereka kesadaran mereka itu apabila (hari Kiamat) itu sudah datang?"},{"id":"19","ar":"فَاعْلَمْ اَنَّهٗ لَآ اِلٰهَ اِلَّا اللّٰهُ وَاسْتَغْفِرْ لِذَنْۢبِكَ وَلِلْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِۚ وَاللّٰهُ يَعْلَمُ مُتَقَلَّبَكُمْ وَمَثْوٰىكُمْ","bc":"fa'lam annahụ lā ilāha illallāhu wastagfir liżambika wa lil-mu`minīna wal-mu`mināt, wallāhu ya'lamu mutaqallabakum wa maṡwākum","at":"Maka ketahuilah, bahwa tidak ada tuhan (yang patut disembah) selain Allah dan mohonlah ampunan atas dosamu dan atas (dosa) orang-orang mukmin, laki-laki dan perempuan. Dan Allah mengetahui tempat usaha dan tempat tinggalmu."},{"id":"20","ar":"وَيَقُوْلُ الَّذِيْنَ اٰمَنُوْا لَوْلَا نُزِّلَتْ سُوْرَةٌ ۚفَاِذَآ اُنْزِلَتْ سُوْرَةٌ مُّحْكَمَةٌ وَّذُكِرَ فِيْهَا الْقِتَالُ ۙرَاَيْتَ الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ يَّنْظُرُوْنَ اِلَيْكَ نَظَرَ الْمَغْشِيِّ عَلَيْهِ مِنَ الْمَوْتِۗ فَاَوْلٰى لَهُمْۚ ","bc":"wa yaqụlullażīna āmanụ lau lā nuzzilat sụrah, fa iżā unzilat sụratum muḥkamatuw wa żukira fīhal-qitālu ra`aitallażīna fī qulụbihim maraḍuy yanẓurụna ilaika naẓaral-magsyiyyi 'alaihi minal-maụt, fa aulā lahum","at":"Dan orang-orang yang beriman berkata, “Mengapa tidak ada suatu surah (tentang perintah jihad) yang diturunkan?” Maka apabila ada suatu surah diturunkan yang jelas maksudnya dan di dalamnya tersebut (perintah) perang, engkau melihat orang-orang yang di dalam hatinya ada penyakit akan memandang kepadamu seperti pandangan orang yang pingsan karena takut mati. Tetapi itu lebih pantas bagi mereka."},{"id":"21","ar":"طَاعَةٌ وَّقَوْلٌ مَّعْرُوْفٌۗ فَاِذَا عَزَمَ الْاَمْرُۗ فَلَوْ صَدَقُوا اللّٰهَ لَكَانَ خَيْرًا لَّهُمْۚ","bc":"ṭā'atuw wa qaulum ma'rụf, fa iżā 'azamal-amr, falau ṣadaqullāha lakāna khairal lahum","at":"(Yang lebih baik bagi mereka adalah) taat (kepada Allah) dan bertutur kata yang baik. Sebab apabila perintah (perang) ditetapkan (mereka tidak menyukainya). Padahal jika mereka benar-benar (beriman) kepada Allah, niscaya yang demikian itu lebih baik bagi mereka."},{"id":"22","ar":"فَهَلْ عَسَيْتُمْ اِنْ تَوَلَّيْتُمْ اَنْ تُفْسِدُوْا فِى الْاَرْضِ وَتُقَطِّعُوْٓا اَرْحَامَكُمْ ","bc":"fa hal 'asaitum in tawallaitum an tufsidụ fil-arḍi wa tuqaṭṭi'ū ar-ḥāmakum","at":"Maka apakah sekiranya kamu berkuasa, kamu akan berbuat kerusakan di bumi dan memutuskan hubungan kekeluargaan?"},{"id":"23","ar":"اُولٰۤىِٕكَ الَّذِيْنَ لَعَنَهُمُ اللّٰهُ فَاَصَمَّهُمْ وَاَعْمٰٓى اَبْصَارَهُمْ ","bc":"ulā`ikallażīna la'anahumullāhu fa aṣammahum wa a'mā abṣārahum","at":"Mereka itulah orang-orang yang dikutuk Allah; lalu dibuat tuli (pendengarannya) dan dibutakan penglihatannya."},{"id":"24","ar":"اَفَلَا يَتَدَبَّرُوْنَ الْقُرْاٰنَ اَمْ عَلٰى قُلُوْبٍ اَقْفَالُهَا ","bc":"a fa lā yatadabbarụnal-qur`āna am 'alā qulụbin aqfāluhā","at":"Maka tidakkah mereka menghayati Al-Qur'an ataukah hati mereka sudah terkunci?"},{"id":"25","ar":"اِنَّ الَّذِيْنَ ارْتَدُّوْا عَلٰٓى اَدْبَارِهِمْ مِّنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدَى الشَّيْطٰنُ سَوَّلَ لَهُمْۗ وَاَمْلٰى لَهُمْ ","bc":"innallażīnartaddụ 'alā adbārihim mim ba'di mā tabayyana lahumul-hudasy-syaiṭānu sawwala lahum, wa amlā lahum","at":"Sesungguhnya orang-orang yang berbalik (kepada kekafiran) setelah petunjuk itu jelas bagi mereka, setanlah yang merayu mereka dan memanjangkan angan-angan mereka."},{"id":"26","ar":"ذٰلِكَ بِاَنَّهُمْ قَالُوْا لِلَّذِيْنَ كَرِهُوْا مَا نَزَّلَ اللّٰهُ سَنُطِيْعُكُمْ فِيْ بَعْضِ الْاَمْرِۚ وَاللّٰهُ يَعْلَمُ اِسْرَارَهُمْ ","bc":"żālika bi`annahum qālụ lillażīna karihụ mā nazzalallāhu sanuṭī'ukum fī ba'ḍil-amr, wallāhu ya'lamu isrārahum","at":"Yang demikian itu, karena sesungguhnya mereka telah mengatakan kepada orang-orang (Yahudi) yang tidak senang kepada apa yang diturunkan Allah, “Kami akan mematuhi kamu dalam beberapa urusan,” tetapi Allah mengetahui rahasia mereka."},{"id":"27","ar":"فَكَيْفَ اِذَا تَوَفَّتْهُمُ الْمَلٰۤىِٕكَةُ يَضْرِبُوْنَ وُجُوْهَهُمْ وَاَدْبَارَهُمْ ","bc":"fa kaifa iżā tawaffat-humul-malā`ikatu yaḍribụna wujụhahum wa adbārahum","at":"Maka bagaimana (nasib mereka) apabila malaikat (maut) mencabut nyawa mereka, memukul wajah dan punggung mereka?"},{"id":"28","ar":"ذٰلِكَ بِاَنَّهُمُ اتَّبَعُوْا مَآ اَسْخَطَ اللّٰهَ وَكَرِهُوْا رِضْوَانَهٗ فَاَحْبَطَ اَعْمَالَهُمْ ","bc":"żālika bi`annahumuttaba'ụ mā askhaṭallāha wa karihụ riḍwānahụ fa aḥbaṭa a'mālahum","at":"Yang demikian itu, karena sesungguhnya mereka mengikuti apa yang menimbulkan kemurkaan Allah dan membenci (apa yang menimbulkan) keridaan-Nya; sebab itu Allah menghapus segala amal mereka."},{"id":"29","ar":"اَمْ حَسِبَ الَّذِيْنَ فِيْ قُلُوْبِهِمْ مَّرَضٌ اَنْ لَّنْ يُّخْرِجَ اللّٰهُ اَضْغَانَهُمْ ","bc":"am ḥasiballażīna fī qulụbihim maraḍun al lay yukhrijallāhu aḍgānahum","at":"Atau apakah orang-orang yang dalam hatinya ada penyakit mengira bahwa Allah tidak akan menampakkan kedengkian mereka?"},{"id":"30","ar":"وَلَوْ نَشَاۤءُ لَاَرَيْنٰكَهُمْ فَلَعَرَفْتَهُمْ بِسِيْمٰهُمْ ۗوَلَتَعْرِفَنَّهُمْ فِيْ لَحْنِ الْقَوْلِۗ وَاللّٰهُ يَعْلَمُ اَعْمَالَكُمْ ","bc":"walau nasyā`u la`arainākahum fa la'araftahum bisīmāhum, wa lata'rifannahum fī laḥnil-qaụl, wallāhu ya'lamu a'mālakum","at":"Dan sekiranya Kami menghendaki, niscaya Kami perlihatkan mereka kepadamu (Muhammad) sehingga engkau benar-benar dapat mengenal mereka dengan tanda-tandanya. Dan engkau benar-benar akan mengenal mereka dari nada bicaranya, dan Allah mengetahui segala amal perbuatan kamu."},{"id":"31","ar":"وَلَنَبْلُوَنَّكُمْ حَتّٰى نَعْلَمَ الْمُجٰهِدِيْنَ مِنْكُمْ وَالصّٰبِرِيْنَۙ وَنَبْلُوَا۟ اَخْبَارَكُمْ ","bc":"wa lanabluwannakum ḥattā na'lamal-mujāhidīna mingkum waṣ-ṣābirīna wa nabluwa akhbārakum","at":"Dan sungguh, Kami benar-benar akan menguji kamu sehingga Kami mengetahui orang-orang yang benar-benar berjihad dan bersabar di antara kamu; dan akan Kami uji perihal kamu."},{"id":"32","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ وَشَاۤقُّوا الرَّسُوْلَ مِنْۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ الْهُدٰى لَنْ يَّضُرُّوا اللّٰهَ شَيْـًٔاۗ وَسَيُحْبِطُ اَعْمَالَهُمْ ","bc":"innallażīna kafarụ wa ṣaddụ 'an sabīlillāhi wa syāqqur-rasụla mim ba'di mā tabayyana lahumul-hudā lay yaḍurrullāha syai`ā, wa sayuḥbiṭu a'mālahum","at":"Sesungguhnya orang-orang yang kafir dan menghalang-halangi (orang lain) dari jalan Allah serta memusuhi rasul setelah ada petunjuk yang jelas bagi mereka, mereka tidak akan dapat memberi mudarat (bahaya) kepada Allah sedikit pun. Dan kelak Allah menghapus segala amal mereka."},{"id":"33","ar":"۞ يٰٓاَيُّهَا الَّذِيْنَ اٰمَنُوْٓا اَطِيْعُوا اللّٰهَ وَاَطِيْعُوا الرَّسُوْلَ وَلَا تُبْطِلُوْٓا اَعْمَالَكُمْ","bc":"yā ayyuhallażīna āmanū aṭī'ullāha wa aṭī'ur-rasụla wa lā tubṭilū a'mālakum","at":"Wahai orang-orang yang beriman! Taatlah kepada Allah dan taatlah kepada Rasul dan janganlah kamu merusakkan segala amalmu."},{"id":"34","ar":"اِنَّ الَّذِيْنَ كَفَرُوْا وَصَدُّوْا عَنْ سَبِيْلِ اللّٰهِ ثُمَّ مَاتُوْا وَهُمْ كُفَّارٌ فَلَنْ يَّغْفِرَ اللّٰهُ لَهُمْ ","bc":"innallażīna kafarụ wa ṣaddụ 'an sabīlillāhi ṡumma mātụ wa hum kuffārun fa lay yagfirallāhu lahum","at":"Sesungguhnya orang-orang yang kafir dan menghalang-halangi (orang lain) dari jalan Allah kemudian mereka mati dalam keadaan kafir, maka Allah tidak akan mengampuni mereka."},{"id":"35","ar":"فَلَا تَهِنُوْا وَتَدْعُوْٓا اِلَى السَّلْمِۖ وَاَنْتُمُ الْاَعْلَوْنَۗ وَاللّٰهُ مَعَكُمْ وَلَنْ يَّتِرَكُمْ اَعْمَالَكُمْ ","bc":"fa lā tahinụ wa tad'ū ilas-salmi wa antumul-a'laụn, wallāhu ma'akum wa lay yatirakum a'mālakum","at":"Maka janganlah kamu lemah dan mengajak damai karena kamulah yang lebih unggul dan Allah (pun) beserta kamu dan Dia tidak akan mengurangi segala amalmu."},{"id":"36","ar":"اِنَّمَا الْحَيٰوةُ الدُّنْيَا لَعِبٌ وَّلَهْوٌ ۗوَاِنْ تُؤْمِنُوْا وَتَتَّقُوْا يُؤْتِكُمْ اُجُوْرَكُمْ وَلَا يَسْـَٔلْكُمْ اَمْوَالَكُمْ ","bc":"innamal-ḥayātud-dun-yā la'ibuw wa lahw, wa in tu`minụ wa tattaqụ yu`tikum ujụrakum wa lā yas`alkum amwālakum","at":"Sesungguhnya kehidupan dunia itu hanyalah permainan dan senda gurau. Jika kamu beriman serta bertakwa, Allah akan memberikan pahala kepadamu dan Dia tidak akan meminta hartamu."},{"id":"37","ar":"اِنْ يَّسْـَٔلْكُمُوْهَا فَيُحْفِكُمْ تَبْخَلُوْا وَيُخْرِجْ اَضْغَانَكُمْ ","bc":"iy yas`alkumụhā fa yuḥfikum tabkhalụ wa yukhrij aḍgānakum","at":"Sekiranya Dia meminta harta kepadamu lalu mendesak kamu (agar memberikan semuanya) niscaya kamu akan kikir dan Dia akan menampakkan kedengkianmu."},{"id":"38","ar":"هٰٓاَنْتُمْ هٰٓؤُلَاۤءِ تُدْعَوْنَ لِتُنْفِقُوْا فِيْ سَبِيْلِ اللّٰهِۚ فَمِنْكُمْ مَّنْ يَّبْخَلُ ۚوَمَنْ يَّبْخَلْ فَاِنَّمَا يَبْخَلُ عَنْ نَّفْسِهٖ ۗوَاللّٰهُ الْغَنِيُّ وَاَنْتُمُ الْفُقَرَاۤءُ ۗ وَاِنْ تَتَوَلَّوْا يَسْتَبْدِلْ قَوْمًا غَيْرَكُمْۙ ثُمَّ لَا يَكُوْنُوْٓا اَمْثَالَكُمْ ","bc":"hā`antum hā`ulā`i tud'auna litunfiqụ fī sabīlillāh, fa mingkum may yabkhal, wa may yabkhal fa innamā yabkhalu 'an nafsih, wallāhul-ganiyyu wa antumul-fuqarā`, wa in tatawallau yastabdil-qauman gairakum ṡumma lā yakụnū amṡālakum","at":"Ingatlah, kamu adalah orang-orang yang diajak untuk menginfakkan (hartamu) di jalan Allah. Lalu di antara kamu ada orang yang kikir, dan barangsiapa kikir maka sesungguhnya dia kikir terhadap dirinya sendiri. Dan Allah-lah Yang Mahakaya dan kamulah yang membutuhkan (karunia-Nya). Dan jika kamu berpaling (dari jalan yang benar) Dia akan menggantikan (kamu) dengan kaum yang lain, dan mereka tidak akan (durhaka) seperti kamu (ini)."}],nuh=[{"id":"1","ar":"اِنَّآ اَرْسَلْنَا نُوْحًا اِلٰى قَوْمِهٖٓ اَنْ اَنْذِرْ قَوْمَكَ مِنْ قَبْلِ اَنْ يَّأْتِيَهُمْ عَذَابٌ اَلِيْمٌ ","bc":"innā arsalnā nụhan ilā qaumihī an anżir qaumaka ming qabli ay ya`tiyahum 'ażābun alīm","at":"Sesungguhnya Kami telah mengutus Nuh kepada kaumnya (dengan perintah), “Berilah kaummu peringatan sebelum datang kepadanya azab yang pedih.” "},{"id":"2","ar":"قَالَ يٰقَوْمِ اِنِّيْ لَكُمْ نَذِيْرٌ مُّبِيْنٌۙ","bc":"qāla yā qaumi innī lakum nażīrum mubīn","at":"Dia (Nuh) berkata, “Wahai kaumku! Sesungguhnya aku ini seorang pemberi peringatan yang menjelaskan kepada kamu, "},{"id":"3","ar":"اَنِ اعْبُدُوا اللّٰهَ وَاتَّقُوْهُ وَاَطِيْعُوْنِۙ ","bc":"ani'budullāha wattaqụhu wa aṭī'ụn","at":"(yaitu) sembahlah Allah, bertakwalah kepada-Nya dan taatlah kepadaku, "},{"id":"4","ar":"يَغْفِرْ لَكُمْ مِّنْ ذُنُوْبِكُمْ وَيُؤَخِّرْكُمْ اِلٰٓى اَجَلٍ مُّسَمًّىۗ اِنَّ اَجَلَ اللّٰهِ اِذَا جَاۤءَ لَا يُؤَخَّرُۘ لَوْ كُنْتُمْ تَعْلَمُوْنَ ","bc":"yagfir lakum min żunụbikum wa yu`akhkhirkum ilā ajalim musammā, inna ajalallāhi iżā jā`a lā yu`akhkhar, lau kuntum ta'lamụn","at":"niscaya Dia mengampuni sebagian dosa-dosamu dan menangguhkan kamu (memanjangkan umurmu) sampai pada batas waktu yang ditentukan. Sungguh, ketetapan Allah itu apabila telah datang tidak dapat ditunda, seandainya kamu mengetahui.” "},{"id":"5","ar":"قَالَ رَبِّ اِنِّيْ دَعَوْتُ قَوْمِيْ لَيْلًا وَّنَهَارًاۙ","bc":"qāla rabbi innī da'autu qaumī lailaw wa nahārā","at":"Dia (Nuh) berkata, “Ya Tuhanku, sesungguhnya aku telah menyeru kaumku siang dan malam,"},{"id":"6","ar":"فَلَمْ يَزِدْهُمْ دُعَاۤءِيْٓ اِلَّا فِرَارًا ","bc":"fa lam yazid-hum du'ā`ī illā firārā","at":"tetapi seruanku itu tidak menambah (iman) mereka, justru mereka lari (dari kebenaran)."},{"id":"7","ar":"وَاِنِّيْ كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوْٓا اَصَابِعَهُمْ فِيْٓ اٰذَانِهِمْ وَاسْتَغْشَوْا ثِيَابَهُمْ وَاَصَرُّوْا وَاسْتَكْبَرُوا اسْتِكْبَارًاۚ ","bc":"wa innī kullamā da'autuhum litagfira lahum ja'alū aṣābi'ahum fī āżānihim wastagsyau ṡiyābahum wa aṣarrụ wastakbarustikbārā","at":"Dan sesungguhnya aku setiap kali menyeru mereka (untuk beriman) agar Engkau mengampuni mereka, mereka memasukkan anak jarinya ke telinganya dan menutupkan bajunya (ke wajahnya) dan mereka tetap (mengingkari) dan sangat menyombongkan diri."},{"id":"8","ar":"ثُمَّ اِنِّيْ دَعَوْتُهُمْ جِهَارًاۙ","bc":"ṡumma innī da'autuhum jihārā","at":"Lalu sesungguhnya aku menyeru mereka dengan cara terang-terangan."},{"id":"9","ar":"ثُمَّ اِنِّيْٓ اَعْلَنْتُ لَهُمْ وَاَسْرَرْتُ لَهُمْ اِسْرَارًاۙ","bc":"ṡumma innī a'lantu lahum wa asrartu lahum isrārā","at":"Kemudian aku menyeru mereka secara terbuka dan dengan diam-diam,"},{"id":"10","ar":"فَقُلْتُ اسْتَغْفِرُوْا رَبَّكُمْ اِنَّهٗ كَانَ غَفَّارًاۙ","bc":"fa qultustagfirụ rabbakum innahụ kāna gaffārā","at":"maka aku berkata (kepada mereka), “Mohonlah ampunan kepada Tuhanmu, Sungguh, Dia Maha Pengampun, "},{"id":"11","ar":"يُّرْسِلِ السَّمَاۤءَ عَلَيْكُمْ مِّدْرَارًاۙ","bc":"yursilis-samā`a 'alaikum midrārā","at":"niscaya Dia akan menurunkan hujan yang lebat dari langit kepadamu, "},{"id":"12","ar":"وَّيُمْدِدْكُمْ بِاَمْوَالٍ وَّبَنِيْنَ وَيَجْعَلْ لَّكُمْ جَنّٰتٍ وَّيَجْعَلْ لَّكُمْ اَنْهٰرًاۗ","bc":"wa yumdidkum bi`amwāliw wa banīna wa yaj'al lakum jannātiw wa yaj'al lakum an-hārā","at":"dan Dia memperbanyak harta dan anak-anakmu, dan mengadakan kebun-kebun untukmu dan mengadakan sungai-sungai untukmu.” "},{"id":"13","ar":"مَا لَكُمْ لَا تَرْجُوْنَ لِلّٰهِ وَقَارًاۚ","bc":"mā lakum lā tarjụna lillāhi waqārā","at":"Mengapa kamu tidak takut akan kebesaran Allah? "},{"id":"14","ar":"وَقَدْ خَلَقَكُمْ اَطْوَارًا ","bc":"wa qad khalaqakum aṭwārā","at":"Dan sungguh, Dia telah menciptakan kamu dalam beberapa tingkatan (kejadian). "},{"id":"15","ar":"اَلَمْ تَرَوْا كَيْفَ خَلَقَ اللّٰهُ سَبْعَ سَمٰوٰتٍ طِبَاقًاۙ","bc":"a lam tarau kaifa khalaqallāhu sab'a samāwātin ṭibāqā","at":"Tidakkah kamu memperhatikan bagai-mana Allah telah menciptakan tujuh langit berlapis-lapis?"},{"id":"16","ar":"وَّجَعَلَ الْقَمَرَ فِيْهِنَّ نُوْرًا وَّجَعَلَ الشَّمْسَ سِرَاجًا ","bc":"wa ja'alal-qamara fīhinna nụraw wa ja'alasy-syamsa sirājā","at":"Dan di sana Dia menciptakan bulan yang bercahaya dan menjadikan matahari sebagai pelita (yang cemerlang)?"},{"id":"17","ar":"وَاللّٰهُ اَنْۢبَتَكُمْ مِّنَ الْاَرْضِ نَبَاتًاۙ","bc":"wallāhu ambatakum minal-arḍi nabātā","at":"Dan Allah menumbuhkan kamu dari tanah, tumbuh (berangsur-angsur), "},{"id":"18","ar":"ثُمَّ يُعِيْدُكُمْ فِيْهَا وَيُخْرِجُكُمْ اِخْرَاجًا ","bc":"ṡumma yu'īdukum fīhā wa yukhrijukum ikhrājā","at":"kemudian Dia akan mengembalikan kamu ke dalamnya (tanah) dan mengeluarkan kamu (pada hari Kiamat) dengan pasti. "},{"id":"19","ar":"وَاللّٰهُ جَعَلَ لَكُمُ الْاَرْضَ بِسَاطًاۙ","bc":"wallāhu ja'ala lakumul-arḍa bisāṭā","at":"Dan Allah menjadikan bumi untukmu sebagai hamparan, "},{"id":"20","ar":"لِّتَسْلُكُوْا مِنْهَا سُبُلًا فِجَاجًا ","bc":"litaslukụ min-hā subulan fijājā","at":"agar kamu dapat pergi kian kemari di jalan-jalan yang luas. "},{"id":"21","ar":"قَالَ نُوْحٌ رَّبِّ اِنَّهُمْ عَصَوْنِيْ وَاتَّبَعُوْا مَنْ لَّمْ يَزِدْهُ مَالُهٗ وَوَلَدُهٗٓ اِلَّا خَسَارًاۚ ","bc":"qāla nụḥur rabbi innahum 'aṣaunī wattaba'ụ mal lam yazid-hu māluhụ wa waladuhū illā khasārā","at":"Nuh berkata, “Ya Tuhanku, sesungguhnya mereka durhaka kepadaku, dan mereka mengikuti orang-orang yang harta dan anak-anaknya hanya menambah kerugian baginya, "},{"id":"22","ar":"وَمَكَرُوْا مَكْرًا كُبَّارًاۚ","bc":"wa makarụ makrang kubbārā","at":"dan mereka melakukan tipu daya yang sangat besar.” "},{"id":"23","ar":"وَقَالُوْا لَا تَذَرُنَّ اٰلِهَتَكُمْ وَلَا تَذَرُنَّ وَدًّا وَّلَا سُوَاعًا ەۙ وَّلَا يَغُوْثَ وَيَعُوْقَ وَنَسْرًاۚ","bc":"wa qālụ lā tażarunna ālihatakum wa lā tażarunna waddaw wa lā suwā'aw wa lā yagụṡa wa ya'ụqa wa nasrā","at":"Dan mereka berkata, “Jangan sekali-kali kamu meninggalkan (penyembahan) tuhan-tuhan kamu dan jangan pula sekali-kali kamu meninggalkan (penyembahan) Wadd, dan jangan pula Suwa‘, Yagus, Ya‘uq dan Nasr.” "},{"id":"24","ar":"وَقَدْ اَضَلُّوْا كَثِيْرًا ەۚ وَلَا تَزِدِ الظّٰلِمِيْنَ اِلَّا ضَلٰلًا","bc":"wa qad aḍallụ kaṡīrā, wa lā tazidiẓ-ẓālimīna illā ḍalālā","at":"Dan sungguh, mereka telah menyesatkan banyak orang; dan janganlah Engkau tambahkan bagi orang-orang yang zalim itu selain kesesatan. "},{"id":"25","ar":"مِمَّا خَطِيْۤـٰٔتِهِمْ اُغْرِقُوْا فَاُدْخِلُوْا نَارًا ەۙ فَلَمْ يَجِدُوْا لَهُمْ مِّنْ دُوْنِ اللّٰهِ اَنْصَارًا","bc":"mimmā khaṭī`ātihim ugriqụ fa udkhilụ nāran fa lam yajidụ lahum min dụnillāhi anṣārā","at":"Disebabkan kesalahan-kesalahan mereka, mereka ditenggelamkan lalu dimasukkan ke neraka, maka mereka tidak mendapat penolong selain Allah."},{"id":"26","ar":"وَقَالَ نُوْحٌ رَّبِّ لَا تَذَرْ عَلَى الْاَرْضِ مِنَ الْكٰفِرِيْنَ دَيَّارًا ","bc":"wa qāla nụḥur rabbi lā tażar 'alal-arḍi minal-kāfirīna dayyārā","at":"Dan Nuh berkata, “Ya Tuhanku, janganlah Engkau biarkan seorang pun di antara orang-orang kafir itu tinggal di atas bumi."},{"id":"27","ar":"اِنَّكَ اِنْ تَذَرْهُمْ يُضِلُّوْا عِبَادَكَ وَلَا يَلِدُوْٓا اِلَّا فَاجِرًا كَفَّارًا ","bc":"innaka in tażar-hum yuḍillụ 'ibādaka wa lā yalidū illā fājirang kaffārā","at":"Sesungguhnya jika Engkau biarkan mereka tinggal, niscaya mereka akan menyesatkan hamba-hamba-Mu, dan mereka hanya akan melahirkan anak-anak yang jahat dan tidak tahu bersyukur. "},{"id":"28","ar":"رَبِّ اغْفِرْ لِيْ وَلِوَالِدَيَّ وَلِمَنْ دَخَلَ بَيْتِيَ مُؤْمِنًا وَّلِلْمُؤْمِنِيْنَ وَالْمُؤْمِنٰتِۗ وَلَا تَزِدِ الظّٰلِمِيْنَ اِلَّا تَبَارًا","bc":"rabbigfir lī wa liwālidayya wa liman dakhala baitiya mu`minaw wa lil-mu`minīna wal-mu`mināt, wa lā tazidiẓ-ẓālimīna illā tabārā","at":"Ya Tuhanku, ampunilah aku, ibu bapakku, dan siapa pun yang memasuki rumahku dengan beriman dan semua orang yang beriman laki-laki dan perempuan. Dan janganlah Engkau tambahkan bagi orang-orang yang zalim itu selain kehancuran.”"}],qaf=[{"id":"1","ar":" قۤ ۗوَالْقُرْاٰنِ الْمَجِيْدِ ۖ ","bc":"qāf, wal-qur`ānil-majīd","at":"Qaf. Demi Al-Qur'an yang mulia. "},{"id":"2","ar":"بَلْ عَجِبُوْٓا اَنْ جَاۤءَهُمْ مُّنْذِرٌ مِّنْهُمْ فَقَالَ الْكٰفِرُوْنَ هٰذَا شَيْءٌ عَجِيْبٌ ۚ","bc":"bal 'ajibū an jā`ahum munżirum min-hum fa qālal-kāfirụna hāżā syai`un 'ajīb","at":"(Mereka tidak menerimanya) bahkan mereka tercengang karena telah datang kepada mereka seorang pemberi peringatan dari (kalangan) mereka sendiri, maka berkatalah orang-orang kafir, “Ini adalah suatu yang sangat ajaib.” "},{"id":"3","ar":"ءَاِذَا مِتْنَا وَكُنَّا تُرَابًا ۚ ذٰلِكَ رَجْعٌۢ بَعِيْدٌ ","bc":"a iżā mitnā wa kunnā turābā, żālika raj'um ba'īd","at":"Apakah apabila kami telah mati dan sudah menjadi tanah (akan kembali lagi)? Itu adalah suatu pengembalian yang tidak mungkin. "},{"id":"4","ar":"قَدْ عَلِمْنَا مَا تَنْقُصُ الْاَرْضُ مِنْهُمْ ۚوَعِنْدَنَا كِتٰبٌ حَفِيْظٌ ","bc":"qad 'alimnā mā tangquṣul-arḍu min-hum, wa 'indanā kitābun ḥafīẓ","at":"Sungguh, Kami telah mengetahui apa yang ditelan bumi dari (tubuh) mereka, sebab pada Kami ada kitab (catatan) yang terpelihara baik. "},{"id":"5","ar":"بَلْ كَذَّبُوْا بِالْحَقِّ لَمَّا جَاۤءَهُمْ فَهُمْ فِيْٓ اَمْرٍ مَّرِيْجٍ ","bc":"bal każżabụ bil-ḥaqqi lammā jā`ahum fa hum fī amrim marīj","at":"Bahkan mereka telah mendustakan kebenaran ketika (kebenaran itu) datang kepada mereka, maka mereka berada dalam keadaan kacau balau. "},{"id":"6","ar":"اَفَلَمْ يَنْظُرُوْٓا اِلَى السَّمَاۤءِ فَوْقَهُمْ كَيْفَ بَنَيْنٰهَا وَزَيَّنّٰهَا وَمَا لَهَا مِنْ فُرُوْجٍ ","bc":"a fa lam yanẓurū ilas-samā`i fauqahum kaifa banaināhā wa zayyannāhā wa mā lahā min furụj","at":"Maka tidakkah mereka memperhatikan langit yang ada di atas mereka, bagaimana cara Kami membangunnya dan menghiasinya dan tidak terdapat retak-retak sedikit pun? "},{"id":"7","ar":"وَالْاَرْضَ مَدَدْنٰهَا وَاَلْقَيْنَا فِيْهَا رَوَاسِيَ وَاَنْۢبَتْنَا فِيْهَا مِنْ كُلِّ زَوْجٍۢ بَهِيْجٍۙ ","bc":"wal-arḍa madadnāhā wa alqainā fīhā rawāsiya wa ambatnā fīhā ming kulli zaujim bahīj","at":"Dan bumi yang Kami hamparkan dan Kami pancangkan di atasnya gunung-gunung yang kokoh dan Kami tumbuhkan di atasnya tanam-tanaman yang indah, "},{"id":"8","ar":"تَبْصِرَةً وَّذِكْرٰى لِكُلِّ عَبْدٍ مُّنِيْبٍ ","bc":"tabṣirataw wa żikrā likulli 'abdim munīb","at":"untuk menjadi pelajaran dan peringatan bagi setiap hamba yang kembali (tunduk kepada Allah). "},{"id":"9","ar":"وَنَزَّلْنَا مِنَ السَّمَاۤءِ مَاۤءً مُّبٰرَكًا فَاَنْۢبَتْنَا بِهٖ جَنّٰتٍ وَّحَبَّ الْحَصِيْدِۙ","bc":"wa nazzalnā minas-samā`i mā`am mubārakan fa ambatnā bihī jannātiw wa ḥabbal-ḥaṣīd","at":"Dan dari langit Kami turunkan air yang memberi berkah lalu Kami tumbuhkan dengan (air) itu pepohonan yang rindang dan biji-bijian yang dapat dipanen. "},{"id":"10","ar":"وَالنَّخْلَ بٰسِقٰتٍ لَّهَا طَلْعٌ نَّضِيْدٌۙ","bc":"wan-nakhla bāsiqātil lahā ṭal'un naḍīd","at":"Dan pohon kurma yang tinggi-tinggi yang mempunyai mayang yang bersusun-susun, "},{"id":"11","ar":"رِّزْقًا لِّلْعِبَادِۙ وَاَحْيَيْنَا بِهٖ بَلْدَةً مَّيْتًاۗ كَذٰلِكَ الْخُرُوْجُ ","bc":"rizqal lil-'ibādi wa aḥyainā bihī baldatam maitā, każālikal-khurụj","at":"(sebagai) rezeki bagi hamba-hamba (Kami), dan Kami hidupkan dengan (air) itu negeri yang mati (tandus). Seperti itulah terjadinya kebangkitan (dari kubur). "},{"id":"12","ar":"كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ وَّاَصْحٰبُ الرَّسِّ وَثَمُوْدُ ","bc":"każżabat qablahum qaumu nụḥiw wa aṣ-ḥābur-rassi wa ṡamụd","at":"Sebelum mereka, kaum Nuh, penduduk Rass dan Samud telah mendustakan (rasul-rasul), "},{"id":"13","ar":"وَعَادٌ وَّفِرْعَوْنُ وَاِخْوَانُ لُوْطٍۙ","bc":"wa 'āduw wa fir'aunu wa ikhwānu lụṭ","at":"dan (demikian juga) kaum ‘Ad, kaum Fir‘aun dan kaum Lut, "},{"id":"14","ar":"وَّاَصْحٰبُ الْاَيْكَةِ وَقَوْمُ تُبَّعٍۗ كُلٌّ كَذَّبَ الرُّسُلَ فَحَقَّ وَعِيْدِ","bc":"wa aṣ-ḥābul-aikati wa qaumu tubba', kullung każżabar-rusula fa ḥaqqa wa'īd","at":"dan (juga) penduduk Aikah serta kaum Tubba‘. Semuanya telah mendustakan rasul-rasul maka berlakulah ancaman-Ku (atas mereka). "},{"id":"15","ar":"اَفَعَيِيْنَا بِالْخَلْقِ الْاَوَّلِۗ بَلْ هُمْ فِيْ لَبْسٍ مِّنْ خَلْقٍ جَدِيْدٍ","bc":"a fa 'ayīnā bil-khalqil-awwal, bal hum fī labsim min khalqin jadīd","at":"Maka apakah Kami letih dengan penciptaan yang pertama? (Sama sekali tidak) bahkan mereka dalam keadaan ragu-ragu tentang penciptaan yang baru. "},{"id":"16","ar":"وَلَقَدْ خَلَقْنَا الْاِنْسَانَ وَنَعْلَمُ مَا تُوَسْوِسُ بِهٖ نَفْسُهٗ ۖوَنَحْنُ اَقْرَبُ اِلَيْهِ مِنْ حَبْلِ الْوَرِيْدِ ","bc":"wa laqad khalaqnal-insāna wa na'lamu mā tuwaswisu bihī nafsuh, wa naḥnu aqrabu ilaihi min ḥablil-warīd","at":"Dan sungguh, Kami telah menciptakan manusia dan mengetahui apa yang dibisikkan oleh hatinya, dan Kami lebih dekat kepadanya daripada urat lehernya. "},{"id":"17","ar":"اِذْ يَتَلَقَّى الْمُتَلَقِّيٰنِ عَنِ الْيَمِيْنِ وَعَنِ الشِّمَالِ قَعِيْدٌ ","bc":"iż yatalaqqal-mutalaqqiyāni 'anil-yamīni wa 'anisy-syimāli qa'īd","at":"(Ingatlah) ketika dua malaikat mencatat (perbuatannya), yang satu duduk di sebelah kanan dan yang lain di sebelah kiri. "},{"id":"18","ar":"مَا يَلْفِظُ مِنْ قَوْلٍ اِلَّا لَدَيْهِ رَقِيْبٌ عَتِيْدٌ ","bc":"mā yalfiẓu ming qaulin illā ladaihi raqībun 'atīd","at":"Tidak ada suatu kata yang diucapkannya melainkan ada di sisinya malaikat pengawas yang selalu siap (mencatat). "},{"id":"19","ar":"وَجَاۤءَتْ سَكْرَةُ الْمَوْتِ بِالْحَقِّ ۗذٰلِكَ مَا كُنْتَ مِنْهُ تَحِيْدُ","bc":"wa jā`at sakratul-mauti bil-ḥaqq, żālika mā kunta min-hu taḥīd","at":"Dan datanglah sakaratul maut dengan sebenar-benarnya. Itulah yang dahulu hendak kamu hindari. "},{"id":"20","ar":"وَنُفِخَ فِى الصُّوْرِۗ ذٰلِكَ يَوْمُ الْوَعِيْدِ ","bc":"wa nufikha fiṣ-ṣụr, żālika yaumul-wa'īd","at":"Dan ditiuplah sangkakala. Itulah hari yang diancamkan. "},{"id":"21","ar":"وَجَاۤءَتْ كُلُّ نَفْسٍ مَّعَهَا سَاۤىِٕقٌ وَّشَهِيْدٌ ","bc":"wa jā`at kullu nafsim ma'ahā sā`iquw wa syahīd","at":"Setiap orang akan datang bersama (malaikat) penggiring dan (malaikat) saksi. "},{"id":"22","ar":"لَقَدْ كُنْتَ فِيْ غَفْلَةٍ مِّنْ هٰذَا فَكَشَفْنَا عَنْكَ غِطَاۤءَكَ فَبَصَرُكَ الْيَوْمَ حَدِيْدٌ ","bc":"laqad kunta fī gaflatim min hāżā fa kasyafnā 'angka giṭā`aka fa baṣarukal-yauma ḥadīd","at":"Sungguh, kamu dahulu lalai tentang (peristiwa) ini, maka Kami singkapkan tutup (yang menutupi) matamu, sehingga penglihatanmu pada hari ini sangat tajam. "},{"id":"23","ar":"وَقَالَ قَرِيْنُهٗ هٰذَا مَا لَدَيَّ عَتِيْدٌۗ","bc":"wa qāla qarīnuhụ hāżā mā ladayya 'atīd","at":"Dan (malaikat) yang menyertainya berkata, “Inilah (catatan perbuatan) yang ada padaku.” "},{"id":"24","ar":"اَلْقِيَا فِيْ جَهَنَّمَ كُلَّ كَفَّارٍ عَنِيْدٍ ","bc":"alqiyā fī jahannama kulla kaffārin 'anīd","at":"(Allah berfirman), “Lemparkanlah olehmu berdua ke dalam neraka Jahanam semua orang yang sangat ingkar dan keras kepala, "},{"id":"25","ar":"مَنَّاعٍ لِّلْخَيْرِ مُعْتَدٍ مُّرِيْبٍۙ","bc":"mannā'il lil-khairi mu'tadim murīb","at":"yang sangat enggan melakukan kebajikan, melampaui batas dan bersikap ragu-ragu, "},{"id":"26","ar":" ۨالَّذِيْ جَعَلَ مَعَ اللّٰهِ اِلٰهًا اٰخَرَ فَاَلْقِيٰهُ فِى الْعَذَابِ الشَّدِيْدِ ","bc":"allażī ja'ala ma'allāhi ilāhan ākhara fa alqiyāhu fil-'ażābisy-syadīd","at":"yang mempersekutukan Allah dengan tuhan lain, maka lemparkanlah dia ke dalam azab yang keras.” "},{"id":"27","ar":"۞ قَالَ قَرِيْنُهٗ رَبَّنَا مَآ اَطْغَيْتُهٗ وَلٰكِنْ كَانَ فِيْ ضَلٰلٍۢ بَعِيْدٍ ","bc":"qāla qarīnuhụ rabbanā mā aṭgaituhụ wa lāking kāna fī ḍalālim ba'īd","at":"(Setan) yang menyertainya berkata (pula), “Ya Tuhan kami, aku tidak menyesatkannya tetapi dia sendiri yang berada dalam kesesatan yang jauh.” "},{"id":"28","ar":"قَالَ لَا تَخْتَصِمُوْا لَدَيَّ وَقَدْ قَدَّمْتُ اِلَيْكُمْ بِالْوَعِيْدِ ","bc":"qāla lā takhtaṣimụ ladayya wa qad qaddamtu ilaikum bil-wa'īd","at":"(Allah) berfirman, “Janganlah kamu bertengkar di hadapan-Ku, dan sungguh, dahulu Aku telah memberikan ancaman kepadamu. "},{"id":"29","ar":"مَا يُبَدَّلُ الْقَوْلُ لَدَيَّ وَمَآ اَنَا۠ بِظَلَّامٍ لِّلْعَبِيْدِ ","bc":"mā yubaddalul-qaulu ladayya wa mā ana biẓallāmil lil-'abīd","at":"Keputusan-Ku tidak dapat diubah dan Aku tidak menzalimi hamba-hamba-Ku.” "},{"id":"30","ar":"يَوْمَ نَقُوْلُ لِجَهَنَّمَ هَلِ امْتَلَـْٔتِ وَتَقُوْلُ هَلْ مِنْ مَّزِيْدٍ ","bc":"yauma naqụlu lijahannama halimtala`ti wa taqụlu hal mim mazīd","at":"(Ingatlah) pada hari (ketika) Kami bertanya kepada Jahanam, “Apakah kamu sudah penuh?” Ia menjawab, “Masih adakah tambahan?” "},{"id":"31","ar":"وَاُزْلِفَتِ الْجَنَّةُ لِلْمُتَّقِيْنَ غَيْرَ بَعِيْدٍ ","bc":"wa uzlifatil-jannatu lil-muttaqīna gaira ba'īd","at":"Sedangkan surga didekatkan kepada orang-orang yang bertakwa pada tempat yang tidak jauh (dari mereka). "},{"id":"32","ar":"هٰذَا مَا تُوْعَدُوْنَ لِكُلِّ اَوَّابٍ حَفِيْظٍۚ","bc":"hāżā mā tụ'adụna likulli awwābin ḥafīẓ","at":"(Kepada mereka dikatakan), “Inilah nikmat yang dijanjikan kepadamu, (yaitu) kepada setiap hamba yang senantiasa bertobat (kepada Allah) dan memelihara (semua peraturan-peraturan-Nya). "},{"id":"33","ar":"مَنْ خَشِيَ الرَّحْمٰنَ بِالْغَيْبِ وَجَاۤءَ بِقَلْبٍ مُّنِيْبٍۙ","bc":"man khasyiyar-raḥmāna bil-gaibi wa jā`a biqalbim munīb","at":"(Yaitu) orang yang takut kepada Allah Yang Maha Pengasih sekalipun tidak kelihatan (olehnya) dan dia datang dengan hati yang bertobat, "},{"id":"34","ar":" ۨادْخُلُوْهَا بِسَلٰمٍ ۗذٰلِكَ يَوْمُ الْخُلُوْدِ ","bc":"udkhulụhā bisalām, żālika yaumul-khulụd","at":"masuklah ke (dalam surga) dengan aman dan damai. Itulah hari yang abadi.” "},{"id":"35","ar":"لَهُمْ مَّا يَشَاۤءُوْنَ فِيْهَا وَلَدَيْنَا مَزِيْدٌ ","bc":"lahum mā yasyā`ụna fīhā wa ladainā mazīd","at":"Mereka di dalamnya memperoleh apa yang mereka kehendaki, dan pada Kami ada tambahannya. "},{"id":"36","ar":"وَكَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنْ قَرْنٍ هُمْ اَشَدُّ مِنْهُمْ بَطْشًا فَنَقَّبُوْا فِى الْبِلَادِۗ هَلْ مِنْ مَّحِيْصٍ ","bc":"wa kam ahlaknā qablahum ming qarnin hum asyaddu min-hum baṭsyan fa naqqabụ fil-bilād, hal mim maḥīṣ","at":"Dan betapa banyak umat yang telah Kami binasakan sebelum mereka, (padahal) mereka lebih hebat kekuatannya daripada mereka (umat yang belakangan) ini. Mereka pernah menjelajah di beberapa negeri. Adakah tempat pelarian (dari kebinasaan bagi mereka)? "},{"id":"37","ar":"اِنَّ فِيْ ذٰلِكَ لَذِكْرٰى لِمَنْ كَانَ لَهٗ قَلْبٌ اَوْ اَلْقَى السَّمْعَ وَهُوَ شَهِيْدٌ ","bc":"inna fī żālika lażikrā limang kāna lahụ qalbun au alqas-sam'a wa huwa syahīd","at":"Sungguh, pada yang demikian itu pasti terdapat peringatan bagi orang-orang yang mempunyai hati atau yang menggunakan pendengarannya, sedang dia menyaksikannya. "},{"id":"38","ar":"وَلَقَدْ خَلَقْنَا السَّمٰوٰتِ وَالْاَرْضَ وَمَا بَيْنَهُمَا فِيْ سِتَّةِ اَيَّامٍۖ وَّمَا مَسَّنَا مِنْ لُّغُوْبٍ ","bc":"wa laqad khalaqnas-samāwāti wal-arḍa wa mā bainahumā fī sittati ayyāmiw wa mā massanā mil lugụb","at":"Dan sungguh, Kami telah menciptakan langit dan bumi dan apa yang ada antara keduanya dalam enam masa, dan Kami tidak merasa letih sedikit pun. "},{"id":"39","ar":"فَاصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوْعِ الشَّمْسِ وَقَبْلَ الْغُرُوْبِ ","bc":"faṣbir 'alā mā yaqụlụna wa sabbiḥ biḥamdi rabbika qabla ṭulụ'isy-syamsi wa qablal-gurụb","at":"Maka bersabarlah engkau (Muhammad) terhadap apa yang mereka katakan dan bertasbihlah dengan memuji Tuhanmu sebelum matahari terbit dan sebelum terbenam. "},{"id":"40","ar":"وَمِنَ الَّيْلِ فَسَبِّحْهُ وَاَدْبَارَ السُّجُوْدِ ","bc":"wa minal-laili fa sabbiḥ-hu wa adbāras-sujụd","at":"Dan bertasbihlah kepada-Nya pada malam hari dan setiap selesai salat. "},{"id":"41","ar":"وَاسْتَمِعْ يَوْمَ يُنَادِ الْمُنَادِ مِنْ مَّكَانٍ قَرِيْبٍ ","bc":"wastami' yauma yunādil-munādi mim makāning qarīb","at":"Dan dengarkanlah (seruan) pada hari (ketika) penyeru (malaikat) menyeru dari tempat yang dekat. "},{"id":"42","ar":"يَوْمَ يَسْمَعُوْنَ الصَّيْحَةَ بِالْحَقِّ ۗذٰلِكَ يَوْمُ الْخُرُوْجِ ","bc":"yauma yasma'ụnaṣ-ṣaiḥata bil-ḥaqq, żālika yaumul-khurụj","at":"(Yaitu) pada hari (ketika) mereka men-dengar suara dahsyat dengan sebenarnya. Itulah hari keluar (dari kubur). "},{"id":"43","ar":"اِنَّا نَحْنُ نُحْيٖ وَنُمِيْتُ وَاِلَيْنَا الْمَصِيْرُۙ","bc":"innā naḥnu nuḥyī wa numītu wa ilainal-maṣīr","at":"Sungguh, Kami yang menghidupkan dan mematikan dan kepada Kami tempat kembali (semua makhluk). "},{"id":"44","ar":"يَوْمَ تَشَقَّقُ الْاَرْضُ عَنْهُمْ سِرَاعًا ۗذٰلِكَ حَشْرٌ عَلَيْنَا يَسِيْرٌ ","bc":"yauma tasyaqqaqul-arḍu 'an-hum sirā'ā, żālika ḥasyrun 'alainā yasīr","at":"(Yaitu) pada hari (ketika) bumi terbelah, mereka keluar dengan cepat. Yang demikian itu adalah pengumpulan yang mudah bagi Kami. "},{"id":"45","ar":"نَحْنُ اَعْلَمُ بِمَا يَقُوْلُوْنَ وَمَآ اَنْتَ عَلَيْهِمْ بِجَبَّارٍۗ فَذَكِّرْ بِالْقُرْاٰنِ مَنْ يَّخَافُ وَعِيْدِ ","bc":"naḥnu a'lamu bimā yaqụlụna wa mā anta 'alaihim bijabbār, fażakkir bil-qur`āni may yakhāfu wa'īd","at":"Kami lebih mengetahui tentang apa yang mereka katakan, dan engkau (Muhammad) bukanlah seorang pemaksa terhadap mereka. Maka berilah peringatan dengan Al-Qur'an kepada siapa pun yang takut kepada ancaman-Ku. "}],quraisy=[{"id":"1","ar":"لِاِيْلٰفِ قُرَيْشٍۙ","bc":"li`īlāfi quraīsy","at":"Karena kebiasaan orang-orang Quraisy,"},{"id":"2","ar":"اٖلٰفِهِمْ رِحْلَةَ الشِّتَاۤءِ وَالصَّيْفِۚ","bc":"īlāfihim riḥlatasy-syitā`i waṣ-ṣaīf","at":"(yaitu) kebiasaan mereka bepergian pada musim dingin dan musim panas."},{"id":"3","ar":"فَلْيَعْبُدُوْا رَبَّ هٰذَا الْبَيْتِۙ","bc":"falya'budụ rabba hāżal-baīt","at":"Maka hendaklah mereka menyembah Tuhan (pemilik) rumah ini (Ka‘bah),"},{"id":"4","ar":"الَّذِيْٓ اَطْعَمَهُمْ مِّنْ جُوْعٍ ەۙ وَّاٰمَنَهُمْ مِّنْ خَوْفٍ","bc":"allażī aṭ'amahum min jụ'iw wa āmanahum min khaụf","at":"yang telah memberi makanan kepada mereka untuk menghilangkan lapar dan mengamankan mereka dari rasa ketakutan."}],saba=[{"id":"1","ar":"اَلْحَمْدُ لِلّٰهِ الَّذِيْ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَلَهُ الْحَمْدُ فِى الْاٰخِرَةِۗ وَهُوَ الْحَكِيْمُ الْخَبِيْرُ","bc":"al-ḥamdu lillāhillażī lahụ mā fis-samāwāti wa mā fil-arḍi wa lahul-ḥamdu fil`ākhirah, wa huwal-ḥakīmul-khabīr","at":"Segala puji bagi Allah yang memiliki apa yang ada di langit dan apa yang ada di bumi dan segala puji di akhirat bagi Allah. Dan Dialah Yang Mahabijaksana, Mahateliti. "},{"id":"2","ar":"يَعْلَمُ مَا يَلِجُ فِى الْاَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنْزِلُ مِنَ السَّمَاۤءِ وَمَا يَعْرُجُ فِيْهَاۗ وَهُوَ الرَّحِيْمُ الْغَفُوْرُ","bc":"ya'lamu mā yaliju fil-arḍi wa mā yakhruju min-hā wa mā yanzilu minas-samā`i wa mā ya'ruju fīhā, wa huwar-raḥīmul-gafụr","at":"Dia mengetahui apa yang masuk ke dalam bumi, apa yang keluar darinya, apa yang turun dari langit dan apa yang naik kepadanya. Dan Dialah Yang Maha Penyayang, Maha Pengampun."},{"id":"3","ar":"وَقَالَ الَّذِيْنَ كَفَرُوْا لَا تَأْتِيْنَا السَّاعَةُ ۗقُلْ بَلٰى وَرَبِّيْ لَتَأْتِيَنَّكُمْۙ عٰلِمِ الْغَيْبِۙ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِ وَلَآ اَصْغَرُ مِنْ ذٰلِكَ وَلَآ اَكْبَرُ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍۙ ","bc":"wa qālallażīna kafarụ lā ta`tīnas-sā'ah, qul balā wa rabbī lata`tiyannakum 'ālimil-gaibi lā ya'zubu 'an-hu miṡqālu żarratin fis-samāwāti wa lā fil-arḍi wa lā aṣgaru min żālika wa lā akbaru illā fī kitābim mubīn","at":"Dan orang-orang yang kafir berkata, “Hari Kiamat itu tidak akan datang kepada kami.” Katakanlah, “Pasti datang, demi Tuhanku yang mengetahui yang gaib, Kiamat itu pasti akan datang kepadamu. Tidak ada yang tersembunyi bagi-Nya sekalipun seberat zarrah baik yang di langit maupun yang di bumi, yang lebih kecil dari itu atau yang lebih besar, semuanya (tertulis) dalam Kitab yang jelas (Lauh Mahfuzh),”"},{"id":"4","ar":"لِّيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِۗ اُولٰۤىِٕكَ لَهُمْ مَّغْفِرَةٌ وَّرِزْقٌ كَرِيْمٌ ","bc":"liyajziyallażīna āmanụ wa 'amiluṣ-ṣāliḥāt, ulā`ika lahum magfiratuw wa rizqung karīm","at":"agar Dia (Allah) memberi balasan kepada orang-orang yang beriman dan mengerjakan kebajikan. Mereka memperoleh ampunan dan rezeki yang mulia (surga)."},{"id":"5","ar":"وَالَّذِيْنَ سَعَوْ فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ لَهُمْ عَذَابٌ مِّنْ رِّجْزٍ اَلِيْمٌ ","bc":"wallażīna sa'au fī āyātinā mu'ājizīna ulā`ika lahum 'ażābum mir rijzin alīm","at":"Dan orang-orang yang berusaha untuk (menentang) ayat-ayat Kami dengan anggapan mereka dapat melemahkan (menggagalkan azab Kami), mereka itu akan memperoleh azab, yaitu azab yang sangat pedih."},{"id":"6","ar":"وَيَرَى الَّذِيْنَ اُوْتُوا الْعِلْمَ الَّذِيْٓ اُنْزِلَ اِلَيْكَ مِنْ رَّبِّكَ هُوَ الْحَقَّۙ وَيَهْدِيْٓ اِلٰى صِرَاطِ الْعَزِيْزِ الْحَمِيْدِ ","bc":"wa yarallażīna ụtul-'ilmallażī unzila ilaika mir rabbika huwal-ḥaqqa wa yahdī ilā ṣirāṭil-'azīzil-ḥamīd","at":"Dan orang-orang yang diberi ilmu (Ahli Kitab) berpendapat bahwa (wahyu) yang diturunkan kepadamu (Muhammad) dari Tuhanmu itulah yang benar dan memberi petunjuk (bagi manusia) kepada jalan (Allah) Yang Mahaperkasa, Maha Terpuji."},{"id":"7","ar":"وَقَالَ الَّذِيْنَ كَفَرُوْا هَلْ نَدُلُّكُمْ عَلٰى رَجُلٍ يُّنَبِّئُكُمْ اِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍۙ اِنَّكُمْ لَفِيْ خَلْقٍ جَدِيْدٍۚ ","bc":"wa qālallażīna kafarụ hal nadullukum 'alā rajuliy yunabbi`ukum iżā muzziqtum kulla mumazzaqin innakum lafī khalqin jadīd","at":"Dan orang-orang kafir berkata (kepada teman-temannya), “Maukah kami tunjukkan kepadamu seorang laki-laki yang memberitakan kepadamu bahwa apabila badanmu telah hancur sehancur-hancurnya, kamu pasti (akan dibangkitkan kembali) dalam ciptaan yang baru."},{"id":"8","ar":"اَفْتَرٰى عَلَى اللّٰهِ كَذِبًا اَمْ بِهٖ جِنَّةٌ ۗبَلِ الَّذِيْنَ لَا يُؤْمِنُوْنَ بِالْاٰخِرَةِ فِى الْعَذَابِ وَالضَّلٰلِ الْبَعِيْدِ ","bc":"aftarā 'alallāhi każiban am bihī jinnah, balillażīna lā yu`minụna bil-ākhirati fil-'ażābi waḍ-ḍalālil-ba'īd","at":"Apakah dia mengada-adakan kebohongan terhadap Allah atau sakit gila?” (Tidak), tetapi orang-orang yang tidak beriman kepada akhirat itu berada dalam siksaan dan kesesatan yang jauh."},{"id":"9","ar":"اَفَلَمْ يَرَوْا اِلٰى مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِۗ اِنْ نَّشَأْ نَخْسِفْ بِهِمُ الْاَرْضَ اَوْ نُسْقِطْ عَلَيْهِمْ كِسَفًا مِّنَ السَّمَاۤءِۗ اِنَّ فِيْ ذٰلِكَ لَاٰيَةً لِّكُلِّ عَبْدٍ مُّنِيْبٍ ","bc":"a fa lam yarau ilā mā baina aidīhim wa mā khalfahum minas-samā`i wal-arḍ, in nasya` nakhsif bihimul-arḍa au nusqiṭ 'alaihim kisafam minas-samā`, inna fī żālika la`āyatal likulli 'abdim munīb","at":"Maka apakah mereka tidak memperhatikan langit dan bumi yang ada di hadapan dan di belakang mereka? Jika Kami menghendaki, niscaya Kami benamkan mereka di bumi atau Kami jatuhkan kepada mereka kepingan-kepingan dari langit. Sungguh, pada yang demikian itu benar-benar terdapat tanda (kekuasaan Allah) bagi setiap hamba yang kembali (kepada-Nya)."},{"id":"10","ar":"۞ وَلَقَدْ اٰتَيْنَا دَاوٗدَ مِنَّا فَضْلًاۗ يٰجِبَالُ اَوِّبِيْ مَعَهٗ وَالطَّيْرَ ۚوَاَلَنَّا لَهُ الْحَدِيْدَۙ ","bc":"wa laqad ātainā dāwụda minnā faḍlā, yā jibālu awwibī ma'ahụ waṭ-ṭaīr, wa alannā lahul-ḥadīd","at":"Dan sungguh, Telah Kami berikan kepada Dawud karunia dari Kami. (Kami berfirman), “Wahai gunung-gunung dan burung-burung! Bertasbihlah berulang-ulang bersama Dawud,” dan Kami telah melunakkan besi untuknya,"},{"id":"11","ar":"اَنِ اعْمَلْ سٰبِغٰتٍ وَّقَدِّرْ فِى السَّرْدِ وَاعْمَلُوْا صَالِحًاۗ اِنِّيْ بِمَا تَعْمَلُوْنَ بَصِيْرٌ ","bc":"ani'mal sābigātiw wa qaddir fis-sardi wa'malụ ṣāliḥā, innī bimā ta'malụna baṣīr","at":"yaitu) buatlah baju besi yang besar-besar dan ukurlah anyamannya; dan kerjakanlah kebajikan. Sungguh, Aku Maha Melihat apa yang kamu kerjakan."},{"id":"12","ar":"وَلِسُلَيْمٰنَ الرِّيْحَ غُدُوُّهَا شَهْرٌ وَّرَوَاحُهَا شَهْرٌۚ وَاَسَلْنَا لَهٗ عَيْنَ الْقِطْرِۗ وَمِنَ الْجِنِّ مَنْ يَّعْمَلُ بَيْنَ يَدَيْهِ بِاِذْنِ رَبِّهٖۗ وَمَنْ يَّزِغْ مِنْهُمْ عَنْ اَمْرِنَا نُذِقْهُ مِنْ عَذَابِ السَّعِيْرِ ","bc":"wa lisulaimānar-rīḥa guduwwuhā syahruw wa rawāḥuhā syahr, wa asalnā lahụ 'ainal-qiṭr, wa minal-jinni may ya'malu baina yadaihi bi`iżni rabbih, wa may yazig min-hum 'an amrinā nużiq-hu min 'ażābis-sa'īr","at":"Dan Kami (tundukkan) angin bagi Sulaiman, yang perjalanannya pada waktu pagi sama dengan perjalanan sebulan dan perjalanannya pada waktu sore sama dengan perjalanan sebulan (pula) dan Kami alirkan cairan tembaga baginya. Dan sebagian dari jin ada yang bekerja di hadapannya (di bawah kekuasaannya) dengan izin Tuhannya. Dan siapa yang menyimpang di antara mereka dari perintah Kami, Kami rasakan kepadanya azab neraka yang apinya menyala-nyala."},{"id":"13","ar":"يَعْمَلُوْنَ لَهٗ مَا يَشَاۤءُ مِنْ مَّحَارِيْبَ وَتَمَاثِيْلَ وَجِفَانٍ كَالْجَوَابِ وَقُدُوْرٍ رّٰسِيٰتٍۗ اِعْمَلُوْٓا اٰلَ دَاوٗدَ شُكْرًا ۗوَقَلِيْلٌ مِّنْ عِبَادِيَ الشَّكُوْرُ ","bc":"ya'malụna lahụ mā yasyā`u mim maḥārība wa tamāṡīla wa jifāning kal-jawābi wa qudụrir rāsiyāt, i'malū āla dāwụda syukrā, wa qalīlum min 'ibādiyasy-syakụr","at":"Mereka (para jin itu) bekerja untuk Sulaiman sesuai dengan apa yang dikehendakinya di antaranya (membuat) gedung-gedung yang tinggi, patung-patung, piring-piring yang (besarnya) seperti kolam dan periuk-periuk yang tetap (berada di atas tungku). Bekerjalah wahai keluarga Dawud untuk bersyukur (kepada Allah). Dan sedikit sekali dari hamba-hamba-Ku yang bersyukur."},{"id":"14","ar":"فَلَمَّا قَضَيْنَا عَلَيْهِ الْمَوْتَ مَا دَلَّهُمْ عَلٰى مَوْتِهٖٓ اِلَّا دَاۤبَّةُ الْاَرْضِ تَأْكُلُ مِنْسَاَتَهٗ ۚفَلَمَّا خَرَّ تَبَيَّنَتِ الْجِنُّ اَنْ لَّوْ كَانُوْا يَعْلَمُوْنَ الْغَيْبَ مَا لَبِثُوْا فِى الْعَذَابِ الْمُهِيْنِۗ ","bc":"fa lammā qaḍainā 'alaihil-mauta mā dallahum 'alā mautihī illā dābbatul-arḍi ta`kulu minsa`atah, fa lammā kharra tabayyanatil-jinnu al lau kānụ ya'lamụnal-gaiba mā labiṡụ fil-'ażābil-muhīn","at":"Maka ketika Kami telah menetapkan kematian atasnya (Sulaiman), tidak ada yang menunjukkan kepada mereka kematiannya itu kecuali rayap yang memakan tongkatnya. Maka ketika dia telah tersungkur, tahulah jin itu bahwa sekiranya mereka mengetahui yang gaib tentu mereka tidak tetap dalam siksa yang menghinakan."},{"id":"15","ar":"لَقَدْ كَانَ لِسَبَاٍ فِيْ مَسْكَنِهِمْ اٰيَةٌ ۚجَنَّتٰنِ عَنْ يَّمِيْنٍ وَّشِمَالٍ ەۗ كُلُوْا مِنْ رِّزْقِ رَبِّكُمْ وَاشْكُرُوْا لَهٗ ۗبَلْدَةٌ طَيِّبَةٌ وَّرَبٌّ غَفُوْرٌ","bc":"laqad kāna lisaba`in fī maskanihim āyah, jannatāni 'ay yamīniw wa syimāl, kulụ mir rizqi rabbikum wasykurụ lah, baldatun ṭayyibatuw wa rabbun gafụr","at":"Sungguh, bagi kaum Saba’ ada tanda (kebesaran Tuhan) di tempat kediaman mereka yaitu dua buah kebun di sebelah kanan dan di sebelah kiri, (kepada mereka dikatakan), “Makanlah olehmu dari rezeki yang (dianugerahkan) Tuhanmu dan bersyukurlah kepada-Nya. (Negerimu) adalah negeri yang baik (nyaman) sedang (Tuhanmu) adalah Tuhan Yang Maha Pengampun.”"},{"id":"16","ar":"فَاَعْرَضُوْا فَاَرْسَلْنَا عَلَيْهِمْ سَيْلَ الْعَرِمِ وَبَدَّلْنٰهُمْ بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَيْ اُكُلٍ خَمْطٍ وَّاَثْلٍ وَّشَيْءٍ مِّنْ سِدْرٍ قَلِيْلٍ ","bc":"fa a'raḍụ fa arsalnā 'alaihim sailal-'arimi wa baddalnāhum bijannataihim jannataini żawātai ukulin khamṭiw wa aṡliw wa syai`im min sidring qalīl","at":"Tetapi mereka berpaling, maka Kami kirim kepada mereka banjir yang besar dan Kami ganti kedua kebun mereka dengan dua kebun yang ditumbuhi (pohon-pohon) yang berbuah pahit, pohon Atsl dan sedikit pohon Sidr."},{"id":"17","ar":"ذٰلِكَ جَزَيْنٰهُمْ بِمَا كَفَرُوْاۗ وَهَلْ نُجٰزِيْٓ اِلَّا الْكَفُوْرَ ","bc":"żālika jazaināhum bimā kafarụ, wa hal nujāzī illal-kafụr","at":"Demikianlah Kami memberi balasan kepada mereka karena kekafiran mereka. Dan Kami tidak menjatuhkan azab (yang demikian itu), melainkan hanya kepada orang-orang yang sangat kafir."},{"id":"18","ar":"وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ الْقُرَى الَّتِيْ بٰرَكْنَا فِيْهَا قُرًى ظَاهِرَةً وَّقَدَّرْنَا فِيْهَا السَّيْرَۗ سِيْرُوْا فِيْهَا لَيَالِيَ وَاَيَّامًا اٰمِنِيْنَ ","bc":"wa ja'alnā bainahum wa bainal-qurallatī bāraknā fīhā quran ẓāhirataw wa qaddarnā fīhas-saīr, sīrụ fīhā layāliya wa ayyāman āminīn","at":"Dan Kami jadikan antara mereka (penduduk Saba’) dan negeri-negeri yang Kami berkahi (Syam), beberapa negeri yang berdekatan dan Kami tetapkan antara negeri-negeri itu (jarak-jarak) perjalanan. Berjalanlah kamu di negeri-negeri itu pada malam dan siang hari dengan aman."},{"id":"19","ar":"فَقَالُوْا رَبَّنَا بٰعِدْ بَيْنَ اَسْفَارِنَا وَظَلَمُوْٓا اَنْفُسَهُمْ فَجَعَلْنٰهُمْ اَحَادِيْثَ وَمَزَّقْنٰهُمْ كُلَّ مُمَزَّقٍۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّكُلِّ صَبَّارٍ شَكُوْرٍ ","bc":"fa qālụ rabbanā bā'id baina asfārinā wa ẓalamū anfusahum fa ja'alnāhum aḥādīṡa wa mazzaqnāhum kulla mumazzaq, inna fī żālika la`āyātil likulli ṣabbārin syakụr","at":"Maka mereka berkata, “Ya Tuhan kami, jauhkanlah jarak perjalanan kami,” dan (berarti mereka) menzalimi diri mereka sendiri; maka Kami jadikan mereka bahan pembicaraan dan Kami hancurkan mereka sehancur-hancurnya. Sungguh, pada yang demikian itu benar-benar terdapat tanda-tanda (kekuasaan Allah) bagi setiap orang yang sabar dan bersyukur."},{"id":"20","ar":"وَلَقَدْ صَدَّقَ عَلَيْهِمْ اِبْلِيْسُ ظَنَّهٗ فَاتَّبَعُوْهُ اِلَّا فَرِيْقًا مِّنَ الْمُؤْمِنِيْنَ ","bc":"wa laqad ṣaddaqa 'alaihim iblīsu ẓannahụ fattaba'ụhu illā farīqam minal-mu`minīn","at":"Dan sungguh, Iblis telah dapat meyakinkan terhadap mereka kebenaran sangkaannya, lalu mereka mengikutinya, kecuali sebagian dari orang-orang mukmin."},{"id":"21","ar":"وَمَا كَانَ لَهٗ عَلَيْهِمْ مِّنْ سُلْطَانٍ اِلَّا لِنَعْلَمَ مَنْ يُّؤْمِنُ بِالْاٰخِرَةِ مِمَّنْ هُوَ مِنْهَا فِيْ شَكٍّ ۗوَرَبُّكَ عَلٰى كُلِّ شَيْءٍ حَفِيْظٌ ","bc":"wa mā kāna lahụ 'alaihim min sulṭānin illā lina'lama may yu`minu bil-ākhirati mim man huwa min-hā fī syakk, wa rabbuka 'alā kulli syai`in ḥafīẓ","at":"Dan tidak ada kekuasaan (Iblis) terhadap mereka, melainkan hanya agar Kami dapat membedakan siapa yang beriman kepada adanya akhirat dan siapa yang masih ragu-ragu tentang (akhirat) itu. Dan Tuhanmu Maha Memelihara segala sesuatu."},{"id":"22","ar":"قُلِ ادْعُوا الَّذِيْنَ زَعَمْتُمْ مِّنْ دُوْنِ اللّٰهِۚ لَا يَمْلِكُوْنَ مِثْقَالَ ذَرَّةٍ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِ وَمَا لَهُمْ فِيْهِمَا مِنْ شِرْكٍ وَّمَا لَهٗ مِنْهُمْ مِّنْ ظَهِيْرٍ ","bc":"qulid'ullażīna za'amtum min dụnillāh, lā yamlikụna miṡqāla żarratin fis-samāwāti wa lā fil-arḍi wa mā lahum fīhimā min syirkiw wa mā lahụ min-hum min ẓahīr","at":"Katakanlah (Muhammad), “Serulah mereka yang kamu anggap (sebagai tuhan) selain Allah! Mereka tidak memiliki (kekuasaan) seberat zarrah pun di langit dan di bumi, dan mereka sama sekali tidak mempunyai peran serta dalam (penciptaan) langit dan bumi dan tidak ada di antara mereka yang menjadi pembantu bagi-Nya.”"},{"id":"23","ar":"وَلَا تَنْفَعُ الشَّفَاعَةُ عِنْدَهٗٓ اِلَّا لِمَنْ اَذِنَ لَهٗ ۗحَتّٰىٓ اِذَا فُزِّعَ عَنْ قُلُوْبِهِمْ قَالُوْا مَاذَاۙ قَالَ رَبُّكُمْۗ قَالُوا الْحَقَّۚ وَهُوَ الْعَلِيُّ الْكَبِيْرُ ","bc":"wa lā tanfa'usy-syafā'atu 'indahū illā liman ażina lah, ḥattā iżā fuzzi'a 'ang qulụbihim qālụ māżā qāla rabbukum, qālul-ḥaqq, wa huwal-'aliyyul-kabīr","at":"Dan syafaat (pertolongan) di sisi-Nya hanya berguna bagi orang yang telah diizinkan-Nya (memperoleh syafaat itu). Sehingga apabila telah dihilangkan ketakutan dari hati mereka, mereka berkata, “Apakah yang telah difirmankan oleh Tuhanmu?” Mereka menjawab, “(Perkataan) yang benar,” dan Dialah Yang Mahatinggi, Mahabesar."},{"id":"24","ar":"۞ قُلْ مَنْ يَّرْزُقُكُمْ مِّنَ السَّمٰوٰتِ وَالْاَرْضِۗ قُلِ اللّٰهُ ۙوَاِنَّآ اَوْ اِيَّاكُمْ لَعَلٰى هُدًى اَوْ فِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"qul may yarzuqukum minas-samāwāti wal-arḍ, qulillāhu wa innā au iyyākum la'alā hudan au fī ḍalālim mubīn","at":"Katakanlah (Muhammad), “Siapakah yang memberi rezeki kepadamu dari langit dan dari bumi?” Katakanlah, “Allah,” dan sesungguhnya kami atau kamu (orang-orang musyrik), pasti berada dalam kebenaran atau dalam kesesatan yang nyata."},{"id":"25","ar":"قُلْ لَّا تُسْـَٔلُوْنَ عَمَّآ اَجْرَمْنَا وَلَا نُسْـَٔلُ عَمَّا تَعْمَلُوْنَ ","bc":"qul lā tus`alụna 'ammā ajramnā wa lā nus`alu 'ammā ta'malụn","at":"Katakanlah, “Kamu tidak akan dimintai tanggung jawab atas apa yang kami kerjakan dan kami juga tidak akan dimintai tanggung jawab atas apa yang kamu kerjakan.”"},{"id":"26","ar":"قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِالْحَقِّۗ وَهُوَ الْفَتَّاحُ الْعَلِيْمُ ","bc":"qul yajma'u bainanā rabbunā ṡumma yaftaḥu bainanā bil-ḥaqq, wa huwal-fattāḥul-'alīm","at":"Katakanlah, “Tuhan kita akan mengumpulkan kita semua, kemudian Dia memberi keputusan antara kita dengan benar. Dan Dia Yang Maha Pemberi keputusan, Maha Mengetahui.”"},{"id":"27","ar":"قُلْ اَرُوْنِيَ الَّذِيْنَ اَلْحَقْتُمْ بِهٖ شُرَكَاۤءَ كَلَّا ۗبَلْ هُوَ اللّٰهُ الْعَزِيْزُ الْحَكِيْمُ ","bc":"qul arụniyallażīna alḥaqtum bihī syurakā`a kallā, bal huwallāhul-'azīzul-ḥakīm","at":"Katakanlah, “Perlihatkanlah kepadaku sembahan-sembahan yang kamu hubungkan dengan Dia sebagai sekutu-sekutu(-Nya), tidak mungkin! Sebenarnya Dialah Allah Yang Mahaperkasa, Mahabijaksana.”"},{"id":"28","ar":"وَمَآ اَرْسَلْنٰكَ اِلَّا كَاۤفَّةً لِّلنَّاسِ بَشِيْرًا وَّنَذِيْرًا وَّلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ","bc":"wa mā arsalnāka illā kāffatal lin-nāsi basyīraw wa nażīraw wa lākinna akṡaran-nāsi lā ya'lamụn","at":"Dan Kami tidak mengutus engkau (Muhammad), melainkan kepada semua umat manusia sebagai pembawa berita gembira dan sebagai pemberi peringatan, tetapi kebanyakan manusia tidak mengetahui."},{"id":"29","ar":"وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"wa yaqụlụna matā hāżal-wa'du ing kuntum ṣādiqīn","at":"Dan mereka berkata, “Kapankah (datangnya) janji ini, jika kamu orang yang benar?”"},{"id":"30","ar":"قُلْ لَّكُمْ مِّيْعَادُ يَوْمٍ لَّا تَسْتَأْخِرُوْنَ عَنْهُ سَاعَةً وَّلَا تَسْتَقْدِمُوْنَ ","bc":"qul lakum mī'ādu yaumil lā tasta`khirụna 'an-hu sā'ataw wa lā tastaqdimụn","at":"Katakanlah, “Bagimu ada hari yang telah dijanjikan (hari Kiamat), kamu tidak dapat meminta penundaan atau percepatannya sesaat pun.”"},{"id":"31","ar":"وَقَالَ الَّذِيْنَ كَفَرُوْا لَنْ نُّؤْمِنَ بِهٰذَا الْقُرْاٰنِ وَلَا بِالَّذِيْ بَيْنَ يَدَيْهِۗ وَلَوْ تَرٰىٓ اِذِ الظّٰلِمُوْنَ مَوْقُوْفُوْنَ عِنْدَ رَبِّهِمْۖ يَرْجِعُ بَعْضُهُمْ اِلٰى بَعْضِ ِۨالْقَوْلَۚ يَقُوْلُ الَّذِيْنَ اسْتُضْعِفُوْا لِلَّذِيْنَ اسْتَكْبَرُوْا لَوْلَآ اَنْتُمْ لَكُنَّا مُؤْمِنِيْنَ ","bc":"wa qālallażīna kafarụ lan nu`mina bihāżal-qur`āni wa lā billażī baina yadaīh, walau tarā iżiẓ-ẓālimụna mauqụfụna 'inda rabbihim yarji'u ba'ḍuhum ilā ba'ḍinil-qaụl, yaqụlullażīnastuḍ'ifụ lillażīnastakbarụ lau lā antum lakunnā mu`minīn","at":"Dan orang-orang kafir berkata, “Kami tidak akan beriman kepada Al-Qur'an ini dan tidak (pula) kepada Kitab yang sebelumnya.” Dan (alangkah mengerikan) kalau kamu melihat ketika orang-orang yang zalim itu dihadapkan kepada Tuhannya, sebagian mereka mengembalikan perkataan kepada sebagian yang lain; orang-orang yang dianggap lemah berkata kepada orang-orang yang menyombongkan diri, “Kalau tidaklah karena kamu tentulah kami menjadi orang-orang mukmin.”"},{"id":"32","ar":"قَالَ الَّذِيْنَ اسْتَكْبَرُوْا لِلَّذِيْنَ اسْتُضْعِفُوْٓا اَنَحْنُ صَدَدْنٰكُمْ عَنِ الْهُدٰى بَعْدَ اِذْ جَاۤءَكُمْ بَلْ كُنْتُمْ مُّجْرِمِيْنَ ","bc":"qālallażīnastakbarụ lillażīnastuḍ'ifū a naḥnu ṣadadnākum 'anil-hudā ba'da iż jā`akum bal kuntum mujrimīn","at":"Orang-orang yang menyombongkan diri berkata kepada orang-orang yang dianggap lemah, “Kamikah yang telah menghalangimu untuk memperoleh petunjuk setelah petunjuk itu datang kepadamu? (Tidak!) Sebenarnya kamu sendirilah orang-orang yang berbuat dosa.”"},{"id":"33","ar":"وَقَالَ الَّذِيْنَ اسْتُضْعِفُوْا لِلَّذِيْنَ اسْتَكْبَرُوْا بَلْ مَكْرُ الَّيْلِ وَالنَّهَارِ اِذْ تَأْمُرُوْنَنَآ اَنْ نَّكْفُرَ بِاللّٰهِ وَنَجْعَلَ لَهٗٓ اَنْدَادًا ۗوَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۗ وَجَعَلْنَا الْاَغْلٰلَ فِيْٓ اَعْنَاقِ الَّذِيْنَ كَفَرُوْاۗ هَلْ يُجْزَوْنَ اِلَّا مَا كَانُوْا يَعْمَلُوْنَ ","bc":"wa qālallażīnastuḍ'ifụ lillażīnastakbarụ bal makrul-laili wan-nahāri iż ta`murụnanā an nakfura billāhi wa naj'ala lahū andādā, wa asarrun-nadāmata lammā ra`awul-'ażāb, wa ja'alnal-aglāla fī a'nāqillażīna kafarụ, hal yujzauna illā mā kānụ ya'malụn","at":"Dan orang-orang yang dianggap lemah berkata kepada orang-orang yang menyombongkan diri, “(Tidak!) Sebenarnya tipu daya(mu) pada waktu malam dan siang (yang menghalangi kami), ketika kamu menyeru kami agar kami kafir kepada Allah dan menjadikan sekutu-sekutu bagi-Nya.” Mereka menyatakan penyesalan ketika mereka melihat azab. Dan Kami pasangkan belenggu di leher orang-orang yang kafir. Mereka tidak dibalas melainkan sesuai dengan apa yang telah mereka kerjakan."},{"id":"34","ar":"وَمَآ اَرْسَلْنَا فِيْ قَرْيَةٍ مِّنْ نَّذِيْرٍ ِالَّا قَالَ مُتْرَفُوْهَآ ۙاِنَّا بِمَآ اُرْسِلْتُمْ بِهٖ كٰفِرُوْنَ ","bc":"wa mā arsalnā fī qaryatim min nażīrin illā qāla mutrafụhā innā bimā ursiltum bihī kāfirụn","at":"Dan setiap Kami mengutus seorang pemberi peringatan kepada suatu negeri, orang-orang yang hidup mewah (di negeri itu) berkata, “Kami benar-benar mengingkari apa yang kamu sampaikan sebagai utusan.”"},{"id":"35","ar":"وَقَالُوْا نَحْنُ اَكْثَرُ اَمْوَالًا وَّاَوْلَادًاۙ وَّمَا نَحْنُ بِمُعَذَّبِيْنَ ","bc":"wa qālụ naḥnu akṡaru amwālaw wa aulādaw wa mā naḥnu bimu'ażżabīn","at":"Dan mereka berkata, “Kami memiliki lebih banyak harta dan anak-anak (daripada kamu) dan kami tidak akan diazab.”"},{"id":"36","ar":"قُلْ اِنَّ رَبِّيْ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ وَيَقْدِرُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ","bc":"qul inna rabbī yabsuṭur-rizqa limay yasyā`u wa yaqdiru wa lākinna akṡaran-nāsi lā ya'lamụn","at":"Katakanlah, “Sungguh, Tuhanku melapangkan rezeki bagi siapa yang Dia kehendaki dan membatasinya (bagi siapa yang Dia kehendaki), tetapi kebanyakan manusia tidak mengetahui.”"},{"id":"37","ar":"وَمَآ اَمْوَالُكُمْ وَلَآ اَوْلَادُكُمْ بِالَّتِيْ تُقَرِّبُكُمْ عِنْدَنَا زُلْفٰىٓ اِلَّا مَنْ اٰمَنَ وَعَمِلَ صَالِحًاۙ فَاُولٰۤىِٕكَ لَهُمْ جَزَاۤءُ الضِّعْفِ بِمَا عَمِلُوْا وَهُمْ فِى الْغُرُفٰتِ اٰمِنُوْنَ ","bc":"wa mā amwālukum wa lā aulādukum billatī tuqarribukum 'indanā zulfā illā man āmana wa 'amila ṣāliḥan fa ulā`ika lahum jazā`uḍ-ḍi'fi bimā 'amilụ wa hum fil-gurufāti āminụn","at":"Dan bukanlah harta atau anak-anakmu yang mendekatkan kamu kepada Kami; melainkan orang-orang yang beriman dan mengerjakan kebajikan, mereka itulah yang memperoleh balasan yang berlipat ganda atas apa yang telah mereka kerjakan; dan mereka aman sentosa di tempat-tempat yang tinggi (dalam surga)."},{"id":"38","ar":"وَالَّذِيْنَ يَسْعَوْنَ فِيْٓ اٰيٰتِنَا مُعٰجِزِيْنَ اُولٰۤىِٕكَ فِى الْعَذَابِ مُحْضَرُوْنَ ","bc":"wallażīna yas'auna fī āyātinā mu'ājizīna ulā`ika fil-'ażābi muḥḍarụn","at":"Dan orang-orang yang berusaha menentang ayat-ayat Kami untuk melemahkan (menggagalkan azab Kami), mereka itu dimasukkan ke dalam azab."},{"id":"39","ar":"قُلْ اِنَّ رَبِّيْ يَبْسُطُ الرِّزْقَ لِمَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ وَيَقْدِرُ لَهٗ ۗوَمَآ اَنْفَقْتُمْ مِّنْ شَيْءٍ فَهُوَ يُخْلِفُهٗ ۚوَهُوَ خَيْرُ الرّٰزِقِيْنَ ","bc":"qul inna rabbī yabsuṭur-rizqa limay yasyā`u min 'ibādihī wa yaqdiru lah, wa mā anfaqtum min syai`in fa huwa yukhlifuh, wa huwa khairur-rāziqīn","at":"Katakanlah, “Sungguh, Tuhanku melapangkan rezeki dan membatasinya bagi siapa yang Dia kehendaki di antara hamba-hamba-Nya.” Dan apa saja yang kamu infakkan, Allah akan menggantinya dan Dialah pemberi rezeki yang terbaik."},{"id":"40","ar":"وَيَوْمَ يَحْشُرُهُمْ جَمِيْعًا ثُمَّ يَقُوْلُ لِلْمَلٰۤىِٕكَةِ اَهٰٓؤُلَاۤءِ اِيَّاكُمْ كَانُوْا يَعْبُدُوْنَ ","bc":"wa yauma yaḥsyuruhum jamī'an ṡumma yaqụlu lil-malā`ikati a hā`ulā`i iyyākum kānụ ya'budụn","at":"Dan (ingatlah) pada hari (ketika) Allah mengumpulkan mereka semuanya kemudian Dia berfirman kepada para malaikat, “Apakah kepadamu mereka ini dahulu menyembah?”"},{"id":"41","ar":"قَالُوْا سُبْحٰنَكَ اَنْتَ وَلِيُّنَا مِنْ دُوْنِهِمْ ۚبَلْ كَانُوْا يَعْبُدُوْنَ الْجِنَّ اَكْثَرُهُمْ بِهِمْ مُّؤْمِنُوْنَ ","bc":"qālụ sub-ḥānaka anta waliyyunā min dụnihim, bal kānụ ya'budụnal-jinna akṡaruhum bihim mu`minụn","at":"Para malaikat itu menjawab, “Mahasuci Engkau. Engkaulah pelindung kami, bukan mereka; bahkan mereka telah menyembah jin; kebanyakan mereka beriman kepada jin itu.”"},{"id":"42","ar":"فَالْيَوْمَ لَا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَّفْعًا وَّلَا ضَرًّا ۗوَنَقُوْلُ لِلَّذِيْنَ ظَلَمُوْا ذُوْقُوْا عَذَابَ النَّارِ الَّتِيْ كُنْتُمْ بِهَا تُكَذِّبُوْنَ ","bc":"fal-yauma lā yamliku ba'ḍukum liba'ḍin naf'aw wa lā ḍarrā, wa naqụlu lillażīna ẓalamụ żụqụ 'ażāban-nārillatī kuntum bihā tukażżibụn","at":"Maka pada hari ini sebagian kamu tidak kuasa (mendatangkan) manfaat maupun (menolak) mudarat kepada se-bagian yang lain. Dan Kami katakan kepada orang-orang yang zalim, “Rasakanlah olehmu azab neraka yang dahulu kamu dustakan.”"},{"id":"43","ar":"وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيٰتُنَا بَيِّنٰتٍ قَالُوْا مَا هٰذَآ اِلَّا رَجُلٌ يُّرِيْدُ اَنْ يَّصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ اٰبَاۤؤُكُمْ ۚوَقَالُوْا مَا هٰذَآ اِلَّآ اِفْكٌ مُّفْتَرًىۗ وَقَالَ الَّذِيْنَ كَفَرُوْا لِلْحَقِّ لَمَّا جَاۤءَهُمْۙ اِنْ هٰذَآ اِلَّا سِحْرٌ مُّبِيْنٌ ","bc":"wa iżā tutlā 'alaihim āyātunā bayyināting qālụ mā hāżā illā rajuluy yurīdu ay yaṣuddakum 'ammā kāna ya'budu ābā`ukum, wa qālụ mā hāżā illā ifkum muftarā, wa qālallażīna kafarụ lil-ḥaqqi lammā jā`ahum in hāżā illā siḥrum mubīn","at":"Dan apabila dibacakan kepada mereka ayat-ayat Kami yang terang, mereka berkata, “Orang ini tidak lain hanya ingin menghalang-halangi kamu dari apa yang disembah oleh nenek moyangmu,” dan mereka berkata, “(Al-Qur'an) ini tidak lain hanyalah kebohongan yang diada-adakan saja.” Dan orang-orang kafir berkata terhadap kebenaran ketika kebenaran (Al-Qur'an) itu datang kepada mereka, “Ini tidak lain hanyalah sihir yang nyata.”"},{"id":"44","ar":"وَمَآ اٰتَيْنٰهُمْ مِّنْ كُتُبٍ يَّدْرُسُوْنَهَا وَمَآ اَرْسَلْنَآ اِلَيْهِمْ قَبْلَكَ مِنْ نَّذِيْرٍۗ ","bc":"wa mā ātaināhum ming kutubiy yadrusụnahā wa mā arsalnā ilaihim qablaka min nażīr","at":"Dan Kami tidak pernah memberikan kepada mereka kitab-kitab yang mereka baca dan Kami tidak pernah mengutus seorang pemberi peringatan kepada mereka sebelum engkau (Muhammad)."},{"id":"45","ar":"وَكَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْۙ وَمَا بَلَغُوْا مِعْشَارَ مَآ اٰتَيْنٰهُمْ فَكَذَّبُوْا رُسُلِيْۗ فَكَيْفَ كَانَ نَكِيْرِ","bc":"wa każżaballażīna ming qablihim wa mā balagụ mi'syāra mā ātaināhum fa każżabụ rusulī, fa kaifa kāna nakīr","at":"Dan orang-orang yang sebelum mereka telah mendustakan (para rasul) sedang orang-orang (kafir Mekah) itu belum sampai menerima sepersepuluh dari apa yang telah Kami berikan kepada orang-orang terdahulu itu namun mereka mendustakan para rasul-Ku. Maka (lihatlah) bagaimana dahsyatnya akibat kemurkaan-Ku."},{"id":"46","ar":"۞ قُلْ اِنَّمَآ اَعِظُكُمْ بِوَاحِدَةٍۚ اَنْ تَقُوْمُوْا لِلّٰهِ مَثْنٰى وَفُرَادٰى ثُمَّ تَتَفَكَّرُوْاۗ مَا بِصَاحِبِكُمْ مِّنْ جِنَّةٍۗ اِنْ هُوَ اِلَّا نَذِيْرٌ لَّكُمْ بَيْنَ يَدَيْ عَذَابٍ شَدِيْدٍ ","bc":"qul innamā a'iẓukum biwāḥidah, an taqụmụ lillāhi maṡnā wa furādā ṡumma tatafakkarụ, mā biṣāḥibikum min jinnah, in huwa illā nażīrul lakum baina yadai 'ażābin syadīd","at":"Katakanlah, “Aku hendak memperingatkan kepadamu satu hal saja, yaitu agar kamu menghadap Allah (dengan ikhlas) berdua-dua atau sendiri-sendiri; kemudian agar kamu pikirkan (tentang Muhammad). Kawanmu itu tidak gila sedikit pun. Dia tidak lain hanyalah seorang pemberi peringatan bagi kamu sebelum (menghadapi) azab yang keras.”"},{"id":"47","ar":"قُلْ مَا سَاَلْتُكُمْ مِّنْ اَجْرٍ فَهُوَ لَكُمْۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ ۚوَهُوَ عَلٰى كُلِّ شَيْءٍ شَهِيْدٌ ","bc":"qul mā sa`altukum min ajrin fa huwa lakum, in ajriya illā 'alallāh, wa huwa 'alā kulli syai`in syahīd","at":"Katakanlah (Muhammad), “Imbalan apa pun yang aku minta kepadamu, maka itu untuk kamu. Imbalanku hanyalah dari Allah, dan Dia Maha Mengetahui segala sesuatu.”"},{"id":"48","ar":"قُلْ اِنَّ رَبِّيْ يَقْذِفُ بِالْحَقِّۚ عَلَّامُ الْغُيُوْبِ ","bc":"qul inna rabbī yaqżifu bil-ḥaqq, 'allāmul-guyụb","at":"Katakanlah, “Sesungguhnya Tuhanku mewahyukan kebenaran. Dia Maha Mengetahui segala yang gaib.”"},{"id":"49","ar":"قُلْ جَاۤءَ الْحَقُّ وَمَا يُبْدِئُ الْبَاطِلُ وَمَا يُعِيْدُ ","bc":"qul jā`al-ḥaqqu wa mā yubdi`ul-bāṭilu wa mā yu'īd","at":"Katakanlah, “Kebenaran telah datang dan yang batil itu tidak akan memulai dan tidak (pula) akan mengulangi.”"},{"id":"50","ar":"قُلْ اِنْ ضَلَلْتُ فَاِنَّمَآ اَضِلُّ عَلٰى نَفْسِيْۚ وَاِنِ اهْتَدَيْتُ فَبِمَا يُوْحِيْٓ اِلَيَّ رَبِّيْۗ اِنَّهٗ سَمِيْعٌ قَرِيْبٌ ","bc":"qul in ḍalaltu fa innamā aḍillu 'alā nafsī, wa inihtadaitu fa bimā yụḥī ilayya rabbī, innahụ samī'ung qarīb","at":"Katakanlah, “Jika aku sesat maka sesungguhnya aku sesat untuk diriku sendiri; dan jika aku mendapat petunjuk maka itu disebabkan apa yang diwahyukan Tuhanku kepadaku. Sungguh, Dia Maha Mendengar, Maha-dekat.”"},{"id":"51","ar":"وَلَوْ تَرٰىٓ اِذْ فَزِعُوْا فَلَا فَوْتَ وَاُخِذُوْا مِنْ مَّكَانٍ قَرِيْبٍۙ ","bc":"walau tarā iż fazi'ụ fa lā fauta wa ukhiżụ mim makāning qarīb","at":"Dan (alangkah mengerikan) sekiranya engkau melihat mereka (orang-orang kafir) ketika terperanjat ketakutan (pada hari Kiamat); lalu mereka tidak dapat melepaskan diri dan mereka ditangkap dari tempat yang dekat (untuk dibawa ke neraka),"},{"id":"52","ar":"وَّقَالُوْٓا اٰمَنَّا بِهٖۚ وَاَنّٰى لَهُمُ التَّنَاوُشُ مِنْ مَّكَانٍۢ بَعِيْدٍۚ ","bc":"wa qālū āmannā bih, wa annā lahumut-tanāwusyu mim makānim ba'īd","at":"dan (ketika) mereka berkata, “Kami beriman kepada-Nya.” Namun bagaimana mereka dapat mencapai (keimanan) dari tempat yang jauh?"},{"id":"53","ar":"وَقَدْ كَفَرُوْا بِهٖ مِنْ قَبْلُۚ وَيَقْذِفُوْنَ بِالْغَيْبِ مِنْ مَّكَانٍۢ بَعِيْدٍۚ ","bc":"wa qad kafarụ bihī ming qabl, wa yaqżifụna bil-gaibi mim makānim ba'īd","at":"Dan sungguh, mereka telah mengingkari Allah sebelum itu; dan mereka mendustakan tentang yang gaib dari tempat yang jauh."},{"id":"54","ar":"وَحِيْلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُوْنَۙ كَمَا فُعِلَ بِاَشْيَاعِهِمْ مِّنْ قَبْلُۗ اِنَّهُمْ كَانُوْا فِيْ شَكٍّ مُّرِيْبٍ","bc":"wa hīla bainahum wa baina mā yasytahụna kamā fu'ila bi`asy-yā'ihim ming qabl, innahum kānụ fī syakkim murīb","at":"Dan diberi penghalang antara mereka dengan apa yang mereka inginkan sebagaimana yang dilakukan terhadap orang-orang yang sepaham dengan mereka yang terdahulu. Sesungguhnya mereka dahulu (di dunia) dalam keraguan yang mendalam."}],sad=[{"id":"1","ar":"صۤ ۗوَالْقُرْاٰنِ ذِى الذِّكْرِۗ","bc":"shād, wal-qur`āni żiż-żikr","at":"Shad, demi Al-Qur'an yang mengandung peringatan. "},{"id":"2","ar":"بَلِ الَّذِيْنَ كَفَرُوْا فِيْ عِزَّةٍ وَّشِقَاقٍ","bc":"balillażīna kafarụ fī 'izzatiw wa syiqāq","at":"Tetapi orang-orang yang kafir (berada) dalam kesombongan dan permusuhan."},{"id":"3","ar":"كَمْ اَهْلَكْنَا مِنْ قَبْلِهِمْ مِّنْ قَرْنٍ فَنَادَوْا وَّلَاتَ حِيْنَ مَنَاصٍ","bc":"kam ahlaknā ming qablihim ming qarnin fa nādaw wa lāta ḥīna manāṣ","at":"Betapa banyak umat sebelum mereka yang telah Kami binasakan, lalu mereka meminta tolong padahal (waktu itu) bukanlah saat untuk lari melepaskan diri."},{"id":"4","ar":"وَعَجِبُوْٓا اَنْ جَاۤءَهُمْ مُّنْذِرٌ مِّنْهُمْ ۖوَقَالَ الْكٰفِرُوْنَ هٰذَا سٰحِرٌ كَذَّابٌۚ","bc":"wa 'ajibū an jā`ahum munżirum min-hum wa qālal-kāfirụna hāżā sāḥirung każżāb","at":"Dan mereka heran karena mereka kedatangan seorang pemberi peringatan (rasul) dari kalangan mereka; dan orang-orang kafir berkata, “Orang ini adalah pesihir yang banyak berdusta.”"},{"id":"5","ar":"اَجَعَلَ الْاٰلِهَةَ اِلٰهًا وَّاحِدًا ۖاِنَّ هٰذَا لَشَيْءٌ عُجَابٌ","bc":"a ja'alal-ālihata ilāhaw wāḥidan inna hāżā lasyai`un 'ujāb","at":"Apakah dia menjadikan tuhan-tuhan itu Tuhan yang satu saja? Sungguh, ini benar-benar sesuatu yang sangat mengherankan."},{"id":"6","ar":"وَانْطَلَقَ الْمَلَاُ مِنْهُمْ اَنِ امْشُوْا وَاصْبِرُوْا عَلٰٓى اٰلِهَتِكُمْ ۖاِنَّ هٰذَا لَشَيْءٌ يُّرَادُ ۖ","bc":"wanṭalaqal-mala`u min-hum animsyụ waṣbirụ 'alā ālihatikum inna hāżā lasyai`uy yurād","at":"Lalu pergilah pemimpin-pemimpin mereka (seraya berkata), “Pergilah kamu dan tetaplah (menyembah) tuhan-tuhanmu, sesungguhnya ini benar-benar suatu hal yang dikehendaki."},{"id":"7","ar":"مَا سَمِعْنَا بِهٰذَا فِى الْمِلَّةِ الْاٰخِرَةِ ۖاِنْ هٰذَآ اِلَّا اخْتِلَاقٌۚ","bc":"mā sami'nā bihāżā fil-millatil-ākhirati in hāżā illakhtilāq","at":"Kami tidak pernah mendengar hal ini dalam agama yang terakhir; ini (mengesakan Allah), tidak lain hanyalah (dusta) yang diada-adakan,"},{"id":"8","ar":"اَؤُنْزِلَ عَلَيْهِ الذِّكْرُ مِنْۢ بَيْنِنَا ۗبَلْ هُمْ فِيْ شَكٍّ مِّنْ ذِكْرِيْۚ بَلْ لَّمَّا يَذُوْقُوْا عَذَابِ ۗ","bc":"a unzila 'alaihiż-żikru mim baininā, bal hum fī syakkim min żikrī, bal lammā yażụqụ 'ażāb","at":"mengapa Al-Qur'an itu diturunkan kepada dia di antara kita?” Sebenarnya mereka ragu-ragu terhadap Al-Qur'an-Ku, tetapi mereka belum merasakan azab(-Ku)."},{"id":"9","ar":"اَمْ عِنْدَهُمْ خَزَاۤىِٕنُ رَحْمَةِ رَبِّكَ الْعَزِيْزِ الْوَهَّابِۚ","bc":"am 'indahum khazā`inu raḥmati rabbikal-'azīzil wahhāb","at":"Atau apakah mereka itu mempunyai perbendaharaan rahmat Tuhanmu Yang Mahaperkasa, Maha Pemberi?]"},{"id":"10","ar":"اَمْ لَهُمْ مُّلْكُ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا ۗفَلْيَرْتَقُوْا فِى الْاَسْبَابِ","bc":"am lahum mulkus-samāwāti wal-arḍi wa mā bainahumā, falyartaqụ fil-asbāb","at":"Atau apakah mereka mempunyai kerajaan langit dan bumi dan apa yang ada di antara keduanya? (Jika ada), maka biarlah mereka menaiki tangga-tangga (ke langit)."},{"id":"11","ar":"جُنْدٌ مَّا هُنَالِكَ مَهْزُوْمٌ مِّنَ الْاَحْزَابِ","bc":"jundum mā hunālika mahzụmum minal-aḥzāb","at":"(Mereka itu) kelompok besar bala tentara yang berada di sana yang akan dikalahkan."},{"id":"12","ar":"كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوْحٍ وَّعَادٌ وَّفِرْعَوْنُ ذُو الْاَوْتَادِۙ","bc":"każżabat qablahum qaumu nụḥiw wa 'āduw wa fir'aunu żul-autād","at":"Sebelum mereka itu, kaum Nuh, ‘Ad dan Fir’aun yang mempunyai bala tentara yang banyak, juga telah mendustakan (rasul-rasul),"},{"id":"13","ar":"وَثَمُوْدُ وَقَوْمُ لُوْطٍ وَّاَصْحٰبُ لْـَٔيْكَةِ ۗ اُولٰۤىِٕكَ الْاَحْزَابُ","bc":"wa ṡamụdu wa qaumu lụṭiw wa aṣ-ḥābul-aikah, ulā`ikal-aḥzāb","at":"dan (begitu juga) Samud, kaum Lut dan penduduk Aikah. Mereka itulah golongan-golongan yang bersekutu (menentang rasul-rasul)."},{"id":"14","ar":"اِنْ كُلٌّ اِلَّا كَذَّبَ الرُّسُلَ فَحَقَّ عِقَابِ","bc":"ing kullun illā każżabar-rusula fa ḥaqqa 'iqāb","at":"Semua mereka itu mendustakan rasul-rasul, maka pantas mereka merasakan azab-Ku."},{"id":"15","ar":"وَمَا يَنْظُرُ هٰٓؤُلَاۤءِ اِلَّا صَيْحَةً وَّاحِدَةً مَّا لَهَا مِنْ فَوَاقٍ","bc":"wa mā yanẓuru hā`ulā`i illā ṣaiḥataw wāḥidatam mā lahā min fawāq","at":"Dan sebenarnya yang mereka tunggu adalah satu teriakan saja, yang tidak ada selanya."},{"id":"16","ar":"وَقَالُوْا رَبَّنَا عَجِّلْ لَّنَا قِطَّنَا قَبْلَ يَوْمِ الْحِسَابِ","bc":"wa qālụ rabbanā 'ajjil lanā qiṭṭanā qabla yaumil-ḥisāb","at":"Dan mereka berkata, “Ya Tuhan kami, segerakanlah azab yang diperuntukkan bagi kami sebelum hari perhitungan.”"},{"id":"17","ar":"اِصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَاذْكُرْ عَبْدَنَا دَاوٗدَ ذَا الْاَيْدِۚ اِنَّهٗٓ اَوَّابٌ","bc":"iṣbir 'alā mā yaqụlụna ważkur 'abdanā dāwụda żal-aīd, innahū awwāb","at":"Bersabarlah atas apa yang mereka katakan; dan ingatlah akan hamba Kami Dawud yang mempunyai kekuatan; sungguh dia sangat taat (kepada Allah)."},{"id":"18","ar":"اِنَّا سَخَّرْنَا الْجِبَالَ مَعَهٗ يُسَبِّحْنَ بِالْعَشِيِّ وَالْاِشْرَاقِۙ","bc":"innā sakhkharnal-jibāla ma'ahụ yusabbiḥna bil-'asyiyyi wal-isyrāq","at":"Sungguh, Kamilah yang menundukkan gunung-gunung untuk bertasbih bersama dia (Dawud) pada waktu petang dan pagi,"},{"id":"19","ar":"وَالطَّيْرَمَحْشُوْرَةً ۗ كُلٌّ لَهٗٓ اَوَّابٌ","bc":"waṭ-ṭaira maḥsyụrah, kullul lahū awwāb","at":"dan (Kami tundukkan pula) burung-burung dalam keadaan terkumpul. Masing-masing sangat taat (kepada Allah)."},{"id":"20","ar":"وَشَدَدْنَا مُلْكَهٗ وَاٰتَيْنٰهُ الْحِكْمَةَ وَفَصْلَ الْخِطَابِ","bc":"wa syadadnā mulkahụ wa ātaināhul-ḥikmata wa faṣlal-khiṭāb","at":"Dan Kami kuatkan kerajaannya dan Kami berikan hikmah kepadanya serta kebijaksanaan dalam memutuskan perkara."},{"id":"21","ar":"وَهَلْ اَتٰىكَ نَبَؤُ الْخَصْمِۘ اِذْ تَسَوَّرُوا الْمِحْرَابَۙ","bc":"wa hal atāka naba`ul khaṣm, iż tasawwarul-miḥrāb","at":"Dan apakah telah sampai kepadamu berita orang-orang yang berselisih ketika mereka memanjat dinding mihrab?"},{"id":"22","ar":"اِذْ دَخَلُوْا عَلٰى دَاوٗدَ فَفَزِعَ مِنْهُمْ قَالُوْا لَا تَخَفْۚ خَصْمٰنِ بَغٰى بَعْضُنَا عَلٰى بَعْضٍ فَاحْكُمْ بَيْنَنَا بِالْحَقِّ وَلَا تُشْطِطْ وَاهْدِنَآ اِلٰى سَوَاۤءِ الصِّرَاطِ","bc":"iż dakhalụ 'alā dāwụda fa fazi'a min-hum qālụ lā takhaf, khaṣmāni bagā ba'ḍunā 'alā ba'ḍin faḥkum bainanā bil-ḥaqqi wa lā tusyṭiṭ wahdinā ilā sawā`iṣ-ṣirāṭ","at":"ketika mereka masuk menemui Dawud lalu dia terkejut karena (kedatangan) mereka. Mereka berkata, “Janganlah takut! (Kami) berdua sedang berselisih, sebagian dari kami berbuat zalim kepada yang lain; maka berilah keputusan di antara kami secara adil dan janganlah menyimpang dari kebenaran serta tunjukilah kami ke jalan yang lurus."},{"id":"23","ar":"اِنَّ هٰذَآ اَخِيْ ۗ لَهٗ تِسْعٌ وَّتِسْعُوْنَ نَعْجَةً وَّلِيَ نَعْجَةٌ وَّاحِدَةٌ ۗفَقَالَ اَكْفِلْنِيْهَا وَعَزَّنِيْ فِى الْخِطَابِ","bc":"inna hāżā akhī, lahụ tis'uw wa tis'ụna na'jataw wa liya na'jatuw wāḥidah, fa qāla akfilnīhā wa 'azzanī fil-khiṭāb","at":"Sesungguhnya saudaraku ini mempunyai sembilan puluh sembilan ekor kambing betina dan aku mempunyai seekor saja, lalu dia berkata, “Serahkanlah (kambingmu) itu kepadaku! Dan dia mengalahkan aku dalam perdebatan.”"},{"id":"24","ar":"قَالَ لَقَدْ ظَلَمَكَ بِسُؤَالِ نَعْجَتِكَ اِلٰى نِعَاجِهٖۗ وَاِنَّ كَثِيْرًا مِّنَ الْخُلَطَاۤءِ لَيَبْغِيْ بَعْضُهُمْ عَلٰى بَعْضٍ اِلَّا الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ وَقَلِيْلٌ مَّا هُمْۗ وَظَنَّ دَاوٗدُ اَنَّمَا فَتَنّٰهُ فَاسْتَغْفَرَ رَبَّهٗ وَخَرَّ رَاكِعًا وَّاَنَابَ ۩ ","bc":"qāla laqad ẓalamaka bisu`āli na'jatika ilā ni'ājih, wa inna kaṡīram minal-khulaṭā`i layabgī ba'ḍuhum 'alā ba'ḍin illallażīna āmanụ wa 'amiluṣ-ṣāliḥāti wa qalīlum mā hum, wa ẓanna dāwụdu annamā fatannāhu fastagfara rabbahụ wa kharra rāki'aw wa anāb","at":"Dia (Dawud) berkata, “Sungguh, dia telah berbuat zalim kepadamu dengan meminta kambingmu itu untuk (ditambahkan) kepada kambingnya. Memang banyak di antara orang-orang yang bersekutu itu berbuat zalim kepada yang lain, kecuali orang-orang yang beriman dan mengerjakan kebajikan; dan hanya sedikitlah mereka yang begitu.” Dan Dawud menduga bahwa Kami mengujinya; maka dia memohon ampunan kepada Tuhannya lalu menyungkur sujud dan bertobat."},{"id":"25","ar":"فَغَفَرْنَا لَهٗ ذٰلِكَۗ وَاِنَّ لَهٗ عِنْدَنَا لَزُلْفٰى وَحُسْنَ مَاٰبٍ","bc":"fa gafarnā lahụ żālik, wa inna lahụ 'indanā lazulfā wa ḥusna ma`āb","at":"Lalu Kami mengampuni (kesalahannya) itu. Dan sungguh, dia mempunyai kedudukan yang benar-benar dekat di sisi Kami dan tempat kembali yang baik."},{"id":"26","ar":"يٰدَاوٗدُ اِنَّا جَعَلْنٰكَ خَلِيْفَةً فِى الْاَرْضِ فَاحْكُمْ بَيْنَ النَّاسِ بِالْحَقِّ وَلَا تَتَّبِعِ الْهَوٰى فَيُضِلَّكَ عَنْ سَبِيْلِ اللّٰهِ ۗاِنَّ الَّذِيْنَ يَضِلُّوْنَ عَنْ سَبِيْلِ اللّٰهِ لَهُمْ عَذَابٌ شَدِيْدٌ ۢبِمَا نَسُوْا يَوْمَ الْحِسَابِ ","bc":"yā dāwụdu innā ja'alnāka khalīfatan fil-arḍi faḥkum bainan-nāsi bil-ḥaqqi wa lā tattabi'il-hawā fa yuḍillaka 'an sabīlillāh, innallażīna yaḍillụna 'an sabīlillāhi lahum 'ażābun syadīdum bimā nasụ yaumal-ḥisāb","at":"(Allah berfirman), “Wahai Dawud! Sesungguhnya engkau Kami jadikan khalifah (penguasa) di bumi, maka berilah keputusan (perkara) di antara manusia dengan adil dan janganlah engkau mengikuti hawa nafsu, karena akan menyesatkan engkau dari jalan Allah. Sungguh, orang-orang yang sesat dari jalan Allah akan mendapat azab yang berat, karena mereka melupakan hari perhitungan.”"},{"id":"27","ar":"وَمَا خَلَقْنَا السَّمَاۤءَ وَالْاَرْضَ وَمَا بَيْنَهُمَا بَاطِلًا ۗذٰلِكَ ظَنُّ الَّذِيْنَ كَفَرُوْا فَوَيْلٌ لِّلَّذِيْنَ كَفَرُوْا مِنَ النَّارِۗ","bc":"wa mā khalaqnas-samā`a wal-arḍa wa mā bainahumā bāṭilā, żālika ẓannullażīna kafarụ fa wailul lillażīna kafarụ minan-nār","at":"Dan Kami tidak menciptakan langit dan bumi dan apa yang ada di antara keduanya dengan sia-sia. Itu anggapan orang-orang kafir, maka celakalah orang-orang yang kafir itu karena mereka akan masuk neraka."},{"id":"28","ar":"اَمْ نَجْعَلُ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ كَالْمُفْسِدِيْنَ فِى الْاَرْضِۖ اَمْ نَجْعَلُ الْمُتَّقِيْنَ كَالْفُجَّارِ","bc":"am naj'alullażīna āmanụ wa 'amiluṣ-ṣāliḥāti kal-mufsidīna fil-arḍi am naj'alul-muttaqīna kal-fujjār","at":"Pantaskah Kami memperlakukan orang-orang yang beriman dan mengerjakan kebajikan sama dengan orang-orang yang berbuat kerusakan di bumi? Atau pantaskah Kami menganggap orang-orang yang bertakwa sama dengan orang-orang yang jahat?"},{"id":"29","ar":"كِتٰبٌ اَنْزَلْنٰهُ اِلَيْكَ مُبٰرَكٌ لِّيَدَّبَّرُوْٓا اٰيٰتِهٖ وَلِيَتَذَكَّرَ اُولُوا الْاَلْبَابِ","bc":"kitābun anzalnāhu ilaika mubārakul liyaddabbarū āyātihī wa liyatażakkara ulul-albāb","at":"Kitab (Al-Qur'an) yang Kami turunkan kepadamu penuh berkah agar mereka menghayati ayat-ayatnya dan agar orang-orang yang berakal sehat mendapat pelajaran."},{"id":"30","ar":"وَوَهَبْنَا لِدَاوٗدَ سُلَيْمٰنَۗ نِعْمَ الْعَبْدُ ۗاِنَّهٗٓ اَوَّابٌۗ","bc":"wa wahabnā lidāwụda sulaimān, ni'mal-'abd, innahū awwāb","at":"Dan kepada Dawud Kami karuniakan (anak bernama) Sulaiman; dia adalah sebaik-baik hamba. Sungguh, dia sangat taat (kepada Allah)."},{"id":"31","ar":"اِذْ عُرِضَ عَلَيْهِ بِالْعَشِيِّ الصّٰفِنٰتُ الْجِيَادُۙ","bc":"iż 'uriḍa 'alaihi bil-'asyiyyiṣ-ṣāfinātul-jiyād","at":"(Ingatlah) ketika pada suatu sore dipertunjukkan kepadanya (kuda-kuda) yang jinak, (tetapi) sangat cepat larinya,"},{"id":"32","ar":"فَقَالَ اِنِّيْٓ اَحْبَبْتُ حُبَّ الْخَيْرِ عَنْ ذِكْرِ رَبِّيْۚ حَتّٰى تَوَارَتْ بِالْحِجَابِۗ","bc":"fa qāla innī aḥbabtu ḥubbal-khairi 'an żikri rabbī, ḥattā tawārat bil-ḥijāb","at":"maka dia berkata, “Sesungguhnya aku menyukai segala yang baik (kuda), yang membuat aku ingat akan (kebesaran) Tuhanku, sampai matahari terbenam.”"},{"id":"33","ar":"رُدُّوْهَا عَلَيَّ ۚفَطَفِقَ مَسْحًا ۢبِالسُّوْقِ وَالْاَعْنَاقِ","bc":"ruddụhā 'alayy, fa ṭafiqa mas-ḥam bis-sụqi wal-a'nāq","at":"”Bawalah semua kuda itu kembali kepadaku.” Lalu dia mengusap-usap kaki dan leher kuda itu."},{"id":"34","ar":"وَلَقَدْ فَتَنَّا سُلَيْمٰنَ وَاَلْقَيْنَا عَلٰى كُرْسِيِّهٖ جَسَدًا ثُمَّ اَنَابَ","bc":"wa laqad fatannā sulaimāna wa alqainā 'alā kursiyyihī jasadan ṡumma anāb","at":"Dan sungguh, Kami telah menguji Sulaiman dan Kami jadikan (dia) tergeletak di atas kursinya sebagai tubuh (yang lemah karena sakit), kemudian dia bertobat."},{"id":"35","ar":"قَالَ رَبِّ اغْفِرْ لِيْ وَهَبْ لِيْ مُلْكًا لَّا يَنْۢبَغِيْ لِاَحَدٍ مِّنْۢ بَعْدِيْۚ اِنَّكَ اَنْتَ الْوَهَّابُ","bc":"qāla rabbigfir lī wa hab lī mulkal lā yambagī li`aḥadim mim ba'dī, innaka antal-wahhāb","at":"Dia berkata, “Ya Tuhanku, ampunilah aku dan anugerahkanlah kepadaku kerajaan yang tidak dimiliki oleh siapa pun setelahku. Sungguh, Engkaulah Yang Maha Pemberi.”"},{"id":"36","ar":"فَسَخَّرْنَا لَهُ الرِّيْحَ تَجْرِيْ بِاَمْرِهٖ رُخَاۤءً حَيْثُ اَصَابَۙ","bc":"fa sakhkharnā lahur-rīḥa tajrī bi`amrihī rukhā`an ḥaiṡu aṣāb","at":"Kemudian Kami tundukkan kepadanya angin yang berhembus dengan baik menurut perintahnya ke mana saja yang dikehendakinya,"},{"id":"37","ar":"وَالشَّيٰطِيْنَ كُلَّ بَنَّاۤءٍ وَّغَوَّاصٍۙ","bc":"wasy-syayāṭīna kulla bannā`iw wa gawwāṣ","at":"dan (Kami tundukkan pula kepadanya) setan-setan, semuanya ahli bangunan dan penyelam,"},{"id":"38","ar":"وَّاٰخَرِيْنَ مُقَرَّنِيْنَ فِى الْاَصْفَادِ","bc":"wa ākharīna muqarranīna fil-aṣfād","at":"dan (setan) yang lain yang terikat dalam belenggu."},{"id":"39","ar":"هٰذَا عَطَاۤؤُنَا فَامْنُنْ اَوْ اَمْسِكْ بِغَيْرِ حِسَابٍ","bc":"hāżā 'aṭā`unā famnun au amsik bigairi ḥisāb","at":"Inilah anugerah Kami; maka berikanlah (kepada orang lain) atau tahanlah (untuk dirimu sendiri) tanpa perhitungan."},{"id":"40","ar":"وَاِنَّ لَهٗ عِنْدَنَا لَزُلْفٰى وَحُسْنَ مَاٰبٍ","bc":"wa inna lahụ 'indanā lazulfā wa ḥusna ma`āb","at":"Dan sungguh, dia mempunyai kedudukan yang dekat pada sisi Kami dan tempat kembali yang baik."},{"id":"41","ar":"وَاذْكُرْ عَبْدَنَآ اَيُّوْبَۘ اِذْ نَادٰى رَبَّهٗٓ اَنِّيْ مَسَّنِيَ الشَّيْطٰنُ بِنُصْبٍ وَّعَذَابٍۗ","bc":"ważkur 'abdanā ayyụb, iż nādā rabbahū annī massaniyasy-syaiṭānu binuṣbiw wa 'ażāb","at":"Dan ingatlah akan hamba Kami Ayyub ketika dia menyeru Tuhannya, “Sesungguhnya aku diganggu setan dengan penderitaan dan bencana.”"},{"id":"42","ar":"اُرْكُضْ بِرِجْلِكَۚ هٰذَا مُغْتَسَلٌۢ بَارِدٌ وَّشَرَابٌ","bc":"urkuḍ birijlik, hāżā mugtasalum bāriduw wa syarāb","at":"Allah berfirman), “Hentakkanlah kakimu; inilah air yang sejuk untuk mandi dan untuk minum.”"},{"id":"43","ar":"وَوَهَبْنَا لَهٗٓ اَهْلَهٗ وَمِثْلَهُمْ مَّعَهُمْ رَحْمَةً مِّنَّا وَذِكْرٰى لِاُولِى الْاَلْبَابِ","bc":"wa wahabnā lahū ahlahụ wa miṡlahum ma'ahum raḥmatam minnā wa żikrā li`ulil-albāb","at":"Dan Kami anugerahi dia (dengan mengumpulkan kembali) keluarganya dan Kami lipatgandakan jumlah mereka, sebagai rahmat dari Kami dan pelajaran bagi orang-orang yang berpikiran sehat."},{"id":"44","ar":"وَخُذْ بِيَدِكَ ضِغْثًا فَاضْرِبْ بِّهٖ وَلَا تَحْنَثْ ۗاِنَّا وَجَدْنٰهُ صَابِرًا ۗنِعْمَ الْعَبْدُ ۗاِنَّهٗٓ اَوَّابٌ","bc":"wa khuż biyadika ḍigṡan faḍrib bihī wa lā taḥnaṡ, innā wajadnāhu ṣābirā, ni'mal-'abd, innahū awwāb","at":"Dan ambillah seikat (rumput) dengan tanganmu, lalu pukullah dengan itu dan janganlah engkau melanggar sumpah. Sesungguhnya Kami dapati dia (Ayyub) seorang yang sabar. Dialah sebaik-baik hamba. Sungguh, dia sangat taat (kepada Allah)."},{"id":"45","ar":"وَاذْكُرْ عِبٰدَنَآ اِبْرٰهِيْمَ وَاِسْحٰقَ وَيَعْقُوْبَ اُولِى الْاَيْدِيْ وَالْاَبْصَارِ","bc":"ważkur 'ibādanā ibrāhīma wa is-ḥāqa wa ya'qụba ulil-aidī wal-abṣār","at":"Dan ingatlah hamba-hamba Kami: Ibrahim, Ishak dan Yakub yang mempunyai kekuatan-kekuatan yang besar dan ilmu-ilmu (yang tinggi)."},{"id":"46","ar":"اِنَّآ اَخْلَصْنٰهُمْ بِخَالِصَةٍ ذِكْرَى الدَّارِۚ ","bc":"innā akhlaṣnāhum bikhāliṣatin żikrad-dār","at":"Sungguh, Kami telah menyucikan mereka dengan (menganugerahkan) akhlak yang tinggi kepadanya yaitu selalu mengingatkan (manusia) kepada negeri akhirat."},{"id":"47","ar":"وَاِنَّهُمْ عِنْدَنَا لَمِنَ الْمُصْطَفَيْنَ الْاَخْيَارِۗ","bc":"wa innahum 'indanā laminal-muṣṭafainal-akhyār","at":"Dan sungguh, di sisi Kami mereka termasuk orang-orang pilihan yang paling baik."},{"id":"48","ar":"وَاذْكُرْ اِسْمٰعِيْلَ وَالْيَسَعَ وَذَا الْكِفْلِ ۗوَكُلٌّ مِّنَ الْاَخْيَارِۗ","bc":"ważkur ismā'īla walyasa'a wa żal-kifl, wa kullum minal-akhyār","at":"Dan ingatlah Ismail, Ilyasa‘ dan Zulkifli. Semuanya termasuk orang-orang yang paling baik."},{"id":"49","ar":"هٰذَا ذِكْرٌ ۗوَاِنَّ لِلْمُتَّقِيْنَ لَحُسْنَ مَاٰبٍۙ ","bc":"hāżā żikr, wa inna lil-muttaqīna laḥusna ma`āb","at":"Ini adalah kehormatan (bagi mereka). Dan sungguh, bagi orang-orang yang bertakwa (disediakan) tempat kembali yang terbaik,"},{"id":"50","ar":"جَنّٰتِ عَدْنٍ مُّفَتَّحَةً لَّهُمُ الْاَبْوَابُۚ","bc":"jannāti 'adnim mufattaḥatal lahumul-abwāb","at":"(yaitu) surga ’Adn yang pintu-pintunya terbuka bagi mereka,"},{"id":"51","ar":"مُتَّكِـِٕيْنَ فِيْهَا يَدْعُوْنَ فِيْهَا بِفَاكِهَةٍ كَثِيْرَةٍ وَّشَرَابٍ","bc":"muttaki`īna fīhā yad'ụna fīhā bifākihating kaṡīratiw wa syarāb","at":"di dalamnya mereka bersandar (di atas dipan-dipan) sambil meminta buah-buahan yang banyak dan minuman (di surga itu)."},{"id":"52","ar":"وَعِنْدَهُمْ قٰصِرٰتُ الطَّرْفِ اَتْرَابٌ","bc":"wa 'indahum qāṣirātuṭ-ṭarfi atrāb","at":"dan di samping mereka (ada bidadari-bidadari) yang redup pandangannya dan sebaya umurnya."},{"id":"53","ar":"هٰذَا مَا تُوْعَدُوْنَ لِيَوْمِ الْحِسَابِ","bc":"hāżā mā tụ'adụna liyaumil-ḥisāb","at":"Inilah apa yang dijanjikan kepadamu pada hari perhitungan."},{"id":"54","ar":"اِنَّ هٰذَا لَرِزْقُنَا مَا لَهٗ مِنْ نَّفَادٍۚ","bc":"inna hāżā larizqunā mā lahụ min nafād","at":"Sungguh, inilah rezeki dari Kami yang tidak ada habis-habisnya."},{"id":"55","ar":"هٰذَا ۗوَاِنَّ لِلطّٰغِيْنَ لَشَرَّ مَاٰبٍۙ","bc":"hāżā, wa inna liṭ-ṭāgīna lasyarra ma`āb","at":"Beginilah (keadaan mereka). Dan sungguh, bagi orang-orang yang durhaka pasti (disediakan) tempat kembali yang buruk,"},{"id":"56","ar":"جَهَنَّمَۚ يَصْلَوْنَهَاۚ فَبِئْسَ الْمِهَادُ","bc":"jahannam, yaṣlaunahā, fa bi`sal-mihād","at":"(yaitu) neraka Jahanam yang mereka masuki; maka itulah seburuk-buruk tempat tinggal."},{"id":"57","ar":"هٰذَاۙ فَلْيَذُوْقُوْهُ حَمِيْمٌ وَّغَسَّاقٌۙ","bc":"hāżā falyażụqụhu ḥamīmuw wa gassāq","at":"Inilah (azab neraka), maka biarlah mereka merasakannya, (minuman mereka) air yang sangat panas dan air yang sangat dingin,"},{"id":"58","ar":"وَّاٰخَرُ مِنْ شَكْلِهٖٓ اَزْوَاجٌۗ","bc":"wa ākharu min syaklihī azwāj","at":"dan berbagai macam (azab) yang lain yang serupa itu."},{"id":"59","ar":"هٰذَا فَوْجٌ مُّقْتَحِمٌ مَّعَكُمْۚ لَا مَرْحَبًا ۢبِهِمْ ۗ اِنَّهُمْ صَالُوا النَّارِ","bc":"hāżā faujum muqtaḥimum ma'akum, lā mar-ḥabam bihim, innahum ṣālun-nār","at":"(Dikatakan kepada mereka), “Ini rombongan besar (pengikut-pengikutmu) yang masuk berdesak-desak bersama kamu (ke neraka).” Tidak ada ucapan selamat datang bagi mereka karena sesungguhnya mereka akan masuk neraka (kata pemimpin-pemimpin mereka)."},{"id":"60","ar":"قَالُوْا بَلْ اَنْتُمْ لَا مَرْحَبًاۢ بِكُمْ ۗ اَنْتُمْ قَدَّمْتُمُوْهُ لَنَاۚ فَبِئْسَ الْقَرَارُ","bc":"qālụ bal antum lā mar-ḥabam bikum, antum qaddamtumụhu lanā, fa bi`sal-qarār","at":"(Para pengikut mereka menjawab), “Sebenarnya kamulah yang (lebih pan-tas) tidak menerima ucapan selamat datang, karena kamulah yang menjerumuskan kami ke dalam azab, maka itulah seburuk-buruk tempat menetap.”"},{"id":"61","ar":"قَالُوْا رَبَّنَا مَنْ قَدَّمَ لَنَا هٰذَا فَزِدْهُ عَذَابًا ضِعْفًا فِى النَّارِ","bc":"qālụ rabbanā mang qaddama lanā hāżā fa zid-hu 'ażāban ḍi'fan fin-nār","at":"Mereka berkata (lagi), “Ya Tuhan kami, barangsiapa menjerumuskan kami ke dalam (azab) ini, maka tambahkanlah azab kepadanya dua kali lipat di dalam neraka.”"},{"id":"62","ar":"وَقَالُوْا مَا لَنَا لَا نَرٰى رِجَالًا كُنَّا نَعُدُّهُمْ مِّنَ الْاَشْرَارِ","bc":"wa qālụ mā lanā lā narā rijālang kunnā na'udduhum minal-asyrār","at":"Dan (orang-orang durhaka) berkata, “Mengapa kami tidak melihat orang-orang yang dahulu (di dunia) kami anggap sebagai orang-orang yang jahat (hina)."},{"id":"63","ar":"اَتَّخَذْنٰهُمْ سِخْرِيًّا اَمْ زَاغَتْ عَنْهُمُ الْاَبْصَارُ","bc":"attakhażnāhum sikhriyyan am zāgat 'an-humul-abṣār","at":"Dahulu kami menjadikan mereka olok-olokan, ataukah karena penglihatan kami yang tidak melihat mereka?”"},{"id":"64","ar":"اِنَّ ذٰلِكَ لَحَقٌّ تَخَاصُمُ اَهْلِ النَّارِ","bc":"inna żālika laḥaqqun takhāṣumu ahlin-nār","at":"Sungguh, yang demikian benar-benar terjadi, (yaitu) pertengkaran di antara penghuni neraka."},{"id":"65","ar":"قُلْ اِنَّمَآ اَنَا۠ مُنْذِرٌ ۖوَّمَا مِنْ اِلٰهٍ اِلَّا اللّٰهُ الْوَاحِدُ الْقَهَّارُ","bc":"qul innamā ana munżiruw wa mā min ilāhin illallāhul-wāḥidul-qahhār","at":"Katakanlah (Muhammad), “Sesungguhnya aku hanya seorang pemberi peringatan, tidak ada tuhan selain Allah Yang Maha Esa, Mahaperkasa,"},{"id":"66","ar":"رَبُّ السَّمٰوٰتِ وَالْاَرْضِ وَمَا بَيْنَهُمَا الْعَزِيْزُ الْغَفَّارُ","bc":"rabbus-samāwāti wal-arḍi wa mā bainahumal-'azīzul-gaffār","at":"(yaitu) Tuhan langit dan bumi dan apa yang ada di antara keduanya, Yang Mahaperkasa, Maha Pengampun.”"},{"id":"67","ar":"قُلْ هُوَ نَبَؤٌا عَظِيْمٌۙ","bc":"qul huwa naba`un 'aẓīm","at":"Katakanlah, “Itu (Al-Qur'an) adalah berita besar,"},{"id":"68","ar":"اَنْتُمْ عَنْهُ مُعْرِضُوْنَ","bc":"antum 'an-hu mu'riḍụn","at":"yang kamu berpaling darinya."},{"id":"69","ar":"مَا كَانَ لِيَ مِنْ عِلْمٍۢ بِالْمَلَاِ الْاَعْلٰٓى اِذْ يَخْتَصِمُوْنَ","bc":"mā kāna liya min 'ilmim bil-mala`il-a'lā iż yakhtaṣimụn","at":"Aku tidak mempunyai pengetahuan sedikit pun tentang al-mala’ul a’la (malaikat) itu ketika mereka berbantah-bantahan."},{"id":"70","ar":"اِنْ يُّوْحٰىٓ اِلَيَّ اِلَّآ اَنَّمَآ اَنَا۠ نَذِيْرٌ مُّبِيْنٌ","bc":"iy yụḥā ilayya illā annamā ana nażīrum mubīn","at":"Yang diwahyukan kepadaku, bahwa aku hanyalah seorang pemberi peringatan yang nyata.”"},{"id":"71","ar":"اِذْ قَالَ رَبُّكَ لِلْمَلٰۤىِٕكَةِ اِنِّيْ خَالِقٌۢ بَشَرًا مِّنْ طِيْنٍ","bc":"iż qāla rabbuka lil-malā`ikati innī khāliqum basyaram min ṭīn","at":"(Ingatlah) ketika Tuhanmu berfirman kepada malaikat, “Sesungguhnya Aku akan menciptakan manusia dari tanah."},{"id":"72","ar":"فَاِذَا سَوَّيْتُهٗ وَنَفَخْتُ فِيْهِ مِنْ رُّوْحِيْ فَقَعُوْا لَهٗ سٰجِدِيْنَ","bc":"fa iżā sawwaituhụ wa nafakhtu fīhi mir rụḥī faqa'ụ lahụ sājidīn","at":"Kemudian apabila telah Aku sempurnakan kejadiannya dan Aku tiupkan roh (ciptaan)-Ku kepadanya; maka tunduklah kamu dengan bersujud kepadanya.”"},{"id":"73","ar":"فَسَجَدَ الْمَلٰۤىِٕكَةُ كُلُّهُمْ اَجْمَعُوْنَۙ","bc":"fa sajadal-malā`ikatu kulluhum ajma'ụn","at":"Lalu para malaikat itu bersujud semuanya,"},{"id":"74","ar":"اِلَّآ اِبْلِيْسَۗ اِسْتَكْبَرَ وَكَانَ مِنَ الْكٰفِرِيْنَ","bc":"illā iblīs, istakbara wa kāna minal-kāfirīn","at":"kecuali Iblis; ia menyombongkan diri dan ia termasuk golongan yang kafir."},{"id":"75","ar":"قَالَ يٰٓاِبْلِيْسُ مَا مَنَعَكَ اَنْ تَسْجُدَ لِمَا خَلَقْتُ بِيَدَيَّ ۗ اَسْتَكْبَرْتَ اَمْ كُنْتَ مِنَ الْعَالِيْنَ","bc":"qāla yā iblīsu mā mana'aka an tasjuda limā khalaqtu biyadayy, astakbarta am kunta minal-'ālīn","at":"(Allah) berfirman, “Wahai Iblis, apakah yang menghalangi kamu sujud kepada yang telah Aku ciptakan dengan kekuasaan-Ku. Apakah kamu menyombongkan diri atau kamu (merasa) termasuk golongan yang (lebih) tinggi?”"},{"id":"76","ar":"قَالَ اَنَا۠ خَيْرٌ مِّنْهُ خَلَقْتَنِيْ مِنْ نَّارٍ وَّخَلَقْتَهٗ مِنْ طِيْنٍ","bc":"qāla ana khairum min-hu khalaqtanī min nāriw wa khalaqtahụ min ṭīn","at":"(Iblis) berkata, “Aku lebih baik daripadanya, karena Engkau ciptakan aku dari api, sedangkan dia Engkau ciptakan dari tanah.”"},{"id":"77","ar":"قَالَ فَاخْرُجْ مِنْهَا فَاِنَّكَ رَجِيْمٌۖ","bc":"qāla fakhruj min-hā fa innaka rajīm","at":"(Allah) berfirman, “Kalau begitu keluarlah kamu dari surga! Sesungguhnya kamu adalah makhluk yang terkutuk."},{"id":"78","ar":"وَّاِنَّ عَلَيْكَ لَعْنَتِيْٓ اِلٰى يَوْمِ الدِّيْنِ","bc":"wa inna 'alaika la'natī ilā yaumid-dīn","at":"Dan sungguh, kutukan-Ku tetap atasmu sampai hari pembalasan.”"},{"id":"79","ar":"قَالَ رَبِّ فَاَنْظِرْنِيْٓ اِلٰى يَوْمِ يُبْعَثُوْنَ","bc":"qāla rabbi fa anẓirnī ilā yaumi yub'aṡụn","at":"(Iblis) berkata, “Ya Tuhanku, tangguhkanlah aku sampai pada hari mereka dibangkitkan.”"},{"id":"80","ar":"قَالَ فَاِنَّكَ مِنَ الْمُنْظَرِيْنَۙ","bc":"qāla fa innaka minal-munẓarīn","at":"(Allah) berfirman, “Maka sesungguhnya kamu termasuk golongan yang diberi penangguhan,"},{"id":"81","ar":"اِلٰى يَوْمِ الْوَقْتِ الْمَعْلُوْمِ","bc":"ilā yaumil-waqtil-ma'lụm","at":"sampai pada hari yang telah ditentukan waktunya (hari Kiamat).”"},{"id":"82","ar":"قَالَ فَبِعِزَّتِكَ لَاُغْوِيَنَّهُمْ اَجْمَعِيْنَۙ","bc":"qāla fa bi'izzatika la`ugwiyannahum ajma'īn","at":"(Iblis) menjawab, “Demi kemuliaan-Mu, pasti aku akan menyesatkan mereka semuanya,"},{"id":"83","ar":"اِلَّا عِبَادَكَ مِنْهُمُ الْمُخْلَصِيْنَ","bc":"illā 'ibādaka min-humul-mukhlaṣīn","at":"kecuali hamba-hamba-Mu yang terpilih di antara mereka.”"},{"id":"84","ar":"قَالَ فَالْحَقُّۖ وَالْحَقَّ اَقُوْلُۚ","bc":"qāla fal-ḥaqqu wal-ḥaqqa aqụl","at":"(Allah) berfirman, “Maka yang benar (adalah sumpahku), dan hanya kebenaran itulah yang Aku katakan."},{"id":"85","ar":"لَاَمْلَئَنَّ جَهَنَّمَ مِنْكَ وَمِمَّنْ تَبِعَكَ مِنْهُمْ اَجْمَعِيْنَ","bc":"la`amla`anna jahannama mingka wa mim man tabi'aka min-hum ajma'īn","at":"Sungguh, Aku akan memenuhi neraka Jahanam dengan kamu dan dengan orang-orang yang mengikutimu di antara mereka semuanya.”"},{"id":"86","ar":"قُلْ مَآ اَسْـَٔلُكُمْ عَلَيْهِ مِنْ اَجْرٍ وَّمَآ اَنَا۠ مِنَ الْمُتَكَلِّفِيْنَ","bc":"qul mā as`alukum 'alaihi min ajriw wa mā ana minal-mutakallifīn","at":"Katakanlah (Muhammad), “Aku tidak meminta imbalan sedikit pun kepadamu atasnya (dakwahku); dan aku bukanlah termasuk orang yang mengada-ada."},{"id":"87","ar":"اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَ","bc":"in huwa illā żikrul lil-'ālamīn","at":"(Al-Qur'an) ini tidak lain hanyalah peringatan bagi seluruh alam."},{"id":"88","ar":"وَلَتَعْلَمُنَّ نَبَاَهٗ بَعْدَ حِيْنٍ","bc":"wa lata'lamunna naba`ahụ ba'da ḥīn","at":"Dan sungguh, kamu akan mengetahui (kebenaran) beritanya (Al-Qur'an) setelah beberapa waktu lagi.”"}],taha=[{"id":"1","ar":"طٰهٰ ۚ","bc":"ṭā hā","at":"Thaha"},{"id":"2","ar":"مَآ اَنْزَلْنَا عَلَيْكَ الْقُرْاٰنَ لِتَشْقٰٓى ۙ","bc":"mā anzalnā 'alaikal-qur`āna litasyqā","at":" Kami tidak menurunkan Al-Qur'an ini kepadamu (Muhammad) agar engkau menjadi susah;"},{"id":"3","ar":"اِلَّا تَذْكِرَةً لِّمَنْ يَّخْشٰى ۙ","bc":"illā tażkiratal limay yakhsyā","at":" melainkan sebagai peringatan bagi orang yang takut (kepada Allah),"},{"id":"4","ar":"تَنْزِيْلًا مِّمَّنْ خَلَقَ الْاَرْضَ وَالسَّمٰوٰتِ الْعُلٰى ۗ","bc":"tanzīlam mim man khalaqal-arḍa was-samāwātil-'ulā","at":"diturunkan dari (Allah) yang menciptakan bumi dan langit yang tinggi,"},{"id":"5","ar":"اَلرَّحْمٰنُ عَلَى الْعَرْشِ اسْتَوٰى ","bc":"ar-raḥmānu 'alal-'arsyistawā","at":"(yaitu) Yang Maha Pengasih, yang bersemayam di atas ‘Arsy."},{"id":"6","ar":"لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِ وَمَا بَيْنَهُمَا وَمَا تَحْتَ الثَّرٰى ","bc":"lahụ mā fis-samāwāti wa mā fil-arḍi wa mā bainahumā wa mā taḥtaṡ-ṡarā","at":"Milik-Nyalah apa yang ada di langit, apa yang ada di bumi, apa yang ada di antara keduanya, dan apa yang ada di bawah tanah."},{"id":"7","ar":"وَاِنْ تَجْهَرْ بِالْقَوْلِ فَاِنَّهٗ يَعْلَمُ السِّرَّ وَاَخْفٰى ","bc":"wa in taj-har bil-qauli fa innahụ ya'lamus-sirra wa akhfā","at":"Dan jika engkau mengeraskan ucapanmu, sungguh, Dia mengetahui rahasia dan yang lebih tersembunyi."},{"id":"8","ar":"اَللّٰهُ لَآ اِلٰهَ اِلَّا هُوَۗ لَهُ الْاَسْمَاۤءُ الْحُسْنٰى ","bc":"allāhu lā ilāha illā huw, lahul-asmā`ul-ḥusnā","at":"(Dialah) Allah, tidak ada tuhan selain Dia, yang mempunyai nama-nama yang terbaik."},{"id":"9","ar":"وَهَلْ اَتٰىكَ حَدِيْثُ مُوْسٰى ۘ","bc":"wa hal atāka ḥadīṡu mụsā","at":" Dan apakah telah sampai kepadamu kisah Musa?"},{"id":"10","ar":"اِذْ رَاٰ نَارًا فَقَالَ لِاَهْلِهِ امْكُثُوْٓا اِنِّيْ اٰنَسْتُ نَارًا لَّعَلِّيْٓ اٰتِيْكُمْ مِّنْهَا بِقَبَسٍ اَوْ اَجِدُ عَلَى النَّارِ هُدًى ","bc":"iż ra`ā nāran fa qāla li`ahlihimkuṡū innī ānastu nāral la'allī ātīkum min-hā biqabasin au ajidu 'alan-nāri hudā","at":" Ketika dia (Musa) melihat api, lalu dia berkata kepada keluarganya, “Tinggallah kamu (di sini), sesungguhnya aku melihat api, mudah-mudahan aku dapat membawa sedikit nyala api kepadamu atau aku akan mendapat petunjuk di tempat api itu.”"},{"id":"11","ar":"فَلَمَّآ اَتٰىهَا نُوْدِيَ يٰمُوْسٰٓى ۙ","bc":"fa lammā atāhā nụdiya yā mụsā","at":"Maka ketika dia mendatanginya (ke tempat api itu) dia dipanggil, “Wahai Musa!"},{"id":"12","ar":"اِنِّيْٓ اَنَا۠ رَبُّكَ فَاخْلَعْ نَعْلَيْكَۚ اِنَّكَ بِالْوَادِ الْمُقَدَّسِ طُوًى ۗ","bc":"innī anā rabbuka fakhla' na'laīk, innaka bil-wādil-muqaddasi ṭuwā","at":"Sungguh, Aku adalah Tuhanmu, maka lepaskan kedua terompahmu. Karena sesungguhnya engkau berada di lembah yang suci, Tuwa."},{"id":"13","ar":"وَاَنَا اخْتَرْتُكَ فَاسْتَمِعْ لِمَا يُوْحٰى ","bc":"wa anakhtartuka fastami' limā yụḥā","at":" Dan Aku telah memilih engkau, maka dengarkanlah apa yang akan diwahyukan (kepadamu)."},{"id":"14","ar":"اِنَّنِيْٓ اَنَا اللّٰهُ لَآ اِلٰهَ اِلَّآ اَنَا۠ فَاعْبُدْنِيْۙ وَاَقِمِ الصَّلٰوةَ لِذِكْرِيْ ","bc":"innanī anallāhu lā ilāha illā ana fa'budnī wa aqimiṣ-ṣalāta liżikrī","at":" Sungguh, Aku ini Allah, tidak ada tuhan selain Aku, maka sembahlah Aku dan laksanakanlah salat untuk mengingat Aku."},{"id":"15","ar":"اِنَّ السَّاعَةَ اٰتِيَةٌ اَكَادُ اُخْفِيْهَا لِتُجْزٰى كُلُّ نَفْسٍۢ بِمَا تَسْعٰى ","bc":"innas-sā'ata ātiyatun akādu ukhfīhā litujzā kullu nafsim bimā tas'ā","at":"Sungguh, hari Kiamat itu akan datang, Aku merahasiakan (waktunya) agar setiap orang dibalas sesuai dengan apa yang telah dia usahakan."},{"id":"16","ar":"فَلَا يَصُدَّنَّكَ عَنْهَا مَنْ لَّا يُؤْمِنُ بِهَا وَاتَّبَعَ هَوٰىهُ فَتَرْدٰى ","bc":"fa lā yaṣuddannaka 'an-hā mal lā yu`minu bihā wattaba'a hawāhu fa tardā","at":" Maka janganlah engkau dipalingkan dari (Kiamat itu) oleh orang yang tidak beriman kepadanya dan oleh orang yang mengikuti keinginannya, yang menyebabkan engkau binasa.”"},{"id":"17","ar":"وَمَا تِلْكَ بِيَمِيْنِكَ يٰمُوْسٰى ","bc":"wa mā tilka biyamīnika yā mụsā","at":"”Dan apakah yang ada di tangan kananmu, wahai Musa? ”"},{"id":"18","ar":"قَالَ هِيَ عَصَايَۚ اَتَوَكَّؤُا عَلَيْهَا وَاَهُشُّ بِهَا عَلٰى غَنَمِيْ وَلِيَ فِيْهَا مَاٰرِبُ اُخْرٰى ","bc":"qāla hiya 'aṣāy, atawakka`u 'alaihā wa ahusysyu bihā 'alā ganamī wa liya fīhā ma`āribu ukhrā","at":"Dia (Musa) berkata, “Ini adalah tongkatku, aku bertumpu padanya, dan aku merontokkan (daun-daun) dengannya untuk (makanan) kambingku, dan bagiku masih ada lagi manfaat yang lain.”"},{"id":"19","ar":"قَالَ اَلْقِهَا يٰمُوْسٰى ","bc":"qāla alqihā yā mụsā","at":"Dia (Allah) berfirman, “Lemparkanlah ia, wahai Musa!”"},{"id":"20","ar":"فَاَلْقٰىهَا فَاِذَا هِيَ حَيَّةٌ تَسْعٰى ","bc":"fa alqāhā fa iżā hiya ḥayyatun tas'ā","at":" Lalu (Musa) melemparkan tongkat itu, maka tiba-tiba ia menjadi seekor ular yang merayap dengan cepat."},{"id":"21","ar":"قَالَ خُذْهَا وَلَا تَخَفْۗ سَنُعِيْدُهَا سِيْرَتَهَا الْاُوْلٰى ","bc":"qāla khuż-hā wa lā takhaf, sanu'īduhā sīratahal-ụlā","at":"Dia (Allah) berfirman, “Peganglah ia dan jangan takut, Kami akan mengembalikannya kepada keadaannya semula,"},{"id":"22","ar":"وَاضْمُمْ يَدَكَ اِلٰى جَنَاحِكَ تَخْرُجْ بَيْضَاۤءَ مِنْ غَيْرِ سُوْۤءٍ اٰيَةً اُخْرٰىۙ ","bc":"waḍmum yadaka ilā janāḥika takhruj baiḍā`a min gairi sū`in āyatan ukhrā","at":"dan kepitlah tanganmu ke ketiakmu, niscaya ia keluar menjadi putih (bercahaya) tanpa cacat, sebagai mukjizat yang lain,"},{"id":"23","ar":"لِنُرِيَكَ مِنْ اٰيٰتِنَا الْكُبْرٰى ۚ","bc":"linuriyaka min āyātinal-kubrā","at":"untuk Kami perlihatkan kepadamu (sebagian) dari tanda-tanda kebesaran Kami yang sangat besar,"},{"id":"24","ar":"اِذْهَبْ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰى","bc":"iż-hab ilā fir'auna innahụ ṭagā","at":"Pergilah kepada Fir‘aun; dia benar-benar telah melampaui batas.”"},{"id":"25","ar":"قَالَ رَبِّ اشْرَحْ لِيْ صَدْرِيْ ۙ","bc":"qāla rabbisyraḥ lī ṣadrī","at":" Dia (Musa) berkata, “Ya Tuhanku, lapangkanlah dadaku,"},{"id":"26","ar":"وَيَسِّرْ لِيْٓ اَمْرِيْ ۙ","bc":"wa yassir lī amrī","at":"dan mudahkanlah untukku urusanku, "},{"id":"27","ar":"وَاحْلُلْ عُقْدَةً مِّنْ لِّسَانِيْ ۙ","bc":"waḥlul 'uqdatam mil lisānī","at":"dan lepaskanlah kekakuan dari lidahku,"},{"id":"28","ar":"يَفْقَهُوْا قَوْلِيْ ۖ","bc":"yafqahụ qaulī","at":"agar mereka mengerti perkataanku,"},{"id":"29","ar":"وَاجْعَلْ لِّيْ وَزِيْرًا مِّنْ اَهْلِيْ ۙ","bc":"waj'al lī wazīram min ahlī","at":"dan jadikanlah untukku seorang pembantu dari keluargaku,"},{"id":"30","ar":"هٰرُوْنَ اَخِى ۙ","bc":"hārụna akhī","at":"(yaitu) Harun, saudaraku,"},{"id":"31","ar":"اشْدُدْ بِهٖٓ اَزْرِيْ ۙ","bc":"usydud bihī azrī","at":"teguhkanlah kekuatanku dengan (adanya) dia,"},{"id":"32","ar":"وَاَشْرِكْهُ فِيْٓ اَمْرِيْ ۙ","bc":"wa asyrik-hu fī amrī","at":"dan jadikanlah dia teman dalam urusanku,"},{"id":"33","ar":"كَيْ نُسَبِّحَكَ كَثِيْرًا ۙ","bc":"kai nusabbiḥaka kaṡīrā","at":"agar kami banyak bertasbih kepada-Mu,"},{"id":"34","ar":"وَّنَذْكُرَكَ كَثِيْرًا ۗ","bc":"wa nażkuraka kaṡīrā","at":"dan banyak mengingat-Mu,"},{"id":"35","ar":"اِنَّكَ كُنْتَ بِنَا بَصِيْرًا ","bc":"innaka kunta binā baṣīrā","at":"sesungguhnya Engkau Maha Melihat (keadaan) kami.”"},{"id":"36","ar":"قَالَ قَدْ اُوْتِيْتَ سُؤْلَكَ يٰمُوْسٰى ","bc":"qāla qad ụtīta su`laka yā mụsā","at":"Dia (Allah) berfirman, “Sungguh, telah diperkenankan permintaanmu, wahai Musa!"},{"id":"37","ar":"وَلَقَدْ مَنَنَّا عَلَيْكَ مَرَّةً اُخْرٰىٓ ۙ","bc":"wa laqad manannā 'alaika marratan ukhrā","at":"Dan sungguh, Kami telah memberi nikmat kepadamu pada kesempatan yang lain (sebelum ini),"},{"id":"38","ar":"اِذْ اَوْحَيْنَآ اِلٰٓى اُمِّكَ مَا يُوْحٰىٓ ۙ","bc":"iż auḥainā ilā ummika mā yụḥā","at":"(yaitu) ketika Kami mengilhamkan kepada ibumu sesuatu yang diilhamkan,"},{"id":"39","ar":"اَنِ اقْذِفِيْهِ فِى التَّابُوْتِ فَاقْذِفِيْهِ فِى الْيَمِّ فَلْيُلْقِهِ الْيَمُّ بِالسَّاحِلِ يَأْخُذْهُ عَدُوٌّ لِّيْ وَعَدُوٌّ لَّهٗ ۗوَاَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِّنِّيْ ەۚ وَلِتُصْنَعَ عَلٰى عَيْنِيْ ۘ","bc":"aniqżi fīhi fit-tābụti faqżi fīhi fil-yammi falyulqihil-yammu bis-sāḥili ya`khuż-hu 'aduwwul lī wa 'aduwwul lah, wa alqaitu 'alaika maḥabbatam minnī, wa lituṣna'a 'alā 'ainī","at":"(yaitu), letakkanlah dia (Musa) di dalam peti, kemudian hanyutkanlah dia ke sungai (Nil), maka biarlah (arus) sungai itu membawanya ke tepi, dia akan diambil oleh (Fir‘aun) musuh-Ku dan musuhnya. Aku telah melimpahkan kepadamu kasih sayang yang datang dari-Ku; dan agar engkau diasuh di bawah pengawasan-Ku."},{"id":"40","ar":"اِذْ تَمْشِيْٓ اُخْتُكَ فَتَقُوْلُ هَلْ اَدُلُّكُمْ عَلٰى مَنْ يَّكْفُلُهٗ ۗفَرَجَعْنٰكَ اِلٰٓى اُمِّكَ كَيْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ ەۗ وَقَتَلْتَ نَفْسًا فَنَجَّيْنٰكَ مِنَ الْغَمِّ وَفَتَنّٰكَ فُتُوْنًا ەۗ فَلَبِثْتَ سِنِيْنَ فِيْٓ اَهْلِ مَدْيَنَ ەۙ ثُمَّ جِئْتَ عَلٰى قَدَرٍ يّٰمُوْسٰى","bc":"iż tamsyī ukhtuka fa taqụlu hal adullukum 'alā may yakfuluh, fa raja'nāka ilā ummika kai taqarra 'ainuhā wa lā taḥzan, wa qatalta nafsan fa najjaināka minal-gammi wa fatannāka futụnā, fa labiṡta sinīna fī ahli madyana ṡumma ji`ta 'alā qadariy yā mụsā","at":"(Yaitu) ketika saudara perempuanmu berjalan, lalu dia berkata (kepada keluarga Fir‘aun), ‘Bolehkah saya menunjukkan kepadamu orang yang akan memeliharanya?’ Maka Kami mengembalikanmu kepada ibumu, agar senang hatinya dan tidak bersedih hati. Dan engkau pernah membunuh seseorang, lalu Kami selamatkan engkau dari kesulitan (yang besar) dan Kami telah mencobamu dengan beberapa cobaan (yang berat); lalu engkau tinggal beberapa tahun di antara penduduk Madyan, kemudian engkau, wahai Musa, datang menurut waktu yang ditetapkan,"},{"id":"41","ar":"وَاصْطَنَعْتُكَ لِنَفْسِيْۚ ","bc":"waṣṭana'tuka linafsī","at":"dan Aku telah memilihmu (menjadi rasul) untuk diri-Ku."},{"id":"42","ar":"اِذْهَبْ اَنْتَ وَاَخُوْكَ بِاٰيٰتِيْ وَلَا تَنِيَا فِيْ ذِكْرِيْۚ ","bc":"iż-hab anta wa akhụka bi`āyātī wa lā taniyā fī żikrī","at":"Pergilah engkau beserta saudaramu dengan membawa tanda-tanda (kekuasaan)-Ku, dan janganlah kamu berdua lalai mengingat-Ku;"},{"id":"43","ar":"اِذْهَبَآ اِلٰى فِرْعَوْنَ اِنَّهٗ طَغٰىۚ ","bc":"iż-habā ilā fir'auna innahụ ṭagā","at":"pergilah kamu berdua kepada Fir‘aun, karena dia benar-benar telah melampaui batas;"},{"id":"44","ar":"فَقُوْلَا لَهٗ قَوْلًا لَّيِّنًا لَّعَلَّهٗ يَتَذَكَّرُ اَوْ يَخْشٰى ","bc":"fa qụlā lahụ qaulal layyinal la'allahụ yatażakkaru au yakhsyā","at":"maka berbicaralah kamu berdua kepadanya (Fir‘aun) dengan kata-kata yang lemah lembut, mudah-mudahan dia sadar atau takut."},{"id":"45","ar":"قَالَا رَبَّنَآ اِنَّنَا نَخَافُ اَنْ يَّفْرُطَ عَلَيْنَآ اَوْ اَنْ يَّطْغٰى ","bc":"qālā rabbanā innanā nakhāfu ay yafruṭa 'alainā au ay yaṭgā","at":"Keduanya berkata, “Ya Tuhan kami, sungguh, kami khawatir dia akan segera menyiksa kami atau akan bertambah melampaui batas,”"},{"id":"46","ar":"قَالَ لَا تَخَافَآ اِنَّنِيْ مَعَكُمَآ اَسْمَعُ وَاَرٰى ","bc":"qāla lā takhāfā innanī ma'akumā asma'u wa arā","at":"Dia (Allah) berfirman, “Janganlah kamu berdua khawatir, sesungguhnya Aku bersama kamu berdua, Aku mendengar dan melihat."},{"id":"47","ar":"فَأْتِيٰهُ فَقُوْلَآ اِنَّا رَسُوْلَا رَبِّكَ فَاَرْسِلْ مَعَنَا بَنِيْٓ اِسْرَاۤءِيْلَ ەۙ وَلَا تُعَذِّبْهُمْۗ قَدْ جِئْنٰكَ بِاٰيَةٍ مِّنْ رَّبِّكَ ۗوَالسَّلٰمُ عَلٰى مَنِ اتَّبَعَ الْهُدٰى","bc":"fa`tiyāhu fa qụlā innā rasụlā rabbika fa arsil ma'anā banī isrā`īla wa lā tu'ażżib-hum, qad ji`nāka bi`āyatim mir rabbik, was-salāmu 'alā manittaba'al-hudā","at":"Maka pergilah kamu berdua kepadanya (Fir‘aun) dan katakanlah, “Sungguh, kami berdua adalah utusan Tuhanmu, maka lepaskanlah Bani Israil bersama kami dan janganlah engkau menyiksa mereka. Sungguh, kami datang kepadamu dengan membawa bukti (atas kerasulan kami) dari Tuhanmu. Dan keselamatan itu dilimpahkan kepada orang yang mengikuti petunjuk."},{"id":"48","ar":"اِنَّا قَدْ اُوْحِيَ اِلَيْنَآ اَنَّ الْعَذَابَ عَلٰى مَنْ كَذَّبَ وَتَوَلّٰى ","bc":"innā qad ụḥiya ilainā annal-'ażāba 'alā mang każżaba wa tawallā","at":"Sungguh, telah diwahyukan kepada kami bahwa siksa itu (ditimpakan) pada siapa pun yang mendustakan (ajaran agama yang kami bawa) dan berpaling (tidak mempedulikannya).”"},{"id":"49","ar":"قَالَ فَمَنْ رَّبُّكُمَا يٰمُوْسٰى ","bc":"qāla fa mar rabbukumā yā mụsā","at":"Dia (Fir‘aun) berkata, “Siapakah Tuhanmu berdua, wahai Musa?”"},{"id":"50","ar":"قَالَ رَبُّنَا الَّذِيْٓ اَعْطٰى كُلَّ شَيْءٍ خَلْقَهٗ ثُمَّ هَدٰى ","bc":"qāla rabbunallażī a'ṭā kulla syai`in khalqahụ ṡumma hadā","at":"Dia (Musa) menjawab, “Tuhan kami ialah (Tuhan) yang telah memberikan bentuk kejadian kepada segala sesuatu, kemudian memberinya petunjuk.”"},{"id":"51","ar":"قَالَ فَمَا بَالُ الْقُرُوْنِ الْاُوْلٰى ","bc":"qāla fa mā bālul-qurụnil-ụlā","at":"Dia (Fir‘aun) berkata, “Jadi bagaimana keadaan umat-umat yang dahulu?”"},{"id":"52","ar":"قَالَ عِلْمُهَا عِنْدَ رَبِّيْ فِيْ كِتٰبٍۚ لَا يَضِلُّ رَبِّيْ وَلَا يَنْسَىۖ ","bc":"qāla 'ilmuhā 'inda rabbī fī kitāb, lā yaḍillu rabbī wa lā yansā","at":"Dia (Musa) menjawab, “Pengetahuan tentang itu ada pada Tuhanku, di dalam sebuah Kitab (Lauh Mahfuzh), Tuhanku tidak akan salah ataupun lupa;"},{"id":"53","ar":"الَّذِيْ جَعَلَ لَكُمُ الْاَرْضَ مَهْدًا وَّسَلَكَ لَكُمْ فِيْهَا سُبُلًا وَّاَنْزَلَ مِنَ السَّمَاۤءِ مَاۤءًۗ فَاَخْرَجْنَا بِهٖٓ اَزْوَاجًا مِّنْ نَّبَاتٍ شَتّٰى ","bc":"allażī ja'ala lakumul-arḍa mahdaw wa salaka lakum fīhā subulaw wa anzala minas-samā`i mā`ā, fa akhrajnā bihī azwājam min nabātin syattā","at":"(Tuhan) yang telah menjadikan bumi sebagai hamparan bagimu, dan menjadikan jalan-jalan di atasnya bagimu, dan yang menurunkan air (hujan) dari langit.” Kemudian Kami tumbuhkan dengannya (air hujan itu) berjenis-jenis aneka macam tumbuh-tumbuhan."},{"id":"54","ar":"كُلُوْا وَارْعَوْا اَنْعَامَكُمْ ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّاُولِى النُّهٰى","bc":"kulụ war'au an'āmakum, inna fī żālika la`āyātil li`ulin-nuhā","at":"Makanlah dan gembalakanlah hewan-hewanmu. Sungguh, pada yang demikian itu, terdapat tanda-tanda (kebesaran Allah) bagi orang yang berakal."},{"id":"55","ar":"۞ مِنْهَا خَلَقْنٰكُمْ وَفِيْهَا نُعِيْدُكُمْ وَمِنْهَا نُخْرِجُكُمْ تَارَةً اُخْرٰى ","bc":"min-hā khalaqnākum wa fīhā nu'īdukum wa min-hā nukhrijukum tāratan ukhrā","at":"Darinya (tanah) itulah Kami menciptakan kamu dan kepadanyalah Kami akan mengembalikan kamu dan dari sanalah Kami akan mengeluarkan kamu pada waktu yang lain."},{"id":"56","ar":"وَلَقَدْ اَرَيْنٰهُ اٰيٰتِنَا كُلَّهَا فَكَذَّبَ وَاَبٰى ","bc":"wa laqad araināhu āyātinā kullahā fa każżaba wa abā","at":"Dan sungguh, Kami telah memperlihatkan kepadanya (Fir‘aun) tanda-tanda (kebesaran) Kami semuanya, ternyata dia mendustakan dan enggan (menerima kebenaran)."},{"id":"57","ar":"قَالَ اَجِئْتَنَا لِتُخْرِجَنَا مِنْ اَرْضِنَا بِسِحْرِكَ يٰمُوْسٰى ","bc":"qāla a ji`tanā litukhrijanā min arḍinā bisiḥrika yā mụsā","at":"Dia (Fir‘aun) berkata, “Apakah engkau datang kepada kami untuk mengusir kami dari negeri kami dengan sihirmu, wahai Musa?"},{"id":"58","ar":"فَلَنَأْتِيَنَّكَ بِسِحْرٍ مِّثْلِهٖ فَاجْعَلْ بَيْنَنَا وَبَيْنَكَ مَوْعِدًا لَّا نُخْلِفُهٗ نَحْنُ وَلَآ اَنْتَ مَكَانًا سُوًى ","bc":"fa lana`tiyannaka bisiḥrim miṡlihī faj'al bainanā wa bainaka mau'idal lā nukhlifuhụ naḥnu wa lā anta makānan suwā","at":"Maka kami pun pasti akan mendatangkan sihir semacam itu kepadamu, maka buatlah suatu perjanjian untuk pertemuan antara kami dan engkau yang kami tidak akan menyalahinya dan tidak (pula) engkau, di suatu tempat yang terbuka.”"},{"id":"59","ar":"قَالَ مَوْعِدُكُمْ يَوْمُ الزِّيْنَةِ وَاَنْ يُّحْشَرَ النَّاسُ ضُحًى ","bc":"qāla mau'idukum yaumuz-zīnati wa ay yuḥsyaran-nāsu ḍuḥā","at":"Dia (Musa) berkata, “(Perjanjian) waktu (untuk pertemuan kami dengan kamu itu) ialah pada hari raya dan hendaklah orang-orang dikumpulkan pada pagi hari (duha).”"},{"id":"60","ar":"فَتَوَلّٰى فِرْعَوْنُ فَجَمَعَ كَيْدَهٗ ثُمَّ اَتٰى ","bc":"fa tawallā fir'aunu fa jama'a kaidahụ ṡumma atā","at":"Maka Fir‘aun meninggalkan (tempat itu), lalu mengatur tipu dayanya, kemudian dia datang kembali (pada hari yang ditentukan)."},{"id":"61","ar":"قَالَ لَهُمْ مُّوْسٰى وَيْلَكُمْ لَا تَفْتَرُوْا عَلَى اللّٰهِ كَذِبًا فَيُسْحِتَكُمْ بِعَذَابٍۚ وَقَدْ خَابَ مَنِ افْتَرٰى ","bc":"qāla lahum mụsā wailakum lā taftarụ 'alallāhi każiban fa yus-ḥitakum bi'ażāb, wa qad khāba maniftarā","at":"Musa berkata kepada mereka (para pesihir), “Celakalah kamu! Janganlah kamu mengada-adakan kedustaan terhadap Allah, nanti Dia membinasakan kamu dengan azab.” Dan sungguh rugi orang yang mengada-adakan kedustaan."},{"id":"62","ar":"فَتَنَازَعُوْٓا اَمْرَهُمْ بَيْنَهُمْ وَاَسَرُّوا النَّجْوٰى ","bc":"fa tanāza'ū amrahum bainahum wa asarrun-najwā","at":"Maka mereka berbantah-bantahan tentang urusan mereka dan mereka merahasiakan percakapan (mereka)."},{"id":"63","ar":"قَالُوْٓا اِنْ هٰذٰنِ لَسٰحِرَانِ يُرِيْدَانِ اَنْ يُّخْرِجٰكُمْ مِّنْ اَرْضِكُمْ بِسِحْرِهِمَا وَيَذْهَبَا بِطَرِيْقَتِكُمُ الْمُثْلٰى ","bc":"qālū in hāżāni lasāḥirāni yurīdāni ay yukhrijākum min arḍikum bisiḥrihimā wa yaż-habā biṭarīqatikumul-muṡlā","at":"Mereka (para pesihir) berkata, “Sesungguhnya dua orang ini adalah pesihir yang hendak mengusirmu (Fir‘aun) dari negerimu dengan sihir mereka berdua, dan hendak melenyapkan adat kebiasaanmu yang utama."},{"id":"64","ar":"فَاَجْمِعُوْا كَيْدَكُمْ ثُمَّ ائْتُوْا صَفًّاۚ وَقَدْ اَفْلَحَ الْيَوْمَ مَنِ اسْتَعْلٰى ","bc":"fa ajmi'ụ kaidakum ṡumma`tụ ṣaffā, wa qad aflaḥal-yauma manista'lā","at":"Maka kumpulkanlah segala tipu daya (sihir) kamu, kemudian datanglah dengan berbaris, dan sungguh, beruntung orang yang menang pada hari ini.”"},{"id":"65","ar":"قَالُوْا يٰمُوْسٰٓى اِمَّآ اَنْ تُلْقِيَ وَاِمَّآ اَنْ نَّكُوْنَ اَوَّلَ مَنْ اَلْقٰى ","bc":"qālụ yā mụsā immā an tulqiya wa immā an nakụna awwala man alqā","at":"Mereka berkata, “Wahai Musa! Apakah engkau yang melemparkan (dahulu) atau kami yang lebih dahulu melemparkan?”"},{"id":"66","ar":"قَالَ بَلْ اَلْقُوْاۚ فَاِذَا حِبَالُهُمْ وَعِصِيُّهُمْ يُخَيَّلُ اِلَيْهِ مِنْ سِحْرِهِمْ اَنَّهَا تَسْعٰى","bc":"qāla bal alqụ, fa iżā ḥibāluhum wa 'iṣiyyuhum yukhayyalu ilaihi min siḥrihim annahā tas'ā","at":"Dia (Musa) berkata, “Silakan kamu melemparkan!” Maka tiba-tiba tali-tali dan tongkat-tongkat mereka terbayang olehnya (Musa) seakan-akan ia merayap cepat, karena sihir mereka."},{"id":"67","ar":"فَاَوْجَسَ فِيْ نَفْسِهٖ خِيْفَةً مُّوْسٰى ","bc":"fa aujasa fī nafsihī khīfatam mụsā","at":"Maka Musa merasa takut dalam hatinya."},{"id":"68","ar":"قُلْنَا لَا تَخَفْ اِنَّكَ اَنْتَ الْاَعْلٰى ","bc":"qulnā lā takhaf innaka antal-a'lā","at":"Kami berfirman, “Jangan takut! Sungguh, engkaulah yang unggul (menang)."},{"id":"69","ar":"وَاَلْقِ مَا فِيْ يَمِيْنِكَ تَلْقَفْ مَا صَنَعُوْاۗ اِنَّمَا صَنَعُوْا كَيْدُ سٰحِرٍۗ وَلَا يُفْلِحُ السّٰحِرُ حَيْثُ اَتٰى ","bc":"wa alqi mā fī yamīnika talqaf mā ṣana'ụ, innamā ṣana'ụ kaidu sāḥir, wa lā yufliḥus-sāḥiru ḥaiṡu atā","at":"Dan lemparkan apa yang ada di tangan kananmu, niscaya ia akan menelan apa yang mereka buat. Apa yang mereka buat itu hanyalah tipu daya pesihir (belaka). Dan tidak akan menang pesihir itu, dari mana pun ia datang.”"},{"id":"70","ar":"فَاُلْقِيَ السَّحَرَةُ سُجَّدًا قَالُوْٓا اٰمَنَّا بِرَبِّ هٰرُوْنَ وَمُوْسٰى ","bc":"fa ulqiyas-saḥaratu sujjadang qālū āmannā birabbi hārụna wa mụsā","at":"Lalu para pesihir itu merunduk bersujud, seraya berkata, “Kami telah percaya kepada Tuhannya Harun dan Musa.”"},{"id":"71","ar":"قَالَ اٰمَنْتُمْ لَهٗ قَبْلَ اَنْ اٰذَنَ لَكُمْۗ اِنَّهٗ لَكَبِيْرُكُمُ الَّذِيْ عَلَّمَكُمُ السِّحْرَۚ فَلَاُقَطِّعَنَّ اَيْدِيَكُمْ وَاَرْجُلَكُمْ مِّنْ خِلَافٍ وَّلَاُصَلِّبَنَّكُمْ فِيْ جُذُوْعِ النَّخْلِۖ وَلَتَعْلَمُنَّ اَيُّنَآ اَشَدُّ عَذَابًا وَّاَبْقٰى ","bc":"qāla āmantum lahụ qabla an āżana lakum, innahụ lakabīrukumullażī 'allamakumus-siḥr, fa la`uqaṭṭi'anna aidiyakum wa arjulakum min khilāfiw wa la`uṣallibannakum fī jużụ'in-nakhli wa lata'lamunna ayyunā asyaddu 'ażābaw wa abqā","at":"Dia (Fir‘aun) berkata, “Apakah kamu telah beriman kepadanya (Musa) sebelum aku memberi izin kepadamu? Sesungguhnya dia itu pemimpinmu yang mengajarkan sihir kepadamu. Maka sungguh, akan kupotong tangan dan kakimu secara bersilang, dan sungguh, akan aku salib kamu pada pangkal pohon kurma dan sungguh, kamu pasti akan mengetahui siapa di antara kita yang lebih pedih dan lebih kekal siksaannya.”"},{"id":"72","ar":"قَالُوْا لَنْ نُّؤْثِرَكَ عَلٰى مَا جَاۤءَنَا مِنَ الْبَيِّنٰتِ وَالَّذِيْ فَطَرَنَا فَاقْضِ مَآ اَنْتَ قَاضٍۗ اِنَّمَا تَقْضِيْ هٰذِهِ الْحَيٰوةَ الدُّنْيَا ۗ","bc":"qālụ lan nu`ṡiraka 'alā mā jā`anā minal-bayyināti wallażī faṭaranā faqḍi mā anta qāḍ, innamā taqḍī hāżihil-ḥayātad-dun-yā","at":"Mereka (para pesihir) berkata, “Kami tidak akan memilih (tunduk) kepadamu atas bukti-bukti nyata (mukjizat), yang telah datang kepada kami dan atas (Allah) yang telah menciptakan kami. Maka putuskanlah yang hendak engkau putuskan. Sesungguhnya engkau hanya dapat memutuskan pada kehidupan di dunia ini."},{"id":"73","ar":"اِنَّآ اٰمَنَّا بِرَبِّنَا لِيَغْفِرَ لَنَا خَطٰيٰنَا وَمَآ اَكْرَهْتَنَا عَلَيْهِ مِنَ السِّحْرِۗ وَاللّٰهُ خَيْرٌ وَّاَبْقٰى ","bc":"innā āmannā birabbinā liyagfira lanā khaṭāyānā wa mā akrahtanā 'alaihi minas-siḥr, wallāhu khairuw wa abqā","at":"Kami benar-benar telah beriman kepada Tuhan kami, agar Dia mengampuni kesalahan-kesalahan kami dan sihir yang telah engkau paksakan kepada kami. Dan Allah lebih baik (pahala-Nya) dan lebih kekal (azab-Nya).”"},{"id":"74","ar":"اِنَّهٗ مَنْ يَّأْتِ رَبَّهٗ مُجْرِمًا فَاِنَّ لَهٗ جَهَنَّمَ ۗ لَا يَمُوْتُ فِيْهَا وَلَا يَحْيٰى ","bc":"innahụ may ya`ti rabbahụ mujriman fa inna lahụ jahannam, lā yamụtu fīhā wa lā yaḥyā","at":"Sesungguhnya barang siapa datang kepada Tuhannya dalam keadaan berdosa, maka sungguh, baginya adalah neraka Jahanam. Dia tidak mati (terus merasakan azab) di dalamnya dan tidak (pula) hidup (tidak dapat bertobat)."},{"id":"75","ar":"وَمَنْ يَّأْتِهٖ مُؤْمِنًا قَدْ عَمِلَ الصّٰلِحٰتِ فَاُولٰۤىِٕكَ لَهُمُ الدَّرَجٰتُ الْعُلٰى ۙ","bc":"wa may ya`tihī mu`minang qad 'amilaṣ-ṣāliḥāti fa ulā`ika lahumud-darajātul-'ulā","at":"Tetapi barang siapa datang kepada-Nya dalam keadaan beriman, dan telah mengerjakan kebajikan, maka mereka itulah orang yang memperoleh derajat yang tinggi (mulia),"},{"id":"76","ar":"جَنّٰتُ عَدْنٍ تَجْرِيْ مِنْ تَحْتِهَا الْاَنْهٰرُ خٰلِدِيْنَ فِيْهَا ۗوَذٰلِكَ جَزٰۤؤُا مَنْ تَزَكّٰى","bc":"jannātu 'adnin tajrī min taḥtihal-an-hāru khālidīna fīhā, wa żālika jazā`u man tazakkā","at":"(yaitu) surga-surga ‘Adn, yang mengalir di bawahnya sungai-sungai, mereka kekal di dalamnya. Itulah balasan bagi orang yang menyucikan diri."},{"id":"77","ar":"وَلَقَدْ اَوْحَيْنَآ اِلٰى مُوْسٰٓى اَنْ اَسْرِ بِعِبَادِيْ فَاضْرِبْ لَهُمْ طَرِيْقًا فِى الْبَحْرِ يَبَسًاۙ لَّا تَخٰفُ دَرَكًا وَّلَا تَخْشٰى ","bc":"wa laqad auḥainā ilā mụsā an asri bi'ibādī faḍrib lahum ṭarīqan fil-baḥri yabasal lā takhāfu darakaw wa lā takhsyā","at":"Dan sungguh, telah Kami wahyukan kepada Musa, “Pergilah bersama hamba-hamba-Ku (Bani Israil) pada malam hari, dan pukullah (buatlah) untuk mereka jalan yang kering di laut itu, (engkau) tidak perlu takut akan tersusul dan tidak perlu khawatir (akan tenggelam).”"},{"id":"78","ar":"فَاَتْبَعَهُمْ فِرْعَوْنُ بِجُنُوْدِهٖ فَغَشِيَهُمْ مِّنَ الْيَمِّ مَا غَشِيَهُمْ ۗ","bc":"fa atba'ahum fir'aunu bijunụdihī fa gasyiyahum minal-yammi mā gasyiyahum","at":"Kemudian Fir‘aun dengan bala tentaranya mengejar mereka, tetapi mereka digulung ombak laut yang menenggelamkan mereka."},{"id":"79","ar":"وَاَضَلَّ فِرْعَوْنُ قَوْمَهٗ وَمَا هَدٰى ","bc":"wa aḍalla fir'aunu qaumahụ wa mā hadā","at":"Dan Fir‘aun telah menyesatkan kaumnya dan tidak memberi petunjuk."},{"id":"80","ar":"يٰبَنِيْٓ اِسْرَاۤءِيْلَ قَدْ اَنْجَيْنٰكُمْ مِّنْ عَدُوِّكُمْ وَوٰعَدْنٰكُمْ جَانِبَ الطُّوْرِ الْاَيْمَنَ وَنَزَّلْنَا عَلَيْكُمُ الْمَنَّ وَالسَّلْوٰى ","bc":"yā banī isrā`īla qad anjainākum min 'aduwwikum wa wā'adnākum jānibaṭ-ṭụril-aimana wa nazzalnā 'alaikumul-manna was-salwā","at":"Wahai Bani Israil! Sungguh, Kami telah menyelamatkan kamu dari musuhmu, dan Kami telah mengadakan perjanjian dengan kamu (untuk bermunajat) di sebelah kanan gunung itu (gunung Sinai) dan Kami telah menurunkan kepada kamu mann dan salwa."},{"id":"81","ar":"كُلُوْا مِنْ طَيِّبٰتِ مَا رَزَقْنٰكُمْۙ وَلَا تَطْغَوْا فِيْهِ فَيَحِلَّ عَلَيْكُمْ غَضَبِيْۚ وَمَنْ يَّحْلِلْ عَلَيْهِ غَضَبِيْ فَقَدْ هَوٰى ","bc":"kulụ min ṭayyibāti mā razaqnākum wa lā taṭgau fīhi fa yaḥilla 'alaikum gaḍabī, wa may yaḥlil 'alaihi gaḍabī fa qad hawā","at":"Makanlah dari rezeki yang baik-baik yang telah Kami berikan kepadamu, dan janganlah melampaui batas, yang menyebabkan kemurkaan-Ku menimpamu. Barangsiapa ditimpa kemurkaan-Ku, maka sungguh, binasalah dia."},{"id":"82","ar":"وَاِنِّي لَغَفَّارٌ لِّمَنْ تَابَ وَاٰمَنَ وَعَمِلَ صَالِحًا ثُمَّ اهْتَدٰى ","bc":"wa innī lagaffārul liman tāba wa āmana wa 'amila ṣāliḥan ṡummahtadā","at":"Dan sungguh, Aku Maha Pengampun bagi yang bertobat, beriman dan berbuat kebajikan, kemudian tetap dalam petunjuk."},{"id":"83","ar":"۞ وَمَآ اَعْجَلَكَ عَنْ قَوْمِكَ يٰمُوْسٰى ","bc":"wa mā a'jalaka 'ang qaumika yā mụsā","at":"“Dan mengapa engkau datang lebih cepat daripada kaummu, wahai Musa?”"},{"id":"84","ar":"قَالَ هُمْ اُولَاۤءِ عَلٰٓى اَثَرِيْ وَعَجِلْتُ اِلَيْكَ رَبِّ لِتَرْضٰى ","bc":"qāla hum ulā`i 'alā aṡarī wa 'ajiltu ilaika rabbi litarḍā","at":"Dia (Musa) berkata, “Itu mereka sedang menyusul aku dan aku bersegera kepada-Mu, Ya Tuhanku, agar Engkau rida (kepadaku).”"},{"id":"85","ar":"قَالَ فَاِنَّا قَدْ فَتَنَّا قَوْمَكَ مِنْۢ بَعْدِكَ وَاَضَلَّهُمُ السَّامِرِيُّ ","bc":"qāla fa innā qad fatannā qaumaka mim ba'dika wa aḍallahumus-sāmiriyy","at":"Dia (Allah) berfirman, “Sungguh, Kami telah menguji kaummu setelah engkau tinggalkan, dan mereka telah disesatkan oleh Samiri.” "},{"id":"86","ar":"فَرَجَعَ مُوْسٰٓى اِلٰى قَوْمِهٖ غَضْبَانَ اَسِفًا ەۚ قَالَ يٰقَوْمِ اَلَمْ يَعِدْكُمْ رَبُّكُمْ وَعْدًا حَسَنًا ەۗ اَفَطَالَ عَلَيْكُمُ الْعَهْدُ اَمْ اَرَدْتُّمْ اَنْ يَّحِلَّ عَلَيْكُمْ غَضَبٌ مِّنْ رَّبِّكُمْ فَاَخْلَفْتُمْ مَّوْعِدِيْ","bc":"fa raja'a mụsā ilā qaumihī gaḍbāna asifā, qāla yā qaumi a lam ya'idkum rabbukum wa'dan ḥasanā, a fa ṭāla 'alaikumul-'ahdu am arattum ay yaḥilla 'alaikum gaḍabum mir rabbikum fa akhlaftum mau'idī","at":"Kemudian Musa kembali kepada kaumnya dengan marah dan bersedih hati. Dia (Musa) berkata, “Wahai kaumku! Bukankah Tuhanmu telah menjanjikan kepadamu suatu janji yang baik? Apakah terlalu lama masa perjanjian itu bagimu atau kamu menghendaki agar kemurkaan Tuhan menimpamu, mengapa kamu melanggar perjanjianmu dengan aku?”"},{"id":"87","ar":"قَالُوْا مَآ اَخْلَفْنَا مَوْعِدَكَ بِمَلْكِنَا وَلٰكِنَّا حُمِّلْنَآ اَوْزَارًا مِّنْ زِيْنَةِ الْقَوْمِ فَقَذَفْنٰهَا فَكَذٰلِكَ اَلْقَى السَّامِرِيُّ ۙ","bc":"qālụ mā akhlafnā mau'idaka bimalkinā wa lākinnā ḥummilnā auzāram min zīnatil-qaumi fa qażafnāhā fa każālika alqas-sāmiriyy","at":"Mereka berkata, “Kami tidak melanggar perjanjianmu dengan kemauan kami sendiri, tetapi kami harus membawa beban berat dari perhiasan kaum (Fir‘aun) itu, kemudian kami melemparkannya (ke dalam api), dan demikian pula Samiri melemparkannya,"},{"id":"88","ar":"فَاَخْرَجَ لَهُمْ عِجْلًا جَسَدًا لَّهٗ خُوَارٌ فَقَالُوْا هٰذَآ اِلٰهُكُمْ وَاِلٰهُ مُوْسٰى ەۙ فَنَسِيَ ۗ","bc":"fa akhraja lahum 'ijlan jasadal lahụ khuwārun fa qālụ hāżā ilāhukum wa ilāhu mụsā fa nasiy","at":"kemudian (dari lubang api itu) dia (Samiri) mengeluarkan (patung) anak sapi yang bertubuh dan bersuara untuk mereka, maka mereka berkata, “Inilah Tuhanmu dan Tuhannya Musa, tetapi dia (Musa) telah lupa.”"},{"id":"89","ar":"اَفَلَا يَرَوْنَ اَلَّا يَرْجِعُ اِلَيْهِمْ قَوْلًا ەۙ وَّلَا يَمْلِكُ لَهُمْ ضَرًّا وَّلَا نَفْعًا","bc":"a fa lā yarauna allā yarji'u ilaihim qaulaw wa lā yamliku lahum ḍarraw wa lā naf'ā","at":"Maka tidakkah mereka memperhatikan bahwa (patung anak sapi itu) tidak dapat memberi jawaban kepada mereka, dan tidak kuasa menolak mudarat mau-pun mendatangkan manfaat kepada mereka?"},{"id":"90","ar":"وَلَقَدْ قَالَ لَهُمْ هٰرُوْنُ مِنْ قَبْلُ يٰقَوْمِ اِنَّمَا فُتِنْتُمْ بِهٖۚ وَاِنَّ رَبَّكُمُ الرَّحْمٰنُ فَاتَّبِعُوْنِيْ وَاَطِيْعُوْٓا اَمْرِيْ ","bc":"wa laqad qāla lahum hārụnu ming qablu yā qaumi innamā futintum bih, wa inna rabbakumur-raḥmānu fattabi'ụnī wa aṭī'ū amrī","at":"Dan sungguh, sebelumnya Harun telah berkata kepada mereka, “Wahai kaumku! Sesungguhnya kamu hanya sekedar diberi cobaan (dengan patung anak sapi) itu dan sungguh, Tuhanmu ialah (Allah) Yang Maha Pengasih, maka ikutilah aku dan taatilah perintahku.”"},{"id":"91","ar":"قَالُوْا لَنْ نَّبْرَحَ عَلَيْهِ عٰكِفِيْنَ حَتّٰى يَرْجِعَ اِلَيْنَا مُوْسٰى ","bc":"qālụ lan nabraḥa 'alaihi 'ākifīna ḥattā yarji'a ilainā mụsā","at":"Mereka menjawab, “Kami tidak akan meninggalkannya (dan) tetap menyembahnya (patung anak sapi) sampai Musa kembali kepada kami.”"},{"id":"92","ar":"قَالَ يٰهٰرُوْنُ مَا مَنَعَكَ اِذْ رَاَيْتَهُمْ ضَلُّوْٓا ۙ","bc":"qāla yā hārụnu mā mana'aka iż ra`aitahum ḍallū","at":"Dia (Musa) berkata, “Wahai Harun! Apa yang menghalangimu ketika engkau melihat mereka telah sesat,"},{"id":"93","ar":"اَلَّا تَتَّبِعَنِۗ اَفَعَصَيْتَ اَمْرِيْ ","bc":"allā tattabi'an, a fa 'aṣaita amrī","at":"(sehingga) engkau tidak mengikuti aku? Apakah engkau telah (sengaja) melanggar perintahku?”"},{"id":"94","ar":"قَالَ يَبْنَؤُمَّ لَا تَأْخُذْ بِلِحْيَتِيْ وَلَا بِرَأْسِيْۚ اِنِّيْ خَشِيْتُ اَنْ تَقُوْلَ فَرَّقْتَ بَيْنَ بَنِيْٓ اِسْرَاۤءِيْلَ وَلَمْ تَرْقُبْ قَوْلِيْ ","bc":"qāla yabna`umma lā ta`khuż biliḥyatī wa lā bira`sī, innī khasyītu an taqụla farraqta baina banī isrā`īla wa lam tarqub qaulī","at":"Dia (Harun) menjawab, “Wahai putra ibuku! Janganlah engkau pegang janggutku dan jangan (pula) kepalaku. Aku sungguh khawatir engkau akan berkata (kepadaku), ‘Engkau telah memecah belah antara Bani Israil dan engkau tidak memelihara amanatku.’” "},{"id":"95","ar":"قَالَ فَمَا خَطْبُكَ يٰسَامِرِيُّ ","bc":"qāla fa mā khaṭbuka yā sāmiriyy","at":"Dia (Musa) berkata, “Apakah yang mendorongmu (berbuat demikian) wahai Samiri?”"},{"id":"96","ar":"قَالَ بَصُرْتُ بِمَا لَمْ يَبْصُرُوْا بِهٖ فَقَبَضْتُ قَبْضَةً مِّنْ اَثَرِ الرَّسُوْلِ فَنَبَذْتُهَا وَكَذٰلِكَ سَوَّلَتْ لِيْ نَفْسِيْ ","bc":"qāla baṣurtu bimā lam yabṣurụ bihī fa qabaḍtu qabḍatam min aṡarir-rasụli fa nabażtuhā wa każālika sawwalat lī nafsī","at":"Dia (Samiri) menjawab, “Aku mengetahui sesuatu yang tidak mereka ketahui, jadi aku ambil segenggam (tanah dari) jejak rasul lalu aku melemparkannya (ke dalam api itu), demikianlah nafsuku membujukku.”"},{"id":"97","ar":"قَالَ فَاذْهَبْ فَاِنَّ لَكَ فِى الْحَيٰوةِ اَنْ تَقُوْلَ لَا مِسَاسَۖ وَاِنَّ لَكَ مَوْعِدًا لَّنْ تُخْلَفَهٗۚ وَانْظُرْ اِلٰٓى اِلٰهِكَ الَّذِيْ ظَلْتَ عَلَيْهِ عَاكِفًا ۗ لَنُحَرِّقَنَّهٗ ثُمَّ لَنَنْسِفَنَّهٗ فِى الْيَمِّ نَسْفًا ","bc":"qāla faż-hab fa inna laka fil-ḥayāti an taqụla lā misāsa wa inna laka mau'idal lan tukhlafah, wanẓur ilā ilāhikallażī ẓalta 'alaihi 'ākifā, lanuḥarriqannahụ ṡumma lanansifannahụ fil-yammi nasfā","at":"Dia (Musa) berkata, “Pergilah kau! Maka sesungguhnya di dalam kehidupan (di dunia) engkau (hanya dapat) mengatakan, ‘Janganlah menyentuh (aku),’. Dan engkau pasti mendapat (hukuman) yang telah dijanjikan (di akhirat) yang tidak akan dapat engkau hindari, dan lihatlah tuhanmu itu yang engkau tetap menyembahnya. Kami pasti akan membakarnya, kemudian sungguh kami akan menghamburkannya (abunya) ke dalam laut (berserakan)."},{"id":"98","ar":"اِنَّمَآ اِلٰهُكُمُ اللّٰهُ الَّذِيْ لَآ اِلٰهَ اِلَّا هُوَۗ وَسِعَ كُلَّ شَيْءٍ عِلْمًا ","bc":"innamā ilāhukumullāhullażī lā ilāha illā huw, wasi'a kulla syai`in 'ilmā","at":" Sungguh, Tuhanmu hanyalah Allah, tidak ada tuhan selain Dia. Pengetahuan-Nya meliputi segala sesuatu.”"},{"id":"99","ar":"كَذٰلِكَ نَقُصُّ عَلَيْكَ مِنْ اَنْۢبَاۤءِ مَا قَدْ سَبَقَۚ وَقَدْ اٰتَيْنٰكَ مِنْ لَّدُنَّا ذِكْرًا ۚ","bc":"każālika naquṣṣu 'alaika min ambā`i mā qad sabaq, wa qad ātaināka mil ladunnā żikrā","at":"Demikianlah Kami kisahkan kepadamu (Muhammad) sebagian kisah (umat) yang telah lalu, dan sungguh, telah Kami berikan kepadamu suatu peringatan (Al-Qur'an) dari sisi Kami."},{"id":"100","ar":"مَنْ اَعْرَضَ عَنْهُ فَاِنَّهٗ يَحْمِلُ يَوْمَ الْقِيٰمَةِ وِزْرًا ","bc":"man a'raḍa 'an-hu fa innahụ yaḥmilu yaumal-qiyāmati wizrā","at":"Barangsiapa berpaling darinya (Al-Qur'an), maka sesungguhnya dia akan memikul beban yang berat (dosa) pada hari Kiamat, "},{"id":"101","ar":"خٰلِدِيْنَ فِيْهِ ۗوَسَاۤءَ لَهُمْ يَوْمَ الْقِيٰمَةِ حِمْلًاۙ ","bc":"khālidīna fīh, wa sā`a lahum yaumal-qiyāmati ḥimlā","at":"mereka kekal di dalam keadaan itu. Dan sungguh buruk beban dosa itu bagi mereka pada hari Kiamat, "},{"id":"102","ar":"يَّوْمَ يُنْفَخُ فِى الصُّوْرِ وَنَحْشُرُ الْمُجْرِمِيْنَ يَوْمَىِٕذٍ زُرْقًا ۖ","bc":"yauma yunfakhu fiṣ-ṣụri wa naḥsyurul-mujrimīna yauma`iżin zurqā","at":"pada hari (Kiamat) sangkakala ditiup (yang kedua kali) dan pada hari itu Kami kumpulkan orang-orang yang berdosa dengan (wajah) biru muram,"},{"id":"103","ar":"يَّتَخَافَتُوْنَ بَيْنَهُمْ اِنْ لَّبِثْتُمْ اِلَّا عَشْرًا ","bc":"yatakhāfatụna bainahum il labiṡtum illā 'asyrā","at":"mereka saling berbisik satu sama lain, “Kamu tinggal (di dunia) tidak lebih dari sepuluh (hari).”"},{"id":"104","ar":"نَحْنُ اَعْلَمُ بِمَا يَقُوْلُوْنَ اِذْ يَقُوْلُ اَمْثَلُهُمْ طَرِيْقَةً اِنْ لَّبِثْتُمْ اِلَّا يَوْمًا","bc":"naḥnu a'lamu bimā yaqụlụna iż yaqụlu amṡaluhum ṭarīqatan il labiṡtum illā yaumā","at":"Kami lebih mengetahui apa yang akan mereka katakan, ketika orang yang paling lurus jalannya mengatakan, “Kamu tinggal (di dunia), tidak lebih dari sehari saja.”"},{"id":"105","ar":"وَيَسْـَٔلُوْنَكَ عَنِ الْجِبَالِ فَقُلْ يَنْسِفُهَا رَبِّيْ نَسْفًا ۙ","bc":"wa yas`alụnaka 'anil-jibāli fa qul yansifuhā rabbī nasfā","at":"Dan mereka bertanya kepadamu (Muhammad) tentang gunung-gunung, maka katakanlah, “Tuhanku akan menghancurkannya (pada hari Kiamat) sehancur-hancurnya,"},{"id":"106","ar":"فَيَذَرُهَا قَاعًا صَفْصَفًا ۙ","bc":"fa yażaruhā qā'an ṣafṣafā","at":"kemudian Dia akan menjadikan (bekas gunung-gunung) itu rata sama sekali, "},{"id":"107","ar":"لَّا تَرٰى فِيْهَا عِوَجًا وَّلَآ اَمْتًا ۗ","bc":"lā tarā fīhā 'iwajaw wa lā amtā","at":"(sehingga) kamu tidak akan melihat lagi ada tempat yang rendah dan yang tinggi di sana.” "},{"id":"108","ar":"يَوْمَىِٕذٍ يَّتَّبِعُوْنَ الدَّاعِيَ لَا عِوَجَ لَهٗ ۚوَخَشَعَتِ الْاَصْوَاتُ لِلرَّحْمٰنِ فَلَا تَسْمَعُ اِلَّا هَمْسًا ","bc":"yauma`iżiy yattabi'ụnad-dā'iya lā 'iwaja lah, wa khasya'atil-aṣwātu lir-raḥmāni fa lā tasma'u illā hamsā","at":"Pada hari itu mereka mengikuti (panggilan) penyeru (malaikat) tanpa berbelok-belok (membantah); dan semua suara tunduk merendah kepada Tuhan Yang Maha Pengasih, sehingga yang kamu dengar hanyalah bisik-bisik."},{"id":"109","ar":"يَوْمَىِٕذٍ لَّا تَنْفَعُ الشَّفَاعَةُ اِلَّا مَنْ اَذِنَ لَهُ الرَّحْمٰنُ وَرَضِيَ لَهٗ قَوْلًا ","bc":"yauma`iżil lā tanfa'usy-syafā'atu illā man ażina lahur-raḥmānu wa raḍiya lahụ qaulā","at":"Pada hari itu tidak berguna syafaat (pertolongan), kecuali dari orang yang telah diberi izin oleh Tuhan Yang Maha Pengasih, dan Dia ridai perkataannya."},{"id":"110","ar":"يَعْلَمُ مَا بَيْنَ اَيْدِيْهِمْ وَمَا خَلْفَهُمْ وَلَا يُحِيْطُوْنَ بِهٖ عِلْمًا ","bc":"ya'lamu mā baina aidīhim wa mā khalfahum wa lā yuḥīṭụna bihī 'ilmā","at":" Dia (Allah) mengetahui apa yang di hadapan mereka (yang akan terjadi) dan apa yang di belakang mereka (yang telah terjadi), sedang ilmu mereka tidak dapat meliputi ilmu-Nya."},{"id":"111","ar":"۞ وَعَنَتِ الْوُجُوْهُ لِلْحَيِّ الْقَيُّوْمِۗ وَقَدْ خَابَ مَنْ حَمَلَ ظُلْمًا ","bc":"wa 'anatil-wujụhu lil-ḥayyil-qayyụm, wa qad khāba man ḥamala ẓulmā","at":"Dan semua wajah tertunduk di hadapan (Allah) Yang Hidup dan Yang Berdiri Sendiri. Sungguh rugi orang yang melakukan kezaliman."},{"id":"112","ar":"وَمَنْ يَّعْمَلْ مِنَ الصّٰلِحٰتِ وَهُوَ مُؤْمِنٌ فَلَا يَخٰفُ ظُلْمًا وَّلَا هَضْمًا ","bc":"wa may ya'mal minaṣ-ṣāliḥāti wa huwa mu`minun fa lā yakhāfu ẓulmaw wa lā haḍmā","at":"Dan barang siapa mengerjakan kebajikan sedang dia (dalam keadaan) beriman, maka dia tidak khawatir akan perlakuan zalim (terhadapnya) dan tidak (pula khawatir) akan pengurangan haknya."},{"id":"113","ar":"وَكَذٰلِكَ اَنْزَلْنٰهُ قُرْاٰنًا عَرَبِيًّا وَّصَرَّفْنَا فِيْهِ مِنَ الْوَعِيْدِ لَعَلَّهُمْ يَتَّقُوْنَ اَوْ يُحْدِثُ لَهُمْ ذِكْرًا ","bc":"wa każālika anzalnāhu qur`ānan 'arabiyyaw wa ṣarrafnā fīhi minal-wa'īdi la'allahum yattaqụna au yuḥdiṡu lahum żikrā","at":"Dan demikianlah Kami menurunkan Al-Qur'an dalam bahasa Arab, dan Kami telah menjelaskan berulang-ulang di dalamnya sebagian dari ancaman, agar mereka bertakwa, atau agar (Al-Qur'an) itu memberi pengajaran bagi mereka."},{"id":"114","ar":"فَتَعٰلَى اللّٰهُ الْمَلِكُ الْحَقُّۚ وَلَا تَعْجَلْ بِالْقُرْاٰنِ مِنْ قَبْلِ اَنْ يُّقْضٰٓى اِلَيْكَ وَحْيُهٗ ۖوَقُلْ رَّبِّ زِدْنِيْ عِلْمًا ","bc":"fa ta'ālallāhul-malikul-ḥaqq, wa lā ta'jal bil-qur`āni ming qabli ay yuqḍā ilaika waḥyuhụ wa qur rabbi zidnī 'ilmā","at":"Maka Mahatinggi Allah, Raja yang sebenar-benarnya. Dan janganlah engkau (Muhammad) tergesa-gesa (membaca) Al-Qur'an sebelum selesai diwahyukan kepadamu, dan katakanlah, “Ya Tuhanku, tambahkanlah ilmu kepadaku. ”"},{"id":"115","ar":"وَلَقَدْ عَهِدْنَآ اِلٰٓى اٰدَمَ مِنْ قَبْلُ فَنَسِيَ وَلَمْ نَجِدْ لَهٗ عَزْمًا","bc":"wa laqad 'ahidnā ilā ādama ming qablu fa nasiya wa lam najid lahụ 'azmā","at":"Dan sungguh telah Kami pesankan kepada Adam dahulu, tetapi dia lupa, dan Kami tidak dapati kemauan yang kuat padanya."},{"id":"116","ar":"وَاِذْ قُلْنَا لِلْمَلٰۤىِٕكَةِ اسْجُدُوْا لِاٰدَمَ فَسَجَدُوْٓا اِلَّآ اِبْلِيْسَ اَبٰى ۗ","bc":"wa iż qulnā lil-malā`ikatisjudụ li`ādama fa sajadū illā iblīsa abā","at":"Dan (ingatlah) ketika Kami berfirman kepada para malaikat, “Sujudlah kamu kepada Adam!” Lalu mereka pun sujud kecuali Iblis; dia menolak."},{"id":"117","ar":"فَقُلْنَا يٰٓاٰدَمُ اِنَّ هٰذَا عَدُوٌّ لَّكَ وَلِزَوْجِكَ فَلَا يُخْرِجَنَّكُمَا مِنَ الْجَنَّةِ فَتَشْقٰى ","bc":"fa qulnā yā ādamu inna hāżā 'aduwwul laka wa lizaujika fa lā yukhrijannakumā minal-jannati fa tasyqā","at":"Kemudian Kami berfirman, “Wahai Adam! Sungguh ini (Iblis) musuh bagimu dan bagi istrimu, maka sekali-kali jangan sampai dia mengeluarkan kamu berdua dari surga, nanti kamu celaka."},{"id":"118","ar":"اِنَّ لَكَ اَلَّا تَجُوْعَ فِيْهَا وَلَا تَعْرٰى ۙ","bc":"inna laka allā tajụ'a fīhā wa lā ta'rā","at":"Sungguh, ada (jaminan) untukmu di sana, engkau tidak akan kelaparan dan tidak akan telanjang."},{"id":"119","ar":"وَاَنَّكَ لَا تَظْمَؤُا فِيْهَا وَلَا تَضْحٰى ","bc":"wa annaka lā taẓma`u fīhā wa lā taḍ-ḥā","at":"Dan sungguh, di sana engkau tidak akan merasa dahaga dan tidak akan ditimpa panas matahari.” "},{"id":"120","ar":"فَوَسْوَسَ اِلَيْهِ الشَّيْطٰنُ قَالَ يٰٓاٰدَمُ هَلْ اَدُلُّكَ عَلٰى شَجَرَةِ الْخُلْدِ وَمُلْكٍ لَّا يَبْلٰى ","bc":"fa waswasa ilaihisy-syaiṭānu qāla yā ādamu hal adulluka 'alā syajaratil-khuldi wa mulkil lā yablā","at":" Kemudian setan membisikkan (pikiran jahat) kepadanya, dengan berkata, “Wahai Adam! Maukah aku tunjukkan kepadamu pohon keabadian (khuldi) dan kerajaan yang tidak akan binasa?”"},{"id":"121","ar":"فَاَكَلَا مِنْهَا فَبَدَتْ لَهُمَا سَوْاٰتُهُمَا وَطَفِقَا يَخْصِفٰنِ عَلَيْهِمَا مِنْ وَّرَقِ الْجَنَّةِۚ وَعَصٰٓى اٰدَمُ رَبَّهٗ فَغَوٰى ۖ","bc":"fa akalā min-hā fa badat lahumā sau`ātuhumā wa ṭafiqā yakhṣifāni 'alaihimā miw waraqil jannah, wa 'aṣā ādamu rabbahụ fa gawā","at":"Lalu keduanya memakannya, lalu tampaklah oleh keduanya aurat mereka dan mulailah keduanya menutupinya dengan daun-daun (yang ada di) surga, dan telah durhakalah Adam kepada Tuhannya, dan sesatlah dia."},{"id":"122","ar":"ثُمَّ اجْتَبٰىهُ رَبُّهٗ فَتَابَ عَلَيْهِ وَهَدٰى ","bc":"ṡummajtabāhu rabbuhụ fa tāba 'alaihi wa hadā","at":"Kemudian Tuhannya memilih dia, maka Dia menerima tobatnya dan memberinya petunjuk."},{"id":"123","ar":"قَالَ اهْبِطَا مِنْهَا جَمِيعًاۢ بَعْضُكُمْ لِبَعْضٍ عَدُوٌّ ۚفَاِمَّا يَأْتِيَنَّكُمْ مِّنِّيْ هُدًى ەۙ فَمَنِ اتَّبَعَ هُدٰيَ فَلَا يَضِلُّ وَلَا يَشْقٰى","bc":"qālahbiṭā min-hā jamī'am ba'ḍukum liba'ḍin 'aduww, fa immā ya`tiyannakum minnī hudan fa manittaba'a hudāya fa lā yaḍillu wa lā yasyqā","at":"Dia (Allah) berfirman, “Turunlah kamu berdua dari surga bersama-sama, sebagian kamu menjadi musuh bagi sebagian yang lain. Jika datang kepadamu petunjuk dari-Ku, maka (ketahuilah) barang siapa mengikuti petunjuk-Ku, dia tidak akan sesat dan tidak akan celaka."},{"id":"124","ar":"وَمَنْ اَعْرَضَ عَنْ ذِكْرِيْ فَاِنَّ لَهٗ مَعِيْشَةً ضَنْكًا وَّنَحْشُرُهٗ يَوْمَ الْقِيٰمَةِ اَعْمٰى ","bc":"wa man a'raḍa 'an żikrī fa inna lahụ ma'īsyatan ḍangkaw wa naḥsyuruhụ yaumal-qiyāmati a'mā","at":"Dan barang siapa berpaling dari peringatan-Ku, maka sungguh, dia akan menjalani kehidupan yang sempit, dan Kami akan mengumpulkannya pada hari Kiamat dalam keadaan buta.”"},{"id":"125","ar":"قَالَ رَبِّ لِمَ حَشَرْتَنِيْٓ اَعْمٰى وَقَدْ كُنْتُ بَصِيْرًا ","bc":"qāla rabbi lima ḥasyartanī a'mā wa qad kuntu baṣīrā","at":"Dia berkata, “Ya Tuhanku, mengapa Engkau kumpulkan aku dalam keadaan buta, padahal dahulu aku dapat melihat?”"},{"id":"126","ar":"قَالَ كَذٰلِكَ اَتَتْكَ اٰيٰتُنَا فَنَسِيْتَهَاۚ وَكَذٰلِكَ الْيَوْمَ تُنْسٰى ","bc":"qāla każālika atatka āyātunā fa nasītahā, wa każālikal-yauma tunsā","at":"Dia (Allah) berfirman, “Demikianlah, dahulu telah datang kepadamu ayat-ayat Kami, dan kamu mengabaikannya, jadi begitu (pula) pada hari ini kamu diabaikan.”"},{"id":"127","ar":"وَكَذٰلِكَ نَجْزِيْ مَنْ اَسْرَفَ وَلَمْ يُؤْمِنْۢ بِاٰيٰتِ رَبِّهٖۗ وَلَعَذَابُ الْاٰخِرَةِ اَشَدُّ وَاَبْقٰى ","bc":"wa każālika najzī man asrafa wa lam yu`mim bi`āyāti rabbih, wa la'ażābul-ākhirati asyaddu wa abqā","at":"Dan demikianlah Kami membalas orang yang melampaui batas dan tidak percaya kepada ayat-ayat Tuhannya. Sungguh, azab di akhirat itu lebih berat dan lebih kekal. "},{"id":"128","ar":"اَفَلَمْ يَهْدِ لَهُمْ كَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنَ الْقُرُوْنِ يَمْشُوْنَ فِيْ مَسٰكِنِهِمْۗ اِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّاُولِى النُّهٰى","bc":"a fa lam yahdi lahum kam ahlaknā qablahum minal-qurụni yamsyụna fī masākinihim, inna fī żālika la`āyātil li`ulin-nuhā","at":"Maka tidakkah menjadi petunjuk bagi mereka (orang-orang musyrik) berapa banyak (generasi) sebelum mereka yang telah Kami binasakan, padahal mereka melewati (bekas-bekas) tempat tinggal mereka (umat-umat itu)? Sungguh, pada yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang berakal."},{"id":"129","ar":"وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَكَانَ لِزَامًا وَّاَجَلٌ مُّسَمًّى ۗ","bc":"walau lā kalimatun sabaqat mir rabbika lakāna lizāmaw wa ajalum musammā","at":"Dan kalau tidak ada suatu ketetapan terdahulu dari Tuhanmu serta tidak ada batas yang telah ditentukan (ajal), pasti (siksaan itu) menimpa mereka."},{"id":"130","ar":"فَاصْبِرْ عَلٰى مَا يَقُوْلُوْنَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوْعِ الشَّمْسِ وَقَبْلَ غُرُوْبِهَا ۚوَمِنْ اٰنَاۤئِ الَّيْلِ فَسَبِّحْ وَاَطْرَافَ النَّهَارِ لَعَلَّكَ تَرْضٰى","bc":"faṣbir 'alā mā yaqụlụna wa sabbiḥ biḥamdi rabbika qabla ṭulụ'isy-syamsi wa qabla gurụbihā, wa min ānā`il-laili fa sabbiḥ wa aṭrāfan-nahāri la'allaka tarḍā","at":"Maka sabarlah engkau (Muhammad) atas apa yang mereka katakan, dan bertasbihlah dengan memuji Tuhanmu, sebelum matahari terbit, dan sebelum terbenam; dan bertasbihlah (pula) pada waktu tengah malam dan di ujung siang hari, agar engkau merasa tenang."},{"id":"131","ar":"وَلَا تَمُدَّنَّ عَيْنَيْكَ اِلٰى مَا مَتَّعْنَا بِهٖٓ اَزْوَاجًا مِّنْهُمْ زَهْرَةَ الْحَيٰوةِ الدُّنْيَا ەۙ لِنَفْتِنَهُمْ فِيْهِ ۗوَرِزْقُ رَبِّكَ خَيْرٌ وَّاَبْقٰى","bc":"wa lā tamuddanna 'ainaika ilā mā matta'nā bihī azwājam min-hum zahratal-ḥayātid-dun-yā linaftinahum fīh, wa rizqu rabbika khairuw wa abqā","at":"Dan janganlah engkau tujukan pandangan matamu kepada kenikmatan yang telah Kami berikan kepada beberapa golongan dari mereka, (sebagai) bunga kehidupan dunia agar Kami uji mereka dengan (kesenangan) itu. Karunia Tuhanmu lebih baik dan lebih kekal."},{"id":"132","ar":"وَأْمُرْ اَهْلَكَ بِالصَّلٰوةِ وَاصْطَبِرْ عَلَيْهَاۗ لَا نَسْـَٔلُكَ رِزْقًاۗ نَحْنُ نَرْزُقُكَۗ وَالْعَاقِبَةُ لِلتَّقْوٰى ","bc":"wa`mur ahlaka biṣ-ṣalāti waṣṭabir 'alaihā, lā nas`aluka rizqā, naḥnu narzuquk, wal-'āqibatu lit-taqwā","at":"Dan perintahkanlah keluargamu melaksanakan salat dan sabar dalam mengerjakannya. Kami tidak meminta rezeki kepadamu, Kamilah yang memberi rezeki kepadamu. Dan akibat (yang baik di akhirat) adalah bagi orang yang bertakwa."},{"id":"133","ar":"وَقَالُوْا لَوْلَا يَأْتِيْنَا بِاٰيَةٍ مِّنْ رَّبِّهٖۗ اَوَلَمْ تَأْتِهِمْ بَيِّنَةُ مَا فِى الصُّحُفِ الْاُولٰى ","bc":"wa qālụ lau lā ya`tīnā bi`āyatim mir rabbih, a wa lam ta`tihim bayyinatu mā fiṣ-ṣuḥufil-ụlā","at":"Dan mereka berkata, “Mengapa dia tidak membawa tanda (bukti) kepada kami dari Tuhannya?” Bukankah telah datang kepada mereka bukti (yang nyata) sebagaimana yang tersebut di dalam kitab-kitab yang dahulu?"},{"id":"134","ar":"وَلَوْ اَنَّآ اَهْلَكْنٰهُمْ بِعَذَابٍ مِّنْ قَبْلِهٖ لَقَالُوْا رَبَّنَا لَوْلَآ اَرْسَلْتَ اِلَيْنَا رَسُوْلًا فَنَتَّبِعَ اٰيٰتِكَ مِنْ قَبْلِ اَنْ نَّذِلَّ وَنَخْزٰى ","bc":"walau annā ahlaknāhum bi'ażābim ming qablihī laqālụ rabbanā lau lā arsalta ilainā rasụlan fa nattabi'a āyātika ming qabli an nażilla wa nakhzā","at":"Dan kalau mereka Kami binasakan dengan suatu siksaan sebelumnya (Al-Qur'an itu diturunkan), tentulah mereka berkata, “Ya Tuhan kami, mengapa tidak Engkau utus seorang rasul kepada kami, sehingga kami mengikuti ayat-ayat-Mu sebelum kami menjadi hina dan rendah?”"},{"id":"135","ar":"قُلْ كُلٌّ مُّتَرَبِّصٌ فَتَرَبَّصُوْاۚ فَسَتَعْلَمُوْنَ مَنْ اَصْحٰبُ الصِّرَاطِ السَّوِيِّ وَمَنِ اهْتَدٰى ۔","bc":"qul kullum mutarabbiṣun fa tarabbaṣụ, fa sata'lamụna man aṣ-ḥābuṣ-ṣirāṭis-sawiyyi wa manihtadā","at":"Katakanlah (Muhammad), “Masing-masing (kita) menanti, maka nantikanlah olehmu! Dan kelak kamu akan mengetahui, siapa yang menempuh jalan yang lurus, dan siapa yang telah mendapat petunjuk.”"}],yasin=[{"id":"1","ar":"يٰسۤ ۚ","bc":"yā sīn","at":"Ya Sin"},{"id":"2","ar":"وَالْقُرْاٰنِ الْحَكِيْمِۙ ","bc":"wal-qur`ānil-ḥakīm","at":"Demi Al-Qur'an yang penuh hikmah,"},{"id":"3","ar":"اِنَّكَ لَمِنَ الْمُرْسَلِيْنَۙ ","bc":"innaka laminal-mursalīn","at":"sungguh, engkau (Muhammad) adalah salah seorang dari rasul-rasul,"},{"id":"4","ar":"عَلٰى صِرَاطٍ مُّسْتَقِيْمٍۗ ","bc":"'alā ṣirāṭim mustaqīm","at":"(yang berada) di atas jalan yang lurus,"},{"id":"5","ar":"تَنْزِيْلَ الْعَزِيْزِ الرَّحِيْمِۙ ","bc":"tanzīlal-'azīzir-raḥīm","at":"(sebagai wahyu) yang diturunkan oleh (Allah) Yang Mahaperkasa, Maha Penyayang,"},{"id":"6","ar":"لِتُنْذِرَ قَوْمًا مَّآ اُنْذِرَ اٰبَاۤؤُهُمْ فَهُمْ غٰفِلُوْنَ ","bc":"litunżira qaumam mā unżira ābā`uhum fa hum gāfilụn","at":"agar engkau memberi peringatan kepada suatu kaum yang nenek moyangnya belum pernah diberi peringatan, karena itu mereka lalai."},{"id":"7","ar":"لَقَدْ حَقَّ الْقَوْلُ عَلٰٓى اَكْثَرِهِمْ فَهُمْ لَا يُؤْمِنُوْنَ ","bc":"laqad ḥaqqal-qaulu 'alā akṡarihim fa hum lā yu`minụn","at":"Sungguh, pasti berlaku perkataan (hukuman) terhadap kebanyakan mereka, karena mereka tidak beriman."},{"id":"8","ar":"اِنَّا جَعَلْنَا فِيْٓ اَعْنَاقِهِمْ اَغْلٰلًا فَهِيَ اِلَى الْاَذْقَانِ فَهُمْ مُّقْمَحُوْنَ ","bc":"innā ja'alnā fī a'nāqihim aglālan fa hiya ilal-ażqāni fa hum muqmaḥụn","at":"Sungguh, Kami telah memasang belenggu di leher mereka, lalu tangan mereka (diangkat) ke dagu, karena itu mereka tertengadah."},{"id":"9","ar":"وَجَعَلْنَا مِنْۢ بَيْنِ اَيْدِيْهِمْ سَدًّا وَّمِنْ خَلْفِهِمْ سَدًّا فَاَغْشَيْنٰهُمْ فَهُمْ لَا يُبْصِرُوْنَ ","bc":"wa ja'alnā mim baini aidīhim saddaw wa min khalfihim saddan fa agsyaināhum fa hum lā yubṣirụn","at":"Dan Kami jadikan di hadapan mereka sekat (dinding) dan di belakang mereka juga sekat, dan Kami tutup (mata) mereka sehingga mereka tidak dapat melihat."},{"id":"10","ar":"وَسَوَاۤءٌ عَلَيْهِمْ ءَاَنْذَرْتَهُمْ اَمْ لَمْ تُنْذِرْهُمْ لَا يُؤْمِنُوْنَ ","bc":"wa sawā`un 'alaihim a anżartahum am lam tunżir-hum lā yu`minụn","at":"Dan sama saja bagi mereka, apakah engkau memberi peringatan kepada mereka atau engkau tidak memberi peringatan kepada mereka, mereka tidak akan beriman juga."},{"id":"11","ar":"اِنَّمَا تُنْذِرُ مَنِ اتَّبَعَ الذِّكْرَ وَخَشِيَ الرَّحْمٰنَ بِالْغَيْبِۚ فَبَشِّرْهُ بِمَغْفِرَةٍ وَّاَجْرٍ كَرِيْمٍ ","bc":"innamā tunżiru manittaba'aż-żikra wa khasyiyar-raḥmāna bil-gaīb, fa basysyir-hu bimagfiratiw wa ajring karīm","at":"Sesungguhnya engkau hanya memberi peringatan kepada orang-orang yang mau mengikuti peringatan dan yang takut kepada Tuhan Yang Maha Pengasih, walaupun mereka tidak melihat-Nya. Maka berilah mereka kabar gembira dengan ampunan dan pahala yang mulia."},{"id":"12","ar":"اِنَّا نَحْنُ نُحْيِ الْمَوْتٰى وَنَكْتُبُ مَا قَدَّمُوْا وَاٰثَارَهُمْۗ وَكُلَّ شَيْءٍ اَحْصَيْنٰهُ فِيْٓ اِمَامٍ مُّبِيْنٍ","bc":"innā naḥnu nuḥyil-mautā wa naktubu mā qaddamụ wa āṡārahum, wa kulla syai`in aḥṣaināhu fī imāmim mubīn","at":"Sungguh, Kamilah yang menghidupkan orang-orang yang mati, dan Kamilah yang mencatat apa yang telah mereka kerjakan dan bekas-bekas yang mereka (tinggalkan). Dan segala sesuatu Kami kumpulkan dalam Kitab yang jelas (Lauh Mahfuzh)."},{"id":"13","ar":"وَاضْرِبْ لَهُمْ مَّثَلًا اَصْحٰبَ الْقَرْيَةِۘ اِذْ جَاۤءَهَا الْمُرْسَلُوْنَۚ ","bc":"waḍrib lahum maṡalan aṣ-ḥābal-qaryah, iż jā`ahal-mursalụn","at":"Dan buatlah suatu perumpamaan bagi mereka, yaitu penduduk suatu negeri, ketika utusan-utusan datang kepada mereka;"},{"id":"14","ar":"اِذْ اَرْسَلْنَآ اِلَيْهِمُ اثْنَيْنِ فَكَذَّبُوْهُمَا فَعَزَّزْنَا بِثَالِثٍ فَقَالُوْٓا اِنَّآ اِلَيْكُمْ مُّرْسَلُوْنَ ","bc":"iż arsalnā ilaihimuṡnaini fa każżabụhumā fa 'azzaznā biṡāliṡin fa qālū innā ilaikum mursalụn","at":"(yaitu) ketika Kami mengutus kepada mereka dua orang utusan, lalu mereka mendustakan keduanya; kemudian Kami kuatkan dengan (utusan) yang ketiga, maka ketiga (utusan itu) berkata, “Sungguh, kami adalah orang-orang yang diutus kepadamu.”"},{"id":"15","ar":"قَالُوْا مَآ اَنْتُمْ اِلَّا بَشَرٌ مِّثْلُنَاۙ وَمَآ اَنْزَلَ الرَّحْمٰنُ مِنْ شَيْءٍۙ اِنْ اَنْتُمْ اِلَّا تَكْذِبُوْنَ ","bc":"qālụ mā antum illā basyarum miṡlunā wa mā anzalar-raḥmānu min syai`in in antum illā takżibụn","at":"Mereka (penduduk negeri) menjawab, “Kamu ini hanyalah manusia seperti kami, dan (Allah) Yang Maha Pengasih tidak menurunkan sesuatu apa pun; kamu hanyalah pendusta belaka.”"},{"id":"16","ar":"قَالُوْا رَبُّنَا يَعْلَمُ اِنَّآ اِلَيْكُمْ لَمُرْسَلُوْنَ ","bc":"qālụ rabbunā ya'lamu innā ilaikum lamursalụn","at":"Mereka berkata, “Tuhan kami mengetahui sesungguhnya kami adalah utusan-utusan(-Nya) kepada kamu."},{"id":"17","ar":"وَمَا عَلَيْنَآ اِلَّا الْبَلٰغُ الْمُبِيْنُ ","bc":"wa mā 'alainā illal-balāgul-mubīn","at":"Dan kewajiban kami hanyalah menyampaikan (perintah Allah) dengan jelas.”"},{"id":"18","ar":"قَالُوْٓا اِنَّا تَطَيَّرْنَا بِكُمْۚ لَىِٕنْ لَّمْ تَنْتَهُوْا لَنَرْجُمَنَّكُمْ وَلَيَمَسَّنَّكُمْ مِّنَّا عَذَابٌ اَلِيْمٌ ","bc":"qālū innā taṭayyarnā bikum, la`il lam tantahụ lanarjumannakum wa layamassannakum minnā 'ażābun alīm","at":"Mereka menjawab, “Sesungguhnya kami bernasib malang karena kamu. Sungguh, jika kamu tidak berhenti (menyeru kami), niscaya kami rajam kamu dan kamu pasti akan merasakan siksaan yang pedih dari kami.”"},{"id":"19","ar":"قَالُوْا طَاۤىِٕرُكُمْ مَّعَكُمْۗ اَىِٕنْ ذُكِّرْتُمْۗ بَلْ اَنْتُمْ قَوْمٌ مُّسْرِفُوْنَ ","bc":"qālụ ṭā`irukum ma'akum, a in żukkirtum, bal antum qaumum musrifụn","at":"Mereka (utusan-utusan) itu berkata, “Kemalangan kamu itu adalah karena kamu sendiri. Apakah karena kamu diberi peringatan? Sebenarnya kamu adalah kaum yang melampaui batas.”"},{"id":"20","ar":"وَجَاۤءَ مِنْ اَقْصَا الْمَدِيْنَةِ رَجُلٌ يَّسْعٰى قَالَ يٰقَوْمِ اتَّبِعُوا الْمُرْسَلِيْنَۙ ","bc":"wa jā`a min aqṣal-madīnati rajuluy yas'ā qāla yā qaumittabi'ul-mursalīn","at":"Dan datanglah dari ujung kota, seorang laki-laki dengan bergegas dia berkata, “Wahai kaumku! Ikutilah utusan-utusan itu."},{"id":"21","ar":"اتَّبِعُوْا مَنْ لَّا يَسْـَٔلُكُمْ اَجْرًا وَّهُمْ مُّهْتَدُوْنَ ۔","bc":"ittabi'ụ mal lā yas`alukum ajraw wa hum muhtadụn","at":"Ikutilah orang yang tidak meminta imbalan kepadamu; dan mereka adalah orang-orang yang mendapat petunjuk."},{"id":"22","ar":"وَمَا لِيَ لَآ اَعْبُدُ الَّذِيْ فَطَرَنِيْ وَاِلَيْهِ تُرْجَعُوْنَ ","bc":"wa mā liya lā a'budullażī faṭaranī wa ilaihi turja'ụn","at":"Dan tidak ada alasan bagiku untuk tidak menyembah (Allah) yang telah menciptakanku dan hanya kepada-Nyalah kamu akan dikembalikan."},{"id":"23","ar":"ءَاَتَّخِذُ مِنْ دُوْنِهٖٓ اٰلِهَةً اِنْ يُّرِدْنِ الرَّحْمٰنُ بِضُرٍّ لَّا تُغْنِ عَنِّيْ شَفَاعَتُهُمْ شَيْـًٔا وَّلَا يُنْقِذُوْنِۚ ","bc":"a attakhiżu min dụnihī ālihatan iy yuridnir-raḥmānu biḍurril lā tugni 'annī syafā'atuhum syai`aw wa lā yungqiżụn","at":"Mengapa aku akan menyembah tuhan-tuhan selain-Nya? Jika (Allah) Yang Maha Pengasih menghendaki bencana terhadapku, pasti pertolongan mereka tidak berguna sama sekali bagi diriku dan mereka (juga) tidak dapat menyelamatkanku."},{"id":"24","ar":"اِنِّيْٓ اِذًا لَّفِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"innī iżal lafī ḍalālim mubīn","at":"Sesungguhnya jika aku (berbuat) begitu, pasti aku berada dalam kesesatan yang nyata."},{"id":"25","ar":"اِنِّيْٓ اٰمَنْتُ بِرَبِّكُمْ فَاسْمَعُوْنِۗ ","bc":"innī āmantu birabbikum fasma'ụn","at":"Sesungguhnya aku telah beriman kepada Tuhanmu; maka dengarkanlah (pengakuan keimanan)-ku.”"},{"id":"26","ar":"قِيْلَ ادْخُلِ الْجَنَّةَ ۗقَالَ يٰلَيْتَ قَوْمِيْ يَعْلَمُوْنَۙ","bc":"qīladkhulil-jannah, qāla yā laita qaumī ya'lamụn","at":"Dikatakan (kepadanya), “Masuklah ke surga.” Dia (laki-laki itu) berkata, “Alangkah baiknya sekiranya kaumku mengetahui,"},{"id":"27","ar":"بِمَا غَفَرَ لِيْ رَبِّيْ وَجَعَلَنِيْ مِنَ الْمُكْرَمِيْنَ","bc":"bimā gafara lī rabbī wa ja'alanī minal-mukramīn","at":"apa yang menyebabkan Tuhanku memberi ampun kepadaku dan menjadikan aku termasuk orang-orang yang telah dimuliakan.”"},{"id":"28","ar":"۞ وَمَآ اَنْزَلْنَا عَلٰى قَوْمِهٖ مِنْۢ بَعْدِهٖ مِنْ جُنْدٍ مِّنَ السَّمَاۤءِ وَمَا كُنَّا مُنْزِلِيْنَ ","bc":"wa mā anzalnā 'alā qaumihī mim ba'dihī min jundim minas-samā`i wa mā kunnā munzilīn","at":"Dan setelah dia (meninggal), Kami tidak menurunkan suatu pasukan pun dari langit kepada kaumnya, dan Kami tidak perlu menurunkannya."},{"id":"29","ar":"اِنْ كَانَتْ اِلَّا صَيْحَةً وَّاحِدَةً فَاِذَا هُمْ خَامِدُوْنَ ","bc":"ing kānat illā ṣaiḥataw wāḥidatan fa iżā hum khāmidụn","at":"Tidak ada siksaan terhadap mereka melainkan dengan satu teriakan saja; maka seketika itu mereka mati."},{"id":"30","ar":"يٰحَسْرَةً عَلَى الْعِبَادِۚ مَا يَأْتِيْهِمْ مِّنْ رَّسُوْلٍ اِلَّا كَانُوْا بِهٖ يَسْتَهْزِءُوْنَ ","bc":"yā ḥasratan 'alal-'ibād, mā ya`tīhim mir rasụlin illā kānụ bihī yastahzi`ụn","at":"Alangkah besar penyesalan terhadap hamba-hamba itu, setiap datang seorang rasul kepada mereka, mereka selalu memperolok-olokkannya."},{"id":"31","ar":"اَلَمْ يَرَوْا كَمْ اَهْلَكْنَا قَبْلَهُمْ مِّنَ الْقُرُوْنِ اَنَّهُمْ اِلَيْهِمْ لَا يَرْجِعُوْنَ ","bc":"a lam yarau kam ahlaknā qablahum minal-qurụni annahum ilaihim lā yarji'ụn","at":"Tidakkah mereka mengetahui berapa banyak umat-umat sebelum mereka yang telah Kami binasakan. Orang-orang (yang telah Kami binasakan) itu tidak ada yang kembali kepada mereka."},{"id":"32","ar":"وَاِنْ كُلٌّ لَّمَّا جَمِيْعٌ لَّدَيْنَا مُحْضَرُوْنَ","bc":"wa ing kullul lammā jamī'ul ladainā muḥḍarụn","at":"Dan setiap (umat), semuanya akan dihadapkan kepada Kami."},{"id":"33","ar":"وَاٰيَةٌ لَّهُمُ الْاَرْضُ الْمَيْتَةُ ۖاَحْيَيْنٰهَا وَاَخْرَجْنَا مِنْهَا حَبًّا فَمِنْهُ يَأْكُلُوْنَ","bc":"wa āyatul lahumul-arḍul-maitatu aḥyaināhā wa akhrajnā min-hā ḥabban fa min-hu ya`kulụn","at":"Dan suatu tanda (kebesaran Allah) bagi mereka adalah bumi yang mati (tandus). Kami hidupkan bumi itu dan Kami keluarkan darinya biji-bijian, maka dari (biji-bijian) itu mereka makan."},{"id":"34","ar":"وَجَعَلْنَا فِيْهَا جَنّٰتٍ مِّنْ نَّخِيْلٍ وَّاَعْنَابٍ وَّفَجَّرْنَا فِيْهَا مِنَ الْعُيُوْنِۙ","bc":"wa ja'alnā fīhā jannātim min nakhīliw wa a'nābiw wa fajjarnā fīhā minal-'uyụn","at":"Dan Kami jadikan padanya di bumi itu kebun-kebun kurma dan anggur dan Kami pancarkan padanya beberapa mata air,"},{"id":"35","ar":"لِيَأْكُلُوْا مِنْ ثَمَرِهٖۙ وَمَا عَمِلَتْهُ اَيْدِيْهِمْ ۗ اَفَلَا يَشْكُرُوْنَ ","bc":"liya`kulụ min ṡamarihī wa mā 'amilat-hu aidīhim, a fa lā yasykurụn","at":"agar mereka dapat makan dari buahnya, dan dari hasil usaha tangan mereka. Maka mengapa mereka tidak bersyukur?"},{"id":"36","ar":"سُبْحٰنَ الَّذِيْ خَلَقَ الْاَزْوَاجَ كُلَّهَا مِمَّا تُنْۢبِتُ الْاَرْضُ وَمِنْ اَنْفُسِهِمْ وَمِمَّا لَا يَعْلَمُوْنَ","bc":"sub-ḥānallażī khalaqal-azwāja kullahā mimmā tumbitul-arḍu wa min anfusihim wa mimmā lā ya'lamụn","at":"Mahasuci (Allah) yang telah menciptakan semuanya berpasang-pasangan, baik dari apa yang ditumbuhkan oleh bumi dan dari diri mereka sendiri, maupun dari apa yang tidak mereka ketahui."},{"id":"37","ar":"وَاٰيَةٌ لَّهُمُ الَّيْلُ ۖنَسْلَخُ مِنْهُ النَّهَارَ فَاِذَا هُمْ مُّظْلِمُوْنَۙ","bc":"wa āyatul lahumul-lailu naslakhu min-hun-nahāra fa iżā hum muẓlimụn","at":"Dan suatu tanda (kebesaran Allah) bagi mereka adalah malam; Kami tanggalkan siang dari (malam) itu, maka seketika itu mereka (berada dalam) kegelapan,"},{"id":"38","ar":"وَالشَّمْسُ تَجْرِيْ لِمُسْتَقَرٍّ لَّهَا ۗذٰلِكَ تَقْدِيْرُ الْعَزِيْزِ الْعَلِيْمِۗ ","bc":"wasy-syamsu tajrī limustaqarril lahā, żālika taqdīrul-'azīzil-'alīm","at":"dan matahari berjalan di tempat peredarannya. Demikianlah ketetapan (Allah) Yang Mahaperkasa, Maha Mengetahui."},{"id":"39","ar":"وَالْقَمَرَ قَدَّرْنٰهُ مَنَازِلَ حَتّٰى عَادَ كَالْعُرْجُوْنِ الْقَدِيْمِ ","bc":"wal-qamara qaddarnāhu manāzila ḥattā 'āda kal-'urjụnil-qadīm","at":"Dan telah Kami tetapkan tempat peredaran bagi bulan, sehingga (setelah ia sampai ke tempat peredaran yang terakhir) kembalilah ia seperti bentuk tandan yang tua."},{"id":"40","ar":"لَا الشَّمْسُ يَنْۢبَغِيْ لَهَآ اَنْ تُدْرِكَ الْقَمَرَ وَلَا الَّيْلُ سَابِقُ النَّهَارِ ۗوَكُلٌّ فِيْ فَلَكٍ يَّسْبَحُوْنَ","bc":"lasy-syamsu yambagī lahā an tudrikal-qamara wa lal-lailu sābiqun-nahār, wa kullun fī falakiy yasbaḥụn","at":"Tidaklah mungkin bagi matahari mengejar bulan dan malam pun tidak dapat mendahului siang. Masing-masing beredar pada garis edarnya."},{"id":"41","ar":"وَاٰيَةٌ لَّهُمْ اَنَّا حَمَلْنَا ذُرِّيَّتَهُمْ فِى الْفُلْكِ الْمَشْحُوْنِۙ","bc":"wa āyatul lahum annā ḥamalnā żurriyyatahum fil-fulkil-masy-ḥụn","at":"Dan suatu tanda (kebesaran Allah) bagi mereka adalah bahwa Kami angkut keturunan mereka dalam kapal yang penuh muatan,"},{"id":"42","ar":"وَخَلَقْنَا لَهُمْ مِّنْ مِّثْلِهٖ مَا يَرْكَبُوْنَ","bc":"wa khalaqnā lahum mim miṡlihī mā yarkabụn","at":"dan Kami ciptakan (juga) untuk mereka (angkutan lain) seperti apa yang mereka kendarai."},{"id":"43","ar":"وَاِنْ نَّشَأْ نُغْرِقْهُمْ فَلَا صَرِيْخَ لَهُمْ وَلَاهُمْ يُنْقَذُوْنَۙ","bc":"wa in nasya` nugriq-hum fa lā ṣarīkha lahum wa lā hum yungqażụn","at":"Dan jika Kami menghendaki, Kami tenggelamkan mereka. Maka tidak ada penolong bagi mereka dan tidak (pula) mereka diselamatkan,"},{"id":"44","ar":"اِلَّا رَحْمَةً مِّنَّا وَمَتَاعًا اِلٰى حِيْنٍ","bc":"illā raḥmatam minnā wa matā'an ilā ḥīn","at":"melainkan (Kami selamatkan mereka) karena rahmat yang besar dari Kami dan untuk memberikan kesenangan hidup sampai waktu tertentu."},{"id":"45","ar":"وَاِذَا قِيْلَ لَهُمُ اتَّقُوْا مَا بَيْنَ اَيْدِيْكُمْ وَمَا خَلْفَكُمْ لَعَلَّكُمْ تُرْحَمُوْنَ","bc":"wa iżā qīla lahumuttaqụ mā baina aidīkum wa mā khalfakum la'allakum tur-ḥamụn","at":"Dan apabila dikatakan kepada mereka, “Takutlah kamu akan siksa yang di hadapanmu (di dunia) dan azab yang akan datang (akhirat) agar kamu mendapat rahmat.”"},{"id":"46","ar":"وَمَا تَأْتِيْهِمْ مِّنْ اٰيَةٍ مِّنْ اٰيٰتِ رَبِّهِمْ اِلَّا كَانُوْا عَنْهَا مُعْرِضِيْنَ ","bc":"wa mā ta`tīhim min āyatim min āyāti rabbihim illā kānụ 'an-hā mu'riḍīn","at":"Dan setiap kali suatu tanda dari tanda-tanda (kebesaran) Tuhan datang kepada mereka, mereka selalu berpaling darinya."},{"id":"47","ar":"وَاِذَا قِيْلَ لَهُمْ اَنْفِقُوْا مِمَّا رَزَقَكُمُ اللّٰهُ ۙقَالَ الَّذِيْنَ كَفَرُوْا لِلَّذِيْنَ اٰمَنُوْٓا اَنُطْعِمُ مَنْ لَّوْ يَشَاۤءُ اللّٰهُ اَطْعَمَهٗٓ ۖاِنْ اَنْتُمْ اِلَّا فِيْ ضَلٰلٍ مُّبِيْنٍ","bc":"wa iżā qīla lahum anfiqụ mimmā razaqakumullāhu qālallażīna kafarụ lillażīna āmanū a nuṭ'imu mal lau yasyā`ullāhu aṭ'amahū in antum illā fī ḍalālim mubīn","at":"Dan apabila dikatakan kepada mereka, “Infakkanlah sebagian rezeki yang diberikan Allah kepadamu,” orang-orang yang kafir itu berkata kepada orang-orang yang beriman, “Apakah pantas kami memberi makan kepada orang-orang yang jika Allah menghendaki Dia akan memberinya makan? Kamu benar-benar dalam kesesatan yang nyata.”"},{"id":"48","ar":"وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ","bc":"wa yaqụlụna matā hāżal-wa'du ing kuntum ṣādiqīn","at":"Dan mereka (orang-orang kafir) berkata, “Kapan janji (hari berbangkit) itu (terjadi) jika kamu orang yang benar?”"},{"id":"49","ar":"مَا يَنْظُرُوْنَ اِلَّا صَيْحَةً وَّاحِدَةً تَأْخُذُهُمْ وَهُمْ يَخِصِّمُوْنَ","bc":"mā yanẓurụna illā ṣaiḥataw wāḥidatan ta`khużuhum wa hum yakhiṣṣimụn","at":"Mereka hanya menunggu satu teriakan, yang akan membinasakan mereka ketika mereka sedang bertengkar."},{"id":"50","ar":"فَلَا يَسْتَطِيْعُوْنَ تَوْصِيَةً وَّلَآ اِلٰٓى اَهْلِهِمْ يَرْجِعُوْنَ ","bc":"fa lā yastaṭī'ụna tauṣiyataw wa lā ilā ahlihim yarji'ụn","at":"Sehingga mereka tidak mampu membuat suatu wasiat dan mereka (juga) tidak dapat kembali kepada keluarganya."},{"id":"51","ar":"وَنُفِخَ فِى الصُّوْرِ فَاِذَا هُمْ مِّنَ الْاَجْدَاثِ اِلٰى رَبِّهِمْ يَنْسِلُوْنَ","bc":"wa nufikha fiṣ-ṣụri fa iżā hum minal-ajdāṡi ilā rabbihim yansilụn","at":"Lalu ditiuplah sangkakala, maka seketika itu mereka keluar dari kuburnya (dalam keadaan hidup), menuju kepada Tuhannya."},{"id":"52","ar":"قَالُوْا يٰوَيْلَنَا مَنْۢ بَعَثَنَا مِنْ مَّرْقَدِنَا ۜهٰذَا مَا وَعَدَ الرَّحْمٰنُ وَصَدَقَ الْمُرْسَلُوْنَ","bc":"qālụ yā wailanā mam ba'aṡanā mim marqadinā hāżā mā wa'adar-raḥmānu wa ṣadaqal-mursalụn","at":"Mereka berkata, “Celakalah kami! Siapakah yang membangkitkan kami dari tempat tidur kami (kubur)?” Inilah yang dijanjikan (Allah) Yang Maha Pengasih dan benarlah rasul-rasul(-Nya)."},{"id":"53","ar":"اِنْ كَانَتْ اِلَّا صَيْحَةً وَّاحِدَةً فَاِذَا هُمْ جَمِيْعٌ لَّدَيْنَا مُحْضَرُوْنَ","bc":"ing kānat illā ṣaiḥataw wāḥidatan fa iżā hum jamī'ul ladainā muḥḍarụn","at":"Teriakan itu hanya sekali saja, maka seketika itu mereka semua dihadapkan kepada Kami (untuk dihisab)."},{"id":"54","ar":"فَالْيَوْمَ لَا تُظْلَمُ نَفْسٌ شَيْـًٔا وَّلَا تُجْزَوْنَ اِلَّا مَا كُنْتُمْ تَعْمَلُوْنَ","bc":"fal-yauma lā tuẓlamu nafsun syai`aw wa lā tujzauna illā mā kuntum ta'malụn","at":"Maka pada hari itu seseorang tidak akan dirugikan sedikit pun dan kamu tidak akan diberi balasan, kecuali sesuai dengan apa yang telah kamu kerjakan."},{"id":"55","ar":"اِنَّ اَصْحٰبَ الْجَنَّةِ الْيَوْمَ فِيْ شُغُلٍ فٰكِهُوْنَ ۚ ","bc":"inna aṣ-ḥābal-jannatil-yauma fī syugulin fākihụn","at":"Sesungguhnya penghuni surga pada hari itu bersenang-senang dalam kesibukan (mereka)."},{"id":"56","ar":"هُمْ وَاَزْوَاجُهُمْ فِيْ ظِلٰلٍ عَلَى الْاَرَاۤىِٕكِ مُتَّكِـُٔوْنَ ۚ ","bc":"hum wa azwājuhum fī ẓilālin 'alal-arā`iki muttaki`ụn","at":"Mereka dan pasangan-pasangannya berada dalam tempat yang teduh, bersandar di atas dipan-dipan."},{"id":"57","ar":"لَهُمْ فِيْهَا فَاكِهَةٌ وَّلَهُمْ مَّا يَدَّعُوْنَ ۚ ","bc":"lahum fīhā fākihatuw wa lahum mā yadda'ụn","at":"Di surga itu mereka memperoleh buah-buahan dan memperoleh apa saja yang mereka inginkan."},{"id":"58","ar":"سَلٰمٌۗ قَوْلًا مِّنْ رَّبٍّ رَّحِيْمٍ","bc":"salām, qaulam mir rabbir raḥīm","at":"(Kepada mereka dikatakan), “Salam,” sebagai ucapan selamat dari Tuhan Yang Maha Penyayang."},{"id":"59","ar":"وَامْتَازُوا الْيَوْمَ اَيُّهَا الْمُجْرِمُوْنَ","bc":"wamtāzul-yauma ayyuhal-mujrimụn","at":"Dan (dikatakan kepada orang-orang kafir), “Berpisahlah kamu (dari orang-orang mukmin) pada hari ini, wahai orang-orang yang berdosa!"},{"id":"60","ar":"اَلَمْ اَعْهَدْ اِلَيْكُمْ يٰبَنِيْٓ اٰدَمَ اَنْ لَّا تَعْبُدُوا الشَّيْطٰنَۚ اِنَّهٗ لَكُمْ عَدُوٌّ مُّبِيْنٌ ","bc":"a lam a'had ilaikum yā banī ādama al lā ta'budusy-syaiṭān, innahụ lakum 'aduwwum mubīn","at":"Bukankah Aku telah memerintahkan kepadamu wahai anak cucu Adam agar kamu tidak menyembah setan? Sungguh, setan itu musuh yang nyata bagi kamu,"},{"id":"61","ar":"وَاَنِ اعْبُدُوْنِيْ ۗهٰذَا صِرَاطٌ مُّسْتَقِيْمٌ ","bc":"wa ani'budụnī, hāżā ṣirāṭum mustaqīm","at":"dan hendaklah kamu menyembah-Ku. Inilah jalan yang lurus.”"},{"id":"62","ar":"وَلَقَدْ اَضَلَّ مِنْكُمْ جِبِلًّا كَثِيْرًا ۗاَفَلَمْ تَكُوْنُوْا تَعْقِلُوْنَ ","bc":"wa laqad aḍalla mingkum jibillang kaṡīrā, a fa lam takụnụ ta'qilụn","at":"Dan sungguh, ia (setan itu) telah menyesatkan sebagian besar di antara kamu. Maka apakah kamu tidak mengerti?"},{"id":"63","ar":"هٰذِهٖ جَهَنَّمُ الَّتِيْ كُنْتُمْ تُوْعَدُوْنَ","bc":"hāżihī jahannamullatī kuntum tụ'adụn","at":"Inilah (neraka) Jahanam yang dahulu telah diperingatkan kepadamu."},{"id":"64","ar":"اِصْلَوْهَا الْيَوْمَ بِمَا كُنْتُمْ تَكْفُرُوْنَ ","bc":"iṣlauhal-yauma bimā kuntum takfurụn","at":"Masuklah ke dalamnya pada hari ini karena dahulu kamu mengingkarinya."},{"id":"65","ar":"اَلْيَوْمَ نَخْتِمُ عَلٰٓى اَفْوَاهِهِمْ وَتُكَلِّمُنَآ اَيْدِيْهِمْ وَتَشْهَدُ اَرْجُلُهُمْ بِمَا كَانُوْا يَكْسِبُوْنَ ","bc":"al-yauma nakhtimu 'alā afwāhihim wa tukallimunā aidīhim wa tasy-hadu arjuluhum bimā kānụ yaksibụn","at":"Pada hari ini Kami tutup mulut mereka; tangan mereka akan berkata kepada Kami dan kaki mereka akan memberi kesaksian terhadap apa yang dahulu mereka kerjakan."},{"id":"66","ar":"وَلَوْ نَشَاۤءُ لَطَمَسْنَا عَلٰٓى اَعْيُنِهِمْ فَاسْتَبَقُوا الصِّرَاطَ فَاَنّٰى يُبْصِرُوْنَ","bc":"walau nasyā`u laṭamasnā 'alā a'yunihim fastabaquṣ-ṣirāṭa fa annā yubṣirụn","at":"Dan jika Kami menghendaki, pastilah Kami hapuskan penglihatan mata mereka; sehingga mereka berlomba-lomba (mencari) jalan. Maka bagaimana mungkin mereka dapat melihat?"},{"id":"67","ar":"وَلَوْ نَشَاۤءُ لَمَسَخْنٰهُمْ عَلٰى مَكَانَتِهِمْ فَمَا اسْتَطَاعُوْا مُضِيًّا وَّلَا يَرْجِعُوْنَ ","bc":"walau nasyā`u lamasakhnāhum 'alā makānatihim famastaṭā'ụ muḍiyyaw wa lā yarji'ụn","at":"Dan jika Kami menghendaki, pastilah Kami ubah bentuk mereka di tempat mereka berada; sehingga mereka tidak sanggup berjalan lagi dan juga tidak sanggup kembali."},{"id":"68","ar":"وَمَنْ نُّعَمِّرْهُ نُنَكِّسْهُ فِى الْخَلْقِۗ اَفَلَا يَعْقِلُوْنَ ","bc":"wa man nu'ammir-hu nunakkis-hu fil-khalq, a fa lā ya'qilụn","at":"Dan barangsiapa Kami panjangkan umurnya niscaya Kami kembalikan dia kepada awal kejadian(nya). Maka mengapa mereka tidak mengerti?"},{"id":"69","ar":"وَمَا عَلَّمْنٰهُ الشِّعْرَ وَمَا يَنْۢبَغِيْ لَهٗ ۗاِنْ هُوَ اِلَّا ذِكْرٌ وَّقُرْاٰنٌ مُّبِيْنٌ ۙ","bc":"wa mā 'allamnāhusy-syi'ra wa mā yambagī lah, in huwa illā żikruw wa qur`ānum mubīn","at":"Dan Kami tidak mengajarkan syair kepadanya (Muhammad) dan bersyair itu tidaklah pantas baginya. Al-Qur'an itu tidak lain hanyalah pelajaran dan Kitab yang jelas,"},{"id":"70","ar":"لِّيُنْذِرَ مَنْ كَانَ حَيًّا وَّيَحِقَّ الْقَوْلُ عَلَى الْكٰفِرِيْنَ","bc":"liyunżira mang kāna ḥayyaw wa yaḥiqqal-qaulu 'alal-kāfirīn","at":"agar dia (Muhammad) memberi peringatan kepada orang-orang yang hidup (hatinya) dan agar pasti ketetapan (azab) terhadap orang-orang kafir."},{"id":"71","ar":"اَوَلَمْ يَرَوْا اَنَّا خَلَقْنَا لَهُمْ مِّمَّا عَمِلَتْ اَيْدِيْنَآ اَنْعَامًا فَهُمْ لَهَا مَالِكُوْنَ ","bc":"a wa lam yarau annā khalaqnā lahum mimmā 'amilat aidīnā an'āman fa hum lahā mālikụn","at":"Dan tidakkah mereka melihat bahwa Kami telah menciptakan hewan ternak untuk mereka, yaitu sebagian dari apa yang telah Kami ciptakan dengan kekuasaan Kami, lalu mereka menguasainya?"},{"id":"72","ar":"وَذَلَّلْنٰهَا لَهُمْ فَمِنْهَا رَكُوْبُهُمْ وَمِنْهَا يَأْكُلُوْنَ ","bc":"wa żallalnāhā lahum fa min-hā rakụbuhum wa min-hā ya`kulụn","at":"Dan Kami menundukkannya (hewan-hewan itu) untuk mereka; lalu sebagiannya untuk menjadi tunggangan mereka dan sebagian untuk mereka makan."},{"id":"73","ar":"وَلَهُمْ فِيْهَا مَنَافِعُ وَمَشَارِبُۗ اَفَلَا يَشْكُرُوْنَ ","bc":"wa lahum fīhā manāfi'u wa masyārib, a fa lā yasykurụn","at":"Dan mereka memperoleh berbagai manfaat dan minuman darinya. Maka mengapa mereka tidak bersyukur?"},{"id":"74","ar":"وَاتَّخَذُوْا مِنْ دُوْنِ اللّٰهِ اٰلِهَةً لَّعَلَّهُمْ يُنْصَرُوْنَ ۗ ","bc":"wattakhażụ min dụnillāhi ālihatal la'allahum yunṣarụn","at":"Dan mereka mengambil sesembahan selain Allah agar mereka mendapat pertolongan."},{"id":"75","ar":"لَا يَسْتَطِيْعُوْنَ نَصْرَهُمْۙ وَهُمْ لَهُمْ جُنْدٌ مُّحْضَرُوْنَ","bc":"lā yastaṭī'ụna naṣrahum wa hum lahum jundum muḥḍarụn","at":"Mereka (sesembahan) itu tidak dapat menolong mereka; padahal mereka itu menjadi tentara yang disiapkan untuk menjaga (sesembahan) itu."},{"id":"76","ar":"فَلَا يَحْزُنْكَ قَوْلُهُمْ ۘاِنَّا نَعْلَمُ مَا يُسِرُّوْنَ وَمَا يُعْلِنُوْنَ ","bc":"fa lā yaḥzungka qauluhum, innā na'lamu mā yusirrụna wa mā yu'linụn","at":"Maka jangan sampai ucapan mereka membuat engkau (Muhammad) bersedih hati. Sungguh, Kami mengetahui apa yang mereka rahasiakan dan apa yang mereka nyatakan."},{"id":"77","ar":"اَوَلَمْ يَرَ الْاِنْسَانُ اَنَّا خَلَقْنٰهُ مِنْ نُّطْفَةٍ فَاِذَا هُوَ خَصِيْمٌ مُّبِيْنٌ ","bc":"a wa lam yaral-insānu annā khalaqnāhu min nuṭfatin fa iżā huwa khaṣīmum mubīn","at":"Dan tidakkah manusia memperhatikan bahwa Kami menciptakannya dari setetes mani, ternyata dia menjadi musuh yang nyata!"},{"id":"78","ar":"وَضَرَبَ لَنَا مَثَلًا وَّنَسِيَ خَلْقَهٗۗ قَالَ مَنْ يُّحْيِ الْعِظَامَ وَهِيَ رَمِيْمٌ ","bc":"wa ḍaraba lanā maṡalaw wa nasiya khalqah, qāla may yuḥyil-'iẓāma wa hiya ramīm","at":"Dan dia membuat perumpamaan bagi Kami dan melupakan asal kejadiannya; dia berkata, “Siapakah yang dapat menghidupkan tulang-belulang, yang telah hancur luluh?”"},{"id":"79","ar":"قُلْ يُحْيِيْهَا الَّذِيْٓ اَنْشَاَهَآ اَوَّلَ مَرَّةٍ ۗوَهُوَ بِكُلِّ خَلْقٍ عَلِيْمٌ ۙ","bc":"qul yuḥyīhallażī ansya`ahā awwala marrah, wa huwa bikulli khalqin 'alīm","at":"Katakanlah (Muhammad), “Yang akan menghidupkannya ialah (Allah) yang menciptakannya pertama kali. Dan Dia Maha Mengetahui tentang segala makhluk,"},{"id":"80","ar":" ِۨالَّذِيْ جَعَلَ لَكُمْ مِّنَ الشَّجَرِ الْاَخْضَرِ نَارًاۙ فَاِذَآ اَنْتُمْ مِّنْهُ تُوْقِدُوْنَ ","bc":"allażī ja'ala lakum minasy-syajaril-akhḍari nāran fa iżā antum min-hu tụqidụn","at":"yaitu (Allah) yang menjadikan api untukmu dari kayu yang hijau, maka seketika itu kamu nyalakan (api) dari kayu itu.”"},{"id":"81","ar":"اَوَلَيْسَ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ بِقٰدِرٍ عَلٰٓى اَنْ يَّخْلُقَ مِثْلَهُمْ ۗبَلٰى وَهُوَ الْخَلّٰقُ الْعَلِيْمُ ","bc":"a wa laisallażī khalaqas-samāwāti wal-arḍa biqādirin 'alā ay yakhluqa miṡlahum, balā wa huwal-khallāqul-'alīm","at":"Dan bukankah (Allah) yang menciptakan langit dan bumi, mampu menciptakan kembali yang serupa itu (jasad mereka yang sudah hancur itu)? Benar, dan Dia Maha Pencipta, Maha Mengetahui."},{"id":"82","ar":"اِنَّمَآ اَمْرُهٗٓ اِذَآ اَرَادَ شَيْـًٔاۖ اَنْ يَّقُوْلَ لَهٗ كُنْ فَيَكُوْنُ ","bc":"innamā amruhū iżā arāda syai`an ay yaqụla lahụ kun fa yakụn","at":"Sesungguhnya urusan-Nya apabila Dia menghendaki sesuatu Dia hanya berkata kepadanya, “Jadilah!” Maka jadilah sesuatu itu."},{"id":"83","ar":"فَسُبْحٰنَ الَّذِيْ بِيَدِهٖ مَلَكُوْتُ كُلِّ شَيْءٍ وَّاِلَيْهِ تُرْجَعُوْنَ","bc":"fa sub-ḥānallażī biyadihī malakụtu kulli syai`iw wa ilaihi turja'ụn","at":"Maka Mahasuci (Allah) yang di tangan-Nya kekuasaan atas segala sesuatu dan kepada-Nya kamu dikembalikan."}],yunus=[{"id":"1","ar":"الۤرٰ ۗتِلْكَ اٰيٰتُ الْكِتٰبِ الْحَكِيْمِ ","bc":"alif lām rā, tilka āyātul-kitābil-ḥakīm","at":"Alif Lam Ra. Inilah ayat-ayat Al-Qur'an yang penuh hikmah."},{"id":"2","ar":"اَكَانَ لِلنَّاسِ عَجَبًا اَنْ اَوْحَيْنَآ اِلٰى رَجُلٍ مِّنْهُمْ اَنْ اَنْذِرِ النَّاسَ وَبَشِّرِ الَّذِيْنَ اٰمَنُوْٓا اَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْ ۗ قَالَ الْكٰفِرُوْنَ اِنَّ هٰذَا لَسٰحِرٌ مُّبِيْنٌ ","bc":"a kāna lin-nāsi 'ajaban an auḥainā ilā rajulim min-hum an anżirin-nāsa wa basysyirillażīna āmanū anna lahum qadama ṣidqin 'inda rabbihim, qālal-kāfirụna inna hāżā lasāḥirum mubīn","at":"Pantaskah manusia menjadi heran bahwa Kami memberi wahyu kepada seorang laki-laki di antara mereka, “Berilah peringatan kepada manusia dan gembirakanlah orang-orang beriman bahwa mereka mempunyai kedudukan yang tinggi di sisi Tuhan.” Orang-orang kafir berkata, “Orang ini (Muhammad) benar-benar pesihir.”"},{"id":"3","ar":"اِنَّ رَبَّكُمُ اللّٰهُ الَّذِيْ خَلَقَ السَّمٰوٰتِ وَالْاَرْضَ فِيْ سِتَّةِ اَيَّامٍ ثُمَّ اسْتَوٰى عَلَى الْعَرْشِ يُدَبِّرُ الْاَمْرَۗ مَا مِنْ شَفِيْعٍ اِلَّا مِنْۢ بَعْدِ اِذْنِهٖۗ ذٰلِكُمُ اللّٰهُ رَبُّكُمْ فَاعْبُدُوْهُۗ اَفَلَا تَذَكَّرُوْنَ ","bc":"inna rabbakumullāhullażī khalaqas-samāwāti wal-arḍa fī sittati ayyāmin ṡummastawā 'alal-'arsyi yudabbirul-amr, mā min syafī'in illā mim ba'di iżnih, żālikumullāhu rabbukum fa'budụh, a fa lā tażakkarụn","at":"Sesungguhnya Tuhan kamu Dialah Allah yang menciptakan langit dan bumi dalam enam masa, kemudian Dia bersemayam di atas ‘Arsy (singgasana) untuk mengatur segala urusan. Tidak ada yang dapat memberi syafaat kecuali setelah ada izin-Nya. Itulah Allah, Tuhanmu, maka sembahlah Dia. Apakah kamu tidak mengambil pelajaran?"},{"id":"4","ar":"اِلَيْهِ مَرْجِعُكُمْ جَمِيْعًاۗ وَعْدَ اللّٰهِ حَقًّاۗ اِنَّهٗ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ لِيَجْزِيَ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ بِالْقِسْطِۗ وَالَّذِيْنَ كَفَرُوْا لَهُمْ شَرَابٌ مِّنْ حَمِيْمٍ وَّعَذَابٌ اَلِيْمٌ ۢبِمَا كَانُوْا يَكْفُرُوْنَ ","bc":"ilaihi marji'ukum jamī'ā, wa'dallāhi ḥaqqā, innahụ yabda`ul-khalqa ṡumma yu'īduhụ liyajziyallażīna āmanụ wa 'amiluṣ-ṣāliḥāti bil-qisṭ, wallażīna kafarụ lahum syarābum min ḥamīmiw wa 'ażābun alīmum bimā kānụ yakfurụn","at":"Hanya kepada-Nya kamu semua akan kembali. Itu merupakan janji Allah yang benar dan pasti. Sesungguhnya Dialah yang memulai penciptaan makhluk kemudian mengulanginya (menghidupkannya kembali setelah berbangkit), agar Dia memberi balasan kepada orang-orang yang beriman dan mengerjakan kebajikan dengan adil. Sedangkan untuk orang-orang kafir (disediakan) minuman air yang mendidih dan siksaan yang pedih karena kekafiran mereka."},{"id":"5","ar":"هُوَ الَّذِيْ جَعَلَ الشَّمْسَ ضِيَاۤءً وَّالْقَمَرَ نُوْرًا وَّقَدَّرَهٗ مَنَازِلَ لِتَعْلَمُوْا عَدَدَ السِّنِيْنَ وَالْحِسَابَۗ مَا خَلَقَ اللّٰهُ ذٰلِكَ اِلَّا بِالْحَقِّۗ يُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّعْلَمُوْنَ ","bc":"huwallażī ja'alasy-syamsa ḍiyā`aw wal-qamara nụraw wa qaddarahụ manāzila lita'lamụ 'adadas-sinīna wal-ḥisāb, mā khalaqallāhu żālika illā bil-ḥaqq, yufaṣṣilul-āyāti liqaumiy ya'lamụn","at":"Dialah yang menjadikan matahari bersinar dan bulan bercahaya, dan Dialah yang menetapkan tempat-tempat orbitnya, agar kamu mengetahui bilangan tahun, dan perhitungan (waktu). Allah tidak menciptakan demikian itu melainkan dengan benar. Dia menjelaskan tanda-tanda (kebesaran-Nya) kepada orang-orang yang mengetahui."},{"id":"6","ar":"اِنَّ فِى اخْتِلَافِ الَّيْلِ وَالنَّهَارِ وَمَا خَلَقَ اللّٰهُ فِى السَّمٰوٰتِ وَالْاَرْضِ لَاٰيٰتٍ لِّقَوْمٍ يَّتَّقُوْنَ ","bc":"inna fikhtilāfil-laili wan-nahāri wa mā khalaqallāhu fis-samāwāti wal-arḍi la`āyātil liqaumiy yattaqụn","at":"Sesungguhnya pada pergantian malam dan siang dan pada apa yang diciptakan Allah di langit dan di bumi, pasti terdapat tanda-tanda (kebesaran-Nya) bagi orang-orang yang bertakwa."},{"id":"7","ar":"اِنَّ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا وَرَضُوْا بِالْحَيٰوةِ الدُّنْيَا وَاطْمَـَٔنُّوْا بِهَا وَالَّذِيْنَ هُمْ عَنْ اٰيٰتِنَا غٰفِلُوْنَۙ","bc":"innallażīna lā yarjụna liqā`ana wa raḍụ bil-ḥayātid-dun-yā waṭma`annụ bihā wallażīna hum 'an āyātinā gāfilụn","at":"Sesungguhnya orang-orang yang tidak mengharapkan (tidak percaya akan) pertemuan dengan Kami, dan merasa puas dengan kehidupan dunia serta merasa tenteram dengan (kehidupan) itu, dan orang-orang yang melalaikan ayat-ayat Kami,"},{"id":"8","ar":"اُولٰۤىِٕكَ مَأْوٰىهُمُ النَّارُ بِمَا كَانُوْا يَكْسِبُوْنَ ","bc":"ulā`ika ma`wāhumun-nāru bimā kānụ yaksibụn","at":"mereka itu tempatnya di neraka, karena apa yang telah mereka lakukan."},{"id":"9","ar":"اِنَّ الَّذِيْنَ اٰمَنُوْا وَعَمِلُوا الصّٰلِحٰتِ يَهْدِيْهِمْ رَبُّهُمْ بِاِيْمَانِهِمْۚ تَجْرِيْ مِنْ تَحْتِهِمُ الْاَنْهٰرُ فِيْ جَنّٰتِ النَّعِيْمِ","bc":"innallażīna āmanụ wa 'amiluṣ-ṣāliḥāti yahdīhim rabbuhum bi`īmānihim, tajrī min taḥtihimul-an-hāru fī jannātin-na'īm","at":"Sesungguhnya orang-orang yang beriman dan mengerjakan kebajikan, niscaya diberi petunjuk oleh Tuhan karena keimanannya. Mereka di dalam surga yang penuh kenikmatan, mengalir di bawahnya sungai-sungai."},{"id":"10","ar":"دَعْوٰىهُمْ فِيْهَا سُبْحٰنَكَ اللهم وَتَحِيَّتُهُمْ فِيْهَا سَلٰمٌۚ وَاٰخِرُ دَعْوٰىهُمْ اَنِ الْحَمْدُ لِلّٰهِ رَبِّ الْعٰلَمِيْنَ","bc":"da'wāhum fīhā sub-ḥānakallāhumma wa taḥiyyatuhum fīhā salām, wa ākhiru da'wāhum anil-ḥamdu lillāhi rabbil 'ālamīn","at":"Doa mereka di dalamnya ialah, “Subhanakallahumma” (Mahasuci Engkau, ya Tuhan kami), dan salam penghormatan mereka ialah, “Salam” (salam sejahtera). Dan penutup doa mereka ialah, “Al-hamdu lillahi Rabbil ‘alamin” (segala puji bagi Allah Tuhan seluruh alam)."},{"id":"11","ar":"۞ وَلَوْ يُعَجِّلُ اللّٰهُ لِلنَّاسِ الشَّرَّ اسْتِعْجَالَهُمْ بِالْخَيْرِ لَقُضِيَ اِلَيْهِمْ اَجَلُهُمْۗ فَنَذَرُ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا فِيْ طُغْيَانِهِمْ يَعْمَهُوْنَ ","bc":"walau yu'ajjilullāhu lin-nāsisy-syarrasti'jālahum bil-khairi laquḍiya ilaihim ajaluhum, fa nażarullażīna lā yarjụna liqā`anā fī ṭugyānihim ya'mahụn","at":"Dan kalau Allah menyegerakan keburukan bagi manusia seperti permintaan mereka untuk menyegerakan kebaikan, pasti diakhiri umur mereka. Namun Kami biarkan orang-orang yang tidak mengharapkan pertemuan dengan Kami, bingung di dalam kesesatan mereka."},{"id":"12","ar":"وَاِذَا مَسَّ الْاِنْسَانَ الضُّرُّ دَعَانَا لِجَنْۢبِهٖٓ اَوْ قَاعِدًا اَوْ قَاۤىِٕمًا ۚفَلَمَّا كَشَفْنَا عَنْهُ ضُرَّهٗ مَرَّ كَاَنْ لَّمْ يَدْعُنَآ اِلٰى ضُرٍّ مَّسَّهٗۗ كَذٰلِكَ زُيِّنَ لِلْمُسْرِفِيْنَ مَا كَانُوْا يَعْمَلُوْنَ ","bc":"wa iżā massal-insānaḍ-ḍurru da'ānā lijambihī au qā'idan au qā`imā, fa lammā kasyafnā 'an-hu ḍurrahụ marra ka`al lam yad'unā ilā ḍurrim massah, każālika zuyyina lil-musrifīna mā kānụ ya'malụn","at":"Dan apabila manusia ditimpa bahaya dia berdoa kepada Kami dalam keadaan berbaring, duduk atau berdiri, tetapi setelah Kami hilangkan bahaya itu darinya, dia kembali (ke jalan yang sesat), seolah-olah dia tidak pernah berdoa kepada Kami untuk (menghilangkan) bahaya yang telah menimpanya. Demikianlah dijadikan terasa indah bagi orang-orang yang melampaui batas apa yang mereka kerjakan."},{"id":"13","ar":"وَلَقَدْ اَهْلَكْنَا الْقُرُوْنَ مِنْ قَبْلِكُمْ لَمَّا ظَلَمُوْاۙ وَجَاۤءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنٰتِ وَمَا كَانُوْا لِيُؤْمِنُوْا ۗ كَذٰلِكَ نَجْزِى الْقَوْمَ الْمُجْرِمِيْنَ ","bc":"wa laqad ahlaknal-qurụna ming qablikum lammā ẓalamụ wa jā`at-hum rusuluhum bil-bayyināti wa mā kānụ liyu`minụ, każālika najzil-qaumal-mujrimīn","at":" Dan sungguh, Kami telah membinasakan umat-umat sebelum kamu, ketika mereka berbuat zalim, padahal para rasul mereka telah datang membawa keterangan-keterangan (yang nyata), tetapi mereka sama sekali tidak mau beriman. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat dosa."},{"id":"14","ar":"ثُمَّ جَعَلْنٰكُمْ خَلٰۤىِٕفَ فِى الْاَرْضِ مِنْۢ بَعْدِهِمْ لِنَنْظُرَ كَيْفَ تَعْمَلُوْنَ ","bc":"ṡumma ja'alnākum khalā`ifa fil-arḍi mim ba'dihim linanẓura kaifa ta'malụn","at":"Kemudian Kami jadikan kamu sebagai pengganti-pengganti (mereka) di bumi setelah mereka, untuk Kami lihat bagaimana kamu berbuat."},{"id":"15","ar":"وَاِذَا تُتْلٰى عَلَيْهِمْ اٰيَاتُنَا بَيِّنٰتٍۙ قَالَ الَّذِيْنَ لَا يَرْجُوْنَ لِقَاۤءَنَا ائْتِ بِقُرْاٰنٍ غَيْرِ هٰذَآ اَوْ بَدِّلْهُ ۗ قُلْ مَا يَكُوْنُ لِيْٓ اَنْ اُبَدِّلَهٗ مِنْ تِلْقَاۤئِ نَفْسِيْ ۚاِنْ اَتَّبِعُ اِلَّا مَا يُوْحٰٓى اِلَيَّ ۚ اِنِّيْٓ اَخَافُ اِنْ عَصَيْتُ رَبِّيْ عَذَابَ يَوْمٍ عَظِيْمٍ ","bc":"wa iżā tutlā 'alaihim āyātunā bayyināting qālallażīna lā yarjụna liqā`ana`ti biqur`ānin gairi hāżā au baddil-h, qul mā yakụnu lī an ubaddilahụ min tilqā`i nafsī, in attabi'u illā mā yụḥā ilayy, innī akhāfu in 'aṣaitu rabbī 'ażāba yaumin 'aẓīm","at":"Dan apabila dibacakan kepada mereka ayat-ayat Kami dengan jelas, orang-orang yang tidak mengharapkan pertemuan dengan Kami berkata, “Datangkanlah kitab selain Al-Qur'an ini atau gantilah.” Katakanlah (Muhammad), “Tidaklah pantas bagiku menggantinya atas kemauanku sendiri. Aku hanya mengikuti apa yang diwahyukan kepadaku. Aku benar-benar takut akan azab hari yang besar (Kiamat) jika mendurhakai Tuhanku.”"},{"id":"16","ar":"قُلْ لَّوْ شَاۤءَ اللّٰهُ مَا تَلَوْتُهٗ عَلَيْكُمْ وَلَآ اَدْرٰىكُمْ بِهٖ ۖفَقَدْ لَبِثْتُ فِيْكُمْ عُمُرًا مِّنْ قَبْلِهٖۗ اَفَلَا تَعْقِلُوْنَ","bc":"qul lau syā`allāhu mā talautuhụ 'alaikum wa lā adrākum bihī fa qad labiṡtu fīkum 'umuram ming qablih, a fa lā ta'qilụn","at":"Katakanlah (Muhammad), “Jika Allah menghendaki, niscaya aku tidak membacakannya kepadamu dan Allah tidak (pula) memberitahukannya kepadamu.” Aku telah tinggal bersamamu beberapa lama sebelumnya (sebelum turun Al-Qur'an). Apakah kamu tidak mengerti?"},{"id":"17","ar":"فَمَنْ اَظْلَمُ مِمَّنِ افْتَرٰى عَلَى اللّٰهِ كَذِبًا اَوْ كَذَّبَ بِاٰيٰتِهٖۗ اِنَّهٗ لَا يُفْلِحُ الْمُجْرِمُوْنَ ","bc":"fa man aẓlamu mim maniftarā 'alallāhi każiban au każżaba bi`āyātih, innahụ lā yufliḥul-mujrimụn","at":"Maka siapakah yang lebih zalim daripada orang yang mengada-adakan kebohongan terhadap Allah atau mendustakan ayat-ayat-Nya? Sesungguhnya orang-orang yang berbuat dosa itu tidak akan beruntung."},{"id":"18","ar":"وَيَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ مَا لَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ وَيَقُوْلُوْنَ هٰٓؤُلَاۤءِ شُفَعَاۤؤُنَا عِنْدَ اللّٰهِ ۗقُلْ اَتُنَبِّـُٔوْنَ اللّٰهَ بِمَا لَا يَعْلَمُ فِى السَّمٰوٰتِ وَلَا فِى الْاَرْضِۗ سُبْحٰنَهٗ وَتَعٰلٰى عَمَّا يُشْرِكُوْنَ ","bc":"wa ya'budụna min dụnillāhi mā lā yaḍurruhum wa lā yanfa'uhum wa yaqụlụna hā`ulā`i syufa'ā`unā 'indallāh, qul a tunabbi`ụnallāha bimā lā ya'lamu fis-samāwāti wa lā fil-arḍ, sub-ḥānahụ wa ta'ālā 'ammā yusyrikụn","at":"Dan mereka menyembah selain Allah, sesuatu yang tidak dapat mendatangkan bencana kepada mereka dan tidak (pula) memberi manfaat, dan mereka berkata, “Mereka itu adalah pemberi syafaat kami di hadapan Allah.” Katakanlah, “Apakah kamu akan memberitahu kepada Allah sesuatu yang tidak diketahui-Nya apa yang di langit dan tidak (pula) yang di bumi?” Mahasuci Allah dan Mahatinggi dari apa yang mereka persekutukan itu."},{"id":"19","ar":"وَمَا كَانَ النَّاسُ اِلَّآ اُمَّةً وَّاحِدَةً فَاخْتَلَفُوْاۗ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِنْ رَّبِّكَ لَقُضِيَ بَيْنَهُمْ فِيْمَا فِيْهِ يَخْتَلِفُوْنَ ","bc":"wa mā kānan-nāsu illā ummataw wāḥidatan fakhtalafụ, walau lā kalimatun sabaqat mir rabbika laquḍiya bainahum fīmā fīhi yakhtalifụn","at":"Dan manusia itu dahulunya hanyalah satu umat, kemudian mereka berselisih. Kalau tidak karena suatu ketetapan yang telah ada dari Tuhanmu, pastilah telah diberi keputusan (di dunia) di antara mereka, tentang apa yang mereka perselisihkan itu."},{"id":"20","ar":"وَيَقُوْلُوْنَ لَوْلَآ اُنْزِلَ عَلَيْهِ اٰيَةٌ مِّنْ رَّبِّهٖۚ فَقُلْ اِنَّمَا الْغَيْبُ لِلّٰهِ فَانْتَظِرُوْاۚ اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ ","bc":"wa yaqụlụna lau lā unzila 'alaihi āyatum mir rabbih, fa qul innamal-gaibu lillāhi fantaẓirụ, innī ma'akum minal-muntaẓirīn","at":"Dan mereka berkata, “Mengapa tidak diturunkan kepadanya (Muhammad) suatu bukti (mukjizat) dari Tuhannya?” Katakanlah, “Sungguh, segala yang gaib itu hanya milik Allah; sebab itu tunggu (sajalah) olehmu. Ketahuilah aku juga menunggu bersama kamu.”"},{"id":"21","ar":"وَاِذَآ اَذَقْنَا النَّاسَ رَحْمَةً مِّنْۢ بَعْدِ ضَرَّاۤءَ مَسَّتْهُمْ اِذَا لَهُمْ مَّكْرٌ فِيْٓ اٰيٰتِنَاۗ قُلِ اللّٰهُ اَسْرَعُ مَكْرًاۗ اِنَّ رُسُلَنَا يَكْتُبُوْنَ مَا تَمْكُرُوْنَ ","bc":"wa iżā ażaqnan-nāsa raḥmatam mim ba'di ḍarrā`a massat-hum iżā lahum makrun fī āyātinā, qulillāhu asra'u makrā, inna rusulana yaktubụna mā tamkurụn","at":"Dan apabila Kami memberikan suatu rahmat kepada manusia, setelah mereka ditimpa bencana, mereka segera melakukan segala tipu daya (menentang) ayat-ayat Kami. Katakanlah, “Allah lebih cepat pembalasannya (atas tipu daya itu).” Sesungguhnya malaikat-malaikat Kami mencatat tipu dayamu."},{"id":"22","ar":"هُوَ الَّذِيْ يُسَيِّرُكُمْ فِى الْبَرِّ وَالْبَحْرِۗ حَتّٰٓى اِذَا كُنْتُمْ فِىْ الْفُلْكِۚ وَجَرَيْنَ بِهِمْ بِرِيْحٍ طَيِّبَةٍ وَّفَرِحُوْا بِهَا جَاۤءَتْهَا رِيْحٌ عَاصِفٌ وَّجَاۤءَهُمُ الْمَوْجُ مِنْ كُلِّ مَكَانٍ وَّظَنُّوْٓا اَنَّهُمْ اُحِيْطَ بِهِمْۙ دَعَوُا اللّٰهَ مُخْلِصِيْنَ لَهُ الدِّيْنَ ەۚ لَىِٕنْ اَنْجَيْتَنَا مِنْ هٰذِهٖ لَنَكُوْنَنَّ مِنَ الشّٰكِرِيْنَ","bc":"huwallażī yusayyirukum fil-barri wal-baḥr, ḥattā iżā kuntum fil-fulk, wa jaraina bihim birīḥin ṭayyibatiw wa fariḥụ bihā jā`at-hā rīḥun 'āṣifuw wa jā`ahumul-mauju ming kulli makāniw wa ẓannū annahum uḥīṭa bihim da'awullāha mukhliṣīna lahud-dīn, la`in anjaitanā min hāżihī lanakụnanna minasy-syākirīn","at":"Dialah Tuhan yang menjadikan kamu dapat berjalan di daratan, (dan berlayar) di lautan. Sehingga ketika kamu berada di dalam kapal, dan meluncurlah (kapal) itu membawa mereka (orang-orang yang ada di dalamnya) dengan tiupan angin yang baik, dan mereka bergembira karenanya; tiba-tiba datanglah badai dan gelombang menimpanya dari segenap penjuru, dan mereka mengira telah terkepung (bahaya), maka mereka berdoa dengan tulus ikhlas kepada Allah semata. (Seraya berkata), “Sekiranya Engkau menyelamatkan kami dari (bahaya) ini, pasti kami termasuk orang-orang yang bersyukur.”"},{"id":"23","ar":"فَلَمَّآ اَنْجٰىهُمْ اِذَا هُمْ يَبْغُوْنَ فِى الْاَرْضِ بِغَيْرِ الْحَقِّ ۗيٰٓاَيُّهَا النَّاسُ اِنَّمَا بَغْيُكُمْ عَلٰٓى اَنْفُسِكُمْ مَّتَاعَ الْحَيٰوةِ الدُّنْيَاۖ ثُمَّ اِلَيْنَا مَرْجِعُكُمْ فَنُنَبِّئُكُمْ بِمَا كُنْتُمْ تَعْمَلُوْنَ","bc":"fa lammā anjāhum iżā hum yabgụna fil-arḍi bigairil-ḥaqq, yā ayyuhan-nāsu innamā bagyukum 'alā anfusikum matā'al-ḥayātid-dun-yā ṡumma ilainā marji'ukum fa nunabbi`ukum bimā kuntum ta'malụn","at":"Tetapi ketika Allah menyelamatkan mereka, malah mereka berbuat kezaliman di bumi tanpa (alasan) yang benar. Wahai manusia! Sesungguhnya kezalimanmu bahayanya akan menimpa dirimu sendiri; itu hanya kenikmatan hidup duniawi, selanjutnya kepada Kamilah kembalimu, kelak akan Kami kabarkan kepadamu apa yang telah kamu kerjakan."},{"id":"24","ar":"اِنَّمَا مَثَلُ الْحَيٰوةِ الدُّنْيَا كَمَاۤءٍ اَنْزَلْنٰهُ مِنَ السَّمَاۤءِ فَاخْتَلَطَ بِهٖ نَبَاتُ الْاَرْضِ مِمَّا يَأْكُلُ النَّاسُ وَالْاَنْعَامُ ۗحَتّٰٓى اِذَآ اَخَذَتِ الْاَرْضُ زُخْرُفَهَا وَازَّيَّنَتْ وَظَنَّ اَهْلُهَآ اَنَّهُمْ قٰدِرُوْنَ عَلَيْهَآ اَتٰىهَآ اَمْرُنَا لَيْلًا اَوْ نَهَارًا فَجَعَلْنٰهَا حَصِيْدًا كَاَنْ لَّمْ تَغْنَ بِالْاَمْسِۗ كَذٰلِكَ نُفَصِّلُ الْاٰيٰتِ لِقَوْمٍ يَّتَفَكَّرُوْنَ ","bc":"innamā maṡalul-ḥayātid-dun-yā kamā`in anzalnāhu minas-samā`i fakhtalaṭa bihī nabātul-arḍi mimmā ya`kulun-nāsu wal-an'ām, ḥattā iżā akhażatil-arḍu zukhrufahā wazzayyanat wa ẓanna ahluhā annahum qādirụna 'alaihā atāhā amrunā lailan au nahāran fa ja'alnāhā ḥaṣīdang ka`al lam tagna bil-ams, każālika nufaṣṣilul-āyāti liqaumiy yatafakkarụn","at":"Sesungguhnya perumpamaan kehidupan duniawi itu, hanya seperti air (hujan) yang Kami turunkan dari langit, lalu tumbuhlah tanaman-tanaman bumi dengan subur (karena air itu), di antaranya ada yang dimakan manusia dan hewan ternak. Hingga apabila bumi itu telah sempurna keindahannya, dan berhias, dan pemiliknya mengira bahwa mereka pasti menguasainya (memetik hasilnya), datanglah kepadanya azab Kami pada waktu malam atau siang, lalu Kami jadikan (tanaman)nya seperti tanaman yang sudah disabit, seakan-akan belum pernah tumbuh kemarin. Demikianlah Kami menjelaskan tanda-tanda (kekuasaan Kami) kepada orang yang berpikir."},{"id":"25","ar":"وَاللّٰهُ يَدْعُوْٓ اِلٰى دَارِ السَّلٰمِ ۚوَيَهْدِيْ مَنْ يَّشَاۤءُ اِلٰى صِرَاطٍ مُّسْتَقِيْمٍ ","bc":"wallāhu yad'ū ilā dāris-salām, wa yahdī may yasyā`u ilā ṣirāṭim mustaqīm","at":"Dan Allah menyeru (manusia) ke Darus-salam (surga), dan memberikan petunjuk kepada orang yang Dia kehendaki ke jalan yang lurus (Islam)."},{"id":"26","ar":"۞ لِلَّذِيْنَ اَحْسَنُوا الْحُسْنٰى وَزِيَادَةٌ ۗوَلَا يَرْهَقُ وُجُوْهَهُمْ قَتَرٌ وَّلَا ذِلَّةٌ ۗاُولٰۤىِٕكَ اَصْحٰبُ الْجَنَّةِ هُمْ فِيْهَا خٰلِدُوْنَ ","bc":"lillażīna aḥsanul-ḥusnā wa ziyādah, wa lā yar-haqu wujụhahum qataruw wa lā żillah, ulā`ika aṣ-ḥābul-jannati hum fīhā khālidụn","at":"Bagi orang-orang yang berbuat baik, ada pahala yang terbaik (surga) dan tambahannya (kenikmatan melihat Allah). Dan wajah mereka tidak ditutupi debu hitam dan tidak (pula) dalam kehinaan. Mereka itulah penghuni surga, mereka kekal di dalamnya."},{"id":"27","ar":"وَالَّذِيْنَ كَسَبُوا السَّيِّاٰتِ جَزَاۤءُ سَيِّئَةٍ ۢبِمِثْلِهَاۙ وَتَرْهَقُهُمْ ذِلَّةٌ ۗمَا لَهُمْ مِّنَ اللّٰهِ مِنْ عَاصِمٍۚ كَاَنَّمَآ اُغْشِيَتْ وُجُوْهُهُمْ قِطَعًا مِّنَ الَّيْلِ مُظْلِمًاۗ اُولٰۤىِٕكَ اَصْحٰبُ النَّارِ ۚهُمْ فِيْهَا خٰلِدُوْنَ ","bc":"wallażīna kasabus-sayyi`āti jazā`u sayyi`atim bimiṡlihā wa tar-haquhum żillah, mā lahum minallāhi min 'āṣim, ka`annamā ugsyiyat wujụhuhum qiṭa'am minal-laili muẓlimā, ulā`ika aṣ-ḥābun-nār, hum fīhā khālidụn","at":"Adapun orang-orang yang berbuat kejahatan (akan mendapat) balasan kejahatan yang setimpal dan mereka diselubungi kehinaan. Tidak ada bagi mereka seorang pelindung pun dari (azab) Allah, seakan-akan wajah mereka ditutupi dengan kepingan-kepingan malam yang gelap gulita. Mereka itulah penghuni neraka, mereka kekal di dalamnya. "},{"id":"28","ar":"وَيَوْمَ نَحْشُرُهُمْ جَمِيْعًا ثُمَّ نَقُوْلُ لِلَّذِيْنَ اَشْرَكُوْا مَكَانَكُمْ اَنْتُمْ وَشُرَكَاۤؤُكُمْۚ فَزَيَّلْنَا بَيْنَهُمْ وَقَالَ شُرَكَاۤؤُهُمْ مَّا كُنْتُمْ اِيَّانَا تَعْبُدُوْنَ","bc":"wa yauma naḥsyuruhum jamī'an ṡumma naqụlu lillażīna asyrakụ makānakum antum wa syurakā`ukum, fa zayyalnā bainahum wa qāla syurakā`uhum mā kuntum iyyānā ta'budụn","at":"Dan (ingatlah) pada hari (ketika) itu Kami mengumpulkan mereka semuanya, kemudian Kami berkata kepada orang yang mempersekutukan (Allah), “Tetaplah di tempatmu, kamu dan para sekutumu.” Lalu Kami pisahkan mereka dan berkatalah sekutu-sekutu mereka, “Kamu sekali-kali tidak pernah menyembah kami. "},{"id":"29","ar":"فَكَفٰى بِاللّٰهِ شَهِيْدًاۢ بَيْنَنَا وَبَيْنَكُمْ اِنْ كُنَّا عَنْ عِبَادَتِكُمْ لَغٰفِلِيْنَ ","bc":"fa kafā billāhi syahīdam bainanā wa bainakum ing kunnā 'an 'ibādatikum lagāfilīn","at":"Maka cukuplah Allah menjadi saksi antara kami dengan kamu, sebab kami tidak tahu-menahu tentang penyembahan kamu (kepada kami).”"},{"id":"30","ar":"هُنَالِكَ تَبْلُوْا كُلُّ نَفْسٍ مَّآ اَسْلَفَتْ وَرُدُّوْٓا اِلَى اللّٰهِ مَوْلٰىهُمُ الْحَقِّ وَضَلَّ عَنْهُمْ مَّا كَانُوْا يَفْتَرُوْنَ ","bc":"hunālika tablụ kullu nafsim mā aslafat wa ruddū ilallāhi maulāhumul-ḥaqqi wa ḍalla 'an-hum mā kānụ yaftarụn","at":"Di tempat itu (padang Mahsyar), setiap jiwa merasakan pembalasan dari apa yang telah dikerjakannya (dahulu) dan mereka dikembalikan kepada Allah, pelindung mereka yang sebenarnya, dan lenyaplah dari mereka apa (pelindung palsu) yang mereka ada-adakan."},{"id":"31","ar":"قُلْ مَنْ يَّرْزُقُكُمْ مِّنَ السَّمَاۤءِ وَالْاَرْضِ اَمَّنْ يَّمْلِكُ السَّمْعَ وَالْاَبْصَارَ وَمَنْ يُّخْرِجُ الْحَيَّ مِنَ الْمَيِّتِ وَيُخْرِجُ الْمَيِّتَ مِنَ الْحَيِّ وَمَنْ يُّدَبِّرُ الْاَمْرَۗ فَسَيَقُوْلُوْنَ اللّٰهُ ۚفَقُلْ اَفَلَا تَتَّقُوْنَ ","bc":"qul may yarzuqukum minas-samā`i wal-arḍi am may yamlikus-sam'a wal-abṣāra wa may yukhrijul-ḥayya minal-mayyiti wa yukhrijul-mayyita minal-ḥayyi wa may yudabbirul-amr, fa sayaqụlụnallāh, fa qul a fa lā tattaqụn","at":"Katakanlah (Muhammad), “Siapakah yang memberi rezeki kepadamu dari langit dan bumi, atau siapakah yang kuasa (menciptakan) pendengaran dan penglihatan, dan siapakah yang mengeluarkan yang hidup dari yang mati dan mengeluarkan yang mati dari yang hidup, dan siapakah yang mengatur segala urusan?” Maka mereka akan menjawab, “Allah.” Maka katakanlah, “Mengapa kamu tidak bertakwa (kepada-Nya)?”"},{"id":"32","ar":"فَذٰلِكُمُ اللّٰهُ رَبُّكُمُ الْحَقُّۚ فَمَاذَا بَعْدَ الْحَقِّ اِلَّا الضَّلٰلُ ۖفَاَنّٰى تُصْرَفُوْنَ ","bc":"fa żālikumullāhu rabbukumul-ḥaqq, fa māżā ba'dal-ḥaqqi illaḍ-ḍalālu fa annā tuṣrafụn","at":"Maka itulah Allah, Tuhan kamu yang sebenarnya; maka tidak ada setelah kebenaran itu melainkan kesesatan. Maka mengapa kamu berpaling (dari kebenaran)?"},{"id":"33","ar":"كَذٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى الَّذِيْنَ فَسَقُوْٓا اَنَّهُمْ لَا يُؤْمِنُوْنَ ","bc":"każālika ḥaqqat kalimatu rabbika 'alallażīna fasaqū annahum lā yu`minụn","at":"Demikianlah telah tetap (hukuman) Tuhanmu terhadap orang-orang yang fasik, karena sesungguhnya mereka tidak beriman."},{"id":"34","ar":"قُلْ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗۗ قُلِ اللّٰهُ يَبْدَؤُا الْخَلْقَ ثُمَّ يُعِيْدُهٗ فَاَنّٰى تُؤْفَكُوْنَ ","bc":"qul hal min syurakā`ikum may yabda`ul-khalqa ṡumma yu'īduh, qulillāhu yabda`ul-khalqa ṡumma yu'īduhụ fa annā tu`fakụn","at":"Katakanlah, “Adakah di antara sekutumu yang dapat memulai penciptaan (makhluk), kemudian mengulanginya (menghidupkannya) kembali?” Katakanlah, “Allah memulai (penciptaan) makhluk, kemudian mengulanginya. Maka bagaimana kamu dipalingkan (menyembah selain Allah)?”"},{"id":"35","ar":"قُلْ هَلْ مِنْ شُرَكَاۤىِٕكُمْ مَّنْ يَّهْدِيْٓ اِلَى الْحَقِّۗ قُلِ اللّٰهُ يَهْدِيْ لِلْحَقِّۗ اَفَمَنْ يَّهْدِيْٓ اِلَى الْحَقِّ اَحَقُّ اَنْ يُّتَّبَعَ اَمَّنْ لَّا يَهِدِّيْٓ اِلَّآ اَنْ يُّهْدٰىۚ فَمَا لَكُمْۗ كَيْفَ تَحْكُمُوْنَ ","bc":"qul hal min syurakā`ikum may yahdī ilal-ḥaqq, qulillāhu yahdī lil-ḥaqq, a fa may yahdī ilal-ḥaqqi aḥaqqu ay yuttaba'a am mal lā yahiddī illā ay yuhdā, fa mā lakum, kaifa taḥkumụn","at":"Katakanlah, “Apakah di antara sekutumu ada yang membimbing kepada kebenaran?” Katakanlah, “Allah-lah yang membimbing kepada kebenaran.” Maka manakah yang lebih berhak diikuti, Tuhan yang membimbing kepada kebenaran itu, ataukah orang yang tidak mampu membimbing bahkan perlu dibimbing? Maka mengapa kamu (berbuat demikian)? Bagaimanakah kamu mengambil keputusan?"},{"id":"36","ar":"وَمَا يَتَّبِعُ اَكْثَرُهُمْ اِلَّا ظَنًّاۗ اِنَّ الظَّنَّ لَا يُغْنِيْ مِنَ الْحَقِّ شَيْـًٔاۗ اِنَّ اللّٰهَ عَلِيْمٌ ۢبِمَا يَفْعَلُوْنَ ","bc":"wa mā yattabi'u akṡaruhum illā ẓannā, innaẓ-ẓanna lā yugnī minal-ḥaqqi syai`ā, innallāha 'alīmum bimā yaf'alụn","at":"Dan kebanyakan mereka hanya mengikuti dugaan. Sesungguhnya dugaan itu tidak sedikit pun berguna untuk melawan kebenaran. Sungguh, Allah Maha Mengetahui apa yang mereka kerjakan."},{"id":"37","ar":"وَمَا كَانَ هٰذَا الْقُرْاٰنُ اَنْ يُّفْتَرٰى مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ تَصْدِيْقَ الَّذِيْ بَيْنَ يَدَيْهِ وَتَفْصِيْلَ الْكِتٰبِ لَا رَيْبَ فِيْهِ مِنْ رَّبِّ الْعٰلَمِيْنَۗ ","bc":"wa mā kāna hāżal-qur`ānu ay yuftarā min dụnillāhi wa lākin taṣdīqallażī baina yadaihi wa tafṣīlal-kitābi lā raiba fīhi mir rabbil-'ālamīn","at":"Dan tidak mungkin Al-Qur'an ini dibuat-buat oleh selain Allah; tetapi (Al-Qur'an) membenarkan (kitab-kitab) yang sebelumnya dan menjelaskan hukum-hukum yang telah ditetapkannya, tidak ada keraguan di dalamnya, (diturunkan) dari Tuhan seluruh alam."},{"id":"38","ar":"اَمْ يَقُوْلُوْنَ افْتَرٰىهُ ۗ قُلْ فَأْتُوْا بِسُوْرَةٍ مِّثْلِهٖ وَادْعُوْا مَنِ اسْتَطَعْتُمْ مِّنْ دُوْنِ اللّٰهِ اِنْ كُنْتُمْ صٰدِقِيْنَ","bc":"am yaqụlụnaftarāh, qul fa`tụ bisụratim miṡlihī wad'ụ manistaṭa'tum min dụnillāhi ing kuntum ṣādiqīn","at":"Apakah pantas mereka mengatakan dia (Muhammad) yang telah membuat-buatnya? Katakanlah, “Buatlah sebuah surah yang semisal dengan surah (Al-Qur'an), dan ajaklah siapa saja di antara kamu orang yang mampu (membuatnya) selain Allah, jika kamu orang-orang yang benar.”"},{"id":"39","ar":"بَلْ كَذَّبُوْا بِمَا لَمْ يُحِيْطُوْا بِعِلْمِهٖ وَلَمَّا يَأْتِهِمْ تَأْوِيْلُهٗۗ كَذٰلِكَ كَذَّبَ الَّذِيْنَ مِنْ قَبْلِهِمْ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الظّٰلِمِيْنَ ","bc":"bal każżabụ bimā lam yuḥīṭụ bi'ilmihī wa lammā ya`tihim ta`wīluh, każālika każżaballażīna ming qablihim fanẓur kaifa kāna 'āqibatuẓ-ẓālimīn","at":"Bahkan (yang sebenarnya), mereka mendustakan apa yang mereka belum mengetahuinya dengan sempurna dan belum mereka peroleh penjelasannya. Demikianlah halnya umat-umat yang ada sebelum mereka telah mendustakan (rasul). Maka perhatikanlah bagaimana akibat orang yang zalim. "},{"id":"40","ar":"وَمِنْهُمْ مَّنْ يُّؤْمِنُ بِهٖ وَمِنْهُمْ مَّنْ لَّا يُؤْمِنُ بِهٖۗ وَرَبُّكَ اَعْلَمُ بِالْمُفْسِدِيْنَ ","bc":"wa min-hum may yu`minu bihī wa min-hum mal lā yu`minu bih, wa rabbuka a'lamu bil-mufsidīn","at":"Dan di antara mereka ada orang-orang yang beriman kepadanya (Al-Qur'an), dan di antaranya ada (pula) orang-orang yang tidak beriman kepadanya. Sedangkan Tuhanmu lebih mengetahui tentang orang-orang yang berbuat kerusakan."},{"id":"41","ar":"وَاِنْ كَذَّبُوْكَ فَقُلْ لِّيْ عَمَلِيْ وَلَكُمْ عَمَلُكُمْۚ اَنْتُمْ بَرِيْۤـُٔوْنَ مِمَّآ اَعْمَلُ وَاَنَا۠ بَرِيْۤءٌ مِّمَّا تَعْمَلُوْنَ ","bc":"wa ing każżabụka fa qul lī 'amalī wa lakum 'amalukum, antum barī`ụna mimmā a'malu wa ana barī`um mimmā ta'malụn","at":"Dan jika mereka (tetap) mendustakanmu (Muhammad), maka katakanlah, “Bagiku pekerjaanku dan bagimu pekerjaanmu. Kamu tidak bertanggung jawab terhadap apa yang aku kerjakan dan aku pun tidak bertanggung jawab terhadap apa yang kamu kerjakan.”"},{"id":"42","ar":"وَمِنْهُمْ مَّنْ يَّسْتَمِعُوْنَ اِلَيْكَۗ اَفَاَنْتَ تُسْمِعُ الصُّمَّ وَلَوْ كَانُوْا لَا يَعْقِلُوْنَ ","bc":"wa min-hum may yastami'ụna ilaīk, a fa anta tusmi'uṣ-ṣumma walau kānụ lā ya'qilụn","at":"Dan di antara mereka ada yang mendengarkan engkau (Muhammad). Tetapi apakah engkau dapat menjadikan orang yang tuli itu mendengar walaupun mereka tidak mengerti?"},{"id":"43","ar":"وَمِنْهُمْ مَّنْ يَّنْظُرُ اِلَيْكَۗ اَفَاَنْتَ تَهْدِى الْعُمْيَ وَلَوْ كَانُوْا لَا يُبْصِرُوْنَ ","bc":"wa min-hum may yanẓuru ilaīk, a fa anta tahdil-'umya walau kānụ lā yubṣirụn","at":"Dan di antara mereka ada yang melihat kepada engkau. Tetapi apakah engkau dapat memberi petunjuk kepada orang yang buta, walaupun mereka tidak memperhatikan?"},{"id":"44","ar":"اِنَّ اللّٰهَ لَا يَظْلِمُ النَّاسَ شَيْـًٔا وَّلٰكِنَّ النَّاسَ اَنْفُسَهُمْ يَظْلِمُوْنَ","bc":"innallāha lā yaẓlimun-nāsa syai`aw wa lākinnan-nāsa anfusahum yaẓlimụn","at":"Sesungguhnya Allah tidak menzalimi manusia sedikit pun, tetapi manusia itulah yang menzalimi dirinya sendiri."},{"id":"45","ar":"وَيَوْمَ يَحْشُرُهُمْ كَاَنْ لَّمْ يَلْبَثُوْٓا اِلَّا سَاعَةً مِّنَ النَّهَارِ يَتَعَارَفُوْنَ بَيْنَهُمْۗ قَدْ خَسِرَ الَّذِيْنَ كَذَّبُوْا بِلِقَاۤءِ اللّٰهِ وَمَا كَانُوْا مُهْتَدِيْنَ ","bc":"wa yauma yaḥsyuruhum ka`al lam yalbaṡū illā sā'atam minan-nahāri yata'ārafụna bainahum, qad khasirallażīna każżabụ biliqā`illāhi wa mā kānụ muhtadīn","at":"Dan (ingatlah) pada hari (ketika) Allah mengumpulkan mereka, (mereka merasa) seakan-akan tidak pernah berdiam (di dunia) kecuali sesaat saja pada siang hari, (pada waktu) mereka saling berkenalan. Sungguh rugi orang yang mendustakan pertemuan mereka dengan Allah dan mereka tidak mendapat petunjuk."},{"id":"46","ar":"وَاِمَّا نُرِيَنَّكَ بَعْضَ الَّذِيْ نَعِدُهُمْ اَوْ نَتَوَفَّيَنَّكَ فَاِلَيْنَا مَرْجِعُهُمْ ثُمَّ اللّٰهُ شَهِيْدٌ عَلٰى مَا يَفْعَلُوْنَ ","bc":"wa immā nuriyannaka ba'ḍallażī na'iduhum au natawaffayannaka fa ilainā marji'uhum ṡummallāhu syahīdun 'alā mā yaf'alụn","at":"Dan jika Kami perlihatkan kepadamu (Muhammad) sebagian dari (siksaan) yang Kami janjikan kepada mereka, (tentulah engkau akan melihatnya) atau (jika) Kami wafatkan engkau (sebelum itu), maka kepada Kami (jualah) mereka kembali, dan Allah menjadi saksi atas apa yang mereka kerjakan."},{"id":"47","ar":"وَلِكُلِّ اُمَّةٍ رَّسُوْلٌ ۚفَاِذَا جَاۤءَ رَسُوْلُهُمْ قُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُوْنَ ","bc":"wa likulli ummatir rasụl, fa iżā jā`a rasụluhum quḍiya bainahum bil-qisṭi wa hum lā yuẓlamụn","at":"Dan setiap umat (mempunyai) rasul. Maka apabila rasul mereka telah datang, diberlakukanlah hukum bagi mereka dengan adil dan (sedikit pun) tidak dizalimi."},{"id":"48","ar":"وَيَقُوْلُوْنَ مَتٰى هٰذَا الْوَعْدُ اِنْ كُنْتُمْ صٰدِقِيْنَ ","bc":"wa yaqụlụna matā hāżal-wa'du ing kuntum ṣādiqīn","at":"Dan mereka mengatakan, ”Bilakah (datangnya) ancaman itu, jika kamu orang-orang yang benar?”"},{"id":"49","ar":"قُلْ لَّآ اَمْلِكُ لِنَفْسِيْ ضَرًّا وَّلَا نَفْعًا اِلَّا مَا شَاۤءَ اللّٰهُ ۗ لِكُلِّ اُمَّةٍ اَجَلٌ ۚاِذَا جَاۤءَ اَجَلُهُمْ فَلَا يَسْتَأْخِرُوْنَ سَاعَةً وَّلَا يَسْتَقْدِمُوْنَ ","bc":"qul lā amliku linafsī ḍarraw wa lā naf'an illā mā syā`allāh, likulli ummatin ajal, iżā jā`a ajaluhum fa lā yasta`khirụna sā'ataw wa lā yastaqdimụn","at":"Katakanlah (Muhammad), “Aku tidak kuasa menolak mudarat maupun mendatangkan manfaat kepada diriku, kecuali apa yang Allah kehendaki.” Bagi setiap umat mempunyai ajal (batas waktu). Apabila ajalnya tiba, mereka tidak dapat meminta penundaan atau percepatan sesaat pun. "},{"id":"50","ar":"قُلْ اَرَءَيْتُمْ اِنْ اَتٰىكُمْ عَذَابُهٗ بَيَاتًا اَوْ نَهَارًا مَّاذَا يَسْتَعْجِلُ مِنْهُ الْمُجْرِمُوْنَ","bc":"qul a ra`aitum in atākum 'ażābuhụ bayātan au nahāram māżā yasta'jilu min-hul-mujrimụn","at":"Katakanlah, “Terangkanlah kepadaku, jika datang kepada kamu siksaan-Nya pada waktu malam atau siang hari, manakah yang diminta untuk disegerakan orang-orang yang berdosa itu?”"},{"id":"51","ar":"اَثُمَّ اِذَا مَا وَقَعَ اٰمَنْتُمْ بِهٖۗ اٰۤلْـٰٔنَ وَقَدْ كُنْتُمْ بِهٖ تَسْتَعْجِلُوْنَ ","bc":"a ṡumma iżā mā waqa'a āmantum bih, āl-āna waqad kuntum bihī tasta'jilụn","at":"Kemudian apakah setelah azab itu terjadi, kamu baru mempercayainya? Apakah (baru) sekarang, padahal sebelumnya kamu selalu meminta agar disegerakan?"},{"id":"52","ar":"ثُمَّ قِيْلَ لِلَّذِيْنَ ظَلَمُوْا ذُوْقُوْا عَذَابَ الْخُلْدِۚ هَلْ تُجْزَوْنَ اِلَّا بِمَا كُنْتُمْ تَكْسِبُوْنَ ","bc":"ṡumma qīla lillażīna ẓalamụ żụqụ 'ażābal-khuld, hal tujzauna illā bimā kuntum taksibụn","at":"Kemudian dikatakan kepada orang-orang yang zalim itu, “Rasakanlah olehmu siksaan yang kekal. Kamu tidak diberi balasan, melainkan (sesuai) dengan apa yang telah kamu lakukan.”"},{"id":"53","ar":"وَيَسْتَنْۢبِـُٔوْنَكَ اَحَقٌّ هُوَ ۗ قُلْ اِيْ وَرَبِّيْٓ اِنَّهٗ لَحَقٌّ ۗوَمَآ اَنْتُمْ بِمُعْجِزِيْنَ ","bc":"wa yastambi`ụnaka aḥaqqun huw, qul ī wa rabbī innahụ laḥaqq, wa mā antum bimu'jizīn","at":"Dan mereka menanyakan kepadamu (Muhammad), “Benarkah (azab yang dijanjikan) itu?” Katakanlah, “Ya, demi Tuhanku, sesungguhnya (azab) itu pasti benar dan kamu sekali-kali tidak dapat menghindar.”"},{"id":"54","ar":"وَلَوْ اَنَّ لِكُلِّ نَفْسٍ ظَلَمَتْ مَا فِى الْاَرْضِ لَافْتَدَتْ بِهٖۗ وَاَسَرُّوا النَّدَامَةَ لَمَّا رَاَوُا الْعَذَابَۚ وَقُضِيَ بَيْنَهُمْ بِالْقِسْطِ وَهُمْ لَا يُظْلَمُوْنَ ","bc":"walau anna likulli nafsin ẓalamat mā fil-arḍi laftadat bih, wa asarrun-nadāmata lammā ra`awul-'ażāb, wa quḍiya bainahum bil-qisṭi wa hum lā yuẓlamụn","at":"Dan kalau setiap orang yang zalim itu (mempunyai) segala yang ada di bumi, tentu dia menebus dirinya dengan itu, dan mereka menyembunyikan penyesalannya ketika mereka telah menyaksikan azab itu. Kemudian diberi keputusan di antara mereka dengan adil, dan mereka tidak dizalimi."},{"id":"55","ar":"اَلَآ اِنَّ لِلّٰهِ مَا فِى السَّمٰوٰتِ وَالْاَرْضِۗ اَلَآ اِنَّ وَعْدَ اللّٰهِ حَقٌّ وَّلٰكِنَّ اَكْثَرَهُمْ لَا يَعْلَمُوْنَ","bc":"alā inna lillāhi mā fis-samāwāti wal-arḍ, alā inna wa'dallāhi ḥaqquw wa lākinna akṡarahum lā ya'lamụn","at":"Ketahuilah sesungguhnya milik Allah-lah apa yang ada di langit dan di bumi. Bukankah janji Allah itu benar? Tetapi kebanyakan mereka tidak mengetahui."},{"id":"56","ar":"هُوَ يُحْيٖ وَيُمِيْتُ وَاِلَيْهِ تُرْجَعُوْنَ ","bc":"huwa yuḥyī wa yumītu wa ilaihi turja'ụn","at":"Dialah yang menghidupkan dan mematikan dan hanya kepada-Nyalah kamu dikembalikan."},{"id":"57","ar":"يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَتْكُمْ مَّوْعِظَةٌ مِّنْ رَّبِّكُمْ وَشِفَاۤءٌ لِّمَا فِى الصُّدُوْرِۙ وَهُدًى وَّرَحْمَةٌ لِّلْمُؤْمِنِيْنَ ","bc":"yā ayyuhan-nāsu qad jā`atkum mau'iẓatum mir rabbikum wa syifā`ul limā fiṣ-ṣudụri wa hudaw wa raḥmatul lil-mu`minīn","at":"Wahai manusia! Sungguh, telah datang kepadamu pelajaran (Al-Qur'an) dari Tuhanmu, penyembuh bagi penyakit yang ada dalam dada dan petunjuk serta rahmat bagi orang yang beriman."},{"id":"58","ar":"قُلْ بِفَضْلِ اللّٰهِ وَبِرَحْمَتِهٖ فَبِذٰلِكَ فَلْيَفْرَحُوْاۗ هُوَ خَيْرٌ مِّمَّا يَجْمَعُوْنَ","bc":"qul bifaḍlillāhi wa biraḥmatihī fa biżālika falyafraḥụ, huwa khairum mimmā yajma'ụn","at":"Katakanlah (Muhammad), “Dengan karunia Allah dan rahmat-Nya, hendaklah dengan itu mereka bergembira. Itu lebih baik daripada apa yang mereka kumpulkan.”"},{"id":"59","ar":"قُلْ اَرَءَيْتُمْ مَّآ اَنْزَلَ اللّٰهُ لَكُمْ مِّنْ رِّزْقٍ فَجَعَلْتُمْ مِّنْهُ حَرَامًا وَّحَلٰلًا ۗ قُلْ اٰۤللّٰهُ اَذِنَ لَكُمْ اَمْ عَلَى اللّٰهِ تَفْتَرُوْنَ","bc":"qul a ra`aitum mā anzalallāhu lakum mir rizqin fa ja'altum min-hu ḥarāmaw wa halālā, qul āllāhu ażina lakum am 'alallāhi taftarụn","at":"Katakanlah (Muhammad), “Terangkanlah kepadaku tentang rezeki yang diturunkan Allah kepadamu, lalu kamu jadikan sebagiannya haram dan sebagiannya halal.” Katakanlah, “Apakah Allah telah memberikan izin kepadamu (ten-tang ini) ataukah kamu mengada-ada atas nama Allah?”"},{"id":"60","ar":"وَمَا ظَنُّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ يَوْمَ الْقِيٰمَةِ ۗاِنَّ اللّٰهَ لَذُوْ فَضْلٍ عَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَهُمْ لَا يَشْكُرُوْنَ ","bc":"wa mā ẓannullażīna yaftarụna 'alallāhil-każiba yaumal-qiyāmah, innallāha lażụ faḍlin 'alan-nāsi wa lākinna akṡarahum lā yasykurụn","at":"Dan apakah dugaan orang-orang yang mengada-adakan kebohongan terhadap Allah pada hari Kiamat? Sesungguhnya Allah benar-benar mempunyai karunia (yang dilimpahkan) kepada manusia, tetapi kebanyakan mereka tidak bersyukur."},{"id":"61","ar":"وَمَا تَكُوْنُ فِيْ شَأْنٍ وَّمَا تَتْلُوْا مِنْهُ مِنْ قُرْاٰنٍ وَّلَا تَعْمَلُوْنَ مِنْ عَمَلٍ اِلَّا كُنَّا عَلَيْكُمْ شُهُوْدًا اِذْ تُفِيْضُوْنَ فِيْهِۗ وَمَا يَعْزُبُ عَنْ رَّبِّكَ مِنْ مِّثْقَالِ ذَرَّةٍ فِى الْاَرْضِ وَلَا فِى السَّمَاۤءِ وَلَآ اَصْغَرَ مِنْ ذٰلِكَ وَلَآ اَكْبَرَ اِلَّا فِيْ كِتٰبٍ مُّبِيْنٍ ","bc":"wa mā takụnu fī sya`niw wa mā tatlụ min-hu ming qur`āniw wa lā ta'malụna min 'amalin illā kunnā 'alaikum syuhụdan iż tufīḍụna fīh, wa mā ya'zubu 'ar rabbika mim miṡqāli żarratin fil-arḍi wa lā fis-samā`i wa lā aṣgara min żālika wa lā akbara illā fī kitābim mubīn","at":"Dan tidakkah engkau (Muhammad) berada dalam suatu urusan, dan tidak membaca suatu ayat Al-Qur'an serta tidak pula kamu melakukan suatu pekerjaan, melainkan Kami menjadi saksi atasmu ketika kamu melakukannya. Tidak lengah sedikit pun dari pengetahuan Tuhanmu biarpun sebesar zarrah, baik di bumi ataupun di langit. Tidak ada sesuatu yang lebih kecil dan yang lebih besar daripada itu, melainkan semua tercatat dalam Kitab yang nyata (Lauh Mahfuzh)."},{"id":"62","ar":"اَلَآ اِنَّ اَوْلِيَاۤءَ اللّٰهِ لَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُوْنَۚ ","bc":"alā inna auliyā`allāhi lā khaufun 'alaihim wa lā hum yaḥzanụn","at":"Ingatlah wali-wali Allah itu, tidak ada rasa takut pada mereka dan mereka tidak bersedih hati."},{"id":"63","ar":"اَلَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَۗ ","bc":"allażīna āmanụ wa kānụ yattaqụn","at":"(Yaitu) orang-orang yang beriman dan senantiasa bertakwa."},{"id":"64","ar":"لَهُمُ الْبُشْرٰى فِى الْحَيٰوةِ الدُّنْيَا وَفِى الْاٰخِرَةِۗ لَا تَبْدِيْلَ لِكَلِمٰتِ اللّٰهِ ۗذٰلِكَ هُوَ الْفَوْزُ الْعَظِيْمُۗ ","bc":"lahumul-busyrā fil-ḥayātid-dun-yā wa fil-ākhirah, lā tabdīla likalimātillāh, żālika huwal-fauzul-'aẓīm","at":"Bagi mereka berita gembira di dalam kehidupan di dunia dan di akhirat. Tidak ada perubahan bagi janji-janji Allah. Demikian itulah kemenangan yang agung."},{"id":"65","ar":"وَلَا يَحْزُنْكَ قَوْلُهُمْۘ اِنَّ الْعِزَّةَ لِلّٰهِ جَمِيْعًاۗ هُوَ السَّمِيْعُ الْعَلِيْمُ","bc":"wa lā yaḥzungka qauluhum, innal-'izzata lillāhi jamī'ā, huwas-samī'ul-'alīm","at":" Dan janganlah engkau (Muhammad) sedih oleh perkataan mereka. Sungguh, kekuasaan itu seluruhnya milik Allah. Dia Maha Mendengar, Maha Mengetahui."},{"id":"66","ar":"اَلَآ اِنَّ لِلّٰهِ مَنْ فِى السَّمٰوٰتِ وَمَنْ فِى الْاَرْضِۗ وَمَا يَتَّبِعُ الَّذِيْنَ يَدْعُوْنَ مِنْ دُوْنِ اللّٰهِ شُرَكَاۤءَ ۗاِنْ يَّتَّبِعُوْنَ اِلَّا الظَّنَّ وَاِنْ هُمْ اِلَّا يَخْرُصُوْنَ ","bc":"alā inna lillāhi man fis-samāwāti wa man fil-arḍ, wa mā yattabi'ullażīna yad'ụna min dụnillāhi syurakā`, iy yattabi'ụna illaẓ-ẓanna wa in hum illā yakhruṣụn","at":"Ingatlah, milik Allah meliputi siapa yang ada di langit dan siapa yang ada di bumi. Dan orang-orang yang menyeru sekutu-sekutu selain Allah, tidaklah mengikuti (suatu keyakinan). Mereka hanya mengikuti persangkaan belaka, dan mereka hanyalah menduga-duga."},{"id":"67","ar":"هُوَ الَّذِيْ جَعَلَ لَكُمُ الَّيْلَ لِتَسْكُنُوْا فِيْهِ وَالنَّهَارَ مُبْصِرًا ۗاِنَّ فِيْ ذٰلِكَ لَاٰيٰتٍ لِّقَوْمٍ يَّسْمَعُوْنَ ","bc":"huwallażī ja'ala lakumul-laila litaskunụ fīhi wan-nahāra mubṣirā, inna fī żālika la`āyātil liqaumiy yasma'ụn","at":"Dialah yang menjadikan malam bagimu agar kamu beristirahat padanya dan menjadikan siang terang benderang. Sungguh, yang demikian itu terdapat tanda-tanda (kekuasaan Allah) bagi orang-orang yang mendengar."},{"id":"68","ar":"قَالُوا اتَّخَذَ اللّٰهُ وَلَدًا سُبْحٰنَهٗ ۗ هُوَ الْغَنِيُّ ۗ لَهٗ مَا فِى السَّمٰوٰتِ وَمَا فِى الْاَرْضِۗ اِنْ عِنْدَكُمْ مِّنْ سُلْطٰنٍۢ بِهٰذَاۗ اَتَقُوْلُوْنَ عَلَى اللّٰهِ مَا لَا تَعْلَمُوْنَ ","bc":"qāluttakhażallāhu waladan sub-ḥānah, huwal-ganiyy, lahụ mā fis-samāwāti wa mā fil-arḍ, in 'indakum min sulṭānim bihāżā, a taqụlụna 'alallāhi mā lā ta'lamụn","at":"Mereka (orang-orang Yahudi dan Nasrani) berkata, “Allah mempunyai anak.” Mahasuci Dia, Dialah Yang Mahakaya; milik-Nyalah apa yang ada di langit dan apa yang ada di bumi. Kamu tidak mempunyai alasan kuat tentang ini. Pantaskah kamu mengatakan tentang Allah apa yang kamu tidak ketahui?"},{"id":"69","ar":"قُلْ اِنَّ الَّذِيْنَ يَفْتَرُوْنَ عَلَى اللّٰهِ الْكَذِبَ لَا يُفْلِحُوْنَۗ ","bc":"qul innallażīna yaftarụna 'alallāhil-każiba lā yufliḥụn","at":"Katakanlah, “Sesungguhnya orang-orang yang mengada-adakan kebohongan terhadap Allah tidak akan beruntung.”"},{"id":"70","ar":"مَتَاعٌ فِى الدُّنْيَا ثُمَّ اِلَيْنَا مَرْجِعُهُمْ ثُمَّ نُذِيْقُهُمُ الْعَذَابَ الشَّدِيْدَ بِمَا كَانُوْا يَكْفُرُوْنَ ","bc":"matā'un fid-dun-yā ṡumma ilainā marji'uhum ṡumma nużīquhumul-'ażābasy-syadīda bimā kānụ yakfurụn","at":"(Bagi mereka) kesenangan (sesaat) ketika di dunia, selanjutnya kepada Kamilah mereka kembali, kemudian Kami rasakan kepada mereka azab yang berat, karena kekafiran mereka."},{"id":"71","ar":"۞ وَاتْلُ عَلَيْهِمْ نَبَاَ نُوْحٍۘ اِذْ قَالَ لِقَوْمِهٖ يٰقَوْمِ اِنْ كَانَ كَبُرَ عَلَيْكُمْ مَّقَامِيْ وَتَذْكِيْرِيْ بِاٰيٰتِ اللّٰهِ فَعَلَى اللّٰهِ تَوَكَّلْتُ فَاَجْمِعُوْٓا اَمْرَكُمْ وَشُرَكَاۤءَكُمْ ثُمَّ لَا يَكُنْ اَمْرُكُمْ عَلَيْكُمْ غُمَّةً ثُمَّ اقْضُوْٓا اِلَيَّ وَلَا تُنْظِرُوْنِ ","bc":"watlu 'alaihim naba`a nụḥ, iż qāla liqaumihī yā qaumi ing kāna kabura 'alaikum maqāmī wa tażkīrī bi`āyātillāhi fa 'alallāhi tawakkaltu fa ajmi'ū amrakum wa syurakā`akum ṡumma lā yakun amrukum 'alaikum gummatan ṡummaqḍū ilayya wa lā tunẓirụn","at":"Dan bacakanlah kepada mereka berita penting (tentang) Nuh ketika (dia) berkata kepada kaumnya, “Wahai kaumku! Jika terasa berat bagimu aku tinggal (bersamamu) dan peringatanku dengan ayat-ayat Allah, maka kepada Allah aku bertawakal. Karena itu bulatkanlah keputusanmu dan kumpulkanlah sekutu-sekutumu (untuk membinasakanku), dan janganlah keputusanmu itu dirahasiakan. Kemudian bertindaklah terhadap diriku, dan janganlah kamu tunda lagi."},{"id":"72","ar":"فَاِنْ تَوَلَّيْتُمْ فَمَا سَاَلْتُكُمْ مِّنْ اَجْرٍۗ اِنْ اَجْرِيَ اِلَّا عَلَى اللّٰهِ ۙوَاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُسْلِمِيْنَ","bc":"fa in tawallaitum fa mā sa`altukum min ajr, in ajriya illā 'alallāhi wa umirtu an akụna minal-muslimīn","at":"Maka jika kamu berpaling (dari peringatanku), aku tidak meminta imbalan sedikit pun darimu. Imbalanku tidak lain hanyalah dari Allah, dan aku diperintah agar aku termasuk golongan orang-orang Muslim (berserah diri).”"},{"id":"73","ar":"فَكَذَّبُوْهُ فَنَجَّيْنٰهُ وَمَنْ مَّعَهٗ فِى الْفُلْكِ وَجَعَلْنٰهُمْ خَلٰۤىِٕفَ وَاَغْرَقْنَا الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِنَاۚ فَانْظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُنْذَرِيْنَ ","bc":"fa każżabụhu fa najjaināhu wa mam ma'ahụ fil-fulki wa ja'alnāhum khalā`ifa wa agraqnallażīna każżabụ bi`āyātinā, fanẓur kaifa kāna 'āqibatul-munżarīn","at":"Kemudian mereka mendustakannya (Nuh), lalu Kami selamatkan dia dan orang yang bersamanya di dalam kapal, dan Kami jadikan mereka itu khalifah dan Kami tenggelamkan orang yang mendustakan ayat-ayat Kami. Maka perhatikanlah bagaimana kesudahan orang-orang yang diberi peringatan itu."},{"id":"74","ar":"ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهٖ رُسُلًا اِلٰى قَوْمِهِمْ فَجَاۤءُوْهُمْ بِالْبَيِّنٰتِ فَمَا كَانُوْا لِيُؤْمِنُوْا بِمَا كَذَّبُوْا بِهٖ مِنْ قَبْلُ ۗ كَذٰلِكَ نَطْبَعُ عَلٰى قُلُوْبِ الْمُعْتَدِيْنَ ","bc":"ṡumma ba'aṡnā mim ba'dihī rusulan ilā qaumihim fa jā`ụhum bil-bayyināti fa mā kānụ liyu`minụ bimā każżabụ bihī ming qabl, każālika naṭba'u 'alā qulụbil-mu'tadīn","at":"Kemudian setelahnya (Nuh), Kami utus beberapa rasul kepada kaum mereka (masing-masing), maka rasul-rasul itu datang kepada mereka dengan membawa keterangan yang jelas, tetapi mereka tidak mau beriman karena mereka dahulu telah (biasa) mendustakannya. Demikianlah Kami mengunci hati orang-orang yang melampaui batas."},{"id":"75","ar":"ثُمَّ بَعَثْنَا مِنْۢ بَعْدِهِمْ مُّوْسٰى وَهٰرُوْنَ اِلٰى فِرْعَوْنَ وَمَلَا۟ىِٕهٖ بِاٰيٰتِنَا فَاسْتَكْبَرُوْا وَكَانُوْا قَوْمًا مُّجْرِمِيْنَ ","bc":"ṡumma ba'aṡnā mim ba'dihim mụsā wa hārụna ilā fir'auna wa mala`ihī bi`āyātinā fastakbarụ wa kānụ qaumam mujrimīn","at":"Kemudian setelah mereka, Kami utus Musa dan Harun kepada Fir‘aun dan para pemuka kaumnya, dengan membawa tanda-tanda (kekuasaan) Kami. Ternyata mereka menyombongkan diri dan mereka adalah orang-orang yang berdosa."},{"id":"76","ar":"فَلَمَّا جَاۤءَهُمُ الْحَقُّ مِنْ عِنْدِنَا قَالُوْٓا اِنَّ هٰذَا لَسِحْرٌ مُّبِيْنٌ ","bc":"fa lammā jā`ahumul-ḥaqqu min 'indinā qālū inna hāżā lasiḥrum mubīn","at":"Maka ketika telah datang kepada mereka kebenaran dari sisi Kami, mereka berkata, “Ini benar-benar sihir yang nyata.”"},{"id":"77","ar":"قَالَ مُوْسٰٓى اَتَقُوْلُوْنَ لِلْحَقِّ لَمَّا جَاۤءَكُمْ ۗ اَسِحْرٌ هٰذَاۗ وَلَا يُفْلِحُ السَّاحِرُوْنَ ","bc":"qāla mụsā a taqụlụna lil-ḥaqqi lammā jā`akum, a siḥrun hāżā, wa lā yufliḥus-sāḥirụn","at":"Musa berkata, “Pantaskah kamu mengatakan terhadap kebenaran ketika ia datang kepadamu, ‘sihirkah ini?’ Padahal para pesihir itu tidaklah mendapat kemenangan.”"},{"id":"78","ar":"قَالُوْٓا اَجِئْتَنَا لِتَلْفِتَنَا عَمَّا وَجَدْنَا عَلَيْهِ اٰبَاۤءَنَا وَتَكُوْنَ لَكُمَا الْكِبْرِيَاۤءُ فِى الْاَرْضِۗ وَمَا نَحْنُ لَكُمَا بِمُؤْمِنِيْنَ ","bc":"qālū a ji`tanā litalfitanā 'ammā wajadnā 'alaihi ābā`anā wa takụna lakumal-kibriyā`u fil-arḍ, wa mā naḥnu lakumā bimu`minīn","at":"Mereka berkata, “Apakah engkau datang kepada kami untuk memalingkan kami dari apa (kepercayaan) yang kami dapati nenek moyang kami mengerjakannya (menyembah berhala), dan agar kamu berdua mempunyai kekuasaan di bumi (negeri Mesir)? Kami tidak akan mempercayai kamu berdua.”"},{"id":"79","ar":"وَقَالَ فِرْعَوْنُ ائْتُوْنِيْ بِكُلِّ سٰحِرٍ عَلِيْمٍ ","bc":"wa qāla fir'aunu`tụnī bikulli sāḥirin 'alīm","at":"Dan Fir‘aun berkata (kepada pemuka kaumnya), “Datangkanlah kepadaku semua pesihir yang ulung!”"},{"id":"80","ar":"فَلَمَّا جَاۤءَ السَّحَرَةُ قَالَ لَهُمْ مُّوْسٰٓى اَلْقُوْا مَآ اَنْتُمْ مُّلْقُوْنَ ","bc":"fa lammā jā`as-saḥaratu qāla lahum mụsā alqụ mā antum mulqụn","at":"Maka ketika para pesihir itu datang, Musa berkata kepada mereka, “Lemparkanlah apa yang hendak kamu lemparkan!”"},{"id":"81","ar":"فَلَمَّآ اَلْقَوْا قَالَ مُوْسٰى مَا جِئْتُمْ بِهِ ۙالسِّحْرُۗ اِنَّ اللّٰهَ سَيُبْطِلُهٗۗ اِنَّ اللّٰهَ لَا يُصْلِحُ عَمَلَ الْمُفْسِدِيْنَ ","bc":"fa lammā alqau qāla mụsā mā ji`tum bihis-siḥr, innallāha sayubṭiluh, innallāha lā yuṣliḥu 'amalal-mufsidīn","at":" Setelah mereka melemparkan, Musa berkata, “Apa yang kamu lakukan itu, itulah sihir, sesungguhnya Allah akan menampakkan kepalsuan sihir itu. Sungguh, Allah tidak akan membiarkan terus berlangsungnya pekerjaan orang yang berbuat kerusakan.”"},{"id":"82","ar":"وَيُحِقُّ اللّٰهُ الْحَقَّ بِكَلِمٰتِهٖ وَلَوْ كَرِهَ الْمُجْرِمُوْنَ ","bc":"wa yuḥiqqullāhul-ḥaqqa bikalimātihī walau karihal-mujrimụn","at":"Dan Allah akan mengukuhkan yang benar dengan ketetapan-Nya, walaupun orang-orang yang berbuat dosa tidak menyukainya."},{"id":"83","ar":"فَمَآ اٰمَنَ لِمُوْسٰىٓ اِلَّا ذُرِّيَّةٌ مِّنْ قَوْمِهٖ عَلٰى خَوْفٍ مِّنْ فِرْعَوْنَ وَمَلَا۟ىِٕهِمْ اَنْ يَّفْتِنَهُمْ ۗوَاِنَّ فِرْعَوْنَ لَعَالٍ فِى الْاَرْضِۚ وَاِنَّهٗ لَمِنَ الْمُسْرِفِيْنَ ","bc":"fa mā āmana limụsā illā żurriyyatum ming qaumihī 'alā khaufim min fir'auna wa mala`ihim ay yaftinahum, wa inna fir'auna la'ālin fil-arḍ, wa innahụ laminal-musrifīn","at":"Maka tidak ada yang beriman kepada Musa, selain keturunan dari kaumnya dalam keadaan takut bahwa Fir‘aun dan para pemuka (kaum)nya akan menyiksa mereka. Dan sungguh, Fir‘aun itu benar-benar telah berbuat sewenang-wenang di bumi, dan benar-benar termasuk orang yang melampaui batas."},{"id":"84","ar":"وَقَالَ مُوْسٰى يٰقَوْمِ اِنْ كُنْتُمْ اٰمَنْتُمْ بِاللّٰهِ فَعَلَيْهِ تَوَكَّلُوْٓا اِنْ كُنْتُمْ مُّسْلِمِيْنَ ","bc":"wa qāla mụsā yā qaumi ing kuntum āmantum billāhi fa 'alaihi tawakkalū ing kuntum muslimīn","at":"Dan Musa berkata, “Wahai kaumku! Apabila kamu beriman kepada Allah, maka bertawakallah kepada-Nya, jika kamu benar-benar orang Muslim (berserah diri).”"},{"id":"85","ar":"فَقَالُوْا عَلَى اللّٰهِ تَوَكَّلْنَا ۚرَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِّلْقَوْمِ الظّٰلِمِيْنَ ","bc":"fa qālụ 'alallāhi tawakkalnā, rabbanā lā taj'alnā fitnatal lil-qaumiẓ-ẓālimīn","at":"Lalu mereka berkata, “Kepada Allah-lah kami bertawakal. Ya Tuhan kami, janganlah Engkau jadikan kami (sasaran) fitnah bagi kaum yang zalim,"},{"id":"86","ar":"وَنَجِّنَا بِرَحْمَتِكَ مِنَ الْقَوْمِ الْكٰفِرِيْنَ ","bc":"wa najjinā biraḥmatika minal-qaumil-kāfirīn","at":"dan selamatkanlah kami dengan rahmat-Mu dari orang-orang kafir.”"},{"id":"87","ar":"وَاَوْحَيْنَآ اِلٰى مُوْسٰى وَاَخِيْهِ اَنْ تَبَوَّاٰ لِقَوْمِكُمَا بِمِصْرَ بُيُوْتًا وَّاجْعَلُوْا بُيُوْتَكُمْ قِبْلَةً وَّاَقِيْمُوا الصَّلٰوةَۗ وَبَشِّرِ الْمُؤْمِنِيْنَ ","bc":"wa auḥainā ilā mụsā wa akhīhi an tabawwa`ā liqaumikumā bimiṣra buyụtaw waj'alụ buyụtakum qiblataw wa aqīmuṣ-ṣalāh, wa basysyiril-mu`minīn","at":"Dan Kami wahyukan kepada Musa dan saudaranya, “Ambillah beberapa rumah di Mesir untuk (tempat tinggal) kaummu dan jadikanlah rumah-rumahmu itu tempat ibadah dan laksanakanlah salat serta gembirakanlah orang-orang mukmin.”"},{"id":"88","ar":"وَقَالَ مُوْسٰى رَبَّنَآ اِنَّكَ اٰتَيْتَ فِرْعَوْنَ وَمَلَاَهٗ زِيْنَةً وَّاَمْوَالًا فِى الْحَيٰوةِ الدُّنْيَاۗ رَبَّنَا لِيُضِلُّوْا عَنْ سَبِيْلِكَ ۚرَبَّنَا اطْمِسْ عَلٰٓى اَمْوَالِهِمْ وَاشْدُدْ عَلٰى قُلُوْبِهِمْ فَلَا يُؤْمِنُوْا حَتّٰى يَرَوُا الْعَذَابَ الْاَلِيْمَ ","bc":"wa qāla mụsā rabbanā innaka ātaita fir'auna wa mala`ahụ zīnataw wa amwālan fil-ḥayātid-dun-yā, rabbanā liyuḍillụ 'an sabīlik, rabbanaṭmis 'alā amwālihim wasydud 'alā qulụbihim fa lā yu`minụ ḥattā yarawul-'ażābal-alīm","at":"Dan Musa berkata, “Ya Tuhan kami, Engkau telah memberikan kepada Fir‘aun dan para pemuka kaumnya perhiasan dan harta kekayaan dalam kehidupan dunia. Ya Tuhan kami, (akibatnya) mereka menyesatkan (manusia) dari jalan-Mu. Ya Tuhan, binasakanlah harta mereka, dan kuncilah hati mereka, sehingga mereka tidak beriman sampai mereka melihat azab yang pedih.”"},{"id":"89","ar":"قَالَ قَدْ اُجِيْبَتْ دَّعْوَتُكُمَا فَاسْتَقِيْمَا وَلَا تَتَّبِعٰۤنِّ سَبِيْلَ الَّذِيْنَ لَا يَعْلَمُوْنَ ","bc":"qāla qad ujībad da'watukumā fastaqīmā wa lā tattabi'ānni sabīlallażīna lā ya'lamụn","at":"Dia Allah berfirman, “Sungguh, telah diperkenankan permohonan kamu berdua, sebab itu tetaplah kamu berdua pada jalan yang lurus dan jangan sekali-kali kamu mengikuti jalan orang yang tidak mengetahui.”"},{"id":"90","ar":"۞ وَجَاوَزْنَا بِبَنِيْٓ اِسْرَاۤءِيْلَ الْبَحْرَ فَاَتْبَعَهُمْ فِرْعَوْنُ وَجُنُوْدُهٗ بَغْيًا وَّعَدْوًا ۗحَتّٰىٓ اِذَآ اَدْرَكَهُ الْغَرَقُ قَالَ اٰمَنْتُ اَنَّهٗ لَآ اِلٰهَ اِلَّا الَّذِيْٓ اٰمَنَتْ بِهٖ بَنُوْٓا اِسْرَاۤءِيْلَ وَاَنَا۠ مِنَ الْمُسْلِمِيْنَ","bc":"wa jāwaznā bibanī isrā`īlal-baḥra fa atba'ahum fir'aunu wa junụduhụ bagyaw wa 'adwā, ḥattā iżā adrakahul-garaqu qāla āmantu annahụ lā ilāha illallażī āmanat bihī banū isrā`īla wa ana minal-muslimīn","at":"Dan Kami selamatkan Bani Israil melintasi laut, kemudian Fir‘aun dan bala tentaranya mengikuti mereka, untuk menzalimi dan menindas (mereka). Sehingga ketika Fir‘aun hampir tenggelam dia berkata, “Aku percaya bahwa tidak ada tuhan melainkan Tuhan yang dipercayai oleh Bani Israil, dan aku termasuk orang-orang Muslim (berserah diri).”"},{"id":"91","ar":"اٰۤلْـٰٔنَ وَقَدْ عَصَيْتَ قَبْلُ وَكُنْتَ مِنَ الْمُفْسِدِيْنَ","bc":"āl-āna wa qad 'aṣaita qablu wa kunta minal-mufsidīn","at":"Mengapa baru sekarang (kamu beriman), padahal sesungguhnya engkau telah durhaka sejak dahulu, dan engkau termasuk orang yang berbuat kerusakan."},{"id":"92","ar":"فَالْيَوْمَ نُنَجِّيْكَ بِبَدَنِكَ لِتَكُوْنَ لِمَنْ خَلْفَكَ اٰيَةً ۗوَاِنَّ كَثِيْرًا مِّنَ النَّاسِ عَنْ اٰيٰتِنَا لَغٰفِلُوْنَ ","bc":"fal-yauma nunajjīka bibadanika litakụna liman khalfaka āyah, wa inna kaṡīram minan-nāsi 'an āyātinā lagāfilụn","at":"Maka pada hari ini Kami selamatkan jasadmu agar engkau dapat menjadi pelajaran bagi orang-orang yang datang setelahmu, tetapi kebanyakan manusia tidak mengindahkan tanda-tanda (kekuasaan) Kami."},{"id":"93","ar":"وَلَقَدْ بَوَّأْنَا بَنِيْٓ اِسْرَاۤءِيْلَ مُبَوَّاَ صِدْقٍ وَّرَزَقْنٰهُمْ مِّنَ الطَّيِّبٰتِ ۚفَمَا اخْتَلَفُوْا حَتّٰى جَاۤءَهُمُ الْعِلْمُ ۗاِنَّ رَبَّكَ يَقْضِيْ بَيْنَهُمْ يَوْمَ الْقِيٰمَةِ فِيْمَا كَانُوْا فِيْهِ يَخْتَلِفُوْنَ ","bc":"wa laqad bawwa`nā banī isrā`īla mubawwa`a ṣidqiw wa razaqnāhum minaṭ-ṭayyibāt, famakhtalafụ ḥattā jā`ahumul-'ilm, inna rabbaka yaqḍī bainahum yaumal-qiyāmati fīmā kānụ fīhi yakhtalifụn","at":"Dan sungguh, Kami telah menempatkan Bani Israil di tempat kediaman yang bagus dan Kami beri mereka rezeki yang baik. Maka mereka tidak berselisih, kecuali setelah datang kepada mereka pengetahuan (yang tersebut dalam Taurat). Sesungguhnya Tuhan kamu akan memberi keputusan antara mereka pada hari Kiamat tentang apa yang mereka perselisihkan itu."},{"id":"94","ar":"فَاِنْ كُنْتَ فِيْ شَكٍّ مِّمَّآ اَنْزَلْنَآ اِلَيْكَ فَسْـَٔلِ الَّذِيْنَ يَقْرَءُوْنَ الْكِتٰبَ مِنْ قَبْلِكَ ۚ لَقَدْ جَاۤءَكَ الْحَقُّ مِنْ رَّبِّكَ فَلَا تَكُوْنَنَّ مِنَ الْمُمْتَرِيْنَۙ","bc":"fa ing kunta fī syakkim mimmā anzalnā ilaika fas`alillażīna yaqra`ụnal-kitāba ming qablik, laqad jā`akal-ḥaqqu mir rabbika fa lā takụnanna minal-mumtarīn","at":"Maka jika engkau (Muhammad) berada dalam keragu-raguan tentang apa yang Kami turunkan kepadamu, maka tanyakanlah kepada orang yang membaca kitab sebelummu. Sungguh, telah datang kebenaran kepadamu dari Tuhanmu, maka janganlah sekali-kali engkau termasuk orang yang ragu."},{"id":"95","ar":"وَلَا تَكُوْنَنَّ مِنَ الَّذِيْنَ كَذَّبُوْا بِاٰيٰتِ اللّٰهِ فَتَكُوْنَ مِنَ الْخٰسِرِيْنَ ","bc":"wa lā takụnanna minallażīna każżabụ bi`āyātillāhi fa takụna minal-khāsirīn","at":"Dan janganlah sekali-kali engkau termasuk orang yang mendustakan ayat-ayat Allah, nanti engkau termasuk orang yang rugi."},{"id":"96","ar":"اِنَّ الَّذِيْنَ حَقَّتْ عَلَيْهِمْ كَلِمَةُ رَبِّكَ لَا يُؤْمِنُوْنَ ","bc":"innallażīna ḥaqqat 'alaihim kalimatu rabbika lā yu`minụn","at":"Sungguh, orang-orang yang telah dipastikan mendapat ketetapan Tuhanmu, tidaklah akan beriman,"},{"id":"97","ar":"وَلَوْ جَاۤءَتْهُمْ كُلُّ اٰيَةٍ حَتّٰى يَرَوُا الْعَذَابَ الْاَلِيْمَ ","bc":"walau jā`at-hum kullu āyatin ḥattā yarawul-'ażābal-alīm","at":"meskipun mereka mendapat tanda-tanda (kebesaran Allah), hingga mereka menyaksikan azab yang pedih."},{"id":"98","ar":"فَلَوْلَا كَانَتْ قَرْيَةٌ اٰمَنَتْ فَنَفَعَهَآ اِيْمَانُهَآ اِلَّا قَوْمَ يُوْنُسَۗ لَمَّآ اٰمَنُوْا كَشَفْنَا عَنْهُمْ عَذَابَ الْخِزْيِ فِى الْحَيٰوةِ الدُّنْيَا وَمَتَّعْنٰهُمْ اِلٰى حِيْنٍ ","bc":"falau lā kānat qaryatun āmanat fa nafa'ahā īmānuhā illā qauma yụnus, lammā āmanụ kasyafnā 'an-hum 'ażābal-khizyi fil-ḥayātid-dun-yā wa matta'nāhum ilā ḥīn","at":"Maka mengapa tidak ada (penduduk) suatu negeri pun yang beriman, lalu imannya itu bermanfaat kepadanya selain kaum Yunus? Ketika mereka (kaum Yunus itu) beriman, Kami hilangkan dari mereka azab yang menghinakan dalam kehidupan dunia, dan Kami beri kesenangan kepada mereka sampai waktu tertentu."},{"id":"99","ar":"وَلَوْ شَاۤءَ رَبُّكَ لَاٰمَنَ مَنْ فِى الْاَرْضِ كُلُّهُمْ جَمِيْعًاۗ اَفَاَنْتَ تُكْرِهُ النَّاسَ حَتّٰى يَكُوْنُوْا مُؤْمِنِيْنَ ","bc":"walau syā`a rabbuka la`āmana man fil-arḍi kulluhum jamī'ā, a fa anta tukrihun-nāsa ḥattā yakụnụ mu`minīn","at":"Dan jika Tuhanmu menghendaki, tentulah beriman semua orang di bumi seluruhnya. Tetapi apakah kamu (hendak) memaksa manusia agar mereka menjadi orang-orang yang beriman?"},{"id":"100","ar":"وَمَا كَانَ لِنَفْسٍ اَنْ تُؤْمِنَ اِلَّا بِاِذْنِ اللّٰهِ ۗوَيَجْعَلُ الرِّجْسَ عَلَى الَّذِيْنَ لَا يَعْقِلُوْنَ ","bc":"wa mā kāna linafsin an tu`mina illā bi`iżnillāh, wa yaj'alur-rijsa 'alallażīna lā ya'qilụn","at":"Dan tidak seorang pun akan beriman kecuali dengan izin Allah, dan Allah menimpakan azab kepada orang yang tidak mengerti."},{"id":"101","ar":"قُلِ انْظُرُوْا مَاذَا فِى السَّمٰوٰتِ وَالْاَرْضِ ۗوَمَا تُغْنِى الْاٰيٰتُ وَالنُّذُرُ عَنْ قَوْمٍ لَّا يُؤْمِنُوْنَ ","bc":"qulinẓurụ māżā fis-samāwāti wal-arḍ, wa mā tugnil-āyātu wan-nużuru 'ang qaumil lā yu`minụn","at":"Katakanlah, “Perhatikanlah apa yang ada di langit dan di bumi!” Tidaklah bermanfaat tanda-tanda (kebesaran Allah) dan rasul-rasul yang memberi peringatan bagi orang yang tidak beriman."},{"id":"102","ar":"فَهَلْ يَنْتَظِرُوْنَ اِلَّا مِثْلَ اَيَّامِ الَّذِيْنَ خَلَوْا مِنْ قَبْلِهِمْۗ قُلْ فَانْتَظِرُوْٓا اِنِّيْ مَعَكُمْ مِّنَ الْمُنْتَظِرِيْنَ ","bc":"fa hal yantaẓirụna illā miṡla ayyāmillażīna khalau ming qablihim, qul fantaẓirū innī ma'akum minal-muntaẓirīn","at":"Maka mereka tidak menunggu-nunggu kecuali (kejadian-kejadian) yang sama dengan kejadian-kejadian (yang menimpa) orang-orang terdahulu sebelum mereka. Katakanlah, “Maka tunggulah, aku pun termasuk orang yang menunggu bersama kamu.”"},{"id":"103","ar":"ثُمَّ نُنَجِّيْ رُسُلَنَا وَالَّذِيْنَ اٰمَنُوْا كَذٰلِكَ ۚحَقًّا عَلَيْنَا نُنْجِ الْمُؤْمِنِيْنَ ","bc":"ṡumma nunajjī rusulana wallażīna āmanụ każālik, ḥaqqan 'alainā nunjil-mu`minīn","at":"Kemudian Kami selamatkan rasul-rasul Kami dan orang-orang yang beri-man, demikianlah menjadi kewajiban Kami menyelamatkan orang yang beriman."},{"id":"104","ar":"قُلْ يٰٓاَيُّهَا النَّاسُ اِنْ كُنْتُمْ فِيْ شَكٍّ مِّنْ دِيْنِيْ فَلَآ اَعْبُدُ الَّذِيْنَ تَعْبُدُوْنَ مِنْ دُوْنِ اللّٰهِ وَلٰكِنْ اَعْبُدُ اللّٰهَ الَّذِيْ يَتَوَفّٰىكُمْ ۖ وَاُمِرْتُ اَنْ اَكُوْنَ مِنَ الْمُؤْمِنِيْنَ","bc":"qul yā ayyuhan-nāsu ing kuntum fī syakkim min dīnī fa lā a'budullażīna ta'budụna min dụnillāhi wa lākin a'budullāhallażī yatawaffākum wa umirtu an akụna minal-mu`minīn","at":"Katakanlah (Muhammad), “Wahai manusia! Jika kamu masih dalam keragu-raguan tentang agamaku, maka (ketahuilah) aku tidak menyembah yang kamu sembah selain Allah, tetapi aku menyembah Allah yang akan mematikan kamu dan aku telah diperintah agar termasuk orang yang beriman,”"},{"id":"105","ar":"وَاَنْ اَقِمْ وَجْهَكَ لِلدِّيْنِ حَنِيْفًاۚ وَلَا تَكُوْنَنَّ مِنَ الْمُشْرِكِيْنَ ","bc":"wa an aqim waj-haka lid-dīni ḥanīfā, wa lā takụnanna minal-musyrikīn","at":"dan (aku telah diperintah), “Hadapkanlah wajahmu kepada agama dengan tulus dan ikhlas dan jangan sekali-kali engkau termasuk orang yang musyrik."},{"id":"106","ar":"وَلَا تَدْعُ مِنْ دُوْنِ اللّٰهِ مَا لَا يَنْفَعُكَ وَلَا يَضُرُّكَ ۚفَاِنْ فَعَلْتَ فَاِنَّكَ اِذًا مِّنَ الظّٰلِمِيْنَ ","bc":"wa lā tad'u min dụnillāhi mā lā yanfa'uka wa lā yaḍurruk, fa in fa'alta fa innaka iżam minaẓ-ẓālimīn","at":"Dan jangan engkau menyembah sesuatu yang tidak memberi manfaat dan tidak (pula) memberi bencana kepadamu selain Allah, sebab jika engkau lakukan (yang demikian), maka sesungguhnya engkau termasuk orang-orang zalim.”"},{"id":"107","ar":"وَاِنْ يَّمْسَسْكَ اللّٰهُ بِضُرٍّ فَلَا كَاشِفَ لَهٗ ٓاِلَّا هُوَ ۚوَاِنْ يُّرِدْكَ بِخَيْرٍ فَلَا رَاۤدَّ لِفَضْلِهٖۗ يُصِيْبُ بِهٖ مَنْ يَّشَاۤءُ مِنْ عِبَادِهٖ ۗوَهُوَ الْغَفُوْرُ الرَّحِيْمُ ","bc":"wa iy yamsaskallāhu biḍurrin fa lā kāsyifa lahū illā huw, wa iy yuridka bikhairin fa lā rādda lifaḍlih, yuṣību bihī may yasyā`u min 'ibādih, wa huwal-gafụrur-raḥīm","at":"Dan jika Allah menimpakan suatu bencana kepadamu, maka tidak ada yang dapat menghilangkannya kecuali Dia. Dan jika Allah menghendaki kebaikan bagi kamu, maka tidak ada yang dapat menolak karunia-Nya. Dia memberikan kebaikan kepada siapa saja yang Dia kehendaki di antara hamba-hamba-Nya. Dia Maha Pengampun, Maha Penyayang."},{"id":"108","ar":"قُلْ يٰٓاَيُّهَا النَّاسُ قَدْ جَاۤءَكُمُ الْحَقُّ مِنْ رَّبِّكُمْ ۚفَمَنِ اهْتَدٰى فَاِنَّمَا يَهْتَدِيْ لِنَفْسِهٖ ۚوَمَنْ ضَلَّ فَاِنَّمَا يَضِلُّ عَلَيْهَا ۚوَمَآ اَنَا۠ عَلَيْكُمْ بِوَكِيْلٍۗ ","bc":"qul yā ayyuhan-nāsu qad jā`akumul-ḥaqqu mir rabbikum, fa manihtadā fa innamā yahtadī linafsih, wa man ḍalla fa innamā yaḍillu 'alaihā, wa mā ana 'alaikum biwakīl","at":"Katakanlah (Muhammad), “Wahai manusia! Telah datang kepadamu kebenaran (Al-Qur'an) dari Tuhanmu, sebab itu barang siapa mendapat petunjuk, maka sebenarnya (petunjuk itu) untuk (kebaikan) dirinya sendiri. Dan barang siapa sesat, sesungguhnya kesesatannya itu (mencelakakan) dirinya sendiri. Dan Aku bukanlah pemelihara dirimu.”"},{"id":"109","ar":"وَاتَّبِعْ مَا يُوْحٰىٓ اِلَيْكَ وَاصْبِرْ حَتّٰى يَحْكُمَ اللّٰهُ ۚوَهُوَ خَيْرُ الْحٰكِمِيْنَ ","bc":"wattabi' mā yụḥā ilaika waṣbir ḥattā yaḥkumallāh, wa huwa khairul-ḥākimīn","at":"Dan ikutilah apa yang diwahyukan kepadamu, dan bersabarlah hingga Allah memberi keputusan. Dialah hakim yang terbaik."}],yusuf=[{"id":"1","ar":"الۤرٰ ۗ تِلْكَ اٰيٰتُ الْكِتٰبِ الْمُبِيْنِۗ ","bc":"alif lām rā, tilka āyātul-kitābil mubīn","at":"Alif Lam Ra. Ini adalah ayat-ayat Kitab (Al-Qur'an) yang jelas."},{"id":"2","ar":"اِنَّآ اَنْزَلْنٰهُ قُرْاٰنًا عَرَبِيًّا لَّعَلَّكُمْ تَعْقِلُوْنَ","bc":"innā anzalnāhu qur`ānan 'arabiyyal la'allakum ta'qilụn","at":"Sesungguhnya Kami menurunkannya sebagai Qur'an berbahasa Arab, agar kamu mengerti."},{"id":"3","ar":"نَحْنُ نَقُصُّ عَلَيْكَ اَحْسَنَ الْقَصَصِ بِمَآ اَوْحَيْنَآ اِلَيْكَ هٰذَا الْقُرْاٰنَۖ وَاِنْ كُنْتَ مِنْ قَبْلِهٖ لَمِنَ الْغٰفِلِيْنَ","bc":"naḥnu naquṣṣu 'alaika aḥsanal-qaṣaṣi bimā auḥainā ilaika hāżal-qur`āna wa ing kunta ming qablihī laminal-gāfilīn","at":"Kami menceritakan kepadamu (Muhammad) kisah yang paling baik dengan mewahyukan Al-Qur'an ini kepadamu, dan sesungguhnya engkau sebelum itu termasuk orang yang tidak mengetahui."},{"id":"4","ar":"اِذْ قَالَ يُوْسُفُ لِاَبِيْهِ يٰٓاَبَتِ اِنِّيْ رَاَيْتُ اَحَدَ عَشَرَ كَوْكَبًا وَّالشَّمْسَ وَالْقَمَرَ رَاَيْتُهُمْ لِيْ سٰجِدِيْنَ","bc":"iż qāla yụsufu li`abīhi yā abati innī ra`aitu aḥada 'asyara kaukabaw wasy-syamsa wal-qamara ra`aituhum lī sājidīn","at":"(Ingatlah), ketika Yusuf berkata kepada ayahnya, “Wahai ayahku! Sungguh, aku (bermimpi) melihat sebelas bintang, matahari dan bulan; kulihat semuanya sujud kepadaku.”"},{"id":"5","ar":"قَالَ يٰبُنَيَّ لَا تَقْصُصْ رُءْيَاكَ عَلٰٓى اِخْوَتِكَ فَيَكِيْدُوْا لَكَ كَيْدًا ۗاِنَّ الشَّيْطٰنَ لِلْاِنْسَانِ عَدُوٌّ مُّبِيْنٌ","bc":"qāla yā bunayya lā taqṣuṣ ru`yāka 'alā ikhwatika fa yakīdụ laka kaidā, innasy-syaiṭāna lil-insāni 'aduwwum mubīn","at":"Dia (ayahnya) berkata, “Wahai anakku! Janganlah engkau ceritakan mimpimu kepada saudara-saudaramu, mereka akan membuat tipu daya (untuk membinasakan)mu. Sungguh, setan itu musuh yang jelas bagi manusia.”"},{"id":"6","ar":"وَكَذٰلِكَ يَجْتَبِيْكَ رَبُّكَ وَيُعَلِّمُكَ مِنْ تَأْوِيْلِ الْاَحَادِيْثِ وَيُتِمُّ نِعْمَتَهٗ عَلَيْكَ وَعَلٰٓى اٰلِ يَعْقُوْبَ كَمَآ اَتَمَّهَا عَلٰٓى اَبَوَيْكَ مِنْ قَبْلُ اِبْرٰهِيْمَ وَاِسْحٰقَۗ اِنَّ رَبَّكَ عَلِيْمٌ حَكِيْمٌ","bc":"wa każālika yajtabīka rabbuka wa yu'allimuka min ta`wīlil-aḥādīṡi wa yutimmu ni'matahụ 'alaika wa 'alā āli ya'qụba kamā atammahā 'alā abawaika ming qablu ibrāhīma wa is-ḥāq, inna rabbaka 'alīmun ḥakīm","at":"Dan demikianlah, Tuhan memilih engkau (untuk menjadi Nabi) dan mengajarkan kepadamu sebagian dari takwil mimpi dan menyempurnakan (nikmat-Nya) kepadamu dan kepada keluarga Yakub, sebagaimana Dia telah menyempurnakan nikmat-Nya kepada kedua orang kakekmu sebelum itu, (yaitu) Ibrahim dan Ishak. Sungguh, Tuhanmu Maha Mengetahui, Mahabijak-sana."},{"id":"7","ar":"۞ لَقَدْ كَانَ فِيْ يُوْسُفَ وَاِخْوَتِهٖٓ اٰيٰتٌ لِّلسَّاۤىِٕلِيْنَ ","bc":"laqad kāna fī yụsufa wa ikhwatihī āyātul lis-sā`ilīn","at":"Sungguh, dalam (kisah) Yusuf dan saudara-saudaranya terdapat tanda-tanda (kekuasaan Allah) bagi orang yang bertanya. "},{"id":"8","ar":"اِذْ قَالُوْا لَيُوْسُفُ وَاَخُوْهُ اَحَبُّ اِلٰٓى اَبِيْنَا مِنَّا وَنَحْنُ عُصْبَةٌ ۗاِنَّ اَبَانَا لَفِيْ ضَلٰلٍ مُّبِيْنٍۙ ","bc":"iż qālụ layụsufu wa akhụhu aḥabbu ilā abīnā minnā wa naḥnu 'uṣbah, inna abānā lafī ḍalālim mubīn","at":"Ketika mereka berkata, “Sesungguhnya Yusuf dan saudaranya (Bunyamin) lebih dicintai ayah daripada kita, padahal kita adalah satu golongan (yang kuat). Sungguh, ayah kita dalam kekeliruan yang nyata."},{"id":"9","ar":" ۨاقْتُلُوْا يُوْسُفَ اَوِ اطْرَحُوْهُ اَرْضًا يَّخْلُ لَكُمْ وَجْهُ اَبِيْكُمْ وَتَكُوْنُوْا مِنْۢ بَعْدِهٖ قَوْمًا صٰلِحِيْنَ","bc":"uqtulụ yụsufa awiṭraḥụhu arḍay yakhlu lakum waj-hu abīkum wa takụnụ mim ba'dihī qauman ṣāliḥīn","at":"Bunuhlah Yusuf atau buanglah dia ke suatu tempat agar perhatian ayah tertumpah kepadamu, dan setelah itu kamu menjadi orang yang baik.”"},{"id":"10","ar":"قَالَ قَاۤئِلٌ مِّنْهُمْ لَا تَقْتُلُوْا يُوْسُفَ وَاَلْقُوْهُ فِيْ غَيٰبَتِ الْجُبِّ يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ اِنْ كُنْتُمْ فٰعِلِيْنَ","bc":"qāla qā`ilum min-hum lā taqtulụ yụsufa wa alqụhu fī gayābatil-jubbi yaltaqiṭ-hu ba'ḍus-sayyārati ing kuntum fā'ilīn","at":"Seorang di antara mereka berkata, “Janganlah kamu membunuh Yusuf, tetapi masukan saja dia ke dasar sumur agar dia dipungut oleh sebagian musafir, jika kamu hendak berbuat.”"},{"id":"11","ar":"قَالُوْا يٰٓاَبَانَا مَالَكَ لَا تَأْمَنَّ۫ا عَلٰى يُوْسُفَ وَاِنَّا لَهٗ لَنَاصِحُوْنَ","bc":"qālụ yā abānā mā laka lā ta`mannā 'alā yụsufa wa innā lahụ lanāṣiḥụn","at":"Mereka berkata, “Wahai ayah kami! Mengapa engkau tidak mempercayai kami terhadap Yusuf, padahal sesungguhnya kami semua menginginkan kebaikan baginya."},{"id":"12","ar":"اَرْسِلْهُ مَعَنَا غَدًا يَّرْتَعْ وَيَلْعَبْ وَاِنَّا لَهٗ لَحٰفِظُوْنَ","bc":"arsil-hu ma'anā gaday yarta' wa yal'ab wa innā lahụ laḥāfiẓụn","at":"Biarkanlah dia pergi bersama kami besok pagi, agar dia bersenang-senang dan bermain-main, dan kami pasti menjaganya.”"},{"id":"13","ar":"قَالَ اِنِّيْ لَيَحْزُنُنِيْٓ اَنْ تَذْهَبُوْا بِهٖ وَاَخَافُ اَنْ يَّأْكُلَهُ الذِّئْبُ وَاَنْتُمْ عَنْهُ غٰفِلُوْنَ ","bc":"qāla innī layaḥzununī an taż-habụ bihī wa akhāfu ay ya`kulahuż-żi`bu wa antum 'an-hu gāfilụn","at":"Dia (Yakub) berkata, “Sesungguhnya kepergian kamu bersama dia (Yusuf) sangat menyedihkanku dan aku khawatir dia dimakan serigala, sedang kamu lengah darinya.”"},{"id":"14","ar":"قَالُوْا لَىِٕنْ اَكَلَهُ الذِّئْبُ وَنَحْنُ عُصْبَةٌ اِنَّآ اِذًا لَّخٰسِرُوْنَ","bc":"qālụ la`in akalahuż-żi`bu wa naḥnu 'uṣbatun innā iżal lakhāsirụn","at":"Sesungguhnya mereka berkata, “Jika dia dimakan serigala, padahal kami golongan (yang kuat), kalau demikian tentu kami orang-orang yang rugi.”"},{"id":"15","ar":"فَلَمَّا ذَهَبُوْا بِهٖ وَاَجْمَعُوْٓا اَنْ يَّجْعَلُوْهُ فِيْ غَيٰبَتِ الْجُبِّۚ وَاَوْحَيْنَآ اِلَيْهِ لَتُنَبِّئَنَّهُمْ بِاَمْرِهِمْ هٰذَا وَهُمْ لَا يَشْعُرُوْنَ","bc":"fa lammā żahabụ bihī wa ajma'ū ay yaj'alụhu fī gayābatil-jubb, wa auḥainā ilaihi latunabbi`annahum bi`amrihim hāżā wa hum lā yasy'urụn","at":"Maka ketika mereka membawanya dan sepakat memasukkan ke dasar sumur, Kami wahyukan kepadanya, “Engkau kelak pasti akan menceritakan perbuatan ini kepada mereka, sedang mereka tidak menyadari.”"},{"id":"16","ar":"وَجَاۤءُوْٓ اَبَاهُمْ عِشَاۤءً يَّبْكُوْنَۗ","bc":"wa jā`ū abāhum 'isyā`ay yabkụn","at":"Kemudian mereka datang kepada ayah mereka pada petang hari sambil menangis."},{"id":"17","ar":"قَالُوْا يٰٓاَبَانَآ اِنَّا ذَهَبْنَا نَسْتَبِقُ وَتَرَكْنَا يُوْسُفَ عِنْدَ مَتَاعِنَا فَاَكَلَهُ الذِّئْبُۚ وَمَآ اَنْتَ بِمُؤْمِنٍ لَّنَا وَلَوْ كُنَّا صٰدِقِيْنَ","bc":"qālụ yā abānā innā żahabnā nastabiqu wa taraknā yụsufa 'inda matā'inā fa akalahuż-żi`b, wa mā anta bimu`minil lanā walau kunnā ṣādiqīn","at":"Mereka berkata, “Wahai ayah kami! Sesungguhnya kami pergi berlomba dan kami tinggalkan Yusuf di dekat barang-barang kami, lalu dia dimakan serigala; dan engkau tentu tidak akan percaya kepada kami, sekalipun kami berkata benar.”"},{"id":"18","ar":"وَجَاۤءُوْ عَلٰى قَمِيْصِهٖ بِدَمٍ كَذِبٍۗ قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْرًاۗ فَصَبْرٌ جَمِيْلٌ ۗوَاللّٰهُ الْمُسْتَعَانُ عَلٰى مَا تَصِفُوْنَ","bc":"wa jā`ụ 'alā qamīṣihī bidaming każib, qāla bal sawwalat lakum anfusukum amrā, fa ṣabrun jamīl, wallāhul-musta'ānu 'alā mā taṣifụn","at":"Dan mereka datang membawa baju gamisnya (yang berlumuran) darah palsu. Dia (Yakub) berkata, “Sebenarnya hanya dirimu sendirilah yang memandang baik urusan yang buruk itu; maka hanya bersabar itulah yang terbaik (bagiku). Dan kepada Allah saja memohon pertolongan-Nya terhadap apa yang kamu ceritakan.”"},{"id":"19","ar":"وَجَاۤءَتْ سَيَّارَةٌ فَاَرْسَلُوْا وَارِدَهُمْ فَاَدْلٰى دَلْوَهٗ ۗقَالَ يٰبُشْرٰى هٰذَا غُلٰمٌ ۗوَاَسَرُّوْهُ بِضَاعَةً ۗوَاللّٰهُ عَلِيْمٌ ۢبِمَا يَعْمَلُوْنَ","bc":"wa jā`at sayyāratun fa arsalụ wāridahum fa adlā dalwah, qāla yā busyrā hāżā gulām, wa asarrụhu biḍā'ah, wallāhu 'alīmum bimā ya'malụn","at":"Dan datanglah sekelompok musafir, mereka menyuruh seorang pengambil air. Lalu dia menurunkan timbanya. Dia berkata, “Oh, senangnya, ini ada seorang anak muda!” Kemudian mereka menyembunyikannya sebagai barang dagangan. Dan Allah Maha Mengetahui apa yang mereka kerjakan."},{"id":"20","ar":"وَشَرَوْهُ بِثَمَنٍۢ بَخْسٍ دَرَاهِمَ مَعْدُوْدَةٍ ۚوَكَانُوْا فِيْهِ مِنَ الزَّاهِدِيْنَ ","bc":"wa syarauhu biṡamanim bakhsin darāhima ma'dụdah, wa kānụ fīhi minaz-zāhidīn","at":"Dan mereka menjualnya (Yusuf) dengan harga rendah, yaitu beberapa dirham saja, sebab mereka tidak tertarik kepadanya."},{"id":"21","ar":"وَقَالَ الَّذِى اشْتَرٰىهُ مِنْ مِّصْرَ لِامْرَاَتِهٖٓ اَكْرِمِيْ مَثْوٰىهُ عَسٰىٓ اَنْ يَّنْفَعَنَآ اَوْ نَتَّخِذَهٗ وَلَدًا ۗوَكَذٰلِكَ مَكَّنَّا لِيُوْسُفَ فِى الْاَرْضِۖ وَلِنُعَلِّمَهٗ مِنْ تَأْوِيْلِ الْاَحَادِيْثِۗ وَاللّٰهُ غَالِبٌ عَلٰٓى اَمْرِهٖ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ","bc":"wa qālallażisytarāhu mim miṣra limra`atihī akrimī maṡwāhu 'asā ay yanfa'anā au nattakhiżahụ waladā, wa każālika makkannā liyụsufa fil-arḍi wa linu'allimahụ min ta`wīlil-aḥādīṡ, wallāhu gālibun 'alā amrihī wa lākinna akṡaran-nāsi lā ya'lamụn","at":"Dan orang dari Mesir yang membelinya berkata kepada istrinya,” Berikanlah kepadanya tempat (dan layanan) yang baik, mudah-mudahan dia bermanfaat bagi kita atau kita pungut dia sebagai anak.” Dan demikianlah Kami memberikan kedudukan yang baik kepada Yusuf di negeri (Mesir), dan agar Kami ajarkan kepadanya takwil mimpi. Dan Allah berkuasa terhadap urusan-Nya, tetapi kebanyakan manusia tidak mengerti."},{"id":"22","ar":"وَلَمَّا بَلَغَ اَشُدَّهٗٓ اٰتَيْنٰهُ حُكْمًا وَّعِلْمًا ۗوَكَذٰلِكَ نَجْزِى الْمُحْسِنِيْنَ","bc":"wa lammā balaga asyuddahū ātaināhu ḥukmaw wa 'ilmā, wa każālika najzil-muḥsinīn","at":"Dan ketika dia telah cukup dewasa Kami berikan kepadanya kekuasaan dan ilmu. Demikianlah Kami memberi balasan kepada orang-orang yang berbuat baik."},{"id":"23","ar":"وَرَاوَدَتْهُ الَّتِيْ هُوَ فِيْ بَيْتِهَا عَنْ نَّفْسِهٖ وَغَلَّقَتِ الْاَبْوَابَ وَقَالَتْ هَيْتَ لَكَ ۗقَالَ مَعَاذَ اللّٰهِ اِنَّهٗ رَبِّيْٓ اَحْسَنَ مَثْوَايَۗ اِنَّهٗ لَا يُفْلِحُ الظّٰلِمُوْنَ","bc":"wa rāwadat-hullatī huwa fī baitihā 'an nafsihī wa gallaqatil-abwāba wa qālat haita lak, qāla ma'āżallāhi innahụ rabbī aḥsana maṡwāy, innahụ lā yufliḥuẓ-ẓālimụn","at":"Dan perempuan yang dia (Yusuf) tinggal di rumahnya menggoda dirinya. Dan dia menutup pintu-pintu, lalu berkata, “Marilah mendekat kepadaku.” Yusuf berkata, “Aku berlindung kepada Allah, sungguh, tuanku telah memperlakukan aku dengan baik.” Sesungguhnya orang yang zalim itu tidak akan beruntung."},{"id":"24","ar":"وَلَقَدْ هَمَّتْ بِهٖۙ وَهَمَّ بِهَا ۚ لَوْلَآ اَنْ رَّاٰى بُرْهَانَ رَبِّهٖۗ كَذٰلِكَ لِنَصْرِفَ عَنْهُ السُّوْۤءَ وَالْفَحْشَاۤءَۗ اِنَّهٗ مِنْ عِبَادِنَا الْمُخْلَصِيْنَ ","bc":"wa laqad hammat bihī wa hamma bihā, lau lā ar ra`ā bur-hāna rabbih, każālika linaṣrifa 'an-hus-sū`a wal-faḥsyā`, innahụ min 'ibādinal-mukhlaṣīn","at":"Dan sungguh, perempuan itu telah berkehendak kepadanya (Yusuf). Dan Yusuf pun berkehendak kepadanya, sekiranya dia tidak melihat tanda (dari) Tuhannya. Demikianlah, Kami palingkan darinya keburukan dan kekejian. Sungguh, dia (Yusuf) termasuk hamba Kami yang terpilih."},{"id":"25","ar":"وَاسْتَبَقَا الْبَابَ وَقَدَّتْ قَمِيْصَهٗ مِنْ دُبُرٍ وَّاَلْفَيَا سَيِّدَهَا لَدَا الْبَابِۗ قَالَتْ مَا جَزَاۤءُ مَنْ اَرَادَ بِاَهْلِكَ سُوْۤءًا اِلَّآ اَنْ يُّسْجَنَ اَوْ عَذَابٌ اَلِيْمٌ ","bc":"wastabaqal-bāba wa qaddat qamīṣahụ min duburiw wa alfayā sayyidahā ladal-bāb, qālat mā jazā`u man arāda bi`ahlika sū`an illā ay yusjana au 'ażābun alīm","at":"Dan keduanya berlomba menuju pintu dan perempuan itu menarik baju gamisnya (Yusuf) dari belakang hingga koyak dan keduanya mendapati suami perempuan itu di depan pintu. Dia (perempuan itu) berkata, “Apakah balasan terhadap orang yang bermaksud buruk terhadap istrimu, selain dipenjarakan atau (dihukum) dengan siksa yang pedih?”"},{"id":"26","ar":"قَالَ هِيَ رَاوَدَتْنِيْ عَنْ نَّفْسِيْ وَشَهِدَ شَاهِدٌ مِّنْ اَهْلِهَاۚ اِنْ كَانَ قَمِيْصُهٗ قُدَّ مِنْ قُبُلٍ فَصَدَقَتْ وَهُوَ مِنَ الْكٰذِبِيْنَ ","bc":"qāla hiya rāwadatnī 'an nafsī wa syahida syāhidum min ahlihā, ing kāna qamīṣuhụ qudda ming qubulin fa ṣadaqat wa huwa minal-kāżibīn","at":"Dia (Yusuf) berkata, “Dia yang menggodaku dan merayu diriku.” Seorang saksi dari keluarga perempuan itu memberikan kesaksian, “Jika baju gamisnya koyak di bagian depan, maka perempuan itu benar, dan dia (Yusuf) termasuk orang yang dusta."},{"id":"27","ar":"وَاِنْ كَانَ قَمِيْصُهٗ قُدَّ مِنْ دُبُرٍ فَكَذَبَتْ وَهُوَ مِنَ الصّٰدِقِيْنَ ","bc":"wa ing kāna qamīṣuhụ qudda min duburin fa każabat wa huwa minaṣ-ṣādiqīn","at":"Dan jika baju gamisnya koyak di bagian belakang, maka perempuan itulah yang dusta, dan dia (Yusuf) termasuk orang yang benar.”"},{"id":"28","ar":"فَلَمَّا رَاٰى قَمِيْصَهٗ قُدَّ مِنْ دُبُرٍ قَالَ اِنَّهٗ مِنْ كَيْدِكُنَّ ۗاِنَّ كَيْدَكُنَّ عَظِيْمٌ ","bc":"fa lammā ra`ā qamīṣahụ qudda min duburing qāla innahụ ming kaidikunn, inna kaidakunna 'aẓīm","at":"Maka ketika dia (suami perempuan itu) melihat baju gamisnya (Yusuf) koyak di bagian belakang, dia berkata, “Sesungguhnya ini adalah tipu dayamu. Tipu dayamu benar-benar hebat.”"},{"id":"29","ar":"يُوْسُفُ اَعْرِضْ عَنْ هٰذَا وَاسْتَغْفِرِيْ لِذَنْۢبِكِۖ اِنَّكِ كُنْتِ مِنَ الْخٰطِـِٕيْنَ ","bc":"yụsufu a'riḍ 'an hāżā wastagfirī liżambiki innaki kunti minal-khāṭi`īn","at":"Wahai Yusuf! ”Lupakanlah ini, dan (istriku) mohonlah ampunan atas dosamu, karena engkau termasuk orang yang bersalah.”"},{"id":"30","ar":"۞ وَقَالَ نِسْوَةٌ فِى الْمَدِيْنَةِ امْرَاَتُ الْعَزِيْزِ تُرَاوِدُ فَتٰىهَا عَنْ نَّفْسِهٖۚ قَدْ شَغَفَهَا حُبًّاۗ اِنَّا لَنَرٰىهَا فِيْ ضَلٰلٍ مُّبِيْنٍ ","bc":"wa qāla niswatun fil-madīnatimra`atul-'azīzi turāwidu fatāhā 'an nafsih, qad syagafahā ḥubbā, innā lanarāhā fī ḍalālim mubīn","at":"Dan perempuan-perempuan di kota berkata, “Istri Al-Aziz menggoda dan merayu pelayannya untuk menundukkan dirinya, pelayannya benar-benar membuatnya mabuk cinta. Kami pasti memandang dia dalam kesesatan yang nyata.”"},{"id":"31","ar":"فَلَمَّا سَمِعَتْ بِمَكْرِهِنَّ اَرْسَلَتْ اِلَيْهِنَّ وَاَعْتَدَتْ لَهُنَّ مُتَّكَاً وَّاٰتَتْ كُلَّ وَاحِدَةٍ مِّنْهُنَّ سِكِّيْنًا وَّقَالَتِ اخْرُجْ عَلَيْهِنَّ ۚ فَلَمَّا رَاَيْنَهٗٓ اَكْبَرْنَهٗ وَقَطَّعْنَ اَيْدِيَهُنَّۖ وَقُلْنَ حَاشَ لِلّٰهِ مَا هٰذَا بَشَرًاۗ اِنْ هٰذَآ اِلَّا مَلَكٌ كَرِيْمٌ ","bc":"fa lammā sami'at bimakrihinna arsalat ilaihinna wa a'tadat lahunna muttaka`aw wa ātat kulla wāḥidatim min-hunna sikkīnaw wa qālatikhruj 'alaihinn, fa lammā ra`ainahū akbarnahụ wa qaṭṭa'na aidiyahunna wa qulna ḥāsya lillāhi mā hāżā basyarā, in hāżā illā malakung karīm","at":"Maka ketika perempuan itu mendengar cercaan mereka, diundangnyalah perempuan-perempuan itu dan disediakannya tempat duduk bagi mereka, dan kepada masing-masing mereka diberikan sebuah pisau (untuk memotong jamuan), kemudian dia berkata (kepada Yusuf), “Keluarlah (tampakkanlah dirimu) kepada mereka.” Ketika perempuan-perempuan itu melihatnya, mereka terpesona kepada (keelokan rupa)nya, dan mereka (tanpa sadar) melukai tangannya sendiri. Seraya berkata, “Mahasempurna Allah, ini bukanlah manusia. Ini benar-benar malaikat yang mulia.”"},{"id":"32","ar":"قَالَتْ فَذٰلِكُنَّ الَّذِيْ لُمْتُنَّنِيْ فِيْهِ ۗوَلَقَدْ رَاوَدْتُّهٗ عَنْ نَّفْسِهٖ فَاسْتَعْصَمَ ۗوَلَىِٕنْ لَّمْ يَفْعَلْ مَآ اٰمُرُهٗ لَيُسْجَنَنَّ وَلَيَكُوْنًا مِّنَ الصّٰغِرِيْنَ ","bc":"qālat fa żālikunnallażī lumtunnanī fīh, wa laqad rāwattuhụ 'an nafsihī fasta'ṣam, wa la`il lam yaf'al mā āmuruhụ layusjananna wa layakụnam minaṣ-ṣāgirīn","at":"Dia (istri Al-Aziz) berkata, “Itulah orangnya yang menyebabkan kamu mencela aku karena (aku tertarik) kepadanya, dan sungguh, aku telah menggoda untuk menundukkan dirinya tetapi dia menolak. Jika dia tidak melakukan apa yang aku perintahkan kepadanya, niscaya dia akan dipenjarakan, dan dia akan menjadi orang yang hina.”"},{"id":"33","ar":"قَالَ رَبِّ السِّجْنُ اَحَبُّ اِلَيَّ مِمَّا يَدْعُوْنَنِيْٓ اِلَيْهِ ۚوَاِلَّا تَصْرِفْ عَنِّيْ كَيْدَهُنَّ اَصْبُ اِلَيْهِنَّ وَاَكُنْ مِّنَ الْجٰهِلِيْنَ ","bc":"qāla rabbis-sijnu aḥabbu ilayya mimmā yad'ụnanī ilaīh, wa illā taṣrif 'annī kaidahunna aṣbu ilaihinna wa akum minal-jāhilīn","at":"Yusuf berkata, “Wahai Tuhanku! Penjara lebih aku sukai daripada memenuhi ajakan mereka. Jika aku tidak Engkau hindarkan dari tipu daya mereka, niscaya aku akan cenderung untuk (memenuhi keinginan mereka) dan tentu aku termasuk orang yang bodoh.”"},{"id":"34","ar":"فَاسْتَجَابَ لَهٗ رَبُّهٗ فَصَرَفَ عَنْهُ كَيْدَهُنَّ ۗاِنَّهٗ هُوَ السَّمِيْعُ الْعَلِيْمُ ","bc":"fastajāba lahụ rabbuhụ fa ṣarafa 'an-hu kaidahunn, innahụ huwas-samī'ul 'alīm","at":"Maka Tuhan memperkenankan doa Yusuf, dan Dia menghindarkan Yusuf dari tipu daya mereka. Dialah Yang Maha Mendengar, Maha Mengetahui."},{"id":"35","ar":"ثُمَّ بَدَا لَهُمْ مِّنْۢ بَعْدِ مَا رَاَوُا الْاٰيٰتِ لَيَسْجُنُنَّهٗ حَتّٰى حِيْنٍ ","bc":"ṡumma badā lahum mim ba'di mā ra`awul-āyāti layasjununnahụ ḥattā ḥīn","at":"Kemudian timbul pikiran pada mereka setelah melihat tanda-tanda (kebenaran Yusuf) bahwa mereka harus memenjarakannya sampai waktu tertentu."},{"id":"36","ar":"وَدَخَلَ مَعَهُ السِّجْنَ فَتَيٰنِ ۗقَالَ اَحَدُهُمَآ اِنِّيْٓ اَرٰىنِيْٓ اَعْصِرُ خَمْرًا ۚوَقَالَ الْاٰخَرُ اِنِّيْٓ اَرٰىنِيْٓ اَحْمِلُ فَوْقَ رَأْسِيْ خُبْزًا تَأْكُلُ الطَّيْرُ مِنْهُ ۗنَبِّئْنَا بِتَأْوِيْلِهٖ ۚاِنَّا نَرٰىكَ مِنَ الْمُحْسِنِيْنَ","bc":"wa dakhala ma'ahus-sijna fatayān, qāla aḥaduhumā innī arānī a'ṣiru khamrā, wa qālal-ākharu innī arānī aḥmilu fauqa ra`sī khubzan ta`kuluṭ-ṭairu min-h, nabbi`nā bita`wīlih, innā narāka minal-muḥsinīn","at":"Dan bersama dia masuk pula dua orang pemuda ke dalam penjara. Salah satunya berkata, “Sesungguhnya aku bermimpi memeras anggur,” dan yang lainnya berkata, “Aku bermimpi, membawa roti di atas kepalaku, sebagiannya dimakan burung.” Berikanlah kepada kami takwilnya. Sesungguhnya kami memandangmu termasuk orang yang berbuat baik."},{"id":"37","ar":"قَالَ لَا يَأْتِيْكُمَا طَعَامٌ تُرْزَقٰنِهٖٓ اِلَّا نَبَّأْتُكُمَا بِتَأْوِيْلِهٖ قَبْلَ اَنْ يَّأْتِيَكُمَا ۗذٰلِكُمَا مِمَّا عَلَّمَنِيْ رَبِّيْۗ اِنِّيْ تَرَكْتُ مِلَّةَ قَوْمٍ لَّا يُؤْمِنُوْنَ بِاللّٰهِ وَهُمْ بِالْاٰخِرَةِ هُمْ كٰفِرُوْنَۙ ","bc":"qāla lā ya`tīkumā ṭa'āmun turzaqānihī illā nabba`tukumā bita`wīlihī qabla ay ya`tiyakumā, żālikumā mimmā 'allamanī rabbī, innī taraktu millata qaumil lā yu`minụna billāhi wa hum bil-ākhirati hum kāfirụn","at":"Dia (Yusuf) berkata, “Makanan apa pun yang akan diberikan kepadamu berdua aku telah dapat menerangkan takwilnya, sebelum (makanan) itu sampai kepadamu. Itu sebagian dari yang diajarkan Tuhan kepadaku. Sesungguhnya aku telah meninggalkan agama orang-orang yang tidak beriman kepada Allah, bahkan mereka tidak percaya kepada hari akhirat."},{"id":"38","ar":"وَاتَّبَعْتُ مِلَّةَ اٰبَاۤءِيْٓ اِبْرٰهِيْمَ وَاِسْحٰقَ وَيَعْقُوْبَۗ مَا كَانَ لَنَآ اَنْ نُّشْرِكَ بِاللّٰهِ مِنْ شَيْءٍۗ ذٰلِكَ مِنْ فَضْلِ اللّٰهِ عَلَيْنَا وَعَلَى النَّاسِ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَشْكُرُوْنَ","bc":"wattaba'tu millata ābā`ī ibrāhīma wa is-ḥāqa wa ya'qụb, mā kāna lanā an nusyrika billāhi min syaī`, żālika min faḍlillāhi 'alainā wa 'alan-nāsi wa lākinna akṡaran-nāsi lā yasykurụn","at":"Dan aku mengikuti agama nenek moyangku: Ibrahim, Ishak dan Yakub. Tidak pantas bagi kami (para nabi) mempersekutukan sesuatu apa pun dengan Allah. Itu adalah dari karunia Allah kepada kami dan kepada manusia (semuanya); tetapi kebanyakan manusia tidak bersyukur."},{"id":"39","ar":"يٰصَاحِبَيِ السِّجْنِ ءَاَرْبَابٌ مُتَفَرِّقُوْنَ خَيْرٌ اَمِ اللّٰهُ الْوَاحِدُ الْقَهَّارُۗ ","bc":"yā ṣāḥibayis-sijni a arbābum mutafarriqụna khairun amillāhul-wāḥidul-qahhār","at":"Wahai kedua penghuni penjara! Manakah yang baik, tuhan-tuhan yang bermacam-macam itu ataukah Allah Yang Maha Esa, Mahaperkasa?"},{"id":"40","ar":"مَا تَعْبُدُوْنَ مِنْ دُوْنِهٖٓ اِلَّآ اَسْمَاۤءً سَمَّيْتُمُوْهَآ اَنْتُمْ وَاٰبَاۤؤُكُمْ مَّآ اَنْزَلَ اللّٰهُ بِهَا مِنْ سُلْطٰنٍۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗاَمَرَ اَلَّا تَعْبُدُوْٓا اِلَّآ اِيَّاهُ ۗذٰلِكَ الدِّيْنُ الْقَيِّمُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ ","bc":"mā ta'budụna min dụnihī illā asmā`an sammaitumụhā antum wa ābā`ukum mā anzalallāhu bihā min sulṭān, inil-ḥukmu illā lillāh, amara allā ta'budū illā iyyāh, żālikad-dīnul-qayyimu wa lākinna akṡaran-nāsi lā ya'lamụn","at":"Apa yang kamu sembah selain Dia, hanyalah nama-nama yang kamu buat-buat baik oleh kamu sendiri maupun oleh nenek moyangmu. Allah tidak menurunkan suatu keterangan pun tentang hal (nama-nama) itu. Keputusan itu hanyalah milik Allah. Dia telah memerintahkan agar kamu tidak menyembah selain Dia. Itulah agama yang lurus, tetapi kebanyakan manusia tidak mengetahui."},{"id":"41","ar":"يٰصَاحِبَيِ السِّجْنِ اَمَّآ اَحَدُكُمَا فَيَسْقِيْ رَبَّهٗ خَمْرًا ۗوَاَمَّا الْاٰخَرُ فَيُصْلَبُ فَتَأْكُلُ الطَّيْرُ مِنْ رَّأْسِهٖ ۗ قُضِيَ الْاَمْرُ الَّذِيْ فِيْهِ تَسْتَفْتِيٰنِۗ ","bc":"yā ṣāḥibayis-sijni ammā aḥadukumā fa yasqī rabbahụ khamrā, wa ammal-ākharu fa yuṣlabu fa ta`kuluṭ-ṭairu mir ra`sih, quḍiyal-amrullażī fīhi tastaftiyān","at":"Wahai kedua penghuni penjara, “Salah seorang di antara kamu, akan bertugas menyediakan minuman khamar bagi tuannya. Adapun yang seorang lagi dia akan disalib, lalu burung memakan sebagian kepalanya. Telah terjawab perkara yang kamu tanyakan (kepadaku).”"},{"id":"42","ar":"وَقَالَ لِلَّذِيْ ظَنَّ اَنَّهٗ نَاجٍ مِّنْهُمَا اذْكُرْنِيْ عِنْدَ رَبِّكَۖ فَاَنْسٰىهُ الشَّيْطٰنُ ذِكْرَ رَبِّهٖ فَلَبِثَ فِى السِّجْنِ بِضْعَ سِنِيْنَ ","bc":"wa qāla lillażī ẓanna annahụ nājim min-humażkurnī 'inda rabbika fa ansāhusy-syaiṭānu żikra rabbihī fa labiṡa fis-sijni biḍ'a sinīn","at":"Dan dia (Yusuf) berkata kepada orang yang diketahuinya akan selamat di antara mereka berdua, “Terangkanlah keadaanku kepada tuanmu.” Maka setan menjadikan dia lupa untuk menerangkan (keadaan Yusuf) kepada tuannya. Karena itu dia (Yusuf) tetap dalam penjara beberapa tahun lamanya."},{"id":"43","ar":"وَقَالَ الْمَلِكُ اِنِّيْٓ اَرٰى سَبْعَ بَقَرٰتٍ سِمَانٍ يَّأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَّسَبْعَ سُنْۢبُلٰتٍ خُضْرٍ وَّاُخَرَ يٰبِسٰتٍۗ يٰٓاَيُّهَا الْمَلَاُ اَفْتُوْنِيْ فِيْ رُؤْيَايَ اِنْ كُنْتُمْ لِلرُّءْيَا تَعْبُرُوْنَ","bc":"wa qālal-maliku innī arā sab'a baqarātin simāniy ya`kuluhunna sab'un 'ijāfuw wa sab'a sumbulātin khuḍriw wa ukhara yābisāt, yā ayyuhal-mala`u aftụnī fī ru`yāya ing kuntum lir-ru`yā ta'burụn","at":"Dan raja berkata (kepada para pemuka kaumnya), “Sesungguhnya aku bermimpi melihat tujuh ekor sapi betina yang gemuk dimakan oleh tujuh ekor sapi betina yang kurus; tujuh tangkai (gandum) yang hijau dan (tujuh tangkai) lainnya yang kering. Wahai orang yang terkemuka! Terangkanlah kepadaku tentang takwil mimpiku itu jika kamu dapat menakwilkan mimpi.” "},{"id":"44","ar":"قَالُوْٓا اَضْغَاثُ اَحْلَامٍ ۚوَمَا نَحْنُ بِتَأْوِيْلِ الْاَحْلَامِ بِعٰلِمِيْنَ ","bc":"qālū aḍgāṡu aḥlām, wa mā naḥnu bita`wīlil-aḥlāmi bi'ālimīn","at":"Mereka menjawab, “(Itu) mimpi-mimpi yang kosong dan kami tidak mampu menakwilkan mimpi itu.”"},{"id":"45","ar":"وَقَالَ الَّذِيْ نَجَا مِنْهُمَا وَادَّكَرَ بَعْدَ اُمَّةٍ اَنَا۠ اُنَبِّئُكُمْ بِتَأْوِيْلِهٖ فَاَرْسِلُوْنِ ","bc":"wa qālallażī najā min-humā waddakara ba'da ummatin ana unabbi`ukum bita`wīlihī fa arsilụn","at":"Dan berkatalah orang yang selamat di antara mereka berdua dan teringat (kepada Yusuf) setelah beberapa waktu lamanya, “Aku akan memberitahukan kepadamu tentang (orang yang pandai) menakwilkan mimpi itu, maka utuslah aku (kepadanya).”"},{"id":"46","ar":"يُوْسُفُ اَيُّهَا الصِّدِّيْقُ اَفْتِنَا فِيْ سَبْعِ بَقَرٰتٍ سِمَانٍ يَّأْكُلُهُنَّ سَبْعٌ عِجَافٌ وَّسَبْعِ سُنْۢبُلٰتٍ خُضْرٍ وَّاُخَرَ يٰبِسٰتٍۙ لَّعَلِّيْٓ اَرْجِعُ اِلَى النَّاسِ لَعَلَّهُمْ يَعْلَمُوْنَ ","bc":"yụsufu ayyuhaṣ-ṣiddīqu aftinā fī sab'i baqarātin simāniy ya`kuluhunna sab'un 'ijāfuw wa sab'i sumbulātin khuḍriw wa ukhara yābisātil la'allī arji'u ilan-nāsi la'allahum ya'lamụn","at":"”Yusuf, wahai orang yang sangat dipercaya! Terangkanlah kepada kami (takwil mimpi) tentang tujuh ekor sapi betina yang gemuk yang dimakan oleh tujuh (ekor sapi betina) yang kurus, tujuh tangkai (gandum) yang hijau dan (tujuh tangkai) lainnya yang kering agar aku kembali kepada orang-orang itu, agar mereka mengetahui.”"},{"id":"47","ar":"قَالَ تَزْرَعُوْنَ سَبْعَ سِنِيْنَ دَاَبًاۚ فَمَا حَصَدْتُّمْ فَذَرُوْهُ فِيْ سُنْۢبُلِهٖٓ اِلَّا قَلِيْلًا مِّمَّا تَأْكُلُوْنَ ","bc":"qāla tazra'ụna sab'a sinīna da`abā, fa mā ḥaṣattum fa żarụhu fī sumbulihī illā qalīlam mimmā ta`kulụn","at":"Dia (Yusuf) berkata, “Agar kamu bercocok tanam tujuh tahun (berturut-turut) sebagaimana biasa; kemudian apa yang kamu tuai hendaklah kamu biarkan di tangkainya kecuali sedikit untuk kamu makan."},{"id":"48","ar":"ثُمَّ يَأْتِيْ مِنْۢ بَعْدِ ذٰلِكَ سَبْعٌ شِدَادٌ يَّأْكُلْنَ مَا قَدَّمْتُمْ لَهُنَّ اِلَّا قَلِيْلًا مِّمَّا تُحْصِنُوْنَ","bc":"ṡumma ya`tī mim ba'di żālika sab'un syidāduy ya`kulna mā qaddamtum lahunna illā qalīlam mimmā tuḥṣinụn","at":"Kemudian setelah itu akan datang tujuh (tahun) yang sangat sulit, yang menghabiskan apa yang kamu simpan untuk menghadapinya (tahun sulit), kecuali sedikit dari apa (bibit gandum) yang kamu simpan. "},{"id":"49","ar":"ثُمَّ يَأْتِيْ مِنْۢ بَعْدِ ذٰلِكَ عَامٌ فِيْهِ يُغَاثُ النَّاسُ وَفِيْهِ يَعْصِرُوْنَ","bc":"ṡumma ya`tī mim ba'di żālika 'āmun fīhi yugāṡun-nāsu wa fīhi ya'ṣirụn","at":"Setelah itu akan datang tahun, di mana manusia diberi hujan (dengan cukup) dan pada masa itu mereka memeras (anggur).”"},{"id":"50","ar":"وَقَالَ الْمَلِكُ ائْتُوْنِيْ بِهٖ ۚفَلَمَّا جَاۤءَهُ الرَّسُوْلُ قَالَ ارْجِعْ اِلٰى رَبِّكَ فَسْـَٔلْهُ مَا بَالُ النِّسْوَةِ الّٰتِيْ قَطَّعْنَ اَيْدِيَهُنَّ ۗاِنَّ رَبِّيْ بِكَيْدِهِنَّ عَلِيْمٌ ","bc":"wa qālal-maliku`tụnī bih, fa lammā jā`ahur-rasụlu qālarji' ilā rabbika fas`al-hu mā bālun-niswatillātī qaṭṭa'na aidiyahunn, inna rabbī bikaidihinna 'alīm","at":"Dan raja berkata, “Bawalah dia kepadaku.” Ketika utusan itu datang kepadanya, dia (Yusuf) berkata, “Kembalilah kepada tuanmu dan tanyakan kepadanya bagaimana halnya perempuan-perempuan yang telah melukai tangannya. Sungguh, Tuhanku Maha Mengetahui tipu daya mereka.”"},{"id":"51","ar":"قَالَ مَا خَطْبُكُنَّ اِذْ رَاوَدْتُّنَّ يُوْسُفَ عَنْ نَّفْسِهٖۗ قُلْنَ حَاشَ لِلّٰهِ مَا عَلِمْنَا عَلَيْهِ مِنْ سُوْۤءٍ ۗقَالَتِ امْرَاَتُ الْعَزِيْزِ الْـٰٔنَ حَصْحَصَ الْحَقُّۖ اَنَا۠ رَاوَدْتُّهٗ عَنْ نَّفْسِهٖ وَاِنَّهٗ لَمِنَ الصّٰدِقِيْنَ ","bc":"qāla mā khaṭbukunna iż rāwattunna yụsufa 'an nafsih, qulna ḥāsya lillāhi mā 'alimnā 'alaihi min sū`, qālatimra`atul-'azīzil-āna ḥaṣ-ḥaṣal-ḥaqqu ana rāwattuhụ 'an nafsihī wa innahụ laminaṣ-ṣādiqīn","at":"Dia (raja) berkata (kepada perempuan-perempuan itu), “Bagaimana keadaanmu ketika kamu menggoda Yusuf untuk menundukkan dirinya?” Mereka berkata, “Mahasempurna Allah, kami tidak mengetahui sesuatu keburukan darinya.” Istri Al-Aziz berkata, “Sekarang jelaslah kebenaran itu, akulah yang menggoda dan merayunya, dan sesungguhnya dia termasuk orang yang benar.”"},{"id":"52","ar":"ذٰلِكَ لِيَعْلَمَ اَنِّيْ لَمْ اَخُنْهُ بِالْغَيْبِ وَاَنَّ اللّٰهَ لَا يَهْدِيْ كَيْدَ الْخَاۤىِٕنِيْنَ ۔","bc":"żālika liya'lama annī lam akhun-hu bil-gaibi wa annallāha lā yahdī kaidal-khā`inīn","at":"(Yusuf berkata), “Yang demikian itu agar dia (Al-Aziz) mengetahui bahwa aku benar-benar tidak mengkhianatinya ketika dia tidak ada (di rumah), dan bahwa Allah tidak meridai tipu daya orang-orang yang berkhianat. "},{"id":"53","ar":"۞ وَمَآ اُبَرِّئُ نَفْسِيْۚ اِنَّ النَّفْسَ لَاَمَّارَةٌ ۢ بِالسُّوْۤءِ اِلَّا مَا رَحِمَ رَبِّيْۗ اِنَّ رَبِّيْ غَفُوْرٌ رَّحِيْمٌ","bc":"wa mā ubarri`u nafsī, innan-nafsa la`ammāratum bis-sū`i illā mā raḥima rabbī, inna rabbī gafụrur raḥīm","at":"Dan aku tidak (menyatakan) diriku bebas (dari kesalahan), karena sesungguhnya nafsu itu selalu mendorong kepada kejahatan, kecuali (nafsu) yang diberi rahmat oleh Tuhanku. Sesungguhnya Tuhanku Maha Pengampun, Maha Penyayang."},{"id":"54","ar":"وَقَالَ الْمَلِكُ ائْتُوْنِيْ بِهٖٓ اَسْتَخْلِصْهُ لِنَفْسِيْۚ فَلَمَّا كَلَّمَهٗ قَالَ اِنَّكَ الْيَوْمَ لَدَيْنَا مَكِيْنٌ اَمِيْنٌ","bc":"wa qālal-maliku`tụnī bihī astakhliṣ-hu linafsī, fa lammā kallamahụ qāla innakal-yauma ladainā makīnun amīn","at":"Dan raja berkata, “Bawalah dia (Yusuf) kepadaku, agar aku memilih dia (sebagai orang yang dekat) kepadaku.” Ketika dia (raja) telah bercakap-cakap dengan dia, dia (raja) berkata, “Sesungguhnya kamu (mulai) hari ini menjadi seorang yang berkedudukan tinggi di lingkungan kami dan dipercaya.”"},{"id":"55","ar":"قَالَ اجْعَلْنِيْ عَلٰى خَزَاۤىِٕنِ الْاَرْضِۚ اِنِّيْ حَفِيْظٌ عَلِيْمٌ","bc":"qālaj'alnī 'alā khazā`inil-arḍ, innī ḥafīẓun 'alīm","at":"Dia (Yusuf) berkata, “Jadikanlah aku bendaharawan negeri (Mesir); karena sesungguhnya aku adalah orang yang pandai menjaga, dan berpengetahuan.”"},{"id":"56","ar":"وَكَذٰلِكَ مَكَّنَّا لِيُوْسُفَ فِى الْاَرْضِ يَتَبَوَّاُ مِنْهَا حَيْثُ يَشَاۤءُۗ نُصِيْبُ بِرَحْمَتِنَا مَنْ نَّشَاۤءُ وَلَا نُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ","bc":"wa każālika makkannā liyụsufa fil-arḍi yatabawwa`u min-hā ḥaiṡu yasyā`, nuṣību biraḥmatinā man nasyā`u wa lā nuḍī'u ajral-muḥsinīn","at":"Dan demikianlah Kami memberi kedudukan kepada Yusuf di negeri ini (Mesir); untuk tinggal di mana saja yang dia kehendaki. Kami melimpahkan rahmat kepada siapa yang Kami kehendaki dan Kami tidak menyia-nyiakan pahala orang yang berbuat baik."},{"id":"57","ar":"وَلَاَجْرُ الْاٰخِرَةِ خَيْرٌ لِّلَّذِيْنَ اٰمَنُوْا وَكَانُوْا يَتَّقُوْنَ","bc":"wa la`ajrul-ākhirati khairul lillażīna āmanụ wa kānụ yattaqụn","at":"Dan sungguh, pahala akhirat itu lebih baik bagi orang-orang yang beriman dan selalu bertakwa."},{"id":"58","ar":"وَجَاۤءَ اِخْوَةُ يُوْسُفَ فَدَخَلُوْا عَلَيْهِ فَعَرَفَهُمْ وَهُمْ لَهٗ مُنْكِرُوْنَ","bc":"wa jā`a ikhwatu yụsufa fa dakhalụ 'alaihi fa 'arafahum wa hum lahụ mungkirụn","at":"Dan saudara-saudara Yusuf datang (ke Mesir) lalu mereka masuk ke (tempat)nya. Maka dia (Yusuf) mengenal mereka, sedang mereka tidak mengenalinya (lagi) kepadanya."},{"id":"59","ar":"وَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ قَالَ ائْتُوْنِيْ بِاَخٍ لَّكُمْ مِّنْ اَبِيْكُمْ ۚ اَلَا تَرَوْنَ اَنِّيْٓ اُوْفِى الْكَيْلَ وَاَنَا۠ خَيْرُ الْمُنْزِلِيْنَ ","bc":"wa lammā jahhazahum bijahāzihim qāla`tụnī bi`akhil lakum min abīkum, alā tarauna annī ụfil-kaila wa ana khairul-munzilīn","at":"Dan ketika dia (Yusuf) menyiapkan bahan makanan untuk mereka, dia berkata, “Bawalah kepadaku saudaramu yang seayah dengan kamu (Bunyamin), tidakkah kamu melihat bahwa aku menyempurnakan takaran dan aku adalah penerima tamu yang terbaik?"},{"id":"60","ar":"فَاِنْ لَّمْ تَأْتُوْنِيْ بِهٖ فَلَا كَيْلَ لَكُمْ عِنْدِيْ وَلَا تَقْرَبُوْنِ","bc":"fa il lam ta`tụnī bihī fa lā kaila lakum 'indī wa lā taqrabụn","at":"Maka jika kamu tidak membawanya kepadaku, maka kamu tidak akan mendapat jatah (gandum) lagi dariku dan jangan kamu mendekatiku.”"},{"id":"61","ar":"قَالُوْا سَنُرَاوِدُ عَنْهُ اَبَاهُ وَاِنَّا لَفَاعِلُوْنَ","bc":"qālụ sanurāwidu 'an-hu abāhu wa innā lafā'ilụn","at":"Mereka berkata, “Kami akan membujuk ayahnya (untuk membawanya) dan kami benar-benar akan melaksanakannya.”"},{"id":"62","ar":"وَقَالَ لِفِتْيٰنِهِ اجْعَلُوْا بِضَاعَتَهُمْ فِيْ رِحَالِهِمْ لَعَلَّهُمْ يَعْرِفُوْنَهَآ اِذَا انْقَلَبُوْٓا اِلٰٓى اَهْلِهِمْ لَعَلَّهُمْ يَرْجِعُوْنَ","bc":"wa qāla lifityānihij'alụ biḍā'atahum fī riḥālihim la'allahum ya'rifụnahā iżangqalabū ilā ahlihim la'allahum yarji'ụn","at":"Dan dia (Yusuf) berkata kepada pelayan-pelayannya, “Masukkanlah barang-barang (penukar) mereka ke dalam karung-karungnya, agar mereka mengetahuinya apabila telah kembali kepada keluarganya, mudah-mudahan mereka kembali lagi.”"},{"id":"63","ar":"فَلَمَّا رَجَعُوْٓا اِلٰٓى اَبِيْهِمْ قَالُوْا يٰٓاَبَانَا مُنِعَ مِنَّا الْكَيْلُ فَاَرْسِلْ مَعَنَآ اَخَانَا نَكْتَلْ وَاِنَّا لَهٗ لَحٰفِظُوْنَ","bc":"fa lammā raja'ū ilā abīhim qālụ yā abānā muni'a minnal-kailu fa arsil ma'anā akhānā naktal wa innā lahụ laḥāfiẓụn","at":"Maka ketika mereka telah kembali kepada ayahnya (Yakub) mereka berkata, “Wahai ayah kami! Kami tidak akan mendapat jatah (gandum) lagi, (jika tidak membawa saudara kami), sebab itu biarkanlah saudara kami pergi bersama kami agar kami mendapat jatah, dan kami benar-benar akan menjaganya.”"},{"id":"64","ar":"قَالَ هَلْ اٰمَنُكُمْ عَلَيْهِ اِلَّا كَمَآ اَمِنْتُكُمْ عَلٰٓى اَخِيْهِ مِنْ قَبْلُۗ فَاللّٰهُ خَيْرٌ حٰفِظًا وَّهُوَ اَرْحَمُ الرّٰحِمِيْنَ","bc":"qāla hal āmanukum 'alaihi illā kamā amintukum 'alā akhīhi ming qabl, fallāhu khairun ḥāfiẓaw wa huwa ar-ḥamur-rāḥimīn","at":"Dia (Yakub) berkata, “Bagaimana aku akan mempercayakannya (Bunyamin) kepadamu, seperti aku telah mempercayakan saudaranya (Yusuf) kepada kamu dahulu?” Maka Allah adalah penjaga yang terbaik dan Dia Maha Penyayang di antara para penyayang."},{"id":"65","ar":"وَلَمَّا فَتَحُوْا مَتَاعَهُمْ وَجَدُوْا بِضَاعَتَهُمْ رُدَّتْ اِلَيْهِمْۗ قَالُوْا يٰٓاَبَانَا مَا نَبْغِيْۗ هٰذِهٖ بِضَاعَتُنَا رُدَّتْ اِلَيْنَا وَنَمِيْرُ اَهْلَنَا وَنَحْفَظُ اَخَانَا وَنَزْدَادُ كَيْلَ بَعِيْرٍۗ ذٰلِكَ كَيْلٌ يَّسِيْرٌ","bc":"wa lammā fataḥụ matā'ahum wajadụ biḍā'atahum ruddat ilaihim, qālụ yā abānā mā nabgī, hāżihī biḍā'atunā ruddat ilainā wa namīru ahlanā wa naḥfaẓu akhānā wa nazdādu kaila ba'īr, żālika kailuy yasīr","at":"Dan ketika mereka membuka barang-barangnya, mereka menemukan barang-barang (penukar) mereka dikembalikan kepada mereka. Mereka berkata, “Wahai ayah kami! Apalagi yang kita inginkan. Ini barang-barang kita dikembalikan kepada kita, dan kita akan dapat memberi makan keluarga kita, dan kami akan memelihara saudara kami, dan kita akan mendapat tambahan jatah (gandum) seberat beban seekor unta. Itu suatu hal yang mudah (bagi raja Mesir).”"},{"id":"66","ar":"قَالَ لَنْ اُرْسِلَهٗ مَعَكُمْ حَتّٰى تُؤْتُوْنِ مَوْثِقًا مِّنَ اللّٰهِ لَتَأْتُنَّنِيْ بِهٖٓ اِلَّآ اَنْ يُّحَاطَ بِكُمْۚ فَلَمَّآ اٰتَوْهُ مَوْثِقَهُمْ قَالَ اللّٰهُ عَلٰى مَا نَقُوْلُ وَكِيْلٌ","bc":"qāla lan ursilahụ ma'akum ḥattā tu`tụni mauṡiqam minallāhi lata`tunnanī bihī illā ay yuḥāṭa bikum, fa lammā ātauhu mauṡiqahum qālallāhu 'alā mā naqụlu wakīl","at":"Dia (Yakub) berkata, “Aku tidak akan melepaskannya (pergi) bersama kamu, sebelum kamu bersumpah kepadaku atas (nama) Allah, bahwa kamu pasti akan membawanya kepadaku kembali, kecuali jika kamu dikepung (musuh).” Setelah mereka mengucapkan sumpah, dia (Yakub) berkata, “Allah adalah saksi terhadap apa yang kita ucapkan.”"},{"id":"67","ar":"وَقَالَ يٰبَنِيَّ لَا تَدْخُلُوْا مِنْۢ بَابٍ وَّاحِدٍ وَّادْخُلُوْا مِنْ اَبْوَابٍ مُّتَفَرِّقَةٍۗ وَمَآ اُغْنِيْ عَنْكُمْ مِّنَ اللّٰهِ مِنْ شَيْءٍۗ اِنِ الْحُكْمُ اِلَّا لِلّٰهِ ۗعَلَيْهِ تَوَكَّلْتُ وَعَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُوْنَ","bc":"wa qāla yā baniyya lā tadkhulụ mim bābiw wāḥidiw wadkhulụ min abwābim mutafarriqah, wa mā ugnī 'angkum minallāhi min syaī`, inil-ḥukmu illā lillāh, 'alaihi tawakkaltu wa 'alaihi falyatawakkalil-mutawakkilụn","at":"Dan dia (Yakub) berkata, “Wahai anak-anakku! Janganlah kamu masuk dari satu pintu gerbang, dan masuklah dari pintu-pintu gerbang yang berbeda; namun demikian aku tidak dapat mempertahankan kamu sedikit pun dari (takdir) Allah. Keputusan itu hanyalah bagi Allah. Kepada-Nya aku bertawakal dan kepada-Nya pula bertawakallah orang-orang yang bertawakal.”"},{"id":"68","ar":"وَلَمَّا دَخَلُوْا مِنْ حَيْثُ اَمَرَهُمْ اَبُوْهُمْۗ مَا كَانَ يُغْنِيْ عَنْهُمْ مِّنَ اللّٰهِ مِنْ شَيْءٍ اِلَّا حَاجَةً فِيْ نَفْسِ يَعْقُوْبَ قَضٰىهَاۗ وَاِنَّهٗ لَذُوْ عِلْمٍ لِّمَا عَلَّمْنٰهُ وَلٰكِنَّ اَكْثَرَ النَّاسِ لَا يَعْلَمُوْنَ","bc":"wa lammā dakhalụ min ḥaiṡu amarahum abụhum, mā kāna yugnī 'an-hum minallāhi min syai`in illā ḥājatan fī nafsi ya'qụba qaḍāhā, wa innahụ lażụ 'ilmil limā 'allamnāhu wa lākinna akṡaran-nāsi lā ya'lamụn","at":"Dan ketika mereka masuk sesuai dengan perintah ayah mereka, (masuknya mereka itu) tidak dapat menolak sedikit pun keputusan Allah, (tetapi itu) hanya suatu keinginan pada diri Yakub yang telah ditetapkannya. Dan sesungguhnya dia mempunyai pengetahuan, karena Kami telah mengajarkan kepadanya. Tetapi kebanyakan manusia tidak mengetahui."},{"id":"69","ar":"وَلَمَّا دَخَلُوْا عَلٰى يُوْسُفَ اٰوٰٓى اِلَيْهِ اَخَاهُ قَالَ اِنِّيْٓ اَنَا۠ اَخُوْكَ فَلَا تَبْتَىِٕسْ بِمَا كَانُوْا يَعْمَلُوْنَ","bc":"wa lammā dakhalụ 'alā yụsufa āwā ilaihi akhāhu qāla innī ana akhụka fa lā tabta`is bimā kānụ ya'malụn","at":"Dan ketika mereka masuk ke (tempat) Yusuf, dia menempatkan saudaranya (Bunyamin) di tempatnya, dia (Yusuf) berkata, “Sesungguhnya aku adalah saudaramu, jangan engkau bersedih hati terhadap apa yang telah mereka kerjakan.”"},{"id":"70","ar":"فَلَمَّا جَهَّزَهُمْ بِجَهَازِهِمْ جَعَلَ السِّقَايَةَ فِيْ رَحْلِ اَخِيْهِ ثُمَّ اَذَّنَ مُؤَذِّنٌ اَيَّتُهَا الْعِيْرُ اِنَّكُمْ لَسَارِقُوْنَ","bc":"fa lammā jahhazahum bijahāzihim ja'alas-siqāyata fī raḥli akhīhi ṡumma ażżana mu`ażżinun ayyatuhal-'īru innakum lasāriqụn","at":"Maka ketika telah disiapkan bahan makanan untuk mereka, dia (Yusuf) memasukkan piala ke dalam karung saudaranya. Kemudian berteriaklah seseorang yang menyerukan, “Wahai kafilah! Sesungguhnya kamu pasti pencuri.” "},{"id":"71","ar":"قَالُوْا وَاَقْبَلُوْا عَلَيْهِمْ مَّاذَا تَفْقِدُوْنَ","bc":"qālụ wa aqbalụ 'alaihim māżā tafqidụn","at":"Mereka bertanya, sambil menghadap kepada mereka (yang menuduh), “Kamu kehilangan apa?”"},{"id":"72","ar":"قَالُوْا نَفْقِدُ صُوَاعَ الْمَلِكِ وَلِمَنْ جَاۤءَ بِهٖ حِمْلُ بَعِيْرٍ وَّاَنَا۠ بِهٖ زَعِيْمٌ","bc":"qālụ nafqidu ṣuwā'al-maliki wa liman jā`a bihī ḥimlu ba'īriw wa ana bihī za'īm","at":"Mereka menjawab, “Kami kehilangan piala raja, dan siapa yang dapat mengembalikannya akan memperoleh (bahan makanan seberat) beban unta, dan aku jamin itu.”"},{"id":"73","ar":"قَالُوْا تَاللّٰهِ لَقَدْ عَلِمْتُمْ مَّا جِئْنَا لِنُفْسِدَ فِى الْاَرْضِ وَمَا كُنَّا سَارِقِيْنَ","bc":"qālụ tallāhi laqad 'alimtum mā ji`nā linufsida fil-arḍi wa mā kunnā sāriqīn","at":"Mereka (saudara-saudara Yusuf) menjawab, “Demi Allah, sungguh, kamu mengetahui bahwa kami datang bukan untuk berbuat kerusakan di negeri ini dan kami bukanlah para pencuri.”"},{"id":"74","ar":"قَالُوْا فَمَا جَزَاۤؤُهٗٓ اِنْ كُنْتُمْ كٰذِبِيْنَ","bc":"qālụ fa mā jazā`uhū ing kuntum kāżibīn","at":"Mereka berkata, “Tetapi apa hukumannya jika kamu dusta?”"},{"id":"75","ar":"قَالُوْا جَزَاۤؤُهٗ مَنْ وُّجِدَ فِيْ رَحْلِهٖ فَهُوَ جَزَاۤؤُهٗ ۗ كَذٰلِكَ نَجْزِى الظّٰلِمِيْنَ","bc":"qālụ jazā`uhụ maw wujida fī raḥlihī fa huwa jazā`uh, każālika najziẓ-ẓālimīn","at":"Mereka menjawab, “Hukumannya ialah pada siapa ditemukan dalam karungnya (barang yang hilang itu), maka dia sendirilah menerima hukumannya. Demikianlah kami memberi hukuman kepada orang-orang zalim.”"},{"id":"76","ar":"فَبَدَاَ بِاَوْعِيَتِهِمْ قَبْلَ وِعَاۤءِ اَخِيْهِ ثُمَّ اسْتَخْرَجَهَا مِنْ وِّعَاۤءِ اَخِيْهِۗ كَذٰلِكَ كِدْنَا لِيُوْسُفَۗ مَا كَانَ لِيَأْخُذَ اَخَاهُ فِيْ دِيْنِ الْمَلِكِ اِلَّآ اَنْ يَّشَاۤءَ اللّٰهُ ۗنَرْفَعُ دَرَجَاتٍ مَّنْ نَّشَاۤءُۗ وَفَوْقَ كُلِّ ذِيْ عِلْمٍ عَلِيْمٌ","bc":"fa bada`a bi`au'iyatihim qabla wi'ā`i akhīhi ṡummastakhrajahā miw wi'ā`i akhīh, każālika kidnā liyụsuf, mā kāna liya`khuża akhāhu fī dīnil-maliki illā ay yasyā`allāh, narfa'u darajātim man nasyā`, wa fauqa kulli żī 'ilmin 'alīm","at":"Maka mulailah dia (memeriksa) karung-karung mereka sebelum (memeriksa) karung saudaranya sendiri, kemudian dia mengeluarkan (piala raja) itu dari karung saudaranya. Demikianlah Kami mengatur (rencana) untuk Yusuf. Dia tidak dapat menghukum saudaranya menurut undang-undang raja, kecuali Allah menghendakinya. Kami angkat derajat orang yang Kami kehendaki; dan di atas setiap orang yang berpengetahuan ada yang lebih mengetahui."},{"id":"77","ar":"۞ قَالُوْٓا اِنْ يَّسْرِقْ فَقَدْ سَرَقَ اَخٌ لَّهٗ مِنْ قَبْلُۚ فَاَسَرَّهَا يُوْسُفُ فِيْ نَفْسِهٖ وَلَمْ يُبْدِهَا لَهُمْۚ قَالَ اَنْتُمْ شَرٌّ مَّكَانًا ۚوَاللّٰهُ اَعْلَمُ بِمَا تَصِفُوْنَ","bc":"qālū iy yasriq fa qad saraqa akhul lahụ ming qabl, fa asarrahā yụsufu fī nafsihī wa lam yubdihā lahum, qāla antum syarrum makānā, wallāhu a'lamu bimā taṣifụn","at":"Mereka berkata, “Jika dia mencuri, maka sungguh sebelum itu saudaranya pun pernah pula mencuri.” Maka Yusuf menyembunyikan (kejengkelan) dalam hatinya dan tidak ditampakkannya kepada mereka. Dia berkata (dalam hatinya), “Kedudukanmu justru lebih buruk. Dan Allah Maha Mengetahui apa yang kamu terangkan.”"},{"id":"78","ar":"قَالُوْا يٰٓاَيُّهَا الْعَزِيْزُ اِنَّ لَهٗٓ اَبًا شَيْخًا كَبِيْرًا فَخُذْ اَحَدَنَا مَكَانَهٗ ۚاِنَّا نَرٰىكَ مِنَ الْمُحْسِنِيْنَ","bc":"qālụ yā ayyuhal-'azīzu inna lahū aban syaikhang kabīran fa khuż aḥadanā makānah, innā narāka minal-muḥsinīn","at":"Mereka berkata, “Wahai Al-Aziz! Dia mempunyai ayah yang sudah lanjut usia, karena itu ambillah salah seorang di antara kami sebagai gantinya, sesungguhnya kami melihat engkau termasuk orang-orang yang berbuat baik.”"},{"id":"79","ar":"قَالَ مَعَاذَ اللّٰهِ اَنْ نَّأْخُذَ اِلَّا مَنْ وَّجَدْنَا مَتَاعَنَا عِنْدَهٗٓ ۙاِنَّآ اِذًا لَّظٰلِمُوْنَ","bc":"qāla ma'āżallāhi an na`khuża illā maw wajadnā matā'anā 'indahū innā iżal laẓālimụn","at":"Dia (Yusuf) berkata, “Aku memohon perlindungan kepada Allah dari menahan (seseorang), kecuali orang yang kami temukan harta kami padanya, jika kami (berbuat) demikian, berarti kami orang yang zalim.”"},{"id":"80","ar":"فَلَمَّا اسْتَا۟يْـَٔسُوْا مِنْهُ خَلَصُوْا نَجِيًّاۗ قَالَ كَبِيْرُهُمْ اَلَمْ تَعْلَمُوْٓا اَنَّ اَبَاكُمْ قَدْ اَخَذَ عَلَيْكُمْ مَّوْثِقًا مِّنَ اللّٰهِ وَمِنْ قَبْلُ مَا فَرَّطْتُّمْ فِيْ يُوْسُفَ فَلَنْ اَبْرَحَ الْاَرْضَ حَتّٰى يَأْذَنَ لِيْٓ اَبِيْٓ اَوْ يَحْكُمَ اللّٰهُ لِيْۚ وَهُوَ خَيْرُ الْحٰكِمِيْنَ","bc":"fa lammastai`asụ min-hu khalaṣụ najiyyā, qāla kabīruhum a lam ta'lamū anna abākum qad akhaża 'alaikum mauṡiqam minallāhi wa ming qablu mā farrattum fī yụsufa fa lan abraḥal-arḍa ḥattā ya`żana lī abī au yaḥkumallāhu lī, wa huwa khairul-ḥākimīn","at":"Maka ketika mereka berputus asa darinya (putusan Yusuf) mereka menyendiri (sambil berunding) dengan berbisik-bisik. Yang tertua di antara mereka berkata, “Tidakkah kamu ketahui bahwa ayahmu telah mengambil janji dari kamu dengan (nama) Allah dan sebelum itu kamu telah menyia-nyiakan Yusuf? Sebab itu aku tidak akan meninggalkan negeri ini (Mesir), sampai ayahku mengizinkan (untuk kembali), atau Allah memberi keputusan terhadapku. Dan Dia adalah hakim yang terbaik.”"},{"id":"81","ar":"اِرْجِعُوْٓا اِلٰٓى اَبِيْكُمْ فَقُوْلُوْا يٰٓاَبَانَآ اِنَّ ابْنَكَ سَرَقَۚ وَمَا شَهِدْنَآ اِلَّا بِمَا عَلِمْنَا وَمَا كُنَّا لِلْغَيْبِ حٰفِظِيْنَ","bc":"irji'ū ilā abīkum fa qụlụ yā abānā innabnaka saraq, wa mā syahidnā illā bimā 'alimnā wa mā kunnā lil-gaibi ḥāfiẓīn","at":"Kembalilah kepada ayahmu dan katakanlah, “Wahai ayah kami! Sesungguhnya anakmu telah mencuri dan kami hanya menyaksikan apa yang kami ketahui dan kami tidak mengetahui apa yang di balik itu."},{"id":"82","ar":"وَسْـَٔلِ الْقَرْيَةَ الَّتِيْ كُنَّا فِيْهَا وَالْعِيْرَ الَّتِيْٓ اَقْبَلْنَا فِيْهَاۗ وَاِنَّا لَصٰدِقُوْنَ","bc":"was`alil-qaryatallatī kunnā fīhā wal-'īrallatī aqbalnā fīhā, wa innā laṣādiqụn","at":"Dan tanyalah (penduduk) negeri tempat kami berada, dan kafilah yang datang bersama kami. Dan kami adalah orang yang benar.”"},{"id":"83","ar":"قَالَ بَلْ سَوَّلَتْ لَكُمْ اَنْفُسُكُمْ اَمْرًاۗ فَصَبْرٌ جَمِيْلٌ ۗعَسَى اللّٰهُ اَنْ يَّأْتِيَنِيْ بِهِمْ جَمِيْعًاۗ اِنَّهٗ هُوَ الْعَلِيْمُ الْحَكِيْمُ","bc":"qāla bal sawwalat lakum anfusukum amrā, fa ṣabrun jamīl, 'asallāhu ay ya`tiyanī bihim jamī'ā, innahụ huwal-'alīmul-ḥakīm","at":"Dia (Yakub) berkata, “Sebenarnya hanya dirimu sendiri yang memandang baik urusan (yang buruk) itu. Maka (kesabaranku) adalah kesabaran yang baik. Mudah-mudahan Allah mendatangkan mereka semuanya kepadaku. Sungguh, Dialah Yang Maha Mengetahui, Mahabijaksana.”"},{"id":"84","ar":"وَتَوَلّٰى عَنْهُمْ وَقَالَ يٰٓاَسَفٰى عَلٰى يُوْسُفَ وَابْيَضَّتْ عَيْنٰهُ مِنَ الْحُزْنِ فَهُوَ كَظِيْمٌ ","bc":"wa tawallā 'an-hum wa qāla yā asafā 'alā yụsufa wabyaḍḍat 'aināhu minal-ḥuzni fa huwa kaẓīm","at":"Dan dia (Yakub) berpaling dari mereka (anak-anaknya) seraya berkata, “Aduhai dukacitaku terhadap Yusuf,” dan kedua matanya menjadi putih karena sedih. Dia diam menahan amarah (terhadap anak-anaknya)."},{"id":"85","ar":"قَالُوْا تَاللّٰهِ تَفْتَؤُا تَذْكُرُ يُوْسُفَ حَتّٰى تَكُوْنَ حَرَضًا اَوْ تَكُوْنَ مِنَ الْهَالِكِيْنَ","bc":"qālụ tallāhi tafta`u tażkuru yụsufa ḥattā takụna ḥaraḍan au takụna minal-hālikīn","at":"Mereka berkata, “Demi Allah, engkau tidak henti-hentinya mengingat Yusuf, sehingga engkau (mengidap) penyakit berat atau engkau termasuk orang-orang yang akan binasa.”"},{"id":"86","ar":"قَالَ اِنَّمَآ اَشْكُوْا بَثِّيْ وَحُزْنِيْٓ اِلَى اللّٰهِ وَاَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ","bc":"qāla innamā asykụ baṡṡī wa ḥuznī ilallāhi wa a'lamu minallāhi mā lā ta'lamụn","at":"Dia (Yakub) menjawab, “Hanya kepada Allah aku mengadukan kesusahan dan kesedihanku. Dan aku mengetahui dari Allah apa yang tidak kamu ketahui."},{"id":"87","ar":"يٰبَنِيَّ اذْهَبُوْا فَتَحَسَّسُوْا مِنْ يُّوْسُفَ وَاَخِيْهِ وَلَا تَا۟يْـَٔسُوْا مِنْ رَّوْحِ اللّٰهِ ۗاِنَّهٗ لَا يَا۟يْـَٔسُ مِنْ رَّوْحِ اللّٰهِ اِلَّا الْقَوْمُ الْكٰفِرُوْنَ","bc":"yā baniyyaż-habụ fa taḥassasụ miy yụsufa wa akhīhi wa lā tai`asụ mir rauḥillāh, innahụ lā yai`asu mir rauḥillāhi illal-qaumul-kāfirụn","at":"Wahai anak-anakku! Pergilah kamu, carilah (berita) tentang Yusuf dan saudaranya dan jangan kamu berputus asa dari rahmat Allah. Sesungguhnya yang berputus asa dari rahmat Allah, hanyalah orang-orang yang kafir.”"},{"id":"88","ar":"فَلَمَّا دَخَلُوْا عَلَيْهِ قَالُوْا يٰٓاَيُّهَا الْعَزِيْزُ مَسَّنَا وَاَهْلَنَا الضُّرُّ وَجِئْنَا بِبِضَاعَةٍ مُّزْجٰىةٍ فَاَوْفِ لَنَا الْكَيْلَ وَتَصَدَّقْ عَلَيْنَاۗ اِنَّ اللّٰهَ يَجْزِى الْمُتَصَدِّقِيْنَ","bc":"fa lammā dakhalụ 'alaihi qālụ yā ayyuhal-'azīzu massanā wa ahlanaḍ-ḍurru wa ji`nā bibiḍā'atim muzjātin fa aufi lanal-kaila wa taṣaddaq 'alainā, innallāha yajzil-mutaṣaddiqīn","at":"Maka ketika mereka masuk ke (tempat) Yusuf, mereka berkata, “Wahai Al-Aziz! Kami dan keluarga kami telah ditimpa kesengsaraan dan kami datang membawa barang-barang yang tidak berharga, maka penuhilah jatah (gandum) untuk kami, dan bersedekahlah kepada kami. Sesungguhnya Allah memberi balasan kepada orang yang bersedekah.”"},{"id":"89","ar":"قَالَ هَلْ عَلِمْتُمْ مَّا فَعَلْتُمْ بِيُوْسُفَ وَاَخِيْهِ اِذْ اَنْتُمْ جَاهِلُوْنَ","bc":"qāla hal 'alimtum mā fa'altum biyụsufa wa akhīhi iż antum jāhilụn","at":"Dia (Yusuf) berkata, “Tahukah kamu (kejelekan) apa yang telah kamu perbuat terhadap Yusuf dan saudaranya karena kamu tidak menyadari (akibat) perbuatanmu itu?”"},{"id":"90","ar":"قَالُوْٓا ءَاِنَّكَ لَاَنْتَ يُوْسُفُۗ قَالَ اَنَا۠ يُوْسُفُ وَهٰذَآ اَخِيْ قَدْ مَنَّ اللّٰهُ عَلَيْنَاۗ اِنَّهٗ مَنْ يَّتَّقِ وَيَصْبِرْ فَاِنَّ اللّٰهَ لَا يُضِيْعُ اَجْرَ الْمُحْسِنِيْنَ","bc":"qālū a innaka la`anta yụsuf, qāla ana yụsufu wa hāżā akhī qad mannallāhu 'alainā, innahụ may yattaqi wa yaṣbir fa innallāha lā yuḍī'u ajral-muḥsinīn","at":"Mereka berkata, “Apakah engkau benar-benar Yusuf?” Dia (Yusuf) menjawab, “Aku Yusuf dan ini saudaraku. Sungguh, Allah telah melimpahkan karunia-Nya kepada kami. Sesungguhnya barangsiapa bertakwa dan bersabar, maka Sungguh, Allah tidak menyia-nyiakan pahala orang yang berbuat baik.”"},{"id":"91","ar":"قَالُوْا تَاللّٰهِ لَقَدْ اٰثَرَكَ اللّٰهُ عَلَيْنَا وَاِنْ كُنَّا لَخٰطِـِٕيْنَ","bc":"qālụ tallāhi laqad āṡarakallāhu 'alainā wa ing kunnā lakhāṭi`īn","at":"Mereka berkata, “Demi Allah, sungguh Allah telah melebihkan engkau di atas kami, dan sesungguhnya kami adalah orang yang bersalah (berdosa).”"},{"id":"92","ar":"قَالَ لَا تَثْرِيْبَ عَلَيْكُمُ الْيَوْمَۗ يَغْفِرُ اللّٰهُ لَكُمْ ۖوَهُوَ اَرْحَمُ الرّٰحِمِيْنَ","bc":"qāla lā taṡrība 'alaikumul-yaụm, yagfirullāhu lakum wa huwa ar-ḥamur-rāḥimīn","at":"Dia (Yusuf) berkata, “Pada hari ini tidak ada cercaan terhadap kamu, mudah-mudahan Allah mengampuni kamu. Dan Dia Maha Penyayang di antara para penyayang."},{"id":"93","ar":"اِذْهَبُوْا بِقَمِيْصِيْ هٰذَا فَاَلْقُوْهُ عَلٰى وَجْهِ اَبِيْ يَأْتِ بَصِيْرًا ۚوَأْتُوْنِيْ بِاَهْلِكُمْ اَجْمَعِيْنَ","bc":"iż-habụ biqamīṣī hāżā fa alqụhu 'alā waj-hi abī ya`ti baṣīrā, wa`tụnī bi`ahlikum ajma'īn","at":"Pergilah kamu dengan membawa bajuku ini, lalu usapkan ke wajah ayahku, nanti dia akan melihat kembali; dan bawalah seluruh keluargamu kepadaku.”"},{"id":"94","ar":"وَلَمَّا فَصَلَتِ الْعِيْرُ قَالَ اَبُوْهُمْ اِنِّيْ لَاَجِدُ رِيْحَ يُوْسُفَ لَوْلَآ اَنْ تُفَنِّدُوْنِ","bc":"wa lammā faṣalatil-'īru qāla abụhum innī la`ajidu rīḥa yụsufa lau lā an tufannidụn","at":"Dan ketika kafilah itu telah keluar (dari negeri Mesir), ayah mereka berkata, “Sesungguhnya Aku mencium bau Yusuf, sekiranya kamu tidak menuduhku lemah akal (tentu kamu membenarkan aku).”"},{"id":"95","ar":"قَالُوْا تَاللّٰهِ اِنَّكَ لَفِيْ ضَلٰلِكَ الْقَدِيْمِ","bc":"qālụ tallāhi innaka lafī ḍalālikal-qadīm","at":"Mereka (keluarganya) berkata, “Demi Allah, sesungguhnya engkau masih dalam kekeliruanmu yang dahulu.”"},{"id":"96","ar":"فَلَمَّآ اَنْ جَاۤءَ الْبَشِيْرُ اَلْقٰىهُ عَلٰى وَجْهِهٖ فَارْتَدَّ بَصِيْرًاۗ قَالَ اَلَمْ اَقُلْ لَّكُمْۙ اِنِّيْٓ اَعْلَمُ مِنَ اللّٰهِ مَا لَا تَعْلَمُوْنَ","bc":"fa lammā an jā`al-basyīru alqāhu 'alā waj-hihī fartadda baṣīrā, qāla a lam aqul lakum innī a'lamu minallāhi mā lā ta'lamụn","at":"Maka ketika telah tiba pembawa kabar gembira itu, maka diusapkannya (baju itu) ke wajahnya (Yakub), lalu dia dapat melihat kembali. Dia (Yakub) berkata, “Bukankah telah aku katakan kepadamu, bahwa aku mengetahui dari Allah apa yang tidak kamu ketahui.”"},{"id":"97","ar":"قَالُوْا يٰٓاَبَانَا اسْتَغْفِرْ لَنَا ذُنُوْبَنَآ اِنَّا كُنَّا خٰطِـِٕيْنَ","bc":"qālụ yā abānastagfir lanā żunụbanā innā kunnā khāṭi`īn","at":"Mereka berkata, “Wahai ayah kami! Mohonkanlah ampunan untuk kami atas dosa-dosa kami, sesungguhnya kami adalah orang yang bersalah (berdosa).” "},{"id":"98","ar":"قَالَ سَوْفَ اَسْتَغْفِرُ لَكُمْ رَبِّيْ ۗاِنَّهٗ هُوَ الْغَفُوْرُ الرَّحِيْمُ","bc":"qāla saufa astagfiru lakum rabbī, innahụ huwal-gafụrur-raḥīm","at":"Dia (Yakub) berkata, “Aku akan memohonkan ampunan bagimu kepada Tuhanku. Sungguh, Dia Yang Maha Pengampun, Maha Penyayang.”"},{"id":"99","ar":"فَلَمَّا دَخَلُوْا عَلٰى يُوْسُفَ اٰوٰٓى اِلَيْهِ اَبَوَيْهِ وَقَالَ ادْخُلُوْا مِصْرَ اِنْ شَاۤءَ اللّٰهُ اٰمِنِيْنَ ۗ ","bc":"fa lammā dakhalụ 'alā yụsufa āwā ilaihi abawaihi wa qāladkhulụ miṣra in syā`allāhu āminīn","at":"Maka ketika mereka masuk ke (tempat) Yusuf, dia merangkul (dan menyiapkan tempat untuk) kedua orang tuanya seraya berkata, “Masuklah kamu ke negeri Mesir, insya Allah dalam keadaan aman.”"},{"id":"100","ar":"وَرَفَعَ اَبَوَيْهِ عَلَى الْعَرْشِ وَخَرُّوْا لَهٗ سُجَّدًاۚ وَقَالَ يٰٓاَبَتِ هٰذَا تَأْوِيْلُ رُءْيَايَ مِنْ قَبْلُ ۖقَدْ جَعَلَهَا رَبِّيْ حَقًّاۗ وَقَدْ اَحْسَنَ بِيْٓ اِذْ اَخْرَجَنِيْ مِنَ السِّجْنِ وَجَاۤءَ بِكُمْ مِّنَ الْبَدْوِ مِنْۢ بَعْدِ اَنْ نَّزَغَ الشَّيْطٰنُ بَيْنِيْ وَبَيْنَ اِخْوَتِيْۗ اِنَّ رَبِّيْ لَطِيْفٌ لِّمَا يَشَاۤءُ ۗاِنَّهٗ هُوَ الْعَلِيْمُ الْحَكِيْمُ","bc":"wa rafa'a abawaihi 'alal-'arsyi wa kharrụ lahụ sujjadā, wa qāla yā abati hāżā ta`wīlu ru`yāya ming qablu qad ja'alahā rabbī ḥaqqā, wa qad aḥsana bī iż akhrajanī minas-sijni wa jā`a bikum minal-badwi mim ba'di an nazagasy-syaiṭānu bainī wa baina ikhwatī, inna rabbī laṭīful limā yasyā`, innahụ huwal-'alīmul-ḥakīm","at":"Dan dia menaikkan kedua orang tuanya ke atas singgasana. Dan mereka (semua) tunduk bersujud kepadanya (Yusuf). Dan dia (Yusuf) berkata, “Wahai ayahku! Inilah takwil mimpiku yang dahulu itu. Dan sesungguhnya Tuhanku telah menjadikannya kenyataan. Sesungguhnya Tuhanku telah berbuat baik kepadaku, ketika Dia membebaskan aku dari penjara dan ketika membawa kamu dari dusun, setelah setan merusak (hubungan) antara aku dengan saudara-saudaraku. Sungguh, Tuhanku Mahalembut terhadap apa yang Dia kehendaki. Sungguh, Dia Yang Maha Mengetahui, Mahabijaksana."},{"id":"101","ar":"۞ رَبِّ قَدْ اٰتَيْتَنِيْ مِنَ الْمُلْكِ وَعَلَّمْتَنِيْ مِنْ تَأْوِيْلِ الْاَحَادِيْثِۚ فَاطِرَ السَّمٰوٰتِ وَالْاَرْضِۗ اَنْتَ وَلِيّٖ فِى الدُّنْيَا وَالْاٰخِرَةِۚ تَوَفَّنِيْ مُسْلِمًا وَّاَلْحِقْنِيْ بِالصّٰلِحِيْنَ","bc":"rabbi qad ātaitanī minal-mulki wa 'allamtanī min ta`wīlil-aḥādīṡ, fāṭiras-samāwāti wal-arḍ, anta waliyyī fid-dun-yā wal-ākhirah, tawaffanī muslimaw wa al-ḥiqnī biṣ-ṣāliḥīn","at":"Tuhanku, sesungguhnya Engkau telah menganugerahkan kepadaku sebagian kekuasaan dan telah mengajarkan kepadaku sebagian takwil mimpi. (Wahai Tuhan) pencipta langit dan bumi, Engkaulah pelindungku di dunia dan di akhirat, wafatkanlah aku dalam keadaan muslim dan gabungkanlah aku dengan orang yang saleh.” "},{"id":"102","ar":"ذٰلِكَ مِنْ اَنْۢبَاۤءِ الْغَيْبِ نُوْحِيْهِ اِلَيْكَۚ وَمَا كُنْتَ لَدَيْهِمْ اِذْ اَجْمَعُوْٓا اَمْرَهُمْ وَهُمْ يَمْكُرُوْنَ","bc":"żālika min ambā`il-gaibi nụḥīhi ilaīk, wa mā kunta ladaihim iż ajma'ū amrahum wa hum yamkurụn","at":"Itulah sebagian berita gaib yang Kami wahyukan kepadamu (Muhammad); padahal engkau tidak berada di samping mereka, ketika mereka bersepakat mengatur tipu muslihat (untuk memasukkan Yusuf ke dalam sumur)."},{"id":"103","ar":"وَمَآ اَكْثَرُ النَّاسِ وَلَوْ حَرَصْتَ بِمُؤْمِنِيْنَ","bc":"wa mā akṡarun-nāsi walau ḥaraṣta bimu`minīn","at":"Dan kebanyakan manusia tidak akan beriman walaupun engkau sangat menginginkannya."},{"id":"104","ar":"وَمَا تَسْـَٔلُهُمْ عَلَيْهِ مِنْ اَجْرٍۗ اِنْ هُوَ اِلَّا ذِكْرٌ لِّلْعٰلَمِيْنَ","bc":"wa mā tas`aluhum 'alaihi min ajr, in huwa illā żikrul lil-'ālamīn","at":"Dan engkau tidak meminta imbalan apa pun kepada mereka (terhadap seruanmu ini), sebab (seruan) itu adalah pengajaran bagi seluruh alam."},{"id":"105","ar":"وَكَاَيِّنْ مِّنْ اٰيَةٍ فِى السَّمٰوٰتِ وَالْاَرْضِ يَمُرُّوْنَ عَلَيْهَا وَهُمْ عَنْهَا مُعْرِضُوْنَ","bc":"wa ka`ayyim min āyatin fis-samāwāti wal-arḍi yamurrụna 'alaihā wa hum 'an-hā mu'riḍụn","at":"Dan berapa banyak tanda-tanda (kebesaran Allah) di langit dan di bumi yang mereka lalui, namun mereka berpaling daripadanya."},{"id":"106","ar":"وَمَا يُؤْمِنُ اَكْثَرُهُمْ بِاللّٰهِ اِلَّا وَهُمْ مُّشْرِكُوْنَ","bc":"wa mā yu`minu akṡaruhum billāhi illā wa hum musyrikụn","at":"Dan kebanyakan mereka tidak beriman kepada Allah, bahkan mereka mempersekutukan-Nya."},{"id":"107","ar":"اَفَاَمِنُوْٓا اَنْ تَأْتِيَهُمْ غَاشِيَةٌ مِّنْ عَذَابِ اللّٰهِ اَوْ تَأْتِيَهُمُ السَّاعَةُ بَغْتَةً وَّهُمْ لَا يَشْعُرُوْنَ","bc":"a fa aminū an ta`tiyahum gāsyiyatum min 'ażābillāhi au ta`tiyahumus-sā'atu bagtataw wa hum lā yasy'urụn","at":"Apakah mereka merasa aman dari kedatangan siksa Allah yang meliputi mereka, atau kedatangan Kiamat kepada mereka secara mendadak, sedang mereka tidak menyadarinya?"},{"id":"108","ar":"قُلْ هٰذِهٖ سَبِيْلِيْٓ اَدْعُوْٓا اِلَى اللّٰهِ ۗعَلٰى بَصِيْرَةٍ اَنَا۠ وَمَنِ اتَّبَعَنِيْ ۗوَسُبْحٰنَ اللّٰهِ وَمَآ اَنَا۠ مِنَ الْمُشْرِكِيْنَ","bc":"qul hāżihī sabīlī ad'ū ilallāh, 'alā baṣīratin ana wa manittaba'anī, wa sub-ḥānallāhi wa mā ana minal-musyrikīn","at":"Katakanlah (Muhammad), “Inilah jalanku, aku dan orang-orang yang mengikutiku mengajak (kamu) kepada Allah dengan yakin, Mahasuci Allah, dan aku tidak termasuk orang-orang musyrik.”"},{"id":"109","ar":"وَمَآ اَرْسَلْنَا مِنْ قَبْلِكَ اِلَّا رِجَالًا نُّوْحِيْٓ اِلَيْهِمْ مِّنْ اَهْلِ الْقُرٰىۗ اَفَلَمْ يَسِيْرُوْا فِى الْاَرْضِ فَيَنْظُرُوْا كَيْفَ كَانَ عَاقِبَةُ الَّذِيْنَ مِنْ قَبْلِهِمْۗ وَلَدَارُ الْاٰخِرَةِ خَيْرٌ لِّلَّذِيْنَ اتَّقَوْاۗ اَفَلَا تَعْقِلُوْنَ","bc":"wa mā arsalnā ming qablika illā rijālan nụḥī ilaihim min ahlil-qurā, a fa lam yasīrụ fil-arḍi fa yanẓurụ kaifa kāna 'āqibatullażīna ming qablihim, wa ladārul-ākhirati khairul lillażīnattaqau, a fa lā ta'qilụn","at":"Dan Kami tidak mengutus sebelummu (Muhammad), melainkan orang laki-laki yang Kami berikan wahyu kepadanya di antara penduduk negeri. Tidakkah mereka bepergian di bumi lalu melihat bagaimana kesudahan orang-orang sebelum mereka (yang mendustakan rasul). Dan sungguh, negeri akhirat itu lebih baik bagi orang yang bertakwa. Tidakkah kamu mengerti?"},{"id":"110","ar":"حَتّٰٓى اِذَا اسْتَا۟يْـَٔسَ الرُّسُلُ وَظَنُّوْٓا اَنَّهُمْ قَدْ كُذِبُوْا جَاۤءَهُمْ نَصْرُنَاۙ فَنُجِّيَ مَنْ نَّشَاۤءُ ۗوَلَا يُرَدُّ بَأْسُنَا عَنِ الْقَوْمِ الْمُجْرِمِيْنَ","bc":"ḥattā iżastai`asar-rusulu wa ẓannū annahum qad kużibụ jā`ahum naṣrunā fa nujjiya man nasyā`, wa lā yuraddu ba`sunā 'anil-qaumil-mujrimīn","at":"Sehingga apabila para rasul tidak mempunyai harapan lagi (tentang keimanan kaumnya) dan telah meyakini bahwa mereka telah didustakan, datanglah kepada mereka (para rasul) itu pertolongan Kami, lalu diselamatkan orang yang Kami kehendaki. Dan siksa Kami tidak dapat ditolak dari orang yang berdosa."},{"id":"111","ar":"لَقَدْ كَانَ فِيْ قَصَصِهِمْ عِبْرَةٌ لِّاُولِى الْاَلْبَابِۗ مَا كَانَ حَدِيْثًا يُّفْتَرٰى وَلٰكِنْ تَصْدِيْقَ الَّذِيْ بَيْنَ يَدَيْهِ وَتَفْصِيْلَ كُلِّ شَيْءٍ وَّهُدًى وَّرَحْمَةً لِّقَوْمٍ يُّؤْمِنُوْنَ","bc":"laqad kāna fī qaṣaṣihim 'ibratul li`ulil-albāb, mā kāna ḥadīṡay yuftarā wa lākin taṣdīqallażī baina yadaihi wa tafṣīla kulli syai`iw wa hudaw wa raḥmatal liqaumiy yu`minụn","at":"Sungguh, pada kisah-kisah mereka itu terdapat pengajaran bagi orang yang mempunyai akal. (Al-Qur'an) itu bukanlah cerita yang dibuat-buat, tetapi membenarkan (kitab-kitab) yang sebelumnya, menjelaskan segala sesuatu, dan (sebagai) petunjuk dan rahmat bagi orang-orang yang beriman."}];